INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (1, 1, "In the name of Allah, the Entirely Merciful, the Especially Merciful", "بِسۡمِ ٱللَّهِ ٱلرَّحۡمَٰنِ ٱلرَّحِيمِ", "Bismi Allahi alrrahmani alrraheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (1, 2, "[All] praise is [due] to Allah, Lord of the worlds", "ٱلۡحَمۡدُ لِلَّهِ رَبِّ ٱلۡعَٰلَمِينَ", "Alhamdu lillahi rabbi alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (1, 3, "The Entirely Merciful, the Especially Merciful", "ٱلرَّحۡمَٰنِ ٱلرَّحِيمِ", "Alrrahmani alrraheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (1, 4, "Sovereign of the Day of Recompense", "مَٰلِكِ يَوۡمِ ٱلدِّينِ", "Maliki yawmi alddeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (1, 5, "It is You we worship and You we ask for help", "إِيَّاكَ نَعۡبُدُ وَإِيَّاكَ نَسۡتَعِينُ", "Iyyaka naAAbudu waiyyaka nastaAAeenu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (1, 6, "Guide us to the straight path", "ٱهۡدِنَا ٱلصِّرَٰطَ ٱلۡمُسۡتَقِيمَ", "Ihdina alssirata almustaqeema");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (1, 7, "The path of those upon whom You have bestowed favor, not of those who have evoked [Your] anger or of those who are astray", "صِرَٰطَ ٱلَّذِينَ أَنۡعَمۡتَ عَلَيۡهِمۡ غَيۡرِ ٱلۡمَغۡضُوبِ عَلَيۡهِمۡ وَلَا ٱلضَّآلِّينَ", "Sirata allatheena anAAamta AAalayhim ghayri almaghdoobi AAalayhim wala alddalleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 1, "Alif, Lam, Meem", "الٓمٓ", "Aliflammeem");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 2, "This is the Book about which there is no doubt, a guidance for those conscious of Allah", "ذَٰلِكَ ٱلۡكِتَٰبُ لَا رَيۡبَۛ فِيهِۛ هُدٗى لِّلۡمُتَّقِينَ", "Thalika alkitabu la rayba feehi hudan lilmuttaqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 3, "Who believe in the unseen, establish prayer, and spend out of what We have provided for them", "ٱلَّذِينَ يُؤۡمِنُونَ بِٱلۡغَيۡبِ وَيُقِيمُونَ ٱلصَّلَوٰةَ وَمِمَّا رَزَقۡنَٰهُمۡ يُنفِقُونَ", "Allatheena yuminoona bialghaybi wayuqeemoona alssalata wamimma razaqnahum yunfiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 4, "And who believe in what has been revealed to you, [O Muhammad], and what was revealed before you, and of the Hereafter they are certain [in faith]", "وَٱلَّذِينَ يُؤۡمِنُونَ بِمَآ أُنزِلَ إِلَيۡكَ وَمَآ أُنزِلَ مِن قَبۡلِكَ وَبِٱلۡأٓخِرَةِ هُمۡ يُوقِنُونَ", "Waallatheena yuminoona bima onzila ilayka wama onzila min qablika wabialakhirati hum yooqinoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 5, "Those are upon [right] guidance from their Lord, and it is those who are the successful", "أُوْلَـٰٓئِكَ عَلَىٰ هُدٗى مِّن رَّبِّهِمۡۖ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُفۡلِحُونَ", "Olaika AAala hudan min rabbihim waolaika humu almuflihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 6, "Indeed, those who disbelieve - it is all the same for them whether you warn them or do not warn them - they will not believe", "إِنَّ ٱلَّذِينَ كَفَرُواْ سَوَآءٌ عَلَيۡهِمۡ ءَأَنذَرۡتَهُمۡ أَمۡ لَمۡ تُنذِرۡهُمۡ لَا يُؤۡمِنُونَ", "Inna allatheena kafaroo sawaon AAalayhim aanthartahum am lam tunthirhum la yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 7, "Allah has set a seal upon their hearts and upon their hearing, and over their vision is a veil. And for them is a great punishment", "خَتَمَ ٱللَّهُ عَلَىٰ قُلُوبِهِمۡ وَعَلَىٰ سَمۡعِهِمۡۖ وَعَلَىٰٓ أَبۡصَٰرِهِمۡ غِشَٰوَةٞۖ وَلَهُمۡ عَذَابٌ عَظِيمٞ", "Khatama Allahu AAala quloobihim waAAala samAAihim waAAala absarihim ghishawatun walahum AAathabun AAatheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 8, "And of the people are some who say, ""We believe in Allah and the Last Day,"" but they are not believers", "وَمِنَ ٱلنَّاسِ مَن يَقُولُ ءَامَنَّا بِٱللَّهِ وَبِٱلۡيَوۡمِ ٱلۡأٓخِرِ وَمَا هُم بِمُؤۡمِنِينَ", "Wamina alnnasi man yaqoolu amanna biAllahi wabialyawmi alakhiri wama hum bimumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 9, "They [think to] deceive Allah and those who believe, but they deceive not except themselves and perceive [it] not", "يُخَٰدِعُونَ ٱللَّهَ وَٱلَّذِينَ ءَامَنُواْ وَمَا يَخۡدَعُونَ إِلَّآ أَنفُسَهُمۡ وَمَا يَشۡعُرُونَ", "YukhadiAAoona Allaha waallatheena amanoo wama yakhdaAAoona illa anfusahum wama yashAAuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 10, "In their hearts is disease, so Allah has increased their disease; and for them is a painful punishment because they [habitually] used to lie", "فِي قُلُوبِهِم مَّرَضٞ فَزَادَهُمُ ٱللَّهُ مَرَضٗاۖ وَلَهُمۡ عَذَابٌ أَلِيمُۢ بِمَا كَانُواْ يَكۡذِبُونَ", "Fee quloobihim maradun fazadahumu Allahu maradan walahum AAathabun aleemun bima kanoo yakthiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 11, "And when it is said to them, ""Do not cause corruption on the earth,"" they say, ""We are but reformers", "وَإِذَا قِيلَ لَهُمۡ لَا تُفۡسِدُواْ فِي ٱلۡأَرۡضِ قَالُوٓاْ إِنَّمَا نَحۡنُ مُصۡلِحُونَ", "Waitha qeela lahum la tufsidoo fee alardi qaloo innama nahnu muslihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 12, "Unquestionably, it is they who are the corrupters, but they perceive [it] not", "أَلَآ إِنَّهُمۡ هُمُ ٱلۡمُفۡسِدُونَ وَلَٰكِن لَّا يَشۡعُرُونَ", "Ala innahum humu almufsidoona walakin la yashAAuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 13, "And when it is said to them, ""Believe as the people have believed,"" they say, ""Should we believe as the foolish have believed?"" Unquestionably, it is they who are the foolish, but they know [it] not", "وَإِذَا قِيلَ لَهُمۡ ءَامِنُواْ كَمَآ ءَامَنَ ٱلنَّاسُ قَالُوٓاْ أَنُؤۡمِنُ كَمَآ ءَامَنَ ٱلسُّفَهَآءُۗ أَلَآ إِنَّهُمۡ هُمُ ٱلسُّفَهَآءُ وَلَٰكِن لَّا يَعۡلَمُونَ", "Waitha qeela lahum aminoo kama amana alnnasu qaloo anuminu kama amana alssufahao ala innahum humu alssufahao walakin la yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 14, "And when they meet those who believe, they say, ""We believe""; but when they are alone with their evil ones, they say, ""Indeed, we are with you; we were only mockers", "وَإِذَا لَقُواْ ٱلَّذِينَ ءَامَنُواْ قَالُوٓاْ ءَامَنَّا وَإِذَا خَلَوۡاْ إِلَىٰ شَيَٰطِينِهِمۡ قَالُوٓاْ إِنَّا مَعَكُمۡ إِنَّمَا نَحۡنُ مُسۡتَهۡزِءُونَ", "Waitha laqoo allatheena amanoo qaloo amanna waitha khalaw ila shayateenihim qaloo inna maAAakum innama nahnu mustahzioona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 15, "[But] Allah mocks them and prolongs them in their transgression [while] they wander blindly", "ٱللَّهُ يَسۡتَهۡزِئُ بِهِمۡ وَيَمُدُّهُمۡ فِي طُغۡيَٰنِهِمۡ يَعۡمَهُونَ", "Allahu yastahzio bihim wayamudduhum fee tughyanihim yaAAmahoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 16, "Those are the ones who have purchased error [in exchange] for guidance, so their transaction has brought no profit, nor were they guided", "أُوْلَـٰٓئِكَ ٱلَّذِينَ ٱشۡتَرَوُاْ ٱلضَّلَٰلَةَ بِٱلۡهُدَىٰ فَمَا رَبِحَت تِّجَٰرَتُهُمۡ وَمَا كَانُواْ مُهۡتَدِينَ", "Olaika allatheena ishtarawoo alddalalata bialhuda fama rabihat tijaratuhum wama kanoo muhtadeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 17, "Their example is that of one who kindled a fire, but when it illuminated what was around him, Allah took away their light and left them in darkness [so] they could not see", "مَثَلُهُمۡ كَمَثَلِ ٱلَّذِي ٱسۡتَوۡقَدَ نَارٗا فَلَمَّآ أَضَآءَتۡ مَا حَوۡلَهُۥ ذَهَبَ ٱللَّهُ بِنُورِهِمۡ وَتَرَكَهُمۡ فِي ظُلُمَٰتٖ لَّا يُبۡصِرُونَ", "Mathaluhum kamathali allathee istawqada naran falamma adaat ma hawlahu thahaba Allahu binoorihim watarakahum fee thulumatin la yubsiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 18, "Deaf, dumb and blind - so they will not return [to the right path]", "صُمُّۢ بُكۡمٌ عُمۡيٞ فَهُمۡ لَا يَرۡجِعُونَ", "Summun bukmun AAumyun fahum la yarjiAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 19, "Or [it is] like a rainstorm from the sky within which is darkness, thunder and lightning. They put their fingers in their ears against the thunderclaps in dread of death. But Allah is encompassing of the disbelievers", "أَوۡ كَصَيِّبٖ مِّنَ ٱلسَّمَآءِ فِيهِ ظُلُمَٰتٞ وَرَعۡدٞ وَبَرۡقٞ يَجۡعَلُونَ أَصَٰبِعَهُمۡ فِيٓ ءَاذَانِهِم مِّنَ ٱلصَّوَٰعِقِ حَذَرَ ٱلۡمَوۡتِۚ وَٱللَّهُ مُحِيطُۢ بِٱلۡكَٰفِرِينَ", "Aw kasayyibin mina alssamai feehi thulumatun waraAAdun wabarqun yajAAaloona asabiAAahum fee athanihim mina alssawaAAiqi hathara almawti waAllahu muheetun bialkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 20, "The lightning almost snatches away their sight. Every time it lights [the way] for them, they walk therein; but when darkness comes over them, they stand [still]. And if Allah had willed, He could have taken away their hearing and their sight. Indeed, Allah is over all things competent", "يَكَادُ ٱلۡبَرۡقُ يَخۡطَفُ أَبۡصَٰرَهُمۡۖ كُلَّمَآ أَضَآءَ لَهُم مَّشَوۡاْ فِيهِ وَإِذَآ أَظۡلَمَ عَلَيۡهِمۡ قَامُواْۚ وَلَوۡ شَآءَ ٱللَّهُ لَذَهَبَ بِسَمۡعِهِمۡ وَأَبۡصَٰرِهِمۡۚ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ", "Yakadu albarqu yakhtafu absarahum kullama adaa lahum mashaw feehi waitha athlama AAalayhim qamoo walaw shaa Allahu lathahaba bisamAAihim waabsarihim inna Allaha AAala kulli shayin qadeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 21, "O mankind, worship your Lord, who created you and those before you, that you may become righteous", "يَـٰٓأَيُّهَا ٱلنَّاسُ ٱعۡبُدُواْ رَبَّكُمُ ٱلَّذِي خَلَقَكُمۡ وَٱلَّذِينَ مِن قَبۡلِكُمۡ لَعَلَّكُمۡ تَتَّقُونَ", "Ya ayyuha alnnasu oAAbudoo rabbakumu allathee khalaqakum waallatheena min qablikum laAAallakum tattaqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 22, "[He] who made for you the earth a bed [spread out] and the sky a ceiling and sent down from the sky, rain and brought forth thereby fruits as provision for you. So do not attribute to Allah equals while you know [that there is nothing similar to Him]", "ٱلَّذِي جَعَلَ لَكُمُ ٱلۡأَرۡضَ فِرَٰشٗا وَٱلسَّمَآءَ بِنَآءٗ وَأَنزَلَ مِنَ ٱلسَّمَآءِ مَآءٗ فَأَخۡرَجَ بِهِۦ مِنَ ٱلثَّمَرَٰتِ رِزۡقٗا لَّكُمۡۖ فَلَا تَجۡعَلُواْ لِلَّهِ أَندَادٗا وَأَنتُمۡ تَعۡلَمُونَ", "Allathee jaAAala lakumu alarda firashan waalssamaa binaan waanzala mina alssamai maan faakhraja bihi mina alththamarati rizqan lakum fala tajAAaloo lillahi andadan waantum taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 23, "And if you are in doubt about what We have sent down upon Our Servant [Muhammad], then produce a surah the like thereof and call upon your witnesses other than Allah, if you should be truthful", "وَإِن كُنتُمۡ فِي رَيۡبٖ مِّمَّا نَزَّلۡنَا عَلَىٰ عَبۡدِنَا فَأۡتُواْ بِسُورَةٖ مِّن مِّثۡلِهِۦ وَٱدۡعُواْ شُهَدَآءَكُم مِّن دُونِ ٱللَّهِ إِن كُنتُمۡ صَٰدِقِينَ", "Wain kuntum fee raybin mimma nazzalna AAala AAabdina fatoo bisooratin min mithlihi waodAAoo shuhadaakum min dooni Allahi in kuntum sadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 24, "But if you do not - and you will never be able to - then fear the Fire, whose fuel is men and stones, prepared for the disbelievers", "فَإِن لَّمۡ تَفۡعَلُواْ وَلَن تَفۡعَلُواْ فَٱتَّقُواْ ٱلنَّارَ ٱلَّتِي وَقُودُهَا ٱلنَّاسُ وَٱلۡحِجَارَةُۖ أُعِدَّتۡ لِلۡكَٰفِرِينَ", "Fain lam tafAAaloo walan tafAAaloo faittaqoo alnnara allatee waqooduha alnnasu waalhijaratu oAAiddat lilkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 25, "And give good tidings to those who believe and do righteous deeds that they will have gardens [in Paradise] beneath which rivers flow. Whenever they are provided with a provision of fruit therefrom, they will say, ""This is what we were provided with before."" And it is given to them in likeness. And they will have therein purified spouses, and they will abide therein eternally", "وَبَشِّرِ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ أَنَّ لَهُمۡ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُۖ كُلَّمَا رُزِقُواْ مِنۡهَا مِن ثَمَرَةٖ رِّزۡقٗا قَالُواْ هَٰذَا ٱلَّذِي رُزِقۡنَا مِن قَبۡلُۖ وَأُتُواْ بِهِۦ مُتَشَٰبِهٗاۖ وَلَهُمۡ فِيهَآ أَزۡوَٰجٞ مُّطَهَّرَةٞۖ وَهُمۡ فِيهَا خَٰلِدُونَ", "Wabashshiri allatheena amanoo waAAamiloo alssalihati anna lahum jannatin tajree min tahtiha alanharu kullama ruziqoo minha min thamaratin rizqan qaloo hatha allathee ruziqna min qablu waotoo bihi mutashabihan walahum feeha azwajun mutahharatun wahum feeha khalidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 26, "Indeed, Allah is not timid to present an example - that of a mosquito or what is smaller than it. And those who have believed know that it is the truth from their Lord. But as for those who disbelieve, they say, ""What did Allah intend by this as an example?"" He misleads many thereby and guides many thereby. And He misleads not except the defiantly disobedient", "۞إِنَّ ٱللَّهَ لَا يَسۡتَحۡيِۦٓ أَن يَضۡرِبَ مَثَلٗا مَّا بَعُوضَةٗ فَمَا فَوۡقَهَاۚ فَأَمَّا ٱلَّذِينَ ءَامَنُواْ فَيَعۡلَمُونَ أَنَّهُ ٱلۡحَقُّ مِن رَّبِّهِمۡۖ وَأَمَّا ٱلَّذِينَ كَفَرُواْ فَيَقُولُونَ مَاذَآ أَرَادَ ٱللَّهُ بِهَٰذَا مَثَلٗاۘ يُضِلُّ بِهِۦ كَثِيرٗا وَيَهۡدِي بِهِۦ كَثِيرٗاۚ وَمَا يُضِلُّ بِهِۦٓ إِلَّا ٱلۡفَٰسِقِينَ", "Inna Allaha la yastahyee an yadriba mathalan ma baAAoodatan fama fawqaha faamma allatheena amanoo fayaAAlamoona annahu alhaqqu min rabbihim waamma allatheena kafaroo fayaqooloona matha arada Allahu bihatha mathalan yudillu bihi katheeran wayahdee bihi katheeran wama yudillu bihi illa alfasiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 27, "Who break the covenant of Allah after contracting it and sever that which Allah has ordered to be joined and cause corruption on earth. It is those who are the losers", "ٱلَّذِينَ يَنقُضُونَ عَهۡدَ ٱللَّهِ مِنۢ بَعۡدِ مِيثَٰقِهِۦ وَيَقۡطَعُونَ مَآ أَمَرَ ٱللَّهُ بِهِۦٓ أَن يُوصَلَ وَيُفۡسِدُونَ فِي ٱلۡأَرۡضِۚ أُوْلَـٰٓئِكَ هُمُ ٱلۡخَٰسِرُونَ", "Allatheena yanqudoona AAahda Allahi min baAAdi meethaqihi wayaqtaAAoona ma amara Allahu bihi an yoosala wayufsidoona fee alardi olaika humu alkhasiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 28, "How can you disbelieve in Allah when you were lifeless and He brought you to life; then He will cause you to die, then He will bring you [back] to life, and then to Him you will be returned", "كَيۡفَ تَكۡفُرُونَ بِٱللَّهِ وَكُنتُمۡ أَمۡوَٰتٗا فَأَحۡيَٰكُمۡۖ ثُمَّ يُمِيتُكُمۡ ثُمَّ يُحۡيِيكُمۡ ثُمَّ إِلَيۡهِ تُرۡجَعُونَ", "Kayfa takfuroona biAllahi wakuntum amwatan faahyakum thumma yumeetukum thumma yuhyeekum thumma ilayhi turjaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 29, "It is He who created for you all of that which is on the earth. Then He directed Himself to the heaven, [His being above all creation], and made them seven heavens, and He is Knowing of all things", "هُوَ ٱلَّذِي خَلَقَ لَكُم مَّا فِي ٱلۡأَرۡضِ جَمِيعٗا ثُمَّ ٱسۡتَوَىٰٓ إِلَى ٱلسَّمَآءِ فَسَوَّىٰهُنَّ سَبۡعَ سَمَٰوَٰتٖۚ وَهُوَ بِكُلِّ شَيۡءٍ عَلِيمٞ", "Huwa allathee khalaqa lakum ma fee alardi jameeAAan thumma istawa ila alssamai fasawwahunna sabAAa samawatin wahuwa bikulli shayin AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 30, "And [mention, O Muhammad], when your Lord said to the angels, ""Indeed, I will make upon the earth a successive authority."" They said, ""Will You place upon it one who causes corruption therein and sheds blood, while we declare Your praise and sanctify You?"" Allah said, ""Indeed, I know that which you do not know", "وَإِذۡ قَالَ رَبُّكَ لِلۡمَلَـٰٓئِكَةِ إِنِّي جَاعِلٞ فِي ٱلۡأَرۡضِ خَلِيفَةٗۖ قَالُوٓاْ أَتَجۡعَلُ فِيهَا مَن يُفۡسِدُ فِيهَا وَيَسۡفِكُ ٱلدِّمَآءَ وَنَحۡنُ نُسَبِّحُ بِحَمۡدِكَ وَنُقَدِّسُ لَكَۖ قَالَ إِنِّيٓ أَعۡلَمُ مَا لَا تَعۡلَمُونَ", "Waith qala rabbuka lilmalaikati innee jaAAilun fee alardi khaleefatan qaloo atajAAalu feeha man yufsidu feeha wayasfiku alddimaa wanahnu nusabbihu bihamdika wanuqaddisu laka qala innee aAAlamu ma la taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 31, "And He taught Adam the names - all of them. Then He showed them to the angels and said, ""Inform Me of the names of these, if you are truthful", "وَعَلَّمَ ءَادَمَ ٱلۡأَسۡمَآءَ كُلَّهَا ثُمَّ عَرَضَهُمۡ عَلَى ٱلۡمَلَـٰٓئِكَةِ فَقَالَ أَنۢبِـُٔونِي بِأَسۡمَآءِ هَـٰٓؤُلَآءِ إِن كُنتُمۡ صَٰدِقِينَ", "WaAAallama adama alasmaa kullaha thumma AAaradahum AAala almalaikati faqala anbioonee biasmai haolai in kuntum sadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 32, "They said, ""Exalted are You; we have no knowledge except what You have taught us. Indeed, it is You who is the Knowing, the Wise", "قَالُواْ سُبۡحَٰنَكَ لَا عِلۡمَ لَنَآ إِلَّا مَا عَلَّمۡتَنَآۖ إِنَّكَ أَنتَ ٱلۡعَلِيمُ ٱلۡحَكِيمُ", "Qaloo subhanaka la AAilma lana illa ma AAallamtana innaka anta alAAaleemu alhakeemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 33, "He said, ""O Adam, inform them of their names."" And when he had informed them of their names, He said, ""Did I not tell you that I know the unseen [aspects] of the heavens and the earth? And I know what you reveal and what you have concealed", "قَالَ يَـٰٓـَٔادَمُ أَنۢبِئۡهُم بِأَسۡمَآئِهِمۡۖ فَلَمَّآ أَنۢبَأَهُم بِأَسۡمَآئِهِمۡ قَالَ أَلَمۡ أَقُل لَّكُمۡ إِنِّيٓ أَعۡلَمُ غَيۡبَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَأَعۡلَمُ مَا تُبۡدُونَ وَمَا كُنتُمۡ تَكۡتُمُونَ", "Qala ya adamu anbihum biasmaihim falamma anbaahum biasmaihim qala alam aqul lakum innee aAAlamu ghayba alssamawati waalardi waaAAlamu ma tubdoona wama kuntum taktumoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 34, "And [mention] when We said to the angels, ""Prostrate before Adam""; so they prostrated, except for Iblees. He refused and was arrogant and became of the disbelievers", "وَإِذۡ قُلۡنَا لِلۡمَلَـٰٓئِكَةِ ٱسۡجُدُواْ لِأٓدَمَ فَسَجَدُوٓاْ إِلَّآ إِبۡلِيسَ أَبَىٰ وَٱسۡتَكۡبَرَ وَكَانَ مِنَ ٱلۡكَٰفِرِينَ", "Waith qulna lilmalaikati osjudoo liadama fasajadoo illa ibleesa aba waistakbara wakana mina alkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 35, "And We said, ""O Adam, dwell, you and your wife, in Paradise and eat therefrom in [ease and] abundance from wherever you will. But do not approach this tree, lest you be among the wrongdoers", "وَقُلۡنَا يَـٰٓـَٔادَمُ ٱسۡكُنۡ أَنتَ وَزَوۡجُكَ ٱلۡجَنَّةَ وَكُلَا مِنۡهَا رَغَدًا حَيۡثُ شِئۡتُمَا وَلَا تَقۡرَبَا هَٰذِهِ ٱلشَّجَرَةَ فَتَكُونَا مِنَ ٱلظَّـٰلِمِينَ", "Waqulna ya adamu oskun anta wazawjuka aljannata wakula minha raghadan haythu shituma wala taqraba hathihi alshshajarata fatakoona mina alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 36, "But Satan caused them to slip out of it and removed them from that [condition] in which they had been. And We said, ""Go down, [all of you], as enemies to one another, and you will have upon the earth a place of settlement and provision for a time", "فَأَزَلَّهُمَا ٱلشَّيۡطَٰنُ عَنۡهَا فَأَخۡرَجَهُمَا مِمَّا كَانَا فِيهِۖ وَقُلۡنَا ٱهۡبِطُواْ بَعۡضُكُمۡ لِبَعۡضٍ عَدُوّٞۖ وَلَكُمۡ فِي ٱلۡأَرۡضِ مُسۡتَقَرّٞ وَمَتَٰعٌ إِلَىٰ حِينٖ", "Faazallahuma alshshaytanu AAanha faakhrajahuma mimma kana feehi waqulna ihbitoo baAAdukum libaAAdin AAaduwwun walakum fee alardi mustaqarrun wamataAAun ila heenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 37, "Then Adam received from his Lord [some] words, and He accepted his repentance. Indeed, it is He who is the Accepting of repentance, the Merciful", "فَتَلَقَّىٰٓ ءَادَمُ مِن رَّبِّهِۦ كَلِمَٰتٖ فَتَابَ عَلَيۡهِۚ إِنَّهُۥ هُوَ ٱلتَّوَّابُ ٱلرَّحِيمُ", "Fatalaqqa adamu min rabbihi kalimatin fataba AAalayhi innahu huwa alttawwabu alrraheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 38, "We said, ""Go down from it, all of you. And when guidance comes to you from Me, whoever follows My guidance - there will be no fear concerning them, nor will they grieve", "قُلۡنَا ٱهۡبِطُواْ مِنۡهَا جَمِيعٗاۖ فَإِمَّا يَأۡتِيَنَّكُم مِّنِّي هُدٗى فَمَن تَبِعَ هُدَايَ فَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ", "Qulna ihbitoo minha jameeAAan faimma yatiyannakum minnee hudan faman tabiAAa hudaya fala khawfun AAalayhim wala hum yahzanoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 39, "And those who disbelieve and deny Our signs - those will be companions of the Fire; they will abide therein eternally", "وَٱلَّذِينَ كَفَرُواْ وَكَذَّبُواْ بِـَٔايَٰتِنَآ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلنَّارِۖ هُمۡ فِيهَا خَٰلِدُونَ", "Waallatheena kafaroo wakaththaboo biayatina olaika ashabu alnnari hum feeha khalidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 40, "O Children of Israel, remember My favor which I have bestowed upon you and fulfill My covenant [upon you] that I will fulfill your covenant [from Me], and be afraid of [only] Me", "يَٰبَنِيٓ إِسۡرَـٰٓءِيلَ ٱذۡكُرُواْ نِعۡمَتِيَ ٱلَّتِيٓ أَنۡعَمۡتُ عَلَيۡكُمۡ وَأَوۡفُواْ بِعَهۡدِيٓ أُوفِ بِعَهۡدِكُمۡ وَإِيَّـٰيَ فَٱرۡهَبُونِ", "Ya banee israeela othkuroo niAAmatiya allatee anAAamtu AAalaykum waawfoo biAAahdee oofi biAAahdikum waiyyaya fairhabooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 41, "And believe in what I have sent down confirming that which is [already] with you, and be not the first to disbelieve in it. And do not exchange My signs for a small price, and fear [only] Me", "وَءَامِنُواْ بِمَآ أَنزَلۡتُ مُصَدِّقٗا لِّمَا مَعَكُمۡ وَلَا تَكُونُوٓاْ أَوَّلَ كَافِرِۭ بِهِۦۖ وَلَا تَشۡتَرُواْ بِـَٔايَٰتِي ثَمَنٗا قَلِيلٗا وَإِيَّـٰيَ فَٱتَّقُونِ", "Waaminoo bima anzaltu musaddiqan lima maAAakum wala takoonoo awwala kafirin bihi wala tashtaroo biayatee thamanan qaleelan waiyyaya faittaqooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 42, "And do not mix the truth with falsehood or conceal the truth while you know [it]", "وَلَا تَلۡبِسُواْ ٱلۡحَقَّ بِٱلۡبَٰطِلِ وَتَكۡتُمُواْ ٱلۡحَقَّ وَأَنتُمۡ تَعۡلَمُونَ", "Wala talbisoo alhaqqa bialbatili wataktumoo alhaqqa waantum taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 43, "And establish prayer and give zakah and bow with those who bow [in worship and obedience]", "وَأَقِيمُواْ ٱلصَّلَوٰةَ وَءَاتُواْ ٱلزَّكَوٰةَ وَٱرۡكَعُواْ مَعَ ٱلرَّـٰكِعِينَ", "Waaqeemoo alssalata waatoo alzzakata wairkaAAoo maAAa alrrakiAAeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 44, "Do you order righteousness of the people and forget yourselves while you recite the Scripture? Then will you not reason", "۞أَتَأۡمُرُونَ ٱلنَّاسَ بِٱلۡبِرِّ وَتَنسَوۡنَ أَنفُسَكُمۡ وَأَنتُمۡ تَتۡلُونَ ٱلۡكِتَٰبَۚ أَفَلَا تَعۡقِلُونَ", "Atamuroona alnnasa bialbirri watansawna anfusakum waantum tatloona alkitaba afala taAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 45, "And seek help through patience and prayer, and indeed, it is difficult except for the humbly submissive [to Allah]", "وَٱسۡتَعِينُواْ بِٱلصَّبۡرِ وَٱلصَّلَوٰةِۚ وَإِنَّهَا لَكَبِيرَةٌ إِلَّا عَلَى ٱلۡخَٰشِعِينَ", "WaistaAAeenoo bialssabri waalssalati wainnaha lakabeeratun illa AAala alkhashiAAeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 46, "Who are certain that they will meet their Lord and that they will return to Him", "ٱلَّذِينَ يَظُنُّونَ أَنَّهُم مُّلَٰقُواْ رَبِّهِمۡ وَأَنَّهُمۡ إِلَيۡهِ رَٰجِعُونَ", "Allatheena yathunnoona annahum mulaqoo rabbihim waannahum ilayhi rajiAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 47, "O Children of Israel, remember My favor that I have bestowed upon you and that I preferred you over the worlds", "يَٰبَنِيٓ إِسۡرَـٰٓءِيلَ ٱذۡكُرُواْ نِعۡمَتِيَ ٱلَّتِيٓ أَنۡعَمۡتُ عَلَيۡكُمۡ وَأَنِّي فَضَّلۡتُكُمۡ عَلَى ٱلۡعَٰلَمِينَ", "Ya banee israeela othkuroo niAAmatiya allatee anAAamtu AAalaykum waannee faddaltukum AAala alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 48, "And fear a Day when no soul will suffice for another soul at all, nor will intercession be accepted from it, nor will compensation be taken from it, nor will they be aided", "وَٱتَّقُواْ يَوۡمٗا لَّا تَجۡزِي نَفۡسٌ عَن نَّفۡسٖ شَيۡـٔٗا وَلَا يُقۡبَلُ مِنۡهَا شَفَٰعَةٞ وَلَا يُؤۡخَذُ مِنۡهَا عَدۡلٞ وَلَا هُمۡ يُنصَرُونَ", "Waittaqoo yawman la tajzee nafsun AAan nafsin shayan wala yuqbalu minha shafaAAatun wala yukhathu minha AAadlun wala hum yunsaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 49, "And [recall] when We saved your forefathers from the people of Pharaoh, who afflicted you with the worst torment, slaughtering your [newborn] sons and keeping your females alive. And in that was a great trial from your Lord", "وَإِذۡ نَجَّيۡنَٰكُم مِّنۡ ءَالِ فِرۡعَوۡنَ يَسُومُونَكُمۡ سُوٓءَ ٱلۡعَذَابِ يُذَبِّحُونَ أَبۡنَآءَكُمۡ وَيَسۡتَحۡيُونَ نِسَآءَكُمۡۚ وَفِي ذَٰلِكُم بَلَآءٞ مِّن رَّبِّكُمۡ عَظِيمٞ", "Waith najjaynakum min ali firAAawna yasoomoonakum sooa alAAathabi yuthabbihoona abnaakum wayastahyoona nisaakum wafee thalikum balaon min rabbikum AAatheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 50, "And [recall] when We parted the sea for you and saved you and drowned the people of Pharaoh while you were looking on", "وَإِذۡ فَرَقۡنَا بِكُمُ ٱلۡبَحۡرَ فَأَنجَيۡنَٰكُمۡ وَأَغۡرَقۡنَآ ءَالَ فِرۡعَوۡنَ وَأَنتُمۡ تَنظُرُونَ", "Waith faraqna bikumu albahra faanjaynakum waaghraqna ala firAAawna waantum tanthuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 51, "And [recall] when We made an appointment with Moses for forty nights. Then you took [for worship] the calf after him, while you were wrongdoers", "وَإِذۡ وَٰعَدۡنَا مُوسَىٰٓ أَرۡبَعِينَ لَيۡلَةٗ ثُمَّ ٱتَّخَذۡتُمُ ٱلۡعِجۡلَ مِنۢ بَعۡدِهِۦ وَأَنتُمۡ ظَٰلِمُونَ", "Waith waAAadna moosa arbaAAeena laylatan thumma ittakhathtumu alAAijla min baAAdihi waantum thalimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 52, "Then We forgave you after that so perhaps you would be grateful", "ثُمَّ عَفَوۡنَا عَنكُم مِّنۢ بَعۡدِ ذَٰلِكَ لَعَلَّكُمۡ تَشۡكُرُونَ", "Thumma AAafawna AAankum min baAAdi thalika laAAallakum tashkuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 53, "And [recall] when We gave Moses the Scripture and criterion that perhaps you would be guided", "وَإِذۡ ءَاتَيۡنَا مُوسَى ٱلۡكِتَٰبَ وَٱلۡفُرۡقَانَ لَعَلَّكُمۡ تَهۡتَدُونَ", "Waith atayna moosa alkitaba waalfurqana laAAallakum tahtadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 54, "And [recall] when Moses said to his people, ""O my people, indeed you have wronged yourselves by your taking of the calf [for worship]. So repent to your Creator and kill yourselves. That is best for [all of] you in the sight of your Creator."" Then He accepted your repentance; indeed, He is the Accepting of repentance, the Merciful", "وَإِذۡ قَالَ مُوسَىٰ لِقَوۡمِهِۦ يَٰقَوۡمِ إِنَّكُمۡ ظَلَمۡتُمۡ أَنفُسَكُم بِٱتِّخَاذِكُمُ ٱلۡعِجۡلَ فَتُوبُوٓاْ إِلَىٰ بَارِئِكُمۡ فَٱقۡتُلُوٓاْ أَنفُسَكُمۡ ذَٰلِكُمۡ خَيۡرٞ لَّكُمۡ عِندَ بَارِئِكُمۡ فَتَابَ عَلَيۡكُمۡۚ إِنَّهُۥ هُوَ ٱلتَّوَّابُ ٱلرَّحِيمُ", "Waith qala moosa liqawmihi ya qawmi innakum thalamtum anfusakum biittikhathikumu alAAijla fatooboo ila bariikum faoqtuloo anfusakum thalikum khayrun lakum AAinda bariikum fataba AAalaykum innahu huwa alttawwabu alrraheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 55, "And [recall] when you said, ""O Moses, we will never believe you until we see Allah outright""; so the thunderbolt took you while you were looking on", "وَإِذۡ قُلۡتُمۡ يَٰمُوسَىٰ لَن نُّؤۡمِنَ لَكَ حَتَّىٰ نَرَى ٱللَّهَ جَهۡرَةٗ فَأَخَذَتۡكُمُ ٱلصَّـٰعِقَةُ وَأَنتُمۡ تَنظُرُونَ", "Waith qultum ya moosa lan numina laka hatta nara Allaha jahratan faakhathatkumu alssaAAiqatu waantum tanthuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 56, "Then We revived you after your death that perhaps you would be grateful", "ثُمَّ بَعَثۡنَٰكُم مِّنۢ بَعۡدِ مَوۡتِكُمۡ لَعَلَّكُمۡ تَشۡكُرُونَ", "Thumma baAAathnakum min baAAdi mawtikum laAAallakum tashkuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 57, "And We shaded you with clouds and sent down to you manna and quails, [saying], ""Eat from the good things with which We have provided you."" And they wronged Us not - but they were [only] wronging themselves", "وَظَلَّلۡنَا عَلَيۡكُمُ ٱلۡغَمَامَ وَأَنزَلۡنَا عَلَيۡكُمُ ٱلۡمَنَّ وَٱلسَّلۡوَىٰۖ كُلُواْ مِن طَيِّبَٰتِ مَا رَزَقۡنَٰكُمۡۚ وَمَا ظَلَمُونَا وَلَٰكِن كَانُوٓاْ أَنفُسَهُمۡ يَظۡلِمُونَ", "Wathallalna AAalaykumu alghamama waanzalna AAalaykumu almanna waalssalwa kuloo min tayyibati ma razaqnakum wama thalamoona walakin kanoo anfusahum yathlimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 58, "And [recall] when We said, ""Enter this city and eat from it wherever you will in [ease and] abundance, and enter the gate bowing humbly and say, 'Relieve us of our burdens.' We will [then] forgive your sins for you, and We will increase the doers of good [in goodness and reward]", "وَإِذۡ قُلۡنَا ٱدۡخُلُواْ هَٰذِهِ ٱلۡقَرۡيَةَ فَكُلُواْ مِنۡهَا حَيۡثُ شِئۡتُمۡ رَغَدٗا وَٱدۡخُلُواْ ٱلۡبَابَ سُجَّدٗا وَقُولُواْ حِطَّةٞ نَّغۡفِرۡ لَكُمۡ خَطَٰيَٰكُمۡۚ وَسَنَزِيدُ ٱلۡمُحۡسِنِينَ", "Waith qulna odkhuloo hathihi alqaryata fakuloo minha haythu shitum raghadan waodkhuloo albaba sujjadan waqooloo hittatun naghfir lakum khatayakum wasanazeedu almuhsineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 59, "But those who wronged changed [those words] to a statement other than that which had been said to them, so We sent down upon those who wronged a punishment from the sky because they were defiantly disobeying", "فَبَدَّلَ ٱلَّذِينَ ظَلَمُواْ قَوۡلًا غَيۡرَ ٱلَّذِي قِيلَ لَهُمۡ فَأَنزَلۡنَا عَلَى ٱلَّذِينَ ظَلَمُواْ رِجۡزٗا مِّنَ ٱلسَّمَآءِ بِمَا كَانُواْ يَفۡسُقُونَ", "Fabaddala allatheena thalamoo qawlan ghayra allathee qeela lahum faanzalna AAala allatheena thalamoo rijzan mina alssamai bima kanoo yafsuqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 60, "And [recall] when Moses prayed for water for his people, so We said, ""Strike with your staff the stone."" And there gushed forth from it twelve springs, and every people knew its watering place. ""Eat and drink from the provision of Allah, and do not commit abuse on the earth, spreading corruption", "۞وَإِذِ ٱسۡتَسۡقَىٰ مُوسَىٰ لِقَوۡمِهِۦ فَقُلۡنَا ٱضۡرِب بِّعَصَاكَ ٱلۡحَجَرَۖ فَٱنفَجَرَتۡ مِنۡهُ ٱثۡنَتَا عَشۡرَةَ عَيۡنٗاۖ قَدۡ عَلِمَ كُلُّ أُنَاسٖ مَّشۡرَبَهُمۡۖ كُلُواْ وَٱشۡرَبُواْ مِن رِّزۡقِ ٱللَّهِ وَلَا تَعۡثَوۡاْ فِي ٱلۡأَرۡضِ مُفۡسِدِينَ", "Waithi istasqa moosa liqawmihi faqulna idrib biAAasaka alhajara fainfajarat minhu ithnata AAashrata AAaynan qad AAalima kullu onasin mashrabahum kuloo waishraboo min rizqi Allahi wala taAAthaw fee alardi mufsideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 61, "And [recall] when you said, ""O Moses, we can never endure one [kind of] food. So call upon your Lord to bring forth for us from the earth its green herbs and its cucumbers and its garlic and its lentils and its onions."" [Moses] said, ""Would you exchange what is better for what is less? Go into [any] settlement and indeed, you will have what you have asked."" And they were covered with humiliation and poverty and returned with anger from Allah [upon them]. That was because they [repeatedly] disbelieved in the signs of Allah and killed the prophets without right. That was because they disobeyed and were [habitually] transgressing", "وَإِذۡ قُلۡتُمۡ يَٰمُوسَىٰ لَن نَّصۡبِرَ عَلَىٰ طَعَامٖ وَٰحِدٖ فَٱدۡعُ لَنَا رَبَّكَ يُخۡرِجۡ لَنَا مِمَّا تُنۢبِتُ ٱلۡأَرۡضُ مِنۢ بَقۡلِهَا وَقِثَّآئِهَا وَفُومِهَا وَعَدَسِهَا وَبَصَلِهَاۖ قَالَ أَتَسۡتَبۡدِلُونَ ٱلَّذِي هُوَ أَدۡنَىٰ بِٱلَّذِي هُوَ خَيۡرٌۚ ٱهۡبِطُواْ مِصۡرٗا فَإِنَّ لَكُم مَّا سَأَلۡتُمۡۗ وَضُرِبَتۡ عَلَيۡهِمُ ٱلذِّلَّةُ وَٱلۡمَسۡكَنَةُ وَبَآءُو بِغَضَبٖ مِّنَ ٱللَّهِۚ ذَٰلِكَ بِأَنَّهُمۡ كَانُواْ يَكۡفُرُونَ بِـَٔايَٰتِ ٱللَّهِ وَيَقۡتُلُونَ ٱلنَّبِيِّـۧنَ بِغَيۡرِ ٱلۡحَقِّۚ ذَٰلِكَ بِمَا عَصَواْ وَّكَانُواْ يَعۡتَدُونَ", "Waith qultum ya moosa lan nasbira AAala taAAamin wahidin faodAAu lana rabbaka yukhrij lana mimma tunbitu alardu min baqliha waqiththaiha wafoomiha waAAadasiha wabasaliha qala atastabdiloona allathee huwa adna biallathee huwa khayrun ihbitoo misran fainna lakum ma saaltum waduribat AAalayhimu alththillatu waalmaskanatu wabaoo bighadabin mina Allahi thalika biannahum kanoo yakfuroona biayati Allahi wayaqtuloona alnnabiyyeena bighayri alhaqqi thalika bima AAasaw wakanoo yaAAtadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 62, "Indeed, those who believed and those who were Jews or Christians or Sabeans [before Prophet Muhammad] - those [among them] who believed in Allah and the Last Day and did righteousness - will have their reward with their Lord, and no fear will there be concerning them, nor will they grieve", "إِنَّ ٱلَّذِينَ ءَامَنُواْ وَٱلَّذِينَ هَادُواْ وَٱلنَّصَٰرَىٰ وَٱلصَّـٰبِـِٔينَ مَنۡ ءَامَنَ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ وَعَمِلَ صَٰلِحٗا فَلَهُمۡ أَجۡرُهُمۡ عِندَ رَبِّهِمۡ وَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ", "Inna allatheena amanoo waallatheena hadoo waalnnasara waalssabieena man amana biAllahi waalyawmi alakhiri waAAamila salihan falahum ajruhum AAinda rabbihim wala khawfun AAalayhim wala hum yahzanoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 63, "And [recall] when We took your covenant, [O Children of Israel, to abide by the Torah] and We raised over you the mount, [saying], ""Take what We have given you with determination and remember what is in it that perhaps you may become righteous", "وَإِذۡ أَخَذۡنَا مِيثَٰقَكُمۡ وَرَفَعۡنَا فَوۡقَكُمُ ٱلطُّورَ خُذُواْ مَآ ءَاتَيۡنَٰكُم بِقُوَّةٖ وَٱذۡكُرُواْ مَا فِيهِ لَعَلَّكُمۡ تَتَّقُونَ", "Waith akhathna meethaqakum warafaAAna fawqakumu alttoora khuthoo ma ataynakum biquwwatin waothkuroo ma feehi laAAallakum tattaqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 64, "Then you turned away after that. And if not for the favor of Allah upon you and His mercy, you would have been among the losers", "ثُمَّ تَوَلَّيۡتُم مِّنۢ بَعۡدِ ذَٰلِكَۖ فَلَوۡلَا فَضۡلُ ٱللَّهِ عَلَيۡكُمۡ وَرَحۡمَتُهُۥ لَكُنتُم مِّنَ ٱلۡخَٰسِرِينَ", "Thumma tawallaytum min baAAdi thalika falawla fadlu Allahi AAalaykum warahmatuhu lakuntum mina alkhasireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 65, "And you had already known about those who transgressed among you concerning the sabbath, and We said to them, ""Be apes, despised", "وَلَقَدۡ عَلِمۡتُمُ ٱلَّذِينَ ٱعۡتَدَوۡاْ مِنكُمۡ فِي ٱلسَّبۡتِ فَقُلۡنَا لَهُمۡ كُونُواْ قِرَدَةً خَٰسِـِٔينَ", "Walaqad AAalimtumu allatheena iAAtadaw minkum fee alssabti faqulna lahum koonoo qiradatan khasieena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 66, "And We made it a deterrent punishment for those who were present and those who succeeded [them] and a lesson for those who fear Allah", "فَجَعَلۡنَٰهَا نَكَٰلٗا لِّمَا بَيۡنَ يَدَيۡهَا وَمَا خَلۡفَهَا وَمَوۡعِظَةٗ لِّلۡمُتَّقِينَ", "FajaAAalnaha nakalan lima bayna yadayha wama khalfaha wamawAAithatan lilmuttaqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 67, "And [recall] when Moses said to his people, ""Indeed, Allah commands you to slaughter a cow."" They said, ""Do you take us in ridicule?"" He said, ""I seek refuge in Allah from being among the ignorant", "وَإِذۡ قَالَ مُوسَىٰ لِقَوۡمِهِۦٓ إِنَّ ٱللَّهَ يَأۡمُرُكُمۡ أَن تَذۡبَحُواْ بَقَرَةٗۖ قَالُوٓاْ أَتَتَّخِذُنَا هُزُوٗاۖ قَالَ أَعُوذُ بِٱللَّهِ أَنۡ أَكُونَ مِنَ ٱلۡجَٰهِلِينَ", "Waith qala moosa liqawmihi inna Allaha yamurukum an tathbahoo baqaratan qaloo atattakhithuna huzuwan qala aAAoothu biAllahi an akoona mina aljahileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 68, "They said, ""Call upon your Lord to make clear to us what it is."" [Moses] said, ""[Allah] says, 'It is a cow which is neither old nor virgin, but median between that,' so do what you are commanded", "قَالُواْ ٱدۡعُ لَنَا رَبَّكَ يُبَيِّن لَّنَا مَا هِيَۚ قَالَ إِنَّهُۥ يَقُولُ إِنَّهَا بَقَرَةٞ لَّا فَارِضٞ وَلَا بِكۡرٌ عَوَانُۢ بَيۡنَ ذَٰلِكَۖ فَٱفۡعَلُواْ مَا تُؤۡمَرُونَ", "Qaloo odAAu lana rabbaka yubayyin lana ma hiya qala innahu yaqoolu innaha baqaratun la faridun wala bikrun AAawanun bayna thalika faifAAaloo ma tumaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 69, "They said, ""Call upon your Lord to show us what is her color."" He said, ""He says, 'It is a yellow cow, bright in color - pleasing to the observers", "قَالُواْ ٱدۡعُ لَنَا رَبَّكَ يُبَيِّن لَّنَا مَا لَوۡنُهَاۚ قَالَ إِنَّهُۥ يَقُولُ إِنَّهَا بَقَرَةٞ صَفۡرَآءُ فَاقِعٞ لَّوۡنُهَا تَسُرُّ ٱلنَّـٰظِرِينَ", "Qaloo odAAu lana rabbaka yubayyin lana ma lawnuha qala innahu yaqoolu innaha baqaratun safrao faqiAAun lawnuha tasurru alnnathireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 70, "They said, ""Call upon your Lord to make clear to us what it is. Indeed, [all] cows look alike to us. And indeed we, if Allah wills, will be guided", "قَالُواْ ٱدۡعُ لَنَا رَبَّكَ يُبَيِّن لَّنَا مَا هِيَ إِنَّ ٱلۡبَقَرَ تَشَٰبَهَ عَلَيۡنَا وَإِنَّآ إِن شَآءَ ٱللَّهُ لَمُهۡتَدُونَ", "Qaloo odAAu lana rabbaka yubayyin lana ma hiya inna albaqara tashabaha AAalayna wainna in shaa Allahu lamuhtadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 71, "He said, ""He says, 'It is a cow neither trained to plow the earth nor to irrigate the field, one free from fault with no spot upon her.' "" They said, ""Now you have come with the truth."" So they slaughtered her, but they could hardly do it", "قَالَ إِنَّهُۥ يَقُولُ إِنَّهَا بَقَرَةٞ لَّا ذَلُولٞ تُثِيرُ ٱلۡأَرۡضَ وَلَا تَسۡقِي ٱلۡحَرۡثَ مُسَلَّمَةٞ لَّا شِيَةَ فِيهَاۚ قَالُواْ ٱلۡـَٰٔنَ جِئۡتَ بِٱلۡحَقِّۚ فَذَبَحُوهَا وَمَا كَادُواْ يَفۡعَلُونَ", "Qala innahu yaqoolu innaha baqaratun la thaloolun tutheeru alarda wala tasqee alhartha musallamatun la shiyata feeha qaloo alana jita bialhaqqi fathabahooha wama kadoo yafAAaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 72, "And [recall] when you slew a man and disputed over it, but Allah was to bring out that which you were concealing", "وَإِذۡ قَتَلۡتُمۡ نَفۡسٗا فَٱدَّـٰرَ ٰٔتُمۡ فِيهَاۖ وَٱللَّهُ مُخۡرِجٞ مَّا كُنتُمۡ تَكۡتُمُونَ", "Waith qataltum nafsan faiddaratum feeha waAllahu mukhrijun ma kuntum taktumoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 73, "So, We said, ""Strike the slain man with part of it."" Thus does Allah bring the dead to life, and He shows you His signs that you might reason", "فَقُلۡنَا ٱضۡرِبُوهُ بِبَعۡضِهَاۚ كَذَٰلِكَ يُحۡيِ ٱللَّهُ ٱلۡمَوۡتَىٰ وَيُرِيكُمۡ ءَايَٰتِهِۦ لَعَلَّكُمۡ تَعۡقِلُونَ", "Faqulna idriboohu bibaAAdiha kathalika yuhyee Allahu almawta wayureekum ayatihi laAAallakum taAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 74, "Then your hearts became hardened after that, being like stones or even harder. For indeed, there are stones from which rivers burst forth, and there are some of them that split open and water comes out, and there are some of them that fall down for fear of Allah. And Allah is not unaware of what you do", "ثُمَّ قَسَتۡ قُلُوبُكُم مِّنۢ بَعۡدِ ذَٰلِكَ فَهِيَ كَٱلۡحِجَارَةِ أَوۡ أَشَدُّ قَسۡوَةٗۚ وَإِنَّ مِنَ ٱلۡحِجَارَةِ لَمَا يَتَفَجَّرُ مِنۡهُ ٱلۡأَنۡهَٰرُۚ وَإِنَّ مِنۡهَا لَمَا يَشَّقَّقُ فَيَخۡرُجُ مِنۡهُ ٱلۡمَآءُۚ وَإِنَّ مِنۡهَا لَمَا يَهۡبِطُ مِنۡ خَشۡيَةِ ٱللَّهِۗ وَمَا ٱللَّهُ بِغَٰفِلٍ عَمَّا تَعۡمَلُونَ", "Thumma qasat quloobukum min baAAdi thalika fahiya kaalhijarati aw ashaddu qaswatan wainna mina alhijarati lama yatafajjaru minhu alanharu wainna minha lama yashshaqqaqu fayakhruju minhu almao wainna minha lama yahbitu min khashyati Allahi wama Allahu bighafilin AAamma taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 75, "Do you covet [the hope, O believers], that they would believe for you while a party of them used to hear the words of Allah and then distort the Torah after they had understood it while they were knowing", "۞أَفَتَطۡمَعُونَ أَن يُؤۡمِنُواْ لَكُمۡ وَقَدۡ كَانَ فَرِيقٞ مِّنۡهُمۡ يَسۡمَعُونَ كَلَٰمَ ٱللَّهِ ثُمَّ يُحَرِّفُونَهُۥ مِنۢ بَعۡدِ مَا عَقَلُوهُ وَهُمۡ يَعۡلَمُونَ", "AfatatmaAAoona an yuminoo lakum waqad kana fareequn minhum yasmaAAoona kalama Allahi thumma yuharrifoonahu min baAAdi ma AAaqaloohu wahum yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 76, "And when they meet those who believe, they say, ""We have believed""; but when they are alone with one another, they say, ""Do you talk to them about what Allah has revealed to you so they can argue with you about it before your Lord?"" Then will you not reason", "وَإِذَا لَقُواْ ٱلَّذِينَ ءَامَنُواْ قَالُوٓاْ ءَامَنَّا وَإِذَا خَلَا بَعۡضُهُمۡ إِلَىٰ بَعۡضٖ قَالُوٓاْ أَتُحَدِّثُونَهُم بِمَا فَتَحَ ٱللَّهُ عَلَيۡكُمۡ لِيُحَآجُّوكُم بِهِۦ عِندَ رَبِّكُمۡۚ أَفَلَا تَعۡقِلُونَ", "Waitha laqoo allatheena amanoo qaloo amanna waitha khala baAAduhum ila baAAdin qaloo atuhaddithoonahum bima fataha Allahu AAalaykum liyuhajjookum bihi AAinda rabbikum afala taAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 77, "But do they not know that Allah knows what they conceal and what they declare", "أَوَلَا يَعۡلَمُونَ أَنَّ ٱللَّهَ يَعۡلَمُ مَا يُسِرُّونَ وَمَا يُعۡلِنُونَ", "Awala yaAAlamoona anna Allaha yaAAlamu ma yusirroona wama yuAAlinoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 78, "And among them are unlettered ones who do not know the Scripture except in wishful thinking, but they are only assuming", "وَمِنۡهُمۡ أُمِّيُّونَ لَا يَعۡلَمُونَ ٱلۡكِتَٰبَ إِلَّآ أَمَانِيَّ وَإِنۡ هُمۡ إِلَّا يَظُنُّونَ", "Waminhum ommiyyoona la yaAAlamoona alkitaba illa amaniyya wain hum illa yathunnoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 79, "So woe to those who write the ""scripture"" with their own hands, then say, ""This is from Allah,"" in order to exchange it for a small price. Woe to them for what their hands have written and woe to them for what they earn", "فَوَيۡلٞ لِّلَّذِينَ يَكۡتُبُونَ ٱلۡكِتَٰبَ بِأَيۡدِيهِمۡ ثُمَّ يَقُولُونَ هَٰذَا مِنۡ عِندِ ٱللَّهِ لِيَشۡتَرُواْ بِهِۦ ثَمَنٗا قَلِيلٗاۖ فَوَيۡلٞ لَّهُم مِّمَّا كَتَبَتۡ أَيۡدِيهِمۡ وَوَيۡلٞ لَّهُم مِّمَّا يَكۡسِبُونَ", "Fawaylun lillatheena yaktuboona alkitaba biaydeehim thumma yaqooloona hatha min AAindi Allahi liyashtaroo bihi thamanan qaleelan fawaylun lahum mimma katabat aydeehim wawaylun lahum mimma yaksiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 80, "And they say, ""Never will the Fire touch us, except for a few days."" Say, ""Have you taken a covenant with Allah? For Allah will never break His covenant. Or do you say about Allah that which you do not know", "وَقَالُواْ لَن تَمَسَّنَا ٱلنَّارُ إِلَّآ أَيَّامٗا مَّعۡدُودَةٗۚ قُلۡ أَتَّخَذۡتُمۡ عِندَ ٱللَّهِ عَهۡدٗا فَلَن يُخۡلِفَ ٱللَّهُ عَهۡدَهُۥٓۖ أَمۡ تَقُولُونَ عَلَى ٱللَّهِ مَا لَا تَعۡلَمُونَ", "Waqaloo lan tamassana alnnaru illa ayyaman maAAdoodatan qul attakhathtum AAinda Allahi AAahdan falan yukhlifa Allahu AAahdahu am taqooloona AAala Allahi ma la taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 81, "Yes, whoever earns evil and his sin has encompassed him - those are the companions of the Fire; they will abide therein eternally", "بَلَىٰۚ مَن كَسَبَ سَيِّئَةٗ وَأَحَٰطَتۡ بِهِۦ خَطِيٓـَٔتُهُۥ فَأُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلنَّارِۖ هُمۡ فِيهَا خَٰلِدُونَ", "Bala man kasaba sayyiatan waahatat bihi khateeatuhu faolaika ashabu alnnari hum feeha khalidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 82, "But they who believe and do righteous deeds - those are the companions of Paradise; they will abide therein eternally", "وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلۡجَنَّةِۖ هُمۡ فِيهَا خَٰلِدُونَ", "Waallatheena amanoo waAAamiloo alssalihati olaika ashabu aljannati hum feeha khalidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 83, "And [recall] when We took the covenant from the Children of Israel, [enjoining upon them], ""Do not worship except Allah; and to parents do good and to relatives, orphans, and the needy. And speak to people good [words] and establish prayer and give zakah."" Then you turned away, except a few of you, and you were refusing", "وَإِذۡ أَخَذۡنَا مِيثَٰقَ بَنِيٓ إِسۡرَـٰٓءِيلَ لَا تَعۡبُدُونَ إِلَّا ٱللَّهَ وَبِٱلۡوَٰلِدَيۡنِ إِحۡسَانٗا وَذِي ٱلۡقُرۡبَىٰ وَٱلۡيَتَٰمَىٰ وَٱلۡمَسَٰكِينِ وَقُولُواْ لِلنَّاسِ حُسۡنٗا وَأَقِيمُواْ ٱلصَّلَوٰةَ وَءَاتُواْ ٱلزَّكَوٰةَ ثُمَّ تَوَلَّيۡتُمۡ إِلَّا قَلِيلٗا مِّنكُمۡ وَأَنتُم مُّعۡرِضُونَ", "Waith akhathna meethaqa banee israeela la taAAbudoona illa Allaha wabialwalidayni ihsanan wathee alqurba waalyatama waalmasakeeni waqooloo lilnnasi husnan waaqeemoo alssalata waatoo alzzakata thumma tawallaytum illa qaleelan minkum waantum muAAridoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 84, "And [recall] when We took your covenant, [saying], ""Do not shed each other's blood or evict one another from your homes."" Then you acknowledged [this] while you were witnessing", "وَإِذۡ أَخَذۡنَا مِيثَٰقَكُمۡ لَا تَسۡفِكُونَ دِمَآءَكُمۡ وَلَا تُخۡرِجُونَ أَنفُسَكُم مِّن دِيَٰرِكُمۡ ثُمَّ أَقۡرَرۡتُمۡ وَأَنتُمۡ تَشۡهَدُونَ", "Waith akhathna meethaqakum la tasfikoona dimaakum wala tukhrijoona anfusakum min diyarikum thumma aqrartum waantum tashhadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 85, "Then, you are those [same ones who are] killing one another and evicting a party of your people from their homes, cooperating against them in sin and aggression. And if they come to you as captives, you ransom them, although their eviction was forbidden to you. So do you believe in part of the Scripture and disbelieve in part? Then what is the recompense for those who do that among you except disgrace in worldly life; and on the Day of Resurrection they will be sent back to the severest of punishment. And Allah is not unaware of what you do", "ثُمَّ أَنتُمۡ هَـٰٓؤُلَآءِ تَقۡتُلُونَ أَنفُسَكُمۡ وَتُخۡرِجُونَ فَرِيقٗا مِّنكُم مِّن دِيَٰرِهِمۡ تَظَٰهَرُونَ عَلَيۡهِم بِٱلۡإِثۡمِ وَٱلۡعُدۡوَٰنِ وَإِن يَأۡتُوكُمۡ أُسَٰرَىٰ تُفَٰدُوهُمۡ وَهُوَ مُحَرَّمٌ عَلَيۡكُمۡ إِخۡرَاجُهُمۡۚ أَفَتُؤۡمِنُونَ بِبَعۡضِ ٱلۡكِتَٰبِ وَتَكۡفُرُونَ بِبَعۡضٖۚ فَمَا جَزَآءُ مَن يَفۡعَلُ ذَٰلِكَ مِنكُمۡ إِلَّا خِزۡيٞ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۖ وَيَوۡمَ ٱلۡقِيَٰمَةِ يُرَدُّونَ إِلَىٰٓ أَشَدِّ ٱلۡعَذَابِۗ وَمَا ٱللَّهُ بِغَٰفِلٍ عَمَّا تَعۡمَلُونَ", "Thumma antum haolai taqtuloona anfusakum watukhrijoona fareeqan minkum min diyarihim tathaharoona AAalayhim bialithmi waalAAudwani wain yatookum osara tufadoohum wahuwa muharramun AAalaykum ikhrajuhum afatuminoona bibaAAdi alkitabi watakfuroona bibaAAdin fama jazao man yafAAalu thalika minkum illa khizyun fee alhayati alddunya wayawma alqiyamati yuraddoona ila ashaddi alAAathabi wama Allahu bighafilin AAamma taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 86, "Those are the ones who have bought the life of this world [in exchange] for the Hereafter, so the punishment will not be lightened for them, nor will they be aided", "أُوْلَـٰٓئِكَ ٱلَّذِينَ ٱشۡتَرَوُاْ ٱلۡحَيَوٰةَ ٱلدُّنۡيَا بِٱلۡأٓخِرَةِۖ فَلَا يُخَفَّفُ عَنۡهُمُ ٱلۡعَذَابُ وَلَا هُمۡ يُنصَرُونَ", "Olaika allatheena ishtarawoo alhayata alddunya bialakhirati fala yukhaffafu AAanhumu alAAathabu wala hum yunsaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 87, "And We did certainly give Moses the Torah and followed up after him with messengers. And We gave Jesus, the son of Mary, clear proofs and supported him with the Pure Spirit. But is it [not] that every time a messenger came to you, [O Children of Israel], with what your souls did not desire, you were arrogant? And a party [of messengers] you denied and another party you killed", "وَلَقَدۡ ءَاتَيۡنَا مُوسَى ٱلۡكِتَٰبَ وَقَفَّيۡنَا مِنۢ بَعۡدِهِۦ بِٱلرُّسُلِۖ وَءَاتَيۡنَا عِيسَى ٱبۡنَ مَرۡيَمَ ٱلۡبَيِّنَٰتِ وَأَيَّدۡنَٰهُ بِرُوحِ ٱلۡقُدُسِۗ أَفَكُلَّمَا جَآءَكُمۡ رَسُولُۢ بِمَا لَا تَهۡوَىٰٓ أَنفُسُكُمُ ٱسۡتَكۡبَرۡتُمۡ فَفَرِيقٗا كَذَّبۡتُمۡ وَفَرِيقٗا تَقۡتُلُونَ", "Walaqad atayna moosa alkitaba waqaffayna min baAAdihi bialrrusuli waatayna AAeesa ibna maryama albayyinati waayyadnahu biroohi alqudusi afakullama jaakum rasoolun bima la tahwa anfusukumu istakbartum fafareeqan kaththabtum wafareeqan taqtuloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 88, "And they said, ""Our hearts are wrapped."" But, [in fact], Allah has cursed them for their disbelief, so little is it that they believe", "وَقَالُواْ قُلُوبُنَا غُلۡفُۢۚ بَل لَّعَنَهُمُ ٱللَّهُ بِكُفۡرِهِمۡ فَقَلِيلٗا مَّا يُؤۡمِنُونَ", "Waqaloo quloobuna ghulfun bal laAAanahumu Allahu bikufrihim faqaleelan ma yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 89, "And when there came to them a Book from Allah confirming that which was with them - although before they used to pray for victory against those who disbelieved - but [then] when there came to them that which they recognized, they disbelieved in it; so the curse of Allah will be upon the disbelievers", "وَلَمَّا جَآءَهُمۡ كِتَٰبٞ مِّنۡ عِندِ ٱللَّهِ مُصَدِّقٞ لِّمَا مَعَهُمۡ وَكَانُواْ مِن قَبۡلُ يَسۡتَفۡتِحُونَ عَلَى ٱلَّذِينَ كَفَرُواْ فَلَمَّا جَآءَهُم مَّا عَرَفُواْ كَفَرُواْ بِهِۦۚ فَلَعۡنَةُ ٱللَّهِ عَلَى ٱلۡكَٰفِرِينَ", "Walamma jaahum kitabun min AAindi Allahi musaddiqun lima maAAahum wakanoo min qablu yastaftihoona AAala allatheena kafaroo falamma jaahum ma AAarafoo kafaroo bihi falaAAnatu Allahi AAala alkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 90, "How wretched is that for which they sold themselves - that they would disbelieve in what Allah has revealed through [their] outrage that Allah would send down His favor upon whom He wills from among His servants. So they returned having [earned] wrath upon wrath. And for the disbelievers is a humiliating punishment", "بِئۡسَمَا ٱشۡتَرَوۡاْ بِهِۦٓ أَنفُسَهُمۡ أَن يَكۡفُرُواْ بِمَآ أَنزَلَ ٱللَّهُ بَغۡيًا أَن يُنَزِّلَ ٱللَّهُ مِن فَضۡلِهِۦ عَلَىٰ مَن يَشَآءُ مِنۡ عِبَادِهِۦۖ فَبَآءُو بِغَضَبٍ عَلَىٰ غَضَبٖۚ وَلِلۡكَٰفِرِينَ عَذَابٞ مُّهِينٞ", "Bisama ishtaraw bihi anfusahum an yakfuroo bima anzala Allahu baghyan an yunazzila Allahu min fadlihi AAala man yashao min AAibadihi fabaoo bighadabin AAala ghadabin walilkafireena AAathabun muheenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 91, "And when it is said to them, ""Believe in what Allah has revealed,"" they say, ""We believe [only] in what was revealed to us."" And they disbelieve in what came after it, while it is the truth confirming that which is with them. Say, ""Then why did you kill the prophets of Allah before, if you are [indeed] believers", "وَإِذَا قِيلَ لَهُمۡ ءَامِنُواْ بِمَآ أَنزَلَ ٱللَّهُ قَالُواْ نُؤۡمِنُ بِمَآ أُنزِلَ عَلَيۡنَا وَيَكۡفُرُونَ بِمَا وَرَآءَهُۥ وَهُوَ ٱلۡحَقُّ مُصَدِّقٗا لِّمَا مَعَهُمۡۗ قُلۡ فَلِمَ تَقۡتُلُونَ أَنۢبِيَآءَ ٱللَّهِ مِن قَبۡلُ إِن كُنتُم مُّؤۡمِنِينَ", "Waitha qeela lahum aminoo bima anzala Allahu qaloo numinu bima onzila AAalayna wayakfuroona bima waraahu wahuwa alhaqqu musaddiqan lima maAAahum qul falima taqtuloona anbiyaa Allahi min qablu in kuntum mumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 92, "And Moses had certainly brought you clear proofs. Then you took the calf [in worship] after that, while you were wrongdoers", "۞وَلَقَدۡ جَآءَكُم مُّوسَىٰ بِٱلۡبَيِّنَٰتِ ثُمَّ ٱتَّخَذۡتُمُ ٱلۡعِجۡلَ مِنۢ بَعۡدِهِۦ وَأَنتُمۡ ظَٰلِمُونَ", "Walaqad jaakum moosa bialbayyinati thumma ittakhathtumu alAAijla min baAAdihi waantum thalimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 93, "And [recall] when We took your covenant and raised over you the mount, [saying], ""Take what We have given you with determination and listen."" They said [instead], ""We hear and disobey."" And their hearts absorbed [the worship of] the calf because of their disbelief. Say, ""How wretched is that which your faith enjoins upon you, if you should be believers", "وَإِذۡ أَخَذۡنَا مِيثَٰقَكُمۡ وَرَفَعۡنَا فَوۡقَكُمُ ٱلطُّورَ خُذُواْ مَآ ءَاتَيۡنَٰكُم بِقُوَّةٖ وَٱسۡمَعُواْۖ قَالُواْ سَمِعۡنَا وَعَصَيۡنَا وَأُشۡرِبُواْ فِي قُلُوبِهِمُ ٱلۡعِجۡلَ بِكُفۡرِهِمۡۚ قُلۡ بِئۡسَمَا يَأۡمُرُكُم بِهِۦٓ إِيمَٰنُكُمۡ إِن كُنتُم مُّؤۡمِنِينَ", "Waith akhathna meethaqakum warafaAAna fawqakumu alttoora khuthoo ma ataynakum biquwwatin waismaAAoo qaloo samiAAna waAAasayna waoshriboo fee quloobihimu alAAijla bikufrihim qul bisama yamurukum bihi eemanukum in kuntum mumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 94, "Say, [O Muhammad], ""If the home of the Hereafter with Allah is for you alone and not the [other] people, then wish for death, if you should be truthful", "قُلۡ إِن كَانَتۡ لَكُمُ ٱلدَّارُ ٱلۡأٓخِرَةُ عِندَ ٱللَّهِ خَالِصَةٗ مِّن دُونِ ٱلنَّاسِ فَتَمَنَّوُاْ ٱلۡمَوۡتَ إِن كُنتُمۡ صَٰدِقِينَ", "Qul in kanat lakumu alddaru alakhiratu AAinda Allahi khalisatan min dooni alnnasi fatamannawoo almawta in kuntum sadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 95, "But they will never wish for it, ever, because of what their hands have put forth. And Allah is Knowing of the wrongdoers", "وَلَن يَتَمَنَّوۡهُ أَبَدَۢا بِمَا قَدَّمَتۡ أَيۡدِيهِمۡۚ وَٱللَّهُ عَلِيمُۢ بِٱلظَّـٰلِمِينَ", "Walan yatamannawhu abadan bima qaddamat aydeehim waAllahu AAaleemun bialththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 96, "And you will surely find them the most greedy of people for life - [even] more than those who associate others with Allah. One of them wishes that he could be granted life a thousand years, but it would not remove him in the least from the [coming] punishment that he should be granted life. And Allah is Seeing of what they do", "وَلَتَجِدَنَّهُمۡ أَحۡرَصَ ٱلنَّاسِ عَلَىٰ حَيَوٰةٖ وَمِنَ ٱلَّذِينَ أَشۡرَكُواْۚ يَوَدُّ أَحَدُهُمۡ لَوۡ يُعَمَّرُ أَلۡفَ سَنَةٖ وَمَا هُوَ بِمُزَحۡزِحِهِۦ مِنَ ٱلۡعَذَابِ أَن يُعَمَّرَۗ وَٱللَّهُ بَصِيرُۢ بِمَا يَعۡمَلُونَ", "Walatajidannahum ahrasa alnnasi AAala hayatin wamina allatheena ashrakoo yawaddu ahaduhum law yuAAammaru alfa sanatin wama huwa bimuzahzihihi mina alAAathabi an yuAAammara waAllahu baseerun bima yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 97, "Say, ""Whoever is an enemy to Gabriel - it is [none but] he who has brought the Qur'an down upon your heart, [O Muhammad], by permission of Allah, confirming that which was before it and as guidance and good tidings for the believers", "قُلۡ مَن كَانَ عَدُوّٗا لِّـجِبۡرِيلَ فَإِنَّهُۥ نَزَّلَهُۥ عَلَىٰ قَلۡبِكَ بِإِذۡنِ ٱللَّهِ مُصَدِّقٗا لِّمَا بَيۡنَ يَدَيۡهِ وَهُدٗى وَبُشۡرَىٰ لِلۡمُؤۡمِنِينَ", "Qul man kana AAaduwwan lijibreela fainnahu nazzalahu AAala qalbika biithni Allahi musaddiqan lima bayna yadayhi wahudan wabushra lilmumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 98, "Whoever is an enemy to Allah and His angels and His messengers and Gabriel and Michael - then indeed, Allah is an enemy to the disbelievers", "مَن كَانَ عَدُوّٗا لِّلَّهِ وَمَلَـٰٓئِكَتِهِۦ وَرُسُلِهِۦ وَجِبۡرِيلَ وَمِيكَىٰلَ فَإِنَّ ٱللَّهَ عَدُوّٞ لِّلۡكَٰفِرِينَ", "Man kana AAaduwwan lillahi wamalaikatihi warusulihi wajibreela wameekala fainna Allaha AAaduwwun lilkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 99, "And We have certainly revealed to you verses [which are] clear proofs, and no one would deny them except the defiantly disobedient", "وَلَقَدۡ أَنزَلۡنَآ إِلَيۡكَ ءَايَٰتِۭ بَيِّنَٰتٖۖ وَمَا يَكۡفُرُ بِهَآ إِلَّا ٱلۡفَٰسِقُونَ", "Walaqad anzalna ilayka ayatin bayyinatin wama yakfuru biha illa alfasiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 100, "Is it not [true] that every time they took a covenant a party of them threw it away? But, [in fact], most of them do not believe", "أَوَكُلَّمَا عَٰهَدُواْ عَهۡدٗا نَّبَذَهُۥ فَرِيقٞ مِّنۡهُمۚ بَلۡ أَكۡثَرُهُمۡ لَا يُؤۡمِنُونَ", "Awakullama AAahadoo AAahdan nabathahu fareequn minhum bal aktharuhum la yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 101, "And when a messenger from Allah came to them confirming that which was with them, a party of those who had been given the Scripture threw the Scripture of Allah behind their backs as if they did not know [what it contained]", "وَلَمَّا جَآءَهُمۡ رَسُولٞ مِّنۡ عِندِ ٱللَّهِ مُصَدِّقٞ لِّمَا مَعَهُمۡ نَبَذَ فَرِيقٞ مِّنَ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ كِتَٰبَ ٱللَّهِ وَرَآءَ ظُهُورِهِمۡ كَأَنَّهُمۡ لَا يَعۡلَمُونَ", "Walamma jaahum rasoolun min AAindi Allahi musaddiqun lima maAAahum nabatha fareequn mina allatheena ootoo alkitaba kitaba Allahi waraa thuhoorihim kaannahum la yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 102, "And they followed [instead] what the devils had recited during the reign of Solomon. It was not Solomon who disbelieved, but the devils disbelieved, teaching people magic and that which was revealed to the two angels at Babylon, Harut and Marut. But the two angels do not teach anyone unless they say, ""We are a trial, so do not disbelieve [by practicing magic]."" And [yet] they learn from them that by which they cause separation between a man and his wife. But they do not harm anyone through it except by permission of Allah. And the people learn what harms them and does not benefit them. But the Children of Israel certainly knew that whoever purchased the magic would not have in the Hereafter any share. And wretched is that for which they sold themselves, if they only knew", "وَٱتَّبَعُواْ مَا تَتۡلُواْ ٱلشَّيَٰطِينُ عَلَىٰ مُلۡكِ سُلَيۡمَٰنَۖ وَمَا كَفَرَ سُلَيۡمَٰنُ وَلَٰكِنَّ ٱلشَّيَٰطِينَ كَفَرُواْ يُعَلِّمُونَ ٱلنَّاسَ ٱلسِّحۡرَ وَمَآ أُنزِلَ عَلَى ٱلۡمَلَكَيۡنِ بِبَابِلَ هَٰرُوتَ وَمَٰرُوتَۚ وَمَا يُعَلِّمَانِ مِنۡ أَحَدٍ حَتَّىٰ يَقُولَآ إِنَّمَا نَحۡنُ فِتۡنَةٞ فَلَا تَكۡفُرۡۖ فَيَتَعَلَّمُونَ مِنۡهُمَا مَا يُفَرِّقُونَ بِهِۦ بَيۡنَ ٱلۡمَرۡءِ وَزَوۡجِهِۦۚ وَمَا هُم بِضَآرِّينَ بِهِۦ مِنۡ أَحَدٍ إِلَّا بِإِذۡنِ ٱللَّهِۚ وَيَتَعَلَّمُونَ مَا يَضُرُّهُمۡ وَلَا يَنفَعُهُمۡۚ وَلَقَدۡ عَلِمُواْ لَمَنِ ٱشۡتَرَىٰهُ مَا لَهُۥ فِي ٱلۡأٓخِرَةِ مِنۡ خَلَٰقٖۚ وَلَبِئۡسَ مَا شَرَوۡاْ بِهِۦٓ أَنفُسَهُمۡۚ لَوۡ كَانُواْ يَعۡلَمُونَ", "WaittabaAAoo ma tatloo alshshayateenu AAala mulki sulaymana wama kafara sulaymanu walakinna alshshayateena kafaroo yuAAallimoona alnnasa alssihra wama onzila AAala almalakayni bibabila haroota wamaroota wama yuAAallimani min ahadin hatta yaqoola innama nahnu fitnatun fala takfur fayataAAallamoona minhuma ma yufarriqoona bihi bayna almari wazawjihi wama hum bidarreena bihi min ahadin illa biithni Allahi wayataAAallamoona ma yadurruhum wala yanfaAAuhum walaqad AAalimoo lamani ishtarahu ma lahu fee alakhirati min khalaqin walabisa ma sharaw bihi anfusahum law kanoo yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 103, "And if they had believed and feared Allah, then the reward from Allah would have been [far] better, if they only knew", "وَلَوۡ أَنَّهُمۡ ءَامَنُواْ وَٱتَّقَوۡاْ لَمَثُوبَةٞ مِّنۡ عِندِ ٱللَّهِ خَيۡرٞۚ لَّوۡ كَانُواْ يَعۡلَمُونَ", "Walaw annahum amanoo waittaqaw lamathoobatun min AAindi Allahi khayrun law kanoo yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 104, "O you who have believed, say not [to Allah 's Messenger], ""Ra'ina"" but say, ""Unthurna"" and listen. And for the disbelievers is a painful punishment", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَقُولُواْ رَٰعِنَا وَقُولُواْ ٱنظُرۡنَا وَٱسۡمَعُواْۗ وَلِلۡكَٰفِرِينَ عَذَابٌ أَلِيمٞ", "Ya ayyuha allatheena amanoo la taqooloo raAAina waqooloo onthurna waismaAAoo walilkafireena AAathabun aleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 105, "Neither those who disbelieve from the People of the Scripture nor the polytheists wish that any good should be sent down to you from your Lord. But Allah selects for His mercy whom He wills, and Allah is the possessor of great bounty", "مَّا يَوَدُّ ٱلَّذِينَ كَفَرُواْ مِنۡ أَهۡلِ ٱلۡكِتَٰبِ وَلَا ٱلۡمُشۡرِكِينَ أَن يُنَزَّلَ عَلَيۡكُم مِّنۡ خَيۡرٖ مِّن رَّبِّكُمۡۚ وَٱللَّهُ يَخۡتَصُّ بِرَحۡمَتِهِۦ مَن يَشَآءُۚ وَٱللَّهُ ذُو ٱلۡفَضۡلِ ٱلۡعَظِيمِ", "Ma yawaddu allatheena kafaroo min ahli alkitabi wala almushrikeena an yunazzala AAalaykum min khayrin min rabbikum waAllahu yakhtassu birahmatihi man yashao waAllahu thoo alfadli alAAatheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 106, "We do not abrogate a verse or cause it to be forgotten except that We bring forth [one] better than it or similar to it. Do you not know that Allah is over all things competent", "۞مَا نَنسَخۡ مِنۡ ءَايَةٍ أَوۡ نُنسِهَا نَأۡتِ بِخَيۡرٖ مِّنۡهَآ أَوۡ مِثۡلِهَآۗ أَلَمۡ تَعۡلَمۡ أَنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ", "Ma nansakh min ayatin aw nunsiha nati bikhayrin minha aw mithliha alam taAAlam anna Allaha AAala kulli shayin qadeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 107, "Do you not know that to Allah belongs the dominion of the heavens and the earth and [that] you have not besides Allah any protector or any helper", "أَلَمۡ تَعۡلَمۡ أَنَّ ٱللَّهَ لَهُۥ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۗ وَمَا لَكُم مِّن دُونِ ٱللَّهِ مِن وَلِيّٖ وَلَا نَصِيرٍ", "Alam taAAlam anna Allaha lahu mulku alssamawati waalardi wama lakum min dooni Allahi min waliyyin wala naseerin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 108, "Or do you intend to ask your Messenger as Moses was asked before? And whoever exchanges faith for disbelief has certainly strayed from the soundness of the way", "أَمۡ تُرِيدُونَ أَن تَسۡـَٔلُواْ رَسُولَكُمۡ كَمَا سُئِلَ مُوسَىٰ مِن قَبۡلُۗ وَمَن يَتَبَدَّلِ ٱلۡكُفۡرَ بِٱلۡإِيمَٰنِ فَقَدۡ ضَلَّ سَوَآءَ ٱلسَّبِيلِ", "Am tureedoona an tasaloo rasoolakum kama suila moosa min qablu waman yatabaddali alkufra bialeemani faqad dalla sawaa alssabeeli");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 109, "Many of the People of the Scripture wish they could turn you back to disbelief after you have believed, out of envy from themselves [even] after the truth has become clear to them. So pardon and overlook until Allah delivers His command. Indeed, Allah is over all things competent", "وَدَّ كَثِيرٞ مِّنۡ أَهۡلِ ٱلۡكِتَٰبِ لَوۡ يَرُدُّونَكُم مِّنۢ بَعۡدِ إِيمَٰنِكُمۡ كُفَّارًا حَسَدٗا مِّنۡ عِندِ أَنفُسِهِم مِّنۢ بَعۡدِ مَا تَبَيَّنَ لَهُمُ ٱلۡحَقُّۖ فَٱعۡفُواْ وَٱصۡفَحُواْ حَتَّىٰ يَأۡتِيَ ٱللَّهُ بِأَمۡرِهِۦٓۗ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ", "Wadda katheerun min ahli alkitabi law yaruddoonakum min baAAdi eemanikum kuffaran hasadan min AAindi anfusihim min baAAdi ma tabayyana lahumu alhaqqu faoAAfoo waisfahoo hatta yatiya Allahu biamrihi inna Allaha AAala kulli shayin qadeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 110, "And establish prayer and give zakah, and whatever good you put forward for yourselves - you will find it with Allah. Indeed, Allah of what you do, is Seeing", "وَأَقِيمُواْ ٱلصَّلَوٰةَ وَءَاتُواْ ٱلزَّكَوٰةَۚ وَمَا تُقَدِّمُواْ لِأَنفُسِكُم مِّنۡ خَيۡرٖ تَجِدُوهُ عِندَ ٱللَّهِۗ إِنَّ ٱللَّهَ بِمَا تَعۡمَلُونَ بَصِيرٞ", "Waaqeemoo alssalata waatoo alzzakata wama tuqaddimoo lianfusikum min khayrin tajidoohu AAinda Allahi inna Allaha bima taAAmaloona baseerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 111, "And they say, ""None will enter Paradise except one who is a Jew or a Christian."" That is [merely] their wishful thinking, Say, ""Produce your proof, if you should be truthful", "وَقَالُواْ لَن يَدۡخُلَ ٱلۡجَنَّةَ إِلَّا مَن كَانَ هُودًا أَوۡ نَصَٰرَىٰۗ تِلۡكَ أَمَانِيُّهُمۡۗ قُلۡ هَاتُواْ بُرۡهَٰنَكُمۡ إِن كُنتُمۡ صَٰدِقِينَ", "Waqaloo lan yadkhula aljannata illa man kana hoodan aw nasara tilka amaniyyuhum qul hatoo burhanakum in kuntum sadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 112, "Yes [on the contrary], whoever submits his face in Islam to Allah while being a doer of good will have his reward with his Lord. And no fear will there be concerning them, nor will they grieve", "بَلَىٰۚ مَنۡ أَسۡلَمَ وَجۡهَهُۥ لِلَّهِ وَهُوَ مُحۡسِنٞ فَلَهُۥٓ أَجۡرُهُۥ عِندَ رَبِّهِۦ وَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ", "Bala man aslama wajhahu lillahi wahuwa muhsinun falahu ajruhu AAinda rabbihi wala khawfun AAalayhim wala hum yahzanoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 113, "The Jews say ""The Christians have nothing [true] to stand on,"" and the Christians say, ""The Jews have nothing to stand on,"" although they [both] recite the Scripture. Thus the polytheists speak the same as their words. But Allah will judge between them on the Day of Resurrection concerning that over which they used to differ", "وَقَالَتِ ٱلۡيَهُودُ لَيۡسَتِ ٱلنَّصَٰرَىٰ عَلَىٰ شَيۡءٖ وَقَالَتِ ٱلنَّصَٰرَىٰ لَيۡسَتِ ٱلۡيَهُودُ عَلَىٰ شَيۡءٖ وَهُمۡ يَتۡلُونَ ٱلۡكِتَٰبَۗ كَذَٰلِكَ قَالَ ٱلَّذِينَ لَا يَعۡلَمُونَ مِثۡلَ قَوۡلِهِمۡۚ فَٱللَّهُ يَحۡكُمُ بَيۡنَهُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ فِيمَا كَانُواْ فِيهِ يَخۡتَلِفُونَ", "Waqalati alyahoodu laysati alnnasara AAala shayin waqalati alnnasara laysati alyahoodu AAala shayin wahum yatloona alkitaba kathalika qala allatheena la yaAAlamoona mithla qawlihim faAllahu yahkumu baynahum yawma alqiyamati feema kanoo feehi yakhtalifoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 114, "And who are more unjust than those who prevent the name of Allah from being mentioned in His mosques and strive toward their destruction. It is not for them to enter them except in fear. For them in this world is disgrace, and they will have in the Hereafter a great punishment", "وَمَنۡ أَظۡلَمُ مِمَّن مَّنَعَ مَسَٰجِدَ ٱللَّهِ أَن يُذۡكَرَ فِيهَا ٱسۡمُهُۥ وَسَعَىٰ فِي خَرَابِهَآۚ أُوْلَـٰٓئِكَ مَا كَانَ لَهُمۡ أَن يَدۡخُلُوهَآ إِلَّا خَآئِفِينَۚ لَهُمۡ فِي ٱلدُّنۡيَا خِزۡيٞ وَلَهُمۡ فِي ٱلۡأٓخِرَةِ عَذَابٌ عَظِيمٞ", "Waman athlamu mimman manaAAa masajida Allahi an yuthkara feeha ismuhu wasaAAa fee kharabiha olaika ma kana lahum an yadkhulooha illa khaifeena lahum fee alddunya khizyun walahum fee alakhirati AAathabun AAatheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 115, "And to Allah belongs the east and the west. So wherever you [might] turn, there is the Face of Allah. Indeed, Allah is all-Encompassing and Knowing", "وَلِلَّهِ ٱلۡمَشۡرِقُ وَٱلۡمَغۡرِبُۚ فَأَيۡنَمَا تُوَلُّواْ فَثَمَّ وَجۡهُ ٱللَّهِۚ إِنَّ ٱللَّهَ وَٰسِعٌ عَلِيمٞ", "Walillahi almashriqu waalmaghribu faaynama tuwalloo fathamma wajhu Allahi inna Allaha wasiAAun AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 116, "They say, ""Allah has taken a son."" Exalted is He! Rather, to Him belongs whatever is in the heavens and the earth. All are devoutly obedient to Him", "وَقَالُواْ ٱتَّخَذَ ٱللَّهُ وَلَدٗاۗ سُبۡحَٰنَهُۥۖ بَل لَّهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ كُلّٞ لَّهُۥ قَٰنِتُونَ", "Waqaloo itakhatha Allahu waladan subhanahu bal lahu ma fee alssamawati waalardi kullun lahu qanitoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 117, "Originator of the heavens and the earth. When He decrees a matter, He only says to it, ""Be,"" and it is", "بَدِيعُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ وَإِذَا قَضَىٰٓ أَمۡرٗا فَإِنَّمَا يَقُولُ لَهُۥ كُن فَيَكُونُ", "BadeeAAu alssamawati waalardi waitha qada amran fainnama yaqoolu lahu kun fayakoonu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 118, "Those who do not know say, ""Why does Allah not speak to us or there come to us a sign?"" Thus spoke those before them like their words. Their hearts resemble each other. We have shown clearly the signs to a people who are certain [in faith]", "وَقَالَ ٱلَّذِينَ لَا يَعۡلَمُونَ لَوۡلَا يُكَلِّمُنَا ٱللَّهُ أَوۡ تَأۡتِينَآ ءَايَةٞۗ كَذَٰلِكَ قَالَ ٱلَّذِينَ مِن قَبۡلِهِم مِّثۡلَ قَوۡلِهِمۡۘ تَشَٰبَهَتۡ قُلُوبُهُمۡۗ قَدۡ بَيَّنَّا ٱلۡأٓيَٰتِ لِقَوۡمٖ يُوقِنُونَ", "Waqala allatheena la yaAAlamoona lawla yukallimuna Allahu aw tateena ayatun kathalika qala allatheena min qablihim mithla qawlihim tashabahat quloobuhum qad bayyanna alayati liqawmin yooqinoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 119, "Indeed, We have sent you, [O Muhammad], with the truth as a bringer of good tidings and a warner, and you will not be asked about the companions of Hellfire", "إِنَّآ أَرۡسَلۡنَٰكَ بِٱلۡحَقِّ بَشِيرٗا وَنَذِيرٗاۖ وَلَا تُسۡـَٔلُ عَنۡ أَصۡحَٰبِ ٱلۡجَحِيمِ", "Inna arsalnaka bialhaqqi basheeran wanatheeran wala tusalu Aaan ashabi aljaheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 120, "And never will the Jews or the Christians approve of you until you follow their religion. Say, ""Indeed, the guidance of Allah is the [only] guidance."" If you were to follow their desires after what has come to you of knowledge, you would have against Allah no protector or helper", "وَلَن تَرۡضَىٰ عَنكَ ٱلۡيَهُودُ وَلَا ٱلنَّصَٰرَىٰ حَتَّىٰ تَتَّبِعَ مِلَّتَهُمۡۗ قُلۡ إِنَّ هُدَى ٱللَّهِ هُوَ ٱلۡهُدَىٰۗ وَلَئِنِ ٱتَّبَعۡتَ أَهۡوَآءَهُم بَعۡدَ ٱلَّذِي جَآءَكَ مِنَ ٱلۡعِلۡمِ مَا لَكَ مِنَ ٱللَّهِ مِن وَلِيّٖ وَلَا نَصِيرٍ", "Walan tarda AAanka alyahoodu wala alnnasara hatta tattabiAAa millatahum qul inna huda Allahi huwa alhuda walaini ittabaAAta ahwaahum baAAda allathee jaaka mina alAAilmi ma laka mina Allahi min waliyyin wala naseerin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 121, "Those to whom We have given the Book recite it with its true recital. They [are the ones who] believe in it. And whoever disbelieves in it - it is they who are the losers", "ٱلَّذِينَ ءَاتَيۡنَٰهُمُ ٱلۡكِتَٰبَ يَتۡلُونَهُۥ حَقَّ تِلَاوَتِهِۦٓ أُوْلَـٰٓئِكَ يُؤۡمِنُونَ بِهِۦۗ وَمَن يَكۡفُرۡ بِهِۦ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡخَٰسِرُونَ", "Allatheena ataynahumu alkitaba yatloonahu haqqa tilawatihi olaika yuminoona bihi waman yakfur bihi faolaika humu alkhasiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 122, "O Children of Israel, remember My favor which I have bestowed upon you and that I preferred you over the worlds", "يَٰبَنِيٓ إِسۡرَـٰٓءِيلَ ٱذۡكُرُواْ نِعۡمَتِيَ ٱلَّتِيٓ أَنۡعَمۡتُ عَلَيۡكُمۡ وَأَنِّي فَضَّلۡتُكُمۡ عَلَى ٱلۡعَٰلَمِينَ", "Ya banee israeela othkuroo niAAmatiya allatee anAAamtu AAalaykum waannee faddaltukum AAala alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 123, "And fear a Day when no soul will suffice for another soul at all, and no compensation will be accepted from it, nor will any intercession benefit it, nor will they be aided", "وَٱتَّقُواْ يَوۡمٗا لَّا تَجۡزِي نَفۡسٌ عَن نَّفۡسٖ شَيۡـٔٗا وَلَا يُقۡبَلُ مِنۡهَا عَدۡلٞ وَلَا تَنفَعُهَا شَفَٰعَةٞ وَلَا هُمۡ يُنصَرُونَ", "Waittaqoo yawman la tajzee nafsun AAan nafsin shayan wala yuqbalu minha AAadlun wala tanfaAAuha shafaAAatun wala hum yunsaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 124, "And [mention, O Muhammad], when Abraham was tried by his Lord with commands and he fulfilled them. [Allah] said, ""Indeed, I will make you a leader for the people."" [Abraham] said, ""And of my descendants?"" [Allah] said, ""My covenant does not include the wrongdoers", "۞وَإِذِ ٱبۡتَلَىٰٓ إِبۡرَٰهِـۧمَ رَبُّهُۥ بِكَلِمَٰتٖ فَأَتَمَّهُنَّۖ قَالَ إِنِّي جَاعِلُكَ لِلنَّاسِ إِمَامٗاۖ قَالَ وَمِن ذُرِّيَّتِيۖ قَالَ لَا يَنَالُ عَهۡدِي ٱلظَّـٰلِمِينَ", "Waithi ibtala ibraheema rabbuhu bikalimatin faatammahunna qala innee jaAAiluka lilnnasi imaman qala wamin thurriyyatee qala la yanalu AAahdee alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 125, "And [mention] when We made the House a place of return for the people and [a place of] security. And take, [O believers], from the standing place of Abraham a place of prayer. And We charged Abraham and Ishmael, [saying], ""Purify My House for those who perform Tawaf and those who are staying [there] for worship and those who bow and prostrate [in prayer]", "وَإِذۡ جَعَلۡنَا ٱلۡبَيۡتَ مَثَابَةٗ لِّلنَّاسِ وَأَمۡنٗا وَٱتَّخِذُواْ مِن مَّقَامِ إِبۡرَٰهِـۧمَ مُصَلّٗىۖ وَعَهِدۡنَآ إِلَىٰٓ إِبۡرَٰهِـۧمَ وَإِسۡمَٰعِيلَ أَن طَهِّرَا بَيۡتِيَ لِلطَّآئِفِينَ وَٱلۡعَٰكِفِينَ وَٱلرُّكَّعِ ٱلسُّجُودِ", "Waith jaAAalna albayta mathabatan lilnnasi waamnan waittakhithoo min maqami ibraheema musallan waAAahidna ila ibraheema waismaAAeela an tahhira baytiya lilttaifeena waalAAakifeena waalrrukkaAAi alssujoodi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 126, "And [mention] when Abraham said, ""My Lord, make this a secure city and provide its people with fruits - whoever of them believes in Allah and the Last Day."" [Allah] said. ""And whoever disbelieves - I will grant him enjoyment for a little; then I will force him to the punishment of the Fire, and wretched is the destination", "وَإِذۡ قَالَ إِبۡرَٰهِـۧمُ رَبِّ ٱجۡعَلۡ هَٰذَا بَلَدًا ءَامِنٗا وَٱرۡزُقۡ أَهۡلَهُۥ مِنَ ٱلثَّمَرَٰتِ مَنۡ ءَامَنَ مِنۡهُم بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِۚ قَالَ وَمَن كَفَرَ فَأُمَتِّعُهُۥ قَلِيلٗا ثُمَّ أَضۡطَرُّهُۥٓ إِلَىٰ عَذَابِ ٱلنَّارِۖ وَبِئۡسَ ٱلۡمَصِيرُ", "Waith qala ibraheemu rabbi ijAAal hatha baladan aminan waorzuq ahlahu mina alththamarati man amana minhum biAllahi waalyawmi alakhiri qala waman kafara faomattiAAuhu qaleelan thumma adtarruhu ila AAathabi alnnari wabisa almaseeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 127, "And [mention] when Abraham was raising the foundations of the House and [with him] Ishmael, [saying], ""Our Lord, accept [this] from us. Indeed You are the Hearing, the Knowing", "وَإِذۡ يَرۡفَعُ إِبۡرَٰهِـۧمُ ٱلۡقَوَاعِدَ مِنَ ٱلۡبَيۡتِ وَإِسۡمَٰعِيلُ رَبَّنَا تَقَبَّلۡ مِنَّآۖ إِنَّكَ أَنتَ ٱلسَّمِيعُ ٱلۡعَلِيمُ", "Waith yarfaAAu ibraheemu alqawaAAida mina albayti waismaAAeelu rabbana taqabbal minna innaka anta alssameeAAu alAAaleemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 128, "Our Lord, and make us Muslims [in submission] to You and from our descendants a Muslim nation [in submission] to You. And show us our rites and accept our repentance. Indeed, You are the Accepting of repentance, the Merciful", "رَبَّنَا وَٱجۡعَلۡنَا مُسۡلِمَيۡنِ لَكَ وَمِن ذُرِّيَّتِنَآ أُمَّةٗ مُّسۡلِمَةٗ لَّكَ وَأَرِنَا مَنَاسِكَنَا وَتُبۡ عَلَيۡنَآۖ إِنَّكَ أَنتَ ٱلتَّوَّابُ ٱلرَّحِيمُ", "Rabbana waijAAalna muslimayni laka wamin thurriyyatina ommatan muslimatan laka waarina manasikana watub AAalayna innaka anta alttawwabu alrraheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 129, "Our Lord, and send among them a messenger from themselves who will recite to them Your verses and teach them the Book and wisdom and purify them. Indeed, You are the Exalted in Might, the Wise", "رَبَّنَا وَٱبۡعَثۡ فِيهِمۡ رَسُولٗا مِّنۡهُمۡ يَتۡلُواْ عَلَيۡهِمۡ ءَايَٰتِكَ وَيُعَلِّمُهُمُ ٱلۡكِتَٰبَ وَٱلۡحِكۡمَةَ وَيُزَكِّيهِمۡۖ إِنَّكَ أَنتَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ", "Rabbana waibAAath feehim rasoolan minhum yatloo AAalayhim ayatika wayuAAallimuhumu alkitaba waalhikmata wayuzakkeehim innaka anta alAAazeezu alhakeemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 130, "And who would be averse to the religion of Abraham except one who makes a fool of himself. And We had chosen him in this world, and indeed he, in the Hereafter, will be among the righteous", "وَمَن يَرۡغَبُ عَن مِّلَّةِ إِبۡرَٰهِـۧمَ إِلَّا مَن سَفِهَ نَفۡسَهُۥۚ وَلَقَدِ ٱصۡطَفَيۡنَٰهُ فِي ٱلدُّنۡيَاۖ وَإِنَّهُۥ فِي ٱلۡأٓخِرَةِ لَمِنَ ٱلصَّـٰلِحِينَ", "Waman yarghabu AAan millati ibraheema illa man safiha nafsahu walaqadi istafaynahu fee alddunya wainnahu fee alakhirati lamina alssaliheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 131, "When his Lord said to him, ""Submit"", he said ""I have submitted [in Islam] to the Lord of the worlds", "إِذۡ قَالَ لَهُۥ رَبُّهُۥٓ أَسۡلِمۡۖ قَالَ أَسۡلَمۡتُ لِرَبِّ ٱلۡعَٰلَمِينَ", "Ith qala lahu rabbuhu aslim qala aslamtu lirabbi alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 132, "And Abraham instructed his sons [to do the same] and [so did] Jacob, [saying], ""O my sons, indeed Allah has chosen for you this religion, so do not die except while you are Muslims", "وَوَصَّىٰ بِهَآ إِبۡرَٰهِـۧمُ بَنِيهِ وَيَعۡقُوبُ يَٰبَنِيَّ إِنَّ ٱللَّهَ ٱصۡطَفَىٰ لَكُمُ ٱلدِّينَ فَلَا تَمُوتُنَّ إِلَّا وَأَنتُم مُّسۡلِمُونَ", "Wawassa biha ibraheemu baneehi wayaAAqoobu ya baniyya inna Allaha istafa lakumu alddeena fala tamootunna illa waantum muslimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 133, "Or were you witnesses when death approached Jacob, when he said to his sons, ""What will you worship after me?"" They said, ""We will worship your God and the God of your fathers, Abraham and Ishmael and Isaac - one God. And we are Muslims [in submission] to Him", "أَمۡ كُنتُمۡ شُهَدَآءَ إِذۡ حَضَرَ يَعۡقُوبَ ٱلۡمَوۡتُ إِذۡ قَالَ لِبَنِيهِ مَا تَعۡبُدُونَ مِنۢ بَعۡدِيۖ قَالُواْ نَعۡبُدُ إِلَٰهَكَ وَإِلَٰهَ ءَابَآئِكَ إِبۡرَٰهِـۧمَ وَإِسۡمَٰعِيلَ وَإِسۡحَٰقَ إِلَٰهٗا وَٰحِدٗا وَنَحۡنُ لَهُۥ مُسۡلِمُونَ", "Am kuntum shuhadaa ith hadara yaAAqooba almawtu ith qala libaneehi ma taAAbudoona min baAAdee qaloo naAAbudu ilahaka wailaha abaika ibraheema waismaAAeela waishaqa ilahan wahidan wanahnu lahu muslimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 134, "That was a nation which has passed on. It will have [the consequence of] what it earned, and you will have what you have earned. And you will not be asked about what they used to do", "تِلۡكَ أُمَّةٞ قَدۡ خَلَتۡۖ لَهَا مَا كَسَبَتۡ وَلَكُم مَّا كَسَبۡتُمۡۖ وَلَا تُسۡـَٔلُونَ عَمَّا كَانُواْ يَعۡمَلُونَ", "Tilka ommatun qad khalat laha ma kasabat walakum ma kasabtum wala tusaloona AAamma kano yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 135, "They say, ""Be Jews or Christians [so] you will be guided."" Say, ""Rather, [we follow] the religion of Abraham, inclining toward truth, and he was not of the polytheists", "وَقَالُواْ كُونُواْ هُودًا أَوۡ نَصَٰرَىٰ تَهۡتَدُواْۗ قُلۡ بَلۡ مِلَّةَ إِبۡرَٰهِـۧمَ حَنِيفٗاۖ وَمَا كَانَ مِنَ ٱلۡمُشۡرِكِينَ", "Waqaloo koonoo hoodan aw nasara tahtadoo qul bal millata ibraheema haneefan wama kana mina almushrikeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 136, "Say, [O believers], ""We have believed in Allah and what has been revealed to us and what has been revealed to Abraham and Ishmael and Isaac and Jacob and the Descendants and what was given to Moses and Jesus and what was given to the prophets from their Lord. We make no distinction between any of them, and we are Muslims [in submission] to Him", "قُولُوٓاْ ءَامَنَّا بِٱللَّهِ وَمَآ أُنزِلَ إِلَيۡنَا وَمَآ أُنزِلَ إِلَىٰٓ إِبۡرَٰهِـۧمَ وَإِسۡمَٰعِيلَ وَإِسۡحَٰقَ وَيَعۡقُوبَ وَٱلۡأَسۡبَاطِ وَمَآ أُوتِيَ مُوسَىٰ وَعِيسَىٰ وَمَآ أُوتِيَ ٱلنَّبِيُّونَ مِن رَّبِّهِمۡ لَا نُفَرِّقُ بَيۡنَ أَحَدٖ مِّنۡهُمۡ وَنَحۡنُ لَهُۥ مُسۡلِمُونَ", "Qooloo amanna biAllahi wama onzila ilayna wama onzila ila ibraheema waismaAAeela waishaqa wayaAAqooba waalasbati wama ootiya moosa waAAeesa wama ootiya alnnabiyyoona min rabbihim la nufarriqu bayna ahadin minhum wanahnu lahu muslimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 137, "So if they believe in the same as you believe in, then they have been [rightly] guided; but if they turn away, they are only in dissension, and Allah will be sufficient for you against them. And He is the Hearing, the Knowing", "فَإِنۡ ءَامَنُواْ بِمِثۡلِ مَآ ءَامَنتُم بِهِۦ فَقَدِ ٱهۡتَدَواْۖ وَّإِن تَوَلَّوۡاْ فَإِنَّمَا هُمۡ فِي شِقَاقٖۖ فَسَيَكۡفِيكَهُمُ ٱللَّهُۚ وَهُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ", "Fain amanoo bimithli ma amantum bihi faqadi ihtadaw wain tawallaw fainnama hum fee shiqaqin fasayakfeekahumu Allahu wahuwa alssameeAAu alAAaleemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 138, "[And say, ""Ours is] the religion of Allah. And who is better than Allah in [ordaining] religion? And we are worshippers of Him", "صِبۡغَةَ ٱللَّهِ وَمَنۡ أَحۡسَنُ مِنَ ٱللَّهِ صِبۡغَةٗۖ وَنَحۡنُ لَهُۥ عَٰبِدُونَ", "Sibghata Allahi waman ahsanu mina Allahi sibghatan wanahnu lahu AAabidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 139, "Say, [O Muhammad], ""Do you argue with us about Allah while He is our Lord and your Lord? For us are our deeds, and for you are your deeds. And we are sincere [in deed and intention] to Him", "قُلۡ أَتُحَآجُّونَنَا فِي ٱللَّهِ وَهُوَ رَبُّنَا وَرَبُّكُمۡ وَلَنَآ أَعۡمَٰلُنَا وَلَكُمۡ أَعۡمَٰلُكُمۡ وَنَحۡنُ لَهُۥ مُخۡلِصُونَ", "Qul atuhajjoonana fee Allahi wahuwa rabbuna warabbukum walana aAAmaluna walakum aAAmalukum wanahnu lahu mukhlisoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 140, "Or do you say that Abraham and Ishmael and Isaac and Jacob and the Descendants were Jews or Christians? Say, ""Are you more knowing or is Allah?"" And who is more unjust than one who conceals a testimony he has from Allah? And Allah is not unaware of what you do", "أَمۡ تَقُولُونَ إِنَّ إِبۡرَٰهِـۧمَ وَإِسۡمَٰعِيلَ وَإِسۡحَٰقَ وَيَعۡقُوبَ وَٱلۡأَسۡبَاطَ كَانُواْ هُودًا أَوۡ نَصَٰرَىٰۗ قُلۡ ءَأَنتُمۡ أَعۡلَمُ أَمِ ٱللَّهُۗ وَمَنۡ أَظۡلَمُ مِمَّن كَتَمَ شَهَٰدَةً عِندَهُۥ مِنَ ٱللَّهِۗ وَمَا ٱللَّهُ بِغَٰفِلٍ عَمَّا تَعۡمَلُونَ", "Am taqooloona inna ibraheema waismaAAeela waishaqa wayaAAqooba waalasbata kanoo hoodan aw nasara qul aantum aAAlamu ami Allahu waman athlamu mimman katama shahadatan AAindahu mina Allahi wama Allahu bighafilin AAamma taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 141, "That is a nation which has passed on. It will have [the consequence of] what it earned, and you will have what you have earned. And you will not be asked about what they used to do", "تِلۡكَ أُمَّةٞ قَدۡ خَلَتۡۖ لَهَا مَا كَسَبَتۡ وَلَكُم مَّا كَسَبۡتُمۡۖ وَلَا تُسۡـَٔلُونَ عَمَّا كَانُواْ يَعۡمَلُونَ", "Tilka ommatun qad khalat laha ma kasabat walakum ma kasabtum wala tusaloona AAamma kanoo yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 142, "The foolish among the people will say, ""What has turned them away from their qiblah, which they used to face?"" Say, ""To Allah belongs the east and the west. He guides whom He wills to a straight path", "۞سَيَقُولُ ٱلسُّفَهَآءُ مِنَ ٱلنَّاسِ مَا وَلَّىٰهُمۡ عَن قِبۡلَتِهِمُ ٱلَّتِي كَانُواْ عَلَيۡهَاۚ قُل لِّلَّهِ ٱلۡمَشۡرِقُ وَٱلۡمَغۡرِبُۚ يَهۡدِي مَن يَشَآءُ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ", "Sayaqoolu alssufahao mina alnnasi ma wallahum AAan qiblatihimu allatee kanoo AAalayha qul lillahi almashriqu waalmaghribu yahdee man yashao ila siratin mustaqeemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 143, "And thus we have made you a just community that you will be witnesses over the people and the Messenger will be a witness over you. And We did not make the qiblah which you used to face except that We might make evident who would follow the Messenger from who would turn back on his heels. And indeed, it is difficult except for those whom Allah has guided. And never would Allah have caused you to lose your faith. Indeed Allah is, to the people, Kind and Merciful", "وَكَذَٰلِكَ جَعَلۡنَٰكُمۡ أُمَّةٗ وَسَطٗا لِّتَكُونُواْ شُهَدَآءَ عَلَى ٱلنَّاسِ وَيَكُونَ ٱلرَّسُولُ عَلَيۡكُمۡ شَهِيدٗاۗ وَمَا جَعَلۡنَا ٱلۡقِبۡلَةَ ٱلَّتِي كُنتَ عَلَيۡهَآ إِلَّا لِنَعۡلَمَ مَن يَتَّبِعُ ٱلرَّسُولَ مِمَّن يَنقَلِبُ عَلَىٰ عَقِبَيۡهِۚ وَإِن كَانَتۡ لَكَبِيرَةً إِلَّا عَلَى ٱلَّذِينَ هَدَى ٱللَّهُۗ وَمَا كَانَ ٱللَّهُ لِيُضِيعَ إِيمَٰنَكُمۡۚ إِنَّ ٱللَّهَ بِٱلنَّاسِ لَرَءُوفٞ رَّحِيمٞ", "Wakathalika jaAAalnakum ommatan wasatan litakoonoo shuhadaa AAala alnnasi wayakoona alrrasoolu AAalaykum shaheedan wama jaAAalna alqiblata allatee kunta AAalayha illa linaAAlama man yattabiAAu alrrasoola mimman yanqalibu AAala AAaqibayhi wain kanat lakabeeratan illa AAala allatheena hada Allahu wama kana Allahu liyudeeAAa eemanakum inna Allaha bialnnasi laraoofun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 144, "We have certainly seen the turning of your face, [O Muhammad], toward the heaven, and We will surely turn you to a qiblah with which you will be pleased. So turn your face toward al-Masjid al-Haram. And wherever you [believers] are, turn your faces toward it [in prayer]. Indeed, those who have been given the Scripture well know that it is the truth from their Lord. And Allah is not unaware of what they do", "قَدۡ نَرَىٰ تَقَلُّبَ وَجۡهِكَ فِي ٱلسَّمَآءِۖ فَلَنُوَلِّيَنَّكَ قِبۡلَةٗ تَرۡضَىٰهَاۚ فَوَلِّ وَجۡهَكَ شَطۡرَ ٱلۡمَسۡجِدِ ٱلۡحَرَامِۚ وَحَيۡثُ مَا كُنتُمۡ فَوَلُّواْ وُجُوهَكُمۡ شَطۡرَهُۥۗ وَإِنَّ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ لَيَعۡلَمُونَ أَنَّهُ ٱلۡحَقُّ مِن رَّبِّهِمۡۗ وَمَا ٱللَّهُ بِغَٰفِلٍ عَمَّا يَعۡمَلُونَ", "Qad nara taqalluba wajhika fee alssamai falanuwalliyannaka qiblatan tardaha fawalli wajhaka shatra almasjidi alharami wahaythu ma kuntum fawalloo wujoohakum shatrahu wainna allatheena ootoo alkitaba layaAAlamoona annahu alhaqqu min rabbihim wama Allahu bighafilin AAamma yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 145, "And if you brought to those who were given the Scripture every sign, they would not follow your qiblah. Nor will you be a follower of their qiblah. Nor would they be followers of one another's qiblah. So if you were to follow their desires after what has come to you of knowledge, indeed, you would then be among the wrongdoers", "وَلَئِنۡ أَتَيۡتَ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ بِكُلِّ ءَايَةٖ مَّا تَبِعُواْ قِبۡلَتَكَۚ وَمَآ أَنتَ بِتَابِعٖ قِبۡلَتَهُمۡۚ وَمَا بَعۡضُهُم بِتَابِعٖ قِبۡلَةَ بَعۡضٖۚ وَلَئِنِ ٱتَّبَعۡتَ أَهۡوَآءَهُم مِّنۢ بَعۡدِ مَا جَآءَكَ مِنَ ٱلۡعِلۡمِ إِنَّكَ إِذٗا لَّمِنَ ٱلظَّـٰلِمِينَ", "Walain atayta allatheena ootoo alkitaba bikulli ayatin ma tabiAAoo qiblataka wama anta bitabiAAin qiblatahum wama baAAduhum bitabiAAin qiblata baAAdin walaini ittabaAAta ahwaahum min baAAdi ma jaaka mina alAAilmi innaka ithan lamina alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 146, "Those to whom We gave the Scripture know him as they know their own sons. But indeed, a party of them conceal the truth while they know [it]", "ٱلَّذِينَ ءَاتَيۡنَٰهُمُ ٱلۡكِتَٰبَ يَعۡرِفُونَهُۥ كَمَا يَعۡرِفُونَ أَبۡنَآءَهُمۡۖ وَإِنَّ فَرِيقٗا مِّنۡهُمۡ لَيَكۡتُمُونَ ٱلۡحَقَّ وَهُمۡ يَعۡلَمُونَ", "Allatheena ataynahumu alkitaba yaAArifoonahu kama yaAArifoona abnaahum wainna fareeqan minhum layaktumoona alhaqqa wahum yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 147, "The truth is from your Lord, so never be among the doubters", "ٱلۡحَقُّ مِن رَّبِّكَ فَلَا تَكُونَنَّ مِنَ ٱلۡمُمۡتَرِينَ", "Alhaqqu min rabbika fala takoonanna mina almumtareena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 148, "For each [religious following] is a direction toward which it faces. So race to [all that is] good. Wherever you may be, Allah will bring you forth [for judgement] all together. Indeed, Allah is over all things competent", "وَلِكُلّٖ وِجۡهَةٌ هُوَ مُوَلِّيهَاۖ فَٱسۡتَبِقُواْ ٱلۡخَيۡرَٰتِۚ أَيۡنَ مَا تَكُونُواْ يَأۡتِ بِكُمُ ٱللَّهُ جَمِيعًاۚ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ", "Walikullin wijhatun huwa muwalleeha faistabiqoo alkhayrati ayna ma takoonoo yati bikumu Allahu jameeAAan inna Allaha AAala kulli shayin qadeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 149, "So from wherever you go out [for prayer, O Muhammad] turn your face toward al- Masjid al-Haram, and indeed, it is the truth from your Lord. And Allah is not unaware of what you do", "وَمِنۡ حَيۡثُ خَرَجۡتَ فَوَلِّ وَجۡهَكَ شَطۡرَ ٱلۡمَسۡجِدِ ٱلۡحَرَامِۖ وَإِنَّهُۥ لَلۡحَقُّ مِن رَّبِّكَۗ وَمَا ٱللَّهُ بِغَٰفِلٍ عَمَّا تَعۡمَلُونَ", "Wamin haythu kharajta fawalli wajhaka shatra almasjidi alharami wainnahu lalhaqqu min rabbika wama Allahu bighafilin AAamma taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 150, "And from wherever you go out [for prayer], turn your face toward al-Masjid al-Haram. And wherever you [believers] may be, turn your faces toward it in order that the people will not have any argument against you, except for those of them who commit wrong; so fear them not but fear Me. And [it is] so I may complete My favor upon you and that you may be guided", "وَمِنۡ حَيۡثُ خَرَجۡتَ فَوَلِّ وَجۡهَكَ شَطۡرَ ٱلۡمَسۡجِدِ ٱلۡحَرَامِۚ وَحَيۡثُ مَا كُنتُمۡ فَوَلُّواْ وُجُوهَكُمۡ شَطۡرَهُۥ لِئَلَّا يَكُونَ لِلنَّاسِ عَلَيۡكُمۡ حُجَّةٌ إِلَّا ٱلَّذِينَ ظَلَمُواْ مِنۡهُمۡ فَلَا تَخۡشَوۡهُمۡ وَٱخۡشَوۡنِي وَلِأُتِمَّ نِعۡمَتِي عَلَيۡكُمۡ وَلَعَلَّكُمۡ تَهۡتَدُونَ", "Wamin haythu kharajta fawalli wajhaka shatra almasjidi alharami wahaythu ma kuntum fawalloo wujoohakum shatrahu lialla yakoona lilnnasi AAalaykum hujjatun illa allatheena thalamoo minhum fala takhshawhum waikhshawnee waliotimma niAAmatee AAalaykum walaAAallakum tahtadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 151, "Just as We have sent among you a messenger from yourselves reciting to you Our verses and purifying you and teaching you the Book and wisdom and teaching you that which you did not know", "كَمَآ أَرۡسَلۡنَا فِيكُمۡ رَسُولٗا مِّنكُمۡ يَتۡلُواْ عَلَيۡكُمۡ ءَايَٰتِنَا وَيُزَكِّيكُمۡ وَيُعَلِّمُكُمُ ٱلۡكِتَٰبَ وَٱلۡحِكۡمَةَ وَيُعَلِّمُكُم مَّا لَمۡ تَكُونُواْ تَعۡلَمُونَ", "Kama arsalna feekum rasoolan minkum yatloo AAalaykum ayatina wayuzakkeekum wayuAAallimukumu alkitaba waalhikmata wayuAAallimukum ma lam takoonoo taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 152, "So remember Me; I will remember you. And be grateful to Me and do not deny Me", "فَٱذۡكُرُونِيٓ أَذۡكُرۡكُمۡ وَٱشۡكُرُواْ لِي وَلَا تَكۡفُرُونِ", "Faothkuroonee athkurkum waoshkuroo lee wala takfurooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 153, "O you who have believed, seek help through patience and prayer. Indeed, Allah is with the patient", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱسۡتَعِينُواْ بِٱلصَّبۡرِ وَٱلصَّلَوٰةِۚ إِنَّ ٱللَّهَ مَعَ ٱلصَّـٰبِرِينَ", "Ya ayyuha allatheena amanoo istaAAeenoo bialssabri waalssalati inna Allaha maAAa alssabireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 154, "And do not say about those who are killed in the way of Allah, ""They are dead."" Rather, they are alive, but you perceive [it] not", "وَلَا تَقُولُواْ لِمَن يُقۡتَلُ فِي سَبِيلِ ٱللَّهِ أَمۡوَٰتُۢۚ بَلۡ أَحۡيَآءٞ وَلَٰكِن لَّا تَشۡعُرُونَ", "Wala taqooloo liman yuqtalu fee sabeeli Allahi amwatun bal ahyaon walakin la tashAAuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 155, "And We will surely test you with something of fear and hunger and a loss of wealth and lives and fruits, but give good tidings to the patient", "وَلَنَبۡلُوَنَّكُم بِشَيۡءٖ مِّنَ ٱلۡخَوۡفِ وَٱلۡجُوعِ وَنَقۡصٖ مِّنَ ٱلۡأَمۡوَٰلِ وَٱلۡأَنفُسِ وَٱلثَّمَرَٰتِۗ وَبَشِّرِ ٱلصَّـٰبِرِينَ", "Walanabluwannakum bishayin mina alkhawfi waaljooAAi wanaqsin mina alamwali waalanfusi waalththamarati wabashshiri alssabireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 156, "Who, when disaster strikes them, say, ""Indeed we belong to Allah, and indeed to Him we will return", "ٱلَّذِينَ إِذَآ أَصَٰبَتۡهُم مُّصِيبَةٞ قَالُوٓاْ إِنَّا لِلَّهِ وَإِنَّآ إِلَيۡهِ رَٰجِعُونَ", "Allatheena itha asabathum museebatun qaloo inna lillahi wainna ilayhi rajiAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 157, "Those are the ones upon whom are blessings from their Lord and mercy. And it is those who are the [rightly] guided", "أُوْلَـٰٓئِكَ عَلَيۡهِمۡ صَلَوَٰتٞ مِّن رَّبِّهِمۡ وَرَحۡمَةٞۖ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُهۡتَدُونَ", "Olaika AAalayhim salawatun min rabbihim warahmatun waolaika humu almuhtadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 158, "Indeed, as-Safa and al-Marwah are among the symbols of Allah. So whoever makes Hajj to the House or performs 'umrah - there is no blame upon him for walking between them. And whoever volunteers good - then indeed, Allah is appreciative and Knowing", "۞إِنَّ ٱلصَّفَا وَٱلۡمَرۡوَةَ مِن شَعَآئِرِ ٱللَّهِۖ فَمَنۡ حَجَّ ٱلۡبَيۡتَ أَوِ ٱعۡتَمَرَ فَلَا جُنَاحَ عَلَيۡهِ أَن يَطَّوَّفَ بِهِمَاۚ وَمَن تَطَوَّعَ خَيۡرٗا فَإِنَّ ٱللَّهَ شَاكِرٌ عَلِيمٌ", "Inna alssafa waalmarwata min shaAAairi Allahi faman hajja albayta awi iAAtamara fala junaha AAalayhi an yattawwafa bihima waman tatawwaAAa khayran fainna Allaha shakirun AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 159, "Indeed, those who conceal what We sent down of clear proofs and guidance after We made it clear for the people in the Scripture - those are cursed by Allah and cursed by those who curse", "إِنَّ ٱلَّذِينَ يَكۡتُمُونَ مَآ أَنزَلۡنَا مِنَ ٱلۡبَيِّنَٰتِ وَٱلۡهُدَىٰ مِنۢ بَعۡدِ مَا بَيَّنَّـٰهُ لِلنَّاسِ فِي ٱلۡكِتَٰبِ أُوْلَـٰٓئِكَ يَلۡعَنُهُمُ ٱللَّهُ وَيَلۡعَنُهُمُ ٱللَّـٰعِنُونَ", "Inna allatheena yaktumoona ma anzalna mina albayyinati waalhuda min baAAdi ma bayyannahu lilnnasi fee alkitabi olaika yalAAanuhumu Allahu wayalAAanuhumu allaAAinoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 160, "Except for those who repent and correct themselves and make evident [what they concealed]. Those - I will accept their repentance, and I am the Accepting of repentance, the Merciful", "إِلَّا ٱلَّذِينَ تَابُواْ وَأَصۡلَحُواْ وَبَيَّنُواْ فَأُوْلَـٰٓئِكَ أَتُوبُ عَلَيۡهِمۡ وَأَنَا ٱلتَّوَّابُ ٱلرَّحِيمُ", "Illa allatheena taboo waaslahoo wabayyanoo faolaika atoobu AAalayhim waana alttawwabu alrraheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 161, "Indeed, those who disbelieve and die while they are disbelievers - upon them will be the curse of Allah and of the angels and the people, all together", "إِنَّ ٱلَّذِينَ كَفَرُواْ وَمَاتُواْ وَهُمۡ كُفَّارٌ أُوْلَـٰٓئِكَ عَلَيۡهِمۡ لَعۡنَةُ ٱللَّهِ وَٱلۡمَلَـٰٓئِكَةِ وَٱلنَّاسِ أَجۡمَعِينَ", "Inna allatheena kafaroo wamatoo wahum kuffarun olaika AAalayhim laAAnatu Allahi waalmalaikati waalnnasi ajmaAAeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 162, "Abiding eternally therein. The punishment will not be lightened for them, nor will they be reprieved", "خَٰلِدِينَ فِيهَا لَا يُخَفَّفُ عَنۡهُمُ ٱلۡعَذَابُ وَلَا هُمۡ يُنظَرُونَ", "Khalideena feeha la yukhaffafu AAanhumu alAAathabu wala hum yuntharoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 163, "And your god is one God. There is no deity [worthy of worship] except Him, the Entirely Merciful, the Especially Merciful", "وَإِلَٰهُكُمۡ إِلَٰهٞ وَٰحِدٞۖ لَّآ إِلَٰهَ إِلَّا هُوَ ٱلرَّحۡمَٰنُ ٱلرَّحِيمُ", "Wailahukum ilahun wahidun la ilaha illa huwa alrrahmanu alrraheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 164, "Indeed, in the creation of the heavens and earth, and the alternation of the night and the day, and the [great] ships which sail through the sea with that which benefits people, and what Allah has sent down from the heavens of rain, giving life thereby to the earth after its lifelessness and dispersing therein every [kind of] moving creature, and [His] directing of the winds and the clouds controlled between the heaven and the earth are signs for a people who use reason", "إِنَّ فِي خَلۡقِ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَٱخۡتِلَٰفِ ٱلَّيۡلِ وَٱلنَّهَارِ وَٱلۡفُلۡكِ ٱلَّتِي تَجۡرِي فِي ٱلۡبَحۡرِ بِمَا يَنفَعُ ٱلنَّاسَ وَمَآ أَنزَلَ ٱللَّهُ مِنَ ٱلسَّمَآءِ مِن مَّآءٖ فَأَحۡيَا بِهِ ٱلۡأَرۡضَ بَعۡدَ مَوۡتِهَا وَبَثَّ فِيهَا مِن كُلِّ دَآبَّةٖ وَتَصۡرِيفِ ٱلرِّيَٰحِ وَٱلسَّحَابِ ٱلۡمُسَخَّرِ بَيۡنَ ٱلسَّمَآءِ وَٱلۡأَرۡضِ لَأٓيَٰتٖ لِّقَوۡمٖ يَعۡقِلُونَ", "Inna fee khalqi alssamawati waalardi waikhtilafi allayli waalnnahari waalfulki allatee tajree fee albahri bima yanfaAAu alnnasa wama anzala Allahu mina alssamai min main faahya bihi alarda baAAda mawtiha wabaththa feeha min kulli dabbatin watasreefi alrriyahi waalssahabi almusakhkhari bayna alssamai waalardi laayatin liqawmin yaAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 165, "And [yet], among the people are those who take other than Allah as equals [to Him]. They love them as they [should] love Allah. But those who believe are stronger in love for Allah. And if only they who have wronged would consider [that] when they see the punishment, [they will be certain] that all power belongs to Allah and that Allah is severe in punishment", "وَمِنَ ٱلنَّاسِ مَن يَتَّخِذُ مِن دُونِ ٱللَّهِ أَندَادٗا يُحِبُّونَهُمۡ كَحُبِّ ٱللَّهِۖ وَٱلَّذِينَ ءَامَنُوٓاْ أَشَدُّ حُبّٗا لِّلَّهِۗ وَلَوۡ يَرَى ٱلَّذِينَ ظَلَمُوٓاْ إِذۡ يَرَوۡنَ ٱلۡعَذَابَ أَنَّ ٱلۡقُوَّةَ لِلَّهِ جَمِيعٗا وَأَنَّ ٱللَّهَ شَدِيدُ ٱلۡعَذَابِ", "Wamina alnnasi man yattakhithu min dooni Allahi andadan yuhibboonahum kahubbi Allahi waallatheena amanoo ashaddu hubban lillahi walaw yara allatheena thalamoo ith yarawna alAAathaba anna alquwwata lillahi jameeAAan waanna Allaha shadeedu alAAathabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 166, "[And they should consider that] when those who have been followed disassociate themselves from those who followed [them], and they [all] see the punishment, and cut off from them are the ties [of relationship]", "إِذۡ تَبَرَّأَ ٱلَّذِينَ ٱتُّبِعُواْ مِنَ ٱلَّذِينَ ٱتَّبَعُواْ وَرَأَوُاْ ٱلۡعَذَابَ وَتَقَطَّعَتۡ بِهِمُ ٱلۡأَسۡبَابُ", "Ith tabarraa allatheena ittubiAAoo mina allatheena ittabaAAoo waraawoo alAAathaba wataqattaAAat bihimu alasbabu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 167, "Those who followed will say, ""If only we had another turn [at worldly life] so we could disassociate ourselves from them as they have disassociated themselves from us."" Thus will Allah show them their deeds as regrets upon them. And they are never to emerge from the Fire", "وَقَالَ ٱلَّذِينَ ٱتَّبَعُواْ لَوۡ أَنَّ لَنَا كَرَّةٗ فَنَتَبَرَّأَ مِنۡهُمۡ كَمَا تَبَرَّءُواْ مِنَّاۗ كَذَٰلِكَ يُرِيهِمُ ٱللَّهُ أَعۡمَٰلَهُمۡ حَسَرَٰتٍ عَلَيۡهِمۡۖ وَمَا هُم بِخَٰرِجِينَ مِنَ ٱلنَّارِ", "Waqala allatheena ittabaAAoo law anna lana karratan fanatabarraa minhum kama tabarraoo minna kathalika yureehimu Allahu aAAmalahum hasaratin AAalayhim wama hum bikharijeena mina alnnari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 168, "O mankind, eat from whatever is on earth [that is] lawful and good and do not follow the footsteps of Satan. Indeed, he is to you a clear enemy", "يَـٰٓأَيُّهَا ٱلنَّاسُ كُلُواْ مِمَّا فِي ٱلۡأَرۡضِ حَلَٰلٗا طَيِّبٗا وَلَا تَتَّبِعُواْ خُطُوَٰتِ ٱلشَّيۡطَٰنِۚ إِنَّهُۥ لَكُمۡ عَدُوّٞ مُّبِينٌ", "Ya ayyuha alnnasu kuloo mimma fee alardi halalan tayyiban wala tattabiAAoo khutuwati alshshaytani innahu lakum AAaduwwun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 169, "He only orders you to evil and immorality and to say about Allah what you do not know", "إِنَّمَا يَأۡمُرُكُم بِٱلسُّوٓءِ وَٱلۡفَحۡشَآءِ وَأَن تَقُولُواْ عَلَى ٱللَّهِ مَا لَا تَعۡلَمُونَ", "Innama yamurukum bialssooi waalfahshai waan taqooloo AAala Allahi ma la taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 170, "And when it is said to them, ""Follow what Allah has revealed,"" they say, ""Rather, we will follow that which we found our fathers doing."" Even though their fathers understood nothing, nor were they guided", "وَإِذَا قِيلَ لَهُمُ ٱتَّبِعُواْ مَآ أَنزَلَ ٱللَّهُ قَالُواْ بَلۡ نَتَّبِعُ مَآ أَلۡفَيۡنَا عَلَيۡهِ ءَابَآءَنَآۚ أَوَلَوۡ كَانَ ءَابَآؤُهُمۡ لَا يَعۡقِلُونَ شَيۡـٔٗا وَلَا يَهۡتَدُونَ", "Waitha qeela lahumu ittabiAAoo ma anzala Allahu qaloo bal nattabiAAu ma alfayna AAalayhi abaana awalaw kana abaohum la yaAAqiloona shayan wala yahtadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 171, "The example of those who disbelieve is like that of one who shouts at what hears nothing but calls and cries cattle or sheep - deaf, dumb and blind, so they do not understand", "وَمَثَلُ ٱلَّذِينَ كَفَرُواْ كَمَثَلِ ٱلَّذِي يَنۡعِقُ بِمَا لَا يَسۡمَعُ إِلَّا دُعَآءٗ وَنِدَآءٗۚ صُمُّۢ بُكۡمٌ عُمۡيٞ فَهُمۡ لَا يَعۡقِلُونَ", "Wamathalu allatheena kafaroo kamathali allathee yanAAiqu bima la yasmaAAu illa duAAaan wanidaan summun bukmun AAumyun fahum la yaAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 172, "O you who have believed, eat from the good things which We have provided for you and be grateful to Allah if it is [indeed] Him that you worship", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ كُلُواْ مِن طَيِّبَٰتِ مَا رَزَقۡنَٰكُمۡ وَٱشۡكُرُواْ لِلَّهِ إِن كُنتُمۡ إِيَّاهُ تَعۡبُدُونَ", "Ya ayyuha allatheena amanoo kuloo min tayyibati ma razaqnakum waoshkuroo lillahi in kuntum iyyahu taAAbudoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 173, "He has only forbidden to you dead animals, blood, the flesh of swine, and that which has been dedicated to other than Allah. But whoever is forced [by necessity], neither desiring [it] nor transgressing [its limit], there is no sin upon him. Indeed, Allah is Forgiving and Merciful", "إِنَّمَا حَرَّمَ عَلَيۡكُمُ ٱلۡمَيۡتَةَ وَٱلدَّمَ وَلَحۡمَ ٱلۡخِنزِيرِ وَمَآ أُهِلَّ بِهِۦ لِغَيۡرِ ٱللَّهِۖ فَمَنِ ٱضۡطُرَّ غَيۡرَ بَاغٖ وَلَا عَادٖ فَلَآ إِثۡمَ عَلَيۡهِۚ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٌ", "Innama harrama AAalaykumu almaytata waalddama walahma alkhinzeeri wama ohilla bihi lighayri Allahi famani idturra ghayra baghin wala AAadin fala ithma AAalayhi inna Allaha ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 174, "Indeed, they who conceal what Allah has sent down of the Book and exchange it for a small price - those consume not into their bellies except the Fire. And Allah will not speak to them on the Day of Resurrection, nor will He purify them. And they will have a painful punishment", "إِنَّ ٱلَّذِينَ يَكۡتُمُونَ مَآ أَنزَلَ ٱللَّهُ مِنَ ٱلۡكِتَٰبِ وَيَشۡتَرُونَ بِهِۦ ثَمَنٗا قَلِيلًا أُوْلَـٰٓئِكَ مَا يَأۡكُلُونَ فِي بُطُونِهِمۡ إِلَّا ٱلنَّارَ وَلَا يُكَلِّمُهُمُ ٱللَّهُ يَوۡمَ ٱلۡقِيَٰمَةِ وَلَا يُزَكِّيهِمۡ وَلَهُمۡ عَذَابٌ أَلِيمٌ", "Inna allatheena yaktumoona ma anzala Allahu mina alkitabi wayashtaroona bihi thamanan qaleelan olaika ma yakuloona fee butoonihim illa alnnara wala yukallimuhumu Allahu yawma alqiyamati wala yuzakkeehim walahum AAathabun aleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 175, "Those are the ones who have exchanged guidance for error and forgiveness for punishment. How patient they are in pursuit of the Fire", "أُوْلَـٰٓئِكَ ٱلَّذِينَ ٱشۡتَرَوُاْ ٱلضَّلَٰلَةَ بِٱلۡهُدَىٰ وَٱلۡعَذَابَ بِٱلۡمَغۡفِرَةِۚ فَمَآ أَصۡبَرَهُمۡ عَلَى ٱلنَّارِ", "Olaika allatheena ishtarawoo alddalalata bialhuda waalAAathaba bialmaghfirati fama asbarahum AAala alnnari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 176, "That is [deserved by them] because Allah has sent down the Book in truth. And indeed, those who differ over the Book are in extreme dissension", "ذَٰلِكَ بِأَنَّ ٱللَّهَ نَزَّلَ ٱلۡكِتَٰبَ بِٱلۡحَقِّۗ وَإِنَّ ٱلَّذِينَ ٱخۡتَلَفُواْ فِي ٱلۡكِتَٰبِ لَفِي شِقَاقِۭ بَعِيدٖ", "Thalika bianna Allaha nazzala alkitaba bialhaqqi wainna allatheena ikhtalafoo fee alkitabi lafee shiqaqin baAAeedin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 177, "Righteousness is not that you turn your faces toward the east or the west, but [true] righteousness is [in] one who believes in Allah, the Last Day, the angels, the Book, and the prophets and gives wealth, in spite of love for it, to relatives, orphans, the needy, the traveler, those who ask [for help], and for freeing slaves; [and who] establishes prayer and gives zakah; [those who] fulfill their promise when they promise; and [those who] are patient in poverty and hardship and during battle. Those are the ones who have been true, and it is those who are the righteous", "۞لَّيۡسَ ٱلۡبِرَّ أَن تُوَلُّواْ وُجُوهَكُمۡ قِبَلَ ٱلۡمَشۡرِقِ وَٱلۡمَغۡرِبِ وَلَٰكِنَّ ٱلۡبِرَّ مَنۡ ءَامَنَ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ وَٱلۡمَلَـٰٓئِكَةِ وَٱلۡكِتَٰبِ وَٱلنَّبِيِّـۧنَ وَءَاتَى ٱلۡمَالَ عَلَىٰ حُبِّهِۦ ذَوِي ٱلۡقُرۡبَىٰ وَٱلۡيَتَٰمَىٰ وَٱلۡمَسَٰكِينَ وَٱبۡنَ ٱلسَّبِيلِ وَٱلسَّآئِلِينَ وَفِي ٱلرِّقَابِ وَأَقَامَ ٱلصَّلَوٰةَ وَءَاتَى ٱلزَّكَوٰةَ وَٱلۡمُوفُونَ بِعَهۡدِهِمۡ إِذَا عَٰهَدُواْۖ وَٱلصَّـٰبِرِينَ فِي ٱلۡبَأۡسَآءِ وَٱلضَّرَّآءِ وَحِينَ ٱلۡبَأۡسِۗ أُوْلَـٰٓئِكَ ٱلَّذِينَ صَدَقُواْۖ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُتَّقُونَ", "Laysa albirra an tuwalloo wujoohakum qibala almashriqi waalmaghribi walakinna albirra man amana biAllahi waalyawmi alakhiri waalmalaikati waalkitabi waalnnabiyyeena waata almala AAala hubbihi thawee alqurba waalyatama waalmasakeena waibna alssabeeli waalssaileena wafee alrriqabi waaqama alssalata waata alzzakata waalmoofoona biAAahdihim itha AAahadoo waalssabireena fee albasai waalddarrai waheena albasi olaika allatheena sadaqoo waolaika humu almuttaqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 178, "O you who have believed, prescribed for you is legal retribution for those murdered - the free for the free, the slave for the slave, and the female for the female. But whoever overlooks from his brother anything, then there should be a suitable follow-up and payment to him with good conduct. This is an alleviation from your Lord and a mercy. But whoever transgresses after that will have a painful punishment", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ كُتِبَ عَلَيۡكُمُ ٱلۡقِصَاصُ فِي ٱلۡقَتۡلَىۖ ٱلۡحُرُّ بِٱلۡحُرِّ وَٱلۡعَبۡدُ بِٱلۡعَبۡدِ وَٱلۡأُنثَىٰ بِٱلۡأُنثَىٰۚ فَمَنۡ عُفِيَ لَهُۥ مِنۡ أَخِيهِ شَيۡءٞ فَٱتِّبَاعُۢ بِٱلۡمَعۡرُوفِ وَأَدَآءٌ إِلَيۡهِ بِإِحۡسَٰنٖۗ ذَٰلِكَ تَخۡفِيفٞ مِّن رَّبِّكُمۡ وَرَحۡمَةٞۗ فَمَنِ ٱعۡتَدَىٰ بَعۡدَ ذَٰلِكَ فَلَهُۥ عَذَابٌ أَلِيمٞ", "Ya ayyuha allatheena amanoo kutiba AAalaykumu alqisasu fee alqatla alhurru bialhurri waalAAabdu bialAAabdi waalontha bialontha faman AAufiya lahu min akheehi shayon faittibaAAun bialmaAAroofi waadaon ilayhi biihsanin thalika takhfeefun min rabbikum warahmatun famani iAAtada baAAda thalika falahu AAathabun aleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 179, "And there is for you in legal retribution [saving of] life, O you [people] of understanding, that you may become righteous", "وَلَكُمۡ فِي ٱلۡقِصَاصِ حَيَوٰةٞ يَـٰٓأُوْلِي ٱلۡأَلۡبَٰبِ لَعَلَّكُمۡ تَتَّقُونَ", "Walakum fee alqisasi hayatun ya olee alalbabi laAAallakum tattaqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 180, "Prescribed for you when death approaches [any] one of you if he leaves wealth [is that he should make] a bequest for the parents and near relatives according to what is acceptable - a duty upon the righteous", "كُتِبَ عَلَيۡكُمۡ إِذَا حَضَرَ أَحَدَكُمُ ٱلۡمَوۡتُ إِن تَرَكَ خَيۡرًا ٱلۡوَصِيَّةُ لِلۡوَٰلِدَيۡنِ وَٱلۡأَقۡرَبِينَ بِٱلۡمَعۡرُوفِۖ حَقًّا عَلَى ٱلۡمُتَّقِينَ", "Kutiba AAalaykum itha hadara ahadakumu almawtu in taraka khayran alwasiyyatu lilwalidayni waalaqrabeena bialmaAAroofi haqqan AAala almuttaqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 181, "Then whoever alters the bequest after he has heard it - the sin is only upon those who have altered it. Indeed, Allah is Hearing and Knowing", "فَمَنۢ بَدَّلَهُۥ بَعۡدَ مَا سَمِعَهُۥ فَإِنَّمَآ إِثۡمُهُۥ عَلَى ٱلَّذِينَ يُبَدِّلُونَهُۥٓۚ إِنَّ ٱللَّهَ سَمِيعٌ عَلِيمٞ", "Faman baddalahu baAAdama samiAAahu fainnama ithmuhu AAala allatheena yubaddiloonahu inna Allaha sameeAAun AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 182, "But if one fears from the bequeather [some] error or sin and corrects that which is between them, there is no sin upon him. Indeed, Allah is Forgiving and Merciful", "فَمَنۡ خَافَ مِن مُّوصٖ جَنَفًا أَوۡ إِثۡمٗا فَأَصۡلَحَ بَيۡنَهُمۡ فَلَآ إِثۡمَ عَلَيۡهِۚ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ", "Faman khafa min moosin janafan aw ithman faaslaha baynahum fala ithma AAalayhi inna Allaha ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 183, "O you who have believed, decreed upon you is fasting as it was decreed upon those before you that you may become righteous", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ كُتِبَ عَلَيۡكُمُ ٱلصِّيَامُ كَمَا كُتِبَ عَلَى ٱلَّذِينَ مِن قَبۡلِكُمۡ لَعَلَّكُمۡ تَتَّقُونَ", "Ya ayyuha allatheena amanoo kutiba AAalaykumu alssiyamu kama kutiba AAala allatheena min qablikum laAAallakum tattaqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 184, "[Fasting for] a limited number of days. So whoever among you is ill or on a journey [during them] - then an equal number of days [are to be made up]. And upon those who are able [to fast, but with hardship] - a ransom [as substitute] of feeding a poor person [each day]. And whoever volunteers excess - it is better for him. But to fast is best for you, if you only knew", "أَيَّامٗا مَّعۡدُودَٰتٖۚ فَمَن كَانَ مِنكُم مَّرِيضًا أَوۡ عَلَىٰ سَفَرٖ فَعِدَّةٞ مِّنۡ أَيَّامٍ أُخَرَۚ وَعَلَى ٱلَّذِينَ يُطِيقُونَهُۥ فِدۡيَةٞ طَعَامُ مِسۡكِينٖۖ فَمَن تَطَوَّعَ خَيۡرٗا فَهُوَ خَيۡرٞ لَّهُۥۚ وَأَن تَصُومُواْ خَيۡرٞ لَّكُمۡ إِن كُنتُمۡ تَعۡلَمُونَ", "Ayyaman maAAdoodatin faman kana minkum mareedan aw AAala safarin faAAiddatun min ayyamin okhara waAAala allatheena yuteeqoonahu fidyatun taAAamu miskeenin faman tatawwaAAa khayran fahuwa khayrun lahu waan tasoomoo khayrun lakum in kuntum taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 185, "The month of Ramadhan [is that] in which was revealed the Qur'an, a guidance for the people and clear proofs of guidance and criterion. So whoever sights [the new moon of] the month, let him fast it; and whoever is ill or on a journey - then an equal number of other days. Allah intends for you ease and does not intend for you hardship and [wants] for you to complete the period and to glorify Allah for that [to] which He has guided you; and perhaps you will be grateful", "شَهۡرُ رَمَضَانَ ٱلَّذِيٓ أُنزِلَ فِيهِ ٱلۡقُرۡءَانُ هُدٗى لِّلنَّاسِ وَبَيِّنَٰتٖ مِّنَ ٱلۡهُدَىٰ وَٱلۡفُرۡقَانِۚ فَمَن شَهِدَ مِنكُمُ ٱلشَّهۡرَ فَلۡيَصُمۡهُۖ وَمَن كَانَ مَرِيضًا أَوۡ عَلَىٰ سَفَرٖ فَعِدَّةٞ مِّنۡ أَيَّامٍ أُخَرَۗ يُرِيدُ ٱللَّهُ بِكُمُ ٱلۡيُسۡرَ وَلَا يُرِيدُ بِكُمُ ٱلۡعُسۡرَ وَلِتُكۡمِلُواْ ٱلۡعِدَّةَ وَلِتُكَبِّرُواْ ٱللَّهَ عَلَىٰ مَا هَدَىٰكُمۡ وَلَعَلَّكُمۡ تَشۡكُرُونَ", "Shahru ramadana allathee onzila feehi alquranu hudan lilnnasi wabayyinatin mina alhuda waalfurqani faman shahida minkumu alshshahra falyasumhu waman kana mareedan aw AAala safarin faAAiddatun min ayyamin okhara yureedu Allahu bikumu alyusra wala yureedu bikumu alAAusra walitukmiloo alAAiddata walitukabbiroo Allaha AAala ma hadakum walaAAallakum tashkuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 186, "And when My servants ask you, [O Muhammad], concerning Me - indeed I am near. I respond to the invocation of the supplicant when he calls upon Me. So let them respond to Me [by obedience] and believe in Me that they may be [rightly] guided", "وَإِذَا سَأَلَكَ عِبَادِي عَنِّي فَإِنِّي قَرِيبٌۖ أُجِيبُ دَعۡوَةَ ٱلدَّاعِ إِذَا دَعَانِۖ فَلۡيَسۡتَجِيبُواْ لِي وَلۡيُؤۡمِنُواْ بِي لَعَلَّهُمۡ يَرۡشُدُونَ", "Waitha saalaka AAibadee AAannee fainnee qareebun ojeebu daAAwata alddaAAi itha daAAani falyastajeeboo lee walyuminoo bee laAAallahum yarshudoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 187, "It has been made permissible for you the night preceding fasting to go to your wives [for sexual relations]. They are clothing for you and you are clothing for them. Allah knows that you used to deceive yourselves, so He accepted your repentance and forgave you. So now, have relations with them and seek that which Allah has decreed for you. And eat and drink until the white thread of dawn becomes distinct to you from the black thread [of night]. Then complete the fast until the sunset. And do not have relations with them as long as you are staying for worship in the mosques. These are the limits [set by] Allah, so do not approach them. Thus does Allah make clear His ordinances to the people that they may become righteous", "أُحِلَّ لَكُمۡ لَيۡلَةَ ٱلصِّيَامِ ٱلرَّفَثُ إِلَىٰ نِسَآئِكُمۡۚ هُنَّ لِبَاسٞ لَّكُمۡ وَأَنتُمۡ لِبَاسٞ لَّهُنَّۗ عَلِمَ ٱللَّهُ أَنَّكُمۡ كُنتُمۡ تَخۡتَانُونَ أَنفُسَكُمۡ فَتَابَ عَلَيۡكُمۡ وَعَفَا عَنكُمۡۖ فَٱلۡـَٰٔنَ بَٰشِرُوهُنَّ وَٱبۡتَغُواْ مَا كَتَبَ ٱللَّهُ لَكُمۡۚ وَكُلُواْ وَٱشۡرَبُواْ حَتَّىٰ يَتَبَيَّنَ لَكُمُ ٱلۡخَيۡطُ ٱلۡأَبۡيَضُ مِنَ ٱلۡخَيۡطِ ٱلۡأَسۡوَدِ مِنَ ٱلۡفَجۡرِۖ ثُمَّ أَتِمُّواْ ٱلصِّيَامَ إِلَى ٱلَّيۡلِۚ وَلَا تُبَٰشِرُوهُنَّ وَأَنتُمۡ عَٰكِفُونَ فِي ٱلۡمَسَٰجِدِۗ تِلۡكَ حُدُودُ ٱللَّهِ فَلَا تَقۡرَبُوهَاۗ كَذَٰلِكَ يُبَيِّنُ ٱللَّهُ ءَايَٰتِهِۦ لِلنَّاسِ لَعَلَّهُمۡ يَتَّقُونَ", "Ohilla lakum laylata alssiyami alrrafathu ila nisaikum hunna libasun lakum waantum libasun lahunna AAalima Allahu annakum kuntum takhtanoona anfusakum fataba AAalaykum waAAafa AAankum faalana bashiroohunna waibtaghoo ma kataba Allahu lakum wakuloo waishraboo hatta yatabayyana lakumu alkhaytu alabyadu mina alkhayti alaswadi mina alfajri thumma atimmoo alssiyama ila allayli wala tubashiroohunna waantum AAakifoona fee almasajidi tilka hudoodu Allahi fala taqrabooha kathalika yubayyinu Allahu ayatihi lilnnasi laAAallahum yattaqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 188, "And do not consume one another's wealth unjustly or send it [in bribery] to the rulers in order that [they might aid] you [to] consume a portion of the wealth of the people in sin, while you know [it is unlawful]", "وَلَا تَأۡكُلُوٓاْ أَمۡوَٰلَكُم بَيۡنَكُم بِٱلۡبَٰطِلِ وَتُدۡلُواْ بِهَآ إِلَى ٱلۡحُكَّامِ لِتَأۡكُلُواْ فَرِيقٗا مِّنۡ أَمۡوَٰلِ ٱلنَّاسِ بِٱلۡإِثۡمِ وَأَنتُمۡ تَعۡلَمُونَ", "Wala takuloo amwalakum baynakum bialbatili watudloo biha ila alhukkami litakuloo fareeqan min amwali alnnasi bialithmi waantum taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 189, "They ask you, [O Muhammad], about the new moons. Say, ""They are measurements of time for the people and for Hajj."" And it is not righteousness to enter houses from the back, but righteousness is [in] one who fears Allah. And enter houses from their doors. And fear Allah that you may succeed", "۞يَسۡـَٔلُونَكَ عَنِ ٱلۡأَهِلَّةِۖ قُلۡ هِيَ مَوَٰقِيتُ لِلنَّاسِ وَٱلۡحَجِّۗ وَلَيۡسَ ٱلۡبِرُّ بِأَن تَأۡتُواْ ٱلۡبُيُوتَ مِن ظُهُورِهَا وَلَٰكِنَّ ٱلۡبِرَّ مَنِ ٱتَّقَىٰۗ وَأۡتُواْ ٱلۡبُيُوتَ مِنۡ أَبۡوَٰبِهَاۚ وَٱتَّقُواْ ٱللَّهَ لَعَلَّكُمۡ تُفۡلِحُونَ", "Yasaloonaka AAani alahillati qul hiya mawaqeetu lilnnasi waalhajji walaysa albirru bian tatoo albuyoota min thuhooriha walakinna albirra mani ittaqa watoo albuyoota min abwabiha waittaqoo Allaha laAAallakum tuflihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 190, "Fight in the way of Allah those who fight you but do not transgress. Indeed. Allah does not like transgressors", "وَقَٰتِلُواْ فِي سَبِيلِ ٱللَّهِ ٱلَّذِينَ يُقَٰتِلُونَكُمۡ وَلَا تَعۡتَدُوٓاْۚ إِنَّ ٱللَّهَ لَا يُحِبُّ ٱلۡمُعۡتَدِينَ", "Waqatiloo fee sabeeli Allahi allatheena yuqatiloonakum wala taAAtadoo inna Allaha la yuhibbu almuAAtadeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 191, "And kill them wherever you overtake them and expel them from wherever they have expelled you, and fitnah is worse than killing. And do not fight them at al-Masjid al- Haram until they fight you there. But if they fight you, then kill them. Such is the recompense of the disbelievers", "وَٱقۡتُلُوهُمۡ حَيۡثُ ثَقِفۡتُمُوهُمۡ وَأَخۡرِجُوهُم مِّنۡ حَيۡثُ أَخۡرَجُوكُمۡۚ وَٱلۡفِتۡنَةُ أَشَدُّ مِنَ ٱلۡقَتۡلِۚ وَلَا تُقَٰتِلُوهُمۡ عِندَ ٱلۡمَسۡجِدِ ٱلۡحَرَامِ حَتَّىٰ يُقَٰتِلُوكُمۡ فِيهِۖ فَإِن قَٰتَلُوكُمۡ فَٱقۡتُلُوهُمۡۗ كَذَٰلِكَ جَزَآءُ ٱلۡكَٰفِرِينَ", "Waoqtuloohum haythu thaqiftumoohum waakhrijoohum min haythu akhrajookum waalfitnatu ashaddu mina alqatli wala tuqatiloohum AAinda almasjidi alharami hatta yuqatilookum feehi fain qatalookum faoqtuloohum kathalika jazao alkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 192, "And if they cease, then indeed, Allah is Forgiving and Merciful", "فَإِنِ ٱنتَهَوۡاْ فَإِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ", "Faini intahaw fainna Allaha ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 193, "Fight them until there is no [more] fitnah and [until] worship is [acknowledged to be] for Allah. But if they cease, then there is to be no aggression except against the oppressors", "وَقَٰتِلُوهُمۡ حَتَّىٰ لَا تَكُونَ فِتۡنَةٞ وَيَكُونَ ٱلدِّينُ لِلَّهِۖ فَإِنِ ٱنتَهَوۡاْ فَلَا عُدۡوَٰنَ إِلَّا عَلَى ٱلظَّـٰلِمِينَ", "Waqatiloohum hatta la takoona fitnatun wayakoona alddeenu lillahi faini intahaw fala AAudwana illa AAala alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 194, "[Fighting in] the sacred month is for [aggression committed in] the sacred month, and for [all] violations is legal retribution. So whoever has assaulted you, then assault him in the same way that he has assaulted you. And fear Allah and know that Allah is with those who fear Him", "ٱلشَّهۡرُ ٱلۡحَرَامُ بِٱلشَّهۡرِ ٱلۡحَرَامِ وَٱلۡحُرُمَٰتُ قِصَاصٞۚ فَمَنِ ٱعۡتَدَىٰ عَلَيۡكُمۡ فَٱعۡتَدُواْ عَلَيۡهِ بِمِثۡلِ مَا ٱعۡتَدَىٰ عَلَيۡكُمۡۚ وَٱتَّقُواْ ٱللَّهَ وَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ مَعَ ٱلۡمُتَّقِينَ", "Alshshahru alharamu bialshshahri alharami waalhurumatu qisasun famani iAAtada AAalaykum faiAAtadoo AAalayhi bimithli ma iAAtada AAalaykum waittaqoo Allaha waiAAlamoo anna Allaha maAAa almuttaqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 195, "And spend in the way of Allah and do not throw [yourselves] with your [own] hands into destruction [by refraining]. And do good; indeed, Allah loves the doers of good", "وَأَنفِقُواْ فِي سَبِيلِ ٱللَّهِ وَلَا تُلۡقُواْ بِأَيۡدِيكُمۡ إِلَى ٱلتَّهۡلُكَةِ وَأَحۡسِنُوٓاْۚ إِنَّ ٱللَّهَ يُحِبُّ ٱلۡمُحۡسِنِينَ", "Waanfiqoo fee sabeeli Allahi wala tulqoo biaydeekum ila alttahlukati waahsinoo inna Allaha yuhibbu almuhsineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 196, "And complete the Hajj and 'umrah for Allah. But if you are prevented, then [offer] what can be obtained with ease of sacrificial animals. And do not shave your heads until the sacrificial animal has reached its place of slaughter. And whoever among you is ill or has an ailment of the head [making shaving necessary must offer] a ransom of fasting [three days] or charity or sacrifice. And when you are secure, then whoever performs 'umrah [during the Hajj months] followed by Hajj [offers] what can be obtained with ease of sacrificial animals. And whoever cannot find [or afford such an animal] - then a fast of three days during Hajj and of seven when you have returned [home]. Those are ten complete [days]. This is for those whose family is not in the area of al-Masjid al-Haram. And fear Allah and know that Allah is severe in penalty", "وَأَتِمُّواْ ٱلۡحَجَّ وَٱلۡعُمۡرَةَ لِلَّهِۚ فَإِنۡ أُحۡصِرۡتُمۡ فَمَا ٱسۡتَيۡسَرَ مِنَ ٱلۡهَدۡيِۖ وَلَا تَحۡلِقُواْ رُءُوسَكُمۡ حَتَّىٰ يَبۡلُغَ ٱلۡهَدۡيُ مَحِلَّهُۥۚ فَمَن كَانَ مِنكُم مَّرِيضًا أَوۡ بِهِۦٓ أَذٗى مِّن رَّأۡسِهِۦ فَفِدۡيَةٞ مِّن صِيَامٍ أَوۡ صَدَقَةٍ أَوۡ نُسُكٖۚ فَإِذَآ أَمِنتُمۡ فَمَن تَمَتَّعَ بِٱلۡعُمۡرَةِ إِلَى ٱلۡحَجِّ فَمَا ٱسۡتَيۡسَرَ مِنَ ٱلۡهَدۡيِۚ فَمَن لَّمۡ يَجِدۡ فَصِيَامُ ثَلَٰثَةِ أَيَّامٖ فِي ٱلۡحَجِّ وَسَبۡعَةٍ إِذَا رَجَعۡتُمۡۗ تِلۡكَ عَشَرَةٞ كَامِلَةٞۗ ذَٰلِكَ لِمَن لَّمۡ يَكُنۡ أَهۡلُهُۥ حَاضِرِي ٱلۡمَسۡجِدِ ٱلۡحَرَامِۚ وَٱتَّقُواْ ٱللَّهَ وَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ شَدِيدُ ٱلۡعِقَابِ", "Waatimmoo alhajja waalAAumrata lillahi fain ohsirtum fama istaysara mina alhadyi wala tahliqoo ruoosakum hatta yablugha alhadyu mahillahu faman kana minkum mareedan aw bihi athan min rasihi fafidyatun min siyamin aw sadaqatin aw nusukin faitha amintum faman tamattaAAa bialAAumrati ila alhajji fama istaysara mina alhadyi faman lam yajid fasiyamu thalathati ayyamin fee alhajji wasabAAatin itha rajaAAtum tilka AAasharatun kamilatun thalika liman lam yakun ahluhu hadiree almasjidi alharami waittaqoo Allaha waiAAlamoo anna Allaha shadeedu alAAiqabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 197, "Hajj is [during] well-known months, so whoever has made Hajj obligatory upon himself therein [by entering the state of ihram], there is [to be for him] no sexual relations and no disobedience and no disputing during Hajj. And whatever good you do - Allah knows it. And take provisions, but indeed, the best provision is fear of Allah. And fear Me, O you of understanding", "ٱلۡحَجُّ أَشۡهُرٞ مَّعۡلُومَٰتٞۚ فَمَن فَرَضَ فِيهِنَّ ٱلۡحَجَّ فَلَا رَفَثَ وَلَا فُسُوقَ وَلَا جِدَالَ فِي ٱلۡحَجِّۗ وَمَا تَفۡعَلُواْ مِنۡ خَيۡرٖ يَعۡلَمۡهُ ٱللَّهُۗ وَتَزَوَّدُواْ فَإِنَّ خَيۡرَ ٱلزَّادِ ٱلتَّقۡوَىٰۖ وَٱتَّقُونِ يَـٰٓأُوْلِي ٱلۡأَلۡبَٰبِ", "Alhajju ashhurun maAAloomatun faman farada feehinna alhajja fala rafatha wala fusooqa wala jidala fee alhajji wama tafAAaloo min khayrin yaAAlamhu Allahu watazawwadoo fainna khayra alzzadi alttaqwa waittaqooni ya olee alalbabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 198, "There is no blame upon you for seeking bounty from your Lord [during Hajj]. But when you depart from 'Arafat, remember Allah at al- Mash'ar al-Haram. And remember Him, as He has guided you, for indeed, you were before that among those astray", "لَيۡسَ عَلَيۡكُمۡ جُنَاحٌ أَن تَبۡتَغُواْ فَضۡلٗا مِّن رَّبِّكُمۡۚ فَإِذَآ أَفَضۡتُم مِّنۡ عَرَفَٰتٖ فَٱذۡكُرُواْ ٱللَّهَ عِندَ ٱلۡمَشۡعَرِ ٱلۡحَرَامِۖ وَٱذۡكُرُوهُ كَمَا هَدَىٰكُمۡ وَإِن كُنتُم مِّن قَبۡلِهِۦ لَمِنَ ٱلضَّآلِّينَ", "Laysa AAalaykum junahun an tabtaghoo fadlan min rabbikum faitha afadtum min AAarafatin faothkuroo Allaha AAinda almashAAari alharami waothkuroohu kama hadakum wain kuntum min qablihi lamina alddalleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 199, "Then depart from the place from where [all] the people depart and ask forgiveness of Allah. Indeed, Allah is Forgiving and Merciful", "ثُمَّ أَفِيضُواْ مِنۡ حَيۡثُ أَفَاضَ ٱلنَّاسُ وَٱسۡتَغۡفِرُواْ ٱللَّهَۚ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ", "Thumma afeedoo min haythu afada alnnasu waistaghfiroo Allaha inna Allaha ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 200, "And when you have completed your rites, remember Allah like your [previous] remembrance of your fathers or with [much] greater remembrance. And among the people is he who says, ""Our Lord, give us in this world,"" and he will have in the Hereafter no share", "فَإِذَا قَضَيۡتُم مَّنَٰسِكَكُمۡ فَٱذۡكُرُواْ ٱللَّهَ كَذِكۡرِكُمۡ ءَابَآءَكُمۡ أَوۡ أَشَدَّ ذِكۡرٗاۗ فَمِنَ ٱلنَّاسِ مَن يَقُولُ رَبَّنَآ ءَاتِنَا فِي ٱلدُّنۡيَا وَمَا لَهُۥ فِي ٱلۡأٓخِرَةِ مِنۡ خَلَٰقٖ", "Faitha qadaytum manasikakum faothkuroo Allaha kathikrikum abaakum aw ashadda thikran famina alnnasi man yaqoolu rabbana atina fee alddunya wama lahu fee alakhirati min khalaqin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 201, "But among them is he who says, ""Our Lord, give us in this world [that which is] good and in the Hereafter [that which is] good and protect us from the punishment of the Fire", "وَمِنۡهُم مَّن يَقُولُ رَبَّنَآ ءَاتِنَا فِي ٱلدُّنۡيَا حَسَنَةٗ وَفِي ٱلۡأٓخِرَةِ حَسَنَةٗ وَقِنَا عَذَابَ ٱلنَّارِ", "Waminhum man yaqoolu rabbana atina fee alddunya hasanatan wafee alakhirati hasanatan waqina AAathaba alnnari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 202, "Those will have a share of what they have earned, and Allah is swift in account", "أُوْلَـٰٓئِكَ لَهُمۡ نَصِيبٞ مِّمَّا كَسَبُواْۚ وَٱللَّهُ سَرِيعُ ٱلۡحِسَابِ", "Olaika lahum naseebun mimma kasaboo waAllahu sareeAAu alhisabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 203, "And remember Allah during [specific] numbered days. Then whoever hastens [his departure] in two days - there is no sin upon him; and whoever delays [until the third] - there is no sin upon him - for him who fears Allah. And fear Allah and know that unto Him you will be gathered", "۞وَٱذۡكُرُواْ ٱللَّهَ فِيٓ أَيَّامٖ مَّعۡدُودَٰتٖۚ فَمَن تَعَجَّلَ فِي يَوۡمَيۡنِ فَلَآ إِثۡمَ عَلَيۡهِ وَمَن تَأَخَّرَ فَلَآ إِثۡمَ عَلَيۡهِۖ لِمَنِ ٱتَّقَىٰۗ وَٱتَّقُواْ ٱللَّهَ وَٱعۡلَمُوٓاْ أَنَّكُمۡ إِلَيۡهِ تُحۡشَرُونَ", "Waothkuroo Allaha fee ayyamin maAAdoodatin faman taAAajjala fee yawmayni fala ithma AAalayhi waman taakhkhara fala ithma AAalayhi limani ittaqa waittaqoo Allaha waiAAlamoo annakum ilayhi tuhsharoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 204, "And of the people is he whose speech pleases you in worldly life, and he calls Allah to witness as to what is in his heart, yet he is the fiercest of opponents", "وَمِنَ ٱلنَّاسِ مَن يُعۡجِبُكَ قَوۡلُهُۥ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَيُشۡهِدُ ٱللَّهَ عَلَىٰ مَا فِي قَلۡبِهِۦ وَهُوَ أَلَدُّ ٱلۡخِصَامِ", "Wamina alnnasi man yuAAjibuka qawluhu fee alhayati alddunya wayushhidu Allaha AAala ma fee qalbihi wahuwa aladdu alkhisami");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 205, "And when he goes away, he strives throughout the land to cause corruption therein and destroy crops and animals. And Allah does not like corruption", "وَإِذَا تَوَلَّىٰ سَعَىٰ فِي ٱلۡأَرۡضِ لِيُفۡسِدَ فِيهَا وَيُهۡلِكَ ٱلۡحَرۡثَ وَٱلنَّسۡلَۚ وَٱللَّهُ لَا يُحِبُّ ٱلۡفَسَادَ", "Waitha tawalla saAAa fee alardi liyufsida feeha wayuhlika alhartha waalnnasla waAllahu la yuhibbu alfasada");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 206, "And when it is said to him, ""Fear Allah,"" pride in the sin takes hold of him. Sufficient for him is Hellfire, and how wretched is the resting place", "وَإِذَا قِيلَ لَهُ ٱتَّقِ ٱللَّهَ أَخَذَتۡهُ ٱلۡعِزَّةُ بِٱلۡإِثۡمِۚ فَحَسۡبُهُۥ جَهَنَّمُۖ وَلَبِئۡسَ ٱلۡمِهَادُ", "Waitha qeela lahu ittaqi Allaha akhathathu alAAizzatu bialithmi fahasbuhu jahannamu walabisa almihadu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 207, "And of the people is he who sells himself, seeking means to the approval of Allah. And Allah is kind to [His] servants", "وَمِنَ ٱلنَّاسِ مَن يَشۡرِي نَفۡسَهُ ٱبۡتِغَآءَ مَرۡضَاتِ ٱللَّهِۚ وَٱللَّهُ رَءُوفُۢ بِٱلۡعِبَادِ", "Wamina alnnasi man yashree nafsahu ibtighaa mardati Allahi waAllahu raoofun bialAAibadi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 208, "O you who have believed, enter into Islam completely [and perfectly] and do not follow the footsteps of Satan. Indeed, he is to you a clear enemy", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱدۡخُلُواْ فِي ٱلسِّلۡمِ كَآفَّةٗ وَلَا تَتَّبِعُواْ خُطُوَٰتِ ٱلشَّيۡطَٰنِۚ إِنَّهُۥ لَكُمۡ عَدُوّٞ مُّبِينٞ", "Ya ayyuha allatheena amanoo odkhuloo fee alssilmi kaffatan wala tattabiAAoo khutuwati alshshaytani innahu lakum AAaduwwun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 209, "But if you deviate after clear proofs have come to you, then know that Allah is Exalted in Might and Wise", "فَإِن زَلَلۡتُم مِّنۢ بَعۡدِ مَا جَآءَتۡكُمُ ٱلۡبَيِّنَٰتُ فَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ عَزِيزٌ حَكِيمٌ", "Fain zalaltum min baAAdi ma jaatkumu albayyinatu faiAAlamoo anna Allaha AAazeezun hakeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 210, "Do they await but that Allah should come to them in covers of clouds and the angels [as well] and the matter is [then] decided? And to Allah [all] matters are returned", "هَلۡ يَنظُرُونَ إِلَّآ أَن يَأۡتِيَهُمُ ٱللَّهُ فِي ظُلَلٖ مِّنَ ٱلۡغَمَامِ وَٱلۡمَلَـٰٓئِكَةُ وَقُضِيَ ٱلۡأَمۡرُۚ وَإِلَى ٱللَّهِ تُرۡجَعُ ٱلۡأُمُورُ", "Hal yanthuroona illa an yatiyahumu Allahu fee thulalin mina alghamami waalmalaikatu waqudiya alamru waila Allahi turjaAAu alomooru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 211, "Ask the Children of Israel how many a sign of evidence We have given them. And whoever exchanges the favor of Allah [for disbelief] after it has come to him - then indeed, Allah is severe in penalty", "سَلۡ بَنِيٓ إِسۡرَـٰٓءِيلَ كَمۡ ءَاتَيۡنَٰهُم مِّنۡ ءَايَةِۭ بَيِّنَةٖۗ وَمَن يُبَدِّلۡ نِعۡمَةَ ٱللَّهِ مِنۢ بَعۡدِ مَا جَآءَتۡهُ فَإِنَّ ٱللَّهَ شَدِيدُ ٱلۡعِقَابِ", "Sal banee israeela kam ataynahum min ayatin bayyinatin waman yubaddil niAAmata Allahi min baAAdi ma jaathu fainna Allaha shadeedu alAAiqabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 212, "Beautified for those who disbelieve is the life of this world, and they ridicule those who believe. But those who fear Allah are above them on the Day of Resurrection. And Allah gives provision to whom He wills without account", "زُيِّنَ لِلَّذِينَ كَفَرُواْ ٱلۡحَيَوٰةُ ٱلدُّنۡيَا وَيَسۡخَرُونَ مِنَ ٱلَّذِينَ ءَامَنُواْۘ وَٱلَّذِينَ ٱتَّقَوۡاْ فَوۡقَهُمۡ يَوۡمَ ٱلۡقِيَٰمَةِۗ وَٱللَّهُ يَرۡزُقُ مَن يَشَآءُ بِغَيۡرِ حِسَابٖ", "Zuyyina lillatheena kafaroo alhayatu alddunya wayaskharoona mina allatheena amanoo waallatheena ittaqaw fawqahum yawma alqiyamati waAllahu yarzuqu man yashao bighayri hisabin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 213, "Mankind was [of] one religion [before their deviation]; then Allah sent the prophets as bringers of good tidings and warners and sent down with them the Scripture in truth to judge between the people concerning that in which they differed. And none differed over the Scripture except those who were given it - after the clear proofs came to them - out of jealous animosity among themselves. And Allah guided those who believed to the truth concerning that over which they had differed, by His permission. And Allah guides whom He wills to a straight path", "كَانَ ٱلنَّاسُ أُمَّةٗ وَٰحِدَةٗ فَبَعَثَ ٱللَّهُ ٱلنَّبِيِّـۧنَ مُبَشِّرِينَ وَمُنذِرِينَ وَأَنزَلَ مَعَهُمُ ٱلۡكِتَٰبَ بِٱلۡحَقِّ لِيَحۡكُمَ بَيۡنَ ٱلنَّاسِ فِيمَا ٱخۡتَلَفُواْ فِيهِۚ وَمَا ٱخۡتَلَفَ فِيهِ إِلَّا ٱلَّذِينَ أُوتُوهُ مِنۢ بَعۡدِ مَا جَآءَتۡهُمُ ٱلۡبَيِّنَٰتُ بَغۡيَۢا بَيۡنَهُمۡۖ فَهَدَى ٱللَّهُ ٱلَّذِينَ ءَامَنُواْ لِمَا ٱخۡتَلَفُواْ فِيهِ مِنَ ٱلۡحَقِّ بِإِذۡنِهِۦۗ وَٱللَّهُ يَهۡدِي مَن يَشَآءُ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٍ", "Kana alnnasu ommatan wahidatan fabaAAatha Allahu alnnabiyyeena mubashshireena wamunthireena waanzala maAAahumu alkitaba bialhaqqi liyahkuma bayna alnnasi feema ikhtalafoo feehi wama ikhtalafa feehi illa allatheena ootoohu min baAAdi ma jaathumu albayyinatu baghyan baynahum fahada Allahu allatheena amanoo lima ikhtalafoo feehi mina alhaqqi biithnihi waAllahu yahdee man yashao ila siratin mustaqeemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 214, "Or do you think that you will enter Paradise while such [trial] has not yet come to you as came to those who passed on before you? They were touched by poverty and hardship and were shaken until [even their] messenger and those who believed with him said, ""When is the help of Allah?"" Unquestionably, the help of Allah is near", "أَمۡ حَسِبۡتُمۡ أَن تَدۡخُلُواْ ٱلۡجَنَّةَ وَلَمَّا يَأۡتِكُم مَّثَلُ ٱلَّذِينَ خَلَوۡاْ مِن قَبۡلِكُمۖ مَّسَّتۡهُمُ ٱلۡبَأۡسَآءُ وَٱلضَّرَّآءُ وَزُلۡزِلُواْ حَتَّىٰ يَقُولَ ٱلرَّسُولُ وَٱلَّذِينَ ءَامَنُواْ مَعَهُۥ مَتَىٰ نَصۡرُ ٱللَّهِۗ أَلَآ إِنَّ نَصۡرَ ٱللَّهِ قَرِيبٞ", "Am hasibtum an tadkhuloo aljannata walamma yatikum mathalu allatheena khalaw min qablikum massathumu albasao waalddarrao wazulziloo hatta yaqoola alrrasoolu waallatheena amanoo maAAahu mata nasru Allahi ala inna nasra Allahi qareebun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 215, "They ask you, [O Muhammad], what they should spend. Say, ""Whatever you spend of good is [to be] for parents and relatives and orphans and the needy and the traveler. And whatever you do of good - indeed, Allah is Knowing of it", "يَسۡـَٔلُونَكَ مَاذَا يُنفِقُونَۖ قُلۡ مَآ أَنفَقۡتُم مِّنۡ خَيۡرٖ فَلِلۡوَٰلِدَيۡنِ وَٱلۡأَقۡرَبِينَ وَٱلۡيَتَٰمَىٰ وَٱلۡمَسَٰكِينِ وَٱبۡنِ ٱلسَّبِيلِۗ وَمَا تَفۡعَلُواْ مِنۡ خَيۡرٖ فَإِنَّ ٱللَّهَ بِهِۦ عَلِيمٞ", "Yasaloonaka matha yunfiqoona qul ma anfaqtum min khayrin falilwalidayni waalaqrabeena waalyatama waalmasakeeni waibni alssabeeli wama tafAAaloo min khayrin fainna Allaha bihi AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 216, "Fighting has been enjoined upon you while it is hateful to you. But perhaps you hate a thing and it is good for you; and perhaps you love a thing and it is bad for you. And Allah Knows, while you know not", "كُتِبَ عَلَيۡكُمُ ٱلۡقِتَالُ وَهُوَ كُرۡهٞ لَّكُمۡۖ وَعَسَىٰٓ أَن تَكۡرَهُواْ شَيۡـٔٗا وَهُوَ خَيۡرٞ لَّكُمۡۖ وَعَسَىٰٓ أَن تُحِبُّواْ شَيۡـٔٗا وَهُوَ شَرّٞ لَّكُمۡۚ وَٱللَّهُ يَعۡلَمُ وَأَنتُمۡ لَا تَعۡلَمُونَ", "Kutiba AAalaykumu alqitalu wahuwa kurhun lakum waAAasa an takrahoo shayan wahuwa khayrun lakum waAAasa an tuhibboo shayan wahuwa sharrun lakum waAllahu yaAAlamu waantum la taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 217, "They ask you about the sacred month - about fighting therein. Say, ""Fighting therein is great [sin], but averting [people] from the way of Allah and disbelief in Him and [preventing access to] al-Masjid al-Haram and the expulsion of its people therefrom are greater [evil] in the sight of Allah. And fitnah is greater than killing."" And they will continue to fight you until they turn you back from your religion if they are able. And whoever of you reverts from his religion [to disbelief] and dies while he is a disbeliever - for those, their deeds have become worthless in this world and the Hereafter, and those are the companions of the Fire, they will abide therein eternally", "يَسۡـَٔلُونَكَ عَنِ ٱلشَّهۡرِ ٱلۡحَرَامِ قِتَالٖ فِيهِۖ قُلۡ قِتَالٞ فِيهِ كَبِيرٞۚ وَصَدٌّ عَن سَبِيلِ ٱللَّهِ وَكُفۡرُۢ بِهِۦ وَٱلۡمَسۡجِدِ ٱلۡحَرَامِ وَإِخۡرَاجُ أَهۡلِهِۦ مِنۡهُ أَكۡبَرُ عِندَ ٱللَّهِۚ وَٱلۡفِتۡنَةُ أَكۡبَرُ مِنَ ٱلۡقَتۡلِۗ وَلَا يَزَالُونَ يُقَٰتِلُونَكُمۡ حَتَّىٰ يَرُدُّوكُمۡ عَن دِينِكُمۡ إِنِ ٱسۡتَطَٰعُواْۚ وَمَن يَرۡتَدِدۡ مِنكُمۡ عَن دِينِهِۦ فَيَمُتۡ وَهُوَ كَافِرٞ فَأُوْلَـٰٓئِكَ حَبِطَتۡ أَعۡمَٰلُهُمۡ فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِۖ وَأُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلنَّارِۖ هُمۡ فِيهَا خَٰلِدُونَ", "Yasaloonaka AAani alshshahri alharami qitalin feehi qul qitalun feehi kabeerun wasaddun AAan sabeeli Allahi wakufrun bihi waalmasjidi alharami waikhraju ahlihi minhu akbaru AAinda Allahi waalfitnatu akbaru mina alqatli wala yazaloona yuqatiloonakum hatta yaruddookum AAan deenikum ini istataAAoo waman yartadid minkum AAan deenihi fayamut wahuwa kafirun faolaika habitat aAAmaluhum fee alddunya waalakhirati waolaika ashabu alnnari hum feeha khalidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 218, "Indeed, those who have believed and those who have emigrated and fought in the cause of Allah - those expect the mercy of Allah. And Allah is Forgiving and Merciful", "إِنَّ ٱلَّذِينَ ءَامَنُواْ وَٱلَّذِينَ هَاجَرُواْ وَجَٰهَدُواْ فِي سَبِيلِ ٱللَّهِ أُوْلَـٰٓئِكَ يَرۡجُونَ رَحۡمَتَ ٱللَّهِۚ وَٱللَّهُ غَفُورٞ رَّحِيمٞ", "Inna allatheena amanoo waallatheena hajaroo wajahadoo fee sabeeli Allahi olaika yarjoona rahmata Allahi waAllahu ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 219, "They ask you about wine and gambling. Say, ""In them is great sin and [yet, some] benefit for people. But their sin is greater than their benefit."" And they ask you what they should spend. Say, ""The excess [beyond needs]."" Thus Allah makes clear to you the verses [of revelation] that you might give thought", "۞يَسۡـَٔلُونَكَ عَنِ ٱلۡخَمۡرِ وَٱلۡمَيۡسِرِۖ قُلۡ فِيهِمَآ إِثۡمٞ كَبِيرٞ وَمَنَٰفِعُ لِلنَّاسِ وَإِثۡمُهُمَآ أَكۡبَرُ مِن نَّفۡعِهِمَاۗ وَيَسۡـَٔلُونَكَ مَاذَا يُنفِقُونَۖ قُلِ ٱلۡعَفۡوَۗ كَذَٰلِكَ يُبَيِّنُ ٱللَّهُ لَكُمُ ٱلۡأٓيَٰتِ لَعَلَّكُمۡ تَتَفَكَّرُونَ", "Yasaloonaka AAani alkhamri waalmaysiri qul feehima ithmun kabeerun wamanafiAAu lilnnasi waithmuhuma akbaru min nafAAihima wayasaloonaka matha yunfiqoona quli alAAafwa kathalika yubayyinu Allahu lakumu alayati laAAallakum tatafakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 220, "To this world and the Hereafter. And they ask you about orphans. Say, ""Improvement for them is best. And if you mix your affairs with theirs - they are your brothers. And Allah knows the corrupter from the amender. And if Allah had willed, He could have put you in difficulty. Indeed, Allah is Exalted in Might and Wise", "فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِۗ وَيَسۡـَٔلُونَكَ عَنِ ٱلۡيَتَٰمَىٰۖ قُلۡ إِصۡلَاحٞ لَّهُمۡ خَيۡرٞۖ وَإِن تُخَالِطُوهُمۡ فَإِخۡوَٰنُكُمۡۚ وَٱللَّهُ يَعۡلَمُ ٱلۡمُفۡسِدَ مِنَ ٱلۡمُصۡلِحِۚ وَلَوۡ شَآءَ ٱللَّهُ لَأَعۡنَتَكُمۡۚ إِنَّ ٱللَّهَ عَزِيزٌ حَكِيمٞ", "Fee alddunya waalakhirati wayasaloonaka AAani alyatama qul islahun lahum khayrun wain tukhalitoohum faikhwanukum waAllahu yaAAlamu almufsida mina almuslihi walaw shaa Allahu laaAAnatakum inna Allaha AAazeezun hakeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 221, "And do not marry polytheistic women until they believe. And a believing slave woman is better than a polytheist, even though she might please you. And do not marry polytheistic men [to your women] until they believe. And a believing slave is better than a polytheist, even though he might please you. Those invite [you] to the Fire, but Allah invites to Paradise and to forgiveness, by His permission. And He makes clear His verses to the people that perhaps they may remember", "وَلَا تَنكِحُواْ ٱلۡمُشۡرِكَٰتِ حَتَّىٰ يُؤۡمِنَّۚ وَلَأَمَةٞ مُّؤۡمِنَةٌ خَيۡرٞ مِّن مُّشۡرِكَةٖ وَلَوۡ أَعۡجَبَتۡكُمۡۗ وَلَا تُنكِحُواْ ٱلۡمُشۡرِكِينَ حَتَّىٰ يُؤۡمِنُواْۚ وَلَعَبۡدٞ مُّؤۡمِنٌ خَيۡرٞ مِّن مُّشۡرِكٖ وَلَوۡ أَعۡجَبَكُمۡۗ أُوْلَـٰٓئِكَ يَدۡعُونَ إِلَى ٱلنَّارِۖ وَٱللَّهُ يَدۡعُوٓاْ إِلَى ٱلۡجَنَّةِ وَٱلۡمَغۡفِرَةِ بِإِذۡنِهِۦۖ وَيُبَيِّنُ ءَايَٰتِهِۦ لِلنَّاسِ لَعَلَّهُمۡ يَتَذَكَّرُونَ", "Wala tankihoo almushrikati hatta yuminna walaamatun muminatun khayrun min mushrikatin walaw aAAjabatkum wala tunkihoo almushrikeena hatta yuminoo walaAAabdun muminun khayrun min mushrikin walaw aAAjabakum olaika yadAAoona ila alnnari waAllahu yadAAoo ila aljannati waalmaghfirati biithnihi wayubayyinu ayatihi lilnnasi laAAallahum yatathakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 222, "And they ask you about menstruation. Say, ""It is harm, so keep away from wives during menstruation. And do not approach them until they are pure. And when they have purified themselves, then come to them from where Allah has ordained for you. Indeed, Allah loves those who are constantly repentant and loves those who purify themselves", "وَيَسۡـَٔلُونَكَ عَنِ ٱلۡمَحِيضِۖ قُلۡ هُوَ أَذٗى فَٱعۡتَزِلُواْ ٱلنِّسَآءَ فِي ٱلۡمَحِيضِ وَلَا تَقۡرَبُوهُنَّ حَتَّىٰ يَطۡهُرۡنَۖ فَإِذَا تَطَهَّرۡنَ فَأۡتُوهُنَّ مِنۡ حَيۡثُ أَمَرَكُمُ ٱللَّهُۚ إِنَّ ٱللَّهَ يُحِبُّ ٱلتَّوَّـٰبِينَ وَيُحِبُّ ٱلۡمُتَطَهِّرِينَ", "Wayasaloonaka AAani almaheedi qul huwa athan faiAAtaziloo alnnisaa fee almaheedi wala taqraboohunna hatta yathurna faitha tatahharna fatoohunna min haythu amarakumu Allahu inna Allaha yuhibbu alttawwabeena wayuhibbu almutatahhireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 223, "Your wives are a place of sowing of seed for you, so come to your place of cultivation however you wish and put forth [righteousness] for yourselves. And fear Allah and know that you will meet Him. And give good tidings to the believers", "نِسَآؤُكُمۡ حَرۡثٞ لَّكُمۡ فَأۡتُواْ حَرۡثَكُمۡ أَنَّىٰ شِئۡتُمۡۖ وَقَدِّمُواْ لِأَنفُسِكُمۡۚ وَٱتَّقُواْ ٱللَّهَ وَٱعۡلَمُوٓاْ أَنَّكُم مُّلَٰقُوهُۗ وَبَشِّرِ ٱلۡمُؤۡمِنِينَ", "Nisaokum harthun lakum fatoo harthakum anna shitum waqaddimoo lianfusikum waittaqoo Allaha waiAAlamoo annakum mulaqoohu wabashshiri almumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 224, "And do not make [your oath by] Allah an excuse against being righteous and fearing Allah and making peace among people. And Allah is Hearing and Knowing", "وَلَا تَجۡعَلُواْ ٱللَّهَ عُرۡضَةٗ لِّأَيۡمَٰنِكُمۡ أَن تَبَرُّواْ وَتَتَّقُواْ وَتُصۡلِحُواْ بَيۡنَ ٱلنَّاسِۚ وَٱللَّهُ سَمِيعٌ عَلِيمٞ", "Wala tajAAaloo Allaha AAurdatan liaymanikum an tabarroo watattaqoo watuslihoo bayna alnnasi waAllahu sameeAAun AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 225, "Allah does not impose blame upon you for what is unintentional in your oaths, but He imposes blame upon you for what your hearts have earned. And Allah is Forgiving and Forbearing", "لَّا يُؤَاخِذُكُمُ ٱللَّهُ بِٱللَّغۡوِ فِيٓ أَيۡمَٰنِكُمۡ وَلَٰكِن يُؤَاخِذُكُم بِمَا كَسَبَتۡ قُلُوبُكُمۡۗ وَٱللَّهُ غَفُورٌ حَلِيمٞ", "La yuakhithukumu Allahu biallaghwi fee aymanikum walakin yuakhithukum bima kasabat quloobukum waAllahu ghafoorun haleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 226, "For those who swear not to have sexual relations with their wives is a waiting time of four months, but if they return [to normal relations] - then indeed, Allah is Forgiving and Merciful", "لِّلَّذِينَ يُؤۡلُونَ مِن نِّسَآئِهِمۡ تَرَبُّصُ أَرۡبَعَةِ أَشۡهُرٖۖ فَإِن فَآءُو فَإِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ", "Lillatheena yuloona min nisaihim tarabbusu arbaAAati ashhurin fain faoo fainna Allaha ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 227, "And if they decide on divorce - then indeed, Allah is Hearing and Knowing", "وَإِنۡ عَزَمُواْ ٱلطَّلَٰقَ فَإِنَّ ٱللَّهَ سَمِيعٌ عَلِيمٞ", "Wain AAazamoo alttalaqa fainna Allaha sameeAAun AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 228, "Divorced women remain in waiting for three periods, and it is not lawful for them to conceal what Allah has created in their wombs if they believe in Allah and the Last Day. And their husbands have more right to take them back in this [period] if they want reconciliation. And due to the wives is similar to what is expected of them, according to what is reasonable. But the men have a degree over them [in responsibility and authority]. And Allah is Exalted in Might and Wise", "وَٱلۡمُطَلَّقَٰتُ يَتَرَبَّصۡنَ بِأَنفُسِهِنَّ ثَلَٰثَةَ قُرُوٓءٖۚ وَلَا يَحِلُّ لَهُنَّ أَن يَكۡتُمۡنَ مَا خَلَقَ ٱللَّهُ فِيٓ أَرۡحَامِهِنَّ إِن كُنَّ يُؤۡمِنَّ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِۚ وَبُعُولَتُهُنَّ أَحَقُّ بِرَدِّهِنَّ فِي ذَٰلِكَ إِنۡ أَرَادُوٓاْ إِصۡلَٰحٗاۚ وَلَهُنَّ مِثۡلُ ٱلَّذِي عَلَيۡهِنَّ بِٱلۡمَعۡرُوفِۚ وَلِلرِّجَالِ عَلَيۡهِنَّ دَرَجَةٞۗ وَٱللَّهُ عَزِيزٌ حَكِيمٌ", "Waalmutallaqatu yatarabbasna bianfusihinna thalathata qurooin wala yahillu lahunna an yaktumna ma khalaqa Allahu fee arhamihinna in kunna yuminna biAllahi waalyawmi alakhiri wabuAAoolatuhunna ahaqqu biraddihinna fee thalika in aradoo islahan walahunna mithlu allathee AAalayhinna bialmaAAroofi walilrrijali AAalayhinna darajatun waAllahu AAazeezun hakeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 229, "Divorce is twice. Then, either keep [her] in an acceptable manner or release [her] with good treatment. And it is not lawful for you to take anything of what you have given them unless both fear that they will not be able to keep [within] the limits of Allah. But if you fear that they will not keep [within] the limits of Allah, then there is no blame upon either of them concerning that by which she ransoms herself. These are the limits of Allah, so do not transgress them. And whoever transgresses the limits of Allah - it is those who are the wrongdoers", "ٱلطَّلَٰقُ مَرَّتَانِۖ فَإِمۡسَاكُۢ بِمَعۡرُوفٍ أَوۡ تَسۡرِيحُۢ بِإِحۡسَٰنٖۗ وَلَا يَحِلُّ لَكُمۡ أَن تَأۡخُذُواْ مِمَّآ ءَاتَيۡتُمُوهُنَّ شَيۡـًٔا إِلَّآ أَن يَخَافَآ أَلَّا يُقِيمَا حُدُودَ ٱللَّهِۖ فَإِنۡ خِفۡتُمۡ أَلَّا يُقِيمَا حُدُودَ ٱللَّهِ فَلَا جُنَاحَ عَلَيۡهِمَا فِيمَا ٱفۡتَدَتۡ بِهِۦۗ تِلۡكَ حُدُودُ ٱللَّهِ فَلَا تَعۡتَدُوهَاۚ وَمَن يَتَعَدَّ حُدُودَ ٱللَّهِ فَأُوْلَـٰٓئِكَ هُمُ ٱلظَّـٰلِمُونَ", "Alttalaqu marratani faimsakun bimaAAroofin aw tasreehun biihsanin wala yahillu lakum an takhuthoo mimma ataytumoohunna shayan illa an yakhafa alla yuqeema hudooda Allahi fain khiftum alla yuqeema hudooda Allahi fala junaha AAalayhima feema iftadat bihi tilka hudoodu Allahi fala taAAtadooha waman yataAAadda hudooda Allahi faolaika humu alththalimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 230, "And if he has divorced her [for the third time], then she is not lawful to him afterward until [after] she marries a husband other than him. And if the latter husband divorces her [or dies], there is no blame upon the woman and her former husband for returning to each other if they think that they can keep [within] the limits of Allah. These are the limits of Allah, which He makes clear to a people who know", "فَإِن طَلَّقَهَا فَلَا تَحِلُّ لَهُۥ مِنۢ بَعۡدُ حَتَّىٰ تَنكِحَ زَوۡجًا غَيۡرَهُۥۗ فَإِن طَلَّقَهَا فَلَا جُنَاحَ عَلَيۡهِمَآ أَن يَتَرَاجَعَآ إِن ظَنَّآ أَن يُقِيمَا حُدُودَ ٱللَّهِۗ وَتِلۡكَ حُدُودُ ٱللَّهِ يُبَيِّنُهَا لِقَوۡمٖ يَعۡلَمُونَ", "Fain tallaqaha fala tahillu lahu min baAAdu hatta tankiha zawjan ghayrahu fain tallaqaha fala junaha AAalayhima an yatarajaAAa in thanna an yuqeema hudooda Allahi watilka hudoodu Allahi yubayyinuha liqawmin yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 231, "And when you divorce women and they have [nearly] fulfilled their term, either retain them according to acceptable terms or release them according to acceptable terms, and do not keep them, intending harm, to transgress [against them]. And whoever does that has certainly wronged himself. And do not take the verses of Allah in jest. And remember the favor of Allah upon you and what has been revealed to you of the Book and wisdom by which He instructs you. And fear Allah and know that Allah is Knowing of all things", "وَإِذَا طَلَّقۡتُمُ ٱلنِّسَآءَ فَبَلَغۡنَ أَجَلَهُنَّ فَأَمۡسِكُوهُنَّ بِمَعۡرُوفٍ أَوۡ سَرِّحُوهُنَّ بِمَعۡرُوفٖۚ وَلَا تُمۡسِكُوهُنَّ ضِرَارٗا لِّتَعۡتَدُواْۚ وَمَن يَفۡعَلۡ ذَٰلِكَ فَقَدۡ ظَلَمَ نَفۡسَهُۥۚ وَلَا تَتَّخِذُوٓاْ ءَايَٰتِ ٱللَّهِ هُزُوٗاۚ وَٱذۡكُرُواْ نِعۡمَتَ ٱللَّهِ عَلَيۡكُمۡ وَمَآ أَنزَلَ عَلَيۡكُم مِّنَ ٱلۡكِتَٰبِ وَٱلۡحِكۡمَةِ يَعِظُكُم بِهِۦۚ وَٱتَّقُواْ ٱللَّهَ وَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ بِكُلِّ شَيۡءٍ عَلِيمٞ", "Waitha tallaqtumu alnnisaa fabalaghna ajalahunna faamsikoohunna bimaAAroofin aw sarrihoohunna bimaAAroofin wala tumsikoohunna diraran litaAAtadoo waman yafAAal thalika faqad thalama nafsahu wala tattakhithoo ayati Allahi huzuwan waothkuroo niAAmata Allahi AAalaykum wama anzala AAalaykum mina alkitabi waalhikmati yaAAithukum bihi waittaqoo Allaha waiAAlamoo anna Allaha bikulli shayin AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 232, "And when you divorce women and they have fulfilled their term, do not prevent them from remarrying their [former] husbands if they agree among themselves on an acceptable basis. That is instructed to whoever of you believes in Allah and the Last Day. That is better for you and purer, and Allah knows and you know not", "وَإِذَا طَلَّقۡتُمُ ٱلنِّسَآءَ فَبَلَغۡنَ أَجَلَهُنَّ فَلَا تَعۡضُلُوهُنَّ أَن يَنكِحۡنَ أَزۡوَٰجَهُنَّ إِذَا تَرَٰضَوۡاْ بَيۡنَهُم بِٱلۡمَعۡرُوفِۗ ذَٰلِكَ يُوعَظُ بِهِۦ مَن كَانَ مِنكُمۡ يُؤۡمِنُ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِۗ ذَٰلِكُمۡ أَزۡكَىٰ لَكُمۡ وَأَطۡهَرُۚ وَٱللَّهُ يَعۡلَمُ وَأَنتُمۡ لَا تَعۡلَمُونَ", "Waitha tallaqtumu alnnisaa fabalaghna ajalahunna fala taAAduloohunna an yankihna azwajahunna itha taradaw baynahum bialmaAAroofi thalika yooAAathu bihi man kana minkum yuminu biAllahi waalyawmi alakhiri thalikum azka lakum waatharu waAllahu yaAAlamu waantum la taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 233, "Mothers may breastfeed their children two complete years for whoever wishes to complete the nursing [period]. Upon the father is the mothers' provision and their clothing according to what is acceptable. No person is charged with more than his capacity. No mother should be harmed through her child, and no father through his child. And upon the [father's] heir is [a duty] like that [of the father]. And if they both desire weaning through mutual consent from both of them and consultation, there is no blame upon either of them. And if you wish to have your children nursed by a substitute, there is no blame upon you as long as you give payment according to what is acceptable. And fear Allah and know that Allah is Seeing of what you do", "۞وَٱلۡوَٰلِدَٰتُ يُرۡضِعۡنَ أَوۡلَٰدَهُنَّ حَوۡلَيۡنِ كَامِلَيۡنِۖ لِمَنۡ أَرَادَ أَن يُتِمَّ ٱلرَّضَاعَةَۚ وَعَلَى ٱلۡمَوۡلُودِ لَهُۥ رِزۡقُهُنَّ وَكِسۡوَتُهُنَّ بِٱلۡمَعۡرُوفِۚ لَا تُكَلَّفُ نَفۡسٌ إِلَّا وُسۡعَهَاۚ لَا تُضَآرَّ وَٰلِدَةُۢ بِوَلَدِهَا وَلَا مَوۡلُودٞ لَّهُۥ بِوَلَدِهِۦۚ وَعَلَى ٱلۡوَارِثِ مِثۡلُ ذَٰلِكَۗ فَإِنۡ أَرَادَا فِصَالًا عَن تَرَاضٖ مِّنۡهُمَا وَتَشَاوُرٖ فَلَا جُنَاحَ عَلَيۡهِمَاۗ وَإِنۡ أَرَدتُّمۡ أَن تَسۡتَرۡضِعُوٓاْ أَوۡلَٰدَكُمۡ فَلَا جُنَاحَ عَلَيۡكُمۡ إِذَا سَلَّمۡتُم مَّآ ءَاتَيۡتُم بِٱلۡمَعۡرُوفِۗ وَٱتَّقُواْ ٱللَّهَ وَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ بِمَا تَعۡمَلُونَ بَصِيرٞ", "Waalwalidatu yurdiAAna awladahunna hawlayni kamilayni liman arada an yutimma alrradaAAata waAAala almawloodi lahu rizquhunna wakiswatuhunna bialmaAAroofi la tukallafu nafsun illa wusAAaha la tudarra walidatun biwaladiha wala mawloodun lahu biwaladihi waAAala alwarithi mithlu thalika fain arada fisalan AAan taradin minhuma watashawurin fala junaha AAalayhima wain aradtum an tastardiAAoo awladakum fala junaha AAalaykum itha sallamtum ma ataytum bialmaAAroofi waittaqoo Allaha waiAAlamoo anna Allaha bima taAAmaloona baseerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 234, "And those who are taken in death among you and leave wives behind - they, [the wives, shall] wait four months and ten [days]. And when they have fulfilled their term, then there is no blame upon you for what they do with themselves in an acceptable manner. And Allah is [fully] Acquainted with what you do", "وَٱلَّذِينَ يُتَوَفَّوۡنَ مِنكُمۡ وَيَذَرُونَ أَزۡوَٰجٗا يَتَرَبَّصۡنَ بِأَنفُسِهِنَّ أَرۡبَعَةَ أَشۡهُرٖ وَعَشۡرٗاۖ فَإِذَا بَلَغۡنَ أَجَلَهُنَّ فَلَا جُنَاحَ عَلَيۡكُمۡ فِيمَا فَعَلۡنَ فِيٓ أَنفُسِهِنَّ بِٱلۡمَعۡرُوفِۗ وَٱللَّهُ بِمَا تَعۡمَلُونَ خَبِيرٞ", "Waallatheena yutawaffawna minkum wayatharoona azwajan yatarabbasna bianfusihinna arbaAAata ashhurin waAAashran faitha balaghna ajalahunna fala junaha AAalaykum feema faAAalna fee anfusihinna bialmaAAroofi waAllahu bima taAAmaloona khabeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 235, "There is no blame upon you for that to which you [indirectly] allude concerning a proposal to women or for what you conceal within yourselves. Allah knows that you will have them in mind. But do not promise them secretly except for saying a proper saying. And do not determine to undertake a marriage contract until the decreed period reaches its end. And know that Allah knows what is within yourselves, so beware of Him. And know that Allah is Forgiving and Forbearing", "وَلَا جُنَاحَ عَلَيۡكُمۡ فِيمَا عَرَّضۡتُم بِهِۦ مِنۡ خِطۡبَةِ ٱلنِّسَآءِ أَوۡ أَكۡنَنتُمۡ فِيٓ أَنفُسِكُمۡۚ عَلِمَ ٱللَّهُ أَنَّكُمۡ سَتَذۡكُرُونَهُنَّ وَلَٰكِن لَّا تُوَاعِدُوهُنَّ سِرًّا إِلَّآ أَن تَقُولُواْ قَوۡلٗا مَّعۡرُوفٗاۚ وَلَا تَعۡزِمُواْ عُقۡدَةَ ٱلنِّكَاحِ حَتَّىٰ يَبۡلُغَ ٱلۡكِتَٰبُ أَجَلَهُۥۚ وَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ يَعۡلَمُ مَا فِيٓ أَنفُسِكُمۡ فَٱحۡذَرُوهُۚ وَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ غَفُورٌ حَلِيمٞ", "Wala junaha AAalaykum feema AAarradtum bihi min khitbati alnnisai aw aknantum fee anfusikum AAalima Allahu annakum satathkuroonahunna walakin la tuwaAAidoohunna sirran illa an taqooloo qawlan maAAroofan wala taAAzimoo AAuqdata alnnikahi hatta yablugha alkitabu ajalahu waiAAlamoo anna Allaha yaAAlamu ma fee anfusikum faihtharoohu waiAAlamoo anna Allaha ghafoorun haleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 236, "There is no blame upon you if you divorce women you have not touched nor specified for them an obligation. But give them [a gift of] compensation - the wealthy according to his capability and the poor according to his capability - a provision according to what is acceptable, a duty upon the doers of good", "لَّا جُنَاحَ عَلَيۡكُمۡ إِن طَلَّقۡتُمُ ٱلنِّسَآءَ مَا لَمۡ تَمَسُّوهُنَّ أَوۡ تَفۡرِضُواْ لَهُنَّ فَرِيضَةٗۚ وَمَتِّعُوهُنَّ عَلَى ٱلۡمُوسِعِ قَدَرُهُۥ وَعَلَى ٱلۡمُقۡتِرِ قَدَرُهُۥ مَتَٰعَۢا بِٱلۡمَعۡرُوفِۖ حَقًّا عَلَى ٱلۡمُحۡسِنِينَ", "La junaha AAalaykum in tallaqtumu alnnisaa ma lam tamassoohunna aw tafridoo lahunna fareedatan wamattiAAoohunna AAala almoosiAAi qadaruhu waAAala almuqtiri qadaruhu mataAAan bialmaAAroofi haqqan AAala almuhsineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 237, "And if you divorce them before you have touched them and you have already specified for them an obligation, then [give] half of what you specified - unless they forego the right or the one in whose hand is the marriage contract foregoes it. And to forego it is nearer to righteousness. And do not forget graciousness between you. Indeed Allah, of whatever you do, is Seeing", "وَإِن طَلَّقۡتُمُوهُنَّ مِن قَبۡلِ أَن تَمَسُّوهُنَّ وَقَدۡ فَرَضۡتُمۡ لَهُنَّ فَرِيضَةٗ فَنِصۡفُ مَا فَرَضۡتُمۡ إِلَّآ أَن يَعۡفُونَ أَوۡ يَعۡفُوَاْ ٱلَّذِي بِيَدِهِۦ عُقۡدَةُ ٱلنِّكَاحِۚ وَأَن تَعۡفُوٓاْ أَقۡرَبُ لِلتَّقۡوَىٰۚ وَلَا تَنسَوُاْ ٱلۡفَضۡلَ بَيۡنَكُمۡۚ إِنَّ ٱللَّهَ بِمَا تَعۡمَلُونَ بَصِيرٌ", "Wain tallaqtumoohunna min qabli an tamassoohunna waqad faradtum lahunna fareedatan fanisfu ma faradtum illa an yaAAfoona aw yaAAfuwa allathee biyadihi AAuqdatu alnnikahi waan taAAfoo aqrabu lilttaqwa wala tansawoo alfadla baynakum inna Allaha bima taAAmaloona baseerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 238, "Maintain with care the [obligatory] prayers and [in particular] the middle prayer and stand before Allah, devoutly obedient", "حَٰفِظُواْ عَلَى ٱلصَّلَوَٰتِ وَٱلصَّلَوٰةِ ٱلۡوُسۡطَىٰ وَقُومُواْ لِلَّهِ قَٰنِتِينَ", "Hafithoo AAala alssalawati waalssalati alwusta waqoomoo lillahi qaniteena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 239, "And if you fear [an enemy, then pray] on foot or riding. But when you are secure, then remember Allah [in prayer], as He has taught you that which you did not [previously] know", "فَإِنۡ خِفۡتُمۡ فَرِجَالًا أَوۡ رُكۡبَانٗاۖ فَإِذَآ أَمِنتُمۡ فَٱذۡكُرُواْ ٱللَّهَ كَمَا عَلَّمَكُم مَّا لَمۡ تَكُونُواْ تَعۡلَمُونَ", "Fain khiftum farijalan aw rukbanan faitha amintum faothkuroo Allaha kama AAallamakum ma lam takoonoo taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 240, "And those who are taken in death among you and leave wives behind - for their wives is a bequest: maintenance for one year without turning [them] out. But if they leave [of their own accord], then there is no blame upon you for what they do with themselves in an acceptable way. And Allah is Exalted in Might and Wise", "وَٱلَّذِينَ يُتَوَفَّوۡنَ مِنكُمۡ وَيَذَرُونَ أَزۡوَٰجٗا وَصِيَّةٗ لِّأَزۡوَٰجِهِم مَّتَٰعًا إِلَى ٱلۡحَوۡلِ غَيۡرَ إِخۡرَاجٖۚ فَإِنۡ خَرَجۡنَ فَلَا جُنَاحَ عَلَيۡكُمۡ فِي مَا فَعَلۡنَ فِيٓ أَنفُسِهِنَّ مِن مَّعۡرُوفٖۗ وَٱللَّهُ عَزِيزٌ حَكِيمٞ", "Waallatheena yutawaffawna minkum wayatharoona azwajan wasiyyatan liazwajihim mataAAan ila alhawli ghayra ikhrajin fain kharajna fala junaha AAalaykum fee ma faAAalna fee anfusihinna min maAAroofin waAllahu AAazeezun hakeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 241, "And for divorced women is a provision according to what is acceptable - a duty upon the righteous", "وَلِلۡمُطَلَّقَٰتِ مَتَٰعُۢ بِٱلۡمَعۡرُوفِۖ حَقًّا عَلَى ٱلۡمُتَّقِينَ", "Walilmutallaqati mataAAun bialmaAAroofi haqqan AAala almuttaqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 242, "Thus does Allah make clear to you His verses that you might use reason", "كَذَٰلِكَ يُبَيِّنُ ٱللَّهُ لَكُمۡ ءَايَٰتِهِۦ لَعَلَّكُمۡ تَعۡقِلُونَ", "Kathalika yubayyinu Allahu lakum ayatihi laAAallakum taAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 243, "Have you not considered those who left their homes in many thousands, fearing death? Allah said to them, ""Die""; then He restored them to life. And Allah is full of bounty to the people, but most of the people do not show gratitude", "۞أَلَمۡ تَرَ إِلَى ٱلَّذِينَ خَرَجُواْ مِن دِيَٰرِهِمۡ وَهُمۡ أُلُوفٌ حَذَرَ ٱلۡمَوۡتِ فَقَالَ لَهُمُ ٱللَّهُ مُوتُواْ ثُمَّ أَحۡيَٰهُمۡۚ إِنَّ ٱللَّهَ لَذُو فَضۡلٍ عَلَى ٱلنَّاسِ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَشۡكُرُونَ", "Alam tara ila allatheena kharajoo min diyarihim wahum oloofun hathara almawti faqala lahumu Allahu mootoo thumma ahyahum inna Allaha lathoo fadlin AAala alnnasi walakinna akthara alnnasi la yashkuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 244, "And fight in the cause of Allah and know that Allah is Hearing and Knowing", "وَقَٰتِلُواْ فِي سَبِيلِ ٱللَّهِ وَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ سَمِيعٌ عَلِيمٞ", "Waqatiloo fee sabeeli Allahi waiAAlamoo anna Allaha sameeAAun AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 245, "Who is it that would loan Allah a goodly loan so He may multiply it for him many times over? And it is Allah who withholds and grants abundance, and to Him you will be returned", "مَّن ذَا ٱلَّذِي يُقۡرِضُ ٱللَّهَ قَرۡضًا حَسَنٗا فَيُضَٰعِفَهُۥ لَهُۥٓ أَضۡعَافٗا كَثِيرَةٗۚ وَٱللَّهُ يَقۡبِضُ وَيَبۡصُۜطُ وَإِلَيۡهِ تُرۡجَعُونَ", "Man tha allathee yuqridu Allaha qardan hasanan fayudaAAifahu lahu adAAafan katheeratan waAllahu yaqbidu wayabsutu wailayhi turjaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 246, "Have you not considered the assembly of the Children of Israel after [the time of] Moses when they said to a prophet of theirs, ""Send to us a king, and we will fight in the way of Allah ""? He said, ""Would you perhaps refrain from fighting if fighting was prescribed for you?"" They said, ""And why should we not fight in the cause of Allah when we have been driven out from our homes and from our children?"" But when fighting was prescribed for them, they turned away, except for a few of them. And Allah is Knowing of the wrongdoers", "أَلَمۡ تَرَ إِلَى ٱلۡمَلَإِ مِنۢ بَنِيٓ إِسۡرَـٰٓءِيلَ مِنۢ بَعۡدِ مُوسَىٰٓ إِذۡ قَالُواْ لِنَبِيّٖ لَّهُمُ ٱبۡعَثۡ لَنَا مَلِكٗا نُّقَٰتِلۡ فِي سَبِيلِ ٱللَّهِۖ قَالَ هَلۡ عَسَيۡتُمۡ إِن كُتِبَ عَلَيۡكُمُ ٱلۡقِتَالُ أَلَّا تُقَٰتِلُواْۖ قَالُواْ وَمَا لَنَآ أَلَّا نُقَٰتِلَ فِي سَبِيلِ ٱللَّهِ وَقَدۡ أُخۡرِجۡنَا مِن دِيَٰرِنَا وَأَبۡنَآئِنَاۖ فَلَمَّا كُتِبَ عَلَيۡهِمُ ٱلۡقِتَالُ تَوَلَّوۡاْ إِلَّا قَلِيلٗا مِّنۡهُمۡۚ وَٱللَّهُ عَلِيمُۢ بِٱلظَّـٰلِمِينَ", "Alam tara ila almalai min banee israeela min baAAdi moosa ith qaloo linabiyyin lahumu ibAAath lana malikan nuqatil fee sabeeli Allahi qala hal AAasaytum in kutiba AAalaykumu alqitalu alla tuqatiloo qaloo wama lana alla nuqatila fee sabeeli Allahi waqad okhrijna min diyarina waabnaina falamma kutiba AAalayhimu alqitalu tawallaw illa qaleelan minhum waAllahu AAaleemun bialththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 247, "And their prophet said to them, ""Indeed, Allah has sent to you Saul as a king."" They said, ""How can he have kingship over us while we are more worthy of kingship than him and he has not been given any measure of wealth?"" He said, ""Indeed, Allah has chosen him over you and has increased him abundantly in knowledge and stature. And Allah gives His sovereignty to whom He wills. And Allah is all-Encompassing [in favor] and Knowing", "وَقَالَ لَهُمۡ نَبِيُّهُمۡ إِنَّ ٱللَّهَ قَدۡ بَعَثَ لَكُمۡ طَالُوتَ مَلِكٗاۚ قَالُوٓاْ أَنَّىٰ يَكُونُ لَهُ ٱلۡمُلۡكُ عَلَيۡنَا وَنَحۡنُ أَحَقُّ بِٱلۡمُلۡكِ مِنۡهُ وَلَمۡ يُؤۡتَ سَعَةٗ مِّنَ ٱلۡمَالِۚ قَالَ إِنَّ ٱللَّهَ ٱصۡطَفَىٰهُ عَلَيۡكُمۡ وَزَادَهُۥ بَسۡطَةٗ فِي ٱلۡعِلۡمِ وَٱلۡجِسۡمِۖ وَٱللَّهُ يُؤۡتِي مُلۡكَهُۥ مَن يَشَآءُۚ وَٱللَّهُ وَٰسِعٌ عَلِيمٞ", "Waqala lahum nabiyyuhum inna Allaha qad baAAatha lakum taloota malikan qaloo anna yakoonu lahu almulku AAalayna wanahnu ahaqqu bialmulki minhu walam yuta saAAatan mina almali qala inna Allaha istafahu AAalaykum wazadahu bastatan fee alAAilmi waaljismi waAllahu yutee mulkahu man yashao waAllahu wasiAAun AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 248, "And their prophet said to them, ""Indeed, a sign of his kingship is that the chest will come to you in which is assurance from your Lord and a remnant of what the family of Moses and the family of Aaron had left, carried by the angels. Indeed in that is a sign for you, if you are believers", "وَقَالَ لَهُمۡ نَبِيُّهُمۡ إِنَّ ءَايَةَ مُلۡكِهِۦٓ أَن يَأۡتِيَكُمُ ٱلتَّابُوتُ فِيهِ سَكِينَةٞ مِّن رَّبِّكُمۡ وَبَقِيَّةٞ مِّمَّا تَرَكَ ءَالُ مُوسَىٰ وَءَالُ هَٰرُونَ تَحۡمِلُهُ ٱلۡمَلَـٰٓئِكَةُۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لَّكُمۡ إِن كُنتُم مُّؤۡمِنِينَ", "Waqala lahum nabiyyuhum inna ayata mulkihi an yatiyakumu alttabootu feehi sakeenatun min rabbikum wabaqiyyatun mimma taraka alu moosa waalu haroona tahmiluhu almalaikatu inna fee thalika laayatan lakum in kuntum mumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 249, "And when Saul went forth with the soldiers, he said, ""Indeed, Allah will be testing you with a river. So whoever drinks from it is not of me, and whoever does not taste it is indeed of me, excepting one who takes [from it] in the hollow of his hand."" But they drank from it, except a [very] few of them. Then when he had crossed it along with those who believed with him, they said, ""There is no power for us today against Goliath and his soldiers."" But those who were certain that they would meet Allah said, ""How many a small company has overcome a large company by permission of Allah. And Allah is with the patient", "فَلَمَّا فَصَلَ طَالُوتُ بِٱلۡجُنُودِ قَالَ إِنَّ ٱللَّهَ مُبۡتَلِيكُم بِنَهَرٖ فَمَن شَرِبَ مِنۡهُ فَلَيۡسَ مِنِّي وَمَن لَّمۡ يَطۡعَمۡهُ فَإِنَّهُۥ مِنِّيٓ إِلَّا مَنِ ٱغۡتَرَفَ غُرۡفَةَۢ بِيَدِهِۦۚ فَشَرِبُواْ مِنۡهُ إِلَّا قَلِيلٗا مِّنۡهُمۡۚ فَلَمَّا جَاوَزَهُۥ هُوَ وَٱلَّذِينَ ءَامَنُواْ مَعَهُۥ قَالُواْ لَا طَاقَةَ لَنَا ٱلۡيَوۡمَ بِجَالُوتَ وَجُنُودِهِۦۚ قَالَ ٱلَّذِينَ يَظُنُّونَ أَنَّهُم مُّلَٰقُواْ ٱللَّهِ كَم مِّن فِئَةٖ قَلِيلَةٍ غَلَبَتۡ فِئَةٗ كَثِيرَةَۢ بِإِذۡنِ ٱللَّهِۗ وَٱللَّهُ مَعَ ٱلصَّـٰبِرِينَ", "Falamma fasala talootu bialjunoodi qala inna Allaha mubtaleekum binaharin faman shariba minhu falaysa minnee waman lam yatAAamhu fainnahu minnee illa mani ightarafa ghurfatan biyadihi fashariboo minhu illa qaleelan minhum falamma jawazahu huwa waallatheena amanoo maAAahu qaloo la taqata lana alyawma bijaloota wajunoodihi qala allatheena yathunnoona annahum mulaqoo Allahi kam min fiatin qaleelatin ghalabat fiatan katheeratan biithni Allahi waAllahu maAAa alssabireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 250, "And when they went forth to [face] Goliath and his soldiers, they said, ""Our Lord, pour upon us patience and plant firmly our feet and give us victory over the disbelieving people", "وَلَمَّا بَرَزُواْ لِجَالُوتَ وَجُنُودِهِۦ قَالُواْ رَبَّنَآ أَفۡرِغۡ عَلَيۡنَا صَبۡرٗا وَثَبِّتۡ أَقۡدَامَنَا وَٱنصُرۡنَا عَلَى ٱلۡقَوۡمِ ٱلۡكَٰفِرِينَ", "Walamma barazoo lijaloota wajunoodihi qaloo rabbana afrigh AAalayna sabran wathabbit aqdamana waonsurna AAala alqawmi alkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 251, "So they defeated them by permission of Allah, and David killed Goliath, and Allah gave him the kingship and prophethood and taught him from that which He willed. And if it were not for Allah checking [some] people by means of others, the earth would have been corrupted, but Allah is full of bounty to the worlds", "فَهَزَمُوهُم بِإِذۡنِ ٱللَّهِ وَقَتَلَ دَاوُۥدُ جَالُوتَ وَءَاتَىٰهُ ٱللَّهُ ٱلۡمُلۡكَ وَٱلۡحِكۡمَةَ وَعَلَّمَهُۥ مِمَّا يَشَآءُۗ وَلَوۡلَا دَفۡعُ ٱللَّهِ ٱلنَّاسَ بَعۡضَهُم بِبَعۡضٖ لَّفَسَدَتِ ٱلۡأَرۡضُ وَلَٰكِنَّ ٱللَّهَ ذُو فَضۡلٍ عَلَى ٱلۡعَٰلَمِينَ", "Fahazamoohum biithni Allahi waqatala dawoodu jaloota waatahu Allahu almulka waalhikmata waAAallamahu mimma yashao walawla dafAAu Allahi alnnasa baAAdahum bibaAAdin lafasadati alardu walakinna Allaha thoo fadlin AAala alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 252, "These are the verses of Allah which We recite to you, [O Muhammad], in truth. And indeed, you are from among the messengers", "تِلۡكَ ءَايَٰتُ ٱللَّهِ نَتۡلُوهَا عَلَيۡكَ بِٱلۡحَقِّۚ وَإِنَّكَ لَمِنَ ٱلۡمُرۡسَلِينَ", "Tilka ayatu Allahi natlooha AAalayka bialhaqqi wainnaka lamina almursaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 253, "Those messengers - some of them We caused to exceed others. Among them were those to whom Allah spoke, and He raised some of them in degree. And We gave Jesus, the Son of Mary, clear proofs, and We supported him with the Pure Spirit. If Allah had willed, those [generations] succeeding them would not have fought each other after the clear proofs had come to them. But they differed, and some of them believed and some of them disbelieved. And if Allah had willed, they would not have fought each other, but Allah does what He intends", "۞تِلۡكَ ٱلرُّسُلُ فَضَّلۡنَا بَعۡضَهُمۡ عَلَىٰ بَعۡضٖۘ مِّنۡهُم مَّن كَلَّمَ ٱللَّهُۖ وَرَفَعَ بَعۡضَهُمۡ دَرَجَٰتٖۚ وَءَاتَيۡنَا عِيسَى ٱبۡنَ مَرۡيَمَ ٱلۡبَيِّنَٰتِ وَأَيَّدۡنَٰهُ بِرُوحِ ٱلۡقُدُسِۗ وَلَوۡ شَآءَ ٱللَّهُ مَا ٱقۡتَتَلَ ٱلَّذِينَ مِنۢ بَعۡدِهِم مِّنۢ بَعۡدِ مَا جَآءَتۡهُمُ ٱلۡبَيِّنَٰتُ وَلَٰكِنِ ٱخۡتَلَفُواْ فَمِنۡهُم مَّنۡ ءَامَنَ وَمِنۡهُم مَّن كَفَرَۚ وَلَوۡ شَآءَ ٱللَّهُ مَا ٱقۡتَتَلُواْ وَلَٰكِنَّ ٱللَّهَ يَفۡعَلُ مَا يُرِيدُ", "Tilka alrrusulu faddalna baAAdahum AAala baAAdin minhum man kallama Allahu warafaAAa baAAdahum darajatin waatayna AAeesa ibna maryama albayyinati waayyadnahu biroohi alqudusi walaw shaa Allahu ma iqtatala allatheena min baAAdihim min baAAdi ma jaathumu albayyinatu walakini ikhtalafoo faminhum man amana waminhum man kafara walaw shaa Allahu ma iqtataloo walakinna Allaha yafAAalu ma yureedu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 254, "O you who have believed, spend from that which We have provided for you before there comes a Day in which there is no exchange and no friendship and no intercession. And the disbelievers - they are the wrongdoers", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ أَنفِقُواْ مِمَّا رَزَقۡنَٰكُم مِّن قَبۡلِ أَن يَأۡتِيَ يَوۡمٞ لَّا بَيۡعٞ فِيهِ وَلَا خُلَّةٞ وَلَا شَفَٰعَةٞۗ وَٱلۡكَٰفِرُونَ هُمُ ٱلظَّـٰلِمُونَ", "Ya ayyuha allatheena amanoo anfiqoo mimma razaqnakum min qabli an yatiya yawmun la bayAAun feehi wala khullatun wala shafaAAatun waalkafiroona humu alththalimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 255, "Allah - there is no deity except Him, the Ever-Living, the Sustainer of [all] existence. Neither drowsiness overtakes Him nor sleep. To Him belongs whatever is in the heavens and whatever is on the earth. Who is it that can intercede with Him except by His permission? He knows what is [presently] before them and what will be after them, and they encompass not a thing of His knowledge except for what He wills. His Kursi extends over the heavens and the earth, and their preservation tires Him not. And He is the Most High, the Most Great", "ٱللَّهُ لَآ إِلَٰهَ إِلَّا هُوَ ٱلۡحَيُّ ٱلۡقَيُّومُۚ لَا تَأۡخُذُهُۥ سِنَةٞ وَلَا نَوۡمٞۚ لَّهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۗ مَن ذَا ٱلَّذِي يَشۡفَعُ عِندَهُۥٓ إِلَّا بِإِذۡنِهِۦۚ يَعۡلَمُ مَا بَيۡنَ أَيۡدِيهِمۡ وَمَا خَلۡفَهُمۡۖ وَلَا يُحِيطُونَ بِشَيۡءٖ مِّنۡ عِلۡمِهِۦٓ إِلَّا بِمَا شَآءَۚ وَسِعَ كُرۡسِيُّهُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَۖ وَلَا يَـُٔودُهُۥ حِفۡظُهُمَاۚ وَهُوَ ٱلۡعَلِيُّ ٱلۡعَظِيمُ", "Allahu la ilaha illa huwa alhayyu alqayyoomu la takhuthuhu sinatun wala nawmun lahu ma fee alssamawati wama fee alardi man tha allathee yashfaAAu AAindahu illa biithnihi yaAAlamu ma bayna aydeehim wama khalfahum wala yuheetoona bishayin min AAilmihi illa bima shaa wasiAAa kursiyyuhu alssamawati waalarda wala yaooduhu hifthuhuma wahuwa alAAaliyyu alAAatheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 256, "There shall be no compulsion in [acceptance of] the religion. The right course has become clear from the wrong. So whoever disbelieves in Taghut and believes in Allah has grasped the most trustworthy handhold with no break in it. And Allah is Hearing and Knowing", "لَآ إِكۡرَاهَ فِي ٱلدِّينِۖ قَد تَّبَيَّنَ ٱلرُّشۡدُ مِنَ ٱلۡغَيِّۚ فَمَن يَكۡفُرۡ بِٱلطَّـٰغُوتِ وَيُؤۡمِنۢ بِٱللَّهِ فَقَدِ ٱسۡتَمۡسَكَ بِٱلۡعُرۡوَةِ ٱلۡوُثۡقَىٰ لَا ٱنفِصَامَ لَهَاۗ وَٱللَّهُ سَمِيعٌ عَلِيمٌ", "La ikraha fee alddeeni qad tabayyana alrrushdu mina alghayyi faman yakfur bialttaghooti wayumin biAllahi faqadi istamsaka bialAAurwati alwuthqa la infisama laha waAllahu sameeAAun AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 257, "Allah is the ally of those who believe. He brings them out from darknesses into the light. And those who disbelieve - their allies are Taghut. They take them out of the light into darknesses. Those are the companions of the Fire; they will abide eternally therein", "ٱللَّهُ وَلِيُّ ٱلَّذِينَ ءَامَنُواْ يُخۡرِجُهُم مِّنَ ٱلظُّلُمَٰتِ إِلَى ٱلنُّورِۖ وَٱلَّذِينَ كَفَرُوٓاْ أَوۡلِيَآؤُهُمُ ٱلطَّـٰغُوتُ يُخۡرِجُونَهُم مِّنَ ٱلنُّورِ إِلَى ٱلظُّلُمَٰتِۗ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلنَّارِۖ هُمۡ فِيهَا خَٰلِدُونَ", "Allahu waliyyu allatheena amanoo yukhrijuhum mina alththulumati ila alnnoori waallatheena kafaroo awliyaohumu alttaghootu yukhrijoonahum mina alnnoori ila alththulumati olaika ashabu alnnari hum feeha khalidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 258, "Have you not considered the one who argued with Abraham about his Lord [merely] because Allah had given him kingship? When Abraham said, ""My Lord is the one who gives life and causes death,"" he said, ""I give life and cause death."" Abraham said, ""Indeed, Allah brings up the sun from the east, so bring it up from the west."" So the disbeliever was overwhelmed [by astonishment], and Allah does not guide the wrongdoing people", "أَلَمۡ تَرَ إِلَى ٱلَّذِي حَآجَّ إِبۡرَٰهِـۧمَ فِي رَبِّهِۦٓ أَنۡ ءَاتَىٰهُ ٱللَّهُ ٱلۡمُلۡكَ إِذۡ قَالَ إِبۡرَٰهِـۧمُ رَبِّيَ ٱلَّذِي يُحۡيِۦ وَيُمِيتُ قَالَ أَنَا۠ أُحۡيِۦ وَأُمِيتُۖ قَالَ إِبۡرَٰهِـۧمُ فَإِنَّ ٱللَّهَ يَأۡتِي بِٱلشَّمۡسِ مِنَ ٱلۡمَشۡرِقِ فَأۡتِ بِهَا مِنَ ٱلۡمَغۡرِبِ فَبُهِتَ ٱلَّذِي كَفَرَۗ وَٱللَّهُ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلظَّـٰلِمِينَ", "Alam tara ila allathee hajja ibraheema fee rabbihi an atahu Allahu almulka ith qala ibraheemu rabbiya allathee yuhyee wayumeetu qala ana ohyee waomeetu qala ibraheemu fainna Allaha yatee bialshshamsi mina almashriqi fati biha mina almaghribi fabuhita allathee kafara waAllahu la yahdee alqawma alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 259, "Or [consider such an example] as the one who passed by a township which had fallen into ruin. He said, ""How will Allah bring this to life after its death?"" So Allah caused him to die for a hundred years; then He revived him. He said, ""How long have you remained?"" The man said, ""I have remained a day or part of a day."" He said, ""Rather, you have remained one hundred years. Look at your food and your drink; it has not changed with time. And look at your donkey; and We will make you a sign for the people. And look at the bones [of this donkey] - how We raise them and then We cover them with flesh."" And when it became clear to him, he said, ""I know that Allah is over all things competent", "أَوۡ كَٱلَّذِي مَرَّ عَلَىٰ قَرۡيَةٖ وَهِيَ خَاوِيَةٌ عَلَىٰ عُرُوشِهَا قَالَ أَنَّىٰ يُحۡيِۦ هَٰذِهِ ٱللَّهُ بَعۡدَ مَوۡتِهَاۖ فَأَمَاتَهُ ٱللَّهُ مِاْئَةَ عَامٖ ثُمَّ بَعَثَهُۥۖ قَالَ كَمۡ لَبِثۡتَۖ قَالَ لَبِثۡتُ يَوۡمًا أَوۡ بَعۡضَ يَوۡمٖۖ قَالَ بَل لَّبِثۡتَ مِاْئَةَ عَامٖ فَٱنظُرۡ إِلَىٰ طَعَامِكَ وَشَرَابِكَ لَمۡ يَتَسَنَّهۡۖ وَٱنظُرۡ إِلَىٰ حِمَارِكَ وَلِنَجۡعَلَكَ ءَايَةٗ لِّلنَّاسِۖ وَٱنظُرۡ إِلَى ٱلۡعِظَامِ كَيۡفَ نُنشِزُهَا ثُمَّ نَكۡسُوهَا لَحۡمٗاۚ فَلَمَّا تَبَيَّنَ لَهُۥ قَالَ أَعۡلَمُ أَنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ", "Aw kaallathee marra AAala qaryatin wahiya khawiyatun AAala AAurooshiha qala anna yuhyee hathihi Allahu baAAda mawtiha faamatahu Allahu miata AAamin thumma baAAathahu qala kam labithta qala labithtu yawman aw baAAda yawmin qala bal labithta miata AAamin faonthur ila taAAamika washarabika lam yatasannah waonthur ila himarika walinajAAalaka ayatan lilnnasi waonthur ila alAAithami kayfa nunshizuha thumma naksooha lahman falamma tabayyana lahu qala aAAlamu anna Allaha AAala kulli shayin qadeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 260, "And [mention] when Abraham said, ""My Lord, show me how You give life to the dead."" [Allah] said, ""Have you not believed?"" He said, ""Yes, but [I ask] only that my heart may be satisfied."" [Allah] said, ""Take four birds and commit them to yourself. Then [after slaughtering them] put on each hill a portion of them; then call them - they will come [flying] to you in haste. And know that Allah is Exalted in Might and Wise", "وَإِذۡ قَالَ إِبۡرَٰهِـۧمُ رَبِّ أَرِنِي كَيۡفَ تُحۡيِ ٱلۡمَوۡتَىٰۖ قَالَ أَوَلَمۡ تُؤۡمِنۖ قَالَ بَلَىٰ وَلَٰكِن لِّيَطۡمَئِنَّ قَلۡبِيۖ قَالَ فَخُذۡ أَرۡبَعَةٗ مِّنَ ٱلطَّيۡرِ فَصُرۡهُنَّ إِلَيۡكَ ثُمَّ ٱجۡعَلۡ عَلَىٰ كُلِّ جَبَلٖ مِّنۡهُنَّ جُزۡءٗا ثُمَّ ٱدۡعُهُنَّ يَأۡتِينَكَ سَعۡيٗاۚ وَٱعۡلَمۡ أَنَّ ٱللَّهَ عَزِيزٌ حَكِيمٞ", "Waith qala ibraheemu rabbi arinee kayfa tuhyee almawta qala awalam tumin qala bala walakin liyatmainna qalbee qala fakhuth arbaAAatan mina alttayri fasurhunna ilayka thumma ijAAal AAala kulli jabalin minhunna juzan thumma odAAuhunna yateenaka saAAyan waiAAlam anna Allaha AAazeezun hakeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 261, "The example of those who spend their wealth in the way of Allah is like a seed [of grain] which grows seven spikes; in each spike is a hundred grains. And Allah multiplies [His reward] for whom He wills. And Allah is all-Encompassing and Knowing", "مَّثَلُ ٱلَّذِينَ يُنفِقُونَ أَمۡوَٰلَهُمۡ فِي سَبِيلِ ٱللَّهِ كَمَثَلِ حَبَّةٍ أَنۢبَتَتۡ سَبۡعَ سَنَابِلَ فِي كُلِّ سُنۢبُلَةٖ مِّاْئَةُ حَبَّةٖۗ وَٱللَّهُ يُضَٰعِفُ لِمَن يَشَآءُۚ وَٱللَّهُ وَٰسِعٌ عَلِيمٌ", "Mathalu allatheena yunfiqoona amwalahum fee sabeeli Allahi kamathali habbatin anbatat sabAAa sanabila fee kulli sunbulatin miatu habbatin waAllahu yudaAAifu liman yashao waAllahu wasiAAun AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 262, "Those who spend their wealth in the way of Allah and then do not follow up what they have spent with reminders [of it] or [other] injury will have their reward with their Lord, and there will be no fear concerning them, nor will they grieve", "ٱلَّذِينَ يُنفِقُونَ أَمۡوَٰلَهُمۡ فِي سَبِيلِ ٱللَّهِ ثُمَّ لَا يُتۡبِعُونَ مَآ أَنفَقُواْ مَنّٗا وَلَآ أَذٗى لَّهُمۡ أَجۡرُهُمۡ عِندَ رَبِّهِمۡ وَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ", "Allatheena yunfiqoona amwalahum fee sabeeli Allahi thumma la yutbiAAoona ma anfaqoo mannan wala athan lahum ajruhum AAinda rabbihim wala khawfun AAalayhim wala hum yahzanoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 263, "Kind speech and forgiveness are better than charity followed by injury. And Allah is Free of need and Forbearing", "۞قَوۡلٞ مَّعۡرُوفٞ وَمَغۡفِرَةٌ خَيۡرٞ مِّن صَدَقَةٖ يَتۡبَعُهَآ أَذٗىۗ وَٱللَّهُ غَنِيٌّ حَلِيمٞ", "Qawlun maAAroofun wamaghfiratun khayrun min sadaqatin yatbaAAuha athan waAllahu ghaniyyun haleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 264, "O you who have believed, do not invalidate your charities with reminders or injury as does one who spends his wealth [only] to be seen by the people and does not believe in Allah and the Last Day. His example is like that of a [large] smooth stone upon which is dust and is hit by a downpour that leaves it bare. They are unable [to keep] anything of what they have earned. And Allah does not guide the disbelieving people", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تُبۡطِلُواْ صَدَقَٰتِكُم بِٱلۡمَنِّ وَٱلۡأَذَىٰ كَٱلَّذِي يُنفِقُ مَالَهُۥ رِئَآءَ ٱلنَّاسِ وَلَا يُؤۡمِنُ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِۖ فَمَثَلُهُۥ كَمَثَلِ صَفۡوَانٍ عَلَيۡهِ تُرَابٞ فَأَصَابَهُۥ وَابِلٞ فَتَرَكَهُۥ صَلۡدٗاۖ لَّا يَقۡدِرُونَ عَلَىٰ شَيۡءٖ مِّمَّا كَسَبُواْۗ وَٱللَّهُ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلۡكَٰفِرِينَ", "Ya ayyuha allatheena amanoo la tubtiloo sadaqatikum bialmanni waalatha kaallathee yunfiqu malahu riaa alnnasi wala yuminu biAllahi waalyawmi alakhiri famathaluhu kamathali safwanin AAalayhi turabun faasabahu wabilun fatarakahu saldan la yaqdiroona AAala shayin mimma kasaboo waAllahu la yahdee alqawma alkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 265, "And the example of those who spend their wealth seeking means to the approval of Allah and assuring [reward for] themselves is like a garden on high ground which is hit by a downpour - so it yields its fruits in double. And [even] if it is not hit by a downpour, then a drizzle [is sufficient]. And Allah, of what you do, is Seeing", "وَمَثَلُ ٱلَّذِينَ يُنفِقُونَ أَمۡوَٰلَهُمُ ٱبۡتِغَآءَ مَرۡضَاتِ ٱللَّهِ وَتَثۡبِيتٗا مِّنۡ أَنفُسِهِمۡ كَمَثَلِ جَنَّةِۭ بِرَبۡوَةٍ أَصَابَهَا وَابِلٞ فَـَٔاتَتۡ أُكُلَهَا ضِعۡفَيۡنِ فَإِن لَّمۡ يُصِبۡهَا وَابِلٞ فَطَلّٞۗ وَٱللَّهُ بِمَا تَعۡمَلُونَ بَصِيرٌ", "Wamathalu allatheena yunfiqoona amwalahumu ibtighaa mardati Allahi watathbeetan min anfusihim kamathali jannatin birabwatin asabaha wabilun faatat okulaha diAAfayni fain lam yusibha wabilun fatallun waAllahu bima taAAmaloona baseerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 266, "Would one of you like to have a garden of palm trees and grapevines underneath which rivers flow in which he has from every fruit? But he is afflicted with old age and has weak offspring, and it is hit by a whirlwind containing fire and is burned. Thus does Allah make clear to you [His] verses that you might give thought", "أَيَوَدُّ أَحَدُكُمۡ أَن تَكُونَ لَهُۥ جَنَّةٞ مِّن نَّخِيلٖ وَأَعۡنَابٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ لَهُۥ فِيهَا مِن كُلِّ ٱلثَّمَرَٰتِ وَأَصَابَهُ ٱلۡكِبَرُ وَلَهُۥ ذُرِّيَّةٞ ضُعَفَآءُ فَأَصَابَهَآ إِعۡصَارٞ فِيهِ نَارٞ فَٱحۡتَرَقَتۡۗ كَذَٰلِكَ يُبَيِّنُ ٱللَّهُ لَكُمُ ٱلۡأٓيَٰتِ لَعَلَّكُمۡ تَتَفَكَّرُونَ", "Ayawaddu ahadukum an takoona lahu jannatun min nakheelin waaAAnabin tajree min tahtiha alanharu lahu feeha min kulli alththamarati waasabahu alkibaru walahu thurriyyatun duAAafao faasabaha iAAsarun feehi narun faihtaraqat kathalika yubayyinu Allahu lakumu alayati laAAallakum tatafakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 267, "O you who have believed, spend from the good things which you have earned and from that which We have produced for you from the earth. And do not aim toward the defective therefrom, spending [from that] while you would not take it [yourself] except with closed eyes. And know that Allah is Free of need and Praiseworthy", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ أَنفِقُواْ مِن طَيِّبَٰتِ مَا كَسَبۡتُمۡ وَمِمَّآ أَخۡرَجۡنَا لَكُم مِّنَ ٱلۡأَرۡضِۖ وَلَا تَيَمَّمُواْ ٱلۡخَبِيثَ مِنۡهُ تُنفِقُونَ وَلَسۡتُم بِـَٔاخِذِيهِ إِلَّآ أَن تُغۡمِضُواْ فِيهِۚ وَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ غَنِيٌّ حَمِيدٌ", "Ya ayyuha allatheena amanoo anfiqoo min tayyibati ma kasabtum wamimma akhrajna lakum mina alardi wala tayammamoo alkhabeetha minhu tunfiqoona walastum biakhitheehi illa an tughmidoo feehi waiAAlamoo anna Allaha ghaniyyun hameedun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 268, "Satan threatens you with poverty and orders you to immorality, while Allah promises you forgiveness from Him and bounty. And Allah is all-Encompassing and Knowing", "ٱلشَّيۡطَٰنُ يَعِدُكُمُ ٱلۡفَقۡرَ وَيَأۡمُرُكُم بِٱلۡفَحۡشَآءِۖ وَٱللَّهُ يَعِدُكُم مَّغۡفِرَةٗ مِّنۡهُ وَفَضۡلٗاۗ وَٱللَّهُ وَٰسِعٌ عَلِيمٞ", "Alshshaytanu yaAAidukumu alfaqra wayamurukum bialfahshai waAllahu yaAAidukum maghfiratan minhu wafadlan waAllahu wasiAAun AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 269, "He gives wisdom to whom He wills, and whoever has been given wisdom has certainly been given much good. And none will remember except those of understanding", "يُؤۡتِي ٱلۡحِكۡمَةَ مَن يَشَآءُۚ وَمَن يُؤۡتَ ٱلۡحِكۡمَةَ فَقَدۡ أُوتِيَ خَيۡرٗا كَثِيرٗاۗ وَمَا يَذَّكَّرُ إِلَّآ أُوْلُواْ ٱلۡأَلۡبَٰبِ", "Yutee alhikmata man yashao waman yuta alhikmata faqad ootiya khayran katheeran wama yaththakkaru illa oloo alalbabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 270, "And whatever you spend of expenditures or make of vows - indeed, Allah knows of it. And for the wrongdoers there are no helpers", "وَمَآ أَنفَقۡتُم مِّن نَّفَقَةٍ أَوۡ نَذَرۡتُم مِّن نَّذۡرٖ فَإِنَّ ٱللَّهَ يَعۡلَمُهُۥۗ وَمَا لِلظَّـٰلِمِينَ مِنۡ أَنصَارٍ", "Wama anfaqtum min nafaqatin aw nathartum min nathrin fainna Allaha yaAAlamuhu wama lilththalimeena min ansarin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 271, "If you disclose your charitable expenditures, they are good; but if you conceal them and give them to the poor, it is better for you, and He will remove from you some of your misdeeds [thereby]. And Allah, with what you do, is [fully] Acquainted", "إِن تُبۡدُواْ ٱلصَّدَقَٰتِ فَنِعِمَّا هِيَۖ وَإِن تُخۡفُوهَا وَتُؤۡتُوهَا ٱلۡفُقَرَآءَ فَهُوَ خَيۡرٞ لَّكُمۡۚ وَيُكَفِّرُ عَنكُم مِّن سَيِّـَٔاتِكُمۡۗ وَٱللَّهُ بِمَا تَعۡمَلُونَ خَبِيرٞ", "In tubdoo alssadaqati faniAAimma hiya wain tukhfooha watutooha alfuqaraa fahuwa khayrun lakum wayukaffiru AAankum min sayyiatikum waAllahu bima taAAmaloona khabeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 272, "Not upon you, [O Muhammad], is [responsibility for] their guidance, but Allah guides whom He wills. And whatever good you [believers] spend is for yourselves, and you do not spend except seeking the countenance of Allah. And whatever you spend of good - it will be fully repaid to you, and you will not be wronged", "۞لَّيۡسَ عَلَيۡكَ هُدَىٰهُمۡ وَلَٰكِنَّ ٱللَّهَ يَهۡدِي مَن يَشَآءُۗ وَمَا تُنفِقُواْ مِنۡ خَيۡرٖ فَلِأَنفُسِكُمۡۚ وَمَا تُنفِقُونَ إِلَّا ٱبۡتِغَآءَ وَجۡهِ ٱللَّهِۚ وَمَا تُنفِقُواْ مِنۡ خَيۡرٖ يُوَفَّ إِلَيۡكُمۡ وَأَنتُمۡ لَا تُظۡلَمُونَ", "Laysa AAalayka hudahum walakinna Allaha yahdee man yashao wama tunfiqoo min khayrin falianfusikum wama tunfiqoona illa ibtighaa wajhi Allahi wama tunfiqoo min khayrin yuwaffa ilaykum waantum la tuthlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 273, "[Charity is] for the poor who have been restricted for the cause of Allah, unable to move about in the land. An ignorant [person] would think them self-sufficient because of their restraint, but you will know them by their [characteristic] sign. They do not ask people persistently [or at all]. And whatever you spend of good - indeed, Allah is Knowing of it", "لِلۡفُقَرَآءِ ٱلَّذِينَ أُحۡصِرُواْ فِي سَبِيلِ ٱللَّهِ لَا يَسۡتَطِيعُونَ ضَرۡبٗا فِي ٱلۡأَرۡضِ يَحۡسَبُهُمُ ٱلۡجَاهِلُ أَغۡنِيَآءَ مِنَ ٱلتَّعَفُّفِ تَعۡرِفُهُم بِسِيمَٰهُمۡ لَا يَسۡـَٔلُونَ ٱلنَّاسَ إِلۡحَافٗاۗ وَمَا تُنفِقُواْ مِنۡ خَيۡرٖ فَإِنَّ ٱللَّهَ بِهِۦ عَلِيمٌ", "Lilfuqarai allatheena ohsiroo fee sabeeli Allahi la yastateeAAoona darban fee alardi yahsabuhumu aljahilu aghniyaa mina alttaAAaffufi taAArifuhum biseemahum la yasaloona alnnasa ilhafan wama tunfiqoo min khayrin fainna Allaha bihi AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 274, "Those who spend their wealth [in Allah 's way] by night and by day, secretly and publicly - they will have their reward with their Lord. And no fear will there be concerning them, nor will they grieve", "ٱلَّذِينَ يُنفِقُونَ أَمۡوَٰلَهُم بِٱلَّيۡلِ وَٱلنَّهَارِ سِرّٗا وَعَلَانِيَةٗ فَلَهُمۡ أَجۡرُهُمۡ عِندَ رَبِّهِمۡ وَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ", "Allatheena yunfiqoona amwalahum biallayli waalnnahari sirran waAAalaniyatan falahum ajruhum AAinda rabbihim wala khawfun AAalayhim wala hum yahzanoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 275, "Those who consume interest cannot stand [on the Day of Resurrection] except as one stands who is being beaten by Satan into insanity. That is because they say, ""Trade is [just] like interest."" But Allah has permitted trade and has forbidden interest. So whoever has received an admonition from his Lord and desists may have what is past, and his affair rests with Allah. But whoever returns to [dealing in interest or usury] - those are the companions of the Fire; they will abide eternally therein", "ٱلَّذِينَ يَأۡكُلُونَ ٱلرِّبَوٰاْ لَا يَقُومُونَ إِلَّا كَمَا يَقُومُ ٱلَّذِي يَتَخَبَّطُهُ ٱلشَّيۡطَٰنُ مِنَ ٱلۡمَسِّۚ ذَٰلِكَ بِأَنَّهُمۡ قَالُوٓاْ إِنَّمَا ٱلۡبَيۡعُ مِثۡلُ ٱلرِّبَوٰاْۗ وَأَحَلَّ ٱللَّهُ ٱلۡبَيۡعَ وَحَرَّمَ ٱلرِّبَوٰاْۚ فَمَن جَآءَهُۥ مَوۡعِظَةٞ مِّن رَّبِّهِۦ فَٱنتَهَىٰ فَلَهُۥ مَا سَلَفَ وَأَمۡرُهُۥٓ إِلَى ٱللَّهِۖ وَمَنۡ عَادَ فَأُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلنَّارِۖ هُمۡ فِيهَا خَٰلِدُونَ", "Allatheena yakuloona alrriba la yaqoomoona illa kama yaqoomu allathee yatakhabbatuhu alshshaytanu mina almassi thalika biannahum qaloo innama albayAAu mithlu alrriba waahalla Allahu albayAAa waharrama alrriba faman jaahu mawAAithatun min rabbihi faintaha falahu ma salafa waamruhu ila Allahi waman AAada faolaika ashabu alnnari hum feeha khalidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 276, "Allah destroys interest and gives increase for charities. And Allah does not like every sinning disbeliever", "يَمۡحَقُ ٱللَّهُ ٱلرِّبَوٰاْ وَيُرۡبِي ٱلصَّدَقَٰتِۗ وَٱللَّهُ لَا يُحِبُّ كُلَّ كَفَّارٍ أَثِيمٍ", "Yamhaqu Allahu alrriba wayurbee alssadaqati waAllahu la yuhibbu kulla kaffarin atheemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 277, "Indeed, those who believe and do righteous deeds and establish prayer and give zakah will have their reward with their Lord, and there will be no fear concerning them, nor will they grieve", "إِنَّ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ وَأَقَامُواْ ٱلصَّلَوٰةَ وَءَاتَوُاْ ٱلزَّكَوٰةَ لَهُمۡ أَجۡرُهُمۡ عِندَ رَبِّهِمۡ وَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ", "Inna allatheena amanoo waAAamiloo alssalihati waaqamoo alssalata waatawoo alzzakata lahum ajruhum AAinda rabbihim wala khawfun AAalayhim wala hum yahzanoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 278, "O you who have believed, fear Allah and give up what remains [due to you] of interest, if you should be believers", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱتَّقُواْ ٱللَّهَ وَذَرُواْ مَا بَقِيَ مِنَ ٱلرِّبَوٰٓاْ إِن كُنتُم مُّؤۡمِنِينَ", "Ya ayyuha allatheena amanoo ittaqoo Allaha watharoo ma baqiya mina alrriba in kuntum mumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 279, "And if you do not, then be informed of a war [against you] from Allah and His Messenger. But if you repent, you may have your principal - [thus] you do no wrong, nor are you wronged", "فَإِن لَّمۡ تَفۡعَلُواْ فَأۡذَنُواْ بِحَرۡبٖ مِّنَ ٱللَّهِ وَرَسُولِهِۦۖ وَإِن تُبۡتُمۡ فَلَكُمۡ رُءُوسُ أَمۡوَٰلِكُمۡ لَا تَظۡلِمُونَ وَلَا تُظۡلَمُونَ", "Fain lam tafAAaloo fathanoo biharbin mina Allahi warasoolihi wain tubtum falakum ruoosu amwalikum la tathlimoona wala tuthlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 280, "And if someone is in hardship, then [let there be] postponement until [a time of] ease. But if you give [from your right as] charity, then it is better for you, if you only knew", "وَإِن كَانَ ذُو عُسۡرَةٖ فَنَظِرَةٌ إِلَىٰ مَيۡسَرَةٖۚ وَأَن تَصَدَّقُواْ خَيۡرٞ لَّكُمۡ إِن كُنتُمۡ تَعۡلَمُونَ", "Wain kana thoo AAusratin fanathiratun ila maysaratin waan tasaddaqoo khayrun lakum in kuntum taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 281, "And fear a Day when you will be returned to Allah. Then every soul will be compensated for what it earned, and they will not be treated unjustly", "وَٱتَّقُواْ يَوۡمٗا تُرۡجَعُونَ فِيهِ إِلَى ٱللَّهِۖ ثُمَّ تُوَفَّىٰ كُلُّ نَفۡسٖ مَّا كَسَبَتۡ وَهُمۡ لَا يُظۡلَمُونَ", "Waittaqoo yawman turjaAAoona feehi ila Allahi thumma tuwaffa kullu nafsin ma kasabat wahum la yuthlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 282, "O you who have believed, when you contract a debt for a specified term, write it down. And let a scribe write [it] between you in justice. Let no scribe refuse to write as Allah has taught him. So let him write and let the one who has the obligation dictate. And let him fear Allah, his Lord, and not leave anything out of it. But if the one who has the obligation is of limited understanding or weak or unable to dictate himself, then let his guardian dictate in justice. And bring to witness two witnesses from among your men. And if there are not two men [available], then a man and two women from those whom you accept as witnesses - so that if one of the women errs, then the other can remind her. And let not the witnesses refuse when they are called upon. And do not be [too] weary to write it, whether it is small or large, for its [specified] term. That is more just in the sight of Allah and stronger as evidence and more likely to prevent doubt between you, except when it is an immediate transaction which you conduct among yourselves. For [then] there is no blame upon you if you do not write it. And take witnesses when you conclude a contract. Let no scribe be harmed or any witness. For if you do so, indeed, it is [grave] disobedience in you. And fear Allah. And Allah teaches you. And Allah is Knowing of all things", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِذَا تَدَايَنتُم بِدَيۡنٍ إِلَىٰٓ أَجَلٖ مُّسَمّٗى فَٱكۡتُبُوهُۚ وَلۡيَكۡتُب بَّيۡنَكُمۡ كَاتِبُۢ بِٱلۡعَدۡلِۚ وَلَا يَأۡبَ كَاتِبٌ أَن يَكۡتُبَ كَمَا عَلَّمَهُ ٱللَّهُۚ فَلۡيَكۡتُبۡ وَلۡيُمۡلِلِ ٱلَّذِي عَلَيۡهِ ٱلۡحَقُّ وَلۡيَتَّقِ ٱللَّهَ رَبَّهُۥ وَلَا يَبۡخَسۡ مِنۡهُ شَيۡـٔٗاۚ فَإِن كَانَ ٱلَّذِي عَلَيۡهِ ٱلۡحَقُّ سَفِيهًا أَوۡ ضَعِيفًا أَوۡ لَا يَسۡتَطِيعُ أَن يُمِلَّ هُوَ فَلۡيُمۡلِلۡ وَلِيُّهُۥ بِٱلۡعَدۡلِۚ وَٱسۡتَشۡهِدُواْ شَهِيدَيۡنِ مِن رِّجَالِكُمۡۖ فَإِن لَّمۡ يَكُونَا رَجُلَيۡنِ فَرَجُلٞ وَٱمۡرَأَتَانِ مِمَّن تَرۡضَوۡنَ مِنَ ٱلشُّهَدَآءِ أَن تَضِلَّ إِحۡدَىٰهُمَا فَتُذَكِّرَ إِحۡدَىٰهُمَا ٱلۡأُخۡرَىٰۚ وَلَا يَأۡبَ ٱلشُّهَدَآءُ إِذَا مَا دُعُواْۚ وَلَا تَسۡـَٔمُوٓاْ أَن تَكۡتُبُوهُ صَغِيرًا أَوۡ كَبِيرًا إِلَىٰٓ أَجَلِهِۦۚ ذَٰلِكُمۡ أَقۡسَطُ عِندَ ٱللَّهِ وَأَقۡوَمُ لِلشَّهَٰدَةِ وَأَدۡنَىٰٓ أَلَّا تَرۡتَابُوٓاْ إِلَّآ أَن تَكُونَ تِجَٰرَةً حَاضِرَةٗ تُدِيرُونَهَا بَيۡنَكُمۡ فَلَيۡسَ عَلَيۡكُمۡ جُنَاحٌ أَلَّا تَكۡتُبُوهَاۗ وَأَشۡهِدُوٓاْ إِذَا تَبَايَعۡتُمۡۚ وَلَا يُضَآرَّ كَاتِبٞ وَلَا شَهِيدٞۚ وَإِن تَفۡعَلُواْ فَإِنَّهُۥ فُسُوقُۢ بِكُمۡۗ وَٱتَّقُواْ ٱللَّهَۖ وَيُعَلِّمُكُمُ ٱللَّهُۗ وَٱللَّهُ بِكُلِّ شَيۡءٍ عَلِيمٞ", "Ya ayyuha allatheena amanoo itha tadayantum bidaynin ila ajalin musamman faoktuboohu walyaktub baynakum katibun bialAAadli wala yaba katibun an yaktuba kama AAallamahu Allahu falyaktub walyumlili allathee AAalayhi alhaqqu walyattaqi Allaha rabbahu wala yabkhas minhu shayan fain kana allathee AAalayhi alhaqqu safeehan aw daAAeefan aw la yastateeAAu an yumilla huwa falyumlil waliyyuhu bialAAadli waistashhidoo shaheedayni min rijalikum fain lam yakoona rajulayni farajulun waimraatani mimman tardawna mina alshshuhadai an tadilla ihdahuma fatuthakkira ihdahuma alokhra wala yaba alshshuhadao itha ma duAAoo wala tasamoo an taktuboohu sagheeran aw kabeeran ila ajalihi thalikum aqsatu AAinda Allahi waaqwamu lilshshahadati waadna alla tartaboo illa an takoona tijaratan hadiratan tudeeroonaha baynakum falaysa AAalaykum junahun alla taktubooha waashhidoo itha tabayaAAtum wala yudarra katibun wala shaheedun wain tafAAaloo fainnahu fusooqun bikum waittaqoo Allaha wayuAAallimukumu Allahu waAllahu bikulli shayin AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 283, "And if you are on a journey and cannot find a scribe, then a security deposit [should be] taken. And if one of you entrusts another, then let him who is entrusted discharge his trust [faithfully] and let him fear Allah, his Lord. And do not conceal testimony, for whoever conceals it - his heart is indeed sinful, and Allah is Knowing of what you do", "۞وَإِن كُنتُمۡ عَلَىٰ سَفَرٖ وَلَمۡ تَجِدُواْ كَاتِبٗا فَرِهَٰنٞ مَّقۡبُوضَةٞۖ فَإِنۡ أَمِنَ بَعۡضُكُم بَعۡضٗا فَلۡيُؤَدِّ ٱلَّذِي ٱؤۡتُمِنَ أَمَٰنَتَهُۥ وَلۡيَتَّقِ ٱللَّهَ رَبَّهُۥۗ وَلَا تَكۡتُمُواْ ٱلشَّهَٰدَةَۚ وَمَن يَكۡتُمۡهَا فَإِنَّهُۥٓ ءَاثِمٞ قَلۡبُهُۥۗ وَٱللَّهُ بِمَا تَعۡمَلُونَ عَلِيمٞ", "Wain kuntum AAala safarin walam tajidoo katiban farihanun maqboodatun fain amina baAAdukum baAAdan falyuaddi allathee itumina amanatahu walyattaqi Allaha rabbahu wala taktumoo alshshahadata waman yaktumha fainnahu athimun qalbuhu waAllahu bima taAAmaloona AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 284, "To Allah belongs whatever is in the heavens and whatever is in the earth. Whether you show what is within yourselves or conceal it, Allah will bring you to account for it. Then He will forgive whom He wills and punish whom He wills, and Allah is over all things competent", "لِّلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۗ وَإِن تُبۡدُواْ مَا فِيٓ أَنفُسِكُمۡ أَوۡ تُخۡفُوهُ يُحَاسِبۡكُم بِهِ ٱللَّهُۖ فَيَغۡفِرُ لِمَن يَشَآءُ وَيُعَذِّبُ مَن يَشَآءُۗ وَٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ", "Lillahi ma fee alssamawati wama fee alardi wain tubdoo ma fee anfusikum aw tukhfoohu yuhasibkum bihi Allahu fayaghfiru liman yashao wayuAAaththibu man yashao waAllahu AAala kulli shayin qadeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 285, "The Messenger has believed in what was revealed to him from his Lord, and [so have] the believers. All of them have believed in Allah and His angels and His books and His messengers, [saying], ""We make no distinction between any of His messengers."" And they say, ""We hear and we obey. [We seek] Your forgiveness, our Lord, and to You is the [final] destination", "ءَامَنَ ٱلرَّسُولُ بِمَآ أُنزِلَ إِلَيۡهِ مِن رَّبِّهِۦ وَٱلۡمُؤۡمِنُونَۚ كُلٌّ ءَامَنَ بِٱللَّهِ وَمَلَـٰٓئِكَتِهِۦ وَكُتُبِهِۦ وَرُسُلِهِۦ لَا نُفَرِّقُ بَيۡنَ أَحَدٖ مِّن رُّسُلِهِۦۚ وَقَالُواْ سَمِعۡنَا وَأَطَعۡنَاۖ غُفۡرَانَكَ رَبَّنَا وَإِلَيۡكَ ٱلۡمَصِيرُ", "Amana alrrasoolu bima onzila ilayhi min rabbihi waalmuminoona kullun amana biAllahi wamalaikatihi wakutubihi warusulihi la nufarriqu bayna ahadin min rusulihi waqaloo samiAAna waataAAna ghufranaka rabbana wailayka almaseeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (2, 286, "Allah does not charge a soul except [with that within] its capacity. It will have [the consequence of] what [good] it has gained, and it will bear [the consequence of] what [evil] it has earned. ""Our Lord, do not impose blame upon us if we have forgotten or erred. Our Lord, and lay not upon us a burden like that which You laid upon those before us. Our Lord, and burden us not with that which we have no ability to bear. And pardon us; and forgive us; and have mercy upon us. You are our protector, so give us victory over the disbelieving people", "لَا يُكَلِّفُ ٱللَّهُ نَفۡسًا إِلَّا وُسۡعَهَاۚ لَهَا مَا كَسَبَتۡ وَعَلَيۡهَا مَا ٱكۡتَسَبَتۡۗ رَبَّنَا لَا تُؤَاخِذۡنَآ إِن نَّسِينَآ أَوۡ أَخۡطَأۡنَاۚ رَبَّنَا وَلَا تَحۡمِلۡ عَلَيۡنَآ إِصۡرٗا كَمَا حَمَلۡتَهُۥ عَلَى ٱلَّذِينَ مِن قَبۡلِنَاۚ رَبَّنَا وَلَا تُحَمِّلۡنَا مَا لَا طَاقَةَ لَنَا بِهِۦۖ وَٱعۡفُ عَنَّا وَٱغۡفِرۡ لَنَا وَٱرۡحَمۡنَآۚ أَنتَ مَوۡلَىٰنَا فَٱنصُرۡنَا عَلَى ٱلۡقَوۡمِ ٱلۡكَٰفِرِينَ", "La yukallifu Allahu nafsan illa wusAAaha laha ma kasabat waAAalayha ma iktasabat rabbana la tuakhithna in naseena aw akhtana rabbana wala tahmil AAalayna isran kama hamaltahu AAala allatheena min qablina rabbana wala tuhammilna ma la taqata lana bihi waoAAfu AAanna waighfir lana wairhamna anta mawlana faonsurna AAala alqawmi alkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 1, "Alif, Lam, Meem", "الٓمٓ", "Aliflammeem");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 2, "Allah - there is no deity except Him, the Ever-Living, the Sustainer of existence", "ٱللَّهُ لَآ إِلَٰهَ إِلَّا هُوَ ٱلۡحَيُّ ٱلۡقَيُّومُ", "Allahu la ilaha illa huwa alhayyu alqayyoomu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 3, "He has sent down upon you, [O Muhammad], the Book in truth, confirming what was before it. And He revealed the Torah and the Gospel", "نَزَّلَ عَلَيۡكَ ٱلۡكِتَٰبَ بِٱلۡحَقِّ مُصَدِّقٗا لِّمَا بَيۡنَ يَدَيۡهِ وَأَنزَلَ ٱلتَّوۡرَىٰةَ وَٱلۡإِنجِيلَ", "Nazzala AAalayka alkitaba bialhaqqi musaddiqan lima bayna yadayhi waanzala alttawrata waalinjeela");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 4, "Before, as guidance for the people. And He revealed the Qur'an. Indeed, those who disbelieve in the verses of Allah will have a severe punishment, and Allah is exalted in Might, the Owner of Retribution", "مِن قَبۡلُ هُدٗى لِّلنَّاسِ وَأَنزَلَ ٱلۡفُرۡقَانَۗ إِنَّ ٱلَّذِينَ كَفَرُواْ بِـَٔايَٰتِ ٱللَّهِ لَهُمۡ عَذَابٞ شَدِيدٞۗ وَٱللَّهُ عَزِيزٞ ذُو ٱنتِقَامٍ", "Min qablu hudan lilnnasi waanzala alfurqana inna allatheena kafaroo biayati Allahi lahum AAathabun shadeedun waAllahu AAazeezun thoo intiqamin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 5, "Indeed, from Allah nothing is hidden in the earth nor in the heaven", "إِنَّ ٱللَّهَ لَا يَخۡفَىٰ عَلَيۡهِ شَيۡءٞ فِي ٱلۡأَرۡضِ وَلَا فِي ٱلسَّمَآءِ", "Inna Allaha la yakhfa AAalayhi shayon fee alardi wala fee alssamai");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 6, "It is He who forms you in the wombs however He wills. There is no deity except Him, the Exalted in Might, the Wise", "هُوَ ٱلَّذِي يُصَوِّرُكُمۡ فِي ٱلۡأَرۡحَامِ كَيۡفَ يَشَآءُۚ لَآ إِلَٰهَ إِلَّا هُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ", "Huwa allathee yusawwirukum fee alarhami kayfa yashao la ilaha illa huwa alAAazeezu alhakeemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 7, "It is He who has sent down to you, [O Muhammad], the Book; in it are verses [that are] precise - they are the foundation of the Book - and others unspecific. As for those in whose hearts is deviation [from truth], they will follow that of it which is unspecific, seeking discord and seeking an interpretation [suitable to them]. And no one knows its [true] interpretation except Allah. But those firm in knowledge say, ""We believe in it. All [of it] is from our Lord."" And no one will be reminded except those of understanding", "هُوَ ٱلَّذِيٓ أَنزَلَ عَلَيۡكَ ٱلۡكِتَٰبَ مِنۡهُ ءَايَٰتٞ مُّحۡكَمَٰتٌ هُنَّ أُمُّ ٱلۡكِتَٰبِ وَأُخَرُ مُتَشَٰبِهَٰتٞۖ فَأَمَّا ٱلَّذِينَ فِي قُلُوبِهِمۡ زَيۡغٞ فَيَتَّبِعُونَ مَا تَشَٰبَهَ مِنۡهُ ٱبۡتِغَآءَ ٱلۡفِتۡنَةِ وَٱبۡتِغَآءَ تَأۡوِيلِهِۦۖ وَمَا يَعۡلَمُ تَأۡوِيلَهُۥٓ إِلَّا ٱللَّهُۗ وَٱلرَّـٰسِخُونَ فِي ٱلۡعِلۡمِ يَقُولُونَ ءَامَنَّا بِهِۦ كُلّٞ مِّنۡ عِندِ رَبِّنَاۗ وَمَا يَذَّكَّرُ إِلَّآ أُوْلُواْ ٱلۡأَلۡبَٰبِ", "Huwa allathee anzala AAalayka alkitaba minhu ayatun muhkamatun hunna ommu alkitabi waokharu mutashabihatun faamma allatheena fee quloobihim zayghun fayattabiAAoona ma tashabaha minhu ibtighaa alfitnati waibtighaa taweelihi wama yaAAlamu taweelahu illa Allahu waalrrasikhoona fee alAAilmi yaqooloona amanna bihi kullun min AAindi rabbina wama yaththakkaru illa oloo alalbabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 8, "[Who say], ""Our Lord, let not our hearts deviate after You have guided us and grant us from Yourself mercy. Indeed, You are the Bestower", "رَبَّنَا لَا تُزِغۡ قُلُوبَنَا بَعۡدَ إِذۡ هَدَيۡتَنَا وَهَبۡ لَنَا مِن لَّدُنكَ رَحۡمَةًۚ إِنَّكَ أَنتَ ٱلۡوَهَّابُ", "Rabbana la tuzigh quloobana baAAda ith hadaytana wahab lana min ladunka rahmatan innaka anta alwahhabu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 9, "Our Lord, surely You will gather the people for a Day about which there is no doubt. Indeed, Allah does not fail in His promise", "رَبَّنَآ إِنَّكَ جَامِعُ ٱلنَّاسِ لِيَوۡمٖ لَّا رَيۡبَ فِيهِۚ إِنَّ ٱللَّهَ لَا يُخۡلِفُ ٱلۡمِيعَادَ", "Rabbana innaka jamiAAu alnnasi liyawmin la rayba feehi inna Allaha la yukhlifu almeeAAada");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 10, "Indeed, those who disbelieve - never will their wealth or their children avail them against Allah at all. And it is they who are fuel for the Fire", "إِنَّ ٱلَّذِينَ كَفَرُواْ لَن تُغۡنِيَ عَنۡهُمۡ أَمۡوَٰلُهُمۡ وَلَآ أَوۡلَٰدُهُم مِّنَ ٱللَّهِ شَيۡـٔٗاۖ وَأُوْلَـٰٓئِكَ هُمۡ وَقُودُ ٱلنَّارِ", "Inna allatheena kafaroo lan tughniya AAanhum amwaluhum wala awladuhum mina Allahi shayan waolaika hum waqoodu alnnari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 11, "[Theirs is] like the custom of the people of Pharaoh and those before them. They denied Our signs, so Allah seized them for their sins. And Allah is severe in penalty", "كَدَأۡبِ ءَالِ فِرۡعَوۡنَ وَٱلَّذِينَ مِن قَبۡلِهِمۡۚ كَذَّبُواْ بِـَٔايَٰتِنَا فَأَخَذَهُمُ ٱللَّهُ بِذُنُوبِهِمۡۗ وَٱللَّهُ شَدِيدُ ٱلۡعِقَابِ", "Kadabi ali firAAawna waallatheena min qablihim kaththaboo biayatina faakhathahumu Allahu bithunoobihim waAllahu shadeedu alAAiqabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 12, "Say to those who disbelieve, ""You will be overcome and gathered together to Hell, and wretched is the resting place", "قُل لِّلَّذِينَ كَفَرُواْ سَتُغۡلَبُونَ وَتُحۡشَرُونَ إِلَىٰ جَهَنَّمَۖ وَبِئۡسَ ٱلۡمِهَادُ", "Qul lillatheena kafaroo satughlaboona watuhsharoona ila jahannama wabisa almihadu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 13, "Already there has been for you a sign in the two armies which met - one fighting in the cause of Allah and another of disbelievers. They saw them [to be] twice their [own] number by [their] eyesight. But Allah supports with His victory whom He wills. Indeed in that is a lesson for those of vision", "قَدۡ كَانَ لَكُمۡ ءَايَةٞ فِي فِئَتَيۡنِ ٱلۡتَقَتَاۖ فِئَةٞ تُقَٰتِلُ فِي سَبِيلِ ٱللَّهِ وَأُخۡرَىٰ كَافِرَةٞ يَرَوۡنَهُم مِّثۡلَيۡهِمۡ رَأۡيَ ٱلۡعَيۡنِۚ وَٱللَّهُ يُؤَيِّدُ بِنَصۡرِهِۦ مَن يَشَآءُۚ إِنَّ فِي ذَٰلِكَ لَعِبۡرَةٗ لِّأُوْلِي ٱلۡأَبۡصَٰرِ", "Qad kana lakum ayatun fee fiatayni iltaqata fiatun tuqatilu fee sabeeli Allahi waokhra kafiratun yarawnahum mithlayhim raya alAAayni waAllahu yuayyidu binasrihi man yashao inna fee thalika laAAibratan liolee alabsari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 14, "Beautified for people is the love of that which they desire - of women and sons, heaped-up sums of gold and silver, fine branded horses, and cattle and tilled land. That is the enjoyment of worldly life, but Allah has with Him the best return", "زُيِّنَ لِلنَّاسِ حُبُّ ٱلشَّهَوَٰتِ مِنَ ٱلنِّسَآءِ وَٱلۡبَنِينَ وَٱلۡقَنَٰطِيرِ ٱلۡمُقَنطَرَةِ مِنَ ٱلذَّهَبِ وَٱلۡفِضَّةِ وَٱلۡخَيۡلِ ٱلۡمُسَوَّمَةِ وَٱلۡأَنۡعَٰمِ وَٱلۡحَرۡثِۗ ذَٰلِكَ مَتَٰعُ ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۖ وَٱللَّهُ عِندَهُۥ حُسۡنُ ٱلۡمَـَٔابِ", "Zuyyina lilnnasi hubbu alshshahawati mina alnnisai waalbaneena waalqanateeri almuqantarati mina alththahabi waalfiddati waalkhayli almusawwamati waalanAAami waalharthi thalika mataAAu alhayati alddunya waAllahu AAindahu husnu almaabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 15, "Say, ""Shall I inform you of [something] better than that? For those who fear Allah will be gardens in the presence of their Lord beneath which rivers flow, wherein they abide eternally, and purified spouses and approval from Allah. And Allah is Seeing of [His] servants", "۞قُلۡ أَؤُنَبِّئُكُم بِخَيۡرٖ مِّن ذَٰلِكُمۡۖ لِلَّذِينَ ٱتَّقَوۡاْ عِندَ رَبِّهِمۡ جَنَّـٰتٞ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَا وَأَزۡوَٰجٞ مُّطَهَّرَةٞ وَرِضۡوَٰنٞ مِّنَ ٱللَّهِۗ وَٱللَّهُ بَصِيرُۢ بِٱلۡعِبَادِ", "Qul aonabbiokum bikhayrin min thalikum lillatheena ittaqaw AAinda rabbihim jannatun tajree min tahtiha alanharu khalideena feeha waazwajun mutahharatun waridwanun mina Allahi waAllahu baseerun bialAAibadi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 16, "Those who say, ""Our Lord, indeed we have believed, so forgive us our sins and protect us from the punishment of the Fire", "ٱلَّذِينَ يَقُولُونَ رَبَّنَآ إِنَّنَآ ءَامَنَّا فَٱغۡفِرۡ لَنَا ذُنُوبَنَا وَقِنَا عَذَابَ ٱلنَّارِ", "Allatheena yaqooloona rabbana innana amanna faighfir lana thunoobana waqina AAathaba alnnari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 17, "The patient, the true, the obedient, those who spend [in the way of Allah], and those who seek forgiveness before dawn", "ٱلصَّـٰبِرِينَ وَٱلصَّـٰدِقِينَ وَٱلۡقَٰنِتِينَ وَٱلۡمُنفِقِينَ وَٱلۡمُسۡتَغۡفِرِينَ بِٱلۡأَسۡحَارِ", "Alssabireena waalssadiqeena waalqaniteena waalmunfiqeena waalmustaghfireena bialashari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 18, "Allah witnesses that there is no deity except Him, and [so do] the angels and those of knowledge - [that He is] maintaining [creation] in justice. There is no deity except Him, the Exalted in Might, the Wise", "شَهِدَ ٱللَّهُ أَنَّهُۥ لَآ إِلَٰهَ إِلَّا هُوَ وَٱلۡمَلَـٰٓئِكَةُ وَأُوْلُواْ ٱلۡعِلۡمِ قَآئِمَۢا بِٱلۡقِسۡطِۚ لَآ إِلَٰهَ إِلَّا هُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ", "Shahida Allahu annahu la ilaha illa huwa waalmalaikatu waoloo alAAilmi qaiman bialqisti la ilaha illa huwa alAAazeezu alhakeemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 19, "Indeed, the religion in the sight of Allah is Islam. And those who were given the Scripture did not differ except after knowledge had come to them - out of jealous animosity between themselves. And whoever disbelieves in the verses of Allah, then indeed, Allah is swift in [taking] account", "إِنَّ ٱلدِّينَ عِندَ ٱللَّهِ ٱلۡإِسۡلَٰمُۗ وَمَا ٱخۡتَلَفَ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ إِلَّا مِنۢ بَعۡدِ مَا جَآءَهُمُ ٱلۡعِلۡمُ بَغۡيَۢا بَيۡنَهُمۡۗ وَمَن يَكۡفُرۡ بِـَٔايَٰتِ ٱللَّهِ فَإِنَّ ٱللَّهَ سَرِيعُ ٱلۡحِسَابِ", "Inna alddeena AAinda Allahi alislamu wama ikhtalafa allatheena ootoo alkitaba illa min baAAdi ma jaahumu alAAilmu baghyan baynahum waman yakfur biayati Allahi fainna Allaha sareeAAu alhisabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 20, "So if they argue with you, say, ""I have submitted myself to Allah [in Islam], and [so have] those who follow me."" And say to those who were given the Scripture and [to] the unlearned, ""Have you submitted yourselves?"" And if they submit [in Islam], they are rightly guided; but if they turn away - then upon you is only the [duty of] notification. And Allah is Seeing of [His] servants", "فَإِنۡ حَآجُّوكَ فَقُلۡ أَسۡلَمۡتُ وَجۡهِيَ لِلَّهِ وَمَنِ ٱتَّبَعَنِۗ وَقُل لِّلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ وَٱلۡأُمِّيِّـۧنَ ءَأَسۡلَمۡتُمۡۚ فَإِنۡ أَسۡلَمُواْ فَقَدِ ٱهۡتَدَواْۖ وَّإِن تَوَلَّوۡاْ فَإِنَّمَا عَلَيۡكَ ٱلۡبَلَٰغُۗ وَٱللَّهُ بَصِيرُۢ بِٱلۡعِبَادِ", "Fain hajjooka faqul aslamtu wajhiya lillahi wamani ittabaAAani waqul lillatheena ootoo alkitaba waalommiyyeena aaslamtum fain aslamoo faqadi ihtadaw wain tawallaw fainnama AAalayka albalaghu waAllahu baseerun bialAAibadi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 21, "Those who disbelieve in the signs of Allah and kill the prophets without right and kill those who order justice from among the people - give them tidings of a painful punishment", "إِنَّ ٱلَّذِينَ يَكۡفُرُونَ بِـَٔايَٰتِ ٱللَّهِ وَيَقۡتُلُونَ ٱلنَّبِيِّـۧنَ بِغَيۡرِ حَقّٖ وَيَقۡتُلُونَ ٱلَّذِينَ يَأۡمُرُونَ بِٱلۡقِسۡطِ مِنَ ٱلنَّاسِ فَبَشِّرۡهُم بِعَذَابٍ أَلِيمٍ", "Inna allatheena yakfuroona biayati Allahi wayaqtuloona alnnabiyyeena bighayri haqqin wayaqtuloona allatheena yamuroona bialqisti mina alnnasi fabashshirhum biAAathabin aleemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 22, "They are the ones whose deeds have become worthless in this world and the Hereafter, and for them there will be no helpers", "أُوْلَـٰٓئِكَ ٱلَّذِينَ حَبِطَتۡ أَعۡمَٰلُهُمۡ فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِ وَمَا لَهُم مِّن نَّـٰصِرِينَ", "Olaika allatheena habitat aAAmaluhum fee alddunya waalakhirati wama lahum min nasireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 23, "Do you not consider, [O Muhammad], those who were given a portion of the Scripture? They are invited to the Scripture of Allah that it should arbitrate between them; then a party of them turns away, and they are refusing", "أَلَمۡ تَرَ إِلَى ٱلَّذِينَ أُوتُواْ نَصِيبٗا مِّنَ ٱلۡكِتَٰبِ يُدۡعَوۡنَ إِلَىٰ كِتَٰبِ ٱللَّهِ لِيَحۡكُمَ بَيۡنَهُمۡ ثُمَّ يَتَوَلَّىٰ فَرِيقٞ مِّنۡهُمۡ وَهُم مُّعۡرِضُونَ", "Alam tara ila allatheena ootoo naseeban mina alkitabi yudAAawna ila kitabi Allahi liyahkuma baynahum thumma yatawalla fareequn minhum wahum muAAridoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 24, "That is because they say, ""Never will the Fire touch us except for [a few] numbered days,"" and [because] they were deluded in their religion by what they were inventing", "ذَٰلِكَ بِأَنَّهُمۡ قَالُواْ لَن تَمَسَّنَا ٱلنَّارُ إِلَّآ أَيَّامٗا مَّعۡدُودَٰتٖۖ وَغَرَّهُمۡ فِي دِينِهِم مَّا كَانُواْ يَفۡتَرُونَ", "Thalika biannahum qaloo lan tamassana alnnaru illa ayyaman maAAdoodatin wagharrahum fee deenihim ma kanoo yaftaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 25, "So how will it be when We assemble them for a Day about which there is no doubt? And each soul will be compensated [in full for] what it earned, and they will not be wronged", "فَكَيۡفَ إِذَا جَمَعۡنَٰهُمۡ لِيَوۡمٖ لَّا رَيۡبَ فِيهِ وَوُفِّيَتۡ كُلُّ نَفۡسٖ مَّا كَسَبَتۡ وَهُمۡ لَا يُظۡلَمُونَ", "Fakayfa itha jamaAAnahum liyawmin la rayba feehi wawuffiyat kullu nafsin ma kasabat wahum la yuthlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 26, "Say, ""O Allah, Owner of Sovereignty, You give sovereignty to whom You will and You take sovereignty away from whom You will. You honor whom You will and You humble whom You will. In Your hand is [all] good. Indeed, You are over all things competent", "قُلِ ٱللَّهُمَّ مَٰلِكَ ٱلۡمُلۡكِ تُؤۡتِي ٱلۡمُلۡكَ مَن تَشَآءُ وَتَنزِعُ ٱلۡمُلۡكَ مِمَّن تَشَآءُ وَتُعِزُّ مَن تَشَآءُ وَتُذِلُّ مَن تَشَآءُۖ بِيَدِكَ ٱلۡخَيۡرُۖ إِنَّكَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ", "Quli allahumma malika almulki tutee almulka man tashao watanziAAu almulka mimman tashao watuAAizzu man tashao watuthillu man tashao biyadika alkhayru innaka AAala kulli shayin qadeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 27, "You cause the night to enter the day, and You cause the day to enter the night; and You bring the living out of the dead, and You bring the dead out of the living. And You give provision to whom You will without account", "تُولِجُ ٱلَّيۡلَ فِي ٱلنَّهَارِ وَتُولِجُ ٱلنَّهَارَ فِي ٱلَّيۡلِۖ وَتُخۡرِجُ ٱلۡحَيَّ مِنَ ٱلۡمَيِّتِ وَتُخۡرِجُ ٱلۡمَيِّتَ مِنَ ٱلۡحَيِّۖ وَتَرۡزُقُ مَن تَشَآءُ بِغَيۡرِ حِسَابٖ", "Tooliju allayla fee alnnahari watooliju alnnahara fee allayli watukhriju alhayya mina almayyiti watukhriju almayyita mina alhayyi watarzuqu man tashao bighayri hisabin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 28, "Let not believers take disbelievers as allies rather than believers. And whoever [of you] does that has nothing with Allah, except when taking precaution against them in prudence. And Allah warns you of Himself, and to Allah is the [final] destination", "لَّا يَتَّخِذِ ٱلۡمُؤۡمِنُونَ ٱلۡكَٰفِرِينَ أَوۡلِيَآءَ مِن دُونِ ٱلۡمُؤۡمِنِينَۖ وَمَن يَفۡعَلۡ ذَٰلِكَ فَلَيۡسَ مِنَ ٱللَّهِ فِي شَيۡءٍ إِلَّآ أَن تَتَّقُواْ مِنۡهُمۡ تُقَىٰةٗۗ وَيُحَذِّرُكُمُ ٱللَّهُ نَفۡسَهُۥۗ وَإِلَى ٱللَّهِ ٱلۡمَصِيرُ", "La yattakhithi almuminoona alkafireena awliyaa min dooni almumineena waman yafAAal thalika falaysa mina Allahi fee shayin illa an tattaqoo minhum tuqatan wayuhaththirukumu Allahu nafsahu waila Allahi almaseeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 29, "Say, ""Whether you conceal what is in your breasts or reveal it, Allah knows it. And He knows that which is in the heavens and that which is on the earth. And Allah is over all things competent", "قُلۡ إِن تُخۡفُواْ مَا فِي صُدُورِكُمۡ أَوۡ تُبۡدُوهُ يَعۡلَمۡهُ ٱللَّهُۗ وَيَعۡلَمُ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۗ وَٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ", "Qul in tukhfoo ma fee sudoorikum aw tubdoohu yaAAlamhu Allahu wayaAAlamu ma fee alssamawati wama fee alardi waAllahu AAala kulli shayin qadeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 30, "The Day every soul will find what it has done of good present [before it] and what it has done of evil, it will wish that between itself and that [evil] was a great distance. And Allah warns you of Himself, and Allah is Kind to [His] servants", "يَوۡمَ تَجِدُ كُلُّ نَفۡسٖ مَّا عَمِلَتۡ مِنۡ خَيۡرٖ مُّحۡضَرٗا وَمَا عَمِلَتۡ مِن سُوٓءٖ تَوَدُّ لَوۡ أَنَّ بَيۡنَهَا وَبَيۡنَهُۥٓ أَمَدَۢا بَعِيدٗاۗ وَيُحَذِّرُكُمُ ٱللَّهُ نَفۡسَهُۥۗ وَٱللَّهُ رَءُوفُۢ بِٱلۡعِبَادِ", "Yawma tajidu kullu nafsin ma AAamilat min khayrin muhdaran wama AAamilat min sooin tawaddu law anna baynaha wabaynahu amadan baAAeedan wayuhaththirukumu Allahu nafsahu waAllahu raoofun bialAAibadi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 31, "Say, [O Muhammad], ""If you should love Allah, then follow me, [so] Allah will love you and forgive you your sins. And Allah is Forgiving and Merciful", "قُلۡ إِن كُنتُمۡ تُحِبُّونَ ٱللَّهَ فَٱتَّبِعُونِي يُحۡبِبۡكُمُ ٱللَّهُ وَيَغۡفِرۡ لَكُمۡ ذُنُوبَكُمۡۚ وَٱللَّهُ غَفُورٞ رَّحِيمٞ", "Qul in kuntum tuhibboona Allaha faittabiAAoonee yuhbibkumu Allahu wayaghfir lakum thunoobakum waAllahu ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 32, "Say, ""Obey Allah and the Messenger."" But if they turn away - then indeed, Allah does not like the disbelievers", "قُلۡ أَطِيعُواْ ٱللَّهَ وَٱلرَّسُولَۖ فَإِن تَوَلَّوۡاْ فَإِنَّ ٱللَّهَ لَا يُحِبُّ ٱلۡكَٰفِرِينَ", "Qul ateeAAoo Allaha waalrrasoola fain tawallaw fainna Allaha la yuhibbu alkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 33, "Indeed, Allah chose Adam and Noah and the family of Abraham and the family of 'Imran over the worlds", "۞إِنَّ ٱللَّهَ ٱصۡطَفَىٰٓ ءَادَمَ وَنُوحٗا وَءَالَ إِبۡرَٰهِيمَ وَءَالَ عِمۡرَٰنَ عَلَى ٱلۡعَٰلَمِينَ", "Inna Allaha istafa adama wanoohan waala ibraheema waala AAimrana AAala alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 34, "Descendants, some of them from others. And Allah is Hearing and Knowing", "ذُرِّيَّةَۢ بَعۡضُهَا مِنۢ بَعۡضٖۗ وَٱللَّهُ سَمِيعٌ عَلِيمٌ", "Thurriyyatan baAAduha min baAAdin waAllahu sameeAAun AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 35, "[Mention, O Muhammad], when the wife of 'Imran said, ""My Lord, indeed I have pledged to You what is in my womb, consecrated [for Your service], so accept this from me. Indeed, You are the Hearing, the Knowing", "إِذۡ قَالَتِ ٱمۡرَأَتُ عِمۡرَٰنَ رَبِّ إِنِّي نَذَرۡتُ لَكَ مَا فِي بَطۡنِي مُحَرَّرٗا فَتَقَبَّلۡ مِنِّيٓۖ إِنَّكَ أَنتَ ٱلسَّمِيعُ ٱلۡعَلِيمُ", "Ith qalati imraatu AAimrana rabbi innee nathartu laka ma fee batnee muharraran fataqabbal minnee innaka anta alssameeAAu alAAaleemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 36, "But when she delivered her, she said, ""My Lord, I have delivered a female."" And Allah was most knowing of what she delivered, ""And the male is not like the female. And I have named her Mary, and I seek refuge for her in You and [for] her descendants from Satan, the expelled [from the mercy of Allah]", "فَلَمَّا وَضَعَتۡهَا قَالَتۡ رَبِّ إِنِّي وَضَعۡتُهَآ أُنثَىٰ وَٱللَّهُ أَعۡلَمُ بِمَا وَضَعَتۡ وَلَيۡسَ ٱلذَّكَرُ كَٱلۡأُنثَىٰۖ وَإِنِّي سَمَّيۡتُهَا مَرۡيَمَ وَإِنِّيٓ أُعِيذُهَا بِكَ وَذُرِّيَّتَهَا مِنَ ٱلشَّيۡطَٰنِ ٱلرَّجِيمِ", "Falamma wadaAAatha qalat rabbi innee wadaAAtuha ontha waAllahu aAAlamu bima wadaAAat walaysa alththakaru kaalontha wainnee sammaytuha maryama wainnee oAAeethuha bika wathurriyyataha mina alshshaytani alrrajeemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 37, "So her Lord accepted her with good acceptance and caused her to grow in a good manner and put her in the care of Zechariah. Every time Zechariah entered upon her in the prayer chamber, he found with her provision. He said, ""O Mary, from where is this [coming] to you?"" She said, ""It is from Allah. Indeed, Allah provides for whom He wills without account", "فَتَقَبَّلَهَا رَبُّهَا بِقَبُولٍ حَسَنٖ وَأَنۢبَتَهَا نَبَاتًا حَسَنٗا وَكَفَّلَهَا زَكَرِيَّاۖ كُلَّمَا دَخَلَ عَلَيۡهَا زَكَرِيَّا ٱلۡمِحۡرَابَ وَجَدَ عِندَهَا رِزۡقٗاۖ قَالَ يَٰمَرۡيَمُ أَنَّىٰ لَكِ هَٰذَاۖ قَالَتۡ هُوَ مِنۡ عِندِ ٱللَّهِۖ إِنَّ ٱللَّهَ يَرۡزُقُ مَن يَشَآءُ بِغَيۡرِ حِسَابٍ", "Fataqabbalaha rabbuha biqaboolin hasanin waanbataha nabatan hasanan wakaffalaha zakariyya kullama dakhala AAalayha zakariyya almihraba wajada AAindaha rizqan qala ya maryamu anna laki hatha qalat huwa min AAindi Allahi inna Allaha yarzuqu man yashao bighayri hisabin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 38, "At that, Zechariah called upon his Lord, saying, ""My Lord, grant me from Yourself a good offspring. Indeed, You are the Hearer of supplication", "هُنَالِكَ دَعَا زَكَرِيَّا رَبَّهُۥۖ قَالَ رَبِّ هَبۡ لِي مِن لَّدُنكَ ذُرِّيَّةٗ طَيِّبَةًۖ إِنَّكَ سَمِيعُ ٱلدُّعَآءِ", "Hunalika daAAa zakariyya rabbahu qala rabbi hab lee min ladunka thurriyyatan tayyibatan innaka sameeAAu aldduAAai");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 39, "So the angels called him while he was standing in prayer in the chamber, ""Indeed, Allah gives you good tidings of John, confirming a word from Allah and [who will be] honorable, abstaining [from women], and a prophet from among the righteous", "فَنَادَتۡهُ ٱلۡمَلَـٰٓئِكَةُ وَهُوَ قَآئِمٞ يُصَلِّي فِي ٱلۡمِحۡرَابِ أَنَّ ٱللَّهَ يُبَشِّرُكَ بِيَحۡيَىٰ مُصَدِّقَۢا بِكَلِمَةٖ مِّنَ ٱللَّهِ وَسَيِّدٗا وَحَصُورٗا وَنَبِيّٗا مِّنَ ٱلصَّـٰلِحِينَ", "Fanadathu almalaikatu wahuwa qaimun yusallee fee almihrabi anna Allaha yubashshiruka biyahya musaddiqan bikalimatin mina Allahi wasayyidan wahasooran wanabiyyan mina alssaliheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 40, "He said, ""My Lord, how will I have a boy when I have reached old age and my wife is barren?"" The angel said, ""Such is Allah; He does what He wills", "قَالَ رَبِّ أَنَّىٰ يَكُونُ لِي غُلَٰمٞ وَقَدۡ بَلَغَنِيَ ٱلۡكِبَرُ وَٱمۡرَأَتِي عَاقِرٞۖ قَالَ كَذَٰلِكَ ٱللَّهُ يَفۡعَلُ مَا يَشَآءُ", "Qala rabbi anna yakoonu lee ghulamun waqad balaghaniya alkibaru waimraatee AAaqirun qala kathalika Allahu yafAAalu ma yashao");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 41, "He said, ""My Lord, make for me a sign."" He Said, ""Your sign is that you will not [be able to] speak to the people for three days except by gesture. And remember your Lord much and exalt [Him with praise] in the evening and the morning", "قَالَ رَبِّ ٱجۡعَل لِّيٓ ءَايَةٗۖ قَالَ ءَايَتُكَ أَلَّا تُكَلِّمَ ٱلنَّاسَ ثَلَٰثَةَ أَيَّامٍ إِلَّا رَمۡزٗاۗ وَٱذۡكُر رَّبَّكَ كَثِيرٗا وَسَبِّحۡ بِٱلۡعَشِيِّ وَٱلۡإِبۡكَٰرِ", "Qala rabbi ijAAal lee ayatan qala ayatuka alla tukallima alnnasa thalathata ayyamin illa ramzan waothkur rabbaka katheeran wasabbih bialAAashiyyi waalibkari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 42, "And [mention] when the angels said, ""O Mary, indeed Allah has chosen you and purified you and chosen you above the women of the worlds", "وَإِذۡ قَالَتِ ٱلۡمَلَـٰٓئِكَةُ يَٰمَرۡيَمُ إِنَّ ٱللَّهَ ٱصۡطَفَىٰكِ وَطَهَّرَكِ وَٱصۡطَفَىٰكِ عَلَىٰ نِسَآءِ ٱلۡعَٰلَمِينَ", "Waith qalati almalaikatu ya maryamu inna Allaha istafaki watahharaki waistafaki AAala nisai alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 43, "O Mary, be devoutly obedient to your Lord and prostrate and bow with those who bow [in prayer]", "يَٰمَرۡيَمُ ٱقۡنُتِي لِرَبِّكِ وَٱسۡجُدِي وَٱرۡكَعِي مَعَ ٱلرَّـٰكِعِينَ", "Ya maryamu oqnutee lirabbiki waosjudee wairkaAAee maAAa alrrakiAAeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 44, "That is from the news of the unseen which We reveal to you, [O Muhammad]. And you were not with them when they cast their pens as to which of them should be responsible for Mary. Nor were you with them when they disputed", "ذَٰلِكَ مِنۡ أَنۢبَآءِ ٱلۡغَيۡبِ نُوحِيهِ إِلَيۡكَۚ وَمَا كُنتَ لَدَيۡهِمۡ إِذۡ يُلۡقُونَ أَقۡلَٰمَهُمۡ أَيُّهُمۡ يَكۡفُلُ مَرۡيَمَ وَمَا كُنتَ لَدَيۡهِمۡ إِذۡ يَخۡتَصِمُونَ", "Thalika min anbai alghaybi nooheehi ilayka wama kunta ladayhim ith yulqoona aqlamahum ayyuhum yakfulu maryama wama kunta ladayhim ith yakhtasimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 45, "[And mention] when the angels said, ""O Mary, indeed Allah gives you good tidings of a word from Him, whose name will be the Messiah, Jesus, the son of Mary - distinguished in this world and the Hereafter and among those brought near [to Allah]", "إِذۡ قَالَتِ ٱلۡمَلَـٰٓئِكَةُ يَٰمَرۡيَمُ إِنَّ ٱللَّهَ يُبَشِّرُكِ بِكَلِمَةٖ مِّنۡهُ ٱسۡمُهُ ٱلۡمَسِيحُ عِيسَى ٱبۡنُ مَرۡيَمَ وَجِيهٗا فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِ وَمِنَ ٱلۡمُقَرَّبِينَ", "Ith qalati almalaikatu ya maryamu inna Allaha yubashshiruki bikalimatin minhu ismuhu almaseehu AAeesa ibnu maryama wajeehan fee alddunya waalakhirati wamina almuqarrabeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 46, "He will speak to the people in the cradle and in maturity and will be of the righteous", "وَيُكَلِّمُ ٱلنَّاسَ فِي ٱلۡمَهۡدِ وَكَهۡلٗا وَمِنَ ٱلصَّـٰلِحِينَ", "Wayukallimu alnnasa fee almahdi wakahlan wamina alssaliheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 47, "She said, ""My Lord, how will I have a child when no man has touched me?"" [The angel] said, ""Such is Allah; He creates what He wills. When He decrees a matter, He only says to it, 'Be,' and it is", "قَالَتۡ رَبِّ أَنَّىٰ يَكُونُ لِي وَلَدٞ وَلَمۡ يَمۡسَسۡنِي بَشَرٞۖ قَالَ كَذَٰلِكِ ٱللَّهُ يَخۡلُقُ مَا يَشَآءُۚ إِذَا قَضَىٰٓ أَمۡرٗا فَإِنَّمَا يَقُولُ لَهُۥ كُن فَيَكُونُ", "Qalat rabbi anna yakoonu lee waladun walam yamsasnee basharun qala kathaliki Allahu yakhluqu ma yashao itha qada amran fainnama yaqoolu lahu kun fayakoonu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 48, "And He will teach him writing and wisdom and the Torah and the Gospel", "وَيُعَلِّمُهُ ٱلۡكِتَٰبَ وَٱلۡحِكۡمَةَ وَٱلتَّوۡرَىٰةَ وَٱلۡإِنجِيلَ", "WayuAAallimuhu alkitaba waalhikmata waalttawrata waalinjeela");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 49, "And [make him] a messenger to the Children of Israel, [who will say], 'Indeed I have come to you with a sign from your Lord in that I design for you from clay [that which is] like the form of a bird, then I breathe into it and it becomes a bird by permission of Allah. And I cure the blind and the leper, and I give life to the dead - by permission of Allah. And I inform you of what you eat and what you store in your houses. Indeed in that is a sign for you, if you are believers", "وَرَسُولًا إِلَىٰ بَنِيٓ إِسۡرَـٰٓءِيلَ أَنِّي قَدۡ جِئۡتُكُم بِـَٔايَةٖ مِّن رَّبِّكُمۡ أَنِّيٓ أَخۡلُقُ لَكُم مِّنَ ٱلطِّينِ كَهَيۡـَٔةِ ٱلطَّيۡرِ فَأَنفُخُ فِيهِ فَيَكُونُ طَيۡرَۢا بِإِذۡنِ ٱللَّهِۖ وَأُبۡرِئُ ٱلۡأَكۡمَهَ وَٱلۡأَبۡرَصَ وَأُحۡيِ ٱلۡمَوۡتَىٰ بِإِذۡنِ ٱللَّهِۖ وَأُنَبِّئُكُم بِمَا تَأۡكُلُونَ وَمَا تَدَّخِرُونَ فِي بُيُوتِكُمۡۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لَّكُمۡ إِن كُنتُم مُّؤۡمِنِينَ", "Warasoolan ila banee israeela annee qad jitukum biayatin min rabbikum annee akhluqu lakum mina altteeni kahayati alttayri faanfukhu feehi fayakoonu tayran biithni Allahi waobrio alakmaha waalabrasa waohyee almawta biithni Allahi waonabbiokum bima takuloona wama taddakhiroona fee buyootikum inna fee thalika laayatan lakum in kuntum mumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 50, "And [I have come] confirming what was before me of the Torah and to make lawful for you some of what was forbidden to you. And I have come to you with a sign from your Lord, so fear Allah and obey me", "وَمُصَدِّقٗا لِّمَا بَيۡنَ يَدَيَّ مِنَ ٱلتَّوۡرَىٰةِ وَلِأُحِلَّ لَكُم بَعۡضَ ٱلَّذِي حُرِّمَ عَلَيۡكُمۡۚ وَجِئۡتُكُم بِـَٔايَةٖ مِّن رَّبِّكُمۡ فَٱتَّقُواْ ٱللَّهَ وَأَطِيعُونِ", "Wamusaddiqan lima bayna yadayya mina alttawrati waliohilla lakum baAAda allathee hurrima AAalaykum wajitukum biayatin min rabbikum faittaqoo Allaha waateeAAooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 51, "Indeed, Allah is my Lord and your Lord, so worship Him. That is the straight path", "إِنَّ ٱللَّهَ رَبِّي وَرَبُّكُمۡ فَٱعۡبُدُوهُۚ هَٰذَا صِرَٰطٞ مُّسۡتَقِيمٞ", "Inna Allaha rabbee warabbukum faoAAbudoohu hatha siratun mustaqeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 52, "But when Jesus felt [persistence in] disbelief from them, he said, ""Who are my supporters for [the cause of] Allah?"" The disciples said, ""We are supporters for Allah. We have believed in Allah and testify that we are Muslims [submitting to Him]", "۞فَلَمَّآ أَحَسَّ عِيسَىٰ مِنۡهُمُ ٱلۡكُفۡرَ قَالَ مَنۡ أَنصَارِيٓ إِلَى ٱللَّهِۖ قَالَ ٱلۡحَوَارِيُّونَ نَحۡنُ أَنصَارُ ٱللَّهِ ءَامَنَّا بِٱللَّهِ وَٱشۡهَدۡ بِأَنَّا مُسۡلِمُونَ", "Falamma ahassa AAeesa minhumu alkufra qala man ansaree ila Allahi qala alhawariyyoona nahnu ansaru Allahi amanna biAllahi waishhad bianna muslimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 53, "Our Lord, we have believed in what You revealed and have followed the messenger Jesus, so register us among the witnesses [to truth]", "رَبَّنَآ ءَامَنَّا بِمَآ أَنزَلۡتَ وَٱتَّبَعۡنَا ٱلرَّسُولَ فَٱكۡتُبۡنَا مَعَ ٱلشَّـٰهِدِينَ", "Rabbana amanna bima anzalta waittabaAAna alrrasoola faoktubna maAAa alshshahideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 54, "And the disbelievers planned, but Allah planned. And Allah is the best of planners", "وَمَكَرُواْ وَمَكَرَ ٱللَّهُۖ وَٱللَّهُ خَيۡرُ ٱلۡمَٰكِرِينَ", "Wamakaroo wamakara Allahu waAllahu khayru almakireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 55, "[Mention] when Allah said, ""O Jesus, indeed I will take you and raise you to Myself and purify you from those who disbelieve and make those who follow you [in submission to Allah alone] superior to those who disbelieve until the Day of Resurrection. Then to Me is your return, and I will judge between you concerning that in which you used to differ", "إِذۡ قَالَ ٱللَّهُ يَٰعِيسَىٰٓ إِنِّي مُتَوَفِّيكَ وَرَافِعُكَ إِلَيَّ وَمُطَهِّرُكَ مِنَ ٱلَّذِينَ كَفَرُواْ وَجَاعِلُ ٱلَّذِينَ ٱتَّبَعُوكَ فَوۡقَ ٱلَّذِينَ كَفَرُوٓاْ إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِۖ ثُمَّ إِلَيَّ مَرۡجِعُكُمۡ فَأَحۡكُمُ بَيۡنَكُمۡ فِيمَا كُنتُمۡ فِيهِ تَخۡتَلِفُونَ", "Ith qala Allahu ya AAeesa innee mutawaffeeka warafiAAuka ilayya wamutahhiruka mina allatheena kafaroo wajaAAilu allatheena ittabaAAooka fawqa allatheena kafaroo ila yawmi alqiyamati thumma ilayya marjiAAukum faahkumu baynakum feema kuntum feehi takhtalifoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 56, "And as for those who disbelieved, I will punish them with a severe punishment in this world and the Hereafter, and they will have no helpers", "فَأَمَّا ٱلَّذِينَ كَفَرُواْ فَأُعَذِّبُهُمۡ عَذَابٗا شَدِيدٗا فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِ وَمَا لَهُم مِّن نَّـٰصِرِينَ", "Faamma allatheena kafaroo faoAAaththibuhum AAathaban shadeedan fee alddunya waalakhirati wama lahum min nasireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 57, "But as for those who believed and did righteous deeds, He will give them in full their rewards, and Allah does not like the wrongdoers", "وَأَمَّا ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ فَيُوَفِّيهِمۡ أُجُورَهُمۡۗ وَٱللَّهُ لَا يُحِبُّ ٱلظَّـٰلِمِينَ", "Waamma allatheena amanoo waAAamiloo alssalihati fayuwaffeehim ojoorahum waAllahu la yuhibbu alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 58, "This is what We recite to you, [O Muhammad], of [Our] verses and the precise [and wise] message", "ذَٰلِكَ نَتۡلُوهُ عَلَيۡكَ مِنَ ٱلۡأٓيَٰتِ وَٱلذِّكۡرِ ٱلۡحَكِيمِ", "Thalika natloohu AAalayka mina alayati waalththikri alhakeemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 59, "Indeed, the example of Jesus to Allah is like that of Adam. He created Him from dust; then He said to him, ""Be,"" and he was", "إِنَّ مَثَلَ عِيسَىٰ عِندَ ٱللَّهِ كَمَثَلِ ءَادَمَۖ خَلَقَهُۥ مِن تُرَابٖ ثُمَّ قَالَ لَهُۥ كُن فَيَكُونُ", "Inna mathala AAeesa AAinda Allahi kamathali adama khalaqahu min turabin thumma qala lahu kun fayakoonu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 60, "The truth is from your Lord, so do not be among the doubters", "ٱلۡحَقُّ مِن رَّبِّكَ فَلَا تَكُن مِّنَ ٱلۡمُمۡتَرِينَ", "Alhaqqu min rabbika fala takun mina almumtareena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 61, "Then whoever argues with you about it after [this] knowledge has come to you - say, ""Come, let us call our sons and your sons, our women and your women, ourselves and yourselves, then supplicate earnestly [together] and invoke the curse of Allah upon the liars [among us]", "فَمَنۡ حَآجَّكَ فِيهِ مِنۢ بَعۡدِ مَا جَآءَكَ مِنَ ٱلۡعِلۡمِ فَقُلۡ تَعَالَوۡاْ نَدۡعُ أَبۡنَآءَنَا وَأَبۡنَآءَكُمۡ وَنِسَآءَنَا وَنِسَآءَكُمۡ وَأَنفُسَنَا وَأَنفُسَكُمۡ ثُمَّ نَبۡتَهِلۡ فَنَجۡعَل لَّعۡنَتَ ٱللَّهِ عَلَى ٱلۡكَٰذِبِينَ", "Faman hajjaka feehi min baAAdi ma jaaka mina alAAilmi faqul taAAalaw nadAAu abnaana waabnaakum wanisaana wanisaakum waanfusana waanfusakum thumma nabtahil fanajAAal laAAnata Allahi AAala alkathibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 62, "Indeed, this is the true narration. And there is no deity except Allah. And indeed, Allah is the Exalted in Might, the Wise", "إِنَّ هَٰذَا لَهُوَ ٱلۡقَصَصُ ٱلۡحَقُّۚ وَمَا مِنۡ إِلَٰهٍ إِلَّا ٱللَّهُۚ وَإِنَّ ٱللَّهَ لَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ", "Inna hatha lahuwa alqasasu alhaqqu wama min ilahin illa Allahu wainna Allaha lahuwa alAAazeezu alhakeemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 63, "But if they turn away, then indeed - Allah is Knowing of the corrupters", "فَإِن تَوَلَّوۡاْ فَإِنَّ ٱللَّهَ عَلِيمُۢ بِٱلۡمُفۡسِدِينَ", "Fain tawallaw fainna Allaha AAaleemun bialmufsideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 64, "Say, ""O People of the Scripture, come to a word that is equitable between us and you - that we will not worship except Allah and not associate anything with Him and not take one another as lords instead of Allah."" But if they turn away, then say, ""Bear witness that we are Muslims [submitting to Him]", "قُلۡ يَـٰٓأَهۡلَ ٱلۡكِتَٰبِ تَعَالَوۡاْ إِلَىٰ كَلِمَةٖ سَوَآءِۭ بَيۡنَنَا وَبَيۡنَكُمۡ أَلَّا نَعۡبُدَ إِلَّا ٱللَّهَ وَلَا نُشۡرِكَ بِهِۦ شَيۡـٔٗا وَلَا يَتَّخِذَ بَعۡضُنَا بَعۡضًا أَرۡبَابٗا مِّن دُونِ ٱللَّهِۚ فَإِن تَوَلَّوۡاْ فَقُولُواْ ٱشۡهَدُواْ بِأَنَّا مُسۡلِمُونَ", "Qul ya ahla alkitabi taAAalaw ila kalimatin sawain baynana wabaynakum alla naAAbuda illa Allaha wala nushrika bihi shayan wala yattakhitha baAAduna baAAdan arbaban min dooni Allahi fain tawallaw faqooloo ishhadoo bianna muslimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 65, "O People of the Scripture, why do you argue about Abraham while the Torah and the Gospel were not revealed until after him? Then will you not reason", "يَـٰٓأَهۡلَ ٱلۡكِتَٰبِ لِمَ تُحَآجُّونَ فِيٓ إِبۡرَٰهِيمَ وَمَآ أُنزِلَتِ ٱلتَّوۡرَىٰةُ وَٱلۡإِنجِيلُ إِلَّا مِنۢ بَعۡدِهِۦٓۚ أَفَلَا تَعۡقِلُونَ", "Ya ahla alkitabi lima tuhajjoona fee ibraheema wama onzilati alttawratu waalinjeelu illa min baAAdihi afala taAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 66, "Here you are - those who have argued about that of which you have [some] knowledge, but why do you argue about that of which you have no knowledge? And Allah knows, while you know not", "هَـٰٓأَنتُمۡ هَـٰٓؤُلَآءِ حَٰجَجۡتُمۡ فِيمَا لَكُم بِهِۦ عِلۡمٞ فَلِمَ تُحَآجُّونَ فِيمَا لَيۡسَ لَكُم بِهِۦ عِلۡمٞۚ وَٱللَّهُ يَعۡلَمُ وَأَنتُمۡ لَا تَعۡلَمُونَ", "Haantum haolai hajajtum feema lakum bihi AAilmun falima tuhajjoona feema laysa lakum bihi AAilmun waAllahu yaAAlamu waantum la taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 67, "Abraham was neither a Jew nor a Christian, but he was one inclining toward truth, a Muslim [submitting to Allah]. And he was not of the polytheists", "مَا كَانَ إِبۡرَٰهِيمُ يَهُودِيّٗا وَلَا نَصۡرَانِيّٗا وَلَٰكِن كَانَ حَنِيفٗا مُّسۡلِمٗا وَمَا كَانَ مِنَ ٱلۡمُشۡرِكِينَ", "Ma kana ibraheemu yahoodiyyan wala nasraniyyan walakin kana haneefan musliman wama kana mina almushrikeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 68, "Indeed, the most worthy of Abraham among the people are those who followed him [in submission to Allah] and this prophet, and those who believe [in his message]. And Allah is the ally of the believers", "إِنَّ أَوۡلَى ٱلنَّاسِ بِإِبۡرَٰهِيمَ لَلَّذِينَ ٱتَّبَعُوهُ وَهَٰذَا ٱلنَّبِيُّ وَٱلَّذِينَ ءَامَنُواْۗ وَٱللَّهُ وَلِيُّ ٱلۡمُؤۡمِنِينَ", "Inna awla alnnasi biibraheema lallatheena ittabaAAoohu wahatha alnnabiyyu waallatheena amanoo waAllahu waliyyu almumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 69, "A faction of the people of the Scripture wish they could mislead you. But they do not mislead except themselves, and they perceive [it] not", "وَدَّت طَّآئِفَةٞ مِّنۡ أَهۡلِ ٱلۡكِتَٰبِ لَوۡ يُضِلُّونَكُمۡ وَمَا يُضِلُّونَ إِلَّآ أَنفُسَهُمۡ وَمَا يَشۡعُرُونَ", "Waddat taifatun min ahli alkitabi law yudilloonakum wama yudilloona illa anfusahum wama yashAAuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 70, "O People of the Scripture, why do you disbelieve in the verses of Allah while you witness [to their truth]", "يَـٰٓأَهۡلَ ٱلۡكِتَٰبِ لِمَ تَكۡفُرُونَ بِـَٔايَٰتِ ٱللَّهِ وَأَنتُمۡ تَشۡهَدُونَ", "Ya ahla alkitabi lima takfuroona biayati Allahi waantum tashhadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 71, "O People of the Scripture, why do you confuse the truth with falsehood and conceal the truth while you know [it]", "يَـٰٓأَهۡلَ ٱلۡكِتَٰبِ لِمَ تَلۡبِسُونَ ٱلۡحَقَّ بِٱلۡبَٰطِلِ وَتَكۡتُمُونَ ٱلۡحَقَّ وَأَنتُمۡ تَعۡلَمُونَ", "Ya ahla alkitabi lima talbisoona alhaqqa bialbatili wataktumoona alhaqqa waantum taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 72, "And a faction of the People of the Scripture say [to each other], ""Believe in that which was revealed to the believers at the beginning of the day and reject it at its end that perhaps they will abandon their religion", "وَقَالَت طَّآئِفَةٞ مِّنۡ أَهۡلِ ٱلۡكِتَٰبِ ءَامِنُواْ بِٱلَّذِيٓ أُنزِلَ عَلَى ٱلَّذِينَ ءَامَنُواْ وَجۡهَ ٱلنَّهَارِ وَٱكۡفُرُوٓاْ ءَاخِرَهُۥ لَعَلَّهُمۡ يَرۡجِعُونَ", "Waqalat taifatun min ahli alkitabi aminoo biallathee onzila AAala allatheena amanoo wajha alnnahari waokfuroo akhirahu laAAallahum yarjiAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 73, "And do not trust except those who follow your religion."" Say, ""Indeed, the [true] guidance is the guidance of Allah. [Do you fear] lest someone be given [knowledge] like you were given or that they would [thereby] argue with you before your Lord?"" Say, ""Indeed, [all] bounty is in the hand of Allah - He grants it to whom He wills. And Allah is all-Encompassing and Wise", "وَلَا تُؤۡمِنُوٓاْ إِلَّا لِمَن تَبِعَ دِينَكُمۡ قُلۡ إِنَّ ٱلۡهُدَىٰ هُدَى ٱللَّهِ أَن يُؤۡتَىٰٓ أَحَدٞ مِّثۡلَ مَآ أُوتِيتُمۡ أَوۡ يُحَآجُّوكُمۡ عِندَ رَبِّكُمۡۗ قُلۡ إِنَّ ٱلۡفَضۡلَ بِيَدِ ٱللَّهِ يُؤۡتِيهِ مَن يَشَآءُۗ وَٱللَّهُ وَٰسِعٌ عَلِيمٞ", "Wala tuminoo illa liman tabiAAa deenakum qul inna alhuda huda Allahi an yuta ahadun mithla ma ooteetum aw yuhajjookum AAinda rabbikum qul inna alfadla biyadi Allahi yuteehi man yashao waAllahu wasiAAun AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 74, "He selects for His mercy whom He wills. And Allah is the possessor of great bounty", "يَخۡتَصُّ بِرَحۡمَتِهِۦ مَن يَشَآءُۗ وَٱللَّهُ ذُو ٱلۡفَضۡلِ ٱلۡعَظِيمِ", "Yakhtassu birahmatihi man yashao waAllahu thoo alfadli alAAatheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 75, "And among the People of the Scripture is he who, if you entrust him with a great amount [of wealth], he will return it to you. And among them is he who, if you entrust him with a [single] silver coin, he will not return it to you unless you are constantly standing over him [demanding it]. That is because they say, ""There is no blame upon us concerning the unlearned."" And they speak untruth about Allah while they know [it]", "۞وَمِنۡ أَهۡلِ ٱلۡكِتَٰبِ مَنۡ إِن تَأۡمَنۡهُ بِقِنطَارٖ يُؤَدِّهِۦٓ إِلَيۡكَ وَمِنۡهُم مَّنۡ إِن تَأۡمَنۡهُ بِدِينَارٖ لَّا يُؤَدِّهِۦٓ إِلَيۡكَ إِلَّا مَا دُمۡتَ عَلَيۡهِ قَآئِمٗاۗ ذَٰلِكَ بِأَنَّهُمۡ قَالُواْ لَيۡسَ عَلَيۡنَا فِي ٱلۡأُمِّيِّـۧنَ سَبِيلٞ وَيَقُولُونَ عَلَى ٱللَّهِ ٱلۡكَذِبَ وَهُمۡ يَعۡلَمُونَ", "Wamin ahli alkitabi man in tamanhu biqintarin yuaddihi ilayka waminhum man in tamanhu bideenarin la yuaddihi ilayka illa ma dumta AAalayhi qaiman thalika biannahum qaloo laysa AAalayna fee alommiyyeena sabeelun wayaqooloona AAala Allahi alkathiba wahum yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 76, "But yes, whoever fulfills his commitment and fears Allah - then indeed, Allah loves those who fear Him", "بَلَىٰۚ مَنۡ أَوۡفَىٰ بِعَهۡدِهِۦ وَٱتَّقَىٰ فَإِنَّ ٱللَّهَ يُحِبُّ ٱلۡمُتَّقِينَ", "Bala man awfa biAAahdihi waittaqa fainna Allaha yuhibbu almuttaqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 77, "Indeed, those who exchange the covenant of Allah and their [own] oaths for a small price will have no share in the Hereafter, and Allah will not speak to them or look at them on the Day of Resurrection, nor will He purify them; and they will have a painful punishment", "إِنَّ ٱلَّذِينَ يَشۡتَرُونَ بِعَهۡدِ ٱللَّهِ وَأَيۡمَٰنِهِمۡ ثَمَنٗا قَلِيلًا أُوْلَـٰٓئِكَ لَا خَلَٰقَ لَهُمۡ فِي ٱلۡأٓخِرَةِ وَلَا يُكَلِّمُهُمُ ٱللَّهُ وَلَا يَنظُرُ إِلَيۡهِمۡ يَوۡمَ ٱلۡقِيَٰمَةِ وَلَا يُزَكِّيهِمۡ وَلَهُمۡ عَذَابٌ أَلِيمٞ", "Inna allatheena yashtaroona biAAahdi Allahi waaymanihim thamanan qaleelan olaika la khalaqa lahum fee alakhirati wala yukallimuhumu Allahu wala yanthuru ilayhim yawma alqiyamati wala yuzakkeehim walahum AAathabun aleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 78, "And indeed, there is among them a party who alter the Scripture with their tongues so you may think it is from the Scripture, but it is not from the Scripture. And they say, ""This is from Allah,"" but it is not from Allah. And they speak untruth about Allah while they know", "وَإِنَّ مِنۡهُمۡ لَفَرِيقٗا يَلۡوُۥنَ أَلۡسِنَتَهُم بِٱلۡكِتَٰبِ لِتَحۡسَبُوهُ مِنَ ٱلۡكِتَٰبِ وَمَا هُوَ مِنَ ٱلۡكِتَٰبِ وَيَقُولُونَ هُوَ مِنۡ عِندِ ٱللَّهِ وَمَا هُوَ مِنۡ عِندِ ٱللَّهِۖ وَيَقُولُونَ عَلَى ٱللَّهِ ٱلۡكَذِبَ وَهُمۡ يَعۡلَمُونَ", "Wainna minhum lafareeqan yalwoona alsinatahum bialkitabi litahsaboohu mina alkitabi wama huwa mina alkitabi wayaqooloona huwa min AAindi Allahi wama huwa min AAindi Allahi wayaqooloona AAala Allahi alkathiba wahum yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 79, "It is not for a human [prophet] that Allah should give him the Scripture and authority and prophethood and then he would say to the people, ""Be servants to me rather than Allah,"" but [instead, he would say], ""Be pious scholars of the Lord because of what you have taught of the Scripture and because of what you have studied", "مَا كَانَ لِبَشَرٍ أَن يُؤۡتِيَهُ ٱللَّهُ ٱلۡكِتَٰبَ وَٱلۡحُكۡمَ وَٱلنُّبُوَّةَ ثُمَّ يَقُولَ لِلنَّاسِ كُونُواْ عِبَادٗا لِّي مِن دُونِ ٱللَّهِ وَلَٰكِن كُونُواْ رَبَّـٰنِيِّـۧنَ بِمَا كُنتُمۡ تُعَلِّمُونَ ٱلۡكِتَٰبَ وَبِمَا كُنتُمۡ تَدۡرُسُونَ", "Ma kana libasharin an yutiyahu Allahu alkitaba waalhukma waalnnubuwwata thumma yaqoola lilnnasi koonoo AAibadan lee min dooni Allahi walakin koonoo rabbaniyyeena bima kuntum tuAAallimoona alkitaba wabima kuntum tadrusoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 80, "Nor could he order you to take the angels and prophets as lords. Would he order you to disbelief after you had been Muslims", "وَلَا يَأۡمُرَكُمۡ أَن تَتَّخِذُواْ ٱلۡمَلَـٰٓئِكَةَ وَٱلنَّبِيِّـۧنَ أَرۡبَابًاۚ أَيَأۡمُرُكُم بِٱلۡكُفۡرِ بَعۡدَ إِذۡ أَنتُم مُّسۡلِمُونَ", "Wala yamurakum an tattakhithoo almalaikata waalnnabiyyeena arbaban ayamurukum bialkufri baAAda ith antum muslimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 81, "And [recall, O People of the Scripture], when Allah took the covenant of the prophets, [saying], ""Whatever I give you of the Scripture and wisdom and then there comes to you a messenger confirming what is with you, you [must] believe in him and support him."" [Allah] said, ""Have you acknowledged and taken upon that My commitment?"" They said, ""We have acknowledged it."" He said, ""Then bear witness, and I am with you among the witnesses", "وَإِذۡ أَخَذَ ٱللَّهُ مِيثَٰقَ ٱلنَّبِيِّـۧنَ لَمَآ ءَاتَيۡتُكُم مِّن كِتَٰبٖ وَحِكۡمَةٖ ثُمَّ جَآءَكُمۡ رَسُولٞ مُّصَدِّقٞ لِّمَا مَعَكُمۡ لَتُؤۡمِنُنَّ بِهِۦ وَلَتَنصُرُنَّهُۥۚ قَالَ ءَأَقۡرَرۡتُمۡ وَأَخَذۡتُمۡ عَلَىٰ ذَٰلِكُمۡ إِصۡرِيۖ قَالُوٓاْ أَقۡرَرۡنَاۚ قَالَ فَٱشۡهَدُواْ وَأَنَا۠ مَعَكُم مِّنَ ٱلشَّـٰهِدِينَ", "Waith akhatha Allahu meethaqa alnnabiyyeena lama ataytukum min kitabin wahikmatin thumma jaakum rasoolun musaddiqun lima maAAakum latuminunna bihi walatansurunnahu qala aaqrartum waakhathtum AAala thalikum isree qaloo aqrarna qala faishhadoo waana maAAakum mina alshshahideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 82, "And whoever turned away after that - they were the defiantly disobedient", "فَمَن تَوَلَّىٰ بَعۡدَ ذَٰلِكَ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡفَٰسِقُونَ", "Faman tawalla baAAda thalika faolaika humu alfasiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 83, "So is it other than the religion of Allah they desire, while to Him have submitted [all] those within the heavens and earth, willingly or by compulsion, and to Him they will be returned", "أَفَغَيۡرَ دِينِ ٱللَّهِ يَبۡغُونَ وَلَهُۥٓ أَسۡلَمَ مَن فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ طَوۡعٗا وَكَرۡهٗا وَإِلَيۡهِ يُرۡجَعُونَ", "Afaghayra deeni Allahi yabghoona walahu aslama man fee alssamawati waalardi tawAAan wakarhan wailayhi yurjaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 84, "Say, ""We have believed in Allah and in what was revealed to us and what was revealed to Abraham, Ishmael, Isaac, Jacob, and the Descendants, and in what was given to Moses and Jesus and to the prophets from their Lord. We make no distinction between any of them, and we are Muslims [submitting] to Him", "قُلۡ ءَامَنَّا بِٱللَّهِ وَمَآ أُنزِلَ عَلَيۡنَا وَمَآ أُنزِلَ عَلَىٰٓ إِبۡرَٰهِيمَ وَإِسۡمَٰعِيلَ وَإِسۡحَٰقَ وَيَعۡقُوبَ وَٱلۡأَسۡبَاطِ وَمَآ أُوتِيَ مُوسَىٰ وَعِيسَىٰ وَٱلنَّبِيُّونَ مِن رَّبِّهِمۡ لَا نُفَرِّقُ بَيۡنَ أَحَدٖ مِّنۡهُمۡ وَنَحۡنُ لَهُۥ مُسۡلِمُونَ", "Qul amanna biAllahi wama onzila AAalayna wama onzila AAala ibraheema waismaAAeela waishaqa wayaAAqooba waalasbati wama ootiya moosa waAAeesa waalnnabiyyoona min rabbihim la nufarriqu bayna ahadin minhum wanahnu lahu muslimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 85, "And whoever desires other than Islam as religion - never will it be accepted from him, and he, in the Hereafter, will be among the losers", "وَمَن يَبۡتَغِ غَيۡرَ ٱلۡإِسۡلَٰمِ دِينٗا فَلَن يُقۡبَلَ مِنۡهُ وَهُوَ فِي ٱلۡأٓخِرَةِ مِنَ ٱلۡخَٰسِرِينَ", "Waman yabtaghi ghayra alislami deenan falan yuqbala minhu wahuwa fee alakhirati mina alkhasireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 86, "How shall Allah guide a people who disbelieved after their belief and had witnessed that the Messenger is true and clear signs had come to them? And Allah does not guide the wrongdoing people", "كَيۡفَ يَهۡدِي ٱللَّهُ قَوۡمٗا كَفَرُواْ بَعۡدَ إِيمَٰنِهِمۡ وَشَهِدُوٓاْ أَنَّ ٱلرَّسُولَ حَقّٞ وَجَآءَهُمُ ٱلۡبَيِّنَٰتُۚ وَٱللَّهُ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلظَّـٰلِمِينَ", "Kayfa yahdee Allahu qawman kafaroo baAAda eemanihim washahidoo anna alrrasoola haqqun wajaahumu albayyinatu waAllahu la yahdee alqawma alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 87, "Those - their recompense will be that upon them is the curse of Allah and the angels and the people, all together", "أُوْلَـٰٓئِكَ جَزَآؤُهُمۡ أَنَّ عَلَيۡهِمۡ لَعۡنَةَ ٱللَّهِ وَٱلۡمَلَـٰٓئِكَةِ وَٱلنَّاسِ أَجۡمَعِينَ", "Olaika jazaohum anna AAalayhim laAAnata Allahi waalmalaikati waalnnasi ajmaAAeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 88, "Abiding eternally therein. The punishment will not be lightened for them, nor will they be reprieved", "خَٰلِدِينَ فِيهَا لَا يُخَفَّفُ عَنۡهُمُ ٱلۡعَذَابُ وَلَا هُمۡ يُنظَرُونَ", "Khalideena feeha la yukhaffafu AAanhumu alAAathabu wala hum yuntharoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 89, "Except for those who repent after that and correct themselves. For indeed, Allah is Forgiving and Merciful", "إِلَّا ٱلَّذِينَ تَابُواْ مِنۢ بَعۡدِ ذَٰلِكَ وَأَصۡلَحُواْ فَإِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٌ", "Illa allatheena taboo min baAAdi thalika waaslahoo fainna Allaha ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 90, "Indeed, those who reject the message after their belief and then increase in disbelief - never will their [claimed] repentance be accepted, and they are the ones astray", "إِنَّ ٱلَّذِينَ كَفَرُواْ بَعۡدَ إِيمَٰنِهِمۡ ثُمَّ ٱزۡدَادُواْ كُفۡرٗا لَّن تُقۡبَلَ تَوۡبَتُهُمۡ وَأُوْلَـٰٓئِكَ هُمُ ٱلضَّآلُّونَ", "Inna allatheena kafaroo baAAda eemanihim thumma izdadoo kufran lan tuqbala tawbatuhum waolaika humu alddalloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 91, "Indeed, those who disbelieve and die while they are disbelievers - never would the [whole] capacity of the earth in gold be accepted from one of them if he would [seek to] ransom himself with it. For those there will be a painful punishment, and they will have no helpers", "إِنَّ ٱلَّذِينَ كَفَرُواْ وَمَاتُواْ وَهُمۡ كُفَّارٞ فَلَن يُقۡبَلَ مِنۡ أَحَدِهِم مِّلۡءُ ٱلۡأَرۡضِ ذَهَبٗا وَلَوِ ٱفۡتَدَىٰ بِهِۦٓۗ أُوْلَـٰٓئِكَ لَهُمۡ عَذَابٌ أَلِيمٞ وَمَا لَهُم مِّن نَّـٰصِرِينَ", "Inna allatheena kafaroo wamatoo wahum kuffarun falan yuqbala min ahadihim milo alardi thahaban walawi iftada bihi olaika lahum AAathabun aleemun wama lahum min nasireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 92, "Never will you attain the good [reward] until you spend [in the way of Allah] from that which you love. And whatever you spend - indeed, Allah is Knowing of it", "لَن تَنَالُواْ ٱلۡبِرَّ حَتَّىٰ تُنفِقُواْ مِمَّا تُحِبُّونَۚ وَمَا تُنفِقُواْ مِن شَيۡءٖ فَإِنَّ ٱللَّهَ بِهِۦ عَلِيمٞ", "Lan tanaloo albirra hatta tunfiqoo mimma tuhibboona wama tunfiqoo min shayin fainna Allaha bihi AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 93, "All food was lawful to the Children of Israel except what Israel had made unlawful to himself before the Torah was revealed. Say, [O Muhammad], ""So bring the Torah and recite it, if you should be truthful", "۞كُلُّ ٱلطَّعَامِ كَانَ حِلّٗا لِّبَنِيٓ إِسۡرَـٰٓءِيلَ إِلَّا مَا حَرَّمَ إِسۡرَـٰٓءِيلُ عَلَىٰ نَفۡسِهِۦ مِن قَبۡلِ أَن تُنَزَّلَ ٱلتَّوۡرَىٰةُۚ قُلۡ فَأۡتُواْ بِٱلتَّوۡرَىٰةِ فَٱتۡلُوهَآ إِن كُنتُمۡ صَٰدِقِينَ", "Kullu alttaAAami kana hillan libanee israeela illa ma harrama israeelu AAala nafsihi min qabli an tunazzala alttawratu qul fatoo bialttawrati faotlooha in kuntum sadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 94, "And whoever invents about Allah untruth after that - then those are [truly] the wrongdoers", "فَمَنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ ٱلۡكَذِبَ مِنۢ بَعۡدِ ذَٰلِكَ فَأُوْلَـٰٓئِكَ هُمُ ٱلظَّـٰلِمُونَ", "Famani iftara AAala Allahi alkathiba min baAAdi thalika faolaika humu althalimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 95, "Say, ""Allah has told the truth. So follow the religion of Abraham, inclining toward truth; and he was not of the polytheists", "قُلۡ صَدَقَ ٱللَّهُۗ فَٱتَّبِعُواْ مِلَّةَ إِبۡرَٰهِيمَ حَنِيفٗاۖ وَمَا كَانَ مِنَ ٱلۡمُشۡرِكِينَ", "Qul sadaqa Allahu faittabiAAoo millata ibraheema haneefan wama kana mina almushrikeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 96, "Indeed, the first House [of worship] established for mankind was that at Makkah - blessed and a guidance for the worlds", "إِنَّ أَوَّلَ بَيۡتٖ وُضِعَ لِلنَّاسِ لَلَّذِي بِبَكَّةَ مُبَارَكٗا وَهُدٗى لِّلۡعَٰلَمِينَ", "Inna awwala baytin wudiAAa lilnnasi lallathee bibakkata mubarakan wahudan lilAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 97, "In it are clear signs [such as] the standing place of Abraham. And whoever enters it shall be safe. And [due] to Allah from the people is a pilgrimage to the House - for whoever is able to find thereto a way. But whoever disbelieves - then indeed, Allah is free from need of the worlds", "فِيهِ ءَايَٰتُۢ بَيِّنَٰتٞ مَّقَامُ إِبۡرَٰهِيمَۖ وَمَن دَخَلَهُۥ كَانَ ءَامِنٗاۗ وَلِلَّهِ عَلَى ٱلنَّاسِ حِجُّ ٱلۡبَيۡتِ مَنِ ٱسۡتَطَاعَ إِلَيۡهِ سَبِيلٗاۚ وَمَن كَفَرَ فَإِنَّ ٱللَّهَ غَنِيٌّ عَنِ ٱلۡعَٰلَمِينَ", "Feehi ayatun bayyinatun maqamu ibraheema waman dakhalahu kana aminan walillahi AAala alnnasi hijju albayti mani istataAAa ilayhi sabeelan waman kafara fainna Allaha ghaniyyun AAani alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 98, "Say, ""O People of the Scripture, why do you disbelieve in the verses of Allah while Allah is Witness over what you do", "قُلۡ يَـٰٓأَهۡلَ ٱلۡكِتَٰبِ لِمَ تَكۡفُرُونَ بِـَٔايَٰتِ ٱللَّهِ وَٱللَّهُ شَهِيدٌ عَلَىٰ مَا تَعۡمَلُونَ", "Qul ya ahla alkitabi lima takfuroona biayati Allahi waAllahu shaheedun AAala ma taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 99, "Say, ""O People of the Scripture, why do you avert from the way of Allah those who believe, seeking to make it [seem] deviant, while you are witnesses [to the truth]? And Allah is not unaware of what you do", "قُلۡ يَـٰٓأَهۡلَ ٱلۡكِتَٰبِ لِمَ تَصُدُّونَ عَن سَبِيلِ ٱللَّهِ مَنۡ ءَامَنَ تَبۡغُونَهَا عِوَجٗا وَأَنتُمۡ شُهَدَآءُۗ وَمَا ٱللَّهُ بِغَٰفِلٍ عَمَّا تَعۡمَلُونَ", "Qul ya ahla alkitabi lima tasuddoona AAan sabeeli Allahi man amana tabghoonaha AAiwajan waantum shuhadao wama Allahu bighafilin AAamma taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 100, "O you who have believed, if you obey a party of those who were given the Scripture, they would turn you back, after your belief, [to being] unbelievers", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِن تُطِيعُواْ فَرِيقٗا مِّنَ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ يَرُدُّوكُم بَعۡدَ إِيمَٰنِكُمۡ كَٰفِرِينَ", "Ya ayyuha allatheena amanoo in tuteeAAoo fareeqan mina allatheena ootoo alkitaba yaruddookum baAAda eemanikum kafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 101, "And how could you disbelieve while to you are being recited the verses of Allah and among you is His Messenger? And whoever holds firmly to Allah has [indeed] been guided to a straight path", "وَكَيۡفَ تَكۡفُرُونَ وَأَنتُمۡ تُتۡلَىٰ عَلَيۡكُمۡ ءَايَٰتُ ٱللَّهِ وَفِيكُمۡ رَسُولُهُۥۗ وَمَن يَعۡتَصِم بِٱللَّهِ فَقَدۡ هُدِيَ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ", "Wakayfa takfuroona waantum tutla AAalaykum ayatu Allahi wafeekum rasooluhu waman yaAAtasim biAllahi faqad hudiya ila siratin mustaqeemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 102, "O you who have believed, fear Allah as He should be feared and do not die except as Muslims [in submission to Him]", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱتَّقُواْ ٱللَّهَ حَقَّ تُقَاتِهِۦ وَلَا تَمُوتُنَّ إِلَّا وَأَنتُم مُّسۡلِمُونَ", "Ya ayyuha allatheena amanoo ittaqoo Allaha haqqa tuqatihi wala tamootunna illa waantum muslimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 103, "And hold firmly to the rope of Allah all together and do not become divided. And remember the favor of Allah upon you - when you were enemies and He brought your hearts together and you became, by His favor, brothers. And you were on the edge of a pit of the Fire, and He saved you from it. Thus does Allah make clear to you His verses that you may be guided", "وَٱعۡتَصِمُواْ بِحَبۡلِ ٱللَّهِ جَمِيعٗا وَلَا تَفَرَّقُواْۚ وَٱذۡكُرُواْ نِعۡمَتَ ٱللَّهِ عَلَيۡكُمۡ إِذۡ كُنتُمۡ أَعۡدَآءٗ فَأَلَّفَ بَيۡنَ قُلُوبِكُمۡ فَأَصۡبَحۡتُم بِنِعۡمَتِهِۦٓ إِخۡوَٰنٗا وَكُنتُمۡ عَلَىٰ شَفَا حُفۡرَةٖ مِّنَ ٱلنَّارِ فَأَنقَذَكُم مِّنۡهَاۗ كَذَٰلِكَ يُبَيِّنُ ٱللَّهُ لَكُمۡ ءَايَٰتِهِۦ لَعَلَّكُمۡ تَهۡتَدُونَ", "WaiAAtasimoo bihabli Allahi jameeAAan wala tafarraqoo waothkuroo niAAmata Allahi AAalaykum ith kuntum aAAdaan faallafa bayna quloobikum faasbahtum biniAAmatihi ikhwanan wakuntum AAala shafa hufratin mina alnnari faanqathakum minha kathalika yubayyinu Allahu lakum ayatihi laAAallakum tahtadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 104, "And let there be [arising] from you a nation inviting to [all that is] good, enjoining what is right and forbidding what is wrong, and those will be the successful", "وَلۡتَكُن مِّنكُمۡ أُمَّةٞ يَدۡعُونَ إِلَى ٱلۡخَيۡرِ وَيَأۡمُرُونَ بِٱلۡمَعۡرُوفِ وَيَنۡهَوۡنَ عَنِ ٱلۡمُنكَرِۚ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُفۡلِحُونَ", "Waltakun minkum ommatun yadAAoona ila alkhayri wayamuroona bialmaAAroofi wayanhawna AAani almunkari waolaika humu almuflihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 105, "And do not be like the ones who became divided and differed after the clear proofs had come to them. And those will have a great punishment", "وَلَا تَكُونُواْ كَٱلَّذِينَ تَفَرَّقُواْ وَٱخۡتَلَفُواْ مِنۢ بَعۡدِ مَا جَآءَهُمُ ٱلۡبَيِّنَٰتُۚ وَأُوْلَـٰٓئِكَ لَهُمۡ عَذَابٌ عَظِيمٞ", "Wala takoonoo kaallatheena tafarraqoo waikhtalafoo min baAAdi ma jaahumu albayyinatu waolaika lahum AAathabun AAatheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 106, "On the Day [some] faces will turn white and [some] faces will turn black. As for those whose faces turn black, [to them it will be said], ""Did you disbelieve after your belief? Then taste the punishment for what you used to reject", "يَوۡمَ تَبۡيَضُّ وُجُوهٞ وَتَسۡوَدُّ وُجُوهٞۚ فَأَمَّا ٱلَّذِينَ ٱسۡوَدَّتۡ وُجُوهُهُمۡ أَكَفَرۡتُم بَعۡدَ إِيمَٰنِكُمۡ فَذُوقُواْ ٱلۡعَذَابَ بِمَا كُنتُمۡ تَكۡفُرُونَ", "Yawma tabyaddu wujoohun wataswaddu wujoohun faamma allatheena iswaddat wujoohuhum akafartum baAAda eemanikum fathooqoo alAAathaba bima kuntum takfuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 107, "But as for those whose faces will turn white, [they will be] within the mercy of Allah. They will abide therein eternally", "وَأَمَّا ٱلَّذِينَ ٱبۡيَضَّتۡ وُجُوهُهُمۡ فَفِي رَحۡمَةِ ٱللَّهِۖ هُمۡ فِيهَا خَٰلِدُونَ", "Waamma allatheena ibyaddat wujoohuhum fafee rahmati Allahi hum feeha khalidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 108, "These are the verses of Allah. We recite them to you, [O Muhammad], in truth; and Allah wants no injustice to the worlds", "تِلۡكَ ءَايَٰتُ ٱللَّهِ نَتۡلُوهَا عَلَيۡكَ بِٱلۡحَقِّۗ وَمَا ٱللَّهُ يُرِيدُ ظُلۡمٗا لِّلۡعَٰلَمِينَ", "Tilka ayatu Allahi natlooha AAalayka bialhaqqi wama Allahu yureedu thulman lilAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 109, "To Allah belongs whatever is in the heavens and whatever is on the earth. And to Allah will [all] matters be returned", "وَلِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۚ وَإِلَى ٱللَّهِ تُرۡجَعُ ٱلۡأُمُورُ", "Walillahi ma fee alssamawati wama fee alardi waila Allahi turjaAAu alomooru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 110, "You are the best nation produced [as an example] for mankind. You enjoin what is right and forbid what is wrong and believe in Allah. If only the People of the Scripture had believed, it would have been better for them. Among them are believers, but most of them are defiantly disobedient", "كُنتُمۡ خَيۡرَ أُمَّةٍ أُخۡرِجَتۡ لِلنَّاسِ تَأۡمُرُونَ بِٱلۡمَعۡرُوفِ وَتَنۡهَوۡنَ عَنِ ٱلۡمُنكَرِ وَتُؤۡمِنُونَ بِٱللَّهِۗ وَلَوۡ ءَامَنَ أَهۡلُ ٱلۡكِتَٰبِ لَكَانَ خَيۡرٗا لَّهُمۚ مِّنۡهُمُ ٱلۡمُؤۡمِنُونَ وَأَكۡثَرُهُمُ ٱلۡفَٰسِقُونَ", "Kuntum khayra ommatin okhrijat lilnnasi tamuroona bialmaAAroofi watanhawna AAani almunkari watuminoona biAllahi walaw amana ahlu alkitabi lakana khayran lahum minhumu almuminoona waaktharuhumu alfasiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 111, "They will not harm you except for [some] annoyance. And if they fight you, they will show you their backs; then they will not be aided", "لَن يَضُرُّوكُمۡ إِلَّآ أَذٗىۖ وَإِن يُقَٰتِلُوكُمۡ يُوَلُّوكُمُ ٱلۡأَدۡبَارَ ثُمَّ لَا يُنصَرُونَ", "Lan yadurrookum illa athan wain yuqatilookum yuwallookumu aladbara thumma la yunsaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 112, "They have been put under humiliation [by Allah] wherever they are overtaken, except for a covenant from Allah and a rope from the Muslims. And they have drawn upon themselves anger from Allah and have been put under destitution. That is because they disbelieved in the verses of Allah and killed the prophets without right. That is because they disobeyed and [habitually] transgressed", "ضُرِبَتۡ عَلَيۡهِمُ ٱلذِّلَّةُ أَيۡنَ مَا ثُقِفُوٓاْ إِلَّا بِحَبۡلٖ مِّنَ ٱللَّهِ وَحَبۡلٖ مِّنَ ٱلنَّاسِ وَبَآءُو بِغَضَبٖ مِّنَ ٱللَّهِ وَضُرِبَتۡ عَلَيۡهِمُ ٱلۡمَسۡكَنَةُۚ ذَٰلِكَ بِأَنَّهُمۡ كَانُواْ يَكۡفُرُونَ بِـَٔايَٰتِ ٱللَّهِ وَيَقۡتُلُونَ ٱلۡأَنۢبِيَآءَ بِغَيۡرِ حَقّٖۚ ذَٰلِكَ بِمَا عَصَواْ وَّكَانُواْ يَعۡتَدُونَ", "Duribat AAalayhimu alththillatu ayna ma thuqifoo illa bihablin mina Allahi wahablin mina alnnasi wabaoo bighadabin mina Allahi waduribat AAalayhimu almaskanatu thalika biannahum kanoo yakfuroona biayati Allahi wayaqtuloona alanbiyaa bighayri haqqin thalika bima AAasaw wakanoo yaAAtadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 113, "They are not [all] the same; among the People of the Scripture is a community standing [in obedience], reciting the verses of Allah during periods of the night and prostrating [in prayer]", "۞لَيۡسُواْ سَوَآءٗۗ مِّنۡ أَهۡلِ ٱلۡكِتَٰبِ أُمَّةٞ قَآئِمَةٞ يَتۡلُونَ ءَايَٰتِ ٱللَّهِ ءَانَآءَ ٱلَّيۡلِ وَهُمۡ يَسۡجُدُونَ", "Laysoo sawaan min ahli alkitabi ommatun qaimatun yatloona ayati Allahi anaa allayli wahum yasjudoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 114, "They believe in Allah and the Last Day, and they enjoin what is right and forbid what is wrong and hasten to good deeds. And those are among the righteous", "يُؤۡمِنُونَ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ وَيَأۡمُرُونَ بِٱلۡمَعۡرُوفِ وَيَنۡهَوۡنَ عَنِ ٱلۡمُنكَرِ وَيُسَٰرِعُونَ فِي ٱلۡخَيۡرَٰتِۖ وَأُوْلَـٰٓئِكَ مِنَ ٱلصَّـٰلِحِينَ", "Yuminoona biAllahi waalyawmi alakhiri wayamuroona bialmaAAroofi wayanhawna AAani almunkari wayusariAAoona fee alkhayrati waolaika mina alssaliheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 115, "And whatever good they do - never will it be removed from them. And Allah is Knowing of the righteous", "وَمَا يَفۡعَلُواْ مِنۡ خَيۡرٖ فَلَن يُكۡفَرُوهُۗ وَٱللَّهُ عَلِيمُۢ بِٱلۡمُتَّقِينَ", "Wama yafAAaloo min khayrin falan yukfaroohu waAllahu AAaleemun bialmuttaqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 116, "Indeed, those who disbelieve - never will their wealth or their children avail them against Allah at all, and those are the companions of the Fire; they will abide therein eternally", "إِنَّ ٱلَّذِينَ كَفَرُواْ لَن تُغۡنِيَ عَنۡهُمۡ أَمۡوَٰلُهُمۡ وَلَآ أَوۡلَٰدُهُم مِّنَ ٱللَّهِ شَيۡـٔٗاۖ وَأُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلنَّارِۖ هُمۡ فِيهَا خَٰلِدُونَ", "Inna allatheena kafaroo lan tughniya AAanhum amwaluhum wala awladuhum mina Allahi shayan waolaika ashabu alnnari hum feeha khalidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 117, "The example of what they spend in this worldly life is like that of a wind containing frost which strikes the harvest of a people who have wronged themselves and destroys it. And Allah has not wronged them, but they wrong themselves", "مَثَلُ مَا يُنفِقُونَ فِي هَٰذِهِ ٱلۡحَيَوٰةِ ٱلدُّنۡيَا كَمَثَلِ رِيحٖ فِيهَا صِرٌّ أَصَابَتۡ حَرۡثَ قَوۡمٖ ظَلَمُوٓاْ أَنفُسَهُمۡ فَأَهۡلَكَتۡهُۚ وَمَا ظَلَمَهُمُ ٱللَّهُ وَلَٰكِنۡ أَنفُسَهُمۡ يَظۡلِمُونَ", "Mathalu ma yunfiqoona fee hathihi alhayati alddunya kamathali reehin feeha sirrun asabat hartha qawmin thalamoo anfusahum faahlakathu wama thalamahumu Allahu walakin anfusahum yathlimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 118, "O you who have believed, do not take as intimates those other than yourselves, for they will not spare you [any] ruin. They wish you would have hardship. Hatred has already appeared from their mouths, and what their breasts conceal is greater. We have certainly made clear to you the signs, if you will use reason", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَتَّخِذُواْ بِطَانَةٗ مِّن دُونِكُمۡ لَا يَأۡلُونَكُمۡ خَبَالٗا وَدُّواْ مَا عَنِتُّمۡ قَدۡ بَدَتِ ٱلۡبَغۡضَآءُ مِنۡ أَفۡوَٰهِهِمۡ وَمَا تُخۡفِي صُدُورُهُمۡ أَكۡبَرُۚ قَدۡ بَيَّنَّا لَكُمُ ٱلۡأٓيَٰتِۖ إِن كُنتُمۡ تَعۡقِلُونَ", "Ya ayyuha allatheena amanoo la tattakhithoo bitanatan min doonikum la yaloonakum khabalan waddoo ma AAanittum qad badati albaghdao min afwahihim wama tukhfee sudooruhum akbaru qad bayyanna lakumu alayati in kuntum taAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 119, "Here you are loving them but they are not loving you, while you believe in the Scripture - all of it. And when they meet you, they say, ""We believe."" But when they are alone, they bite their fingertips at you in rage. Say, ""Die in your rage. Indeed, Allah is Knowing of that within the breasts", "هَـٰٓأَنتُمۡ أُوْلَآءِ تُحِبُّونَهُمۡ وَلَا يُحِبُّونَكُمۡ وَتُؤۡمِنُونَ بِٱلۡكِتَٰبِ كُلِّهِۦ وَإِذَا لَقُوكُمۡ قَالُوٓاْ ءَامَنَّا وَإِذَا خَلَوۡاْ عَضُّواْ عَلَيۡكُمُ ٱلۡأَنَامِلَ مِنَ ٱلۡغَيۡظِۚ قُلۡ مُوتُواْ بِغَيۡظِكُمۡۗ إِنَّ ٱللَّهَ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ", "Haantum olai tuhibboonahum wala yuhibboonakum watuminoona bialkitabi kullihi waitha laqookum qaloo amanna waitha khalaw AAaddoo AAalaykumu alanamila mina alghaythi qul mootoo bighaythikum inna Allaha AAaleemun bithati alssudoori");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 120, "If good touches you, it distresses them; but if harm strikes you, they rejoice at it. And if you are patient and fear Allah, their plot will not harm you at all. Indeed, Allah is encompassing of what they do", "إِن تَمۡسَسۡكُمۡ حَسَنَةٞ تَسُؤۡهُمۡ وَإِن تُصِبۡكُمۡ سَيِّئَةٞ يَفۡرَحُواْ بِهَاۖ وَإِن تَصۡبِرُواْ وَتَتَّقُواْ لَا يَضُرُّكُمۡ كَيۡدُهُمۡ شَيۡـًٔاۗ إِنَّ ٱللَّهَ بِمَا يَعۡمَلُونَ مُحِيطٞ", "In tamsaskum hasanatun tasuhum wain tusibkum sayyiatun yafrahoo biha wain tasbiroo watattaqoo la yadurrukum kayduhum shayan inna Allaha bima yaAAmaloona muheetun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 121, "And [remember] when you, [O Muhammad], left your family in the morning to post the believers at their stations for the battle [of Uhud] - and Allah is Hearing and Knowing", "وَإِذۡ غَدَوۡتَ مِنۡ أَهۡلِكَ تُبَوِّئُ ٱلۡمُؤۡمِنِينَ مَقَٰعِدَ لِلۡقِتَالِۗ وَٱللَّهُ سَمِيعٌ عَلِيمٌ", "Waith ghadawta min ahlika tubawwio almumineena maqaAAida lilqitali waAllahu sameeAAun AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 122, "When two parties among you were about to lose courage, but Allah was their ally; and upon Allah the believers should rely", "إِذۡ هَمَّت طَّآئِفَتَانِ مِنكُمۡ أَن تَفۡشَلَا وَٱللَّهُ وَلِيُّهُمَاۗ وَعَلَى ٱللَّهِ فَلۡيَتَوَكَّلِ ٱلۡمُؤۡمِنُونَ", "Ith hammat taifatani minkum an tafshala waAllahu waliyyuhuma waAAala Allahi falyatawakkali almuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 123, "And already had Allah given you victory at [the battle of] Badr while you were few in number. Then fear Allah; perhaps you will be grateful", "وَلَقَدۡ نَصَرَكُمُ ٱللَّهُ بِبَدۡرٖ وَأَنتُمۡ أَذِلَّةٞۖ فَٱتَّقُواْ ٱللَّهَ لَعَلَّكُمۡ تَشۡكُرُونَ", "Walaqad nasarakumu Allahu bibadrin waantum athillatun faittaqoo Allaha laAAallakum tashkuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 124, "[Remember] when you said to the believers, ""Is it not sufficient for you that your Lord should reinforce you with three thousand angels sent down", "إِذۡ تَقُولُ لِلۡمُؤۡمِنِينَ أَلَن يَكۡفِيَكُمۡ أَن يُمِدَّكُمۡ رَبُّكُم بِثَلَٰثَةِ ءَالَٰفٖ مِّنَ ٱلۡمَلَـٰٓئِكَةِ مُنزَلِينَ", "Ith taqoolu lilmumineena alan yakfiyakum an yumiddakum rabbukum bithalathati alafin mina almalaikati munzaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 125, "Yes, if you remain patient and conscious of Allah and the enemy come upon you [attacking] in rage, your Lord will reinforce you with five thousand angels having marks [of distinction]", "بَلَىٰٓۚ إِن تَصۡبِرُواْ وَتَتَّقُواْ وَيَأۡتُوكُم مِّن فَوۡرِهِمۡ هَٰذَا يُمۡدِدۡكُمۡ رَبُّكُم بِخَمۡسَةِ ءَالَٰفٖ مِّنَ ٱلۡمَلَـٰٓئِكَةِ مُسَوِّمِينَ", "Bala in tasbiroo watattaqoo wayatookum min fawrihim hatha yumdidkum rabbukum bikhamsati alafin mina almalaikati musawwimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 126, "And Allah made it not except as [a sign of] good tidings for you and to reassure your hearts thereby. And victory is not except from Allah, the Exalted in Might, the Wise", "وَمَا جَعَلَهُ ٱللَّهُ إِلَّا بُشۡرَىٰ لَكُمۡ وَلِتَطۡمَئِنَّ قُلُوبُكُم بِهِۦۗ وَمَا ٱلنَّصۡرُ إِلَّا مِنۡ عِندِ ٱللَّهِ ٱلۡعَزِيزِ ٱلۡحَكِيمِ", "Wama jaAAalahu Allahu illa bushra lakum walitatmainna quloobukum bihi wama alnnasru illa min AAindi Allahi alAAazeezi alhakeemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 127, "That He might cut down a section of the disbelievers or suppress them so that they turn back disappointed", "لِيَقۡطَعَ طَرَفٗا مِّنَ ٱلَّذِينَ كَفَرُوٓاْ أَوۡ يَكۡبِتَهُمۡ فَيَنقَلِبُواْ خَآئِبِينَ", "LiyaqtaAAa tarafan mina allatheena kafaroo aw yakbitahum fayanqaliboo khaibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 128, "Not for you, [O Muhammad, but for Allah], is the decision whether He should [cut them down] or forgive them or punish them, for indeed, they are wrongdoers", "لَيۡسَ لَكَ مِنَ ٱلۡأَمۡرِ شَيۡءٌ أَوۡ يَتُوبَ عَلَيۡهِمۡ أَوۡ يُعَذِّبَهُمۡ فَإِنَّهُمۡ ظَٰلِمُونَ", "Laysa laka mina alamri shayon aw yatooba AAalayhim aw yuAAaththibahum fainnahum thalimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 129, "And to Allah belongs whatever is in the heavens and whatever is on the earth. He forgives whom He wills and punishes whom He wills. And Allah is Forgiving and Merciful", "وَلِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۚ يَغۡفِرُ لِمَن يَشَآءُ وَيُعَذِّبُ مَن يَشَآءُۚ وَٱللَّهُ غَفُورٞ رَّحِيمٞ", "Walillahi ma fee alssamawati wama fee alardi yaghfiru liman yashao wayuAAaththibu man yashao waAllahu ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 130, "O you who have believed, do not consume usury, doubled and multiplied, but fear Allah that you may be successful", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَأۡكُلُواْ ٱلرِّبَوٰٓاْ أَضۡعَٰفٗا مُّضَٰعَفَةٗۖ وَٱتَّقُواْ ٱللَّهَ لَعَلَّكُمۡ تُفۡلِحُونَ", "Ya ayyuha allatheena amanoo la takuloo alrriba adAAafan mudaAAafatan waittaqoo Allaha laAAallakum tuflihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 131, "And fear the Fire, which has been prepared for the disbelievers", "وَٱتَّقُواْ ٱلنَّارَ ٱلَّتِيٓ أُعِدَّتۡ لِلۡكَٰفِرِينَ", "Waittaqoo alnnara allatee oAAiddat lilkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 132, "And obey Allah and the Messenger that you may obtain mercy", "وَأَطِيعُواْ ٱللَّهَ وَٱلرَّسُولَ لَعَلَّكُمۡ تُرۡحَمُونَ", "WaateeAAoo Allaha waalrrasoola laAAallakum turhamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 133, "And hasten to forgiveness from your Lord and a garden as wide as the heavens and earth, prepared for the righteous", "۞وَسَارِعُوٓاْ إِلَىٰ مَغۡفِرَةٖ مِّن رَّبِّكُمۡ وَجَنَّةٍ عَرۡضُهَا ٱلسَّمَٰوَٰتُ وَٱلۡأَرۡضُ أُعِدَّتۡ لِلۡمُتَّقِينَ", "WasariAAoo ila maghfiratin min rabbikum wajannatin AAarduha alssamawatu waalardu oAAiddat lilmuttaqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 134, "Who spend [in the cause of Allah] during ease and hardship and who restrain anger and who pardon the people - and Allah loves the doers of good", "ٱلَّذِينَ يُنفِقُونَ فِي ٱلسَّرَّآءِ وَٱلضَّرَّآءِ وَٱلۡكَٰظِمِينَ ٱلۡغَيۡظَ وَٱلۡعَافِينَ عَنِ ٱلنَّاسِۗ وَٱللَّهُ يُحِبُّ ٱلۡمُحۡسِنِينَ", "Allatheena yunfiqoona fee alssarrai waalddarrai waalkathimeena alghaytha waalAAafeena AAani alnnasi waAllahu yuhibbu almuhsineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 135, "And those who, when they commit an immorality or wrong themselves [by transgression], remember Allah and seek forgiveness for their sins - and who can forgive sins except Allah? - and [who] do not persist in what they have done while they know", "وَٱلَّذِينَ إِذَا فَعَلُواْ فَٰحِشَةً أَوۡ ظَلَمُوٓاْ أَنفُسَهُمۡ ذَكَرُواْ ٱللَّهَ فَٱسۡتَغۡفَرُواْ لِذُنُوبِهِمۡ وَمَن يَغۡفِرُ ٱلذُّنُوبَ إِلَّا ٱللَّهُ وَلَمۡ يُصِرُّواْ عَلَىٰ مَا فَعَلُواْ وَهُمۡ يَعۡلَمُونَ", "Waallatheena itha faAAaloo fahishatan aw thalamoo anfusahum thakaroo Allaha faistaghfaroo lithunoobihim waman yaghfiru alththunooba illa Allahu walam yusirroo AAala ma faAAaloo wahum yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 136, "Those - their reward is forgiveness from their Lord and gardens beneath which rivers flow [in Paradise], wherein they will abide eternally; and excellent is the reward of the [righteous] workers", "أُوْلَـٰٓئِكَ جَزَآؤُهُم مَّغۡفِرَةٞ مِّن رَّبِّهِمۡ وَجَنَّـٰتٞ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَاۚ وَنِعۡمَ أَجۡرُ ٱلۡعَٰمِلِينَ", "Olaika jazaohum maghfiratun min rabbihim wajannatun tajree min tahtiha alanharu khalideena feeha waniAAma ajru alAAamileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 137, "Similar situations [as yours] have passed on before you, so proceed throughout the earth and observe how was the end of those who denied", "قَدۡ خَلَتۡ مِن قَبۡلِكُمۡ سُنَنٞ فَسِيرُواْ فِي ٱلۡأَرۡضِ فَٱنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلۡمُكَذِّبِينَ", "Qad khalat min qablikum sunanun faseeroo fee alardi faonthuroo kayfa kana AAaqibatu almukaththibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 138, "This [Qur'an] is a clear statement to [all] the people and a guidance and instruction for those conscious of Allah", "هَٰذَا بَيَانٞ لِّلنَّاسِ وَهُدٗى وَمَوۡعِظَةٞ لِّلۡمُتَّقِينَ", "Hatha bayanun lilnnasi wahudan wamawAAithatun lilmuttaqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 139, "So do not weaken and do not grieve, and you will be superior if you are [true] believers", "وَلَا تَهِنُواْ وَلَا تَحۡزَنُواْ وَأَنتُمُ ٱلۡأَعۡلَوۡنَ إِن كُنتُم مُّؤۡمِنِينَ", "Wala tahinoo wala tahzanoo waantumu alaAAlawna in kuntum mumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 140, "If a wound should touch you - there has already touched the [opposing] people a wound similar to it. And these days [of varying conditions] We alternate among the people so that Allah may make evident those who believe and [may] take to Himself from among you martyrs - and Allah does not like the wrongdoers", "إِن يَمۡسَسۡكُمۡ قَرۡحٞ فَقَدۡ مَسَّ ٱلۡقَوۡمَ قَرۡحٞ مِّثۡلُهُۥۚ وَتِلۡكَ ٱلۡأَيَّامُ نُدَاوِلُهَا بَيۡنَ ٱلنَّاسِ وَلِيَعۡلَمَ ٱللَّهُ ٱلَّذِينَ ءَامَنُواْ وَيَتَّخِذَ مِنكُمۡ شُهَدَآءَۗ وَٱللَّهُ لَا يُحِبُّ ٱلظَّـٰلِمِينَ", "In yamsaskum qarhun faqad massa alqawma qarhun mithluhu watilka alayyamu nudawiluha bayna alnnasi waliyaAAlama Allahu allatheena amanoo wayattakhitha minkum shuhadaa waAllahu la yuhibbu alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 141, "And that Allah may purify the believers [through trials] and destroy the disbelievers", "وَلِيُمَحِّصَ ٱللَّهُ ٱلَّذِينَ ءَامَنُواْ وَيَمۡحَقَ ٱلۡكَٰفِرِينَ", "Waliyumahhisa Allahu allatheena amanoo wayamhaqa alkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 142, "Or do you think that you will enter Paradise while Allah has not yet made evident those of you who fight in His cause and made evident those who are steadfast", "أَمۡ حَسِبۡتُمۡ أَن تَدۡخُلُواْ ٱلۡجَنَّةَ وَلَمَّا يَعۡلَمِ ٱللَّهُ ٱلَّذِينَ جَٰهَدُواْ مِنكُمۡ وَيَعۡلَمَ ٱلصَّـٰبِرِينَ", "Am hasibtum an tadkhuloo aljannata walamma yaAAlami Allahu allatheena jahadoo minkum wayaAAlama alssabireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 143, "And you had certainly wished for martyrdom before you encountered it, and you have [now] seen it [before you] while you were looking on", "وَلَقَدۡ كُنتُمۡ تَمَنَّوۡنَ ٱلۡمَوۡتَ مِن قَبۡلِ أَن تَلۡقَوۡهُ فَقَدۡ رَأَيۡتُمُوهُ وَأَنتُمۡ تَنظُرُونَ", "Walaqad kuntum tamannawna almawta min qabli an talqawhu faqad raaytumoohu waantum tanthuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 144, "Muhammad is not but a messenger. [Other] messengers have passed on before him. So if he was to die or be killed, would you turn back on your heels [to unbelief]? And he who turns back on his heels will never harm Allah at all; but Allah will reward the grateful", "وَمَا مُحَمَّدٌ إِلَّا رَسُولٞ قَدۡ خَلَتۡ مِن قَبۡلِهِ ٱلرُّسُلُۚ أَفَإِيْن مَّاتَ أَوۡ قُتِلَ ٱنقَلَبۡتُمۡ عَلَىٰٓ أَعۡقَٰبِكُمۡۚ وَمَن يَنقَلِبۡ عَلَىٰ عَقِبَيۡهِ فَلَن يَضُرَّ ٱللَّهَ شَيۡـٔٗاۚ وَسَيَجۡزِي ٱللَّهُ ٱلشَّـٰكِرِينَ", "Wama muhammadun illa rasoolun qad khalat min qablihi alrrusulu afain mata aw qutila inqalabtum AAala aAAqabikum waman yanqalib AAala AAaqibayhi falan yadurra Allaha shayan wasayajzee Allahu alshshakireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 145, "And it is not [possible] for one to die except by permission of Allah at a decree determined. And whoever desires the reward of this world - We will give him thereof; and whoever desires the reward of the Hereafter - We will give him thereof. And we will reward the grateful", "وَمَا كَانَ لِنَفۡسٍ أَن تَمُوتَ إِلَّا بِإِذۡنِ ٱللَّهِ كِتَٰبٗا مُّؤَجَّلٗاۗ وَمَن يُرِدۡ ثَوَابَ ٱلدُّنۡيَا نُؤۡتِهِۦ مِنۡهَا وَمَن يُرِدۡ ثَوَابَ ٱلۡأٓخِرَةِ نُؤۡتِهِۦ مِنۡهَاۚ وَسَنَجۡزِي ٱلشَّـٰكِرِينَ", "Wama kana linafsin an tamoota illa biithni Allahi kitaban muajjalan waman yurid thawaba alddunya nutihi minha waman yurid thawaba alakhirati nutihi minha wasanajzee alshshakireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 146, "And how many a prophet [fought and] with him fought many religious scholars. But they never lost assurance due to what afflicted them in the cause of Allah, nor did they weaken or submit. And Allah loves the steadfast", "وَكَأَيِّن مِّن نَّبِيّٖ قَٰتَلَ مَعَهُۥ رِبِّيُّونَ كَثِيرٞ فَمَا وَهَنُواْ لِمَآ أَصَابَهُمۡ فِي سَبِيلِ ٱللَّهِ وَمَا ضَعُفُواْ وَمَا ٱسۡتَكَانُواْۗ وَٱللَّهُ يُحِبُّ ٱلصَّـٰبِرِينَ", "Wakaayyin min nabiyyin qatala maAAahu ribbiyyoona katheerun fama wahanoo lima asabahum fee sabeeli Allahi wama daAAufoo wama istakanoo waAllahu yuhibbu alssabireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 147, "And their words were not but that they said, ""Our Lord, forgive us our sins and the excess [committed] in our affairs and plant firmly our feet and give us victory over the disbelieving people", "وَمَا كَانَ قَوۡلَهُمۡ إِلَّآ أَن قَالُواْ رَبَّنَا ٱغۡفِرۡ لَنَا ذُنُوبَنَا وَإِسۡرَافَنَا فِيٓ أَمۡرِنَا وَثَبِّتۡ أَقۡدَامَنَا وَٱنصُرۡنَا عَلَى ٱلۡقَوۡمِ ٱلۡكَٰفِرِينَ", "Wama kana qawlahum illa an qaloo rabbana ighfir lana thunoobana waisrafana fee amrina wathabbit aqdamana waonsurna AAala alqawmi alkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 148, "So Allah gave them the reward of this world and the good reward of the Hereafter. And Allah loves the doers of good", "فَـَٔاتَىٰهُمُ ٱللَّهُ ثَوَابَ ٱلدُّنۡيَا وَحُسۡنَ ثَوَابِ ٱلۡأٓخِرَةِۗ وَٱللَّهُ يُحِبُّ ٱلۡمُحۡسِنِينَ", "Faatahumu Allahu thawaba alddunya wahusna thawabi alakhirati waAllahu yuhibbu almuhsineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 149, "O you who have believed, if you obey those who disbelieve, they will turn you back on your heels, and you will [then] become losers", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِن تُطِيعُواْ ٱلَّذِينَ كَفَرُواْ يَرُدُّوكُمۡ عَلَىٰٓ أَعۡقَٰبِكُمۡ فَتَنقَلِبُواْ خَٰسِرِينَ", "Ya ayyuha allatheena amanoo in tuteeAAoo allatheena kafaroo yaruddookum AAala aAAqabikum fatanqaliboo khasireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 150, "But Allah is your protector, and He is the best of helpers", "بَلِ ٱللَّهُ مَوۡلَىٰكُمۡۖ وَهُوَ خَيۡرُ ٱلنَّـٰصِرِينَ", "Bali Allahu mawlakum wahuwa khayru alnnasireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 151, "We will cast terror into the hearts of those who disbelieve for what they have associated with Allah of which He had not sent down [any] authority. And their refuge will be the Fire, and wretched is the residence of the wrongdoers", "سَنُلۡقِي فِي قُلُوبِ ٱلَّذِينَ كَفَرُواْ ٱلرُّعۡبَ بِمَآ أَشۡرَكُواْ بِٱللَّهِ مَا لَمۡ يُنَزِّلۡ بِهِۦ سُلۡطَٰنٗاۖ وَمَأۡوَىٰهُمُ ٱلنَّارُۖ وَبِئۡسَ مَثۡوَى ٱلظَّـٰلِمِينَ", "Sanulqee fee quloobi allatheena kafaroo alrruAAba bima ashrakoo biAllahi ma lam yunazzil bihi sultanan wamawahumu alnnaru wabisa mathwa alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 152, "And Allah had certainly fulfilled His promise to you when you were killing the enemy by His permission until [the time] when you lost courage and fell to disputing about the order [given by the Prophet] and disobeyed after He had shown you that which you love. Among you are some who desire this world, and among you are some who desire the Hereafter. Then he turned you back from them [defeated] that He might test you. And He has already forgiven you, and Allah is the possessor of bounty for the believers", "وَلَقَدۡ صَدَقَكُمُ ٱللَّهُ وَعۡدَهُۥٓ إِذۡ تَحُسُّونَهُم بِإِذۡنِهِۦۖ حَتَّىٰٓ إِذَا فَشِلۡتُمۡ وَتَنَٰزَعۡتُمۡ فِي ٱلۡأَمۡرِ وَعَصَيۡتُم مِّنۢ بَعۡدِ مَآ أَرَىٰكُم مَّا تُحِبُّونَۚ مِنكُم مَّن يُرِيدُ ٱلدُّنۡيَا وَمِنكُم مَّن يُرِيدُ ٱلۡأٓخِرَةَۚ ثُمَّ صَرَفَكُمۡ عَنۡهُمۡ لِيَبۡتَلِيَكُمۡۖ وَلَقَدۡ عَفَا عَنكُمۡۗ وَٱللَّهُ ذُو فَضۡلٍ عَلَى ٱلۡمُؤۡمِنِينَ", "Walaqad sadaqakumu Allahu waAAdahu ith tahussoonahum biithnihi hatta itha fashiltum watanazaAAtum fee alamri waAAasaytum min baAAdi ma arakum ma tuhibboona minkum man yureedu alddunya waminkum man yureedu alakhirata thumma sarafakum AAanhum liyabtaliyakum walaqad AAafa AAankum waAllahu thoo fadlin AAala almumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 153, "[Remember] when you [fled and] climbed [the mountain] without looking aside at anyone while the Messenger was calling you from behind. So Allah repaid you with distress upon distress so you would not grieve for that which had escaped you [of victory and spoils of war] or [for] that which had befallen you [of injury and death]. And Allah is [fully] Acquainted with what you do", "۞إِذۡ تُصۡعِدُونَ وَلَا تَلۡوُۥنَ عَلَىٰٓ أَحَدٖ وَٱلرَّسُولُ يَدۡعُوكُمۡ فِيٓ أُخۡرَىٰكُمۡ فَأَثَٰبَكُمۡ غَمَّۢا بِغَمّٖ لِّكَيۡلَا تَحۡزَنُواْ عَلَىٰ مَا فَاتَكُمۡ وَلَا مَآ أَصَٰبَكُمۡۗ وَٱللَّهُ خَبِيرُۢ بِمَا تَعۡمَلُونَ", "Ith tusAAidoona wala talwoona AAala ahadin waalrrasoolu yadAAookum fee okhrakum faathabakum ghamman bighammin likayla tahzanoo AAala ma fatakum wala ma asabakum waAllahu khabeerun bima taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 154, "Then after distress, He sent down upon you security [in the form of] drowsiness, overcoming a faction of you, while another faction worried about themselves, thinking of Allah other than the truth - the thought of ignorance, saying, ""Is there anything for us [to have done] in this matter?"" Say, ""Indeed, the matter belongs completely to Allah."" They conceal within themselves what they will not reveal to you. They say, ""If there was anything we could have done in the matter, some of us would not have been killed right here."" Say, ""Even if you had been inside your houses, those decreed to be killed would have come out to their death beds."" [It was] so that Allah might test what is in your breasts and purify what is in your hearts. And Allah is Knowing of that within the breasts", "ثُمَّ أَنزَلَ عَلَيۡكُم مِّنۢ بَعۡدِ ٱلۡغَمِّ أَمَنَةٗ نُّعَاسٗا يَغۡشَىٰ طَآئِفَةٗ مِّنكُمۡۖ وَطَآئِفَةٞ قَدۡ أَهَمَّتۡهُمۡ أَنفُسُهُمۡ يَظُنُّونَ بِٱللَّهِ غَيۡرَ ٱلۡحَقِّ ظَنَّ ٱلۡجَٰهِلِيَّةِۖ يَقُولُونَ هَل لَّنَا مِنَ ٱلۡأَمۡرِ مِن شَيۡءٖۗ قُلۡ إِنَّ ٱلۡأَمۡرَ كُلَّهُۥ لِلَّهِۗ يُخۡفُونَ فِيٓ أَنفُسِهِم مَّا لَا يُبۡدُونَ لَكَۖ يَقُولُونَ لَوۡ كَانَ لَنَا مِنَ ٱلۡأَمۡرِ شَيۡءٞ مَّا قُتِلۡنَا هَٰهُنَاۗ قُل لَّوۡ كُنتُمۡ فِي بُيُوتِكُمۡ لَبَرَزَ ٱلَّذِينَ كُتِبَ عَلَيۡهِمُ ٱلۡقَتۡلُ إِلَىٰ مَضَاجِعِهِمۡۖ وَلِيَبۡتَلِيَ ٱللَّهُ مَا فِي صُدُورِكُمۡ وَلِيُمَحِّصَ مَا فِي قُلُوبِكُمۡۚ وَٱللَّهُ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ", "Thumma anzala AAalaykum min baAAdi alghammi amanatan nuAAasan yaghsha taifatan minkum wataifatun qad ahammathum anfusuhum yathunnoona biAllahi ghayra alhaqqi thanna aljahiliyyati yaqooloona hal lana mina alamri min shayin qul inna alamra kullahu lillahi yukhfoona fee anfusihim ma la yubdoona laka yaqooloona law kana lana mina alamri shayon ma qutilna hahuna qul law kuntum fee buyootikum labaraza allatheena kutiba AAalayhimu alqatlu ila madajiAAihim waliyabtaliya Allahu ma fee sudoorikum waliyumahhisa ma fee quloobikum waAllahu AAaleemun bithati alssudoori");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 155, "Indeed, those of you who turned back on the day the two armies met, it was Satan who caused them to slip because of some [blame] they had earned. But Allah has already forgiven them. Indeed, Allah is Forgiving and Forbearing", "إِنَّ ٱلَّذِينَ تَوَلَّوۡاْ مِنكُمۡ يَوۡمَ ٱلۡتَقَى ٱلۡجَمۡعَانِ إِنَّمَا ٱسۡتَزَلَّهُمُ ٱلشَّيۡطَٰنُ بِبَعۡضِ مَا كَسَبُواْۖ وَلَقَدۡ عَفَا ٱللَّهُ عَنۡهُمۡۗ إِنَّ ٱللَّهَ غَفُورٌ حَلِيمٞ", "Inna allatheena tawallaw minkum yawma iltaqa aljamAAani innama istazallahumu alshshaytanu bibaAAdi ma kasaboo walaqad AAafa Allahu AAanhum inna Allaha ghafoorun haleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 156, "O you who have believed, do not be like those who disbelieved and said about their brothers when they traveled through the land or went out to fight, ""If they had been with us, they would not have died or have been killed,"" so Allah makes that [misconception] a regret within their hearts. And it is Allah who gives life and causes death, and Allah is Seeing of what you do", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَكُونُواْ كَٱلَّذِينَ كَفَرُواْ وَقَالُواْ لِإِخۡوَٰنِهِمۡ إِذَا ضَرَبُواْ فِي ٱلۡأَرۡضِ أَوۡ كَانُواْ غُزّٗى لَّوۡ كَانُواْ عِندَنَا مَا مَاتُواْ وَمَا قُتِلُواْ لِيَجۡعَلَ ٱللَّهُ ذَٰلِكَ حَسۡرَةٗ فِي قُلُوبِهِمۡۗ وَٱللَّهُ يُحۡيِۦ وَيُمِيتُۗ وَٱللَّهُ بِمَا تَعۡمَلُونَ بَصِيرٞ", "Ya ayyuha allatheena amanoo la takoonoo kaallatheena kafaroo waqaloo liikhwanihim itha daraboo fee alardi aw kanoo ghuzzan law kanoo AAindana ma matoo wama qutiloo liyajAAala Allahu thalika hasratan fee quloobihim waAllahu yuhyee wayumeetu waAllahu bima taAAmaloona baseerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 157, "And if you are killed in the cause of Allah or die - then forgiveness from Allah and mercy are better than whatever they accumulate [in this world]", "وَلَئِن قُتِلۡتُمۡ فِي سَبِيلِ ٱللَّهِ أَوۡ مُتُّمۡ لَمَغۡفِرَةٞ مِّنَ ٱللَّهِ وَرَحۡمَةٌ خَيۡرٞ مِّمَّا يَجۡمَعُونَ", "Walain qutiltum fee sabeeli Allahi aw muttum lamaghfiratun mina Allahi warahmatun khayrun mimma yajmaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 158, "And whether you die or are killed, unto Allah you will be gathered", "وَلَئِن مُّتُّمۡ أَوۡ قُتِلۡتُمۡ لَإِلَى ٱللَّهِ تُحۡشَرُونَ", "Walain muttum aw qutiltum laila Allahi tuhsharoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 159, "So by mercy from Allah, [O Muhammad], you were lenient with them. And if you had been rude [in speech] and harsh in heart, they would have disbanded from about you. So pardon them and ask forgiveness for them and consult them in the matter. And when you have decided, then rely upon Allah. Indeed, Allah loves those who rely [upon Him]", "فَبِمَا رَحۡمَةٖ مِّنَ ٱللَّهِ لِنتَ لَهُمۡۖ وَلَوۡ كُنتَ فَظًّا غَلِيظَ ٱلۡقَلۡبِ لَٱنفَضُّواْ مِنۡ حَوۡلِكَۖ فَٱعۡفُ عَنۡهُمۡ وَٱسۡتَغۡفِرۡ لَهُمۡ وَشَاوِرۡهُمۡ فِي ٱلۡأَمۡرِۖ فَإِذَا عَزَمۡتَ فَتَوَكَّلۡ عَلَى ٱللَّهِۚ إِنَّ ٱللَّهَ يُحِبُّ ٱلۡمُتَوَكِّلِينَ", "Fabima rahmatin mina Allahi linta lahum walaw kunta faththan ghaleetha alqalbi lainfaddoo min hawlika faoAAfu AAanhum waistaghfir lahum washawirhum fee alamri faitha AAazamta fatawakkal AAala Allahi inna Allaha yuhibbu almutawakkileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 160, "If Allah should aid you, no one can overcome you; but if He should forsake you, who is there that can aid you after Him? And upon Allah let the believers rely", "إِن يَنصُرۡكُمُ ٱللَّهُ فَلَا غَالِبَ لَكُمۡۖ وَإِن يَخۡذُلۡكُمۡ فَمَن ذَا ٱلَّذِي يَنصُرُكُم مِّنۢ بَعۡدِهِۦۗ وَعَلَى ٱللَّهِ فَلۡيَتَوَكَّلِ ٱلۡمُؤۡمِنُونَ", "In yansurkumu Allahu fala ghaliba lakum wain yakhthulkum faman tha allathee yansurukum min baAAdihi waAAala Allahi falyatawakkali almuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 161, "It is not [attributable] to any prophet that he would act unfaithfully [in regard to war booty]. And whoever betrays, [taking unlawfully], will come with what he took on the Day of Resurrection. Then will every soul be [fully] compensated for what it earned, and they will not be wronged", "وَمَا كَانَ لِنَبِيٍّ أَن يَغُلَّۚ وَمَن يَغۡلُلۡ يَأۡتِ بِمَا غَلَّ يَوۡمَ ٱلۡقِيَٰمَةِۚ ثُمَّ تُوَفَّىٰ كُلُّ نَفۡسٖ مَّا كَسَبَتۡ وَهُمۡ لَا يُظۡلَمُونَ", "Wama kana linabiyyin an yaghulla waman yaghlul yati bima ghalla yawma alqiyamati thumma tuwaffa kullu nafsin ma kasabat wahum la yuthlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 162, "So is one who pursues the pleasure of Allah like one who brings upon himself the anger of Allah and whose refuge is Hell? And wretched is the destination", "أَفَمَنِ ٱتَّبَعَ رِضۡوَٰنَ ٱللَّهِ كَمَنۢ بَآءَ بِسَخَطٖ مِّنَ ٱللَّهِ وَمَأۡوَىٰهُ جَهَنَّمُۖ وَبِئۡسَ ٱلۡمَصِيرُ", "Afamani ittabaAAa ridwana Allahi kaman baa bisakhatin mina Allahi wamawahu jahannamu wabisa almaseeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 163, "They are [varying] degrees in the sight of Allah, and Allah is Seeing of whatever they do", "هُمۡ دَرَجَٰتٌ عِندَ ٱللَّهِۗ وَٱللَّهُ بَصِيرُۢ بِمَا يَعۡمَلُونَ", "Hum darajatun AAinda Allahi waAllahu baseerun bima yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 164, "Certainly did Allah confer [great] favor upon the believers when He sent among them a Messenger from themselves, reciting to them His verses and purifying them and teaching them the Book and wisdom, although they had been before in manifest error", "لَقَدۡ مَنَّ ٱللَّهُ عَلَى ٱلۡمُؤۡمِنِينَ إِذۡ بَعَثَ فِيهِمۡ رَسُولٗا مِّنۡ أَنفُسِهِمۡ يَتۡلُواْ عَلَيۡهِمۡ ءَايَٰتِهِۦ وَيُزَكِّيهِمۡ وَيُعَلِّمُهُمُ ٱلۡكِتَٰبَ وَٱلۡحِكۡمَةَ وَإِن كَانُواْ مِن قَبۡلُ لَفِي ضَلَٰلٖ مُّبِينٍ", "Laqad manna Allahu AAala almumineena ith baAAatha feehim rasoolan min anfusihim yatloo AAalayhim ayatihi wayuzakkeehim wayuAAallimuhumu alkitaba waalhikmata wain kanoo min qablu lafee dalalin mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 165, "Why [is it that] when a [single] disaster struck you [on the day of Uhud], although you had struck [the enemy in the battle of Badr] with one twice as great, you said, ""From where is this?"" Say, ""It is from yourselves."" Indeed, Allah is over all things competent", "أَوَلَمَّآ أَصَٰبَتۡكُم مُّصِيبَةٞ قَدۡ أَصَبۡتُم مِّثۡلَيۡهَا قُلۡتُمۡ أَنَّىٰ هَٰذَاۖ قُلۡ هُوَ مِنۡ عِندِ أَنفُسِكُمۡۗ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ", "Awalamma asabatkum museebatun qad asabtum mithlayha qultum anna hatha qul huwa min AAindi anfusikum inna Allaha AAala kulli shayin qadeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 166, "And what struck you on the day the two armies met was by permission of Allah that He might make evident the [true] believers", "وَمَآ أَصَٰبَكُمۡ يَوۡمَ ٱلۡتَقَى ٱلۡجَمۡعَانِ فَبِإِذۡنِ ٱللَّهِ وَلِيَعۡلَمَ ٱلۡمُؤۡمِنِينَ", "Wama asabakum yawma iltaqa aljamAAani fabiithni Allahi waliyaAAlama almumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 167, "And that He might make evident those who are hypocrites. For it was said to them, ""Come, fight in the way of Allah or [at least] defend."" They said, ""If we had known [there would be] fighting, we would have followed you."" They were nearer to disbelief that day than to faith, saying with their mouths what was not in their hearts. And Allah is most Knowing of what they conceal", "وَلِيَعۡلَمَ ٱلَّذِينَ نَافَقُواْۚ وَقِيلَ لَهُمۡ تَعَالَوۡاْ قَٰتِلُواْ فِي سَبِيلِ ٱللَّهِ أَوِ ٱدۡفَعُواْۖ قَالُواْ لَوۡ نَعۡلَمُ قِتَالٗا لَّٱتَّبَعۡنَٰكُمۡۗ هُمۡ لِلۡكُفۡرِ يَوۡمَئِذٍ أَقۡرَبُ مِنۡهُمۡ لِلۡإِيمَٰنِۚ يَقُولُونَ بِأَفۡوَٰهِهِم مَّا لَيۡسَ فِي قُلُوبِهِمۡۚ وَٱللَّهُ أَعۡلَمُ بِمَا يَكۡتُمُونَ", "WaliyaAAlama allatheena nafaqoo waqeela lahum taAAalaw qatiloo fee sabeeli Allahi awi idfaAAoo qaloo law naAAlamu qitalan laittabaAAnakum hum lilkufri yawmaithin aqrabu minhum lileemani yaqooloona biafwahihim ma laysa fee quloobihim waAllahu aAAlamu bima yaktumoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 168, "Those who said about their brothers while sitting [at home], ""If they had obeyed us, they would not have been killed."" Say, ""Then prevent death from yourselves, if you should be truthful", "ٱلَّذِينَ قَالُواْ لِإِخۡوَٰنِهِمۡ وَقَعَدُواْ لَوۡ أَطَاعُونَا مَا قُتِلُواْۗ قُلۡ فَٱدۡرَءُواْ عَنۡ أَنفُسِكُمُ ٱلۡمَوۡتَ إِن كُنتُمۡ صَٰدِقِينَ", "Allatheena qaloo liikhwanihim waqaAAadoo law ataAAoona ma qutiloo qul faidraoo AAan anfusikumu almawta in kuntum sadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 169, "And never think of those who have been killed in the cause of Allah as dead. Rather, they are alive with their Lord, receiving provision", "وَلَا تَحۡسَبَنَّ ٱلَّذِينَ قُتِلُواْ فِي سَبِيلِ ٱللَّهِ أَمۡوَٰتَۢاۚ بَلۡ أَحۡيَآءٌ عِندَ رَبِّهِمۡ يُرۡزَقُونَ", "Wala tahsabanna allatheena qutiloo fee sabeeli Allahi amwatan bal ahyaon AAinda rabbihim yurzaqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 170, "Rejoicing in what Allah has bestowed upon them of His bounty, and they receive good tidings about those [to be martyred] after them who have not yet joined them - that there will be no fear concerning them, nor will they grieve", "فَرِحِينَ بِمَآ ءَاتَىٰهُمُ ٱللَّهُ مِن فَضۡلِهِۦ وَيَسۡتَبۡشِرُونَ بِٱلَّذِينَ لَمۡ يَلۡحَقُواْ بِهِم مِّنۡ خَلۡفِهِمۡ أَلَّا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ", "Fariheena bima atahumu Allahu min fadlihi wayastabshiroona biallatheena lam yalhaqoo bihim min khalfihim alla khawfun AAalayhim wala hum yahzanoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 171, "They receive good tidings of favor from Allah and bounty and [of the fact] that Allah does not allow the reward of believers to be lost", "۞يَسۡتَبۡشِرُونَ بِنِعۡمَةٖ مِّنَ ٱللَّهِ وَفَضۡلٖ وَأَنَّ ٱللَّهَ لَا يُضِيعُ أَجۡرَ ٱلۡمُؤۡمِنِينَ", "Yastabshiroona biniAAmatin mina Allahi wafadlin waanna Allaha la yudeeAAu ajra almumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 172, "Those [believers] who responded to Allah and the Messenger after injury had struck them. For those who did good among them and feared Allah is a great reward", "ٱلَّذِينَ ٱسۡتَجَابُواْ لِلَّهِ وَٱلرَّسُولِ مِنۢ بَعۡدِ مَآ أَصَابَهُمُ ٱلۡقَرۡحُۚ لِلَّذِينَ أَحۡسَنُواْ مِنۡهُمۡ وَٱتَّقَوۡاْ أَجۡرٌ عَظِيمٌ", "Allatheena istajaboo lillahi waalrrasooli min baAAdi ma asabahumu alqarhu lillatheena ahsanoo minhum waittaqaw ajrun AAatheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 173, "Those to whom hypocrites said, ""Indeed, the people have gathered against you, so fear them."" But it [merely] increased them in faith, and they said, ""Sufficient for us is Allah, and [He is] the best Disposer of affairs", "ٱلَّذِينَ قَالَ لَهُمُ ٱلنَّاسُ إِنَّ ٱلنَّاسَ قَدۡ جَمَعُواْ لَكُمۡ فَٱخۡشَوۡهُمۡ فَزَادَهُمۡ إِيمَٰنٗا وَقَالُواْ حَسۡبُنَا ٱللَّهُ وَنِعۡمَ ٱلۡوَكِيلُ", "Allatheena qala lahumu alnnasu inna alnnasa qad jamaAAoo lakum faikhshawhum fazadahum eemanan waqaloo hasbuna Allahu waniAAma alwakeelu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 174, "So they returned with favor from Allah and bounty, no harm having touched them. And they pursued the pleasure of Allah, and Allah is the possessor of great bounty", "فَٱنقَلَبُواْ بِنِعۡمَةٖ مِّنَ ٱللَّهِ وَفَضۡلٖ لَّمۡ يَمۡسَسۡهُمۡ سُوٓءٞ وَٱتَّبَعُواْ رِضۡوَٰنَ ٱللَّهِۗ وَٱللَّهُ ذُو فَضۡلٍ عَظِيمٍ", "Fainqalaboo biniAAmatin mina Allahi wafadlin lam yamsashum sooon waittabaAAoo ridwana Allahi waAllahu thoo fadlin AAatheemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 175, "That is only Satan who frightens [you] of his supporters. So fear them not, but fear Me, if you are [indeed] believers", "إِنَّمَا ذَٰلِكُمُ ٱلشَّيۡطَٰنُ يُخَوِّفُ أَوۡلِيَآءَهُۥ فَلَا تَخَافُوهُمۡ وَخَافُونِ إِن كُنتُم مُّؤۡمِنِينَ", "Innama thalikumu alshshaytanu yukhawwifu awliyaahu fala takhafoohum wakhafooni in kuntum mumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 176, "And do not be grieved, [O Muhammad], by those who hasten into disbelief. Indeed, they will never harm Allah at all. Allah intends that He should give them no share in the Hereafter, and for them is a great punishment", "وَلَا يَحۡزُنكَ ٱلَّذِينَ يُسَٰرِعُونَ فِي ٱلۡكُفۡرِۚ إِنَّهُمۡ لَن يَضُرُّواْ ٱللَّهَ شَيۡـٔٗاۚ يُرِيدُ ٱللَّهُ أَلَّا يَجۡعَلَ لَهُمۡ حَظّٗا فِي ٱلۡأٓخِرَةِۖ وَلَهُمۡ عَذَابٌ عَظِيمٌ", "Wala yahzunka allatheena yusariAAoona fee alkufri innahum lan yadurroo Allaha shayan yureedu Allahu alla yajAAala lahum haththan fee alakhirati walahum AAathabun AAatheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 177, "Indeed, those who purchase disbelief [in exchange] for faith - never will they harm Allah at all, and for them is a painful punishment", "إِنَّ ٱلَّذِينَ ٱشۡتَرَوُاْ ٱلۡكُفۡرَ بِٱلۡإِيمَٰنِ لَن يَضُرُّواْ ٱللَّهَ شَيۡـٔٗاۖ وَلَهُمۡ عَذَابٌ أَلِيمٞ", "Inna allatheena ishtarawoo alkufra bialeemani lan yadurroo Allaha shayan walahum AAathabun aleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 178, "And let not those who disbelieve ever think that [because] We extend their time [of enjoyment] it is better for them. We only extend it for them so that they may increase in sin, and for them is a humiliating punishment", "وَلَا يَحۡسَبَنَّ ٱلَّذِينَ كَفَرُوٓاْ أَنَّمَا نُمۡلِي لَهُمۡ خَيۡرٞ لِّأَنفُسِهِمۡۚ إِنَّمَا نُمۡلِي لَهُمۡ لِيَزۡدَادُوٓاْ إِثۡمٗاۖ وَلَهُمۡ عَذَابٞ مُّهِينٞ", "Wala yahsabanna allatheena kafaroo annama numlee lahum khayrun lianfusihim innama numlee lahum liyazdadoo ithman walahum AAathabun muheenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 179, "Allah would not leave the believers in that [state] you are in [presently] until He separates the evil from the good. Nor would Allah reveal to you the unseen. But [instead], Allah chooses of His messengers whom He wills, so believe in Allah and His messengers. And if you believe and fear Him, then for you is a great reward", "مَّا كَانَ ٱللَّهُ لِيَذَرَ ٱلۡمُؤۡمِنِينَ عَلَىٰ مَآ أَنتُمۡ عَلَيۡهِ حَتَّىٰ يَمِيزَ ٱلۡخَبِيثَ مِنَ ٱلطَّيِّبِۗ وَمَا كَانَ ٱللَّهُ لِيُطۡلِعَكُمۡ عَلَى ٱلۡغَيۡبِ وَلَٰكِنَّ ٱللَّهَ يَجۡتَبِي مِن رُّسُلِهِۦ مَن يَشَآءُۖ فَـَٔامِنُواْ بِٱللَّهِ وَرُسُلِهِۦۚ وَإِن تُؤۡمِنُواْ وَتَتَّقُواْ فَلَكُمۡ أَجۡرٌ عَظِيمٞ", "Ma kana Allahu liyathara almumineena AAala ma antum AAalayhi hatta yameeza alkhabeetha mina alttayyibi wama kana Allahu liyutliAAakum AAala alghaybi walakinna Allaha yajtabee min rusulihi man yashao faaminoo biAllahi warusulihi wain tuminoo watattaqoo falakum ajrun AAatheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 180, "And let not those who [greedily] withhold what Allah has given them of His bounty ever think that it is better for them. Rather, it is worse for them. Their necks will be encircled by what they withheld on the Day of Resurrection. And to Allah belongs the heritage of the heavens and the earth. And Allah, with what you do, is [fully] Acquainted", "وَلَا يَحۡسَبَنَّ ٱلَّذِينَ يَبۡخَلُونَ بِمَآ ءَاتَىٰهُمُ ٱللَّهُ مِن فَضۡلِهِۦ هُوَ خَيۡرٗا لَّهُمۖ بَلۡ هُوَ شَرّٞ لَّهُمۡۖ سَيُطَوَّقُونَ مَا بَخِلُواْ بِهِۦ يَوۡمَ ٱلۡقِيَٰمَةِۗ وَلِلَّهِ مِيرَٰثُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۗ وَٱللَّهُ بِمَا تَعۡمَلُونَ خَبِيرٞ", "Wala yahsabanna allatheena yabkhaloona bima atahummu Allahu min fadlihi huwa khayran lahum bal huwa sharrun lahum sayutawwaqoona ma bakhiloo bihi yawma alqiyamati walillahi meerathu alssamawati waalardi waAllahu bima taAAmaloona khabeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 181, "Allah has certainly heard the statement of those [Jews] who said, ""Indeed, Allah is poor, while we are rich."" We will record what they said and their killing of the prophets without right and will say, ""Taste the punishment of the Burning Fire", "لَّقَدۡ سَمِعَ ٱللَّهُ قَوۡلَ ٱلَّذِينَ قَالُوٓاْ إِنَّ ٱللَّهَ فَقِيرٞ وَنَحۡنُ أَغۡنِيَآءُۘ سَنَكۡتُبُ مَا قَالُواْ وَقَتۡلَهُمُ ٱلۡأَنۢبِيَآءَ بِغَيۡرِ حَقّٖ وَنَقُولُ ذُوقُواْ عَذَابَ ٱلۡحَرِيقِ", "Laqad samiAAa Allahu qawla allatheena qaloo inna Allaha faqeerun wanahnu aghniyaon sanaktubu ma qaloo waqatlahumu alanbiyaa bighayri haqqin wanaqoolu thooqoo AAathaba alhareeqi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 182, "That is for what your hands have put forth and because Allah is not ever unjust to [His] servants", "ذَٰلِكَ بِمَا قَدَّمَتۡ أَيۡدِيكُمۡ وَأَنَّ ٱللَّهَ لَيۡسَ بِظَلَّامٖ لِّلۡعَبِيدِ", "Thalika bima qaddamat aydeekum waanna Allaha laysa bithallamin lilAAabeedi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 183, "[They are] those who said, ""Indeed, Allah has taken our promise not to believe any messenger until he brings us an offering which fire [from heaven] will consume."" Say, ""There have already come to you messengers before me with clear proofs and [even] that of which you speak. So why did you kill them, if you should be truthful", "ٱلَّذِينَ قَالُوٓاْ إِنَّ ٱللَّهَ عَهِدَ إِلَيۡنَآ أَلَّا نُؤۡمِنَ لِرَسُولٍ حَتَّىٰ يَأۡتِيَنَا بِقُرۡبَانٖ تَأۡكُلُهُ ٱلنَّارُۗ قُلۡ قَدۡ جَآءَكُمۡ رُسُلٞ مِّن قَبۡلِي بِٱلۡبَيِّنَٰتِ وَبِٱلَّذِي قُلۡتُمۡ فَلِمَ قَتَلۡتُمُوهُمۡ إِن كُنتُمۡ صَٰدِقِينَ", "Allatheena qaloo inna Allaha AAahida ilayna alla numina lirasoolin hatta yatiyana biqurbanin takuluhu alnnaru qul qad jaakum rusulun min qablee bialbayyinati wabiallathee qultum falima qataltumoohum in kutum sadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 184, "Then if they deny you, [O Muhammad] - so were messengers denied before you, who brought clear proofs and written ordinances and the enlightening Scripture", "فَإِن كَذَّبُوكَ فَقَدۡ كُذِّبَ رُسُلٞ مِّن قَبۡلِكَ جَآءُو بِٱلۡبَيِّنَٰتِ وَٱلزُّبُرِ وَٱلۡكِتَٰبِ ٱلۡمُنِيرِ", "Fain kaththabooka faqad kuththiba rusulun min qablika jaoo bialbayyinati waalzzuburi waalkitabi almuneeri");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 185, "Every soul will taste death, and you will only be given your [full] compensation on the Day of Resurrection. So he who is drawn away from the Fire and admitted to Paradise has attained [his desire]. And what is the life of this world except the enjoyment of delusion", "كُلُّ نَفۡسٖ ذَآئِقَةُ ٱلۡمَوۡتِۗ وَإِنَّمَا تُوَفَّوۡنَ أُجُورَكُمۡ يَوۡمَ ٱلۡقِيَٰمَةِۖ فَمَن زُحۡزِحَ عَنِ ٱلنَّارِ وَأُدۡخِلَ ٱلۡجَنَّةَ فَقَدۡ فَازَۗ وَمَا ٱلۡحَيَوٰةُ ٱلدُّنۡيَآ إِلَّا مَتَٰعُ ٱلۡغُرُورِ", "Kullu nafsin thaiqatu almawti wainnama tuwaffawna ojoorakum yawma alqiyamati faman zuhziha AAani alnnari waodkhila aljannata faqad faza wama alhayatu alddunya illa mataAAu alghuroori");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 186, "You will surely be tested in your possessions and in yourselves. And you will surely hear from those who were given the Scripture before you and from those who associate others with Allah much abuse. But if you are patient and fear Allah - indeed, that is of the matters [worthy] of determination", "۞لَتُبۡلَوُنَّ فِيٓ أَمۡوَٰلِكُمۡ وَأَنفُسِكُمۡ وَلَتَسۡمَعُنَّ مِنَ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ مِن قَبۡلِكُمۡ وَمِنَ ٱلَّذِينَ أَشۡرَكُوٓاْ أَذٗى كَثِيرٗاۚ وَإِن تَصۡبِرُواْ وَتَتَّقُواْ فَإِنَّ ذَٰلِكَ مِنۡ عَزۡمِ ٱلۡأُمُورِ", "Latublawunna fee amwalikum waanfusikum walatasmaAAunna mina allatheena ootoo alkitaba min qablikum wamina allatheena ashrakoo athan katheeran wain tasbiroo watattaqoo fainna thalika min AAazmi alomoori");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 187, "And [mention, O Muhammad], when Allah took a covenant from those who were given the Scripture, [saying], ""You must make it clear to the people and not conceal it."" But they threw it away behind their backs and exchanged it for a small price. And wretched is that which they purchased", "وَإِذۡ أَخَذَ ٱللَّهُ مِيثَٰقَ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ لَتُبَيِّنُنَّهُۥ لِلنَّاسِ وَلَا تَكۡتُمُونَهُۥ فَنَبَذُوهُ وَرَآءَ ظُهُورِهِمۡ وَٱشۡتَرَوۡاْ بِهِۦ ثَمَنٗا قَلِيلٗاۖ فَبِئۡسَ مَا يَشۡتَرُونَ", "Waith akhatha Allahu meethaqa allatheena ootoo alkitaba latubayyinunnahu lilnnasi wala taktumoonahu fanabathoohu waraa thuhoorihim waishtaraw bihi thamanan qaleelan fabisa ma yashtaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 188, "And never think that those who rejoice in what they have perpetrated and like to be praised for what they did not do - never think them [to be] in safety from the punishment, and for them is a painful punishment", "لَا تَحۡسَبَنَّ ٱلَّذِينَ يَفۡرَحُونَ بِمَآ أَتَواْ وَّيُحِبُّونَ أَن يُحۡمَدُواْ بِمَا لَمۡ يَفۡعَلُواْ فَلَا تَحۡسَبَنَّهُم بِمَفَازَةٖ مِّنَ ٱلۡعَذَابِۖ وَلَهُمۡ عَذَابٌ أَلِيمٞ", "La tahsabanna allatheena yafrahoona bima ataw wayuhibboona an yuhmadoo bima lam yafAAaloo fala tahsabannahum bimafazatin mina alAAathabi walahum AAathabun aleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 189, "And to Allah belongs the dominion of the heavens and the earth, and Allah is over all things competent", "وَلِلَّهِ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۗ وَٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ", "Walillahi mulku alssamawati waalardi waAllahu AAala kulli shayin qadeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 190, "Indeed, in the creation of the heavens and the earth and the alternation of the night and the day are signs for those of understanding", "إِنَّ فِي خَلۡقِ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَٱخۡتِلَٰفِ ٱلَّيۡلِ وَٱلنَّهَارِ لَأٓيَٰتٖ لِّأُوْلِي ٱلۡأَلۡبَٰبِ", "Inna fee khalqi alssamawati waalardi waikhtilafi allayli waalnnahari laayatin liolee alalbabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 191, "Who remember Allah while standing or sitting or [lying] on their sides and give thought to the creation of the heavens and the earth, [saying], ""Our Lord, You did not create this aimlessly; exalted are You [above such a thing]; then protect us from the punishment of the Fire", "ٱلَّذِينَ يَذۡكُرُونَ ٱللَّهَ قِيَٰمٗا وَقُعُودٗا وَعَلَىٰ جُنُوبِهِمۡ وَيَتَفَكَّرُونَ فِي خَلۡقِ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ رَبَّنَا مَا خَلَقۡتَ هَٰذَا بَٰطِلٗا سُبۡحَٰنَكَ فَقِنَا عَذَابَ ٱلنَّارِ", "Allatheena yathkuroona Allaha qiyaman waquAAoodan waAAala junoobihim wayatafakkaroona fee khalqi alssamawati waalardi rabbana ma khalaqta hatha batilan subhanaka faqina AAathaba alnnari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 192, "Our Lord, indeed whoever You admit to the Fire - You have disgraced him, and for the wrongdoers there are no helpers", "رَبَّنَآ إِنَّكَ مَن تُدۡخِلِ ٱلنَّارَ فَقَدۡ أَخۡزَيۡتَهُۥۖ وَمَا لِلظَّـٰلِمِينَ مِنۡ أَنصَارٖ", "Rabbana innaka man tudkhili alnnara faqad akhzaytahu wama lilththalimeena min ansarin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 193, "Our Lord, indeed we have heard a caller calling to faith, [saying], 'Believe in your Lord,' and we have believed. Our Lord, so forgive us our sins and remove from us our misdeeds and cause us to die with the righteous", "رَّبَّنَآ إِنَّنَا سَمِعۡنَا مُنَادِيٗا يُنَادِي لِلۡإِيمَٰنِ أَنۡ ءَامِنُواْ بِرَبِّكُمۡ فَـَٔامَنَّاۚ رَبَّنَا فَٱغۡفِرۡ لَنَا ذُنُوبَنَا وَكَفِّرۡ عَنَّا سَيِّـَٔاتِنَا وَتَوَفَّنَا مَعَ ٱلۡأَبۡرَارِ", "Rabbana innana samiAAna munadiyan yunadee lileemani an aminoo birabbikum faamanna rabbana faighfir lana thunoobana wakaffir AAanna sayyiatina watawaffana maAAa alabrari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 194, "Our Lord, and grant us what You promised us through Your messengers and do not disgrace us on the Day of Resurrection. Indeed, You do not fail in [Your] promise", "رَبَّنَا وَءَاتِنَا مَا وَعَدتَّنَا عَلَىٰ رُسُلِكَ وَلَا تُخۡزِنَا يَوۡمَ ٱلۡقِيَٰمَةِۖ إِنَّكَ لَا تُخۡلِفُ ٱلۡمِيعَادَ", "Rabbana waatina ma waAAadtana AAala rusulika wala tukhzina yawma alqiyamati innaka la tukhlifu almeeAAada");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 195, "And their Lord responded to them, ""Never will I allow to be lost the work of [any] worker among you, whether male or female; you are of one another. So those who emigrated or were evicted from their homes or were harmed in My cause or fought or were killed - I will surely remove from them their misdeeds, and I will surely admit them to gardens beneath which rivers flow as reward from Allah, and Allah has with Him the best reward", "فَٱسۡتَجَابَ لَهُمۡ رَبُّهُمۡ أَنِّي لَآ أُضِيعُ عَمَلَ عَٰمِلٖ مِّنكُم مِّن ذَكَرٍ أَوۡ أُنثَىٰۖ بَعۡضُكُم مِّنۢ بَعۡضٖۖ فَٱلَّذِينَ هَاجَرُواْ وَأُخۡرِجُواْ مِن دِيَٰرِهِمۡ وَأُوذُواْ فِي سَبِيلِي وَقَٰتَلُواْ وَقُتِلُواْ لَأُكَفِّرَنَّ عَنۡهُمۡ سَيِّـَٔاتِهِمۡ وَلَأُدۡخِلَنَّهُمۡ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ ثَوَابٗا مِّنۡ عِندِ ٱللَّهِۚ وَٱللَّهُ عِندَهُۥ حُسۡنُ ٱلثَّوَابِ", "Faistajaba lahum rabbuhum annee la odeeAAu AAamala AAamilin minkum min thakarin aw ontha baAAdukum min baAAdin faallatheena hajaroo waokhrijoo min diyarihim waoothoo fee sabeelee waqataloo waqutiloo laokaffiranna AAanhum sayyiatihim walaodkhilannahum jannatin tajree min tahtiha alanharu thawaban min AAindi Allahi waAllahu AAindahu husnu alththawabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 196, "Be not deceived by the [uninhibited] movement of the disbelievers throughout the land", "لَا يَغُرَّنَّكَ تَقَلُّبُ ٱلَّذِينَ كَفَرُواْ فِي ٱلۡبِلَٰدِ", "La yaghurrannaka taqallubu allatheena kafaroo fee albiladi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 197, "[It is but] a small enjoyment; then their [final] refuge is Hell, and wretched is the resting place", "مَتَٰعٞ قَلِيلٞ ثُمَّ مَأۡوَىٰهُمۡ جَهَنَّمُۖ وَبِئۡسَ ٱلۡمِهَادُ", "MataAAun qaleelun thumma mawahum jahannamu wabisa almihadu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 198, "But those who feared their Lord will have gardens beneath which rivers flow, abiding eternally therein, as accommodation from Allah. And that which is with Allah is best for the righteous", "لَٰكِنِ ٱلَّذِينَ ٱتَّقَوۡاْ رَبَّهُمۡ لَهُمۡ جَنَّـٰتٞ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَا نُزُلٗا مِّنۡ عِندِ ٱللَّهِۗ وَمَا عِندَ ٱللَّهِ خَيۡرٞ لِّلۡأَبۡرَارِ", "Lakini allatheena ittaqaw rabbahum lahum jannatun tajree min tahtiha alanharu khalideena feeha nuzulan min AAindi Allahi wama AAinda Allahi khayrun lilabrari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 199, "And indeed, among the People of the Scripture are those who believe in Allah and what was revealed to you and what was revealed to them, [being] humbly submissive to Allah. They do not exchange the verses of Allah for a small price. Those will have their reward with their Lord. Indeed, Allah is swift in account", "وَإِنَّ مِنۡ أَهۡلِ ٱلۡكِتَٰبِ لَمَن يُؤۡمِنُ بِٱللَّهِ وَمَآ أُنزِلَ إِلَيۡكُمۡ وَمَآ أُنزِلَ إِلَيۡهِمۡ خَٰشِعِينَ لِلَّهِ لَا يَشۡتَرُونَ بِـَٔايَٰتِ ٱللَّهِ ثَمَنٗا قَلِيلًاۚ أُوْلَـٰٓئِكَ لَهُمۡ أَجۡرُهُمۡ عِندَ رَبِّهِمۡۗ إِنَّ ٱللَّهَ سَرِيعُ ٱلۡحِسَابِ", "Wainna min ahli alkitabi laman yuminu biAllahi wama onzila ilaykum wama onzila ilayhim khashiAAeena lillahi la yashtaroona biayati Allahi thamanan qaleelan olaika lahum ajruhum AAinda rabbihim inna Allaha sareeAAu alhisabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (3, 200, "O you who have believed, persevere and endure and remain stationed and fear Allah that you may be successful", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱصۡبِرُواْ وَصَابِرُواْ وَرَابِطُواْ وَٱتَّقُواْ ٱللَّهَ لَعَلَّكُمۡ تُفۡلِحُونَ", "Ya ayyuha allatheena amanoo isbiroo wasabiroo warabitoo waittaqoo Allaha laAAallakum tuflihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 1, "O mankind, fear your Lord, who created you from one soul and created from it its mate and dispersed from both of them many men and women. And fear Allah, through whom you ask one another, and the wombs. Indeed Allah is ever, over you, an Observer", "يَـٰٓأَيُّهَا ٱلنَّاسُ ٱتَّقُواْ رَبَّكُمُ ٱلَّذِي خَلَقَكُم مِّن نَّفۡسٖ وَٰحِدَةٖ وَخَلَقَ مِنۡهَا زَوۡجَهَا وَبَثَّ مِنۡهُمَا رِجَالٗا كَثِيرٗا وَنِسَآءٗۚ وَٱتَّقُواْ ٱللَّهَ ٱلَّذِي تَسَآءَلُونَ بِهِۦ وَٱلۡأَرۡحَامَۚ إِنَّ ٱللَّهَ كَانَ عَلَيۡكُمۡ رَقِيبٗا", "Ya ayyuha alnnasu ittaqoo rabbakumu allathee khalaqakum min nafsin wahidatin wakhalaqa minha zawjaha wabaththa minhuma rijalan katheeran wanisaan waittaqoo Allaha allathee tasaaloona bihi waalarhama inna Allaha kana AAalaykum raqeeban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 2, "And give to the orphans their properties and do not substitute the defective [of your own] for the good [of theirs]. And do not consume their properties into your own. Indeed, that is ever a great sin", "وَءَاتُواْ ٱلۡيَتَٰمَىٰٓ أَمۡوَٰلَهُمۡۖ وَلَا تَتَبَدَّلُواْ ٱلۡخَبِيثَ بِٱلطَّيِّبِۖ وَلَا تَأۡكُلُوٓاْ أَمۡوَٰلَهُمۡ إِلَىٰٓ أَمۡوَٰلِكُمۡۚ إِنَّهُۥ كَانَ حُوبٗا كَبِيرٗا", "Waatoo alyatama amwalahum wala tatabaddaloo alkhabeetha bialttayyibi wala takuloo amwalahum ila amwalikum innahu kana hooban kabeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 3, "And if you fear that you will not deal justly with the orphan girls, then marry those that please you of [other] women, two or three or four. But if you fear that you will not be just, then [marry only] one or those your right hand possesses. That is more suitable that you may not incline [to injustice]", "وَإِنۡ خِفۡتُمۡ أَلَّا تُقۡسِطُواْ فِي ٱلۡيَتَٰمَىٰ فَٱنكِحُواْ مَا طَابَ لَكُم مِّنَ ٱلنِّسَآءِ مَثۡنَىٰ وَثُلَٰثَ وَرُبَٰعَۖ فَإِنۡ خِفۡتُمۡ أَلَّا تَعۡدِلُواْ فَوَٰحِدَةً أَوۡ مَا مَلَكَتۡ أَيۡمَٰنُكُمۡۚ ذَٰلِكَ أَدۡنَىٰٓ أَلَّا تَعُولُواْ", "Wain khiftum alla tuqsitoo fee alyatama fainkihoo ma taba lakum mina alnnisai mathna wathulatha warubaAAa fain khiftum alla taAAdiloo fawahidatan aw ma malakat aymanukum thalika adna alla taAAooloo");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 4, "And give the women [upon marriage] their [bridal] gifts graciously. But if they give up willingly to you anything of it, then take it in satisfaction and ease", "وَءَاتُواْ ٱلنِّسَآءَ صَدُقَٰتِهِنَّ نِحۡلَةٗۚ فَإِن طِبۡنَ لَكُمۡ عَن شَيۡءٖ مِّنۡهُ نَفۡسٗا فَكُلُوهُ هَنِيٓـٔٗا مَّرِيٓـٔٗا", "Waatoo alnnisaa saduqatihinna nihlatan fain tibna lakum AAan shayin minhu nafsan fakuloohu haneean mareean");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 5, "And do not give the weak-minded your property, which Allah has made a means of sustenance for you, but provide for them with it and clothe them and speak to them words of appropriate kindness", "وَلَا تُؤۡتُواْ ٱلسُّفَهَآءَ أَمۡوَٰلَكُمُ ٱلَّتِي جَعَلَ ٱللَّهُ لَكُمۡ قِيَٰمٗا وَٱرۡزُقُوهُمۡ فِيهَا وَٱكۡسُوهُمۡ وَقُولُواْ لَهُمۡ قَوۡلٗا مَّعۡرُوفٗا", "Wala tutoo alssufahaa amwalakumu allatee jaAAala Allahu lakum qiyaman waorzuqoohum feeha waoksoohum waqooloo lahum qawlan maAAroofan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 6, "And test the orphans [in their abilities] until they reach marriageable age. Then if you perceive in them sound judgement, release their property to them. And do not consume it excessively and quickly, [anticipating] that they will grow up. And whoever, [when acting as guardian], is self-sufficient should refrain [from taking a fee]; and whoever is poor - let him take according to what is acceptable. Then when you release their property to them, bring witnesses upon them. And sufficient is Allah as Accountant", "وَٱبۡتَلُواْ ٱلۡيَتَٰمَىٰ حَتَّىٰٓ إِذَا بَلَغُواْ ٱلنِّكَاحَ فَإِنۡ ءَانَسۡتُم مِّنۡهُمۡ رُشۡدٗا فَٱدۡفَعُوٓاْ إِلَيۡهِمۡ أَمۡوَٰلَهُمۡۖ وَلَا تَأۡكُلُوهَآ إِسۡرَافٗا وَبِدَارًا أَن يَكۡبَرُواْۚ وَمَن كَانَ غَنِيّٗا فَلۡيَسۡتَعۡفِفۡۖ وَمَن كَانَ فَقِيرٗا فَلۡيَأۡكُلۡ بِٱلۡمَعۡرُوفِۚ فَإِذَا دَفَعۡتُمۡ إِلَيۡهِمۡ أَمۡوَٰلَهُمۡ فَأَشۡهِدُواْ عَلَيۡهِمۡۚ وَكَفَىٰ بِٱللَّهِ حَسِيبٗا", "Waibtaloo alyatama hatta itha balaghoo alnnikaha fain anastum minhum rushdan faidfaAAoo ilayhim amwalahum wala takulooha israfan wabidaran an yakbaroo waman kana ghaniyyan falyastaAAfif waman kana faqeeran falyakul bialmaAAroofi faitha dafaAAtum ilayhim amwalahum faashhidoo AAalayhim wakafa biAllahi haseeban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 7, "For men is a share of what the parents and close relatives leave, and for women is a share of what the parents and close relatives leave, be it little or much - an obligatory share", "لِّلرِّجَالِ نَصِيبٞ مِّمَّا تَرَكَ ٱلۡوَٰلِدَانِ وَٱلۡأَقۡرَبُونَ وَلِلنِّسَآءِ نَصِيبٞ مِّمَّا تَرَكَ ٱلۡوَٰلِدَانِ وَٱلۡأَقۡرَبُونَ مِمَّا قَلَّ مِنۡهُ أَوۡ كَثُرَۚ نَصِيبٗا مَّفۡرُوضٗا", "Lilrrijali naseebun mimma taraka alwalidani waalaqraboona walilnnisai naseebun mimma taraka alwalidani waalaqraboona mimma qalla minhu aw kathura naseeban mafroodan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 8, "And when [other] relatives and orphans and the needy are present at the [time of] division, then provide for them [something] out of the estate and speak to them words of appropriate kindness", "وَإِذَا حَضَرَ ٱلۡقِسۡمَةَ أُوْلُواْ ٱلۡقُرۡبَىٰ وَٱلۡيَتَٰمَىٰ وَٱلۡمَسَٰكِينُ فَٱرۡزُقُوهُم مِّنۡهُ وَقُولُواْ لَهُمۡ قَوۡلٗا مَّعۡرُوفٗا", "Waitha hadara alqismata oloo alqurba waalyatama waalmasakeenu faorzuqoohum minhu waqooloo lahum qawlan maAAroofan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 9, "And let those [executors and guardians] fear [injustice] as if they [themselves] had left weak offspring behind and feared for them. So let them fear Allah and speak words of appropriate justice", "وَلۡيَخۡشَ ٱلَّذِينَ لَوۡ تَرَكُواْ مِنۡ خَلۡفِهِمۡ ذُرِّيَّةٗ ضِعَٰفًا خَافُواْ عَلَيۡهِمۡ فَلۡيَتَّقُواْ ٱللَّهَ وَلۡيَقُولُواْ قَوۡلٗا سَدِيدًا", "Walyakhsha allatheena law tarakoo min khalfihim thurriyyatan diAAafan khafoo AAalayhim falyattaqoo Allaha walyaqooloo qawlan sadeedan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 10, "Indeed, those who devour the property of orphans unjustly are only consuming into their bellies fire. And they will be burned in a Blaze", "إِنَّ ٱلَّذِينَ يَأۡكُلُونَ أَمۡوَٰلَ ٱلۡيَتَٰمَىٰ ظُلۡمًا إِنَّمَا يَأۡكُلُونَ فِي بُطُونِهِمۡ نَارٗاۖ وَسَيَصۡلَوۡنَ سَعِيرٗا", "Inna allatheena yakuloona amwala alyatama thulman innama yakuloona fee butoonihim naran wasayaslawna saAAeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 11, "Allah instructs you concerning your children: for the male, what is equal to the share of two females. But if there are [only] daughters, two or more, for them is two thirds of one's estate. And if there is only one, for her is half. And for one's parents, to each one of them is a sixth of his estate if he left children. But if he had no children and the parents [alone] inherit from him, then for his mother is one third. And if he had brothers [or sisters], for his mother is a sixth, after any bequest he [may have] made or debt. Your parents or your children - you know not which of them are nearest to you in benefit. [These shares are] an obligation [imposed] by Allah. Indeed, Allah is ever Knowing and Wise", "يُوصِيكُمُ ٱللَّهُ فِيٓ أَوۡلَٰدِكُمۡۖ لِلذَّكَرِ مِثۡلُ حَظِّ ٱلۡأُنثَيَيۡنِۚ فَإِن كُنَّ نِسَآءٗ فَوۡقَ ٱثۡنَتَيۡنِ فَلَهُنَّ ثُلُثَا مَا تَرَكَۖ وَإِن كَانَتۡ وَٰحِدَةٗ فَلَهَا ٱلنِّصۡفُۚ وَلِأَبَوَيۡهِ لِكُلِّ وَٰحِدٖ مِّنۡهُمَا ٱلسُّدُسُ مِمَّا تَرَكَ إِن كَانَ لَهُۥ وَلَدٞۚ فَإِن لَّمۡ يَكُن لَّهُۥ وَلَدٞ وَوَرِثَهُۥٓ أَبَوَاهُ فَلِأُمِّهِ ٱلثُّلُثُۚ فَإِن كَانَ لَهُۥٓ إِخۡوَةٞ فَلِأُمِّهِ ٱلسُّدُسُۚ مِنۢ بَعۡدِ وَصِيَّةٖ يُوصِي بِهَآ أَوۡ دَيۡنٍۗ ءَابَآؤُكُمۡ وَأَبۡنَآؤُكُمۡ لَا تَدۡرُونَ أَيُّهُمۡ أَقۡرَبُ لَكُمۡ نَفۡعٗاۚ فَرِيضَةٗ مِّنَ ٱللَّهِۗ إِنَّ ٱللَّهَ كَانَ عَلِيمًا حَكِيمٗا", "Yooseekumu Allahu fee awladikum lilththakari mithlu haththi alonthayayni fain kunna nisaan fawqa ithnatayni falahunna thulutha ma taraka wain kanat wahidatan falaha alnnisfu waliabawayhi likulli wahidin minhuma alssudusu mimma taraka in kana lahu waladun fain lam yakun lahu waladun wawarithahu abawahu faliommihi alththuluthu fain kana lahu ikhwatun faliommihi alssudusu min baAAdi wasiyyatin yoosee biha aw daynin abaokum waabnaokum la tadroona ayyuhum aqrabu lakum nafAAan fareedatan mina Allahi inna Allaha kana AAaleeman hakeeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 12, "And for you is half of what your wives leave if they have no child. But if they have a child, for you is one fourth of what they leave, after any bequest they [may have] made or debt. And for the wives is one fourth if you leave no child. But if you leave a child, then for them is an eighth of what you leave, after any bequest you [may have] made or debt. And if a man or woman leaves neither ascendants nor descendants but has a brother or a sister, then for each one of them is a sixth. But if they are more than two, they share a third, after any bequest which was made or debt, as long as there is no detriment [caused]. [This is] an ordinance from Allah, and Allah is Knowing and Forbearing", "۞وَلَكُمۡ نِصۡفُ مَا تَرَكَ أَزۡوَٰجُكُمۡ إِن لَّمۡ يَكُن لَّهُنَّ وَلَدٞۚ فَإِن كَانَ لَهُنَّ وَلَدٞ فَلَكُمُ ٱلرُّبُعُ مِمَّا تَرَكۡنَۚ مِنۢ بَعۡدِ وَصِيَّةٖ يُوصِينَ بِهَآ أَوۡ دَيۡنٖۚ وَلَهُنَّ ٱلرُّبُعُ مِمَّا تَرَكۡتُمۡ إِن لَّمۡ يَكُن لَّكُمۡ وَلَدٞۚ فَإِن كَانَ لَكُمۡ وَلَدٞ فَلَهُنَّ ٱلثُّمُنُ مِمَّا تَرَكۡتُمۚ مِّنۢ بَعۡدِ وَصِيَّةٖ تُوصُونَ بِهَآ أَوۡ دَيۡنٖۗ وَإِن كَانَ رَجُلٞ يُورَثُ كَلَٰلَةً أَوِ ٱمۡرَأَةٞ وَلَهُۥٓ أَخٌ أَوۡ أُخۡتٞ فَلِكُلِّ وَٰحِدٖ مِّنۡهُمَا ٱلسُّدُسُۚ فَإِن كَانُوٓاْ أَكۡثَرَ مِن ذَٰلِكَ فَهُمۡ شُرَكَآءُ فِي ٱلثُّلُثِۚ مِنۢ بَعۡدِ وَصِيَّةٖ يُوصَىٰ بِهَآ أَوۡ دَيۡنٍ غَيۡرَ مُضَآرّٖۚ وَصِيَّةٗ مِّنَ ٱللَّهِۗ وَٱللَّهُ عَلِيمٌ حَلِيمٞ", "Walakum nisfu ma taraka azwajukum in lam yakun lahunna waladun fain kana lahunna waladun falakumu alrrubuAAu mimma tarakna min baAAdi wasiyyatin yooseena biha aw daynin walahunna alrrubuAAu mimma taraktum in lam yakun lakum waladun fain kana lakum waladun falahunna alththumunu mimma taraktum min baAAdi wasiyyatin toosoona biha aw daynin wain kana rajulun yoorathu kalalatan awi imraatun walahu akhun aw okhtun falikulli wahidin minhuma alssudusu fain kanoo akthara min thalika fahum shurakao fee alththuluthi min baAAdi wasiyyatin yoosa biha aw daynin ghayra mudarrin wasiyyatan mina Allahi waAllahu AAaleemun haleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 13, "These are the limits [set by] Allah, and whoever obeys Allah and His Messenger will be admitted by Him to gardens [in Paradise] under which rivers flow, abiding eternally therein; and that is the great attainment", "تِلۡكَ حُدُودُ ٱللَّهِۚ وَمَن يُطِعِ ٱللَّهَ وَرَسُولَهُۥ يُدۡخِلۡهُ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَاۚ وَذَٰلِكَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ", "Tilka hudoodu Allahi waman yutiAAi Allaha warasoolahu yudkhilhu jannatin tajree min tahtiha alanharu khalideena feeha wathalika alfawzu alAAatheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 14, "And whoever disobeys Allah and His Messenger and transgresses His limits - He will put him into the Fire to abide eternally therein, and he will have a humiliating punishment", "وَمَن يَعۡصِ ٱللَّهَ وَرَسُولَهُۥ وَيَتَعَدَّ حُدُودَهُۥ يُدۡخِلۡهُ نَارًا خَٰلِدٗا فِيهَا وَلَهُۥ عَذَابٞ مُّهِينٞ", "Waman yaAAsi Allaha warasoolahu wayataAAadda hudoodahu yudkhilhu naran khalidan feeha walahu AAathabun muheenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 15, "Those who commit unlawful sexual intercourse of your women - bring against them four [witnesses] from among you. And if they testify, confine the guilty women to houses until death takes them or Allah ordains for them [another] way", "وَٱلَّـٰتِي يَأۡتِينَ ٱلۡفَٰحِشَةَ مِن نِّسَآئِكُمۡ فَٱسۡتَشۡهِدُواْ عَلَيۡهِنَّ أَرۡبَعَةٗ مِّنكُمۡۖ فَإِن شَهِدُواْ فَأَمۡسِكُوهُنَّ فِي ٱلۡبُيُوتِ حَتَّىٰ يَتَوَفَّىٰهُنَّ ٱلۡمَوۡتُ أَوۡ يَجۡعَلَ ٱللَّهُ لَهُنَّ سَبِيلٗا", "Waallatee yateena alfahishata min nisaikum faistashhidoo AAalayhinna arbaAAatan minkum fain shahidoo faamsikoohunna fee albuyooti hatta yatawaffahunna almawtu aw yajAAala Allahu lahunna sabeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 16, "And the two who commit it among you, dishonor them both. But if they repent and correct themselves, leave them alone. Indeed, Allah is ever Accepting of repentance and Merciful", "وَٱلَّذَانِ يَأۡتِيَٰنِهَا مِنكُمۡ فَـَٔاذُوهُمَاۖ فَإِن تَابَا وَأَصۡلَحَا فَأَعۡرِضُواْ عَنۡهُمَآۗ إِنَّ ٱللَّهَ كَانَ تَوَّابٗا رَّحِيمًا", "Waallathani yatiyaniha minkum faathoohuma fain taba waaslaha faaAAridoo AAanhuma inna Allaha kana tawwaban raheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 17, "The repentance accepted by Allah is only for those who do wrong in ignorance [or carelessness] and then repent soon after. It is those to whom Allah will turn in forgiveness, and Allah is ever Knowing and Wise", "إِنَّمَا ٱلتَّوۡبَةُ عَلَى ٱللَّهِ لِلَّذِينَ يَعۡمَلُونَ ٱلسُّوٓءَ بِجَهَٰلَةٖ ثُمَّ يَتُوبُونَ مِن قَرِيبٖ فَأُوْلَـٰٓئِكَ يَتُوبُ ٱللَّهُ عَلَيۡهِمۡۗ وَكَانَ ٱللَّهُ عَلِيمًا حَكِيمٗا", "Innama alttawbatu AAala Allahi lillatheena yaAAmaloona alssooa bijahalatin thumma yatooboona min qareebin faolaika yatoobu Allahu AAalayhim wakana Allahu AAaleeman hakeeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 18, "But repentance is not [accepted] of those who [continue to] do evil deeds up until, when death comes to one of them, he says, ""Indeed, I have repented now,"" or of those who die while they are disbelievers. For them We have prepared a painful punishment", "وَلَيۡسَتِ ٱلتَّوۡبَةُ لِلَّذِينَ يَعۡمَلُونَ ٱلسَّيِّـَٔاتِ حَتَّىٰٓ إِذَا حَضَرَ أَحَدَهُمُ ٱلۡمَوۡتُ قَالَ إِنِّي تُبۡتُ ٱلۡـَٰٔنَ وَلَا ٱلَّذِينَ يَمُوتُونَ وَهُمۡ كُفَّارٌۚ أُوْلَـٰٓئِكَ أَعۡتَدۡنَا لَهُمۡ عَذَابًا أَلِيمٗا", "Walaysati alttawbatu lillatheena yaAAmaloona alssayyiati hatta itha hadara ahadahumu almawtu qala innee tubtu alana wala allatheena yamootoona wahum kuffarun olaika aAAtadna lahum AAathaban aleeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 19, "O you who have believed, it is not lawful for you to inherit women by compulsion. And do not make difficulties for them in order to take [back] part of what you gave them unless they commit a clear immorality. And live with them in kindness. For if you dislike them - perhaps you dislike a thing and Allah makes therein much good", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا يَحِلُّ لَكُمۡ أَن تَرِثُواْ ٱلنِّسَآءَ كَرۡهٗاۖ وَلَا تَعۡضُلُوهُنَّ لِتَذۡهَبُواْ بِبَعۡضِ مَآ ءَاتَيۡتُمُوهُنَّ إِلَّآ أَن يَأۡتِينَ بِفَٰحِشَةٖ مُّبَيِّنَةٖۚ وَعَاشِرُوهُنَّ بِٱلۡمَعۡرُوفِۚ فَإِن كَرِهۡتُمُوهُنَّ فَعَسَىٰٓ أَن تَكۡرَهُواْ شَيۡـٔٗا وَيَجۡعَلَ ٱللَّهُ فِيهِ خَيۡرٗا كَثِيرٗا", "Ya ayyuha allatheena amanoo la yahillu lakum an tarithoo alnnisaa karhan wala taAAduloohunna litathhaboo bibaAAdi ma ataytumoohunna illa an yateena bifahishatin mubayyinatin waAAashiroohunna bialmaAAroofi fain karihtumoohunna faAAasa an takrahoo shayan wayajAAala Allahu feehi khayran katheeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 20, "But if you want to replace one wife with another and you have given one of them a great amount [in gifts], do not take [back] from it anything. Would you take it in injustice and manifest sin", "وَإِنۡ أَرَدتُّمُ ٱسۡتِبۡدَالَ زَوۡجٖ مَّكَانَ زَوۡجٖ وَءَاتَيۡتُمۡ إِحۡدَىٰهُنَّ قِنطَارٗا فَلَا تَأۡخُذُواْ مِنۡهُ شَيۡـًٔاۚ أَتَأۡخُذُونَهُۥ بُهۡتَٰنٗا وَإِثۡمٗا مُّبِينٗا", "Wain aradtumu istibdala zawjin makana zawjin waataytum ihdahunna qintaran fala takhuthoo minhu shayan atakhuthoonahu buhtanan waithman mubeenan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 21, "And how could you take it while you have gone in unto each other and they have taken from you a solemn covenant", "وَكَيۡفَ تَأۡخُذُونَهُۥ وَقَدۡ أَفۡضَىٰ بَعۡضُكُمۡ إِلَىٰ بَعۡضٖ وَأَخَذۡنَ مِنكُم مِّيثَٰقًا غَلِيظٗا", "Wakayfa takhuthoonahu waqad afda baAAdukum ila baAAdin waakhathna minkum meethaqan ghaleethan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 22, "And do not marry those [women] whom your fathers married, except what has already occurred. Indeed, it was an immorality and hateful [to Allah] and was evil as a way", "وَلَا تَنكِحُواْ مَا نَكَحَ ءَابَآؤُكُم مِّنَ ٱلنِّسَآءِ إِلَّا مَا قَدۡ سَلَفَۚ إِنَّهُۥ كَانَ فَٰحِشَةٗ وَمَقۡتٗا وَسَآءَ سَبِيلًا", "Wala tankihoo ma nakaha abaokum mina alnnisai illa ma qad salafa innahu kana fahishatan wamaqtan wasaa sabeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 23, "Prohibited to you [for marriage] are your mothers, your daughters, your sisters, your father's sisters, your mother's sisters, your brother's daughters, your sister's daughters, your [milk] mothers who nursed you, your sisters through nursing, your wives' mothers, and your step-daughters under your guardianship [born] of your wives unto whom you have gone in. But if you have not gone in unto them, there is no sin upon you. And [also prohibited are] the wives of your sons who are from your [own] loins, and that you take [in marriage] two sisters simultaneously, except for what has already occurred. Indeed, Allah is ever Forgiving and Merciful", "حُرِّمَتۡ عَلَيۡكُمۡ أُمَّهَٰتُكُمۡ وَبَنَاتُكُمۡ وَأَخَوَٰتُكُمۡ وَعَمَّـٰتُكُمۡ وَخَٰلَٰتُكُمۡ وَبَنَاتُ ٱلۡأَخِ وَبَنَاتُ ٱلۡأُخۡتِ وَأُمَّهَٰتُكُمُ ٱلَّـٰتِيٓ أَرۡضَعۡنَكُمۡ وَأَخَوَٰتُكُم مِّنَ ٱلرَّضَٰعَةِ وَأُمَّهَٰتُ نِسَآئِكُمۡ وَرَبَـٰٓئِبُكُمُ ٱلَّـٰتِي فِي حُجُورِكُم مِّن نِّسَآئِكُمُ ٱلَّـٰتِي دَخَلۡتُم بِهِنَّ فَإِن لَّمۡ تَكُونُواْ دَخَلۡتُم بِهِنَّ فَلَا جُنَاحَ عَلَيۡكُمۡ وَحَلَـٰٓئِلُ أَبۡنَآئِكُمُ ٱلَّذِينَ مِنۡ أَصۡلَٰبِكُمۡ وَأَن تَجۡمَعُواْ بَيۡنَ ٱلۡأُخۡتَيۡنِ إِلَّا مَا قَدۡ سَلَفَۗ إِنَّ ٱللَّهَ كَانَ غَفُورٗا رَّحِيمٗا", "Hurrimat AAalaykum ommahatukum wabanatukum waakhawatukum waAAammatukum wakhalatukum wabanatu alakhi wabanatu alokhti waommahatukumu allatee ardaAAnakum waakhawatukum mina alrradaAAati waommahatu nisaikum warabaibukumu allatee fee hujoorikum min nisaikumu allatee dakhaltum bihinna fain lam takoonoo dakhaltum bihinna fala junaha AAalaykum wahalailu abnaikumu allatheena min aslabikum waan tajmaAAoo bayna alokhtayni illa ma qad salafa inna Allaha kana ghafooran raheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 24, "And [also prohibited to you are all] married women except those your right hands possess. [This is] the decree of Allah upon you. And lawful to you are [all others] beyond these, [provided] that you seek them [in marriage] with [gifts from] your property, desiring chastity, not unlawful sexual intercourse. So for whatever you enjoy [of marriage] from them, give them their due compensation as an obligation. And there is no blame upon you for what you mutually agree to beyond the obligation. Indeed, Allah is ever Knowing and Wise", "۞وَٱلۡمُحۡصَنَٰتُ مِنَ ٱلنِّسَآءِ إِلَّا مَا مَلَكَتۡ أَيۡمَٰنُكُمۡۖ كِتَٰبَ ٱللَّهِ عَلَيۡكُمۡۚ وَأُحِلَّ لَكُم مَّا وَرَآءَ ذَٰلِكُمۡ أَن تَبۡتَغُواْ بِأَمۡوَٰلِكُم مُّحۡصِنِينَ غَيۡرَ مُسَٰفِحِينَۚ فَمَا ٱسۡتَمۡتَعۡتُم بِهِۦ مِنۡهُنَّ فَـَٔاتُوهُنَّ أُجُورَهُنَّ فَرِيضَةٗۚ وَلَا جُنَاحَ عَلَيۡكُمۡ فِيمَا تَرَٰضَيۡتُم بِهِۦ مِنۢ بَعۡدِ ٱلۡفَرِيضَةِۚ إِنَّ ٱللَّهَ كَانَ عَلِيمًا حَكِيمٗا", "Waalmuhsanatu mina alnnisai illa ma malakat aymanukum kitaba Allahi AAalaykum waohilla lakum ma waraa thalikum an tabtaghoo biamwalikum muhsineena ghayra musafiheena fama istamtaAAtum bihi minhunna faatoohunna ojoorahunna fareedatan wala junaha AAalaykum feema taradaytum bihi min baAAdi alfareedati inna Allaha kana AAaleeman hakeeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 25, "And whoever among you cannot [find] the means to marry free, believing women, then [he may marry] from those whom your right hands possess of believing slave girls. And Allah is most knowing about your faith. You [believers] are of one another. So marry them with the permission of their people and give them their due compensation according to what is acceptable. [They should be] chaste, neither [of] those who commit unlawful intercourse randomly nor those who take [secret] lovers. But once they are sheltered in marriage, if they should commit adultery, then for them is half the punishment for free [unmarried] women. This [allowance] is for him among you who fears sin, but to be patient is better for you. And Allah is Forgiving and Merciful", "وَمَن لَّمۡ يَسۡتَطِعۡ مِنكُمۡ طَوۡلًا أَن يَنكِحَ ٱلۡمُحۡصَنَٰتِ ٱلۡمُؤۡمِنَٰتِ فَمِن مَّا مَلَكَتۡ أَيۡمَٰنُكُم مِّن فَتَيَٰتِكُمُ ٱلۡمُؤۡمِنَٰتِۚ وَٱللَّهُ أَعۡلَمُ بِإِيمَٰنِكُمۚ بَعۡضُكُم مِّنۢ بَعۡضٖۚ فَٱنكِحُوهُنَّ بِإِذۡنِ أَهۡلِهِنَّ وَءَاتُوهُنَّ أُجُورَهُنَّ بِٱلۡمَعۡرُوفِ مُحۡصَنَٰتٍ غَيۡرَ مُسَٰفِحَٰتٖ وَلَا مُتَّخِذَٰتِ أَخۡدَانٖۚ فَإِذَآ أُحۡصِنَّ فَإِنۡ أَتَيۡنَ بِفَٰحِشَةٖ فَعَلَيۡهِنَّ نِصۡفُ مَا عَلَى ٱلۡمُحۡصَنَٰتِ مِنَ ٱلۡعَذَابِۚ ذَٰلِكَ لِمَنۡ خَشِيَ ٱلۡعَنَتَ مِنكُمۡۚ وَأَن تَصۡبِرُواْ خَيۡرٞ لَّكُمۡۗ وَٱللَّهُ غَفُورٞ رَّحِيمٞ", "Waman lam yastatiAA minkum tawlan an yankiha almuhsanati almuminati famin ma malakat aymanukum min fatayatikumu almuminati waAllahu aAAlamu bieemanikum baAAdukum min baAAdin fainkihoohunna biithni ahlihinna waatoohunna ojoorahunna bialmaAAroofi muhsanatin ghayra masafihatin wala muttakhithati akhdanin faitha ohsinna fain atayna bifahishatin faAAalayhinna nisfu ma AAala almuhsanati mina alAAathabi thalika liman khashiya alAAanata minkum waan tasbiroo khayrun lakum waAllahu ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 26, "Allah wants to make clear to you [the lawful from the unlawful] and guide you to the [good] practices of those before you and to accept your repentance. And Allah is Knowing and Wise", "يُرِيدُ ٱللَّهُ لِيُبَيِّنَ لَكُمۡ وَيَهۡدِيَكُمۡ سُنَنَ ٱلَّذِينَ مِن قَبۡلِكُمۡ وَيَتُوبَ عَلَيۡكُمۡۗ وَٱللَّهُ عَلِيمٌ حَكِيمٞ", "Yureedu Allahu liyubayyina lakum wayahdiyakum sunana allatheena min qablikum wayatooba AAalaykum waAllahu AAaleemun hakeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 27, "Allah wants to accept your repentance, but those who follow [their] passions want you to digress [into] a great deviation", "وَٱللَّهُ يُرِيدُ أَن يَتُوبَ عَلَيۡكُمۡ وَيُرِيدُ ٱلَّذِينَ يَتَّبِعُونَ ٱلشَّهَوَٰتِ أَن تَمِيلُواْ مَيۡلًا عَظِيمٗا", "WaAllahu yureedu an yatooba AAalaykum wayureedu allatheena yattabiAAoona alshshahawati an tameeloo maylan AAatheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 28, "And Allah wants to lighten for you [your difficulties]; and mankind was created weak", "يُرِيدُ ٱللَّهُ أَن يُخَفِّفَ عَنكُمۡۚ وَخُلِقَ ٱلۡإِنسَٰنُ ضَعِيفٗا", "Yureedu Allahu an yukhaffifa AAankum wakhuliqa alinsanu daAAeefan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 29, "O you who have believed, do not consume one another's wealth unjustly but only [in lawful] business by mutual consent. And do not kill yourselves [or one another]. Indeed, Allah is to you ever Merciful", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَأۡكُلُوٓاْ أَمۡوَٰلَكُم بَيۡنَكُم بِٱلۡبَٰطِلِ إِلَّآ أَن تَكُونَ تِجَٰرَةً عَن تَرَاضٖ مِّنكُمۡۚ وَلَا تَقۡتُلُوٓاْ أَنفُسَكُمۡۚ إِنَّ ٱللَّهَ كَانَ بِكُمۡ رَحِيمٗا", "Ya ayyuha allatheena amanoo la takuloo amwalakum baynakum bialbatili illa an takoona tijaratan AAan taradin minkum wala taqtuloo anfusakum inna Allaha kana bikum raheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 30, "And whoever does that in aggression and injustice - then We will drive him into a Fire. And that, for Allah, is [always] easy", "وَمَن يَفۡعَلۡ ذَٰلِكَ عُدۡوَٰنٗا وَظُلۡمٗا فَسَوۡفَ نُصۡلِيهِ نَارٗاۚ وَكَانَ ذَٰلِكَ عَلَى ٱللَّهِ يَسِيرًا", "Waman yafAAal thalika AAudwanan wathulman fasawfa nusleehi naran wakana thalika AAala Allahi yaseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 31, "If you avoid the major sins which you are forbidden, We will remove from you your lesser sins and admit you to a noble entrance [into Paradise]", "إِن تَجۡتَنِبُواْ كَبَآئِرَ مَا تُنۡهَوۡنَ عَنۡهُ نُكَفِّرۡ عَنكُمۡ سَيِّـَٔاتِكُمۡ وَنُدۡخِلۡكُم مُّدۡخَلٗا كَرِيمٗا", "In tajtaniboo kabaira ma tunhawna AAanhu nukaffir AAankum sayyiatikum wanudkhilkum mudkhalan kareeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 32, "And do not wish for that by which Allah has made some of you exceed others. For men is a share of what they have earned, and for women is a share of what they have earned. And ask Allah of his bounty. Indeed Allah is ever, of all things, Knowing", "وَلَا تَتَمَنَّوۡاْ مَا فَضَّلَ ٱللَّهُ بِهِۦ بَعۡضَكُمۡ عَلَىٰ بَعۡضٖۚ لِّلرِّجَالِ نَصِيبٞ مِّمَّا ٱكۡتَسَبُواْۖ وَلِلنِّسَآءِ نَصِيبٞ مِّمَّا ٱكۡتَسَبۡنَۚ وَسۡـَٔلُواْ ٱللَّهَ مِن فَضۡلِهِۦٓۚ إِنَّ ٱللَّهَ كَانَ بِكُلِّ شَيۡءٍ عَلِيمٗا", "Wala tatamannaw ma faddala Allahu bihi baAAdakum AAala baAAdin lilrrijali naseebun mimma iktasaboo walilnnisai naseebun mimma iktasabna waisaloo Allaha min fadlihi inna Allaha kana bikulli shayin AAaleeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 33, "And for all, We have made heirs to what is left by parents and relatives. And to those whom your oaths have bound [to you] - give them their share. Indeed Allah is ever, over all things, a Witness", "وَلِكُلّٖ جَعَلۡنَا مَوَٰلِيَ مِمَّا تَرَكَ ٱلۡوَٰلِدَانِ وَٱلۡأَقۡرَبُونَۚ وَٱلَّذِينَ عَقَدَتۡ أَيۡمَٰنُكُمۡ فَـَٔاتُوهُمۡ نَصِيبَهُمۡۚ إِنَّ ٱللَّهَ كَانَ عَلَىٰ كُلِّ شَيۡءٖ شَهِيدًا", "Walikullin jaAAalna mawaliya mimma taraka alwalidani waalaqraboona waallatheena AAaqadat aymanukum faatoohum naseebahum inna Allaha kana AAala kulli shayin shaheedan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 34, "Men are in charge of women by [right of] what Allah has given one over the other and what they spend [for maintenance] from their wealth. So righteous women are devoutly obedient, guarding in [the husband's] absence what Allah would have them guard. But those [wives] from whom you fear arrogance - [first] advise them; [then if they persist], forsake them in bed; and [finally], strike them. But if they obey you [once more], seek no means against them. Indeed, Allah is ever Exalted and Grand", "ٱلرِّجَالُ قَوَّـٰمُونَ عَلَى ٱلنِّسَآءِ بِمَا فَضَّلَ ٱللَّهُ بَعۡضَهُمۡ عَلَىٰ بَعۡضٖ وَبِمَآ أَنفَقُواْ مِنۡ أَمۡوَٰلِهِمۡۚ فَٱلصَّـٰلِحَٰتُ قَٰنِتَٰتٌ حَٰفِظَٰتٞ لِّلۡغَيۡبِ بِمَا حَفِظَ ٱللَّهُۚ وَٱلَّـٰتِي تَخَافُونَ نُشُوزَهُنَّ فَعِظُوهُنَّ وَٱهۡجُرُوهُنَّ فِي ٱلۡمَضَاجِعِ وَٱضۡرِبُوهُنَّۖ فَإِنۡ أَطَعۡنَكُمۡ فَلَا تَبۡغُواْ عَلَيۡهِنَّ سَبِيلًاۗ إِنَّ ٱللَّهَ كَانَ عَلِيّٗا كَبِيرٗا", "Alrrijalu qawwamoona AAala alnnisai bima faddala Allahu baAAdahum AAala baAAdin wabima anfaqoo min amwalihim faalssalihatu qanitatun hafithatun lilghaybi bima hafitha Allahu waallatee takhafoona nushoozahunna faAAithoohunna waohjuroohunna fee almadajiAAi waidriboohunna fain ataAAnakum fala tabghoo AAalayhinna sabeelan inna Allaha kana AAaliyyan kabeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 35, "And if you fear dissension between the two, send an arbitrator from his people and an arbitrator from her people. If they both desire reconciliation, Allah will cause it between them. Indeed, Allah is ever Knowing and Acquainted [with all things]", "وَإِنۡ خِفۡتُمۡ شِقَاقَ بَيۡنِهِمَا فَٱبۡعَثُواْ حَكَمٗا مِّنۡ أَهۡلِهِۦ وَحَكَمٗا مِّنۡ أَهۡلِهَآ إِن يُرِيدَآ إِصۡلَٰحٗا يُوَفِّقِ ٱللَّهُ بَيۡنَهُمَآۗ إِنَّ ٱللَّهَ كَانَ عَلِيمًا خَبِيرٗا", "Wain khiftum shiqaqa baynihima faibAAathoo hakaman min ahlihi wahakaman min ahliha in yureeda islahan yuwaffiqi Allahu baynahuma inna Allaha kana AAaleeman khabeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 36, "Worship Allah and associate nothing with Him, and to parents do good, and to relatives, orphans, the needy, the near neighbor, the neighbor farther away, the companion at your side, the traveler, and those whom your right hands possess. Indeed, Allah does not like those who are self-deluding and boastful", "۞وَٱعۡبُدُواْ ٱللَّهَ وَلَا تُشۡرِكُواْ بِهِۦ شَيۡـٔٗاۖ وَبِٱلۡوَٰلِدَيۡنِ إِحۡسَٰنٗا وَبِذِي ٱلۡقُرۡبَىٰ وَٱلۡيَتَٰمَىٰ وَٱلۡمَسَٰكِينِ وَٱلۡجَارِ ذِي ٱلۡقُرۡبَىٰ وَٱلۡجَارِ ٱلۡجُنُبِ وَٱلصَّاحِبِ بِٱلۡجَنۢبِ وَٱبۡنِ ٱلسَّبِيلِ وَمَا مَلَكَتۡ أَيۡمَٰنُكُمۡۗ إِنَّ ٱللَّهَ لَا يُحِبُّ مَن كَانَ مُخۡتَالٗا فَخُورًا", "WaoAAbudoo Allaha wala tushrikoo bihi shayan wabialwalidayni ihsanan wabithee alqurba waalyatama waalmasakeeni waaljari thee alqurba waaljari aljunubi waalssahibi bialjanbi waibni alssabeeli wama malakat aymanukum inna Allaha la yuhibbu man kana mukhtalan fakhooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 37, "Who are stingy and enjoin upon [other] people stinginess and conceal what Allah has given them of His bounty - and We have prepared for the disbelievers a humiliating punishment", "ٱلَّذِينَ يَبۡخَلُونَ وَيَأۡمُرُونَ ٱلنَّاسَ بِٱلۡبُخۡلِ وَيَكۡتُمُونَ مَآ ءَاتَىٰهُمُ ٱللَّهُ مِن فَضۡلِهِۦۗ وَأَعۡتَدۡنَا لِلۡكَٰفِرِينَ عَذَابٗا مُّهِينٗا", "Allatheena yabkhaloona wayamuroona alnnasa bialbukhli wayaktumoona ma atahumu Allahu min fadlihi waaAAtadna lilkafireena AAathaban muheenan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 38, "And [also] those who spend of their wealth to be seen by the people and believe not in Allah nor in the Last Day. And he to whom Satan is a companion - then evil is he as a companion", "وَٱلَّذِينَ يُنفِقُونَ أَمۡوَٰلَهُمۡ رِئَآءَ ٱلنَّاسِ وَلَا يُؤۡمِنُونَ بِٱللَّهِ وَلَا بِٱلۡيَوۡمِ ٱلۡأٓخِرِۗ وَمَن يَكُنِ ٱلشَّيۡطَٰنُ لَهُۥ قَرِينٗا فَسَآءَ قَرِينٗا", "Waallatheena yunfiqoona amwalahum riaa alnnasi wala yuminoona biAllahi wala bialyawmi alakhiri waman yakuni alshshaytanu lahu qareenan fasaa qareenan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 39, "And what [harm would come] upon them if they believed in Allah and the Last Day and spent out of what Allah provided for them? And Allah is ever, about them, Knowing", "وَمَاذَا عَلَيۡهِمۡ لَوۡ ءَامَنُواْ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ وَأَنفَقُواْ مِمَّا رَزَقَهُمُ ٱللَّهُۚ وَكَانَ ٱللَّهُ بِهِمۡ عَلِيمًا", "Wamatha AAalayhim law amanoo biAllahi waalyawmi alakhiri waanfaqoo mimma razaqahumu Allahu wakana Allahu bihim AAaleeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 40, "Indeed, Allah does not do injustice, [even] as much as an atom's weight; while if there is a good deed, He multiplies it and gives from Himself a great reward", "إِنَّ ٱللَّهَ لَا يَظۡلِمُ مِثۡقَالَ ذَرَّةٖۖ وَإِن تَكُ حَسَنَةٗ يُضَٰعِفۡهَا وَيُؤۡتِ مِن لَّدُنۡهُ أَجۡرًا عَظِيمٗا", "Inna Allaha la yathlimu mithqala tharratin wain taku hasanatan yudaAAifha wayuti min ladunhu ajran AAatheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 41, "So how [will it be] when We bring from every nation a witness and we bring you, [O Muhammad] against these [people] as a witness", "فَكَيۡفَ إِذَا جِئۡنَا مِن كُلِّ أُمَّةِۭ بِشَهِيدٖ وَجِئۡنَا بِكَ عَلَىٰ هَـٰٓؤُلَآءِ شَهِيدٗا", "Fakayfa itha jina min kulli ommatin bishaheedin wajina bika AAala haolai shaheedan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 42, "That Day, those who disbelieved and disobeyed the Messenger will wish they could be covered by the earth. And they will not conceal from Allah a [single] statement", "يَوۡمَئِذٖ يَوَدُّ ٱلَّذِينَ كَفَرُواْ وَعَصَوُاْ ٱلرَّسُولَ لَوۡ تُسَوَّىٰ بِهِمُ ٱلۡأَرۡضُ وَلَا يَكۡتُمُونَ ٱللَّهَ حَدِيثٗا", "Yawmaithin yawaddu allatheena kafaroo waAAasawoo alrrasoola law tusawwa bihimu alardu wala yaktumoona Allaha hadeethan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 43, "O you who have believed, do not approach prayer while you are intoxicated until you know what you are saying or in a state of janabah, except those passing through [a place of prayer], until you have washed [your whole body]. And if you are ill or on a journey or one of you comes from the place of relieving himself or you have contacted women and find no water, then seek clean earth and wipe over your faces and your hands [with it]. Indeed, Allah is ever Pardoning and Forgiving", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَقۡرَبُواْ ٱلصَّلَوٰةَ وَأَنتُمۡ سُكَٰرَىٰ حَتَّىٰ تَعۡلَمُواْ مَا تَقُولُونَ وَلَا جُنُبًا إِلَّا عَابِرِي سَبِيلٍ حَتَّىٰ تَغۡتَسِلُواْۚ وَإِن كُنتُم مَّرۡضَىٰٓ أَوۡ عَلَىٰ سَفَرٍ أَوۡ جَآءَ أَحَدٞ مِّنكُم مِّنَ ٱلۡغَآئِطِ أَوۡ لَٰمَسۡتُمُ ٱلنِّسَآءَ فَلَمۡ تَجِدُواْ مَآءٗ فَتَيَمَّمُواْ صَعِيدٗا طَيِّبٗا فَٱمۡسَحُواْ بِوُجُوهِكُمۡ وَأَيۡدِيكُمۡۗ إِنَّ ٱللَّهَ كَانَ عَفُوًّا غَفُورًا", "Ya ayyuha allatheena amanoo la taqraboo alssalata waantum sukara hatta taAAlamoo ma taqooloona wala junuban illa AAabiree sabeelin hatta taghtasiloo wain kuntum marda aw AAala safarin aw jaa ahadun minkum mina alghaiti aw lamastumu alnnisaa falam tajidoo maan fatayammamoo saAAeedan tayyiban faimsahoo biwujoohikum waaydeekum inna Allaha kana AAafuwwan ghafooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 44, "Have you not seen those who were given a portion of the Scripture, purchasing error [in exchange for it] and wishing you would lose the way", "أَلَمۡ تَرَ إِلَى ٱلَّذِينَ أُوتُواْ نَصِيبٗا مِّنَ ٱلۡكِتَٰبِ يَشۡتَرُونَ ٱلضَّلَٰلَةَ وَيُرِيدُونَ أَن تَضِلُّواْ ٱلسَّبِيلَ", "Alam tara ila allatheena ootoo naseeban mina alkitabi yashtaroona alddalalata wayureedoona an tadilloo alssabeela");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 45, "And Allah is most knowing of your enemies; and sufficient is Allah as an ally, and sufficient is Allah as a helper", "وَٱللَّهُ أَعۡلَمُ بِأَعۡدَآئِكُمۡۚ وَكَفَىٰ بِٱللَّهِ وَلِيّٗا وَكَفَىٰ بِٱللَّهِ نَصِيرٗا", "WaAllahu aAAlamu biaAAdaikum wakafa biAllahi waliyyan wakafa biAllahi naseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 46, "Among the Jews are those who distort words from their [proper] usages and say, ""We hear and disobey"" and ""Hear but be not heard"" and ""Ra'ina,"" twisting their tongues and defaming the religion. And if they had said [instead], ""We hear and obey"" and ""Wait for us [to understand],"" it would have been better for them and more suitable. But Allah has cursed them for their disbelief, so they believe not, except for a few", "مِّنَ ٱلَّذِينَ هَادُواْ يُحَرِّفُونَ ٱلۡكَلِمَ عَن مَّوَاضِعِهِۦ وَيَقُولُونَ سَمِعۡنَا وَعَصَيۡنَا وَٱسۡمَعۡ غَيۡرَ مُسۡمَعٖ وَرَٰعِنَا لَيَّۢا بِأَلۡسِنَتِهِمۡ وَطَعۡنٗا فِي ٱلدِّينِۚ وَلَوۡ أَنَّهُمۡ قَالُواْ سَمِعۡنَا وَأَطَعۡنَا وَٱسۡمَعۡ وَٱنظُرۡنَا لَكَانَ خَيۡرٗا لَّهُمۡ وَأَقۡوَمَ وَلَٰكِن لَّعَنَهُمُ ٱللَّهُ بِكُفۡرِهِمۡ فَلَا يُؤۡمِنُونَ إِلَّا قَلِيلٗا", "Mina allatheena hadoo yuharrifoona alkalima AAan mawadiAAihi wayaqooloona samiAAna waAAasayna waismaAA ghayra musmaAAin waraAAina layyan bialsinatihim wataAAnan fee alddeeni walaw annahum qaloo samiAAna waataAAna waismaAA waonthurna lakana khayran lahum waaqwama walakin laAAanahumu Allahu bikufrihim fala yuminoona illa qaleelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 47, "O you who were given the Scripture, believe in what We have sent down [to Muhammad], confirming that which is with you, before We obliterate faces and turn them toward their backs or curse them as We cursed the sabbath-breakers. And ever is the decree of Allah accomplished", "يَـٰٓأَيُّهَا ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ ءَامِنُواْ بِمَا نَزَّلۡنَا مُصَدِّقٗا لِّمَا مَعَكُم مِّن قَبۡلِ أَن نَّطۡمِسَ وُجُوهٗا فَنَرُدَّهَا عَلَىٰٓ أَدۡبَارِهَآ أَوۡ نَلۡعَنَهُمۡ كَمَا لَعَنَّآ أَصۡحَٰبَ ٱلسَّبۡتِۚ وَكَانَ أَمۡرُ ٱللَّهِ مَفۡعُولًا", "Ya ayyuha allatheena ootoo alkitaba aminoo bima nazzalna musaddiqan lima maAAakum min qabli an natmisa wujoohan fanaruddaha AAala adbariha aw nalAAanahum kama laAAanna ashaba alssabti wakana amru Allahi mafAAoolan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 48, "Indeed, Allah does not forgive association with Him, but He forgives what is less than that for whom He wills. And he who associates others with Allah has certainly fabricated a tremendous sin", "إِنَّ ٱللَّهَ لَا يَغۡفِرُ أَن يُشۡرَكَ بِهِۦ وَيَغۡفِرُ مَا دُونَ ذَٰلِكَ لِمَن يَشَآءُۚ وَمَن يُشۡرِكۡ بِٱللَّهِ فَقَدِ ٱفۡتَرَىٰٓ إِثۡمًا عَظِيمًا", "Inna Allaha la yaghfiru an yushraka bihi wayaghfiru ma doona thalika liman yashao waman yushrik biAllahi faqadi iftara ithman AAatheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 49, "Have you not seen those who claim themselves to be pure? Rather, Allah purifies whom He wills, and injustice is not done to them, [even] as much as a thread [inside a date seed]", "أَلَمۡ تَرَ إِلَى ٱلَّذِينَ يُزَكُّونَ أَنفُسَهُمۚ بَلِ ٱللَّهُ يُزَكِّي مَن يَشَآءُ وَلَا يُظۡلَمُونَ فَتِيلًا", "Alam tara ila allatheena yuzakkoona anfusahum bali Allahu yuzakkee man yashao wala yuthlamoona fateelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 50, "Look how they invent about Allah untruth, and sufficient is that as a manifest sin", "ٱنظُرۡ كَيۡفَ يَفۡتَرُونَ عَلَى ٱللَّهِ ٱلۡكَذِبَۖ وَكَفَىٰ بِهِۦٓ إِثۡمٗا مُّبِينًا", "Onthur kayfa yaftaroona AAala Allahi alkathiba wakafa bihi ithman mubeenan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 51, "Have you not seen those who were given a portion of the Scripture, who believe in superstition and false objects of worship and say about the disbelievers, ""These are better guided than the believers as to the way", "أَلَمۡ تَرَ إِلَى ٱلَّذِينَ أُوتُواْ نَصِيبٗا مِّنَ ٱلۡكِتَٰبِ يُؤۡمِنُونَ بِٱلۡجِبۡتِ وَٱلطَّـٰغُوتِ وَيَقُولُونَ لِلَّذِينَ كَفَرُواْ هَـٰٓؤُلَآءِ أَهۡدَىٰ مِنَ ٱلَّذِينَ ءَامَنُواْ سَبِيلًا", "Alam tara ila allatheena ootoo naseeban mina alkitabi yuminoona bialjibti waalttaghooti wayaqooloona lillatheena kafaroo haolai ahda mina allatheena amanoo sabeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 52, "Those are the ones whom Allah has cursed; and he whom Allah curses - never will you find for him a helper", "أُوْلَـٰٓئِكَ ٱلَّذِينَ لَعَنَهُمُ ٱللَّهُۖ وَمَن يَلۡعَنِ ٱللَّهُ فَلَن تَجِدَ لَهُۥ نَصِيرًا", "Olaika allatheena laAAanahumu Allahu waman yalAAani Allahu falan tajida lahu naseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 53, "Or have they a share of dominion? Then [if that were so], they would not give the people [even as much as] the speck on a date seed", "أَمۡ لَهُمۡ نَصِيبٞ مِّنَ ٱلۡمُلۡكِ فَإِذٗا لَّا يُؤۡتُونَ ٱلنَّاسَ نَقِيرًا", "Am lahum naseebun mina almulki faithan la yutoona alnnasa naqeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 54, "Or do they envy people for what Allah has given them of His bounty? But we had already given the family of Abraham the Scripture and wisdom and conferred upon them a great kingdom", "أَمۡ يَحۡسُدُونَ ٱلنَّاسَ عَلَىٰ مَآ ءَاتَىٰهُمُ ٱللَّهُ مِن فَضۡلِهِۦۖ فَقَدۡ ءَاتَيۡنَآ ءَالَ إِبۡرَٰهِيمَ ٱلۡكِتَٰبَ وَٱلۡحِكۡمَةَ وَءَاتَيۡنَٰهُم مُّلۡكًا عَظِيمٗا", "Am yahsudoona alnnasa AAala ma atahumu Allahu min fadlihi faqad atayna ala ibraheema alkitaba waalhikmata waataynahum mulkan AAatheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 55, "And some among them believed in it, and some among them were averse to it. And sufficient is Hell as a blaze", "فَمِنۡهُم مَّنۡ ءَامَنَ بِهِۦ وَمِنۡهُم مَّن صَدَّ عَنۡهُۚ وَكَفَىٰ بِجَهَنَّمَ سَعِيرًا", "Faminhum man amana bihi waminhum man sadda AAanhu wakafa bijahannama saAAeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 56, "Indeed, those who disbelieve in Our verses - We will drive them into a Fire. Every time their skins are roasted through We will replace them with other skins so they may taste the punishment. Indeed, Allah is ever Exalted in Might and Wise", "إِنَّ ٱلَّذِينَ كَفَرُواْ بِـَٔايَٰتِنَا سَوۡفَ نُصۡلِيهِمۡ نَارٗا كُلَّمَا نَضِجَتۡ جُلُودُهُم بَدَّلۡنَٰهُمۡ جُلُودًا غَيۡرَهَا لِيَذُوقُواْ ٱلۡعَذَابَۗ إِنَّ ٱللَّهَ كَانَ عَزِيزًا حَكِيمٗا", "Inna allatheena kafaroo biayatina sawfa nusleehim naran kullama nadijat julooduhum baddalnahum juloodan ghayraha liyathooqoo alAAathaba inna Allaha kana AAazeezan hakeeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 57, "But those who believe and do righteous deeds - We will admit them to gardens beneath which rivers flow, wherein they abide forever. For them therein are purified spouses, and We will admit them to deepening shade", "وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ سَنُدۡخِلُهُمۡ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَآ أَبَدٗاۖ لَّهُمۡ فِيهَآ أَزۡوَٰجٞ مُّطَهَّرَةٞۖ وَنُدۡخِلُهُمۡ ظِلّٗا ظَلِيلًا", "Waallatheena amanoo waAAamiloo alssalihati sanudkhiluhum jannatin tajree min tahtiha alanharu khalideena feeha abadan lahum feeha azwajun mutahharatun wanudkhiluhum thillan thaleelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 58, "Indeed, Allah commands you to render trusts to whom they are due and when you judge between people to judge with justice. Excellent is that which Allah instructs you. Indeed, Allah is ever Hearing and Seeing", "۞إِنَّ ٱللَّهَ يَأۡمُرُكُمۡ أَن تُؤَدُّواْ ٱلۡأَمَٰنَٰتِ إِلَىٰٓ أَهۡلِهَا وَإِذَا حَكَمۡتُم بَيۡنَ ٱلنَّاسِ أَن تَحۡكُمُواْ بِٱلۡعَدۡلِۚ إِنَّ ٱللَّهَ نِعِمَّا يَعِظُكُم بِهِۦٓۗ إِنَّ ٱللَّهَ كَانَ سَمِيعَۢا بَصِيرٗا", "Inna Allaha yamurukum an tuaddoo alamanati ila ahliha waitha hakamtum bayna alnnasi an tahkumoo bialAAadli inna Allaha niAAimma yaAAithukum bihi inna Allaha kana sameeAAan baseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 59, "O you who have believed, obey Allah and obey the Messenger and those in authority among you. And if you disagree over anything, refer it to Allah and the Messenger, if you should believe in Allah and the Last Day. That is the best [way] and best in result", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ أَطِيعُواْ ٱللَّهَ وَأَطِيعُواْ ٱلرَّسُولَ وَأُوْلِي ٱلۡأَمۡرِ مِنكُمۡۖ فَإِن تَنَٰزَعۡتُمۡ فِي شَيۡءٖ فَرُدُّوهُ إِلَى ٱللَّهِ وَٱلرَّسُولِ إِن كُنتُمۡ تُؤۡمِنُونَ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِۚ ذَٰلِكَ خَيۡرٞ وَأَحۡسَنُ تَأۡوِيلًا", "Ya ayyuha allatheena amanoo ateeAAoo Allaha waateeAAoo alrrasoola waolee alamri minkum fain tanazaAAtum fee shayin faruddoohu ila Allahi waalrrasooli in kuntum tuminoona biAllahi waalyawmi alakhiri thalika khayrun waahsanu taweelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 60, "Have you not seen those who claim to have believed in what was revealed to you, [O Muhammad], and what was revealed before you? They wish to refer legislation to Taghut, while they were commanded to reject it; and Satan wishes to lead them far astray", "أَلَمۡ تَرَ إِلَى ٱلَّذِينَ يَزۡعُمُونَ أَنَّهُمۡ ءَامَنُواْ بِمَآ أُنزِلَ إِلَيۡكَ وَمَآ أُنزِلَ مِن قَبۡلِكَ يُرِيدُونَ أَن يَتَحَاكَمُوٓاْ إِلَى ٱلطَّـٰغُوتِ وَقَدۡ أُمِرُوٓاْ أَن يَكۡفُرُواْ بِهِۦۖ وَيُرِيدُ ٱلشَّيۡطَٰنُ أَن يُضِلَّهُمۡ ضَلَٰلَۢا بَعِيدٗا", "Alam tara ila allatheena yazAAumoona annahum amanoo bima onzila ilayka wama onzila min qablika yureedoona an yatahakamoo ila alttaghooti waqad omiroo an yakfuroo bihi wayureedu alshshaytanu an yudillahum dalalan baAAeedan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 61, "And when it is said to them, ""Come to what Allah has revealed and to the Messenger,"" you see the hypocrites turning away from you in aversion", "وَإِذَا قِيلَ لَهُمۡ تَعَالَوۡاْ إِلَىٰ مَآ أَنزَلَ ٱللَّهُ وَإِلَى ٱلرَّسُولِ رَأَيۡتَ ٱلۡمُنَٰفِقِينَ يَصُدُّونَ عَنكَ صُدُودٗا", "Waitha qeela lahum taAAalaw ila ma anzala Allahu waila alrrasooli raayta almunafiqeena yasuddoona AAanka sudoodan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 62, "So how [will it be] when disaster strikes them because of what their hands have put forth and then they come to you swearing by Allah, ""We intended nothing but good conduct and accommodation", "فَكَيۡفَ إِذَآ أَصَٰبَتۡهُم مُّصِيبَةُۢ بِمَا قَدَّمَتۡ أَيۡدِيهِمۡ ثُمَّ جَآءُوكَ يَحۡلِفُونَ بِٱللَّهِ إِنۡ أَرَدۡنَآ إِلَّآ إِحۡسَٰنٗا وَتَوۡفِيقًا", "Fakayfa itha asabathum museebatun bima qaddamat aydeehim thumma jaooka yahlifoona biAllahi in aradna illa ihsanan watawfeeqan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 63, "Those are the ones of whom Allah knows what is in their hearts, so turn away from them but admonish them and speak to them a far-reaching word", "أُوْلَـٰٓئِكَ ٱلَّذِينَ يَعۡلَمُ ٱللَّهُ مَا فِي قُلُوبِهِمۡ فَأَعۡرِضۡ عَنۡهُمۡ وَعِظۡهُمۡ وَقُل لَّهُمۡ فِيٓ أَنفُسِهِمۡ قَوۡلَۢا بَلِيغٗا", "Olaika allatheena yaAAlamu Allahu ma fee quloobihim faaAArid AAanhum waAAithhum waqul lahum fee anfusihim qawlan baleeghan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 64, "And We did not send any messenger except to be obeyed by permission of Allah. And if, when they wronged themselves, they had come to you, [O Muhammad], and asked forgiveness of Allah and the Messenger had asked forgiveness for them, they would have found Allah Accepting of repentance and Merciful", "وَمَآ أَرۡسَلۡنَا مِن رَّسُولٍ إِلَّا لِيُطَاعَ بِإِذۡنِ ٱللَّهِۚ وَلَوۡ أَنَّهُمۡ إِذ ظَّلَمُوٓاْ أَنفُسَهُمۡ جَآءُوكَ فَٱسۡتَغۡفَرُواْ ٱللَّهَ وَٱسۡتَغۡفَرَ لَهُمُ ٱلرَّسُولُ لَوَجَدُواْ ٱللَّهَ تَوَّابٗا رَّحِيمٗا", "Wama arsalna min rasoolin illa liyutaAAa biithni Allahi walaw annahum ith thalamoo anfusahum jaooka faistaghfaroo Allaha waistaghfara lahumu alrrasoolu lawajadoo Allaha tawwaban raheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 65, "But no, by your Lord, they will not [truly] believe until they make you, [O Muhammad], judge concerning that over which they dispute among themselves and then find within themselves no discomfort from what you have judged and submit in [full, willing] submission", "فَلَا وَرَبِّكَ لَا يُؤۡمِنُونَ حَتَّىٰ يُحَكِّمُوكَ فِيمَا شَجَرَ بَيۡنَهُمۡ ثُمَّ لَا يَجِدُواْ فِيٓ أَنفُسِهِمۡ حَرَجٗا مِّمَّا قَضَيۡتَ وَيُسَلِّمُواْ تَسۡلِيمٗا", "Fala warabbika la yuminoona hatta yuhakkimooka feema shajara baynahum thumma la yajidoo fee anfusihim harajan mimma qadayta wayusallimoo tasleeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 66, "And if We had decreed upon them, ""Kill yourselves"" or ""Leave your homes,"" they would not have done it, except for a few of them. But if they had done what they were instructed, it would have been better for them and a firmer position [for them in faith]", "وَلَوۡ أَنَّا كَتَبۡنَا عَلَيۡهِمۡ أَنِ ٱقۡتُلُوٓاْ أَنفُسَكُمۡ أَوِ ٱخۡرُجُواْ مِن دِيَٰرِكُم مَّا فَعَلُوهُ إِلَّا قَلِيلٞ مِّنۡهُمۡۖ وَلَوۡ أَنَّهُمۡ فَعَلُواْ مَا يُوعَظُونَ بِهِۦ لَكَانَ خَيۡرٗا لَّهُمۡ وَأَشَدَّ تَثۡبِيتٗا", "Walaw anna katabna AAalayhim ani oqtuloo anfusakum awi okhrujoo min diyarikum ma faAAaloohu illa qaleelun minhum walaw annahum faAAaloo ma yooAAathoona bihi lakana khayran lahum waashadda tathbeetan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 67, "And then We would have given them from Us a great reward", "وَإِذٗا لَّأٓتَيۡنَٰهُم مِّن لَّدُنَّآ أَجۡرًا عَظِيمٗا", "Waithan laataynahum min ladunna ajran AAatheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 68, "And We would have guided them to a straight path", "وَلَهَدَيۡنَٰهُمۡ صِرَٰطٗا مُّسۡتَقِيمٗا", "Walahadaynahum siratan mustaqeeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 69, "And whoever obeys Allah and the Messenger - those will be with the ones upon whom Allah has bestowed favor of the prophets, the steadfast affirmers of truth, the martyrs and the righteous. And excellent are those as companions", "وَمَن يُطِعِ ٱللَّهَ وَٱلرَّسُولَ فَأُوْلَـٰٓئِكَ مَعَ ٱلَّذِينَ أَنۡعَمَ ٱللَّهُ عَلَيۡهِم مِّنَ ٱلنَّبِيِّـۧنَ وَٱلصِّدِّيقِينَ وَٱلشُّهَدَآءِ وَٱلصَّـٰلِحِينَۚ وَحَسُنَ أُوْلَـٰٓئِكَ رَفِيقٗا", "Waman yutiAAi Allaha waalrrasoola faolaika maAAa allatheena anAAama Allahu AAalayhim mina alnnabiyyeena waalssiddeeqeena waalshshuhadai waalssaliheena wahasuna olaika rafeeqan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 70, "That is the bounty from Allah, and sufficient is Allah as Knower", "ذَٰلِكَ ٱلۡفَضۡلُ مِنَ ٱللَّهِۚ وَكَفَىٰ بِٱللَّهِ عَلِيمٗا", "Thalika alfadlu mina Allahi wakafa biAllahi AAaleeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 71, "O you who have believed, take your precaution and [either] go forth in companies or go forth all together", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ خُذُواْ حِذۡرَكُمۡ فَٱنفِرُواْ ثُبَاتٍ أَوِ ٱنفِرُواْ جَمِيعٗا", "Ya ayyuha allatheena amanoo khuthoo hithrakum fainfiroo thubatin awi infiroo jameeAAan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 72, "And indeed, there is among you he who lingers behind; and if disaster strikes you, he says, ""Allah has favored me in that I was not present with them", "وَإِنَّ مِنكُمۡ لَمَن لَّيُبَطِّئَنَّ فَإِنۡ أَصَٰبَتۡكُم مُّصِيبَةٞ قَالَ قَدۡ أَنۡعَمَ ٱللَّهُ عَلَيَّ إِذۡ لَمۡ أَكُن مَّعَهُمۡ شَهِيدٗا", "Wainna minkum laman layubattianna fain asabatkum museebatun qala qad anAAama Allahu AAalayya ith lam akun maAAahum shaheedan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 73, "But if bounty comes to you from Allah, he will surely say, as if there had never been between you and him any affection. ""Oh, I wish I had been with them so I could have attained a great attainment", "وَلَئِنۡ أَصَٰبَكُمۡ فَضۡلٞ مِّنَ ٱللَّهِ لَيَقُولَنَّ كَأَن لَّمۡ تَكُنۢ بَيۡنَكُمۡ وَبَيۡنَهُۥ مَوَدَّةٞ يَٰلَيۡتَنِي كُنتُ مَعَهُمۡ فَأَفُوزَ فَوۡزًا عَظِيمٗا", "Walain asabakum fadlun mina Allahi layaqoolanna kaan lam takun baynakum wabaynahu mawaddatun ya laytanee kuntu maAAahum faafooza fawzan AAatheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 74, "So let those fight in the cause of Allah who sell the life of this world for the Hereafter. And he who fights in the cause of Allah and is killed or achieves victory - We will bestow upon him a great reward", "۞فَلۡيُقَٰتِلۡ فِي سَبِيلِ ٱللَّهِ ٱلَّذِينَ يَشۡرُونَ ٱلۡحَيَوٰةَ ٱلدُّنۡيَا بِٱلۡأٓخِرَةِۚ وَمَن يُقَٰتِلۡ فِي سَبِيلِ ٱللَّهِ فَيُقۡتَلۡ أَوۡ يَغۡلِبۡ فَسَوۡفَ نُؤۡتِيهِ أَجۡرًا عَظِيمٗا", "Falyuqatil fee sabeeli Allahi allatheena yashroona alhayata alddunya bialakhirati waman yuqatil fee sabeeli Allahi fayuqtal aw yaghlib fasawfa nuteehi ajran AAatheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 75, "And what is [the matter] with you that you fight not in the cause of Allah and [for] the oppressed among men, women, and children who say, ""Our Lord, take us out of this city of oppressive people and appoint for us from Yourself a protector and appoint for us from Yourself a helper", "وَمَا لَكُمۡ لَا تُقَٰتِلُونَ فِي سَبِيلِ ٱللَّهِ وَٱلۡمُسۡتَضۡعَفِينَ مِنَ ٱلرِّجَالِ وَٱلنِّسَآءِ وَٱلۡوِلۡدَٰنِ ٱلَّذِينَ يَقُولُونَ رَبَّنَآ أَخۡرِجۡنَا مِنۡ هَٰذِهِ ٱلۡقَرۡيَةِ ٱلظَّالِمِ أَهۡلُهَا وَٱجۡعَل لَّنَا مِن لَّدُنكَ وَلِيّٗا وَٱجۡعَل لَّنَا مِن لَّدُنكَ نَصِيرًا", "Wama lakum la tuqatiloona fee sabeeli Allahi waalmustadAAafeena mina alrrijali waalnnisai waalwildani allatheena yaqooloona rabbana akhrijna min hathihi alqaryati alththalimi ahluha waijAAal lana min ladunka waliyyan waijAAal lana min ladunka naseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 76, "Those who believe fight in the cause of Allah, and those who disbelieve fight in the cause of Taghut. So fight against the allies of Satan. Indeed, the plot of Satan has ever been weak", "ٱلَّذِينَ ءَامَنُواْ يُقَٰتِلُونَ فِي سَبِيلِ ٱللَّهِۖ وَٱلَّذِينَ كَفَرُواْ يُقَٰتِلُونَ فِي سَبِيلِ ٱلطَّـٰغُوتِ فَقَٰتِلُوٓاْ أَوۡلِيَآءَ ٱلشَّيۡطَٰنِۖ إِنَّ كَيۡدَ ٱلشَّيۡطَٰنِ كَانَ ضَعِيفًا", "Allatheena amanoo yuqatiloona fee sabeeli Allahi waallatheena kafaroo yuqatiloona fee sabeeli alttaghooti faqatiloo awliyaa alshshaytani inna kayda alshshaytani kana daAAeefan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 77, "Have you not seen those who were told, ""Restrain your hands [from fighting] and establish prayer and give zakah""? But then when fighting was ordained for them, at once a party of them feared men as they fear Allah or with [even] greater fear. They said, ""Our Lord, why have You decreed upon us fighting? If only You had postponed [it for] us for a short time."" Say, The enjoyment of this world is little, and the Hereafter is better for he who fears Allah. And injustice will not be done to you, [even] as much as a thread [inside a date seed]", "أَلَمۡ تَرَ إِلَى ٱلَّذِينَ قِيلَ لَهُمۡ كُفُّوٓاْ أَيۡدِيَكُمۡ وَأَقِيمُواْ ٱلصَّلَوٰةَ وَءَاتُواْ ٱلزَّكَوٰةَ فَلَمَّا كُتِبَ عَلَيۡهِمُ ٱلۡقِتَالُ إِذَا فَرِيقٞ مِّنۡهُمۡ يَخۡشَوۡنَ ٱلنَّاسَ كَخَشۡيَةِ ٱللَّهِ أَوۡ أَشَدَّ خَشۡيَةٗۚ وَقَالُواْ رَبَّنَا لِمَ كَتَبۡتَ عَلَيۡنَا ٱلۡقِتَالَ لَوۡلَآ أَخَّرۡتَنَآ إِلَىٰٓ أَجَلٖ قَرِيبٖۗ قُلۡ مَتَٰعُ ٱلدُّنۡيَا قَلِيلٞ وَٱلۡأٓخِرَةُ خَيۡرٞ لِّمَنِ ٱتَّقَىٰ وَلَا تُظۡلَمُونَ فَتِيلًا", "Alam tara ila allatheena qeela lahum kuffoo aydiyakum waaqeemoo alssalata waatoo alzzakata falamma kutiba AAalayhimu alqitalu itha fareequn minhum yakhshawna alnnasa kakhashyati Allahi aw ashadda khashyatan waqaloo rabbana lima katabta AAalayna alqitala lawla akhkhartana ila ajalin qareebin qul mataAAu alddunya qaleelun waalakhiratu khayrun limani ittaqa wala tuthlamoona fateelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 78, "Wherever you may be, death will overtake you, even if you should be within towers of lofty construction. But if good comes to them, they say, ""This is from Allah ""; and if evil befalls them, they say, ""This is from you."" Say, ""All [things] are from Allah."" So what is [the matter] with those people that they can hardly understand any statement", "أَيۡنَمَا تَكُونُواْ يُدۡرِككُّمُ ٱلۡمَوۡتُ وَلَوۡ كُنتُمۡ فِي بُرُوجٖ مُّشَيَّدَةٖۗ وَإِن تُصِبۡهُمۡ حَسَنَةٞ يَقُولُواْ هَٰذِهِۦ مِنۡ عِندِ ٱللَّهِۖ وَإِن تُصِبۡهُمۡ سَيِّئَةٞ يَقُولُواْ هَٰذِهِۦ مِنۡ عِندِكَۚ قُلۡ كُلّٞ مِّنۡ عِندِ ٱللَّهِۖ فَمَالِ هَـٰٓؤُلَآءِ ٱلۡقَوۡمِ لَا يَكَادُونَ يَفۡقَهُونَ حَدِيثٗا", "Aynama takoonoo yudrikkumu almawtu walaw kuntum fee buroojin mushayyadatin wain tusibhum hasanatun yaqooloo hathihi min AAindi Allahi wain tusibhum sayyiatun yaqooloo hathihi min AAindika qul kullun min AAindi Allahi famali haolai alqawmi la yakadoona yafqahoona hadeethan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 79, "What comes to you of good is from Allah, but what comes to you of evil, [O man], is from yourself. And We have sent you, [O Muhammad], to the people as a messenger, and sufficient is Allah as Witness", "مَّآ أَصَابَكَ مِنۡ حَسَنَةٖ فَمِنَ ٱللَّهِۖ وَمَآ أَصَابَكَ مِن سَيِّئَةٖ فَمِن نَّفۡسِكَۚ وَأَرۡسَلۡنَٰكَ لِلنَّاسِ رَسُولٗاۚ وَكَفَىٰ بِٱللَّهِ شَهِيدٗا", "Ma asabaka min hasanatin famina Allahi wama asabaka min sayyiatin famin nafsika waarsalnaka lilnnasi rasoolan wakafa biAllahi shaheedan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 80, "He who obeys the Messenger has obeyed Allah; but those who turn away - We have not sent you over them as a guardian", "مَّن يُطِعِ ٱلرَّسُولَ فَقَدۡ أَطَاعَ ٱللَّهَۖ وَمَن تَوَلَّىٰ فَمَآ أَرۡسَلۡنَٰكَ عَلَيۡهِمۡ حَفِيظٗا", "Man yutiAAi alrrasoola faqad ataAAa Allaha waman tawalla fama arsalnaka AAalayhim hafeethan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 81, "And they say, ""[We pledge] obedience."" But when they leave you, a group of them spend the night determining to do other than what you say. But Allah records what they plan by night. So leave them alone and rely upon Allah. And sufficient is Allah as Disposer of affairs", "وَيَقُولُونَ طَاعَةٞ فَإِذَا بَرَزُواْ مِنۡ عِندِكَ بَيَّتَ طَآئِفَةٞ مِّنۡهُمۡ غَيۡرَ ٱلَّذِي تَقُولُۖ وَٱللَّهُ يَكۡتُبُ مَا يُبَيِّتُونَۖ فَأَعۡرِضۡ عَنۡهُمۡ وَتَوَكَّلۡ عَلَى ٱللَّهِۚ وَكَفَىٰ بِٱللَّهِ وَكِيلًا", "Wayaqooloona taAAatun faitha barazoo min AAindika bayyata taifatun minhum ghayra allathee taqoolu waAllahu yaktubu ma yubayyitoona faaAArid AAanhum watawakkal AAala Allahi wakafa biAllahi wakeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 82, "Then do they not reflect upon the Qur'an? If it had been from [any] other than Allah, they would have found within it much contradiction", "أَفَلَا يَتَدَبَّرُونَ ٱلۡقُرۡءَانَۚ وَلَوۡ كَانَ مِنۡ عِندِ غَيۡرِ ٱللَّهِ لَوَجَدُواْ فِيهِ ٱخۡتِلَٰفٗا كَثِيرٗا", "Afala yatadabbaroona alqurana walaw kana min AAindi ghayri Allahi lawajadoo feehi ikhtilafan katheeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 83, "And when there comes to them information about [public] security or fear, they spread it around. But if they had referred it back to the Messenger or to those of authority among them, then the ones who [can] draw correct conclusions from it would have known about it. And if not for the favor of Allah upon you and His mercy, you would have followed Satan, except for a few", "وَإِذَا جَآءَهُمۡ أَمۡرٞ مِّنَ ٱلۡأَمۡنِ أَوِ ٱلۡخَوۡفِ أَذَاعُواْ بِهِۦۖ وَلَوۡ رَدُّوهُ إِلَى ٱلرَّسُولِ وَإِلَىٰٓ أُوْلِي ٱلۡأَمۡرِ مِنۡهُمۡ لَعَلِمَهُ ٱلَّذِينَ يَسۡتَنۢبِطُونَهُۥ مِنۡهُمۡۗ وَلَوۡلَا فَضۡلُ ٱللَّهِ عَلَيۡكُمۡ وَرَحۡمَتُهُۥ لَٱتَّبَعۡتُمُ ٱلشَّيۡطَٰنَ إِلَّا قَلِيلٗا", "Waitha jaahum amrun mina alamni awi alkhawfi athaAAoo bihi walaw raddoohu ila alrrasooli waila olee alamri minhum laAAalimahu allatheena yastanbitoonahu minhum walawla fadlu Allahi AAalaykum warahmatuhu laittabaAAtumu alshshaytana illa qaleelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 84, "So fight, [O Muhammad], in the cause of Allah; you are not held responsible except for yourself. And encourage the believers [to join you] that perhaps Allah will restrain the [military] might of those who disbelieve. And Allah is greater in might and stronger in [exemplary] punishment", "فَقَٰتِلۡ فِي سَبِيلِ ٱللَّهِ لَا تُكَلَّفُ إِلَّا نَفۡسَكَۚ وَحَرِّضِ ٱلۡمُؤۡمِنِينَۖ عَسَى ٱللَّهُ أَن يَكُفَّ بَأۡسَ ٱلَّذِينَ كَفَرُواْۚ وَٱللَّهُ أَشَدُّ بَأۡسٗا وَأَشَدُّ تَنكِيلٗا", "Faqatil fee sabeeli Allahi la tukallafu illa nafsaka waharridi almumineena AAasa Allahu an yakuffa basa allatheena kafaroo waAllahu ashaddu basan waashaddu tankeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 85, "Whoever intercedes for a good cause will have a reward therefrom; and whoever intercedes for an evil cause will have a burden therefrom. And ever is Allah, over all things, a Keeper", "مَّن يَشۡفَعۡ شَفَٰعَةً حَسَنَةٗ يَكُن لَّهُۥ نَصِيبٞ مِّنۡهَاۖ وَمَن يَشۡفَعۡ شَفَٰعَةٗ سَيِّئَةٗ يَكُن لَّهُۥ كِفۡلٞ مِّنۡهَاۗ وَكَانَ ٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ مُّقِيتٗا", "Man yashfaAA shafaAAatan hasanatan yakun lahu naseebun minha waman yashfaAA shafaAAatan sayyiatan yakun lahu kiflun minha wakana Allahu AAala kulli shayin muqeetan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 86, "And when you are greeted with a greeting, greet [in return] with one better than it or [at least] return it [in a like manner]. Indeed, Allah is ever, over all things, an Accountant", "وَإِذَا حُيِّيتُم بِتَحِيَّةٖ فَحَيُّواْ بِأَحۡسَنَ مِنۡهَآ أَوۡ رُدُّوهَآۗ إِنَّ ٱللَّهَ كَانَ عَلَىٰ كُلِّ شَيۡءٍ حَسِيبًا", "Waitha huyyeetum bitahiyyatin fahayyoo biahsana minha aw ruddooha inna Allaha kana AAala kulli shayin haseeban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 87, "Allah - there is no deity except Him. He will surely assemble you for [account on] the Day of Resurrection, about which there is no doubt. And who is more truthful than Allah in statement", "ٱللَّهُ لَآ إِلَٰهَ إِلَّا هُوَۚ لَيَجۡمَعَنَّكُمۡ إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِ لَا رَيۡبَ فِيهِۗ وَمَنۡ أَصۡدَقُ مِنَ ٱللَّهِ حَدِيثٗا", "Allahu la ilaha illa huwa layajmaAAannakum ila yawmi alqiyamati la rayba feehi waman asdaqu mina Allahi hadeethan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 88, "What is [the matter] with you [that you are] two groups concerning the hypocrites, while Allah has made them fall back [into error and disbelief] for what they earned. Do you wish to guide those whom Allah has sent astray? And he whom Allah sends astray - never will you find for him a way [of guidance]", "۞فَمَا لَكُمۡ فِي ٱلۡمُنَٰفِقِينَ فِئَتَيۡنِ وَٱللَّهُ أَرۡكَسَهُم بِمَا كَسَبُوٓاْۚ أَتُرِيدُونَ أَن تَهۡدُواْ مَنۡ أَضَلَّ ٱللَّهُۖ وَمَن يُضۡلِلِ ٱللَّهُ فَلَن تَجِدَ لَهُۥ سَبِيلٗا", "Fama lakum fee almunafiqeena fiatayni waAllahu arkasahum bima kasaboo atureedoona an tahdoo man adalla Allahu waman yudlili Allahu falan tajida lahu sabeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 89, "They wish you would disbelieve as they disbelieved so you would be alike. So do not take from among them allies until they emigrate for the cause of Allah. But if they turn away, then seize them and kill them wherever you find them and take not from among them any ally or helper", "وَدُّواْ لَوۡ تَكۡفُرُونَ كَمَا كَفَرُواْ فَتَكُونُونَ سَوَآءٗۖ فَلَا تَتَّخِذُواْ مِنۡهُمۡ أَوۡلِيَآءَ حَتَّىٰ يُهَاجِرُواْ فِي سَبِيلِ ٱللَّهِۚ فَإِن تَوَلَّوۡاْ فَخُذُوهُمۡ وَٱقۡتُلُوهُمۡ حَيۡثُ وَجَدتُّمُوهُمۡۖ وَلَا تَتَّخِذُواْ مِنۡهُمۡ وَلِيّٗا وَلَا نَصِيرًا", "Waddoo law takfuroona kama kafaroo fatakoonoona sawaan fala tattakhithoo minhum awliyaa hatta yuhajiroo fee sabeeli Allahi fain tawallaw fakhuthoohum waoqtuloohum haythu wajadtumoohum wala tattakhithoo minhum waliyyan wala naseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 90, "Except for those who take refuge with a people between yourselves and whom is a treaty or those who come to you, their hearts strained at [the prospect of] fighting you or fighting their own people. And if Allah had willed, He could have given them power over you, and they would have fought you. So if they remove themselves from you and do not fight you and offer you peace, then Allah has not made for you a cause [for fighting] against them", "إِلَّا ٱلَّذِينَ يَصِلُونَ إِلَىٰ قَوۡمِۭ بَيۡنَكُمۡ وَبَيۡنَهُم مِّيثَٰقٌ أَوۡ جَآءُوكُمۡ حَصِرَتۡ صُدُورُهُمۡ أَن يُقَٰتِلُوكُمۡ أَوۡ يُقَٰتِلُواْ قَوۡمَهُمۡۚ وَلَوۡ شَآءَ ٱللَّهُ لَسَلَّطَهُمۡ عَلَيۡكُمۡ فَلَقَٰتَلُوكُمۡۚ فَإِنِ ٱعۡتَزَلُوكُمۡ فَلَمۡ يُقَٰتِلُوكُمۡ وَأَلۡقَوۡاْ إِلَيۡكُمُ ٱلسَّلَمَ فَمَا جَعَلَ ٱللَّهُ لَكُمۡ عَلَيۡهِمۡ سَبِيلٗا", "Illa allatheena yasiloona ila qawmin baynakum wabaynahum meethaqun aw jaookum hasirat sudooruhum an yuqatilookum aw yuqatiloo qawmahum walaw shaa Allahu lasallatahum AAalaykum falaqatalookum faini iAAtazalookum falam yuqatilookum waalqaw ilaykumu alssalama fama jaAAala Allahu lakum AAalayhim sabeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 91, "You will find others who wish to obtain security from you and [to] obtain security from their people. Every time they are returned to [the influence of] disbelief, they fall back into it. So if they do not withdraw from you or offer you peace or restrain their hands, then seize them and kill them wherever you overtake them. And those - We have made for you against them a clear authorization", "سَتَجِدُونَ ءَاخَرِينَ يُرِيدُونَ أَن يَأۡمَنُوكُمۡ وَيَأۡمَنُواْ قَوۡمَهُمۡ كُلَّ مَا رُدُّوٓاْ إِلَى ٱلۡفِتۡنَةِ أُرۡكِسُواْ فِيهَاۚ فَإِن لَّمۡ يَعۡتَزِلُوكُمۡ وَيُلۡقُوٓاْ إِلَيۡكُمُ ٱلسَّلَمَ وَيَكُفُّوٓاْ أَيۡدِيَهُمۡ فَخُذُوهُمۡ وَٱقۡتُلُوهُمۡ حَيۡثُ ثَقِفۡتُمُوهُمۡۚ وَأُوْلَـٰٓئِكُمۡ جَعَلۡنَا لَكُمۡ عَلَيۡهِمۡ سُلۡطَٰنٗا مُّبِينٗا", "Satajidoona akhareena yureedoona an yamanookum wayamanoo qawmahum kulla ma ruddoo ila alfitnati orkisoo feeha fain lam yaAAtazilookum wayulqoo ilaykumu alssalama wayakuffoo aydiyahum fakhuthoohum waoqtuloohum haythu thaqiftumoohum waolaikum jaAAalna lakum AAalayhim sultanan mubeenan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 92, "And never is it for a believer to kill a believer except by mistake. And whoever kills a believer by mistake - then the freeing of a believing slave and a compensation payment presented to the deceased's family [is required] unless they give [up their right as] charity. But if the deceased was from a people at war with you and he was a believer - then [only] the freeing of a believing slave; and if he was from a people with whom you have a treaty - then a compensation payment presented to his family and the freeing of a believing slave. And whoever does not find [one or cannot afford to buy one] - then [instead], a fast for two months consecutively, [seeking] acceptance of repentance from Allah. And Allah is ever Knowing and Wise", "وَمَا كَانَ لِمُؤۡمِنٍ أَن يَقۡتُلَ مُؤۡمِنًا إِلَّا خَطَـٔٗاۚ وَمَن قَتَلَ مُؤۡمِنًا خَطَـٔٗا فَتَحۡرِيرُ رَقَبَةٖ مُّؤۡمِنَةٖ وَدِيَةٞ مُّسَلَّمَةٌ إِلَىٰٓ أَهۡلِهِۦٓ إِلَّآ أَن يَصَّدَّقُواْۚ فَإِن كَانَ مِن قَوۡمٍ عَدُوّٖ لَّكُمۡ وَهُوَ مُؤۡمِنٞ فَتَحۡرِيرُ رَقَبَةٖ مُّؤۡمِنَةٖۖ وَإِن كَانَ مِن قَوۡمِۭ بَيۡنَكُمۡ وَبَيۡنَهُم مِّيثَٰقٞ فَدِيَةٞ مُّسَلَّمَةٌ إِلَىٰٓ أَهۡلِهِۦ وَتَحۡرِيرُ رَقَبَةٖ مُّؤۡمِنَةٖۖ فَمَن لَّمۡ يَجِدۡ فَصِيَامُ شَهۡرَيۡنِ مُتَتَابِعَيۡنِ تَوۡبَةٗ مِّنَ ٱللَّهِۗ وَكَانَ ٱللَّهُ عَلِيمًا حَكِيمٗا", "Wama kana limuminin an yaqtula muminan illa khataan waman qatala muminan khataan fatahreeru raqabatin muminatin wadiyatun musallamatun ila ahlihi illa an yassaddaqoo fain kana min qawmin AAaduwwin lakum wahuwa muminun fatahreeru raqabatin muminatin wain kana min qawmin baynakum wabaynahum meethaqun fadiyatun musallamatun ila ahlihi watahreeru raqabatin muminatin faman lam yajid fasiyamu shahrayni mutatabiAAayni tawbatan mina Allahi wakana Allahu AAaleeman hakeeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 93, "But whoever kills a believer intentionally - his recompense is Hell, wherein he will abide eternally, and Allah has become angry with him and has cursed him and has prepared for him a great punishment", "وَمَن يَقۡتُلۡ مُؤۡمِنٗا مُّتَعَمِّدٗا فَجَزَآؤُهُۥ جَهَنَّمُ خَٰلِدٗا فِيهَا وَغَضِبَ ٱللَّهُ عَلَيۡهِ وَلَعَنَهُۥ وَأَعَدَّ لَهُۥ عَذَابًا عَظِيمٗا", "Waman yaqtul muminan mutaAAammidan fajazaohu jahannamu khalidan feeha waghadiba Allahu AAalayhi walaAAanahu waaAAadda lahu AAathaban AAatheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 94, "O you who have believed, when you go forth [to fight] in the cause of Allah, investigate; and do not say to one who gives you [a greeting of] peace ""You are not a believer,"" aspiring for the goods of worldly life; for with Allah are many acquisitions. You [yourselves] were like that before; then Allah conferred His favor upon you, so investigate. Indeed Allah is ever, with what you do, Acquainted", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِذَا ضَرَبۡتُمۡ فِي سَبِيلِ ٱللَّهِ فَتَبَيَّنُواْ وَلَا تَقُولُواْ لِمَنۡ أَلۡقَىٰٓ إِلَيۡكُمُ ٱلسَّلَٰمَ لَسۡتَ مُؤۡمِنٗا تَبۡتَغُونَ عَرَضَ ٱلۡحَيَوٰةِ ٱلدُّنۡيَا فَعِندَ ٱللَّهِ مَغَانِمُ كَثِيرَةٞۚ كَذَٰلِكَ كُنتُم مِّن قَبۡلُ فَمَنَّ ٱللَّهُ عَلَيۡكُمۡ فَتَبَيَّنُوٓاْۚ إِنَّ ٱللَّهَ كَانَ بِمَا تَعۡمَلُونَ خَبِيرٗا", "Ya ayyuha allatheena amanoo itha darabtum fee sabeeli Allahi fatabayyanoo wala taqooloo liman alqa ilaykumu alssalama lasta muminan tabtaghoona AAarada alhayati alddunya faAAinda Allahi maghanimu katheeratun kathalika kuntum min qablu famanna Allahu AAalaykum fatabayyanoo inna Allaha kana bima taAAmaloona khabeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 95, "Not equal are those believers remaining [at home] - other than the disabled - and the mujahideen, [who strive and fight] in the cause of Allah with their wealth and their lives. Allah has preferred the mujahideen through their wealth and their lives over those who remain [behind], by degrees. And to both Allah has promised the best [reward]. But Allah has preferred the mujahideen over those who remain [behind] with a great reward", "لَّا يَسۡتَوِي ٱلۡقَٰعِدُونَ مِنَ ٱلۡمُؤۡمِنِينَ غَيۡرُ أُوْلِي ٱلضَّرَرِ وَٱلۡمُجَٰهِدُونَ فِي سَبِيلِ ٱللَّهِ بِأَمۡوَٰلِهِمۡ وَأَنفُسِهِمۡۚ فَضَّلَ ٱللَّهُ ٱلۡمُجَٰهِدِينَ بِأَمۡوَٰلِهِمۡ وَأَنفُسِهِمۡ عَلَى ٱلۡقَٰعِدِينَ دَرَجَةٗۚ وَكُلّٗا وَعَدَ ٱللَّهُ ٱلۡحُسۡنَىٰۚ وَفَضَّلَ ٱللَّهُ ٱلۡمُجَٰهِدِينَ عَلَى ٱلۡقَٰعِدِينَ أَجۡرًا عَظِيمٗا", "La yastawee alqaAAidoona mina almumineena ghayru olee alddarari waalmujahidoona fee sabeeli Allahi biamwalihim waanfusihim faddala Allahu almujahideena biamwalihim waanfusihim AAala alqaAAideena darajatan wakullan waAAada Allahu alhusna wafaddala Allahu almujahideena AAala alqaAAideena ajran AAatheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 96, "Degrees [of high position] from Him and forgiveness and mercy. And Allah is ever Forgiving and Merciful", "دَرَجَٰتٖ مِّنۡهُ وَمَغۡفِرَةٗ وَرَحۡمَةٗۚ وَكَانَ ٱللَّهُ غَفُورٗا رَّحِيمًا", "Darajatin minhu wamaghfiratan warahmatan wakana Allahu ghafooran raheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 97, "Indeed, those whom the angels take [in death] while wronging themselves - [the angels] will say, ""In what [condition] were you?"" They will say, ""We were oppressed in the land."" The angels will say, ""Was not the earth of Allah spacious [enough] for you to emigrate therein?"" For those, their refuge is Hell - and evil it is as a destination", "إِنَّ ٱلَّذِينَ تَوَفَّىٰهُمُ ٱلۡمَلَـٰٓئِكَةُ ظَالِمِيٓ أَنفُسِهِمۡ قَالُواْ فِيمَ كُنتُمۡۖ قَالُواْ كُنَّا مُسۡتَضۡعَفِينَ فِي ٱلۡأَرۡضِۚ قَالُوٓاْ أَلَمۡ تَكُنۡ أَرۡضُ ٱللَّهِ وَٰسِعَةٗ فَتُهَاجِرُواْ فِيهَاۚ فَأُوْلَـٰٓئِكَ مَأۡوَىٰهُمۡ جَهَنَّمُۖ وَسَآءَتۡ مَصِيرًا", "Inna allatheena tawaffahumu almalaikatu thalimee anfusihim qaloo feema kuntum qaloo kunna mustadAAafeena fee alardi qaloo alam takun ardu Allahi wasiAAatan fatuhajiroo feeha faolaika mawahum jahannamu wasaat maseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 98, "Except for the oppressed among men, women and children who cannot devise a plan nor are they directed to a way", "إِلَّا ٱلۡمُسۡتَضۡعَفِينَ مِنَ ٱلرِّجَالِ وَٱلنِّسَآءِ وَٱلۡوِلۡدَٰنِ لَا يَسۡتَطِيعُونَ حِيلَةٗ وَلَا يَهۡتَدُونَ سَبِيلٗا", "Illa almustadAAafeena mina alrrijali waalnnisai waalwildani la yastateeAAoona heelatan wala yahtadoona sabeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 99, "For those it is expected that Allah will pardon them, and Allah is ever Pardoning and Forgiving", "فَأُوْلَـٰٓئِكَ عَسَى ٱللَّهُ أَن يَعۡفُوَ عَنۡهُمۡۚ وَكَانَ ٱللَّهُ عَفُوًّا غَفُورٗا", "Faolaika AAasa Allahu an yaAAfuwa AAanhum wakana Allahu AAafuwwan ghafooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 100, "And whoever emigrates for the cause of Allah will find on the earth many [alternative] locations and abundance. And whoever leaves his home as an emigrant to Allah and His Messenger and then death overtakes him - his reward has already become incumbent upon Allah. And Allah is ever Forgiving and Merciful", "۞وَمَن يُهَاجِرۡ فِي سَبِيلِ ٱللَّهِ يَجِدۡ فِي ٱلۡأَرۡضِ مُرَٰغَمٗا كَثِيرٗا وَسَعَةٗۚ وَمَن يَخۡرُجۡ مِنۢ بَيۡتِهِۦ مُهَاجِرًا إِلَى ٱللَّهِ وَرَسُولِهِۦ ثُمَّ يُدۡرِكۡهُ ٱلۡمَوۡتُ فَقَدۡ وَقَعَ أَجۡرُهُۥ عَلَى ٱللَّهِۗ وَكَانَ ٱللَّهُ غَفُورٗا رَّحِيمٗا", "Waman yuhajir fee sabeeli Allahi yajid fee alardi muraghaman katheeran wasaAAatan waman yakhruj min baytihi muhajiran ila Allahi warasoolihi thumma yudrikhu almawtu faqad waqaAAa ajruhu AAala Allahi wakana Allahu ghafooran raheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 101, "And when you travel throughout the land, there is no blame upon you for shortening the prayer, [especially] if you fear that those who disbelieve may disrupt [or attack] you. Indeed, the disbelievers are ever to you a clear enemy", "وَإِذَا ضَرَبۡتُمۡ فِي ٱلۡأَرۡضِ فَلَيۡسَ عَلَيۡكُمۡ جُنَاحٌ أَن تَقۡصُرُواْ مِنَ ٱلصَّلَوٰةِ إِنۡ خِفۡتُمۡ أَن يَفۡتِنَكُمُ ٱلَّذِينَ كَفَرُوٓاْۚ إِنَّ ٱلۡكَٰفِرِينَ كَانُواْ لَكُمۡ عَدُوّٗا مُّبِينٗا", "Waitha darabtum fee alardi falaysa AAalaykum junahun an taqsuroo mina alssalati in khiftum an yaftinakumu allatheena kafaroo inna alkafireena kanoo lakum AAaduwwan mubeenan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 102, "And when you are among them and lead them in prayer, let a group of them stand [in prayer] with you and let them carry their arms. And when they have prostrated, let them be [in position] behind you and have the other group come forward which has not [yet] prayed and let them pray with you, taking precaution and carrying their arms. Those who disbelieve wish that you would neglect your weapons and your baggage so they could come down upon you in one [single] attack. But there is no blame upon you, if you are troubled by rain or are ill, for putting down your arms, but take precaution. Indeed, Allah has prepared for the disbelievers a humiliating punishment", "وَإِذَا كُنتَ فِيهِمۡ فَأَقَمۡتَ لَهُمُ ٱلصَّلَوٰةَ فَلۡتَقُمۡ طَآئِفَةٞ مِّنۡهُم مَّعَكَ وَلۡيَأۡخُذُوٓاْ أَسۡلِحَتَهُمۡۖ فَإِذَا سَجَدُواْ فَلۡيَكُونُواْ مِن وَرَآئِكُمۡ وَلۡتَأۡتِ طَآئِفَةٌ أُخۡرَىٰ لَمۡ يُصَلُّواْ فَلۡيُصَلُّواْ مَعَكَ وَلۡيَأۡخُذُواْ حِذۡرَهُمۡ وَأَسۡلِحَتَهُمۡۗ وَدَّ ٱلَّذِينَ كَفَرُواْ لَوۡ تَغۡفُلُونَ عَنۡ أَسۡلِحَتِكُمۡ وَأَمۡتِعَتِكُمۡ فَيَمِيلُونَ عَلَيۡكُم مَّيۡلَةٗ وَٰحِدَةٗۚ وَلَا جُنَاحَ عَلَيۡكُمۡ إِن كَانَ بِكُمۡ أَذٗى مِّن مَّطَرٍ أَوۡ كُنتُم مَّرۡضَىٰٓ أَن تَضَعُوٓاْ أَسۡلِحَتَكُمۡۖ وَخُذُواْ حِذۡرَكُمۡۗ إِنَّ ٱللَّهَ أَعَدَّ لِلۡكَٰفِرِينَ عَذَابٗا مُّهِينٗا", "Waitha kunta feehim faaqamta lahumu alssalata faltaqum taifatun minhum maAAaka walyakhuthoo aslihatahum faitha sajadoo falyakoonoo min waraikum waltati taifatun okhra lam yusalloo falyusalloo maAAaka walyakhuthoo hithrahum waaslihatahum wadda allatheena kafaroo law taghfuloona AAan aslihatikum waamtiAAatikum fayameeloona AAalaykum maylatan wahidatan wala junaha AAalaykum in kana bikum athan min matarin aw kuntum marda an tadaAAoo aslihatakum wakhuthoo hithrakum inna Allaha aAAadda lilkafireena AAathaban muheenan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 103, "And when you have completed the prayer, remember Allah standing, sitting, or [lying] on your sides. But when you become secure, re-establish [regular] prayer. Indeed, prayer has been decreed upon the believers a decree of specified times", "فَإِذَا قَضَيۡتُمُ ٱلصَّلَوٰةَ فَٱذۡكُرُواْ ٱللَّهَ قِيَٰمٗا وَقُعُودٗا وَعَلَىٰ جُنُوبِكُمۡۚ فَإِذَا ٱطۡمَأۡنَنتُمۡ فَأَقِيمُواْ ٱلصَّلَوٰةَۚ إِنَّ ٱلصَّلَوٰةَ كَانَتۡ عَلَى ٱلۡمُؤۡمِنِينَ كِتَٰبٗا مَّوۡقُوتٗا", "Faitha qadaytumu alssalata faothkuroo Allaha qiyaman waquAAoodan waAAala junoobikum faitha itmanantum faaqeemoo alssalata inna alssalata kanat AAala almumineena kitaban mawqootan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 104, "And do not weaken in pursuit of the enemy. If you should be suffering - so are they suffering as you are suffering, but you expect from Allah that which they expect not. And Allah is ever Knowing and Wise", "وَلَا تَهِنُواْ فِي ٱبۡتِغَآءِ ٱلۡقَوۡمِۖ إِن تَكُونُواْ تَأۡلَمُونَ فَإِنَّهُمۡ يَأۡلَمُونَ كَمَا تَأۡلَمُونَۖ وَتَرۡجُونَ مِنَ ٱللَّهِ مَا لَا يَرۡجُونَۗ وَكَانَ ٱللَّهُ عَلِيمًا حَكِيمًا", "Wala tahinoo fee ibtighai alqawmi in takoonoo talamoona fainnahum yalamoona kama talamoona watarjoona mina Allahi ma la yarjoona wakana Allahu AAaleeman hakeeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 105, "Indeed, We have revealed to you, [O Muhammad], the Book in truth so you may judge between the people by that which Allah has shown you. And do not be for the deceitful an advocate", "إِنَّآ أَنزَلۡنَآ إِلَيۡكَ ٱلۡكِتَٰبَ بِٱلۡحَقِّ لِتَحۡكُمَ بَيۡنَ ٱلنَّاسِ بِمَآ أَرَىٰكَ ٱللَّهُۚ وَلَا تَكُن لِّلۡخَآئِنِينَ خَصِيمٗا", "Inna anzalna ilayka alkitaba bialhaqqi litahkuma bayna alnnasi bima araka Allahu wala takun lilkhaineena khaseeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 106, "And seek forgiveness of Allah. Indeed, Allah is ever Forgiving and Merciful", "وَٱسۡتَغۡفِرِ ٱللَّهَۖ إِنَّ ٱللَّهَ كَانَ غَفُورٗا رَّحِيمٗا", "Waistaghfiri Allaha inna Allaha kana ghafooran raheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 107, "And do not argue on behalf of those who deceive themselves. Indeed, Allah loves not one who is a habitually sinful deceiver", "وَلَا تُجَٰدِلۡ عَنِ ٱلَّذِينَ يَخۡتَانُونَ أَنفُسَهُمۡۚ إِنَّ ٱللَّهَ لَا يُحِبُّ مَن كَانَ خَوَّانًا أَثِيمٗا", "Wala tujadil AAani allatheena yakhtanoona anfusahum inna Allaha la yuhibbu man kana khawwanan atheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 108, "They conceal [their evil intentions and deeds] from the people, but they cannot conceal [them] from Allah, and He is with them [in His knowledge] when they spend the night in such as He does not accept of speech. And ever is Allah, of what they do, encompassing", "يَسۡتَخۡفُونَ مِنَ ٱلنَّاسِ وَلَا يَسۡتَخۡفُونَ مِنَ ٱللَّهِ وَهُوَ مَعَهُمۡ إِذۡ يُبَيِّتُونَ مَا لَا يَرۡضَىٰ مِنَ ٱلۡقَوۡلِۚ وَكَانَ ٱللَّهُ بِمَا يَعۡمَلُونَ مُحِيطًا", "Yastakhfoona mina alnnasi wala yastakhfoona mina Allahi wahuwa maAAahum ith yubayyitoona ma la yarda mina alqawli wakana Allahu bima yaAAmaloona muheetan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 109, "Here you are - those who argue on their behalf in [this] worldly life - but who will argue with Allah for them on the Day of Resurrection, or who will [then] be their representative", "هَـٰٓأَنتُمۡ هَـٰٓؤُلَآءِ جَٰدَلۡتُمۡ عَنۡهُمۡ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا فَمَن يُجَٰدِلُ ٱللَّهَ عَنۡهُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ أَم مَّن يَكُونُ عَلَيۡهِمۡ وَكِيلٗا", "Haantum haolai jadaltum AAanhum fee alhayati alddunya faman yujadilu Allaha AAanhum yawma alqiyamati am man yakoonu AAalayhim wakeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 110, "And whoever does a wrong or wrongs himself but then seeks forgiveness of Allah will find Allah Forgiving and Merciful", "وَمَن يَعۡمَلۡ سُوٓءًا أَوۡ يَظۡلِمۡ نَفۡسَهُۥ ثُمَّ يَسۡتَغۡفِرِ ٱللَّهَ يَجِدِ ٱللَّهَ غَفُورٗا رَّحِيمٗا", "Waman yaAAmal sooan aw yathlim nafsahu thumma yastaghfiri Allaha yajidi Allaha ghafooran raheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 111, "And whoever commits a sin only earns it against himself. And Allah is ever Knowing and Wise", "وَمَن يَكۡسِبۡ إِثۡمٗا فَإِنَّمَا يَكۡسِبُهُۥ عَلَىٰ نَفۡسِهِۦۚ وَكَانَ ٱللَّهُ عَلِيمًا حَكِيمٗا", "Waman yaksib ithman fainnama yaksibuhu AAala nafsihi wakana Allahu AAaleeman hakeeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 112, "But whoever earns an offense or a sin and then blames it on an innocent [person] has taken upon himself a slander and manifest sin", "وَمَن يَكۡسِبۡ خَطِيٓـَٔةً أَوۡ إِثۡمٗا ثُمَّ يَرۡمِ بِهِۦ بَرِيٓـٔٗا فَقَدِ ٱحۡتَمَلَ بُهۡتَٰنٗا وَإِثۡمٗا مُّبِينٗا", "Waman yaksib khateeatan aw ithman thumma yarmi bihi bareean faqadi ihtamala buhtanan waithman mubeenan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 113, "And if it was not for the favor of Allah upon you, [O Muhammad], and His mercy, a group of them would have determined to mislead you. But they do not mislead except themselves, and they will not harm you at all. And Allah has revealed to you the Book and wisdom and has taught you that which you did not know. And ever has the favor of Allah upon you been great", "وَلَوۡلَا فَضۡلُ ٱللَّهِ عَلَيۡكَ وَرَحۡمَتُهُۥ لَهَمَّت طَّآئِفَةٞ مِّنۡهُمۡ أَن يُضِلُّوكَ وَمَا يُضِلُّونَ إِلَّآ أَنفُسَهُمۡۖ وَمَا يَضُرُّونَكَ مِن شَيۡءٖۚ وَأَنزَلَ ٱللَّهُ عَلَيۡكَ ٱلۡكِتَٰبَ وَٱلۡحِكۡمَةَ وَعَلَّمَكَ مَا لَمۡ تَكُن تَعۡلَمُۚ وَكَانَ فَضۡلُ ٱللَّهِ عَلَيۡكَ عَظِيمٗا", "Walawla fadlu Allahi AAalayka warahmatuhu lahammat taifatun minhum an yudillooka wama yudilloona illa anfusahum wama yadurroonaka min shayin waanzala Allahu AAalayka alkitaba waalhikmata waAAallamaka ma lam takun taAAlamu wakana fadlu Allahi AAalayka AAatheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 114, "No good is there in much of their private conversation, except for those who enjoin charity or that which is right or conciliation between people. And whoever does that seeking means to the approval of Allah - then We are going to give him a great reward", "۞لَّا خَيۡرَ فِي كَثِيرٖ مِّن نَّجۡوَىٰهُمۡ إِلَّا مَنۡ أَمَرَ بِصَدَقَةٍ أَوۡ مَعۡرُوفٍ أَوۡ إِصۡلَٰحِۭ بَيۡنَ ٱلنَّاسِۚ وَمَن يَفۡعَلۡ ذَٰلِكَ ٱبۡتِغَآءَ مَرۡضَاتِ ٱللَّهِ فَسَوۡفَ نُؤۡتِيهِ أَجۡرًا عَظِيمٗا", "La khayra fee katheerin min najwahum illa man amara bisadaqatin aw maAAroofin aw islahin bayna alnnasi waman yafAAal thalika ibtighaa mardati Allahi fasawfa nuteehi ajran AAatheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 115, "And whoever opposes the Messenger after guidance has become clear to him and follows other than the way of the believers - We will give him what he has taken and drive him into Hell, and evil it is as a destination", "وَمَن يُشَاقِقِ ٱلرَّسُولَ مِنۢ بَعۡدِ مَا تَبَيَّنَ لَهُ ٱلۡهُدَىٰ وَيَتَّبِعۡ غَيۡرَ سَبِيلِ ٱلۡمُؤۡمِنِينَ نُوَلِّهِۦ مَا تَوَلَّىٰ وَنُصۡلِهِۦ جَهَنَّمَۖ وَسَآءَتۡ مَصِيرًا", "Waman yushaqiqi alrrasoola min baAAdi ma tabayyana lahu alhuda wayattabiAA ghayra sabeeli almumineena nuwallihi ma tawalla wanuslihi jahannama wasaat maseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 116, "Indeed, Allah does not forgive association with Him, but He forgives what is less than that for whom He wills. And he who associates others with Allah has certainly gone far astray", "إِنَّ ٱللَّهَ لَا يَغۡفِرُ أَن يُشۡرَكَ بِهِۦ وَيَغۡفِرُ مَا دُونَ ذَٰلِكَ لِمَن يَشَآءُۚ وَمَن يُشۡرِكۡ بِٱللَّهِ فَقَدۡ ضَلَّ ضَلَٰلَۢا بَعِيدًا", "Inna Allaha la yaghfiru an yushraka bihi wayaghfiru ma doona thalika liman yashao waman yushrik biAllahi faqad dalla dalalan baAAeedan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 117, "They call upon instead of Him none but female [deities], and they [actually] call upon none but a rebellious Satan", "إِن يَدۡعُونَ مِن دُونِهِۦٓ إِلَّآ إِنَٰثٗا وَإِن يَدۡعُونَ إِلَّا شَيۡطَٰنٗا مَّرِيدٗا", "In yadAAoona min doonihi illa inathan wain yadAAoona illa shaytanan mareedan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 118, "Whom Allah has cursed. For he had said, ""I will surely take from among Your servants a specific portion", "لَّعَنَهُ ٱللَّهُۘ وَقَالَ لَأَتَّخِذَنَّ مِنۡ عِبَادِكَ نَصِيبٗا مَّفۡرُوضٗا", "LaAAanahu Allahu waqala laattakhithanna min AAibadika naseeban mafroodan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 119, "And I will mislead them, and I will arouse in them [sinful] desires, and I will command them so they will slit the ears of cattle, and I will command them so they will change the creation of Allah."" And whoever takes Satan as an ally instead of Allah has certainly sustained a clear loss", "وَلَأُضِلَّنَّهُمۡ وَلَأُمَنِّيَنَّهُمۡ وَلَأٓمُرَنَّهُمۡ فَلَيُبَتِّكُنَّ ءَاذَانَ ٱلۡأَنۡعَٰمِ وَلَأٓمُرَنَّهُمۡ فَلَيُغَيِّرُنَّ خَلۡقَ ٱللَّهِۚ وَمَن يَتَّخِذِ ٱلشَّيۡطَٰنَ وَلِيّٗا مِّن دُونِ ٱللَّهِ فَقَدۡ خَسِرَ خُسۡرَانٗا مُّبِينٗا", "Walaodillannahum walaomanniyannahum walaamurannahum falayubattikunna athana alanAAami walaamurannahum falayughayyirunna khalqa Allahi waman yattakhithi alshshaytana waliyyan min dooni Allahi faqad khasira khusranan mubeenan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 120, "Satan promises them and arouses desire in them. But Satan does not promise them except delusion", "يَعِدُهُمۡ وَيُمَنِّيهِمۡۖ وَمَا يَعِدُهُمُ ٱلشَّيۡطَٰنُ إِلَّا غُرُورًا", "YaAAiduhum wayumanneehim wama yaAAiduhumu alshshaytanu illa ghurooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 121, "The refuge of those will be Hell, and they will not find from it an escape", "أُوْلَـٰٓئِكَ مَأۡوَىٰهُمۡ جَهَنَّمُ وَلَا يَجِدُونَ عَنۡهَا مَحِيصٗا", "Olaika mawahum jahannamu wala yajidoona AAanha maheesan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 122, "But the ones who believe and do righteous deeds - We will admit them to gardens beneath which rivers flow, wherein they will abide forever. [It is] the promise of Allah, [which is] truth, and who is more truthful than Allah in statement", "وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ سَنُدۡخِلُهُمۡ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَآ أَبَدٗاۖ وَعۡدَ ٱللَّهِ حَقّٗاۚ وَمَنۡ أَصۡدَقُ مِنَ ٱللَّهِ قِيلٗا", "Waallatheena amanoo waAAamiloo alssalihati sanudkhiluhum jannatin tajree min tahtiha alanharu khalideena feeha abadan waAAda Allahi haqqan waman asdaqu mina Allahi qeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 123, "Paradise is not [obtained] by your wishful thinking nor by that of the People of the Scripture. Whoever does a wrong will be recompensed for it, and he will not find besides Allah a protector or a helper", "لَّيۡسَ بِأَمَانِيِّكُمۡ وَلَآ أَمَانِيِّ أَهۡلِ ٱلۡكِتَٰبِۗ مَن يَعۡمَلۡ سُوٓءٗا يُجۡزَ بِهِۦ وَلَا يَجِدۡ لَهُۥ مِن دُونِ ٱللَّهِ وَلِيّٗا وَلَا نَصِيرٗا", "Laysa biamaniyyikum wala amaniyyi ahli alkitabi man yaAAmal sooan yujza bihi wala yajid lahu min dooni Allahi waliyyan wala naseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 124, "And whoever does righteous deeds, whether male or female, while being a believer - those will enter Paradise and will not be wronged, [even as much as] the speck on a date seed", "وَمَن يَعۡمَلۡ مِنَ ٱلصَّـٰلِحَٰتِ مِن ذَكَرٍ أَوۡ أُنثَىٰ وَهُوَ مُؤۡمِنٞ فَأُوْلَـٰٓئِكَ يَدۡخُلُونَ ٱلۡجَنَّةَ وَلَا يُظۡلَمُونَ نَقِيرٗا", "Waman yaAAmal mina alssalihati min thakarin aw ontha wahuwa muminun faolaika yadkhuloona aljannata wala yuthlamoona naqeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 125, "And who is better in religion than one who submits himself to Allah while being a doer of good and follows the religion of Abraham, inclining toward truth? And Allah took Abraham as an intimate friend", "وَمَنۡ أَحۡسَنُ دِينٗا مِّمَّنۡ أَسۡلَمَ وَجۡهَهُۥ لِلَّهِ وَهُوَ مُحۡسِنٞ وَٱتَّبَعَ مِلَّةَ إِبۡرَٰهِيمَ حَنِيفٗاۗ وَٱتَّخَذَ ٱللَّهُ إِبۡرَٰهِيمَ خَلِيلٗا", "Waman ahsanu deenan mimman aslama wajhahu lillahi wahuwa muhsinun waittabaAAa millata ibraheema haneefan waittakhatha Allahu ibraheema khaleelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 126, "And to Allah belongs whatever is in the heavens and whatever is on the earth. And ever is Allah, of all things, encompassing", "وَلِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۚ وَكَانَ ٱللَّهُ بِكُلِّ شَيۡءٖ مُّحِيطٗا", "Walillahi ma fee alssamawati wama fee alardi wakana Allahu bikulli shayin muheetan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 127, "And they request from you, [O Muhammad], a [legal] ruling concerning women. Say, ""Allah gives you a ruling about them and [about] what has been recited to you in the Book concerning the orphan girls to whom you do not give what is decreed for them - and [yet] you desire to marry them - and concerning the oppressed among children and that you maintain for orphans [their rights] in justice."" And whatever you do of good - indeed, Allah is ever Knowing of it", "وَيَسۡتَفۡتُونَكَ فِي ٱلنِّسَآءِۖ قُلِ ٱللَّهُ يُفۡتِيكُمۡ فِيهِنَّ وَمَا يُتۡلَىٰ عَلَيۡكُمۡ فِي ٱلۡكِتَٰبِ فِي يَتَٰمَى ٱلنِّسَآءِ ٱلَّـٰتِي لَا تُؤۡتُونَهُنَّ مَا كُتِبَ لَهُنَّ وَتَرۡغَبُونَ أَن تَنكِحُوهُنَّ وَٱلۡمُسۡتَضۡعَفِينَ مِنَ ٱلۡوِلۡدَٰنِ وَأَن تَقُومُواْ لِلۡيَتَٰمَىٰ بِٱلۡقِسۡطِۚ وَمَا تَفۡعَلُواْ مِنۡ خَيۡرٖ فَإِنَّ ٱللَّهَ كَانَ بِهِۦ عَلِيمٗا", "Wayastaftoonaka fee alnnisai quli Allahu yufteekum feehinna wama yutla AAalaykum fee alkitabi fee yatama alnnisai allatee la tutoonahunna ma kutiba lahunna watarghaboona an tankihoohunna waalmustadAAafeena mina alwildani waan taqoomoo lilyatama bialqisti wama tafAAaloo min khayrin fainna Allaha kana bihi AAaleeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 128, "And if a woman fears from her husband contempt or evasion, there is no sin upon them if they make terms of settlement between them - and settlement is best. And present in [human] souls is stinginess. But if you do good and fear Allah - then indeed Allah is ever, with what you do, Acquainted", "وَإِنِ ٱمۡرَأَةٌ خَافَتۡ مِنۢ بَعۡلِهَا نُشُوزًا أَوۡ إِعۡرَاضٗا فَلَا جُنَاحَ عَلَيۡهِمَآ أَن يُصۡلِحَا بَيۡنَهُمَا صُلۡحٗاۚ وَٱلصُّلۡحُ خَيۡرٞۗ وَأُحۡضِرَتِ ٱلۡأَنفُسُ ٱلشُّحَّۚ وَإِن تُحۡسِنُواْ وَتَتَّقُواْ فَإِنَّ ٱللَّهَ كَانَ بِمَا تَعۡمَلُونَ خَبِيرٗا", "Waini imraatun khafat min baAAliha nushoozan aw iAAradan fala junaha AAalayhima an yusliha baynahuma sulhan waalssulhu khayrun waohdirati alanfusu alshshuhha wain tuhsinoo watattaqoo fainna Allaha kana bima taAAmaloona khabeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 129, "And you will never be able to be equal [in feeling] between wives, even if you should strive [to do so]. So do not incline completely [toward one] and leave another hanging. And if you amend [your affairs] and fear Allah - then indeed, Allah is ever Forgiving and Merciful", "وَلَن تَسۡتَطِيعُوٓاْ أَن تَعۡدِلُواْ بَيۡنَ ٱلنِّسَآءِ وَلَوۡ حَرَصۡتُمۡۖ فَلَا تَمِيلُواْ كُلَّ ٱلۡمَيۡلِ فَتَذَرُوهَا كَٱلۡمُعَلَّقَةِۚ وَإِن تُصۡلِحُواْ وَتَتَّقُواْ فَإِنَّ ٱللَّهَ كَانَ غَفُورٗا رَّحِيمٗا", "Walan tastateeAAoo an taAAdiloo bayna alnnisai walaw harastum fala tameeloo kulla almayli fatatharooha kaalmuAAallaqati wain tuslihoo watattaqoo fainna Allaha kana ghafooran raheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 130, "But if they separate [by divorce], Allah will enrich each [of them] from His abundance. And ever is Allah Encompassing and Wise", "وَإِن يَتَفَرَّقَا يُغۡنِ ٱللَّهُ كُلّٗا مِّن سَعَتِهِۦۚ وَكَانَ ٱللَّهُ وَٰسِعًا حَكِيمٗا", "Wain yatafarraqa yughni Allahu kullan min saAAatihi wakana Allahu wasiAAan hakeeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 131, "And to Allah belongs whatever is in the heavens and whatever is on the earth. And We have instructed those who were given the Scripture before you and yourselves to fear Allah. But if you disbelieve - then to Allah belongs whatever is in the heavens and whatever is on the earth. And ever is Allah Free of need and Praiseworthy", "وَلِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۗ وَلَقَدۡ وَصَّيۡنَا ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ مِن قَبۡلِكُمۡ وَإِيَّاكُمۡ أَنِ ٱتَّقُواْ ٱللَّهَۚ وَإِن تَكۡفُرُواْ فَإِنَّ لِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۚ وَكَانَ ٱللَّهُ غَنِيًّا حَمِيدٗا", "Walillahi ma fee alssamawati wama fee alardi walaqad wassayna allatheena ootoo alkitaba min qablikum waiyyakum ani ittaqoo Allaha wain takfuroo fainna lillahi ma fee alssamawati wama fee alardi wakana Allahu ghaniyyan hameedan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 132, "And to Allah belongs whatever is in the heavens and whatever is on the earth. And sufficient is Allah as Disposer of affairs", "وَلِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۚ وَكَفَىٰ بِٱللَّهِ وَكِيلًا", "Walillahi ma fee alssamawati wama fee alardi wakafa biAllahi wakeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 133, "If He wills, He can do away with you, O people, and bring others [in your place]. And ever is Allah competent to do that", "إِن يَشَأۡ يُذۡهِبۡكُمۡ أَيُّهَا ٱلنَّاسُ وَيَأۡتِ بِـَٔاخَرِينَۚ وَكَانَ ٱللَّهُ عَلَىٰ ذَٰلِكَ قَدِيرٗا", "In yasha yuthhibkum ayyuha alnnasu wayati biakhareena wakana Allahu AAala thalika qadeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 134, "Whoever desires the reward of this world - then with Allah is the reward of this world and the Hereafter. And ever is Allah Hearing and Seeing", "مَّن كَانَ يُرِيدُ ثَوَابَ ٱلدُّنۡيَا فَعِندَ ٱللَّهِ ثَوَابُ ٱلدُّنۡيَا وَٱلۡأٓخِرَةِۚ وَكَانَ ٱللَّهُ سَمِيعَۢا بَصِيرٗا", "Man kana yureedu thawaba alddunya faAAinda Allahi thawabu alddunya waalakhirati wakana Allahu sameeAAan baseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 135, "O you who have believed, be persistently standing firm in justice, witnesses for Allah, even if it be against yourselves or parents and relatives. Whether one is rich or poor, Allah is more worthy of both. So follow not [personal] inclination, lest you not be just. And if you distort [your testimony] or refuse [to give it], then indeed Allah is ever, with what you do, Acquainted", "۞يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ كُونُواْ قَوَّـٰمِينَ بِٱلۡقِسۡطِ شُهَدَآءَ لِلَّهِ وَلَوۡ عَلَىٰٓ أَنفُسِكُمۡ أَوِ ٱلۡوَٰلِدَيۡنِ وَٱلۡأَقۡرَبِينَۚ إِن يَكُنۡ غَنِيًّا أَوۡ فَقِيرٗا فَٱللَّهُ أَوۡلَىٰ بِهِمَاۖ فَلَا تَتَّبِعُواْ ٱلۡهَوَىٰٓ أَن تَعۡدِلُواْۚ وَإِن تَلۡوُۥٓاْ أَوۡ تُعۡرِضُواْ فَإِنَّ ٱللَّهَ كَانَ بِمَا تَعۡمَلُونَ خَبِيرٗا", "Ya ayyuha allatheena amanoo koonoo qawwameena bialqisti shuhadaa lillahi walaw AAala anfusikum awi alwalidayni waalaqrabeena in yakun ghaniyyan aw faqeeran faAllahu awla bihima fala tattabiAAoo alhawa an taAAdiloo wain talwoo aw tuAAridoo fainna Allaha kana bima taAAmaloona khabeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 136, "O you who have believed, believe in Allah and His Messenger and the Book that He sent down upon His Messenger and the Scripture which He sent down before. And whoever disbelieves in Allah, His angels, His books, His messengers, and the Last Day has certainly gone far astray", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ ءَامِنُواْ بِٱللَّهِ وَرَسُولِهِۦ وَٱلۡكِتَٰبِ ٱلَّذِي نَزَّلَ عَلَىٰ رَسُولِهِۦ وَٱلۡكِتَٰبِ ٱلَّذِيٓ أَنزَلَ مِن قَبۡلُۚ وَمَن يَكۡفُرۡ بِٱللَّهِ وَمَلَـٰٓئِكَتِهِۦ وَكُتُبِهِۦ وَرُسُلِهِۦ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ فَقَدۡ ضَلَّ ضَلَٰلَۢا بَعِيدًا", "Ya ayyuha allatheena amanoo aminoo biAllahi warasoolihi waalkitabi allathee nazzala AAala rasoolihi waalkitabi allathee anzala min qablu waman yakfur biAllahi wamalaikatihi wakutubihi warusulihi waalyawmi alakhiri faqad dalla dalalan baAAeedan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 137, "Indeed, those who have believed then disbelieved, then believed, then disbelieved, and then increased in disbelief - never will Allah forgive them, nor will He guide them to a way", "إِنَّ ٱلَّذِينَ ءَامَنُواْ ثُمَّ كَفَرُواْ ثُمَّ ءَامَنُواْ ثُمَّ كَفَرُواْ ثُمَّ ٱزۡدَادُواْ كُفۡرٗا لَّمۡ يَكُنِ ٱللَّهُ لِيَغۡفِرَ لَهُمۡ وَلَا لِيَهۡدِيَهُمۡ سَبِيلَۢا", "Inna allatheena amanoo thumma kafaroo thumma amanoo thumma kafaroo thumma izdadoo kufran lam yakuni Allahu liyaghfira lahum wala liyahdiyahum sabeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 138, "Give tidings to the hypocrites that there is for them a painful punishment", "بَشِّرِ ٱلۡمُنَٰفِقِينَ بِأَنَّ لَهُمۡ عَذَابًا أَلِيمًا", "Bashshiri almunafiqeena bianna lahum AAathaban aleeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 139, "Those who take disbelievers as allies instead of the believers. Do they seek with them honor [through power]? But indeed, honor belongs to Allah entirely", "ٱلَّذِينَ يَتَّخِذُونَ ٱلۡكَٰفِرِينَ أَوۡلِيَآءَ مِن دُونِ ٱلۡمُؤۡمِنِينَۚ أَيَبۡتَغُونَ عِندَهُمُ ٱلۡعِزَّةَ فَإِنَّ ٱلۡعِزَّةَ لِلَّهِ جَمِيعٗا", "Allatheena yattakhithoona alkafireena awliyaa min dooni almumineena ayabtaghoona AAindahumu alAAizzata fainna alAAizzata lillahi jameeAAan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 140, "And it has already come down to you in the Book that when you hear the verses of Allah [recited], they are denied [by them] and ridiculed; so do not sit with them until they enter into another conversation. Indeed, you would then be like them. Indeed Allah will gather the hypocrites and disbelievers in Hell all together", "وَقَدۡ نَزَّلَ عَلَيۡكُمۡ فِي ٱلۡكِتَٰبِ أَنۡ إِذَا سَمِعۡتُمۡ ءَايَٰتِ ٱللَّهِ يُكۡفَرُ بِهَا وَيُسۡتَهۡزَأُ بِهَا فَلَا تَقۡعُدُواْ مَعَهُمۡ حَتَّىٰ يَخُوضُواْ فِي حَدِيثٍ غَيۡرِهِۦٓ إِنَّكُمۡ إِذٗا مِّثۡلُهُمۡۗ إِنَّ ٱللَّهَ جَامِعُ ٱلۡمُنَٰفِقِينَ وَٱلۡكَٰفِرِينَ فِي جَهَنَّمَ جَمِيعًا", "Waqad nazzala AAalaykum fee alkitabi an itha samiAAtum ayati Allahi yukfaru biha wayustahzao biha fala taqAAudoo maAAahum hatta yakhoodoo fee hadeethin ghayrihi innakum ithan mithluhum inna Allaha jamiAAu almunafiqeena waalkafireena fee jahannama jameeAAan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 141, "Those who wait [and watch] you. Then if you gain a victory from Allah, they say, ""Were we not with you?"" But if the disbelievers have a success, they say [to them], ""Did we not gain the advantage over you, but we protected you from the believers?"" Allah will judge between [all of] you on the Day of Resurrection, and never will Allah give the disbelievers over the believers a way [to overcome them]", "ٱلَّذِينَ يَتَرَبَّصُونَ بِكُمۡ فَإِن كَانَ لَكُمۡ فَتۡحٞ مِّنَ ٱللَّهِ قَالُوٓاْ أَلَمۡ نَكُن مَّعَكُمۡ وَإِن كَانَ لِلۡكَٰفِرِينَ نَصِيبٞ قَالُوٓاْ أَلَمۡ نَسۡتَحۡوِذۡ عَلَيۡكُمۡ وَنَمۡنَعۡكُم مِّنَ ٱلۡمُؤۡمِنِينَۚ فَٱللَّهُ يَحۡكُمُ بَيۡنَكُمۡ يَوۡمَ ٱلۡقِيَٰمَةِۚ وَلَن يَجۡعَلَ ٱللَّهُ لِلۡكَٰفِرِينَ عَلَى ٱلۡمُؤۡمِنِينَ سَبِيلًا", "Allatheena yatarabbasoona bikum fain kana lakum fathun mina Allahi qaloo alam nakun maAAakum wain kana lilkafireena naseebun qaloo alam nastahwith AAalaykum wanamnaAAkum mina almumineena faAllahu yahkumu baynakum yawma alqiyamati walan yajAAala Allahu lilkafireena AAala almumineena sabeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 142, "Indeed, the hypocrites [think to] deceive Allah, but He is deceiving them. And when they stand for prayer, they stand lazily, showing [themselves to] the people and not remembering Allah except a little", "إِنَّ ٱلۡمُنَٰفِقِينَ يُخَٰدِعُونَ ٱللَّهَ وَهُوَ خَٰدِعُهُمۡ وَإِذَا قَامُوٓاْ إِلَى ٱلصَّلَوٰةِ قَامُواْ كُسَالَىٰ يُرَآءُونَ ٱلنَّاسَ وَلَا يَذۡكُرُونَ ٱللَّهَ إِلَّا قَلِيلٗا", "Inna almunafiqeena yukhadiAAoona Allaha wahuwa khadiAAuhum waitha qamoo ila alssalati qamoo kusala yuraoona alnnasa wala yathkuroona Allaha illa qaleelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 143, "Wavering between them, [belonging] neither to the believers nor to the disbelievers. And whoever Allah leaves astray - never will you find for him a way", "مُّذَبۡذَبِينَ بَيۡنَ ذَٰلِكَ لَآ إِلَىٰ هَـٰٓؤُلَآءِ وَلَآ إِلَىٰ هَـٰٓؤُلَآءِۚ وَمَن يُضۡلِلِ ٱللَّهُ فَلَن تَجِدَ لَهُۥ سَبِيلٗا", "Muthabthabeena bayna thalika la ila haolai wala ila haolai waman yudlili Allahu falan tajida lahu sabeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 144, "O you who have believed, do not take the disbelievers as allies instead of the believers. Do you wish to give Allah against yourselves a clear case", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَتَّخِذُواْ ٱلۡكَٰفِرِينَ أَوۡلِيَآءَ مِن دُونِ ٱلۡمُؤۡمِنِينَۚ أَتُرِيدُونَ أَن تَجۡعَلُواْ لِلَّهِ عَلَيۡكُمۡ سُلۡطَٰنٗا مُّبِينًا", "Ya ayyuha allatheena amanoo la tattakhithoo alkafireena awliyaa min dooni almumineena atureedoona an tajAAaloo lillahi AAalaykum sultanan mubeenan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 145, "Indeed, the hypocrites will be in the lowest depths of the Fire - and never will you find for them a helper", "إِنَّ ٱلۡمُنَٰفِقِينَ فِي ٱلدَّرۡكِ ٱلۡأَسۡفَلِ مِنَ ٱلنَّارِ وَلَن تَجِدَ لَهُمۡ نَصِيرًا", "Inna almunafiqeena fee alddarki alasfali mina alnnari walan tajida lahum naseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 146, "Except for those who repent, correct themselves, hold fast to Allah, and are sincere in their religion for Allah, for those will be with the believers. And Allah is going to give the believers a great reward", "إِلَّا ٱلَّذِينَ تَابُواْ وَأَصۡلَحُواْ وَٱعۡتَصَمُواْ بِٱللَّهِ وَأَخۡلَصُواْ دِينَهُمۡ لِلَّهِ فَأُوْلَـٰٓئِكَ مَعَ ٱلۡمُؤۡمِنِينَۖ وَسَوۡفَ يُؤۡتِ ٱللَّهُ ٱلۡمُؤۡمِنِينَ أَجۡرًا عَظِيمٗا", "Illa allatheena taboo waaslahoo waiAAtasamoo biAllahi waakhlasoo deenahum lillahi faolaika maAAa almumineena wasawfa yuti Allahu almumineena ajran AAatheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 147, "What would Allah do with your punishment if you are grateful and believe? And ever is Allah Appreciative and Knowing", "مَّا يَفۡعَلُ ٱللَّهُ بِعَذَابِكُمۡ إِن شَكَرۡتُمۡ وَءَامَنتُمۡۚ وَكَانَ ٱللَّهُ شَاكِرًا عَلِيمٗا", "Ma yafAAalu Allahu biAAathabikum in shakartum waamantum wakana Allahu shakiran AAaleeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 148, "Allah does not like the public mention of evil except by one who has been wronged. And ever is Allah Hearing and Knowing", "۞لَّا يُحِبُّ ٱللَّهُ ٱلۡجَهۡرَ بِٱلسُّوٓءِ مِنَ ٱلۡقَوۡلِ إِلَّا مَن ظُلِمَۚ وَكَانَ ٱللَّهُ سَمِيعًا عَلِيمًا", "La yuhibbu Allahu aljahra bialssooi mina alqawli illa man thulima wakana Allahu sameeAAan AAaleeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 149, "If [instead] you show [some] good or conceal it or pardon an offense - indeed, Allah is ever Pardoning and Competent", "إِن تُبۡدُواْ خَيۡرًا أَوۡ تُخۡفُوهُ أَوۡ تَعۡفُواْ عَن سُوٓءٖ فَإِنَّ ٱللَّهَ كَانَ عَفُوّٗا قَدِيرًا", "In tubdoo khayran aw tukhfoohu aw taAAfoo AAan sooin fainna Allaha kana AAafuwwan qadeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 150, "Indeed, those who disbelieve in Allah and His messengers and wish to discriminate between Allah and His messengers and say, ""We believe in some and disbelieve in others,"" and wish to adopt a way in between", "إِنَّ ٱلَّذِينَ يَكۡفُرُونَ بِٱللَّهِ وَرُسُلِهِۦ وَيُرِيدُونَ أَن يُفَرِّقُواْ بَيۡنَ ٱللَّهِ وَرُسُلِهِۦ وَيَقُولُونَ نُؤۡمِنُ بِبَعۡضٖ وَنَكۡفُرُ بِبَعۡضٖ وَيُرِيدُونَ أَن يَتَّخِذُواْ بَيۡنَ ذَٰلِكَ سَبِيلًا", "Inna allatheena yakfuroona biAllahi warusulihi wayureedoona an yufarriqoo bayna Allahi warusulihi wayaqooloona numinu bibaAAdin wanakfuru bibaAAdin wayureedoona an yattakhithoo bayna thalika sabeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 151, "Those are the disbelievers, truly. And We have prepared for the disbelievers a humiliating punishment", "أُوْلَـٰٓئِكَ هُمُ ٱلۡكَٰفِرُونَ حَقّٗاۚ وَأَعۡتَدۡنَا لِلۡكَٰفِرِينَ عَذَابٗا مُّهِينٗا", "Olaika humu alkafiroona haqqan waaAAtadna lilkafireena AAathaban muheenan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 152, "But they who believe in Allah and His messengers and do not discriminate between any of them - to those He is going to give their rewards. And ever is Allah Forgiving and Merciful", "وَٱلَّذِينَ ءَامَنُواْ بِٱللَّهِ وَرُسُلِهِۦ وَلَمۡ يُفَرِّقُواْ بَيۡنَ أَحَدٖ مِّنۡهُمۡ أُوْلَـٰٓئِكَ سَوۡفَ يُؤۡتِيهِمۡ أُجُورَهُمۡۚ وَكَانَ ٱللَّهُ غَفُورٗا رَّحِيمٗا", "Waallatheena amanoo biAllahi warusulihi walam yufarriqoo bayna ahadin minhum olaika sawfa yuteehim ojoorahum wakana Allahu ghafooran raheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 153, "The People of the Scripture ask you to bring down to them a book from the heaven. But they had asked of Moses [even] greater than that and said, ""Show us Allah outright,"" so the thunderbolt struck them for their wrongdoing. Then they took the calf [for worship] after clear evidences had come to them, and We pardoned that. And We gave Moses a clear authority", "يَسۡـَٔلُكَ أَهۡلُ ٱلۡكِتَٰبِ أَن تُنَزِّلَ عَلَيۡهِمۡ كِتَٰبٗا مِّنَ ٱلسَّمَآءِۚ فَقَدۡ سَأَلُواْ مُوسَىٰٓ أَكۡبَرَ مِن ذَٰلِكَ فَقَالُوٓاْ أَرِنَا ٱللَّهَ جَهۡرَةٗ فَأَخَذَتۡهُمُ ٱلصَّـٰعِقَةُ بِظُلۡمِهِمۡۚ ثُمَّ ٱتَّخَذُواْ ٱلۡعِجۡلَ مِنۢ بَعۡدِ مَا جَآءَتۡهُمُ ٱلۡبَيِّنَٰتُ فَعَفَوۡنَا عَن ذَٰلِكَۚ وَءَاتَيۡنَا مُوسَىٰ سُلۡطَٰنٗا مُّبِينٗا", "Yasaluka ahlu alkitabi an tunazzila AAalayhim kitaban mina alssamai faqad saaloo moosa akbara min thalika faqaloo arina Allaha jahratan faakhathathumu alssaAAiqatu bithulmihim thumma ittakhathoo alAAijla min baAAdi ma jaathumu albayyinatu faAAafawna AAan thalika waatayna moosa sultanan mubeenan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 154, "And We raised over them the mount for [refusal of] their covenant; and We said to them, ""Enter the gate bowing humbly"", and We said to them, ""Do not transgress on the sabbath"", and We took from them a solemn covenant", "وَرَفَعۡنَا فَوۡقَهُمُ ٱلطُّورَ بِمِيثَٰقِهِمۡ وَقُلۡنَا لَهُمُ ٱدۡخُلُواْ ٱلۡبَابَ سُجَّدٗا وَقُلۡنَا لَهُمۡ لَا تَعۡدُواْ فِي ٱلسَّبۡتِ وَأَخَذۡنَا مِنۡهُم مِّيثَٰقًا غَلِيظٗا", "WarafaAAna fawqahumu alttoora bimeethaqihim waqulna lahumu odkhuloo albaba sujjadan waqulna lahum la taAAdoo fee alssabti waakhathna minhum meethaqan ghaleethan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 155, "And [We cursed them] for their breaking of the covenant and their disbelief in the signs of Allah and their killing of the prophets without right and their saying, ""Our hearts are wrapped"". Rather, Allah has sealed them because of their disbelief, so they believe not, except for a few", "فَبِمَا نَقۡضِهِم مِّيثَٰقَهُمۡ وَكُفۡرِهِم بِـَٔايَٰتِ ٱللَّهِ وَقَتۡلِهِمُ ٱلۡأَنۢبِيَآءَ بِغَيۡرِ حَقّٖ وَقَوۡلِهِمۡ قُلُوبُنَا غُلۡفُۢۚ بَلۡ طَبَعَ ٱللَّهُ عَلَيۡهَا بِكُفۡرِهِمۡ فَلَا يُؤۡمِنُونَ إِلَّا قَلِيلٗا", "Fabima naqdihim meethaqahum wakufrihim biayati Allahi waqatlihimu alanbiyaa bighayri haqqin waqawlihim quloobuna ghulfun bal tabaAAa Allahu AAalayha bikufrihim fala yuminoona illa qaleelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 156, "And [We cursed them] for their disbelief and their saying against Mary a great slander", "وَبِكُفۡرِهِمۡ وَقَوۡلِهِمۡ عَلَىٰ مَرۡيَمَ بُهۡتَٰنًا عَظِيمٗا", "Wabikufrihim waqawlihim AAala maryama buhtanan AAatheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 157, "And [for] their saying, ""Indeed, we have killed the Messiah, Jesus, the son of Mary, the messenger of Allah."" And they did not kill him, nor did they crucify him; but [another] was made to resemble him to them. And indeed, those who differ over it are in doubt about it. They have no knowledge of it except the following of assumption. And they did not kill him, for certain", "وَقَوۡلِهِمۡ إِنَّا قَتَلۡنَا ٱلۡمَسِيحَ عِيسَى ٱبۡنَ مَرۡيَمَ رَسُولَ ٱللَّهِ وَمَا قَتَلُوهُ وَمَا صَلَبُوهُ وَلَٰكِن شُبِّهَ لَهُمۡۚ وَإِنَّ ٱلَّذِينَ ٱخۡتَلَفُواْ فِيهِ لَفِي شَكّٖ مِّنۡهُۚ مَا لَهُم بِهِۦ مِنۡ عِلۡمٍ إِلَّا ٱتِّبَاعَ ٱلظَّنِّۚ وَمَا قَتَلُوهُ يَقِينَۢا", "Waqawlihim inna qatalna almaseeha AAeesa ibna maryama rasoola Allahi wama qataloohu wama salaboohu walakin shubbiha lahum wainna allatheena ikhtalafoo feehi lafee shakkin minhu ma lahum bihi min AAilmin illa ittibaAAa alththanni wama qataloohu yaqeenan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 158, "Rather, Allah raised him to Himself. And ever is Allah Exalted in Might and Wise", "بَل رَّفَعَهُ ٱللَّهُ إِلَيۡهِۚ وَكَانَ ٱللَّهُ عَزِيزًا حَكِيمٗا", "Bal rafaAAahu Allahu ilayhi wakana Allahu AAazeezan hakeeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 159, "And there is none from the People of the Scripture but that he will surely believe in Jesus before his death. And on the Day of Resurrection he will be against them a witness", "وَإِن مِّنۡ أَهۡلِ ٱلۡكِتَٰبِ إِلَّا لَيُؤۡمِنَنَّ بِهِۦ قَبۡلَ مَوۡتِهِۦۖ وَيَوۡمَ ٱلۡقِيَٰمَةِ يَكُونُ عَلَيۡهِمۡ شَهِيدٗا", "Wain min ahli alkitabi illa layuminanna bihi qabla mawtihi wayawma alqiyamati yakoonu AAalayhim shaheedan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 160, "For wrongdoing on the part of the Jews, We made unlawful for them [certain] good foods which had been lawful to them, and for their averting from the way of Allah many [people]", "فَبِظُلۡمٖ مِّنَ ٱلَّذِينَ هَادُواْ حَرَّمۡنَا عَلَيۡهِمۡ طَيِّبَٰتٍ أُحِلَّتۡ لَهُمۡ وَبِصَدِّهِمۡ عَن سَبِيلِ ٱللَّهِ كَثِيرٗا", "Fabithulmin mina allatheena hadoo harramna AAalayhim tayyibatin ohillat lahum wabisaddihim AAan sabeeli Allahi katheeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 161, "And [for] their taking of usury while they had been forbidden from it, and their consuming of the people's wealth unjustly. And we have prepared for the disbelievers among them a painful punishment", "وَأَخۡذِهِمُ ٱلرِّبَوٰاْ وَقَدۡ نُهُواْ عَنۡهُ وَأَكۡلِهِمۡ أَمۡوَٰلَ ٱلنَّاسِ بِٱلۡبَٰطِلِۚ وَأَعۡتَدۡنَا لِلۡكَٰفِرِينَ مِنۡهُمۡ عَذَابًا أَلِيمٗا", "Waakhthihimu alrriba waqad nuhoo AAanhu waaklihim amwala alnnasi bialbatili waaAAtadna lilkafireena minhum AAathaban aleeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 162, "But those firm in knowledge among them and the believers believe in what has been revealed to you, [O Muhammad], and what was revealed before you. And the establishers of prayer [especially] and the givers of zakah and the believers in Allah and the Last Day - those We will give a great reward", "لَّـٰكِنِ ٱلرَّـٰسِخُونَ فِي ٱلۡعِلۡمِ مِنۡهُمۡ وَٱلۡمُؤۡمِنُونَ يُؤۡمِنُونَ بِمَآ أُنزِلَ إِلَيۡكَ وَمَآ أُنزِلَ مِن قَبۡلِكَۚ وَٱلۡمُقِيمِينَ ٱلصَّلَوٰةَۚ وَٱلۡمُؤۡتُونَ ٱلزَّكَوٰةَ وَٱلۡمُؤۡمِنُونَ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ أُوْلَـٰٓئِكَ سَنُؤۡتِيهِمۡ أَجۡرًا عَظِيمًا", "Lakini alrrasikhoona fee alAAilmi minhum waalmuminoona yuminoona bima onzila ilayka wama onzila min qablika waalmuqeemeena alssalata waalmutoona alzzakata waalmuminoona biAllahi waalyawmi alakhiri olaika sanuteehim ajran AAatheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 163, "Indeed, We have revealed to you, [O Muhammad], as We revealed to Noah and the prophets after him. And we revealed to Abraham, Ishmael, Isaac, Jacob, the Descendants, Jesus, Job, Jonah, Aaron, and Solomon, and to David We gave the book [of Psalms]", "۞إِنَّآ أَوۡحَيۡنَآ إِلَيۡكَ كَمَآ أَوۡحَيۡنَآ إِلَىٰ نُوحٖ وَٱلنَّبِيِّـۧنَ مِنۢ بَعۡدِهِۦۚ وَأَوۡحَيۡنَآ إِلَىٰٓ إِبۡرَٰهِيمَ وَإِسۡمَٰعِيلَ وَإِسۡحَٰقَ وَيَعۡقُوبَ وَٱلۡأَسۡبَاطِ وَعِيسَىٰ وَأَيُّوبَ وَيُونُسَ وَهَٰرُونَ وَسُلَيۡمَٰنَۚ وَءَاتَيۡنَا دَاوُۥدَ زَبُورٗا", "Inna awhayna ilayka kama awhayna ila noohin waalnnabiyyeena min baAAdihi waawhayna ila ibraheema waismaAAeela waishaqa wayaAAqooba waalasbati waAAeesa waayyooba wayoonusa waharoona wasulaymana waatayna dawooda zabooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 164, "And [We sent] messengers about whom We have related [their stories] to you before and messengers about whom We have not related to you. And Allah spoke to Moses with [direct] speech", "وَرُسُلٗا قَدۡ قَصَصۡنَٰهُمۡ عَلَيۡكَ مِن قَبۡلُ وَرُسُلٗا لَّمۡ نَقۡصُصۡهُمۡ عَلَيۡكَۚ وَكَلَّمَ ٱللَّهُ مُوسَىٰ تَكۡلِيمٗا", "Warusulan qad qasasnahum AAalayka min qablu warusulan lam naqsushum AAalayka wakallama Allahu moosa takleeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 165, "[We sent] messengers as bringers of good tidings and warners so that mankind will have no argument against Allah after the messengers. And ever is Allah Exalted in Might and Wise", "رُّسُلٗا مُّبَشِّرِينَ وَمُنذِرِينَ لِئَلَّا يَكُونَ لِلنَّاسِ عَلَى ٱللَّهِ حُجَّةُۢ بَعۡدَ ٱلرُّسُلِۚ وَكَانَ ٱللَّهُ عَزِيزًا حَكِيمٗا", "Rusulan mubashshireena wamunthireena lialla yakoona lilnnasi AAala Allahi hujjatun baAAda alrrusuli wakana Allahu AAazeezan hakeeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 166, "But Allah bears witness to that which He has revealed to you. He has sent it down with His knowledge, and the angels bear witness [as well]. And sufficient is Allah as Witness", "لَّـٰكِنِ ٱللَّهُ يَشۡهَدُ بِمَآ أَنزَلَ إِلَيۡكَۖ أَنزَلَهُۥ بِعِلۡمِهِۦۖ وَٱلۡمَلَـٰٓئِكَةُ يَشۡهَدُونَۚ وَكَفَىٰ بِٱللَّهِ شَهِيدًا", "Lakini Allahu yashhadu bima anzala ilayka anzalahu biAAilmihi waalmalaikatu yashhadoona wakafa biAllahi shaheedan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 167, "Indeed, those who disbelieve and avert [people] from the way of Allah have certainly gone far astray", "إِنَّ ٱلَّذِينَ كَفَرُواْ وَصَدُّواْ عَن سَبِيلِ ٱللَّهِ قَدۡ ضَلُّواْ ضَلَٰلَۢا بَعِيدًا", "Inna allatheena kafaroo wasaddoo AAan sabeeli Allahi qad dalloo dalalan baAAeedan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 168, "Indeed, those who disbelieve and commit wrong [or injustice] - never will Allah forgive them, nor will He guide them to a path", "إِنَّ ٱلَّذِينَ كَفَرُواْ وَظَلَمُواْ لَمۡ يَكُنِ ٱللَّهُ لِيَغۡفِرَ لَهُمۡ وَلَا لِيَهۡدِيَهُمۡ طَرِيقًا", "Inna allatheena kafaroo wathalamoo lam yakuni Allahu liyaghfira lahum wala liyahdiyahum tareeqan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 169, "Except the path of Hell; they will abide therein forever. And that, for Allah, is [always] easy", "إِلَّا طَرِيقَ جَهَنَّمَ خَٰلِدِينَ فِيهَآ أَبَدٗاۚ وَكَانَ ذَٰلِكَ عَلَى ٱللَّهِ يَسِيرٗا", "Illa tareeqa jahannama khalideena feeha abadan wakana thalika AAala Allahi yaseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 170, "O Mankind, the Messenger has come to you with the truth from your Lord, so believe; it is better for you. But if you disbelieve - then indeed, to Allah belongs whatever is in the heavens and earth. And ever is Allah Knowing and Wise", "يَـٰٓأَيُّهَا ٱلنَّاسُ قَدۡ جَآءَكُمُ ٱلرَّسُولُ بِٱلۡحَقِّ مِن رَّبِّكُمۡ فَـَٔامِنُواْ خَيۡرٗا لَّكُمۡۚ وَإِن تَكۡفُرُواْ فَإِنَّ لِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَكَانَ ٱللَّهُ عَلِيمًا حَكِيمٗا", "Ya ayyuha alnnasu qad jaakumu alrrasoolu bialhaqqi min rabbikum faaminoo khayran lakum wain takfuroo fainna lillahi ma fee alssamawati waalardi wakana Allahu AAaleeman hakeeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 171, "O People of the Scripture, do not commit excess in your religion or say about Allah except the truth. The Messiah, Jesus, the son of Mary, was but a messenger of Allah and His word which He directed to Mary and a soul [created at a command] from Him. So believe in Allah and His messengers. And do not say, ""Three""; desist - it is better for you. Indeed, Allah is but one God. Exalted is He above having a son. To Him belongs whatever is in the heavens and whatever is on the earth. And sufficient is Allah as Disposer of affairs", "يَـٰٓأَهۡلَ ٱلۡكِتَٰبِ لَا تَغۡلُواْ فِي دِينِكُمۡ وَلَا تَقُولُواْ عَلَى ٱللَّهِ إِلَّا ٱلۡحَقَّۚ إِنَّمَا ٱلۡمَسِيحُ عِيسَى ٱبۡنُ مَرۡيَمَ رَسُولُ ٱللَّهِ وَكَلِمَتُهُۥٓ أَلۡقَىٰهَآ إِلَىٰ مَرۡيَمَ وَرُوحٞ مِّنۡهُۖ فَـَٔامِنُواْ بِٱللَّهِ وَرُسُلِهِۦۖ وَلَا تَقُولُواْ ثَلَٰثَةٌۚ ٱنتَهُواْ خَيۡرٗا لَّكُمۡۚ إِنَّمَا ٱللَّهُ إِلَٰهٞ وَٰحِدٞۖ سُبۡحَٰنَهُۥٓ أَن يَكُونَ لَهُۥ وَلَدٞۘ لَّهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۗ وَكَفَىٰ بِٱللَّهِ وَكِيلٗا", "Ya ahla alkitabi la taghloo fee deenikum wala taqooloo AAala Allahi illa alhaqqa innama almaseehu AAeesa ibnu maryama rasoolu Allahi wakalimatuhu alqaha ila maryama waroohun minhu faaminoo biAllahi warusulihi wala taqooloo thalathatun intahoo khayran lakum innama Allahu ilahun wahidun subhanahu an yakoona lahu waladun lahu ma fee alssamawati wama fee alardi wakafa biAllahi wakeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 172, "Never would the Messiah disdain to be a servant of Allah, nor would the angels near [to Him]. And whoever disdains His worship and is arrogant - He will gather them to Himself all together", "لَّن يَسۡتَنكِفَ ٱلۡمَسِيحُ أَن يَكُونَ عَبۡدٗا لِّلَّهِ وَلَا ٱلۡمَلَـٰٓئِكَةُ ٱلۡمُقَرَّبُونَۚ وَمَن يَسۡتَنكِفۡ عَنۡ عِبَادَتِهِۦ وَيَسۡتَكۡبِرۡ فَسَيَحۡشُرُهُمۡ إِلَيۡهِ جَمِيعٗا", "Lan yastankifa almaseehu an yakoona AAabdan lillahi wala almalaikatu almuqarraboona waman yastankif AAan AAibadatihi wayastakbir fasayahshuruhum ilayhi jameeAAan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 173, "And as for those who believed and did righteous deeds, He will give them in full their rewards and grant them extra from His bounty. But as for those who disdained and were arrogant, He will punish them with a painful punishment, and they will not find for themselves besides Allah any protector or helper", "فَأَمَّا ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ فَيُوَفِّيهِمۡ أُجُورَهُمۡ وَيَزِيدُهُم مِّن فَضۡلِهِۦۖ وَأَمَّا ٱلَّذِينَ ٱسۡتَنكَفُواْ وَٱسۡتَكۡبَرُواْ فَيُعَذِّبُهُمۡ عَذَابًا أَلِيمٗا وَلَا يَجِدُونَ لَهُم مِّن دُونِ ٱللَّهِ وَلِيّٗا وَلَا نَصِيرٗا", "Faamma allatheena amanoo waAAamiloo alssalihati fayuwaffeehim ojoorahum wayazeeduhum min fadlihi waamma allatheena istankafoo waistakbaroo fayuAAaththibuhum AAathaban aleeman wala yajidoona lahum min dooni Allahi waliyyan wala naseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 174, "O mankind, there has come to you a conclusive proof from your Lord, and We have sent down to you a clear light", "يَـٰٓأَيُّهَا ٱلنَّاسُ قَدۡ جَآءَكُم بُرۡهَٰنٞ مِّن رَّبِّكُمۡ وَأَنزَلۡنَآ إِلَيۡكُمۡ نُورٗا مُّبِينٗا", "Ya ayyuha alnnasu qad jaakum burhanun min rabbikum waanzalna ilaykum nooran mubeenan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 175, "So those who believe in Allah and hold fast to Him - He will admit them to mercy from Himself and bounty and guide them to Himself on a straight path", "فَأَمَّا ٱلَّذِينَ ءَامَنُواْ بِٱللَّهِ وَٱعۡتَصَمُواْ بِهِۦ فَسَيُدۡخِلُهُمۡ فِي رَحۡمَةٖ مِّنۡهُ وَفَضۡلٖ وَيَهۡدِيهِمۡ إِلَيۡهِ صِرَٰطٗا مُّسۡتَقِيمٗا", "Faamma allatheena amanoo biAllahi waiAAtasamoo bihi fasayudkhiluhum fee rahmatin minhu wafadlin wayahdeehim ilayhi siratan mustaqeeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (4, 176, "They request from you a [legal] ruling. Say, ""Allah gives you a ruling concerning one having neither descendants nor ascendants [as heirs]."" If a man dies, leaving no child but [only] a sister, she will have half of what he left. And he inherits from her if she [dies and] has no child. But if there are two sisters [or more], they will have two-thirds of what he left. If there are both brothers and sisters, the male will have the share of two females. Allah makes clear to you [His law], lest you go astray. And Allah is Knowing of all things", "يَسۡتَفۡتُونَكَ قُلِ ٱللَّهُ يُفۡتِيكُمۡ فِي ٱلۡكَلَٰلَةِۚ إِنِ ٱمۡرُؤٌاْ هَلَكَ لَيۡسَ لَهُۥ وَلَدٞ وَلَهُۥٓ أُخۡتٞ فَلَهَا نِصۡفُ مَا تَرَكَۚ وَهُوَ يَرِثُهَآ إِن لَّمۡ يَكُن لَّهَا وَلَدٞۚ فَإِن كَانَتَا ٱثۡنَتَيۡنِ فَلَهُمَا ٱلثُّلُثَانِ مِمَّا تَرَكَۚ وَإِن كَانُوٓاْ إِخۡوَةٗ رِّجَالٗا وَنِسَآءٗ فَلِلذَّكَرِ مِثۡلُ حَظِّ ٱلۡأُنثَيَيۡنِۗ يُبَيِّنُ ٱللَّهُ لَكُمۡ أَن تَضِلُّواْۗ وَٱللَّهُ بِكُلِّ شَيۡءٍ عَلِيمُۢ", "Yastaftoonaka quli Allahu yufteekum fee alkalalati ini imruon halaka laysa lahu waladun walahu okhtun falaha nisfu ma taraka wahuwa yarithuha in lam yakun laha waladun fain kanata ithnatayni falahuma alththuluthani mimma taraka wain kanoo ikhwatan rijalan wanisaan falilththakari mithlu haththi alonthayayni yubayyinu Allahu lakum an tadilloo waAllahu bikulli shayin AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 1, "O you who have believed, fulfill [all] contracts. Lawful for you are the animals of grazing livestock except for that which is recited to you [in this Qur'an] - hunting not being permitted while you are in the state of ihram. Indeed, Allah ordains what He intends", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ أَوۡفُواْ بِٱلۡعُقُودِۚ أُحِلَّتۡ لَكُم بَهِيمَةُ ٱلۡأَنۡعَٰمِ إِلَّا مَا يُتۡلَىٰ عَلَيۡكُمۡ غَيۡرَ مُحِلِّي ٱلصَّيۡدِ وَأَنتُمۡ حُرُمٌۗ إِنَّ ٱللَّهَ يَحۡكُمُ مَا يُرِيدُ", "Ya ayyuha allatheena amanoo awfoo bialAAuqoodi ohillat lakum baheematu alanAAami illa ma yutla AAalaykum ghayra muhillee alssaydi waantum hurumun inna Allaha yahkumu ma yureedu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 2, "O you who have believed, do not violate the rites of Allah or [the sanctity of] the sacred month or [neglect the marking of] the sacrificial animals and garlanding [them] or [violate the safety of] those coming to the Sacred House seeking bounty from their Lord and [His] approval. But when you come out of ihram, then [you may] hunt. And do not let the hatred of a people for having obstructed you from al-Masjid al-Haram lead you to transgress. And cooperate in righteousness and piety, but do not cooperate in sin and aggression. And fear Allah; indeed, Allah is severe in penalty", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تُحِلُّواْ شَعَـٰٓئِرَ ٱللَّهِ وَلَا ٱلشَّهۡرَ ٱلۡحَرَامَ وَلَا ٱلۡهَدۡيَ وَلَا ٱلۡقَلَـٰٓئِدَ وَلَآ ءَآمِّينَ ٱلۡبَيۡتَ ٱلۡحَرَامَ يَبۡتَغُونَ فَضۡلٗا مِّن رَّبِّهِمۡ وَرِضۡوَٰنٗاۚ وَإِذَا حَلَلۡتُمۡ فَٱصۡطَادُواْۚ وَلَا يَجۡرِمَنَّكُمۡ شَنَـَٔانُ قَوۡمٍ أَن صَدُّوكُمۡ عَنِ ٱلۡمَسۡجِدِ ٱلۡحَرَامِ أَن تَعۡتَدُواْۘ وَتَعَاوَنُواْ عَلَى ٱلۡبِرِّ وَٱلتَّقۡوَىٰۖ وَلَا تَعَاوَنُواْ عَلَى ٱلۡإِثۡمِ وَٱلۡعُدۡوَٰنِۚ وَٱتَّقُواْ ٱللَّهَۖ إِنَّ ٱللَّهَ شَدِيدُ ٱلۡعِقَابِ", "Ya ayyuha allatheena amanoo la tuhilloo shaAAaira Allahi wala alshshahra alharama wala alhadya wala alqalaida wala ammeena albayta alharama yabtaghoona fadlan min rabbihim waridwanan waitha halaltum faistadoo wala yajrimannakum shanaanu qawmin an saddookum AAani almasjidi alharami an taAAtadoo wataAAawanoo AAala albirri waalttaqwa wala taAAawanoo AAala alithmi waalAAudwani waittaqoo Allaha inna Allaha shadeedu alAAiqabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 3, "Prohibited to you are dead animals, blood, the flesh of swine, and that which has been dedicated to other than Allah, and [those animals] killed by strangling or by a violent blow or by a head-long fall or by the goring of horns, and those from which a wild animal has eaten, except what you [are able to] slaughter [before its death], and those which are sacrificed on stone altars, and [prohibited is] that you seek decision through divining arrows. That is grave disobedience. This day those who disbelieve have despaired of [defeating] your religion; so fear them not, but fear Me. This day I have perfected for you your religion and completed My favor upon you and have approved for you Islam as religion. But whoever is forced by severe hunger with no inclination to sin - then indeed, Allah is Forgiving and Merciful", "حُرِّمَتۡ عَلَيۡكُمُ ٱلۡمَيۡتَةُ وَٱلدَّمُ وَلَحۡمُ ٱلۡخِنزِيرِ وَمَآ أُهِلَّ لِغَيۡرِ ٱللَّهِ بِهِۦ وَٱلۡمُنۡخَنِقَةُ وَٱلۡمَوۡقُوذَةُ وَٱلۡمُتَرَدِّيَةُ وَٱلنَّطِيحَةُ وَمَآ أَكَلَ ٱلسَّبُعُ إِلَّا مَا ذَكَّيۡتُمۡ وَمَا ذُبِحَ عَلَى ٱلنُّصُبِ وَأَن تَسۡتَقۡسِمُواْ بِٱلۡأَزۡلَٰمِۚ ذَٰلِكُمۡ فِسۡقٌۗ ٱلۡيَوۡمَ يَئِسَ ٱلَّذِينَ كَفَرُواْ مِن دِينِكُمۡ فَلَا تَخۡشَوۡهُمۡ وَٱخۡشَوۡنِۚ ٱلۡيَوۡمَ أَكۡمَلۡتُ لَكُمۡ دِينَكُمۡ وَأَتۡمَمۡتُ عَلَيۡكُمۡ نِعۡمَتِي وَرَضِيتُ لَكُمُ ٱلۡإِسۡلَٰمَ دِينٗاۚ فَمَنِ ٱضۡطُرَّ فِي مَخۡمَصَةٍ غَيۡرَ مُتَجَانِفٖ لِّإِثۡمٖ فَإِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ", "Hurrimat AAalaykumu almaytatu waalddamu walahmu alkhinzeeri wama ohilla lighayri Allahi bihi waalmunkhaniqatu waalmawqoothatu waalmutaraddiyatu waalnnateehatu wama akala alssabuAAu illa ma thakkaytum wama thubiha AAala alnnusubi waan tastaqsimoo bialazlami thalikum fisqun alyawma yaisa allatheena kafaroo min deenikum fala takhshawhum waikhshawni alyawma akmaltu lakum deenakum waatmamtu AAalaykum niAAmatee waradeetu lakumu alislama deenan famani idturra fee makhmasatin ghayra mutajanifin liithmin fainna Allaha ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 4, "They ask you, [O Muhammad], what has been made lawful for them. Say, ""Lawful for you are [all] good foods and [game caught by] what you have trained of hunting animals which you train as Allah has taught you. So eat of what they catch for you, and mention the name of Allah upon it, and fear Allah."" Indeed, Allah is swift in account", "يَسۡـَٔلُونَكَ مَاذَآ أُحِلَّ لَهُمۡۖ قُلۡ أُحِلَّ لَكُمُ ٱلطَّيِّبَٰتُ وَمَا عَلَّمۡتُم مِّنَ ٱلۡجَوَارِحِ مُكَلِّبِينَ تُعَلِّمُونَهُنَّ مِمَّا عَلَّمَكُمُ ٱللَّهُۖ فَكُلُواْ مِمَّآ أَمۡسَكۡنَ عَلَيۡكُمۡ وَٱذۡكُرُواْ ٱسۡمَ ٱللَّهِ عَلَيۡهِۖ وَٱتَّقُواْ ٱللَّهَۚ إِنَّ ٱللَّهَ سَرِيعُ ٱلۡحِسَابِ", "Yasaloonaka matha ohilla lahum qul ohilla lakumu alttayyibatu wama AAallamtum mina aljawarihi mukallibeena tuAAallimoonahunna mimma AAallamakumu Allahu fakuloo mimma amsakna AAalaykum waothkuroo isma Allahi AAalayhi waittaqoo Allaha inna Allaha sareeAAu alhisabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 5, "This day [all] good foods have been made lawful, and the food of those who were given the Scripture is lawful for you and your food is lawful for them. And [lawful in marriage are] chaste women from among the believers and chaste women from among those who were given the Scripture before you, when you have given them their due compensation, desiring chastity, not unlawful sexual intercourse or taking [secret] lovers. And whoever denies the faith - his work has become worthless, and he, in the Hereafter, will be among the losers", "ٱلۡيَوۡمَ أُحِلَّ لَكُمُ ٱلطَّيِّبَٰتُۖ وَطَعَامُ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ حِلّٞ لَّكُمۡ وَطَعَامُكُمۡ حِلّٞ لَّهُمۡۖ وَٱلۡمُحۡصَنَٰتُ مِنَ ٱلۡمُؤۡمِنَٰتِ وَٱلۡمُحۡصَنَٰتُ مِنَ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ مِن قَبۡلِكُمۡ إِذَآ ءَاتَيۡتُمُوهُنَّ أُجُورَهُنَّ مُحۡصِنِينَ غَيۡرَ مُسَٰفِحِينَ وَلَا مُتَّخِذِيٓ أَخۡدَانٖۗ وَمَن يَكۡفُرۡ بِٱلۡإِيمَٰنِ فَقَدۡ حَبِطَ عَمَلُهُۥ وَهُوَ فِي ٱلۡأٓخِرَةِ مِنَ ٱلۡخَٰسِرِينَ", "Alyawma ohilla lakumu alttayyibatu wataAAamu allatheena ootoo alkitaba hillun lakum wataAAamukum hillun lahum waalmuhsanatu mina almuminati waalmuhsanatu mina allatheena ootoo alkitaba min qablikum itha ataytumoohunna ojoorahunna muhsineena ghayra musafiheena wala muttakhithee akhdanin waman yakfur bialeemani faqad habita AAamaluhu wahuwa fee alakhirati mina alkhasireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 6, "O you who have believed, when you rise to [perform] prayer, wash your faces and your forearms to the elbows and wipe over your heads and wash your feet to the ankles. And if you are in a state of janabah, then purify yourselves. But if you are ill or on a journey or one of you comes from the place of relieving himself or you have contacted women and do not find water, then seek clean earth and wipe over your faces and hands with it. Allah does not intend to make difficulty for you, but He intends to purify you and complete His favor upon you that you may be grateful", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِذَا قُمۡتُمۡ إِلَى ٱلصَّلَوٰةِ فَٱغۡسِلُواْ وُجُوهَكُمۡ وَأَيۡدِيَكُمۡ إِلَى ٱلۡمَرَافِقِ وَٱمۡسَحُواْ بِرُءُوسِكُمۡ وَأَرۡجُلَكُمۡ إِلَى ٱلۡكَعۡبَيۡنِۚ وَإِن كُنتُمۡ جُنُبٗا فَٱطَّهَّرُواْۚ وَإِن كُنتُم مَّرۡضَىٰٓ أَوۡ عَلَىٰ سَفَرٍ أَوۡ جَآءَ أَحَدٞ مِّنكُم مِّنَ ٱلۡغَآئِطِ أَوۡ لَٰمَسۡتُمُ ٱلنِّسَآءَ فَلَمۡ تَجِدُواْ مَآءٗ فَتَيَمَّمُواْ صَعِيدٗا طَيِّبٗا فَٱمۡسَحُواْ بِوُجُوهِكُمۡ وَأَيۡدِيكُم مِّنۡهُۚ مَا يُرِيدُ ٱللَّهُ لِيَجۡعَلَ عَلَيۡكُم مِّنۡ حَرَجٖ وَلَٰكِن يُرِيدُ لِيُطَهِّرَكُمۡ وَلِيُتِمَّ نِعۡمَتَهُۥ عَلَيۡكُمۡ لَعَلَّكُمۡ تَشۡكُرُونَ", "Ya ayyuha allatheena amanoo itha qumtum ila alssalati faighsiloo wujoohakum waaydiyakum ila almarafiqi waimsahoo biruoosikum waarjulakum ila alkaAAbayni wain kuntum junuban faittahharoo wain kuntum marda aw AAala safarin aw jaa ahadun minkum mina alghaiti aw lamastumu alnnisaa falam tajidoo maan fatayammamoo saAAeedan tayyiban faimsahoo biwujoohikum waaydeekum minhu ma yureedu Allahu liyajAAala AAalaykum min harajin walakin yureedu liyutahhirakum waliyutimma niAAmatahu AAalaykum laAAallakum tashkuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 7, "And remember the favor of Allah upon you and His covenant with which He bound you when you said, ""We hear and we obey""; and fear Allah. Indeed, Allah is Knowing of that within the breasts", "وَٱذۡكُرُواْ نِعۡمَةَ ٱللَّهِ عَلَيۡكُمۡ وَمِيثَٰقَهُ ٱلَّذِي وَاثَقَكُم بِهِۦٓ إِذۡ قُلۡتُمۡ سَمِعۡنَا وَأَطَعۡنَاۖ وَٱتَّقُواْ ٱللَّهَۚ إِنَّ ٱللَّهَ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ", "Waothkuroo niAAmata Allahi AAalaykum wameethaqahu allathee wathaqakum bihi ith qultum samiAAna waataAAna waittaqoo Allaha inna Allaha AAaleemun bithati alssudoori");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 8, "O you who have believed, be persistently standing firm for Allah, witnesses in justice, and do not let the hatred of a people prevent you from being just. Be just; that is nearer to righteousness. And fear Allah; indeed, Allah is Acquainted with what you do", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ كُونُواْ قَوَّـٰمِينَ لِلَّهِ شُهَدَآءَ بِٱلۡقِسۡطِۖ وَلَا يَجۡرِمَنَّكُمۡ شَنَـَٔانُ قَوۡمٍ عَلَىٰٓ أَلَّا تَعۡدِلُواْۚ ٱعۡدِلُواْ هُوَ أَقۡرَبُ لِلتَّقۡوَىٰۖ وَٱتَّقُواْ ٱللَّهَۚ إِنَّ ٱللَّهَ خَبِيرُۢ بِمَا تَعۡمَلُونَ", "Ya ayyuha allatheena amanoo koonoo qawwameena lillahi shuhadaa bialqisti wala yajrimannakum shanaanu qawmin AAala alla taAAdiloo iAAdiloo huwa aqrabu lilttaqwa waittaqoo Allaha inna Allaha khabeerun bima taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 9, "Allah has promised those who believe and do righteous deeds [that] for them there is forgiveness and great reward", "وَعَدَ ٱللَّهُ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ لَهُم مَّغۡفِرَةٞ وَأَجۡرٌ عَظِيمٞ", "WaAAada Allahu allatheena amanoo waAAamiloo alssalihati lahum maghfiratun waajrun AAatheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 10, "But those who disbelieve and deny Our signs - those are the companions of Hellfire", "وَٱلَّذِينَ كَفَرُواْ وَكَذَّبُواْ بِـَٔايَٰتِنَآ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلۡجَحِيمِ", "Waallatheena kafaroo wakaththaboo biayatina olaika ashabu aljaheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 11, "O you who have believed, remember the favor of Allah upon you when a people determined to extend their hands [in aggression] against you, but He withheld their hands from you; and fear Allah. And upon Allah let the believers rely", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱذۡكُرُواْ نِعۡمَتَ ٱللَّهِ عَلَيۡكُمۡ إِذۡ هَمَّ قَوۡمٌ أَن يَبۡسُطُوٓاْ إِلَيۡكُمۡ أَيۡدِيَهُمۡ فَكَفَّ أَيۡدِيَهُمۡ عَنكُمۡۖ وَٱتَّقُواْ ٱللَّهَۚ وَعَلَى ٱللَّهِ فَلۡيَتَوَكَّلِ ٱلۡمُؤۡمِنُونَ", "Ya ayyuha allatheena amanoo othkuroo niAAmata Allahi AAalaykum ith hamma qawmun an yabsutoo ilaykum aydiyahum fakaffa aydiyahum AAankum waittaqoo Allaha waAAala Allahi falyatawakkali almuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 12, "And Allah had already taken a covenant from the Children of Israel, and We delegated from among them twelve leaders. And Allah said, ""I am with you. If you establish prayer and give zakah and believe in My messengers and support them and loan Allah a goodly loan, I will surely remove from you your misdeeds and admit you to gardens beneath which rivers flow. But whoever of you disbelieves after that has certainly strayed from the soundness of the way", "۞وَلَقَدۡ أَخَذَ ٱللَّهُ مِيثَٰقَ بَنِيٓ إِسۡرَـٰٓءِيلَ وَبَعَثۡنَا مِنۡهُمُ ٱثۡنَيۡ عَشَرَ نَقِيبٗاۖ وَقَالَ ٱللَّهُ إِنِّي مَعَكُمۡۖ لَئِنۡ أَقَمۡتُمُ ٱلصَّلَوٰةَ وَءَاتَيۡتُمُ ٱلزَّكَوٰةَ وَءَامَنتُم بِرُسُلِي وَعَزَّرۡتُمُوهُمۡ وَأَقۡرَضۡتُمُ ٱللَّهَ قَرۡضًا حَسَنٗا لَّأُكَفِّرَنَّ عَنكُمۡ سَيِّـَٔاتِكُمۡ وَلَأُدۡخِلَنَّكُمۡ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُۚ فَمَن كَفَرَ بَعۡدَ ذَٰلِكَ مِنكُمۡ فَقَدۡ ضَلَّ سَوَآءَ ٱلسَّبِيلِ", "Walaqad akhatha Allahu meethaqa banee israeela wabaAAathna minhumu ithnay AAashara naqeeban waqala Allahu innee maAAakum lain aqamtumu alssalata waataytumu alzzakata waamantum birusulee waAAazzartumoohum waaqradtumu Allaha qardan hasanan laokaffiranna AAankum sayyiatikum walaodkhilannakum jannatin tajree min tahtiha alanharu faman kafara baAAda thalika minkum faqad dalla sawaa alssabeeli");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 13, "So for their breaking of the covenant We cursed them and made their hearts hard. They distort words from their [proper] usages and have forgotten a portion of that of which they were reminded. And you will still observe deceit among them, except a few of them. But pardon them and overlook [their misdeeds]. Indeed, Allah loves the doers of good", "فَبِمَا نَقۡضِهِم مِّيثَٰقَهُمۡ لَعَنَّـٰهُمۡ وَجَعَلۡنَا قُلُوبَهُمۡ قَٰسِيَةٗۖ يُحَرِّفُونَ ٱلۡكَلِمَ عَن مَّوَاضِعِهِۦ وَنَسُواْ حَظّٗا مِّمَّا ذُكِّرُواْ بِهِۦۚ وَلَا تَزَالُ تَطَّلِعُ عَلَىٰ خَآئِنَةٖ مِّنۡهُمۡ إِلَّا قَلِيلٗا مِّنۡهُمۡۖ فَٱعۡفُ عَنۡهُمۡ وَٱصۡفَحۡۚ إِنَّ ٱللَّهَ يُحِبُّ ٱلۡمُحۡسِنِينَ", "Fabima naqdihim meethaqahum laAAannahum wajaAAalna quloobahum qasiyatan yuharrifoona alkalima AAan mawadiAAihi wanasoo haththan mimma thukkiroo bihi wala tazalu tattaliAAu AAala khainatin minhum illa qaleelan minhum faoAAfu AAanhum waisfah inna Allaha yuhibbu almuhsineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 14, "And from those who say, ""We are Christians"" We took their covenant; but they forgot a portion of that of which they were reminded. So We caused among them animosity and hatred until the Day of Resurrection. And Allah is going to inform them about what they used to do", "وَمِنَ ٱلَّذِينَ قَالُوٓاْ إِنَّا نَصَٰرَىٰٓ أَخَذۡنَا مِيثَٰقَهُمۡ فَنَسُواْ حَظّٗا مِّمَّا ذُكِّرُواْ بِهِۦ فَأَغۡرَيۡنَا بَيۡنَهُمُ ٱلۡعَدَاوَةَ وَٱلۡبَغۡضَآءَ إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِۚ وَسَوۡفَ يُنَبِّئُهُمُ ٱللَّهُ بِمَا كَانُواْ يَصۡنَعُونَ", "Wamina allatheena qaloo inna nasara akhathna meethaqahum fanasoo haththan mimma thukkiroo bihi faaghrayna baynahumu alAAadawata waalbaghdaa ila yawmi alqiyamati wasawfa yunabbiohumu Allahu bima kanoo yasnaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 15, "O People of the Scripture, there has come to you Our Messenger making clear to you much of what you used to conceal of the Scripture and overlooking much. There has come to you from Allah a light and a clear Book", "يَـٰٓأَهۡلَ ٱلۡكِتَٰبِ قَدۡ جَآءَكُمۡ رَسُولُنَا يُبَيِّنُ لَكُمۡ كَثِيرٗا مِّمَّا كُنتُمۡ تُخۡفُونَ مِنَ ٱلۡكِتَٰبِ وَيَعۡفُواْ عَن كَثِيرٖۚ قَدۡ جَآءَكُم مِّنَ ٱللَّهِ نُورٞ وَكِتَٰبٞ مُّبِينٞ", "Ya ahla alkitabi qad jaakum rasooluna yubayyinu lakum katheeran mimma kuntum tukhfoona mina alkitabi wayaAAfoo AAan katheerin qad jaakum mina Allahi noorun wakitabun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 16, "By which Allah guides those who pursue His pleasure to the ways of peace and brings them out from darknesses into the light, by His permission, and guides them to a straight path", "يَهۡدِي بِهِ ٱللَّهُ مَنِ ٱتَّبَعَ رِضۡوَٰنَهُۥ سُبُلَ ٱلسَّلَٰمِ وَيُخۡرِجُهُم مِّنَ ٱلظُّلُمَٰتِ إِلَى ٱلنُّورِ بِإِذۡنِهِۦ وَيَهۡدِيهِمۡ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ", "Yahdee bihi Allahu mani ittabaAAa ridwanahu subula alssalami wayukhrijuhum mina alththulumati ila alnnoori biithnihi wayahdeehim ila siratin mustaqeemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 17, "They have certainly disbelieved who say that Allah is Christ, the son of Mary. Say, ""Then who could prevent Allah at all if He had intended to destroy Christ, the son of Mary, or his mother or everyone on the earth?"" And to Allah belongs the dominion of the heavens and the earth and whatever is between them. He creates what He wills, and Allah is over all things competent", "لَّقَدۡ كَفَرَ ٱلَّذِينَ قَالُوٓاْ إِنَّ ٱللَّهَ هُوَ ٱلۡمَسِيحُ ٱبۡنُ مَرۡيَمَۚ قُلۡ فَمَن يَمۡلِكُ مِنَ ٱللَّهِ شَيۡـًٔا إِنۡ أَرَادَ أَن يُهۡلِكَ ٱلۡمَسِيحَ ٱبۡنَ مَرۡيَمَ وَأُمَّهُۥ وَمَن فِي ٱلۡأَرۡضِ جَمِيعٗاۗ وَلِلَّهِ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا بَيۡنَهُمَاۚ يَخۡلُقُ مَا يَشَآءُۚ وَٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ", "Laqad kafara allatheena qaloo inna Allaha huwa almaseehu ibnu maryama qul faman yamliku mina Allahi shayan in arada an yuhlika almaseeha ibna maryama waommahu waman fee alardi jameeAAan walillahi mulku alssamawati waalardi wama baynahuma yakhluqu ma yashao waAllahu AAala kulli shayin qadeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 18, "But the Jews and the Christians say, ""We are the children of Allah and His beloved."" Say, ""Then why does He punish you for your sins?"" Rather, you are human beings from among those He has created. He forgives whom He wills, and He punishes whom He wills. And to Allah belongs the dominion of the heavens and the earth and whatever is between them, and to Him is the [final] destination", "وَقَالَتِ ٱلۡيَهُودُ وَٱلنَّصَٰرَىٰ نَحۡنُ أَبۡنَـٰٓؤُاْ ٱللَّهِ وَأَحِبَّـٰٓؤُهُۥۚ قُلۡ فَلِمَ يُعَذِّبُكُم بِذُنُوبِكُمۖ بَلۡ أَنتُم بَشَرٞ مِّمَّنۡ خَلَقَۚ يَغۡفِرُ لِمَن يَشَآءُ وَيُعَذِّبُ مَن يَشَآءُۚ وَلِلَّهِ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا بَيۡنَهُمَاۖ وَإِلَيۡهِ ٱلۡمَصِيرُ", "Waqalati alyahoodu waalnnasara nahnu abnao Allahi waahibbaohu qul falima yuAAaththibukum bithunoobikum bal antum basharun mimman khalaqa yaghfiru liman yashao wayuAAaththibu man yashao walillahi mulku alssamawati waalardi wama baynahuma wailayhi almaseeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 19, "O People of the Scripture, there has come to you Our Messenger to make clear to you [the religion] after a period [of suspension] of messengers, lest you say, ""There came not to us any bringer of good tidings or a warner."" But there has come to you a bringer of good tidings and a warner. And Allah is over all things competent", "يَـٰٓأَهۡلَ ٱلۡكِتَٰبِ قَدۡ جَآءَكُمۡ رَسُولُنَا يُبَيِّنُ لَكُمۡ عَلَىٰ فَتۡرَةٖ مِّنَ ٱلرُّسُلِ أَن تَقُولُواْ مَا جَآءَنَا مِنۢ بَشِيرٖ وَلَا نَذِيرٖۖ فَقَدۡ جَآءَكُم بَشِيرٞ وَنَذِيرٞۗ وَٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ", "Ya ahla alkitabi qad jaakum rasooluna yubayyinu lakum AAala fatratin mina alrrusuli an taqooloo ma jaana min basheerin wala natheerin faqad jaakum basheerun wanatheerun waAllahu AAala kulli shayin qadeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 20, "And [mention, O Muhammad], when Moses said to his people, ""O my people, remember the favor of Allah upon you when He appointed among you prophets and made you possessors and gave you that which He had not given anyone among the worlds", "وَإِذۡ قَالَ مُوسَىٰ لِقَوۡمِهِۦ يَٰقَوۡمِ ٱذۡكُرُواْ نِعۡمَةَ ٱللَّهِ عَلَيۡكُمۡ إِذۡ جَعَلَ فِيكُمۡ أَنۢبِيَآءَ وَجَعَلَكُم مُّلُوكٗا وَءَاتَىٰكُم مَّا لَمۡ يُؤۡتِ أَحَدٗا مِّنَ ٱلۡعَٰلَمِينَ", "Waith qala moosa liqawmihi ya qawmi othkuroo niAAmata Allahi AAalaykum ith jaAAala feekum anbiyaa wajaAAalakum mulookan waatakum ma lam yuti ahadan mina alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 21, "O my people, enter the Holy Land which Allah has assigned to you and do not turn back [from fighting in Allah 's cause] and [thus] become losers", "يَٰقَوۡمِ ٱدۡخُلُواْ ٱلۡأَرۡضَ ٱلۡمُقَدَّسَةَ ٱلَّتِي كَتَبَ ٱللَّهُ لَكُمۡ وَلَا تَرۡتَدُّواْ عَلَىٰٓ أَدۡبَارِكُمۡ فَتَنقَلِبُواْ خَٰسِرِينَ", "Ya qawmi odkhuloo alarda almuqaddasata allatee kataba Allahu lakum wala tartaddoo AAala adbarikum fatanqaliboo khasireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 22, "They said, ""O Moses, indeed within it is a people of tyrannical strength, and indeed, we will never enter it until they leave it; but if they leave it, then we will enter", "قَالُواْ يَٰمُوسَىٰٓ إِنَّ فِيهَا قَوۡمٗا جَبَّارِينَ وَإِنَّا لَن نَّدۡخُلَهَا حَتَّىٰ يَخۡرُجُواْ مِنۡهَا فَإِن يَخۡرُجُواْ مِنۡهَا فَإِنَّا دَٰخِلُونَ", "Qaloo ya moosa inna feeha qawman jabbareena wainna lan nadkhulaha hatta yakhrujoo minha fain yakhrujoo minha fainna dakhiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 23, "Said two men from those who feared [to disobey] upon whom Allah had bestowed favor, ""Enter upon them through the gate, for when you have entered it, you will be predominant. And upon Allah rely, if you should be believers", "قَالَ رَجُلَانِ مِنَ ٱلَّذِينَ يَخَافُونَ أَنۡعَمَ ٱللَّهُ عَلَيۡهِمَا ٱدۡخُلُواْ عَلَيۡهِمُ ٱلۡبَابَ فَإِذَا دَخَلۡتُمُوهُ فَإِنَّكُمۡ غَٰلِبُونَۚ وَعَلَى ٱللَّهِ فَتَوَكَّلُوٓاْ إِن كُنتُم مُّؤۡمِنِينَ", "Qala rajulani mina allatheena yakhafoona anAAama Allahu AAalayhima odkhuloo AAalayhimu albaba faitha dakhaltumoohu fainnakum ghaliboona waAAala Allahi fatawakkaloo in kuntum mumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 24, "They said, ""O Moses, indeed we will not enter it, ever, as long as they are within it; so go, you and your Lord, and fight. Indeed, we are remaining right here", "قَالُواْ يَٰمُوسَىٰٓ إِنَّا لَن نَّدۡخُلَهَآ أَبَدٗا مَّا دَامُواْ فِيهَا فَٱذۡهَبۡ أَنتَ وَرَبُّكَ فَقَٰتِلَآ إِنَّا هَٰهُنَا قَٰعِدُونَ", "Qaloo ya moosa inna lan nadkhulaha abadan ma damoo feeha faithhab anta warabbuka faqatila inna hahuna qaAAidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 25, "[Moses] said, ""My Lord, indeed I do not possess except myself and my brother, so part us from the defiantly disobedient people", "قَالَ رَبِّ إِنِّي لَآ أَمۡلِكُ إِلَّا نَفۡسِي وَأَخِيۖ فَٱفۡرُقۡ بَيۡنَنَا وَبَيۡنَ ٱلۡقَوۡمِ ٱلۡفَٰسِقِينَ", "Qala rabbi innee la amliku illa nafsee waakhee faofruq baynana wabayna alqawmi alfasiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 26, "[Allah] said, ""Then indeed, it is forbidden to them for forty years [in which] they will wander throughout the land. So do not grieve over the defiantly disobedient people", "قَالَ فَإِنَّهَا مُحَرَّمَةٌ عَلَيۡهِمۡۛ أَرۡبَعِينَ سَنَةٗۛ يَتِيهُونَ فِي ٱلۡأَرۡضِۚ فَلَا تَأۡسَ عَلَى ٱلۡقَوۡمِ ٱلۡفَٰسِقِينَ", "Qala fainnaha muharramatun AAalayhim arbaAAeena sanatan yateehoona fee alardi fala tasa AAala alqawmi alfasiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 27, "And recite to them the story of Adam's two sons, in truth, when they both offered a sacrifice [to Allah], and it was accepted from one of them but was not accepted from the other. Said [the latter], ""I will surely kill you."" Said [the former], ""Indeed, Allah only accepts from the righteous [who fear Him]", "۞وَٱتۡلُ عَلَيۡهِمۡ نَبَأَ ٱبۡنَيۡ ءَادَمَ بِٱلۡحَقِّ إِذۡ قَرَّبَا قُرۡبَانٗا فَتُقُبِّلَ مِنۡ أَحَدِهِمَا وَلَمۡ يُتَقَبَّلۡ مِنَ ٱلۡأٓخَرِ قَالَ لَأَقۡتُلَنَّكَۖ قَالَ إِنَّمَا يَتَقَبَّلُ ٱللَّهُ مِنَ ٱلۡمُتَّقِينَ", "Waotlu AAalayhim nabaa ibnay adama bialhaqqi ith qarraba qurbanan fatuqubbila min ahadihima walam yutaqabbal mina alakhari qala laaqtulannaka qala innama yataqabbalu Allahu mina almuttaqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 28, "If you should raise your hand against me to kill me - I shall not raise my hand against you to kill you. Indeed, I fear Allah, Lord of the worlds", "لَئِنۢ بَسَطتَ إِلَيَّ يَدَكَ لِتَقۡتُلَنِي مَآ أَنَا۠ بِبَاسِطٖ يَدِيَ إِلَيۡكَ لِأَقۡتُلَكَۖ إِنِّيٓ أَخَافُ ٱللَّهَ رَبَّ ٱلۡعَٰلَمِينَ", "Lain basatta ilayya yadaka litaqtulanee ma ana bibasitin yadiya ilayka liaqtulaka innee akhafu Allaha rabba alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 29, "Indeed I want you to obtain [thereby] my sin and your sin so you will be among the companions of the Fire. And that is the recompense of wrongdoers", "إِنِّيٓ أُرِيدُ أَن تَبُوٓأَ بِإِثۡمِي وَإِثۡمِكَ فَتَكُونَ مِنۡ أَصۡحَٰبِ ٱلنَّارِۚ وَذَٰلِكَ جَزَـٰٓؤُاْ ٱلظَّـٰلِمِينَ", "Innee oreedu an tabooa biithmee waithmika fatakoona min ashabi alnnari wathalika jazao alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 30, "And his soul permitted to him the murder of his brother, so he killed him and became among the losers", "فَطَوَّعَتۡ لَهُۥ نَفۡسُهُۥ قَتۡلَ أَخِيهِ فَقَتَلَهُۥ فَأَصۡبَحَ مِنَ ٱلۡخَٰسِرِينَ", "FatawwaAAat lahu nafsuhu qatla akheehi faqatalahu faasbaha mina alkhasireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 31, "Then Allah sent a crow searching in the ground to show him how to hide the disgrace of his brother. He said, ""O woe to me! Have I failed to be like this crow and hide the body of my brother?"" And he became of the regretful", "فَبَعَثَ ٱللَّهُ غُرَابٗا يَبۡحَثُ فِي ٱلۡأَرۡضِ لِيُرِيَهُۥ كَيۡفَ يُوَٰرِي سَوۡءَةَ أَخِيهِۚ قَالَ يَٰوَيۡلَتَىٰٓ أَعَجَزۡتُ أَنۡ أَكُونَ مِثۡلَ هَٰذَا ٱلۡغُرَابِ فَأُوَٰرِيَ سَوۡءَةَ أَخِيۖ فَأَصۡبَحَ مِنَ ٱلنَّـٰدِمِينَ", "FabaAAatha Allahu ghuraban yabhathu fee alardi liyuriyahu kayfa yuwaree sawata akheehi qala ya waylata aAAajaztu an akoona mithla hatha alghurabi faowariya sawata akhee faasbaha mina alnnadimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 32, "Because of that, We decreed upon the Children of Israel that whoever kills a soul unless for a soul or for corruption [done] in the land - it is as if he had slain mankind entirely. And whoever saves one - it is as if he had saved mankind entirely. And our messengers had certainly come to them with clear proofs. Then indeed many of them, [even] after that, throughout the land, were transgressors", "مِنۡ أَجۡلِ ذَٰلِكَ كَتَبۡنَا عَلَىٰ بَنِيٓ إِسۡرَـٰٓءِيلَ أَنَّهُۥ مَن قَتَلَ نَفۡسَۢا بِغَيۡرِ نَفۡسٍ أَوۡ فَسَادٖ فِي ٱلۡأَرۡضِ فَكَأَنَّمَا قَتَلَ ٱلنَّاسَ جَمِيعٗا وَمَنۡ أَحۡيَاهَا فَكَأَنَّمَآ أَحۡيَا ٱلنَّاسَ جَمِيعٗاۚ وَلَقَدۡ جَآءَتۡهُمۡ رُسُلُنَا بِٱلۡبَيِّنَٰتِ ثُمَّ إِنَّ كَثِيرٗا مِّنۡهُم بَعۡدَ ذَٰلِكَ فِي ٱلۡأَرۡضِ لَمُسۡرِفُونَ", "Min ajli thalika katabna AAala banee israeela annahu man qatala nafsan bighayri nafsin aw fasadin fee alardi fakaannama qatala alnnasa jameeAAan waman ahyaha fakaannama ahya alnnasa jameeAAan walaqad jaa thum rusuluna bialbayyinati thumma inna katheeran minhum baAAda thalika fee alardi lamusrifoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 33, "Indeed, the penalty for those who wage war against Allah and His Messenger and strive upon earth [to cause] corruption is none but that they be killed or crucified or that their hands and feet be cut off from opposite sides or that they be exiled from the land. That is for them a disgrace in this world; and for them in the Hereafter is a great punishment", "إِنَّمَا جَزَـٰٓؤُاْ ٱلَّذِينَ يُحَارِبُونَ ٱللَّهَ وَرَسُولَهُۥ وَيَسۡعَوۡنَ فِي ٱلۡأَرۡضِ فَسَادًا أَن يُقَتَّلُوٓاْ أَوۡ يُصَلَّبُوٓاْ أَوۡ تُقَطَّعَ أَيۡدِيهِمۡ وَأَرۡجُلُهُم مِّنۡ خِلَٰفٍ أَوۡ يُنفَوۡاْ مِنَ ٱلۡأَرۡضِۚ ذَٰلِكَ لَهُمۡ خِزۡيٞ فِي ٱلدُّنۡيَاۖ وَلَهُمۡ فِي ٱلۡأٓخِرَةِ عَذَابٌ عَظِيمٌ", "Innama jazao allatheena yuhariboona Allaha warasoolahu wayasAAawna fee alardi fasadan an yuqattaloo aw yusallaboo aw tuqattaAAa aydeehim waarjuluhum min khilafin aw yunfaw mina alardi thalika lahum khizyun fee alddunya walahum fee alakhirati AAathabun AAatheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 34, "Except for those who return [repenting] before you apprehend them. And know that Allah is Forgiving and Merciful", "إِلَّا ٱلَّذِينَ تَابُواْ مِن قَبۡلِ أَن تَقۡدِرُواْ عَلَيۡهِمۡۖ فَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ", "Illa allatheena taboo min qabli an taqdiroo AAalayhim faiAAlamoo anna Allaha ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 35, "O you who have believed, fear Allah and seek the means [of nearness] to Him and strive in His cause that you may succeed", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱتَّقُواْ ٱللَّهَ وَٱبۡتَغُوٓاْ إِلَيۡهِ ٱلۡوَسِيلَةَ وَجَٰهِدُواْ فِي سَبِيلِهِۦ لَعَلَّكُمۡ تُفۡلِحُونَ", "Ya ayyuha allatheena amanoo ittaqoo Allaha waibtaghoo ilayhi alwaseelata wajahidoo fee sabeelihi laAAallakum tuflihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 36, "Indeed, those who disbelieve - if they should have all that is in the earth and the like of it with it by which to ransom themselves from the punishment of the Day of Resurrection, it will not be accepted from them, and for them is a painful punishment", "إِنَّ ٱلَّذِينَ كَفَرُواْ لَوۡ أَنَّ لَهُم مَّا فِي ٱلۡأَرۡضِ جَمِيعٗا وَمِثۡلَهُۥ مَعَهُۥ لِيَفۡتَدُواْ بِهِۦ مِنۡ عَذَابِ يَوۡمِ ٱلۡقِيَٰمَةِ مَا تُقُبِّلَ مِنۡهُمۡۖ وَلَهُمۡ عَذَابٌ أَلِيمٞ", "Inna allatheena kafaroo law anna lahum ma fee alardi jameeAAan wamithlahu maAAahu liyaftadoo bihi min AAathabi yawmi alqiyamati ma tuqubbila minhum walahum AAathabun aleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 37, "They will wish to get out of the Fire, but never are they to emerge therefrom, and for them is an enduring punishment", "يُرِيدُونَ أَن يَخۡرُجُواْ مِنَ ٱلنَّارِ وَمَا هُم بِخَٰرِجِينَ مِنۡهَاۖ وَلَهُمۡ عَذَابٞ مُّقِيمٞ", "Yureedoona an yakhrujoo mina alnnari wama hum bikharijeena minha walahum AAathabun muqeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 38, "[As for] the thief, the male and the female, amputate their hands in recompense for what they committed as a deterrent [punishment] from Allah. And Allah is Exalted in Might and Wise", "وَٱلسَّارِقُ وَٱلسَّارِقَةُ فَٱقۡطَعُوٓاْ أَيۡدِيَهُمَا جَزَآءَۢ بِمَا كَسَبَا نَكَٰلٗا مِّنَ ٱللَّهِۗ وَٱللَّهُ عَزِيزٌ حَكِيمٞ", "Waalssariqu waalssariqatu faiqtaAAoo aydiyahuma jazaan bima kasaba nakalan mina Allahi waAllahu AAazeezun hakeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 39, "But whoever repents after his wrongdoing and reforms, indeed, Allah will turn to him in forgiveness. Indeed, Allah is Forgiving and Merciful", "فَمَن تَابَ مِنۢ بَعۡدِ ظُلۡمِهِۦ وَأَصۡلَحَ فَإِنَّ ٱللَّهَ يَتُوبُ عَلَيۡهِۚ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٌ", "Faman taba min baAAdi thulmihi waaslaha fainna Allaha yatoobu AAalayhi inna Allaha ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 40, "Do you not know that to Allah belongs the dominion of the heavens and the earth? He punishes whom He wills and forgives whom He wills, and Allah is over all things competent", "أَلَمۡ تَعۡلَمۡ أَنَّ ٱللَّهَ لَهُۥ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ يُعَذِّبُ مَن يَشَآءُ وَيَغۡفِرُ لِمَن يَشَآءُۗ وَٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ", "Alam taAAlam anna Allaha lahu mulku alssamawati waalardi yuAAaththibu man yashao wayaghfiru liman yashao waAllahu AAala kulli shayin qadeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 41, "O Messenger, let them not grieve you who hasten into disbelief of those who say, ""We believe"" with their mouths, but their hearts believe not, and from among the Jews. [They are] avid listeners to falsehood, listening to another people who have not come to you. They distort words beyond their [proper] usages, saying ""If you are given this, take it; but if you are not given it, then beware."" But he for whom Allah intends fitnah - never will you possess [power to do] for him a thing against Allah. Those are the ones for whom Allah does not intend to purify their hearts. For them in this world is disgrace, and for them in the Hereafter is a great punishment", "۞يَـٰٓأَيُّهَا ٱلرَّسُولُ لَا يَحۡزُنكَ ٱلَّذِينَ يُسَٰرِعُونَ فِي ٱلۡكُفۡرِ مِنَ ٱلَّذِينَ قَالُوٓاْ ءَامَنَّا بِأَفۡوَٰهِهِمۡ وَلَمۡ تُؤۡمِن قُلُوبُهُمۡۛ وَمِنَ ٱلَّذِينَ هَادُواْۛ سَمَّـٰعُونَ لِلۡكَذِبِ سَمَّـٰعُونَ لِقَوۡمٍ ءَاخَرِينَ لَمۡ يَأۡتُوكَۖ يُحَرِّفُونَ ٱلۡكَلِمَ مِنۢ بَعۡدِ مَوَاضِعِهِۦۖ يَقُولُونَ إِنۡ أُوتِيتُمۡ هَٰذَا فَخُذُوهُ وَإِن لَّمۡ تُؤۡتَوۡهُ فَٱحۡذَرُواْۚ وَمَن يُرِدِ ٱللَّهُ فِتۡنَتَهُۥ فَلَن تَمۡلِكَ لَهُۥ مِنَ ٱللَّهِ شَيۡـًٔاۚ أُوْلَـٰٓئِكَ ٱلَّذِينَ لَمۡ يُرِدِ ٱللَّهُ أَن يُطَهِّرَ قُلُوبَهُمۡۚ لَهُمۡ فِي ٱلدُّنۡيَا خِزۡيٞۖ وَلَهُمۡ فِي ٱلۡأٓخِرَةِ عَذَابٌ عَظِيمٞ", "Ya ayyuha alrrasoolu la yahzunka allatheena yusariAAoona fee alkufri mina allatheena qaloo amanna biafwahihim walam tumin quloobuhum wamina allatheena hadoo sammaAAoona lilkathibi sammaAAoona liqawmin akhareena lam yatooka yuharrifoona alkalima min baAAdi mawadiAAihi yaqooloona in ooteetum hatha fakhuthoohu wain lam tutawhu faihtharoo waman yuridi Allahu fitnatahu falan tamlika lahu mina Allahi shayan olaika allatheena lam yuridi Allahu an yutahhira quloobahum lahum fee alddunya khizyun walahum fee alakhirati AAathabun AAatheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 42, "[They are] avid listeners to falsehood, devourers of [what is] unlawful. So if they come to you, [O Muhammad], judge between them or turn away from them. And if you turn away from them - never will they harm you at all. And if you judge, judge between them with justice. Indeed, Allah loves those who act justly", "سَمَّـٰعُونَ لِلۡكَذِبِ أَكَّـٰلُونَ لِلسُّحۡتِۚ فَإِن جَآءُوكَ فَٱحۡكُم بَيۡنَهُمۡ أَوۡ أَعۡرِضۡ عَنۡهُمۡۖ وَإِن تُعۡرِضۡ عَنۡهُمۡ فَلَن يَضُرُّوكَ شَيۡـٔٗاۖ وَإِنۡ حَكَمۡتَ فَٱحۡكُم بَيۡنَهُم بِٱلۡقِسۡطِۚ إِنَّ ٱللَّهَ يُحِبُّ ٱلۡمُقۡسِطِينَ", "SammaAAoona lilkathibi akkaloona lilssuhti fain jaooka faohkum baynahum aw aAArid AAanhum wain tuAArid AAanhum falan yadurrooka shayan wain hakamta faohkum baynahum bialqisti inna Allaha yuhibbu almuqsiteena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 43, "But how is it that they come to you for judgement while they have the Torah, in which is the judgement of Allah? Then they turn away, [even] after that; but those are not [in fact] believers", "وَكَيۡفَ يُحَكِّمُونَكَ وَعِندَهُمُ ٱلتَّوۡرَىٰةُ فِيهَا حُكۡمُ ٱللَّهِ ثُمَّ يَتَوَلَّوۡنَ مِنۢ بَعۡدِ ذَٰلِكَۚ وَمَآ أُوْلَـٰٓئِكَ بِٱلۡمُؤۡمِنِينَ", "Wakayfa yuhakkimoonaka waAAindahumu alttawratu feeha hukmu Allahi thumma yatawallawna min baAAdi thalika wama olaika bialmumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 44, "Indeed, We sent down the Torah, in which was guidance and light. The prophets who submitted [to Allah] judged by it for the Jews, as did the rabbis and scholars by that with which they were entrusted of the Scripture of Allah, and they were witnesses thereto. So do not fear the people but fear Me, and do not exchange My verses for a small price. And whoever does not judge by what Allah has revealed - then it is those who are the disbelievers", "إِنَّآ أَنزَلۡنَا ٱلتَّوۡرَىٰةَ فِيهَا هُدٗى وَنُورٞۚ يَحۡكُمُ بِهَا ٱلنَّبِيُّونَ ٱلَّذِينَ أَسۡلَمُواْ لِلَّذِينَ هَادُواْ وَٱلرَّبَّـٰنِيُّونَ وَٱلۡأَحۡبَارُ بِمَا ٱسۡتُحۡفِظُواْ مِن كِتَٰبِ ٱللَّهِ وَكَانُواْ عَلَيۡهِ شُهَدَآءَۚ فَلَا تَخۡشَوُاْ ٱلنَّاسَ وَٱخۡشَوۡنِ وَلَا تَشۡتَرُواْ بِـَٔايَٰتِي ثَمَنٗا قَلِيلٗاۚ وَمَن لَّمۡ يَحۡكُم بِمَآ أَنزَلَ ٱللَّهُ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡكَٰفِرُونَ", "Inna anzalna alttawrata feeha hudan wanoorun yahkumu biha alnnabiyyoona allatheena aslamoo lillatheena hadoo waalrrabbaniyyoona waalahbaru bima istuhfithoo min kitabi Allahi wakanoo AAalayhi shuhadaa fala takhshawoo alnnasa waikhshawni wala tashtaroo biayatee thamanan qaleelan waman lam yahkum bima anzala Allahu faolaika humu alkafiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 45, "And We ordained for them therein a life for a life, an eye for an eye, a nose for a nose, an ear for an ear, a tooth for a tooth, and for wounds is legal retribution. But whoever gives [up his right as] charity, it is an expiation for him. And whoever does not judge by what Allah has revealed - then it is those who are the wrongdoers", "وَكَتَبۡنَا عَلَيۡهِمۡ فِيهَآ أَنَّ ٱلنَّفۡسَ بِٱلنَّفۡسِ وَٱلۡعَيۡنَ بِٱلۡعَيۡنِ وَٱلۡأَنفَ بِٱلۡأَنفِ وَٱلۡأُذُنَ بِٱلۡأُذُنِ وَٱلسِّنَّ بِٱلسِّنِّ وَٱلۡجُرُوحَ قِصَاصٞۚ فَمَن تَصَدَّقَ بِهِۦ فَهُوَ كَفَّارَةٞ لَّهُۥۚ وَمَن لَّمۡ يَحۡكُم بِمَآ أَنزَلَ ٱللَّهُ فَأُوْلَـٰٓئِكَ هُمُ ٱلظَّـٰلِمُونَ", "Wakatabna AAalayhim feeha anna alnnafsa bialnnafsi waalAAayna bialAAayni waalanfa bialanfi waalothuna bialothuni waalssinna bialssinni waaljurooha qisasun faman tasaddaqa bihi fahuwa kaffaratun lahu waman lam yahkum bima anzala Allahu faolaika humu alththalimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 46, "And We sent, following in their footsteps, Jesus, the son of Mary, confirming that which came before him in the Torah; and We gave him the Gospel, in which was guidance and light and confirming that which preceded it of the Torah as guidance and instruction for the righteous", "وَقَفَّيۡنَا عَلَىٰٓ ءَاثَٰرِهِم بِعِيسَى ٱبۡنِ مَرۡيَمَ مُصَدِّقٗا لِّمَا بَيۡنَ يَدَيۡهِ مِنَ ٱلتَّوۡرَىٰةِۖ وَءَاتَيۡنَٰهُ ٱلۡإِنجِيلَ فِيهِ هُدٗى وَنُورٞ وَمُصَدِّقٗا لِّمَا بَيۡنَ يَدَيۡهِ مِنَ ٱلتَّوۡرَىٰةِ وَهُدٗى وَمَوۡعِظَةٗ لِّلۡمُتَّقِينَ", "Waqaffayna AAala atharihim biAAeesa ibni maryama musaddiqan lima bayna yadayhi mina alttawrati waataynahu alinjeela feehi hudan wanoorun wamusaddiqan lima bayna yadayhi mina alttawrati wahudan wamawAAithatan lilmuttaqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 47, "And let the People of the Gospel judge by what Allah has revealed therein. And whoever does not judge by what Allah has revealed - then it is those who are the defiantly disobedient", "وَلۡيَحۡكُمۡ أَهۡلُ ٱلۡإِنجِيلِ بِمَآ أَنزَلَ ٱللَّهُ فِيهِۚ وَمَن لَّمۡ يَحۡكُم بِمَآ أَنزَلَ ٱللَّهُ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡفَٰسِقُونَ", "Walyahkum ahlu alinjeeli bima anzala Allahu feehi waman lam yahkum bima anzala Allahu faolaika humu alfasiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 48, "And We have revealed to you, [O Muhammad], the Book in truth, confirming that which preceded it of the Scripture and as a criterion over it. So judge between them by what Allah has revealed and do not follow their inclinations away from what has come to you of the truth. To each of you We prescribed a law and a method. Had Allah willed, He would have made you one nation [united in religion], but [He intended] to test you in what He has given you; so race to [all that is] good. To Allah is your return all together, and He will [then] inform you concerning that over which you used to differ", "وَأَنزَلۡنَآ إِلَيۡكَ ٱلۡكِتَٰبَ بِٱلۡحَقِّ مُصَدِّقٗا لِّمَا بَيۡنَ يَدَيۡهِ مِنَ ٱلۡكِتَٰبِ وَمُهَيۡمِنًا عَلَيۡهِۖ فَٱحۡكُم بَيۡنَهُم بِمَآ أَنزَلَ ٱللَّهُۖ وَلَا تَتَّبِعۡ أَهۡوَآءَهُمۡ عَمَّا جَآءَكَ مِنَ ٱلۡحَقِّۚ لِكُلّٖ جَعَلۡنَا مِنكُمۡ شِرۡعَةٗ وَمِنۡهَاجٗاۚ وَلَوۡ شَآءَ ٱللَّهُ لَجَعَلَكُمۡ أُمَّةٗ وَٰحِدَةٗ وَلَٰكِن لِّيَبۡلُوَكُمۡ فِي مَآ ءَاتَىٰكُمۡۖ فَٱسۡتَبِقُواْ ٱلۡخَيۡرَٰتِۚ إِلَى ٱللَّهِ مَرۡجِعُكُمۡ جَمِيعٗا فَيُنَبِّئُكُم بِمَا كُنتُمۡ فِيهِ تَخۡتَلِفُونَ", "Waanzalna ilayka alkitaba bialhaqqi musaddiqan lima bayna yadayhi mina alkitabi wamuhayminan AAalayhi faohkum baynahum bima anzala Allahu wala tattabiAA ahwaahum AAamma jaaka mina alhaqqi likullin jaAAalna minkum shirAAatan waminhajan walaw shaa Allahu lajaAAalakum ommatan wahidatan walakin liyabluwakum fee ma atakum faistabiqoo alkhayrati ila Allahi marjiAAukum jameeAAan fayunabbiokum bima kuntum feehi takhtalifoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 49, "And judge, [O Muhammad], between them by what Allah has revealed and do not follow their inclinations and beware of them, lest they tempt you away from some of what Allah has revealed to you. And if they turn away - then know that Allah only intends to afflict them with some of their [own] sins. And indeed, many among the people are defiantly disobedient", "وَأَنِ ٱحۡكُم بَيۡنَهُم بِمَآ أَنزَلَ ٱللَّهُ وَلَا تَتَّبِعۡ أَهۡوَآءَهُمۡ وَٱحۡذَرۡهُمۡ أَن يَفۡتِنُوكَ عَنۢ بَعۡضِ مَآ أَنزَلَ ٱللَّهُ إِلَيۡكَۖ فَإِن تَوَلَّوۡاْ فَٱعۡلَمۡ أَنَّمَا يُرِيدُ ٱللَّهُ أَن يُصِيبَهُم بِبَعۡضِ ذُنُوبِهِمۡۗ وَإِنَّ كَثِيرٗا مِّنَ ٱلنَّاسِ لَفَٰسِقُونَ", "Waani ohkum baynahum bima anzala Allahu wala tattabiAA ahwaahum waihtharhum an yaftinooka AAan baAAdi ma anzala Allahu ilayka fain tawallaw faiAAlam annama yureedu Allahu an yuseebahum bibaAAdi thunoobihim wainna katheeran mina alnnasi lafasiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 50, "Then is it the judgement of [the time of] ignorance they desire? But who is better than Allah in judgement for a people who are certain [in faith]", "أَفَحُكۡمَ ٱلۡجَٰهِلِيَّةِ يَبۡغُونَۚ وَمَنۡ أَحۡسَنُ مِنَ ٱللَّهِ حُكۡمٗا لِّقَوۡمٖ يُوقِنُونَ", "Afahukma aljahiliyyati yabghoona waman ahsanu mina Allahu hukman liqawmin yooqinoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 51, "O you who have believed, do not take the Jews and the Christians as allies. They are [in fact] allies of one another. And whoever is an ally to them among you - then indeed, he is [one] of them. Indeed, Allah guides not the wrongdoing people", "۞يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَتَّخِذُواْ ٱلۡيَهُودَ وَٱلنَّصَٰرَىٰٓ أَوۡلِيَآءَۘ بَعۡضُهُمۡ أَوۡلِيَآءُ بَعۡضٖۚ وَمَن يَتَوَلَّهُم مِّنكُمۡ فَإِنَّهُۥ مِنۡهُمۡۗ إِنَّ ٱللَّهَ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلظَّـٰلِمِينَ", "Ya ayyuha allatheena amanoo la tattakhithoo alyahooda waalnnasara awliyaa baAAduhum awliyao baAAdin waman yatawallahum minkum fainnahu minhum inna Allaha la yahdee alqawma alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 52, "So you see those in whose hearts is disease hastening into [association with] them, saying, ""We are afraid a misfortune may strike us."" But perhaps Allah will bring conquest or a decision from Him, and they will become, over what they have been concealing within themselves, regretful", "فَتَرَى ٱلَّذِينَ فِي قُلُوبِهِم مَّرَضٞ يُسَٰرِعُونَ فِيهِمۡ يَقُولُونَ نَخۡشَىٰٓ أَن تُصِيبَنَا دَآئِرَةٞۚ فَعَسَى ٱللَّهُ أَن يَأۡتِيَ بِٱلۡفَتۡحِ أَوۡ أَمۡرٖ مِّنۡ عِندِهِۦ فَيُصۡبِحُواْ عَلَىٰ مَآ أَسَرُّواْ فِيٓ أَنفُسِهِمۡ نَٰدِمِينَ", "Fatara allatheena fee quloobihim maradun yusariAAoona feehim yaqooloona nakhsha an tuseebana dairatun faAAasa Allahu an yatiya bialfathi aw amrin min AAindihi fayusbihoo AAala ma asarroo fee anfusihim nadimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 53, "And those who believe will say, ""Are these the ones who swore by Allah their strongest oaths that indeed they were with you?"" Their deeds have become worthless, and they have become losers", "وَيَقُولُ ٱلَّذِينَ ءَامَنُوٓاْ أَهَـٰٓؤُلَآءِ ٱلَّذِينَ أَقۡسَمُواْ بِٱللَّهِ جَهۡدَ أَيۡمَٰنِهِمۡ إِنَّهُمۡ لَمَعَكُمۡۚ حَبِطَتۡ أَعۡمَٰلُهُمۡ فَأَصۡبَحُواْ خَٰسِرِينَ", "Wayaqoolu allatheena amanoo ahaolai allatheena aqsamoo biAllahi jahda aymanihim innahum lamaAAakum habitat aAAmaluhum faasbahoo khasireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 54, "O you who have believed, whoever of you should revert from his religion - Allah will bring forth [in place of them] a people He will love and who will love Him [who are] humble toward the believers, powerful against the disbelievers; they strive in the cause of Allah and do not fear the blame of a critic. That is the favor of Allah; He bestows it upon whom He wills. And Allah is all-Encompassing and Knowing", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ مَن يَرۡتَدَّ مِنكُمۡ عَن دِينِهِۦ فَسَوۡفَ يَأۡتِي ٱللَّهُ بِقَوۡمٖ يُحِبُّهُمۡ وَيُحِبُّونَهُۥٓ أَذِلَّةٍ عَلَى ٱلۡمُؤۡمِنِينَ أَعِزَّةٍ عَلَى ٱلۡكَٰفِرِينَ يُجَٰهِدُونَ فِي سَبِيلِ ٱللَّهِ وَلَا يَخَافُونَ لَوۡمَةَ لَآئِمٖۚ ذَٰلِكَ فَضۡلُ ٱللَّهِ يُؤۡتِيهِ مَن يَشَآءُۚ وَٱللَّهُ وَٰسِعٌ عَلِيمٌ", "Ya ayyuha allatheena amanoo man yartadda minkum AAan deenihi fasawfa yatee Allahu biqawmin yuhibbuhum wayuhibboonahu athillatin AAala almumineena aAAizzatin AAala alkafireena yujahidoona fee sabeeli Allahi wala yakhafoona lawmata laimin thalika fadlu Allahi yuteehi man yashao waAllahu wasiAAun AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 55, "Your ally is none but Allah and [therefore] His Messenger and those who have believed - those who establish prayer and give zakah, and they bow [in worship]", "إِنَّمَا وَلِيُّكُمُ ٱللَّهُ وَرَسُولُهُۥ وَٱلَّذِينَ ءَامَنُواْ ٱلَّذِينَ يُقِيمُونَ ٱلصَّلَوٰةَ وَيُؤۡتُونَ ٱلزَّكَوٰةَ وَهُمۡ رَٰكِعُونَ", "Innama waliyyukumu Allahu warasooluhu waallatheena amanoo allatheena yuqeemoona alssalata wayutoona alzzakata wahum rakiAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 56, "And whoever is an ally of Allah and His Messenger and those who have believed - indeed, the party of Allah - they will be the predominant", "وَمَن يَتَوَلَّ ٱللَّهَ وَرَسُولَهُۥ وَٱلَّذِينَ ءَامَنُواْ فَإِنَّ حِزۡبَ ٱللَّهِ هُمُ ٱلۡغَٰلِبُونَ", "Waman yatawalla Allaha warasoolahu waallatheena amanoo fainna hizba Allahi humu alghaliboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 57, "O you who have believed, take not those who have taken your religion in ridicule and amusement among the ones who were given the Scripture before you nor the disbelievers as allies. And fear Allah, if you should [truly] be believers", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَتَّخِذُواْ ٱلَّذِينَ ٱتَّخَذُواْ دِينَكُمۡ هُزُوٗا وَلَعِبٗا مِّنَ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ مِن قَبۡلِكُمۡ وَٱلۡكُفَّارَ أَوۡلِيَآءَۚ وَٱتَّقُواْ ٱللَّهَ إِن كُنتُم مُّؤۡمِنِينَ", "Ya ayyuha allatheena amanoo la tattakhithoo allatheena ittakhathoo deenakum huzuwan walaAAiban mina allatheena ootoo alkitaba min qablikum waalkuffara awliyaa waittaqoo Allaha in kuntum mumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 58, "And when you call to prayer, they take it in ridicule and amusement. That is because they are a people who do not use reason", "وَإِذَا نَادَيۡتُمۡ إِلَى ٱلصَّلَوٰةِ ٱتَّخَذُوهَا هُزُوٗا وَلَعِبٗاۚ ذَٰلِكَ بِأَنَّهُمۡ قَوۡمٞ لَّا يَعۡقِلُونَ", "Waitha nadaytum ila alssalati ittakhathooha huzuwan walaAAiban thalika biannahum qawmun la yaAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 59, "Say, ""O People of the Scripture, do you resent us except [for the fact] that we have believed in Allah and what was revealed to us and what was revealed before and because most of you are defiantly disobedient", "قُلۡ يَـٰٓأَهۡلَ ٱلۡكِتَٰبِ هَلۡ تَنقِمُونَ مِنَّآ إِلَّآ أَنۡ ءَامَنَّا بِٱللَّهِ وَمَآ أُنزِلَ إِلَيۡنَا وَمَآ أُنزِلَ مِن قَبۡلُ وَأَنَّ أَكۡثَرَكُمۡ فَٰسِقُونَ", "Qul ya ahla alkitabi hal tanqimoona minna illa an amanna biAllahi wama onzila ilayna wama onzila min qablu waanna aktharakum fasiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 60, "Say, ""Shall I inform you of [what is] worse than that as penalty from Allah? [It is that of] those whom Allah has cursed and with whom He became angry and made of them apes and pigs and slaves of Taghut. Those are worse in position and further astray from the sound way", "قُلۡ هَلۡ أُنَبِّئُكُم بِشَرّٖ مِّن ذَٰلِكَ مَثُوبَةً عِندَ ٱللَّهِۚ مَن لَّعَنَهُ ٱللَّهُ وَغَضِبَ عَلَيۡهِ وَجَعَلَ مِنۡهُمُ ٱلۡقِرَدَةَ وَٱلۡخَنَازِيرَ وَعَبَدَ ٱلطَّـٰغُوتَۚ أُوْلَـٰٓئِكَ شَرّٞ مَّكَانٗا وَأَضَلُّ عَن سَوَآءِ ٱلسَّبِيلِ", "Qul hal onabbiokum bisharrin min thalika mathoobatan AAinda Allahi man laAAanahu Allahu waghadiba AAalayhi wajaAAala minhumu alqiradata waalkhanazeera waAAabada alttaghooti olaika sharrun makanan waadallu AAan sawai alssabeeli");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 61, "And when they come to you, they say, ""We believe."" But they have entered with disbelief [in their hearts], and they have certainly left with it. And Allah is most knowing of what they were concealing", "وَإِذَا جَآءُوكُمۡ قَالُوٓاْ ءَامَنَّا وَقَد دَّخَلُواْ بِٱلۡكُفۡرِ وَهُمۡ قَدۡ خَرَجُواْ بِهِۦۚ وَٱللَّهُ أَعۡلَمُ بِمَا كَانُواْ يَكۡتُمُونَ", "Waitha jaookum qaloo amanna waqad dakhaloo bialkufri wahum qad kharajoo bihi waAllahu aAAlamu bima kanoo yaktumoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 62, "And you see many of them hastening into sin and aggression and the devouring of [what is] unlawful. How wretched is what they have been doing", "وَتَرَىٰ كَثِيرٗا مِّنۡهُمۡ يُسَٰرِعُونَ فِي ٱلۡإِثۡمِ وَٱلۡعُدۡوَٰنِ وَأَكۡلِهِمُ ٱلسُّحۡتَۚ لَبِئۡسَ مَا كَانُواْ يَعۡمَلُونَ", "Watara katheeran minhum yusariAAoona fee alithmi waalAAudwani waaklihimu alssuhta labisa ma kanoo yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 63, "Why do the rabbis and religious scholars not forbid them from saying what is sinful and devouring what is unlawful? How wretched is what they have been practicing", "لَوۡلَا يَنۡهَىٰهُمُ ٱلرَّبَّـٰنِيُّونَ وَٱلۡأَحۡبَارُ عَن قَوۡلِهِمُ ٱلۡإِثۡمَ وَأَكۡلِهِمُ ٱلسُّحۡتَۚ لَبِئۡسَ مَا كَانُواْ يَصۡنَعُونَ", "Lawla yanhahumu alrrabbaniyyoona waalahbaru AAan qawlihimu alithma waaklihimu alssuhta labisa ma kanoo yasnaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 64, "And the Jews say, ""The hand of Allah is chained."" Chained are their hands, and cursed are they for what they say. Rather, both His hands are extended; He spends however He wills. And that which has been revealed to you from your Lord will surely increase many of them in transgression and disbelief. And We have cast among them animosity and hatred until the Day of Resurrection. Every time they kindled the fire of war [against you], Allah extinguished it. And they strive throughout the land [causing] corruption, and Allah does not like corrupters", "وَقَالَتِ ٱلۡيَهُودُ يَدُ ٱللَّهِ مَغۡلُولَةٌۚ غُلَّتۡ أَيۡدِيهِمۡ وَلُعِنُواْ بِمَا قَالُواْۘ بَلۡ يَدَاهُ مَبۡسُوطَتَانِ يُنفِقُ كَيۡفَ يَشَآءُۚ وَلَيَزِيدَنَّ كَثِيرٗا مِّنۡهُم مَّآ أُنزِلَ إِلَيۡكَ مِن رَّبِّكَ طُغۡيَٰنٗا وَكُفۡرٗاۚ وَأَلۡقَيۡنَا بَيۡنَهُمُ ٱلۡعَدَٰوَةَ وَٱلۡبَغۡضَآءَ إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِۚ كُلَّمَآ أَوۡقَدُواْ نَارٗا لِّلۡحَرۡبِ أَطۡفَأَهَا ٱللَّهُۚ وَيَسۡعَوۡنَ فِي ٱلۡأَرۡضِ فَسَادٗاۚ وَٱللَّهُ لَا يُحِبُّ ٱلۡمُفۡسِدِينَ", "Waqalati alyahoodu yadu Allahi maghloolatun ghullat aydeehim waluAAinoo bima qaloo bal yadahu mabsootatani yunfiqu kayfa yashao walayazeedanna katheeran minhum ma onzila ilayka min rabbika tughyanan wakufran waalqayna baynahumu alAAadawata waalbaghdaa ila yawmi alqiyamati kullama awqadoo naran lilharbi atfaaha Allahu wayasAAawna fee alardi fasadan waAllahu la yuhibbu almufsideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 65, "And if only the People of the Scripture had believed and feared Allah, We would have removed from them their misdeeds and admitted them to Gardens of Pleasure", "وَلَوۡ أَنَّ أَهۡلَ ٱلۡكِتَٰبِ ءَامَنُواْ وَٱتَّقَوۡاْ لَكَفَّرۡنَا عَنۡهُمۡ سَيِّـَٔاتِهِمۡ وَلَأَدۡخَلۡنَٰهُمۡ جَنَّـٰتِ ٱلنَّعِيمِ", "Walaw anna ahla alkitabi amanoo waittaqaw lakaffarna AAanhum sayyiatihim walaadkhalnahum jannati alnnaAAeemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 66, "And if only they upheld [the law of] the Torah, the Gospel, and what has been revealed to them from their Lord, they would have consumed [provision] from above them and from beneath their feet. Among them are a moderate community, but many of them - evil is that which they do", "وَلَوۡ أَنَّهُمۡ أَقَامُواْ ٱلتَّوۡرَىٰةَ وَٱلۡإِنجِيلَ وَمَآ أُنزِلَ إِلَيۡهِم مِّن رَّبِّهِمۡ لَأَكَلُواْ مِن فَوۡقِهِمۡ وَمِن تَحۡتِ أَرۡجُلِهِمۚ مِّنۡهُمۡ أُمَّةٞ مُّقۡتَصِدَةٞۖ وَكَثِيرٞ مِّنۡهُمۡ سَآءَ مَا يَعۡمَلُونَ", "Walaw annahum aqamoo alttawrata waalinjeela wama onzila ilayhim min rabbihim laakaloo min fawqihim wamin tahti arjulihim minhum ommatun muqtasidatun wakatheerun minhum saa ma yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 67, "O Messenger, announce that which has been revealed to you from your Lord, and if you do not, then you have not conveyed His message. And Allah will protect you from the people. Indeed, Allah does not guide the disbelieving people", "۞يَـٰٓأَيُّهَا ٱلرَّسُولُ بَلِّغۡ مَآ أُنزِلَ إِلَيۡكَ مِن رَّبِّكَۖ وَإِن لَّمۡ تَفۡعَلۡ فَمَا بَلَّغۡتَ رِسَالَتَهُۥۚ وَٱللَّهُ يَعۡصِمُكَ مِنَ ٱلنَّاسِۗ إِنَّ ٱللَّهَ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلۡكَٰفِرِينَ", "Ya ayyuha alrrasoolu balligh ma onzila ilayka min rabbika wain lam tafAAal fama ballaghta risalatahu waAllahu yaAAsimuka mina alnnasi inna Allaha la yahdee alqawma alkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 68, "Say, ""O People of the Scripture, you are [standing] on nothing until you uphold [the law of] the Torah, the Gospel, and what has been revealed to you from your Lord."" And that which has been revealed to you from your Lord will surely increase many of them in transgression and disbelief. So do not grieve over the disbelieving people", "قُلۡ يَـٰٓأَهۡلَ ٱلۡكِتَٰبِ لَسۡتُمۡ عَلَىٰ شَيۡءٍ حَتَّىٰ تُقِيمُواْ ٱلتَّوۡرَىٰةَ وَٱلۡإِنجِيلَ وَمَآ أُنزِلَ إِلَيۡكُم مِّن رَّبِّكُمۡۗ وَلَيَزِيدَنَّ كَثِيرٗا مِّنۡهُم مَّآ أُنزِلَ إِلَيۡكَ مِن رَّبِّكَ طُغۡيَٰنٗا وَكُفۡرٗاۖ فَلَا تَأۡسَ عَلَى ٱلۡقَوۡمِ ٱلۡكَٰفِرِينَ", "Qul ya ahla alkitabi lastum AAala shayin hatta tuqeemoo alttawrata waalinjeela wama onzila ilaykum min rabbikum walayazeedanna katheeran minhum ma onzila ilayka min rabbika tughyanan wakufran fala tasa AAala alqawmi alkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 69, "Indeed, those who have believed [in Prophet Muhammad] and those [before Him] who were Jews or Sabeans or Christians - those [among them] who believed in Allah and the Last Day and did righteousness - no fear will there be concerning them, nor will they grieve", "إِنَّ ٱلَّذِينَ ءَامَنُواْ وَٱلَّذِينَ هَادُواْ وَٱلصَّـٰبِـُٔونَ وَٱلنَّصَٰرَىٰ مَنۡ ءَامَنَ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ وَعَمِلَ صَٰلِحٗا فَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ", "Inna allatheena amanoo waallatheena hadoo waalssabioona waalnnasara man amana biAllahi waalyawmi alakhiri waAAamila salihan fala khawfun AAalayhim wala hum yahzanoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 70, "We had already taken the covenant of the Children of Israel and had sent to them messengers. Whenever there came to them a messenger with what their souls did not desire, a party [of messengers] they denied, and another party they killed", "لَقَدۡ أَخَذۡنَا مِيثَٰقَ بَنِيٓ إِسۡرَـٰٓءِيلَ وَأَرۡسَلۡنَآ إِلَيۡهِمۡ رُسُلٗاۖ كُلَّمَا جَآءَهُمۡ رَسُولُۢ بِمَا لَا تَهۡوَىٰٓ أَنفُسُهُمۡ فَرِيقٗا كَذَّبُواْ وَفَرِيقٗا يَقۡتُلُونَ", "Laqad akhathna meethaqa banee israeela waarsalna ilayhim rusulan kullama jaahum rasoolun bima la tahwa anfusuhum fareeqan kaththaboo wafareeqan yaqtuloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 71, "And they thought there would be no [resulting] punishment, so they became blind and deaf. Then Allah turned to them in forgiveness; then [again] many of them became blind and deaf. And Allah is Seeing of what they do", "وَحَسِبُوٓاْ أَلَّا تَكُونَ فِتۡنَةٞ فَعَمُواْ وَصَمُّواْ ثُمَّ تَابَ ٱللَّهُ عَلَيۡهِمۡ ثُمَّ عَمُواْ وَصَمُّواْ كَثِيرٞ مِّنۡهُمۡۚ وَٱللَّهُ بَصِيرُۢ بِمَا يَعۡمَلُونَ", "Wahasiboo alla takoona fitnatun faAAamoo wasammoo thumma taba Allahu AAalayhim thumma AAamoo wasammoo katheerun minhum waAllahu baseerun bima yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 72, "They have certainly disbelieved who say, ""Allah is the Messiah, the son of Mary"" while the Messiah has said, ""O Children of Israel, worship Allah, my Lord and your Lord."" Indeed, he who associates others with Allah - Allah has forbidden him Paradise, and his refuge is the Fire. And there are not for the wrongdoers any helpers", "لَقَدۡ كَفَرَ ٱلَّذِينَ قَالُوٓاْ إِنَّ ٱللَّهَ هُوَ ٱلۡمَسِيحُ ٱبۡنُ مَرۡيَمَۖ وَقَالَ ٱلۡمَسِيحُ يَٰبَنِيٓ إِسۡرَـٰٓءِيلَ ٱعۡبُدُواْ ٱللَّهَ رَبِّي وَرَبَّكُمۡۖ إِنَّهُۥ مَن يُشۡرِكۡ بِٱللَّهِ فَقَدۡ حَرَّمَ ٱللَّهُ عَلَيۡهِ ٱلۡجَنَّةَ وَمَأۡوَىٰهُ ٱلنَّارُۖ وَمَا لِلظَّـٰلِمِينَ مِنۡ أَنصَارٖ", "Laqad kafara allatheena qaloo inna Allaha huwa almaseehu ibnu maryama waqala almaseehu ya banee israeela oAAbudoo Allaha rabbee warabbakum innahu man yushrik biAllahi faqad harrama Allahu AAalayhi aljannata wamawahu alnnaru wama lilththalimeena min ansarin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 73, "They have certainly disbelieved who say, ""Allah is the third of three."" And there is no god except one God. And if they do not desist from what they are saying, there will surely afflict the disbelievers among them a painful punishment", "لَّقَدۡ كَفَرَ ٱلَّذِينَ قَالُوٓاْ إِنَّ ٱللَّهَ ثَالِثُ ثَلَٰثَةٖۘ وَمَا مِنۡ إِلَٰهٍ إِلَّآ إِلَٰهٞ وَٰحِدٞۚ وَإِن لَّمۡ يَنتَهُواْ عَمَّا يَقُولُونَ لَيَمَسَّنَّ ٱلَّذِينَ كَفَرُواْ مِنۡهُمۡ عَذَابٌ أَلِيمٌ", "Laqad kafara allatheena qaloo inna Allaha thalithu thalathatin wama min ilahin illa ilahun wahidun wain lam yantahoo AAamma yaqooloona layamassanna allatheena kafaroo minhum AAathabun aleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 74, "So will they not repent to Allah and seek His forgiveness? And Allah is Forgiving and Merciful", "أَفَلَا يَتُوبُونَ إِلَى ٱللَّهِ وَيَسۡتَغۡفِرُونَهُۥۚ وَٱللَّهُ غَفُورٞ رَّحِيمٞ", "Afala yatooboona ila Allahi wayastaghfiroonahu waAllahu ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 75, "The Messiah, son of Mary, was not but a messenger; [other] messengers have passed on before him. And his mother was a supporter of truth. They both used to eat food. Look how We make clear to them the signs; then look how they are deluded", "مَّا ٱلۡمَسِيحُ ٱبۡنُ مَرۡيَمَ إِلَّا رَسُولٞ قَدۡ خَلَتۡ مِن قَبۡلِهِ ٱلرُّسُلُ وَأُمُّهُۥ صِدِّيقَةٞۖ كَانَا يَأۡكُلَانِ ٱلطَّعَامَۗ ٱنظُرۡ كَيۡفَ نُبَيِّنُ لَهُمُ ٱلۡأٓيَٰتِ ثُمَّ ٱنظُرۡ أَنَّىٰ يُؤۡفَكُونَ", "Ma almaseehu ibnu maryama illa rasoolun qad khalat min qablihi alrrusulu waommuhu siddeeqatun kana yakulani alttaAAama onthur kayfa nubayyinu lahumu alayati thumma onthur anna yufakoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 76, "Say, ""Do you worship besides Allah that which holds for you no [power of] harm or benefit while it is Allah who is the Hearing, the Knowing", "قُلۡ أَتَعۡبُدُونَ مِن دُونِ ٱللَّهِ مَا لَا يَمۡلِكُ لَكُمۡ ضَرّٗا وَلَا نَفۡعٗاۚ وَٱللَّهُ هُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ", "Qul ataAAbudoona min dooni Allahi ma la yamliku lakum darran wala nafAAan waAllahu huwa alssameeAAu alAAaleemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 77, "Say, ""O People of the Scripture, do not exceed limits in your religion beyond the truth and do not follow the inclinations of a people who had gone astray before and misled many and have strayed from the soundness of the way", "قُلۡ يَـٰٓأَهۡلَ ٱلۡكِتَٰبِ لَا تَغۡلُواْ فِي دِينِكُمۡ غَيۡرَ ٱلۡحَقِّ وَلَا تَتَّبِعُوٓاْ أَهۡوَآءَ قَوۡمٖ قَدۡ ضَلُّواْ مِن قَبۡلُ وَأَضَلُّواْ كَثِيرٗا وَضَلُّواْ عَن سَوَآءِ ٱلسَّبِيلِ", "Qul ya ahla alkitabi la taghloo fee deenikum ghayra alhaqqi wala tattabiAAoo ahwaa qawmin qad dalloo min qablu waadalloo katheeran wadalloo AAan sawai alssabeeli");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 78, "Cursed were those who disbelieved among the Children of Israel by the tongue of David and of Jesus, the son of Mary. That was because they disobeyed and [habitually] transgressed", "لُعِنَ ٱلَّذِينَ كَفَرُواْ مِنۢ بَنِيٓ إِسۡرَـٰٓءِيلَ عَلَىٰ لِسَانِ دَاوُۥدَ وَعِيسَى ٱبۡنِ مَرۡيَمَۚ ذَٰلِكَ بِمَا عَصَواْ وَّكَانُواْ يَعۡتَدُونَ", "LuAAina allatheena kafaroo min banee israeela AAala lisani dawooda waAAeesa ibni maryama thalika bima AAasaw wakanoo yaAAtadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 79, "They used not to prevent one another from wrongdoing that they did. How wretched was that which they were doing", "كَانُواْ لَا يَتَنَاهَوۡنَ عَن مُّنكَرٖ فَعَلُوهُۚ لَبِئۡسَ مَا كَانُواْ يَفۡعَلُونَ", "Kanoo la yatanahawna AAan munkarin faAAaloohu labisa ma kanoo yafAAaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 80, "You see many of them becoming allies of those who disbelieved. How wretched is that which they have put forth for themselves in that Allah has become angry with them, and in the punishment they will abide eternally", "تَرَىٰ كَثِيرٗا مِّنۡهُمۡ يَتَوَلَّوۡنَ ٱلَّذِينَ كَفَرُواْۚ لَبِئۡسَ مَا قَدَّمَتۡ لَهُمۡ أَنفُسُهُمۡ أَن سَخِطَ ٱللَّهُ عَلَيۡهِمۡ وَفِي ٱلۡعَذَابِ هُمۡ خَٰلِدُونَ", "Tara katheeran minhum yatawallawna allatheena kafaroo labisa ma qaddamat lahum anfusuhum an sakhita Allahu AAalayhim wafee alAAathabi hum khalidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 81, "And if they had believed in Allah and the Prophet and in what was revealed to him, they would not have taken them as allies; but many of them are defiantly disobedient", "وَلَوۡ كَانُواْ يُؤۡمِنُونَ بِٱللَّهِ وَٱلنَّبِيِّ وَمَآ أُنزِلَ إِلَيۡهِ مَا ٱتَّخَذُوهُمۡ أَوۡلِيَآءَ وَلَٰكِنَّ كَثِيرٗا مِّنۡهُمۡ فَٰسِقُونَ", "Walaw kanoo yuminoona biAllahi waalnnabiyyi wama onzila ilayhi ma ittakhathoohum awliyaa walakinna katheeran minhum fasiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 82, "You will surely find the most intense of the people in animosity toward the believers [to be] the Jews and those who associate others with Allah; and you will find the nearest of them in affection to the believers those who say, ""We are Christians."" That is because among them are priests and monks and because they are not arrogant", "۞لَتَجِدَنَّ أَشَدَّ ٱلنَّاسِ عَدَٰوَةٗ لِّلَّذِينَ ءَامَنُواْ ٱلۡيَهُودَ وَٱلَّذِينَ أَشۡرَكُواْۖ وَلَتَجِدَنَّ أَقۡرَبَهُم مَّوَدَّةٗ لِّلَّذِينَ ءَامَنُواْ ٱلَّذِينَ قَالُوٓاْ إِنَّا نَصَٰرَىٰۚ ذَٰلِكَ بِأَنَّ مِنۡهُمۡ قِسِّيسِينَ وَرُهۡبَانٗا وَأَنَّهُمۡ لَا يَسۡتَكۡبِرُونَ", "Latajidanna ashadda alnnasi AAadawatan lillatheena amanoo alyahooda waallatheena ashrakoo walatajidanna aqrabahum mawaddatan lillatheena amanoo allatheena qaloo inna nasara thalika bianna minhum qisseeseena waruhbanan waannahum la yastakbiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 83, "And when they hear what has been revealed to the Messenger, you see their eyes overflowing with tears because of what they have recognized of the truth. They say, ""Our Lord, we have believed, so register us among the witnesses", "وَإِذَا سَمِعُواْ مَآ أُنزِلَ إِلَى ٱلرَّسُولِ تَرَىٰٓ أَعۡيُنَهُمۡ تَفِيضُ مِنَ ٱلدَّمۡعِ مِمَّا عَرَفُواْ مِنَ ٱلۡحَقِّۖ يَقُولُونَ رَبَّنَآ ءَامَنَّا فَٱكۡتُبۡنَا مَعَ ٱلشَّـٰهِدِينَ", "Waitha samiAAoo ma onzila ila alrrasooli tara aAAyunahum tafeedu mina alddamAAi mimma AAarafoo mina alhaqqi yaqooloona rabbana amanna faoktubna maAAa alshshahideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 84, "And why should we not believe in Allah and what has come to us of the truth? And we aspire that our Lord will admit us [to Paradise] with the righteous people", "وَمَا لَنَا لَا نُؤۡمِنُ بِٱللَّهِ وَمَا جَآءَنَا مِنَ ٱلۡحَقِّ وَنَطۡمَعُ أَن يُدۡخِلَنَا رَبُّنَا مَعَ ٱلۡقَوۡمِ ٱلصَّـٰلِحِينَ", "Wama lana la numinu biAllahi wama jaana mina alhaqqi wanatmaAAu an yudkhilana rabbuna maAAa alqawmi alssaliheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 85, "So Allah rewarded them for what they said with gardens [in Paradise] beneath which rivers flow, wherein they abide eternally. And that is the reward of doers of good", "فَأَثَٰبَهُمُ ٱللَّهُ بِمَا قَالُواْ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَاۚ وَذَٰلِكَ جَزَآءُ ٱلۡمُحۡسِنِينَ", "Faathabahumu Allahu bima qaloo jannatin tajree min tahtiha alanharu khalideena feeha wathalika jazao almuhsineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 86, "But those who disbelieved and denied Our signs - they are the companions of Hellfire", "وَٱلَّذِينَ كَفَرُواْ وَكَذَّبُواْ بِـَٔايَٰتِنَآ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلۡجَحِيمِ", "Waallatheena kafaroo wakaththaboo biayatina olaika ashabu aljaheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 87, "O you who have believed, do not prohibit the good things which Allah has made lawful to you and do not transgress. Indeed, Allah does not like transgressors", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تُحَرِّمُواْ طَيِّبَٰتِ مَآ أَحَلَّ ٱللَّهُ لَكُمۡ وَلَا تَعۡتَدُوٓاْۚ إِنَّ ٱللَّهَ لَا يُحِبُّ ٱلۡمُعۡتَدِينَ", "Ya ayyuha allatheena amanoo la tuharrimoo tayyibati ma ahalla Allahu lakum wala taAAtadoo inna Allaha la yuhibbu almuAAtadeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 88, "And eat of what Allah has provided for you [which is] lawful and good. And fear Allah, in whom you are believers", "وَكُلُواْ مِمَّا رَزَقَكُمُ ٱللَّهُ حَلَٰلٗا طَيِّبٗاۚ وَٱتَّقُواْ ٱللَّهَ ٱلَّذِيٓ أَنتُم بِهِۦ مُؤۡمِنُونَ", "Wakuloo mimma razaqakumu Allahu halalan tayyiban waittaqoo Allaha allathee antum bihi muminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 89, "Allah will not impose blame upon you for what is meaningless in your oaths, but He will impose blame upon you for [breaking] what you intended of oaths. So its expiation is the feeding of ten needy people from the average of that which you feed your [own] families or clothing them or the freeing of a slave. But whoever cannot find [or afford it] - then a fast of three days [is required]. That is the expiation for oaths when you have sworn. But guard your oaths. Thus does Allah make clear to you His verses that you may be grateful", "لَا يُؤَاخِذُكُمُ ٱللَّهُ بِٱللَّغۡوِ فِيٓ أَيۡمَٰنِكُمۡ وَلَٰكِن يُؤَاخِذُكُم بِمَا عَقَّدتُّمُ ٱلۡأَيۡمَٰنَۖ فَكَفَّـٰرَتُهُۥٓ إِطۡعَامُ عَشَرَةِ مَسَٰكِينَ مِنۡ أَوۡسَطِ مَا تُطۡعِمُونَ أَهۡلِيكُمۡ أَوۡ كِسۡوَتُهُمۡ أَوۡ تَحۡرِيرُ رَقَبَةٖۖ فَمَن لَّمۡ يَجِدۡ فَصِيَامُ ثَلَٰثَةِ أَيَّامٖۚ ذَٰلِكَ كَفَّـٰرَةُ أَيۡمَٰنِكُمۡ إِذَا حَلَفۡتُمۡۚ وَٱحۡفَظُوٓاْ أَيۡمَٰنَكُمۡۚ كَذَٰلِكَ يُبَيِّنُ ٱللَّهُ لَكُمۡ ءَايَٰتِهِۦ لَعَلَّكُمۡ تَشۡكُرُونَ", "La yuakhithukumu Allahu biallaghwi fee aymanikum walakin yuakhithukum bima AAaqqadtumu alaymana fakaffaratuhu itAAamu AAasharati masakeena min awsati ma tutAAimoona ahleekum aw kiswatuhum aw tahreeru raqabatin faman lam yajid fasiyamu thalathati ayyamin thalika kaffaratu aymanikum itha halaftum waihfathoo aymanakum kathalika yubayyinu Allahu lakum ayatihi laAAallakum tashkuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 90, "O you who have believed, indeed, intoxicants, gambling, [sacrificing on] stone alters [to other than Allah], and divining arrows are but defilement from the work of Satan, so avoid it that you may be successful", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِنَّمَا ٱلۡخَمۡرُ وَٱلۡمَيۡسِرُ وَٱلۡأَنصَابُ وَٱلۡأَزۡلَٰمُ رِجۡسٞ مِّنۡ عَمَلِ ٱلشَّيۡطَٰنِ فَٱجۡتَنِبُوهُ لَعَلَّكُمۡ تُفۡلِحُونَ", "Ya ayyuha allatheena amanoo innama alkhamru waalmaysiru waalansabu waalazlamu rijsun min AAamali alshshaytani faijtaniboohu laAAallakum tuflihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 91, "Satan only wants to cause between you animosity and hatred through intoxicants and gambling and to avert you from the remembrance of Allah and from prayer. So will you not desist", "إِنَّمَا يُرِيدُ ٱلشَّيۡطَٰنُ أَن يُوقِعَ بَيۡنَكُمُ ٱلۡعَدَٰوَةَ وَٱلۡبَغۡضَآءَ فِي ٱلۡخَمۡرِ وَٱلۡمَيۡسِرِ وَيَصُدَّكُمۡ عَن ذِكۡرِ ٱللَّهِ وَعَنِ ٱلصَّلَوٰةِۖ فَهَلۡ أَنتُم مُّنتَهُونَ", "Innama yureedu alshshaytanu an yooqiAAa baynakumu alAAadawata waalbaghdaa fee alkhamri waalmaysiri wayasuddakum AAan thikri Allahi waAAani alssalati fahal antum muntahoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 92, "And obey Allah and obey the Messenger and beware. And if you turn away - then know that upon Our Messenger is only [the responsibility for] clear notification", "وَأَطِيعُواْ ٱللَّهَ وَأَطِيعُواْ ٱلرَّسُولَ وَٱحۡذَرُواْۚ فَإِن تَوَلَّيۡتُمۡ فَٱعۡلَمُوٓاْ أَنَّمَا عَلَىٰ رَسُولِنَا ٱلۡبَلَٰغُ ٱلۡمُبِينُ", "WaateeAAoo Allaha waateeAAoo alrrasoola waihtharoo fain tawallaytum faiAAlamoo annama AAala rasoolina albalaghu almubeenu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 93, "There is not upon those who believe and do righteousness [any] blame concerning what they have eaten [in the past] if they [now] fear Allah and believe and do righteous deeds, and then fear Allah and believe, and then fear Allah and do good; and Allah loves the doers of good", "لَيۡسَ عَلَى ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ جُنَاحٞ فِيمَا طَعِمُوٓاْ إِذَا مَا ٱتَّقَواْ وَّءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ ثُمَّ ٱتَّقَواْ وَّءَامَنُواْ ثُمَّ ٱتَّقَواْ وَّأَحۡسَنُواْۚ وَٱللَّهُ يُحِبُّ ٱلۡمُحۡسِنِينَ", "Laysa AAala allatheena amanoo waAAamiloo alssalihati junahun feema taAAimoo itha ma ittaqaw waamanoo waAAamiloo alssalihati thumma ittaqaw waamanoo thumma ittaqaw waahsanoo waAllahu yuhibbu almuhsineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 94, "O you who have believed, Allah will surely test you through something of the game that your hands and spears [can] reach, that Allah may make evident those who fear Him unseen. And whoever transgresses after that - for him is a painful punishment", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَيَبۡلُوَنَّكُمُ ٱللَّهُ بِشَيۡءٖ مِّنَ ٱلصَّيۡدِ تَنَالُهُۥٓ أَيۡدِيكُمۡ وَرِمَاحُكُمۡ لِيَعۡلَمَ ٱللَّهُ مَن يَخَافُهُۥ بِٱلۡغَيۡبِۚ فَمَنِ ٱعۡتَدَىٰ بَعۡدَ ذَٰلِكَ فَلَهُۥ عَذَابٌ أَلِيمٞ", "Ya ayyuha allatheena amanoo layabluwannakumu Allahu bishayin mina alssaydi tanaluhu aydeekum warimahukum liyaAAlama Allahu man yakhafuhu bialghaybi famani iAAtada baAAda thalika falahu AAathabun aleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 95, "O you who have believed, do not kill game while you are in the state of ihram. And whoever of you kills it intentionally - the penalty is an equivalent from sacrificial animals to what he killed, as judged by two just men among you as an offering [to Allah] delivered to the Ka'bah, or an expiation: the feeding of needy people or the equivalent of that in fasting, that he may taste the consequence of his deed. Allah has pardoned what is past; but whoever returns [to violation], then Allah will take retribution from him. And Allah is Exalted in Might and Owner of Retribution", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَقۡتُلُواْ ٱلصَّيۡدَ وَأَنتُمۡ حُرُمٞۚ وَمَن قَتَلَهُۥ مِنكُم مُّتَعَمِّدٗا فَجَزَآءٞ مِّثۡلُ مَا قَتَلَ مِنَ ٱلنَّعَمِ يَحۡكُمُ بِهِۦ ذَوَا عَدۡلٖ مِّنكُمۡ هَدۡيَۢا بَٰلِغَ ٱلۡكَعۡبَةِ أَوۡ كَفَّـٰرَةٞ طَعَامُ مَسَٰكِينَ أَوۡ عَدۡلُ ذَٰلِكَ صِيَامٗا لِّيَذُوقَ وَبَالَ أَمۡرِهِۦۗ عَفَا ٱللَّهُ عَمَّا سَلَفَۚ وَمَنۡ عَادَ فَيَنتَقِمُ ٱللَّهُ مِنۡهُۚ وَٱللَّهُ عَزِيزٞ ذُو ٱنتِقَامٍ", "Ya ayyuha allatheena amanoo la taqtuloo alssayda waantum hurumun waman qatalahu minkum mutaAAammidan fajazaon mithlu ma qatala mina alnnaAAami yahkumu bihi thawa AAadlin minkum hadyan baligha alkaAAbati aw kaffaratun taAAamu masakeena aw AAadlu thalika siyaman liyathooqa wabala amrihi AAafa Allahu AAamma salafa waman AAada fayantaqimu Allahu minhu waAllahu AAazeezun thoo intiqamin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 96, "Lawful to you is game from the sea and its food as provision for you and the travelers, but forbidden to you is game from the land as long as you are in the state of ihram. And fear Allah to whom you will be gathered", "أُحِلَّ لَكُمۡ صَيۡدُ ٱلۡبَحۡرِ وَطَعَامُهُۥ مَتَٰعٗا لَّكُمۡ وَلِلسَّيَّارَةِۖ وَحُرِّمَ عَلَيۡكُمۡ صَيۡدُ ٱلۡبَرِّ مَا دُمۡتُمۡ حُرُمٗاۗ وَٱتَّقُواْ ٱللَّهَ ٱلَّذِيٓ إِلَيۡهِ تُحۡشَرُونَ", "Ohilla lakum saydu albahri wataAAamuhu mataAAan lakum walilssayyarati wahurrima AAalaykum saydu albarri ma dumtum huruman waittaqoo Allaha allathee ilayhi tuhsharoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 97, "Allah has made the Ka'bah, the Sacred House, standing for the people and [has sanctified] the sacred months and the sacrificial animals and the garlands [by which they are identified]. That is so you may know that Allah knows what is in the heavens and what is in the earth and that Allah is Knowing of all things", "۞جَعَلَ ٱللَّهُ ٱلۡكَعۡبَةَ ٱلۡبَيۡتَ ٱلۡحَرَامَ قِيَٰمٗا لِّلنَّاسِ وَٱلشَّهۡرَ ٱلۡحَرَامَ وَٱلۡهَدۡيَ وَٱلۡقَلَـٰٓئِدَۚ ذَٰلِكَ لِتَعۡلَمُوٓاْ أَنَّ ٱللَّهَ يَعۡلَمُ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِ وَأَنَّ ٱللَّهَ بِكُلِّ شَيۡءٍ عَلِيمٌ", "JaAAala Allahu alkaAAbata albayta alharama qiyaman lilnnasi waalshshahra alharama waalhadya waalqalaida thalika litaAAlamoo anna Allaha yaAAlamu ma fee alssamawati wama fee alardi waanna Allaha bikulli shayin AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 98, "Know that Allah is severe in penalty and that Allah is Forgiving and Merciful", "ٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ شَدِيدُ ٱلۡعِقَابِ وَأَنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ", "IAAlamoo anna Allaha shadeedu alAAiqabi waanna Allaha ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 99, "Not upon the Messenger is [responsibility] except [for] notification. And Allah knows whatever you reveal and whatever you conceal", "مَّا عَلَى ٱلرَّسُولِ إِلَّا ٱلۡبَلَٰغُۗ وَٱللَّهُ يَعۡلَمُ مَا تُبۡدُونَ وَمَا تَكۡتُمُونَ", "Ma AAala alrrasooli illa albalaghu waAllahu yaAAlamu ma tubdoona wama taktumoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 100, "Say, ""Not equal are the evil and the good, although the abundance of evil might impress you."" So fear Allah, O you of understanding, that you may be successful", "قُل لَّا يَسۡتَوِي ٱلۡخَبِيثُ وَٱلطَّيِّبُ وَلَوۡ أَعۡجَبَكَ كَثۡرَةُ ٱلۡخَبِيثِۚ فَٱتَّقُواْ ٱللَّهَ يَـٰٓأُوْلِي ٱلۡأَلۡبَٰبِ لَعَلَّكُمۡ تُفۡلِحُونَ", "Qul la yastawee alkhabeethu waalttayyibu walaw aAAjabaka kathratu alkhabeethi faittaqoo Allaha ya olee alalbabi laAAallakum tuflihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 101, "O you who have believed, do not ask about things which, if they are shown to you, will distress you. But if you ask about them while the Qur'an is being revealed, they will be shown to you. Allah has pardoned that which is past; and Allah is Forgiving and Forbearing", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَسۡـَٔلُواْ عَنۡ أَشۡيَآءَ إِن تُبۡدَ لَكُمۡ تَسُؤۡكُمۡ وَإِن تَسۡـَٔلُواْ عَنۡهَا حِينَ يُنَزَّلُ ٱلۡقُرۡءَانُ تُبۡدَ لَكُمۡ عَفَا ٱللَّهُ عَنۡهَاۗ وَٱللَّهُ غَفُورٌ حَلِيمٞ", "Ya ayyuha allatheena amanoo la tasaloo AAan ashyaa in tubda lakum tasukum wain tasaloo AAanha heena yunazzalu alquranu tubda lakum AAafa Allahu AAanha waAllahu ghafoorun haleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 102, "A people asked such [questions] before you; then they became thereby disbelievers", "قَدۡ سَأَلَهَا قَوۡمٞ مِّن قَبۡلِكُمۡ ثُمَّ أَصۡبَحُواْ بِهَا كَٰفِرِينَ", "Qad saalaha qawmun min qablikum thumma asbahoo biha kafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 103, "Allah has not appointed [such innovations as] bahirah or sa'ibah or wasilah or ham. But those who disbelieve invent falsehood about Allah, and most of them do not reason", "مَا جَعَلَ ٱللَّهُ مِنۢ بَحِيرَةٖ وَلَا سَآئِبَةٖ وَلَا وَصِيلَةٖ وَلَا حَامٖ وَلَٰكِنَّ ٱلَّذِينَ كَفَرُواْ يَفۡتَرُونَ عَلَى ٱللَّهِ ٱلۡكَذِبَۖ وَأَكۡثَرُهُمۡ لَا يَعۡقِلُونَ", "Ma jaAAala Allahu min baheeratin wala saibatin wala waseelatin wala hamin walakinna allatheena kafaroo yaftaroona AAala Allahi alkathiba waaktharuhum la yaAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 104, "And when it is said to them, ""Come to what Allah has revealed and to the Messenger,"" they say, ""Sufficient for us is that upon which we found our fathers."" Even though their fathers knew nothing, nor were they guided", "وَإِذَا قِيلَ لَهُمۡ تَعَالَوۡاْ إِلَىٰ مَآ أَنزَلَ ٱللَّهُ وَإِلَى ٱلرَّسُولِ قَالُواْ حَسۡبُنَا مَا وَجَدۡنَا عَلَيۡهِ ءَابَآءَنَآۚ أَوَلَوۡ كَانَ ءَابَآؤُهُمۡ لَا يَعۡلَمُونَ شَيۡـٔٗا وَلَا يَهۡتَدُونَ", "Waitha qeela lahum taAAalaw ila ma anzala Allahu waila alrrasooli qaloo hasbuna ma wajadna AAalayhi abaana awalaw kana abaohum la yaAAlamoona shayan wala yahtadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 105, "O you who have believed, upon you is [responsibility for] yourselves. Those who have gone astray will not harm you when you have been guided. To Allah is you return all together; then He will inform you of what you used to do", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ عَلَيۡكُمۡ أَنفُسَكُمۡۖ لَا يَضُرُّكُم مَّن ضَلَّ إِذَا ٱهۡتَدَيۡتُمۡۚ إِلَى ٱللَّهِ مَرۡجِعُكُمۡ جَمِيعٗا فَيُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ", "Ya ayyuha allatheena amanoo AAalaykum anfusakum la yadurrukum man dalla itha ihtadaytum ila Allahi marjiAAukum jameeAAan fayunabbiokum bima kuntum taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 106, "O you who have believed, testimony [should be taken] among you when death approaches one of you at the time of bequest - [that of] two just men from among you or two others from outside if you are traveling through the land and the disaster of death should strike you. Detain them after the prayer and let them both swear by Allah if you doubt [their testimony, saying], ""We will not exchange our oath for a price, even if he should be a near relative, and we will not withhold the testimony of Allah. Indeed, we would then be of the sinful", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ شَهَٰدَةُ بَيۡنِكُمۡ إِذَا حَضَرَ أَحَدَكُمُ ٱلۡمَوۡتُ حِينَ ٱلۡوَصِيَّةِ ٱثۡنَانِ ذَوَا عَدۡلٖ مِّنكُمۡ أَوۡ ءَاخَرَانِ مِنۡ غَيۡرِكُمۡ إِنۡ أَنتُمۡ ضَرَبۡتُمۡ فِي ٱلۡأَرۡضِ فَأَصَٰبَتۡكُم مُّصِيبَةُ ٱلۡمَوۡتِۚ تَحۡبِسُونَهُمَا مِنۢ بَعۡدِ ٱلصَّلَوٰةِ فَيُقۡسِمَانِ بِٱللَّهِ إِنِ ٱرۡتَبۡتُمۡ لَا نَشۡتَرِي بِهِۦ ثَمَنٗا وَلَوۡ كَانَ ذَا قُرۡبَىٰ وَلَا نَكۡتُمُ شَهَٰدَةَ ٱللَّهِ إِنَّآ إِذٗا لَّمِنَ ٱلۡأٓثِمِينَ", "Ya ayyuha allatheena amanoo shahadatu baynikum itha hadara ahadakumu almawtu heena alwasiyyati ithnani thawa AAadlin minkum aw akharani min ghayrikum in antum darabtum fee alardi faasabatkum museebatu almawti tahbisoonahuma min baAAdi alssalati fayuqsimani biAllahi ini irtabtum la nashtaree bihi thamanan walaw kana tha qurba wala naktumu shahadata Allahi inna ithan lamina alathimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 107, "But if it is found that those two were guilty of perjury, let two others stand in their place [who are] foremost [in claim] from those who have a lawful right. And let them swear by Allah, ""Our testimony is truer than their testimony, and we have not transgressed. Indeed, we would then be of the wrongdoers", "فَإِنۡ عُثِرَ عَلَىٰٓ أَنَّهُمَا ٱسۡتَحَقَّآ إِثۡمٗا فَـَٔاخَرَانِ يَقُومَانِ مَقَامَهُمَا مِنَ ٱلَّذِينَ ٱسۡتَحَقَّ عَلَيۡهِمُ ٱلۡأَوۡلَيَٰنِ فَيُقۡسِمَانِ بِٱللَّهِ لَشَهَٰدَتُنَآ أَحَقُّ مِن شَهَٰدَتِهِمَا وَمَا ٱعۡتَدَيۡنَآ إِنَّآ إِذٗا لَّمِنَ ٱلظَّـٰلِمِينَ", "Fain AAuthira AAala annahuma istahaqqa ithman faakharani yaqoomani maqamahuma mina allatheena istahaqqa AAalayhimu alawlayani fayuqsimani biAllahi lashahadatuna ahaqqu min shahadatihima wama iAAtadayna inna ithan lamina alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 108, "That is more likely that they will give testimony according to its [true] objective, or [at least] they would fear that [other] oaths might be taken after their oaths. And fear Allah and listen; and Allah does not guide the defiantly disobedient people", "ذَٰلِكَ أَدۡنَىٰٓ أَن يَأۡتُواْ بِٱلشَّهَٰدَةِ عَلَىٰ وَجۡهِهَآ أَوۡ يَخَافُوٓاْ أَن تُرَدَّ أَيۡمَٰنُۢ بَعۡدَ أَيۡمَٰنِهِمۡۗ وَٱتَّقُواْ ٱللَّهَ وَٱسۡمَعُواْۗ وَٱللَّهُ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلۡفَٰسِقِينَ", "Thalika adna an yatoo bialshshahadati AAala wajhiha aw yakhafoo an turadda aymanun baAAda aymanihim waittaqoo Allaha waismaAAoo waAllahu la yahdee alqawma alfasiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 109, "[Be warned of] the Day when Allah will assemble the messengers and say, ""What was the response you received?"" They will say, ""We have no knowledge. Indeed, it is You who is Knower of the unseen", "۞يَوۡمَ يَجۡمَعُ ٱللَّهُ ٱلرُّسُلَ فَيَقُولُ مَاذَآ أُجِبۡتُمۡۖ قَالُواْ لَا عِلۡمَ لَنَآۖ إِنَّكَ أَنتَ عَلَّـٰمُ ٱلۡغُيُوبِ", "Yawma yajmaAAu Allahu alrrusula fayaqoolu matha ojibtum qaloo la AAilma lana innaka anta AAallamu alghuyoobi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 110, "[The Day] when Allah will say, ""O Jesus, Son of Mary, remember My favor upon you and upon your mother when I supported you with the Pure Spirit and you spoke to the people in the cradle and in maturity; and [remember] when I taught you writing and wisdom and the Torah and the Gospel; and when you designed from clay [what was] like the form of a bird with My permission, then you breathed into it, and it became a bird with My permission; and you healed the blind and the leper with My permission; and when you brought forth the dead with My permission; and when I restrained the Children of Israel from [killing] you when you came to them with clear proofs and those who disbelieved among them said, ""This is not but obvious magic", "إِذۡ قَالَ ٱللَّهُ يَٰعِيسَى ٱبۡنَ مَرۡيَمَ ٱذۡكُرۡ نِعۡمَتِي عَلَيۡكَ وَعَلَىٰ وَٰلِدَتِكَ إِذۡ أَيَّدتُّكَ بِرُوحِ ٱلۡقُدُسِ تُكَلِّمُ ٱلنَّاسَ فِي ٱلۡمَهۡدِ وَكَهۡلٗاۖ وَإِذۡ عَلَّمۡتُكَ ٱلۡكِتَٰبَ وَٱلۡحِكۡمَةَ وَٱلتَّوۡرَىٰةَ وَٱلۡإِنجِيلَۖ وَإِذۡ تَخۡلُقُ مِنَ ٱلطِّينِ كَهَيۡـَٔةِ ٱلطَّيۡرِ بِإِذۡنِي فَتَنفُخُ فِيهَا فَتَكُونُ طَيۡرَۢا بِإِذۡنِيۖ وَتُبۡرِئُ ٱلۡأَكۡمَهَ وَٱلۡأَبۡرَصَ بِإِذۡنِيۖ وَإِذۡ تُخۡرِجُ ٱلۡمَوۡتَىٰ بِإِذۡنِيۖ وَإِذۡ كَفَفۡتُ بَنِيٓ إِسۡرَـٰٓءِيلَ عَنكَ إِذۡ جِئۡتَهُم بِٱلۡبَيِّنَٰتِ فَقَالَ ٱلَّذِينَ كَفَرُواْ مِنۡهُمۡ إِنۡ هَٰذَآ إِلَّا سِحۡرٞ مُّبِينٞ", "Ith qala Allahu ya AAeesa ibna maryama othkur niAAmatee AAalayka waAAala walidatika ith ayyadtuka biroohi alqudusi tukallimu alnnasa fee almahdi wakahlan waith AAallamtuka alkitaba waalhikmata waalttawrata waalinjeela waith takhluqu mina altteeni kahayati alttayri biithnee fatanfukhu feeha fatakoonu tayran biithnee watubrio alakmaha waalabrasa biithnee waith tukhriju almawta biithnee waith kafaftu banee israeela AAanka ith jitahum bialbayyinati faqala allatheena kafaroo minhum in hatha illa sihrun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 111, "And [remember] when I inspired to the disciples, ""Believe in Me and in My messenger Jesus."" They said, ""We have believed, so bear witness that indeed we are Muslims [in submission to Allah]", "وَإِذۡ أَوۡحَيۡتُ إِلَى ٱلۡحَوَارِيِّـۧنَ أَنۡ ءَامِنُواْ بِي وَبِرَسُولِي قَالُوٓاْ ءَامَنَّا وَٱشۡهَدۡ بِأَنَّنَا مُسۡلِمُونَ", "Waith awhaytu ila alhawariyyeena an aminoo bee wabirasoolee qaloo amanna waishhad biannana muslimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 112, "[And remember] when the disciples said, ""O Jesus, Son of Mary, can your Lord send down to us a table [spread with food] from the heaven? [Jesus] said,"" Fear Allah, if you should be believers", "إِذۡ قَالَ ٱلۡحَوَارِيُّونَ يَٰعِيسَى ٱبۡنَ مَرۡيَمَ هَلۡ يَسۡتَطِيعُ رَبُّكَ أَن يُنَزِّلَ عَلَيۡنَا مَآئِدَةٗ مِّنَ ٱلسَّمَآءِۖ قَالَ ٱتَّقُواْ ٱللَّهَ إِن كُنتُم مُّؤۡمِنِينَ", "Ith qala alhawariyyoona ya AAeesa ibna maryama hal yastateeAAu rabbuka an yunazzila AAalayna maidatan mina alssamai qala ittaqoo Allaha in kuntum mumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 113, "They said, ""We wish to eat from it and let our hearts be reassured and know that you have been truthful to us and be among its witnesses", "قَالُواْ نُرِيدُ أَن نَّأۡكُلَ مِنۡهَا وَتَطۡمَئِنَّ قُلُوبُنَا وَنَعۡلَمَ أَن قَدۡ صَدَقۡتَنَا وَنَكُونَ عَلَيۡهَا مِنَ ٱلشَّـٰهِدِينَ", "Qaloo nureedu an nakula minha watatmainna quloobuna wanaAAlama an qad sadaqtana wanakoona AAalayha mina alshshahideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 114, "Said Jesus, the son of Mary, ""O Allah, our Lord, send down to us a table [spread with food] from the heaven to be for us a festival for the first of us and the last of us and a sign from You. And provide for us, and You are the best of providers", "قَالَ عِيسَى ٱبۡنُ مَرۡيَمَ ٱللَّهُمَّ رَبَّنَآ أَنزِلۡ عَلَيۡنَا مَآئِدَةٗ مِّنَ ٱلسَّمَآءِ تَكُونُ لَنَا عِيدٗا لِّأَوَّلِنَا وَءَاخِرِنَا وَءَايَةٗ مِّنكَۖ وَٱرۡزُقۡنَا وَأَنتَ خَيۡرُ ٱلرَّـٰزِقِينَ", "Qala AAeesa ibnu maryama allahumma rabbana anzil AAalayna maidatan mina alssamai takoonu lana AAeedan liawwalina waakhirina waayatan minka waorzuqna waanta khayru alrraziqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 115, "Allah said, ""Indeed, I will sent it down to you, but whoever disbelieves afterwards from among you - then indeed will I punish him with a punishment by which I have not punished anyone among the worlds", "قَالَ ٱللَّهُ إِنِّي مُنَزِّلُهَا عَلَيۡكُمۡۖ فَمَن يَكۡفُرۡ بَعۡدُ مِنكُمۡ فَإِنِّيٓ أُعَذِّبُهُۥ عَذَابٗا لَّآ أُعَذِّبُهُۥٓ أَحَدٗا مِّنَ ٱلۡعَٰلَمِينَ", "Qala Allahu innee munazziluha AAalaykum faman yakfur baAAdu minkum fainnee oAAaththibuhu AAathaban la oAAaththibuhu ahadan mina alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 116, "And [beware the Day] when Allah will say, ""O Jesus, Son of Mary, did you say to the people, 'Take me and my mother as deities besides Allah?'"" He will say, ""Exalted are You! It was not for me to say that to which I have no right. If I had said it, You would have known it. You know what is within myself, and I do not know what is within Yourself. Indeed, it is You who is Knower of the unseen", "وَإِذۡ قَالَ ٱللَّهُ يَٰعِيسَى ٱبۡنَ مَرۡيَمَ ءَأَنتَ قُلۡتَ لِلنَّاسِ ٱتَّخِذُونِي وَأُمِّيَ إِلَٰهَيۡنِ مِن دُونِ ٱللَّهِۖ قَالَ سُبۡحَٰنَكَ مَا يَكُونُ لِيٓ أَنۡ أَقُولَ مَا لَيۡسَ لِي بِحَقٍّۚ إِن كُنتُ قُلۡتُهُۥ فَقَدۡ عَلِمۡتَهُۥۚ تَعۡلَمُ مَا فِي نَفۡسِي وَلَآ أَعۡلَمُ مَا فِي نَفۡسِكَۚ إِنَّكَ أَنتَ عَلَّـٰمُ ٱلۡغُيُوبِ", "Waith qala Allahu ya AAeesa ibna maryama aanta qulta lilnnasi ittakhithoonee waommiya ilahayni min dooni Allahi qala subhanaka ma yakoonu lee an aqoola ma laysa lee bihaqqin in kuntu qultuhu faqad AAalimtahu taAAlamu ma fee nafsee wala aAAlamu ma fee nafsika innaka anta AAallamu alghuyoobi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 117, "I said not to them except what You commanded me - to worship Allah, my Lord and your Lord. And I was a witness over them as long as I was among them; but when You took me up, You were the Observer over them, and You are, over all things, Witness", "مَا قُلۡتُ لَهُمۡ إِلَّا مَآ أَمَرۡتَنِي بِهِۦٓ أَنِ ٱعۡبُدُواْ ٱللَّهَ رَبِّي وَرَبَّكُمۡۚ وَكُنتُ عَلَيۡهِمۡ شَهِيدٗا مَّا دُمۡتُ فِيهِمۡۖ فَلَمَّا تَوَفَّيۡتَنِي كُنتَ أَنتَ ٱلرَّقِيبَ عَلَيۡهِمۡۚ وَأَنتَ عَلَىٰ كُلِّ شَيۡءٖ شَهِيدٌ", "Ma qultu lahum illa ma amartanee bihi ani oAAbudoo Allaha rabbee warabbakum wakuntu AAalayhim shaheedan ma dumtu feehim falamma tawaffaytanee kunta anta alrraqeeba AAalayhim waanta AAala kulli shayin shaheedun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 118, "If You should punish them - indeed they are Your servants; but if You forgive them - indeed it is You who is the Exalted in Might, the Wise", "إِن تُعَذِّبۡهُمۡ فَإِنَّهُمۡ عِبَادُكَۖ وَإِن تَغۡفِرۡ لَهُمۡ فَإِنَّكَ أَنتَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ", "In tuAAaththibhum fainnahum AAibaduka wain taghfir lahum fainnaka anta alAAazeezu alhakeemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 119, "Allah will say, ""This is the Day when the truthful will benefit from their truthfulness."" For them are gardens [in Paradise] beneath which rivers flow, wherein they will abide forever, Allah being pleased with them, and they with Him. That is the great attainment", "قَالَ ٱللَّهُ هَٰذَا يَوۡمُ يَنفَعُ ٱلصَّـٰدِقِينَ صِدۡقُهُمۡۚ لَهُمۡ جَنَّـٰتٞ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَآ أَبَدٗاۖ رَّضِيَ ٱللَّهُ عَنۡهُمۡ وَرَضُواْ عَنۡهُۚ ذَٰلِكَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ", "Qala Allahu hatha yawmu yanfaAAu alssadiqeena sidquhum lahum jannatun tajree min tahtiha alanharu khalideena feeha abadan radiya Allahu AAanhum waradoo AAanhu thalika alfawzu alAAatheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (5, 120, "To Allah belongs the dominion of the heavens and the earth and whatever is within them. And He is over all things competent", "لِلَّهِ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا فِيهِنَّۚ وَهُوَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرُۢ", "Lillahi mulku alssamawati waalardi wama feehinna wahuwa AAala kulli shayin qadeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 1, "[All] praise is [due] to Allah, who created the heavens and the earth and made the darkness and the light. Then those who disbelieve equate [others] with their Lord", "ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَجَعَلَ ٱلظُّلُمَٰتِ وَٱلنُّورَۖ ثُمَّ ٱلَّذِينَ كَفَرُواْ بِرَبِّهِمۡ يَعۡدِلُونَ", "Alhamdu lillahi allathee khalaqa alssamawati waalarda wajaAAala alththulumati waalnnoora thumma allatheena kafaroo birabbihim yaAAdiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 2, "It is He who created you from clay and then decreed a term and a specified time [known] to Him; then [still] you are in dispute", "هُوَ ٱلَّذِي خَلَقَكُم مِّن طِينٖ ثُمَّ قَضَىٰٓ أَجَلٗاۖ وَأَجَلٞ مُّسَمًّى عِندَهُۥۖ ثُمَّ أَنتُمۡ تَمۡتَرُونَ", "Huwa allathee khalaqakum min teenin thumma qada ajalan waajalun musamman AAindahu thumma antum tamtaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 3, "And He is Allah, [the only deity] in the heavens and the earth. He knows your secret and what you make public, and He knows that which you earn", "وَهُوَ ٱللَّهُ فِي ٱلسَّمَٰوَٰتِ وَفِي ٱلۡأَرۡضِ يَعۡلَمُ سِرَّكُمۡ وَجَهۡرَكُمۡ وَيَعۡلَمُ مَا تَكۡسِبُونَ", "Wahuwa Allahu fee alssamawati wafee alardi yaAAlamu sirrakum wajahrakum wayaAAlamu ma taksiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 4, "And no sign comes to them from the signs of their Lord except that they turn away therefrom", "وَمَا تَأۡتِيهِم مِّنۡ ءَايَةٖ مِّنۡ ءَايَٰتِ رَبِّهِمۡ إِلَّا كَانُواْ عَنۡهَا مُعۡرِضِينَ", "Wama tateehim min ayatin min ayati rabbihim illa kanoo AAanha muAArideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 5, "For they had denied the truth when it came to them, but there is going to reach them the news of what they used to ridicule", "فَقَدۡ كَذَّبُواْ بِٱلۡحَقِّ لَمَّا جَآءَهُمۡ فَسَوۡفَ يَأۡتِيهِمۡ أَنۢبَـٰٓؤُاْ مَا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ", "Faqad kaththaboo bialhaqqi lamma jaahum fasawfa yateehim anbao ma kanoo bihi yastahzioona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 6, "Have they not seen how many generations We destroyed before them which We had established upon the earth as We have not established you? And We sent [rain from] the sky upon them in showers and made rivers flow beneath them; then We destroyed them for their sins and brought forth after them a generation of others", "أَلَمۡ يَرَوۡاْ كَمۡ أَهۡلَكۡنَا مِن قَبۡلِهِم مِّن قَرۡنٖ مَّكَّنَّـٰهُمۡ فِي ٱلۡأَرۡضِ مَا لَمۡ نُمَكِّن لَّكُمۡ وَأَرۡسَلۡنَا ٱلسَّمَآءَ عَلَيۡهِم مِّدۡرَارٗا وَجَعَلۡنَا ٱلۡأَنۡهَٰرَ تَجۡرِي مِن تَحۡتِهِمۡ فَأَهۡلَكۡنَٰهُم بِذُنُوبِهِمۡ وَأَنشَأۡنَا مِنۢ بَعۡدِهِمۡ قَرۡنًا ءَاخَرِينَ", "Alam yaraw kam ahlakna min qablihim min qarnin makkannahum fee alardi ma lam numakkin lakum waarsalna alssamaa AAalayhim midraran wajaAAalna alanhara tajree min tahtihim faahlaknahum bithunoobihim waanshana min baAAdihim qarnan akhareena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 7, "And even if We had sent down to you, [O Muhammad], a written scripture on a page and they touched it with their hands, the disbelievers would say, ""This is not but obvious magic", "وَلَوۡ نَزَّلۡنَا عَلَيۡكَ كِتَٰبٗا فِي قِرۡطَاسٖ فَلَمَسُوهُ بِأَيۡدِيهِمۡ لَقَالَ ٱلَّذِينَ كَفَرُوٓاْ إِنۡ هَٰذَآ إِلَّا سِحۡرٞ مُّبِينٞ", "Walaw nazzalna AAalayka kitaban fee qirtasin falamasoohu biaydeehim laqala allatheena kafaroo in hatha illa sihrun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 8, "And they say, ""Why was there not sent down to him an angel?"" But if We had sent down an angel, the matter would have been decided; then they would not be reprieved", "وَقَالُواْ لَوۡلَآ أُنزِلَ عَلَيۡهِ مَلَكٞۖ وَلَوۡ أَنزَلۡنَا مَلَكٗا لَّقُضِيَ ٱلۡأَمۡرُ ثُمَّ لَا يُنظَرُونَ", "Waqaloo lawla onzila AAalayhi malakun walaw anzalna malakan laqudiya alamru thumma la yuntharoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 9, "And if We had made him an angel, We would have made him [appear as] a man, and We would have covered them with that in which they cover themselves", "وَلَوۡ جَعَلۡنَٰهُ مَلَكٗا لَّجَعَلۡنَٰهُ رَجُلٗا وَلَلَبَسۡنَا عَلَيۡهِم مَّا يَلۡبِسُونَ", "Walaw jaAAalnahu malakan lajaAAalnahu rajulan walalabasna AAalayhim ma yalbisoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 10, "And already were messengers ridiculed before you, but those who mocked them were enveloped by that which they used to ridicule", "وَلَقَدِ ٱسۡتُهۡزِئَ بِرُسُلٖ مِّن قَبۡلِكَ فَحَاقَ بِٱلَّذِينَ سَخِرُواْ مِنۡهُم مَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ", "Walaqadi istuhzia birusulin min qablika fahaqa biallatheena sakhiroo minhum ma kanoo bihi yastahzioona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 11, "Say, ""Travel through the land; then observe how was the end of the deniers", "قُلۡ سِيرُواْ فِي ٱلۡأَرۡضِ ثُمَّ ٱنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلۡمُكَذِّبِينَ", "Qul seeroo fee alardi thumma onthuroo kayfa kana AAaqibatu almukaththibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 12, "Say, ""To whom belongs whatever is in the heavens and earth?"" Say, ""To Allah."" He has decreed upon Himself mercy. He will surely assemble you for the Day of Resurrection, about which there is no doubt. Those who will lose themselves [that Day] do not believe", "قُل لِّمَن مَّا فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ قُل لِّلَّهِۚ كَتَبَ عَلَىٰ نَفۡسِهِ ٱلرَّحۡمَةَۚ لَيَجۡمَعَنَّكُمۡ إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِ لَا رَيۡبَ فِيهِۚ ٱلَّذِينَ خَسِرُوٓاْ أَنفُسَهُمۡ فَهُمۡ لَا يُؤۡمِنُونَ", "Qul liman ma fee alssamawati waalardi qul lillahi kataba AAala nafsihi alrrahmata layajmaAAannakum ila yawmi alqiyamati la rayba feehi allatheena khasiroo anfusahum fahum la yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 13, "And to Him belongs that which reposes by night and by day, and He is the Hearing, the Knowing", "۞وَلَهُۥ مَا سَكَنَ فِي ٱلَّيۡلِ وَٱلنَّهَارِۚ وَهُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ", "Walahu ma sakana fee allayli waalnnahari wahuwa alssameeAAu alAAaleemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 14, "Say, ""Is it other than Allah I should take as a protector, Creator of the heavens and the earth, while it is He who feeds and is not fed?"" Say, [O Muhammad], ""Indeed, I have been commanded to be the first [among you] who submit [to Allah] and [was commanded], 'Do not ever be of the polytheists", "قُلۡ أَغَيۡرَ ٱللَّهِ أَتَّخِذُ وَلِيّٗا فَاطِرِ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَهُوَ يُطۡعِمُ وَلَا يُطۡعَمُۗ قُلۡ إِنِّيٓ أُمِرۡتُ أَنۡ أَكُونَ أَوَّلَ مَنۡ أَسۡلَمَۖ وَلَا تَكُونَنَّ مِنَ ٱلۡمُشۡرِكِينَ", "Qul aghayra Allahi attakhithu waliyyan fatiri alssamawati waalardi wahuwa yutAAimu wala yutAAamu qul innee omirtu an akoona awwala man aslama wala takoonanna mina almushrikeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 15, "Say, ""Indeed I fear, if I should disobey my Lord, the punishment of a tremendous Day", "قُلۡ إِنِّيٓ أَخَافُ إِنۡ عَصَيۡتُ رَبِّي عَذَابَ يَوۡمٍ عَظِيمٖ", "Qul innee akhafu in AAasaytu rabbee AAathaba yawmin AAatheemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 16, "He from whom it is averted that Day - [Allah] has granted him mercy. And that is the clear attainment", "مَّن يُصۡرَفۡ عَنۡهُ يَوۡمَئِذٖ فَقَدۡ رَحِمَهُۥۚ وَذَٰلِكَ ٱلۡفَوۡزُ ٱلۡمُبِينُ", "Man yusraf AAanhu yawmaithin faqad rahimahu wathalika alfawzu almubeenu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 17, "And if Allah should touch you with adversity, there is no remover of it except Him. And if He touches you with good - then He is over all things competent", "وَإِن يَمۡسَسۡكَ ٱللَّهُ بِضُرّٖ فَلَا كَاشِفَ لَهُۥٓ إِلَّا هُوَۖ وَإِن يَمۡسَسۡكَ بِخَيۡرٖ فَهُوَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ", "Wain yamsaska Allahu bidurrin fala kashifa lahu illa huwa wain yamsaska bikhayrin fahuwa AAala kulli shayin qadeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 18, "And He is the subjugator over His servants. And He is the Wise, the Acquainted [with all]", "وَهُوَ ٱلۡقَاهِرُ فَوۡقَ عِبَادِهِۦۚ وَهُوَ ٱلۡحَكِيمُ ٱلۡخَبِيرُ", "Wahuwa alqahiru fawqa AAibadihi wahuwa alhakeemu alkhabeeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 19, "Say, ""What thing is greatest in testimony?"" Say, ""Allah is witness between me and you. And this Qur'an was revealed to me that I may warn you thereby and whomever it reaches. Do you [truly] testify that with Allah there are other deities?"" Say, ""I will not testify [with you]."" Say, ""Indeed, He is but one God, and indeed, I am free of what you associate [with Him]", "قُلۡ أَيُّ شَيۡءٍ أَكۡبَرُ شَهَٰدَةٗۖ قُلِ ٱللَّهُۖ شَهِيدُۢ بَيۡنِي وَبَيۡنَكُمۡۚ وَأُوحِيَ إِلَيَّ هَٰذَا ٱلۡقُرۡءَانُ لِأُنذِرَكُم بِهِۦ وَمَنۢ بَلَغَۚ أَئِنَّكُمۡ لَتَشۡهَدُونَ أَنَّ مَعَ ٱللَّهِ ءَالِهَةً أُخۡرَىٰۚ قُل لَّآ أَشۡهَدُۚ قُلۡ إِنَّمَا هُوَ إِلَٰهٞ وَٰحِدٞ وَإِنَّنِي بَرِيٓءٞ مِّمَّا تُشۡرِكُونَ", "Qul ayyu shayin akbaru shahadatan quli Allahu shaheedun baynee wabaynakum waoohiya ilayya hatha alquranu lionthirakum bihi waman balagha ainnakum latashhadoona anna maAAa Allahi alihatan okhra qul la ashhadu qul innama huwa ilahun wahidun wainnanee bareeon mimma tushrikoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 20, "Those to whom We have given the Scripture recognize it as they recognize their [own] sons. Those who will lose themselves [in the Hereafter] do not believe", "ٱلَّذِينَ ءَاتَيۡنَٰهُمُ ٱلۡكِتَٰبَ يَعۡرِفُونَهُۥ كَمَا يَعۡرِفُونَ أَبۡنَآءَهُمُۘ ٱلَّذِينَ خَسِرُوٓاْ أَنفُسَهُمۡ فَهُمۡ لَا يُؤۡمِنُونَ", "Allatheena ataynahumu alkitaba yaAArifoonahu kama yaAArifoona abnaahum allatheena khasiroo anfusahum fahum la yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 21, "And who is more unjust than one who invents about Allah a lie or denies His verses? Indeed, the wrongdoers will not succeed", "وَمَنۡ أَظۡلَمُ مِمَّنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبًا أَوۡ كَذَّبَ بِـَٔايَٰتِهِۦٓۚ إِنَّهُۥ لَا يُفۡلِحُ ٱلظَّـٰلِمُونَ", "Waman athlamu mimmani iftara AAala Allahi kathiban aw kaththaba biayatihi innahu la yuflihu alththalimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 22, "And [mention, O Muhammad], the Day We will gather them all together; then We will say to those who associated others with Allah, ""Where are your 'partners' that you used to claim [with Him]", "وَيَوۡمَ نَحۡشُرُهُمۡ جَمِيعٗا ثُمَّ نَقُولُ لِلَّذِينَ أَشۡرَكُوٓاْ أَيۡنَ شُرَكَآؤُكُمُ ٱلَّذِينَ كُنتُمۡ تَزۡعُمُونَ", "Wayawma nahshuruhum jameeAAan thumma naqoolu lillatheena ashrakoo ayna shurakaokumu allatheena kuntum tazAAumoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 23, "Then there will be no [excuse upon] examination except they will say, ""By Allah, our Lord, we were not those who associated", "ثُمَّ لَمۡ تَكُن فِتۡنَتُهُمۡ إِلَّآ أَن قَالُواْ وَٱللَّهِ رَبِّنَا مَا كُنَّا مُشۡرِكِينَ", "Thumma lam takun fitnatuhum illa an qaloo waAllahi rabbina ma kunna mushrikeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 24, "See how they will lie about themselves. And lost from them will be what they used to invent", "ٱنظُرۡ كَيۡفَ كَذَبُواْ عَلَىٰٓ أَنفُسِهِمۡۚ وَضَلَّ عَنۡهُم مَّا كَانُواْ يَفۡتَرُونَ", "Onthur kayfa kathaboo AAala anfusihim wadalla AAanhum ma kanoo yaftaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 25, "And among them are those who listen to you, but We have placed over their hearts coverings, lest they understand it, and in their ears deafness. And if they should see every sign, they will not believe in it. Even when they come to you arguing with you, those who disbelieve say, ""This is not but legends of the former peoples", "وَمِنۡهُم مَّن يَسۡتَمِعُ إِلَيۡكَۖ وَجَعَلۡنَا عَلَىٰ قُلُوبِهِمۡ أَكِنَّةً أَن يَفۡقَهُوهُ وَفِيٓ ءَاذَانِهِمۡ وَقۡرٗاۚ وَإِن يَرَوۡاْ كُلَّ ءَايَةٖ لَّا يُؤۡمِنُواْ بِهَاۖ حَتَّىٰٓ إِذَا جَآءُوكَ يُجَٰدِلُونَكَ يَقُولُ ٱلَّذِينَ كَفَرُوٓاْ إِنۡ هَٰذَآ إِلَّآ أَسَٰطِيرُ ٱلۡأَوَّلِينَ", "Waminhum man yastamiAAu ilayka wajaAAalna AAala quloobihim akinnatan an yafqahoohu wafee athanihim waqran wain yaraw kulla ayatin la yuminoo biha hatta itha jaooka yujadiloonaka yaqoolu allatheena kafaroo in hatha illa asateeru alawwaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 26, "And they prevent [others] from him and are [themselves] remote from him. And they do not destroy except themselves, but they perceive [it] not", "وَهُمۡ يَنۡهَوۡنَ عَنۡهُ وَيَنۡـَٔوۡنَ عَنۡهُۖ وَإِن يُهۡلِكُونَ إِلَّآ أَنفُسَهُمۡ وَمَا يَشۡعُرُونَ", "Wahum yanhawna AAanhu wayanawna AAanhu wain yuhlikoona illa anfusahum wama yashAAuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 27, "If you could but see when they are made to stand before the Fire and will say, ""Oh, would that we could be returned [to life on earth] and not deny the signs of our Lord and be among the believers", "وَلَوۡ تَرَىٰٓ إِذۡ وُقِفُواْ عَلَى ٱلنَّارِ فَقَالُواْ يَٰلَيۡتَنَا نُرَدُّ وَلَا نُكَذِّبَ بِـَٔايَٰتِ رَبِّنَا وَنَكُونَ مِنَ ٱلۡمُؤۡمِنِينَ", "Walaw tara ith wuqifoo AAala alnnari faqaloo ya laytana nuraddu wala nukaththiba biayati rabbina wanakoona mina almumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 28, "But what they concealed before has [now] appeared to them. And even if they were returned, they would return to that which they were forbidden; and indeed, they are liars", "بَلۡ بَدَا لَهُم مَّا كَانُواْ يُخۡفُونَ مِن قَبۡلُۖ وَلَوۡ رُدُّواْ لَعَادُواْ لِمَا نُهُواْ عَنۡهُ وَإِنَّهُمۡ لَكَٰذِبُونَ", "Bal bada lahum ma kanoo yukhfoona min qablu walaw ruddoo laAAadoo lima nuhoo AAanhu wainnahum lakathiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 29, "And they say, ""There is none but our worldly life, and we will not be resurrected", "وَقَالُوٓاْ إِنۡ هِيَ إِلَّا حَيَاتُنَا ٱلدُّنۡيَا وَمَا نَحۡنُ بِمَبۡعُوثِينَ", "Waqaloo in hiya illa hayatuna alddunya wama nahnu bimabAAootheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 30, "If you could but see when they will be made to stand before their Lord. He will say, ""Is this not the truth?"" They will say, ""Yes, by our Lord."" He will [then] say, ""So taste the punishment because you used to disbelieve", "وَلَوۡ تَرَىٰٓ إِذۡ وُقِفُواْ عَلَىٰ رَبِّهِمۡۚ قَالَ أَلَيۡسَ هَٰذَا بِٱلۡحَقِّۚ قَالُواْ بَلَىٰ وَرَبِّنَاۚ قَالَ فَذُوقُواْ ٱلۡعَذَابَ بِمَا كُنتُمۡ تَكۡفُرُونَ", "Walaw tara ith wuqifoo AAala rabbihim qala alaysa hatha bialhaqqi qaloo bala warabbina qala fathooqoo alAAathaba bima kuntum takfuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 31, "Those will have lost who deny the meeting with Allah, until when the Hour [of resurrection] comes upon them unexpectedly, they will say, ""Oh, [how great is] our regret over what we neglected concerning it,"" while they bear their burdens on their backs. Unquestionably, evil is that which they bear", "قَدۡ خَسِرَ ٱلَّذِينَ كَذَّبُواْ بِلِقَآءِ ٱللَّهِۖ حَتَّىٰٓ إِذَا جَآءَتۡهُمُ ٱلسَّاعَةُ بَغۡتَةٗ قَالُواْ يَٰحَسۡرَتَنَا عَلَىٰ مَا فَرَّطۡنَا فِيهَا وَهُمۡ يَحۡمِلُونَ أَوۡزَارَهُمۡ عَلَىٰ ظُهُورِهِمۡۚ أَلَا سَآءَ مَا يَزِرُونَ", "Qad khasira allatheena kaththaboo biliqai Allahi hatta itha jaathumu alssaAAatu baghtatan qaloo ya hasratana AAala ma farratna feeha wahum yahmiloona awzarahum AAala thuhoorihim ala saa ma yaziroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 32, "And the worldly life is not but amusement and diversion; but the home of the Hereafter is best for those who fear Allah, so will you not reason", "وَمَا ٱلۡحَيَوٰةُ ٱلدُّنۡيَآ إِلَّا لَعِبٞ وَلَهۡوٞۖ وَلَلدَّارُ ٱلۡأٓخِرَةُ خَيۡرٞ لِّلَّذِينَ يَتَّقُونَۚ أَفَلَا تَعۡقِلُونَ", "Wama alhayatu alddunya illa laAAibun walahwun walalddaru alakhirati khayrun lillatheena yattaqoona afala taAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 33, "We know that you, [O Muhammad], are saddened by what they say. And indeed, they do not call you untruthful, but it is the verses of Allah that the wrongdoers reject", "قَدۡ نَعۡلَمُ إِنَّهُۥ لَيَحۡزُنُكَ ٱلَّذِي يَقُولُونَۖ فَإِنَّهُمۡ لَا يُكَذِّبُونَكَ وَلَٰكِنَّ ٱلظَّـٰلِمِينَ بِـَٔايَٰتِ ٱللَّهِ يَجۡحَدُونَ", "Qad naAAlamu innahu layahzunuka allathee yaqooloona fainnahum la yukaththiboonaka walakinna alththalimeena biayati Allahi yajhadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 34, "And certainly were messengers denied before you, but they were patient over [the effects of] denial, and they were harmed until Our victory came to them. And none can alter the words of Allah. And there has certainly come to you some information about the [previous] messengers", "وَلَقَدۡ كُذِّبَتۡ رُسُلٞ مِّن قَبۡلِكَ فَصَبَرُواْ عَلَىٰ مَا كُذِّبُواْ وَأُوذُواْ حَتَّىٰٓ أَتَىٰهُمۡ نَصۡرُنَاۚ وَلَا مُبَدِّلَ لِكَلِمَٰتِ ٱللَّهِۚ وَلَقَدۡ جَآءَكَ مِن نَّبَإِيْ ٱلۡمُرۡسَلِينَ", "Walaqad kuththibat rusulun min qablika fasabaroo AAala ma kuththiboo waoothoo hatta atahum nasruna wala mubaddila likalimati Allahi walaqad jaaka min nabai almursaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 35, "And if their evasion is difficult for you, then if you are able to seek a tunnel into the earth or a stairway into the sky to bring them a sign, [then do so]. But if Allah had willed, He would have united them upon guidance. So never be of the ignorant", "وَإِن كَانَ كَبُرَ عَلَيۡكَ إِعۡرَاضُهُمۡ فَإِنِ ٱسۡتَطَعۡتَ أَن تَبۡتَغِيَ نَفَقٗا فِي ٱلۡأَرۡضِ أَوۡ سُلَّمٗا فِي ٱلسَّمَآءِ فَتَأۡتِيَهُم بِـَٔايَةٖۚ وَلَوۡ شَآءَ ٱللَّهُ لَجَمَعَهُمۡ عَلَى ٱلۡهُدَىٰۚ فَلَا تَكُونَنَّ مِنَ ٱلۡجَٰهِلِينَ", "Wain kana kabura AAalayka iAAraduhum faini istataAAta an tabtaghiya nafaqan fee alardi aw sullaman fee alssamai fatatiyahum biayatin walaw shaa Allahu lajamaAAahum AAala alhuda fala takoonanna mina aljahileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 36, "Only those who hear will respond. But the dead - Allah will resurrect them; then to Him they will be returned", "۞إِنَّمَا يَسۡتَجِيبُ ٱلَّذِينَ يَسۡمَعُونَۘ وَٱلۡمَوۡتَىٰ يَبۡعَثُهُمُ ٱللَّهُ ثُمَّ إِلَيۡهِ يُرۡجَعُونَ", "Innama yastajeebu allatheena yasmaAAoona waalmawta yabAAathuhumu Allahu thumma ilayhi yurjaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 37, "And they say, ""Why has a sign not been sent down to him from his Lord?"" Say, ""Indeed, Allah is Able to send down a sign, but most of them do not know", "وَقَالُواْ لَوۡلَا نُزِّلَ عَلَيۡهِ ءَايَةٞ مِّن رَّبِّهِۦۚ قُلۡ إِنَّ ٱللَّهَ قَادِرٌ عَلَىٰٓ أَن يُنَزِّلَ ءَايَةٗ وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَعۡلَمُونَ", "Waqaloo lawla nuzzila AAalayhi ayatun min rabbihi qul inna Allaha qadirun AAala an yunazzila ayatan walakinna aktharahum la yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 38, "And there is no creature on [or within] the earth or bird that flies with its wings except [that they are] communities like you. We have not neglected in the Register a thing. Then unto their Lord they will be gathered", "وَمَا مِن دَآبَّةٖ فِي ٱلۡأَرۡضِ وَلَا طَـٰٓئِرٖ يَطِيرُ بِجَنَاحَيۡهِ إِلَّآ أُمَمٌ أَمۡثَالُكُمۚ مَّا فَرَّطۡنَا فِي ٱلۡكِتَٰبِ مِن شَيۡءٖۚ ثُمَّ إِلَىٰ رَبِّهِمۡ يُحۡشَرُونَ", "Wama min dabbatin fee alardi wala tairin yateeru bijanahayhi illa omamun amthalukum ma farratna fee alkitabi min shayin thumma ila rabbihim yuhsharoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 39, "But those who deny Our verses are deaf and dumb within darknesses. Whomever Allah wills - He leaves astray; and whomever He wills - He puts him on a straight path", "وَٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا صُمّٞ وَبُكۡمٞ فِي ٱلظُّلُمَٰتِۗ مَن يَشَإِ ٱللَّهُ يُضۡلِلۡهُ وَمَن يَشَأۡ يَجۡعَلۡهُ عَلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ", "Waallatheena kaththaboo biayatina summun wabukmun fee alththulumati man yashai Allahu yudlilhu waman yasha yajAAalhu AAala siratin mustaqeemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 40, "Say, ""Have you considered: if there came to you the punishment of Allah or there came to you the Hour - is it other than Allah you would invoke, if you should be truthful", "قُلۡ أَرَءَيۡتَكُمۡ إِنۡ أَتَىٰكُمۡ عَذَابُ ٱللَّهِ أَوۡ أَتَتۡكُمُ ٱلسَّاعَةُ أَغَيۡرَ ٱللَّهِ تَدۡعُونَ إِن كُنتُمۡ صَٰدِقِينَ", "Qul araaytakum in atakum AAathabu Allahi aw atatkumu alssaAAatu aghayra Allahi tadAAoona in kuntum sadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 41, "No, it is Him [alone] you would invoke, and He would remove that for which you invoked Him if He willed, and you would forget what you associate [with Him]", "بَلۡ إِيَّاهُ تَدۡعُونَ فَيَكۡشِفُ مَا تَدۡعُونَ إِلَيۡهِ إِن شَآءَ وَتَنسَوۡنَ مَا تُشۡرِكُونَ", "Bal iyyahu tadAAoona fayakshifu ma tadAAoona ilayhi in shaa watansawna ma tushrikoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 42, "And We have already sent [messengers] to nations before you, [O Muhammad]; then We seized them with poverty and hardship that perhaps they might humble themselves [to Us]", "وَلَقَدۡ أَرۡسَلۡنَآ إِلَىٰٓ أُمَمٖ مِّن قَبۡلِكَ فَأَخَذۡنَٰهُم بِٱلۡبَأۡسَآءِ وَٱلضَّرَّآءِ لَعَلَّهُمۡ يَتَضَرَّعُونَ", "Walaqad arsalna ila omamin min qablika faakhathnahum bialbasai waalddarrai laAAallahum yatadarraAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 43, "Then why, when Our punishment came to them, did they not humble themselves? But their hearts became hardened, and Satan made attractive to them that which they were doing", "فَلَوۡلَآ إِذۡ جَآءَهُم بَأۡسُنَا تَضَرَّعُواْ وَلَٰكِن قَسَتۡ قُلُوبُهُمۡ وَزَيَّنَ لَهُمُ ٱلشَّيۡطَٰنُ مَا كَانُواْ يَعۡمَلُونَ", "Falawla ith jaahum basuna tadarraAAoo walakin qasat quloobuhum wazayyana lahumu alshshaytanu ma kanoo yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 44, "So when they forgot that by which they had been reminded, We opened to them the doors of every [good] thing until, when they rejoiced in that which they were given, We seized them suddenly, and they were [then] in despair", "فَلَمَّا نَسُواْ مَا ذُكِّرُواْ بِهِۦ فَتَحۡنَا عَلَيۡهِمۡ أَبۡوَٰبَ كُلِّ شَيۡءٍ حَتَّىٰٓ إِذَا فَرِحُواْ بِمَآ أُوتُوٓاْ أَخَذۡنَٰهُم بَغۡتَةٗ فَإِذَا هُم مُّبۡلِسُونَ", "Falamma nasoo ma thukkiroo bihi fatahna AAalayhim abwaba kulli shayin hatta itha farihoo bima ootoo akhathnahum baghtatan faitha hum mublisoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 45, "So the people that committed wrong were eliminated. And praise to Allah, Lord of the worlds", "فَقُطِعَ دَابِرُ ٱلۡقَوۡمِ ٱلَّذِينَ ظَلَمُواْۚ وَٱلۡحَمۡدُ لِلَّهِ رَبِّ ٱلۡعَٰلَمِينَ", "FaqutiAAa dabiru alqawmi allatheena thalamoo waalhamdu lillahi rabbi alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 46, "Say, ""Have you considered: if Allah should take away your hearing and your sight and set a seal upon your hearts, which deity other than Allah could bring them [back] to you?"" Look how we diversify the verses; then they [still] turn away", "قُلۡ أَرَءَيۡتُمۡ إِنۡ أَخَذَ ٱللَّهُ سَمۡعَكُمۡ وَأَبۡصَٰرَكُمۡ وَخَتَمَ عَلَىٰ قُلُوبِكُم مَّنۡ إِلَٰهٌ غَيۡرُ ٱللَّهِ يَأۡتِيكُم بِهِۗ ٱنظُرۡ كَيۡفَ نُصَرِّفُ ٱلۡأٓيَٰتِ ثُمَّ هُمۡ يَصۡدِفُونَ", "Qul araaytum in akhatha Allahu samAAakum waabsarakum wakhatama AAala quloobikum man ilahun ghayru Allahi yateekum bihi onthur kayfa nusarrifu alayati thumma hum yasdifoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 47, "Say, ""Have you considered: if the punishment of Allah should come to you unexpectedly or manifestly, will any be destroyed but the wrongdoing people", "قُلۡ أَرَءَيۡتَكُمۡ إِنۡ أَتَىٰكُمۡ عَذَابُ ٱللَّهِ بَغۡتَةً أَوۡ جَهۡرَةً هَلۡ يُهۡلَكُ إِلَّا ٱلۡقَوۡمُ ٱلظَّـٰلِمُونَ", "Qul araaytakum in atakum AAathabu Allahi baghtatan aw jahratan hal yuhlaku illa alqawmu alththalimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 48, "And We send not the messengers except as bringers of good tidings and warners. So whoever believes and reforms - there will be no fear concerning them, nor will they grieve", "وَمَا نُرۡسِلُ ٱلۡمُرۡسَلِينَ إِلَّا مُبَشِّرِينَ وَمُنذِرِينَۖ فَمَنۡ ءَامَنَ وَأَصۡلَحَ فَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ", "Wama nursilu almursaleena illa mubashshireena wamunthireena faman amana waaslaha fala khawfun AAalayhim wala hum yahzanoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 49, "But those who deny Our verses - the punishment will touch them for their defiant disobedience", "وَٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا يَمَسُّهُمُ ٱلۡعَذَابُ بِمَا كَانُواْ يَفۡسُقُونَ", "Waallatheena kaththaboo biayatina yamassuhumu alAAathabu bima kanoo yafsuqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 50, "Say, [O Muhammad], ""I do not tell you that I have the depositories [containing the provision] of Allah or that I know the unseen, nor do I tell you that I am an angel. I only follow what is revealed to me."" Say, ""Is the blind equivalent to the seeing? Then will you not give thought", "قُل لَّآ أَقُولُ لَكُمۡ عِندِي خَزَآئِنُ ٱللَّهِ وَلَآ أَعۡلَمُ ٱلۡغَيۡبَ وَلَآ أَقُولُ لَكُمۡ إِنِّي مَلَكٌۖ إِنۡ أَتَّبِعُ إِلَّا مَا يُوحَىٰٓ إِلَيَّۚ قُلۡ هَلۡ يَسۡتَوِي ٱلۡأَعۡمَىٰ وَٱلۡبَصِيرُۚ أَفَلَا تَتَفَكَّرُونَ", "Qul la aqoolu lakum AAindee khazainu Allahi wala aAAlamu alghayba wala aqoolu lakum innee malakun in attabiAAu illa ma yooha ilayya qul hal yastawee alaAAma waalbaseeru afala tatafakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 51, "And warn by the Qur'an those who fear that they will be gathered before their Lord - for them besides Him will be no protector and no intercessor - that they might become righteous", "وَأَنذِرۡ بِهِ ٱلَّذِينَ يَخَافُونَ أَن يُحۡشَرُوٓاْ إِلَىٰ رَبِّهِمۡ لَيۡسَ لَهُم مِّن دُونِهِۦ وَلِيّٞ وَلَا شَفِيعٞ لَّعَلَّهُمۡ يَتَّقُونَ", "Waanthir bihi allatheena yakhafoona an yuhsharoo ila rabbihim laysa lahum min doonihi waliyyun wala shafeeAAun laAAallahum yattaqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 52, "And do not send away those who call upon their Lord morning and afternoon, seeking His countenance. Not upon you is anything of their account and not upon them is anything of your account. So were you to send them away, you would [then] be of the wrongdoers", "وَلَا تَطۡرُدِ ٱلَّذِينَ يَدۡعُونَ رَبَّهُم بِٱلۡغَدَوٰةِ وَٱلۡعَشِيِّ يُرِيدُونَ وَجۡهَهُۥۖ مَا عَلَيۡكَ مِنۡ حِسَابِهِم مِّن شَيۡءٖ وَمَا مِنۡ حِسَابِكَ عَلَيۡهِم مِّن شَيۡءٖ فَتَطۡرُدَهُمۡ فَتَكُونَ مِنَ ٱلظَّـٰلِمِينَ", "Wala tatrudi allatheena yadAAoona rabbahum bialghadati waalAAashiyyi yureedoona wajhahu ma AAalayka min hisabihim min shayin wama min hisabika AAalayhim min shayin fatatrudahum fatakoona mina alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 53, "And thus We have tried some of them through others that the disbelievers might say, ""Is it these whom Allah has favored among us?"" Is not Allah most knowing of those who are grateful", "وَكَذَٰلِكَ فَتَنَّا بَعۡضَهُم بِبَعۡضٖ لِّيَقُولُوٓاْ أَهَـٰٓؤُلَآءِ مَنَّ ٱللَّهُ عَلَيۡهِم مِّنۢ بَيۡنِنَآۗ أَلَيۡسَ ٱللَّهُ بِأَعۡلَمَ بِٱلشَّـٰكِرِينَ", "Wakathalika fatanna baAAdahum bibaAAdin liyaqooloo ahaolai manna Allahu AAalayhim min baynina alaysa Allahu biaAAlama bialshshakireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 54, "And when those come to you who believe in Our verses, say, ""Peace be upon you. Your Lord has decreed upon Himself mercy: that any of you who does wrong out of ignorance and then repents after that and corrects himself - indeed, He is Forgiving and Merciful", "وَإِذَا جَآءَكَ ٱلَّذِينَ يُؤۡمِنُونَ بِـَٔايَٰتِنَا فَقُلۡ سَلَٰمٌ عَلَيۡكُمۡۖ كَتَبَ رَبُّكُمۡ عَلَىٰ نَفۡسِهِ ٱلرَّحۡمَةَ أَنَّهُۥ مَنۡ عَمِلَ مِنكُمۡ سُوٓءَۢا بِجَهَٰلَةٖ ثُمَّ تَابَ مِنۢ بَعۡدِهِۦ وَأَصۡلَحَ فَأَنَّهُۥ غَفُورٞ رَّحِيمٞ", "Waitha jaaka allatheena yuminoona biayatina faqul salamun AAalaykum kataba rabbukum AAala nafsihi alrrahmata annahu man AAamila minkum sooan bijahalatin thumma taba min baAAdihi waaslaha faannahu ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 55, "And thus do We detail the verses, and [thus] the way of the criminals will become evident", "وَكَذَٰلِكَ نُفَصِّلُ ٱلۡأٓيَٰتِ وَلِتَسۡتَبِينَ سَبِيلُ ٱلۡمُجۡرِمِينَ", "Wakathalika nufassilu alayati walitastabeena sabeelu almujrimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 56, "Say, ""Indeed, I have been forbidden to worship those you invoke besides Allah."" Say, ""I will not follow your desires, for I would then have gone astray, and I would not be of the [rightly] guided", "قُلۡ إِنِّي نُهِيتُ أَنۡ أَعۡبُدَ ٱلَّذِينَ تَدۡعُونَ مِن دُونِ ٱللَّهِۚ قُل لَّآ أَتَّبِعُ أَهۡوَآءَكُمۡ قَدۡ ضَلَلۡتُ إِذٗا وَمَآ أَنَا۠ مِنَ ٱلۡمُهۡتَدِينَ", "Qul innee nuheetu an aAAbuda allatheena tadAAoona min dooni Allahi qul la attabiAAu ahwaakum qad dalaltu ithan wama ana mina almuhtadeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 57, "Say, ""Indeed, I am on clear evidence from my Lord, and you have denied it. I do not have that for which you are impatient. The decision is only for Allah. He relates the truth, and He is the best of deciders", "قُلۡ إِنِّي عَلَىٰ بَيِّنَةٖ مِّن رَّبِّي وَكَذَّبۡتُم بِهِۦۚ مَا عِندِي مَا تَسۡتَعۡجِلُونَ بِهِۦٓۚ إِنِ ٱلۡحُكۡمُ إِلَّا لِلَّهِۖ يَقُصُّ ٱلۡحَقَّۖ وَهُوَ خَيۡرُ ٱلۡفَٰصِلِينَ", "Qul innee AAala bayyinatin min rabbee wakaththabtum bihi ma AAindee ma tastaAAjiloona bihi ini alhukmu illa lillahi yaqussu alhaqqa wahuwa khayru alfasileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 58, "Say, ""If I had that for which you are impatient, the matter would have been decided between me and you, but Allah is most knowing of the wrongdoers", "قُل لَّوۡ أَنَّ عِندِي مَا تَسۡتَعۡجِلُونَ بِهِۦ لَقُضِيَ ٱلۡأَمۡرُ بَيۡنِي وَبَيۡنَكُمۡۗ وَٱللَّهُ أَعۡلَمُ بِٱلظَّـٰلِمِينَ", "Qul law anna AAindee ma tastaAAjiloona bihi laqudiya alamru baynee wabaynakum waAllahu aAAlamu bialththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 59, "And with Him are the keys of the unseen; none knows them except Him. And He knows what is on the land and in the sea. Not a leaf falls but that He knows it. And no grain is there within the darknesses of the earth and no moist or dry [thing] but that it is [written] in a clear record", "۞وَعِندَهُۥ مَفَاتِحُ ٱلۡغَيۡبِ لَا يَعۡلَمُهَآ إِلَّا هُوَۚ وَيَعۡلَمُ مَا فِي ٱلۡبَرِّ وَٱلۡبَحۡرِۚ وَمَا تَسۡقُطُ مِن وَرَقَةٍ إِلَّا يَعۡلَمُهَا وَلَا حَبَّةٖ فِي ظُلُمَٰتِ ٱلۡأَرۡضِ وَلَا رَطۡبٖ وَلَا يَابِسٍ إِلَّا فِي كِتَٰبٖ مُّبِينٖ", "WaAAindahu mafatihu alghaybi la yaAAlamuha illa huwa wayaAAlamu ma fee albarri waalbahri wama tasqutu min waraqatin illa yaAAlamuha wala habbatin fee thulumati alardi wala ratbin wala yabisin illa fee kitabin mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 60, "And it is He who takes your souls by night and knows what you have committed by day. Then He revives you therein that a specified term may be fulfilled. Then to Him will be your return; then He will inform you about what you used to do", "وَهُوَ ٱلَّذِي يَتَوَفَّىٰكُم بِٱلَّيۡلِ وَيَعۡلَمُ مَا جَرَحۡتُم بِٱلنَّهَارِ ثُمَّ يَبۡعَثُكُمۡ فِيهِ لِيُقۡضَىٰٓ أَجَلٞ مُّسَمّٗىۖ ثُمَّ إِلَيۡهِ مَرۡجِعُكُمۡ ثُمَّ يُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ", "Wahuwa allathee yatawaffakum biallayli wayaAAlamu ma jarahtum bialnnahari thumma yabAAathukum feehi liyuqda ajalun musamman thumma ilayhi marjiAAukum thumma yunabbiokum bima kuntum taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 61, "And He is the subjugator over His servants, and He sends over you guardian-angels until, when death comes to one of you, Our messengers take him, and they do not fail [in their duties]", "وَهُوَ ٱلۡقَاهِرُ فَوۡقَ عِبَادِهِۦۖ وَيُرۡسِلُ عَلَيۡكُمۡ حَفَظَةً حَتَّىٰٓ إِذَا جَآءَ أَحَدَكُمُ ٱلۡمَوۡتُ تَوَفَّتۡهُ رُسُلُنَا وَهُمۡ لَا يُفَرِّطُونَ", "Wahuwa alqahiru fawqa AAibadihi wayursilu AAalaykum hafathatan hatta itha jaa ahadakumu almawtu tawaffathu rusuluna wahum la yufarritoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 62, "Then they His servants are returned to Allah, their true Lord. Unquestionably, His is the judgement, and He is the swiftest of accountants", "ثُمَّ رُدُّوٓاْ إِلَى ٱللَّهِ مَوۡلَىٰهُمُ ٱلۡحَقِّۚ أَلَا لَهُ ٱلۡحُكۡمُ وَهُوَ أَسۡرَعُ ٱلۡحَٰسِبِينَ", "Thumma ruddoo ila Allahi mawlahumu alhaqqi ala lahu alhukmu wahuwa asraAAu alhasibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 63, "Say, ""Who rescues you from the darknesses of the land and sea [when] you call upon Him imploring [aloud] and privately, 'If He should save us from this [crisis], we will surely be among the thankful", "قُلۡ مَن يُنَجِّيكُم مِّن ظُلُمَٰتِ ٱلۡبَرِّ وَٱلۡبَحۡرِ تَدۡعُونَهُۥ تَضَرُّعٗا وَخُفۡيَةٗ لَّئِنۡ أَنجَىٰنَا مِنۡ هَٰذِهِۦ لَنَكُونَنَّ مِنَ ٱلشَّـٰكِرِينَ", "Qul man yunajjeekum min thulumati albarri waalbahri tadAAoonahu tadarruAAan wakhufyatan lain anjana min hathihi lanakoonanna mina alshshakireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 64, "Say, ""It is Allah who saves you from it and from every distress; then you [still] associate others with Him", "قُلِ ٱللَّهُ يُنَجِّيكُم مِّنۡهَا وَمِن كُلِّ كَرۡبٖ ثُمَّ أَنتُمۡ تُشۡرِكُونَ", "Quli Allahu yunajjeekum minha wamin kulli karbin thumma antum tushrikoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 65, "Say, ""He is the [one] Able to send upon you affliction from above you or from beneath your feet or to confuse you [so you become] sects and make you taste the violence of one another."" Look how We diversify the signs that they might understand", "قُلۡ هُوَ ٱلۡقَادِرُ عَلَىٰٓ أَن يَبۡعَثَ عَلَيۡكُمۡ عَذَابٗا مِّن فَوۡقِكُمۡ أَوۡ مِن تَحۡتِ أَرۡجُلِكُمۡ أَوۡ يَلۡبِسَكُمۡ شِيَعٗا وَيُذِيقَ بَعۡضَكُم بَأۡسَ بَعۡضٍۗ ٱنظُرۡ كَيۡفَ نُصَرِّفُ ٱلۡأٓيَٰتِ لَعَلَّهُمۡ يَفۡقَهُونَ", "Qul huwa alqadiru AAala an yabAAatha AAalaykum AAathaban min fawqikum aw min tahti arjulikum aw yalbisakum shiyaAAan wayutheeqa baAAdakum basa baAAdin onthur kayfa nusarrifu alayati laAAallahum yafqahoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 66, "But your people have denied it while it is the truth. Say, ""I am not over you a manager", "وَكَذَّبَ بِهِۦ قَوۡمُكَ وَهُوَ ٱلۡحَقُّۚ قُل لَّسۡتُ عَلَيۡكُم بِوَكِيلٖ", "Wakaththaba bihi qawmuka wahuwa alhaqqu qul lastu AAalaykum biwakeelin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 67, "For every happening is a finality; and you are going to know", "لِّكُلِّ نَبَإٖ مُّسۡتَقَرّٞۚ وَسَوۡفَ تَعۡلَمُونَ", "Likulli nabain mustaqarrun wasawfa taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 68, "And when you see those who engage in [offensive] discourse concerning Our verses, then turn away from them until they enter into another conversation. And if Satan should cause you to forget, then do not remain after the reminder with the wrongdoing people", "وَإِذَا رَأَيۡتَ ٱلَّذِينَ يَخُوضُونَ فِيٓ ءَايَٰتِنَا فَأَعۡرِضۡ عَنۡهُمۡ حَتَّىٰ يَخُوضُواْ فِي حَدِيثٍ غَيۡرِهِۦۚ وَإِمَّا يُنسِيَنَّكَ ٱلشَّيۡطَٰنُ فَلَا تَقۡعُدۡ بَعۡدَ ٱلذِّكۡرَىٰ مَعَ ٱلۡقَوۡمِ ٱلظَّـٰلِمِينَ", "Waitha raayta allatheena yakhoodoona fee ayatina faaAArid AAanhum hatta yakhoodoo fee hadeethin ghayrihi waimma yunsiyannaka alshshaytanu fala taqAAud baAAda alththikra maAAa alqawmi alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 69, "And those who fear Allah are not held accountable for the disbelievers at all, but [only for] a reminder - that perhaps they will fear Him", "وَمَا عَلَى ٱلَّذِينَ يَتَّقُونَ مِنۡ حِسَابِهِم مِّن شَيۡءٖ وَلَٰكِن ذِكۡرَىٰ لَعَلَّهُمۡ يَتَّقُونَ", "Wama AAala allatheena yattaqoona min hisabihim min shayin walakin thikra laAAallahum yattaqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 70, "And leave those who take their religion as amusement and diversion and whom the worldly life has deluded. But remind with the Qur'an, lest a soul be given up to destruction for what it earned; it will have other than Allah no protector and no intercessor. And if it should offer every compensation, it would not be taken from it. Those are the ones who are given to destruction for what they have earned. For them will be a drink of scalding water and a painful punishment because they used to disbelieve", "وَذَرِ ٱلَّذِينَ ٱتَّخَذُواْ دِينَهُمۡ لَعِبٗا وَلَهۡوٗا وَغَرَّتۡهُمُ ٱلۡحَيَوٰةُ ٱلدُّنۡيَاۚ وَذَكِّرۡ بِهِۦٓ أَن تُبۡسَلَ نَفۡسُۢ بِمَا كَسَبَتۡ لَيۡسَ لَهَا مِن دُونِ ٱللَّهِ وَلِيّٞ وَلَا شَفِيعٞ وَإِن تَعۡدِلۡ كُلَّ عَدۡلٖ لَّا يُؤۡخَذۡ مِنۡهَآۗ أُوْلَـٰٓئِكَ ٱلَّذِينَ أُبۡسِلُواْ بِمَا كَسَبُواْۖ لَهُمۡ شَرَابٞ مِّنۡ حَمِيمٖ وَعَذَابٌ أَلِيمُۢ بِمَا كَانُواْ يَكۡفُرُونَ", "Wathari allatheena ittakhathoo deenahum laAAiban walahwan wagharrathumu alhayatu alddunya wathakkir bihi an tubsala nafsun bima kasabat laysa laha min dooni Allahi waliyyun wala shafeeAAun wain taAAdil kulla AAadlin la yukhath minha olaika allatheena obsiloo bima kasaboo lahum sharabun min hameemin waAAathabun aleemun bima kanoo yakfuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 71, "Say, ""Shall we invoke instead of Allah that which neither benefits us nor harms us and be turned back on our heels after Allah has guided us? [We would then be] like one whom the devils enticed [to wander] upon the earth confused, [while] he has companions inviting him to guidance, [calling], 'Come to us.' "" Say, ""Indeed, the guidance of Allah is the [only] guidance; and we have been commanded to submit to the Lord of the worlds", "قُلۡ أَنَدۡعُواْ مِن دُونِ ٱللَّهِ مَا لَا يَنفَعُنَا وَلَا يَضُرُّنَا وَنُرَدُّ عَلَىٰٓ أَعۡقَابِنَا بَعۡدَ إِذۡ هَدَىٰنَا ٱللَّهُ كَٱلَّذِي ٱسۡتَهۡوَتۡهُ ٱلشَّيَٰطِينُ فِي ٱلۡأَرۡضِ حَيۡرَانَ لَهُۥٓ أَصۡحَٰبٞ يَدۡعُونَهُۥٓ إِلَى ٱلۡهُدَى ٱئۡتِنَاۗ قُلۡ إِنَّ هُدَى ٱللَّهِ هُوَ ٱلۡهُدَىٰۖ وَأُمِرۡنَا لِنُسۡلِمَ لِرَبِّ ٱلۡعَٰلَمِينَ", "Qul anadAAoo min dooni Allahi ma la yanfaAAuna wala yadurruna wanuraddu AAala aAAqabina baAAda ith hadana Allahu kaallathee istahwathu alshshayateenu fee alardi hayrana lahu ashabun yadAAoonahu ila alhuda itina qul inna huda Allahi huwa alhuda waomirna linuslima lirabbi alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 72, "And to establish prayer and fear Him."" And it is He to whom you will be gathered", "وَأَنۡ أَقِيمُواْ ٱلصَّلَوٰةَ وَٱتَّقُوهُۚ وَهُوَ ٱلَّذِيٓ إِلَيۡهِ تُحۡشَرُونَ", "Waan aqeemoo alssalata waittaqoohu wahuwa allathee ilayhi tuhsharoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 73, "And it is He who created the heavens and earth in truth. And the day He says, ""Be,"" and it is, His word is the truth. And His is the dominion [on] the Day the Horn is blown. [He is] Knower of the unseen and the witnessed; and He is the Wise, the Acquainted", "وَهُوَ ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ بِٱلۡحَقِّۖ وَيَوۡمَ يَقُولُ كُن فَيَكُونُۚ قَوۡلُهُ ٱلۡحَقُّۚ وَلَهُ ٱلۡمُلۡكُ يَوۡمَ يُنفَخُ فِي ٱلصُّورِۚ عَٰلِمُ ٱلۡغَيۡبِ وَٱلشَّهَٰدَةِۚ وَهُوَ ٱلۡحَكِيمُ ٱلۡخَبِيرُ", "Wahuwa allathee khalaqa alssamawati waalarda bialhaqqi wayawma yaqoolu kun fayakoonu qawluhu alhaqqu walahu almulku yawma yunfakhu fee alssoori AAalimu alghaybi waalshshahadati wahuwa alhakeemu alkhabeeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 74, "And [mention, O Muhammad], when Abraham said to his father Azar, ""Do you take idols as deities? Indeed, I see you and your people to be in manifest error", "۞وَإِذۡ قَالَ إِبۡرَٰهِيمُ لِأَبِيهِ ءَازَرَ أَتَتَّخِذُ أَصۡنَامًا ءَالِهَةً إِنِّيٓ أَرَىٰكَ وَقَوۡمَكَ فِي ضَلَٰلٖ مُّبِينٖ", "Waith qala ibraheemu liabeehi azara atattakhithu asnaman alihatan innee araka waqawmaka fee dalalin mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 75, "And thus did We show Abraham the realm of the heavens and the earth that he would be among the certain [in faith]", "وَكَذَٰلِكَ نُرِيٓ إِبۡرَٰهِيمَ مَلَكُوتَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَلِيَكُونَ مِنَ ٱلۡمُوقِنِينَ", "Wakathalika nuree ibraheema malakoota alssamawati waalardi waliyakoona mina almooqineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 76, "So when the night covered him [with darkness], he saw a star. He said, ""This is my lord."" But when it set, he said, ""I like not those that disappear", "فَلَمَّا جَنَّ عَلَيۡهِ ٱلَّيۡلُ رَءَا كَوۡكَبٗاۖ قَالَ هَٰذَا رَبِّيۖ فَلَمَّآ أَفَلَ قَالَ لَآ أُحِبُّ ٱلۡأٓفِلِينَ", "Falamma janna AAalayhi allaylu raa kawkaban qala hatha rabbee falamma afala qala la ohibbu alafileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 77, "And when he saw the moon rising, he said, ""This is my lord."" But when it set, he said, ""Unless my Lord guides me, I will surely be among the people gone astray", "فَلَمَّا رَءَا ٱلۡقَمَرَ بَازِغٗا قَالَ هَٰذَا رَبِّيۖ فَلَمَّآ أَفَلَ قَالَ لَئِن لَّمۡ يَهۡدِنِي رَبِّي لَأَكُونَنَّ مِنَ ٱلۡقَوۡمِ ٱلضَّآلِّينَ", "Falamma raa alqamara bazighan qala hatha rabbee falamma afala qala lain lam yahdinee rabbee laakoonanna mina alqawmi alddalleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 78, "And when he saw the sun rising, he said, ""This is my lord; this is greater."" But when it set, he said, ""O my people, indeed I am free from what you associate with Allah", "فَلَمَّا رَءَا ٱلشَّمۡسَ بَازِغَةٗ قَالَ هَٰذَا رَبِّي هَٰذَآ أَكۡبَرُۖ فَلَمَّآ أَفَلَتۡ قَالَ يَٰقَوۡمِ إِنِّي بَرِيٓءٞ مِّمَّا تُشۡرِكُونَ", "Falamma raa alshshamsa bazighatan qala hatha rabbee hatha akbaru falamma afalat qala ya qawmi innee bareeon mimma tushrikoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 79, "Indeed, I have turned my face toward He who created the heavens and the earth, inclining toward truth, and I am not of those who associate others with Allah", "إِنِّي وَجَّهۡتُ وَجۡهِيَ لِلَّذِي فَطَرَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ حَنِيفٗاۖ وَمَآ أَنَا۠ مِنَ ٱلۡمُشۡرِكِينَ", "Innee wajjahtu wajhiya lillathee fatara alssamawati waalarda haneefan wama ana mina almushrikeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 80, "And his people argued with him. He said, ""Do you argue with me concerning Allah while He has guided me? And I fear not what you associate with Him [and will not be harmed] unless my Lord should will something. My Lord encompasses all things in knowledge; then will you not remember", "وَحَآجَّهُۥ قَوۡمُهُۥۚ قَالَ أَتُحَـٰٓجُّوٓنِّي فِي ٱللَّهِ وَقَدۡ هَدَىٰنِۚ وَلَآ أَخَافُ مَا تُشۡرِكُونَ بِهِۦٓ إِلَّآ أَن يَشَآءَ رَبِّي شَيۡـٔٗاۚ وَسِعَ رَبِّي كُلَّ شَيۡءٍ عِلۡمًاۚ أَفَلَا تَتَذَكَّرُونَ", "Wahajjahu qawmuhu qala atuhajjoonnee fee Allahi waqad hadani wala akhafu ma tushrikoona bihi illa an yashaa rabbee shayan wasiAAa rabbee kulla shayin AAilman afala tatathakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 81, "And how should I fear what you associate while you do not fear that you have associated with Allah that for which He has not sent down to you any authority? So which of the two parties has more right to security, if you should know", "وَكَيۡفَ أَخَافُ مَآ أَشۡرَكۡتُمۡ وَلَا تَخَافُونَ أَنَّكُمۡ أَشۡرَكۡتُم بِٱللَّهِ مَا لَمۡ يُنَزِّلۡ بِهِۦ عَلَيۡكُمۡ سُلۡطَٰنٗاۚ فَأَيُّ ٱلۡفَرِيقَيۡنِ أَحَقُّ بِٱلۡأَمۡنِۖ إِن كُنتُمۡ تَعۡلَمُونَ", "Wakayfa akhafu ma ashraktum wala takhafoona annakum ashraktum biAllahi ma lam yunazzil bihi AAalaykum sultanan faayyu alfareeqayni ahaqqu bialamni in kuntum taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 82, "They who believe and do not mix their belief with injustice - those will have security, and they are [rightly] guided", "ٱلَّذِينَ ءَامَنُواْ وَلَمۡ يَلۡبِسُوٓاْ إِيمَٰنَهُم بِظُلۡمٍ أُوْلَـٰٓئِكَ لَهُمُ ٱلۡأَمۡنُ وَهُم مُّهۡتَدُونَ", "Allatheena amanoo walam yalbisoo eemanahum bithulmin olaika lahumu alamnu wahum muhtadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 83, "And that was Our [conclusive] argument which We gave Abraham against his people. We raise by degrees whom We will. Indeed, your Lord is Wise and Knowing", "وَتِلۡكَ حُجَّتُنَآ ءَاتَيۡنَٰهَآ إِبۡرَٰهِيمَ عَلَىٰ قَوۡمِهِۦۚ نَرۡفَعُ دَرَجَٰتٖ مَّن نَّشَآءُۗ إِنَّ رَبَّكَ حَكِيمٌ عَلِيمٞ", "Watilka hujjatuna ataynaha ibraheema AAala qawmihi narfaAAu darajatin man nashao inna rabbaka hakeemun AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 84, "And We gave to Abraham, Isaac and Jacob - all [of them] We guided. And Noah, We guided before; and among his descendants, David and Solomon and Job and Joseph and Moses and Aaron. Thus do We reward the doers of good", "وَوَهَبۡنَا لَهُۥٓ إِسۡحَٰقَ وَيَعۡقُوبَۚ كُلًّا هَدَيۡنَاۚ وَنُوحًا هَدَيۡنَا مِن قَبۡلُۖ وَمِن ذُرِّيَّتِهِۦ دَاوُۥدَ وَسُلَيۡمَٰنَ وَأَيُّوبَ وَيُوسُفَ وَمُوسَىٰ وَهَٰرُونَۚ وَكَذَٰلِكَ نَجۡزِي ٱلۡمُحۡسِنِينَ", "Wawahabna lahu ishaqa wayaAAqooba kullan hadayna wanoohan hadayna min qablu wamin thurriyyatihi dawooda wasulaymana waayyooba wayoosufa wamoosa waharoona wakathalika najzee almuhsineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 85, "And Zechariah and John and Jesus and Elias - and all were of the righteous", "وَزَكَرِيَّا وَيَحۡيَىٰ وَعِيسَىٰ وَإِلۡيَاسَۖ كُلّٞ مِّنَ ٱلصَّـٰلِحِينَ", "Wazakariyya wayahya waAAeesa wailyasa kullun mina alssaliheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 86, "And Ishmael and Elisha and Jonah and Lot - and all [of them] We preferred over the worlds", "وَإِسۡمَٰعِيلَ وَٱلۡيَسَعَ وَيُونُسَ وَلُوطٗاۚ وَكُلّٗا فَضَّلۡنَا عَلَى ٱلۡعَٰلَمِينَ", "WaismaAAeela wailyasaAAa wayoonusa walootan wakullan faddalna AAala alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 87, "And [some] among their fathers and their descendants and their brothers - and We chose them and We guided them to a straight path", "وَمِنۡ ءَابَآئِهِمۡ وَذُرِّيَّـٰتِهِمۡ وَإِخۡوَٰنِهِمۡۖ وَٱجۡتَبَيۡنَٰهُمۡ وَهَدَيۡنَٰهُمۡ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ", "Wamin abaihim wathurriyyatihim waikhwanihim waijtabaynahum wahadaynahum ila siratin mustaqeemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 88, "That is the guidance of Allah by which He guides whomever He wills of His servants. But if they had associated others with Allah, then worthless for them would be whatever they were doing", "ذَٰلِكَ هُدَى ٱللَّهِ يَهۡدِي بِهِۦ مَن يَشَآءُ مِنۡ عِبَادِهِۦۚ وَلَوۡ أَشۡرَكُواْ لَحَبِطَ عَنۡهُم مَّا كَانُواْ يَعۡمَلُونَ", "Thalika huda Allahi yahdee bihi man yashao min AAibadihi walaw ashrakoo lahabita AAanhum ma kanoo yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 89, "Those are the ones to whom We gave the Scripture and authority and prophethood. But if the disbelievers deny it, then We have entrusted it to a people who are not therein disbelievers", "أُوْلَـٰٓئِكَ ٱلَّذِينَ ءَاتَيۡنَٰهُمُ ٱلۡكِتَٰبَ وَٱلۡحُكۡمَ وَٱلنُّبُوَّةَۚ فَإِن يَكۡفُرۡ بِهَا هَـٰٓؤُلَآءِ فَقَدۡ وَكَّلۡنَا بِهَا قَوۡمٗا لَّيۡسُواْ بِهَا بِكَٰفِرِينَ", "Olaika allatheena ataynahumu alkitaba waalhukma waalnnubuwwata fain yakfur biha haolai faqad wakkalna biha qawman laysoo biha bikafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 90, "Those are the ones whom Allah has guided, so from their guidance take an example. Say, ""I ask of you for this message no payment. It is not but a reminder for the worlds", "أُوْلَـٰٓئِكَ ٱلَّذِينَ هَدَى ٱللَّهُۖ فَبِهُدَىٰهُمُ ٱقۡتَدِهۡۗ قُل لَّآ أَسۡـَٔلُكُمۡ عَلَيۡهِ أَجۡرًاۖ إِنۡ هُوَ إِلَّا ذِكۡرَىٰ لِلۡعَٰلَمِينَ", "Olaika allatheena hada Allahu fabihudahumu iqtadih qul la asalukum AAalayhi ajran in huwa illa thikra lilAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 91, "And they did not appraise Allah with true appraisal when they said, ""Allah did not reveal to a human being anything."" Say, ""Who revealed the Scripture that Moses brought as light and guidance to the people? You [Jews] make it into pages, disclosing [some of] it and concealing much. And you were taught that which you knew not - neither you nor your fathers."" Say, ""Allah [revealed it]."" Then leave them in their [empty] discourse, amusing themselves", "وَمَا قَدَرُواْ ٱللَّهَ حَقَّ قَدۡرِهِۦٓ إِذۡ قَالُواْ مَآ أَنزَلَ ٱللَّهُ عَلَىٰ بَشَرٖ مِّن شَيۡءٖۗ قُلۡ مَنۡ أَنزَلَ ٱلۡكِتَٰبَ ٱلَّذِي جَآءَ بِهِۦ مُوسَىٰ نُورٗا وَهُدٗى لِّلنَّاسِۖ تَجۡعَلُونَهُۥ قَرَاطِيسَ تُبۡدُونَهَا وَتُخۡفُونَ كَثِيرٗاۖ وَعُلِّمۡتُم مَّا لَمۡ تَعۡلَمُوٓاْ أَنتُمۡ وَلَآ ءَابَآؤُكُمۡۖ قُلِ ٱللَّهُۖ ثُمَّ ذَرۡهُمۡ فِي خَوۡضِهِمۡ يَلۡعَبُونَ", "Wama qadaroo Allaha haqqa qadrihi ith qaloo ma anzala Allahu AAala basharin min shayin qul man anzala alkitaba allathee jaa bihi moosa nooran wahudan lilnnasi tajAAaloonahu qarateesa tubdoonaha watukhfoona katheeran waAAullimtum ma lam taAAlamoo antum wala abaokum quli Allahu thumma tharhum fee khawdihim yalAAaboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 92, "And this is a Book which We have sent down, blessed and confirming what was before it, that you may warn the Mother of Cities and those around it. Those who believe in the Hereafter believe in it, and they are maintaining their prayers", "وَهَٰذَا كِتَٰبٌ أَنزَلۡنَٰهُ مُبَارَكٞ مُّصَدِّقُ ٱلَّذِي بَيۡنَ يَدَيۡهِ وَلِتُنذِرَ أُمَّ ٱلۡقُرَىٰ وَمَنۡ حَوۡلَهَاۚ وَٱلَّذِينَ يُؤۡمِنُونَ بِٱلۡأٓخِرَةِ يُؤۡمِنُونَ بِهِۦۖ وَهُمۡ عَلَىٰ صَلَاتِهِمۡ يُحَافِظُونَ", "Wahatha kitabun anzalnahu mubarakun musaddiqu allathee bayna yadayhi walitunthira omma alqura waman hawlaha waallatheena yuminoona bialakhirati yuminoona bihi wahum AAala salatihim yuhafithoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 93, "And who is more unjust than one who invents a lie about Allah or says, ""It has been inspired to me,"" while nothing has been inspired to him, and one who says, ""I will reveal [something] like what Allah revealed."" And if you could but see when the wrongdoers are in the overwhelming pangs of death while the angels extend their hands, [saying], ""Discharge your souls! Today you will be awarded the punishment of [extreme] humiliation for what you used to say against Allah other than the truth and [that] you were, toward His verses, being arrogant", "وَمَنۡ أَظۡلَمُ مِمَّنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبًا أَوۡ قَالَ أُوحِيَ إِلَيَّ وَلَمۡ يُوحَ إِلَيۡهِ شَيۡءٞ وَمَن قَالَ سَأُنزِلُ مِثۡلَ مَآ أَنزَلَ ٱللَّهُۗ وَلَوۡ تَرَىٰٓ إِذِ ٱلظَّـٰلِمُونَ فِي غَمَرَٰتِ ٱلۡمَوۡتِ وَٱلۡمَلَـٰٓئِكَةُ بَاسِطُوٓاْ أَيۡدِيهِمۡ أَخۡرِجُوٓاْ أَنفُسَكُمُۖ ٱلۡيَوۡمَ تُجۡزَوۡنَ عَذَابَ ٱلۡهُونِ بِمَا كُنتُمۡ تَقُولُونَ عَلَى ٱللَّهِ غَيۡرَ ٱلۡحَقِّ وَكُنتُمۡ عَنۡ ءَايَٰتِهِۦ تَسۡتَكۡبِرُونَ", "Waman athlamu mimmani iftara AAala Allahi kathiban aw qala oohiya ilayya walam yooha ilayhi shayon waman qala saonzilu mithla ma anzala Allahu walaw tara ithi alththalimoona fee ghamarati almawti waalmalaikatu basitoo aydeehim akhrijoo anfusakumu alyawma tujzawna AAathaba alhooni bima kuntum taqooloona AAala Allahi ghayra alhaqqi wakuntum AAan ayatihi tastakbiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 94, "[It will be said to them], ""And you have certainly come to Us alone as We created you the first time, and you have left whatever We bestowed upon you behind you. And We do not see with you your 'intercessors' which you claimed that they were among you associates [of Allah]. It has [all] been severed between you, and lost from you is what you used to claim", "وَلَقَدۡ جِئۡتُمُونَا فُرَٰدَىٰ كَمَا خَلَقۡنَٰكُمۡ أَوَّلَ مَرَّةٖ وَتَرَكۡتُم مَّا خَوَّلۡنَٰكُمۡ وَرَآءَ ظُهُورِكُمۡۖ وَمَا نَرَىٰ مَعَكُمۡ شُفَعَآءَكُمُ ٱلَّذِينَ زَعَمۡتُمۡ أَنَّهُمۡ فِيكُمۡ شُرَكَـٰٓؤُاْۚ لَقَد تَّقَطَّعَ بَيۡنَكُمۡ وَضَلَّ عَنكُم مَّا كُنتُمۡ تَزۡعُمُونَ", "Walaqad jitumoona furada kama khalaqnakum awwala marratin wataraktum ma khawwalnakum waraa thuhoorikum wama nara maAAakum shufaAAaakumu allatheena zaAAamtum annahum feekum shurakao laqad taqattaAAa baynakum wadalla AAankum ma kuntum tazAAumoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 95, "Indeed, Allah is the cleaver of grain and date seeds. He brings the living out of the dead and brings the dead out of the living. That is Allah; so how are you deluded", "۞إِنَّ ٱللَّهَ فَالِقُ ٱلۡحَبِّ وَٱلنَّوَىٰۖ يُخۡرِجُ ٱلۡحَيَّ مِنَ ٱلۡمَيِّتِ وَمُخۡرِجُ ٱلۡمَيِّتِ مِنَ ٱلۡحَيِّۚ ذَٰلِكُمُ ٱللَّهُۖ فَأَنَّىٰ تُؤۡفَكُونَ", "Inna Allaha faliqu alhabbi waalnnawa yukhriju alhayya mina almayyiti wamukhriju almayyiti mina alhayyi thalikumu Allahu faanna tufakoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 96, "[He is] the cleaver of daybreak and has made the night for rest and the sun and moon for calculation. That is the determination of the Exalted in Might, the Knowing", "فَالِقُ ٱلۡإِصۡبَاحِ وَجَعَلَ ٱلَّيۡلَ سَكَنٗا وَٱلشَّمۡسَ وَٱلۡقَمَرَ حُسۡبَانٗاۚ ذَٰلِكَ تَقۡدِيرُ ٱلۡعَزِيزِ ٱلۡعَلِيمِ", "Faliqu alisbahi wajaAAala allayla sakanan waalshshamsa waalqamara husbanan thalika taqdeeru alAAazeezi alAAaleemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 97, "And it is He who placed for you the stars that you may be guided by them through the darknesses of the land and sea. We have detailed the signs for a people who know", "وَهُوَ ٱلَّذِي جَعَلَ لَكُمُ ٱلنُّجُومَ لِتَهۡتَدُواْ بِهَا فِي ظُلُمَٰتِ ٱلۡبَرِّ وَٱلۡبَحۡرِۗ قَدۡ فَصَّلۡنَا ٱلۡأٓيَٰتِ لِقَوۡمٖ يَعۡلَمُونَ", "Wahuwa allathee jaAAala lakumu alnnujooma litahtadoo biha fee thulumati albarri waalbahri qad fassalna alayati liqawmin yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 98, "And it is He who produced you from one soul and [gave you] a place of dwelling and of storage. We have detailed the signs for a people who understand", "وَهُوَ ٱلَّذِيٓ أَنشَأَكُم مِّن نَّفۡسٖ وَٰحِدَةٖ فَمُسۡتَقَرّٞ وَمُسۡتَوۡدَعٞۗ قَدۡ فَصَّلۡنَا ٱلۡأٓيَٰتِ لِقَوۡمٖ يَفۡقَهُونَ", "Wahuwa allathee anshaakum min nafsin wahidatin famustaqarrun wamustawdaAAun qad fassalna alayati liqawmin yafqahoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 99, "And it is He who sends down rain from the sky, and We produce thereby the growth of all things. We produce from it greenery from which We produce grains arranged in layers. And from the palm trees - of its emerging fruit are clusters hanging low. And [We produce] gardens of grapevines and olives and pomegranates, similar yet varied. Look at [each of] its fruit when it yields and [at] its ripening. Indeed in that are signs for a people who believe", "وَهُوَ ٱلَّذِيٓ أَنزَلَ مِنَ ٱلسَّمَآءِ مَآءٗ فَأَخۡرَجۡنَا بِهِۦ نَبَاتَ كُلِّ شَيۡءٖ فَأَخۡرَجۡنَا مِنۡهُ خَضِرٗا نُّخۡرِجُ مِنۡهُ حَبّٗا مُّتَرَاكِبٗا وَمِنَ ٱلنَّخۡلِ مِن طَلۡعِهَا قِنۡوَانٞ دَانِيَةٞ وَجَنَّـٰتٖ مِّنۡ أَعۡنَابٖ وَٱلزَّيۡتُونَ وَٱلرُّمَّانَ مُشۡتَبِهٗا وَغَيۡرَ مُتَشَٰبِهٍۗ ٱنظُرُوٓاْ إِلَىٰ ثَمَرِهِۦٓ إِذَآ أَثۡمَرَ وَيَنۡعِهِۦٓۚ إِنَّ فِي ذَٰلِكُمۡ لَأٓيَٰتٖ لِّقَوۡمٖ يُؤۡمِنُونَ", "Wahuwa allathee anzala mina alssamai maan faakhrajna bihi nabata kulli shayin faakhrajna minhu khadiran nukhriju minhu habban mutarakiban wamina alnnakhli min talAAiha qinwanun daniyatun wajannatin min aAAnabin waalzzaytoona waalrrummana mushtabihan waghayra mutashabihin onthuroo ila thamarihi itha athmara wayanAAihi inna fee thalikum laayatin liqawmin yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 100, "But they have attributed to Allah partners - the jinn, while He has created them - and have fabricated for Him sons and daughters. Exalted is He and high above what they describe", "وَجَعَلُواْ لِلَّهِ شُرَكَآءَ ٱلۡجِنَّ وَخَلَقَهُمۡۖ وَخَرَقُواْ لَهُۥ بَنِينَ وَبَنَٰتِۭ بِغَيۡرِ عِلۡمٖۚ سُبۡحَٰنَهُۥ وَتَعَٰلَىٰ عَمَّا يَصِفُونَ", "WajaAAaloo lillahi shurakaa aljinna wakhalaqahum wakharaqoo lahu baneena wabanatin bighayri AAilmin subhanahu wataAAala AAamma yasifoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 101, "[He is] Originator of the heavens and the earth. How could He have a son when He does not have a companion and He created all things? And He is, of all things, Knowing", "بَدِيعُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ أَنَّىٰ يَكُونُ لَهُۥ وَلَدٞ وَلَمۡ تَكُن لَّهُۥ صَٰحِبَةٞۖ وَخَلَقَ كُلَّ شَيۡءٖۖ وَهُوَ بِكُلِّ شَيۡءٍ عَلِيمٞ", "BadeeAAu alssamawati waalardi anna yakoonu lahu waladun walam takun lahu sahibatun wakhalaqa kulla shayin wahuwa bikulli shayin AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 102, "That is Allah, your Lord; there is no deity except Him, the Creator of all things, so worship Him. And He is Disposer of all things", "ذَٰلِكُمُ ٱللَّهُ رَبُّكُمۡۖ لَآ إِلَٰهَ إِلَّا هُوَۖ خَٰلِقُ كُلِّ شَيۡءٖ فَٱعۡبُدُوهُۚ وَهُوَ عَلَىٰ كُلِّ شَيۡءٖ وَكِيلٞ", "Thalikumu Allahu rabbukum la ilaha illa huwa khaliqu kulli shayin faoAAbudoohu wahuwa AAala kulli shayin wakeelun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 103, "Vision perceives Him not, but He perceives [all] vision; and He is the Subtle, the Acquainted", "لَّا تُدۡرِكُهُ ٱلۡأَبۡصَٰرُ وَهُوَ يُدۡرِكُ ٱلۡأَبۡصَٰرَۖ وَهُوَ ٱللَّطِيفُ ٱلۡخَبِيرُ", "La tudrikuhu alabsaru wahuwa yudriku alabsara wahuwa allateefu alkhabeeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 104, "There has come to you enlightenment from your Lord. So whoever will see does so for [the benefit of] his soul, and whoever is blind [does harm] against it. And [say], ""I am not a guardian over you", "قَدۡ جَآءَكُم بَصَآئِرُ مِن رَّبِّكُمۡۖ فَمَنۡ أَبۡصَرَ فَلِنَفۡسِهِۦۖ وَمَنۡ عَمِيَ فَعَلَيۡهَاۚ وَمَآ أَنَا۠ عَلَيۡكُم بِحَفِيظٖ", "Qad jaakum basairu min rabbikum faman absara falinafsihi waman AAamiya faAAalayha wama ana AAalaykum bihafeethin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 105, "And thus do We diversify the verses so the disbelievers will say, ""You have studied,"" and so We may make the Qur'an clear for a people who know", "وَكَذَٰلِكَ نُصَرِّفُ ٱلۡأٓيَٰتِ وَلِيَقُولُواْ دَرَسۡتَ وَلِنُبَيِّنَهُۥ لِقَوۡمٖ يَعۡلَمُونَ", "Wakathalika nusarrifu alayati waliyaqooloo darasta walinubayyinahu liqawmin yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 106, "Follow, [O Muhammad], what has been revealed to you from your Lord - there is no deity except Him - and turn away from those who associate others with Allah", "ٱتَّبِعۡ مَآ أُوحِيَ إِلَيۡكَ مِن رَّبِّكَۖ لَآ إِلَٰهَ إِلَّا هُوَۖ وَأَعۡرِضۡ عَنِ ٱلۡمُشۡرِكِينَ", "IttabiAA ma oohiya ilayka min rabbika la ilaha illa huwa waaAArid AAani almushrikeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 107, "But if Allah had willed, they would not have associated. And We have not appointed you over them as a guardian, nor are you a manager over them", "وَلَوۡ شَآءَ ٱللَّهُ مَآ أَشۡرَكُواْۗ وَمَا جَعَلۡنَٰكَ عَلَيۡهِمۡ حَفِيظٗاۖ وَمَآ أَنتَ عَلَيۡهِم بِوَكِيلٖ", "Walaw shaa Allahu ma ashrakoo wama jaAAalnaka AAalayhim hafeethan wama anta AAalayhim biwakeelin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 108, "And do not insult those they invoke other than Allah, lest they insult Allah in enmity without knowledge. Thus We have made pleasing to every community their deeds. Then to their Lord is their return, and He will inform them about what they used to do", "وَلَا تَسُبُّواْ ٱلَّذِينَ يَدۡعُونَ مِن دُونِ ٱللَّهِ فَيَسُبُّواْ ٱللَّهَ عَدۡوَۢا بِغَيۡرِ عِلۡمٖۗ كَذَٰلِكَ زَيَّنَّا لِكُلِّ أُمَّةٍ عَمَلَهُمۡ ثُمَّ إِلَىٰ رَبِّهِم مَّرۡجِعُهُمۡ فَيُنَبِّئُهُم بِمَا كَانُواْ يَعۡمَلُونَ", "Wala tasubboo allatheena yadAAoona min dooni Allahi fayasubboo Allaha AAadwan bighayri AAilmin kathalika zayyanna likulli ommatin AAamalahum thumma ila rabbihim marjiAAuhum fayunabbiohum bima kanoo yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 109, "And they swear by Allah their strongest oaths that if a sign came to them, they would surely believe in it. Say, ""The signs are only with Allah."" And what will make you perceive that even if a sign came, they would not believe", "وَأَقۡسَمُواْ بِٱللَّهِ جَهۡدَ أَيۡمَٰنِهِمۡ لَئِن جَآءَتۡهُمۡ ءَايَةٞ لَّيُؤۡمِنُنَّ بِهَاۚ قُلۡ إِنَّمَا ٱلۡأٓيَٰتُ عِندَ ٱللَّهِۖ وَمَا يُشۡعِرُكُمۡ أَنَّهَآ إِذَا جَآءَتۡ لَا يُؤۡمِنُونَ", "Waaqsamoo biAllahi jahda aymanihim lain jaathum ayatun layuminunna biha qul innama alayatu AAinda Allahi wama yushAAirukum annaha itha jaat la yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 110, "And We will turn away their hearts and their eyes just as they refused to believe in it the first time. And We will leave them in their transgression, wandering blindly", "وَنُقَلِّبُ أَفۡـِٔدَتَهُمۡ وَأَبۡصَٰرَهُمۡ كَمَا لَمۡ يُؤۡمِنُواْ بِهِۦٓ أَوَّلَ مَرَّةٖ وَنَذَرُهُمۡ فِي طُغۡيَٰنِهِمۡ يَعۡمَهُونَ", "Wanuqallibu afidatahum waabsarahum kama lam yuminoo bihi awwala marratin wanatharuhum fee tughyanihim yaAAmahoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 111, "And even if We had sent down to them the angels [with the message] and the dead spoke to them [of it] and We gathered together every [created] thing in front of them, they would not believe unless Allah should will. But most of them, [of that], are ignorant", "۞وَلَوۡ أَنَّنَا نَزَّلۡنَآ إِلَيۡهِمُ ٱلۡمَلَـٰٓئِكَةَ وَكَلَّمَهُمُ ٱلۡمَوۡتَىٰ وَحَشَرۡنَا عَلَيۡهِمۡ كُلَّ شَيۡءٖ قُبُلٗا مَّا كَانُواْ لِيُؤۡمِنُوٓاْ إِلَّآ أَن يَشَآءَ ٱللَّهُ وَلَٰكِنَّ أَكۡثَرَهُمۡ يَجۡهَلُونَ", "Walaw annana nazzalna ilayhimu almalaikata wakallamahumu almawta wahasharna AAalayhim kulla shayin qubulan ma kanoo liyuminoo illa an yashaa Allahu walakinna aktharahum yajhaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 112, "And thus We have made for every prophet an enemy - devils from mankind and jinn, inspiring to one another decorative speech in delusion. But if your Lord had willed, they would not have done it, so leave them and that which they invent", "وَكَذَٰلِكَ جَعَلۡنَا لِكُلِّ نَبِيٍّ عَدُوّٗا شَيَٰطِينَ ٱلۡإِنسِ وَٱلۡجِنِّ يُوحِي بَعۡضُهُمۡ إِلَىٰ بَعۡضٖ زُخۡرُفَ ٱلۡقَوۡلِ غُرُورٗاۚ وَلَوۡ شَآءَ رَبُّكَ مَا فَعَلُوهُۖ فَذَرۡهُمۡ وَمَا يَفۡتَرُونَ", "Wakathalika jaAAalna likulli nabiyyin AAaduwwan shayateena alinsi waaljinni yoohee baAAduhum ila baAAdin zukhrufa alqawli ghurooran walaw shaa rabbuka ma faAAaloohu fatharhum wama yaftaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 113, "And [it is] so the hearts of those who disbelieve in the Hereafter will incline toward it and that they will be satisfied with it and that they will commit that which they are committing", "وَلِتَصۡغَىٰٓ إِلَيۡهِ أَفۡـِٔدَةُ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱلۡأٓخِرَةِ وَلِيَرۡضَوۡهُ وَلِيَقۡتَرِفُواْ مَا هُم مُّقۡتَرِفُونَ", "Walitasgha ilayhi afidatu allatheena la yuminoona bialakhirati waliyardawhu waliyaqtarifoo ma hum muqtarifoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 114, "[Say], ""Then is it other than Allah I should seek as judge while it is He who has revealed to you the Book explained in detail?"" And those to whom We [previously] gave the Scripture know that it is sent down from your Lord in truth, so never be among the doubters", "أَفَغَيۡرَ ٱللَّهِ أَبۡتَغِي حَكَمٗا وَهُوَ ٱلَّذِيٓ أَنزَلَ إِلَيۡكُمُ ٱلۡكِتَٰبَ مُفَصَّلٗاۚ وَٱلَّذِينَ ءَاتَيۡنَٰهُمُ ٱلۡكِتَٰبَ يَعۡلَمُونَ أَنَّهُۥ مُنَزَّلٞ مِّن رَّبِّكَ بِٱلۡحَقِّۖ فَلَا تَكُونَنَّ مِنَ ٱلۡمُمۡتَرِينَ", "Afaghayra Allahi abtaghee hakaman wahuwa allathee anzala ilaykumu alkitaba mufassalan waallatheena ataynahumu alkitaba yaAAlamoona annahu munazzalun min rabbika bialhaqqi fala takoonanna mina almumtareena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 115, "And the word of your Lord has been fulfilled in truth and in justice. None can alter His words, and He is the Hearing, the Knowing", "وَتَمَّتۡ كَلِمَتُ رَبِّكَ صِدۡقٗا وَعَدۡلٗاۚ لَّا مُبَدِّلَ لِكَلِمَٰتِهِۦۚ وَهُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ", "Watammat kalimatu rabbika sidqan waAAadlan la mubaddila likalimatihi wahuwa alssameeAAu alAAaleemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 116, "And if you obey most of those upon the earth, they will mislead you from the way of Allah. They follow not except assumption, and they are not but falsifying", "وَإِن تُطِعۡ أَكۡثَرَ مَن فِي ٱلۡأَرۡضِ يُضِلُّوكَ عَن سَبِيلِ ٱللَّهِۚ إِن يَتَّبِعُونَ إِلَّا ٱلظَّنَّ وَإِنۡ هُمۡ إِلَّا يَخۡرُصُونَ", "Wain tutiAA akthara man fee alardi yudillooka AAan sabeeli Allahi in yattabiAAoona illa alththanna wain hum illa yakhrusoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 117, "Indeed, your Lord is most knowing of who strays from His way, and He is most knowing of the [rightly] guided", "إِنَّ رَبَّكَ هُوَ أَعۡلَمُ مَن يَضِلُّ عَن سَبِيلِهِۦۖ وَهُوَ أَعۡلَمُ بِٱلۡمُهۡتَدِينَ", "Inna rabbaka huwa aAAlamu man yadillu AAan sabeelihi wahuwa aAAlamu bialmuhtadeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 118, "So eat of that [meat] upon which the name of Allah has been mentioned, if you are believers in His verses", "فَكُلُواْ مِمَّا ذُكِرَ ٱسۡمُ ٱللَّهِ عَلَيۡهِ إِن كُنتُم بِـَٔايَٰتِهِۦ مُؤۡمِنِينَ", "Fakuloo mimma thukira ismu Allahi AAalayhi in kuntum biayatihi mumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 119, "And why should you not eat of that upon which the name of Allah has been mentioned while He has explained in detail to you what He has forbidden you, excepting that to which you are compelled. And indeed do many lead [others] astray through their [own] inclinations without knowledge. Indeed, your Lord - He is most knowing of the transgressors", "وَمَا لَكُمۡ أَلَّا تَأۡكُلُواْ مِمَّا ذُكِرَ ٱسۡمُ ٱللَّهِ عَلَيۡهِ وَقَدۡ فَصَّلَ لَكُم مَّا حَرَّمَ عَلَيۡكُمۡ إِلَّا مَا ٱضۡطُرِرۡتُمۡ إِلَيۡهِۗ وَإِنَّ كَثِيرٗا لَّيُضِلُّونَ بِأَهۡوَآئِهِم بِغَيۡرِ عِلۡمٍۚ إِنَّ رَبَّكَ هُوَ أَعۡلَمُ بِٱلۡمُعۡتَدِينَ", "Wama lakum alla takuloo mimma thukira ismu Allahi AAalayhi waqad fassala lakum ma harrama AAalaykum illa ma idturirtum ilayhi wainna katheeran layudilloona biahwaihim bighayri AAilmin inna rabbaka huwa aAAlamu bialmuAAtadeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 120, "And leave what is apparent of sin and what is concealed thereof. Indeed, those who earn [blame for] sin will be recompensed for that which they used to commit", "وَذَرُواْ ظَٰهِرَ ٱلۡإِثۡمِ وَبَاطِنَهُۥٓۚ إِنَّ ٱلَّذِينَ يَكۡسِبُونَ ٱلۡإِثۡمَ سَيُجۡزَوۡنَ بِمَا كَانُواْ يَقۡتَرِفُونَ", "Watharoo thahira alithmi wabatinahu inna allatheena yaksiboona alithma sayujzawna bima kanoo yaqtarifoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 121, "And do not eat of that upon which the name of Allah has not been mentioned, for indeed, it is grave disobedience. And indeed do the devils inspire their allies [among men] to dispute with you. And if you were to obey them, indeed, you would be associators [of others with Him]", "وَلَا تَأۡكُلُواْ مِمَّا لَمۡ يُذۡكَرِ ٱسۡمُ ٱللَّهِ عَلَيۡهِ وَإِنَّهُۥ لَفِسۡقٞۗ وَإِنَّ ٱلشَّيَٰطِينَ لَيُوحُونَ إِلَىٰٓ أَوۡلِيَآئِهِمۡ لِيُجَٰدِلُوكُمۡۖ وَإِنۡ أَطَعۡتُمُوهُمۡ إِنَّكُمۡ لَمُشۡرِكُونَ", "Wala takuloo mimma lam yuthkari ismu Allahi AAalayhi wainnahu lafisqun wainna alshshayateena layoohoona ila awliyaihim liyujadilookum wain ataAAtumoohum innakum lamushrikoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 122, "And is one who was dead and We gave him life and made for him light by which to walk among the people like one who is in darkness, never to emerge therefrom? Thus it has been made pleasing to the disbelievers that which they were doing", "أَوَمَن كَانَ مَيۡتٗا فَأَحۡيَيۡنَٰهُ وَجَعَلۡنَا لَهُۥ نُورٗا يَمۡشِي بِهِۦ فِي ٱلنَّاسِ كَمَن مَّثَلُهُۥ فِي ٱلظُّلُمَٰتِ لَيۡسَ بِخَارِجٖ مِّنۡهَاۚ كَذَٰلِكَ زُيِّنَ لِلۡكَٰفِرِينَ مَا كَانُواْ يَعۡمَلُونَ", "Awa man kana maytan faahyaynahu wajaAAalna lahu nooran yamshee bihi fee alnnasi kaman mathaluhu fee alththulumati laysa bikharijin minha kathalika zuyyina lilkafireena ma kanoo yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 123, "And thus We have placed within every city the greatest of its criminals to conspire therein. But they conspire not except against themselves, and they perceive [it] not", "وَكَذَٰلِكَ جَعَلۡنَا فِي كُلِّ قَرۡيَةٍ أَكَٰبِرَ مُجۡرِمِيهَا لِيَمۡكُرُواْ فِيهَاۖ وَمَا يَمۡكُرُونَ إِلَّا بِأَنفُسِهِمۡ وَمَا يَشۡعُرُونَ", "Wakathalika jaAAalna fee kulli qaryatin akabira mujrimeeha liyamkuroo feeha wama yamkuroona illa bianfusihim wama yashAAuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 124, "And when a sign comes to them, they say, ""Never will we believe until we are given like that which was given to the messengers of Allah."" Allah is most knowing of where He places His message. There will afflict those who committed crimes debasement before Allah and severe punishment for what they used to conspire", "وَإِذَا جَآءَتۡهُمۡ ءَايَةٞ قَالُواْ لَن نُّؤۡمِنَ حَتَّىٰ نُؤۡتَىٰ مِثۡلَ مَآ أُوتِيَ رُسُلُ ٱللَّهِۘ ٱللَّهُ أَعۡلَمُ حَيۡثُ يَجۡعَلُ رِسَالَتَهُۥۗ سَيُصِيبُ ٱلَّذِينَ أَجۡرَمُواْ صَغَارٌ عِندَ ٱللَّهِ وَعَذَابٞ شَدِيدُۢ بِمَا كَانُواْ يَمۡكُرُونَ", "Waitha jaathum ayatun qaloo lan numina hatta nuta mithla ma ootiya rusulu Allahi Allahu aAAlamu haythu yajAAalu risalatahu sayuseebu allatheena ajramoo sagharun AAinda Allahi waAAathabun shadeedun bima kanoo yamkuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 125, "So whoever Allah wants to guide - He expands his breast to [contain] Islam; and whoever He wants to misguide - He makes his breast tight and constricted as though he were climbing into the sky. Thus does Allah place defilement upon those who do not believe", "فَمَن يُرِدِ ٱللَّهُ أَن يَهۡدِيَهُۥ يَشۡرَحۡ صَدۡرَهُۥ لِلۡإِسۡلَٰمِۖ وَمَن يُرِدۡ أَن يُضِلَّهُۥ يَجۡعَلۡ صَدۡرَهُۥ ضَيِّقًا حَرَجٗا كَأَنَّمَا يَصَّعَّدُ فِي ٱلسَّمَآءِۚ كَذَٰلِكَ يَجۡعَلُ ٱللَّهُ ٱلرِّجۡسَ عَلَى ٱلَّذِينَ لَا يُؤۡمِنُونَ", "Faman yuridi Allahu an yahdiyahu yashrah sadrahu lilislami waman yurid an yudillahu yajAAal sadrahu dayyiqan harajan kaannama yassaAAAAadu fee alssamai kathalika yajAAalu Allahu alrrijsa AAala allatheena la yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 126, "And this is the path of your Lord, [leading] straight. We have detailed the verses for a people who remember", "وَهَٰذَا صِرَٰطُ رَبِّكَ مُسۡتَقِيمٗاۗ قَدۡ فَصَّلۡنَا ٱلۡأٓيَٰتِ لِقَوۡمٖ يَذَّكَّرُونَ", "Wahatha siratu rabbika mustaqeeman qad fassalna alayati liqawmin yaththakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 127, "For them will be the Home of Peace with their Lord. And He will be their protecting friend because of what they used to do", "۞لَهُمۡ دَارُ ٱلسَّلَٰمِ عِندَ رَبِّهِمۡۖ وَهُوَ وَلِيُّهُم بِمَا كَانُواْ يَعۡمَلُونَ", "Lahum daru alssalami AAinda rabbihim wahuwa waliyyuhum bima kanoo yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 128, "And [mention, O Muhammad], the Day when He will gather them together [and say], ""O company of jinn, you have [misled] many of mankind."" And their allies among mankind will say, ""Our Lord, some of us made use of others, and we have [now] reached our term, which you appointed for us."" He will say, ""The Fire is your residence, wherein you will abide eternally, except for what Allah wills. Indeed, your Lord is Wise and Knowing", "وَيَوۡمَ يَحۡشُرُهُمۡ جَمِيعٗا يَٰمَعۡشَرَ ٱلۡجِنِّ قَدِ ٱسۡتَكۡثَرۡتُم مِّنَ ٱلۡإِنسِۖ وَقَالَ أَوۡلِيَآؤُهُم مِّنَ ٱلۡإِنسِ رَبَّنَا ٱسۡتَمۡتَعَ بَعۡضُنَا بِبَعۡضٖ وَبَلَغۡنَآ أَجَلَنَا ٱلَّذِيٓ أَجَّلۡتَ لَنَاۚ قَالَ ٱلنَّارُ مَثۡوَىٰكُمۡ خَٰلِدِينَ فِيهَآ إِلَّا مَا شَآءَ ٱللَّهُۚ إِنَّ رَبَّكَ حَكِيمٌ عَلِيمٞ", "Wayawma yahshuruhum jameeAAan ya maAAshara aljinni qadi istakthartum mina alinsi waqala awliyaohum mina alinsi rabbana istamtaAAa baAAduna bibaAAdin wabalaghna ajalana allathee ajjalta lana qala alnnaru mathwakum khalideena feeha illa ma shaa Allahu inna rabbaka hakeemun AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 129, "And thus will We make some of the wrongdoers allies of others for what they used to earn", "وَكَذَٰلِكَ نُوَلِّي بَعۡضَ ٱلظَّـٰلِمِينَ بَعۡضَۢا بِمَا كَانُواْ يَكۡسِبُونَ", "Wakathalika nuwallee baAAda alththalimeena baAAdan bima kanoo yaksiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 130, "O company of jinn and mankind, did there not come to you messengers from among you, relating to you My verses and warning you of the meeting of this Day of yours?"" They will say, ""We bear witness against ourselves""; and the worldly life had deluded them, and they will bear witness against themselves that they were disbelievers", "يَٰمَعۡشَرَ ٱلۡجِنِّ وَٱلۡإِنسِ أَلَمۡ يَأۡتِكُمۡ رُسُلٞ مِّنكُمۡ يَقُصُّونَ عَلَيۡكُمۡ ءَايَٰتِي وَيُنذِرُونَكُمۡ لِقَآءَ يَوۡمِكُمۡ هَٰذَاۚ قَالُواْ شَهِدۡنَا عَلَىٰٓ أَنفُسِنَاۖ وَغَرَّتۡهُمُ ٱلۡحَيَوٰةُ ٱلدُّنۡيَا وَشَهِدُواْ عَلَىٰٓ أَنفُسِهِمۡ أَنَّهُمۡ كَانُواْ كَٰفِرِينَ", "Ya maAAshara aljinni waalinsi alam yatikum rusulun minkum yaqussoona AAalaykum ayatee wayunthiroonakum liqaa yawmikum hatha qaloo shahidna AAala anfusina wagharrathumu alhayatu alddunya washahidoo AAala anfusihim annahum kanoo kafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 131, "That is because your Lord would not destroy the cities for wrongdoing while their people were unaware", "ذَٰلِكَ أَن لَّمۡ يَكُن رَّبُّكَ مُهۡلِكَ ٱلۡقُرَىٰ بِظُلۡمٖ وَأَهۡلُهَا غَٰفِلُونَ", "Thalika an lam yakun rabbuka muhlika alqura bithulmin waahluha ghafiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 132, "And for all are degrees from what they have done. And your Lord is not unaware of what they do", "وَلِكُلّٖ دَرَجَٰتٞ مِّمَّا عَمِلُواْۚ وَمَا رَبُّكَ بِغَٰفِلٍ عَمَّا يَعۡمَلُونَ", "Walikullin darajatun mimma AAamiloo wama rabbuka bighafilin AAamma yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 133, "And your Lord is the Free of need, the possessor of mercy. If He wills, he can do away with you and give succession after you to whomever He wills, just as He produced you from the descendants of another people", "وَرَبُّكَ ٱلۡغَنِيُّ ذُو ٱلرَّحۡمَةِۚ إِن يَشَأۡ يُذۡهِبۡكُمۡ وَيَسۡتَخۡلِفۡ مِنۢ بَعۡدِكُم مَّا يَشَآءُ كَمَآ أَنشَأَكُم مِّن ذُرِّيَّةِ قَوۡمٍ ءَاخَرِينَ", "Warabbuka alghaniyyu thoo alrrahmati in yasha yuthhibkum wayastakhlif min baAAdikum ma yashao kama anshaakum min thurriyyati qawmin akhareena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 134, "Indeed, what you are promised is coming, and you will not cause failure [to Allah]", "إِنَّ مَا تُوعَدُونَ لَأٓتٖۖ وَمَآ أَنتُم بِمُعۡجِزِينَ", "Inna ma tooAAadoona laatin wama antum bimuAAjizeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 135, "Say, ""O my people, work according to your position; [for] indeed, I am working. And you are going to know who will have succession in the home. Indeed, the wrongdoers will not succeed", "قُلۡ يَٰقَوۡمِ ٱعۡمَلُواْ عَلَىٰ مَكَانَتِكُمۡ إِنِّي عَامِلٞۖ فَسَوۡفَ تَعۡلَمُونَ مَن تَكُونُ لَهُۥ عَٰقِبَةُ ٱلدَّارِۚ إِنَّهُۥ لَا يُفۡلِحُ ٱلظَّـٰلِمُونَ", "Qul ya qawmi iAAmaloo AAala makanatikum innee AAamilun fasawfa taAAlamoona man takoonu lahu AAaqibatu alddari innahu la yuflihu alththalimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 136, "And the polytheists assign to Allah from that which He created of crops and livestock a share and say, ""This is for Allah,"" by their claim, ""and this is for our partners [associated with Him]."" But what is for their ""partners"" does not reach Allah, while what is for Allah - this reaches their ""partners."" Evil is that which they rule", "وَجَعَلُواْ لِلَّهِ مِمَّا ذَرَأَ مِنَ ٱلۡحَرۡثِ وَٱلۡأَنۡعَٰمِ نَصِيبٗا فَقَالُواْ هَٰذَا لِلَّهِ بِزَعۡمِهِمۡ وَهَٰذَا لِشُرَكَآئِنَاۖ فَمَا كَانَ لِشُرَكَآئِهِمۡ فَلَا يَصِلُ إِلَى ٱللَّهِۖ وَمَا كَانَ لِلَّهِ فَهُوَ يَصِلُ إِلَىٰ شُرَكَآئِهِمۡۗ سَآءَ مَا يَحۡكُمُونَ", "WajaAAaloo lillahi mimma tharaa mina alharthi waalanAAami naseeban faqaloo hatha lillahi bizaAAmihim wahatha lishurakaina fama kana lishurakaihim fala yasilu ila Allahi wama kana lillahi fahuwa yasilu ila shurakaihim saa ma yahkumoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 137, "And likewise, to many of the polytheists their partners have made [to seem] pleasing the killing of their children in order to bring about their destruction and to cover them with confusion in their religion. And if Allah had willed, they would not have done so. So leave them and that which they invent", "وَكَذَٰلِكَ زَيَّنَ لِكَثِيرٖ مِّنَ ٱلۡمُشۡرِكِينَ قَتۡلَ أَوۡلَٰدِهِمۡ شُرَكَآؤُهُمۡ لِيُرۡدُوهُمۡ وَلِيَلۡبِسُواْ عَلَيۡهِمۡ دِينَهُمۡۖ وَلَوۡ شَآءَ ٱللَّهُ مَا فَعَلُوهُۖ فَذَرۡهُمۡ وَمَا يَفۡتَرُونَ", "Wakathalika zayyana likatheerin mina almushrikeena qatla awladihim shurakaohum liyurdoohum waliyalbisoo AAalayhim deenahum walaw shaa Allahu ma faAAaloohu fatharhum wama yaftaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 138, "And they say, ""These animals and crops are forbidden; no one may eat from them except whom we will,"" by their claim. And there are those [camels] whose backs are forbidden [by them] and those upon which the name of Allah is not mentioned - [all of this] an invention of untruth about Him. He will punish them for what they were inventing", "وَقَالُواْ هَٰذِهِۦٓ أَنۡعَٰمٞ وَحَرۡثٌ حِجۡرٞ لَّا يَطۡعَمُهَآ إِلَّا مَن نَّشَآءُ بِزَعۡمِهِمۡ وَأَنۡعَٰمٌ حُرِّمَتۡ ظُهُورُهَا وَأَنۡعَٰمٞ لَّا يَذۡكُرُونَ ٱسۡمَ ٱللَّهِ عَلَيۡهَا ٱفۡتِرَآءً عَلَيۡهِۚ سَيَجۡزِيهِم بِمَا كَانُواْ يَفۡتَرُونَ", "Waqaloo hathihi anAAamun waharthun hijrun la yatAAamuha illa man nashao bizaAAmihim waanAAamun hurrimat thuhooruha waanAAamun la yathkuroona isma Allahi AAalayha iftiraan AAalayhi sayajzeehim bima kanoo yaftaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 139, "And they say, ""What is in the bellies of these animals is exclusively for our males and forbidden to our females. But if it is [born] dead, then all of them have shares therein."" He will punish them for their description. Indeed, He is Wise and Knowing", "وَقَالُواْ مَا فِي بُطُونِ هَٰذِهِ ٱلۡأَنۡعَٰمِ خَالِصَةٞ لِّذُكُورِنَا وَمُحَرَّمٌ عَلَىٰٓ أَزۡوَٰجِنَاۖ وَإِن يَكُن مَّيۡتَةٗ فَهُمۡ فِيهِ شُرَكَآءُۚ سَيَجۡزِيهِمۡ وَصۡفَهُمۡۚ إِنَّهُۥ حَكِيمٌ عَلِيمٞ", "Waqaloo ma fee butooni hathihi alanAAami khalisatun lithukoorina wamuharramun AAala azwajina wain yakun maytatan fahum feehi shurakao sayajzeehim wasfahum innahu hakeemun AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 140, "Those will have lost who killed their children in foolishness without knowledge and prohibited what Allah had provided for them, inventing untruth about Allah. They have gone astray and were not [rightly] guided", "قَدۡ خَسِرَ ٱلَّذِينَ قَتَلُوٓاْ أَوۡلَٰدَهُمۡ سَفَهَۢا بِغَيۡرِ عِلۡمٖ وَحَرَّمُواْ مَا رَزَقَهُمُ ٱللَّهُ ٱفۡتِرَآءً عَلَى ٱللَّهِۚ قَدۡ ضَلُّواْ وَمَا كَانُواْ مُهۡتَدِينَ", "Qad khasira allatheena qataloo awladahum safahan bighayri AAilmin waharramoo ma razaqahumu Allahu iftiraan AAala Allahi qad dalloo wama kanoo muhtadeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 141, "And He it is who causes gardens to grow, [both] trellised and untrellised, and palm trees and crops of different [kinds of] food and olives and pomegranates, similar and dissimilar. Eat of [each of] its fruit when it yields and give its due [zakah] on the day of its harvest. And be not excessive. Indeed, He does not like those who commit excess", "۞وَهُوَ ٱلَّذِيٓ أَنشَأَ جَنَّـٰتٖ مَّعۡرُوشَٰتٖ وَغَيۡرَ مَعۡرُوشَٰتٖ وَٱلنَّخۡلَ وَٱلزَّرۡعَ مُخۡتَلِفًا أُكُلُهُۥ وَٱلزَّيۡتُونَ وَٱلرُّمَّانَ مُتَشَٰبِهٗا وَغَيۡرَ مُتَشَٰبِهٖۚ كُلُواْ مِن ثَمَرِهِۦٓ إِذَآ أَثۡمَرَ وَءَاتُواْ حَقَّهُۥ يَوۡمَ حَصَادِهِۦۖ وَلَا تُسۡرِفُوٓاْۚ إِنَّهُۥ لَا يُحِبُّ ٱلۡمُسۡرِفِينَ", "Wahuwa allathee anshaa jannatin maAArooshatin waghayra maAArooshatin waalnnakhla waalzzarAAa mukhtalifan okuluhu waalzzaytoona waalrrummana mutashabihan waghayra mutashabihin kuloo min thamarihi itha athmara waatoo haqqahu yawma hasadihi wala tusrifoo innahu la yuhibbu almusrifeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 142, "And of the grazing livestock are carriers [of burdens] and those [too] small. Eat of what Allah has provided for you and do not follow the footsteps of Satan. Indeed, he is to you a clear enemy", "وَمِنَ ٱلۡأَنۡعَٰمِ حَمُولَةٗ وَفَرۡشٗاۚ كُلُواْ مِمَّا رَزَقَكُمُ ٱللَّهُ وَلَا تَتَّبِعُواْ خُطُوَٰتِ ٱلشَّيۡطَٰنِۚ إِنَّهُۥ لَكُمۡ عَدُوّٞ مُّبِينٞ", "Wamina alanAAami hamoolatan wafarshan kuloo mimma razaqakumu Allahu wala tattabiAAoo khutuwati alshshaytani innahu lakum AAaduwwun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 143, "[They are] eight mates - of the sheep, two and of the goats, two. Say, ""Is it the two males He has forbidden or the two females or that which the wombs of the two females contain? Inform me with knowledge, if you should be truthful", "ثَمَٰنِيَةَ أَزۡوَٰجٖۖ مِّنَ ٱلضَّأۡنِ ٱثۡنَيۡنِ وَمِنَ ٱلۡمَعۡزِ ٱثۡنَيۡنِۗ قُلۡ ءَآلذَّكَرَيۡنِ حَرَّمَ أَمِ ٱلۡأُنثَيَيۡنِ أَمَّا ٱشۡتَمَلَتۡ عَلَيۡهِ أَرۡحَامُ ٱلۡأُنثَيَيۡنِۖ نَبِّـُٔونِي بِعِلۡمٍ إِن كُنتُمۡ صَٰدِقِينَ", "Thamaniyata azwajin mina alddani ithnayni wamina almaAAzi ithnayni qul alththakarayni harrama ami alonthayayni amma ishtamalat AAalayhi arhamu alonthayayni nabbioonee biAAilmin in kuntum sadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 144, "And of the camels, two and of the cattle, two. Say, ""Is it the two males He has forbidden or the two females or that which the wombs of the two females contain? Or were you witnesses when Allah charged you with this? Then who is more unjust than one who invents a lie about Allah to mislead the people by [something] other than knowledge? Indeed, Allah does not guide the wrongdoing people", "وَمِنَ ٱلۡإِبِلِ ٱثۡنَيۡنِ وَمِنَ ٱلۡبَقَرِ ٱثۡنَيۡنِۗ قُلۡ ءَآلذَّكَرَيۡنِ حَرَّمَ أَمِ ٱلۡأُنثَيَيۡنِ أَمَّا ٱشۡتَمَلَتۡ عَلَيۡهِ أَرۡحَامُ ٱلۡأُنثَيَيۡنِۖ أَمۡ كُنتُمۡ شُهَدَآءَ إِذۡ وَصَّىٰكُمُ ٱللَّهُ بِهَٰذَاۚ فَمَنۡ أَظۡلَمُ مِمَّنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبٗا لِّيُضِلَّ ٱلنَّاسَ بِغَيۡرِ عِلۡمٍۚ إِنَّ ٱللَّهَ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلظَّـٰلِمِينَ", "Wamina alibili ithnayni wamina albaqari ithnayni qul alththakarayni harrama ami alonthayayni amma ishtamalat AAalayhi arhamu alonthayayni am kuntum shuhadaa ith wassakumu Allahu bihatha faman athlamu mimmani iftara AAala Allahi kathiban liyudilla alnnasa bighayri AAilmin inna Allaha la yahdee alqawma alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 145, "Say, ""I do not find within that which was revealed to me [anything] forbidden to one who would eat it unless it be a dead animal or blood spilled out or the flesh of swine - for indeed, it is impure - or it be [that slaughtered in] disobedience, dedicated to other than Allah. But whoever is forced [by necessity], neither desiring [it] nor transgressing [its limit], then indeed, your Lord is Forgiving and Merciful", "قُل لَّآ أَجِدُ فِي مَآ أُوحِيَ إِلَيَّ مُحَرَّمًا عَلَىٰ طَاعِمٖ يَطۡعَمُهُۥٓ إِلَّآ أَن يَكُونَ مَيۡتَةً أَوۡ دَمٗا مَّسۡفُوحًا أَوۡ لَحۡمَ خِنزِيرٖ فَإِنَّهُۥ رِجۡسٌ أَوۡ فِسۡقًا أُهِلَّ لِغَيۡرِ ٱللَّهِ بِهِۦۚ فَمَنِ ٱضۡطُرَّ غَيۡرَ بَاغٖ وَلَا عَادٖ فَإِنَّ رَبَّكَ غَفُورٞ رَّحِيمٞ", "Qul la ajidu fee ma oohiya ilayya muharraman AAala taAAimin yatAAamuhu illa an yakoona maytatan aw daman masfoohan aw lahma khinzeerin fainnahu rijsun aw fisqan ohilla lighayri Allahi bihi famani idturra ghayra baghin wala AAadin fainna rabbaka ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 146, "And to those who are Jews We prohibited every animal of uncloven hoof; and of the cattle and the sheep We prohibited to them their fat, except what adheres to their backs or the entrails or what is joined with bone. [By] that We repaid them for their injustice. And indeed, We are truthful", "وَعَلَى ٱلَّذِينَ هَادُواْ حَرَّمۡنَا كُلَّ ذِي ظُفُرٖۖ وَمِنَ ٱلۡبَقَرِ وَٱلۡغَنَمِ حَرَّمۡنَا عَلَيۡهِمۡ شُحُومَهُمَآ إِلَّا مَا حَمَلَتۡ ظُهُورُهُمَآ أَوِ ٱلۡحَوَايَآ أَوۡ مَا ٱخۡتَلَطَ بِعَظۡمٖۚ ذَٰلِكَ جَزَيۡنَٰهُم بِبَغۡيِهِمۡۖ وَإِنَّا لَصَٰدِقُونَ", "WaAAala allatheena hadoo harramna kulla thee thufurin wamina albaqari waalghanami harramna AAalayhim shuhoomahuma illa ma hamalat thuhooruhuma awi alhawaya aw ma ikhtalata biAAathmin thalika jazaynahum bibaghyihim wainna lasadiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 147, "So if they deny you, [O Muhammad], say, ""Your Lord is the possessor of vast mercy; but His punishment cannot be repelled from the people who are criminals", "فَإِن كَذَّبُوكَ فَقُل رَّبُّكُمۡ ذُو رَحۡمَةٖ وَٰسِعَةٖ وَلَا يُرَدُّ بَأۡسُهُۥ عَنِ ٱلۡقَوۡمِ ٱلۡمُجۡرِمِينَ", "Fain kaththabooka faqul rabbukum thoo rahmatin wasiAAatin wala yuraddu basuhu AAani alqawmi almujrimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 148, "Those who associated with Allah will say, ""If Allah had willed, we would not have associated [anything] and neither would our fathers, nor would we have prohibited anything."" Likewise did those before deny until they tasted Our punishment. Say, ""Do you have any knowledge that you can produce for us? You follow not except assumption, and you are not but falsifying", "سَيَقُولُ ٱلَّذِينَ أَشۡرَكُواْ لَوۡ شَآءَ ٱللَّهُ مَآ أَشۡرَكۡنَا وَلَآ ءَابَآؤُنَا وَلَا حَرَّمۡنَا مِن شَيۡءٖۚ كَذَٰلِكَ كَذَّبَ ٱلَّذِينَ مِن قَبۡلِهِمۡ حَتَّىٰ ذَاقُواْ بَأۡسَنَاۗ قُلۡ هَلۡ عِندَكُم مِّنۡ عِلۡمٖ فَتُخۡرِجُوهُ لَنَآۖ إِن تَتَّبِعُونَ إِلَّا ٱلظَّنَّ وَإِنۡ أَنتُمۡ إِلَّا تَخۡرُصُونَ", "Sayaqoolu allatheena ashrakoo law shaa Allahu ma ashrakna wala abaona wala harramna min shayin kathalika kaththaba allatheena min qablihim hatta thaqoo basana qul hal AAindakum min AAilmin fatukhrijoohu lana in tattabiAAoona illa alththanna wain antum illa takhrusoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 149, "Say, ""With Allah is the far-reaching argument. If He had willed, He would have guided you all", "قُلۡ فَلِلَّهِ ٱلۡحُجَّةُ ٱلۡبَٰلِغَةُۖ فَلَوۡ شَآءَ لَهَدَىٰكُمۡ أَجۡمَعِينَ", "Qul falillahi alhujjatu albalighatu falaw shaa lahadakum ajmaAAeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 150, "Say, [O Muhammad], ""Bring forward your witnesses who will testify that Allah has prohibited this."" And if they testify, do not testify with them. And do not follow the desires of those who deny Our verses and those who do not believe in the Hereafter, while they equate [others] with their Lord", "قُلۡ هَلُمَّ شُهَدَآءَكُمُ ٱلَّذِينَ يَشۡهَدُونَ أَنَّ ٱللَّهَ حَرَّمَ هَٰذَاۖ فَإِن شَهِدُواْ فَلَا تَشۡهَدۡ مَعَهُمۡۚ وَلَا تَتَّبِعۡ أَهۡوَآءَ ٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا وَٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱلۡأٓخِرَةِ وَهُم بِرَبِّهِمۡ يَعۡدِلُونَ", "Qul halumma shuhadaakumu allatheena yashhadoona anna Allaha harrama hatha fain shahidoo fala tashhad maAAahum wala tattabiAA ahwaa allatheena kaththaboo biayatina waallatheena la yuminoona bialakhirati wahum birabbihim yaAAdiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 151, "Say, ""Come, I will recite what your Lord has prohibited to you. [He commands] that you not associate anything with Him, and to parents, good treatment, and do not kill your children out of poverty; We will provide for you and them. And do not approach immoralities - what is apparent of them and what is concealed. And do not kill the soul which Allah has forbidden [to be killed] except by [legal] right. This has He instructed you that you may use reason", "۞قُلۡ تَعَالَوۡاْ أَتۡلُ مَا حَرَّمَ رَبُّكُمۡ عَلَيۡكُمۡۖ أَلَّا تُشۡرِكُواْ بِهِۦ شَيۡـٔٗاۖ وَبِٱلۡوَٰلِدَيۡنِ إِحۡسَٰنٗاۖ وَلَا تَقۡتُلُوٓاْ أَوۡلَٰدَكُم مِّنۡ إِمۡلَٰقٖ نَّحۡنُ نَرۡزُقُكُمۡ وَإِيَّاهُمۡۖ وَلَا تَقۡرَبُواْ ٱلۡفَوَٰحِشَ مَا ظَهَرَ مِنۡهَا وَمَا بَطَنَۖ وَلَا تَقۡتُلُواْ ٱلنَّفۡسَ ٱلَّتِي حَرَّمَ ٱللَّهُ إِلَّا بِٱلۡحَقِّۚ ذَٰلِكُمۡ وَصَّىٰكُم بِهِۦ لَعَلَّكُمۡ تَعۡقِلُونَ", "Qul taAAalaw atlu ma harrama rabbukum AAalaykum alla tushrikoo bihi shayan wabialwalidayni ihsanan wala taqtuloo awladakum min imlaqin nahnu narzuqukum waiyyahum wala taqraboo alfawahisha ma thahara minha wama batana wala taqtuloo alnnafsa allatee harrama Allahu illa bialhaqqi thalikum wassakum bihi laAAallakum taAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 152, "And do not approach the orphan's property except in a way that is best until he reaches maturity. And give full measure and weight in justice. We do not charge any soul except [with that within] its capacity. And when you testify, be just, even if [it concerns] a near relative. And the covenant of Allah fulfill. This has He instructed you that you may remember", "وَلَا تَقۡرَبُواْ مَالَ ٱلۡيَتِيمِ إِلَّا بِٱلَّتِي هِيَ أَحۡسَنُ حَتَّىٰ يَبۡلُغَ أَشُدَّهُۥۚ وَأَوۡفُواْ ٱلۡكَيۡلَ وَٱلۡمِيزَانَ بِٱلۡقِسۡطِۖ لَا نُكَلِّفُ نَفۡسًا إِلَّا وُسۡعَهَاۖ وَإِذَا قُلۡتُمۡ فَٱعۡدِلُواْ وَلَوۡ كَانَ ذَا قُرۡبَىٰۖ وَبِعَهۡدِ ٱللَّهِ أَوۡفُواْۚ ذَٰلِكُمۡ وَصَّىٰكُم بِهِۦ لَعَلَّكُمۡ تَذَكَّرُونَ", "Wala taqraboo mala alyateemi illa biallatee hiya ahsanu hatta yablugha ashuddahu waawfoo alkayla waalmeezana bialqisti la nukallifu nafsan illa wusAAaha waitha qultum faiAAdiloo walaw kana tha qurba wabiAAahdi Allahi awfoo thalikum wassakum bihi laAAallakum tathakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 153, "And, [moreover], this is My path, which is straight, so follow it; and do not follow [other] ways, for you will be separated from His way. This has He instructed you that you may become righteous", "وَأَنَّ هَٰذَا صِرَٰطِي مُسۡتَقِيمٗا فَٱتَّبِعُوهُۖ وَلَا تَتَّبِعُواْ ٱلسُّبُلَ فَتَفَرَّقَ بِكُمۡ عَن سَبِيلِهِۦۚ ذَٰلِكُمۡ وَصَّىٰكُم بِهِۦ لَعَلَّكُمۡ تَتَّقُونَ", "Waanna hatha siratee mustaqeeman faittabiAAoohu wala tattabiAAoo alssubula fatafarraqa bikum AAan sabeelihi thalikum wassakum bihi laAAallakum tattaqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 154, "Then We gave Moses the Scripture, making complete [Our favor] upon the one who did good and as a detailed explanation of all things and as guidance and mercy that perhaps in [the matter of] the meeting with their Lord they would believe", "ثُمَّ ءَاتَيۡنَا مُوسَى ٱلۡكِتَٰبَ تَمَامًا عَلَى ٱلَّذِيٓ أَحۡسَنَ وَتَفۡصِيلٗا لِّكُلِّ شَيۡءٖ وَهُدٗى وَرَحۡمَةٗ لَّعَلَّهُم بِلِقَآءِ رَبِّهِمۡ يُؤۡمِنُونَ", "Thumma atayna moosa alkitaba tamaman AAala allathee ahsana watafseelan likulli shayin wahudan warahmatan laAAallahum biliqai rabbihim yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 155, "And this [Qur'an] is a Book We have revealed [which is] blessed, so follow it and fear Allah that you may receive mercy", "وَهَٰذَا كِتَٰبٌ أَنزَلۡنَٰهُ مُبَارَكٞ فَٱتَّبِعُوهُ وَٱتَّقُواْ لَعَلَّكُمۡ تُرۡحَمُونَ", "Wahatha kitabun anzalnahu mubarakun faittabiAAoohu waittaqoo laAAallakum turhamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 156, "[We revealed it] lest you say, ""The Scripture was only sent down to two groups before us, but we were of their study unaware", "أَن تَقُولُوٓاْ إِنَّمَآ أُنزِلَ ٱلۡكِتَٰبُ عَلَىٰ طَآئِفَتَيۡنِ مِن قَبۡلِنَا وَإِن كُنَّا عَن دِرَاسَتِهِمۡ لَغَٰفِلِينَ", "An taqooloo innama onzila alkitabu AAala taifatayni min qablina wain kunna AAan dirasatihim laghafileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 157, "Or lest you say, ""If only the Scripture had been revealed to us, we would have been better guided than they."" So there has [now] come to you a clear evidence from your Lord and a guidance and mercy. Then who is more unjust than one who denies the verses of Allah and turns away from them? We will recompense those who turn away from Our verses with the worst of punishment for their having turned away", "أَوۡ تَقُولُواْ لَوۡ أَنَّآ أُنزِلَ عَلَيۡنَا ٱلۡكِتَٰبُ لَكُنَّآ أَهۡدَىٰ مِنۡهُمۡۚ فَقَدۡ جَآءَكُم بَيِّنَةٞ مِّن رَّبِّكُمۡ وَهُدٗى وَرَحۡمَةٞۚ فَمَنۡ أَظۡلَمُ مِمَّن كَذَّبَ بِـَٔايَٰتِ ٱللَّهِ وَصَدَفَ عَنۡهَاۗ سَنَجۡزِي ٱلَّذِينَ يَصۡدِفُونَ عَنۡ ءَايَٰتِنَا سُوٓءَ ٱلۡعَذَابِ بِمَا كَانُواْ يَصۡدِفُونَ", "Aw taqooloo law anna onzila AAalayna alkitabu lakunna ahda minhum faqad jaakum bayyinatun min rabbikum wahudan warahmatun faman athlamu mimman kaththaba biayati Allahi wasadafa AAanha sanajzee allatheena yasdifoona AAan ayatina sooa alAAathabi bima kanoo yasdifoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 158, "Do they [then] wait for anything except that the angels should come to them or your Lord should come or that there come some of the signs of your Lord? The Day that some of the signs of your Lord will come no soul will benefit from its faith as long as it had not believed before or had earned through its faith some good. Say, ""Wait. Indeed, we [also] are waiting", "هَلۡ يَنظُرُونَ إِلَّآ أَن تَأۡتِيَهُمُ ٱلۡمَلَـٰٓئِكَةُ أَوۡ يَأۡتِيَ رَبُّكَ أَوۡ يَأۡتِيَ بَعۡضُ ءَايَٰتِ رَبِّكَۗ يَوۡمَ يَأۡتِي بَعۡضُ ءَايَٰتِ رَبِّكَ لَا يَنفَعُ نَفۡسًا إِيمَٰنُهَا لَمۡ تَكُنۡ ءَامَنَتۡ مِن قَبۡلُ أَوۡ كَسَبَتۡ فِيٓ إِيمَٰنِهَا خَيۡرٗاۗ قُلِ ٱنتَظِرُوٓاْ إِنَّا مُنتَظِرُونَ", "Hal yanthuroona illa an tatiyahumu almalaikatu aw yatiya rabbuka aw yatiya baAAdu ayati rabbika yawma yatee baAAdu ayati rabbika la yanfaAAu nafsan eemanuha lam takun amanat min qablu aw kasabat fee eemaniha khayran quli intathiroo inna muntathiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 159, "Indeed, those who have divided their religion and become sects - you, [O Muhammad], are not [associated] with them in anything. Their affair is only [left] to Allah; then He will inform them about what they used to do", "إِنَّ ٱلَّذِينَ فَرَّقُواْ دِينَهُمۡ وَكَانُواْ شِيَعٗا لَّسۡتَ مِنۡهُمۡ فِي شَيۡءٍۚ إِنَّمَآ أَمۡرُهُمۡ إِلَى ٱللَّهِ ثُمَّ يُنَبِّئُهُم بِمَا كَانُواْ يَفۡعَلُونَ", "Inna allatheena farraqoo deenahum wakanoo shiyaAAan lasta minhum fee shayin innama amruhum ila Allahi thumma yunabbiohum bima kanoo yafAAaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 160, "Whoever comes [on the Day of Judgement] with a good deed will have ten times the like thereof [to his credit], and whoever comes with an evil deed will not be recompensed except the like thereof; and they will not be wronged", "مَن جَآءَ بِٱلۡحَسَنَةِ فَلَهُۥ عَشۡرُ أَمۡثَالِهَاۖ وَمَن جَآءَ بِٱلسَّيِّئَةِ فَلَا يُجۡزَىٰٓ إِلَّا مِثۡلَهَا وَهُمۡ لَا يُظۡلَمُونَ", "Man jaa bialhasanati falahu AAashru amthaliha waman jaa bialssayyiati fala yujza illa mithlaha wahum la yuthlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 161, "Say, ""Indeed, my Lord has guided me to a straight path - a correct religion - the way of Abraham, inclining toward truth. And he was not among those who associated others with Allah", "قُلۡ إِنَّنِي هَدَىٰنِي رَبِّيٓ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ دِينٗا قِيَمٗا مِّلَّةَ إِبۡرَٰهِيمَ حَنِيفٗاۚ وَمَا كَانَ مِنَ ٱلۡمُشۡرِكِينَ", "Qul innanee hadanee rabbee ila siratin mustaqeemin deenan qiyaman millata ibraheema haneefan wama kana mina almushrikeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 162, "Say, ""Indeed, my prayer, my rites of sacrifice, my living and my dying are for Allah, Lord of the worlds", "قُلۡ إِنَّ صَلَاتِي وَنُسُكِي وَمَحۡيَايَ وَمَمَاتِي لِلَّهِ رَبِّ ٱلۡعَٰلَمِينَ", "Qul inna salatee wanusukee wamahyaya wamamatee lillahi rabbi alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 163, "No partner has He. And this I have been commanded, and I am the first [among you] of the Muslims", "لَا شَرِيكَ لَهُۥۖ وَبِذَٰلِكَ أُمِرۡتُ وَأَنَا۠ أَوَّلُ ٱلۡمُسۡلِمِينَ", "La shareeka lahu wabithalika omirtu waana awwalu almuslimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 164, "Say, ""Is it other than Allah I should desire as a lord while He is the Lord of all things? And every soul earns not [blame] except against itself, and no bearer of burdens will bear the burden of another. Then to your Lord is your return, and He will inform you concerning that over which you used to differ", "قُلۡ أَغَيۡرَ ٱللَّهِ أَبۡغِي رَبّٗا وَهُوَ رَبُّ كُلِّ شَيۡءٖۚ وَلَا تَكۡسِبُ كُلُّ نَفۡسٍ إِلَّا عَلَيۡهَاۚ وَلَا تَزِرُ وَازِرَةٞ وِزۡرَ أُخۡرَىٰۚ ثُمَّ إِلَىٰ رَبِّكُم مَّرۡجِعُكُمۡ فَيُنَبِّئُكُم بِمَا كُنتُمۡ فِيهِ تَخۡتَلِفُونَ", "Qul aghayra Allahi abghee rabban wahuwa rabbu kulli shayin wala taksibu kullu nafsin illa AAalayha wala taziru waziratun wizra okhra thumma ila rabbikum marjiAAukum fayunabbiokum bima kuntum feehi takhtalifoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (6, 165, "And it is He who has made you successors upon the earth and has raised some of you above others in degrees [of rank] that He may try you through what He has given you. Indeed, your Lord is swift in penalty; but indeed, He is Forgiving and Merciful", "وَهُوَ ٱلَّذِي جَعَلَكُمۡ خَلَـٰٓئِفَ ٱلۡأَرۡضِ وَرَفَعَ بَعۡضَكُمۡ فَوۡقَ بَعۡضٖ دَرَجَٰتٖ لِّيَبۡلُوَكُمۡ فِي مَآ ءَاتَىٰكُمۡۗ إِنَّ رَبَّكَ سَرِيعُ ٱلۡعِقَابِ وَإِنَّهُۥ لَغَفُورٞ رَّحِيمُۢ", "Wahuwa allathee jaAAalakum khalaifa alardi warafaAAa baAAdakum fawqa baAAdin darajatin liyabluwakum fee ma atakum inna rabbaka sareeAAu alAAiqabi wainnahu laghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 1, "Alif, Lam, Meem, Sad", "الٓمٓصٓ", "Aliflammeemsad");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 2, "[This is] a Book revealed to you, [O Muhammad] - so let there not be in your breast distress therefrom - that you may warn thereby and as a reminder to the believers", "كِتَٰبٌ أُنزِلَ إِلَيۡكَ فَلَا يَكُن فِي صَدۡرِكَ حَرَجٞ مِّنۡهُ لِتُنذِرَ بِهِۦ وَذِكۡرَىٰ لِلۡمُؤۡمِنِينَ", "Kitabun onzila ilayka fala yakun fee sadrika harajun minhu litunthira bihi wathikra lilmumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 3, "Follow, [O mankind], what has been revealed to you from your Lord and do not follow other than Him any allies. Little do you remember", "ٱتَّبِعُواْ مَآ أُنزِلَ إِلَيۡكُم مِّن رَّبِّكُمۡ وَلَا تَتَّبِعُواْ مِن دُونِهِۦٓ أَوۡلِيَآءَۗ قَلِيلٗا مَّا تَذَكَّرُونَ", "IttabiAAoo ma onzila ilaykum min rabbikum wala tattabiAAoo min doonihi awliyaa qaleelan ma tathakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 4, "And how many cities have We destroyed, and Our punishment came to them at night or while they were sleeping at noon", "وَكَم مِّن قَرۡيَةٍ أَهۡلَكۡنَٰهَا فَجَآءَهَا بَأۡسُنَا بَيَٰتًا أَوۡ هُمۡ قَآئِلُونَ", "Wakam min qaryatin ahlaknaha fajaaha basuna bayatan aw hum qailoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 5, "And their declaration when Our punishment came to them was only that they said, ""Indeed, we were wrongdoers", "فَمَا كَانَ دَعۡوَىٰهُمۡ إِذۡ جَآءَهُم بَأۡسُنَآ إِلَّآ أَن قَالُوٓاْ إِنَّا كُنَّا ظَٰلِمِينَ", "Fama kana daAAwahum ith jaahum basuna illa an qaloo inna kunna thalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 6, "Then We will surely question those to whom [a message] was sent, and We will surely question the messengers", "فَلَنَسۡـَٔلَنَّ ٱلَّذِينَ أُرۡسِلَ إِلَيۡهِمۡ وَلَنَسۡـَٔلَنَّ ٱلۡمُرۡسَلِينَ", "Falanasalanna allatheena orsila ilayhim walanasalanna almursaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 7, "Then We will surely relate [their deeds] to them with knowledge, and We were not [at all] absent", "فَلَنَقُصَّنَّ عَلَيۡهِم بِعِلۡمٖۖ وَمَا كُنَّا غَآئِبِينَ", "Falanaqussanna AAalayhim biAAilmin wama kunna ghaibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 8, "And the weighing [of deeds] that Day will be the truth. So those whose scales are heavy - it is they who will be the successful", "وَٱلۡوَزۡنُ يَوۡمَئِذٍ ٱلۡحَقُّۚ فَمَن ثَقُلَتۡ مَوَٰزِينُهُۥ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُفۡلِحُونَ", "Waalwaznu yawmaithini alhaqqu faman thaqulat mawazeenuhu faolaika humu almuflihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 9, "And those whose scales are light - they are the ones who will lose themselves for what injustice they were doing toward Our verses", "وَمَنۡ خَفَّتۡ مَوَٰزِينُهُۥ فَأُوْلَـٰٓئِكَ ٱلَّذِينَ خَسِرُوٓاْ أَنفُسَهُم بِمَا كَانُواْ بِـَٔايَٰتِنَا يَظۡلِمُونَ", "Waman khaffat mawazeenuhu faolaika allatheena khasiroo anfusahum bima kanoo biayatina yathlimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 10, "And We have certainly established you upon the earth and made for you therein ways of livelihood. Little are you grateful", "وَلَقَدۡ مَكَّنَّـٰكُمۡ فِي ٱلۡأَرۡضِ وَجَعَلۡنَا لَكُمۡ فِيهَا مَعَٰيِشَۗ قَلِيلٗا مَّا تَشۡكُرُونَ", "Walaqad makkannakum fee alardi wajaAAalna lakum feeha maAAayisha qaleelan ma tashkuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 11, "And We have certainly created you, [O Mankind], and given you [human] form. Then We said to the angels, ""Prostrate to Adam""; so they prostrated, except for Iblees. He was not of those who prostrated", "وَلَقَدۡ خَلَقۡنَٰكُمۡ ثُمَّ صَوَّرۡنَٰكُمۡ ثُمَّ قُلۡنَا لِلۡمَلَـٰٓئِكَةِ ٱسۡجُدُواْ لِأٓدَمَ فَسَجَدُوٓاْ إِلَّآ إِبۡلِيسَ لَمۡ يَكُن مِّنَ ٱلسَّـٰجِدِينَ", "Walaqad khalaqnakum thumma sawwarnakum thumma qulna lilmalaikati osjudoo liadama fasajadoo illa ibleesa lam yakun mina alssajideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 12, "[Allah] said, ""What prevented you from prostrating when I commanded you?"" [Satan] said, ""I am better than him. You created me from fire and created him from clay", "قَالَ مَا مَنَعَكَ أَلَّا تَسۡجُدَ إِذۡ أَمَرۡتُكَۖ قَالَ أَنَا۠ خَيۡرٞ مِّنۡهُ خَلَقۡتَنِي مِن نَّارٖ وَخَلَقۡتَهُۥ مِن طِينٖ", "Qala ma manaAAaka alla tasjuda ith amartuka qala ana khayrun minhu khalaqtanee min narin wakhalaqtahu min teenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 13, "[Allah] said, ""Descend from Paradise, for it is not for you to be arrogant therein. So get out; indeed, you are of the debased", "قَالَ فَٱهۡبِطۡ مِنۡهَا فَمَا يَكُونُ لَكَ أَن تَتَكَبَّرَ فِيهَا فَٱخۡرُجۡ إِنَّكَ مِنَ ٱلصَّـٰغِرِينَ", "Qala faihbit minha fama yakoonu laka an tatakabbara feeha faokhruj innaka mina alssaghireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 14, "[Satan] said, ""Reprieve me until the Day they are resurrected", "قَالَ أَنظِرۡنِيٓ إِلَىٰ يَوۡمِ يُبۡعَثُونَ", "Qala anthirnee ila yawmi yubAAathoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 15, "[Allah] said, ""Indeed, you are of those reprieved", "قَالَ إِنَّكَ مِنَ ٱلۡمُنظَرِينَ", "Qala innaka mina almunthareena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 16, "[Satan] said, ""Because You have put me in error, I will surely sit in wait for them on Your straight path", "قَالَ فَبِمَآ أَغۡوَيۡتَنِي لَأَقۡعُدَنَّ لَهُمۡ صِرَٰطَكَ ٱلۡمُسۡتَقِيمَ", "Qala fabima aghwaytanee laaqAAudanna lahum sirataka almustaqeema");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 17, "Then I will come to them from before them and from behind them and on their right and on their left, and You will not find most of them grateful [to You]", "ثُمَّ لَأٓتِيَنَّهُم مِّنۢ بَيۡنِ أَيۡدِيهِمۡ وَمِنۡ خَلۡفِهِمۡ وَعَنۡ أَيۡمَٰنِهِمۡ وَعَن شَمَآئِلِهِمۡۖ وَلَا تَجِدُ أَكۡثَرَهُمۡ شَٰكِرِينَ", "Thumma laatiyannahum min bayni aydeehim wamin khalfihim waAAan aymanihim waAAan shamailihim wala tajidu aktharahum shakireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 18, "[Allah] said, ""Get out of Paradise, reproached and expelled. Whoever follows you among them - I will surely fill Hell with you, all together", "قَالَ ٱخۡرُجۡ مِنۡهَا مَذۡءُومٗا مَّدۡحُورٗاۖ لَّمَن تَبِعَكَ مِنۡهُمۡ لَأَمۡلَأَنَّ جَهَنَّمَ مِنكُمۡ أَجۡمَعِينَ", "Qala okhruj minha mathooman madhooran laman tabiAAaka minhum laamlaanna jahannama minkum ajmaAAeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 19, "And ""O Adam, dwell, you and your wife, in Paradise and eat from wherever you will but do not approach this tree, lest you be among the wrongdoers", "وَيَـٰٓـَٔادَمُ ٱسۡكُنۡ أَنتَ وَزَوۡجُكَ ٱلۡجَنَّةَ فَكُلَا مِنۡ حَيۡثُ شِئۡتُمَا وَلَا تَقۡرَبَا هَٰذِهِ ٱلشَّجَرَةَ فَتَكُونَا مِنَ ٱلظَّـٰلِمِينَ", "Waya adamu oskun anta wazawjuka aljannata fakula min haythu shituma wala taqraba hathihi alshshajarata fatakoona mina alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 20, "But Satan whispered to them to make apparent to them that which was concealed from them of their private parts. He said, ""Your Lord did not forbid you this tree except that you become angels or become of the immortal", "فَوَسۡوَسَ لَهُمَا ٱلشَّيۡطَٰنُ لِيُبۡدِيَ لَهُمَا مَا وُۥرِيَ عَنۡهُمَا مِن سَوۡءَٰتِهِمَا وَقَالَ مَا نَهَىٰكُمَا رَبُّكُمَا عَنۡ هَٰذِهِ ٱلشَّجَرَةِ إِلَّآ أَن تَكُونَا مَلَكَيۡنِ أَوۡ تَكُونَا مِنَ ٱلۡخَٰلِدِينَ", "Fawaswasa lahuma alshshaytanu liyubdiya lahuma ma wooriya AAanhuma min sawatihima waqala ma nahakuma rabbukuma AAan hathihi alshshajarati illa an takoona malakayni aw takoona mina alkhalideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 21, "And he swore [by Allah] to them, ""Indeed, I am to you from among the sincere advisors", "وَقَاسَمَهُمَآ إِنِّي لَكُمَا لَمِنَ ٱلنَّـٰصِحِينَ", "Waqasamahuma innee lakuma lamina alnnasiheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 22, "So he made them fall, through deception. And when they tasted of the tree, their private parts became apparent to them, and they began to fasten together over themselves from the leaves of Paradise. And their Lord called to them, ""Did I not forbid you from that tree and tell you that Satan is to you a clear enemy", "فَدَلَّىٰهُمَا بِغُرُورٖۚ فَلَمَّا ذَاقَا ٱلشَّجَرَةَ بَدَتۡ لَهُمَا سَوۡءَٰتُهُمَا وَطَفِقَا يَخۡصِفَانِ عَلَيۡهِمَا مِن وَرَقِ ٱلۡجَنَّةِۖ وَنَادَىٰهُمَا رَبُّهُمَآ أَلَمۡ أَنۡهَكُمَا عَن تِلۡكُمَا ٱلشَّجَرَةِ وَأَقُل لَّكُمَآ إِنَّ ٱلشَّيۡطَٰنَ لَكُمَا عَدُوّٞ مُّبِينٞ", "Fadallahuma bighuroorin falamma thaqa alshshajarata badat lahuma sawatuhuma watafiqa yakhsifani AAalayhima min waraqi aljannati wanadahuma rabbuhuma alam anhakuma AAan tilkuma alshshajarati waaqul lakuma inna alshshaytana lakuma AAaduwwun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 23, "They said, ""Our Lord, we have wronged ourselves, and if You do not forgive us and have mercy upon us, we will surely be among the losers", "قَالَا رَبَّنَا ظَلَمۡنَآ أَنفُسَنَا وَإِن لَّمۡ تَغۡفِرۡ لَنَا وَتَرۡحَمۡنَا لَنَكُونَنَّ مِنَ ٱلۡخَٰسِرِينَ", "Qala rabbana thalamna anfusana wain lam taghfir lana watarhamna lanakoonanna mina alkhasireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 24, "[Allah] said, ""Descend, being to one another enemies. And for you on the earth is a place of settlement and enjoyment for a time", "قَالَ ٱهۡبِطُواْ بَعۡضُكُمۡ لِبَعۡضٍ عَدُوّٞۖ وَلَكُمۡ فِي ٱلۡأَرۡضِ مُسۡتَقَرّٞ وَمَتَٰعٌ إِلَىٰ حِينٖ", "Qala ihbitoo baAAdukum libaAAdin AAaduwwun walakum fee alardi mustaqarrun wamataAAun ila heenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 25, "He said, ""Therein you will live, and therein you will die, and from it you will be brought forth", "قَالَ فِيهَا تَحۡيَوۡنَ وَفِيهَا تَمُوتُونَ وَمِنۡهَا تُخۡرَجُونَ", "Qala feeha tahyawna wafeeha tamootoona waminha tukhrajoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 26, "O children of Adam, We have bestowed upon you clothing to conceal your private parts and as adornment. But the clothing of righteousness - that is best. That is from the signs of Allah that perhaps they will remember", "يَٰبَنِيٓ ءَادَمَ قَدۡ أَنزَلۡنَا عَلَيۡكُمۡ لِبَاسٗا يُوَٰرِي سَوۡءَٰتِكُمۡ وَرِيشٗاۖ وَلِبَاسُ ٱلتَّقۡوَىٰ ذَٰلِكَ خَيۡرٞۚ ذَٰلِكَ مِنۡ ءَايَٰتِ ٱللَّهِ لَعَلَّهُمۡ يَذَّكَّرُونَ", "Ya banee adama qad anzalna AAalaykum libasan yuwaree sawatikum wareeshan walibasu alttaqwa thalika khayrun thalika min ayati Allahi laAAallahum yaththakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 27, "O children of Adam, let not Satan tempt you as he removed your parents from Paradise, stripping them of their clothing to show them their private parts. Indeed, he sees you, he and his tribe, from where you do not see them. Indeed, We have made the devils allies to those who do not believe", "يَٰبَنِيٓ ءَادَمَ لَا يَفۡتِنَنَّكُمُ ٱلشَّيۡطَٰنُ كَمَآ أَخۡرَجَ أَبَوَيۡكُم مِّنَ ٱلۡجَنَّةِ يَنزِعُ عَنۡهُمَا لِبَاسَهُمَا لِيُرِيَهُمَا سَوۡءَٰتِهِمَآۚ إِنَّهُۥ يَرَىٰكُمۡ هُوَ وَقَبِيلُهُۥ مِنۡ حَيۡثُ لَا تَرَوۡنَهُمۡۗ إِنَّا جَعَلۡنَا ٱلشَّيَٰطِينَ أَوۡلِيَآءَ لِلَّذِينَ لَا يُؤۡمِنُونَ", "Ya banee adama la yaftinannakumu alshshaytanu kama akhraja abawaykum mina aljannati yanziAAu AAanhuma libasahuma liyuriyahuma sawatihima innahu yarakum huwa waqabeeluhu min haythu la tarawnahum inna jaAAalna alshshayateena awliyaa lillatheena la yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 28, "And when they commit an immorality, they say, ""We found our fathers doing it, and Allah has ordered us to do it."" Say, ""Indeed, Allah does not order immorality. Do you say about Allah that which you do not know", "وَإِذَا فَعَلُواْ فَٰحِشَةٗ قَالُواْ وَجَدۡنَا عَلَيۡهَآ ءَابَآءَنَا وَٱللَّهُ أَمَرَنَا بِهَاۗ قُلۡ إِنَّ ٱللَّهَ لَا يَأۡمُرُ بِٱلۡفَحۡشَآءِۖ أَتَقُولُونَ عَلَى ٱللَّهِ مَا لَا تَعۡلَمُونَ", "Waitha faAAaloo fahishatan qaloo wajadna AAalayha abaana waAllahu amarana biha qul inna Allaha la yamuru bialfahshai ataqooloona AAala Allahi ma la taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 29, "Say, [O Muhammad], ""My Lord has ordered justice and that you maintain yourselves [in worship of Him] at every place [or time] of prostration, and invoke Him, sincere to Him in religion."" Just as He originated you, you will return [to life]", "قُلۡ أَمَرَ رَبِّي بِٱلۡقِسۡطِۖ وَأَقِيمُواْ وُجُوهَكُمۡ عِندَ كُلِّ مَسۡجِدٖ وَٱدۡعُوهُ مُخۡلِصِينَ لَهُ ٱلدِّينَۚ كَمَا بَدَأَكُمۡ تَعُودُونَ", "Qul amara rabbee bialqisti waaqeemoo wujoohakum AAinda kulli masjidin waodAAoohu mukhliseena lahu alddeena kama badaakum taAAoodoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 30, "A group [of you] He guided, and a group deserved [to be in] error. Indeed, they had taken the devils as allies instead of Allah while they thought that they were guided", "فَرِيقًا هَدَىٰ وَفَرِيقًا حَقَّ عَلَيۡهِمُ ٱلضَّلَٰلَةُۚ إِنَّهُمُ ٱتَّخَذُواْ ٱلشَّيَٰطِينَ أَوۡلِيَآءَ مِن دُونِ ٱللَّهِ وَيَحۡسَبُونَ أَنَّهُم مُّهۡتَدُونَ", "Fareeqan hada wafareeqan haqqa AAalayhimu alddalalatu innahumu ittakhathoo alshshayateena awliyaa min dooni Allahi wayahsaboona annahum muhtadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 31, "O children of Adam, take your adornment at every masjid, and eat and drink, but be not excessive. Indeed, He likes not those who commit excess", "۞يَٰبَنِيٓ ءَادَمَ خُذُواْ زِينَتَكُمۡ عِندَ كُلِّ مَسۡجِدٖ وَكُلُواْ وَٱشۡرَبُواْ وَلَا تُسۡرِفُوٓاْۚ إِنَّهُۥ لَا يُحِبُّ ٱلۡمُسۡرِفِينَ", "Ya banee adama khuthoo zeenatakum AAinda kulli masjidin wakuloo waishraboo wala tusrifoo innahu la yuhibbu almusrifeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 32, "Say, ""Who has forbidden the adornment of Allah which He has produced for His servants and the good [lawful] things of provision?"" Say, ""They are for those who believe during the worldly life [but] exclusively for them on the Day of Resurrection."" Thus do We detail the verses for a people who know", "قُلۡ مَنۡ حَرَّمَ زِينَةَ ٱللَّهِ ٱلَّتِيٓ أَخۡرَجَ لِعِبَادِهِۦ وَٱلطَّيِّبَٰتِ مِنَ ٱلرِّزۡقِۚ قُلۡ هِيَ لِلَّذِينَ ءَامَنُواْ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا خَالِصَةٗ يَوۡمَ ٱلۡقِيَٰمَةِۗ كَذَٰلِكَ نُفَصِّلُ ٱلۡأٓيَٰتِ لِقَوۡمٖ يَعۡلَمُونَ", "Qul man harrama zeenata Allahi allatee akhraja liAAibadihi waalttayyibati mina alrrizqi qul hiya lillatheena amanoo fee alhayati alddunya khalisatan yawma alqiyamati kathalika nufassilu alayati liqawmin yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 33, "Say, ""My Lord has only forbidden immoralities - what is apparent of them and what is concealed - and sin, and oppression without right, and that you associate with Allah that for which He has not sent down authority, and that you say about Allah that which you do not know", "قُلۡ إِنَّمَا حَرَّمَ رَبِّيَ ٱلۡفَوَٰحِشَ مَا ظَهَرَ مِنۡهَا وَمَا بَطَنَ وَٱلۡإِثۡمَ وَٱلۡبَغۡيَ بِغَيۡرِ ٱلۡحَقِّ وَأَن تُشۡرِكُواْ بِٱللَّهِ مَا لَمۡ يُنَزِّلۡ بِهِۦ سُلۡطَٰنٗا وَأَن تَقُولُواْ عَلَى ٱللَّهِ مَا لَا تَعۡلَمُونَ", "Qul innama harrama rabbiya alfawahisha ma thahara minha wama batana waalithma waalbaghya bighayri alhaqqi waan tushrikoo biAllahi ma lam yunazzil bihi sultanan waan taqooloo AAala Allahi ma la taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 34, "And for every nation is a [specified] term. So when their time has come, they will not remain behind an hour, nor will they precede [it]", "وَلِكُلِّ أُمَّةٍ أَجَلٞۖ فَإِذَا جَآءَ أَجَلُهُمۡ لَا يَسۡتَأۡخِرُونَ سَاعَةٗ وَلَا يَسۡتَقۡدِمُونَ", "Walikulli ommatin ajalun faitha jaa ajaluhum la yastakhiroona saAAatan wala yastaqdimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 35, "O children of Adam, if there come to you messengers from among you relating to you My verses, then whoever fears Allah and reforms - there will be no fear concerning them, nor will they grieve", "يَٰبَنِيٓ ءَادَمَ إِمَّا يَأۡتِيَنَّكُمۡ رُسُلٞ مِّنكُمۡ يَقُصُّونَ عَلَيۡكُمۡ ءَايَٰتِي فَمَنِ ٱتَّقَىٰ وَأَصۡلَحَ فَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ", "Ya banee adama imma yatiyannakum rusulun minkum yaqussoona AAalaykum ayatee famani ittaqa waaslaha fala khawfun AAalayhim wala hum yahzanoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 36, "But the ones who deny Our verses and are arrogant toward them - those are the companions of the Fire; they will abide therein eternally", "وَٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا وَٱسۡتَكۡبَرُواْ عَنۡهَآ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلنَّارِۖ هُمۡ فِيهَا خَٰلِدُونَ", "Waallatheena kaththaboo biayatina waistakbaroo AAanha olaika ashabu alnnari hum feeha khalidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 37, "And who is more unjust than one who invents about Allah a lie or denies His verses? Those will attain their portion of the decree until when Our messengers come to them to take them in death, they will say, ""Where are those you used to invoke besides Allah?"" They will say, ""They have departed from us,"" and will bear witness against themselves that they were disbelievers", "فَمَنۡ أَظۡلَمُ مِمَّنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبًا أَوۡ كَذَّبَ بِـَٔايَٰتِهِۦٓۚ أُوْلَـٰٓئِكَ يَنَالُهُمۡ نَصِيبُهُم مِّنَ ٱلۡكِتَٰبِۖ حَتَّىٰٓ إِذَا جَآءَتۡهُمۡ رُسُلُنَا يَتَوَفَّوۡنَهُمۡ قَالُوٓاْ أَيۡنَ مَا كُنتُمۡ تَدۡعُونَ مِن دُونِ ٱللَّهِۖ قَالُواْ ضَلُّواْ عَنَّا وَشَهِدُواْ عَلَىٰٓ أَنفُسِهِمۡ أَنَّهُمۡ كَانُواْ كَٰفِرِينَ", "Faman athlamu mimmani iftara AAala Allahi kathiban aw kaththaba biayatihi olaika yanaluhum naseebuhum mina alkitabi hatta itha jaathum rusuluna yatawaffawnahum qaloo ayna ma kuntum tadAAoona min dooni Allahi qaloo dalloo AAanna washahidoo AAala anfusihim annahum kanoo kafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 38, "[Allah] will say, ""Enter among nations which had passed on before you of jinn and mankind into the Fire."" Every time a nation enters, it will curse its sister until, when they have all overtaken one another therein, the last of them will say about the first of them ""Our Lord, these had misled us, so give them a double punishment of the Fire. He will say, ""For each is double, but you do not know", "قَالَ ٱدۡخُلُواْ فِيٓ أُمَمٖ قَدۡ خَلَتۡ مِن قَبۡلِكُم مِّنَ ٱلۡجِنِّ وَٱلۡإِنسِ فِي ٱلنَّارِۖ كُلَّمَا دَخَلَتۡ أُمَّةٞ لَّعَنَتۡ أُخۡتَهَاۖ حَتَّىٰٓ إِذَا ٱدَّارَكُواْ فِيهَا جَمِيعٗا قَالَتۡ أُخۡرَىٰهُمۡ لِأُولَىٰهُمۡ رَبَّنَا هَـٰٓؤُلَآءِ أَضَلُّونَا فَـَٔاتِهِمۡ عَذَابٗا ضِعۡفٗا مِّنَ ٱلنَّارِۖ قَالَ لِكُلّٖ ضِعۡفٞ وَلَٰكِن لَّا تَعۡلَمُونَ", "Qala odkhuloo fee omamin qad khalat min qablikum mina aljinni waalinsi fee alnnari kullama dakhalat ommatun laAAanat okhtaha hatta itha iddarakoo feeha jameeAAan qalat okhrahum lioolahum rabbana haolai adalloona faatihim AAathaban diAAfan mina alnnari qala likullin diAAfun walakin la taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 39, "And the first of them will say to the last of them, ""Then you had not any favor over us, so taste the punishment for what you used to earn", "وَقَالَتۡ أُولَىٰهُمۡ لِأُخۡرَىٰهُمۡ فَمَا كَانَ لَكُمۡ عَلَيۡنَا مِن فَضۡلٖ فَذُوقُواْ ٱلۡعَذَابَ بِمَا كُنتُمۡ تَكۡسِبُونَ", "Waqalat oolahum liokhrahum fama kana lakum AAalayna min fadlin fathooqoo alAAathaba bima kuntum taksiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 40, "Indeed, those who deny Our verses and are arrogant toward them - the gates of Heaven will not be opened for them, nor will they enter Paradise until a camel enters into the eye of a needle. And thus do We recompense the criminals", "إِنَّ ٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا وَٱسۡتَكۡبَرُواْ عَنۡهَا لَا تُفَتَّحُ لَهُمۡ أَبۡوَٰبُ ٱلسَّمَآءِ وَلَا يَدۡخُلُونَ ٱلۡجَنَّةَ حَتَّىٰ يَلِجَ ٱلۡجَمَلُ فِي سَمِّ ٱلۡخِيَاطِۚ وَكَذَٰلِكَ نَجۡزِي ٱلۡمُجۡرِمِينَ", "Inna allatheena kaththaboo biayatina waistakbaroo AAanha la tufattahu lahum abwabu alssamai wala yadkhuloona aljannata hatta yalija aljamalu fee sammi alkhiyati wakathalika najzee almujrimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 41, "They will have from Hell a bed and over them coverings [of fire]. And thus do We recompense the wrongdoers", "لَهُم مِّن جَهَنَّمَ مِهَادٞ وَمِن فَوۡقِهِمۡ غَوَاشٖۚ وَكَذَٰلِكَ نَجۡزِي ٱلظَّـٰلِمِينَ", "Lahum min jahannama mihadun wamin fawqihim ghawashin wakathalika najzee alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 42, "But those who believed and did righteous deeds - We charge no soul except [within] its capacity. Those are the companions of Paradise; they will abide therein eternally", "وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ لَا نُكَلِّفُ نَفۡسًا إِلَّا وُسۡعَهَآ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلۡجَنَّةِۖ هُمۡ فِيهَا خَٰلِدُونَ", "Waallatheena amanoo waAAamiloo alssalihati la nukallifu nafsan illa wusAAaha olaika ashabu aljannati hum feeha khalidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 43, "And We will have removed whatever is within their breasts of resentment, [while] flowing beneath them are rivers. And they will say, ""Praise to Allah, who has guided us to this; and we would never have been guided if Allah had not guided us. Certainly the messengers of our Lord had come with the truth."" And they will be called, ""This is Paradise, which you have been made to inherit for what you used to do", "وَنَزَعۡنَا مَا فِي صُدُورِهِم مِّنۡ غِلّٖ تَجۡرِي مِن تَحۡتِهِمُ ٱلۡأَنۡهَٰرُۖ وَقَالُواْ ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِي هَدَىٰنَا لِهَٰذَا وَمَا كُنَّا لِنَهۡتَدِيَ لَوۡلَآ أَنۡ هَدَىٰنَا ٱللَّهُۖ لَقَدۡ جَآءَتۡ رُسُلُ رَبِّنَا بِٱلۡحَقِّۖ وَنُودُوٓاْ أَن تِلۡكُمُ ٱلۡجَنَّةُ أُورِثۡتُمُوهَا بِمَا كُنتُمۡ تَعۡمَلُونَ", "WanazaAAna ma fee sudoorihim min ghillin tajree min tahtihimu alanharu waqaloo alhamdu lillahi allathee hadana lihatha wama kunna linahtadiya lawla an hadana Allahu laqad jaat rusulu rabbina bialhaqqi wanoodoo an tilkumu aljannatu oorithtumooha bima kuntum taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 44, "And the companions of Paradise will call out to the companions of the Fire, ""We have already found what our Lord promised us to be true. Have you found what your Lord promised to be true?"" They will say, ""Yes."" Then an announcer will announce among them, ""The curse of Allah shall be upon the wrongdoers", "وَنَادَىٰٓ أَصۡحَٰبُ ٱلۡجَنَّةِ أَصۡحَٰبَ ٱلنَّارِ أَن قَدۡ وَجَدۡنَا مَا وَعَدَنَا رَبُّنَا حَقّٗا فَهَلۡ وَجَدتُّم مَّا وَعَدَ رَبُّكُمۡ حَقّٗاۖ قَالُواْ نَعَمۡۚ فَأَذَّنَ مُؤَذِّنُۢ بَيۡنَهُمۡ أَن لَّعۡنَةُ ٱللَّهِ عَلَى ٱلظَّـٰلِمِينَ", "Wanada ashabu aljannati ashaba alnnari an qad wajadna ma waAAadana rabbuna haqqan fahal wajadtum ma waAAada rabbukum haqqan qaloo naAAam faaththana muaththinun baynahum an laAAnatu Allahi AAala alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 45, "Who averted [people] from the way of Allah and sought to make it [seem] deviant while they were, concerning the Hereafter, disbelievers", "ٱلَّذِينَ يَصُدُّونَ عَن سَبِيلِ ٱللَّهِ وَيَبۡغُونَهَا عِوَجٗا وَهُم بِٱلۡأٓخِرَةِ كَٰفِرُونَ", "Allatheena yasuddoona AAan sabeeli Allahi wayabghoonaha AAiwajan wahum bialakhirati kafiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 46, "And between them will be a partition, and on [its] elevations are men who recognize all by their mark. And they call out to the companions of Paradise, ""Peace be upon you."" They have not [yet] entered it, but they long intensely", "وَبَيۡنَهُمَا حِجَابٞۚ وَعَلَى ٱلۡأَعۡرَافِ رِجَالٞ يَعۡرِفُونَ كُلَّۢا بِسِيمَىٰهُمۡۚ وَنَادَوۡاْ أَصۡحَٰبَ ٱلۡجَنَّةِ أَن سَلَٰمٌ عَلَيۡكُمۡۚ لَمۡ يَدۡخُلُوهَا وَهُمۡ يَطۡمَعُونَ", "Wabaynahuma hijabun waAAala alaAArafi rijalun yaAArifoona kullan biseemahum wanadaw ashaba aljannati an salamun AAalaykum lam yadkhulooha wahum yatmaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 47, "And when their eyes are turned toward the companions of the Fire, they say, ""Our Lord, do not place us with the wrongdoing people", "۞وَإِذَا صُرِفَتۡ أَبۡصَٰرُهُمۡ تِلۡقَآءَ أَصۡحَٰبِ ٱلنَّارِ قَالُواْ رَبَّنَا لَا تَجۡعَلۡنَا مَعَ ٱلۡقَوۡمِ ٱلظَّـٰلِمِينَ", "Waitha surifat absaruhum tilqaa ashabi alnnari qaloo rabbana la tajAAalna maAAa alqawmi alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 48, "And the companions of the Elevations will call to men [within Hell] whom they recognize by their mark, saying, ""Of no avail to you was your gathering and [the fact] that you were arrogant", "وَنَادَىٰٓ أَصۡحَٰبُ ٱلۡأَعۡرَافِ رِجَالٗا يَعۡرِفُونَهُم بِسِيمَىٰهُمۡ قَالُواْ مَآ أَغۡنَىٰ عَنكُمۡ جَمۡعُكُمۡ وَمَا كُنتُمۡ تَسۡتَكۡبِرُونَ", "Wanada ashabu alaAArafi rijalan yaAArifoonahum biseemahum qaloo ma aghna AAankum jamAAukum wama kuntum tastakbiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 49, "[Allah will say], ""Are these the ones whom you [inhabitants of Hell] swore that Allah would never offer them mercy? Enter Paradise, [O People of the Elevations]. No fear will there be concerning you, nor will you grieve", "أَهَـٰٓؤُلَآءِ ٱلَّذِينَ أَقۡسَمۡتُمۡ لَا يَنَالُهُمُ ٱللَّهُ بِرَحۡمَةٍۚ ٱدۡخُلُواْ ٱلۡجَنَّةَ لَا خَوۡفٌ عَلَيۡكُمۡ وَلَآ أَنتُمۡ تَحۡزَنُونَ", "Ahaolai allatheena aqsamtum la yanaluhumu Allahu birahmatin odkhuloo aljannata la khawfun AAalaykum wala antum tahzanoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 50, "And the companions of the Fire will call to the companions of Paradise, ""Pour upon us some water or from whatever Allah has provided you."" They will say, ""Indeed, Allah has forbidden them both to the disbelievers", "وَنَادَىٰٓ أَصۡحَٰبُ ٱلنَّارِ أَصۡحَٰبَ ٱلۡجَنَّةِ أَنۡ أَفِيضُواْ عَلَيۡنَا مِنَ ٱلۡمَآءِ أَوۡ مِمَّا رَزَقَكُمُ ٱللَّهُۚ قَالُوٓاْ إِنَّ ٱللَّهَ حَرَّمَهُمَا عَلَى ٱلۡكَٰفِرِينَ", "Wanada ashabu alnari ashaba aljannati an afeedoo AAalayna mina almai aw mimma razaqakumu Allahu qaloo inna Allaha harramahuma AAala alkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 51, "Who took their religion as distraction and amusement and whom the worldly life deluded."" So today We will forget them just as they forgot the meeting of this Day of theirs and for having rejected Our verses", "ٱلَّذِينَ ٱتَّخَذُواْ دِينَهُمۡ لَهۡوٗا وَلَعِبٗا وَغَرَّتۡهُمُ ٱلۡحَيَوٰةُ ٱلدُّنۡيَاۚ فَٱلۡيَوۡمَ نَنسَىٰهُمۡ كَمَا نَسُواْ لِقَآءَ يَوۡمِهِمۡ هَٰذَا وَمَا كَانُواْ بِـَٔايَٰتِنَا يَجۡحَدُونَ", "Allatheena ittakhathoo deenahum lahwan walaAAiban wagharrathumu alhayatu alddunya faalyawma nansahum kama nasoo liqaa yawmihim hatha wama kanoo biayatina yajhadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 52, "And We had certainly brought them a Book which We detailed by knowledge - as guidance and mercy to a people who believe", "وَلَقَدۡ جِئۡنَٰهُم بِكِتَٰبٖ فَصَّلۡنَٰهُ عَلَىٰ عِلۡمٍ هُدٗى وَرَحۡمَةٗ لِّقَوۡمٖ يُؤۡمِنُونَ", "Walaqad jinahum bikitabin fassalnahu AAala AAilmin hudan warahmatan liqawmin yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 53, "Do they await except its result? The Day its result comes those who had ignored it before will say, ""The messengers of our Lord had come with the truth, so are there [now] any intercessors to intercede for us or could we be sent back to do other than we used to do?"" They will have lost themselves, and lost from them is what they used to invent", "هَلۡ يَنظُرُونَ إِلَّا تَأۡوِيلَهُۥۚ يَوۡمَ يَأۡتِي تَأۡوِيلُهُۥ يَقُولُ ٱلَّذِينَ نَسُوهُ مِن قَبۡلُ قَدۡ جَآءَتۡ رُسُلُ رَبِّنَا بِٱلۡحَقِّ فَهَل لَّنَا مِن شُفَعَآءَ فَيَشۡفَعُواْ لَنَآ أَوۡ نُرَدُّ فَنَعۡمَلَ غَيۡرَ ٱلَّذِي كُنَّا نَعۡمَلُۚ قَدۡ خَسِرُوٓاْ أَنفُسَهُمۡ وَضَلَّ عَنۡهُم مَّا كَانُواْ يَفۡتَرُونَ", "Hal yanthuroona illa taweelahu yawma yatee taweeluhu yaqoolu allatheena nasoohu min qablu qad jaat rusulu rabbina bialhaqqi fahal lana min shufaAAaa fayashfaAAoo lana aw nuraddu fanaAAmala ghayra allathee kunna naAAmalu qad khasiroo anfusahum wadalla AAanhum ma kanoo yaftaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 54, "Indeed, your Lord is Allah, who created the heavens and earth in six days and then established Himself above the Throne. He covers the night with the day, [another night] chasing it rapidly; and [He created] the sun, the moon, and the stars, subjected by His command. Unquestionably, His is the creation and the command; blessed is Allah, Lord of the worlds", "إِنَّ رَبَّكُمُ ٱللَّهُ ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ فِي سِتَّةِ أَيَّامٖ ثُمَّ ٱسۡتَوَىٰ عَلَى ٱلۡعَرۡشِۖ يُغۡشِي ٱلَّيۡلَ ٱلنَّهَارَ يَطۡلُبُهُۥ حَثِيثٗا وَٱلشَّمۡسَ وَٱلۡقَمَرَ وَٱلنُّجُومَ مُسَخَّرَٰتِۭ بِأَمۡرِهِۦٓۗ أَلَا لَهُ ٱلۡخَلۡقُ وَٱلۡأَمۡرُۗ تَبَارَكَ ٱللَّهُ رَبُّ ٱلۡعَٰلَمِينَ", "Inna rabbakumu Allahu allathee khalaqa alssamawati waalarda fee sittati ayyamin thumma istawa AAala alAAarshi yughshee allayla alnnahara yatlubuhu hatheethan waalshshamsa waalqamara waalnnujooma musakhkharatin biamrihi ala lahu alkhalqu waalamru tabaraka Allahu rabbu alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 55, "Call upon your Lord in humility and privately; indeed, He does not like transgressors", "ٱدۡعُواْ رَبَّكُمۡ تَضَرُّعٗا وَخُفۡيَةًۚ إِنَّهُۥ لَا يُحِبُّ ٱلۡمُعۡتَدِينَ", "OdAAoo rabbakum tadarruAAan wakhufyatan innahu la yuhibbu almuAAtadeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 56, "And cause not corruption upon the earth after its reformation. And invoke Him in fear and aspiration. Indeed, the mercy of Allah is near to the doers of good", "وَلَا تُفۡسِدُواْ فِي ٱلۡأَرۡضِ بَعۡدَ إِصۡلَٰحِهَا وَٱدۡعُوهُ خَوۡفٗا وَطَمَعًاۚ إِنَّ رَحۡمَتَ ٱللَّهِ قَرِيبٞ مِّنَ ٱلۡمُحۡسِنِينَ", "Wala tufsidoo fee alardi baAAda islahiha waodAAoohu khawfan watamaAAan inna rahmata Allahi qareebun mina almuhsineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 57, "And it is He who sends the winds as good tidings before His mercy until, when they have carried heavy rainclouds, We drive them to a dead land and We send down rain therein and bring forth thereby [some] of all the fruits. Thus will We bring forth the dead; perhaps you may be reminded", "وَهُوَ ٱلَّذِي يُرۡسِلُ ٱلرِّيَٰحَ بُشۡرَۢا بَيۡنَ يَدَيۡ رَحۡمَتِهِۦۖ حَتَّىٰٓ إِذَآ أَقَلَّتۡ سَحَابٗا ثِقَالٗا سُقۡنَٰهُ لِبَلَدٖ مَّيِّتٖ فَأَنزَلۡنَا بِهِ ٱلۡمَآءَ فَأَخۡرَجۡنَا بِهِۦ مِن كُلِّ ٱلثَّمَرَٰتِۚ كَذَٰلِكَ نُخۡرِجُ ٱلۡمَوۡتَىٰ لَعَلَّكُمۡ تَذَكَّرُونَ", "Wahuwa allathee yursilu alrriyaha bushran bayna yaday rahmatihi hatta itha aqallat sahaban thiqalan suqnahu libaladin mayyitin faanzalna bihi almaa faakhrajna bihi min kulli alththamarati kathalika nukhriju almawta laAAallakum tathakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 58, "And the good land - its vegetation emerges by permission of its Lord; but that which is bad - nothing emerges except sparsely, with difficulty. Thus do We diversify the signs for a people who are grateful", "وَٱلۡبَلَدُ ٱلطَّيِّبُ يَخۡرُجُ نَبَاتُهُۥ بِإِذۡنِ رَبِّهِۦۖ وَٱلَّذِي خَبُثَ لَا يَخۡرُجُ إِلَّا نَكِدٗاۚ كَذَٰلِكَ نُصَرِّفُ ٱلۡأٓيَٰتِ لِقَوۡمٖ يَشۡكُرُونَ", "Waalbaladu alttayyibu yakhruju nabatuhu biithni rabbihi waallathee khabutha la yakhruju illa nakidan kathalika nusarrifu alayati liqawmin yashkuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 59, "We had certainly sent Noah to his people, and he said, ""O my people, worship Allah; you have no deity other than Him. Indeed, I fear for you the punishment of a tremendous Day", "لَقَدۡ أَرۡسَلۡنَا نُوحًا إِلَىٰ قَوۡمِهِۦ فَقَالَ يَٰقَوۡمِ ٱعۡبُدُواْ ٱللَّهَ مَا لَكُم مِّنۡ إِلَٰهٍ غَيۡرُهُۥٓ إِنِّيٓ أَخَافُ عَلَيۡكُمۡ عَذَابَ يَوۡمٍ عَظِيمٖ", "Laqad arsalna noohan ila qawmihi faqala ya qawmi oAAbudoo Allaha ma lakum min ilahin ghayruhu innee akhafu AAalaykum AAathaba yawmin AAatheemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 60, "Said the eminent among his people, ""Indeed, we see you in clear error", "قَالَ ٱلۡمَلَأُ مِن قَوۡمِهِۦٓ إِنَّا لَنَرَىٰكَ فِي ضَلَٰلٖ مُّبِينٖ", "Qala almalao min qawmihi inna lanaraka fee dalalin mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 61, "[Noah] said, ""O my people, there is not error in me, but I am a messenger from the Lord of the worlds", "قَالَ يَٰقَوۡمِ لَيۡسَ بِي ضَلَٰلَةٞ وَلَٰكِنِّي رَسُولٞ مِّن رَّبِّ ٱلۡعَٰلَمِينَ", "Qala ya qawmi laysa bee dalalatun walakinnee rasoolun min rabbi alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 62, "I convey to you the messages of my Lord and advise you; and I know from Allah what you do not know", "أُبَلِّغُكُمۡ رِسَٰلَٰتِ رَبِّي وَأَنصَحُ لَكُمۡ وَأَعۡلَمُ مِنَ ٱللَّهِ مَا لَا تَعۡلَمُونَ", "Oballighukum risalati rabbee waansahu lakum waaAAlamu mina Allahi ma la taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 63, "Then do you wonder that there has come to you a reminder from your Lord through a man from among you, that he may warn you and that you may fear Allah so you might receive mercy", "أَوَعَجِبۡتُمۡ أَن جَآءَكُمۡ ذِكۡرٞ مِّن رَّبِّكُمۡ عَلَىٰ رَجُلٖ مِّنكُمۡ لِيُنذِرَكُمۡ وَلِتَتَّقُواْ وَلَعَلَّكُمۡ تُرۡحَمُونَ", "AwaAAajibtum an jaakum thikrun min rabbikum AAala rajulin minkum liyunthirakum walitattaqoo walaAAallakum turhamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 64, "But they denied him, so We saved him and those who were with him in the ship. And We drowned those who denied Our signs. Indeed, they were a blind people", "فَكَذَّبُوهُ فَأَنجَيۡنَٰهُ وَٱلَّذِينَ مَعَهُۥ فِي ٱلۡفُلۡكِ وَأَغۡرَقۡنَا ٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَآۚ إِنَّهُمۡ كَانُواْ قَوۡمًا عَمِينَ", "Fakaththaboohu faanjaynahu waallatheena maAAahu fee alfulki waaghraqna allatheena kaththaboo biayatina innahum kanoo qawman AAameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 65, "And to the 'Aad [We sent] their brother Hud. He said, ""O my people, worship Allah; you have no deity other than Him. Then will you not fear Him", "۞وَإِلَىٰ عَادٍ أَخَاهُمۡ هُودٗاۚ قَالَ يَٰقَوۡمِ ٱعۡبُدُواْ ٱللَّهَ مَا لَكُم مِّنۡ إِلَٰهٍ غَيۡرُهُۥٓۚ أَفَلَا تَتَّقُونَ", "Waila AAadin akhahum hoodan qala ya qawmi oAAbudoo Allaha ma lakum min ilahin ghayruhu afala tattaqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 66, "Said the eminent ones who disbelieved among his people, ""Indeed, we see you in foolishness, and indeed, we think you are of the liars", "قَالَ ٱلۡمَلَأُ ٱلَّذِينَ كَفَرُواْ مِن قَوۡمِهِۦٓ إِنَّا لَنَرَىٰكَ فِي سَفَاهَةٖ وَإِنَّا لَنَظُنُّكَ مِنَ ٱلۡكَٰذِبِينَ", "Qala almalao allatheena kafaroo min qawmihi inna lanaraka fee safahatin wainna lanathunnuka mina alkathibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 67, "[Hud] said, ""O my people, there is not foolishness in me, but I am a messenger from the Lord of the worlds", "قَالَ يَٰقَوۡمِ لَيۡسَ بِي سَفَاهَةٞ وَلَٰكِنِّي رَسُولٞ مِّن رَّبِّ ٱلۡعَٰلَمِينَ", "Qala ya qawmi laysa bee safahatun walakinnee rasoolun min rabbi alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 68, "I convey to you the messages of my Lord, and I am to you a trustworthy adviser", "أُبَلِّغُكُمۡ رِسَٰلَٰتِ رَبِّي وَأَنَا۠ لَكُمۡ نَاصِحٌ أَمِينٌ", "Oballighukum risalati rabbee waana lakum nasihun ameenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 69, "Then do you wonder that there has come to you a reminder from your Lord through a man from among you, that he may warn you? And remember when He made you successors after the people of Noah and increased you in stature extensively. So remember the favors of Allah that you might succeed", "أَوَعَجِبۡتُمۡ أَن جَآءَكُمۡ ذِكۡرٞ مِّن رَّبِّكُمۡ عَلَىٰ رَجُلٖ مِّنكُمۡ لِيُنذِرَكُمۡۚ وَٱذۡكُرُوٓاْ إِذۡ جَعَلَكُمۡ خُلَفَآءَ مِنۢ بَعۡدِ قَوۡمِ نُوحٖ وَزَادَكُمۡ فِي ٱلۡخَلۡقِ بَصۜۡطَةٗۖ فَٱذۡكُرُوٓاْ ءَالَآءَ ٱللَّهِ لَعَلَّكُمۡ تُفۡلِحُونَ", "AwaAAajibtum an jaakum thikrun min rabbikum AAala rajulin minkum liyunthirakum waothkuroo ith jaAAalakum khulafaa min baAAdi qawmi noohin wazadakum fee alkhalqi bastatan faothkuroo alaa Allahi laAAallakum tuflihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 70, "They said, ""Have you come to us that we should worship Allah alone and leave what our fathers have worshipped? Then bring us what you promise us, if you should be of the truthful", "قَالُوٓاْ أَجِئۡتَنَا لِنَعۡبُدَ ٱللَّهَ وَحۡدَهُۥ وَنَذَرَ مَا كَانَ يَعۡبُدُ ءَابَآؤُنَا فَأۡتِنَا بِمَا تَعِدُنَآ إِن كُنتَ مِنَ ٱلصَّـٰدِقِينَ", "Qaloo ajitana linaAAbuda Allaha wahdahu wanathara ma kana yaAAbudu abaona fatina bima taAAiduna in kunta mina alssadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 71, "[Hud] said, ""Already have defilement and anger fallen upon you from your Lord. Do you dispute with me concerning [mere] names you have named them, you and your fathers, for which Allah has not sent down any authority? Then wait; indeed, I am with you among those who wait", "قَالَ قَدۡ وَقَعَ عَلَيۡكُم مِّن رَّبِّكُمۡ رِجۡسٞ وَغَضَبٌۖ أَتُجَٰدِلُونَنِي فِيٓ أَسۡمَآءٖ سَمَّيۡتُمُوهَآ أَنتُمۡ وَءَابَآؤُكُم مَّا نَزَّلَ ٱللَّهُ بِهَا مِن سُلۡطَٰنٖۚ فَٱنتَظِرُوٓاْ إِنِّي مَعَكُم مِّنَ ٱلۡمُنتَظِرِينَ", "Qala qad waqaAAa AAalaykum min rabbikum rijsun waghadabun atujadiloonanee fee asmain sammaytumooha antum waabaokum ma nazzala Allahu biha min sultanin faintathiroo innee maAAakum mina almuntathireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 72, "So We saved him and those with him by mercy from Us. And We eliminated those who denied Our signs, and they were not [at all] believers", "فَأَنجَيۡنَٰهُ وَٱلَّذِينَ مَعَهُۥ بِرَحۡمَةٖ مِّنَّا وَقَطَعۡنَا دَابِرَ ٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَاۖ وَمَا كَانُواْ مُؤۡمِنِينَ", "Faanjaynahu waallatheena maAAahu birahmatin minna waqataAAna dabira allatheena kaththaboo biayatina wama kanoo mumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 73, "And to the Thamud [We sent] their brother Salih. He said, ""O my people, worship Allah; you have no deity other than Him. There has come to you clear evidence from your Lord. This is the she-camel of Allah [sent] to you as a sign. So leave her to eat within Allah 's land and do not touch her with harm, lest there seize you a painful punishment", "وَإِلَىٰ ثَمُودَ أَخَاهُمۡ صَٰلِحٗاۚ قَالَ يَٰقَوۡمِ ٱعۡبُدُواْ ٱللَّهَ مَا لَكُم مِّنۡ إِلَٰهٍ غَيۡرُهُۥۖ قَدۡ جَآءَتۡكُم بَيِّنَةٞ مِّن رَّبِّكُمۡۖ هَٰذِهِۦ نَاقَةُ ٱللَّهِ لَكُمۡ ءَايَةٗۖ فَذَرُوهَا تَأۡكُلۡ فِيٓ أَرۡضِ ٱللَّهِۖ وَلَا تَمَسُّوهَا بِسُوٓءٖ فَيَأۡخُذَكُمۡ عَذَابٌ أَلِيمٞ", "Waila thamooda akhahum salihan qala ya qawmi oAAbudoo Allaha ma lakum min ilahin ghayruhu qad jaatkum bayyinatun min rabbikum hathihi naqatu Allahi lakum ayatan fatharooha takul fee ardi Allahi wala tamassooha bisooin fayakhuthakum AAathabun aleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 74, "And remember when He made you successors after the 'Aad and settled you in the land, [and] you take for yourselves palaces from its plains and carve from the mountains, homes. Then remember the favors of Allah and do not commit abuse on the earth, spreading corruption", "وَٱذۡكُرُوٓاْ إِذۡ جَعَلَكُمۡ خُلَفَآءَ مِنۢ بَعۡدِ عَادٖ وَبَوَّأَكُمۡ فِي ٱلۡأَرۡضِ تَتَّخِذُونَ مِن سُهُولِهَا قُصُورٗا وَتَنۡحِتُونَ ٱلۡجِبَالَ بُيُوتٗاۖ فَٱذۡكُرُوٓاْ ءَالَآءَ ٱللَّهِ وَلَا تَعۡثَوۡاْ فِي ٱلۡأَرۡضِ مُفۡسِدِينَ", "Waothkuroo ith jaAAalakum khulafaa min baAAdi AAadin wabawwaakum fee alardi tattakhithoona min suhooliha qusooran watanhitoona aljibala buyootan faothkuroo alaa Allahi wala taAAthaw fee alardi mufsideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 75, "Said the eminent ones who were arrogant among his people to those who were oppressed - to those who believed among them, ""Do you [actually] know that Salih is sent from his Lord?"" They said, ""Indeed we, in that with which he was sent, are believers", "قَالَ ٱلۡمَلَأُ ٱلَّذِينَ ٱسۡتَكۡبَرُواْ مِن قَوۡمِهِۦ لِلَّذِينَ ٱسۡتُضۡعِفُواْ لِمَنۡ ءَامَنَ مِنۡهُمۡ أَتَعۡلَمُونَ أَنَّ صَٰلِحٗا مُّرۡسَلٞ مِّن رَّبِّهِۦۚ قَالُوٓاْ إِنَّا بِمَآ أُرۡسِلَ بِهِۦ مُؤۡمِنُونَ", "Qala almalao allatheena istakbaroo min qawmihi lillatheena istudAAifoo liman amana minhum ataAAlamoona anna salihan mursalun min rabbihi qaloo inna bima orsila bihi muminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 76, "Said those who were arrogant, ""Indeed we, in that which you have believed, are disbelievers", "قَالَ ٱلَّذِينَ ٱسۡتَكۡبَرُوٓاْ إِنَّا بِٱلَّذِيٓ ءَامَنتُم بِهِۦ كَٰفِرُونَ", "Qala allatheena istakbaroo inna biallathee amantum bihi kafiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 77, "So they hamstrung the she-camel and were insolent toward the command of their Lord and said, ""O Salih, bring us what you promise us, if you should be of the messengers", "فَعَقَرُواْ ٱلنَّاقَةَ وَعَتَوۡاْ عَنۡ أَمۡرِ رَبِّهِمۡ وَقَالُواْ يَٰصَٰلِحُ ٱئۡتِنَا بِمَا تَعِدُنَآ إِن كُنتَ مِنَ ٱلۡمُرۡسَلِينَ", "FaAAaqaroo alnnaqata waAAataw AAan amri rabbihim waqaloo ya salihu itina bima taAAiduna in kunta mina almursaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 78, "So the earthquake seized them, and they became within their home [corpses] fallen prone", "فَأَخَذَتۡهُمُ ٱلرَّجۡفَةُ فَأَصۡبَحُواْ فِي دَارِهِمۡ جَٰثِمِينَ", "Faakhathathumu alrrajfatu faasbahoo fee darihim jathimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 79, "And he turned away from them and said, ""O my people, I had certainly conveyed to you the message of my Lord and advised you, but you do not like advisors", "فَتَوَلَّىٰ عَنۡهُمۡ وَقَالَ يَٰقَوۡمِ لَقَدۡ أَبۡلَغۡتُكُمۡ رِسَالَةَ رَبِّي وَنَصَحۡتُ لَكُمۡ وَلَٰكِن لَّا تُحِبُّونَ ٱلنَّـٰصِحِينَ", "Fatawalla AAanhum waqala ya qawmi laqad ablaghtukum risalata rabbee wanasahtu lakum walakin la tuhibboona alnnasiheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 80, "And [We had sent] Lot when he said to his people, ""Do you commit such immorality as no one has preceded you with from among the worlds", "وَلُوطًا إِذۡ قَالَ لِقَوۡمِهِۦٓ أَتَأۡتُونَ ٱلۡفَٰحِشَةَ مَا سَبَقَكُم بِهَا مِنۡ أَحَدٖ مِّنَ ٱلۡعَٰلَمِينَ", "Walootan ith qala liqawmihi atatoona alfahishata ma sabaqakum biha min ahadin mina alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 81, "Indeed, you approach men with desire, instead of women. Rather, you are a transgressing people", "إِنَّكُمۡ لَتَأۡتُونَ ٱلرِّجَالَ شَهۡوَةٗ مِّن دُونِ ٱلنِّسَآءِۚ بَلۡ أَنتُمۡ قَوۡمٞ مُّسۡرِفُونَ", "Innakum latatoona alrrijala shahwatan min dooni alnnisai bal antum qawmun musrifoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 82, "But the answer of his people was only that they said, ""Evict them from your city! Indeed, they are men who keep themselves pure", "وَمَا كَانَ جَوَابَ قَوۡمِهِۦٓ إِلَّآ أَن قَالُوٓاْ أَخۡرِجُوهُم مِّن قَرۡيَتِكُمۡۖ إِنَّهُمۡ أُنَاسٞ يَتَطَهَّرُونَ", "Wama kana jawaba qawmihi illa an qaloo akhrijoohum min qaryatikum innahum onasun yatatahharoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 83, "So We saved him and his family, except for his wife; she was of those who remained [with the evildoers]", "فَأَنجَيۡنَٰهُ وَأَهۡلَهُۥٓ إِلَّا ٱمۡرَأَتَهُۥ كَانَتۡ مِنَ ٱلۡغَٰبِرِينَ", "Faanjaynahu waahlahu illa imraatahu kanat mina alghabireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 84, "And We rained upon them a rain [of stones]. Then see how was the end of the criminals", "وَأَمۡطَرۡنَا عَلَيۡهِم مَّطَرٗاۖ فَٱنظُرۡ كَيۡفَ كَانَ عَٰقِبَةُ ٱلۡمُجۡرِمِينَ", "Waamtarna AAalayhim mataran faonthur kayfa kana AAaqibatu almujrimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 85, "And to [the people of] Madyan [We sent] their brother Shu'ayb. He said, ""O my people, worship Allah; you have no deity other than Him. There has come to you clear evidence from your Lord. So fulfill the measure and weight and do not deprive people of their due and cause not corruption upon the earth after its reformation. That is better for you, if you should be believers", "وَإِلَىٰ مَدۡيَنَ أَخَاهُمۡ شُعَيۡبٗاۚ قَالَ يَٰقَوۡمِ ٱعۡبُدُواْ ٱللَّهَ مَا لَكُم مِّنۡ إِلَٰهٍ غَيۡرُهُۥۖ قَدۡ جَآءَتۡكُم بَيِّنَةٞ مِّن رَّبِّكُمۡۖ فَأَوۡفُواْ ٱلۡكَيۡلَ وَٱلۡمِيزَانَ وَلَا تَبۡخَسُواْ ٱلنَّاسَ أَشۡيَآءَهُمۡ وَلَا تُفۡسِدُواْ فِي ٱلۡأَرۡضِ بَعۡدَ إِصۡلَٰحِهَاۚ ذَٰلِكُمۡ خَيۡرٞ لَّكُمۡ إِن كُنتُم مُّؤۡمِنِينَ", "Waila madyana akhahum shuAAayban qala ya qawmi oAAbudoo Allaha ma lakum min ilahin ghayruhu qad jaatkum bayyinatun min rabbikum faawfoo alkayla waalmeezana wala tabkhasoo alnnasa ashyaahum wala tufsidoo fee alardi baAAda islahiha thalikum khayrun lakum in kuntum mumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 86, "And do not sit on every path, threatening and averting from the way of Allah those who believe in Him, seeking to make it [seem] deviant. And remember when you were few and He increased you. And see how was the end of the corrupters", "وَلَا تَقۡعُدُواْ بِكُلِّ صِرَٰطٖ تُوعِدُونَ وَتَصُدُّونَ عَن سَبِيلِ ٱللَّهِ مَنۡ ءَامَنَ بِهِۦ وَتَبۡغُونَهَا عِوَجٗاۚ وَٱذۡكُرُوٓاْ إِذۡ كُنتُمۡ قَلِيلٗا فَكَثَّرَكُمۡۖ وَٱنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلۡمُفۡسِدِينَ", "Wala taqAAudoo bikulli siratin tooAAidoona watasuddoona AAan sabeeli Allahi man amana bihi watabghoonaha AAiwajan waothkuroo ith kuntum qaleelan fakaththarakum waonthuroo kayfa kana AAaqibatu almufsideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 87, "And if there should be a group among you who has believed in that with which I have been sent and a group that has not believed, then be patient until Allah judges between us. And He is the best of judges", "وَإِن كَانَ طَآئِفَةٞ مِّنكُمۡ ءَامَنُواْ بِٱلَّذِيٓ أُرۡسِلۡتُ بِهِۦ وَطَآئِفَةٞ لَّمۡ يُؤۡمِنُواْ فَٱصۡبِرُواْ حَتَّىٰ يَحۡكُمَ ٱللَّهُ بَيۡنَنَاۚ وَهُوَ خَيۡرُ ٱلۡحَٰكِمِينَ", "Wain kana taifatun minkum amanoo biallathee orsiltu bihi wataifatun lam yuminoo faisbiroo hatta yahkuma Allahu baynana wahuwa khayru alhakimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 88, "Said the eminent ones who were arrogant among his people, ""We will surely evict you, O Shu'ayb, and those who have believed with you from our city, or you must return to our religion."" He said, ""Even if we were unwilling", "۞قَالَ ٱلۡمَلَأُ ٱلَّذِينَ ٱسۡتَكۡبَرُواْ مِن قَوۡمِهِۦ لَنُخۡرِجَنَّكَ يَٰشُعَيۡبُ وَٱلَّذِينَ ءَامَنُواْ مَعَكَ مِن قَرۡيَتِنَآ أَوۡ لَتَعُودُنَّ فِي مِلَّتِنَاۚ قَالَ أَوَلَوۡ كُنَّا كَٰرِهِينَ", "Qala almalao allatheena istakbaroo min qawmihi lanukhrijannaka ya shuAAaybu waallatheena amanoo maAAaka min qaryatina aw lataAAoodunna fee millatina qala awalaw kunna kariheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 89, "We would have invented against Allah a lie if we returned to your religion after Allah had saved us from it. And it is not for us to return to it except that Allah, our Lord, should will. Our Lord has encompassed all things in knowledge. Upon Allah we have relied. Our Lord, decide between us and our people in truth, and You are the best of those who give decision", "قَدِ ٱفۡتَرَيۡنَا عَلَى ٱللَّهِ كَذِبًا إِنۡ عُدۡنَا فِي مِلَّتِكُم بَعۡدَ إِذۡ نَجَّىٰنَا ٱللَّهُ مِنۡهَاۚ وَمَا يَكُونُ لَنَآ أَن نَّعُودَ فِيهَآ إِلَّآ أَن يَشَآءَ ٱللَّهُ رَبُّنَاۚ وَسِعَ رَبُّنَا كُلَّ شَيۡءٍ عِلۡمًاۚ عَلَى ٱللَّهِ تَوَكَّلۡنَاۚ رَبَّنَا ٱفۡتَحۡ بَيۡنَنَا وَبَيۡنَ قَوۡمِنَا بِٱلۡحَقِّ وَأَنتَ خَيۡرُ ٱلۡفَٰتِحِينَ", "Qadi iftarayna AAala Allahi kathiban in AAudna fee millatikum baAAda ith najjana Allahu minha wama yakoonu lana an naAAooda feeha illa an yashaa Allahu rabbuna wasiAAa rabbuna kulla shayin AAilman AAala Allahi tawakkalna rabbana iftah baynana wabayna qawmina bialhaqqi waanta khayru alfatiheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 90, "Said the eminent ones who disbelieved among his people, ""If you should follow Shu'ayb, indeed, you would then be losers", "وَقَالَ ٱلۡمَلَأُ ٱلَّذِينَ كَفَرُواْ مِن قَوۡمِهِۦ لَئِنِ ٱتَّبَعۡتُمۡ شُعَيۡبًا إِنَّكُمۡ إِذٗا لَّخَٰسِرُونَ", "Waqala almalao allatheena kafaroo min qawmihi laini ittabaAAtum shuAAayban innakum ithan lakhasiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 91, "So the earthquake seized them, and they became within their home [corpses] fallen prone", "فَأَخَذَتۡهُمُ ٱلرَّجۡفَةُ فَأَصۡبَحُواْ فِي دَارِهِمۡ جَٰثِمِينَ", "Faakhathathumu alrrajfatu faasbahoo fee darihim jathimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 92, "Those who denied Shu'ayb - it was as though they had never resided there. Those who denied Shu'ayb - it was they who were the losers", "ٱلَّذِينَ كَذَّبُواْ شُعَيۡبٗا كَأَن لَّمۡ يَغۡنَوۡاْ فِيهَاۚ ٱلَّذِينَ كَذَّبُواْ شُعَيۡبٗا كَانُواْ هُمُ ٱلۡخَٰسِرِينَ", "Allatheena kaththaboo shuAAayban kaan lam yaghnaw feeha allatheena kaththaboo shuAAayban kanoo humu alkhasireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 93, "And he turned away from them and said, ""O my people, I had certainly conveyed to you the messages of my Lord and advised you, so how could I grieve for a disbelieving people", "فَتَوَلَّىٰ عَنۡهُمۡ وَقَالَ يَٰقَوۡمِ لَقَدۡ أَبۡلَغۡتُكُمۡ رِسَٰلَٰتِ رَبِّي وَنَصَحۡتُ لَكُمۡۖ فَكَيۡفَ ءَاسَىٰ عَلَىٰ قَوۡمٖ كَٰفِرِينَ", "Fatawalla AAanhum waqala ya qawmi laqad ablaghtukum risalati rabbee wanasahtu lakum fakayfa asa AAala qawmin kafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 94, "And We sent to no city a prophet [who was denied] except that We seized its people with poverty and hardship that they might humble themselves [to Allah]", "وَمَآ أَرۡسَلۡنَا فِي قَرۡيَةٖ مِّن نَّبِيٍّ إِلَّآ أَخَذۡنَآ أَهۡلَهَا بِٱلۡبَأۡسَآءِ وَٱلضَّرَّآءِ لَعَلَّهُمۡ يَضَّرَّعُونَ", "Wama arsalna fee qaryatin min nabiyyin illa akhathna ahlaha bialbasai waalddarrai laAAallahum yaddarraAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 95, "Then We exchanged in place of the bad [condition], good, until they increased [and prospered] and said, ""Our fathers [also] were touched with hardship and ease."" So We seized them suddenly while they did not perceive", "ثُمَّ بَدَّلۡنَا مَكَانَ ٱلسَّيِّئَةِ ٱلۡحَسَنَةَ حَتَّىٰ عَفَواْ وَّقَالُواْ قَدۡ مَسَّ ءَابَآءَنَا ٱلضَّرَّآءُ وَٱلسَّرَّآءُ فَأَخَذۡنَٰهُم بَغۡتَةٗ وَهُمۡ لَا يَشۡعُرُونَ", "Thumma baddalna makana alssayyiati alhasanata hatta AAafaw waqaloo qad massa abaana alddarrao waalssarrao faakhathnahum baghtatan wahum la yashAAuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 96, "And if only the people of the cities had believed and feared Allah, We would have opened upon them blessings from the heaven and the earth; but they denied [the messengers], so We seized them for what they were earning", "وَلَوۡ أَنَّ أَهۡلَ ٱلۡقُرَىٰٓ ءَامَنُواْ وَٱتَّقَوۡاْ لَفَتَحۡنَا عَلَيۡهِم بَرَكَٰتٖ مِّنَ ٱلسَّمَآءِ وَٱلۡأَرۡضِ وَلَٰكِن كَذَّبُواْ فَأَخَذۡنَٰهُم بِمَا كَانُواْ يَكۡسِبُونَ", "Walaw anna ahla alqura amanoo waittaqaw lafatahna AAalayhim barakatin mina alssamai waalardi walakin kaththaboo faakhathnahum bima kanoo yaksiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 97, "Then, did the people of the cities feel secure from Our punishment coming to them at night while they were asleep", "أَفَأَمِنَ أَهۡلُ ٱلۡقُرَىٰٓ أَن يَأۡتِيَهُم بَأۡسُنَا بَيَٰتٗا وَهُمۡ نَآئِمُونَ", "Afaamina ahlu alqura an yatiyahum basuna bayatan wahum naimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 98, "Or did the people of the cities feel secure from Our punishment coming to them in the morning while they were at play", "أَوَأَمِنَ أَهۡلُ ٱلۡقُرَىٰٓ أَن يَأۡتِيَهُم بَأۡسُنَا ضُحٗى وَهُمۡ يَلۡعَبُونَ", "Awa amina ahlu alqura an yatiyahum basuna duhan wahum yalAAaboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 99, "Then did they feel secure from the plan of Allah? But no one feels secure from the plan of Allah except the losing people", "أَفَأَمِنُواْ مَكۡرَ ٱللَّهِۚ فَلَا يَأۡمَنُ مَكۡرَ ٱللَّهِ إِلَّا ٱلۡقَوۡمُ ٱلۡخَٰسِرُونَ", "Afaaminoo makra Allahi fala yamanu makra Allahi illa alqawmu alkhasiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 100, "Has it not become clear to those who inherited the earth after its [previous] people that if We willed, We could afflict them for their sins? But We seal over their hearts so they do not hear", "أَوَلَمۡ يَهۡدِ لِلَّذِينَ يَرِثُونَ ٱلۡأَرۡضَ مِنۢ بَعۡدِ أَهۡلِهَآ أَن لَّوۡ نَشَآءُ أَصَبۡنَٰهُم بِذُنُوبِهِمۡۚ وَنَطۡبَعُ عَلَىٰ قُلُوبِهِمۡ فَهُمۡ لَا يَسۡمَعُونَ", "Awalam yahdi lillatheena yarithoona alarda min baAAdi ahliha an law nashao asabnahum bithunoobihim wanatbaAAu AAala quloobihim fahum la yasmaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 101, "Those cities - We relate to you, [O Muhammad], some of their news. And certainly did their messengers come to them with clear proofs, but they were not to believe in that which they had denied before. Thus does Allah seal over the hearts of the disbelievers", "تِلۡكَ ٱلۡقُرَىٰ نَقُصُّ عَلَيۡكَ مِنۡ أَنۢبَآئِهَاۚ وَلَقَدۡ جَآءَتۡهُمۡ رُسُلُهُم بِٱلۡبَيِّنَٰتِ فَمَا كَانُواْ لِيُؤۡمِنُواْ بِمَا كَذَّبُواْ مِن قَبۡلُۚ كَذَٰلِكَ يَطۡبَعُ ٱللَّهُ عَلَىٰ قُلُوبِ ٱلۡكَٰفِرِينَ", "Tilka alqura naqussu AAalayka min anbaiha walaqad jaathum rusuluhum bialbayyinati fama kanoo liyuminoo bima kaththaboo min qablu kathalika yatbaAAu Allahu AAala quloobi alkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 102, "And We did not find for most of them any covenant; but indeed, We found most of them defiantly disobedient", "وَمَا وَجَدۡنَا لِأَكۡثَرِهِم مِّنۡ عَهۡدٖۖ وَإِن وَجَدۡنَآ أَكۡثَرَهُمۡ لَفَٰسِقِينَ", "Wama wajadna liaktharihim min AAahdin wain wajadna aktharahum lafasiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 103, "Then We sent after them Moses with Our signs to Pharaoh and his establishment, but they were unjust toward them. So see how was the end of the corrupters", "ثُمَّ بَعَثۡنَا مِنۢ بَعۡدِهِم مُّوسَىٰ بِـَٔايَٰتِنَآ إِلَىٰ فِرۡعَوۡنَ وَمَلَإِيْهِۦ فَظَلَمُواْ بِهَاۖ فَٱنظُرۡ كَيۡفَ كَانَ عَٰقِبَةُ ٱلۡمُفۡسِدِينَ", "Thumma baAAathna min baAAdihim moosa biayatina ila firAAawna wamalaihi fathalamoo biha faonuthur kayfa kana AAaqibatu almufsideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 104, "And Moses said, ""O Pharaoh, I am a messenger from the Lord of the worlds", "وَقَالَ مُوسَىٰ يَٰفِرۡعَوۡنُ إِنِّي رَسُولٞ مِّن رَّبِّ ٱلۡعَٰلَمِينَ", "Waqala moosa ya firAAawnu innee rasoolun min rabbi alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 105, "[Who is] obligated not to say about Allah except the truth. I have come to you with clear evidence from your Lord, so send with me the Children of Israel", "حَقِيقٌ عَلَىٰٓ أَن لَّآ أَقُولَ عَلَى ٱللَّهِ إِلَّا ٱلۡحَقَّۚ قَدۡ جِئۡتُكُم بِبَيِّنَةٖ مِّن رَّبِّكُمۡ فَأَرۡسِلۡ مَعِيَ بَنِيٓ إِسۡرَـٰٓءِيلَ", "Haqeequn AAala an la aqoola AAala Allahi illa alhaqqa qad jitukum bibayyinatin min rabbikum faarsil maAAiya banee israeela");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 106, "[Pharaoh] said, ""If you have come with a sign, then bring it forth, if you should be of the truthful", "قَالَ إِن كُنتَ جِئۡتَ بِـَٔايَةٖ فَأۡتِ بِهَآ إِن كُنتَ مِنَ ٱلصَّـٰدِقِينَ", "Qala in kunta jita biayatin fati biha in kunta mina alssadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 107, "So Moses threw his staff, and suddenly it was a serpent, manifest", "فَأَلۡقَىٰ عَصَاهُ فَإِذَا هِيَ ثُعۡبَانٞ مُّبِينٞ", "Faalqa AAasahu faitha hiya thuAAbanun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 108, "And he drew out his hand; thereupon it was white [with radiance] for the observers", "وَنَزَعَ يَدَهُۥ فَإِذَا هِيَ بَيۡضَآءُ لِلنَّـٰظِرِينَ", "WanazaAAa yadahu faitha hiya baydao lilnnathireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 109, "Said the eminent among the people of Pharaoh, ""Indeed, this is a learned magician", "قَالَ ٱلۡمَلَأُ مِن قَوۡمِ فِرۡعَوۡنَ إِنَّ هَٰذَا لَسَٰحِرٌ عَلِيمٞ", "Qala almalao min qawmi firAAawna inna hatha lasahirun AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 110, "Who wants to expel you from your land [through magic], so what do you instruct", "يُرِيدُ أَن يُخۡرِجَكُم مِّنۡ أَرۡضِكُمۡۖ فَمَاذَا تَأۡمُرُونَ", "Yureedu an yukhrijakum min ardikum famatha tamuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 111, "They said, ""Postpone [the matter of] him and his brother and send among the cities gatherers", "قَالُوٓاْ أَرۡجِهۡ وَأَخَاهُ وَأَرۡسِلۡ فِي ٱلۡمَدَآئِنِ حَٰشِرِينَ", "Qaloo arjih waakhahu waarsil fee almadaini hashireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 112, "Who will bring you every learned magician", "يَأۡتُوكَ بِكُلِّ سَٰحِرٍ عَلِيمٖ", "Yatooka bikulli sahirin AAaleemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 113, "And the magicians came to Pharaoh. They said, ""Indeed for us is a reward if we are the predominant", "وَجَآءَ ٱلسَّحَرَةُ فِرۡعَوۡنَ قَالُوٓاْ إِنَّ لَنَا لَأَجۡرًا إِن كُنَّا نَحۡنُ ٱلۡغَٰلِبِينَ", "Wajaa alssaharatu firAAawna qaloo inna lana laajran in kunna nahnu alghalibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 114, "He said, ""Yes, and, [moreover], you will be among those made near [to me]", "قَالَ نَعَمۡ وَإِنَّكُمۡ لَمِنَ ٱلۡمُقَرَّبِينَ", "Qala naAAam wainnakum lamina almuqarrabeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 115, "They said, ""O Moses, either you throw [your staff], or we will be the ones to throw [first]", "قَالُواْ يَٰمُوسَىٰٓ إِمَّآ أَن تُلۡقِيَ وَإِمَّآ أَن نَّكُونَ نَحۡنُ ٱلۡمُلۡقِينَ", "Qaloo ya moosa imma an tulqiya waimma an nakoona nahnu almulqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 116, "He said, ""Throw,"" and when they threw, they bewitched the eyes of the people and struck terror into them, and they presented a great [feat of] magic", "قَالَ أَلۡقُواْۖ فَلَمَّآ أَلۡقَوۡاْ سَحَرُوٓاْ أَعۡيُنَ ٱلنَّاسِ وَٱسۡتَرۡهَبُوهُمۡ وَجَآءُو بِسِحۡرٍ عَظِيمٖ", "Qala alqoo falamma alqaw saharoo aAAyuna alnnasi waistarhaboohum wajaoo bisihrin AAatheemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 117, "And We inspired to Moses, ""Throw your staff,"" and at once it devoured what they were falsifying", "۞وَأَوۡحَيۡنَآ إِلَىٰ مُوسَىٰٓ أَنۡ أَلۡقِ عَصَاكَۖ فَإِذَا هِيَ تَلۡقَفُ مَا يَأۡفِكُونَ", "Waawhayna ila moosa an alqi AAasaka faitha hiya talqafu ma yafikoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 118, "So the truth was established, and abolished was what they were doing", "فَوَقَعَ ٱلۡحَقُّ وَبَطَلَ مَا كَانُواْ يَعۡمَلُونَ", "FawaqaAAa alhaqqu wabatala ma kanoo yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 119, "And Pharaoh and his people were overcome right there and became debased", "فَغُلِبُواْ هُنَالِكَ وَٱنقَلَبُواْ صَٰغِرِينَ", "Faghuliboo hunalika wainqalaboo saghireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 120, "And the magicians fell down in prostration [to Allah]", "وَأُلۡقِيَ ٱلسَّحَرَةُ سَٰجِدِينَ", "Waolqiya alssaharatu sajideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 121, "They said, ""We have believed in the Lord of the worlds", "قَالُوٓاْ ءَامَنَّا بِرَبِّ ٱلۡعَٰلَمِينَ", "Qaloo amanna birabbi alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 122, "The Lord of Moses and Aaron", "رَبِّ مُوسَىٰ وَهَٰرُونَ", "Rabbi moosa waharoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 123, "Said Pharaoh, ""You believed in him before I gave you permission. Indeed, this is a conspiracy which you conspired in the city to expel therefrom its people. But you are going to know", "قَالَ فِرۡعَوۡنُ ءَامَنتُم بِهِۦ قَبۡلَ أَنۡ ءَاذَنَ لَكُمۡۖ إِنَّ هَٰذَا لَمَكۡرٞ مَّكَرۡتُمُوهُ فِي ٱلۡمَدِينَةِ لِتُخۡرِجُواْ مِنۡهَآ أَهۡلَهَاۖ فَسَوۡفَ تَعۡلَمُونَ", "Qala firAAawnu amantum bihi qabla an athana lakum inna hatha lamakrun makartumoohu fee almadeenati litukhrijoo minha ahlaha fasawfa taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 124, "I will surely cut off your hands and your feet on opposite sides; then I will surely crucify you all", "لَأُقَطِّعَنَّ أَيۡدِيَكُمۡ وَأَرۡجُلَكُم مِّنۡ خِلَٰفٖ ثُمَّ لَأُصَلِّبَنَّكُمۡ أَجۡمَعِينَ", "LaoqatiAAanna aydiyakum waarjulakum min khilafin thumma laosallibannakum ajmaAAeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 125, "They said, ""Indeed, to our Lord we will return", "قَالُوٓاْ إِنَّآ إِلَىٰ رَبِّنَا مُنقَلِبُونَ", "Qaloo inna ila rabbina munqaliboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 126, "And you do not resent us except because we believed in the signs of our Lord when they came to us. Our Lord, pour upon us patience and let us die as Muslims [in submission to You]", "وَمَا تَنقِمُ مِنَّآ إِلَّآ أَنۡ ءَامَنَّا بِـَٔايَٰتِ رَبِّنَا لَمَّا جَآءَتۡنَاۚ رَبَّنَآ أَفۡرِغۡ عَلَيۡنَا صَبۡرٗا وَتَوَفَّنَا مُسۡلِمِينَ", "Wama tanqimu minna illa an amanna biayati rabbina lamma jaatna rabbana afrigh AAalayna sabran watawaffana muslimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 127, "And the eminent among the people of Pharaoh said,"" Will you leave Moses and his people to cause corruption in the land and abandon you and your gods?"" [Pharaoh] said, ""We will kill their sons and keep their women alive; and indeed, we are subjugators over them", "وَقَالَ ٱلۡمَلَأُ مِن قَوۡمِ فِرۡعَوۡنَ أَتَذَرُ مُوسَىٰ وَقَوۡمَهُۥ لِيُفۡسِدُواْ فِي ٱلۡأَرۡضِ وَيَذَرَكَ وَءَالِهَتَكَۚ قَالَ سَنُقَتِّلُ أَبۡنَآءَهُمۡ وَنَسۡتَحۡيِۦ نِسَآءَهُمۡ وَإِنَّا فَوۡقَهُمۡ قَٰهِرُونَ", "Waqala almalao min qawmi firAAawna atatharu moosa waqawmahu liyufsidoo fee alardi wayatharaka waalihataka qala sanuqattilu abnaahum wanastahyee nisaahum wainna fawqahum qahiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 128, "Said Moses to his people, ""Seek help through Allah and be patient. Indeed, the earth belongs to Allah. He causes to inherit it whom He wills of His servants. And the [best] outcome is for the righteous", "قَالَ مُوسَىٰ لِقَوۡمِهِ ٱسۡتَعِينُواْ بِٱللَّهِ وَٱصۡبِرُوٓاْۖ إِنَّ ٱلۡأَرۡضَ لِلَّهِ يُورِثُهَا مَن يَشَآءُ مِنۡ عِبَادِهِۦۖ وَٱلۡعَٰقِبَةُ لِلۡمُتَّقِينَ", "Qala moosa liqawmihi istaAAeenoo biAllahi waisbiroo inna alarda lillahi yoorithuha man yashao min AAibadihi waalAAaqibatu lilmuttaqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 129, "They said, ""We have been harmed before you came to us and after you have come to us."" He said, ""Perhaps your Lord will destroy your enemy and grant you succession in the land and see how you will do", "قَالُوٓاْ أُوذِينَا مِن قَبۡلِ أَن تَأۡتِيَنَا وَمِنۢ بَعۡدِ مَا جِئۡتَنَاۚ قَالَ عَسَىٰ رَبُّكُمۡ أَن يُهۡلِكَ عَدُوَّكُمۡ وَيَسۡتَخۡلِفَكُمۡ فِي ٱلۡأَرۡضِ فَيَنظُرَ كَيۡفَ تَعۡمَلُونَ", "Qaloo ootheena min qabli an tatiyana wamin baAAdi ma jitana qala AAasa rabbukum an yuhlika AAaduwwakum wayastakhlifakum fee alardi fayanthura kayfa taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 130, "And We certainly seized the people of Pharaoh with years of famine and a deficiency in fruits that perhaps they would be reminded", "وَلَقَدۡ أَخَذۡنَآ ءَالَ فِرۡعَوۡنَ بِٱلسِّنِينَ وَنَقۡصٖ مِّنَ ٱلثَّمَرَٰتِ لَعَلَّهُمۡ يَذَّكَّرُونَ", "Walaqad akhathna ala firAAawna bialssineena wanaqsin mina alththamarati laAAallahum yaththakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 131, "But when good came to them, they said, ""This is ours [by right]."" And if a bad [condition] struck them, they saw an evil omen in Moses and those with him. Unquestionably, their fortune is with Allah, but most of them do not know", "فَإِذَا جَآءَتۡهُمُ ٱلۡحَسَنَةُ قَالُواْ لَنَا هَٰذِهِۦۖ وَإِن تُصِبۡهُمۡ سَيِّئَةٞ يَطَّيَّرُواْ بِمُوسَىٰ وَمَن مَّعَهُۥٓۗ أَلَآ إِنَّمَا طَـٰٓئِرُهُمۡ عِندَ ٱللَّهِ وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَعۡلَمُونَ", "Faitha jaathumu alhasanatu qaloo lana hathihi wain tusibhum sayyiatun yattayyaroo bimoosa waman maAAahu ala innama tairuhum AAinda Allahi walakinna aktharahum la yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 132, "And they said, ""No matter what sign you bring us with which to bewitch us, we will not be believers in you", "وَقَالُواْ مَهۡمَا تَأۡتِنَا بِهِۦ مِنۡ ءَايَةٖ لِّتَسۡحَرَنَا بِهَا فَمَا نَحۡنُ لَكَ بِمُؤۡمِنِينَ", "Waqaloo mahma tatina bihi min ayatin litasharana biha fama nahnu laka bimumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 133, "So We sent upon them the flood and locusts and lice and frogs and blood as distinct signs, but they were arrogant and were a criminal people", "فَأَرۡسَلۡنَا عَلَيۡهِمُ ٱلطُّوفَانَ وَٱلۡجَرَادَ وَٱلۡقُمَّلَ وَٱلضَّفَادِعَ وَٱلدَّمَ ءَايَٰتٖ مُّفَصَّلَٰتٖ فَٱسۡتَكۡبَرُواْ وَكَانُواْ قَوۡمٗا مُّجۡرِمِينَ", "Faarsalna AAalayhimu alttoofana waaljarada waalqummala waalddafadiAAa waalddama ayatin mufassalatin faistakbaroo wakanoo qawman mujrimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 134, "And when the punishment descended upon them, they said, ""O Moses, invoke for us your Lord by what He has promised you. If you [can] remove the punishment from us, we will surely believe you, and we will send with you the Children of Israel", "وَلَمَّا وَقَعَ عَلَيۡهِمُ ٱلرِّجۡزُ قَالُواْ يَٰمُوسَى ٱدۡعُ لَنَا رَبَّكَ بِمَا عَهِدَ عِندَكَۖ لَئِن كَشَفۡتَ عَنَّا ٱلرِّجۡزَ لَنُؤۡمِنَنَّ لَكَ وَلَنُرۡسِلَنَّ مَعَكَ بَنِيٓ إِسۡرَـٰٓءِيلَ", "Walamma waqaAAa AAalayhimu alrrijzu qaloo ya moosa odAAu lana rabbaka bima AAahida AAindaka lain kashafta AAanna alrrijza lanuminanna laka walanursilanna maAAaka banee israeela");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 135, "But when We removed the punishment from them until a term which they were to reach, then at once they broke their word", "فَلَمَّا كَشَفۡنَا عَنۡهُمُ ٱلرِّجۡزَ إِلَىٰٓ أَجَلٍ هُم بَٰلِغُوهُ إِذَا هُمۡ يَنكُثُونَ", "Falamma kashafna AAanhumu alrrijza ila ajalin hum balighoohu itha hum yankuthoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 136, "So We took retribution from them, and We drowned them in the sea because they denied Our signs and were heedless of them", "فَٱنتَقَمۡنَا مِنۡهُمۡ فَأَغۡرَقۡنَٰهُمۡ فِي ٱلۡيَمِّ بِأَنَّهُمۡ كَذَّبُواْ بِـَٔايَٰتِنَا وَكَانُواْ عَنۡهَا غَٰفِلِينَ", "Faintaqamna minhum faaghraqnahum fee alyammi biannahum kaththaboo biayatina wakanoo AAanha ghafileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 137, "And We caused the people who had been oppressed to inherit the eastern regions of the land and the western ones, which We had blessed. And the good word of your Lord was fulfilled for the Children of Israel because of what they had patiently endured. And We destroyed [all] that Pharaoh and his people were producing and what they had been building", "وَأَوۡرَثۡنَا ٱلۡقَوۡمَ ٱلَّذِينَ كَانُواْ يُسۡتَضۡعَفُونَ مَشَٰرِقَ ٱلۡأَرۡضِ وَمَغَٰرِبَهَا ٱلَّتِي بَٰرَكۡنَا فِيهَاۖ وَتَمَّتۡ كَلِمَتُ رَبِّكَ ٱلۡحُسۡنَىٰ عَلَىٰ بَنِيٓ إِسۡرَـٰٓءِيلَ بِمَا صَبَرُواْۖ وَدَمَّرۡنَا مَا كَانَ يَصۡنَعُ فِرۡعَوۡنُ وَقَوۡمُهُۥ وَمَا كَانُواْ يَعۡرِشُونَ", "Waawrathna alqawma allatheena kanoo yustadAAafoona mashariqa alardi wamagharibaha allatee barakna feeha watammat kalimatu rabbika alhusna AAala banee israeela bima sabaroo wadammarna ma kana yasnaAAu firAAawnu waqawmuhu wama kanoo yaAArishoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 138, "And We took the Children of Israel across the sea; then they came upon a people intent in devotion to [some] idols of theirs. They said, ""O Moses, make for us a god just as they have gods."" He said, ""Indeed, you are a people behaving ignorantly", "وَجَٰوَزۡنَا بِبَنِيٓ إِسۡرَـٰٓءِيلَ ٱلۡبَحۡرَ فَأَتَوۡاْ عَلَىٰ قَوۡمٖ يَعۡكُفُونَ عَلَىٰٓ أَصۡنَامٖ لَّهُمۡۚ قَالُواْ يَٰمُوسَى ٱجۡعَل لَّنَآ إِلَٰهٗا كَمَا لَهُمۡ ءَالِهَةٞۚ قَالَ إِنَّكُمۡ قَوۡمٞ تَجۡهَلُونَ", "Wajawazna bibanee israeela albahra faataw AAala qawmin yaAAkufoona AAala asnamin lahum qaloo ya moosa ijAAal lana ilahan kama lahum alihatun qala innakum qawmun tajhaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 139, "Indeed, those [worshippers] - destroyed is that in which they are [engaged], and worthless is whatever they were doing", "إِنَّ هَـٰٓؤُلَآءِ مُتَبَّرٞ مَّا هُمۡ فِيهِ وَبَٰطِلٞ مَّا كَانُواْ يَعۡمَلُونَ", "Inna haolai mutabbarun ma hum feehi wabatilun ma kanoo yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 140, "He said, ""Is it other than Allah I should desire for you as a god while He has preferred you over the worlds", "قَالَ أَغَيۡرَ ٱللَّهِ أَبۡغِيكُمۡ إِلَٰهٗا وَهُوَ فَضَّلَكُمۡ عَلَى ٱلۡعَٰلَمِينَ", "Qala aghayra Allahi abgheekum ilahan wahuwa faddalakum AAala alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 141, "And [recall, O Children of Israel], when We saved you from the people of Pharaoh, [who were] afflicting you with the worst torment - killing your sons and keeping your women alive. And in that was a great trial from your Lord", "وَإِذۡ أَنجَيۡنَٰكُم مِّنۡ ءَالِ فِرۡعَوۡنَ يَسُومُونَكُمۡ سُوٓءَ ٱلۡعَذَابِ يُقَتِّلُونَ أَبۡنَآءَكُمۡ وَيَسۡتَحۡيُونَ نِسَآءَكُمۡۚ وَفِي ذَٰلِكُم بَلَآءٞ مِّن رَّبِّكُمۡ عَظِيمٞ", "Waith anjaynakum min ali firAAawna yasoomoonakum sooa alAAathabi yuqattiloona abnaakum wayastahyoona nisaakum wafee thalikum balaon min rabbikum AAatheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 142, "And We made an appointment with Moses for thirty nights and perfected them by [the addition of] ten; so the term of his Lord was completed as forty nights. And Moses said to his brother Aaron, ""Take my place among my people, do right [by them], and do not follow the way of the corrupters", "۞وَوَٰعَدۡنَا مُوسَىٰ ثَلَٰثِينَ لَيۡلَةٗ وَأَتۡمَمۡنَٰهَا بِعَشۡرٖ فَتَمَّ مِيقَٰتُ رَبِّهِۦٓ أَرۡبَعِينَ لَيۡلَةٗۚ وَقَالَ مُوسَىٰ لِأَخِيهِ هَٰرُونَ ٱخۡلُفۡنِي فِي قَوۡمِي وَأَصۡلِحۡ وَلَا تَتَّبِعۡ سَبِيلَ ٱلۡمُفۡسِدِينَ", "WawaAAadna moosa thalatheena laylatan waatmamnaha biAAashrin fatamma meeqatu rabbihi arbaAAeena laylatan waqala moosa liakheehi haroona okhlufnee fee qawmee waaslih wala tattabiAA sabeela almufsideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 143, "And when Moses arrived at Our appointed time and his Lord spoke to him, he said, ""My Lord, show me [Yourself] that I may look at You."" [Allah] said, ""You will not see Me, but look at the mountain; if it should remain in place, then you will see Me."" But when his Lord appeared to the mountain, He rendered it level, and Moses fell unconscious. And when he awoke, he said, ""Exalted are You! I have repented to You, and I am the first of the believers", "وَلَمَّا جَآءَ مُوسَىٰ لِمِيقَٰتِنَا وَكَلَّمَهُۥ رَبُّهُۥ قَالَ رَبِّ أَرِنِيٓ أَنظُرۡ إِلَيۡكَۚ قَالَ لَن تَرَىٰنِي وَلَٰكِنِ ٱنظُرۡ إِلَى ٱلۡجَبَلِ فَإِنِ ٱسۡتَقَرَّ مَكَانَهُۥ فَسَوۡفَ تَرَىٰنِيۚ فَلَمَّا تَجَلَّىٰ رَبُّهُۥ لِلۡجَبَلِ جَعَلَهُۥ دَكّٗا وَخَرَّ مُوسَىٰ صَعِقٗاۚ فَلَمَّآ أَفَاقَ قَالَ سُبۡحَٰنَكَ تُبۡتُ إِلَيۡكَ وَأَنَا۠ أَوَّلُ ٱلۡمُؤۡمِنِينَ", "Walamma jaa moosa limeeqatina wakallamahu rabbuhu qala rabbi arinee anthur ilayka qala lan taranee walakini onthur ila aljabali faini istaqarra makanahu fasawfa taranee falamma tajalla rabbuhu liljabali jaAAalahu dakkan wakharra moosa saAAiqan falamma afaqa qala subhanaka tubtu ilayka waana awwalu almumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 144, "[Allah] said, ""O Moses, I have chosen you over the people with My messages and My words [to you]. So take what I have given you and be among the grateful", "قَالَ يَٰمُوسَىٰٓ إِنِّي ٱصۡطَفَيۡتُكَ عَلَى ٱلنَّاسِ بِرِسَٰلَٰتِي وَبِكَلَٰمِي فَخُذۡ مَآ ءَاتَيۡتُكَ وَكُن مِّنَ ٱلشَّـٰكِرِينَ", "Qala ya moosa innee istafaytuka AAala alnnasi birisalatee wabikalamee fakhuth ma ataytuka wakun mina alshshakireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 145, "And We wrote for him on the tablets [something] of all things - instruction and explanation for all things, [saying], ""Take them with determination and order your people to take the best of it. I will show you the home of the defiantly disobedient", "وَكَتَبۡنَا لَهُۥ فِي ٱلۡأَلۡوَاحِ مِن كُلِّ شَيۡءٖ مَّوۡعِظَةٗ وَتَفۡصِيلٗا لِّكُلِّ شَيۡءٖ فَخُذۡهَا بِقُوَّةٖ وَأۡمُرۡ قَوۡمَكَ يَأۡخُذُواْ بِأَحۡسَنِهَاۚ سَأُوْرِيكُمۡ دَارَ ٱلۡفَٰسِقِينَ", "Wakatabna lahu fee alalwahi min kulli shayin mawAAithatan watafseelan likulli shayin fakhuthha biquwwatin wamur qawmaka yakhuthoo biahsaniha saoreekum dara alfasiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 146, "I will turn away from My signs those who are arrogant upon the earth without right; and if they should see every sign, they will not believe in it. And if they see the way of consciousness, they will not adopt it as a way; but if they see the way of error, they will adopt it as a way. That is because they have denied Our signs and they were heedless of them", "سَأَصۡرِفُ عَنۡ ءَايَٰتِيَ ٱلَّذِينَ يَتَكَبَّرُونَ فِي ٱلۡأَرۡضِ بِغَيۡرِ ٱلۡحَقِّ وَإِن يَرَوۡاْ كُلَّ ءَايَةٖ لَّا يُؤۡمِنُواْ بِهَا وَإِن يَرَوۡاْ سَبِيلَ ٱلرُّشۡدِ لَا يَتَّخِذُوهُ سَبِيلٗا وَإِن يَرَوۡاْ سَبِيلَ ٱلۡغَيِّ يَتَّخِذُوهُ سَبِيلٗاۚ ذَٰلِكَ بِأَنَّهُمۡ كَذَّبُواْ بِـَٔايَٰتِنَا وَكَانُواْ عَنۡهَا غَٰفِلِينَ", "Saasrifu AAan ayatiya allatheena yatakabbaroona fee alardi bighayri alhaqqi wain yaraw kulla ayatin la yuminoo biha wain yaraw sabeela alrrushdi la yattakhithoohu sabeelan wain yaraw sabeela alghayyi yattakhithoohu sabeelan thalika biannahum kaththaboo biayatina wakanoo AAanha ghafileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 147, "Those who denied Our signs and the meeting of the Hereafter - their deeds have become worthless. Are they recompensed except for what they used to do", "وَٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا وَلِقَآءِ ٱلۡأٓخِرَةِ حَبِطَتۡ أَعۡمَٰلُهُمۡۚ هَلۡ يُجۡزَوۡنَ إِلَّا مَا كَانُواْ يَعۡمَلُونَ", "Waallatheena kaththaboo biayatina waliqai alakhirati habitat aAAmaluhum hal yujzawna illa ma kanoo yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 148, "And the people of Moses made, after [his departure], from their ornaments a calf - an image having a lowing sound. Did they not see that it could neither speak to them nor guide them to a way? They took it [for worship], and they were wrongdoers", "وَٱتَّخَذَ قَوۡمُ مُوسَىٰ مِنۢ بَعۡدِهِۦ مِنۡ حُلِيِّهِمۡ عِجۡلٗا جَسَدٗا لَّهُۥ خُوَارٌۚ أَلَمۡ يَرَوۡاْ أَنَّهُۥ لَا يُكَلِّمُهُمۡ وَلَا يَهۡدِيهِمۡ سَبِيلًاۘ ٱتَّخَذُوهُ وَكَانُواْ ظَٰلِمِينَ", "Waittakhatha qawmu moosa min baAAdihi min huliyyihim AAijlan jasadan lahu khuwarun alam yaraw annahu la yukallimuhum wala yahdeehim sabeelan ittakhathoohu wakanoo thalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 149, "And when regret overcame them and they saw that they had gone astray, they said, ""If our Lord does not have mercy upon us and forgive us, we will surely be among the losers", "وَلَمَّا سُقِطَ فِيٓ أَيۡدِيهِمۡ وَرَأَوۡاْ أَنَّهُمۡ قَدۡ ضَلُّواْ قَالُواْ لَئِن لَّمۡ يَرۡحَمۡنَا رَبُّنَا وَيَغۡفِرۡ لَنَا لَنَكُونَنَّ مِنَ ٱلۡخَٰسِرِينَ", "Walamma suqita fee aydeehim waraaw annahum qad dalloo qaloo lain lam yarhamna rabbuna wayaghfir lana lanakoonanna mina alkhasireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 150, "And when Moses returned to his people, angry and grieved, he said, ""How wretched is that by which you have replaced me after [my departure]. Were you impatient over the matter of your Lord?"" And he threw down the tablets and seized his brother by [the hair of] his head, pulling him toward him. [Aaron] said, ""O son of my mother, indeed the people oppressed me and were about to kill me, so let not the enemies rejoice over me and do not place me among the wrongdoing people", "وَلَمَّا رَجَعَ مُوسَىٰٓ إِلَىٰ قَوۡمِهِۦ غَضۡبَٰنَ أَسِفٗا قَالَ بِئۡسَمَا خَلَفۡتُمُونِي مِنۢ بَعۡدِيٓۖ أَعَجِلۡتُمۡ أَمۡرَ رَبِّكُمۡۖ وَأَلۡقَى ٱلۡأَلۡوَاحَ وَأَخَذَ بِرَأۡسِ أَخِيهِ يَجُرُّهُۥٓ إِلَيۡهِۚ قَالَ ٱبۡنَ أُمَّ إِنَّ ٱلۡقَوۡمَ ٱسۡتَضۡعَفُونِي وَكَادُواْ يَقۡتُلُونَنِي فَلَا تُشۡمِتۡ بِيَ ٱلۡأَعۡدَآءَ وَلَا تَجۡعَلۡنِي مَعَ ٱلۡقَوۡمِ ٱلظَّـٰلِمِينَ", "Walamma rajaAAa moosa ila qawmihi ghadbana asifan qala bisama khalaftumoonee min baAAdee aAAajiltum amra rabbikum waalqa alalwaha waakhatha birasi akheehi yajurruhu ilayhi qala ibna omma inna alqawma istadAAafoonee wakadoo yaqtuloonanee fala tushmit biya alaAAdaa wala tajAAalnee maAAa alqawmi alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 151, "[Moses] said, ""My Lord, forgive me and my brother and admit us into Your mercy, for You are the most merciful of the merciful", "قَالَ رَبِّ ٱغۡفِرۡ لِي وَلِأَخِي وَأَدۡخِلۡنَا فِي رَحۡمَتِكَۖ وَأَنتَ أَرۡحَمُ ٱلرَّـٰحِمِينَ", "Qala rabbi ighfir lee waliakhee waadkhilna fee rahmatika waanta arhamu alrrahimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 152, "Indeed, those who took the calf [for worship] will obtain anger from their Lord and humiliation in the life of this world, and thus do We recompense the inventors [of falsehood]", "إِنَّ ٱلَّذِينَ ٱتَّخَذُواْ ٱلۡعِجۡلَ سَيَنَالُهُمۡ غَضَبٞ مِّن رَّبِّهِمۡ وَذِلَّةٞ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۚ وَكَذَٰلِكَ نَجۡزِي ٱلۡمُفۡتَرِينَ", "Inna allatheena ittakhathoo alAAijla sayanaluhum ghadabun min rabbihim wathillatun fee alhayati alddunya wakathalika najzee almuftareena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 153, "But those who committed misdeeds and then repented after them and believed - indeed your Lord, thereafter, is Forgiving and Merciful", "وَٱلَّذِينَ عَمِلُواْ ٱلسَّيِّـَٔاتِ ثُمَّ تَابُواْ مِنۢ بَعۡدِهَا وَءَامَنُوٓاْ إِنَّ رَبَّكَ مِنۢ بَعۡدِهَا لَغَفُورٞ رَّحِيمٞ", "Waallatheena AAamiloo alssayyiati thumma taboo min baAAdiha waamanoo inna rabbaka min baAAdiha laghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 154, "And when the anger subsided in Moses, he took up the tablets; and in their inscription was guidance and mercy for those who are fearful of their Lord", "وَلَمَّا سَكَتَ عَن مُّوسَى ٱلۡغَضَبُ أَخَذَ ٱلۡأَلۡوَاحَۖ وَفِي نُسۡخَتِهَا هُدٗى وَرَحۡمَةٞ لِّلَّذِينَ هُمۡ لِرَبِّهِمۡ يَرۡهَبُونَ", "Walamma sakata AAan moosa alghadabu akhatha alalwaha wafee nuskhatiha hudan warahmatun lillatheena hum lirabbihim yarhaboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 155, "And Moses chose from his people seventy men for Our appointment. And when the earthquake seized them, he said, ""My Lord, if You had willed, You could have destroyed them before and me [as well]. Would You destroy us for what the foolish among us have done? This is not but Your trial by which You send astray whom You will and guide whom You will. You are our Protector, so forgive us and have mercy upon us; and You are the best of forgivers", "وَٱخۡتَارَ مُوسَىٰ قَوۡمَهُۥ سَبۡعِينَ رَجُلٗا لِّمِيقَٰتِنَاۖ فَلَمَّآ أَخَذَتۡهُمُ ٱلرَّجۡفَةُ قَالَ رَبِّ لَوۡ شِئۡتَ أَهۡلَكۡتَهُم مِّن قَبۡلُ وَإِيَّـٰيَۖ أَتُهۡلِكُنَا بِمَا فَعَلَ ٱلسُّفَهَآءُ مِنَّآۖ إِنۡ هِيَ إِلَّا فِتۡنَتُكَ تُضِلُّ بِهَا مَن تَشَآءُ وَتَهۡدِي مَن تَشَآءُۖ أَنتَ وَلِيُّنَا فَٱغۡفِرۡ لَنَا وَٱرۡحَمۡنَاۖ وَأَنتَ خَيۡرُ ٱلۡغَٰفِرِينَ", "Waikhtara moosa qawmahu sabAAeena rajulan limeeqatina falamma akhathathumu alrrajfatu qala rabbi law shita ahlaktahum min qablu waiyyaya atuhlikuna bima faAAala alssufahao minna in hiya illa fitnatuka tudillu biha man tashao watahdee man tashao anta waliyyuna faighfir lana wairhamna waanta khayru alghafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 156, "And decree for us in this world [that which is] good and [also] in the Hereafter; indeed, we have turned back to You."" [Allah] said, ""My punishment - I afflict with it whom I will, but My mercy encompasses all things."" So I will decree it [especially] for those who fear Me and give zakah and those who believe in Our verses", "۞وَٱكۡتُبۡ لَنَا فِي هَٰذِهِ ٱلدُّنۡيَا حَسَنَةٗ وَفِي ٱلۡأٓخِرَةِ إِنَّا هُدۡنَآ إِلَيۡكَۚ قَالَ عَذَابِيٓ أُصِيبُ بِهِۦ مَنۡ أَشَآءُۖ وَرَحۡمَتِي وَسِعَتۡ كُلَّ شَيۡءٖۚ فَسَأَكۡتُبُهَا لِلَّذِينَ يَتَّقُونَ وَيُؤۡتُونَ ٱلزَّكَوٰةَ وَٱلَّذِينَ هُم بِـَٔايَٰتِنَا يُؤۡمِنُونَ", "Waoktub lana fee hathihi alddunya hasanatan wafee alakhirati inna hudna ilayka qala AAathabee oseebu bihi man ashao warahmatee wasiAAat kulla shayin fasaaktubuha lillatheena yattaqoona wayutoona alzzakata waallatheena hum biayatina yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 157, "Those who follow the Messenger, the unlettered prophet, whom they find written in what they have of the Torah and the Gospel, who enjoins upon them what is right and forbids them what is wrong and makes lawful for them the good things and prohibits for them the evil and relieves them of their burden and the shackles which were upon them. So they who have believed in him, honored him, supported him and followed the light which was sent down with him - it is those who will be the successful", "ٱلَّذِينَ يَتَّبِعُونَ ٱلرَّسُولَ ٱلنَّبِيَّ ٱلۡأُمِّيَّ ٱلَّذِي يَجِدُونَهُۥ مَكۡتُوبًا عِندَهُمۡ فِي ٱلتَّوۡرَىٰةِ وَٱلۡإِنجِيلِ يَأۡمُرُهُم بِٱلۡمَعۡرُوفِ وَيَنۡهَىٰهُمۡ عَنِ ٱلۡمُنكَرِ وَيُحِلُّ لَهُمُ ٱلطَّيِّبَٰتِ وَيُحَرِّمُ عَلَيۡهِمُ ٱلۡخَبَـٰٓئِثَ وَيَضَعُ عَنۡهُمۡ إِصۡرَهُمۡ وَٱلۡأَغۡلَٰلَ ٱلَّتِي كَانَتۡ عَلَيۡهِمۡۚ فَٱلَّذِينَ ءَامَنُواْ بِهِۦ وَعَزَّرُوهُ وَنَصَرُوهُ وَٱتَّبَعُواْ ٱلنُّورَ ٱلَّذِيٓ أُنزِلَ مَعَهُۥٓ أُوْلَـٰٓئِكَ هُمُ ٱلۡمُفۡلِحُونَ", "Allatheena yattabiAAoona alrrasoola alnnabiyya alommiyya allathee yajidoonahu maktooban AAindahum fee alttawrati waalinjeeli yamuruhum bialmaAAroofi wayanhahum AAani almunkari wayuhillu lahumu alttayyibati wayuharrimu AAalayhimu alkhabaitha wayadaAAu AAanhum israhum waalaghlala allatee kanat AAalayhim faallatheena amanoo bihi waAAazzaroohu wanasaroohu waittabaAAoo alnnoora allathee onzila maAAahu olaika humu almuflihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 158, "Say, [O Muhammad], ""O mankind, indeed I am the Messenger of Allah to you all, [from Him] to whom belongs the dominion of the heavens and the earth. There is no deity except Him; He gives life and causes death."" So believe in Allah and His Messenger, the unlettered prophet, who believes in Allah and His words, and follow him that you may be guided", "قُلۡ يَـٰٓأَيُّهَا ٱلنَّاسُ إِنِّي رَسُولُ ٱللَّهِ إِلَيۡكُمۡ جَمِيعًا ٱلَّذِي لَهُۥ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ لَآ إِلَٰهَ إِلَّا هُوَ يُحۡيِۦ وَيُمِيتُۖ فَـَٔامِنُواْ بِٱللَّهِ وَرَسُولِهِ ٱلنَّبِيِّ ٱلۡأُمِّيِّ ٱلَّذِي يُؤۡمِنُ بِٱللَّهِ وَكَلِمَٰتِهِۦ وَٱتَّبِعُوهُ لَعَلَّكُمۡ تَهۡتَدُونَ", "Qul ya ayyuha alnnasu innee rasoolu Allahi ilaykum jameeAAan allathee lahu mulku alssamawati waalardi la ilaha illa huwa yuhyee wayumeetu faaminoo biAllahi warasoolihi alnnabiyyi alommiyyi allathee yuminu biAllahi wakalimatihi waittabiAAoohu laAAallakum tahtadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 159, "And among the people of Moses is a community which guides by truth and by it establishes justice", "وَمِن قَوۡمِ مُوسَىٰٓ أُمَّةٞ يَهۡدُونَ بِٱلۡحَقِّ وَبِهِۦ يَعۡدِلُونَ", "Wamin qawmi moosa ommatun yahdoona bialhaqqi wabihi yaAAdiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 160, "And We divided them into twelve descendant tribes [as distinct] nations. And We inspired to Moses when his people implored him for water, ""Strike with your staff the stone,"" and there gushed forth from it twelve springs. Every people knew its watering place. And We shaded them with clouds and sent down upon them manna and quails, [saying], ""Eat from the good things with which We have provided you."" And they wronged Us not, but they were [only] wronging themselves", "وَقَطَّعۡنَٰهُمُ ٱثۡنَتَيۡ عَشۡرَةَ أَسۡبَاطًا أُمَمٗاۚ وَأَوۡحَيۡنَآ إِلَىٰ مُوسَىٰٓ إِذِ ٱسۡتَسۡقَىٰهُ قَوۡمُهُۥٓ أَنِ ٱضۡرِب بِّعَصَاكَ ٱلۡحَجَرَۖ فَٱنۢبَجَسَتۡ مِنۡهُ ٱثۡنَتَا عَشۡرَةَ عَيۡنٗاۖ قَدۡ عَلِمَ كُلُّ أُنَاسٖ مَّشۡرَبَهُمۡۚ وَظَلَّلۡنَا عَلَيۡهِمُ ٱلۡغَمَٰمَ وَأَنزَلۡنَا عَلَيۡهِمُ ٱلۡمَنَّ وَٱلسَّلۡوَىٰۖ كُلُواْ مِن طَيِّبَٰتِ مَا رَزَقۡنَٰكُمۡۚ وَمَا ظَلَمُونَا وَلَٰكِن كَانُوٓاْ أَنفُسَهُمۡ يَظۡلِمُونَ", "WaqattaAAnahumu ithnatay AAashrata asbatan omaman waawhayna ila moosa ithi istasqahu qawmuhu ani idrib biAAasaka alhajara fainbajasat minhu ithnata AAashrata AAaynan qad AAalima kullu onasin mashrabahum wathallalna AAalayhimu alghamama waanzalna AAalayhimu almanna waalssalwa kuloo min tayyibati ma razaqnakum wama thalamoona walakin kanoo anfusahum yathlimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 161, "And [mention, O Muhammad], when it was said to them, ""Dwell in this city and eat from it wherever you will and say, 'Relieve us of our burdens,' and enter the gate bowing humbly; We will [then] forgive you your sins. We will increase the doers of good [in goodness and reward]", "وَإِذۡ قِيلَ لَهُمُ ٱسۡكُنُواْ هَٰذِهِ ٱلۡقَرۡيَةَ وَكُلُواْ مِنۡهَا حَيۡثُ شِئۡتُمۡ وَقُولُواْ حِطَّةٞ وَٱدۡخُلُواْ ٱلۡبَابَ سُجَّدٗا نَّغۡفِرۡ لَكُمۡ خَطِيٓـَٰٔتِكُمۡۚ سَنَزِيدُ ٱلۡمُحۡسِنِينَ", "Waith qeela lahumu oskunoo hathihi alqaryata wakuloo minha haythu shitum waqooloo hittatun waodkhuloo albaba sujjadan naghfir lakum khateeatikum sanazeedu almuhsineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 162, "But those who wronged among them changed [the words] to a statement other than that which had been said to them. So We sent upon them a punishment from the sky for the wrong that they were doing", "فَبَدَّلَ ٱلَّذِينَ ظَلَمُواْ مِنۡهُمۡ قَوۡلًا غَيۡرَ ٱلَّذِي قِيلَ لَهُمۡ فَأَرۡسَلۡنَا عَلَيۡهِمۡ رِجۡزٗا مِّنَ ٱلسَّمَآءِ بِمَا كَانُواْ يَظۡلِمُونَ", "Fabaddala allatheena thalamoo minhum qawlan ghayra allathee qeela lahum faarsalna AAalayhim rijzan mina alssamai bima kanoo yathlimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 163, "And ask them about the town that was by the sea - when they transgressed in [the matter of] the sabbath - when their fish came to them openly on their sabbath day, and the day they had no sabbath they did not come to them. Thus did We give them trial because they were defiantly disobedient", "وَسۡـَٔلۡهُمۡ عَنِ ٱلۡقَرۡيَةِ ٱلَّتِي كَانَتۡ حَاضِرَةَ ٱلۡبَحۡرِ إِذۡ يَعۡدُونَ فِي ٱلسَّبۡتِ إِذۡ تَأۡتِيهِمۡ حِيتَانُهُمۡ يَوۡمَ سَبۡتِهِمۡ شُرَّعٗا وَيَوۡمَ لَا يَسۡبِتُونَ لَا تَأۡتِيهِمۡۚ كَذَٰلِكَ نَبۡلُوهُم بِمَا كَانُواْ يَفۡسُقُونَ", "Waisalhum AAani alqaryati allatee kanat hadirata albahri ith yaAAdoona fee alssabti ith tateehim heetanuhum yawma sabtihim shurraAAan wayawma la yasbitoona la tateehim kathalika nabloohum bima kanoo yafsuqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 164, "And when a community among them said, ""Why do you advise [or warn] a people whom Allah is [about] to destroy or to punish with a severe punishment?"" they [the advisors] said, ""To be absolved before your Lord and perhaps they may fear Him", "وَإِذۡ قَالَتۡ أُمَّةٞ مِّنۡهُمۡ لِمَ تَعِظُونَ قَوۡمًا ٱللَّهُ مُهۡلِكُهُمۡ أَوۡ مُعَذِّبُهُمۡ عَذَابٗا شَدِيدٗاۖ قَالُواْ مَعۡذِرَةً إِلَىٰ رَبِّكُمۡ وَلَعَلَّهُمۡ يَتَّقُونَ", "Waith qalat ommatun minhum lima taAAithoona qawman Allahu muhlikuhum aw muAAaththibuhum AAathaban shadeedan qaloo maAAthiratan ila rabbikum walaAAallahum yattaqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 165, "And when they forgot that by which they had been reminded, We saved those who had forbidden evil and seized those who wronged, with a wretched punishment, because they were defiantly disobeying", "فَلَمَّا نَسُواْ مَا ذُكِّرُواْ بِهِۦٓ أَنجَيۡنَا ٱلَّذِينَ يَنۡهَوۡنَ عَنِ ٱلسُّوٓءِ وَأَخَذۡنَا ٱلَّذِينَ ظَلَمُواْ بِعَذَابِۭ بَـِٔيسِۭ بِمَا كَانُواْ يَفۡسُقُونَ", "Falamma nasoo ma thukkiroo bihi anjayna allatheena yanhawna AAani alssooi waakhathna allatheena thalamoo biAAathabin baeesin bima kanoo yafsuqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 166, "So when they were insolent about that which they had been forbidden, We said to them, ""Be apes, despised", "فَلَمَّا عَتَوۡاْ عَن مَّا نُهُواْ عَنۡهُ قُلۡنَا لَهُمۡ كُونُواْ قِرَدَةً خَٰسِـِٔينَ", "Falamma AAataw AAan ma nuhoo AAanhu qulna lahum koonoo qiradatan khasieena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 167, "And [mention] when your Lord declared that He would surely [continue to] send upon them until the Day of Resurrection those who would afflict them with the worst torment. Indeed, your Lord is swift in penalty; but indeed, He is Forgiving and Merciful", "وَإِذۡ تَأَذَّنَ رَبُّكَ لَيَبۡعَثَنَّ عَلَيۡهِمۡ إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِ مَن يَسُومُهُمۡ سُوٓءَ ٱلۡعَذَابِۗ إِنَّ رَبَّكَ لَسَرِيعُ ٱلۡعِقَابِ وَإِنَّهُۥ لَغَفُورٞ رَّحِيمٞ", "Waith taaththana rabbuka layabAAathanna AAalayhim ila yawmi alqiyamati man yasoomuhum sooa alAAathabi inna rabbaka lasareeAAu alAAiqabi wainnahu laghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 168, "And We divided them throughout the earth into nations. Of them some were righteous, and of them some were otherwise. And We tested them with good [times] and bad that perhaps they would return [to obedience]", "وَقَطَّعۡنَٰهُمۡ فِي ٱلۡأَرۡضِ أُمَمٗاۖ مِّنۡهُمُ ٱلصَّـٰلِحُونَ وَمِنۡهُمۡ دُونَ ذَٰلِكَۖ وَبَلَوۡنَٰهُم بِٱلۡحَسَنَٰتِ وَٱلسَّيِّـَٔاتِ لَعَلَّهُمۡ يَرۡجِعُونَ", "WaqattaAAnahum fee alardi omaman minhumu alssalihoona waminhum doona thalika wabalawnahum bialhasanati waalssayyiati laAAallahum yarjiAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 169, "And there followed them successors who inherited the Scripture [while] taking the commodities of this lower life and saying, ""It will be forgiven for us."" And if an offer like it comes to them, they will [again] take it. Was not the covenant of the Scripture taken from them that they would not say about Allah except the truth, and they studied what was in it? And the home of the Hereafter is better for those who fear Allah, so will you not use reason", "فَخَلَفَ مِنۢ بَعۡدِهِمۡ خَلۡفٞ وَرِثُواْ ٱلۡكِتَٰبَ يَأۡخُذُونَ عَرَضَ هَٰذَا ٱلۡأَدۡنَىٰ وَيَقُولُونَ سَيُغۡفَرُ لَنَا وَإِن يَأۡتِهِمۡ عَرَضٞ مِّثۡلُهُۥ يَأۡخُذُوهُۚ أَلَمۡ يُؤۡخَذۡ عَلَيۡهِم مِّيثَٰقُ ٱلۡكِتَٰبِ أَن لَّا يَقُولُواْ عَلَى ٱللَّهِ إِلَّا ٱلۡحَقَّ وَدَرَسُواْ مَا فِيهِۗ وَٱلدَّارُ ٱلۡأٓخِرَةُ خَيۡرٞ لِّلَّذِينَ يَتَّقُونَۚ أَفَلَا تَعۡقِلُونَ", "Fakhalafa min baAAdihim khalfun warithoo alkitaba yakhuthoona AAarada hatha aladna wayaqooloona sayughfaru lana wain yatihim AAaradun mithluhu yakhuthoohu alam yukhath AAalayhim meethaqu alkitabi an la yaqooloo AAala Allahi illa alhaqqa wadarasoo ma feehi waalddaru alakhiratu khayrun lillatheena yattaqoona afala taAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 170, "But those who hold fast to the Book and establish prayer - indeed, We will not allow to be lost the reward of the reformers", "وَٱلَّذِينَ يُمَسِّكُونَ بِٱلۡكِتَٰبِ وَأَقَامُواْ ٱلصَّلَوٰةَ إِنَّا لَا نُضِيعُ أَجۡرَ ٱلۡمُصۡلِحِينَ", "Waallatheena yumassikoona bialkitabi waaqamoo alssalata inna la nudeeAAu ajra almusliheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 171, "And [mention] when We raised the mountain above them as if it was a dark cloud and they were certain that it would fall upon them, [and Allah said], ""Take what We have given you with determination and remember what is in it that you might fear Allah", "۞وَإِذۡ نَتَقۡنَا ٱلۡجَبَلَ فَوۡقَهُمۡ كَأَنَّهُۥ ظُلَّةٞ وَظَنُّوٓاْ أَنَّهُۥ وَاقِعُۢ بِهِمۡ خُذُواْ مَآ ءَاتَيۡنَٰكُم بِقُوَّةٖ وَٱذۡكُرُواْ مَا فِيهِ لَعَلَّكُمۡ تَتَّقُونَ", "Waith nataqna aljabala fawqahum kaannahu thullatun wathannoo annahu waqiAAun bihim khuthoo ma ataynakum biquwwatin waothkuroo ma feehi laAAallakum tattaqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 172, "And [mention] when your Lord took from the children of Adam - from their loins - their descendants and made them testify of themselves, [saying to them], ""Am I not your Lord?"" They said, ""Yes, we have testified."" [This] - lest you should say on the day of Resurrection, ""Indeed, we were of this unaware", "وَإِذۡ أَخَذَ رَبُّكَ مِنۢ بَنِيٓ ءَادَمَ مِن ظُهُورِهِمۡ ذُرِّيَّتَهُمۡ وَأَشۡهَدَهُمۡ عَلَىٰٓ أَنفُسِهِمۡ أَلَسۡتُ بِرَبِّكُمۡۖ قَالُواْ بَلَىٰ شَهِدۡنَآۚ أَن تَقُولُواْ يَوۡمَ ٱلۡقِيَٰمَةِ إِنَّا كُنَّا عَنۡ هَٰذَا غَٰفِلِينَ", "Waith akhatha rabbuka min banee adama min thuhoorihim thurriyyatahum waashhadahum AAala anfusihim alastu birabbikum qaloo bala shahidna an taqooloo yawma alqiyamati inna kunna AAan hatha ghafileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 173, "Or [lest] you say, ""It was only that our fathers associated [others in worship] with Allah before, and we were but descendants after them. Then would You destroy us for what the falsifiers have done", "أَوۡ تَقُولُوٓاْ إِنَّمَآ أَشۡرَكَ ءَابَآؤُنَا مِن قَبۡلُ وَكُنَّا ذُرِّيَّةٗ مِّنۢ بَعۡدِهِمۡۖ أَفَتُهۡلِكُنَا بِمَا فَعَلَ ٱلۡمُبۡطِلُونَ", "Aw taqooloo innama ashraka abaona min qablu wakunna thurriyyatan min baAAdihim afatuhlikuna bima faAAala almubtiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 174, "And thus do We [explain in] detail the verses, and perhaps they will return", "وَكَذَٰلِكَ نُفَصِّلُ ٱلۡأٓيَٰتِ وَلَعَلَّهُمۡ يَرۡجِعُونَ", "Wakathalika nufassilu alayati walaAAallahum yarjiAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 175, "And recite to them, [O Muhammad], the news of him to whom we gave [knowledge of] Our signs, but he detached himself from them; so Satan pursued him, and he became of the deviators", "وَٱتۡلُ عَلَيۡهِمۡ نَبَأَ ٱلَّذِيٓ ءَاتَيۡنَٰهُ ءَايَٰتِنَا فَٱنسَلَخَ مِنۡهَا فَأَتۡبَعَهُ ٱلشَّيۡطَٰنُ فَكَانَ مِنَ ٱلۡغَاوِينَ", "Waotlu AAalayhim nabaa allathee ataynahu ayatina fainsalakha minha faatbaAAahu alshshaytanu fakana mina alghaweena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 176, "And if We had willed, we could have elevated him thereby, but he adhered [instead] to the earth and followed his own desire. So his example is like that of the dog: if you chase him, he pants, or if you leave him, he [still] pants. That is the example of the people who denied Our signs. So relate the stories that perhaps they will give thought", "وَلَوۡ شِئۡنَا لَرَفَعۡنَٰهُ بِهَا وَلَٰكِنَّهُۥٓ أَخۡلَدَ إِلَى ٱلۡأَرۡضِ وَٱتَّبَعَ هَوَىٰهُۚ فَمَثَلُهُۥ كَمَثَلِ ٱلۡكَلۡبِ إِن تَحۡمِلۡ عَلَيۡهِ يَلۡهَثۡ أَوۡ تَتۡرُكۡهُ يَلۡهَثۚ ذَّـٰلِكَ مَثَلُ ٱلۡقَوۡمِ ٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَاۚ فَٱقۡصُصِ ٱلۡقَصَصَ لَعَلَّهُمۡ يَتَفَكَّرُونَ", "Walaw shina larafaAAnahu biha walakinnahu akhlada ila alardi waittabaAAa hawahu famathaluhu kamathali alkalbi in tahmil AAalayhi yalhath aw tatrukhu yalhath thalika mathalu alqawmi allatheena kaththaboo biayatina faoqsusi alqasasa laAAallahum yatafakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 177, "How evil an example [is that of] the people who denied Our signs and used to wrong themselves", "سَآءَ مَثَلًا ٱلۡقَوۡمُ ٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا وَأَنفُسَهُمۡ كَانُواْ يَظۡلِمُونَ", "Saa mathalan alqawmu allatheena kaththaboo biayatina waanfusahum kanoo yathlimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 178, "Whoever Allah guides - he is the [rightly] guided; and whoever He sends astray - it is those who are the losers", "مَن يَهۡدِ ٱللَّهُ فَهُوَ ٱلۡمُهۡتَدِيۖ وَمَن يُضۡلِلۡ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡخَٰسِرُونَ", "Man yahdi Allahu fahuwa almuhtadee waman yudlil faolaika humu alkhasiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 179, "And We have certainly created for Hell many of the jinn and mankind. They have hearts with which they do not understand, they have eyes with which they do not see, and they have ears with which they do not hear. Those are like livestock; rather, they are more astray. It is they who are the heedless", "وَلَقَدۡ ذَرَأۡنَا لِجَهَنَّمَ كَثِيرٗا مِّنَ ٱلۡجِنِّ وَٱلۡإِنسِۖ لَهُمۡ قُلُوبٞ لَّا يَفۡقَهُونَ بِهَا وَلَهُمۡ أَعۡيُنٞ لَّا يُبۡصِرُونَ بِهَا وَلَهُمۡ ءَاذَانٞ لَّا يَسۡمَعُونَ بِهَآۚ أُوْلَـٰٓئِكَ كَٱلۡأَنۡعَٰمِ بَلۡ هُمۡ أَضَلُّۚ أُوْلَـٰٓئِكَ هُمُ ٱلۡغَٰفِلُونَ", "Walaqad tharana lijahannama katheeran mina aljinni waalinsi lahum quloobun la yafqahoona biha walahum aAAyunun la yubsiroona biha walahum athanun la yasmaAAoona biha olaika kaalanAAami bal hum adallu olaika humu alghafiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 180, "And to Allah belong the best names, so invoke Him by them. And leave [the company of] those who practice deviation concerning His names. They will be recompensed for what they have been doing", "وَلِلَّهِ ٱلۡأَسۡمَآءُ ٱلۡحُسۡنَىٰ فَٱدۡعُوهُ بِهَاۖ وَذَرُواْ ٱلَّذِينَ يُلۡحِدُونَ فِيٓ أَسۡمَـٰٓئِهِۦۚ سَيُجۡزَوۡنَ مَا كَانُواْ يَعۡمَلُونَ", "Walillahi alasmao alhusna faodAAoohu biha watharoo allatheena yulhidoona fee asmaihi sayujzawna ma kanoo yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 181, "And among those We created is a community which guides by truth and thereby establishes justice", "وَمِمَّنۡ خَلَقۡنَآ أُمَّةٞ يَهۡدُونَ بِٱلۡحَقِّ وَبِهِۦ يَعۡدِلُونَ", "Wamimman khalaqna ommatun yahdoona bialhaqqi wabihi yaAAdiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 182, "But those who deny Our signs - We will progressively lead them [to destruction] from where they do not know", "وَٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا سَنَسۡتَدۡرِجُهُم مِّنۡ حَيۡثُ لَا يَعۡلَمُونَ", "Waallatheena kaththaboo biayatina sanastadrijuhum min haythu la yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 183, "And I will give them time. Indeed, my plan is firm", "وَأُمۡلِي لَهُمۡۚ إِنَّ كَيۡدِي مَتِينٌ", "Waomlee lahum inna kaydee mateenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 184, "Then do they not give thought? There is in their companion [Muhammad] no madness. He is not but a clear warner", "أَوَلَمۡ يَتَفَكَّرُواْۗ مَا بِصَاحِبِهِم مِّن جِنَّةٍۚ إِنۡ هُوَ إِلَّا نَذِيرٞ مُّبِينٌ", "Awalam yatafakkaroo ma bisahibihim min jinnatin in huwa illa natheerun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 185, "Do they not look into the realm of the heavens and the earth and everything that Allah has created and [think] that perhaps their appointed time has come near? So in what statement hereafter will they believe", "أَوَلَمۡ يَنظُرُواْ فِي مَلَكُوتِ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا خَلَقَ ٱللَّهُ مِن شَيۡءٖ وَأَنۡ عَسَىٰٓ أَن يَكُونَ قَدِ ٱقۡتَرَبَ أَجَلُهُمۡۖ فَبِأَيِّ حَدِيثِۭ بَعۡدَهُۥ يُؤۡمِنُونَ", "Awalam yanthuroo fee malakooti alssamawati waalardi wama khalaqa Allahu min shayin waan AAasa an yakoona qadi iqtaraba ajaluhum fabiayyi hadeethin baAAdahu yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 186, "Whoever Allah sends astray - there is no guide for him. And He leaves them in their transgression, wandering blindly", "مَن يُضۡلِلِ ٱللَّهُ فَلَا هَادِيَ لَهُۥۚ وَيَذَرُهُمۡ فِي طُغۡيَٰنِهِمۡ يَعۡمَهُونَ", "Man yudlili Allahu fala hadiya lahu wayatharuhum fee tughyanihim yaAAmahoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 187, "They ask you, [O Muhammad], about the Hour: when is its arrival? Say, ""Its knowledge is only with my Lord. None will reveal its time except Him. It lays heavily upon the heavens and the earth. It will not come upon you except unexpectedly."" They ask you as if you are familiar with it. Say, ""Its knowledge is only with Allah, but most of the people do not know", "يَسۡـَٔلُونَكَ عَنِ ٱلسَّاعَةِ أَيَّانَ مُرۡسَىٰهَاۖ قُلۡ إِنَّمَا عِلۡمُهَا عِندَ رَبِّيۖ لَا يُجَلِّيهَا لِوَقۡتِهَآ إِلَّا هُوَۚ ثَقُلَتۡ فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ لَا تَأۡتِيكُمۡ إِلَّا بَغۡتَةٗۗ يَسۡـَٔلُونَكَ كَأَنَّكَ حَفِيٌّ عَنۡهَاۖ قُلۡ إِنَّمَا عِلۡمُهَا عِندَ ٱللَّهِ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَعۡلَمُونَ", "Yasaloonaka AAani alssaAAati ayyana mursaha qul innama AAilmuha AAinda rabbee la yujalleeha liwaqtiha illa huwa thaqulat fee alssamawati waalardi la tateekum illa baghtatan yasaloonaka kaannaka hafiyyun AAanha qul innama AAilmuha AAinda Allahi walakinna akthara alnnasi la yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 188, "Say, ""I hold not for myself [the power of] benefit or harm, except what Allah has willed. And if I knew the unseen, I could have acquired much wealth, and no harm would have touched me. I am not except a warner and a bringer of good tidings to a people who believe", "قُل لَّآ أَمۡلِكُ لِنَفۡسِي نَفۡعٗا وَلَا ضَرًّا إِلَّا مَا شَآءَ ٱللَّهُۚ وَلَوۡ كُنتُ أَعۡلَمُ ٱلۡغَيۡبَ لَٱسۡتَكۡثَرۡتُ مِنَ ٱلۡخَيۡرِ وَمَا مَسَّنِيَ ٱلسُّوٓءُۚ إِنۡ أَنَا۠ إِلَّا نَذِيرٞ وَبَشِيرٞ لِّقَوۡمٖ يُؤۡمِنُونَ", "Qul la amliku linafsee nafAAan wala darran illa ma shaa Allahu walaw kuntu aAAlamu alghayba laistakthartu mina alkhayri wama massaniya alssooo in ana illa natheerun wabasheerun liqawmin yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 189, "It is He who created you from one soul and created from it its mate that he might dwell in security with her. And when he covers her, she carries a light burden and continues therein. And when it becomes heavy, they both invoke Allah, their Lord, ""If You should give us a good [child], we will surely be among the grateful", "۞هُوَ ٱلَّذِي خَلَقَكُم مِّن نَّفۡسٖ وَٰحِدَةٖ وَجَعَلَ مِنۡهَا زَوۡجَهَا لِيَسۡكُنَ إِلَيۡهَاۖ فَلَمَّا تَغَشَّىٰهَا حَمَلَتۡ حَمۡلًا خَفِيفٗا فَمَرَّتۡ بِهِۦۖ فَلَمَّآ أَثۡقَلَت دَّعَوَا ٱللَّهَ رَبَّهُمَا لَئِنۡ ءَاتَيۡتَنَا صَٰلِحٗا لَّنَكُونَنَّ مِنَ ٱلشَّـٰكِرِينَ", "Huwa allathee khalaqakum min nafsin wahidatin wajaAAala minha zawjaha liyaskuna ilayha falamma taghashshaha hamalat hamlan khafeefan famarrat bihi falamma athqalat daAAawa Allaha rabbahuma lain ataytana salihan lanakoonanna mina alshshakireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 190, "But when He gives them a good [child], they ascribe partners to Him concerning that which He has given them. Exalted is Allah above what they associate with Him", "فَلَمَّآ ءَاتَىٰهُمَا صَٰلِحٗا جَعَلَا لَهُۥ شُرَكَآءَ فِيمَآ ءَاتَىٰهُمَاۚ فَتَعَٰلَى ٱللَّهُ عَمَّا يُشۡرِكُونَ", "Falamma atahuma salihan jaAAala lahu shurakaa feema atahuma fataAAala Allahu AAamma yushrikoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 191, "Do they associate with Him those who create nothing and they are [themselves] created", "أَيُشۡرِكُونَ مَا لَا يَخۡلُقُ شَيۡـٔٗا وَهُمۡ يُخۡلَقُونَ", "Ayushrikoona ma la yakhluqu shayan wahum yukhlaqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 192, "And the false deities are unable to [give] them help, nor can they help themselves", "وَلَا يَسۡتَطِيعُونَ لَهُمۡ نَصۡرٗا وَلَآ أَنفُسَهُمۡ يَنصُرُونَ", "Wala yastateeAAoona lahum nasran wala anfusahum yansuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 193, "And if you [believers] invite them to guidance, they will not follow you. It is all the same for you whether you invite them or you are silent", "وَإِن تَدۡعُوهُمۡ إِلَى ٱلۡهُدَىٰ لَا يَتَّبِعُوكُمۡۚ سَوَآءٌ عَلَيۡكُمۡ أَدَعَوۡتُمُوهُمۡ أَمۡ أَنتُمۡ صَٰمِتُونَ", "Wain tadAAoohum ila alhuda la yattabiAAookum sawaon AAalaykum adaAAawtumoohum am antum samitoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 194, "Indeed, those you [polytheists] call upon besides Allah are servants like you. So call upon them and let them respond to you, if you should be truthful", "إِنَّ ٱلَّذِينَ تَدۡعُونَ مِن دُونِ ٱللَّهِ عِبَادٌ أَمۡثَالُكُمۡۖ فَٱدۡعُوهُمۡ فَلۡيَسۡتَجِيبُواْ لَكُمۡ إِن كُنتُمۡ صَٰدِقِينَ", "Inna allatheena tadAAoona min dooni Allahi AAibadun amthalukum faodAAoohum falyastajeeboo lakum in kuntum sadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 195, "Do they have feet by which they walk? Or do they have hands by which they strike? Or do they have eyes by which they see? Or do they have ears by which they hear? Say, [O Muhammad], ""Call your 'partners' and then conspire against me and give me no respite", "أَلَهُمۡ أَرۡجُلٞ يَمۡشُونَ بِهَآۖ أَمۡ لَهُمۡ أَيۡدٖ يَبۡطِشُونَ بِهَآۖ أَمۡ لَهُمۡ أَعۡيُنٞ يُبۡصِرُونَ بِهَآۖ أَمۡ لَهُمۡ ءَاذَانٞ يَسۡمَعُونَ بِهَاۗ قُلِ ٱدۡعُواْ شُرَكَآءَكُمۡ ثُمَّ كِيدُونِ فَلَا تُنظِرُونِ", "Alahum arjulun yamshoona biha am lahum aydin yabtishoona biha am lahum aAAyunun yubsiroona biha am lahum athanun yasmaAAoona biha quli odAAoo shurakaakum thumma keedooni fala tunthirooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 196, "Indeed, my protector is Allah, who has sent down the Book; and He is an ally to the righteous", "إِنَّ وَلِـِّۧيَ ٱللَّهُ ٱلَّذِي نَزَّلَ ٱلۡكِتَٰبَۖ وَهُوَ يَتَوَلَّى ٱلصَّـٰلِحِينَ", "Inna waliyyiya Allahu allathee nazzala alkitaba wahuwa yatawalla alssaliheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 197, "And those you call upon besides Him are unable to help you, nor can they help themselves", "وَٱلَّذِينَ تَدۡعُونَ مِن دُونِهِۦ لَا يَسۡتَطِيعُونَ نَصۡرَكُمۡ وَلَآ أَنفُسَهُمۡ يَنصُرُونَ", "Waallatheena tadAAoona min doonihi la yastateeAAoona nasrakum wala anfusahum yansuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 198, "And if you invite them to guidance, they do not hear; and you see them looking at you while they do not see", "وَإِن تَدۡعُوهُمۡ إِلَى ٱلۡهُدَىٰ لَا يَسۡمَعُواْۖ وَتَرَىٰهُمۡ يَنظُرُونَ إِلَيۡكَ وَهُمۡ لَا يُبۡصِرُونَ", "Wain tadAAoohum ila alhuda la yasmaAAoo watarahum yanthuroona ilayka wahum la yubsiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 199, "Take what is given freely, enjoin what is good, and turn away from the ignorant", "خُذِ ٱلۡعَفۡوَ وَأۡمُرۡ بِٱلۡعُرۡفِ وَأَعۡرِضۡ عَنِ ٱلۡجَٰهِلِينَ", "Khuthi alAAafwa wamur bialAAurfi waaAArid AAani aljahileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 200, "And if an evil suggestion comes to you from Satan, then seek refuge in Allah. Indeed, He is Hearing and Knowing", "وَإِمَّا يَنزَغَنَّكَ مِنَ ٱلشَّيۡطَٰنِ نَزۡغٞ فَٱسۡتَعِذۡ بِٱللَّهِۚ إِنَّهُۥ سَمِيعٌ عَلِيمٌ", "Waimma yanzaghannaka mina alshshaytani nazghun faistaAAith biAllahi innahu sameeAAun AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 201, "Indeed, those who fear Allah - when an impulse touches them from Satan, they remember [Him] and at once they have insight", "إِنَّ ٱلَّذِينَ ٱتَّقَوۡاْ إِذَا مَسَّهُمۡ طَـٰٓئِفٞ مِّنَ ٱلشَّيۡطَٰنِ تَذَكَّرُواْ فَإِذَا هُم مُّبۡصِرُونَ", "Inna allatheena ittaqaw itha massahum taifun mina alshshaytani tathakkaroo faitha hum mubsiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 202, "But their brothers - the devils increase them in error; then they do not stop short", "وَإِخۡوَٰنُهُمۡ يَمُدُّونَهُمۡ فِي ٱلۡغَيِّ ثُمَّ لَا يُقۡصِرُونَ", "Waikhwanuhum yamuddoonahum fee alghayyi thumma la yuqsiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 203, "And when you, [O Muhammad], do not bring them a sign, they say, ""Why have you not contrived it?"" Say, ""I only follow what is revealed to me from my Lord. This [Qur'an] is enlightenment from your Lord and guidance and mercy for a people who believe", "وَإِذَا لَمۡ تَأۡتِهِم بِـَٔايَةٖ قَالُواْ لَوۡلَا ٱجۡتَبَيۡتَهَاۚ قُلۡ إِنَّمَآ أَتَّبِعُ مَا يُوحَىٰٓ إِلَيَّ مِن رَّبِّيۚ هَٰذَا بَصَآئِرُ مِن رَّبِّكُمۡ وَهُدٗى وَرَحۡمَةٞ لِّقَوۡمٖ يُؤۡمِنُونَ", "Waitha lam tatihim biayatin qaloo lawla ijtabaytaha qul innama attabiAAu ma yooha ilayya min rabbee hatha basairu min rabbikum wahudan warahmatun liqawmin yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 204, "So when the Qur'an is recited, then listen to it and pay attention that you may receive mercy", "وَإِذَا قُرِئَ ٱلۡقُرۡءَانُ فَٱسۡتَمِعُواْ لَهُۥ وَأَنصِتُواْ لَعَلَّكُمۡ تُرۡحَمُونَ", "Waitha quria alquranu faistamiAAoo lahu waansitoo laAAallakum turhamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 205, "And remember your Lord within yourself in humility and in fear without being apparent in speech - in the mornings and the evenings. And do not be among the heedless", "وَٱذۡكُر رَّبَّكَ فِي نَفۡسِكَ تَضَرُّعٗا وَخِيفَةٗ وَدُونَ ٱلۡجَهۡرِ مِنَ ٱلۡقَوۡلِ بِٱلۡغُدُوِّ وَٱلۡأٓصَالِ وَلَا تَكُن مِّنَ ٱلۡغَٰفِلِينَ", "Waothkur rabbaka fee nafsika tadarruAAan wakheefatan wadoona aljahri mina alqawli bialghuduwwi waalasali wala takun mina alghafileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (7, 206, "Indeed, those who are near your Lord are not prevented by arrogance from His worship, and they exalt Him, and to Him they prostrate", "إِنَّ ٱلَّذِينَ عِندَ رَبِّكَ لَا يَسۡتَكۡبِرُونَ عَنۡ عِبَادَتِهِۦ وَيُسَبِّحُونَهُۥ وَلَهُۥ يَسۡجُدُونَۤ۩", "Inna allatheena AAinda rabbika la yastakbiroona AAan AAibadatihi wayusabbihoonahu walahu yasjudoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 1, "They ask you, [O Muhammad], about the bounties [of war]. Say, ""The [decision concerning] bounties is for Allah and the Messenger."" So fear Allah and amend that which is between you and obey Allah and His Messenger, if you should be believers", "يَسۡـَٔلُونَكَ عَنِ ٱلۡأَنفَالِۖ قُلِ ٱلۡأَنفَالُ لِلَّهِ وَٱلرَّسُولِۖ فَٱتَّقُواْ ٱللَّهَ وَأَصۡلِحُواْ ذَاتَ بَيۡنِكُمۡۖ وَأَطِيعُواْ ٱللَّهَ وَرَسُولَهُۥٓ إِن كُنتُم مُّؤۡمِنِينَ", "Yasaloonaka AAani alanfali quli alanfalu lillahi waalrrasooli faittaqoo Allaha waaslihoo thata baynikum waateeAAoo Allaha warasoolahu in kuntum mumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 2, "The believers are only those who, when Allah is mentioned, their hearts become fearful, and when His verses are recited to them, it increases them in faith; and upon their Lord they rely", "إِنَّمَا ٱلۡمُؤۡمِنُونَ ٱلَّذِينَ إِذَا ذُكِرَ ٱللَّهُ وَجِلَتۡ قُلُوبُهُمۡ وَإِذَا تُلِيَتۡ عَلَيۡهِمۡ ءَايَٰتُهُۥ زَادَتۡهُمۡ إِيمَٰنٗا وَعَلَىٰ رَبِّهِمۡ يَتَوَكَّلُونَ", "Innama almuminoona allatheena itha thukira Allahu wajilat quloobuhum waitha tuliyat AAalayhim ayatuhu zadathum eemanan waAAala rabbihim yatawakkaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 3, "The ones who establish prayer, and from what We have provided them, they spend", "ٱلَّذِينَ يُقِيمُونَ ٱلصَّلَوٰةَ وَمِمَّا رَزَقۡنَٰهُمۡ يُنفِقُونَ", "Allatheena yuqeemoona alssalata wamimma razaqnahum yunfiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 4, "Those are the believers, truly. For them are degrees [of high position] with their Lord and forgiveness and noble provision", "أُوْلَـٰٓئِكَ هُمُ ٱلۡمُؤۡمِنُونَ حَقّٗاۚ لَّهُمۡ دَرَجَٰتٌ عِندَ رَبِّهِمۡ وَمَغۡفِرَةٞ وَرِزۡقٞ كَرِيمٞ", "Olaika humu almuminoona haqqan lahum darajatun AAinda rabbihim wamaghfiratun warizqun kareemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 5, "[It is] just as when your Lord brought you out of your home [for the battle of Badr] in truth, while indeed, a party among the believers were unwilling", "كَمَآ أَخۡرَجَكَ رَبُّكَ مِنۢ بَيۡتِكَ بِٱلۡحَقِّ وَإِنَّ فَرِيقٗا مِّنَ ٱلۡمُؤۡمِنِينَ لَكَٰرِهُونَ", "Kama akhrajaka rabbuka min baytika bialhaqqi wainna fareeqan mina almumineena lakarihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 6, "Arguing with you concerning the truth after it had become clear, as if they were being driven toward death while they were looking on", "يُجَٰدِلُونَكَ فِي ٱلۡحَقِّ بَعۡدَ مَا تَبَيَّنَ كَأَنَّمَا يُسَاقُونَ إِلَى ٱلۡمَوۡتِ وَهُمۡ يَنظُرُونَ", "Yujadiloonaka fee alhaqqi baAAdama tabayyana kaannama yusaqoona ila almawti wahum yanthuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 7, "[Remember, O believers], when Allah promised you one of the two groups - that it would be yours - and you wished that the unarmed one would be yours. But Allah intended to establish the truth by His words and to eliminate the disbelievers", "وَإِذۡ يَعِدُكُمُ ٱللَّهُ إِحۡدَى ٱلطَّآئِفَتَيۡنِ أَنَّهَا لَكُمۡ وَتَوَدُّونَ أَنَّ غَيۡرَ ذَاتِ ٱلشَّوۡكَةِ تَكُونُ لَكُمۡ وَيُرِيدُ ٱللَّهُ أَن يُحِقَّ ٱلۡحَقَّ بِكَلِمَٰتِهِۦ وَيَقۡطَعَ دَابِرَ ٱلۡكَٰفِرِينَ", "Waith yaAAidukumu Allahu ihda alttaifatayni annaha lakum watawaddoona anna ghayra thati alshshawkati takoonu lakum wayureedu Allahu an yuhiqqa alhaqqa bikalimatihi wayaqtaAAa dabira alkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 8, "That He should establish the truth and abolish falsehood, even if the criminals disliked it", "لِيُحِقَّ ٱلۡحَقَّ وَيُبۡطِلَ ٱلۡبَٰطِلَ وَلَوۡ كَرِهَ ٱلۡمُجۡرِمُونَ", "Liyuhiqqa alhaqqa wayubtila albatila walaw kariha almujrimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 9, "[Remember] when you asked help of your Lord, and He answered you, ""Indeed, I will reinforce you with a thousand from the angels, following one another", "إِذۡ تَسۡتَغِيثُونَ رَبَّكُمۡ فَٱسۡتَجَابَ لَكُمۡ أَنِّي مُمِدُّكُم بِأَلۡفٖ مِّنَ ٱلۡمَلَـٰٓئِكَةِ مُرۡدِفِينَ", "Ith tastagheethoona rabbakum faistajaba lakum annee mumiddukum bialfin mina almalaikati murdifeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 10, "And Allah made it not but good tidings and so that your hearts would be assured thereby. And victory is not but from Allah. Indeed, Allah is Exalted in Might and Wise", "وَمَا جَعَلَهُ ٱللَّهُ إِلَّا بُشۡرَىٰ وَلِتَطۡمَئِنَّ بِهِۦ قُلُوبُكُمۡۚ وَمَا ٱلنَّصۡرُ إِلَّا مِنۡ عِندِ ٱللَّهِۚ إِنَّ ٱللَّهَ عَزِيزٌ حَكِيمٌ", "Wama jaAAalahu Allahu illa bushra walitatmainna bihi quloobukum wama alnnasru illa min AAindi Allahi inna Allaha AAazeezun hakeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 11, "[Remember] when He overwhelmed you with drowsiness [giving] security from Him and sent down upon you from the sky, rain by which to purify you and remove from you the evil [suggestions] of Satan and to make steadfast your hearts and plant firmly thereby your feet", "إِذۡ يُغَشِّيكُمُ ٱلنُّعَاسَ أَمَنَةٗ مِّنۡهُ وَيُنَزِّلُ عَلَيۡكُم مِّنَ ٱلسَّمَآءِ مَآءٗ لِّيُطَهِّرَكُم بِهِۦ وَيُذۡهِبَ عَنكُمۡ رِجۡزَ ٱلشَّيۡطَٰنِ وَلِيَرۡبِطَ عَلَىٰ قُلُوبِكُمۡ وَيُثَبِّتَ بِهِ ٱلۡأَقۡدَامَ", "Ith yughashsheekumu alnnuAAasa amanatan minhu wayunazzilu AAalaykum mina alssamai maan liyutahhirakum bihi wayuthhiba AAankum rijza alshshaytani waliyarbita AAala quloobikum wayuthabbita bihi alaqdama");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 12, "[Remember] when your Lord inspired to the angels, ""I am with you, so strengthen those who have believed. I will cast terror into the hearts of those who disbelieved, so strike [them] upon the necks and strike from them every fingertip", "إِذۡ يُوحِي رَبُّكَ إِلَى ٱلۡمَلَـٰٓئِكَةِ أَنِّي مَعَكُمۡ فَثَبِّتُواْ ٱلَّذِينَ ءَامَنُواْۚ سَأُلۡقِي فِي قُلُوبِ ٱلَّذِينَ كَفَرُواْ ٱلرُّعۡبَ فَٱضۡرِبُواْ فَوۡقَ ٱلۡأَعۡنَاقِ وَٱضۡرِبُواْ مِنۡهُمۡ كُلَّ بَنَانٖ", "Ith yoohee rabbuka ila almalaikati annee maAAakum fathabbitoo allatheena amanoo saolqee fee quloobi allatheena kafaroo alrruAAba faidriboo fawqa alaAAnaqi waidriboo minhum kulla bananin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 13, "That is because they opposed Allah and His Messenger. And whoever opposes Allah and His Messenger - indeed, Allah is severe in penalty", "ذَٰلِكَ بِأَنَّهُمۡ شَآقُّواْ ٱللَّهَ وَرَسُولَهُۥۚ وَمَن يُشَاقِقِ ٱللَّهَ وَرَسُولَهُۥ فَإِنَّ ٱللَّهَ شَدِيدُ ٱلۡعِقَابِ", "Thalika biannahum shaqqoo Allaha warasoolahu waman yushaqiqi Allaha warasoolahu fainna Allaha shadeedu alAAiqabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 14, "That [is yours], so taste it."" And indeed for the disbelievers is the punishment of the Fire", "ذَٰلِكُمۡ فَذُوقُوهُ وَأَنَّ لِلۡكَٰفِرِينَ عَذَابَ ٱلنَّارِ", "Thalikum fathooqoohu waanna lilkafireena AAathaba alnnari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 15, "O you who have believed, when you meet those who disbelieve advancing [for battle], do not turn to them your backs [in flight]", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِذَا لَقِيتُمُ ٱلَّذِينَ كَفَرُواْ زَحۡفٗا فَلَا تُوَلُّوهُمُ ٱلۡأَدۡبَارَ", "Ya ayyuha allatheena amanoo itha laqeetumu allatheena kafaroo zahfan fala tuwalloohumu aladbara");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 16, "And whoever turns his back to them on such a day, unless swerving [as a strategy] for war or joining [another] company, has certainly returned with anger [upon him] from Allah, and his refuge is Hell - and wretched is the destination", "وَمَن يُوَلِّهِمۡ يَوۡمَئِذٖ دُبُرَهُۥٓ إِلَّا مُتَحَرِّفٗا لِّقِتَالٍ أَوۡ مُتَحَيِّزًا إِلَىٰ فِئَةٖ فَقَدۡ بَآءَ بِغَضَبٖ مِّنَ ٱللَّهِ وَمَأۡوَىٰهُ جَهَنَّمُۖ وَبِئۡسَ ٱلۡمَصِيرُ", "Waman yuwallihim yawmaithin duburahu illa mutaharrifan liqitalin aw mutahayyizan ila fiatin faqad baa bighadabin mina Allahi wamawahu jahannamu wabisa almaseeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 17, "And you did not kill them, but it was Allah who killed them. And you threw not, [O Muhammad], when you threw, but it was Allah who threw that He might test the believers with a good test. Indeed, Allah is Hearing and Knowing", "فَلَمۡ تَقۡتُلُوهُمۡ وَلَٰكِنَّ ٱللَّهَ قَتَلَهُمۡۚ وَمَا رَمَيۡتَ إِذۡ رَمَيۡتَ وَلَٰكِنَّ ٱللَّهَ رَمَىٰ وَلِيُبۡلِيَ ٱلۡمُؤۡمِنِينَ مِنۡهُ بَلَآءً حَسَنًاۚ إِنَّ ٱللَّهَ سَمِيعٌ عَلِيمٞ", "Falam taqtuloohum walakinna Allaha qatalahum wama ramayta ith ramayta walakinna Allaha rama waliyubliya almumineena minhu balaan hasanan inna Allaha sameeAAun AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 18, "That [is so], and [also] that Allah will weaken the plot of the disbelievers", "ذَٰلِكُمۡ وَأَنَّ ٱللَّهَ مُوهِنُ كَيۡدِ ٱلۡكَٰفِرِينَ", "Thalikum waanna Allaha moohinu kaydi alkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 19, "If you [disbelievers] seek the victory - the defeat has come to you. And if you desist [from hostilities], it is best for you; but if you return [to war], We will return, and never will you be availed by your [large] company at all, even if it should increase; and [that is] because Allah is with the believers", "إِن تَسۡتَفۡتِحُواْ فَقَدۡ جَآءَكُمُ ٱلۡفَتۡحُۖ وَإِن تَنتَهُواْ فَهُوَ خَيۡرٞ لَّكُمۡۖ وَإِن تَعُودُواْ نَعُدۡ وَلَن تُغۡنِيَ عَنكُمۡ فِئَتُكُمۡ شَيۡـٔٗا وَلَوۡ كَثُرَتۡ وَأَنَّ ٱللَّهَ مَعَ ٱلۡمُؤۡمِنِينَ", "In tastaftihoo faqad jaakumu alfathu wain tantahoo fahuwa khayrun lakum wain taAAoodoo naAAud walan tughniya AAankum fiatukum shayan walaw kathurat waanna Allaha maAAa almumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 20, "O you who have believed, obey Allah and His Messenger and do not turn from him while you hear [his order]", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ أَطِيعُواْ ٱللَّهَ وَرَسُولَهُۥ وَلَا تَوَلَّوۡاْ عَنۡهُ وَأَنتُمۡ تَسۡمَعُونَ", "Ya ayyuha allatheena amanoo ateeAAoo Allaha warasoolahu wala tawallaw AAanhu waantum tasmaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 21, "And do not be like those who say, ""We have heard,"" while they do not hear", "وَلَا تَكُونُواْ كَٱلَّذِينَ قَالُواْ سَمِعۡنَا وَهُمۡ لَا يَسۡمَعُونَ", "Wala takoonoo kaallatheena qaloo samiAAna wahum la yasmaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 22, "Indeed, the worst of living creatures in the sight of Allah are the deaf and dumb who do not use reason", "۞إِنَّ شَرَّ ٱلدَّوَآبِّ عِندَ ٱللَّهِ ٱلصُّمُّ ٱلۡبُكۡمُ ٱلَّذِينَ لَا يَعۡقِلُونَ", "Inna sharra alddawabbi AAinda Allahi alssummu albukmu allatheena la yaAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 23, "Had Allah known any good in them, He would have made them hear. And if He had made them hear, they would [still] have turned away, while they were refusing", "وَلَوۡ عَلِمَ ٱللَّهُ فِيهِمۡ خَيۡرٗا لَّأَسۡمَعَهُمۡۖ وَلَوۡ أَسۡمَعَهُمۡ لَتَوَلَّواْ وَّهُم مُّعۡرِضُونَ", "Walaw AAalima Allahu feehim khayran laasmaAAahum walaw asmaAAahum latawallaw wahum muAAridoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 24, "O you who have believed, respond to Allah and to the Messenger when he calls you to that which gives you life. And know that Allah intervenes between a man and his heart and that to Him you will be gathered", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱسۡتَجِيبُواْ لِلَّهِ وَلِلرَّسُولِ إِذَا دَعَاكُمۡ لِمَا يُحۡيِيكُمۡۖ وَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ يَحُولُ بَيۡنَ ٱلۡمَرۡءِ وَقَلۡبِهِۦ وَأَنَّهُۥٓ إِلَيۡهِ تُحۡشَرُونَ", "Ya ayyuha allatheena amanoo istajeeboo lillahi walilrrasooli itha daAAakum lima yuhyeekum waiAAlamoo anna Allaha yahoolu bayna almari waqalbihi waannahu ilayhi tuhsharoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 25, "And fear a trial which will not strike those who have wronged among you exclusively, and know that Allah is severe in penalty", "وَٱتَّقُواْ فِتۡنَةٗ لَّا تُصِيبَنَّ ٱلَّذِينَ ظَلَمُواْ مِنكُمۡ خَآصَّةٗۖ وَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ شَدِيدُ ٱلۡعِقَابِ", "Waittaqoo fitnatan la tuseebanna allatheena thalamoo minkum khassatan waiAAlamoo anna Allaha shadeedu alAAiqabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 26, "And remember when you were few and oppressed in the land, fearing that people might abduct you, but He sheltered you, supported you with His victory, and provided you with good things - that you might be grateful", "وَٱذۡكُرُوٓاْ إِذۡ أَنتُمۡ قَلِيلٞ مُّسۡتَضۡعَفُونَ فِي ٱلۡأَرۡضِ تَخَافُونَ أَن يَتَخَطَّفَكُمُ ٱلنَّاسُ فَـَٔاوَىٰكُمۡ وَأَيَّدَكُم بِنَصۡرِهِۦ وَرَزَقَكُم مِّنَ ٱلطَّيِّبَٰتِ لَعَلَّكُمۡ تَشۡكُرُونَ", "Waothkuroo ith antum qaleelun mustadAAafoona fee alardi takhafoona an yatakhattafakumu alnnasu faawakum waayyadakum binasrihi warazaqakum mina alttayyibati laAAallakum tashkuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 27, "O you who have believed, do not betray Allah and the Messenger or betray your trusts while you know [the consequence]", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَخُونُواْ ٱللَّهَ وَٱلرَّسُولَ وَتَخُونُوٓاْ أَمَٰنَٰتِكُمۡ وَأَنتُمۡ تَعۡلَمُونَ", "Ya ayyuha allatheena amanoo la takhoonoo Allaha waalrrasoola watakhoonoo amanatikum waantum taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 28, "And know that your properties and your children are but a trial and that Allah has with Him a great reward", "وَٱعۡلَمُوٓاْ أَنَّمَآ أَمۡوَٰلُكُمۡ وَأَوۡلَٰدُكُمۡ فِتۡنَةٞ وَأَنَّ ٱللَّهَ عِندَهُۥٓ أَجۡرٌ عَظِيمٞ", "WaiAAlamoo annama amwalukum waawladukum fitnatun waanna Allaha AAindahu ajrun AAatheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 29, "O you who have believed, if you fear Allah, He will grant you a criterion and will remove from you your misdeeds and forgive you. And Allah is the possessor of great bounty", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِن تَتَّقُواْ ٱللَّهَ يَجۡعَل لَّكُمۡ فُرۡقَانٗا وَيُكَفِّرۡ عَنكُمۡ سَيِّـَٔاتِكُمۡ وَيَغۡفِرۡ لَكُمۡۗ وَٱللَّهُ ذُو ٱلۡفَضۡلِ ٱلۡعَظِيمِ", "Ya ayyuha allatheena amanoo in tattaqoo Allaha yajAAal lakum furqanan wayukaffir AAankum sayyiatikum wayaghfir lakum waAllahu thoo alfadli alAAatheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 30, "And [remember, O Muhammad], when those who disbelieved plotted against you to restrain you or kill you or evict you [from Makkah]. But they plan, and Allah plans. And Allah is the best of planners", "وَإِذۡ يَمۡكُرُ بِكَ ٱلَّذِينَ كَفَرُواْ لِيُثۡبِتُوكَ أَوۡ يَقۡتُلُوكَ أَوۡ يُخۡرِجُوكَۚ وَيَمۡكُرُونَ وَيَمۡكُرُ ٱللَّهُۖ وَٱللَّهُ خَيۡرُ ٱلۡمَٰكِرِينَ", "Waith yamkuru bika allatheena kafaroo liyuthbitooka aw yaqtulooka aw yukhrijooka wayamkuroona wayamkuru Allahu waAllahu khayru almakireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 31, "And when Our verses are recited to them, they say, ""We have heard. If we willed, we could say [something] like this. This is not but legends of the former peoples", "وَإِذَا تُتۡلَىٰ عَلَيۡهِمۡ ءَايَٰتُنَا قَالُواْ قَدۡ سَمِعۡنَا لَوۡ نَشَآءُ لَقُلۡنَا مِثۡلَ هَٰذَآ إِنۡ هَٰذَآ إِلَّآ أَسَٰطِيرُ ٱلۡأَوَّلِينَ", "Waitha tutla AAalayhim ayatuna qaloo qad samiAAna law nashao laqulna mithla hatha in hatha illa asateeru alawwaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 32, "And [remember] when they said, ""O Allah, if this should be the truth from You, then rain down upon us stones from the sky or bring us a painful punishment", "وَإِذۡ قَالُواْ ٱللَّهُمَّ إِن كَانَ هَٰذَا هُوَ ٱلۡحَقَّ مِنۡ عِندِكَ فَأَمۡطِرۡ عَلَيۡنَا حِجَارَةٗ مِّنَ ٱلسَّمَآءِ أَوِ ٱئۡتِنَا بِعَذَابٍ أَلِيمٖ", "Waith qaloo allahumma in kana hatha huwa alhaqqa min AAindika faamtir AAalayna hijaratan mina alssamai awi itina biAAathabin aleemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 33, "But Allah would not punish them while you, [O Muhammad], are among them, and Allah would not punish them while they seek forgiveness", "وَمَا كَانَ ٱللَّهُ لِيُعَذِّبَهُمۡ وَأَنتَ فِيهِمۡۚ وَمَا كَانَ ٱللَّهُ مُعَذِّبَهُمۡ وَهُمۡ يَسۡتَغۡفِرُونَ", "Wama kana Allahu liyuAAaththibahum waanta feehim wama kana Allahu muAAaththibahum wahum yastaghfiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 34, "But why should Allah not punish them while they obstruct [people] from al-Masjid al- Haram and they were not [fit to be] its guardians? Its [true] guardians are not but the righteous, but most of them do not know", "وَمَا لَهُمۡ أَلَّا يُعَذِّبَهُمُ ٱللَّهُ وَهُمۡ يَصُدُّونَ عَنِ ٱلۡمَسۡجِدِ ٱلۡحَرَامِ وَمَا كَانُوٓاْ أَوۡلِيَآءَهُۥٓۚ إِنۡ أَوۡلِيَآؤُهُۥٓ إِلَّا ٱلۡمُتَّقُونَ وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَعۡلَمُونَ", "Wama lahum alla yuAAaththibahumu Allahu wahum yasuddoona AAani almasjidi alharami wama kanoo awliyaahu in awliyaohu illa almuttaqoona walakinna aktharahum la yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 35, "And their prayer at the House was not except whistling and handclapping. So taste the punishment for what you disbelieved", "وَمَا كَانَ صَلَاتُهُمۡ عِندَ ٱلۡبَيۡتِ إِلَّا مُكَآءٗ وَتَصۡدِيَةٗۚ فَذُوقُواْ ٱلۡعَذَابَ بِمَا كُنتُمۡ تَكۡفُرُونَ", "Wama kana salatuhum AAinda albayti illa mukaan watasdiyatan fathooqoo alAAathaba bima kuntum takfuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 36, "Indeed, those who disbelieve spend their wealth to avert [people] from the way of Allah. So they will spend it; then it will be for them a [source of] regret; then they will be overcome. And those who have disbelieved - unto Hell they will be gathered", "إِنَّ ٱلَّذِينَ كَفَرُواْ يُنفِقُونَ أَمۡوَٰلَهُمۡ لِيَصُدُّواْ عَن سَبِيلِ ٱللَّهِۚ فَسَيُنفِقُونَهَا ثُمَّ تَكُونُ عَلَيۡهِمۡ حَسۡرَةٗ ثُمَّ يُغۡلَبُونَۗ وَٱلَّذِينَ كَفَرُوٓاْ إِلَىٰ جَهَنَّمَ يُحۡشَرُونَ", "Inna allatheena kafaroo yunfiqoona amwalahum liyasuddoo AAan sabeeli Allahi fasayunfiqoonaha thumma takoonu AAalayhim hasratan thumma yughlaboona waallatheena kafaroo ila jahannama yuhsharoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 37, "[This is] so that Allah may distinguish the wicked from the good and place the wicked some of them upon others and heap them all together and put them into Hell. It is those who are the losers", "لِيَمِيزَ ٱللَّهُ ٱلۡخَبِيثَ مِنَ ٱلطَّيِّبِ وَيَجۡعَلَ ٱلۡخَبِيثَ بَعۡضَهُۥ عَلَىٰ بَعۡضٖ فَيَرۡكُمَهُۥ جَمِيعٗا فَيَجۡعَلَهُۥ فِي جَهَنَّمَۚ أُوْلَـٰٓئِكَ هُمُ ٱلۡخَٰسِرُونَ", "Liyameeza Allahu alkhabeetha mina alttayyibi wayajAAala alkhabeetha baAAdahu AAala baAAdin fayarkumahu jameeAAan fayajAAalahu fee jahannama olaika humu alkhasiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 38, "Say to those who have disbelieved [that] if they cease, what has previously occurred will be forgiven for them. But if they return [to hostility] - then the precedent of the former [rebellious] peoples has already taken place", "قُل لِّلَّذِينَ كَفَرُوٓاْ إِن يَنتَهُواْ يُغۡفَرۡ لَهُم مَّا قَدۡ سَلَفَ وَإِن يَعُودُواْ فَقَدۡ مَضَتۡ سُنَّتُ ٱلۡأَوَّلِينَ", "Qul lillatheena kafaroo in yantahoo yughfar lahum ma qad salafa wain yaAAoodoo faqad madat sunnatu alawwaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 39, "And fight them until there is no fitnah and [until] the religion, all of it, is for Allah. And if they cease - then indeed, Allah is Seeing of what they do", "وَقَٰتِلُوهُمۡ حَتَّىٰ لَا تَكُونَ فِتۡنَةٞ وَيَكُونَ ٱلدِّينُ كُلُّهُۥ لِلَّهِۚ فَإِنِ ٱنتَهَوۡاْ فَإِنَّ ٱللَّهَ بِمَا يَعۡمَلُونَ بَصِيرٞ", "Waqatiloohum hatta la takoona fitnatun wayakoona alddeenu kulluhu lillahi faini intahaw fainna Allaha bima yaAAmaloona baseerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 40, "But if they turn away - then know that Allah is your protector. Excellent is the protector, and Excellent is the helper", "وَإِن تَوَلَّوۡاْ فَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ مَوۡلَىٰكُمۡۚ نِعۡمَ ٱلۡمَوۡلَىٰ وَنِعۡمَ ٱلنَّصِيرُ", "Wain tawallaw faiAAlamoo anna Allaha mawlakum niAAma almawla waniAAma alnnaseeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 41, "And know that anything you obtain of war booty - then indeed, for Allah is one fifth of it and for the Messenger and for [his] near relatives and the orphans, the needy, and the [stranded] traveler, if you have believed in Allah and in that which We sent down to Our Servant on the day of criterion - the day when the two armies met. And Allah, over all things, is competent", "۞وَٱعۡلَمُوٓاْ أَنَّمَا غَنِمۡتُم مِّن شَيۡءٖ فَأَنَّ لِلَّهِ خُمُسَهُۥ وَلِلرَّسُولِ وَلِذِي ٱلۡقُرۡبَىٰ وَٱلۡيَتَٰمَىٰ وَٱلۡمَسَٰكِينِ وَٱبۡنِ ٱلسَّبِيلِ إِن كُنتُمۡ ءَامَنتُم بِٱللَّهِ وَمَآ أَنزَلۡنَا عَلَىٰ عَبۡدِنَا يَوۡمَ ٱلۡفُرۡقَانِ يَوۡمَ ٱلۡتَقَى ٱلۡجَمۡعَانِۗ وَٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ", "WaiAAlamoo annama ghanimtum min shayin faanna lillahi khumusahu walilrrasooli walithee alqurba waalyatama waalmasakeeni waibni alssabeeli in kuntum amantum biAllahi wama anzalna AAala AAabdina yawma alfurqani yawma iltaqa aljamAAani waAllahu AAala kulli shayin qadeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 42, "[Remember] when you were on the near side of the valley, and they were on the farther side, and the caravan was lower [in position] than you. If you had made an appointment [to meet], you would have missed the appointment. But [it was] so that Allah might accomplish a matter already destined - that those who perished [through disbelief] would perish upon evidence and those who lived [in faith] would live upon evidence; and indeed, Allah is Hearing and Knowing", "إِذۡ أَنتُم بِٱلۡعُدۡوَةِ ٱلدُّنۡيَا وَهُم بِٱلۡعُدۡوَةِ ٱلۡقُصۡوَىٰ وَٱلرَّكۡبُ أَسۡفَلَ مِنكُمۡۚ وَلَوۡ تَوَاعَدتُّمۡ لَٱخۡتَلَفۡتُمۡ فِي ٱلۡمِيعَٰدِ وَلَٰكِن لِّيَقۡضِيَ ٱللَّهُ أَمۡرٗا كَانَ مَفۡعُولٗا لِّيَهۡلِكَ مَنۡ هَلَكَ عَنۢ بَيِّنَةٖ وَيَحۡيَىٰ مَنۡ حَيَّ عَنۢ بَيِّنَةٖۗ وَإِنَّ ٱللَّهَ لَسَمِيعٌ عَلِيمٌ", "Ith antum bialAAudwati alddunya wahum bialAAudwati alquswa waalrrakbu asfala minkum walaw tawaAAadtum laikhtalaftum fee almeeAAadi walakin liyaqdiya Allahu amran kana mafAAoolan liyahlika man halaka AAan bayyinatin wayahya man hayya AAan bayyinatin wainna Allaha lasameeAAun AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 43, "[Remember, O Muhammad], when Allah showed them to you in your dream as few; and if He had shown them to you as many, you [believers] would have lost courage and would have disputed in the matter [of whether to fight], but Allah saved [you from that]. Indeed, He is Knowing of that within the breasts", "إِذۡ يُرِيكَهُمُ ٱللَّهُ فِي مَنَامِكَ قَلِيلٗاۖ وَلَوۡ أَرَىٰكَهُمۡ كَثِيرٗا لَّفَشِلۡتُمۡ وَلَتَنَٰزَعۡتُمۡ فِي ٱلۡأَمۡرِ وَلَٰكِنَّ ٱللَّهَ سَلَّمَۚ إِنَّهُۥ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ", "Ith yureekahumu Allahu fee manamika qaleelan walaw arakahum katheeran lafashiltum walatanazaAAtum fee alamri walakinna Allaha sallama innahu AAaleemun bithati alssudoori");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 44, "And [remember] when He showed them to you, when you met, as few in your eyes, and He made you [appear] as few in their eyes so that Allah might accomplish a matter already destined. And to Allah are [all] matters returned", "وَإِذۡ يُرِيكُمُوهُمۡ إِذِ ٱلۡتَقَيۡتُمۡ فِيٓ أَعۡيُنِكُمۡ قَلِيلٗا وَيُقَلِّلُكُمۡ فِيٓ أَعۡيُنِهِمۡ لِيَقۡضِيَ ٱللَّهُ أَمۡرٗا كَانَ مَفۡعُولٗاۗ وَإِلَى ٱللَّهِ تُرۡجَعُ ٱلۡأُمُورُ", "Waith yureekumoohum ithi iltaqaytum fee aAAyunikum qaleelan wayuqallilukum fee aAAyunihim liyaqdiya Allahu amran kana mafAAoolan waila Allahi turjaAAu alomooru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 45, "O you who have believed, when you encounter a company [from the enemy forces], stand firm and remember Allah much that you may be successful", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِذَا لَقِيتُمۡ فِئَةٗ فَٱثۡبُتُواْ وَٱذۡكُرُواْ ٱللَّهَ كَثِيرٗا لَّعَلَّكُمۡ تُفۡلِحُونَ", "Ya ayyuha allatheena amanoo itha laqeetum fiatan faothbutoo waothkuroo Allaha katheeran laAAallakum tuflihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 46, "And obey Allah and His Messenger, and do not dispute and [thus] lose courage and [then] your strength would depart; and be patient. Indeed, Allah is with the patient", "وَأَطِيعُواْ ٱللَّهَ وَرَسُولَهُۥ وَلَا تَنَٰزَعُواْ فَتَفۡشَلُواْ وَتَذۡهَبَ رِيحُكُمۡۖ وَٱصۡبِرُوٓاْۚ إِنَّ ٱللَّهَ مَعَ ٱلصَّـٰبِرِينَ", "WaateeAAoo Allaha warasoolahu wala tanazaAAoo fatafshaloo watathhaba reehukum waisbiroo inna Allaha maAAa alssabireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 47, "And do not be like those who came forth from their homes insolently and to be seen by people and avert [them] from the way of Allah. And Allah is encompassing of what they do", "وَلَا تَكُونُواْ كَٱلَّذِينَ خَرَجُواْ مِن دِيَٰرِهِم بَطَرٗا وَرِئَآءَ ٱلنَّاسِ وَيَصُدُّونَ عَن سَبِيلِ ٱللَّهِۚ وَٱللَّهُ بِمَا يَعۡمَلُونَ مُحِيطٞ", "Wala takoonoo kaallatheena kharajoo min diyarihim bataran wariaa alnnasi wayasuddoona AAan sabeeli Allahi waAllahu bima yaAAmaloona muheetun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 48, "And [remember] when Satan made their deeds pleasing to them and said, ""No one can overcome you today from among the people, and indeed, I am your protector."" But when the two armies sighted each other, he turned on his heels and said, ""Indeed, I am disassociated from you. Indeed, I see what you do not see; indeed I fear Allah. And Allah is severe in penalty", "وَإِذۡ زَيَّنَ لَهُمُ ٱلشَّيۡطَٰنُ أَعۡمَٰلَهُمۡ وَقَالَ لَا غَالِبَ لَكُمُ ٱلۡيَوۡمَ مِنَ ٱلنَّاسِ وَإِنِّي جَارٞ لَّكُمۡۖ فَلَمَّا تَرَآءَتِ ٱلۡفِئَتَانِ نَكَصَ عَلَىٰ عَقِبَيۡهِ وَقَالَ إِنِّي بَرِيٓءٞ مِّنكُمۡ إِنِّيٓ أَرَىٰ مَا لَا تَرَوۡنَ إِنِّيٓ أَخَافُ ٱللَّهَۚ وَٱللَّهُ شَدِيدُ ٱلۡعِقَابِ", "Waith zayyana lahumu alshshaytanu aAAmalahum waqala la ghaliba lakumu alyawma mina alnnasi wainnee jarun lakum falamma taraati alfiatani nakasa AAala AAaqibayhi waqala innee bareeon minkum innee ara ma la tarawna innee akhafu Allaha waAllahu shadeedu alAAiqabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 49, "[Remember] when the hypocrites and those in whose hearts was disease said, ""Their religion has deluded those [Muslims]."" But whoever relies upon Allah - then indeed, Allah is Exalted in Might and Wise", "إِذۡ يَقُولُ ٱلۡمُنَٰفِقُونَ وَٱلَّذِينَ فِي قُلُوبِهِم مَّرَضٌ غَرَّ هَـٰٓؤُلَآءِ دِينُهُمۡۗ وَمَن يَتَوَكَّلۡ عَلَى ٱللَّهِ فَإِنَّ ٱللَّهَ عَزِيزٌ حَكِيمٞ", "Ith yaqoolu almunafiqoona waallatheena fee quloobihim maradun gharra haolai deenuhum waman yatawakkal AAala Allahi fainna Allaha AAazeezun hakeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 50, "And if you could but see when the angels take the souls of those who disbelieved... They are striking their faces and their backs and [saying], ""Taste the punishment of the Burning Fire", "وَلَوۡ تَرَىٰٓ إِذۡ يَتَوَفَّى ٱلَّذِينَ كَفَرُواْ ٱلۡمَلَـٰٓئِكَةُ يَضۡرِبُونَ وُجُوهَهُمۡ وَأَدۡبَٰرَهُمۡ وَذُوقُواْ عَذَابَ ٱلۡحَرِيقِ", "Walaw tara ith yatawaffa allatheena kafaroo almalaikatu yadriboona wujoohahum waadbarahum wathooqoo AAathaba alhareeqi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 51, "That is for what your hands have put forth [of evil] and because Allah is not ever unjust to His servants", "ذَٰلِكَ بِمَا قَدَّمَتۡ أَيۡدِيكُمۡ وَأَنَّ ٱللَّهَ لَيۡسَ بِظَلَّـٰمٖ لِّلۡعَبِيدِ", "Thalika bima qaddamat aydeekum waanna Allaha laysa bithallamin lilAAabeedi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 52, "[Theirs is] like the custom of the people of Pharaoh and of those before them. They disbelieved in the signs of Allah, so Allah seized them for their sins. Indeed, Allah is Powerful and severe in penalty", "كَدَأۡبِ ءَالِ فِرۡعَوۡنَ وَٱلَّذِينَ مِن قَبۡلِهِمۡۚ كَفَرُواْ بِـَٔايَٰتِ ٱللَّهِ فَأَخَذَهُمُ ٱللَّهُ بِذُنُوبِهِمۡۚ إِنَّ ٱللَّهَ قَوِيّٞ شَدِيدُ ٱلۡعِقَابِ", "Kadabi ali firAAawna waallatheena min qablihim kafaroo biayati Allahi faakhathahumu Allahu bithunoobihim inna Allaha qawiyyun shadeedu alAAiqabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 53, "That is because Allah would not change a favor which He had bestowed upon a people until they change what is within themselves. And indeed, Allah is Hearing and Knowing", "ذَٰلِكَ بِأَنَّ ٱللَّهَ لَمۡ يَكُ مُغَيِّرٗا نِّعۡمَةً أَنۡعَمَهَا عَلَىٰ قَوۡمٍ حَتَّىٰ يُغَيِّرُواْ مَا بِأَنفُسِهِمۡ وَأَنَّ ٱللَّهَ سَمِيعٌ عَلِيمٞ", "Thalika bianna Allaha lam yaku mughayyiran niAAmatan anAAamaha AAala qawmin hatta yughayyiroo ma bianfusihim waanna Allaha sameeAAun AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 54, "[Theirs is] like the custom of the people of Pharaoh and of those before them. They denied the signs of their Lord, so We destroyed them for their sins, and We drowned the people of Pharaoh. And all [of them] were wrongdoers", "كَدَأۡبِ ءَالِ فِرۡعَوۡنَ وَٱلَّذِينَ مِن قَبۡلِهِمۡۚ كَذَّبُواْ بِـَٔايَٰتِ رَبِّهِمۡ فَأَهۡلَكۡنَٰهُم بِذُنُوبِهِمۡ وَأَغۡرَقۡنَآ ءَالَ فِرۡعَوۡنَۚ وَكُلّٞ كَانُواْ ظَٰلِمِينَ", "Kadabi ali firAAawna waallatheena min qablihim kaththaboo biayati rabbihim faahlaknahum bithunoobihim waaghraqna ala firAAawna wakullun kanoo thalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 55, "Indeed, the worst of living creatures in the sight of Allah are those who have disbelieved, and they will not [ever] believe", "إِنَّ شَرَّ ٱلدَّوَآبِّ عِندَ ٱللَّهِ ٱلَّذِينَ كَفَرُواْ فَهُمۡ لَا يُؤۡمِنُونَ", "Inna sharra alddawabbi AAinda Allahi allatheena kafaroo fahum la yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 56, "The ones with whom you made a treaty but then they break their pledge every time, and they do not fear Allah", "ٱلَّذِينَ عَٰهَدتَّ مِنۡهُمۡ ثُمَّ يَنقُضُونَ عَهۡدَهُمۡ فِي كُلِّ مَرَّةٖ وَهُمۡ لَا يَتَّقُونَ", "Allatheena AAahadta minhum thumma yanqudoona AAahdahum fee kulli marratin wahum la yattaqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 57, "So if you, [O Muhammad], gain dominance over them in war, disperse by [means of] them those behind them that perhaps they will be reminded", "فَإِمَّا تَثۡقَفَنَّهُمۡ فِي ٱلۡحَرۡبِ فَشَرِّدۡ بِهِم مَّنۡ خَلۡفَهُمۡ لَعَلَّهُمۡ يَذَّكَّرُونَ", "Faimma tathqafannahum fee alharbi fasharrid bihim man khalfahum laAAallahum yaththakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 58, "If you [have reason to] fear from a people betrayal, throw [their treaty] back to them, [putting you] on equal terms. Indeed, Allah does not like traitors", "وَإِمَّا تَخَافَنَّ مِن قَوۡمٍ خِيَانَةٗ فَٱنۢبِذۡ إِلَيۡهِمۡ عَلَىٰ سَوَآءٍۚ إِنَّ ٱللَّهَ لَا يُحِبُّ ٱلۡخَآئِنِينَ", "Waimma takhafanna min qawmin khiyanatan fainbith ilayhim AAala sawain inna Allaha la yuhibbu alkhaineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 59, "And let not those who disbelieve think they will escape. Indeed, they will not cause failure [to Allah]", "وَلَا يَحۡسَبَنَّ ٱلَّذِينَ كَفَرُواْ سَبَقُوٓاْۚ إِنَّهُمۡ لَا يُعۡجِزُونَ", "Wala yahsabanna allatheena kafaroo sabaqoo innahum la yuAAjizoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 60, "And prepare against them whatever you are able of power and of steeds of war by which you may terrify the enemy of Allah and your enemy and others besides them whom you do not know [but] whom Allah knows. And whatever you spend in the cause of Allah will be fully repaid to you, and you will not be wronged", "وَأَعِدُّواْ لَهُم مَّا ٱسۡتَطَعۡتُم مِّن قُوَّةٖ وَمِن رِّبَاطِ ٱلۡخَيۡلِ تُرۡهِبُونَ بِهِۦ عَدُوَّ ٱللَّهِ وَعَدُوَّكُمۡ وَءَاخَرِينَ مِن دُونِهِمۡ لَا تَعۡلَمُونَهُمُ ٱللَّهُ يَعۡلَمُهُمۡۚ وَمَا تُنفِقُواْ مِن شَيۡءٖ فِي سَبِيلِ ٱللَّهِ يُوَفَّ إِلَيۡكُمۡ وَأَنتُمۡ لَا تُظۡلَمُونَ", "WaaAAiddoo lahum ma istataAAtum min quwwatin wamin ribati alkhayli turhiboona bihi AAaduwwa Allahi waAAaduwwakum waakhareena min doonihim la taAAlamoonahumu Allahu yaAAlamuhum wama tunfiqoo min shayin fee sabeeli Allahi yuwaffa ilaykum waantum la tuthlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 61, "And if they incline to peace, then incline to it [also] and rely upon Allah. Indeed, it is He who is the Hearing, the Knowing", "۞وَإِن جَنَحُواْ لِلسَّلۡمِ فَٱجۡنَحۡ لَهَا وَتَوَكَّلۡ عَلَى ٱللَّهِۚ إِنَّهُۥ هُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ", "Wain janahoo lilssalmi faijnah laha watawakkal AAala Allahi innahu huwa alssameeAAu alAAaleemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 62, "But if they intend to deceive you - then sufficient for you is Allah. It is He who supported you with His help and with the believers", "وَإِن يُرِيدُوٓاْ أَن يَخۡدَعُوكَ فَإِنَّ حَسۡبَكَ ٱللَّهُۚ هُوَ ٱلَّذِيٓ أَيَّدَكَ بِنَصۡرِهِۦ وَبِٱلۡمُؤۡمِنِينَ", "Wain yureedoo an yakhdaAAooka fainna hasbaka Allahu huwa allathee ayyadaka binasrihi wabialmumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 63, "And brought together their hearts. If you had spent all that is in the earth, you could not have brought their hearts together; but Allah brought them together. Indeed, He is Exalted in Might and Wise", "وَأَلَّفَ بَيۡنَ قُلُوبِهِمۡۚ لَوۡ أَنفَقۡتَ مَا فِي ٱلۡأَرۡضِ جَمِيعٗا مَّآ أَلَّفۡتَ بَيۡنَ قُلُوبِهِمۡ وَلَٰكِنَّ ٱللَّهَ أَلَّفَ بَيۡنَهُمۡۚ إِنَّهُۥ عَزِيزٌ حَكِيمٞ", "Waallafa bayna quloobihim law anfaqta ma fee alardi jameeAAan ma allafta bayna quloobihim walakinna Allaha allafa baynahum innahu AAazeezun hakeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 64, "O Prophet, sufficient for you is Allah and for whoever follows you of the believers", "يَـٰٓأَيُّهَا ٱلنَّبِيُّ حَسۡبُكَ ٱللَّهُ وَمَنِ ٱتَّبَعَكَ مِنَ ٱلۡمُؤۡمِنِينَ", "Ya ayyuha alnnabiyyu hasbuka Allahu wamani ittabaAAaka mina almumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 65, "O Prophet, urge the believers to battle. If there are among you twenty [who are] steadfast, they will overcome two hundred. And if there are among you one hundred [who are] steadfast, they will overcome a thousand of those who have disbelieved because they are a people who do not understand", "يَـٰٓأَيُّهَا ٱلنَّبِيُّ حَرِّضِ ٱلۡمُؤۡمِنِينَ عَلَى ٱلۡقِتَالِۚ إِن يَكُن مِّنكُمۡ عِشۡرُونَ صَٰبِرُونَ يَغۡلِبُواْ مِاْئَتَيۡنِۚ وَإِن يَكُن مِّنكُم مِّاْئَةٞ يَغۡلِبُوٓاْ أَلۡفٗا مِّنَ ٱلَّذِينَ كَفَرُواْ بِأَنَّهُمۡ قَوۡمٞ لَّا يَفۡقَهُونَ", "Ya ayyuha alnnabiyyu harridi almumineena AAala alqitali in yakun minkum AAishroona sabiroona yaghliboo miatayni wain yakun minkum miatun yaghliboo alfan mina allatheena kafaroo biannahum qawmun la yafqahoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 66, "Now, Allah has lightened [the hardship] for you, and He knows that among you is weakness. So if there are from you one hundred [who are] steadfast, they will overcome two hundred. And if there are among you a thousand, they will overcome two thousand by permission of Allah. And Allah is with the steadfast", "ٱلۡـَٰٔنَ خَفَّفَ ٱللَّهُ عَنكُمۡ وَعَلِمَ أَنَّ فِيكُمۡ ضَعۡفٗاۚ فَإِن يَكُن مِّنكُم مِّاْئَةٞ صَابِرَةٞ يَغۡلِبُواْ مِاْئَتَيۡنِۚ وَإِن يَكُن مِّنكُمۡ أَلۡفٞ يَغۡلِبُوٓاْ أَلۡفَيۡنِ بِإِذۡنِ ٱللَّهِۗ وَٱللَّهُ مَعَ ٱلصَّـٰبِرِينَ", "Alana khaffafa Allahu AAankum waAAalima anna feekum daAAfan fain yakun minkum miatun sabiratun yaghliboo miatayni wain yakun minkum alfun yaghliboo alfayni biithni Allahi waAllahu maAAa alssabireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 67, "It is not for a prophet to have captives [of war] until he inflicts a massacre [upon Allah 's enemies] in the land. Some Muslims desire the commodities of this world, but Allah desires [for you] the Hereafter. And Allah is Exalted in Might and Wise", "مَا كَانَ لِنَبِيٍّ أَن يَكُونَ لَهُۥٓ أَسۡرَىٰ حَتَّىٰ يُثۡخِنَ فِي ٱلۡأَرۡضِۚ تُرِيدُونَ عَرَضَ ٱلدُّنۡيَا وَٱللَّهُ يُرِيدُ ٱلۡأٓخِرَةَۗ وَٱللَّهُ عَزِيزٌ حَكِيمٞ", "Ma kana linabiyyin an yakoona lahu asra hatta yuthkhina fee alardi tureedoona AAarada alddunya waAllahu yureedu alakhirata waAllahu AAazeezun hakeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 68, "If not for a decree from Allah that preceded, you would have been touched for what you took by a great punishment", "لَّوۡلَا كِتَٰبٞ مِّنَ ٱللَّهِ سَبَقَ لَمَسَّكُمۡ فِيمَآ أَخَذۡتُمۡ عَذَابٌ عَظِيمٞ", "Lawla kitabun mina Allahi sabaqa lamassakum feema akhathtum AAathabun AAatheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 69, "So consume what you have taken of war booty [as being] lawful and good, and fear Allah. Indeed, Allah is Forgiving and Merciful", "فَكُلُواْ مِمَّا غَنِمۡتُمۡ حَلَٰلٗا طَيِّبٗاۚ وَٱتَّقُواْ ٱللَّهَۚ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ", "Fakuloo mimma ghanimtum halalan tayyiban waittaqoo Allaha inna Allaha ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 70, "O Prophet, say to whoever is in your hands of the captives, ""If Allah knows [any] good in your hearts, He will give you [something] better than what was taken from you, and He will forgive you; and Allah is Forgiving and Merciful", "يَـٰٓأَيُّهَا ٱلنَّبِيُّ قُل لِّمَن فِيٓ أَيۡدِيكُم مِّنَ ٱلۡأَسۡرَىٰٓ إِن يَعۡلَمِ ٱللَّهُ فِي قُلُوبِكُمۡ خَيۡرٗا يُؤۡتِكُمۡ خَيۡرٗا مِّمَّآ أُخِذَ مِنكُمۡ وَيَغۡفِرۡ لَكُمۡۚ وَٱللَّهُ غَفُورٞ رَّحِيمٞ", "Ya ayyuha alnnabiyyu qul liman fee aydeekum mina alasra in yaAAlami Allahu fee quloobikum khayran yutikum khayran mimma okhitha minkum wayaghfir lakum waAllahu ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 71, "But if they intend to betray you - then they have already betrayed Allah before, and He empowered [you] over them. And Allah is Knowing and Wise", "وَإِن يُرِيدُواْ خِيَانَتَكَ فَقَدۡ خَانُواْ ٱللَّهَ مِن قَبۡلُ فَأَمۡكَنَ مِنۡهُمۡۗ وَٱللَّهُ عَلِيمٌ حَكِيمٌ", "Wain yureedoo khiyanataka faqad khanoo Allaha min qablu faamkana minhum waAllahu AAaleemun hakeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 72, "Indeed, those who have believed and emigrated and fought with their wealth and lives in the cause of Allah and those who gave shelter and aided - they are allies of one another. But those who believed and did not emigrate - for you there is no guardianship of them until they emigrate. And if they seek help of you for the religion, then you must help, except against a people between yourselves and whom is a treaty. And Allah is Seeing of what you do", "إِنَّ ٱلَّذِينَ ءَامَنُواْ وَهَاجَرُواْ وَجَٰهَدُواْ بِأَمۡوَٰلِهِمۡ وَأَنفُسِهِمۡ فِي سَبِيلِ ٱللَّهِ وَٱلَّذِينَ ءَاوَواْ وَّنَصَرُوٓاْ أُوْلَـٰٓئِكَ بَعۡضُهُمۡ أَوۡلِيَآءُ بَعۡضٖۚ وَٱلَّذِينَ ءَامَنُواْ وَلَمۡ يُهَاجِرُواْ مَا لَكُم مِّن وَلَٰيَتِهِم مِّن شَيۡءٍ حَتَّىٰ يُهَاجِرُواْۚ وَإِنِ ٱسۡتَنصَرُوكُمۡ فِي ٱلدِّينِ فَعَلَيۡكُمُ ٱلنَّصۡرُ إِلَّا عَلَىٰ قَوۡمِۭ بَيۡنَكُمۡ وَبَيۡنَهُم مِّيثَٰقٞۗ وَٱللَّهُ بِمَا تَعۡمَلُونَ بَصِيرٞ", "Inna allatheena amanoo wahajaroo wajahadoo biamwalihim waanfusihim fee sabeeli Allahi waallatheena awaw wanasaroo olaika baAAduhum awliyao baAAdin waallatheena amanoo walam yuhajiroo ma lakum min walayatihim min shayin hatta yuhajiroo waini istansarookum fee alddeeni faAAalaykumu alnnasru illa AAala qawmin baynakum wabaynahum meethaqun waAllahu bima taAAmaloona baseerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 73, "And those who disbelieved are allies of one another. If you do not do so, there will be fitnah on earth and great corruption", "وَٱلَّذِينَ كَفَرُواْ بَعۡضُهُمۡ أَوۡلِيَآءُ بَعۡضٍۚ إِلَّا تَفۡعَلُوهُ تَكُن فِتۡنَةٞ فِي ٱلۡأَرۡضِ وَفَسَادٞ كَبِيرٞ", "Waallatheena kafaroo baAAduhum awliyao baAAdin illa tafAAaloohu takun fitnatun fee alardi wafasadun kabeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 74, "But those who have believed and emigrated and fought in the cause of Allah and those who gave shelter and aided - it is they who are the believers, truly. For them is forgiveness and noble provision", "وَٱلَّذِينَ ءَامَنُواْ وَهَاجَرُواْ وَجَٰهَدُواْ فِي سَبِيلِ ٱللَّهِ وَٱلَّذِينَ ءَاوَواْ وَّنَصَرُوٓاْ أُوْلَـٰٓئِكَ هُمُ ٱلۡمُؤۡمِنُونَ حَقّٗاۚ لَّهُم مَّغۡفِرَةٞ وَرِزۡقٞ كَرِيمٞ", "Waallatheena amanoo wahajaroo wajahadoo fee sabeeli Allahi waallatheena awaw wanasaroo olaika humu almuminoona haqqan lahum maghfiratun warizqun kareemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (8, 75, "And those who believed after [the initial emigration] and emigrated and fought with you - they are of you. But those of [blood] relationship are more entitled [to inheritance] in the decree of Allah. Indeed, Allah is Knowing of all things", "وَٱلَّذِينَ ءَامَنُواْ مِنۢ بَعۡدُ وَهَاجَرُواْ وَجَٰهَدُواْ مَعَكُمۡ فَأُوْلَـٰٓئِكَ مِنكُمۡۚ وَأُوْلُواْ ٱلۡأَرۡحَامِ بَعۡضُهُمۡ أَوۡلَىٰ بِبَعۡضٖ فِي كِتَٰبِ ٱللَّهِۚ إِنَّ ٱللَّهَ بِكُلِّ شَيۡءٍ عَلِيمُۢ", "Waallatheena amanoo min baAAdu wahajaroo wajahadoo maAAakum faolaika minkum waoloo alarhami baAAduhum awla bibaAAdin fee kitabi Allahi inna Allaha bikulli shayin AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 1, "[This is a declaration of] disassociation, from Allah and His Messenger, to those with whom you had made a treaty among the polytheists", "بَرَآءَةٞ مِّنَ ٱللَّهِ وَرَسُولِهِۦٓ إِلَى ٱلَّذِينَ عَٰهَدتُّم مِّنَ ٱلۡمُشۡرِكِينَ", "Baraatun mina Allahi warasoolihi ila allatheena AAahadtum mina almushrikeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 2, "So travel freely, [O disbelievers], throughout the land [during] four months but know that you cannot cause failure to Allah and that Allah will disgrace the disbelievers", "فَسِيحُواْ فِي ٱلۡأَرۡضِ أَرۡبَعَةَ أَشۡهُرٖ وَٱعۡلَمُوٓاْ أَنَّكُمۡ غَيۡرُ مُعۡجِزِي ٱللَّهِ وَأَنَّ ٱللَّهَ مُخۡزِي ٱلۡكَٰفِرِينَ", "Faseehoo fee alardi arbaAAata ashhurin waiAAlamoo annakum ghayru muAAjizee Allahi waanna Allaha mukhzee alkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 3, "And [it is] an announcement from Allah and His Messenger to the people on the day of the greater pilgrimage that Allah is disassociated from the disbelievers, and [so is] His Messenger. So if you repent, that is best for you; but if you turn away - then know that you will not cause failure to Allah. And give tidings to those who disbelieve of a painful punishment", "وَأَذَٰنٞ مِّنَ ٱللَّهِ وَرَسُولِهِۦٓ إِلَى ٱلنَّاسِ يَوۡمَ ٱلۡحَجِّ ٱلۡأَكۡبَرِ أَنَّ ٱللَّهَ بَرِيٓءٞ مِّنَ ٱلۡمُشۡرِكِينَ وَرَسُولُهُۥۚ فَإِن تُبۡتُمۡ فَهُوَ خَيۡرٞ لَّكُمۡۖ وَإِن تَوَلَّيۡتُمۡ فَٱعۡلَمُوٓاْ أَنَّكُمۡ غَيۡرُ مُعۡجِزِي ٱللَّهِۗ وَبَشِّرِ ٱلَّذِينَ كَفَرُواْ بِعَذَابٍ أَلِيمٍ", "Waathanun mina Allahi warasoolihi ila alnnasi yawma alhajji alakbari anna Allaha bareeon mina almushrikeena warasooluhu fain tubtum fahuwa khayrun lakum wain tawallaytum faiAAlamoo annakum ghayru muAAjizee Allahi wabashshiri allatheena kafaroo biAAathabin aleemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 4, "Excepted are those with whom you made a treaty among the polytheists and then they have not been deficient toward you in anything or supported anyone against you; so complete for them their treaty until their term [has ended]. Indeed, Allah loves the righteous [who fear Him]", "إِلَّا ٱلَّذِينَ عَٰهَدتُّم مِّنَ ٱلۡمُشۡرِكِينَ ثُمَّ لَمۡ يَنقُصُوكُمۡ شَيۡـٔٗا وَلَمۡ يُظَٰهِرُواْ عَلَيۡكُمۡ أَحَدٗا فَأَتِمُّوٓاْ إِلَيۡهِمۡ عَهۡدَهُمۡ إِلَىٰ مُدَّتِهِمۡۚ إِنَّ ٱللَّهَ يُحِبُّ ٱلۡمُتَّقِينَ", "Illa allatheena AAahadtum mina almushrikeena thumma lam yanqusookum shayan walam yuthahiroo AAalaykum ahadan faatimmoo ilayhim AAahdahum ila muddatihim inna Allaha yuhibbu almuttaqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 5, "And when the sacred months have passed, then kill the polytheists wherever you find them and capture them and besiege them and sit in wait for them at every place of ambush. But if they should repent, establish prayer, and give zakah, let them [go] on their way. Indeed, Allah is Forgiving and Merciful", "فَإِذَا ٱنسَلَخَ ٱلۡأَشۡهُرُ ٱلۡحُرُمُ فَٱقۡتُلُواْ ٱلۡمُشۡرِكِينَ حَيۡثُ وَجَدتُّمُوهُمۡ وَخُذُوهُمۡ وَٱحۡصُرُوهُمۡ وَٱقۡعُدُواْ لَهُمۡ كُلَّ مَرۡصَدٖۚ فَإِن تَابُواْ وَأَقَامُواْ ٱلصَّلَوٰةَ وَءَاتَوُاْ ٱلزَّكَوٰةَ فَخَلُّواْ سَبِيلَهُمۡۚ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ", "Faitha insalakha alashhuru alhurumu faoqtuloo almushrikeena haythu wajadtumoohum wakhuthoohum waohsuroohum waoqAAudoo lahum kulla marsadin fain taboo waaqamoo alssalata waatawoo alzzakata fakhalloo sabeelahum inna Allaha ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 6, "And if any one of the polytheists seeks your protection, then grant him protection so that he may hear the words of Allah. Then deliver him to his place of safety. That is because they are a people who do not know", "وَإِنۡ أَحَدٞ مِّنَ ٱلۡمُشۡرِكِينَ ٱسۡتَجَارَكَ فَأَجِرۡهُ حَتَّىٰ يَسۡمَعَ كَلَٰمَ ٱللَّهِ ثُمَّ أَبۡلِغۡهُ مَأۡمَنَهُۥۚ ذَٰلِكَ بِأَنَّهُمۡ قَوۡمٞ لَّا يَعۡلَمُونَ", "Wain ahadun mina almushrikeena istajaraka faajirhu hatta yasmaAAa kalama Allahi thumma ablighhu mamanahu thalika biannahum qawmun la yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 7, "How can there be for the polytheists a treaty in the sight of Allah and with His Messenger, except for those with whom you made a treaty at al-Masjid al-Haram? So as long as they are upright toward you, be upright toward them. Indeed, Allah loves the righteous [who fear Him]", "كَيۡفَ يَكُونُ لِلۡمُشۡرِكِينَ عَهۡدٌ عِندَ ٱللَّهِ وَعِندَ رَسُولِهِۦٓ إِلَّا ٱلَّذِينَ عَٰهَدتُّمۡ عِندَ ٱلۡمَسۡجِدِ ٱلۡحَرَامِۖ فَمَا ٱسۡتَقَٰمُواْ لَكُمۡ فَٱسۡتَقِيمُواْ لَهُمۡۚ إِنَّ ٱللَّهَ يُحِبُّ ٱلۡمُتَّقِينَ", "Kayfa yakoonu lilmushrikeena AAahdun AAinda Allahi waAAinda rasoolihi illa allatheena AAahadtum AAinda almasjidi alharami fama istaqamoo lakum faistaqeemoo lahum inna Allaha yuhibbu almuttaqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 8, "How [can there be a treaty] while, if they gain dominance over you, they do not observe concerning you any pact of kinship or covenant of protection? They satisfy you with their mouths, but their hearts refuse [compliance], and most of them are defiantly disobedient", "كَيۡفَ وَإِن يَظۡهَرُواْ عَلَيۡكُمۡ لَا يَرۡقُبُواْ فِيكُمۡ إِلّٗا وَلَا ذِمَّةٗۚ يُرۡضُونَكُم بِأَفۡوَٰهِهِمۡ وَتَأۡبَىٰ قُلُوبُهُمۡ وَأَكۡثَرُهُمۡ فَٰسِقُونَ", "Kayfa wain yathharoo AAalaykum la yarquboo feekum illan wala thimmatan yurdoonakum biafwahihim wataba quloobuhum waaktharuhum fasiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 9, "They have exchanged the signs of Allah for a small price and averted [people] from His way. Indeed, it was evil that they were doing", "ٱشۡتَرَوۡاْ بِـَٔايَٰتِ ٱللَّهِ ثَمَنٗا قَلِيلٗا فَصَدُّواْ عَن سَبِيلِهِۦٓۚ إِنَّهُمۡ سَآءَ مَا كَانُواْ يَعۡمَلُونَ", "Ishtaraw biayati Allahi thamanan qaleelan fasaddoo AAan sabeelihi innahum saa ma kanoo yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 10, "They do not observe toward a believer any pact of kinship or covenant of protection. And it is they who are the transgressors", "لَا يَرۡقُبُونَ فِي مُؤۡمِنٍ إِلّٗا وَلَا ذِمَّةٗۚ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُعۡتَدُونَ", "La yarquboona fee muminin illan wala thimmatan waolaika humu almuAAtadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 11, "But if they repent, establish prayer, and give zakah, then they are your brothers in religion; and We detail the verses for a people who know", "فَإِن تَابُواْ وَأَقَامُواْ ٱلصَّلَوٰةَ وَءَاتَوُاْ ٱلزَّكَوٰةَ فَإِخۡوَٰنُكُمۡ فِي ٱلدِّينِۗ وَنُفَصِّلُ ٱلۡأٓيَٰتِ لِقَوۡمٖ يَعۡلَمُونَ", "Fain taboo waaqamoo alssalata waatawoo alzzakata faikhwanukum fee alddeeni wanufassilu alayati liqawmin yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 12, "And if they break their oaths after their treaty and defame your religion, then fight the leaders of disbelief, for indeed, there are no oaths [sacred] to them; [fight them that] they might cease", "وَإِن نَّكَثُوٓاْ أَيۡمَٰنَهُم مِّنۢ بَعۡدِ عَهۡدِهِمۡ وَطَعَنُواْ فِي دِينِكُمۡ فَقَٰتِلُوٓاْ أَئِمَّةَ ٱلۡكُفۡرِ إِنَّهُمۡ لَآ أَيۡمَٰنَ لَهُمۡ لَعَلَّهُمۡ يَنتَهُونَ", "Wain nakathoo aymanahum min baAAdi AAahdihim wataAAanoo fee deenikum faqatiloo aimmata alkufri innahum la aymana lahum laAAallahum yantahoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 13, "Would you not fight a people who broke their oaths and determined to expel the Messenger, and they had begun [the attack upon] you the first time? Do you fear them? But Allah has more right that you should fear Him, if you are [truly] believers", "أَلَا تُقَٰتِلُونَ قَوۡمٗا نَّكَثُوٓاْ أَيۡمَٰنَهُمۡ وَهَمُّواْ بِإِخۡرَاجِ ٱلرَّسُولِ وَهُم بَدَءُوكُمۡ أَوَّلَ مَرَّةٍۚ أَتَخۡشَوۡنَهُمۡۚ فَٱللَّهُ أَحَقُّ أَن تَخۡشَوۡهُ إِن كُنتُم مُّؤۡمِنِينَ", "Ala tuqatiloona qawman nakathoo aymanahum wahammoo biikhraji alrrasooli wahum badaookum awwala marratin atakhshawnahum faAllahu ahaqqu an takhshawhu in kuntum mumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 14, "Fight them; Allah will punish them by your hands and will disgrace them and give you victory over them and satisfy the breasts of a believing people", "قَٰتِلُوهُمۡ يُعَذِّبۡهُمُ ٱللَّهُ بِأَيۡدِيكُمۡ وَيُخۡزِهِمۡ وَيَنصُرۡكُمۡ عَلَيۡهِمۡ وَيَشۡفِ صُدُورَ قَوۡمٖ مُّؤۡمِنِينَ", "Qatiloohum yuAAaththibhumu Allahu biaydeekum wayukhzihim wayansurkum AAalayhim wayashfi sudoora qawmin mumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 15, "And remove the fury in the believers' hearts. And Allah turns in forgiveness to whom He wills; and Allah is Knowing and Wise", "وَيُذۡهِبۡ غَيۡظَ قُلُوبِهِمۡۗ وَيَتُوبُ ٱللَّهُ عَلَىٰ مَن يَشَآءُۗ وَٱللَّهُ عَلِيمٌ حَكِيمٌ", "Wayuthhib ghaytha quloobihim wayatoobu Allahu AAala man yashao waAllahu AAaleemun hakeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 16, "Do you think that you will be left [as you are] while Allah has not yet made evident those among you who strive [for His cause] and do not take other than Allah, His Messenger and the believers as intimates? And Allah is Acquainted with what you do", "أَمۡ حَسِبۡتُمۡ أَن تُتۡرَكُواْ وَلَمَّا يَعۡلَمِ ٱللَّهُ ٱلَّذِينَ جَٰهَدُواْ مِنكُمۡ وَلَمۡ يَتَّخِذُواْ مِن دُونِ ٱللَّهِ وَلَا رَسُولِهِۦ وَلَا ٱلۡمُؤۡمِنِينَ وَلِيجَةٗۚ وَٱللَّهُ خَبِيرُۢ بِمَا تَعۡمَلُونَ", "Am hasibtum an tutrakoo walamma yaAAlami Allahu allatheena jahadoo minkum walam yattakhithoo min dooni Allahi wala rasoolihi wala almumineena waleejatan waAllahu khabeerun bima taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 17, "It is not for the polytheists to maintain the mosques of Allah [while] witnessing against themselves with disbelief. [For] those, their deeds have become worthless, and in the Fire they will abide eternally", "مَا كَانَ لِلۡمُشۡرِكِينَ أَن يَعۡمُرُواْ مَسَٰجِدَ ٱللَّهِ شَٰهِدِينَ عَلَىٰٓ أَنفُسِهِم بِٱلۡكُفۡرِۚ أُوْلَـٰٓئِكَ حَبِطَتۡ أَعۡمَٰلُهُمۡ وَفِي ٱلنَّارِ هُمۡ خَٰلِدُونَ", "Ma kana lilmushrikeena an yaAAmuroo masajida Allahi shahideena AAala anfusihim bialkufri olaika habitat aAAmaluhum wafee alnnari hum khalidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 18, "The mosques of Allah are only to be maintained by those who believe in Allah and the Last Day and establish prayer and give zakah and do not fear except Allah, for it is expected that those will be of the [rightly] guided", "إِنَّمَا يَعۡمُرُ مَسَٰجِدَ ٱللَّهِ مَنۡ ءَامَنَ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ وَأَقَامَ ٱلصَّلَوٰةَ وَءَاتَى ٱلزَّكَوٰةَ وَلَمۡ يَخۡشَ إِلَّا ٱللَّهَۖ فَعَسَىٰٓ أُوْلَـٰٓئِكَ أَن يَكُونُواْ مِنَ ٱلۡمُهۡتَدِينَ", "Innama yaAAmuru masajida Allahi man amana biAllahi waalyawmi alakhiri waaqama alssalata waata alzzakata walam yakhsha illa Allaha faAAasa olaika an yakoonoo mina almuhtadeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 19, "Have you made the providing of water for the pilgrim and the maintenance of al-Masjid al-Haram equal to [the deeds of] one who believes in Allah and the Last Day and strives in the cause of Allah? They are not equal in the sight of Allah. And Allah does not guide the wrongdoing people", "۞أَجَعَلۡتُمۡ سِقَايَةَ ٱلۡحَآجِّ وَعِمَارَةَ ٱلۡمَسۡجِدِ ٱلۡحَرَامِ كَمَنۡ ءَامَنَ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ وَجَٰهَدَ فِي سَبِيلِ ٱللَّهِۚ لَا يَسۡتَوُۥنَ عِندَ ٱللَّهِۗ وَٱللَّهُ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلظَّـٰلِمِينَ", "AjaAAaltum siqayata alhajji waAAimarata almasjidi alharami kaman amana biAllahi waalyawmi alakhiri wajahada fee sabeeli Allahi la yastawoona AAinda Allahi waAllahu la yahdee alqawma alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 20, "The ones who have believed, emigrated and striven in the cause of Allah with their wealth and their lives are greater in rank in the sight of Allah. And it is those who are the attainers [of success]", "ٱلَّذِينَ ءَامَنُواْ وَهَاجَرُواْ وَجَٰهَدُواْ فِي سَبِيلِ ٱللَّهِ بِأَمۡوَٰلِهِمۡ وَأَنفُسِهِمۡ أَعۡظَمُ دَرَجَةً عِندَ ٱللَّهِۚ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡفَآئِزُونَ", "Allatheena amanoo wahajaroo wajahadoo fee sabeeli Allahi biamwalihim waanfusihim aAAthamu darajatan AAinda Allahi waolaika humu alfaizoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 21, "Their Lord gives them good tidings of mercy from Him and approval and of gardens for them wherein is enduring pleasure", "يُبَشِّرُهُمۡ رَبُّهُم بِرَحۡمَةٖ مِّنۡهُ وَرِضۡوَٰنٖ وَجَنَّـٰتٖ لَّهُمۡ فِيهَا نَعِيمٞ مُّقِيمٌ", "Yubashshiruhum rabbuhum birahmatin minhu waridwanin wajannatin lahum feeha naAAeemun muqeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 22, "[They will be] abiding therein forever. Indeed, Allah has with Him a great reward", "خَٰلِدِينَ فِيهَآ أَبَدًاۚ إِنَّ ٱللَّهَ عِندَهُۥٓ أَجۡرٌ عَظِيمٞ", "Khalideena feeha abadan inna Allaha AAindahu ajrun AAatheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 23, "O you who have believed, do not take your fathers or your brothers as allies if they have preferred disbelief over belief. And whoever does so among you - then it is those who are the wrongdoers", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَتَّخِذُوٓاْ ءَابَآءَكُمۡ وَإِخۡوَٰنَكُمۡ أَوۡلِيَآءَ إِنِ ٱسۡتَحَبُّواْ ٱلۡكُفۡرَ عَلَى ٱلۡإِيمَٰنِۚ وَمَن يَتَوَلَّهُم مِّنكُمۡ فَأُوْلَـٰٓئِكَ هُمُ ٱلظَّـٰلِمُونَ", "Ya ayyuha allatheena amanoo la tattakhithoo abaakum waikhwanakum awliyaa ini istahabboo alkufra AAala aleemani waman yatawallahum minkum faolaika humu alththalimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 24, "Say, [O Muhammad], ""If your fathers, your sons, your brothers, your wives, your relatives, wealth which you have obtained, commerce wherein you fear decline, and dwellings with which you are pleased are more beloved to you than Allah and His Messenger and jihad in His cause, then wait until Allah executes His command. And Allah does not guide the defiantly disobedient people", "قُلۡ إِن كَانَ ءَابَآؤُكُمۡ وَأَبۡنَآؤُكُمۡ وَإِخۡوَٰنُكُمۡ وَأَزۡوَٰجُكُمۡ وَعَشِيرَتُكُمۡ وَأَمۡوَٰلٌ ٱقۡتَرَفۡتُمُوهَا وَتِجَٰرَةٞ تَخۡشَوۡنَ كَسَادَهَا وَمَسَٰكِنُ تَرۡضَوۡنَهَآ أَحَبَّ إِلَيۡكُم مِّنَ ٱللَّهِ وَرَسُولِهِۦ وَجِهَادٖ فِي سَبِيلِهِۦ فَتَرَبَّصُواْ حَتَّىٰ يَأۡتِيَ ٱللَّهُ بِأَمۡرِهِۦۗ وَٱللَّهُ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلۡفَٰسِقِينَ", "Qul in kana abaokum waabnaokum waikhwanukum waazwajukum waAAasheeratukum waamwalun iqtaraftumooha watijaratun takhshawna kasadaha wamasakinu tardawnaha ahabba ilaykum mina Allahi warasoolihi wajihadin fee sabeelihi fatarabbasoo hatta yatiya Allahu biamrihi waAllahu la yahdee alqawma alfasiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 25, "Allah has already given you victory in many regions and [even] on the day of Hunayn, when your great number pleased you, but it did not avail you at all, and the earth was confining for you with its vastness; then you turned back, fleeing", "لَقَدۡ نَصَرَكُمُ ٱللَّهُ فِي مَوَاطِنَ كَثِيرَةٖ وَيَوۡمَ حُنَيۡنٍ إِذۡ أَعۡجَبَتۡكُمۡ كَثۡرَتُكُمۡ فَلَمۡ تُغۡنِ عَنكُمۡ شَيۡـٔٗا وَضَاقَتۡ عَلَيۡكُمُ ٱلۡأَرۡضُ بِمَا رَحُبَتۡ ثُمَّ وَلَّيۡتُم مُّدۡبِرِينَ", "Laqad nasarakumu Allahu fee mawatina katheeratin wayawma hunaynin ith aAAjabatkum kathratukum falam tughni AAankum shayan wadaqat AAalaykumu alardu bima rahubat thumma wallaytum mudbireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 26, "Then Allah sent down His tranquillity upon His Messenger and upon the believers and sent down soldiers angels whom you did not see and punished those who disbelieved. And that is the recompense of the disbelievers", "ثُمَّ أَنزَلَ ٱللَّهُ سَكِينَتَهُۥ عَلَىٰ رَسُولِهِۦ وَعَلَى ٱلۡمُؤۡمِنِينَ وَأَنزَلَ جُنُودٗا لَّمۡ تَرَوۡهَا وَعَذَّبَ ٱلَّذِينَ كَفَرُواْۚ وَذَٰلِكَ جَزَآءُ ٱلۡكَٰفِرِينَ", "Thumma anzala Allahu sakeenatahu AAala rasoolihi waAAala almumineena waanzala junoodan lam tarawha waAAaththaba allatheena kafaroo wathalika jazao alkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 27, "Then Allah will accept repentance after that for whom He wills; and Allah is Forgiving and Merciful", "ثُمَّ يَتُوبُ ٱللَّهُ مِنۢ بَعۡدِ ذَٰلِكَ عَلَىٰ مَن يَشَآءُۗ وَٱللَّهُ غَفُورٞ رَّحِيمٞ", "Thumma yatoobu Allahu min baAAdi thalika AAala man yashao waAllahu ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 28, "O you who have believed, indeed the polytheists are unclean, so let them not approach al-Masjid al-Haram after this, their [final] year. And if you fear privation, Allah will enrich you from His bounty if He wills. Indeed, Allah is Knowing and Wise", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِنَّمَا ٱلۡمُشۡرِكُونَ نَجَسٞ فَلَا يَقۡرَبُواْ ٱلۡمَسۡجِدَ ٱلۡحَرَامَ بَعۡدَ عَامِهِمۡ هَٰذَاۚ وَإِنۡ خِفۡتُمۡ عَيۡلَةٗ فَسَوۡفَ يُغۡنِيكُمُ ٱللَّهُ مِن فَضۡلِهِۦٓ إِن شَآءَۚ إِنَّ ٱللَّهَ عَلِيمٌ حَكِيمٞ", "Ya ayyuha allatheena amanoo innama almushrikoona najasun fala yaqraboo almasjida alharama baAAda AAamihim hatha wain khiftum AAaylatan fasawfa yughneekumu Allahu min fadlihi in shaa inna Allaha AAaleemun hakeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 29, "Fight those who do not believe in Allah or in the Last Day and who do not consider unlawful what Allah and His Messenger have made unlawful and who do not adopt the religion of truth from those who were given the Scripture - [fight] until they give the jizyah willingly while they are humbled", "قَٰتِلُواْ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱللَّهِ وَلَا بِٱلۡيَوۡمِ ٱلۡأٓخِرِ وَلَا يُحَرِّمُونَ مَا حَرَّمَ ٱللَّهُ وَرَسُولُهُۥ وَلَا يَدِينُونَ دِينَ ٱلۡحَقِّ مِنَ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ حَتَّىٰ يُعۡطُواْ ٱلۡجِزۡيَةَ عَن يَدٖ وَهُمۡ صَٰغِرُونَ", "Qatiloo allatheena la yuminoona biAllahi wala bialyawmi alakhiri wala yuharrimoona ma harrama Allahu warasooluhu wala yadeenoona deena alhaqqi mina allatheena ootoo alkitaba hatta yuAAtoo aljizyata AAan yadin wahum saghiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 30, "The Jews say, ""Ezra is the son of Allah ""; and the Christians say, ""The Messiah is the son of Allah."" That is their statement from their mouths; they imitate the saying of those who disbelieved [before them]. May Allah destroy them; how are they deluded", "وَقَالَتِ ٱلۡيَهُودُ عُزَيۡرٌ ٱبۡنُ ٱللَّهِ وَقَالَتِ ٱلنَّصَٰرَى ٱلۡمَسِيحُ ٱبۡنُ ٱللَّهِۖ ذَٰلِكَ قَوۡلُهُم بِأَفۡوَٰهِهِمۡۖ يُضَٰهِـُٔونَ قَوۡلَ ٱلَّذِينَ كَفَرُواْ مِن قَبۡلُۚ قَٰتَلَهُمُ ٱللَّهُۖ أَنَّىٰ يُؤۡفَكُونَ", "Waqalati alyahoodu AAuzayrun ibnu Allahi waqalati alnnasara almaseehu ibnu Allahi thalika qawluhum biafwahihim yudahioona qawla allatheena kafaroo min qablu qatalahumu Allahu anna yufakoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 31, "They have taken their scholars and monks as lords besides Allah, and [also] the Messiah, the son of Mary. And they were not commanded except to worship one God; there is no deity except Him. Exalted is He above whatever they associate with Him", "ٱتَّخَذُوٓاْ أَحۡبَارَهُمۡ وَرُهۡبَٰنَهُمۡ أَرۡبَابٗا مِّن دُونِ ٱللَّهِ وَٱلۡمَسِيحَ ٱبۡنَ مَرۡيَمَ وَمَآ أُمِرُوٓاْ إِلَّا لِيَعۡبُدُوٓاْ إِلَٰهٗا وَٰحِدٗاۖ لَّآ إِلَٰهَ إِلَّا هُوَۚ سُبۡحَٰنَهُۥ عَمَّا يُشۡرِكُونَ", "Ittakhathoo ahbarahum waruhbanahum arbaban min dooni Allahi waalmaseeha ibna maryama wama omiroo illa liyaAAbudoo ilahan wahidan la ilaha illa huwa subhanahu AAamma yushrikoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 32, "They want to extinguish the light of Allah with their mouths, but Allah refuses except to perfect His light, although the disbelievers dislike it", "يُرِيدُونَ أَن يُطۡفِـُٔواْ نُورَ ٱللَّهِ بِأَفۡوَٰهِهِمۡ وَيَأۡبَى ٱللَّهُ إِلَّآ أَن يُتِمَّ نُورَهُۥ وَلَوۡ كَرِهَ ٱلۡكَٰفِرُونَ", "Yureedoona an yutfioo noora Allahi biafwahihim wayaba Allahu illa an yutimma noorahu walaw kariha alkafiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 33, "It is He who has sent His Messenger with guidance and the religion of truth to manifest it over all religion, although they who associate others with Allah dislike it", "هُوَ ٱلَّذِيٓ أَرۡسَلَ رَسُولَهُۥ بِٱلۡهُدَىٰ وَدِينِ ٱلۡحَقِّ لِيُظۡهِرَهُۥ عَلَى ٱلدِّينِ كُلِّهِۦ وَلَوۡ كَرِهَ ٱلۡمُشۡرِكُونَ", "Huwa allathee arsala rasoolahu bialhuda wadeeni alhaqqi liyuthhirahu AAala alddeeni kullihi walaw kariha almushrikoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 34, "O you who have believed, indeed many of the scholars and the monks devour the wealth of people unjustly and avert [them] from the way of Allah. And those who hoard gold and silver and spend it not in the way of Allah - give them tidings of a painful punishment", "۞يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِنَّ كَثِيرٗا مِّنَ ٱلۡأَحۡبَارِ وَٱلرُّهۡبَانِ لَيَأۡكُلُونَ أَمۡوَٰلَ ٱلنَّاسِ بِٱلۡبَٰطِلِ وَيَصُدُّونَ عَن سَبِيلِ ٱللَّهِۗ وَٱلَّذِينَ يَكۡنِزُونَ ٱلذَّهَبَ وَٱلۡفِضَّةَ وَلَا يُنفِقُونَهَا فِي سَبِيلِ ٱللَّهِ فَبَشِّرۡهُم بِعَذَابٍ أَلِيمٖ", "Ya ayyuha allatheena amanoo inna katheeran mina alahbari waalrruhbani layakuloona amwala alnnasi bialbatili wayasuddoona AAan sabeeli Allahi waallatheena yaknizoona alththahaba waalfiddata wala yunfiqoonaha fee sabeeli Allahi fabashshirhum biAAathabin aleemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 35, "The Day when it will be heated in the fire of Hell and seared therewith will be their foreheads, their flanks, and their backs, [it will be said], ""This is what you hoarded for yourselves, so taste what you used to hoard", "يَوۡمَ يُحۡمَىٰ عَلَيۡهَا فِي نَارِ جَهَنَّمَ فَتُكۡوَىٰ بِهَا جِبَاهُهُمۡ وَجُنُوبُهُمۡ وَظُهُورُهُمۡۖ هَٰذَا مَا كَنَزۡتُمۡ لِأَنفُسِكُمۡ فَذُوقُواْ مَا كُنتُمۡ تَكۡنِزُونَ", "Yawma yuhma AAalayha fee nari jahannama fatukwa biha jibahuhum wajunoobuhum wathuhooruhum hatha ma kanaztum lianfusikum fathooqoo ma kuntum taknizoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 36, "Indeed, the number of months with Allah is twelve [lunar] months in the register of Allah [from] the day He created the heavens and the earth; of these, four are sacred. That is the correct religion, so do not wrong yourselves during them. And fight against the disbelievers collectively as they fight against you collectively. And know that Allah is with the righteous [who fear Him]", "إِنَّ عِدَّةَ ٱلشُّهُورِ عِندَ ٱللَّهِ ٱثۡنَا عَشَرَ شَهۡرٗا فِي كِتَٰبِ ٱللَّهِ يَوۡمَ خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ مِنۡهَآ أَرۡبَعَةٌ حُرُمٞۚ ذَٰلِكَ ٱلدِّينُ ٱلۡقَيِّمُۚ فَلَا تَظۡلِمُواْ فِيهِنَّ أَنفُسَكُمۡۚ وَقَٰتِلُواْ ٱلۡمُشۡرِكِينَ كَآفَّةٗ كَمَا يُقَٰتِلُونَكُمۡ كَآفَّةٗۚ وَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ مَعَ ٱلۡمُتَّقِينَ", "Inna AAiddata alshshuhoori AAinda Allahi ithna AAashara shahran fee kitabi Allahi yawma khalaqa alssamawati waalarda minha arbaAAatun hurumun thalika alddeenu alqayyimu fala tathlimoo feehinna anfusakum waqatiloo almushrikeena kaffatan kama yuqatiloonakum kaffatan waiAAlamoo anna Allaha maAAa almuttaqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 37, "Indeed, the postponing [of restriction within sacred months] is an increase in disbelief by which those who have disbelieved are led [further] astray. They make it lawful one year and unlawful another year to correspond to the number made unlawful by Allah and [thus] make lawful what Allah has made unlawful. Made pleasing to them is the evil of their deeds; and Allah does not guide the disbelieving people", "إِنَّمَا ٱلنَّسِيٓءُ زِيَادَةٞ فِي ٱلۡكُفۡرِۖ يُضَلُّ بِهِ ٱلَّذِينَ كَفَرُواْ يُحِلُّونَهُۥ عَامٗا وَيُحَرِّمُونَهُۥ عَامٗا لِّيُوَاطِـُٔواْ عِدَّةَ مَا حَرَّمَ ٱللَّهُ فَيُحِلُّواْ مَا حَرَّمَ ٱللَّهُۚ زُيِّنَ لَهُمۡ سُوٓءُ أَعۡمَٰلِهِمۡۗ وَٱللَّهُ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلۡكَٰفِرِينَ", "Innama alnnaseeo ziyadatun fee alkufri yudallu bihi allatheena kafaroo yuhilloonahu AAaman wayuharrimoonahu AAaman liyuwatioo AAiddata ma harrama Allahu fayuhilloo ma harrama Allahu zuyyina lahum sooo aAAmalihim waAllahu la yahdee alqawma alkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 38, "O you who have believed, what is [the matter] with you that, when you are told to go forth in the cause of Allah, you adhere heavily to the earth? Are you satisfied with the life of this world rather than the Hereafter? But what is the enjoyment of worldly life compared to the Hereafter except a [very] little", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ مَا لَكُمۡ إِذَا قِيلَ لَكُمُ ٱنفِرُواْ فِي سَبِيلِ ٱللَّهِ ٱثَّاقَلۡتُمۡ إِلَى ٱلۡأَرۡضِۚ أَرَضِيتُم بِٱلۡحَيَوٰةِ ٱلدُّنۡيَا مِنَ ٱلۡأٓخِرَةِۚ فَمَا مَتَٰعُ ٱلۡحَيَوٰةِ ٱلدُّنۡيَا فِي ٱلۡأٓخِرَةِ إِلَّا قَلِيلٌ", "Ya ayyuha allatheena amanoo ma lakum itha qeela lakumu infiroo fee sabeeli Allahi iththaqaltum ila alardi aradeetum bialhayati alddunya mina alakhirati fama mataAAu alhayati alddunya fee alakhirati illa qaleelun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 39, "If you do not go forth, He will punish you with a painful punishment and will replace you with another people, and you will not harm Him at all. And Allah is over all things competent", "إِلَّا تَنفِرُواْ يُعَذِّبۡكُمۡ عَذَابًا أَلِيمٗا وَيَسۡتَبۡدِلۡ قَوۡمًا غَيۡرَكُمۡ وَلَا تَضُرُّوهُ شَيۡـٔٗاۗ وَٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ", "Illa tanfiroo yuAAaththibkum AAathaban aleeman wayastabdil qawman ghayrakum wala tadurroohu shayan waAllahu AAala kulli shayin qadeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 40, "If you do not aid the Prophet - Allah has already aided him when those who disbelieved had driven him out [of Makkah] as one of two, when they were in the cave and he said to his companion, ""Do not grieve; indeed Allah is with us."" And Allah sent down his tranquillity upon him and supported him with angels you did not see and made the word of those who disbelieved the lowest, while the word of Allah - that is the highest. And Allah is Exalted in Might and Wise", "إِلَّا تَنصُرُوهُ فَقَدۡ نَصَرَهُ ٱللَّهُ إِذۡ أَخۡرَجَهُ ٱلَّذِينَ كَفَرُواْ ثَانِيَ ٱثۡنَيۡنِ إِذۡ هُمَا فِي ٱلۡغَارِ إِذۡ يَقُولُ لِصَٰحِبِهِۦ لَا تَحۡزَنۡ إِنَّ ٱللَّهَ مَعَنَاۖ فَأَنزَلَ ٱللَّهُ سَكِينَتَهُۥ عَلَيۡهِ وَأَيَّدَهُۥ بِجُنُودٖ لَّمۡ تَرَوۡهَا وَجَعَلَ كَلِمَةَ ٱلَّذِينَ كَفَرُواْ ٱلسُّفۡلَىٰۗ وَكَلِمَةُ ٱللَّهِ هِيَ ٱلۡعُلۡيَاۗ وَٱللَّهُ عَزِيزٌ حَكِيمٌ", "Illa tansuroohu faqad nasarahu Allahu ith akhrajahu allatheena kafaroo thaniya ithnayni ith huma fee alghari ith yaqoolu lisahibihi la tahzan inna Allaha maAAana faanzala Allahu sakeenatahu AAalayhi waayyadahu bijunoodin lam tarawha wajaAAala kalimata allatheena kafaroo alssufla wakalimatu Allahi hiya alAAulya waAllahu AAazeezun hakeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 41, "Go forth, whether light or heavy, and strive with your wealth and your lives in the cause of Allah. That is better for you, if you only knew", "ٱنفِرُواْ خِفَافٗا وَثِقَالٗا وَجَٰهِدُواْ بِأَمۡوَٰلِكُمۡ وَأَنفُسِكُمۡ فِي سَبِيلِ ٱللَّهِۚ ذَٰلِكُمۡ خَيۡرٞ لَّكُمۡ إِن كُنتُمۡ تَعۡلَمُونَ", "Infiroo khifafan wathiqalan wajahidoo biamwalikum waanfusikum fee sabeeli Allahi thalikum khayrun lakum in kuntum taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 42, "Had it been an easy gain and a moderate trip, the hypocrites would have followed you, but distant to them was the journey. And they will swear by Allah, ""If we were able, we would have gone forth with you,"" destroying themselves [through false oaths], and Allah knows that indeed they are liars", "لَوۡ كَانَ عَرَضٗا قَرِيبٗا وَسَفَرٗا قَاصِدٗا لَّٱتَّبَعُوكَ وَلَٰكِنۢ بَعُدَتۡ عَلَيۡهِمُ ٱلشُّقَّةُۚ وَسَيَحۡلِفُونَ بِٱللَّهِ لَوِ ٱسۡتَطَعۡنَا لَخَرَجۡنَا مَعَكُمۡ يُهۡلِكُونَ أَنفُسَهُمۡ وَٱللَّهُ يَعۡلَمُ إِنَّهُمۡ لَكَٰذِبُونَ", "Law kana AAaradan qareeban wasafaran qasidan laittabaAAooka walakin baAAudat AAalayhimu alshshuqqatu wasayahlifoona biAllahi lawi istataAAna lakharajna maAAakum yuhlikoona anfusahum waAllahu yaAAlamu innahum lakathiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 43, "May Allah pardon you, [O Muhammad]; why did you give them permission [to remain behind]? [You should not have] until it was evident to you who were truthful and you knew [who were] the liars", "عَفَا ٱللَّهُ عَنكَ لِمَ أَذِنتَ لَهُمۡ حَتَّىٰ يَتَبَيَّنَ لَكَ ٱلَّذِينَ صَدَقُواْ وَتَعۡلَمَ ٱلۡكَٰذِبِينَ", "AAafa Allahu AAanka lima athinta lahum hatta yatabayyana laka allatheena sadaqoo wataAAlama alkathibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 44, "Those who believe in Allah and the Last Day would not ask permission of you to be excused from striving with their wealth and their lives. And Allah is Knowing of those who fear Him", "لَا يَسۡتَـٔۡذِنُكَ ٱلَّذِينَ يُؤۡمِنُونَ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ أَن يُجَٰهِدُواْ بِأَمۡوَٰلِهِمۡ وَأَنفُسِهِمۡۗ وَٱللَّهُ عَلِيمُۢ بِٱلۡمُتَّقِينَ", "La yastathinuka allatheena yuminoona biAllahi waalyawmi alakhiri an yujahidoo biamwalihim waanfusihim waAllahu AAaleemun bialmuttaqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 45, "Only those would ask permission of you who do not believe in Allah and the Last Day and whose hearts have doubted, and they, in their doubt, are hesitating", "إِنَّمَا يَسۡتَـٔۡذِنُكَ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ وَٱرۡتَابَتۡ قُلُوبُهُمۡ فَهُمۡ فِي رَيۡبِهِمۡ يَتَرَدَّدُونَ", "Innama yastathinuka allatheena la yuminoona biAllahi waalyawmi alakhiri wairtabat quloobuhum fahum fee raybihim yataraddadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 46, "And if they had intended to go forth, they would have prepared for it [some] preparation. But Allah disliked their being sent, so He kept them back, and they were told, ""Remain [behind] with those who remain", "۞وَلَوۡ أَرَادُواْ ٱلۡخُرُوجَ لَأَعَدُّواْ لَهُۥ عُدَّةٗ وَلَٰكِن كَرِهَ ٱللَّهُ ٱنۢبِعَاثَهُمۡ فَثَبَّطَهُمۡ وَقِيلَ ٱقۡعُدُواْ مَعَ ٱلۡقَٰعِدِينَ", "Walaw aradoo alkhurooja laaAAaddoo lahu AAuddatan walakin kariha Allahu inbiAAathahum fathabbatahum waqeela oqAAudoo maAAa alqaAAideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 47, "Had they gone forth with you, they would not have increased you except in confusion, and they would have been active among you, seeking [to cause] you fitnah. And among you are avid listeners to them. And Allah is Knowing of the wrongdoers", "لَوۡ خَرَجُواْ فِيكُم مَّا زَادُوكُمۡ إِلَّا خَبَالٗا وَلَأَوۡضَعُواْ خِلَٰلَكُمۡ يَبۡغُونَكُمُ ٱلۡفِتۡنَةَ وَفِيكُمۡ سَمَّـٰعُونَ لَهُمۡۗ وَٱللَّهُ عَلِيمُۢ بِٱلظَّـٰلِمِينَ", "Law kharajoo feekum ma zadookum illa khabalan walaawdaAAoo khilalakum yabghoonakumu alfitnata wafeekum sammaAAoona lahum waAllahu AAaleemun bialththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 48, "They had already desired dissension before and had upset matters for you until the truth came and the ordinance of Allah appeared, while they were averse", "لَقَدِ ٱبۡتَغَوُاْ ٱلۡفِتۡنَةَ مِن قَبۡلُ وَقَلَّبُواْ لَكَ ٱلۡأُمُورَ حَتَّىٰ جَآءَ ٱلۡحَقُّ وَظَهَرَ أَمۡرُ ٱللَّهِ وَهُمۡ كَٰرِهُونَ", "Laqadi ibtaghawoo alfitnata min qablu waqallaboo laka alomoora hatta jaa alhaqqu wathahara amru Allahi wahum karihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 49, "And among them is he who says, ""Permit me [to remain at home] and do not put me to trial."" Unquestionably, into trial they have fallen. And indeed, Hell will encompass the disbelievers", "وَمِنۡهُم مَّن يَقُولُ ٱئۡذَن لِّي وَلَا تَفۡتِنِّيٓۚ أَلَا فِي ٱلۡفِتۡنَةِ سَقَطُواْۗ وَإِنَّ جَهَنَّمَ لَمُحِيطَةُۢ بِٱلۡكَٰفِرِينَ", "Waminhum man yaqoolu ithan lee wala taftinnee ala fee alfitnati saqatoo wainna jahannama lamuheetatun bialkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 50, "If good befalls you, it distresses them; but if disaster strikes you, they say, ""We took our matter [in hand] before,"" and turn away while they are rejoicing", "إِن تُصِبۡكَ حَسَنَةٞ تَسُؤۡهُمۡۖ وَإِن تُصِبۡكَ مُصِيبَةٞ يَقُولُواْ قَدۡ أَخَذۡنَآ أَمۡرَنَا مِن قَبۡلُ وَيَتَوَلَّواْ وَّهُمۡ فَرِحُونَ", "In tusibka hasanatun tasuhum wain tusibka museebatun yaqooloo qad akhathna amrana min qablu wayatawallaw wahum farihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 51, "Say, ""Never will we be struck except by what Allah has decreed for us; He is our protector."" And upon Allah let the believers rely", "قُل لَّن يُصِيبَنَآ إِلَّا مَا كَتَبَ ٱللَّهُ لَنَا هُوَ مَوۡلَىٰنَاۚ وَعَلَى ٱللَّهِ فَلۡيَتَوَكَّلِ ٱلۡمُؤۡمِنُونَ", "Qul lan yuseebana illa ma kataba Allahu lana huwa mawlana waAAala Allahi falyatawakkali almuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 52, "Say, ""Do you await for us except one of the two best things while we await for you that Allah will afflict you with punishment from Himself or at our hands? So wait; indeed we, along with you, are waiting", "قُلۡ هَلۡ تَرَبَّصُونَ بِنَآ إِلَّآ إِحۡدَى ٱلۡحُسۡنَيَيۡنِۖ وَنَحۡنُ نَتَرَبَّصُ بِكُمۡ أَن يُصِيبَكُمُ ٱللَّهُ بِعَذَابٖ مِّنۡ عِندِهِۦٓ أَوۡ بِأَيۡدِينَاۖ فَتَرَبَّصُوٓاْ إِنَّا مَعَكُم مُّتَرَبِّصُونَ", "Qul hal tarabbasoona bina illa ihda alhusnayayni wanahnu natarabbasu bikum an yuseebakumu Allahu biAAathabin min AAindihi aw biaydeena fatarabbasoo inna maAAakum mutarabbisoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 53, "Say, ""Spend willingly or unwillingly; never will it be accepted from you. Indeed, you have been a defiantly disobedient people", "قُلۡ أَنفِقُواْ طَوۡعًا أَوۡ كَرۡهٗا لَّن يُتَقَبَّلَ مِنكُمۡ إِنَّكُمۡ كُنتُمۡ قَوۡمٗا فَٰسِقِينَ", "Qul anfiqoo tawAAan aw karhan lan yutaqabbala minkum innakum kuntum qawman fasiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 54, "And what prevents their expenditures from being accepted from them but that they have disbelieved in Allah and in His Messenger and that they come not to prayer except while they are lazy and that they do not spend except while they are unwilling", "وَمَا مَنَعَهُمۡ أَن تُقۡبَلَ مِنۡهُمۡ نَفَقَٰتُهُمۡ إِلَّآ أَنَّهُمۡ كَفَرُواْ بِٱللَّهِ وَبِرَسُولِهِۦ وَلَا يَأۡتُونَ ٱلصَّلَوٰةَ إِلَّا وَهُمۡ كُسَالَىٰ وَلَا يُنفِقُونَ إِلَّا وَهُمۡ كَٰرِهُونَ", "Wama manaAAahum an tuqbala minhum nafaqatuhum illa annahum kafaroo biAllahi wabirasoolihi wala yatoona alssalata illa wahum kusala wala yunfiqoona illa wahum karihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 55, "So let not their wealth or their children impress you. Allah only intends to punish them through them in worldly life and that their souls should depart [at death] while they are disbelievers", "فَلَا تُعۡجِبۡكَ أَمۡوَٰلُهُمۡ وَلَآ أَوۡلَٰدُهُمۡۚ إِنَّمَا يُرِيدُ ٱللَّهُ لِيُعَذِّبَهُم بِهَا فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَتَزۡهَقَ أَنفُسُهُمۡ وَهُمۡ كَٰفِرُونَ", "Fala tuAAjibka amwaluhum wala awladuhum innama yureedu Allahu liyuAAaththibahum biha fee alhayati alddunya watazhaqa anfusuhum wahum kafiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 56, "And they swear by Allah that they are from among you while they are not from among you; but they are a people who are afraid", "وَيَحۡلِفُونَ بِٱللَّهِ إِنَّهُمۡ لَمِنكُمۡ وَمَا هُم مِّنكُمۡ وَلَٰكِنَّهُمۡ قَوۡمٞ يَفۡرَقُونَ", "Wayahlifoona biAllahi innahum laminkum wama hum minkum walakinnahum qawmun yafraqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 57, "If they could find a refuge or some caves or any place to enter [and hide], they would turn to it while they run heedlessly", "لَوۡ يَجِدُونَ مَلۡجَـًٔا أَوۡ مَغَٰرَٰتٍ أَوۡ مُدَّخَلٗا لَّوَلَّوۡاْ إِلَيۡهِ وَهُمۡ يَجۡمَحُونَ", "Law yajidoona maljaan aw magharatin aw muddakhalan lawallaw ilayhi wahum yajmahoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 58, "And among them are some who criticize you concerning the [distribution of] charities. If they are given from them, they approve; but if they are not given from them, at once they become angry", "وَمِنۡهُم مَّن يَلۡمِزُكَ فِي ٱلصَّدَقَٰتِ فَإِنۡ أُعۡطُواْ مِنۡهَا رَضُواْ وَإِن لَّمۡ يُعۡطَوۡاْ مِنۡهَآ إِذَا هُمۡ يَسۡخَطُونَ", "Waminhum man yalmizuka fee alssadaqati fain oAAtoo minha radoo wain lam yuAAtaw minha itha hum yaskhatoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 59, "If only they had been satisfied with what Allah and His Messenger gave them and said, ""Sufficient for us is Allah; Allah will give us of His bounty, and [so will] His Messenger; indeed, we are desirous toward Allah,"" [it would have been better for them]", "وَلَوۡ أَنَّهُمۡ رَضُواْ مَآ ءَاتَىٰهُمُ ٱللَّهُ وَرَسُولُهُۥ وَقَالُواْ حَسۡبُنَا ٱللَّهُ سَيُؤۡتِينَا ٱللَّهُ مِن فَضۡلِهِۦ وَرَسُولُهُۥٓ إِنَّآ إِلَى ٱللَّهِ رَٰغِبُونَ", "Walaw annahum radoo ma atahumu Allahu warasooluhu waqaloo hasbuna Allahu sayuteena Allahu min fadlihi warasooluhu inna ila Allahi raghiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 60, "Zakah expenditures are only for the poor and for the needy and for those employed to collect [zakah] and for bringing hearts together [for Islam] and for freeing captives [or slaves] and for those in debt and for the cause of Allah and for the [stranded] traveler - an obligation [imposed] by Allah. And Allah is Knowing and Wise", "۞إِنَّمَا ٱلصَّدَقَٰتُ لِلۡفُقَرَآءِ وَٱلۡمَسَٰكِينِ وَٱلۡعَٰمِلِينَ عَلَيۡهَا وَٱلۡمُؤَلَّفَةِ قُلُوبُهُمۡ وَفِي ٱلرِّقَابِ وَٱلۡغَٰرِمِينَ وَفِي سَبِيلِ ٱللَّهِ وَٱبۡنِ ٱلسَّبِيلِۖ فَرِيضَةٗ مِّنَ ٱللَّهِۗ وَٱللَّهُ عَلِيمٌ حَكِيمٞ", "Innama alssadaqatu lilfuqarai waalmasakeeni waalAAamileena AAalayha waalmuallafati quloobuhum wafee alrriqabi waalgharimeena wafee sabeeli Allahi waibni alssabeeli fareedatan mina Allahi waAllahu AAaleemun hakeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 61, "And among them are those who abuse the Prophet and say, ""He is an ear."" Say, ""[It is] an ear of goodness for you that believes in Allah and believes the believers and [is] a mercy to those who believe among you."" And those who abuse the Messenger of Allah - for them is a painful punishment", "وَمِنۡهُمُ ٱلَّذِينَ يُؤۡذُونَ ٱلنَّبِيَّ وَيَقُولُونَ هُوَ أُذُنٞۚ قُلۡ أُذُنُ خَيۡرٖ لَّكُمۡ يُؤۡمِنُ بِٱللَّهِ وَيُؤۡمِنُ لِلۡمُؤۡمِنِينَ وَرَحۡمَةٞ لِّلَّذِينَ ءَامَنُواْ مِنكُمۡۚ وَٱلَّذِينَ يُؤۡذُونَ رَسُولَ ٱللَّهِ لَهُمۡ عَذَابٌ أَلِيمٞ", "Waminhumu allatheena yuthoona alnnabiyya wayaqooloona huwa othunun qul othunu khayrin lakum yuminu biAllahi wayuminu lilmumineena warahmatun lillatheena amanoo minkum waallatheena yuthoona rasoola Allahi lahum AAathabun aleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 62, "They swear by Allah to you [Muslims] to satisfy you. But Allah and His Messenger are more worthy for them to satisfy, if they should be believers", "يَحۡلِفُونَ بِٱللَّهِ لَكُمۡ لِيُرۡضُوكُمۡ وَٱللَّهُ وَرَسُولُهُۥٓ أَحَقُّ أَن يُرۡضُوهُ إِن كَانُواْ مُؤۡمِنِينَ", "Yahlifoona biAllahi lakum liyurdookum waAllahu warasooluhu ahaqqu an yurdoohu in kanoo mumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 63, "Do they not know that whoever opposes Allah and His Messenger - that for him is the fire of Hell, wherein he will abide eternally? That is the great disgrace", "أَلَمۡ يَعۡلَمُوٓاْ أَنَّهُۥ مَن يُحَادِدِ ٱللَّهَ وَرَسُولَهُۥ فَأَنَّ لَهُۥ نَارَ جَهَنَّمَ خَٰلِدٗا فِيهَاۚ ذَٰلِكَ ٱلۡخِزۡيُ ٱلۡعَظِيمُ", "Alam yaAAlamoo annahu man yuhadidi Allaha warasoolahu faanna lahu nara jahannama khalidan feeha thalika alkhizyu alAAatheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 64, "They hypocrites are apprehensive lest a surah be revealed about them, informing them of what is in their hearts. Say, ""Mock [as you wish]; indeed, Allah will expose that which you fear", "يَحۡذَرُ ٱلۡمُنَٰفِقُونَ أَن تُنَزَّلَ عَلَيۡهِمۡ سُورَةٞ تُنَبِّئُهُم بِمَا فِي قُلُوبِهِمۡۚ قُلِ ٱسۡتَهۡزِءُوٓاْ إِنَّ ٱللَّهَ مُخۡرِجٞ مَّا تَحۡذَرُونَ", "Yahtharu almunafiqoona an tunazzala AAalayhim sooratun tunabbiohum bima fee quloobihim quli istahzioo inna Allaha mukhrijun ma tahtharoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 65, "And if you ask them, they will surely say, ""We were only conversing and playing."" Say, ""Is it Allah and His verses and His Messenger that you were mocking", "وَلَئِن سَأَلۡتَهُمۡ لَيَقُولُنَّ إِنَّمَا كُنَّا نَخُوضُ وَنَلۡعَبُۚ قُلۡ أَبِٱللَّهِ وَءَايَٰتِهِۦ وَرَسُولِهِۦ كُنتُمۡ تَسۡتَهۡزِءُونَ", "Walain saaltahum layaqoolunna innama kunna nakhoodu wanalAAabu qul abiAllahi waayatihi warasoolihi kuntum tastahzioona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 66, "Make no excuse; you have disbelieved after your belief. If We pardon one faction of you - We will punish another faction because they were criminals", "لَا تَعۡتَذِرُواْ قَدۡ كَفَرۡتُم بَعۡدَ إِيمَٰنِكُمۡۚ إِن نَّعۡفُ عَن طَآئِفَةٖ مِّنكُمۡ نُعَذِّبۡ طَآئِفَةَۢ بِأَنَّهُمۡ كَانُواْ مُجۡرِمِينَ", "La taAAtathiroo qad kafartum baAAda eemanikum in naAAfu AAan taifatin minkum nuAAaththib taifatan biannahum kanoo mujrimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 67, "The hypocrite men and hypocrite women are of one another. They enjoin what is wrong and forbid what is right and close their hands. They have forgotten Allah, so He has forgotten them [accordingly]. Indeed, the hypocrites - it is they who are the defiantly disobedient", "ٱلۡمُنَٰفِقُونَ وَٱلۡمُنَٰفِقَٰتُ بَعۡضُهُم مِّنۢ بَعۡضٖۚ يَأۡمُرُونَ بِٱلۡمُنكَرِ وَيَنۡهَوۡنَ عَنِ ٱلۡمَعۡرُوفِ وَيَقۡبِضُونَ أَيۡدِيَهُمۡۚ نَسُواْ ٱللَّهَ فَنَسِيَهُمۡۚ إِنَّ ٱلۡمُنَٰفِقِينَ هُمُ ٱلۡفَٰسِقُونَ", "Almunafiqoona waalmunafiqatu baAAduhum min baAAdin yamuroona bialmunkari wayanhawna AAani almaAAroofi wayaqbidoona aydiyahum nasoo Allaha fanasiyahum inna almunafiqeena humu alfasiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 68, "Allah has promised the hypocrite men and hypocrite women and the disbelievers the fire of Hell, wherein they will abide eternally. It is sufficient for them. And Allah has cursed them, and for them is an enduring punishment", "وَعَدَ ٱللَّهُ ٱلۡمُنَٰفِقِينَ وَٱلۡمُنَٰفِقَٰتِ وَٱلۡكُفَّارَ نَارَ جَهَنَّمَ خَٰلِدِينَ فِيهَاۚ هِيَ حَسۡبُهُمۡۚ وَلَعَنَهُمُ ٱللَّهُۖ وَلَهُمۡ عَذَابٞ مُّقِيمٞ", "WaAAada Allahu almunafiqeena waalmunafiqati waalkuffara nara jahannama khalideena feeha hiya hasbuhum walaAAanahumu Allahu walahum AAathabun muqeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 69, "[You disbelievers are] like those before you; they were stronger than you in power and more abundant in wealth and children. They enjoyed their portion [of worldly enjoyment], and you have enjoyed your portion as those before you enjoyed their portion, and you have engaged [in vanities] like that in which they engaged. [It is] those whose deeds have become worthless in this world and in the Hereafter, and it is they who are the losers", "كَٱلَّذِينَ مِن قَبۡلِكُمۡ كَانُوٓاْ أَشَدَّ مِنكُمۡ قُوَّةٗ وَأَكۡثَرَ أَمۡوَٰلٗا وَأَوۡلَٰدٗا فَٱسۡتَمۡتَعُواْ بِخَلَٰقِهِمۡ فَٱسۡتَمۡتَعۡتُم بِخَلَٰقِكُمۡ كَمَا ٱسۡتَمۡتَعَ ٱلَّذِينَ مِن قَبۡلِكُم بِخَلَٰقِهِمۡ وَخُضۡتُمۡ كَٱلَّذِي خَاضُوٓاْۚ أُوْلَـٰٓئِكَ حَبِطَتۡ أَعۡمَٰلُهُمۡ فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِۖ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡخَٰسِرُونَ", "Kaallatheena min qablikum kanoo ashadda minkum quwwatan waakthara amwalan waawladan faistamtaAAoo bikhalaqihim faistamtaAAtum bikhalaqikum kama istamtaAAa allatheena min qablikum bikhalaqihim wakhudtum kaallathee khadoo olaika habitat aAAmaluhum fee alddunya waalakhirati waolaika humu alkhasiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 70, "Has there not reached them the news of those before them - the people of Noah and [the tribes of] 'Aad and Thamud and the people of Abraham and the companions of Madyan and the towns overturned? Their messengers came to them with clear proofs. And Allah would never have wronged them, but they were wronging themselves", "أَلَمۡ يَأۡتِهِمۡ نَبَأُ ٱلَّذِينَ مِن قَبۡلِهِمۡ قَوۡمِ نُوحٖ وَعَادٖ وَثَمُودَ وَقَوۡمِ إِبۡرَٰهِيمَ وَأَصۡحَٰبِ مَدۡيَنَ وَٱلۡمُؤۡتَفِكَٰتِۚ أَتَتۡهُمۡ رُسُلُهُم بِٱلۡبَيِّنَٰتِۖ فَمَا كَانَ ٱللَّهُ لِيَظۡلِمَهُمۡ وَلَٰكِن كَانُوٓاْ أَنفُسَهُمۡ يَظۡلِمُونَ", "Alam yatihim nabao allatheena min qablihim qawmi noohin waAAadin wathamooda waqawmi ibraheema waashabi madyana waalmutafikati atathum rusuluhum bialbayyinati fama kana Allahu liyathlimahum walakin kanoo anfusahum yathlimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 71, "The believing men and believing women are allies of one another. They enjoin what is right and forbid what is wrong and establish prayer and give zakah and obey Allah and His Messenger. Those - Allah will have mercy upon them. Indeed, Allah is Exalted in Might and Wise", "وَٱلۡمُؤۡمِنُونَ وَٱلۡمُؤۡمِنَٰتُ بَعۡضُهُمۡ أَوۡلِيَآءُ بَعۡضٖۚ يَأۡمُرُونَ بِٱلۡمَعۡرُوفِ وَيَنۡهَوۡنَ عَنِ ٱلۡمُنكَرِ وَيُقِيمُونَ ٱلصَّلَوٰةَ وَيُؤۡتُونَ ٱلزَّكَوٰةَ وَيُطِيعُونَ ٱللَّهَ وَرَسُولَهُۥٓۚ أُوْلَـٰٓئِكَ سَيَرۡحَمُهُمُ ٱللَّهُۗ إِنَّ ٱللَّهَ عَزِيزٌ حَكِيمٞ", "Waalmuminoona waalmuminatu baAAduhum awliyao baAAdin yamuroona bialmaAAroofi wayanhawna AAani almunkari wayuqeemoona alssalata wayutoona alzzakata wayuteeAAoona Allaha warasoolahu olaika sayarhamuhumu Allahu inna Allaha AAazeezun hakeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 72, "Allah has promised the believing men and believing women gardens beneath which rivers flow, wherein they abide eternally, and pleasant dwellings in gardens of perpetual residence; but approval from Allah is greater. It is that which is the great attainment", "وَعَدَ ٱللَّهُ ٱلۡمُؤۡمِنِينَ وَٱلۡمُؤۡمِنَٰتِ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَا وَمَسَٰكِنَ طَيِّبَةٗ فِي جَنَّـٰتِ عَدۡنٖۚ وَرِضۡوَٰنٞ مِّنَ ٱللَّهِ أَكۡبَرُۚ ذَٰلِكَ هُوَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ", "WaAAada Allahu almumineena waalmuminati jannatin tajree min tahtiha alanharu khalideena feeha wamasakina tayyibatan fee jannati AAadnin waridwanun mina Allahi akbaru thalika huwa alfawzu alAAatheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 73, "O Prophet, fight against the disbelievers and the hypocrites and be harsh upon them. And their refuge is Hell, and wretched is the destination", "يَـٰٓأَيُّهَا ٱلنَّبِيُّ جَٰهِدِ ٱلۡكُفَّارَ وَٱلۡمُنَٰفِقِينَ وَٱغۡلُظۡ عَلَيۡهِمۡۚ وَمَأۡوَىٰهُمۡ جَهَنَّمُۖ وَبِئۡسَ ٱلۡمَصِيرُ", "Ya ayyuha alnnabiyyu jahidi alkuffara waalmunafiqeena waoghluth AAalayhim wamawahum jahannamu wabisa almaseeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 74, "They swear by Allah that they did not say [anything against the Prophet] while they had said the word of disbelief and disbelieved after their [pretense of] Islam and planned that which they were not to attain. And they were not resentful except [for the fact] that Allah and His Messenger had enriched them of His bounty. So if they repent, it is better for them; but if they turn away, Allah will punish them with a painful punishment in this world and the Hereafter. And there will not be for them on earth any protector or helper", "يَحۡلِفُونَ بِٱللَّهِ مَا قَالُواْ وَلَقَدۡ قَالُواْ كَلِمَةَ ٱلۡكُفۡرِ وَكَفَرُواْ بَعۡدَ إِسۡلَٰمِهِمۡ وَهَمُّواْ بِمَا لَمۡ يَنَالُواْۚ وَمَا نَقَمُوٓاْ إِلَّآ أَنۡ أَغۡنَىٰهُمُ ٱللَّهُ وَرَسُولُهُۥ مِن فَضۡلِهِۦۚ فَإِن يَتُوبُواْ يَكُ خَيۡرٗا لَّهُمۡۖ وَإِن يَتَوَلَّوۡاْ يُعَذِّبۡهُمُ ٱللَّهُ عَذَابًا أَلِيمٗا فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِۚ وَمَا لَهُمۡ فِي ٱلۡأَرۡضِ مِن وَلِيّٖ وَلَا نَصِيرٖ", "Yahlifoona biAllahi ma qaloo walaqad qaloo kalimata alkufri wakafaroo baAAda islamihim wahammoo bima lam yanaloo wama naqamoo illa an aghnahumu Allahu warasooluhu min fadlihi fain yatooboo yaku khayran lahum wain yatawallaw yuAAaththibhumu Allahu AAathaban aleeman fee alddunya waalakhirati wama lahum fee alardi min waliyyin wala naseerin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 75, "And among them are those who made a covenant with Allah, [saying], ""If He should give us from His bounty, we will surely spend in charity, and we will surely be among the righteous", "۞وَمِنۡهُم مَّنۡ عَٰهَدَ ٱللَّهَ لَئِنۡ ءَاتَىٰنَا مِن فَضۡلِهِۦ لَنَصَّدَّقَنَّ وَلَنَكُونَنَّ مِنَ ٱلصَّـٰلِحِينَ", "Waminhum man AAahada Allaha lain atana min fadlihi lanassaddaqanna walanakoonanna mina alssaliheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 76, "But when he gave them from His bounty, they were stingy with it and turned away while they refused", "فَلَمَّآ ءَاتَىٰهُم مِّن فَضۡلِهِۦ بَخِلُواْ بِهِۦ وَتَوَلَّواْ وَّهُم مُّعۡرِضُونَ", "Falamma atahum min fadlihi bakhiloo bihi watawallaw wahum muAAridoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 77, "So He penalized them with hypocrisy in their hearts until the Day they will meet Him - because they failed Allah in what they promised Him and because they [habitually] used to lie", "فَأَعۡقَبَهُمۡ نِفَاقٗا فِي قُلُوبِهِمۡ إِلَىٰ يَوۡمِ يَلۡقَوۡنَهُۥ بِمَآ أَخۡلَفُواْ ٱللَّهَ مَا وَعَدُوهُ وَبِمَا كَانُواْ يَكۡذِبُونَ", "FaaAAqabahum nifaqan fee quloobihim ila yawmi yalqawnahu bima akhlafoo Allaha ma waAAadoohu wabima kanoo yakthiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 78, "Did they not know that Allah knows their secrets and their private conversations and that Allah is the Knower of the unseen", "أَلَمۡ يَعۡلَمُوٓاْ أَنَّ ٱللَّهَ يَعۡلَمُ سِرَّهُمۡ وَنَجۡوَىٰهُمۡ وَأَنَّ ٱللَّهَ عَلَّـٰمُ ٱلۡغُيُوبِ", "Alam yaAAlamoo anna Allaha yaAAlamu sirrahum wanajwahum waanna Allaha AAallamu alghuyoobi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 79, "Those who criticize the contributors among the believers concerning [their] charities and [criticize] the ones who find nothing [to spend] except their effort, so they ridicule them - Allah will ridicule them, and they will have a painful punishment", "ٱلَّذِينَ يَلۡمِزُونَ ٱلۡمُطَّوِّعِينَ مِنَ ٱلۡمُؤۡمِنِينَ فِي ٱلصَّدَقَٰتِ وَٱلَّذِينَ لَا يَجِدُونَ إِلَّا جُهۡدَهُمۡ فَيَسۡخَرُونَ مِنۡهُمۡ سَخِرَ ٱللَّهُ مِنۡهُمۡ وَلَهُمۡ عَذَابٌ أَلِيمٌ", "Allatheena yalmizoona almuttawwiAAeena mina almumineena fee alssadaqati waallatheena la yajidoona illa juhdahum fayaskharoona minhum sakhira Allahu minhum walahum AAathabun aleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 80, "Ask forgiveness for them, [O Muhammad], or do not ask forgiveness for them. If you should ask forgiveness for them seventy times - never will Allah forgive them. That is because they disbelieved in Allah and His Messenger, and Allah does not guide the defiantly disobedient people", "ٱسۡتَغۡفِرۡ لَهُمۡ أَوۡ لَا تَسۡتَغۡفِرۡ لَهُمۡ إِن تَسۡتَغۡفِرۡ لَهُمۡ سَبۡعِينَ مَرَّةٗ فَلَن يَغۡفِرَ ٱللَّهُ لَهُمۡۚ ذَٰلِكَ بِأَنَّهُمۡ كَفَرُواْ بِٱللَّهِ وَرَسُولِهِۦۗ وَٱللَّهُ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلۡفَٰسِقِينَ", "Istaghfir lahum aw la tastaghfir lahum in tastaghfir lahum sabAAeena marratan falan yaghfira Allahu lahum thalika biannahum kafaroo biAllahi warasoolihi waAllahu la yahdee alqawma alfasiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 81, "Those who remained behind rejoiced in their staying [at home] after [the departure of] the Messenger of Allah and disliked to strive with their wealth and their lives in the cause of Allah and said, ""Do not go forth in the heat."" Say, ""The fire of Hell is more intensive in heat"" - if they would but understand", "فَرِحَ ٱلۡمُخَلَّفُونَ بِمَقۡعَدِهِمۡ خِلَٰفَ رَسُولِ ٱللَّهِ وَكَرِهُوٓاْ أَن يُجَٰهِدُواْ بِأَمۡوَٰلِهِمۡ وَأَنفُسِهِمۡ فِي سَبِيلِ ٱللَّهِ وَقَالُواْ لَا تَنفِرُواْ فِي ٱلۡحَرِّۗ قُلۡ نَارُ جَهَنَّمَ أَشَدُّ حَرّٗاۚ لَّوۡ كَانُواْ يَفۡقَهُونَ", "Fariha almukhallafoona bimaqAAadihim khilafa rasooli Allahi wakarihoo an yujahidoo biamwalihim waanfusihim fee sabeeli Allahi waqaloo la tanfiroo fee alharri qul naru jahannama ashaddu harran law kanoo yafqahoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 82, "So let them laugh a little and [then] weep much as recompense for what they used to earn", "فَلۡيَضۡحَكُواْ قَلِيلٗا وَلۡيَبۡكُواْ كَثِيرٗا جَزَآءَۢ بِمَا كَانُواْ يَكۡسِبُونَ", "Falyadhakoo qaleelan walyabkoo katheeran jazaan bima kanoo yaksiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 83, "If Allah should return you to a faction of them [after the expedition] and then they ask your permission to go out [to battle], say, ""You will not go out with me, ever, and you will never fight with me an enemy. Indeed, you were satisfied with sitting [at home] the first time, so sit [now] with those who stay behind", "فَإِن رَّجَعَكَ ٱللَّهُ إِلَىٰ طَآئِفَةٖ مِّنۡهُمۡ فَٱسۡتَـٔۡذَنُوكَ لِلۡخُرُوجِ فَقُل لَّن تَخۡرُجُواْ مَعِيَ أَبَدٗا وَلَن تُقَٰتِلُواْ مَعِيَ عَدُوًّاۖ إِنَّكُمۡ رَضِيتُم بِٱلۡقُعُودِ أَوَّلَ مَرَّةٖ فَٱقۡعُدُواْ مَعَ ٱلۡخَٰلِفِينَ", "Fain rajaAAaka Allahu ila taifatin minhum faistathanooka lilkhurooji faqul lan takhrujoo maAAiya abadan walan tuqatiloo maAAiya AAaduwwan innakum radeetum bialquAAoodi awwala marratin faoqAAudoo maAAa alkhalifeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 84, "And do not pray [the funeral prayer, O Muhammad], over any of them who has died - ever - or stand at his grave. Indeed, they disbelieved in Allah and His Messenger and died while they were defiantly disobedient", "وَلَا تُصَلِّ عَلَىٰٓ أَحَدٖ مِّنۡهُم مَّاتَ أَبَدٗا وَلَا تَقُمۡ عَلَىٰ قَبۡرِهِۦٓۖ إِنَّهُمۡ كَفَرُواْ بِٱللَّهِ وَرَسُولِهِۦ وَمَاتُواْ وَهُمۡ فَٰسِقُونَ", "Wala tusalli AAala ahadin minhum mata abadan wala taqum AAala qabrihi innahum kafaroo biAllahi warasoolihi wamatoo wahum fasiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 85, "And let not their wealth and their children impress you. Allah only intends to punish them through them in this world and that their souls should depart [at death] while they are disbelievers", "وَلَا تُعۡجِبۡكَ أَمۡوَٰلُهُمۡ وَأَوۡلَٰدُهُمۡۚ إِنَّمَا يُرِيدُ ٱللَّهُ أَن يُعَذِّبَهُم بِهَا فِي ٱلدُّنۡيَا وَتَزۡهَقَ أَنفُسُهُمۡ وَهُمۡ كَٰفِرُونَ", "Wala tuAAjibka amwaluhum waawladuhum innama yureedu Allahu an yuAAaththibahum biha fee alddunya watazhaqa anfusuhum wahum kafiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 86, "And when a surah was revealed [enjoining them] to believe in Allah and to fight with His Messenger, those of wealth among them asked your permission [to stay back] and said, ""Leave us to be with them who sit [at home]", "وَإِذَآ أُنزِلَتۡ سُورَةٌ أَنۡ ءَامِنُواْ بِٱللَّهِ وَجَٰهِدُواْ مَعَ رَسُولِهِ ٱسۡتَـٔۡذَنَكَ أُوْلُواْ ٱلطَّوۡلِ مِنۡهُمۡ وَقَالُواْ ذَرۡنَا نَكُن مَّعَ ٱلۡقَٰعِدِينَ", "Waitha onzilat sooratun an aminoo biAllahi wajahidoo maAAa rasoolihi istathanaka oloo alttawli minhum waqaloo tharna nakun maAAa alqaAAideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 87, "They were satisfied to be with those who stay behind, and their hearts were sealed over, so they do not understand", "رَضُواْ بِأَن يَكُونُواْ مَعَ ٱلۡخَوَالِفِ وَطُبِعَ عَلَىٰ قُلُوبِهِمۡ فَهُمۡ لَا يَفۡقَهُونَ", "Radoo bian yakoonoo maAAa alkhawalifi watubiAAa AAala quloobihim fahum la yafqahoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 88, "But the Messenger and those who believed with him fought with their wealth and their lives. Those will have [all that is] good, and it is those who are the successful", "لَٰكِنِ ٱلرَّسُولُ وَٱلَّذِينَ ءَامَنُواْ مَعَهُۥ جَٰهَدُواْ بِأَمۡوَٰلِهِمۡ وَأَنفُسِهِمۡۚ وَأُوْلَـٰٓئِكَ لَهُمُ ٱلۡخَيۡرَٰتُۖ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُفۡلِحُونَ", "Lakini alrrasoolu waallatheena amanoo maAAahu jahadoo biamwalihim waanfusihim waolaika lahumu alkhayratu waolaika humu almuflihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 89, "Allah has prepared for them gardens beneath which rivers flow, wherein they will abide eternally. That is the great attainment", "أَعَدَّ ٱللَّهُ لَهُمۡ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَاۚ ذَٰلِكَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ", "aAAadda Allahu lahum jannatin tajree min tahtiha alanharu khalideena feeha thalika alfawzu alAAatheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 90, "And those with excuses among the bedouins came to be permitted [to remain], and they who had lied to Allah and His Messenger sat [at home]. There will strike those who disbelieved among them a painful punishment", "وَجَآءَ ٱلۡمُعَذِّرُونَ مِنَ ٱلۡأَعۡرَابِ لِيُؤۡذَنَ لَهُمۡ وَقَعَدَ ٱلَّذِينَ كَذَبُواْ ٱللَّهَ وَرَسُولَهُۥۚ سَيُصِيبُ ٱلَّذِينَ كَفَرُواْ مِنۡهُمۡ عَذَابٌ أَلِيمٞ", "Wajaa almuAAaththiroona mina alaAArabi liyuthana lahum waqaAAada allatheena kathaboo Allaha warasoolahu sayuseebu allatheena kafaroo minhum AAathabun aleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 91, "There is not upon the weak or upon the ill or upon those who do not find anything to spend any discomfort when they are sincere to Allah and His Messenger. There is not upon the doers of good any cause [for blame]. And Allah is Forgiving and Merciful", "لَّيۡسَ عَلَى ٱلضُّعَفَآءِ وَلَا عَلَى ٱلۡمَرۡضَىٰ وَلَا عَلَى ٱلَّذِينَ لَا يَجِدُونَ مَا يُنفِقُونَ حَرَجٌ إِذَا نَصَحُواْ لِلَّهِ وَرَسُولِهِۦۚ مَا عَلَى ٱلۡمُحۡسِنِينَ مِن سَبِيلٖۚ وَٱللَّهُ غَفُورٞ رَّحِيمٞ", "Laysa AAala aldduAAafai wala AAala almarda wala AAala allatheena la yajidoona ma yunfiqoona harajun itha nasahoo lillahi warasoolihi ma AAala almuhsineena min sabeelin waAllahu ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 92, "Nor [is there blame] upon those who, when they came to you that you might give them mounts, you said, ""I can find nothing for you to ride upon."" They turned back while their eyes overflowed with tears out of grief that they could not find something to spend [for the cause of Allah]", "وَلَا عَلَى ٱلَّذِينَ إِذَا مَآ أَتَوۡكَ لِتَحۡمِلَهُمۡ قُلۡتَ لَآ أَجِدُ مَآ أَحۡمِلُكُمۡ عَلَيۡهِ تَوَلَّواْ وَّأَعۡيُنُهُمۡ تَفِيضُ مِنَ ٱلدَّمۡعِ حَزَنًا أَلَّا يَجِدُواْ مَا يُنفِقُونَ", "Wala AAala allatheena itha ma atawka litahmilahum qulta la ajidu ma ahmilukum AAalayhi tawallaw waaAAyunuhum tafeedu mina alddamAAi hazanan alla yajidoo ma yunfiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 93, "The cause [for blame] is only upon those who ask permission of you while they are rich. They are satisfied to be with those who stay behind, and Allah has sealed over their hearts, so they do not know", "۞إِنَّمَا ٱلسَّبِيلُ عَلَى ٱلَّذِينَ يَسۡتَـٔۡذِنُونَكَ وَهُمۡ أَغۡنِيَآءُۚ رَضُواْ بِأَن يَكُونُواْ مَعَ ٱلۡخَوَالِفِ وَطَبَعَ ٱللَّهُ عَلَىٰ قُلُوبِهِمۡ فَهُمۡ لَا يَعۡلَمُونَ", "Innama alssabeelu AAala allatheena yastathinoonaka wahum aghniyao radoo bian yakoonoo maAAa alkhawalifi watabaAAa Allahu AAala quloobihim fahum la yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 94, "They will make excuses to you when you have returned to them. Say, ""Make no excuse - never will we believe you. Allah has already informed us of your news. And Allah will observe your deeds, and [so will] His Messenger; then you will be taken back to the Knower of the unseen and the witnessed, and He will inform you of what you used to do", "يَعۡتَذِرُونَ إِلَيۡكُمۡ إِذَا رَجَعۡتُمۡ إِلَيۡهِمۡۚ قُل لَّا تَعۡتَذِرُواْ لَن نُّؤۡمِنَ لَكُمۡ قَدۡ نَبَّأَنَا ٱللَّهُ مِنۡ أَخۡبَارِكُمۡۚ وَسَيَرَى ٱللَّهُ عَمَلَكُمۡ وَرَسُولُهُۥ ثُمَّ تُرَدُّونَ إِلَىٰ عَٰلِمِ ٱلۡغَيۡبِ وَٱلشَّهَٰدَةِ فَيُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ", "YaAAtathiroona ilaykum itha rajaAAtum ilayhim qul la taAAtathiroo lan numina lakum qad nabbaana Allahu min akhbarikum wasayara Allahu AAamalakum warasooluhu thumma turaddoona ila AAalimi alghaybi waalshshahadati fayunabbiokum bima kuntum taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 95, "They will swear by Allah to you when you return to them that you would leave them alone. So leave them alone; indeed they are evil; and their refuge is Hell as recompense for what they had been earning", "سَيَحۡلِفُونَ بِٱللَّهِ لَكُمۡ إِذَا ٱنقَلَبۡتُمۡ إِلَيۡهِمۡ لِتُعۡرِضُواْ عَنۡهُمۡۖ فَأَعۡرِضُواْ عَنۡهُمۡۖ إِنَّهُمۡ رِجۡسٞۖ وَمَأۡوَىٰهُمۡ جَهَنَّمُ جَزَآءَۢ بِمَا كَانُواْ يَكۡسِبُونَ", "Sayahlifoona biAllahi lakum itha inqalabtum ilayhim lituAAridoo AAanhum faaAAridoo AAanhum innahum rijsun wamawahum jahannamu jazaan bima kanoo yaksiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 96, "They swear to you so that you might be satisfied with them. But if you should be satisfied with them - indeed, Allah is not satisfied with a defiantly disobedient people", "يَحۡلِفُونَ لَكُمۡ لِتَرۡضَوۡاْ عَنۡهُمۡۖ فَإِن تَرۡضَوۡاْ عَنۡهُمۡ فَإِنَّ ٱللَّهَ لَا يَرۡضَىٰ عَنِ ٱلۡقَوۡمِ ٱلۡفَٰسِقِينَ", "Yahlifoona lakum litardaw AAanhum fain tardaw AAanhum fainna Allaha la yarda AAani alqawmi alfasiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 97, "The bedouins are stronger in disbelief and hypocrisy and more likely not to know the limits of what [laws] Allah has revealed to His Messenger. And Allah is Knowing and Wise", "ٱلۡأَعۡرَابُ أَشَدُّ كُفۡرٗا وَنِفَاقٗا وَأَجۡدَرُ أَلَّا يَعۡلَمُواْ حُدُودَ مَآ أَنزَلَ ٱللَّهُ عَلَىٰ رَسُولِهِۦۗ وَٱللَّهُ عَلِيمٌ حَكِيمٞ", "AlaAArabu ashaddu kufran wanifaqan waajdaru alla yaAAlamoo hudooda ma anzala Allahu AAala rasoolihi waAllahu AAaleemun hakeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 98, "And among the bedouins are some who consider what they spend as a loss and await for you turns of misfortune. Upon them will be a misfortune of evil. And Allah is Hearing and Knowing", "وَمِنَ ٱلۡأَعۡرَابِ مَن يَتَّخِذُ مَا يُنفِقُ مَغۡرَمٗا وَيَتَرَبَّصُ بِكُمُ ٱلدَّوَآئِرَۚ عَلَيۡهِمۡ دَآئِرَةُ ٱلسَّوۡءِۗ وَٱللَّهُ سَمِيعٌ عَلِيمٞ", "Wamina alaAArabi man yattakhithu ma yunfiqu maghraman wayatarabbasu bikumu alddawaira AAalayhim dairatu alssawi waAllahu sameeAAun AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 99, "But among the bedouins are some who believe in Allah and the Last Day and consider what they spend as means of nearness to Allah and of [obtaining] invocations of the Messenger. Unquestionably, it is a means of nearness for them. Allah will admit them to His mercy. Indeed, Allah is Forgiving and Merciful", "وَمِنَ ٱلۡأَعۡرَابِ مَن يُؤۡمِنُ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ وَيَتَّخِذُ مَا يُنفِقُ قُرُبَٰتٍ عِندَ ٱللَّهِ وَصَلَوَٰتِ ٱلرَّسُولِۚ أَلَآ إِنَّهَا قُرۡبَةٞ لَّهُمۡۚ سَيُدۡخِلُهُمُ ٱللَّهُ فِي رَحۡمَتِهِۦٓۚ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ", "Wamina alaAArabi man yuminu biAllahi waalyawmi alakhiri wayattakhithu ma yunfiqu qurubatin AAinda Allahi wasalawati alrrasooli ala innaha qurbatun lahum sayudkhiluhumu Allahu fee rahmatihi inna Allaha ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 100, "And the first forerunners [in the faith] among the Muhajireen and the Ansar and those who followed them with good conduct - Allah is pleased with them and they are pleased with Him, and He has prepared for them gardens beneath which rivers flow, wherein they will abide forever. That is the great attainment", "وَٱلسَّـٰبِقُونَ ٱلۡأَوَّلُونَ مِنَ ٱلۡمُهَٰجِرِينَ وَٱلۡأَنصَارِ وَٱلَّذِينَ ٱتَّبَعُوهُم بِإِحۡسَٰنٖ رَّضِيَ ٱللَّهُ عَنۡهُمۡ وَرَضُواْ عَنۡهُ وَأَعَدَّ لَهُمۡ جَنَّـٰتٖ تَجۡرِي تَحۡتَهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَآ أَبَدٗاۚ ذَٰلِكَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ", "Waalssabiqoona alawwaloona mina almuhajireena waalansari waallatheena ittabaAAoohum biihsanin radiya Allahu AAanhum waradoo AAanhu waaAAadda lahum jannatin tajree tahtaha alanharu khalideena feeha abadan thalika alfawzu alAAatheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 101, "And among those around you of the bedouins are hypocrites, and [also] from the people of Madinah. They have become accustomed to hypocrisy. You, [O Muhammad], do not know them, [but] We know them. We will punish them twice [in this world]; then they will be returned to a great punishment", "وَمِمَّنۡ حَوۡلَكُم مِّنَ ٱلۡأَعۡرَابِ مُنَٰفِقُونَۖ وَمِنۡ أَهۡلِ ٱلۡمَدِينَةِ مَرَدُواْ عَلَى ٱلنِّفَاقِ لَا تَعۡلَمُهُمۡۖ نَحۡنُ نَعۡلَمُهُمۡۚ سَنُعَذِّبُهُم مَّرَّتَيۡنِ ثُمَّ يُرَدُّونَ إِلَىٰ عَذَابٍ عَظِيمٖ", "Wamimman hawlakum mina alaAArabi munafiqoona wamin ahli almadeenati maradoo AAala alnnifaqi la taAAlamuhum nahnu naAAlamuhum sanuAAaththibuhum marratayni thumma yuraddoona ila AAathabin AAatheemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 102, "And [there are] others who have acknowledged their sins. They had mixed a righteous deed with another that was bad. Perhaps Allah will turn to them in forgiveness. Indeed, Allah is Forgiving and Merciful", "وَءَاخَرُونَ ٱعۡتَرَفُواْ بِذُنُوبِهِمۡ خَلَطُواْ عَمَلٗا صَٰلِحٗا وَءَاخَرَ سَيِّئًا عَسَى ٱللَّهُ أَن يَتُوبَ عَلَيۡهِمۡۚ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٌ", "Waakharoona iAAtarafoo bithunoobihim khalatoo AAamalan salihan waakhara sayyian AAasa Allahu an yatooba AAalayhim inna Allaha ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 103, "Take, [O, Muhammad], from their wealth a charity by which you purify them and cause them increase, and invoke [Allah 's blessings] upon them. Indeed, your invocations are reassurance for them. And Allah is Hearing and Knowing", "خُذۡ مِنۡ أَمۡوَٰلِهِمۡ صَدَقَةٗ تُطَهِّرُهُمۡ وَتُزَكِّيهِم بِهَا وَصَلِّ عَلَيۡهِمۡۖ إِنَّ صَلَوٰتَكَ سَكَنٞ لَّهُمۡۗ وَٱللَّهُ سَمِيعٌ عَلِيمٌ", "Khuth min amwalihim sadaqatan tutahhiruhum watuzakkeehim biha wasalli AAalayhim inna salataka sakanun lahum waAllahu sameeAAun AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 104, "Do they not know that it is Allah who accepts repentance from His servants and receives charities and that it is Allah who is the Accepting of repentance, the Merciful", "أَلَمۡ يَعۡلَمُوٓاْ أَنَّ ٱللَّهَ هُوَ يَقۡبَلُ ٱلتَّوۡبَةَ عَنۡ عِبَادِهِۦ وَيَأۡخُذُ ٱلصَّدَقَٰتِ وَأَنَّ ٱللَّهَ هُوَ ٱلتَّوَّابُ ٱلرَّحِيمُ", "Alam yaAAlamoo anna Allaha huwa yaqbalu alttawbata AAan AAibadihi wayakhuthu alssadaqati waanna Allaha huwa alttawwabu alrraheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 105, "And say, ""Do [as you will], for Allah will see your deeds, and [so, will] His Messenger and the believers. And you will be returned to the Knower of the unseen and the witnessed, and He will inform you of what you used to do", "وَقُلِ ٱعۡمَلُواْ فَسَيَرَى ٱللَّهُ عَمَلَكُمۡ وَرَسُولُهُۥ وَٱلۡمُؤۡمِنُونَۖ وَسَتُرَدُّونَ إِلَىٰ عَٰلِمِ ٱلۡغَيۡبِ وَٱلشَّهَٰدَةِ فَيُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ", "Waquli iAAmaloo fasayara Allahu AAamalakum warasooluhu waalmuminoona wasaturaddoona ila AAalimi alghaybi waalshshahadati fayunabbiokum bima kuntum taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 106, "And [there are] others deferred until the command of Allah - whether He will punish them or whether He will forgive them. And Allah is Knowing and Wise", "وَءَاخَرُونَ مُرۡجَوۡنَ لِأَمۡرِ ٱللَّهِ إِمَّا يُعَذِّبُهُمۡ وَإِمَّا يَتُوبُ عَلَيۡهِمۡۗ وَٱللَّهُ عَلِيمٌ حَكِيمٞ", "Waakharoona murjawna liamri Allahi imma yuAAaththibuhum waimma yatoobu AAalayhim waAllahu AAaleemun hakeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 107, "And [there are] those [hypocrites] who took for themselves a mosque for causing harm and disbelief and division among the believers and as a station for whoever had warred against Allah and His Messenger before. And they will surely swear, ""We intended only the best."" And Allah testifies that indeed they are liars", "وَٱلَّذِينَ ٱتَّخَذُواْ مَسۡجِدٗا ضِرَارٗا وَكُفۡرٗا وَتَفۡرِيقَۢا بَيۡنَ ٱلۡمُؤۡمِنِينَ وَإِرۡصَادٗا لِّمَنۡ حَارَبَ ٱللَّهَ وَرَسُولَهُۥ مِن قَبۡلُۚ وَلَيَحۡلِفُنَّ إِنۡ أَرَدۡنَآ إِلَّا ٱلۡحُسۡنَىٰۖ وَٱللَّهُ يَشۡهَدُ إِنَّهُمۡ لَكَٰذِبُونَ", "Waallatheena ittakhathoo masjidan diraran wakufran watafreeqan bayna almumineena wairsadan liman haraba Allaha warasoolahu min qablu walayahlifunna in aradna illa alhusna waAllahu yashhadu innahum lakathiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 108, "Do not stand [for prayer] within it - ever. A mosque founded on righteousness from the first day is more worthy for you to stand in. Within it are men who love to purify themselves; and Allah loves those who purify themselves", "لَا تَقُمۡ فِيهِ أَبَدٗاۚ لَّمَسۡجِدٌ أُسِّسَ عَلَى ٱلتَّقۡوَىٰ مِنۡ أَوَّلِ يَوۡمٍ أَحَقُّ أَن تَقُومَ فِيهِۚ فِيهِ رِجَالٞ يُحِبُّونَ أَن يَتَطَهَّرُواْۚ وَٱللَّهُ يُحِبُّ ٱلۡمُطَّهِّرِينَ", "La taqum feehi abadan lamasjidun ossisa AAala alttaqwa min awwali yawmin ahaqqu an taqooma feehi feehi rijalun yuhibboona an yatatahharoo waAllahu yuhibbu almuttahhireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 109, "Then is one who laid the foundation of his building on righteousness [with fear] from Allah and [seeking] His approval better or one who laid the foundation of his building on the edge of a bank about to collapse, so it collapsed with him into the fire of Hell? And Allah does not guide the wrongdoing people", "أَفَمَنۡ أَسَّسَ بُنۡيَٰنَهُۥ عَلَىٰ تَقۡوَىٰ مِنَ ٱللَّهِ وَرِضۡوَٰنٍ خَيۡرٌ أَم مَّنۡ أَسَّسَ بُنۡيَٰنَهُۥ عَلَىٰ شَفَا جُرُفٍ هَارٖ فَٱنۡهَارَ بِهِۦ فِي نَارِ جَهَنَّمَۗ وَٱللَّهُ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلظَّـٰلِمِينَ", "Afaman assasa bunyanahu AAala taqwa mina Allahi waridwanin khayrun am man assasa bunyanahu AAala shafa jurufin harin fainhara bihi fee nari jahannama waAllahu la yahdee alqawma alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 110, "Their building which they built will not cease to be a [cause of] skepticism in their hearts until their hearts are stopped. And Allah is Knowing and Wise", "لَا يَزَالُ بُنۡيَٰنُهُمُ ٱلَّذِي بَنَوۡاْ رِيبَةٗ فِي قُلُوبِهِمۡ إِلَّآ أَن تَقَطَّعَ قُلُوبُهُمۡۗ وَٱللَّهُ عَلِيمٌ حَكِيمٌ", "La yazalu bunyanuhumu allathee banaw reebatan fee quloobihim illa an taqattaAAa quloobuhum waAllahu AAaleemun hakeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 111, "Indeed, Allah has purchased from the believers their lives and their properties [in exchange] for that they will have Paradise. They fight in the cause of Allah, so they kill and are killed. [It is] a true promise [binding] upon Him in the Torah and the Gospel and the Qur'an. And who is truer to his covenant than Allah? So rejoice in your transaction which you have contracted. And it is that which is the great attainment", "۞إِنَّ ٱللَّهَ ٱشۡتَرَىٰ مِنَ ٱلۡمُؤۡمِنِينَ أَنفُسَهُمۡ وَأَمۡوَٰلَهُم بِأَنَّ لَهُمُ ٱلۡجَنَّةَۚ يُقَٰتِلُونَ فِي سَبِيلِ ٱللَّهِ فَيَقۡتُلُونَ وَيُقۡتَلُونَۖ وَعۡدًا عَلَيۡهِ حَقّٗا فِي ٱلتَّوۡرَىٰةِ وَٱلۡإِنجِيلِ وَٱلۡقُرۡءَانِۚ وَمَنۡ أَوۡفَىٰ بِعَهۡدِهِۦ مِنَ ٱللَّهِۚ فَٱسۡتَبۡشِرُواْ بِبَيۡعِكُمُ ٱلَّذِي بَايَعۡتُم بِهِۦۚ وَذَٰلِكَ هُوَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ", "Inna Allaha ishtara mina almumineena anfusahum waamwalahum bianna lahumu aljannata yuqatiloona fee sabeeli Allahi fayaqtuloona wayuqtaloona waAAdan AAalayhi haqqan fee alttawrati waalinjeeli waalqurani waman awfa biAAahdihi mina Allahi faistabshiroo bibayAAikumu allathee bayaAAtum bihi wathalika huwa alfawzu alAAatheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 112, "[Such believers are] the repentant, the worshippers, the praisers [of Allah], the travelers [for His cause], those who bow and prostrate [in prayer], those who enjoin what is right and forbid what is wrong, and those who observe the limits [set by] Allah. And give good tidings to the believers", "ٱلتَّـٰٓئِبُونَ ٱلۡعَٰبِدُونَ ٱلۡحَٰمِدُونَ ٱلسَّـٰٓئِحُونَ ٱلرَّـٰكِعُونَ ٱلسَّـٰجِدُونَ ٱلۡأٓمِرُونَ بِٱلۡمَعۡرُوفِ وَٱلنَّاهُونَ عَنِ ٱلۡمُنكَرِ وَٱلۡحَٰفِظُونَ لِحُدُودِ ٱللَّهِۗ وَبَشِّرِ ٱلۡمُؤۡمِنِينَ", "Alttaiboona alAAabidoona alhamidoona alssaihoona alrrakiAAoona alssajidoona alamiroona bialmaAAroofi waalnnahoona AAani almunkari waalhafithoona lihudoodi Allahi wabashshiri almumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 113, "It is not for the Prophet and those who have believed to ask forgiveness for the polytheists, even if they were relatives, after it has become clear to them that they are companions of Hellfire", "مَا كَانَ لِلنَّبِيِّ وَٱلَّذِينَ ءَامَنُوٓاْ أَن يَسۡتَغۡفِرُواْ لِلۡمُشۡرِكِينَ وَلَوۡ كَانُوٓاْ أُوْلِي قُرۡبَىٰ مِنۢ بَعۡدِ مَا تَبَيَّنَ لَهُمۡ أَنَّهُمۡ أَصۡحَٰبُ ٱلۡجَحِيمِ", "Ma kana lilnnabiyyi waallatheena amanoo an yastaghfiroo lilmushrikeena walaw kanoo olee qurba min baAAdi ma tabayyana lahum annahum ashabu aljaheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 114, "And the request of forgiveness of Abraham for his father was only because of a promise he had made to him. But when it became apparent to Abraham that his father was an enemy to Allah, he disassociated himself from him. Indeed was Abraham compassionate and patient", "وَمَا كَانَ ٱسۡتِغۡفَارُ إِبۡرَٰهِيمَ لِأَبِيهِ إِلَّا عَن مَّوۡعِدَةٖ وَعَدَهَآ إِيَّاهُ فَلَمَّا تَبَيَّنَ لَهُۥٓ أَنَّهُۥ عَدُوّٞ لِّلَّهِ تَبَرَّأَ مِنۡهُۚ إِنَّ إِبۡرَٰهِيمَ لَأَوَّـٰهٌ حَلِيمٞ", "Wama kana istighfaru ibraheema liabeehi illa AAan mawAAidatin waAAadaha iyyahu falamma tabayyana lahu annahu AAaduwwun lillahi tabarraa minhu inna ibraheema laawwahun haleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 115, "And Allah would not let a people stray after He has guided them until He makes clear to them what they should avoid. Indeed, Allah is Knowing of all things", "وَمَا كَانَ ٱللَّهُ لِيُضِلَّ قَوۡمَۢا بَعۡدَ إِذۡ هَدَىٰهُمۡ حَتَّىٰ يُبَيِّنَ لَهُم مَّا يَتَّقُونَۚ إِنَّ ٱللَّهَ بِكُلِّ شَيۡءٍ عَلِيمٌ", "Wama kana Allahu liyudilla qawman baAAda ith hadahum hatta yubayyina lahum ma yattaqoona inna Allaha bikulli shayin AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 116, "Indeed, to Allah belongs the dominion of the heavens and the earth; He gives life and causes death. And you have not besides Allah any protector or any helper", "إِنَّ ٱللَّهَ لَهُۥ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ يُحۡيِۦ وَيُمِيتُۚ وَمَا لَكُم مِّن دُونِ ٱللَّهِ مِن وَلِيّٖ وَلَا نَصِيرٖ", "Inna Allaha lahu mulku alssamawati waalardi yuhyee wayumeetu wama lakum min dooni Allahi min waliyyin wala naseerin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 117, "Allah has already forgiven the Prophet and the Muhajireen and the Ansar who followed him in the hour of difficulty after the hearts of a party of them had almost inclined [to doubt], and then He forgave them. Indeed, He was to them Kind and Merciful", "لَّقَد تَّابَ ٱللَّهُ عَلَى ٱلنَّبِيِّ وَٱلۡمُهَٰجِرِينَ وَٱلۡأَنصَارِ ٱلَّذِينَ ٱتَّبَعُوهُ فِي سَاعَةِ ٱلۡعُسۡرَةِ مِنۢ بَعۡدِ مَا كَادَ يَزِيغُ قُلُوبُ فَرِيقٖ مِّنۡهُمۡ ثُمَّ تَابَ عَلَيۡهِمۡۚ إِنَّهُۥ بِهِمۡ رَءُوفٞ رَّحِيمٞ", "Laqad taba Allahu AAala alnnabiyyi waalmuhajireena waalansari allatheena ittabaAAoohu fee saAAati alAAusrati min baAAdi ma kada yazeeghu quloobu fareeqin minhum thumma taba AAalayhim innahu bihim raoofun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 118, "And [He also forgave] the three who were left behind [and regretted their error] to the point that the earth closed in on them in spite of its vastness and their souls confined them and they were certain that there is no refuge from Allah except in Him. Then He turned to them so they could repent. Indeed, Allah is the Accepting of repentance, the Merciful", "وَعَلَى ٱلثَّلَٰثَةِ ٱلَّذِينَ خُلِّفُواْ حَتَّىٰٓ إِذَا ضَاقَتۡ عَلَيۡهِمُ ٱلۡأَرۡضُ بِمَا رَحُبَتۡ وَضَاقَتۡ عَلَيۡهِمۡ أَنفُسُهُمۡ وَظَنُّوٓاْ أَن لَّا مَلۡجَأَ مِنَ ٱللَّهِ إِلَّآ إِلَيۡهِ ثُمَّ تَابَ عَلَيۡهِمۡ لِيَتُوبُوٓاْۚ إِنَّ ٱللَّهَ هُوَ ٱلتَّوَّابُ ٱلرَّحِيمُ", "WaAAala alththalathati allatheena khullifoo hatta itha daqat AAalayhimu alardu bima rahubat wadaqat AAalayhim anfusuhum wathannoo an la maljaa mina Allahi illa ilayhi thumma taba AAalayhim liyatooboo inna Allaha huwa alttawwabu alrraheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 119, "O you who have believed, fear Allah and be with those who are true", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱتَّقُواْ ٱللَّهَ وَكُونُواْ مَعَ ٱلصَّـٰدِقِينَ", "Ya ayyuha allatheena amanoo ittaqoo Allaha wakoonoo maAAa alssadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 120, "It was not [proper] for the people of Madinah and those surrounding them of the bedouins that they remain behind after [the departure of] the Messenger of Allah or that they prefer themselves over his self. That is because they are not afflicted by thirst or fatigue or hunger in the cause of Allah, nor do they tread on any ground that enrages the disbelievers, nor do they inflict upon an enemy any infliction but that is registered for them as a righteous deed. Indeed, Allah does not allow to be lost the reward of the doers of good", "مَا كَانَ لِأَهۡلِ ٱلۡمَدِينَةِ وَمَنۡ حَوۡلَهُم مِّنَ ٱلۡأَعۡرَابِ أَن يَتَخَلَّفُواْ عَن رَّسُولِ ٱللَّهِ وَلَا يَرۡغَبُواْ بِأَنفُسِهِمۡ عَن نَّفۡسِهِۦۚ ذَٰلِكَ بِأَنَّهُمۡ لَا يُصِيبُهُمۡ ظَمَأٞ وَلَا نَصَبٞ وَلَا مَخۡمَصَةٞ فِي سَبِيلِ ٱللَّهِ وَلَا يَطَـُٔونَ مَوۡطِئٗا يَغِيظُ ٱلۡكُفَّارَ وَلَا يَنَالُونَ مِنۡ عَدُوّٖ نَّيۡلًا إِلَّا كُتِبَ لَهُم بِهِۦ عَمَلٞ صَٰلِحٌۚ إِنَّ ٱللَّهَ لَا يُضِيعُ أَجۡرَ ٱلۡمُحۡسِنِينَ", "Ma kana liahli almadeenati waman hawlahum mina alaAArabi an yatakhallafoo AAan rasooli Allahi wala yarghaboo bianfusihim AAan nafsihi thalika biannahum la yuseebuhum thamaon wala nasabun wala makhmasatun fee sabeeli Allahi wala yataoona mawtian yagheethu alkuffara wala yanaloona min AAaduwwin naylan illa kutiba lahum bihi AAamalun salihun inna Allaha la yudeeAAu ajra almuhsineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 121, "Nor do they spend an expenditure, small or large, or cross a valley but that it is registered for them that Allah may reward them for the best of what they were doing", "وَلَا يُنفِقُونَ نَفَقَةٗ صَغِيرَةٗ وَلَا كَبِيرَةٗ وَلَا يَقۡطَعُونَ وَادِيًا إِلَّا كُتِبَ لَهُمۡ لِيَجۡزِيَهُمُ ٱللَّهُ أَحۡسَنَ مَا كَانُواْ يَعۡمَلُونَ", "Wala yunfiqoona nafaqatan sagheeratan wala kabeeratan wala yaqtaAAoona wadiyan illa kutiba lahum liyajziyahumu Allahu ahsana ma kanoo yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 122, "And it is not for the believers to go forth [to battle] all at once. For there should separate from every division of them a group [remaining] to obtain understanding in the religion and warn their people when they return to them that they might be cautious", "۞وَمَا كَانَ ٱلۡمُؤۡمِنُونَ لِيَنفِرُواْ كَآفَّةٗۚ فَلَوۡلَا نَفَرَ مِن كُلِّ فِرۡقَةٖ مِّنۡهُمۡ طَآئِفَةٞ لِّيَتَفَقَّهُواْ فِي ٱلدِّينِ وَلِيُنذِرُواْ قَوۡمَهُمۡ إِذَا رَجَعُوٓاْ إِلَيۡهِمۡ لَعَلَّهُمۡ يَحۡذَرُونَ", "Wama kana almuminoona liyanfiroo kaffatan falawla nafara min kulli firqatin minhum taifatun liyatafaqqahoo fee alddeeni waliyunthiroo qawmahum itha rajaAAoo ilayhim laAAallahum yahtharoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 123, "O you who have believed, fight those adjacent to you of the disbelievers and let them find in you harshness. And know that Allah is with the righteous", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ قَٰتِلُواْ ٱلَّذِينَ يَلُونَكُم مِّنَ ٱلۡكُفَّارِ وَلۡيَجِدُواْ فِيكُمۡ غِلۡظَةٗۚ وَٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ مَعَ ٱلۡمُتَّقِينَ", "Ya ayyuha allatheena amanoo qatiloo allatheena yaloonakum mina alkuffari walyajidoo feekum ghilthatan waiAAlamoo anna Allaha maAAa almuttaqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 124, "And whenever a surah is revealed, there are among the hypocrites those who say, ""Which of you has this increased faith?"" As for those who believed, it has increased them in faith, while they are rejoicing", "وَإِذَا مَآ أُنزِلَتۡ سُورَةٞ فَمِنۡهُم مَّن يَقُولُ أَيُّكُمۡ زَادَتۡهُ هَٰذِهِۦٓ إِيمَٰنٗاۚ فَأَمَّا ٱلَّذِينَ ءَامَنُواْ فَزَادَتۡهُمۡ إِيمَٰنٗا وَهُمۡ يَسۡتَبۡشِرُونَ", "Waitha ma onzilat sooratun faminhum man yaqoolu ayyukum zadathu hathihi eemanan faamma allatheena amanoo fazadathum eemanan wahum yastabshiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 125, "But as for those in whose hearts is disease, it has [only] increased them in evil [in addition] to their evil. And they will have died while they are disbelievers", "وَأَمَّا ٱلَّذِينَ فِي قُلُوبِهِم مَّرَضٞ فَزَادَتۡهُمۡ رِجۡسًا إِلَىٰ رِجۡسِهِمۡ وَمَاتُواْ وَهُمۡ كَٰفِرُونَ", "Waamma allatheena fee quloobihim maradun fazadathum rijsan ila rijsihim wamatoo wahum kafiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 126, "Do they not see that they are tried every year once or twice but then they do not repent nor do they remember", "أَوَلَا يَرَوۡنَ أَنَّهُمۡ يُفۡتَنُونَ فِي كُلِّ عَامٖ مَّرَّةً أَوۡ مَرَّتَيۡنِ ثُمَّ لَا يَتُوبُونَ وَلَا هُمۡ يَذَّكَّرُونَ", "Awala yarawna annahum yuftanoona fee kulli AAamin marratan aw marratayni thumma la yatooboona wala hum yaththakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 127, "And whenever a surah is revealed, they look at each other, [saying], ""Does anyone see you?"" and then they dismiss themselves. Allah has dismissed their hearts because they are a people who do not understand", "وَإِذَا مَآ أُنزِلَتۡ سُورَةٞ نَّظَرَ بَعۡضُهُمۡ إِلَىٰ بَعۡضٍ هَلۡ يَرَىٰكُم مِّنۡ أَحَدٖ ثُمَّ ٱنصَرَفُواْۚ صَرَفَ ٱللَّهُ قُلُوبَهُم بِأَنَّهُمۡ قَوۡمٞ لَّا يَفۡقَهُونَ", "Waitha ma onzilat sooratun nathara baAAduhum ila baAAdin hal yarakum min ahadin thumma insarafoo sarafa Allahu quloobahum biannahum qawmun la yafqahoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 128, "There has certainly come to you a Messenger from among yourselves. Grievous to him is what you suffer; [he is] concerned over you and to the believers is kind and merciful", "لَقَدۡ جَآءَكُمۡ رَسُولٞ مِّنۡ أَنفُسِكُمۡ عَزِيزٌ عَلَيۡهِ مَا عَنِتُّمۡ حَرِيصٌ عَلَيۡكُم بِٱلۡمُؤۡمِنِينَ رَءُوفٞ رَّحِيمٞ", "Laqad jaakum rasoolun min anfusikum AAazeezun AAalayhi ma AAanittum hareesun AAalaykum bialmumineena raoofun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (9, 129, "But if they turn away, [O Muhammad], say, ""Sufficient for me is Allah; there is no deity except Him. On Him I have relied, and He is the Lord of the Great Throne", "فَإِن تَوَلَّوۡاْ فَقُلۡ حَسۡبِيَ ٱللَّهُ لَآ إِلَٰهَ إِلَّا هُوَۖ عَلَيۡهِ تَوَكَّلۡتُۖ وَهُوَ رَبُّ ٱلۡعَرۡشِ ٱلۡعَظِيمِ", "Fain tawallaw faqul hasbiya Allahu la ilaha illa huwa AAalayhi tawakkaltu wahuwa rabbu alAAarshi alAAatheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 1, "Alif, Lam, Ra. These are the verses of the wise Book", "الٓرۚ تِلۡكَ ءَايَٰتُ ٱلۡكِتَٰبِ ٱلۡحَكِيمِ", "Aliflamra tilka ayatu alkitabi alhakeemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 2, "Have the people been amazed that We revealed [revelation] to a man from among them, [saying], ""Warn mankind and give good tidings to those who believe that they will have a [firm] precedence of honor with their Lord""? [But] the disbelievers say, ""Indeed, this is an obvious magician", "أَكَانَ لِلنَّاسِ عَجَبًا أَنۡ أَوۡحَيۡنَآ إِلَىٰ رَجُلٖ مِّنۡهُمۡ أَنۡ أَنذِرِ ٱلنَّاسَ وَبَشِّرِ ٱلَّذِينَ ءَامَنُوٓاْ أَنَّ لَهُمۡ قَدَمَ صِدۡقٍ عِندَ رَبِّهِمۡۗ قَالَ ٱلۡكَٰفِرُونَ إِنَّ هَٰذَا لَسَٰحِرٞ مُّبِينٌ", "Akana lilnnasi AAajaban an awhayna ila rajulin minhum an anthiri alnnasa wabashshiri allatheena amanoo anna lahum qadama sidqin AAinda rabbihim qala alkafiroona inna hatha lasahirun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 3, "Indeed, your Lord is Allah, who created the heavens and the earth in six days and then established Himself above the Throne, arranging the matter [of His creation]. There is no intercessor except after His permission. That is Allah, your Lord, so worship Him. Then will you not remember", "إِنَّ رَبَّكُمُ ٱللَّهُ ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ فِي سِتَّةِ أَيَّامٖ ثُمَّ ٱسۡتَوَىٰ عَلَى ٱلۡعَرۡشِۖ يُدَبِّرُ ٱلۡأَمۡرَۖ مَا مِن شَفِيعٍ إِلَّا مِنۢ بَعۡدِ إِذۡنِهِۦۚ ذَٰلِكُمُ ٱللَّهُ رَبُّكُمۡ فَٱعۡبُدُوهُۚ أَفَلَا تَذَكَّرُونَ", "Inna rabbakumu Allahu allathee khalaqa alssamawati waalarda fee sittati ayyamin thumma istawa AAala alAAarshi yudabbiru alamra ma min shafeeAAin illa min baAAdi ithnihi thalikumu Allahu rabbukum faoAAbudoohu afala tathakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 4, "To Him is your return all together. [It is] the promise of Allah [which is] truth. Indeed, He begins the [process of] creation and then repeats it that He may reward those who have believed and done righteous deeds, in justice. But those who disbelieved will have a drink of scalding water and a painful punishment for what they used to deny", "إِلَيۡهِ مَرۡجِعُكُمۡ جَمِيعٗاۖ وَعۡدَ ٱللَّهِ حَقًّاۚ إِنَّهُۥ يَبۡدَؤُاْ ٱلۡخَلۡقَ ثُمَّ يُعِيدُهُۥ لِيَجۡزِيَ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ بِٱلۡقِسۡطِۚ وَٱلَّذِينَ كَفَرُواْ لَهُمۡ شَرَابٞ مِّنۡ حَمِيمٖ وَعَذَابٌ أَلِيمُۢ بِمَا كَانُواْ يَكۡفُرُونَ", "Ilayhi marjiAAukum jameeAAan waAAda Allahi haqqan innahu yabdao alkhalqa thumma yuAAeeduhu liyajziya allatheena amanoo waAAamiloo alssalihati bialqisti waallatheena kafaroo lahum sharabun min hameemin waAAathabun aleemun bima kanoo yakfuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 5, "It is He who made the sun a shining light and the moon a derived light and determined for it phases - that you may know the number of years and account [of time]. Allah has not created this except in truth. He details the signs for a people who know", "هُوَ ٱلَّذِي جَعَلَ ٱلشَّمۡسَ ضِيَآءٗ وَٱلۡقَمَرَ نُورٗا وَقَدَّرَهُۥ مَنَازِلَ لِتَعۡلَمُواْ عَدَدَ ٱلسِّنِينَ وَٱلۡحِسَابَۚ مَا خَلَقَ ٱللَّهُ ذَٰلِكَ إِلَّا بِٱلۡحَقِّۚ يُفَصِّلُ ٱلۡأٓيَٰتِ لِقَوۡمٖ يَعۡلَمُونَ", "Huwa allathee jaAAala alshshamsa diyaan waalqamara nooran waqaddarahu manazila litaAAlamoo AAadada alssineena waalhisaba ma khalaqa Allahu thalika illa bialhaqqi yufassilu alayati liqawmin yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 6, "Indeed, in the alternation of the night and the day and [in] what Allah has created in the heavens and the earth are signs for a people who fear Allah", "إِنَّ فِي ٱخۡتِلَٰفِ ٱلَّيۡلِ وَٱلنَّهَارِ وَمَا خَلَقَ ٱللَّهُ فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ لَأٓيَٰتٖ لِّقَوۡمٖ يَتَّقُونَ", "Inna fee ikhtilafi allayli waalnnahari wama khalaqa Allahu fee alssamawati waalardi laayatin liqawmin yattaqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 7, "Indeed, those who do not expect the meeting with Us and are satisfied with the life of this world and feel secure therein and those who are heedless of Our signs", "إِنَّ ٱلَّذِينَ لَا يَرۡجُونَ لِقَآءَنَا وَرَضُواْ بِٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَٱطۡمَأَنُّواْ بِهَا وَٱلَّذِينَ هُمۡ عَنۡ ءَايَٰتِنَا غَٰفِلُونَ", "Inna allatheena la yarjoona liqaana waradoo bialhayati alddunya waitmaannoo biha waallatheena hum AAan ayatina ghafiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 8, "For those their refuge will be the Fire because of what they used to earn", "أُوْلَـٰٓئِكَ مَأۡوَىٰهُمُ ٱلنَّارُ بِمَا كَانُواْ يَكۡسِبُونَ", "Olaika mawahumu alnnaru bima kanoo yaksiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 9, "Indeed, those who have believed and done righteous deeds - their Lord will guide them because of their faith. Beneath them rivers will flow in the Gardens of Pleasure", "إِنَّ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ يَهۡدِيهِمۡ رَبُّهُم بِإِيمَٰنِهِمۡۖ تَجۡرِي مِن تَحۡتِهِمُ ٱلۡأَنۡهَٰرُ فِي جَنَّـٰتِ ٱلنَّعِيمِ", "Inna allatheena amanoo waAAamiloo alssalihati yahdeehim rabbuhum bieemanihim tajree min tahtihimu alanharu fee jannati alnnaAAeemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 10, "Their call therein will be, ""Exalted are You, O Allah,"" and their greeting therein will be, ""Peace."" And the last of their call will be, ""Praise to Allah, Lord of the worlds", "دَعۡوَىٰهُمۡ فِيهَا سُبۡحَٰنَكَ ٱللَّهُمَّ وَتَحِيَّتُهُمۡ فِيهَا سَلَٰمٞۚ وَءَاخِرُ دَعۡوَىٰهُمۡ أَنِ ٱلۡحَمۡدُ لِلَّهِ رَبِّ ٱلۡعَٰلَمِينَ", "DaAAwahum feeha subhanaka allahumma watahiyyatuhum feeha salamun waakhiru daAAwahum ani alhamdu lillahi rabbi alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 11, "And if Allah was to hasten for the people the evil [they invoke] as He hastens for them the good, their term would have been ended for them. But We leave the ones who do not expect the meeting with Us, in their transgression, wandering blindly", "۞وَلَوۡ يُعَجِّلُ ٱللَّهُ لِلنَّاسِ ٱلشَّرَّ ٱسۡتِعۡجَالَهُم بِٱلۡخَيۡرِ لَقُضِيَ إِلَيۡهِمۡ أَجَلُهُمۡۖ فَنَذَرُ ٱلَّذِينَ لَا يَرۡجُونَ لِقَآءَنَا فِي طُغۡيَٰنِهِمۡ يَعۡمَهُونَ", "Walaw yuAAajjilu Allahu lilnnasi alshsharra istiAAjalahum bialkhayri laqudiya ilayhim ajaluhum fanatharu allatheena la yarjoona liqaana fee tughyanihim yaAAmahoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 12, "And when affliction touches man, he calls upon Us, whether lying on his side or sitting or standing; but when We remove from him his affliction, he continues [in disobedience] as if he had never called upon Us to [remove] an affliction that touched him. Thus is made pleasing to the transgressors that which they have been doing", "وَإِذَا مَسَّ ٱلۡإِنسَٰنَ ٱلضُّرُّ دَعَانَا لِجَنۢبِهِۦٓ أَوۡ قَاعِدًا أَوۡ قَآئِمٗا فَلَمَّا كَشَفۡنَا عَنۡهُ ضُرَّهُۥ مَرَّ كَأَن لَّمۡ يَدۡعُنَآ إِلَىٰ ضُرّٖ مَّسَّهُۥۚ كَذَٰلِكَ زُيِّنَ لِلۡمُسۡرِفِينَ مَا كَانُواْ يَعۡمَلُونَ", "Waitha massa alinsana alddurru daAAana lijanbihi aw qaAAidan aw qaiman falamma kashafna AAanhu durrahu marra kaan lam yadAAuna ila durrin massahu kathalika zuyyina lilmusrifeena ma kanoo yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 13, "And We had already destroyed generations before you when they wronged, and their messengers had come to them with clear proofs, but they were not to believe. Thus do We recompense the criminal people", "وَلَقَدۡ أَهۡلَكۡنَا ٱلۡقُرُونَ مِن قَبۡلِكُمۡ لَمَّا ظَلَمُواْ وَجَآءَتۡهُمۡ رُسُلُهُم بِٱلۡبَيِّنَٰتِ وَمَا كَانُواْ لِيُؤۡمِنُواْۚ كَذَٰلِكَ نَجۡزِي ٱلۡقَوۡمَ ٱلۡمُجۡرِمِينَ", "Walaqad ahlakna alquroona min qablikum lamma thalamoo wajaathum rusuluhum bialbayyinati wama kanoo liyuminoo kathalika najzee alqawma almujrimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 14, "Then We made you successors in the land after them so that We may observe how you will do", "ثُمَّ جَعَلۡنَٰكُمۡ خَلَـٰٓئِفَ فِي ٱلۡأَرۡضِ مِنۢ بَعۡدِهِمۡ لِنَنظُرَ كَيۡفَ تَعۡمَلُونَ", "Thumma jaAAalnakum khalaifa fee alardi min baAAdihim linanthura kayfa taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 15, "And when Our verses are recited to them as clear evidences, those who do not expect the meeting with Us say, ""Bring us a Qur'an other than this or change it."" Say, [O Muhammad], ""It is not for me to change it on my own accord. I only follow what is revealed to me. Indeed I fear, if I should disobey my Lord, the punishment of a tremendous Day", "وَإِذَا تُتۡلَىٰ عَلَيۡهِمۡ ءَايَاتُنَا بَيِّنَٰتٖ قَالَ ٱلَّذِينَ لَا يَرۡجُونَ لِقَآءَنَا ٱئۡتِ بِقُرۡءَانٍ غَيۡرِ هَٰذَآ أَوۡ بَدِّلۡهُۚ قُلۡ مَا يَكُونُ لِيٓ أَنۡ أُبَدِّلَهُۥ مِن تِلۡقَآيِٕ نَفۡسِيٓۖ إِنۡ أَتَّبِعُ إِلَّا مَا يُوحَىٰٓ إِلَيَّۖ إِنِّيٓ أَخَافُ إِنۡ عَصَيۡتُ رَبِّي عَذَابَ يَوۡمٍ عَظِيمٖ", "Waitha tutla AAalayhim ayatuna bayyinatin qala allatheena la yarjoona liqaana iti biquranin ghayri hatha aw baddilhu qul ma yakoonu lee an obaddilahu min tilqai nafsee in attabiAAu illa ma yooha ilayya innee akhafu in AAasaytu rabbee AAathaba yawmin AAatheemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 16, "Say, ""If Allah had willed, I would not have recited it to you, nor would He have made it known to you, for I had remained among you a lifetime before it. Then will you not reason", "قُل لَّوۡ شَآءَ ٱللَّهُ مَا تَلَوۡتُهُۥ عَلَيۡكُمۡ وَلَآ أَدۡرَىٰكُم بِهِۦۖ فَقَدۡ لَبِثۡتُ فِيكُمۡ عُمُرٗا مِّن قَبۡلِهِۦٓۚ أَفَلَا تَعۡقِلُونَ", "Qul law shaa Allahu ma talawtuhu AAalaykum wala adrakum bihi faqad labithtu feekum AAumuran min qablihi afala taAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 17, "So who is more unjust than he who invents a lie about Allah or denies His signs? Indeed, the criminals will not succeed", "فَمَنۡ أَظۡلَمُ مِمَّنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبًا أَوۡ كَذَّبَ بِـَٔايَٰتِهِۦٓۚ إِنَّهُۥ لَا يُفۡلِحُ ٱلۡمُجۡرِمُونَ", "Faman athlamu mimmani iftara AAala Allahi kathiban aw kaththaba biayatihi innahu la yuflihu almujrimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 18, "And they worship other than Allah that which neither harms them nor benefits them, and they say, ""These are our intercessors with Allah "" Say, ""Do you inform Allah of something He does not know in the heavens or on the earth?"" Exalted is He and high above what they associate with Him", "وَيَعۡبُدُونَ مِن دُونِ ٱللَّهِ مَا لَا يَضُرُّهُمۡ وَلَا يَنفَعُهُمۡ وَيَقُولُونَ هَـٰٓؤُلَآءِ شُفَعَـٰٓؤُنَا عِندَ ٱللَّهِۚ قُلۡ أَتُنَبِّـُٔونَ ٱللَّهَ بِمَا لَا يَعۡلَمُ فِي ٱلسَّمَٰوَٰتِ وَلَا فِي ٱلۡأَرۡضِۚ سُبۡحَٰنَهُۥ وَتَعَٰلَىٰ عَمَّا يُشۡرِكُونَ", "WayaAAbudoona min dooni Allahi ma la yadurruhum wala yanfaAAuhum wayaqooloona haolai shufaAAaona AAinda Allahi qul atunabbioona Allaha bima la yaAAlamu fee alssamawati wala fee alardi subhanahu wataAAala AAamma yushrikoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 19, "And mankind was not but one community [united in religion], but [then] they differed. And if not for a word that preceded from your Lord, it would have been judged between them [immediately] concerning that over which they differ", "وَمَا كَانَ ٱلنَّاسُ إِلَّآ أُمَّةٗ وَٰحِدَةٗ فَٱخۡتَلَفُواْۚ وَلَوۡلَا كَلِمَةٞ سَبَقَتۡ مِن رَّبِّكَ لَقُضِيَ بَيۡنَهُمۡ فِيمَا فِيهِ يَخۡتَلِفُونَ", "Wama kana alnnasu illa ommatan wahidatan faikhtalafoo walawla kalimatun sabaqat min rabbika laqudiya baynahum feema feehi yakhtalifoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 20, "And they say, ""Why is a sign not sent down to him from his Lord?"" So say, ""The unseen is only for Allah [to administer], so wait; indeed, I am with you among those who wait", "وَيَقُولُونَ لَوۡلَآ أُنزِلَ عَلَيۡهِ ءَايَةٞ مِّن رَّبِّهِۦۖ فَقُلۡ إِنَّمَا ٱلۡغَيۡبُ لِلَّهِ فَٱنتَظِرُوٓاْ إِنِّي مَعَكُم مِّنَ ٱلۡمُنتَظِرِينَ", "Wayaqooloona lawla onzila AAalayhi ayatun min rabbihi faqul innama alghaybu lillahi faintathiroo innee maAAakum mina almuntathireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 21, "And when We give the people a taste of mercy after adversity has touched them, at once they conspire against Our verses. Say, ""Allah is swifter in strategy."" Indeed, Our messengers record that which you conspire", "وَإِذَآ أَذَقۡنَا ٱلنَّاسَ رَحۡمَةٗ مِّنۢ بَعۡدِ ضَرَّآءَ مَسَّتۡهُمۡ إِذَا لَهُم مَّكۡرٞ فِيٓ ءَايَاتِنَاۚ قُلِ ٱللَّهُ أَسۡرَعُ مَكۡرًاۚ إِنَّ رُسُلَنَا يَكۡتُبُونَ مَا تَمۡكُرُونَ", "Waitha athaqna alnnasa rahmatan min baAAdi darraa massathum itha lahum makrun fee ayatina quli Allahu asraAAu makran inna rusulana yaktuboona ma tamkuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 22, "It is He who enables you to travel on land and sea until, when you are in ships and they sail with them by a good wind and they rejoice therein, there comes a storm wind and the waves come upon them from everywhere and they assume that they are surrounded, supplicating Allah, sincere to Him in religion, ""If You should save us from this, we will surely be among the thankful", "هُوَ ٱلَّذِي يُسَيِّرُكُمۡ فِي ٱلۡبَرِّ وَٱلۡبَحۡرِۖ حَتَّىٰٓ إِذَا كُنتُمۡ فِي ٱلۡفُلۡكِ وَجَرَيۡنَ بِهِم بِرِيحٖ طَيِّبَةٖ وَفَرِحُواْ بِهَا جَآءَتۡهَا رِيحٌ عَاصِفٞ وَجَآءَهُمُ ٱلۡمَوۡجُ مِن كُلِّ مَكَانٖ وَظَنُّوٓاْ أَنَّهُمۡ أُحِيطَ بِهِمۡ دَعَوُاْ ٱللَّهَ مُخۡلِصِينَ لَهُ ٱلدِّينَ لَئِنۡ أَنجَيۡتَنَا مِنۡ هَٰذِهِۦ لَنَكُونَنَّ مِنَ ٱلشَّـٰكِرِينَ", "Huwa allathee yusayyirukum fee albarri waalbahri hatta itha kuntum fee alfulki wajarayna bihim bireehin tayyibatin wafarihoo biha jaatha reehun AAasifun wajaahumu almawju min kulli makanin wathannoo annahum oheeta bihim daAAawoo Allaha mukhliseena lahu alddeena lain anjaytana min hathihi lanakoonanna mina alshshakireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 23, "But when He saves them, at once they commit injustice upon the earth without right. O mankind, your injustice is only against yourselves, [being merely] the enjoyment of worldly life. Then to Us is your return, and We will inform you of what you used to do", "فَلَمَّآ أَنجَىٰهُمۡ إِذَا هُمۡ يَبۡغُونَ فِي ٱلۡأَرۡضِ بِغَيۡرِ ٱلۡحَقِّۗ يَـٰٓأَيُّهَا ٱلنَّاسُ إِنَّمَا بَغۡيُكُمۡ عَلَىٰٓ أَنفُسِكُمۖ مَّتَٰعَ ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۖ ثُمَّ إِلَيۡنَا مَرۡجِعُكُمۡ فَنُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ", "Falamma anjahum itha hum yabghoona fee alardi bighayri alhaqqi ya ayyuha alnnasu innama baghyukum AAala anfusikum mataAAa alhayati alddunya thumma ilayna marjiAAukum fanunabbiokum bima kuntum taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 24, "The example of [this] worldly life is but like rain which We have sent down from the sky that the plants of the earth absorb - [those] from which men and livestock eat - until, when the earth has taken on its adornment and is beautified and its people suppose that they have capability over it, there comes to it Our command by night or by day, and We make it as a harvest, as if it had not flourished yesterday. Thus do We explain in detail the signs for a people who give thought", "إِنَّمَا مَثَلُ ٱلۡحَيَوٰةِ ٱلدُّنۡيَا كَمَآءٍ أَنزَلۡنَٰهُ مِنَ ٱلسَّمَآءِ فَٱخۡتَلَطَ بِهِۦ نَبَاتُ ٱلۡأَرۡضِ مِمَّا يَأۡكُلُ ٱلنَّاسُ وَٱلۡأَنۡعَٰمُ حَتَّىٰٓ إِذَآ أَخَذَتِ ٱلۡأَرۡضُ زُخۡرُفَهَا وَٱزَّيَّنَتۡ وَظَنَّ أَهۡلُهَآ أَنَّهُمۡ قَٰدِرُونَ عَلَيۡهَآ أَتَىٰهَآ أَمۡرُنَا لَيۡلًا أَوۡ نَهَارٗا فَجَعَلۡنَٰهَا حَصِيدٗا كَأَن لَّمۡ تَغۡنَ بِٱلۡأَمۡسِۚ كَذَٰلِكَ نُفَصِّلُ ٱلۡأٓيَٰتِ لِقَوۡمٖ يَتَفَكَّرُونَ", "Innama mathalu alhayati alddunya kamain anzalnahu mina alssamai faikhtalata bihi nabatu alardi mimma yakulu alnnasu waalanAAamu hatta itha akhathati alardu zukhrufaha waizzayyanat wathanna ahluha annahum qadiroona AAalayha ataha amruna laylan aw naharan fajaAAalnaha haseedan kaan lam taghna bialamsi kathalika nufassilu alayati liqawmin yatafakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 25, "And Allah invites to the Home of Peace and guides whom He wills to a straight path", "وَٱللَّهُ يَدۡعُوٓاْ إِلَىٰ دَارِ ٱلسَّلَٰمِ وَيَهۡدِي مَن يَشَآءُ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ", "WaAllahu yadAAoo ila dari alssalami wayahdee man yashao ila siratin mustaqeemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 26, "For them who have done good is the best [reward] and extra. No darkness will cover their faces, nor humiliation. Those are companions of Paradise; they will abide therein eternally", "۞لِّلَّذِينَ أَحۡسَنُواْ ٱلۡحُسۡنَىٰ وَزِيَادَةٞۖ وَلَا يَرۡهَقُ وُجُوهَهُمۡ قَتَرٞ وَلَا ذِلَّةٌۚ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلۡجَنَّةِۖ هُمۡ فِيهَا خَٰلِدُونَ", "Lillatheena ahsanoo alhusna waziyadatun wala yarhaqu wujoohahum qatarun wala thillatun olaika ashabu aljannati hum feeha khalidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 27, "But they who have earned [blame for] evil doings - the recompense of an evil deed is its equivalent, and humiliation will cover them. They will have from Allah no protector. It will be as if their faces are covered with pieces of the night - so dark [are they]. Those are the companions of the Fire; they will abide therein eternally", "وَٱلَّذِينَ كَسَبُواْ ٱلسَّيِّـَٔاتِ جَزَآءُ سَيِّئَةِۭ بِمِثۡلِهَا وَتَرۡهَقُهُمۡ ذِلَّةٞۖ مَّا لَهُم مِّنَ ٱللَّهِ مِنۡ عَاصِمٖۖ كَأَنَّمَآ أُغۡشِيَتۡ وُجُوهُهُمۡ قِطَعٗا مِّنَ ٱلَّيۡلِ مُظۡلِمًاۚ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلنَّارِۖ هُمۡ فِيهَا خَٰلِدُونَ", "Waallatheena kasaboo alssayyiati jazao sayyiatin bimithliha watarhaquhum thillatun ma lahum mina Allahi min AAasimin kaannama oghshiyat wujoohuhum qitaAAan mina allayli muthliman olaika ashabu alnnari hum feeha khalidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 28, "And [mention, O Muhammad], the Day We will gather them all together - then We will say to those who associated others with Allah, ""[Remain in] your place, you and your 'partners.' "" Then We will separate them, and their ""partners"" will say, ""You did not used to worship us", "وَيَوۡمَ نَحۡشُرُهُمۡ جَمِيعٗا ثُمَّ نَقُولُ لِلَّذِينَ أَشۡرَكُواْ مَكَانَكُمۡ أَنتُمۡ وَشُرَكَآؤُكُمۡۚ فَزَيَّلۡنَا بَيۡنَهُمۡۖ وَقَالَ شُرَكَآؤُهُم مَّا كُنتُمۡ إِيَّانَا تَعۡبُدُونَ", "Wayawma nahshuruhum jameeAAan thumma naqoolu lillatheena ashrakoo makanakum antum washurakaokum fazayyalna baynahum waqala shurakaohum ma kuntum iyyana taAAbudoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 29, "And sufficient is Allah as a witness between us and you that we were of your worship unaware", "فَكَفَىٰ بِٱللَّهِ شَهِيدَۢا بَيۡنَنَا وَبَيۡنَكُمۡ إِن كُنَّا عَنۡ عِبَادَتِكُمۡ لَغَٰفِلِينَ", "Fakafa biAllahi shaheedan baynana wabaynakum in kunna AAan AAibadatikum laghafileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 30, "There, [on that Day], every soul will be put to trial for what it did previously, and they will be returned to Allah, their master, the Truth, and lost from them is whatever they used to invent", "هُنَالِكَ تَبۡلُواْ كُلُّ نَفۡسٖ مَّآ أَسۡلَفَتۡۚ وَرُدُّوٓاْ إِلَى ٱللَّهِ مَوۡلَىٰهُمُ ٱلۡحَقِّۖ وَضَلَّ عَنۡهُم مَّا كَانُواْ يَفۡتَرُونَ", "Hunalika tabloo kullu nafsin ma aslafat waruddoo ila Allahi mawlahumu alhaqqi wadalla AAanhum ma kanoo yaftaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 31, "Say, ""Who provides for you from the heaven and the earth? Or who controls hearing and sight and who brings the living out of the dead and brings the dead out of the living and who arranges [every] matter?"" They will say, ""Allah,"" so say, ""Then will you not fear Him", "قُلۡ مَن يَرۡزُقُكُم مِّنَ ٱلسَّمَآءِ وَٱلۡأَرۡضِ أَمَّن يَمۡلِكُ ٱلسَّمۡعَ وَٱلۡأَبۡصَٰرَ وَمَن يُخۡرِجُ ٱلۡحَيَّ مِنَ ٱلۡمَيِّتِ وَيُخۡرِجُ ٱلۡمَيِّتَ مِنَ ٱلۡحَيِّ وَمَن يُدَبِّرُ ٱلۡأَمۡرَۚ فَسَيَقُولُونَ ٱللَّهُۚ فَقُلۡ أَفَلَا تَتَّقُونَ", "Qul man yarzuqukum mina alssamai waalardi amman yamliku alssamAAa waalabsara waman yukhriju alhayya mina almayyiti wayukhriju almayyita mina alhayyi waman yudabbiru alamra fasayaqooloona Allahu faqul afala tattaqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 32, "For that is Allah, your Lord, the Truth. And what can be beyond truth except error? So how are you averted", "فَذَٰلِكُمُ ٱللَّهُ رَبُّكُمُ ٱلۡحَقُّۖ فَمَاذَا بَعۡدَ ٱلۡحَقِّ إِلَّا ٱلضَّلَٰلُۖ فَأَنَّىٰ تُصۡرَفُونَ", "Fathalikumu Allahu rabbukumu alhaqqu famatha baAAda alhaqqi illa alddalalu faanna tusrafoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 33, "Thus the word of your Lord has come into effect upon those who defiantly disobeyed - that they will not believe", "كَذَٰلِكَ حَقَّتۡ كَلِمَتُ رَبِّكَ عَلَى ٱلَّذِينَ فَسَقُوٓاْ أَنَّهُمۡ لَا يُؤۡمِنُونَ", "Kathalika haqqat kalimatu rabbika AAala allatheena fasaqoo annahum la yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 34, "Say, ""Are there of your 'partners' any who begins creation and then repeats it?"" Say, ""Allah begins creation and then repeats it, so how are you deluded", "قُلۡ هَلۡ مِن شُرَكَآئِكُم مَّن يَبۡدَؤُاْ ٱلۡخَلۡقَ ثُمَّ يُعِيدُهُۥۚ قُلِ ٱللَّهُ يَبۡدَؤُاْ ٱلۡخَلۡقَ ثُمَّ يُعِيدُهُۥۖ فَأَنَّىٰ تُؤۡفَكُونَ", "Qul hal min shurakaikum man yabdao alkhalqa thumma yuAAeeduhu quli Allahu yabdao alkhalqa thumma yuAAeeduhu faanna tufakoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 35, "Say, ""Are there of your 'partners' any who guides to the truth?"" Say, ""Allah guides to the truth. So is He who guides to the truth more worthy to be followed or he who guides not unless he is guided? Then what is [wrong] with you - how do you judge", "قُلۡ هَلۡ مِن شُرَكَآئِكُم مَّن يَهۡدِيٓ إِلَى ٱلۡحَقِّۚ قُلِ ٱللَّهُ يَهۡدِي لِلۡحَقِّۗ أَفَمَن يَهۡدِيٓ إِلَى ٱلۡحَقِّ أَحَقُّ أَن يُتَّبَعَ أَمَّن لَّا يَهِدِّيٓ إِلَّآ أَن يُهۡدَىٰۖ فَمَا لَكُمۡ كَيۡفَ تَحۡكُمُونَ", "Qul hal min shurakaikum man yahdee ila alhaqqi quli Allahu yahdee lilhaqqi afaman yahdee ila alhaqqi ahaqqu an yuttabaAAa amman la yahiddee illa an yuhda fama lakum kayfa tahkumoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 36, "And most of them follow not except assumption. Indeed, assumption avails not against the truth at all. Indeed, Allah is Knowing of what they do", "وَمَا يَتَّبِعُ أَكۡثَرُهُمۡ إِلَّا ظَنًّاۚ إِنَّ ٱلظَّنَّ لَا يُغۡنِي مِنَ ٱلۡحَقِّ شَيۡـًٔاۚ إِنَّ ٱللَّهَ عَلِيمُۢ بِمَا يَفۡعَلُونَ", "Wama yattabiAAu aktharuhum illa thannan inna alththanna la yughnee mina alhaqqi shayan inna Allaha AAaleemun bima yafAAaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 37, "And it was not [possible] for this Qur'an to be produced by other than Allah, but [it is] a confirmation of what was before it and a detailed explanation of the [former] Scripture, about which there is no doubt, from the Lord of the worlds", "وَمَا كَانَ هَٰذَا ٱلۡقُرۡءَانُ أَن يُفۡتَرَىٰ مِن دُونِ ٱللَّهِ وَلَٰكِن تَصۡدِيقَ ٱلَّذِي بَيۡنَ يَدَيۡهِ وَتَفۡصِيلَ ٱلۡكِتَٰبِ لَا رَيۡبَ فِيهِ مِن رَّبِّ ٱلۡعَٰلَمِينَ", "Wama kana hatha alquranu an yuftara min dooni Allahi walakin tasdeeqa allathee bayna yadayhi watafseela alkitabi la rayba feehi min rabbi alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 38, "Or do they say [about the Prophet], ""He invented it?"" Say, ""Then bring forth a surah like it and call upon [for assistance] whomever you can besides Allah, if you should be truthful", "أَمۡ يَقُولُونَ ٱفۡتَرَىٰهُۖ قُلۡ فَأۡتُواْ بِسُورَةٖ مِّثۡلِهِۦ وَٱدۡعُواْ مَنِ ٱسۡتَطَعۡتُم مِّن دُونِ ٱللَّهِ إِن كُنتُمۡ صَٰدِقِينَ", "Am yaqooloona iftarahu qul fatoo bisooratin mithlihi waodAAoo mani istataAAtum min dooni Allahi in kuntum sadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 39, "Rather, they have denied that which they encompass not in knowledge and whose interpretation has not yet come to them. Thus did those before them deny. Then observe how was the end of the wrongdoers", "بَلۡ كَذَّبُواْ بِمَا لَمۡ يُحِيطُواْ بِعِلۡمِهِۦ وَلَمَّا يَأۡتِهِمۡ تَأۡوِيلُهُۥۚ كَذَٰلِكَ كَذَّبَ ٱلَّذِينَ مِن قَبۡلِهِمۡۖ فَٱنظُرۡ كَيۡفَ كَانَ عَٰقِبَةُ ٱلظَّـٰلِمِينَ", "Bal kaththaboo bima lam yuheetoo biAAilmihi walamma yatihim taweeluhu kathalika kaththaba allatheena min qablihim faonthur kayfa kana AAaqibatu alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 40, "And of them are those who believe in it, and of them are those who do not believe in it. And your Lord is most knowing of the corrupters", "وَمِنۡهُم مَّن يُؤۡمِنُ بِهِۦ وَمِنۡهُم مَّن لَّا يُؤۡمِنُ بِهِۦۚ وَرَبُّكَ أَعۡلَمُ بِٱلۡمُفۡسِدِينَ", "Waminhum man yuminu bihi waminhum man la yuminu bihi warabbuka aAAlamu bialmufsideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 41, "And if they deny you, [O Muhammad], then say, ""For me are my deeds, and for you are your deeds. You are disassociated from what I do, and I am disassociated from what you do", "وَإِن كَذَّبُوكَ فَقُل لِّي عَمَلِي وَلَكُمۡ عَمَلُكُمۡۖ أَنتُم بَرِيٓـُٔونَ مِمَّآ أَعۡمَلُ وَأَنَا۠ بَرِيٓءٞ مِّمَّا تَعۡمَلُونَ", "Wain kaththabooka faqul lee AAamalee walakum AAamalukum antum bareeoona mimma aAAmalu waana bareeon mimma taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 42, "And among them are those who listen to you. But can you cause the deaf to hear, although they will not use reason", "وَمِنۡهُم مَّن يَسۡتَمِعُونَ إِلَيۡكَۚ أَفَأَنتَ تُسۡمِعُ ٱلصُّمَّ وَلَوۡ كَانُواْ لَا يَعۡقِلُونَ", "Waminhum man yastamiAAoona ilayka afaanta tusmiAAu alssumma walaw kanoo la yaAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 43, "And among them are those who look at you. But can you guide the blind although they will not [attempt to] see", "وَمِنۡهُم مَّن يَنظُرُ إِلَيۡكَۚ أَفَأَنتَ تَهۡدِي ٱلۡعُمۡيَ وَلَوۡ كَانُواْ لَا يُبۡصِرُونَ", "Waminhum man yanthuru ilayka afaanta tahdee alAAumya walaw kanoo la yubsiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 44, "Indeed, Allah does not wrong the people at all, but it is the people who are wronging themselves", "إِنَّ ٱللَّهَ لَا يَظۡلِمُ ٱلنَّاسَ شَيۡـٔٗا وَلَٰكِنَّ ٱلنَّاسَ أَنفُسَهُمۡ يَظۡلِمُونَ", "Inna Allaha la yathlimu alnnasa shayan walakinna alnnasa anfusahum yathlimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 45, "And on the Day when He will gather them, [it will be] as if they had not remained [in the world] but an hour of the day, [and] they will know each other. Those will have lost who denied the meeting with Allah and were not guided", "وَيَوۡمَ يَحۡشُرُهُمۡ كَأَن لَّمۡ يَلۡبَثُوٓاْ إِلَّا سَاعَةٗ مِّنَ ٱلنَّهَارِ يَتَعَارَفُونَ بَيۡنَهُمۡۚ قَدۡ خَسِرَ ٱلَّذِينَ كَذَّبُواْ بِلِقَآءِ ٱللَّهِ وَمَا كَانُواْ مُهۡتَدِينَ", "Wayawma yahshuruhum kaan lam yalbathoo illa saAAatan mina alnnahari yataAAarafoona baynahum qad khasira allatheena kaththaboo biliqai Allahi wama kanoo muhtadeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 46, "And whether We show you some of what We promise them, [O Muhammad], or We take you in death, to Us is their return; then, [either way], Allah is a witness concerning what they are doing", "وَإِمَّا نُرِيَنَّكَ بَعۡضَ ٱلَّذِي نَعِدُهُمۡ أَوۡ نَتَوَفَّيَنَّكَ فَإِلَيۡنَا مَرۡجِعُهُمۡ ثُمَّ ٱللَّهُ شَهِيدٌ عَلَىٰ مَا يَفۡعَلُونَ", "Waimma nuriyannaka baAAda allathee naAAiduhum aw natawaffayannaka failayna marjiAAuhum thumma Allahu shaheedun AAala ma yafAAaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 47, "And for every nation is a messenger. So when their messenger comes, it will be judged between them in justice, and they will not be wronged", "وَلِكُلِّ أُمَّةٖ رَّسُولٞۖ فَإِذَا جَآءَ رَسُولُهُمۡ قُضِيَ بَيۡنَهُم بِٱلۡقِسۡطِ وَهُمۡ لَا يُظۡلَمُونَ", "Walikulli ommatin rasoolun faitha jaa rasooluhum qudiya baynahum bialqisti wahum la yuthlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 48, "And they say, ""When is [the fulfillment of] this promise, if you should be truthful", "وَيَقُولُونَ مَتَىٰ هَٰذَا ٱلۡوَعۡدُ إِن كُنتُمۡ صَٰدِقِينَ", "Wayaqooloona mata hatha alwaAAdu in kuntum sadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 49, "Say, ""I possess not for myself any harm or benefit except what Allah should will. For every nation is a [specified] term. When their time has come, then they will not remain behind an hour, nor will they precede [it]", "قُل لَّآ أَمۡلِكُ لِنَفۡسِي ضَرّٗا وَلَا نَفۡعًا إِلَّا مَا شَآءَ ٱللَّهُۗ لِكُلِّ أُمَّةٍ أَجَلٌۚ إِذَا جَآءَ أَجَلُهُمۡ فَلَا يَسۡتَـٔۡخِرُونَ سَاعَةٗ وَلَا يَسۡتَقۡدِمُونَ", "Qul la amliku linafsee darran wala nafAAan illa ma shaa Allahu likulli ommatin ajalun itha jaa ajaluhum fala yastakhiroona saAAatan wala yastaqdimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 50, "Say, ""Have you considered: if His punishment should come to you by night or by day - for which [aspect] of it would the criminals be impatient", "قُلۡ أَرَءَيۡتُمۡ إِنۡ أَتَىٰكُمۡ عَذَابُهُۥ بَيَٰتًا أَوۡ نَهَارٗا مَّاذَا يَسۡتَعۡجِلُ مِنۡهُ ٱلۡمُجۡرِمُونَ", "Qul araaytum in atakum AAathabuhu bayatan aw naharan matha yastaAAjilu minhu almujrimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 51, "Then is it that when it has [actually] occurred you will believe in it? Now? And you were [once] for it impatient", "أَثُمَّ إِذَا مَا وَقَعَ ءَامَنتُم بِهِۦٓۚ ءَآلۡـَٰٔنَ وَقَدۡ كُنتُم بِهِۦ تَسۡتَعۡجِلُونَ", "Athumma itha ma waqaAAa amantum bihi alana waqad kuntum bihi tastaAAjiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 52, "Then it will be said to those who had wronged, ""Taste the punishment of eternity; are you being recompensed except for what you used to earn", "ثُمَّ قِيلَ لِلَّذِينَ ظَلَمُواْ ذُوقُواْ عَذَابَ ٱلۡخُلۡدِ هَلۡ تُجۡزَوۡنَ إِلَّا بِمَا كُنتُمۡ تَكۡسِبُونَ", "Thumma qeela lillatheena thalamoo thooqoo AAathaba alkhuldi hal tujzawna illa bima kuntum taksiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 53, "And they ask information of you, [O Muhammad], ""Is it true?"" Say, ""Yes, by my Lord. Indeed, it is truth; and you will not cause failure [to Allah]", "۞وَيَسۡتَنۢبِـُٔونَكَ أَحَقٌّ هُوَۖ قُلۡ إِي وَرَبِّيٓ إِنَّهُۥ لَحَقّٞۖ وَمَآ أَنتُم بِمُعۡجِزِينَ", "Wayastanbioonaka ahaqqun huwa qul ee warabbee innahu lahaqqun wama antum bimuAAjizeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 54, "And if each soul that wronged had everything on earth, it would offer it in ransom. And they will confide regret when they see the punishment; and they will be judged in justice, and they will not be wronged", "وَلَوۡ أَنَّ لِكُلِّ نَفۡسٖ ظَلَمَتۡ مَا فِي ٱلۡأَرۡضِ لَٱفۡتَدَتۡ بِهِۦۗ وَأَسَرُّواْ ٱلنَّدَامَةَ لَمَّا رَأَوُاْ ٱلۡعَذَابَۖ وَقُضِيَ بَيۡنَهُم بِٱلۡقِسۡطِ وَهُمۡ لَا يُظۡلَمُونَ", "Walaw anna likulli nafsin thalamat ma fee alardi laiftadat bihi waasarroo alnnadamata lamma raawoo alAAathaba waqudiya baynahum bialqisti wahum la yuthlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 55, "Unquestionably, to Allah belongs whatever is in the heavens and the earth. Unquestionably, the promise of Allah is truth, but most of them do not know", "أَلَآ إِنَّ لِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۗ أَلَآ إِنَّ وَعۡدَ ٱللَّهِ حَقّٞ وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَعۡلَمُونَ", "Ala inna lillahi ma fee alssamawati waalardi ala inna waAAda Allahi haqqun walakinna aktharahum la yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 56, "He gives life and causes death, and to Him you will be returned", "هُوَ يُحۡيِۦ وَيُمِيتُ وَإِلَيۡهِ تُرۡجَعُونَ", "Huwa yuhyee wayumeetu wailayhi turjaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 57, "O mankind, there has to come to you instruction from your Lord and healing for what is in the breasts and guidance and mercy for the believers", "يَـٰٓأَيُّهَا ٱلنَّاسُ قَدۡ جَآءَتۡكُم مَّوۡعِظَةٞ مِّن رَّبِّكُمۡ وَشِفَآءٞ لِّمَا فِي ٱلصُّدُورِ وَهُدٗى وَرَحۡمَةٞ لِّلۡمُؤۡمِنِينَ", "Ya ayyuha alnnasu qad jaatkum mawAAithatun min rabbikum washifaon lima fee alssudoori wahudan warahmatun lilmumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 58, "Say, ""In the bounty of Allah and in His mercy - in that let them rejoice; it is better than what they accumulate", "قُلۡ بِفَضۡلِ ٱللَّهِ وَبِرَحۡمَتِهِۦ فَبِذَٰلِكَ فَلۡيَفۡرَحُواْ هُوَ خَيۡرٞ مِّمَّا يَجۡمَعُونَ", "Qul bifadli Allahi wabirahmatihi fabithalika falyafrahoo huwa khayrun mimma yajmaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 59, "Say, ""Have you seen what Allah has sent down to you of provision of which you have made [some] lawful and [some] unlawful?"" Say, ""Has Allah permitted you [to do so], or do you invent [something] about Allah", "قُلۡ أَرَءَيۡتُم مَّآ أَنزَلَ ٱللَّهُ لَكُم مِّن رِّزۡقٖ فَجَعَلۡتُم مِّنۡهُ حَرَامٗا وَحَلَٰلٗا قُلۡ ءَآللَّهُ أَذِنَ لَكُمۡۖ أَمۡ عَلَى ٱللَّهِ تَفۡتَرُونَ", "Qul araaytum ma anzala Allahu lakum min rizqin fajaAAaltum minhu haraman wahalalan qul allahu athina lakum am AAala Allahi taftaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 60, "And what will be the supposition of those who invent falsehood about Allah on the Day of Resurrection? Indeed, Allah is full of bounty to the people, but most of them are not grateful", "وَمَا ظَنُّ ٱلَّذِينَ يَفۡتَرُونَ عَلَى ٱللَّهِ ٱلۡكَذِبَ يَوۡمَ ٱلۡقِيَٰمَةِۗ إِنَّ ٱللَّهَ لَذُو فَضۡلٍ عَلَى ٱلنَّاسِ وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَشۡكُرُونَ", "Wama thannu allatheena yaftaroona AAala Allahi alkathiba yawma alqiyamati inna Allaha lathoo fadlin AAala alnnasi walakinna aktharahum la yashkuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 61, "And, [O Muhammad], you are not [engaged] in any matter or recite any of the Qur'an and you [people] do not do any deed except that We are witness over you when you are involved in it. And not absent from your Lord is any [part] of an atom's weight within the earth or within the heaven or [anything] smaller than that or greater but that it is in a clear register", "وَمَا تَكُونُ فِي شَأۡنٖ وَمَا تَتۡلُواْ مِنۡهُ مِن قُرۡءَانٖ وَلَا تَعۡمَلُونَ مِنۡ عَمَلٍ إِلَّا كُنَّا عَلَيۡكُمۡ شُهُودًا إِذۡ تُفِيضُونَ فِيهِۚ وَمَا يَعۡزُبُ عَن رَّبِّكَ مِن مِّثۡقَالِ ذَرَّةٖ فِي ٱلۡأَرۡضِ وَلَا فِي ٱلسَّمَآءِ وَلَآ أَصۡغَرَ مِن ذَٰلِكَ وَلَآ أَكۡبَرَ إِلَّا فِي كِتَٰبٖ مُّبِينٍ", "Wama takoonu fee shanin wama tatloo minhu min quranin wala taAAmaloona min AAamalin illa kunna AAalaykum shuhoodan ith tufeedoona feehi wama yaAAzubu AAan rabbika min mithqali tharratin fee alardi wala fee alssamai wala asghara min thalika wala akbara illa fee kitabin mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 62, "Unquestionably, [for] the allies of Allah there will be no fear concerning them, nor will they grieve", "أَلَآ إِنَّ أَوۡلِيَآءَ ٱللَّهِ لَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ", "Ala inna awliyaa Allahi la khawfun AAalayhim wala hum yahzanoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 63, "Those who believed and were fearing Allah", "ٱلَّذِينَ ءَامَنُواْ وَكَانُواْ يَتَّقُونَ", "Allatheena amanoo wakanoo yattaqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 64, "For them are good tidings in the worldly life and in the Hereafter. No change is there in the words of Allah. That is what is the great attainment", "لَهُمُ ٱلۡبُشۡرَىٰ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَفِي ٱلۡأٓخِرَةِۚ لَا تَبۡدِيلَ لِكَلِمَٰتِ ٱللَّهِۚ ذَٰلِكَ هُوَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ", "Lahumu albushra fee alhayati alddunya wafee alakhirati la tabdeela likalimati Allahi thalika huwa alfawzu alAAatheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 65, "And let not their speech grieve you. Indeed, honor [due to power] belongs to Allah entirely. He is the Hearing, the Knowing", "وَلَا يَحۡزُنكَ قَوۡلُهُمۡۘ إِنَّ ٱلۡعِزَّةَ لِلَّهِ جَمِيعًاۚ هُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ", "Wala yahzunka qawluhum inna alAAizzata lillahi jameeAAan huwa alssameeAAu alAAaleemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 66, "Unquestionably, to Allah belongs whoever is in the heavens and whoever is on the earth. And those who invoke other than Allah do not [actually] follow [His] ""partners."" They follow not except assumption, and they are not but falsifying", "أَلَآ إِنَّ لِلَّهِ مَن فِي ٱلسَّمَٰوَٰتِ وَمَن فِي ٱلۡأَرۡضِۗ وَمَا يَتَّبِعُ ٱلَّذِينَ يَدۡعُونَ مِن دُونِ ٱللَّهِ شُرَكَآءَۚ إِن يَتَّبِعُونَ إِلَّا ٱلظَّنَّ وَإِنۡ هُمۡ إِلَّا يَخۡرُصُونَ", "Ala inna lillahi man fee alssamawati waman fee alardi wama yattabiAAu allatheena yadAAoona min dooni Allahi shurakaa in yattabiAAoona illa alththanna wain hum illa yakhrusoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 67, "It is He who made for you the night to rest therein and the day, giving sight. Indeed in that are signs for a people who listen", "هُوَ ٱلَّذِي جَعَلَ لَكُمُ ٱلَّيۡلَ لِتَسۡكُنُواْ فِيهِ وَٱلنَّهَارَ مُبۡصِرًاۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يَسۡمَعُونَ", "Huwa allathee jaAAala lakumu allayla litaskunoo feehi waalnnahara mubsiran inna fee thalika laayatin liqawmin yasmaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 68, "They have said, ""Allah has taken a son."" Exalted is He; He is the [one] Free of need. To Him belongs whatever is in the heavens and whatever is in the earth. You have no authority for this [claim]. Do you say about Allah that which you do not know", "قَالُواْ ٱتَّخَذَ ٱللَّهُ وَلَدٗاۗ سُبۡحَٰنَهُۥۖ هُوَ ٱلۡغَنِيُّۖ لَهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۚ إِنۡ عِندَكُم مِّن سُلۡطَٰنِۭ بِهَٰذَآۚ أَتَقُولُونَ عَلَى ٱللَّهِ مَا لَا تَعۡلَمُونَ", "Qaloo ittakhatha Allahu waladan subhanahu huwa alghaniyyu lahu ma fee alssamawati wama fee alardi in AAindakum min sultanin bihatha ataqooloona AAala Allahi ma la taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 69, "Say, ""Indeed, those who invent falsehood about Allah will not succeed", "قُلۡ إِنَّ ٱلَّذِينَ يَفۡتَرُونَ عَلَى ٱللَّهِ ٱلۡكَذِبَ لَا يُفۡلِحُونَ", "Qul inna allatheena yaftaroona AAala Allahi alkathiba la yuflihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 70, "[For them is brief] enjoyment in this world; then to Us is their return; then We will make them taste the severe punishment because they used to disbelieve", "مَتَٰعٞ فِي ٱلدُّنۡيَا ثُمَّ إِلَيۡنَا مَرۡجِعُهُمۡ ثُمَّ نُذِيقُهُمُ ٱلۡعَذَابَ ٱلشَّدِيدَ بِمَا كَانُواْ يَكۡفُرُونَ", "MataAAun fee alddunya thumma ilayna marjiAAuhum thumma nutheequhumu alAAathaba alshshadeeda bima kanoo yakfuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 71, "And recite to them the news of Noah, when he said to his people, ""O my people, if my residence and my reminding of the signs of Allah has become burdensome upon you - then I have relied upon Allah. So resolve upon your plan and [call upon] your associates. Then let not your plan be obscure to you. Then carry it out upon me and do not give me respite", "۞وَٱتۡلُ عَلَيۡهِمۡ نَبَأَ نُوحٍ إِذۡ قَالَ لِقَوۡمِهِۦ يَٰقَوۡمِ إِن كَانَ كَبُرَ عَلَيۡكُم مَّقَامِي وَتَذۡكِيرِي بِـَٔايَٰتِ ٱللَّهِ فَعَلَى ٱللَّهِ تَوَكَّلۡتُ فَأَجۡمِعُوٓاْ أَمۡرَكُمۡ وَشُرَكَآءَكُمۡ ثُمَّ لَا يَكُنۡ أَمۡرُكُمۡ عَلَيۡكُمۡ غُمَّةٗ ثُمَّ ٱقۡضُوٓاْ إِلَيَّ وَلَا تُنظِرُونِ", "Waotlu AAalayhim nabaa noohin ith qala liqawmihi ya qawmi in kana kabura AAalaykum maqamee watathkeeree biayati Allahi faAAala Allahi tawakkaltu faajmiAAoo amrakum washurakaakum thumma la yakun amrukum AAalaykum ghummatan thumma iqdoo ilayya wala tunthirooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 72, "And if you turn away [from my advice] then no payment have I asked of you. My reward is only from Allah, and I have been commanded to be of the Muslims", "فَإِن تَوَلَّيۡتُمۡ فَمَا سَأَلۡتُكُم مِّنۡ أَجۡرٍۖ إِنۡ أَجۡرِيَ إِلَّا عَلَى ٱللَّهِۖ وَأُمِرۡتُ أَنۡ أَكُونَ مِنَ ٱلۡمُسۡلِمِينَ", "Fain tawallaytum fama saaltukum min ajrin in ajriya illa AAala Allahi waomirtu an akoona mina almuslimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 73, "And they denied him, so We saved him and those with him in the ship and made them successors, and We drowned those who denied Our signs. Then see how was the end of those who were warned", "فَكَذَّبُوهُ فَنَجَّيۡنَٰهُ وَمَن مَّعَهُۥ فِي ٱلۡفُلۡكِ وَجَعَلۡنَٰهُمۡ خَلَـٰٓئِفَ وَأَغۡرَقۡنَا ٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَاۖ فَٱنظُرۡ كَيۡفَ كَانَ عَٰقِبَةُ ٱلۡمُنذَرِينَ", "Fakaththaboohu fanajjaynahu waman maAAahu fee alfulki wajaAAalnahum khalaifa waaghraqna allatheena kaththaboo biayatina faonthur kayfa kana AAaqibatu almunthareena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 74, "Then We sent after him messengers to their peoples, and they came to them with clear proofs. But they were not to believe in that which they had denied before. Thus We seal over the hearts of the transgressors", "ثُمَّ بَعَثۡنَا مِنۢ بَعۡدِهِۦ رُسُلًا إِلَىٰ قَوۡمِهِمۡ فَجَآءُوهُم بِٱلۡبَيِّنَٰتِ فَمَا كَانُواْ لِيُؤۡمِنُواْ بِمَا كَذَّبُواْ بِهِۦ مِن قَبۡلُۚ كَذَٰلِكَ نَطۡبَعُ عَلَىٰ قُلُوبِ ٱلۡمُعۡتَدِينَ", "Thumma baAAathna min baAAdihi rusulan ila qawmihim fajaoohum bialbayyinati fama kanoo liyuminoo bima kaththaboo bihi min qablu kathalika natbaAAu AAala quloobi almuAAtadeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 75, "Then We sent after them Moses and Aaron to Pharaoh and his establishment with Our signs, but they behaved arrogantly and were a criminal people", "ثُمَّ بَعَثۡنَا مِنۢ بَعۡدِهِم مُّوسَىٰ وَهَٰرُونَ إِلَىٰ فِرۡعَوۡنَ وَمَلَإِيْهِۦ بِـَٔايَٰتِنَا فَٱسۡتَكۡبَرُواْ وَكَانُواْ قَوۡمٗا مُّجۡرِمِينَ", "Thumma baAAathna min baAAdihim moosa waharoona ila firAAawna wamalaihi biayatina faistakbaroo wakanoo qawman mujrimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 76, "So when there came to them the truth from Us, they said, ""Indeed, this is obvious magic", "فَلَمَّا جَآءَهُمُ ٱلۡحَقُّ مِنۡ عِندِنَا قَالُوٓاْ إِنَّ هَٰذَا لَسِحۡرٞ مُّبِينٞ", "Falamma jaahumu alhaqqu min AAindina qaloo inna hatha lasihrun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 77, "Moses said, ""Do you say [thus] about the truth when it has come to you? Is this magic? But magicians will not succeed", "قَالَ مُوسَىٰٓ أَتَقُولُونَ لِلۡحَقِّ لَمَّا جَآءَكُمۡۖ أَسِحۡرٌ هَٰذَا وَلَا يُفۡلِحُ ٱلسَّـٰحِرُونَ", "Qala moosa ataqooloona lilhaqqi lamma jaakum asihrun hatha wala yuflihu alssahiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 78, "They said, ""Have you come to us to turn us away from that upon which we found our fathers and so that you two may have grandeur in the land? And we are not believers in you", "قَالُوٓاْ أَجِئۡتَنَا لِتَلۡفِتَنَا عَمَّا وَجَدۡنَا عَلَيۡهِ ءَابَآءَنَا وَتَكُونَ لَكُمَا ٱلۡكِبۡرِيَآءُ فِي ٱلۡأَرۡضِ وَمَا نَحۡنُ لَكُمَا بِمُؤۡمِنِينَ", "Qaloo ajitana litalfitana AAamma wajadna AAalayhi abaana watakoona lakuma alkibriyao fee alardi wama nahnu lakuma bimumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 79, "And Pharaoh said, ""Bring to me every learned magician", "وَقَالَ فِرۡعَوۡنُ ٱئۡتُونِي بِكُلِّ سَٰحِرٍ عَلِيمٖ", "Waqala firAAawnu itoonee bikulli sahirin AAaleemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 80, "So when the magicians came, Moses said to them, ""Throw down whatever you will throw", "فَلَمَّا جَآءَ ٱلسَّحَرَةُ قَالَ لَهُم مُّوسَىٰٓ أَلۡقُواْ مَآ أَنتُم مُّلۡقُونَ", "Falamma jaa alssaharatu qala lahum moosa alqoo ma antum mulqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 81, "And when they had thrown, Moses said, ""What you have brought is [only] magic. Indeed, Allah will expose its worthlessness. Indeed, Allah does not amend the work of corrupters", "فَلَمَّآ أَلۡقَوۡاْ قَالَ مُوسَىٰ مَا جِئۡتُم بِهِ ٱلسِّحۡرُۖ إِنَّ ٱللَّهَ سَيُبۡطِلُهُۥٓ إِنَّ ٱللَّهَ لَا يُصۡلِحُ عَمَلَ ٱلۡمُفۡسِدِينَ", "Falamma alqaw qala moosa ma jitum bihi alssihru inna Allaha sayubtiluhu inna Allaha la yuslihu AAamala almufsideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 82, "And Allah will establish the truth by His words, even if the criminals dislike it", "وَيُحِقُّ ٱللَّهُ ٱلۡحَقَّ بِكَلِمَٰتِهِۦ وَلَوۡ كَرِهَ ٱلۡمُجۡرِمُونَ", "Wayuhiqqu Allahu alhaqqa bikalimatihi walaw kariha almujrimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 83, "But no one believed Moses, except [some] youths among his people, for fear of Pharaoh and his establishment that they would persecute them. And indeed, Pharaoh was haughty within the land, and indeed, he was of the transgressors", "فَمَآ ءَامَنَ لِمُوسَىٰٓ إِلَّا ذُرِّيَّةٞ مِّن قَوۡمِهِۦ عَلَىٰ خَوۡفٖ مِّن فِرۡعَوۡنَ وَمَلَإِيْهِمۡ أَن يَفۡتِنَهُمۡۚ وَإِنَّ فِرۡعَوۡنَ لَعَالٖ فِي ٱلۡأَرۡضِ وَإِنَّهُۥ لَمِنَ ٱلۡمُسۡرِفِينَ", "Fama amana limoosa illa thurriyyatun min qawmihi AAala khawfin min firAAawna wamalaihim an yaftinahum wainna firAAawna laAAalin fee alardi wainnahu lamina almusrifeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 84, "And Moses said, ""O my people, if you have believed in Allah, then rely upon Him, if you should be Muslims", "وَقَالَ مُوسَىٰ يَٰقَوۡمِ إِن كُنتُمۡ ءَامَنتُم بِٱللَّهِ فَعَلَيۡهِ تَوَكَّلُوٓاْ إِن كُنتُم مُّسۡلِمِينَ", "Waqala moosa ya qawmi in kuntum amantum biAllahi faAAalayhi tawakkaloo in kuntum muslimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 85, "So they said, ""Upon Allah do we rely. Our Lord, make us not [objects of] trial for the wrongdoing people", "فَقَالُواْ عَلَى ٱللَّهِ تَوَكَّلۡنَا رَبَّنَا لَا تَجۡعَلۡنَا فِتۡنَةٗ لِّلۡقَوۡمِ ٱلظَّـٰلِمِينَ", "Faqaloo AAala Allahi tawakkalna rabbana la tajAAalna fitnatan lilqawmi alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 86, "And save us by Your mercy from the disbelieving people", "وَنَجِّنَا بِرَحۡمَتِكَ مِنَ ٱلۡقَوۡمِ ٱلۡكَٰفِرِينَ", "Wanajjina birahmatika mina alqawmi alkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 87, "And We inspired to Moses and his brother, ""Settle your people in Egypt in houses and make your houses [facing the] qiblah and establish prayer and give good tidings to the believers", "وَأَوۡحَيۡنَآ إِلَىٰ مُوسَىٰ وَأَخِيهِ أَن تَبَوَّءَا لِقَوۡمِكُمَا بِمِصۡرَ بُيُوتٗا وَٱجۡعَلُواْ بُيُوتَكُمۡ قِبۡلَةٗ وَأَقِيمُواْ ٱلصَّلَوٰةَۗ وَبَشِّرِ ٱلۡمُؤۡمِنِينَ", "Waawhayna ila moosa waakheehi an tabawwaa liqawmikuma bimisra buyootan waijAAaloo buyootakum qiblatan waaqeemoo alssalata wabashshiri almumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 88, "And Moses said, ""Our Lord, indeed You have given Pharaoh and his establishment splendor and wealth in the worldly life, our Lord, that they may lead [men] astray from Your way. Our Lord, obliterate their wealth and harden their hearts so that they will not believe until they see the painful punishment", "وَقَالَ مُوسَىٰ رَبَّنَآ إِنَّكَ ءَاتَيۡتَ فِرۡعَوۡنَ وَمَلَأَهُۥ زِينَةٗ وَأَمۡوَٰلٗا فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا رَبَّنَا لِيُضِلُّواْ عَن سَبِيلِكَۖ رَبَّنَا ٱطۡمِسۡ عَلَىٰٓ أَمۡوَٰلِهِمۡ وَٱشۡدُدۡ عَلَىٰ قُلُوبِهِمۡ فَلَا يُؤۡمِنُواْ حَتَّىٰ يَرَوُاْ ٱلۡعَذَابَ ٱلۡأَلِيمَ", "Waqala moosa rabbana innaka atayta firAAawna wamalaahu zeenatan waamwalan fee alhayati alddunya rabbana liyudilloo AAan sabeelika rabbana itmis AAala amwalihim waoshdud AAala quloobihim fala yuminoo hatta yarawoo alAAathaba alaleema");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 89, "[Allah] said, ""Your supplication has been answered."" So remain on a right course and follow not the way of those who do not know", "قَالَ قَدۡ أُجِيبَت دَّعۡوَتُكُمَا فَٱسۡتَقِيمَا وَلَا تَتَّبِعَآنِّ سَبِيلَ ٱلَّذِينَ لَا يَعۡلَمُونَ", "Qala qad ojeebat daAAwatukuma faistaqeema wala tattabiAAanni sabeela allatheena la yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 90, "And We took the Children of Israel across the sea, and Pharaoh and his soldiers pursued them in tyranny and enmity until, when drowning overtook him, he said, ""I believe that there is no deity except that in whom the Children of Israel believe, and I am of the Muslims", "۞وَجَٰوَزۡنَا بِبَنِيٓ إِسۡرَـٰٓءِيلَ ٱلۡبَحۡرَ فَأَتۡبَعَهُمۡ فِرۡعَوۡنُ وَجُنُودُهُۥ بَغۡيٗا وَعَدۡوًاۖ حَتَّىٰٓ إِذَآ أَدۡرَكَهُ ٱلۡغَرَقُ قَالَ ءَامَنتُ أَنَّهُۥ لَآ إِلَٰهَ إِلَّا ٱلَّذِيٓ ءَامَنَتۡ بِهِۦ بَنُوٓاْ إِسۡرَـٰٓءِيلَ وَأَنَا۠ مِنَ ٱلۡمُسۡلِمِينَ", "Wajawazna bibanee israeela albahra faatbaAAahum firAAawnu wajunooduhu baghyan waAAadwan hatta itha adrakahu algharaqu qala amantu annahu la ilaha illa allathee amanat bihi banoo israeela waana mina almuslimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 91, "Now? And you had disobeyed [Him] before and were of the corrupters", "ءَآلۡـَٰٔنَ وَقَدۡ عَصَيۡتَ قَبۡلُ وَكُنتَ مِنَ ٱلۡمُفۡسِدِينَ", "Alana waqad AAasayta qablu wakunta mina almufsideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 92, "So today We will save you in body that you may be to those who succeed you a sign. And indeed, many among the people, of Our signs, are heedless", "فَٱلۡيَوۡمَ نُنَجِّيكَ بِبَدَنِكَ لِتَكُونَ لِمَنۡ خَلۡفَكَ ءَايَةٗۚ وَإِنَّ كَثِيرٗا مِّنَ ٱلنَّاسِ عَنۡ ءَايَٰتِنَا لَغَٰفِلُونَ", "Faalyawma nunajjeeka bibadanika litakoona liman khalfaka ayatan wainna katheeran mina alnnasi AAan ayatina laghafiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 93, "And We had certainty settled the Children of Israel in an agreeable settlement and provided them with good things. And they did not differ until [after] knowledge had come to them. Indeed, your Lord will judge between them on the Day of Resurrection concerning that over which they used to differ", "وَلَقَدۡ بَوَّأۡنَا بَنِيٓ إِسۡرَـٰٓءِيلَ مُبَوَّأَ صِدۡقٖ وَرَزَقۡنَٰهُم مِّنَ ٱلطَّيِّبَٰتِ فَمَا ٱخۡتَلَفُواْ حَتَّىٰ جَآءَهُمُ ٱلۡعِلۡمُۚ إِنَّ رَبَّكَ يَقۡضِي بَيۡنَهُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ فِيمَا كَانُواْ فِيهِ يَخۡتَلِفُونَ", "Walaqad bawwana banee israeela mubawwaa sidqin warazaqnahum mina alttayyibati fama ikhtalafoo hatta jaahumu alAAilmu inna rabbaka yaqdee baynahum yawma alqiyamati feema kanoo feehi yakhtalifoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 94, "So if you are in doubt, [O Muhammad], about that which We have revealed to you, then ask those who have been reading the Scripture before you. The truth has certainly come to you from your Lord, so never be among the doubters", "فَإِن كُنتَ فِي شَكّٖ مِّمَّآ أَنزَلۡنَآ إِلَيۡكَ فَسۡـَٔلِ ٱلَّذِينَ يَقۡرَءُونَ ٱلۡكِتَٰبَ مِن قَبۡلِكَۚ لَقَدۡ جَآءَكَ ٱلۡحَقُّ مِن رَّبِّكَ فَلَا تَكُونَنَّ مِنَ ٱلۡمُمۡتَرِينَ", "Fain kunta fee shakkin mimma anzalnna ilayka faisali allatheena yaqraoona alkitaba min qablika laqad jaaka alhaqqu min rabbika fala takoonanna mina almumtareena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 95, "And never be of those who deny the signs of Allah and [thus] be among the losers", "وَلَا تَكُونَنَّ مِنَ ٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِ ٱللَّهِ فَتَكُونَ مِنَ ٱلۡخَٰسِرِينَ", "Wala takoonanna mina allatheena kaththaboo biayati Allahi fatakoona mina alkhasireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 96, "Indeed, those upon whom the word of your Lord has come into effect will not believe", "إِنَّ ٱلَّذِينَ حَقَّتۡ عَلَيۡهِمۡ كَلِمَتُ رَبِّكَ لَا يُؤۡمِنُونَ", "Inna allatheena haqqat AAalayhim kalimatu rabbika la yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 97, "Even if every sign should come to them, until they see the painful punishment", "وَلَوۡ جَآءَتۡهُمۡ كُلُّ ءَايَةٍ حَتَّىٰ يَرَوُاْ ٱلۡعَذَابَ ٱلۡأَلِيمَ", "Walaw jaathum kullu ayatin hatta yarawoo alAAathaba alaleema");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 98, "Then has there not been a [single] city that believed so its faith benefited it except the people of Jonah? When they believed, We removed from them the punishment of disgrace in worldly life and gave them enjoyment for a time", "فَلَوۡلَا كَانَتۡ قَرۡيَةٌ ءَامَنَتۡ فَنَفَعَهَآ إِيمَٰنُهَآ إِلَّا قَوۡمَ يُونُسَ لَمَّآ ءَامَنُواْ كَشَفۡنَا عَنۡهُمۡ عَذَابَ ٱلۡخِزۡيِ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَمَتَّعۡنَٰهُمۡ إِلَىٰ حِينٖ", "Falawla kanat qaryatun amanat fanafaAAaha eemanuha illa qawma yoonusa lamma amanoo kashafna AAanhum AAathaba alkhizyi fee alhayati alddunya wamattaAAnahum ila heenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 99, "And had your Lord willed, those on earth would have believed - all of them entirely. Then, [O Muhammad], would you compel the people in order that they become believers", "وَلَوۡ شَآءَ رَبُّكَ لَأٓمَنَ مَن فِي ٱلۡأَرۡضِ كُلُّهُمۡ جَمِيعًاۚ أَفَأَنتَ تُكۡرِهُ ٱلنَّاسَ حَتَّىٰ يَكُونُواْ مُؤۡمِنِينَ", "Walaw shaa rabbuka laamana man fee alardi kulluhum jameeAAan afaanta tukrihu alnnasa hatta yakoonoo mumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 100, "And it is not for a soul to believe except by permission of Allah, and He will place defilement upon those who will not use reason", "وَمَا كَانَ لِنَفۡسٍ أَن تُؤۡمِنَ إِلَّا بِإِذۡنِ ٱللَّهِۚ وَيَجۡعَلُ ٱلرِّجۡسَ عَلَى ٱلَّذِينَ لَا يَعۡقِلُونَ", "Wama kana linafsin an tumina illa biithni Allahi wayajAAalu alrrijsa AAala allatheena la yaAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 101, "Say, ""Observe what is in the heavens and earth."" But of no avail will be signs or warners to a people who do not believe", "قُلِ ٱنظُرُواْ مَاذَا فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَمَا تُغۡنِي ٱلۡأٓيَٰتُ وَٱلنُّذُرُ عَن قَوۡمٖ لَّا يُؤۡمِنُونَ", "Quli onthuroo matha fee alssamawati waalardi wama tughnee alayatu waalnnuthuru AAan qawmin la yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 102, "So do they wait except for like [what occurred in] the days of those who passed on before them? Say, ""Then wait; indeed, I am with you among those who wait", "فَهَلۡ يَنتَظِرُونَ إِلَّا مِثۡلَ أَيَّامِ ٱلَّذِينَ خَلَوۡاْ مِن قَبۡلِهِمۡۚ قُلۡ فَٱنتَظِرُوٓاْ إِنِّي مَعَكُم مِّنَ ٱلۡمُنتَظِرِينَ", "Fahal yantathiroona illa mithla ayyami allatheena khalaw min qablihim qul faintathiroo innee maAAakum mina almuntathireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 103, "Then We will save our messengers and those who have believed. Thus, it is an obligation upon Us that We save the believers", "ثُمَّ نُنَجِّي رُسُلَنَا وَٱلَّذِينَ ءَامَنُواْۚ كَذَٰلِكَ حَقًّا عَلَيۡنَا نُنجِ ٱلۡمُؤۡمِنِينَ", "Thumma nunajjee rusulana waallatheena amanoo kathalika haqqan AAalayna nunjee almumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 104, "Say, [O Muhammad], ""O people, if you are in doubt as to my religion - then I do not worship those which you worship besides Allah; but I worship Allah, who causes your death. And I have been commanded to be of the believers", "قُلۡ يَـٰٓأَيُّهَا ٱلنَّاسُ إِن كُنتُمۡ فِي شَكّٖ مِّن دِينِي فَلَآ أَعۡبُدُ ٱلَّذِينَ تَعۡبُدُونَ مِن دُونِ ٱللَّهِ وَلَٰكِنۡ أَعۡبُدُ ٱللَّهَ ٱلَّذِي يَتَوَفَّىٰكُمۡۖ وَأُمِرۡتُ أَنۡ أَكُونَ مِنَ ٱلۡمُؤۡمِنِينَ", "Qul ya ayyuha alnnasu in kuntum fee shakkin min deenee fala aAAbudu allatheena taAAbudoona min dooni Allahi walakin aAAbudu Allaha allathee yatawaffakum waomirtu an akoona mina almumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 105, "And [commanded], 'Direct your face toward the religion, inclining to truth, and never be of those who associate others with Allah", "وَأَنۡ أَقِمۡ وَجۡهَكَ لِلدِّينِ حَنِيفٗا وَلَا تَكُونَنَّ مِنَ ٱلۡمُشۡرِكِينَ", "Waan aqim wajhaka lilddeeni haneefan wala takoonanna mina almushrikeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 106, "And do not invoke besides Allah that which neither benefits you nor harms you, for if you did, then indeed you would be of the wrongdoers", "وَلَا تَدۡعُ مِن دُونِ ٱللَّهِ مَا لَا يَنفَعُكَ وَلَا يَضُرُّكَۖ فَإِن فَعَلۡتَ فَإِنَّكَ إِذٗا مِّنَ ٱلظَّـٰلِمِينَ", "Wala tadAAu min dooni Allahi ma la yanfaAAuka wala yadurruka fain faAAalta fainnaka ithan mina alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 107, "And if Allah should touch you with adversity, there is no remover of it except Him; and if He intends for you good, then there is no repeller of His bounty. He causes it to reach whom He wills of His servants. And He is the Forgiving, the Merciful", "وَإِن يَمۡسَسۡكَ ٱللَّهُ بِضُرّٖ فَلَا كَاشِفَ لَهُۥٓ إِلَّا هُوَۖ وَإِن يُرِدۡكَ بِخَيۡرٖ فَلَا رَآدَّ لِفَضۡلِهِۦۚ يُصِيبُ بِهِۦ مَن يَشَآءُ مِنۡ عِبَادِهِۦۚ وَهُوَ ٱلۡغَفُورُ ٱلرَّحِيمُ", "Wain yamsaska Allahu bidurrin fala kashifa lahu illa huwa wain yuridka bikhayrin fala radda lifadlihi yuseebu bihi man yashao min AAibadihi wahuwa alghafooru alrraheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 108, "Say, ""O mankind, the truth has come to you from your Lord, so whoever is guided is only guided for [the benefit of] his soul, and whoever goes astray only goes astray [in violation] against it. And I am not over you a manager", "قُلۡ يَـٰٓأَيُّهَا ٱلنَّاسُ قَدۡ جَآءَكُمُ ٱلۡحَقُّ مِن رَّبِّكُمۡۖ فَمَنِ ٱهۡتَدَىٰ فَإِنَّمَا يَهۡتَدِي لِنَفۡسِهِۦۖ وَمَن ضَلَّ فَإِنَّمَا يَضِلُّ عَلَيۡهَاۖ وَمَآ أَنَا۠ عَلَيۡكُم بِوَكِيلٖ", "Qul ya ayyuha alnnasu qad jaakumu alhaqqu min rabbikum famani ihtada fainnama yahtadee linafsihi waman dalla fainnama yadillu AAalayha wama ana AAalaykum biwakeelin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (10, 109, "And follow what is revealed to you, [O Muhammad], and be patient until Allah will judge. And He is the best of judges", "وَٱتَّبِعۡ مَا يُوحَىٰٓ إِلَيۡكَ وَٱصۡبِرۡ حَتَّىٰ يَحۡكُمَ ٱللَّهُۚ وَهُوَ خَيۡرُ ٱلۡحَٰكِمِينَ", "WaittabiAA ma yooha ilayka waisbir hatta yahkuma Allahu wahuwa khayru alhakimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 1, "Alif, Lam, Ra. [This is] a Book whose verses are perfected and then presented in detail from [one who is] Wise and Acquainted", "الٓرۚ كِتَٰبٌ أُحۡكِمَتۡ ءَايَٰتُهُۥ ثُمَّ فُصِّلَتۡ مِن لَّدُنۡ حَكِيمٍ خَبِيرٍ", "Aliflamra kitabun ohkimat ayatuhu thumma fussilat min ladun hakeemin khabeerin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 2, "[Through a messenger, saying], ""Do not worship except Allah. Indeed, I am to you from Him a warner and a bringer of good tidings", "أَلَّا تَعۡبُدُوٓاْ إِلَّا ٱللَّهَۚ إِنَّنِي لَكُم مِّنۡهُ نَذِيرٞ وَبَشِيرٞ", "Alla taAAbudoo illa Allaha innanee lakum minhu natheerun wabasheerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 3, "And [saying], ""Seek forgiveness of your Lord and repent to Him, [and] He will let you enjoy a good provision for a specified term and give every doer of favor his favor. But if you turn away, then indeed, I fear for you the punishment of a great Day", "وَأَنِ ٱسۡتَغۡفِرُواْ رَبَّكُمۡ ثُمَّ تُوبُوٓاْ إِلَيۡهِ يُمَتِّعۡكُم مَّتَٰعًا حَسَنًا إِلَىٰٓ أَجَلٖ مُّسَمّٗى وَيُؤۡتِ كُلَّ ذِي فَضۡلٖ فَضۡلَهُۥۖ وَإِن تَوَلَّوۡاْ فَإِنِّيٓ أَخَافُ عَلَيۡكُمۡ عَذَابَ يَوۡمٖ كَبِيرٍ", "Waani istaghfiroo rabbakum thumma tooboo ilayhi yumattiAAkum mataAAan hasanan ila ajalin musamman wayuti kulla thee fadlin fadlahu wain tawallaw fainee akhafu AAalaykum AAathaba yawmin kabeerin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 4, "To Allah is your return, and He is over all things competent", "إِلَى ٱللَّهِ مَرۡجِعُكُمۡۖ وَهُوَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ", "Ila Allahi marjiAAukum wahuwa AAala kulli shayin qadeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 5, "Unquestionably, they the disbelievers turn away their breasts to hide themselves from Him. Unquestionably, [even] when they cover themselves in their clothing, Allah knows what they conceal and what they declare. Indeed, He is Knowing of that within the breasts", "أَلَآ إِنَّهُمۡ يَثۡنُونَ صُدُورَهُمۡ لِيَسۡتَخۡفُواْ مِنۡهُۚ أَلَا حِينَ يَسۡتَغۡشُونَ ثِيَابَهُمۡ يَعۡلَمُ مَا يُسِرُّونَ وَمَا يُعۡلِنُونَۚ إِنَّهُۥ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ", "Ala innahum yathnoona sudoorahum liyastakhfoo minhu ala heena yastaghshoona thiyabahum yaAAlamu ma yusirroona wama yuAAlinoona innahu AAaleemun bithati alssudoori");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 6, "And there is no creature on earth but that upon Allah is its provision, and He knows its place of dwelling and place of storage. All is in a clear register", "۞وَمَا مِن دَآبَّةٖ فِي ٱلۡأَرۡضِ إِلَّا عَلَى ٱللَّهِ رِزۡقُهَا وَيَعۡلَمُ مُسۡتَقَرَّهَا وَمُسۡتَوۡدَعَهَاۚ كُلّٞ فِي كِتَٰبٖ مُّبِينٖ", "Wama min dabbatin fee alardi illa AAala Allahi rizquha wayaAAlamu mustaqarraha wamustawdaAAaha kullun fee kitabin mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 7, "And it is He who created the heavens and the earth in six days - and His Throne had been upon water - that He might test you as to which of you is best in deed. But if you say, ""Indeed, you are resurrected after death,"" those who disbelieve will surely say, ""This is not but obvious magic", "وَهُوَ ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ فِي سِتَّةِ أَيَّامٖ وَكَانَ عَرۡشُهُۥ عَلَى ٱلۡمَآءِ لِيَبۡلُوَكُمۡ أَيُّكُمۡ أَحۡسَنُ عَمَلٗاۗ وَلَئِن قُلۡتَ إِنَّكُم مَّبۡعُوثُونَ مِنۢ بَعۡدِ ٱلۡمَوۡتِ لَيَقُولَنَّ ٱلَّذِينَ كَفَرُوٓاْ إِنۡ هَٰذَآ إِلَّا سِحۡرٞ مُّبِينٞ", "Wahuwa allathee khalaqa alssamawati waalarda fee sittati ayyamin wakana AAarshuhu AAala almai liyabluwakum ayyukum ahsanu AAamalan walain qulta innakum mabAAoothoona min baAAdi almawti layaqoolanna allatheena kafaroo in hatha illa sihrun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 8, "And if We hold back from them the punishment for a limited time, they will surely say, ""What detains it?"" Unquestionably, on the Day it comes to them, it will not be averted from them, and they will be enveloped by what they used to ridicule", "وَلَئِنۡ أَخَّرۡنَا عَنۡهُمُ ٱلۡعَذَابَ إِلَىٰٓ أُمَّةٖ مَّعۡدُودَةٖ لَّيَقُولُنَّ مَا يَحۡبِسُهُۥٓۗ أَلَا يَوۡمَ يَأۡتِيهِمۡ لَيۡسَ مَصۡرُوفًا عَنۡهُمۡ وَحَاقَ بِهِم مَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ", "Walain akhkharna AAanhumu alAAathaba ila ommatin maAAdoodatin layaqoolunna ma yahbisuhu ala yawma yateehim laysa masroofan AAanhum wahaqa bihim ma kanoo bihi yastahzioona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 9, "And if We give man a taste of mercy from Us and then We withdraw it from him, indeed, he is despairing and ungrateful", "وَلَئِنۡ أَذَقۡنَا ٱلۡإِنسَٰنَ مِنَّا رَحۡمَةٗ ثُمَّ نَزَعۡنَٰهَا مِنۡهُ إِنَّهُۥ لَيَـُٔوسٞ كَفُورٞ", "Walain athaqna alinsana minna rahmatan thumma nazaAAnaha minhu innahu layaoosun kafoorun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 10, "But if We give him a taste of favor after hardship has touched him, he will surely say, ""Bad times have left me."" Indeed, he is exultant and boastful", "وَلَئِنۡ أَذَقۡنَٰهُ نَعۡمَآءَ بَعۡدَ ضَرَّآءَ مَسَّتۡهُ لَيَقُولَنَّ ذَهَبَ ٱلسَّيِّـَٔاتُ عَنِّيٓۚ إِنَّهُۥ لَفَرِحٞ فَخُورٌ", "Walain athaqnahu naAAmaa baAAda darraa massathu layaqoolanna thahaba alssayyiatu AAannee innahu lafarihun fakhoorun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 11, "Except for those who are patient and do righteous deeds; those will have forgiveness and great reward", "إِلَّا ٱلَّذِينَ صَبَرُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ أُوْلَـٰٓئِكَ لَهُم مَّغۡفِرَةٞ وَأَجۡرٞ كَبِيرٞ", "Illa allatheena sabaroo waAAamiloo alssalihati olaika lahum maghfiratun waajrun kabeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 12, "Then would you possibly leave [out] some of what is revealed to you, or is your breast constrained by it because they say, ""Why has there not been sent down to him a treasure or come with him an angel?"" But you are only a warner. And Allah is Disposer of all things", "فَلَعَلَّكَ تَارِكُۢ بَعۡضَ مَا يُوحَىٰٓ إِلَيۡكَ وَضَآئِقُۢ بِهِۦ صَدۡرُكَ أَن يَقُولُواْ لَوۡلَآ أُنزِلَ عَلَيۡهِ كَنزٌ أَوۡ جَآءَ مَعَهُۥ مَلَكٌۚ إِنَّمَآ أَنتَ نَذِيرٞۚ وَٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ وَكِيلٌ", "FalaAAallaka tarikun baAAda ma yooha ilayka wadaiqun bihi sadruka an yaqooloo lawla onzila AAalayhi kanzun aw jaa maAAahu malakun innama anta natheerun waAllahu AAala kulli shayin wakeelun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 13, "Or do they say, ""He invented it""? Say, ""Then bring ten surahs like it that have been invented and call upon [for assistance] whomever you can besides Allah, if you should be truthful", "أَمۡ يَقُولُونَ ٱفۡتَرَىٰهُۖ قُلۡ فَأۡتُواْ بِعَشۡرِ سُوَرٖ مِّثۡلِهِۦ مُفۡتَرَيَٰتٖ وَٱدۡعُواْ مَنِ ٱسۡتَطَعۡتُم مِّن دُونِ ٱللَّهِ إِن كُنتُمۡ صَٰدِقِينَ", "Am yaqooloona iftarahu qul fatoo biAAashri suwarin mithlihi muftarayatin waodAAoo mani istataAAtum min dooni Allahi in kuntum sadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 14, "And if they do not respond to you - then know that the Qur'an was revealed with the knowledge of Allah and that there is no deity except Him. Then, would you [not] be Muslims", "فَإِلَّمۡ يَسۡتَجِيبُواْ لَكُمۡ فَٱعۡلَمُوٓاْ أَنَّمَآ أُنزِلَ بِعِلۡمِ ٱللَّهِ وَأَن لَّآ إِلَٰهَ إِلَّا هُوَۖ فَهَلۡ أَنتُم مُّسۡلِمُونَ", "Fai llam yastajeeboo lakum faiAAlamoo annama onzila biAAilmi Allahi waan la ilaha illa huwa fahal antum muslimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 15, "Whoever desires the life of this world and its adornments - We fully repay them for their deeds therein, and they therein will not be deprived", "مَن كَانَ يُرِيدُ ٱلۡحَيَوٰةَ ٱلدُّنۡيَا وَزِينَتَهَا نُوَفِّ إِلَيۡهِمۡ أَعۡمَٰلَهُمۡ فِيهَا وَهُمۡ فِيهَا لَا يُبۡخَسُونَ", "Man kana yureedu alhayata alddunya wazeenataha nuwaffi ilayhim aAAmalahum feeha wahum feeha la yubkhasoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 16, "Those are the ones for whom there is not in the Hereafter but the Fire. And lost is what they did therein, and worthless is what they used to do", "أُوْلَـٰٓئِكَ ٱلَّذِينَ لَيۡسَ لَهُمۡ فِي ٱلۡأٓخِرَةِ إِلَّا ٱلنَّارُۖ وَحَبِطَ مَا صَنَعُواْ فِيهَا وَبَٰطِلٞ مَّا كَانُواْ يَعۡمَلُونَ", "Olaika allatheena laysa lahum fee alakhirati illa alnnaru wahabita ma sanaAAoo feeha wabatilun ma kanoo yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 17, "So is one who [stands] upon a clear evidence from his Lord [like the aforementioned]? And a witness from Him follows it, and before it was the Scripture of Moses to lead and as mercy. Those [believers in the former revelations] believe in the Qur'an. But whoever disbelieves in it from the [various] factions - the Fire is his promised destination. So be not in doubt about it. Indeed, it is the truth from your Lord, but most of the people do not believe", "أَفَمَن كَانَ عَلَىٰ بَيِّنَةٖ مِّن رَّبِّهِۦ وَيَتۡلُوهُ شَاهِدٞ مِّنۡهُ وَمِن قَبۡلِهِۦ كِتَٰبُ مُوسَىٰٓ إِمَامٗا وَرَحۡمَةًۚ أُوْلَـٰٓئِكَ يُؤۡمِنُونَ بِهِۦۚ وَمَن يَكۡفُرۡ بِهِۦ مِنَ ٱلۡأَحۡزَابِ فَٱلنَّارُ مَوۡعِدُهُۥۚ فَلَا تَكُ فِي مِرۡيَةٖ مِّنۡهُۚ إِنَّهُ ٱلۡحَقُّ مِن رَّبِّكَ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يُؤۡمِنُونَ", "Afaman kana AAala bayyinatin min rabbihi wayatloohu shahidun minhu wamin qablihi kitabu moosa imaman warahmatan olaika yuminoona bihi waman yakfur bihi mina alahzabi faalnnaru mawAAiduhu fala taku fee miryatin minhu innahu alhaqqu min rabbika walakinna akthara alnnasi la yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 18, "And who is more unjust than he who invents a lie about Allah? Those will be presented before their Lord, and the witnesses will say, ""These are the ones who lied against their Lord."" Unquestionably, the curse of Allah is upon the wrongdoers", "وَمَنۡ أَظۡلَمُ مِمَّنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبًاۚ أُوْلَـٰٓئِكَ يُعۡرَضُونَ عَلَىٰ رَبِّهِمۡ وَيَقُولُ ٱلۡأَشۡهَٰدُ هَـٰٓؤُلَآءِ ٱلَّذِينَ كَذَبُواْ عَلَىٰ رَبِّهِمۡۚ أَلَا لَعۡنَةُ ٱللَّهِ عَلَى ٱلظَّـٰلِمِينَ", "Waman athlamu mimmani iftara AAala Allahi kathiban olaika yuAAradoona AAala rabbihim wayaqoolu alashhadu haolai allatheena kathaboo AAala rabbihim ala laAAnatu Allahi AAala alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 19, "Who averted [people] from the way of Allah and sought to make it [seem] deviant while they, concerning the Hereafter, were disbelievers", "ٱلَّذِينَ يَصُدُّونَ عَن سَبِيلِ ٱللَّهِ وَيَبۡغُونَهَا عِوَجٗا وَهُم بِٱلۡأٓخِرَةِ هُمۡ كَٰفِرُونَ", "Allatheena yasuddoona AAan sabeeli Allahi wayabghoonaha AAiwajan wahum bialakhirati hum kafiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 20, "Those were not causing failure [to Allah] on earth, nor did they have besides Allah any protectors. For them the punishment will be multiplied. They were not able to hear, nor did they see", "أُوْلَـٰٓئِكَ لَمۡ يَكُونُواْ مُعۡجِزِينَ فِي ٱلۡأَرۡضِ وَمَا كَانَ لَهُم مِّن دُونِ ٱللَّهِ مِنۡ أَوۡلِيَآءَۘ يُضَٰعَفُ لَهُمُ ٱلۡعَذَابُۚ مَا كَانُواْ يَسۡتَطِيعُونَ ٱلسَّمۡعَ وَمَا كَانُواْ يُبۡصِرُونَ", "Olaika lam yakoonoo muAAjizeena fee alardi wama kana lahum min dooni Allahi min awliyaa yudaAAafu lahumu alAAathabu ma kanoo yastateeAAoona alssamAAa wama kanoo yubsiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 21, "Those are the ones who will have lost themselves, and lost from them is what they used to invent", "أُوْلَـٰٓئِكَ ٱلَّذِينَ خَسِرُوٓاْ أَنفُسَهُمۡ وَضَلَّ عَنۡهُم مَّا كَانُواْ يَفۡتَرُونَ", "Olaika allatheena khasiroo anfusahum wadalla AAanhum ma kanoo yaftaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 22, "Assuredly, it is they in the Hereafter who will be the greatest losers", "لَا جَرَمَ أَنَّهُمۡ فِي ٱلۡأٓخِرَةِ هُمُ ٱلۡأَخۡسَرُونَ", "La jarama annahum fee alakhirati humu alakhsaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 23, "Indeed, they who have believed and done righteous deeds and humbled themselves to their Lord - those are the companions of Paradise; they will abide eternally therein", "إِنَّ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ وَأَخۡبَتُوٓاْ إِلَىٰ رَبِّهِمۡ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلۡجَنَّةِۖ هُمۡ فِيهَا خَٰلِدُونَ", "Inna allatheena amanoo waAAamiloo alsalihati waakhbatoo ila rabbihim olaika ashabu aljannati hum feeha khalidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 24, "The example of the two parties is like the blind and deaf, and the seeing and hearing. Are they equal in comparison? Then, will you not remember", "۞مَثَلُ ٱلۡفَرِيقَيۡنِ كَٱلۡأَعۡمَىٰ وَٱلۡأَصَمِّ وَٱلۡبَصِيرِ وَٱلسَّمِيعِۚ هَلۡ يَسۡتَوِيَانِ مَثَلًاۚ أَفَلَا تَذَكَّرُونَ", "Mathalu alfareeqayni kaalaAAma waalasammi waalbaseeri waalssameeAAi hal yastawiyani mathalan afala tathakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 25, "And We had certainly sent Noah to his people, [saying], ""Indeed, I am to you a clear warner", "وَلَقَدۡ أَرۡسَلۡنَا نُوحًا إِلَىٰ قَوۡمِهِۦٓ إِنِّي لَكُمۡ نَذِيرٞ مُّبِينٌ", "Walaqad arsalna noohan ila qawmihi innee lakum natheerun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 26, "That you not worship except Allah. Indeed, I fear for you the punishment of a painful day", "أَن لَّا تَعۡبُدُوٓاْ إِلَّا ٱللَّهَۖ إِنِّيٓ أَخَافُ عَلَيۡكُمۡ عَذَابَ يَوۡمٍ أَلِيمٖ", "An la taAAbudoo illa Allaha innee akhafu AAalaykum AAathaba yawmin aleemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 27, "So the eminent among those who disbelieved from his people said, ""We do not see you but as a man like ourselves, and we do not see you followed except by those who are the lowest of us [and] at first suggestion. And we do not see in you over us any merit; rather, we think you are liars", "فَقَالَ ٱلۡمَلَأُ ٱلَّذِينَ كَفَرُواْ مِن قَوۡمِهِۦ مَا نَرَىٰكَ إِلَّا بَشَرٗا مِّثۡلَنَا وَمَا نَرَىٰكَ ٱتَّبَعَكَ إِلَّا ٱلَّذِينَ هُمۡ أَرَاذِلُنَا بَادِيَ ٱلرَّأۡيِ وَمَا نَرَىٰ لَكُمۡ عَلَيۡنَا مِن فَضۡلِۭ بَلۡ نَظُنُّكُمۡ كَٰذِبِينَ", "Faqala almalao allatheena kafaroo min qawmihi ma naraka illa basharan mithlana wama naraka ittabaAAaka illa allatheena hum arathiluna badiya alrrayi wama nara lakum AAalayna min fadlin bal nathunnukum kathibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 28, "He said, ""O my people have you considered: if I should be upon clear evidence from my Lord while He has given me mercy from Himself but it has been made unapparent to you, should we force it upon you while you are averse to it", "قَالَ يَٰقَوۡمِ أَرَءَيۡتُمۡ إِن كُنتُ عَلَىٰ بَيِّنَةٖ مِّن رَّبِّي وَءَاتَىٰنِي رَحۡمَةٗ مِّنۡ عِندِهِۦ فَعُمِّيَتۡ عَلَيۡكُمۡ أَنُلۡزِمُكُمُوهَا وَأَنتُمۡ لَهَا كَٰرِهُونَ", "Qala ya qawmi araaytum in kuntu AAala bayyinatin min rabbee waatanee rahmatan min AAindihi faAAummiyat AAalaykum anulzimukumooha waantum laha karihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 29, "And O my people, I ask not of you for it any wealth. My reward is not but from Allah. And I am not one to drive away those who have believed. Indeed, they will meet their Lord, but I see that you are a people behaving ignorantly", "وَيَٰقَوۡمِ لَآ أَسۡـَٔلُكُمۡ عَلَيۡهِ مَالًاۖ إِنۡ أَجۡرِيَ إِلَّا عَلَى ٱللَّهِۚ وَمَآ أَنَا۠ بِطَارِدِ ٱلَّذِينَ ءَامَنُوٓاْۚ إِنَّهُم مُّلَٰقُواْ رَبِّهِمۡ وَلَٰكِنِّيٓ أَرَىٰكُمۡ قَوۡمٗا تَجۡهَلُونَ", "Waya qawmi la asalukum AAalayhi malan in ajriya illa AAala Allahi wama ana bitaridi allatheena amanoo innahum mulaqoo rabbihim walakinnee arakum qawman tajhaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 30, "And O my people, who would protect me from Allah if I drove them away? Then will you not be reminded", "وَيَٰقَوۡمِ مَن يَنصُرُنِي مِنَ ٱللَّهِ إِن طَرَدتُّهُمۡۚ أَفَلَا تَذَكَّرُونَ", "Waya qawmi man yansurunee mina Allahi in taradtuhum afala tathakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 31, "And I do not tell you that I have the depositories [containing the provision] of Allah or that I know the unseen, nor do I tell you that I am an angel, nor do I say of those upon whom your eyes look down that Allah will never grant them any good. Allah is most knowing of what is within their souls. Indeed, I would then be among the wrongdoers", "وَلَآ أَقُولُ لَكُمۡ عِندِي خَزَآئِنُ ٱللَّهِ وَلَآ أَعۡلَمُ ٱلۡغَيۡبَ وَلَآ أَقُولُ إِنِّي مَلَكٞ وَلَآ أَقُولُ لِلَّذِينَ تَزۡدَرِيٓ أَعۡيُنُكُمۡ لَن يُؤۡتِيَهُمُ ٱللَّهُ خَيۡرًاۖ ٱللَّهُ أَعۡلَمُ بِمَا فِيٓ أَنفُسِهِمۡ إِنِّيٓ إِذٗا لَّمِنَ ٱلظَّـٰلِمِينَ", "Wala aqoolu lakum AAindee khazainu Allahi wala aAAlamu alghayba wala aqoolu innee malakun wala aqoolu lillatheena tazdaree aAAyunukum lan yutiyahumu Allahu khayran Allahu aAAlamu bima fee anfusihim innee ithan lamina alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 32, "They said, ""O Noah, you have disputed us and been frequent in dispute of us. So bring us what you threaten us, if you should be of the truthful", "قَالُواْ يَٰنُوحُ قَدۡ جَٰدَلۡتَنَا فَأَكۡثَرۡتَ جِدَٰلَنَا فَأۡتِنَا بِمَا تَعِدُنَآ إِن كُنتَ مِنَ ٱلصَّـٰدِقِينَ", "Qaloo ya noohu qad jadaltana faaktharta jidalana fatina bima taAAiduna in kunta mina alssadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 33, "He said, ""Allah will only bring it to you if He wills, and you will not cause [Him] failure", "قَالَ إِنَّمَا يَأۡتِيكُم بِهِ ٱللَّهُ إِن شَآءَ وَمَآ أَنتُم بِمُعۡجِزِينَ", "Qala innama yateekum bihi Allahu in shaa wama antum bimuAAjizeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 34, "And my advice will not benefit you - although I wished to advise you - If Allah should intend to put you in error. He is your Lord, and to Him you will be returned", "وَلَا يَنفَعُكُمۡ نُصۡحِيٓ إِنۡ أَرَدتُّ أَنۡ أَنصَحَ لَكُمۡ إِن كَانَ ٱللَّهُ يُرِيدُ أَن يُغۡوِيَكُمۡۚ هُوَ رَبُّكُمۡ وَإِلَيۡهِ تُرۡجَعُونَ", "Wala yanfaAAukum nushee in aradtu an ansaha lakum in kana Allahu yureedu an yughwiyakum huwa rabbukum wailayhi turjaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 35, "Or do they say [about Prophet Muhammad], ""He invented it""? Say, ""If I have invented it, then upon me is [the consequence of] my crime; but I am innocent of what [crimes] you commit", "أَمۡ يَقُولُونَ ٱفۡتَرَىٰهُۖ قُلۡ إِنِ ٱفۡتَرَيۡتُهُۥ فَعَلَيَّ إِجۡرَامِي وَأَنَا۠ بَرِيٓءٞ مِّمَّا تُجۡرِمُونَ", "Am yaqooloona iftarahu qul ini iftaraytuhu faAAalayya ijramee waana bareeon mimma tujrimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 36, "And it was revealed to Noah that, ""No one will believe from your people except those who have already believed, so do not be distressed by what they have been doing", "وَأُوحِيَ إِلَىٰ نُوحٍ أَنَّهُۥ لَن يُؤۡمِنَ مِن قَوۡمِكَ إِلَّا مَن قَدۡ ءَامَنَ فَلَا تَبۡتَئِسۡ بِمَا كَانُواْ يَفۡعَلُونَ", "Waoohiya ila noohin annahu lan yumina min qawmika illa man qad amana fala tabtais bima kanoo yafAAaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 37, "And construct the ship under Our observation and Our inspiration and do not address Me concerning those who have wronged; indeed, they are [to be] drowned", "وَٱصۡنَعِ ٱلۡفُلۡكَ بِأَعۡيُنِنَا وَوَحۡيِنَا وَلَا تُخَٰطِبۡنِي فِي ٱلَّذِينَ ظَلَمُوٓاْ إِنَّهُم مُّغۡرَقُونَ", "WaisnaAAi alfulka biaAAyunina wawahyina wala tukhatibnee fee allatheena thalamoo innahum mughraqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 38, "And he constructed the ship, and whenever an assembly of the eminent of his people passed by him, they ridiculed him. He said, ""If you ridicule us, then we will ridicule you just as you ridicule", "وَيَصۡنَعُ ٱلۡفُلۡكَ وَكُلَّمَا مَرَّ عَلَيۡهِ مَلَأٞ مِّن قَوۡمِهِۦ سَخِرُواْ مِنۡهُۚ قَالَ إِن تَسۡخَرُواْ مِنَّا فَإِنَّا نَسۡخَرُ مِنكُمۡ كَمَا تَسۡخَرُونَ", "WayasnaAAu alfulka wakullama marra AAalayhi malaon min qawmihi sakhiroo minhu qala in taskharoo minna fainna naskharu minkum kama taskharoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 39, "And you are going to know who will get a punishment that will disgrace him [on earth] and upon whom will descend an enduring punishment [in the Hereafter]", "فَسَوۡفَ تَعۡلَمُونَ مَن يَأۡتِيهِ عَذَابٞ يُخۡزِيهِ وَيَحِلُّ عَلَيۡهِ عَذَابٞ مُّقِيمٌ", "Fasawfa taAAlamoona man yateehi AAathabun yukhzeehi wayahillu AAalayhi AAathabun muqeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 40, "[So it was], until when Our command came and the oven overflowed, We said, ""Load upon the ship of each [creature] two mates and your family, except those about whom the word has preceded, and [include] whoever has believed."" But none had believed with him, except a few", "حَتَّىٰٓ إِذَا جَآءَ أَمۡرُنَا وَفَارَ ٱلتَّنُّورُ قُلۡنَا ٱحۡمِلۡ فِيهَا مِن كُلّٖ زَوۡجَيۡنِ ٱثۡنَيۡنِ وَأَهۡلَكَ إِلَّا مَن سَبَقَ عَلَيۡهِ ٱلۡقَوۡلُ وَمَنۡ ءَامَنَۚ وَمَآ ءَامَنَ مَعَهُۥٓ إِلَّا قَلِيلٞ", "Hatta itha jaa amruna wafara alttannooru qulna ihmil feeha min kullin zawjayni ithnayni waahlaka illa man sabaqa AAalayhi alqawlu waman amana wama amana maAAahu illa qaleelun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 41, "And [Noah] said, ""Embark therein; in the name of Allah is its course and its anchorage. Indeed, my Lord is Forgiving and Merciful", "۞وَقَالَ ٱرۡكَبُواْ فِيهَا بِسۡمِ ٱللَّهِ مَجۡرٜىٰهَا وَمُرۡسَىٰهَآۚ إِنَّ رَبِّي لَغَفُورٞ رَّحِيمٞ", "Waqala irkaboo feeha bismi Allahi majraha wamursaha inna rabbee laghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 42, "And it sailed with them through waves like mountains, and Noah called to his son who was apart [from them], ""O my son, come aboard with us and be not with the disbelievers", "وَهِيَ تَجۡرِي بِهِمۡ فِي مَوۡجٖ كَٱلۡجِبَالِ وَنَادَىٰ نُوحٌ ٱبۡنَهُۥ وَكَانَ فِي مَعۡزِلٖ يَٰبُنَيَّ ٱرۡكَب مَّعَنَا وَلَا تَكُن مَّعَ ٱلۡكَٰفِرِينَ", "Wahiya tajree bihim fee mawjin kaaljibali wanada noohunu ibnahu wakana fee maAAzilin ya bunayya irkab maAAana wala takun maAAa alkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 43, "[But] he said, ""I will take refuge on a mountain to protect me from the water."" [Noah] said, ""There is no protector today from the decree of Allah, except for whom He gives mercy."" And the waves came between them, and he was among the drowned", "قَالَ سَـَٔاوِيٓ إِلَىٰ جَبَلٖ يَعۡصِمُنِي مِنَ ٱلۡمَآءِۚ قَالَ لَا عَاصِمَ ٱلۡيَوۡمَ مِنۡ أَمۡرِ ٱللَّهِ إِلَّا مَن رَّحِمَۚ وَحَالَ بَيۡنَهُمَا ٱلۡمَوۡجُ فَكَانَ مِنَ ٱلۡمُغۡرَقِينَ", "Qala saawee ila jabalin yaAAsimunee mina almai qala la AAasima alyawma min amri Allahi illa man rahima wahala baynahuma almawju fakana mina almughraqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 44, "And it was said, ""O earth, swallow your water, and O sky, withhold [your rain]."" And the water subsided, and the matter was accomplished, and the ship came to rest on the [mountain of] Judiyy. And it was said, ""Away with the wrongdoing people", "وَقِيلَ يَـٰٓأَرۡضُ ٱبۡلَعِي مَآءَكِ وَيَٰسَمَآءُ أَقۡلِعِي وَغِيضَ ٱلۡمَآءُ وَقُضِيَ ٱلۡأَمۡرُ وَٱسۡتَوَتۡ عَلَى ٱلۡجُودِيِّۖ وَقِيلَ بُعۡدٗا لِّلۡقَوۡمِ ٱلظَّـٰلِمِينَ", "Waqeela ya ardu iblaAAee maaki waya samao aqliAAee wagheeda almao waqudiya alamru waistawat AAala aljoodiyyi waqeela buAAdan lilqawmi alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 45, "And Noah called to his Lord and said, ""My Lord, indeed my son is of my family; and indeed, Your promise is true; and You are the most just of judges", "وَنَادَىٰ نُوحٞ رَّبَّهُۥ فَقَالَ رَبِّ إِنَّ ٱبۡنِي مِنۡ أَهۡلِي وَإِنَّ وَعۡدَكَ ٱلۡحَقُّ وَأَنتَ أَحۡكَمُ ٱلۡحَٰكِمِينَ", "Wanada noohun rabbahu faqala rabbi inna ibnee min ahlee wainna waAAdaka alhaqqu waanta ahkamu alhakimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 46, "He said, ""O Noah, indeed he is not of your family; indeed, he is [one whose] work was other than righteous, so ask Me not for that about which you have no knowledge. Indeed, I advise you, lest you be among the ignorant", "قَالَ يَٰنُوحُ إِنَّهُۥ لَيۡسَ مِنۡ أَهۡلِكَۖ إِنَّهُۥ عَمَلٌ غَيۡرُ صَٰلِحٖۖ فَلَا تَسۡـَٔلۡنِ مَا لَيۡسَ لَكَ بِهِۦ عِلۡمٌۖ إِنِّيٓ أَعِظُكَ أَن تَكُونَ مِنَ ٱلۡجَٰهِلِينَ", "Qala ya noohu innahu laysa min ahlika innahu AAamalun ghayru salihin fala tasalni ma laysa laka bihi AAilmun innee aAAithuka an takoona mina aljahileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 47, "[Noah] said, ""My Lord, I seek refuge in You from asking that of which I have no knowledge. And unless You forgive me and have mercy upon me, I will be among the losers", "قَالَ رَبِّ إِنِّيٓ أَعُوذُ بِكَ أَنۡ أَسۡـَٔلَكَ مَا لَيۡسَ لِي بِهِۦ عِلۡمٞۖ وَإِلَّا تَغۡفِرۡ لِي وَتَرۡحَمۡنِيٓ أَكُن مِّنَ ٱلۡخَٰسِرِينَ", "Qala rabbi innee aAAoothu bika an asalaka ma laysa lee bihi AAilmun wailla taghfir lee watarhamnee akun mina alkhasireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 48, "It was said, ""O Noah, disembark in security from Us and blessings upon you and upon nations [descending] from those with you. But other nations [of them] We will grant enjoyment; then there will touch them from Us a painful punishment", "قِيلَ يَٰنُوحُ ٱهۡبِطۡ بِسَلَٰمٖ مِّنَّا وَبَرَكَٰتٍ عَلَيۡكَ وَعَلَىٰٓ أُمَمٖ مِّمَّن مَّعَكَۚ وَأُمَمٞ سَنُمَتِّعُهُمۡ ثُمَّ يَمَسُّهُم مِّنَّا عَذَابٌ أَلِيمٞ", "Qeela ya noohu ihbit bisalamin minna wabarakatin AAalayka waAAala omamin mimman maAAaka waomamun sanumattiAAuhum thumma yamassuhum minna AAathabun aleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 49, "That is from the news of the unseen which We reveal to you, [O Muhammad]. You knew it not, neither you nor your people, before this. So be patient; indeed, the [best] outcome is for the righteous", "تِلۡكَ مِنۡ أَنۢبَآءِ ٱلۡغَيۡبِ نُوحِيهَآ إِلَيۡكَۖ مَا كُنتَ تَعۡلَمُهَآ أَنتَ وَلَا قَوۡمُكَ مِن قَبۡلِ هَٰذَاۖ فَٱصۡبِرۡۖ إِنَّ ٱلۡعَٰقِبَةَ لِلۡمُتَّقِينَ", "Tilka min anbai alghaybi nooheeha ilayka ma kunta taAAlamuha anta wala qawmuka min qabli hatha faisbir inna alAAaqibata lilmuttaqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 50, "And to 'Aad [We sent] their brother Hud. He said, ""O my people, worship Allah; you have no deity other than Him. You are not but inventors [of falsehood]", "وَإِلَىٰ عَادٍ أَخَاهُمۡ هُودٗاۚ قَالَ يَٰقَوۡمِ ٱعۡبُدُواْ ٱللَّهَ مَا لَكُم مِّنۡ إِلَٰهٍ غَيۡرُهُۥٓۖ إِنۡ أَنتُمۡ إِلَّا مُفۡتَرُونَ", "Waila AAadin akhahum hoodan qala ya qawmi oAAbudoo Allaha ma lakum min ilahin ghayruhu in antum illa muftaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 51, "O my people, I do not ask you for it any reward. My reward is only from the one who created me. Then will you not reason", "يَٰقَوۡمِ لَآ أَسۡـَٔلُكُمۡ عَلَيۡهِ أَجۡرًاۖ إِنۡ أَجۡرِيَ إِلَّا عَلَى ٱلَّذِي فَطَرَنِيٓۚ أَفَلَا تَعۡقِلُونَ", "Ya qawmi la asalukum AAalayhi ajran in ajriya illa AAala allathee fataranee afala taAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 52, "And O my people, ask forgiveness of your Lord and then repent to Him. He will send [rain from] the sky upon you in showers and increase you in strength [added] to your strength. And do not turn away, [being] criminals", "وَيَٰقَوۡمِ ٱسۡتَغۡفِرُواْ رَبَّكُمۡ ثُمَّ تُوبُوٓاْ إِلَيۡهِ يُرۡسِلِ ٱلسَّمَآءَ عَلَيۡكُم مِّدۡرَارٗا وَيَزِدۡكُمۡ قُوَّةً إِلَىٰ قُوَّتِكُمۡ وَلَا تَتَوَلَّوۡاْ مُجۡرِمِينَ", "Waya qawmi istaghfiroo rabbakum thumma tooboo ilayhi yursili alssamaa AAalaykum midraran wayazidkum quwwatan ila quwwatikum wala tatawallaw mujrimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 53, "They said, ""O Hud, you have not brought us clear evidence, and we are not ones to leave our gods on your say-so. Nor are we believers in you", "قَالُواْ يَٰهُودُ مَا جِئۡتَنَا بِبَيِّنَةٖ وَمَا نَحۡنُ بِتَارِكِيٓ ءَالِهَتِنَا عَن قَوۡلِكَ وَمَا نَحۡنُ لَكَ بِمُؤۡمِنِينَ", "Qaloo ya hoodu ma jitana bibayyinatin wama nahnu bitarikee alihatina AAan qawlika wama nahnu laka bimumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 54, "We only say that some of our gods have possessed you with evil."" He said, ""Indeed, I call Allah to witness, and witness [yourselves] that I am free from whatever you associate with Allah", "إِن نَّقُولُ إِلَّا ٱعۡتَرَىٰكَ بَعۡضُ ءَالِهَتِنَا بِسُوٓءٖۗ قَالَ إِنِّيٓ أُشۡهِدُ ٱللَّهَ وَٱشۡهَدُوٓاْ أَنِّي بَرِيٓءٞ مِّمَّا تُشۡرِكُونَ", "In naqoolu illa iAAtaraka baAAdu alihatina bisooin qala innee oshhidu Allaha waishhadoo annee bareeon mimma tushrikoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 55, "Other than Him. So plot against me all together; then do not give me respite", "مِن دُونِهِۦۖ فَكِيدُونِي جَمِيعٗا ثُمَّ لَا تُنظِرُونِ", "Min doonihi fakeedoonee jameeAAan thumma la tunthirooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 56, "Indeed, I have relied upon Allah, my Lord and your Lord. There is no creature but that He holds its forelock. Indeed, my Lord is on a path [that is] straight", "إِنِّي تَوَكَّلۡتُ عَلَى ٱللَّهِ رَبِّي وَرَبِّكُمۚ مَّا مِن دَآبَّةٍ إِلَّا هُوَ ءَاخِذُۢ بِنَاصِيَتِهَآۚ إِنَّ رَبِّي عَلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ", "Innee tawakkaltu AAala Allahi rabbee warabbikum ma min dabbatin illa huwa akhithun binasiyatiha inna rabbee AAala siratin mustaqeemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 57, "But if they turn away, [say], ""I have already conveyed that with which I was sent to you. My Lord will give succession to a people other than you, and you will not harm Him at all. Indeed my Lord is, over all things, Guardian", "فَإِن تَوَلَّوۡاْ فَقَدۡ أَبۡلَغۡتُكُم مَّآ أُرۡسِلۡتُ بِهِۦٓ إِلَيۡكُمۡۚ وَيَسۡتَخۡلِفُ رَبِّي قَوۡمًا غَيۡرَكُمۡ وَلَا تَضُرُّونَهُۥ شَيۡـًٔاۚ إِنَّ رَبِّي عَلَىٰ كُلِّ شَيۡءٍ حَفِيظٞ", "Fain tawallaw faqad ablaghtukum ma orsiltu bihi ilaykum wayastakhlifu rabbee qawman ghayrakum wala tadurroonahu shayan inna rabbee AAala kulli shayin hafeethun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 58, "And when Our command came, We saved Hud and those who believed with him, by mercy from Us; and We saved them from a harsh punishment", "وَلَمَّا جَآءَ أَمۡرُنَا نَجَّيۡنَا هُودٗا وَٱلَّذِينَ ءَامَنُواْ مَعَهُۥ بِرَحۡمَةٖ مِّنَّا وَنَجَّيۡنَٰهُم مِّنۡ عَذَابٍ غَلِيظٖ", "Walamma jaa amruna najjayna hoodan waallatheena amanoo maAAahu birahmatin minna wanajjaynahum min AAathabin ghaleethin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 59, "And that was 'Aad, who rejected the signs of their Lord and disobeyed His messengers and followed the order of every obstinate tyrant", "وَتِلۡكَ عَادٞۖ جَحَدُواْ بِـَٔايَٰتِ رَبِّهِمۡ وَعَصَوۡاْ رُسُلَهُۥ وَٱتَّبَعُوٓاْ أَمۡرَ كُلِّ جَبَّارٍ عَنِيدٖ", "Watilka AAadun jahadoo biayati rabbihim waAAasaw rusulahu waittabaAAoo amra kulli jabbarin AAaneedin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 60, "And they were [therefore] followed in this world with a curse and [as well] on the Day of Resurrection. Unquestionably, 'Aad denied their Lord; then away with 'Aad, the people of Hud", "وَأُتۡبِعُواْ فِي هَٰذِهِ ٱلدُّنۡيَا لَعۡنَةٗ وَيَوۡمَ ٱلۡقِيَٰمَةِۗ أَلَآ إِنَّ عَادٗا كَفَرُواْ رَبَّهُمۡۗ أَلَا بُعۡدٗا لِّعَادٖ قَوۡمِ هُودٖ", "WaotbiAAoo fee hathihi alddunya laAAnatan wayawma alqiyamati ala inna AAadan kafaroo rabbahum ala buAAdan liAAadin qawmi hoodin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 61, "And to Thamud [We sent] their brother Salih. He said, ""O my people, worship Allah; you have no deity other than Him. He has produced you from the earth and settled you in it, so ask forgiveness of Him and then repent to Him. Indeed, my Lord is near and responsive", "۞وَإِلَىٰ ثَمُودَ أَخَاهُمۡ صَٰلِحٗاۚ قَالَ يَٰقَوۡمِ ٱعۡبُدُواْ ٱللَّهَ مَا لَكُم مِّنۡ إِلَٰهٍ غَيۡرُهُۥۖ هُوَ أَنشَأَكُم مِّنَ ٱلۡأَرۡضِ وَٱسۡتَعۡمَرَكُمۡ فِيهَا فَٱسۡتَغۡفِرُوهُ ثُمَّ تُوبُوٓاْ إِلَيۡهِۚ إِنَّ رَبِّي قَرِيبٞ مُّجِيبٞ", "Waila thamooda akhahum salihan qala ya qawmi oAAbudoo Allaha ma lakum min ilahin ghayruhu huwa anshaakum mina alardi waistaAAmarakum feeha faistaghfiroohu thumma tooboo ilayhi inna rabbee qareebun mujeebun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 62, "They said, ""O Salih, you were among us a man of promise before this. Do you forbid us to worship what our fathers worshipped? And indeed we are, about that to which you invite us, in disquieting doubt", "قَالُواْ يَٰصَٰلِحُ قَدۡ كُنتَ فِينَا مَرۡجُوّٗا قَبۡلَ هَٰذَآۖ أَتَنۡهَىٰنَآ أَن نَّعۡبُدَ مَا يَعۡبُدُ ءَابَآؤُنَا وَإِنَّنَا لَفِي شَكّٖ مِّمَّا تَدۡعُونَآ إِلَيۡهِ مُرِيبٖ", "Qaloo ya salihu qad kunta feena marjuwwan qabla hatha atanhana an naAAbuda ma yaAAbudu abaona wainnana lafee shakkin mimma tadAAoona ilayhi mureebun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 63, "He said, ""O my people, have you considered: if I should be upon clear evidence from my Lord and He has given me mercy from Himself, who would protect me from Allah if I disobeyed Him? So you would not increase me except in loss", "قَالَ يَٰقَوۡمِ أَرَءَيۡتُمۡ إِن كُنتُ عَلَىٰ بَيِّنَةٖ مِّن رَّبِّي وَءَاتَىٰنِي مِنۡهُ رَحۡمَةٗ فَمَن يَنصُرُنِي مِنَ ٱللَّهِ إِنۡ عَصَيۡتُهُۥۖ فَمَا تَزِيدُونَنِي غَيۡرَ تَخۡسِيرٖ", "Qala ya qawmi araaytum in kuntu AAala bayyinatin min rabbee waatanee minhu rahmatan faman yansurunee mina Allahi in AAasaytuhu fama tazeedoonanee ghayra takhseerin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 64, "And O my people, this is the she-camel of Allah - [she is] to you a sign. So let her feed upon Allah 's earth and do not touch her with harm, or you will be taken by an impending punishment", "وَيَٰقَوۡمِ هَٰذِهِۦ نَاقَةُ ٱللَّهِ لَكُمۡ ءَايَةٗۖ فَذَرُوهَا تَأۡكُلۡ فِيٓ أَرۡضِ ٱللَّهِۖ وَلَا تَمَسُّوهَا بِسُوٓءٖ فَيَأۡخُذَكُمۡ عَذَابٞ قَرِيبٞ", "Waya qawmi hathihi naqatu Allahi lakum ayatan fatharooha takul fee ardi Allahi wala tamassooha bisooin fayakhuthakum AAathabun qareebun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 65, "But they hamstrung her, so he said, ""Enjoy yourselves in your homes for three days. That is a promise not to be denied", "فَعَقَرُوهَا فَقَالَ تَمَتَّعُواْ فِي دَارِكُمۡ ثَلَٰثَةَ أَيَّامٖۖ ذَٰلِكَ وَعۡدٌ غَيۡرُ مَكۡذُوبٖ", "FaAAaqarooha faqala tamattaAAoo fee darikum thalathata ayyamin thalika waAAdun ghayru makthoobin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 66, "So when Our command came, We saved Salih and those who believed with him, by mercy from Us, and [saved them] from the disgrace of that day. Indeed, it is your Lord who is the Powerful, the Exalted in Might", "فَلَمَّا جَآءَ أَمۡرُنَا نَجَّيۡنَا صَٰلِحٗا وَٱلَّذِينَ ءَامَنُواْ مَعَهُۥ بِرَحۡمَةٖ مِّنَّا وَمِنۡ خِزۡيِ يَوۡمِئِذٍۚ إِنَّ رَبَّكَ هُوَ ٱلۡقَوِيُّ ٱلۡعَزِيزُ", "Falamma jaa amruna najjayna salihan waallatheena amanoo maAAahu birahmatin minna wamin khizyi yawmiithin inna rabbaka huwa alqawiyyu alAAazeezu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 67, "And the shriek seized those who had wronged, and they became within their homes [corpses] fallen prone", "وَأَخَذَ ٱلَّذِينَ ظَلَمُواْ ٱلصَّيۡحَةُ فَأَصۡبَحُواْ فِي دِيَٰرِهِمۡ جَٰثِمِينَ", "Waakhatha allatheena thalamoo alssayhatu faasbahoo fee diyarihim jathimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 68, "As if they had never prospered therein. Unquestionably, Thamud denied their Lord; then, away with Thamud", "كَأَن لَّمۡ يَغۡنَوۡاْ فِيهَآۗ أَلَآ إِنَّ ثَمُودَاْ كَفَرُواْ رَبَّهُمۡۗ أَلَا بُعۡدٗا لِّثَمُودَ", "Kaan lam yaghnaw feeha ala inna thamooda kafaroo rabbahum ala buAAdan lithamooda");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 69, "And certainly did Our messengers come to Abraham with good tidings; they said, ""Peace."" He said, ""Peace,"" and did not delay in bringing [them] a roasted calf", "وَلَقَدۡ جَآءَتۡ رُسُلُنَآ إِبۡرَٰهِيمَ بِٱلۡبُشۡرَىٰ قَالُواْ سَلَٰمٗاۖ قَالَ سَلَٰمٞۖ فَمَا لَبِثَ أَن جَآءَ بِعِجۡلٍ حَنِيذٖ", "Walaqad jaat rusuluna ibraheema bialbushra qaloo salaman qala salamun fama labitha an jaa biAAijlin haneethin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 70, "But when he saw their hands not reaching for it, he distrusted them and felt from them apprehension. They said, ""Fear not. We have been sent to the people of Lot", "فَلَمَّا رَءَآ أَيۡدِيَهُمۡ لَا تَصِلُ إِلَيۡهِ نَكِرَهُمۡ وَأَوۡجَسَ مِنۡهُمۡ خِيفَةٗۚ قَالُواْ لَا تَخَفۡ إِنَّآ أُرۡسِلۡنَآ إِلَىٰ قَوۡمِ لُوطٖ", "Falamma raa aydiyahum la tasilu ilayhi nakirahum waawjasa minhum kheefatan qaloo la takhaf inna orsilna ila qawmi lootin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 71, "And his Wife was standing, and she smiled. Then We gave her good tidings of Isaac and after Isaac, Jacob", "وَٱمۡرَأَتُهُۥ قَآئِمَةٞ فَضَحِكَتۡ فَبَشَّرۡنَٰهَا بِإِسۡحَٰقَ وَمِن وَرَآءِ إِسۡحَٰقَ يَعۡقُوبَ", "Waimraatuhu qaimatun fadahikat fabashsharnaha biishaqa wamin warai ishaqa yaAAqooba");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 72, "She said, ""Woe to me! Shall I give birth while I am an old woman and this, my husband, is an old man? Indeed, this is an amazing thing", "قَالَتۡ يَٰوَيۡلَتَىٰٓ ءَأَلِدُ وَأَنَا۠ عَجُوزٞ وَهَٰذَا بَعۡلِي شَيۡخًاۖ إِنَّ هَٰذَا لَشَيۡءٌ عَجِيبٞ", "Qalat ya waylata aalidu waana AAajoozun wahatha baAAlee shaykhan inna hatha lashayon AAajeebun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 73, "They said, ""Are you amazed at the decree of Allah? May the mercy of Allah and His blessings be upon you, people of the house. Indeed, He is Praiseworthy and Honorable", "قَالُوٓاْ أَتَعۡجَبِينَ مِنۡ أَمۡرِ ٱللَّهِۖ رَحۡمَتُ ٱللَّهِ وَبَرَكَٰتُهُۥ عَلَيۡكُمۡ أَهۡلَ ٱلۡبَيۡتِۚ إِنَّهُۥ حَمِيدٞ مَّجِيدٞ", "Qaloo ataAAjabeena min amri Allahi rahmatu Allahi wabarakatuhu AAalaykum ahla albayti innahu hameedun majeedun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 74, "And when the fright had left Abraham and the good tidings had reached him, he began to argue with Us concerning the people of Lot", "فَلَمَّا ذَهَبَ عَنۡ إِبۡرَٰهِيمَ ٱلرَّوۡعُ وَجَآءَتۡهُ ٱلۡبُشۡرَىٰ يُجَٰدِلُنَا فِي قَوۡمِ لُوطٍ", "Falamma thahaba AAan ibraheema alrrawAAu wajaathu albushra yujadiluna fee qawmi lootin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 75, "Indeed, Abraham was forbearing, grieving and [frequently] returning [to Allah]", "إِنَّ إِبۡرَٰهِيمَ لَحَلِيمٌ أَوَّـٰهٞ مُّنِيبٞ", "Inna ibraheema lahaleemun awwahun muneebun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 76, "[The angels said], ""O Abraham, give up this [plea]. Indeed, the command of your Lord has come, and indeed, there will reach them a punishment that cannot be repelled", "يَـٰٓإِبۡرَٰهِيمُ أَعۡرِضۡ عَنۡ هَٰذَآۖ إِنَّهُۥ قَدۡ جَآءَ أَمۡرُ رَبِّكَۖ وَإِنَّهُمۡ ءَاتِيهِمۡ عَذَابٌ غَيۡرُ مَرۡدُودٖ", "Ya ibraheemu aAArid AAan hatha innahu qad jaa amru rabbika wainnahum ateehim AAathabun ghayru mardoodin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 77, "And when Our messengers, [the angels], came to Lot, he was anguished for them and felt for them great discomfort and said, ""This is a trying day", "وَلَمَّا جَآءَتۡ رُسُلُنَا لُوطٗا سِيٓءَ بِهِمۡ وَضَاقَ بِهِمۡ ذَرۡعٗا وَقَالَ هَٰذَا يَوۡمٌ عَصِيبٞ", "Walamma jaat rusuluna lootan seea bihim wadaqa bihim tharAAan waqala hatha yawmun AAaseebun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 78, "And his people came hastening to him, and before [this] they had been doing evil deeds. He said, ""O my people, these are my daughters; they are purer for you. So fear Allah and do not disgrace me concerning my guests. Is there not among you a man of reason", "وَجَآءَهُۥ قَوۡمُهُۥ يُهۡرَعُونَ إِلَيۡهِ وَمِن قَبۡلُ كَانُواْ يَعۡمَلُونَ ٱلسَّيِّـَٔاتِۚ قَالَ يَٰقَوۡمِ هَـٰٓؤُلَآءِ بَنَاتِي هُنَّ أَطۡهَرُ لَكُمۡۖ فَٱتَّقُواْ ٱللَّهَ وَلَا تُخۡزُونِ فِي ضَيۡفِيٓۖ أَلَيۡسَ مِنكُمۡ رَجُلٞ رَّشِيدٞ", "Wajaahu qawmuhu yuhraAAoona ilayhi wamin qablu kanoo yaAAmaloona alssayyiati qala ya qawmi haolai banatee hunna atharu lakum faittaqoo Allaha wala tukhzooni fee dayfee alaysa minkum rajulun rasheedun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 79, "They said, ""You have already known that we have not concerning your daughters any claim, and indeed, you know what we want", "قَالُواْ لَقَدۡ عَلِمۡتَ مَا لَنَا فِي بَنَاتِكَ مِنۡ حَقّٖ وَإِنَّكَ لَتَعۡلَمُ مَا نُرِيدُ", "Qaloo laqad AAalimta ma lana fee banatika min haqqin wainnaka lataAAlamu ma nureedu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 80, "He said, ""If only I had against you some power or could take refuge in a strong support", "قَالَ لَوۡ أَنَّ لِي بِكُمۡ قُوَّةً أَوۡ ءَاوِيٓ إِلَىٰ رُكۡنٖ شَدِيدٖ", "Qala law anna lee bikum quwwatan aw awee ila ruknin shadeedin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 81, "The angels said, ""O Lot, indeed we are messengers of your Lord; [therefore], they will never reach you. So set out with your family during a portion of the night and let not any among you look back - except your wife; indeed, she will be struck by that which strikes them. Indeed, their appointment is [for] the morning. Is not the morning near", "قَالُواْ يَٰلُوطُ إِنَّا رُسُلُ رَبِّكَ لَن يَصِلُوٓاْ إِلَيۡكَۖ فَأَسۡرِ بِأَهۡلِكَ بِقِطۡعٖ مِّنَ ٱلَّيۡلِ وَلَا يَلۡتَفِتۡ مِنكُمۡ أَحَدٌ إِلَّا ٱمۡرَأَتَكَۖ إِنَّهُۥ مُصِيبُهَا مَآ أَصَابَهُمۡۚ إِنَّ مَوۡعِدَهُمُ ٱلصُّبۡحُۚ أَلَيۡسَ ٱلصُّبۡحُ بِقَرِيبٖ", "Qaloo ya lootu inna rusulu rabbika lan yasiloo ilayka faasri biahlika biqitAAin mina allayli wala yaltafit minkum ahadun illa imraataka innahu museebuha ma asabahum inna mawAAidahumu alssubhu alaysa alssubhu biqareebin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 82, "So when Our command came, We made the highest part [of the city] its lowest and rained upon them stones of layered hard clay, [which were]", "فَلَمَّا جَآءَ أَمۡرُنَا جَعَلۡنَا عَٰلِيَهَا سَافِلَهَا وَأَمۡطَرۡنَا عَلَيۡهَا حِجَارَةٗ مِّن سِجِّيلٖ مَّنضُودٖ", "Falamma jaa amruna jaAAalna AAaliyaha safilaha waamtarna AAalayha hijaratan min sijjeelin mandoodin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 83, "Marked from your Lord. And Allah 's punishment is not from the wrongdoers [very] far", "مُّسَوَّمَةً عِندَ رَبِّكَۖ وَمَا هِيَ مِنَ ٱلظَّـٰلِمِينَ بِبَعِيدٖ", "Musawwamatan AAinda rabbika wama hiya mina alththalimeena bibaAAeedin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 84, "And to Madyan [We sent] their brother Shu'ayb. He said, ""O my people, worship Allah; you have no deity other than Him. And do not decrease from the measure and the scale. Indeed, I see you in prosperity, but indeed, I fear for you the punishment of an all-encompassing Day", "۞وَإِلَىٰ مَدۡيَنَ أَخَاهُمۡ شُعَيۡبٗاۚ قَالَ يَٰقَوۡمِ ٱعۡبُدُواْ ٱللَّهَ مَا لَكُم مِّنۡ إِلَٰهٍ غَيۡرُهُۥۖ وَلَا تَنقُصُواْ ٱلۡمِكۡيَالَ وَٱلۡمِيزَانَۖ إِنِّيٓ أَرَىٰكُم بِخَيۡرٖ وَإِنِّيٓ أَخَافُ عَلَيۡكُمۡ عَذَابَ يَوۡمٖ مُّحِيطٖ", "Waila madyana akhahum shuAAayban qala ya qawmi oAAbudoo Allaha ma lakum min ilahin ghayruhu wala tanqusoo almikyala waalmeezana innee arakum bikhayrin wainnee akhafu AAalaykum AAathaba yawmin muheetin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 85, "And O my people, give full measure and weight in justice and do not deprive the people of their due and do not commit abuse on the earth, spreading corruption", "وَيَٰقَوۡمِ أَوۡفُواْ ٱلۡمِكۡيَالَ وَٱلۡمِيزَانَ بِٱلۡقِسۡطِۖ وَلَا تَبۡخَسُواْ ٱلنَّاسَ أَشۡيَآءَهُمۡ وَلَا تَعۡثَوۡاْ فِي ٱلۡأَرۡضِ مُفۡسِدِينَ", "Waya qawmi awfoo almikyala waalmeezana bialqisti wala tabkhasoo alnnasa ashyaahum wala taAAthaw fee alardi mufsideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 86, "What remains [lawful] from Allah is best for you, if you would be believers. But I am not a guardian over you", "بَقِيَّتُ ٱللَّهِ خَيۡرٞ لَّكُمۡ إِن كُنتُم مُّؤۡمِنِينَۚ وَمَآ أَنَا۠ عَلَيۡكُم بِحَفِيظٖ", "Baqiyyatu Allahi khayrun lakum in kuntum mumineena wama ana AAalaykum bihafeethin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 87, "They said, ""O Shu'ayb, does your prayer command you that we should leave what our fathers worship or not do with our wealth what we please? Indeed, you are the forbearing, the discerning", "قَالُواْ يَٰشُعَيۡبُ أَصَلَوٰتُكَ تَأۡمُرُكَ أَن نَّتۡرُكَ مَا يَعۡبُدُ ءَابَآؤُنَآ أَوۡ أَن نَّفۡعَلَ فِيٓ أَمۡوَٰلِنَا مَا نَشَـٰٓؤُاْۖ إِنَّكَ لَأَنتَ ٱلۡحَلِيمُ ٱلرَّشِيدُ", "Qaloo ya shuAAaybu asalatuka tamuruka an natruka ma yaAAbudu abaona aw an nafAAala fee amwalina ma nashao innaka laanta alhaleemu alrrasheedu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 88, "He said, ""O my people, have you considered: if I am upon clear evidence from my Lord and He has provided me with a good provision from Him...? And I do not intend to differ from you in that which I have forbidden you; I only intend reform as much as I am able. And my success is not but through Allah. Upon him I have relied, and to Him I return", "قَالَ يَٰقَوۡمِ أَرَءَيۡتُمۡ إِن كُنتُ عَلَىٰ بَيِّنَةٖ مِّن رَّبِّي وَرَزَقَنِي مِنۡهُ رِزۡقًا حَسَنٗاۚ وَمَآ أُرِيدُ أَنۡ أُخَالِفَكُمۡ إِلَىٰ مَآ أَنۡهَىٰكُمۡ عَنۡهُۚ إِنۡ أُرِيدُ إِلَّا ٱلۡإِصۡلَٰحَ مَا ٱسۡتَطَعۡتُۚ وَمَا تَوۡفِيقِيٓ إِلَّا بِٱللَّهِۚ عَلَيۡهِ تَوَكَّلۡتُ وَإِلَيۡهِ أُنِيبُ", "Qala ya qawmi araaytum in kuntu AAala bayyinatin min rabbee warazaqanee minhu rizqan hasanan wama oreedu an okhalifakum ila ma anhakum AAanhu in oreedu illa alislaha ma istataAAtu wama tawfeeqee illa biAllahi AAalayhi tawakkaltu wailayhi oneebu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 89, "And O my people, let not [your] dissension from me cause you to be struck by that similar to what struck the people of Noah or the people of Hud or the people of Salih. And the people of Lot are not from you far away", "وَيَٰقَوۡمِ لَا يَجۡرِمَنَّكُمۡ شِقَاقِيٓ أَن يُصِيبَكُم مِّثۡلُ مَآ أَصَابَ قَوۡمَ نُوحٍ أَوۡ قَوۡمَ هُودٍ أَوۡ قَوۡمَ صَٰلِحٖۚ وَمَا قَوۡمُ لُوطٖ مِّنكُم بِبَعِيدٖ", "Waya qawmi la yajrimannakum shiqaqee an yuseebakum mithlu ma asaba qawma noohin aw qawma hoodin aw qawma salihin wama qawmu lootin minkum bibaAAeedin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 90, "And ask forgiveness of your Lord and then repent to Him. Indeed, my Lord is Merciful and Affectionate", "وَٱسۡتَغۡفِرُواْ رَبَّكُمۡ ثُمَّ تُوبُوٓاْ إِلَيۡهِۚ إِنَّ رَبِّي رَحِيمٞ وَدُودٞ", "Waistaghfiroo rabbakum thumma tooboo ilayhi inna rabbee raheemun wadoodun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 91, "They said, ""O Shu'ayb, we do not understand much of what you say, and indeed, we consider you among us as weak. And if not for your family, we would have stoned you [to death]; and you are not to us one respected", "قَالُواْ يَٰشُعَيۡبُ مَا نَفۡقَهُ كَثِيرٗا مِّمَّا تَقُولُ وَإِنَّا لَنَرَىٰكَ فِينَا ضَعِيفٗاۖ وَلَوۡلَا رَهۡطُكَ لَرَجَمۡنَٰكَۖ وَمَآ أَنتَ عَلَيۡنَا بِعَزِيزٖ", "Qaloo ya shuAAaybu ma nafqahu katheeran mimma taqoolu wainna lanaraka feena daAAeefan walawla rahtuka larajamnaka wama anta AAalayna biAAazeezin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 92, "He said, ""O my people, is my family more respected for power by you than Allah? But you put Him behind your backs [in neglect]. Indeed, my Lord is encompassing of what you do", "قَالَ يَٰقَوۡمِ أَرَهۡطِيٓ أَعَزُّ عَلَيۡكُم مِّنَ ٱللَّهِ وَٱتَّخَذۡتُمُوهُ وَرَآءَكُمۡ ظِهۡرِيًّاۖ إِنَّ رَبِّي بِمَا تَعۡمَلُونَ مُحِيطٞ", "Qala ya qawmi arahtee aAAazzu AAalaykum mina Allahi waittakhathtumoohu waraakum thihriyyan inna rabbee bima taAAmaloona muheetun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 93, "And O my people, work according to your position; indeed, I am working. You are going to know to whom will come a punishment that will disgrace him and who is a liar. So watch; indeed, I am with you a watcher, [awaiting the outcome]", "وَيَٰقَوۡمِ ٱعۡمَلُواْ عَلَىٰ مَكَانَتِكُمۡ إِنِّي عَٰمِلٞۖ سَوۡفَ تَعۡلَمُونَ مَن يَأۡتِيهِ عَذَابٞ يُخۡزِيهِ وَمَنۡ هُوَ كَٰذِبٞۖ وَٱرۡتَقِبُوٓاْ إِنِّي مَعَكُمۡ رَقِيبٞ", "Waya qawmi iAAmaloo AAala makanatikum innee AAamilun sawfa taAAlamoona man yateehi AAathabun yukhzeehi waman huwa kathibun wairtaqiboo inne maAAakum raqeebun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 94, "And when Our command came, We saved Shu'ayb and those who believed with him, by mercy from Us. And the shriek seized those who had wronged, and they became within their homes [corpses] fallen prone", "وَلَمَّا جَآءَ أَمۡرُنَا نَجَّيۡنَا شُعَيۡبٗا وَٱلَّذِينَ ءَامَنُواْ مَعَهُۥ بِرَحۡمَةٖ مِّنَّا وَأَخَذَتِ ٱلَّذِينَ ظَلَمُواْ ٱلصَّيۡحَةُ فَأَصۡبَحُواْ فِي دِيَٰرِهِمۡ جَٰثِمِينَ", "Walamma jaa amruna najjayna shuAAayban waallatheena amanoo maAAahu birahmatin minna waakhathati allatheena thalamoo alssayhatu faasbahoo fee diyarihim jathimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 95, "As if they had never prospered therein. Then, away with Madyan as Thamud was taken away", "كَأَن لَّمۡ يَغۡنَوۡاْ فِيهَآۗ أَلَا بُعۡدٗا لِّمَدۡيَنَ كَمَا بَعِدَتۡ ثَمُودُ", "Kaan lam yaghnaw feeha ala buAAdan limadyana kama baAAidat thamoodu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 96, "And We did certainly send Moses with Our signs and a clear authority", "وَلَقَدۡ أَرۡسَلۡنَا مُوسَىٰ بِـَٔايَٰتِنَا وَسُلۡطَٰنٖ مُّبِينٍ", "Walaqad arsalna moosa biayatina wasultanin mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 97, "To Pharaoh and his establishment, but they followed the command of Pharaoh, and the command of Pharaoh was not [at all] discerning", "إِلَىٰ فِرۡعَوۡنَ وَمَلَإِيْهِۦ فَٱتَّبَعُوٓاْ أَمۡرَ فِرۡعَوۡنَۖ وَمَآ أَمۡرُ فِرۡعَوۡنَ بِرَشِيدٖ", "Ila firAAawna wamalaihi faittabaAAoo amra firAAawna wama amru firAAawna birasheedin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 98, "He will precede his people on the Day of Resurrection and lead them into the Fire; and wretched is the place to which they are led", "يَقۡدُمُ قَوۡمَهُۥ يَوۡمَ ٱلۡقِيَٰمَةِ فَأَوۡرَدَهُمُ ٱلنَّارَۖ وَبِئۡسَ ٱلۡوِرۡدُ ٱلۡمَوۡرُودُ", "Yaqdumu qawmahu yawma alqiyamati faawradahumu alnnara wabisa alwirdu almawroodu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 99, "And they were followed in this [world] with a curse and on the Day of Resurrection. And wretched is the gift which is given", "وَأُتۡبِعُواْ فِي هَٰذِهِۦ لَعۡنَةٗ وَيَوۡمَ ٱلۡقِيَٰمَةِۚ بِئۡسَ ٱلرِّفۡدُ ٱلۡمَرۡفُودُ", "WaotbiAAoo fee hathihi laAAnatan wayawma alqiyamati bisa alrrifdu almarfoodu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 100, "That is from the news of the cities, which We relate to you; of them, some are [still] standing and some are [as] a harvest [mowed down]", "ذَٰلِكَ مِنۡ أَنۢبَآءِ ٱلۡقُرَىٰ نَقُصُّهُۥ عَلَيۡكَۖ مِنۡهَا قَآئِمٞ وَحَصِيدٞ", "Thalika min anbai alqura naqussuhu AAalayka minha qaimun wahaseedun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 101, "And We did not wrong them, but they wronged themselves. And they were not availed at all by their gods which they invoked other than Allah when there came the command of your Lord. And they did not increase them in other than ruin", "وَمَا ظَلَمۡنَٰهُمۡ وَلَٰكِن ظَلَمُوٓاْ أَنفُسَهُمۡۖ فَمَآ أَغۡنَتۡ عَنۡهُمۡ ءَالِهَتُهُمُ ٱلَّتِي يَدۡعُونَ مِن دُونِ ٱللَّهِ مِن شَيۡءٖ لَّمَّا جَآءَ أَمۡرُ رَبِّكَۖ وَمَا زَادُوهُمۡ غَيۡرَ تَتۡبِيبٖ", "Wama thalamnahum walakin thalamoo anfusahum fama aghnat AAanhum alihatuhumu allatee yadAAoona min dooni Allahi min shayin lamma jaa amru rabbika wama zadoohum ghayra tatbeebin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 102, "And thus is the seizure of your Lord when He seizes the cities while they are committing wrong. Indeed, His seizure is painful and severe", "وَكَذَٰلِكَ أَخۡذُ رَبِّكَ إِذَآ أَخَذَ ٱلۡقُرَىٰ وَهِيَ ظَٰلِمَةٌۚ إِنَّ أَخۡذَهُۥٓ أَلِيمٞ شَدِيدٌ", "Wakathalika akhthu rabbika itha akhatha alqura wahiya thalimatun inna akhthahu aleemun shadeedun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 103, "Indeed in that is a sign for those who fear the punishment of the Hereafter. That is a Day for which the people will be collected, and that is a Day [which will be] witnessed", "إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لِّمَنۡ خَافَ عَذَابَ ٱلۡأٓخِرَةِۚ ذَٰلِكَ يَوۡمٞ مَّجۡمُوعٞ لَّهُ ٱلنَّاسُ وَذَٰلِكَ يَوۡمٞ مَّشۡهُودٞ", "Inna fee thalika laayatan liman khafa AAathaba alakhirati thalika yawmun majmooAAun lahu alnnasu wathalika yawmun mashhoodun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 104, "And We do not delay it except for a limited term", "وَمَا نُؤَخِّرُهُۥٓ إِلَّا لِأَجَلٖ مَّعۡدُودٖ", "Wama nuakhkhiruhu illa liajalin maAAdoodin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 105, "The Day it comes no soul will speak except by His permission. And among them will be the wretched and the prosperous", "يَوۡمَ يَأۡتِ لَا تَكَلَّمُ نَفۡسٌ إِلَّا بِإِذۡنِهِۦۚ فَمِنۡهُمۡ شَقِيّٞ وَسَعِيدٞ", "Yawma yati la takallamu nafsun illa biithnihi faminhum shaqiyyun wasaAAeedin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 106, "As for those who were [destined to be] wretched, they will be in the Fire. For them therein is [violent] exhaling and inhaling", "فَأَمَّا ٱلَّذِينَ شَقُواْ فَفِي ٱلنَّارِ لَهُمۡ فِيهَا زَفِيرٞ وَشَهِيقٌ", "Faamma allatheena shaqoo fafee alnnari lahum feeha zafeerun washaheequn");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 107, "[They will be] abiding therein as long as the heavens and the earth endure, except what your Lord should will. Indeed, your Lord is an effecter of what He intends", "خَٰلِدِينَ فِيهَا مَا دَامَتِ ٱلسَّمَٰوَٰتُ وَٱلۡأَرۡضُ إِلَّا مَا شَآءَ رَبُّكَۚ إِنَّ رَبَّكَ فَعَّالٞ لِّمَا يُرِيدُ", "Khalideena feeha ma damati alssamawatu waalardu illa ma shaa rabbuka inna rabbaka faAAAAalun lima yureedu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 108, "And as for those who were [destined to be] prosperous, they will be in Paradise, abiding therein as long as the heavens and the earth endure, except what your Lord should will - a bestowal uninterrupted", "۞وَأَمَّا ٱلَّذِينَ سُعِدُواْ فَفِي ٱلۡجَنَّةِ خَٰلِدِينَ فِيهَا مَا دَامَتِ ٱلسَّمَٰوَٰتُ وَٱلۡأَرۡضُ إِلَّا مَا شَآءَ رَبُّكَۖ عَطَآءً غَيۡرَ مَجۡذُوذٖ", "Waamma allatheena suAAidoo fafee aljannati khalideena feeha ma damati alssamawatu waalardu illa ma shaa rabbuka AAataan ghayra majthoothin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 109, "So do not be in doubt, [O Muhammad], as to what these [polytheists] are worshipping. They worship not except as their fathers worshipped before. And indeed, We will give them their share undiminished", "فَلَا تَكُ فِي مِرۡيَةٖ مِّمَّا يَعۡبُدُ هَـٰٓؤُلَآءِۚ مَا يَعۡبُدُونَ إِلَّا كَمَا يَعۡبُدُ ءَابَآؤُهُم مِّن قَبۡلُۚ وَإِنَّا لَمُوَفُّوهُمۡ نَصِيبَهُمۡ غَيۡرَ مَنقُوصٖ", "Fala taku fee miryatin mimma yaAAbudu haolai ma yaAAbudoona illa kama yaAAbudu abaohum min qablu wainna lamuwaffoohum naseebahum ghayra manqoosin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 110, "And We had certainly given Moses the Scripture, but it came under disagreement. And if not for a word that preceded from your Lord, it would have been judged between them. And indeed they are, concerning the Qur'an, in disquieting doubt", "وَلَقَدۡ ءَاتَيۡنَا مُوسَى ٱلۡكِتَٰبَ فَٱخۡتُلِفَ فِيهِۚ وَلَوۡلَا كَلِمَةٞ سَبَقَتۡ مِن رَّبِّكَ لَقُضِيَ بَيۡنَهُمۡۚ وَإِنَّهُمۡ لَفِي شَكّٖ مِّنۡهُ مُرِيبٖ", "Walaqad atayna moosa alkitaba faikhtulifa feehi walawla kalimatun sabaqat min rabbika laqudiya baynahum wainnahum lafee shakkin minhu mureebun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 111, "And indeed, each [of the believers and disbelievers] - your Lord will fully compensate them for their deeds. Indeed, He is Acquainted with what they do", "وَإِنَّ كُلّٗا لَّمَّا لَيُوَفِّيَنَّهُمۡ رَبُّكَ أَعۡمَٰلَهُمۡۚ إِنَّهُۥ بِمَا يَعۡمَلُونَ خَبِيرٞ", "Wainna kullan lamma layuwaffiyannahum rabbuka aAAmalahum innahu bima yaAAmaloona khabeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 112, "So remain on a right course as you have been commanded, [you] and those who have turned back with you [to Allah], and do not transgress. Indeed, He is Seeing of what you do", "فَٱسۡتَقِمۡ كَمَآ أُمِرۡتَ وَمَن تَابَ مَعَكَ وَلَا تَطۡغَوۡاْۚ إِنَّهُۥ بِمَا تَعۡمَلُونَ بَصِيرٞ", "Faistaqim kama omirta waman taba maAAaka wala tatghaw innahu bima taAAmaloona baseerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 113, "And do not incline toward those who do wrong, lest you be touched by the Fire, and you would not have other than Allah any protectors; then you would not be helped", "وَلَا تَرۡكَنُوٓاْ إِلَى ٱلَّذِينَ ظَلَمُواْ فَتَمَسَّكُمُ ٱلنَّارُ وَمَا لَكُم مِّن دُونِ ٱللَّهِ مِنۡ أَوۡلِيَآءَ ثُمَّ لَا تُنصَرُونَ", "Wala tarkanoo ila allatheena thalamoo fatamassakumu alnnaru wama lakum min dooni Allahi min awliyaa thumma la tunsaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 114, "And establish prayer at the two ends of the day and at the approach of the night. Indeed, good deeds do away with misdeeds. That is a reminder for those who remember", "وَأَقِمِ ٱلصَّلَوٰةَ طَرَفَيِ ٱلنَّهَارِ وَزُلَفٗا مِّنَ ٱلَّيۡلِۚ إِنَّ ٱلۡحَسَنَٰتِ يُذۡهِبۡنَ ٱلسَّيِّـَٔاتِۚ ذَٰلِكَ ذِكۡرَىٰ لِلذَّـٰكِرِينَ", "Waaqimi alssalata tarafayi alnnahari wazulafan mina allayli inna alhasanati yuthhibna alssayyiati thalika thikra lilththakireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 115, "And be patient, for indeed, Allah does not allow to be lost the reward of those who do good", "وَٱصۡبِرۡ فَإِنَّ ٱللَّهَ لَا يُضِيعُ أَجۡرَ ٱلۡمُحۡسِنِينَ", "Waisbir fainna Allaha la yudeeAAu ajra almuhsineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 116, "So why were there not among the generations before you those of enduring discrimination forbidding corruption on earth - except a few of those We saved from among them? But those who wronged pursued what luxury they were given therein, and they were criminals", "فَلَوۡلَا كَانَ مِنَ ٱلۡقُرُونِ مِن قَبۡلِكُمۡ أُوْلُواْ بَقِيَّةٖ يَنۡهَوۡنَ عَنِ ٱلۡفَسَادِ فِي ٱلۡأَرۡضِ إِلَّا قَلِيلٗا مِّمَّنۡ أَنجَيۡنَا مِنۡهُمۡۗ وَٱتَّبَعَ ٱلَّذِينَ ظَلَمُواْ مَآ أُتۡرِفُواْ فِيهِ وَكَانُواْ مُجۡرِمِينَ", "Falawla kana mina alqurooni min qablikum oloo baqiyyatin yanhawna AAani alfasadi fee alardi illa qaleelan mimman anjayna minhum waittabaAAa allatheena thalamoo ma otrifoo feehi wakanoo mujrimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 117, "And your Lord would not have destroyed the cities unjustly while their people were reformers", "وَمَا كَانَ رَبُّكَ لِيُهۡلِكَ ٱلۡقُرَىٰ بِظُلۡمٖ وَأَهۡلُهَا مُصۡلِحُونَ", "Wama kana rabbuka liyuhlika alqura bithulmin waahluha muslihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 118, "And if your Lord had willed, He could have made mankind one community; but they will not cease to differ", "وَلَوۡ شَآءَ رَبُّكَ لَجَعَلَ ٱلنَّاسَ أُمَّةٗ وَٰحِدَةٗۖ وَلَا يَزَالُونَ مُخۡتَلِفِينَ", "Walaw shaa rabbuka lajaAAala alnnasa ommatan wahidatan wala yazaloona mukhtalifeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 119, "Except whom your Lord has given mercy, and for that He created them. But the word of your Lord is to be fulfilled that, ""I will surely fill Hell with jinn and men all together", "إِلَّا مَن رَّحِمَ رَبُّكَۚ وَلِذَٰلِكَ خَلَقَهُمۡۗ وَتَمَّتۡ كَلِمَةُ رَبِّكَ لَأَمۡلَأَنَّ جَهَنَّمَ مِنَ ٱلۡجِنَّةِ وَٱلنَّاسِ أَجۡمَعِينَ", "Illa man rahima rabbuka walithalika khalaqahum watammat kalimatu rabbika laamlaanna jahannama mina aljinnati waalnnasi ajmaAAeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 120, "And each [story] We relate to you from the news of the messengers is that by which We make firm your heart. And there has come to you, in this, the truth and an instruction and a reminder for the believers", "وَكُلّٗا نَّقُصُّ عَلَيۡكَ مِنۡ أَنۢبَآءِ ٱلرُّسُلِ مَا نُثَبِّتُ بِهِۦ فُؤَادَكَۚ وَجَآءَكَ فِي هَٰذِهِ ٱلۡحَقُّ وَمَوۡعِظَةٞ وَذِكۡرَىٰ لِلۡمُؤۡمِنِينَ", "Wakullan naqussu AAalayka min anbai alrrusuli ma nuthabbitu bihi fuadaka wajaaka fee hathihi alhaqqu wamawAAithatun wathikra lilmumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 121, "And say to those who do not believe, ""Work according to your position; indeed, we are working", "وَقُل لِّلَّذِينَ لَا يُؤۡمِنُونَ ٱعۡمَلُواْ عَلَىٰ مَكَانَتِكُمۡ إِنَّا عَٰمِلُونَ", "Waqul lillatheena la yuminoona iAAmaloo AAala makanatikum inna AAamiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 122, "And wait, indeed, we are waiting", "وَٱنتَظِرُوٓاْ إِنَّا مُنتَظِرُونَ", "Waintathiroo inna muntathiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (11, 123, "And to Allah belong the unseen [aspects] of the heavens and the earth and to Him will be returned the matter, all of it, so worship Him and rely upon Him. And your Lord is not unaware of that which you do", "وَلِلَّهِ غَيۡبُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَإِلَيۡهِ يُرۡجَعُ ٱلۡأَمۡرُ كُلُّهُۥ فَٱعۡبُدۡهُ وَتَوَكَّلۡ عَلَيۡهِۚ وَمَا رَبُّكَ بِغَٰفِلٍ عَمَّا تَعۡمَلُونَ", "Walillahi ghaybu alssamawati waalardi wailayhi yurjaAAu alamru kulluhu faoAAbudhu watawakkal AAalayhi wama rabbuka bighafilin AAamma taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 1, "Alif, Lam, Ra. These are the verses of the clear Book", "الٓرۚ تِلۡكَ ءَايَٰتُ ٱلۡكِتَٰبِ ٱلۡمُبِينِ", "Aliflamra tilka ayatu alkitabi almubeenu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 2, "Indeed, We have sent it down as an Arabic Qur'an that you might understand", "إِنَّآ أَنزَلۡنَٰهُ قُرۡءَٰنًا عَرَبِيّٗا لَّعَلَّكُمۡ تَعۡقِلُونَ", "Inna anzalnahu quranan AAarabiyyan laAAallakum taAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 3, "We relate to you, [O Muhammad], the best of stories in what We have revealed to you of this Qur'an although you were, before it, among the unaware", "نَحۡنُ نَقُصُّ عَلَيۡكَ أَحۡسَنَ ٱلۡقَصَصِ بِمَآ أَوۡحَيۡنَآ إِلَيۡكَ هَٰذَا ٱلۡقُرۡءَانَ وَإِن كُنتَ مِن قَبۡلِهِۦ لَمِنَ ٱلۡغَٰفِلِينَ", "Nahnu naqussu AAalayka ahsana alqasasi bima awhayna ilayka hatha alqurana wain kunta min qablihi lamina alghafileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 4, "[Of these stories mention] when Joseph said to his father, ""O my father, indeed I have seen [in a dream] eleven stars and the sun and the moon; I saw them prostrating to me", "إِذۡ قَالَ يُوسُفُ لِأَبِيهِ يَـٰٓأَبَتِ إِنِّي رَأَيۡتُ أَحَدَ عَشَرَ كَوۡكَبٗا وَٱلشَّمۡسَ وَٱلۡقَمَرَ رَأَيۡتُهُمۡ لِي سَٰجِدِينَ", "Ith qala yoosufu liabeehi ya abati innee raaytu ahada AAashara kawkaban waalshshamsa waalqamara raaytuhum lee sajideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 5, "He said, ""O my son, do not relate your vision to your brothers or they will contrive against you a plan. Indeed Satan, to man, is a manifest enemy", "قَالَ يَٰبُنَيَّ لَا تَقۡصُصۡ رُءۡيَاكَ عَلَىٰٓ إِخۡوَتِكَ فَيَكِيدُواْ لَكَ كَيۡدًاۖ إِنَّ ٱلشَّيۡطَٰنَ لِلۡإِنسَٰنِ عَدُوّٞ مُّبِينٞ", "Qala ya bunayya la taqsus ruyaka AAala ikhwatika fayakeedoo laka kaydan inna alshshaytana lilinsani AAaduwwun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 6, "And thus will your Lord choose you and teach you the interpretation of narratives and complete His favor upon you and upon the family of Jacob, as He completed it upon your fathers before, Abraham and Isaac. Indeed, your Lord is Knowing and Wise", "وَكَذَٰلِكَ يَجۡتَبِيكَ رَبُّكَ وَيُعَلِّمُكَ مِن تَأۡوِيلِ ٱلۡأَحَادِيثِ وَيُتِمُّ نِعۡمَتَهُۥ عَلَيۡكَ وَعَلَىٰٓ ءَالِ يَعۡقُوبَ كَمَآ أَتَمَّهَا عَلَىٰٓ أَبَوَيۡكَ مِن قَبۡلُ إِبۡرَٰهِيمَ وَإِسۡحَٰقَۚ إِنَّ رَبَّكَ عَلِيمٌ حَكِيمٞ", "Wakathalika yajtabeeka rabbuka wayuAAallimuka min taweeli alahadeethi wayutimmu niAAmatahu AAalayka waAAala ali yaAAqooba kama atammaha AAala abawayka min qablu ibraheema waishaqa inna rabbaka AAaleemun hakeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 7, "Certainly were there in Joseph and his brothers signs for those who ask", "۞لَّقَدۡ كَانَ فِي يُوسُفَ وَإِخۡوَتِهِۦٓ ءَايَٰتٞ لِّلسَّآئِلِينَ", "Laqad kana fee yoosufa waikhwatihi ayatun lilssaileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 8, "When they said, ""Joseph and his brother are more beloved to our father than we, while we are a clan. Indeed, our father is in clear error", "إِذۡ قَالُواْ لَيُوسُفُ وَأَخُوهُ أَحَبُّ إِلَىٰٓ أَبِينَا مِنَّا وَنَحۡنُ عُصۡبَةٌ إِنَّ أَبَانَا لَفِي ضَلَٰلٖ مُّبِينٍ", "Ith qaloo layoosufu waakhoohu ahabbu ila abeena minna wanahnu AAusbatun inna abana lafee dalalin mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 9, "Kill Joseph or cast him out to [another] land; the countenance of your father will [then] be only for you, and you will be after that a righteous people", "ٱقۡتُلُواْ يُوسُفَ أَوِ ٱطۡرَحُوهُ أَرۡضٗا يَخۡلُ لَكُمۡ وَجۡهُ أَبِيكُمۡ وَتَكُونُواْ مِنۢ بَعۡدِهِۦ قَوۡمٗا صَٰلِحِينَ", "Oqtuloo yoosufa awi itrahoohu ardan yakhlu lakum wajhu abeekum watakoonoo min baAAdihi qawman saliheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 10, "Said a speaker among them, ""Do not kill Joseph but throw him into the bottom of the well; some travelers will pick him up - if you would do [something]", "قَالَ قَآئِلٞ مِّنۡهُمۡ لَا تَقۡتُلُواْ يُوسُفَ وَأَلۡقُوهُ فِي غَيَٰبَتِ ٱلۡجُبِّ يَلۡتَقِطۡهُ بَعۡضُ ٱلسَّيَّارَةِ إِن كُنتُمۡ فَٰعِلِينَ", "Qala qailun minhum la taqtuloo yoosufa waalqoohu fee ghayabati aljubbi yaltaqithu baAAdu alssayyarati in kuntum faAAileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 11, "They said, ""O our father, why do you not entrust us with Joseph while indeed, we are to him sincere counselors", "قَالُواْ يَـٰٓأَبَانَا مَا لَكَ لَا تَأۡمَ۬نَّا عَلَىٰ يُوسُفَ وَإِنَّا لَهُۥ لَنَٰصِحُونَ", "Qaloo ya abana ma laka la tamanna AAala yoosufa wainna lahu lanasihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 12, "Send him with us tomorrow that he may eat well and play. And indeed, we will be his guardians", "أَرۡسِلۡهُ مَعَنَا غَدٗا يَرۡتَعۡ وَيَلۡعَبۡ وَإِنَّا لَهُۥ لَحَٰفِظُونَ", "Arsilhu maAAana ghadan yartaAA wayalAAab wainna lahu lahafithoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 13, "[Jacob] said, ""Indeed, it saddens me that you should take him, and I fear that a wolf would eat him while you are of him unaware", "قَالَ إِنِّي لَيَحۡزُنُنِيٓ أَن تَذۡهَبُواْ بِهِۦ وَأَخَافُ أَن يَأۡكُلَهُ ٱلذِّئۡبُ وَأَنتُمۡ عَنۡهُ غَٰفِلُونَ", "Qala innee layahzununee an thathhaboo bihi waakhafu an yakulahu alththibu waantum AAanhu ghafiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 14, "They said, ""If a wolf should eat him while we are a [strong] clan, indeed, we would then be losers", "قَالُواْ لَئِنۡ أَكَلَهُ ٱلذِّئۡبُ وَنَحۡنُ عُصۡبَةٌ إِنَّآ إِذٗا لَّخَٰسِرُونَ", "Qaloo lain akalahu alththibu wanahnu AAusbatun inna ithan lakhasiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 15, "So when they took him [out] and agreed to put him into the bottom of the well... But We inspired to him, ""You will surely inform them [someday] about this affair of theirs while they do not perceive [your identity]", "فَلَمَّا ذَهَبُواْ بِهِۦ وَأَجۡمَعُوٓاْ أَن يَجۡعَلُوهُ فِي غَيَٰبَتِ ٱلۡجُبِّۚ وَأَوۡحَيۡنَآ إِلَيۡهِ لَتُنَبِّئَنَّهُم بِأَمۡرِهِمۡ هَٰذَا وَهُمۡ لَا يَشۡعُرُونَ", "Falamma thahaboo bihi waajmaAAoo an yajAAaloohu fee ghayabati aljubbi waawhayna ilayhi latunabiannahum biamrihim hatha wahum la yashAAuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 16, "And they came to their father at night, weeping", "وَجَآءُوٓ أَبَاهُمۡ عِشَآءٗ يَبۡكُونَ", "Wajaoo abahum AAishaan yabkoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 17, "They said, ""O our father, indeed we went racing each other and left Joseph with our possessions, and a wolf ate him. But you would not believe us, even if we were truthful", "قَالُواْ يَـٰٓأَبَانَآ إِنَّا ذَهَبۡنَا نَسۡتَبِقُ وَتَرَكۡنَا يُوسُفَ عِندَ مَتَٰعِنَا فَأَكَلَهُ ٱلذِّئۡبُۖ وَمَآ أَنتَ بِمُؤۡمِنٖ لَّنَا وَلَوۡ كُنَّا صَٰدِقِينَ", "Qaloo ya abana inna thahabna nastabiqu watarakna yoosufa AAinda mataAAina faakalahu alththibu wama anta bimuminin lana walaw kunna sadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 18, "And they brought upon his shirt false blood. [Jacob] said, ""Rather, your souls have enticed you to something, so patience is most fitting. And Allah is the one sought for help against that which you describe", "وَجَآءُو عَلَىٰ قَمِيصِهِۦ بِدَمٖ كَذِبٖۚ قَالَ بَلۡ سَوَّلَتۡ لَكُمۡ أَنفُسُكُمۡ أَمۡرٗاۖ فَصَبۡرٞ جَمِيلٞۖ وَٱللَّهُ ٱلۡمُسۡتَعَانُ عَلَىٰ مَا تَصِفُونَ", "Wajaoo AAala qameesihi bidamin kathibin qala bal sawwalat lakum anfusukum amran fasabrun jameelun waAllahu almustaAAanu AAala ma tasifoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 19, "And there came a company of travelers; then they sent their water drawer, and he let down his bucket. He said, ""Good news! Here is a boy."" And they concealed him, [taking him] as merchandise; and Allah was knowing of what they did", "وَجَآءَتۡ سَيَّارَةٞ فَأَرۡسَلُواْ وَارِدَهُمۡ فَأَدۡلَىٰ دَلۡوَهُۥۖ قَالَ يَٰبُشۡرَىٰ هَٰذَا غُلَٰمٞۚ وَأَسَرُّوهُ بِضَٰعَةٗۚ وَٱللَّهُ عَلِيمُۢ بِمَا يَعۡمَلُونَ", "Wajaat sayyaratun faarsaloo waridahum faadla dalwahu qala ya bushra hatha ghulamun waasarroohu bidaAAatan waAllahu AAaleemun bima yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 20, "And they sold him for a reduced price - a few dirhams - and they were, concerning him, of those content with little", "وَشَرَوۡهُ بِثَمَنِۭ بَخۡسٖ دَرَٰهِمَ مَعۡدُودَةٖ وَكَانُواْ فِيهِ مِنَ ٱلزَّـٰهِدِينَ", "Washarawhu bithamanin bakhsin darahima maAAdoodatin wakanoo feehi mina alzzahideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 21, "And the one from Egypt who bought him said to his wife, ""Make his residence comfortable. Perhaps he will benefit us, or we will adopt him as a son."" And thus, We established Joseph in the land that We might teach him the interpretation of events. And Allah is predominant over His affair, but most of the people do not know", "وَقَالَ ٱلَّذِي ٱشۡتَرَىٰهُ مِن مِّصۡرَ لِٱمۡرَأَتِهِۦٓ أَكۡرِمِي مَثۡوَىٰهُ عَسَىٰٓ أَن يَنفَعَنَآ أَوۡ نَتَّخِذَهُۥ وَلَدٗاۚ وَكَذَٰلِكَ مَكَّنَّا لِيُوسُفَ فِي ٱلۡأَرۡضِ وَلِنُعَلِّمَهُۥ مِن تَأۡوِيلِ ٱلۡأَحَادِيثِۚ وَٱللَّهُ غَالِبٌ عَلَىٰٓ أَمۡرِهِۦ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَعۡلَمُونَ", "Waqala allathee ishtarahu min misra liimraatihi akrimee mathwahu AAasa an yanfaAAana aw nattakhithahu waladan wakathalika makkanna liyoosufa fee alardi walinuAAallimahu min taweeli alahadeethi waAllahu ghalibun AAala amrihi walakinna akthara alnnasi la yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 22, "And when Joseph reached maturity, We gave him judgment and knowledge. And thus We reward the doers of good", "وَلَمَّا بَلَغَ أَشُدَّهُۥٓ ءَاتَيۡنَٰهُ حُكۡمٗا وَعِلۡمٗاۚ وَكَذَٰلِكَ نَجۡزِي ٱلۡمُحۡسِنِينَ", "Walamma balagha ashuddahu ataynahu hukman waAAilman wakathalika najzee almuhsineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 23, "And she, in whose house he was, sought to seduce him. She closed the doors and said, ""Come, you."" He said, ""[I seek] the refuge of Allah. Indeed, he is my master, who has made good my residence. Indeed, wrongdoers will not succeed", "وَرَٰوَدَتۡهُ ٱلَّتِي هُوَ فِي بَيۡتِهَا عَن نَّفۡسِهِۦ وَغَلَّقَتِ ٱلۡأَبۡوَٰبَ وَقَالَتۡ هَيۡتَ لَكَۚ قَالَ مَعَاذَ ٱللَّهِۖ إِنَّهُۥ رَبِّيٓ أَحۡسَنَ مَثۡوَايَۖ إِنَّهُۥ لَا يُفۡلِحُ ٱلظَّـٰلِمُونَ", "Warawadathu allatee huwa fee baytiha AAan nafsihi waghallaqati alabwaba waqalat hayta laka qala maAAatha Allahi innahu rabbee ahsana mathwaya innahu la yuflihu alththalimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 24, "And she certainly determined [to seduce] him, and he would have inclined to her had he not seen the proof of his Lord. And thus [it was] that We should avert from him evil and immorality. Indeed, he was of Our chosen servants", "وَلَقَدۡ هَمَّتۡ بِهِۦۖ وَهَمَّ بِهَا لَوۡلَآ أَن رَّءَا بُرۡهَٰنَ رَبِّهِۦۚ كَذَٰلِكَ لِنَصۡرِفَ عَنۡهُ ٱلسُّوٓءَ وَٱلۡفَحۡشَآءَۚ إِنَّهُۥ مِنۡ عِبَادِنَا ٱلۡمُخۡلَصِينَ", "Walaqad hammat bihi wahamma biha lawla an raa burhana rabbihi kathalika linasrifa AAanhu alssooa waalfahshaa innahu min AAibadina almukhlaseena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 25, "And they both raced to the door, and she tore his shirt from the back, and they found her husband at the door. She said, ""What is the recompense of one who intended evil for your wife but that he be imprisoned or a painful punishment", "وَٱسۡتَبَقَا ٱلۡبَابَ وَقَدَّتۡ قَمِيصَهُۥ مِن دُبُرٖ وَأَلۡفَيَا سَيِّدَهَا لَدَا ٱلۡبَابِۚ قَالَتۡ مَا جَزَآءُ مَنۡ أَرَادَ بِأَهۡلِكَ سُوٓءًا إِلَّآ أَن يُسۡجَنَ أَوۡ عَذَابٌ أَلِيمٞ", "Waistabaqa albaba waqaddat qameesahu min duburin waalfaya sayyidaha lada albabi qalat ma jazao man arada biahlika sooan illa an yusjana aw AAathabun aleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 26, "[Joseph] said, ""It was she who sought to seduce me."" And a witness from her family testified. ""If his shirt is torn from the front, then she has told the truth, and he is of the liars", "قَالَ هِيَ رَٰوَدَتۡنِي عَن نَّفۡسِيۚ وَشَهِدَ شَاهِدٞ مِّنۡ أَهۡلِهَآ إِن كَانَ قَمِيصُهُۥ قُدَّ مِن قُبُلٖ فَصَدَقَتۡ وَهُوَ مِنَ ٱلۡكَٰذِبِينَ", "Qala hiya rawadatnee AAan nafsee washahida shahidun min ahliha in kana qameesuhu qudda min qubulin fasadaqat wahuwa mina alkathibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 27, "But if his shirt is torn from the back, then she has lied, and he is of the truthful", "وَإِن كَانَ قَمِيصُهُۥ قُدَّ مِن دُبُرٖ فَكَذَبَتۡ وَهُوَ مِنَ ٱلصَّـٰدِقِينَ", "Wain kana qameesuhu qudda min duburin fakathabat wahuwa mina alssadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 28, "So when her husband saw his shirt torn from the back, he said, ""Indeed, it is of the women's plan. Indeed, your plan is great", "فَلَمَّا رَءَا قَمِيصَهُۥ قُدَّ مِن دُبُرٖ قَالَ إِنَّهُۥ مِن كَيۡدِكُنَّۖ إِنَّ كَيۡدَكُنَّ عَظِيمٞ", "Falamma raa qameesahu qudda min duburin qala innahu min kaydikunna inna kaydakunna AAatheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 29, "Joseph, ignore this. And, [my wife], ask forgiveness for your sin. Indeed, you were of the sinful", "يُوسُفُ أَعۡرِضۡ عَنۡ هَٰذَاۚ وَٱسۡتَغۡفِرِي لِذَنۢبِكِۖ إِنَّكِ كُنتِ مِنَ ٱلۡخَاطِـِٔينَ", "Yoosufu aAArid AAan hatha waistaghfiree lithanbiki innaki kunti mina alkhatieena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 30, "And women in the city said, ""The wife of al-'Azeez is seeking to seduce her slave boy; he has impassioned her with love. Indeed, we see her [to be] in clear error", "۞وَقَالَ نِسۡوَةٞ فِي ٱلۡمَدِينَةِ ٱمۡرَأَتُ ٱلۡعَزِيزِ تُرَٰوِدُ فَتَىٰهَا عَن نَّفۡسِهِۦۖ قَدۡ شَغَفَهَا حُبًّاۖ إِنَّا لَنَرَىٰهَا فِي ضَلَٰلٖ مُّبِينٖ", "Waqala niswatun fee almadeenati imraatu alAAazeezi turawidu fataha AAan nafsihi qad shaghafaha hubban inna lanaraha fee dalalin mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 31, "So when she heard of their scheming, she sent for them and prepared for them a banquet and gave each one of them a knife and said [to Joseph], ""Come out before them."" And when they saw him, they greatly admired him and cut their hands and said, ""Perfect is Allah! This is not a man; this is none but a noble angel", "فَلَمَّا سَمِعَتۡ بِمَكۡرِهِنَّ أَرۡسَلَتۡ إِلَيۡهِنَّ وَأَعۡتَدَتۡ لَهُنَّ مُتَّكَـٔٗا وَءَاتَتۡ كُلَّ وَٰحِدَةٖ مِّنۡهُنَّ سِكِّينٗا وَقَالَتِ ٱخۡرُجۡ عَلَيۡهِنَّۖ فَلَمَّا رَأَيۡنَهُۥٓ أَكۡبَرۡنَهُۥ وَقَطَّعۡنَ أَيۡدِيَهُنَّ وَقُلۡنَ حَٰشَ لِلَّهِ مَا هَٰذَا بَشَرًا إِنۡ هَٰذَآ إِلَّا مَلَكٞ كَرِيمٞ", "Falamma samiAAat bimakrihinna arsalat ilayhinna waaAAtadat lahunna muttakaan waatat kulla wahidatin minhunna sikkeenan waqalati okhruj AAalayhinna falamma raaynahu akbarnahu waqattaAAna aydiyahunna waqulna hasha lillahi ma hatha basharan in hatha illa malakun kareemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 32, "She said, ""That is the one about whom you blamed me. And I certainly sought to seduce him, but he firmly refused; and if he will not do what I order him, he will surely be imprisoned and will be of those debased", "قَالَتۡ فَذَٰلِكُنَّ ٱلَّذِي لُمۡتُنَّنِي فِيهِۖ وَلَقَدۡ رَٰوَدتُّهُۥ عَن نَّفۡسِهِۦ فَٱسۡتَعۡصَمَۖ وَلَئِن لَّمۡ يَفۡعَلۡ مَآ ءَامُرُهُۥ لَيُسۡجَنَنَّ وَلَيَكُونٗا مِّنَ ٱلصَّـٰغِرِينَ", "Qalat fathalikunna allathee lumtunnanee feehi walaqad rawadtuhu AAan nafsihi faistAAsama walain lam yafAAal ma amuruhu layusjananna walayakoonan mina alssaghireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 33, "He said, ""My Lord, prison is more to my liking than that to which they invite me. And if You do not avert from me their plan, I might incline toward them and [thus] be of the ignorant", "قَالَ رَبِّ ٱلسِّجۡنُ أَحَبُّ إِلَيَّ مِمَّا يَدۡعُونَنِيٓ إِلَيۡهِۖ وَإِلَّا تَصۡرِفۡ عَنِّي كَيۡدَهُنَّ أَصۡبُ إِلَيۡهِنَّ وَأَكُن مِّنَ ٱلۡجَٰهِلِينَ", "Qala rabbi alssijnu ahabbu ilayya mimma yadAAoonanee ilayhi wailla tasrif AAannee kaydahunna asbu ilayhinna waakun mina aljahileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 34, "So his Lord responded to him and averted from him their plan. Indeed, He is the Hearing, the Knowing", "فَٱسۡتَجَابَ لَهُۥ رَبُّهُۥ فَصَرَفَ عَنۡهُ كَيۡدَهُنَّۚ إِنَّهُۥ هُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ", "Faistajaba lahu rabbuhu fasarafa AAanhu kaydahunna innahu huwa alssameeAAu alAAaleemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 35, "Then it appeared to them after they had seen the signs that al-'Azeez should surely imprison him for a time", "ثُمَّ بَدَا لَهُم مِّنۢ بَعۡدِ مَا رَأَوُاْ ٱلۡأٓيَٰتِ لَيَسۡجُنُنَّهُۥ حَتَّىٰ حِينٖ", "Thumma bada lahum min baAAdi ma raawoo alayati layasjununnahu hatta heenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 36, "And there entered the prison with him two young men. One of them said, ""Indeed, I have seen myself [in a dream] pressing wine."" The other said, ""Indeed, I have seen myself carrying upon my head [some] bread, from which the birds were eating. Inform us of its interpretation; indeed, we see you to be of those who do good", "وَدَخَلَ مَعَهُ ٱلسِّجۡنَ فَتَيَانِۖ قَالَ أَحَدُهُمَآ إِنِّيٓ أَرَىٰنِيٓ أَعۡصِرُ خَمۡرٗاۖ وَقَالَ ٱلۡأٓخَرُ إِنِّيٓ أَرَىٰنِيٓ أَحۡمِلُ فَوۡقَ رَأۡسِي خُبۡزٗا تَأۡكُلُ ٱلطَّيۡرُ مِنۡهُۖ نَبِّئۡنَا بِتَأۡوِيلِهِۦٓۖ إِنَّا نَرَىٰكَ مِنَ ٱلۡمُحۡسِنِينَ", "Wadakhala maAAahu alssijna fatayani qala ahaduhuma innee aranee aAAsiru khamran waqala alakharu innee aranee ahmilu fawqa rasee khubzan takulu alttayru minhu nabbina bitaweelihi inna naraka mina almuhsineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 37, "He said, ""You will not receive food that is provided to you except that I will inform you of its interpretation before it comes to you. That is from what my Lord has taught me. Indeed, I have left the religion of a people who do not believe in Allah, and they, in the Hereafter, are disbelievers", "قَالَ لَا يَأۡتِيكُمَا طَعَامٞ تُرۡزَقَانِهِۦٓ إِلَّا نَبَّأۡتُكُمَا بِتَأۡوِيلِهِۦ قَبۡلَ أَن يَأۡتِيَكُمَاۚ ذَٰلِكُمَا مِمَّا عَلَّمَنِي رَبِّيٓۚ إِنِّي تَرَكۡتُ مِلَّةَ قَوۡمٖ لَّا يُؤۡمِنُونَ بِٱللَّهِ وَهُم بِٱلۡأٓخِرَةِ هُمۡ كَٰفِرُونَ", "Qala la yateekuma taAAamun turzaqanihi illa nabbatukuma bitaweelihi qabla an yatiyakuma thalikuma mimma AAallamanee rabbee innee taraktu millata qawmin la yuminoona biAllahi wahum bialakhirati hum kafiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 38, "And I have followed the religion of my fathers, Abraham, Isaac and Jacob. And it was not for us to associate anything with Allah. That is from the favor of Allah upon us and upon the people, but most of the people are not grateful", "وَٱتَّبَعۡتُ مِلَّةَ ءَابَآءِيٓ إِبۡرَٰهِيمَ وَإِسۡحَٰقَ وَيَعۡقُوبَۚ مَا كَانَ لَنَآ أَن نُّشۡرِكَ بِٱللَّهِ مِن شَيۡءٖۚ ذَٰلِكَ مِن فَضۡلِ ٱللَّهِ عَلَيۡنَا وَعَلَى ٱلنَّاسِ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَشۡكُرُونَ", "WaittabaAAtu millata abaee ibraheema waishaqa wayaAAqooba ma kana lana an nushrika biAllahi min shayin thalika min fadli Allahi AAalayna waAAala alnnasi walakinna akthara alnnasi la yashkuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 39, "O [my] two companions of prison, are separate lords better or Allah, the One, the Prevailing", "يَٰصَٰحِبَيِ ٱلسِّجۡنِ ءَأَرۡبَابٞ مُّتَفَرِّقُونَ خَيۡرٌ أَمِ ٱللَّهُ ٱلۡوَٰحِدُ ٱلۡقَهَّارُ", "Ya sahibayi alssijni aarbabun mutafarriqoona khayrun ami Allahu alwahidu alqahharu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 40, "You worship not besides Him except [mere] names you have named them, you and your fathers, for which Allah has sent down no authority. Legislation is not but for Allah. He has commanded that you worship not except Him. That is the correct religion, but most of the people do not know", "مَا تَعۡبُدُونَ مِن دُونِهِۦٓ إِلَّآ أَسۡمَآءٗ سَمَّيۡتُمُوهَآ أَنتُمۡ وَءَابَآؤُكُم مَّآ أَنزَلَ ٱللَّهُ بِهَا مِن سُلۡطَٰنٍۚ إِنِ ٱلۡحُكۡمُ إِلَّا لِلَّهِ أَمَرَ أَلَّا تَعۡبُدُوٓاْ إِلَّآ إِيَّاهُۚ ذَٰلِكَ ٱلدِّينُ ٱلۡقَيِّمُ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَعۡلَمُونَ", "Ma taAAbudoona min doonihi illa asmaan sammaytumooha antum waabaokum ma anzala Allahu biha min sultanin ini alhukmu illa lillahi amara alla taAAbudoo illa iyyahu thalika alddeenu alqayyimu walakinna akthara alnnasi la yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 41, "O two companions of prison, as for one of you, he will give drink to his master of wine; but as for the other, he will be crucified, and the birds will eat from his head. The matter has been decreed about which you both inquire", "يَٰصَٰحِبَيِ ٱلسِّجۡنِ أَمَّآ أَحَدُكُمَا فَيَسۡقِي رَبَّهُۥ خَمۡرٗاۖ وَأَمَّا ٱلۡأٓخَرُ فَيُصۡلَبُ فَتَأۡكُلُ ٱلطَّيۡرُ مِن رَّأۡسِهِۦۚ قُضِيَ ٱلۡأَمۡرُ ٱلَّذِي فِيهِ تَسۡتَفۡتِيَانِ", "Ya sahibayi alssijni amma ahadukuma fayasqee rabbahu khamran waamma alakharu fayuslabu fatakulu alttayru min rasihi qudiya alamru allathee feehi tastaftiyani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 42, "And he said to the one whom he knew would go free, ""Mention me before your master."" But Satan made him forget the mention [to] his master, and Joseph remained in prison several years", "وَقَالَ لِلَّذِي ظَنَّ أَنَّهُۥ نَاجٖ مِّنۡهُمَا ٱذۡكُرۡنِي عِندَ رَبِّكَ فَأَنسَىٰهُ ٱلشَّيۡطَٰنُ ذِكۡرَ رَبِّهِۦ فَلَبِثَ فِي ٱلسِّجۡنِ بِضۡعَ سِنِينَ", "Waqala lillathee thanna annahu najin minhuma othkurnee AAinda rabbika faansahu alshshaytanu thikra rabbihi falabitha fee alssijni bidAAa sineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 43, "And [subsequently] the king said, ""Indeed, I have seen [in a dream] seven fat cows being eaten by seven [that were] lean, and seven green spikes [of grain] and others [that were] dry. O eminent ones, explain to me my vision, if you should interpret visions", "وَقَالَ ٱلۡمَلِكُ إِنِّيٓ أَرَىٰ سَبۡعَ بَقَرَٰتٖ سِمَانٖ يَأۡكُلُهُنَّ سَبۡعٌ عِجَافٞ وَسَبۡعَ سُنۢبُلَٰتٍ خُضۡرٖ وَأُخَرَ يَابِسَٰتٖۖ يَـٰٓأَيُّهَا ٱلۡمَلَأُ أَفۡتُونِي فِي رُءۡيَٰيَ إِن كُنتُمۡ لِلرُّءۡيَا تَعۡبُرُونَ", "Waqala almaliku innee ara sabAAa baqaratin simanin yakuluhunna sabAAun AAijafun wasabAAa sunbulatin khudrin waokhara yabisatin ya ayyuha almalao aftoonee fee ruyaya in kuntum lilrruya taAAburoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 44, "They said, ""[It is but] a mixture of false dreams, and we are not learned in the interpretation of dreams", "قَالُوٓاْ أَضۡغَٰثُ أَحۡلَٰمٖۖ وَمَا نَحۡنُ بِتَأۡوِيلِ ٱلۡأَحۡلَٰمِ بِعَٰلِمِينَ", "Qaloo adghathu ahlamin wama nahnu bitaweeli alahlami biAAalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 45, "But the one who was freed and remembered after a time said, ""I will inform you of its interpretation, so send me forth", "وَقَالَ ٱلَّذِي نَجَا مِنۡهُمَا وَٱدَّكَرَ بَعۡدَ أُمَّةٍ أَنَا۠ أُنَبِّئُكُم بِتَأۡوِيلِهِۦ فَأَرۡسِلُونِ", "Waqala allathee naja minhuma waiddakara baAAda ommatin ana onabbiokum bitaweelihi faarsilooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 46, "[He said], ""Joseph, O man of truth, explain to us about seven fat cows eaten by seven [that were] lean, and seven green spikes [of grain] and others [that were] dry - that I may return to the people; perhaps they will know [about you]", "يُوسُفُ أَيُّهَا ٱلصِّدِّيقُ أَفۡتِنَا فِي سَبۡعِ بَقَرَٰتٖ سِمَانٖ يَأۡكُلُهُنَّ سَبۡعٌ عِجَافٞ وَسَبۡعِ سُنۢبُلَٰتٍ خُضۡرٖ وَأُخَرَ يَابِسَٰتٖ لَّعَلِّيٓ أَرۡجِعُ إِلَى ٱلنَّاسِ لَعَلَّهُمۡ يَعۡلَمُونَ", "Yoosufu ayyuha alssiddeequ aftina fee sabAAi baqaratin simanin yakuluhunna sabAAun AAijafun wasabAAi sunbulatin khudrin waokhara yabisatin laAAallee arjiAAu ila alnnasi laAAallahum yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 47, "[Joseph] said, ""You will plant for seven years consecutively; and what you harvest leave in its spikes, except a little from which you will eat", "قَالَ تَزۡرَعُونَ سَبۡعَ سِنِينَ دَأَبٗا فَمَا حَصَدتُّمۡ فَذَرُوهُ فِي سُنۢبُلِهِۦٓ إِلَّا قَلِيلٗا مِّمَّا تَأۡكُلُونَ", "Qala tazraAAoona sabAAa sineena daaban fama hasadtum fatharoohu fee sunbulihi illa qaleelan mimma takuloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 48, "Then will come after that seven difficult [years] which will consume what you saved for them, except a little from which you will store", "ثُمَّ يَأۡتِي مِنۢ بَعۡدِ ذَٰلِكَ سَبۡعٞ شِدَادٞ يَأۡكُلۡنَ مَا قَدَّمۡتُمۡ لَهُنَّ إِلَّا قَلِيلٗا مِّمَّا تُحۡصِنُونَ", "Thumma yatee min baAAdi thalika sabAAun shidadun yakulna ma qaddamtum lahunna illa qaleelan mimma tuhsinoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 49, "Then will come after that a year in which the people will be given rain and in which they will press [olives and grapes]", "ثُمَّ يَأۡتِي مِنۢ بَعۡدِ ذَٰلِكَ عَامٞ فِيهِ يُغَاثُ ٱلنَّاسُ وَفِيهِ يَعۡصِرُونَ", "Thumma yatee min baAAdi thalika AAamun feehi yughathu alnnasu wafeehi yaAAsiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 50, "And the king said, ""Bring him to me."" But when the messenger came to him, [Joseph] said, ""Return to your master and ask him what is the case of the women who cut their hands. Indeed, my Lord is Knowing of their plan", "وَقَالَ ٱلۡمَلِكُ ٱئۡتُونِي بِهِۦۖ فَلَمَّا جَآءَهُ ٱلرَّسُولُ قَالَ ٱرۡجِعۡ إِلَىٰ رَبِّكَ فَسۡـَٔلۡهُ مَا بَالُ ٱلنِّسۡوَةِ ٱلَّـٰتِي قَطَّعۡنَ أَيۡدِيَهُنَّۚ إِنَّ رَبِّي بِكَيۡدِهِنَّ عَلِيمٞ", "Waqala almaliku itoonee bihi falamma jaahu alrrasoolu qala irjiAA ila rabbika faisalhu ma balu alnniswati allatee qattaAAna aydiyahunna inna rabbee bikaydihinna AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 51, "Said [the king to the women], ""What was your condition when you sought to seduce Joseph?"" They said, ""Perfect is Allah! We know about him no evil."" The wife of al-'Azeez said, ""Now the truth has become evident. It was I who sought to seduce him, and indeed, he is of the truthful", "قَالَ مَا خَطۡبُكُنَّ إِذۡ رَٰوَدتُّنَّ يُوسُفَ عَن نَّفۡسِهِۦۚ قُلۡنَ حَٰشَ لِلَّهِ مَا عَلِمۡنَا عَلَيۡهِ مِن سُوٓءٖۚ قَالَتِ ٱمۡرَأَتُ ٱلۡعَزِيزِ ٱلۡـَٰٔنَ حَصۡحَصَ ٱلۡحَقُّ أَنَا۠ رَٰوَدتُّهُۥ عَن نَّفۡسِهِۦ وَإِنَّهُۥ لَمِنَ ٱلصَّـٰدِقِينَ", "Qala ma khatbukunna ith rawadtunna yoosufa AAan nafsihi qulna hasha lillahi ma AAalimna AAalayhi min sooin qalati imraatu alAAazeezi alana hashasa alhaqqu ana rawadtuhu AAan nafsihi wainnahu lamina alssadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 52, "That is so al-'Azeez will know that I did not betray him in [his] absence and that Allah does not guide the plan of betrayers", "ذَٰلِكَ لِيَعۡلَمَ أَنِّي لَمۡ أَخُنۡهُ بِٱلۡغَيۡبِ وَأَنَّ ٱللَّهَ لَا يَهۡدِي كَيۡدَ ٱلۡخَآئِنِينَ", "Thalika liyaAAlama annee lam akhunhu bialghaybi waanna Allaha la yahdee kayda alkhaineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 53, "And I do not acquit myself. Indeed, the soul is a persistent enjoiner of evil, except those upon which my Lord has mercy. Indeed, my Lord is Forgiving and Merciful", "۞وَمَآ أُبَرِّئُ نَفۡسِيٓۚ إِنَّ ٱلنَّفۡسَ لَأَمَّارَةُۢ بِٱلسُّوٓءِ إِلَّا مَا رَحِمَ رَبِّيٓۚ إِنَّ رَبِّي غَفُورٞ رَّحِيمٞ", "Wama obarrio nafsee inna alnnafsa laammaratun bialssooi illa ma rahima rabbee inna rabbee ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 54, "And the king said, ""Bring him to me; I will appoint him exclusively for myself."" And when he spoke to him, he said, ""Indeed, you are today established [in position] and trusted", "وَقَالَ ٱلۡمَلِكُ ٱئۡتُونِي بِهِۦٓ أَسۡتَخۡلِصۡهُ لِنَفۡسِيۖ فَلَمَّا كَلَّمَهُۥ قَالَ إِنَّكَ ٱلۡيَوۡمَ لَدَيۡنَا مَكِينٌ أَمِينٞ", "Waqala almaliku itoonee bihi astakhlishu linafsee falamma kallamahu qala innaka alyawma ladayna makeenun ameenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 55, "[Joseph] said, ""Appoint me over the storehouses of the land. Indeed, I will be a knowing guardian", "قَالَ ٱجۡعَلۡنِي عَلَىٰ خَزَآئِنِ ٱلۡأَرۡضِۖ إِنِّي حَفِيظٌ عَلِيمٞ", "Qala ijAAalnee AAala khazaini alardi innee hafeethun AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 56, "And thus We established Joseph in the land to settle therein wherever he willed. We touch with Our mercy whom We will, and We do not allow to be lost the reward of those who do good", "وَكَذَٰلِكَ مَكَّنَّا لِيُوسُفَ فِي ٱلۡأَرۡضِ يَتَبَوَّأُ مِنۡهَا حَيۡثُ يَشَآءُۚ نُصِيبُ بِرَحۡمَتِنَا مَن نَّشَآءُۖ وَلَا نُضِيعُ أَجۡرَ ٱلۡمُحۡسِنِينَ", "Wakathalika makanna liyoosufa fee alardi yatabawwao minha haythu yashao nuseebu birahmatina man nashao wala nudeeAAu ajra almuhsineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 57, "And the reward of the Hereafter is better for those who believed and were fearing Allah", "وَلَأَجۡرُ ٱلۡأٓخِرَةِ خَيۡرٞ لِّلَّذِينَ ءَامَنُواْ وَكَانُواْ يَتَّقُونَ", "Walaajru alakhirati khayrun lillatheena amanoo wakanoo yattaqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 58, "And the brothers of Joseph came [seeking food], and they entered upon him; and he recognized them, but he was to them unknown", "وَجَآءَ إِخۡوَةُ يُوسُفَ فَدَخَلُواْ عَلَيۡهِ فَعَرَفَهُمۡ وَهُمۡ لَهُۥ مُنكِرُونَ", "Wajaa ikhwatu yoosufa fadakhaloo AAalayhi faAAarafahum wahum lahu munkiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 59, "And when he had furnished them with their supplies, he said, ""Bring me a brother of yours from your father. Do not you see that I give full measure and that I am the best of accommodators", "وَلَمَّا جَهَّزَهُم بِجَهَازِهِمۡ قَالَ ٱئۡتُونِي بِأَخٖ لَّكُم مِّنۡ أَبِيكُمۡۚ أَلَا تَرَوۡنَ أَنِّيٓ أُوفِي ٱلۡكَيۡلَ وَأَنَا۠ خَيۡرُ ٱلۡمُنزِلِينَ", "Walamma jahhazahum bijahazihim qala itoonee biakhin lakum min abeekum ala tarawna annee oofee alkayla waana khayru almunzileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 60, "But if you do not bring him to me, no measure will there be [hereafter] for you from me, nor will you approach me", "فَإِن لَّمۡ تَأۡتُونِي بِهِۦ فَلَا كَيۡلَ لَكُمۡ عِندِي وَلَا تَقۡرَبُونِ", "Fain lam tatoonee bihi fala kayla lakum AAindee wala taqrabooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 61, "They said, ""We will attempt to dissuade his father from [keeping] him, and indeed, we will do [it]", "قَالُواْ سَنُرَٰوِدُ عَنۡهُ أَبَاهُ وَإِنَّا لَفَٰعِلُونَ", "Qaloo sanurawidu AAanhu abahu wainna lafaAAiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 62, "And [Joseph] said to his servants, ""Put their merchandise into their saddlebags so they might recognize it when they have gone back to their people that perhaps they will [again] return", "وَقَالَ لِفِتۡيَٰنِهِ ٱجۡعَلُواْ بِضَٰعَتَهُمۡ فِي رِحَالِهِمۡ لَعَلَّهُمۡ يَعۡرِفُونَهَآ إِذَا ٱنقَلَبُوٓاْ إِلَىٰٓ أَهۡلِهِمۡ لَعَلَّهُمۡ يَرۡجِعُونَ", "Waqala lifityanihi ijAAaloo bidaAAatahum fee rihalihim laAAallahum yaAArifoonaha itha inqalaboo ila ahlihim laAAallahum yarjiAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 63, "So when they returned to their father, they said, ""O our father, [further] measure has been denied to us, so send with us our brother [that] we will be given measure. And indeed, we will be his guardians", "فَلَمَّا رَجَعُوٓاْ إِلَىٰٓ أَبِيهِمۡ قَالُواْ يَـٰٓأَبَانَا مُنِعَ مِنَّا ٱلۡكَيۡلُ فَأَرۡسِلۡ مَعَنَآ أَخَانَا نَكۡتَلۡ وَإِنَّا لَهُۥ لَحَٰفِظُونَ", "Falamma rajaAAoo ila abeehim qaloo ya abana muniAAa minna alkaylu faarsil maAAana akhana naktal wainna lahu lahafithoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 64, "He said, ""Should I entrust you with him except [under coercion] as I entrusted you with his brother before? But Allah is the best guardian, and He is the most merciful of the merciful", "قَالَ هَلۡ ءَامَنُكُمۡ عَلَيۡهِ إِلَّا كَمَآ أَمِنتُكُمۡ عَلَىٰٓ أَخِيهِ مِن قَبۡلُ فَٱللَّهُ خَيۡرٌ حَٰفِظٗاۖ وَهُوَ أَرۡحَمُ ٱلرَّـٰحِمِينَ", "Qala hal amanukum AAalayhi illa kama amintukum AAala akheehi min qablu faAllahu khayrun hafithan wahuwa arhamu alrrahimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 65, "And when they opened their baggage, they found their merchandise returned to them. They said, ""O our father, what [more] could we desire? This is our merchandise returned to us. And we will obtain supplies for our family and protect our brother and obtain an increase of a camel's load; that is an easy measurement", "وَلَمَّا فَتَحُواْ مَتَٰعَهُمۡ وَجَدُواْ بِضَٰعَتَهُمۡ رُدَّتۡ إِلَيۡهِمۡۖ قَالُواْ يَـٰٓأَبَانَا مَا نَبۡغِيۖ هَٰذِهِۦ بِضَٰعَتُنَا رُدَّتۡ إِلَيۡنَاۖ وَنَمِيرُ أَهۡلَنَا وَنَحۡفَظُ أَخَانَا وَنَزۡدَادُ كَيۡلَ بَعِيرٖۖ ذَٰلِكَ كَيۡلٞ يَسِيرٞ", "Walamma fatahoo mataAAahum wajadoo bidaAAatahum ruddat ilayhim qaloo ya abana ma nabghee hathihi bidaAAatuna ruddat ilayna wanameeru ahlana wanahfathu akhana wanazdadu kayla baAAeerin thalika kaylun yaseerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 66, "[Jacob] said, ""Never will I send him with you until you give me a promise by Allah that you will bring him [back] to me, unless you should be surrounded by enemies."" And when they had given their promise, he said, ""Allah, over what we say, is Witness", "قَالَ لَنۡ أُرۡسِلَهُۥ مَعَكُمۡ حَتَّىٰ تُؤۡتُونِ مَوۡثِقٗا مِّنَ ٱللَّهِ لَتَأۡتُنَّنِي بِهِۦٓ إِلَّآ أَن يُحَاطَ بِكُمۡۖ فَلَمَّآ ءَاتَوۡهُ مَوۡثِقَهُمۡ قَالَ ٱللَّهُ عَلَىٰ مَا نَقُولُ وَكِيلٞ", "Qala lan orsilahu maAAakum hatta tutooni mawthiqan mina Allahi latatunnanee bihi illa an yuhata bikum falamma atawhu mawthiqahum qala Allahu AAala ma naqoolu wakeelun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 67, "And he said, ""O my sons, do not enter from one gate but enter from different gates; and I cannot avail you against [the decree of] Allah at all. The decision is only for Allah; upon Him I have relied, and upon Him let those who would rely [indeed] rely", "وَقَالَ يَٰبَنِيَّ لَا تَدۡخُلُواْ مِنۢ بَابٖ وَٰحِدٖ وَٱدۡخُلُواْ مِنۡ أَبۡوَٰبٖ مُّتَفَرِّقَةٖۖ وَمَآ أُغۡنِي عَنكُم مِّنَ ٱللَّهِ مِن شَيۡءٍۖ إِنِ ٱلۡحُكۡمُ إِلَّا لِلَّهِۖ عَلَيۡهِ تَوَكَّلۡتُۖ وَعَلَيۡهِ فَلۡيَتَوَكَّلِ ٱلۡمُتَوَكِّلُونَ", "Waqala ya baniyya la tadkhuloo min babin wahidin waodkhuloo min abwabin mutafarriqatin wama oghnee AAankum mina Allahi min shayin ini alhukmu illa lillahi AAalayhi tawakkaltu waAAalayhi falyatawakkali almutawakkiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 68, "And when they entered from where their father had ordered them, it did not avail them against Allah at all except [it was] a need within the soul of Jacob, which he satisfied. And indeed, he was a possessor of knowledge because of what We had taught him, but most of the people do not know", "وَلَمَّا دَخَلُواْ مِنۡ حَيۡثُ أَمَرَهُمۡ أَبُوهُم مَّا كَانَ يُغۡنِي عَنۡهُم مِّنَ ٱللَّهِ مِن شَيۡءٍ إِلَّا حَاجَةٗ فِي نَفۡسِ يَعۡقُوبَ قَضَىٰهَاۚ وَإِنَّهُۥ لَذُو عِلۡمٖ لِّمَا عَلَّمۡنَٰهُ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَعۡلَمُونَ", "Walamma dakhaloo min haythu amarahum aboohum ma kana yughnee AAanhum mina Allahi min shayin illa hajatan fee nafsi yaAAqooba qadaha wainnahu lathoo AAilmin lima AAallamnahu walakinna akthara alnnasi la yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 69, "And when they entered upon Joseph, he took his brother to himself; he said, ""Indeed, I am your brother, so do not despair over what they used to do [to me]", "وَلَمَّا دَخَلُواْ عَلَىٰ يُوسُفَ ءَاوَىٰٓ إِلَيۡهِ أَخَاهُۖ قَالَ إِنِّيٓ أَنَا۠ أَخُوكَ فَلَا تَبۡتَئِسۡ بِمَا كَانُواْ يَعۡمَلُونَ", "Walamma dakhaloo AAala yoosufa awa ilayhi akhahu qala innee ana akhooka fala tabtais bima kanoo yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 70, "So when he had furnished them with their supplies, he put the [gold measuring] bowl into the bag of his brother. Then an announcer called out, ""O caravan, indeed you are thieves", "فَلَمَّا جَهَّزَهُم بِجَهَازِهِمۡ جَعَلَ ٱلسِّقَايَةَ فِي رَحۡلِ أَخِيهِ ثُمَّ أَذَّنَ مُؤَذِّنٌ أَيَّتُهَا ٱلۡعِيرُ إِنَّكُمۡ لَسَٰرِقُونَ", "Falamma jahhazahum bijahazihim jaAAala alssiqayata fee rahli akheehi thumma aththana muaththinun ayyatuha alAAeeru innakum lasariqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 71, "They said while approaching them, ""What is it you are missing", "قَالُواْ وَأَقۡبَلُواْ عَلَيۡهِم مَّاذَا تَفۡقِدُونَ", "Qaloo waaqbaloo AAalayhim matha tafqidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 72, "They said, ""We are missing the measure of the king. And for he who produces it is [the reward of] a camel's load, and I am responsible for it", "قَالُواْ نَفۡقِدُ صُوَاعَ ٱلۡمَلِكِ وَلِمَن جَآءَ بِهِۦ حِمۡلُ بَعِيرٖ وَأَنَا۠ بِهِۦ زَعِيمٞ", "Qaloo nafqidu suwaAAa almaliki waliman jaa bihi himlu baAAeerin waana bihi zaAAeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 73, "They said, ""By Allah, you have certainly known that we did not come to cause corruption in the land, and we have not been thieves", "قَالُواْ تَٱللَّهِ لَقَدۡ عَلِمۡتُم مَّا جِئۡنَا لِنُفۡسِدَ فِي ٱلۡأَرۡضِ وَمَا كُنَّا سَٰرِقِينَ", "Qaloo taAllahi laqad AAalimtum ma jina linufsida fee alardi wama kunna sariqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 74, "The accusers said, ""Then what would be its recompense if you should be liars", "قَالُواْ فَمَا جَزَـٰٓؤُهُۥٓ إِن كُنتُمۡ كَٰذِبِينَ", "Qaloo fama jazaohu in kuntum kathibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 75, "[The brothers] said, ""Its recompense is that he in whose bag it is found - he [himself] will be its recompense. Thus do we recompense the wrongdoers", "قَالُواْ جَزَـٰٓؤُهُۥ مَن وُجِدَ فِي رَحۡلِهِۦ فَهُوَ جَزَـٰٓؤُهُۥۚ كَذَٰلِكَ نَجۡزِي ٱلظَّـٰلِمِينَ", "Qaloo jazaohu man wujida fee rahlihi fahuwa jazaohu kathalika najzee alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 76, "So he began [the search] with their bags before the bag of his brother; then he extracted it from the bag of his brother. Thus did We plan for Joseph. He could not have taken his brother within the religion of the king except that Allah willed. We raise in degrees whom We will, but over every possessor of knowledge is one [more] knowing", "فَبَدَأَ بِأَوۡعِيَتِهِمۡ قَبۡلَ وِعَآءِ أَخِيهِ ثُمَّ ٱسۡتَخۡرَجَهَا مِن وِعَآءِ أَخِيهِۚ كَذَٰلِكَ كِدۡنَا لِيُوسُفَۖ مَا كَانَ لِيَأۡخُذَ أَخَاهُ فِي دِينِ ٱلۡمَلِكِ إِلَّآ أَن يَشَآءَ ٱللَّهُۚ نَرۡفَعُ دَرَجَٰتٖ مَّن نَّشَآءُۗ وَفَوۡقَ كُلِّ ذِي عِلۡمٍ عَلِيمٞ", "Fabadaa biawAAiyatihim qabla wiAAai akheehi thumma istakhrajaha min wiAAai akheehi kathalika kidna liyoosufa ma kana liyakhutha akhahu fee deeni almaliki illa an yashaa Allahu narfaAAu darajatin man nashao wafawqa kulli thee AAilmin AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 77, "They said, ""If he steals - a brother of his has stolen before."" But Joseph kept it within himself and did not reveal it to them. He said, ""You are worse in position, and Allah is most knowing of what you describe", "۞قَالُوٓاْ إِن يَسۡرِقۡ فَقَدۡ سَرَقَ أَخٞ لَّهُۥ مِن قَبۡلُۚ فَأَسَرَّهَا يُوسُفُ فِي نَفۡسِهِۦ وَلَمۡ يُبۡدِهَا لَهُمۡۚ قَالَ أَنتُمۡ شَرّٞ مَّكَانٗاۖ وَٱللَّهُ أَعۡلَمُ بِمَا تَصِفُونَ", "Qaloo in yasriq faqad saraqa akhun lahu min qablu faasarraha yoosufu fee nafsihi walam yubdiha lahum qala antum sharrun makanan waAllahu aAAlamu bima tasifoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 78, "They said, ""O 'Azeez, indeed he has a father [who is] an old man, so take one of us in place of him. Indeed, we see you as a doer of good", "قَالُواْ يَـٰٓأَيُّهَا ٱلۡعَزِيزُ إِنَّ لَهُۥٓ أَبٗا شَيۡخٗا كَبِيرٗا فَخُذۡ أَحَدَنَا مَكَانَهُۥٓۖ إِنَّا نَرَىٰكَ مِنَ ٱلۡمُحۡسِنِينَ", "Qaloo ya ayyuha alAAazeezu inna lahu aban shaykhan kabeeran fakhuth ahadana makanahu inna naraka mina almuhsineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 79, "He said, ""[I seek] the refuge of Allah [to prevent] that we take except him with whom we found our possession. Indeed, we would then be unjust", "قَالَ مَعَاذَ ٱللَّهِ أَن نَّأۡخُذَ إِلَّا مَن وَجَدۡنَا مَتَٰعَنَا عِندَهُۥٓ إِنَّآ إِذٗا لَّظَٰلِمُونَ", "Qala maAAatha Allahi an nakhutha illa man wajadna mataAAana AAindahu inna ithan lathalimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 80, "So when they had despaired of him, they secluded themselves in private consultation. The eldest of them said, ""Do you not know that your father has taken upon you an oath by Allah and [that] before you failed in [your duty to] Joseph? So I will never leave [this] land until my father permits me or Allah decides for me, and He is the best of judges", "فَلَمَّا ٱسۡتَيۡـَٔسُواْ مِنۡهُ خَلَصُواْ نَجِيّٗاۖ قَالَ كَبِيرُهُمۡ أَلَمۡ تَعۡلَمُوٓاْ أَنَّ أَبَاكُمۡ قَدۡ أَخَذَ عَلَيۡكُم مَّوۡثِقٗا مِّنَ ٱللَّهِ وَمِن قَبۡلُ مَا فَرَّطتُمۡ فِي يُوسُفَۖ فَلَنۡ أَبۡرَحَ ٱلۡأَرۡضَ حَتَّىٰ يَأۡذَنَ لِيٓ أَبِيٓ أَوۡ يَحۡكُمَ ٱللَّهُ لِيۖ وَهُوَ خَيۡرُ ٱلۡحَٰكِمِينَ", "Falamma istayasoo minhu khalasoo najiyyan qala kabeeruhum alam taAAlamoo anna abakum qad akhatha AAalaykum mawthiqan mina Allahi wamin qablu ma farrattum fee yoosufa falan abraha alarda hatta yathana lee abee aw yahkuma Allahu lee wahuwa khayru alhakimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 81, "Return to your father and say, ""O our father, indeed your son has stolen, and we did not testify except to what we knew. And we were not witnesses of the unseen", "ٱرۡجِعُوٓاْ إِلَىٰٓ أَبِيكُمۡ فَقُولُواْ يَـٰٓأَبَانَآ إِنَّ ٱبۡنَكَ سَرَقَ وَمَا شَهِدۡنَآ إِلَّا بِمَا عَلِمۡنَا وَمَا كُنَّا لِلۡغَيۡبِ حَٰفِظِينَ", "IrjiAAoo ila abeekum faqooloo ya abana inna ibnaka saraqa wama shahidna illa bima AAalimna wama kunna lilghaybi hafitheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 82, "And ask the city in which we were and the caravan in which we came - and indeed, we are truthful", "وَسۡـَٔلِ ٱلۡقَرۡيَةَ ٱلَّتِي كُنَّا فِيهَا وَٱلۡعِيرَ ٱلَّتِيٓ أَقۡبَلۡنَا فِيهَاۖ وَإِنَّا لَصَٰدِقُونَ", "Waisali alqaryata allatee kunna feeha waalAAeera allatee aqbalna feeha wainna lasadiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 83, "[Jacob] said, ""Rather, your souls have enticed you to something, so patience is most fitting. Perhaps Allah will bring them to me all together. Indeed it is He who is the Knowing, the Wise", "قَالَ بَلۡ سَوَّلَتۡ لَكُمۡ أَنفُسُكُمۡ أَمۡرٗاۖ فَصَبۡرٞ جَمِيلٌۖ عَسَى ٱللَّهُ أَن يَأۡتِيَنِي بِهِمۡ جَمِيعًاۚ إِنَّهُۥ هُوَ ٱلۡعَلِيمُ ٱلۡحَكِيمُ", "Qala bal sawwalat lakum anfusukum amran fasabrun jameelun AAasa Allahu an yatiyanee bihim jameeAAan innahu huwa alAAaleemu alhakeemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 84, "And he turned away from them and said, ""Oh, my sorrow over Joseph,"" and his eyes became white from grief, for he was [of that] a suppressor", "وَتَوَلَّىٰ عَنۡهُمۡ وَقَالَ يَـٰٓأَسَفَىٰ عَلَىٰ يُوسُفَ وَٱبۡيَضَّتۡ عَيۡنَاهُ مِنَ ٱلۡحُزۡنِ فَهُوَ كَظِيمٞ", "Watawalla AAanhum waqala ya asafa AAala yoosufa waibyaddat AAaynahu mina alhuzni fahuwa katheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 85, "They said, ""By Allah, you will not cease remembering Joseph until you become fatally ill or become of those who perish", "قَالُواْ تَٱللَّهِ تَفۡتَؤُاْ تَذۡكُرُ يُوسُفَ حَتَّىٰ تَكُونَ حَرَضًا أَوۡ تَكُونَ مِنَ ٱلۡهَٰلِكِينَ", "Qaloo taAllahi taftao tathkuru yoosufa hatta takoona haradan aw takoona mina alhalikeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 86, "He said, ""I only complain of my suffering and my grief to Allah, and I know from Allah that which you do not know", "قَالَ إِنَّمَآ أَشۡكُواْ بَثِّي وَحُزۡنِيٓ إِلَى ٱللَّهِ وَأَعۡلَمُ مِنَ ٱللَّهِ مَا لَا تَعۡلَمُونَ", "Qala innama ashkoo baththee wahuznee ila Allahi waaAAlamu mina Allahi ma la taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 87, "O my sons, go and find out about Joseph and his brother and despair not of relief from Allah. Indeed, no one despairs of relief from Allah except the disbelieving people", "يَٰبَنِيَّ ٱذۡهَبُواْ فَتَحَسَّسُواْ مِن يُوسُفَ وَأَخِيهِ وَلَا تَاْيۡـَٔسُواْ مِن رَّوۡحِ ٱللَّهِۖ إِنَّهُۥ لَا يَاْيۡـَٔسُ مِن رَّوۡحِ ٱللَّهِ إِلَّا ٱلۡقَوۡمُ ٱلۡكَٰفِرُونَ", "Ya baniyya ithhaboo fatahassasoo min yoosufa waakheehi wala tayasoo min rawhi Allahi innahu la yayasu min rawhi Allahi illa alqawmu alkafiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 88, "So when they entered upon Joseph, they said, ""O 'Azeez, adversity has touched us and our family, and we have come with goods poor in quality, but give us full measure and be charitable to us. Indeed, Allah rewards the charitable", "فَلَمَّا دَخَلُواْ عَلَيۡهِ قَالُواْ يَـٰٓأَيُّهَا ٱلۡعَزِيزُ مَسَّنَا وَأَهۡلَنَا ٱلضُّرُّ وَجِئۡنَا بِبِضَٰعَةٖ مُّزۡجَىٰةٖ فَأَوۡفِ لَنَا ٱلۡكَيۡلَ وَتَصَدَّقۡ عَلَيۡنَآۖ إِنَّ ٱللَّهَ يَجۡزِي ٱلۡمُتَصَدِّقِينَ", "Falamma dakhaloo AAalayhi qaloo ya ayyuha alAAazeezu massana waahlana alddurru wajina bibidaAAatin muzjatin faawfi lana alkayla watasaddaq AAalayna inna Allaha yajzee almutasaddiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 89, "He said, ""Do you know what you did with Joseph and his brother when you were ignorant", "قَالَ هَلۡ عَلِمۡتُم مَّا فَعَلۡتُم بِيُوسُفَ وَأَخِيهِ إِذۡ أَنتُمۡ جَٰهِلُونَ", "Qala hal AAalimtum ma faAAaltum biyoosufa waakheehi ith antum jahiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 90, "They said, ""Are you indeed Joseph?"" He said ""I am Joseph, and this is my brother. Allah has certainly favored us. Indeed, he who fears Allah and is patient, then indeed, Allah does not allow to be lost the reward of those who do good", "قَالُوٓاْ أَءِنَّكَ لَأَنتَ يُوسُفُۖ قَالَ أَنَا۠ يُوسُفُ وَهَٰذَآ أَخِيۖ قَدۡ مَنَّ ٱللَّهُ عَلَيۡنَآۖ إِنَّهُۥ مَن يَتَّقِ وَيَصۡبِرۡ فَإِنَّ ٱللَّهَ لَا يُضِيعُ أَجۡرَ ٱلۡمُحۡسِنِينَ", "Qaloo ainnaka laanta yoosufa qala ana yoosufu wahatha akhee qad manna Allahu AAalayna innahu man yattaqi wayasbir fainna Allaha la yudeeAAu ajra almuhsineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 91, "They said, ""By Allah, certainly has Allah preferred you over us, and indeed, we have been sinners", "قَالُواْ تَٱللَّهِ لَقَدۡ ءَاثَرَكَ ٱللَّهُ عَلَيۡنَا وَإِن كُنَّا لَخَٰطِـِٔينَ", "Qaloo taAllahi laqad atharaka Allahu AAalayna wain kunna lakhatieena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 92, "He said, ""No blame will there be upon you today. Allah will forgive you; and He is the most merciful of the merciful", "قَالَ لَا تَثۡرِيبَ عَلَيۡكُمُ ٱلۡيَوۡمَۖ يَغۡفِرُ ٱللَّهُ لَكُمۡۖ وَهُوَ أَرۡحَمُ ٱلرَّـٰحِمِينَ", "Qala la tathreeba AAalaykumu alyawma yaghfiru Allahu lakum wahuwa arhamu alrrahimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 93, "Take this, my shirt, and cast it over the face of my father; he will become seeing. And bring me your family, all together", "ٱذۡهَبُواْ بِقَمِيصِي هَٰذَا فَأَلۡقُوهُ عَلَىٰ وَجۡهِ أَبِي يَأۡتِ بَصِيرٗا وَأۡتُونِي بِأَهۡلِكُمۡ أَجۡمَعِينَ", "Ithhaboo biqameesee hatha faalqoohu AAala wajhi abee yati baseeran watoonee biahlikum ajmaAAeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 94, "And when the caravan departed [from Egypt], their father said, ""Indeed, I find the smell of Joseph [and would say that he was alive] if you did not think me weakened in mind", "وَلَمَّا فَصَلَتِ ٱلۡعِيرُ قَالَ أَبُوهُمۡ إِنِّي لَأَجِدُ رِيحَ يُوسُفَۖ لَوۡلَآ أَن تُفَنِّدُونِ", "Walamma fasalati alAAeeru qala aboohum innee laajidu reeha yoosufa lawla an tufannidooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 95, "They said, ""By Allah, indeed you are in your [same] old error", "قَالُواْ تَٱللَّهِ إِنَّكَ لَفِي ضَلَٰلِكَ ٱلۡقَدِيمِ", "Qaloo taAllahi innaka lafee dalalika alqadeemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 96, "And when the bearer of good tidings arrived, he cast it over his face, and he returned [once again] seeing. He said, ""Did I not tell you that I know from Allah that which you do not know", "فَلَمَّآ أَن جَآءَ ٱلۡبَشِيرُ أَلۡقَىٰهُ عَلَىٰ وَجۡهِهِۦ فَٱرۡتَدَّ بَصِيرٗاۖ قَالَ أَلَمۡ أَقُل لَّكُمۡ إِنِّيٓ أَعۡلَمُ مِنَ ٱللَّهِ مَا لَا تَعۡلَمُونَ", "Falamma an jaa albasheeru alqahu AAala wajhihi fairtadda baseeran qala alam aqul lakum innee aAAlamu mina Allahi ma la taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 97, "They said, ""O our father, ask for us forgiveness of our sins; indeed, we have been sinners", "قَالُواْ يَـٰٓأَبَانَا ٱسۡتَغۡفِرۡ لَنَا ذُنُوبَنَآ إِنَّا كُنَّا خَٰطِـِٔينَ", "Qaloo ya abana istaghfir lana thunoobana inna kunna khatieena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 98, "He said, ""I will ask forgiveness for you from my Lord. Indeed, it is He who is the Forgiving, the Merciful", "قَالَ سَوۡفَ أَسۡتَغۡفِرُ لَكُمۡ رَبِّيٓۖ إِنَّهُۥ هُوَ ٱلۡغَفُورُ ٱلرَّحِيمُ", "Qala sawfa astaghfiru lakum rabbee innahu huwa alghafooru alrraheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 99, "And when they entered upon Joseph, he took his parents to himself and said, ""Enter Egypt, Allah willing, safe [and secure]", "فَلَمَّا دَخَلُواْ عَلَىٰ يُوسُفَ ءَاوَىٰٓ إِلَيۡهِ أَبَوَيۡهِ وَقَالَ ٱدۡخُلُواْ مِصۡرَ إِن شَآءَ ٱللَّهُ ءَامِنِينَ", "Falamma dakhaloo AAala yoosufa awa ilayhi abawayhi waqala odkhuloo misra in shaa Allahu amineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 100, "And he raised his parents upon the throne, and they bowed to him in prostration. And he said, ""O my father, this is the explanation of my vision of before. My Lord has made it reality. And He was certainly good to me when He took me out of prison and brought you [here] from bedouin life after Satan had induced [estrangement] between me and my brothers. Indeed, my Lord is Subtle in what He wills. Indeed, it is He who is the Knowing, the Wise", "وَرَفَعَ أَبَوَيۡهِ عَلَى ٱلۡعَرۡشِ وَخَرُّواْ لَهُۥ سُجَّدٗاۖ وَقَالَ يَـٰٓأَبَتِ هَٰذَا تَأۡوِيلُ رُءۡيَٰيَ مِن قَبۡلُ قَدۡ جَعَلَهَا رَبِّي حَقّٗاۖ وَقَدۡ أَحۡسَنَ بِيٓ إِذۡ أَخۡرَجَنِي مِنَ ٱلسِّجۡنِ وَجَآءَ بِكُم مِّنَ ٱلۡبَدۡوِ مِنۢ بَعۡدِ أَن نَّزَغَ ٱلشَّيۡطَٰنُ بَيۡنِي وَبَيۡنَ إِخۡوَتِيٓۚ إِنَّ رَبِّي لَطِيفٞ لِّمَا يَشَآءُۚ إِنَّهُۥ هُوَ ٱلۡعَلِيمُ ٱلۡحَكِيمُ", "WarafaAAa abawayhi AAala alAAarshi wakharroo lahu sujjadan waqala ya abati hatha taweelu ruyaya min qablu qad jaAAalaha rabbee haqqan waqad ahsana bee ith akhrajanee mina alssijni wajaa bikum mina albadwi min baAAdi an nazagha alshshaytanu baynee wabayna ikhwatee inna rabbee lateefun lima yashao innahu huwa alAAaleemu alhakeemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 101, "My Lord, You have given me [something] of sovereignty and taught me of the interpretation of dreams. Creator of the heavens and earth, You are my protector in this world and in the Hereafter. Cause me to die a Muslim and join me with the righteous", "۞رَبِّ قَدۡ ءَاتَيۡتَنِي مِنَ ٱلۡمُلۡكِ وَعَلَّمۡتَنِي مِن تَأۡوِيلِ ٱلۡأَحَادِيثِۚ فَاطِرَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ أَنتَ وَلِيِّۦ فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِۖ تَوَفَّنِي مُسۡلِمٗا وَأَلۡحِقۡنِي بِٱلصَّـٰلِحِينَ", "Rabbi qad ataytanee mina almulki waAAallamtanee min taweeli alahadeethi fatira alssamawati waalardi anta waliyyee fee alddunya waalakhirati tawaffanee musliman waalhiqnee bialssaliheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 102, "That is from the news of the unseen which We reveal, [O Muhammad], to you. And you were not with them when they put together their plan while they conspired", "ذَٰلِكَ مِنۡ أَنۢبَآءِ ٱلۡغَيۡبِ نُوحِيهِ إِلَيۡكَۖ وَمَا كُنتَ لَدَيۡهِمۡ إِذۡ أَجۡمَعُوٓاْ أَمۡرَهُمۡ وَهُمۡ يَمۡكُرُونَ", "Thalika min anbai alghaybi nooheehi ilayka wama kunta ladayhim ith ajmaAAoo amrahum wahum yamkuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 103, "And most of the people, although you strive [for it], are not believers", "وَمَآ أَكۡثَرُ ٱلنَّاسِ وَلَوۡ حَرَصۡتَ بِمُؤۡمِنِينَ", "Wama aktharu alnnasi walaw harasta bimumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 104, "And you do not ask of them for it any payment. It is not except a reminder to the worlds", "وَمَا تَسۡـَٔلُهُمۡ عَلَيۡهِ مِنۡ أَجۡرٍۚ إِنۡ هُوَ إِلَّا ذِكۡرٞ لِّلۡعَٰلَمِينَ", "Wama tasaluhum AAalayhi min ajrin in huwa illa thikrun lilAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 105, "And how many a sign within the heavens and earth do they pass over while they, therefrom, are turning away", "وَكَأَيِّن مِّنۡ ءَايَةٖ فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ يَمُرُّونَ عَلَيۡهَا وَهُمۡ عَنۡهَا مُعۡرِضُونَ", "Wakaayyin min ayatin fee alssamawati waalardi yamurroona AAalayha wahum AAanha muAAridoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 106, "And most of them believe not in Allah except while they associate others with Him", "وَمَا يُؤۡمِنُ أَكۡثَرُهُم بِٱللَّهِ إِلَّا وَهُم مُّشۡرِكُونَ", "Wama yuminu aktharuhum biAllahi illa wahum mushrikoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 107, "Then do they feel secure that there will not come to them an overwhelming [aspect] of the punishment of Allah or that the Hour will not come upon them suddenly while they do not perceive", "أَفَأَمِنُوٓاْ أَن تَأۡتِيَهُمۡ غَٰشِيَةٞ مِّنۡ عَذَابِ ٱللَّهِ أَوۡ تَأۡتِيَهُمُ ٱلسَّاعَةُ بَغۡتَةٗ وَهُمۡ لَا يَشۡعُرُونَ", "Afaaminoo an tatiyahum ghashiyatun min AAathabi Allahi aw tatiyahumu alssaAAatu baghtatan wahum la yashAAuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 108, "Say, ""This is my way; I invite to Allah with insight, I and those who follow me. And exalted is Allah; and I am not of those who associate others with Him", "قُلۡ هَٰذِهِۦ سَبِيلِيٓ أَدۡعُوٓاْ إِلَى ٱللَّهِۚ عَلَىٰ بَصِيرَةٍ أَنَا۠ وَمَنِ ٱتَّبَعَنِيۖ وَسُبۡحَٰنَ ٱللَّهِ وَمَآ أَنَا۠ مِنَ ٱلۡمُشۡرِكِينَ", "Qul hathihi sabeelee adAAoo ila Allahi AAala baseeratin ana wamani ittabaAAanee wasubhana Allahi wama ana mina almushrikeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 109, "And We sent not before you [as messengers] except men to whom We revealed from among the people of cities. So have they not traveled through the earth and observed how was the end of those before them? And the home of the Hereafter is best for those who fear Allah; then will you not reason", "وَمَآ أَرۡسَلۡنَا مِن قَبۡلِكَ إِلَّا رِجَالٗا نُّوحِيٓ إِلَيۡهِم مِّنۡ أَهۡلِ ٱلۡقُرَىٰٓۗ أَفَلَمۡ يَسِيرُواْ فِي ٱلۡأَرۡضِ فَيَنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلَّذِينَ مِن قَبۡلِهِمۡۗ وَلَدَارُ ٱلۡأٓخِرَةِ خَيۡرٞ لِّلَّذِينَ ٱتَّقَوۡاْۚ أَفَلَا تَعۡقِلُونَ", "Wama arsalna min qablika illa rijalan noohee ilayhim min ahli alqura afalam yaseeroo fee alardi fayanthuroo kayfa kana AAaqibatu allatheena min qablihim waladaru alakhirati khayrun lillatheena ittaqaw afala taAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 110, "[They continued] until, when the messengers despaired and were certain that they had been denied, there came to them Our victory, and whoever We willed was saved. And Our punishment cannot be repelled from the people who are criminals", "حَتَّىٰٓ إِذَا ٱسۡتَيۡـَٔسَ ٱلرُّسُلُ وَظَنُّوٓاْ أَنَّهُمۡ قَدۡ كُذِبُواْ جَآءَهُمۡ نَصۡرُنَا فَنُجِّيَ مَن نَّشَآءُۖ وَلَا يُرَدُّ بَأۡسُنَا عَنِ ٱلۡقَوۡمِ ٱلۡمُجۡرِمِينَ", "Hatta itha istayasa alrrusulu wathannoo annahum qad kuthiboo jaahum nasruna fanujjiya man nashao wala yuraddu basuna AAani alqawmi almujrimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (12, 111, "There was certainly in their stories a lesson for those of understanding. Never was the Qur'an a narration invented, but a confirmation of what was before it and a detailed explanation of all things and guidance and mercy for a people who believe", "لَقَدۡ كَانَ فِي قَصَصِهِمۡ عِبۡرَةٞ لِّأُوْلِي ٱلۡأَلۡبَٰبِۗ مَا كَانَ حَدِيثٗا يُفۡتَرَىٰ وَلَٰكِن تَصۡدِيقَ ٱلَّذِي بَيۡنَ يَدَيۡهِ وَتَفۡصِيلَ كُلِّ شَيۡءٖ وَهُدٗى وَرَحۡمَةٗ لِّقَوۡمٖ يُؤۡمِنُونَ", "Laqad kana fee qasasihim AAibratun liolee alalbabi ma kana hadeethan yuftara walakin tasdeeqa allathee bayna yadayhi watafseela kulli shayin wahudan warahmatan liqawmin yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 1, "Alif, Lam, Meem, Ra. These are the verses of the Book; and what has been revealed to you from your Lord is the truth, but most of the people do not believe", "الٓمٓرۚ تِلۡكَ ءَايَٰتُ ٱلۡكِتَٰبِۗ وَٱلَّذِيٓ أُنزِلَ إِلَيۡكَ مِن رَّبِّكَ ٱلۡحَقُّ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يُؤۡمِنُونَ", "Aliflammeemra tilka ayatu alkitabi waallathee onzila ilayka min rabbika alhaqqu walakinna akthara alnnasi la yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 2, "It is Allah who erected the heavens without pillars that you [can] see; then He established Himself above the Throne and made subject the sun and the moon, each running [its course] for a specified term. He arranges [each] matter; He details the signs that you may, of the meeting with your Lord, be certain", "ٱللَّهُ ٱلَّذِي رَفَعَ ٱلسَّمَٰوَٰتِ بِغَيۡرِ عَمَدٖ تَرَوۡنَهَاۖ ثُمَّ ٱسۡتَوَىٰ عَلَى ٱلۡعَرۡشِۖ وَسَخَّرَ ٱلشَّمۡسَ وَٱلۡقَمَرَۖ كُلّٞ يَجۡرِي لِأَجَلٖ مُّسَمّٗىۚ يُدَبِّرُ ٱلۡأَمۡرَ يُفَصِّلُ ٱلۡأٓيَٰتِ لَعَلَّكُم بِلِقَآءِ رَبِّكُمۡ تُوقِنُونَ", "Allahu allathee rafaAAa alssamawati bighayri AAamadin tarawnaha thumma istawa AAala alAAarshi wasakhkhara alshshamsa waalqamara kullun yajree liajalin musamman yudabbiru alamra yufassilu alayati laAAallakum biliqai rabbikum tooqinoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 3, "And it is He who spread the earth and placed therein firmly set mountains and rivers; and from all of the fruits He made therein two mates; He causes the night to cover the day. Indeed in that are signs for a people who give thought", "وَهُوَ ٱلَّذِي مَدَّ ٱلۡأَرۡضَ وَجَعَلَ فِيهَا رَوَٰسِيَ وَأَنۡهَٰرٗاۖ وَمِن كُلِّ ٱلثَّمَرَٰتِ جَعَلَ فِيهَا زَوۡجَيۡنِ ٱثۡنَيۡنِۖ يُغۡشِي ٱلَّيۡلَ ٱلنَّهَارَۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يَتَفَكَّرُونَ", "Wahuwa allathee madda alarda wajaAAala feeha rawasiya waanharan wamin kulli alththamarati jaAAala feeha zawjayni ithnayni yughshee allayla alnnahara inna fee thalika laayatin liqawmin yatafakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 4, "And within the land are neighboring plots and gardens of grapevines and crops and palm trees, [growing] several from a root or otherwise, watered with one water; but We make some of them exceed others in [quality of] fruit. Indeed in that are signs for a people who reason", "وَفِي ٱلۡأَرۡضِ قِطَعٞ مُّتَجَٰوِرَٰتٞ وَجَنَّـٰتٞ مِّنۡ أَعۡنَٰبٖ وَزَرۡعٞ وَنَخِيلٞ صِنۡوَانٞ وَغَيۡرُ صِنۡوَانٖ يُسۡقَىٰ بِمَآءٖ وَٰحِدٖ وَنُفَضِّلُ بَعۡضَهَا عَلَىٰ بَعۡضٖ فِي ٱلۡأُكُلِۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يَعۡقِلُونَ", "Wafee alardi qitaAAun mutajawiratun wajannatun min aAAnabin wazarAAun wanakheelun sinwanun waghayru sinwanin yusqa bimain wahidin wanufaddilu baAAdaha AAala baAAdin fee alokuli inna fee thalika laayatin liqawmin yaAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 5, "And if you are astonished, [O Muhammad] - then astonishing is their saying, ""When we are dust, will we indeed be [brought] into a new creation?"" Those are the ones who have disbelieved in their Lord, and those will have shackles upon their necks, and those are the companions of the Fire; they will abide therein eternally", "۞وَإِن تَعۡجَبۡ فَعَجَبٞ قَوۡلُهُمۡ أَءِذَا كُنَّا تُرَٰبًا أَءِنَّا لَفِي خَلۡقٖ جَدِيدٍۗ أُوْلَـٰٓئِكَ ٱلَّذِينَ كَفَرُواْ بِرَبِّهِمۡۖ وَأُوْلَـٰٓئِكَ ٱلۡأَغۡلَٰلُ فِيٓ أَعۡنَاقِهِمۡۖ وَأُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلنَّارِۖ هُمۡ فِيهَا خَٰلِدُونَ", "Wain taAAjab faAAajabun qawluhum aitha kunna turaban ainna lafee khalqin jadeedin olaika allatheena kafaroo birabbihim waolaika alaghlalu fee aAAnaqihim waolaika ashabu alnnari hum feeha khalidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 6, "They impatiently urge you to bring about evil before good, while there has already occurred before them similar punishments [to what they demand]. And indeed, your Lord is full of forgiveness for the people despite their wrongdoing, and indeed, your Lord is severe in penalty", "وَيَسۡتَعۡجِلُونَكَ بِٱلسَّيِّئَةِ قَبۡلَ ٱلۡحَسَنَةِ وَقَدۡ خَلَتۡ مِن قَبۡلِهِمُ ٱلۡمَثُلَٰتُۗ وَإِنَّ رَبَّكَ لَذُو مَغۡفِرَةٖ لِّلنَّاسِ عَلَىٰ ظُلۡمِهِمۡۖ وَإِنَّ رَبَّكَ لَشَدِيدُ ٱلۡعِقَابِ", "WayastaAAjiloonaka bialssayyiati qabla alhasanati waqad khalat min qablihimu almathulatu wainna rabbaka lathoo maghfiratin lilnnasi AAala thulmihim wainna rabbaka lashadeedu alAAiqabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 7, "And those who disbelieved say, ""Why has a sign not been sent down to him from his Lord?"" You are only a warner, and for every people is a guide", "وَيَقُولُ ٱلَّذِينَ كَفَرُواْ لَوۡلَآ أُنزِلَ عَلَيۡهِ ءَايَةٞ مِّن رَّبِّهِۦٓۗ إِنَّمَآ أَنتَ مُنذِرٞۖ وَلِكُلِّ قَوۡمٍ هَادٍ", "Wayaqoolu allatheena kafaroo lawla onzila AAalayhi ayatun min rabbihi innama anta munthirun walikulli qawmin hadin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 8, "Allah knows what every female carries and what the wombs lose [prematurely] or exceed. And everything with Him is by due measure", "ٱللَّهُ يَعۡلَمُ مَا تَحۡمِلُ كُلُّ أُنثَىٰ وَمَا تَغِيضُ ٱلۡأَرۡحَامُ وَمَا تَزۡدَادُۚ وَكُلُّ شَيۡءٍ عِندَهُۥ بِمِقۡدَارٍ", "Allahu yaAAlamu ma tahmilu kullu ontha wama tagheedu alarhamu wama tazdadu wakullu shayin AAindahu bimiqdarin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 9, "[He is] Knower of the unseen and the witnessed, the Grand, the Exalted", "عَٰلِمُ ٱلۡغَيۡبِ وَٱلشَّهَٰدَةِ ٱلۡكَبِيرُ ٱلۡمُتَعَالِ", "AAalimu alghaybi waalshshahadati alkabeeru almutaAAali");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 10, "It is the same [to Him] concerning you whether one conceals [his] speech or one publicizes it and whether one is hidden by night or conspicuous [among others] by day", "سَوَآءٞ مِّنكُم مَّنۡ أَسَرَّ ٱلۡقَوۡلَ وَمَن جَهَرَ بِهِۦ وَمَنۡ هُوَ مُسۡتَخۡفِۭ بِٱلَّيۡلِ وَسَارِبُۢ بِٱلنَّهَارِ", "Sawaon minkum man asarra alqawla waman jahara bihi waman huwa mustakhfin biallayli wasaribun bialnnahari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 11, "For each one are successive [angels] before and behind him who protect him by the decree of Allah. Indeed, Allah will not change the condition of a people until they change what is in themselves. And when Allah intends for a people ill, there is no repelling it. And there is not for them besides Him any patron", "لَهُۥ مُعَقِّبَٰتٞ مِّنۢ بَيۡنِ يَدَيۡهِ وَمِنۡ خَلۡفِهِۦ يَحۡفَظُونَهُۥ مِنۡ أَمۡرِ ٱللَّهِۗ إِنَّ ٱللَّهَ لَا يُغَيِّرُ مَا بِقَوۡمٍ حَتَّىٰ يُغَيِّرُواْ مَا بِأَنفُسِهِمۡۗ وَإِذَآ أَرَادَ ٱللَّهُ بِقَوۡمٖ سُوٓءٗا فَلَا مَرَدَّ لَهُۥۚ وَمَا لَهُم مِّن دُونِهِۦ مِن وَالٍ", "Lahu muAAaqqibatun min bayni yadayhi wamin khalfihi yahfathoonahu min amri Allahi inna Allaha la yughayyiru ma biqawmin hatta yughayyiroo ma bianfusihim waitha arada Allahu biqawmin sooan fala maradda lahu wama lahum min doonihi min walin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 12, "It is He who shows you lightening, [causing] fear and aspiration, and generates the heavy clouds", "هُوَ ٱلَّذِي يُرِيكُمُ ٱلۡبَرۡقَ خَوۡفٗا وَطَمَعٗا وَيُنشِئُ ٱلسَّحَابَ ٱلثِّقَالَ", "Huwa allathee yureekumu albarqa khawfan watamaAAan wayunshio alssahaba alththiqala");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 13, "And the thunder exalts [Allah] with praise of Him - and the angels [as well] from fear of Him - and He sends thunderbolts and strikes therewith whom He wills while they dispute about Allah; and He is severe in assault", "وَيُسَبِّحُ ٱلرَّعۡدُ بِحَمۡدِهِۦ وَٱلۡمَلَـٰٓئِكَةُ مِنۡ خِيفَتِهِۦ وَيُرۡسِلُ ٱلصَّوَٰعِقَ فَيُصِيبُ بِهَا مَن يَشَآءُ وَهُمۡ يُجَٰدِلُونَ فِي ٱللَّهِ وَهُوَ شَدِيدُ ٱلۡمِحَالِ", "Wayusabbihu alrraAAdu bihamdihi waalmalaikatu min kheefatihi wayursilu alssawaAAiqa fayuseebu biha man yashao wahum yujadiloona fee Allahi wahuwa shadeedu almihali");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 14, "To Him [alone] is the supplication of truth. And those they call upon besides Him do not respond to them with a thing, except as one who stretches his hands toward water [from afar, calling it] to reach his mouth, but it will not reach it [thus]. And the supplication of the disbelievers is not but in error [i.e. futility]", "لَهُۥ دَعۡوَةُ ٱلۡحَقِّۚ وَٱلَّذِينَ يَدۡعُونَ مِن دُونِهِۦ لَا يَسۡتَجِيبُونَ لَهُم بِشَيۡءٍ إِلَّا كَبَٰسِطِ كَفَّيۡهِ إِلَى ٱلۡمَآءِ لِيَبۡلُغَ فَاهُ وَمَا هُوَ بِبَٰلِغِهِۦۚ وَمَا دُعَآءُ ٱلۡكَٰفِرِينَ إِلَّا فِي ضَلَٰلٖ", "Lahu daAAwatu alhaqqi waallatheena yadAAoona min doonihi la yastajeeboona lahum bishayin illa kabasiti kaffayhi ila almai liyablugha fahu wama huwa bibalighihi wama duAAao alkafireena illa fee dalalin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 15, "And to Allah prostrates whoever is within the heavens and the earth, willingly or by compulsion, and their shadows [as well] in the mornings and the afternoons", "وَلِلَّهِۤ يَسۡجُدُۤ مَن فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ طَوۡعٗا وَكَرۡهٗا وَظِلَٰلُهُم بِٱلۡغُدُوِّ وَٱلۡأٓصَالِ۩", "Walillahi yasjudu man fee alssamawati waalardi tawAAan wakarhan wathilaluhum bialghuduwwi waalasali");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 16, "Say, ""Who is Lord of the heavens and earth?"" Say, ""Allah."" Say, ""Have you then taken besides Him allies not possessing [even] for themselves any benefit or any harm?"" Say, ""Is the blind equivalent to the seeing? Or is darkness equivalent to light? Or have they attributed to Allah partners who created like His creation so that the creation [of each] seemed similar to them?"" Say, ""Allah is the Creator of all things, and He is the One, the Prevailing", "قُلۡ مَن رَّبُّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ قُلِ ٱللَّهُۚ قُلۡ أَفَٱتَّخَذۡتُم مِّن دُونِهِۦٓ أَوۡلِيَآءَ لَا يَمۡلِكُونَ لِأَنفُسِهِمۡ نَفۡعٗا وَلَا ضَرّٗاۚ قُلۡ هَلۡ يَسۡتَوِي ٱلۡأَعۡمَىٰ وَٱلۡبَصِيرُ أَمۡ هَلۡ تَسۡتَوِي ٱلظُّلُمَٰتُ وَٱلنُّورُۗ أَمۡ جَعَلُواْ لِلَّهِ شُرَكَآءَ خَلَقُواْ كَخَلۡقِهِۦ فَتَشَٰبَهَ ٱلۡخَلۡقُ عَلَيۡهِمۡۚ قُلِ ٱللَّهُ خَٰلِقُ كُلِّ شَيۡءٖ وَهُوَ ٱلۡوَٰحِدُ ٱلۡقَهَّـٰرُ", "Qul man rabbu alssamawati waalardi quli Allahu qul afaittakhathtum min doonihi awliyaa la yamlikoona lianfusihim nafAAan wala darran qul hal yastawee alaAAma waalbaseeru am hal tastawee alththulumatu waalnnooru am jaAAaloo lillahi shurakaa khalaqoo kakhalqihi fatashabaha alkhalqu AAalayhim quli Allahu khaliqu kulli shayin wahuwa alwahidu alqahharu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 17, "He sends down from the sky, rain, and valleys flow according to their capacity, and the torrent carries a rising foam. And from that [ore] which they heat in the fire, desiring adornments and utensils, is a foam like it. Thus Allah presents [the example of] truth and falsehood. As for the foam, it vanishes, [being] cast off; but as for that which benefits the people, it remains on the earth. Thus does Allah present examples", "أَنزَلَ مِنَ ٱلسَّمَآءِ مَآءٗ فَسَالَتۡ أَوۡدِيَةُۢ بِقَدَرِهَا فَٱحۡتَمَلَ ٱلسَّيۡلُ زَبَدٗا رَّابِيٗاۖ وَمِمَّا يُوقِدُونَ عَلَيۡهِ فِي ٱلنَّارِ ٱبۡتِغَآءَ حِلۡيَةٍ أَوۡ مَتَٰعٖ زَبَدٞ مِّثۡلُهُۥۚ كَذَٰلِكَ يَضۡرِبُ ٱللَّهُ ٱلۡحَقَّ وَٱلۡبَٰطِلَۚ فَأَمَّا ٱلزَّبَدُ فَيَذۡهَبُ جُفَآءٗۖ وَأَمَّا مَا يَنفَعُ ٱلنَّاسَ فَيَمۡكُثُ فِي ٱلۡأَرۡضِۚ كَذَٰلِكَ يَضۡرِبُ ٱللَّهُ ٱلۡأَمۡثَالَ", "Anzala mina alssamai maan fasalat awdiyatun biqadariha faihtamala alssaylu zabadan rabiyan wamimma yooqidoona AAalayhi fee alnnari ibtighaa hilyatin aw mataAAin zabadun mithluhu kathalika yadribu Allahu alhaqqa waalbatila faamma alzzabadu fayathhabu jufaan waamma ma yanfaAAu alnnasa fayamkuthu fee alardi kathalika yadribu Allahu alamthala");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 18, "For those who have responded to their Lord is the best [reward], but those who did not respond to Him - if they had all that is in the earth entirely and the like of it with it, they would [attempt to] ransom themselves thereby. Those will have the worst account, and their refuge is Hell, and wretched is the resting place", "لِلَّذِينَ ٱسۡتَجَابُواْ لِرَبِّهِمُ ٱلۡحُسۡنَىٰۚ وَٱلَّذِينَ لَمۡ يَسۡتَجِيبُواْ لَهُۥ لَوۡ أَنَّ لَهُم مَّا فِي ٱلۡأَرۡضِ جَمِيعٗا وَمِثۡلَهُۥ مَعَهُۥ لَٱفۡتَدَوۡاْ بِهِۦٓۚ أُوْلَـٰٓئِكَ لَهُمۡ سُوٓءُ ٱلۡحِسَابِ وَمَأۡوَىٰهُمۡ جَهَنَّمُۖ وَبِئۡسَ ٱلۡمِهَادُ", "Lillatheena istajaboo lirabbihimu alhusna waallatheena lam yastajeeboo lahu law anna lahum ma fee alardi jameeAAan wamithlahu maAAahu laiftadaw bihi olaika lahum sooo alhisabi wamawahum jahannamu wabisa almihadu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 19, "Then is he who knows that what has been revealed to you from your Lord is the truth like one who is blind? They will only be reminded who are people of understanding", "۞أَفَمَن يَعۡلَمُ أَنَّمَآ أُنزِلَ إِلَيۡكَ مِن رَّبِّكَ ٱلۡحَقُّ كَمَنۡ هُوَ أَعۡمَىٰٓۚ إِنَّمَا يَتَذَكَّرُ أُوْلُواْ ٱلۡأَلۡبَٰبِ", "Afaman yaAAlamu annama onzila ilayka min rabbika alhaqqu kaman huwa aAAma innama yatathakkaru oloo alalbabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 20, "Those who fulfill the covenant of Allah and do not break the contract", "ٱلَّذِينَ يُوفُونَ بِعَهۡدِ ٱللَّهِ وَلَا يَنقُضُونَ ٱلۡمِيثَٰقَ", "Allatheena yoofoona biAAahdi Allahi wala yanqudoona almeethaqa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 21, "And those who join that which Allah has ordered to be joined and fear their Lord and are afraid of the evil of [their] account", "وَٱلَّذِينَ يَصِلُونَ مَآ أَمَرَ ٱللَّهُ بِهِۦٓ أَن يُوصَلَ وَيَخۡشَوۡنَ رَبَّهُمۡ وَيَخَافُونَ سُوٓءَ ٱلۡحِسَابِ", "Waallatheena yasiloona ma amara Allahu bihi an yoosala wayakhshawna rabbahum wayakhafoona sooa alhisabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 22, "And those who are patient, seeking the countenance of their Lord, and establish prayer and spend from what We have provided for them secretly and publicly and prevent evil with good - those will have the good consequence of [this] home", "وَٱلَّذِينَ صَبَرُواْ ٱبۡتِغَآءَ وَجۡهِ رَبِّهِمۡ وَأَقَامُواْ ٱلصَّلَوٰةَ وَأَنفَقُواْ مِمَّا رَزَقۡنَٰهُمۡ سِرّٗا وَعَلَانِيَةٗ وَيَدۡرَءُونَ بِٱلۡحَسَنَةِ ٱلسَّيِّئَةَ أُوْلَـٰٓئِكَ لَهُمۡ عُقۡبَى ٱلدَّارِ", "Waallatheena sabaroo ibtighaa wajhi rabbihim waaqamoo alssalata waanfaqoo mimma razaqnahum sirran waAAalaniyatan wayadraoona bialhasanati alssayyiata olaika lahum AAuqba alddari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 23, "Gardens of perpetual residence; they will enter them with whoever were righteous among their fathers, their spouses and their descendants. And the angels will enter upon them from every gate, [saying]", "جَنَّـٰتُ عَدۡنٖ يَدۡخُلُونَهَا وَمَن صَلَحَ مِنۡ ءَابَآئِهِمۡ وَأَزۡوَٰجِهِمۡ وَذُرِّيَّـٰتِهِمۡۖ وَٱلۡمَلَـٰٓئِكَةُ يَدۡخُلُونَ عَلَيۡهِم مِّن كُلِّ بَابٖ", "Jannatu AAadnin yadkhuloonaha waman salaha min abaihim waazwajihim wathurriyyatihim waalmalaikatu yadkhuloona AAalayhim min kulli babin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 24, "Peace be upon you for what you patiently endured. And excellent is the final home", "سَلَٰمٌ عَلَيۡكُم بِمَا صَبَرۡتُمۡۚ فَنِعۡمَ عُقۡبَى ٱلدَّارِ", "Salamun AAalaykum bima sabartum faniAAma AAuqba alddari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 25, "But those who break the covenant of Allah after contracting it and sever that which Allah has ordered to be joined and spread corruption on earth - for them is the curse, and they will have the worst home", "وَٱلَّذِينَ يَنقُضُونَ عَهۡدَ ٱللَّهِ مِنۢ بَعۡدِ مِيثَٰقِهِۦ وَيَقۡطَعُونَ مَآ أَمَرَ ٱللَّهُ بِهِۦٓ أَن يُوصَلَ وَيُفۡسِدُونَ فِي ٱلۡأَرۡضِ أُوْلَـٰٓئِكَ لَهُمُ ٱللَّعۡنَةُ وَلَهُمۡ سُوٓءُ ٱلدَّارِ", "Waallatheena yanqudoona AAahda Allahi min baAAdi meethaqihi wayaqtaAAoona ma amara Allahu bihi an yoosala wayufsidoona fee alardi olaika lahumu allaAAnatu walahum sooo alddari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 26, "Allah extends provision for whom He wills and restricts [it]. And they rejoice in the worldly life, while the worldly life is not, compared to the Hereafter, except [brief] enjoyment", "ٱللَّهُ يَبۡسُطُ ٱلرِّزۡقَ لِمَن يَشَآءُ وَيَقۡدِرُۚ وَفَرِحُواْ بِٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَمَا ٱلۡحَيَوٰةُ ٱلدُّنۡيَا فِي ٱلۡأٓخِرَةِ إِلَّا مَتَٰعٞ", "Allahu yabsutu alrrizqa liman yashao wayaqdiru wafarihoo bialhayati alddunya wama alhayatu alddunya fee alakhirati illa mataAAun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 27, "And those who disbelieved say, ""Why has a sign not been sent down to him from his Lord?"" Say, [O Muhammad], ""Indeed, Allah leaves astray whom He wills and guides to Himself whoever turns back [to Him]", "وَيَقُولُ ٱلَّذِينَ كَفَرُواْ لَوۡلَآ أُنزِلَ عَلَيۡهِ ءَايَةٞ مِّن رَّبِّهِۦۚ قُلۡ إِنَّ ٱللَّهَ يُضِلُّ مَن يَشَآءُ وَيَهۡدِيٓ إِلَيۡهِ مَنۡ أَنَابَ", "Wayaqoolu allatheena kafaroo lawla onzila AAalayhi ayatun min rabbihi qul inna Allaha yudillu man yashao wayahdee ilayhi man anaba");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 28, "Those who have believed and whose hearts are assured by the remembrance of Allah. Unquestionably, by the remembrance of Allah hearts are assured", "ٱلَّذِينَ ءَامَنُواْ وَتَطۡمَئِنُّ قُلُوبُهُم بِذِكۡرِ ٱللَّهِۗ أَلَا بِذِكۡرِ ٱللَّهِ تَطۡمَئِنُّ ٱلۡقُلُوبُ", "Allatheena amanoo watatmainnu quloobuhum bithikri Allahi ala bithikri Allahi tatmainnu alquloobu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 29, "Those who have believed and done righteous deeds - a good state is theirs and a good return", "ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ طُوبَىٰ لَهُمۡ وَحُسۡنُ مَـَٔابٖ", "Allatheena amanoo waAAamiloo alssalihati tooba lahum wahusnu maabin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 30, "Thus have We sent you to a community before which [other] communities have passed on so you might recite to them that which We revealed to you, while they disbelieve in the Most Merciful. Say, ""He is my Lord; there is no deity except Him. Upon Him I rely, and to Him is my return", "كَذَٰلِكَ أَرۡسَلۡنَٰكَ فِيٓ أُمَّةٖ قَدۡ خَلَتۡ مِن قَبۡلِهَآ أُمَمٞ لِّتَتۡلُوَاْ عَلَيۡهِمُ ٱلَّذِيٓ أَوۡحَيۡنَآ إِلَيۡكَ وَهُمۡ يَكۡفُرُونَ بِٱلرَّحۡمَٰنِۚ قُلۡ هُوَ رَبِّي لَآ إِلَٰهَ إِلَّا هُوَ عَلَيۡهِ تَوَكَّلۡتُ وَإِلَيۡهِ مَتَابِ", "Kathalika arsalnaka fee ommatin qad khalat min qabliha omamun litatluwa AAalayhimu allathee awhayna ilayka wahum yakfuroona bialrrahmani qul huwa rabbee la ilaha illa huwa AAalayhi tawakkaltu wailayhi matabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 31, "And if there was any qur'an by which the mountains would be removed or the earth would be broken apart or the dead would be made to speak, [it would be this Qur'an], but to Allah belongs the affair entirely. Then have those who believed not accepted that had Allah willed, He would have guided the people, all of them? And those who disbelieve do not cease to be struck, for what they have done, by calamity - or it will descend near their home - until there comes the promise of Allah. Indeed, Allah does not fail in [His] promise", "وَلَوۡ أَنَّ قُرۡءَانٗا سُيِّرَتۡ بِهِ ٱلۡجِبَالُ أَوۡ قُطِّعَتۡ بِهِ ٱلۡأَرۡضُ أَوۡ كُلِّمَ بِهِ ٱلۡمَوۡتَىٰۗ بَل لِّلَّهِ ٱلۡأَمۡرُ جَمِيعًاۗ أَفَلَمۡ يَاْيۡـَٔسِ ٱلَّذِينَ ءَامَنُوٓاْ أَن لَّوۡ يَشَآءُ ٱللَّهُ لَهَدَى ٱلنَّاسَ جَمِيعٗاۗ وَلَا يَزَالُ ٱلَّذِينَ كَفَرُواْ تُصِيبُهُم بِمَا صَنَعُواْ قَارِعَةٌ أَوۡ تَحُلُّ قَرِيبٗا مِّن دَارِهِمۡ حَتَّىٰ يَأۡتِيَ وَعۡدُ ٱللَّهِۚ إِنَّ ٱللَّهَ لَا يُخۡلِفُ ٱلۡمِيعَادَ", "Walaw anna quranan suyyirat bihi aljibalu aw quttiAAat bihi alardu aw kullima bihi almawta bal lillahi alamru jameeAAan afalam yayasi allatheena amanoo an law yashao Allahu lahada alnnasa jameeAAan wala yazalu allatheena kafaroo tuseebuhum bima sanaAAoo qariAAatun aw tahullu qareeban min darihim hatta yatiya waAAdu Allahi inna Allaha la yukhlifu almeeAAada");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 32, "And already were [other] messengers ridiculed before you, and I extended the time of those who disbelieved; then I seized them, and how [terrible] was My penalty", "وَلَقَدِ ٱسۡتُهۡزِئَ بِرُسُلٖ مِّن قَبۡلِكَ فَأَمۡلَيۡتُ لِلَّذِينَ كَفَرُواْ ثُمَّ أَخَذۡتُهُمۡۖ فَكَيۡفَ كَانَ عِقَابِ", "Walaqadi istuhzia birusulin min qablika faamlaytu lillatheena kafaroo thumma akhathtuhum fakayfa kana AAiqabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 33, "Then is He who is a maintainer of every soul, [knowing] what it has earned, [like any other]? But to Allah they have attributed partners. Say, ""Name them. Or do you inform Him of that which He knows not upon the earth or of what is apparent of speech?"" Rather, their [own] plan has been made attractive to those who disbelieve, and they have been averted from the way. And whomever Allah leaves astray - there will be for him no guide", "أَفَمَنۡ هُوَ قَآئِمٌ عَلَىٰ كُلِّ نَفۡسِۭ بِمَا كَسَبَتۡۗ وَجَعَلُواْ لِلَّهِ شُرَكَآءَ قُلۡ سَمُّوهُمۡۚ أَمۡ تُنَبِّـُٔونَهُۥ بِمَا لَا يَعۡلَمُ فِي ٱلۡأَرۡضِ أَم بِظَٰهِرٖ مِّنَ ٱلۡقَوۡلِۗ بَلۡ زُيِّنَ لِلَّذِينَ كَفَرُواْ مَكۡرُهُمۡ وَصُدُّواْ عَنِ ٱلسَّبِيلِۗ وَمَن يُضۡلِلِ ٱللَّهُ فَمَا لَهُۥ مِنۡ هَادٖ", "Afaman huwa qaimun AAala kulli nafsin bima kasabat wajaAAaloo lillahi shurakaa qul sammoohum am tunabbioonahu bima la yaAAlamu fee alardi am bithahirin mina alqawli bal zuyyina lillatheena kafaroo makruhum wasuddoo AAani alssabeeli waman yudlili Allahu fama lahu min hadin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 34, "For them will be punishment in the life of [this] world, and the punishment of the Hereafter is more severe. And they will not have from Allah any protector", "لَّهُمۡ عَذَابٞ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۖ وَلَعَذَابُ ٱلۡأٓخِرَةِ أَشَقُّۖ وَمَا لَهُم مِّنَ ٱللَّهِ مِن وَاقٖ", "Lahum AAathabun fee alhayati alddunya walaAAathabu alakhirati ashaqqu wama lahum mina Allahi min waqin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 35, "The example of Paradise, which the righteous have been promised, is [that] beneath it rivers flow. Its fruit is lasting, and its shade. That is the consequence for the righteous, and the consequence for the disbelievers is the Fire", "۞مَّثَلُ ٱلۡجَنَّةِ ٱلَّتِي وُعِدَ ٱلۡمُتَّقُونَۖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُۖ أُكُلُهَا دَآئِمٞ وَظِلُّهَاۚ تِلۡكَ عُقۡبَى ٱلَّذِينَ ٱتَّقَواْۚ وَّعُقۡبَى ٱلۡكَٰفِرِينَ ٱلنَّارُ", "Mathalu aljannati allatee wuAAida almuttaqoona tajree min tahtiha alanharu okuluha daimun wathilluha tilka AAuqba allatheena ittaqaw waAAuqba alkafireena alnnaru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 36, "And [the believers among] those to whom We have given the [previous] Scripture rejoice at what has been revealed to you, [O Muhammad], but among the [opposing] factions are those who deny part of it. Say, ""I have only been commanded to worship Allah and not associate [anything] with Him. To Him I invite, and to Him is my return", "وَٱلَّذِينَ ءَاتَيۡنَٰهُمُ ٱلۡكِتَٰبَ يَفۡرَحُونَ بِمَآ أُنزِلَ إِلَيۡكَۖ وَمِنَ ٱلۡأَحۡزَابِ مَن يُنكِرُ بَعۡضَهُۥۚ قُلۡ إِنَّمَآ أُمِرۡتُ أَنۡ أَعۡبُدَ ٱللَّهَ وَلَآ أُشۡرِكَ بِهِۦٓۚ إِلَيۡهِ أَدۡعُواْ وَإِلَيۡهِ مَـَٔابِ", "Waallatheena ataynahumu alkitaba yafrahoona bima onzila ilayka wamina alahzabi man yunkiru baAAdahu qul innama omirtu an aAAbuda Allaha wala oshrika bihi ilayhi adAAoo wailayhi maabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 37, "And thus We have revealed it as an Arabic legislation. And if you should follow their inclinations after what has come to you of knowledge, you would not have against Allah any ally or any protector", "وَكَذَٰلِكَ أَنزَلۡنَٰهُ حُكۡمًا عَرَبِيّٗاۚ وَلَئِنِ ٱتَّبَعۡتَ أَهۡوَآءَهُم بَعۡدَ مَا جَآءَكَ مِنَ ٱلۡعِلۡمِ مَا لَكَ مِنَ ٱللَّهِ مِن وَلِيّٖ وَلَا وَاقٖ", "Wakathalika anzalnahu hukman AAarabiyyan walaini ittabaAAta ahwaahum baAAda ma jaaka mina alAAilmi ma laka mina Allahi min waliyyin wala waqin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 38, "And We have already sent messengers before you and assigned to them wives and descendants. And it was not for a messenger to come with a sign except by permission of Allah. For every term is a decree", "وَلَقَدۡ أَرۡسَلۡنَا رُسُلٗا مِّن قَبۡلِكَ وَجَعَلۡنَا لَهُمۡ أَزۡوَٰجٗا وَذُرِّيَّةٗۚ وَمَا كَانَ لِرَسُولٍ أَن يَأۡتِيَ بِـَٔايَةٍ إِلَّا بِإِذۡنِ ٱللَّهِۗ لِكُلِّ أَجَلٖ كِتَابٞ", "Walaqad arsalna rusulan min qablika wajaAAalna lahum azwajan wathurriyyatan wama kana lirasoolin an yatiya biayatin illa biithni Allahi likulli ajalin kitabun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 39, "Allah eliminates what He wills or confirms, and with Him is the Mother of the Book", "يَمۡحُواْ ٱللَّهُ مَا يَشَآءُ وَيُثۡبِتُۖ وَعِندَهُۥٓ أُمُّ ٱلۡكِتَٰبِ", "Yamhoo Allahu ma yashao wayuthbitu waAAindahu ommu alkitabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 40, "And whether We show you part of what We promise them or take you in death, upon you is only the [duty of] notification, and upon Us is the account", "وَإِن مَّا نُرِيَنَّكَ بَعۡضَ ٱلَّذِي نَعِدُهُمۡ أَوۡ نَتَوَفَّيَنَّكَ فَإِنَّمَا عَلَيۡكَ ٱلۡبَلَٰغُ وَعَلَيۡنَا ٱلۡحِسَابُ", "Wain ma nuriyannaka baAAda allathee naAAiduhum aw natawaffayannaka fainnama AAalayka albalaghu waAAalayna alhisabu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 41, "Have they not seen that We set upon the land, reducing it from its borders? And Allah decides; there is no adjuster of His decision. And He is swift in account", "أَوَلَمۡ يَرَوۡاْ أَنَّا نَأۡتِي ٱلۡأَرۡضَ نَنقُصُهَا مِنۡ أَطۡرَافِهَاۚ وَٱللَّهُ يَحۡكُمُ لَا مُعَقِّبَ لِحُكۡمِهِۦۚ وَهُوَ سَرِيعُ ٱلۡحِسَابِ", "Awalam yaraw anna natee alarda nanqusuha min atrafiha waAllahu yahkumu la muAAaqqiba lihukmihi wahuwa sareeAAu alhisabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 42, "And those before them had plotted, but to Allah belongs the plan entirely. He knows what every soul earns, and the disbelievers will know for whom is the final home", "وَقَدۡ مَكَرَ ٱلَّذِينَ مِن قَبۡلِهِمۡ فَلِلَّهِ ٱلۡمَكۡرُ جَمِيعٗاۖ يَعۡلَمُ مَا تَكۡسِبُ كُلُّ نَفۡسٖۗ وَسَيَعۡلَمُ ٱلۡكُفَّـٰرُ لِمَنۡ عُقۡبَى ٱلدَّارِ", "Waqad makara allatheena min qablihim falillahi almakru jameeAAan yaAAlamu ma taksibu kullu nafsin wasayaAAlamu alkuffaru liman AAuqba alddari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (13, 43, "And those who have disbelieved say, ""You are not a messenger."" Say, [O Muhammad], ""Sufficient is Allah as Witness between me and you, and [the witness of] whoever has knowledge of the Scripture", "وَيَقُولُ ٱلَّذِينَ كَفَرُواْ لَسۡتَ مُرۡسَلٗاۚ قُلۡ كَفَىٰ بِٱللَّهِ شَهِيدَۢا بَيۡنِي وَبَيۡنَكُمۡ وَمَنۡ عِندَهُۥ عِلۡمُ ٱلۡكِتَٰبِ", "Wayaqoolu allatheena kafaroo lasta mursalan qul kafa biAllahi shaheedan baynee wabaynakum waman AAindahu AAilmu alkitabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 1, "Alif, Lam, Ra. [This is] a Book which We have revealed to you, [O Muhammad], that you might bring mankind out of darknesses into the light by permission of their Lord - to the path of the Exalted in Might, the Praiseworthy", "الٓرۚ كِتَٰبٌ أَنزَلۡنَٰهُ إِلَيۡكَ لِتُخۡرِجَ ٱلنَّاسَ مِنَ ٱلظُّلُمَٰتِ إِلَى ٱلنُّورِ بِإِذۡنِ رَبِّهِمۡ إِلَىٰ صِرَٰطِ ٱلۡعَزِيزِ ٱلۡحَمِيدِ", "Aliflamra kitabun anzalnahu ilayka litukhrija alnnasa mina alththulumati ila alnnoori biithni rabbihim ila sirati alAAazeezi alhameedi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 2, "Allah, to whom belongs whatever is in the heavens and whatever is on the earth. And woe to the disbelievers from a severe punishment", "ٱللَّهِ ٱلَّذِي لَهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۗ وَوَيۡلٞ لِّلۡكَٰفِرِينَ مِنۡ عَذَابٖ شَدِيدٍ", "Allahi allathee lahu ma fee alssamawati wama fee alardi wawaylun lilkafireena min AAathabin shadeedin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 3, "The ones who prefer the worldly life over the Hereafter and avert [people] from the way of Allah, seeking to make it (seem) deviant. Those are in extreme error", "ٱلَّذِينَ يَسۡتَحِبُّونَ ٱلۡحَيَوٰةَ ٱلدُّنۡيَا عَلَى ٱلۡأٓخِرَةِ وَيَصُدُّونَ عَن سَبِيلِ ٱللَّهِ وَيَبۡغُونَهَا عِوَجًاۚ أُوْلَـٰٓئِكَ فِي ضَلَٰلِۭ بَعِيدٖ", "Allatheena yastahibboona alhayata alddunya AAala alakhirati wayasuddoona AAan sabeeli Allahi wayabghoonaha AAiwajan olaika fee dalalin baAAeedin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 4, "And We did not send any messenger except [speaking] in the language of his people to state clearly for them, and Allah sends astray [thereby] whom He wills and guides whom He wills. And He is the Exalted in Might, the Wise", "وَمَآ أَرۡسَلۡنَا مِن رَّسُولٍ إِلَّا بِلِسَانِ قَوۡمِهِۦ لِيُبَيِّنَ لَهُمۡۖ فَيُضِلُّ ٱللَّهُ مَن يَشَآءُ وَيَهۡدِي مَن يَشَآءُۚ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ", "Wama arsalna min rasoolin illa bilisani qawmihi liyubayyina lahum fayudillu Allahu man yashao wayahdee man yashao wahuwa alAAazeezu alhakeemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 5, "And We certainly sent Moses with Our signs, [saying], ""Bring out your people from darknesses into the light and remind them of the days of Allah."" Indeed in that are signs for everyone patient and grateful", "وَلَقَدۡ أَرۡسَلۡنَا مُوسَىٰ بِـَٔايَٰتِنَآ أَنۡ أَخۡرِجۡ قَوۡمَكَ مِنَ ٱلظُّلُمَٰتِ إِلَى ٱلنُّورِ وَذَكِّرۡهُم بِأَيَّىٰمِ ٱللَّهِۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّكُلِّ صَبَّارٖ شَكُورٖ", "Walaqad arsalna moosa biayatina an akhrij qawmaka mina alththulumati ila alnnoori wathakkirhum biayyami Allahi inna fee thalika laayatin likulli sabbarin shakoorin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 6, "And [recall, O Children of Israel], when Moses said to His people, ""Remember the favor of Allah upon you when He saved you from the people of Pharaoh, who were afflicting you with the worst torment and were slaughtering your [newborn] sons and keeping your females alive. And in that was a great trial from your Lord", "وَإِذۡ قَالَ مُوسَىٰ لِقَوۡمِهِ ٱذۡكُرُواْ نِعۡمَةَ ٱللَّهِ عَلَيۡكُمۡ إِذۡ أَنجَىٰكُم مِّنۡ ءَالِ فِرۡعَوۡنَ يَسُومُونَكُمۡ سُوٓءَ ٱلۡعَذَابِ وَيُذَبِّحُونَ أَبۡنَآءَكُمۡ وَيَسۡتَحۡيُونَ نِسَآءَكُمۡۚ وَفِي ذَٰلِكُم بَلَآءٞ مِّن رَّبِّكُمۡ عَظِيمٞ", "Waith qala moosa liqawmihi othkuroo niAAmata Allahi AAalaykum ith anjakum min ali firAAawna yasoomoonakum sooa alAAathabi wayuthabbihoona abnaakum wayastahyoona nisaakum wafee thalikum balaon min rabbikum AAatheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 7, "And [remember] when your Lord proclaimed, 'If you are grateful, I will surely increase you [in favor]; but if you deny, indeed, My punishment is severe", "وَإِذۡ تَأَذَّنَ رَبُّكُمۡ لَئِن شَكَرۡتُمۡ لَأَزِيدَنَّكُمۡۖ وَلَئِن كَفَرۡتُمۡ إِنَّ عَذَابِي لَشَدِيدٞ", "Waith taaththana rabbukum lain shakartum laazeedannakum walain kafartum inna AAathabee lashadeedun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 8, "And Moses said, ""If you should disbelieve, you and whoever is on the earth entirely - indeed, Allah is Free of need and Praiseworthy", "وَقَالَ مُوسَىٰٓ إِن تَكۡفُرُوٓاْ أَنتُمۡ وَمَن فِي ٱلۡأَرۡضِ جَمِيعٗا فَإِنَّ ٱللَّهَ لَغَنِيٌّ حَمِيدٌ", "Waqala moosa in takfuroo antum waman fee alardi jameeAAan fainna Allaha laghaniyyun hameedun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 9, "Has there not reached you the news of those before you - the people of Noah and 'Aad and Thamud and those after them? No one knows them but Allah. Their messengers brought them clear proofs, but they returned their hands to their mouths and said, ""Indeed, we disbelieve in that with which you have been sent, and indeed we are, about that to which you invite us, in disquieting doubt", "أَلَمۡ يَأۡتِكُمۡ نَبَؤُاْ ٱلَّذِينَ مِن قَبۡلِكُمۡ قَوۡمِ نُوحٖ وَعَادٖ وَثَمُودَ وَٱلَّذِينَ مِنۢ بَعۡدِهِمۡ لَا يَعۡلَمُهُمۡ إِلَّا ٱللَّهُۚ جَآءَتۡهُمۡ رُسُلُهُم بِٱلۡبَيِّنَٰتِ فَرَدُّوٓاْ أَيۡدِيَهُمۡ فِيٓ أَفۡوَٰهِهِمۡ وَقَالُوٓاْ إِنَّا كَفَرۡنَا بِمَآ أُرۡسِلۡتُم بِهِۦ وَإِنَّا لَفِي شَكّٖ مِّمَّا تَدۡعُونَنَآ إِلَيۡهِ مُرِيبٖ", "Alam yatikum nabao allatheena min qablikum qawmi noohin waAAadin wathamooda waallatheena min baAAdihim la yaAAlamuhum illa Allahu jaathum rusuluhum bialbayyinati faraddoo aydiyahum fee afwahihim waqaloo inna kafarna bima orsiltum bihi wainna lafee shakkin mimma tadAAoonana ilayhi mureebun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 10, "Their messengers said, ""Can there be doubt about Allah, Creator of the heavens and earth? He invites you that He may forgive you of your sins, and He delays your death for a specified term."" They said, ""You are not but men like us who wish to avert us from what our fathers were worshipping. So bring us a clear authority", "۞قَالَتۡ رُسُلُهُمۡ أَفِي ٱللَّهِ شَكّٞ فَاطِرِ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ يَدۡعُوكُمۡ لِيَغۡفِرَ لَكُم مِّن ذُنُوبِكُمۡ وَيُؤَخِّرَكُمۡ إِلَىٰٓ أَجَلٖ مُّسَمّٗىۚ قَالُوٓاْ إِنۡ أَنتُمۡ إِلَّا بَشَرٞ مِّثۡلُنَا تُرِيدُونَ أَن تَصُدُّونَا عَمَّا كَانَ يَعۡبُدُ ءَابَآؤُنَا فَأۡتُونَا بِسُلۡطَٰنٖ مُّبِينٖ", "Qalat rusuluhum afee Allahi shakkun fatiri alssamawati waalardi yadAAookum liyaghfira lakum min thunoobikum wayuakhkhirakum ila ajalin musamman qaloo in antum illa basharun mithluna tureedoona an tasuddoona AAamma kana yaAAbudu abaona fatoona bisultanin mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 11, "Their messengers said to them, ""We are only men like you, but Allah confers favor upon whom He wills of His servants. It has never been for us to bring you evidence except by permission of Allah. And upon Allah let the believers rely", "قَالَتۡ لَهُمۡ رُسُلُهُمۡ إِن نَّحۡنُ إِلَّا بَشَرٞ مِّثۡلُكُمۡ وَلَٰكِنَّ ٱللَّهَ يَمُنُّ عَلَىٰ مَن يَشَآءُ مِنۡ عِبَادِهِۦۖ وَمَا كَانَ لَنَآ أَن نَّأۡتِيَكُم بِسُلۡطَٰنٍ إِلَّا بِإِذۡنِ ٱللَّهِۚ وَعَلَى ٱللَّهِ فَلۡيَتَوَكَّلِ ٱلۡمُؤۡمِنُونَ", "Qalat lahum rusuluhum in nahnu illa basharun mithlukum walakinna Allaha yamunnu AAala man yashao min AAibadihi wama kana lana an natiyakum bisultanin illa biithni Allahi waAAala Allahi falyatawakkali almuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 12, "And why should we not rely upon Allah while He has guided us to our [good] ways. And we will surely be patient against whatever harm you should cause us. And upon Allah let those who would rely [indeed] rely", "وَمَا لَنَآ أَلَّا نَتَوَكَّلَ عَلَى ٱللَّهِ وَقَدۡ هَدَىٰنَا سُبُلَنَاۚ وَلَنَصۡبِرَنَّ عَلَىٰ مَآ ءَاذَيۡتُمُونَاۚ وَعَلَى ٱللَّهِ فَلۡيَتَوَكَّلِ ٱلۡمُتَوَكِّلُونَ", "Wama lana alla natawakkala AAala Allahi waqad hadana subulana walanasbiranna AAala ma athaytumoona waAAala Allahi falyatawakkali almutawakkiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 13, "And those who disbelieved said to their messengers, ""We will surely drive you out of our land, or you must return to our religion."" So their Lord inspired to them, ""We will surely destroy the wrongdoers", "وَقَالَ ٱلَّذِينَ كَفَرُواْ لِرُسُلِهِمۡ لَنُخۡرِجَنَّكُم مِّنۡ أَرۡضِنَآ أَوۡ لَتَعُودُنَّ فِي مِلَّتِنَاۖ فَأَوۡحَىٰٓ إِلَيۡهِمۡ رَبُّهُمۡ لَنُهۡلِكَنَّ ٱلظَّـٰلِمِينَ", "Waqala allatheena kafaroo lirusulihim lanukhrijannakum min ardina aw lataAAoodunna fee millatina faawha ilayhim rabbuhum lanuhlikanna alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 14, "And We will surely cause you to dwell in the land after them. That is for he who fears My position and fears My threat", "وَلَنُسۡكِنَنَّكُمُ ٱلۡأَرۡضَ مِنۢ بَعۡدِهِمۡۚ ذَٰلِكَ لِمَنۡ خَافَ مَقَامِي وَخَافَ وَعِيدِ", "Walanuskinannakumu alarda min baAAdihim thalika liman khafa maqamee wakhafa waAAeedi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 15, "And they requested victory from Allah, and disappointed, [therefore], was every obstinate tyrant", "وَٱسۡتَفۡتَحُواْ وَخَابَ كُلُّ جَبَّارٍ عَنِيدٖ", "Waistaftahoo wakhaba kullu jabbarin AAaneedin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 16, "Before him is Hell, and he will be given a drink of purulent water", "مِّن وَرَآئِهِۦ جَهَنَّمُ وَيُسۡقَىٰ مِن مَّآءٖ صَدِيدٖ", "Min waraihi jahannamu wayusqa min main sadeedin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 17, "He will gulp it but will hardly [be able to] swallow it. And death will come to him from everywhere, but he is not to die. And before him is a massive punishment", "يَتَجَرَّعُهُۥ وَلَا يَكَادُ يُسِيغُهُۥ وَيَأۡتِيهِ ٱلۡمَوۡتُ مِن كُلِّ مَكَانٖ وَمَا هُوَ بِمَيِّتٖۖ وَمِن وَرَآئِهِۦ عَذَابٌ غَلِيظٞ", "YatajarraAAuhu wala yakadu yuseeghuhu wayateehi almawtu min kulli makanin wama huwa bimayyitin wamin waraihi AAathabun ghaleethun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 18, "The example of those who disbelieve in their Lord is [that] their deeds are like ashes which the wind blows forcefully on a stormy day; they are unable [to keep] from what they earned a [single] thing. That is what is extreme error", "مَّثَلُ ٱلَّذِينَ كَفَرُواْ بِرَبِّهِمۡۖ أَعۡمَٰلُهُمۡ كَرَمَادٍ ٱشۡتَدَّتۡ بِهِ ٱلرِّيحُ فِي يَوۡمٍ عَاصِفٖۖ لَّا يَقۡدِرُونَ مِمَّا كَسَبُواْ عَلَىٰ شَيۡءٖۚ ذَٰلِكَ هُوَ ٱلضَّلَٰلُ ٱلۡبَعِيدُ", "Mathalu allatheena kafaroo birabbihim aAAmaluhum karamadin ishtaddat bihi alrreehu fee yawmin AAasifin la yaqdiroona mimma kasaboo AAala shayin thalika huwa alddalalu albaAAeedu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 19, "Have you not seen that Allah created the heavens and the earth in truth? If He wills, He can do away with you and produce a new creation", "أَلَمۡ تَرَ أَنَّ ٱللَّهَ خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ بِٱلۡحَقِّۚ إِن يَشَأۡ يُذۡهِبۡكُمۡ وَيَأۡتِ بِخَلۡقٖ جَدِيدٖ", "Alam tara anna Allaha khalaqa alssamawati waalarda bialhaqqi in yasha yuthhibkum wayati bikhalqin jadeedin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 20, "And that is not difficult for Allah", "وَمَا ذَٰلِكَ عَلَى ٱللَّهِ بِعَزِيزٖ", "Wama thalika AAala Allahi biAAazeezin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 21, "And they will come out [for judgement] before Allah all together, and the weak will say to those who were arrogant, ""Indeed, we were your followers, so can you avail us anything against the punishment of Allah?"" They will say, ""If Allah had guided us, we would have guided you. It is all the same for us whether we show intolerance or are patient: there is for us no place of escape", "وَبَرَزُواْ لِلَّهِ جَمِيعٗا فَقَالَ ٱلضُّعَفَـٰٓؤُاْ لِلَّذِينَ ٱسۡتَكۡبَرُوٓاْ إِنَّا كُنَّا لَكُمۡ تَبَعٗا فَهَلۡ أَنتُم مُّغۡنُونَ عَنَّا مِنۡ عَذَابِ ٱللَّهِ مِن شَيۡءٖۚ قَالُواْ لَوۡ هَدَىٰنَا ٱللَّهُ لَهَدَيۡنَٰكُمۡۖ سَوَآءٌ عَلَيۡنَآ أَجَزِعۡنَآ أَمۡ صَبَرۡنَا مَا لَنَا مِن مَّحِيصٖ", "Wabarazoo lillahi jameeAAan faqala aldduAAafao lillatheena istakbaroo inna kunna lakum tabaAAan fahal antum mughnoona AAanna min AAathabi Allahi min shayin qaloo law hadana Allahu lahadaynakum sawaon AAalayna ajaziAAna am sabarna ma lana min maheesin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 22, "And Satan will say when the matter has been concluded, ""Indeed, Allah had promised you the promise of truth. And I promised you, but I betrayed you. But I had no authority over you except that I invited you, and you responded to me. So do not blame me; but blame yourselves. I cannot be called to your aid, nor can you be called to my aid. Indeed, I deny your association of me [with Allah] before. Indeed, for the wrongdoers is a painful punishment", "وَقَالَ ٱلشَّيۡطَٰنُ لَمَّا قُضِيَ ٱلۡأَمۡرُ إِنَّ ٱللَّهَ وَعَدَكُمۡ وَعۡدَ ٱلۡحَقِّ وَوَعَدتُّكُمۡ فَأَخۡلَفۡتُكُمۡۖ وَمَا كَانَ لِيَ عَلَيۡكُم مِّن سُلۡطَٰنٍ إِلَّآ أَن دَعَوۡتُكُمۡ فَٱسۡتَجَبۡتُمۡ لِيۖ فَلَا تَلُومُونِي وَلُومُوٓاْ أَنفُسَكُمۖ مَّآ أَنَا۠ بِمُصۡرِخِكُمۡ وَمَآ أَنتُم بِمُصۡرِخِيَّ إِنِّي كَفَرۡتُ بِمَآ أَشۡرَكۡتُمُونِ مِن قَبۡلُۗ إِنَّ ٱلظَّـٰلِمِينَ لَهُمۡ عَذَابٌ أَلِيمٞ", "Waqala alshshaytanu lamma qudiya alamru inna Allaha waAAadakum waAAda alhaqqi wawaAAadtukum faakhlaftukum wama kana liya AAalaykum min sultanin illa an daAAawtukum faistajabtum lee fala taloomoonee waloomoo anfusakum ma ana bimusrikhikum wama antum bimusrikhiyya innee kafartu bima ashraktumooni min qablu inna alththalimeena lahum AAathabun aleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 23, "And those who believed and did righteous deeds will be admitted to gardens beneath which rivers flow, abiding eternally therein by permission of their Lord; and their greeting therein will be, ""Peace", "وَأُدۡخِلَ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَا بِإِذۡنِ رَبِّهِمۡۖ تَحِيَّتُهُمۡ فِيهَا سَلَٰمٌ", "Waodkhila allatheena amanoo waAAamiloo alssalihati jannatin tajree min tahtiha alanharu khalideena feeha biithni rabbihim tahiyyatuhum feeha salamun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 24, "Have you not considered how Allah presents an example, [making] a good word like a good tree, whose root is firmly fixed and its branches [high] in the sky", "أَلَمۡ تَرَ كَيۡفَ ضَرَبَ ٱللَّهُ مَثَلٗا كَلِمَةٗ طَيِّبَةٗ كَشَجَرَةٖ طَيِّبَةٍ أَصۡلُهَا ثَابِتٞ وَفَرۡعُهَا فِي ٱلسَّمَآءِ", "Alam tara kayfa daraba Allahu mathalan kalimatan tayyibatan kashajaratin tayyibatin asluha thabitun wafarAAuha fee alssamai");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 25, "It produces its fruit all the time, by permission of its Lord. And Allah presents examples for the people that perhaps they will be reminded", "تُؤۡتِيٓ أُكُلَهَا كُلَّ حِينِۭ بِإِذۡنِ رَبِّهَاۗ وَيَضۡرِبُ ٱللَّهُ ٱلۡأَمۡثَالَ لِلنَّاسِ لَعَلَّهُمۡ يَتَذَكَّرُونَ", "Tutee okulaha kulla heenin biithni rabbiha wayadribu Allahu alamthala lilnnasi laAAallahum yatathakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 26, "And the example of a bad word is like a bad tree, uprooted from the surface of the earth, not having any stability", "وَمَثَلُ كَلِمَةٍ خَبِيثَةٖ كَشَجَرَةٍ خَبِيثَةٍ ٱجۡتُثَّتۡ مِن فَوۡقِ ٱلۡأَرۡضِ مَا لَهَا مِن قَرَارٖ", "Wamathalu kalimatin khabeethatin kashajaratin khabeethatin ijtuththat min fawqi alardi ma laha min qararin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 27, "Allah keeps firm those who believe, with the firm word, in worldly life and in the Hereafter. And Allah sends astray the wrongdoers. And Allah does what He wills", "يُثَبِّتُ ٱللَّهُ ٱلَّذِينَ ءَامَنُواْ بِٱلۡقَوۡلِ ٱلثَّابِتِ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَفِي ٱلۡأٓخِرَةِۖ وَيُضِلُّ ٱللَّهُ ٱلظَّـٰلِمِينَۚ وَيَفۡعَلُ ٱللَّهُ مَا يَشَآءُ", "Yuthabbitu Allahu allatheena amanoo bialqawli alththabiti fee alhayati alddunya wafee alakhirati wayudillu Allahu alththalimeena wayafAAalu Allahu ma yashao");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 28, "Have you not considered those who exchanged the favor of Allah for disbelief and settled their people [in] the home of ruin", "۞أَلَمۡ تَرَ إِلَى ٱلَّذِينَ بَدَّلُواْ نِعۡمَتَ ٱللَّهِ كُفۡرٗا وَأَحَلُّواْ قَوۡمَهُمۡ دَارَ ٱلۡبَوَارِ", "Alam tara ila allatheena baddaloo niAAmata Allahi kufran waahalloo qawmahum dara albawari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 29, "[It is] Hell, which they will [enter to] burn, and wretched is the settlement", "جَهَنَّمَ يَصۡلَوۡنَهَاۖ وَبِئۡسَ ٱلۡقَرَارُ", "Jahannama yaslawnaha wabisa alqararu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 30, "And they have attributed to Allah equals to mislead [people] from His way. Say, ""Enjoy yourselves, for indeed, your destination is the Fire", "وَجَعَلُواْ لِلَّهِ أَندَادٗا لِّيُضِلُّواْ عَن سَبِيلِهِۦۗ قُلۡ تَمَتَّعُواْ فَإِنَّ مَصِيرَكُمۡ إِلَى ٱلنَّارِ", "WajaAAaloo lillahi andadan liyudilloo AAan sabeelihi qul tamattaAAoo fainna maseerakum ila alnnari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 31, "[O Muhammad], tell My servants who have believed to establish prayer and spend from what We have provided them, secretly and publicly, before a Day comes in which there will be no exchange, nor any friendships", "قُل لِّعِبَادِيَ ٱلَّذِينَ ءَامَنُواْ يُقِيمُواْ ٱلصَّلَوٰةَ وَيُنفِقُواْ مِمَّا رَزَقۡنَٰهُمۡ سِرّٗا وَعَلَانِيَةٗ مِّن قَبۡلِ أَن يَأۡتِيَ يَوۡمٞ لَّا بَيۡعٞ فِيهِ وَلَا خِلَٰلٌ", "Qul liAAibadiya allatheena amanoo yuqeemoo alssalata wayunfiqoo mimma razaqnahum sirran waAAalaniyatan min qabli an yatiya yawmun la bayAAun feehi wala khilalun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 32, "It is Allah who created the heavens and the earth and sent down rain from the sky and produced thereby some fruits as provision for you and subjected for you the ships to sail through the sea by His command and subjected for you the rivers", "ٱللَّهُ ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَأَنزَلَ مِنَ ٱلسَّمَآءِ مَآءٗ فَأَخۡرَجَ بِهِۦ مِنَ ٱلثَّمَرَٰتِ رِزۡقٗا لَّكُمۡۖ وَسَخَّرَ لَكُمُ ٱلۡفُلۡكَ لِتَجۡرِيَ فِي ٱلۡبَحۡرِ بِأَمۡرِهِۦۖ وَسَخَّرَ لَكُمُ ٱلۡأَنۡهَٰرَ", "Allahu allathee khalaqa alssamawati waalarda waanzala mina alssamai maan faakhraja bihi mina alththamarati rizqan lakum wasakhkhara lakumu alfulka litajriya fee albahri biamrihi wasakhkhara lakumu alanhara");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 33, "And He subjected for you the sun and the moon, continuous [in orbit], and subjected for you the night and the day", "وَسَخَّرَ لَكُمُ ٱلشَّمۡسَ وَٱلۡقَمَرَ دَآئِبَيۡنِۖ وَسَخَّرَ لَكُمُ ٱلَّيۡلَ وَٱلنَّهَارَ", "Wasakhkhara lakumu alshshamsa waalqamara daibayni wasakhkhara lakumu allayla waalnnahara");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 34, "And He gave you from all you asked of Him. And if you should count the favor of Allah, you could not enumerate them. Indeed, mankind is [generally] most unjust and ungrateful", "وَءَاتَىٰكُم مِّن كُلِّ مَا سَأَلۡتُمُوهُۚ وَإِن تَعُدُّواْ نِعۡمَتَ ٱللَّهِ لَا تُحۡصُوهَآۗ إِنَّ ٱلۡإِنسَٰنَ لَظَلُومٞ كَفَّارٞ", "Waatakum min kulli ma saaltumoohu wain taAAuddoo niAAmata Allahi la tuhsooha inna alinsana lathaloomun kaffarun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 35, "And [mention, O Muhammad], when Abraham said, ""My Lord, make this city [Makkah] secure and keep me and my sons away from worshipping idols", "وَإِذۡ قَالَ إِبۡرَٰهِيمُ رَبِّ ٱجۡعَلۡ هَٰذَا ٱلۡبَلَدَ ءَامِنٗا وَٱجۡنُبۡنِي وَبَنِيَّ أَن نَّعۡبُدَ ٱلۡأَصۡنَامَ", "Waith qala ibraheemu rabbi ijAAal hatha albalada aminan waojnubnee wabaniyya an naAAbuda alasnama");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 36, "My Lord, indeed they have led astray many among the people. So whoever follows me - then he is of me; and whoever disobeys me - indeed, You are [yet] Forgiving and Merciful", "رَبِّ إِنَّهُنَّ أَضۡلَلۡنَ كَثِيرٗا مِّنَ ٱلنَّاسِۖ فَمَن تَبِعَنِي فَإِنَّهُۥ مِنِّيۖ وَمَنۡ عَصَانِي فَإِنَّكَ غَفُورٞ رَّحِيمٞ", "Rabbi innahunna adlalna katheeran mina alnnasi faman tabiAAanee fainnahu minnee waman AAasanee fainnaka ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 37, "Our Lord, I have settled some of my descendants in an uncultivated valley near Your sacred House, our Lord, that they may establish prayer. So make hearts among the people incline toward them and provide for them from the fruits that they might be grateful", "رَّبَّنَآ إِنِّيٓ أَسۡكَنتُ مِن ذُرِّيَّتِي بِوَادٍ غَيۡرِ ذِي زَرۡعٍ عِندَ بَيۡتِكَ ٱلۡمُحَرَّمِ رَبَّنَا لِيُقِيمُواْ ٱلصَّلَوٰةَ فَٱجۡعَلۡ أَفۡـِٔدَةٗ مِّنَ ٱلنَّاسِ تَهۡوِيٓ إِلَيۡهِمۡ وَٱرۡزُقۡهُم مِّنَ ٱلثَّمَرَٰتِ لَعَلَّهُمۡ يَشۡكُرُونَ", "Rabbana innee askantu min thurriyyatee biwadin ghayri thee zarAAin AAinda baytika almuharrami rabbana liyuqeemoo alssalata faijAAal afidatan mina alnnasi tahwee ilayhim waorzuqhum mina alththamarati laAAallahum yashkuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 38, "Our Lord, indeed You know what we conceal and what we declare, and nothing is hidden from Allah on the earth or in the heaven", "رَبَّنَآ إِنَّكَ تَعۡلَمُ مَا نُخۡفِي وَمَا نُعۡلِنُۗ وَمَا يَخۡفَىٰ عَلَى ٱللَّهِ مِن شَيۡءٖ فِي ٱلۡأَرۡضِ وَلَا فِي ٱلسَّمَآءِ", "Rabbana innaka taAAlamu ma nukhfee wama nuAAlinu wama yakhfa AAala Allahi min shayin fee alardi wala fee alssamai");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 39, "Praise to Allah, who has granted to me in old age Ishmael and Isaac. Indeed, my Lord is the Hearer of supplication", "ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِي وَهَبَ لِي عَلَى ٱلۡكِبَرِ إِسۡمَٰعِيلَ وَإِسۡحَٰقَۚ إِنَّ رَبِّي لَسَمِيعُ ٱلدُّعَآءِ", "Alhamdu lillahi allathee wahaba lee AAala alkibari ismaAAeela waishaqa inna rabbee lasameeAAu aldduAAai");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 40, "My Lord, make me an establisher of prayer, and [many] from my descendants. Our Lord, and accept my supplication", "رَبِّ ٱجۡعَلۡنِي مُقِيمَ ٱلصَّلَوٰةِ وَمِن ذُرِّيَّتِيۚ رَبَّنَا وَتَقَبَّلۡ دُعَآءِ", "Rabbi ijAAalnee muqeema alssalati wamin thurriyyatee rabbana wtaqabbal duAAai");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 41, "Our Lord, forgive me and my parents and the believers the Day the account is established", "رَبَّنَا ٱغۡفِرۡ لِي وَلِوَٰلِدَيَّ وَلِلۡمُؤۡمِنِينَ يَوۡمَ يَقُومُ ٱلۡحِسَابُ", "Rabbana ighfir lee waliwalidayya walilmumineena yawma yaqoomu alhisabu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 42, "And never think that Allah is unaware of what the wrongdoers do. He only delays them for a Day when eyes will stare [in horror]", "وَلَا تَحۡسَبَنَّ ٱللَّهَ غَٰفِلًا عَمَّا يَعۡمَلُ ٱلظَّـٰلِمُونَۚ إِنَّمَا يُؤَخِّرُهُمۡ لِيَوۡمٖ تَشۡخَصُ فِيهِ ٱلۡأَبۡصَٰرُ", "Wala tahsabanna Allaha ghafilan AAamma yaAAmalu alththalimoona innama yuakhkhiruhum liyawmin tashkhasu feehi alabsaru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 43, "Racing ahead, their heads raised up, their glance does not come back to them, and their hearts are void", "مُهۡطِعِينَ مُقۡنِعِي رُءُوسِهِمۡ لَا يَرۡتَدُّ إِلَيۡهِمۡ طَرۡفُهُمۡۖ وَأَفۡـِٔدَتُهُمۡ هَوَآءٞ", "MuhtiAAeena muqniAAee ruoosihim la yartaddu ilayhim tarfuhum waafidatuhum hawaon");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 44, "And, [O Muhammad], warn the people of a Day when the punishment will come to them and those who did wrong will say, ""Our Lord, delay us for a short term; we will answer Your call and follow the messengers."" [But it will be said], ""Had you not sworn, before, that for you there would be no cessation", "وَأَنذِرِ ٱلنَّاسَ يَوۡمَ يَأۡتِيهِمُ ٱلۡعَذَابُ فَيَقُولُ ٱلَّذِينَ ظَلَمُواْ رَبَّنَآ أَخِّرۡنَآ إِلَىٰٓ أَجَلٖ قَرِيبٖ نُّجِبۡ دَعۡوَتَكَ وَنَتَّبِعِ ٱلرُّسُلَۗ أَوَلَمۡ تَكُونُوٓاْ أَقۡسَمۡتُم مِّن قَبۡلُ مَا لَكُم مِّن زَوَالٖ", "Waanthiri alnnasa yawma yateehimu alAAathabu fayaqoolu allatheena thalamoo rabbana akhkhirna ila ajalin qareebin nujib daAAwataka wanattabiAAi alrrusula awalam takoonoo aqsamtum min qablu ma lakum min zawalin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 45, "And you lived among the dwellings of those who wronged themselves, and it had become clear to you how We dealt with them. And We presented for you [many] examples", "وَسَكَنتُمۡ فِي مَسَٰكِنِ ٱلَّذِينَ ظَلَمُوٓاْ أَنفُسَهُمۡ وَتَبَيَّنَ لَكُمۡ كَيۡفَ فَعَلۡنَا بِهِمۡ وَضَرَبۡنَا لَكُمُ ٱلۡأَمۡثَالَ", "Wasakantum fee masakini allatheena thalamoo anfusahum watabayyana lakum kayfa faAAalna bihim wadarabna lakumu alamthala");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 46, "And they had planned their plan, but with Allah is [recorded] their plan, even if their plan had been [sufficient] to do away with the mountains", "وَقَدۡ مَكَرُواْ مَكۡرَهُمۡ وَعِندَ ٱللَّهِ مَكۡرُهُمۡ وَإِن كَانَ مَكۡرُهُمۡ لِتَزُولَ مِنۡهُ ٱلۡجِبَالُ", "Waqad makaroo makrahum waAAinda Allahi makruhum wain kana makruhum litazoola minhu aljibalu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 47, "So never think that Allah will fail in His promise to His messengers. Indeed, Allah is Exalted in Might and Owner of Retribution", "فَلَا تَحۡسَبَنَّ ٱللَّهَ مُخۡلِفَ وَعۡدِهِۦ رُسُلَهُۥٓۚ إِنَّ ٱللَّهَ عَزِيزٞ ذُو ٱنتِقَامٖ", "Fala tahsabanna Allaha mukhlifa waAAdihi rusulahu inna Allaha AAazeezun thoo intiqamin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 48, "[It will be] on the Day the earth will be replaced by another earth, and the heavens [as well], and all creatures will come out before Allah, the One, the Prevailing", "يَوۡمَ تُبَدَّلُ ٱلۡأَرۡضُ غَيۡرَ ٱلۡأَرۡضِ وَٱلسَّمَٰوَٰتُۖ وَبَرَزُواْ لِلَّهِ ٱلۡوَٰحِدِ ٱلۡقَهَّارِ", "Yawma tubaddalu alardu ghayra alardi waalssamawatu wabarazoo lillahi alwahidi alqahhari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 49, "And you will see the criminals that Day bound together in shackles", "وَتَرَى ٱلۡمُجۡرِمِينَ يَوۡمَئِذٖ مُّقَرَّنِينَ فِي ٱلۡأَصۡفَادِ", "Watara almujrimeena yawmaithin muqarraneena fee alasfadi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 50, "Their garments of liquid pitch and their faces covered by the Fire", "سَرَابِيلُهُم مِّن قَطِرَانٖ وَتَغۡشَىٰ وُجُوهَهُمُ ٱلنَّارُ", "Sarabeeluhum min qatranin wataghsha wujoohahumu alnnaru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 51, "So that Allah will recompense every soul for what it earned. Indeed, Allah is swift in account", "لِيَجۡزِيَ ٱللَّهُ كُلَّ نَفۡسٖ مَّا كَسَبَتۡۚ إِنَّ ٱللَّهَ سَرِيعُ ٱلۡحِسَابِ", "Liyajziya Allahu kulla nafsin ma kasabat inna Allaha sareeAAu alhisabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (14, 52, "This [Qur'an] is notification for the people that they may be warned thereby and that they may know that He is but one God and that those of understanding will be reminded", "هَٰذَا بَلَٰغٞ لِّلنَّاسِ وَلِيُنذَرُواْ بِهِۦ وَلِيَعۡلَمُوٓاْ أَنَّمَا هُوَ إِلَٰهٞ وَٰحِدٞ وَلِيَذَّكَّرَ أُوْلُواْ ٱلۡأَلۡبَٰبِ", "Hatha balaghun lilnnasi waliyuntharoo bihi waliyaAAlamoo annama huwa ilahun wahidun waliyaththakkara oloo alalbabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 1, "Alif, Lam, Ra. These are the verses of the Book and a clear Qur'an", "الٓرۚ تِلۡكَ ءَايَٰتُ ٱلۡكِتَٰبِ وَقُرۡءَانٖ مُّبِينٖ", "Aliflamra tilka ayatu alkitabi waquranin mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 2, "Perhaps those who disbelieve will wish that they had been Muslims", "رُّبَمَا يَوَدُّ ٱلَّذِينَ كَفَرُواْ لَوۡ كَانُواْ مُسۡلِمِينَ", "Rubama yawaddu allatheena kafaroo law kanoo muslimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 3, "Let them eat and enjoy themselves and be diverted by [false] hope, for they are going to know", "ذَرۡهُمۡ يَأۡكُلُواْ وَيَتَمَتَّعُواْ وَيُلۡهِهِمُ ٱلۡأَمَلُۖ فَسَوۡفَ يَعۡلَمُونَ", "Tharhum yakuloo wayatamattaAAoo wayulhihimu alamalu fasawfa yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 4, "And We did not destroy any city but that for it was a known decree", "وَمَآ أَهۡلَكۡنَا مِن قَرۡيَةٍ إِلَّا وَلَهَا كِتَابٞ مَّعۡلُومٞ", "Wama ahlakna min qaryatin illa walaha kitabun maAAloomun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 5, "No nation will precede its term, nor will they remain thereafter", "مَّا تَسۡبِقُ مِنۡ أُمَّةٍ أَجَلَهَا وَمَا يَسۡتَـٔۡخِرُونَ", "Ma tasbiqu min ommatin ajalaha wama yastakhiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 6, "And they say, ""O you upon whom the message has been sent down, indeed you are mad", "وَقَالُواْ يَـٰٓأَيُّهَا ٱلَّذِي نُزِّلَ عَلَيۡهِ ٱلذِّكۡرُ إِنَّكَ لَمَجۡنُونٞ", "Waqaloo ya ayyuha allathee nuzzila AAalayhi alththikru innaka lamajnoonun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 7, "Why do you not bring us the angels, if you should be among the truthful", "لَّوۡمَا تَأۡتِينَا بِٱلۡمَلَـٰٓئِكَةِ إِن كُنتَ مِنَ ٱلصَّـٰدِقِينَ", "Law ma tateena bialmalaikati in kunta mina alssadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 8, "We do not send down the angels except with truth; and the disbelievers would not then be reprieved", "مَا نُنَزِّلُ ٱلۡمَلَـٰٓئِكَةَ إِلَّا بِٱلۡحَقِّ وَمَا كَانُوٓاْ إِذٗا مُّنظَرِينَ", "Ma nunazzilu almalaikata illa bialhaqqi wama kanoo ithan munthareena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 9, "Indeed, it is We who sent down the Qur'an and indeed, We will be its guardian", "إِنَّا نَحۡنُ نَزَّلۡنَا ٱلذِّكۡرَ وَإِنَّا لَهُۥ لَحَٰفِظُونَ", "Inna nahnu nazzalna alththikra wainna lahu lahafithoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 10, "And We had certainly sent [messengers] before you, [O Muhammad], among the sects of the former peoples", "وَلَقَدۡ أَرۡسَلۡنَا مِن قَبۡلِكَ فِي شِيَعِ ٱلۡأَوَّلِينَ", "Walaqad arsalna min qablika fee shiyaAAi alawwaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 11, "And no messenger would come to them except that they ridiculed him", "وَمَا يَأۡتِيهِم مِّن رَّسُولٍ إِلَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ", "Wama yateehim min rasoolin illa kanoo bihi yastahzioona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 12, "Thus do We insert denial into the hearts of the criminals", "كَذَٰلِكَ نَسۡلُكُهُۥ فِي قُلُوبِ ٱلۡمُجۡرِمِينَ", "Kathalika naslukuhu fee quloobi almujrimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 13, "They will not believe in it, while there has already occurred the precedent of the former peoples", "لَا يُؤۡمِنُونَ بِهِۦ وَقَدۡ خَلَتۡ سُنَّةُ ٱلۡأَوَّلِينَ", "La yuminoona bihi waqad khalat sunnatu alawwaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 14, "And [even] if We opened to them a gate from the heaven and they continued therein to ascend", "وَلَوۡ فَتَحۡنَا عَلَيۡهِم بَابٗا مِّنَ ٱلسَّمَآءِ فَظَلُّواْ فِيهِ يَعۡرُجُونَ", "Walaw fatahna AAalayhim baban mina alssamai fathalloo feehi yaAArujoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 15, "They would say, ""Our eyes have only been dazzled. Rather, we are a people affected by magic", "لَقَالُوٓاْ إِنَّمَا سُكِّرَتۡ أَبۡصَٰرُنَا بَلۡ نَحۡنُ قَوۡمٞ مَّسۡحُورُونَ", "Laqaloo innama sukkirat absaruna bal nahnu qawmun mashooroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 16, "And We have placed within the heaven great stars and have beautified it for the observers", "وَلَقَدۡ جَعَلۡنَا فِي ٱلسَّمَآءِ بُرُوجٗا وَزَيَّنَّـٰهَا لِلنَّـٰظِرِينَ", "Walaqad jaAAalna fee alssamai buroojan wazayyannaha lilnnathireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 17, "And We have protected it from every devil expelled [from the mercy of Allah]", "وَحَفِظۡنَٰهَا مِن كُلِّ شَيۡطَٰنٖ رَّجِيمٍ", "Wahafithnaha min kulli shaytanin rajeemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 18, "Except one who steals a hearing and is pursued by a clear burning flame", "إِلَّا مَنِ ٱسۡتَرَقَ ٱلسَّمۡعَ فَأَتۡبَعَهُۥ شِهَابٞ مُّبِينٞ", "Illa mani istaraqa alssamAAa faatbaAAahu shihabun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 19, "And the earth - We have spread it and cast therein firmly set mountains and caused to grow therein [something] of every well-balanced thing", "وَٱلۡأَرۡضَ مَدَدۡنَٰهَا وَأَلۡقَيۡنَا فِيهَا رَوَٰسِيَ وَأَنۢبَتۡنَا فِيهَا مِن كُلِّ شَيۡءٖ مَّوۡزُونٖ", "Waalarda madadnaha waalqayna feeha rawasiya waanbatna feeha min kulli shayin mawzoonin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 20, "And We have made for you therein means of living and [for] those for whom you are not providers", "وَجَعَلۡنَا لَكُمۡ فِيهَا مَعَٰيِشَ وَمَن لَّسۡتُمۡ لَهُۥ بِرَٰزِقِينَ", "WajaAAalna lakum feeha maAAayisha waman lastum lahu biraziqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 21, "And there is not a thing but that with Us are its depositories, and We do not send it down except according to a known measure", "وَإِن مِّن شَيۡءٍ إِلَّا عِندَنَا خَزَآئِنُهُۥ وَمَا نُنَزِّلُهُۥٓ إِلَّا بِقَدَرٖ مَّعۡلُومٖ", "Wain min shayin illa AAindana khazainuhu wama nunazziluhu illa biqadarin maAAloomin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 22, "And We have sent the fertilizing winds and sent down water from the sky and given you drink from it. And you are not its retainers", "وَأَرۡسَلۡنَا ٱلرِّيَٰحَ لَوَٰقِحَ فَأَنزَلۡنَا مِنَ ٱلسَّمَآءِ مَآءٗ فَأَسۡقَيۡنَٰكُمُوهُ وَمَآ أَنتُمۡ لَهُۥ بِخَٰزِنِينَ", "Waarsalna alrriyaha lawaqiha faanzalna mina alssamai maan faasqaynakumoohu wama antum lahu bikhazineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 23, "And indeed, it is We who give life and cause death, and We are the Inheritor", "وَإِنَّا لَنَحۡنُ نُحۡيِۦ وَنُمِيتُ وَنَحۡنُ ٱلۡوَٰرِثُونَ", "Wainna lanahnu nuhyee wanumeetu wanahnu alwarithoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 24, "And We have already known the preceding [generations] among you, and We have already known the later [ones to come]", "وَلَقَدۡ عَلِمۡنَا ٱلۡمُسۡتَقۡدِمِينَ مِنكُمۡ وَلَقَدۡ عَلِمۡنَا ٱلۡمُسۡتَـٔۡخِرِينَ", "Walaqad AAalimna almustaqdimeena minkum walaqad AAalimna almustakhireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 25, "And indeed, your Lord will gather them; indeed, He is Wise and Knowing", "وَإِنَّ رَبَّكَ هُوَ يَحۡشُرُهُمۡۚ إِنَّهُۥ حَكِيمٌ عَلِيمٞ", "Wainna rabbaka huwa yahshuruhum innahu hakeemun AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 26, "And We did certainly create man out of clay from an altered black mud", "وَلَقَدۡ خَلَقۡنَا ٱلۡإِنسَٰنَ مِن صَلۡصَٰلٖ مِّنۡ حَمَإٖ مَّسۡنُونٖ", "Walaqad khalaqna alinsana min salsalin min hamain masnoonin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 27, "And the jinn We created before from scorching fire", "وَٱلۡجَآنَّ خَلَقۡنَٰهُ مِن قَبۡلُ مِن نَّارِ ٱلسَّمُومِ", "Waaljanna khalaqnahu min qablu min nari alssamoomi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 28, "And [mention, O Muhammad], when your Lord said to the angels, ""I will create a human being out of clay from an altered black mud", "وَإِذۡ قَالَ رَبُّكَ لِلۡمَلَـٰٓئِكَةِ إِنِّي خَٰلِقُۢ بَشَرٗا مِّن صَلۡصَٰلٖ مِّنۡ حَمَإٖ مَّسۡنُونٖ", "Waith qala rabbuka lilmalaikati innee khaliqun basharan min salsalin min hamain masnoonin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 29, "And when I have proportioned him and breathed into him of My [created] soul, then fall down to him in prostration", "فَإِذَا سَوَّيۡتُهُۥ وَنَفَخۡتُ فِيهِ مِن رُّوحِي فَقَعُواْ لَهُۥ سَٰجِدِينَ", "Faitha sawwaytuhu wanafakhtu feehi min roohee faqaAAoo lahu sajideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 30, "So the angels prostrated - all of them entirely", "فَسَجَدَ ٱلۡمَلَـٰٓئِكَةُ كُلُّهُمۡ أَجۡمَعُونَ", "Fasajada almalaikatu kulluhum ajmaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 31, "Except Iblees, he refused to be with those who prostrated", "إِلَّآ إِبۡلِيسَ أَبَىٰٓ أَن يَكُونَ مَعَ ٱلسَّـٰجِدِينَ", "Illa ibleesa aba an yakoona maAAa alssajideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 32, "[Allah] said, O Iblees, what is [the matter] with you that you are not with those who prostrate", "قَالَ يَـٰٓإِبۡلِيسُ مَا لَكَ أَلَّا تَكُونَ مَعَ ٱلسَّـٰجِدِينَ", "Qala ya ibleesu ma laka alla takoona maAAa alssajideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 33, "He said, ""Never would I prostrate to a human whom You created out of clay from an altered black mud", "قَالَ لَمۡ أَكُن لِّأَسۡجُدَ لِبَشَرٍ خَلَقۡتَهُۥ مِن صَلۡصَٰلٖ مِّنۡ حَمَإٖ مَّسۡنُونٖ", "Qala lam akun liasjuda libasharin khalaqtahu min salsalin min hamain masnoonin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 34, "[Allah] said, ""Then get out of it, for indeed, you are expelled", "قَالَ فَٱخۡرُجۡ مِنۡهَا فَإِنَّكَ رَجِيمٞ", "Qala faokhruj minha fainnaka rajeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 35, "And indeed, upon you is the curse until the Day of Recompense", "وَإِنَّ عَلَيۡكَ ٱللَّعۡنَةَ إِلَىٰ يَوۡمِ ٱلدِّينِ", "Wainna AAalayka allaAAnata ila yawmi alddeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 36, "He said, ""My Lord, then reprieve me until the Day they are resurrected", "قَالَ رَبِّ فَأَنظِرۡنِيٓ إِلَىٰ يَوۡمِ يُبۡعَثُونَ", "Qala rabbi faanthirnee ila yawmi yubAAathoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 37, "[Allah] said, ""So indeed, you are of those reprieved", "قَالَ فَإِنَّكَ مِنَ ٱلۡمُنظَرِينَ", "Qala fainnaka mina almunthareena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 38, "Until the Day of the time well-known", "إِلَىٰ يَوۡمِ ٱلۡوَقۡتِ ٱلۡمَعۡلُومِ", "Ila yawmi alwaqti almaAAloomi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 39, "[Iblees] said, ""My Lord, because You have put me in error, I will surely make [disobedience] attractive to them on earth, and I will mislead them all", "قَالَ رَبِّ بِمَآ أَغۡوَيۡتَنِي لَأُزَيِّنَنَّ لَهُمۡ فِي ٱلۡأَرۡضِ وَلَأُغۡوِيَنَّهُمۡ أَجۡمَعِينَ", "Qala rabbi bima aghwaytanee laozayyinanna lahum fee alardi walaoghwiyannahum ajmaAAeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 40, "Except, among them, Your chosen servants", "إِلَّا عِبَادَكَ مِنۡهُمُ ٱلۡمُخۡلَصِينَ", "Illa AAibadaka minhumu almukhlaseena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 41, "[Allah] said, ""This is a path [of return] to Me [that is] straight", "قَالَ هَٰذَا صِرَٰطٌ عَلَيَّ مُسۡتَقِيمٌ", "Qala hatha siratun AAalayya mustaqeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 42, "Indeed, My servants - no authority will you have over them, except those who follow you of the deviators", "إِنَّ عِبَادِي لَيۡسَ لَكَ عَلَيۡهِمۡ سُلۡطَٰنٌ إِلَّا مَنِ ٱتَّبَعَكَ مِنَ ٱلۡغَاوِينَ", "Inna AAibadee laysa laka AAalayhim sultanun illa mani ittabaAAaka mina alghaweena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 43, "And indeed, Hell is the promised place for them all", "وَإِنَّ جَهَنَّمَ لَمَوۡعِدُهُمۡ أَجۡمَعِينَ", "Wainna jahannama lamawAAiduhum ajmaAAeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 44, "It has seven gates; for every gate is of them a portion designated", "لَهَا سَبۡعَةُ أَبۡوَٰبٖ لِّكُلِّ بَابٖ مِّنۡهُمۡ جُزۡءٞ مَّقۡسُومٌ", "Laha sabAAatu abwabin likulli babin minhum juzon maqsoomun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 45, "Indeed, the righteous will be within gardens and springs", "إِنَّ ٱلۡمُتَّقِينَ فِي جَنَّـٰتٖ وَعُيُونٍ", "Inna almuttaqeena fee jannatin waAAuyoonin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 46, "[Having been told], ""Enter it in peace, safe [and secure]", "ٱدۡخُلُوهَا بِسَلَٰمٍ ءَامِنِينَ", "Odkhulooha bisalamin amineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 47, "And We will remove whatever is in their breasts of resentment, [so they will be] brothers, on thrones facing each other", "وَنَزَعۡنَا مَا فِي صُدُورِهِم مِّنۡ غِلٍّ إِخۡوَٰنًا عَلَىٰ سُرُرٖ مُّتَقَٰبِلِينَ", "WanazaAAna ma fee sudoorihim min ghillin ikhwanan AAala sururin mutaqabileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 48, "No fatigue will touch them therein, nor from it will they [ever] be removed", "لَا يَمَسُّهُمۡ فِيهَا نَصَبٞ وَمَا هُم مِّنۡهَا بِمُخۡرَجِينَ", "La yamassuhum feeha nasabun wama hum minha bimukhrajeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 49, "[O Muhammad], inform My servants that it is I who am the Forgiving, the Merciful", "۞نَبِّئۡ عِبَادِيٓ أَنِّيٓ أَنَا ٱلۡغَفُورُ ٱلرَّحِيمُ", "Nabbi AAibadee annee ana alghafooru alrraheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 50, "And that it is My punishment which is the painful punishment", "وَأَنَّ عَذَابِي هُوَ ٱلۡعَذَابُ ٱلۡأَلِيمُ", "Wa anna AAathabee huwa alAAathabu alaleemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 51, "And inform them about the guests of Abraham", "وَنَبِّئۡهُمۡ عَن ضَيۡفِ إِبۡرَٰهِيمَ", "Wanabbihum AAan dayfi ibraheema");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 52, "When they entered upon him and said, ""Peace."" [Abraham] said, ""Indeed, we are fearful of you", "إِذۡ دَخَلُواْ عَلَيۡهِ فَقَالُواْ سَلَٰمٗا قَالَ إِنَّا مِنكُمۡ وَجِلُونَ", "Ith dakhaloo AAalayhi faqaloo salaman qala inna minkum wajiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 53, "[The angels] said, ""Fear not. Indeed, we give you good tidings of a learned boy", "قَالُواْ لَا تَوۡجَلۡ إِنَّا نُبَشِّرُكَ بِغُلَٰمٍ عَلِيمٖ", "Qaloo la tawjal inna nubashshiruka bighulamin AAaleemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 54, "He said, ""Have you given me good tidings although old age has come upon me? Then of what [wonder] do you inform", "قَالَ أَبَشَّرۡتُمُونِي عَلَىٰٓ أَن مَّسَّنِيَ ٱلۡكِبَرُ فَبِمَ تُبَشِّرُونَ", "Qala abashshartumoonee AAala an massaniya alkibaru fabima tubashshirooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 55, "They said, ""We have given you good tidings in truth, so do not be of the despairing", "قَالُواْ بَشَّرۡنَٰكَ بِٱلۡحَقِّ فَلَا تَكُن مِّنَ ٱلۡقَٰنِطِينَ", "Qaloo bashsharnaka bialhaqqi fala takun mina alqaniteena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 56, "He said, ""And who despairs of the mercy of his Lord except for those astray", "قَالَ وَمَن يَقۡنَطُ مِن رَّحۡمَةِ رَبِّهِۦٓ إِلَّا ٱلضَّآلُّونَ", "Qala waman yaqnatu min rahmati rabbihi illa alddalloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 57, "[Abraham] said, ""Then what is your business [here], O messengers", "قَالَ فَمَا خَطۡبُكُمۡ أَيُّهَا ٱلۡمُرۡسَلُونَ", "Qala fama khatbukum ayyuha almursaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 58, "They said, ""Indeed, we have been sent to a people of criminals", "قَالُوٓاْ إِنَّآ أُرۡسِلۡنَآ إِلَىٰ قَوۡمٖ مُّجۡرِمِينَ", "Qaloo inna orsilna ila qawmin mujrimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 59, "Except the family of Lot; indeed, we will save them all", "إِلَّآ ءَالَ لُوطٍ إِنَّا لَمُنَجُّوهُمۡ أَجۡمَعِينَ", "Illa ala lootin inna lamunajjoohum ajmaAAeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 60, "Except his wife."" Allah decreed that she is of those who remain behind", "إِلَّا ٱمۡرَأَتَهُۥ قَدَّرۡنَآ إِنَّهَا لَمِنَ ٱلۡغَٰبِرِينَ", "Illa imraatahu qaddarna innaha lamina alghabireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 61, "And when the messengers came to the family of Lot", "فَلَمَّا جَآءَ ءَالَ لُوطٍ ٱلۡمُرۡسَلُونَ", "Falamma jaa ala lootin almursaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 62, "He said, ""Indeed, you are people unknown", "قَالَ إِنَّكُمۡ قَوۡمٞ مُّنكَرُونَ", "Qala innakum qawmun munkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 63, "They said, ""But we have come to you with that about which they were disputing", "قَالُواْ بَلۡ جِئۡنَٰكَ بِمَا كَانُواْ فِيهِ يَمۡتَرُونَ", "Qaloo bal jinaka bima kanoo feehi yamtaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 64, "And we have come to you with truth, and indeed, we are truthful", "وَأَتَيۡنَٰكَ بِٱلۡحَقِّ وَإِنَّا لَصَٰدِقُونَ", "Waataynaka bialhaqqi wainna lasadiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 65, "So set out with your family during a portion of the night and follow behind them and let not anyone among you look back and continue on to where you are commanded", "فَأَسۡرِ بِأَهۡلِكَ بِقِطۡعٖ مِّنَ ٱلَّيۡلِ وَٱتَّبِعۡ أَدۡبَٰرَهُمۡ وَلَا يَلۡتَفِتۡ مِنكُمۡ أَحَدٞ وَٱمۡضُواْ حَيۡثُ تُؤۡمَرُونَ", "Faasri biahlika biqitAAin mina allayli waittabiAA adbarahum wala yaltafit minkum ahadun waimdoo haythu tumaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 66, "And We conveyed to him [the decree] of that matter: that those [sinners] would be eliminated by early morning", "وَقَضَيۡنَآ إِلَيۡهِ ذَٰلِكَ ٱلۡأَمۡرَ أَنَّ دَابِرَ هَـٰٓؤُلَآءِ مَقۡطُوعٞ مُّصۡبِحِينَ", "Waqadayna ilayhi thalika alamra anna dabira haolai maqtooAAun musbiheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 67, "And the people of the city came rejoicing", "وَجَآءَ أَهۡلُ ٱلۡمَدِينَةِ يَسۡتَبۡشِرُونَ", "Wajaa ahlu almadeenati yastabshiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 68, "[Lot] said, ""Indeed, these are my guests, so do not shame me", "قَالَ إِنَّ هَـٰٓؤُلَآءِ ضَيۡفِي فَلَا تَفۡضَحُونِ", "Qala inna haolai dayfee fala tafdahooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 69, "And fear Allah and do not disgrace me", "وَٱتَّقُواْ ٱللَّهَ وَلَا تُخۡزُونِ", "Waittaqoo Allaha wala tukhzooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 70, "They said, ""Have we not forbidden you from [protecting] people", "قَالُوٓاْ أَوَلَمۡ نَنۡهَكَ عَنِ ٱلۡعَٰلَمِينَ", "Qaloo awalam nanhaka AAani alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 71, "[Lot] said, ""These are my daughters - if you would be doers [of lawful marriage]", "قَالَ هَـٰٓؤُلَآءِ بَنَاتِيٓ إِن كُنتُمۡ فَٰعِلِينَ", "Qala haolai banatee in kuntum faAAileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 72, "By your life, [O Muhammad], indeed they were, in their intoxication, wandering blindly", "لَعَمۡرُكَ إِنَّهُمۡ لَفِي سَكۡرَتِهِمۡ يَعۡمَهُونَ", "LaAAamruka innahum lafee sakratihim yaAAmahoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 73, "So the shriek seized them at sunrise", "فَأَخَذَتۡهُمُ ٱلصَّيۡحَةُ مُشۡرِقِينَ", "Faakhathathumu alssayhatu mushriqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 74, "And We made the highest part [of the city] its lowest and rained upon them stones of hard clay", "فَجَعَلۡنَا عَٰلِيَهَا سَافِلَهَا وَأَمۡطَرۡنَا عَلَيۡهِمۡ حِجَارَةٗ مِّن سِجِّيلٍ", "FajaAAalna AAaliyaha safilaha waamtarna AAalayhim hijaratan min sijjeelin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 75, "Indeed in that are signs for those who discern", "إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّلۡمُتَوَسِّمِينَ", "Inna fee thalika laayatin lilmutawassimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 76, "And indeed, those cities are [situated] on an established road", "وَإِنَّهَا لَبِسَبِيلٖ مُّقِيمٍ", "Wainnaha labisabeelin muqeemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 77, "Indeed in that is a sign for the believers", "إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لِّلۡمُؤۡمِنِينَ", "Inna fee thalika laayatan lilmumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 78, "And the companions of the thicket were [also] wrongdoers", "وَإِن كَانَ أَصۡحَٰبُ ٱلۡأَيۡكَةِ لَظَٰلِمِينَ", "Wain kana ashabu alaykati lathalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 79, "So We took retribution from them, and indeed, both [cities] are on a clear highway", "فَٱنتَقَمۡنَا مِنۡهُمۡ وَإِنَّهُمَا لَبِإِمَامٖ مُّبِينٖ", "Faintaqamna minhum wainnahuma labiimamin mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 80, "And certainly did the companions of Thamud deny the messengers", "وَلَقَدۡ كَذَّبَ أَصۡحَٰبُ ٱلۡحِجۡرِ ٱلۡمُرۡسَلِينَ", "Walaqad kaththaba ashabu alhijri almursaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 81, "And We gave them Our signs, but from them they were turning away", "وَءَاتَيۡنَٰهُمۡ ءَايَٰتِنَا فَكَانُواْ عَنۡهَا مُعۡرِضِينَ", "Waataynahum ayatina fakanoo AAanha muAArideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 82, "And they used to carve from the mountains, houses, feeling secure", "وَكَانُواْ يَنۡحِتُونَ مِنَ ٱلۡجِبَالِ بُيُوتًا ءَامِنِينَ", "Wakanoo yanhitoona mina aljibali buyootan amineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 83, "But the shriek seized them at early morning", "فَأَخَذَتۡهُمُ ٱلصَّيۡحَةُ مُصۡبِحِينَ", "Faakhathathumu alssayhatu musbiheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 84, "So nothing availed them [from] what they used to earn", "فَمَآ أَغۡنَىٰ عَنۡهُم مَّا كَانُواْ يَكۡسِبُونَ", "Fama aghna AAanhum ma kanoo yaksiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 85, "And We have not created the heavens and earth and that between them except in truth. And indeed, the Hour is coming; so forgive with gracious forgiveness", "وَمَا خَلَقۡنَا ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَمَا بَيۡنَهُمَآ إِلَّا بِٱلۡحَقِّۗ وَإِنَّ ٱلسَّاعَةَ لَأٓتِيَةٞۖ فَٱصۡفَحِ ٱلصَّفۡحَ ٱلۡجَمِيلَ", "Wama khalaqna alssamawati waalarda wama baynahuma illa bialhaqqi wainna alssaAAata laatiyatun faisfahi alssafha aljameela");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 86, "Indeed, your Lord - He is the Knowing Creator", "إِنَّ رَبَّكَ هُوَ ٱلۡخَلَّـٰقُ ٱلۡعَلِيمُ", "Inna rabbaka huwa alkhallaqu alAAaleemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 87, "And We have certainly given you, [O Muhammad], seven of the often repeated [verses] and the great Qur'an", "وَلَقَدۡ ءَاتَيۡنَٰكَ سَبۡعٗا مِّنَ ٱلۡمَثَانِي وَٱلۡقُرۡءَانَ ٱلۡعَظِيمَ", "Walaqad ataynaka sabAAan mina almathanee waalqurana alAAatheema");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 88, "Do not extend your eyes toward that by which We have given enjoyment to [certain] categories of the disbelievers, and do not grieve over them. And lower your wing to the believers", "لَا تَمُدَّنَّ عَيۡنَيۡكَ إِلَىٰ مَا مَتَّعۡنَا بِهِۦٓ أَزۡوَٰجٗا مِّنۡهُمۡ وَلَا تَحۡزَنۡ عَلَيۡهِمۡ وَٱخۡفِضۡ جَنَاحَكَ لِلۡمُؤۡمِنِينَ", "La tamuddanna AAaynayka ila ma mattaAAna bihi azwajan minhum wala tahzan AAalayhim waikhfid janahaka lilmumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 89, "And say, ""Indeed, I am the clear warner", "وَقُلۡ إِنِّيٓ أَنَا ٱلنَّذِيرُ ٱلۡمُبِينُ", "Waqul innee ana alnnatheeru almubeenu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 90, "Just as We had revealed [scriptures] to the separators", "كَمَآ أَنزَلۡنَا عَلَى ٱلۡمُقۡتَسِمِينَ", "Kama anzalna AAala almuqtasimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 91, "Who have made the Qur'an into portions", "ٱلَّذِينَ جَعَلُواْ ٱلۡقُرۡءَانَ عِضِينَ", "Allatheena jaAAaloo alqurana AAideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 92, "So by your Lord, We will surely question them all", "فَوَرَبِّكَ لَنَسۡـَٔلَنَّهُمۡ أَجۡمَعِينَ", "Fawarabbika lanasalannahum ajmaAAeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 93, "About what they used to do", "عَمَّا كَانُواْ يَعۡمَلُونَ", "AAamma kanoo yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 94, "Then declare what you are commanded and turn away from the polytheists", "فَٱصۡدَعۡ بِمَا تُؤۡمَرُ وَأَعۡرِضۡ عَنِ ٱلۡمُشۡرِكِينَ", "FaisdaAA bima tumaru waaAArid AAani almushrikeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 95, "Indeed, We are sufficient for you against the mockers", "إِنَّا كَفَيۡنَٰكَ ٱلۡمُسۡتَهۡزِءِينَ", "Inna kafaynaka almustahzieena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 96, "Who make [equal] with Allah another deity. But they are going to know", "ٱلَّذِينَ يَجۡعَلُونَ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَۚ فَسَوۡفَ يَعۡلَمُونَ", "Allatheena yajAAaloona maAAa Allahi ilahan akhara fasawfa yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 97, "And We already know that your breast is constrained by what they say", "وَلَقَدۡ نَعۡلَمُ أَنَّكَ يَضِيقُ صَدۡرُكَ بِمَا يَقُولُونَ", "Walaqad naAAlamu annaka yadeequ sadruka bima yaqooloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 98, "So exalt [Allah] with praise of your Lord and be of those who prostrate [to Him]", "فَسَبِّحۡ بِحَمۡدِ رَبِّكَ وَكُن مِّنَ ٱلسَّـٰجِدِينَ", "Fasabbih bihamdi rabbika wakun mina alssajideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (15, 99, "And worship your Lord until there comes to you the certainty (death)", "وَٱعۡبُدۡ رَبَّكَ حَتَّىٰ يَأۡتِيَكَ ٱلۡيَقِينُ", "WaoAAbud rabbaka hatta yatiyaka alyaqeenu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 1, "The command of Allah is coming, so be not impatient for it. Exalted is He and high above what they associate with Him", "أَتَىٰٓ أَمۡرُ ٱللَّهِ فَلَا تَسۡتَعۡجِلُوهُۚ سُبۡحَٰنَهُۥ وَتَعَٰلَىٰ عَمَّا يُشۡرِكُونَ", "Ata amru Allahi fala tastaAAjiloohu subhanahu wataAAala AAamma yushrikoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 2, "He sends down the angels, with the inspiration of His command, upon whom He wills of His servants, [telling them], ""Warn that there is no deity except Me; so fear Me", "يُنَزِّلُ ٱلۡمَلَـٰٓئِكَةَ بِٱلرُّوحِ مِنۡ أَمۡرِهِۦ عَلَىٰ مَن يَشَآءُ مِنۡ عِبَادِهِۦٓ أَنۡ أَنذِرُوٓاْ أَنَّهُۥ لَآ إِلَٰهَ إِلَّآ أَنَا۠ فَٱتَّقُونِ", "Yunazzilu almalaikata bialrroohi min amrihi AAala man yashao min AAibadihi an anthiroo annahu la ilaha illa ana faittaqooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 3, "He created the heavens and earth in truth. High is He above what they associate with Him", "خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ بِٱلۡحَقِّۚ تَعَٰلَىٰ عَمَّا يُشۡرِكُونَ", "Khalaqa alssamawati waalarda bialhaqqi taAAala AAamma yushrikoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 4, "He created man from a sperm-drop; then at once, he is a clear adversary", "خَلَقَ ٱلۡإِنسَٰنَ مِن نُّطۡفَةٖ فَإِذَا هُوَ خَصِيمٞ مُّبِينٞ", "Khalaqa alinsana min nutfatin faitha huwa khaseemun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 5, "And the grazing livestock He has created for you; in them is warmth and [numerous] benefits, and from them you eat", "وَٱلۡأَنۡعَٰمَ خَلَقَهَاۖ لَكُمۡ فِيهَا دِفۡءٞ وَمَنَٰفِعُ وَمِنۡهَا تَأۡكُلُونَ", "WaalanAAama khalaqaha lakum feeha difon wamanafiAAu waminha takuloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 6, "And for you in them is [the enjoyment of] beauty when you bring them in [for the evening] and when you send them out [to pasture]", "وَلَكُمۡ فِيهَا جَمَالٌ حِينَ تُرِيحُونَ وَحِينَ تَسۡرَحُونَ", "Walakum feeha jamalun heena tureehoona waheena tasrahoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 7, "And they carry your loads to a land you could not have reached except with difficulty to yourselves. Indeed, your Lord is Kind and Merciful", "وَتَحۡمِلُ أَثۡقَالَكُمۡ إِلَىٰ بَلَدٖ لَّمۡ تَكُونُواْ بَٰلِغِيهِ إِلَّا بِشِقِّ ٱلۡأَنفُسِۚ إِنَّ رَبَّكُمۡ لَرَءُوفٞ رَّحِيمٞ", "Watahmilu athqalakum ila baladin lam takoonoo baligheehi illa bishiqqi alanfusi inna rabbakum laraoofun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 8, "And [He created] the horses, mules and donkeys for you to ride and [as] adornment. And He creates that which you do not know", "وَٱلۡخَيۡلَ وَٱلۡبِغَالَ وَٱلۡحَمِيرَ لِتَرۡكَبُوهَا وَزِينَةٗۚ وَيَخۡلُقُ مَا لَا تَعۡلَمُونَ", "Waalkhayla waalbighala waalhameera litarkabooha wazeenatan wayakhluqu ma la taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 9, "And upon Allah is the direction of the [right] way, and among the various paths are those deviating. And if He willed, He could have guided you all", "وَعَلَى ٱللَّهِ قَصۡدُ ٱلسَّبِيلِ وَمِنۡهَا جَآئِرٞۚ وَلَوۡ شَآءَ لَهَدَىٰكُمۡ أَجۡمَعِينَ", "WaAAala Allahi qasdu alssabeeli waminha jairun walaw shaa lahadakum ajmaAAeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 10, "It is He who sends down rain from the sky; from it is drink and from it is foliage in which you pasture [animals]", "هُوَ ٱلَّذِيٓ أَنزَلَ مِنَ ٱلسَّمَآءِ مَآءٗۖ لَّكُم مِّنۡهُ شَرَابٞ وَمِنۡهُ شَجَرٞ فِيهِ تُسِيمُونَ", "Huwa allathee anzala mina alssamai maan lakum minhu sharabun waminhu shajarun feehi tuseemoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 11, "He causes to grow for you thereby the crops, olives, palm trees, grapevines, and from all the fruits. Indeed in that is a sign for a people who give thought", "يُنۢبِتُ لَكُم بِهِ ٱلزَّرۡعَ وَٱلزَّيۡتُونَ وَٱلنَّخِيلَ وَٱلۡأَعۡنَٰبَ وَمِن كُلِّ ٱلثَّمَرَٰتِۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لِّقَوۡمٖ يَتَفَكَّرُونَ", "Yunbitu lakum bihi alzzarAAa waalzzaytoona waalnnakheela waalaAAnaba wamin kulli alththamarati inna fee thalika laayatan liqawmin yatafakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 12, "And He has subjected for you the night and day and the sun and moon, and the stars are subjected by His command. Indeed in that are signs for a people who reason", "وَسَخَّرَ لَكُمُ ٱلَّيۡلَ وَٱلنَّهَارَ وَٱلشَّمۡسَ وَٱلۡقَمَرَۖ وَٱلنُّجُومُ مُسَخَّرَٰتُۢ بِأَمۡرِهِۦٓۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يَعۡقِلُونَ", "Wasakhkhara lakumu allayla waalnnahara waalshshamsa waalqamara waalnnujoomu musakhkharatun biamrihi inna fee thalika laayatin liqawmin yaAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 13, "And [He has subjected] whatever He multiplied for you on the earth of varying colors. Indeed in that is a sign for a people who remember", "وَمَا ذَرَأَ لَكُمۡ فِي ٱلۡأَرۡضِ مُخۡتَلِفًا أَلۡوَٰنُهُۥٓۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لِّقَوۡمٖ يَذَّكَّرُونَ", "Wama tharaa lakum fee alardi mukhtalifan alwanuhu inna fee thalika laayatan liqawmin yaththakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 14, "And it is He who subjected the sea for you to eat from it tender meat and to extract from it ornaments which you wear. And you see the ships plowing through it, and [He subjected it] that you may seek of His bounty; and perhaps you will be grateful", "وَهُوَ ٱلَّذِي سَخَّرَ ٱلۡبَحۡرَ لِتَأۡكُلُواْ مِنۡهُ لَحۡمٗا طَرِيّٗا وَتَسۡتَخۡرِجُواْ مِنۡهُ حِلۡيَةٗ تَلۡبَسُونَهَاۖ وَتَرَى ٱلۡفُلۡكَ مَوَاخِرَ فِيهِ وَلِتَبۡتَغُواْ مِن فَضۡلِهِۦ وَلَعَلَّكُمۡ تَشۡكُرُونَ", "Wahuwa allathee sakhkhara albahra litakuloo minhu lahman tariyyan watastakhrijoo minhu hilyatan talbasoonaha watara alfulka mawakhira feehi walitabtaghoo min fadlihi walaAAallakum tashkuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 15, "And He has cast into the earth firmly set mountains, lest it shift with you, and [made] rivers and roads, that you may be guided", "وَأَلۡقَىٰ فِي ٱلۡأَرۡضِ رَوَٰسِيَ أَن تَمِيدَ بِكُمۡ وَأَنۡهَٰرٗا وَسُبُلٗا لَّعَلَّكُمۡ تَهۡتَدُونَ", "Waalqa fee alardi rawasiya an tameeda bikum waanharan wasubulan laAAallakum tahtadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 16, "And landmarks. And by the stars they are [also] guided", "وَعَلَٰمَٰتٖۚ وَبِٱلنَّجۡمِ هُمۡ يَهۡتَدُونَ", "WaAAalamatin wabialnnajmi hum yahtadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 17, "Then is He who creates like one who does not create? So will you not be reminded", "أَفَمَن يَخۡلُقُ كَمَن لَّا يَخۡلُقُۚ أَفَلَا تَذَكَّرُونَ", "Afaman yakhluqu kaman la yakhluqu afala tathakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 18, "And if you should count the favors of Allah, you could not enumerate them. Indeed, Allah is Forgiving and Merciful", "وَإِن تَعُدُّواْ نِعۡمَةَ ٱللَّهِ لَا تُحۡصُوهَآۗ إِنَّ ٱللَّهَ لَغَفُورٞ رَّحِيمٞ", "Wain taAAuddoo niAAmata Allahi la tuhsooha inna Allaha laghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 19, "And Allah knows what you conceal and what you declare", "وَٱللَّهُ يَعۡلَمُ مَا تُسِرُّونَ وَمَا تُعۡلِنُونَ", "WaAllahu yaAAlamu ma tusirroona wama tuAAlinoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 20, "And those they invoke other than Allah create nothing, and they [themselves] are created", "وَٱلَّذِينَ يَدۡعُونَ مِن دُونِ ٱللَّهِ لَا يَخۡلُقُونَ شَيۡـٔٗا وَهُمۡ يُخۡلَقُونَ", "Waallatheena yadAAoona min dooni Allahi la yakhluqoona shayan wahum yukhlaqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 21, "They are, [in fact], dead, not alive, and they do not perceive when they will be resurrected", "أَمۡوَٰتٌ غَيۡرُ أَحۡيَآءٖۖ وَمَا يَشۡعُرُونَ أَيَّانَ يُبۡعَثُونَ", "Amwatun ghayru ahyain wama yashAAuroona ayyana yubAAathoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 22, "Your god is one God. But those who do not believe in the Hereafter - their hearts are disapproving, and they are arrogant", "إِلَٰهُكُمۡ إِلَٰهٞ وَٰحِدٞۚ فَٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱلۡأٓخِرَةِ قُلُوبُهُم مُّنكِرَةٞ وَهُم مُّسۡتَكۡبِرُونَ", "Ilahukum ilahun wahidun faallatheena la yuminoona bialakhirati quloobuhum munkiratun wahum mustakbiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 23, "Assuredly, Allah knows what they conceal and what they declare. Indeed, He does not like the arrogant", "لَا جَرَمَ أَنَّ ٱللَّهَ يَعۡلَمُ مَا يُسِرُّونَ وَمَا يُعۡلِنُونَۚ إِنَّهُۥ لَا يُحِبُّ ٱلۡمُسۡتَكۡبِرِينَ", "La jarama anna Allaha yaAAlamu ma yusirroona wama yuAAlinoona innahu la yuhibbu almustakbireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 24, "And when it is said to them, ""What has your Lord sent down?"" They say, ""Legends of the former peoples", "وَإِذَا قِيلَ لَهُم مَّاذَآ أَنزَلَ رَبُّكُمۡ قَالُوٓاْ أَسَٰطِيرُ ٱلۡأَوَّلِينَ", "Waitha qeela lahum matha anzala rabbukum qaloo asateeru alawwaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 25, "That they may bear their own burdens in full on the Day of Resurrection and some of the burdens of those whom they misguide without knowledge. Unquestionably, evil is that which they bear", "لِيَحۡمِلُوٓاْ أَوۡزَارَهُمۡ كَامِلَةٗ يَوۡمَ ٱلۡقِيَٰمَةِ وَمِنۡ أَوۡزَارِ ٱلَّذِينَ يُضِلُّونَهُم بِغَيۡرِ عِلۡمٍۗ أَلَا سَآءَ مَا يَزِرُونَ", "Liyahmiloo awzarahum kamilatan yawma alqiyamati wamin awzari allatheena yudilloonahum bighayri AAilmin ala saa ma yaziroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 26, "Those before them had already plotted, but Allah came at their building from the foundations, so the roof fell upon them from above them, and the punishment came to them from where they did not perceive", "قَدۡ مَكَرَ ٱلَّذِينَ مِن قَبۡلِهِمۡ فَأَتَى ٱللَّهُ بُنۡيَٰنَهُم مِّنَ ٱلۡقَوَاعِدِ فَخَرَّ عَلَيۡهِمُ ٱلسَّقۡفُ مِن فَوۡقِهِمۡ وَأَتَىٰهُمُ ٱلۡعَذَابُ مِنۡ حَيۡثُ لَا يَشۡعُرُونَ", "Qad makara allatheena min qablihim faata Allahu bunyanahum mina alqawaAAidi fakharra AAalayhimu alssaqfu min fawqihim waatahumu alAAathabu min haythu la yashAAuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 27, "Then on the Day of Resurrection He will disgrace them and say, ""Where are My 'partners' for whom you used to oppose [the believers]?"" Those who were given knowledge will say, ""Indeed disgrace, this Day, and evil are upon the disbelievers", "ثُمَّ يَوۡمَ ٱلۡقِيَٰمَةِ يُخۡزِيهِمۡ وَيَقُولُ أَيۡنَ شُرَكَآءِيَ ٱلَّذِينَ كُنتُمۡ تُشَـٰٓقُّونَ فِيهِمۡۚ قَالَ ٱلَّذِينَ أُوتُواْ ٱلۡعِلۡمَ إِنَّ ٱلۡخِزۡيَ ٱلۡيَوۡمَ وَٱلسُّوٓءَ عَلَى ٱلۡكَٰفِرِينَ", "Thumma yawma alqiyamati yukhzeehim wayaqoolu ayna shurakaiya allatheena kuntum tushaqqoona feehim qala allatheena ootoo alAAilma inna alkhizya alyawma waalssooa AAala alkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 28, "The ones whom the angels take in death [while] wronging themselves, and [who] then offer submission, [saying], ""We were not doing any evil."" But, yes! Indeed, Allah is Knowing of what you used to do", "ٱلَّذِينَ تَتَوَفَّىٰهُمُ ٱلۡمَلَـٰٓئِكَةُ ظَالِمِيٓ أَنفُسِهِمۡۖ فَأَلۡقَوُاْ ٱلسَّلَمَ مَا كُنَّا نَعۡمَلُ مِن سُوٓءِۭۚ بَلَىٰٓۚ إِنَّ ٱللَّهَ عَلِيمُۢ بِمَا كُنتُمۡ تَعۡمَلُونَ", "Allatheena tatawaffahumu almalaikatu thalimee anfusihim faalqawoo alssalama ma kunna naAAmalu min sooin bala inna Allaha AAaleemun bima kuntum taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 29, "So enter the gates of Hell to abide eternally therein, and how wretched is the residence of the arrogant", "فَٱدۡخُلُوٓاْ أَبۡوَٰبَ جَهَنَّمَ خَٰلِدِينَ فِيهَاۖ فَلَبِئۡسَ مَثۡوَى ٱلۡمُتَكَبِّرِينَ", "Faodkhuloo abwaba jahannama khalideena feeha falabisa mathwa almutakabbireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 30, "And it will be said to those who feared Allah, ""What did your Lord send down?"" They will say, ""[That which is] good."" For those who do good in this world is good; and the home of the Hereafter is better. And how excellent is the home of the righteous", "۞وَقِيلَ لِلَّذِينَ ٱتَّقَوۡاْ مَاذَآ أَنزَلَ رَبُّكُمۡۚ قَالُواْ خَيۡرٗاۗ لِّلَّذِينَ أَحۡسَنُواْ فِي هَٰذِهِ ٱلدُّنۡيَا حَسَنَةٞۚ وَلَدَارُ ٱلۡأٓخِرَةِ خَيۡرٞۚ وَلَنِعۡمَ دَارُ ٱلۡمُتَّقِينَ", "Waqeela lillatheena ittaqaw matha anzala rabbukum qaloo khayran lillatheena ahsanoo fee hathihi alddunya hasanatun waladaru alakhirati khayrun walaniAAma daru almuttaqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 31, "Gardens of perpetual residence, which they will enter, beneath which rivers flow. They will have therein whatever they wish. Thus does Allah reward the righteous", "جَنَّـٰتُ عَدۡنٖ يَدۡخُلُونَهَا تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُۖ لَهُمۡ فِيهَا مَا يَشَآءُونَۚ كَذَٰلِكَ يَجۡزِي ٱللَّهُ ٱلۡمُتَّقِينَ", "Jannatu AAadnin yadkhuloonaha tajree min tahtiha alanharu lahum feeha ma yashaoona kathalika yajzee Allahu almuttaqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 32, "The ones whom the angels take in death, [being] good and pure; [the angels] will say, ""Peace be upon you. Enter Paradise for what you used to do", "ٱلَّذِينَ تَتَوَفَّىٰهُمُ ٱلۡمَلَـٰٓئِكَةُ طَيِّبِينَ يَقُولُونَ سَلَٰمٌ عَلَيۡكُمُ ٱدۡخُلُواْ ٱلۡجَنَّةَ بِمَا كُنتُمۡ تَعۡمَلُونَ", "Allatheena tatawaffahumu almalaikatu tayyibeena yaqooloona salamun AAalaykumu odkhuloo aljannata bima kuntum taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 33, "Do the disbelievers await [anything] except that the angels should come to them or there comes the command of your Lord? Thus did those do before them. And Allah wronged them not, but they had been wronging themselves", "هَلۡ يَنظُرُونَ إِلَّآ أَن تَأۡتِيَهُمُ ٱلۡمَلَـٰٓئِكَةُ أَوۡ يَأۡتِيَ أَمۡرُ رَبِّكَۚ كَذَٰلِكَ فَعَلَ ٱلَّذِينَ مِن قَبۡلِهِمۡۚ وَمَا ظَلَمَهُمُ ٱللَّهُ وَلَٰكِن كَانُوٓاْ أَنفُسَهُمۡ يَظۡلِمُونَ", "Hal yanthuroona illa an tatiyahumu almalaikatu aw yatiya amru rabbika kathalika faAAala allatheena min qablihim wama thalamahumu Allahu walakin kanoo anfusahum yathlimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 34, "So they were struck by the evil consequences of what they did and were enveloped by what they used to ridicule", "فَأَصَابَهُمۡ سَيِّـَٔاتُ مَا عَمِلُواْ وَحَاقَ بِهِم مَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ", "Faasabahum sayyiatu ma AAamiloo wahaqa bihim ma kanoo bihi yastahzioona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 35, "And those who associate others with Allah say, ""If Allah had willed, we would not have worshipped anything other than Him, neither we nor our fathers, nor would we have forbidden anything through other than Him."" Thus did those do before them. So is there upon the messengers except [the duty of] clear notification", "وَقَالَ ٱلَّذِينَ أَشۡرَكُواْ لَوۡ شَآءَ ٱللَّهُ مَا عَبَدۡنَا مِن دُونِهِۦ مِن شَيۡءٖ نَّحۡنُ وَلَآ ءَابَآؤُنَا وَلَا حَرَّمۡنَا مِن دُونِهِۦ مِن شَيۡءٖۚ كَذَٰلِكَ فَعَلَ ٱلَّذِينَ مِن قَبۡلِهِمۡۚ فَهَلۡ عَلَى ٱلرُّسُلِ إِلَّا ٱلۡبَلَٰغُ ٱلۡمُبِينُ", "Waqala allatheena ashrakoo law shaa Allahu ma AAabadna min doonihi min shayin nahnu wala abaona wala harramna min doonihi min shayin kathalika faAAala allatheena min qablihim fahal AAala alrrusuli illa albalaghu almubeenu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 36, "And We certainly sent into every nation a messenger, [saying], ""Worship Allah and avoid Taghut."" And among them were those whom Allah guided, and among them were those upon whom error was [deservedly] decreed. So proceed through the earth and observe how was the end of the deniers", "وَلَقَدۡ بَعَثۡنَا فِي كُلِّ أُمَّةٖ رَّسُولًا أَنِ ٱعۡبُدُواْ ٱللَّهَ وَٱجۡتَنِبُواْ ٱلطَّـٰغُوتَۖ فَمِنۡهُم مَّنۡ هَدَى ٱللَّهُ وَمِنۡهُم مَّنۡ حَقَّتۡ عَلَيۡهِ ٱلضَّلَٰلَةُۚ فَسِيرُواْ فِي ٱلۡأَرۡضِ فَٱنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلۡمُكَذِّبِينَ", "Walaqad baAAathna fee kulli ommatin rasoolan ani oAAbudoo Allaha waijtaniboo alttaghoota faminhum man hada Allahu waminhum man haqqat AAalayhi alddalalatu faseeroo fee alardi faonthuroo kayfa kana AAaqibatu almukaththibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 37, "[Even] if you should strive for their guidance, [O Muhammad], indeed, Allah does not guide those He sends astray, and they will have no helpers", "إِن تَحۡرِصۡ عَلَىٰ هُدَىٰهُمۡ فَإِنَّ ٱللَّهَ لَا يَهۡدِي مَن يُضِلُّۖ وَمَا لَهُم مِّن نَّـٰصِرِينَ", "In tahris AAala hudahum fainna Allaha la yahdee man yudillu wama lahum min nasireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 38, "And they swear by Allah their strongest oaths [that] Allah will not resurrect one who dies. But yes - [it is] a true promise [binding] upon Him, but most of the people do not know", "وَأَقۡسَمُواْ بِٱللَّهِ جَهۡدَ أَيۡمَٰنِهِمۡ لَا يَبۡعَثُ ٱللَّهُ مَن يَمُوتُۚ بَلَىٰ وَعۡدًا عَلَيۡهِ حَقّٗا وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَعۡلَمُونَ", "Waaqsamoo biAllahi jahda aymanihim la yabAAathu Allahu man yamootu bala waAAdan AAalayhi haqqan walakinna akthara alnnasi la yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 39, "[It is] so He will make clear to them [the truth of] that wherein they differ and so those who have disbelieved may know that they were liars", "لِيُبَيِّنَ لَهُمُ ٱلَّذِي يَخۡتَلِفُونَ فِيهِ وَلِيَعۡلَمَ ٱلَّذِينَ كَفَرُوٓاْ أَنَّهُمۡ كَانُواْ كَٰذِبِينَ", "Liyubayyina lahumu allathee yakhtalifoona feehi waliyaAAlama allatheena kafaroo annahum kanoo kathibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 40, "Indeed, Our word to a thing when We intend it is but that We say to it, ""Be,"" and it is", "إِنَّمَا قَوۡلُنَا لِشَيۡءٍ إِذَآ أَرَدۡنَٰهُ أَن نَّقُولَ لَهُۥ كُن فَيَكُونُ", "Innama qawluna lishayin itha aradnahu an naqoola lahu kun fayakoonu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 41, "And those who emigrated for [the cause of] Allah after they had been wronged - We will surely settle them in this world in a good place; but the reward of the Hereafter is greater, if only they could know", "وَٱلَّذِينَ هَاجَرُواْ فِي ٱللَّهِ مِنۢ بَعۡدِ مَا ظُلِمُواْ لَنُبَوِّئَنَّهُمۡ فِي ٱلدُّنۡيَا حَسَنَةٗۖ وَلَأَجۡرُ ٱلۡأٓخِرَةِ أَكۡبَرُۚ لَوۡ كَانُواْ يَعۡلَمُونَ", "Waallatheena hajaroo fee Allahi min baAAdi ma thulimoo lanubawwiannahum fee alddunya hasanatan walaajru alakhirati akbaru law kanoo yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 42, "[They are] those who endured patiently and upon their Lord relied", "ٱلَّذِينَ صَبَرُواْ وَعَلَىٰ رَبِّهِمۡ يَتَوَكَّلُونَ", "Allatheena sabaroo waAAala rabbihim yatawakkaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 43, "And We sent not before you except men to whom We revealed [Our message]. So ask the people of the message if you do not know", "وَمَآ أَرۡسَلۡنَا مِن قَبۡلِكَ إِلَّا رِجَالٗا نُّوحِيٓ إِلَيۡهِمۡۖ فَسۡـَٔلُوٓاْ أَهۡلَ ٱلذِّكۡرِ إِن كُنتُمۡ لَا تَعۡلَمُونَ", "Wama arsalna min qablika illa rijalan noohee ilayhim faisaloo ahla alththikri in kuntum la taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 44, "[We sent them] with clear proofs and written ordinances. And We revealed to you the message that you may make clear to the people what was sent down to them and that they might give thought", "بِٱلۡبَيِّنَٰتِ وَٱلزُّبُرِۗ وَأَنزَلۡنَآ إِلَيۡكَ ٱلذِّكۡرَ لِتُبَيِّنَ لِلنَّاسِ مَا نُزِّلَ إِلَيۡهِمۡ وَلَعَلَّهُمۡ يَتَفَكَّرُونَ", "Bialbayyinati waalzzuburi waanzalna ilayka alththikra litubayyina lilnnasi ma nuzzila ilayhim walaAAallahum yatafakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 45, "Then, do those who have planned evil deeds feel secure that Allah will not cause the earth to swallow them or that the punishment will not come upon them from where they do not perceive", "أَفَأَمِنَ ٱلَّذِينَ مَكَرُواْ ٱلسَّيِّـَٔاتِ أَن يَخۡسِفَ ٱللَّهُ بِهِمُ ٱلۡأَرۡضَ أَوۡ يَأۡتِيَهُمُ ٱلۡعَذَابُ مِنۡ حَيۡثُ لَا يَشۡعُرُونَ", "Afaamina allatheena makaroo alssayyiati an yakhsifa Allahu bihimu alarda aw yatiyahumu alAAathabu min haythu la yashAAuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 46, "Or that He would not seize them during their [usual] activity, and they could not cause failure", "أَوۡ يَأۡخُذَهُمۡ فِي تَقَلُّبِهِمۡ فَمَا هُم بِمُعۡجِزِينَ", "Aw yakhuthahum fee taqallubihim fama hum bimuAAjizeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 47, "Or that He would not seize them gradually [in a state of dread]? But indeed, your Lord is Kind and Merciful", "أَوۡ يَأۡخُذَهُمۡ عَلَىٰ تَخَوُّفٖ فَإِنَّ رَبَّكُمۡ لَرَءُوفٞ رَّحِيمٌ", "Aw yakhuthahum AAala takhawwufin fainna rabbakum laraoofun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 48, "Have they not considered what things Allah has created? Their shadows incline to the right and to the left, prostrating to Allah, while they are humble", "أَوَلَمۡ يَرَوۡاْ إِلَىٰ مَا خَلَقَ ٱللَّهُ مِن شَيۡءٖ يَتَفَيَّؤُاْ ظِلَٰلُهُۥ عَنِ ٱلۡيَمِينِ وَٱلشَّمَآئِلِ سُجَّدٗا لِّلَّهِ وَهُمۡ دَٰخِرُونَ", "Awa lam yaraw ila ma khalaqa Allahu min shayin yatafayyao thilaluhu AAani alyameeni waalshshamaili sujjadan lillahi wahum dakhiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 49, "And to Allah prostrates whatever is in the heavens and whatever is on the earth of creatures, and the angels [as well], and they are not arrogant", "وَلِلَّهِۤ يَسۡجُدُۤ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِ مِن دَآبَّةٖ وَٱلۡمَلَـٰٓئِكَةُ وَهُمۡ لَا يَسۡتَكۡبِرُونَ", "Walillahi yasjudu ma fee alssamawati wama fee alardi min dabbatin waalmalaikatu wahum la yastakbiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 50, "They fear their Lord above them, and they do what they are commanded", "يَخَافُونَ رَبَّهُم مِّن فَوۡقِهِمۡ وَيَفۡعَلُونَ مَا يُؤۡمَرُونَ۩", "Yakhafoona rabbahum min fawqihim wayafAAaloona ma yumaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 51, "And Allah has said, ""Do not take for yourselves two deities. He is but one God, so fear only Me", "۞وَقَالَ ٱللَّهُ لَا تَتَّخِذُوٓاْ إِلَٰهَيۡنِ ٱثۡنَيۡنِۖ إِنَّمَا هُوَ إِلَٰهٞ وَٰحِدٞ فَإِيَّـٰيَ فَٱرۡهَبُونِ", "Waqala Allahu la tattakhithoo ilahayni ithnayni innama huwa ilahun wahidun faiyyaya fairhabooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 52, "And to Him belongs whatever is in the heavens and the earth, and to Him is [due] worship constantly. Then is it other than Allah that you fear", "وَلَهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَلَهُ ٱلدِّينُ وَاصِبًاۚ أَفَغَيۡرَ ٱللَّهِ تَتَّقُونَ", "Walahu ma fee alssamawati waalardi walahu alddeenu wasiban afaghayra Allahi tattaqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 53, "And whatever you have of favor - it is from Allah. Then when adversity touches you, to Him you cry for help", "وَمَا بِكُم مِّن نِّعۡمَةٖ فَمِنَ ٱللَّهِۖ ثُمَّ إِذَا مَسَّكُمُ ٱلضُّرُّ فَإِلَيۡهِ تَجۡـَٔرُونَ", "Wama bikum min niAAmatin famina Allahi thumma itha massakumu alddurru failayhi tajaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 54, "Then when He removes the adversity from you, at once a party of you associates others with their Lord", "ثُمَّ إِذَا كَشَفَ ٱلضُّرَّ عَنكُمۡ إِذَا فَرِيقٞ مِّنكُم بِرَبِّهِمۡ يُشۡرِكُونَ", "Thumma itha kashafa alddurra AAankum itha fareequn minkum birabbihim yushrikoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 55, "So they will deny what We have given them. Then enjoy yourselves, for you are going to know", "لِيَكۡفُرُواْ بِمَآ ءَاتَيۡنَٰهُمۡۚ فَتَمَتَّعُواْ فَسَوۡفَ تَعۡلَمُونَ", "Liyakfuroo bima ataynahum fatamattaAAoo fasawfa taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 56, "And they assign to what they do not know a portion of that which We have provided them. By Allah, you will surely be questioned about what you used to invent", "وَيَجۡعَلُونَ لِمَا لَا يَعۡلَمُونَ نَصِيبٗا مِّمَّا رَزَقۡنَٰهُمۡۗ تَٱللَّهِ لَتُسۡـَٔلُنَّ عَمَّا كُنتُمۡ تَفۡتَرُونَ", "WayajAAaloona lima la yaAAlamoona naseeban mimma razaqnahum taAllahi latusalunna AAamma kuntum taftaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 57, "And they attribute to Allah daughters - exalted is He - and for them is what they desire", "وَيَجۡعَلُونَ لِلَّهِ ٱلۡبَنَٰتِ سُبۡحَٰنَهُۥ وَلَهُم مَّا يَشۡتَهُونَ", "WayajAAaloona lillahi albanati subhanahu walahum ma yashtahoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 58, "And when one of them is informed of [the birth of] a female, his face becomes dark, and he suppresses grief", "وَإِذَا بُشِّرَ أَحَدُهُم بِٱلۡأُنثَىٰ ظَلَّ وَجۡهُهُۥ مُسۡوَدّٗا وَهُوَ كَظِيمٞ", "Waitha bushshira ahaduhum bialontha thalla wajhuhu muswaddan wahuwa katheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 59, "He hides himself from the people because of the ill of which he has been informed. Should he keep it in humiliation or bury it in the ground? Unquestionably, evil is what they decide", "يَتَوَٰرَىٰ مِنَ ٱلۡقَوۡمِ مِن سُوٓءِ مَا بُشِّرَ بِهِۦٓۚ أَيُمۡسِكُهُۥ عَلَىٰ هُونٍ أَمۡ يَدُسُّهُۥ فِي ٱلتُّرَابِۗ أَلَا سَآءَ مَا يَحۡكُمُونَ", "Yatawara mina alqawmi min sooi ma bushshira bihi ayumsikuhu AAala hoonin am yadussuhu fee altturabi ala saa ma yahkumoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 60, "For those who do not believe in the Hereafter is the description of evil; and for Allah is the highest attribute. And He is Exalted in Might, the Wise", "لِلَّذِينَ لَا يُؤۡمِنُونَ بِٱلۡأٓخِرَةِ مَثَلُ ٱلسَّوۡءِۖ وَلِلَّهِ ٱلۡمَثَلُ ٱلۡأَعۡلَىٰۚ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ", "Lillatheena la yuminoona bialakhirati mathalu alssawi walillahi almathalu alaAAla wahuwa alAAazeezu alhakeemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 61, "And if Allah were to impose blame on the people for their wrongdoing, He would not have left upon the earth any creature, but He defers them for a specified term. And when their term has come, they will not remain behind an hour, nor will they precede [it]", "وَلَوۡ يُؤَاخِذُ ٱللَّهُ ٱلنَّاسَ بِظُلۡمِهِم مَّا تَرَكَ عَلَيۡهَا مِن دَآبَّةٖ وَلَٰكِن يُؤَخِّرُهُمۡ إِلَىٰٓ أَجَلٖ مُّسَمّٗىۖ فَإِذَا جَآءَ أَجَلُهُمۡ لَا يَسۡتَـٔۡخِرُونَ سَاعَةٗ وَلَا يَسۡتَقۡدِمُونَ", "Walaw yuakhithu Allahu alnnasa bithulmihim ma taraka AAalayha min dabbatin walakin yuakhkhiruhum ila ajalin musamman faitha jaa ajaluhum la yastakhiroona saAAatan wala yastaqdimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 62, "And they attribute to Allah that which they dislike, and their tongues assert the lie that they will have the best [from Him]. Assuredly, they will have the Fire, and they will be [therein] neglected", "وَيَجۡعَلُونَ لِلَّهِ مَا يَكۡرَهُونَۚ وَتَصِفُ أَلۡسِنَتُهُمُ ٱلۡكَذِبَ أَنَّ لَهُمُ ٱلۡحُسۡنَىٰۚ لَا جَرَمَ أَنَّ لَهُمُ ٱلنَّارَ وَأَنَّهُم مُّفۡرَطُونَ", "WayajAAaloona lillahi ma yakrahoona watasifu alsinatuhumu alkathiba anna lahumu alhusna la jarama anna lahumu alnnara waannahum mufratoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 63, "By Allah, We did certainly send [messengers] to nations before you, but Satan made their deeds attractive to them. And he is the disbelievers' ally today [as well], and they will have a painful punishment", "تَٱللَّهِ لَقَدۡ أَرۡسَلۡنَآ إِلَىٰٓ أُمَمٖ مِّن قَبۡلِكَ فَزَيَّنَ لَهُمُ ٱلشَّيۡطَٰنُ أَعۡمَٰلَهُمۡ فَهُوَ وَلِيُّهُمُ ٱلۡيَوۡمَ وَلَهُمۡ عَذَابٌ أَلِيمٞ", "TaAllahi laqad arsalna ila omamin min qablika fazayyana lahumu alshshaytanu aAAmalahum fahuwa waliyyuhumu alyawma walahum AAathabun aleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 64, "And We have not revealed to you the Book, [O Muhammad], except for you to make clear to them that wherein they have differed and as guidance and mercy for a people who believe", "وَمَآ أَنزَلۡنَا عَلَيۡكَ ٱلۡكِتَٰبَ إِلَّا لِتُبَيِّنَ لَهُمُ ٱلَّذِي ٱخۡتَلَفُواْ فِيهِ وَهُدٗى وَرَحۡمَةٗ لِّقَوۡمٖ يُؤۡمِنُونَ", "Wama anzalna AAalayka alkitaba illa litubayyina lahumu allathee ikhtalafoo feehi wahudan warahmatan liqawmin yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 65, "And Allah has sent down rain from the sky and given life thereby to the earth after its lifelessness. Indeed in that is a sign for a people who listen", "وَٱللَّهُ أَنزَلَ مِنَ ٱلسَّمَآءِ مَآءٗ فَأَحۡيَا بِهِ ٱلۡأَرۡضَ بَعۡدَ مَوۡتِهَآۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لِّقَوۡمٖ يَسۡمَعُونَ", "WaAllahu anzala mina alssamai maan faahya bihi alarda baAAda mawtiha inna fee thalika laayatan liqawmin yasmaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 66, "And indeed, for you in grazing livestock is a lesson. We give you drink from what is in their bellies - between excretion and blood - pure milk, palatable to drinkers", "وَإِنَّ لَكُمۡ فِي ٱلۡأَنۡعَٰمِ لَعِبۡرَةٗۖ نُّسۡقِيكُم مِّمَّا فِي بُطُونِهِۦ مِنۢ بَيۡنِ فَرۡثٖ وَدَمٖ لَّبَنًا خَالِصٗا سَآئِغٗا لِّلشَّـٰرِبِينَ", "Wainna lakum fee alanAAami laAAibratan nusqeekum mimma fee butoonihi min bayni farthin wadamin labanan khalisan saighan lilshsharibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 67, "And from the fruits of the palm trees and grapevines you take intoxicant and good provision. Indeed in that is a sign for a people who reason", "وَمِن ثَمَرَٰتِ ٱلنَّخِيلِ وَٱلۡأَعۡنَٰبِ تَتَّخِذُونَ مِنۡهُ سَكَرٗا وَرِزۡقًا حَسَنًاۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لِّقَوۡمٖ يَعۡقِلُونَ", "Wamin thamarati alnnakheeli waalaAAnabi tattakhithoona minhu sakaran warizqan hasanan inna fee thalika laayatan liqawmin yaAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 68, "And your Lord inspired to the bee, ""Take for yourself among the mountains, houses, and among the trees and [in] that which they construct", "وَأَوۡحَىٰ رَبُّكَ إِلَى ٱلنَّحۡلِ أَنِ ٱتَّخِذِي مِنَ ٱلۡجِبَالِ بُيُوتٗا وَمِنَ ٱلشَّجَرِ وَمِمَّا يَعۡرِشُونَ", "Waawha rabbuka ila alnnahli ani ittakhithee mina aljibali buyootan wamina alshshajari wamimma yaAArishoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 69, "Then eat from all the fruits and follow the ways of your Lord laid down [for you]."" There emerges from their bellies a drink, varying in colors, in which there is healing for people. Indeed in that is a sign for a people who give thought", "ثُمَّ كُلِي مِن كُلِّ ٱلثَّمَرَٰتِ فَٱسۡلُكِي سُبُلَ رَبِّكِ ذُلُلٗاۚ يَخۡرُجُ مِنۢ بُطُونِهَا شَرَابٞ مُّخۡتَلِفٌ أَلۡوَٰنُهُۥ فِيهِ شِفَآءٞ لِّلنَّاسِۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لِّقَوۡمٖ يَتَفَكَّرُونَ", "Thumma kulee min kulli alththamarati faoslukee subula rabbiki thululan yakhruju min butooniha sharabun mukhtalifun alwanuhu feehi shifaon lilnnasi inna fee thalika laayatan liqawmin yatafakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 70, "And Allah created you; then He will take you in death. And among you is he who is reversed to the most decrepit [old] age so that he will not know, after [having had] knowledge, a thing. Indeed, Allah is Knowing and Competent", "وَٱللَّهُ خَلَقَكُمۡ ثُمَّ يَتَوَفَّىٰكُمۡۚ وَمِنكُم مَّن يُرَدُّ إِلَىٰٓ أَرۡذَلِ ٱلۡعُمُرِ لِكَيۡ لَا يَعۡلَمَ بَعۡدَ عِلۡمٖ شَيۡـًٔاۚ إِنَّ ٱللَّهَ عَلِيمٞ قَدِيرٞ", "WaAllahu khalaqakum thumma yatawaffakum waminkum man yuraddu ila arthali alAAumuri likay la yaAAlama baAAda AAilmin shayan inna Allaha AAaleemun qadeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 71, "And Allah has favored some of you over others in provision. But those who were favored would not hand over their provision to those whom their right hands possess so they would be equal to them therein. Then is it the favor of Allah they reject", "وَٱللَّهُ فَضَّلَ بَعۡضَكُمۡ عَلَىٰ بَعۡضٖ فِي ٱلرِّزۡقِۚ فَمَا ٱلَّذِينَ فُضِّلُواْ بِرَآدِّي رِزۡقِهِمۡ عَلَىٰ مَا مَلَكَتۡ أَيۡمَٰنُهُمۡ فَهُمۡ فِيهِ سَوَآءٌۚ أَفَبِنِعۡمَةِ ٱللَّهِ يَجۡحَدُونَ", "WaAllahu faddala baAAdakum AAala baAAdin fee alrrizqi fama allatheena fuddiloo biraddee rizqihim AAala ma malakat aymanuhum fahum feehi sawaon afabiniAAmati Allahi yajhadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 72, "And Allah has made for you from yourselves mates and has made for you from your mates sons and grandchildren and has provided for you from the good things. Then in falsehood do they believe and in the favor of Allah they disbelieve", "وَٱللَّهُ جَعَلَ لَكُم مِّنۡ أَنفُسِكُمۡ أَزۡوَٰجٗا وَجَعَلَ لَكُم مِّنۡ أَزۡوَٰجِكُم بَنِينَ وَحَفَدَةٗ وَرَزَقَكُم مِّنَ ٱلطَّيِّبَٰتِۚ أَفَبِٱلۡبَٰطِلِ يُؤۡمِنُونَ وَبِنِعۡمَتِ ٱللَّهِ هُمۡ يَكۡفُرُونَ", "WaAllahu jaAAala lakum min anfusikum azwajan wajaAAala lakum min azwajikum baneena wahafadatan warazaqakum mina alttayyibati afabialbatili yuminoona wabiniAAmati Allahi hum yakfuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 73, "And they worship besides Allah that which does not possess for them [the power of] provision from the heavens and the earth at all, and [in fact], they are unable", "وَيَعۡبُدُونَ مِن دُونِ ٱللَّهِ مَا لَا يَمۡلِكُ لَهُمۡ رِزۡقٗا مِّنَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ شَيۡـٔٗا وَلَا يَسۡتَطِيعُونَ", "WayaAAbudoona min dooni Allahi ma la yamliku lahum rizqan mina alssamawati waalardi shayan wala yastateeAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 74, "So do not assert similarities to Allah. Indeed, Allah knows and you do not know", "فَلَا تَضۡرِبُواْ لِلَّهِ ٱلۡأَمۡثَالَۚ إِنَّ ٱللَّهَ يَعۡلَمُ وَأَنتُمۡ لَا تَعۡلَمُونَ", "Fala tadriboo lillahi alamthala inna Allaha yaAAlamu waantum la taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 75, "Allah presents an example: a slave [who is] owned and unable to do a thing and he to whom We have provided from Us good provision, so he spends from it secretly and publicly. Can they be equal? Praise to Allah! But most of them do not know", "۞ضَرَبَ ٱللَّهُ مَثَلًا عَبۡدٗا مَّمۡلُوكٗا لَّا يَقۡدِرُ عَلَىٰ شَيۡءٖ وَمَن رَّزَقۡنَٰهُ مِنَّا رِزۡقًا حَسَنٗا فَهُوَ يُنفِقُ مِنۡهُ سِرّٗا وَجَهۡرًاۖ هَلۡ يَسۡتَوُۥنَۚ ٱلۡحَمۡدُ لِلَّهِۚ بَلۡ أَكۡثَرُهُمۡ لَا يَعۡلَمُونَ", "Daraba Allahu mathalan AAabdan mamlookan la yaqdiru AAala shayin waman razaqnahu minna rizqan hasanan fahuwa yunfiqu minhu sirran wajahran hal yastawoona alhamdu lillahi bal aktharuhum la yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 76, "And Allah presents an example of two men, one of them dumb and unable to do a thing, while he is a burden to his guardian. Wherever he directs him, he brings no good. Is he equal to one who commands justice, while he is on a straight path", "وَضَرَبَ ٱللَّهُ مَثَلٗا رَّجُلَيۡنِ أَحَدُهُمَآ أَبۡكَمُ لَا يَقۡدِرُ عَلَىٰ شَيۡءٖ وَهُوَ كَلٌّ عَلَىٰ مَوۡلَىٰهُ أَيۡنَمَا يُوَجِّههُّ لَا يَأۡتِ بِخَيۡرٍ هَلۡ يَسۡتَوِي هُوَ وَمَن يَأۡمُرُ بِٱلۡعَدۡلِ وَهُوَ عَلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ", "Wadaraba Allahu mathalan rajulayni ahaduhuma abkamu la yaqdiru AAala shayin wahuwa kallun AAala mawlahu aynama yuwajjihhu la yati bikhayrin hal yastawee huwa waman yamuru bialAAadli wahuwa AAala siratin mustaqeemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 77, "And to Allah belongs the unseen [aspects] of the heavens and the earth. And the command for the Hour is not but as a glance of the eye or even nearer. Indeed, Allah is over all things competent", "وَلِلَّهِ غَيۡبُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَمَآ أَمۡرُ ٱلسَّاعَةِ إِلَّا كَلَمۡحِ ٱلۡبَصَرِ أَوۡ هُوَ أَقۡرَبُۚ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ", "Walillahi ghaybu alssamawati waalardi wama amru alssaAAati illa kalamhi albasari aw huwa aqrabu inna Allaha AAala kulli shayin qadeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 78, "And Allah has extracted you from the wombs of your mothers not knowing a thing, and He made for you hearing and vision and intellect that perhaps you would be grateful", "وَٱللَّهُ أَخۡرَجَكُم مِّنۢ بُطُونِ أُمَّهَٰتِكُمۡ لَا تَعۡلَمُونَ شَيۡـٔٗا وَجَعَلَ لَكُمُ ٱلسَّمۡعَ وَٱلۡأَبۡصَٰرَ وَٱلۡأَفۡـِٔدَةَ لَعَلَّكُمۡ تَشۡكُرُونَ", "WaAllahu akhrajakum min butooni ommahatikum la taAAlamoona shayan wajaAAala lakumu alssamAAa waalabsara waalafidata laAAallakum tashkuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 79, "Do they not see the birds controlled in the atmosphere of the sky? None holds them up except Allah. Indeed in that are signs for a people who believe", "أَلَمۡ يَرَوۡاْ إِلَى ٱلطَّيۡرِ مُسَخَّرَٰتٖ فِي جَوِّ ٱلسَّمَآءِ مَا يُمۡسِكُهُنَّ إِلَّا ٱللَّهُۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يُؤۡمِنُونَ", "Alam yaraw ila alttayri musakhkharatin fee jawwi alssamai ma yumsikuhunna illa Allahu inna fee thalika laayatin liqawmin yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 80, "And Allah has made for you from your homes a place of rest and made for you from the hides of the animals tents which you find light on your day of travel and your day of encampment; and from their wool, fur and hair is furnishing and enjoyment for a time", "وَٱللَّهُ جَعَلَ لَكُم مِّنۢ بُيُوتِكُمۡ سَكَنٗا وَجَعَلَ لَكُم مِّن جُلُودِ ٱلۡأَنۡعَٰمِ بُيُوتٗا تَسۡتَخِفُّونَهَا يَوۡمَ ظَعۡنِكُمۡ وَيَوۡمَ إِقَامَتِكُمۡ وَمِنۡ أَصۡوَافِهَا وَأَوۡبَارِهَا وَأَشۡعَارِهَآ أَثَٰثٗا وَمَتَٰعًا إِلَىٰ حِينٖ", "WaAllahu jaAAala lakum min buyootikum sakanan wajaAAala lakum min juloodi alanAAami buyootan tastakhiffoonaha yawma thaAAnikum wayawma iqamatikum wamin aswafiha waawbariha waashAAariha athathan wamataAAan ila heenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 81, "And Allah has made for you, from that which He has created, shadows and has made for you from the mountains, shelters and has made for you garments which protect you from the heat and garments which protect you from your [enemy in] battle. Thus does He complete His favor upon you that you might submit [to Him]", "وَٱللَّهُ جَعَلَ لَكُم مِّمَّا خَلَقَ ظِلَٰلٗا وَجَعَلَ لَكُم مِّنَ ٱلۡجِبَالِ أَكۡنَٰنٗا وَجَعَلَ لَكُمۡ سَرَٰبِيلَ تَقِيكُمُ ٱلۡحَرَّ وَسَرَٰبِيلَ تَقِيكُم بَأۡسَكُمۡۚ كَذَٰلِكَ يُتِمُّ نِعۡمَتَهُۥ عَلَيۡكُمۡ لَعَلَّكُمۡ تُسۡلِمُونَ", "WaAllahu jaAAala lakum mimma khalaqa thilalan wajaAAala lakum mina aljibali aknanan wajaAAala lakum sarabeela taqeekumu alharra wasarabeela taqeekum basakum kathalika yutimmu niAAmatahu AAalaykum laAAallakum tuslimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 82, "But if they turn away, [O Muhammad] - then only upon you is [responsibility for] clear notification", "فَإِن تَوَلَّوۡاْ فَإِنَّمَا عَلَيۡكَ ٱلۡبَلَٰغُ ٱلۡمُبِينُ", "Fain tawallaw fainnama AAalayka albalaghu almubeenu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 83, "They recognize the favor of Allah; then they deny it. And most of them are disbelievers", "يَعۡرِفُونَ نِعۡمَتَ ٱللَّهِ ثُمَّ يُنكِرُونَهَا وَأَكۡثَرُهُمُ ٱلۡكَٰفِرُونَ", "YaAArifoona niAAmata Allahi thumma yunkiroonaha waaktharuhumu alkafiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 84, "And [mention] the Day when We will resurrect from every nation a witness. Then it will not be permitted to the disbelievers [to apologize or make excuses], nor will they be asked to appease [Allah]", "وَيَوۡمَ نَبۡعَثُ مِن كُلِّ أُمَّةٖ شَهِيدٗا ثُمَّ لَا يُؤۡذَنُ لِلَّذِينَ كَفَرُواْ وَلَا هُمۡ يُسۡتَعۡتَبُونَ", "Wayawma nabAAathu min kulli ommatin shaheedan thumma la yuthanu lillatheena kafaroo wala hum yustaAAtaboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 85, "And when those who wronged see the punishment, it will not be lightened for them, nor will they be reprieved", "وَإِذَا رَءَا ٱلَّذِينَ ظَلَمُواْ ٱلۡعَذَابَ فَلَا يُخَفَّفُ عَنۡهُمۡ وَلَا هُمۡ يُنظَرُونَ", "Waitha raa allatheena thalamoo alAAathaba fala yukhaffafu AAanhum wala hum yuntharoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 86, "And when those who associated others with Allah see their ""partners,"" they will say,"" Our Lord, these are our partners [to You] whom we used to invoke besides You."" But they will throw at them the statement, ""Indeed, you are liars", "وَإِذَا رَءَا ٱلَّذِينَ أَشۡرَكُواْ شُرَكَآءَهُمۡ قَالُواْ رَبَّنَا هَـٰٓؤُلَآءِ شُرَكَآؤُنَا ٱلَّذِينَ كُنَّا نَدۡعُواْ مِن دُونِكَۖ فَأَلۡقَوۡاْ إِلَيۡهِمُ ٱلۡقَوۡلَ إِنَّكُمۡ لَكَٰذِبُونَ", "Waitha raa allatheena ashrakoo shurakaahum qaloo rabbana haolai shurakaona allatheena kunna nadAAoo min doonika faalqaw ilayhimu alqawla innakum lakathiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 87, "And they will impart to Allah that Day [their] submission, and lost from them is what they used to invent", "وَأَلۡقَوۡاْ إِلَى ٱللَّهِ يَوۡمَئِذٍ ٱلسَّلَمَۖ وَضَلَّ عَنۡهُم مَّا كَانُواْ يَفۡتَرُونَ", "Waalqaw ila Allahi yawmaithin alssalama wadalla AAanhum ma kanoo yaftaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 88, "Those who disbelieved and averted [others] from the way of Allah - We will increase them in punishment over [their] punishment for what corruption they were causing", "ٱلَّذِينَ كَفَرُواْ وَصَدُّواْ عَن سَبِيلِ ٱللَّهِ زِدۡنَٰهُمۡ عَذَابٗا فَوۡقَ ٱلۡعَذَابِ بِمَا كَانُواْ يُفۡسِدُونَ", "Allatheena kafaroo wasaddoo AAan sabeeli Allahi zidnahum AAathaban fawqa alAAathabi bima kanoo yufsidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 89, "And [mention] the Day when We will resurrect among every nation a witness over them from themselves. And We will bring you, [O Muhammad], as a witness over your nation. And We have sent down to you the Book as clarification for all things and as guidance and mercy and good tidings for the Muslims", "وَيَوۡمَ نَبۡعَثُ فِي كُلِّ أُمَّةٖ شَهِيدًا عَلَيۡهِم مِّنۡ أَنفُسِهِمۡۖ وَجِئۡنَا بِكَ شَهِيدًا عَلَىٰ هَـٰٓؤُلَآءِۚ وَنَزَّلۡنَا عَلَيۡكَ ٱلۡكِتَٰبَ تِبۡيَٰنٗا لِّكُلِّ شَيۡءٖ وَهُدٗى وَرَحۡمَةٗ وَبُشۡرَىٰ لِلۡمُسۡلِمِينَ", "Wayawma nabAAathu fee kulli ommatin shaheedan AAalayhim min anfusihim wajina bika shaheedan AAala haolai wanazzalna AAalayka alkitaba tibyanan likulli shayin wahudan warahmatan wabushra lilmuslimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 90, "Indeed, Allah orders justice and good conduct and giving to relatives and forbids immorality and bad conduct and oppression. He admonishes you that perhaps you will be reminded", "۞إِنَّ ٱللَّهَ يَأۡمُرُ بِٱلۡعَدۡلِ وَٱلۡإِحۡسَٰنِ وَإِيتَآيِٕ ذِي ٱلۡقُرۡبَىٰ وَيَنۡهَىٰ عَنِ ٱلۡفَحۡشَآءِ وَٱلۡمُنكَرِ وَٱلۡبَغۡيِۚ يَعِظُكُمۡ لَعَلَّكُمۡ تَذَكَّرُونَ", "Inna Allaha yamuru bialAAadli waalihsani waeetai thee alqurba wayanha AAani alfahshai waalmunkari waalbaghyi yaAAithukum laAAallakum tathakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 91, "And fulfill the covenant of Allah when you have taken it, [O believers], and do not break oaths after their confirmation while you have made Allah, over you, a witness. Indeed, Allah knows what you do", "وَأَوۡفُواْ بِعَهۡدِ ٱللَّهِ إِذَا عَٰهَدتُّمۡ وَلَا تَنقُضُواْ ٱلۡأَيۡمَٰنَ بَعۡدَ تَوۡكِيدِهَا وَقَدۡ جَعَلۡتُمُ ٱللَّهَ عَلَيۡكُمۡ كَفِيلًاۚ إِنَّ ٱللَّهَ يَعۡلَمُ مَا تَفۡعَلُونَ", "Waawfoo biAAahdi Allahi itha AAahadtum wala tanqudoo alaymana baAAda tawkeediha waqad jaAAaltumu Allaha AAalaykum kafeelan inna Allaha yaAAlamu ma tafAAaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 92, "And do not be like she who untwisted her spun thread after it was strong [by] taking your oaths as [means of] deceit between you because one community is more plentiful [in number or wealth] than another community. Allah only tries you thereby. And He will surely make clear to you on the Day of Resurrection that over which you used to differ", "وَلَا تَكُونُواْ كَٱلَّتِي نَقَضَتۡ غَزۡلَهَا مِنۢ بَعۡدِ قُوَّةٍ أَنكَٰثٗا تَتَّخِذُونَ أَيۡمَٰنَكُمۡ دَخَلَۢا بَيۡنَكُمۡ أَن تَكُونَ أُمَّةٌ هِيَ أَرۡبَىٰ مِنۡ أُمَّةٍۚ إِنَّمَا يَبۡلُوكُمُ ٱللَّهُ بِهِۦۚ وَلَيُبَيِّنَنَّ لَكُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ مَا كُنتُمۡ فِيهِ تَخۡتَلِفُونَ", "Wala takoonoo kaallatee naqadat ghazlaha min baAAdi quwwatin ankathan tattakhithoona aymanakum dakhalan baynakum an takoona ommatun hiya arba min ommatin innama yablookumu Allahu bihi walayubayyinanna lakum yawma alqiyamati ma kuntum feehi takhtalifoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 93, "And if Allah had willed, He could have made you [of] one religion, but He causes to stray whom He wills and guides whom He wills. And you will surely be questioned about what you used to do", "وَلَوۡ شَآءَ ٱللَّهُ لَجَعَلَكُمۡ أُمَّةٗ وَٰحِدَةٗ وَلَٰكِن يُضِلُّ مَن يَشَآءُ وَيَهۡدِي مَن يَشَآءُۚ وَلَتُسۡـَٔلُنَّ عَمَّا كُنتُمۡ تَعۡمَلُونَ", "Walaw shaa Allahu lajaAAalakum ommatan wahidatan walakin yudillu man yashao wayahdee man yashao walatusalunna AAamma kuntum taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 94, "And do not take your oaths as [means of] deceit between you, lest a foot slip after it was [once] firm, and you would taste evil [in this world] for what [people] you diverted from the way of Allah, and you would have [in the Hereafter] a great punishment", "وَلَا تَتَّخِذُوٓاْ أَيۡمَٰنَكُمۡ دَخَلَۢا بَيۡنَكُمۡ فَتَزِلَّ قَدَمُۢ بَعۡدَ ثُبُوتِهَا وَتَذُوقُواْ ٱلسُّوٓءَ بِمَا صَدَدتُّمۡ عَن سَبِيلِ ٱللَّهِ وَلَكُمۡ عَذَابٌ عَظِيمٞ", "Wala tattakhithoo aymanakum dakhalan baynakum fatazilla qadamun baAAda thubootiha watathooqoo alssooa bima sadadtum AAan sabeeli Allahi walakum AAathabun AAatheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 95, "And do not exchange the covenant of Allah for a small price. Indeed, what is with Allah is best for you, if only you could know", "وَلَا تَشۡتَرُواْ بِعَهۡدِ ٱللَّهِ ثَمَنٗا قَلِيلًاۚ إِنَّمَا عِندَ ٱللَّهِ هُوَ خَيۡرٞ لَّكُمۡ إِن كُنتُمۡ تَعۡلَمُونَ", "Wala tashtaroo biAAahdi Allahi thamanan qaleelan innama AAinda Allahi huwa khayrun lakum in kuntum taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 96, "Whatever you have will end, but what Allah has is lasting. And We will surely give those who were patient their reward according to the best of what they used to do", "مَا عِندَكُمۡ يَنفَدُ وَمَا عِندَ ٱللَّهِ بَاقٖۗ وَلَنَجۡزِيَنَّ ٱلَّذِينَ صَبَرُوٓاْ أَجۡرَهُم بِأَحۡسَنِ مَا كَانُواْ يَعۡمَلُونَ", "Ma AAindakum yanfadu wama AAinda Allahi baqin walanajziyanna allatheena sabaroo ajrahum biahsani ma kanoo yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 97, "Whoever does righteousness, whether male or female, while he is a believer - We will surely cause him to live a good life, and We will surely give them their reward [in the Hereafter] according to the best of what they used to do", "مَنۡ عَمِلَ صَٰلِحٗا مِّن ذَكَرٍ أَوۡ أُنثَىٰ وَهُوَ مُؤۡمِنٞ فَلَنُحۡيِيَنَّهُۥ حَيَوٰةٗ طَيِّبَةٗۖ وَلَنَجۡزِيَنَّهُمۡ أَجۡرَهُم بِأَحۡسَنِ مَا كَانُواْ يَعۡمَلُونَ", "Man AAamila salihan min thakarin aw ontha wahuwa muminun falanuhyiyannahu hayatan tayyibatan walanajziyannahum ajrahum biahsani ma kanoo yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 98, "So when you recite the Qur'an, [first] seek refuge in Allah from Satan, the expelled [from His mercy]", "فَإِذَا قَرَأۡتَ ٱلۡقُرۡءَانَ فَٱسۡتَعِذۡ بِٱللَّهِ مِنَ ٱلشَّيۡطَٰنِ ٱلرَّجِيمِ", "Faitha qarata alqurana faistaAAith biAllahi mina alshshaytani alrrajeemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 99, "Indeed, there is for him no authority over those who have believed and rely upon their Lord", "إِنَّهُۥ لَيۡسَ لَهُۥ سُلۡطَٰنٌ عَلَى ٱلَّذِينَ ءَامَنُواْ وَعَلَىٰ رَبِّهِمۡ يَتَوَكَّلُونَ", "Innahu laysa lahu sultanun AAala allatheena amanoo waAAala rabbihim yatawakkaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 100, "His authority is only over those who take him as an ally and those who through him associate others with Allah", "إِنَّمَا سُلۡطَٰنُهُۥ عَلَى ٱلَّذِينَ يَتَوَلَّوۡنَهُۥ وَٱلَّذِينَ هُم بِهِۦ مُشۡرِكُونَ", "Innama sultanuhu AAala allatheena yatawallawnahu waallatheena hum bihi mushrikoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 101, "And when We substitute a verse in place of a verse - and Allah is most knowing of what He sends down - they say, ""You, [O Muhammad], are but an inventor [of lies]."" But most of them do not know", "وَإِذَا بَدَّلۡنَآ ءَايَةٗ مَّكَانَ ءَايَةٖ وَٱللَّهُ أَعۡلَمُ بِمَا يُنَزِّلُ قَالُوٓاْ إِنَّمَآ أَنتَ مُفۡتَرِۭۚ بَلۡ أَكۡثَرُهُمۡ لَا يَعۡلَمُونَ", "Waitha baddalna ayatan makana ayatin waAllahu aAAlamu bima yunazzilu qaloo innama anta muftarin bal aktharuhum la yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 102, "Say, [O Muhammad], ""The Pure Spirit has brought it down from your Lord in truth to make firm those who believe and as guidance and good tidings to the Muslims", "قُلۡ نَزَّلَهُۥ رُوحُ ٱلۡقُدُسِ مِن رَّبِّكَ بِٱلۡحَقِّ لِيُثَبِّتَ ٱلَّذِينَ ءَامَنُواْ وَهُدٗى وَبُشۡرَىٰ لِلۡمُسۡلِمِينَ", "Qul nazzalahu roohu alqudusi min rabbika bialhaqqi liyuthabbita allatheena amanoo wahudan wabushra lilmuslimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 103, "And We certainly know that they say, ""It is only a human being who teaches the Prophet."" The tongue of the one they refer to is foreign, and this Qur'an is [in] a clear Arabic language", "وَلَقَدۡ نَعۡلَمُ أَنَّهُمۡ يَقُولُونَ إِنَّمَا يُعَلِّمُهُۥ بَشَرٞۗ لِّسَانُ ٱلَّذِي يُلۡحِدُونَ إِلَيۡهِ أَعۡجَمِيّٞ وَهَٰذَا لِسَانٌ عَرَبِيّٞ مُّبِينٌ", "Walaqad naAAlamu annahum yaqooloona innama yuAAallimuhu basharun lisanu allathee yulhidoona ilayhi aAAjamiyyun wahatha lisanun AAarabiyyun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 104, "Indeed, those who do not believe in the verses of Allah - Allah will not guide them, and for them is a painful punishment", "إِنَّ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِـَٔايَٰتِ ٱللَّهِ لَا يَهۡدِيهِمُ ٱللَّهُ وَلَهُمۡ عَذَابٌ أَلِيمٌ", "Inna allatheena la yuminoona biayati Allahi la yahdeehimu Allahu walahum AAathabun aleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 105, "They only invent falsehood who do not believe in the verses of Allah, and it is those who are the liars", "إِنَّمَا يَفۡتَرِي ٱلۡكَذِبَ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِـَٔايَٰتِ ٱللَّهِۖ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡكَٰذِبُونَ", "Innama yaftaree alkathiba allatheena la yuminoona biayati Allahi waolaika humu alkathiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 106, "Whoever disbelieves in Allah after his belief... except for one who is forced [to renounce his religion] while his heart is secure in faith. But those who [willingly] open their breasts to disbelief, upon them is wrath from Allah, and for them is a great punishment", "مَن كَفَرَ بِٱللَّهِ مِنۢ بَعۡدِ إِيمَٰنِهِۦٓ إِلَّا مَنۡ أُكۡرِهَ وَقَلۡبُهُۥ مُطۡمَئِنُّۢ بِٱلۡإِيمَٰنِ وَلَٰكِن مَّن شَرَحَ بِٱلۡكُفۡرِ صَدۡرٗا فَعَلَيۡهِمۡ غَضَبٞ مِّنَ ٱللَّهِ وَلَهُمۡ عَذَابٌ عَظِيمٞ", "Man kafara biAllahi min baAAdi eemanihi illa man okriha waqalbuhu mutmainnun bialeemani walakin man sharaha bialkufri sadran faAAalayhim ghadabun mina Allahi walahum AAathabun AAatheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 107, "That is because they preferred the worldly life over the Hereafter and that Allah does not guide the disbelieving people", "ذَٰلِكَ بِأَنَّهُمُ ٱسۡتَحَبُّواْ ٱلۡحَيَوٰةَ ٱلدُّنۡيَا عَلَى ٱلۡأٓخِرَةِ وَأَنَّ ٱللَّهَ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلۡكَٰفِرِينَ", "Thalika biannahumu istahabboo alhayata alddunya AAala alakhirati waanna Allaha la yahdee alqawma alkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 108, "Those are the ones over whose hearts and hearing and vision Allah has sealed, and it is those who are the heedless", "أُوْلَـٰٓئِكَ ٱلَّذِينَ طَبَعَ ٱللَّهُ عَلَىٰ قُلُوبِهِمۡ وَسَمۡعِهِمۡ وَأَبۡصَٰرِهِمۡۖ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡغَٰفِلُونَ", "Olaika allatheena tabaAAa Allahu AAala quloobihim wasamAAihim waabsarihim waolaika humu alghafiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 109, "Assuredly, it is they, in the Hereafter, who will be the losers", "لَا جَرَمَ أَنَّهُمۡ فِي ٱلۡأٓخِرَةِ هُمُ ٱلۡخَٰسِرُونَ", "La jarama annahum fee alakhirati humu alkhasiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 110, "Then, indeed your Lord, to those who emigrated after they had been compelled [to renounce their religion] and thereafter fought [for the cause of Allah] and were patient - indeed, your Lord, after that, is Forgiving and Merciful", "ثُمَّ إِنَّ رَبَّكَ لِلَّذِينَ هَاجَرُواْ مِنۢ بَعۡدِ مَا فُتِنُواْ ثُمَّ جَٰهَدُواْ وَصَبَرُوٓاْ إِنَّ رَبَّكَ مِنۢ بَعۡدِهَا لَغَفُورٞ رَّحِيمٞ", "Thumma inna rabbaka lillatheena hajaroo min baAAdi ma futinoo thumma jahadoo wasabaroo inna rabbaka min baAAdiha laghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 111, "On the Day when every soul will come disputing for itself, and every soul will be fully compensated for what it did, and they will not be wronged", "۞يَوۡمَ تَأۡتِي كُلُّ نَفۡسٖ تُجَٰدِلُ عَن نَّفۡسِهَا وَتُوَفَّىٰ كُلُّ نَفۡسٖ مَّا عَمِلَتۡ وَهُمۡ لَا يُظۡلَمُونَ", "Yawma tatee kullu nafsin tujadilu AAan nafsiha watuwaffa kullu nafsin ma AAamilat wahum la yuthlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 112, "And Allah presents an example: a city which was safe and secure, its provision coming to it in abundance from every location, but it denied the favors of Allah. So Allah made it taste the envelopment of hunger and fear for what they had been doing", "وَضَرَبَ ٱللَّهُ مَثَلٗا قَرۡيَةٗ كَانَتۡ ءَامِنَةٗ مُّطۡمَئِنَّةٗ يَأۡتِيهَا رِزۡقُهَا رَغَدٗا مِّن كُلِّ مَكَانٖ فَكَفَرَتۡ بِأَنۡعُمِ ٱللَّهِ فَأَذَٰقَهَا ٱللَّهُ لِبَاسَ ٱلۡجُوعِ وَٱلۡخَوۡفِ بِمَا كَانُواْ يَصۡنَعُونَ", "Wadaraba Allahu mathalan qaryatan kanat aminatan mutmainnatan yateeha rizquha raghadan min kulli makanin fakafarat bianAAumi Allahi faathaqaha Allahu libasa aljooAAi waalkhawfi bima kanoo yasnaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 113, "And there had certainly come to them a Messenger from among themselves, but they denied him; so punishment overtook them while they were wrongdoers", "وَلَقَدۡ جَآءَهُمۡ رَسُولٞ مِّنۡهُمۡ فَكَذَّبُوهُ فَأَخَذَهُمُ ٱلۡعَذَابُ وَهُمۡ ظَٰلِمُونَ", "Walaqad jaahum rasoolun minhum fakaththaboohu faakhathahumu alAAathabu wahum thalimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 114, "Then eat of what Allah has provided for you [which is] lawful and good. And be grateful for the favor of Allah, if it is [indeed] Him that you worship", "فَكُلُواْ مِمَّا رَزَقَكُمُ ٱللَّهُ حَلَٰلٗا طَيِّبٗا وَٱشۡكُرُواْ نِعۡمَتَ ٱللَّهِ إِن كُنتُمۡ إِيَّاهُ تَعۡبُدُونَ", "Fakuloo mimma razaqakumu Allahu halalan tayyiban waoshkuroo niAAmata Allahi in kuntum iyyahu taAAbudoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 115, "He has only forbidden to you dead animals, blood, the flesh of swine, and that which has been dedicated to other than Allah. But whoever is forced [by necessity], neither desiring [it] nor transgressing [its limit] - then indeed, Allah is Forgiving and Merciful", "إِنَّمَا حَرَّمَ عَلَيۡكُمُ ٱلۡمَيۡتَةَ وَٱلدَّمَ وَلَحۡمَ ٱلۡخِنزِيرِ وَمَآ أُهِلَّ لِغَيۡرِ ٱللَّهِ بِهِۦۖ فَمَنِ ٱضۡطُرَّ غَيۡرَ بَاغٖ وَلَا عَادٖ فَإِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ", "Innama harrama AAalaykumu almaytata waalddama walahma alkhinzeeri wama ohilla lighayri Allahi bihi famani idturra ghayra baghin wala AAadin fainna Allaha ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 116, "And do not say about what your tongues assert of untruth, ""This is lawful and this is unlawful,"" to invent falsehood about Allah. Indeed, those who invent falsehood about Allah will not succeed", "وَلَا تَقُولُواْ لِمَا تَصِفُ أَلۡسِنَتُكُمُ ٱلۡكَذِبَ هَٰذَا حَلَٰلٞ وَهَٰذَا حَرَامٞ لِّتَفۡتَرُواْ عَلَى ٱللَّهِ ٱلۡكَذِبَۚ إِنَّ ٱلَّذِينَ يَفۡتَرُونَ عَلَى ٱللَّهِ ٱلۡكَذِبَ لَا يُفۡلِحُونَ", "Wala taqooloo lima tasifu alsinatukumu alkathiba hatha halalun wahatha haramun litaftaroo AAala Allahi alkathiba inna allatheena yaftaroona AAala Allahi alkathiba la yuflihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 117, "[It is but] a brief enjoyment, and they will have a painful punishment", "مَتَٰعٞ قَلِيلٞ وَلَهُمۡ عَذَابٌ أَلِيمٞ", "MataAAun qaleelun walahum AAathabun aleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 118, "And to those who are Jews We have prohibited that which We related to you before. And We did not wrong them [thereby], but they were wronging themselves", "وَعَلَى ٱلَّذِينَ هَادُواْ حَرَّمۡنَا مَا قَصَصۡنَا عَلَيۡكَ مِن قَبۡلُۖ وَمَا ظَلَمۡنَٰهُمۡ وَلَٰكِن كَانُوٓاْ أَنفُسَهُمۡ يَظۡلِمُونَ", "WaAAala allatheena hadoo harramna ma qasasna AAalayka min qablu wama thalamnahum walakin kanoo anfusahum yathlimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 119, "Then, indeed your Lord, to those who have done wrong out of ignorance and then repent after that and correct themselves - indeed, your Lord, thereafter, is Forgiving and Merciful", "ثُمَّ إِنَّ رَبَّكَ لِلَّذِينَ عَمِلُواْ ٱلسُّوٓءَ بِجَهَٰلَةٖ ثُمَّ تَابُواْ مِنۢ بَعۡدِ ذَٰلِكَ وَأَصۡلَحُوٓاْ إِنَّ رَبَّكَ مِنۢ بَعۡدِهَا لَغَفُورٞ رَّحِيمٌ", "Thumma inna rabbaka lillatheena AAamiloo alssooa bijahalatin thumma taboo min baAAdi thalika waaslahoo inna rabbaka min baAAdiha laghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 120, "Indeed, Abraham was a [comprehensive] leader, devoutly obedient to Allah, inclining toward truth, and he was not of those who associate others with Allah", "إِنَّ إِبۡرَٰهِيمَ كَانَ أُمَّةٗ قَانِتٗا لِّلَّهِ حَنِيفٗا وَلَمۡ يَكُ مِنَ ٱلۡمُشۡرِكِينَ", "Inna ibraheema kana ommatan qanitan lillahi haneefan walam yaku mina almushrikeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 121, "[He was] grateful for His favors. Allah chose him and guided him to a straight path", "شَاكِرٗا لِّأَنۡعُمِهِۚ ٱجۡتَبَىٰهُ وَهَدَىٰهُ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ", "Shakiran lianAAumihi ijtabahu wahadahu ila siratin mustaqeemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 122, "And We gave him good in this world, and indeed, in the Hereafter he will be among the righteous", "وَءَاتَيۡنَٰهُ فِي ٱلدُّنۡيَا حَسَنَةٗۖ وَإِنَّهُۥ فِي ٱلۡأٓخِرَةِ لَمِنَ ٱلصَّـٰلِحِينَ", "Waataynahu fee alddunya hasanatan wainnahu fee alakhirati lamina alssaliheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 123, "Then We revealed to you, [O Muhammad], to follow the religion of Abraham, inclining toward truth; and he was not of those who associate with Allah", "ثُمَّ أَوۡحَيۡنَآ إِلَيۡكَ أَنِ ٱتَّبِعۡ مِلَّةَ إِبۡرَٰهِيمَ حَنِيفٗاۖ وَمَا كَانَ مِنَ ٱلۡمُشۡرِكِينَ", "Thumma awhayna ilayka ani ittabiAA millata ibraheema haneefan wama kana mina almushrikeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 124, "The sabbath was only appointed for those who differed over it. And indeed, your Lord will judge between them on the Day of Resurrection concerning that over which they used to differ", "إِنَّمَا جُعِلَ ٱلسَّبۡتُ عَلَى ٱلَّذِينَ ٱخۡتَلَفُواْ فِيهِۚ وَإِنَّ رَبَّكَ لَيَحۡكُمُ بَيۡنَهُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ فِيمَا كَانُواْ فِيهِ يَخۡتَلِفُونَ", "Innama juAAila alssabtu AAala allatheena ikhtalafoo feehi wainna rabbaka layahkumu baynahum yawma alqiyamati feema kanoo feehi yakhtalifoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 125, "Invite to the way of your Lord with wisdom and good instruction, and argue with them in a way that is best. Indeed, your Lord is most knowing of who has strayed from His way, and He is most knowing of who is [rightly] guided", "ٱدۡعُ إِلَىٰ سَبِيلِ رَبِّكَ بِٱلۡحِكۡمَةِ وَٱلۡمَوۡعِظَةِ ٱلۡحَسَنَةِۖ وَجَٰدِلۡهُم بِٱلَّتِي هِيَ أَحۡسَنُۚ إِنَّ رَبَّكَ هُوَ أَعۡلَمُ بِمَن ضَلَّ عَن سَبِيلِهِۦ وَهُوَ أَعۡلَمُ بِٱلۡمُهۡتَدِينَ", "OdAAu ila sabeeli rabbika bialhikmati waalmawAAithati alhasanati wajadilhum biallatee hiya ahsanu inna rabbaka huwa aAAlamu biman dalla AAan sabeelihi wahuwa aAAlamu bialmuhtadeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 126, "And if you punish [an enemy, O believers], punish with an equivalent of that with which you were harmed. But if you are patient - it is better for those who are patient", "وَإِنۡ عَاقَبۡتُمۡ فَعَاقِبُواْ بِمِثۡلِ مَا عُوقِبۡتُم بِهِۦۖ وَلَئِن صَبَرۡتُمۡ لَهُوَ خَيۡرٞ لِّلصَّـٰبِرِينَ", "Wain AAaqabtum faAAaqiboo bimithli ma AAooqibtum bihi walain sabartum lahuwa khayrun lilssabireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 127, "And be patient, [O Muhammad], and your patience is not but through Allah. And do not grieve over them and do not be in distress over what they conspire", "وَٱصۡبِرۡ وَمَا صَبۡرُكَ إِلَّا بِٱللَّهِۚ وَلَا تَحۡزَنۡ عَلَيۡهِمۡ وَلَا تَكُ فِي ضَيۡقٖ مِّمَّا يَمۡكُرُونَ", "Waisbir wama sabruka illa biAllahi wala tahzan AAalayhim wala taku fee dayqin mimma yamkuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (16, 128, "Indeed, Allah is with those who fear Him and those who are doers of good", "إِنَّ ٱللَّهَ مَعَ ٱلَّذِينَ ٱتَّقَواْ وَّٱلَّذِينَ هُم مُّحۡسِنُونَ", "Inna Allaha maAAa allatheena ittaqaw waallatheena hum muhsinoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 1, "Exalted is He who took His Servant by night from al-Masjid al-Haram to al-Masjid al-Aqsa, whose surroundings We have blessed, to show him of Our signs. Indeed, He is the Hearing, the Seeing", "سُبۡحَٰنَ ٱلَّذِيٓ أَسۡرَىٰ بِعَبۡدِهِۦ لَيۡلٗا مِّنَ ٱلۡمَسۡجِدِ ٱلۡحَرَامِ إِلَى ٱلۡمَسۡجِدِ ٱلۡأَقۡصَا ٱلَّذِي بَٰرَكۡنَا حَوۡلَهُۥ لِنُرِيَهُۥ مِنۡ ءَايَٰتِنَآۚ إِنَّهُۥ هُوَ ٱلسَّمِيعُ ٱلۡبَصِيرُ", "Subhana allathee asra biAAabdihi laylan mina almasjidi alharami ila almasjidi alaqsa allathee barakna hawlahu linuriyahu min ayatina innahu huwa alssameeAAu albaseeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 2, "And We gave Moses the Scripture and made it a guidance for the Children of Israel that you not take other than Me as Disposer of affairs", "وَءَاتَيۡنَا مُوسَى ٱلۡكِتَٰبَ وَجَعَلۡنَٰهُ هُدٗى لِّبَنِيٓ إِسۡرَـٰٓءِيلَ أَلَّا تَتَّخِذُواْ مِن دُونِي وَكِيلٗا", "Waatayna moosa alkitaba wajaAAalnahu hudan libanee israeela alla tattakhithoo min doonee wakeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 3, "O descendants of those We carried [in the ship] with Noah. Indeed, he was a grateful servant", "ذُرِّيَّةَ مَنۡ حَمَلۡنَا مَعَ نُوحٍۚ إِنَّهُۥ كَانَ عَبۡدٗا شَكُورٗا", "Thurriyyata man hamalna maAAa noohin innahu kana AAabdan shakooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 4, "And We conveyed to the Children of Israel in the Scripture that, ""You will surely cause corruption on the earth twice, and you will surely reach [a degree of] great haughtiness", "وَقَضَيۡنَآ إِلَىٰ بَنِيٓ إِسۡرَـٰٓءِيلَ فِي ٱلۡكِتَٰبِ لَتُفۡسِدُنَّ فِي ٱلۡأَرۡضِ مَرَّتَيۡنِ وَلَتَعۡلُنَّ عُلُوّٗا كَبِيرٗا", "Waqadayna ila banee israeela fee alkitabi latufsidunna fee alardi marratayni walataAAlunna AAuluwwan kabeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 5, "So when the [time of] promise came for the first of them, We sent against you servants of Ours - those of great military might, and they probed [even] into the homes, and it was a promise fulfilled", "فَإِذَا جَآءَ وَعۡدُ أُولَىٰهُمَا بَعَثۡنَا عَلَيۡكُمۡ عِبَادٗا لَّنَآ أُوْلِي بَأۡسٖ شَدِيدٖ فَجَاسُواْ خِلَٰلَ ٱلدِّيَارِۚ وَكَانَ وَعۡدٗا مَّفۡعُولٗا", "Faitha jaa waAAdu oolahuma baAAathna AAalaykum AAibadan lana olee basin shadeedin fajasoo khilala alddiyari wakana waAAdan mafAAoolan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 6, "Then We gave back to you a return victory over them. And We reinforced you with wealth and sons and made you more numerous in manpower", "ثُمَّ رَدَدۡنَا لَكُمُ ٱلۡكَرَّةَ عَلَيۡهِمۡ وَأَمۡدَدۡنَٰكُم بِأَمۡوَٰلٖ وَبَنِينَ وَجَعَلۡنَٰكُمۡ أَكۡثَرَ نَفِيرًا", "Thumma radadna lakumu alkarrata AAalayhim waamdadnakum biamwalin wabaneena wajaAAalnakum akthara nafeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 7, "[And said], ""If you do good, you do good for yourselves; and if you do evil, [you do it] to yourselves."" Then when the final promise came, [We sent your enemies] to sadden your faces and to enter the temple in Jerusalem, as they entered it the first time, and to destroy what they had taken over with [total] destruction", "إِنۡ أَحۡسَنتُمۡ أَحۡسَنتُمۡ لِأَنفُسِكُمۡۖ وَإِنۡ أَسَأۡتُمۡ فَلَهَاۚ فَإِذَا جَآءَ وَعۡدُ ٱلۡأٓخِرَةِ لِيَسُـُٔواْ وُجُوهَكُمۡ وَلِيَدۡخُلُواْ ٱلۡمَسۡجِدَ كَمَا دَخَلُوهُ أَوَّلَ مَرَّةٖ وَلِيُتَبِّرُواْ مَا عَلَوۡاْ تَتۡبِيرًا", "In ahsantum ahsantum lianfusikum wain asatum falaha faitha jaa waAAdu alakhirati liyasoooo wujoohakum waliyadkhuloo almasjida kama dakhaloohu awwala marratin waliyutabbiroo ma AAalaw tatbeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 8, "[Then Allah said], ""It is expected, [if you repent], that your Lord will have mercy upon you. But if you return [to sin], We will return [to punishment]. And We have made Hell, for the disbelievers, a prison-bed", "عَسَىٰ رَبُّكُمۡ أَن يَرۡحَمَكُمۡۚ وَإِنۡ عُدتُّمۡ عُدۡنَاۚ وَجَعَلۡنَا جَهَنَّمَ لِلۡكَٰفِرِينَ حَصِيرًا", "AAasa rabbukum an yarhamakum wain AAudtum AAudna wajaAAalna jahannama lilkafireena haseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 9, "Indeed, this Qur'an guides to that which is most suitable and gives good tidings to the believers who do righteous deeds that they will have a great reward", "إِنَّ هَٰذَا ٱلۡقُرۡءَانَ يَهۡدِي لِلَّتِي هِيَ أَقۡوَمُ وَيُبَشِّرُ ٱلۡمُؤۡمِنِينَ ٱلَّذِينَ يَعۡمَلُونَ ٱلصَّـٰلِحَٰتِ أَنَّ لَهُمۡ أَجۡرٗا كَبِيرٗا", "Inna hatha alqurana yahdee lillatee hiya aqwamu wayubashshiru almumineena allatheena yaAAmaloona alssalihati anna lahum ajran kabeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 10, "And that those who do not believe in the Hereafter - We have prepared for them a painful punishment", "وَأَنَّ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱلۡأٓخِرَةِ أَعۡتَدۡنَا لَهُمۡ عَذَابًا أَلِيمٗا", "Waanna allatheena la yuminoona bialakhirati aAAtadna lahum AAathaban aleeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 11, "And man supplicates for evil as he supplicates for good, and man is ever hasty", "وَيَدۡعُ ٱلۡإِنسَٰنُ بِٱلشَّرِّ دُعَآءَهُۥ بِٱلۡخَيۡرِۖ وَكَانَ ٱلۡإِنسَٰنُ عَجُولٗا", "WayadAAu alinsanu bialshsharri duAAaahu bialkhayri wakana alinsanu AAajoolan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 12, "And We have made the night and day two signs, and We erased the sign of the night and made the sign of the day visible that you may seek bounty from your Lord and may know the number of years and the account [of time]. And everything We have set out in detail", "وَجَعَلۡنَا ٱلَّيۡلَ وَٱلنَّهَارَ ءَايَتَيۡنِۖ فَمَحَوۡنَآ ءَايَةَ ٱلَّيۡلِ وَجَعَلۡنَآ ءَايَةَ ٱلنَّهَارِ مُبۡصِرَةٗ لِّتَبۡتَغُواْ فَضۡلٗا مِّن رَّبِّكُمۡ وَلِتَعۡلَمُواْ عَدَدَ ٱلسِّنِينَ وَٱلۡحِسَابَۚ وَكُلَّ شَيۡءٖ فَصَّلۡنَٰهُ تَفۡصِيلٗا", "WajaAAalna allayla waalnnahara ayatayni famahawna ayata allayli wajaAAalna ayata alnnahari mubsiratan litabtaghoo fadlan min rabbikum walitaAAlamoo AAadada alssineena waalhisaba wakulla shayin fassalnahu tafseelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 13, "And [for] every person We have imposed his fate upon his neck, and We will produce for him on the Day of Resurrection a record which he will encounter spread open", "وَكُلَّ إِنسَٰنٍ أَلۡزَمۡنَٰهُ طَـٰٓئِرَهُۥ فِي عُنُقِهِۦۖ وَنُخۡرِجُ لَهُۥ يَوۡمَ ٱلۡقِيَٰمَةِ كِتَٰبٗا يَلۡقَىٰهُ مَنشُورًا", "Wakulla insanin alzamnahu tairahu fee AAunuqihi wanukhriju lahu yawma alqiyamati kitaban yalqahu manshooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 14, "[It will be said], ""Read your record. Sufficient is yourself against you this Day as accountant", "ٱقۡرَأۡ كِتَٰبَكَ كَفَىٰ بِنَفۡسِكَ ٱلۡيَوۡمَ عَلَيۡكَ حَسِيبٗا", "Iqra kitabaka kafa binafsika alyawma AAalayka haseeban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 15, "Whoever is guided is only guided for [the benefit of] his soul. And whoever errs only errs against it. And no bearer of burdens will bear the burden of another. And never would We punish until We sent a messenger", "مَّنِ ٱهۡتَدَىٰ فَإِنَّمَا يَهۡتَدِي لِنَفۡسِهِۦۖ وَمَن ضَلَّ فَإِنَّمَا يَضِلُّ عَلَيۡهَاۚ وَلَا تَزِرُ وَازِرَةٞ وِزۡرَ أُخۡرَىٰۗ وَمَا كُنَّا مُعَذِّبِينَ حَتَّىٰ نَبۡعَثَ رَسُولٗا", "Mani ihtada fainnama yahtadee linafsihi waman dalla fainnama yadillu AAalayha wala taziru waziratun wizra okhra wama kunna muAAaththibeena hatta nabAAatha rasoolan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 16, "And when We intend to destroy a city, We command its affluent but they defiantly disobey therein; so the word comes into effect upon it, and We destroy it with [complete] destruction", "وَإِذَآ أَرَدۡنَآ أَن نُّهۡلِكَ قَرۡيَةً أَمَرۡنَا مُتۡرَفِيهَا فَفَسَقُواْ فِيهَا فَحَقَّ عَلَيۡهَا ٱلۡقَوۡلُ فَدَمَّرۡنَٰهَا تَدۡمِيرٗا", "Waitha aradna an nuhlika qaryatan amarna mutrafeeha fafasaqoo feeha fahaqqa AAalayha alqawlu fadammarnaha tadmeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 17, "And how many have We destroyed from the generations after Noah. And sufficient is your Lord, concerning the sins of His servants, as Acquainted and Seeing", "وَكَمۡ أَهۡلَكۡنَا مِنَ ٱلۡقُرُونِ مِنۢ بَعۡدِ نُوحٖۗ وَكَفَىٰ بِرَبِّكَ بِذُنُوبِ عِبَادِهِۦ خَبِيرَۢا بَصِيرٗا", "Wakam ahlakna mina alqurooni min baAAdi noohin wakafa birabbika bithunoobi AAibadihi khabeeran baseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 18, "Whoever should desire the immediate - We hasten for him from it what We will to whom We intend. Then We have made for him Hell, which he will [enter to] burn, censured and banished", "مَّن كَانَ يُرِيدُ ٱلۡعَاجِلَةَ عَجَّلۡنَا لَهُۥ فِيهَا مَا نَشَآءُ لِمَن نُّرِيدُ ثُمَّ جَعَلۡنَا لَهُۥ جَهَنَّمَ يَصۡلَىٰهَا مَذۡمُومٗا مَّدۡحُورٗا", "Man kana yureedu alAAajilata AAajjalna lahu feeha ma nashao liman nureedu thumma jaAAalna lahu jahannama yaslaha mathmooman madhooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 19, "But whoever desires the Hereafter and exerts the effort due to it while he is a believer - it is those whose effort is ever appreciated [by Allah]", "وَمَنۡ أَرَادَ ٱلۡأٓخِرَةَ وَسَعَىٰ لَهَا سَعۡيَهَا وَهُوَ مُؤۡمِنٞ فَأُوْلَـٰٓئِكَ كَانَ سَعۡيُهُم مَّشۡكُورٗا", "Waman arada alakhirata wasaAAa laha saAAyaha wahuwa muminun faolaika kana saAAyuhum mashkooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 20, "To each [category] We extend - to these and to those - from the gift of your Lord. And never has the gift of your Lord been restricted", "كُلّٗا نُّمِدُّ هَـٰٓؤُلَآءِ وَهَـٰٓؤُلَآءِ مِنۡ عَطَآءِ رَبِّكَۚ وَمَا كَانَ عَطَآءُ رَبِّكَ مَحۡظُورًا", "Kullan numiddu haolai wahaolai min AAatai rabbika wama kana AAatao rabbika mahthooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 21, "Look how We have favored [in provision] some of them over others. But the Hereafter is greater in degrees [of difference] and greater in distinction", "ٱنظُرۡ كَيۡفَ فَضَّلۡنَا بَعۡضَهُمۡ عَلَىٰ بَعۡضٖۚ وَلَلۡأٓخِرَةُ أَكۡبَرُ دَرَجَٰتٖ وَأَكۡبَرُ تَفۡضِيلٗا", "Onthur kayfa faddalna baAAdahum AAala baAAdin walalakhiratu akbaru darajatin waakbaru tafdeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 22, "Do not make [as equal] with Allah another deity and [thereby] become censured and forsaken", "لَّا تَجۡعَلۡ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَ فَتَقۡعُدَ مَذۡمُومٗا مَّخۡذُولٗا", "La tajAAal maAAa Allahi ilahan akhara fataqAAuda mathmooman makhthoolan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 23, "And your Lord has decreed that you not worship except Him, and to parents, good treatment. Whether one or both of them reach old age [while] with you, say not to them [so much as], ""uff,"" and do not repel them but speak to them a noble word", "۞وَقَضَىٰ رَبُّكَ أَلَّا تَعۡبُدُوٓاْ إِلَّآ إِيَّاهُ وَبِٱلۡوَٰلِدَيۡنِ إِحۡسَٰنًاۚ إِمَّا يَبۡلُغَنَّ عِندَكَ ٱلۡكِبَرَ أَحَدُهُمَآ أَوۡ كِلَاهُمَا فَلَا تَقُل لَّهُمَآ أُفّٖ وَلَا تَنۡهَرۡهُمَا وَقُل لَّهُمَا قَوۡلٗا كَرِيمٗا", "Waqada rabbuka alla taAAbudoo illa iyyahu wabialwalidayni ihsanan imma yablughanna AAindaka alkibara ahaduhuma aw kilahuma fala taqul lahuma offin wala tanharhuma waqul lahuma qawlan kareeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 24, "And lower to them the wing of humility out of mercy and say, ""My Lord, have mercy upon them as they brought me up [when I was] small", "وَٱخۡفِضۡ لَهُمَا جَنَاحَ ٱلذُّلِّ مِنَ ٱلرَّحۡمَةِ وَقُل رَّبِّ ٱرۡحَمۡهُمَا كَمَا رَبَّيَانِي صَغِيرٗا", "Waikhfid lahuma janaha alththulli mina alrrahmati waqul rabbi irhamhuma kama rabbayanee sagheeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 25, "Your Lord is most knowing of what is within yourselves. If you should be righteous [in intention] - then indeed He is ever, to the often returning [to Him], Forgiving", "رَّبُّكُمۡ أَعۡلَمُ بِمَا فِي نُفُوسِكُمۡۚ إِن تَكُونُواْ صَٰلِحِينَ فَإِنَّهُۥ كَانَ لِلۡأَوَّـٰبِينَ غَفُورٗا", "Rabbukum aAAlamu bima fee nufoosikum in takoonoo saliheena fainnahu kana lilawwabeena ghafooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 26, "And give the relative his right, and [also] the poor and the traveler, and do not spend wastefully", "وَءَاتِ ذَا ٱلۡقُرۡبَىٰ حَقَّهُۥ وَٱلۡمِسۡكِينَ وَٱبۡنَ ٱلسَّبِيلِ وَلَا تُبَذِّرۡ تَبۡذِيرًا", "Waati tha alqurba haqqahu waalmiskeena waibna alssabeeli wala tubaththir tabtheeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 27, "Indeed, the wasteful are brothers of the devils, and ever has Satan been to his Lord ungrateful", "إِنَّ ٱلۡمُبَذِّرِينَ كَانُوٓاْ إِخۡوَٰنَ ٱلشَّيَٰطِينِۖ وَكَانَ ٱلشَّيۡطَٰنُ لِرَبِّهِۦ كَفُورٗا", "Inna almubaththireena kanoo ikhwana alshshayateeni wakana alshshaytanu lirabbihi kafooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 28, "And if you [must] turn away from the needy awaiting mercy from your Lord which you expect, then speak to them a gentle word", "وَإِمَّا تُعۡرِضَنَّ عَنۡهُمُ ٱبۡتِغَآءَ رَحۡمَةٖ مِّن رَّبِّكَ تَرۡجُوهَا فَقُل لَّهُمۡ قَوۡلٗا مَّيۡسُورٗا", "Waimma tuAAridanna AAanhumu ibtighaa rahmatin min rabbika tarjooha faqul lahum qawlan maysooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 29, "And do not make your hand [as] chained to your neck or extend it completely and [thereby] become blamed and insolvent", "وَلَا تَجۡعَلۡ يَدَكَ مَغۡلُولَةً إِلَىٰ عُنُقِكَ وَلَا تَبۡسُطۡهَا كُلَّ ٱلۡبَسۡطِ فَتَقۡعُدَ مَلُومٗا مَّحۡسُورًا", "Wala tajAAal yadaka maghloolatan ila AAunuqika wala tabsutha kulla albasti fataqAAuda malooman mahsooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 30, "Indeed, your Lord extends provision for whom He wills and restricts [it]. Indeed He is ever, concerning His servants, Acquainted and Seeing", "إِنَّ رَبَّكَ يَبۡسُطُ ٱلرِّزۡقَ لِمَن يَشَآءُ وَيَقۡدِرُۚ إِنَّهُۥ كَانَ بِعِبَادِهِۦ خَبِيرَۢا بَصِيرٗا", "Inna rabbaka yabsutu alrrizqa liman yashao wayaqdiru innahu kana biAAibadihi khabeeran baseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 31, "And do not kill your children for fear of poverty. We provide for them and for you. Indeed, their killing is ever a great sin", "وَلَا تَقۡتُلُوٓاْ أَوۡلَٰدَكُمۡ خَشۡيَةَ إِمۡلَٰقٖۖ نَّحۡنُ نَرۡزُقُهُمۡ وَإِيَّاكُمۡۚ إِنَّ قَتۡلَهُمۡ كَانَ خِطۡـٔٗا كَبِيرٗا", "Wala taqtuloo awladakum khashyata imlaqin nahnu narzuquhum waiyyakum inna qatlahum kana khitan kabeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 32, "And do not approach unlawful sexual intercourse. Indeed, it is ever an immorality and is evil as a way", "وَلَا تَقۡرَبُواْ ٱلزِّنَىٰٓۖ إِنَّهُۥ كَانَ فَٰحِشَةٗ وَسَآءَ سَبِيلٗا", "Wala taqraboo alzzina innahu kana fahishatan wasaa sabeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 33, "And do not kill the soul which Allah has forbidden, except by right. And whoever is killed unjustly - We have given his heir authority, but let him not exceed limits in [the matter of] taking life. Indeed, he has been supported [by the law]", "وَلَا تَقۡتُلُواْ ٱلنَّفۡسَ ٱلَّتِي حَرَّمَ ٱللَّهُ إِلَّا بِٱلۡحَقِّۗ وَمَن قُتِلَ مَظۡلُومٗا فَقَدۡ جَعَلۡنَا لِوَلِيِّهِۦ سُلۡطَٰنٗا فَلَا يُسۡرِف فِّي ٱلۡقَتۡلِۖ إِنَّهُۥ كَانَ مَنصُورٗا", "Wala taqtuloo alnnafsa allatee harrama Allahu illa bialhaqqi waman qutila mathlooman faqad jaAAalna liwaliyyihi sultanan fala yusrif fee alqatli innahu kana mansooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 34, "And do not approach the property of an orphan, except in the way that is best, until he reaches maturity. And fulfill [every] commitment. Indeed, the commitment is ever [that about which one will be] questioned", "وَلَا تَقۡرَبُواْ مَالَ ٱلۡيَتِيمِ إِلَّا بِٱلَّتِي هِيَ أَحۡسَنُ حَتَّىٰ يَبۡلُغَ أَشُدَّهُۥۚ وَأَوۡفُواْ بِٱلۡعَهۡدِۖ إِنَّ ٱلۡعَهۡدَ كَانَ مَسۡـُٔولٗا", "Wala taqraboo mala alyateemi illa biallatee hiya ahsanu hatta yablugha ashuddahu waawfoo bialAAahdi inna alAAahda kana masoolan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 35, "And give full measure when you measure, and weigh with an even balance. That is the best [way] and best in result", "وَأَوۡفُواْ ٱلۡكَيۡلَ إِذَا كِلۡتُمۡ وَزِنُواْ بِٱلۡقِسۡطَاسِ ٱلۡمُسۡتَقِيمِۚ ذَٰلِكَ خَيۡرٞ وَأَحۡسَنُ تَأۡوِيلٗا", "Waawfoo alkayla itha kiltum wazinoo bialqistasi almustaqeemi thalika khayrun waahsanu taweelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 36, "And do not pursue that of which you have no knowledge. Indeed, the hearing, the sight and the heart - about all those [one] will be questioned", "وَلَا تَقۡفُ مَا لَيۡسَ لَكَ بِهِۦ عِلۡمٌۚ إِنَّ ٱلسَّمۡعَ وَٱلۡبَصَرَ وَٱلۡفُؤَادَ كُلُّ أُوْلَـٰٓئِكَ كَانَ عَنۡهُ مَسۡـُٔولٗا", "Wala taqfu ma laysa laka bihi AAilmun inna alssamAAa waalbasara waalfuada kullu olaika kana AAanhu masoolan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 37, "And do not walk upon the earth exultantly. Indeed, you will never tear the earth [apart], and you will never reach the mountains in height", "وَلَا تَمۡشِ فِي ٱلۡأَرۡضِ مَرَحًاۖ إِنَّكَ لَن تَخۡرِقَ ٱلۡأَرۡضَ وَلَن تَبۡلُغَ ٱلۡجِبَالَ طُولٗا", "Wala tamshi fee alardi marahan innaka lan takhriqa alarda walan tablugha aljibala toolan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 38, "All that - its evil is ever, in the sight of your Lord, detested", "كُلُّ ذَٰلِكَ كَانَ سَيِّئُهُۥ عِندَ رَبِّكَ مَكۡرُوهٗا", "Kullu thalika kana sayyiohu AAinda rabbika makroohan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 39, "That is from what your Lord has revealed to you, [O Muhammad], of wisdom. And, [O mankind], do not make [as equal] with Allah another deity, lest you be thrown into Hell, blamed and banished", "ذَٰلِكَ مِمَّآ أَوۡحَىٰٓ إِلَيۡكَ رَبُّكَ مِنَ ٱلۡحِكۡمَةِۗ وَلَا تَجۡعَلۡ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَ فَتُلۡقَىٰ فِي جَهَنَّمَ مَلُومٗا مَّدۡحُورًا", "Thalika mimma awha ilayka rabbuka mina alhikmati wala tajAAal maAAa Allahi ilahan akhara fatulqa fee jahannama malooman madhooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 40, "Then, has your Lord chosen you for [having] sons and taken from among the angels daughters? Indeed, you say a grave saying", "أَفَأَصۡفَىٰكُمۡ رَبُّكُم بِٱلۡبَنِينَ وَٱتَّخَذَ مِنَ ٱلۡمَلَـٰٓئِكَةِ إِنَٰثًاۚ إِنَّكُمۡ لَتَقُولُونَ قَوۡلًا عَظِيمٗا", "Afaasfakum rabbukum bialbaneena waittakhatha mina almalaikati inathan innakum lataqooloona qawlan AAatheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 41, "And We have certainly diversified [the contents] in this Qur'an that mankind may be reminded, but it does not increase the disbelievers except in aversion", "وَلَقَدۡ صَرَّفۡنَا فِي هَٰذَا ٱلۡقُرۡءَانِ لِيَذَّكَّرُواْ وَمَا يَزِيدُهُمۡ إِلَّا نُفُورٗا", "Walaqad sarrafna fee hatha alqurani liyaththakkaroo wama yazeeduhum illa nufooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 42, "Say, [O Muhammad], ""If there had been with Him [other] gods, as they say, then they [each] would have sought to the Owner of the Throne a way", "قُل لَّوۡ كَانَ مَعَهُۥٓ ءَالِهَةٞ كَمَا يَقُولُونَ إِذٗا لَّٱبۡتَغَوۡاْ إِلَىٰ ذِي ٱلۡعَرۡشِ سَبِيلٗا", "Qul law kana maAAahu alihatun kama yaqooloona ithan laibtaghaw ila thee alAAarshi sabeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 43, "Exalted is He and high above what they say by great sublimity", "سُبۡحَٰنَهُۥ وَتَعَٰلَىٰ عَمَّا يَقُولُونَ عُلُوّٗا كَبِيرٗا", "Subhanahu wataAAala AAamma yaqooloona AAuluwwan kabeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 44, "The seven heavens and the earth and whatever is in them exalt Him. And there is not a thing except that it exalts [Allah] by His praise, but you do not understand their [way of] exalting. Indeed, He is ever Forbearing and Forgiving", "تُسَبِّحُ لَهُ ٱلسَّمَٰوَٰتُ ٱلسَّبۡعُ وَٱلۡأَرۡضُ وَمَن فِيهِنَّۚ وَإِن مِّن شَيۡءٍ إِلَّا يُسَبِّحُ بِحَمۡدِهِۦ وَلَٰكِن لَّا تَفۡقَهُونَ تَسۡبِيحَهُمۡۚ إِنَّهُۥ كَانَ حَلِيمًا غَفُورٗا", "Tusabbihu lahu alssamawatu alssabAAu waalardu waman feehinna wain min shayin illa yusabbihu bihamdihi walakin la tafqahoona tasbeehahum innahu kana haleeman ghafooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 45, "And when you recite the Qur'an, We put between you and those who do not believe in the Hereafter a concealed partition", "وَإِذَا قَرَأۡتَ ٱلۡقُرۡءَانَ جَعَلۡنَا بَيۡنَكَ وَبَيۡنَ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱلۡأٓخِرَةِ حِجَابٗا مَّسۡتُورٗا", "Waitha qarata alqurana jaAAalna baynaka wabayna allatheena la yuminoona bialakhirati hijaban mastooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 46, "And We have placed over their hearts coverings, lest they understand it, and in their ears deafness. And when you mention your Lord alone in the Qur'an, they turn back in aversion", "وَجَعَلۡنَا عَلَىٰ قُلُوبِهِمۡ أَكِنَّةً أَن يَفۡقَهُوهُ وَفِيٓ ءَاذَانِهِمۡ وَقۡرٗاۚ وَإِذَا ذَكَرۡتَ رَبَّكَ فِي ٱلۡقُرۡءَانِ وَحۡدَهُۥ وَلَّوۡاْ عَلَىٰٓ أَدۡبَٰرِهِمۡ نُفُورٗا", "WajaAAalna AAala quloobihim akinnatan an yafqahoohu wafee athanihim waqran waitha thakarta rabbaka fee alqurani wahdahu wallaw AAala adbarihim nufooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 47, "We are most knowing of how they listen to it when they listen to you and [of] when they are in private conversation, when the wrongdoers say, ""You follow not but a man affected by magic", "نَّحۡنُ أَعۡلَمُ بِمَا يَسۡتَمِعُونَ بِهِۦٓ إِذۡ يَسۡتَمِعُونَ إِلَيۡكَ وَإِذۡ هُمۡ نَجۡوَىٰٓ إِذۡ يَقُولُ ٱلظَّـٰلِمُونَ إِن تَتَّبِعُونَ إِلَّا رَجُلٗا مَّسۡحُورًا", "Nahnu aAAlamu bima yastamiAAoona bihi ith yastamiAAoona ilayka waith hum najwa ith yaqoolu alththalimoona in tattabiAAoona illa rajulan mashooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 48, "Look how they strike for you comparisons; but they have strayed, so they cannot [find] a way", "ٱنظُرۡ كَيۡفَ ضَرَبُواْ لَكَ ٱلۡأَمۡثَالَ فَضَلُّواْ فَلَا يَسۡتَطِيعُونَ سَبِيلٗا", "Onthur kayfa daraboo laka alamthala fadalloo fala yastateeAAoona sabeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 49, "And they say, ""When we are bones and crumbled particles, will we [truly] be resurrected as a new creation", "وَقَالُوٓاْ أَءِذَا كُنَّا عِظَٰمٗا وَرُفَٰتًا أَءِنَّا لَمَبۡعُوثُونَ خَلۡقٗا جَدِيدٗا", "Waqaloo aitha kunna AAithaman warufatan ainna lamabAAoothoona khalqan jadeedan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 50, "Say, ""Be you stones or iron", "۞قُلۡ كُونُواْ حِجَارَةً أَوۡ حَدِيدًا", "Qul koonoo hijaratan aw hadeedan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 51, "Or [any] creation of that which is great within your breasts."" And they will say, ""Who will restore us?"" Say, ""He who brought you forth the first time."" Then they will nod their heads toward you and say, ""When is that?"" Say, ""Perhaps it will be soon", "أَوۡ خَلۡقٗا مِّمَّا يَكۡبُرُ فِي صُدُورِكُمۡۚ فَسَيَقُولُونَ مَن يُعِيدُنَاۖ قُلِ ٱلَّذِي فَطَرَكُمۡ أَوَّلَ مَرَّةٖۚ فَسَيُنۡغِضُونَ إِلَيۡكَ رُءُوسَهُمۡ وَيَقُولُونَ مَتَىٰ هُوَۖ قُلۡ عَسَىٰٓ أَن يَكُونَ قَرِيبٗا", "Aw khalqan mimma yakburu fee sudoorikum fasayaqooloona man yuAAeeduna quli allathee fatarakum awwala marratin fasayunghidoona ilayka ruoosahum wayaqooloona mata huwa qul AAasa an yakoona qareeban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 52, "On the Day He will call you and you will respond with praise of Him and think that you had not remained [in the world] except for a little", "يَوۡمَ يَدۡعُوكُمۡ فَتَسۡتَجِيبُونَ بِحَمۡدِهِۦ وَتَظُنُّونَ إِن لَّبِثۡتُمۡ إِلَّا قَلِيلٗا", "Yawma yadAAookum fatastajeeboona bihamdihi watathunnoona in labithtum illa qaleelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 53, "And tell My servants to say that which is best. Indeed, Satan induces [dissension] among them. Indeed Satan is ever, to mankind, a clear enemy", "وَقُل لِّعِبَادِي يَقُولُواْ ٱلَّتِي هِيَ أَحۡسَنُۚ إِنَّ ٱلشَّيۡطَٰنَ يَنزَغُ بَيۡنَهُمۡۚ إِنَّ ٱلشَّيۡطَٰنَ كَانَ لِلۡإِنسَٰنِ عَدُوّٗا مُّبِينٗا", "Waqul liAAibadee yaqooloo allatee hiya ahsanu inna alshshaytana yanzaghu baynahum inna alshshaytana kana lilinsani AAaduwwan mubeenan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 54, "Your Lord is most knowing of you. If He wills, He will have mercy upon you; or if He wills, He will punish you. And We have not sent you, [O Muhammad], over them as a manager", "رَّبُّكُمۡ أَعۡلَمُ بِكُمۡۖ إِن يَشَأۡ يَرۡحَمۡكُمۡ أَوۡ إِن يَشَأۡ يُعَذِّبۡكُمۡۚ وَمَآ أَرۡسَلۡنَٰكَ عَلَيۡهِمۡ وَكِيلٗا", "Rabbukum aAAlamu bikum in yasha yarhamkum aw in yasha yuAAaththibkum wama arsalnaka AAalayhim wakeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 55, "And your Lord is most knowing of whoever is in the heavens and the earth. And We have made some of the prophets exceed others [in various ways], and to David We gave the book [of Psalms]", "وَرَبُّكَ أَعۡلَمُ بِمَن فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۗ وَلَقَدۡ فَضَّلۡنَا بَعۡضَ ٱلنَّبِيِّـۧنَ عَلَىٰ بَعۡضٖۖ وَءَاتَيۡنَا دَاوُۥدَ زَبُورٗا", "Warabbuka aAAlamu biman fee alssamawati waalardi walaqad faddalna baAAda alnnabiyyeena AAala baAAdin waatayna dawooda zabooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 56, "Say, ""Invoke those you have claimed [as gods] besides Him, for they do not possess the [ability for] removal of adversity from you or [for its] transfer [to someone else]", "قُلِ ٱدۡعُواْ ٱلَّذِينَ زَعَمۡتُم مِّن دُونِهِۦ فَلَا يَمۡلِكُونَ كَشۡفَ ٱلضُّرِّ عَنكُمۡ وَلَا تَحۡوِيلًا", "Quli odAAu allatheena zaAAamtum min doonihi fala yamlikoona kashfa alddurri AAankum wala tahweelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 57, "Those whom they invoke seek means of access to their Lord, [striving as to] which of them would be nearest, and they hope for His mercy and fear His punishment. Indeed, the punishment of your Lord is ever feared", "أُوْلَـٰٓئِكَ ٱلَّذِينَ يَدۡعُونَ يَبۡتَغُونَ إِلَىٰ رَبِّهِمُ ٱلۡوَسِيلَةَ أَيُّهُمۡ أَقۡرَبُ وَيَرۡجُونَ رَحۡمَتَهُۥ وَيَخَافُونَ عَذَابَهُۥٓۚ إِنَّ عَذَابَ رَبِّكَ كَانَ مَحۡذُورٗا", "Olaika allatheena yadAAoona yabtaghoona ila rabbihimu alwaseelata ayyuhum aqrabu wayarjoona rahmatahu wayakhafoona AAathabahu inna AAathaba rabbika kana mahthooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 58, "And there is no city but that We will destroy it before the Day of Resurrection or punish it with a severe punishment. That has ever been in the Register inscribed", "وَإِن مِّن قَرۡيَةٍ إِلَّا نَحۡنُ مُهۡلِكُوهَا قَبۡلَ يَوۡمِ ٱلۡقِيَٰمَةِ أَوۡ مُعَذِّبُوهَا عَذَابٗا شَدِيدٗاۚ كَانَ ذَٰلِكَ فِي ٱلۡكِتَٰبِ مَسۡطُورٗا", "Wain min qaryatin illa nahnu muhlikooha qabla yawmi alqiyamati aw muAAaththibooha AAathaban shadeedan kana thalika fee alkitabi mastooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 59, "And nothing has prevented Us from sending signs except that the former peoples denied them. And We gave Thamud the she-camel as a visible sign, but they wronged her. And We send not the signs except as a warning", "وَمَا مَنَعَنَآ أَن نُّرۡسِلَ بِٱلۡأٓيَٰتِ إِلَّآ أَن كَذَّبَ بِهَا ٱلۡأَوَّلُونَۚ وَءَاتَيۡنَا ثَمُودَ ٱلنَّاقَةَ مُبۡصِرَةٗ فَظَلَمُواْ بِهَاۚ وَمَا نُرۡسِلُ بِٱلۡأٓيَٰتِ إِلَّا تَخۡوِيفٗا", "Wama manaAAana an nursila bialayati illa an kaththaba biha alawwaloona waatayna thamooda alnnaqata mubsiratan fathalamoo biha wama nursilu bialayati illa takhweefan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 60, "And [remember, O Muhammad], when We told you, ""Indeed, your Lord has encompassed the people."" And We did not make the sight which We showed you except as a trial for the people, as was the accursed tree [mentioned] in the Qur'an. And We threaten them, but it increases them not except in great transgression", "وَإِذۡ قُلۡنَا لَكَ إِنَّ رَبَّكَ أَحَاطَ بِٱلنَّاسِۚ وَمَا جَعَلۡنَا ٱلرُّءۡيَا ٱلَّتِيٓ أَرَيۡنَٰكَ إِلَّا فِتۡنَةٗ لِّلنَّاسِ وَٱلشَّجَرَةَ ٱلۡمَلۡعُونَةَ فِي ٱلۡقُرۡءَانِۚ وَنُخَوِّفُهُمۡ فَمَا يَزِيدُهُمۡ إِلَّا طُغۡيَٰنٗا كَبِيرٗا", "Waith qulna laka inna rabbaka ahata bialnnasi wama jaAAalna alrruya allatee araynaka illa fitnatan lilnnasi waalshshajarata almalAAoonata fee alqurani wanukhawwifuhum fama yazeeduhum illa tughyanan kabeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 61, "And [mention] when We said to the angles, ""Prostrate to Adam,"" and they prostrated, except for Iblees. He said, ""Should I prostrate to one You created from clay", "وَإِذۡ قُلۡنَا لِلۡمَلَـٰٓئِكَةِ ٱسۡجُدُواْ لِأٓدَمَ فَسَجَدُوٓاْ إِلَّآ إِبۡلِيسَ قَالَ ءَأَسۡجُدُ لِمَنۡ خَلَقۡتَ طِينٗا", "Waith qulna lilmalaikati osjudoo liadama fasajadoo illa ibleesa qala aasjudu liman khalaqta teenan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 62, "[Iblees] said, ""Do You see this one whom You have honored above me? If You delay me until the Day of Resurrection, I will surely destroy his descendants, except for a few", "قَالَ أَرَءَيۡتَكَ هَٰذَا ٱلَّذِي كَرَّمۡتَ عَلَيَّ لَئِنۡ أَخَّرۡتَنِ إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِ لَأَحۡتَنِكَنَّ ذُرِّيَّتَهُۥٓ إِلَّا قَلِيلٗا", "Qala araaytaka hatha allathee karramta AAalayya lain akhkhartani ila yawmi alqiyamati laahtanikanna thurriyyatahu illa qaleelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 63, "[Allah] said, ""Go, for whoever of them follows you, indeed Hell will be the recompense of you - an ample recompense", "قَالَ ٱذۡهَبۡ فَمَن تَبِعَكَ مِنۡهُمۡ فَإِنَّ جَهَنَّمَ جَزَآؤُكُمۡ جَزَآءٗ مَّوۡفُورٗا", "Qala ithhab faman tabiAAaka minhum fainna jahannama jazaokum jazaan mawfooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 64, "And incite [to senselessness] whoever you can among them with your voice and assault them with your horses and foot soldiers and become a partner in their wealth and their children and promise them."" But Satan does not promise them except delusion", "وَٱسۡتَفۡزِزۡ مَنِ ٱسۡتَطَعۡتَ مِنۡهُم بِصَوۡتِكَ وَأَجۡلِبۡ عَلَيۡهِم بِخَيۡلِكَ وَرَجِلِكَ وَشَارِكۡهُمۡ فِي ٱلۡأَمۡوَٰلِ وَٱلۡأَوۡلَٰدِ وَعِدۡهُمۡۚ وَمَا يَعِدُهُمُ ٱلشَّيۡطَٰنُ إِلَّا غُرُورًا", "Waistafziz mani istataAAta minhum bisawtika waajlib AAalayhim bikhaylika warajlika washarikhum fee alamwali waalawladi waAAidhum wama yaAAiduhumu alshshaytanu illa ghurooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 65, "Indeed, over My [believing] servants there is for you no authority. And sufficient is your Lord as Disposer of affairs", "إِنَّ عِبَادِي لَيۡسَ لَكَ عَلَيۡهِمۡ سُلۡطَٰنٞۚ وَكَفَىٰ بِرَبِّكَ وَكِيلٗا", "Inna AAibadee laysa laka AAalayhim sultanun wakafa birabbika wakeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 66, "It is your Lord who drives the ship for you through the sea that you may seek of His bounty. Indeed, He is ever, to you, Merciful", "رَّبُّكُمُ ٱلَّذِي يُزۡجِي لَكُمُ ٱلۡفُلۡكَ فِي ٱلۡبَحۡرِ لِتَبۡتَغُواْ مِن فَضۡلِهِۦٓۚ إِنَّهُۥ كَانَ بِكُمۡ رَحِيمٗا", "Rabbukumu allathee yuzjee lakumu alfulka fee albahri litabtaghoo min fadlihi innahu kana bikum raheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 67, "And when adversity touches you at sea, lost are [all] those you invoke except for Him. But when He delivers you to the land, you turn away [from Him]. And ever is man ungrateful", "وَإِذَا مَسَّكُمُ ٱلضُّرُّ فِي ٱلۡبَحۡرِ ضَلَّ مَن تَدۡعُونَ إِلَّآ إِيَّاهُۖ فَلَمَّا نَجَّىٰكُمۡ إِلَى ٱلۡبَرِّ أَعۡرَضۡتُمۡۚ وَكَانَ ٱلۡإِنسَٰنُ كَفُورًا", "Waitha massakumu alddurru fee albahri dalla man tadAAoona illa iyyahu falamma najjakum ila albarri aAAradtum wakana alinsanu kafooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 68, "Then do you feel secure that [instead] He will not cause a part of the land to swallow you or send against you a storm of stones? Then you would not find for yourselves an advocate", "أَفَأَمِنتُمۡ أَن يَخۡسِفَ بِكُمۡ جَانِبَ ٱلۡبَرِّ أَوۡ يُرۡسِلَ عَلَيۡكُمۡ حَاصِبٗا ثُمَّ لَا تَجِدُواْ لَكُمۡ وَكِيلًا", "Afaamintum an yakhsifa bikum janiba albarri aw yursila AAalaykum hasiban thumma la tajidoo lakum wakeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 69, "Or do you feel secure that He will not send you back into the sea another time and send upon you a hurricane of wind and drown you for what you denied? Then you would not find for yourselves against Us an avenger", "أَمۡ أَمِنتُمۡ أَن يُعِيدَكُمۡ فِيهِ تَارَةً أُخۡرَىٰ فَيُرۡسِلَ عَلَيۡكُمۡ قَاصِفٗا مِّنَ ٱلرِّيحِ فَيُغۡرِقَكُم بِمَا كَفَرۡتُمۡ ثُمَّ لَا تَجِدُواْ لَكُمۡ عَلَيۡنَا بِهِۦ تَبِيعٗا", "Am amintum an yuAAeedakum feehi taratan okhra fayursila AAalaykum qasifan mina alrreehi fayughriqakum bima kafartum thumma la tajidoo lakum AAalayna bihi tabeeAAan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 70, "And We have certainly honored the children of Adam and carried them on the land and sea and provided for them of the good things and preferred them over much of what We have created, with [definite] preference", "۞وَلَقَدۡ كَرَّمۡنَا بَنِيٓ ءَادَمَ وَحَمَلۡنَٰهُمۡ فِي ٱلۡبَرِّ وَٱلۡبَحۡرِ وَرَزَقۡنَٰهُم مِّنَ ٱلطَّيِّبَٰتِ وَفَضَّلۡنَٰهُمۡ عَلَىٰ كَثِيرٖ مِّمَّنۡ خَلَقۡنَا تَفۡضِيلٗا", "Walaqad karramna banee adama wahamalnahum fee albarri waalbahri warazaqnahum mina alttayyibati wafaddalnahum AAala katheerin mimman khalaqna tafdeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 71, "[Mention, O Muhammad], the Day We will call forth every people with their record [of deeds]. Then whoever is given his record in his right hand - those will read their records, and injustice will not be done to them, [even] as much as a thread [inside the date seed]", "يَوۡمَ نَدۡعُواْ كُلَّ أُنَاسِۭ بِإِمَٰمِهِمۡۖ فَمَنۡ أُوتِيَ كِتَٰبَهُۥ بِيَمِينِهِۦ فَأُوْلَـٰٓئِكَ يَقۡرَءُونَ كِتَٰبَهُمۡ وَلَا يُظۡلَمُونَ فَتِيلٗا", "Yawma nadAAoo kulla onasin biimamihim faman ootiya kitabahu biyameenihi faolaika yaqraoona kitabahum wala yuthlamoona fateelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 72, "And whoever is blind in this [life] will be blind in the Hereafter and more astray in way", "وَمَن كَانَ فِي هَٰذِهِۦٓ أَعۡمَىٰ فَهُوَ فِي ٱلۡأٓخِرَةِ أَعۡمَىٰ وَأَضَلُّ سَبِيلٗا", "Waman kana fee hathihi aAAma fahuwa fee alakhirati aAAma waadallu sabeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 73, "And indeed, they were about to tempt you away from that which We revealed to you in order to [make] you invent about Us something else; and then they would have taken you as a friend", "وَإِن كَادُواْ لَيَفۡتِنُونَكَ عَنِ ٱلَّذِيٓ أَوۡحَيۡنَآ إِلَيۡكَ لِتَفۡتَرِيَ عَلَيۡنَا غَيۡرَهُۥۖ وَإِذٗا لَّٱتَّخَذُوكَ خَلِيلٗا", "Wain kadoo layaftinoonaka AAani allathee awhayna ilayka litaftariya AAalayna ghayrahu waithan laittakhathooka khaleelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 74, "And if We had not strengthened you, you would have almost inclined to them a little", "وَلَوۡلَآ أَن ثَبَّتۡنَٰكَ لَقَدۡ كِدتَّ تَرۡكَنُ إِلَيۡهِمۡ شَيۡـٔٗا قَلِيلًا", "Walawla an thabbatnaka laqad kidta tarkanu ilayhim shayan qaleelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 75, "Then [if you had], We would have made you taste double [punishment in] life and double [after] death. Then you would not find for yourself against Us a helper", "إِذٗا لَّأَذَقۡنَٰكَ ضِعۡفَ ٱلۡحَيَوٰةِ وَضِعۡفَ ٱلۡمَمَاتِ ثُمَّ لَا تَجِدُ لَكَ عَلَيۡنَا نَصِيرٗا", "Ithan laathaqnaka diAAfa alhayati wadiAAfa almamati thumma la tajidu laka AAalayna naseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 76, "And indeed, they were about to drive you from the land to evict you therefrom. And then [when they do], they will not remain [there] after you, except for a little", "وَإِن كَادُواْ لَيَسۡتَفِزُّونَكَ مِنَ ٱلۡأَرۡضِ لِيُخۡرِجُوكَ مِنۡهَاۖ وَإِذٗا لَّا يَلۡبَثُونَ خِلَٰفَكَ إِلَّا قَلِيلٗا", "Wain kadoo layastafizzoonaka mina alardi liyukhrijooka minha waithan la yalbathoona khilafaka illa qaleelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 77, "[That is Our] established way for those We had sent before you of Our messengers; and you will not find in Our way any alteration", "سُنَّةَ مَن قَدۡ أَرۡسَلۡنَا قَبۡلَكَ مِن رُّسُلِنَاۖ وَلَا تَجِدُ لِسُنَّتِنَا تَحۡوِيلًا", "Sunnata man qad arsalna qablaka min rusulina wala tajidu lisunnatina tahweelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 78, "Establish prayer at the decline of the sun [from its meridian] until the darkness of the night and [also] the Qur'an of dawn. Indeed, the recitation of dawn is ever witnessed", "أَقِمِ ٱلصَّلَوٰةَ لِدُلُوكِ ٱلشَّمۡسِ إِلَىٰ غَسَقِ ٱلَّيۡلِ وَقُرۡءَانَ ٱلۡفَجۡرِۖ إِنَّ قُرۡءَانَ ٱلۡفَجۡرِ كَانَ مَشۡهُودٗا", "Aqimi alssalata lidulooki alshshamsi ila ghasaqi allayli waqurana alfajri inna qurana alfajri kana mashhoodan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 79, "And from [part of] the night, pray with it as additional [worship] for you; it is expected that your Lord will resurrect you to a praised station", "وَمِنَ ٱلَّيۡلِ فَتَهَجَّدۡ بِهِۦ نَافِلَةٗ لَّكَ عَسَىٰٓ أَن يَبۡعَثَكَ رَبُّكَ مَقَامٗا مَّحۡمُودٗا", "Wamina allayli fatahajjad bihi nafilatan laka AAasa an yabAAathaka rabbuka maqaman mahmoodan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 80, "And say, ""My Lord, cause me to enter a sound entrance and to exit a sound exit and grant me from Yourself a supporting authority", "وَقُل رَّبِّ أَدۡخِلۡنِي مُدۡخَلَ صِدۡقٖ وَأَخۡرِجۡنِي مُخۡرَجَ صِدۡقٖ وَٱجۡعَل لِّي مِن لَّدُنكَ سُلۡطَٰنٗا نَّصِيرٗا", "Waqul rabbi adkhilnee mudkhala sidqin waakhrijnee mukhraja sidqin waijAAal lee min ladunka sultanan naseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 81, "And say, ""Truth has come, and falsehood has departed. Indeed is falsehood, [by nature], ever bound to depart", "وَقُلۡ جَآءَ ٱلۡحَقُّ وَزَهَقَ ٱلۡبَٰطِلُۚ إِنَّ ٱلۡبَٰطِلَ كَانَ زَهُوقٗا", "Waqul jaa alhaqqu wazahaqa albatilu inna albatila kana zahooqan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 82, "And We send down of the Qur'an that which is healing and mercy for the believers, but it does not increase the wrongdoers except in loss", "وَنُنَزِّلُ مِنَ ٱلۡقُرۡءَانِ مَا هُوَ شِفَآءٞ وَرَحۡمَةٞ لِّلۡمُؤۡمِنِينَ وَلَا يَزِيدُ ٱلظَّـٰلِمِينَ إِلَّا خَسَارٗا", "Wanunazzilu mina alqurani ma huwa shifaon warahmatun lilmumineena wala yazeedu alththalimeena illa khasaran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 83, "And when We bestow favor upon the disbeliever, he turns away and distances himself; and when evil touches him, he is ever despairing", "وَإِذَآ أَنۡعَمۡنَا عَلَى ٱلۡإِنسَٰنِ أَعۡرَضَ وَنَـَٔا بِجَانِبِهِۦ وَإِذَا مَسَّهُ ٱلشَّرُّ كَانَ يَـُٔوسٗا", "Waitha anAAamna AAala alinsani aAArada wanaa bijanibihi waitha massahu alshsharru kana yaoosan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 84, "Say, ""Each works according to his manner, but your Lord is most knowing of who is best guided in way", "قُلۡ كُلّٞ يَعۡمَلُ عَلَىٰ شَاكِلَتِهِۦ فَرَبُّكُمۡ أَعۡلَمُ بِمَنۡ هُوَ أَهۡدَىٰ سَبِيلٗا", "Qul kullun yaAAmalu AAala shakilatihi farabbukum aAAlamu biman huwa ahda sabeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 85, "And they ask you, [O Muhammad], about the soul. Say, ""The soul is of the affair of my Lord. And mankind have not been given of knowledge except a little", "وَيَسۡـَٔلُونَكَ عَنِ ٱلرُّوحِۖ قُلِ ٱلرُّوحُ مِنۡ أَمۡرِ رَبِّي وَمَآ أُوتِيتُم مِّنَ ٱلۡعِلۡمِ إِلَّا قَلِيلٗا", "Wayasaloonaka AAani alrroohi quli alrroohu min amri rabbee wama ooteetum mina alAAilmi illa qaleelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 86, "And if We willed, We could surely do away with that which We revealed to you. Then you would not find for yourself concerning it an advocate against Us", "وَلَئِن شِئۡنَا لَنَذۡهَبَنَّ بِٱلَّذِيٓ أَوۡحَيۡنَآ إِلَيۡكَ ثُمَّ لَا تَجِدُ لَكَ بِهِۦ عَلَيۡنَا وَكِيلًا", "Walain shina lanathhabanna biallathee awhayna ilayka thumma la tajidu laka bihi AAalayna wakeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 87, "Except [We have left it with you] as a mercy from your Lord. Indeed, His favor upon you has ever been great", "إِلَّا رَحۡمَةٗ مِّن رَّبِّكَۚ إِنَّ فَضۡلَهُۥ كَانَ عَلَيۡكَ كَبِيرٗا", "Illa rahmatan min rabbika inna fadlahu kana AAalayka kabeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 88, "Say, ""If mankind and the jinn gathered in order to produce the like of this Qur'an, they could not produce the like of it, even if they were to each other assistants", "قُل لَّئِنِ ٱجۡتَمَعَتِ ٱلۡإِنسُ وَٱلۡجِنُّ عَلَىٰٓ أَن يَأۡتُواْ بِمِثۡلِ هَٰذَا ٱلۡقُرۡءَانِ لَا يَأۡتُونَ بِمِثۡلِهِۦ وَلَوۡ كَانَ بَعۡضُهُمۡ لِبَعۡضٖ ظَهِيرٗا", "Qul laini ijtamaAAati alinsu waaljinnu AAala an yatoo bimithli hatha alqurani la yatoona bimithlihi walaw kana baAAduhum libaAAdin thaheeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 89, "And We have certainly diversified for the people in this Qur'an from every [kind] of example, but most of the people refused [anything] except disbelief", "وَلَقَدۡ صَرَّفۡنَا لِلنَّاسِ فِي هَٰذَا ٱلۡقُرۡءَانِ مِن كُلِّ مَثَلٖ فَأَبَىٰٓ أَكۡثَرُ ٱلنَّاسِ إِلَّا كُفُورٗا", "Walaqad sarrafna lilnnasi fee hatha alqurani min kulli mathalin faaba aktharu alnnasi illa kufooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 90, "And they say, ""We will not believe you until you break open for us from the ground a spring", "وَقَالُواْ لَن نُّؤۡمِنَ لَكَ حَتَّىٰ تَفۡجُرَ لَنَا مِنَ ٱلۡأَرۡضِ يَنۢبُوعًا", "Waqaloo lan numina laka hatta tafjura lana mina alardi yanbooAAan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 91, "Or [until] you have a garden of palm tress and grapes and make rivers gush forth within them in force [and abundance]", "أَوۡ تَكُونَ لَكَ جَنَّةٞ مِّن نَّخِيلٖ وَعِنَبٖ فَتُفَجِّرَ ٱلۡأَنۡهَٰرَ خِلَٰلَهَا تَفۡجِيرًا", "Aw takoona laka jannatun min nakheelin waAAinabin fatufajjira alanhara khilalaha tafjeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 92, "Or you make the heaven fall upon us in fragments as you have claimed or you bring Allah and the angels before [us]", "أَوۡ تُسۡقِطَ ٱلسَّمَآءَ كَمَا زَعَمۡتَ عَلَيۡنَا كِسَفًا أَوۡ تَأۡتِيَ بِٱللَّهِ وَٱلۡمَلَـٰٓئِكَةِ قَبِيلًا", "Aw tusqita alssamaa kama zaAAamta AAalayna kisafan aw tatiya biAllahi waalmalaikati qabeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 93, "Or you have a house of gold or you ascend into the sky. And [even then], we will not believe in your ascension until you bring down to us a book we may read."" Say, ""Exalted is my Lord! Was I ever but a human messenger", "أَوۡ يَكُونَ لَكَ بَيۡتٞ مِّن زُخۡرُفٍ أَوۡ تَرۡقَىٰ فِي ٱلسَّمَآءِ وَلَن نُّؤۡمِنَ لِرُقِيِّكَ حَتَّىٰ تُنَزِّلَ عَلَيۡنَا كِتَٰبٗا نَّقۡرَؤُهُۥۗ قُلۡ سُبۡحَانَ رَبِّي هَلۡ كُنتُ إِلَّا بَشَرٗا رَّسُولٗا", "Aw yakoona laka baytun min zukhrufin aw tarqa fee alssamai walan numina liruqiyyika hatta tunazzila AAalayna kitaban naqraohu qul subhana rabbee hal kuntu illa basharan rasoolan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 94, "And what prevented the people from believing when guidance came to them except that they said, ""Has Allah sent a human messenger", "وَمَا مَنَعَ ٱلنَّاسَ أَن يُؤۡمِنُوٓاْ إِذۡ جَآءَهُمُ ٱلۡهُدَىٰٓ إِلَّآ أَن قَالُوٓاْ أَبَعَثَ ٱللَّهُ بَشَرٗا رَّسُولٗا", "Wama manaAAa alnnasa an yuminoo ith jaahumu alhuda illa an qaloo abaAAatha Allahu basharan rasoolan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 95, "Say, ""If there were upon the earth angels walking securely, We would have sent down to them from the heaven an angel [as a] messenger", "قُل لَّوۡ كَانَ فِي ٱلۡأَرۡضِ مَلَـٰٓئِكَةٞ يَمۡشُونَ مُطۡمَئِنِّينَ لَنَزَّلۡنَا عَلَيۡهِم مِّنَ ٱلسَّمَآءِ مَلَكٗا رَّسُولٗا", "Qul law kana fee alardi malaikatun yamshoona mutmainneena lanazzalna AAalayhim mina alssamai malakan rasoolan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 96, "Say, ""Sufficient is Allah as Witness between me and you. Indeed he is ever, concerning His servants, Acquainted and Seeing", "قُلۡ كَفَىٰ بِٱللَّهِ شَهِيدَۢا بَيۡنِي وَبَيۡنَكُمۡۚ إِنَّهُۥ كَانَ بِعِبَادِهِۦ خَبِيرَۢا بَصِيرٗا", "Qul kafa biAllahi shaheedan baynee wabaynakum innahu kana biAAibadihi khabeeran baseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 97, "And whoever Allah guides - he is the [rightly] guided; and whoever He sends astray - you will never find for them protectors besides Him, and We will gather them on the Day of Resurrection [fallen] on their faces - blind, dumb and deaf. Their refuge is Hell; every time it subsides We increase them in blazing fire", "وَمَن يَهۡدِ ٱللَّهُ فَهُوَ ٱلۡمُهۡتَدِۖ وَمَن يُضۡلِلۡ فَلَن تَجِدَ لَهُمۡ أَوۡلِيَآءَ مِن دُونِهِۦۖ وَنَحۡشُرُهُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ عَلَىٰ وُجُوهِهِمۡ عُمۡيٗا وَبُكۡمٗا وَصُمّٗاۖ مَّأۡوَىٰهُمۡ جَهَنَّمُۖ كُلَّمَا خَبَتۡ زِدۡنَٰهُمۡ سَعِيرٗا", "Waman yahdi Allahu fahuwa almuhtadi waman yudlil falan tajida lahum awliyaa min doonihi wanahshuruhum yawma alqiyamati AAala wujoohihim AAumyan wabukman wasumman mawahum jahannamu kullama khabat zidnahum saAAeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 98, "That is their recompense because they disbelieved in Our verses and said, ""When we are bones and crumbled particles, will we [truly] be resurrected [in] a new creation", "ذَٰلِكَ جَزَآؤُهُم بِأَنَّهُمۡ كَفَرُواْ بِـَٔايَٰتِنَا وَقَالُوٓاْ أَءِذَا كُنَّا عِظَٰمٗا وَرُفَٰتًا أَءِنَّا لَمَبۡعُوثُونَ خَلۡقٗا جَدِيدًا", "Thalika jazaohum biannahum kafaroo biayatina waqaloo aitha kunna AAithaman warufatan ainna lamabAAoothoona khalqan jadeedan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 99, "Do they not see that Allah, who created the heavens and earth, is [the one] Able to create the likes of them? And He has appointed for them a term, about which there is no doubt. But the wrongdoers refuse [anything] except disbelief", "۞أَوَلَمۡ يَرَوۡاْ أَنَّ ٱللَّهَ ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ قَادِرٌ عَلَىٰٓ أَن يَخۡلُقَ مِثۡلَهُمۡ وَجَعَلَ لَهُمۡ أَجَلٗا لَّا رَيۡبَ فِيهِ فَأَبَى ٱلظَّـٰلِمُونَ إِلَّا كُفُورٗا", "Awalam yaraw anna Allaha allathee khalaqa alssamawati waalarda qadirun AAala an yakhluqa mithlahum wajaAAala lahum ajalan la rayba feehi faaba alththalimoona illa kufooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 100, "Say [to them], ""If you possessed the depositories of the mercy of my Lord, then you would withhold out of fear of spending."" And ever has man been stingy", "قُل لَّوۡ أَنتُمۡ تَمۡلِكُونَ خَزَآئِنَ رَحۡمَةِ رَبِّيٓ إِذٗا لَّأَمۡسَكۡتُمۡ خَشۡيَةَ ٱلۡإِنفَاقِۚ وَكَانَ ٱلۡإِنسَٰنُ قَتُورٗا", "Qul law antum tamlikoona khazaina rahmati rabbee ithan laamsaktum khashyata alinfaqi wakana alinsanu qatooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 101, "And We had certainly given Moses nine evident signs, so ask the Children of Israel [about] when he came to them and Pharaoh said to him, ""Indeed I think, O Moses, that you are affected by magic", "وَلَقَدۡ ءَاتَيۡنَا مُوسَىٰ تِسۡعَ ءَايَٰتِۭ بَيِّنَٰتٖۖ فَسۡـَٔلۡ بَنِيٓ إِسۡرَـٰٓءِيلَ إِذۡ جَآءَهُمۡ فَقَالَ لَهُۥ فِرۡعَوۡنُ إِنِّي لَأَظُنُّكَ يَٰمُوسَىٰ مَسۡحُورٗا", "Walaqad atayna moosa tisAAa ayatin bayyinatin faisal banee israeela ith jaahum faqala lahu firAAawnu innee laathunnuka ya moosa mashooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 102, "[Moses] said, ""You have already known that none has sent down these [signs] except the Lord of the heavens and the earth as evidence, and indeed I think, O Pharaoh, that you are destroyed", "قَالَ لَقَدۡ عَلِمۡتَ مَآ أَنزَلَ هَـٰٓؤُلَآءِ إِلَّا رَبُّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ بَصَآئِرَ وَإِنِّي لَأَظُنُّكَ يَٰفِرۡعَوۡنُ مَثۡبُورٗا", "Qala laqad AAalimta ma anzala haolai illa rabbu alssamawati waalardi basaira wainnee laathunnuka ya firAAawnu mathbooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 103, "So he intended to drive them from the land, but We drowned him and those with him all together", "فَأَرَادَ أَن يَسۡتَفِزَّهُم مِّنَ ٱلۡأَرۡضِ فَأَغۡرَقۡنَٰهُ وَمَن مَّعَهُۥ جَمِيعٗا", "Faarada an yastafizzahum mina alardi faaghraqnahu waman maAAahu jameeAAan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 104, "And We said after Pharaoh to the Children of Israel, ""Dwell in the land, and when there comes the promise of the Hereafter, We will bring you forth in [one] gathering", "وَقُلۡنَا مِنۢ بَعۡدِهِۦ لِبَنِيٓ إِسۡرَـٰٓءِيلَ ٱسۡكُنُواْ ٱلۡأَرۡضَ فَإِذَا جَآءَ وَعۡدُ ٱلۡأٓخِرَةِ جِئۡنَا بِكُمۡ لَفِيفٗا", "Waqulna min baAAdihi libanee israeela oskunoo alarda faitha jaa waAAdu alakhirati jina bikum lafeefan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 105, "And with the truth We have sent the Qur'an down, and with the truth it has descended. And We have not sent you, [O Muhammad], except as a bringer of good tidings and a warner", "وَبِٱلۡحَقِّ أَنزَلۡنَٰهُ وَبِٱلۡحَقِّ نَزَلَۗ وَمَآ أَرۡسَلۡنَٰكَ إِلَّا مُبَشِّرٗا وَنَذِيرٗا", "Wabialhaqqi anzalnahu wabialhaqqi nazala wama arsalnaka illa mubashshiran wanatheeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 106, "And [it is] a Qur'an which We have separated [by intervals] that you might recite it to the people over a prolonged period. And We have sent it down progressively", "وَقُرۡءَانٗا فَرَقۡنَٰهُ لِتَقۡرَأَهُۥ عَلَى ٱلنَّاسِ عَلَىٰ مُكۡثٖ وَنَزَّلۡنَٰهُ تَنزِيلٗا", "Waquranan faraqnahu litaqraahu AAala alnnasi AAala mukthin wanazzalnahu tanzeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 107, "Say, ""Believe in it or do not believe. Indeed, those who were given knowledge before it - when it is recited to them, they fall upon their faces in prostration", "قُلۡ ءَامِنُواْ بِهِۦٓ أَوۡ لَا تُؤۡمِنُوٓاْۚ إِنَّ ٱلَّذِينَ أُوتُواْ ٱلۡعِلۡمَ مِن قَبۡلِهِۦٓ إِذَا يُتۡلَىٰ عَلَيۡهِمۡ يَخِرُّونَۤ لِلۡأَذۡقَانِۤ سُجَّدٗاۤ", "Qul aminoo bihi aw la tuminoo inna allatheena ootoo alAAilma min qablihi itha yutla AAalayhim yakhirroona lilathqani sujjadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 108, "And they say, ""Exalted is our Lord! Indeed, the promise of our Lord has been fulfilled", "وَيَقُولُونَ سُبۡحَٰنَ رَبِّنَآ إِن كَانَ وَعۡدُ رَبِّنَا لَمَفۡعُولٗا", "Wayaqooloona subhana rabbina in kana waAAdu rabbina lamafAAoolan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 109, "And they fall upon their faces weeping, and the Qur'an increases them in humble submission", "وَيَخِرُّونَ لِلۡأَذۡقَانِ يَبۡكُونَ وَيَزِيدُهُمۡ خُشُوعٗا۩", "Wayakhirroona lilathqani yabkoona wayazeeduhum khushooAAan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 110, "Say, ""Call upon Allah or call upon the Most Merciful. Whichever [name] you call - to Him belong the best names."" And do not recite [too] loudly in your prayer or [too] quietly but seek between that an [intermediate] way", "قُلِ ٱدۡعُواْ ٱللَّهَ أَوِ ٱدۡعُواْ ٱلرَّحۡمَٰنَۖ أَيّٗا مَّا تَدۡعُواْ فَلَهُ ٱلۡأَسۡمَآءُ ٱلۡحُسۡنَىٰۚ وَلَا تَجۡهَرۡ بِصَلَاتِكَ وَلَا تُخَافِتۡ بِهَا وَٱبۡتَغِ بَيۡنَ ذَٰلِكَ سَبِيلٗا", "Quli odAAoo Allaha awi odAAoo alrrahmana ayyan ma tadAAoo falahu alasmao alhusna wala tajhar bisalatika wala tukhafit biha waibtaghi bayna thalika sabeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (17, 111, "And say, ""Praise to Allah, who has not taken a son and has had no partner in [His] dominion and has no [need of a] protector out of weakness; and glorify Him with [great] glorification", "وَقُلِ ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِي لَمۡ يَتَّخِذۡ وَلَدٗا وَلَمۡ يَكُن لَّهُۥ شَرِيكٞ فِي ٱلۡمُلۡكِ وَلَمۡ يَكُن لَّهُۥ وَلِيّٞ مِّنَ ٱلذُّلِّۖ وَكَبِّرۡهُ تَكۡبِيرَۢا", "Waquli alhamdu lillahi allathee lam yattakhith waladan walam yakun lahu shareekun fee almulki walam yakun lahu waliyyun mina alththulli wakabbirhu takbeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 1, "[All] praise is [due] to Allah, who has sent down upon His Servant the Book and has not made therein any deviance", "ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِيٓ أَنزَلَ عَلَىٰ عَبۡدِهِ ٱلۡكِتَٰبَ وَلَمۡ يَجۡعَل لَّهُۥ عِوَجَاۜ", "Alhamdu lillahi allathee anzala AAala AAabdihi alkitaba walam yajAAal lahu AAiwajan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 2, "[He has made it] straight, to warn of severe punishment from Him and to give good tidings to the believers who do righteous deeds that they will have a good reward", "قَيِّمٗا لِّيُنذِرَ بَأۡسٗا شَدِيدٗا مِّن لَّدُنۡهُ وَيُبَشِّرَ ٱلۡمُؤۡمِنِينَ ٱلَّذِينَ يَعۡمَلُونَ ٱلصَّـٰلِحَٰتِ أَنَّ لَهُمۡ أَجۡرًا حَسَنٗا", "Qayyiman liyunthira basan shadeedan min ladunhu wayubashshira almumineena allatheena yaAAmaloona alssalihati anna lahum ajran hasanan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 3, "In which they will remain forever", "مَّـٰكِثِينَ فِيهِ أَبَدٗا", "Makitheena feehi abadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 4, "And to warn those who say, ""Allah has taken a son", "وَيُنذِرَ ٱلَّذِينَ قَالُواْ ٱتَّخَذَ ٱللَّهُ وَلَدٗا", "Wayunthira allatheena qaloo ittakhatha Allahu waladan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 5, "They have no knowledge of it, nor had their fathers. Grave is the word that comes out of their mouths; they speak not except a lie", "مَّا لَهُم بِهِۦ مِنۡ عِلۡمٖ وَلَا لِأٓبَآئِهِمۡۚ كَبُرَتۡ كَلِمَةٗ تَخۡرُجُ مِنۡ أَفۡوَٰهِهِمۡۚ إِن يَقُولُونَ إِلَّا كَذِبٗا", "Ma lahum bihi min AAilmin wala liabaihim kaburat kalimatan takhruju min afwahihim in yaqooloona illa kathiban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 6, "Then perhaps you would kill yourself through grief over them, [O Muhammad], if they do not believe in this message, [and] out of sorrow", "فَلَعَلَّكَ بَٰخِعٞ نَّفۡسَكَ عَلَىٰٓ ءَاثَٰرِهِمۡ إِن لَّمۡ يُؤۡمِنُواْ بِهَٰذَا ٱلۡحَدِيثِ أَسَفًا", "FalaAAallaka bakhiAAun nafsaka AAala atharihim in lam yuminoo bihatha alhadeethi asafan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 7, "Indeed, We have made that which is on the earth adornment for it that We may test them [as to] which of them is best in deed", "إِنَّا جَعَلۡنَا مَا عَلَى ٱلۡأَرۡضِ زِينَةٗ لَّهَا لِنَبۡلُوَهُمۡ أَيُّهُمۡ أَحۡسَنُ عَمَلٗا", "Inna jaAAalna ma AAala alardi zeenatan laha linabluwahum ayyuhum ahsanu AAamalan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 8, "And indeed, We will make that which is upon it [into] a barren ground", "وَإِنَّا لَجَٰعِلُونَ مَا عَلَيۡهَا صَعِيدٗا جُرُزًا", "Wainna lajaAAiloona ma AAalayha saAAeedan juruzan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 9, "Or have you thought that the companions of the cave and the inscription were, among Our signs, a wonder", "أَمۡ حَسِبۡتَ أَنَّ أَصۡحَٰبَ ٱلۡكَهۡفِ وَٱلرَّقِيمِ كَانُواْ مِنۡ ءَايَٰتِنَا عَجَبًا", "Am hasibta anna ashaba alkahfi waalrraqeemi kanoo min ayatina AAajaban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 10, "[Mention] when the youths retreated to the cave and said, ""Our Lord, grant us from Yourself mercy and prepare for us from our affair right guidance", "إِذۡ أَوَى ٱلۡفِتۡيَةُ إِلَى ٱلۡكَهۡفِ فَقَالُواْ رَبَّنَآ ءَاتِنَا مِن لَّدُنكَ رَحۡمَةٗ وَهَيِّئۡ لَنَا مِنۡ أَمۡرِنَا رَشَدٗا", "Ith awa alfityatu ila alkahfi faqaloo rabbana atina min ladunka rahmatan wahayyi lana min amrina rashadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 11, "So We cast [a cover of sleep] over their ears within the cave for a number of years", "فَضَرَبۡنَا عَلَىٰٓ ءَاذَانِهِمۡ فِي ٱلۡكَهۡفِ سِنِينَ عَدَدٗا", "Fadarabna AAala athanihim fee alkahfi sineena AAadadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 12, "Then We awakened them that We might show which of the two factions was most precise in calculating what [extent] they had remained in time", "ثُمَّ بَعَثۡنَٰهُمۡ لِنَعۡلَمَ أَيُّ ٱلۡحِزۡبَيۡنِ أَحۡصَىٰ لِمَا لَبِثُوٓاْ أَمَدٗا", "Thumma baAAathnahum linaAAlama ayyu alhizbayni ahsa lima labithoo amadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 13, "It is We who relate to you, [O Muhammad], their story in truth. Indeed, they were youths who believed in their Lord, and We increased them in guidance", "نَّحۡنُ نَقُصُّ عَلَيۡكَ نَبَأَهُم بِٱلۡحَقِّۚ إِنَّهُمۡ فِتۡيَةٌ ءَامَنُواْ بِرَبِّهِمۡ وَزِدۡنَٰهُمۡ هُدٗى", "Nahnu naqussu AAalayka nabaahum bialhaqqi innahum fityatun amanoo birabbihim wazidnahum hudan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 14, "And We made firm their hearts when they stood up and said, ""Our Lord is the Lord of the heavens and the earth. Never will we invoke besides Him any deity. We would have certainly spoken, then, an excessive transgression", "وَرَبَطۡنَا عَلَىٰ قُلُوبِهِمۡ إِذۡ قَامُواْ فَقَالُواْ رَبُّنَا رَبُّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ لَن نَّدۡعُوَاْ مِن دُونِهِۦٓ إِلَٰهٗاۖ لَّقَدۡ قُلۡنَآ إِذٗا شَطَطًا", "Warabatna AAala quloobihim ith qamoo faqaloo rabbuna rabbu alssamawati waalardi lan nadAAuwa min doonihi ilahan laqad qulna ithan shatatan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 15, "These, our people, have taken besides Him deities. Why do they not bring for [worship of] them a clear authority? And who is more unjust than one who invents about Allah a lie", "هَـٰٓؤُلَآءِ قَوۡمُنَا ٱتَّخَذُواْ مِن دُونِهِۦٓ ءَالِهَةٗۖ لَّوۡلَا يَأۡتُونَ عَلَيۡهِم بِسُلۡطَٰنِۭ بَيِّنٖۖ فَمَنۡ أَظۡلَمُ مِمَّنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبٗا", "Haolai qawmuna ittakhathoo min doonihi alihatan lawla yatoona AAalayhim bisultanin bayyinin faman athlamu mimmani iftara AAala Allahi kathiban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 16, "[The youths said to one another], ""And when you have withdrawn from them and that which they worship other than Allah, retreat to the cave. Your Lord will spread out for you of His mercy and will prepare for you from your affair facility", "وَإِذِ ٱعۡتَزَلۡتُمُوهُمۡ وَمَا يَعۡبُدُونَ إِلَّا ٱللَّهَ فَأۡوُۥٓاْ إِلَى ٱلۡكَهۡفِ يَنشُرۡ لَكُمۡ رَبُّكُم مِّن رَّحۡمَتِهِۦ وَيُهَيِّئۡ لَكُم مِّنۡ أَمۡرِكُم مِّرۡفَقٗا", "Waithi iAAtazaltumoohum wama yaAAbudoona illa Allaha fawoo ila alkahfi yanshur lakum rabbukum min rahmatihi wayuhayyi lakum min amrikum mirfaqan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 17, "And [had you been present], you would see the sun when it rose, inclining away from their cave on the right, and when it set, passing away from them on the left, while they were [laying] within an open space thereof. That was from the signs of Allah. He whom Allah guides is the [rightly] guided, but he whom He leaves astray - never will you find for him a protecting guide", "۞وَتَرَى ٱلشَّمۡسَ إِذَا طَلَعَت تَّزَٰوَرُ عَن كَهۡفِهِمۡ ذَاتَ ٱلۡيَمِينِ وَإِذَا غَرَبَت تَّقۡرِضُهُمۡ ذَاتَ ٱلشِّمَالِ وَهُمۡ فِي فَجۡوَةٖ مِّنۡهُۚ ذَٰلِكَ مِنۡ ءَايَٰتِ ٱللَّهِۗ مَن يَهۡدِ ٱللَّهُ فَهُوَ ٱلۡمُهۡتَدِۖ وَمَن يُضۡلِلۡ فَلَن تَجِدَ لَهُۥ وَلِيّٗا مُّرۡشِدٗا", "Watara alshshamsa itha talaAAat tazawaru AAan kahfihim thata alyameeni waitha gharabat taqriduhum thata alshshimali wahum fee fajwatin minhu thalika min ayati Allahi man yahdi Allahu fahuwa almuhtadi waman yudlil falan tajida lahu waliyyan murshidan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 18, "And you would think them awake, while they were asleep. And We turned them to the right and to the left, while their dog stretched his forelegs at the entrance. If you had looked at them, you would have turned from them in flight and been filled by them with terror", "وَتَحۡسَبُهُمۡ أَيۡقَاظٗا وَهُمۡ رُقُودٞۚ وَنُقَلِّبُهُمۡ ذَاتَ ٱلۡيَمِينِ وَذَاتَ ٱلشِّمَالِۖ وَكَلۡبُهُم بَٰسِطٞ ذِرَاعَيۡهِ بِٱلۡوَصِيدِۚ لَوِ ٱطَّلَعۡتَ عَلَيۡهِمۡ لَوَلَّيۡتَ مِنۡهُمۡ فِرَارٗا وَلَمُلِئۡتَ مِنۡهُمۡ رُعۡبٗا", "Watahsabuhum ayqathan wahum ruqoodun wanuqallibuhum thata alyameeni wathata alshshimali wakalbuhum basitun thiraAAayhi bialwaseedi lawi ittalaAAta AAalayhim lawallayta minhum firaran walamulita minhum ruAAban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 19, "And similarly, We awakened them that they might question one another. Said a speaker from among them, ""How long have you remained [here]?"" They said, ""We have remained a day or part of a day."" They said, ""Your Lord is most knowing of how long you remained. So send one of you with this silver coin of yours to the city and let him look to which is the best of food and bring you provision from it and let him be cautious. And let no one be aware of you", "وَكَذَٰلِكَ بَعَثۡنَٰهُمۡ لِيَتَسَآءَلُواْ بَيۡنَهُمۡۚ قَالَ قَآئِلٞ مِّنۡهُمۡ كَمۡ لَبِثۡتُمۡۖ قَالُواْ لَبِثۡنَا يَوۡمًا أَوۡ بَعۡضَ يَوۡمٖۚ قَالُواْ رَبُّكُمۡ أَعۡلَمُ بِمَا لَبِثۡتُمۡ فَٱبۡعَثُوٓاْ أَحَدَكُم بِوَرِقِكُمۡ هَٰذِهِۦٓ إِلَى ٱلۡمَدِينَةِ فَلۡيَنظُرۡ أَيُّهَآ أَزۡكَىٰ طَعَامٗا فَلۡيَأۡتِكُم بِرِزۡقٖ مِّنۡهُ وَلۡيَتَلَطَّفۡ وَلَا يُشۡعِرَنَّ بِكُمۡ أَحَدًا", "Wakathalika baAAathnahum liyatasaaloo baynahum qala qailun minhum kam labithtum qaloo labithna yawman aw baAAda yawmin qaloo rabbukum aAAlamu bima labithtum faibAAathoo ahadakum biwariqikum hathihi ila almadeenati falyanthur ayyuha azka taAAaman falyatikum birizqin minhu walyatalattaf wala yushAAiranna bikum ahadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 20, "Indeed, if they come to know of you, they will stone you or return you to their religion. And never would you succeed, then - ever", "إِنَّهُمۡ إِن يَظۡهَرُواْ عَلَيۡكُمۡ يَرۡجُمُوكُمۡ أَوۡ يُعِيدُوكُمۡ فِي مِلَّتِهِمۡ وَلَن تُفۡلِحُوٓاْ إِذًا أَبَدٗا", "Innahum in yathharoo AAalaykum yarjumookum aw yuAAeedookum fee millatihim walan tuflihoo ithan abadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 21, "And similarly, We caused them to be found that they [who found them] would know that the promise of Allah is truth and that of the Hour there is no doubt. [That was] when they disputed among themselves about their affair and [then] said, ""Construct over them a structure. Their Lord is most knowing about them."" Said those who prevailed in the matter, ""We will surely take [for ourselves] over them a masjid", "وَكَذَٰلِكَ أَعۡثَرۡنَا عَلَيۡهِمۡ لِيَعۡلَمُوٓاْ أَنَّ وَعۡدَ ٱللَّهِ حَقّٞ وَأَنَّ ٱلسَّاعَةَ لَا رَيۡبَ فِيهَآ إِذۡ يَتَنَٰزَعُونَ بَيۡنَهُمۡ أَمۡرَهُمۡۖ فَقَالُواْ ٱبۡنُواْ عَلَيۡهِم بُنۡيَٰنٗاۖ رَّبُّهُمۡ أَعۡلَمُ بِهِمۡۚ قَالَ ٱلَّذِينَ غَلَبُواْ عَلَىٰٓ أَمۡرِهِمۡ لَنَتَّخِذَنَّ عَلَيۡهِم مَّسۡجِدٗا", "Wakathalika aAAtharna AAalayhim liyaAAlamoo anna waAAda Allahi haqqun waanna alsaAAata la rayba feeha ith yatanazaAAoona baynahum amrahum faqaloo ibnoo AAalayhim bunyanan rabbuhum aAAlamu bihim qala allatheena ghalaboo AAala amrihim lanattakhithanna AAalayhim masjidan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 22, "They will say there were three, the fourth of them being their dog; and they will say there were five, the sixth of them being their dog - guessing at the unseen; and they will say there were seven, and the eighth of them was their dog. Say, [O Muhammad], ""My Lord is most knowing of their number. None knows them except a few. So do not argue about them except with an obvious argument and do not inquire about them among [the speculators] from anyone", "سَيَقُولُونَ ثَلَٰثَةٞ رَّابِعُهُمۡ كَلۡبُهُمۡ وَيَقُولُونَ خَمۡسَةٞ سَادِسُهُمۡ كَلۡبُهُمۡ رَجۡمَۢا بِٱلۡغَيۡبِۖ وَيَقُولُونَ سَبۡعَةٞ وَثَامِنُهُمۡ كَلۡبُهُمۡۚ قُل رَّبِّيٓ أَعۡلَمُ بِعِدَّتِهِم مَّا يَعۡلَمُهُمۡ إِلَّا قَلِيلٞۗ فَلَا تُمَارِ فِيهِمۡ إِلَّا مِرَآءٗ ظَٰهِرٗا وَلَا تَسۡتَفۡتِ فِيهِم مِّنۡهُمۡ أَحَدٗا", "Sayaqooloona thalathatun rabiAAuhum kalbuhum wayaqooloona khamsatun sadisuhum kalbuhum rajman bialghaybi wayaqooloona sabAAatun wathaminuhum kalbuhum qul rabbee aAAlamu biAAiddatihim ma yaAAlamuhum illa qaleelun fala tumari feehim illa miraan thahiran wala tastafti feehim minhum ahadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 23, "And never say of anything, ""Indeed, I will do that tomorrow", "وَلَا تَقُولَنَّ لِشَاْيۡءٍ إِنِّي فَاعِلٞ ذَٰلِكَ غَدًا", "Wala taqoolanna lishayin innee faAAilun thalika ghadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 24, "Except [when adding], ""If Allah wills."" And remember your Lord when you forget [it] and say, ""Perhaps my Lord will guide me to what is nearer than this to right conduct", "إِلَّآ أَن يَشَآءَ ٱللَّهُۚ وَٱذۡكُر رَّبَّكَ إِذَا نَسِيتَ وَقُلۡ عَسَىٰٓ أَن يَهۡدِيَنِ رَبِّي لِأَقۡرَبَ مِنۡ هَٰذَا رَشَدٗا", "Illa an yashaa Allahu waothkur rabbaka itha naseeta waqul AAasa an yahdiyani rabbee liaqraba min hatha rashadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 25, "And they remained in their cave for three hundred years and exceeded by nine", "وَلَبِثُواْ فِي كَهۡفِهِمۡ ثَلَٰثَ مِاْئَةٖ سِنِينَ وَٱزۡدَادُواْ تِسۡعٗا", "Walabithoo fee kahfihim thalatha miatin sineena waizdadoo tisAAan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 26, "Say, ""Allah is most knowing of how long they remained. He has [knowledge of] the unseen [aspects] of the heavens and the earth. How Seeing is He and how Hearing! They have not besides Him any protector, and He shares not His legislation with anyone", "قُلِ ٱللَّهُ أَعۡلَمُ بِمَا لَبِثُواْۖ لَهُۥ غَيۡبُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ أَبۡصِرۡ بِهِۦ وَأَسۡمِعۡۚ مَا لَهُم مِّن دُونِهِۦ مِن وَلِيّٖ وَلَا يُشۡرِكُ فِي حُكۡمِهِۦٓ أَحَدٗا", "Quli Allahu aAAlamu bima labithoo lahu ghaybu alssamawati waalardi absir bihi waasmiAA ma lahum min doonihi min waliyyin wala yushriku fee hukmihi ahadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 27, "And recite, [O Muhammad], what has been revealed to you of the Book of your Lord. There is no changer of His words, and never will you find in other than Him a refuge", "وَٱتۡلُ مَآ أُوحِيَ إِلَيۡكَ مِن كِتَابِ رَبِّكَۖ لَا مُبَدِّلَ لِكَلِمَٰتِهِۦ وَلَن تَجِدَ مِن دُونِهِۦ مُلۡتَحَدٗا", "Waotlu ma oohiya ilayka min kitabi rabbika la mubaddila likalimatihi walan tajida min doonihi multahadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 28, "And keep yourself patient [by being] with those who call upon their Lord in the morning and the evening, seeking His countenance. And let not your eyes pass beyond them, desiring adornments of the worldly life, and do not obey one whose heart We have made heedless of Our remembrance and who follows his desire and whose affair is ever [in] neglect", "وَٱصۡبِرۡ نَفۡسَكَ مَعَ ٱلَّذِينَ يَدۡعُونَ رَبَّهُم بِٱلۡغَدَوٰةِ وَٱلۡعَشِيِّ يُرِيدُونَ وَجۡهَهُۥۖ وَلَا تَعۡدُ عَيۡنَاكَ عَنۡهُمۡ تُرِيدُ زِينَةَ ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۖ وَلَا تُطِعۡ مَنۡ أَغۡفَلۡنَا قَلۡبَهُۥ عَن ذِكۡرِنَا وَٱتَّبَعَ هَوَىٰهُ وَكَانَ أَمۡرُهُۥ فُرُطٗا", "Waisbir nafsaka maAAa allatheena yadAAoona rabbahum bialghadati waalAAashiyyi yureedoona wajhahu wala taAAdu AAaynaka AAanhum tureedu zeenata alhayati alddunya wala tutiAA man aghfalna qalbahu AAan thikrina waittabaAAa hawahu wakana amruhu furutan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 29, "And say, ""The truth is from your Lord, so whoever wills - let him believe; and whoever wills - let him disbelieve."" Indeed, We have prepared for the wrongdoers a fire whose walls will surround them. And if they call for relief, they will be relieved with water like murky oil, which scalds [their] faces. Wretched is the drink, and evil is the resting place", "وَقُلِ ٱلۡحَقُّ مِن رَّبِّكُمۡۖ فَمَن شَآءَ فَلۡيُؤۡمِن وَمَن شَآءَ فَلۡيَكۡفُرۡۚ إِنَّآ أَعۡتَدۡنَا لِلظَّـٰلِمِينَ نَارًا أَحَاطَ بِهِمۡ سُرَادِقُهَاۚ وَإِن يَسۡتَغِيثُواْ يُغَاثُواْ بِمَآءٖ كَٱلۡمُهۡلِ يَشۡوِي ٱلۡوُجُوهَۚ بِئۡسَ ٱلشَّرَابُ وَسَآءَتۡ مُرۡتَفَقًا", "Waquli alhaqqu min rabbikum faman shaa falyumin waman shaa falyakfur inna aAAtadna lilththalimeena naran ahata bihim suradiquha wain yastagheethoo yughathoo bimain kaalmuhli yashwee alwujooha bisa alshsharabu wasaat murtafaqan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 30, "Indeed, those who have believed and done righteous deeds - indeed, We will not allow to be lost the reward of any who did well in deeds", "إِنَّ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ إِنَّا لَا نُضِيعُ أَجۡرَ مَنۡ أَحۡسَنَ عَمَلًا", "Inna allatheena amanoo waAAamiloo alssalihati inna la nudeeAAu ajra man ahsana AAamalan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 31, "Those will have gardens of perpetual residence; beneath them rivers will flow. They will be adorned therein with bracelets of gold and will wear green garments of fine silk and brocade, reclining therein on adorned couches. Excellent is the reward, and good is the resting place", "أُوْلَـٰٓئِكَ لَهُمۡ جَنَّـٰتُ عَدۡنٖ تَجۡرِي مِن تَحۡتِهِمُ ٱلۡأَنۡهَٰرُ يُحَلَّوۡنَ فِيهَا مِنۡ أَسَاوِرَ مِن ذَهَبٖ وَيَلۡبَسُونَ ثِيَابًا خُضۡرٗا مِّن سُندُسٖ وَإِسۡتَبۡرَقٖ مُّتَّكِـِٔينَ فِيهَا عَلَى ٱلۡأَرَآئِكِۚ نِعۡمَ ٱلثَّوَابُ وَحَسُنَتۡ مُرۡتَفَقٗا", "Olaika lahum jannatu AAadnin tajree min tahtihimu alanharu yuhallawna feeha min asawira min thahabin wayalbasoona thiyaban khudran min sundusin waistabraqin muttakieena feeha AAala alaraiki niAAma alththawabu wahasunat murtafaqan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 32, "And present to them an example of two men: We granted to one of them two gardens of grapevines, and We bordered them with palm trees and placed between them [fields of] crops", "۞وَٱضۡرِبۡ لَهُم مَّثَلٗا رَّجُلَيۡنِ جَعَلۡنَا لِأَحَدِهِمَا جَنَّتَيۡنِ مِنۡ أَعۡنَٰبٖ وَحَفَفۡنَٰهُمَا بِنَخۡلٖ وَجَعَلۡنَا بَيۡنَهُمَا زَرۡعٗا", "Waidrib lahum mathalan rajulayni jaAAalna liahadihima jannatayni min aAAnabin wahafafnahuma binakhlin wajaAAalna baynahuma zarAAan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 33, "Each of the two gardens produced its fruit and did not fall short thereof in anything. And We caused to gush forth within them a river", "كِلۡتَا ٱلۡجَنَّتَيۡنِ ءَاتَتۡ أُكُلَهَا وَلَمۡ تَظۡلِم مِّنۡهُ شَيۡـٔٗاۚ وَفَجَّرۡنَا خِلَٰلَهُمَا نَهَرٗا", "Kilta aljannatayni atat okulaha walam tathlim minhu shayan wafajjarna khilalahuma naharan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 34, "And he had fruit, so he said to his companion while he was conversing with him, ""I am greater than you in wealth and mightier in [numbers of] men", "وَكَانَ لَهُۥ ثَمَرٞ فَقَالَ لِصَٰحِبِهِۦ وَهُوَ يُحَاوِرُهُۥٓ أَنَا۠ أَكۡثَرُ مِنكَ مَالٗا وَأَعَزُّ نَفَرٗا", "Wakana lahu thamarun faqala lisahibihi wahuwa yuhawiruhu ana aktharu minka malan waaAAazzu nafaran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 35, "And he entered his garden while he was unjust to himself. He said, ""I do not think that this will perish - ever", "وَدَخَلَ جَنَّتَهُۥ وَهُوَ ظَالِمٞ لِّنَفۡسِهِۦ قَالَ مَآ أَظُنُّ أَن تَبِيدَ هَٰذِهِۦٓ أَبَدٗا", "Wadakhala jannatahu wahuwa thalimun linafsihi qala ma athunnu an tabeeda hathihi abadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 36, "And I do not think the Hour will occur. And even if I should be brought back to my Lord, I will surely find better than this as a return", "وَمَآ أَظُنُّ ٱلسَّاعَةَ قَآئِمَةٗ وَلَئِن رُّدِدتُّ إِلَىٰ رَبِّي لَأَجِدَنَّ خَيۡرٗا مِّنۡهَا مُنقَلَبٗا", "Wama athunnu alssaAAata qaimatan walain rudidtu ila rabbee laajidanna khayran minha munqalaban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 37, "His companion said to him while he was conversing with him, ""Have you disbelieved in He who created you from dust and then from a sperm-drop and then proportioned you [as] a man", "قَالَ لَهُۥ صَاحِبُهُۥ وَهُوَ يُحَاوِرُهُۥٓ أَكَفَرۡتَ بِٱلَّذِي خَلَقَكَ مِن تُرَابٖ ثُمَّ مِن نُّطۡفَةٖ ثُمَّ سَوَّىٰكَ رَجُلٗا", "Qala lahu sahibuhu wahuwa yuhawiruhu akafarta biallathee khalaqaka min turabin thumma min nutfatin thumma sawwaka rajulan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 38, "But as for me, He is Allah, my Lord, and I do not associate with my Lord anyone", "لَّـٰكِنَّا۠ هُوَ ٱللَّهُ رَبِّي وَلَآ أُشۡرِكُ بِرَبِّيٓ أَحَدٗا", "Lakinna huwa Allahu rabbee wala oshriku birabbee ahadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 39, "And why did you, when you entered your garden, not say, 'What Allah willed [has occurred]; there is no power except in Allah '? Although you see me less than you in wealth and children", "وَلَوۡلَآ إِذۡ دَخَلۡتَ جَنَّتَكَ قُلۡتَ مَا شَآءَ ٱللَّهُ لَا قُوَّةَ إِلَّا بِٱللَّهِۚ إِن تَرَنِ أَنَا۠ أَقَلَّ مِنكَ مَالٗا وَوَلَدٗا", "Walawla ith dakhalta jannataka qulta ma shaa Allahu la quwwata illa biAllahi in tarani ana aqalla minka malan wawaladan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 40, "It may be that my Lord will give me [something] better than your garden and will send upon it a calamity from the sky, and it will become a smooth, dusty ground", "فَعَسَىٰ رَبِّيٓ أَن يُؤۡتِيَنِ خَيۡرٗا مِّن جَنَّتِكَ وَيُرۡسِلَ عَلَيۡهَا حُسۡبَانٗا مِّنَ ٱلسَّمَآءِ فَتُصۡبِحَ صَعِيدٗا زَلَقًا", "FaAAasa rabbee an yutiyani khayran min jannatika wayursila AAalayha husbanan mina alssamai fatusbiha saAAeedan zalaqan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 41, "Or its water will become sunken [into the earth], so you would never be able to seek it", "أَوۡ يُصۡبِحَ مَآؤُهَا غَوۡرٗا فَلَن تَسۡتَطِيعَ لَهُۥ طَلَبٗا", "Aw yusbiha maoha ghawran falan tastateeAAa lahu talaban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 42, "And his fruits were encompassed [by ruin], so he began to turn his hands about [in dismay] over what he had spent on it, while it had collapsed upon its trellises, and said, ""Oh, I wish I had not associated with my Lord anyone", "وَأُحِيطَ بِثَمَرِهِۦ فَأَصۡبَحَ يُقَلِّبُ كَفَّيۡهِ عَلَىٰ مَآ أَنفَقَ فِيهَا وَهِيَ خَاوِيَةٌ عَلَىٰ عُرُوشِهَا وَيَقُولُ يَٰلَيۡتَنِي لَمۡ أُشۡرِكۡ بِرَبِّيٓ أَحَدٗا", "Waoheeta bithamarihi faasbaha yuqallibu kaffayhi AAala ma anfaqa feeha wahiya khawiyatun AAala AAurooshiha wayaqoolu ya laytanee lam oshrik birabbee ahadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 43, "And there was for him no company to aid him other than Allah, nor could he defend himself", "وَلَمۡ تَكُن لَّهُۥ فِئَةٞ يَنصُرُونَهُۥ مِن دُونِ ٱللَّهِ وَمَا كَانَ مُنتَصِرًا", "Walam takun lahu fiatun yansuroonahu min dooni Allahi wama kana muntasiran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 44, "There the authority is [completely] for Allah, the Truth. He is best in reward and best in outcome", "هُنَالِكَ ٱلۡوَلَٰيَةُ لِلَّهِ ٱلۡحَقِّۚ هُوَ خَيۡرٞ ثَوَابٗا وَخَيۡرٌ عُقۡبٗا", "Hunalika alwalayatu lillahi alhaqqi huwa khayrun thawaban wakhayrun AAuqban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 45, "And present to them the example of the life of this world, [its being] like rain which We send down from the sky, and the vegetation of the earth mingles with it and [then] it becomes dry remnants, scattered by the winds. And Allah is ever, over all things, Perfect in Ability", "وَٱضۡرِبۡ لَهُم مَّثَلَ ٱلۡحَيَوٰةِ ٱلدُّنۡيَا كَمَآءٍ أَنزَلۡنَٰهُ مِنَ ٱلسَّمَآءِ فَٱخۡتَلَطَ بِهِۦ نَبَاتُ ٱلۡأَرۡضِ فَأَصۡبَحَ هَشِيمٗا تَذۡرُوهُ ٱلرِّيَٰحُۗ وَكَانَ ٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ مُّقۡتَدِرًا", "Waidrib lahum mathala alhayati alddunya kamain anzalnahu mina alssamai faikhtalata bihi nabatu alardi faasbaha hasheeman tathroohu alrriyahu wakana Allahu AAala kulli shayin muqtadiran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 46, "Wealth and children are [but] adornment of the worldly life. But the enduring good deeds are better to your Lord for reward and better for [one's] hope", "ٱلۡمَالُ وَٱلۡبَنُونَ زِينَةُ ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۖ وَٱلۡبَٰقِيَٰتُ ٱلصَّـٰلِحَٰتُ خَيۡرٌ عِندَ رَبِّكَ ثَوَابٗا وَخَيۡرٌ أَمَلٗا", "Almalu waalbanoona zeenatu alhayati alddunya waalbaqiyatu alssalihatu khayrun AAinda rabbika thawaban wakhayrun amalan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 47, "And [warn of] the Day when We will remove the mountains and you will see the earth prominent, and We will gather them and not leave behind from them anyone", "وَيَوۡمَ نُسَيِّرُ ٱلۡجِبَالَ وَتَرَى ٱلۡأَرۡضَ بَارِزَةٗ وَحَشَرۡنَٰهُمۡ فَلَمۡ نُغَادِرۡ مِنۡهُمۡ أَحَدٗا", "Wayawma nusayyiru aljibala watara alarda barizatan wahasharnahum falam nughadir minhum ahadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 48, "And they will be presented before your Lord in rows, [and He will say], ""You have certainly come to Us just as We created you the first time. But you claimed that We would never make for you an appointment", "وَعُرِضُواْ عَلَىٰ رَبِّكَ صَفّٗا لَّقَدۡ جِئۡتُمُونَا كَمَا خَلَقۡنَٰكُمۡ أَوَّلَ مَرَّةِۭۚ بَلۡ زَعَمۡتُمۡ أَلَّن نَّجۡعَلَ لَكُم مَّوۡعِدٗا", "WaAAuridoo AAala rabbika saffan laqad jitumoona kama khalaqnakum awwala marratin bal zaAAamtum allan najAAala lakum mawAAidan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 49, "And the record [of deeds] will be placed [open], and you will see the criminals fearful of that within it, and they will say, ""Oh, woe to us! What is this book that leaves nothing small or great except that it has enumerated it?"" And they will find what they did present [before them]. And your Lord does injustice to no one", "وَوُضِعَ ٱلۡكِتَٰبُ فَتَرَى ٱلۡمُجۡرِمِينَ مُشۡفِقِينَ مِمَّا فِيهِ وَيَقُولُونَ يَٰوَيۡلَتَنَا مَالِ هَٰذَا ٱلۡكِتَٰبِ لَا يُغَادِرُ صَغِيرَةٗ وَلَا كَبِيرَةً إِلَّآ أَحۡصَىٰهَاۚ وَوَجَدُواْ مَا عَمِلُواْ حَاضِرٗاۗ وَلَا يَظۡلِمُ رَبُّكَ أَحَدٗا", "WawudiAAa alkitabu fatara almujrimeena mushfiqeena mimma feehi wayaqooloona ya waylatana ma lihatha alkitabi la yughadiru sagheeratan wala kabeeratan illa ahsaha wawajadoo ma AAamiloo hadiran wala yathlimu rabbuka ahadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 50, "And [mention] when We said to the angels, ""Prostrate to Adam,"" and they prostrated, except for Iblees. He was of the jinn and departed from the command of his Lord. Then will you take him and his descendants as allies other than Me while they are enemies to you? Wretched it is for the wrongdoers as an exchange", "وَإِذۡ قُلۡنَا لِلۡمَلَـٰٓئِكَةِ ٱسۡجُدُواْ لِأٓدَمَ فَسَجَدُوٓاْ إِلَّآ إِبۡلِيسَ كَانَ مِنَ ٱلۡجِنِّ فَفَسَقَ عَنۡ أَمۡرِ رَبِّهِۦٓۗ أَفَتَتَّخِذُونَهُۥ وَذُرِّيَّتَهُۥٓ أَوۡلِيَآءَ مِن دُونِي وَهُمۡ لَكُمۡ عَدُوُّۢۚ بِئۡسَ لِلظَّـٰلِمِينَ بَدَلٗا", "Waith qulna lilmalaikati osjudoo liadama fasajadoo illa ibleesa kana mina aljinni fafasaqa AAan amri rabbihi afatattakhithoonahu wathurriyyatahu awliyaa min doonee wahum lakum AAaduwwun bisa lilththalimeena badalan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 51, "I did not make them witness to the creation of the heavens and the earth or to the creation of themselves, and I would not have taken the misguiders as assistants", "۞مَّآ أَشۡهَدتُّهُمۡ خَلۡقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَلَا خَلۡقَ أَنفُسِهِمۡ وَمَا كُنتُ مُتَّخِذَ ٱلۡمُضِلِّينَ عَضُدٗا", "Ma ashhadtuhum khalqa alssamawati waalardi wala khalqa anfusihim wama kuntu muttakhitha almudilleena AAadudan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 52, "And [warn of] the Day when He will say, ""Call 'My partners' whom you claimed,"" and they will invoke them, but they will not respond to them. And We will put between them [a valley of] destruction", "وَيَوۡمَ يَقُولُ نَادُواْ شُرَكَآءِيَ ٱلَّذِينَ زَعَمۡتُمۡ فَدَعَوۡهُمۡ فَلَمۡ يَسۡتَجِيبُواْ لَهُمۡ وَجَعَلۡنَا بَيۡنَهُم مَّوۡبِقٗا", "Wayawma yaqoolu nadoo shurakaiya allatheena zaAAamtum fadaAAawhum falam yastajeeboo lahum wajaAAalna baynahum mawbiqan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 53, "And the criminals will see the Fire and will be certain that they are to fall therein. And they will not find from it a way elsewhere", "وَرَءَا ٱلۡمُجۡرِمُونَ ٱلنَّارَ فَظَنُّوٓاْ أَنَّهُم مُّوَاقِعُوهَا وَلَمۡ يَجِدُواْ عَنۡهَا مَصۡرِفٗا", "Waraa almujrimoona alnnara fathannoo annahum muwaqiAAooha walam yajidoo AAanha masrifan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 54, "And We have certainly diversified in this Qur'an for the people from every [kind of] example; but man has ever been, most of anything, [prone to] dispute", "وَلَقَدۡ صَرَّفۡنَا فِي هَٰذَا ٱلۡقُرۡءَانِ لِلنَّاسِ مِن كُلِّ مَثَلٖۚ وَكَانَ ٱلۡإِنسَٰنُ أَكۡثَرَ شَيۡءٖ جَدَلٗا", "Walaqad sarrafna fee hatha alqurani lilnnasi min kulli mathalin wakana alinsanu akthara shayin jadalan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 55, "And nothing has prevented the people from believing when guidance came to them and from asking forgiveness of their Lord except that there [must] befall them the [accustomed] precedent of the former peoples or that the punishment should come [directly] before them", "وَمَا مَنَعَ ٱلنَّاسَ أَن يُؤۡمِنُوٓاْ إِذۡ جَآءَهُمُ ٱلۡهُدَىٰ وَيَسۡتَغۡفِرُواْ رَبَّهُمۡ إِلَّآ أَن تَأۡتِيَهُمۡ سُنَّةُ ٱلۡأَوَّلِينَ أَوۡ يَأۡتِيَهُمُ ٱلۡعَذَابُ قُبُلٗا", "Wama manaAAa alnnasa an yuminoo ith jaahumu alhuda wayastaghfiroo rabbahum illa an tatiyahum sunnatu alawwaleena aw yatiyahumu alAAathabu qubulan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 56, "And We send not the messengers except as bringers of good tidings and warners. And those who disbelieve dispute by [using] falsehood to [attempt to] invalidate thereby the truth and have taken My verses, and that of which they are warned, in ridicule", "وَمَا نُرۡسِلُ ٱلۡمُرۡسَلِينَ إِلَّا مُبَشِّرِينَ وَمُنذِرِينَۚ وَيُجَٰدِلُ ٱلَّذِينَ كَفَرُواْ بِٱلۡبَٰطِلِ لِيُدۡحِضُواْ بِهِ ٱلۡحَقَّۖ وَٱتَّخَذُوٓاْ ءَايَٰتِي وَمَآ أُنذِرُواْ هُزُوٗا", "Wama nursilu almursaleena illa mubashshireena wamunthireena wayujadilu allatheena kafaroo bialbatili liyudhidoo bihi alhaqqa waittakhathoo ayatee wama onthiroo huzuwan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 57, "And who is more unjust than one who is reminded of the verses of his Lord but turns away from them and forgets what his hands have put forth? Indeed, We have placed over their hearts coverings, lest they understand it, and in their ears deafness. And if you invite them to guidance - they will never be guided, then - ever", "وَمَنۡ أَظۡلَمُ مِمَّن ذُكِّرَ بِـَٔايَٰتِ رَبِّهِۦ فَأَعۡرَضَ عَنۡهَا وَنَسِيَ مَا قَدَّمَتۡ يَدَاهُۚ إِنَّا جَعَلۡنَا عَلَىٰ قُلُوبِهِمۡ أَكِنَّةً أَن يَفۡقَهُوهُ وَفِيٓ ءَاذَانِهِمۡ وَقۡرٗاۖ وَإِن تَدۡعُهُمۡ إِلَى ٱلۡهُدَىٰ فَلَن يَهۡتَدُوٓاْ إِذًا أَبَدٗا", "Waman athlamu mimman thukkira biayati rabbihi faaAArada AAanha wanasiya ma qaddamat yadahu inna jaAAalna AAala quloobihim akinnatan an yafqahoohu wafee athanihim waqran wain tadAAuhum ila alhuda falan yahtadoo ithan abadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 58, "And your Lord is the Forgiving, full of mercy. If He were to impose blame upon them for what they earned, He would have hastened for them the punishment. Rather, for them is an appointment from which they will never find an escape", "وَرَبُّكَ ٱلۡغَفُورُ ذُو ٱلرَّحۡمَةِۖ لَوۡ يُؤَاخِذُهُم بِمَا كَسَبُواْ لَعَجَّلَ لَهُمُ ٱلۡعَذَابَۚ بَل لَّهُم مَّوۡعِدٞ لَّن يَجِدُواْ مِن دُونِهِۦ مَوۡئِلٗا", "Warabbuka alghafooru thoo alrrahmati law yuakhithuhum bima kasaboo laAAajjala lahumu alAAathaba bal lahum mawAAidun lan yajidoo min doonihi mawilan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 59, "And those cities - We destroyed them when they wronged, and We made for their destruction an appointed time", "وَتِلۡكَ ٱلۡقُرَىٰٓ أَهۡلَكۡنَٰهُمۡ لَمَّا ظَلَمُواْ وَجَعَلۡنَا لِمَهۡلِكِهِم مَّوۡعِدٗا", "Watilka alqura ahlaknahum lamma thalamoo wajaAAalna limahlikihim mawAAidan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 60, "And [mention] when Moses said to his servant, ""I will not cease [traveling] until I reach the junction of the two seas or continue for a long period", "وَإِذۡ قَالَ مُوسَىٰ لِفَتَىٰهُ لَآ أَبۡرَحُ حَتَّىٰٓ أَبۡلُغَ مَجۡمَعَ ٱلۡبَحۡرَيۡنِ أَوۡ أَمۡضِيَ حُقُبٗا", "Waith qala moosa lifatahu la abrahu hatta ablugha majmaAAa albahrayni aw amdiya huquban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 61, "But when they reached the junction between them, they forgot their fish, and it took its course into the sea, slipping away", "فَلَمَّا بَلَغَا مَجۡمَعَ بَيۡنِهِمَا نَسِيَا حُوتَهُمَا فَٱتَّخَذَ سَبِيلَهُۥ فِي ٱلۡبَحۡرِ سَرَبٗا", "Falamma balagha majmaAAa baynihima nasiya hootahuma faittakhatha sabeelahu fee albahri saraban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 62, "So when they had passed beyond it, [Moses] said to his boy, ""Bring us our morning meal. We have certainly suffered in this, our journey, [much] fatigue", "فَلَمَّا جَاوَزَا قَالَ لِفَتَىٰهُ ءَاتِنَا غَدَآءَنَا لَقَدۡ لَقِينَا مِن سَفَرِنَا هَٰذَا نَصَبٗا", "Falamma jawaza qala lifatahu atina ghadaana laqad laqeena min safarina hatha nasaban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 63, "He said, ""Did you see when we retired to the rock? Indeed, I forgot [there] the fish. And none made me forget it except Satan - that I should mention it. And it took its course into the sea amazingly", "قَالَ أَرَءَيۡتَ إِذۡ أَوَيۡنَآ إِلَى ٱلصَّخۡرَةِ فَإِنِّي نَسِيتُ ٱلۡحُوتَ وَمَآ أَنسَىٰنِيهُ إِلَّا ٱلشَّيۡطَٰنُ أَنۡ أَذۡكُرَهُۥۚ وَٱتَّخَذَ سَبِيلَهُۥ فِي ٱلۡبَحۡرِ عَجَبٗا", "Qala araayta ith awayna ila alssakhrati fainnee naseetu alhoota wama ansaneehu illa alshshaytanu an athkurahu waittakhatha sabeelahu fee albahri AAajaban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 64, "[Moses] said, ""That is what we were seeking."" So they returned, following their footprints", "قَالَ ذَٰلِكَ مَا كُنَّا نَبۡغِۚ فَٱرۡتَدَّا عَلَىٰٓ ءَاثَارِهِمَا قَصَصٗا", "Qala thalika ma kunna nabghi fairtadda AAala atharihima qasasan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 65, "And they found a servant from among Our servants to whom we had given mercy from us and had taught him from Us a [certain] knowledge", "فَوَجَدَا عَبۡدٗا مِّنۡ عِبَادِنَآ ءَاتَيۡنَٰهُ رَحۡمَةٗ مِّنۡ عِندِنَا وَعَلَّمۡنَٰهُ مِن لَّدُنَّا عِلۡمٗا", "Fawajada AAabdan min AAibadina ataynahu rahmatan min AAindina waAAallamnahu min ladunna AAilman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 66, "Moses said to him, ""May I follow you on [the condition] that you teach me from what you have been taught of sound judgement", "قَالَ لَهُۥ مُوسَىٰ هَلۡ أَتَّبِعُكَ عَلَىٰٓ أَن تُعَلِّمَنِ مِمَّا عُلِّمۡتَ رُشۡدٗا", "Qala lahu moosa hal attabiAAuka AAala an tuAAallimani mimma AAullimta rushdan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 67, "He said, ""Indeed, with me you will never be able to have patience", "قَالَ إِنَّكَ لَن تَسۡتَطِيعَ مَعِيَ صَبۡرٗا", "Qala innaka lan tastateeAAa maAAiya sabran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 68, "And how can you have patience for what you do not encompass in knowledge", "وَكَيۡفَ تَصۡبِرُ عَلَىٰ مَا لَمۡ تُحِطۡ بِهِۦ خُبۡرٗا", "Wakayfa tasbiru AAala ma lam tuhit bihi khubran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 69, "[Moses] said, ""You will find me, if Allah wills, patient, and I will not disobey you in [any] order", "قَالَ سَتَجِدُنِيٓ إِن شَآءَ ٱللَّهُ صَابِرٗا وَلَآ أَعۡصِي لَكَ أَمۡرٗا", "Qala satajidunee in shaa Allahu sabiran wala aAAsee laka amran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 70, "He said, ""Then if you follow me, do not ask me about anything until I make to you about it mention", "قَالَ فَإِنِ ٱتَّبَعۡتَنِي فَلَا تَسۡـَٔلۡنِي عَن شَيۡءٍ حَتَّىٰٓ أُحۡدِثَ لَكَ مِنۡهُ ذِكۡرٗا", "Qala faini ittabaAAtanee fala tasalnee AAan shayin hatta ohditha laka minhu thikran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 71, "So they set out, until when they had embarked on the ship, al-Khidh r tore it open. [Moses] said, ""Have you torn it open to drown its people? You have certainly done a grave thing", "فَٱنطَلَقَا حَتَّىٰٓ إِذَا رَكِبَا فِي ٱلسَّفِينَةِ خَرَقَهَاۖ قَالَ أَخَرَقۡتَهَا لِتُغۡرِقَ أَهۡلَهَا لَقَدۡ جِئۡتَ شَيۡـًٔا إِمۡرٗا", "Faintalaqa hatta itha rakiba fee alssafeenati kharaqaha qala akharaqtaha litughriqa ahlaha laqad jita shayan imran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 72, "[Al-Khidh r] said, ""Did I not say that with me you would never be able to have patience", "قَالَ أَلَمۡ أَقُلۡ إِنَّكَ لَن تَسۡتَطِيعَ مَعِيَ صَبۡرٗا", "Qala alam aqul innaka lan tastateeAAa maAAiya sabran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 73, "[Moses] said, ""Do not blame me for what I forgot and do not cover me in my matter with difficulty", "قَالَ لَا تُؤَاخِذۡنِي بِمَا نَسِيتُ وَلَا تُرۡهِقۡنِي مِنۡ أَمۡرِي عُسۡرٗا", "Qala la tuakhithnee bima naseetu wala turhiqnee min amree AAusran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 74, "So they set out, until when they met a boy, al-Khidh r killed him. [Moses] said, ""Have you killed a pure soul for other than [having killed] a soul? You have certainly done a deplorable thing", "فَٱنطَلَقَا حَتَّىٰٓ إِذَا لَقِيَا غُلَٰمٗا فَقَتَلَهُۥ قَالَ أَقَتَلۡتَ نَفۡسٗا زَكِيَّةَۢ بِغَيۡرِ نَفۡسٖ لَّقَدۡ جِئۡتَ شَيۡـٔٗا نُّكۡرٗا", "Faintalaqa hatta itha laqiya ghulaman faqatalahu qala aqatalta nafsan zakiyyatan bighayri nafsin laqad jita shayan nukran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 75, "[Al-Khidh r] said, ""Did I not tell you that with me you would never be able to have patience", "۞قَالَ أَلَمۡ أَقُل لَّكَ إِنَّكَ لَن تَسۡتَطِيعَ مَعِيَ صَبۡرٗا", "Qala alam aqul laka innaka lan tastateeAAa maAAiya sabran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 76, "[Moses] said, ""If I should ask you about anything after this, then do not keep me as a companion. You have obtained from me an excuse", "قَالَ إِن سَأَلۡتُكَ عَن شَيۡءِۭ بَعۡدَهَا فَلَا تُصَٰحِبۡنِيۖ قَدۡ بَلَغۡتَ مِن لَّدُنِّي عُذۡرٗا", "Qala in saaltuka AAan shayin baAAdaha fala tusahibnee qad balaghta min ladunnee AAuthran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 77, "So they set out, until when they came to the people of a town, they asked its people for food, but they refused to offer them hospitality. And they found therein a wall about to collapse, so al-Khidh r restored it. [Moses] said, ""If you wished, you could have taken for it a payment", "فَٱنطَلَقَا حَتَّىٰٓ إِذَآ أَتَيَآ أَهۡلَ قَرۡيَةٍ ٱسۡتَطۡعَمَآ أَهۡلَهَا فَأَبَوۡاْ أَن يُضَيِّفُوهُمَا فَوَجَدَا فِيهَا جِدَارٗا يُرِيدُ أَن يَنقَضَّ فَأَقَامَهُۥۖ قَالَ لَوۡ شِئۡتَ لَتَّخَذۡتَ عَلَيۡهِ أَجۡرٗا", "Faintalaqa hatta itha ataya ahla qaryatin istatAAama ahlaha faabaw an yudayyifoohuma fawajada feeha jidaran yureedu an yanqadda faaqamahu qala law shita laittakhathta AAalayhi ajran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 78, "[Al-Khidh r] said, ""This is parting between me and you. I will inform you of the interpretation of that about which you could not have patience", "قَالَ هَٰذَا فِرَاقُ بَيۡنِي وَبَيۡنِكَۚ سَأُنَبِّئُكَ بِتَأۡوِيلِ مَا لَمۡ تَسۡتَطِع عَّلَيۡهِ صَبۡرًا", "Qala hatha firaqu baynee wabaynika saonabbioka bitaweeli ma lam tastatiAA AAalayhi sabran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 79, "As for the ship, it belonged to poor people working at sea. So I intended to cause defect in it as there was after them a king who seized every [good] ship by force", "أَمَّا ٱلسَّفِينَةُ فَكَانَتۡ لِمَسَٰكِينَ يَعۡمَلُونَ فِي ٱلۡبَحۡرِ فَأَرَدتُّ أَنۡ أَعِيبَهَا وَكَانَ وَرَآءَهُم مَّلِكٞ يَأۡخُذُ كُلَّ سَفِينَةٍ غَصۡبٗا", "Amma alssafeenatu fakanat limasakeena yaAAmaloona fee albahri faaradtu an aAAeebaha wakana waraahum malikun yakhuthu kulla safeenatin ghasban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 80, "And as for the boy, his parents were believers, and we feared that he would overburden them by transgression and disbelief", "وَأَمَّا ٱلۡغُلَٰمُ فَكَانَ أَبَوَاهُ مُؤۡمِنَيۡنِ فَخَشِينَآ أَن يُرۡهِقَهُمَا طُغۡيَٰنٗا وَكُفۡرٗا", "Waamma alghulamu fakana abawahu muminayni fakhasheena an yurhiqahuma tughyanan wakufran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 81, "So we intended that their Lord should substitute for them one better than him in purity and nearer to mercy", "فَأَرَدۡنَآ أَن يُبۡدِلَهُمَا رَبُّهُمَا خَيۡرٗا مِّنۡهُ زَكَوٰةٗ وَأَقۡرَبَ رُحۡمٗا", "Faaradna an yubdilahuma rabbuhuma khayran minhu zakatan waaqraba ruhman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 82, "And as for the wall, it belonged to two orphan boys in the city, and there was beneath it a treasure for them, and their father had been righteous. So your Lord intended that they reach maturity and extract their treasure, as a mercy from your Lord. And I did it not of my own accord. That is the interpretation of that about which you could not have patience", "وَأَمَّا ٱلۡجِدَارُ فَكَانَ لِغُلَٰمَيۡنِ يَتِيمَيۡنِ فِي ٱلۡمَدِينَةِ وَكَانَ تَحۡتَهُۥ كَنزٞ لَّهُمَا وَكَانَ أَبُوهُمَا صَٰلِحٗا فَأَرَادَ رَبُّكَ أَن يَبۡلُغَآ أَشُدَّهُمَا وَيَسۡتَخۡرِجَا كَنزَهُمَا رَحۡمَةٗ مِّن رَّبِّكَۚ وَمَا فَعَلۡتُهُۥ عَنۡ أَمۡرِيۚ ذَٰلِكَ تَأۡوِيلُ مَا لَمۡ تَسۡطِع عَّلَيۡهِ صَبۡرٗا", "Waamma aljidaru fakana lighulamayni yateemayni fee almadeenati wakana tahtahu kanzun lahuma wakana aboohuma salihan faarada rabbuka an yablugha ashuddahuma wayastakhrija kanzahuma rahmatan min rabbika wama faAAaltuhu AAan amree thalika taweelu ma lam tastiAA AAalayhi sabran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 83, "And they ask you, [O Muhammad], about Dhul-Qarnayn. Say, ""I will recite to you about him a report", "وَيَسۡـَٔلُونَكَ عَن ذِي ٱلۡقَرۡنَيۡنِۖ قُلۡ سَأَتۡلُواْ عَلَيۡكُم مِّنۡهُ ذِكۡرًا", "Wayasaloonaka AAan thee alqarnayni qul saatloo AAalaykum minhu thikran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 84, "Indeed We established him upon the earth, and We gave him to everything a way", "إِنَّا مَكَّنَّا لَهُۥ فِي ٱلۡأَرۡضِ وَءَاتَيۡنَٰهُ مِن كُلِّ شَيۡءٖ سَبَبٗا", "Inna makkanna lahu fee alardi waataynahu min kulli shayin sababan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 85, "So he followed a way", "فَأَتۡبَعَ سَبَبًا", "FaatbaAAa sababan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 86, "Until, when he reached the setting of the sun, he found it [as if] setting in a spring of dark mud, and he found near it a people. Allah said, ""O Dhul-Qarnayn, either you punish [them] or else adopt among them [a way of] goodness", "حَتَّىٰٓ إِذَا بَلَغَ مَغۡرِبَ ٱلشَّمۡسِ وَجَدَهَا تَغۡرُبُ فِي عَيۡنٍ حَمِئَةٖ وَوَجَدَ عِندَهَا قَوۡمٗاۖ قُلۡنَا يَٰذَا ٱلۡقَرۡنَيۡنِ إِمَّآ أَن تُعَذِّبَ وَإِمَّآ أَن تَتَّخِذَ فِيهِمۡ حُسۡنٗا", "Hatta itha balagha maghriba alshshamsi wajadaha taghrubu fee AAaynin hamiatin wawajada AAindaha qawman qulna ya tha alqarnayni imma an tuAAaththiba waimma an tattakhitha feehim husnan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 87, "He said, ""As for one who wrongs, we will punish him. Then he will be returned to his Lord, and He will punish him with a terrible punishment", "قَالَ أَمَّا مَن ظَلَمَ فَسَوۡفَ نُعَذِّبُهُۥ ثُمَّ يُرَدُّ إِلَىٰ رَبِّهِۦ فَيُعَذِّبُهُۥ عَذَابٗا نُّكۡرٗا", "Qala amma man thalama fasawfa nuAAaththibuhu thumma yuraddu ila rabbihi fayuAAaththibuhu AAathaban nukran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 88, "But as for one who believes and does righteousness, he will have a reward of Paradise, and we will speak to him from our command with ease", "وَأَمَّا مَنۡ ءَامَنَ وَعَمِلَ صَٰلِحٗا فَلَهُۥ جَزَآءً ٱلۡحُسۡنَىٰۖ وَسَنَقُولُ لَهُۥ مِنۡ أَمۡرِنَا يُسۡرٗا", "Waamma man amana waAAamila salihan falahu jazaan alhusna wasanaqoolu lahu min amrina yusran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 89, "Then he followed a way", "ثُمَّ أَتۡبَعَ سَبَبًا", "Thumma atbaAAa sababan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 90, "Until, when he came to the rising of the sun, he found it rising on a people for whom We had not made against it any shield", "حَتَّىٰٓ إِذَا بَلَغَ مَطۡلِعَ ٱلشَّمۡسِ وَجَدَهَا تَطۡلُعُ عَلَىٰ قَوۡمٖ لَّمۡ نَجۡعَل لَّهُم مِّن دُونِهَا سِتۡرٗا", "Hatta itha balagha matliAAa alshshamsi wajadaha tatluAAu AAala qawmin lam najAAal lahum min dooniha sitran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 91, "Thus. And We had encompassed [all] that he had in knowledge", "كَذَٰلِكَۖ وَقَدۡ أَحَطۡنَا بِمَا لَدَيۡهِ خُبۡرٗا", "Kathalika waqad ahatna bima ladayhi khubran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 92, "Then he followed a way", "ثُمَّ أَتۡبَعَ سَبَبًا", "Thumma atbaAAa sababan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 93, "Until, when he reached [a pass] between two mountains, he found beside them a people who could hardly understand [his] speech", "حَتَّىٰٓ إِذَا بَلَغَ بَيۡنَ ٱلسَّدَّيۡنِ وَجَدَ مِن دُونِهِمَا قَوۡمٗا لَّا يَكَادُونَ يَفۡقَهُونَ قَوۡلٗا", "Hatta itha balagha bayna alssaddayni wajada min doonihima qawman la yakadoona yafqahoona qawlan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 94, "They said, ""O Dhul-Qarnayn, indeed Gog and Magog are [great] corrupters in the land. So may we assign for you an expenditure that you might make between us and them a barrier", "قَالُواْ يَٰذَا ٱلۡقَرۡنَيۡنِ إِنَّ يَأۡجُوجَ وَمَأۡجُوجَ مُفۡسِدُونَ فِي ٱلۡأَرۡضِ فَهَلۡ نَجۡعَلُ لَكَ خَرۡجًا عَلَىٰٓ أَن تَجۡعَلَ بَيۡنَنَا وَبَيۡنَهُمۡ سَدّٗا", "Qaloo ya tha alqarnayni inna yajooja wamajooja mufsidoona fee alardi fahal najAAalu laka kharjan AAala an tajAAala baynana wabaynahum saddan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 95, "He said, ""That in which my Lord has established me is better [than what you offer], but assist me with strength; I will make between you and them a dam", "قَالَ مَا مَكَّنِّي فِيهِ رَبِّي خَيۡرٞ فَأَعِينُونِي بِقُوَّةٍ أَجۡعَلۡ بَيۡنَكُمۡ وَبَيۡنَهُمۡ رَدۡمًا", "Qala ma makkannee feehi rabbee khayrun faaAAeenoonee biquwwatin ajAAal baynakum wabaynahum radman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 96, "Bring me sheets of iron"" - until, when he had leveled [them] between the two mountain walls, he said, ""Blow [with bellows],"" until when he had made it [like] fire, he said, ""Bring me, that I may pour over it molten copper", "ءَاتُونِي زُبَرَ ٱلۡحَدِيدِۖ حَتَّىٰٓ إِذَا سَاوَىٰ بَيۡنَ ٱلصَّدَفَيۡنِ قَالَ ٱنفُخُواْۖ حَتَّىٰٓ إِذَا جَعَلَهُۥ نَارٗا قَالَ ءَاتُونِيٓ أُفۡرِغۡ عَلَيۡهِ قِطۡرٗا", "Atoonee zubara alhadeedi hatta itha sawa bayna alsadafayni qala onfukhoo hatta itha jaAAalahu naran qala atoonee ofrigh AAalayhi qitran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 97, "So Gog and Magog were unable to pass over it, nor were they able [to effect] in it any penetration", "فَمَا ٱسۡطَٰعُوٓاْ أَن يَظۡهَرُوهُ وَمَا ٱسۡتَطَٰعُواْ لَهُۥ نَقۡبٗا", "Fama istaAAoo an yathharoohu wama istataAAoo lahu naqban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 98, "[Dhul-Qarnayn] said, ""This is a mercy from my Lord; but when the promise of my Lord comes, He will make it level, and ever is the promise of my Lord true", "قَالَ هَٰذَا رَحۡمَةٞ مِّن رَّبِّيۖ فَإِذَا جَآءَ وَعۡدُ رَبِّي جَعَلَهُۥ دَكَّآءَۖ وَكَانَ وَعۡدُ رَبِّي حَقّٗا", "Qala hatha rahmatun min rabbee faitha jaa waAAdu rabbee jaAAalahu dakkaa wakana waAAdu rabbee haqqan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 99, "And We will leave them that day surging over each other, and [then] the Horn will be blown, and We will assemble them in [one] assembly", "۞وَتَرَكۡنَا بَعۡضَهُمۡ يَوۡمَئِذٖ يَمُوجُ فِي بَعۡضٖۖ وَنُفِخَ فِي ٱلصُّورِ فَجَمَعۡنَٰهُمۡ جَمۡعٗا", "Watarakna baAAdahum yawmaithin yamooju fee baAAdin wanufikha fee alssoori fajamaAAnahum jamAAan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 100, "And We will present Hell that Day to the Disbelievers, on display", "وَعَرَضۡنَا جَهَنَّمَ يَوۡمَئِذٖ لِّلۡكَٰفِرِينَ عَرۡضًا", "WaAAaradna jahannama yawmaithin lilkafireena AAardan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 101, "Those whose eyes had been within a cover [removed] from My remembrance, and they were not able to hear", "ٱلَّذِينَ كَانَتۡ أَعۡيُنُهُمۡ فِي غِطَآءٍ عَن ذِكۡرِي وَكَانُواْ لَا يَسۡتَطِيعُونَ سَمۡعًا", "Allatheena kanat aAAyunuhum fee ghitain AAan thikree wakanoo la yastateeAAoona samAAan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 102, "Then do those who disbelieve think that they can take My servants instead of Me as allies? Indeed, We have prepared Hell for the disbelievers as a lodging", "أَفَحَسِبَ ٱلَّذِينَ كَفَرُوٓاْ أَن يَتَّخِذُواْ عِبَادِي مِن دُونِيٓ أَوۡلِيَآءَۚ إِنَّآ أَعۡتَدۡنَا جَهَنَّمَ لِلۡكَٰفِرِينَ نُزُلٗا", "Afahasiba allatheena kafaroo an yattakhithoo AAibadee min doonee awliyaa inna aAAtadna jahannama lilkafireena nuzulan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 103, "Say, [O Muhammad], ""Shall we [believers] inform you of the greatest losers as to [their] deeds", "قُلۡ هَلۡ نُنَبِّئُكُم بِٱلۡأَخۡسَرِينَ أَعۡمَٰلًا", "Qul hal nunabbiokum bialakhsareena aAAmalan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 104, "[They are] those whose effort is lost in worldly life, while they think that they are doing well in work", "ٱلَّذِينَ ضَلَّ سَعۡيُهُمۡ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَهُمۡ يَحۡسَبُونَ أَنَّهُمۡ يُحۡسِنُونَ صُنۡعًا", "Allatheena dalla saAAyuhum fee alhayati alddunya wahum yahsaboona annahum yuhsinoona sunAAan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 105, "Those are the ones who disbelieve in the verses of their Lord and in [their] meeting Him, so their deeds have become worthless; and We will not assign to them on the Day of Resurrection any importance", "أُوْلَـٰٓئِكَ ٱلَّذِينَ كَفَرُواْ بِـَٔايَٰتِ رَبِّهِمۡ وَلِقَآئِهِۦ فَحَبِطَتۡ أَعۡمَٰلُهُمۡ فَلَا نُقِيمُ لَهُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ وَزۡنٗا", "Olaika allatheena kafaroo biayati rabbihim waliqaihi fahabitat aAAmaluhum fala nuqeemu lahum yawma alqiyamati waznan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 106, "That is their recompense - Hell - for what they denied and [because] they took My signs and My messengers in ridicule", "ذَٰلِكَ جَزَآؤُهُمۡ جَهَنَّمُ بِمَا كَفَرُواْ وَٱتَّخَذُوٓاْ ءَايَٰتِي وَرُسُلِي هُزُوًا", "Thalika jazaohum jahannamu bima kafaroo waittakhathoo ayatee warusulee huzuwan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 107, "Indeed, those who have believed and done righteous deeds - they will have the Gardens of Paradise as a lodging", "إِنَّ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ كَانَتۡ لَهُمۡ جَنَّـٰتُ ٱلۡفِرۡدَوۡسِ نُزُلًا", "Inna allatheena amanoo waAAamiloo alssalihati kanat lahum jannatu alfirdawsi nuzulan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 108, "Wherein they abide eternally. They will not desire from it any transfer", "خَٰلِدِينَ فِيهَا لَا يَبۡغُونَ عَنۡهَا حِوَلٗا", "Khalideena feeha la yabghoona AAanha hiwalan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 109, "Say, ""If the sea were ink for [writing] the words of my Lord, the sea would be exhausted before the words of my Lord were exhausted, even if We brought the like of it as a supplement", "قُل لَّوۡ كَانَ ٱلۡبَحۡرُ مِدَادٗا لِّكَلِمَٰتِ رَبِّي لَنَفِدَ ٱلۡبَحۡرُ قَبۡلَ أَن تَنفَدَ كَلِمَٰتُ رَبِّي وَلَوۡ جِئۡنَا بِمِثۡلِهِۦ مَدَدٗا", "Qul law kana albahru midadan likalimati rabbee lanafida albahru qabla an tanfada kalimatu rabbee walaw jina bimithlihi madadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (18, 110, "Say, ""I am only a man like you, to whom has been revealed that your god is one God. So whoever would hope for the meeting with his Lord - let him do righteous work and not associate in the worship of his Lord anyone", "قُلۡ إِنَّمَآ أَنَا۠ بَشَرٞ مِّثۡلُكُمۡ يُوحَىٰٓ إِلَيَّ أَنَّمَآ إِلَٰهُكُمۡ إِلَٰهٞ وَٰحِدٞۖ فَمَن كَانَ يَرۡجُواْ لِقَآءَ رَبِّهِۦ فَلۡيَعۡمَلۡ عَمَلٗا صَٰلِحٗا وَلَا يُشۡرِكۡ بِعِبَادَةِ رَبِّهِۦٓ أَحَدَۢا", "Qul innama ana basharun mithlukum yooha ilayya annama ilahukum ilahun wahidun faman kana yarjoo liqaa rabbihi falyaAAmal AAamalan salihan wala yushrik biAAibadati rabbihi ahadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 1, "Kaf, Ha, Ya, 'Ayn, Sad", "كٓهيعٓصٓ", "KafhayaAAaynsad");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 2, "[This is] a mention of the mercy of your Lord to His servant Zechariah", "ذِكۡرُ رَحۡمَتِ رَبِّكَ عَبۡدَهُۥ زَكَرِيَّآ", "Thikru rahmati rabbika AAabdahu zakariyya");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 3, "When he called to his Lord a private supplication", "إِذۡ نَادَىٰ رَبَّهُۥ نِدَآءً خَفِيّٗا", "Ith nada rabbahu nidaan khafiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 4, "He said, ""My Lord, indeed my bones have weakened, and my head has filled with white, and never have I been in my supplication to You, my Lord, unhappy", "قَالَ رَبِّ إِنِّي وَهَنَ ٱلۡعَظۡمُ مِنِّي وَٱشۡتَعَلَ ٱلرَّأۡسُ شَيۡبٗا وَلَمۡ أَكُنۢ بِدُعَآئِكَ رَبِّ شَقِيّٗا", "Qala rabbi innee wahana alAAathmu minnee waishtaAAala alrrasu shayban walam akun biduAAaika rabbi shaqiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 5, "And indeed, I fear the successors after me, and my wife has been barren, so give me from Yourself an heir", "وَإِنِّي خِفۡتُ ٱلۡمَوَٰلِيَ مِن وَرَآءِي وَكَانَتِ ٱمۡرَأَتِي عَاقِرٗا فَهَبۡ لِي مِن لَّدُنكَ وَلِيّٗا", "Wainnee khiftu almawaliya min waraee wakanati imraatee AAaqiran fahab lee min ladunka waliyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 6, "Who will inherit me and inherit from the family of Jacob. And make him, my Lord, pleasing [to You]", "يَرِثُنِي وَيَرِثُ مِنۡ ءَالِ يَعۡقُوبَۖ وَٱجۡعَلۡهُ رَبِّ رَضِيّٗا", "Yarithunee wayarithu min ali yaAAqooba waijAAalhu rabbi radiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 7, "[He was told], ""O Zechariah, indeed We give you good tidings of a boy whose name will be John. We have not assigned to any before [this] name", "يَٰزَكَرِيَّآ إِنَّا نُبَشِّرُكَ بِغُلَٰمٍ ٱسۡمُهُۥ يَحۡيَىٰ لَمۡ نَجۡعَل لَّهُۥ مِن قَبۡلُ سَمِيّٗا", "Ya zakariyya inna nubashshiruka bighulamin ismuhu yahya lam najAAal lahu min qablu samiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 8, "He said, ""My Lord, how will I have a boy when my wife has been barren and I have reached extreme old age", "قَالَ رَبِّ أَنَّىٰ يَكُونُ لِي غُلَٰمٞ وَكَانَتِ ٱمۡرَأَتِي عَاقِرٗا وَقَدۡ بَلَغۡتُ مِنَ ٱلۡكِبَرِ عِتِيّٗا", "Qala rabbi anna yakoonu lee ghulamun wakanati imraatee AAaqiran waqad balaghtu mina alkibari AAitiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 9, "[An angel] said, ""Thus [it will be]; your Lord says, 'It is easy for Me, for I created you before, while you were nothing", "قَالَ كَذَٰلِكَ قَالَ رَبُّكَ هُوَ عَلَيَّ هَيِّنٞ وَقَدۡ خَلَقۡتُكَ مِن قَبۡلُ وَلَمۡ تَكُ شَيۡـٔٗا", "Qala kathalika qala rabbuka huwa AAalayya hayyinun waqad khalaqtuka min qablu walam taku shayan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 10, "[Zechariah] said, ""My Lord, make for me a sign."" He said, ""Your sign is that you will not speak to the people for three nights, [being] sound", "قَالَ رَبِّ ٱجۡعَل لِّيٓ ءَايَةٗۖ قَالَ ءَايَتُكَ أَلَّا تُكَلِّمَ ٱلنَّاسَ ثَلَٰثَ لَيَالٖ سَوِيّٗا", "Qala rabbi ijAAal lee ayatan qala ayatuka alla tukallima alnnasa thalatha layalin sawiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 11, "So he came out to his people from the prayer chamber and signaled to them to exalt [Allah] in the morning and afternoon", "فَخَرَجَ عَلَىٰ قَوۡمِهِۦ مِنَ ٱلۡمِحۡرَابِ فَأَوۡحَىٰٓ إِلَيۡهِمۡ أَن سَبِّحُواْ بُكۡرَةٗ وَعَشِيّٗا", "Fakharaja AAala qawmihi mina almihrabi faawha ilayhim an sabbihoo bukratan waAAashiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 12, "[Allah] said, ""O John, take the Scripture with determination."" And We gave him judgement [while yet] a boy", "يَٰيَحۡيَىٰ خُذِ ٱلۡكِتَٰبَ بِقُوَّةٖۖ وَءَاتَيۡنَٰهُ ٱلۡحُكۡمَ صَبِيّٗا", "Ya yahya khuthi alkitaba biquwwatin waataynahu alhukma sabiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 13, "And affection from Us and purity, and he was fearing of Allah", "وَحَنَانٗا مِّن لَّدُنَّا وَزَكَوٰةٗۖ وَكَانَ تَقِيّٗا", "Wahananan min ladunna wazakatan wakana taqiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 14, "And dutiful to his parents, and he was not a disobedient tyrant", "وَبَرَّۢا بِوَٰلِدَيۡهِ وَلَمۡ يَكُن جَبَّارًا عَصِيّٗا", "Wabarran biwalidayhi walam yakun jabbaran AAasiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 15, "And peace be upon him the day he was born and the day he dies and the day he is raised alive", "وَسَلَٰمٌ عَلَيۡهِ يَوۡمَ وُلِدَ وَيَوۡمَ يَمُوتُ وَيَوۡمَ يُبۡعَثُ حَيّٗا", "Wasalamun AAalayhi yawma wulida wayawma yamootu wayawma yubAAathu hayyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 16, "And mention, [O Muhammad], in the Book [the story of] Mary, when she withdrew from her family to a place toward the east", "وَٱذۡكُرۡ فِي ٱلۡكِتَٰبِ مَرۡيَمَ إِذِ ٱنتَبَذَتۡ مِنۡ أَهۡلِهَا مَكَانٗا شَرۡقِيّٗا", "Waothkur fee alkitabi maryama ithi intabathat min ahliha makanan sharqiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 17, "And she took, in seclusion from them, a screen. Then We sent to her Our Angel, and he represented himself to her as a well-proportioned man", "فَٱتَّخَذَتۡ مِن دُونِهِمۡ حِجَابٗا فَأَرۡسَلۡنَآ إِلَيۡهَا رُوحَنَا فَتَمَثَّلَ لَهَا بَشَرٗا سَوِيّٗا", "Faittakhathat min doonihim hijaban faarsalna ilayha roohana fatamaththala laha basharan sawiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 18, "She said, ""Indeed, I seek refuge in the Most Merciful from you, [so leave me], if you should be fearing of Allah", "قَالَتۡ إِنِّيٓ أَعُوذُ بِٱلرَّحۡمَٰنِ مِنكَ إِن كُنتَ تَقِيّٗا", "Qalat innee aAAoothu bialrrahmani minka in kunta taqiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 19, "He said, ""I am only the messenger of your Lord to give you [news of] a pure boy", "قَالَ إِنَّمَآ أَنَا۠ رَسُولُ رَبِّكِ لِأَهَبَ لَكِ غُلَٰمٗا زَكِيّٗا", "Qala innama ana rasoolu rabbiki liahaba laki ghulaman zakiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 20, "She said, ""How can I have a boy while no man has touched me and I have not been unchaste", "قَالَتۡ أَنَّىٰ يَكُونُ لِي غُلَٰمٞ وَلَمۡ يَمۡسَسۡنِي بَشَرٞ وَلَمۡ أَكُ بَغِيّٗا", "Qalat anna yakoonu lee ghulamun walam yamsasnee basharun walam aku baghiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 21, "He said, ""Thus [it will be]; your Lord says, 'It is easy for Me, and We will make him a sign to the people and a mercy from Us. And it is a matter [already] decreed", "قَالَ كَذَٰلِكِ قَالَ رَبُّكِ هُوَ عَلَيَّ هَيِّنٞۖ وَلِنَجۡعَلَهُۥٓ ءَايَةٗ لِّلنَّاسِ وَرَحۡمَةٗ مِّنَّاۚ وَكَانَ أَمۡرٗا مَّقۡضِيّٗا", "Qala kathaliki qala rabbuki huwa AAalayya hayyinun walinajAAalahu ayatan lilnnasi warahmatan minna wakana amran maqdiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 22, "So she conceived him, and she withdrew with him to a remote place", "۞فَحَمَلَتۡهُ فَٱنتَبَذَتۡ بِهِۦ مَكَانٗا قَصِيّٗا", "Fahamalathu faintabathat bihi makanan qasiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 23, "And the pains of childbirth drove her to the trunk of a palm tree. She said, ""Oh, I wish I had died before this and was in oblivion, forgotten", "فَأَجَآءَهَا ٱلۡمَخَاضُ إِلَىٰ جِذۡعِ ٱلنَّخۡلَةِ قَالَتۡ يَٰلَيۡتَنِي مِتُّ قَبۡلَ هَٰذَا وَكُنتُ نَسۡيٗا مَّنسِيّٗا", "Faajaaha almakhadu ila jithAAi alnnakhlati qalat ya laytanee mittu qabla hatha wakuntu nasyan mansiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 24, "But he called her from below her, ""Do not grieve; your Lord has provided beneath you a stream", "فَنَادَىٰهَا مِن تَحۡتِهَآ أَلَّا تَحۡزَنِي قَدۡ جَعَلَ رَبُّكِ تَحۡتَكِ سَرِيّٗا", "Fanadaha min tahtiha alla tahzanee qad jaAAala rabbuki tahtaki sariyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 25, "And shake toward you the trunk of the palm tree; it will drop upon you ripe, fresh dates", "وَهُزِّيٓ إِلَيۡكِ بِجِذۡعِ ٱلنَّخۡلَةِ تُسَٰقِطۡ عَلَيۡكِ رُطَبٗا جَنِيّٗا", "Wahuzzee ilayki bijithAAi alnnakhlati tusaqit AAalayki rutaban janiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 26, "So eat and drink and be contented. And if you see from among humanity anyone, say, 'Indeed, I have vowed to the Most Merciful abstention, so I will not speak today to [any] man", "فَكُلِي وَٱشۡرَبِي وَقَرِّي عَيۡنٗاۖ فَإِمَّا تَرَيِنَّ مِنَ ٱلۡبَشَرِ أَحَدٗا فَقُولِيٓ إِنِّي نَذَرۡتُ لِلرَّحۡمَٰنِ صَوۡمٗا فَلَنۡ أُكَلِّمَ ٱلۡيَوۡمَ إِنسِيّٗا", "Fakulee waishrabee waqarree AAaynan faimma tarayinna mina albashari ahadan faqoolee innee nathartu lilrrahmani sawman falan okallima alyawma insiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 27, "Then she brought him to her people, carrying him. They said, ""O Mary, you have certainly done a thing unprecedented", "فَأَتَتۡ بِهِۦ قَوۡمَهَا تَحۡمِلُهُۥۖ قَالُواْ يَٰمَرۡيَمُ لَقَدۡ جِئۡتِ شَيۡـٔٗا فَرِيّٗا", "Faatat bihi qawmaha tahmiluhu qaloo ya maryamu laqad jiti shayan fariyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 28, "O sister of Aaron, your father was not a man of evil, nor was your mother unchaste", "يَـٰٓأُخۡتَ هَٰرُونَ مَا كَانَ أَبُوكِ ٱمۡرَأَ سَوۡءٖ وَمَا كَانَتۡ أُمُّكِ بَغِيّٗا", "Ya okhta haroona ma kana abooki imraa sawin wama kanat ommuki baghiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 29, "So she pointed to him. They said, ""How can we speak to one who is in the cradle a child", "فَأَشَارَتۡ إِلَيۡهِۖ قَالُواْ كَيۡفَ نُكَلِّمُ مَن كَانَ فِي ٱلۡمَهۡدِ صَبِيّٗا", "Faasharat ilayhi qaloo kayfa nukallimu man kana fee almahdi sabiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 30, "[Jesus] said, ""Indeed, I am the servant of Allah. He has given me the Scripture and made me a prophet", "قَالَ إِنِّي عَبۡدُ ٱللَّهِ ءَاتَىٰنِيَ ٱلۡكِتَٰبَ وَجَعَلَنِي نَبِيّٗا", "Qala innee AAabdu Allahi ataniya alkitaba wajaAAalanee nabiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 31, "And He has made me blessed wherever I am and has enjoined upon me prayer and zakah as long as I remain alive", "وَجَعَلَنِي مُبَارَكًا أَيۡنَ مَا كُنتُ وَأَوۡصَٰنِي بِٱلصَّلَوٰةِ وَٱلزَّكَوٰةِ مَا دُمۡتُ حَيّٗا", "WajaAAalanee mubarakan ayna ma kuntu waawsanee bialssalati waalzzakati ma dumtu hayyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 32, "And [made me] dutiful to my mother, and He has not made me a wretched tyrant", "وَبَرَّۢا بِوَٰلِدَتِي وَلَمۡ يَجۡعَلۡنِي جَبَّارٗا شَقِيّٗا", "Wabarran biwalidatee walam yajAAalnee jabbaran shaqiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 33, "And peace is on me the day I was born and the day I will die and the day I am raised alive", "وَٱلسَّلَٰمُ عَلَيَّ يَوۡمَ وُلِدتُّ وَيَوۡمَ أَمُوتُ وَيَوۡمَ أُبۡعَثُ حَيّٗا", "Waalssalamu AAalayya yawma wulidtu wayawma amootu wayawma obAAathu hayyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 34, "That is Jesus, the son of Mary - the word of truth about which they are in dispute", "ذَٰلِكَ عِيسَى ٱبۡنُ مَرۡيَمَۖ قَوۡلَ ٱلۡحَقِّ ٱلَّذِي فِيهِ يَمۡتَرُونَ", "Thalika AAeesa ibnu maryama qawla alhaqqi allathee feehi yamtaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 35, "It is not [befitting] for Allah to take a son; exalted is He! When He decrees an affair, He only says to it, ""Be,"" and it is", "مَا كَانَ لِلَّهِ أَن يَتَّخِذَ مِن وَلَدٖۖ سُبۡحَٰنَهُۥٓۚ إِذَا قَضَىٰٓ أَمۡرٗا فَإِنَّمَا يَقُولُ لَهُۥ كُن فَيَكُونُ", "Ma kana lillahi an yattakhitha min waladin subhanahu itha qada amran fainnama yaqoolu lahu kun fayakoonu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 36, "[Jesus said], ""And indeed, Allah is my Lord and your Lord, so worship Him. That is a straight path", "وَإِنَّ ٱللَّهَ رَبِّي وَرَبُّكُمۡ فَٱعۡبُدُوهُۚ هَٰذَا صِرَٰطٞ مُّسۡتَقِيمٞ", "Wainna Allaha rabbee warabbukum faoAAbudoohu hatha siratun mustaqeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 37, "Then the factions differed [concerning Jesus] from among them, so woe to those who disbelieved - from the scene of a tremendous Day", "فَٱخۡتَلَفَ ٱلۡأَحۡزَابُ مِنۢ بَيۡنِهِمۡۖ فَوَيۡلٞ لِّلَّذِينَ كَفَرُواْ مِن مَّشۡهَدِ يَوۡمٍ عَظِيمٍ", "Faikhtalafa alahzabu min baynihim fawaylun lillatheena kafaroo min mashhadi yawmin AAatheemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 38, "How [clearly] they will hear and see the Day they come to Us, but the wrongdoers today are in clear error", "أَسۡمِعۡ بِهِمۡ وَأَبۡصِرۡ يَوۡمَ يَأۡتُونَنَا لَٰكِنِ ٱلظَّـٰلِمُونَ ٱلۡيَوۡمَ فِي ضَلَٰلٖ مُّبِينٖ", "AsmiAA bihim waabsir yawma yatoonana lakini alththalimoona alyawma fee dalalin mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 39, "And warn them, [O Muhammad], of the Day of Regret, when the matter will be concluded; and [yet], they are in [a state of] heedlessness, and they do not believe", "وَأَنذِرۡهُمۡ يَوۡمَ ٱلۡحَسۡرَةِ إِذۡ قُضِيَ ٱلۡأَمۡرُ وَهُمۡ فِي غَفۡلَةٖ وَهُمۡ لَا يُؤۡمِنُونَ", "Waanthirhum yawma alhasrati ith qudiya alamru wahum fee ghaflatin wahum la yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 40, "Indeed, it is We who will inherit the earth and whoever is on it, and to Us they will be returned", "إِنَّا نَحۡنُ نَرِثُ ٱلۡأَرۡضَ وَمَنۡ عَلَيۡهَا وَإِلَيۡنَا يُرۡجَعُونَ", "Inna nahnu narithu alarda waman AAalayha wailayna yurjaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 41, "And mention in the Book [the story of] Abraham. Indeed, he was a man of truth and a prophet", "وَٱذۡكُرۡ فِي ٱلۡكِتَٰبِ إِبۡرَٰهِيمَۚ إِنَّهُۥ كَانَ صِدِّيقٗا نَّبِيًّا", "Waothkur fee alkitabi ibraheema innahu kana siddeeqan nabiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 42, "[Mention] when he said to his father, ""O my father, why do you worship that which does not hear and does not see and will not benefit you at all", "إِذۡ قَالَ لِأَبِيهِ يَـٰٓأَبَتِ لِمَ تَعۡبُدُ مَا لَا يَسۡمَعُ وَلَا يُبۡصِرُ وَلَا يُغۡنِي عَنكَ شَيۡـٔٗا", "Ith qala liabeehi ya abati lima taAAbudu ma la yasmaAAu wala yubsiru wala yughnee AAanka shayan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 43, "O my father, indeed there has come to me of knowledge that which has not come to you, so follow me; I will guide you to an even path", "يَـٰٓأَبَتِ إِنِّي قَدۡ جَآءَنِي مِنَ ٱلۡعِلۡمِ مَا لَمۡ يَأۡتِكَ فَٱتَّبِعۡنِيٓ أَهۡدِكَ صِرَٰطٗا سَوِيّٗا", "Ya abati innee qad jaanee mina alAAilmi ma lam yatika faittabiAAnee ahdika siratan sawiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 44, "O my father, do not worship Satan. Indeed Satan has ever been, to the Most Merciful, disobedient", "يَـٰٓأَبَتِ لَا تَعۡبُدِ ٱلشَّيۡطَٰنَۖ إِنَّ ٱلشَّيۡطَٰنَ كَانَ لِلرَّحۡمَٰنِ عَصِيّٗا", "Ya abati la taAAbudi alshshaytana inna alshshaytana kana lilrrahmani AAasiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 45, "O my father, indeed I fear that there will touch you a punishment from the Most Merciful so you would be to Satan a companion [in Hellfire]", "يَـٰٓأَبَتِ إِنِّيٓ أَخَافُ أَن يَمَسَّكَ عَذَابٞ مِّنَ ٱلرَّحۡمَٰنِ فَتَكُونَ لِلشَّيۡطَٰنِ وَلِيّٗا", "Ya abati innee akhafu an yamassaka AAathabun mina alrrahmani fatakoona lilshshyatani waliyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 46, "[His father] said, ""Have you no desire for my gods, O Abraham? If you do not desist, I will surely stone you, so avoid me a prolonged time", "قَالَ أَرَاغِبٌ أَنتَ عَنۡ ءَالِهَتِي يَـٰٓإِبۡرَٰهِيمُۖ لَئِن لَّمۡ تَنتَهِ لَأَرۡجُمَنَّكَۖ وَٱهۡجُرۡنِي مَلِيّٗا", "Qala araghibun anta AAan alihatee ya ibraheemu lain lam tantahi laarjumannaka waohjurnee maliyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 47, "[Abraham] said, ""Peace will be upon you. I will ask forgiveness for you of my Lord. Indeed, He is ever gracious to me", "قَالَ سَلَٰمٌ عَلَيۡكَۖ سَأَسۡتَغۡفِرُ لَكَ رَبِّيٓۖ إِنَّهُۥ كَانَ بِي حَفِيّٗا", "Qala salamun AAalayka saastaghfiru laka rabbee innahu kana bee hafiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 48, "And I will leave you and those you invoke other than Allah and will invoke my Lord. I expect that I will not be in invocation to my Lord unhappy", "وَأَعۡتَزِلُكُمۡ وَمَا تَدۡعُونَ مِن دُونِ ٱللَّهِ وَأَدۡعُواْ رَبِّي عَسَىٰٓ أَلَّآ أَكُونَ بِدُعَآءِ رَبِّي شَقِيّٗا", "WaaAAtazilukum wama tadAAoona min dooni Allahi waadAAoo rabbee AAasa alla akoona biduAAai rabbee shaqiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 49, "So when he had left them and those they worshipped other than Allah, We gave him Isaac and Jacob, and each [of them] We made a prophet", "فَلَمَّا ٱعۡتَزَلَهُمۡ وَمَا يَعۡبُدُونَ مِن دُونِ ٱللَّهِ وَهَبۡنَا لَهُۥٓ إِسۡحَٰقَ وَيَعۡقُوبَۖ وَكُلّٗا جَعَلۡنَا نَبِيّٗا", "Falamma iAAtazalahum wama yaAAbudoona min dooni Allahi wahabna lahu ishaqa wayaAAqooba wakullan jaAAalna nabiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 50, "And We gave them of Our mercy, and we made for them a reputation of high honor", "وَوَهَبۡنَا لَهُم مِّن رَّحۡمَتِنَا وَجَعَلۡنَا لَهُمۡ لِسَانَ صِدۡقٍ عَلِيّٗا", "Wawahabna lahum min rahmatina wajaAAalna lahum lisana sidqin AAaliyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 51, "And mention in the Book, Moses. Indeed, he was chosen, and he was a messenger and a prophet", "وَٱذۡكُرۡ فِي ٱلۡكِتَٰبِ مُوسَىٰٓۚ إِنَّهُۥ كَانَ مُخۡلَصٗا وَكَانَ رَسُولٗا نَّبِيّٗا", "Waothkur fee alkitabi moosa innahu kana mukhlasan wakana rasoolan nabiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 52, "And We called him from the side of the mount at [his] right and brought him near, confiding [to him]", "وَنَٰدَيۡنَٰهُ مِن جَانِبِ ٱلطُّورِ ٱلۡأَيۡمَنِ وَقَرَّبۡنَٰهُ نَجِيّٗا", "Wanadaynahu min janibi alttoori alaymani waqarrabnahu najiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 53, "And We gave him out of Our mercy his brother Aaron as a prophet", "وَوَهَبۡنَا لَهُۥ مِن رَّحۡمَتِنَآ أَخَاهُ هَٰرُونَ نَبِيّٗا", "Wawahabna lahu min rahmatina akhahu haroona nabiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 54, "And mention in the Book, Ishmael. Indeed, he was true to his promise, and he was a messenger and a prophet", "وَٱذۡكُرۡ فِي ٱلۡكِتَٰبِ إِسۡمَٰعِيلَۚ إِنَّهُۥ كَانَ صَادِقَ ٱلۡوَعۡدِ وَكَانَ رَسُولٗا نَّبِيّٗا", "Waothkur fee alkitabi ismaAAeela innahu kana sadiqa alwaAAdi wakana rasoolan nabiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 55, "And he used to enjoin on his people prayer and zakah and was to his Lord pleasing", "وَكَانَ يَأۡمُرُ أَهۡلَهُۥ بِٱلصَّلَوٰةِ وَٱلزَّكَوٰةِ وَكَانَ عِندَ رَبِّهِۦ مَرۡضِيّٗا", "Wakana yamuru ahlahu bialssalati waalzzakati wakana AAinda rabbihi mardiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 56, "And mention in the Book, Idrees. Indeed, he was a man of truth and a prophet", "وَٱذۡكُرۡ فِي ٱلۡكِتَٰبِ إِدۡرِيسَۚ إِنَّهُۥ كَانَ صِدِّيقٗا نَّبِيّٗا", "Waothkur fee alkitabi idreesa innahu kana siddeeqan nabiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 57, "And We raised him to a high station", "وَرَفَعۡنَٰهُ مَكَانًا عَلِيًّا", "WarafaAAnahu makanan AAaliyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 58, "Those were the ones upon whom Allah bestowed favor from among the prophets of the descendants of Adam and of those We carried [in the ship] with Noah, and of the descendants of Abraham and Israel, and of those whom We guided and chose. When the verses of the Most Merciful were recited to them, they fell in prostration and weeping", "أُوْلَـٰٓئِكَ ٱلَّذِينَ أَنۡعَمَ ٱللَّهُ عَلَيۡهِم مِّنَ ٱلنَّبِيِّـۧنَ مِن ذُرِّيَّةِ ءَادَمَ وَمِمَّنۡ حَمَلۡنَا مَعَ نُوحٖ وَمِن ذُرِّيَّةِ إِبۡرَٰهِيمَ وَإِسۡرَـٰٓءِيلَ وَمِمَّنۡ هَدَيۡنَا وَٱجۡتَبَيۡنَآۚ إِذَا تُتۡلَىٰ عَلَيۡهِمۡ ءَايَٰتُ ٱلرَّحۡمَٰنِ خَرُّواْۤ سُجَّدٗاۤ وَبُكِيّٗا۩", "Olaika allatheena anAAama Allahu AAalayhim mina alnnabiyyeena min thurriyyati adama wamimman hamalna maAAa noohin wamin thurriyyati ibraheema waisraeela wamimman hadayna waijtabayna itha tutla AAalayhim ayatu alrrahmani kharroo sujjadan wabukiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 59, "But there came after them successors who neglected prayer and pursued desires; so they are going to meet evil", "۞فَخَلَفَ مِنۢ بَعۡدِهِمۡ خَلۡفٌ أَضَاعُواْ ٱلصَّلَوٰةَ وَٱتَّبَعُواْ ٱلشَّهَوَٰتِۖ فَسَوۡفَ يَلۡقَوۡنَ غَيًّا", "Fakhalafa min baAAdihim khalfun adaAAoo alssalata waittabaAAoo alshshahawati fasawfa yalqawna ghayyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 60, "Except those who repent, believe and do righteousness; for those will enter Paradise and will not be wronged at all", "إِلَّا مَن تَابَ وَءَامَنَ وَعَمِلَ صَٰلِحٗا فَأُوْلَـٰٓئِكَ يَدۡخُلُونَ ٱلۡجَنَّةَ وَلَا يُظۡلَمُونَ شَيۡـٔٗا", "Illa man taba waamana waAAamila salihan faolaika yadkhuloona aljannata wala yuthlamoona shayan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 61, "[Therein are] gardens of perpetual residence which the Most Merciful has promised His servants in the unseen. Indeed, His promise has ever been coming", "جَنَّـٰتِ عَدۡنٍ ٱلَّتِي وَعَدَ ٱلرَّحۡمَٰنُ عِبَادَهُۥ بِٱلۡغَيۡبِۚ إِنَّهُۥ كَانَ وَعۡدُهُۥ مَأۡتِيّٗا", "Jannati AAadnin allatee waAAada alrrahmanu AAibadahu bialghaybi innahu kana waAAduhu matiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 62, "They will not hear therein any ill speech - only [greetings of] peace - and they will have their provision therein, morning and afternoon", "لَّا يَسۡمَعُونَ فِيهَا لَغۡوًا إِلَّا سَلَٰمٗاۖ وَلَهُمۡ رِزۡقُهُمۡ فِيهَا بُكۡرَةٗ وَعَشِيّٗا", "La yasmaAAoona feeha laghwan illa salaman walahum rizquhum feeha bukratan waAAashiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 63, "That is Paradise, which We give as inheritance to those of Our servants who were fearing of Allah", "تِلۡكَ ٱلۡجَنَّةُ ٱلَّتِي نُورِثُ مِنۡ عِبَادِنَا مَن كَانَ تَقِيّٗا", "Tilka aljannatu allatee noorithu min AAibadina man kana taqiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 64, "[Gabriel said], ""And we [angels] descend not except by the order of your Lord. To Him belongs that before us and that behind us and what is in between. And never is your Lord forgetful", "وَمَا نَتَنَزَّلُ إِلَّا بِأَمۡرِ رَبِّكَۖ لَهُۥ مَا بَيۡنَ أَيۡدِينَا وَمَا خَلۡفَنَا وَمَا بَيۡنَ ذَٰلِكَۚ وَمَا كَانَ رَبُّكَ نَسِيّٗا", "Wama natanazzalu illa biamri rabbika lahu ma bayna aydeena wama khalfana wama bayna thalika wama kana rabbuka nasiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 65, "Lord of the heavens and the earth and whatever is between them - so worship Him and have patience for His worship. Do you know of any similarity to Him", "رَّبُّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا بَيۡنَهُمَا فَٱعۡبُدۡهُ وَٱصۡطَبِرۡ لِعِبَٰدَتِهِۦۚ هَلۡ تَعۡلَمُ لَهُۥ سَمِيّٗا", "Rabbu alssamawati waalardi wama baynahuma faoAAbudhu waistabir liAAibadatihi hal taAAlamu lahu samiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 66, "And the disbeliever says, ""When I have died, am I going to be brought forth alive", "وَيَقُولُ ٱلۡإِنسَٰنُ أَءِذَا مَا مِتُّ لَسَوۡفَ أُخۡرَجُ حَيًّا", "Wayaqoolu alinsanu aitha ma mittu lasawfa okhraju hayyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 67, "Does man not remember that We created him before, while he was nothing", "أَوَلَا يَذۡكُرُ ٱلۡإِنسَٰنُ أَنَّا خَلَقۡنَٰهُ مِن قَبۡلُ وَلَمۡ يَكُ شَيۡـٔٗا", "Awala yathkuru alinsanu anna khalaqnahu min qablu walam yaku shayan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 68, "So by your Lord, We will surely gather them and the devils; then We will bring them to be present around Hell upon their knees", "فَوَرَبِّكَ لَنَحۡشُرَنَّهُمۡ وَٱلشَّيَٰطِينَ ثُمَّ لَنُحۡضِرَنَّهُمۡ حَوۡلَ جَهَنَّمَ جِثِيّٗا", "Fawarabbika lanahshurannahum waalshshayateena thumma lanuhdirannahum hawla jahannama jithiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 69, "Then We will surely extract from every sect those of them who were worst against the Most Merciful in insolence", "ثُمَّ لَنَنزِعَنَّ مِن كُلِّ شِيعَةٍ أَيُّهُمۡ أَشَدُّ عَلَى ٱلرَّحۡمَٰنِ عِتِيّٗا", "Thumma lananziAAanna min kulli sheeAAatin ayyuhum ashaddu AAala alrrahmani AAitiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 70, "Then, surely it is We who are most knowing of those most worthy of burning therein", "ثُمَّ لَنَحۡنُ أَعۡلَمُ بِٱلَّذِينَ هُمۡ أَوۡلَىٰ بِهَا صِلِيّٗا", "Thumma lanahnu aAAlamu biallatheena hum awla biha siliyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 71, "And there is none of you except he will come to it. This is upon your Lord an inevitability decreed", "وَإِن مِّنكُمۡ إِلَّا وَارِدُهَاۚ كَانَ عَلَىٰ رَبِّكَ حَتۡمٗا مَّقۡضِيّٗا", "Wain minkum illa wariduha kana AAala rabbika hatman maqdiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 72, "Then We will save those who feared Allah and leave the wrongdoers within it, on their knees", "ثُمَّ نُنَجِّي ٱلَّذِينَ ٱتَّقَواْ وَّنَذَرُ ٱلظَّـٰلِمِينَ فِيهَا جِثِيّٗا", "Thumma nunajjee allatheena ittaqaw wanatharu alththalimeena feeha jithiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 73, "And when Our verses are recited to them as clear evidences, those who disbelieve say to those who believe, ""Which of [our] two parties is best in position and best in association", "وَإِذَا تُتۡلَىٰ عَلَيۡهِمۡ ءَايَٰتُنَا بَيِّنَٰتٖ قَالَ ٱلَّذِينَ كَفَرُواْ لِلَّذِينَ ءَامَنُوٓاْ أَيُّ ٱلۡفَرِيقَيۡنِ خَيۡرٞ مَّقَامٗا وَأَحۡسَنُ نَدِيّٗا", "Waitha tutla AAalayhim ayatuna bayyinatin qala allatheena kafaroo lillatheena amanoo ayyu alfareeqayni khayrun maqaman waahsanu nadiyyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 74, "And how many a generation have We destroyed before them who were better in possessions and [outward] appearance", "وَكَمۡ أَهۡلَكۡنَا قَبۡلَهُم مِّن قَرۡنٍ هُمۡ أَحۡسَنُ أَثَٰثٗا وَرِءۡيٗا", "Wakam ahlakna qablahum min qarnin hum ahsanu athathan wariyan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 75, "Say, ""Whoever is in error - let the Most Merciful extend for him an extension [in wealth and time] until, when they see that which they were promised - either punishment [in this world] or the Hour [of resurrection] - they will come to know who is worst in position and weaker in soldiers", "قُلۡ مَن كَانَ فِي ٱلضَّلَٰلَةِ فَلۡيَمۡدُدۡ لَهُ ٱلرَّحۡمَٰنُ مَدًّاۚ حَتَّىٰٓ إِذَا رَأَوۡاْ مَا يُوعَدُونَ إِمَّا ٱلۡعَذَابَ وَإِمَّا ٱلسَّاعَةَ فَسَيَعۡلَمُونَ مَنۡ هُوَ شَرّٞ مَّكَانٗا وَأَضۡعَفُ جُندٗا", "Qul man kana fee alddalalati falyamdud lahu alrrahmanu maddan hatta itha raaw ma yooAAadoona imma alAAathaba waimma alssaAAata fasayaAAlamoona man huwa sharrun makanan waadAAafu jundan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 76, "And Allah increases those who were guided, in guidance, and the enduring good deeds are better to your Lord for reward and better for recourse", "وَيَزِيدُ ٱللَّهُ ٱلَّذِينَ ٱهۡتَدَوۡاْ هُدٗىۗ وَٱلۡبَٰقِيَٰتُ ٱلصَّـٰلِحَٰتُ خَيۡرٌ عِندَ رَبِّكَ ثَوَابٗا وَخَيۡرٞ مَّرَدًّا", "Wayazeedu Allahu allatheena ihtadaw hudan waalbaqiyatu alssalihatu khayrun AAinda rabbika thawaban wakhayrun maraddan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 77, "Then, have you seen he who disbelieved in Our verses and said, ""I will surely be given wealth and children [in the next life]", "أَفَرَءَيۡتَ ٱلَّذِي كَفَرَ بِـَٔايَٰتِنَا وَقَالَ لَأُوتَيَنَّ مَالٗا وَوَلَدًا", "Afaraayta allathee kafara biayatina waqala laootayanna malan wawaladan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 78, "Has he looked into the unseen, or has he taken from the Most Merciful a promise", "أَطَّلَعَ ٱلۡغَيۡبَ أَمِ ٱتَّخَذَ عِندَ ٱلرَّحۡمَٰنِ عَهۡدٗا", "AttalaAAa alghayba ami ittakhatha AAinda alrrahmani AAahdan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 79, "No! We will record what he says and extend for him from the punishment extensively", "كَلَّاۚ سَنَكۡتُبُ مَا يَقُولُ وَنَمُدُّ لَهُۥ مِنَ ٱلۡعَذَابِ مَدّٗا", "Kalla sanaktubu ma yaqoolu wanamuddu lahu mina alAAathabi maddan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 80, "And We will inherit him [in] what he mentions, and he will come to Us alone", "وَنَرِثُهُۥ مَا يَقُولُ وَيَأۡتِينَا فَرۡدٗا", "Wanarithuhu ma yaqoolu wayateena fardan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 81, "And they have taken besides Allah [false] deities that they would be for them [a source of] honor", "وَٱتَّخَذُواْ مِن دُونِ ٱللَّهِ ءَالِهَةٗ لِّيَكُونُواْ لَهُمۡ عِزّٗا", "Waittakhathoo min dooni Allahi alihatan liyakoonoo lahum AAizzan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 82, "No! Those ""gods"" will deny their worship of them and will be against them opponents [on the Day of Judgement]", "كَلَّاۚ سَيَكۡفُرُونَ بِعِبَادَتِهِمۡ وَيَكُونُونَ عَلَيۡهِمۡ ضِدًّا", "Kalla sayakfuroona biAAibadatihim wayakoonoona AAalayhim diddan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 83, "Do you not see that We have sent the devils upon the disbelievers, inciting them to [evil] with [constant] incitement", "أَلَمۡ تَرَ أَنَّآ أَرۡسَلۡنَا ٱلشَّيَٰطِينَ عَلَى ٱلۡكَٰفِرِينَ تَؤُزُّهُمۡ أَزّٗا", "Alam tara anna arsalna alshshayateena AAala alkafireena taozzuhum azzan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 84, "So be not impatient over them. We only count out to them a [limited] number", "فَلَا تَعۡجَلۡ عَلَيۡهِمۡۖ إِنَّمَا نَعُدُّ لَهُمۡ عَدّٗا", "Fala taAAjal AAalayhim innama naAAuddu lahum AAaddan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 85, "On the Day We will gather the righteous to the Most Merciful as a delegation", "يَوۡمَ نَحۡشُرُ ٱلۡمُتَّقِينَ إِلَى ٱلرَّحۡمَٰنِ وَفۡدٗا", "Yawma nahshuru almuttaqeena ila alrrahmani wafdan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 86, "And will drive the criminals to Hell in thirst", "وَنَسُوقُ ٱلۡمُجۡرِمِينَ إِلَىٰ جَهَنَّمَ وِرۡدٗا", "Wanasooqu almujrimeena ila jahannama wirdan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 87, "None will have [power of] intercession except he who had taken from the Most Merciful a covenant", "لَّا يَمۡلِكُونَ ٱلشَّفَٰعَةَ إِلَّا مَنِ ٱتَّخَذَ عِندَ ٱلرَّحۡمَٰنِ عَهۡدٗا", "La yamlikoona alshshafaAAata illa mani ittakhatha AAinda alrrahmani AAahdan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 88, "And they say, ""The Most Merciful has taken [for Himself] a son", "وَقَالُواْ ٱتَّخَذَ ٱلرَّحۡمَٰنُ وَلَدٗا", "Waqaloo ittakhatha alrrahmanu waladan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 89, "You have done an atrocious thing", "لَّقَدۡ جِئۡتُمۡ شَيۡـًٔا إِدّٗا", "Laqad jitum shayan iddan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 90, "The heavens almost rupture therefrom and the earth splits open and the mountains collapse in devastation", "تَكَادُ ٱلسَّمَٰوَٰتُ يَتَفَطَّرۡنَ مِنۡهُ وَتَنشَقُّ ٱلۡأَرۡضُ وَتَخِرُّ ٱلۡجِبَالُ هَدًّا", "Takadu alssamawatu yatafattarna minhu watanshaqqu alardu watakhirru aljibalu haddan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 91, "That they attribute to the Most Merciful a son", "أَن دَعَوۡاْ لِلرَّحۡمَٰنِ وَلَدٗا", "An daAAaw lilrrahmani waladan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 92, "And it is not appropriate for the Most Merciful that He should take a son", "وَمَا يَنۢبَغِي لِلرَّحۡمَٰنِ أَن يَتَّخِذَ وَلَدًا", "Wama yanbaghee lilrrahmani an yattakhitha waladan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 93, "There is no one in the heavens and earth but that he comes to the Most Merciful as a servant", "إِن كُلُّ مَن فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ إِلَّآ ءَاتِي ٱلرَّحۡمَٰنِ عَبۡدٗا", "In kullu man fee alssamawati waalardi illa atee alrrahmani AAabdan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 94, "He has enumerated them and counted them a [full] counting", "لَّقَدۡ أَحۡصَىٰهُمۡ وَعَدَّهُمۡ عَدّٗا", "Laqad ahsahum waAAaddahum AAaddan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 95, "And all of them are coming to Him on the Day of Resurrection alone", "وَكُلُّهُمۡ ءَاتِيهِ يَوۡمَ ٱلۡقِيَٰمَةِ فَرۡدًا", "Wakulluhum ateehi yawma alqiyamati fardan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 96, "Indeed, those who have believed and done righteous deeds - the Most Merciful will appoint for them affection", "إِنَّ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ سَيَجۡعَلُ لَهُمُ ٱلرَّحۡمَٰنُ وُدّٗا", "Inna allatheena amanoo waAAamiloo alssalihati sayajAAalu lahumu alrrahmanu wuddan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 97, "So, [O Muhammad], We have only made Qur'an easy in the Arabic language that you may give good tidings thereby to the righteous and warn thereby a hostile people", "فَإِنَّمَا يَسَّرۡنَٰهُ بِلِسَانِكَ لِتُبَشِّرَ بِهِ ٱلۡمُتَّقِينَ وَتُنذِرَ بِهِۦ قَوۡمٗا لُّدّٗا", "Fainnama yassarnahu bilisanika litubashshira bihi almuttaqeena watunthira bihi qawman luddan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (19, 98, "And how many have We destroyed before them of generations? Do you perceive of them anyone or hear from them a sound", "وَكَمۡ أَهۡلَكۡنَا قَبۡلَهُم مِّن قَرۡنٍ هَلۡ تُحِسُّ مِنۡهُم مِّنۡ أَحَدٍ أَوۡ تَسۡمَعُ لَهُمۡ رِكۡزَۢا", "Wakam ahlakna qablahum min qarnin hal tuhissu minhum min ahadin aw tasmaAAu lahum rikzan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 1, "Ta, Ha", "طه", "Taha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 2, "We have not sent down to you the Qur'an that you be distressed", "مَآ أَنزَلۡنَا عَلَيۡكَ ٱلۡقُرۡءَانَ لِتَشۡقَىٰٓ", "Ma anzalna AAalayka alqurana litashqa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 3, "But only as a reminder for those who fear [Allah]", "إِلَّا تَذۡكِرَةٗ لِّمَن يَخۡشَىٰ", "Illa tathkiratan liman yakhsha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 4, "A revelation from He who created the earth and highest heavens", "تَنزِيلٗا مِّمَّنۡ خَلَقَ ٱلۡأَرۡضَ وَٱلسَّمَٰوَٰتِ ٱلۡعُلَى", "Tanzeelan mimman khalaqa alarda waalssamawati alAAula");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 5, "The Most Merciful [who is] above the Throne established", "ٱلرَّحۡمَٰنُ عَلَى ٱلۡعَرۡشِ ٱسۡتَوَىٰ", "Alrrahmanu AAala alAAarshi istawa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 6, "To Him belongs what is in the heavens and what is on the earth and what is between them and what is under the soil", "لَهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِ وَمَا بَيۡنَهُمَا وَمَا تَحۡتَ ٱلثَّرَىٰ", "Lahu ma fee alssamawati wama fee alardi wama baynahuma wama tahta alththara");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 7, "And if you speak aloud - then indeed, He knows the secret and what is [even] more hidden", "وَإِن تَجۡهَرۡ بِٱلۡقَوۡلِ فَإِنَّهُۥ يَعۡلَمُ ٱلسِّرَّ وَأَخۡفَى", "Wain tajhar bialqawli fainnahu yaAAlamu alssirra waakhfa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 8, "Allah - there is no deity except Him. To Him belong the best names", "ٱللَّهُ لَآ إِلَٰهَ إِلَّا هُوَۖ لَهُ ٱلۡأَسۡمَآءُ ٱلۡحُسۡنَىٰ", "Allahu la ilaha illa huwa lahu alasmao alhusna");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 9, "And has the story of Moses reached you", "وَهَلۡ أَتَىٰكَ حَدِيثُ مُوسَىٰٓ", "Wahal ataka hadeethu moosa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 10, "When he saw a fire and said to his family, ""Stay here; indeed, I have perceived a fire; perhaps I can bring you a torch or find at the fire some guidance", "إِذۡ رَءَا نَارٗا فَقَالَ لِأَهۡلِهِ ٱمۡكُثُوٓاْ إِنِّيٓ ءَانَسۡتُ نَارٗا لَّعَلِّيٓ ءَاتِيكُم مِّنۡهَا بِقَبَسٍ أَوۡ أَجِدُ عَلَى ٱلنَّارِ هُدٗى", "Ith raa naran faqala liahlihi omkuthoo innee anastu naran laAAallee ateekum minha biqabasin aw ajidu AAala alnnari hudan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 11, "And when he came to it, he was called, ""O Moses", "فَلَمَّآ أَتَىٰهَا نُودِيَ يَٰمُوسَىٰٓ", "Falamma ataha noodiya ya moosa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 12, "Indeed, I am your Lord, so remove your sandals. Indeed, you are in the sacred valley of Tuwa", "إِنِّيٓ أَنَا۠ رَبُّكَ فَٱخۡلَعۡ نَعۡلَيۡكَ إِنَّكَ بِٱلۡوَادِ ٱلۡمُقَدَّسِ طُوٗى", "Innee ana rabbuka faikhlaAA naAAlayka innaka bialwadi almuqaddasi tuwan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 13, "And I have chosen you, so listen to what is revealed [to you]", "وَأَنَا ٱخۡتَرۡتُكَ فَٱسۡتَمِعۡ لِمَا يُوحَىٰٓ", "Waana ikhtartuka faistamiAA lima yooha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 14, "Indeed, I am Allah. There is no deity except Me, so worship Me and establish prayer for My remembrance", "إِنَّنِيٓ أَنَا ٱللَّهُ لَآ إِلَٰهَ إِلَّآ أَنَا۠ فَٱعۡبُدۡنِي وَأَقِمِ ٱلصَّلَوٰةَ لِذِكۡرِيٓ", "Innanee ana Allahu la ilaha illa ana faoAAbudnee waaqimi alssalata lithikree");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 15, "Indeed, the Hour is coming - I almost conceal it - so that every soul may be recompensed according to that for which it strives", "إِنَّ ٱلسَّاعَةَ ءَاتِيَةٌ أَكَادُ أُخۡفِيهَا لِتُجۡزَىٰ كُلُّ نَفۡسِۭ بِمَا تَسۡعَىٰ", "Inna alssaAAata atiyatun akadu okhfeeha litujza kullu nafsin bima tasAAa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 16, "So do not let one avert you from it who does not believe in it and follows his desire, for you [then] would perish", "فَلَا يَصُدَّنَّكَ عَنۡهَا مَن لَّا يُؤۡمِنُ بِهَا وَٱتَّبَعَ هَوَىٰهُ فَتَرۡدَىٰ", "Fala yasuddannaka AAanha man la yuminu biha waittabaAAa hawahu fatarda");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 17, "And what is that in your right hand, O Moses", "وَمَا تِلۡكَ بِيَمِينِكَ يَٰمُوسَىٰ", "Wama tilka biyameenika ya moosa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 18, "He said, ""It is my staff; I lean upon it, and I bring down leaves for my sheep and I have therein other uses", "قَالَ هِيَ عَصَايَ أَتَوَكَّؤُاْ عَلَيۡهَا وَأَهُشُّ بِهَا عَلَىٰ غَنَمِي وَلِيَ فِيهَا مَـَٔارِبُ أُخۡرَىٰ", "Qala hiya AAasaya atawakkao AAalayha waahushshu biha AAala ghanamee waliya feeha maaribu okhra");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 19, "[Allah] said, ""Throw it down, O Moses", "قَالَ أَلۡقِهَا يَٰمُوسَىٰ", "Qala alqiha ya moosa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 20, "So he threw it down, and thereupon it was a snake, moving swiftly", "فَأَلۡقَىٰهَا فَإِذَا هِيَ حَيَّةٞ تَسۡعَىٰ", "Faalqaha faitha hiya hayyatun tasAAa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 21, "[Allah] said, ""Seize it and fear not; We will return it to its former condition", "قَالَ خُذۡهَا وَلَا تَخَفۡۖ سَنُعِيدُهَا سِيرَتَهَا ٱلۡأُولَىٰ", "Qala khuthha wala takhaf sanuAAeeduha seerataha aloola");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 22, "And draw in your hand to your side; it will come out white without disease - another sign", "وَٱضۡمُمۡ يَدَكَ إِلَىٰ جَنَاحِكَ تَخۡرُجۡ بَيۡضَآءَ مِنۡ غَيۡرِ سُوٓءٍ ءَايَةً أُخۡرَىٰ", "Waodmum yadaka ila janahika takhruj baydaa min ghayri sooin ayatan okhra");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 23, "That We may show you [some] of Our greater signs", "لِنُرِيَكَ مِنۡ ءَايَٰتِنَا ٱلۡكُبۡرَى", "Linuriyaka min ayatina alkubra");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 24, "Go to Pharaoh. Indeed, he has transgressed", "ٱذۡهَبۡ إِلَىٰ فِرۡعَوۡنَ إِنَّهُۥ طَغَىٰ", "Ithhab ila firAAawna innahu tagha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 25, "[Moses] said, ""My Lord, expand for me my breast [with assurance]", "قَالَ رَبِّ ٱشۡرَحۡ لِي صَدۡرِي", "Qala rabbi ishrah lee sadree");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 26, "And ease for me my task", "وَيَسِّرۡ لِيٓ أَمۡرِي", "Wayassir lee amree");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 27, "And untie the knot from my tongue", "وَٱحۡلُلۡ عُقۡدَةٗ مِّن لِّسَانِي", "Waohlul AAuqdatan min lisanee");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 28, "That they may understand my speech", "يَفۡقَهُواْ قَوۡلِي", "Yafqahoo qawlee");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 29, "And appoint for me a minister from my family", "وَٱجۡعَل لِّي وَزِيرٗا مِّنۡ أَهۡلِي", "WaijAAal lee wazeeran min ahlee");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 30, "Aaron, my brother", "هَٰرُونَ أَخِي", "Haroona akhee");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 31, "Increase through him my strength", "ٱشۡدُدۡ بِهِۦٓ أَزۡرِي", "Oshdud bihi azree");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 32, "And let him share my task", "وَأَشۡرِكۡهُ فِيٓ أَمۡرِي", "Waashrikhu fee amree");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 33, "That we may exalt You much", "كَيۡ نُسَبِّحَكَ كَثِيرٗا", "Kay nusabbihaka katheeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 34, "And remember You much", "وَنَذۡكُرَكَ كَثِيرًا", "Wanathkuraka katheeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 35, "Indeed, You are of us ever Seeing", "إِنَّكَ كُنتَ بِنَا بَصِيرٗا", "Innaka kunta bina baseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 36, "[Allah] said, ""You have been granted your request, O Moses", "قَالَ قَدۡ أُوتِيتَ سُؤۡلَكَ يَٰمُوسَىٰ", "Qala qad ooteeta sulaka ya moosa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 37, "And We had already conferred favor upon you another time", "وَلَقَدۡ مَنَنَّا عَلَيۡكَ مَرَّةً أُخۡرَىٰٓ", "Walaqad mananna AAalayka marratan okhra");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 38, "When We inspired to your mother what We inspired", "إِذۡ أَوۡحَيۡنَآ إِلَىٰٓ أُمِّكَ مَا يُوحَىٰٓ", "Ith awhayna ila ommika ma yooha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 39, "[Saying], 'Cast him into the chest and cast it into the river, and the river will throw it onto the bank; there will take him an enemy to Me and an enemy to him.' And I bestowed upon you love from Me that you would be brought up under My eye", "أَنِ ٱقۡذِفِيهِ فِي ٱلتَّابُوتِ فَٱقۡذِفِيهِ فِي ٱلۡيَمِّ فَلۡيُلۡقِهِ ٱلۡيَمُّ بِٱلسَّاحِلِ يَأۡخُذۡهُ عَدُوّٞ لِّي وَعَدُوّٞ لَّهُۥۚ وَأَلۡقَيۡتُ عَلَيۡكَ مَحَبَّةٗ مِّنِّي وَلِتُصۡنَعَ عَلَىٰ عَيۡنِيٓ", "Ani iqthifeehi fee alttabooti faiqthifeehi fee alyammi falyulqihi alyammu bialssahili yakhuthhu AAaduwwun lee waAAaduwwun lahu waalqaytu AAalayka mahabbatan minnee walitusnaAAa AAala AAaynee");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 40, "[And We favored you] when your sister went and said, 'Shall I direct you to someone who will be responsible for him?' So We restored you to your mother that she might be content and not grieve. And you killed someone, but We saved you from retaliation and tried you with a [severe] trial. And you remained [some] years among the people of Madyan. Then you came [here] at the decreed time, O Moses", "إِذۡ تَمۡشِيٓ أُخۡتُكَ فَتَقُولُ هَلۡ أَدُلُّكُمۡ عَلَىٰ مَن يَكۡفُلُهُۥۖ فَرَجَعۡنَٰكَ إِلَىٰٓ أُمِّكَ كَيۡ تَقَرَّ عَيۡنُهَا وَلَا تَحۡزَنَۚ وَقَتَلۡتَ نَفۡسٗا فَنَجَّيۡنَٰكَ مِنَ ٱلۡغَمِّ وَفَتَنَّـٰكَ فُتُونٗاۚ فَلَبِثۡتَ سِنِينَ فِيٓ أَهۡلِ مَدۡيَنَ ثُمَّ جِئۡتَ عَلَىٰ قَدَرٖ يَٰمُوسَىٰ", "Ith tamshee okhtuka fataqoolu hal adullukum AAala man yakfuluhu farajaAAnaka ila ommika kay taqarra AAaynuha wala tahzana waqatalta nafsan fanajjaynaka mina alghammi wafatannaka futoonan falabithta sineena fee ahli madyana thumma jita AAala qadarin ya moosa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 41, "And I produced you for Myself", "وَٱصۡطَنَعۡتُكَ لِنَفۡسِي", "WaistanaAAtuka linafsee");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 42, "Go, you and your brother, with My signs and do not slacken in My remembrance", "ٱذۡهَبۡ أَنتَ وَأَخُوكَ بِـَٔايَٰتِي وَلَا تَنِيَا فِي ذِكۡرِي", "Ithhab anta waakhooka biayatee wala taniya fee thikree");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 43, "Go, both of you, to Pharaoh. Indeed, he has transgressed", "ٱذۡهَبَآ إِلَىٰ فِرۡعَوۡنَ إِنَّهُۥ طَغَىٰ", "Ithhaba ila firAAawna innahu tagha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 44, "And speak to him with gentle speech that perhaps he may be reminded or fear [Allah]", "فَقُولَا لَهُۥ قَوۡلٗا لَّيِّنٗا لَّعَلَّهُۥ يَتَذَكَّرُ أَوۡ يَخۡشَىٰ", "Faqoola lahu qawlan layyinan laAAallahu yatathakkaru aw yakhsha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 45, "They said, ""Our Lord, indeed we are afraid that he will hasten [punishment] against us or that he will transgress", "قَالَا رَبَّنَآ إِنَّنَا نَخَافُ أَن يَفۡرُطَ عَلَيۡنَآ أَوۡ أَن يَطۡغَىٰ", "Qala rabbana innana nakhafu an yafruta AAalayna aw an yatgha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 46, "[Allah] said, ""Fear not. Indeed, I am with you both; I hear and I see", "قَالَ لَا تَخَافَآۖ إِنَّنِي مَعَكُمَآ أَسۡمَعُ وَأَرَىٰ", "Qala la takhafa innanee maAAakuma asmaAAu waara");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 47, "So go to him and say, 'Indeed, we are messengers of your Lord, so send with us the Children of Israel and do not torment them. We have come to you with a sign from your Lord. And peace will be upon he who follows the guidance", "فَأۡتِيَاهُ فَقُولَآ إِنَّا رَسُولَا رَبِّكَ فَأَرۡسِلۡ مَعَنَا بَنِيٓ إِسۡرَـٰٓءِيلَ وَلَا تُعَذِّبۡهُمۡۖ قَدۡ جِئۡنَٰكَ بِـَٔايَةٖ مِّن رَّبِّكَۖ وَٱلسَّلَٰمُ عَلَىٰ مَنِ ٱتَّبَعَ ٱلۡهُدَىٰٓ", "Fatiyahu faqoola inna rasoola rabbika faarsil maAAana banee israeela wala tuAAaththibhum qad jinaka biayatin min rabbika waalssalamu AAala mani ittabaAAa alhuda");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 48, "Indeed, it has been revealed to us that the punishment will be upon whoever denies and turns away", "إِنَّا قَدۡ أُوحِيَ إِلَيۡنَآ أَنَّ ٱلۡعَذَابَ عَلَىٰ مَن كَذَّبَ وَتَوَلَّىٰ", "Inna qad oohiya ilayna anna alAAathaba AAala man kaththaba watawalla");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 49, "[Pharaoh] said, ""So who is the Lord of you two, O Moses", "قَالَ فَمَن رَّبُّكُمَا يَٰمُوسَىٰ", "Qala faman rabbukuma ya moosa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 50, "He said, ""Our Lord is He who gave each thing its form and then guided [it]", "قَالَ رَبُّنَا ٱلَّذِيٓ أَعۡطَىٰ كُلَّ شَيۡءٍ خَلۡقَهُۥ ثُمَّ هَدَىٰ", "Qala rabbuna allathee aAAta kulla shayin khalqahu thumma hada");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 51, "[Pharaoh] said, ""Then what is the case of the former generations", "قَالَ فَمَا بَالُ ٱلۡقُرُونِ ٱلۡأُولَىٰ", "Qala fama balu alqurooni aloola");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 52, "[Moses] said, ""The knowledge thereof is with my Lord in a record. My Lord neither errs nor forgets", "قَالَ عِلۡمُهَا عِندَ رَبِّي فِي كِتَٰبٖۖ لَّا يَضِلُّ رَبِّي وَلَا يَنسَى", "Qala AAilmuha AAinda rabbee fee kitabin la yadillu rabbee wala yansa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 53, "[It is He] who has made for you the earth as a bed [spread out] and inserted therein for you roadways and sent down from the sky, rain and produced thereby categories of various plants", "ٱلَّذِي جَعَلَ لَكُمُ ٱلۡأَرۡضَ مَهۡدٗا وَسَلَكَ لَكُمۡ فِيهَا سُبُلٗا وَأَنزَلَ مِنَ ٱلسَّمَآءِ مَآءٗ فَأَخۡرَجۡنَا بِهِۦٓ أَزۡوَٰجٗا مِّن نَّبَاتٖ شَتَّىٰ", "Allathee jaAAala lakumu alarda mahdan wasalaka lakum feeha subulan waanzala mina alssamai maan faakhrajna bihi azwajan min nabatin shatta");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 54, "Eat [therefrom] and pasture your livestock. Indeed, in that are signs for those of intelligence", "كُلُواْ وَٱرۡعَوۡاْ أَنۡعَٰمَكُمۡۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّأُوْلِي ٱلنُّهَىٰ", "Kuloo wairAAaw anAAamakum inna fee thalika laayatin liolee alnnuha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 55, "From the earth We created you, and into it We will return you, and from it We will extract you another time", "۞مِنۡهَا خَلَقۡنَٰكُمۡ وَفِيهَا نُعِيدُكُمۡ وَمِنۡهَا نُخۡرِجُكُمۡ تَارَةً أُخۡرَىٰ", "Minha khalaqnakum wafeeha nuAAeedukum waminha nukhrijukum taratan okhra");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 56, "And We certainly showed Pharaoh Our signs - all of them - but he denied and refused", "وَلَقَدۡ أَرَيۡنَٰهُ ءَايَٰتِنَا كُلَّهَا فَكَذَّبَ وَأَبَىٰ", "Walaqad araynahu ayatina kullaha fakaththaba waaba");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 57, "He said, ""Have you come to us to drive us out of our land with your magic, O Moses", "قَالَ أَجِئۡتَنَا لِتُخۡرِجَنَا مِنۡ أَرۡضِنَا بِسِحۡرِكَ يَٰمُوسَىٰ", "Qala ajitana litukhrijana min ardina bisihrika ya moosa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 58, "Then we will surely bring you magic like it, so make between us and you an appointment, which we will not fail to keep and neither will you, in a place assigned", "فَلَنَأۡتِيَنَّكَ بِسِحۡرٖ مِّثۡلِهِۦ فَٱجۡعَلۡ بَيۡنَنَا وَبَيۡنَكَ مَوۡعِدٗا لَّا نُخۡلِفُهُۥ نَحۡنُ وَلَآ أَنتَ مَكَانٗا سُوٗى", "Falanatiyannaka bisihrin mithlihi faijAAal baynana wabaynaka mawAAidan la nukhlifuhu nahnu wala anta makanan suwan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 59, "[Moses] said, ""Your appointment is on the day of the festival when the people assemble at mid-morning", "قَالَ مَوۡعِدُكُمۡ يَوۡمُ ٱلزِّينَةِ وَأَن يُحۡشَرَ ٱلنَّاسُ ضُحٗى", "Qala mawAAidukum yawmu alzzeenati waan yuhshara alnnasu duhan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 60, "So Pharaoh went away, put together his plan, and then came [to Moses]", "فَتَوَلَّىٰ فِرۡعَوۡنُ فَجَمَعَ كَيۡدَهُۥ ثُمَّ أَتَىٰ", "Fatawalla firAAawnu fajamaAAa kaydahu thumma ata");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 61, "Moses said to the magicians summoned by Pharaoh, ""Woe to you! Do not invent a lie against Allah or He will exterminate you with a punishment; and he has failed who invents [such falsehood]", "قَالَ لَهُم مُّوسَىٰ وَيۡلَكُمۡ لَا تَفۡتَرُواْ عَلَى ٱللَّهِ كَذِبٗا فَيُسۡحِتَكُم بِعَذَابٖۖ وَقَدۡ خَابَ مَنِ ٱفۡتَرَىٰ", "Qala lahum moosa waylakum la taftaroo AAala Allahi kathiban fayushitakum biAAathabin waqad khaba mani iftara");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 62, "So they disputed over their affair among themselves and concealed their private conversation", "فَتَنَٰزَعُوٓاْ أَمۡرَهُم بَيۡنَهُمۡ وَأَسَرُّواْ ٱلنَّجۡوَىٰ", "FatanazaAAoo amrahum baynahum waasarroo alnnajwa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 63, "They said, ""Indeed, these are two magicians who want to drive you out of your land with their magic and do away with your most exemplary way", "قَالُوٓاْ إِنۡ هَٰذَٰنِ لَسَٰحِرَٰنِ يُرِيدَانِ أَن يُخۡرِجَاكُم مِّنۡ أَرۡضِكُم بِسِحۡرِهِمَا وَيَذۡهَبَا بِطَرِيقَتِكُمُ ٱلۡمُثۡلَىٰ", "Qaloo in hathani lasahirani yureedani an yukhrijakum min ardikum bisihrihima wayathhaba bitareeqatikumu almuthla");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 64, "So resolve upon your plan and then come [forward] in line. And he has succeeded today who overcomes", "فَأَجۡمِعُواْ كَيۡدَكُمۡ ثُمَّ ٱئۡتُواْ صَفّٗاۚ وَقَدۡ أَفۡلَحَ ٱلۡيَوۡمَ مَنِ ٱسۡتَعۡلَىٰ", "FaajmiAAoo kaydakum thumma itoo saffan waqad aflaha alyawma mani istaAAla");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 65, "They said, ""O Moses, either you throw or we will be the first to throw", "قَالُواْ يَٰمُوسَىٰٓ إِمَّآ أَن تُلۡقِيَ وَإِمَّآ أَن نَّكُونَ أَوَّلَ مَنۡ أَلۡقَىٰ", "Qaloo ya moosa imma an tulqiya waimma an nakoona awwala man alqa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 66, "He said, ""Rather, you throw."" And suddenly their ropes and staffs seemed to him from their magic that they were moving [like snakes]", "قَالَ بَلۡ أَلۡقُواْۖ فَإِذَا حِبَالُهُمۡ وَعِصِيُّهُمۡ يُخَيَّلُ إِلَيۡهِ مِن سِحۡرِهِمۡ أَنَّهَا تَسۡعَىٰ", "Qala bal alqoo faitha hibaluhum waAAisiyyuhum yukhayyalu ilayhi min sihrihim annaha tasAAa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 67, "And he sensed within himself apprehension, did Moses", "فَأَوۡجَسَ فِي نَفۡسِهِۦ خِيفَةٗ مُّوسَىٰ", "Faawjasa fee nafsihi kheefatan moosa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 68, "Allah said, ""Fear not. Indeed, it is you who are superior", "قُلۡنَا لَا تَخَفۡ إِنَّكَ أَنتَ ٱلۡأَعۡلَىٰ", "Qulna la takhaf innaka anta alaAAla");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 69, "And throw what is in your right hand; it will swallow up what they have crafted. What they have crafted is but the trick of a magician, and the magician will not succeed wherever he is", "وَأَلۡقِ مَا فِي يَمِينِكَ تَلۡقَفۡ مَا صَنَعُوٓاْۖ إِنَّمَا صَنَعُواْ كَيۡدُ سَٰحِرٖۖ وَلَا يُفۡلِحُ ٱلسَّاحِرُ حَيۡثُ أَتَىٰ", "Waalqi ma fee yameenika talqaf ma sanaAAoo innama sanaAAoo kaydu sahirin wala yuflihu alssahiru haythu ata");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 70, "So the magicians fell down in prostration. They said, ""We have believed in the Lord of Aaron and Moses", "فَأُلۡقِيَ ٱلسَّحَرَةُ سُجَّدٗا قَالُوٓاْ ءَامَنَّا بِرَبِّ هَٰرُونَ وَمُوسَىٰ", "Faolqiya alssaharatu sujjadan qaloo amanna birabbi haroona wamoosa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 71, "[Pharaoh] said, ""You believed him before I gave you permission. Indeed, he is your leader who has taught you magic. So I will surely cut off your hands and your feet on opposite sides, and I will crucify you on the trunks of palm trees, and you will surely know which of us is more severe in [giving] punishment and more enduring", "قَالَ ءَامَنتُمۡ لَهُۥ قَبۡلَ أَنۡ ءَاذَنَ لَكُمۡۖ إِنَّهُۥ لَكَبِيرُكُمُ ٱلَّذِي عَلَّمَكُمُ ٱلسِّحۡرَۖ فَلَأُقَطِّعَنَّ أَيۡدِيَكُمۡ وَأَرۡجُلَكُم مِّنۡ خِلَٰفٖ وَلَأُصَلِّبَنَّكُمۡ فِي جُذُوعِ ٱلنَّخۡلِ وَلَتَعۡلَمُنَّ أَيُّنَآ أَشَدُّ عَذَابٗا وَأَبۡقَىٰ", "Qala amantum lahu qabla an athana lakum innahu lakabeerukumu allathee AAallamakumu alssihra falaoqattiAAanna aydiyakum waarjulakum min khilafin walaosallibannakum fee juthooAAi alnnakhli walataAAlamunna ayyuna ashaddu AAathaban waabqa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 72, "They said, ""Never will we prefer you over what has come to us of clear proofs and [over] He who created us. So decree whatever you are to decree. You can only decree for this worldly life", "قَالُواْ لَن نُّؤۡثِرَكَ عَلَىٰ مَا جَآءَنَا مِنَ ٱلۡبَيِّنَٰتِ وَٱلَّذِي فَطَرَنَاۖ فَٱقۡضِ مَآ أَنتَ قَاضٍۖ إِنَّمَا تَقۡضِي هَٰذِهِ ٱلۡحَيَوٰةَ ٱلدُّنۡيَآ", "Qaloo lan nuthiraka AAala ma jaana mina albayyinati waallathee fatarana faiqdi ma anta qadin innama taqdee hathihi alhayata alddunya");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 73, "Indeed, we have believed in our Lord that He may forgive us our sins and what you compelled us [to do] of magic. And Allah is better and more enduring", "إِنَّآ ءَامَنَّا بِرَبِّنَا لِيَغۡفِرَ لَنَا خَطَٰيَٰنَا وَمَآ أَكۡرَهۡتَنَا عَلَيۡهِ مِنَ ٱلسِّحۡرِۗ وَٱللَّهُ خَيۡرٞ وَأَبۡقَىٰٓ", "Inna amanna birabbina liyaghfira lana khatayana wama akrahtana AAalayhi mina alssihri waAllahu khayrun waabqa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 74, "Indeed, whoever comes to his Lord as a criminal - indeed, for him is Hell; he will neither die therein nor live", "إِنَّهُۥ مَن يَأۡتِ رَبَّهُۥ مُجۡرِمٗا فَإِنَّ لَهُۥ جَهَنَّمَ لَا يَمُوتُ فِيهَا وَلَا يَحۡيَىٰ", "Innahu man yati rabbahu mujriman fainna lahu jahannama la yamootu feeha wala yahya");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 75, "But whoever comes to Him as a believer having done righteous deeds - for those will be the highest degrees [in position]", "وَمَن يَأۡتِهِۦ مُؤۡمِنٗا قَدۡ عَمِلَ ٱلصَّـٰلِحَٰتِ فَأُوْلَـٰٓئِكَ لَهُمُ ٱلدَّرَجَٰتُ ٱلۡعُلَىٰ", "Waman yatihi muminan qad AAamila alssalihati faolaika lahumu alddarajatu alAAula");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 76, "Gardens of perpetual residence beneath which rivers flow, wherein they abide eternally. And that is the reward of one who purifies himself", "جَنَّـٰتُ عَدۡنٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَاۚ وَذَٰلِكَ جَزَآءُ مَن تَزَكَّىٰ", "Jannatu AAadnin tajree min tahtiha alanharu khalideena feeha wathalika jazao man tazakka");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 77, "And We had inspired to Moses, ""Travel by night with My servants and strike for them a dry path through the sea; you will not fear being overtaken [by Pharaoh] nor be afraid [of drowning]", "وَلَقَدۡ أَوۡحَيۡنَآ إِلَىٰ مُوسَىٰٓ أَنۡ أَسۡرِ بِعِبَادِي فَٱضۡرِبۡ لَهُمۡ طَرِيقٗا فِي ٱلۡبَحۡرِ يَبَسٗا لَّا تَخَٰفُ دَرَكٗا وَلَا تَخۡشَىٰ", "Walaqad awhayna ila moosa an asri biAAibadee faidrib lahum tareeqan fee albahri yabasan la takhafu darakan wala takhsha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 78, "So Pharaoh pursued them with his soldiers, and there covered them from the sea that which covered them", "فَأَتۡبَعَهُمۡ فِرۡعَوۡنُ بِجُنُودِهِۦ فَغَشِيَهُم مِّنَ ٱلۡيَمِّ مَا غَشِيَهُمۡ", "FaatbaAAahum firAAawnu bijunoodihi faghashiyahum mina alyammi ma ghashiyahum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 79, "And Pharaoh led his people astray and did not guide [them]", "وَأَضَلَّ فِرۡعَوۡنُ قَوۡمَهُۥ وَمَا هَدَىٰ", "Waadalla firAAawnu qawmahu wama hada");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 80, "O Children of Israel, We delivered you from your enemy, and We made an appointment with you at the right side of the mount, and We sent down to you manna and quails", "يَٰبَنِيٓ إِسۡرَـٰٓءِيلَ قَدۡ أَنجَيۡنَٰكُم مِّنۡ عَدُوِّكُمۡ وَوَٰعَدۡنَٰكُمۡ جَانِبَ ٱلطُّورِ ٱلۡأَيۡمَنَ وَنَزَّلۡنَا عَلَيۡكُمُ ٱلۡمَنَّ وَٱلسَّلۡوَىٰ", "Ya banee israeela qad anjaynakum min AAaduwwikum wawaAAadnakum janiba alttoori alaymana wanazzalna AAalaykumu almanna waalssalwa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 81, "[Saying], ""Eat from the good things with which We have provided you and do not transgress [or oppress others] therein, lest My anger should descend upon you. And he upon whom My anger descends has certainly fallen", "كُلُواْ مِن طَيِّبَٰتِ مَا رَزَقۡنَٰكُمۡ وَلَا تَطۡغَوۡاْ فِيهِ فَيَحِلَّ عَلَيۡكُمۡ غَضَبِيۖ وَمَن يَحۡلِلۡ عَلَيۡهِ غَضَبِي فَقَدۡ هَوَىٰ", "Kuloo min tayyibati ma razaqnakum wala tatghaw feehi fayahilla AAalaykum ghadabee waman yahlil AAalayhi ghadabee faqad hawa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 82, "But indeed, I am the Perpetual Forgiver of whoever repents and believes and does righteousness and then continues in guidance", "وَإِنِّي لَغَفَّارٞ لِّمَن تَابَ وَءَامَنَ وَعَمِلَ صَٰلِحٗا ثُمَّ ٱهۡتَدَىٰ", "Wainnee laghaffarun liman taba waamana waAAamila salihan thumma ihtada");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 83, "[Allah] said, ""And what made you hasten from your people, O Moses", "۞وَمَآ أَعۡجَلَكَ عَن قَوۡمِكَ يَٰمُوسَىٰ", "Wama aAAjalaka AAan qawmika ya moosa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 84, "He said, ""They are close upon my tracks, and I hastened to You, my Lord, that You be pleased", "قَالَ هُمۡ أُوْلَآءِ عَلَىٰٓ أَثَرِي وَعَجِلۡتُ إِلَيۡكَ رَبِّ لِتَرۡضَىٰ", "Qala hum olai AAala atharee waAAajiltu ilayka rabbi litarda");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 85, "[Allah] said, ""But indeed, We have tried your people after you [departed], and the Samiri has led them astray", "قَالَ فَإِنَّا قَدۡ فَتَنَّا قَوۡمَكَ مِنۢ بَعۡدِكَ وَأَضَلَّهُمُ ٱلسَّامِرِيُّ", "Qala fainna qad fatanna qawmaka min baAAdika waadallahumu alssamiriyyu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 86, "So Moses returned to his people, angry and grieved. He said, ""O my people, did your Lord not make you a good promise? Then, was the time [of its fulfillment] too long for you, or did you wish that wrath from your Lord descend upon you, so you broke your promise [of obedience] to me", "فَرَجَعَ مُوسَىٰٓ إِلَىٰ قَوۡمِهِۦ غَضۡبَٰنَ أَسِفٗاۚ قَالَ يَٰقَوۡمِ أَلَمۡ يَعِدۡكُمۡ رَبُّكُمۡ وَعۡدًا حَسَنًاۚ أَفَطَالَ عَلَيۡكُمُ ٱلۡعَهۡدُ أَمۡ أَرَدتُّمۡ أَن يَحِلَّ عَلَيۡكُمۡ غَضَبٞ مِّن رَّبِّكُمۡ فَأَخۡلَفۡتُم مَّوۡعِدِي", "FarajaAAa moosa ila qawmihi ghadbana asifan qala ya qawmi alam yaAAidkum rabbukum waAAdan hasanan afatala AAalaykumu alAAahdu am aradtum an yahilla AAalaykum ghadabun min rabbikum faakhlaftum mawAAidee");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 87, "They said, ""We did not break our promise to you by our will, but we were made to carry burdens from the ornaments of the people [of Pharaoh], so we threw them [into the fire], and thus did the Samiri throw", "قَالُواْ مَآ أَخۡلَفۡنَا مَوۡعِدَكَ بِمَلۡكِنَا وَلَٰكِنَّا حُمِّلۡنَآ أَوۡزَارٗا مِّن زِينَةِ ٱلۡقَوۡمِ فَقَذَفۡنَٰهَا فَكَذَٰلِكَ أَلۡقَى ٱلسَّامِرِيُّ", "Qaloo ma akhlafna mawAAidaka bimalkina walakinna hummilna awzaran min zeenati alqawmi faqathafnaha fakathalika alqa alssamiriyyu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 88, "And he extracted for them [the statue of] a calf which had a lowing sound, and they said, ""This is your god and the god of Moses, but he forgot", "فَأَخۡرَجَ لَهُمۡ عِجۡلٗا جَسَدٗا لَّهُۥ خُوَارٞ فَقَالُواْ هَٰذَآ إِلَٰهُكُمۡ وَإِلَٰهُ مُوسَىٰ فَنَسِيَ", "Faakhraja lahum AAijlan jasadan lahu khuwarun faqaloo hatha ilahukum wailahu moosa fanasiya");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 89, "Did they not see that it could not return to them any speech and that it did not possess for them any harm or benefit", "أَفَلَا يَرَوۡنَ أَلَّا يَرۡجِعُ إِلَيۡهِمۡ قَوۡلٗا وَلَا يَمۡلِكُ لَهُمۡ ضَرّٗا وَلَا نَفۡعٗا", "Afala yarawna alla yarjiAAu ilayhim qawlan wala yamliku lahum darran wala nafAAan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 90, "And Aaron had already told them before [the return of Moses], ""O my people, you are only being tested by it, and indeed, your Lord is the Most Merciful, so follow me and obey my order", "وَلَقَدۡ قَالَ لَهُمۡ هَٰرُونُ مِن قَبۡلُ يَٰقَوۡمِ إِنَّمَا فُتِنتُم بِهِۦۖ وَإِنَّ رَبَّكُمُ ٱلرَّحۡمَٰنُ فَٱتَّبِعُونِي وَأَطِيعُوٓاْ أَمۡرِي", "Walaqad qala lahum haroonu min qablu ya qawmi innama futintum bihi wainna rabbakumu alrrahmanu faittabiAAoonee waateeAAoo amree");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 91, "They said, ""We will never cease being devoted to the calf until Moses returns to us", "قَالُواْ لَن نَّبۡرَحَ عَلَيۡهِ عَٰكِفِينَ حَتَّىٰ يَرۡجِعَ إِلَيۡنَا مُوسَىٰ", "Qaloo lan nabraha AAalayhi AAakifeena hatta yarjiAAa ilayna moosa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 92, "[Moses] said, ""O Aaron, what prevented you, when you saw them going astray", "قَالَ يَٰهَٰرُونُ مَا مَنَعَكَ إِذۡ رَأَيۡتَهُمۡ ضَلُّوٓاْ", "Qala ya haroonu ma manaAAaka ith raaytahum dalloo");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 93, "From following me? Then have you disobeyed my order", "أَلَّا تَتَّبِعَنِۖ أَفَعَصَيۡتَ أَمۡرِي", "Alla tattabiAAani afaAAasayta amree");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 94, "[Aaron] said, ""O son of my mother, do not seize [me] by my beard or by my head. Indeed, I feared that you would say, 'You caused division among the Children of Israel, and you did not observe [or await] my word", "قَالَ يَبۡنَؤُمَّ لَا تَأۡخُذۡ بِلِحۡيَتِي وَلَا بِرَأۡسِيٓۖ إِنِّي خَشِيتُ أَن تَقُولَ فَرَّقۡتَ بَيۡنَ بَنِيٓ إِسۡرَـٰٓءِيلَ وَلَمۡ تَرۡقُبۡ قَوۡلِي", "Qala ya bna omma la takhuth bilihyatee wala birasee innee khasheetu an taqoola farraqta bayna banee israeela walam tarqub qawlee");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 95, "[Moses] said, ""And what is your case, O Samiri", "قَالَ فَمَا خَطۡبُكَ يَٰسَٰمِرِيُّ", "Qala fama khatbuka ya samiriyyu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 96, "He said, ""I saw what they did not see, so I took a handful [of dust] from the track of the messenger and threw it, and thus did my soul entice me", "قَالَ بَصُرۡتُ بِمَا لَمۡ يَبۡصُرُواْ بِهِۦ فَقَبَضۡتُ قَبۡضَةٗ مِّنۡ أَثَرِ ٱلرَّسُولِ فَنَبَذۡتُهَا وَكَذَٰلِكَ سَوَّلَتۡ لِي نَفۡسِي", "Qala basurtu bima lam yabsuroo bihi faqabadtu qabdatan min athari alrrasooli fanabathtuha wakathalika sawwalat lee nafsee");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 97, "[Moses] said, ""Then go. And indeed, it is [decreed] for you in [this] life to say, 'No contact.' And indeed, you have an appointment [in the Hereafter] you will not fail to keep. And look at your 'god' to which you remained devoted. We will surely burn it and blow it into the sea with a blast", "قَالَ فَٱذۡهَبۡ فَإِنَّ لَكَ فِي ٱلۡحَيَوٰةِ أَن تَقُولَ لَا مِسَاسَۖ وَإِنَّ لَكَ مَوۡعِدٗا لَّن تُخۡلَفَهُۥۖ وَٱنظُرۡ إِلَىٰٓ إِلَٰهِكَ ٱلَّذِي ظَلۡتَ عَلَيۡهِ عَاكِفٗاۖ لَّنُحَرِّقَنَّهُۥ ثُمَّ لَنَنسِفَنَّهُۥ فِي ٱلۡيَمِّ نَسۡفًا", "Qala faithhab fainna laka fee alhayati an taqoola la misasa wainna laka mawAAidan lan tukhlafahu waonthur ila ilahika allathee thalta AAalayhi AAakifan lanuharriqannahu thumma lanansifannahu fee alyammi nasfan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 98, "Your god is only Allah, except for whom there is no deity. He has encompassed all things in knowledge", "إِنَّمَآ إِلَٰهُكُمُ ٱللَّهُ ٱلَّذِي لَآ إِلَٰهَ إِلَّا هُوَۚ وَسِعَ كُلَّ شَيۡءٍ عِلۡمٗا", "Innama ilahukumu Allahu allathee la ilaha illa huwa wasiAAa kulla shayin AAilman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 99, "Thus, [O Muhammad], We relate to you from the news of what has preceded. And We have certainly given you from Us the Qur'an", "كَذَٰلِكَ نَقُصُّ عَلَيۡكَ مِنۡ أَنۢبَآءِ مَا قَدۡ سَبَقَۚ وَقَدۡ ءَاتَيۡنَٰكَ مِن لَّدُنَّا ذِكۡرٗا", "Kathalika naqussu AAalayka min anbai ma qad sabaqa waqad ataynaka min ladunna thikran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 100, "Whoever turns away from it - then indeed, he will bear on the Day of Resurrection a burden", "مَّنۡ أَعۡرَضَ عَنۡهُ فَإِنَّهُۥ يَحۡمِلُ يَوۡمَ ٱلۡقِيَٰمَةِ وِزۡرًا", "Man aAArada AAanhu fainnahu yahmilu yawma alqiyamati wizran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 101, "[Abiding] eternally therein, and evil it is for them on the Day of Resurrection as a load", "خَٰلِدِينَ فِيهِۖ وَسَآءَ لَهُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ حِمۡلٗا", "Khalideena feehi wasaa lahum yawma alqiyamati himlan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 102, "The Day the Horn will be blown. And We will gather the criminals, that Day, blue-eyed", "يَوۡمَ يُنفَخُ فِي ٱلصُّورِۚ وَنَحۡشُرُ ٱلۡمُجۡرِمِينَ يَوۡمَئِذٖ زُرۡقٗا", "Yawma yunfakhu fee alssoori wanahshuru almujrimeena yawmaithin zurqan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 103, "They will murmur among themselves, ""You remained not but ten [days in the world]", "يَتَخَٰفَتُونَ بَيۡنَهُمۡ إِن لَّبِثۡتُمۡ إِلَّا عَشۡرٗا", "Yatakhafatoona baynahum in labithtum illa AAashran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 104, "We are most knowing of what they say when the best of them in manner will say, ""You remained not but one day", "نَّحۡنُ أَعۡلَمُ بِمَا يَقُولُونَ إِذۡ يَقُولُ أَمۡثَلُهُمۡ طَرِيقَةً إِن لَّبِثۡتُمۡ إِلَّا يَوۡمٗا", "Nahnu aAAlamu bima yaqooloona ith yaqoolu amthaluhum tareeqatan in labithtum illa yawman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 105, "And they ask you about the mountains, so say, ""My Lord will blow them away with a blast", "وَيَسۡـَٔلُونَكَ عَنِ ٱلۡجِبَالِ فَقُلۡ يَنسِفُهَا رَبِّي نَسۡفٗا", "Wayasaloonaka AAani aljibali faqul yansifuha rabbee nasfan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 106, "And He will leave the earth a level plain", "فَيَذَرُهَا قَاعٗا صَفۡصَفٗا", "Fayatharuha qaAAan safsafan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 107, "You will not see therein a depression or an elevation", "لَّا تَرَىٰ فِيهَا عِوَجٗا وَلَآ أَمۡتٗا", "La tara feeha AAiwajan wala amtan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 108, "That Day, everyone will follow [the call of] the Caller [with] no deviation therefrom, and [all] voices will be stilled before the Most Merciful, so you will not hear except a whisper [of footsteps]", "يَوۡمَئِذٖ يَتَّبِعُونَ ٱلدَّاعِيَ لَا عِوَجَ لَهُۥۖ وَخَشَعَتِ ٱلۡأَصۡوَاتُ لِلرَّحۡمَٰنِ فَلَا تَسۡمَعُ إِلَّا هَمۡسٗا", "Yawmaithin yattabiAAoona alddaAAiya la AAiwaja lahu wakhashaAAati alaswatu lilrrahmani fala tasmaAAu illa hamsan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 109, "That Day, no intercession will benefit except [that of] one to whom the Most Merciful has given permission and has accepted his word", "يَوۡمَئِذٖ لَّا تَنفَعُ ٱلشَّفَٰعَةُ إِلَّا مَنۡ أَذِنَ لَهُ ٱلرَّحۡمَٰنُ وَرَضِيَ لَهُۥ قَوۡلٗا", "Yawmaithin la tanfaAAu alshshafaAAatu illa man athina lahu alrrahmanu waradiya lahu qawlan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 110, "Allah knows what is [presently] before them and what will be after them, but they do not encompass it in knowledge", "يَعۡلَمُ مَا بَيۡنَ أَيۡدِيهِمۡ وَمَا خَلۡفَهُمۡ وَلَا يُحِيطُونَ بِهِۦ عِلۡمٗا", "YaAAlamu ma bayna aydeehim wama khalfahum wala yuheetoona bihi AAilman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 111, "And [all] faces will be humbled before the Ever-Living, the Sustainer of existence. And he will have failed who carries injustice", "۞وَعَنَتِ ٱلۡوُجُوهُ لِلۡحَيِّ ٱلۡقَيُّومِۖ وَقَدۡ خَابَ مَنۡ حَمَلَ ظُلۡمٗا", "WaAAanati alwujoohu lilhayyi alqayyoomi waqad khaba man hamala thulman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 112, "But he who does of righteous deeds while he is a believer - he will neither fear injustice nor deprivation", "وَمَن يَعۡمَلۡ مِنَ ٱلصَّـٰلِحَٰتِ وَهُوَ مُؤۡمِنٞ فَلَا يَخَافُ ظُلۡمٗا وَلَا هَضۡمٗا", "Waman yaAAmal mina alssalihati wahuwa muminun fala yakhafu thulman wala hadman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 113, "And thus We have sent it down as an Arabic Qur'an and have diversified therein the warnings that perhaps they will avoid [sin] or it would cause them remembrance", "وَكَذَٰلِكَ أَنزَلۡنَٰهُ قُرۡءَانًا عَرَبِيّٗا وَصَرَّفۡنَا فِيهِ مِنَ ٱلۡوَعِيدِ لَعَلَّهُمۡ يَتَّقُونَ أَوۡ يُحۡدِثُ لَهُمۡ ذِكۡرٗا", "Wakathalika anzalnahu quranan AAarabiyyan wasarrafna feehi mina alwaAAeedi laAAallahum yattaqoona aw yuhdithu lahum thikran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 114, "So high [above all] is Allah, the Sovereign, the Truth. And, [O Muhammad], do not hasten with [recitation of] the Qur'an before its revelation is completed to you, and say, ""My Lord, increase me in knowledge", "فَتَعَٰلَى ٱللَّهُ ٱلۡمَلِكُ ٱلۡحَقُّۗ وَلَا تَعۡجَلۡ بِٱلۡقُرۡءَانِ مِن قَبۡلِ أَن يُقۡضَىٰٓ إِلَيۡكَ وَحۡيُهُۥۖ وَقُل رَّبِّ زِدۡنِي عِلۡمٗا", "FataAAala Allahu almaliku alhaqqu wala taAAjal bialqurani min qabli an yuqda ilayka wahyuhu waqul rabbi zidnee AAilman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 115, "And We had already taken a promise from Adam before, but he forgot; and We found not in him determination", "وَلَقَدۡ عَهِدۡنَآ إِلَىٰٓ ءَادَمَ مِن قَبۡلُ فَنَسِيَ وَلَمۡ نَجِدۡ لَهُۥ عَزۡمٗا", "Walaqad AAahidna ila adama min qablu fanasiya walam najid lahu AAazman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 116, "And [mention] when We said to the angels, ""Prostrate to Adam,"" and they prostrated, except Iblees; he refused", "وَإِذۡ قُلۡنَا لِلۡمَلَـٰٓئِكَةِ ٱسۡجُدُواْ لِأٓدَمَ فَسَجَدُوٓاْ إِلَّآ إِبۡلِيسَ أَبَىٰ", "Waith qulna lilmalaikati osjudoo liadama fasajadoo illa ibleesa aba");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 117, "So We said, ""O Adam, indeed this is an enemy to you and to your wife. Then let him not remove you from Paradise so you would suffer", "فَقُلۡنَا يَـٰٓـَٔادَمُ إِنَّ هَٰذَا عَدُوّٞ لَّكَ وَلِزَوۡجِكَ فَلَا يُخۡرِجَنَّكُمَا مِنَ ٱلۡجَنَّةِ فَتَشۡقَىٰٓ", "Faqulna ya adamu inna hatha AAaduwwun laka walizawjika fala yukhrijannakuma mina aljannati fatashqa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 118, "Indeed, it is [promised] for you not to be hungry therein or be unclothed", "إِنَّ لَكَ أَلَّا تَجُوعَ فِيهَا وَلَا تَعۡرَىٰ", "Inna laka alla tajooAAa feeha wala taAAra");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 119, "And indeed, you will not be thirsty therein or be hot from the sun", "وَأَنَّكَ لَا تَظۡمَؤُاْ فِيهَا وَلَا تَضۡحَىٰ", "Waannaka la tathmao feeha wala tadha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 120, "Then Satan whispered to him; he said, ""O Adam, shall I direct you to the tree of eternity and possession that will not deteriorate", "فَوَسۡوَسَ إِلَيۡهِ ٱلشَّيۡطَٰنُ قَالَ يَـٰٓـَٔادَمُ هَلۡ أَدُلُّكَ عَلَىٰ شَجَرَةِ ٱلۡخُلۡدِ وَمُلۡكٖ لَّا يَبۡلَىٰ", "Fawaswasa ilayhi alshshaytanu qala ya adamu hal adulluka AAala shajarati alkhuldi wamulkin la yabla");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 121, "And Adam and his wife ate of it, and their private parts became apparent to them, and they began to fasten over themselves from the leaves of Paradise. And Adam disobeyed his Lord and erred", "فَأَكَلَا مِنۡهَا فَبَدَتۡ لَهُمَا سَوۡءَٰتُهُمَا وَطَفِقَا يَخۡصِفَانِ عَلَيۡهِمَا مِن وَرَقِ ٱلۡجَنَّةِۚ وَعَصَىٰٓ ءَادَمُ رَبَّهُۥ فَغَوَىٰ", "Faakala minha fabadat lahuma sawatuhuma watafiqa yakhsifani AAalayhima min waraqi aljannati waAAasa adamu rabbahu faghawa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 122, "Then his Lord chose him and turned to him in forgiveness and guided [him]", "ثُمَّ ٱجۡتَبَٰهُ رَبُّهُۥ فَتَابَ عَلَيۡهِ وَهَدَىٰ", "Thumma ijtabahu rabbuhu fataba AAalayhi wahada");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 123, "[Allah] said, ""Descend from Paradise - all, [your descendants] being enemies to one another. And if there should come to you guidance from Me - then whoever follows My guidance will neither go astray [in the world] nor suffer [in the Hereafter]", "قَالَ ٱهۡبِطَا مِنۡهَا جَمِيعَۢاۖ بَعۡضُكُمۡ لِبَعۡضٍ عَدُوّٞۖ فَإِمَّا يَأۡتِيَنَّكُم مِّنِّي هُدٗى فَمَنِ ٱتَّبَعَ هُدَايَ فَلَا يَضِلُّ وَلَا يَشۡقَىٰ", "Qala ihbita minha jameeAAan baAAdukum libaAAdin AAaduwwun faimma yatiyannakum minnee hudan famani ittabaAAa hudaya fala yadillu wala yashqa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 124, "And whoever turns away from My remembrance - indeed, he will have a depressed life, and We will gather him on the Day of Resurrection blind", "وَمَنۡ أَعۡرَضَ عَن ذِكۡرِي فَإِنَّ لَهُۥ مَعِيشَةٗ ضَنكٗا وَنَحۡشُرُهُۥ يَوۡمَ ٱلۡقِيَٰمَةِ أَعۡمَىٰ", "Waman aAArada AAan thikree fainna lahu maAAeeshatan dankan wanahshuruhu yawma alqiyamati aAAman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 125, "He will say, ""My Lord, why have you raised me blind while I was [once] seeing", "قَالَ رَبِّ لِمَ حَشَرۡتَنِيٓ أَعۡمَىٰ وَقَدۡ كُنتُ بَصِيرٗا", "Qala rabbi lima hashartanee aAAma waqad kuntu baseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 126, "[Allah] will say, ""Thus did Our signs come to you, and you forgot them; and thus will you this Day be forgotten", "قَالَ كَذَٰلِكَ أَتَتۡكَ ءَايَٰتُنَا فَنَسِيتَهَاۖ وَكَذَٰلِكَ ٱلۡيَوۡمَ تُنسَىٰ", "Qala kathalika atatka ayatuna fanaseetaha wakathalika alyawma tunsa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 127, "And thus do We recompense he who transgressed and did not believe in the signs of his Lord. And the punishment of the Hereafter is more severe and more enduring", "وَكَذَٰلِكَ نَجۡزِي مَنۡ أَسۡرَفَ وَلَمۡ يُؤۡمِنۢ بِـَٔايَٰتِ رَبِّهِۦۚ وَلَعَذَابُ ٱلۡأٓخِرَةِ أَشَدُّ وَأَبۡقَىٰٓ", "Wakathalika najzee man asrafa walam yumin biayati rabbihi walaAAathabu alakhirati ashaddu waabqa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 128, "Then, has it not become clear to them how many generations We destroyed before them as they walk among their dwellings? Indeed in that are signs for those of intelligence", "أَفَلَمۡ يَهۡدِ لَهُمۡ كَمۡ أَهۡلَكۡنَا قَبۡلَهُم مِّنَ ٱلۡقُرُونِ يَمۡشُونَ فِي مَسَٰكِنِهِمۡۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّأُوْلِي ٱلنُّهَىٰ", "Afalam yahdi lahum kam ahlakna qablahum mina alqurooni yamshoona fee masakinihim inna fee thalika laayatin liolee alnnuha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 129, "And if not for a word that preceded from your Lord, punishment would have been an obligation [due immediately], and [if not for] a specified term [decreed]", "وَلَوۡلَا كَلِمَةٞ سَبَقَتۡ مِن رَّبِّكَ لَكَانَ لِزَامٗا وَأَجَلٞ مُّسَمّٗى", "Walawla kalimatun sabaqat min rabbika lakana lizaman waajalun musamman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 130, "So be patient over what they say and exalt [Allah] with praise of your Lord before the rising of the sun and before its setting; and during periods of the night [exalt Him] and at the ends of the day, that you may be satisfied", "فَٱصۡبِرۡ عَلَىٰ مَا يَقُولُونَ وَسَبِّحۡ بِحَمۡدِ رَبِّكَ قَبۡلَ طُلُوعِ ٱلشَّمۡسِ وَقَبۡلَ غُرُوبِهَاۖ وَمِنۡ ءَانَآيِٕ ٱلَّيۡلِ فَسَبِّحۡ وَأَطۡرَافَ ٱلنَّهَارِ لَعَلَّكَ تَرۡضَىٰ", "Faisbir AAala ma yaqooloona wasabbih bihamdi rabbika qabla tulooAAi alshshamsi waqabla ghuroobiha wamin anai allayli fasabbih waatrafa alnnahari laAAallaka tarda");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 131, "And do not extend your eyes toward that by which We have given enjoyment to [some] categories of them, [its being but] the splendor of worldly life by which We test them. And the provision of your Lord is better and more enduring", "وَلَا تَمُدَّنَّ عَيۡنَيۡكَ إِلَىٰ مَا مَتَّعۡنَا بِهِۦٓ أَزۡوَٰجٗا مِّنۡهُمۡ زَهۡرَةَ ٱلۡحَيَوٰةِ ٱلدُّنۡيَا لِنَفۡتِنَهُمۡ فِيهِۚ وَرِزۡقُ رَبِّكَ خَيۡرٞ وَأَبۡقَىٰ", "Wala tamuddanna AAaynayka ila ma mattaAAna bihi azwajan minhum zahrata alhayati alddunya linaftinahum feehi warizqu rabbika khayrun waabqa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 132, "And enjoin prayer upon your family [and people] and be steadfast therein. We ask you not for provision; We provide for you, and the [best] outcome is for [those of] righteousness", "وَأۡمُرۡ أَهۡلَكَ بِٱلصَّلَوٰةِ وَٱصۡطَبِرۡ عَلَيۡهَاۖ لَا نَسۡـَٔلُكَ رِزۡقٗاۖ نَّحۡنُ نَرۡزُقُكَۗ وَٱلۡعَٰقِبَةُ لِلتَّقۡوَىٰ", "Wamur ahlaka bialssalati waistabir AAalayha la nasaluka rizqan nahnu narzuquka waalAAaqibatu lilttaqwa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 133, "And they say, ""Why does he not bring us a sign from his Lord?"" Has there not come to them evidence of what was in the former scriptures", "وَقَالُواْ لَوۡلَا يَأۡتِينَا بِـَٔايَةٖ مِّن رَّبِّهِۦٓۚ أَوَلَمۡ تَأۡتِهِم بَيِّنَةُ مَا فِي ٱلصُّحُفِ ٱلۡأُولَىٰ", "Waqaloo lawla yateena biayatin min rabbihi awalam tatihim bayyinatu ma fee alssuhufi aloola");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 134, "And if We had destroyed them with a punishment before him, they would have said, ""Our Lord, why did You not send to us a messenger so we could have followed Your verses before we were humiliated and disgraced", "وَلَوۡ أَنَّآ أَهۡلَكۡنَٰهُم بِعَذَابٖ مِّن قَبۡلِهِۦ لَقَالُواْ رَبَّنَا لَوۡلَآ أَرۡسَلۡتَ إِلَيۡنَا رَسُولٗا فَنَتَّبِعَ ءَايَٰتِكَ مِن قَبۡلِ أَن نَّذِلَّ وَنَخۡزَىٰ", "Walaw anna ahlaknahum biAAathabin min qablihi laqaloo rabbana lawla arsalta ilayna rasoolan fanattabiAAa ayatika min qabli an nathilla wanakhza");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (20, 135, "Say, ""Each [of us] is waiting; so wait. For you will know who are the companions of the sound path and who is guided", "قُلۡ كُلّٞ مُّتَرَبِّصٞ فَتَرَبَّصُواْۖ فَسَتَعۡلَمُونَ مَنۡ أَصۡحَٰبُ ٱلصِّرَٰطِ ٱلسَّوِيِّ وَمَنِ ٱهۡتَدَىٰ", "Qul kullun mutarabbisun fatarabbasoo fasataAAlamoona man ashabu alssirati alssawiyyi wamani ihtada");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 1, "[The time of] their account has approached for the people, while they are in heedlessness turning away", "ٱقۡتَرَبَ لِلنَّاسِ حِسَابُهُمۡ وَهُمۡ فِي غَفۡلَةٖ مُّعۡرِضُونَ", "Iqtaraba lilnnasi hisabuhum wahum fee ghaflatin muAAridoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 2, "No mention comes to them anew from their Lord except that they listen to it while they are at play", "مَا يَأۡتِيهِم مِّن ذِكۡرٖ مِّن رَّبِّهِم مُّحۡدَثٍ إِلَّا ٱسۡتَمَعُوهُ وَهُمۡ يَلۡعَبُونَ", "Ma yateehim min thikrin min rabbihim muhdathin illa istamaAAoohu wahum yalAAaboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 3, "With their hearts distracted. And those who do wrong conceal their private conversation, [saying], ""Is this [Prophet] except a human being like you? So would you approach magic while you are aware [of it]", "لَاهِيَةٗ قُلُوبُهُمۡۗ وَأَسَرُّواْ ٱلنَّجۡوَى ٱلَّذِينَ ظَلَمُواْ هَلۡ هَٰذَآ إِلَّا بَشَرٞ مِّثۡلُكُمۡۖ أَفَتَأۡتُونَ ٱلسِّحۡرَ وَأَنتُمۡ تُبۡصِرُونَ", "Lahiyatan quloobuhum waasarroo alnnajwa allatheena thalamoo hal hatha illa basharun mithlukum afatatoona alssihra waantum tubsiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 4, "The Prophet said, ""My Lord knows whatever is said throughout the heaven and earth, and He is the Hearing, the Knowing", "قَالَ رَبِّي يَعۡلَمُ ٱلۡقَوۡلَ فِي ٱلسَّمَآءِ وَٱلۡأَرۡضِۖ وَهُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ", "Qala rabbee yaAAlamu alqawla fee alssamai waalardi wahuwa alssameeAAu alAAaleemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 5, "But they say, ""[The revelation is but] a mixture of false dreams; rather, he has invented it; rather, he is a poet. So let him bring us a sign just as the previous [messengers] were sent [with miracles]", "بَلۡ قَالُوٓاْ أَضۡغَٰثُ أَحۡلَٰمِۭ بَلِ ٱفۡتَرَىٰهُ بَلۡ هُوَ شَاعِرٞ فَلۡيَأۡتِنَا بِـَٔايَةٖ كَمَآ أُرۡسِلَ ٱلۡأَوَّلُونَ", "Bal qaloo adghathu ahlamin bali iftarahu bal huwa shaAAirun falyatina biayatin kama orsila alawwaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 6, "Not a [single] city which We destroyed believed before them, so will they believe", "مَآ ءَامَنَتۡ قَبۡلَهُم مِّن قَرۡيَةٍ أَهۡلَكۡنَٰهَآۖ أَفَهُمۡ يُؤۡمِنُونَ", "Ma amanat qablahum min qaryatin ahlaknaha afahum yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 7, "And We sent not before you, [O Muhammad], except men to whom We revealed [the message], so ask the people of the message if you do not know", "وَمَآ أَرۡسَلۡنَا قَبۡلَكَ إِلَّا رِجَالٗا نُّوحِيٓ إِلَيۡهِمۡۖ فَسۡـَٔلُوٓاْ أَهۡلَ ٱلذِّكۡرِ إِن كُنتُمۡ لَا تَعۡلَمُونَ", "Wama arsalna qablaka illa rijalan noohee ilayhim faisaloo ahla alththikri in kuntum la taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 8, "And We did not make the prophets forms not eating food, nor were they immortal [on earth]", "وَمَا جَعَلۡنَٰهُمۡ جَسَدٗا لَّا يَأۡكُلُونَ ٱلطَّعَامَ وَمَا كَانُواْ خَٰلِدِينَ", "Wama jaAAalnahum jasadan la yakuloona alttaAAama wama kanoo khalideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 9, "Then We fulfilled for them the promise, and We saved them and whom We willed and destroyed the transgressors", "ثُمَّ صَدَقۡنَٰهُمُ ٱلۡوَعۡدَ فَأَنجَيۡنَٰهُمۡ وَمَن نَّشَآءُ وَأَهۡلَكۡنَا ٱلۡمُسۡرِفِينَ", "Thumma sadaqnahumu alwaAAda faanjaynahum waman nashao waahlakna almusrifeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 10, "We have certainly sent down to you a Book in which is your mention. Then will you not reason", "لَقَدۡ أَنزَلۡنَآ إِلَيۡكُمۡ كِتَٰبٗا فِيهِ ذِكۡرُكُمۡۚ أَفَلَا تَعۡقِلُونَ", "Laqad anzalna ilaykum kitaban feehi thikrukum afala taAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 11, "And how many a city which was unjust have We shattered and produced after it another people", "وَكَمۡ قَصَمۡنَا مِن قَرۡيَةٖ كَانَتۡ ظَالِمَةٗ وَأَنشَأۡنَا بَعۡدَهَا قَوۡمًا ءَاخَرِينَ", "Wakam qasamna min qaryatin kanat thalimatan waanshana baAAdaha qawman akhareena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 12, "And when its inhabitants perceived Our punishment, at once they fled from it", "فَلَمَّآ أَحَسُّواْ بَأۡسَنَآ إِذَا هُم مِّنۡهَا يَرۡكُضُونَ", "Falamma ahassoo basana itha hum minha yarkudoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 13, "[Some angels said], ""Do not flee but return to where you were given luxury and to your homes - perhaps you will be questioned", "لَا تَرۡكُضُواْ وَٱرۡجِعُوٓاْ إِلَىٰ مَآ أُتۡرِفۡتُمۡ فِيهِ وَمَسَٰكِنِكُمۡ لَعَلَّكُمۡ تُسۡـَٔلُونَ", "La tarkudoo wairjiAAoo ila ma otriftum feehi wamasakinikum laAAallakum tusaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 14, "They said, ""O woe to us! Indeed, we were wrongdoers", "قَالُواْ يَٰوَيۡلَنَآ إِنَّا كُنَّا ظَٰلِمِينَ", "Qaloo ya waylana inna kunna thalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 15, "And that declaration of theirs did not cease until We made them [as] a harvest [mowed down], extinguished [like a fire]", "فَمَا زَالَت تِّلۡكَ دَعۡوَىٰهُمۡ حَتَّىٰ جَعَلۡنَٰهُمۡ حَصِيدًا خَٰمِدِينَ", "Fama zalat tilka daAAwahum hatta jaAAalnahum haseedan khamideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 16, "And We did not create the heaven and earth and that between them in play", "وَمَا خَلَقۡنَا ٱلسَّمَآءَ وَٱلۡأَرۡضَ وَمَا بَيۡنَهُمَا لَٰعِبِينَ", "Wama khalaqna alssamaa waalarda wama baynahuma laAAibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 17, "Had We intended to take a diversion, We could have taken it from [what is] with Us - if [indeed] We were to do so", "لَوۡ أَرَدۡنَآ أَن نَّتَّخِذَ لَهۡوٗا لَّٱتَّخَذۡنَٰهُ مِن لَّدُنَّآ إِن كُنَّا فَٰعِلِينَ", "Law aradna an nattakhitha lahwan laittakhathnahu min ladunna in kunna faAAileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 18, "Rather, We dash the truth upon falsehood, and it destroys it, and thereupon it departs. And for you is destruction from that which you describe", "بَلۡ نَقۡذِفُ بِٱلۡحَقِّ عَلَى ٱلۡبَٰطِلِ فَيَدۡمَغُهُۥ فَإِذَا هُوَ زَاهِقٞۚ وَلَكُمُ ٱلۡوَيۡلُ مِمَّا تَصِفُونَ", "Bal naqthifu bialhaqqi AAala albatili fayadmaghuhu faitha huwa zahiqun walakumu alwaylu mimma tasifoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 19, "To Him belongs whoever is in the heavens and the earth. And those near Him are not prevented by arrogance from His worship, nor do they tire", "وَلَهُۥ مَن فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَمَنۡ عِندَهُۥ لَا يَسۡتَكۡبِرُونَ عَنۡ عِبَادَتِهِۦ وَلَا يَسۡتَحۡسِرُونَ", "Walahu man fee alssamawati waalardi waman AAindahu la yastakbiroona AAan AAibadatihi wala yastahsiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 20, "They exalt [Him] night and day [and] do not slacken", "يُسَبِّحُونَ ٱلَّيۡلَ وَٱلنَّهَارَ لَا يَفۡتُرُونَ", "Yusabbihoona allayla waalnnahara la yafturoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 21, "Or have men taken for themselves gods from the earth who resurrect [the dead]", "أَمِ ٱتَّخَذُوٓاْ ءَالِهَةٗ مِّنَ ٱلۡأَرۡضِ هُمۡ يُنشِرُونَ", "Ami ittakhathoo alihatan mina alardi hum yunshiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 22, "Had there been within the heavens and earth gods besides Allah, they both would have been ruined. So exalted is Allah, Lord of the Throne, above what they describe", "لَوۡ كَانَ فِيهِمَآ ءَالِهَةٌ إِلَّا ٱللَّهُ لَفَسَدَتَاۚ فَسُبۡحَٰنَ ٱللَّهِ رَبِّ ٱلۡعَرۡشِ عَمَّا يَصِفُونَ", "Law kana feehima alihatun illa Allahu lafasadata fasubhana Allahi rabbi alAAarshi AAamma yasifoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 23, "He is not questioned about what He does, but they will be questioned", "لَا يُسۡـَٔلُ عَمَّا يَفۡعَلُ وَهُمۡ يُسۡـَٔلُونَ", "La yusalu AAamma yafAAalu wahum yusaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 24, "Or have they taken gods besides Him? Say, [O Muhammad], ""Produce your proof. This [Qur'an] is the message for those with me and the message of those before me."" But most of them do not know the truth, so they are turning away", "أَمِ ٱتَّخَذُواْ مِن دُونِهِۦٓ ءَالِهَةٗۖ قُلۡ هَاتُواْ بُرۡهَٰنَكُمۡۖ هَٰذَا ذِكۡرُ مَن مَّعِيَ وَذِكۡرُ مَن قَبۡلِيۚ بَلۡ أَكۡثَرُهُمۡ لَا يَعۡلَمُونَ ٱلۡحَقَّۖ فَهُم مُّعۡرِضُونَ", "Ami ittakhathoo min doonihi alihatan qul hatoo burhanakum hatha thikru man maAAiya wathikru man qablee bal aktharuhum la yaAAlamoona alhaqqa fahum muAAridoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 25, "And We sent not before you any messenger except that We revealed to him that, ""There is no deity except Me, so worship Me", "وَمَآ أَرۡسَلۡنَا مِن قَبۡلِكَ مِن رَّسُولٍ إِلَّا نُوحِيٓ إِلَيۡهِ أَنَّهُۥ لَآ إِلَٰهَ إِلَّآ أَنَا۠ فَٱعۡبُدُونِ", "Wama arsalna min qablika min rasoolin illa noohee ilayhi annahu la ilaha illa ana faoAAbudooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 26, "And they say, ""The Most Merciful has taken a son."" Exalted is He! Rather, they are [but] honored servants", "وَقَالُواْ ٱتَّخَذَ ٱلرَّحۡمَٰنُ وَلَدٗاۗ سُبۡحَٰنَهُۥۚ بَلۡ عِبَادٞ مُّكۡرَمُونَ", "Waqaloo ittakhatha alrrahmanu waladan subhanahu bal AAibadun mukramoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 27, "They cannot precede Him in word, and they act by His command", "لَا يَسۡبِقُونَهُۥ بِٱلۡقَوۡلِ وَهُم بِأَمۡرِهِۦ يَعۡمَلُونَ", "La yasbiqoonahu bialqawli wahum biamrihi yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 28, "He knows what is [presently] before them and what will be after them, and they cannot intercede except on behalf of one whom He approves. And they, from fear of Him, are apprehensive", "يَعۡلَمُ مَا بَيۡنَ أَيۡدِيهِمۡ وَمَا خَلۡفَهُمۡ وَلَا يَشۡفَعُونَ إِلَّا لِمَنِ ٱرۡتَضَىٰ وَهُم مِّنۡ خَشۡيَتِهِۦ مُشۡفِقُونَ", "YaAAlamu ma bayna aydeehim wama khalfahum wala yashfaAAoona illa limani irtada wahum min khashyatihi mushfiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 29, "And whoever of them should say, ""Indeed, I am a god besides Him""- that one We would recompense with Hell. Thus do We recompense the wrongdoers", "۞وَمَن يَقُلۡ مِنۡهُمۡ إِنِّيٓ إِلَٰهٞ مِّن دُونِهِۦ فَذَٰلِكَ نَجۡزِيهِ جَهَنَّمَۚ كَذَٰلِكَ نَجۡزِي ٱلظَّـٰلِمِينَ", "Waman yaqul minhum innee ilahun min doonihi fathalika najzeehi jahannama kathalika najzee alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 30, "Have those who disbelieved not considered that the heavens and the earth were a joined entity, and We separated them and made from water every living thing? Then will they not believe", "أَوَلَمۡ يَرَ ٱلَّذِينَ كَفَرُوٓاْ أَنَّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ كَانَتَا رَتۡقٗا فَفَتَقۡنَٰهُمَاۖ وَجَعَلۡنَا مِنَ ٱلۡمَآءِ كُلَّ شَيۡءٍ حَيٍّۚ أَفَلَا يُؤۡمِنُونَ", "Awalam yara allatheena kafaroo anna alssamawati waalarda kanata ratqan fafataqnahuma wajaAAalna mina almai kulla shayin hayyin afala yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 31, "And We placed within the earth firmly set mountains, lest it should shift with them, and We made therein [mountain] passes [as] roads that they might be guided", "وَجَعَلۡنَا فِي ٱلۡأَرۡضِ رَوَٰسِيَ أَن تَمِيدَ بِهِمۡ وَجَعَلۡنَا فِيهَا فِجَاجٗا سُبُلٗا لَّعَلَّهُمۡ يَهۡتَدُونَ", "WajaAAalna fee alardi rawasiya an tameeda bihim wajaAAalna feeha fijajan subulan laAAallahum yahtadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 32, "And We made the sky a protected ceiling, but they, from its signs, are turning away", "وَجَعَلۡنَا ٱلسَّمَآءَ سَقۡفٗا مَّحۡفُوظٗاۖ وَهُمۡ عَنۡ ءَايَٰتِهَا مُعۡرِضُونَ", "WajaAAalna alssamaa saqfan mahfoothan wahum AAan ayatiha muAAridoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 33, "And it is He who created the night and the day and the sun and the moon; all [heavenly bodies] in an orbit are swimming", "وَهُوَ ٱلَّذِي خَلَقَ ٱلَّيۡلَ وَٱلنَّهَارَ وَٱلشَّمۡسَ وَٱلۡقَمَرَۖ كُلّٞ فِي فَلَكٖ يَسۡبَحُونَ", "Wahuwa allathee khalaqa allayla waalnnahara waalshshamsa waalqamara kullun fee falakin yasbahoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 34, "And We did not grant to any man before you eternity [on earth]; so if you die - would they be eternal", "وَمَا جَعَلۡنَا لِبَشَرٖ مِّن قَبۡلِكَ ٱلۡخُلۡدَۖ أَفَإِيْن مِّتَّ فَهُمُ ٱلۡخَٰلِدُونَ", "Wama jaAAalna libasharin min qablika alkhulda afain mitta fahumu alkhalidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 35, "Every soul will taste death. And We test you with evil and with good as trial; and to Us you will be returned", "كُلُّ نَفۡسٖ ذَآئِقَةُ ٱلۡمَوۡتِۗ وَنَبۡلُوكُم بِٱلشَّرِّ وَٱلۡخَيۡرِ فِتۡنَةٗۖ وَإِلَيۡنَا تُرۡجَعُونَ", "Kullu nafsin thaiqatu almawti wanablookum bialshsharri waalkhayri fitnatan wailayna turjaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 36, "And when those who disbelieve see you, [O Muhammad], they take you not except in ridicule, [saying], ""Is this the one who insults your gods?"" And they are, at the mention of the Most Merciful, disbelievers", "وَإِذَا رَءَاكَ ٱلَّذِينَ كَفَرُوٓاْ إِن يَتَّخِذُونَكَ إِلَّا هُزُوًا أَهَٰذَا ٱلَّذِي يَذۡكُرُ ءَالِهَتَكُمۡ وَهُم بِذِكۡرِ ٱلرَّحۡمَٰنِ هُمۡ كَٰفِرُونَ", "Waitha raaka allatheena kafaroo in yattakhithoonaka illa huzuwan ahatha allathee yathkuru alihatakum wahum bithikri alrrahmani hum kafiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 37, "Man was created of haste. I will show you My signs, so do not impatiently urge Me", "خُلِقَ ٱلۡإِنسَٰنُ مِنۡ عَجَلٖۚ سَأُوْرِيكُمۡ ءَايَٰتِي فَلَا تَسۡتَعۡجِلُونِ", "Khuliqa alinsanu min AAajalin saoreekum ayatee fala tastaAAjiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 38, "And they say, ""When is this promise, if you should be truthful", "وَيَقُولُونَ مَتَىٰ هَٰذَا ٱلۡوَعۡدُ إِن كُنتُمۡ صَٰدِقِينَ", "Wayaqooloona mata hatha alwaAAdu in kuntum sadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 39, "If those who disbelieved but knew the time when they will not avert the Fire from their faces or from their backs and they will not be aided", "لَوۡ يَعۡلَمُ ٱلَّذِينَ كَفَرُواْ حِينَ لَا يَكُفُّونَ عَن وُجُوهِهِمُ ٱلنَّارَ وَلَا عَن ظُهُورِهِمۡ وَلَا هُمۡ يُنصَرُونَ", "Law yaAAlamu allatheena kafaroo heena la yakuffoona AAan wujoohihimu alnnara wala AAan thuhoorihim wala hum yunsaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 40, "Rather, it will come to them unexpectedly and bewilder them, and they will not be able to repel it, nor will they be reprieved", "بَلۡ تَأۡتِيهِم بَغۡتَةٗ فَتَبۡهَتُهُمۡ فَلَا يَسۡتَطِيعُونَ رَدَّهَا وَلَا هُمۡ يُنظَرُونَ", "Bal tateehim baghtatan fatabhatuhum fala yastateeAAoona raddaha wala hum yuntharoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 41, "And already were messengers ridiculed before you, but those who mocked them were enveloped by what they used to ridicule", "وَلَقَدِ ٱسۡتُهۡزِئَ بِرُسُلٖ مِّن قَبۡلِكَ فَحَاقَ بِٱلَّذِينَ سَخِرُواْ مِنۡهُم مَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ", "Walaqadi istuhzia birusulin min qablika fahaqa biallatheena sakhiroo minhum ma kanoo bihi yastahzioona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 42, "Say, ""Who can protect you at night or by day from the Most Merciful?"" But they are, from the remembrance of their Lord, turning away", "قُلۡ مَن يَكۡلَؤُكُم بِٱلَّيۡلِ وَٱلنَّهَارِ مِنَ ٱلرَّحۡمَٰنِۚ بَلۡ هُمۡ عَن ذِكۡرِ رَبِّهِم مُّعۡرِضُونَ", "Qul man yaklaokum biallayli waalnnahari mina alrrahmani bal hum AAan thikri rabbihim muAAridoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 43, "Or do they have gods to defend them other than Us? They are unable [even] to help themselves, nor can they be protected from Us", "أَمۡ لَهُمۡ ءَالِهَةٞ تَمۡنَعُهُم مِّن دُونِنَاۚ لَا يَسۡتَطِيعُونَ نَصۡرَ أَنفُسِهِمۡ وَلَا هُم مِّنَّا يُصۡحَبُونَ", "Am lahum alihatun tamnaAAuhum min doonina la yastateeAAoona nasra anfusihim wala hum minna yushaboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 44, "But, [on the contrary], We have provided good things for these [disbelievers] and their fathers until life was prolonged for them. Then do they not see that We set upon the land, reducing it from its borders? So it is they who will overcome", "بَلۡ مَتَّعۡنَا هَـٰٓؤُلَآءِ وَءَابَآءَهُمۡ حَتَّىٰ طَالَ عَلَيۡهِمُ ٱلۡعُمُرُۗ أَفَلَا يَرَوۡنَ أَنَّا نَأۡتِي ٱلۡأَرۡضَ نَنقُصُهَا مِنۡ أَطۡرَافِهَآۚ أَفَهُمُ ٱلۡغَٰلِبُونَ", "Bal mattaAAna haolai waabaahum hatta tala AAalayhimu alAAumuru afala yarawna anna natee alarda nanqusuha min atrafiha afahumu alghaliboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 45, "Say, ""I only warn you by revelation."" But the deaf do not hear the call when they are warned", "قُلۡ إِنَّمَآ أُنذِرُكُم بِٱلۡوَحۡيِۚ وَلَا يَسۡمَعُ ٱلصُّمُّ ٱلدُّعَآءَ إِذَا مَا يُنذَرُونَ", "Qul innama onthirukum bialwahyi wala yasmaAAu alssummu aldduAAaa itha ma yuntharoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 46, "And if [as much as] a whiff of the punishment of your Lord should touch them, they would surely say, ""O woe to us! Indeed, we have been wrongdoers", "وَلَئِن مَّسَّتۡهُمۡ نَفۡحَةٞ مِّنۡ عَذَابِ رَبِّكَ لَيَقُولُنَّ يَٰوَيۡلَنَآ إِنَّا كُنَّا ظَٰلِمِينَ", "Walain massathum nafhatun min AAathabi rabbika layaqoolunna ya waylana inna kunna thalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 47, "And We place the scales of justice for the Day of Resurrection, so no soul will be treated unjustly at all. And if there is [even] the weight of a mustard seed, We will bring it forth. And sufficient are We as accountant", "وَنَضَعُ ٱلۡمَوَٰزِينَ ٱلۡقِسۡطَ لِيَوۡمِ ٱلۡقِيَٰمَةِ فَلَا تُظۡلَمُ نَفۡسٞ شَيۡـٔٗاۖ وَإِن كَانَ مِثۡقَالَ حَبَّةٖ مِّنۡ خَرۡدَلٍ أَتَيۡنَا بِهَاۗ وَكَفَىٰ بِنَا حَٰسِبِينَ", "WanadaAAu almawazeena alqista liyawmi alqiyamati fala tuthlamu nafsun shayan wain kana mithqala habbatin min khardalin atayna biha wakafa bina hasibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 48, "And We had already given Moses and Aaron the criterion and a light and a reminder for the righteous", "وَلَقَدۡ ءَاتَيۡنَا مُوسَىٰ وَهَٰرُونَ ٱلۡفُرۡقَانَ وَضِيَآءٗ وَذِكۡرٗا لِّلۡمُتَّقِينَ", "Walaqad atayna moosa waharoona alfurqana wadiyaan wathikran lilmuttaqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 49, "Who fear their Lord unseen, while they are of the Hour apprehensive", "ٱلَّذِينَ يَخۡشَوۡنَ رَبَّهُم بِٱلۡغَيۡبِ وَهُم مِّنَ ٱلسَّاعَةِ مُشۡفِقُونَ", "Allatheena yakhshawna rabbahum bialghaybi wahum mina alssaAAati mushfiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 50, "And this [Qur'an] is a blessed message which We have sent down. Then are you with it unacquainted", "وَهَٰذَا ذِكۡرٞ مُّبَارَكٌ أَنزَلۡنَٰهُۚ أَفَأَنتُمۡ لَهُۥ مُنكِرُونَ", "Wahatha thikrun mubarakun anzalnahu afaantum lahu munkiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 51, "And We had certainly given Abraham his sound judgement before, and We were of him well-Knowing", "۞وَلَقَدۡ ءَاتَيۡنَآ إِبۡرَٰهِيمَ رُشۡدَهُۥ مِن قَبۡلُ وَكُنَّا بِهِۦ عَٰلِمِينَ", "Walaqad atayna ibraheema rushdahu min qablu wakunna bihi AAalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 52, "When he said to his father and his people, ""What are these statues to which you are devoted", "إِذۡ قَالَ لِأَبِيهِ وَقَوۡمِهِۦ مَا هَٰذِهِ ٱلتَّمَاثِيلُ ٱلَّتِيٓ أَنتُمۡ لَهَا عَٰكِفُونَ", "Ith qala liabeehi waqawmihi ma hathihi alttamatheelu allatee antum laha AAakifoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 53, "They said, ""We found our fathers worshippers of them", "قَالُواْ وَجَدۡنَآ ءَابَآءَنَا لَهَا عَٰبِدِينَ", "Qaloo wajadna abaana laha AAabideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 54, "He said, ""You were certainly, you and your fathers, in manifest error", "قَالَ لَقَدۡ كُنتُمۡ أَنتُمۡ وَءَابَآؤُكُمۡ فِي ضَلَٰلٖ مُّبِينٖ", "Qala laqad kuntum antum waabaokum fee dalalin mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 55, "They said, ""Have you come to us with truth, or are you of those who jest", "قَالُوٓاْ أَجِئۡتَنَا بِٱلۡحَقِّ أَمۡ أَنتَ مِنَ ٱللَّـٰعِبِينَ", "Qaloo ajitana bialhaqqi am anta mina allaAAibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 56, "He said, ""[No], rather, your Lord is the Lord of the heavens and the earth who created them, and I, to that, am of those who testify", "قَالَ بَل رَّبُّكُمۡ رَبُّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ ٱلَّذِي فَطَرَهُنَّ وَأَنَا۠ عَلَىٰ ذَٰلِكُم مِّنَ ٱلشَّـٰهِدِينَ", "Qala bal rabbukum rabbu alssamawati waalardi allathee fatarahunna waana AAala thalikum mina alshshahideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 57, "And [I swear] by Allah, I will surely plan against your idols after you have turned and gone away", "وَتَٱللَّهِ لَأَكِيدَنَّ أَصۡنَٰمَكُم بَعۡدَ أَن تُوَلُّواْ مُدۡبِرِينَ", "WataAllahi laakeedanna asnamakum baAAda an tuwalloo mudbireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 58, "So he made them into fragments, except a large one among them, that they might return to it [and question]", "فَجَعَلَهُمۡ جُذَٰذًا إِلَّا كَبِيرٗا لَّهُمۡ لَعَلَّهُمۡ إِلَيۡهِ يَرۡجِعُونَ", "FajaAAalahum juthathan illa kabeeran lahum laAAallahum ilayhi yarjiAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 59, "They said, ""Who has done this to our gods? Indeed, he is of the wrongdoers", "قَالُواْ مَن فَعَلَ هَٰذَا بِـَٔالِهَتِنَآ إِنَّهُۥ لَمِنَ ٱلظَّـٰلِمِينَ", "Qaloo man faAAala hatha bialihatina innahu lamina alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 60, "They said, ""We heard a young man mention them who is called Abraham", "قَالُواْ سَمِعۡنَا فَتٗى يَذۡكُرُهُمۡ يُقَالُ لَهُۥٓ إِبۡرَٰهِيمُ", "Qaloo samiAAna fatan yathkuruhum yuqalu lahu ibraheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 61, "They said, ""Then bring him before the eyes of the people that they may testify", "قَالُواْ فَأۡتُواْ بِهِۦ عَلَىٰٓ أَعۡيُنِ ٱلنَّاسِ لَعَلَّهُمۡ يَشۡهَدُونَ", "Qaloo fatoo bihi AAala aAAyuni alnnasi laAAallahum yashhadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 62, "They said, ""Have you done this to our gods, O Abraham", "قَالُوٓاْ ءَأَنتَ فَعَلۡتَ هَٰذَا بِـَٔالِهَتِنَا يَـٰٓإِبۡرَٰهِيمُ", "Qaloo aanta faAAalta hatha bialihatina ya ibraheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 63, "He said, ""Rather, this - the largest of them - did it, so ask them, if they should [be able to] speak", "قَالَ بَلۡ فَعَلَهُۥ كَبِيرُهُمۡ هَٰذَا فَسۡـَٔلُوهُمۡ إِن كَانُواْ يَنطِقُونَ", "Qala bal faAAalahu kabeeruhum hatha faisaloohum in kanoo yantiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 64, "So they returned to [blaming] themselves and said [to each other], ""Indeed, you are the wrongdoers", "فَرَجَعُوٓاْ إِلَىٰٓ أَنفُسِهِمۡ فَقَالُوٓاْ إِنَّكُمۡ أَنتُمُ ٱلظَّـٰلِمُونَ", "FarajaAAoo ila anfusihim faqaloo innakum antumu alththalimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 65, "Then they reversed themselves, [saying], ""You have already known that these do not speak", "ثُمَّ نُكِسُواْ عَلَىٰ رُءُوسِهِمۡ لَقَدۡ عَلِمۡتَ مَا هَـٰٓؤُلَآءِ يَنطِقُونَ", "Thumma nukisoo AAala ruoosihim laqad AAalimta ma haolai yantiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 66, "He said, ""Then do you worship instead of Allah that which does not benefit you at all or harm you", "قَالَ أَفَتَعۡبُدُونَ مِن دُونِ ٱللَّهِ مَا لَا يَنفَعُكُمۡ شَيۡـٔٗا وَلَا يَضُرُّكُمۡ", "Qala afataAAbudoona min dooni Allahi ma la yanfaAAukum shayan wala yadurrukum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 67, "Uff to you and to what you worship instead of Allah. Then will you not use reason", "أُفّٖ لَّكُمۡ وَلِمَا تَعۡبُدُونَ مِن دُونِ ٱللَّهِۚ أَفَلَا تَعۡقِلُونَ", "Offin lakum walima taAAbudoona min dooni Allahi afala taAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 68, "They said, ""Burn him and support your gods - if you are to act", "قَالُواْ حَرِّقُوهُ وَٱنصُرُوٓاْ ءَالِهَتَكُمۡ إِن كُنتُمۡ فَٰعِلِينَ", "Qaloo harriqoohu waonsuroo alihatakum in kuntum faAAileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 69, "Allah said, ""O fire, be coolness and safety upon Abraham", "قُلۡنَا يَٰنَارُ كُونِي بَرۡدٗا وَسَلَٰمًا عَلَىٰٓ إِبۡرَٰهِيمَ", "Qulna ya naru koonee bardan wasalaman AAala ibraheema");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 70, "And they intended for him harm, but We made them the greatest losers", "وَأَرَادُواْ بِهِۦ كَيۡدٗا فَجَعَلۡنَٰهُمُ ٱلۡأَخۡسَرِينَ", "Waaradoo bihi kaydan fajaAAalnahumu alakhsareena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 71, "And We delivered him and Lot to the land which We had blessed for the worlds", "وَنَجَّيۡنَٰهُ وَلُوطًا إِلَى ٱلۡأَرۡضِ ٱلَّتِي بَٰرَكۡنَا فِيهَا لِلۡعَٰلَمِينَ", "Wanajjaynahu walootan ila alardi allatee barakna feeha lilAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 72, "And We gave him Isaac and Jacob in addition, and all [of them] We made righteous", "وَوَهَبۡنَا لَهُۥٓ إِسۡحَٰقَ وَيَعۡقُوبَ نَافِلَةٗۖ وَكُلّٗا جَعَلۡنَا صَٰلِحِينَ", "Wawahabna lahu ishaqa wayaAAqooba nafilatan wakullan jaAAalna saliheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 73, "And We made them leaders guiding by Our command. And We inspired to them the doing of good deeds, establishment of prayer, and giving of zakah; and they were worshippers of Us", "وَجَعَلۡنَٰهُمۡ أَئِمَّةٗ يَهۡدُونَ بِأَمۡرِنَا وَأَوۡحَيۡنَآ إِلَيۡهِمۡ فِعۡلَ ٱلۡخَيۡرَٰتِ وَإِقَامَ ٱلصَّلَوٰةِ وَإِيتَآءَ ٱلزَّكَوٰةِۖ وَكَانُواْ لَنَا عَٰبِدِينَ", "WajaAAalnahum aimmatan yahdoona biamrina waawhayna ilayhim fiAAla alkhayrati waiqama alssalati waeetaa alzzakati wakanoo lana AAabideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 74, "And to Lot We gave judgement and knowledge, and We saved him from the city that was committing wicked deeds. Indeed, they were a people of evil, defiantly disobedient", "وَلُوطًا ءَاتَيۡنَٰهُ حُكۡمٗا وَعِلۡمٗا وَنَجَّيۡنَٰهُ مِنَ ٱلۡقَرۡيَةِ ٱلَّتِي كَانَت تَّعۡمَلُ ٱلۡخَبَـٰٓئِثَۚ إِنَّهُمۡ كَانُواْ قَوۡمَ سَوۡءٖ فَٰسِقِينَ", "Walootan ataynahu hukman waAAilman wanajjaynahu mina alqaryati allatee kanat taAAmalu alkhabaitha innahum kanoo qawma sawin fasiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 75, "And We admitted him into Our mercy. Indeed, he was of the righteous", "وَأَدۡخَلۡنَٰهُ فِي رَحۡمَتِنَآۖ إِنَّهُۥ مِنَ ٱلصَّـٰلِحِينَ", "Waadkhalnahu fee rahmatina innahu mina alssaliheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 76, "And [mention] Noah, when he called [to Allah] before [that time], so We responded to him and saved him and his family from the great flood", "وَنُوحًا إِذۡ نَادَىٰ مِن قَبۡلُ فَٱسۡتَجَبۡنَا لَهُۥ فَنَجَّيۡنَٰهُ وَأَهۡلَهُۥ مِنَ ٱلۡكَرۡبِ ٱلۡعَظِيمِ", "Wanoohan ith nada min qablu faistajabna lahu fanajjaynahu waahlahu mina alkarbi alAAatheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 77, "And We saved him from the people who denied Our signs. Indeed, they were a people of evil, so We drowned them, all together", "وَنَصَرۡنَٰهُ مِنَ ٱلۡقَوۡمِ ٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَآۚ إِنَّهُمۡ كَانُواْ قَوۡمَ سَوۡءٖ فَأَغۡرَقۡنَٰهُمۡ أَجۡمَعِينَ", "Wanasarnahu mina alqawmi allatheena kaththaboo biayatina innahum kanoo qawma sawin faaghraqnahum ajmaAAeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 78, "And [mention] David and Solomon, when they judged concerning the field - when the sheep of a people overran it [at night], and We were witness to their judgement", "وَدَاوُۥدَ وَسُلَيۡمَٰنَ إِذۡ يَحۡكُمَانِ فِي ٱلۡحَرۡثِ إِذۡ نَفَشَتۡ فِيهِ غَنَمُ ٱلۡقَوۡمِ وَكُنَّا لِحُكۡمِهِمۡ شَٰهِدِينَ", "Wadawooda wasulaymana ith yahkumani fee alharthi ith nafashat feehi ghanamu alqawmi wakunna lihukmihim shahideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 79, "And We gave understanding of the case to Solomon, and to each [of them] We gave judgement and knowledge. And We subjected the mountains to exalt [Us], along with David and [also] the birds. And We were doing [that]", "فَفَهَّمۡنَٰهَا سُلَيۡمَٰنَۚ وَكُلًّا ءَاتَيۡنَا حُكۡمٗا وَعِلۡمٗاۚ وَسَخَّرۡنَا مَعَ دَاوُۥدَ ٱلۡجِبَالَ يُسَبِّحۡنَ وَٱلطَّيۡرَۚ وَكُنَّا فَٰعِلِينَ", "Fafahhamnaha sulaymana wakullan atayna hukman waAAilman wasakhkharna maAAa dawooda aljibala yusabbihna waalttayra wakunna faAAileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 80, "And We taught him the fashioning of coats of armor to protect you from your [enemy in] battle. So will you then be grateful", "وَعَلَّمۡنَٰهُ صَنۡعَةَ لَبُوسٖ لَّكُمۡ لِتُحۡصِنَكُم مِّنۢ بَأۡسِكُمۡۖ فَهَلۡ أَنتُمۡ شَٰكِرُونَ", "WaAAallamnahu sanAAata laboosin lakum lituhsinakum min basikum fahal antum shakiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 81, "And to Solomon [We subjected] the wind, blowing forcefully, proceeding by his command toward the land which We had blessed. And We are ever, of all things, Knowing", "وَلِسُلَيۡمَٰنَ ٱلرِّيحَ عَاصِفَةٗ تَجۡرِي بِأَمۡرِهِۦٓ إِلَى ٱلۡأَرۡضِ ٱلَّتِي بَٰرَكۡنَا فِيهَاۚ وَكُنَّا بِكُلِّ شَيۡءٍ عَٰلِمِينَ", "Walisulaymana alrreeha AAasifatan tajree biamrihi ila alardi allatee barakna feeha wakunna bikulli shayin AAalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 82, "And of the devils were those who dived for him and did work other than that. And We were of them a guardian", "وَمِنَ ٱلشَّيَٰطِينِ مَن يَغُوصُونَ لَهُۥ وَيَعۡمَلُونَ عَمَلٗا دُونَ ذَٰلِكَۖ وَكُنَّا لَهُمۡ حَٰفِظِينَ", "Wamina alshshayateeni man yaghoosoona lahu wayaAAmaloona AAamalan doona thalika wakunna lahum hafitheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 83, "And [mention] Job, when he called to his Lord, ""Indeed, adversity has touched me, and you are the Most Merciful of the merciful", "۞وَأَيُّوبَ إِذۡ نَادَىٰ رَبَّهُۥٓ أَنِّي مَسَّنِيَ ٱلضُّرُّ وَأَنتَ أَرۡحَمُ ٱلرَّـٰحِمِينَ", "Waayyooba ith nada rabbahu annee massaniya alddurru waanta arhamu alrrahimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 84, "So We responded to him and removed what afflicted him of adversity. And We gave him [back] his family and the like thereof with them as mercy from Us and a reminder for the worshippers [of Allah]", "فَٱسۡتَجَبۡنَا لَهُۥ فَكَشَفۡنَا مَا بِهِۦ مِن ضُرّٖۖ وَءَاتَيۡنَٰهُ أَهۡلَهُۥ وَمِثۡلَهُم مَّعَهُمۡ رَحۡمَةٗ مِّنۡ عِندِنَا وَذِكۡرَىٰ لِلۡعَٰبِدِينَ", "Faistajabna lahu fakashafna ma bihi min durrin waataynahu ahlahu wamithlahum maAAahum rahmatan min AAindina wathikra lilAAabideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 85, "And [mention] Ishmael and Idrees and Dhul-Kifl; all were of the patient", "وَإِسۡمَٰعِيلَ وَإِدۡرِيسَ وَذَا ٱلۡكِفۡلِۖ كُلّٞ مِّنَ ٱلصَّـٰبِرِينَ", "WaismaAAeela waidreesa watha alkifli kullun mina alssabireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 86, "And We admitted them into Our mercy. Indeed, they were of the righteous", "وَأَدۡخَلۡنَٰهُمۡ فِي رَحۡمَتِنَآۖ إِنَّهُم مِّنَ ٱلصَّـٰلِحِينَ", "Waadkhalnahum fee rahmatina innahum mina alssaliheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 87, "And [mention] the man of the fish, when he went off in anger and thought that We would not decree [anything] upon him. And he called out within the darknesses, ""There is no deity except You; exalted are You. Indeed, I have been of the wrongdoers", "وَذَا ٱلنُّونِ إِذ ذَّهَبَ مُغَٰضِبٗا فَظَنَّ أَن لَّن نَّقۡدِرَ عَلَيۡهِ فَنَادَىٰ فِي ٱلظُّلُمَٰتِ أَن لَّآ إِلَٰهَ إِلَّآ أَنتَ سُبۡحَٰنَكَ إِنِّي كُنتُ مِنَ ٱلظَّـٰلِمِينَ", "Watha alnnooni ith thahaba mughadiban fathanna an lan naqdira AAalayhi fanada fee alththulumati an la ilaha illa anta subhanaka innee kuntu mina alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 88, "So We responded to him and saved him from the distress. And thus do We save the believers", "فَٱسۡتَجَبۡنَا لَهُۥ وَنَجَّيۡنَٰهُ مِنَ ٱلۡغَمِّۚ وَكَذَٰلِكَ نُـۨجِي ٱلۡمُؤۡمِنِينَ", "Faistajabna lahu wanajjaynahu mina alghammi wakathalika nunjee almumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 89, "And [mention] Zechariah, when he called to his Lord, ""My Lord, do not leave me alone [with no heir], while you are the best of inheritors", "وَزَكَرِيَّآ إِذۡ نَادَىٰ رَبَّهُۥ رَبِّ لَا تَذَرۡنِي فَرۡدٗا وَأَنتَ خَيۡرُ ٱلۡوَٰرِثِينَ", "Wazakariyya ith nada rabbahu rabbi la tatharnee fardan waanta khayru alwaritheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 90, "So We responded to him, and We gave to him John, and amended for him his wife. Indeed, they used to hasten to good deeds and supplicate Us in hope and fear, and they were to Us humbly submissive", "فَٱسۡتَجَبۡنَا لَهُۥ وَوَهَبۡنَا لَهُۥ يَحۡيَىٰ وَأَصۡلَحۡنَا لَهُۥ زَوۡجَهُۥٓۚ إِنَّهُمۡ كَانُواْ يُسَٰرِعُونَ فِي ٱلۡخَيۡرَٰتِ وَيَدۡعُونَنَا رَغَبٗا وَرَهَبٗاۖ وَكَانُواْ لَنَا خَٰشِعِينَ", "Faistajabna lahu wawahabna lahu yahya waaslahna lahu zawjahu innahum kanoo yusariAAoona fee alkhayrati wayadAAoonana raghaban warahaban wakanoo lana khashiAAeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 91, "And [mention] the one who guarded her chastity, so We blew into her [garment] through Our angel [Gabriel], and We made her and her son a sign for the worlds", "وَٱلَّتِيٓ أَحۡصَنَتۡ فَرۡجَهَا فَنَفَخۡنَا فِيهَا مِن رُّوحِنَا وَجَعَلۡنَٰهَا وَٱبۡنَهَآ ءَايَةٗ لِّلۡعَٰلَمِينَ", "Waallatee ahsanat farjaha fanafakhna feeha min roohina wajaAAalnaha waibnaha ayatan lilAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 92, "Indeed this, your religion, is one religion, and I am your Lord, so worship Me", "إِنَّ هَٰذِهِۦٓ أُمَّتُكُمۡ أُمَّةٗ وَٰحِدَةٗ وَأَنَا۠ رَبُّكُمۡ فَٱعۡبُدُونِ", "Inna hathihi ommatukum ommatan wahidatan waana rabbukum faoAAbudooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 93, "And [yet] they divided their affair among themselves, [but] all to Us will return", "وَتَقَطَّعُوٓاْ أَمۡرَهُم بَيۡنَهُمۡۖ كُلٌّ إِلَيۡنَا رَٰجِعُونَ", "WataqattaAAoo amrahum baynahum kullun ilayna rajiAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 94, "So whoever does righteous deeds while he is a believer - no denial will there be for his effort, and indeed We, of it, are recorders", "فَمَن يَعۡمَلۡ مِنَ ٱلصَّـٰلِحَٰتِ وَهُوَ مُؤۡمِنٞ فَلَا كُفۡرَانَ لِسَعۡيِهِۦ وَإِنَّا لَهُۥ كَٰتِبُونَ", "Faman yaAAmal mina alssalihati wahuwa muminun fala kufrana lisaAAyihi wainna lahu katiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 95, "And there is prohibition upon [the people of] a city which We have destroyed that they will [ever] return", "وَحَرَٰمٌ عَلَىٰ قَرۡيَةٍ أَهۡلَكۡنَٰهَآ أَنَّهُمۡ لَا يَرۡجِعُونَ", "Waharamun AAala qaryatin ahlaknaha annahum la yarjiAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 96, "Until when [the dam of] Gog and Magog has been opened and they, from every elevation, descend", "حَتَّىٰٓ إِذَا فُتِحَتۡ يَأۡجُوجُ وَمَأۡجُوجُ وَهُم مِّن كُلِّ حَدَبٖ يَنسِلُونَ", "Hatta itha futihat yajooju wamajooju wahum min kulli hadabin yansiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 97, "And [when] the true promise has approached; then suddenly the eyes of those who disbelieved will be staring [in horror, while they say], ""O woe to us; we had been unmindful of this; rather, we were wrongdoers", "وَٱقۡتَرَبَ ٱلۡوَعۡدُ ٱلۡحَقُّ فَإِذَا هِيَ شَٰخِصَةٌ أَبۡصَٰرُ ٱلَّذِينَ كَفَرُواْ يَٰوَيۡلَنَا قَدۡ كُنَّا فِي غَفۡلَةٖ مِّنۡ هَٰذَا بَلۡ كُنَّا ظَٰلِمِينَ", "Waiqtaraba alwaAAdu alhaqqu faitha hiya shakhisatun absaru allatheena kafaroo ya waylana qad kunna fee ghaflatin min hatha bal kunna thalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 98, "Indeed, you [disbelievers] and what you worship other than Allah are the firewood of Hell. You will be coming to [enter] it", "إِنَّكُمۡ وَمَا تَعۡبُدُونَ مِن دُونِ ٱللَّهِ حَصَبُ جَهَنَّمَ أَنتُمۡ لَهَا وَٰرِدُونَ", "Innakum wama taAAbudoona min dooni Allahi hasabu jahannama antum laha waridoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 99, "Had these [false deities] been [actual] gods, they would not have come to it, but all are eternal therein", "لَوۡ كَانَ هَـٰٓؤُلَآءِ ءَالِهَةٗ مَّا وَرَدُوهَاۖ وَكُلّٞ فِيهَا خَٰلِدُونَ", "Law kana haolai alihatan ma waradooha wakullun feeha khalidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 100, "For them therein is heavy sighing, and they therein will not hear", "لَهُمۡ فِيهَا زَفِيرٞ وَهُمۡ فِيهَا لَا يَسۡمَعُونَ", "Lahum feeha zafeerun wahum feeha la yasmaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 101, "Indeed, those for whom the best [reward] has preceded from Us - they are from it far removed", "إِنَّ ٱلَّذِينَ سَبَقَتۡ لَهُم مِّنَّا ٱلۡحُسۡنَىٰٓ أُوْلَـٰٓئِكَ عَنۡهَا مُبۡعَدُونَ", "Inna allatheena sabaqat lahum minna alhusna olaika AAanha mubAAadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 102, "They will not hear its sound, while they are, in that which their souls desire, abiding eternally", "لَا يَسۡمَعُونَ حَسِيسَهَاۖ وَهُمۡ فِي مَا ٱشۡتَهَتۡ أَنفُسُهُمۡ خَٰلِدُونَ", "La yasmaAAoona haseesaha wahum fee ma ishtahat anfusuhum khalidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 103, "They will not be grieved by the greatest terror, and the angels will meet them, [saying], ""This is your Day which you have been promised", "لَا يَحۡزُنُهُمُ ٱلۡفَزَعُ ٱلۡأَكۡبَرُ وَتَتَلَقَّىٰهُمُ ٱلۡمَلَـٰٓئِكَةُ هَٰذَا يَوۡمُكُمُ ٱلَّذِي كُنتُمۡ تُوعَدُونَ", "La yahzunuhumu alfazaAAu alakbaru watatalaqqahumu almalaikatu hatha yawmukumu allathee kuntum tooAAadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 104, "The Day when We will fold the heaven like the folding of a [written] sheet for the records. As We began the first creation, We will repeat it. [That is] a promise binding upon Us. Indeed, We will do it", "يَوۡمَ نَطۡوِي ٱلسَّمَآءَ كَطَيِّ ٱلسِّجِلِّ لِلۡكُتُبِۚ كَمَا بَدَأۡنَآ أَوَّلَ خَلۡقٖ نُّعِيدُهُۥۚ وَعۡدًا عَلَيۡنَآۚ إِنَّا كُنَّا فَٰعِلِينَ", "Yawma natwee alssamaa katayyi alssijlli lilkutubi kama badana awwala khalqin nuAAeeduhu waAAdan AAalayna inna kunna faAAileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 105, "And We have already written in the book [of Psalms] after the [previous] mention that the land [of Paradise] is inherited by My righteous servants", "وَلَقَدۡ كَتَبۡنَا فِي ٱلزَّبُورِ مِنۢ بَعۡدِ ٱلذِّكۡرِ أَنَّ ٱلۡأَرۡضَ يَرِثُهَا عِبَادِيَ ٱلصَّـٰلِحُونَ", "Walaqad katabna fee alzzaboori min baAAdi alththikri anna alarda yarithuha AAibadiya alssalihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 106, "Indeed, in this [Qur'an] is notification for a worshipping people", "إِنَّ فِي هَٰذَا لَبَلَٰغٗا لِّقَوۡمٍ عَٰبِدِينَ", "Inna fee hatha labalaghan liqawmin AAabideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 107, "And We have not sent you, [O Muhammad], except as a mercy to the worlds", "وَمَآ أَرۡسَلۡنَٰكَ إِلَّا رَحۡمَةٗ لِّلۡعَٰلَمِينَ", "Wama arsalnaka illa rahmatan lilAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 108, "Say, ""It is only revealed to me that your god is but one God; so will you be Muslims [in submission to Him]", "قُلۡ إِنَّمَا يُوحَىٰٓ إِلَيَّ أَنَّمَآ إِلَٰهُكُمۡ إِلَٰهٞ وَٰحِدٞۖ فَهَلۡ أَنتُم مُّسۡلِمُونَ", "Qul innama yooha ilayya annama ilahukum ilahun wahidun fahal antum muslimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 109, "But if they turn away, then say, ""I have announced to [all of] you equally. And I know not whether near or far is that which you are promised", "فَإِن تَوَلَّوۡاْ فَقُلۡ ءَاذَنتُكُمۡ عَلَىٰ سَوَآءٖۖ وَإِنۡ أَدۡرِيٓ أَقَرِيبٌ أَم بَعِيدٞ مَّا تُوعَدُونَ", "Fain tawallaw faqul athantukum AAala sawain wain adree aqareebun am baAAeedun ma tooAAadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 110, "Indeed, He knows what is declared of speech, and He knows what you conceal", "إِنَّهُۥ يَعۡلَمُ ٱلۡجَهۡرَ مِنَ ٱلۡقَوۡلِ وَيَعۡلَمُ مَا تَكۡتُمُونَ", "Innahu yaAAlamu aljahra mina alqawli wayaAAlamu ma taktumoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 111, "And I know not; perhaps it is a trial for you and enjoyment for a time", "وَإِنۡ أَدۡرِي لَعَلَّهُۥ فِتۡنَةٞ لَّكُمۡ وَمَتَٰعٌ إِلَىٰ حِينٖ", "Wain adree laAAallahu fitnatun lakum wamataAAun ila heenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (21, 112, "[The Prophet] has said, ""My Lord, judge [between us] in truth. And our Lord is the Most Merciful, the one whose help is sought against that which you describe", "قَٰلَ رَبِّ ٱحۡكُم بِٱلۡحَقِّۗ وَرَبُّنَا ٱلرَّحۡمَٰنُ ٱلۡمُسۡتَعَانُ عَلَىٰ مَا تَصِفُونَ", "Qala rabbi ohkum bialhaqqi warabbuna alrrahmanu almustaAAanu AAala ma tasifoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 1, "O mankind, fear your Lord. Indeed, the convulsion of the [final] Hour is a terrible thing", "يَـٰٓأَيُّهَا ٱلنَّاسُ ٱتَّقُواْ رَبَّكُمۡۚ إِنَّ زَلۡزَلَةَ ٱلسَّاعَةِ شَيۡءٌ عَظِيمٞ", "Ya ayyuha alnnasu ittaqoo rabbakum inna zalzalata alssaAAati shayon AAatheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 2, "On the Day you see it every nursing mother will be distracted from that [child] she was nursing, and every pregnant woman will abort her pregnancy, and you will see the people [appearing] intoxicated while they are not intoxicated; but the punishment of Allah is severe", "يَوۡمَ تَرَوۡنَهَا تَذۡهَلُ كُلُّ مُرۡضِعَةٍ عَمَّآ أَرۡضَعَتۡ وَتَضَعُ كُلُّ ذَاتِ حَمۡلٍ حَمۡلَهَا وَتَرَى ٱلنَّاسَ سُكَٰرَىٰ وَمَا هُم بِسُكَٰرَىٰ وَلَٰكِنَّ عَذَابَ ٱللَّهِ شَدِيدٞ", "Yawma tarawnaha tathhalu kullu murdiAAatin AAamma ardaAAat watadaAAu kullu thati hamlin hamlaha watara alnnasa sukara wama hum bisukara walakinna AAathaba Allahi shadeedun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 3, "And of the people is he who disputes about Allah without knowledge and follows every rebellious devil", "وَمِنَ ٱلنَّاسِ مَن يُجَٰدِلُ فِي ٱللَّهِ بِغَيۡرِ عِلۡمٖ وَيَتَّبِعُ كُلَّ شَيۡطَٰنٖ مَّرِيدٖ", "Wamina alnnasi man yujadilu fee Allahi bighayri AAilmin wayattabiAAu kulla shaytanin mareedin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 4, "It has been decreed for every devil that whoever turns to him - he will misguide him and will lead him to the punishment of the Blaze", "كُتِبَ عَلَيۡهِ أَنَّهُۥ مَن تَوَلَّاهُ فَأَنَّهُۥ يُضِلُّهُۥ وَيَهۡدِيهِ إِلَىٰ عَذَابِ ٱلسَّعِيرِ", "Kutiba AAalayhi annahu man tawallahu faannahu yudilluhu wayahdeehi ila AAathabi alssaAAeeri");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 5, "O People, if you should be in doubt about the Resurrection, then [consider that] indeed, We created you from dust, then from a sperm-drop, then from a clinging clot, and then from a lump of flesh, formed and unformed - that We may show you. And We settle in the wombs whom We will for a specified term, then We bring you out as a child, and then [We develop you] that you may reach your [time of] maturity. And among you is he who is taken in [early] death, and among you is he who is returned to the most decrepit [old] age so that he knows, after [once having] knowledge, nothing. And you see the earth barren, but when We send down upon it rain, it quivers and swells and grows [something] of every beautiful kind", "يَـٰٓأَيُّهَا ٱلنَّاسُ إِن كُنتُمۡ فِي رَيۡبٖ مِّنَ ٱلۡبَعۡثِ فَإِنَّا خَلَقۡنَٰكُم مِّن تُرَابٖ ثُمَّ مِن نُّطۡفَةٖ ثُمَّ مِنۡ عَلَقَةٖ ثُمَّ مِن مُّضۡغَةٖ مُّخَلَّقَةٖ وَغَيۡرِ مُخَلَّقَةٖ لِّنُبَيِّنَ لَكُمۡۚ وَنُقِرُّ فِي ٱلۡأَرۡحَامِ مَا نَشَآءُ إِلَىٰٓ أَجَلٖ مُّسَمّٗى ثُمَّ نُخۡرِجُكُمۡ طِفۡلٗا ثُمَّ لِتَبۡلُغُوٓاْ أَشُدَّكُمۡۖ وَمِنكُم مَّن يُتَوَفَّىٰ وَمِنكُم مَّن يُرَدُّ إِلَىٰٓ أَرۡذَلِ ٱلۡعُمُرِ لِكَيۡلَا يَعۡلَمَ مِنۢ بَعۡدِ عِلۡمٖ شَيۡـٔٗاۚ وَتَرَى ٱلۡأَرۡضَ هَامِدَةٗ فَإِذَآ أَنزَلۡنَا عَلَيۡهَا ٱلۡمَآءَ ٱهۡتَزَّتۡ وَرَبَتۡ وَأَنۢبَتَتۡ مِن كُلِّ زَوۡجِۭ بَهِيجٖ", "Ya ayyuha alnnasu in kuntum fee raybin mina albaAAthi fainna khalaqnakum min turabin thumma min nutfatin thumma min AAalaqatin thumma min mudghatin mukhallaqatin waghayri mukhallaqatin linubayyina lakum wanuqirru fee alarhami ma nashao ila ajalin musamman thumma nukhrijukum tiflan thumma litablughoo ashuddakum waminkum man yutawaffa waminkum man yuraddu ila arthali alAAumuri likayla yaAAlama min baAAdi AAilmin shayan watara alarda hamidatan faitha anzalna AAalayha almaa ihtazzat warabat waanbatat min kulli zawjin baheejin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 6, "That is because Allah is the Truth and because He gives life to the dead and because He is over all things competent", "ذَٰلِكَ بِأَنَّ ٱللَّهَ هُوَ ٱلۡحَقُّ وَأَنَّهُۥ يُحۡيِ ٱلۡمَوۡتَىٰ وَأَنَّهُۥ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ", "Thalika bianna Allaha huwa alhaqqu waannahu yuhyee almawta waannahu AAala kulli shayin qadeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 7, "And [that they may know] that the Hour is coming - no doubt about it - and that Allah will resurrect those in the graves", "وَأَنَّ ٱلسَّاعَةَ ءَاتِيَةٞ لَّا رَيۡبَ فِيهَا وَأَنَّ ٱللَّهَ يَبۡعَثُ مَن فِي ٱلۡقُبُورِ", "Waanna alssaAAata atiyatun la rayba feeha waanna Allaha yabAAathu man fee alquboori");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 8, "And of the people is he who disputes about Allah without knowledge or guidance or an enlightening book [from Him]", "وَمِنَ ٱلنَّاسِ مَن يُجَٰدِلُ فِي ٱللَّهِ بِغَيۡرِ عِلۡمٖ وَلَا هُدٗى وَلَا كِتَٰبٖ مُّنِيرٖ", "Wamina alnnasi man yujadilu fee Allahi bighayri AAilmin wala hudan wala kitabin muneerin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 9, "Twisting his neck [in arrogance] to mislead [people] from the way of Allah. For him in the world is disgrace, and We will make him taste on the Day of Resurrection the punishment of the Burning Fire [while it is said]", "ثَانِيَ عِطۡفِهِۦ لِيُضِلَّ عَن سَبِيلِ ٱللَّهِۖ لَهُۥ فِي ٱلدُّنۡيَا خِزۡيٞۖ وَنُذِيقُهُۥ يَوۡمَ ٱلۡقِيَٰمَةِ عَذَابَ ٱلۡحَرِيقِ", "Thaniya AAitfihi liyudilla AAan sabeeli Allahi lahu fee alddunya khizyun wanutheequhu yawma alqiyamati AAathaba alhareeqi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 10, "That is for what your hands have put forth and because Allah is not ever unjust to [His] servants", "ذَٰلِكَ بِمَا قَدَّمَتۡ يَدَاكَ وَأَنَّ ٱللَّهَ لَيۡسَ بِظَلَّـٰمٖ لِّلۡعَبِيدِ", "Thalika bima qaddamat yadaka waanna Allaha laysa bithallamin lilAAabeedi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 11, "And of the people is he who worships Allah on an edge. If he is touched by good, he is reassured by it; but if he is struck by trial, he turns on his face [to the other direction]. He has lost [this] world and the Hereafter. That is what is the manifest loss", "وَمِنَ ٱلنَّاسِ مَن يَعۡبُدُ ٱللَّهَ عَلَىٰ حَرۡفٖۖ فَإِنۡ أَصَابَهُۥ خَيۡرٌ ٱطۡمَأَنَّ بِهِۦۖ وَإِنۡ أَصَابَتۡهُ فِتۡنَةٌ ٱنقَلَبَ عَلَىٰ وَجۡهِهِۦ خَسِرَ ٱلدُّنۡيَا وَٱلۡأٓخِرَةَۚ ذَٰلِكَ هُوَ ٱلۡخُسۡرَانُ ٱلۡمُبِينُ", "Wamina alnnasi man yaAAbudu Allaha AAala harfin fain asabahu khayrun itmaanna bihi wain asabathu fitnatun inqalaba AAala wajhihi khasira alddunya waalakhirata thalika huwa alkhusranu almubeenu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 12, "He invokes instead of Allah that which neither harms him nor benefits him. That is what is the extreme error", "يَدۡعُواْ مِن دُونِ ٱللَّهِ مَا لَا يَضُرُّهُۥ وَمَا لَا يَنفَعُهُۥۚ ذَٰلِكَ هُوَ ٱلضَّلَٰلُ ٱلۡبَعِيدُ", "YadAAoo min dooni Allahi ma la yadurruhu wama la yanfaAAuhu thalika huwa alddalalu albaAAeedu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 13, "He invokes one whose harm is closer than his benefit - how wretched the protector and how wretched the associate", "يَدۡعُواْ لَمَن ضَرُّهُۥٓ أَقۡرَبُ مِن نَّفۡعِهِۦۚ لَبِئۡسَ ٱلۡمَوۡلَىٰ وَلَبِئۡسَ ٱلۡعَشِيرُ", "YadAAoo laman darruhu aqrabu min nafAAihi labisa almawla walabisa alAAasheeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 14, "Indeed, Allah will admit those who believe and do righteous deeds to gardens beneath which rivers flow. Indeed, Allah does what He intends", "إِنَّ ٱللَّهَ يُدۡخِلُ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُۚ إِنَّ ٱللَّهَ يَفۡعَلُ مَا يُرِيدُ", "Inna Allaha yudkhilu allatheena amanoo waAAamiloo alssalihati jannatin tajree min tahtiha alanharu inna Allaha yafAAalu ma yureedu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 15, "Whoever should think that Allah will not support [Prophet Muhammad] in this world and the Hereafter - let him extend a rope to the ceiling, then cut off [his breath], and let him see: will his effort remove that which enrages [him]", "مَن كَانَ يَظُنُّ أَن لَّن يَنصُرَهُ ٱللَّهُ فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِ فَلۡيَمۡدُدۡ بِسَبَبٍ إِلَى ٱلسَّمَآءِ ثُمَّ لۡيَقۡطَعۡ فَلۡيَنظُرۡ هَلۡ يُذۡهِبَنَّ كَيۡدُهُۥ مَا يَغِيظُ", "Man kana yathunnu an lan yansurahu Allahu fee alddunya waalakhirati falyamdud bisababin ila alssamai thumma liyaqtaAA falyanthur hal yuthhibanna kayduhu ma yagheethu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 16, "And thus have We sent the Qur'an down as verses of clear evidence and because Allah guides whom He intends", "وَكَذَٰلِكَ أَنزَلۡنَٰهُ ءَايَٰتِۭ بَيِّنَٰتٖ وَأَنَّ ٱللَّهَ يَهۡدِي مَن يُرِيدُ", "Wakathalika anzalnahu ayatin bayyinatin waanna Allaha yahdee man yureedu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 17, "Indeed, those who have believed and those who were Jews and the Sabeans and the Christians and the Magians and those who associated with Allah - Allah will judge between them on the Day of Resurrection. Indeed Allah is, over all things, Witness", "إِنَّ ٱلَّذِينَ ءَامَنُواْ وَٱلَّذِينَ هَادُواْ وَٱلصَّـٰبِـِٔينَ وَٱلنَّصَٰرَىٰ وَٱلۡمَجُوسَ وَٱلَّذِينَ أَشۡرَكُوٓاْ إِنَّ ٱللَّهَ يَفۡصِلُ بَيۡنَهُمۡ يَوۡمَ ٱلۡقِيَٰمَةِۚ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ شَهِيدٌ", "Inna allatheena amanoo waallatheena hadoo waalssabieena waalnnasara waalmajoosa waallatheena ashrakoo inna Allaha yafsilu baynahum yawma alqiyamati inna Allaha AAala kulli shayin shaheedun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 18, "Do you not see that to Allah prostrates whoever is in the heavens and whoever is on the earth and the sun, the moon, the stars, the mountains, the trees, the moving creatures and many of the people? But upon many the punishment has been justified. And he whom Allah humiliates - for him there is no bestower of honor. Indeed, Allah does what He wills", "أَلَمۡ تَرَ أَنَّ ٱللَّهَ يَسۡجُدُۤ لَهُۥۤ مَن فِي ٱلسَّمَٰوَٰتِ وَمَن فِي ٱلۡأَرۡضِ وَٱلشَّمۡسُ وَٱلۡقَمَرُ وَٱلنُّجُومُ وَٱلۡجِبَالُ وَٱلشَّجَرُ وَٱلدَّوَآبُّ وَكَثِيرٞ مِّنَ ٱلنَّاسِۖ وَكَثِيرٌ حَقَّ عَلَيۡهِ ٱلۡعَذَابُۗ وَمَن يُهِنِ ٱللَّهُ فَمَا لَهُۥ مِن مُّكۡرِمٍۚ إِنَّ ٱللَّهَ يَفۡعَلُ مَا يَشَآءُ۩", "Alam tara anna Allaha yasjudu lahu man fee alssamawati waman fee alardi waalshshamsu waalqamaru waalnnujoomu waaljibalu waalshshajaru waalddawabbu wakatheerun mina alnnasi wakatheerun haqqa AAalayhi alAAathabu waman yuhini Allahu fama lahu min mukrimin inna Allaha yafAAalu ma yashao");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 19, "These are two adversaries who have disputed over their Lord. But those who disbelieved will have cut out for them garments of fire. Poured upon their heads will be scalding water", "۞هَٰذَانِ خَصۡمَانِ ٱخۡتَصَمُواْ فِي رَبِّهِمۡۖ فَٱلَّذِينَ كَفَرُواْ قُطِّعَتۡ لَهُمۡ ثِيَابٞ مِّن نَّارٖ يُصَبُّ مِن فَوۡقِ رُءُوسِهِمُ ٱلۡحَمِيمُ", "Hathani khasmani ikhtasamoo fee rabbihim faallatheena kafaroo quttiAAat lahum thiyabun min narin yusabbu min fawqi ruoosihimu alhameemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 20, "By which is melted that within their bellies and [their] skins", "يُصۡهَرُ بِهِۦ مَا فِي بُطُونِهِمۡ وَٱلۡجُلُودُ", "Yusharu bihi ma fee butoonihim waaljuloodu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 21, "And for [striking] them are maces of iron", "وَلَهُم مَّقَٰمِعُ مِنۡ حَدِيدٖ", "Walahum maqamiAAu min hadeedin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 22, "Every time they want to get out of Hellfire from anguish, they will be returned to it, and [it will be said], ""Taste the punishment of the Burning Fire", "كُلَّمَآ أَرَادُوٓاْ أَن يَخۡرُجُواْ مِنۡهَا مِنۡ غَمٍّ أُعِيدُواْ فِيهَا وَذُوقُواْ عَذَابَ ٱلۡحَرِيقِ", "Kullama aradoo an yakhrujoo minha min ghammin oAAeedoo feeha wathooqoo AAathaba alhareeqi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 23, "Indeed, Allah will admit those who believe and do righteous deeds to gardens beneath which rivers flow. They will be adorned therein with bracelets of gold and pearl, and their garments therein will be silk", "إِنَّ ٱللَّهَ يُدۡخِلُ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ يُحَلَّوۡنَ فِيهَا مِنۡ أَسَاوِرَ مِن ذَهَبٖ وَلُؤۡلُؤٗاۖ وَلِبَاسُهُمۡ فِيهَا حَرِيرٞ", "Inna Allaha yudkhilu allatheena amanoo waAAamiloo alssalihati jannatin tajree min tahtiha alanharu yuhallawna feeha min asawira min thahabin waluluan walibasuhum feeha hareerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 24, "And they had been guided [in worldly life] to good speech, and they were guided to the path of the Praiseworthy", "وَهُدُوٓاْ إِلَى ٱلطَّيِّبِ مِنَ ٱلۡقَوۡلِ وَهُدُوٓاْ إِلَىٰ صِرَٰطِ ٱلۡحَمِيدِ", "Wahudoo ila alttayyibi mina alqawli wahudoo ila sirati alhameedi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 25, "Indeed, those who have disbelieved and avert [people] from the way of Allah and [from] al-Masjid al-Haram, which We made for the people - equal are the resident therein and one from outside; and [also] whoever intends [a deed] therein of deviation [in religion] or wrongdoing - We will make him taste of a painful punishment", "إِنَّ ٱلَّذِينَ كَفَرُواْ وَيَصُدُّونَ عَن سَبِيلِ ٱللَّهِ وَٱلۡمَسۡجِدِ ٱلۡحَرَامِ ٱلَّذِي جَعَلۡنَٰهُ لِلنَّاسِ سَوَآءً ٱلۡعَٰكِفُ فِيهِ وَٱلۡبَادِۚ وَمَن يُرِدۡ فِيهِ بِإِلۡحَادِۭ بِظُلۡمٖ نُّذِقۡهُ مِنۡ عَذَابٍ أَلِيمٖ", "Inna allatheena kafaroo wayasuddoona AAan sabeeli Allahi waalmasjidi alharami allathee jaAAalnahu lilnnasi sawaan alAAakifu feehi waalbadi waman yurid feehi biilhadin bithulmin nuthiqhu min AAathabin aleemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 26, "And [mention, O Muhammad], when We designated for Abraham the site of the House, [saying], ""Do not associate anything with Me and purify My House for those who perform Tawaf and those who stand [in prayer] and those who bow and prostrate", "وَإِذۡ بَوَّأۡنَا لِإِبۡرَٰهِيمَ مَكَانَ ٱلۡبَيۡتِ أَن لَّا تُشۡرِكۡ بِي شَيۡـٔٗا وَطَهِّرۡ بَيۡتِيَ لِلطَّآئِفِينَ وَٱلۡقَآئِمِينَ وَٱلرُّكَّعِ ٱلسُّجُودِ", "Waith bawwana liibraheema makana albayti an la tushrik bee shayan watahhir baytiya lilttaifeena waalqaimeena waalrrukkaAAi alssujoodi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 27, "And proclaim to the people the Hajj [pilgrimage]; they will come to you on foot and on every lean camel; they will come from every distant pass", "وَأَذِّن فِي ٱلنَّاسِ بِٱلۡحَجِّ يَأۡتُوكَ رِجَالٗا وَعَلَىٰ كُلِّ ضَامِرٖ يَأۡتِينَ مِن كُلِّ فَجٍّ عَمِيقٖ", "Waaththin fee alnnasi bialhajji yatooka rijalan waAAala kulli damirin yateena min kulli fajjin AAameeqin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 28, "That they may witness benefits for themselves and mention the name of Allah on known days over what He has provided for them of [sacrificial] animals. So eat of them and feed the miserable and poor", "لِّيَشۡهَدُواْ مَنَٰفِعَ لَهُمۡ وَيَذۡكُرُواْ ٱسۡمَ ٱللَّهِ فِيٓ أَيَّامٖ مَّعۡلُومَٰتٍ عَلَىٰ مَا رَزَقَهُم مِّنۢ بَهِيمَةِ ٱلۡأَنۡعَٰمِۖ فَكُلُواْ مِنۡهَا وَأَطۡعِمُواْ ٱلۡبَآئِسَ ٱلۡفَقِيرَ", "Liyashhadoo manafiAAa lahum wayathkuroo isma Allahi fee ayyamin maAAloomatin AAala ma razaqahum min baheemati alanAAami fakuloo minha waatAAimoo albaisa alfaqeera");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 29, "Then let them end their untidiness and fulfill their vows and perform Tawaf around the ancient House", "ثُمَّ لۡيَقۡضُواْ تَفَثَهُمۡ وَلۡيُوفُواْ نُذُورَهُمۡ وَلۡيَطَّوَّفُواْ بِٱلۡبَيۡتِ ٱلۡعَتِيقِ", "Thumma lyaqdoo tafathahum walyoofoo nuthoorahum walyattawwafoo bialbayti alAAateeqi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 30, "That [has been commanded], and whoever honors the sacred ordinances of Allah - it is best for him in the sight of his Lord. And permitted to you are the grazing livestock, except what is recited to you. So avoid the uncleanliness of idols and avoid false statement", "ذَٰلِكَۖ وَمَن يُعَظِّمۡ حُرُمَٰتِ ٱللَّهِ فَهُوَ خَيۡرٞ لَّهُۥ عِندَ رَبِّهِۦۗ وَأُحِلَّتۡ لَكُمُ ٱلۡأَنۡعَٰمُ إِلَّا مَا يُتۡلَىٰ عَلَيۡكُمۡۖ فَٱجۡتَنِبُواْ ٱلرِّجۡسَ مِنَ ٱلۡأَوۡثَٰنِ وَٱجۡتَنِبُواْ قَوۡلَ ٱلزُّورِ", "Thalika waman yuAAaththim hurumati Allahi fahuwa khayrun lahu AAinda rabbihi waohillat lakumu alanAAamu illa ma yutla AAalaykum faijtaniboo alrrijsa mina alawthani waijtaniboo qawla alzzoori");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 31, "Inclining [only] to Allah, not associating [anything] with Him. And he who associates with Allah - it is as though he had fallen from the sky and was snatched by the birds or the wind carried him down into a remote place", "حُنَفَآءَ لِلَّهِ غَيۡرَ مُشۡرِكِينَ بِهِۦۚ وَمَن يُشۡرِكۡ بِٱللَّهِ فَكَأَنَّمَا خَرَّ مِنَ ٱلسَّمَآءِ فَتَخۡطَفُهُ ٱلطَّيۡرُ أَوۡ تَهۡوِي بِهِ ٱلرِّيحُ فِي مَكَانٖ سَحِيقٖ", "Hunafaa lillahi ghayra mushrikeena bihi waman yushrik biAllahi fakaannama kharra mina alssamai fatakhtafuhu alttayru aw tahwee bihi alrreehu fee makanin saheeqin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 32, "That [is so]. And whoever honors the symbols of Allah - indeed, it is from the piety of hearts", "ذَٰلِكَۖ وَمَن يُعَظِّمۡ شَعَـٰٓئِرَ ٱللَّهِ فَإِنَّهَا مِن تَقۡوَى ٱلۡقُلُوبِ", "Thalika waman yuAAaththim shaAAaira Allahi fainnaha min taqwa alquloobi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 33, "For you the animals marked for sacrifice are benefits for a specified term; then their place of sacrifice is at the ancient House", "لَكُمۡ فِيهَا مَنَٰفِعُ إِلَىٰٓ أَجَلٖ مُّسَمّٗى ثُمَّ مَحِلُّهَآ إِلَى ٱلۡبَيۡتِ ٱلۡعَتِيقِ", "Lakum feeha manafiAAu ila ajalin musamman thumma mahilluha ila albayti alAAateeqi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 34, "And for all religion We have appointed a rite [of sacrifice] that they may mention the name of Allah over what He has provided for them of [sacrificial] animals. For your god is one God, so to Him submit. And, [O Muhammad], give good tidings to the humble [before their Lord]", "وَلِكُلِّ أُمَّةٖ جَعَلۡنَا مَنسَكٗا لِّيَذۡكُرُواْ ٱسۡمَ ٱللَّهِ عَلَىٰ مَا رَزَقَهُم مِّنۢ بَهِيمَةِ ٱلۡأَنۡعَٰمِۗ فَإِلَٰهُكُمۡ إِلَٰهٞ وَٰحِدٞ فَلَهُۥٓ أَسۡلِمُواْۗ وَبَشِّرِ ٱلۡمُخۡبِتِينَ", "Walikulli ommatin jaAAalna mansakan liyathkuroo isma Allahi AAala ma razaqahum min baheemati alanAAami failahukum ilahun wahidun falahu aslimoo wabashshiri almukhbiteena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 35, "Who, when Allah is mentioned, their hearts are fearful, and [to] the patient over what has afflicted them, and the establishers of prayer and those who spend from what We have provided them", "ٱلَّذِينَ إِذَا ذُكِرَ ٱللَّهُ وَجِلَتۡ قُلُوبُهُمۡ وَٱلصَّـٰبِرِينَ عَلَىٰ مَآ أَصَابَهُمۡ وَٱلۡمُقِيمِي ٱلصَّلَوٰةِ وَمِمَّا رَزَقۡنَٰهُمۡ يُنفِقُونَ", "Allatheena itha thukira Allahu wajilat quloobuhum waalssabireena AAala ma asabahum waalmuqeemee alssalati wamimma razaqnahum yunfiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 36, "And the camels and cattle We have appointed for you as among the symbols of Allah; for you therein is good. So mention the name of Allah upon them when lined up [for sacrifice]; and when they are [lifeless] on their sides, then eat from them and feed the needy and the beggar. Thus have We subjected them to you that you may be grateful", "وَٱلۡبُدۡنَ جَعَلۡنَٰهَا لَكُم مِّن شَعَـٰٓئِرِ ٱللَّهِ لَكُمۡ فِيهَا خَيۡرٞۖ فَٱذۡكُرُواْ ٱسۡمَ ٱللَّهِ عَلَيۡهَا صَوَآفَّۖ فَإِذَا وَجَبَتۡ جُنُوبُهَا فَكُلُواْ مِنۡهَا وَأَطۡعِمُواْ ٱلۡقَانِعَ وَٱلۡمُعۡتَرَّۚ كَذَٰلِكَ سَخَّرۡنَٰهَا لَكُمۡ لَعَلَّكُمۡ تَشۡكُرُونَ", "Waalbudna jaAAalnaha lakum min shaAAairi Allahi lakum feeha khayrun faothkuroo isma Allahi AAalayha sawaffa faitha wajabat junoobuha fakuloo minha waatAAimoo alqaniAAa waalmuAAtarra kathalika sakhkharnaha lakum laAAallakum tashkuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 37, "Their meat will not reach Allah, nor will their blood, but what reaches Him is piety from you. Thus have We subjected them to you that you may glorify Allah for that [to] which He has guided you; and give good tidings to the doers of good", "لَن يَنَالَ ٱللَّهَ لُحُومُهَا وَلَا دِمَآؤُهَا وَلَٰكِن يَنَالُهُ ٱلتَّقۡوَىٰ مِنكُمۡۚ كَذَٰلِكَ سَخَّرَهَا لَكُمۡ لِتُكَبِّرُواْ ٱللَّهَ عَلَىٰ مَا هَدَىٰكُمۡۗ وَبَشِّرِ ٱلۡمُحۡسِنِينَ", "Lan yanala Allaha luhoomuha wala dimaoha walakin yanaluhu alttaqwa minkum kathalika sakhkharaha lakum litukabbiroo Allaha AAala ma hadakum wabashshiri almuhsineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 38, "Indeed, Allah defends those who have believed. Indeed, Allah does not like everyone treacherous and ungrateful", "۞إِنَّ ٱللَّهَ يُدَٰفِعُ عَنِ ٱلَّذِينَ ءَامَنُوٓاْۗ إِنَّ ٱللَّهَ لَا يُحِبُّ كُلَّ خَوَّانٖ كَفُورٍ", "Inna Allaha yudafiAAu AAani allatheena amanoo inna Allaha la yuhibbu kulla khawwanin kafoorin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 39, "Permission [to fight] has been given to those who are being fought, because they were wronged. And indeed, Allah is competent to give them victory", "أُذِنَ لِلَّذِينَ يُقَٰتَلُونَ بِأَنَّهُمۡ ظُلِمُواْۚ وَإِنَّ ٱللَّهَ عَلَىٰ نَصۡرِهِمۡ لَقَدِيرٌ", "Othina lillatheena yuqataloona biannahum thulimoo wainna Allaha AAala nasrihim laqadeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 40, "[They are] those who have been evicted from their homes without right - only because they say, ""Our Lord is Allah."" And were it not that Allah checks the people, some by means of others, there would have been demolished monasteries, churches, synagogues, and mosques in which the name of Allah is much mentioned. And Allah will surely support those who support Him. Indeed, Allah is Powerful and Exalted in Might", "ٱلَّذِينَ أُخۡرِجُواْ مِن دِيَٰرِهِم بِغَيۡرِ حَقٍّ إِلَّآ أَن يَقُولُواْ رَبُّنَا ٱللَّهُۗ وَلَوۡلَا دَفۡعُ ٱللَّهِ ٱلنَّاسَ بَعۡضَهُم بِبَعۡضٖ لَّهُدِّمَتۡ صَوَٰمِعُ وَبِيَعٞ وَصَلَوَٰتٞ وَمَسَٰجِدُ يُذۡكَرُ فِيهَا ٱسۡمُ ٱللَّهِ كَثِيرٗاۗ وَلَيَنصُرَنَّ ٱللَّهُ مَن يَنصُرُهُۥٓۚ إِنَّ ٱللَّهَ لَقَوِيٌّ عَزِيزٌ", "Allatheena okhrijoo min diyarihim bighayri haqqin illa an yaqooloo rabbuna Allahu walawla dafAAu Allahi alnnasa baAAdahum bibaAAdin lahuddimat sawamiAAu wabiyaAAun wasalawatun wamasajidu yuthkaru feeha ismu Allahi katheeran walayansuranna Allahu man yansuruhu inna Allaha laqawiyyun AAazeezun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 41, "[And they are] those who, if We give them authority in the land, establish prayer and give zakah and enjoin what is right and forbid what is wrong. And to Allah belongs the outcome of [all] matters", "ٱلَّذِينَ إِن مَّكَّنَّـٰهُمۡ فِي ٱلۡأَرۡضِ أَقَامُواْ ٱلصَّلَوٰةَ وَءَاتَوُاْ ٱلزَّكَوٰةَ وَأَمَرُواْ بِٱلۡمَعۡرُوفِ وَنَهَوۡاْ عَنِ ٱلۡمُنكَرِۗ وَلِلَّهِ عَٰقِبَةُ ٱلۡأُمُورِ", "Allatheena in makkannahum fee alardi aqamoo alssalata waatawoo alzzakata waamaroo bialmaAAroofi wanahaw AAani almunkari walillahi AAaqibatu alomoori");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 42, "And if they deny you, [O Muhammad] - so, before them, did the people of Noah and 'Aad and Thamud deny [their prophets]", "وَإِن يُكَذِّبُوكَ فَقَدۡ كَذَّبَتۡ قَبۡلَهُمۡ قَوۡمُ نُوحٖ وَعَادٞ وَثَمُودُ", "Wain yukaththibooka faqad kaththabat qablahum qawmu noohin waAAadun wathamoodu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 43, "And the people of Abraham and the people of Lot", "وَقَوۡمُ إِبۡرَٰهِيمَ وَقَوۡمُ لُوطٖ", "Waqawmu ibraheema waqawmu lootin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 44, "And the inhabitants of Madyan. And Moses was denied, so I prolonged enjoyment for the disbelievers; then I seized them, and how [terrible] was My reproach", "وَأَصۡحَٰبُ مَدۡيَنَۖ وَكُذِّبَ مُوسَىٰۖ فَأَمۡلَيۡتُ لِلۡكَٰفِرِينَ ثُمَّ أَخَذۡتُهُمۡۖ فَكَيۡفَ كَانَ نَكِيرِ", "Waashabu madyana wakuththiba moosa faamlaytu lilkafireena thumma akhathtuhum fakayfa kana nakeeri");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 45, "And how many a city did We destroy while it was committing wrong - so it is [now] fallen into ruin - and [how many] an abandoned well and [how many] a lofty palace", "فَكَأَيِّن مِّن قَرۡيَةٍ أَهۡلَكۡنَٰهَا وَهِيَ ظَالِمَةٞ فَهِيَ خَاوِيَةٌ عَلَىٰ عُرُوشِهَا وَبِئۡرٖ مُّعَطَّلَةٖ وَقَصۡرٖ مَّشِيدٍ", "Fakaayyin min qaryatin ahlaknaha wahiya thalimatun fahiya khawiyatun AAala AAurooshiha wabirin muAAattalatin waqasrin masheedin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 46, "So have they not traveled through the earth and have hearts by which to reason and ears by which to hear? For indeed, it is not eyes that are blinded, but blinded are the hearts which are within the breasts", "أَفَلَمۡ يَسِيرُواْ فِي ٱلۡأَرۡضِ فَتَكُونَ لَهُمۡ قُلُوبٞ يَعۡقِلُونَ بِهَآ أَوۡ ءَاذَانٞ يَسۡمَعُونَ بِهَاۖ فَإِنَّهَا لَا تَعۡمَى ٱلۡأَبۡصَٰرُ وَلَٰكِن تَعۡمَى ٱلۡقُلُوبُ ٱلَّتِي فِي ٱلصُّدُورِ", "Afalam yaseeroo fee alardi fatakoona lahum quloobun yaAAqiloona biha aw athanun yasmaAAoona biha fainnaha la taAAma alabsaru walakin taAAma alquloobu allatee fee alssudoori");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 47, "And they urge you to hasten the punishment. But Allah will never fail in His promise. And indeed, a day with your Lord is like a thousand years of those which you count", "وَيَسۡتَعۡجِلُونَكَ بِٱلۡعَذَابِ وَلَن يُخۡلِفَ ٱللَّهُ وَعۡدَهُۥۚ وَإِنَّ يَوۡمًا عِندَ رَبِّكَ كَأَلۡفِ سَنَةٖ مِّمَّا تَعُدُّونَ", "WayastaAAjiloonaka bialAAathabi walan yukhlifa Allahu waAAdahu wainna yawman AAinda rabbika kaalfi sanatin mimma taAAuddoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 48, "And for how many a city did I prolong enjoyment while it was committing wrong. Then I seized it, and to Me is the [final] destination", "وَكَأَيِّن مِّن قَرۡيَةٍ أَمۡلَيۡتُ لَهَا وَهِيَ ظَالِمَةٞ ثُمَّ أَخَذۡتُهَا وَإِلَيَّ ٱلۡمَصِيرُ", "Wakaayyin min qaryatin amlaytu laha wahiya thalimatun thumma akhathtuha wailayya almaseeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 49, "Say, ""O people, I am only to you a clear warner", "قُلۡ يَـٰٓأَيُّهَا ٱلنَّاسُ إِنَّمَآ أَنَا۠ لَكُمۡ نَذِيرٞ مُّبِينٞ", "Qul ya ayyuha alnnasu innama ana lakum natheerun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 50, "And those who have believed and done righteous deeds - for them is forgiveness and noble provision", "فَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ لَهُم مَّغۡفِرَةٞ وَرِزۡقٞ كَرِيمٞ", "Faallatheena amanoo waAAamiloo alssalihati lahum maghfiratun warizqun kareemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 51, "But the ones who strove against Our verses, [seeking] to cause failure - those are the companions of Hellfire", "وَٱلَّذِينَ سَعَوۡاْ فِيٓ ءَايَٰتِنَا مُعَٰجِزِينَ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلۡجَحِيمِ", "Waallatheena saAAaw fee ayatina muAAajizeena olaika ashabu aljaheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 52, "And We did not send before you any messenger or prophet except that when he spoke [or recited], Satan threw into it [some misunderstanding]. But Allah abolishes that which Satan throws in; then Allah makes precise His verses. And Allah is Knowing and Wise", "وَمَآ أَرۡسَلۡنَا مِن قَبۡلِكَ مِن رَّسُولٖ وَلَا نَبِيٍّ إِلَّآ إِذَا تَمَنَّىٰٓ أَلۡقَى ٱلشَّيۡطَٰنُ فِيٓ أُمۡنِيَّتِهِۦ فَيَنسَخُ ٱللَّهُ مَا يُلۡقِي ٱلشَّيۡطَٰنُ ثُمَّ يُحۡكِمُ ٱللَّهُ ءَايَٰتِهِۦۗ وَٱللَّهُ عَلِيمٌ حَكِيمٞ", "Wama arsalna min qablika min rasoolin wala nabiyyin illa itha tamanna alqa alshshaytanu fee omniyyatihi fayansakhu Allahu ma yulqee alshshaytanu thumma yuhkimu Allahu ayatihi waAllahu AAaleemun hakeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 53, "[That is] so He may make what Satan throws in a trial for those within whose hearts is disease and those hard of heart. And indeed, the wrongdoers are in extreme dissension", "لِّيَجۡعَلَ مَا يُلۡقِي ٱلشَّيۡطَٰنُ فِتۡنَةٗ لِّلَّذِينَ فِي قُلُوبِهِم مَّرَضٞ وَٱلۡقَاسِيَةِ قُلُوبُهُمۡۗ وَإِنَّ ٱلظَّـٰلِمِينَ لَفِي شِقَاقِۭ بَعِيدٖ", "LiyajAAala ma yulqee alshshaytanu fitnatan lillatheena fee quloobihim maradun waalqasiyati quloobuhum wainna alththalimeena lafee shiqaqin baAAeedin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 54, "And so those who were given knowledge may know that it is the truth from your Lord and [therefore] believe in it, and their hearts humbly submit to it. And indeed is Allah the Guide of those who have believed to a straight path", "وَلِيَعۡلَمَ ٱلَّذِينَ أُوتُواْ ٱلۡعِلۡمَ أَنَّهُ ٱلۡحَقُّ مِن رَّبِّكَ فَيُؤۡمِنُواْ بِهِۦ فَتُخۡبِتَ لَهُۥ قُلُوبُهُمۡۗ وَإِنَّ ٱللَّهَ لَهَادِ ٱلَّذِينَ ءَامَنُوٓاْ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ", "WaliyaAAlama allatheena ootoo alAAilma annahu alhaqqu min rabbika fayuminoo bihi fatukhbita lahu quloobuhum wainna Allaha lahadi allatheena amanoo ila siratin mustaqeemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 55, "But those who disbelieve will not cease to be in doubt of it until the Hour comes upon them unexpectedly or there comes to them the punishment of a barren Day", "وَلَا يَزَالُ ٱلَّذِينَ كَفَرُواْ فِي مِرۡيَةٖ مِّنۡهُ حَتَّىٰ تَأۡتِيَهُمُ ٱلسَّاعَةُ بَغۡتَةً أَوۡ يَأۡتِيَهُمۡ عَذَابُ يَوۡمٍ عَقِيمٍ", "Wala yazalu allatheena kafaroo fee miryatin minhu hatta tatiyahumu alssaAAatu baghtatan aw yatiyahum AAathabu yawmin AAaqeemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 56, "[All] sovereignty that Day is for Allah; He will judge between them. So they who believed and did righteous deeds will be in the Gardens of Pleasure", "ٱلۡمُلۡكُ يَوۡمَئِذٖ لِّلَّهِ يَحۡكُمُ بَيۡنَهُمۡۚ فَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ فِي جَنَّـٰتِ ٱلنَّعِيمِ", "Almulku yawmaithin lillahi yahkumu baynahum faallatheena amanoo waAAamiloo alssalihati fee jannati alnnaAAeemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 57, "And they who disbelieved and denied Our signs - for those there will be a humiliating punishment", "وَٱلَّذِينَ كَفَرُواْ وَكَذَّبُواْ بِـَٔايَٰتِنَا فَأُوْلَـٰٓئِكَ لَهُمۡ عَذَابٞ مُّهِينٞ", "Waallatheena kafaroo wakaththaboo biayatina faolaika lahum AAathabun muheenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 58, "And those who emigrated for the cause of Allah and then were killed or died - Allah will surely provide for them a good provision. And indeed, it is Allah who is the best of providers", "وَٱلَّذِينَ هَاجَرُواْ فِي سَبِيلِ ٱللَّهِ ثُمَّ قُتِلُوٓاْ أَوۡ مَاتُواْ لَيَرۡزُقَنَّهُمُ ٱللَّهُ رِزۡقًا حَسَنٗاۚ وَإِنَّ ٱللَّهَ لَهُوَ خَيۡرُ ٱلرَّـٰزِقِينَ", "Waallatheena hajaroo fee sabeeli Allahi thumma qutiloo aw matoo layarzuqannahumu Allahu rizqan hasanan wainna Allaha lahuwa khayru alrraziqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 59, "He will surely cause them to enter an entrance with which they will be pleased, and indeed, Allah is Knowing and Forbearing", "لَيُدۡخِلَنَّهُم مُّدۡخَلٗا يَرۡضَوۡنَهُۥۚ وَإِنَّ ٱللَّهَ لَعَلِيمٌ حَلِيمٞ", "Layudkhilannahum mudkhalan yardawnahu wainna Allaha laAAaleemun haleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 60, "That [is so]. And whoever responds [to injustice] with the equivalent of that with which he was harmed and then is tyrannized - Allah will surely aid him. Indeed, Allah is Pardoning and Forgiving", "۞ذَٰلِكَۖ وَمَنۡ عَاقَبَ بِمِثۡلِ مَا عُوقِبَ بِهِۦ ثُمَّ بُغِيَ عَلَيۡهِ لَيَنصُرَنَّهُ ٱللَّهُۚ إِنَّ ٱللَّهَ لَعَفُوٌّ غَفُورٞ", "Thalika waman AAaqaba bimithli ma AAooqiba bihi thumma bughiya AAalayhi layansurannahu Allahu inna Allaha laAAafuwwun ghafoorun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 61, "That is because Allah causes the night to enter the day and causes the day to enter the night and because Allah is Hearing and Seeing", "ذَٰلِكَ بِأَنَّ ٱللَّهَ يُولِجُ ٱلَّيۡلَ فِي ٱلنَّهَارِ وَيُولِجُ ٱلنَّهَارَ فِي ٱلَّيۡلِ وَأَنَّ ٱللَّهَ سَمِيعُۢ بَصِيرٞ", "Thalika bianna Allaha yooliju allayla fee alnnahari wayooliju alnnahara fee allayli waanna Allaha sameeAAun baseerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 62, "That is because Allah is the Truth, and that which they call upon other than Him is falsehood, and because Allah is the Most High, the Grand", "ذَٰلِكَ بِأَنَّ ٱللَّهَ هُوَ ٱلۡحَقُّ وَأَنَّ مَا يَدۡعُونَ مِن دُونِهِۦ هُوَ ٱلۡبَٰطِلُ وَأَنَّ ٱللَّهَ هُوَ ٱلۡعَلِيُّ ٱلۡكَبِيرُ", "Thalika bianna Allaha huwa alhaqqu waanna ma yadAAoona min doonihi huwa albatilu waanna Allaha huwa alAAaliyyu alkabeeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 63, "Do you not see that Allah has sent down rain from the sky and the earth becomes green? Indeed, Allah is Subtle and Acquainted", "أَلَمۡ تَرَ أَنَّ ٱللَّهَ أَنزَلَ مِنَ ٱلسَّمَآءِ مَآءٗ فَتُصۡبِحُ ٱلۡأَرۡضُ مُخۡضَرَّةًۚ إِنَّ ٱللَّهَ لَطِيفٌ خَبِيرٞ", "Alam tara anna Allaha anzala mina alssamai maan fatusbihu alardu mukhdarratan inna Allaha lateefun khabeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 64, "To Him belongs what is in the heavens and what is on the earth. And indeed, Allah is the Free of need, the Praiseworthy", "لَّهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۚ وَإِنَّ ٱللَّهَ لَهُوَ ٱلۡغَنِيُّ ٱلۡحَمِيدُ", "Lahu ma fee alssamawati wama fee alardi wainna Allaha lahuwa alghaniyyu alhameedu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 65, "Do you not see that Allah has subjected to you whatever is on the earth and the ships which run through the sea by His command? And He restrains the sky from falling upon the earth, unless by His permission. Indeed Allah, to the people, is Kind and Merciful", "أَلَمۡ تَرَ أَنَّ ٱللَّهَ سَخَّرَ لَكُم مَّا فِي ٱلۡأَرۡضِ وَٱلۡفُلۡكَ تَجۡرِي فِي ٱلۡبَحۡرِ بِأَمۡرِهِۦ وَيُمۡسِكُ ٱلسَّمَآءَ أَن تَقَعَ عَلَى ٱلۡأَرۡضِ إِلَّا بِإِذۡنِهِۦٓۚ إِنَّ ٱللَّهَ بِٱلنَّاسِ لَرَءُوفٞ رَّحِيمٞ", "Alam tara anna Allaha sakhkhara lakum ma fee alardi waalfulka tajree fee albahri biamrihi wayumsiku alssamaa an taqaAAa AAala alardi illa biithnihi inna Allaha bialnnasi laraoofun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 66, "And He is the one who gave you life; then He causes you to die and then will [again] give you life. Indeed, mankind is ungrateful", "وَهُوَ ٱلَّذِيٓ أَحۡيَاكُمۡ ثُمَّ يُمِيتُكُمۡ ثُمَّ يُحۡيِيكُمۡۗ إِنَّ ٱلۡإِنسَٰنَ لَكَفُورٞ", "Wahuwa allathee ahyakum thumma yumeetukum thumma yuhyeekum inna alinsana lakafoorun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 67, "For every religion We have appointed rites which they perform. So, [O Muhammad], let the disbelievers not contend with you over the matter but invite them to your Lord. Indeed, you are upon straight guidance", "لِّكُلِّ أُمَّةٖ جَعَلۡنَا مَنسَكًا هُمۡ نَاسِكُوهُۖ فَلَا يُنَٰزِعُنَّكَ فِي ٱلۡأَمۡرِۚ وَٱدۡعُ إِلَىٰ رَبِّكَۖ إِنَّكَ لَعَلَىٰ هُدٗى مُّسۡتَقِيمٖ", "Likulli ommatin jaAAalna mansakan hum nasikoohu fala yunaziAAunnaka fee alamri waodAAu ila rabbika innaka laAAala hudan mustaqeemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 68, "And if they dispute with you, then say, ""Allah is most knowing of what you do", "وَإِن جَٰدَلُوكَ فَقُلِ ٱللَّهُ أَعۡلَمُ بِمَا تَعۡمَلُونَ", "Wain jadalooka faquli Allahu aAAlamu bima taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 69, "Allah will judge between you on the Day of Resurrection concerning that over which you used to differ", "ٱللَّهُ يَحۡكُمُ بَيۡنَكُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ فِيمَا كُنتُمۡ فِيهِ تَخۡتَلِفُونَ", "Allahu yahkumu baynakum yawma alqiyamati feema kuntum feehi takhtalifoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 70, "Do you not know that Allah knows what is in the heaven and earth? Indeed, that is in a Record. Indeed that, for Allah, is easy", "أَلَمۡ تَعۡلَمۡ أَنَّ ٱللَّهَ يَعۡلَمُ مَا فِي ٱلسَّمَآءِ وَٱلۡأَرۡضِۚ إِنَّ ذَٰلِكَ فِي كِتَٰبٍۚ إِنَّ ذَٰلِكَ عَلَى ٱللَّهِ يَسِيرٞ", "Alam taAAlam anna Allaha yaAAlamu ma fee alssamai waalardi inna thalika fee kitabin inna thalika AAala Allahi yaseerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 71, "And they worship besides Allah that for which He has not sent down authority and that of which they have no knowledge. And there will not be for the wrongdoers any helper", "وَيَعۡبُدُونَ مِن دُونِ ٱللَّهِ مَا لَمۡ يُنَزِّلۡ بِهِۦ سُلۡطَٰنٗا وَمَا لَيۡسَ لَهُم بِهِۦ عِلۡمٞۗ وَمَا لِلظَّـٰلِمِينَ مِن نَّصِيرٖ", "WayaAAbudoona min dooni Allahi ma lam yunazzil bihi sultanan wama laysa lahum bihi AAilmun wama lilththalimeena min naseerin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 72, "And when Our verses are recited to them as clear evidences, you recognize in the faces of those who disbelieve disapproval. They are almost on the verge of assaulting those who recite to them Our verses. Say, ""Then shall I inform you of [what is] worse than that? [It is] the Fire which Allah has promised those who disbelieve, and wretched is the destination", "وَإِذَا تُتۡلَىٰ عَلَيۡهِمۡ ءَايَٰتُنَا بَيِّنَٰتٖ تَعۡرِفُ فِي وُجُوهِ ٱلَّذِينَ كَفَرُواْ ٱلۡمُنكَرَۖ يَكَادُونَ يَسۡطُونَ بِٱلَّذِينَ يَتۡلُونَ عَلَيۡهِمۡ ءَايَٰتِنَاۗ قُلۡ أَفَأُنَبِّئُكُم بِشَرّٖ مِّن ذَٰلِكُمُۚ ٱلنَّارُ وَعَدَهَا ٱللَّهُ ٱلَّذِينَ كَفَرُواْۖ وَبِئۡسَ ٱلۡمَصِيرُ", "Waitha tutla AAalayhim ayatuna bayyinatin taAArifu fee wujoohi allatheena kafaroo almunkara yakadoona yastoona biallatheena yatloona AAalayhim ayatina qul afaonabbiokum bisharrin min thalikum alnnaru waAAadaha Allahu allatheena kafaroo wabisa almaseeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 73, "O people, an example is presented, so listen to it. Indeed, those you invoke besides Allah will never create [as much as] a fly, even if they gathered together for that purpose. And if the fly should steal away from them a [tiny] thing, they could not recover it from him. Weak are the pursuer and pursued", "يَـٰٓأَيُّهَا ٱلنَّاسُ ضُرِبَ مَثَلٞ فَٱسۡتَمِعُواْ لَهُۥٓۚ إِنَّ ٱلَّذِينَ تَدۡعُونَ مِن دُونِ ٱللَّهِ لَن يَخۡلُقُواْ ذُبَابٗا وَلَوِ ٱجۡتَمَعُواْ لَهُۥۖ وَإِن يَسۡلُبۡهُمُ ٱلذُّبَابُ شَيۡـٔٗا لَّا يَسۡتَنقِذُوهُ مِنۡهُۚ ضَعُفَ ٱلطَّالِبُ وَٱلۡمَطۡلُوبُ", "Ya ayyuha alnnasu duriba mathalun faistamiAAoo lahu inna allatheena tadAAoona min dooni Allahi lan yakhluqoo thubaban walawi ijtamaAAoo lahu wain yaslubuhumu alththubabu shayan la yastanqithoohu minhu daAAufa alttalibu waalmatloobu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 74, "They have not appraised Allah with true appraisal. Indeed, Allah is Powerful and Exalted in Might", "مَا قَدَرُواْ ٱللَّهَ حَقَّ قَدۡرِهِۦٓۚ إِنَّ ٱللَّهَ لَقَوِيٌّ عَزِيزٌ", "Ma qadaroo Allaha haqqa qadrihi inna Allaha laqawiyyun AAazeezun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 75, "Allah chooses from the angels messengers and from the people. Indeed, Allah is Hearing and Seeing", "ٱللَّهُ يَصۡطَفِي مِنَ ٱلۡمَلَـٰٓئِكَةِ رُسُلٗا وَمِنَ ٱلنَّاسِۚ إِنَّ ٱللَّهَ سَمِيعُۢ بَصِيرٞ", "Allahu yastafee mina almalaikati rusulan wamina alnnasi inna Allaha sameeAAun baseerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 76, "He knows what is [presently] before them and what will be after them. And to Allah will be returned [all] matters", "يَعۡلَمُ مَا بَيۡنَ أَيۡدِيهِمۡ وَمَا خَلۡفَهُمۡۚ وَإِلَى ٱللَّهِ تُرۡجَعُ ٱلۡأُمُورُ", "YaAAlamu ma bayna aydeehim wama khalfahum waila Allahi turjaAAu alomooru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 77, "O you who have believed, bow and prostrate and worship your Lord and do good - that you may succeed", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱرۡكَعُواْ وَٱسۡجُدُواْۤ وَٱعۡبُدُواْ رَبَّكُمۡ وَٱفۡعَلُواْ ٱلۡخَيۡرَ لَعَلَّكُمۡ تُفۡلِحُونَ۩", "Ya ayyuha allatheena amanoo irkaAAoo waosjudoo waoAAbudoo rabbakum waifAAaloo alkhayra laAAallakum tuflihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (22, 78, "And strive for Allah with the striving due to Him. He has chosen you and has not placed upon you in the religion any difficulty. [It is] the religion of your father, Abraham. Allah named you ""Muslims"" before [in former scriptures] and in this [revelation] that the Messenger may be a witness over you and you may be witnesses over the people. So establish prayer and give zakah and hold fast to Allah. He is your protector; and excellent is the protector, and excellent is the helper", "وَجَٰهِدُواْ فِي ٱللَّهِ حَقَّ جِهَادِهِۦۚ هُوَ ٱجۡتَبَىٰكُمۡ وَمَا جَعَلَ عَلَيۡكُمۡ فِي ٱلدِّينِ مِنۡ حَرَجٖۚ مِّلَّةَ أَبِيكُمۡ إِبۡرَٰهِيمَۚ هُوَ سَمَّىٰكُمُ ٱلۡمُسۡلِمِينَ مِن قَبۡلُ وَفِي هَٰذَا لِيَكُونَ ٱلرَّسُولُ شَهِيدًا عَلَيۡكُمۡ وَتَكُونُواْ شُهَدَآءَ عَلَى ٱلنَّاسِۚ فَأَقِيمُواْ ٱلصَّلَوٰةَ وَءَاتُواْ ٱلزَّكَوٰةَ وَٱعۡتَصِمُواْ بِٱللَّهِ هُوَ مَوۡلَىٰكُمۡۖ فَنِعۡمَ ٱلۡمَوۡلَىٰ وَنِعۡمَ ٱلنَّصِيرُ", "Wajahidoo fee Allahi haqqa jihadihi huwa ijtabakum wama jaAAala AAalaykum fee alddeeni min harajin millata abeekum ibraheema huwa sammakumu almuslimeena min qablu wafee hatha liyakoona alrrasoolu shaheedan AAalaykum watakoonoo shuhadaa AAala alnnasi faaqeemoo alssalata waatoo alzzakata waiAAtasimoo biAllahi huwa mawlakum faniAAma almawla waniAAma alnnaseeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 1, "Certainly will the believers have succeeded", "قَدۡ أَفۡلَحَ ٱلۡمُؤۡمِنُونَ", "Qad aflaha almuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 2, "They who are during their prayer humbly submissive", "ٱلَّذِينَ هُمۡ فِي صَلَاتِهِمۡ خَٰشِعُونَ", "Allatheena hum fee salatihim khashiAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 3, "And they who turn away from ill speech", "وَٱلَّذِينَ هُمۡ عَنِ ٱللَّغۡوِ مُعۡرِضُونَ", "Waallatheena hum AAani allaghwi muAAridoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 4, "And they who are observant of zakah", "وَٱلَّذِينَ هُمۡ لِلزَّكَوٰةِ فَٰعِلُونَ", "Waallatheena hum lilzzakati faAAiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 5, "And they who guard their private parts", "وَٱلَّذِينَ هُمۡ لِفُرُوجِهِمۡ حَٰفِظُونَ", "Waallatheena hum lifuroojihim hafithoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 6, "Except from their wives or those their right hands possess, for indeed, they will not be blamed", "إِلَّا عَلَىٰٓ أَزۡوَٰجِهِمۡ أَوۡ مَا مَلَكَتۡ أَيۡمَٰنُهُمۡ فَإِنَّهُمۡ غَيۡرُ مَلُومِينَ", "Illa AAala azwajihim aw ma malakat aymanuhum fainnahum ghayru maloomeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 7, "But whoever seeks beyond that, then those are the transgressors", "فَمَنِ ٱبۡتَغَىٰ وَرَآءَ ذَٰلِكَ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡعَادُونَ", "Famani ibtagha waraa thalika faolaika humu alAAadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 8, "And they who are to their trusts and their promises attentive", "وَٱلَّذِينَ هُمۡ لِأَمَٰنَٰتِهِمۡ وَعَهۡدِهِمۡ رَٰعُونَ", "Waallatheena hum liamanatihim waAAahdihim raAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 9, "And they who carefully maintain their prayers", "وَٱلَّذِينَ هُمۡ عَلَىٰ صَلَوَٰتِهِمۡ يُحَافِظُونَ", "Waallatheena hum AAala salawatihim yuhafithoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 10, "Those are the inheritors", "أُوْلَـٰٓئِكَ هُمُ ٱلۡوَٰرِثُونَ", "Olaika humu alwarithoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 11, "Who will inherit al-Firdaus. They will abide therein eternally", "ٱلَّذِينَ يَرِثُونَ ٱلۡفِرۡدَوۡسَ هُمۡ فِيهَا خَٰلِدُونَ", "Allatheena yarithoona alfirdawsa hum feeha khalidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 12, "And certainly did We create man from an extract of clay", "وَلَقَدۡ خَلَقۡنَا ٱلۡإِنسَٰنَ مِن سُلَٰلَةٖ مِّن طِينٖ", "Walaqad khalaqna alinsana min sulalatin min teenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 13, "Then We placed him as a sperm-drop in a firm lodging", "ثُمَّ جَعَلۡنَٰهُ نُطۡفَةٗ فِي قَرَارٖ مَّكِينٖ", "Thumma jaAAalnahu nutfatan fee qararin makeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 14, "Then We made the sperm-drop into a clinging clot, and We made the clot into a lump [of flesh], and We made [from] the lump, bones, and We covered the bones with flesh; then We developed him into another creation. So blessed is Allah, the best of creators", "ثُمَّ خَلَقۡنَا ٱلنُّطۡفَةَ عَلَقَةٗ فَخَلَقۡنَا ٱلۡعَلَقَةَ مُضۡغَةٗ فَخَلَقۡنَا ٱلۡمُضۡغَةَ عِظَٰمٗا فَكَسَوۡنَا ٱلۡعِظَٰمَ لَحۡمٗا ثُمَّ أَنشَأۡنَٰهُ خَلۡقًا ءَاخَرَۚ فَتَبَارَكَ ٱللَّهُ أَحۡسَنُ ٱلۡخَٰلِقِينَ", "Thumma khalaqna alnnutfata AAalaqatan fakhalaqna alAAalaqata mudghatan fakhalaqna almudghata AAithaman fakasawna alAAithama lahman thumma anshanahu khalqan akhara fatabaraka Allahu ahsanu alkhaliqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 15, "Then indeed, after that you are to die", "ثُمَّ إِنَّكُم بَعۡدَ ذَٰلِكَ لَمَيِّتُونَ", "Thumma innakum baAAda thalika lamayyitoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 16, "Then indeed you, on the Day of Resurrection, will be resurrected", "ثُمَّ إِنَّكُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ تُبۡعَثُونَ", "Thumma innakum yawma alqiyamati tubAAathoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 17, "And We have created above you seven layered heavens, and never have We been of [Our] creation unaware", "وَلَقَدۡ خَلَقۡنَا فَوۡقَكُمۡ سَبۡعَ طَرَآئِقَ وَمَا كُنَّا عَنِ ٱلۡخَلۡقِ غَٰفِلِينَ", "Walaqad khalaqna fawqakum sabAAa taraiqa wama kunna AAani alkhalqi ghafileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 18, "And We have sent down rain from the sky in a measured amount and settled it in the earth. And indeed, We are Able to take it away", "وَأَنزَلۡنَا مِنَ ٱلسَّمَآءِ مَآءَۢ بِقَدَرٖ فَأَسۡكَنَّـٰهُ فِي ٱلۡأَرۡضِۖ وَإِنَّا عَلَىٰ ذَهَابِۭ بِهِۦ لَقَٰدِرُونَ", "Waanzalna mina alssamai maan biqadarin faaskannahu fee alardi wainna AAala thahabin bihi laqadiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 19, "And We brought forth for you thereby gardens of palm trees and grapevines in which for you are abundant fruits and from which you eat", "فَأَنشَأۡنَا لَكُم بِهِۦ جَنَّـٰتٖ مِّن نَّخِيلٖ وَأَعۡنَٰبٖ لَّكُمۡ فِيهَا فَوَٰكِهُ كَثِيرَةٞ وَمِنۡهَا تَأۡكُلُونَ", "Faanshana lakum bihi jannatin min nakheelin waaAAnabin lakum feeha fawakihu katheeratun waminha takuloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 20, "And [We brought forth] a tree issuing from Mount Sinai which produces oil and food for those who eat", "وَشَجَرَةٗ تَخۡرُجُ مِن طُورِ سَيۡنَآءَ تَنۢبُتُ بِٱلدُّهۡنِ وَصِبۡغٖ لِّلۡأٓكِلِينَ", "Washajaratan takhruju min toori saynaa tanbutu bialdduhni wasibghin lilakileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 21, "And indeed, for you in livestock is a lesson. We give you drink from that which is in their bellies, and for you in them are numerous benefits, and from them you eat", "وَإِنَّ لَكُمۡ فِي ٱلۡأَنۡعَٰمِ لَعِبۡرَةٗۖ نُّسۡقِيكُم مِّمَّا فِي بُطُونِهَا وَلَكُمۡ فِيهَا مَنَٰفِعُ كَثِيرَةٞ وَمِنۡهَا تَأۡكُلُونَ", "Wainna lakum fee alanAAami laAAibratan nusqeekum mimma fee butooniha walakum feeha manafiAAu katheeratun waminha takuloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 22, "And upon them and on ships you are carried", "وَعَلَيۡهَا وَعَلَى ٱلۡفُلۡكِ تُحۡمَلُونَ", "WaAAalayha waAAala alfulki tuhmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 23, "And We had certainly sent Noah to his people, and he said, ""O my people, worship Allah; you have no deity other than Him; then will you not fear Him", "وَلَقَدۡ أَرۡسَلۡنَا نُوحًا إِلَىٰ قَوۡمِهِۦ فَقَالَ يَٰقَوۡمِ ٱعۡبُدُواْ ٱللَّهَ مَا لَكُم مِّنۡ إِلَٰهٍ غَيۡرُهُۥٓۚ أَفَلَا تَتَّقُونَ", "Walaqad arsalna noohan ila qawmihi faqala ya qawmi oAAbudoo Allaha ma lakum min ilahin ghayruhu afala tattaqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 24, "But the eminent among those who disbelieved from his people said, ""This is not but a man like yourselves who wishes to take precedence over you; and if Allah had willed [to send a messenger], He would have sent down angels. We have not heard of this among our forefathers", "فَقَالَ ٱلۡمَلَؤُاْ ٱلَّذِينَ كَفَرُواْ مِن قَوۡمِهِۦ مَا هَٰذَآ إِلَّا بَشَرٞ مِّثۡلُكُمۡ يُرِيدُ أَن يَتَفَضَّلَ عَلَيۡكُمۡ وَلَوۡ شَآءَ ٱللَّهُ لَأَنزَلَ مَلَـٰٓئِكَةٗ مَّا سَمِعۡنَا بِهَٰذَا فِيٓ ءَابَآئِنَا ٱلۡأَوَّلِينَ", "Faqala almalao allatheena kafaroo min qawmihi ma hatha illa basharun mithlukum yureedu an yatafaddala AAalaykum walaw shaa Allahu laanzala malaikatan ma samiAAna bihatha fee abaina alawwaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 25, "He is not but a man possessed with madness, so wait concerning him for a time", "إِنۡ هُوَ إِلَّا رَجُلُۢ بِهِۦ جِنَّةٞ فَتَرَبَّصُواْ بِهِۦ حَتَّىٰ حِينٖ", "In huwa illa rajulun bihi jinnatun fatarabbasoo bihi hatta heenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 26, "[Noah] said, ""My Lord, support me because they have denied me", "قَالَ رَبِّ ٱنصُرۡنِي بِمَا كَذَّبُونِ", "Qala rabbi onsurnee bima kaththabooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 27, "So We inspired to him, ""Construct the ship under Our observation, and Our inspiration, and when Our command comes and the oven overflows, put into the ship from each [creature] two mates and your family, except those for whom the decree [of destruction] has proceeded. And do not address Me concerning those who have wronged; indeed, they are to be drowned", "فَأَوۡحَيۡنَآ إِلَيۡهِ أَنِ ٱصۡنَعِ ٱلۡفُلۡكَ بِأَعۡيُنِنَا وَوَحۡيِنَا فَإِذَا جَآءَ أَمۡرُنَا وَفَارَ ٱلتَّنُّورُ فَٱسۡلُكۡ فِيهَا مِن كُلّٖ زَوۡجَيۡنِ ٱثۡنَيۡنِ وَأَهۡلَكَ إِلَّا مَن سَبَقَ عَلَيۡهِ ٱلۡقَوۡلُ مِنۡهُمۡۖ وَلَا تُخَٰطِبۡنِي فِي ٱلَّذِينَ ظَلَمُوٓاْ إِنَّهُم مُّغۡرَقُونَ", "Faawhayna ilayhi ani isnaAAi alfulka biaAAyunina wawahyina faitha jaa amruna wafara alttannooru faosluk feeha min kullin zawjayni ithnayni waahlaka illa man sabaqa AAalayhi alqawlu minhum wala tukhatibnee fee allatheena thalamoo innahum mughraqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 28, "And when you have boarded the ship, you and those with you, then say, 'Praise to Allah who has saved us from the wrongdoing people", "فَإِذَا ٱسۡتَوَيۡتَ أَنتَ وَمَن مَّعَكَ عَلَى ٱلۡفُلۡكِ فَقُلِ ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِي نَجَّىٰنَا مِنَ ٱلۡقَوۡمِ ٱلظَّـٰلِمِينَ", "Faitha istawayta anta waman maAAaka AAala alfulki faquli alhamdu lillahi allathee najjana mina alqawmi alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 29, "And say, 'My Lord, let me land at a blessed landing place, and You are the best to accommodate [us]", "وَقُل رَّبِّ أَنزِلۡنِي مُنزَلٗا مُّبَارَكٗا وَأَنتَ خَيۡرُ ٱلۡمُنزِلِينَ", "Waqul rabbi anzilnee munzalan mubarakan waanta khayru almunzileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 30, "Indeed in that are signs, and indeed, We are ever testing [Our servants]", "إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ وَإِن كُنَّا لَمُبۡتَلِينَ", "Inna fee thalika laayatin wain kunna lamubtaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 31, "Then We produced after them a generation of others", "ثُمَّ أَنشَأۡنَا مِنۢ بَعۡدِهِمۡ قَرۡنًا ءَاخَرِينَ", "Thumma anshana min baAAdihim qarnan akhareena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 32, "And We sent among them a messenger from themselves, [saying], ""Worship Allah; you have no deity other than Him; then will you not fear Him", "فَأَرۡسَلۡنَا فِيهِمۡ رَسُولٗا مِّنۡهُمۡ أَنِ ٱعۡبُدُواْ ٱللَّهَ مَا لَكُم مِّنۡ إِلَٰهٍ غَيۡرُهُۥٓۚ أَفَلَا تَتَّقُونَ", "Faarsalna feehim rasoolan minhum ani oAAbudoo Allaha ma lakum min ilahin ghayruhu afala tattaqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 33, "And the eminent among his people who disbelieved and denied the meeting of the Hereafter while We had given them luxury in the worldly life said, ""This is not but a man like yourselves. He eats of that from which you eat and drinks of what you drink", "وَقَالَ ٱلۡمَلَأُ مِن قَوۡمِهِ ٱلَّذِينَ كَفَرُواْ وَكَذَّبُواْ بِلِقَآءِ ٱلۡأٓخِرَةِ وَأَتۡرَفۡنَٰهُمۡ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا مَا هَٰذَآ إِلَّا بَشَرٞ مِّثۡلُكُمۡ يَأۡكُلُ مِمَّا تَأۡكُلُونَ مِنۡهُ وَيَشۡرَبُ مِمَّا تَشۡرَبُونَ", "Waqala almalao min qawmihi allatheena kafaroo wakaththaboo biliqai alakhirati waatrafnahum fee alhayati alddunya ma hatha illa basharun mithlukum yakulu mimma takuloona minhu wayashrabu mimma tashraboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 34, "And if you should obey a man like yourselves, indeed, you would then be losers", "وَلَئِنۡ أَطَعۡتُم بَشَرٗا مِّثۡلَكُمۡ إِنَّكُمۡ إِذٗا لَّخَٰسِرُونَ", "Walain ataAAtum basharan mithlakum innakum ithan lakhasiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 35, "Does he promise you that when you have died and become dust and bones that you will be brought forth [once more]", "أَيَعِدُكُمۡ أَنَّكُمۡ إِذَا مِتُّمۡ وَكُنتُمۡ تُرَابٗا وَعِظَٰمًا أَنَّكُم مُّخۡرَجُونَ", "AyaAAidukum annakum itha mittum wakuntum turaban waAAithaman annakum mukhrajoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 36, "How far, how far, is that which you are promised", "۞هَيۡهَاتَ هَيۡهَاتَ لِمَا تُوعَدُونَ", "Hayhata hayhata lima tooAAadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 37, "Life is not but our worldly life - we die and live, but we will not be resurrected", "إِنۡ هِيَ إِلَّا حَيَاتُنَا ٱلدُّنۡيَا نَمُوتُ وَنَحۡيَا وَمَا نَحۡنُ بِمَبۡعُوثِينَ", "In hiya illa hayatuna alddunya namootu wanahya wama nahnu bimabAAootheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 38, "He is not but a man who has invented a lie about Allah, and we will not believe him", "إِنۡ هُوَ إِلَّا رَجُلٌ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبٗا وَمَا نَحۡنُ لَهُۥ بِمُؤۡمِنِينَ", "In huwa illa rajulun iftara AAala Allahi kathiban wama nahnu lahu bimumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 39, "He said, ""My Lord, support me because they have denied me", "قَالَ رَبِّ ٱنصُرۡنِي بِمَا كَذَّبُونِ", "Qala rabbi onsurnee bima kaththabooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 40, "[Allah] said, ""After a little, they will surely become regretful", "قَالَ عَمَّا قَلِيلٖ لَّيُصۡبِحُنَّ نَٰدِمِينَ", "Qala AAamma qaleelin layusbihunna nadimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 41, "So the shriek seized them in truth, and We made them as [plant] stubble. Then away with the wrongdoing people", "فَأَخَذَتۡهُمُ ٱلصَّيۡحَةُ بِٱلۡحَقِّ فَجَعَلۡنَٰهُمۡ غُثَآءٗۚ فَبُعۡدٗا لِّلۡقَوۡمِ ٱلظَّـٰلِمِينَ", "Faakhathathumu alssayhatu bialhaqqi fajaAAalnahum ghuthaan fabuAAdan lilqawmi alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 42, "Then We produced after them other generations", "ثُمَّ أَنشَأۡنَا مِنۢ بَعۡدِهِمۡ قُرُونًا ءَاخَرِينَ", "Thumma anshana min baAAdihim quroonan akhareena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 43, "No nation will precede its time [of termination], nor will they remain [thereafter]", "مَا تَسۡبِقُ مِنۡ أُمَّةٍ أَجَلَهَا وَمَا يَسۡتَـٔۡخِرُونَ", "Ma tasbiqu min ommatin ajalaha wama yastakhiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 44, "Then We sent Our messengers in succession. Every time there came to a nation its messenger, they denied him, so We made them follow one another [to destruction], and We made them narrations. So away with a people who do not believe", "ثُمَّ أَرۡسَلۡنَا رُسُلَنَا تَتۡرَاۖ كُلَّ مَا جَآءَ أُمَّةٗ رَّسُولُهَا كَذَّبُوهُۖ فَأَتۡبَعۡنَا بَعۡضَهُم بَعۡضٗا وَجَعَلۡنَٰهُمۡ أَحَادِيثَۚ فَبُعۡدٗا لِّقَوۡمٖ لَّا يُؤۡمِنُونَ", "Thumma arsalna rusulana tatra kulla ma jaa ommatan rasooluha kaththaboohu faatbaAAna baAAdahum baAAdan wajaAAalnahum ahadeetha fabuAAdan liqawmin la yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 45, "Then We sent Moses and his brother Aaron with Our signs and a clear authority", "ثُمَّ أَرۡسَلۡنَا مُوسَىٰ وَأَخَاهُ هَٰرُونَ بِـَٔايَٰتِنَا وَسُلۡطَٰنٖ مُّبِينٍ", "Thumma arsalna moosa waakhahu haroona biayatina wasultanin mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 46, "To Pharaoh and his establishment, but they were arrogant and were a haughty people", "إِلَىٰ فِرۡعَوۡنَ وَمَلَإِيْهِۦ فَٱسۡتَكۡبَرُواْ وَكَانُواْ قَوۡمًا عَالِينَ", "Ila firAAawna wamalaihi faistakbaroo wakanoo qawman AAaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 47, "They said, ""Should we believe two men like ourselves while their people are for us in servitude", "فَقَالُوٓاْ أَنُؤۡمِنُ لِبَشَرَيۡنِ مِثۡلِنَا وَقَوۡمُهُمَا لَنَا عَٰبِدُونَ", "Faqaloo anuminu libasharayni mithlina waqawmuhuma lana AAabidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 48, "So they denied them and were of those destroyed", "فَكَذَّبُوهُمَا فَكَانُواْ مِنَ ٱلۡمُهۡلَكِينَ", "Fakaththaboohuma fakanoo mina almuhlakeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 49, "And We certainly gave Moses the Scripture that perhaps they would be guided", "وَلَقَدۡ ءَاتَيۡنَا مُوسَى ٱلۡكِتَٰبَ لَعَلَّهُمۡ يَهۡتَدُونَ", "Walaqad atayna moosa alkitaba laAAallahum yahtadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 50, "And We made the son of Mary and his mother a sign and sheltered them within a high ground having level [areas] and flowing water", "وَجَعَلۡنَا ٱبۡنَ مَرۡيَمَ وَأُمَّهُۥٓ ءَايَةٗ وَءَاوَيۡنَٰهُمَآ إِلَىٰ رَبۡوَةٖ ذَاتِ قَرَارٖ وَمَعِينٖ", "WajaAAalna ibna maryama waommahu ayatan waawaynahuma ila rabwatin thati qararin wamaAAeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 51, "[Allah said], ""O messengers, eat from the good foods and work righteousness. Indeed, I, of what you do, am Knowing", "يَـٰٓأَيُّهَا ٱلرُّسُلُ كُلُواْ مِنَ ٱلطَّيِّبَٰتِ وَٱعۡمَلُواْ صَٰلِحًاۖ إِنِّي بِمَا تَعۡمَلُونَ عَلِيمٞ", "Ya ayyuha alrrusulu kuloo mina alttayyibati waiAAmaloo salihan innee bima taAAmaloona AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 52, "And indeed this, your religion, is one religion, and I am your Lord, so fear Me", "وَإِنَّ هَٰذِهِۦٓ أُمَّتُكُمۡ أُمَّةٗ وَٰحِدَةٗ وَأَنَا۠ رَبُّكُمۡ فَٱتَّقُونِ", "Wainna hathihi ommatukum ommatan wahidatan waana rabbukum faittaqooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 53, "But the people divided their religion among them into sects - each faction, in what it has, rejoicing", "فَتَقَطَّعُوٓاْ أَمۡرَهُم بَيۡنَهُمۡ زُبُرٗاۖ كُلُّ حِزۡبِۭ بِمَا لَدَيۡهِمۡ فَرِحُونَ", "FataqattaAAoo amrahum baynahum zuburan kullu hizbin bima ladayhim farihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 54, "So leave them in their confusion for a time", "فَذَرۡهُمۡ فِي غَمۡرَتِهِمۡ حَتَّىٰ حِينٍ", "Fatharhum fee ghamratihim hatta heenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 55, "Do they think that what We extend to them of wealth and children", "أَيَحۡسَبُونَ أَنَّمَا نُمِدُّهُم بِهِۦ مِن مَّالٖ وَبَنِينَ", "Ayahsaboona annama numidduhum bihi min malin wabaneena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 56, "Is [because] We hasten for them good things? Rather, they do not perceive", "نُسَارِعُ لَهُمۡ فِي ٱلۡخَيۡرَٰتِۚ بَل لَّا يَشۡعُرُونَ", "NusariAAu lahum fee alkhayrati bal la yashAAuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 57, "Indeed, they who are apprehensive from fear of their Lord", "إِنَّ ٱلَّذِينَ هُم مِّنۡ خَشۡيَةِ رَبِّهِم مُّشۡفِقُونَ", "Inna allatheena hum min khashyati rabbihim mushfiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 58, "And they who believe in the signs of their Lord", "وَٱلَّذِينَ هُم بِـَٔايَٰتِ رَبِّهِمۡ يُؤۡمِنُونَ", "Waallatheena hum biayati rabbihim yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 59, "And they who do not associate anything with their Lord", "وَٱلَّذِينَ هُم بِرَبِّهِمۡ لَا يُشۡرِكُونَ", "Waallatheena hum birabbihim la yushrikoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 60, "And they who give what they give while their hearts are fearful because they will be returning to their Lord", "وَٱلَّذِينَ يُؤۡتُونَ مَآ ءَاتَواْ وَّقُلُوبُهُمۡ وَجِلَةٌ أَنَّهُمۡ إِلَىٰ رَبِّهِمۡ رَٰجِعُونَ", "Waallatheena yutoona ma ataw waquloobuhum wajilatun annahum ila rabbihim rajiAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 61, "It is those who hasten to good deeds, and they outstrip [others] therein", "أُوْلَـٰٓئِكَ يُسَٰرِعُونَ فِي ٱلۡخَيۡرَٰتِ وَهُمۡ لَهَا سَٰبِقُونَ", "Olaika yusariAAoona fee alkhayrati wahum laha sabiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 62, "And We charge no soul except [with that within] its capacity, and with Us is a record which speaks with truth; and they will not be wronged", "وَلَا نُكَلِّفُ نَفۡسًا إِلَّا وُسۡعَهَاۚ وَلَدَيۡنَا كِتَٰبٞ يَنطِقُ بِٱلۡحَقِّ وَهُمۡ لَا يُظۡلَمُونَ", "Wala nukallifu nafsan illa wusAAaha waladayna kitabun yantiqu bialhaqqi wahum la yuthlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 63, "But their hearts are covered with confusion over this, and they have [evil] deeds besides disbelief which they are doing", "بَلۡ قُلُوبُهُمۡ فِي غَمۡرَةٖ مِّنۡ هَٰذَا وَلَهُمۡ أَعۡمَٰلٞ مِّن دُونِ ذَٰلِكَ هُمۡ لَهَا عَٰمِلُونَ", "Bal quloobuhum fee ghamratin min hatha walahum aAAmalun min dooni thalika hum laha AAamiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 64, "Until when We seize their affluent ones with punishment, at once they are crying [to Allah] for help", "حَتَّىٰٓ إِذَآ أَخَذۡنَا مُتۡرَفِيهِم بِٱلۡعَذَابِ إِذَا هُمۡ يَجۡـَٔرُونَ", "Hatta itha akhathna mutrafeehim bialAAathabi itha hum yajaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 65, "Do not cry out today. Indeed, by Us you will not be helped", "لَا تَجۡـَٔرُواْ ٱلۡيَوۡمَۖ إِنَّكُم مِّنَّا لَا تُنصَرُونَ", "La tajaroo alyawma innakum minna la tunsaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 66, "My verses had already been recited to you, but you were turning back on your heels", "قَدۡ كَانَتۡ ءَايَٰتِي تُتۡلَىٰ عَلَيۡكُمۡ فَكُنتُمۡ عَلَىٰٓ أَعۡقَٰبِكُمۡ تَنكِصُونَ", "Qad kanat ayatee tutla AAalaykum fakuntum AAala aAAqabikum tankisoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 67, "In arrogance regarding it, conversing by night, speaking evil", "مُسۡتَكۡبِرِينَ بِهِۦ سَٰمِرٗا تَهۡجُرُونَ", "Mustakbireena bihi samiran tahjuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 68, "Then have they not reflected over the Qur'an, or has there come to them that which had not come to their forefathers", "أَفَلَمۡ يَدَّبَّرُواْ ٱلۡقَوۡلَ أَمۡ جَآءَهُم مَّا لَمۡ يَأۡتِ ءَابَآءَهُمُ ٱلۡأَوَّلِينَ", "Afalam yaddabbaroo alqawla am jaahum ma lam yati abaahumu alawwaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 69, "Or did they not know their Messenger, so they are toward him disacknowledging", "أَمۡ لَمۡ يَعۡرِفُواْ رَسُولَهُمۡ فَهُمۡ لَهُۥ مُنكِرُونَ", "Am lam yaAArifoo rasoolahum fahum lahu munkiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 70, "Or do they say, ""In him is madness?"" Rather, he brought them the truth, but most of them, to the truth, are averse", "أَمۡ يَقُولُونَ بِهِۦ جِنَّةُۢۚ بَلۡ جَآءَهُم بِٱلۡحَقِّ وَأَكۡثَرُهُمۡ لِلۡحَقِّ كَٰرِهُونَ", "Am yaqooloona bihi jinnatun bal jaahum bialhaqqi waaktharuhum lilhaqqi karihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 71, "But if the Truth had followed their inclinations, the heavens and the earth and whoever is in them would have been ruined. Rather, We have brought them their message, but they, from their message, are turning away", "وَلَوِ ٱتَّبَعَ ٱلۡحَقُّ أَهۡوَآءَهُمۡ لَفَسَدَتِ ٱلسَّمَٰوَٰتُ وَٱلۡأَرۡضُ وَمَن فِيهِنَّۚ بَلۡ أَتَيۡنَٰهُم بِذِكۡرِهِمۡ فَهُمۡ عَن ذِكۡرِهِم مُّعۡرِضُونَ", "Walawi ittabaAAa alhaqqu ahwaahum lafasadati alssamawatu waalardu waman feehinna bal ataynahum bithikrihim fahum AAan thikrihim muAAridoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 72, "Or do you, [O Muhammad], ask them for payment? But the reward of your Lord is best, and He is the best of providers", "أَمۡ تَسۡـَٔلُهُمۡ خَرۡجٗا فَخَرَاجُ رَبِّكَ خَيۡرٞۖ وَهُوَ خَيۡرُ ٱلرَّـٰزِقِينَ", "Am tasaluhum kharjan fakharaju rabbika khayrun wahuwa khayru alrraziqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 73, "And indeed, you invite them to a straight path", "وَإِنَّكَ لَتَدۡعُوهُمۡ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ", "Wainnaka latadAAoohum ila siratin mustaqeemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 74, "But indeed, those who do not believe in the Hereafter are deviating from the path", "وَإِنَّ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱلۡأٓخِرَةِ عَنِ ٱلصِّرَٰطِ لَنَٰكِبُونَ", "Wainna allatheena la yuminoona bialakhirati AAani alssirati lanakiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 75, "And even if We gave them mercy and removed what was upon them of affliction, they would persist in their transgression, wandering blindly", "۞وَلَوۡ رَحِمۡنَٰهُمۡ وَكَشَفۡنَا مَا بِهِم مِّن ضُرّٖ لَّلَجُّواْ فِي طُغۡيَٰنِهِمۡ يَعۡمَهُونَ", "Walaw rahimnahum wakashafna ma bihim min durrin lalajjoo fee tughyanihim yaAAmahoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 76, "And We had gripped them with suffering [as a warning], but they did not yield to their Lord, nor did they humbly supplicate, [and will continue thus]", "وَلَقَدۡ أَخَذۡنَٰهُم بِٱلۡعَذَابِ فَمَا ٱسۡتَكَانُواْ لِرَبِّهِمۡ وَمَا يَتَضَرَّعُونَ", "Walaqad akhathnahum bialAAathabi fama istakanoo lirabbihim wama yatadarraAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 77, "Until when We have opened before them a door of severe punishment, immediately they will be therein in despair", "حَتَّىٰٓ إِذَا فَتَحۡنَا عَلَيۡهِم بَابٗا ذَا عَذَابٖ شَدِيدٍ إِذَا هُمۡ فِيهِ مُبۡلِسُونَ", "Hatta itha fatahna AAalayhim baban tha AAathabin shadeedin itha hum feehi mublisoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 78, "And it is He who produced for you hearing and vision and hearts; little are you grateful", "وَهُوَ ٱلَّذِيٓ أَنشَأَ لَكُمُ ٱلسَّمۡعَ وَٱلۡأَبۡصَٰرَ وَٱلۡأَفۡـِٔدَةَۚ قَلِيلٗا مَّا تَشۡكُرُونَ", "Wahuwa allathee anshaa lakumu alssamAAa waalabsara waalafidata qaleelan ma tashkuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 79, "And it is He who has multiplied you throughout the earth, and to Him you will be gathered", "وَهُوَ ٱلَّذِي ذَرَأَكُمۡ فِي ٱلۡأَرۡضِ وَإِلَيۡهِ تُحۡشَرُونَ", "Wahuwa allathee tharaakum fee alardi wailayhi tuhsharoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 80, "And it is He who gives life and causes death, and His is the alternation of the night and the day. Then will you not reason", "وَهُوَ ٱلَّذِي يُحۡيِۦ وَيُمِيتُ وَلَهُ ٱخۡتِلَٰفُ ٱلَّيۡلِ وَٱلنَّهَارِۚ أَفَلَا تَعۡقِلُونَ", "Wahuwa allathee yuhyee wayumeetu walahu ikhtilafu allayli waalnnahari afala taAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 81, "Rather, they say like what the former peoples said", "بَلۡ قَالُواْ مِثۡلَ مَا قَالَ ٱلۡأَوَّلُونَ", "Bal qaloo mithla ma qala alawwaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 82, "They said, ""When we have died and become dust and bones, are we indeed to be resurrected", "قَالُوٓاْ أَءِذَا مِتۡنَا وَكُنَّا تُرَابٗا وَعِظَٰمًا أَءِنَّا لَمَبۡعُوثُونَ", "Qaloo aitha mitna wakunna turaban waAAithaman ainna lamabAAoothoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 83, "We have been promised this, we and our forefathers, before; this is not but legends of the former peoples", "لَقَدۡ وُعِدۡنَا نَحۡنُ وَءَابَآؤُنَا هَٰذَا مِن قَبۡلُ إِنۡ هَٰذَآ إِلَّآ أَسَٰطِيرُ ٱلۡأَوَّلِينَ", "Laqad wuAAidna nahnu waabaona hatha min qablu in hatha illa asateeru alawwaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 84, "Say, [O Muhammad], ""To whom belongs the earth and whoever is in it, if you should know", "قُل لِّمَنِ ٱلۡأَرۡضُ وَمَن فِيهَآ إِن كُنتُمۡ تَعۡلَمُونَ", "Qul limani alardu waman feeha in kuntum taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 85, "They will say, ""To Allah."" Say, ""Then will you not remember", "سَيَقُولُونَ لِلَّهِۚ قُلۡ أَفَلَا تَذَكَّرُونَ", "Sayaqooloona lillahi qul afala tathakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 86, "Say, ""Who is Lord of the seven heavens and Lord of the Great Throne", "قُلۡ مَن رَّبُّ ٱلسَّمَٰوَٰتِ ٱلسَّبۡعِ وَرَبُّ ٱلۡعَرۡشِ ٱلۡعَظِيمِ", "Qul man rabbu alssamawati alssabAAi warabbu alAAarshi alAAatheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 87, "They will say, ""[They belong] to Allah."" Say, ""Then will you not fear Him", "سَيَقُولُونَ لِلَّهِۚ قُلۡ أَفَلَا تَتَّقُونَ", "Sayaqooloona lillahi qul afala tattaqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 88, "Say, ""In whose hand is the realm of all things - and He protects while none can protect against Him - if you should know", "قُلۡ مَنۢ بِيَدِهِۦ مَلَكُوتُ كُلِّ شَيۡءٖ وَهُوَ يُجِيرُ وَلَا يُجَارُ عَلَيۡهِ إِن كُنتُمۡ تَعۡلَمُونَ", "Qul man biyadihi malakootu kulli shayin wahuwa yujeeru wala yujaru AAalayhi in kuntum taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 89, "They will say, ""[All belongs] to Allah."" Say, ""Then how are you deluded", "سَيَقُولُونَ لِلَّهِۚ قُلۡ فَأَنَّىٰ تُسۡحَرُونَ", "Sayaqooloona lillahi qul faanna tusharoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 90, "Rather, We have brought them the truth, and indeed they are liars", "بَلۡ أَتَيۡنَٰهُم بِٱلۡحَقِّ وَإِنَّهُمۡ لَكَٰذِبُونَ", "Bal ataynahum bialhaqqi wainnahum lakathiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 91, "Allah has not taken any son, nor has there ever been with Him any deity. [If there had been], then each deity would have taken what it created, and some of them would have sought to overcome others. Exalted is Allah above what they describe [concerning Him]", "مَا ٱتَّخَذَ ٱللَّهُ مِن وَلَدٖ وَمَا كَانَ مَعَهُۥ مِنۡ إِلَٰهٍۚ إِذٗا لَّذَهَبَ كُلُّ إِلَٰهِۭ بِمَا خَلَقَ وَلَعَلَا بَعۡضُهُمۡ عَلَىٰ بَعۡضٖۚ سُبۡحَٰنَ ٱللَّهِ عَمَّا يَصِفُونَ", "Ma ittakhatha Allahu min waladin wama kana maAAahu min ilahin ithan lathahaba kullu ilahin bima khalaqa walaAAala baAAduhum AAala baAAdin subhana Allahi AAamma yasifoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 92, "[He is] Knower of the unseen and the witnessed, so high is He above what they associate [with Him]", "عَٰلِمِ ٱلۡغَيۡبِ وَٱلشَّهَٰدَةِ فَتَعَٰلَىٰ عَمَّا يُشۡرِكُونَ", "AAalimi alghaybi waalshshahadati fataAAala AAamma yushrikoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 93, "Say, [O Muhammad], ""My Lord, if You should show me that which they are promised", "قُل رَّبِّ إِمَّا تُرِيَنِّي مَا يُوعَدُونَ", "Qul rabbi imma turiyannee ma yooAAadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 94, "My Lord, then do not place me among the wrongdoing people", "رَبِّ فَلَا تَجۡعَلۡنِي فِي ٱلۡقَوۡمِ ٱلظَّـٰلِمِينَ", "Rabbi fala tajAAalnee fee alqawmi alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 95, "And indeed, We are able to show you what We have promised them", "وَإِنَّا عَلَىٰٓ أَن نُّرِيَكَ مَا نَعِدُهُمۡ لَقَٰدِرُونَ", "Wainna AAala an nuriyaka ma naAAiduhum laqadiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 96, "Repel, by [means of] what is best, [their] evil. We are most knowing of what they describe", "ٱدۡفَعۡ بِٱلَّتِي هِيَ أَحۡسَنُ ٱلسَّيِّئَةَۚ نَحۡنُ أَعۡلَمُ بِمَا يَصِفُونَ", "IdfaAA biallatee hiya ahsanu alssayyiata nahnu aAAlamu bima yasifoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 97, "And say, ""My Lord, I seek refuge in You from the incitements of the devils", "وَقُل رَّبِّ أَعُوذُ بِكَ مِنۡ هَمَزَٰتِ ٱلشَّيَٰطِينِ", "Waqul rabbi aAAoothu bika min hamazati alshshayateeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 98, "And I seek refuge in You, my Lord, lest they be present with me", "وَأَعُوذُ بِكَ رَبِّ أَن يَحۡضُرُونِ", "WaaAAoothu bika rabbi an yahdurooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 99, "[For such is the state of the disbelievers], until, when death comes to one of them, he says, ""My Lord, send me back", "حَتَّىٰٓ إِذَا جَآءَ أَحَدَهُمُ ٱلۡمَوۡتُ قَالَ رَبِّ ٱرۡجِعُونِ", "Hatta itha jaa ahadahumu almawtu qala rabbi irjiAAooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 100, "That I might do righteousness in that which I left behind."" No! It is only a word he is saying; and behind them is a barrier until the Day they are resurrected", "لَعَلِّيٓ أَعۡمَلُ صَٰلِحٗا فِيمَا تَرَكۡتُۚ كَلَّآۚ إِنَّهَا كَلِمَةٌ هُوَ قَآئِلُهَاۖ وَمِن وَرَآئِهِم بَرۡزَخٌ إِلَىٰ يَوۡمِ يُبۡعَثُونَ", "LaAAallee aAAmalu salihan feema taraktu kalla innaha kalimatun huwa qailuha wamin waraihim barzakhun ila yawmi yubAAathoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 101, "So when the Horn is blown, no relationship will there be among them that Day, nor will they ask about one another", "فَإِذَا نُفِخَ فِي ٱلصُّورِ فَلَآ أَنسَابَ بَيۡنَهُمۡ يَوۡمَئِذٖ وَلَا يَتَسَآءَلُونَ", "Faitha nufikha fee alssoori fala ansaba baynahum yawmaithin wala yatasaaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 102, "And those whose scales are heavy [with good deeds] - it is they who are the successful", "فَمَن ثَقُلَتۡ مَوَٰزِينُهُۥ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُفۡلِحُونَ", "Faman thaqulat mawazeenuhu faolaika humu almuflihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 103, "But those whose scales are light - those are the ones who have lost their souls, [being] in Hell, abiding eternally", "وَمَنۡ خَفَّتۡ مَوَٰزِينُهُۥ فَأُوْلَـٰٓئِكَ ٱلَّذِينَ خَسِرُوٓاْ أَنفُسَهُمۡ فِي جَهَنَّمَ خَٰلِدُونَ", "Waman khaffat mawazeenuhu faolaika allatheena khasiroo anfusahum fee jahannama khalidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 104, "The Fire will sear their faces, and they therein will have taut smiles", "تَلۡفَحُ وُجُوهَهُمُ ٱلنَّارُ وَهُمۡ فِيهَا كَٰلِحُونَ", "Talfahu wujoohahumu alnnaru wahum feeha kalihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 105, "[It will be said]. ""Were not My verses recited to you and you used to deny them", "أَلَمۡ تَكُنۡ ءَايَٰتِي تُتۡلَىٰ عَلَيۡكُمۡ فَكُنتُم بِهَا تُكَذِّبُونَ", "Alam takun ayatee tutla AAalaykum fakuntum biha tukaththiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 106, "They will say, ""Our Lord, our wretchedness overcame us, and we were a people astray", "قَالُواْ رَبَّنَا غَلَبَتۡ عَلَيۡنَا شِقۡوَتُنَا وَكُنَّا قَوۡمٗا ضَآلِّينَ", "Qaloo rabbana ghalabat AAalayna shiqwatuna wakunna qawman dalleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 107, "Our Lord, remove us from it, and if we were to return [to evil], we would indeed be wrongdoers", "رَبَّنَآ أَخۡرِجۡنَا مِنۡهَا فَإِنۡ عُدۡنَا فَإِنَّا ظَٰلِمُونَ", "Rabbana akhrijna minha fain AAudna fainna thalimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 108, "He will say, ""Remain despised therein and do not speak to Me", "قَالَ ٱخۡسَـُٔواْ فِيهَا وَلَا تُكَلِّمُونِ", "Qala ikhsaoo feeha wala tukallimooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 109, "Indeed, there was a party of My servants who said, 'Our Lord, we have believed, so forgive us and have mercy upon us, and You are the best of the merciful", "إِنَّهُۥ كَانَ فَرِيقٞ مِّنۡ عِبَادِي يَقُولُونَ رَبَّنَآ ءَامَنَّا فَٱغۡفِرۡ لَنَا وَٱرۡحَمۡنَا وَأَنتَ خَيۡرُ ٱلرَّـٰحِمِينَ", "Innahu kana fareequn min AAibadee yaqooloona rabbana amanna faighfir lana wairhamna waanta khayru alrrahimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 110, "But you took them in mockery to the point that they made you forget My remembrance, and you used to laugh at them", "فَٱتَّخَذۡتُمُوهُمۡ سِخۡرِيًّا حَتَّىٰٓ أَنسَوۡكُمۡ ذِكۡرِي وَكُنتُم مِّنۡهُمۡ تَضۡحَكُونَ", "Faittakhathtumoohum sikhriyyan hatta ansawkum thikree wakuntum minhum tadhakoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 111, "Indeed, I have rewarded them this Day for their patient endurance - that they are the attainers [of success]", "إِنِّي جَزَيۡتُهُمُ ٱلۡيَوۡمَ بِمَا صَبَرُوٓاْ أَنَّهُمۡ هُمُ ٱلۡفَآئِزُونَ", "Innee jazaytuhumu alyawma bima sabaroo annahum humu alfaizoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 112, "[Allah] will say, ""How long did you remain on earth in number of years", "قَٰلَ كَمۡ لَبِثۡتُمۡ فِي ٱلۡأَرۡضِ عَدَدَ سِنِينَ", "Qala kam labithtum fee alardi AAadada sineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 113, "They will say, ""We remained a day or part of a day; ask those who enumerate", "قَالُواْ لَبِثۡنَا يَوۡمًا أَوۡ بَعۡضَ يَوۡمٖ فَسۡـَٔلِ ٱلۡعَآدِّينَ", "Qaloo labithna yawman aw baAAda yawmin faisali alAAaddeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 114, "He will say, ""You stayed not but a little - if only you had known", "قَٰلَ إِن لَّبِثۡتُمۡ إِلَّا قَلِيلٗاۖ لَّوۡ أَنَّكُمۡ كُنتُمۡ تَعۡلَمُونَ", "Qala in labithtum illa qaleelan law annakum kuntum taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 115, "Then did you think that We created you uselessly and that to Us you would not be returned", "أَفَحَسِبۡتُمۡ أَنَّمَا خَلَقۡنَٰكُمۡ عَبَثٗا وَأَنَّكُمۡ إِلَيۡنَا لَا تُرۡجَعُونَ", "Afahasibtum annama khalaqnakum AAabathan waannakum ilayna la turjaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 116, "So exalted is Allah, the Sovereign, the Truth; there is no deity except Him, Lord of the Noble Throne", "فَتَعَٰلَى ٱللَّهُ ٱلۡمَلِكُ ٱلۡحَقُّۖ لَآ إِلَٰهَ إِلَّا هُوَ رَبُّ ٱلۡعَرۡشِ ٱلۡكَرِيمِ", "FataAAala Allahu almaliku alhaqqu la ilaha illa huwa rabbu alAAarshi alkareemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 117, "And whoever invokes besides Allah another deity for which he has no proof - then his account is only with his Lord. Indeed, the disbelievers will not succeed", "وَمَن يَدۡعُ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَ لَا بُرۡهَٰنَ لَهُۥ بِهِۦ فَإِنَّمَا حِسَابُهُۥ عِندَ رَبِّهِۦٓۚ إِنَّهُۥ لَا يُفۡلِحُ ٱلۡكَٰفِرُونَ", "Waman yadAAu maAAa Allahi ilahan akhara la burhana lahu bihi fainnama hisabuhu AAinda rabbihi innahu la yuflihu alkafiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (23, 118, "And, [O Muhammad], say, ""My Lord, forgive and have mercy, and You are the best of the merciful", "وَقُل رَّبِّ ٱغۡفِرۡ وَٱرۡحَمۡ وَأَنتَ خَيۡرُ ٱلرَّـٰحِمِينَ", "Waqul rabbi ighfir wairham waanta khayru alrrahimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 1, "[This is] a surah which We have sent down and made [that within it] obligatory and revealed therein verses of clear evidence that you might remember", "سُورَةٌ أَنزَلۡنَٰهَا وَفَرَضۡنَٰهَا وَأَنزَلۡنَا فِيهَآ ءَايَٰتِۭ بَيِّنَٰتٖ لَّعَلَّكُمۡ تَذَكَّرُونَ", "Sooratun anzalnaha wafaradnaha waanzalna feeha ayatin bayyinatin laAAallakum tathakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 2, "The [unmarried] woman or [unmarried] man found guilty of sexual intercourse - lash each one of them with a hundred lashes, and do not be taken by pity for them in the religion of Allah, if you should believe in Allah and the Last Day. And let a group of the believers witness their punishment", "ٱلزَّانِيَةُ وَٱلزَّانِي فَٱجۡلِدُواْ كُلَّ وَٰحِدٖ مِّنۡهُمَا مِاْئَةَ جَلۡدَةٖۖ وَلَا تَأۡخُذۡكُم بِهِمَا رَأۡفَةٞ فِي دِينِ ٱللَّهِ إِن كُنتُمۡ تُؤۡمِنُونَ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِۖ وَلۡيَشۡهَدۡ عَذَابَهُمَا طَآئِفَةٞ مِّنَ ٱلۡمُؤۡمِنِينَ", "Alzzaniyatu waalzzanee faijlidoo kulla wahidin minhuma miata jaldatin wala takhuthkum bihima rafatun fee deeni Allahi in kuntum tuminoona biAllahi waalyawmi alakhiri walyashhad AAathabahuma taifatun mina almumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 3, "The fornicator does not marry except a [female] fornicator or polytheist, and none marries her except a fornicator or a polytheist, and that has been made unlawful to the believers", "ٱلزَّانِي لَا يَنكِحُ إِلَّا زَانِيَةً أَوۡ مُشۡرِكَةٗ وَٱلزَّانِيَةُ لَا يَنكِحُهَآ إِلَّا زَانٍ أَوۡ مُشۡرِكٞۚ وَحُرِّمَ ذَٰلِكَ عَلَى ٱلۡمُؤۡمِنِينَ", "Alzzanee la yankihu illa zaniyatan aw mushrikatan waalzzaniyatu la yankihuha illa zanin aw mushrikun wahurrima thalika AAala almumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 4, "And those who accuse chaste women and then do not produce four witnesses - lash them with eighty lashes and do not accept from them testimony ever after. And those are the defiantly disobedient", "وَٱلَّذِينَ يَرۡمُونَ ٱلۡمُحۡصَنَٰتِ ثُمَّ لَمۡ يَأۡتُواْ بِأَرۡبَعَةِ شُهَدَآءَ فَٱجۡلِدُوهُمۡ ثَمَٰنِينَ جَلۡدَةٗ وَلَا تَقۡبَلُواْ لَهُمۡ شَهَٰدَةً أَبَدٗاۚ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡفَٰسِقُونَ", "Waallatheena yarmoona almuhsanati thumma lam yatoo biarbaAAati shuhadaa faijlidoohum thamaneena jaldatan wala taqbaloo lahum shahadatan abadan waolaika humu alfasiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 5, "Except for those who repent thereafter and reform, for indeed, Allah is Forgiving and Merciful", "إِلَّا ٱلَّذِينَ تَابُواْ مِنۢ بَعۡدِ ذَٰلِكَ وَأَصۡلَحُواْ فَإِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ", "Illa allatheena taboo min baAAdi thalika waaslahoo fainna Allaha ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 6, "And those who accuse their wives [of adultery] and have no witnesses except themselves - then the witness of one of them [shall be] four testimonies [swearing] by Allah that indeed, he is of the truthful", "وَٱلَّذِينَ يَرۡمُونَ أَزۡوَٰجَهُمۡ وَلَمۡ يَكُن لَّهُمۡ شُهَدَآءُ إِلَّآ أَنفُسُهُمۡ فَشَهَٰدَةُ أَحَدِهِمۡ أَرۡبَعُ شَهَٰدَٰتِۭ بِٱللَّهِ إِنَّهُۥ لَمِنَ ٱلصَّـٰدِقِينَ", "Waallatheena yarmoona azwajahum walam yakun lahum shuhadao illa anfusuhum fashahadatu ahadihim arbaAAu shahadatin biAllahi innahu lamina alssadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 7, "And the fifth [oath will be] that the curse of Allah be upon him if he should be among the liars", "وَٱلۡخَٰمِسَةُ أَنَّ لَعۡنَتَ ٱللَّهِ عَلَيۡهِ إِن كَانَ مِنَ ٱلۡكَٰذِبِينَ", "Waalkhamisatu anna laAAnata Allahi AAalayhi in kana mina alkathibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 8, "But it will prevent punishment from her if she gives four testimonies [swearing] by Allah that indeed, he is of the liars", "وَيَدۡرَؤُاْ عَنۡهَا ٱلۡعَذَابَ أَن تَشۡهَدَ أَرۡبَعَ شَهَٰدَٰتِۭ بِٱللَّهِ إِنَّهُۥ لَمِنَ ٱلۡكَٰذِبِينَ", "Wayadrao AAanha alAAathaba an tashhada arbaAAa shahadatin biAllahi innahu lamina alkathibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 9, "And the fifth [oath will be] that the wrath of Allah be upon her if he was of the truthful", "وَٱلۡخَٰمِسَةَ أَنَّ غَضَبَ ٱللَّهِ عَلَيۡهَآ إِن كَانَ مِنَ ٱلصَّـٰدِقِينَ", "Waalkhamisata anna ghadaba Allahi AAalayha in kana mina alssadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 10, "And if not for the favor of Allah upon you and His mercy... and because Allah is Accepting of repentance and Wise", "وَلَوۡلَا فَضۡلُ ٱللَّهِ عَلَيۡكُمۡ وَرَحۡمَتُهُۥ وَأَنَّ ٱللَّهَ تَوَّابٌ حَكِيمٌ", "Walawla fadlu Allahi AAalaykum warahmatuhu waanna Allaha tawwabun hakeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 11, "Indeed, those who came with falsehood are a group among you. Do not think it bad for you; rather it is good for you. For every person among them is what [punishment] he has earned from the sin, and he who took upon himself the greater portion thereof - for him is a great punishment", "إِنَّ ٱلَّذِينَ جَآءُو بِٱلۡإِفۡكِ عُصۡبَةٞ مِّنكُمۡۚ لَا تَحۡسَبُوهُ شَرّٗا لَّكُمۖ بَلۡ هُوَ خَيۡرٞ لَّكُمۡۚ لِكُلِّ ٱمۡرِيٕٖ مِّنۡهُم مَّا ٱكۡتَسَبَ مِنَ ٱلۡإِثۡمِۚ وَٱلَّذِي تَوَلَّىٰ كِبۡرَهُۥ مِنۡهُمۡ لَهُۥ عَذَابٌ عَظِيمٞ", "Inna allatheena jaoo bialifki AAusbatun minkum la tahsaboohu sharran lakum bal huwa khayrun lakum likulli imriin minhum ma iktasaba mina alithmi waallathee tawalla kibrahu minhum lahu AAathabun AAatheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 12, "Why, when you heard it, did not the believing men and believing women think good of one another and say, ""This is an obvious falsehood", "لَّوۡلَآ إِذۡ سَمِعۡتُمُوهُ ظَنَّ ٱلۡمُؤۡمِنُونَ وَٱلۡمُؤۡمِنَٰتُ بِأَنفُسِهِمۡ خَيۡرٗا وَقَالُواْ هَٰذَآ إِفۡكٞ مُّبِينٞ", "Lawla ith samiAAtumoohu thanna almuminoona waalmuminatu bianfusihim khayran waqaloo hatha ifkun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 13, "Why did they [who slandered] not produce for it four witnesses? And when they do not produce the witnesses, then it is they, in the sight of Allah, who are the liars", "لَّوۡلَا جَآءُو عَلَيۡهِ بِأَرۡبَعَةِ شُهَدَآءَۚ فَإِذۡ لَمۡ يَأۡتُواْ بِٱلشُّهَدَآءِ فَأُوْلَـٰٓئِكَ عِندَ ٱللَّهِ هُمُ ٱلۡكَٰذِبُونَ", "Lawla jaoo AAalayhi biarbaAAati shuhadaa faith lam yatoo bialshshuhadai faolaika AAinda Allahi humu alkathiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 14, "And if it had not been for the favor of Allah upon you and His mercy in this world and the Hereafter, you would have been touched for that [lie] in which you were involved by a great punishment", "وَلَوۡلَا فَضۡلُ ٱللَّهِ عَلَيۡكُمۡ وَرَحۡمَتُهُۥ فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِ لَمَسَّكُمۡ فِي مَآ أَفَضۡتُمۡ فِيهِ عَذَابٌ عَظِيمٌ", "Walawla fadlu Allahi AAalaykum warahmatuhu fee alddunya waalakhirati lamassakum fee ma afadtum feehi AAathabun AAatheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 15, "When you received it with your tongues and said with your mouths that of which you had no knowledge and thought it was insignificant while it was, in the sight of Allah, tremendous", "إِذۡ تَلَقَّوۡنَهُۥ بِأَلۡسِنَتِكُمۡ وَتَقُولُونَ بِأَفۡوَاهِكُم مَّا لَيۡسَ لَكُم بِهِۦ عِلۡمٞ وَتَحۡسَبُونَهُۥ هَيِّنٗا وَهُوَ عِندَ ٱللَّهِ عَظِيمٞ", "Ith talaqqawnahu bialsinatikum wataqooloona biafwahikum ma laysa lakum bihi AAilmun watahsaboonahu hayyinan wahuwa AAinda Allahi AAatheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 16, "And why, when you heard it, did you not say, ""It is not for us to speak of this. Exalted are You, [O Allah]; this is a great slander", "وَلَوۡلَآ إِذۡ سَمِعۡتُمُوهُ قُلۡتُم مَّا يَكُونُ لَنَآ أَن نَّتَكَلَّمَ بِهَٰذَا سُبۡحَٰنَكَ هَٰذَا بُهۡتَٰنٌ عَظِيمٞ", "Walawla ith samiAAtumoohu qultum ma yakoonu lana an natakallama bihatha subhanaka hatha buhtanun AAatheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 17, "Allah warns you against returning to the likes of this [conduct], ever, if you should be believers", "يَعِظُكُمُ ٱللَّهُ أَن تَعُودُواْ لِمِثۡلِهِۦٓ أَبَدًا إِن كُنتُم مُّؤۡمِنِينَ", "YaAAithukumu Allahu an taAAoodoo limithlihi abadan in kuntum mumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 18, "And Allah makes clear to you the verses, and Allah is Knowing and Wise", "وَيُبَيِّنُ ٱللَّهُ لَكُمُ ٱلۡأٓيَٰتِۚ وَٱللَّهُ عَلِيمٌ حَكِيمٌ", "Wayubayyinu Allahu lakumu alayati waAllahu AAaleemun hakeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 19, "Indeed, those who like that immorality should be spread [or publicized] among those who have believed will have a painful punishment in this world and the Hereafter. And Allah knows and you do not know", "إِنَّ ٱلَّذِينَ يُحِبُّونَ أَن تَشِيعَ ٱلۡفَٰحِشَةُ فِي ٱلَّذِينَ ءَامَنُواْ لَهُمۡ عَذَابٌ أَلِيمٞ فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِۚ وَٱللَّهُ يَعۡلَمُ وَأَنتُمۡ لَا تَعۡلَمُونَ", "Inna allatheena yuhibboona an tasheeAAa alfahishatu fee allatheena amanoo lahum AAathabun aleemun fee alddunya waalakhirati waAllahu yaAAlamu waantum la taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 20, "And if it had not been for the favor of Allah upon you and His mercy... and because Allah is Kind and Merciful", "وَلَوۡلَا فَضۡلُ ٱللَّهِ عَلَيۡكُمۡ وَرَحۡمَتُهُۥ وَأَنَّ ٱللَّهَ رَءُوفٞ رَّحِيمٞ", "Walawla fadlu Allahi AAalaykum warahmatuhu waanna Allaha raoofun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 21, "O you who have believed, do not follow the footsteps of Satan. And whoever follows the footsteps of Satan - indeed, he enjoins immorality and wrongdoing. And if not for the favor of Allah upon you and His mercy, not one of you would have been pure, ever, but Allah purifies whom He wills, and Allah is Hearing and Knowing", "۞يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَتَّبِعُواْ خُطُوَٰتِ ٱلشَّيۡطَٰنِۚ وَمَن يَتَّبِعۡ خُطُوَٰتِ ٱلشَّيۡطَٰنِ فَإِنَّهُۥ يَأۡمُرُ بِٱلۡفَحۡشَآءِ وَٱلۡمُنكَرِۚ وَلَوۡلَا فَضۡلُ ٱللَّهِ عَلَيۡكُمۡ وَرَحۡمَتُهُۥ مَا زَكَىٰ مِنكُم مِّنۡ أَحَدٍ أَبَدٗا وَلَٰكِنَّ ٱللَّهَ يُزَكِّي مَن يَشَآءُۗ وَٱللَّهُ سَمِيعٌ عَلِيمٞ", "Ya ayyuha allatheena amanoo la tattabiAAoo khutuwati alshshaytani waman yattabiAA khutuwati alshshaytani fainnahu yamuru bialfahshai waalmunkari walawla fadlu Allahi AAalaykum warahmatuhu ma zaka minkum min ahadin abadan walakinna Allaha yuzakkee man yashao waAllahu sameeAAun AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 22, "And let not those of virtue among you and wealth swear not to give [aid] to their relatives and the needy and the emigrants for the cause of Allah, and let them pardon and overlook. Would you not like that Allah should forgive you? And Allah is Forgiving and Merciful", "وَلَا يَأۡتَلِ أُوْلُواْ ٱلۡفَضۡلِ مِنكُمۡ وَٱلسَّعَةِ أَن يُؤۡتُوٓاْ أُوْلِي ٱلۡقُرۡبَىٰ وَٱلۡمَسَٰكِينَ وَٱلۡمُهَٰجِرِينَ فِي سَبِيلِ ٱللَّهِۖ وَلۡيَعۡفُواْ وَلۡيَصۡفَحُوٓاْۗ أَلَا تُحِبُّونَ أَن يَغۡفِرَ ٱللَّهُ لَكُمۡۚ وَٱللَّهُ غَفُورٞ رَّحِيمٌ", "Wala yatali oloo alfadli minkum waalssaAAati an yutoo olee alqurba waalmasakeena waalmuhajireena fee sabeeli Allahi walyaAAfoo walyasfahoo ala tuhibboona an yaghfira Allahu lakum waAllahu ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 23, "Indeed, those who [falsely] accuse chaste, unaware and believing women are cursed in this world and the Hereafter; and they will have a great punishment", "إِنَّ ٱلَّذِينَ يَرۡمُونَ ٱلۡمُحۡصَنَٰتِ ٱلۡغَٰفِلَٰتِ ٱلۡمُؤۡمِنَٰتِ لُعِنُواْ فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِ وَلَهُمۡ عَذَابٌ عَظِيمٞ", "Inna allatheena yarmoona almuhsanati alghafilati almuminati luAAinoo fee alddunya waalakhirati walahum AAathabun AAatheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 24, "On a Day when their tongues, their hands and their feet will bear witness against them as to what they used to do", "يَوۡمَ تَشۡهَدُ عَلَيۡهِمۡ أَلۡسِنَتُهُمۡ وَأَيۡدِيهِمۡ وَأَرۡجُلُهُم بِمَا كَانُواْ يَعۡمَلُونَ", "Yawma tashhadu AAalayhim alsinatuhum waaydeehim waarjuluhum bima kanoo yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 25, "That Day, Allah will pay them in full their deserved recompense, and they will know that it is Allah who is the perfect in justice", "يَوۡمَئِذٖ يُوَفِّيهِمُ ٱللَّهُ دِينَهُمُ ٱلۡحَقَّ وَيَعۡلَمُونَ أَنَّ ٱللَّهَ هُوَ ٱلۡحَقُّ ٱلۡمُبِينُ", "Yawmaithin yuwaffeehimu Allahu deenahumu alhaqqa wayaAAlamoona anna Allaha huwa alhaqqu almubeenu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 26, "Evil words are for evil men, and evil men are [subjected] to evil words. And good words are for good men, and good men are [an object] of good words. Those [good people] are declared innocent of what the slanderers say. For them is forgiveness and noble provision", "ٱلۡخَبِيثَٰتُ لِلۡخَبِيثِينَ وَٱلۡخَبِيثُونَ لِلۡخَبِيثَٰتِۖ وَٱلطَّيِّبَٰتُ لِلطَّيِّبِينَ وَٱلطَّيِّبُونَ لِلطَّيِّبَٰتِۚ أُوْلَـٰٓئِكَ مُبَرَّءُونَ مِمَّا يَقُولُونَۖ لَهُم مَّغۡفِرَةٞ وَرِزۡقٞ كَرِيمٞ", "Alkhabeethatu lilkhabeetheena waalkhabeethoona lilkhabeethati waalttayyibatu lilttayyibeena waalttayyiboona lilttayyibati olaika mubarraoona mimma yaqooloona lahum maghfiratun warizqun kareemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 27, "O you who have believed, do not enter houses other than your own houses until you ascertain welcome and greet their inhabitants. That is best for you; perhaps you will be reminded", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَدۡخُلُواْ بُيُوتًا غَيۡرَ بُيُوتِكُمۡ حَتَّىٰ تَسۡتَأۡنِسُواْ وَتُسَلِّمُواْ عَلَىٰٓ أَهۡلِهَاۚ ذَٰلِكُمۡ خَيۡرٞ لَّكُمۡ لَعَلَّكُمۡ تَذَكَّرُونَ", "Ya ayyuha allatheena amanoo la tadkhuloo buyootan ghayra buyootikum hatta tastanisoo watusallimoo AAala ahliha thalikum khayrun lakum laAAallakum tathakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 28, "And if you do not find anyone therein, do not enter them until permission has been given you. And if it is said to you, ""Go back,"" then go back; it is purer for you. And Allah is Knowing of what you do", "فَإِن لَّمۡ تَجِدُواْ فِيهَآ أَحَدٗا فَلَا تَدۡخُلُوهَا حَتَّىٰ يُؤۡذَنَ لَكُمۡۖ وَإِن قِيلَ لَكُمُ ٱرۡجِعُواْ فَٱرۡجِعُواْۖ هُوَ أَزۡكَىٰ لَكُمۡۚ وَٱللَّهُ بِمَا تَعۡمَلُونَ عَلِيمٞ", "Fain lam tajidoo feeha ahadan fala tadkhulooha hatta yuthana lakum wain qeela lakumu irjiAAoo fairjiAAoo huwa azka lakum waAllahu bima taAAmaloona AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 29, "There is no blame upon you for entering houses not inhabited in which there is convenience for you. And Allah knows what you reveal and what you conceal", "لَّيۡسَ عَلَيۡكُمۡ جُنَاحٌ أَن تَدۡخُلُواْ بُيُوتًا غَيۡرَ مَسۡكُونَةٖ فِيهَا مَتَٰعٞ لَّكُمۡۚ وَٱللَّهُ يَعۡلَمُ مَا تُبۡدُونَ وَمَا تَكۡتُمُونَ", "Laysa AAalaykum junahun an tadkhuloo buyootan ghayra maskoonatin feeha mataAAun lakum waAllahu yaAAlamu ma tubdoona wama taktumoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 30, "Tell the believing men to reduce [some] of their vision and guard their private parts. That is purer for them. Indeed, Allah is Acquainted with what they do", "قُل لِّلۡمُؤۡمِنِينَ يَغُضُّواْ مِنۡ أَبۡصَٰرِهِمۡ وَيَحۡفَظُواْ فُرُوجَهُمۡۚ ذَٰلِكَ أَزۡكَىٰ لَهُمۡۚ إِنَّ ٱللَّهَ خَبِيرُۢ بِمَا يَصۡنَعُونَ", "Qul lilmumineena yaghuddoo min absarihum wayahfathoo furoojahum thalika azka lahum inna Allaha khabeerun bima yasnaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 31, "And tell the believing women to reduce [some] of their vision and guard their private parts and not expose their adornment except that which [necessarily] appears thereof and to wrap [a portion of] their headcovers over their chests and not expose their adornment except to their husbands, their fathers, their husbands' fathers, their sons, their husbands' sons, their brothers, their brothers' sons, their sisters' sons, their women, that which their right hands possess, or those male attendants having no physical desire, or children who are not yet aware of the private aspects of women. And let them not stamp their feet to make known what they conceal of their adornment. And turn to Allah in repentance, all of you, O believers, that you might succeed", "وَقُل لِّلۡمُؤۡمِنَٰتِ يَغۡضُضۡنَ مِنۡ أَبۡصَٰرِهِنَّ وَيَحۡفَظۡنَ فُرُوجَهُنَّ وَلَا يُبۡدِينَ زِينَتَهُنَّ إِلَّا مَا ظَهَرَ مِنۡهَاۖ وَلۡيَضۡرِبۡنَ بِخُمُرِهِنَّ عَلَىٰ جُيُوبِهِنَّۖ وَلَا يُبۡدِينَ زِينَتَهُنَّ إِلَّا لِبُعُولَتِهِنَّ أَوۡ ءَابَآئِهِنَّ أَوۡ ءَابَآءِ بُعُولَتِهِنَّ أَوۡ أَبۡنَآئِهِنَّ أَوۡ أَبۡنَآءِ بُعُولَتِهِنَّ أَوۡ إِخۡوَٰنِهِنَّ أَوۡ بَنِيٓ إِخۡوَٰنِهِنَّ أَوۡ بَنِيٓ أَخَوَٰتِهِنَّ أَوۡ نِسَآئِهِنَّ أَوۡ مَا مَلَكَتۡ أَيۡمَٰنُهُنَّ أَوِ ٱلتَّـٰبِعِينَ غَيۡرِ أُوْلِي ٱلۡإِرۡبَةِ مِنَ ٱلرِّجَالِ أَوِ ٱلطِّفۡلِ ٱلَّذِينَ لَمۡ يَظۡهَرُواْ عَلَىٰ عَوۡرَٰتِ ٱلنِّسَآءِۖ وَلَا يَضۡرِبۡنَ بِأَرۡجُلِهِنَّ لِيُعۡلَمَ مَا يُخۡفِينَ مِن زِينَتِهِنَّۚ وَتُوبُوٓاْ إِلَى ٱللَّهِ جَمِيعًا أَيُّهَ ٱلۡمُؤۡمِنُونَ لَعَلَّكُمۡ تُفۡلِحُونَ", "Waqul lilmuminati yaghdudna min absarihinna wayahfathna furoojahunna wala yubdeena zeenatahunna illa ma thahara minha walyadribna bikhumurihinna AAala juyoobihinna wala yubdeena zeenatahunna illa libuAAoolatihinna aw abaihinna aw abai buAAoolatihinna aw abnaihinna aw abnai buAAoolatihinna aw ikhwanihinna aw banee ikhwanihinna aw banee akhawatihinna aw nisaihinna aw ma malakat aymanuhunna awi alttabiAAeena ghayri olee alirbati mina alrrijali awi alttifli allatheena lam yathharoo AAala AAawrati alnnisai wala yadribna biarjulihinna liyuAAlama ma yukhfeena min zeenatihinna watooboo ila Allahi jameeAAan ayyuha almuminoona laAAallakum tuflihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 32, "And marry the unmarried among you and the righteous among your male slaves and female slaves. If they should be poor, Allah will enrich them from His bounty, and Allah is all-Encompassing and Knowing", "وَأَنكِحُواْ ٱلۡأَيَٰمَىٰ مِنكُمۡ وَٱلصَّـٰلِحِينَ مِنۡ عِبَادِكُمۡ وَإِمَآئِكُمۡۚ إِن يَكُونُواْ فُقَرَآءَ يُغۡنِهِمُ ٱللَّهُ مِن فَضۡلِهِۦۗ وَٱللَّهُ وَٰسِعٌ عَلِيمٞ", "Waankihoo alayama minkum waalssaliheena min AAibadikum waimaikum in yakoonoo fuqaraa yughnihimu Allahu min fadlihi waAllahu wasiAAun AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 33, "But let them who find not [the means for] marriage abstain [from sexual relations] until Allah enriches them from His bounty. And those who seek a contract [for eventual emancipation] from among whom your right hands possess - then make a contract with them if you know there is within them goodness and give them from the wealth of Allah which He has given you. And do not compel your slave girls to prostitution, if they desire chastity, to seek [thereby] the temporary interests of worldly life. And if someone should compel them, then indeed, Allah is [to them], after their compulsion, Forgiving and Merciful", "وَلۡيَسۡتَعۡفِفِ ٱلَّذِينَ لَا يَجِدُونَ نِكَاحًا حَتَّىٰ يُغۡنِيَهُمُ ٱللَّهُ مِن فَضۡلِهِۦۗ وَٱلَّذِينَ يَبۡتَغُونَ ٱلۡكِتَٰبَ مِمَّا مَلَكَتۡ أَيۡمَٰنُكُمۡ فَكَاتِبُوهُمۡ إِنۡ عَلِمۡتُمۡ فِيهِمۡ خَيۡرٗاۖ وَءَاتُوهُم مِّن مَّالِ ٱللَّهِ ٱلَّذِيٓ ءَاتَىٰكُمۡۚ وَلَا تُكۡرِهُواْ فَتَيَٰتِكُمۡ عَلَى ٱلۡبِغَآءِ إِنۡ أَرَدۡنَ تَحَصُّنٗا لِّتَبۡتَغُواْ عَرَضَ ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۚ وَمَن يُكۡرِههُّنَّ فَإِنَّ ٱللَّهَ مِنۢ بَعۡدِ إِكۡرَٰهِهِنَّ غَفُورٞ رَّحِيمٞ", "WalyastaAAfifi allatheena la yajidoona nikahan hatta yughniyahumu Allahu min fadlihi waallatheena yabtaghoona alkitaba mimma malakat aymanukum fakatiboohum in AAalimtum feehim khayran waatoohum min mali Allahi allathee atakum wala tukrihoo fatayatikum AAala albighai in aradna tahassunan litabtaghoo AAarada alhayati alddunya waman yukrihhunna fainna Allaha min baAAdi ikrahihinna ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 34, "And We have certainly sent down to you distinct verses and examples from those who passed on before you and an admonition for those who fear Allah", "وَلَقَدۡ أَنزَلۡنَآ إِلَيۡكُمۡ ءَايَٰتٖ مُّبَيِّنَٰتٖ وَمَثَلٗا مِّنَ ٱلَّذِينَ خَلَوۡاْ مِن قَبۡلِكُمۡ وَمَوۡعِظَةٗ لِّلۡمُتَّقِينَ", "Walaqad anzalna ilaykum ayatin mubayyinatin wamathalan mina allatheena khalaw min qablikum wamawAAithatan lilmuttaqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 35, "Allah is the Light of the heavens and the earth. The example of His light is like a niche within which is a lamp, the lamp is within glass, the glass as if it were a pearly [white] star lit from [the oil of] a blessed olive tree, neither of the east nor of the west, whose oil would almost glow even if untouched by fire. Light upon light. Allah guides to His light whom He wills. And Allah presents examples for the people, and Allah is Knowing of all things", "۞ٱللَّهُ نُورُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ مَثَلُ نُورِهِۦ كَمِشۡكَوٰةٖ فِيهَا مِصۡبَاحٌۖ ٱلۡمِصۡبَاحُ فِي زُجَاجَةٍۖ ٱلزُّجَاجَةُ كَأَنَّهَا كَوۡكَبٞ دُرِّيّٞ يُوقَدُ مِن شَجَرَةٖ مُّبَٰرَكَةٖ زَيۡتُونَةٖ لَّا شَرۡقِيَّةٖ وَلَا غَرۡبِيَّةٖ يَكَادُ زَيۡتُهَا يُضِيٓءُ وَلَوۡ لَمۡ تَمۡسَسۡهُ نَارٞۚ نُّورٌ عَلَىٰ نُورٖۚ يَهۡدِي ٱللَّهُ لِنُورِهِۦ مَن يَشَآءُۚ وَيَضۡرِبُ ٱللَّهُ ٱلۡأَمۡثَٰلَ لِلنَّاسِۗ وَٱللَّهُ بِكُلِّ شَيۡءٍ عَلِيمٞ", "Allahu nooru alssamawati waalardi mathalu noorihi kamishkatin feeha misbahun almisbahu fee zujajatin alzzujajatu kaannaha kawkabun durriyyun yooqadu min shajaratin mubarakatin zaytoonatin la sharqiyyatin wala gharbiyyatin yakadu zaytuha yudeeo walaw lam tamsashu narun noorun AAala noorin yahdee Allahu linoorihi man yashao wayadribu Allahu alamthala lilnnasi waAllahu bikulli shayin AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 36, "[Such niches are] in mosques which Allah has ordered to be raised and that His name be mentioned therein; exalting Him within them in the morning and the evenings", "فِي بُيُوتٍ أَذِنَ ٱللَّهُ أَن تُرۡفَعَ وَيُذۡكَرَ فِيهَا ٱسۡمُهُۥ يُسَبِّحُ لَهُۥ فِيهَا بِٱلۡغُدُوِّ وَٱلۡأٓصَالِ", "Fee buyootin athina Allahu an turfaAAa wayuthkara feeha ismuhu yusabbihu lahu feeha bialghuduwwi waalasali");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 37, "[Are] men whom neither commerce nor sale distracts from the remembrance of Allah and performance of prayer and giving of zakah. They fear a Day in which the hearts and eyes will [fearfully] turn about", "رِجَالٞ لَّا تُلۡهِيهِمۡ تِجَٰرَةٞ وَلَا بَيۡعٌ عَن ذِكۡرِ ٱللَّهِ وَإِقَامِ ٱلصَّلَوٰةِ وَإِيتَآءِ ٱلزَّكَوٰةِ يَخَافُونَ يَوۡمٗا تَتَقَلَّبُ فِيهِ ٱلۡقُلُوبُ وَٱلۡأَبۡصَٰرُ", "Rijalun la tulheehim tijaratun wala bayAAun AAan thikri Allahi waiqami alssalati waeetai alzzakati yakhafoona yawman tataqallabu feehi alquloobu waalabsaru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 38, "That Allah may reward them [according to] the best of what they did and increase them from His bounty. And Allah gives provision to whom He wills without account", "لِيَجۡزِيَهُمُ ٱللَّهُ أَحۡسَنَ مَا عَمِلُواْ وَيَزِيدَهُم مِّن فَضۡلِهِۦۗ وَٱللَّهُ يَرۡزُقُ مَن يَشَآءُ بِغَيۡرِ حِسَابٖ", "Liyajziyahumu Allahu ahsana ma AAamiloo wayazeedahum min fadlihi waAllahu yarzuqu man yashao bighayri hisabin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 39, "But those who disbelieved - their deeds are like a mirage in a lowland which a thirsty one thinks is water until, when he comes to it, he finds it is nothing but finds Allah before Him, and He will pay him in full his due; and Allah is swift in account", "وَٱلَّذِينَ كَفَرُوٓاْ أَعۡمَٰلُهُمۡ كَسَرَابِۭ بِقِيعَةٖ يَحۡسَبُهُ ٱلظَّمۡـَٔانُ مَآءً حَتَّىٰٓ إِذَا جَآءَهُۥ لَمۡ يَجِدۡهُ شَيۡـٔٗا وَوَجَدَ ٱللَّهَ عِندَهُۥ فَوَفَّىٰهُ حِسَابَهُۥۗ وَٱللَّهُ سَرِيعُ ٱلۡحِسَابِ", "Waallatheena kafaroo aAAmaluhum kasarabin biqeeAAatin yahsabuhu alththamanu maan hatta itha jaahu lam yajidhu shayan wawajada Allaha AAindahu fawaffahu hisabahu waAllahu sareeAAu alhisabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 40, "Or [they are] like darknesses within an unfathomable sea which is covered by waves, upon which are waves, over which are clouds - darknesses, some of them upon others. When one puts out his hand [therein], he can hardly see it. And he to whom Allah has not granted light - for him there is no light", "أَوۡ كَظُلُمَٰتٖ فِي بَحۡرٖ لُّجِّيّٖ يَغۡشَىٰهُ مَوۡجٞ مِّن فَوۡقِهِۦ مَوۡجٞ مِّن فَوۡقِهِۦ سَحَابٞۚ ظُلُمَٰتُۢ بَعۡضُهَا فَوۡقَ بَعۡضٍ إِذَآ أَخۡرَجَ يَدَهُۥ لَمۡ يَكَدۡ يَرَىٰهَاۗ وَمَن لَّمۡ يَجۡعَلِ ٱللَّهُ لَهُۥ نُورٗا فَمَا لَهُۥ مِن نُّورٍ", "Aw kathulumatin fee bahrin lujjiyyin yaghshahu mawjun min fawqihi mawjun min fawqihi sahabun thulumatun baAAduha fawqa baAAdin itha akhraja yadahu lam yakad yaraha waman lam yajAAali Allahu lahu nooran fama lahu min noorin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 41, "Do you not see that Allah is exalted by whomever is within the heavens and the earth and [by] the birds with wings spread [in flight]? Each [of them] has known his [means of] prayer and exalting [Him], and Allah is Knowing of what they do", "أَلَمۡ تَرَ أَنَّ ٱللَّهَ يُسَبِّحُ لَهُۥ مَن فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَٱلطَّيۡرُ صَـٰٓفَّـٰتٖۖ كُلّٞ قَدۡ عَلِمَ صَلَاتَهُۥ وَتَسۡبِيحَهُۥۗ وَٱللَّهُ عَلِيمُۢ بِمَا يَفۡعَلُونَ", "Alam tara anna Allaha yusabbihu lahu man fee alssamawati waalardi waalttayru saffatin kullun qad AAalima salatahu watasbeehahu waAllahu AAaleemun bima yafAAaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 42, "And to Allah belongs the dominion of the heavens and the earth, and to Allah is the destination", "وَلِلَّهِ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ وَإِلَى ٱللَّهِ ٱلۡمَصِيرُ", "Walillahi mulku alssamawati waalardi waila Allahi almaseeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 43, "Do you not see that Allah drives clouds? Then He brings them together, then He makes them into a mass, and you see the rain emerge from within it. And He sends down from the sky, mountains [of clouds] within which is hail, and He strikes with it whom He wills and averts it from whom He wills. The flash of its lightening almost takes away the eyesight", "أَلَمۡ تَرَ أَنَّ ٱللَّهَ يُزۡجِي سَحَابٗا ثُمَّ يُؤَلِّفُ بَيۡنَهُۥ ثُمَّ يَجۡعَلُهُۥ رُكَامٗا فَتَرَى ٱلۡوَدۡقَ يَخۡرُجُ مِنۡ خِلَٰلِهِۦ وَيُنَزِّلُ مِنَ ٱلسَّمَآءِ مِن جِبَالٖ فِيهَا مِنۢ بَرَدٖ فَيُصِيبُ بِهِۦ مَن يَشَآءُ وَيَصۡرِفُهُۥ عَن مَّن يَشَآءُۖ يَكَادُ سَنَا بَرۡقِهِۦ يَذۡهَبُ بِٱلۡأَبۡصَٰرِ", "Alam tara anna Allaha yuzjee sahaban thumma yuallifu baynahu thumma yajAAaluhu rukaman fatara alwadqa yakhruju min khilalihi wayunazzilu mina alssamai min jibalin feeha min baradin fayuseebu bihi man yashao wayasrifuhu AAan man yashao yakadu sana barqihi yathhabu bialabsari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 44, "Allah alternates the night and the day. Indeed in that is a lesson for those who have vision", "يُقَلِّبُ ٱللَّهُ ٱلَّيۡلَ وَٱلنَّهَارَۚ إِنَّ فِي ذَٰلِكَ لَعِبۡرَةٗ لِّأُوْلِي ٱلۡأَبۡصَٰرِ", "Yuqallibu Allahu allayla waalnnahara inna fee thalika laAAibratan liolee alabsari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 45, "Allah has created every [living] creature from water. And of them are those that move on their bellies, and of them are those that walk on two legs, and of them are those that walk on four. Allah creates what He wills. Indeed, Allah is over all things competent", "وَٱللَّهُ خَلَقَ كُلَّ دَآبَّةٖ مِّن مَّآءٖۖ فَمِنۡهُم مَّن يَمۡشِي عَلَىٰ بَطۡنِهِۦ وَمِنۡهُم مَّن يَمۡشِي عَلَىٰ رِجۡلَيۡنِ وَمِنۡهُم مَّن يَمۡشِي عَلَىٰٓ أَرۡبَعٖۚ يَخۡلُقُ ٱللَّهُ مَا يَشَآءُۚ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ", "WaAllahu khalaqa kulla dabbatin min main faminhum man yamshee AAala batnihi waminhum man yamshee AAala rijlayni waminhum man yamshee AAala arbaAAin yakhluqu Allahu ma yashao inna Allaha AAala kulli shayin qadeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 46, "We have certainly sent down distinct verses. And Allah guides whom He wills to a straight path", "لَّقَدۡ أَنزَلۡنَآ ءَايَٰتٖ مُّبَيِّنَٰتٖۚ وَٱللَّهُ يَهۡدِي مَن يَشَآءُ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ", "Laqad anzalna ayatin mubayyinatin waAllahu yahdee man yashao ila siratin mustaqeemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 47, "But the hypocrites say, ""We have believed in Allah and in the Messenger, and we obey""; then a party of them turns away after that. And those are not believers", "وَيَقُولُونَ ءَامَنَّا بِٱللَّهِ وَبِٱلرَّسُولِ وَأَطَعۡنَا ثُمَّ يَتَوَلَّىٰ فَرِيقٞ مِّنۡهُم مِّنۢ بَعۡدِ ذَٰلِكَۚ وَمَآ أُوْلَـٰٓئِكَ بِٱلۡمُؤۡمِنِينَ", "Wayaqooloona amanna biAllahi wabialrrasooli waataAAna thumma yatawalla fareequn minhum min baAAdi thalika wama olaika bialmumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 48, "And when they are called to [the words of] Allah and His Messenger to judge between them, at once a party of them turns aside [in refusal]", "وَإِذَا دُعُوٓاْ إِلَى ٱللَّهِ وَرَسُولِهِۦ لِيَحۡكُمَ بَيۡنَهُمۡ إِذَا فَرِيقٞ مِّنۡهُم مُّعۡرِضُونَ", "Waitha duAAoo ila Allahi warasoolihi liyahkuma baynahum itha fareequn minhum muAAridoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 49, "But if the right is theirs, they come to him in prompt obedience", "وَإِن يَكُن لَّهُمُ ٱلۡحَقُّ يَأۡتُوٓاْ إِلَيۡهِ مُذۡعِنِينَ", "Wain yakun lahumu alhaqqu yatoo ilayhi muthAAineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 50, "Is there disease in their hearts? Or have they doubted? Or do they fear that Allah will be unjust to them, or His Messenger? Rather, it is they who are the wrongdoers", "أَفِي قُلُوبِهِم مَّرَضٌ أَمِ ٱرۡتَابُوٓاْ أَمۡ يَخَافُونَ أَن يَحِيفَ ٱللَّهُ عَلَيۡهِمۡ وَرَسُولُهُۥۚ بَلۡ أُوْلَـٰٓئِكَ هُمُ ٱلظَّـٰلِمُونَ", "Afee quloobihim maradun ami irtaboo am yakhafoona an yaheefa Allahu AAalayhim warasooluhu bal olaika humu alththalimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 51, "The only statement of the [true] believers when they are called to Allah and His Messenger to judge between them is that they say, ""We hear and we obey."" And those are the successful", "إِنَّمَا كَانَ قَوۡلَ ٱلۡمُؤۡمِنِينَ إِذَا دُعُوٓاْ إِلَى ٱللَّهِ وَرَسُولِهِۦ لِيَحۡكُمَ بَيۡنَهُمۡ أَن يَقُولُواْ سَمِعۡنَا وَأَطَعۡنَاۚ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُفۡلِحُونَ", "Innama kana qawla almumineena itha duAAoo ila Allahi warasoolihi liyahkuma baynahum an yaqooloo samiAAna waataAAna waolaika humu almuflihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 52, "And whoever obeys Allah and His Messenger and fears Allah and is conscious of Him - it is those who are the attainers", "وَمَن يُطِعِ ٱللَّهَ وَرَسُولَهُۥ وَيَخۡشَ ٱللَّهَ وَيَتَّقۡهِ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡفَآئِزُونَ", "Waman yutiAAi Allaha warasoolahu wayakhsha Allaha wayattaqhi faolaika humu alfaizoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 53, "And they swear by Allah their strongest oaths that if you ordered them, they would go forth [in Allah 's cause]. Say, ""Do not swear. [Such] obedience is known. Indeed, Allah is Acquainted with that which you do", "۞وَأَقۡسَمُواْ بِٱللَّهِ جَهۡدَ أَيۡمَٰنِهِمۡ لَئِنۡ أَمَرۡتَهُمۡ لَيَخۡرُجُنَّۖ قُل لَّا تُقۡسِمُواْۖ طَاعَةٞ مَّعۡرُوفَةٌۚ إِنَّ ٱللَّهَ خَبِيرُۢ بِمَا تَعۡمَلُونَ", "Waaqsamoo biAllahi jahda aymanihim lain amartahum layakhrujunna qul la tuqsimoo taAAatun maAAroofatun inna Allaha khabeerun bima taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 54, "Say, ""Obey Allah and obey the Messenger; but if you turn away - then upon him is only that [duty] with which he has been charged, and upon you is that with which you have been charged. And if you obey him, you will be [rightly] guided. And there is not upon the Messenger except the [responsibility for] clear notification", "قُلۡ أَطِيعُواْ ٱللَّهَ وَأَطِيعُواْ ٱلرَّسُولَۖ فَإِن تَوَلَّوۡاْ فَإِنَّمَا عَلَيۡهِ مَا حُمِّلَ وَعَلَيۡكُم مَّا حُمِّلۡتُمۡۖ وَإِن تُطِيعُوهُ تَهۡتَدُواْۚ وَمَا عَلَى ٱلرَّسُولِ إِلَّا ٱلۡبَلَٰغُ ٱلۡمُبِينُ", "Qul ateeAAoo Allaha waateeAAoo alrrasoola fain tawallaw fainnama AAalayhi ma hummila waAAalaykum ma hummiltum wain tuteeAAoohu tahtadoo wama AAala alrrasooli illa albalaghu almubeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 55, "Allah has promised those who have believed among you and done righteous deeds that He will surely grant them succession [to authority] upon the earth just as He granted it to those before them and that He will surely establish for them [therein] their religion which He has preferred for them and that He will surely substitute for them, after their fear, security, [for] they worship Me, not associating anything with Me. But whoever disbelieves after that - then those are the defiantly disobedient", "وَعَدَ ٱللَّهُ ٱلَّذِينَ ءَامَنُواْ مِنكُمۡ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ لَيَسۡتَخۡلِفَنَّهُمۡ فِي ٱلۡأَرۡضِ كَمَا ٱسۡتَخۡلَفَ ٱلَّذِينَ مِن قَبۡلِهِمۡ وَلَيُمَكِّنَنَّ لَهُمۡ دِينَهُمُ ٱلَّذِي ٱرۡتَضَىٰ لَهُمۡ وَلَيُبَدِّلَنَّهُم مِّنۢ بَعۡدِ خَوۡفِهِمۡ أَمۡنٗاۚ يَعۡبُدُونَنِي لَا يُشۡرِكُونَ بِي شَيۡـٔٗاۚ وَمَن كَفَرَ بَعۡدَ ذَٰلِكَ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡفَٰسِقُونَ", "WaAAada Allahu allatheena amanoo minkum waAAamiloo alssalihati layastakhlifannahum fee alardi kama istakhlafa allatheena min qablihim walayumakkinanna lahum deenahumu allathee irtada lahum walayubaddilannahum min baAAdi khawfihim amnan yaAAbudoonanee la yushrikoona bee shayan waman kafara baAAda thalika faolaika humu alfasiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 56, "And establish prayer and give zakah and obey the Messenger - that you may receive mercy", "وَأَقِيمُواْ ٱلصَّلَوٰةَ وَءَاتُواْ ٱلزَّكَوٰةَ وَأَطِيعُواْ ٱلرَّسُولَ لَعَلَّكُمۡ تُرۡحَمُونَ", "Waaqeemoo alssalata waatoo alzzakata waateeAAoo alrrasoola laAAallakum turhamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 57, "Never think that the disbelievers are causing failure [to Allah] upon the earth. Their refuge will be the Fire - and how wretched the destination", "لَا تَحۡسَبَنَّ ٱلَّذِينَ كَفَرُواْ مُعۡجِزِينَ فِي ٱلۡأَرۡضِۚ وَمَأۡوَىٰهُمُ ٱلنَّارُۖ وَلَبِئۡسَ ٱلۡمَصِيرُ", "La tahsabanna allatheena kafaroo muAAjizeena fee alardi wamawahumu alnnaru walabisa almaseeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 58, "O you who have believed, let those whom your right hands possess and those who have not [yet] reached puberty among you ask permission of you [before entering] at three times: before the dawn prayer and when you put aside your clothing [for rest] at noon and after the night prayer. [These are] three times of privacy for you. There is no blame upon you nor upon them beyond these [periods], for they continually circulate among you - some of you, among others. Thus does Allah make clear to you the verses; and Allah is Knowing and Wise", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لِيَسۡتَـٔۡذِنكُمُ ٱلَّذِينَ مَلَكَتۡ أَيۡمَٰنُكُمۡ وَٱلَّذِينَ لَمۡ يَبۡلُغُواْ ٱلۡحُلُمَ مِنكُمۡ ثَلَٰثَ مَرَّـٰتٖۚ مِّن قَبۡلِ صَلَوٰةِ ٱلۡفَجۡرِ وَحِينَ تَضَعُونَ ثِيَابَكُم مِّنَ ٱلظَّهِيرَةِ وَمِنۢ بَعۡدِ صَلَوٰةِ ٱلۡعِشَآءِۚ ثَلَٰثُ عَوۡرَٰتٖ لَّكُمۡۚ لَيۡسَ عَلَيۡكُمۡ وَلَا عَلَيۡهِمۡ جُنَاحُۢ بَعۡدَهُنَّۚ طَوَّـٰفُونَ عَلَيۡكُم بَعۡضُكُمۡ عَلَىٰ بَعۡضٖۚ كَذَٰلِكَ يُبَيِّنُ ٱللَّهُ لَكُمُ ٱلۡأٓيَٰتِۗ وَٱللَّهُ عَلِيمٌ حَكِيمٞ", "Ya ayyuha allatheena amanoo liyastathinkumu allatheena malakat aymanukum waallatheena lam yablughoo alhuluma minkum thalatha marratin min qabli salati alfajri waheena tadaAAoona thiyabakum mina alththaheerati wamin baAAdi salati alAAishai thalathu AAawratin lakum laysa AAalaykum wala AAalayhim junahun baAAdahunna tawwafoona AAalaykum baAAdukum AAala baAAdin kathalika yubayyinu Allahu lakumu alayati waAllahu AAaleemun hakeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 59, "And when the children among you reach puberty, let them ask permission [at all times] as those before them have done. Thus does Allah make clear to you His verses; and Allah is Knowing and Wise", "وَإِذَا بَلَغَ ٱلۡأَطۡفَٰلُ مِنكُمُ ٱلۡحُلُمَ فَلۡيَسۡتَـٔۡذِنُواْ كَمَا ٱسۡتَـٔۡذَنَ ٱلَّذِينَ مِن قَبۡلِهِمۡۚ كَذَٰلِكَ يُبَيِّنُ ٱللَّهُ لَكُمۡ ءَايَٰتِهِۦۗ وَٱللَّهُ عَلِيمٌ حَكِيمٞ", "Waitha balagha alatfalu minkumu alhuluma falyastathinoo kama istathana allatheena min qablihim kathalika yubayyinu Allahu lakum ayatihi waAllahu AAaleemun hakeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 60, "And women of post-menstrual age who have no desire for marriage - there is no blame upon them for putting aside their outer garments [but] not displaying adornment. But to modestly refrain [from that] is better for them. And Allah is Hearing and Knowing", "وَٱلۡقَوَٰعِدُ مِنَ ٱلنِّسَآءِ ٱلَّـٰتِي لَا يَرۡجُونَ نِكَاحٗا فَلَيۡسَ عَلَيۡهِنَّ جُنَاحٌ أَن يَضَعۡنَ ثِيَابَهُنَّ غَيۡرَ مُتَبَرِّجَٰتِۭ بِزِينَةٖۖ وَأَن يَسۡتَعۡفِفۡنَ خَيۡرٞ لَّهُنَّۗ وَٱللَّهُ سَمِيعٌ عَلِيمٞ", "WaalqawaAAidu mina alnnisai allatee la yarjoona nikahan falaysa AAalayhinna junahun an yadaAAna thiyabahunna ghayra mutabarrijatin bizeenatin waan yastaAAfifna khayrun lahunna waAllahu sameeAAun AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 61, "There is not upon the blind [any] constraint nor upon the lame constraint nor upon the ill constraint nor upon yourselves when you eat from your [own] houses or the houses of your fathers or the houses of your mothers or the houses of your brothers or the houses of your sisters or the houses of your father's brothers or the houses of your father's sisters or the houses of your mother's brothers or the houses of your mother's sisters or [from houses] whose keys you possess or [from the house] of your friend. There is no blame upon you whether you eat together or separately. But when you enter houses, give greetings of peace upon each other - a greeting from Allah, blessed and good. Thus does Allah make clear to you the verses [of ordinance] that you may understand", "لَّيۡسَ عَلَى ٱلۡأَعۡمَىٰ حَرَجٞ وَلَا عَلَى ٱلۡأَعۡرَجِ حَرَجٞ وَلَا عَلَى ٱلۡمَرِيضِ حَرَجٞ وَلَا عَلَىٰٓ أَنفُسِكُمۡ أَن تَأۡكُلُواْ مِنۢ بُيُوتِكُمۡ أَوۡ بُيُوتِ ءَابَآئِكُمۡ أَوۡ بُيُوتِ أُمَّهَٰتِكُمۡ أَوۡ بُيُوتِ إِخۡوَٰنِكُمۡ أَوۡ بُيُوتِ أَخَوَٰتِكُمۡ أَوۡ بُيُوتِ أَعۡمَٰمِكُمۡ أَوۡ بُيُوتِ عَمَّـٰتِكُمۡ أَوۡ بُيُوتِ أَخۡوَٰلِكُمۡ أَوۡ بُيُوتِ خَٰلَٰتِكُمۡ أَوۡ مَا مَلَكۡتُم مَّفَاتِحَهُۥٓ أَوۡ صَدِيقِكُمۡۚ لَيۡسَ عَلَيۡكُمۡ جُنَاحٌ أَن تَأۡكُلُواْ جَمِيعًا أَوۡ أَشۡتَاتٗاۚ فَإِذَا دَخَلۡتُم بُيُوتٗا فَسَلِّمُواْ عَلَىٰٓ أَنفُسِكُمۡ تَحِيَّةٗ مِّنۡ عِندِ ٱللَّهِ مُبَٰرَكَةٗ طَيِّبَةٗۚ كَذَٰلِكَ يُبَيِّنُ ٱللَّهُ لَكُمُ ٱلۡأٓيَٰتِ لَعَلَّكُمۡ تَعۡقِلُونَ", "Laysa AAala alaAAma harajun wala AAala alaAAraji harajun wala AAala almareedi harajun wala AAala anfusikum an takuloo min buyootikum aw buyooti abaikum aw buyooti ommahatikum aw buyooti ikhwanikum aw buyooti akhawatikum aw buyooti aAAmamikum aw buyooti AAammatikum aw buyooti akhwalikum aw buyooti khalatikum aw ma malaktum mafatihahu aw sadeeqikum laysa AAalaykum junahun an takuloo jameeAAan aw ashtatan faitha dakhaltum buyootan fasallimoo AAala anfusikum tahiyyatan min AAindi Allahi mubarakatan tayyibatan kathalika yubayyinu Allahu lakumu alayati laAAallakum taAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 62, "The believers are only those who believe in Allah and His Messenger and, when they are [meeting] with him for a matter of common interest, do not depart until they have asked his permission. Indeed, those who ask your permission, [O Muhammad] - those are the ones who believe in Allah and His Messenger. So when they ask your permission for something of their affairs, then give permission to whom you will among them and ask forgiveness for them of Allah. Indeed, Allah is Forgiving and Merciful", "إِنَّمَا ٱلۡمُؤۡمِنُونَ ٱلَّذِينَ ءَامَنُواْ بِٱللَّهِ وَرَسُولِهِۦ وَإِذَا كَانُواْ مَعَهُۥ عَلَىٰٓ أَمۡرٖ جَامِعٖ لَّمۡ يَذۡهَبُواْ حَتَّىٰ يَسۡتَـٔۡذِنُوهُۚ إِنَّ ٱلَّذِينَ يَسۡتَـٔۡذِنُونَكَ أُوْلَـٰٓئِكَ ٱلَّذِينَ يُؤۡمِنُونَ بِٱللَّهِ وَرَسُولِهِۦۚ فَإِذَا ٱسۡتَـٔۡذَنُوكَ لِبَعۡضِ شَأۡنِهِمۡ فَأۡذَن لِّمَن شِئۡتَ مِنۡهُمۡ وَٱسۡتَغۡفِرۡ لَهُمُ ٱللَّهَۚ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ", "Innama almuminoona allatheena amanoo biAllahi warasoolihi waitha kanoo maAAahu AAala amrin jamiAAin lam yathhaboo hatta yastathinoohu inna allatheena yastathinoonaka olaika allatheena yuminoona biAllahi warasoolihi faitha istathanooka libaAAdi shanihim fathan liman shita minhum waistaghfir lahumu Allaha inna Allaha ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 63, "Do not make [your] calling of the Messenger among yourselves as the call of one of you to another. Already Allah knows those of you who slip away, concealed by others. So let those beware who dissent from the Prophet's order, lest fitnah strike them or a painful punishment", "لَّا تَجۡعَلُواْ دُعَآءَ ٱلرَّسُولِ بَيۡنَكُمۡ كَدُعَآءِ بَعۡضِكُم بَعۡضٗاۚ قَدۡ يَعۡلَمُ ٱللَّهُ ٱلَّذِينَ يَتَسَلَّلُونَ مِنكُمۡ لِوَاذٗاۚ فَلۡيَحۡذَرِ ٱلَّذِينَ يُخَالِفُونَ عَنۡ أَمۡرِهِۦٓ أَن تُصِيبَهُمۡ فِتۡنَةٌ أَوۡ يُصِيبَهُمۡ عَذَابٌ أَلِيمٌ", "La tajAAaloo duAAaa alrrasooli baynakum kaduAAai baAAdikum baAAdan qad yaAAlamu Allahu allatheena yatasallaloona minkum liwathan falyahthari allatheena yukhalifoona AAan amrihi an tuseebahum fitnatun aw yuseebahum AAathabun aleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (24, 64, "Unquestionably, to Allah belongs whatever is in the heavens and earth. Already He knows that upon which you [stand] and [knows] the Day when they will be returned to Him and He will inform them of what they have done. And Allah is Knowing of all things", "أَلَآ إِنَّ لِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ قَدۡ يَعۡلَمُ مَآ أَنتُمۡ عَلَيۡهِ وَيَوۡمَ يُرۡجَعُونَ إِلَيۡهِ فَيُنَبِّئُهُم بِمَا عَمِلُواْۗ وَٱللَّهُ بِكُلِّ شَيۡءٍ عَلِيمُۢ", "Ala inna lillahi ma fee alssamawati waalardi qad yaAAlamu ma antum AAalayhi wayawma yurjaAAoona ilayhi fayunabbiohum bima AAamiloo waAllahu bikulli shayin AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 1, "Blessed is He who sent down the Criterion upon His Servant that he may be to the worlds a warner", "تَبَارَكَ ٱلَّذِي نَزَّلَ ٱلۡفُرۡقَانَ عَلَىٰ عَبۡدِهِۦ لِيَكُونَ لِلۡعَٰلَمِينَ نَذِيرًا", "Tabaraka allathee nazzala alfurqana AAala AAabdihi liyakoona lilAAalameena natheeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 2, "He to whom belongs the dominion of the heavens and the earth and who has not taken a son and has not had a partner in dominion and has created each thing and determined it with [precise] determination", "ٱلَّذِي لَهُۥ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَلَمۡ يَتَّخِذۡ وَلَدٗا وَلَمۡ يَكُن لَّهُۥ شَرِيكٞ فِي ٱلۡمُلۡكِ وَخَلَقَ كُلَّ شَيۡءٖ فَقَدَّرَهُۥ تَقۡدِيرٗا", "Allathee lahu mulku alssamawati waalardi walam yattakhith waladan walam yakun lahu shareekun fee almulki wakhalaqa kulla shayin faqaddarahu taqdeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 3, "But they have taken besides Him gods which create nothing, while they are created, and possess not for themselves any harm or benefit and possess not [power to cause] death or life or resurrection", "وَٱتَّخَذُواْ مِن دُونِهِۦٓ ءَالِهَةٗ لَّا يَخۡلُقُونَ شَيۡـٔٗا وَهُمۡ يُخۡلَقُونَ وَلَا يَمۡلِكُونَ لِأَنفُسِهِمۡ ضَرّٗا وَلَا نَفۡعٗا وَلَا يَمۡلِكُونَ مَوۡتٗا وَلَا حَيَوٰةٗ وَلَا نُشُورٗا", "Waittakhathoo min doonihi alihatan la yakhluqoona shayan wahum yukhlaqoona wala yamlikoona lianfusihim darran wala nafAAan wala yamlikoona mawtan wala hayatan wala nushooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 4, "And those who disbelieve say, ""This [Qur'an] is not except a falsehood he invented, and another people assisted him in it."" But they have committed an injustice and a lie", "وَقَالَ ٱلَّذِينَ كَفَرُوٓاْ إِنۡ هَٰذَآ إِلَّآ إِفۡكٌ ٱفۡتَرَىٰهُ وَأَعَانَهُۥ عَلَيۡهِ قَوۡمٌ ءَاخَرُونَۖ فَقَدۡ جَآءُو ظُلۡمٗا وَزُورٗا", "Waqala allatheena kafaroo in hatha illa ifkun iftarahu waaAAanahu AAalayhi qawmun akharoona faqad jaoo thulman wazooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 5, "And they say, ""Legends of the former peoples which he has written down, and they are dictated to him morning and afternoon", "وَقَالُوٓاْ أَسَٰطِيرُ ٱلۡأَوَّلِينَ ٱكۡتَتَبَهَا فَهِيَ تُمۡلَىٰ عَلَيۡهِ بُكۡرَةٗ وَأَصِيلٗا", "Waqaloo asateeru alawwaleena iktatabaha fahiya tumla AAalayhi bukratan waaseelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 6, "Say, [O Muhammad], ""It has been revealed by He who knows [every] secret within the heavens and the earth. Indeed, He is ever Forgiving and Merciful", "قُلۡ أَنزَلَهُ ٱلَّذِي يَعۡلَمُ ٱلسِّرَّ فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ إِنَّهُۥ كَانَ غَفُورٗا رَّحِيمٗا", "Qul anzalahu allathee yaAAlamu alssirra fee alssamawati waalardi innahu kana ghafooran raheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 7, "And they say, ""What is this messenger that eats food and walks in the markets? Why was there not sent down to him an angel so he would be with him a warner", "وَقَالُواْ مَالِ هَٰذَا ٱلرَّسُولِ يَأۡكُلُ ٱلطَّعَامَ وَيَمۡشِي فِي ٱلۡأَسۡوَاقِ لَوۡلَآ أُنزِلَ إِلَيۡهِ مَلَكٞ فَيَكُونَ مَعَهُۥ نَذِيرًا", "Waqaloo mali hatha alrrasooli yakulu alttaAAama wayamshee fee alaswaqi lawla onzila ilayhi malakun fayakoona maAAahu natheeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 8, "Or [why is not] a treasure presented to him [from heaven], or does he [not] have a garden from which he eats?"" And the wrongdoers say, ""You follow not but a man affected by magic", "أَوۡ يُلۡقَىٰٓ إِلَيۡهِ كَنزٌ أَوۡ تَكُونُ لَهُۥ جَنَّةٞ يَأۡكُلُ مِنۡهَاۚ وَقَالَ ٱلظَّـٰلِمُونَ إِن تَتَّبِعُونَ إِلَّا رَجُلٗا مَّسۡحُورًا", "Aw yulqa ilayhi kanzun aw takoonu lahu jannatun yakulu minha waqala alththalimoona in tattabiAAoona illa rajulan mashooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 9, "Look how they strike for you comparisons; but they have strayed, so they cannot [find] a way", "ٱنظُرۡ كَيۡفَ ضَرَبُواْ لَكَ ٱلۡأَمۡثَٰلَ فَضَلُّواْ فَلَا يَسۡتَطِيعُونَ سَبِيلٗا", "Onthur kayfa daraboo laka alamthala fadalloo fala yastateeAAoona sabeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 10, "Blessed is He who, if He willed, could have made for you [something] better than that - gardens beneath which rivers flow - and could make for you palaces", "تَبَارَكَ ٱلَّذِيٓ إِن شَآءَ جَعَلَ لَكَ خَيۡرٗا مِّن ذَٰلِكَ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ وَيَجۡعَل لَّكَ قُصُورَۢا", "Tabaraka allathee in shaa jaAAala laka khayran min thalika jannatin tajree min tahtiha alanharu wayajAAal laka qusooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 11, "But they have denied the Hour, and We have prepared for those who deny the Hour a Blaze", "بَلۡ كَذَّبُواْ بِٱلسَّاعَةِۖ وَأَعۡتَدۡنَا لِمَن كَذَّبَ بِٱلسَّاعَةِ سَعِيرًا", "Bal kaththaboo bialssaAAati waaAAtadna liman kaththaba bialssaAAati saAAeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 12, "When the Hellfire sees them from a distant place, they will hear its fury and roaring", "إِذَا رَأَتۡهُم مِّن مَّكَانِۭ بَعِيدٖ سَمِعُواْ لَهَا تَغَيُّظٗا وَزَفِيرٗا", "Itha raathum min makanin baAAeedin samiAAoo laha taghayyuthan wazafeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 13, "And when they are thrown into a narrow place therein bound in chains, they will cry out thereupon for destruction", "وَإِذَآ أُلۡقُواْ مِنۡهَا مَكَانٗا ضَيِّقٗا مُّقَرَّنِينَ دَعَوۡاْ هُنَالِكَ ثُبُورٗا", "Waitha olqoo minha makanan dayyiqan muqarraneena daAAaw hunalika thubooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 14, "[They will be told], ""Do not cry this Day for one destruction but cry for much destruction", "لَّا تَدۡعُواْ ٱلۡيَوۡمَ ثُبُورٗا وَٰحِدٗا وَٱدۡعُواْ ثُبُورٗا كَثِيرٗا", "La tadAAoo alyawma thubooran wahidan waodAAoo thubooran katheeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 15, "Say, ""Is that better or the Garden of Eternity which is promised to the righteous? It will be for them a reward and destination", "قُلۡ أَذَٰلِكَ خَيۡرٌ أَمۡ جَنَّةُ ٱلۡخُلۡدِ ٱلَّتِي وُعِدَ ٱلۡمُتَّقُونَۚ كَانَتۡ لَهُمۡ جَزَآءٗ وَمَصِيرٗا", "Qul athalika khayrun am jannatu alkhuldi allatee wuAAida almuttaqoona kanat lahum jazaan wamaseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 16, "For them therein is whatever they wish, [while] abiding eternally. It is ever upon your Lord a promise [worthy to be] requested", "لَّهُمۡ فِيهَا مَا يَشَآءُونَ خَٰلِدِينَۚ كَانَ عَلَىٰ رَبِّكَ وَعۡدٗا مَّسۡـُٔولٗا", "Lahum feeha ma yashaoona khalideena kana AAala rabbika waAAdan masoolan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 17, "And [mention] the Day He will gather them and that which they worship besides Allah and will say, ""Did you mislead these, My servants, or did they [themselves] stray from the way", "وَيَوۡمَ يَحۡشُرُهُمۡ وَمَا يَعۡبُدُونَ مِن دُونِ ٱللَّهِ فَيَقُولُ ءَأَنتُمۡ أَضۡلَلۡتُمۡ عِبَادِي هَـٰٓؤُلَآءِ أَمۡ هُمۡ ضَلُّواْ ٱلسَّبِيلَ", "Wayawma yahshuruhum wama yaAAbudoona min dooni Allahi fayaqoolu aantum adlaltum AAibadee haolai am hum dalloo alssabeela");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 18, "They will say, ""Exalted are You! It was not for us to take besides You any allies. But You provided comforts for them and their fathers until they forgot the message and became a people ruined", "قَالُواْ سُبۡحَٰنَكَ مَا كَانَ يَنۢبَغِي لَنَآ أَن نَّتَّخِذَ مِن دُونِكَ مِنۡ أَوۡلِيَآءَ وَلَٰكِن مَّتَّعۡتَهُمۡ وَءَابَآءَهُمۡ حَتَّىٰ نَسُواْ ٱلذِّكۡرَ وَكَانُواْ قَوۡمَۢا بُورٗا", "Qaloo subhanaka ma kana yanbaghee lana an nattakhitha min doonika min awliyaa walakin mattaAAtahum waabaahum hatta nasoo alththikra wakanoo qawman booran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 19, "So they will deny you, [disbelievers], in what you say, and you cannot avert [punishment] or [find] help. And whoever commits injustice among you - We will make him taste a great punishment", "فَقَدۡ كَذَّبُوكُم بِمَا تَقُولُونَ فَمَا تَسۡتَطِيعُونَ صَرۡفٗا وَلَا نَصۡرٗاۚ وَمَن يَظۡلِم مِّنكُمۡ نُذِقۡهُ عَذَابٗا كَبِيرٗا", "Faqad kaththabookum bima taqooloona fama tastateeAAoona sarfan wala nasran waman yathlim minkum nuthiqhu AAathaban kabeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 20, "And We did not send before you, [O Muhammad], any of the messengers except that they ate food and walked in the markets. And We have made some of you [people] as trial for others - will you have patience? And ever is your Lord, Seeing", "وَمَآ أَرۡسَلۡنَا قَبۡلَكَ مِنَ ٱلۡمُرۡسَلِينَ إِلَّآ إِنَّهُمۡ لَيَأۡكُلُونَ ٱلطَّعَامَ وَيَمۡشُونَ فِي ٱلۡأَسۡوَاقِۗ وَجَعَلۡنَا بَعۡضَكُمۡ لِبَعۡضٖ فِتۡنَةً أَتَصۡبِرُونَۗ وَكَانَ رَبُّكَ بَصِيرٗا", "Wama arsalna qablaka mina almursaleena illa innahum layakuloona alttaAAama wayamshoona fee alaswaqi wajaAAalna baAAdakum libaAAdin fitnatan atasbiroona wakana rabbuka baseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 21, "And those who do not expect the meeting with Us say, ""Why were not angels sent down to us, or [why] do we [not] see our Lord?"" They have certainly become arrogant within themselves and [become] insolent with great insolence", "۞وَقَالَ ٱلَّذِينَ لَا يَرۡجُونَ لِقَآءَنَا لَوۡلَآ أُنزِلَ عَلَيۡنَا ٱلۡمَلَـٰٓئِكَةُ أَوۡ نَرَىٰ رَبَّنَاۗ لَقَدِ ٱسۡتَكۡبَرُواْ فِيٓ أَنفُسِهِمۡ وَعَتَوۡ عُتُوّٗا كَبِيرٗا", "Waqala allatheena la yarjoona liqaana lawla onzila AAalayna almalaikatu aw nara rabbana laqadi istakbaroo fee anfusihim waAAataw AAutuwwan kabeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 22, "The day they see the angels - no good tidings will there be that day for the criminals, and [the angels] will say, ""Prevented and inaccessible", "يَوۡمَ يَرَوۡنَ ٱلۡمَلَـٰٓئِكَةَ لَا بُشۡرَىٰ يَوۡمَئِذٖ لِّلۡمُجۡرِمِينَ وَيَقُولُونَ حِجۡرٗا مَّحۡجُورٗا", "Yawma yarawna almalaikata la bushra yawmaithin lilmujrimeena wayaqooloona hijran mahjooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 23, "And We will regard what they have done of deeds and make them as dust dispersed", "وَقَدِمۡنَآ إِلَىٰ مَا عَمِلُواْ مِنۡ عَمَلٖ فَجَعَلۡنَٰهُ هَبَآءٗ مَّنثُورًا", "Waqadimna ila ma AAamiloo min AAamalin fajaAAalnahu habaan manthooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 24, "The companions of Paradise, that Day, are [in] a better settlement and better resting place", "أَصۡحَٰبُ ٱلۡجَنَّةِ يَوۡمَئِذٍ خَيۡرٞ مُّسۡتَقَرّٗا وَأَحۡسَنُ مَقِيلٗا", "Ashabu aljannati yawmaithin khayrun mustaqarran waahsanu maqeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 25, "And [mention] the Day when the heaven will split open with [emerging] clouds, and the angels will be sent down in successive descent", "وَيَوۡمَ تَشَقَّقُ ٱلسَّمَآءُ بِٱلۡغَمَٰمِ وَنُزِّلَ ٱلۡمَلَـٰٓئِكَةُ تَنزِيلًا", "Wayawma tashaqqaqu alssamao bialghamami wanuzzila almalaikatu tanzeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 26, "True sovereignty, that Day, is for the Most Merciful. And it will be upon the disbelievers a difficult Day", "ٱلۡمُلۡكُ يَوۡمَئِذٍ ٱلۡحَقُّ لِلرَّحۡمَٰنِۚ وَكَانَ يَوۡمًا عَلَى ٱلۡكَٰفِرِينَ عَسِيرٗا", "Almulku yawmaithin alhaqqu lilrrahmani wakana yawman AAala alkafireena AAaseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 27, "And the Day the wrongdoer will bite on his hands [in regret] he will say, ""Oh, I wish I had taken with the Messenger a way", "وَيَوۡمَ يَعَضُّ ٱلظَّالِمُ عَلَىٰ يَدَيۡهِ يَقُولُ يَٰلَيۡتَنِي ٱتَّخَذۡتُ مَعَ ٱلرَّسُولِ سَبِيلٗا", "Wayawma yaAAaddu alththalimu AAala yadayhi yaqoolu ya laytanee ittakhathtu maAAa alrrasooli sabeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 28, "Oh, woe to me! I wish I had not taken that one as a friend", "يَٰوَيۡلَتَىٰ لَيۡتَنِي لَمۡ أَتَّخِذۡ فُلَانًا خَلِيلٗا", "Ya waylata laytanee lam attakhith fulanan khaleelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 29, "He led me away from the remembrance after it had come to me. And ever is Satan, to man, a deserter", "لَّقَدۡ أَضَلَّنِي عَنِ ٱلذِّكۡرِ بَعۡدَ إِذۡ جَآءَنِيۗ وَكَانَ ٱلشَّيۡطَٰنُ لِلۡإِنسَٰنِ خَذُولٗا", "Laqad adallanee AAani alththikri baAAda ith jaanee wakana alshshaytanu lilinsani khathoolan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 30, "And the Messenger has said, ""O my Lord, indeed my people have taken this Qur'an as [a thing] abandoned", "وَقَالَ ٱلرَّسُولُ يَٰرَبِّ إِنَّ قَوۡمِي ٱتَّخَذُواْ هَٰذَا ٱلۡقُرۡءَانَ مَهۡجُورٗا", "Waqala alrrasoolu ya rabbi inna qawmee ittakhathoo hatha alqurana mahjooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 31, "And thus have We made for every prophet an enemy from among the criminals. But sufficient is your Lord as a guide and a helper", "وَكَذَٰلِكَ جَعَلۡنَا لِكُلِّ نَبِيٍّ عَدُوّٗا مِّنَ ٱلۡمُجۡرِمِينَۗ وَكَفَىٰ بِرَبِّكَ هَادِيٗا وَنَصِيرٗا", "Wakathalika jaAAalna likulli nabiyyin AAaduwwan mina almujrimeena wakafa birabbika hadiyan wanaseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 32, "And those who disbelieve say, ""Why was the Qur'an not revealed to him all at once?"" Thus [it is] that We may strengthen thereby your heart. And We have spaced it distinctly", "وَقَالَ ٱلَّذِينَ كَفَرُواْ لَوۡلَا نُزِّلَ عَلَيۡهِ ٱلۡقُرۡءَانُ جُمۡلَةٗ وَٰحِدَةٗۚ كَذَٰلِكَ لِنُثَبِّتَ بِهِۦ فُؤَادَكَۖ وَرَتَّلۡنَٰهُ تَرۡتِيلٗا", "Waqala allatheena kafaroo lawla nuzzila AAalayhi alquranu jumlatan wahidatan kathalika linuthabbita bihi fuadaka warattalnahu tarteelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 33, "And they do not come to you with an argument except that We bring you the truth and the best explanation", "وَلَا يَأۡتُونَكَ بِمَثَلٍ إِلَّا جِئۡنَٰكَ بِٱلۡحَقِّ وَأَحۡسَنَ تَفۡسِيرًا", "Wala yatoonaka bimathalin illa jinaka bialhaqqi waahsana tafseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 34, "The ones who are gathered on their faces to Hell - those are the worst in position and farthest astray in [their] way", "ٱلَّذِينَ يُحۡشَرُونَ عَلَىٰ وُجُوهِهِمۡ إِلَىٰ جَهَنَّمَ أُوْلَـٰٓئِكَ شَرّٞ مَّكَانٗا وَأَضَلُّ سَبِيلٗا", "Allatheena yuhsharoona AAala wujoohihim ila jahannama olaika sharrun makanan waadallu sabeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 35, "And We had certainly given Moses the Scripture and appointed with him his brother Aaron as an assistant", "وَلَقَدۡ ءَاتَيۡنَا مُوسَى ٱلۡكِتَٰبَ وَجَعَلۡنَا مَعَهُۥٓ أَخَاهُ هَٰرُونَ وَزِيرٗا", "Walaqad atayna moosa alkitaba wajaAAalna maAAahu akhahu haroona wazeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 36, "And We said, ""Go both of you to the people who have denied Our signs."" Then We destroyed them with [complete] destruction", "فَقُلۡنَا ٱذۡهَبَآ إِلَى ٱلۡقَوۡمِ ٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا فَدَمَّرۡنَٰهُمۡ تَدۡمِيرٗا", "Faqulna ithhaba ila alqawmi allatheena kaththaboo biayatina fadammarnahum tadmeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 37, "And the people of Noah - when they denied the messengers, We drowned them, and We made them for mankind a sign. And We have prepared for the wrongdoers a painful punishment", "وَقَوۡمَ نُوحٖ لَّمَّا كَذَّبُواْ ٱلرُّسُلَ أَغۡرَقۡنَٰهُمۡ وَجَعَلۡنَٰهُمۡ لِلنَّاسِ ءَايَةٗۖ وَأَعۡتَدۡنَا لِلظَّـٰلِمِينَ عَذَابًا أَلِيمٗا", "Waqawma noohin lamma kaththaboo alrrusula aghraqnahum wajaAAalnahum lilnnasi ayatan waaAAtadna lilththalimeena AAathaban aleeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 38, "And [We destroyed] 'Aad and Thamud and the companions of the well and many generations between them", "وَعَادٗا وَثَمُودَاْ وَأَصۡحَٰبَ ٱلرَّسِّ وَقُرُونَۢا بَيۡنَ ذَٰلِكَ كَثِيرٗا", "WaAAadan wathamooda waashaba alrrassi waquroonan bayna thalika katheeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 39, "And for each We presented examples [as warnings], and each We destroyed with [total] destruction", "وَكُلّٗا ضَرَبۡنَا لَهُ ٱلۡأَمۡثَٰلَۖ وَكُلّٗا تَبَّرۡنَا تَتۡبِيرٗا", "Wakullan darabna lahu alamthala wakullan tabbarna tatbeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 40, "And they have already come upon the town which was showered with a rain of evil. So have they not seen it? But they are not expecting resurrection", "وَلَقَدۡ أَتَوۡاْ عَلَى ٱلۡقَرۡيَةِ ٱلَّتِيٓ أُمۡطِرَتۡ مَطَرَ ٱلسَّوۡءِۚ أَفَلَمۡ يَكُونُواْ يَرَوۡنَهَاۚ بَلۡ كَانُواْ لَا يَرۡجُونَ نُشُورٗا", "Walaqad ataw AAala alqaryati allatee omtirat matara alssawi afalam yakoonoo yarawnaha bal kanoo la yarjoona nushooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 41, "And when they see you, [O Muhammad], they take you not except in ridicule, [saying], ""Is this the one whom Allah has sent as a messenger", "وَإِذَا رَأَوۡكَ إِن يَتَّخِذُونَكَ إِلَّا هُزُوًا أَهَٰذَا ٱلَّذِي بَعَثَ ٱللَّهُ رَسُولًا", "Waitha raawka in yattakhithoonaka illa huzuwan ahatha allathee baAAatha Allahu rasoolan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 42, "He almost would have misled us from our gods had we not been steadfast in [worship of] them."" But they are going to know, when they see the punishment, who is farthest astray in [his] way", "إِن كَادَ لَيُضِلُّنَا عَنۡ ءَالِهَتِنَا لَوۡلَآ أَن صَبَرۡنَا عَلَيۡهَاۚ وَسَوۡفَ يَعۡلَمُونَ حِينَ يَرَوۡنَ ٱلۡعَذَابَ مَنۡ أَضَلُّ سَبِيلًا", "In kada layudilluna AAan alihatina lawla an sabarna AAalayha wasawfa yaAAlamoona heena yarawna alAAathaba man adallu sabeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 43, "Have you seen the one who takes as his god his own desire? Then would you be responsible for him", "أَرَءَيۡتَ مَنِ ٱتَّخَذَ إِلَٰهَهُۥ هَوَىٰهُ أَفَأَنتَ تَكُونُ عَلَيۡهِ وَكِيلًا", "Araayta mani ittakhatha ilahahu hawahu afaanta takoonu AAalayhi wakeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 44, "Or do you think that most of them hear or reason? They are not except like livestock. Rather, they are [even] more astray in [their] way", "أَمۡ تَحۡسَبُ أَنَّ أَكۡثَرَهُمۡ يَسۡمَعُونَ أَوۡ يَعۡقِلُونَۚ إِنۡ هُمۡ إِلَّا كَٱلۡأَنۡعَٰمِ بَلۡ هُمۡ أَضَلُّ سَبِيلًا", "Am tahsabu anna aktharahum yasmaAAoona aw yaAAqiloona in hum illa kaalanAAami bal hum adallu sabeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 45, "Have you not considered your Lord - how He extends the shadow, and if He willed, He could have made it stationary? Then We made the sun for it an indication", "أَلَمۡ تَرَ إِلَىٰ رَبِّكَ كَيۡفَ مَدَّ ٱلظِّلَّ وَلَوۡ شَآءَ لَجَعَلَهُۥ سَاكِنٗا ثُمَّ جَعَلۡنَا ٱلشَّمۡسَ عَلَيۡهِ دَلِيلٗا", "Alam tara ila rabbika kayfa madda alththilla walaw shaa lajaAAalahu sakinan thumma jaAAalna alshshamsa AAalayhi daleelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 46, "Then We hold it in hand for a brief grasp", "ثُمَّ قَبَضۡنَٰهُ إِلَيۡنَا قَبۡضٗا يَسِيرٗا", "Thumma qabadnahu ilayna qabdan yaseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 47, "And it is He who has made the night for you as clothing and sleep [a means for] rest and has made the day a resurrection", "وَهُوَ ٱلَّذِي جَعَلَ لَكُمُ ٱلَّيۡلَ لِبَاسٗا وَٱلنَّوۡمَ سُبَاتٗا وَجَعَلَ ٱلنَّهَارَ نُشُورٗا", "Wahuwa allathee jaAAala lakumu allayla libasan waalnnawma subatan wajaAAala alnnahara nushooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 48, "And it is He who sends the winds as good tidings before His mercy, and We send down from the sky pure water", "وَهُوَ ٱلَّذِيٓ أَرۡسَلَ ٱلرِّيَٰحَ بُشۡرَۢا بَيۡنَ يَدَيۡ رَحۡمَتِهِۦۚ وَأَنزَلۡنَا مِنَ ٱلسَّمَآءِ مَآءٗ طَهُورٗا", "Wahuwa allathee arsala alrriyaha bushran bayna yaday rahmatihi waanzalna mina alssamai maan tahooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 49, "That We may bring to life thereby a dead land and give it as drink to those We created of numerous livestock and men", "لِّنُحۡـِۧيَ بِهِۦ بَلۡدَةٗ مَّيۡتٗا وَنُسۡقِيَهُۥ مِمَّا خَلَقۡنَآ أَنۡعَٰمٗا وَأَنَاسِيَّ كَثِيرٗا", "Linuhyiya bihi baldatan maytan wanusqiyahu mimma khalaqna anAAaman waanasiyya katheeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 50, "And We have certainly distributed it among them that they might be reminded, but most of the people refuse except disbelief", "وَلَقَدۡ صَرَّفۡنَٰهُ بَيۡنَهُمۡ لِيَذَّكَّرُواْ فَأَبَىٰٓ أَكۡثَرُ ٱلنَّاسِ إِلَّا كُفُورٗا", "Walaqad sarrafnahu baynahum liyaththakkaroo faaba aktharu alnnasi illa kufooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 51, "And if We had willed, We could have sent into every city a warner", "وَلَوۡ شِئۡنَا لَبَعَثۡنَا فِي كُلِّ قَرۡيَةٖ نَّذِيرٗا", "Walaw shina labaAAathna fee kulli qaryatin natheeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 52, "So do not obey the disbelievers, and strive against them with the Qur'an a great striving", "فَلَا تُطِعِ ٱلۡكَٰفِرِينَ وَجَٰهِدۡهُم بِهِۦ جِهَادٗا كَبِيرٗا", "Fala tutiAAi alkafireena wajahidhum bihi jihadan kabeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 53, "And it is He who has released [simultaneously] the two seas, one fresh and sweet and one salty and bitter, and He placed between them a barrier and prohibiting partition", "۞وَهُوَ ٱلَّذِي مَرَجَ ٱلۡبَحۡرَيۡنِ هَٰذَا عَذۡبٞ فُرَاتٞ وَهَٰذَا مِلۡحٌ أُجَاجٞ وَجَعَلَ بَيۡنَهُمَا بَرۡزَخٗا وَحِجۡرٗا مَّحۡجُورٗا", "Wahuwa allathee maraja albahrayni hatha AAathbun furatun wahatha milhun ojajun wajaAAala baynahuma barzakhan wahijran mahjooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 54, "And it is He who has created from water a human being and made him [a relative by] lineage and marriage. And ever is your Lord competent [concerning creation]", "وَهُوَ ٱلَّذِي خَلَقَ مِنَ ٱلۡمَآءِ بَشَرٗا فَجَعَلَهُۥ نَسَبٗا وَصِهۡرٗاۗ وَكَانَ رَبُّكَ قَدِيرٗا", "Wahuwa allathee khalaqa mina almai basharan fajaAAalahu nasaban wasihran wakana rabbuka qadeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 55, "But they worship rather than Allah that which does not benefit them or harm them, and the disbeliever is ever, against his Lord, an assistant [to Satan]", "وَيَعۡبُدُونَ مِن دُونِ ٱللَّهِ مَا لَا يَنفَعُهُمۡ وَلَا يَضُرُّهُمۡۗ وَكَانَ ٱلۡكَافِرُ عَلَىٰ رَبِّهِۦ ظَهِيرٗا", "WayaAAbudoona min dooni Allahi ma la yanfaAAuhum wala yadurruhum wakana alkafiru AAala rabbihi thaheeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 56, "And We have not sent you, [O Muhammad], except as a bringer of good tidings and a warner", "وَمَآ أَرۡسَلۡنَٰكَ إِلَّا مُبَشِّرٗا وَنَذِيرٗا", "Wama arsalnaka illa mubashshiran wanatheeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 57, "Say, ""I do not ask of you for it any payment - only that whoever wills might take to his Lord a way", "قُلۡ مَآ أَسۡـَٔلُكُمۡ عَلَيۡهِ مِنۡ أَجۡرٍ إِلَّا مَن شَآءَ أَن يَتَّخِذَ إِلَىٰ رَبِّهِۦ سَبِيلٗا", "Qul ma asalukum AAalayhi min ajrin illa man shaa an yattakhitha ila rabbihi sabeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 58, "And rely upon the Ever-Living who does not die, and exalt [Allah] with His praise. And sufficient is He to be, with the sins of His servants, Acquainted", "وَتَوَكَّلۡ عَلَى ٱلۡحَيِّ ٱلَّذِي لَا يَمُوتُ وَسَبِّحۡ بِحَمۡدِهِۦۚ وَكَفَىٰ بِهِۦ بِذُنُوبِ عِبَادِهِۦ خَبِيرًا", "Watawakkal AAala alhayyi allathee la yamootu wasabbih bihamdihi wakafa bihi bithunoobi AAibadihi khabeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 59, "He who created the heavens and the earth and what is between them in six days and then established Himself above the Throne - the Most Merciful, so ask about Him one well informed", "ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَمَا بَيۡنَهُمَا فِي سِتَّةِ أَيَّامٖ ثُمَّ ٱسۡتَوَىٰ عَلَى ٱلۡعَرۡشِۖ ٱلرَّحۡمَٰنُ فَسۡـَٔلۡ بِهِۦ خَبِيرٗا", "Allathee khalaqa alssamawati waalarda wama baynahuma fee sittati ayyamin thumma istawa AAala alAAarshi alrrahmanu faisal bihi khabeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 60, "And when it is said to them, ""Prostrate to the Most Merciful,"" they say, ""And what is the Most Merciful? Should we prostrate to that which you order us?"" And it increases them in aversion", "وَإِذَا قِيلَ لَهُمُ ٱسۡجُدُواْۤ لِلرَّحۡمَٰنِ قَالُواْ وَمَا ٱلرَّحۡمَٰنُ أَنَسۡجُدُ لِمَا تَأۡمُرُنَا وَزَادَهُمۡ نُفُورٗا۩", "Waitha qeela lahumu osjudoo lilrrahmani qaloo wama alrrahmanu anasjudu lima tamuruna wazadahum nufooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 61, "Blessed is He who has placed in the sky great stars and placed therein a [burning] lamp and luminous moon", "تَبَارَكَ ٱلَّذِي جَعَلَ فِي ٱلسَّمَآءِ بُرُوجٗا وَجَعَلَ فِيهَا سِرَٰجٗا وَقَمَرٗا مُّنِيرٗا", "Tabaraka allathee jaAAala fee alssamai buroojan wajaAAala feeha sirajan waqamaran muneeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 62, "And it is He who has made the night and the day in succession for whoever desires to remember or desires gratitude", "وَهُوَ ٱلَّذِي جَعَلَ ٱلَّيۡلَ وَٱلنَّهَارَ خِلۡفَةٗ لِّمَنۡ أَرَادَ أَن يَذَّكَّرَ أَوۡ أَرَادَ شُكُورٗا", "Wahuwa allathee jaAAala allayla waalnnahara khilfatan liman arada an yaththakkara aw arada shukooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 63, "And the servants of the Most Merciful are those who walk upon the earth easily, and when the ignorant address them [harshly], they say [words of] peace", "وَعِبَادُ ٱلرَّحۡمَٰنِ ٱلَّذِينَ يَمۡشُونَ عَلَى ٱلۡأَرۡضِ هَوۡنٗا وَإِذَا خَاطَبَهُمُ ٱلۡجَٰهِلُونَ قَالُواْ سَلَٰمٗا", "WaAAibadu alrrahmani allatheena yamshoona AAala alardi hawnan waitha khatabahumu aljahiloona qaloo salaman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 64, "And those who spend [part of] the night to their Lord prostrating and standing [in prayer]", "وَٱلَّذِينَ يَبِيتُونَ لِرَبِّهِمۡ سُجَّدٗا وَقِيَٰمٗا", "Waallatheena yabeetoona lirabbihim sujjadan waqiyaman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 65, "And those who say, ""Our Lord, avert from us the punishment of Hell. Indeed, its punishment is ever adhering", "وَٱلَّذِينَ يَقُولُونَ رَبَّنَا ٱصۡرِفۡ عَنَّا عَذَابَ جَهَنَّمَۖ إِنَّ عَذَابَهَا كَانَ غَرَامًا", "Waallatheena yaqooloona rabbana isrif AAanna AAathaba jahannama inna AAathabaha kana gharaman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 66, "Indeed, it is evil as a settlement and residence", "إِنَّهَا سَآءَتۡ مُسۡتَقَرّٗا وَمُقَامٗا", "Innaha saat mustaqarran wamuqaman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 67, "And [they are] those who, when they spend, do so not excessively or sparingly but are ever, between that, [justly] moderate", "وَٱلَّذِينَ إِذَآ أَنفَقُواْ لَمۡ يُسۡرِفُواْ وَلَمۡ يَقۡتُرُواْ وَكَانَ بَيۡنَ ذَٰلِكَ قَوَامٗا", "Waallatheena itha anfaqoo lam yusrifoo walam yaqturoo wakana bayna thalika qawaman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 68, "And those who do not invoke with Allah another deity or kill the soul which Allah has forbidden [to be killed], except by right, and do not commit unlawful sexual intercourse. And whoever should do that will meet a penalty", "وَٱلَّذِينَ لَا يَدۡعُونَ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَ وَلَا يَقۡتُلُونَ ٱلنَّفۡسَ ٱلَّتِي حَرَّمَ ٱللَّهُ إِلَّا بِٱلۡحَقِّ وَلَا يَزۡنُونَۚ وَمَن يَفۡعَلۡ ذَٰلِكَ يَلۡقَ أَثَامٗا", "Waallatheena la yadAAoona maAAa Allahi ilahan akhara wala yaqtuloona alnnafsa allatee harrama Allahu illa bialhaqqi wala yaznoona waman yafAAal thalika yalqa athaman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 69, "Multiplied for him is the punishment on the Day of Resurrection, and he will abide therein humiliated", "يُضَٰعَفۡ لَهُ ٱلۡعَذَابُ يَوۡمَ ٱلۡقِيَٰمَةِ وَيَخۡلُدۡ فِيهِۦ مُهَانًا", "YudaAAaf lahu alAAathabu yawma alqiyamati wayakhlud feehi muhanan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 70, "Except for those who repent, believe and do righteous work. For them Allah will replace their evil deeds with good. And ever is Allah Forgiving and Merciful", "إِلَّا مَن تَابَ وَءَامَنَ وَعَمِلَ عَمَلٗا صَٰلِحٗا فَأُوْلَـٰٓئِكَ يُبَدِّلُ ٱللَّهُ سَيِّـَٔاتِهِمۡ حَسَنَٰتٖۗ وَكَانَ ٱللَّهُ غَفُورٗا رَّحِيمٗا", "Illa man taba waamana waAAamila AAamalan salihan faolaika yubaddilu Allahu sayyiatihim hasanatin wakana Allahu ghafooran raheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 71, "And he who repents and does righteousness does indeed turn to Allah with [accepted] repentance", "وَمَن تَابَ وَعَمِلَ صَٰلِحٗا فَإِنَّهُۥ يَتُوبُ إِلَى ٱللَّهِ مَتَابٗا", "Waman taba waAAamila salihan fainnahu yatoobu ila Allahi mataban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 72, "And [they are] those who do not testify to falsehood, and when they pass near ill speech, they pass by with dignity", "وَٱلَّذِينَ لَا يَشۡهَدُونَ ٱلزُّورَ وَإِذَا مَرُّواْ بِٱللَّغۡوِ مَرُّواْ كِرَامٗا", "Waallatheena la yashhadoona alzzoora waitha marroo biallaghwi marroo kiraman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 73, "And those who, when reminded of the verses of their Lord, do not fall upon them deaf and blind", "وَٱلَّذِينَ إِذَا ذُكِّرُواْ بِـَٔايَٰتِ رَبِّهِمۡ لَمۡ يَخِرُّواْ عَلَيۡهَا صُمّٗا وَعُمۡيَانٗا", "Waallatheena itha thukkiroo biayati rabbihim lam yakhirroo AAalayha summan waAAumyanan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 74, "And those who say, ""Our Lord, grant us from among our wives and offspring comfort to our eyes and make us an example for the righteous", "وَٱلَّذِينَ يَقُولُونَ رَبَّنَا هَبۡ لَنَا مِنۡ أَزۡوَٰجِنَا وَذُرِّيَّـٰتِنَا قُرَّةَ أَعۡيُنٖ وَٱجۡعَلۡنَا لِلۡمُتَّقِينَ إِمَامًا", "Waallatheena yaqooloona rabbana hab lana min azwajina wathurriyyatina qurrata aAAyunin waijAAalna lilmuttaqeena imaman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 75, "Those will be awarded the Chamber for what they patiently endured, and they will be received therein with greetings and [words of] peace", "أُوْلَـٰٓئِكَ يُجۡزَوۡنَ ٱلۡغُرۡفَةَ بِمَا صَبَرُواْ وَيُلَقَّوۡنَ فِيهَا تَحِيَّةٗ وَسَلَٰمًا", "Olaika yujzawna alghurfata bima sabaroo wayulaqqawna feeha tahiyyatan wasalaman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 76, "Abiding eternally therein. Good is the settlement and residence", "خَٰلِدِينَ فِيهَاۚ حَسُنَتۡ مُسۡتَقَرّٗا وَمُقَامٗا", "Khalideena feeha hasunat mustaqarran wamuqaman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (25, 77, "Say, ""What would my Lord care for you if not for your supplication?"" For you [disbelievers] have denied, so your denial is going to be adherent", "قُلۡ مَا يَعۡبَؤُاْ بِكُمۡ رَبِّي لَوۡلَا دُعَآؤُكُمۡۖ فَقَدۡ كَذَّبۡتُمۡ فَسَوۡفَ يَكُونُ لِزَامَۢا", "Qul ma yaAAbao bikum rabbee lawla duAAaokum faqad kaththabtum fasawfa yakoonu lizaman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 1, "Ta, Seen, Meem", "طسٓمٓ", "Taseenmeem");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 2, "These are the verses of the clear Book", "تِلۡكَ ءَايَٰتُ ٱلۡكِتَٰبِ ٱلۡمُبِينِ", "Tilka ayatu alkitabi almubeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 3, "Perhaps, [O Muhammad], you would kill yourself with grief that they will not be believers", "لَعَلَّكَ بَٰخِعٞ نَّفۡسَكَ أَلَّا يَكُونُواْ مُؤۡمِنِينَ", "LaAAallaka bakhiAAun nafsaka alla yakoonoo mumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 4, "If We willed, We could send down to them from the sky a sign for which their necks would remain humbled", "إِن نَّشَأۡ نُنَزِّلۡ عَلَيۡهِم مِّنَ ٱلسَّمَآءِ ءَايَةٗ فَظَلَّتۡ أَعۡنَٰقُهُمۡ لَهَا خَٰضِعِينَ", "In nasha nunazzil AAalayhim mina alssamai ayatan fathallat aAAnaquhum laha khadiAAeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 5, "And no revelation comes to them anew from the Most Merciful except that they turn away from it", "وَمَا يَأۡتِيهِم مِّن ذِكۡرٖ مِّنَ ٱلرَّحۡمَٰنِ مُحۡدَثٍ إِلَّا كَانُواْ عَنۡهُ مُعۡرِضِينَ", "Wama yateehim min thikrin mina alrrahmani muhdathin illa kanoo AAanhu muAArideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 6, "For they have already denied, but there will come to them the news of that which they used to ridicule", "فَقَدۡ كَذَّبُواْ فَسَيَأۡتِيهِمۡ أَنۢبَـٰٓؤُاْ مَا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ", "Faqad kaththaboo fasayateehim anbao ma kanoo bihi yastahzioona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 7, "Did they not look at the earth - how much We have produced therein from every noble kind", "أَوَلَمۡ يَرَوۡاْ إِلَى ٱلۡأَرۡضِ كَمۡ أَنۢبَتۡنَا فِيهَا مِن كُلِّ زَوۡجٖ كَرِيمٍ", "Awalam yaraw ila alardi kam anbatna feeha min kulli zawjin kareemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 8, "Indeed in that is a sign, but most of them were not to be believers", "إِنَّ فِي ذَٰلِكَ لَأٓيَةٗۖ وَمَا كَانَ أَكۡثَرُهُم مُّؤۡمِنِينَ", "Inna fee thalika laayatan wama kana aktharuhum mumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 9, "And indeed, your Lord - He is the Exalted in Might, the Merciful", "وَإِنَّ رَبَّكَ لَهُوَ ٱلۡعَزِيزُ ٱلرَّحِيمُ", "Wainna rabbaka lahuwa alAAazeezu alrraheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 10, "And [mention] when your Lord called Moses, [saying], ""Go to the wrongdoing people", "وَإِذۡ نَادَىٰ رَبُّكَ مُوسَىٰٓ أَنِ ٱئۡتِ ٱلۡقَوۡمَ ٱلظَّـٰلِمِينَ", "Waith nada rabbuka moosa ani iti alqawma alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 11, "The people of Pharaoh. Will they not fear Allah", "قَوۡمَ فِرۡعَوۡنَۚ أَلَا يَتَّقُونَ", "Qawma firAAawna ala yattaqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 12, "He said, ""My Lord, indeed I fear that they will deny me", "قَالَ رَبِّ إِنِّيٓ أَخَافُ أَن يُكَذِّبُونِ", "Qala rabbi innee akhafu an yukaththibooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 13, "And that my breast will tighten and my tongue will not be fluent, so send for Aaron", "وَيَضِيقُ صَدۡرِي وَلَا يَنطَلِقُ لِسَانِي فَأَرۡسِلۡ إِلَىٰ هَٰرُونَ", "Wayadeequ sadree wala yantaliqu lisanee faarsil ila haroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 14, "And they have upon me a [claim due to] sin, so I fear that they will kill me", "وَلَهُمۡ عَلَيَّ ذَنۢبٞ فَأَخَافُ أَن يَقۡتُلُونِ", "Walahum AAalayya thanbun faakhafu an yaqtulooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 15, "[Allah] said, ""No. Go both of you with Our signs; indeed, We are with you, listening", "قَالَ كَلَّاۖ فَٱذۡهَبَا بِـَٔايَٰتِنَآۖ إِنَّا مَعَكُم مُّسۡتَمِعُونَ", "Qala kalla faithhaba biayatina inna maAAakum mustamiAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 16, "Go to Pharaoh and say, 'We are the messengers of the Lord of the worlds", "فَأۡتِيَا فِرۡعَوۡنَ فَقُولَآ إِنَّا رَسُولُ رَبِّ ٱلۡعَٰلَمِينَ", "Fatiya firAAawna faqoola inna rasoolu rabbi alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 17, "[Commanded to say], ""Send with us the Children of Israel", "أَنۡ أَرۡسِلۡ مَعَنَا بَنِيٓ إِسۡرَـٰٓءِيلَ", "An arsil maAAana banee israeela");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 18, "[Pharaoh] said, ""Did we not raise you among us as a child, and you remained among us for years of your life", "قَالَ أَلَمۡ نُرَبِّكَ فِينَا وَلِيدٗا وَلَبِثۡتَ فِينَا مِنۡ عُمُرِكَ سِنِينَ", "Qala alam nurabbika feena waleedan walabithta feena min AAumurika sineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 19, "And [then] you did your deed which you did, and you were of the ungrateful", "وَفَعَلۡتَ فَعۡلَتَكَ ٱلَّتِي فَعَلۡتَ وَأَنتَ مِنَ ٱلۡكَٰفِرِينَ", "WafaAAalta faAAlataka allatee faAAalta waanta mina alkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 20, "[Moses] said, ""I did it, then, while I was of those astray", "قَالَ فَعَلۡتُهَآ إِذٗا وَأَنَا۠ مِنَ ٱلضَّآلِّينَ", "Qala faAAaltuha ithan waana mina alddalleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 21, "So I fled from you when I feared you. Then my Lord granted me wisdom and prophethood and appointed me [as one] of the messengers", "فَفَرَرۡتُ مِنكُمۡ لَمَّا خِفۡتُكُمۡ فَوَهَبَ لِي رَبِّي حُكۡمٗا وَجَعَلَنِي مِنَ ٱلۡمُرۡسَلِينَ", "Fafarartu minkum lamma khiftukum fawahaba lee rabbee hukman wajaAAalanee mina almursaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 22, "And is this a favor of which you remind me - that you have enslaved the Children of Israel", "وَتِلۡكَ نِعۡمَةٞ تَمُنُّهَا عَلَيَّ أَنۡ عَبَّدتَّ بَنِيٓ إِسۡرَـٰٓءِيلَ", "Watilka niAAmatun tamunnuha AAalayya an AAabbadta banee israeela");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 23, "Said Pharaoh, ""And what is the Lord of the worlds", "قَالَ فِرۡعَوۡنُ وَمَا رَبُّ ٱلۡعَٰلَمِينَ", "Qala firAAawnu wama rabbu alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 24, "[Moses] said, ""The Lord of the heavens and earth and that between them, if you should be convinced", "قَالَ رَبُّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا بَيۡنَهُمَآۖ إِن كُنتُم مُّوقِنِينَ", "Qala rabbu alssamawati waalardi wama baynahuma in kuntum mooqineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 25, "[Pharaoh] said to those around him, ""Do you not hear", "قَالَ لِمَنۡ حَوۡلَهُۥٓ أَلَا تَسۡتَمِعُونَ", "Qala liman hawlahu ala tastamiAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 26, "[Moses] said, ""Your Lord and the Lord of your first forefathers", "قَالَ رَبُّكُمۡ وَرَبُّ ءَابَآئِكُمُ ٱلۡأَوَّلِينَ", "Qala rabbukum warabbu abaikumu alawwaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 27, "[Pharaoh] said, ""Indeed, your 'messenger' who has been sent to you is mad", "قَالَ إِنَّ رَسُولَكُمُ ٱلَّذِيٓ أُرۡسِلَ إِلَيۡكُمۡ لَمَجۡنُونٞ", "Qala inna rasoolakumu allathee orsila ilaykum lamajnoonun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 28, "[Moses] said, ""Lord of the east and the west and that between them, if you were to reason", "قَالَ رَبُّ ٱلۡمَشۡرِقِ وَٱلۡمَغۡرِبِ وَمَا بَيۡنَهُمَآۖ إِن كُنتُمۡ تَعۡقِلُونَ", "Qala rabbu almashriqi waalmaghribi wama baynahuma in kuntum taAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 29, "[Pharaoh] said, ""If you take a god other than me, I will surely place you among those imprisoned", "قَالَ لَئِنِ ٱتَّخَذۡتَ إِلَٰهًا غَيۡرِي لَأَجۡعَلَنَّكَ مِنَ ٱلۡمَسۡجُونِينَ", "Qala laini ittakhathta ilahan ghayree laajAAalannaka mina almasjooneena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 30, "[Moses] said, ""Even if I brought you proof manifest", "قَالَ أَوَلَوۡ جِئۡتُكَ بِشَيۡءٖ مُّبِينٖ", "Qala awalaw jituka bishayin mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 31, "[Pharaoh] said, ""Then bring it, if you should be of the truthful", "قَالَ فَأۡتِ بِهِۦٓ إِن كُنتَ مِنَ ٱلصَّـٰدِقِينَ", "Qala fati bihi in kunta mina alssadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 32, "So [Moses] threw his staff, and suddenly it was a serpent manifest", "فَأَلۡقَىٰ عَصَاهُ فَإِذَا هِيَ ثُعۡبَانٞ مُّبِينٞ", "Faalqa AAasahu faitha hiya thuAAbanun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 33, "And he drew out his hand; thereupon it was white for the observers", "وَنَزَعَ يَدَهُۥ فَإِذَا هِيَ بَيۡضَآءُ لِلنَّـٰظِرِينَ", "WanazaAAa yadahu faitha hiya baydao lilnnathireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 34, "[Pharaoh] said to the eminent ones around him, ""Indeed, this is a learned magician", "قَالَ لِلۡمَلَإِ حَوۡلَهُۥٓ إِنَّ هَٰذَا لَسَٰحِرٌ عَلِيمٞ", "Qala lilmalai hawlahu inna hatha lasahirun AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 35, "He wants to drive you out of your land by his magic, so what do you advise", "يُرِيدُ أَن يُخۡرِجَكُم مِّنۡ أَرۡضِكُم بِسِحۡرِهِۦ فَمَاذَا تَأۡمُرُونَ", "Yureedu an yukhrijakum min ardikum bisihrihi famatha tamuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 36, "They said, ""Postpone [the matter of] him and his brother and send among the cities gatherers", "قَالُوٓاْ أَرۡجِهۡ وَأَخَاهُ وَٱبۡعَثۡ فِي ٱلۡمَدَآئِنِ حَٰشِرِينَ", "Qaloo arjih waakhahu waibAAath fee almadaini hashireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 37, "Who will bring you every learned, skilled magician", "يَأۡتُوكَ بِكُلِّ سَحَّارٍ عَلِيمٖ", "Yatooka bikulli sahharin AAaleemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 38, "So the magicians were assembled for the appointment of a well-known day", "فَجُمِعَ ٱلسَّحَرَةُ لِمِيقَٰتِ يَوۡمٖ مَّعۡلُومٖ", "FajumiAAa alssaharatu limeeqati yawmin maAAloomin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 39, "And it was said to the people, ""Will you congregate", "وَقِيلَ لِلنَّاسِ هَلۡ أَنتُم مُّجۡتَمِعُونَ", "Waqeela lilnnasi hal antum mujtamiAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 40, "That we might follow the magicians if they are the predominant", "لَعَلَّنَا نَتَّبِعُ ٱلسَّحَرَةَ إِن كَانُواْ هُمُ ٱلۡغَٰلِبِينَ", "LaAAallana nattabiAAu alssaharata in kanoo humu alghalibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 41, "And when the magicians arrived, they said to Pharaoh, ""Is there indeed for us a reward if we are the predominant", "فَلَمَّا جَآءَ ٱلسَّحَرَةُ قَالُواْ لِفِرۡعَوۡنَ أَئِنَّ لَنَا لَأَجۡرًا إِن كُنَّا نَحۡنُ ٱلۡغَٰلِبِينَ", "Falamma jaa alssaharatu qaloo lifirAAawna ainna lana laajran in kunna nahnu alghalibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 42, "He said, ""Yes, and indeed, you will then be of those near [to me]", "قَالَ نَعَمۡ وَإِنَّكُمۡ إِذٗا لَّمِنَ ٱلۡمُقَرَّبِينَ", "Qala naAAam wainnakum ithan lamina almuqarrabeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 43, "Moses said to them, ""Throw whatever you will throw", "قَالَ لَهُم مُّوسَىٰٓ أَلۡقُواْ مَآ أَنتُم مُّلۡقُونَ", "Qala lahum moosa alqoo ma antum mulqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 44, "So they threw their ropes and their staffs and said, ""By the might of Pharaoh, indeed it is we who are predominant", "فَأَلۡقَوۡاْ حِبَالَهُمۡ وَعِصِيَّهُمۡ وَقَالُواْ بِعِزَّةِ فِرۡعَوۡنَ إِنَّا لَنَحۡنُ ٱلۡغَٰلِبُونَ", "Faalqaw hibalahum waAAisiyyahum waqaloo biAAizzati firAAawna inna lanahnu alghaliboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 45, "Then Moses threw his staff, and at once it devoured what they falsified", "فَأَلۡقَىٰ مُوسَىٰ عَصَاهُ فَإِذَا هِيَ تَلۡقَفُ مَا يَأۡفِكُونَ", "Faalqa moosa AAasahu faitha hiya talqafu ma yafikoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 46, "So the magicians fell down in prostration [to Allah]", "فَأُلۡقِيَ ٱلسَّحَرَةُ سَٰجِدِينَ", "Faolqiya alssaharatu sajideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 47, "They said, ""We have believed in the Lord of the worlds", "قَالُوٓاْ ءَامَنَّا بِرَبِّ ٱلۡعَٰلَمِينَ", "Qaloo amanna birabbi alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 48, "The Lord of Moses and Aaron", "رَبِّ مُوسَىٰ وَهَٰرُونَ", "Rabbi moosa waharoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 49, "[Pharaoh] said, ""You believed Moses before I gave you permission. Indeed, he is your leader who has taught you magic, but you are going to know. I will surely cut off your hands and your feet on opposite sides, and I will surely crucify you all", "قَالَ ءَامَنتُمۡ لَهُۥ قَبۡلَ أَنۡ ءَاذَنَ لَكُمۡۖ إِنَّهُۥ لَكَبِيرُكُمُ ٱلَّذِي عَلَّمَكُمُ ٱلسِّحۡرَ فَلَسَوۡفَ تَعۡلَمُونَۚ لَأُقَطِّعَنَّ أَيۡدِيَكُمۡ وَأَرۡجُلَكُم مِّنۡ خِلَٰفٖ وَلَأُصَلِّبَنَّكُمۡ أَجۡمَعِينَ", "Qala amantum lahu qabla an athana lakum innahu lakabeerukumu allathee AAallamakumu alssihra falasawfa taAAlamoona laoqattiAAanna aydiyakum waarjulakum min khilafin walaosallibannakum ajmaAAeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 50, "They said, ""No harm. Indeed, to our Lord we will return", "قَالُواْ لَا ضَيۡرَۖ إِنَّآ إِلَىٰ رَبِّنَا مُنقَلِبُونَ", "Qaloo la dayra inna ila rabbina munqaliboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 51, "Indeed, we aspire that our Lord will forgive us our sins because we were the first of the believers", "إِنَّا نَطۡمَعُ أَن يَغۡفِرَ لَنَا رَبُّنَا خَطَٰيَٰنَآ أَن كُنَّآ أَوَّلَ ٱلۡمُؤۡمِنِينَ", "Inna natmaAAu an yaghfira lana rabbuna khatayana an kunna awwala almumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 52, "And We inspired to Moses, ""Travel by night with My servants; indeed, you will be pursued", "۞وَأَوۡحَيۡنَآ إِلَىٰ مُوسَىٰٓ أَنۡ أَسۡرِ بِعِبَادِيٓ إِنَّكُم مُّتَّبَعُونَ", "Waawhayna ila moosa an asri biAAibadee innakum muttabaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 53, "Then Pharaoh sent among the cities gatherers", "فَأَرۡسَلَ فِرۡعَوۡنُ فِي ٱلۡمَدَآئِنِ حَٰشِرِينَ", "Faarsala firAAawnu fee almadaini hashireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 54, "[And said], ""Indeed, those are but a small band", "إِنَّ هَـٰٓؤُلَآءِ لَشِرۡذِمَةٞ قَلِيلُونَ", "Inna haolai lashirthimatun qaleeloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 55, "And indeed, they are enraging us", "وَإِنَّهُمۡ لَنَا لَغَآئِظُونَ", "Wainnahum lana laghaithoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 56, "And indeed, we are a cautious society", "وَإِنَّا لَجَمِيعٌ حَٰذِرُونَ", "Wainna lajameeAAun hathiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 57, "So We removed them from gardens and springs", "فَأَخۡرَجۡنَٰهُم مِّن جَنَّـٰتٖ وَعُيُونٖ", "Faakhrajnahum min jannatin waAAuyoonin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 58, "And treasures and honorable station", "وَكُنُوزٖ وَمَقَامٖ كَرِيمٖ", "Wakunoozin wamaqamin kareemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 59, "Thus. And We caused to inherit it the Children of Israel", "كَذَٰلِكَۖ وَأَوۡرَثۡنَٰهَا بَنِيٓ إِسۡرَـٰٓءِيلَ", "Kathalika waawrathnaha banee israeela");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 60, "So they pursued them at sunrise", "فَأَتۡبَعُوهُم مُّشۡرِقِينَ", "FaatbaAAoohum mushriqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 61, "And when the two companies saw one another, the companions of Moses said, ""Indeed, we are to be overtaken", "فَلَمَّا تَرَـٰٓءَا ٱلۡجَمۡعَانِ قَالَ أَصۡحَٰبُ مُوسَىٰٓ إِنَّا لَمُدۡرَكُونَ", "Falamma taraa aljamAAani qala ashabu moosa inna lamudrakoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 62, "[Moses] said, ""No! Indeed, with me is my Lord; He will guide me", "قَالَ كَلَّآۖ إِنَّ مَعِيَ رَبِّي سَيَهۡدِينِ", "Qala kalla inna maAAiya rabbee sayahdeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 63, "Then We inspired to Moses, ""Strike with your staff the sea,"" and it parted, and each portion was like a great towering mountain", "فَأَوۡحَيۡنَآ إِلَىٰ مُوسَىٰٓ أَنِ ٱضۡرِب بِّعَصَاكَ ٱلۡبَحۡرَۖ فَٱنفَلَقَ فَكَانَ كُلُّ فِرۡقٖ كَٱلطَّوۡدِ ٱلۡعَظِيمِ", "Faawhayna ila moosa ani idrib biAAasaka albahra fainfalaqa fakana kullu firqin kaalttawdi alAAatheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 64, "And We advanced thereto the pursuers", "وَأَزۡلَفۡنَا ثَمَّ ٱلۡأٓخَرِينَ", "Waazlafna thamma alakhareena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 65, "And We saved Moses and those with him, all together", "وَأَنجَيۡنَا مُوسَىٰ وَمَن مَّعَهُۥٓ أَجۡمَعِينَ", "Waanjayna moosa waman maAAahu ajmaAAeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 66, "Then We drowned the others", "ثُمَّ أَغۡرَقۡنَا ٱلۡأٓخَرِينَ", "Thumma aghraqna alakhareena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 67, "Indeed in that is a sign, but most of them were not to be believers", "إِنَّ فِي ذَٰلِكَ لَأٓيَةٗۖ وَمَا كَانَ أَكۡثَرُهُم مُّؤۡمِنِينَ", "Inna fee thalika laayatan wama kana aktharuhum mumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 68, "And indeed, your Lord - He is the Exalted in Might, the Merciful", "وَإِنَّ رَبَّكَ لَهُوَ ٱلۡعَزِيزُ ٱلرَّحِيمُ", "Wainna rabbaka lahuwa alAAazeezu alrraheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 69, "And recite to them the news of Abraham", "وَٱتۡلُ عَلَيۡهِمۡ نَبَأَ إِبۡرَٰهِيمَ", "Waotlu AAalayhim nabaa ibraheema");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 70, "When he said to his father and his people, ""What do you worship", "إِذۡ قَالَ لِأَبِيهِ وَقَوۡمِهِۦ مَا تَعۡبُدُونَ", "Ith qala liabeehi waqawmihi ma taAAbudoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 71, "They said, ""We worship idols and remain to them devoted", "قَالُواْ نَعۡبُدُ أَصۡنَامٗا فَنَظَلُّ لَهَا عَٰكِفِينَ", "Qaloo naAAbudu asnaman fanathallu laha AAakifeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 72, "He said, ""Do they hear you when you supplicate", "قَالَ هَلۡ يَسۡمَعُونَكُمۡ إِذۡ تَدۡعُونَ", "Qala hal yasmaAAoonakum ith tadAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 73, "Or do they benefit you, or do they harm", "أَوۡ يَنفَعُونَكُمۡ أَوۡ يَضُرُّونَ", "Aw yanfaAAoonakum aw yadurroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 74, "They said, ""But we found our fathers doing thus", "قَالُواْ بَلۡ وَجَدۡنَآ ءَابَآءَنَا كَذَٰلِكَ يَفۡعَلُونَ", "Qaloo bal wajadna abaana kathalika yafAAaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 75, "He said, ""Then do you see what you have been worshipping", "قَالَ أَفَرَءَيۡتُم مَّا كُنتُمۡ تَعۡبُدُونَ", "Qala afaraaytum ma kuntum taAAbudoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 76, "You and your ancient forefathers", "أَنتُمۡ وَءَابَآؤُكُمُ ٱلۡأَقۡدَمُونَ", "Antum waabaokumu alaqdamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 77, "Indeed, they are enemies to me, except the Lord of the worlds", "فَإِنَّهُمۡ عَدُوّٞ لِّيٓ إِلَّا رَبَّ ٱلۡعَٰلَمِينَ", "Fainnahum AAaduwwun lee illa rabba alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 78, "Who created me, and He [it is who] guides me", "ٱلَّذِي خَلَقَنِي فَهُوَ يَهۡدِينِ", "Allathee khalaqanee fahuwa yahdeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 79, "And it is He who feeds me and gives me drink", "وَٱلَّذِي هُوَ يُطۡعِمُنِي وَيَسۡقِينِ", "Waallathee huwa yutAAimunee wayasqeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 80, "And when I am ill, it is He who cures me", "وَإِذَا مَرِضۡتُ فَهُوَ يَشۡفِينِ", "Waitha maridtu fahuwa yashfeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 81, "And who will cause me to die and then bring me to life", "وَٱلَّذِي يُمِيتُنِي ثُمَّ يُحۡيِينِ", "Waallathee yumeetunee thumma yuhyeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 82, "And who I aspire that He will forgive me my sin on the Day of Recompense", "وَٱلَّذِيٓ أَطۡمَعُ أَن يَغۡفِرَ لِي خَطِيٓـَٔتِي يَوۡمَ ٱلدِّينِ", "Waallathee atmaAAu an yaghfira lee khateeatee yawma alddeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 83, "[And he said], ""My Lord, grant me authority and join me with the righteous", "رَبِّ هَبۡ لِي حُكۡمٗا وَأَلۡحِقۡنِي بِٱلصَّـٰلِحِينَ", "Rabbi hab lee hukman waalhiqnee bialssaliheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 84, "And grant me a reputation of honor among later generations", "وَٱجۡعَل لِّي لِسَانَ صِدۡقٖ فِي ٱلۡأٓخِرِينَ", "WaijAAal lee lisana sidqin fee alakhireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 85, "And place me among the inheritors of the Garden of Pleasure", "وَٱجۡعَلۡنِي مِن وَرَثَةِ جَنَّةِ ٱلنَّعِيمِ", "WaijAAalnee min warathati jannati alnnaAAeemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 86, "And forgive my father. Indeed, he has been of those astray", "وَٱغۡفِرۡ لِأَبِيٓ إِنَّهُۥ كَانَ مِنَ ٱلضَّآلِّينَ", "Waighfir liabee innahu kana mina alddalleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 87, "And do not disgrace me on the Day they are [all] resurrected", "وَلَا تُخۡزِنِي يَوۡمَ يُبۡعَثُونَ", "Wala tukhzinee yawma yubAAathoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 88, "The Day when there will not benefit [anyone] wealth or children", "يَوۡمَ لَا يَنفَعُ مَالٞ وَلَا بَنُونَ", "Yawma la yanfaAAu malun wala banoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 89, "But only one who comes to Allah with a sound heart", "إِلَّا مَنۡ أَتَى ٱللَّهَ بِقَلۡبٖ سَلِيمٖ", "Illa man ata Allaha biqalbin saleemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 90, "And Paradise will be brought near [that Day] to the righteous", "وَأُزۡلِفَتِ ٱلۡجَنَّةُ لِلۡمُتَّقِينَ", "Waozlifati aljannatu lilmuttaqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 91, "And Hellfire will be brought forth for the deviators", "وَبُرِّزَتِ ٱلۡجَحِيمُ لِلۡغَاوِينَ", "Waburrizati aljaheemu lilghaweena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 92, "And it will be said to them, ""Where are those you used to worship", "وَقِيلَ لَهُمۡ أَيۡنَ مَا كُنتُمۡ تَعۡبُدُونَ", "Waqeela lahum ayna ma kuntum taAAbudoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 93, "Other than Allah? Can they help you or help themselves", "مِن دُونِ ٱللَّهِ هَلۡ يَنصُرُونَكُمۡ أَوۡ يَنتَصِرُونَ", "Min dooni Allahi hal yansuroonakum aw yantasiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 94, "So they will be overturned into Hellfire, they and the deviators", "فَكُبۡكِبُواْ فِيهَا هُمۡ وَٱلۡغَاوُۥنَ", "Fakubkiboo feeha hum waalghawoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 95, "And the soldiers of Iblees, all together", "وَجُنُودُ إِبۡلِيسَ أَجۡمَعُونَ", "Wajunoodu ibleesa ajmaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 96, "They will say while they dispute therein", "قَالُواْ وَهُمۡ فِيهَا يَخۡتَصِمُونَ", "Qaloo wahum feeha yakhtasimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 97, "By Allah, we were indeed in manifest error", "تَٱللَّهِ إِن كُنَّا لَفِي ضَلَٰلٖ مُّبِينٍ", "TaAllahi in kunna lafee dalalin mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 98, "When we equated you with the Lord of the worlds", "إِذۡ نُسَوِّيكُم بِرَبِّ ٱلۡعَٰلَمِينَ", "Ith nusawweekum birabbi alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 99, "And no one misguided us except the criminals", "وَمَآ أَضَلَّنَآ إِلَّا ٱلۡمُجۡرِمُونَ", "Wama adallana illa almujrimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 100, "So now we have no intercessors", "فَمَا لَنَا مِن شَٰفِعِينَ", "Fama lana min shafiAAeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 101, "And not a devoted friend", "وَلَا صَدِيقٍ حَمِيمٖ", "Wala sadeeqin hameemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 102, "Then if we only had a return [to the world] and could be of the believers", "فَلَوۡ أَنَّ لَنَا كَرَّةٗ فَنَكُونَ مِنَ ٱلۡمُؤۡمِنِينَ", "Falaw anna lana karratan fanakoona mina almumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 103, "Indeed in that is a sign, but most of them were not to be believers", "إِنَّ فِي ذَٰلِكَ لَأٓيَةٗۖ وَمَا كَانَ أَكۡثَرُهُم مُّؤۡمِنِينَ", "Inna fee thalika laayatan wama kana aktharuhum mumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 104, "And indeed, your Lord - He is the Exalted in Might, the Merciful", "وَإِنَّ رَبَّكَ لَهُوَ ٱلۡعَزِيزُ ٱلرَّحِيمُ", "Wainna rabbaka lahuwa alAAazeezu alrraheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 105, "The people of Noah denied the messengers", "كَذَّبَتۡ قَوۡمُ نُوحٍ ٱلۡمُرۡسَلِينَ", "Kaththabat qawmu noohin almursaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 106, "When their brother Noah said to them, ""Will you not fear Allah", "إِذۡ قَالَ لَهُمۡ أَخُوهُمۡ نُوحٌ أَلَا تَتَّقُونَ", "Ith qala lahum akhoohum noohun ala tattaqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 107, "Indeed, I am to you a trustworthy messenger", "إِنِّي لَكُمۡ رَسُولٌ أَمِينٞ", "Innee lakum rasoolun ameenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 108, "So fear Allah and obey me", "فَٱتَّقُواْ ٱللَّهَ وَأَطِيعُونِ", "Faittaqoo Allaha waateeAAooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 109, "And I do not ask you for it any payment. My payment is only from the Lord of the worlds", "وَمَآ أَسۡـَٔلُكُمۡ عَلَيۡهِ مِنۡ أَجۡرٍۖ إِنۡ أَجۡرِيَ إِلَّا عَلَىٰ رَبِّ ٱلۡعَٰلَمِينَ", "Wama asalukum AAalayhi min ajrin in ajriya illa AAala rabbi alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 110, "So fear Allah and obey me", "فَٱتَّقُواْ ٱللَّهَ وَأَطِيعُونِ", "Faittaqoo Allaha waateeAAooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 111, "They said, ""Should we believe you while you are followed by the lowest [class of people]", "۞قَالُوٓاْ أَنُؤۡمِنُ لَكَ وَٱتَّبَعَكَ ٱلۡأَرۡذَلُونَ", "Qaloo anuminu laka waittabaAAaka alarthaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 112, "He said, ""And what is my knowledge of what they used to do", "قَالَ وَمَا عِلۡمِي بِمَا كَانُواْ يَعۡمَلُونَ", "Qala wama AAilmee bima kanoo yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 113, "Their account is only upon my Lord, if you [could] perceive", "إِنۡ حِسَابُهُمۡ إِلَّا عَلَىٰ رَبِّيۖ لَوۡ تَشۡعُرُونَ", "In hisabuhum illa AAala rabbee law tashAAuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 114, "And I am not one to drive away the believers", "وَمَآ أَنَا۠ بِطَارِدِ ٱلۡمُؤۡمِنِينَ", "Wama ana bitaridi almumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 115, "I am only a clear warner", "إِنۡ أَنَا۠ إِلَّا نَذِيرٞ مُّبِينٞ", "In ana illa natheerun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 116, "They said, ""If you do not desist, O Noah, you will surely be of those who are stoned", "قَالُواْ لَئِن لَّمۡ تَنتَهِ يَٰنُوحُ لَتَكُونَنَّ مِنَ ٱلۡمَرۡجُومِينَ", "Qaloo lain lam tantahi ya noohu latakoonanna mina almarjoomeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 117, "He said, ""My Lord, indeed my people have denied me", "قَالَ رَبِّ إِنَّ قَوۡمِي كَذَّبُونِ", "Qala rabbi inna qawmee kaththabooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 118, "Then judge between me and them with decisive judgement and save me and those with me of the believers", "فَٱفۡتَحۡ بَيۡنِي وَبَيۡنَهُمۡ فَتۡحٗا وَنَجِّنِي وَمَن مَّعِيَ مِنَ ٱلۡمُؤۡمِنِينَ", "Faiftah baynee wabaynahum fathan wanajjinee waman maAAiya mina almumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 119, "So We saved him and those with him in the laden ship", "فَأَنجَيۡنَٰهُ وَمَن مَّعَهُۥ فِي ٱلۡفُلۡكِ ٱلۡمَشۡحُونِ", "Faanjaynahu waman maAAahu fee alfulki almashhooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 120, "Then We drowned thereafter the remaining ones", "ثُمَّ أَغۡرَقۡنَا بَعۡدُ ٱلۡبَاقِينَ", "Thumma aghraqna baAAdu albaqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 121, "Indeed in that is a sign, but most of them were not to be believers", "إِنَّ فِي ذَٰلِكَ لَأٓيَةٗۖ وَمَا كَانَ أَكۡثَرُهُم مُّؤۡمِنِينَ", "Inna fee thalika laayatan wama kana aktharuhum mumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 122, "And indeed, your Lord - He is the Exalted in Might, the Merciful", "وَإِنَّ رَبَّكَ لَهُوَ ٱلۡعَزِيزُ ٱلرَّحِيمُ", "Wainna rabbaka lahuwa alAAazeezu alrraheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 123, "Aad denied the messengers", "كَذَّبَتۡ عَادٌ ٱلۡمُرۡسَلِينَ", "Kaththabat AAadun almursaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 124, "When their brother Hud said to them, ""Will you not fear Allah", "إِذۡ قَالَ لَهُمۡ أَخُوهُمۡ هُودٌ أَلَا تَتَّقُونَ", "Ith qala lahum akhoohum hoodun ala tattaqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 125, "Indeed, I am to you a trustworthy messenger", "إِنِّي لَكُمۡ رَسُولٌ أَمِينٞ", "Innee lakum rasoolun ameenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 126, "So fear Allah and obey me", "فَٱتَّقُواْ ٱللَّهَ وَأَطِيعُونِ", "Faittaqoo Allaha waateeAAooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 127, "And I do not ask you for it any payment. My payment is only from the Lord of the worlds", "وَمَآ أَسۡـَٔلُكُمۡ عَلَيۡهِ مِنۡ أَجۡرٍۖ إِنۡ أَجۡرِيَ إِلَّا عَلَىٰ رَبِّ ٱلۡعَٰلَمِينَ", "Wama asalukum AAalayhi min ajrin in ajriya illa AAala rabbi alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 128, "Do you construct on every elevation a sign, amusing yourselves", "أَتَبۡنُونَ بِكُلِّ رِيعٍ ءَايَةٗ تَعۡبَثُونَ", "Atabnoona bikulli reeAAin ayatan taAAbathoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 129, "And take for yourselves palaces and fortresses that you might abide eternally", "وَتَتَّخِذُونَ مَصَانِعَ لَعَلَّكُمۡ تَخۡلُدُونَ", "Watattakhithoona masaniAAa laAAallakum takhludoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 130, "And when you strike, you strike as tyrants", "وَإِذَا بَطَشۡتُم بَطَشۡتُمۡ جَبَّارِينَ", "Waitha batashtum batashtum jabbareena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 131, "So fear Allah and obey me", "فَٱتَّقُواْ ٱللَّهَ وَأَطِيعُونِ", "Faittaqoo Allaha waateeAAooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 132, "And fear He who provided you with that which you know", "وَٱتَّقُواْ ٱلَّذِيٓ أَمَدَّكُم بِمَا تَعۡلَمُونَ", "Waittaqoo allathee amaddakum bima taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 133, "Provided you with grazing livestock and children", "أَمَدَّكُم بِأَنۡعَٰمٖ وَبَنِينَ", "Amaddakum bianAAamin wabaneena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 134, "And gardens and springs", "وَجَنَّـٰتٖ وَعُيُونٍ", "Wajannatin waAAuyoonin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 135, "Indeed, I fear for you the punishment of a terrible day", "إِنِّيٓ أَخَافُ عَلَيۡكُمۡ عَذَابَ يَوۡمٍ عَظِيمٖ", "Innee akhafu AAalaykum AAathaba yawmin AAatheemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 136, "They said, ""It is all the same to us whether you advise or are not of the advisors", "قَالُواْ سَوَآءٌ عَلَيۡنَآ أَوَعَظۡتَ أَمۡ لَمۡ تَكُن مِّنَ ٱلۡوَٰعِظِينَ", "Qaloo sawaon AAalayna awaAAathta am lam takun mina alwaAAitheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 137, "This is not but the custom of the former peoples", "إِنۡ هَٰذَآ إِلَّا خُلُقُ ٱلۡأَوَّلِينَ", "In hatha illa khuluqu alawwaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 138, "And we are not to be punished", "وَمَا نَحۡنُ بِمُعَذَّبِينَ", "Wama nahnu bimuAAaththabeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 139, "And they denied him, so We destroyed them. Indeed in that is a sign, but most of them were not to be believers", "فَكَذَّبُوهُ فَأَهۡلَكۡنَٰهُمۡۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗۖ وَمَا كَانَ أَكۡثَرُهُم مُّؤۡمِنِينَ", "Fakaththaboohu faahlaknahum inna fee thalika laayatan wama kana aktharuhum mumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 140, "And indeed, your Lord - He is the Exalted in Might, the Merciful", "وَإِنَّ رَبَّكَ لَهُوَ ٱلۡعَزِيزُ ٱلرَّحِيمُ", "Wainna rabbaka lahuwa alAAazeezu alrraheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 141, "Thamud denied the messengers", "كَذَّبَتۡ ثَمُودُ ٱلۡمُرۡسَلِينَ", "Kaththabat thamoodu almursaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 142, "When their brother Salih said to them, ""Will you not fear Allah", "إِذۡ قَالَ لَهُمۡ أَخُوهُمۡ صَٰلِحٌ أَلَا تَتَّقُونَ", "Ith qala lahum akhoohum salihun ala tattaqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 143, "Indeed, I am to you a trustworthy messenger", "إِنِّي لَكُمۡ رَسُولٌ أَمِينٞ", "Innee lakum rasoolun ameenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 144, "So fear Allah and obey me", "فَٱتَّقُواْ ٱللَّهَ وَأَطِيعُونِ", "Faittaqoo Allaha waateeAAooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 145, "And I do not ask you for it any payment. My payment is only from the Lord of the worlds", "وَمَآ أَسۡـَٔلُكُمۡ عَلَيۡهِ مِنۡ أَجۡرٍۖ إِنۡ أَجۡرِيَ إِلَّا عَلَىٰ رَبِّ ٱلۡعَٰلَمِينَ", "Wama asalukum AAalayhi min ajrin in ajriya illa AAala rabbi alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 146, "Will you be left in what is here, secure [from death]", "أَتُتۡرَكُونَ فِي مَا هَٰهُنَآ ءَامِنِينَ", "Atutrakoona fee ma hahuna amineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 147, "Within gardens and springs", "فِي جَنَّـٰتٖ وَعُيُونٖ", "Fee jannatin waAAuyoonin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 148, "And fields of crops and palm trees with softened fruit", "وَزُرُوعٖ وَنَخۡلٖ طَلۡعُهَا هَضِيمٞ", "WazurooAAin wanakhlin talAAuha hadeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 149, "And you carve out of the mountains, homes, with skill", "وَتَنۡحِتُونَ مِنَ ٱلۡجِبَالِ بُيُوتٗا فَٰرِهِينَ", "Watanhitoona mina aljibali buyootan fariheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 150, "So fear Allah and obey me", "فَٱتَّقُواْ ٱللَّهَ وَأَطِيعُونِ", "Faittaqoo Allaha waateeAAooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 151, "And do not obey the order of the transgressors", "وَلَا تُطِيعُوٓاْ أَمۡرَ ٱلۡمُسۡرِفِينَ", "Wala tuteeAAoo amra almusrifeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 152, "Who cause corruption in the land and do not amend", "ٱلَّذِينَ يُفۡسِدُونَ فِي ٱلۡأَرۡضِ وَلَا يُصۡلِحُونَ", "Allatheena yufsidoona fee alardi wala yuslihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 153, "They said, ""You are only of those affected by magic", "قَالُوٓاْ إِنَّمَآ أَنتَ مِنَ ٱلۡمُسَحَّرِينَ", "Qaloo innama anta mina almusahhareena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 154, "You are but a man like ourselves, so bring a sign, if you should be of the truthful", "مَآ أَنتَ إِلَّا بَشَرٞ مِّثۡلُنَا فَأۡتِ بِـَٔايَةٍ إِن كُنتَ مِنَ ٱلصَّـٰدِقِينَ", "Ma anta illa basharun mithluna fati biayatin in kunta mina alssadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 155, "He said, ""This is a she-camel. For her is a [time of] drink, and for you is a [time of] drink, [each] on a known day", "قَالَ هَٰذِهِۦ نَاقَةٞ لَّهَا شِرۡبٞ وَلَكُمۡ شِرۡبُ يَوۡمٖ مَّعۡلُومٖ", "Qala hathihi naqatun laha shirbun walakum shirbu yawmin maAAloomin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 156, "And do not touch her with harm, lest you be seized by the punishment of a terrible day", "وَلَا تَمَسُّوهَا بِسُوٓءٖ فَيَأۡخُذَكُمۡ عَذَابُ يَوۡمٍ عَظِيمٖ", "Wala tamassooha bisooin fayakhuthakum AAathabu yawmin AAatheemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 157, "But they hamstrung her and so became regretful", "فَعَقَرُوهَا فَأَصۡبَحُواْ نَٰدِمِينَ", "FaAAaqarooha faasbahoo nadimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 158, "And the punishment seized them. Indeed in that is a sign, but most of them were not to be believers", "فَأَخَذَهُمُ ٱلۡعَذَابُۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗۖ وَمَا كَانَ أَكۡثَرُهُم مُّؤۡمِنِينَ", "Faakhathahumu alAAathabu inna fee thalika laayatan wama kana aktharuhum mumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 159, "And indeed, your Lord - He is the Exalted in Might, the Merciful", "وَإِنَّ رَبَّكَ لَهُوَ ٱلۡعَزِيزُ ٱلرَّحِيمُ", "Wainna rabbaka lahuwa alAAazeezu alrraheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 160, "The people of Lot denied the messengers", "كَذَّبَتۡ قَوۡمُ لُوطٍ ٱلۡمُرۡسَلِينَ", "Kaththabat qawmu lootin almursaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 161, "When their brother Lot said to them, ""Will you not fear Allah", "إِذۡ قَالَ لَهُمۡ أَخُوهُمۡ لُوطٌ أَلَا تَتَّقُونَ", "Ith qala lahum akhoohum lootun ala tattaqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 162, "Indeed, I am to you a trustworthy messenger", "إِنِّي لَكُمۡ رَسُولٌ أَمِينٞ", "Innee lakum rasoolun ameenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 163, "So fear Allah and obey me", "فَٱتَّقُواْ ٱللَّهَ وَأَطِيعُونِ", "Faittaqoo Allaha waateeAAooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 164, "And I do not ask you for it any payment. My payment is only from the Lord of the worlds", "وَمَآ أَسۡـَٔلُكُمۡ عَلَيۡهِ مِنۡ أَجۡرٍۖ إِنۡ أَجۡرِيَ إِلَّا عَلَىٰ رَبِّ ٱلۡعَٰلَمِينَ", "Wama asalukum AAalayhi min ajrin in ajriya illa AAala rabbi alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 165, "Do you approach males among the worlds", "أَتَأۡتُونَ ٱلذُّكۡرَانَ مِنَ ٱلۡعَٰلَمِينَ", "Atatoona alththukrana mina alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 166, "And leave what your Lord has created for you as mates? But you are a people transgressing", "وَتَذَرُونَ مَا خَلَقَ لَكُمۡ رَبُّكُم مِّنۡ أَزۡوَٰجِكُمۚ بَلۡ أَنتُمۡ قَوۡمٌ عَادُونَ", "Watatharoona ma khalaqa lakum rabbukum min azwajikum bal antum qawmun AAadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 167, "They said, ""If you do not desist, O Lot, you will surely be of those evicted", "قَالُواْ لَئِن لَّمۡ تَنتَهِ يَٰلُوطُ لَتَكُونَنَّ مِنَ ٱلۡمُخۡرَجِينَ", "Qaloo lain lam tantahi ya lootu latakoonanna mina almukhrajeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 168, "He said, ""Indeed, I am, toward your deed, of those who detest [it]", "قَالَ إِنِّي لِعَمَلِكُم مِّنَ ٱلۡقَالِينَ", "Qala innee liAAamalikum mina alqaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 169, "My Lord, save me and my family from [the consequence of] what they do", "رَبِّ نَجِّنِي وَأَهۡلِي مِمَّا يَعۡمَلُونَ", "Rabbi najjinee waahlee mimma yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 170, "So We saved him and his family, all", "فَنَجَّيۡنَٰهُ وَأَهۡلَهُۥٓ أَجۡمَعِينَ", "Fanajjaynahu waahlahu ajmaAAeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 171, "Except an old woman among those who remained behind", "إِلَّا عَجُوزٗا فِي ٱلۡغَٰبِرِينَ", "Illa AAajoozan fee alghabireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 172, "Then We destroyed the others", "ثُمَّ دَمَّرۡنَا ٱلۡأٓخَرِينَ", "Thumma dammarna alakhareena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 173, "And We rained upon them a rain [of stones], and evil was the rain of those who were warned", "وَأَمۡطَرۡنَا عَلَيۡهِم مَّطَرٗاۖ فَسَآءَ مَطَرُ ٱلۡمُنذَرِينَ", "Waamtarna AAalayhim mataran fasaa mataru almunthareena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 174, "Indeed in that is a sign, but most of them were not to be believers", "إِنَّ فِي ذَٰلِكَ لَأٓيَةٗۖ وَمَا كَانَ أَكۡثَرُهُم مُّؤۡمِنِينَ", "Inna fee thalika laayatan wama kana aktharuhum mumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 175, "And indeed, your Lord - He is the Exalted in Might, the Merciful", "وَإِنَّ رَبَّكَ لَهُوَ ٱلۡعَزِيزُ ٱلرَّحِيمُ", "Wainna rabbaka lahuwa alAAazeezu alrraheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 176, "The companions of the thicket denied the messengers", "كَذَّبَ أَصۡحَٰبُ لۡـَٔيۡكَةِ ٱلۡمُرۡسَلِينَ", "Kaththaba ashabu alaykati almursaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 177, "When Shu'ayb said to them, ""Will you not fear Allah", "إِذۡ قَالَ لَهُمۡ شُعَيۡبٌ أَلَا تَتَّقُونَ", "Ith qala lahum shuAAaybun ala tattaqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 178, "Indeed, I am to you a trustworthy messenger", "إِنِّي لَكُمۡ رَسُولٌ أَمِينٞ", "Innee lakum rasoolun ameenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 179, "So fear Allah and obey me", "فَٱتَّقُواْ ٱللَّهَ وَأَطِيعُونِ", "Faittaqoo Allaha waateeAAooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 180, "And I do not ask you for it any payment. My payment is only from the Lord of the worlds", "وَمَآ أَسۡـَٔلُكُمۡ عَلَيۡهِ مِنۡ أَجۡرٍۖ إِنۡ أَجۡرِيَ إِلَّا عَلَىٰ رَبِّ ٱلۡعَٰلَمِينَ", "Wama asalukum AAalayhi min ajrin in ajriya illa AAala rabbi alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 181, "Give full measure and do not be of those who cause loss", "۞أَوۡفُواْ ٱلۡكَيۡلَ وَلَا تَكُونُواْ مِنَ ٱلۡمُخۡسِرِينَ", "Awfoo alkayla wala takoonoo mina almukhsireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 182, "And weigh with an even balance", "وَزِنُواْ بِٱلۡقِسۡطَاسِ ٱلۡمُسۡتَقِيمِ", "Wazinoo bialqistasi almustaqeemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 183, "And do not deprive people of their due and do not commit abuse on earth, spreading corruption", "وَلَا تَبۡخَسُواْ ٱلنَّاسَ أَشۡيَآءَهُمۡ وَلَا تَعۡثَوۡاْ فِي ٱلۡأَرۡضِ مُفۡسِدِينَ", "Wala tabkhasoo alnnasa ashyaahum wala taAAthaw fee alardi mufsideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 184, "And fear He who created you and the former creation", "وَٱتَّقُواْ ٱلَّذِي خَلَقَكُمۡ وَٱلۡجِبِلَّةَ ٱلۡأَوَّلِينَ", "Waittaqoo allathee khalaqakum waaljibillata alawwaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 185, "They said, ""You are only of those affected by magic", "قَالُوٓاْ إِنَّمَآ أَنتَ مِنَ ٱلۡمُسَحَّرِينَ", "Qaloo innama anta mina almusahhareena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 186, "You are but a man like ourselves, and indeed, we think you are among the liars", "وَمَآ أَنتَ إِلَّا بَشَرٞ مِّثۡلُنَا وَإِن نَّظُنُّكَ لَمِنَ ٱلۡكَٰذِبِينَ", "Wama anta illa basharun mithluna wain nathunnuka lamina alkathibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 187, "So cause to fall upon us fragments of the sky, if you should be of the truthful", "فَأَسۡقِطۡ عَلَيۡنَا كِسَفٗا مِّنَ ٱلسَّمَآءِ إِن كُنتَ مِنَ ٱلصَّـٰدِقِينَ", "Faasqit AAalayna kisafan mina alssamai in kunta mina alssadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 188, "He said, ""My Lord is most knowing of what you do", "قَالَ رَبِّيٓ أَعۡلَمُ بِمَا تَعۡمَلُونَ", "Qala rabbee aAAlamu bima taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 189, "And they denied him, so the punishment of the day of the black cloud seized them. Indeed, it was the punishment of a terrible day", "فَكَذَّبُوهُ فَأَخَذَهُمۡ عَذَابُ يَوۡمِ ٱلظُّلَّةِۚ إِنَّهُۥ كَانَ عَذَابَ يَوۡمٍ عَظِيمٍ", "Fakaththaboohu faakhathahum AAathabu yawmi alththullati innahu kana AAathaba yawmin AAatheemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 190, "Indeed in that is a sign, but most of them were not to be believers", "إِنَّ فِي ذَٰلِكَ لَأٓيَةٗۖ وَمَا كَانَ أَكۡثَرُهُم مُّؤۡمِنِينَ", "Inna fee thalika laayatan wama kana aktharuhum mumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 191, "And indeed, your Lord - He is the Exalted in Might, the Merciful", "وَإِنَّ رَبَّكَ لَهُوَ ٱلۡعَزِيزُ ٱلرَّحِيمُ", "Wainna rabbaka lahuwa alAAazeezu alrraheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 192, "And indeed, the Qur'an is the revelation of the Lord of the worlds", "وَإِنَّهُۥ لَتَنزِيلُ رَبِّ ٱلۡعَٰلَمِينَ", "Wainnahu latanzeelu rabbi alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 193, "The Trustworthy Spirit has brought it down", "نَزَلَ بِهِ ٱلرُّوحُ ٱلۡأَمِينُ", "Nazala bihi alrroohu alameenu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 194, "Upon your heart, [O Muhammad] - that you may be of the warners", "عَلَىٰ قَلۡبِكَ لِتَكُونَ مِنَ ٱلۡمُنذِرِينَ", "AAala qalbika litakoona mina almunthireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 195, "In a clear Arabic language", "بِلِسَانٍ عَرَبِيّٖ مُّبِينٖ", "Bilisanin AAarabiyyin mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 196, "And indeed, it is [mentioned] in the scriptures of former peoples", "وَإِنَّهُۥ لَفِي زُبُرِ ٱلۡأَوَّلِينَ", "Wainnahu lafee zuburi alawwaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 197, "And has it not been a sign to them that it is recognized by the scholars of the Children of Israel", "أَوَلَمۡ يَكُن لَّهُمۡ ءَايَةً أَن يَعۡلَمَهُۥ عُلَمَـٰٓؤُاْ بَنِيٓ إِسۡرَـٰٓءِيلَ", "Awalam yakun lahum ayatan an yaAAlamahu AAulamao banee israeela");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 198, "And even if We had revealed it to one among the foreigners", "وَلَوۡ نَزَّلۡنَٰهُ عَلَىٰ بَعۡضِ ٱلۡأَعۡجَمِينَ", "Walaw nazzalnahu AAala baAAdi alaAAjameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 199, "And he had recited it to them [perfectly], they would [still] not have been believers in it", "فَقَرَأَهُۥ عَلَيۡهِم مَّا كَانُواْ بِهِۦ مُؤۡمِنِينَ", "Faqaraahu AAalayhim ma kanoo bihi mumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 200, "Thus have We inserted disbelief into the hearts of the criminals", "كَذَٰلِكَ سَلَكۡنَٰهُ فِي قُلُوبِ ٱلۡمُجۡرِمِينَ", "Kathalika salaknahu fee quloobi almujrimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 201, "They will not believe in it until they see the painful punishment", "لَا يُؤۡمِنُونَ بِهِۦ حَتَّىٰ يَرَوُاْ ٱلۡعَذَابَ ٱلۡأَلِيمَ", "La yuminoona bihi hatta yarawoo alAAathaba alaleema");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 202, "And it will come to them suddenly while they perceive [it] not", "فَيَأۡتِيَهُم بَغۡتَةٗ وَهُمۡ لَا يَشۡعُرُونَ", "Fayatiyahum baghtatan wahum la yashAAuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 203, "And they will say, ""May we be reprieved", "فَيَقُولُواْ هَلۡ نَحۡنُ مُنظَرُونَ", "Fayaqooloo hal nahnu muntharoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 204, "So for Our punishment are they impatient", "أَفَبِعَذَابِنَا يَسۡتَعۡجِلُونَ", "AfabiAAathabina yastaAAjiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 205, "Then have you considered if We gave them enjoyment for years", "أَفَرَءَيۡتَ إِن مَّتَّعۡنَٰهُمۡ سِنِينَ", "Afaraayta in mattaAAnahum sineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 206, "And then there came to them that which they were promised", "ثُمَّ جَآءَهُم مَّا كَانُواْ يُوعَدُونَ", "Thumma jaahum ma kanoo yooAAadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 207, "They would not be availed by the enjoyment with which they were provided", "مَآ أَغۡنَىٰ عَنۡهُم مَّا كَانُواْ يُمَتَّعُونَ", "Ma aghna AAanhum ma kanoo yumattaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 208, "And We did not destroy any city except that it had warners", "وَمَآ أَهۡلَكۡنَا مِن قَرۡيَةٍ إِلَّا لَهَا مُنذِرُونَ", "Wama ahlakna min qaryatin illa laha munthiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 209, "As a reminder; and never have We been unjust", "ذِكۡرَىٰ وَمَا كُنَّا ظَٰلِمِينَ", "Thikra wama kunna thalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 210, "And the devils have not brought the revelation down", "وَمَا تَنَزَّلَتۡ بِهِ ٱلشَّيَٰطِينُ", "Wama tanazzalat bihi alshshayateenu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 211, "It is not allowable for them, nor would they be able", "وَمَا يَنۢبَغِي لَهُمۡ وَمَا يَسۡتَطِيعُونَ", "Wama yanbaghee lahum wama yastateeAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 212, "Indeed they, from [its] hearing, are removed", "إِنَّهُمۡ عَنِ ٱلسَّمۡعِ لَمَعۡزُولُونَ", "Innahum AAani alssamAAi lamaAAzooloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 213, "So do not invoke with Allah another deity and [thus] be among the punished", "فَلَا تَدۡعُ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَ فَتَكُونَ مِنَ ٱلۡمُعَذَّبِينَ", "Fala tadAAu maAAa Allahi ilahan akhara fatakoona mina almuAAaththabeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 214, "And warn, [O Muhammad], your closest kindred", "وَأَنذِرۡ عَشِيرَتَكَ ٱلۡأَقۡرَبِينَ", "Waanthir AAasheerataka alaqrabeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 215, "And lower your wing to those who follow you of the believers", "وَٱخۡفِضۡ جَنَاحَكَ لِمَنِ ٱتَّبَعَكَ مِنَ ٱلۡمُؤۡمِنِينَ", "Waikhfid janahaka limani ittabaAAaka mina almumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 216, "And if they disobey you, then say, ""Indeed, I am disassociated from what you are doing", "فَإِنۡ عَصَوۡكَ فَقُلۡ إِنِّي بَرِيٓءٞ مِّمَّا تَعۡمَلُونَ", "Fain AAasawka faqul innee bareeon mimma taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 217, "And rely upon the Exalted in Might, the Merciful", "وَتَوَكَّلۡ عَلَى ٱلۡعَزِيزِ ٱلرَّحِيمِ", "Watawakkal AAala alAAazeezi alrraheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 218, "Who sees you when you arise", "ٱلَّذِي يَرَىٰكَ حِينَ تَقُومُ", "Allathee yaraka heena taqoomu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 219, "And your movement among those who prostrate", "وَتَقَلُّبَكَ فِي ٱلسَّـٰجِدِينَ", "Wataqallubaka fee alssajideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 220, "Indeed, He is the Hearing, the Knowing", "إِنَّهُۥ هُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ", "Innahu huwa alssameeAAu alAAaleemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 221, "Shall I inform you upon whom the devils descend", "هَلۡ أُنَبِّئُكُمۡ عَلَىٰ مَن تَنَزَّلُ ٱلشَّيَٰطِينُ", "Hal onabbiokum AAala man tanazzalu alshshayateenu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 222, "They descend upon every sinful liar", "تَنَزَّلُ عَلَىٰ كُلِّ أَفَّاكٍ أَثِيمٖ", "Tanazzalu AAala kulli affakin atheemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 223, "They pass on what is heard, and most of them are liars", "يُلۡقُونَ ٱلسَّمۡعَ وَأَكۡثَرُهُمۡ كَٰذِبُونَ", "Yulqoona alssamAAa waaktharuhum kathiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 224, "And the poets - [only] the deviators follow them", "وَٱلشُّعَرَآءُ يَتَّبِعُهُمُ ٱلۡغَاوُۥنَ", "WaalshshuAAarao yattabiAAuhumu alghawoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 225, "Do you not see that in every valley they roam", "أَلَمۡ تَرَ أَنَّهُمۡ فِي كُلِّ وَادٖ يَهِيمُونَ", "Alam tara annahum fee kulli wadin yaheemoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 226, "And that they say what they do not do", "وَأَنَّهُمۡ يَقُولُونَ مَا لَا يَفۡعَلُونَ", "Waannahum yaqooloona ma la yafAAaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (26, 227, "Except those [poets] who believe and do righteous deeds and remember Allah often and defend [the Muslims] after they were wronged. And those who have wronged are going to know to what [kind of] return they will be returned", "إِلَّا ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ وَذَكَرُواْ ٱللَّهَ كَثِيرٗا وَٱنتَصَرُواْ مِنۢ بَعۡدِ مَا ظُلِمُواْۗ وَسَيَعۡلَمُ ٱلَّذِينَ ظَلَمُوٓاْ أَيَّ مُنقَلَبٖ يَنقَلِبُونَ", "Illa allatheena amanoo waAAamiloo alssalihati wathakaroo Allaha katheeran waintasaroo min baAAdi ma thulimoo wasayaAAlamu allatheena thalamoo ayya munqalabin yanqaliboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 1, "Ta, Seen. These are the verses of the Qur'an and a clear Book", "طسٓۚ تِلۡكَ ءَايَٰتُ ٱلۡقُرۡءَانِ وَكِتَابٖ مُّبِينٍ", "Taseen tilka ayatu alqurani wakitabin mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 2, "As guidance and good tidings for the believers", "هُدٗى وَبُشۡرَىٰ لِلۡمُؤۡمِنِينَ", "Hudan wabushra lilmumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 3, "Who establish prayer and give zakah, and of the Hereafter they are certain [in faith]", "ٱلَّذِينَ يُقِيمُونَ ٱلصَّلَوٰةَ وَيُؤۡتُونَ ٱلزَّكَوٰةَ وَهُم بِٱلۡأٓخِرَةِ هُمۡ يُوقِنُونَ", "Allatheena yuqeemoona alssalata wayutoona alzzakata wahum bialakhirati hum yooqinoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 4, "Indeed, for those who do not believe in the Hereafter, We have made pleasing to them their deeds, so they wander blindly", "إِنَّ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱلۡأٓخِرَةِ زَيَّنَّا لَهُمۡ أَعۡمَٰلَهُمۡ فَهُمۡ يَعۡمَهُونَ", "Inna allatheena la yuminoona bialakhirati zayyanna lahum aAAmalahum fahum yaAAmahoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 5, "Those are the ones for whom there will be the worst of punishment, and in the Hereafter they are the greatest losers", "أُوْلَـٰٓئِكَ ٱلَّذِينَ لَهُمۡ سُوٓءُ ٱلۡعَذَابِ وَهُمۡ فِي ٱلۡأٓخِرَةِ هُمُ ٱلۡأَخۡسَرُونَ", "Olaika allatheena lahum sooo alAAathabi wahum fee alakhirati humu alakhsaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 6, "And indeed, [O Muhammad], you receive the Qur'an from one Wise and Knowing", "وَإِنَّكَ لَتُلَقَّى ٱلۡقُرۡءَانَ مِن لَّدُنۡ حَكِيمٍ عَلِيمٍ", "Wainnaka latulaqqa alqurana min ladun hakeemin AAaleemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 7, "[Mention] when Moses said to his family, ""Indeed, I have perceived a fire. I will bring you from there information or will bring you a burning torch that you may warm yourselves", "إِذۡ قَالَ مُوسَىٰ لِأَهۡلِهِۦٓ إِنِّيٓ ءَانَسۡتُ نَارٗا سَـَٔاتِيكُم مِّنۡهَا بِخَبَرٍ أَوۡ ءَاتِيكُم بِشِهَابٖ قَبَسٖ لَّعَلَّكُمۡ تَصۡطَلُونَ", "Ith qala moosa liahlihi innee anastu naran saateekum minha bikhabarin aw ateekum bishihabin qabasin laAAallakum tastaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 8, "But when he came to it, he was called, ""Blessed is whoever is at the fire and whoever is around it. And exalted is Allah, Lord of the worlds", "فَلَمَّا جَآءَهَا نُودِيَ أَنۢ بُورِكَ مَن فِي ٱلنَّارِ وَمَنۡ حَوۡلَهَا وَسُبۡحَٰنَ ٱللَّهِ رَبِّ ٱلۡعَٰلَمِينَ", "Falamma jaaha noodiya an boorika man fee alnnari waman hawlaha wasubhana Allahi rabbi alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 9, "O Moses, indeed it is I - Allah, the Exalted in Might, the Wise", "يَٰمُوسَىٰٓ إِنَّهُۥٓ أَنَا ٱللَّهُ ٱلۡعَزِيزُ ٱلۡحَكِيمُ", "Ya moosa innahu ana Allahu alAAazeezu alhakeemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 10, "And [he was told], ""Throw down your staff."" But when he saw it writhing as if it were a snake, he turned in flight and did not return. [Allah said], ""O Moses, fear not. Indeed, in My presence the messengers do not fear", "وَأَلۡقِ عَصَاكَۚ فَلَمَّا رَءَاهَا تَهۡتَزُّ كَأَنَّهَا جَآنّٞ وَلَّىٰ مُدۡبِرٗا وَلَمۡ يُعَقِّبۡۚ يَٰمُوسَىٰ لَا تَخَفۡ إِنِّي لَا يَخَافُ لَدَيَّ ٱلۡمُرۡسَلُونَ", "Waalqi AAasaka falamma raaha tahtazzu kaannaha jannun walla mudbiran walam yuAAaqqib ya moosa la takhaf innee la yakhafu ladayya almursaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 11, "Otherwise, he who wrongs, then substitutes good after evil - indeed, I am Forgiving and Merciful", "إِلَّا مَن ظَلَمَ ثُمَّ بَدَّلَ حُسۡنَۢا بَعۡدَ سُوٓءٖ فَإِنِّي غَفُورٞ رَّحِيمٞ", "Illa man thalama thumma baddala husnan baAAda sooin fainnee ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 12, "And put your hand into the opening of your garment [at the breast]; it will come out white without disease. [These are] among the nine signs [you will take] to Pharaoh and his people. Indeed, they have been a people defiantly disobedient", "وَأَدۡخِلۡ يَدَكَ فِي جَيۡبِكَ تَخۡرُجۡ بَيۡضَآءَ مِنۡ غَيۡرِ سُوٓءٖۖ فِي تِسۡعِ ءَايَٰتٍ إِلَىٰ فِرۡعَوۡنَ وَقَوۡمِهِۦٓۚ إِنَّهُمۡ كَانُواْ قَوۡمٗا فَٰسِقِينَ", "Waadkhil yadaka fee jaybika takhruj baydaa min ghayri sooin fee tisAAi ayatin ila firAAawna waqawmihi innahum kanoo qawman fasiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 13, "But when there came to them Our visible signs, they said, ""This is obvious magic", "فَلَمَّا جَآءَتۡهُمۡ ءَايَٰتُنَا مُبۡصِرَةٗ قَالُواْ هَٰذَا سِحۡرٞ مُّبِينٞ", "Falamma jaathum ayatuna mubsiratan qaloo hatha sihrun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 14, "And they rejected them, while their [inner] selves were convinced thereof, out of injustice and haughtiness. So see how was the end of the corrupters", "وَجَحَدُواْ بِهَا وَٱسۡتَيۡقَنَتۡهَآ أَنفُسُهُمۡ ظُلۡمٗا وَعُلُوّٗاۚ فَٱنظُرۡ كَيۡفَ كَانَ عَٰقِبَةُ ٱلۡمُفۡسِدِينَ", "Wajahadoo biha waistayqanatha anfusuhum thulman waAAuluwwan faonthur kayfa kana AAaqibatu almufsideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 15, "And We had certainly given to David and Solomon knowledge, and they said, ""Praise [is due] to Allah, who has favored us over many of His believing servants", "وَلَقَدۡ ءَاتَيۡنَا دَاوُۥدَ وَسُلَيۡمَٰنَ عِلۡمٗاۖ وَقَالَا ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِي فَضَّلَنَا عَلَىٰ كَثِيرٖ مِّنۡ عِبَادِهِ ٱلۡمُؤۡمِنِينَ", "Walaqad atayna dawooda wasulaymana AAilman waqala alhamdu lillahi allathee faddalana AAala katheerin min AAibadihi almumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 16, "And Solomon inherited David. He said, ""O people, we have been taught the language of birds, and we have been given from all things. Indeed, this is evident bounty", "وَوَرِثَ سُلَيۡمَٰنُ دَاوُۥدَۖ وَقَالَ يَـٰٓأَيُّهَا ٱلنَّاسُ عُلِّمۡنَا مَنطِقَ ٱلطَّيۡرِ وَأُوتِينَا مِن كُلِّ شَيۡءٍۖ إِنَّ هَٰذَا لَهُوَ ٱلۡفَضۡلُ ٱلۡمُبِينُ", "Wawaritha sulaymanu dawooda waqala ya ayyuha alnnasu AAullimna mantiqa alttayri waooteena min kulli shayin inna hatha lahuwa alfadlu almubeenu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 17, "And gathered for Solomon were his soldiers of the jinn and men and birds, and they were [marching] in rows", "وَحُشِرَ لِسُلَيۡمَٰنَ جُنُودُهُۥ مِنَ ٱلۡجِنِّ وَٱلۡإِنسِ وَٱلطَّيۡرِ فَهُمۡ يُوزَعُونَ", "Wahushira lisulaymana junooduhu mina aljinni waalinsi waalttayri fahum yoozaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 18, "Until, when they came upon the valley of the ants, an ant said, ""O ants, enter your dwellings that you not be crushed by Solomon and his soldiers while they perceive not", "حَتَّىٰٓ إِذَآ أَتَوۡاْ عَلَىٰ وَادِ ٱلنَّمۡلِ قَالَتۡ نَمۡلَةٞ يَـٰٓأَيُّهَا ٱلنَّمۡلُ ٱدۡخُلُواْ مَسَٰكِنَكُمۡ لَا يَحۡطِمَنَّكُمۡ سُلَيۡمَٰنُ وَجُنُودُهُۥ وَهُمۡ لَا يَشۡعُرُونَ", "Hatta itha ataw AAala wadi alnnamli qalat namlatun ya ayyuha alnnamlu odkhuloo masakinakum la yahtimannakum sulaymanu wajunooduhu wahum la yashAAuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 19, "So [Solomon] smiled, amused at her speech, and said, ""My Lord, enable me to be grateful for Your favor which You have bestowed upon me and upon my parents and to do righteousness of which You approve. And admit me by Your mercy into [the ranks of] Your righteous servants", "فَتَبَسَّمَ ضَاحِكٗا مِّن قَوۡلِهَا وَقَالَ رَبِّ أَوۡزِعۡنِيٓ أَنۡ أَشۡكُرَ نِعۡمَتَكَ ٱلَّتِيٓ أَنۡعَمۡتَ عَلَيَّ وَعَلَىٰ وَٰلِدَيَّ وَأَنۡ أَعۡمَلَ صَٰلِحٗا تَرۡضَىٰهُ وَأَدۡخِلۡنِي بِرَحۡمَتِكَ فِي عِبَادِكَ ٱلصَّـٰلِحِينَ", "Fatabassama dahikan min qawliha waqala rabbi awziAAnee an ashkura niAAmataka allatee anAAamta AAalayya waAAala walidayya waan aAAmala salihan tardahu waadkhilnee birahmatika fee AAibadika alssaliheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 20, "And he took attendance of the birds and said, ""Why do I not see the hoopoe - or is he among the absent", "وَتَفَقَّدَ ٱلطَّيۡرَ فَقَالَ مَالِيَ لَآ أَرَى ٱلۡهُدۡهُدَ أَمۡ كَانَ مِنَ ٱلۡغَآئِبِينَ", "Watafaqqada alttayra faqala ma liya la ara alhudhuda am kana mina alghaibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 21, "I will surely punish him with a severe punishment or slaughter him unless he brings me clear authorization", "لَأُعَذِّبَنَّهُۥ عَذَابٗا شَدِيدًا أَوۡ لَأَاْذۡبَحَنَّهُۥٓ أَوۡ لَيَأۡتِيَنِّي بِسُلۡطَٰنٖ مُّبِينٖ", "LaoAAaththibannahu AAathaban shadeedan aw laathbahannahu aw layatiyannee bisultanin mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 22, "But the hoopoe stayed not long and said, ""I have encompassed [in knowledge] that which you have not encompassed, and I have come to you from Sheba with certain news", "فَمَكَثَ غَيۡرَ بَعِيدٖ فَقَالَ أَحَطتُ بِمَا لَمۡ تُحِطۡ بِهِۦ وَجِئۡتُكَ مِن سَبَإِۭ بِنَبَإٖ يَقِينٍ", "Famakatha ghayra baAAeedin faqala ahattu bima lam tuhit bihi wajituka min sabain binabain yaqeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 23, "Indeed, I found [there] a woman ruling them, and she has been given of all things, and she has a great throne", "إِنِّي وَجَدتُّ ٱمۡرَأَةٗ تَمۡلِكُهُمۡ وَأُوتِيَتۡ مِن كُلِّ شَيۡءٖ وَلَهَا عَرۡشٌ عَظِيمٞ", "Innee wajadtu imraatan tamlikuhum waootiyat min kulli shayin walaha AAarshun AAatheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 24, "I found her and her people prostrating to the sun instead of Allah, and Satan has made their deeds pleasing to them and averted them from [His] way, so they are not guided", "وَجَدتُّهَا وَقَوۡمَهَا يَسۡجُدُونَ لِلشَّمۡسِ مِن دُونِ ٱللَّهِ وَزَيَّنَ لَهُمُ ٱلشَّيۡطَٰنُ أَعۡمَٰلَهُمۡ فَصَدَّهُمۡ عَنِ ٱلسَّبِيلِ فَهُمۡ لَا يَهۡتَدُونَ", "Wajadtuha waqawmaha yasjudoona lilshshamsi min dooni Allahi wazayyana lahumu alshshaytanu aAAmalahum fasaddahum AAani alssabeeli fahum la yahtadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 25, "[And] so they do not prostrate to Allah, who brings forth what is hidden within the heavens and the earth and knows what you conceal and what you declare", "أَلَّاۤ يَسۡجُدُواْۤ لِلَّهِ ٱلَّذِي يُخۡرِجُ ٱلۡخَبۡءَ فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَيَعۡلَمُ مَا تُخۡفُونَ وَمَا تُعۡلِنُونَ", "Alla yasjudoo lillahi allathee yukhriju alkhabaa fee alssamawati waalardi wayaAAlamu ma tukhfoona wama tuAAlinoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 26, "Allah - there is no deity except Him, Lord of the Great Throne", "ٱللَّهُ لَآ إِلَٰهَ إِلَّا هُوَ رَبُّ ٱلۡعَرۡشِ ٱلۡعَظِيمِ۩", "Allahu la ilaha illa huwa rabbu alAAarshi alAAatheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 27, "[Solomon] said, ""We will see whether you were truthful or were of the liars", "۞قَالَ سَنَنظُرُ أَصَدَقۡتَ أَمۡ كُنتَ مِنَ ٱلۡكَٰذِبِينَ", "Qala sananthuru asadaqta am kunta mina alkathibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 28, "Take this letter of mine and deliver it to them. Then leave them and see what [answer] they will return", "ٱذۡهَب بِّكِتَٰبِي هَٰذَا فَأَلۡقِهۡ إِلَيۡهِمۡ ثُمَّ تَوَلَّ عَنۡهُمۡ فَٱنظُرۡ مَاذَا يَرۡجِعُونَ", "Ithhab bikitabee hatha faalqih ilayhim thumma tawalla AAanhum faonthur matha yarjiAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 29, "She said, ""O eminent ones, indeed, to me has been delivered a noble letter", "قَالَتۡ يَـٰٓأَيُّهَا ٱلۡمَلَؤُاْ إِنِّيٓ أُلۡقِيَ إِلَيَّ كِتَٰبٞ كَرِيمٌ", "Qalat ya ayyuha almalao innee olqiya ilayya kitabun kareemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 30, "Indeed, it is from Solomon, and indeed, it reads: 'In the name of Allah, the Entirely Merciful, the Especially Merciful", "إِنَّهُۥ مِن سُلَيۡمَٰنَ وَإِنَّهُۥ بِسۡمِ ٱللَّهِ ٱلرَّحۡمَٰنِ ٱلرَّحِيمِ", "Innahu min sulaymana wainnahu bismi Allahi alrrahmani alrraheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 31, "Be not haughty with me but come to me in submission [as Muslims]", "أَلَّا تَعۡلُواْ عَلَيَّ وَأۡتُونِي مُسۡلِمِينَ", "Alla taAAloo AAalayya watoonee muslimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 32, "She said, ""O eminent ones, advise me in my affair. I would not decide a matter until you witness [for] me", "قَالَتۡ يَـٰٓأَيُّهَا ٱلۡمَلَؤُاْ أَفۡتُونِي فِيٓ أَمۡرِي مَا كُنتُ قَاطِعَةً أَمۡرًا حَتَّىٰ تَشۡهَدُونِ", "Qalat ya ayyuha almalao aftoonee fee amree ma kuntu qatiAAatan amran hatta tashhadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 33, "They said, ""We are men of strength and of great military might, but the command is yours, so see what you will command", "قَالُواْ نَحۡنُ أُوْلُواْ قُوَّةٖ وَأُوْلُواْ بَأۡسٖ شَدِيدٖ وَٱلۡأَمۡرُ إِلَيۡكِ فَٱنظُرِي مَاذَا تَأۡمُرِينَ", "Qaloo nahnu oloo quwwatin waoloo basin shadeedin waalamru ilayki faonthuree matha tamureena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 34, "She said, ""Indeed kings - when they enter a city, they ruin it and render the honored of its people humbled. And thus do they do", "قَالَتۡ إِنَّ ٱلۡمُلُوكَ إِذَا دَخَلُواْ قَرۡيَةً أَفۡسَدُوهَا وَجَعَلُوٓاْ أَعِزَّةَ أَهۡلِهَآ أَذِلَّةٗۚ وَكَذَٰلِكَ يَفۡعَلُونَ", "Qalat inna almulooka itha dakhaloo qaryatan afsadooha wajaAAaloo aAAizzata ahliha athillatan wakathalika yafAAaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 35, "But indeed, I will send to them a gift and see with what [reply] the messengers will return", "وَإِنِّي مُرۡسِلَةٌ إِلَيۡهِم بِهَدِيَّةٖ فَنَاظِرَةُۢ بِمَ يَرۡجِعُ ٱلۡمُرۡسَلُونَ", "Wainnee mursilatun ilayhim bihadiyyatin fanathiratun bima yarjiAAu almursaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 36, "So when they came to Solomon, he said, ""Do you provide me with wealth? But what Allah has given me is better than what He has given you. Rather, it is you who rejoice in your gift", "فَلَمَّا جَآءَ سُلَيۡمَٰنَ قَالَ أَتُمِدُّونَنِ بِمَالٖ فَمَآ ءَاتَىٰنِۦَ ٱللَّهُ خَيۡرٞ مِّمَّآ ءَاتَىٰكُمۚ بَلۡ أَنتُم بِهَدِيَّتِكُمۡ تَفۡرَحُونَ", "Falamma jaa sulaymana qala atumiddoonani bimalin fama ataniya Allahu khayrun mimma atakum bal antum bihadiyyatikum tafrahoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 37, "Return to them, for we will surely come to them with soldiers that they will be powerless to encounter, and we will surely expel them therefrom in humiliation, and they will be debased", "ٱرۡجِعۡ إِلَيۡهِمۡ فَلَنَأۡتِيَنَّهُم بِجُنُودٖ لَّا قِبَلَ لَهُم بِهَا وَلَنُخۡرِجَنَّهُم مِّنۡهَآ أَذِلَّةٗ وَهُمۡ صَٰغِرُونَ", "IrjiAA ilayhim falanatiyannahum bijunoodin la qibala lahum biha walanukhrijannahum minha athillatan wahum saghiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 38, "[Solomon] said, ""O assembly [of jinn], which of you will bring me her throne before they come to me in submission", "قَالَ يَـٰٓأَيُّهَا ٱلۡمَلَؤُاْ أَيُّكُمۡ يَأۡتِينِي بِعَرۡشِهَا قَبۡلَ أَن يَأۡتُونِي مُسۡلِمِينَ", "Qala ya ayyuha almalao ayyukum yateenee biAAarshiha qabla an yatoonee muslimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 39, "A powerful one from among the jinn said, ""I will bring it to you before you rise from your place, and indeed, I am for this [task] strong and trustworthy", "قَالَ عِفۡرِيتٞ مِّنَ ٱلۡجِنِّ أَنَا۠ ءَاتِيكَ بِهِۦ قَبۡلَ أَن تَقُومَ مِن مَّقَامِكَۖ وَإِنِّي عَلَيۡهِ لَقَوِيٌّ أَمِينٞ", "Qala AAifreetun mina aljinni ana ateeka bihi qabla an taqooma min maqamika wainne AAalayhi laqawiyyun ameenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 40, "Said one who had knowledge from the Scripture, ""I will bring it to you before your glance returns to you."" And when [Solomon] saw it placed before him, he said, ""This is from the favor of my Lord to test me whether I will be grateful or ungrateful. And whoever is grateful - his gratitude is only for [the benefit of] himself. And whoever is ungrateful - then indeed, my Lord is Free of need and Generous", "قَالَ ٱلَّذِي عِندَهُۥ عِلۡمٞ مِّنَ ٱلۡكِتَٰبِ أَنَا۠ ءَاتِيكَ بِهِۦ قَبۡلَ أَن يَرۡتَدَّ إِلَيۡكَ طَرۡفُكَۚ فَلَمَّا رَءَاهُ مُسۡتَقِرًّا عِندَهُۥ قَالَ هَٰذَا مِن فَضۡلِ رَبِّي لِيَبۡلُوَنِيٓ ءَأَشۡكُرُ أَمۡ أَكۡفُرُۖ وَمَن شَكَرَ فَإِنَّمَا يَشۡكُرُ لِنَفۡسِهِۦۖ وَمَن كَفَرَ فَإِنَّ رَبِّي غَنِيّٞ كَرِيمٞ", "Qala allathee AAindahu AAilmun mina alkitabi ana ateeka bihi qabla an yartadda ilayka tarfuka falamma raahu mustaqirran AAindahu qala hatha min fadli rabbee liyabluwanee aashkuru am akfuru waman shakara fainnama yashkuru linafsihi waman kafara fainna rabbee ghaniyyun kareemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 41, "He said, ""Disguise for her her throne; we will see whether she will be guided [to truth] or will be of those who is not guided", "قَالَ نَكِّرُواْ لَهَا عَرۡشَهَا نَنظُرۡ أَتَهۡتَدِيٓ أَمۡ تَكُونُ مِنَ ٱلَّذِينَ لَا يَهۡتَدُونَ", "Qala nakkiroo laha AAarshaha nanthur atahtadee am takoonu mina allatheena la yahtadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 42, "So when she arrived, it was said [to her], ""Is your throne like this?"" She said, ""[It is] as though it was it."" [Solomon said], ""And we were given knowledge before her, and we have been Muslims [in submission to Allah]", "فَلَمَّا جَآءَتۡ قِيلَ أَهَٰكَذَا عَرۡشُكِۖ قَالَتۡ كَأَنَّهُۥ هُوَۚ وَأُوتِينَا ٱلۡعِلۡمَ مِن قَبۡلِهَا وَكُنَّا مُسۡلِمِينَ", "Falamma jaat qeela ahakatha AAarshuki qalat kaannahu huwa waooteena alAAilma min qabliha wakunna muslimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 43, "And that which she was worshipping other than Allah had averted her [from submission to Him]. Indeed, she was from a disbelieving people", "وَصَدَّهَا مَا كَانَت تَّعۡبُدُ مِن دُونِ ٱللَّهِۖ إِنَّهَا كَانَتۡ مِن قَوۡمٖ كَٰفِرِينَ", "Wasaddaha ma kanat taAAbudu min dooni Allahi innaha kanat min qawmin kafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 44, "She was told, ""Enter the palace."" But when she saw it, she thought it was a body of water and uncovered her shins [to wade through]. He said, ""Indeed, it is a palace [whose floor is] made smooth with glass."" She said, ""My Lord, indeed I have wronged myself, and I submit with Solomon to Allah, Lord of the worlds", "قِيلَ لَهَا ٱدۡخُلِي ٱلصَّرۡحَۖ فَلَمَّا رَأَتۡهُ حَسِبَتۡهُ لُجَّةٗ وَكَشَفَتۡ عَن سَاقَيۡهَاۚ قَالَ إِنَّهُۥ صَرۡحٞ مُّمَرَّدٞ مِّن قَوَارِيرَۗ قَالَتۡ رَبِّ إِنِّي ظَلَمۡتُ نَفۡسِي وَأَسۡلَمۡتُ مَعَ سُلَيۡمَٰنَ لِلَّهِ رَبِّ ٱلۡعَٰلَمِينَ", "Qeela laha odkhulee alssarha falamma raathu hasibathu lujjatan wakashafat AAan saqayha qala innahu sarhun mumarradun min qawareera qalat rabbi innee thalamtu nafsee waaslamtu maAAa sulaymana lillahi rabbi alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 45, "And We had certainly sent to Thamud their brother Salih, [saying], ""Worship Allah,"" and at once they were two parties conflicting", "وَلَقَدۡ أَرۡسَلۡنَآ إِلَىٰ ثَمُودَ أَخَاهُمۡ صَٰلِحًا أَنِ ٱعۡبُدُواْ ٱللَّهَ فَإِذَا هُمۡ فَرِيقَانِ يَخۡتَصِمُونَ", "Walaqad arsalna ila thamooda akhahum salihan ani oAAbudoo Allaha faitha hum fareeqani yakhtasimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 46, "He said, ""O my people, why are you impatient for evil instead of good? Why do you not seek forgiveness of Allah that you may receive mercy", "قَالَ يَٰقَوۡمِ لِمَ تَسۡتَعۡجِلُونَ بِٱلسَّيِّئَةِ قَبۡلَ ٱلۡحَسَنَةِۖ لَوۡلَا تَسۡتَغۡفِرُونَ ٱللَّهَ لَعَلَّكُمۡ تُرۡحَمُونَ", "Qala ya qawmi lima tastaAAjiloona bialssayyiati qabla alhasanati lawla tastaghfiroona Allaha laAAallakum turhamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 47, "They said, ""We consider you a bad omen, you and those with you."" He said, ""Your omen is with Allah. Rather, you are a people being tested", "قَالُواْ ٱطَّيَّرۡنَا بِكَ وَبِمَن مَّعَكَۚ قَالَ طَـٰٓئِرُكُمۡ عِندَ ٱللَّهِۖ بَلۡ أَنتُمۡ قَوۡمٞ تُفۡتَنُونَ", "Qaloo ittayyarna bika wabiman maAAaka qala tairukum AAinda Allahi bal antum qawmun tuftanoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 48, "And there were in the city nine family heads causing corruption in the land and not amending [its affairs]", "وَكَانَ فِي ٱلۡمَدِينَةِ تِسۡعَةُ رَهۡطٖ يُفۡسِدُونَ فِي ٱلۡأَرۡضِ وَلَا يُصۡلِحُونَ", "Wakana fee almadeenati tisAAatu rahtin yufsidoona fee alardi wala yuslihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 49, "They said, ""Take a mutual oath by Allah that we will kill him by night, he and his family. Then we will say to his executor, 'We did not witness the destruction of his family, and indeed, we are truthful", "قَالُواْ تَقَاسَمُواْ بِٱللَّهِ لَنُبَيِّتَنَّهُۥ وَأَهۡلَهُۥ ثُمَّ لَنَقُولَنَّ لِوَلِيِّهِۦ مَا شَهِدۡنَا مَهۡلِكَ أَهۡلِهِۦ وَإِنَّا لَصَٰدِقُونَ", "Qaloo taqasamoo biAllahi lanubayyitannahu waahlahu thumma lanaqoolanna liwaliyyihi ma shahidna mahlika ahlihi wainna lasadiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 50, "And they planned a plan, and We planned a plan, while they perceived not", "وَمَكَرُواْ مَكۡرٗا وَمَكَرۡنَا مَكۡرٗا وَهُمۡ لَا يَشۡعُرُونَ", "Wamakaroo makran wamakarna makran wahum la yashAAuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 51, "Then look how was the outcome of their plan - that We destroyed them and their people, all", "فَٱنظُرۡ كَيۡفَ كَانَ عَٰقِبَةُ مَكۡرِهِمۡ أَنَّا دَمَّرۡنَٰهُمۡ وَقَوۡمَهُمۡ أَجۡمَعِينَ", "Faonthur kayfa kana AAaqibatu makrihim anna dammarnahum waqawmahum ajmaAAeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 52, "So those are their houses, desolate because of the wrong they had done. Indeed in that is a sign for people who know", "فَتِلۡكَ بُيُوتُهُمۡ خَاوِيَةَۢ بِمَا ظَلَمُوٓاْۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لِّقَوۡمٖ يَعۡلَمُونَ", "Fatilka buyootuhum khawiyatan bima thalamoo inna fee thalika laayatan liqawmin yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 53, "And We saved those who believed and used to fear Allah", "وَأَنجَيۡنَا ٱلَّذِينَ ءَامَنُواْ وَكَانُواْ يَتَّقُونَ", "Waanjayna allatheena amanoo wakanoo yattaqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 54, "And [mention] Lot, when he said to his people, ""Do you commit immorality while you are seeing", "وَلُوطًا إِذۡ قَالَ لِقَوۡمِهِۦٓ أَتَأۡتُونَ ٱلۡفَٰحِشَةَ وَأَنتُمۡ تُبۡصِرُونَ", "Walootan ith qala liqawmihi atatoona alfahishata waantum tubsiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 55, "Do you indeed approach men with desire instead of women? Rather, you are a people behaving ignorantly", "أَئِنَّكُمۡ لَتَأۡتُونَ ٱلرِّجَالَ شَهۡوَةٗ مِّن دُونِ ٱلنِّسَآءِۚ بَلۡ أَنتُمۡ قَوۡمٞ تَجۡهَلُونَ", "Ainnakum latatoona alrrijala shahwatan min dooni alnnisai bal antum qawmun tajhaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 56, "But the answer of his people was not except that they said, ""Expel the family of Lot from your city. Indeed, they are people who keep themselves pure", "۞فَمَا كَانَ جَوَابَ قَوۡمِهِۦٓ إِلَّآ أَن قَالُوٓاْ أَخۡرِجُوٓاْ ءَالَ لُوطٖ مِّن قَرۡيَتِكُمۡۖ إِنَّهُمۡ أُنَاسٞ يَتَطَهَّرُونَ", "Fama kana jawaba qawmihi illa an qaloo akhrijoo ala lootin min qaryatikum innahum onasun yatatahharoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 57, "So We saved him and his family, except for his wife; We destined her to be of those who remained behind", "فَأَنجَيۡنَٰهُ وَأَهۡلَهُۥٓ إِلَّا ٱمۡرَأَتَهُۥ قَدَّرۡنَٰهَا مِنَ ٱلۡغَٰبِرِينَ", "Faanjaynahu waahlahu illa imraatahu qaddarnaha mina alghabireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 58, "And We rained upon them a rain [of stones], and evil was the rain of those who were warned", "وَأَمۡطَرۡنَا عَلَيۡهِم مَّطَرٗاۖ فَسَآءَ مَطَرُ ٱلۡمُنذَرِينَ", "Waamtarna AAalayhim mataran fasaa mataru almunthareena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 59, "Say, [O Muhammad], ""Praise be to Allah, and peace upon His servants whom He has chosen. Is Allah better or what they associate with Him", "قُلِ ٱلۡحَمۡدُ لِلَّهِ وَسَلَٰمٌ عَلَىٰ عِبَادِهِ ٱلَّذِينَ ٱصۡطَفَىٰٓۗ ءَآللَّهُ خَيۡرٌ أَمَّا يُشۡرِكُونَ", "Quli alhamdu lillahi wasalamun AAala AAibadihi allatheena istafa allahu khayrun amma yushrikoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 60, "[More precisely], is He [not best] who created the heavens and the earth and sent down for you rain from the sky, causing to grow thereby gardens of joyful beauty which you could not [otherwise] have grown the trees thereof? Is there a deity with Allah? [No], but they are a people who ascribe equals [to Him]", "أَمَّنۡ خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَأَنزَلَ لَكُم مِّنَ ٱلسَّمَآءِ مَآءٗ فَأَنۢبَتۡنَا بِهِۦ حَدَآئِقَ ذَاتَ بَهۡجَةٖ مَّا كَانَ لَكُمۡ أَن تُنۢبِتُواْ شَجَرَهَآۗ أَءِلَٰهٞ مَّعَ ٱللَّهِۚ بَلۡ هُمۡ قَوۡمٞ يَعۡدِلُونَ", "Amman khalaqa alssamawati waalarda waanzala lakum mina alssamai maan faanbatna bihi hadaiqa thata bahjatin ma kana lakum an tunbitoo shajaraha ailahun maAAa Allahi bal hum qawmun yaAAdiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 61, "Is He [not best] who made the earth a stable ground and placed within it rivers and made for it firmly set mountains and placed between the two seas a barrier? Is there a deity with Allah? [No], but most of them do not know", "أَمَّن جَعَلَ ٱلۡأَرۡضَ قَرَارٗا وَجَعَلَ خِلَٰلَهَآ أَنۡهَٰرٗا وَجَعَلَ لَهَا رَوَٰسِيَ وَجَعَلَ بَيۡنَ ٱلۡبَحۡرَيۡنِ حَاجِزًاۗ أَءِلَٰهٞ مَّعَ ٱللَّهِۚ بَلۡ أَكۡثَرُهُمۡ لَا يَعۡلَمُونَ", "Amman jaAAala alarda qararan wajaAAala khilalaha anharan wajaAAala laha rawasiya wajaAAala bayna albahrayni hajizan ailahun maAAa Allahi bal aktharuhum la yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 62, "Is He [not best] who responds to the desperate one when he calls upon Him and removes evil and makes you inheritors of the earth? Is there a deity with Allah? Little do you remember", "أَمَّن يُجِيبُ ٱلۡمُضۡطَرَّ إِذَا دَعَاهُ وَيَكۡشِفُ ٱلسُّوٓءَ وَيَجۡعَلُكُمۡ خُلَفَآءَ ٱلۡأَرۡضِۗ أَءِلَٰهٞ مَّعَ ٱللَّهِۚ قَلِيلٗا مَّا تَذَكَّرُونَ", "Amman yujeebu almudtarra itha daAAahu wayakshifu alssooa wayajAAalukum khulafaa alardi ailahun maAAa Allahi qaleelan ma tathakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 63, "Is He [not best] who guides you through the darknesses of the land and sea and who sends the winds as good tidings before His mercy? Is there a deity with Allah? High is Allah above whatever they associate with Him", "أَمَّن يَهۡدِيكُمۡ فِي ظُلُمَٰتِ ٱلۡبَرِّ وَٱلۡبَحۡرِ وَمَن يُرۡسِلُ ٱلرِّيَٰحَ بُشۡرَۢا بَيۡنَ يَدَيۡ رَحۡمَتِهِۦٓۗ أَءِلَٰهٞ مَّعَ ٱللَّهِۚ تَعَٰلَى ٱللَّهُ عَمَّا يُشۡرِكُونَ", "Amman yahdeekum fee thulumati albarri waalbahri waman yursilu alrriyaha bushran bayna yaday rahmatihi ailahun maAAa Allahi taAAala Allahu AAamma yushrikoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 64, "Is He [not best] who begins creation and then repeats it and who provides for you from the heaven and earth? Is there a deity with Allah? Say, ""Produce your proof, if you should be truthful", "أَمَّن يَبۡدَؤُاْ ٱلۡخَلۡقَ ثُمَّ يُعِيدُهُۥ وَمَن يَرۡزُقُكُم مِّنَ ٱلسَّمَآءِ وَٱلۡأَرۡضِۗ أَءِلَٰهٞ مَّعَ ٱللَّهِۚ قُلۡ هَاتُواْ بُرۡهَٰنَكُمۡ إِن كُنتُمۡ صَٰدِقِينَ", "Amman yabdao alkhalqa thumma yuAAeeduhu waman yarzuqukum mina alssamai waalardi ailahun maAAa Allahi qul hatoo burhanakum in kuntum sadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 65, "Say, ""None in the heavens and earth knows the unseen except Allah, and they do not perceive when they will be resurrected", "قُل لَّا يَعۡلَمُ مَن فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ ٱلۡغَيۡبَ إِلَّا ٱللَّهُۚ وَمَا يَشۡعُرُونَ أَيَّانَ يُبۡعَثُونَ", "Qul la yaAAlamu man fee alssamawati waalardi alghayba illa Allahu wama yashAAuroona ayyana yubAAathoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 66, "Rather, their knowledge is arrested concerning the Hereafter. Rather, they are in doubt about it. Rather, they are, concerning it, blind", "بَلِ ٱدَّـٰرَكَ عِلۡمُهُمۡ فِي ٱلۡأٓخِرَةِۚ بَلۡ هُمۡ فِي شَكّٖ مِّنۡهَاۖ بَلۡ هُم مِّنۡهَا عَمُونَ", "Bali iddaraka AAilmuhum fee alakhirati bal hum fee shakkin minha bal hum minha AAamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 67, "And those who disbelieve say, ""When we have become dust as well as our forefathers, will we indeed be brought out [of the graves]", "وَقَالَ ٱلَّذِينَ كَفَرُوٓاْ أَءِذَا كُنَّا تُرَٰبٗا وَءَابَآؤُنَآ أَئِنَّا لَمُخۡرَجُونَ", "Waqala allatheena kafaroo aitha kunna turaban waabaona ainna lamukhrajoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 68, "We have been promised this, we and our forefathers, before. This is not but legends of the former peoples", "لَقَدۡ وُعِدۡنَا هَٰذَا نَحۡنُ وَءَابَآؤُنَا مِن قَبۡلُ إِنۡ هَٰذَآ إِلَّآ أَسَٰطِيرُ ٱلۡأَوَّلِينَ", "Laqad wuAAidna hatha nahnu waabaona min qablu in hatha illa asateeru alawwaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 69, "Say, [O Muhammad], ""Travel through the land and observe how was the end of the criminals", "قُلۡ سِيرُواْ فِي ٱلۡأَرۡضِ فَٱنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلۡمُجۡرِمِينَ", "Qul seeroo fee alardi faonthuroo kayfa kana AAaqibatu almujrimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 70, "And grieve not over them or be in distress from what they conspire", "وَلَا تَحۡزَنۡ عَلَيۡهِمۡ وَلَا تَكُن فِي ضَيۡقٖ مِّمَّا يَمۡكُرُونَ", "Wala tahzan AAalayhim wala takun fee dayqin mimma yamkuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 71, "And they say, ""When is [the fulfillment of] this promise, if you should be truthful", "وَيَقُولُونَ مَتَىٰ هَٰذَا ٱلۡوَعۡدُ إِن كُنتُمۡ صَٰدِقِينَ", "Wayaqooloona mata hatha alwaAAdu in kuntum sadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 72, "Say, ""Perhaps it is close behind you - some of that for which you are impatient", "قُلۡ عَسَىٰٓ أَن يَكُونَ رَدِفَ لَكُم بَعۡضُ ٱلَّذِي تَسۡتَعۡجِلُونَ", "Qul AAasa an yakoona radifa lakum baAAdu allathee tastaAAjiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 73, "And indeed, your Lord is full of bounty for the people, but most of them do not show gratitude", "وَإِنَّ رَبَّكَ لَذُو فَضۡلٍ عَلَى ٱلنَّاسِ وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَشۡكُرُونَ", "Wainna rabbaka lathoo fadlin AAala alnnasi walakinna aktharahum la yashkuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 74, "And indeed, your Lord knows what their breasts conceal and what they declare", "وَإِنَّ رَبَّكَ لَيَعۡلَمُ مَا تُكِنُّ صُدُورُهُمۡ وَمَا يُعۡلِنُونَ", "Wainna rabbaka layaAAlamu ma tukinnu sudooruhum wama yuAAlinoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 75, "And there is nothing concealed within the heaven and the earth except that it is in a clear Register", "وَمَا مِنۡ غَآئِبَةٖ فِي ٱلسَّمَآءِ وَٱلۡأَرۡضِ إِلَّا فِي كِتَٰبٖ مُّبِينٍ", "Wama min ghaibatin fee alssamai waalardi illa fee kitabin mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 76, "Indeed, this Qur'an relates to the Children of Israel most of that over which they disagree", "إِنَّ هَٰذَا ٱلۡقُرۡءَانَ يَقُصُّ عَلَىٰ بَنِيٓ إِسۡرَـٰٓءِيلَ أَكۡثَرَ ٱلَّذِي هُمۡ فِيهِ يَخۡتَلِفُونَ", "Inna hatha alqurana yaqussu AAala banee israeela akthara allathee hum feehi yakhtalifoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 77, "And indeed, it is guidance and mercy for the believers", "وَإِنَّهُۥ لَهُدٗى وَرَحۡمَةٞ لِّلۡمُؤۡمِنِينَ", "Wainnahu lahudan warahmatun lilmumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 78, "Indeed, your Lord will judge between them by His [wise] judgement. And He is the Exalted in Might, the Knowing", "إِنَّ رَبَّكَ يَقۡضِي بَيۡنَهُم بِحُكۡمِهِۦۚ وَهُوَ ٱلۡعَزِيزُ ٱلۡعَلِيمُ", "Inna rabbaka yaqdee baynahum bihukmihi wahuwa alAAazeezu alAAaleemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 79, "So rely upon Allah; indeed, you are upon the clear truth", "فَتَوَكَّلۡ عَلَى ٱللَّهِۖ إِنَّكَ عَلَى ٱلۡحَقِّ ٱلۡمُبِينِ", "Fatawakkal AAala Allahi innaka AAala alhaqqi almubeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 80, "Indeed, you will not make the dead hear, nor will you make the deaf hear the call when they have turned their backs retreating", "إِنَّكَ لَا تُسۡمِعُ ٱلۡمَوۡتَىٰ وَلَا تُسۡمِعُ ٱلصُّمَّ ٱلدُّعَآءَ إِذَا وَلَّوۡاْ مُدۡبِرِينَ", "Innaka la tusmiAAu almawta wala tusmiAAu alssumma aldduAAaa itha wallaw mudbireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 81, "And you cannot guide the blind away from their error. You will only make hear those who believe in Our verses so they are Muslims [submitting to Allah]", "وَمَآ أَنتَ بِهَٰدِي ٱلۡعُمۡيِ عَن ضَلَٰلَتِهِمۡۖ إِن تُسۡمِعُ إِلَّا مَن يُؤۡمِنُ بِـَٔايَٰتِنَا فَهُم مُّسۡلِمُونَ", "Wama anta bihadee alAAumyi AAan dalalatihim in tusmiAAu illa man yuminu biayatina fahum muslimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 82, "And when the word befalls them, We will bring forth for them a creature from the earth speaking to them, [saying] that the people were, of Our verses, not certain [in faith]", "۞وَإِذَا وَقَعَ ٱلۡقَوۡلُ عَلَيۡهِمۡ أَخۡرَجۡنَا لَهُمۡ دَآبَّةٗ مِّنَ ٱلۡأَرۡضِ تُكَلِّمُهُمۡ أَنَّ ٱلنَّاسَ كَانُواْ بِـَٔايَٰتِنَا لَا يُوقِنُونَ", "Waitha waqaAAa alqawlu AAalayhim akhrajna lahum dabbatan mina alardi tukallimuhum anna alnnasa kanoo biayatina la yooqinoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 83, "And [warn of] the Day when We will gather from every nation a company of those who deny Our signs, and they will be [driven] in rows", "وَيَوۡمَ نَحۡشُرُ مِن كُلِّ أُمَّةٖ فَوۡجٗا مِّمَّن يُكَذِّبُ بِـَٔايَٰتِنَا فَهُمۡ يُوزَعُونَ", "Wayawma nahshuru min kulli ommatin fawjan mimman yukaththibu biayatina fahum yoozaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 84, "Until, when they arrive [at the place of Judgement], He will say, ""Did you deny My signs while you encompassed them not in knowledge, or what [was it that] you were doing", "حَتَّىٰٓ إِذَا جَآءُو قَالَ أَكَذَّبۡتُم بِـَٔايَٰتِي وَلَمۡ تُحِيطُواْ بِهَا عِلۡمًا أَمَّاذَا كُنتُمۡ تَعۡمَلُونَ", "Hatta itha jaoo qala akaththabtum biayatee walam tuheetoo biha AAilman ammatha kuntum taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 85, "And the decree will befall them for the wrong they did, and they will not [be able to] speak", "وَوَقَعَ ٱلۡقَوۡلُ عَلَيۡهِم بِمَا ظَلَمُواْ فَهُمۡ لَا يَنطِقُونَ", "WawaqaAAa alqawlu AAalayhim bima thalamoo fahum la yantiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 86, "Do they not see that We made the night that they may rest therein and the day giving sight? Indeed in that are signs for a people who believe", "أَلَمۡ يَرَوۡاْ أَنَّا جَعَلۡنَا ٱلَّيۡلَ لِيَسۡكُنُواْ فِيهِ وَٱلنَّهَارَ مُبۡصِرًاۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يُؤۡمِنُونَ", "Alam yaraw anna jaAAalna allayla liyaskunoo feehi waalnnahara mubsiran inna fee thalika laayatin liqawmin yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 87, "And [warn of] the Day the Horn will be blown, and whoever is in the heavens and whoever is on the earth will be terrified except whom Allah wills. And all will come to Him humbled", "وَيَوۡمَ يُنفَخُ فِي ٱلصُّورِ فَفَزِعَ مَن فِي ٱلسَّمَٰوَٰتِ وَمَن فِي ٱلۡأَرۡضِ إِلَّا مَن شَآءَ ٱللَّهُۚ وَكُلٌّ أَتَوۡهُ دَٰخِرِينَ", "Wayawma yunfakhu fee alssoori fafaziAAa man fee alssamawati waman fee alardi illa man shaa Allahu wakullun atawhu dakhireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 88, "And you see the mountains, thinking them rigid, while they will pass as the passing of clouds. [It is] the work of Allah, who perfected all things. Indeed, He is Acquainted with that which you do", "وَتَرَى ٱلۡجِبَالَ تَحۡسَبُهَا جَامِدَةٗ وَهِيَ تَمُرُّ مَرَّ ٱلسَّحَابِۚ صُنۡعَ ٱللَّهِ ٱلَّذِيٓ أَتۡقَنَ كُلَّ شَيۡءٍۚ إِنَّهُۥ خَبِيرُۢ بِمَا تَفۡعَلُونَ", "Watara aljibala tahsabuha jamidatan wahiya tamurru marra alssahabi sunAAa Allahi allathee atqana kulla shayin innahu khabeerun bima tafAAaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 89, "Whoever comes [at Judgement] with a good deed will have better than it, and they, from the terror of that Day, will be safe", "مَن جَآءَ بِٱلۡحَسَنَةِ فَلَهُۥ خَيۡرٞ مِّنۡهَا وَهُم مِّن فَزَعٖ يَوۡمَئِذٍ ءَامِنُونَ", "Man jaa bialhasanati falahu khayrun minha wahum min fazaAAin yawmaithin aminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 90, "And whoever comes with an evil deed - their faces will be overturned into the Fire, [and it will be said], ""Are you recompensed except for what you used to do", "وَمَن جَآءَ بِٱلسَّيِّئَةِ فَكُبَّتۡ وُجُوهُهُمۡ فِي ٱلنَّارِ هَلۡ تُجۡزَوۡنَ إِلَّا مَا كُنتُمۡ تَعۡمَلُونَ", "Waman jaa bialssayyiati fakubbat wujoohuhum fee alnnari hal tujzawna illa ma kuntum taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 91, "[Say, O Muhammad], ""I have only been commanded to worship the Lord of this city, who made it sacred and to whom [belongs] all things. And I am commanded to be of the Muslims [those who submit to Allah]", "إِنَّمَآ أُمِرۡتُ أَنۡ أَعۡبُدَ رَبَّ هَٰذِهِ ٱلۡبَلۡدَةِ ٱلَّذِي حَرَّمَهَا وَلَهُۥ كُلُّ شَيۡءٖۖ وَأُمِرۡتُ أَنۡ أَكُونَ مِنَ ٱلۡمُسۡلِمِينَ", "Innama omirtu an aAAbuda rabba hathihi albaldati allathee harramaha walahu kullu shayin waomirtu an akoona mina almuslimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 92, "And to recite the Qur'an."" And whoever is guided is only guided for [the benefit of] himself; and whoever strays - say, ""I am only [one] of the warners", "وَأَنۡ أَتۡلُوَاْ ٱلۡقُرۡءَانَۖ فَمَنِ ٱهۡتَدَىٰ فَإِنَّمَا يَهۡتَدِي لِنَفۡسِهِۦۖ وَمَن ضَلَّ فَقُلۡ إِنَّمَآ أَنَا۠ مِنَ ٱلۡمُنذِرِينَ", "Waan atluwa alqurana famani ihtada fainnama yahtadee linafsihi waman dalla faqul innama ana mina almunthireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (27, 93, "And say, ""[All] praise is [due] to Allah. He will show you His signs, and you will recognize them. And your Lord is not unaware of what you do", "وَقُلِ ٱلۡحَمۡدُ لِلَّهِ سَيُرِيكُمۡ ءَايَٰتِهِۦ فَتَعۡرِفُونَهَاۚ وَمَا رَبُّكَ بِغَٰفِلٍ عَمَّا تَعۡمَلُونَ", "Waquli alhamdu lillahi sayureekum ayatihi fataAArifoonaha wama rabbuka bighafilin AAamma taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 1, "Ta, Seen, Meem", "طسٓمٓ", "Taseenmeem");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 2, "These are the verses of the clear Book", "تِلۡكَ ءَايَٰتُ ٱلۡكِتَٰبِ ٱلۡمُبِينِ", "Tilka ayatu alkitabi almubeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 3, "We recite to you from the news of Moses and Pharaoh in truth for a people who believe", "نَتۡلُواْ عَلَيۡكَ مِن نَّبَإِ مُوسَىٰ وَفِرۡعَوۡنَ بِٱلۡحَقِّ لِقَوۡمٖ يُؤۡمِنُونَ", "Natloo AAalayka min nabai moosa wafirAAawna bialhaqqi liqawmin yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 4, "Indeed, Pharaoh exalted himself in the land and made its people into factions, oppressing a sector among them, slaughtering their [newborn] sons and keeping their females alive. Indeed, he was of the corrupters", "إِنَّ فِرۡعَوۡنَ عَلَا فِي ٱلۡأَرۡضِ وَجَعَلَ أَهۡلَهَا شِيَعٗا يَسۡتَضۡعِفُ طَآئِفَةٗ مِّنۡهُمۡ يُذَبِّحُ أَبۡنَآءَهُمۡ وَيَسۡتَحۡيِۦ نِسَآءَهُمۡۚ إِنَّهُۥ كَانَ مِنَ ٱلۡمُفۡسِدِينَ", "Inna firAAawna AAala fee alardi wajaAAala ahlaha shiyaAAan yastadAAifu taifatan minhum yuthabbihu abnaahum wayastahyee nisaahum innahu kana mina almufsideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 5, "And We wanted to confer favor upon those who were oppressed in the land and make them leaders and make them inheritors", "وَنُرِيدُ أَن نَّمُنَّ عَلَى ٱلَّذِينَ ٱسۡتُضۡعِفُواْ فِي ٱلۡأَرۡضِ وَنَجۡعَلَهُمۡ أَئِمَّةٗ وَنَجۡعَلَهُمُ ٱلۡوَٰرِثِينَ", "Wanureedu an namunna AAala allatheena istudAAifoo fee alardi wanajAAalahum aimmatan wanajAAalahumu alwaritheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 6, "And establish them in the land and show Pharaoh and [his minister] Haman and their soldiers through them that which they had feared", "وَنُمَكِّنَ لَهُمۡ فِي ٱلۡأَرۡضِ وَنُرِيَ فِرۡعَوۡنَ وَهَٰمَٰنَ وَجُنُودَهُمَا مِنۡهُم مَّا كَانُواْ يَحۡذَرُونَ", "Wanumakkina lahum fee alardi wanuriya firAAawna wahamana wajunoodahuma minhum ma kanoo yahtharoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 7, "And We inspired to the mother of Moses, ""Suckle him; but when you fear for him, cast him into the river and do not fear and do not grieve. Indeed, We will return him to you and will make him [one] of the messengers", "وَأَوۡحَيۡنَآ إِلَىٰٓ أُمِّ مُوسَىٰٓ أَنۡ أَرۡضِعِيهِۖ فَإِذَا خِفۡتِ عَلَيۡهِ فَأَلۡقِيهِ فِي ٱلۡيَمِّ وَلَا تَخَافِي وَلَا تَحۡزَنِيٓۖ إِنَّا رَآدُّوهُ إِلَيۡكِ وَجَاعِلُوهُ مِنَ ٱلۡمُرۡسَلِينَ", "Waawhayna ila ommi moosa an ardiAAeehi faitha khifti AAalayhi faalqeehi fee alyammi wala takhafee wala tahzanee inna raddoohu ilayki wajaAAiloohu mina almursaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 8, "And the family of Pharaoh picked him up [out of the river] so that he would become to them an enemy and a [cause of] grief. Indeed, Pharaoh and Haman and their soldiers were deliberate sinners", "فَٱلۡتَقَطَهُۥٓ ءَالُ فِرۡعَوۡنَ لِيَكُونَ لَهُمۡ عَدُوّٗا وَحَزَنًاۗ إِنَّ فِرۡعَوۡنَ وَهَٰمَٰنَ وَجُنُودَهُمَا كَانُواْ خَٰطِـِٔينَ", "Failtaqatahu alu firAAawna liyakoona lahum AAaduwwan wahazanan inna firAAawna wahamana wajunoodahuma kanoo khatieena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 9, "And the wife of Pharaoh said, ""[He will be] a comfort of the eye for me and for you. Do not kill him; perhaps he may benefit us, or we may adopt him as a son."" And they perceived not", "وَقَالَتِ ٱمۡرَأَتُ فِرۡعَوۡنَ قُرَّتُ عَيۡنٖ لِّي وَلَكَۖ لَا تَقۡتُلُوهُ عَسَىٰٓ أَن يَنفَعَنَآ أَوۡ نَتَّخِذَهُۥ وَلَدٗا وَهُمۡ لَا يَشۡعُرُونَ", "Waqalati imraatu firAAawna qurratu AAaynin lee walaka la taqtuloohu AAasa an yanfaAAana aw nattakhithahu waladan wahum la yashAAuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 10, "And the heart of Moses' mother became empty [of all else]. She was about to disclose [the matter concerning] him had We not bound fast her heart that she would be of the believers", "وَأَصۡبَحَ فُؤَادُ أُمِّ مُوسَىٰ فَٰرِغًاۖ إِن كَادَتۡ لَتُبۡدِي بِهِۦ لَوۡلَآ أَن رَّبَطۡنَا عَلَىٰ قَلۡبِهَا لِتَكُونَ مِنَ ٱلۡمُؤۡمِنِينَ", "Waasbaha fuadu ommi moosa farighan in kadat latubdee bihi lawla an rabatna AAala qalbiha litakoona mina almumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 11, "And she said to his sister, ""Follow him""; so she watched him from a distance while they perceived not", "وَقَالَتۡ لِأُخۡتِهِۦ قُصِّيهِۖ فَبَصُرَتۡ بِهِۦ عَن جُنُبٖ وَهُمۡ لَا يَشۡعُرُونَ", "Waqalat liokhtihi qusseehi fabasurat bihi AAan junubin wahum la yashAAuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 12, "And We had prevented from him [all] wet nurses before, so she said, ""Shall I direct you to a household that will be responsible for him for you while they are to him [for his upbringing] sincere", "۞وَحَرَّمۡنَا عَلَيۡهِ ٱلۡمَرَاضِعَ مِن قَبۡلُ فَقَالَتۡ هَلۡ أَدُلُّكُمۡ عَلَىٰٓ أَهۡلِ بَيۡتٖ يَكۡفُلُونَهُۥ لَكُمۡ وَهُمۡ لَهُۥ نَٰصِحُونَ", "Waharramna AAalayhi almaradiAAa min qablu faqalat hal adullukum AAala ahli baytin yakfuloonahu lakum wahum lahu nasihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 13, "So We restored him to his mother that she might be content and not grieve and that she would know that the promise of Allah is true. But most of the people do not know", "فَرَدَدۡنَٰهُ إِلَىٰٓ أُمِّهِۦ كَيۡ تَقَرَّ عَيۡنُهَا وَلَا تَحۡزَنَ وَلِتَعۡلَمَ أَنَّ وَعۡدَ ٱللَّهِ حَقّٞ وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَعۡلَمُونَ", "Faradadnahu ila ommihi kay taqarra AAaynuha wala tahzana walitaAAlama anna waAAda Allahi haqqun walakinna aktharahum la yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 14, "And when he attained his full strength and was [mentally] mature, We bestowed upon him judgement and knowledge. And thus do We reward the doers of good", "وَلَمَّا بَلَغَ أَشُدَّهُۥ وَٱسۡتَوَىٰٓ ءَاتَيۡنَٰهُ حُكۡمٗا وَعِلۡمٗاۚ وَكَذَٰلِكَ نَجۡزِي ٱلۡمُحۡسِنِينَ", "Walamma balagha ashuddahu waistawa ataynahu hukman waAAilman wakathalika najzee almuhsineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 15, "And he entered the city at a time of inattention by its people and found therein two men fighting: one from his faction and one from among his enemy. And the one from his faction called for help to him against the one from his enemy, so Moses struck him and [unintentionally] killed him. [Moses] said, ""This is from the work of Satan. Indeed, he is a manifest, misleading enemy", "وَدَخَلَ ٱلۡمَدِينَةَ عَلَىٰ حِينِ غَفۡلَةٖ مِّنۡ أَهۡلِهَا فَوَجَدَ فِيهَا رَجُلَيۡنِ يَقۡتَتِلَانِ هَٰذَا مِن شِيعَتِهِۦ وَهَٰذَا مِنۡ عَدُوِّهِۦۖ فَٱسۡتَغَٰثَهُ ٱلَّذِي مِن شِيعَتِهِۦ عَلَى ٱلَّذِي مِنۡ عَدُوِّهِۦ فَوَكَزَهُۥ مُوسَىٰ فَقَضَىٰ عَلَيۡهِۖ قَالَ هَٰذَا مِنۡ عَمَلِ ٱلشَّيۡطَٰنِۖ إِنَّهُۥ عَدُوّٞ مُّضِلّٞ مُّبِينٞ", "Wadakhala almadeenata AAala heeni ghaflatin min ahliha fawajada feeha rajulayni yaqtatilani hatha min sheeAAatihi wahatha min AAaduwwihi faistaghathahu allathee min sheeAAatihi AAala allathee min AAaduwwihi fawakazahu moosa faqada AAalayhi qala hatha min AAamali alshshaytani innahu AAaduwwun mudillun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 16, "He said, ""My Lord, indeed I have wronged myself, so forgive me,"" and He forgave him. Indeed, He is the Forgiving, the Merciful", "قَالَ رَبِّ إِنِّي ظَلَمۡتُ نَفۡسِي فَٱغۡفِرۡ لِي فَغَفَرَ لَهُۥٓۚ إِنَّهُۥ هُوَ ٱلۡغَفُورُ ٱلرَّحِيمُ", "Qala rabbi innee thalamtu nafsee faighfir lee faghafara lahu innahu huwa alghafooru alrraheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 17, "He said, ""My Lord, for the favor You bestowed upon me, I will never be an assistant to the criminals", "قَالَ رَبِّ بِمَآ أَنۡعَمۡتَ عَلَيَّ فَلَنۡ أَكُونَ ظَهِيرٗا لِّلۡمُجۡرِمِينَ", "Qala rabbi bima anAAamta AAalayya falan akoona thaheeran lilmujrimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 18, "And he became inside the city fearful and anticipating [exposure], when suddenly the one who sought his help the previous day cried out to him [once again]. Moses said to him, ""Indeed, you are an evident, [persistent] deviator", "فَأَصۡبَحَ فِي ٱلۡمَدِينَةِ خَآئِفٗا يَتَرَقَّبُ فَإِذَا ٱلَّذِي ٱسۡتَنصَرَهُۥ بِٱلۡأَمۡسِ يَسۡتَصۡرِخُهُۥۚ قَالَ لَهُۥ مُوسَىٰٓ إِنَّكَ لَغَوِيّٞ مُّبِينٞ", "Faasbaha fee almadeenati khaifan yataraqqabu faitha allathee istansarahu bialamsi yastasrikhuhu qala lahu moosa innaka laghawiyyun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 19, "And when he wanted to strike the one who was an enemy to both of them, he said, ""O Moses, do you intend to kill me as you killed someone yesterday? You only want to be a tyrant in the land and do not want to be of the amenders", "فَلَمَّآ أَنۡ أَرَادَ أَن يَبۡطِشَ بِٱلَّذِي هُوَ عَدُوّٞ لَّهُمَا قَالَ يَٰمُوسَىٰٓ أَتُرِيدُ أَن تَقۡتُلَنِي كَمَا قَتَلۡتَ نَفۡسَۢا بِٱلۡأَمۡسِۖ إِن تُرِيدُ إِلَّآ أَن تَكُونَ جَبَّارٗا فِي ٱلۡأَرۡضِ وَمَا تُرِيدُ أَن تَكُونَ مِنَ ٱلۡمُصۡلِحِينَ", "Falamma an arada an yabtisha biallathee huwa AAaduwwun lahuma qala ya moosa atureedu an taqtulanee kama qatalta nafsan bialamsi in tureedu illa an takoona jabbaran fee alardi wama tureedu an takoona mina almusliheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 20, "And a man came from the farthest end of the city, running. He said, ""O Moses, indeed the eminent ones are conferring over you [intending] to kill you, so leave [the city]; indeed, I am to you of the sincere advisors", "وَجَآءَ رَجُلٞ مِّنۡ أَقۡصَا ٱلۡمَدِينَةِ يَسۡعَىٰ قَالَ يَٰمُوسَىٰٓ إِنَّ ٱلۡمَلَأَ يَأۡتَمِرُونَ بِكَ لِيَقۡتُلُوكَ فَٱخۡرُجۡ إِنِّي لَكَ مِنَ ٱلنَّـٰصِحِينَ", "Wajaa rajulun min aqsa almadeenati yasAAa qala ya moosa inna almalaa yatamiroona bika liyaqtulooka faokhruj innee laka mina alnnasiheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 21, "So he left it, fearful and anticipating [apprehension]. He said, ""My Lord, save me from the wrongdoing people", "فَخَرَجَ مِنۡهَا خَآئِفٗا يَتَرَقَّبُۖ قَالَ رَبِّ نَجِّنِي مِنَ ٱلۡقَوۡمِ ٱلظَّـٰلِمِينَ", "Fakharaja minha khaifan yataraqqabu qala rabbi najjinee mina alqawmi alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 22, "And when he directed himself toward Madyan, he said, ""Perhaps my Lord will guide me to the sound way", "وَلَمَّا تَوَجَّهَ تِلۡقَآءَ مَدۡيَنَ قَالَ عَسَىٰ رَبِّيٓ أَن يَهۡدِيَنِي سَوَآءَ ٱلسَّبِيلِ", "Walamma tawajjaha tilqaa madyana qala AAasa rabbee an yahdiyanee sawaa alssabeeli");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 23, "And when he came to the well of Madyan, he found there a crowd of people watering [their flocks], and he found aside from them two women driving back [their flocks]. He said, ""What is your circumstance?"" They said, ""We do not water until the shepherds dispatch [their flocks]; and our father is an old man", "وَلَمَّا وَرَدَ مَآءَ مَدۡيَنَ وَجَدَ عَلَيۡهِ أُمَّةٗ مِّنَ ٱلنَّاسِ يَسۡقُونَ وَوَجَدَ مِن دُونِهِمُ ٱمۡرَأَتَيۡنِ تَذُودَانِۖ قَالَ مَا خَطۡبُكُمَاۖ قَالَتَا لَا نَسۡقِي حَتَّىٰ يُصۡدِرَ ٱلرِّعَآءُۖ وَأَبُونَا شَيۡخٞ كَبِيرٞ", "Walamma warada maa madyana wajada AAalayhi ommatan mina alnnasi yasqoona wawajada min doonihimu imraatayni tathoodani qala ma khatbukuma qalata la nasqee hatta yusdira alrriAAao waaboona shaykhun kabeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 24, "So he watered [their flocks] for them; then he went back to the shade and said, ""My Lord, indeed I am, for whatever good You would send down to me, in need", "فَسَقَىٰ لَهُمَا ثُمَّ تَوَلَّىٰٓ إِلَى ٱلظِّلِّ فَقَالَ رَبِّ إِنِّي لِمَآ أَنزَلۡتَ إِلَيَّ مِنۡ خَيۡرٖ فَقِيرٞ", "Fasaqa lahuma thumma tawalla ila alththilli faqala rabbi innee lima anzalta ilayya min khayrin faqeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 25, "Then one of the two women came to him walking with shyness. She said, ""Indeed, my father invites you that he may reward you for having watered for us."" So when he came to him and related to him the story, he said, ""Fear not. You have escaped from the wrongdoing people", "فَجَآءَتۡهُ إِحۡدَىٰهُمَا تَمۡشِي عَلَى ٱسۡتِحۡيَآءٖ قَالَتۡ إِنَّ أَبِي يَدۡعُوكَ لِيَجۡزِيَكَ أَجۡرَ مَا سَقَيۡتَ لَنَاۚ فَلَمَّا جَآءَهُۥ وَقَصَّ عَلَيۡهِ ٱلۡقَصَصَ قَالَ لَا تَخَفۡۖ نَجَوۡتَ مِنَ ٱلۡقَوۡمِ ٱلظَّـٰلِمِينَ", "Fajaathu ihdahuma tamshee AAala istihyain qalat inna abee yadAAooka liyajziyaka ajra ma saqayta lana falamma jaahu waqassa AAalayhi alqasasa qala la takhaf najawta mina alqawmi alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 26, "One of the women said, ""O my father, hire him. Indeed, the best one you can hire is the strong and the trustworthy", "قَالَتۡ إِحۡدَىٰهُمَا يَـٰٓأَبَتِ ٱسۡتَـٔۡجِرۡهُۖ إِنَّ خَيۡرَ مَنِ ٱسۡتَـٔۡجَرۡتَ ٱلۡقَوِيُّ ٱلۡأَمِينُ", "Qalat ihdahuma ya abati istajirhu inna khayra mani istajarta alqawiyyu alameenu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 27, "He said, ""Indeed, I wish to wed you one of these, my two daughters, on [the condition] that you serve me for eight years; but if you complete ten, it will be [as a favor] from you. And I do not wish to put you in difficulty. You will find me, if Allah wills, from among the righteous", "قَالَ إِنِّيٓ أُرِيدُ أَنۡ أُنكِحَكَ إِحۡدَى ٱبۡنَتَيَّ هَٰتَيۡنِ عَلَىٰٓ أَن تَأۡجُرَنِي ثَمَٰنِيَ حِجَجٖۖ فَإِنۡ أَتۡمَمۡتَ عَشۡرٗا فَمِنۡ عِندِكَۖ وَمَآ أُرِيدُ أَنۡ أَشُقَّ عَلَيۡكَۚ سَتَجِدُنِيٓ إِن شَآءَ ٱللَّهُ مِنَ ٱلصَّـٰلِحِينَ", "Qala innee oreedu an onkihaka ihda ibnatayya hatayni AAala an tajuranee thamaniya hijajin fain atmamta AAashran famin AAindika wama oreedu an ashuqqa AAalayka satajidunee in shaa Allahu mina alssaliheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 28, "[Moses] said, ""That is [established] between me and you. Whichever of the two terms I complete - there is no injustice to me, and Allah, over what we say, is Witness", "قَالَ ذَٰلِكَ بَيۡنِي وَبَيۡنَكَۖ أَيَّمَا ٱلۡأَجَلَيۡنِ قَضَيۡتُ فَلَا عُدۡوَٰنَ عَلَيَّۖ وَٱللَّهُ عَلَىٰ مَا نَقُولُ وَكِيلٞ", "Qala thalika baynee wabaynaka ayyama alajalayni qadaytu fala AAudwana AAalayya waAllahu AAala ma naqoolu wakeelun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 29, "And when Moses had completed the term and was traveling with his family, he perceived from the direction of the mount a fire. He said to his family, ""Stay here; indeed, I have perceived a fire. Perhaps I will bring you from there [some] information or burning wood from the fire that you may warm yourselves", "۞فَلَمَّا قَضَىٰ مُوسَى ٱلۡأَجَلَ وَسَارَ بِأَهۡلِهِۦٓ ءَانَسَ مِن جَانِبِ ٱلطُّورِ نَارٗاۖ قَالَ لِأَهۡلِهِ ٱمۡكُثُوٓاْ إِنِّيٓ ءَانَسۡتُ نَارٗا لَّعَلِّيٓ ءَاتِيكُم مِّنۡهَا بِخَبَرٍ أَوۡ جَذۡوَةٖ مِّنَ ٱلنَّارِ لَعَلَّكُمۡ تَصۡطَلُونَ", "Falamma qada moosaalajala wasara biahlihi anasa min janibi alttoori naran qala liahlihi omkuthoo innee anastu naran laAAallee ateekum minha bikhabarin aw jathwatin mina alnnari laAAallakum tastaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 30, "But when he came to it, he was called from the right side of the valley in a blessed spot - from the tree, ""O Moses, indeed I am Allah, Lord of the worlds", "فَلَمَّآ أَتَىٰهَا نُودِيَ مِن شَٰطِيِٕ ٱلۡوَادِ ٱلۡأَيۡمَنِ فِي ٱلۡبُقۡعَةِ ٱلۡمُبَٰرَكَةِ مِنَ ٱلشَّجَرَةِ أَن يَٰمُوسَىٰٓ إِنِّيٓ أَنَا ٱللَّهُ رَبُّ ٱلۡعَٰلَمِينَ", "Falamma ataha noodiya min shatii alwadi alaymani fee albuqAAati almubarakati mina alshshajarati an ya moosa innee ana Allahu rabbu alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 31, "And [he was told], ""Throw down your staff."" But when he saw it writhing as if it was a snake, he turned in flight and did not return. [Allah said], ""O Moses, approach and fear not. Indeed, you are of the secure", "وَأَنۡ أَلۡقِ عَصَاكَۚ فَلَمَّا رَءَاهَا تَهۡتَزُّ كَأَنَّهَا جَآنّٞ وَلَّىٰ مُدۡبِرٗا وَلَمۡ يُعَقِّبۡۚ يَٰمُوسَىٰٓ أَقۡبِلۡ وَلَا تَخَفۡۖ إِنَّكَ مِنَ ٱلۡأٓمِنِينَ", "Waan alqi AAasaka falamma raaha tahtazzu kaannaha jannun walla mudbiran walam yuAAaqqib ya moosa aqbil wala takhaf innaka mina alamineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 32, "Insert your hand into the opening of your garment; it will come out white, without disease. And draw in your arm close to you [as prevention] from fear, for those are two proofs from your Lord to Pharaoh and his establishment. Indeed, they have been a people defiantly disobedient", "ٱسۡلُكۡ يَدَكَ فِي جَيۡبِكَ تَخۡرُجۡ بَيۡضَآءَ مِنۡ غَيۡرِ سُوٓءٖ وَٱضۡمُمۡ إِلَيۡكَ جَنَاحَكَ مِنَ ٱلرَّهۡبِۖ فَذَٰنِكَ بُرۡهَٰنَانِ مِن رَّبِّكَ إِلَىٰ فِرۡعَوۡنَ وَمَلَإِيْهِۦٓۚ إِنَّهُمۡ كَانُواْ قَوۡمٗا فَٰسِقِينَ", "Osluk yadaka fee jaybika takhruj baydaa min ghayri sooin waodmum ilayka janahaka mina alrrahbi fathanika burhanani min rabbika ila firAAawna wamalaihi innahum kanoo qawman fasiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 33, "He said, ""My Lord, indeed, I killed from among them someone, and I fear they will kill me", "قَالَ رَبِّ إِنِّي قَتَلۡتُ مِنۡهُمۡ نَفۡسٗا فَأَخَافُ أَن يَقۡتُلُونِ", "Qala rabbi innee qataltu minhum nafsan faakhafu an yaqtulooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 34, "And my brother Aaron is more fluent than me in tongue, so send him with me as support, verifying me. Indeed, I fear that they will deny me", "وَأَخِي هَٰرُونُ هُوَ أَفۡصَحُ مِنِّي لِسَانٗا فَأَرۡسِلۡهُ مَعِيَ رِدۡءٗا يُصَدِّقُنِيٓۖ إِنِّيٓ أَخَافُ أَن يُكَذِّبُونِ", "Waakhee haroonu huwa afsahu minnee lisanan faarsilhu maAAiya ridan yusaddiqunee innee akhafu an yukaththibooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 35, "[Allah] said, ""We will strengthen your arm through your brother and grant you both supremacy so they will not reach you. [It will be] through Our signs; you and those who follow you will be the predominant", "قَالَ سَنَشُدُّ عَضُدَكَ بِأَخِيكَ وَنَجۡعَلُ لَكُمَا سُلۡطَٰنٗا فَلَا يَصِلُونَ إِلَيۡكُمَا بِـَٔايَٰتِنَآۚ أَنتُمَا وَمَنِ ٱتَّبَعَكُمَا ٱلۡغَٰلِبُونَ", "Qala sanashuddu AAadudaka biakheeka wanajAAalu lakuma sultanan fala yasiloona ilaykuma biayatina antuma wamani ittabaAAakuma alghaliboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 36, "But when Moses came to them with Our signs as clear evidences, they said, ""This is not except invented magic, and we have not heard of this [religion] among our forefathers", "فَلَمَّا جَآءَهُم مُّوسَىٰ بِـَٔايَٰتِنَا بَيِّنَٰتٖ قَالُواْ مَا هَٰذَآ إِلَّا سِحۡرٞ مُّفۡتَرٗى وَمَا سَمِعۡنَا بِهَٰذَا فِيٓ ءَابَآئِنَا ٱلۡأَوَّلِينَ", "Falamma jaahum moosa biayatina bayyinatin qaloo ma hatha illa sihrun muftaran wama samiAAna bihatha fee abaina alawwaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 37, "And Moses said, ""My Lord is more knowing [than we or you] of who has come with guidance from Him and to whom will be succession in the home. Indeed, wrongdoers do not succeed", "وَقَالَ مُوسَىٰ رَبِّيٓ أَعۡلَمُ بِمَن جَآءَ بِٱلۡهُدَىٰ مِنۡ عِندِهِۦ وَمَن تَكُونُ لَهُۥ عَٰقِبَةُ ٱلدَّارِۚ إِنَّهُۥ لَا يُفۡلِحُ ٱلظَّـٰلِمُونَ", "Waqala moosa rabbee aAAlamu biman jaa bialhuda min AAindihi waman takoonu lahu AAaqibatu alddari innahu la yuflihu alththalimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 38, "And Pharaoh said, ""O eminent ones, I have not known you to have a god other than me. Then ignite for me, O Haman, [a fire] upon the clay and make for me a tower that I may look at the God of Moses. And indeed, I do think he is among the liars", "وَقَالَ فِرۡعَوۡنُ يَـٰٓأَيُّهَا ٱلۡمَلَأُ مَا عَلِمۡتُ لَكُم مِّنۡ إِلَٰهٍ غَيۡرِي فَأَوۡقِدۡ لِي يَٰهَٰمَٰنُ عَلَى ٱلطِّينِ فَٱجۡعَل لِّي صَرۡحٗا لَّعَلِّيٓ أَطَّلِعُ إِلَىٰٓ إِلَٰهِ مُوسَىٰ وَإِنِّي لَأَظُنُّهُۥ مِنَ ٱلۡكَٰذِبِينَ", "Waqala firAAawnu ya ayyuha almalao ma AAalimtu lakum min ilahin ghayree faawqid lee ya hamanu AAala altteeni faijAAal lee sarhan laAAallee attaliAAu ila ilahi moosa wainnee laathunnuhu mina alkathibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 39, "And he was arrogant, he and his soldiers, in the land, without right, and they thought that they would not be returned to Us", "وَٱسۡتَكۡبَرَ هُوَ وَجُنُودُهُۥ فِي ٱلۡأَرۡضِ بِغَيۡرِ ٱلۡحَقِّ وَظَنُّوٓاْ أَنَّهُمۡ إِلَيۡنَا لَا يُرۡجَعُونَ", "Waistakbara huwa wajunooduhu fee alardi bighayri alhaqqi wathannoo annahum ilayna la yurjaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 40, "So We took him and his soldiers and threw them into the sea. So see how was the end of the wrongdoers", "فَأَخَذۡنَٰهُ وَجُنُودَهُۥ فَنَبَذۡنَٰهُمۡ فِي ٱلۡيَمِّۖ فَٱنظُرۡ كَيۡفَ كَانَ عَٰقِبَةُ ٱلظَّـٰلِمِينَ", "Faakhathnahu wajunoodahu fanabathnahum fee alyammi faonthur kayfa kana AAaqibatu alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 41, "And We made them leaders inviting to the Fire, and on the Day of Resurrection they will not be helped", "وَجَعَلۡنَٰهُمۡ أَئِمَّةٗ يَدۡعُونَ إِلَى ٱلنَّارِۖ وَيَوۡمَ ٱلۡقِيَٰمَةِ لَا يُنصَرُونَ", "WajaAAalnahum aimmatan yadAAoona ila alnnari wayawma alqiyamati la yunsaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 42, "And We caused to overtake them in this world a curse, and on the Day of Resurrection they will be of the despised", "وَأَتۡبَعۡنَٰهُمۡ فِي هَٰذِهِ ٱلدُّنۡيَا لَعۡنَةٗۖ وَيَوۡمَ ٱلۡقِيَٰمَةِ هُم مِّنَ ٱلۡمَقۡبُوحِينَ", "WaatbaAAnahum fee hathihi alddunya laAAnatan wayawma alqiyamati hum mina almaqbooheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 43, "And We gave Moses the Scripture, after We had destroyed the former generations, as enlightenment for the people and guidance and mercy that they might be reminded", "وَلَقَدۡ ءَاتَيۡنَا مُوسَى ٱلۡكِتَٰبَ مِنۢ بَعۡدِ مَآ أَهۡلَكۡنَا ٱلۡقُرُونَ ٱلۡأُولَىٰ بَصَآئِرَ لِلنَّاسِ وَهُدٗى وَرَحۡمَةٗ لَّعَلَّهُمۡ يَتَذَكَّرُونَ", "Walaqad atayna moosa alkitaba min baAAdi ma ahlakna alquroona aloola basaira lilnnasi wahudan warahmatan laAAallahum yatathakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 44, "And you, [O Muhammad], were not on the western side [of the mount] when We revealed to Moses the command, and you were not among the witnesses [to that]", "وَمَا كُنتَ بِجَانِبِ ٱلۡغَرۡبِيِّ إِذۡ قَضَيۡنَآ إِلَىٰ مُوسَى ٱلۡأَمۡرَ وَمَا كُنتَ مِنَ ٱلشَّـٰهِدِينَ", "Wama kunta bijanibi algharbiyyi ith qadayna ila moosa alamra wama kunta mina alshshahideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 45, "But We produced [many] generations [after Moses], and prolonged was their duration. And you were not a resident among the people of Madyan, reciting to them Our verses, but We were senders [of this message]", "وَلَٰكِنَّآ أَنشَأۡنَا قُرُونٗا فَتَطَاوَلَ عَلَيۡهِمُ ٱلۡعُمُرُۚ وَمَا كُنتَ ثَاوِيٗا فِيٓ أَهۡلِ مَدۡيَنَ تَتۡلُواْ عَلَيۡهِمۡ ءَايَٰتِنَا وَلَٰكِنَّا كُنَّا مُرۡسِلِينَ", "Walakinna anshana quroonan fatatawala AAalayhimu alAAumuru wama kunta thawiyan fee ahli madyana tatloo AAalayhim ayatina walakinna kunna mursileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 46, "And you were not at the side of the mount when We called [Moses] but [were sent] as a mercy from your Lord to warn a people to whom no warner had come before you that they might be reminded", "وَمَا كُنتَ بِجَانِبِ ٱلطُّورِ إِذۡ نَادَيۡنَا وَلَٰكِن رَّحۡمَةٗ مِّن رَّبِّكَ لِتُنذِرَ قَوۡمٗا مَّآ أَتَىٰهُم مِّن نَّذِيرٖ مِّن قَبۡلِكَ لَعَلَّهُمۡ يَتَذَكَّرُونَ", "Wama kunta bijanibi alttoori ith nadayna walakin rahmatan min rabbika litunthira qawman ma atahum min natheerin min qablika laAAallahum yatathakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 47, "And if not that a disaster should strike them for what their hands put forth [of sins] and they would say, ""Our Lord, why did You not send us a messenger so we could have followed Your verses and been among the believers", "وَلَوۡلَآ أَن تُصِيبَهُم مُّصِيبَةُۢ بِمَا قَدَّمَتۡ أَيۡدِيهِمۡ فَيَقُولُواْ رَبَّنَا لَوۡلَآ أَرۡسَلۡتَ إِلَيۡنَا رَسُولٗا فَنَتَّبِعَ ءَايَٰتِكَ وَنَكُونَ مِنَ ٱلۡمُؤۡمِنِينَ", "Walawla an tuseebahum museebatun bima qaddamat aydeehim fayaqooloo rabbana lawla arsalta ilayna rasoolan fanattabiAAa ayatika wanakoona mina almumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 48, "But when the truth came to them from Us, they said, ""Why was he not given like that which was given to Moses?"" Did they not disbelieve in that which was given to Moses before? They said, ""[They are but] two works of magic supporting each other, and indeed we are, in both, disbelievers", "فَلَمَّا جَآءَهُمُ ٱلۡحَقُّ مِنۡ عِندِنَا قَالُواْ لَوۡلَآ أُوتِيَ مِثۡلَ مَآ أُوتِيَ مُوسَىٰٓۚ أَوَلَمۡ يَكۡفُرُواْ بِمَآ أُوتِيَ مُوسَىٰ مِن قَبۡلُۖ قَالُواْ سِحۡرَانِ تَظَٰهَرَا وَقَالُوٓاْ إِنَّا بِكُلّٖ كَٰفِرُونَ", "Falamma jaahumu alhaqqu min AAindina qaloo lawla ootiya mithla ma ootiya moosa awalam yakfuroo bima ootiya moosa min qablu qaloo sihrani tathahara waqaloo inna bikullin kafiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 49, "Say, ""Then bring a scripture from Allah which is more guiding than either of them that I may follow it, if you should be truthful", "قُلۡ فَأۡتُواْ بِكِتَٰبٖ مِّنۡ عِندِ ٱللَّهِ هُوَ أَهۡدَىٰ مِنۡهُمَآ أَتَّبِعۡهُ إِن كُنتُمۡ صَٰدِقِينَ", "Qul fatoo bikitabin min AAindi Allahi huwa ahda minhuma attabiAAhu in kuntum sadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 50, "But if they do not respond to you - then know that they only follow their [own] desires. And who is more astray than one who follows his desire without guidance from Allah? Indeed, Allah does not guide the wrongdoing people", "فَإِن لَّمۡ يَسۡتَجِيبُواْ لَكَ فَٱعۡلَمۡ أَنَّمَا يَتَّبِعُونَ أَهۡوَآءَهُمۡۚ وَمَنۡ أَضَلُّ مِمَّنِ ٱتَّبَعَ هَوَىٰهُ بِغَيۡرِ هُدٗى مِّنَ ٱللَّهِۚ إِنَّ ٱللَّهَ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلظَّـٰلِمِينَ", "Fain lam yastajeeboo laka faiAAlam annama yattabiAAoona ahwaahum waman adallu mimmani ittabaAAa hawahu bighayri hudan mina Allahi inna Allaha la yahdee alqawma alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 51, "And We have [repeatedly] conveyed to them the Qur'an that they might be reminded", "۞وَلَقَدۡ وَصَّلۡنَا لَهُمُ ٱلۡقَوۡلَ لَعَلَّهُمۡ يَتَذَكَّرُونَ", "Walaqad wassalna lahumu alqawla laAAallahum yatathakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 52, "Those to whom We gave the Scripture before it - they are believers in it", "ٱلَّذِينَ ءَاتَيۡنَٰهُمُ ٱلۡكِتَٰبَ مِن قَبۡلِهِۦ هُم بِهِۦ يُؤۡمِنُونَ", "Allatheena ataynahumu alkitaba min qablihi hum bihi yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 53, "And when it is recited to them, they say, ""We have believed in it; indeed, it is the truth from our Lord. Indeed we were, [even] before it, Muslims [submitting to Allah]", "وَإِذَا يُتۡلَىٰ عَلَيۡهِمۡ قَالُوٓاْ ءَامَنَّا بِهِۦٓ إِنَّهُ ٱلۡحَقُّ مِن رَّبِّنَآ إِنَّا كُنَّا مِن قَبۡلِهِۦ مُسۡلِمِينَ", "Waitha yutla AAalayhim qaloo amanna bihi innahu alhaqqu min rabbina inna kunna min qablihi muslimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 54, "Those will be given their reward twice for what they patiently endured and [because] they avert evil through good, and from what We have provided them they spend", "أُوْلَـٰٓئِكَ يُؤۡتَوۡنَ أَجۡرَهُم مَّرَّتَيۡنِ بِمَا صَبَرُواْ وَيَدۡرَءُونَ بِٱلۡحَسَنَةِ ٱلسَّيِّئَةَ وَمِمَّا رَزَقۡنَٰهُمۡ يُنفِقُونَ", "Olaika yutawna ajrahum marratayni bima sabaroo wayadraoona bialhasanati alssayyiata wamimma razaqnahum yunfiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 55, "And when they hear ill speech, they turn away from it and say, ""For us are our deeds, and for you are your deeds. Peace will be upon you; we seek not the ignorant", "وَإِذَا سَمِعُواْ ٱللَّغۡوَ أَعۡرَضُواْ عَنۡهُ وَقَالُواْ لَنَآ أَعۡمَٰلُنَا وَلَكُمۡ أَعۡمَٰلُكُمۡ سَلَٰمٌ عَلَيۡكُمۡ لَا نَبۡتَغِي ٱلۡجَٰهِلِينَ", "Waitha samiAAoo allaghwa aAAradoo AAanhu waqaloo lana aAAmaluna walakum aAAmalukum salamun AAalaykum la nabtaghee aljahileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 56, "Indeed, [O Muhammad], you do not guide whom you like, but Allah guides whom He wills. And He is most knowing of the [rightly] guided", "إِنَّكَ لَا تَهۡدِي مَنۡ أَحۡبَبۡتَ وَلَٰكِنَّ ٱللَّهَ يَهۡدِي مَن يَشَآءُۚ وَهُوَ أَعۡلَمُ بِٱلۡمُهۡتَدِينَ", "Innaka la tahdee man ahbabta walakinna Allaha yahdee man yashao wahuwa aAAlamu bialmuhtadeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 57, "And they say, ""If we were to follow the guidance with you, we would be swept from our land."" Have we not established for them a safe sanctuary to which are brought the fruits of all things as provision from Us? But most of them do not know", "وَقَالُوٓاْ إِن نَّتَّبِعِ ٱلۡهُدَىٰ مَعَكَ نُتَخَطَّفۡ مِنۡ أَرۡضِنَآۚ أَوَلَمۡ نُمَكِّن لَّهُمۡ حَرَمًا ءَامِنٗا يُجۡبَىٰٓ إِلَيۡهِ ثَمَرَٰتُ كُلِّ شَيۡءٖ رِّزۡقٗا مِّن لَّدُنَّا وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَعۡلَمُونَ", "Waqaloo in nattabiAAi alhuda maAAaka nutakhattaf min ardina awalam numakkin lahum haraman aminan yujba ilayhi thamaratu kulli shayin rizqan min ladunna walakinna aktharahum la yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 58, "And how many a city have We destroyed that was insolent in its [way of] living, and those are their dwellings which have not been inhabited after them except briefly. And it is We who were the inheritors", "وَكَمۡ أَهۡلَكۡنَا مِن قَرۡيَةِۭ بَطِرَتۡ مَعِيشَتَهَاۖ فَتِلۡكَ مَسَٰكِنُهُمۡ لَمۡ تُسۡكَن مِّنۢ بَعۡدِهِمۡ إِلَّا قَلِيلٗاۖ وَكُنَّا نَحۡنُ ٱلۡوَٰرِثِينَ", "Wakam ahlakna min qaryatin batirat maAAeeshataha fatilka masakinuhum lam tuskan min baAAdihim illa qaleelan wakunna nahnu alwaritheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 59, "And never would your Lord have destroyed the cities until He had sent to their mother a messenger reciting to them Our verses. And We would not destroy the cities except while their people were wrongdoers", "وَمَا كَانَ رَبُّكَ مُهۡلِكَ ٱلۡقُرَىٰ حَتَّىٰ يَبۡعَثَ فِيٓ أُمِّهَا رَسُولٗا يَتۡلُواْ عَلَيۡهِمۡ ءَايَٰتِنَاۚ وَمَا كُنَّا مُهۡلِكِي ٱلۡقُرَىٰٓ إِلَّا وَأَهۡلُهَا ظَٰلِمُونَ", "Wama kana rabbuka muhlika alqura hatta yabAAatha fee ommiha rasoolan yatloo AAalayhim ayatina wama kunna muhlikee alqura illa waahluha thalimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 60, "And whatever thing you [people] have been given - it is [only for] the enjoyment of worldly life and its adornment. And what is with Allah is better and more lasting; so will you not use reason", "وَمَآ أُوتِيتُم مِّن شَيۡءٖ فَمَتَٰعُ ٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَزِينَتُهَاۚ وَمَا عِندَ ٱللَّهِ خَيۡرٞ وَأَبۡقَىٰٓۚ أَفَلَا تَعۡقِلُونَ", "Wama ooteetum min shayin famataAAu alhayati alddunya wazeenatuha wama AAinda Allahi khayrun waabqa afala taAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 61, "Then is he whom We have promised a good promise which he will obtain like he for whom We provided enjoyment of worldly life [but] then he is, on the Day of Resurrection, among those presented [for punishment in Hell]", "أَفَمَن وَعَدۡنَٰهُ وَعۡدًا حَسَنٗا فَهُوَ لَٰقِيهِ كَمَن مَّتَّعۡنَٰهُ مَتَٰعَ ٱلۡحَيَوٰةِ ٱلدُّنۡيَا ثُمَّ هُوَ يَوۡمَ ٱلۡقِيَٰمَةِ مِنَ ٱلۡمُحۡضَرِينَ", "Afaman waAAadnahu waAAdan hasanan fahuwa laqeehi kaman mattaAAnahu mataAAa alhayati alddunya thumma huwa yawma alqiyamati mina almuhdareena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 62, "And [warn of] the Day He will call them and say, ""Where are My 'partners' which you used to claim", "وَيَوۡمَ يُنَادِيهِمۡ فَيَقُولُ أَيۡنَ شُرَكَآءِيَ ٱلَّذِينَ كُنتُمۡ تَزۡعُمُونَ", "Wayawma yunadeehim fayaqoolu ayna shurakaiya allatheena kuntum tazAAumoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 63, "Those upon whom the word will have come into effect will say, ""Our Lord, these are the ones we led to error. We led them to error just as we were in error. We declare our disassociation [from them] to You. They did not used to worship us", "قَالَ ٱلَّذِينَ حَقَّ عَلَيۡهِمُ ٱلۡقَوۡلُ رَبَّنَا هَـٰٓؤُلَآءِ ٱلَّذِينَ أَغۡوَيۡنَآ أَغۡوَيۡنَٰهُمۡ كَمَا غَوَيۡنَاۖ تَبَرَّأۡنَآ إِلَيۡكَۖ مَا كَانُوٓاْ إِيَّانَا يَعۡبُدُونَ", "Qala allatheena haqqa AAalayhimu alqawlu rabbana haolai allatheena aghwayna aghwaynahum kama ghawayna tabarrana ilayka ma kanoo iyyana yaAAbudoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 64, "And it will be said, ""Invoke your 'partners' "" and they will invoke them; but they will not respond to them, and they will see the punishment. If only they had followed guidance", "وَقِيلَ ٱدۡعُواْ شُرَكَآءَكُمۡ فَدَعَوۡهُمۡ فَلَمۡ يَسۡتَجِيبُواْ لَهُمۡ وَرَأَوُاْ ٱلۡعَذَابَۚ لَوۡ أَنَّهُمۡ كَانُواْ يَهۡتَدُونَ", "Waqeela odAAoo shurakaakum fadaAAawhum falam yastajeeboo lahum waraawoo alAAathaba law annahum kanoo yahtadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 65, "And [mention] the Day He will call them and say, ""What did you answer the messengers", "وَيَوۡمَ يُنَادِيهِمۡ فَيَقُولُ مَاذَآ أَجَبۡتُمُ ٱلۡمُرۡسَلِينَ", "Wayawma yunadeehim fayaqoolu matha ajabtumu almursaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 66, "But the information will be unapparent to them that Day, so they will not [be able to] ask one another", "فَعَمِيَتۡ عَلَيۡهِمُ ٱلۡأَنۢبَآءُ يَوۡمَئِذٖ فَهُمۡ لَا يَتَسَآءَلُونَ", "FaAAamiyat AAalayhimu alanbao yawmaithin fahum la yatasaaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 67, "But as for one who had repented, believed, and done righteousness, it is promised by Allah that he will be among the successful", "فَأَمَّا مَن تَابَ وَءَامَنَ وَعَمِلَ صَٰلِحٗا فَعَسَىٰٓ أَن يَكُونَ مِنَ ٱلۡمُفۡلِحِينَ", "Faamma man taba waamana waAAamila salihan faAAasa an yakoona mina almufliheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 68, "And your Lord creates what He wills and chooses; not for them was the choice. Exalted is Allah and high above what they associate with Him", "وَرَبُّكَ يَخۡلُقُ مَا يَشَآءُ وَيَخۡتَارُۗ مَا كَانَ لَهُمُ ٱلۡخِيَرَةُۚ سُبۡحَٰنَ ٱللَّهِ وَتَعَٰلَىٰ عَمَّا يُشۡرِكُونَ", "Warabbuka yakhluqu ma yashao wayakhtaru ma kana lahumu alkhiyaratu subhana Allahi wataAAala AAamma yushrikoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 69, "And your Lord knows what their breasts conceal and what they declare", "وَرَبُّكَ يَعۡلَمُ مَا تُكِنُّ صُدُورُهُمۡ وَمَا يُعۡلِنُونَ", "Warabbuka yaAAlamu ma tukinnu sudooruhum wama yuAAlinoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 70, "And He is Allah; there is no deity except Him. To Him is [due all] praise in the first [life] and the Hereafter. And His is the [final] decision, and to Him you will be returned", "وَهُوَ ٱللَّهُ لَآ إِلَٰهَ إِلَّا هُوَۖ لَهُ ٱلۡحَمۡدُ فِي ٱلۡأُولَىٰ وَٱلۡأٓخِرَةِۖ وَلَهُ ٱلۡحُكۡمُ وَإِلَيۡهِ تُرۡجَعُونَ", "Wahuwa Allahu la ilaha illa huwa lahu alhamdu fee aloola waalakhirati walahu alhukmu wailayhi turjaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 71, "Say, ""Have you considered: if Allah should make for you the night continuous until the Day of Resurrection, what deity other than Allah could bring you light? Then will you not hear", "قُلۡ أَرَءَيۡتُمۡ إِن جَعَلَ ٱللَّهُ عَلَيۡكُمُ ٱلَّيۡلَ سَرۡمَدًا إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِ مَنۡ إِلَٰهٌ غَيۡرُ ٱللَّهِ يَأۡتِيكُم بِضِيَآءٍۚ أَفَلَا تَسۡمَعُونَ", "Qul araaytum in jaAAala Allahu AAalaykumu allayla sarmadan ila yawmi alqiyamati man ilahun ghayru Allahi yateekum bidiyain afala tasmaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 72, "Say, ""Have you considered: if Allah should make for you the day continuous until the Day of Resurrection, what deity other than Allah could bring you a night in which you may rest? Then will you not see", "قُلۡ أَرَءَيۡتُمۡ إِن جَعَلَ ٱللَّهُ عَلَيۡكُمُ ٱلنَّهَارَ سَرۡمَدًا إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِ مَنۡ إِلَٰهٌ غَيۡرُ ٱللَّهِ يَأۡتِيكُم بِلَيۡلٖ تَسۡكُنُونَ فِيهِۚ أَفَلَا تُبۡصِرُونَ", "Qul araaytum in jaAAala Allahu AAalaykumu alnnahara sarmadan ila yawmi alqiyamati man ilahun ghayru Allahi yateekum bilaylin taskunoona feehi afala tubsiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 73, "And out of His mercy He made for you the night and the day that you may rest therein and [by day] seek from His bounty and [that] perhaps you will be grateful", "وَمِن رَّحۡمَتِهِۦ جَعَلَ لَكُمُ ٱلَّيۡلَ وَٱلنَّهَارَ لِتَسۡكُنُواْ فِيهِ وَلِتَبۡتَغُواْ مِن فَضۡلِهِۦ وَلَعَلَّكُمۡ تَشۡكُرُونَ", "Wamin rahmatihi jaAAala lakumu allayla waalnnahara litaskunoo feehi walitabtaghoo min fadlihi walaAAallakum tashkuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 74, "And [warn of] the Day He will call them and say, ""Where are my 'partners' which you used to claim", "وَيَوۡمَ يُنَادِيهِمۡ فَيَقُولُ أَيۡنَ شُرَكَآءِيَ ٱلَّذِينَ كُنتُمۡ تَزۡعُمُونَ", "Wayawma yunadeehim fayaqoolu ayna shurakaiya allatheena kuntum tazAAumoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 75, "And We will extract from every nation a witness and say, ""Produce your proof,"" and they will know that the truth belongs to Allah, and lost from them is that which they used to invent", "وَنَزَعۡنَا مِن كُلِّ أُمَّةٖ شَهِيدٗا فَقُلۡنَا هَاتُواْ بُرۡهَٰنَكُمۡ فَعَلِمُوٓاْ أَنَّ ٱلۡحَقَّ لِلَّهِ وَضَلَّ عَنۡهُم مَّا كَانُواْ يَفۡتَرُونَ", "WanazaAAna min kulli ommatin shaheedan faqulna hatoo burhanakum faAAalimoo anna alhaqqa lillahi wadalla AAanhum ma kanoo yaftaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 76, "Indeed, Qarun was from the people of Moses, but he tyrannized them. And We gave him of treasures whose keys would burden a band of strong men; thereupon his people said to him, ""Do not exult. Indeed, Allah does not like the exultant", "۞إِنَّ قَٰرُونَ كَانَ مِن قَوۡمِ مُوسَىٰ فَبَغَىٰ عَلَيۡهِمۡۖ وَءَاتَيۡنَٰهُ مِنَ ٱلۡكُنُوزِ مَآ إِنَّ مَفَاتِحَهُۥ لَتَنُوٓأُ بِٱلۡعُصۡبَةِ أُوْلِي ٱلۡقُوَّةِ إِذۡ قَالَ لَهُۥ قَوۡمُهُۥ لَا تَفۡرَحۡۖ إِنَّ ٱللَّهَ لَا يُحِبُّ ٱلۡفَرِحِينَ", "Inna qaroona kana min qawmi moosa fabagha AAalayhim waataynahu mina alkunoozi ma inna mafatihahu latanooo bialAAusbati olee alquwwati ith qala lahu qawmuhu la tafrah inna Allaha la yuhibbu alfariheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 77, "But seek, through that which Allah has given you, the home of the Hereafter; and [yet], do not forget your share of the world. And do good as Allah has done good to you. And desire not corruption in the land. Indeed, Allah does not like corrupters", "وَٱبۡتَغِ فِيمَآ ءَاتَىٰكَ ٱللَّهُ ٱلدَّارَ ٱلۡأٓخِرَةَۖ وَلَا تَنسَ نَصِيبَكَ مِنَ ٱلدُّنۡيَاۖ وَأَحۡسِن كَمَآ أَحۡسَنَ ٱللَّهُ إِلَيۡكَۖ وَلَا تَبۡغِ ٱلۡفَسَادَ فِي ٱلۡأَرۡضِۖ إِنَّ ٱللَّهَ لَا يُحِبُّ ٱلۡمُفۡسِدِينَ", "Waibtaghi feema ataka Allahu alddara alakhirata wala tansa naseebaka mina alddunya waahsin kama ahsana Allahu ilayka wala tabghi alfasada fee alardi inna Allaha la yuhibbu almufsideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 78, "He said, ""I was only given it because of knowledge I have."" Did he not know that Allah had destroyed before him of generations those who were greater than him in power and greater in accumulation [of wealth]? But the criminals, about their sins, will not be asked", "قَالَ إِنَّمَآ أُوتِيتُهُۥ عَلَىٰ عِلۡمٍ عِندِيٓۚ أَوَلَمۡ يَعۡلَمۡ أَنَّ ٱللَّهَ قَدۡ أَهۡلَكَ مِن قَبۡلِهِۦ مِنَ ٱلۡقُرُونِ مَنۡ هُوَ أَشَدُّ مِنۡهُ قُوَّةٗ وَأَكۡثَرُ جَمۡعٗاۚ وَلَا يُسۡـَٔلُ عَن ذُنُوبِهِمُ ٱلۡمُجۡرِمُونَ", "Qala innama ooteetuhu AAala AAilmin AAindee awalam yaAAlam anna Allaha qad ahlaka min qablihi mina alqurooni man huwa ashaddu minhu quwwatan waaktharu jamAAan wala yusalu AAan thunoobihimu almujrimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 79, "So he came out before his people in his adornment. Those who desired the worldly life said, ""Oh, would that we had like what was given to Qarun. Indeed, he is one of great fortune", "فَخَرَجَ عَلَىٰ قَوۡمِهِۦ فِي زِينَتِهِۦۖ قَالَ ٱلَّذِينَ يُرِيدُونَ ٱلۡحَيَوٰةَ ٱلدُّنۡيَا يَٰلَيۡتَ لَنَا مِثۡلَ مَآ أُوتِيَ قَٰرُونُ إِنَّهُۥ لَذُو حَظٍّ عَظِيمٖ", "Fakharaja AAala qawmihi fee zeenatihi qala allatheena yureedoona alhayata alddunya ya layta lana mithla ma ootiya qaroonu innahu lathoo haththin AAatheemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 80, "But those who had been given knowledge said, ""Woe to you! The reward of Allah is better for he who believes and does righteousness. And none are granted it except the patient", "وَقَالَ ٱلَّذِينَ أُوتُواْ ٱلۡعِلۡمَ وَيۡلَكُمۡ ثَوَابُ ٱللَّهِ خَيۡرٞ لِّمَنۡ ءَامَنَ وَعَمِلَ صَٰلِحٗاۚ وَلَا يُلَقَّىٰهَآ إِلَّا ٱلصَّـٰبِرُونَ", "Waqala allatheena ootoo alAAilma waylakum thawabu Allahi khayrun liman amana waAAamila salihan wala yulaqqaha illa alssabiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 81, "And We caused the earth to swallow him and his home. And there was for him no company to aid him other than Allah, nor was he of those who [could] defend themselves", "فَخَسَفۡنَا بِهِۦ وَبِدَارِهِ ٱلۡأَرۡضَ فَمَا كَانَ لَهُۥ مِن فِئَةٖ يَنصُرُونَهُۥ مِن دُونِ ٱللَّهِ وَمَا كَانَ مِنَ ٱلۡمُنتَصِرِينَ", "Fakhasafna bihi wabidarihi alarda fama kana lahu min fiatin yansuroonahu min dooni Allahi wama kana mina almuntasireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 82, "And those who had wished for his position the previous day began to say, ""Oh, how Allah extends provision to whom He wills of His servants and restricts it! If not that Allah had conferred favor on us, He would have caused it to swallow us. Oh, how the disbelievers do not succeed", "وَأَصۡبَحَ ٱلَّذِينَ تَمَنَّوۡاْ مَكَانَهُۥ بِٱلۡأَمۡسِ يَقُولُونَ وَيۡكَأَنَّ ٱللَّهَ يَبۡسُطُ ٱلرِّزۡقَ لِمَن يَشَآءُ مِنۡ عِبَادِهِۦ وَيَقۡدِرُۖ لَوۡلَآ أَن مَّنَّ ٱللَّهُ عَلَيۡنَا لَخَسَفَ بِنَاۖ وَيۡكَأَنَّهُۥ لَا يُفۡلِحُ ٱلۡكَٰفِرُونَ", "Waasbaha allatheena tamannaw makanahu bialamsi yaqooloona waykaanna Allaha yabsutu alrrizqa liman yashao min AAibadihi wayaqdiru lawla an manna Allahu AAalayna lakhasafa bina waykaannahu la yuflihu alkafiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 83, "That home of the Hereafter We assign to those who do not desire exaltedness upon the earth or corruption. And the [best] outcome is for the righteous", "تِلۡكَ ٱلدَّارُ ٱلۡأٓخِرَةُ نَجۡعَلُهَا لِلَّذِينَ لَا يُرِيدُونَ عُلُوّٗا فِي ٱلۡأَرۡضِ وَلَا فَسَادٗاۚ وَٱلۡعَٰقِبَةُ لِلۡمُتَّقِينَ", "Tilka alddaru alakhiratu najAAaluha lillatheena la yureedoona AAuluwwan fee alardi wala fasadan waalAAaqibatu lilmuttaqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 84, "Whoever comes [on the Day of Judgement] with a good deed will have better than it; and whoever comes with an evil deed - then those who did evil deeds will not be recompensed except [as much as] what they used to do", "مَن جَآءَ بِٱلۡحَسَنَةِ فَلَهُۥ خَيۡرٞ مِّنۡهَاۖ وَمَن جَآءَ بِٱلسَّيِّئَةِ فَلَا يُجۡزَى ٱلَّذِينَ عَمِلُواْ ٱلسَّيِّـَٔاتِ إِلَّا مَا كَانُواْ يَعۡمَلُونَ", "Man jaa bialhasanati falahu khayrun minha waman jaa bialssayyiati fala yujza allatheena AAamiloo alssayyiati illa ma kanoo yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 85, "Indeed, [O Muhammad], He who imposed upon you the Qur'an will take you back to a place of return. Say, ""My Lord is most knowing of who brings guidance and who is in clear error", "إِنَّ ٱلَّذِي فَرَضَ عَلَيۡكَ ٱلۡقُرۡءَانَ لَرَآدُّكَ إِلَىٰ مَعَادٖۚ قُل رَّبِّيٓ أَعۡلَمُ مَن جَآءَ بِٱلۡهُدَىٰ وَمَنۡ هُوَ فِي ضَلَٰلٖ مُّبِينٖ", "Inna allathee farada AAalayka alqurana laradduka ila maAAadin qul rabbee aAAlamu man jaa bialhuda waman huwa fee dalalin mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 86, "And you were not expecting that the Book would be conveyed to you, but [it is] a mercy from your Lord. So do not be an assistant to the disbelievers", "وَمَا كُنتَ تَرۡجُوٓاْ أَن يُلۡقَىٰٓ إِلَيۡكَ ٱلۡكِتَٰبُ إِلَّا رَحۡمَةٗ مِّن رَّبِّكَۖ فَلَا تَكُونَنَّ ظَهِيرٗا لِّلۡكَٰفِرِينَ", "Wama kunta tarjoo an yulqa ilayka alkitabu illa rahmatan min rabbika fala takoonanna thaheeran lilkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 87, "And never let them avert you from the verses of Allah after they have been revealed to you. And invite [people] to your Lord. And never be of those who associate others with Allah", "وَلَا يَصُدُّنَّكَ عَنۡ ءَايَٰتِ ٱللَّهِ بَعۡدَ إِذۡ أُنزِلَتۡ إِلَيۡكَۖ وَٱدۡعُ إِلَىٰ رَبِّكَۖ وَلَا تَكُونَنَّ مِنَ ٱلۡمُشۡرِكِينَ", "Wala yasuddunnaka AAan ayati Allahi baAAda ith onzilat ilayka waodAAu ila rabbika wala takoonanna mina almushrikeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (28, 88, "And do not invoke with Allah another deity. There is no deity except Him. Everything will be destroyed except His Face. His is the judgement, and to Him you will be returned", "وَلَا تَدۡعُ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَۘ لَآ إِلَٰهَ إِلَّا هُوَۚ كُلُّ شَيۡءٍ هَالِكٌ إِلَّا وَجۡهَهُۥۚ لَهُ ٱلۡحُكۡمُ وَإِلَيۡهِ تُرۡجَعُونَ", "Wala tadAAu maAAa Allahi ilahan akhara la ilaha illa huwa kullu shayin halikun illa wajhahu lahu alhukmu wailayhi turjaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 1, "Alif, Lam, Meem", "الٓمٓ", "Aliflammeem");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 2, "Do the people think that they will be left to say, ""We believe"" and they will not be tried", "أَحَسِبَ ٱلنَّاسُ أَن يُتۡرَكُوٓاْ أَن يَقُولُوٓاْ ءَامَنَّا وَهُمۡ لَا يُفۡتَنُونَ", "Ahasiba alnnasu an yutrakoo an yaqooloo amanna wahum la yuftanoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 3, "But We have certainly tried those before them, and Allah will surely make evident those who are truthful, and He will surely make evident the liars", "وَلَقَدۡ فَتَنَّا ٱلَّذِينَ مِن قَبۡلِهِمۡۖ فَلَيَعۡلَمَنَّ ٱللَّهُ ٱلَّذِينَ صَدَقُواْ وَلَيَعۡلَمَنَّ ٱلۡكَٰذِبِينَ", "Walaqad fatanna allatheena min qablihim falayaAAlamanna Allahu allatheena sadaqoo walayaAAlamanna alkathibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 4, "Or do those who do evil deeds think they can outrun Us? Evil is what they judge", "أَمۡ حَسِبَ ٱلَّذِينَ يَعۡمَلُونَ ٱلسَّيِّـَٔاتِ أَن يَسۡبِقُونَاۚ سَآءَ مَا يَحۡكُمُونَ", "Am hasiba allatheena yaAAmaloona alssayyiati an yasbiqoona saa ma yahkumoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 5, "Whoever should hope for the meeting with Allah - indeed, the term decreed by Allah is coming. And He is the Hearing, the Knowing", "مَن كَانَ يَرۡجُواْ لِقَآءَ ٱللَّهِ فَإِنَّ أَجَلَ ٱللَّهِ لَأٓتٖۚ وَهُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ", "Man kana yarjoo liqaa Allahi fainna ajala Allahi laatin wahuwa alssameeAAu alAAaleemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 6, "And whoever strives only strives for [the benefit of] himself. Indeed, Allah is free from need of the worlds", "وَمَن جَٰهَدَ فَإِنَّمَا يُجَٰهِدُ لِنَفۡسِهِۦٓۚ إِنَّ ٱللَّهَ لَغَنِيٌّ عَنِ ٱلۡعَٰلَمِينَ", "Waman jahada fainnama yujahidu linafsihi inna Allaha laghaniyyun AAani alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 7, "And those who believe and do righteous deeds - We will surely remove from them their misdeeds and will surely reward them according to the best of what they used to do", "وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ لَنُكَفِّرَنَّ عَنۡهُمۡ سَيِّـَٔاتِهِمۡ وَلَنَجۡزِيَنَّهُمۡ أَحۡسَنَ ٱلَّذِي كَانُواْ يَعۡمَلُونَ", "Waallatheena amanoo waAAamiloo alssalihati lanukaffiranna AAanhum sayyiatihim walanajziyannahum ahsana allathee kanoo yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 8, "And We have enjoined upon man goodness to parents. But if they endeavor to make you associate with Me that of which you have no knowledge, do not obey them. To Me is your return, and I will inform you about what you used to do", "وَوَصَّيۡنَا ٱلۡإِنسَٰنَ بِوَٰلِدَيۡهِ حُسۡنٗاۖ وَإِن جَٰهَدَاكَ لِتُشۡرِكَ بِي مَا لَيۡسَ لَكَ بِهِۦ عِلۡمٞ فَلَا تُطِعۡهُمَآۚ إِلَيَّ مَرۡجِعُكُمۡ فَأُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ", "Wawassayna alinsana biwalidayhi husnan wain jahadaka litushrika bee ma laysa laka bihi AAilmun fala tutiAAhuma ilayya marjiAAukum faonabbiokum bima kuntum taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 9, "And those who believe and do righteous deeds - We will surely admit them among the righteous [into Paradise]", "وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ لَنُدۡخِلَنَّهُمۡ فِي ٱلصَّـٰلِحِينَ", "Waallatheena amanoo waAAamiloo alssalihati lanudkhilannahum fee alssaliheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 10, "And of the people are some who say, ""We believe in Allah,"" but when one [of them] is harmed for [the cause of] Allah, they consider the trial of the people as [if it were] the punishment of Allah. But if victory comes from your Lord, they say, ""Indeed, We were with you."" Is not Allah most knowing of what is within the breasts of all creatures", "وَمِنَ ٱلنَّاسِ مَن يَقُولُ ءَامَنَّا بِٱللَّهِ فَإِذَآ أُوذِيَ فِي ٱللَّهِ جَعَلَ فِتۡنَةَ ٱلنَّاسِ كَعَذَابِ ٱللَّهِۖ وَلَئِن جَآءَ نَصۡرٞ مِّن رَّبِّكَ لَيَقُولُنَّ إِنَّا كُنَّا مَعَكُمۡۚ أَوَلَيۡسَ ٱللَّهُ بِأَعۡلَمَ بِمَا فِي صُدُورِ ٱلۡعَٰلَمِينَ", "Wamina alnnasi man yaqoolu amanna biAllahi faitha oothiya fee Allahi jaAAala fitnata alnnasi kaAAathabi Allahi walain jaa nasrun min rabbika layaqoolunna inna kunna maAAakum awalaysa Allahu biaAAlama bima fee sudoori alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 11, "And Allah will surely make evident those who believe, and He will surely make evident the hypocrites", "وَلَيَعۡلَمَنَّ ٱللَّهُ ٱلَّذِينَ ءَامَنُواْ وَلَيَعۡلَمَنَّ ٱلۡمُنَٰفِقِينَ", "WalayaAAlamanna Allahu allatheena amanoo walayaAAlamanna almunafiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 12, "And those who disbelieve say to those who believe, ""Follow our way, and we will carry your sins."" But they will not carry anything of their sins. Indeed, they are liars", "وَقَالَ ٱلَّذِينَ كَفَرُواْ لِلَّذِينَ ءَامَنُواْ ٱتَّبِعُواْ سَبِيلَنَا وَلۡنَحۡمِلۡ خَطَٰيَٰكُمۡ وَمَا هُم بِحَٰمِلِينَ مِنۡ خَطَٰيَٰهُم مِّن شَيۡءٍۖ إِنَّهُمۡ لَكَٰذِبُونَ", "Waqala allatheena kafaroo lillatheena amanoo ittabiAAoo sabeelana walnahmil khatayakum wama hum bihamileena min khatayahum min shayin innahum lakathiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 13, "But they will surely carry their [own] burdens and [other] burdens along with their burdens, and they will surely be questioned on the Day of Resurrection about what they used to invent", "وَلَيَحۡمِلُنَّ أَثۡقَالَهُمۡ وَأَثۡقَالٗا مَّعَ أَثۡقَالِهِمۡۖ وَلَيُسۡـَٔلُنَّ يَوۡمَ ٱلۡقِيَٰمَةِ عَمَّا كَانُواْ يَفۡتَرُونَ", "Walayahmilunna athqalahum waathqalan maAAa athqalihim walayusalunna yawma alqiyamati AAamma kanoo yaftaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 14, "And We certainly sent Noah to his people, and he remained among them a thousand years minus fifty years, and the flood seized them while they were wrongdoers", "وَلَقَدۡ أَرۡسَلۡنَا نُوحًا إِلَىٰ قَوۡمِهِۦ فَلَبِثَ فِيهِمۡ أَلۡفَ سَنَةٍ إِلَّا خَمۡسِينَ عَامٗا فَأَخَذَهُمُ ٱلطُّوفَانُ وَهُمۡ ظَٰلِمُونَ", "Walaqad arsalna noohan ila qawmihi falabitha feehim alfa sanatin illa khamseena AAaman faakhathahumu alttoofanu wahum thalimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 15, "But We saved him and the companions of the ship, and We made it a sign for the worlds", "فَأَنجَيۡنَٰهُ وَأَصۡحَٰبَ ٱلسَّفِينَةِ وَجَعَلۡنَٰهَآ ءَايَةٗ لِّلۡعَٰلَمِينَ", "Faanjaynahu waashaba alssafeenati wajaAAalnaha ayatan lilAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 16, "And [We sent] Abraham, when he said to his people, ""Worship Allah and fear Him. That is best for you, if you should know", "وَإِبۡرَٰهِيمَ إِذۡ قَالَ لِقَوۡمِهِ ٱعۡبُدُواْ ٱللَّهَ وَٱتَّقُوهُۖ ذَٰلِكُمۡ خَيۡرٞ لَّكُمۡ إِن كُنتُمۡ تَعۡلَمُونَ", "Waibraheema ith qala liqawmihi oAAbudoo Allaha waittaqoohu thalikum khayrun lakum in kuntum taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 17, "You only worship, besides Allah, idols, and you produce a falsehood. Indeed, those you worship besides Allah do not possess for you [the power of] provision. So seek from Allah provision and worship Him and be grateful to Him. To Him you will be returned", "إِنَّمَا تَعۡبُدُونَ مِن دُونِ ٱللَّهِ أَوۡثَٰنٗا وَتَخۡلُقُونَ إِفۡكًاۚ إِنَّ ٱلَّذِينَ تَعۡبُدُونَ مِن دُونِ ٱللَّهِ لَا يَمۡلِكُونَ لَكُمۡ رِزۡقٗا فَٱبۡتَغُواْ عِندَ ٱللَّهِ ٱلرِّزۡقَ وَٱعۡبُدُوهُ وَٱشۡكُرُواْ لَهُۥٓۖ إِلَيۡهِ تُرۡجَعُونَ", "Innama taAAbudoona min dooni Allahi awthanan watakhluqoona ifkan inna allatheena taAAbudoona min dooni Allahi la yamlikoona lakum rizqan faibtaghoo AAinda Allahi alrrizqa waoAAbudoohu waoshkuroo lahu ilayhi turjaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 18, "And if you [people] deny [the message] - already nations before you have denied. And there is not upon the Messenger except [the duty of] clear notification", "وَإِن تُكَذِّبُواْ فَقَدۡ كَذَّبَ أُمَمٞ مِّن قَبۡلِكُمۡۖ وَمَا عَلَى ٱلرَّسُولِ إِلَّا ٱلۡبَلَٰغُ ٱلۡمُبِينُ", "Wain tukaththiboo faqad kaththaba omamun min qablikum wama AAala alrrasooli illa albalaghu almubeenu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 19, "Have they not considered how Allah begins creation and then repeats it? Indeed that, for Allah, is easy", "أَوَلَمۡ يَرَوۡاْ كَيۡفَ يُبۡدِئُ ٱللَّهُ ٱلۡخَلۡقَ ثُمَّ يُعِيدُهُۥٓۚ إِنَّ ذَٰلِكَ عَلَى ٱللَّهِ يَسِيرٞ", "Awalam yaraw kayfa yubdio Allahu alkhalqa thumma yuAAeeduhu inna thalika AAala Allahi yaseerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 20, "Say, [O Muhammad], ""Travel through the land and observe how He began creation. Then Allah will produce the final creation. Indeed Allah, over all things, is competent", "قُلۡ سِيرُواْ فِي ٱلۡأَرۡضِ فَٱنظُرُواْ كَيۡفَ بَدَأَ ٱلۡخَلۡقَۚ ثُمَّ ٱللَّهُ يُنشِئُ ٱلنَّشۡأَةَ ٱلۡأٓخِرَةَۚ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ", "Qul seeroo fee alardi faonthuroo kayfa badaa alkhalqa thumma Allahu yunshio alnnashata alakhirata inna Allaha AAala kulli shayin qadeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 21, "He punishes whom He wills and has mercy upon whom He wills, and to Him you will be returned", "يُعَذِّبُ مَن يَشَآءُ وَيَرۡحَمُ مَن يَشَآءُۖ وَإِلَيۡهِ تُقۡلَبُونَ", "YuAAaththibu man yashao wayarhamu man yashao wailayhi tuqlaboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 22, "And you will not cause failure [to Allah] upon the earth or in the heaven. And you have not other than Allah any protector or any helper", "وَمَآ أَنتُم بِمُعۡجِزِينَ فِي ٱلۡأَرۡضِ وَلَا فِي ٱلسَّمَآءِۖ وَمَا لَكُم مِّن دُونِ ٱللَّهِ مِن وَلِيّٖ وَلَا نَصِيرٖ", "Wama antum bimuAAjizeena fee alardi wala fee alssamai wama lakum min dooni Allahi min waliyyin wala naseerin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 23, "And the ones who disbelieve in the signs of Allah and the meeting with Him - those have despaired of My mercy, and they will have a painful punishment", "وَٱلَّذِينَ كَفَرُواْ بِـَٔايَٰتِ ٱللَّهِ وَلِقَآئِهِۦٓ أُوْلَـٰٓئِكَ يَئِسُواْ مِن رَّحۡمَتِي وَأُوْلَـٰٓئِكَ لَهُمۡ عَذَابٌ أَلِيمٞ", "Waallatheena kafaroo biayati Allahi waliqaihi olaika yaisoo min rahmatee waolaika lahum AAathabun aleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 24, "And the answer of Abraham's people was not but that they said, ""Kill him or burn him,"" but Allah saved him from the fire. Indeed in that are signs for a people who believe", "فَمَا كَانَ جَوَابَ قَوۡمِهِۦٓ إِلَّآ أَن قَالُواْ ٱقۡتُلُوهُ أَوۡ حَرِّقُوهُ فَأَنجَىٰهُ ٱللَّهُ مِنَ ٱلنَّارِۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يُؤۡمِنُونَ", "Fama kana jawaba qawmihi illa an qaloo oqtuloohu aw harriqoohu faanjahu Allahu mina alnnari inna fee thalika laayatin liqawmin yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 25, "And [Abraham] said, ""You have only taken, other than Allah, idols as [a bond of] affection among you in worldly life. Then on the Day of Resurrection you will deny one another and curse one another, and your refuge will be the Fire, and you will not have any helpers", "وَقَالَ إِنَّمَا ٱتَّخَذۡتُم مِّن دُونِ ٱللَّهِ أَوۡثَٰنٗا مَّوَدَّةَ بَيۡنِكُمۡ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۖ ثُمَّ يَوۡمَ ٱلۡقِيَٰمَةِ يَكۡفُرُ بَعۡضُكُم بِبَعۡضٖ وَيَلۡعَنُ بَعۡضُكُم بَعۡضٗا وَمَأۡوَىٰكُمُ ٱلنَّارُ وَمَا لَكُم مِّن نَّـٰصِرِينَ", "Waqala innama ittakhathtum min dooni Allahi awthanan mawaddata baynikum fee alhayati alddunya thumma yawma alqiyamati yakfuru baAAdukum bibaAAdin wayalAAanu baAAdukum baAAdan wamawakumu alnnaru wama lakum min nasireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 26, "And Lot believed him. [Abraham] said, ""Indeed, I will emigrate to [the service of] my Lord. Indeed, He is the Exalted in Might, the Wise", "۞فَـَٔامَنَ لَهُۥ لُوطٞۘ وَقَالَ إِنِّي مُهَاجِرٌ إِلَىٰ رَبِّيٓۖ إِنَّهُۥ هُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ", "Faamana lahu lootun waqala innee muhajirun ila rabbee innahu huwa alAAazeezu alhakeemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 27, "And We gave to Him Isaac and Jacob and placed in his descendants prophethood and scripture. And We gave him his reward in this world, and indeed, he is in the Hereafter among the righteous", "وَوَهَبۡنَا لَهُۥٓ إِسۡحَٰقَ وَيَعۡقُوبَ وَجَعَلۡنَا فِي ذُرِّيَّتِهِ ٱلنُّبُوَّةَ وَٱلۡكِتَٰبَ وَءَاتَيۡنَٰهُ أَجۡرَهُۥ فِي ٱلدُّنۡيَاۖ وَإِنَّهُۥ فِي ٱلۡأٓخِرَةِ لَمِنَ ٱلصَّـٰلِحِينَ", "Wawahabna lahu ishaqa wayaAAqooba wajaAAalna fee thurriyyatihi alnnubuwwata waalkitaba waataynahu ajrahu fee alddunya wainnahu fee alakhirati lamina alssaliheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 28, "And [mention] Lot, when he said to his people, ""Indeed, you commit such immorality as no one has preceded you with from among the worlds", "وَلُوطًا إِذۡ قَالَ لِقَوۡمِهِۦٓ إِنَّكُمۡ لَتَأۡتُونَ ٱلۡفَٰحِشَةَ مَا سَبَقَكُم بِهَا مِنۡ أَحَدٖ مِّنَ ٱلۡعَٰلَمِينَ", "Walootan ith qala liqawmihi innakum latatoona alfahishata ma sabaqakum biha min ahadin mina alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 29, "Indeed, you approach men and obstruct the road and commit in your meetings [every] evil."" And the answer of his people was not but they said, ""Bring us the punishment of Allah, if you should be of the truthful", "أَئِنَّكُمۡ لَتَأۡتُونَ ٱلرِّجَالَ وَتَقۡطَعُونَ ٱلسَّبِيلَ وَتَأۡتُونَ فِي نَادِيكُمُ ٱلۡمُنكَرَۖ فَمَا كَانَ جَوَابَ قَوۡمِهِۦٓ إِلَّآ أَن قَالُواْ ٱئۡتِنَا بِعَذَابِ ٱللَّهِ إِن كُنتَ مِنَ ٱلصَّـٰدِقِينَ", "Ainnakum latatoona alrrijala wataqtaAAoona alssabeela watatoona fee nadeekumu almunkara fama kana jawaba qawmihi illa an qaloo itina biAAathabi Allahi in kunta mina alssadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 30, "He said, ""My Lord, support me against the corrupting people", "قَالَ رَبِّ ٱنصُرۡنِي عَلَى ٱلۡقَوۡمِ ٱلۡمُفۡسِدِينَ", "Qala rabbi onsurnee AAala alqawmi almufsideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 31, "And when Our messengers came to Abraham with the good tidings, they said, ""Indeed, we will destroy the people of that Lot's city. Indeed, its people have been wrongdoers", "وَلَمَّا جَآءَتۡ رُسُلُنَآ إِبۡرَٰهِيمَ بِٱلۡبُشۡرَىٰ قَالُوٓاْ إِنَّا مُهۡلِكُوٓاْ أَهۡلِ هَٰذِهِ ٱلۡقَرۡيَةِۖ إِنَّ أَهۡلَهَا كَانُواْ ظَٰلِمِينَ", "Walamma jaat rusuluna ibraheema bialbushra qaloo inna muhlikoo ahli hathihi alqaryati inna ahlaha kanoo thalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 32, "[Abraham] said, ""Indeed, within it is Lot."" They said, ""We are more knowing of who is within it. We will surely save him and his family, except his wife. She is to be of those who remain behind", "قَالَ إِنَّ فِيهَا لُوطٗاۚ قَالُواْ نَحۡنُ أَعۡلَمُ بِمَن فِيهَاۖ لَنُنَجِّيَنَّهُۥ وَأَهۡلَهُۥٓ إِلَّا ٱمۡرَأَتَهُۥ كَانَتۡ مِنَ ٱلۡغَٰبِرِينَ", "Qala inna feeha lootan qaloo nahnu aAAlamu biman feeha lanunajjiyannahu waahlahu illa imraatahu kanat mina alghabireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 33, "And when Our messengers came to Lot, he was distressed for them and felt for them great discomfort. They said, ""Fear not, nor grieve. Indeed, we will save you and your family, except your wife; she is to be of those who remain behind", "وَلَمَّآ أَن جَآءَتۡ رُسُلُنَا لُوطٗا سِيٓءَ بِهِمۡ وَضَاقَ بِهِمۡ ذَرۡعٗاۖ وَقَالُواْ لَا تَخَفۡ وَلَا تَحۡزَنۡ إِنَّا مُنَجُّوكَ وَأَهۡلَكَ إِلَّا ٱمۡرَأَتَكَ كَانَتۡ مِنَ ٱلۡغَٰبِرِينَ", "Walamma an jaat rusuluna lootan seea bihim wadaqa bihim tharAAan waqaloo la takhaf wala tahzan inna munajjooka waahlaka illa imraataka kanat mina alghabireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 34, "Indeed, we will bring down on the people of this city punishment from the sky because they have been defiantly disobedient", "إِنَّا مُنزِلُونَ عَلَىٰٓ أَهۡلِ هَٰذِهِ ٱلۡقَرۡيَةِ رِجۡزٗا مِّنَ ٱلسَّمَآءِ بِمَا كَانُواْ يَفۡسُقُونَ", "Inna munziloona AAala ahli hathihi alqaryati rijzan mina alssamai bima kanoo yafsuqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 35, "And We have certainly left of it a sign as clear evidence for a people who use reason", "وَلَقَد تَّرَكۡنَا مِنۡهَآ ءَايَةَۢ بَيِّنَةٗ لِّقَوۡمٖ يَعۡقِلُونَ", "Walaqad tarakna minha ayatan bayyinatan liqawmin yaAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 36, "And to Madyan [We sent] their brother Shu'ayb, and he said, ""O my people, worship Allah and expect the Last Day and do not commit abuse on the earth, spreading corruption", "وَإِلَىٰ مَدۡيَنَ أَخَاهُمۡ شُعَيۡبٗا فَقَالَ يَٰقَوۡمِ ٱعۡبُدُواْ ٱللَّهَ وَٱرۡجُواْ ٱلۡيَوۡمَ ٱلۡأٓخِرَ وَلَا تَعۡثَوۡاْ فِي ٱلۡأَرۡضِ مُفۡسِدِينَ", "Waila madyana akhahum shuAAayban faqala ya qawmi oAAbudoo Allaha waorjoo alyawma alakhira wala taAAthaw fee alardi mufsideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 37, "But they denied him, so the earthquake seized them, and they became within their home [corpses] fallen prone", "فَكَذَّبُوهُ فَأَخَذَتۡهُمُ ٱلرَّجۡفَةُ فَأَصۡبَحُواْ فِي دَارِهِمۡ جَٰثِمِينَ", "Fakaththaboohu faakhathathumu alrrajfatu faasbahoo fee darihim jathimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 38, "And [We destroyed] 'Aad and Thamud, and it has become clear to you from their [ruined] dwellings. And Satan had made pleasing to them their deeds and averted them from the path, and they were endowed with perception", "وَعَادٗا وَثَمُودَاْ وَقَد تَّبَيَّنَ لَكُم مِّن مَّسَٰكِنِهِمۡۖ وَزَيَّنَ لَهُمُ ٱلشَّيۡطَٰنُ أَعۡمَٰلَهُمۡ فَصَدَّهُمۡ عَنِ ٱلسَّبِيلِ وَكَانُواْ مُسۡتَبۡصِرِينَ", "WaAAadan wathamooda waqad tabayyana lakum min masakinihim wazayyana lahumu alshshaytanu aAAmalahum fasaddahum AAani alssabeeli wakanoo mustabsireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 39, "And [We destroyed] Qarun and Pharaoh and Haman. And Moses had already come to them with clear evidences, and they were arrogant in the land, but they were not outrunners [of Our punishment]", "وَقَٰرُونَ وَفِرۡعَوۡنَ وَهَٰمَٰنَۖ وَلَقَدۡ جَآءَهُم مُّوسَىٰ بِٱلۡبَيِّنَٰتِ فَٱسۡتَكۡبَرُواْ فِي ٱلۡأَرۡضِ وَمَا كَانُواْ سَٰبِقِينَ", "Waqaroona wafirAAawna wahamana walaqad jaahum moosa bialbayyinati faistakbaroo fee alardi wama kanoo sabiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 40, "So each We seized for his sin; and among them were those upon whom We sent a storm of stones, and among them were those who were seized by the blast [from the sky], and among them were those whom We caused the earth to swallow, and among them were those whom We drowned. And Allah would not have wronged them, but it was they who were wronging themselves", "فَكُلًّا أَخَذۡنَا بِذَنۢبِهِۦۖ فَمِنۡهُم مَّنۡ أَرۡسَلۡنَا عَلَيۡهِ حَاصِبٗا وَمِنۡهُم مَّنۡ أَخَذَتۡهُ ٱلصَّيۡحَةُ وَمِنۡهُم مَّنۡ خَسَفۡنَا بِهِ ٱلۡأَرۡضَ وَمِنۡهُم مَّنۡ أَغۡرَقۡنَاۚ وَمَا كَانَ ٱللَّهُ لِيَظۡلِمَهُمۡ وَلَٰكِن كَانُوٓاْ أَنفُسَهُمۡ يَظۡلِمُونَ", "Fakullan akhathna bithanbihi faminhum man arsalna AAalayhi hasiban waminhum man akhathathu alssayhatu waminhum man khasafna bihi alarda waminhum man aghraqna wama kana Allahu liyathlimahum walakin kanoo anfusahum yathlimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 41, "The example of those who take allies other than Allah is like that of the spider who takes a home. And indeed, the weakest of homes is the home of the spider, if they only knew", "مَثَلُ ٱلَّذِينَ ٱتَّخَذُواْ مِن دُونِ ٱللَّهِ أَوۡلِيَآءَ كَمَثَلِ ٱلۡعَنكَبُوتِ ٱتَّخَذَتۡ بَيۡتٗاۖ وَإِنَّ أَوۡهَنَ ٱلۡبُيُوتِ لَبَيۡتُ ٱلۡعَنكَبُوتِۚ لَوۡ كَانُواْ يَعۡلَمُونَ", "Mathalu allatheena ittakhathoo min dooni Allahi awliyaa kamathali alAAankabooti ittakhathat baytan wainna awhana albuyooti labaytu alAAankabooti law kanoo yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 42, "Indeed, Allah knows whatever thing they call upon other than Him. And He is the Exalted in Might, the Wise", "إِنَّ ٱللَّهَ يَعۡلَمُ مَا يَدۡعُونَ مِن دُونِهِۦ مِن شَيۡءٖۚ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ", "Inna Allaha yaAAlamu ma yadAAoona min doonihi min shayin wahuwa alAAazeezu alhakeemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 43, "And these examples We present to the people, but none will understand them except those of knowledge", "وَتِلۡكَ ٱلۡأَمۡثَٰلُ نَضۡرِبُهَا لِلنَّاسِۖ وَمَا يَعۡقِلُهَآ إِلَّا ٱلۡعَٰلِمُونَ", "Watilka alamthalu nadribuha lilnnasi wama yaAAqiluha illa alAAalimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 44, "Allah created the heavens and the earth in truth. Indeed in that is a sign for the believers", "خَلَقَ ٱللَّهُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ بِٱلۡحَقِّۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لِّلۡمُؤۡمِنِينَ", "Khalaqa Allahu alssamawati waalarda bialhaqqi inna fee thalika laayatan lilmumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 45, "Recite, [O Muhammad], what has been revealed to you of the Book and establish prayer. Indeed, prayer prohibits immorality and wrongdoing, and the remembrance of Allah is greater. And Allah knows that which you do", "ٱتۡلُ مَآ أُوحِيَ إِلَيۡكَ مِنَ ٱلۡكِتَٰبِ وَأَقِمِ ٱلصَّلَوٰةَۖ إِنَّ ٱلصَّلَوٰةَ تَنۡهَىٰ عَنِ ٱلۡفَحۡشَآءِ وَٱلۡمُنكَرِۗ وَلَذِكۡرُ ٱللَّهِ أَكۡبَرُۗ وَٱللَّهُ يَعۡلَمُ مَا تَصۡنَعُونَ", "Otlu ma oohiya ilayka mina alkitabi waaqimi alssalata inna alssalata tanha AAani alfahshai waalmunkari walathikru Allahi akbaru waAllahu yaAAlamu ma tasnaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 46, "And do not argue with the People of the Scripture except in a way that is best, except for those who commit injustice among them, and say, ""We believe in that which has been revealed to us and revealed to you. And our God and your God is one; and we are Muslims [in submission] to Him", "۞وَلَا تُجَٰدِلُوٓاْ أَهۡلَ ٱلۡكِتَٰبِ إِلَّا بِٱلَّتِي هِيَ أَحۡسَنُ إِلَّا ٱلَّذِينَ ظَلَمُواْ مِنۡهُمۡۖ وَقُولُوٓاْ ءَامَنَّا بِٱلَّذِيٓ أُنزِلَ إِلَيۡنَا وَأُنزِلَ إِلَيۡكُمۡ وَإِلَٰهُنَا وَإِلَٰهُكُمۡ وَٰحِدٞ وَنَحۡنُ لَهُۥ مُسۡلِمُونَ", "Wala tujadiloo ahla alkitabi illa biallatee hiya ahsanu illa allatheena thalamoo minhum waqooloo amanna biallathee onzila ilayna waonzila ilaykum wailahuna wailahukum wahidun wanahnu lahu muslimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 47, "And thus We have sent down to you the Qur'an. And those to whom We [previously] gave the Scripture believe in it. And among these [people of Makkah] are those who believe in it. And none reject Our verses except the disbelievers", "وَكَذَٰلِكَ أَنزَلۡنَآ إِلَيۡكَ ٱلۡكِتَٰبَۚ فَٱلَّذِينَ ءَاتَيۡنَٰهُمُ ٱلۡكِتَٰبَ يُؤۡمِنُونَ بِهِۦۖ وَمِنۡ هَـٰٓؤُلَآءِ مَن يُؤۡمِنُ بِهِۦۚ وَمَا يَجۡحَدُ بِـَٔايَٰتِنَآ إِلَّا ٱلۡكَٰفِرُونَ", "Wakathalika anzalna ilayka alkitaba faallatheena ataynahumu alkitaba yuminoona bihi wamin haolai man yuminu bihi wama yajhadu biayatina illa alkafiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 48, "And you did not recite before it any scripture, nor did you inscribe one with your right hand. Otherwise the falsifiers would have had [cause for] doubt", "وَمَا كُنتَ تَتۡلُواْ مِن قَبۡلِهِۦ مِن كِتَٰبٖ وَلَا تَخُطُّهُۥ بِيَمِينِكَۖ إِذٗا لَّٱرۡتَابَ ٱلۡمُبۡطِلُونَ", "Wama kunta tatloo min qablihi min kitabin wala takhuttuhu biyameenika ithan lairtaba almubtiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 49, "Rather, the Qur'an is distinct verses [preserved] within the breasts of those who have been given knowledge. And none reject Our verses except the wrongdoers", "بَلۡ هُوَ ءَايَٰتُۢ بَيِّنَٰتٞ فِي صُدُورِ ٱلَّذِينَ أُوتُواْ ٱلۡعِلۡمَۚ وَمَا يَجۡحَدُ بِـَٔايَٰتِنَآ إِلَّا ٱلظَّـٰلِمُونَ", "Bal huwa ayatun bayyinatun fee sudoori allatheena ootoo alAAilma wama yajhadu biayatina illa alththalimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 50, "But they say, ""Why are not signs sent down to him from his Lord?"" Say, ""The signs are only with Allah, and I am only a clear warner", "وَقَالُواْ لَوۡلَآ أُنزِلَ عَلَيۡهِ ءَايَٰتٞ مِّن رَّبِّهِۦۚ قُلۡ إِنَّمَا ٱلۡأٓيَٰتُ عِندَ ٱللَّهِ وَإِنَّمَآ أَنَا۠ نَذِيرٞ مُّبِينٌ", "Waqaloo lawla onzila AAalayhi ayatun min rabbihi qul innama alayatu AAinda Allahi wainnama ana natheerun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 51, "And is it not sufficient for them that We revealed to you the Book which is recited to them? Indeed in that is a mercy and reminder for a people who believe", "أَوَلَمۡ يَكۡفِهِمۡ أَنَّآ أَنزَلۡنَا عَلَيۡكَ ٱلۡكِتَٰبَ يُتۡلَىٰ عَلَيۡهِمۡۚ إِنَّ فِي ذَٰلِكَ لَرَحۡمَةٗ وَذِكۡرَىٰ لِقَوۡمٖ يُؤۡمِنُونَ", "Awalam yakfihim anna anzalna AAalayka alkitaba yutla AAalayhim inna fee thalika larahmatan wathikra liqawmin yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 52, "Say, ""Sufficient is Allah between me and you as Witness. He knows what is in the heavens and earth. And they who have believed in falsehood and disbelieved in Allah - it is those who are the losers", "قُلۡ كَفَىٰ بِٱللَّهِ بَيۡنِي وَبَيۡنَكُمۡ شَهِيدٗاۖ يَعۡلَمُ مَا فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۗ وَٱلَّذِينَ ءَامَنُواْ بِٱلۡبَٰطِلِ وَكَفَرُواْ بِٱللَّهِ أُوْلَـٰٓئِكَ هُمُ ٱلۡخَٰسِرُونَ", "Qul kafa biAllahi baynee wabaynakum shaheedan yaAAlamu ma fee alssamawati waalardi waallatheena amanoo bialbatili wakafaroo biAllahi olaika humu alkhasiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 53, "And they urge you to hasten the punishment. And if not for [the decree of] a specified term, punishment would have reached them. But it will surely come to them suddenly while they perceive not", "وَيَسۡتَعۡجِلُونَكَ بِٱلۡعَذَابِ وَلَوۡلَآ أَجَلٞ مُّسَمّٗى لَّجَآءَهُمُ ٱلۡعَذَابُۚ وَلَيَأۡتِيَنَّهُم بَغۡتَةٗ وَهُمۡ لَا يَشۡعُرُونَ", "WayastaAAjiloonaka bialAAathabi walawla ajalun musamman lajaahumu alAAathabu walayatiyannahum baghtatan wahum la yashAAuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 54, "They urge you to hasten the punishment. And indeed, Hell will be encompassing of the disbelievers", "يَسۡتَعۡجِلُونَكَ بِٱلۡعَذَابِ وَإِنَّ جَهَنَّمَ لَمُحِيطَةُۢ بِٱلۡكَٰفِرِينَ", "YastaAAjiloonaka bialAAathabi wainna jahannama lamuheetatun bialkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 55, "On the Day the punishment will cover them from above them and from below their feet and it is said, ""Taste [the result of] what you used to do", "يَوۡمَ يَغۡشَىٰهُمُ ٱلۡعَذَابُ مِن فَوۡقِهِمۡ وَمِن تَحۡتِ أَرۡجُلِهِمۡ وَيَقُولُ ذُوقُواْ مَا كُنتُمۡ تَعۡمَلُونَ", "Yawma yaghshahumu alAAathabu min fawqihim wamin tahti arjulihim wayaqoolu thooqoo ma kuntum taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 56, "O My servants who have believed, indeed My earth is spacious, so worship only Me", "يَٰعِبَادِيَ ٱلَّذِينَ ءَامَنُوٓاْ إِنَّ أَرۡضِي وَٰسِعَةٞ فَإِيَّـٰيَ فَٱعۡبُدُونِ", "Ya AAibadiya allatheena amanoo inna ardee wasiAAatun faiyyaya faoAAbudooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 57, "Every soul will taste death. Then to Us will you be returned", "كُلُّ نَفۡسٖ ذَآئِقَةُ ٱلۡمَوۡتِۖ ثُمَّ إِلَيۡنَا تُرۡجَعُونَ", "Kullu nafsin thaiqatu almawti thumma ilayna turjaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 58, "And those who have believed and done righteous deeds - We will surely assign to them of Paradise [elevated] chambers beneath which rivers flow, wherein they abide eternally. Excellent is the reward of the [righteous] workers", "وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ لَنُبَوِّئَنَّهُم مِّنَ ٱلۡجَنَّةِ غُرَفٗا تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَاۚ نِعۡمَ أَجۡرُ ٱلۡعَٰمِلِينَ", "Waallatheena amanoo waAAamiloo alssalihati lanubawwiannahum mina aljannati ghurafan tajree min tahtiha alanharu khalideena feeha niAAma ajru alAAamileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 59, "Who have been patient and upon their Lord rely", "ٱلَّذِينَ صَبَرُواْ وَعَلَىٰ رَبِّهِمۡ يَتَوَكَّلُونَ", "Allatheena sabaroo waAAala rabbihim yatawakkaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 60, "And how many a creature carries not its [own] provision. Allah provides for it and for you. And He is the Hearing, the Knowing", "وَكَأَيِّن مِّن دَآبَّةٖ لَّا تَحۡمِلُ رِزۡقَهَا ٱللَّهُ يَرۡزُقُهَا وَإِيَّاكُمۡۚ وَهُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ", "Wakaayyin min dabbatin la tahmilu rizqaha Allahu yarzuquha waiyyakum wahuwa alssameeAAu alAAaleemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 61, "If you asked them, ""Who created the heavens and earth and subjected the sun and the moon?"" they would surely say, ""Allah."" Then how are they deluded", "وَلَئِن سَأَلۡتَهُم مَّنۡ خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَسَخَّرَ ٱلشَّمۡسَ وَٱلۡقَمَرَ لَيَقُولُنَّ ٱللَّهُۖ فَأَنَّىٰ يُؤۡفَكُونَ", "Walain saaltahum man khalaqa alssamawati waalarda wasakhkhara alshshamsa waalqamara layaqoolunna Allahu faanna yufakoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 62, "Allah extends provision for whom He wills of His servants and restricts for him. Indeed Allah is, of all things, Knowing", "ٱللَّهُ يَبۡسُطُ ٱلرِّزۡقَ لِمَن يَشَآءُ مِنۡ عِبَادِهِۦ وَيَقۡدِرُ لَهُۥٓۚ إِنَّ ٱللَّهَ بِكُلِّ شَيۡءٍ عَلِيمٞ", "Allahu yabsutu alrrizqa liman yashao min AAibadihi wayaqdiru lahu inna Allaha bikulli shayin AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 63, "And if you asked them, ""Who sends down rain from the sky and gives life thereby to the earth after its lifelessness?"" they would surely say "" Allah."" Say, ""Praise to Allah ""; but most of them do not reason", "وَلَئِن سَأَلۡتَهُم مَّن نَّزَّلَ مِنَ ٱلسَّمَآءِ مَآءٗ فَأَحۡيَا بِهِ ٱلۡأَرۡضَ مِنۢ بَعۡدِ مَوۡتِهَا لَيَقُولُنَّ ٱللَّهُۚ قُلِ ٱلۡحَمۡدُ لِلَّهِۚ بَلۡ أَكۡثَرُهُمۡ لَا يَعۡقِلُونَ", "Walain saaltahum man nazzala mina alssamai maan faahya bihi alarda min baAAdi mawtiha layaqoolunna Allahu quli alhamdu lillahi bal aktharuhum la yaAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 64, "And this worldly life is not but diversion and amusement. And indeed, the home of the Hereafter - that is the [eternal] life, if only they knew", "وَمَا هَٰذِهِ ٱلۡحَيَوٰةُ ٱلدُّنۡيَآ إِلَّا لَهۡوٞ وَلَعِبٞۚ وَإِنَّ ٱلدَّارَ ٱلۡأٓخِرَةَ لَهِيَ ٱلۡحَيَوَانُۚ لَوۡ كَانُواْ يَعۡلَمُونَ", "Wama hathihi alhayatu alddunya illa lahwun walaAAibun wainna alddara alakhirata lahiya alhayawanu law kanoo yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 65, "And when they board a ship, they supplicate Allah, sincere to Him in religion. But when He delivers them to the land, at once they associate others with Him", "فَإِذَا رَكِبُواْ فِي ٱلۡفُلۡكِ دَعَوُاْ ٱللَّهَ مُخۡلِصِينَ لَهُ ٱلدِّينَ فَلَمَّا نَجَّىٰهُمۡ إِلَى ٱلۡبَرِّ إِذَا هُمۡ يُشۡرِكُونَ", "Faitha rakiboo fee alfulki daAAawoo Allaha mukhliseena lahu alddeena falamma najjahum ila albarri itha hum yushrikoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 66, "So that they will deny what We have granted them, and they will enjoy themselves. But they are going to know", "لِيَكۡفُرُواْ بِمَآ ءَاتَيۡنَٰهُمۡ وَلِيَتَمَتَّعُواْۚ فَسَوۡفَ يَعۡلَمُونَ", "Liyakfuroo bima ataynahum waliyatamattaAAoo fasawfa yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 67, "Have they not seen that We made [Makkah] a safe sanctuary, while people are being taken away all around them? Then in falsehood do they believe, and in the favor of Allah they disbelieve", "أَوَلَمۡ يَرَوۡاْ أَنَّا جَعَلۡنَا حَرَمًا ءَامِنٗا وَيُتَخَطَّفُ ٱلنَّاسُ مِنۡ حَوۡلِهِمۡۚ أَفَبِٱلۡبَٰطِلِ يُؤۡمِنُونَ وَبِنِعۡمَةِ ٱللَّهِ يَكۡفُرُونَ", "Awalam yaraw anna jaAAalna haraman aminan wayutakhattafu alnnasu min hawlihim afabialbatili yuminoona wabiniAAmati Allahi yakfuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 68, "And who is more unjust than one who invents a lie about Allah or denies the truth when it has come to him? Is there not in Hell a [sufficient] residence for the disbelievers", "وَمَنۡ أَظۡلَمُ مِمَّنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبًا أَوۡ كَذَّبَ بِٱلۡحَقِّ لَمَّا جَآءَهُۥٓۚ أَلَيۡسَ فِي جَهَنَّمَ مَثۡوٗى لِّلۡكَٰفِرِينَ", "Waman athlamu mimmani iftara AAala Allahi kathiban aw kaththaba bialhaqqi lamma jaahu alaysa fee jahannama mathwan lilkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (29, 69, "And those who strive for Us - We will surely guide them to Our ways. And indeed, Allah is with the doers of good", "وَٱلَّذِينَ جَٰهَدُواْ فِينَا لَنَهۡدِيَنَّهُمۡ سُبُلَنَاۚ وَإِنَّ ٱللَّهَ لَمَعَ ٱلۡمُحۡسِنِينَ", "Waallatheena jahadoo feena lanahdiyannahum subulana wainna Allaha lamaAAa almuhsineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 1, "Alif, Lam, Meem", "الٓمٓ", "Aliflammeem");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 2, "The Byzantines have been defeated", "غُلِبَتِ ٱلرُّومُ", "Ghulibati alrroomu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 3, "In the nearest land. But they, after their defeat, will overcome", "فِيٓ أَدۡنَى ٱلۡأَرۡضِ وَهُم مِّنۢ بَعۡدِ غَلَبِهِمۡ سَيَغۡلِبُونَ", "Fee adna alardi wahum min baAAdi ghalabihim sayaghliboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 4, "Within three to nine years. To Allah belongs the command before and after. And that day the believers will rejoice", "فِي بِضۡعِ سِنِينَۗ لِلَّهِ ٱلۡأَمۡرُ مِن قَبۡلُ وَمِنۢ بَعۡدُۚ وَيَوۡمَئِذٖ يَفۡرَحُ ٱلۡمُؤۡمِنُونَ", "Fee bidAAi sineena lillahi alamru min qablu wamin baAAdu wayawmaithin yafrahu almuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 5, "In the victory of Allah. He gives victory to whom He wills, and He is the Exalted in Might, the Merciful", "بِنَصۡرِ ٱللَّهِۚ يَنصُرُ مَن يَشَآءُۖ وَهُوَ ٱلۡعَزِيزُ ٱلرَّحِيمُ", "Binasri Allahi yansuru man yashao wahuwa alAAazeezu alrraheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 6, "[It is] the promise of Allah. Allah does not fail in His promise, but most of the people do not know", "وَعۡدَ ٱللَّهِۖ لَا يُخۡلِفُ ٱللَّهُ وَعۡدَهُۥ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَعۡلَمُونَ", "WaAAda Allahi la yukhlifu Allahu waAAdahu walakinna akthara alnnasi la yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 7, "They know what is apparent of the worldly life, but they, of the Hereafter, are unaware", "يَعۡلَمُونَ ظَٰهِرٗا مِّنَ ٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَهُمۡ عَنِ ٱلۡأٓخِرَةِ هُمۡ غَٰفِلُونَ", "YaAAlamoona thahiran mina alhayati alddunya wahum AAani alakhirati hum ghafiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 8, "Do they not contemplate within themselves? Allah has not created the heavens and the earth and what is between them except in truth and for a specified term. And indeed, many of the people, in [the matter of] the meeting with their Lord, are disbelievers", "أَوَلَمۡ يَتَفَكَّرُواْ فِيٓ أَنفُسِهِمۗ مَّا خَلَقَ ٱللَّهُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَمَا بَيۡنَهُمَآ إِلَّا بِٱلۡحَقِّ وَأَجَلٖ مُّسَمّٗىۗ وَإِنَّ كَثِيرٗا مِّنَ ٱلنَّاسِ بِلِقَآيِٕ رَبِّهِمۡ لَكَٰفِرُونَ", "Awalam yatafakkaroo fee anfusihim ma khalaqa Allahu alssamawati waalarda wama baynahuma illa bialhaqqi waajalin musamman wainna katheeran mina alnnasi biliqai rabbihim lakafiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 9, "Have they not traveled through the earth and observed how was the end of those before them? They were greater than them in power, and they plowed the earth and built it up more than they have built it up, and their messengers came to them with clear evidences. And Allah would not ever have wronged them, but they were wronging themselves", "أَوَلَمۡ يَسِيرُواْ فِي ٱلۡأَرۡضِ فَيَنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلَّذِينَ مِن قَبۡلِهِمۡۚ كَانُوٓاْ أَشَدَّ مِنۡهُمۡ قُوَّةٗ وَأَثَارُواْ ٱلۡأَرۡضَ وَعَمَرُوهَآ أَكۡثَرَ مِمَّا عَمَرُوهَا وَجَآءَتۡهُمۡ رُسُلُهُم بِٱلۡبَيِّنَٰتِۖ فَمَا كَانَ ٱللَّهُ لِيَظۡلِمَهُمۡ وَلَٰكِن كَانُوٓاْ أَنفُسَهُمۡ يَظۡلِمُونَ", "Awalam yaseeroo fee alardi fayanthuroo kayfa kana AAaqibatu allatheena min qablihim kanoo ashadda minhum quwwatan waatharoo alarda waAAamarooha akthara mimma AAamarooha wajaathum rusuluhum bialbayyinati fama kana Allahu liyathlimahum walakin kanoo anfusahum yathlimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 10, "Then the end of those who did evil was the worst [consequence] because they denied the signs of Allah and used to ridicule them", "ثُمَّ كَانَ عَٰقِبَةَ ٱلَّذِينَ أَسَـٰٓـُٔواْ ٱلسُّوٓأَىٰٓ أَن كَذَّبُواْ بِـَٔايَٰتِ ٱللَّهِ وَكَانُواْ بِهَا يَسۡتَهۡزِءُونَ", "Thumma kana AAaqibata allatheena asaoo alssooa an kaththaboo biayati Allahi wakanoo biha yastahzioona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 11, "Allah begins creation; then He will repeat it; then to Him you will be returned", "ٱللَّهُ يَبۡدَؤُاْ ٱلۡخَلۡقَ ثُمَّ يُعِيدُهُۥ ثُمَّ إِلَيۡهِ تُرۡجَعُونَ", "Allahu yabdao alkhalqa thumma yuAAeeduhu thumma ilayhi turjaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 12, "And the Day the Hour appears the criminals will be in despair", "وَيَوۡمَ تَقُومُ ٱلسَّاعَةُ يُبۡلِسُ ٱلۡمُجۡرِمُونَ", "Wayawma taqoomu alssaAAatu yublisu almujrimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 13, "And there will not be for them among their [alleged] partners any intercessors, and they will [then] be disbelievers in their partners", "وَلَمۡ يَكُن لَّهُم مِّن شُرَكَآئِهِمۡ شُفَعَـٰٓؤُاْ وَكَانُواْ بِشُرَكَآئِهِمۡ كَٰفِرِينَ", "Walam yakun lahum min shurakaihim shufaAAao wakanoo bishurakaihim kafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 14, "And the Day the Hour appears - that Day they will become separated", "وَيَوۡمَ تَقُومُ ٱلسَّاعَةُ يَوۡمَئِذٖ يَتَفَرَّقُونَ", "Wayawma taqoomu alssaAAatu yawmaithin yatafarraqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 15, "And as for those who had believed and done righteous deeds, they will be in a garden [of Paradise], delighted", "فَأَمَّا ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ فَهُمۡ فِي رَوۡضَةٖ يُحۡبَرُونَ", "Faamma allatheena amanoo waAAamiloo alssalihati fahum fee rawdatin yuhbaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 16, "But as for those who disbelieved and denied Our verses and the meeting of the Hereafter, those will be brought into the punishment [to remain]", "وَأَمَّا ٱلَّذِينَ كَفَرُواْ وَكَذَّبُواْ بِـَٔايَٰتِنَا وَلِقَآيِٕ ٱلۡأٓخِرَةِ فَأُوْلَـٰٓئِكَ فِي ٱلۡعَذَابِ مُحۡضَرُونَ", "Waamma allatheena kafaroo wakaththaboo biayatina waliqai alakhirati faolaika fee alAAathabi muhdaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 17, "So exalted is Allah when you reach the evening and when you reach the morning", "فَسُبۡحَٰنَ ٱللَّهِ حِينَ تُمۡسُونَ وَحِينَ تُصۡبِحُونَ", "Fasubhana Allahi heena tumsoona waheena tusbihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 18, "And to Him is [due all] praise throughout the heavens and the earth. And [exalted is He] at night and when you are at noon", "وَلَهُ ٱلۡحَمۡدُ فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَعَشِيّٗا وَحِينَ تُظۡهِرُونَ", "Walahu alhamdu fee alssamawati waalardi waAAashiyyan waheena tuthhiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 19, "He brings the living out of the dead and brings the dead out of the living and brings to life the earth after its lifelessness. And thus will you be brought out", "يُخۡرِجُ ٱلۡحَيَّ مِنَ ٱلۡمَيِّتِ وَيُخۡرِجُ ٱلۡمَيِّتَ مِنَ ٱلۡحَيِّ وَيُحۡيِ ٱلۡأَرۡضَ بَعۡدَ مَوۡتِهَاۚ وَكَذَٰلِكَ تُخۡرَجُونَ", "Yukhriju alhayya mina almayyiti wayukhriju almayyita mina alhayyi wayuhyee alarda baAAda mawtiha wakathalika tukhrajoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 20, "And of His signs is that He created you from dust; then, suddenly you were human beings dispersing [throughout the earth]", "وَمِنۡ ءَايَٰتِهِۦٓ أَنۡ خَلَقَكُم مِّن تُرَابٖ ثُمَّ إِذَآ أَنتُم بَشَرٞ تَنتَشِرُونَ", "Wamin ayatihi an khalaqakum min turabin thumma itha antum basharun tantashiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 21, "And of His signs is that He created for you from yourselves mates that you may find tranquillity in them; and He placed between you affection and mercy. Indeed in that are signs for a people who give thought", "وَمِنۡ ءَايَٰتِهِۦٓ أَنۡ خَلَقَ لَكُم مِّنۡ أَنفُسِكُمۡ أَزۡوَٰجٗا لِّتَسۡكُنُوٓاْ إِلَيۡهَا وَجَعَلَ بَيۡنَكُم مَّوَدَّةٗ وَرَحۡمَةًۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يَتَفَكَّرُونَ", "Wamin ayatihi an khalaqa lakum min anfusikum azwajan litaskunoo ilayha wajaAAala baynakum mawaddatan warahmatan inna fee thalika laayatin liqawmin yatafakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 22, "And of His signs is the creation of the heavens and the earth and the diversity of your languages and your colors. Indeed in that are signs for those of knowledge", "وَمِنۡ ءَايَٰتِهِۦ خَلۡقُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَٱخۡتِلَٰفُ أَلۡسِنَتِكُمۡ وَأَلۡوَٰنِكُمۡۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّلۡعَٰلِمِينَ", "Wamin ayatihi khalqu alssamawati waalardi waikhtilafu alsinatikum waalwanikum inna fee thalika laayatin lilAAalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 23, "And of His signs is your sleep by night and day and your seeking of His bounty. Indeed in that are signs for a people who listen", "وَمِنۡ ءَايَٰتِهِۦ مَنَامُكُم بِٱلَّيۡلِ وَٱلنَّهَارِ وَٱبۡتِغَآؤُكُم مِّن فَضۡلِهِۦٓۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يَسۡمَعُونَ", "Wamin ayatihi manamukum biallayli waalnnahari waibtighaokum min fadlihi inna fee thalika laayatin liqawmin yasmaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 24, "And of His signs is [that] He shows you the lightning [causing] fear and aspiration, and He sends down rain from the sky by which He brings to life the earth after its lifelessness. Indeed in that are signs for a people who use reason", "وَمِنۡ ءَايَٰتِهِۦ يُرِيكُمُ ٱلۡبَرۡقَ خَوۡفٗا وَطَمَعٗا وَيُنَزِّلُ مِنَ ٱلسَّمَآءِ مَآءٗ فَيُحۡيِۦ بِهِ ٱلۡأَرۡضَ بَعۡدَ مَوۡتِهَآۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يَعۡقِلُونَ", "Wamin ayatihi yureekumu albarqa khawfan watamaAAan wayunazzilu mina alssamai maan fayuhyee bihi alarda baAAda mawtiha inna fee thalika laayatin liqawmin yaAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 25, "And of His signs is that the heaven and earth remain by His command. Then when He calls you with a [single] call from the earth, immediately you will come forth", "وَمِنۡ ءَايَٰتِهِۦٓ أَن تَقُومَ ٱلسَّمَآءُ وَٱلۡأَرۡضُ بِأَمۡرِهِۦۚ ثُمَّ إِذَا دَعَاكُمۡ دَعۡوَةٗ مِّنَ ٱلۡأَرۡضِ إِذَآ أَنتُمۡ تَخۡرُجُونَ", "Wamin ayatihi an taqooma alssamao waalardu biamrihi thumma itha daAAakum daAAwatan mina alardi itha antum takhrujoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 26, "And to Him belongs whoever is in the heavens and earth. All are to Him devoutly obedient", "وَلَهُۥ مَن فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ كُلّٞ لَّهُۥ قَٰنِتُونَ", "Walahu man fee alssamawati waalardi kullun lahu qanitoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 27, "And it is He who begins creation; then He repeats it, and that is [even] easier for Him. To Him belongs the highest attribute in the heavens and earth. And He is the Exalted in Might, the Wise", "وَهُوَ ٱلَّذِي يَبۡدَؤُاْ ٱلۡخَلۡقَ ثُمَّ يُعِيدُهُۥ وَهُوَ أَهۡوَنُ عَلَيۡهِۚ وَلَهُ ٱلۡمَثَلُ ٱلۡأَعۡلَىٰ فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ", "Wahuwa allathee yabdao alkhalqa thumma yuAAeeduhu wahuwa ahwanu AAalayhi walahu almathalu alaAAla fee alssamawati waalardi wahuwa alAAazeezu alhakeemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 28, "He presents to you an example from yourselves. Do you have among those whom your right hands possess any partners in what We have provided for you so that you are equal therein [and] would fear them as your fear of one another [within a partnership]? Thus do We detail the verses for a people who use reason", "ضَرَبَ لَكُم مَّثَلٗا مِّنۡ أَنفُسِكُمۡۖ هَل لَّكُم مِّن مَّا مَلَكَتۡ أَيۡمَٰنُكُم مِّن شُرَكَآءَ فِي مَا رَزَقۡنَٰكُمۡ فَأَنتُمۡ فِيهِ سَوَآءٞ تَخَافُونَهُمۡ كَخِيفَتِكُمۡ أَنفُسَكُمۡۚ كَذَٰلِكَ نُفَصِّلُ ٱلۡأٓيَٰتِ لِقَوۡمٖ يَعۡقِلُونَ", "Daraba lakum mathalan min anfusikum hal lakum mi mma malakat aymanukum min shurakaa fee ma razaqnakum faantum feehi sawaon takhafoonahum kakheefatikum anfusakum kathalika nufassilu alayati liqawmin yaAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 29, "But those who wrong follow their [own] desires without knowledge. Then who can guide one whom Allah has sent astray? And for them there are no helpers", "بَلِ ٱتَّبَعَ ٱلَّذِينَ ظَلَمُوٓاْ أَهۡوَآءَهُم بِغَيۡرِ عِلۡمٖۖ فَمَن يَهۡدِي مَنۡ أَضَلَّ ٱللَّهُۖ وَمَا لَهُم مِّن نَّـٰصِرِينَ", "Bali ittabaAAa allatheena thalamoo ahwaahum bighayri AAilmin faman yahdee man adalla Allahu wama lahum min nasireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 30, "So direct your face toward the religion, inclining to truth. [Adhere to] the fitrah of Allah upon which He has created [all] people. No change should there be in the creation of Allah. That is the correct religion, but most of the people do not know", "فَأَقِمۡ وَجۡهَكَ لِلدِّينِ حَنِيفٗاۚ فِطۡرَتَ ٱللَّهِ ٱلَّتِي فَطَرَ ٱلنَّاسَ عَلَيۡهَاۚ لَا تَبۡدِيلَ لِخَلۡقِ ٱللَّهِۚ ذَٰلِكَ ٱلدِّينُ ٱلۡقَيِّمُ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَعۡلَمُونَ", "Faaqim wajhaka lilddeeni haneefan fitrata Allahi allatee fatara alnnasa AAalayha la tabdeela likhalqi Allahi thalika alddeenu alqayyimu walakinna akthara alnnasi la yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 31, "[Adhere to it], turning in repentance to Him, and fear Him and establish prayer and do not be of those who associate others with Allah", "۞مُنِيبِينَ إِلَيۡهِ وَٱتَّقُوهُ وَأَقِيمُواْ ٱلصَّلَوٰةَ وَلَا تَكُونُواْ مِنَ ٱلۡمُشۡرِكِينَ", "Muneebeena ilayhi waittaqoohu waaqeemoo alssalata wala takoonoo mina almushrikeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 32, "[Or] of those who have divided their religion and become sects, every faction rejoicing in what it has", "مِنَ ٱلَّذِينَ فَرَّقُواْ دِينَهُمۡ وَكَانُواْ شِيَعٗاۖ كُلُّ حِزۡبِۭ بِمَا لَدَيۡهِمۡ فَرِحُونَ", "Mina allatheena farraqoo deenahum wakanoo shiyaAAan kullu hizbin bima ladayhim farihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 33, "And when adversity touches the people, they call upon their Lord, turning in repentance to Him. Then when He lets them taste mercy from Him, at once a party of them associate others with their Lord", "وَإِذَا مَسَّ ٱلنَّاسَ ضُرّٞ دَعَوۡاْ رَبَّهُم مُّنِيبِينَ إِلَيۡهِ ثُمَّ إِذَآ أَذَاقَهُم مِّنۡهُ رَحۡمَةً إِذَا فَرِيقٞ مِّنۡهُم بِرَبِّهِمۡ يُشۡرِكُونَ", "Waitha massa alnnasa durrun daAAaw rabbahum muneebeena ilayhi thumma itha athaqahum minhu rahmatan itha fareequn minhum birabbihim yushrikoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 34, "So that they will deny what We have granted them. Then enjoy yourselves, for you are going to know", "لِيَكۡفُرُواْ بِمَآ ءَاتَيۡنَٰهُمۡۚ فَتَمَتَّعُواْ فَسَوۡفَ تَعۡلَمُونَ", "Liyakfuroo bima ataynahum fatamattaAAoo fasawfa taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 35, "Or have We sent down to them an authority, and it speaks of what they were associating with Him", "أَمۡ أَنزَلۡنَا عَلَيۡهِمۡ سُلۡطَٰنٗا فَهُوَ يَتَكَلَّمُ بِمَا كَانُواْ بِهِۦ يُشۡرِكُونَ", "Am anzalna AAalayhim sultanan fahuwa yatakallamu bima kanoo bihi yushrikoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 36, "And when We let the people taste mercy, they rejoice therein, but if evil afflicts them for what their hands have put forth, immediately they despair", "وَإِذَآ أَذَقۡنَا ٱلنَّاسَ رَحۡمَةٗ فَرِحُواْ بِهَاۖ وَإِن تُصِبۡهُمۡ سَيِّئَةُۢ بِمَا قَدَّمَتۡ أَيۡدِيهِمۡ إِذَا هُمۡ يَقۡنَطُونَ", "Waitha athaqna alnnasa rahmatan farihoo biha wain tusibhum sayyiatun bima qaddamat aydeehim itha hum yaqnatoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 37, "Do they not see that Allah extends provision for whom He wills and restricts [it]? Indeed, in that are signs for a people who believe", "أَوَلَمۡ يَرَوۡاْ أَنَّ ٱللَّهَ يَبۡسُطُ ٱلرِّزۡقَ لِمَن يَشَآءُ وَيَقۡدِرُۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يُؤۡمِنُونَ", "Awalam yaraw anna Allaha yabsutu alrrizqa liman yashao wayaqdiru inna fee thalika laayatin liqawmin yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 38, "So give the relative his right, as well as the needy and the traveler. That is best for those who desire the countenance of Allah, and it is they who will be the successful", "فَـَٔاتِ ذَا ٱلۡقُرۡبَىٰ حَقَّهُۥ وَٱلۡمِسۡكِينَ وَٱبۡنَ ٱلسَّبِيلِۚ ذَٰلِكَ خَيۡرٞ لِّلَّذِينَ يُرِيدُونَ وَجۡهَ ٱللَّهِۖ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُفۡلِحُونَ", "Faati tha alqurba haqqahu waalmiskeena waibna alssabeeli thalika khayrun lillatheena yureedoona wajha Allahi waolaika humu almuflihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 39, "And whatever you give for interest to increase within the wealth of people will not increase with Allah. But what you give in zakah, desiring the countenance of Allah - those are the multipliers", "وَمَآ ءَاتَيۡتُم مِّن رِّبٗا لِّيَرۡبُوَاْ فِيٓ أَمۡوَٰلِ ٱلنَّاسِ فَلَا يَرۡبُواْ عِندَ ٱللَّهِۖ وَمَآ ءَاتَيۡتُم مِّن زَكَوٰةٖ تُرِيدُونَ وَجۡهَ ٱللَّهِ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُضۡعِفُونَ", "Wama ataytum min riban liyarbuwa fee amwali alnnasi fala yarboo AAinda Allahi wama ataytum min zakatin tureedoona wajha Allahi faolaika humu almudAAifoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 40, "Allah is the one who created you, then provided for you, then will cause you to die, and then will give you life. Are there any of your ""partners"" who does anything of that? Exalted is He and high above what they associate with Him", "ٱللَّهُ ٱلَّذِي خَلَقَكُمۡ ثُمَّ رَزَقَكُمۡ ثُمَّ يُمِيتُكُمۡ ثُمَّ يُحۡيِيكُمۡۖ هَلۡ مِن شُرَكَآئِكُم مَّن يَفۡعَلُ مِن ذَٰلِكُم مِّن شَيۡءٖۚ سُبۡحَٰنَهُۥ وَتَعَٰلَىٰ عَمَّا يُشۡرِكُونَ", "Allahu allathee khalaqakum thumma razaqakum thumma yumeetukum thumma yuhyeekum hal min shurakaikum man yafAAalu min thalikum min shayin subhanahu wataAAala AAamma yushrikoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 41, "Corruption has appeared throughout the land and sea by [reason of] what the hands of people have earned so He may let them taste part of [the consequence of] what they have done that perhaps they will return [to righteousness]", "ظَهَرَ ٱلۡفَسَادُ فِي ٱلۡبَرِّ وَٱلۡبَحۡرِ بِمَا كَسَبَتۡ أَيۡدِي ٱلنَّاسِ لِيُذِيقَهُم بَعۡضَ ٱلَّذِي عَمِلُواْ لَعَلَّهُمۡ يَرۡجِعُونَ", "Thahara alfasadu fee albarri waalbahri bima kasabat aydee alnnasi liyutheeqahum baAAda allathee AAamiloo laAAallahum yarjiAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 42, "Say, [O Muhammad], ""Travel through the land and observe how was the end of those before. Most of them were associators [of others with Allah]", "قُلۡ سِيرُواْ فِي ٱلۡأَرۡضِ فَٱنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلَّذِينَ مِن قَبۡلُۚ كَانَ أَكۡثَرُهُم مُّشۡرِكِينَ", "Qul seeroo fee alardi faonthuroo kayfa kana AAaqibatu allatheena min qablu kana aktharuhum mushrikeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 43, "So direct your face toward the correct religion before a Day comes from Allah of which there is no repelling. That Day, they will be divided", "فَأَقِمۡ وَجۡهَكَ لِلدِّينِ ٱلۡقَيِّمِ مِن قَبۡلِ أَن يَأۡتِيَ يَوۡمٞ لَّا مَرَدَّ لَهُۥ مِنَ ٱللَّهِۖ يَوۡمَئِذٖ يَصَّدَّعُونَ", "Faaqim wajhaka lilddeeni alqayyimi min qabli an yatiya yawmun la maradda lahu mina Allahi yawmaithin yassaddaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 44, "Whoever disbelieves - upon him is [the consequence of] his disbelief. And whoever does righteousness - they are for themselves preparing", "مَن كَفَرَ فَعَلَيۡهِ كُفۡرُهُۥۖ وَمَنۡ عَمِلَ صَٰلِحٗا فَلِأَنفُسِهِمۡ يَمۡهَدُونَ", "Man kafara faAAalayhi kufruhu waman AAamila salihan falianfusihim yamhadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 45, "That He may reward those who have believed and done righteous deeds out of His bounty. Indeed, He does not like the disbelievers", "لِيَجۡزِيَ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ مِن فَضۡلِهِۦٓۚ إِنَّهُۥ لَا يُحِبُّ ٱلۡكَٰفِرِينَ", "Liyajziya allatheena amanoo waAAamiloo alssalihati min fadlihi innahu la yuhibbu alkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 46, "And of His signs is that He sends the winds as bringers of good tidings and to let you taste His mercy and so the ships may sail at His command and so you may seek of His bounty, and perhaps you will be grateful", "وَمِنۡ ءَايَٰتِهِۦٓ أَن يُرۡسِلَ ٱلرِّيَاحَ مُبَشِّرَٰتٖ وَلِيُذِيقَكُم مِّن رَّحۡمَتِهِۦ وَلِتَجۡرِيَ ٱلۡفُلۡكُ بِأَمۡرِهِۦ وَلِتَبۡتَغُواْ مِن فَضۡلِهِۦ وَلَعَلَّكُمۡ تَشۡكُرُونَ", "Wamin ayatihi an yursila alrriyaha mubashshiratin waliyutheeqakum min rahmatihi walitajriya alfulku biamrihi walitabtaghoo min fadlihi walaAAallakum tashkuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 47, "And We have already sent messengers before you to their peoples, and they came to them with clear evidences; then We took retribution from those who committed crimes, and incumbent upon Us was support of the believers", "وَلَقَدۡ أَرۡسَلۡنَا مِن قَبۡلِكَ رُسُلًا إِلَىٰ قَوۡمِهِمۡ فَجَآءُوهُم بِٱلۡبَيِّنَٰتِ فَٱنتَقَمۡنَا مِنَ ٱلَّذِينَ أَجۡرَمُواْۖ وَكَانَ حَقًّا عَلَيۡنَا نَصۡرُ ٱلۡمُؤۡمِنِينَ", "Walaqad arsalna min qablika rusulan ila qawmihim fajaoohum bialbayyinati faintaqamna mina allatheena ajramoo wakana haqqan AAalayna nasru almumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 48, "It is Allah who sends the winds, and they stir the clouds and spread them in the sky however He wills, and He makes them fragments so you see the rain emerge from within them. And when He causes it to fall upon whom He wills of His servants, immediately they rejoice", "ٱللَّهُ ٱلَّذِي يُرۡسِلُ ٱلرِّيَٰحَ فَتُثِيرُ سَحَابٗا فَيَبۡسُطُهُۥ فِي ٱلسَّمَآءِ كَيۡفَ يَشَآءُ وَيَجۡعَلُهُۥ كِسَفٗا فَتَرَى ٱلۡوَدۡقَ يَخۡرُجُ مِنۡ خِلَٰلِهِۦۖ فَإِذَآ أَصَابَ بِهِۦ مَن يَشَآءُ مِنۡ عِبَادِهِۦٓ إِذَا هُمۡ يَسۡتَبۡشِرُونَ", "Allahu allathee yursilu alrriyaha fatutheeru sahaban fayabsutuhu fee alssamai kayfa yashao wayajAAaluhu kisafan fatara alwadqa yakhruju min khilalihi faitha asaba bihi man yashao min AAibadihi itha hum yastabshiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 49, "Although they were, before it was sent down upon them - before that, in despair", "وَإِن كَانُواْ مِن قَبۡلِ أَن يُنَزَّلَ عَلَيۡهِم مِّن قَبۡلِهِۦ لَمُبۡلِسِينَ", "Wain kanoo min qabli an yunazzala AAalayhim min qablihi lamubliseena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 50, "So observe the effects of the mercy of Allah - how He gives life to the earth after its lifelessness. Indeed, that [same one] will give life to the dead, and He is over all things competent", "فَٱنظُرۡ إِلَىٰٓ ءَاثَٰرِ رَحۡمَتِ ٱللَّهِ كَيۡفَ يُحۡيِ ٱلۡأَرۡضَ بَعۡدَ مَوۡتِهَآۚ إِنَّ ذَٰلِكَ لَمُحۡيِ ٱلۡمَوۡتَىٰۖ وَهُوَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ", "Faonthur ila athari rahmati Allahi kayfa yuhyee alarda baAAda mawtiha inna thalika lamuhyee almawta wahuwa AAala kulli shayin qadeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 51, "But if We should send a [bad] wind and they saw [their crops] turned yellow, they would remain thereafter disbelievers", "وَلَئِنۡ أَرۡسَلۡنَا رِيحٗا فَرَأَوۡهُ مُصۡفَرّٗا لَّظَلُّواْ مِنۢ بَعۡدِهِۦ يَكۡفُرُونَ", "Walain arsalna reehan faraawhu musfarran lathalloo min baAAdihi yakfuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 52, "So indeed, you will not make the dead hear, nor will you make the deaf hear the call when they turn their backs, retreating", "فَإِنَّكَ لَا تُسۡمِعُ ٱلۡمَوۡتَىٰ وَلَا تُسۡمِعُ ٱلصُّمَّ ٱلدُّعَآءَ إِذَا وَلَّوۡاْ مُدۡبِرِينَ", "Fainnaka la tusmiAAu almawta wala tusmiAAu alssumma aldduAAaa itha wallaw mudbireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 53, "And you cannot guide the blind away from their error. You will only make hear those who believe in Our verses so they are Muslims [in submission to Allah]", "وَمَآ أَنتَ بِهَٰدِ ٱلۡعُمۡيِ عَن ضَلَٰلَتِهِمۡۖ إِن تُسۡمِعُ إِلَّا مَن يُؤۡمِنُ بِـَٔايَٰتِنَا فَهُم مُّسۡلِمُونَ", "Wama anta bihadi alAAumyi AAan dalalatihim in tusmiAAu illa man yuminu biayatina fahum muslimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 54, "Allah is the one who created you from weakness, then made after weakness strength, then made after strength weakness and white hair. He creates what He wills, and He is the Knowing, the Competent", "۞ٱللَّهُ ٱلَّذِي خَلَقَكُم مِّن ضَعۡفٖ ثُمَّ جَعَلَ مِنۢ بَعۡدِ ضَعۡفٖ قُوَّةٗ ثُمَّ جَعَلَ مِنۢ بَعۡدِ قُوَّةٖ ضَعۡفٗا وَشَيۡبَةٗۚ يَخۡلُقُ مَا يَشَآءُۚ وَهُوَ ٱلۡعَلِيمُ ٱلۡقَدِيرُ", "Allahu allathee khalaqakum min daAAfin thumma jaAAala min baAAdi daAAfin quwwatan thumma jaAAala min baAAdi quwwatin daAAfan washaybatan yakhluqu ma yashao wahuwa alAAaleemu alqadeeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 55, "And the Day the Hour appears the criminals will swear they had remained but an hour. Thus they were deluded", "وَيَوۡمَ تَقُومُ ٱلسَّاعَةُ يُقۡسِمُ ٱلۡمُجۡرِمُونَ مَا لَبِثُواْ غَيۡرَ سَاعَةٖۚ كَذَٰلِكَ كَانُواْ يُؤۡفَكُونَ", "Wayawma taqoomu alssaAAatu yuqsimu almujrimoona ma labithoo ghayra saAAatin kathalika kanoo yufakoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 56, "But those who were given knowledge and faith will say, ""You remained the extent of Allah 's decree until the Day of Resurrection, and this is the Day of Resurrection, but you did not used to know", "وَقَالَ ٱلَّذِينَ أُوتُواْ ٱلۡعِلۡمَ وَٱلۡإِيمَٰنَ لَقَدۡ لَبِثۡتُمۡ فِي كِتَٰبِ ٱللَّهِ إِلَىٰ يَوۡمِ ٱلۡبَعۡثِۖ فَهَٰذَا يَوۡمُ ٱلۡبَعۡثِ وَلَٰكِنَّكُمۡ كُنتُمۡ لَا تَعۡلَمُونَ", "Waqala allatheena ootoo alAAilma waaleemana laqad labithtum fee kitabi Allahi ila yawmi albaAAthi fahatha yawmu albaAAthi walakinnakum kuntum la taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 57, "So that Day, their excuse will not benefit those who wronged, nor will they be asked to appease [Allah]", "فَيَوۡمَئِذٖ لَّا يَنفَعُ ٱلَّذِينَ ظَلَمُواْ مَعۡذِرَتُهُمۡ وَلَا هُمۡ يُسۡتَعۡتَبُونَ", "Fayawmaithin la yanfaAAu allatheena thalamoo maAAthiratuhum wala hum yustaAAtaboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 58, "And We have certainly presented to the people in this Qur'an from every [kind of] example. But, [O Muhammad], if you should bring them a sign, the disbelievers will surely say, ""You [believers] are but falsifiers", "وَلَقَدۡ ضَرَبۡنَا لِلنَّاسِ فِي هَٰذَا ٱلۡقُرۡءَانِ مِن كُلِّ مَثَلٖۚ وَلَئِن جِئۡتَهُم بِـَٔايَةٖ لَّيَقُولَنَّ ٱلَّذِينَ كَفَرُوٓاْ إِنۡ أَنتُمۡ إِلَّا مُبۡطِلُونَ", "Walaqad darabna lilnnasi fee hatha alqurani min kulli mathalin walain jitahum biayatin layaqoolanna allatheena kafaroo in antum illa mubtiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 59, "Thus does Allah seal the hearts of those who do not know", "كَذَٰلِكَ يَطۡبَعُ ٱللَّهُ عَلَىٰ قُلُوبِ ٱلَّذِينَ لَا يَعۡلَمُونَ", "Kathalika yatbaAAu Allahu AAala quloobi allatheena la yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (30, 60, "So be patient. Indeed, the promise of Allah is truth. And let them not disquiet you who are not certain [in faith]", "فَٱصۡبِرۡ إِنَّ وَعۡدَ ٱللَّهِ حَقّٞۖ وَلَا يَسۡتَخِفَّنَّكَ ٱلَّذِينَ لَا يُوقِنُونَ", "Faisbir inna waAAda Allahi haqqun wala yastakhiffannaka allatheena la yooqinoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (31, 1, "Alif, Lam, Meem", "الٓمٓ", "Aliflammeem");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (31, 2, "These are verses of the wise Book", "تِلۡكَ ءَايَٰتُ ٱلۡكِتَٰبِ ٱلۡحَكِيمِ", "Tilka ayatu alkitabi alhakeemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (31, 3, "As guidance and mercy for the doers of good", "هُدٗى وَرَحۡمَةٗ لِّلۡمُحۡسِنِينَ", "Hudan warahmatan lilmuhsineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (31, 4, "Who establish prayer and give zakah, and they, of the Hereafter, are certain [in faith]", "ٱلَّذِينَ يُقِيمُونَ ٱلصَّلَوٰةَ وَيُؤۡتُونَ ٱلزَّكَوٰةَ وَهُم بِٱلۡأٓخِرَةِ هُمۡ يُوقِنُونَ", "Allatheena yuqeemoona alssalata wayutoona alzzakata wahum bialakhirati hum yooqinoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (31, 5, "Those are on [right] guidance from their Lord, and it is those who are the successful", "أُوْلَـٰٓئِكَ عَلَىٰ هُدٗى مِّن رَّبِّهِمۡۖ وَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُفۡلِحُونَ", "Olaika AAala hudan min rabbihim waolaika humu almuflihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (31, 6, "And of the people is he who buys the amusement of speech to mislead [others] from the way of Allah without knowledge and who takes it in ridicule. Those will have a humiliating punishment", "وَمِنَ ٱلنَّاسِ مَن يَشۡتَرِي لَهۡوَ ٱلۡحَدِيثِ لِيُضِلَّ عَن سَبِيلِ ٱللَّهِ بِغَيۡرِ عِلۡمٖ وَيَتَّخِذَهَا هُزُوًاۚ أُوْلَـٰٓئِكَ لَهُمۡ عَذَابٞ مُّهِينٞ", "Wamina alnnasi man yashtaree lahwa alhadeethi liyudilla AAan sabeeli Allahi bighayri AAilmin wayattakhithaha huzuwan olaika lahum AAathabun muheenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (31, 7, "And when our verses are recited to him, he turns away arrogantly as if he had not heard them, as if there was in his ears deafness. So give him tidings of a painful punishment", "وَإِذَا تُتۡلَىٰ عَلَيۡهِ ءَايَٰتُنَا وَلَّىٰ مُسۡتَكۡبِرٗا كَأَن لَّمۡ يَسۡمَعۡهَا كَأَنَّ فِيٓ أُذُنَيۡهِ وَقۡرٗاۖ فَبَشِّرۡهُ بِعَذَابٍ أَلِيمٍ", "Waitha tutla AAalayhi ayatuna walla mustakbiran kaan lam yasmaAAha kaanna fee othunayhi waqran fabashshirhu biAAathabin aleemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (31, 8, "Indeed, those who believe and do righteous deeds - for them are the Gardens of Pleasure", "إِنَّ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ لَهُمۡ جَنَّـٰتُ ٱلنَّعِيمِ", "Inna allatheena amanoo waAAamiloo alssalihati lahum jannatu alnnaAAeemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (31, 9, "Wherein they abide eternally; [it is] the promise of Allah [which is] truth. And He is the Exalted in Might, the Wise", "خَٰلِدِينَ فِيهَاۖ وَعۡدَ ٱللَّهِ حَقّٗاۚ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ", "Khalideena feeha waAAda Allahi haqqan wahuwa alAAazeezu alhakeemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (31, 10, "He created the heavens without pillars that you see and has cast into the earth firmly set mountains, lest it should shift with you, and dispersed therein from every creature. And We sent down rain from the sky and made grow therein [plants] of every noble kind", "خَلَقَ ٱلسَّمَٰوَٰتِ بِغَيۡرِ عَمَدٖ تَرَوۡنَهَاۖ وَأَلۡقَىٰ فِي ٱلۡأَرۡضِ رَوَٰسِيَ أَن تَمِيدَ بِكُمۡ وَبَثَّ فِيهَا مِن كُلِّ دَآبَّةٖۚ وَأَنزَلۡنَا مِنَ ٱلسَّمَآءِ مَآءٗ فَأَنۢبَتۡنَا فِيهَا مِن كُلِّ زَوۡجٖ كَرِيمٍ", "Khalaqa alssamawati bighayri AAamadin tarawnaha waalqa fee alardi rawasiya an tameeda bikum wabaththa feeha min kulli dabbatin waanzalna mina alssamai maan faanbatna feeha min kulli zawjin kareemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (31, 11, "This is the creation of Allah. So show Me what those other than Him have created. Rather, the wrongdoers are in clear error", "هَٰذَا خَلۡقُ ٱللَّهِ فَأَرُونِي مَاذَا خَلَقَ ٱلَّذِينَ مِن دُونِهِۦۚ بَلِ ٱلظَّـٰلِمُونَ فِي ضَلَٰلٖ مُّبِينٖ", "Hatha khalqu Allahi faaroonee matha khalaqa allatheena min doonihi bali alththalimoona fee dalalin mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (31, 12, "And We had certainly given Luqman wisdom [and said], ""Be grateful to Allah."" And whoever is grateful is grateful for [the benefit of] himself. And whoever denies [His favor] - then indeed, Allah is Free of need and Praiseworthy", "وَلَقَدۡ ءَاتَيۡنَا لُقۡمَٰنَ ٱلۡحِكۡمَةَ أَنِ ٱشۡكُرۡ لِلَّهِۚ وَمَن يَشۡكُرۡ فَإِنَّمَا يَشۡكُرُ لِنَفۡسِهِۦۖ وَمَن كَفَرَ فَإِنَّ ٱللَّهَ غَنِيٌّ حَمِيدٞ", "Walaqad atayna luqmana alhikmata ani oshkur lillahi waman yashkur fainnama yashkuru linafsihi waman kafara fainna Allaha ghaniyyun hameedun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (31, 13, "And [mention, O Muhammad], when Luqman said to his son while he was instructing him, ""O my son, do not associate [anything] with Allah. Indeed, association [with him] is great injustice", "وَإِذۡ قَالَ لُقۡمَٰنُ لِٱبۡنِهِۦ وَهُوَ يَعِظُهُۥ يَٰبُنَيَّ لَا تُشۡرِكۡ بِٱللَّهِۖ إِنَّ ٱلشِّرۡكَ لَظُلۡمٌ عَظِيمٞ", "Waith qala luqmanu liibnihi wahuwa yaAAithuhu ya bunayya la tushrik biAllahi inna alshshirka lathulmun AAatheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (31, 14, "And We have enjoined upon man [care] for his parents. His mother carried him, [increasing her] in weakness upon weakness, and his weaning is in two years. Be grateful to Me and to your parents; to Me is the [final] destination", "وَوَصَّيۡنَا ٱلۡإِنسَٰنَ بِوَٰلِدَيۡهِ حَمَلَتۡهُ أُمُّهُۥ وَهۡنًا عَلَىٰ وَهۡنٖ وَفِصَٰلُهُۥ فِي عَامَيۡنِ أَنِ ٱشۡكُرۡ لِي وَلِوَٰلِدَيۡكَ إِلَيَّ ٱلۡمَصِيرُ", "Wawassayna alinsana biwalidayhi hamalathu ommuhu wahnan AAala wahnin wafisaluhu fee AAamayni ani oshkur lee waliwalidayka ilayya almaseeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (31, 15, "But if they endeavor to make you associate with Me that of which you have no knowledge, do not obey them but accompany them in [this] world with appropriate kindness and follow the way of those who turn back to Me [in repentance]. Then to Me will be your return, and I will inform you about what you used to do", "وَإِن جَٰهَدَاكَ عَلَىٰٓ أَن تُشۡرِكَ بِي مَا لَيۡسَ لَكَ بِهِۦ عِلۡمٞ فَلَا تُطِعۡهُمَاۖ وَصَاحِبۡهُمَا فِي ٱلدُّنۡيَا مَعۡرُوفٗاۖ وَٱتَّبِعۡ سَبِيلَ مَنۡ أَنَابَ إِلَيَّۚ ثُمَّ إِلَيَّ مَرۡجِعُكُمۡ فَأُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ", "Wain jahadaka AAala an tushrika bee ma laysa laka bihi AAilmun fala tutiAAhuma wasahibhuma fee alddunya maAAroofan waittabiAA sabeela man anaba ilayya thumma ilayya marjiAAukum faonabbiokum bima kuntum taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (31, 16, "[And Luqman said], ""O my son, indeed if wrong should be the weight of a mustard seed and should be within a rock or [anywhere] in the heavens or in the earth, Allah will bring it forth. Indeed, Allah is Subtle and Acquainted", "يَٰبُنَيَّ إِنَّهَآ إِن تَكُ مِثۡقَالَ حَبَّةٖ مِّنۡ خَرۡدَلٖ فَتَكُن فِي صَخۡرَةٍ أَوۡ فِي ٱلسَّمَٰوَٰتِ أَوۡ فِي ٱلۡأَرۡضِ يَأۡتِ بِهَا ٱللَّهُۚ إِنَّ ٱللَّهَ لَطِيفٌ خَبِيرٞ", "Ya bunayya innaha in taku mithqala habbatin min khardalin fatakun fee sakhratin aw fee alssamawati aw fee alardi yati biha Allahu inna Allaha lateefun khabeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (31, 17, "O my son, establish prayer, enjoin what is right, forbid what is wrong, and be patient over what befalls you. Indeed, [all] that is of the matters [requiring] determination", "يَٰبُنَيَّ أَقِمِ ٱلصَّلَوٰةَ وَأۡمُرۡ بِٱلۡمَعۡرُوفِ وَٱنۡهَ عَنِ ٱلۡمُنكَرِ وَٱصۡبِرۡ عَلَىٰ مَآ أَصَابَكَۖ إِنَّ ذَٰلِكَ مِنۡ عَزۡمِ ٱلۡأُمُورِ", "Ya bunayya aqimi alssalata wamur bialmaAAroofi wainha AAani almunkari waisbir AAala ma asabaka inna thalika min AAazmi alomoori");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (31, 18, "And do not turn your cheek [in contempt] toward people and do not walk through the earth exultantly. Indeed, Allah does not like everyone self-deluded and boastful", "وَلَا تُصَعِّرۡ خَدَّكَ لِلنَّاسِ وَلَا تَمۡشِ فِي ٱلۡأَرۡضِ مَرَحًاۖ إِنَّ ٱللَّهَ لَا يُحِبُّ كُلَّ مُخۡتَالٖ فَخُورٖ", "Wala tusaAAAAir khaddaka lilnnasi wala tamshi fee alardi marahan inna Allaha la yuhibbu kulla mukhtalin fakhoorin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (31, 19, "And be moderate in your pace and lower your voice; indeed, the most disagreeable of sounds is the voice of donkeys", "وَٱقۡصِدۡ فِي مَشۡيِكَ وَٱغۡضُضۡ مِن صَوۡتِكَۚ إِنَّ أَنكَرَ ٱلۡأَصۡوَٰتِ لَصَوۡتُ ٱلۡحَمِيرِ", "Waiqsid fee mashyika waoghdud min sawtika inna ankara alaswati lasawtu alhameeri");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (31, 20, "Do you not see that Allah has made subject to you whatever is in the heavens and whatever is in the earth and amply bestowed upon you His favors, [both] apparent and unapparent? But of the people is he who disputes about Allah without knowledge or guidance or an enlightening Book [from Him]", "أَلَمۡ تَرَوۡاْ أَنَّ ٱللَّهَ سَخَّرَ لَكُم مَّا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِ وَأَسۡبَغَ عَلَيۡكُمۡ نِعَمَهُۥ ظَٰهِرَةٗ وَبَاطِنَةٗۗ وَمِنَ ٱلنَّاسِ مَن يُجَٰدِلُ فِي ٱللَّهِ بِغَيۡرِ عِلۡمٖ وَلَا هُدٗى وَلَا كِتَٰبٖ مُّنِيرٖ", "Alam taraw anna Allaha sakhkhara lakum ma fee alssamawati wama fee alardi waasbagha AAalaykum niAAamahu thahiratan wabatinatan wamina alnnasi man yujadilu fee Allahi bighayri AAilmin wala hudan wala kitabin muneerin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (31, 21, "And when it is said to them, ""Follow what Allah has revealed,"" they say, ""Rather, we will follow that upon which we found our fathers."" Even if Satan was inviting them to the punishment of the Blaze", "وَإِذَا قِيلَ لَهُمُ ٱتَّبِعُواْ مَآ أَنزَلَ ٱللَّهُ قَالُواْ بَلۡ نَتَّبِعُ مَا وَجَدۡنَا عَلَيۡهِ ءَابَآءَنَآۚ أَوَلَوۡ كَانَ ٱلشَّيۡطَٰنُ يَدۡعُوهُمۡ إِلَىٰ عَذَابِ ٱلسَّعِيرِ", "Waitha qeela lahumu ittabiAAoo ma anzala Allahu qaloo bal nattabiAAu ma wajadna AAalayhi abaana awalaw kana alshshaytanu yadAAoohum ila AAathabi alssaAAeeri");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (31, 22, "And whoever submits his face to Allah while he is a doer of good - then he has grasped the most trustworthy handhold. And to Allah will be the outcome of [all] matters", "۞وَمَن يُسۡلِمۡ وَجۡهَهُۥٓ إِلَى ٱللَّهِ وَهُوَ مُحۡسِنٞ فَقَدِ ٱسۡتَمۡسَكَ بِٱلۡعُرۡوَةِ ٱلۡوُثۡقَىٰۗ وَإِلَى ٱللَّهِ عَٰقِبَةُ ٱلۡأُمُورِ", "Waman yuslim wajhahu ila Allahi wahuwa muhsinun faqadi istamsaka bialAAurwati alwuthqa waila Allahi AAaqibatu alomoori");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (31, 23, "And whoever has disbelieved - let not his disbelief grieve you. To Us is their return, and We will inform them of what they did. Indeed, Allah is Knowing of that within the breasts", "وَمَن كَفَرَ فَلَا يَحۡزُنكَ كُفۡرُهُۥٓۚ إِلَيۡنَا مَرۡجِعُهُمۡ فَنُنَبِّئُهُم بِمَا عَمِلُوٓاْۚ إِنَّ ٱللَّهَ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ", "Waman kafara fala yahzunka kufruhu ilayna marjiAAuhum fanunabbiohum bima AAamiloo inna Allaha AAaleemun bithati alssudoori");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (31, 24, "We grant them enjoyment for a little; then We will force them to a massive punishment", "نُمَتِّعُهُمۡ قَلِيلٗا ثُمَّ نَضۡطَرُّهُمۡ إِلَىٰ عَذَابٍ غَلِيظٖ", "NumattiAAuhum qaleelan thumma nadtarruhum ila AAathabin ghaleethin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (31, 25, "And if you asked them, ""Who created the heavens and earth?"" they would surely say, ""Allah."" Say, ""[All] praise is [due] to Allah ""; but most of them do not know", "وَلَئِن سَأَلۡتَهُم مَّنۡ خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ لَيَقُولُنَّ ٱللَّهُۚ قُلِ ٱلۡحَمۡدُ لِلَّهِۚ بَلۡ أَكۡثَرُهُمۡ لَا يَعۡلَمُونَ", "Walain saaltahum man khalaqa alssamawati waalarda layaqoolunna Allahu quli alhamdu lillahi bal aktharuhum la yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (31, 26, "To Allah belongs whatever is in the heavens and earth. Indeed, Allah is the Free of need, the Praiseworthy", "لِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ إِنَّ ٱللَّهَ هُوَ ٱلۡغَنِيُّ ٱلۡحَمِيدُ", "Lillahi ma fee alssamawati waalardi inna Allaha huwa alghaniyyu alhameedu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (31, 27, "And if whatever trees upon the earth were pens and the sea [was ink], replenished thereafter by seven [more] seas, the words of Allah would not be exhausted. Indeed, Allah is Exalted in Might and Wise", "وَلَوۡ أَنَّمَا فِي ٱلۡأَرۡضِ مِن شَجَرَةٍ أَقۡلَٰمٞ وَٱلۡبَحۡرُ يَمُدُّهُۥ مِنۢ بَعۡدِهِۦ سَبۡعَةُ أَبۡحُرٖ مَّا نَفِدَتۡ كَلِمَٰتُ ٱللَّهِۚ إِنَّ ٱللَّهَ عَزِيزٌ حَكِيمٞ", "Walaw annama fee alardi min shajaratin aqlamun waalbahru yamudduhu min baAAdihi sabAAatu abhurin ma nafidat kalimatu Allahi inna Allaha AAazeezun hakeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (31, 28, "Your creation and your resurrection will not be but as that of a single soul. Indeed, Allah is Hearing and Seeing", "مَّا خَلۡقُكُمۡ وَلَا بَعۡثُكُمۡ إِلَّا كَنَفۡسٖ وَٰحِدَةٍۚ إِنَّ ٱللَّهَ سَمِيعُۢ بَصِيرٌ", "Ma khalqukum wala baAAthukum illa kanafsin wahidatin inna Allaha sameeAAun baseerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (31, 29, "Do you not see that Allah causes the night to enter the day and causes the day to enter the night and has subjected the sun and the moon, each running [its course] for a specified term, and that Allah, with whatever you do, is Acquainted", "أَلَمۡ تَرَ أَنَّ ٱللَّهَ يُولِجُ ٱلَّيۡلَ فِي ٱلنَّهَارِ وَيُولِجُ ٱلنَّهَارَ فِي ٱلَّيۡلِ وَسَخَّرَ ٱلشَّمۡسَ وَٱلۡقَمَرَۖ كُلّٞ يَجۡرِيٓ إِلَىٰٓ أَجَلٖ مُّسَمّٗى وَأَنَّ ٱللَّهَ بِمَا تَعۡمَلُونَ خَبِيرٞ", "Alam tara anna Allaha yooliju allayla fee alnnahari wayooliju alnnahara fee allayli wasakhkhara alshshamsa waalqamara kullun yajree ila ajalin musamman waanna Allaha bima taAAmaloona khabeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (31, 30, "That is because Allah is the Truth, and that what they call upon other than Him is falsehood, and because Allah is the Most High, the Grand", "ذَٰلِكَ بِأَنَّ ٱللَّهَ هُوَ ٱلۡحَقُّ وَأَنَّ مَا يَدۡعُونَ مِن دُونِهِ ٱلۡبَٰطِلُ وَأَنَّ ٱللَّهَ هُوَ ٱلۡعَلِيُّ ٱلۡكَبِيرُ", "Thalika bianna Allaha huwa alhaqqu waanna ma yadAAoona min doonihi albatilu waanna Allaha huwa alAAaliyyu alkabeeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (31, 31, "Do you not see that ships sail through the sea by the favor of Allah that He may show you of His signs? Indeed in that are signs for everyone patient and grateful", "أَلَمۡ تَرَ أَنَّ ٱلۡفُلۡكَ تَجۡرِي فِي ٱلۡبَحۡرِ بِنِعۡمَتِ ٱللَّهِ لِيُرِيَكُم مِّنۡ ءَايَٰتِهِۦٓۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّكُلِّ صَبَّارٖ شَكُورٖ", "Alam tara anna alfulka tajree fee albahri biniAAmati Allahi liyuriyakum min ayatihi inna fee thalika laayatin likulli sabbarin shakoorin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (31, 32, "And when waves come over them like canopies, they supplicate Allah, sincere to Him in religion. But when He delivers them to the land, there are [some] of them who are moderate [in faith]. And none rejects Our signs except everyone treacherous and ungrateful", "وَإِذَا غَشِيَهُم مَّوۡجٞ كَٱلظُّلَلِ دَعَوُاْ ٱللَّهَ مُخۡلِصِينَ لَهُ ٱلدِّينَ فَلَمَّا نَجَّىٰهُمۡ إِلَى ٱلۡبَرِّ فَمِنۡهُم مُّقۡتَصِدٞۚ وَمَا يَجۡحَدُ بِـَٔايَٰتِنَآ إِلَّا كُلُّ خَتَّارٖ كَفُورٖ", "Waitha ghashiyahum mawjun kaalththulali daAAawoo Allaha mukhliseena lahu alddeena falamma najjahum ila albarri faminhum muqtasidun wama yajhadu biayatina illa kullu khattarin kafoorin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (31, 33, "O mankind, fear your Lord and fear a Day when no father will avail his son, nor will a son avail his father at all. Indeed, the promise of Allah is truth, so let not the worldly life delude you and be not deceived about Allah by the Deceiver", "يَـٰٓأَيُّهَا ٱلنَّاسُ ٱتَّقُواْ رَبَّكُمۡ وَٱخۡشَوۡاْ يَوۡمٗا لَّا يَجۡزِي وَالِدٌ عَن وَلَدِهِۦ وَلَا مَوۡلُودٌ هُوَ جَازٍ عَن وَالِدِهِۦ شَيۡـًٔاۚ إِنَّ وَعۡدَ ٱللَّهِ حَقّٞۖ فَلَا تَغُرَّنَّكُمُ ٱلۡحَيَوٰةُ ٱلدُّنۡيَا وَلَا يَغُرَّنَّكُم بِٱللَّهِ ٱلۡغَرُورُ", "Ya ayyuha alnnasu ittaqoo rabbakum waikhshaw yawman la yajzee walidun AAan waladihi wala mawloodun huwa jazin AAan walidihi shayan inna waAAda Allahi haqqun fala taghurrannakumu alhayatu alddunya wala yaghurrannakum biAllahi algharooru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (31, 34, "Indeed, Allah [alone] has knowledge of the Hour and sends down the rain and knows what is in the wombs. And no soul perceives what it will earn tomorrow, and no soul perceives in what land it will die. Indeed, Allah is Knowing and Acquainted", "إِنَّ ٱللَّهَ عِندَهُۥ عِلۡمُ ٱلسَّاعَةِ وَيُنَزِّلُ ٱلۡغَيۡثَ وَيَعۡلَمُ مَا فِي ٱلۡأَرۡحَامِۖ وَمَا تَدۡرِي نَفۡسٞ مَّاذَا تَكۡسِبُ غَدٗاۖ وَمَا تَدۡرِي نَفۡسُۢ بِأَيِّ أَرۡضٖ تَمُوتُۚ إِنَّ ٱللَّهَ عَلِيمٌ خَبِيرُۢ", "Inna Allaha AAindahu AAilmu alssaAAati wayunazzilu alghaytha wayaAAlamu ma fee alarhami wama tadree nafsun matha taksibu ghadan wama tadree nafsun biayyi ardin tamootu inna Allaha AAaleemun khabeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (32, 1, "Alif, Lam, Meem", "الٓمٓ", "Aliflammeem");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (32, 2, "[This is] the revelation of the Book about which there is no doubt from the Lord of the worlds", "تَنزِيلُ ٱلۡكِتَٰبِ لَا رَيۡبَ فِيهِ مِن رَّبِّ ٱلۡعَٰلَمِينَ", "Tanzeelu alkitabi la rayba feehi min rabbi alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (32, 3, "Or do they say, ""He invented it""? Rather, it is the truth from your Lord, [O Muhammad], that you may warn a people to whom no warner has come before you [so] perhaps they will be guided", "أَمۡ يَقُولُونَ ٱفۡتَرَىٰهُۚ بَلۡ هُوَ ٱلۡحَقُّ مِن رَّبِّكَ لِتُنذِرَ قَوۡمٗا مَّآ أَتَىٰهُم مِّن نَّذِيرٖ مِّن قَبۡلِكَ لَعَلَّهُمۡ يَهۡتَدُونَ", "Am yaqooloona iftarahu bal huwa alhaqqu min rabbika litunthira qawman ma atahum min natheerin min qablika laAAallahum yahtadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (32, 4, "It is Allah who created the heavens and the earth and whatever is between them in six days; then He established Himself above the Throne. You have not besides Him any protector or any intercessor; so will you not be reminded", "ٱللَّهُ ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَمَا بَيۡنَهُمَا فِي سِتَّةِ أَيَّامٖ ثُمَّ ٱسۡتَوَىٰ عَلَى ٱلۡعَرۡشِۖ مَا لَكُم مِّن دُونِهِۦ مِن وَلِيّٖ وَلَا شَفِيعٍۚ أَفَلَا تَتَذَكَّرُونَ", "Allahu allathee khalaqa alssamawati waalarda wama baynahuma fee sittati ayyamin thumma istawa AAala alAAarshi ma lakum min doonihi min waliyyin wala shafeeAAin afala tatathakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (32, 5, "He arranges [each] matter from the heaven to the earth; then it will ascend to Him in a Day, the extent of which is a thousand years of those which you count", "يُدَبِّرُ ٱلۡأَمۡرَ مِنَ ٱلسَّمَآءِ إِلَى ٱلۡأَرۡضِ ثُمَّ يَعۡرُجُ إِلَيۡهِ فِي يَوۡمٖ كَانَ مِقۡدَارُهُۥٓ أَلۡفَ سَنَةٖ مِّمَّا تَعُدُّونَ", "Yudabbiru alamra mina alssamai ila alardi thumma yaAAruju ilayhi fee yawmin kana miqdaruhu alfa sanatin mimma taAAuddoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (32, 6, "That is the Knower of the unseen and the witnessed, the Exalted in Might, the Merciful", "ذَٰلِكَ عَٰلِمُ ٱلۡغَيۡبِ وَٱلشَّهَٰدَةِ ٱلۡعَزِيزُ ٱلرَّحِيمُ", "Thalika AAalimu alghaybi waalshshahadati alAAazeezu alrraheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (32, 7, "Who perfected everything which He created and began the creation of man from clay", "ٱلَّذِيٓ أَحۡسَنَ كُلَّ شَيۡءٍ خَلَقَهُۥۖ وَبَدَأَ خَلۡقَ ٱلۡإِنسَٰنِ مِن طِينٖ", "Allathee ahsana kulla shayin khalaqahu wabadaa khalqa alinsani min teenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (32, 8, "Then He made his posterity out of the extract of a liquid disdained", "ثُمَّ جَعَلَ نَسۡلَهُۥ مِن سُلَٰلَةٖ مِّن مَّآءٖ مَّهِينٖ", "Thumma jaAAala naslahu min sulalatin min main maheenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (32, 9, "Then He proportioned him and breathed into him from His [created] soul and made for you hearing and vision and hearts; little are you grateful", "ثُمَّ سَوَّىٰهُ وَنَفَخَ فِيهِ مِن رُّوحِهِۦۖ وَجَعَلَ لَكُمُ ٱلسَّمۡعَ وَٱلۡأَبۡصَٰرَ وَٱلۡأَفۡـِٔدَةَۚ قَلِيلٗا مَّا تَشۡكُرُونَ", "Thumma sawwahu wanafakha feehi min roohihi wajaAAala lakumu alssamAAa waalabsara waalafidata qaleelan ma tashkuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (32, 10, "And they say, ""When we are lost within the earth, will we indeed be [recreated] in a new creation?"" Rather, they are, in [the matter of] the meeting with their Lord, disbelievers", "وَقَالُوٓاْ أَءِذَا ضَلَلۡنَا فِي ٱلۡأَرۡضِ أَءِنَّا لَفِي خَلۡقٖ جَدِيدِۭۚ بَلۡ هُم بِلِقَآءِ رَبِّهِمۡ كَٰفِرُونَ", "Waqaloo aitha dalalna fee alardi ainna lafee khalqin jadeedin bal hum biliqai rabbihim kafiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (32, 11, "Say, ""The angel of death will take you who has been entrusted with you. Then to your Lord you will be returned", "۞قُلۡ يَتَوَفَّىٰكُم مَّلَكُ ٱلۡمَوۡتِ ٱلَّذِي وُكِّلَ بِكُمۡ ثُمَّ إِلَىٰ رَبِّكُمۡ تُرۡجَعُونَ", "Qul yatawaffakum malaku almawti allathee wukkila bikum thumma ila rabbikum turjaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (32, 12, "If you could but see when the criminals are hanging their heads before their Lord, [saying], ""Our Lord, we have seen and heard, so return us [to the world]; we will work righteousness. Indeed, we are [now] certain", "وَلَوۡ تَرَىٰٓ إِذِ ٱلۡمُجۡرِمُونَ نَاكِسُواْ رُءُوسِهِمۡ عِندَ رَبِّهِمۡ رَبَّنَآ أَبۡصَرۡنَا وَسَمِعۡنَا فَٱرۡجِعۡنَا نَعۡمَلۡ صَٰلِحًا إِنَّا مُوقِنُونَ", "Walaw tara ithi almujrimoona nakisoo ruoosihim AAinda rabbihim rabbana absarna wasamiAAna faarjiAAna naAAmal salihan inna mooqinoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (32, 13, "And if We had willed, We could have given every soul its guidance, but the word from Me will come into effect [that] ""I will surely fill Hell with jinn and people all together", "وَلَوۡ شِئۡنَا لَأٓتَيۡنَا كُلَّ نَفۡسٍ هُدَىٰهَا وَلَٰكِنۡ حَقَّ ٱلۡقَوۡلُ مِنِّي لَأَمۡلَأَنَّ جَهَنَّمَ مِنَ ٱلۡجِنَّةِ وَٱلنَّاسِ أَجۡمَعِينَ", "Walaw shina laatayna kulla nafsin hudaha walakin haqqa alqawlu minnee laamlaanna jahannama mina aljinnati waalnnasi ajmaAAeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (32, 14, "So taste [punishment] because you forgot the meeting of this, your Day; indeed, We have [accordingly] forgotten you. And taste the punishment of eternity for what you used to do", "فَذُوقُواْ بِمَا نَسِيتُمۡ لِقَآءَ يَوۡمِكُمۡ هَٰذَآ إِنَّا نَسِينَٰكُمۡۖ وَذُوقُواْ عَذَابَ ٱلۡخُلۡدِ بِمَا كُنتُمۡ تَعۡمَلُونَ", "Fathooqoo bima naseetum liqaa yawmikum hatha inna naseenakum wathooqoo AAathaba alkhuldi bima kuntum taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (32, 15, "Only those believe in Our verses who, when they are reminded by them, fall down in prostration and exalt [Allah] with praise of their Lord, and they are not arrogant", "إِنَّمَا يُؤۡمِنُ بِـَٔايَٰتِنَا ٱلَّذِينَ إِذَا ذُكِّرُواْ بِهَا خَرُّواْۤ سُجَّدٗاۤ وَسَبَّحُواْ بِحَمۡدِ رَبِّهِمۡ وَهُمۡ لَا يَسۡتَكۡبِرُونَ۩", "Innama yuminu biayatina allatheena itha thukkiroo biha kharroo sujjadan wasabbahoo bihamdi rabbihim wahum la yastakbiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (32, 16, "They arise from [their] beds; they supplicate their Lord in fear and aspiration, and from what We have provided them, they spend", "تَتَجَافَىٰ جُنُوبُهُمۡ عَنِ ٱلۡمَضَاجِعِ يَدۡعُونَ رَبَّهُمۡ خَوۡفٗا وَطَمَعٗا وَمِمَّا رَزَقۡنَٰهُمۡ يُنفِقُونَ", "Tatajafa junoobuhum AAani almadajiAAi yadAAoona rabbahum khawfan watamaAAan wamimma razaqnahum yunfiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (32, 17, "And no soul knows what has been hidden for them of comfort for eyes as reward for what they used to do", "فَلَا تَعۡلَمُ نَفۡسٞ مَّآ أُخۡفِيَ لَهُم مِّن قُرَّةِ أَعۡيُنٖ جَزَآءَۢ بِمَا كَانُواْ يَعۡمَلُونَ", "Fala taAAlamu nafsun ma okhfiya lahum min qurrati aAAyunin jazaan bima kanoo yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (32, 18, "Then is one who was a believer like one who was defiantly disobedient? They are not equal", "أَفَمَن كَانَ مُؤۡمِنٗا كَمَن كَانَ فَاسِقٗاۚ لَّا يَسۡتَوُۥنَ", "Afaman kana muminan kaman kana fasiqan la yastawoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (32, 19, "As for those who believed and did righteous deeds, for them will be the Gardens of Refuge as accommodation for what they used to do", "أَمَّا ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ فَلَهُمۡ جَنَّـٰتُ ٱلۡمَأۡوَىٰ نُزُلَۢا بِمَا كَانُواْ يَعۡمَلُونَ", "Amma allatheena amanoo waAAamiloo alssalihati falahum jannatu almawa nuzulan bima kanoo yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (32, 20, "But as for those who defiantly disobeyed, their refuge is the Fire. Every time they wish to emerge from it, they will be returned to it while it is said to them, ""Taste the punishment of the Fire which you used to deny", "وَأَمَّا ٱلَّذِينَ فَسَقُواْ فَمَأۡوَىٰهُمُ ٱلنَّارُۖ كُلَّمَآ أَرَادُوٓاْ أَن يَخۡرُجُواْ مِنۡهَآ أُعِيدُواْ فِيهَا وَقِيلَ لَهُمۡ ذُوقُواْ عَذَابَ ٱلنَّارِ ٱلَّذِي كُنتُم بِهِۦ تُكَذِّبُونَ", "Waamma allatheena fasaqoo famawahumu alnnaru kullama aradoo an yakhrujoo minha oAAeedoo feeha waqeela lahum thooqoo AAathaba alnnari allathee kuntum bihi tukaththiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (32, 21, "And we will surely let them taste the nearer punishment short of the greater punishment that perhaps they will repent", "وَلَنُذِيقَنَّهُم مِّنَ ٱلۡعَذَابِ ٱلۡأَدۡنَىٰ دُونَ ٱلۡعَذَابِ ٱلۡأَكۡبَرِ لَعَلَّهُمۡ يَرۡجِعُونَ", "Walanutheeqannahum mina alAAathabi aladna doona alAAathabi alakbari laAAallahum yarjiAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (32, 22, "And who is more unjust than one who is reminded of the verses of his Lord; then he turns away from them? Indeed We, from the criminals, will take retribution", "وَمَنۡ أَظۡلَمُ مِمَّن ذُكِّرَ بِـَٔايَٰتِ رَبِّهِۦ ثُمَّ أَعۡرَضَ عَنۡهَآۚ إِنَّا مِنَ ٱلۡمُجۡرِمِينَ مُنتَقِمُونَ", "Waman athlamu mimman thukkira biayati rabbihi thumma aAArada AAanha inna mina almujrimeena muntaqimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (32, 23, "And We certainly gave Moses the Scripture, so do not be in doubt over his meeting. And we made the Torah guidance for the Children of Israel", "وَلَقَدۡ ءَاتَيۡنَا مُوسَى ٱلۡكِتَٰبَ فَلَا تَكُن فِي مِرۡيَةٖ مِّن لِّقَآئِهِۦۖ وَجَعَلۡنَٰهُ هُدٗى لِّبَنِيٓ إِسۡرَـٰٓءِيلَ", "Walaqad atayna moosa alkitaba fala takun fee miryatin min liqaihi wajaAAalnahu hudan libanee israeela");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (32, 24, "And We made from among them leaders guiding by Our command when they were patient and [when] they were certain of Our signs", "وَجَعَلۡنَا مِنۡهُمۡ أَئِمَّةٗ يَهۡدُونَ بِأَمۡرِنَا لَمَّا صَبَرُواْۖ وَكَانُواْ بِـَٔايَٰتِنَا يُوقِنُونَ", "WajaAAalna minhum aimmatan yahdoona biamrina lamma sabaroo wakanoo biayatina yooqinoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (32, 25, "Indeed, your Lord will judge between them on the Day of Resurrection concerning that over which they used to differ", "إِنَّ رَبَّكَ هُوَ يَفۡصِلُ بَيۡنَهُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ فِيمَا كَانُواْ فِيهِ يَخۡتَلِفُونَ", "Inna rabbaka huwa yafsilu baynahum yawma alqiyamati feema kanoo feehi yakhtalifoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (32, 26, "Has it not become clear to them how many generations We destroyed before them, [as] they walk among their dwellings? Indeed in that are signs; then do they not hear", "أَوَلَمۡ يَهۡدِ لَهُمۡ كَمۡ أَهۡلَكۡنَا مِن قَبۡلِهِم مِّنَ ٱلۡقُرُونِ يَمۡشُونَ فِي مَسَٰكِنِهِمۡۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٍۚ أَفَلَا يَسۡمَعُونَ", "Awalam yahdi lahum kam ahlakna min qablihim mina alqurooni yamshoona fee masakinihim inna fee thalika laayatin afala yasmaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (32, 27, "Have they not seen that We drive the water [in clouds] to barren land and bring forth thereby crops from which their livestock eat and [they] themselves? Then do they not see", "أَوَلَمۡ يَرَوۡاْ أَنَّا نَسُوقُ ٱلۡمَآءَ إِلَى ٱلۡأَرۡضِ ٱلۡجُرُزِ فَنُخۡرِجُ بِهِۦ زَرۡعٗا تَأۡكُلُ مِنۡهُ أَنۡعَٰمُهُمۡ وَأَنفُسُهُمۡۚ أَفَلَا يُبۡصِرُونَ", "Awalam yaraw anna nasooqu almaa ila alardi aljuruzi fanukhriju bihi zarAAan takulu minhu anAAamuhum waanfusuhum afala yubsiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (32, 28, "And they say, ""When will be this conquest, if you should be truthful", "وَيَقُولُونَ مَتَىٰ هَٰذَا ٱلۡفَتۡحُ إِن كُنتُمۡ صَٰدِقِينَ", "Wayaqooloona mata hatha alfathu in kuntum sadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (32, 29, "Say, [O Muhammad], ""On the Day of Conquest the belief of those who had disbelieved will not benefit them, nor will they be reprieved", "قُلۡ يَوۡمَ ٱلۡفَتۡحِ لَا يَنفَعُ ٱلَّذِينَ كَفَرُوٓاْ إِيمَٰنُهُمۡ وَلَا هُمۡ يُنظَرُونَ", "Qul yawma alfathi la yanfaAAu allatheena kafaroo eemanuhum wala hum yuntharoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (32, 30, "So turn away from them and wait. Indeed, they are waiting", "فَأَعۡرِضۡ عَنۡهُمۡ وَٱنتَظِرۡ إِنَّهُم مُّنتَظِرُونَ", "FaaAArid AAanhum waintathir innahum muntathiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 1, "O Prophet, fear Allah and do not obey the disbelievers and the hypocrites. Indeed, Allah is ever Knowing and Wise", "يَـٰٓأَيُّهَا ٱلنَّبِيُّ ٱتَّقِ ٱللَّهَ وَلَا تُطِعِ ٱلۡكَٰفِرِينَ وَٱلۡمُنَٰفِقِينَۚ إِنَّ ٱللَّهَ كَانَ عَلِيمًا حَكِيمٗا", "Ya ayyuha alnnabiyyu ittaqi Allaha wala tutiAAi alkafireena waalmunafiqeena inna Allaha kana AAaleeman hakeeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 2, "And follow that which is revealed to you from your Lord. Indeed Allah is ever, with what you do, Acquainted", "وَٱتَّبِعۡ مَا يُوحَىٰٓ إِلَيۡكَ مِن رَّبِّكَۚ إِنَّ ٱللَّهَ كَانَ بِمَا تَعۡمَلُونَ خَبِيرٗا", "WaittabiAA ma yooha ilayka min rabbika inna Allaha kana bima taAAmaloona khabeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 3, "And rely upon Allah; and sufficient is Allah as Disposer of affairs", "وَتَوَكَّلۡ عَلَى ٱللَّهِۚ وَكَفَىٰ بِٱللَّهِ وَكِيلٗا", "Watawakkal AAala Allahi wakafa biAllahi wakeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 4, "Allah has not made for a man two hearts in his interior. And He has not made your wives whom you declare unlawful your mothers. And he has not made your adopted sons your [true] sons. That is [merely] your saying by your mouths, but Allah says the truth, and He guides to the [right] way", "مَّا جَعَلَ ٱللَّهُ لِرَجُلٖ مِّن قَلۡبَيۡنِ فِي جَوۡفِهِۦۚ وَمَا جَعَلَ أَزۡوَٰجَكُمُ ٱلَّـٰٓـِٔي تُظَٰهِرُونَ مِنۡهُنَّ أُمَّهَٰتِكُمۡۚ وَمَا جَعَلَ أَدۡعِيَآءَكُمۡ أَبۡنَآءَكُمۡۚ ذَٰلِكُمۡ قَوۡلُكُم بِأَفۡوَٰهِكُمۡۖ وَٱللَّهُ يَقُولُ ٱلۡحَقَّ وَهُوَ يَهۡدِي ٱلسَّبِيلَ", "Ma jaAAala Allahu lirajulin min qalbayni fee jawfihi wama jaAAala azwajakumu allaee tuthahiroona minhunna ommahatikum wama jaAAala adAAiyaakum abnaakum thalikum qawlukum biafwahikum waAllahu yaqoolu alhaqqa wahuwa yahdee alssabeela");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 5, "Call them by [the names of] their fathers; it is more just in the sight of Allah. But if you do not know their fathers - then they are [still] your brothers in religion and those entrusted to you. And there is no blame upon you for that in which you have erred but [only for] what your hearts intended. And ever is Allah Forgiving and Merciful", "ٱدۡعُوهُمۡ لِأٓبَآئِهِمۡ هُوَ أَقۡسَطُ عِندَ ٱللَّهِۚ فَإِن لَّمۡ تَعۡلَمُوٓاْ ءَابَآءَهُمۡ فَإِخۡوَٰنُكُمۡ فِي ٱلدِّينِ وَمَوَٰلِيكُمۡۚ وَلَيۡسَ عَلَيۡكُمۡ جُنَاحٞ فِيمَآ أَخۡطَأۡتُم بِهِۦ وَلَٰكِن مَّا تَعَمَّدَتۡ قُلُوبُكُمۡۚ وَكَانَ ٱللَّهُ غَفُورٗا رَّحِيمًا", "OdAAoohum liabaihim huwa aqsatu AAinda Allahi fain lam taAAlamoo abaahum faikhwanukum fee alddeeni wamawaleekum walaysa AAalaykum junahun feema akhtatum bihi walakin ma taAAammadat quloobukum wakana Allahu ghafooran raheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 6, "The Prophet is more worthy of the believers than themselves, and his wives are [in the position of] their mothers. And those of [blood] relationship are more entitled [to inheritance] in the decree of Allah than the [other] believers and the emigrants, except that you may do to your close associates a kindness [through bequest]. That was in the Book inscribed", "ٱلنَّبِيُّ أَوۡلَىٰ بِٱلۡمُؤۡمِنِينَ مِنۡ أَنفُسِهِمۡۖ وَأَزۡوَٰجُهُۥٓ أُمَّهَٰتُهُمۡۗ وَأُوْلُواْ ٱلۡأَرۡحَامِ بَعۡضُهُمۡ أَوۡلَىٰ بِبَعۡضٖ فِي كِتَٰبِ ٱللَّهِ مِنَ ٱلۡمُؤۡمِنِينَ وَٱلۡمُهَٰجِرِينَ إِلَّآ أَن تَفۡعَلُوٓاْ إِلَىٰٓ أَوۡلِيَآئِكُم مَّعۡرُوفٗاۚ كَانَ ذَٰلِكَ فِي ٱلۡكِتَٰبِ مَسۡطُورٗا", "Alnnabiyyu awla bialmumineena min anfusihim waazwajuhu ommahatuhum waoloo alarhami baAAduhum awla bibaAAdin fee kitabi Allahi mina almumineena waalmuhajireena illa an tafAAaloo ila awliyaikum maAAroofan kana thalika fee alkitabi mastooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 7, "And [mention, O Muhammad], when We took from the prophets their covenant and from you and from Noah and Abraham and Moses and Jesus, the son of Mary; and We took from them a solemn covenant", "وَإِذۡ أَخَذۡنَا مِنَ ٱلنَّبِيِّـۧنَ مِيثَٰقَهُمۡ وَمِنكَ وَمِن نُّوحٖ وَإِبۡرَٰهِيمَ وَمُوسَىٰ وَعِيسَى ٱبۡنِ مَرۡيَمَۖ وَأَخَذۡنَا مِنۡهُم مِّيثَٰقًا غَلِيظٗا", "Waith akhathna mina alnnabiyyeena meethaqahum waminka wamin noohin waibraheema wamoosa waAAeesa ibni maryama waakhathna minhum meethaqan ghaleethan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 8, "That He may question the truthful about their truth. And He has prepared for the disbelievers a painful punishment", "لِّيَسۡـَٔلَ ٱلصَّـٰدِقِينَ عَن صِدۡقِهِمۡۚ وَأَعَدَّ لِلۡكَٰفِرِينَ عَذَابًا أَلِيمٗا", "Liyasala alssadiqeena AAan sidqihim waaAAadda lilkafireena AAathaban aleeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 9, "O you who have believed, remember the favor of Allah upon you when armies came to [attack] you and We sent upon them a wind and armies [of angels] you did not see. And ever is Allah, of what you do, Seeing", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱذۡكُرُواْ نِعۡمَةَ ٱللَّهِ عَلَيۡكُمۡ إِذۡ جَآءَتۡكُمۡ جُنُودٞ فَأَرۡسَلۡنَا عَلَيۡهِمۡ رِيحٗا وَجُنُودٗا لَّمۡ تَرَوۡهَاۚ وَكَانَ ٱللَّهُ بِمَا تَعۡمَلُونَ بَصِيرًا", "Ya ayyuha allatheena amanoo othkuroo niAAmata Allahi AAalaykum ith jaatkum junoodun faarsalna AAalayhim reehan wajunoodan lam tarawha wakana Allahu bima taAAmaloona baseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 10, "[Remember] when they came at you from above you and from below you, and when eyes shifted [in fear], and hearts reached the throats and you assumed about Allah [various] assumptions", "إِذۡ جَآءُوكُم مِّن فَوۡقِكُمۡ وَمِنۡ أَسۡفَلَ مِنكُمۡ وَإِذۡ زَاغَتِ ٱلۡأَبۡصَٰرُ وَبَلَغَتِ ٱلۡقُلُوبُ ٱلۡحَنَاجِرَ وَتَظُنُّونَ بِٱللَّهِ ٱلظُّنُونَا۠", "Ith jaookum min fawqikum wamin asfala minkum waith zaghati alabsaru wabalaghati alquloobu alhanajira watathunnoona biAllahi alththunoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 11, "There the believers were tested and shaken with a severe shaking", "هُنَالِكَ ٱبۡتُلِيَ ٱلۡمُؤۡمِنُونَ وَزُلۡزِلُواْ زِلۡزَالٗا شَدِيدٗا", "Hunalika ibtuliya almuminoona wazulziloo zilzalan shadeedan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 12, "And [remember] when the hypocrites and those in whose hearts is disease said, ""Allah and His Messenger did not promise us except delusion", "وَإِذۡ يَقُولُ ٱلۡمُنَٰفِقُونَ وَٱلَّذِينَ فِي قُلُوبِهِم مَّرَضٞ مَّا وَعَدَنَا ٱللَّهُ وَرَسُولُهُۥٓ إِلَّا غُرُورٗا", "Waith yaqoolu almunafiqoona waallatheena fee quloobihim maradun ma waAAadana Allahu warasooluhu illa ghurooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 13, "And when a faction of them said, ""O people of Yathrib, there is no stability for you [here], so return [home]."" And a party of them asked permission of the Prophet, saying, ""Indeed, our houses are unprotected,"" while they were not exposed. They did not intend except to flee", "وَإِذۡ قَالَت طَّآئِفَةٞ مِّنۡهُمۡ يَـٰٓأَهۡلَ يَثۡرِبَ لَا مُقَامَ لَكُمۡ فَٱرۡجِعُواْۚ وَيَسۡتَـٔۡذِنُ فَرِيقٞ مِّنۡهُمُ ٱلنَّبِيَّ يَقُولُونَ إِنَّ بُيُوتَنَا عَوۡرَةٞ وَمَا هِيَ بِعَوۡرَةٍۖ إِن يُرِيدُونَ إِلَّا فِرَارٗا", "Waith qalat taifatun minhum ya ahla yathriba la muqama lakum fairjiAAoo wayastathinu fareequn minhumu alnnabiyya yaqooloona inna buyootana AAawratun wama hiya biAAawratin in yureedoona illa firaran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 14, "And if they had been entered upon from all its [surrounding] regions and fitnah had been demanded of them, they would have done it and not hesitated over it except briefly", "وَلَوۡ دُخِلَتۡ عَلَيۡهِم مِّنۡ أَقۡطَارِهَا ثُمَّ سُئِلُواْ ٱلۡفِتۡنَةَ لَأٓتَوۡهَا وَمَا تَلَبَّثُواْ بِهَآ إِلَّا يَسِيرٗا", "Walaw dukhilat AAalayhim min aqtariha thumma suiloo alfitnata laatawha wama talabbathoo biha illa yaseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 15, "And they had already promised Allah before not to turn their backs and flee. And ever is the promise to Allah [that about which one will be] questioned", "وَلَقَدۡ كَانُواْ عَٰهَدُواْ ٱللَّهَ مِن قَبۡلُ لَا يُوَلُّونَ ٱلۡأَدۡبَٰرَۚ وَكَانَ عَهۡدُ ٱللَّهِ مَسۡـُٔولٗا", "Walaqad kanoo AAahadoo Allaha min qablu la yuwalloona aladbara wakana AAahdu Allahi masoolan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 16, "Say, [O Muhammad], ""Never will fleeing benefit you if you should flee from death or killing; and then [if you did], you would not be given enjoyment [of life] except for a little", "قُل لَّن يَنفَعَكُمُ ٱلۡفِرَارُ إِن فَرَرۡتُم مِّنَ ٱلۡمَوۡتِ أَوِ ٱلۡقَتۡلِ وَإِذٗا لَّا تُمَتَّعُونَ إِلَّا قَلِيلٗا", "Qul lan yanfaAAakumu alfiraru in farartum mina almawti awi alqatli waithan la tumattaAAoona illa qaleelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 17, "Say, ""Who is it that can protect you from Allah if He intends for you an ill or intends for you a mercy?"" And they will not find for themselves besides Allah any protector or any helper", "قُلۡ مَن ذَا ٱلَّذِي يَعۡصِمُكُم مِّنَ ٱللَّهِ إِنۡ أَرَادَ بِكُمۡ سُوٓءًا أَوۡ أَرَادَ بِكُمۡ رَحۡمَةٗۚ وَلَا يَجِدُونَ لَهُم مِّن دُونِ ٱللَّهِ وَلِيّٗا وَلَا نَصِيرٗا", "Qul man tha allathee yaAAsimukum mina Allahi in arada bikum sooan aw arada bikum rahmatan wala yajidoona lahum min dooni Allahi waliyyan wala naseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 18, "Already Allah knows the hinderers among you and those [hypocrites] who say to their brothers, ""Come to us,"" and do not go to battle, except for a few", "۞قَدۡ يَعۡلَمُ ٱللَّهُ ٱلۡمُعَوِّقِينَ مِنكُمۡ وَٱلۡقَآئِلِينَ لِإِخۡوَٰنِهِمۡ هَلُمَّ إِلَيۡنَاۖ وَلَا يَأۡتُونَ ٱلۡبَأۡسَ إِلَّا قَلِيلًا", "Qad yaAAlamu Allahu almuAAawwiqeena minkum waalqaileena liikhwanihim halumma ilayna wala yatoona albasa illa qaleelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 19, "Indisposed toward you. And when fear comes, you see them looking at you, their eyes revolving like one being overcome by death. But when fear departs, they lash you with sharp tongues, indisposed toward [any] good. Those have not believed, so Allah has rendered their deeds worthless, and ever is that, for Allah, easy", "أَشِحَّةً عَلَيۡكُمۡۖ فَإِذَا جَآءَ ٱلۡخَوۡفُ رَأَيۡتَهُمۡ يَنظُرُونَ إِلَيۡكَ تَدُورُ أَعۡيُنُهُمۡ كَٱلَّذِي يُغۡشَىٰ عَلَيۡهِ مِنَ ٱلۡمَوۡتِۖ فَإِذَا ذَهَبَ ٱلۡخَوۡفُ سَلَقُوكُم بِأَلۡسِنَةٍ حِدَادٍ أَشِحَّةً عَلَى ٱلۡخَيۡرِۚ أُوْلَـٰٓئِكَ لَمۡ يُؤۡمِنُواْ فَأَحۡبَطَ ٱللَّهُ أَعۡمَٰلَهُمۡۚ وَكَانَ ذَٰلِكَ عَلَى ٱللَّهِ يَسِيرٗا", "Ashihhatan AAalaykum faitha jaa alkhawfu raaytahum yanthuroona ilayka tadooru aAAyunuhum kaallathee yughsha AAalayhi mina almawti faitha thahaba alkhawfu salaqookum bialsinatin hidadin ashihhatan AAala alkhayri olaika lam yuminoo faahbata Allahu aAAmalahum wakana thalika AAala Allahi yaseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 20, "They think the companies have not [yet] withdrawn. And if the companies should come [again], they would wish they were in the desert among the bedouins, inquiring [from afar] about your news. And if they should be among you, they would not fight except for a little", "يَحۡسَبُونَ ٱلۡأَحۡزَابَ لَمۡ يَذۡهَبُواْۖ وَإِن يَأۡتِ ٱلۡأَحۡزَابُ يَوَدُّواْ لَوۡ أَنَّهُم بَادُونَ فِي ٱلۡأَعۡرَابِ يَسۡـَٔلُونَ عَنۡ أَنۢبَآئِكُمۡۖ وَلَوۡ كَانُواْ فِيكُم مَّا قَٰتَلُوٓاْ إِلَّا قَلِيلٗا", "Yahsaboona alahzaba lam yathhaboo wain yati alahzabu yawaddoo law annahum badoona fee alaAArabi yasaloona AAan anbaikum walaw kanoo feekum ma qataloo illa qaleelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 21, "There has certainly been for you in the Messenger of Allah an excellent pattern for anyone whose hope is in Allah and the Last Day and [who] remembers Allah often", "لَّقَدۡ كَانَ لَكُمۡ فِي رَسُولِ ٱللَّهِ أُسۡوَةٌ حَسَنَةٞ لِّمَن كَانَ يَرۡجُواْ ٱللَّهَ وَٱلۡيَوۡمَ ٱلۡأٓخِرَ وَذَكَرَ ٱللَّهَ كَثِيرٗا", "Laqad kana lakum fee rasooli Allahi oswatun hasanatun liman kana yarjoo Allaha waalyawma alakhira wathakara Allaha katheeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 22, "And when the believers saw the companies, they said, ""This is what Allah and His Messenger had promised us, and Allah and His Messenger spoke the truth."" And it increased them only in faith and acceptance", "وَلَمَّا رَءَا ٱلۡمُؤۡمِنُونَ ٱلۡأَحۡزَابَ قَالُواْ هَٰذَا مَا وَعَدَنَا ٱللَّهُ وَرَسُولُهُۥ وَصَدَقَ ٱللَّهُ وَرَسُولُهُۥۚ وَمَا زَادَهُمۡ إِلَّآ إِيمَٰنٗا وَتَسۡلِيمٗا", "Walamma raa almuminoona alahzaba qaloo hatha ma waAAadana Allahu warasooluhu wasadaqa Allahu warasooluhu wama zadahum illa eemanan watasleeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 23, "Among the believers are men true to what they promised Allah. Among them is he who has fulfilled his vow [to the death], and among them is he who awaits [his chance]. And they did not alter [the terms of their commitment] by any alteration", "مِّنَ ٱلۡمُؤۡمِنِينَ رِجَالٞ صَدَقُواْ مَا عَٰهَدُواْ ٱللَّهَ عَلَيۡهِۖ فَمِنۡهُم مَّن قَضَىٰ نَحۡبَهُۥ وَمِنۡهُم مَّن يَنتَظِرُۖ وَمَا بَدَّلُواْ تَبۡدِيلٗا", "Mina almumineena rijalun sadaqoo ma AAahadoo Allaha AAalayhi faminhum man qada nahbahu waminhum man yantathiru wama baddaloo tabdeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 24, "That Allah may reward the truthful for their truth and punish the hypocrites if He wills or accept their repentance. Indeed, Allah is ever Forgiving and Merciful", "لِّيَجۡزِيَ ٱللَّهُ ٱلصَّـٰدِقِينَ بِصِدۡقِهِمۡ وَيُعَذِّبَ ٱلۡمُنَٰفِقِينَ إِن شَآءَ أَوۡ يَتُوبَ عَلَيۡهِمۡۚ إِنَّ ٱللَّهَ كَانَ غَفُورٗا رَّحِيمٗا", "Liyajziya Allahu alssadiqeena bisidqihim wayuAAaththiba almunafiqeena in shaa aw yatooba AAalayhim inna Allaha kana ghafooran raheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 25, "And Allah repelled those who disbelieved, in their rage, not having obtained any good. And sufficient was Allah for the believers in battle, and ever is Allah Powerful and Exalted in Might", "وَرَدَّ ٱللَّهُ ٱلَّذِينَ كَفَرُواْ بِغَيۡظِهِمۡ لَمۡ يَنَالُواْ خَيۡرٗاۚ وَكَفَى ٱللَّهُ ٱلۡمُؤۡمِنِينَ ٱلۡقِتَالَۚ وَكَانَ ٱللَّهُ قَوِيًّا عَزِيزٗا", "Waradda Allahu allatheena kafaroo bighaythihim lam yanaloo khayran wakafa Allahu almumineena alqitala wakana Allahu qawiyyan AAazeezan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 26, "And He brought down those who supported them among the People of the Scripture from their fortresses and cast terror into their hearts [so that] a party you killed, and you took captive a party", "وَأَنزَلَ ٱلَّذِينَ ظَٰهَرُوهُم مِّنۡ أَهۡلِ ٱلۡكِتَٰبِ مِن صَيَاصِيهِمۡ وَقَذَفَ فِي قُلُوبِهِمُ ٱلرُّعۡبَ فَرِيقٗا تَقۡتُلُونَ وَتَأۡسِرُونَ فَرِيقٗا", "Waanzala allatheena thaharoohum min ahli alkitabi min sayaseehim waqathafa fee quloobihimu alrruAAba fareeqan taqtuloona watasiroona fareeqan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 27, "And He caused you to inherit their land and their homes and their properties and a land which you have not trodden. And ever is Allah, over all things, competent", "وَأَوۡرَثَكُمۡ أَرۡضَهُمۡ وَدِيَٰرَهُمۡ وَأَمۡوَٰلَهُمۡ وَأَرۡضٗا لَّمۡ تَطَـُٔوهَاۚ وَكَانَ ٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٗا", "Waawrathakum ardahum wadiyarahum waamwalahum waardan lam tataooha wakana Allahu AAala kulli shayin qadeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 28, "O Prophet, say to your wives, ""If you should desire the worldly life and its adornment, then come, I will provide for you and give you a gracious release", "يَـٰٓأَيُّهَا ٱلنَّبِيُّ قُل لِّأَزۡوَٰجِكَ إِن كُنتُنَّ تُرِدۡنَ ٱلۡحَيَوٰةَ ٱلدُّنۡيَا وَزِينَتَهَا فَتَعَالَيۡنَ أُمَتِّعۡكُنَّ وَأُسَرِّحۡكُنَّ سَرَاحٗا جَمِيلٗا", "Ya ayyuha alnnabiyyu qul liazwajika in kuntunna turidna alhayata alddunya wazeenataha fataAAalayna omattiAAkunna waosarrihkunna sarahan jameelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 29, "But if you should desire Allah and His Messenger and the home of the Hereafter - then indeed, Allah has prepared for the doers of good among you a great reward", "وَإِن كُنتُنَّ تُرِدۡنَ ٱللَّهَ وَرَسُولَهُۥ وَٱلدَّارَ ٱلۡأٓخِرَةَ فَإِنَّ ٱللَّهَ أَعَدَّ لِلۡمُحۡسِنَٰتِ مِنكُنَّ أَجۡرًا عَظِيمٗا", "Wain kuntunna turidna Allaha warasoolahu waalddara alakhirata fainna Allaha aAAadda lilmuhsinati minkunna ajran AAatheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 30, "O wives of the Prophet, whoever of you should commit a clear immorality - for her the punishment would be doubled two fold, and ever is that, for Allah, easy", "يَٰنِسَآءَ ٱلنَّبِيِّ مَن يَأۡتِ مِنكُنَّ بِفَٰحِشَةٖ مُّبَيِّنَةٖ يُضَٰعَفۡ لَهَا ٱلۡعَذَابُ ضِعۡفَيۡنِۚ وَكَانَ ذَٰلِكَ عَلَى ٱللَّهِ يَسِيرٗا", "Ya nisaa alnnabiyyi man yati minkunna bifahishatin mubayyinatin yudaAAaf laha alAAathabu diAAfayni wakana thalika AAala Allahi yaseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 31, "And whoever of you devoutly obeys Allah and His Messenger and does righteousness - We will give her her reward twice; and We have prepared for her a noble provision", "۞وَمَن يَقۡنُتۡ مِنكُنَّ لِلَّهِ وَرَسُولِهِۦ وَتَعۡمَلۡ صَٰلِحٗا نُّؤۡتِهَآ أَجۡرَهَا مَرَّتَيۡنِ وَأَعۡتَدۡنَا لَهَا رِزۡقٗا كَرِيمٗا", "Waman yaqnut minkunna lillahi warasoolihi wataAAmal salihan nutiha ajraha marratayni waaAAtadna laha rizqan kareeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 32, "O wives of the Prophet, you are not like anyone among women. If you fear Allah, then do not be soft in speech [to men], lest he in whose heart is disease should covet, but speak with appropriate speech", "يَٰنِسَآءَ ٱلنَّبِيِّ لَسۡتُنَّ كَأَحَدٖ مِّنَ ٱلنِّسَآءِ إِنِ ٱتَّقَيۡتُنَّۚ فَلَا تَخۡضَعۡنَ بِٱلۡقَوۡلِ فَيَطۡمَعَ ٱلَّذِي فِي قَلۡبِهِۦ مَرَضٞ وَقُلۡنَ قَوۡلٗا مَّعۡرُوفٗا", "Ya nisaa alnnabiyyi lastunna kaahadin mina alnnisai ini ittaqaytunna fala takhdaAAna bialqawli fayatmaAAa allathee fee qalbihi maradun waqulna qawlan maAAroofan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 33, "And abide in your houses and do not display yourselves as [was] the display of the former times of ignorance. And establish prayer and give zakah and obey Allah and His Messenger. Allah intends only to remove from you the impurity [of sin], O people of the [Prophet's] household, and to purify you with [extensive] purification", "وَقَرۡنَ فِي بُيُوتِكُنَّ وَلَا تَبَرَّجۡنَ تَبَرُّجَ ٱلۡجَٰهِلِيَّةِ ٱلۡأُولَىٰۖ وَأَقِمۡنَ ٱلصَّلَوٰةَ وَءَاتِينَ ٱلزَّكَوٰةَ وَأَطِعۡنَ ٱللَّهَ وَرَسُولَهُۥٓۚ إِنَّمَا يُرِيدُ ٱللَّهُ لِيُذۡهِبَ عَنكُمُ ٱلرِّجۡسَ أَهۡلَ ٱلۡبَيۡتِ وَيُطَهِّرَكُمۡ تَطۡهِيرٗا", "Waqarna fee buyootikunna wala tabarrajna tabarruja aljahiliyyati aloola waaqimna alssalata waateena alzzakata waatiAAna Allaha warasoolahu innama yureedu Allahu liyuthhiba AAankumu alrrijsa ahla albayti wayutahhirakum tatheeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 34, "And remember what is recited in your houses of the verses of Allah and wisdom. Indeed, Allah is ever Subtle and Acquainted [with all things]", "وَٱذۡكُرۡنَ مَا يُتۡلَىٰ فِي بُيُوتِكُنَّ مِنۡ ءَايَٰتِ ٱللَّهِ وَٱلۡحِكۡمَةِۚ إِنَّ ٱللَّهَ كَانَ لَطِيفًا خَبِيرًا", "Waothkurna ma yutla fee buyootikunna min ayati Allahi waalhikmati inna Allaha kana lateefan khabeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 35, "Indeed, the Muslim men and Muslim women, the believing men and believing women, the obedient men and obedient women, the truthful men and truthful women, the patient men and patient women, the humble men and humble women, the charitable men and charitable women, the fasting men and fasting women, the men who guard their private parts and the women who do so, and the men who remember Allah often and the women who do so - for them Allah has prepared forgiveness and a great reward", "إِنَّ ٱلۡمُسۡلِمِينَ وَٱلۡمُسۡلِمَٰتِ وَٱلۡمُؤۡمِنِينَ وَٱلۡمُؤۡمِنَٰتِ وَٱلۡقَٰنِتِينَ وَٱلۡقَٰنِتَٰتِ وَٱلصَّـٰدِقِينَ وَٱلصَّـٰدِقَٰتِ وَٱلصَّـٰبِرِينَ وَٱلصَّـٰبِرَٰتِ وَٱلۡخَٰشِعِينَ وَٱلۡخَٰشِعَٰتِ وَٱلۡمُتَصَدِّقِينَ وَٱلۡمُتَصَدِّقَٰتِ وَٱلصَّـٰٓئِمِينَ وَٱلصَّـٰٓئِمَٰتِ وَٱلۡحَٰفِظِينَ فُرُوجَهُمۡ وَٱلۡحَٰفِظَٰتِ وَٱلذَّـٰكِرِينَ ٱللَّهَ كَثِيرٗا وَٱلذَّـٰكِرَٰتِ أَعَدَّ ٱللَّهُ لَهُم مَّغۡفِرَةٗ وَأَجۡرًا عَظِيمٗا", "Inna almuslimeena waalmuslimati waalmumineena waalmuminati waalqaniteena waalqanitati waalssadiqeena waalssadiqati waalssabireena waalssabirati waalkhashiAAeena waalkhashiAAati waalmutasaddiqeena waalmutasaddiqati waalssaimeena waalssaimati waalhafitheena furoojahum waalhafithati waalththakireena Allaha katheeran waalththakirati aAAadda Allahu lahum maghfiratan waajran AAatheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 36, "It is not for a believing man or a believing woman, when Allah and His Messenger have decided a matter, that they should [thereafter] have any choice about their affair. And whoever disobeys Allah and His Messenger has certainly strayed into clear error", "وَمَا كَانَ لِمُؤۡمِنٖ وَلَا مُؤۡمِنَةٍ إِذَا قَضَى ٱللَّهُ وَرَسُولُهُۥٓ أَمۡرًا أَن يَكُونَ لَهُمُ ٱلۡخِيَرَةُ مِنۡ أَمۡرِهِمۡۗ وَمَن يَعۡصِ ٱللَّهَ وَرَسُولَهُۥ فَقَدۡ ضَلَّ ضَلَٰلٗا مُّبِينٗا", "Wama kana limuminin wala muminatin itha qada Allahu warasooluhu amran an yakoona lahumu alkhiyaratu min amrihim waman yaAAsi Allaha warasoolahu faqad dalla dalalan mubeenan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 37, "And [remember, O Muhammad], when you said to the one on whom Allah bestowed favor and you bestowed favor, ""Keep your wife and fear Allah,"" while you concealed within yourself that which Allah is to disclose. And you feared the people, while Allah has more right that you fear Him. So when Zayd had no longer any need for her, We married her to you in order that there not be upon the believers any discomfort concerning the wives of their adopted sons when they no longer have need of them. And ever is the command of Allah accomplished", "وَإِذۡ تَقُولُ لِلَّذِيٓ أَنۡعَمَ ٱللَّهُ عَلَيۡهِ وَأَنۡعَمۡتَ عَلَيۡهِ أَمۡسِكۡ عَلَيۡكَ زَوۡجَكَ وَٱتَّقِ ٱللَّهَ وَتُخۡفِي فِي نَفۡسِكَ مَا ٱللَّهُ مُبۡدِيهِ وَتَخۡشَى ٱلنَّاسَ وَٱللَّهُ أَحَقُّ أَن تَخۡشَىٰهُۖ فَلَمَّا قَضَىٰ زَيۡدٞ مِّنۡهَا وَطَرٗا زَوَّجۡنَٰكَهَا لِكَيۡ لَا يَكُونَ عَلَى ٱلۡمُؤۡمِنِينَ حَرَجٞ فِيٓ أَزۡوَٰجِ أَدۡعِيَآئِهِمۡ إِذَا قَضَوۡاْ مِنۡهُنَّ وَطَرٗاۚ وَكَانَ أَمۡرُ ٱللَّهِ مَفۡعُولٗا", "Waith taqoolu lillathee anAAama Allahu AAalayhi waanAAamta AAalayhi amsik AAalayka zawjaka waittaqi Allaha watukhfee fee nafsika ma Allahu mubdeehi watakhsha alnnasa waAllahu ahaqqu an takhshahu falamma qada zaydun minha wataran zawwajnakaha likay la yakoona AAala almumineena harajun fee azwaji adAAiyaihim itha qadaw minhunna wataran wakana amru Allahi mafAAoolan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 38, "There is not to be upon the Prophet any discomfort concerning that which Allah has imposed upon him. [This is] the established way of Allah with those [prophets] who have passed on before. And ever is the command of Allah a destiny decreed", "مَّا كَانَ عَلَى ٱلنَّبِيِّ مِنۡ حَرَجٖ فِيمَا فَرَضَ ٱللَّهُ لَهُۥۖ سُنَّةَ ٱللَّهِ فِي ٱلَّذِينَ خَلَوۡاْ مِن قَبۡلُۚ وَكَانَ أَمۡرُ ٱللَّهِ قَدَرٗا مَّقۡدُورًا", "Ma kana AAala alnnabiyyi min harajin feema farada Allahu lahu sunnata Allahi fee allatheena khalaw min qablu wakana amru Allahi qadaran maqdooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 39, "[Allah praises] those who convey the messages of Allah and fear Him and do not fear anyone but Allah. And sufficient is Allah as Accountant", "ٱلَّذِينَ يُبَلِّغُونَ رِسَٰلَٰتِ ٱللَّهِ وَيَخۡشَوۡنَهُۥ وَلَا يَخۡشَوۡنَ أَحَدًا إِلَّا ٱللَّهَۗ وَكَفَىٰ بِٱللَّهِ حَسِيبٗا", "Allatheena yuballighoona risalati Allahi wayakhshawnahu wala yakhshawna ahadan illa Allaha wakafa biAllahi haseeban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 40, "Muhammad is not the father of [any] one of your men, but [he is] the Messenger of Allah and last of the prophets. And ever is Allah, of all things, Knowing", "مَّا كَانَ مُحَمَّدٌ أَبَآ أَحَدٖ مِّن رِّجَالِكُمۡ وَلَٰكِن رَّسُولَ ٱللَّهِ وَخَاتَمَ ٱلنَّبِيِّـۧنَۗ وَكَانَ ٱللَّهُ بِكُلِّ شَيۡءٍ عَلِيمٗا", "Ma kana muhammadun aba ahadin min rijalikum walakin rasoola Allahi wakhatama alnnabiyyeena wakana Allahu bikulli shayin AAaleeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 41, "O you who have believed, remember Allah with much remembrance", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱذۡكُرُواْ ٱللَّهَ ذِكۡرٗا كَثِيرٗا", "Ya ayyuha allatheena amanoo othkuroo Allaha thikran katheeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 42, "And exalt Him morning and afternoon", "وَسَبِّحُوهُ بُكۡرَةٗ وَأَصِيلًا", "Wasabbihoohu bukratan waaseelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 43, "It is He who confers blessing upon you, and His angels [ask Him to do so] that He may bring you out from darknesses into the light. And ever is He, to the believers, Merciful", "هُوَ ٱلَّذِي يُصَلِّي عَلَيۡكُمۡ وَمَلَـٰٓئِكَتُهُۥ لِيُخۡرِجَكُم مِّنَ ٱلظُّلُمَٰتِ إِلَى ٱلنُّورِۚ وَكَانَ بِٱلۡمُؤۡمِنِينَ رَحِيمٗا", "Huwa allathee yusallee AAalaykum wamalaikatuhu liyukhrijakum mina alththulumati ila alnnoori wakana bialmumineena raheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 44, "Their greeting the Day they meet Him will be, ""Peace."" And He has prepared for them a noble reward", "تَحِيَّتُهُمۡ يَوۡمَ يَلۡقَوۡنَهُۥ سَلَٰمٞۚ وَأَعَدَّ لَهُمۡ أَجۡرٗا كَرِيمٗا", "Tahiyyatuhum yawma yalqawnahu salamun waaAAadda lahum ajran kareeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 45, "O Prophet, indeed We have sent you as a witness and a bringer of good tidings and a warner", "يَـٰٓأَيُّهَا ٱلنَّبِيُّ إِنَّآ أَرۡسَلۡنَٰكَ شَٰهِدٗا وَمُبَشِّرٗا وَنَذِيرٗا", "Ya ayyuha alnnabiyyu inna arsalnaka shahidan wamubashshiran wanatheeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 46, "And one who invites to Allah, by His permission, and an illuminating lamp", "وَدَاعِيًا إِلَى ٱللَّهِ بِإِذۡنِهِۦ وَسِرَاجٗا مُّنِيرٗا", "WadaAAiyan ila Allahi biithnihi wasirajan muneeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 47, "And give good tidings to the believers that they will have from Allah great bounty", "وَبَشِّرِ ٱلۡمُؤۡمِنِينَ بِأَنَّ لَهُم مِّنَ ٱللَّهِ فَضۡلٗا كَبِيرٗا", "Wabashshiri almumineena bianna lahum mina Allahi fadlan kabeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 48, "And do not obey the disbelievers and the hypocrites but do not harm them, and rely upon Allah. And sufficient is Allah as Disposer of affairs", "وَلَا تُطِعِ ٱلۡكَٰفِرِينَ وَٱلۡمُنَٰفِقِينَ وَدَعۡ أَذَىٰهُمۡ وَتَوَكَّلۡ عَلَى ٱللَّهِۚ وَكَفَىٰ بِٱللَّهِ وَكِيلٗا", "Wala tutiAAi alkafireena waalmunafiqeena wadaAA athahum watawakkal AAala Allahi wakafa biAllahi wakeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 49, "O You who have believed, when you marry believing women and then divorce them before you have touched them, then there is not for you any waiting period to count concerning them. So provide for them and give them a gracious release", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِذَا نَكَحۡتُمُ ٱلۡمُؤۡمِنَٰتِ ثُمَّ طَلَّقۡتُمُوهُنَّ مِن قَبۡلِ أَن تَمَسُّوهُنَّ فَمَا لَكُمۡ عَلَيۡهِنَّ مِنۡ عِدَّةٖ تَعۡتَدُّونَهَاۖ فَمَتِّعُوهُنَّ وَسَرِّحُوهُنَّ سَرَاحٗا جَمِيلٗا", "Ya ayyuha allatheena amanoo itha nakahtumu almuminati thumma tallaqtumoohunna min qabli an tamassoohunna fama lakum AAalayhinna min AAiddatin taAAtaddoonaha famattiAAoohunna wasarrihoohunna sarahan jameelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 50, "O Prophet, indeed We have made lawful to you your wives to whom you have given their due compensation and those your right hand possesses from what Allah has returned to you [of captives] and the daughters of your paternal uncles and the daughters of your paternal aunts and the daughters of your maternal uncles and the daughters of your maternal aunts who emigrated with you and a believing woman if she gives herself to the Prophet [and] if the Prophet wishes to marry her, [this is] only for you, excluding the [other] believers. We certainly know what We have made obligatory upon them concerning their wives and those their right hands possess, [but this is for you] in order that there will be upon you no discomfort. And ever is Allah Forgiving and Merciful", "يَـٰٓأَيُّهَا ٱلنَّبِيُّ إِنَّآ أَحۡلَلۡنَا لَكَ أَزۡوَٰجَكَ ٱلَّـٰتِيٓ ءَاتَيۡتَ أُجُورَهُنَّ وَمَا مَلَكَتۡ يَمِينُكَ مِمَّآ أَفَآءَ ٱللَّهُ عَلَيۡكَ وَبَنَاتِ عَمِّكَ وَبَنَاتِ عَمَّـٰتِكَ وَبَنَاتِ خَالِكَ وَبَنَاتِ خَٰلَٰتِكَ ٱلَّـٰتِي هَاجَرۡنَ مَعَكَ وَٱمۡرَأَةٗ مُّؤۡمِنَةً إِن وَهَبَتۡ نَفۡسَهَا لِلنَّبِيِّ إِنۡ أَرَادَ ٱلنَّبِيُّ أَن يَسۡتَنكِحَهَا خَالِصَةٗ لَّكَ مِن دُونِ ٱلۡمُؤۡمِنِينَۗ قَدۡ عَلِمۡنَا مَا فَرَضۡنَا عَلَيۡهِمۡ فِيٓ أَزۡوَٰجِهِمۡ وَمَا مَلَكَتۡ أَيۡمَٰنُهُمۡ لِكَيۡلَا يَكُونَ عَلَيۡكَ حَرَجٞۗ وَكَانَ ٱللَّهُ غَفُورٗا رَّحِيمٗا", "Ya ayyuha alnnabiyyu inna ahlalna laka azwajaka allatee atayta ojoorahunna wama malakat yameenuka mimma afaa Allahu AAalayka wabanati AAammika wabanati AAammatika wabanati khalika wabanati khalatika allatee hajarna maAAaka waimraatan muminatan in wahabat nafsaha lilnnabiyyi in arada alnnabiyyu an yastankihaha khalisatan laka min dooni almumineena qad AAalimna ma faradna AAalayhim fee azwajihim wama malakat aymanuhum likayla yakoona AAalayka harajun wakana Allahu ghafooran raheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 51, "You, [O Muhammad], may put aside whom you will of them or take to yourself whom you will. And any that you desire of those [wives] from whom you had [temporarily] separated - there is no blame upon you [in returning her]. That is more suitable that they should be content and not grieve and that they should be satisfied with what you have given them - all of them. And Allah knows what is in your hearts. And ever is Allah Knowing and Forbearing", "۞تُرۡجِي مَن تَشَآءُ مِنۡهُنَّ وَتُـٔۡوِيٓ إِلَيۡكَ مَن تَشَآءُۖ وَمَنِ ٱبۡتَغَيۡتَ مِمَّنۡ عَزَلۡتَ فَلَا جُنَاحَ عَلَيۡكَۚ ذَٰلِكَ أَدۡنَىٰٓ أَن تَقَرَّ أَعۡيُنُهُنَّ وَلَا يَحۡزَنَّ وَيَرۡضَيۡنَ بِمَآ ءَاتَيۡتَهُنَّ كُلُّهُنَّۚ وَٱللَّهُ يَعۡلَمُ مَا فِي قُلُوبِكُمۡۚ وَكَانَ ٱللَّهُ عَلِيمًا حَلِيمٗا", "Turjee man tashao minhunna watuwee ilayka man tashao wamani ibtaghayta mimman AAazalta fala junaha AAalayka thalika adna an taqarra aAAyunuhunna wala yahzanna wayardayna bima ataytahunna kulluhunna waAllahu yaAAlamu ma fee quloobikum wakana Allahu AAaleeman haleeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 52, "Not lawful to you, [O Muhammad], are [any additional] women after [this], nor [is it] for you to exchange them for [other] wives, even if their beauty were to please you, except what your right hand possesses. And ever is Allah, over all things, an Observer", "لَّا يَحِلُّ لَكَ ٱلنِّسَآءُ مِنۢ بَعۡدُ وَلَآ أَن تَبَدَّلَ بِهِنَّ مِنۡ أَزۡوَٰجٖ وَلَوۡ أَعۡجَبَكَ حُسۡنُهُنَّ إِلَّا مَا مَلَكَتۡ يَمِينُكَۗ وَكَانَ ٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ رَّقِيبٗا", "La yahillu laka alnnisao min baAAdu wala an tabaddala bihinna min azwajin walaw aAAjabaka husnuhunna illa ma malakat yameenuka wakana Allahu AAala kulli shayin raqeeban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 53, "O you who have believed, do not enter the houses of the Prophet except when you are permitted for a meal, without awaiting its readiness. But when you are invited, then enter; and when you have eaten, disperse without seeking to remain for conversation. Indeed, that [behavior] was troubling the Prophet, and he is shy of [dismissing] you. But Allah is not shy of the truth. And when you ask [his wives] for something, ask them from behind a partition. That is purer for your hearts and their hearts. And it is not [conceivable or lawful] for you to harm the Messenger of Allah or to marry his wives after him, ever. Indeed, that would be in the sight of Allah an enormity", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَدۡخُلُواْ بُيُوتَ ٱلنَّبِيِّ إِلَّآ أَن يُؤۡذَنَ لَكُمۡ إِلَىٰ طَعَامٍ غَيۡرَ نَٰظِرِينَ إِنَىٰهُ وَلَٰكِنۡ إِذَا دُعِيتُمۡ فَٱدۡخُلُواْ فَإِذَا طَعِمۡتُمۡ فَٱنتَشِرُواْ وَلَا مُسۡتَـٔۡنِسِينَ لِحَدِيثٍۚ إِنَّ ذَٰلِكُمۡ كَانَ يُؤۡذِي ٱلنَّبِيَّ فَيَسۡتَحۡيِۦ مِنكُمۡۖ وَٱللَّهُ لَا يَسۡتَحۡيِۦ مِنَ ٱلۡحَقِّۚ وَإِذَا سَأَلۡتُمُوهُنَّ مَتَٰعٗا فَسۡـَٔلُوهُنَّ مِن وَرَآءِ حِجَابٖۚ ذَٰلِكُمۡ أَطۡهَرُ لِقُلُوبِكُمۡ وَقُلُوبِهِنَّۚ وَمَا كَانَ لَكُمۡ أَن تُؤۡذُواْ رَسُولَ ٱللَّهِ وَلَآ أَن تَنكِحُوٓاْ أَزۡوَٰجَهُۥ مِنۢ بَعۡدِهِۦٓ أَبَدًاۚ إِنَّ ذَٰلِكُمۡ كَانَ عِندَ ٱللَّهِ عَظِيمًا", "Ya ayyuha allatheena amanoo la tadkhuloo buyoota alnnabiyyi illa an yuthana lakum ila taAAamin ghayra nathireena inahu walakin itha duAAeetum faodkhuloo faitha taAAimtum faintashiroo wala mustaniseena lihadeethin inna thalikum kana yuthee alnnabiyya fayastahyee minkum waAllahu la yastahyee mina alhaqqi waitha saaltumoohunna mataAAan faisaloohunna min warai hijabin thalikum atharu liquloobikum waquloobihinna wama kana lakum an tuthoo rasoola Allahi wala an tankihoo azwajahu min baAAdihi abadan inna thalikum kana AAinda Allahi AAatheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 54, "Whether you reveal a thing or conceal it, indeed Allah is ever, of all things, Knowing", "إِن تُبۡدُواْ شَيۡـًٔا أَوۡ تُخۡفُوهُ فَإِنَّ ٱللَّهَ كَانَ بِكُلِّ شَيۡءٍ عَلِيمٗا", "In tubdoo shayan aw tukhfoohu fainna Allaha kana bikulli shayin AAaleeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 55, "There is no blame upon women concerning their fathers or their sons or their brothers or their brothers' sons or their sisters' sons or their women or those their right hands possess. And fear Allah. Indeed Allah is ever, over all things, Witness", "لَّا جُنَاحَ عَلَيۡهِنَّ فِيٓ ءَابَآئِهِنَّ وَلَآ أَبۡنَآئِهِنَّ وَلَآ إِخۡوَٰنِهِنَّ وَلَآ أَبۡنَآءِ إِخۡوَٰنِهِنَّ وَلَآ أَبۡنَآءِ أَخَوَٰتِهِنَّ وَلَا نِسَآئِهِنَّ وَلَا مَا مَلَكَتۡ أَيۡمَٰنُهُنَّۗ وَٱتَّقِينَ ٱللَّهَۚ إِنَّ ٱللَّهَ كَانَ عَلَىٰ كُلِّ شَيۡءٖ شَهِيدًا", "La junaha AAalayhinna fee abaihinna wala abnaihinna wala ikhwanihinna wala abnai ikhwanihinna wala abnai akhawatihinna wala nisaihinna wala ma malakat aymanuhunna waittaqeena Allaha inna Allaha kana AAala kulli shayin shaheedan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 56, "Indeed, Allah confers blessing upon the Prophet, and His angels [ask Him to do so]. O you who have believed, ask [Allah to confer] blessing upon him and ask [Allah to grant him] peace", "إِنَّ ٱللَّهَ وَمَلَـٰٓئِكَتَهُۥ يُصَلُّونَ عَلَى ٱلنَّبِيِّۚ يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ صَلُّواْ عَلَيۡهِ وَسَلِّمُواْ تَسۡلِيمًا", "Inna Allaha wamalaikatahu yusalloona AAala alnnabiyyi ya ayyuha allatheena amanoo salloo AAalayhi wasallimoo tasleeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 57, "Indeed, those who abuse Allah and His Messenger - Allah has cursed them in this world and the Hereafter and prepared for them a humiliating punishment", "إِنَّ ٱلَّذِينَ يُؤۡذُونَ ٱللَّهَ وَرَسُولَهُۥ لَعَنَهُمُ ٱللَّهُ فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِ وَأَعَدَّ لَهُمۡ عَذَابٗا مُّهِينٗا", "Inna allatheena yuthoona Allaha warasoolahu laAAanahumu Allahu fee alddunya waalakhirati waaAAadda lahum AAathaban muheenan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 58, "And those who harm believing men and believing women for [something] other than what they have earned have certainly born upon themselves a slander and manifest sin", "وَٱلَّذِينَ يُؤۡذُونَ ٱلۡمُؤۡمِنِينَ وَٱلۡمُؤۡمِنَٰتِ بِغَيۡرِ مَا ٱكۡتَسَبُواْ فَقَدِ ٱحۡتَمَلُواْ بُهۡتَٰنٗا وَإِثۡمٗا مُّبِينٗا", "Waallatheena yuthoona almumineena waalmuminati bighayri ma iktasaboo faqadi ihtamaloo buhtanan waithman mubeenan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 59, "O Prophet, tell your wives and your daughters and the women of the believers to bring down over themselves [part] of their outer garments. That is more suitable that they will be known and not be abused. And ever is Allah Forgiving and Merciful", "يَـٰٓأَيُّهَا ٱلنَّبِيُّ قُل لِّأَزۡوَٰجِكَ وَبَنَاتِكَ وَنِسَآءِ ٱلۡمُؤۡمِنِينَ يُدۡنِينَ عَلَيۡهِنَّ مِن جَلَٰبِيبِهِنَّۚ ذَٰلِكَ أَدۡنَىٰٓ أَن يُعۡرَفۡنَ فَلَا يُؤۡذَيۡنَۗ وَكَانَ ٱللَّهُ غَفُورٗا رَّحِيمٗا", "Ya ayyuha alnnabiyyu qul liazwajika wabanatika wanisai almumineena yudneena AAalayhinna min jalabeebihinna thalika adna an yuAArafna fala yuthayna wakana Allahu ghafooran raheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 60, "If the hypocrites and those in whose hearts is disease and those who spread rumors in al-Madinah do not cease, We will surely incite you against them; then they will not remain your neighbors therein except for a little", "۞لَّئِن لَّمۡ يَنتَهِ ٱلۡمُنَٰفِقُونَ وَٱلَّذِينَ فِي قُلُوبِهِم مَّرَضٞ وَٱلۡمُرۡجِفُونَ فِي ٱلۡمَدِينَةِ لَنُغۡرِيَنَّكَ بِهِمۡ ثُمَّ لَا يُجَاوِرُونَكَ فِيهَآ إِلَّا قَلِيلٗا", "Lain lam yantahi almunafiqoona waallatheena fee quloobihim maradun waalmurjifoona fee almadeenati lanughriyannaka bihim thumma la yujawiroonaka feeha illa qaleelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 61, "Accursed wherever they are found, [being] seized and massacred completely", "مَّلۡعُونِينَۖ أَيۡنَمَا ثُقِفُوٓاْ أُخِذُواْ وَقُتِّلُواْ تَقۡتِيلٗا", "MalAAooneena aynama thuqifoo okhithoo waquttiloo taqteelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 62, "[This is] the established way of Allah with those who passed on before; and you will not find in the way of Allah any change", "سُنَّةَ ٱللَّهِ فِي ٱلَّذِينَ خَلَوۡاْ مِن قَبۡلُۖ وَلَن تَجِدَ لِسُنَّةِ ٱللَّهِ تَبۡدِيلٗا", "Sunnata Allahi fee allatheena khalaw min qablu walan tajida lisunnati Allahi tabdeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 63, "People ask you concerning the Hour. Say,"" Knowledge of it is only with Allah. And what may make you perceive? Perhaps the Hour is near", "يَسۡـَٔلُكَ ٱلنَّاسُ عَنِ ٱلسَّاعَةِۖ قُلۡ إِنَّمَا عِلۡمُهَا عِندَ ٱللَّهِۚ وَمَا يُدۡرِيكَ لَعَلَّ ٱلسَّاعَةَ تَكُونُ قَرِيبًا", "Yasaluka alnnasu AAani alssaAAati qul innama AAilmuha AAinda Allahi wama yudreeka laAAalla alssaAAata takoonu qareeban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 64, "Indeed, Allah has cursed the disbelievers and prepared for them a Blaze", "إِنَّ ٱللَّهَ لَعَنَ ٱلۡكَٰفِرِينَ وَأَعَدَّ لَهُمۡ سَعِيرًا", "Inna Allaha laAAana alkafireena waaAAadda lahum saAAeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 65, "Abiding therein forever, they will not find a protector or a helper", "خَٰلِدِينَ فِيهَآ أَبَدٗاۖ لَّا يَجِدُونَ وَلِيّٗا وَلَا نَصِيرٗا", "Khalideena feeha abadan la yajidoona waliyyan wala naseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 66, "The Day their faces will be turned about in the Fire, they will say, ""How we wish we had obeyed Allah and obeyed the Messenger", "يَوۡمَ تُقَلَّبُ وُجُوهُهُمۡ فِي ٱلنَّارِ يَقُولُونَ يَٰلَيۡتَنَآ أَطَعۡنَا ٱللَّهَ وَأَطَعۡنَا ٱلرَّسُولَا۠", "Yawma tuqallabu wujoohuhum fee alnnari yaqooloona ya laytana ataAAna Allaha waataAAna alrrasoola");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 67, "And they will say, ""Our Lord, indeed we obeyed our masters and our dignitaries, and they led us astray from the [right] way", "وَقَالُواْ رَبَّنَآ إِنَّآ أَطَعۡنَا سَادَتَنَا وَكُبَرَآءَنَا فَأَضَلُّونَا ٱلسَّبِيلَا۠", "Waqaloo rabbana inna ataAAna sadatana wakubaraana faadalloona alssabeela");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 68, "Our Lord, give them double the punishment and curse them with a great curse", "رَبَّنَآ ءَاتِهِمۡ ضِعۡفَيۡنِ مِنَ ٱلۡعَذَابِ وَٱلۡعَنۡهُمۡ لَعۡنٗا كَبِيرٗا", "Rabbana atihim diAAfayni mina alAAathabi wailAAanhum laAAnan kabeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 69, "O you who have believed, be not like those who abused Moses; then Allah cleared him of what they said. And he, in the sight of Allah, was distinguished", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَكُونُواْ كَٱلَّذِينَ ءَاذَوۡاْ مُوسَىٰ فَبَرَّأَهُ ٱللَّهُ مِمَّا قَالُواْۚ وَكَانَ عِندَ ٱللَّهِ وَجِيهٗا", "Ya ayyuha allatheena amanoo la takoonoo kaallatheena athaw moosa fabarraahu Allahu mimma qaloo wakana AAinda Allahi wajeehan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 70, "O you who have believed, fear Allah and speak words of appropriate justice", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱتَّقُواْ ٱللَّهَ وَقُولُواْ قَوۡلٗا سَدِيدٗا", "Ya ayyuha allatheena amanoo ittaqoo Allaha waqooloo qawlan sadeedan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 71, "He will [then] amend for you your deeds and forgive you your sins. And whoever obeys Allah and His Messenger has certainly attained a great attainment", "يُصۡلِحۡ لَكُمۡ أَعۡمَٰلَكُمۡ وَيَغۡفِرۡ لَكُمۡ ذُنُوبَكُمۡۗ وَمَن يُطِعِ ٱللَّهَ وَرَسُولَهُۥ فَقَدۡ فَازَ فَوۡزًا عَظِيمًا", "Yuslih lakum aAAmalakum wayaghfir lakum thunoobakum waman yutiAAi Allaha warasoolahu faqad faza fawzan AAatheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 72, "Indeed, we offered the Trust to the heavens and the earth and the mountains, and they declined to bear it and feared it; but man [undertook to] bear it. Indeed, he was unjust and ignorant", "إِنَّا عَرَضۡنَا ٱلۡأَمَانَةَ عَلَى ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَٱلۡجِبَالِ فَأَبَيۡنَ أَن يَحۡمِلۡنَهَا وَأَشۡفَقۡنَ مِنۡهَا وَحَمَلَهَا ٱلۡإِنسَٰنُۖ إِنَّهُۥ كَانَ ظَلُومٗا جَهُولٗا", "Inna AAaradna alamanata AAala alssamawati waalardi waaljibali faabayna an yahmilnaha waashfaqna minha wahamalaha alinsanu innahu kana thalooman jahoolan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (33, 73, "[It was] so that Allah may punish the hypocrite men and hypocrite women and the men and women who associate others with Him and that Allah may accept repentance from the believing men and believing women. And ever is Allah Forgiving and Merciful", "لِّيُعَذِّبَ ٱللَّهُ ٱلۡمُنَٰفِقِينَ وَٱلۡمُنَٰفِقَٰتِ وَٱلۡمُشۡرِكِينَ وَٱلۡمُشۡرِكَٰتِ وَيَتُوبَ ٱللَّهُ عَلَى ٱلۡمُؤۡمِنِينَ وَٱلۡمُؤۡمِنَٰتِۗ وَكَانَ ٱللَّهُ غَفُورٗا رَّحِيمَۢا", "LiyuAAaththiba Allahu almunafiqeena waalmunafiqati waalmushrikeena waalmushrikati wayatooba Allahu AAala almumineena waalmuminati wakana Allahu ghafooran raheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 1, "[All] praise is [due] to Allah, to whom belongs whatever is in the heavens and whatever is in the earth, and to Him belongs [all] praise in the Hereafter. And He is the Wise, the Acquainted", "ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِي لَهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِ وَلَهُ ٱلۡحَمۡدُ فِي ٱلۡأٓخِرَةِۚ وَهُوَ ٱلۡحَكِيمُ ٱلۡخَبِيرُ", "Alhamdu lillahi allathee lahu ma fee alssamawati wama fee alardi walahu alhamdu fee alakhirati wahuwa alhakeemu alkhabeeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 2, "He knows what penetrates into the earth and what emerges from it and what descends from the heaven and what ascends therein. And He is the Merciful, the Forgiving", "يَعۡلَمُ مَا يَلِجُ فِي ٱلۡأَرۡضِ وَمَا يَخۡرُجُ مِنۡهَا وَمَا يَنزِلُ مِنَ ٱلسَّمَآءِ وَمَا يَعۡرُجُ فِيهَاۚ وَهُوَ ٱلرَّحِيمُ ٱلۡغَفُورُ", "YaAAlamu ma yaliju fee alardi wama yakhruju minha wama yanzilu mina alssamai wama yaAAruju feeha wahuwa alrraheemu alghafooru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 3, "But those who disbelieve say, ""The Hour will not come to us."" Say, ""Yes, by my Lord, it will surely come to you. [Allah is] the Knower of the unseen."" Not absent from Him is an atom's weight within the heavens or within the earth or [what is] smaller than that or greater, except that it is in a clear register", "وَقَالَ ٱلَّذِينَ كَفَرُواْ لَا تَأۡتِينَا ٱلسَّاعَةُۖ قُلۡ بَلَىٰ وَرَبِّي لَتَأۡتِيَنَّكُمۡ عَٰلِمِ ٱلۡغَيۡبِۖ لَا يَعۡزُبُ عَنۡهُ مِثۡقَالُ ذَرَّةٖ فِي ٱلسَّمَٰوَٰتِ وَلَا فِي ٱلۡأَرۡضِ وَلَآ أَصۡغَرُ مِن ذَٰلِكَ وَلَآ أَكۡبَرُ إِلَّا فِي كِتَٰبٖ مُّبِينٖ", "Waqala allatheena kafaroo la tateena alssaAAatu qul bala warabbee latatiyannakum AAalimi alghaybi la yaAAzubu AAanhu mithqalu tharratin fee alssamawati wala fee alardi wala asgharu min thalika wala akbaru illa fee kitabin mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 4, "That He may reward those who believe and do righteous deeds. Those will have forgiveness and noble provision", "لِّيَجۡزِيَ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِۚ أُوْلَـٰٓئِكَ لَهُم مَّغۡفِرَةٞ وَرِزۡقٞ كَرِيمٞ", "Liyajziya allatheena amanoo waAAamiloo alssalihati olaika lahum maghfiratun warizqun kareemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 5, "But those who strive against Our verses [seeking] to cause failure - for them will be a painful punishment of foul nature", "وَٱلَّذِينَ سَعَوۡ فِيٓ ءَايَٰتِنَا مُعَٰجِزِينَ أُوْلَـٰٓئِكَ لَهُمۡ عَذَابٞ مِّن رِّجۡزٍ أَلِيمٞ", "Waallatheena saAAaw fee ayatina muAAajizeena olaika lahum AAathabun min rijzin aleemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 6, "And those who have been given knowledge see that what is revealed to you from your Lord is the truth, and it guides to the path of the Exalted in Might, the Praiseworthy", "وَيَرَى ٱلَّذِينَ أُوتُواْ ٱلۡعِلۡمَ ٱلَّذِيٓ أُنزِلَ إِلَيۡكَ مِن رَّبِّكَ هُوَ ٱلۡحَقَّ وَيَهۡدِيٓ إِلَىٰ صِرَٰطِ ٱلۡعَزِيزِ ٱلۡحَمِيدِ", "Wayara allatheena ootoo alAAilma allathee onzila ilayka min rabbika huwa alhaqqa wayahdee ila sirati alAAazeezi alhameedi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 7, "But those who disbelieve say, ""Shall we direct you to a man who will inform you [that] when you have disintegrated in complete disintegration, you will [then] be [recreated] in a new creation", "وَقَالَ ٱلَّذِينَ كَفَرُواْ هَلۡ نَدُلُّكُمۡ عَلَىٰ رَجُلٖ يُنَبِّئُكُمۡ إِذَا مُزِّقۡتُمۡ كُلَّ مُمَزَّقٍ إِنَّكُمۡ لَفِي خَلۡقٖ جَدِيدٍ", "Waqala allatheena kafaroo hal nadullukum AAala rajulin yunabbiokum itha muzziqtum kulla mumazzaqin innakum lafee khalqin jadeedin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 8, "Has he invented about Allah a lie or is there in him madness?"" Rather, they who do not believe in the Hereafter will be in the punishment and [are in] extreme error", "أَفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبًا أَم بِهِۦ جِنَّةُۢۗ بَلِ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱلۡأٓخِرَةِ فِي ٱلۡعَذَابِ وَٱلضَّلَٰلِ ٱلۡبَعِيدِ", "Aftara AAala Allahi kathiban am bihi jinnatun bali allatheena la yuminoona bialakhirati fee alAAathabi waalddalali albaAAeedi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 9, "Then, do they not look at what is before them and what is behind them of the heaven and earth? If We should will, We could cause the earth to swallow them or [could] let fall upon them fragments from the sky. Indeed in that is a sign for every servant turning back [to Allah]", "أَفَلَمۡ يَرَوۡاْ إِلَىٰ مَا بَيۡنَ أَيۡدِيهِمۡ وَمَا خَلۡفَهُم مِّنَ ٱلسَّمَآءِ وَٱلۡأَرۡضِۚ إِن نَّشَأۡ نَخۡسِفۡ بِهِمُ ٱلۡأَرۡضَ أَوۡ نُسۡقِطۡ عَلَيۡهِمۡ كِسَفٗا مِّنَ ٱلسَّمَآءِۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لِّكُلِّ عَبۡدٖ مُّنِيبٖ", "Afalam yaraw ila ma bayna aydeehim wama khalfahum mina alssamai waalardi in nasha nakhsif bihimu alarda aw nusqit AAalayhim kisafan mina alssamai inna fee thalika laayatan likulli AAabdin muneebin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 10, "And We certainly gave David from Us bounty. [We said], ""O mountains, repeat [Our] praises with him, and the birds [as well]."" And We made pliable for him iron", "۞وَلَقَدۡ ءَاتَيۡنَا دَاوُۥدَ مِنَّا فَضۡلٗاۖ يَٰجِبَالُ أَوِّبِي مَعَهُۥ وَٱلطَّيۡرَۖ وَأَلَنَّا لَهُ ٱلۡحَدِيدَ", "Walaqad atayna dawooda minna fadlan ya jibalu awwibee maAAahu waalttayra waalanna lahu alhadeeda");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 11, "[Commanding him], ""Make full coats of mail and calculate [precisely] the links, and work [all of you] righteousness. Indeed I, of what you do, am Seeing", "أَنِ ٱعۡمَلۡ سَٰبِغَٰتٖ وَقَدِّرۡ فِي ٱلسَّرۡدِۖ وَٱعۡمَلُواْ صَٰلِحًاۖ إِنِّي بِمَا تَعۡمَلُونَ بَصِيرٞ", "Ani iAAmal sabighatin waqaddir fee alssardi waiAAmaloo salihan innee bima taAAmaloona baseerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 12, "And to Solomon [We subjected] the wind - its morning [journey was that of] a month - and its afternoon [journey was that of] a month, and We made flow for him a spring of [liquid] copper. And among the jinn were those who worked for him by the permission of his Lord. And whoever deviated among them from Our command - We will make him taste of the punishment of the Blaze", "وَلِسُلَيۡمَٰنَ ٱلرِّيحَ غُدُوُّهَا شَهۡرٞ وَرَوَاحُهَا شَهۡرٞۖ وَأَسَلۡنَا لَهُۥ عَيۡنَ ٱلۡقِطۡرِۖ وَمِنَ ٱلۡجِنِّ مَن يَعۡمَلُ بَيۡنَ يَدَيۡهِ بِإِذۡنِ رَبِّهِۦۖ وَمَن يَزِغۡ مِنۡهُمۡ عَنۡ أَمۡرِنَا نُذِقۡهُ مِنۡ عَذَابِ ٱلسَّعِيرِ", "Walisulaymana alrreeha ghuduwwuha shahrun warawahuha shahrun waasalna lahu AAayna alqitri wamina aljinni man yaAAmalu bayna yadayhi biithni rabbihi waman yazigh minhum AAan amrina nuthiqhu min AAathabi alssaAAeeri");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 13, "They made for him what he willed of elevated chambers, statues, bowls like reservoirs, and stationary kettles. [We said], ""Work, O family of David, in gratitude."" And few of My servants are grateful", "يَعۡمَلُونَ لَهُۥ مَا يَشَآءُ مِن مَّحَٰرِيبَ وَتَمَٰثِيلَ وَجِفَانٖ كَٱلۡجَوَابِ وَقُدُورٖ رَّاسِيَٰتٍۚ ٱعۡمَلُوٓاْ ءَالَ دَاوُۥدَ شُكۡرٗاۚ وَقَلِيلٞ مِّنۡ عِبَادِيَ ٱلشَّكُورُ", "YaAAmaloona lahu ma yashao min mahareeba watamatheela wajifanin kaaljawabi waqudoorin rasiyatin iAAmaloo ala dawooda shukran waqaleelun min AAibadiya alshshakooru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 14, "And when We decreed for Solomon death, nothing indicated to the jinn his death except a creature of the earth eating his staff. But when he fell, it became clear to the jinn that if they had known the unseen, they would not have remained in humiliating punishment", "فَلَمَّا قَضَيۡنَا عَلَيۡهِ ٱلۡمَوۡتَ مَا دَلَّهُمۡ عَلَىٰ مَوۡتِهِۦٓ إِلَّا دَآبَّةُ ٱلۡأَرۡضِ تَأۡكُلُ مِنسَأَتَهُۥۖ فَلَمَّا خَرَّ تَبَيَّنَتِ ٱلۡجِنُّ أَن لَّوۡ كَانُواْ يَعۡلَمُونَ ٱلۡغَيۡبَ مَا لَبِثُواْ فِي ٱلۡعَذَابِ ٱلۡمُهِينِ", "Falamma qadayna AAalayhi almawta ma dallahum AAala mawtihi illa dabbatu alardi takulu minsaatahu falamma kharra tabayyanati aljinnu an law kanoo yaAAlamoona alghayba ma labithoo fee alAAathabi almuheeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 15, "There was for [the tribe of] Saba' in their dwelling place a sign: two [fields of] gardens on the right and on the left. [They were told], ""Eat from the provisions of your Lord and be grateful to Him. A good land [have you], and a forgiving Lord", "لَقَدۡ كَانَ لِسَبَإٖ فِي مَسۡكَنِهِمۡ ءَايَةٞۖ جَنَّتَانِ عَن يَمِينٖ وَشِمَالٖۖ كُلُواْ مِن رِّزۡقِ رَبِّكُمۡ وَٱشۡكُرُواْ لَهُۥۚ بَلۡدَةٞ طَيِّبَةٞ وَرَبٌّ غَفُورٞ", "Laqad kana lisabain fee maskanihim ayatun jannatani AAan yameenin washimalin kuloo min rizqi rabbikum waoshkuroo lahu baldatun tayyibatun warabbun ghafoorun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 16, "But they turned away [refusing], so We sent upon them the flood of the dam, and We replaced their two [fields of] gardens with gardens of bitter fruit, tamarisks and something of sparse lote trees", "فَأَعۡرَضُواْ فَأَرۡسَلۡنَا عَلَيۡهِمۡ سَيۡلَ ٱلۡعَرِمِ وَبَدَّلۡنَٰهُم بِجَنَّتَيۡهِمۡ جَنَّتَيۡنِ ذَوَاتَيۡ أُكُلٍ خَمۡطٖ وَأَثۡلٖ وَشَيۡءٖ مِّن سِدۡرٖ قَلِيلٖ", "FaaAAradoo faarsalna AAalayhim sayla alAAarimi wabaddalnahum bijannatayhim jannatayni thawatay okulin khamtin waathlin washayin min sidrin qaleelin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 17, "[By] that We repaid them because they disbelieved. And do We [thus] repay except the ungrateful", "ذَٰلِكَ جَزَيۡنَٰهُم بِمَا كَفَرُواْۖ وَهَلۡ نُجَٰزِيٓ إِلَّا ٱلۡكَفُورَ", "Thalika jazaynahum bima kafaroo wahal nujazee illa alkafoora");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 18, "And We placed between them and the cities which We had blessed [many] visible cities. And We determined between them the [distances of] journey, [saying], ""Travel between them by night or day in safety", "وَجَعَلۡنَا بَيۡنَهُمۡ وَبَيۡنَ ٱلۡقُرَى ٱلَّتِي بَٰرَكۡنَا فِيهَا قُرٗى ظَٰهِرَةٗ وَقَدَّرۡنَا فِيهَا ٱلسَّيۡرَۖ سِيرُواْ فِيهَا لَيَالِيَ وَأَيَّامًا ءَامِنِينَ", "WajaAAalna baynahum wabayna alqura allatee barakna feeha quran thahiratan waqaddarna feeha alssayra seeroo feeha layaliya waayyaman amineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 19, "But [insolently] they said, ""Our Lord, lengthen the distance between our journeys,"" and wronged themselves, so We made them narrations and dispersed them in total dispersion. Indeed in that are signs for everyone patient and grateful", "فَقَالُواْ رَبَّنَا بَٰعِدۡ بَيۡنَ أَسۡفَارِنَا وَظَلَمُوٓاْ أَنفُسَهُمۡ فَجَعَلۡنَٰهُمۡ أَحَادِيثَ وَمَزَّقۡنَٰهُمۡ كُلَّ مُمَزَّقٍۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّكُلِّ صَبَّارٖ شَكُورٖ", "Faqaloo rabbana baAAid bayna asfarina wathalamoo anfusahum fajaAAalnahum ahadeetha wamazzaqnahum kulla mumazzaqin inna fee thalika laayatin likulli sabbarin shakoorin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 20, "And Iblees had already confirmed through them his assumption, so they followed him, except for a party of believers", "وَلَقَدۡ صَدَّقَ عَلَيۡهِمۡ إِبۡلِيسُ ظَنَّهُۥ فَٱتَّبَعُوهُ إِلَّا فَرِيقٗا مِّنَ ٱلۡمُؤۡمِنِينَ", "Walaqad saddaqa AAalayhim ibleesu thannahu faittabaAAoohu illa fareeqan mina almumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 21, "And he had over them no authority except [it was decreed] that We might make evident who believes in the Hereafter from who is thereof in doubt. And your Lord, over all things, is Guardian", "وَمَا كَانَ لَهُۥ عَلَيۡهِم مِّن سُلۡطَٰنٍ إِلَّا لِنَعۡلَمَ مَن يُؤۡمِنُ بِٱلۡأٓخِرَةِ مِمَّنۡ هُوَ مِنۡهَا فِي شَكّٖۗ وَرَبُّكَ عَلَىٰ كُلِّ شَيۡءٍ حَفِيظٞ", "Wama kana lahu AAalayhim min sultanin illa linaAAlama man yuminu bialakhirati mimman huwa minha fee shakkin warabbuka AAala kulli shayin hafeethun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 22, "Say, [O Muhammad], ""Invoke those you claim [as deities] besides Allah."" They do not possess an atom's weight [of ability] in the heavens or on the earth, and they do not have therein any partnership [with Him], nor is there for Him from among them any assistant", "قُلِ ٱدۡعُواْ ٱلَّذِينَ زَعَمۡتُم مِّن دُونِ ٱللَّهِ لَا يَمۡلِكُونَ مِثۡقَالَ ذَرَّةٖ فِي ٱلسَّمَٰوَٰتِ وَلَا فِي ٱلۡأَرۡضِ وَمَا لَهُمۡ فِيهِمَا مِن شِرۡكٖ وَمَا لَهُۥ مِنۡهُم مِّن ظَهِيرٖ", "Quli odAAoo allatheena zaAAamtum min dooni Allahi la yamlikoona mithqala tharratin fee alssamawati wala fee alardi wama lahum feehima min shirkin wama lahu minhum min thaheerin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 23, "And intercession does not benefit with Him except for one whom He permits. [And those wait] until, when terror is removed from their hearts, they will say [to one another], ""What has your Lord said?"" They will say, ""The truth."" And He is the Most High, the Grand", "وَلَا تَنفَعُ ٱلشَّفَٰعَةُ عِندَهُۥٓ إِلَّا لِمَنۡ أَذِنَ لَهُۥۚ حَتَّىٰٓ إِذَا فُزِّعَ عَن قُلُوبِهِمۡ قَالُواْ مَاذَا قَالَ رَبُّكُمۡۖ قَالُواْ ٱلۡحَقَّۖ وَهُوَ ٱلۡعَلِيُّ ٱلۡكَبِيرُ", "Wala tanfaAAu alshshafaAAatu AAindahu illa liman athina lahu hatta itha fuzziAAa AAan quloobihim qaloo matha qala rabbukum qaloo alhaqqa wahuwa alAAaliyyu alkabeeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 24, "Say, ""Who provides for you from the heavens and the earth?"" Say, ""Allah. And indeed, we or you are either upon guidance or in clear error", "۞قُلۡ مَن يَرۡزُقُكُم مِّنَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ قُلِ ٱللَّهُۖ وَإِنَّآ أَوۡ إِيَّاكُمۡ لَعَلَىٰ هُدًى أَوۡ فِي ضَلَٰلٖ مُّبِينٖ", "Qul man yarzuqukum mina alssamawati waalardi quli Allahu wainna aw iyyakum laAAala hudan aw fee dalalin mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 25, "Say, ""You will not be asked about what we committed, and we will not be asked about what you do", "قُل لَّا تُسۡـَٔلُونَ عَمَّآ أَجۡرَمۡنَا وَلَا نُسۡـَٔلُ عَمَّا تَعۡمَلُونَ", "Qul la tusaloona AAamma ajramna wala nusalu AAamma taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 26, "Say, ""Our Lord will bring us together; then He will judge between us in truth. And He is the Knowing Judge", "قُلۡ يَجۡمَعُ بَيۡنَنَا رَبُّنَا ثُمَّ يَفۡتَحُ بَيۡنَنَا بِٱلۡحَقِّ وَهُوَ ٱلۡفَتَّاحُ ٱلۡعَلِيمُ", "Qul yajmaAAu baynana rabbuna thumma yaftahu baynana bialhaqqi wahuwa alfattahu alAAaleemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 27, "Say, ""Show me those whom you have attached to Him as partners. No! Rather, He [alone] is Allah, the Exalted in Might, the Wise", "قُلۡ أَرُونِيَ ٱلَّذِينَ أَلۡحَقۡتُم بِهِۦ شُرَكَآءَۖ كَلَّاۚ بَلۡ هُوَ ٱللَّهُ ٱلۡعَزِيزُ ٱلۡحَكِيمُ", "Qul arooniya allatheena alhaqtum bihi shurakaa kalla bal huwa Allahu alAAazeezu alhakeemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 28, "And We have not sent you except comprehensively to mankind as a bringer of good tidings and a warner. But most of the people do not know", "وَمَآ أَرۡسَلۡنَٰكَ إِلَّا كَآفَّةٗ لِّلنَّاسِ بَشِيرٗا وَنَذِيرٗا وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَعۡلَمُونَ", "Wama arsalnaka illa kaffatan lilnnasi basheeran wanatheeran walakinna akthara alnnasi la yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 29, "And they say, ""When is this promise, if you should be truthful", "وَيَقُولُونَ مَتَىٰ هَٰذَا ٱلۡوَعۡدُ إِن كُنتُمۡ صَٰدِقِينَ", "Wayaqooloona mata hatha alwaAAdu in kuntum sadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 30, "Say, ""For you is the appointment of a Day [when] you will not remain thereafter an hour, nor will you precede [it]", "قُل لَّكُم مِّيعَادُ يَوۡمٖ لَّا تَسۡتَـٔۡخِرُونَ عَنۡهُ سَاعَةٗ وَلَا تَسۡتَقۡدِمُونَ", "Qul lakum meeAAadu yawmin la tastakhiroona AAanhu saAAatan wala tastaqdimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 31, "And those who disbelieve say, ""We will never believe in this Qur'an nor in that before it."" But if you could see when the wrongdoers are made to stand before their Lord, refuting each other's words... Those who were oppressed will say to those who were arrogant, ""If not for you, we would have been believers", "وَقَالَ ٱلَّذِينَ كَفَرُواْ لَن نُّؤۡمِنَ بِهَٰذَا ٱلۡقُرۡءَانِ وَلَا بِٱلَّذِي بَيۡنَ يَدَيۡهِۗ وَلَوۡ تَرَىٰٓ إِذِ ٱلظَّـٰلِمُونَ مَوۡقُوفُونَ عِندَ رَبِّهِمۡ يَرۡجِعُ بَعۡضُهُمۡ إِلَىٰ بَعۡضٍ ٱلۡقَوۡلَ يَقُولُ ٱلَّذِينَ ٱسۡتُضۡعِفُواْ لِلَّذِينَ ٱسۡتَكۡبَرُواْ لَوۡلَآ أَنتُمۡ لَكُنَّا مُؤۡمِنِينَ", "Waqala allatheena kafaroo lan numina bihatha alqurani wala biallathee bayna yadayhi walaw tara ithi alththalimoona mawqoofoona AAinda rabbihim yarjiAAu baAAduhum ila baAAdin alqawla yaqoolu allatheena istudAAifoo lillatheena istakbaroo lawla antum lakunna mumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 32, "Those who were arrogant will say to those who were oppressed, ""Did we avert you from guidance after it had come to you? Rather, you were criminals", "قَالَ ٱلَّذِينَ ٱسۡتَكۡبَرُواْ لِلَّذِينَ ٱسۡتُضۡعِفُوٓاْ أَنَحۡنُ صَدَدۡنَٰكُمۡ عَنِ ٱلۡهُدَىٰ بَعۡدَ إِذۡ جَآءَكُمۖ بَلۡ كُنتُم مُّجۡرِمِينَ", "Qala allatheena istakbaroo lillatheena istudAAifoo anahnu sadadnakum AAani alhuda baAAda ith jaakum bal kuntum mujrimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 33, "Those who were oppressed will say to those who were arrogant, ""Rather, [it was your] conspiracy of night and day when you were ordering us to disbelieve in Allah and attribute to Him equals."" But they will [all] confide regret when they see the punishment; and We will put shackles on the necks of those who disbelieved. Will they be recompensed except for what they used to do", "وَقَالَ ٱلَّذِينَ ٱسۡتُضۡعِفُواْ لِلَّذِينَ ٱسۡتَكۡبَرُواْ بَلۡ مَكۡرُ ٱلَّيۡلِ وَٱلنَّهَارِ إِذۡ تَأۡمُرُونَنَآ أَن نَّكۡفُرَ بِٱللَّهِ وَنَجۡعَلَ لَهُۥٓ أَندَادٗاۚ وَأَسَرُّواْ ٱلنَّدَامَةَ لَمَّا رَأَوُاْ ٱلۡعَذَابَۚ وَجَعَلۡنَا ٱلۡأَغۡلَٰلَ فِيٓ أَعۡنَاقِ ٱلَّذِينَ كَفَرُواْۖ هَلۡ يُجۡزَوۡنَ إِلَّا مَا كَانُواْ يَعۡمَلُونَ", "Waqala allatheena istudAAifoo lillatheena istakbaroo bal makru allayli waalnnahari ith tamuroonana an nakfura biAllahi wanajAAala lahu andadan waasarroo alnnadamata lamma raawoo alAAathaba wajaAAalna alaghlala fee aAAnaqi allatheena kafaroo hal yujzawna illa ma kanoo yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 34, "And We did not send into a city any warner except that its affluent said, ""Indeed we, in that with which you were sent, are disbelievers", "وَمَآ أَرۡسَلۡنَا فِي قَرۡيَةٖ مِّن نَّذِيرٍ إِلَّا قَالَ مُتۡرَفُوهَآ إِنَّا بِمَآ أُرۡسِلۡتُم بِهِۦ كَٰفِرُونَ", "Wama arsalna fee qaryatin min natheerin illa qala mutrafooha inna bima orsiltum bihi kafiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 35, "And they said, ""We are more [than the believers] in wealth and children, and we are not to be punished", "وَقَالُواْ نَحۡنُ أَكۡثَرُ أَمۡوَٰلٗا وَأَوۡلَٰدٗا وَمَا نَحۡنُ بِمُعَذَّبِينَ", "Waqaloo nahnu aktharu amwalan waawladan wama nahnu bimuAAaththabeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 36, "Say, ""Indeed, my Lord extends provision for whom He wills and restricts [it], but most of the people do not know", "قُلۡ إِنَّ رَبِّي يَبۡسُطُ ٱلرِّزۡقَ لِمَن يَشَآءُ وَيَقۡدِرُ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَعۡلَمُونَ", "Qul inna rabbee yabsutu alrrizqa liman yashao wayaqdiru walakinna akthara alnnasi la yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 37, "And it is not your wealth or your children that bring you nearer to Us in position, but it is [by being] one who has believed and done righteousness. For them there will be the double reward for what they did, and they will be in the upper chambers [of Paradise], safe [and secure]", "وَمَآ أَمۡوَٰلُكُمۡ وَلَآ أَوۡلَٰدُكُم بِٱلَّتِي تُقَرِّبُكُمۡ عِندَنَا زُلۡفَىٰٓ إِلَّا مَنۡ ءَامَنَ وَعَمِلَ صَٰلِحٗا فَأُوْلَـٰٓئِكَ لَهُمۡ جَزَآءُ ٱلضِّعۡفِ بِمَا عَمِلُواْ وَهُمۡ فِي ٱلۡغُرُفَٰتِ ءَامِنُونَ", "Wama amwalukum wala awladukum biallatee tuqarribukum AAindana zulfa illa man amana waAAamila salihan faolaika lahum jazao alddiAAfi bima AAamiloo wahum fee alghurufati aminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 38, "And the ones who strive against Our verses to cause [them] failure - those will be brought into the punishment [to remain]", "وَٱلَّذِينَ يَسۡعَوۡنَ فِيٓ ءَايَٰتِنَا مُعَٰجِزِينَ أُوْلَـٰٓئِكَ فِي ٱلۡعَذَابِ مُحۡضَرُونَ", "Waallatheena yasAAawna fee ayatina muAAajizeena olaika fee alAAathabi muhdaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 39, "Say, ""Indeed, my Lord extends provision for whom He wills of His servants and restricts [it] for him. But whatever thing you spend [in His cause] - He will compensate it; and He is the best of providers", "قُلۡ إِنَّ رَبِّي يَبۡسُطُ ٱلرِّزۡقَ لِمَن يَشَآءُ مِنۡ عِبَادِهِۦ وَيَقۡدِرُ لَهُۥۚ وَمَآ أَنفَقۡتُم مِّن شَيۡءٖ فَهُوَ يُخۡلِفُهُۥۖ وَهُوَ خَيۡرُ ٱلرَّـٰزِقِينَ", "Qul inna rabbee yabsutu alrrizqa liman yashao min AAibadihi wayaqdiru lahu wama anfaqtum min shayin fahuwa yukhlifuhu wahuwa khayru alrraziqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 40, "And [mention] the Day when He will gather them all and then say to the angels, ""Did these [people] used to worship you", "وَيَوۡمَ يَحۡشُرُهُمۡ جَمِيعٗا ثُمَّ يَقُولُ لِلۡمَلَـٰٓئِكَةِ أَهَـٰٓؤُلَآءِ إِيَّاكُمۡ كَانُواْ يَعۡبُدُونَ", "Wayawma yahshuruhum jameeAAan thumma yaqoolu lilmalaikati ahaolai iyyakum kanoo yaAAbudoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 41, "They will say, ""Exalted are You! You, [O Allah], are our benefactor not them. Rather, they used to worship the jinn; most of them were believers in them", "قَالُواْ سُبۡحَٰنَكَ أَنتَ وَلِيُّنَا مِن دُونِهِمۖ بَلۡ كَانُواْ يَعۡبُدُونَ ٱلۡجِنَّۖ أَكۡثَرُهُم بِهِم مُّؤۡمِنُونَ", "Qaloo subhanaka anta waliyyuna min doonihim bal kanoo yaAAbudoona aljinna aktharuhum bihim muminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 42, "But today you do not hold for one another [the power of] benefit or harm, and We will say to those who wronged, ""Taste the punishment of the Fire, which you used to deny", "فَٱلۡيَوۡمَ لَا يَمۡلِكُ بَعۡضُكُمۡ لِبَعۡضٖ نَّفۡعٗا وَلَا ضَرّٗا وَنَقُولُ لِلَّذِينَ ظَلَمُواْ ذُوقُواْ عَذَابَ ٱلنَّارِ ٱلَّتِي كُنتُم بِهَا تُكَذِّبُونَ", "Faalyawma la yamliku baAAdukum libaAAdin nafAAan wala darran wanaqoolu lillatheena thalamoo thooqoo AAathaba alnnari allatee kuntum biha tukaththiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 43, "And when our verses are recited to them as clear evidences, they say, ""This is not but a man who wishes to avert you from that which your fathers were worshipping."" And they say, ""This is not except a lie invented."" And those who disbelieve say of the truth when it has come to them, ""This is not but obvious magic", "وَإِذَا تُتۡلَىٰ عَلَيۡهِمۡ ءَايَٰتُنَا بَيِّنَٰتٖ قَالُواْ مَا هَٰذَآ إِلَّا رَجُلٞ يُرِيدُ أَن يَصُدَّكُمۡ عَمَّا كَانَ يَعۡبُدُ ءَابَآؤُكُمۡ وَقَالُواْ مَا هَٰذَآ إِلَّآ إِفۡكٞ مُّفۡتَرٗىۚ وَقَالَ ٱلَّذِينَ كَفَرُواْ لِلۡحَقِّ لَمَّا جَآءَهُمۡ إِنۡ هَٰذَآ إِلَّا سِحۡرٞ مُّبِينٞ", "Waitha tutla AAalayhim ayatuna bayyinatin qaloo ma hatha illa rajulun yureedu an yasuddakum AAamma kana yaAAbudu abaokum waqaloo ma hatha illa ifkun muftaran waqala allatheena kafaroo lilhaqqi lamma jaahum in hatha illa sihrun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 44, "And We had not given them any scriptures which they could study, and We had not sent to them before you, [O Muhammad], any warner", "وَمَآ ءَاتَيۡنَٰهُم مِّن كُتُبٖ يَدۡرُسُونَهَاۖ وَمَآ أَرۡسَلۡنَآ إِلَيۡهِمۡ قَبۡلَكَ مِن نَّذِيرٖ", "Wama ataynahum min kutubin yadrusoonaha wama arsalna ilayhim qablaka min natheerin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 45, "And those before them denied, and the people of Makkah have not attained a tenth of what We had given them. But the former peoples denied My messengers, so how [terrible] was My reproach", "وَكَذَّبَ ٱلَّذِينَ مِن قَبۡلِهِمۡ وَمَا بَلَغُواْ مِعۡشَارَ مَآ ءَاتَيۡنَٰهُمۡ فَكَذَّبُواْ رُسُلِيۖ فَكَيۡفَ كَانَ نَكِيرِ", "Wakaththaba allatheena min qablihim wama balaghoo miAAshara ma ataynahum fakaththaboo rusulee fakayfa kana nakeeri");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 46, "Say, ""I only advise you of one [thing] - that you stand for Allah, [seeking truth] in pairs and individually, and then give thought."" There is not in your companion any madness. He is only a warner to you before a severe punishment", "۞قُلۡ إِنَّمَآ أَعِظُكُم بِوَٰحِدَةٍۖ أَن تَقُومُواْ لِلَّهِ مَثۡنَىٰ وَفُرَٰدَىٰ ثُمَّ تَتَفَكَّرُواْۚ مَا بِصَاحِبِكُم مِّن جِنَّةٍۚ إِنۡ هُوَ إِلَّا نَذِيرٞ لَّكُم بَيۡنَ يَدَيۡ عَذَابٖ شَدِيدٖ", "Qul innama aAAithukum biwahidatin an taqoomoo lillahi mathna wafurada thumma tatafakkaroo ma bisahibikum min jinnatin in huwa illa natheerun lakum bayna yaday AAathabin shadeedin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 47, "Say, ""Whatever payment I might have asked of you - it is yours. My payment is only from Allah, and He is, over all things, Witness", "قُلۡ مَا سَأَلۡتُكُم مِّنۡ أَجۡرٖ فَهُوَ لَكُمۡۖ إِنۡ أَجۡرِيَ إِلَّا عَلَى ٱللَّهِۖ وَهُوَ عَلَىٰ كُلِّ شَيۡءٖ شَهِيدٞ", "Qul ma saaltukum min ajrin fahuwa lakum in ajriya illa AAala Allahi wahuwa AAala kulli shayin shaheedun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 48, "Say, ""Indeed, my Lord projects the truth. Knower of the unseen", "قُلۡ إِنَّ رَبِّي يَقۡذِفُ بِٱلۡحَقِّ عَلَّـٰمُ ٱلۡغُيُوبِ", "Qul inna rabbee yaqthifu bialhaqqi AAallamu alghuyoobi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 49, "Say, ""The truth has come, and falsehood can neither begin [anything] nor repeat [it]", "قُلۡ جَآءَ ٱلۡحَقُّ وَمَا يُبۡدِئُ ٱلۡبَٰطِلُ وَمَا يُعِيدُ", "Qul jaa alhaqqu wama yubdio albatilu wama yuAAeedu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 50, "Say, ""If I should err, I would only err against myself. But if I am guided, it is by what my Lord reveals to me. Indeed, He is Hearing and near", "قُلۡ إِن ضَلَلۡتُ فَإِنَّمَآ أَضِلُّ عَلَىٰ نَفۡسِيۖ وَإِنِ ٱهۡتَدَيۡتُ فَبِمَا يُوحِيٓ إِلَيَّ رَبِّيٓۚ إِنَّهُۥ سَمِيعٞ قَرِيبٞ", "Qul in dalaltu fainnama adillu AAala nafsee waini ihtadaytu fabima yoohee ilayya rabbee innahu sameeAAun qareebun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 51, "And if you could see when they are terrified but there is no escape, and they will be seized from a place nearby", "وَلَوۡ تَرَىٰٓ إِذۡ فَزِعُواْ فَلَا فَوۡتَ وَأُخِذُواْ مِن مَّكَانٖ قَرِيبٖ", "Walaw tara ith faziAAoo fala fawta waokhithoo min makanin qareebin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 52, "And they will [then] say, ""We believe in it!"" But how for them will be the taking [of faith] from a place far away", "وَقَالُوٓاْ ءَامَنَّا بِهِۦ وَأَنَّىٰ لَهُمُ ٱلتَّنَاوُشُ مِن مَّكَانِۭ بَعِيدٖ", "Waqaloo amanna bihi waanna lahumu alttanawushu min makanin baAAeedin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 53, "And they had already disbelieved in it before and would assault the unseen from a place far away", "وَقَدۡ كَفَرُواْ بِهِۦ مِن قَبۡلُۖ وَيَقۡذِفُونَ بِٱلۡغَيۡبِ مِن مَّكَانِۭ بَعِيدٖ", "Waqad kafaroo bihi min qablu wayaqthifoona bialghaybi min makanin baAAeedin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (34, 54, "And prevention will be placed between them and what they desire, as was done with their kind before. Indeed, they were in disquieting denial", "وَحِيلَ بَيۡنَهُمۡ وَبَيۡنَ مَا يَشۡتَهُونَ كَمَا فُعِلَ بِأَشۡيَاعِهِم مِّن قَبۡلُۚ إِنَّهُمۡ كَانُواْ فِي شَكّٖ مُّرِيبِۭ", "Waheela baynahum wabayna ma yashtahoona kama fuAAila biashyaAAihim min qablu innahum kanoo fee shakkin mureebin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 1, "[All] praise is [due] to Allah, Creator of the heavens and the earth, [who] made the angels messengers having wings, two or three or four. He increases in creation what He wills. Indeed, Allah is over all things competent", "ٱلۡحَمۡدُ لِلَّهِ فَاطِرِ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ جَاعِلِ ٱلۡمَلَـٰٓئِكَةِ رُسُلًا أُوْلِيٓ أَجۡنِحَةٖ مَّثۡنَىٰ وَثُلَٰثَ وَرُبَٰعَۚ يَزِيدُ فِي ٱلۡخَلۡقِ مَا يَشَآءُۚ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ", "Alhamdu lillahi fatiri alssamawati waalardi jaAAili almalaikati rusulan olee ajnihatin mathna wathulatha warubaAAa yazeedu fee alkhalqi ma yashao inna Allaha AAala kulli shayin qadeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 2, "Whatever Allah grants to people of mercy - none can withhold it; and whatever He withholds - none can release it thereafter. And He is the Exalted in Might, the Wise", "مَّا يَفۡتَحِ ٱللَّهُ لِلنَّاسِ مِن رَّحۡمَةٖ فَلَا مُمۡسِكَ لَهَاۖ وَمَا يُمۡسِكۡ فَلَا مُرۡسِلَ لَهُۥ مِنۢ بَعۡدِهِۦۚ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ", "Ma yaftahi Allahu lilnnasi min rahmatin fala mumsika laha wama yumsik fala mursila lahu min baAAdihi wahuwa alAAazeezu alhakeemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 3, "O mankind, remember the favor of Allah upon you. Is there any creator other than Allah who provides for you from the heaven and earth? There is no deity except Him, so how are you deluded", "يَـٰٓأَيُّهَا ٱلنَّاسُ ٱذۡكُرُواْ نِعۡمَتَ ٱللَّهِ عَلَيۡكُمۡۚ هَلۡ مِنۡ خَٰلِقٍ غَيۡرُ ٱللَّهِ يَرۡزُقُكُم مِّنَ ٱلسَّمَآءِ وَٱلۡأَرۡضِۚ لَآ إِلَٰهَ إِلَّا هُوَۖ فَأَنَّىٰ تُؤۡفَكُونَ", "Ya ayyuha alnnasu othkuroo niAAmata Allahi AAalaykum hal min khaliqin ghayru Allahi yarzuqukum mina alssamai waalardi la ilaha illa huwa faanna tufakoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 4, "And if they deny you, [O Muhammad] - already were messengers denied before you. And to Allah are returned [all] matters", "وَإِن يُكَذِّبُوكَ فَقَدۡ كُذِّبَتۡ رُسُلٞ مِّن قَبۡلِكَۚ وَإِلَى ٱللَّهِ تُرۡجَعُ ٱلۡأُمُورُ", "Wain yukaththibooka faqad kuththibat rusulun min qablika waila Allahi turjaAAu alomooru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 5, "O mankind, indeed the promise of Allah is truth, so let not the worldly life delude you and be not deceived about Allah by the Deceiver", "يَـٰٓأَيُّهَا ٱلنَّاسُ إِنَّ وَعۡدَ ٱللَّهِ حَقّٞۖ فَلَا تَغُرَّنَّكُمُ ٱلۡحَيَوٰةُ ٱلدُّنۡيَا وَلَا يَغُرَّنَّكُم بِٱللَّهِ ٱلۡغَرُورُ", "Ya ayyuha alnnasu inna waAAda Allahi haqqun fala taghurrannakumu alhayatu alddunya wala yaghurrannakum biAllahi algharooru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 6, "Indeed, Satan is an enemy to you; so take him as an enemy. He only invites his party to be among the companions of the Blaze", "إِنَّ ٱلشَّيۡطَٰنَ لَكُمۡ عَدُوّٞ فَٱتَّخِذُوهُ عَدُوًّاۚ إِنَّمَا يَدۡعُواْ حِزۡبَهُۥ لِيَكُونُواْ مِنۡ أَصۡحَٰبِ ٱلسَّعِيرِ", "Inna alshshaytana lakum AAaduwwun faittakhithoohu AAaduwwan innama yadAAoo hizbahu liyakoonoo min ashabi alssaAAeeri");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 7, "Those who disbelieve will have a severe punishment, and those who believe and do righteous deeds will have forgiveness and great reward", "ٱلَّذِينَ كَفَرُواْ لَهُمۡ عَذَابٞ شَدِيدٞۖ وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ لَهُم مَّغۡفِرَةٞ وَأَجۡرٞ كَبِيرٌ", "Allatheena kafaroo lahum AAathabun shadeedun waallatheena amanoo waAAamiloo alssalihati lahum maghfiratun waajrun kabeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 8, "Then is one to whom the evil of his deed has been made attractive so he considers it good [like one rightly guided]? For indeed, Allah sends astray whom He wills and guides whom He wills. So do not let yourself perish over them in regret. Indeed, Allah is Knowing of what they do", "أَفَمَن زُيِّنَ لَهُۥ سُوٓءُ عَمَلِهِۦ فَرَءَاهُ حَسَنٗاۖ فَإِنَّ ٱللَّهَ يُضِلُّ مَن يَشَآءُ وَيَهۡدِي مَن يَشَآءُۖ فَلَا تَذۡهَبۡ نَفۡسُكَ عَلَيۡهِمۡ حَسَرَٰتٍۚ إِنَّ ٱللَّهَ عَلِيمُۢ بِمَا يَصۡنَعُونَ", "Afaman zuyyina lahu sooo AAamalihi faraahu hasanan fainna Allaha yudillu man yashao wayahdee man yashao fala tathhab nafsuka AAalayhim hasaratin inna Allaha AAaleemun bima yasnaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 9, "And it is Allah who sends the winds, and they stir the clouds, and We drive them to a dead land and give life thereby to the earth after its lifelessness. Thus is the resurrection", "وَٱللَّهُ ٱلَّذِيٓ أَرۡسَلَ ٱلرِّيَٰحَ فَتُثِيرُ سَحَابٗا فَسُقۡنَٰهُ إِلَىٰ بَلَدٖ مَّيِّتٖ فَأَحۡيَيۡنَا بِهِ ٱلۡأَرۡضَ بَعۡدَ مَوۡتِهَاۚ كَذَٰلِكَ ٱلنُّشُورُ", "WaAllahu allathee arsala alrriyaha fatutheeru sahaban fasuqnahu ila baladin mayyitin faahyayna bihi alarda baAAda mawtiha kathalika alnnushooru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 10, "Whoever desires honor [through power] - then to Allah belongs all honor. To Him ascends good speech, and righteous work raises it. But they who plot evil deeds will have a severe punishment, and the plotting of those - it will perish", "مَن كَانَ يُرِيدُ ٱلۡعِزَّةَ فَلِلَّهِ ٱلۡعِزَّةُ جَمِيعًاۚ إِلَيۡهِ يَصۡعَدُ ٱلۡكَلِمُ ٱلطَّيِّبُ وَٱلۡعَمَلُ ٱلصَّـٰلِحُ يَرۡفَعُهُۥۚ وَٱلَّذِينَ يَمۡكُرُونَ ٱلسَّيِّـَٔاتِ لَهُمۡ عَذَابٞ شَدِيدٞۖ وَمَكۡرُ أُوْلَـٰٓئِكَ هُوَ يَبُورُ", "Man kana yureedu alAAizzata falillahi alAAizzatu jameeAAan ilayhi yasAAadu alkalimu alttayyibu waalAAamalu alssalihu yarfaAAuhu waallatheena yamkuroona alssayyiati lahum AAathabun shadeedun wamakru olaika huwa yabooru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 11, "And Allah created you from dust, then from a sperm-drop; then He made you mates. And no female conceives nor does she give birth except with His knowledge. And no aged person is granted [additional] life nor is his lifespan lessened but that it is in a register. Indeed, that for Allah is easy", "وَٱللَّهُ خَلَقَكُم مِّن تُرَابٖ ثُمَّ مِن نُّطۡفَةٖ ثُمَّ جَعَلَكُمۡ أَزۡوَٰجٗاۚ وَمَا تَحۡمِلُ مِنۡ أُنثَىٰ وَلَا تَضَعُ إِلَّا بِعِلۡمِهِۦۚ وَمَا يُعَمَّرُ مِن مُّعَمَّرٖ وَلَا يُنقَصُ مِنۡ عُمُرِهِۦٓ إِلَّا فِي كِتَٰبٍۚ إِنَّ ذَٰلِكَ عَلَى ٱللَّهِ يَسِيرٞ", "WaAllahu khalaqakum min turabin thumma min nutfatin thumma jaAAalakum azwajan wama tahmilu min ontha wala tadaAAu illa biAAilmihi wama yuAAammaru min muAAammarin wala yunqasu min AAumurihi illa fee kitabin inna thalika AAala Allahi yaseerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 12, "And not alike are the two bodies of water. One is fresh and sweet, palatable for drinking, and one is salty and bitter. And from each you eat tender meat and extract ornaments which you wear, and you see the ships plowing through [them] that you might seek of His bounty; and perhaps you will be grateful", "وَمَا يَسۡتَوِي ٱلۡبَحۡرَانِ هَٰذَا عَذۡبٞ فُرَاتٞ سَآئِغٞ شَرَابُهُۥ وَهَٰذَا مِلۡحٌ أُجَاجٞۖ وَمِن كُلّٖ تَأۡكُلُونَ لَحۡمٗا طَرِيّٗا وَتَسۡتَخۡرِجُونَ حِلۡيَةٗ تَلۡبَسُونَهَاۖ وَتَرَى ٱلۡفُلۡكَ فِيهِ مَوَاخِرَ لِتَبۡتَغُواْ مِن فَضۡلِهِۦ وَلَعَلَّكُمۡ تَشۡكُرُونَ", "Wama yastawee albahrani hatha AAathbun furatun saighun sharabuhu wahatha milhun ojajun wamin kullin takuloona lahman tariyyan watastakhrijoona hilyatan talbasoonaha watara alfulka feehi mawakhira litabtaghoo min fadlihi walaAAallakum tashkuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 13, "He causes the night to enter the day, and He causes the day to enter the night and has subjected the sun and the moon - each running [its course] for a specified term. That is Allah, your Lord; to Him belongs sovereignty. And those whom you invoke other than Him do not possess [as much as] the membrane of a date seed", "يُولِجُ ٱلَّيۡلَ فِي ٱلنَّهَارِ وَيُولِجُ ٱلنَّهَارَ فِي ٱلَّيۡلِ وَسَخَّرَ ٱلشَّمۡسَ وَٱلۡقَمَرَۖ كُلّٞ يَجۡرِي لِأَجَلٖ مُّسَمّٗىۚ ذَٰلِكُمُ ٱللَّهُ رَبُّكُمۡ لَهُ ٱلۡمُلۡكُۚ وَٱلَّذِينَ تَدۡعُونَ مِن دُونِهِۦ مَا يَمۡلِكُونَ مِن قِطۡمِيرٍ", "Yooliju allayla fee alnnahari wayooliju alnnahara fee allayli wasakhkhara alshshamsa waalqamara kullun yajree liajalin musamman thalikumu Allahu rabbukum lahu almulku waallatheena tadAAoona min doonihi ma yamlikoona min qitmeerin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 14, "If you invoke them, they do not hear your supplication; and if they heard, they would not respond to you. And on the Day of Resurrection they will deny your association. And none can inform you like [one] Acquainted [with all matters]", "إِن تَدۡعُوهُمۡ لَا يَسۡمَعُواْ دُعَآءَكُمۡ وَلَوۡ سَمِعُواْ مَا ٱسۡتَجَابُواْ لَكُمۡۖ وَيَوۡمَ ٱلۡقِيَٰمَةِ يَكۡفُرُونَ بِشِرۡكِكُمۡۚ وَلَا يُنَبِّئُكَ مِثۡلُ خَبِيرٖ", "In tadAAoohum la yasmaAAoo duAAaakum walaw samiAAoo ma istajaboo lakum wayawma alqiyamati yakfuroona bishirkikum wala yunabbioka mithlu khabeerin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 15, "O mankind, you are those in need of Allah, while Allah is the Free of need, the Praiseworthy", "۞يَـٰٓأَيُّهَا ٱلنَّاسُ أَنتُمُ ٱلۡفُقَرَآءُ إِلَى ٱللَّهِۖ وَٱللَّهُ هُوَ ٱلۡغَنِيُّ ٱلۡحَمِيدُ", "Ya ayyuha alnnasu antumu alfuqarao ila Allahi waAllahu huwa alghaniyyu alhameedu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 16, "If He wills, He can do away with you and bring forth a new creation", "إِن يَشَأۡ يُذۡهِبۡكُمۡ وَيَأۡتِ بِخَلۡقٖ جَدِيدٖ", "In yasha yuthhibkum wayati bikhalqin jadeedin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 17, "And that is for Allah not difficult", "وَمَا ذَٰلِكَ عَلَى ٱللَّهِ بِعَزِيزٖ", "Wama thalika AAala Allahi biAAazeezin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 18, "And no bearer of burdens will bear the burden of another. And if a heavily laden soul calls [another] to [carry some of] its load, nothing of it will be carried, even if he should be a close relative. You can only warn those who fear their Lord unseen and have established prayer. And whoever purifies himself only purifies himself for [the benefit of] his soul. And to Allah is the [final] destination", "وَلَا تَزِرُ وَازِرَةٞ وِزۡرَ أُخۡرَىٰۚ وَإِن تَدۡعُ مُثۡقَلَةٌ إِلَىٰ حِمۡلِهَا لَا يُحۡمَلۡ مِنۡهُ شَيۡءٞ وَلَوۡ كَانَ ذَا قُرۡبَىٰٓۗ إِنَّمَا تُنذِرُ ٱلَّذِينَ يَخۡشَوۡنَ رَبَّهُم بِٱلۡغَيۡبِ وَأَقَامُواْ ٱلصَّلَوٰةَۚ وَمَن تَزَكَّىٰ فَإِنَّمَا يَتَزَكَّىٰ لِنَفۡسِهِۦۚ وَإِلَى ٱللَّهِ ٱلۡمَصِيرُ", "Wala taziru waziratun wizra okhra wain tadAAu muthqalatun ila himliha la yuhmal minhu shayon walaw kana tha qurba innama tunthiru allatheena yakhshawna rabbahum bialghaybi waaqamoo alssalata waman tazakka fainnama yatazakka linafsihi waila Allahi almaseeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 19, "Not equal are the blind and the seeing", "وَمَا يَسۡتَوِي ٱلۡأَعۡمَىٰ وَٱلۡبَصِيرُ", "Wama yastawee alaAAma waalbaseeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 20, "Nor are the darknesses and the light", "وَلَا ٱلظُّلُمَٰتُ وَلَا ٱلنُّورُ", "Wala alththulumatu wala alnnooru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 21, "Nor are the shade and the heat", "وَلَا ٱلظِّلُّ وَلَا ٱلۡحَرُورُ", "Wala alththillu wala alharooru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 22, "And not equal are the living and the dead. Indeed, Allah causes to hear whom He wills, but you cannot make hear those in the graves", "وَمَا يَسۡتَوِي ٱلۡأَحۡيَآءُ وَلَا ٱلۡأَمۡوَٰتُۚ إِنَّ ٱللَّهَ يُسۡمِعُ مَن يَشَآءُۖ وَمَآ أَنتَ بِمُسۡمِعٖ مَّن فِي ٱلۡقُبُورِ", "Wama yastawee alahyao wala alamwatu inna Allaha yusmiAAu man yashao wama anta bimusmiAAin man fee alquboori");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 23, "You, [O Muhammad], are not but a warner", "إِنۡ أَنتَ إِلَّا نَذِيرٌ", "In anta illa natheerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 24, "Indeed, We have sent you with the truth as a bringer of good tidings and a warner. And there was no nation but that there had passed within it a warner", "إِنَّآ أَرۡسَلۡنَٰكَ بِٱلۡحَقِّ بَشِيرٗا وَنَذِيرٗاۚ وَإِن مِّنۡ أُمَّةٍ إِلَّا خَلَا فِيهَا نَذِيرٞ", "Inna arsalnaka bialhaqqi basheeran wanatheeran wain min ommatin illa khala feeha natheerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 25, "And if they deny you - then already have those before them denied. Their messengers came to them with clear proofs and written ordinances and with the enlightening Scripture", "وَإِن يُكَذِّبُوكَ فَقَدۡ كَذَّبَ ٱلَّذِينَ مِن قَبۡلِهِمۡ جَآءَتۡهُمۡ رُسُلُهُم بِٱلۡبَيِّنَٰتِ وَبِٱلزُّبُرِ وَبِٱلۡكِتَٰبِ ٱلۡمُنِيرِ", "Wain yukaththibooka faqad kaththaba allatheena min qablihim jaathum rusuluhum bialbayyinati wabialzzuburi wabialkitabi almuneeri");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 26, "Then I seized the ones who disbelieved, and how [terrible] was My reproach", "ثُمَّ أَخَذۡتُ ٱلَّذِينَ كَفَرُواْۖ فَكَيۡفَ كَانَ نَكِيرِ", "Thumma akhathtu allatheena kafaroo fakayfa kana nakeeri");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 27, "Do you not see that Allah sends down rain from the sky, and We produce thereby fruits of varying colors? And in the mountains are tracts, white and red of varying shades and [some] extremely black", "أَلَمۡ تَرَ أَنَّ ٱللَّهَ أَنزَلَ مِنَ ٱلسَّمَآءِ مَآءٗ فَأَخۡرَجۡنَا بِهِۦ ثَمَرَٰتٖ مُّخۡتَلِفًا أَلۡوَٰنُهَاۚ وَمِنَ ٱلۡجِبَالِ جُدَدُۢ بِيضٞ وَحُمۡرٞ مُّخۡتَلِفٌ أَلۡوَٰنُهَا وَغَرَابِيبُ سُودٞ", "Alam tara anna Allaha anzala mina alssamai maan faakhrajna bihi thamaratin mukhtalifan alwanuha wamina aljibali judadun beedun wahumrun mukhtalifun alwanuha wagharabeebu soodun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 28, "And among people and moving creatures and grazing livestock are various colors similarly. Only those fear Allah, from among His servants, who have knowledge. Indeed, Allah is Exalted in Might and Forgiving", "وَمِنَ ٱلنَّاسِ وَٱلدَّوَآبِّ وَٱلۡأَنۡعَٰمِ مُخۡتَلِفٌ أَلۡوَٰنُهُۥ كَذَٰلِكَۗ إِنَّمَا يَخۡشَى ٱللَّهَ مِنۡ عِبَادِهِ ٱلۡعُلَمَـٰٓؤُاْۗ إِنَّ ٱللَّهَ عَزِيزٌ غَفُورٌ", "Wamina alnnasi waalddawabbi waalanAAami mukhtalifun alwanuhu kathalika innama yakhsha Allaha min AAibadihi alAAulamao inna Allaha AAazeezun ghafoorun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 29, "Indeed, those who recite the Book of Allah and establish prayer and spend [in His cause] out of what We have provided them, secretly and publicly, [can] expect a profit that will never perish", "إِنَّ ٱلَّذِينَ يَتۡلُونَ كِتَٰبَ ٱللَّهِ وَأَقَامُواْ ٱلصَّلَوٰةَ وَأَنفَقُواْ مِمَّا رَزَقۡنَٰهُمۡ سِرّٗا وَعَلَانِيَةٗ يَرۡجُونَ تِجَٰرَةٗ لَّن تَبُورَ", "Inna allatheena yatloona kitaba Allahi waaqamoo alssalata waanfaqoo mimma razaqnahum sirran waAAalaniyatan yarjoona tijaratan lan taboora");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 30, "That He may give them in full their rewards and increase for them of His bounty. Indeed, He is Forgiving and Appreciative", "لِيُوَفِّيَهُمۡ أُجُورَهُمۡ وَيَزِيدَهُم مِّن فَضۡلِهِۦٓۚ إِنَّهُۥ غَفُورٞ شَكُورٞ", "Liyuwaffiyahum ojoorahum wayazeedahum min fadlihi innahu ghafoorun shakoorun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 31, "And that which We have revealed to you, [O Muhammad], of the Book is the truth, confirming what was before it. Indeed, Allah, of His servants, is Acquainted and Seeing", "وَٱلَّذِيٓ أَوۡحَيۡنَآ إِلَيۡكَ مِنَ ٱلۡكِتَٰبِ هُوَ ٱلۡحَقُّ مُصَدِّقٗا لِّمَا بَيۡنَ يَدَيۡهِۗ إِنَّ ٱللَّهَ بِعِبَادِهِۦ لَخَبِيرُۢ بَصِيرٞ", "Waallathee awhayna ilayka mina alkitabi huwa alhaqqu musaddiqan lima bayna yadayhi inna Allaha biAAibadihi lakhabeerun baseerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 32, "Then we caused to inherit the Book those We have chosen of Our servants; and among them is he who wrongs himself, and among them is he who is moderate, and among them is he who is foremost in good deeds by permission of Allah. That [inheritance] is what is the great bounty", "ثُمَّ أَوۡرَثۡنَا ٱلۡكِتَٰبَ ٱلَّذِينَ ٱصۡطَفَيۡنَا مِنۡ عِبَادِنَاۖ فَمِنۡهُمۡ ظَالِمٞ لِّنَفۡسِهِۦ وَمِنۡهُم مُّقۡتَصِدٞ وَمِنۡهُمۡ سَابِقُۢ بِٱلۡخَيۡرَٰتِ بِإِذۡنِ ٱللَّهِۚ ذَٰلِكَ هُوَ ٱلۡفَضۡلُ ٱلۡكَبِيرُ", "Thumma awrathna alkitaba allatheena istafayna min AAibadina faminhum thalimun linafsihi waminhum muqtasidun waminhum sabiqun bialkhayrati biithni Allahi thalika huwa alfadlu alkabeeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 33, "[For them are] gardens of perpetual residence which they will enter. They will be adorned therein with bracelets of gold and pearls, and their garments therein will be silk", "جَنَّـٰتُ عَدۡنٖ يَدۡخُلُونَهَا يُحَلَّوۡنَ فِيهَا مِنۡ أَسَاوِرَ مِن ذَهَبٖ وَلُؤۡلُؤٗاۖ وَلِبَاسُهُمۡ فِيهَا حَرِيرٞ", "Jannatu AAadnin yadkhuloonaha yuhallawna feeha min asawira min thahabin waluluan walibasuhum feeha hareerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 34, "And they will say, ""Praise to Allah, who has removed from us [all] sorrow. Indeed, our Lord is Forgiving and Appreciative", "وَقَالُواْ ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِيٓ أَذۡهَبَ عَنَّا ٱلۡحَزَنَۖ إِنَّ رَبَّنَا لَغَفُورٞ شَكُورٌ", "Waqaloo alhamdu lillahi allathee athhaba AAanna alhazana inna rabbana laghafoorun shakoorun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 35, "He who has settled us in the home of duration out of His bounty. There touches us not in it any fatigue, and there touches us not in it weariness [of mind]", "ٱلَّذِيٓ أَحَلَّنَا دَارَ ٱلۡمُقَامَةِ مِن فَضۡلِهِۦ لَا يَمَسُّنَا فِيهَا نَصَبٞ وَلَا يَمَسُّنَا فِيهَا لُغُوبٞ", "Allathee ahallana dara almuqamati min fadlihi la yamassuna feeha nasabun wala yamassuna feeha lughoobun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 36, "And for those who disbelieve will be the fire of Hell. [Death] is not decreed for them so they may die, nor will its torment be lightened for them. Thus do we recompense every ungrateful one", "وَٱلَّذِينَ كَفَرُواْ لَهُمۡ نَارُ جَهَنَّمَ لَا يُقۡضَىٰ عَلَيۡهِمۡ فَيَمُوتُواْ وَلَا يُخَفَّفُ عَنۡهُم مِّنۡ عَذَابِهَاۚ كَذَٰلِكَ نَجۡزِي كُلَّ كَفُورٖ", "Waallatheena kafaroo lahum naru jahannama la yuqda AAalayhim fayamootoo wala yukhaffafu AAanhum min AAathabiha kathalika najzee kulla kafoorin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 37, "And they will cry out therein, ""Our Lord, remove us; we will do righteousness - other than what we were doing!"" But did We not grant you life enough for whoever would remember therein to remember, and the warner had come to you? So taste [the punishment], for there is not for the wrongdoers any helper", "وَهُمۡ يَصۡطَرِخُونَ فِيهَا رَبَّنَآ أَخۡرِجۡنَا نَعۡمَلۡ صَٰلِحًا غَيۡرَ ٱلَّذِي كُنَّا نَعۡمَلُۚ أَوَلَمۡ نُعَمِّرۡكُم مَّا يَتَذَكَّرُ فِيهِ مَن تَذَكَّرَ وَجَآءَكُمُ ٱلنَّذِيرُۖ فَذُوقُواْ فَمَا لِلظَّـٰلِمِينَ مِن نَّصِيرٍ", "Wahum yastarikhoona feeha rabbana akhrijna naAAmal salihan ghayra allathee kunna naAAmalu awalam nuAAammirkum ma yatathakkaru feehi man tathakkara wajaakumu alnnatheeru fathooqoo fama lilththalimeena min naseerin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 38, "Indeed, Allah is Knower of the unseen [aspects] of the heavens and earth. Indeed, He is Knowing of that within the breasts", "إِنَّ ٱللَّهَ عَٰلِمُ غَيۡبِ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ إِنَّهُۥ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ", "Inna Allaha AAalimu ghaybi alssamawati waalardi innahu AAaleemun bithati alssudoori");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 39, "It is He who has made you successors upon the earth. And whoever disbelieves - upon him will be [the consequence of] his disbelief. And the disbelief of the disbelievers does not increase them in the sight of their Lord except in hatred; and the disbelief of the disbelievers does not increase them except in loss", "هُوَ ٱلَّذِي جَعَلَكُمۡ خَلَـٰٓئِفَ فِي ٱلۡأَرۡضِۚ فَمَن كَفَرَ فَعَلَيۡهِ كُفۡرُهُۥۖ وَلَا يَزِيدُ ٱلۡكَٰفِرِينَ كُفۡرُهُمۡ عِندَ رَبِّهِمۡ إِلَّا مَقۡتٗاۖ وَلَا يَزِيدُ ٱلۡكَٰفِرِينَ كُفۡرُهُمۡ إِلَّا خَسَارٗا", "Huwa allathee jaAAalakum khalaifa fee alardi faman kafara faAAalayhi kufruhu wala yazeedu alkafireena kufruhum AAinda rabbihim illa maqtan wala yazeedu alkafireena kufruhum illa khasaran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 40, "Say, ""Have you considered your 'partners' whom you invoke besides Allah? Show me what they have created from the earth, or have they partnership [with Him] in the heavens? Or have We given them a book so they are [standing] on evidence therefrom? [No], rather, the wrongdoers do not promise each other except delusion", "قُلۡ أَرَءَيۡتُمۡ شُرَكَآءَكُمُ ٱلَّذِينَ تَدۡعُونَ مِن دُونِ ٱللَّهِ أَرُونِي مَاذَا خَلَقُواْ مِنَ ٱلۡأَرۡضِ أَمۡ لَهُمۡ شِرۡكٞ فِي ٱلسَّمَٰوَٰتِ أَمۡ ءَاتَيۡنَٰهُمۡ كِتَٰبٗا فَهُمۡ عَلَىٰ بَيِّنَتٖ مِّنۡهُۚ بَلۡ إِن يَعِدُ ٱلظَّـٰلِمُونَ بَعۡضُهُم بَعۡضًا إِلَّا غُرُورًا", "Qul araaytum shurakaakumu allatheena tadAAoona min dooni Allahi aroonee matha khalaqoo mina alardi am lahum shirkun fee alssamawati am ataynahum kitaban fahum AAala bayyinatin minhu bal in yaAAidu alththalimoona baAAduhum baAAdan illa ghurooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 41, "Indeed, Allah holds the heavens and the earth, lest they cease. And if they should cease, no one could hold them [in place] after Him. Indeed, He is Forbearing and Forgiving", "۞إِنَّ ٱللَّهَ يُمۡسِكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ أَن تَزُولَاۚ وَلَئِن زَالَتَآ إِنۡ أَمۡسَكَهُمَا مِنۡ أَحَدٖ مِّنۢ بَعۡدِهِۦٓۚ إِنَّهُۥ كَانَ حَلِيمًا غَفُورٗا", "Inna Allaha yumsiku alssamawati waalarda an tazoola walain zalata in amsakahuma min ahadin min baAAdihi innahu kana haleeman ghafooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 42, "And they swore by Allah their strongest oaths that if a warner came to them, they would be more guided than [any] one of the [previous] nations. But when a warner came to them, it did not increase them except in aversion", "وَأَقۡسَمُواْ بِٱللَّهِ جَهۡدَ أَيۡمَٰنِهِمۡ لَئِن جَآءَهُمۡ نَذِيرٞ لَّيَكُونُنَّ أَهۡدَىٰ مِنۡ إِحۡدَى ٱلۡأُمَمِۖ فَلَمَّا جَآءَهُمۡ نَذِيرٞ مَّا زَادَهُمۡ إِلَّا نُفُورًا", "Waaqsamoo biAllahi jahda aymanihim lain jaahum natheerun layakoonunna ahda min ihda alomami falamma jaahum natheerun ma zadahum illa nufooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 43, "[Due to] arrogance in the land and plotting of evil; but the evil plot does not encompass except its own people. Then do they await except the way of the former peoples? But you will never find in the way of Allah any change, and you will never find in the way of Allah any alteration", "ٱسۡتِكۡبَارٗا فِي ٱلۡأَرۡضِ وَمَكۡرَ ٱلسَّيِّيِٕۚ وَلَا يَحِيقُ ٱلۡمَكۡرُ ٱلسَّيِّئُ إِلَّا بِأَهۡلِهِۦۚ فَهَلۡ يَنظُرُونَ إِلَّا سُنَّتَ ٱلۡأَوَّلِينَۚ فَلَن تَجِدَ لِسُنَّتِ ٱللَّهِ تَبۡدِيلٗاۖ وَلَن تَجِدَ لِسُنَّتِ ٱللَّهِ تَحۡوِيلًا", "Istikbaran fee alardi wamakra alssayyii wala yaheequ almakru alssayyio illa biahlihi fahal yanthuroona illa sunnata alawwaleena falan tajida lisunnati Allahi tabdeelan walan tajida lisunnati Allahi tahweelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 44, "Have they not traveled through the land and observed how was the end of those before them? And they were greater than them in power. But Allah is not to be caused failure by anything in the heavens or on the earth. Indeed, He is ever Knowing and Competent", "أَوَلَمۡ يَسِيرُواْ فِي ٱلۡأَرۡضِ فَيَنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلَّذِينَ مِن قَبۡلِهِمۡ وَكَانُوٓاْ أَشَدَّ مِنۡهُمۡ قُوَّةٗۚ وَمَا كَانَ ٱللَّهُ لِيُعۡجِزَهُۥ مِن شَيۡءٖ فِي ٱلسَّمَٰوَٰتِ وَلَا فِي ٱلۡأَرۡضِۚ إِنَّهُۥ كَانَ عَلِيمٗا قَدِيرٗا", "Awalam yaseeroo fee alardi fayanthuroo kayfa kana AAaqibatu allatheena min qablihim wakanoo ashadda minhum quwwatan wama kana Allahu liyuAAjizahu min shayin fee alssamawati wala fee alardi innahu kana AAaleeman qadeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (35, 45, "And if Allah were to impose blame on the people for what they have earned, He would not leave upon the earth any creature. But He defers them for a specified term. And when their time comes, then indeed Allah has ever been, of His servants, Seeing", "وَلَوۡ يُؤَاخِذُ ٱللَّهُ ٱلنَّاسَ بِمَا كَسَبُواْ مَا تَرَكَ عَلَىٰ ظَهۡرِهَا مِن دَآبَّةٖ وَلَٰكِن يُؤَخِّرُهُمۡ إِلَىٰٓ أَجَلٖ مُّسَمّٗىۖ فَإِذَا جَآءَ أَجَلُهُمۡ فَإِنَّ ٱللَّهَ كَانَ بِعِبَادِهِۦ بَصِيرَۢا", "Walaw yuakhithu Allahu alnnasa bima kasaboo ma taraka AAala thahriha min dabbatin walakin yuakhkhiruhum ila ajalin musamman faitha jaa ajaluhum fainna Allaha kana biAAibadihi baseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 1, "Ya, Seen", "يسٓ", "Yaseen");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 2, "By the wise Qur'an", "وَٱلۡقُرۡءَانِ ٱلۡحَكِيمِ", "Waalqurani alhakeemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 3, "Indeed you, [O Muhammad], are from among the messengers", "إِنَّكَ لَمِنَ ٱلۡمُرۡسَلِينَ", "Innaka lamina almursaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 4, "On a straight path", "عَلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ", "AAala siratin mustaqeemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 5, "[This is] a revelation of the Exalted in Might, the Merciful", "تَنزِيلَ ٱلۡعَزِيزِ ٱلرَّحِيمِ", "Tanzeela alAAazeezi alrraheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 6, "That you may warn a people whose forefathers were not warned, so they are unaware", "لِتُنذِرَ قَوۡمٗا مَّآ أُنذِرَ ءَابَآؤُهُمۡ فَهُمۡ غَٰفِلُونَ", "Litunthira qawman ma onthira abaohum fahum ghafiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 7, "Already the word has come into effect upon most of them, so they do not believe", "لَقَدۡ حَقَّ ٱلۡقَوۡلُ عَلَىٰٓ أَكۡثَرِهِمۡ فَهُمۡ لَا يُؤۡمِنُونَ", "Laqad haqqa alqawlu AAala aktharihim fahum la yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 8, "Indeed, We have put shackles on their necks, and they are to their chins, so they are with heads [kept] aloft", "إِنَّا جَعَلۡنَا فِيٓ أَعۡنَٰقِهِمۡ أَغۡلَٰلٗا فَهِيَ إِلَى ٱلۡأَذۡقَانِ فَهُم مُّقۡمَحُونَ", "Inna jaAAalna fee aAAnaqihim aghlalan fahiya ila alathqani fahum muqmahoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 9, "And We have put before them a barrier and behind them a barrier and covered them, so they do not see", "وَجَعَلۡنَا مِنۢ بَيۡنِ أَيۡدِيهِمۡ سَدّٗا وَمِنۡ خَلۡفِهِمۡ سَدّٗا فَأَغۡشَيۡنَٰهُمۡ فَهُمۡ لَا يُبۡصِرُونَ", "WajaAAalna min bayni aydeehim saddan wamin khalfihim saddan faaghshaynahum fahum la yubsiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 10, "And it is all the same for them whether you warn them or do not warn them - they will not believe", "وَسَوَآءٌ عَلَيۡهِمۡ ءَأَنذَرۡتَهُمۡ أَمۡ لَمۡ تُنذِرۡهُمۡ لَا يُؤۡمِنُونَ", "Wasawaon AAalayhim aanthartahum am lam tunthirhum la yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 11, "You can only warn one who follows the message and fears the Most Merciful unseen. So give him good tidings of forgiveness and noble reward", "إِنَّمَا تُنذِرُ مَنِ ٱتَّبَعَ ٱلذِّكۡرَ وَخَشِيَ ٱلرَّحۡمَٰنَ بِٱلۡغَيۡبِۖ فَبَشِّرۡهُ بِمَغۡفِرَةٖ وَأَجۡرٖ كَرِيمٍ", "Innama tunthiru mani ittabaAAa alththikra wakhashiya alrrahmana bialghaybi fabashshirhu bimaghfiratin waajrin kareemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 12, "Indeed, it is We who bring the dead to life and record what they have put forth and what they left behind, and all things We have enumerated in a clear register", "إِنَّا نَحۡنُ نُحۡيِ ٱلۡمَوۡتَىٰ وَنَكۡتُبُ مَا قَدَّمُواْ وَءَاثَٰرَهُمۡۚ وَكُلَّ شَيۡءٍ أَحۡصَيۡنَٰهُ فِيٓ إِمَامٖ مُّبِينٖ", "Inna nahnu nuhyee almawta wanaktubu ma qaddamoo waatharahum wakulla shayin ahsaynahu fee imamin mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 13, "And present to them an example: the people of the city, when the messengers came to it", "وَٱضۡرِبۡ لَهُم مَّثَلًا أَصۡحَٰبَ ٱلۡقَرۡيَةِ إِذۡ جَآءَهَا ٱلۡمُرۡسَلُونَ", "Waidrib lahum mathalan ashaba alqaryati ith jaaha almursaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 14, "When We sent to them two but they denied them, so We strengthened them with a third, and they said, ""Indeed, we are messengers to you", "إِذۡ أَرۡسَلۡنَآ إِلَيۡهِمُ ٱثۡنَيۡنِ فَكَذَّبُوهُمَا فَعَزَّزۡنَا بِثَالِثٖ فَقَالُوٓاْ إِنَّآ إِلَيۡكُم مُّرۡسَلُونَ", "Ith arsalna ilayhimu ithnayni fakaththaboohuma faAAazzazna bithalithin faqaloo inna ilaykum mursaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 15, "They said, ""You are not but human beings like us, and the Most Merciful has not revealed a thing. You are only telling lies", "قَالُواْ مَآ أَنتُمۡ إِلَّا بَشَرٞ مِّثۡلُنَا وَمَآ أَنزَلَ ٱلرَّحۡمَٰنُ مِن شَيۡءٍ إِنۡ أَنتُمۡ إِلَّا تَكۡذِبُونَ", "Qaloo ma antum illa basharun mithluna wama anzala alrrahmanu min shayin in antum illa takthiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 16, "They said, ""Our Lord knows that we are messengers to you", "قَالُواْ رَبُّنَا يَعۡلَمُ إِنَّآ إِلَيۡكُمۡ لَمُرۡسَلُونَ", "Qaloo rabbuna yaAAlamu inna ilaykum lamursaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 17, "And we are not responsible except for clear notification", "وَمَا عَلَيۡنَآ إِلَّا ٱلۡبَلَٰغُ ٱلۡمُبِينُ", "Wama AAalayna illa albalaghu almubeenu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 18, "They said, ""Indeed, we consider you a bad omen. If you do not desist, we will surely stone you, and there will surely touch you, from us, a painful punishment", "قَالُوٓاْ إِنَّا تَطَيَّرۡنَا بِكُمۡۖ لَئِن لَّمۡ تَنتَهُواْ لَنَرۡجُمَنَّكُمۡ وَلَيَمَسَّنَّكُم مِّنَّا عَذَابٌ أَلِيمٞ", "Qaloo inna tatayyarna bikum lain lam tantahoo lanarjumannakum walayamassannakum minna AAathabun aleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 19, "They said, ""Your omen is with yourselves. Is it because you were reminded? Rather, you are a transgressing people", "قَالُواْ طَـٰٓئِرُكُم مَّعَكُمۡ أَئِن ذُكِّرۡتُمۚ بَلۡ أَنتُمۡ قَوۡمٞ مُّسۡرِفُونَ", "Qaloo tairukum maAAakum ain thukkirtum bal antum qawmun musrifoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 20, "And there came from the farthest end of the city a man, running. He said, ""O my people, follow the messengers", "وَجَآءَ مِنۡ أَقۡصَا ٱلۡمَدِينَةِ رَجُلٞ يَسۡعَىٰ قَالَ يَٰقَوۡمِ ٱتَّبِعُواْ ٱلۡمُرۡسَلِينَ", "Wajaa min aqsa almadeenati rajulun yasAAa qala ya qawmi ittabiAAoo almursaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 21, "Follow those who do not ask of you [any] payment, and they are [rightly] guided", "ٱتَّبِعُواْ مَن لَّا يَسۡـَٔلُكُمۡ أَجۡرٗا وَهُم مُّهۡتَدُونَ", "IttabiAAoo man la yasalukum ajran wahum muhtadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 22, "And why should I not worship He who created me and to whom you will be returned", "وَمَالِيَ لَآ أَعۡبُدُ ٱلَّذِي فَطَرَنِي وَإِلَيۡهِ تُرۡجَعُونَ", "Wama liya la aAAbudu allathee fataranee wailayhi turjaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 23, "Should I take other than Him [false] deities [while], if the Most Merciful intends for me some adversity, their intercession will not avail me at all, nor can they save me", "ءَأَتَّخِذُ مِن دُونِهِۦٓ ءَالِهَةً إِن يُرِدۡنِ ٱلرَّحۡمَٰنُ بِضُرّٖ لَّا تُغۡنِ عَنِّي شَفَٰعَتُهُمۡ شَيۡـٔٗا وَلَا يُنقِذُونِ", "Aattakhithu min doonihi alihatan in yuridni alrrahmanu bidurrin la tughni AAannee shafaAAatuhum shayan wala yunqithooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 24, "Indeed, I would then be in manifest error", "إِنِّيٓ إِذٗا لَّفِي ضَلَٰلٖ مُّبِينٍ", "Innee ithan lafee dalalin mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 25, "Indeed, I have believed in your Lord, so listen to me", "إِنِّيٓ ءَامَنتُ بِرَبِّكُمۡ فَٱسۡمَعُونِ", "Innee amantu birabbikum faismaAAooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 26, "It was said, ""Enter Paradise."" He said, ""I wish my people could know", "قِيلَ ٱدۡخُلِ ٱلۡجَنَّةَۖ قَالَ يَٰلَيۡتَ قَوۡمِي يَعۡلَمُونَ", "Qeela odkhuli aljannata qala ya layta qawmee yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 27, "Of how my Lord has forgiven me and placed me among the honored", "بِمَا غَفَرَ لِي رَبِّي وَجَعَلَنِي مِنَ ٱلۡمُكۡرَمِينَ", "Bima ghafara lee rabbee wajaAAalanee mina almukrameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 28, "And We did not send down upon his people after him any soldiers from the heaven, nor would We have done so", "۞وَمَآ أَنزَلۡنَا عَلَىٰ قَوۡمِهِۦ مِنۢ بَعۡدِهِۦ مِن جُندٖ مِّنَ ٱلسَّمَآءِ وَمَا كُنَّا مُنزِلِينَ", "Wama anzalna AAala qawmihi min baAAdihi min jundin mina alssamai wama kunna munzileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 29, "It was not but one shout, and immediately they were extinguished", "إِن كَانَتۡ إِلَّا صَيۡحَةٗ وَٰحِدَةٗ فَإِذَا هُمۡ خَٰمِدُونَ", "In kanat illa sayhatan wahidatan faitha hum khamidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 30, "How regretful for the servants. There did not come to them any messenger except that they used to ridicule him", "يَٰحَسۡرَةً عَلَى ٱلۡعِبَادِۚ مَا يَأۡتِيهِم مِّن رَّسُولٍ إِلَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ", "Ya hasratan AAala alAAibadi ma yateehim min rasoolin illa kanoo bihi yastahzioona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 31, "Have they not considered how many generations We destroyed before them - that they to them will not return", "أَلَمۡ يَرَوۡاْ كَمۡ أَهۡلَكۡنَا قَبۡلَهُم مِّنَ ٱلۡقُرُونِ أَنَّهُمۡ إِلَيۡهِمۡ لَا يَرۡجِعُونَ", "Alam yaraw kam ahlakna qablahum mina alqurooni annahum ilayhim la yarjiAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 32, "And indeed, all of them will yet be brought present before Us", "وَإِن كُلّٞ لَّمَّا جَمِيعٞ لَّدَيۡنَا مُحۡضَرُونَ", "Wain kullun lamma jameeAAun ladayna muhdaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 33, "And a sign for them is the dead earth. We have brought it to life and brought forth from it grain, and from it they eat", "وَءَايَةٞ لَّهُمُ ٱلۡأَرۡضُ ٱلۡمَيۡتَةُ أَحۡيَيۡنَٰهَا وَأَخۡرَجۡنَا مِنۡهَا حَبّٗا فَمِنۡهُ يَأۡكُلُونَ", "Waayatun lahumu alardu almaytatu ahyaynaha waakhrajna minha habban faminhu yakuloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 34, "And We placed therein gardens of palm trees and grapevines and caused to burst forth therefrom some springs", "وَجَعَلۡنَا فِيهَا جَنَّـٰتٖ مِّن نَّخِيلٖ وَأَعۡنَٰبٖ وَفَجَّرۡنَا فِيهَا مِنَ ٱلۡعُيُونِ", "WajaAAalna feeha jannatin min nakheelin waaAAnabin wafajjarna feeha mina alAAuyooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 35, "That they may eat of His fruit. And their hands have not produced it, so will they not be grateful", "لِيَأۡكُلُواْ مِن ثَمَرِهِۦ وَمَا عَمِلَتۡهُ أَيۡدِيهِمۡۚ أَفَلَا يَشۡكُرُونَ", "Liyakuloo min thamarihi wama AAamilathu aydeehim afala yashkuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 36, "Exalted is He who created all pairs - from what the earth grows and from themselves and from that which they do not know", "سُبۡحَٰنَ ٱلَّذِي خَلَقَ ٱلۡأَزۡوَٰجَ كُلَّهَا مِمَّا تُنۢبِتُ ٱلۡأَرۡضُ وَمِنۡ أَنفُسِهِمۡ وَمِمَّا لَا يَعۡلَمُونَ", "Subhana allathee khalaqa alazwaja kullaha mimma tunbitu alardu wamin anfusihim wamimma la yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 37, "And a sign for them is the night. We remove from it [the light of] day, so they are [left] in darkness", "وَءَايَةٞ لَّهُمُ ٱلَّيۡلُ نَسۡلَخُ مِنۡهُ ٱلنَّهَارَ فَإِذَا هُم مُّظۡلِمُونَ", "Waayatun lahumu allaylu naslakhu minhu alnnahara faitha hum muthlimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 38, "And the sun runs [on course] toward its stopping point. That is the determination of the Exalted in Might, the Knowing", "وَٱلشَّمۡسُ تَجۡرِي لِمُسۡتَقَرّٖ لَّهَاۚ ذَٰلِكَ تَقۡدِيرُ ٱلۡعَزِيزِ ٱلۡعَلِيمِ", "Waalshshamsu tajree limustaqarrin laha thalika taqdeeru alAAazeezi alAAaleemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 39, "And the moon - We have determined for it phases, until it returns [appearing] like the old date stalk", "وَٱلۡقَمَرَ قَدَّرۡنَٰهُ مَنَازِلَ حَتَّىٰ عَادَ كَٱلۡعُرۡجُونِ ٱلۡقَدِيمِ", "Waalqamara qaddarnahu manazila hatta AAada kaalAAurjooni alqadeemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 40, "It is not allowable for the sun to reach the moon, nor does the night overtake the day, but each, in an orbit, is swimming", "لَا ٱلشَّمۡسُ يَنۢبَغِي لَهَآ أَن تُدۡرِكَ ٱلۡقَمَرَ وَلَا ٱلَّيۡلُ سَابِقُ ٱلنَّهَارِۚ وَكُلّٞ فِي فَلَكٖ يَسۡبَحُونَ", "La alshshamsu yanbaghee laha an tudrika alqamara wala allaylu sabiqu alnnahari wakullun fee falakin yasbahoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 41, "And a sign for them is that We carried their forefathers in a laden ship", "وَءَايَةٞ لَّهُمۡ أَنَّا حَمَلۡنَا ذُرِّيَّتَهُمۡ فِي ٱلۡفُلۡكِ ٱلۡمَشۡحُونِ", "Waayatun lahum anna hamalna thurriyyatahum fee alfulki almashhooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 42, "And We created for them from the likes of it that which they ride", "وَخَلَقۡنَا لَهُم مِّن مِّثۡلِهِۦ مَا يَرۡكَبُونَ", "Wakhalaqna lahum min mithlihi ma yarkaboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 43, "And if We should will, We could drown them; then no one responding to a cry would there be for them, nor would they be saved", "وَإِن نَّشَأۡ نُغۡرِقۡهُمۡ فَلَا صَرِيخَ لَهُمۡ وَلَا هُمۡ يُنقَذُونَ", "Wain nasha nughriqhum fala sareekha lahum wala hum yunqathoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 44, "Except as a mercy from Us and provision for a time", "إِلَّا رَحۡمَةٗ مِّنَّا وَمَتَٰعًا إِلَىٰ حِينٖ", "Illa rahmatan minna wamataAAan ila heenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 45, "But when it is said to them, ""Beware of what is before you and what is behind you; perhaps you will receive mercy", "وَإِذَا قِيلَ لَهُمُ ٱتَّقُواْ مَا بَيۡنَ أَيۡدِيكُمۡ وَمَا خَلۡفَكُمۡ لَعَلَّكُمۡ تُرۡحَمُونَ", "Waitha qeela lahumu ittaqoo ma bayna aydeekum wama khalfakum laAAallakum turhamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 46, "And no sign comes to them from the signs of their Lord except that they are from it turning away", "وَمَا تَأۡتِيهِم مِّنۡ ءَايَةٖ مِّنۡ ءَايَٰتِ رَبِّهِمۡ إِلَّا كَانُواْ عَنۡهَا مُعۡرِضِينَ", "Wama tateehim min ayatin min ayati rabbihim illa kanoo AAanha muAArideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 47, "And when it is said to them, ""Spend from that which Allah has provided for you,"" those who disbelieve say to those who believe, ""Should we feed one whom, if Allah had willed, He would have fed? You are not but in clear error", "وَإِذَا قِيلَ لَهُمۡ أَنفِقُواْ مِمَّا رَزَقَكُمُ ٱللَّهُ قَالَ ٱلَّذِينَ كَفَرُواْ لِلَّذِينَ ءَامَنُوٓاْ أَنُطۡعِمُ مَن لَّوۡ يَشَآءُ ٱللَّهُ أَطۡعَمَهُۥٓ إِنۡ أَنتُمۡ إِلَّا فِي ضَلَٰلٖ مُّبِينٖ", "Waitha qeela lahum anfiqoo mimma razaqakumu Allahu qala allatheena kafaroo lillatheena amanoo anutAAimu man law yashao Allahu atAAamahu in antum illa fee dalalin mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 48, "And they say, ""When is this promise, if you should be truthful", "وَيَقُولُونَ مَتَىٰ هَٰذَا ٱلۡوَعۡدُ إِن كُنتُمۡ صَٰدِقِينَ", "Wayaqooloona mata hatha alwaAAdu in kuntum sadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 49, "They do not await except one blast which will seize them while they are disputing", "مَا يَنظُرُونَ إِلَّا صَيۡحَةٗ وَٰحِدَةٗ تَأۡخُذُهُمۡ وَهُمۡ يَخِصِّمُونَ", "Ma yanthuroona illa sayhatan wahidatan takhuthuhum wahum yakhissimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 50, "And they will not be able [to give] any instruction, nor to their people can they return", "فَلَا يَسۡتَطِيعُونَ تَوۡصِيَةٗ وَلَآ إِلَىٰٓ أَهۡلِهِمۡ يَرۡجِعُونَ", "Fala yastateeAAoona tawsiyatan wala ila ahlihim yarjiAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 51, "And the Horn will be blown; and at once from the graves to their Lord they will hasten", "وَنُفِخَ فِي ٱلصُّورِ فَإِذَا هُم مِّنَ ٱلۡأَجۡدَاثِ إِلَىٰ رَبِّهِمۡ يَنسِلُونَ", "Wanufikha fee alssoori faitha hum mina alajdathi ila rabbihim yansiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 52, "They will say, ""O woe to us! Who has raised us up from our sleeping place?"" [The reply will be], ""This is what the Most Merciful had promised, and the messengers told the truth", "قَالُواْ يَٰوَيۡلَنَا مَنۢ بَعَثَنَا مِن مَّرۡقَدِنَاۜۗ هَٰذَا مَا وَعَدَ ٱلرَّحۡمَٰنُ وَصَدَقَ ٱلۡمُرۡسَلُونَ", "Qaloo ya waylana man baAAathana min marqadina hatha ma waAAada alrrahmanu wasadaqa almursaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 53, "It will not be but one blast, and at once they are all brought present before Us", "إِن كَانَتۡ إِلَّا صَيۡحَةٗ وَٰحِدَةٗ فَإِذَا هُمۡ جَمِيعٞ لَّدَيۡنَا مُحۡضَرُونَ", "In kanat illa sayhatan wahidatan faitha hum jameeAAun ladayna muhdaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 54, "So today no soul will be wronged at all, and you will not be recompensed except for what you used to do", "فَٱلۡيَوۡمَ لَا تُظۡلَمُ نَفۡسٞ شَيۡـٔٗا وَلَا تُجۡزَوۡنَ إِلَّا مَا كُنتُمۡ تَعۡمَلُونَ", "Faalyawma la tuthlamu nafsun shayan wala tujzawna illa ma kuntum taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 55, "Indeed the companions of Paradise, that Day, will be amused in [joyful] occupation", "إِنَّ أَصۡحَٰبَ ٱلۡجَنَّةِ ٱلۡيَوۡمَ فِي شُغُلٖ فَٰكِهُونَ", "Inna ashaba aljannati alyawma fee shughulin fakihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 56, "They and their spouses - in shade, reclining on adorned couches", "هُمۡ وَأَزۡوَٰجُهُمۡ فِي ظِلَٰلٍ عَلَى ٱلۡأَرَآئِكِ مُتَّكِـُٔونَ", "Hum waazwajuhum fee thilalin AAala alaraiki muttakioona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 57, "For them therein is fruit, and for them is whatever they request [or wish]", "لَهُمۡ فِيهَا فَٰكِهَةٞ وَلَهُم مَّا يَدَّعُونَ", "Lahum feeha fakihatun walahum ma yaddaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 58, "[And] ""Peace,"" a word from a Merciful Lord", "سَلَٰمٞ قَوۡلٗا مِّن رَّبّٖ رَّحِيمٖ", "Salamun qawlan min rabbin raheemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 59, "[Then He will say], ""But stand apart today, you criminals", "وَٱمۡتَٰزُواْ ٱلۡيَوۡمَ أَيُّهَا ٱلۡمُجۡرِمُونَ", "Waimtazoo alyawma ayyuha almujrimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 60, "Did I not enjoin upon you, O children of Adam, that you not worship Satan - [for] indeed, he is to you a clear enemy", "۞أَلَمۡ أَعۡهَدۡ إِلَيۡكُمۡ يَٰبَنِيٓ ءَادَمَ أَن لَّا تَعۡبُدُواْ ٱلشَّيۡطَٰنَۖ إِنَّهُۥ لَكُمۡ عَدُوّٞ مُّبِينٞ", "Alam aAAhad ilaykum ya banee adama an la taAAbudoo alshshaytana innahu lakum AAaduwwun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 61, "And that you worship [only] Me? This is a straight path", "وَأَنِ ٱعۡبُدُونِيۚ هَٰذَا صِرَٰطٞ مُّسۡتَقِيمٞ", "Waani oAAbudoonee hatha siratun mustaqeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 62, "And he had already led astray from among you much of creation, so did you not use reason", "وَلَقَدۡ أَضَلَّ مِنكُمۡ جِبِلّٗا كَثِيرًاۖ أَفَلَمۡ تَكُونُواْ تَعۡقِلُونَ", "Walaqad adalla minkum jibillan katheeran afalam takoonoo taAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 63, "This is the Hellfire which you were promised", "هَٰذِهِۦ جَهَنَّمُ ٱلَّتِي كُنتُمۡ تُوعَدُونَ", "Hathihi jahannamu allatee kuntum tooAAadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 64, "[Enter to] burn therein today for what you used to deny", "ٱصۡلَوۡهَا ٱلۡيَوۡمَ بِمَا كُنتُمۡ تَكۡفُرُونَ", "Islawha alyawma bima kuntum takfuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 65, "That Day, We will seal over their mouths, and their hands will speak to Us, and their feet will testify about what they used to earn", "ٱلۡيَوۡمَ نَخۡتِمُ عَلَىٰٓ أَفۡوَٰهِهِمۡ وَتُكَلِّمُنَآ أَيۡدِيهِمۡ وَتَشۡهَدُ أَرۡجُلُهُم بِمَا كَانُواْ يَكۡسِبُونَ", "Alyawma nakhtimu AAala afwahihim watukallimuna aydeehim watashhadu arjuluhum bima kanoo yaksiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 66, "And if We willed, We could have obliterated their eyes, and they would race to [find] the path, and how could they see", "وَلَوۡ نَشَآءُ لَطَمَسۡنَا عَلَىٰٓ أَعۡيُنِهِمۡ فَٱسۡتَبَقُواْ ٱلصِّرَٰطَ فَأَنَّىٰ يُبۡصِرُونَ", "Walaw nashao latamasna AAala aAAyunihim faistabaqoo alssirata faanna yubsiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 67, "And if We willed, We could have deformed them, [paralyzing them] in their places so they would not be able to proceed, nor could they return", "وَلَوۡ نَشَآءُ لَمَسَخۡنَٰهُمۡ عَلَىٰ مَكَانَتِهِمۡ فَمَا ٱسۡتَطَٰعُواْ مُضِيّٗا وَلَا يَرۡجِعُونَ", "Walaw nashao lamasakhnahum AAala makanatihim fama istataAAoo mudiyyan wala yarjiAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 68, "And he to whom We grant long life We reverse in creation; so will they not understand", "وَمَن نُّعَمِّرۡهُ نُنَكِّسۡهُ فِي ٱلۡخَلۡقِۚ أَفَلَا يَعۡقِلُونَ", "Waman nuAAammirhu nunakkishu fee alkhalqi afala yaAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 69, "And We did not give Prophet Muhammad, knowledge of poetry, nor is it befitting for him. It is not but a message and a clear Qur'an", "وَمَا عَلَّمۡنَٰهُ ٱلشِّعۡرَ وَمَا يَنۢبَغِي لَهُۥٓۚ إِنۡ هُوَ إِلَّا ذِكۡرٞ وَقُرۡءَانٞ مُّبِينٞ", "Wama AAallamnahu alshshiAAra wama yanbaghee lahu in huwa illa thikrun waquranun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 70, "To warn whoever is alive and justify the word against the disbelievers", "لِّيُنذِرَ مَن كَانَ حَيّٗا وَيَحِقَّ ٱلۡقَوۡلُ عَلَى ٱلۡكَٰفِرِينَ", "Liyunthira man kana hayyan wayahiqqa alqawlu AAala alkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 71, "Do they not see that We have created for them from what Our hands have made, grazing livestock, and [then] they are their owners", "أَوَلَمۡ يَرَوۡاْ أَنَّا خَلَقۡنَا لَهُم مِّمَّا عَمِلَتۡ أَيۡدِينَآ أَنۡعَٰمٗا فَهُمۡ لَهَا مَٰلِكُونَ", "Awalam yaraw anna khalaqna lahum mimma AAamilat aydeena anAAaman fahum laha malikoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 72, "And We have tamed them for them, so some of them they ride, and some of them they eat", "وَذَلَّلۡنَٰهَا لَهُمۡ فَمِنۡهَا رَكُوبُهُمۡ وَمِنۡهَا يَأۡكُلُونَ", "Wathallalnaha lahum faminha rakoobuhum waminha yakuloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 73, "And for them therein are [other] benefits and drinks, so will they not be grateful", "وَلَهُمۡ فِيهَا مَنَٰفِعُ وَمَشَارِبُۚ أَفَلَا يَشۡكُرُونَ", "Walahum feeha manafiAAu wamasharibu afala yashkuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 74, "But they have taken besides Allah [false] deities that perhaps they would be helped", "وَٱتَّخَذُواْ مِن دُونِ ٱللَّهِ ءَالِهَةٗ لَّعَلَّهُمۡ يُنصَرُونَ", "Waittakhathoo min dooni Allahi alihatan laAAallahum yunsaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 75, "They are not able to help them, and they [themselves] are for them soldiers in attendance", "لَا يَسۡتَطِيعُونَ نَصۡرَهُمۡ وَهُمۡ لَهُمۡ جُندٞ مُّحۡضَرُونَ", "La yastateeAAoona nasrahum wahum lahum jundun muhdaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 76, "So let not their speech grieve you. Indeed, We know what they conceal and what they declare", "فَلَا يَحۡزُنكَ قَوۡلُهُمۡۘ إِنَّا نَعۡلَمُ مَا يُسِرُّونَ وَمَا يُعۡلِنُونَ", "Fala yahzunka qawluhum inna naAAlamu ma yusirroona wama yuAAlinoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 77, "Does man not consider that We created him from a [mere] sperm-drop - then at once he is a clear adversary", "أَوَلَمۡ يَرَ ٱلۡإِنسَٰنُ أَنَّا خَلَقۡنَٰهُ مِن نُّطۡفَةٖ فَإِذَا هُوَ خَصِيمٞ مُّبِينٞ", "Awalam yara alinsanu anna khalaqnahu min nutfatin faitha huwa khaseemun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 78, "And he presents for Us an example and forgets his [own] creation. He says, ""Who will give life to bones while they are disintegrated", "وَضَرَبَ لَنَا مَثَلٗا وَنَسِيَ خَلۡقَهُۥۖ قَالَ مَن يُحۡيِ ٱلۡعِظَٰمَ وَهِيَ رَمِيمٞ", "Wadaraba lana mathalan wanasiya khalqahu qala man yuhyee alAAithama wahiya rameemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 79, "Say, ""He will give them life who produced them the first time; and He is, of all creation, Knowing", "قُلۡ يُحۡيِيهَا ٱلَّذِيٓ أَنشَأَهَآ أَوَّلَ مَرَّةٖۖ وَهُوَ بِكُلِّ خَلۡقٍ عَلِيمٌ", "Qul yuhyeeha allathee anshaaha awwala marratin wahuwa bikulli khalqin AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 80, "[It is] He who made for you from the green tree, fire, and then from it you ignite", "ٱلَّذِي جَعَلَ لَكُم مِّنَ ٱلشَّجَرِ ٱلۡأَخۡضَرِ نَارٗا فَإِذَآ أَنتُم مِّنۡهُ تُوقِدُونَ", "Allathee jaAAala lakum mina alshshajari alakhdari naran faitha antum minhu tooqidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 81, "Is not He who created the heavens and the earth Able to create the likes of them? Yes, [it is so]; and He is the Knowing Creator", "أَوَلَيۡسَ ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ بِقَٰدِرٍ عَلَىٰٓ أَن يَخۡلُقَ مِثۡلَهُمۚ بَلَىٰ وَهُوَ ٱلۡخَلَّـٰقُ ٱلۡعَلِيمُ", "Awalaysa allathee khalaqa alssamawati waalarda biqadirin AAala an yakhluqa mithlahum bala wahuwa alkhallaqu alAAaleemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 82, "His command is only when He intends a thing that He says to it, ""Be,"" and it is", "إِنَّمَآ أَمۡرُهُۥٓ إِذَآ أَرَادَ شَيۡـًٔا أَن يَقُولَ لَهُۥ كُن فَيَكُونُ", "Innama amruhu itha arada shayan an yaqoola lahu kun fayakoonu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (36, 83, "So exalted is He in whose hand is the realm of all things, and to Him you will be returned", "فَسُبۡحَٰنَ ٱلَّذِي بِيَدِهِۦ مَلَكُوتُ كُلِّ شَيۡءٖ وَإِلَيۡهِ تُرۡجَعُونَ", "Fasubhana allathee biyadihi malakootu kulli shayin wailayhi turjaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 1, "By those [angels] lined up in rows", "وَٱلصَّـٰٓفَّـٰتِ صَفّٗا", "Waalssaffati saffan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 2, "And those who drive [the clouds]", "فَٱلزَّـٰجِرَٰتِ زَجۡرٗا", "Faalzzajirati zajran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 3, "And those who recite the message", "فَٱلتَّـٰلِيَٰتِ ذِكۡرًا", "Faalttaliyati thikran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 4, "Indeed, your God is One", "إِنَّ إِلَٰهَكُمۡ لَوَٰحِدٞ", "Inna ilahakum lawahidun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 5, "Lord of the heavens and the earth and that between them and Lord of the sunrises", "رَّبُّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا بَيۡنَهُمَا وَرَبُّ ٱلۡمَشَٰرِقِ", "Rabbu alssamawati waalardi wama baynahuma warabbu almashariqi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 6, "Indeed, We have adorned the nearest heaven with an adornment of stars", "إِنَّا زَيَّنَّا ٱلسَّمَآءَ ٱلدُّنۡيَا بِزِينَةٍ ٱلۡكَوَاكِبِ", "Inna zayyanna alssamaa alddunya bizeenatin alkawakibi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 7, "And as protection against every rebellious devil", "وَحِفۡظٗا مِّن كُلِّ شَيۡطَٰنٖ مَّارِدٖ", "Wahifthan min kulli shaytanin maridin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 8, "[So] they may not listen to the exalted assembly [of angels] and are pelted from every side", "لَّا يَسَّمَّعُونَ إِلَى ٱلۡمَلَإِ ٱلۡأَعۡلَىٰ وَيُقۡذَفُونَ مِن كُلِّ جَانِبٖ", "La yassammaAAoona ila almalai alaAAla wayuqthafoona min kulli janibin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 9, "Repelled; and for them is a constant punishment", "دُحُورٗاۖ وَلَهُمۡ عَذَابٞ وَاصِبٌ", "Duhooran walahum AAathabun wasibun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 10, "Except one who snatches [some words] by theft, but they are pursued by a burning flame, piercing [in brightness]", "إِلَّا مَنۡ خَطِفَ ٱلۡخَطۡفَةَ فَأَتۡبَعَهُۥ شِهَابٞ ثَاقِبٞ", "Illa man khatifa alkhatfata faatbaAAahu shihabun thaqibun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 11, "Then inquire of them, [O Muhammad], ""Are they a stronger [or more difficult] creation or those [others] We have created?"" Indeed, We created men from sticky clay", "فَٱسۡتَفۡتِهِمۡ أَهُمۡ أَشَدُّ خَلۡقًا أَم مَّنۡ خَلَقۡنَآۚ إِنَّا خَلَقۡنَٰهُم مِّن طِينٖ لَّازِبِۭ", "Faistaftihim ahum ashaddu khalqan am man khalaqna inna khalaqnahum min teenin lazibin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 12, "But you wonder, while they mock", "بَلۡ عَجِبۡتَ وَيَسۡخَرُونَ", "Bal AAajibta wayaskharoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 13, "And when they are reminded, they remember not", "وَإِذَا ذُكِّرُواْ لَا يَذۡكُرُونَ", "Waitha thukkiroo la yathkuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 14, "And when they see a sign, they ridicule", "وَإِذَا رَأَوۡاْ ءَايَةٗ يَسۡتَسۡخِرُونَ", "Waitha raaw ayatan yastaskhiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 15, "And say, ""This is not but obvious magic", "وَقَالُوٓاْ إِنۡ هَٰذَآ إِلَّا سِحۡرٞ مُّبِينٌ", "Waqaloo in hatha illa sihrun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 16, "When we have died and become dust and bones, are we indeed to be resurrected", "أَءِذَا مِتۡنَا وَكُنَّا تُرَابٗا وَعِظَٰمًا أَءِنَّا لَمَبۡعُوثُونَ", "Aitha mitna wakunna turaban waAAithaman ainna lamabAAoothoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 17, "And our forefathers [as well]", "أَوَءَابَآؤُنَا ٱلۡأَوَّلُونَ", "Awaabaona alawwaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 18, "Say, ""Yes, and you will be [rendered] contemptible", "قُلۡ نَعَمۡ وَأَنتُمۡ دَٰخِرُونَ", "Qul naAAam waantum dakhiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 19, "It will be only one shout, and at once they will be observing", "فَإِنَّمَا هِيَ زَجۡرَةٞ وَٰحِدَةٞ فَإِذَا هُمۡ يَنظُرُونَ", "Fainnama hiya zajratun wahidatun faitha hum yanthuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 20, "They will say, ""O woe to us! This is the Day of Recompense", "وَقَالُواْ يَٰوَيۡلَنَا هَٰذَا يَوۡمُ ٱلدِّينِ", "Waqaloo ya waylana hatha yawmu alddeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 21, "[They will be told], ""This is the Day of Judgement which you used to deny", "هَٰذَا يَوۡمُ ٱلۡفَصۡلِ ٱلَّذِي كُنتُم بِهِۦ تُكَذِّبُونَ", "Hatha yawmu alfasli allathee kuntum bihi tukaththiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 22, "[The angels will be ordered], ""Gather those who committed wrong, their kinds, and what they used to worship", "۞ٱحۡشُرُواْ ٱلَّذِينَ ظَلَمُواْ وَأَزۡوَٰجَهُمۡ وَمَا كَانُواْ يَعۡبُدُونَ", "Ohshuroo allatheena thalamoo waazwajahum wama kanoo yaAAbudoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 23, "Other than Allah, and guide them to the path of Hellfire", "مِن دُونِ ٱللَّهِ فَٱهۡدُوهُمۡ إِلَىٰ صِرَٰطِ ٱلۡجَحِيمِ", "Min dooni Allahi faihdoohum ila sirati aljaheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 24, "And stop them; indeed, they are to be questioned", "وَقِفُوهُمۡۖ إِنَّهُم مَّسۡـُٔولُونَ", "Waqifoohum innahum masooloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 25, "[They will be asked], ""What is [wrong] with you? Why do you not help each other", "مَا لَكُمۡ لَا تَنَاصَرُونَ", "Ma lakum la tanasaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 26, "But they, that Day, are in surrender", "بَلۡ هُمُ ٱلۡيَوۡمَ مُسۡتَسۡلِمُونَ", "Bal humu alyawma mustaslimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 27, "And they will approach one another blaming each other", "وَأَقۡبَلَ بَعۡضُهُمۡ عَلَىٰ بَعۡضٖ يَتَسَآءَلُونَ", "Waaqbala baAAduhum AAala baAAdin yatasaaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 28, "They will say, ""Indeed, you used to come at us from the right", "قَالُوٓاْ إِنَّكُمۡ كُنتُمۡ تَأۡتُونَنَا عَنِ ٱلۡيَمِينِ", "Qaloo innakum kuntum tatoonana AAani alyameeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 29, "The oppressors will say, ""Rather, you [yourselves] were not believers", "قَالُواْ بَل لَّمۡ تَكُونُواْ مُؤۡمِنِينَ", "Qaloo bal lam takoonoo mumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 30, "And we had over you no authority, but you were a transgressing people", "وَمَا كَانَ لَنَا عَلَيۡكُم مِّن سُلۡطَٰنِۭۖ بَلۡ كُنتُمۡ قَوۡمٗا طَٰغِينَ", "Wama kana lana AAalaykum min sultanin bal kuntum qawman tagheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 31, "So the word of our Lord has come into effect upon us; indeed, we will taste [punishment]", "فَحَقَّ عَلَيۡنَا قَوۡلُ رَبِّنَآۖ إِنَّا لَذَآئِقُونَ", "Fahaqqa AAalayna qawlu rabbina inna lathaiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 32, "And we led you to deviation; indeed, we were deviators", "فَأَغۡوَيۡنَٰكُمۡ إِنَّا كُنَّا غَٰوِينَ", "Faaghwaynakum inna kunna ghaweena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 33, "So indeed they, that Day, will be sharing in the punishment", "فَإِنَّهُمۡ يَوۡمَئِذٖ فِي ٱلۡعَذَابِ مُشۡتَرِكُونَ", "Fainnahum yawmaithin fee alAAathabi mushtarikoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 34, "Indeed, that is how We deal with the criminals", "إِنَّا كَذَٰلِكَ نَفۡعَلُ بِٱلۡمُجۡرِمِينَ", "Inna kathalika nafAAalu bialmujrimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 35, "Indeed they, when it was said to them, ""There is no deity but Allah,"" were arrogant", "إِنَّهُمۡ كَانُوٓاْ إِذَا قِيلَ لَهُمۡ لَآ إِلَٰهَ إِلَّا ٱللَّهُ يَسۡتَكۡبِرُونَ", "Innahum kanoo itha qeela lahum la ilaha illa Allahu yastakbiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 36, "And were saying, ""Are we to leave our gods for a mad poet", "وَيَقُولُونَ أَئِنَّا لَتَارِكُوٓاْ ءَالِهَتِنَا لِشَاعِرٖ مَّجۡنُونِۭ", "Wayaqooloona ainna latarikoo alihatina lishaAAirin majnoonin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 37, "Rather, the Prophet has come with the truth and confirmed the [previous] messengers", "بَلۡ جَآءَ بِٱلۡحَقِّ وَصَدَّقَ ٱلۡمُرۡسَلِينَ", "Bal jaa bialhaqqi wasaddaqa almursaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 38, "Indeed, you [disbelievers] will be tasters of the painful punishment", "إِنَّكُمۡ لَذَآئِقُواْ ٱلۡعَذَابِ ٱلۡأَلِيمِ", "Innakum lathaiqoo alAAathabi alaleemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 39, "And you will not be recompensed except for what you used to do", "وَمَا تُجۡزَوۡنَ إِلَّا مَا كُنتُمۡ تَعۡمَلُونَ", "Wama tujzawna illa ma kuntum taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 40, "But not the chosen servants of Allah", "إِلَّا عِبَادَ ٱللَّهِ ٱلۡمُخۡلَصِينَ", "Illa AAibada Allahi almukhlaseena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 41, "Those will have a provision determined", "أُوْلَـٰٓئِكَ لَهُمۡ رِزۡقٞ مَّعۡلُومٞ", "Olaika lahum rizqun maAAloomun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 42, "Fruits; and they will be honored", "فَوَٰكِهُ وَهُم مُّكۡرَمُونَ", "Fawakihu wahum mukramoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 43, "In gardens of pleasure", "فِي جَنَّـٰتِ ٱلنَّعِيمِ", "Fee jannati alnnaAAeemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 44, "On thrones facing one another", "عَلَىٰ سُرُرٖ مُّتَقَٰبِلِينَ", "AAala sururin mutaqabileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 45, "There will be circulated among them a cup [of wine] from a flowing spring", "يُطَافُ عَلَيۡهِم بِكَأۡسٖ مِّن مَّعِينِۭ", "Yutafu AAalayhim bikasin min maAAeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 46, "White and delicious to the drinkers", "بَيۡضَآءَ لَذَّةٖ لِّلشَّـٰرِبِينَ", "Baydaa laththatin lilshsharibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 47, "No bad effect is there in it, nor from it will they be intoxicated", "لَا فِيهَا غَوۡلٞ وَلَا هُمۡ عَنۡهَا يُنزَفُونَ", "La feeha ghawlun wala hum AAanha yunzafoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 48, "And with them will be women limiting [their] glances, with large, [beautiful] eyes", "وَعِندَهُمۡ قَٰصِرَٰتُ ٱلطَّرۡفِ عِينٞ", "WaAAindahum qasiratu alttarfi AAeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 49, "As if they were [delicate] eggs, well-protected", "كَأَنَّهُنَّ بَيۡضٞ مَّكۡنُونٞ", "Kaannahunna baydun maknoonun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 50, "And they will approach one another, inquiring of each other", "فَأَقۡبَلَ بَعۡضُهُمۡ عَلَىٰ بَعۡضٖ يَتَسَآءَلُونَ", "Faaqbala baAAduhum AAala baAAdin yatasaaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 51, "A speaker among them will say, ""Indeed, I had a companion [on earth]", "قَالَ قَآئِلٞ مِّنۡهُمۡ إِنِّي كَانَ لِي قَرِينٞ", "Qala qailun minhum innee kana lee qareenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 52, "Who would say, 'Are you indeed of those who believe", "يَقُولُ أَءِنَّكَ لَمِنَ ٱلۡمُصَدِّقِينَ", "Yaqoolu ainnaka lamina almusaddiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 53, "That when we have died and become dust and bones, we will indeed be recompensed", "أَءِذَا مِتۡنَا وَكُنَّا تُرَابٗا وَعِظَٰمًا أَءِنَّا لَمَدِينُونَ", "Aitha mitna wakunna turaban waAAithaman ainna lamadeenoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 54, "He will say, ""Would you [care to] look", "قَالَ هَلۡ أَنتُم مُّطَّلِعُونَ", "Qala hal antum muttaliAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 55, "And he will look and see him in the midst of the Hellfire", "فَٱطَّلَعَ فَرَءَاهُ فِي سَوَآءِ ٱلۡجَحِيمِ", "FaittalaAAa faraahu fee sawai aljaheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 56, "He will say, ""By Allah, you almost ruined me", "قَالَ تَٱللَّهِ إِن كِدتَّ لَتُرۡدِينِ", "Qala taAllahi in kidta laturdeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 57, "If not for the favor of my Lord, I would have been of those brought in [to Hell]", "وَلَوۡلَا نِعۡمَةُ رَبِّي لَكُنتُ مِنَ ٱلۡمُحۡضَرِينَ", "Walawla niAAmatu rabbee lakuntu mina almuhdareena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 58, "Then, are we not to die", "أَفَمَا نَحۡنُ بِمَيِّتِينَ", "Afama nahnu bimayyiteena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 59, "Except for our first death, and we will not be punished", "إِلَّا مَوۡتَتَنَا ٱلۡأُولَىٰ وَمَا نَحۡنُ بِمُعَذَّبِينَ", "Illa mawtatana aloola wama nahnu bimuAAaththabeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 60, "Indeed, this is the great attainment", "إِنَّ هَٰذَا لَهُوَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ", "Inna hatha lahuwa alfawzu alAAatheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 61, "For the like of this let the workers [on earth] work", "لِمِثۡلِ هَٰذَا فَلۡيَعۡمَلِ ٱلۡعَٰمِلُونَ", "Limithli hatha falyaAAmali alAAamiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 62, "Is Paradise a better accommodation or the tree of zaqqum", "أَذَٰلِكَ خَيۡرٞ نُّزُلًا أَمۡ شَجَرَةُ ٱلزَّقُّومِ", "Athalika khayrun nuzulan am shajaratu alzzaqqoomi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 63, "Indeed, We have made it a torment for the wrongdoers", "إِنَّا جَعَلۡنَٰهَا فِتۡنَةٗ لِّلظَّـٰلِمِينَ", "Inna jaAAalnaha fitnatan lilththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 64, "Indeed, it is a tree issuing from the bottom of the Hellfire", "إِنَّهَا شَجَرَةٞ تَخۡرُجُ فِيٓ أَصۡلِ ٱلۡجَحِيمِ", "Innaha shajaratun takhruju fee asli aljaheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 65, "Its emerging fruit as if it was heads of the devils", "طَلۡعُهَا كَأَنَّهُۥ رُءُوسُ ٱلشَّيَٰطِينِ", "TalAAuha kaannahu ruoosu alshshayateeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 66, "And indeed, they will eat from it and fill with it their bellies", "فَإِنَّهُمۡ لَأٓكِلُونَ مِنۡهَا فَمَالِـُٔونَ مِنۡهَا ٱلۡبُطُونَ", "Fainnahum laakiloona minha famalioona minha albutoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 67, "Then indeed, they will have after it a mixture of scalding water", "ثُمَّ إِنَّ لَهُمۡ عَلَيۡهَا لَشَوۡبٗا مِّنۡ حَمِيمٖ", "Thumma inna lahum AAalayha lashawban min hameemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 68, "Then indeed, their return will be to the Hellfire", "ثُمَّ إِنَّ مَرۡجِعَهُمۡ لَإِلَى ٱلۡجَحِيمِ", "Thumma inna marjiAAahum laila aljaheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 69, "Indeed they found their fathers astray", "إِنَّهُمۡ أَلۡفَوۡاْ ءَابَآءَهُمۡ ضَآلِّينَ", "Innahum alfaw abaahum dalleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 70, "So they hastened [to follow] in their footsteps", "فَهُمۡ عَلَىٰٓ ءَاثَٰرِهِمۡ يُهۡرَعُونَ", "Fahum AAala atharihim yuhraAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 71, "And there had already strayed before them most of the former peoples", "وَلَقَدۡ ضَلَّ قَبۡلَهُمۡ أَكۡثَرُ ٱلۡأَوَّلِينَ", "Walaqad dalla qablahum aktharu alawwaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 72, "And We had already sent among them warners", "وَلَقَدۡ أَرۡسَلۡنَا فِيهِم مُّنذِرِينَ", "Walaqad arsalna feehim munthireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 73, "Then look how was the end of those who were warned", "فَٱنظُرۡ كَيۡفَ كَانَ عَٰقِبَةُ ٱلۡمُنذَرِينَ", "Faonthur kayfa kana AAaqibatu almunthareena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 74, "But not the chosen servants of Allah", "إِلَّا عِبَادَ ٱللَّهِ ٱلۡمُخۡلَصِينَ", "Illa AAibada Allahi almukhlaseena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 75, "And Noah had certainly called Us, and [We are] the best of responders", "وَلَقَدۡ نَادَىٰنَا نُوحٞ فَلَنِعۡمَ ٱلۡمُجِيبُونَ", "Walaqad nadana noohun falaniAAma almujeeboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 76, "And We saved him and his family from the great affliction", "وَنَجَّيۡنَٰهُ وَأَهۡلَهُۥ مِنَ ٱلۡكَرۡبِ ٱلۡعَظِيمِ", "Wanajjaynahu waahlahu mina alkarbi alAAatheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 77, "And We made his descendants those remaining [on the earth]", "وَجَعَلۡنَا ذُرِّيَّتَهُۥ هُمُ ٱلۡبَاقِينَ", "WajaAAalna thurriyyatahu humu albaqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 78, "And left for him [favorable mention] among later generations", "وَتَرَكۡنَا عَلَيۡهِ فِي ٱلۡأٓخِرِينَ", "Watarakna AAalayhi fee alakhireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 79, "Peace upon Noah among the worlds", "سَلَٰمٌ عَلَىٰ نُوحٖ فِي ٱلۡعَٰلَمِينَ", "Salamun AAala noohin fee alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 80, "Indeed, We thus reward the doers of good", "إِنَّا كَذَٰلِكَ نَجۡزِي ٱلۡمُحۡسِنِينَ", "Inna kathalika najzee almuhsineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 81, "Indeed, he was of Our believing servants", "إِنَّهُۥ مِنۡ عِبَادِنَا ٱلۡمُؤۡمِنِينَ", "Innahu min AAibadina almumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 82, "Then We drowned the disbelievers", "ثُمَّ أَغۡرَقۡنَا ٱلۡأٓخَرِينَ", "Thumma aghraqna alakhareena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 83, "And indeed, among his kind was Abraham", "۞وَإِنَّ مِن شِيعَتِهِۦ لَإِبۡرَٰهِيمَ", "Wainna min sheeAAatihi laibraheema");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 84, "When he came to his Lord with a sound heart", "إِذۡ جَآءَ رَبَّهُۥ بِقَلۡبٖ سَلِيمٍ", "Ith jaa rabbahu biqalbin saleemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 85, "[And] when he said to his father and his people, ""What do you worship", "إِذۡ قَالَ لِأَبِيهِ وَقَوۡمِهِۦ مَاذَا تَعۡبُدُونَ", "Ith qala liabeehi waqawmihi matha taAAbudoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 86, "Is it falsehood [as] gods other than Allah you desire", "أَئِفۡكًا ءَالِهَةٗ دُونَ ٱللَّهِ تُرِيدُونَ", "Aifkan alihatan doona Allahi tureedoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 87, "Then what is your thought about the Lord of the worlds", "فَمَا ظَنُّكُم بِرَبِّ ٱلۡعَٰلَمِينَ", "Fama thannukum birabbi alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 88, "And he cast a look at the stars", "فَنَظَرَ نَظۡرَةٗ فِي ٱلنُّجُومِ", "Fanathara nathratan fee alnnujoomi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 89, "And said, ""Indeed, I am [about to be] ill", "فَقَالَ إِنِّي سَقِيمٞ", "Faqala innee saqeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 90, "So they turned away from him, departing", "فَتَوَلَّوۡاْ عَنۡهُ مُدۡبِرِينَ", "Fatawallaw AAanhu mudbireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 91, "Then he turned to their gods and said, ""Do you not eat", "فَرَاغَ إِلَىٰٓ ءَالِهَتِهِمۡ فَقَالَ أَلَا تَأۡكُلُونَ", "Faragha ila alihatihim faqala ala takuloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 92, "What is [wrong] with you that you do not speak", "مَا لَكُمۡ لَا تَنطِقُونَ", "Ma lakum la tantiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 93, "And he turned upon them a blow with [his] right hand", "فَرَاغَ عَلَيۡهِمۡ ضَرۡبَۢا بِٱلۡيَمِينِ", "Faragha AAalayhim darban bialyameeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 94, "Then the people came toward him, hastening", "فَأَقۡبَلُوٓاْ إِلَيۡهِ يَزِفُّونَ", "Faaqbaloo ilayhi yaziffoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 95, "He said, ""Do you worship that which you [yourselves] carve", "قَالَ أَتَعۡبُدُونَ مَا تَنۡحِتُونَ", "Qala ataAAbudoona ma tanhitoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 96, "While Allah created you and that which you do", "وَٱللَّهُ خَلَقَكُمۡ وَمَا تَعۡمَلُونَ", "WaAllahu khalaqakum wama taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 97, "They said, ""Construct for him a furnace and throw him into the burning fire", "قَالُواْ ٱبۡنُواْ لَهُۥ بُنۡيَٰنٗا فَأَلۡقُوهُ فِي ٱلۡجَحِيمِ", "Qaloo ibnoo lahu bunyanan faalqoohu fee aljaheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 98, "And they intended for him a plan, but We made them the most debased", "فَأَرَادُواْ بِهِۦ كَيۡدٗا فَجَعَلۡنَٰهُمُ ٱلۡأَسۡفَلِينَ", "Faaradoo bihi kaydan fajaAAalnahumu alasfaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 99, "And [then] he said, ""Indeed, I will go to [where I am ordered by] my Lord; He will guide me", "وَقَالَ إِنِّي ذَاهِبٌ إِلَىٰ رَبِّي سَيَهۡدِينِ", "Waqala innee thahibun ila rabbee sayahdeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 100, "My Lord, grant me [a child] from among the righteous", "رَبِّ هَبۡ لِي مِنَ ٱلصَّـٰلِحِينَ", "Rabbi hab lee mina alssaliheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 101, "So We gave him good tidings of a forbearing boy", "فَبَشَّرۡنَٰهُ بِغُلَٰمٍ حَلِيمٖ", "Fabashsharnahu bighulamin haleemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 102, "And when he reached with him [the age of] exertion, he said, ""O my son, indeed I have seen in a dream that I [must] sacrifice you, so see what you think."" He said, ""O my father, do as you are commanded. You will find me, if Allah wills, of the steadfast", "فَلَمَّا بَلَغَ مَعَهُ ٱلسَّعۡيَ قَالَ يَٰبُنَيَّ إِنِّيٓ أَرَىٰ فِي ٱلۡمَنَامِ أَنِّيٓ أَذۡبَحُكَ فَٱنظُرۡ مَاذَا تَرَىٰۚ قَالَ يَـٰٓأَبَتِ ٱفۡعَلۡ مَا تُؤۡمَرُۖ سَتَجِدُنِيٓ إِن شَآءَ ٱللَّهُ مِنَ ٱلصَّـٰبِرِينَ", "Falamma balagha maAAahu alssaAAya qala ya bunayya innee ara fee almanami annee athbahuka faonthur matha tara qala ya abati ifAAal ma tumaru satajidunee in shaa Allahu mina alssabireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 103, "And when they had both submitted and he put him down upon his forehead", "فَلَمَّآ أَسۡلَمَا وَتَلَّهُۥ لِلۡجَبِينِ", "Falamma aslama watallahu liljabeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 104, "We called to him, ""O Abraham", "وَنَٰدَيۡنَٰهُ أَن يَـٰٓإِبۡرَٰهِيمُ", "Wanadaynahu an ya ibraheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 105, "You have fulfilled the vision."" Indeed, We thus reward the doers of good", "قَدۡ صَدَّقۡتَ ٱلرُّءۡيَآۚ إِنَّا كَذَٰلِكَ نَجۡزِي ٱلۡمُحۡسِنِينَ", "Qad saddaqta alrruya inna kathalika najzee almuhsineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 106, "Indeed, this was the clear trial", "إِنَّ هَٰذَا لَهُوَ ٱلۡبَلَـٰٓؤُاْ ٱلۡمُبِينُ", "Inna hatha lahuwa albalao almubeenu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 107, "And We ransomed him with a great sacrifice", "وَفَدَيۡنَٰهُ بِذِبۡحٍ عَظِيمٖ", "Wafadaynahu bithibhin AAatheemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 108, "And We left for him [favorable mention] among later generations", "وَتَرَكۡنَا عَلَيۡهِ فِي ٱلۡأٓخِرِينَ", "Watarakna AAalayhi fee alakhireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 109, "Peace upon Abraham", "سَلَٰمٌ عَلَىٰٓ إِبۡرَٰهِيمَ", "Salamun AAala ibraheema");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 110, "Indeed, We thus reward the doers of good", "كَذَٰلِكَ نَجۡزِي ٱلۡمُحۡسِنِينَ", "Kathalika najzee almuhsineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 111, "Indeed, he was of Our believing servants", "إِنَّهُۥ مِنۡ عِبَادِنَا ٱلۡمُؤۡمِنِينَ", "Innahu min AAibadina almumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 112, "And We gave him good tidings of Isaac, a prophet from among the righteous", "وَبَشَّرۡنَٰهُ بِإِسۡحَٰقَ نَبِيّٗا مِّنَ ٱلصَّـٰلِحِينَ", "Wabashsharnahu biishaqa nabiyyan mina alssaliheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 113, "And We blessed him and Isaac. But among their descendants is the doer of good and the clearly unjust to himself", "وَبَٰرَكۡنَا عَلَيۡهِ وَعَلَىٰٓ إِسۡحَٰقَۚ وَمِن ذُرِّيَّتِهِمَا مُحۡسِنٞ وَظَالِمٞ لِّنَفۡسِهِۦ مُبِينٞ", "Wabarakna AAalayhi waAAala ishaqa wamin thurriyyatihima muhsinun wathalimun linafsihi mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 114, "And We did certainly confer favor upon Moses and Aaron", "وَلَقَدۡ مَنَنَّا عَلَىٰ مُوسَىٰ وَهَٰرُونَ", "Walaqad mananna AAala moosa waharoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 115, "And We saved them and their people from the great affliction", "وَنَجَّيۡنَٰهُمَا وَقَوۡمَهُمَا مِنَ ٱلۡكَرۡبِ ٱلۡعَظِيمِ", "Wanajjaynahuma waqawmahuma mina alkarbi alAAatheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 116, "And We supported them so it was they who overcame", "وَنَصَرۡنَٰهُمۡ فَكَانُواْ هُمُ ٱلۡغَٰلِبِينَ", "Wanasarnahum fakanoo humu alghalibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 117, "And We gave them the explicit Scripture", "وَءَاتَيۡنَٰهُمَا ٱلۡكِتَٰبَ ٱلۡمُسۡتَبِينَ", "Waataynahuma alkitaba almustabeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 118, "And We guided them on the straight path", "وَهَدَيۡنَٰهُمَا ٱلصِّرَٰطَ ٱلۡمُسۡتَقِيمَ", "Wahadaynahuma alssirata almustaqeema");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 119, "And We left for them [favorable mention] among later generations", "وَتَرَكۡنَا عَلَيۡهِمَا فِي ٱلۡأٓخِرِينَ", "Watarakna AAalayhima fee alakhireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 120, "Peace upon Moses and Aaron", "سَلَٰمٌ عَلَىٰ مُوسَىٰ وَهَٰرُونَ", "Salamun AAala moosa waharoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 121, "Indeed, We thus reward the doers of good", "إِنَّا كَذَٰلِكَ نَجۡزِي ٱلۡمُحۡسِنِينَ", "Inna kathalika najzee almuhsineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 122, "Indeed, they were of Our believing servants", "إِنَّهُمَا مِنۡ عِبَادِنَا ٱلۡمُؤۡمِنِينَ", "Innahuma min AAibadina almumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 123, "And indeed, Elias was from among the messengers", "وَإِنَّ إِلۡيَاسَ لَمِنَ ٱلۡمُرۡسَلِينَ", "Wainna ilyasa lamina almursaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 124, "When he said to his people, ""Will you not fear Allah", "إِذۡ قَالَ لِقَوۡمِهِۦٓ أَلَا تَتَّقُونَ", "Ith qala liqawmihi ala tattaqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 125, "Do you call upon Ba'l and leave the best of creators", "أَتَدۡعُونَ بَعۡلٗا وَتَذَرُونَ أَحۡسَنَ ٱلۡخَٰلِقِينَ", "AtadAAoona baAAlan watatharoona ahsana alkhaliqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 126, "Allah, your Lord and the Lord of your first forefathers", "ٱللَّهَ رَبَّكُمۡ وَرَبَّ ءَابَآئِكُمُ ٱلۡأَوَّلِينَ", "Allaha rabbakum warabba abaikumu alawwaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 127, "And they denied him, so indeed, they will be brought [for punishment]", "فَكَذَّبُوهُ فَإِنَّهُمۡ لَمُحۡضَرُونَ", "Fakaththaboohu fainnahum lamuhdaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 128, "Except the chosen servants of Allah", "إِلَّا عِبَادَ ٱللَّهِ ٱلۡمُخۡلَصِينَ", "Illa AAibada Allahi almukhlaseena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 129, "And We left for him [favorable mention] among later generations", "وَتَرَكۡنَا عَلَيۡهِ فِي ٱلۡأٓخِرِينَ", "Watarakna AAalayhi fee alakhireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 130, "Peace upon Elias", "سَلَٰمٌ عَلَىٰٓ إِلۡ يَاسِينَ", "Salamun AAala il yaseena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 131, "Indeed, We thus reward the doers of good", "إِنَّا كَذَٰلِكَ نَجۡزِي ٱلۡمُحۡسِنِينَ", "Inna kathalika najzee almuhsineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 132, "Indeed, he was of Our believing servants", "إِنَّهُۥ مِنۡ عِبَادِنَا ٱلۡمُؤۡمِنِينَ", "Innahu min AAibadina almumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 133, "And indeed, Lot was among the messengers", "وَإِنَّ لُوطٗا لَّمِنَ ٱلۡمُرۡسَلِينَ", "Wainna lootan lamina almursaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 134, "[So mention] when We saved him and his family, all", "إِذۡ نَجَّيۡنَٰهُ وَأَهۡلَهُۥٓ أَجۡمَعِينَ", "Ith najjaynahu waahlahu ajmaAAeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 135, "Except his wife among those who remained [with the evildoers]", "إِلَّا عَجُوزٗا فِي ٱلۡغَٰبِرِينَ", "Illa AAajoozan fee alghabireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 136, "Then We destroyed the others", "ثُمَّ دَمَّرۡنَا ٱلۡأٓخَرِينَ", "Thumma dammarna alakhareena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 137, "And indeed, you pass by them in the morning", "وَإِنَّكُمۡ لَتَمُرُّونَ عَلَيۡهِم مُّصۡبِحِينَ", "Wainnakum latamurroona AAalayhim musbiheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 138, "And at night. Then will you not use reason", "وَبِٱلَّيۡلِۚ أَفَلَا تَعۡقِلُونَ", "Wabiallayli afala taAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 139, "And indeed, Jonah was among the messengers", "وَإِنَّ يُونُسَ لَمِنَ ٱلۡمُرۡسَلِينَ", "Wainna yoonusa lamina almursaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 140, "[Mention] when he ran away to the laden ship", "إِذۡ أَبَقَ إِلَى ٱلۡفُلۡكِ ٱلۡمَشۡحُونِ", "Ith abaqa ila alfulki almashhooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 141, "And he drew lots and was among the losers", "فَسَاهَمَ فَكَانَ مِنَ ٱلۡمُدۡحَضِينَ", "Fasahama fakana mina almudhadeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 142, "Then the fish swallowed him, while he was blameworthy", "فَٱلۡتَقَمَهُ ٱلۡحُوتُ وَهُوَ مُلِيمٞ", "Failtaqamahu alhootu wahuwa muleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 143, "And had he not been of those who exalt Allah", "فَلَوۡلَآ أَنَّهُۥ كَانَ مِنَ ٱلۡمُسَبِّحِينَ", "Falawla annahu kana mina almusabbiheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 144, "He would have remained inside its belly until the Day they are resurrected", "لَلَبِثَ فِي بَطۡنِهِۦٓ إِلَىٰ يَوۡمِ يُبۡعَثُونَ", "Lalabitha fee batnihi ila yawmi yubAAathoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 145, "But We threw him onto the open shore while he was ill", "۞فَنَبَذۡنَٰهُ بِٱلۡعَرَآءِ وَهُوَ سَقِيمٞ", "Fanabathnahu bialAAarai wahuwa saqeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 146, "And We caused to grow over him a gourd vine", "وَأَنۢبَتۡنَا عَلَيۡهِ شَجَرَةٗ مِّن يَقۡطِينٖ", "Waanbatna AAalayhi shajaratan min yaqteenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 147, "And We sent him to [his people of] a hundred thousand or more", "وَأَرۡسَلۡنَٰهُ إِلَىٰ مِاْئَةِ أَلۡفٍ أَوۡ يَزِيدُونَ", "Waarsalnahu ila miati alfin aw yazeedoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 148, "And they believed, so We gave them enjoyment [of life] for a time", "فَـَٔامَنُواْ فَمَتَّعۡنَٰهُمۡ إِلَىٰ حِينٖ", "Faamanoo famattaAAnahum ila heenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 149, "So inquire of them, [O Muhammad], ""Does your Lord have daughters while they have sons", "فَٱسۡتَفۡتِهِمۡ أَلِرَبِّكَ ٱلۡبَنَاتُ وَلَهُمُ ٱلۡبَنُونَ", "Faistaftihim alirabbika albanatu walahumu albanoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 150, "Or did We create the angels as females while they were witnesses", "أَمۡ خَلَقۡنَا ٱلۡمَلَـٰٓئِكَةَ إِنَٰثٗا وَهُمۡ شَٰهِدُونَ", "Am khalaqna almalaikata inathan wahum shahidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 151, "Unquestionably, it is out of their [invented] falsehood that they say", "أَلَآ إِنَّهُم مِّنۡ إِفۡكِهِمۡ لَيَقُولُونَ", "Ala innahum min ifkihim layaqooloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 152, "Allah has begotten,"" and indeed, they are liars", "وَلَدَ ٱللَّهُ وَإِنَّهُمۡ لَكَٰذِبُونَ", "Walada Allahu wainnahum lakathiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 153, "Has He chosen daughters over sons", "أَصۡطَفَى ٱلۡبَنَاتِ عَلَى ٱلۡبَنِينَ", "Astafa albanati AAala albaneena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 154, "What is [wrong] with you? How do you make judgement", "مَا لَكُمۡ كَيۡفَ تَحۡكُمُونَ", "Ma lakum kayfa tahkumoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 155, "Then will you not be reminded", "أَفَلَا تَذَكَّرُونَ", "Afala tathakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 156, "Or do you have a clear authority", "أَمۡ لَكُمۡ سُلۡطَٰنٞ مُّبِينٞ", "Am lakum sultanun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 157, "Then produce your scripture, if you should be truthful", "فَأۡتُواْ بِكِتَٰبِكُمۡ إِن كُنتُمۡ صَٰدِقِينَ", "Fatoo bikitabikum in kuntum sadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 158, "And they have claimed between Him and the jinn a lineage, but the jinn have already known that they [who made such claims] will be brought to [punishment]", "وَجَعَلُواْ بَيۡنَهُۥ وَبَيۡنَ ٱلۡجِنَّةِ نَسَبٗاۚ وَلَقَدۡ عَلِمَتِ ٱلۡجِنَّةُ إِنَّهُمۡ لَمُحۡضَرُونَ", "WajaAAaloo baynahu wabayna aljinnati nasaban walaqad AAalimati aljinnatu innahum lamuhdaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 159, "Exalted is Allah above what they describe", "سُبۡحَٰنَ ٱللَّهِ عَمَّا يَصِفُونَ", "Subhana Allahi AAamma yasifoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 160, "Except the chosen servants of Allah [who do not share in that sin]", "إِلَّا عِبَادَ ٱللَّهِ ٱلۡمُخۡلَصِينَ", "Illa AAibada Allahi almukhlaseena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 161, "So indeed, you [disbelievers] and whatever you worship", "فَإِنَّكُمۡ وَمَا تَعۡبُدُونَ", "Fainnakum wama taAAbudoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 162, "You cannot tempt [anyone] away from Him", "مَآ أَنتُمۡ عَلَيۡهِ بِفَٰتِنِينَ", "Ma antum AAalayhi bifatineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 163, "Except he who is to [enter and] burn in the Hellfire", "إِلَّا مَنۡ هُوَ صَالِ ٱلۡجَحِيمِ", "Illa man huwa sali aljaheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 164, "[The angels say], ""There is not among us any except that he has a known position", "وَمَامِنَّآ إِلَّا لَهُۥ مَقَامٞ مَّعۡلُومٞ", "Wama minna illa lahu maqamun maAAloomun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 165, "And indeed, we are those who line up [for prayer]", "وَإِنَّا لَنَحۡنُ ٱلصَّآفُّونَ", "Wainna lanahnu alssaffoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 166, "And indeed, we are those who exalt Allah", "وَإِنَّا لَنَحۡنُ ٱلۡمُسَبِّحُونَ", "Wainna lanahnu almusabbihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 167, "And indeed, the disbelievers used to say", "وَإِن كَانُواْ لَيَقُولُونَ", "Wain kanoo layaqooloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 168, "If we had a message from [those of] the former peoples", "لَوۡ أَنَّ عِندَنَا ذِكۡرٗا مِّنَ ٱلۡأَوَّلِينَ", "Law anna AAindana thikran mina alawwaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 169, "We would have been the chosen servants of Allah", "لَكُنَّا عِبَادَ ٱللَّهِ ٱلۡمُخۡلَصِينَ", "Lakunna AAibada Allahi almukhlaseena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 170, "But they disbelieved in it, so they are going to know", "فَكَفَرُواْ بِهِۦۖ فَسَوۡفَ يَعۡلَمُونَ", "Fakafaroo bihi fasawfa yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 171, "And Our word has already preceded for Our servants, the messengers", "وَلَقَدۡ سَبَقَتۡ كَلِمَتُنَا لِعِبَادِنَا ٱلۡمُرۡسَلِينَ", "Walaqad sabaqat kalimatuna liAAibadina almursaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 172, "[That] indeed, they would be those given victory", "إِنَّهُمۡ لَهُمُ ٱلۡمَنصُورُونَ", "Innahum lahumu almansooroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 173, "And [that] indeed, Our soldiers will be those who overcome", "وَإِنَّ جُندَنَا لَهُمُ ٱلۡغَٰلِبُونَ", "Wainna jundana lahumu alghaliboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 174, "So, [O Muhammad], leave them for a time", "فَتَوَلَّ عَنۡهُمۡ حَتَّىٰ حِينٖ", "Fatawalla AAanhum hatta heenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 175, "And see [what will befall] them, for they are going to see", "وَأَبۡصِرۡهُمۡ فَسَوۡفَ يُبۡصِرُونَ", "Waabsirhum fasawfa yubsiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 176, "Then for Our punishment are they impatient", "أَفَبِعَذَابِنَا يَسۡتَعۡجِلُونَ", "AfabiAAathabina yastaAAjiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 177, "But when it descends in their territory, then evil is the morning of those who were warned", "فَإِذَا نَزَلَ بِسَاحَتِهِمۡ فَسَآءَ صَبَاحُ ٱلۡمُنذَرِينَ", "Faitha nazala bisahatihim fasaa sabahu almunthareena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 178, "And leave them for a time", "وَتَوَلَّ عَنۡهُمۡ حَتَّىٰ حِينٖ", "Watawalla AAanhum hatta heenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 179, "And see, for they are going to see", "وَأَبۡصِرۡ فَسَوۡفَ يُبۡصِرُونَ", "Waabsir fasawfa yubsiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 180, "Exalted is your Lord, the Lord of might, above what they describe", "سُبۡحَٰنَ رَبِّكَ رَبِّ ٱلۡعِزَّةِ عَمَّا يَصِفُونَ", "Subhana rabbika rabbi alAAizzati AAamma yasifoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 181, "And peace upon the messengers", "وَسَلَٰمٌ عَلَى ٱلۡمُرۡسَلِينَ", "Wasalamun AAala almursaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (37, 182, "And praise to Allah, Lord of the worlds", "وَٱلۡحَمۡدُ لِلَّهِ رَبِّ ٱلۡعَٰلَمِينَ", "Waalhamdu lillahi rabbi alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 1, "Sad. By the Qur'an containing reminder", "صٓۚ وَٱلۡقُرۡءَانِ ذِي ٱلذِّكۡرِ", "Sad waalqurani thee alththikri");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 2, "But those who disbelieve are in pride and dissension", "بَلِ ٱلَّذِينَ كَفَرُواْ فِي عِزَّةٖ وَشِقَاقٖ", "Bali allatheena kafaroo fee AAizzatin washiqaqin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 3, "How many a generation have We destroyed before them, and they [then] called out; but it was not a time for escape", "كَمۡ أَهۡلَكۡنَا مِن قَبۡلِهِم مِّن قَرۡنٖ فَنَادَواْ وَّلَاتَ حِينَ مَنَاصٖ", "Kam ahlakna min qablihim min qarnin fanadaw walata heena manasin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 4, "And they wonder that there has come to them a warner from among themselves. And the disbelievers say, ""This is a magician and a liar", "وَعَجِبُوٓاْ أَن جَآءَهُم مُّنذِرٞ مِّنۡهُمۡۖ وَقَالَ ٱلۡكَٰفِرُونَ هَٰذَا سَٰحِرٞ كَذَّابٌ", "WaAAajiboo an jaahum munthirun minhum waqala alkafiroona hatha sahirun kaththabun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 5, "Has he made the gods [only] one God? Indeed, this is a curious thing", "أَجَعَلَ ٱلۡأٓلِهَةَ إِلَٰهٗا وَٰحِدًاۖ إِنَّ هَٰذَا لَشَيۡءٌ عُجَابٞ", "AjaAAala alalihata ilahan wahidan inna hatha lashayon AAujabun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 6, "And the eminent among them went forth, [saying], ""Continue, and be patient over [the defense of] your gods. Indeed, this is a thing intended", "وَٱنطَلَقَ ٱلۡمَلَأُ مِنۡهُمۡ أَنِ ٱمۡشُواْ وَٱصۡبِرُواْ عَلَىٰٓ ءَالِهَتِكُمۡۖ إِنَّ هَٰذَا لَشَيۡءٞ يُرَادُ", "Waintalaqa almalao minhum ani imshoo waisbiroo AAala alihatikum inna hatha lashayon yuradu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 7, "We have not heard of this in the latest religion. This is not but a fabrication", "مَا سَمِعۡنَا بِهَٰذَا فِي ٱلۡمِلَّةِ ٱلۡأٓخِرَةِ إِنۡ هَٰذَآ إِلَّا ٱخۡتِلَٰقٌ", "Ma samiAAna bihatha fee almillati alakhirati in hatha illa ikhtilaqun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 8, "Has the message been revealed to him out of [all of] us?"" Rather, they are in doubt about My message. Rather, they have not yet tasted My punishment", "أَءُنزِلَ عَلَيۡهِ ٱلذِّكۡرُ مِنۢ بَيۡنِنَاۚ بَلۡ هُمۡ فِي شَكّٖ مِّن ذِكۡرِيۚ بَل لَّمَّا يَذُوقُواْ عَذَابِ", "Aonzila AAalayhi alththikru min baynina bal hum fee shakkin min thikree bal lamma yathooqoo AAathabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 9, "Or do they have the depositories of the mercy of your Lord, the Exalted in Might, the Bestower", "أَمۡ عِندَهُمۡ خَزَآئِنُ رَحۡمَةِ رَبِّكَ ٱلۡعَزِيزِ ٱلۡوَهَّابِ", "Am AAindahum khazainu rahmati rabbika alAAazeezi alwahhabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 10, "Or is theirs the dominion of the heavens and the earth and what is between them? Then let them ascend through [any] ways of access", "أَمۡ لَهُم مُّلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا بَيۡنَهُمَاۖ فَلۡيَرۡتَقُواْ فِي ٱلۡأَسۡبَٰبِ", "Am lahum mulku alssamawati waalardi wama baynahuma falyartaqoo fee alasbabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 11, "[They are but] soldiers [who will be] defeated there among the companies [of disbelievers]", "جُندٞ مَّا هُنَالِكَ مَهۡزُومٞ مِّنَ ٱلۡأَحۡزَابِ", "Jundun ma hunalika mahzoomun mina alahzabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 12, "The people of Noah denied before them, and [the tribe of] 'Aad and Pharaoh, the owner of stakes", "كَذَّبَتۡ قَبۡلَهُمۡ قَوۡمُ نُوحٖ وَعَادٞ وَفِرۡعَوۡنُ ذُو ٱلۡأَوۡتَادِ", "Kaththabat qablahum qawmu noohin waAAadun wafirAAawnu thoo alawtadi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 13, "And [the tribe of] Thamud and the people of Lot and the companions of the thicket. Those are the companies", "وَثَمُودُ وَقَوۡمُ لُوطٖ وَأَصۡحَٰبُ لۡـَٔيۡكَةِۚ أُوْلَـٰٓئِكَ ٱلۡأَحۡزَابُ", "Wathamoodu waqawmu lootin waashabu alaykati olaika alahzabu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 14, "Each of them denied the messengers, so My penalty was justified", "إِن كُلٌّ إِلَّا كَذَّبَ ٱلرُّسُلَ فَحَقَّ عِقَابِ", "In kullun illa kaththaba alrrusula fahaqqa AAiqabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 15, "And these [disbelievers] await not but one blast [of the Horn]; for it there will be no delay", "وَمَا يَنظُرُ هَـٰٓؤُلَآءِ إِلَّا صَيۡحَةٗ وَٰحِدَةٗ مَّا لَهَا مِن فَوَاقٖ", "Wama yanthuru haolai illa sayhatan wahidatan ma laha min fawaqin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 16, "And they say, ""Our Lord, hasten for us our share [of the punishment] before the Day of Account", "وَقَالُواْ رَبَّنَا عَجِّل لَّنَا قِطَّنَا قَبۡلَ يَوۡمِ ٱلۡحِسَابِ", "Waqaloo rabbana AAajjil lana qittana qabla yawmi alhisabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 17, "Be patient over what they say and remember Our servant, David, the possessor of strength; indeed, he was one who repeatedly turned back [to Allah]", "ٱصۡبِرۡ عَلَىٰ مَا يَقُولُونَ وَٱذۡكُرۡ عَبۡدَنَا دَاوُۥدَ ذَا ٱلۡأَيۡدِۖ إِنَّهُۥٓ أَوَّابٌ", "Isbir AAala ma yaqooloona waothkur AAabdana dawooda tha alaydi innahu awwabun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 18, "Indeed, We subjected the mountains [to praise] with him, exalting [Allah] in the [late] afternoon and [after] sunrise", "إِنَّا سَخَّرۡنَا ٱلۡجِبَالَ مَعَهُۥ يُسَبِّحۡنَ بِٱلۡعَشِيِّ وَٱلۡإِشۡرَاقِ", "Inna sakhkharna aljibala maAAahu yusabbihna bialAAashiyyi waalishraqi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 19, "And the birds were assembled, all with him repeating [praises]", "وَٱلطَّيۡرَ مَحۡشُورَةٗۖ كُلّٞ لَّهُۥٓ أَوَّابٞ", "Waalttayra mahshooratan kullun lahu awwabun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 20, "And We strengthened his kingdom and gave him wisdom and discernment in speech", "وَشَدَدۡنَا مُلۡكَهُۥ وَءَاتَيۡنَٰهُ ٱلۡحِكۡمَةَ وَفَصۡلَ ٱلۡخِطَابِ", "Washadadna mulkahu waataynahu alhikmata wafasla alkhitabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 21, "And has there come to you the news of the adversaries, when they climbed over the wall of [his] prayer chamber", "۞وَهَلۡ أَتَىٰكَ نَبَؤُاْ ٱلۡخَصۡمِ إِذۡ تَسَوَّرُواْ ٱلۡمِحۡرَابَ", "Wahal ataka nabao alkhasmi ith tasawwaroo almihraba");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 22, "When they entered upon David and he was alarmed by them? They said, ""Fear not. [We are] two adversaries, one of whom has wronged the other, so judge between us with truth and do not exceed [it] and guide us to the sound path", "إِذۡ دَخَلُواْ عَلَىٰ دَاوُۥدَ فَفَزِعَ مِنۡهُمۡۖ قَالُواْ لَا تَخَفۡۖ خَصۡمَانِ بَغَىٰ بَعۡضُنَا عَلَىٰ بَعۡضٖ فَٱحۡكُم بَيۡنَنَا بِٱلۡحَقِّ وَلَا تُشۡطِطۡ وَٱهۡدِنَآ إِلَىٰ سَوَآءِ ٱلصِّرَٰطِ", "Ith dakhaloo AAala dawooda fafaziAAa minhum qaloo la takhaf khasmani bagha baAAduna AAala baAAdin faohkum baynana bialhaqqi wala tushtit waihdina ila sawai alssirati");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 23, "Indeed this, my brother, has ninety-nine ewes, and I have one ewe; so he said, 'Entrust her to me,' and he overpowered me in speech", "إِنَّ هَٰذَآ أَخِي لَهُۥ تِسۡعٞ وَتِسۡعُونَ نَعۡجَةٗ وَلِيَ نَعۡجَةٞ وَٰحِدَةٞ فَقَالَ أَكۡفِلۡنِيهَا وَعَزَّنِي فِي ٱلۡخِطَابِ", "Inna hatha akhee lahu tisAAun watisAAoona naAAjatan waliya naAAjatun wahidatun faqala akfilneeha waAAazzanee fee alkhitabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 24, "[David] said, ""He has certainly wronged you in demanding your ewe [in addition] to his ewes. And indeed, many associates oppress one another, except for those who believe and do righteous deeds - and few are they."" And David became certain that We had tried him, and he asked forgiveness of his Lord and fell down bowing [in prostration] and turned in repentance [to Allah]", "قَالَ لَقَدۡ ظَلَمَكَ بِسُؤَالِ نَعۡجَتِكَ إِلَىٰ نِعَاجِهِۦۖ وَإِنَّ كَثِيرٗا مِّنَ ٱلۡخُلَطَآءِ لَيَبۡغِي بَعۡضُهُمۡ عَلَىٰ بَعۡضٍ إِلَّا ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ وَقَلِيلٞ مَّا هُمۡۗ وَظَنَّ دَاوُۥدُ أَنَّمَا فَتَنَّـٰهُ فَٱسۡتَغۡفَرَ رَبَّهُۥ وَخَرَّۤ رَاكِعٗاۤ وَأَنَابَ۩", "Qala laqad thalamaka bisuali naAAjatika ila niAAajihi wainna katheeran mina alkhulatai layabghee baAAduhum AAala baAAdin illa allatheena amanoo waAAamiloo alssalihati waqaleelun ma hum wathanna dawoodu annama fatannahu faistaghfara rabbahu wakharra rakiAAan waanaba");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 25, "So We forgave him that; and indeed, for him is nearness to Us and a good place of return", "فَغَفَرۡنَا لَهُۥ ذَٰلِكَۖ وَإِنَّ لَهُۥ عِندَنَا لَزُلۡفَىٰ وَحُسۡنَ مَـَٔابٖ", "Faghafarna lahu thalika wainna lahu AAindana lazulfa wahusna maabin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 26, "[We said], ""O David, indeed We have made you a successor upon the earth, so judge between the people in truth and do not follow [your own] desire, as it will lead you astray from the way of Allah."" Indeed, those who go astray from the way of Allah will have a severe punishment for having forgotten the Day of Account", "يَٰدَاوُۥدُ إِنَّا جَعَلۡنَٰكَ خَلِيفَةٗ فِي ٱلۡأَرۡضِ فَٱحۡكُم بَيۡنَ ٱلنَّاسِ بِٱلۡحَقِّ وَلَا تَتَّبِعِ ٱلۡهَوَىٰ فَيُضِلَّكَ عَن سَبِيلِ ٱللَّهِۚ إِنَّ ٱلَّذِينَ يَضِلُّونَ عَن سَبِيلِ ٱللَّهِ لَهُمۡ عَذَابٞ شَدِيدُۢ بِمَا نَسُواْ يَوۡمَ ٱلۡحِسَابِ", "Ya dawoodu inna jaAAalnaka khaleefatan fee alardi faohkum bayna alnnasi bialhaqqi wala tattabiAAi alhawa fayudillaka AAan sabeeli Allahi inna allatheena yadilloona AAan sabeeli Allahi lahum AAathabun shadeedun bima nasoo yawma alhisabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 27, "And We did not create the heaven and the earth and that between them aimlessly. That is the assumption of those who disbelieve, so woe to those who disbelieve from the Fire", "وَمَا خَلَقۡنَا ٱلسَّمَآءَ وَٱلۡأَرۡضَ وَمَا بَيۡنَهُمَا بَٰطِلٗاۚ ذَٰلِكَ ظَنُّ ٱلَّذِينَ كَفَرُواْۚ فَوَيۡلٞ لِّلَّذِينَ كَفَرُواْ مِنَ ٱلنَّارِ", "Wama khalaqna alssamaa waalarda wama baynahuma batilan thalika thannu allatheena kafaroo fawaylun lillatheena kafaroo mina alnnari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 28, "Or should we treat those who believe and do righteous deeds like corrupters in the land? Or should We treat those who fear Allah like the wicked", "أَمۡ نَجۡعَلُ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ كَٱلۡمُفۡسِدِينَ فِي ٱلۡأَرۡضِ أَمۡ نَجۡعَلُ ٱلۡمُتَّقِينَ كَٱلۡفُجَّارِ", "Am najAAalu allatheena amanoo waAAamiloo alssalihati kaalmufsideena fee alardi am najAAalu almuttaqeena kaalfujjari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 29, "[This is] a blessed Book which We have revealed to you, [O Muhammad], that they might reflect upon its verses and that those of understanding would be reminded", "كِتَٰبٌ أَنزَلۡنَٰهُ إِلَيۡكَ مُبَٰرَكٞ لِّيَدَّبَّرُوٓاْ ءَايَٰتِهِۦ وَلِيَتَذَكَّرَ أُوْلُواْ ٱلۡأَلۡبَٰبِ", "Kitabun anzalnahu ilayka mubarakun liyaddabbaroo ayatihi waliyatathakkara oloo alalbabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 30, "And to David We gave Solomon. An excellent servant, indeed he was one repeatedly turning back [to Allah]", "وَوَهَبۡنَا لِدَاوُۥدَ سُلَيۡمَٰنَۚ نِعۡمَ ٱلۡعَبۡدُ إِنَّهُۥٓ أَوَّابٌ", "Wawahabna lidawooda sulaymana niAAma alAAabdu innahu awwabun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 31, "[Mention] when there were exhibited before him in the afternoon the poised [standing] racehorses", "إِذۡ عُرِضَ عَلَيۡهِ بِٱلۡعَشِيِّ ٱلصَّـٰفِنَٰتُ ٱلۡجِيَادُ", "Ith AAurida AAalayhi bialAAashiyyi alssafinatu aljiyadu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 32, "And he said, ""Indeed, I gave preference to the love of good [things] over the remembrance of my Lord until the sun disappeared into the curtain [of darkness]", "فَقَالَ إِنِّيٓ أَحۡبَبۡتُ حُبَّ ٱلۡخَيۡرِ عَن ذِكۡرِ رَبِّي حَتَّىٰ تَوَارَتۡ بِٱلۡحِجَابِ", "Faqala innee ahbabtu hubba alkhayri AAan thikri rabbee hatta tawarat bialhijabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 33, "[He said], ""Return them to me,"" and set about striking [their] legs and necks", "رُدُّوهَا عَلَيَّۖ فَطَفِقَ مَسۡحَۢا بِٱلسُّوقِ وَٱلۡأَعۡنَاقِ", "Ruddooha AAalayya fatafiqa mashan bialssooqi waalaAAnaqi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 34, "And We certainly tried Solomon and placed on his throne a body; then he returned", "وَلَقَدۡ فَتَنَّا سُلَيۡمَٰنَ وَأَلۡقَيۡنَا عَلَىٰ كُرۡسِيِّهِۦ جَسَدٗا ثُمَّ أَنَابَ", "Walaqad fatanna sulaymana waalqayna AAala kursiyyihi jasadan thumma anaba");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 35, "He said, ""My Lord, forgive me and grant me a kingdom such as will not belong to anyone after me. Indeed, You are the Bestower", "قَالَ رَبِّ ٱغۡفِرۡ لِي وَهَبۡ لِي مُلۡكٗا لَّا يَنۢبَغِي لِأَحَدٖ مِّنۢ بَعۡدِيٓۖ إِنَّكَ أَنتَ ٱلۡوَهَّابُ", "Qala rabbi ighfir lee wahab lee mulkan la yanbaghee liahadin min baAAdee innaka anta alwahhabu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 36, "So We subjected to him the wind blowing by his command, gently, wherever he directed", "فَسَخَّرۡنَا لَهُ ٱلرِّيحَ تَجۡرِي بِأَمۡرِهِۦ رُخَآءً حَيۡثُ أَصَابَ", "Fasakhkharna lahu alrreeha tajree biamrihi rukhaan haythu asaba");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 37, "And [also] the devils [of jinn] - every builder and diver", "وَٱلشَّيَٰطِينَ كُلَّ بَنَّآءٖ وَغَوَّاصٖ", "Waalshshayateena kulla bannain waghawwasin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 38, "And others bound together in shackles", "وَءَاخَرِينَ مُقَرَّنِينَ فِي ٱلۡأَصۡفَادِ", "Waakhareena muqarraneena fee alasfadi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 39, "[We said], ""This is Our gift, so grant or withhold without account", "هَٰذَا عَطَآؤُنَا فَٱمۡنُنۡ أَوۡ أَمۡسِكۡ بِغَيۡرِ حِسَابٖ", "Hatha AAataona faomnun aw amsik bighayri hisabin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 40, "And indeed, for him is nearness to Us and a good place of return", "وَإِنَّ لَهُۥ عِندَنَا لَزُلۡفَىٰ وَحُسۡنَ مَـَٔابٖ", "Wainna lahu AAindana lazulfa wahusna maabin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 41, "And remember Our servant Job, when he called to his Lord, ""Indeed, Satan has touched me with hardship and torment", "وَٱذۡكُرۡ عَبۡدَنَآ أَيُّوبَ إِذۡ نَادَىٰ رَبَّهُۥٓ أَنِّي مَسَّنِيَ ٱلشَّيۡطَٰنُ بِنُصۡبٖ وَعَذَابٍ", "Waothkur AAabdana ayyooba ith nada rabbahu annee massaniya alshshaytanu binusbin waAAathabin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 42, "[So he was told], ""Strike [the ground] with your foot; this is a [spring for] a cool bath and drink", "ٱرۡكُضۡ بِرِجۡلِكَۖ هَٰذَا مُغۡتَسَلُۢ بَارِدٞ وَشَرَابٞ", "Orkud birijlika hatha mughtasalun baridun washarabun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 43, "And We granted him his family and a like [number] with them as mercy from Us and a reminder for those of understanding", "وَوَهَبۡنَا لَهُۥٓ أَهۡلَهُۥ وَمِثۡلَهُم مَّعَهُمۡ رَحۡمَةٗ مِّنَّا وَذِكۡرَىٰ لِأُوْلِي ٱلۡأَلۡبَٰبِ", "Wawahabna lahu ahlahu wamithlahum maAAahum rahmatan minna wathikra liolee alalbabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 44, "[We said], ""And take in your hand a bunch [of grass] and strike with it and do not break your oath."" Indeed, We found him patient, an excellent servant. Indeed, he was one repeatedly turning back [to Allah]", "وَخُذۡ بِيَدِكَ ضِغۡثٗا فَٱضۡرِب بِّهِۦ وَلَا تَحۡنَثۡۗ إِنَّا وَجَدۡنَٰهُ صَابِرٗاۚ نِّعۡمَ ٱلۡعَبۡدُ إِنَّهُۥٓ أَوَّابٞ", "Wakhuth biyadika dighthan faidrib bihi wala tahnath inna wajadnahu sabiran niAAma alAAabdu innahu awwabun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 45, "And remember Our servants, Abraham, Isaac and Jacob - those of strength and [religious] vision", "وَٱذۡكُرۡ عِبَٰدَنَآ إِبۡرَٰهِيمَ وَإِسۡحَٰقَ وَيَعۡقُوبَ أُوْلِي ٱلۡأَيۡدِي وَٱلۡأَبۡصَٰرِ", "Waothkur AAibadana ibraheema waishaqa wayaAAqooba olee alaydee waalabsari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 46, "Indeed, We chose them for an exclusive quality: remembrance of the home [of the Hereafter]", "إِنَّآ أَخۡلَصۡنَٰهُم بِخَالِصَةٖ ذِكۡرَى ٱلدَّارِ", "Inna akhlasnahum bikhalisatin thikra alddari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 47, "And indeed they are, to Us, among the chosen and outstanding", "وَإِنَّهُمۡ عِندَنَا لَمِنَ ٱلۡمُصۡطَفَيۡنَ ٱلۡأَخۡيَارِ", "Wainnahum AAindana lamina almustafayna alakhyari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 48, "And remember Ishmael, Elisha and Dhul-Kifl, and all are among the outstanding", "وَٱذۡكُرۡ إِسۡمَٰعِيلَ وَٱلۡيَسَعَ وَذَا ٱلۡكِفۡلِۖ وَكُلّٞ مِّنَ ٱلۡأَخۡيَارِ", "Waothkur ismaAAeela wailyasaAAa watha alkifli wakullun mina alakhyari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 49, "This is a reminder. And indeed, for the righteous is a good place of return", "هَٰذَا ذِكۡرٞۚ وَإِنَّ لِلۡمُتَّقِينَ لَحُسۡنَ مَـَٔابٖ", "Hatha thikrun wainna lilmuttaqeena lahusna maabin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 50, "Gardens of perpetual residence, whose doors will be opened to them", "جَنَّـٰتِ عَدۡنٖ مُّفَتَّحَةٗ لَّهُمُ ٱلۡأَبۡوَٰبُ", "Jannati AAadnin mufattahatan lahumu alabwabu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 51, "Reclining within them, they will call therein for abundant fruit and drink", "مُتَّكِـِٔينَ فِيهَا يَدۡعُونَ فِيهَا بِفَٰكِهَةٖ كَثِيرَةٖ وَشَرَابٖ", "Muttakieena feeha yadAAoona feeha bifakihatin katheeratin washarabin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 52, "And with them will be women limiting [their] glances and of equal age", "۞وَعِندَهُمۡ قَٰصِرَٰتُ ٱلطَّرۡفِ أَتۡرَابٌ", "WaAAindahum qasiratu alttarfi atrabun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 53, "This is what you, [the righteous], are promised for the Day of Account", "هَٰذَا مَا تُوعَدُونَ لِيَوۡمِ ٱلۡحِسَابِ", "Hatha ma tooAAadoona liyawmi alhisabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 54, "Indeed, this is Our provision; for it there is no depletion", "إِنَّ هَٰذَا لَرِزۡقُنَا مَا لَهُۥ مِن نَّفَادٍ", "Inna hatha larizquna ma lahu min nafadin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 55, "This [is so]. But indeed, for the transgressors is an evil place of return", "هَٰذَاۚ وَإِنَّ لِلطَّـٰغِينَ لَشَرَّ مَـَٔابٖ", "Hatha wainna lilttagheena lasharra maabin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 56, "Hell, which they will [enter to] burn, and wretched is the resting place", "جَهَنَّمَ يَصۡلَوۡنَهَا فَبِئۡسَ ٱلۡمِهَادُ", "Jahannama yaslawnaha fabisa almihadu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 57, "This - so let them taste it - is scalding water and [foul] purulence", "هَٰذَا فَلۡيَذُوقُوهُ حَمِيمٞ وَغَسَّاقٞ", "Hatha falyathooqoohu hameemun waghassaqun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 58, "And other [punishments] of its type [in various] kinds", "وَءَاخَرُ مِن شَكۡلِهِۦٓ أَزۡوَٰجٌ", "Waakharu min shaklihi azwajun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 59, "[Its inhabitants will say], ""This is a company bursting in with you. No welcome for them. Indeed, they will burn in the Fire", "هَٰذَا فَوۡجٞ مُّقۡتَحِمٞ مَّعَكُمۡ لَا مَرۡحَبَۢا بِهِمۡۚ إِنَّهُمۡ صَالُواْ ٱلنَّارِ", "Hatha fawjun muqtahimun maAAakum la marhaban bihim innahum saloo alnnari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 60, "They will say, ""Nor you! No welcome for you. You, [our leaders], brought this upon us, and wretched is the settlement", "قَالُواْ بَلۡ أَنتُمۡ لَا مَرۡحَبَۢا بِكُمۡۖ أَنتُمۡ قَدَّمۡتُمُوهُ لَنَاۖ فَبِئۡسَ ٱلۡقَرَارُ", "Qaloo bal antum la marhaban bikum antum qaddamtumoohu lana fabisa alqararu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 61, "They will say, ""Our Lord, whoever brought this upon us - increase for him double punishment in the Fire", "قَالُواْ رَبَّنَا مَن قَدَّمَ لَنَا هَٰذَا فَزِدۡهُ عَذَابٗا ضِعۡفٗا فِي ٱلنَّارِ", "Qaloo rabbana man qaddama lana hatha fazidhu AAathaban diAAfan fee alnnari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 62, "And they will say, ""Why do we not see men whom we used to count among the worst", "وَقَالُواْ مَا لَنَا لَا نَرَىٰ رِجَالٗا كُنَّا نَعُدُّهُم مِّنَ ٱلۡأَشۡرَارِ", "Waqaloo ma lana la nara rijalan kunna naAAudduhum mina alashrari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 63, "Is it [because] we took them in ridicule, or has [our] vision turned away from them", "أَتَّخَذۡنَٰهُمۡ سِخۡرِيًّا أَمۡ زَاغَتۡ عَنۡهُمُ ٱلۡأَبۡصَٰرُ", "Attakhathnahum sikhriyyan am zaghat AAanhumu alabsaru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 64, "Indeed, that is truth - the quarreling of the people of the Fire", "إِنَّ ذَٰلِكَ لَحَقّٞ تَخَاصُمُ أَهۡلِ ٱلنَّارِ", "Inna thalika lahaqqun takhasumu ahli alnnari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 65, "Say, [O Muhammad], ""I am only a warner, and there is not any deity except Allah, the One, the Prevailing", "قُلۡ إِنَّمَآ أَنَا۠ مُنذِرٞۖ وَمَا مِنۡ إِلَٰهٍ إِلَّا ٱللَّهُ ٱلۡوَٰحِدُ ٱلۡقَهَّارُ", "Qul innama ana munthirun wama min ilahin illa Allahu alwahidu alqahharu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 66, "Lord of the heavens and the earth and whatever is between them, the Exalted in Might, the Perpetual Forgiver", "رَبُّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا بَيۡنَهُمَا ٱلۡعَزِيزُ ٱلۡغَفَّـٰرُ", "Rabbu alssamawati waalardi wama baynahuma alAAazeezu alghaffaru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 67, "Say, ""It is great news", "قُلۡ هُوَ نَبَؤٌاْ عَظِيمٌ", "Qul huwa nabaon AAatheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 68, "From which you turn away", "أَنتُمۡ عَنۡهُ مُعۡرِضُونَ", "Antum AAanhu muAAridoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 69, "I had no knowledge of the exalted assembly [of angels] when they were disputing [the creation of Adam]", "مَا كَانَ لِيَ مِنۡ عِلۡمِۭ بِٱلۡمَلَإِ ٱلۡأَعۡلَىٰٓ إِذۡ يَخۡتَصِمُونَ", "Ma kana liya min AAilmin bialmalai alaAAla ith yakhtasimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 70, "It has not been revealed to me except that I am a clear warner", "إِن يُوحَىٰٓ إِلَيَّ إِلَّآ أَنَّمَآ أَنَا۠ نَذِيرٞ مُّبِينٌ", "In yooha ilayya illa annama ana natheerun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 71, "[So mention] when your Lord said to the angels, ""Indeed, I am going to create a human being from clay", "إِذۡ قَالَ رَبُّكَ لِلۡمَلَـٰٓئِكَةِ إِنِّي خَٰلِقُۢ بَشَرٗا مِّن طِينٖ", "Ith qala rabbuka lilmalaikati innee khaliqun basharan min teenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 72, "So when I have proportioned him and breathed into him of My [created] soul, then fall down to him in prostration", "فَإِذَا سَوَّيۡتُهُۥ وَنَفَخۡتُ فِيهِ مِن رُّوحِي فَقَعُواْ لَهُۥ سَٰجِدِينَ", "Faitha sawwaytuhu wanafakhtu feehi min roohee faqaAAoo lahu sajideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 73, "So the angels prostrated - all of them entirely", "فَسَجَدَ ٱلۡمَلَـٰٓئِكَةُ كُلُّهُمۡ أَجۡمَعُونَ", "Fasajada almalaikatu kulluhum ajmaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 74, "Except Iblees; he was arrogant and became among the disbelievers", "إِلَّآ إِبۡلِيسَ ٱسۡتَكۡبَرَ وَكَانَ مِنَ ٱلۡكَٰفِرِينَ", "Illa ibleesa istakbara wakana mina alkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 75, "[Allah] said, ""O Iblees, what prevented you from prostrating to that which I created with My hands? Were you arrogant [then], or were you [already] among the haughty", "قَالَ يَـٰٓإِبۡلِيسُ مَا مَنَعَكَ أَن تَسۡجُدَ لِمَا خَلَقۡتُ بِيَدَيَّۖ أَسۡتَكۡبَرۡتَ أَمۡ كُنتَ مِنَ ٱلۡعَالِينَ", "Qala ya ibleesu ma manaAAaka an tasjuda lima khalaqtu biyadayya astakbarta am kunta mina alAAaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 76, "He said, ""I am better than him. You created me from fire and created him from clay", "قَالَ أَنَا۠ خَيۡرٞ مِّنۡهُ خَلَقۡتَنِي مِن نَّارٖ وَخَلَقۡتَهُۥ مِن طِينٖ", "Qala ana khayrun minhu khalaqtanee min narin wakhalaqtahu min teenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 77, "[Allah] said, ""Then get out of Paradise, for indeed, you are expelled", "قَالَ فَٱخۡرُجۡ مِنۡهَا فَإِنَّكَ رَجِيمٞ", "Qala faokhruj minha fainnaka rajeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 78, "And indeed, upon you is My curse until the Day of Recompense", "وَإِنَّ عَلَيۡكَ لَعۡنَتِيٓ إِلَىٰ يَوۡمِ ٱلدِّينِ", "Wainna AAalayka laAAnatee ila yawmi alddeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 79, "He said, ""My Lord, then reprieve me until the Day they are resurrected", "قَالَ رَبِّ فَأَنظِرۡنِيٓ إِلَىٰ يَوۡمِ يُبۡعَثُونَ", "Qala rabbi faanthirnee ila yawmi yubAAathoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 80, "[Allah] said, ""So indeed, you are of those reprieved", "قَالَ فَإِنَّكَ مِنَ ٱلۡمُنظَرِينَ", "Qala fainnaka mina almunthareena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 81, "Until the Day of the time well-known", "إِلَىٰ يَوۡمِ ٱلۡوَقۡتِ ٱلۡمَعۡلُومِ", "Ila yawmi alwaqti almaAAloomi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 82, "[Iblees] said, ""By your might, I will surely mislead them all", "قَالَ فَبِعِزَّتِكَ لَأُغۡوِيَنَّهُمۡ أَجۡمَعِينَ", "Qala fabiAAizzatika laoghwiyannahum ajmaAAeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 83, "Except, among them, Your chosen servants", "إِلَّا عِبَادَكَ مِنۡهُمُ ٱلۡمُخۡلَصِينَ", "Illa AAibadaka minhumu almukhlaseena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 84, "[Allah] said, ""The truth [is My oath], and the truth I say", "قَالَ فَٱلۡحَقُّ وَٱلۡحَقَّ أَقُولُ", "Qala faalhaqqu waalhaqqa aqoolu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 85, "[That] I will surely fill Hell with you and those of them that follow you all together", "لَأَمۡلَأَنَّ جَهَنَّمَ مِنكَ وَمِمَّن تَبِعَكَ مِنۡهُمۡ أَجۡمَعِينَ", "Laamlaanna jahannama minka wamimman tabiAAaka minhum ajmaAAeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 86, "Say, [O Muhammad], ""I do not ask you for the Qur'an any payment, and I am not of the pretentious", "قُلۡ مَآ أَسۡـَٔلُكُمۡ عَلَيۡهِ مِنۡ أَجۡرٖ وَمَآ أَنَا۠ مِنَ ٱلۡمُتَكَلِّفِينَ", "Qul ma asalukum AAalayhi min ajrin wama ana mina almutakallifeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 87, "It is but a reminder to the worlds", "إِنۡ هُوَ إِلَّا ذِكۡرٞ لِّلۡعَٰلَمِينَ", "In huwa illa thikrun lilAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (38, 88, "And you will surely know [the truth of] its information after a time", "وَلَتَعۡلَمُنَّ نَبَأَهُۥ بَعۡدَ حِينِۭ", "WalataAAlamunna nabaahu baAAda heenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 1, "The revelation of the Qur'an is from Allah, the Exalted in Might, the Wise", "تَنزِيلُ ٱلۡكِتَٰبِ مِنَ ٱللَّهِ ٱلۡعَزِيزِ ٱلۡحَكِيمِ", "Tanzeelu alkitabi mina Allahi alAAazeezi alhakeemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 2, "Indeed, We have sent down to you the Book, [O Muhammad], in truth. So worship Allah, [being] sincere to Him in religion", "إِنَّآ أَنزَلۡنَآ إِلَيۡكَ ٱلۡكِتَٰبَ بِٱلۡحَقِّ فَٱعۡبُدِ ٱللَّهَ مُخۡلِصٗا لَّهُ ٱلدِّينَ", "Inna anzalna ilayka alkitaba bialhaqqi faoAAbudi Allaha mukhlisan lahu alddeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 3, "Unquestionably, for Allah is the pure religion. And those who take protectors besides Him [say], ""We only worship them that they may bring us nearer to Allah in position."" Indeed, Allah will judge between them concerning that over which they differ. Indeed, Allah does not guide he who is a liar and [confirmed] disbeliever", "أَلَا لِلَّهِ ٱلدِّينُ ٱلۡخَالِصُۚ وَٱلَّذِينَ ٱتَّخَذُواْ مِن دُونِهِۦٓ أَوۡلِيَآءَ مَا نَعۡبُدُهُمۡ إِلَّا لِيُقَرِّبُونَآ إِلَى ٱللَّهِ زُلۡفَىٰٓ إِنَّ ٱللَّهَ يَحۡكُمُ بَيۡنَهُمۡ فِي مَا هُمۡ فِيهِ يَخۡتَلِفُونَۗ إِنَّ ٱللَّهَ لَا يَهۡدِي مَنۡ هُوَ كَٰذِبٞ كَفَّارٞ", "Ala lillahi alddeenu alkhalisu waallatheena ittakhathoo min doonihi awliyaa ma naAAbuduhum illa liyuqarriboona ila Allahi zulfa inna Allaha yahkumu baynahum fee ma hum feehi yakhtalifoona inna Allaha la yahdee man huwa kathibun kaffarun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 4, "If Allah had intended to take a son, He could have chosen from what He creates whatever He willed. Exalted is He; He is Allah, the One, the Prevailing", "لَّوۡ أَرَادَ ٱللَّهُ أَن يَتَّخِذَ وَلَدٗا لَّٱصۡطَفَىٰ مِمَّا يَخۡلُقُ مَا يَشَآءُۚ سُبۡحَٰنَهُۥۖ هُوَ ٱللَّهُ ٱلۡوَٰحِدُ ٱلۡقَهَّارُ", "Law arada Allahu an yattakhitha waladan laistafa mimma yakhluqu ma yashao subhanahu huwa Allahu alwahidu alqahharu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 5, "He created the heavens and earth in truth. He wraps the night over the day and wraps the day over the night and has subjected the sun and the moon, each running [its course] for a specified term. Unquestionably, He is the Exalted in Might, the Perpetual Forgiver", "خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ بِٱلۡحَقِّۖ يُكَوِّرُ ٱلَّيۡلَ عَلَى ٱلنَّهَارِ وَيُكَوِّرُ ٱلنَّهَارَ عَلَى ٱلَّيۡلِۖ وَسَخَّرَ ٱلشَّمۡسَ وَٱلۡقَمَرَۖ كُلّٞ يَجۡرِي لِأَجَلٖ مُّسَمًّىۗ أَلَا هُوَ ٱلۡعَزِيزُ ٱلۡغَفَّـٰرُ", "Khalaqa alssamawati waalarda bialhaqqi yukawwiru allayla AAala alnnahari wayukawwiru alnnahara AAala allayli wasakhkhara alshshamsa waalqamara kullun yajree liajalin musamman ala huwa alAAazeezu alghaffaru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 6, "He created you from one soul. Then He made from it its mate, and He produced for you from the grazing livestock eight mates. He creates you in the wombs of your mothers, creation after creation, within three darknesses. That is Allah, your Lord; to Him belongs dominion. There is no deity except Him, so how are you averted", "خَلَقَكُم مِّن نَّفۡسٖ وَٰحِدَةٖ ثُمَّ جَعَلَ مِنۡهَا زَوۡجَهَا وَأَنزَلَ لَكُم مِّنَ ٱلۡأَنۡعَٰمِ ثَمَٰنِيَةَ أَزۡوَٰجٖۚ يَخۡلُقُكُمۡ فِي بُطُونِ أُمَّهَٰتِكُمۡ خَلۡقٗا مِّنۢ بَعۡدِ خَلۡقٖ فِي ظُلُمَٰتٖ ثَلَٰثٖۚ ذَٰلِكُمُ ٱللَّهُ رَبُّكُمۡ لَهُ ٱلۡمُلۡكُۖ لَآ إِلَٰهَ إِلَّا هُوَۖ فَأَنَّىٰ تُصۡرَفُونَ", "Khalaqakum min nafsin wahidatin thumma jaAAala minha zawjaha waanzala lakum mina alanAAami thamaniyata azwajin yakhluqukum fee butooni ommahatikum khalqan min baAAdi khalqin fee thulumatin thalathin thalikumu Allahu rabbukum lahu almulku la ilaha illa huwa faanna tusrafoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 7, "If you disbelieve - indeed, Allah is Free from need of you. And He does not approve for His servants disbelief. And if you are grateful, He approves it for you; and no bearer of burdens will bear the burden of another. Then to your Lord is your return, and He will inform you about what you used to do. Indeed, He is Knowing of that within the breasts", "إِن تَكۡفُرُواْ فَإِنَّ ٱللَّهَ غَنِيٌّ عَنكُمۡۖ وَلَا يَرۡضَىٰ لِعِبَادِهِ ٱلۡكُفۡرَۖ وَإِن تَشۡكُرُواْ يَرۡضَهُ لَكُمۡۗ وَلَا تَزِرُ وَازِرَةٞ وِزۡرَ أُخۡرَىٰۚ ثُمَّ إِلَىٰ رَبِّكُم مَّرۡجِعُكُمۡ فَيُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَۚ إِنَّهُۥ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ", "In takfuroo fainna Allaha ghaniyyun AAankum wala yarda liAAibadihi alkufra wain tashkuroo yardahu lakum wala taziru waziratun wizra okhra thumma ila rabbikum marjiAAukum fayunabbiokum bima kuntum taAAmaloona innahu AAaleemun bithati alssudoori");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 8, "And when adversity touches man, he calls upon his Lord, turning to Him [alone]; then when He bestows on him a favor from Himself, he forgets Him whom he called upon before, and he attributes to Allah equals to mislead [people] from His way. Say, ""Enjoy your disbelief for a little; indeed, you are of the companions of the Fire", "۞وَإِذَا مَسَّ ٱلۡإِنسَٰنَ ضُرّٞ دَعَا رَبَّهُۥ مُنِيبًا إِلَيۡهِ ثُمَّ إِذَا خَوَّلَهُۥ نِعۡمَةٗ مِّنۡهُ نَسِيَ مَا كَانَ يَدۡعُوٓاْ إِلَيۡهِ مِن قَبۡلُ وَجَعَلَ لِلَّهِ أَندَادٗا لِّيُضِلَّ عَن سَبِيلِهِۦۚ قُلۡ تَمَتَّعۡ بِكُفۡرِكَ قَلِيلًا إِنَّكَ مِنۡ أَصۡحَٰبِ ٱلنَّارِ", "Waitha massa alinsana durrun daAAa rabbahu muneeban ilayhi thumma itha khawwalahu niAAmatan minhu nasiya ma kana yadAAoo ilayhi min qablu wajaAAala lillahi andadan liyudilla AAan sabeelihi qul tamattaAA bikufrika qaleelan innaka min ashabi alnnari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 9, "Is one who is devoutly obedient during periods of the night, prostrating and standing [in prayer], fearing the Hereafter and hoping for the mercy of his Lord, [like one who does not]? Say, ""Are those who know equal to those who do not know?"" Only they will remember [who are] people of understanding", "أَمَّنۡ هُوَ قَٰنِتٌ ءَانَآءَ ٱلَّيۡلِ سَاجِدٗا وَقَآئِمٗا يَحۡذَرُ ٱلۡأٓخِرَةَ وَيَرۡجُواْ رَحۡمَةَ رَبِّهِۦۗ قُلۡ هَلۡ يَسۡتَوِي ٱلَّذِينَ يَعۡلَمُونَ وَٱلَّذِينَ لَا يَعۡلَمُونَۗ إِنَّمَا يَتَذَكَّرُ أُوْلُواْ ٱلۡأَلۡبَٰبِ", "Amman huwa qanitun anaa allayli sajidan waqaiman yahtharu alakhirata wayarjoo rahmata rabbihi qul hal yastawee allatheena yaAAlamoona waallatheena la yaAAlamoona innama yatathakkaru oloo alalbabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 10, "Say, ""O My servants who have believed, fear your Lord. For those who do good in this world is good, and the earth of Allah is spacious. Indeed, the patient will be given their reward without account", "قُلۡ يَٰعِبَادِ ٱلَّذِينَ ءَامَنُواْ ٱتَّقُواْ رَبَّكُمۡۚ لِلَّذِينَ أَحۡسَنُواْ فِي هَٰذِهِ ٱلدُّنۡيَا حَسَنَةٞۗ وَأَرۡضُ ٱللَّهِ وَٰسِعَةٌۗ إِنَّمَا يُوَفَّى ٱلصَّـٰبِرُونَ أَجۡرَهُم بِغَيۡرِ حِسَابٖ", "Qul ya AAibadi allatheena amanoo ittaqoo rabbakum lillatheena ahsanoo fee hathihi alddunya hasanatun waardu Allahi wasiAAatun innama yuwaffa alssabiroona ajrahum bighayri hisabin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 11, "Say, [O Muhammad], ""Indeed, I have been commanded to worship Allah, [being] sincere to Him in religion", "قُلۡ إِنِّيٓ أُمِرۡتُ أَنۡ أَعۡبُدَ ٱللَّهَ مُخۡلِصٗا لَّهُ ٱلدِّينَ", "Qul innee omirtu an aAAbuda Allaha mukhlisan lahu alddeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 12, "And I have been commanded to be the first [among you] of the Muslims", "وَأُمِرۡتُ لِأَنۡ أَكُونَ أَوَّلَ ٱلۡمُسۡلِمِينَ", "Waomirtu lian akoona awwala almuslimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 13, "Say, ""Indeed I fear, if I should disobey my Lord, the punishment of a tremendous Day", "قُلۡ إِنِّيٓ أَخَافُ إِنۡ عَصَيۡتُ رَبِّي عَذَابَ يَوۡمٍ عَظِيمٖ", "Qul innee akhafu in AAasaytu rabbee AAathaba yawmin AAatheemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 14, "Say, ""Allah [alone] do I worship, sincere to Him in my religion", "قُلِ ٱللَّهَ أَعۡبُدُ مُخۡلِصٗا لَّهُۥ دِينِي", "Quli Allaha aAAbudu mukhlisan lahu deenee");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 15, "So worship what you will besides Him."" Say, ""Indeed, the losers are the ones who will lose themselves and their families on the Day of Resurrection. Unquestionably, that is the manifest loss", "فَٱعۡبُدُواْ مَا شِئۡتُم مِّن دُونِهِۦۗ قُلۡ إِنَّ ٱلۡخَٰسِرِينَ ٱلَّذِينَ خَسِرُوٓاْ أَنفُسَهُمۡ وَأَهۡلِيهِمۡ يَوۡمَ ٱلۡقِيَٰمَةِۗ أَلَا ذَٰلِكَ هُوَ ٱلۡخُسۡرَانُ ٱلۡمُبِينُ", "FaoAAbudoo ma shitum min doonihi qul inna alkhasireena allatheena khasiroo anfusahum waahleehim yawma alqiyamati ala thalika huwa alkhusranu almubeenu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 16, "They will have canopies of fire above them and below them, canopies. By that Allah threatens His servants. O My servants, then fear Me", "لَهُم مِّن فَوۡقِهِمۡ ظُلَلٞ مِّنَ ٱلنَّارِ وَمِن تَحۡتِهِمۡ ظُلَلٞۚ ذَٰلِكَ يُخَوِّفُ ٱللَّهُ بِهِۦ عِبَادَهُۥۚ يَٰعِبَادِ فَٱتَّقُونِ", "Lahum min fawqihim thulalun mina alnnari wamin tahtihim thulalun thalika yukhawwifu Allahu bihi AAibadahu ya AAibadi faittaqooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 17, "But those who have avoided Taghut, lest they worship it, and turned back to Allah - for them are good tidings. So give good tidings to My servants", "وَٱلَّذِينَ ٱجۡتَنَبُواْ ٱلطَّـٰغُوتَ أَن يَعۡبُدُوهَا وَأَنَابُوٓاْ إِلَى ٱللَّهِ لَهُمُ ٱلۡبُشۡرَىٰۚ فَبَشِّرۡ عِبَادِ", "Waallatheena ijtanaboo alttaghoota an yaAAbudooha waanaboo ila Allahi lahumu albushra fabashshir AAibadi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 18, "Who listen to speech and follow the best of it. Those are the ones Allah has guided, and those are people of understanding", "ٱلَّذِينَ يَسۡتَمِعُونَ ٱلۡقَوۡلَ فَيَتَّبِعُونَ أَحۡسَنَهُۥٓۚ أُوْلَـٰٓئِكَ ٱلَّذِينَ هَدَىٰهُمُ ٱللَّهُۖ وَأُوْلَـٰٓئِكَ هُمۡ أُوْلُواْ ٱلۡأَلۡبَٰبِ", "Allatheena yastamiAAoona alqawla fayattabiAAoona ahsanahu olaika allatheena hadahumu Allahu waolaika hum oloo alalbabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 19, "Then, is one who has deserved the decree of punishment [to be guided]? Then, can you save one who is in the Fire", "أَفَمَنۡ حَقَّ عَلَيۡهِ كَلِمَةُ ٱلۡعَذَابِ أَفَأَنتَ تُنقِذُ مَن فِي ٱلنَّارِ", "Afaman haqqa AAalayhi kalimatu alAAathabi afaanta tunqithu man fee alnnari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 20, "But those who have feared their Lord - for them are chambers, above them chambers built high, beneath which rivers flow. [This is] the promise of Allah. Allah does not fail in [His] promise", "لَٰكِنِ ٱلَّذِينَ ٱتَّقَوۡاْ رَبَّهُمۡ لَهُمۡ غُرَفٞ مِّن فَوۡقِهَا غُرَفٞ مَّبۡنِيَّةٞ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُۖ وَعۡدَ ٱللَّهِ لَا يُخۡلِفُ ٱللَّهُ ٱلۡمِيعَادَ", "Lakini allatheena ittaqaw rabbahum lahum ghurafun min fawqiha ghurafun mabniyyatun tajree min tahtiha alanharu waAAda Allahi la yukhlifu Allahu almeeAAada");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 21, "Do you not see that Allah sends down rain from the sky and makes it flow as springs [and rivers] in the earth; then He produces thereby crops of varying colors; then they dry and you see them turned yellow; then He makes them [scattered] debris. Indeed in that is a reminder for those of understanding", "أَلَمۡ تَرَ أَنَّ ٱللَّهَ أَنزَلَ مِنَ ٱلسَّمَآءِ مَآءٗ فَسَلَكَهُۥ يَنَٰبِيعَ فِي ٱلۡأَرۡضِ ثُمَّ يُخۡرِجُ بِهِۦ زَرۡعٗا مُّخۡتَلِفًا أَلۡوَٰنُهُۥ ثُمَّ يَهِيجُ فَتَرَىٰهُ مُصۡفَرّٗا ثُمَّ يَجۡعَلُهُۥ حُطَٰمًاۚ إِنَّ فِي ذَٰلِكَ لَذِكۡرَىٰ لِأُوْلِي ٱلۡأَلۡبَٰبِ", "Alam tara anna Allaha anzala mina alssamai maan fasalakahu yanabeeAAa fee alardi thumma yukhriju bihi zarAAan mukhtalifan alwanuhu thumma yaheeju fatarahu musfarran thumma yajAAaluhu hutaman inna fee thalika lathikra liolee alalbabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 22, "So is one whose breast Allah has expanded to [accept] Islam and he is upon a light from his Lord [like one whose heart rejects it]? Then woe to those whose hearts are hardened against the remembrance of Allah. Those are in manifest error", "أَفَمَن شَرَحَ ٱللَّهُ صَدۡرَهُۥ لِلۡإِسۡلَٰمِ فَهُوَ عَلَىٰ نُورٖ مِّن رَّبِّهِۦۚ فَوَيۡلٞ لِّلۡقَٰسِيَةِ قُلُوبُهُم مِّن ذِكۡرِ ٱللَّهِۚ أُوْلَـٰٓئِكَ فِي ضَلَٰلٖ مُّبِينٍ", "Afaman sharaha Allahu sadrahu lilislami fahuwa AAala noorin min rabbihi fawaylun lilqasiyati quloobuhum min thikri Allahi olaika fee dalalin mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 23, "Allah has sent down the best statement: a consistent Book wherein is reiteration. The skins shiver therefrom of those who fear their Lord; then their skins and their hearts relax at the remembrance of Allah. That is the guidance of Allah by which He guides whom He wills. And one whom Allah leaves astray - for him there is no guide", "ٱللَّهُ نَزَّلَ أَحۡسَنَ ٱلۡحَدِيثِ كِتَٰبٗا مُّتَشَٰبِهٗا مَّثَانِيَ تَقۡشَعِرُّ مِنۡهُ جُلُودُ ٱلَّذِينَ يَخۡشَوۡنَ رَبَّهُمۡ ثُمَّ تَلِينُ جُلُودُهُمۡ وَقُلُوبُهُمۡ إِلَىٰ ذِكۡرِ ٱللَّهِۚ ذَٰلِكَ هُدَى ٱللَّهِ يَهۡدِي بِهِۦ مَن يَشَآءُۚ وَمَن يُضۡلِلِ ٱللَّهُ فَمَا لَهُۥ مِنۡ هَادٍ", "Allahu nazzala ahsana alhadeethi kitaban mutashabihan mathaniya taqshaAAirru minhu juloodu allatheena yakhshawna rabbahum thumma taleenu julooduhum waquloobuhum ila thikri Allahi thalika huda Allahi yahdee bihi man yashao waman yudlili Allahu fama lahu min hadin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 24, "Then is he who will shield with his face the worst of the punishment on the Day of Resurrection [like one secure from it]? And it will be said to the wrongdoers, ""Taste what you used to earn", "أَفَمَن يَتَّقِي بِوَجۡهِهِۦ سُوٓءَ ٱلۡعَذَابِ يَوۡمَ ٱلۡقِيَٰمَةِۚ وَقِيلَ لِلظَّـٰلِمِينَ ذُوقُواْ مَا كُنتُمۡ تَكۡسِبُونَ", "Afaman yattaqee biwajhihi sooa alAAathabi yawma alqiyamati waqeela lilththalimeena thooqoo ma kuntum taksiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 25, "Those before them denied, and punishment came upon them from where they did not perceive", "كَذَّبَ ٱلَّذِينَ مِن قَبۡلِهِمۡ فَأَتَىٰهُمُ ٱلۡعَذَابُ مِنۡ حَيۡثُ لَا يَشۡعُرُونَ", "Kaththaba allatheena min qablihim faatahumu alAAathabu min haythu la yashAAuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 26, "So Allah made them taste disgrace in worldly life. But the punishment of the Hereafter is greater, if they only knew", "فَأَذَاقَهُمُ ٱللَّهُ ٱلۡخِزۡيَ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۖ وَلَعَذَابُ ٱلۡأٓخِرَةِ أَكۡبَرُۚ لَوۡ كَانُواْ يَعۡلَمُونَ", "Faathaqahumu Allahu alkhizya fee alhayati alddunya walaAAathabu alakhirati akbaru law kanoo yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 27, "And We have certainly presented for the people in this Qur'an from every [kind of] example - that they might remember", "وَلَقَدۡ ضَرَبۡنَا لِلنَّاسِ فِي هَٰذَا ٱلۡقُرۡءَانِ مِن كُلِّ مَثَلٖ لَّعَلَّهُمۡ يَتَذَكَّرُونَ", "Walaqad darabna lilnnasi fee hatha alqurani min kulli mathalin laAAallahum yatathakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 28, "[It is] an Arabic Qur'an, without any deviance that they might become righteous", "قُرۡءَانًا عَرَبِيًّا غَيۡرَ ذِي عِوَجٖ لَّعَلَّهُمۡ يَتَّقُونَ", "Quranan AAarabiyyan ghayra thee AAiwajin laAAallahum yattaqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 29, "Allah presents an example: a slave owned by quarreling partners and another belonging exclusively to one man - are they equal in comparison? Praise be to Allah! But most of them do not know", "ضَرَبَ ٱللَّهُ مَثَلٗا رَّجُلٗا فِيهِ شُرَكَآءُ مُتَشَٰكِسُونَ وَرَجُلٗا سَلَمٗا لِّرَجُلٍ هَلۡ يَسۡتَوِيَانِ مَثَلًاۚ ٱلۡحَمۡدُ لِلَّهِۚ بَلۡ أَكۡثَرُهُمۡ لَا يَعۡلَمُونَ", "Daraba Allahu mathalan rajulan feehi shurakao mutashakisoona warajulan salaman lirajulin hal yastawiyani mathalan alhamdu lillahi bal aktharuhum la yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 30, "Indeed, you are to die, and indeed, they are to die", "إِنَّكَ مَيِّتٞ وَإِنَّهُم مَّيِّتُونَ", "Innaka mayyitun wainnahum mayyitoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 31, "Then indeed you, on the Day of Resurrection, before your Lord, will dispute", "ثُمَّ إِنَّكُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ عِندَ رَبِّكُمۡ تَخۡتَصِمُونَ", "Thumma innakum yawma alqiyamati AAinda rabbikum takhtasimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 32, "So who is more unjust than one who lies about Allah and denies the truth when it has come to him? Is there not in Hell a residence for the disbelievers", "۞فَمَنۡ أَظۡلَمُ مِمَّن كَذَبَ عَلَى ٱللَّهِ وَكَذَّبَ بِٱلصِّدۡقِ إِذۡ جَآءَهُۥٓۚ أَلَيۡسَ فِي جَهَنَّمَ مَثۡوٗى لِّلۡكَٰفِرِينَ", "Faman athlamu mimman kathaba AAala Allahi wakaththaba bialssidqi ith jaahu alaysa fee jahannama mathwan lilkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 33, "And the one who has brought the truth and [they who] believed in it - those are the righteous", "وَٱلَّذِي جَآءَ بِٱلصِّدۡقِ وَصَدَّقَ بِهِۦٓ أُوْلَـٰٓئِكَ هُمُ ٱلۡمُتَّقُونَ", "Waallathee jaa bialssidqi wasaddaqa bihi olaika humu almuttaqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 34, "They will have whatever they desire with their Lord. That is the reward of the doers of good", "لَهُم مَّا يَشَآءُونَ عِندَ رَبِّهِمۡۚ ذَٰلِكَ جَزَآءُ ٱلۡمُحۡسِنِينَ", "Lahum ma yashaoona AAinda rabbihim thalika jazao almuhsineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 35, "That Allah may remove from them the worst of what they did and reward them their due for the best of what they used to do", "لِيُكَفِّرَ ٱللَّهُ عَنۡهُمۡ أَسۡوَأَ ٱلَّذِي عَمِلُواْ وَيَجۡزِيَهُمۡ أَجۡرَهُم بِأَحۡسَنِ ٱلَّذِي كَانُواْ يَعۡمَلُونَ", "Liyukaffira Allahu AAanhum aswaa allathee AAamiloo wayajziyahum ajrahum biahsani allathee kanoo yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 36, "Is not Allah sufficient for His Servant [Prophet Muhammad]? And [yet], they threaten you with those [they worship] other than Him. And whoever Allah leaves astray - for him there is no guide", "أَلَيۡسَ ٱللَّهُ بِكَافٍ عَبۡدَهُۥۖ وَيُخَوِّفُونَكَ بِٱلَّذِينَ مِن دُونِهِۦۚ وَمَن يُضۡلِلِ ٱللَّهُ فَمَا لَهُۥ مِنۡ هَادٖ", "Alaysa Allahu bikafin AAabdahu wayukhawwifoonaka biallatheena min doonihi waman yudlili Allahu fama lahu min hadin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 37, "And whoever Allah guides - for him there is no misleader. Is not Allah Exalted in Might and Owner of Retribution", "وَمَن يَهۡدِ ٱللَّهُ فَمَا لَهُۥ مِن مُّضِلٍّۗ أَلَيۡسَ ٱللَّهُ بِعَزِيزٖ ذِي ٱنتِقَامٖ", "Waman yahdi Allahu fama lahu min mudillin alaysa Allahu biAAazeezin thee intiqamin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 38, "And if you asked them, ""Who created the heavens and the earth?"" they would surely say, ""Allah."" Say, ""Then have you considered what you invoke besides Allah? If Allah intended me harm, are they removers of His harm; or if He intended me mercy, are they withholders of His mercy?"" Say, ""Sufficient for me is Allah; upon Him [alone] rely the [wise] reliers", "وَلَئِن سَأَلۡتَهُم مَّنۡ خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ لَيَقُولُنَّ ٱللَّهُۚ قُلۡ أَفَرَءَيۡتُم مَّا تَدۡعُونَ مِن دُونِ ٱللَّهِ إِنۡ أَرَادَنِيَ ٱللَّهُ بِضُرٍّ هَلۡ هُنَّ كَٰشِفَٰتُ ضُرِّهِۦٓ أَوۡ أَرَادَنِي بِرَحۡمَةٍ هَلۡ هُنَّ مُمۡسِكَٰتُ رَحۡمَتِهِۦۚ قُلۡ حَسۡبِيَ ٱللَّهُۖ عَلَيۡهِ يَتَوَكَّلُ ٱلۡمُتَوَكِّلُونَ", "Walain saaltahum man khalaqa alssamawati waalarda layaqoolunna Allahu qul afaraaytum ma tadAAoona min dooni Allahi in aradaniya Allahu bidurrin hal hunna kashifatu durrihi aw aradanee birahmatin hal hunna mumsikatu rahmatihi qul hasbiya Allahu AAalayhi yatawakkalu almutawakkiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 39, "Say, ""O my people, work according to your position, [for] indeed, I am working; and you are going to know", "قُلۡ يَٰقَوۡمِ ٱعۡمَلُواْ عَلَىٰ مَكَانَتِكُمۡ إِنِّي عَٰمِلٞۖ فَسَوۡفَ تَعۡلَمُونَ", "Qul ya qawmi iAAmaloo AAala makanatikum innee AAamilun fasawfa taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 40, "To whom will come a torment disgracing him and on whom will descend an enduring punishment", "مَن يَأۡتِيهِ عَذَابٞ يُخۡزِيهِ وَيَحِلُّ عَلَيۡهِ عَذَابٞ مُّقِيمٌ", "Man yateehi AAathabun yukhzeehi wayahillu AAalayhi AAathabun muqeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 41, "Indeed, We sent down to you the Book for the people in truth. So whoever is guided - it is for [the benefit of] his soul; and whoever goes astray only goes astray to its detriment. And you are not a manager over them", "إِنَّآ أَنزَلۡنَا عَلَيۡكَ ٱلۡكِتَٰبَ لِلنَّاسِ بِٱلۡحَقِّۖ فَمَنِ ٱهۡتَدَىٰ فَلِنَفۡسِهِۦۖ وَمَن ضَلَّ فَإِنَّمَا يَضِلُّ عَلَيۡهَاۖ وَمَآ أَنتَ عَلَيۡهِم بِوَكِيلٍ", "Inna anzalna AAalayka alkitaba lilnnasi bialhaqqi famani ihtada falinafsihi waman dalla fainnama yadillu AAalayha wama anta AAalayhim biwakeelin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 42, "Allah takes the souls at the time of their death, and those that do not die [He takes] during their sleep. Then He keeps those for which He has decreed death and releases the others for a specified term. Indeed in that are signs for a people who give thought", "ٱللَّهُ يَتَوَفَّى ٱلۡأَنفُسَ حِينَ مَوۡتِهَا وَٱلَّتِي لَمۡ تَمُتۡ فِي مَنَامِهَاۖ فَيُمۡسِكُ ٱلَّتِي قَضَىٰ عَلَيۡهَا ٱلۡمَوۡتَ وَيُرۡسِلُ ٱلۡأُخۡرَىٰٓ إِلَىٰٓ أَجَلٖ مُّسَمًّىۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يَتَفَكَّرُونَ", "Allahu yatawaffa alanfusa heena mawtiha waallatee lam tamut fee manamiha fayumsiku allatee qada AAalayha almawta wayursilu alokhra ila ajalin musamman inna fee thalika laayatin liqawmin yatafakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 43, "Or have they taken other than Allah as intercessors? Say, ""Even though they do not possess [power over] anything, nor do they reason", "أَمِ ٱتَّخَذُواْ مِن دُونِ ٱللَّهِ شُفَعَآءَۚ قُلۡ أَوَلَوۡ كَانُواْ لَا يَمۡلِكُونَ شَيۡـٔٗا وَلَا يَعۡقِلُونَ", "Ami ittakhathoo min dooni Allahi shufaAAaa qul awalaw kanoo la yamlikoona shayan wala yaAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 44, "Say, ""To Allah belongs [the right to allow] intercession entirely. To Him belongs the dominion of the heavens and the earth. Then to Him you will be returned", "قُل لِّلَّهِ ٱلشَّفَٰعَةُ جَمِيعٗاۖ لَّهُۥ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ ثُمَّ إِلَيۡهِ تُرۡجَعُونَ", "Qul lillahi alshshafaAAatu jameeAAan lahu mulku alssamawati waalardi thumma ilayhi turjaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 45, "And when Allah is mentioned alone, the hearts of those who do not believe in the Hereafter shrink with aversion, but when those [worshipped] other than Him are mentioned, immediately they rejoice", "وَإِذَا ذُكِرَ ٱللَّهُ وَحۡدَهُ ٱشۡمَأَزَّتۡ قُلُوبُ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱلۡأٓخِرَةِۖ وَإِذَا ذُكِرَ ٱلَّذِينَ مِن دُونِهِۦٓ إِذَا هُمۡ يَسۡتَبۡشِرُونَ", "Waitha thukira Allahu wahdahu ishmaazzat quloobu allatheena la yuminoona bialakhirati waitha thukira allatheena min doonihi itha hum yastabshiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 46, "Say, ""O Allah, Creator of the heavens and the earth, Knower of the unseen and the witnessed, You will judge between your servants concerning that over which they used to differ", "قُلِ ٱللَّهُمَّ فَاطِرَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ عَٰلِمَ ٱلۡغَيۡبِ وَٱلشَّهَٰدَةِ أَنتَ تَحۡكُمُ بَيۡنَ عِبَادِكَ فِي مَا كَانُواْ فِيهِ يَخۡتَلِفُونَ", "Quli allahumma fatira alssamawati waalardi AAalima alghaybi waalshshahadati anta tahkumu bayna AAibadika fee ma kanoo feehi yakhtalifoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 47, "And if those who did wrong had all that is in the earth entirely and the like of it with it, they would [attempt to] ransom themselves thereby from the worst of the punishment on the Day of Resurrection. And there will appear to them from Allah that which they had not taken into account", "وَلَوۡ أَنَّ لِلَّذِينَ ظَلَمُواْ مَا فِي ٱلۡأَرۡضِ جَمِيعٗا وَمِثۡلَهُۥ مَعَهُۥ لَٱفۡتَدَوۡاْ بِهِۦ مِن سُوٓءِ ٱلۡعَذَابِ يَوۡمَ ٱلۡقِيَٰمَةِۚ وَبَدَا لَهُم مِّنَ ٱللَّهِ مَا لَمۡ يَكُونُواْ يَحۡتَسِبُونَ", "Walaw anna lillatheena thalamoo ma fee alardi jameeAAan wamithlahu maAAahu laiftadaw bihi min sooi alAAathabi yawma alqiyamati wabada lahum mina Allahi ma lam yakoonoo yahtasiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 48, "And there will appear to them the evils they had earned, and they will be enveloped by what they used to ridicule", "وَبَدَا لَهُمۡ سَيِّـَٔاتُ مَا كَسَبُواْ وَحَاقَ بِهِم مَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ", "Wabada lahum sayyiatu ma kasaboo wahaqa bihim ma kanoo bihi yastahzioona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 49, "And when adversity touches man, he calls upon Us; then when We bestow on him a favor from Us, he says, ""I have only been given it because of [my] knowledge."" Rather, it is a trial, but most of them do not know", "فَإِذَا مَسَّ ٱلۡإِنسَٰنَ ضُرّٞ دَعَانَا ثُمَّ إِذَا خَوَّلۡنَٰهُ نِعۡمَةٗ مِّنَّا قَالَ إِنَّمَآ أُوتِيتُهُۥ عَلَىٰ عِلۡمِۭۚ بَلۡ هِيَ فِتۡنَةٞ وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَعۡلَمُونَ", "Faitha massa alinsana durrun daAAana thumma itha khawwalnahu niAAmatan minna qala innama ooteetuhu AAala AAilmin bal hiya fitnatun walakinna aktharahum la yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 50, "Those before them had already said it, but they were not availed by what they used to earn", "قَدۡ قَالَهَا ٱلَّذِينَ مِن قَبۡلِهِمۡ فَمَآ أَغۡنَىٰ عَنۡهُم مَّا كَانُواْ يَكۡسِبُونَ", "Qad qalaha allatheena min qablihim fama aghna AAanhum ma kanoo yaksiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 51, "And the evil consequences of what they earned struck them. And those who have wronged of these [people] will be afflicted by the evil consequences of what they earned; and they will not cause failure", "فَأَصَابَهُمۡ سَيِّـَٔاتُ مَا كَسَبُواْۚ وَٱلَّذِينَ ظَلَمُواْ مِنۡ هَـٰٓؤُلَآءِ سَيُصِيبُهُمۡ سَيِّـَٔاتُ مَا كَسَبُواْ وَمَا هُم بِمُعۡجِزِينَ", "Faasabahum sayyiatu ma kasaboo waallatheena thalamoo min haolai sayuseebuhum sayyiatu ma kasaboo wama hum bimuAAjizeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 52, "Do they not know that Allah extends provision for whom He wills and restricts [it]? Indeed in that are signs for a people who believe", "أَوَلَمۡ يَعۡلَمُوٓاْ أَنَّ ٱللَّهَ يَبۡسُطُ ٱلرِّزۡقَ لِمَن يَشَآءُ وَيَقۡدِرُۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يُؤۡمِنُونَ", "Awalam yaAAlamoo anna Allaha yabsutu alrrizqa liman yashao wayaqdiru inna fee thalika laayatin liqawmin yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 53, "Say, ""O My servants who have transgressed against themselves [by sinning], do not despair of the mercy of Allah. Indeed, Allah forgives all sins. Indeed, it is He who is the Forgiving, the Merciful", "۞قُلۡ يَٰعِبَادِيَ ٱلَّذِينَ أَسۡرَفُواْ عَلَىٰٓ أَنفُسِهِمۡ لَا تَقۡنَطُواْ مِن رَّحۡمَةِ ٱللَّهِۚ إِنَّ ٱللَّهَ يَغۡفِرُ ٱلذُّنُوبَ جَمِيعًاۚ إِنَّهُۥ هُوَ ٱلۡغَفُورُ ٱلرَّحِيمُ", "Qul ya AAibadiya allatheena asrafoo AAala anfusihim la taqnatoo min rahmati Allahi inna Allaha yaghfiru alththunooba jameeAAan innahu huwa alghafooru alrraheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 54, "And return [in repentance] to your Lord and submit to Him before the punishment comes upon you; then you will not be helped", "وَأَنِيبُوٓاْ إِلَىٰ رَبِّكُمۡ وَأَسۡلِمُواْ لَهُۥ مِن قَبۡلِ أَن يَأۡتِيَكُمُ ٱلۡعَذَابُ ثُمَّ لَا تُنصَرُونَ", "Waaneeboo ila rabbikum waaslimoo lahu min qabli an yatiyakumu alAAathabu thumma la tunsaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 55, "And follow the best of what was revealed to you from your Lord before the punishment comes upon you suddenly while you do not perceive", "وَٱتَّبِعُوٓاْ أَحۡسَنَ مَآ أُنزِلَ إِلَيۡكُم مِّن رَّبِّكُم مِّن قَبۡلِ أَن يَأۡتِيَكُمُ ٱلۡعَذَابُ بَغۡتَةٗ وَأَنتُمۡ لَا تَشۡعُرُونَ", "WaittabiAAoo ahsana ma onzila ilaykum min rabbikum min qabli an yatiyakumu alAAathabu baghtatan waantum la tashAAuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 56, "Lest a soul should say, ""Oh [how great is] my regret over what I neglected in regard to Allah and that I was among the mockers", "أَن تَقُولَ نَفۡسٞ يَٰحَسۡرَتَىٰ عَلَىٰ مَا فَرَّطتُ فِي جَنۢبِ ٱللَّهِ وَإِن كُنتُ لَمِنَ ٱلسَّـٰخِرِينَ", "An taqoola nafsun ya hasrata AAala ma farrattu fee janbi Allahi wain kuntu lamina alssakhireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 57, "Or [lest] it say, ""If only Allah had guided me, I would have been among the righteous", "أَوۡ تَقُولَ لَوۡ أَنَّ ٱللَّهَ هَدَىٰنِي لَكُنتُ مِنَ ٱلۡمُتَّقِينَ", "Aw taqoola law anna Allaha hadanee lakuntu mina almuttaqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 58, "Or [lest] it say when it sees the punishment, ""If only I had another turn so I could be among the doers of good", "أَوۡ تَقُولَ حِينَ تَرَى ٱلۡعَذَابَ لَوۡ أَنَّ لِي كَرَّةٗ فَأَكُونَ مِنَ ٱلۡمُحۡسِنِينَ", "Aw taqoola heena tara alAAathaba law anna lee karratan faakoona mina almuhsineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 59, "But yes, there had come to you My verses, but you denied them and were arrogant, and you were among the disbelievers", "بَلَىٰ قَدۡ جَآءَتۡكَ ءَايَٰتِي فَكَذَّبۡتَ بِهَا وَٱسۡتَكۡبَرۡتَ وَكُنتَ مِنَ ٱلۡكَٰفِرِينَ", "Bala qad jaatka ayatee fakaththabta biha waistakbarta wakunta mina alkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 60, "And on the Day of Resurrection you will see those who lied about Allah [with] their faces blackened. Is there not in Hell a residence for the arrogant", "وَيَوۡمَ ٱلۡقِيَٰمَةِ تَرَى ٱلَّذِينَ كَذَبُواْ عَلَى ٱللَّهِ وُجُوهُهُم مُّسۡوَدَّةٌۚ أَلَيۡسَ فِي جَهَنَّمَ مَثۡوٗى لِّلۡمُتَكَبِّرِينَ", "Wayawma alqiyamati tara allatheena kathaboo AAala Allahi wujoohuhum muswaddatun alaysa fee jahannama mathwan lilmutakabbireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 61, "And Allah will save those who feared Him by their attainment; no evil will touch them, nor will they grieve", "وَيُنَجِّي ٱللَّهُ ٱلَّذِينَ ٱتَّقَوۡاْ بِمَفَازَتِهِمۡ لَا يَمَسُّهُمُ ٱلسُّوٓءُ وَلَا هُمۡ يَحۡزَنُونَ", "Wayunajjee Allahu allatheena ittaqaw bimafazatihim la yamassuhumu alssooo wala hum yahzanoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 62, "Allah is the Creator of all things, and He is, over all things, Disposer of affairs", "ٱللَّهُ خَٰلِقُ كُلِّ شَيۡءٖۖ وَهُوَ عَلَىٰ كُلِّ شَيۡءٖ وَكِيلٞ", "Allahu khaliqu kulli shayin wahuwa AAala kulli shayin wakeelun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 63, "To Him belong the keys of the heavens and the earth. And they who disbelieve in the verses of Allah - it is those who are the losers", "لَّهُۥ مَقَالِيدُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۗ وَٱلَّذِينَ كَفَرُواْ بِـَٔايَٰتِ ٱللَّهِ أُوْلَـٰٓئِكَ هُمُ ٱلۡخَٰسِرُونَ", "Lahu maqaleedu alssamawati waalardi waallatheena kafaroo biayati Allahi olaika humu alkhasiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 64, "Say, [O Muhammad], ""Is it other than Allah that you order me to worship, O ignorant ones", "قُلۡ أَفَغَيۡرَ ٱللَّهِ تَأۡمُرُوٓنِّيٓ أَعۡبُدُ أَيُّهَا ٱلۡجَٰهِلُونَ", "Qul afaghayra Allahi tamuroonnee aAAbudu ayyuha aljahiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 65, "And it was already revealed to you and to those before you that if you should associate [anything] with Allah, your work would surely become worthless, and you would surely be among the losers", "وَلَقَدۡ أُوحِيَ إِلَيۡكَ وَإِلَى ٱلَّذِينَ مِن قَبۡلِكَ لَئِنۡ أَشۡرَكۡتَ لَيَحۡبَطَنَّ عَمَلُكَ وَلَتَكُونَنَّ مِنَ ٱلۡخَٰسِرِينَ", "Walaqad oohiya ilayka waila allatheena min qablika lain ashrakta layahbatanna AAamaluka walatakoonanna mina alkhasireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 66, "Rather, worship [only] Allah and be among the grateful", "بَلِ ٱللَّهَ فَٱعۡبُدۡ وَكُن مِّنَ ٱلشَّـٰكِرِينَ", "Bali Allaha faoAAbud wakun mina alshshakireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 67, "They have not appraised Allah with true appraisal, while the earth entirely will be [within] His grip on the Day of Resurrection, and the heavens will be folded in His right hand. Exalted is He and high above what they associate with Him", "وَمَا قَدَرُواْ ٱللَّهَ حَقَّ قَدۡرِهِۦ وَٱلۡأَرۡضُ جَمِيعٗا قَبۡضَتُهُۥ يَوۡمَ ٱلۡقِيَٰمَةِ وَٱلسَّمَٰوَٰتُ مَطۡوِيَّـٰتُۢ بِيَمِينِهِۦۚ سُبۡحَٰنَهُۥ وَتَعَٰلَىٰ عَمَّا يُشۡرِكُونَ", "Wama qadaroo Allaha haqqa qadrihi waalardu jameeAAan qabdatuhu yawma alqiyamati waalssamawatu matwiyyatun biyameenihi subhanahu wataAAala AAamma yushrikoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 68, "And the Horn will be blown, and whoever is in the heavens and whoever is on the earth will fall dead except whom Allah wills. Then it will be blown again, and at once they will be standing, looking on", "وَنُفِخَ فِي ٱلصُّورِ فَصَعِقَ مَن فِي ٱلسَّمَٰوَٰتِ وَمَن فِي ٱلۡأَرۡضِ إِلَّا مَن شَآءَ ٱللَّهُۖ ثُمَّ نُفِخَ فِيهِ أُخۡرَىٰ فَإِذَا هُمۡ قِيَامٞ يَنظُرُونَ", "Wanufikha fee alssoori fasaAAiqa man fee alssamawati waman fee alardi illa man shaa Allahu thumma nufikha feehi okhra faitha hum qiyamun yanthuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 69, "And the earth will shine with the light of its Lord, and the record [of deeds] will be placed, and the prophets and the witnesses will be brought, and it will be judged between them in truth, and they will not be wronged", "وَأَشۡرَقَتِ ٱلۡأَرۡضُ بِنُورِ رَبِّهَا وَوُضِعَ ٱلۡكِتَٰبُ وَجِاْيٓءَ بِٱلنَّبِيِّـۧنَ وَٱلشُّهَدَآءِ وَقُضِيَ بَيۡنَهُم بِٱلۡحَقِّ وَهُمۡ لَا يُظۡلَمُونَ", "Waashraqati alardu binoori rabbiha wawudiAAa alkitabu wajeea bialnnabiyyeena waalshshuhadai waqudiya baynahum bialhaqqi wahum la yuthlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 70, "And every soul will be fully compensated [for] what it did; and He is most knowing of what they do", "وَوُفِّيَتۡ كُلُّ نَفۡسٖ مَّا عَمِلَتۡ وَهُوَ أَعۡلَمُ بِمَا يَفۡعَلُونَ", "Wawuffiyat kullu nafsin ma AAamilat wahuwa aAAlamu bima yafAAaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 71, "And those who disbelieved will be driven to Hell in groups until, when they reach it, its gates are opened and its keepers will say, ""Did there not come to you messengers from yourselves, reciting to you the verses of your Lord and warning you of the meeting of this Day of yours?"" They will say, ""Yes, but the word of punishment has come into effect upon the disbelievers", "وَسِيقَ ٱلَّذِينَ كَفَرُوٓاْ إِلَىٰ جَهَنَّمَ زُمَرًاۖ حَتَّىٰٓ إِذَا جَآءُوهَا فُتِحَتۡ أَبۡوَٰبُهَا وَقَالَ لَهُمۡ خَزَنَتُهَآ أَلَمۡ يَأۡتِكُمۡ رُسُلٞ مِّنكُمۡ يَتۡلُونَ عَلَيۡكُمۡ ءَايَٰتِ رَبِّكُمۡ وَيُنذِرُونَكُمۡ لِقَآءَ يَوۡمِكُمۡ هَٰذَاۚ قَالُواْ بَلَىٰ وَلَٰكِنۡ حَقَّتۡ كَلِمَةُ ٱلۡعَذَابِ عَلَى ٱلۡكَٰفِرِينَ", "Waseeqa allatheena kafaroo ila jahannama zumaran hatta itha jaooha futihat abwabuha waqala lahum khazanatuha alam yatikum rusulun minkum yatloona AAalaykum ayati rabbikum wayunthiroonakum liqaa yawmikum hatha qaloo bala walakin haqqat kalimatu alAAathabi AAala alkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 72, "[To them] it will be said, ""Enter the gates of Hell to abide eternally therein, and wretched is the residence of the arrogant", "قِيلَ ٱدۡخُلُوٓاْ أَبۡوَٰبَ جَهَنَّمَ خَٰلِدِينَ فِيهَاۖ فَبِئۡسَ مَثۡوَى ٱلۡمُتَكَبِّرِينَ", "Qeela odkhuloo abwaba jahannama khalideena feeha fabisa mathwa almutakabbireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 73, "But those who feared their Lord will be driven to Paradise in groups until, when they reach it while its gates have been opened and its keepers say, ""Peace be upon you; you have become pure; so enter it to abide eternally therein,"" [they will enter]", "وَسِيقَ ٱلَّذِينَ ٱتَّقَوۡاْ رَبَّهُمۡ إِلَى ٱلۡجَنَّةِ زُمَرًاۖ حَتَّىٰٓ إِذَا جَآءُوهَا وَفُتِحَتۡ أَبۡوَٰبُهَا وَقَالَ لَهُمۡ خَزَنَتُهَا سَلَٰمٌ عَلَيۡكُمۡ طِبۡتُمۡ فَٱدۡخُلُوهَا خَٰلِدِينَ", "Waseeqa allatheena ittaqaw rabbahum ila aljannati zumaran hatta itha jaooha wafutihat abwabuha waqala lahum khazanatuha salamun AAalaykum tibtum faodkhulooha khalideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 74, "And they will say, ""Praise to Allah, who has fulfilled for us His promise and made us inherit the earth [so] we may settle in Paradise wherever we will. And excellent is the reward of [righteous] workers", "وَقَالُواْ ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِي صَدَقَنَا وَعۡدَهُۥ وَأَوۡرَثَنَا ٱلۡأَرۡضَ نَتَبَوَّأُ مِنَ ٱلۡجَنَّةِ حَيۡثُ نَشَآءُۖ فَنِعۡمَ أَجۡرُ ٱلۡعَٰمِلِينَ", "Waqaloo alhamdu lillahi allathee sadaqana waAAdahu waawrathana alarda natabawwao mina aljannati haythu nashao faniAAma ajru alAAamileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (39, 75, "And you will see the angels surrounding the Throne, exalting [Allah] with praise of their Lord. And it will be judged between them in truth, and it will be said, ""[All] praise to Allah, Lord of the worlds", "وَتَرَى ٱلۡمَلَـٰٓئِكَةَ حَآفِّينَ مِنۡ حَوۡلِ ٱلۡعَرۡشِ يُسَبِّحُونَ بِحَمۡدِ رَبِّهِمۡۚ وَقُضِيَ بَيۡنَهُم بِٱلۡحَقِّۚ وَقِيلَ ٱلۡحَمۡدُ لِلَّهِ رَبِّ ٱلۡعَٰلَمِينَ", "Watara almalaikata haffeena min hawli alAAarshi yusabbihoona bihamdi rabbihim waqudiya baynahum bialhaqqi waqeela alhamdu lillahi rabbi alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 1, "Ha, Meem", "حمٓ", "Hameem");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 2, "The revelation of the Book is from Allah, the Exalted in Might, the Knowing", "تَنزِيلُ ٱلۡكِتَٰبِ مِنَ ٱللَّهِ ٱلۡعَزِيزِ ٱلۡعَلِيمِ", "Tanzeelu alkitabi mina Allahi alAAazeezi alAAaleemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 3, "The forgiver of sin, acceptor of repentance, severe in punishment, owner of abundance. There is no deity except Him; to Him is the destination", "غَافِرِ ٱلذَّنۢبِ وَقَابِلِ ٱلتَّوۡبِ شَدِيدِ ٱلۡعِقَابِ ذِي ٱلطَّوۡلِۖ لَآ إِلَٰهَ إِلَّا هُوَۖ إِلَيۡهِ ٱلۡمَصِيرُ", "Ghafiri alththanbi waqabili alttawbi shadeedi alAAiqabi thee alttawli la ilaha illa huwa ilayhi almaseeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 4, "No one disputes concerning the signs of Allah except those who disbelieve, so be not deceived by their [uninhibited] movement throughout the land", "مَا يُجَٰدِلُ فِيٓ ءَايَٰتِ ٱللَّهِ إِلَّا ٱلَّذِينَ كَفَرُواْ فَلَا يَغۡرُرۡكَ تَقَلُّبُهُمۡ فِي ٱلۡبِلَٰدِ", "Ma yujadilu fee ayati Allahi illa allatheena kafaroo fala yaghrurka taqallubuhum fee albiladi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 5, "The people of Noah denied before them and the [disbelieving] factions after them, and every nation intended [a plot] for their messenger to seize him, and they disputed by [using] falsehood to [attempt to] invalidate thereby the truth. So I seized them, and how [terrible] was My penalty", "كَذَّبَتۡ قَبۡلَهُمۡ قَوۡمُ نُوحٖ وَٱلۡأَحۡزَابُ مِنۢ بَعۡدِهِمۡۖ وَهَمَّتۡ كُلُّ أُمَّةِۭ بِرَسُولِهِمۡ لِيَأۡخُذُوهُۖ وَجَٰدَلُواْ بِٱلۡبَٰطِلِ لِيُدۡحِضُواْ بِهِ ٱلۡحَقَّ فَأَخَذۡتُهُمۡۖ فَكَيۡفَ كَانَ عِقَابِ", "Kaththabat qablahum qawmu noohin waalahzabu min baAAdihim wahammat kullu ommatin birasoolihim liyakhuthoohu wajadaloo bialbatili liyudhidoo bihi alhaqqa faakhathtuhum fakayfa kana AAiqabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 6, "And thus has the word of your Lord come into effect upon those who disbelieved that they are companions of the Fire", "وَكَذَٰلِكَ حَقَّتۡ كَلِمَتُ رَبِّكَ عَلَى ٱلَّذِينَ كَفَرُوٓاْ أَنَّهُمۡ أَصۡحَٰبُ ٱلنَّارِ", "Wakathalika haqqat kalimatu rabbika AAala allatheena kafaroo annahum ashabu alnnari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 7, "Those [angels] who carry the Throne and those around it exalt [Allah] with praise of their Lord and believe in Him and ask forgiveness for those who have believed, [saying], ""Our Lord, You have encompassed all things in mercy and knowledge, so forgive those who have repented and followed Your way and protect them from the punishment of Hellfire", "ٱلَّذِينَ يَحۡمِلُونَ ٱلۡعَرۡشَ وَمَنۡ حَوۡلَهُۥ يُسَبِّحُونَ بِحَمۡدِ رَبِّهِمۡ وَيُؤۡمِنُونَ بِهِۦ وَيَسۡتَغۡفِرُونَ لِلَّذِينَ ءَامَنُواْۖ رَبَّنَا وَسِعۡتَ كُلَّ شَيۡءٖ رَّحۡمَةٗ وَعِلۡمٗا فَٱغۡفِرۡ لِلَّذِينَ تَابُواْ وَٱتَّبَعُواْ سَبِيلَكَ وَقِهِمۡ عَذَابَ ٱلۡجَحِيمِ", "Allatheena yahmiloona alAAarsha waman hawlahu yusabbihoona bihamdi rabbihim wayuminoona bihi wayastaghfiroona lillatheena amanoo rabbana wasiAAta kulla shayin rahmatan waAAilman faighfir lillatheena taboo waittabaAAoo sabeelaka waqihim AAathaba aljaheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 8, "Our Lord, and admit them to gardens of perpetual residence which You have promised them and whoever was righteous among their fathers, their spouses and their offspring. Indeed, it is You who is the Exalted in Might, the Wise", "رَبَّنَا وَأَدۡخِلۡهُمۡ جَنَّـٰتِ عَدۡنٍ ٱلَّتِي وَعَدتَّهُمۡ وَمَن صَلَحَ مِنۡ ءَابَآئِهِمۡ وَأَزۡوَٰجِهِمۡ وَذُرِّيَّـٰتِهِمۡۚ إِنَّكَ أَنتَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ", "Rabbana waadkhilhum jannati AAadnin allatee waAAadtahum waman salaha min abaihim waazwajihim wathurriyyatihim innaka anta alAAazeezu alhakeemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 9, "And protect them from the evil consequences [of their deeds]. And he whom You protect from evil consequences that Day - You will have given him mercy. And that is the great attainment", "وَقِهِمُ ٱلسَّيِّـَٔاتِۚ وَمَن تَقِ ٱلسَّيِّـَٔاتِ يَوۡمَئِذٖ فَقَدۡ رَحِمۡتَهُۥۚ وَذَٰلِكَ هُوَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ", "Waqihimu alssayyiati waman taqi alssayyiati yawmaithin faqad rahimtahu wathalika huwa alfawzu alAAatheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 10, "Indeed, those who disbelieve will be addressed, ""The hatred of Allah for you was [even] greater than your hatred of yourselves [this Day in Hell] when you were invited to faith, but you refused", "إِنَّ ٱلَّذِينَ كَفَرُواْ يُنَادَوۡنَ لَمَقۡتُ ٱللَّهِ أَكۡبَرُ مِن مَّقۡتِكُمۡ أَنفُسَكُمۡ إِذۡ تُدۡعَوۡنَ إِلَى ٱلۡإِيمَٰنِ فَتَكۡفُرُونَ", "Inna allatheena kafaroo yunadawna lamaqtu Allahi akbaru min maqtikum anfusakum ith tudAAawna ila aleemani fatakfuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 11, "They will say, ""Our Lord, You made us lifeless twice and gave us life twice, and we have confessed our sins. So is there to an exit any way", "قَالُواْ رَبَّنَآ أَمَتَّنَا ٱثۡنَتَيۡنِ وَأَحۡيَيۡتَنَا ٱثۡنَتَيۡنِ فَٱعۡتَرَفۡنَا بِذُنُوبِنَا فَهَلۡ إِلَىٰ خُرُوجٖ مِّن سَبِيلٖ", "Qaloo rabbana amattana ithnatayni waahyaytana ithnatayni faiAAtarafna bithunoobina fahal ila khuroojin min sabeelin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 12, "[They will be told], ""That is because, when Allah was called upon alone, you disbelieved; but if others were associated with Him, you believed. So the judgement is with Allah, the Most High, the Grand", "ذَٰلِكُم بِأَنَّهُۥٓ إِذَا دُعِيَ ٱللَّهُ وَحۡدَهُۥ كَفَرۡتُمۡ وَإِن يُشۡرَكۡ بِهِۦ تُؤۡمِنُواْۚ فَٱلۡحُكۡمُ لِلَّهِ ٱلۡعَلِيِّ ٱلۡكَبِيرِ", "Thalikum biannahu itha duAAiya Allahu wahdahu kafartum wain yushrak bihi tuminoo faalhukmu lillahi alAAaliyyi alkabeeri");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 13, "It is He who shows you His signs and sends down to you from the sky, provision. But none will remember except he who turns back [in repentance]", "هُوَ ٱلَّذِي يُرِيكُمۡ ءَايَٰتِهِۦ وَيُنَزِّلُ لَكُم مِّنَ ٱلسَّمَآءِ رِزۡقٗاۚ وَمَا يَتَذَكَّرُ إِلَّا مَن يُنِيبُ", "Huwa allathee yureekum ayatihi wayunazzilu lakum mina alssamai rizqan wama yatathakkaru illa man yuneebu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 14, "So invoke Allah, [being] sincere to Him in religion, although the disbelievers dislike it", "فَٱدۡعُواْ ٱللَّهَ مُخۡلِصِينَ لَهُ ٱلدِّينَ وَلَوۡ كَرِهَ ٱلۡكَٰفِرُونَ", "FaodAAoo Allaha mukhliseena lahu alddeena walaw kariha alkafiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 15, "[He is] the Exalted above [all] degrees, Owner of the Throne; He places the inspiration of His command upon whom He wills of His servants to warn of the Day of Meeting", "رَفِيعُ ٱلدَّرَجَٰتِ ذُو ٱلۡعَرۡشِ يُلۡقِي ٱلرُّوحَ مِنۡ أَمۡرِهِۦ عَلَىٰ مَن يَشَآءُ مِنۡ عِبَادِهِۦ لِيُنذِرَ يَوۡمَ ٱلتَّلَاقِ", "RafeeAAu alddarajati thoo alAAarshi yulqee alrrooha min amrihi AAala man yashao min AAibadihi liyunthira yawma alttalaqi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 16, "The Day they come forth nothing concerning them will be concealed from Allah. To whom belongs [all] sovereignty this Day? To Allah, the One, the Prevailing", "يَوۡمَ هُم بَٰرِزُونَۖ لَا يَخۡفَىٰ عَلَى ٱللَّهِ مِنۡهُمۡ شَيۡءٞۚ لِّمَنِ ٱلۡمُلۡكُ ٱلۡيَوۡمَۖ لِلَّهِ ٱلۡوَٰحِدِ ٱلۡقَهَّارِ", "Yawma hum barizoona la yakhfa AAala Allahi minhum shayon limani almulku alyawma lillahi alwahidi alqahhari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 17, "This Day every soul will be recompensed for what it earned. No injustice today! Indeed, Allah is swift in account", "ٱلۡيَوۡمَ تُجۡزَىٰ كُلُّ نَفۡسِۭ بِمَا كَسَبَتۡۚ لَا ظُلۡمَ ٱلۡيَوۡمَۚ إِنَّ ٱللَّهَ سَرِيعُ ٱلۡحِسَابِ", "Alyawma tujza kullu nafsin bima kasabat la thulma alyawma inna Allaha sareeAAu alhisabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 18, "And warn them, [O Muhammad], of the Approaching Day, when hearts are at the throats, filled [with distress]. For the wrongdoers there will be no devoted friend and no intercessor [who is] obeyed", "وَأَنذِرۡهُمۡ يَوۡمَ ٱلۡأٓزِفَةِ إِذِ ٱلۡقُلُوبُ لَدَى ٱلۡحَنَاجِرِ كَٰظِمِينَۚ مَا لِلظَّـٰلِمِينَ مِنۡ حَمِيمٖ وَلَا شَفِيعٖ يُطَاعُ", "Waanthirhum yawma alazifati ithi alquloobu lada alhanajiri kathimeena ma lilththalimeena min hameemin wala shafeeAAin yutaAAu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 19, "He knows that which deceives the eyes and what the breasts conceal", "يَعۡلَمُ خَآئِنَةَ ٱلۡأَعۡيُنِ وَمَا تُخۡفِي ٱلصُّدُورُ", "YaAAlamu khainata alaAAyuni wama tukhfee alssudooru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 20, "And Allah judges with truth, while those they invoke besides Him judge not with anything. Indeed, Allah - He is the Hearing, the Seeing", "وَٱللَّهُ يَقۡضِي بِٱلۡحَقِّۖ وَٱلَّذِينَ يَدۡعُونَ مِن دُونِهِۦ لَا يَقۡضُونَ بِشَيۡءٍۗ إِنَّ ٱللَّهَ هُوَ ٱلسَّمِيعُ ٱلۡبَصِيرُ", "WaAllahu yaqdee bialhaqqi waallatheena yadAAoona min doonihi la yaqdoona bishayin inna Allaha huwa alssameeAAu albaseeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 21, "Have they not traveled through the land and observed how was the end of those who were before them? They were greater than them in strength and in impression on the land, but Allah seized them for their sins. And they had not from Allah any protector", "۞أَوَلَمۡ يَسِيرُواْ فِي ٱلۡأَرۡضِ فَيَنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلَّذِينَ كَانُواْ مِن قَبۡلِهِمۡۚ كَانُواْ هُمۡ أَشَدَّ مِنۡهُمۡ قُوَّةٗ وَءَاثَارٗا فِي ٱلۡأَرۡضِ فَأَخَذَهُمُ ٱللَّهُ بِذُنُوبِهِمۡ وَمَا كَانَ لَهُم مِّنَ ٱللَّهِ مِن وَاقٖ", "Awa lam yaseeroo fee alardi fayanthuroo kayfa kana AAaqibatu allatheena kanoo min qablihim kanoo hum ashadda minhum quwwatan waatharan fee alardi faakhathahumu Allahu bithunoobihim wama kana lahum mina Allahi min waqin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 22, "That was because their messengers were coming to them with clear proofs, but they disbelieved, so Allah seized them. Indeed, He is Powerful and severe in punishment", "ذَٰلِكَ بِأَنَّهُمۡ كَانَت تَّأۡتِيهِمۡ رُسُلُهُم بِٱلۡبَيِّنَٰتِ فَكَفَرُواْ فَأَخَذَهُمُ ٱللَّهُۚ إِنَّهُۥ قَوِيّٞ شَدِيدُ ٱلۡعِقَابِ", "Thalika biannahum kanat tateehim rusuluhum bialbayyinati fakafaroo faakhathahumu Allahu innahu qawiyyun shadeedu alAAiqabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 23, "And We did certainly send Moses with Our signs and a clear authority", "وَلَقَدۡ أَرۡسَلۡنَا مُوسَىٰ بِـَٔايَٰتِنَا وَسُلۡطَٰنٖ مُّبِينٍ", "Walaqad arsalna moosa biayatina wasultanin mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 24, "To Pharaoh, Haman and Qarun; but they said, ""[He is] a magician and a liar", "إِلَىٰ فِرۡعَوۡنَ وَهَٰمَٰنَ وَقَٰرُونَ فَقَالُواْ سَٰحِرٞ كَذَّابٞ", "Ila firAAawna wahamana waqaroona faqaloo sahirun kaththabun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 25, "And when he brought them the truth from Us, they said, ""Kill the sons of those who have believed with him and keep their women alive."" But the plan of the disbelievers is not except in error", "فَلَمَّا جَآءَهُم بِٱلۡحَقِّ مِنۡ عِندِنَا قَالُواْ ٱقۡتُلُوٓاْ أَبۡنَآءَ ٱلَّذِينَ ءَامَنُواْ مَعَهُۥ وَٱسۡتَحۡيُواْ نِسَآءَهُمۡۚ وَمَا كَيۡدُ ٱلۡكَٰفِرِينَ إِلَّا فِي ضَلَٰلٖ", "Falamma jaahum bialhaqqi min AAindina qaloo oqtuloo abnaa allatheena amanoo maAAahu waistahyoo nisaahum wama kaydu alkafireena illa fee dalalin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 26, "And Pharaoh said, ""Let me kill Moses and let him call upon his Lord. Indeed, I fear that he will change your religion or that he will cause corruption in the land", "وَقَالَ فِرۡعَوۡنُ ذَرُونِيٓ أَقۡتُلۡ مُوسَىٰ وَلۡيَدۡعُ رَبَّهُۥٓۖ إِنِّيٓ أَخَافُ أَن يُبَدِّلَ دِينَكُمۡ أَوۡ أَن يُظۡهِرَ فِي ٱلۡأَرۡضِ ٱلۡفَسَادَ", "Waqala firAAawnu tharoonee aqtul moosa walyadAAu rabbahu innee akhafu an yubaddila deenakum aw an yuthhira fee alardi alfasada");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 27, "But Moses said, ""Indeed, I have sought refuge in my Lord and your Lord from every arrogant one who does not believe in the Day of Account", "وَقَالَ مُوسَىٰٓ إِنِّي عُذۡتُ بِرَبِّي وَرَبِّكُم مِّن كُلِّ مُتَكَبِّرٖ لَّا يُؤۡمِنُ بِيَوۡمِ ٱلۡحِسَابِ", "Waqala moosa innee AAuthtu birabbee warabbikum min kulli mutakabbirin la yuminu biyawmi alhisabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 28, "And a believing man from the family of Pharaoh who concealed his faith said, ""Do you kill a man [merely] because he says, 'My Lord is Allah' while he has brought you clear proofs from your Lord? And if he should be lying, then upon him is [the consequence of] his lie; but if he should be truthful, there will strike you some of what he promises you. Indeed, Allah does not guide one who is a transgressor and a liar", "وَقَالَ رَجُلٞ مُّؤۡمِنٞ مِّنۡ ءَالِ فِرۡعَوۡنَ يَكۡتُمُ إِيمَٰنَهُۥٓ أَتَقۡتُلُونَ رَجُلًا أَن يَقُولَ رَبِّيَ ٱللَّهُ وَقَدۡ جَآءَكُم بِٱلۡبَيِّنَٰتِ مِن رَّبِّكُمۡۖ وَإِن يَكُ كَٰذِبٗا فَعَلَيۡهِ كَذِبُهُۥۖ وَإِن يَكُ صَادِقٗا يُصِبۡكُم بَعۡضُ ٱلَّذِي يَعِدُكُمۡۖ إِنَّ ٱللَّهَ لَا يَهۡدِي مَنۡ هُوَ مُسۡرِفٞ كَذَّابٞ", "Waqala rajulun muminun min ali firAAawna yaktumu eemanahu ataqtuloona rajulan an yaqoola rabbiyya Allahu waqad jaakum bialbayyinati min rabbikum wain yaku kathiban faAAalayhi kathibuhu wain yaku sadiqan yusibkum baAAdu allathee yaAAidukum inna Allaha la yahdee man huwa musrifun kaththabun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 29, "O my people, sovereignty is yours today, [your being] dominant in the land. But who would protect us from the punishment of Allah if it came to us?"" Pharaoh said, ""I do not show you except what I see, and I do not guide you except to the way of right conduct", "يَٰقَوۡمِ لَكُمُ ٱلۡمُلۡكُ ٱلۡيَوۡمَ ظَٰهِرِينَ فِي ٱلۡأَرۡضِ فَمَن يَنصُرُنَا مِنۢ بَأۡسِ ٱللَّهِ إِن جَآءَنَاۚ قَالَ فِرۡعَوۡنُ مَآ أُرِيكُمۡ إِلَّا مَآ أَرَىٰ وَمَآ أَهۡدِيكُمۡ إِلَّا سَبِيلَ ٱلرَّشَادِ", "Ya qawmi lakumu almulku alyawma thahireena fee alardi faman yansuruna min basi Allahi in jaana qala firAAawnu ma oreekum illa ma ara wama ahdeekum illa sabeela alrrashadi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 30, "And he who believed said, ""O my people, indeed I fear for you [a fate] like the day of the companies", "وَقَالَ ٱلَّذِيٓ ءَامَنَ يَٰقَوۡمِ إِنِّيٓ أَخَافُ عَلَيۡكُم مِّثۡلَ يَوۡمِ ٱلۡأَحۡزَابِ", "Waqala allathee amana ya qawmi innee akhafu AAalaykum mithla yawmi alahzabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 31, "Like the custom of the people of Noah and of 'Aad and Thamud and those after them. And Allah wants no injustice for [His] servants", "مِثۡلَ دَأۡبِ قَوۡمِ نُوحٖ وَعَادٖ وَثَمُودَ وَٱلَّذِينَ مِنۢ بَعۡدِهِمۡۚ وَمَا ٱللَّهُ يُرِيدُ ظُلۡمٗا لِّلۡعِبَادِ", "Mithla dabi qawmi noohin waAAadin wathamooda waallatheena min baAAdihim wama Allahu yureedu thulman lilAAibadi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 32, "And O my people, indeed I fear for you the Day of Calling", "وَيَٰقَوۡمِ إِنِّيٓ أَخَافُ عَلَيۡكُمۡ يَوۡمَ ٱلتَّنَادِ", "Waya qawmi innee akhafu AAalaykum yawma alttanadi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 33, "The Day you will turn your backs fleeing; there is not for you from Allah any protector. And whoever Allah leaves astray - there is not for him any guide", "يَوۡمَ تُوَلُّونَ مُدۡبِرِينَ مَا لَكُم مِّنَ ٱللَّهِ مِنۡ عَاصِمٖۗ وَمَن يُضۡلِلِ ٱللَّهُ فَمَا لَهُۥ مِنۡ هَادٖ", "Yawma tuwalloona mudbireena ma lakum mina Allahi min AAasimin waman yudlili Allahu fama lahu min hadin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 34, "And Joseph had already come to you before with clear proofs, but you remained in doubt of that which he brought to you, until when he died, you said, 'Never will Allah send a messenger after him.' Thus does Allah leave astray he who is a transgressor and skeptic", "وَلَقَدۡ جَآءَكُمۡ يُوسُفُ مِن قَبۡلُ بِٱلۡبَيِّنَٰتِ فَمَا زِلۡتُمۡ فِي شَكّٖ مِّمَّا جَآءَكُم بِهِۦۖ حَتَّىٰٓ إِذَا هَلَكَ قُلۡتُمۡ لَن يَبۡعَثَ ٱللَّهُ مِنۢ بَعۡدِهِۦ رَسُولٗاۚ كَذَٰلِكَ يُضِلُّ ٱللَّهُ مَنۡ هُوَ مُسۡرِفٞ مُّرۡتَابٌ", "Walaqad jaakum yoosufu min qablu bialbayyinati fama ziltum fee shakkin mimma jaakum bihi hatta itha halaka qultum lan yabAAatha Allahu min baAAdihi rasoolan kathalika yudillu Allahu man huwa musrifun murtabun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 35, "Those who dispute concerning the signs of Allah without an authority having come to them - great is hatred [of them] in the sight of Allah and in the sight of those who have believed. Thus does Allah seal over every heart [belonging to] an arrogant tyrant", "ٱلَّذِينَ يُجَٰدِلُونَ فِيٓ ءَايَٰتِ ٱللَّهِ بِغَيۡرِ سُلۡطَٰنٍ أَتَىٰهُمۡۖ كَبُرَ مَقۡتًا عِندَ ٱللَّهِ وَعِندَ ٱلَّذِينَ ءَامَنُواْۚ كَذَٰلِكَ يَطۡبَعُ ٱللَّهُ عَلَىٰ كُلِّ قَلۡبِ مُتَكَبِّرٖ جَبَّارٖ", "Allatheena yujadiloona fee ayati Allahi bighayri sultanin atahum kabura maqtan AAinda Allahi waAAinda allatheena amanoo kathalika yatbaAAu Allahu AAala kulli qalbi mutakabbirin jabbarin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 36, "And Pharaoh said, ""O Haman, construct for me a tower that I might reach the ways", "وَقَالَ فِرۡعَوۡنُ يَٰهَٰمَٰنُ ٱبۡنِ لِي صَرۡحٗا لَّعَلِّيٓ أَبۡلُغُ ٱلۡأَسۡبَٰبَ", "Waqala firAAawnu ya hamanu ibni lee sarhan laAAallee ablughu alasbaba");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 37, "The ways into the heavens - so that I may look at the deity of Moses; but indeed, I think he is a liar."" And thus was made attractive to Pharaoh the evil of his deed, and he was averted from the [right] way. And the plan of Pharaoh was not except in ruin", "أَسۡبَٰبَ ٱلسَّمَٰوَٰتِ فَأَطَّلِعَ إِلَىٰٓ إِلَٰهِ مُوسَىٰ وَإِنِّي لَأَظُنُّهُۥ كَٰذِبٗاۚ وَكَذَٰلِكَ زُيِّنَ لِفِرۡعَوۡنَ سُوٓءُ عَمَلِهِۦ وَصُدَّ عَنِ ٱلسَّبِيلِۚ وَمَا كَيۡدُ فِرۡعَوۡنَ إِلَّا فِي تَبَابٖ", "Asbaba alssamawati faattaliAAa ila ilahi moosa wainnee laathunnuhu kathiban wakathalika zuyyina lifirAAawna sooo AAamalihi wasudda AAani alssabeeli wama kaydu firAAawna illa fee tababin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 38, "And he who believed said, ""O my people, follow me, I will guide you to the way of right conduct", "وَقَالَ ٱلَّذِيٓ ءَامَنَ يَٰقَوۡمِ ٱتَّبِعُونِ أَهۡدِكُمۡ سَبِيلَ ٱلرَّشَادِ", "Waqala allathee amana ya qawmi ittabiAAooni ahdikum sabeela alrrashadi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 39, "O my people, this worldly life is only [temporary] enjoyment, and indeed, the Hereafter - that is the home of [permanent] settlement", "يَٰقَوۡمِ إِنَّمَا هَٰذِهِ ٱلۡحَيَوٰةُ ٱلدُّنۡيَا مَتَٰعٞ وَإِنَّ ٱلۡأٓخِرَةَ هِيَ دَارُ ٱلۡقَرَارِ", "Ya qawmi innama hathihi alhayatu alddunya mataAAun wainna alakhirata hiya daru alqarari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 40, "Whoever does an evil deed will not be recompensed except by the like thereof; but whoever does righteousness, whether male or female, while he is a believer - those will enter Paradise, being given provision therein without account", "مَنۡ عَمِلَ سَيِّئَةٗ فَلَا يُجۡزَىٰٓ إِلَّا مِثۡلَهَاۖ وَمَنۡ عَمِلَ صَٰلِحٗا مِّن ذَكَرٍ أَوۡ أُنثَىٰ وَهُوَ مُؤۡمِنٞ فَأُوْلَـٰٓئِكَ يَدۡخُلُونَ ٱلۡجَنَّةَ يُرۡزَقُونَ فِيهَا بِغَيۡرِ حِسَابٖ", "Man AAamila sayyiatan fala yujza illa mithlaha waman AAamila salihan min thakarin aw ontha wahuwa muminun faolaika yadkhuloona aljannata yurzaqoona feeha bighayri hisabin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 41, "And O my people, how is it that I invite you to salvation while you invite me to the Fire", "۞وَيَٰقَوۡمِ مَا لِيٓ أَدۡعُوكُمۡ إِلَى ٱلنَّجَوٰةِ وَتَدۡعُونَنِيٓ إِلَى ٱلنَّارِ", "Waya qawmi ma lee adAAookum ila alnnajati watadAAoonanee ila alnnari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 42, "You invite me to disbelieve in Allah and associate with Him that of which I have no knowledge, and I invite you to the Exalted in Might, the Perpetual Forgiver", "تَدۡعُونَنِي لِأَكۡفُرَ بِٱللَّهِ وَأُشۡرِكَ بِهِۦ مَا لَيۡسَ لِي بِهِۦ عِلۡمٞ وَأَنَا۠ أَدۡعُوكُمۡ إِلَى ٱلۡعَزِيزِ ٱلۡغَفَّـٰرِ", "TadAAoonanee liakfura biAllahi waoshrika bihi ma laysa lee bihi AAilmun waana adAAookum ila alAAazeezi alghaffari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 43, "Assuredly, that to which you invite me has no [response to a] supplication in this world or in the Hereafter; and indeed, our return is to Allah, and indeed, the transgressors will be companions of the Fire", "لَا جَرَمَ أَنَّمَا تَدۡعُونَنِيٓ إِلَيۡهِ لَيۡسَ لَهُۥ دَعۡوَةٞ فِي ٱلدُّنۡيَا وَلَا فِي ٱلۡأٓخِرَةِ وَأَنَّ مَرَدَّنَآ إِلَى ٱللَّهِ وَأَنَّ ٱلۡمُسۡرِفِينَ هُمۡ أَصۡحَٰبُ ٱلنَّارِ", "La jarama annama tadAAoonanee ilayhi laysa lahu daAAwatun fee alddunya wala fee alakhirati waanna maraddana ila Allahi waanna almusrifeena hum ashabu alnnari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 44, "And you will remember what I [now] say to you, and I entrust my affair to Allah. Indeed, Allah is Seeing of [His] servants", "فَسَتَذۡكُرُونَ مَآ أَقُولُ لَكُمۡۚ وَأُفَوِّضُ أَمۡرِيٓ إِلَى ٱللَّهِۚ إِنَّ ٱللَّهَ بَصِيرُۢ بِٱلۡعِبَادِ", "Fasatathkuroona ma aqoolu lakum waofawwidu amree ila Allahi inna Allaha baseerun bialAAibadi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 45, "So Allah protected him from the evils they plotted, and the people of Pharaoh were enveloped by the worst of punishment", "فَوَقَىٰهُ ٱللَّهُ سَيِّـَٔاتِ مَا مَكَرُواْۖ وَحَاقَ بِـَٔالِ فِرۡعَوۡنَ سُوٓءُ ٱلۡعَذَابِ", "Fawaqahu Allahu sayyiati ma makaroo wahaqa biali firAAawna sooo alAAathabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 46, "The Fire, they are exposed to it morning and evening. And the Day the Hour appears [it will be said], ""Make the people of Pharaoh enter the severest punishment", "ٱلنَّارُ يُعۡرَضُونَ عَلَيۡهَا غُدُوّٗا وَعَشِيّٗاۚ وَيَوۡمَ تَقُومُ ٱلسَّاعَةُ أَدۡخِلُوٓاْ ءَالَ فِرۡعَوۡنَ أَشَدَّ ٱلۡعَذَابِ", "Alnnaru yuAAradoona AAalayha ghuduwwan waAAashiyyan wayawma taqoomu alssaAAatu adkhiloo ala firAAawna ashadda alAAathabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 47, "And [mention] when they will argue within the Fire, and the weak will say to those who had been arrogant, ""Indeed, we were [only] your followers, so will you relieve us of a share of the Fire", "وَإِذۡ يَتَحَآجُّونَ فِي ٱلنَّارِ فَيَقُولُ ٱلضُّعَفَـٰٓؤُاْ لِلَّذِينَ ٱسۡتَكۡبَرُوٓاْ إِنَّا كُنَّا لَكُمۡ تَبَعٗا فَهَلۡ أَنتُم مُّغۡنُونَ عَنَّا نَصِيبٗا مِّنَ ٱلنَّارِ", "Waith yatahajjoona fee alnnari fayaqoolu aldduAAafao lillatheena istakbaroo inna kunna lakum tabaAAan fahal antum mughnoona AAanna naseeban mina alnnari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 48, "Those who had been arrogant will say, ""Indeed, all [of us] are in it. Indeed, Allah has judged between the servants", "قَالَ ٱلَّذِينَ ٱسۡتَكۡبَرُوٓاْ إِنَّا كُلّٞ فِيهَآ إِنَّ ٱللَّهَ قَدۡ حَكَمَ بَيۡنَ ٱلۡعِبَادِ", "Qala allatheena istakbaroo inna kullun feeha inna Allaha qad hakama bayna alAAibadi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 49, "And those in the Fire will say to the keepers of Hell, ""Supplicate your Lord to lighten for us a day from the punishment", "وَقَالَ ٱلَّذِينَ فِي ٱلنَّارِ لِخَزَنَةِ جَهَنَّمَ ٱدۡعُواْ رَبَّكُمۡ يُخَفِّفۡ عَنَّا يَوۡمٗا مِّنَ ٱلۡعَذَابِ", "Waqala allatheena fee alnnari likhazanati jahannama odAAoo rabbakum yukhaffif AAanna yawman mina alAAathabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 50, "They will say, ""Did there not come to you your messengers with clear proofs?"" They will say, ""Yes."" They will reply, ""Then supplicate [yourselves], but the supplication of the disbelievers is not except in error", "قَالُوٓاْ أَوَلَمۡ تَكُ تَأۡتِيكُمۡ رُسُلُكُم بِٱلۡبَيِّنَٰتِۖ قَالُواْ بَلَىٰۚ قَالُواْ فَٱدۡعُواْۗ وَمَا دُعَـٰٓؤُاْ ٱلۡكَٰفِرِينَ إِلَّا فِي ضَلَٰلٍ", "Qaloo awalam taku tateekum rusulukum bialbayyinati qaloo bala qaloo faodAAoo wama duAAao alkafireena illa fee dalalin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 51, "Indeed, We will support Our messengers and those who believe during the life of this world and on the Day when the witnesses will stand", "إِنَّا لَنَنصُرُ رُسُلَنَا وَٱلَّذِينَ ءَامَنُواْ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَيَوۡمَ يَقُومُ ٱلۡأَشۡهَٰدُ", "Inna lanansuru rusulana waallatheena amanoo fee alhayati alddunya wayawma yaqoomu alashhadu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 52, "The Day their excuse will not benefit the wrongdoers, and they will have the curse, and they will have the worst home", "يَوۡمَ لَا يَنفَعُ ٱلظَّـٰلِمِينَ مَعۡذِرَتُهُمۡۖ وَلَهُمُ ٱللَّعۡنَةُ وَلَهُمۡ سُوٓءُ ٱلدَّارِ", "Yawma la yanfaAAu alththalimeena maAAthiratuhum walahumu allaAAnatu walahum sooo alddari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 53, "And We had certainly given Moses guidance, and We caused the Children of Israel to inherit the Scripture", "وَلَقَدۡ ءَاتَيۡنَا مُوسَى ٱلۡهُدَىٰ وَأَوۡرَثۡنَا بَنِيٓ إِسۡرَـٰٓءِيلَ ٱلۡكِتَٰبَ", "Walaqad atayna moosa alhuda waawrathna banee israeela alkitaba");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 54, "As guidance and a reminder for those of understanding", "هُدٗى وَذِكۡرَىٰ لِأُوْلِي ٱلۡأَلۡبَٰبِ", "Hudan wathikra liolee alalbabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 55, "So be patient, [O Muhammad]. Indeed, the promise of Allah is truth. And ask forgiveness for your sin and exalt [Allah] with praise of your Lord in the evening and the morning", "فَٱصۡبِرۡ إِنَّ وَعۡدَ ٱللَّهِ حَقّٞ وَٱسۡتَغۡفِرۡ لِذَنۢبِكَ وَسَبِّحۡ بِحَمۡدِ رَبِّكَ بِٱلۡعَشِيِّ وَٱلۡإِبۡكَٰرِ", "Faisbir inna waAAda Allahi haqqun waistaghfir lithanbika wasabbih bihamdi rabbika bialAAashiyyi waalibkari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 56, "Indeed, those who dispute concerning the signs of Allah without [any] authority having come to them - there is not within their breasts except pride, [the extent of] which they cannot reach. So seek refuge in Allah. Indeed, it is He who is the Hearing, the Seeing", "إِنَّ ٱلَّذِينَ يُجَٰدِلُونَ فِيٓ ءَايَٰتِ ٱللَّهِ بِغَيۡرِ سُلۡطَٰنٍ أَتَىٰهُمۡ إِن فِي صُدُورِهِمۡ إِلَّا كِبۡرٞ مَّا هُم بِبَٰلِغِيهِۚ فَٱسۡتَعِذۡ بِٱللَّهِۖ إِنَّهُۥ هُوَ ٱلسَّمِيعُ ٱلۡبَصِيرُ", "Inna allatheena yujadiloona fee ayati Allahi bighayri sultanin atahum in fee sudoorihim illa kibrun ma hum bibaligheehi faistaAAith biAllahi innahu huwa alssameeAAu albaseeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 57, "The creation of the heavens and earth is greater than the creation of mankind, but most of the people do not know", "لَخَلۡقُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ أَكۡبَرُ مِنۡ خَلۡقِ ٱلنَّاسِ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَعۡلَمُونَ", "Lakhalqu alssamawati waalardi akbaru min khalqi alnnasi walakinna akthara alnnasi la yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 58, "And not equal are the blind and the seeing, nor are those who believe and do righteous deeds and the evildoer. Little do you remember", "وَمَا يَسۡتَوِي ٱلۡأَعۡمَىٰ وَٱلۡبَصِيرُ وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ وَلَا ٱلۡمُسِيٓءُۚ قَلِيلٗا مَّا تَتَذَكَّرُونَ", "Wama yastawee alaAAma waalbaseeru waallatheena amanoo waAAamiloo alssalihati wala almuseeo qaleelan ma tatathakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 59, "Indeed, the Hour is coming - no doubt about it - but most of the people do not believe", "إِنَّ ٱلسَّاعَةَ لَأٓتِيَةٞ لَّا رَيۡبَ فِيهَا وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يُؤۡمِنُونَ", "Inna alssaAAata laatiyatun la rayba feeha walakinna akthara alnnasi la yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 60, "And your Lord says, ""Call upon Me; I will respond to you."" Indeed, those who disdain My worship will enter Hell [rendered] contemptible", "وَقَالَ رَبُّكُمُ ٱدۡعُونِيٓ أَسۡتَجِبۡ لَكُمۡۚ إِنَّ ٱلَّذِينَ يَسۡتَكۡبِرُونَ عَنۡ عِبَادَتِي سَيَدۡخُلُونَ جَهَنَّمَ دَاخِرِينَ", "Waqala rabbukumu odAAoonee astajib lakum inna allatheena yastakbiroona AAan AAibadatee sayadkhuloona jahannama dakhireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 61, "It is Allah who made for you the night that you may rest therein and the day giving sight. Indeed, Allah is full of bounty to the people, but most of the people are not grateful", "ٱللَّهُ ٱلَّذِي جَعَلَ لَكُمُ ٱلَّيۡلَ لِتَسۡكُنُواْ فِيهِ وَٱلنَّهَارَ مُبۡصِرًاۚ إِنَّ ٱللَّهَ لَذُو فَضۡلٍ عَلَى ٱلنَّاسِ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَشۡكُرُونَ", "Allahu allathee jaAAala lakumu allayla litaskunoo feehi waalnnahara mubsiran inna Allaha lathoo fadlin AAala alnnasi walakinna akthara alnnasi la yashkuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 62, "That is Allah, your Lord, Creator of all things; there is no deity except Him, so how are you deluded", "ذَٰلِكُمُ ٱللَّهُ رَبُّكُمۡ خَٰلِقُ كُلِّ شَيۡءٖ لَّآ إِلَٰهَ إِلَّا هُوَۖ فَأَنَّىٰ تُؤۡفَكُونَ", "Thalikumu Allahu rabbukum khaliqu kulli shayin la ilaha illa huwa faanna tufakoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 63, "Thus were those [before you] deluded who were rejecting the signs of Allah", "كَذَٰلِكَ يُؤۡفَكُ ٱلَّذِينَ كَانُواْ بِـَٔايَٰتِ ٱللَّهِ يَجۡحَدُونَ", "Kathalika yufaku allatheena kanoo biayati Allahi yajhadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 64, "It is Allah who made for you the earth a place of settlement and the sky a ceiling and formed you and perfected your forms and provided you with good things. That is Allah, your Lord; then blessed is Allah, Lord of the worlds", "ٱللَّهُ ٱلَّذِي جَعَلَ لَكُمُ ٱلۡأَرۡضَ قَرَارٗا وَٱلسَّمَآءَ بِنَآءٗ وَصَوَّرَكُمۡ فَأَحۡسَنَ صُوَرَكُمۡ وَرَزَقَكُم مِّنَ ٱلطَّيِّبَٰتِۚ ذَٰلِكُمُ ٱللَّهُ رَبُّكُمۡۖ فَتَبَارَكَ ٱللَّهُ رَبُّ ٱلۡعَٰلَمِينَ", "Allahu allathee jaAAala lakumu alarda qararan waalssamaa binaan wasawwarakum faahsana suwarakum warazaqakum mina alttayyibati thalikumu Allahu rabbukum fatabaraka Allahu rabbu alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 65, "He is the Ever-Living; there is no deity except Him, so call upon Him, [being] sincere to Him in religion. [All] praise is [due] to Allah, Lord of the worlds", "هُوَ ٱلۡحَيُّ لَآ إِلَٰهَ إِلَّا هُوَ فَٱدۡعُوهُ مُخۡلِصِينَ لَهُ ٱلدِّينَۗ ٱلۡحَمۡدُ لِلَّهِ رَبِّ ٱلۡعَٰلَمِينَ", "Huwa alhayyu la ilaha illa huwa faodAAoohu mukhliseena lahu alddeena alhamdu lillahi rabbi alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 66, "Say, [O Muhammad], ""Indeed, I have been forbidden to worship those you call upon besides Allah once the clear proofs have come to me from my Lord, and I have been commanded to submit to the Lord of the worlds", "۞قُلۡ إِنِّي نُهِيتُ أَنۡ أَعۡبُدَ ٱلَّذِينَ تَدۡعُونَ مِن دُونِ ٱللَّهِ لَمَّا جَآءَنِيَ ٱلۡبَيِّنَٰتُ مِن رَّبِّي وَأُمِرۡتُ أَنۡ أُسۡلِمَ لِرَبِّ ٱلۡعَٰلَمِينَ", "Qul innee nuheetu an aAAbuda allatheena tadAAoona min dooni Allahi lamma jaaniya albayyinatu min rabbee waomirtu an oslima lirabbi alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 67, "It is He who created you from dust, then from a sperm-drop, then from a clinging clot; then He brings you out as a child; then [He develops you] that you reach your [time of] maturity, then [further] that you become elders. And among you is he who is taken in death before [that], so that you reach a specified term; and perhaps you will use reason", "هُوَ ٱلَّذِي خَلَقَكُم مِّن تُرَابٖ ثُمَّ مِن نُّطۡفَةٖ ثُمَّ مِنۡ عَلَقَةٖ ثُمَّ يُخۡرِجُكُمۡ طِفۡلٗا ثُمَّ لِتَبۡلُغُوٓاْ أَشُدَّكُمۡ ثُمَّ لِتَكُونُواْ شُيُوخٗاۚ وَمِنكُم مَّن يُتَوَفَّىٰ مِن قَبۡلُۖ وَلِتَبۡلُغُوٓاْ أَجَلٗا مُّسَمّٗى وَلَعَلَّكُمۡ تَعۡقِلُونَ", "Huwa allathee khalaqakum min turabin thumma min nutfatin thumma min AAalaqatin thumma yukhrijukum tiflan thumma litablughoo ashuddakum thumma litakoonoo shuyookhan waminkum man yutawaffa min qablu walitablughoo ajalan musamman walaAAallakum taAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 68, "He it is who gives life and causes death; and when He decrees a matter, He but says to it, ""Be,"" and it is", "هُوَ ٱلَّذِي يُحۡيِۦ وَيُمِيتُۖ فَإِذَا قَضَىٰٓ أَمۡرٗا فَإِنَّمَا يَقُولُ لَهُۥ كُن فَيَكُونُ", "Huwa allathee yuhyee wayumeetu faitha qada amran fainnama yaqoolu lahu kun fayakoonu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 69, "Do you not consider those who dispute concerning the signs of Allah - how are they averted", "أَلَمۡ تَرَ إِلَى ٱلَّذِينَ يُجَٰدِلُونَ فِيٓ ءَايَٰتِ ٱللَّهِ أَنَّىٰ يُصۡرَفُونَ", "Alam tara ila allatheena yujadiloona fee ayati Allahi anna yusrafoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 70, "Those who deny the Book and that with which We sent Our messengers - they are going to know", "ٱلَّذِينَ كَذَّبُواْ بِٱلۡكِتَٰبِ وَبِمَآ أَرۡسَلۡنَا بِهِۦ رُسُلَنَاۖ فَسَوۡفَ يَعۡلَمُونَ", "Allatheena kaththaboo bialkitabi wabima arsalna bihi rusulana fasawfa yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 71, "When the shackles are around their necks and the chains; they will be dragged", "إِذِ ٱلۡأَغۡلَٰلُ فِيٓ أَعۡنَٰقِهِمۡ وَٱلسَّلَٰسِلُ يُسۡحَبُونَ", "Ithi alaghlalu fee aAAnaqihim waalssalasilu yushaboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 72, "In boiling water; then in the Fire they will be filled [with flame]", "فِي ٱلۡحَمِيمِ ثُمَّ فِي ٱلنَّارِ يُسۡجَرُونَ", "Fee alhameemi thumma fee alnnari yusjaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 73, "Then it will be said to them, ""Where is that which you used to associate [with Him in worship]", "ثُمَّ قِيلَ لَهُمۡ أَيۡنَ مَا كُنتُمۡ تُشۡرِكُونَ", "Thumma qeela lahum ayna ma kuntum tushrikoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 74, "Other than Allah?"" They will say, ""They have departed from us; rather, we did not used to invoke previously anything."" Thus does Allah put astray the disbelievers", "مِن دُونِ ٱللَّهِۖ قَالُواْ ضَلُّواْ عَنَّا بَل لَّمۡ نَكُن نَّدۡعُواْ مِن قَبۡلُ شَيۡـٔٗاۚ كَذَٰلِكَ يُضِلُّ ٱللَّهُ ٱلۡكَٰفِرِينَ", "Min dooni Allahi qaloo dalloo AAanna bal lam nakun nadAAoo min qablu shayan kathalika yudillu Allahu alkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 75, "[The angels will say], ""That was because you used to exult upon the earth without right and you used to behave insolently", "ذَٰلِكُم بِمَا كُنتُمۡ تَفۡرَحُونَ فِي ٱلۡأَرۡضِ بِغَيۡرِ ٱلۡحَقِّ وَبِمَا كُنتُمۡ تَمۡرَحُونَ", "Thalikum bima kuntum tafrahoona fee alardi bighayri alhaqqi wabima kuntum tamrahoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 76, "Enter the gates of Hell to abide eternally therein, and wretched is the residence of the arrogant", "ٱدۡخُلُوٓاْ أَبۡوَٰبَ جَهَنَّمَ خَٰلِدِينَ فِيهَاۖ فَبِئۡسَ مَثۡوَى ٱلۡمُتَكَبِّرِينَ", "Odkhuloo abwaba jahannama khalideena feeha fabisa mathwa almutakabbireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 77, "So be patient, [O Muhammad]; indeed, the promise of Allah is truth. And whether We show you some of what We have promised them or We take you in death, it is to Us they will be returned", "فَٱصۡبِرۡ إِنَّ وَعۡدَ ٱللَّهِ حَقّٞۚ فَإِمَّا نُرِيَنَّكَ بَعۡضَ ٱلَّذِي نَعِدُهُمۡ أَوۡ نَتَوَفَّيَنَّكَ فَإِلَيۡنَا يُرۡجَعُونَ", "Faisbir inna waAAda Allahi haqqun faimma nuriyannaka baAAda allathee naAAiduhum aw natawaffayannaka failayna yurjaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 78, "And We have already sent messengers before you. Among them are those [whose stories] We have related to you, and among them are those [whose stories] We have not related to you. And it was not for any messenger to bring a sign [or verse] except by permission of Allah. So when the command of Allah comes, it will be concluded in truth, and the falsifiers will thereupon lose [all]", "وَلَقَدۡ أَرۡسَلۡنَا رُسُلٗا مِّن قَبۡلِكَ مِنۡهُم مَّن قَصَصۡنَا عَلَيۡكَ وَمِنۡهُم مَّن لَّمۡ نَقۡصُصۡ عَلَيۡكَۗ وَمَا كَانَ لِرَسُولٍ أَن يَأۡتِيَ بِـَٔايَةٍ إِلَّا بِإِذۡنِ ٱللَّهِۚ فَإِذَا جَآءَ أَمۡرُ ٱللَّهِ قُضِيَ بِٱلۡحَقِّ وَخَسِرَ هُنَالِكَ ٱلۡمُبۡطِلُونَ", "Walaqad arsalna rusulan min qablika minhum man qasasna AAalayka waminhum man lam naqsus AAalayka wama kana lirasoolin an yatiya biayatin illa biithni Allahi faitha jaa amru Allahi qudiya bialhaqqi wakhasira hunalika almubtiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 79, "It is Allah who made for you the grazing animals upon which you ride, and some of them you eat", "ٱللَّهُ ٱلَّذِي جَعَلَ لَكُمُ ٱلۡأَنۡعَٰمَ لِتَرۡكَبُواْ مِنۡهَا وَمِنۡهَا تَأۡكُلُونَ", "Allahu allathee jaAAala lakumu alanAAama litarkaboo minha waminha takuloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 80, "And for you therein are [other] benefits and that you may realize upon them a need which is in your breasts; and upon them and upon ships you are carried", "وَلَكُمۡ فِيهَا مَنَٰفِعُ وَلِتَبۡلُغُواْ عَلَيۡهَا حَاجَةٗ فِي صُدُورِكُمۡ وَعَلَيۡهَا وَعَلَى ٱلۡفُلۡكِ تُحۡمَلُونَ", "Walakum feeha manafiAAu walitablughoo AAalayha hajatan fee sudoorikum waAAalayha waAAala alfulki tuhmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 81, "And He shows you His signs. So which of the signs of Allah do you deny", "وَيُرِيكُمۡ ءَايَٰتِهِۦ فَأَيَّ ءَايَٰتِ ٱللَّهِ تُنكِرُونَ", "Wayureekum ayatihi faayya ayati Allahi tunkiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 82, "Have they not traveled through the land and observed how was the end of those before them? They were more numerous than themselves and greater in strength and in impression on the land, but they were not availed by what they used to earn", "أَفَلَمۡ يَسِيرُواْ فِي ٱلۡأَرۡضِ فَيَنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلَّذِينَ مِن قَبۡلِهِمۡۚ كَانُوٓاْ أَكۡثَرَ مِنۡهُمۡ وَأَشَدَّ قُوَّةٗ وَءَاثَارٗا فِي ٱلۡأَرۡضِ فَمَآ أَغۡنَىٰ عَنۡهُم مَّا كَانُواْ يَكۡسِبُونَ", "Afalam yaseeroo fee alardi fayanthuroo kayfa kana AAaqibatu allatheena min qablihim kanoo akthara minhum waashadda quwwatan waatharan fee alardi fama aghna AAanhum ma kanoo yaksiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 83, "And when their messengers came to them with clear proofs, they [merely] rejoiced in what they had of knowledge, but they were enveloped by what they used to ridicule", "فَلَمَّا جَآءَتۡهُمۡ رُسُلُهُم بِٱلۡبَيِّنَٰتِ فَرِحُواْ بِمَا عِندَهُم مِّنَ ٱلۡعِلۡمِ وَحَاقَ بِهِم مَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ", "Falamma jaathum rusuluhum bialbayyinati farihoo bima AAindahum mina alAAilmi wahaqa bihim ma kanoo bihi yastahzioona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 84, "And when they saw Our punishment, they said,"" We believe in Allah alone and disbelieve in that which we used to associate with Him", "فَلَمَّا رَأَوۡاْ بَأۡسَنَا قَالُوٓاْ ءَامَنَّا بِٱللَّهِ وَحۡدَهُۥ وَكَفَرۡنَا بِمَا كُنَّا بِهِۦ مُشۡرِكِينَ", "Falamma raaw basana qaloo amanna biAllahi wahdahu wakafarna bima kunna bihi mushrikeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (40, 85, "But never did their faith benefit them once they saw Our punishment. [It is] the established way of Allah which has preceded among His servants. And the disbelievers thereupon lost [all]", "فَلَمۡ يَكُ يَنفَعُهُمۡ إِيمَٰنُهُمۡ لَمَّا رَأَوۡاْ بَأۡسَنَاۖ سُنَّتَ ٱللَّهِ ٱلَّتِي قَدۡ خَلَتۡ فِي عِبَادِهِۦۖ وَخَسِرَ هُنَالِكَ ٱلۡكَٰفِرُونَ", "Falam yaku yanfaAAuhum eemanuhum lamma raaw basana sunnata Allahi allatee qad khalat fee AAibadihi wakhasira hunalika alkafiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 1, "Ha, Meem", "حمٓ", "Hameem");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 2, "[This is] a revelation from the Entirely Merciful, the Especially Merciful", "تَنزِيلٞ مِّنَ ٱلرَّحۡمَٰنِ ٱلرَّحِيمِ", "Tanzeelun mina alrrahmani alrraheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 3, "A Book whose verses have been detailed, an Arabic Qur'an for a people who know", "كِتَٰبٞ فُصِّلَتۡ ءَايَٰتُهُۥ قُرۡءَانًا عَرَبِيّٗا لِّقَوۡمٖ يَعۡلَمُونَ", "Kitabun fussilat ayatuhu quranan AAarabiyyan liqawmin yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 4, "As a giver of good tidings and a warner; but most of them turn away, so they do not hear", "بَشِيرٗا وَنَذِيرٗا فَأَعۡرَضَ أَكۡثَرُهُمۡ فَهُمۡ لَا يَسۡمَعُونَ", "Basheeran wanatheeran faaAArada aktharuhum fahum la yasmaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 5, "And they say, ""Our hearts are within coverings from that to which you invite us, and in our ears is deafness, and between us and you is a partition, so work; indeed, we are working", "وَقَالُواْ قُلُوبُنَا فِيٓ أَكِنَّةٖ مِّمَّا تَدۡعُونَآ إِلَيۡهِ وَفِيٓ ءَاذَانِنَا وَقۡرٞ وَمِنۢ بَيۡنِنَا وَبَيۡنِكَ حِجَابٞ فَٱعۡمَلۡ إِنَّنَا عَٰمِلُونَ", "Waqaloo quloobuna fee akinnatin mimma tadAAoona ilayhi wafee athanina waqrun wamin baynina wabaynika hijabun faiAAmal innana AAamiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 6, "Say, O [Muhammad], ""I am only a man like you to whom it has been revealed that your god is but one God; so take a straight course to Him and seek His forgiveness."" And woe to those who associate others with Allah", "قُلۡ إِنَّمَآ أَنَا۠ بَشَرٞ مِّثۡلُكُمۡ يُوحَىٰٓ إِلَيَّ أَنَّمَآ إِلَٰهُكُمۡ إِلَٰهٞ وَٰحِدٞ فَٱسۡتَقِيمُوٓاْ إِلَيۡهِ وَٱسۡتَغۡفِرُوهُۗ وَوَيۡلٞ لِّلۡمُشۡرِكِينَ", "Qul innama ana basharun mithlukum yooha ilayya annama ilahukum ilahun wahidun faistaqeemoo ilayhi waistaghfiroohu wawaylun lilmushrikeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 7, "Those who do not give zakah, and in the Hereafter they are disbelievers", "ٱلَّذِينَ لَا يُؤۡتُونَ ٱلزَّكَوٰةَ وَهُم بِٱلۡأٓخِرَةِ هُمۡ كَٰفِرُونَ", "Allatheena la yutoona alzzakata wahum bialakhirati hum kafiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 8, "Indeed, those who believe and do righteous deeds - for them is a reward uninterrupted", "إِنَّ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ لَهُمۡ أَجۡرٌ غَيۡرُ مَمۡنُونٖ", "Inna allatheena amanoo waAAamiloo alssalihati lahum ajrun ghayru mamnoonin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 9, "Say, ""Do you indeed disbelieve in He who created the earth in two days and attribute to Him equals? That is the Lord of the worlds", "۞قُلۡ أَئِنَّكُمۡ لَتَكۡفُرُونَ بِٱلَّذِي خَلَقَ ٱلۡأَرۡضَ فِي يَوۡمَيۡنِ وَتَجۡعَلُونَ لَهُۥٓ أَندَادٗاۚ ذَٰلِكَ رَبُّ ٱلۡعَٰلَمِينَ", "Qul ainnakum latakfuroona biallathee khalaqa alarda fee yawmayni watajAAaloona lahu andadan thalika rabbu alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 10, "And He placed on the earth firmly set mountains over its surface, and He blessed it and determined therein its [creatures'] sustenance in four days without distinction - for [the information] of those who ask", "وَجَعَلَ فِيهَا رَوَٰسِيَ مِن فَوۡقِهَا وَبَٰرَكَ فِيهَا وَقَدَّرَ فِيهَآ أَقۡوَٰتَهَا فِيٓ أَرۡبَعَةِ أَيَّامٖ سَوَآءٗ لِّلسَّآئِلِينَ", "WajaAAala feeha rawasiya min fawqiha wabaraka feeha waqaddara feeha aqwataha fee arbaAAati ayyamin sawaan lilssaileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 11, "Then He directed Himself to the heaven while it was smoke and said to it and to the earth, ""Come [into being], willingly or by compulsion."" They said, ""We have come willingly", "ثُمَّ ٱسۡتَوَىٰٓ إِلَى ٱلسَّمَآءِ وَهِيَ دُخَانٞ فَقَالَ لَهَا وَلِلۡأَرۡضِ ٱئۡتِيَا طَوۡعًا أَوۡ كَرۡهٗا قَالَتَآ أَتَيۡنَا طَآئِعِينَ", "Thumma istawa ila alssamai wahiya dukhanun faqala laha walilardi itiya tawAAan aw karhan qalata atayna taiAAeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 12, "And He completed them as seven heavens within two days and inspired in each heaven its command. And We adorned the nearest heaven with lamps and as protection. That is the determination of the Exalted in Might, the Knowing", "فَقَضَىٰهُنَّ سَبۡعَ سَمَٰوَاتٖ فِي يَوۡمَيۡنِ وَأَوۡحَىٰ فِي كُلِّ سَمَآءٍ أَمۡرَهَاۚ وَزَيَّنَّا ٱلسَّمَآءَ ٱلدُّنۡيَا بِمَصَٰبِيحَ وَحِفۡظٗاۚ ذَٰلِكَ تَقۡدِيرُ ٱلۡعَزِيزِ ٱلۡعَلِيمِ", "Faqadahunna sabAAa samawatin fee yawmayni waawha fee kulli samain amraha wazayyanna alssamaa alddunya bimasabeeha wahifthan thalika taqdeeru alAAazeezi alAAaleemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 13, "But if they turn away, then say, ""I have warned you of a thunderbolt like the thunderbolt [that struck] 'Aad and Thamud", "فَإِنۡ أَعۡرَضُواْ فَقُلۡ أَنذَرۡتُكُمۡ صَٰعِقَةٗ مِّثۡلَ صَٰعِقَةِ عَادٖ وَثَمُودَ", "Fain aAAradoo fuqul anthartukum saAAiqatan mithla saAAiqati AAadin wathamooda");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 14, "[That occurred] when the messengers had come to them before them and after them, [saying], ""Worship not except Allah."" They said, ""If our Lord had willed, He would have sent down the angels, so indeed we, in that with which you have been sent, are disbelievers", "إِذۡ جَآءَتۡهُمُ ٱلرُّسُلُ مِنۢ بَيۡنِ أَيۡدِيهِمۡ وَمِنۡ خَلۡفِهِمۡ أَلَّا تَعۡبُدُوٓاْ إِلَّا ٱللَّهَۖ قَالُواْ لَوۡ شَآءَ رَبُّنَا لَأَنزَلَ مَلَـٰٓئِكَةٗ فَإِنَّا بِمَآ أُرۡسِلۡتُم بِهِۦ كَٰفِرُونَ", "Ith jaathumu alrrusulu min bayni aydeehim wamin khalfihim alla taAAbudoo illa Allaha qaloo law shaa rabbuna laanzala malaikatan fainna bima orsiltum bihi kafiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 15, "As for 'Aad, they were arrogant upon the earth without right and said, ""Who is greater than us in strength?"" Did they not consider that Allah who created them was greater than them in strength? But they were rejecting Our signs", "فَأَمَّا عَادٞ فَٱسۡتَكۡبَرُواْ فِي ٱلۡأَرۡضِ بِغَيۡرِ ٱلۡحَقِّ وَقَالُواْ مَنۡ أَشَدُّ مِنَّا قُوَّةًۖ أَوَلَمۡ يَرَوۡاْ أَنَّ ٱللَّهَ ٱلَّذِي خَلَقَهُمۡ هُوَ أَشَدُّ مِنۡهُمۡ قُوَّةٗۖ وَكَانُواْ بِـَٔايَٰتِنَا يَجۡحَدُونَ", "Faamma AAadun faistakbaroo fee alardi bighayri alhaqqi waqaloo man ashaddu minna quwwatan awalam yaraw anna Allaha allathee khalaqahum huwa ashaddu minhum quwwatan wakanoo biayatina yajhadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 16, "So We sent upon them a screaming wind during days of misfortune to make them taste the punishment of disgrace in the worldly life; but the punishment of the Hereafter is more disgracing, and they will not be helped", "فَأَرۡسَلۡنَا عَلَيۡهِمۡ رِيحٗا صَرۡصَرٗا فِيٓ أَيَّامٖ نَّحِسَاتٖ لِّنُذِيقَهُمۡ عَذَابَ ٱلۡخِزۡيِ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۖ وَلَعَذَابُ ٱلۡأٓخِرَةِ أَخۡزَىٰۖ وَهُمۡ لَا يُنصَرُونَ", "Faarsalna AAalayhim reehan sarsaran fee ayyamin nahisatin linutheeqahum AAathaba alkhizyi fee alhayati alddunya walaAAathabu alakhirati akhza wahum la yunsaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 17, "And as for Thamud, We guided them, but they preferred blindness over guidance, so the thunderbolt of humiliating punishment seized them for what they used to earn", "وَأَمَّا ثَمُودُ فَهَدَيۡنَٰهُمۡ فَٱسۡتَحَبُّواْ ٱلۡعَمَىٰ عَلَى ٱلۡهُدَىٰ فَأَخَذَتۡهُمۡ صَٰعِقَةُ ٱلۡعَذَابِ ٱلۡهُونِ بِمَا كَانُواْ يَكۡسِبُونَ", "Waamma thamoodu fahadaynahum faistahabboo alAAama AAala alhuda faakhathathum saAAiqatu alAAathabi alhooni bima kanoo yaksiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 18, "And We saved those who believed and used to fear Allah", "وَنَجَّيۡنَا ٱلَّذِينَ ءَامَنُواْ وَكَانُواْ يَتَّقُونَ", "Wanajjayna allatheena amanoo wakanoo yattaqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 19, "And [mention, O Muhammad], the Day when the enemies of Allah will be gathered to the Fire while they are [driven] assembled in rows", "وَيَوۡمَ يُحۡشَرُ أَعۡدَآءُ ٱللَّهِ إِلَى ٱلنَّارِ فَهُمۡ يُوزَعُونَ", "Wayawma yuhsharu aAAdao Allahi ila alnnari fahum yoozaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 20, "Until, when they reach it, their hearing and their eyes and their skins will testify against them of what they used to do", "حَتَّىٰٓ إِذَا مَا جَآءُوهَا شَهِدَ عَلَيۡهِمۡ سَمۡعُهُمۡ وَأَبۡصَٰرُهُمۡ وَجُلُودُهُم بِمَا كَانُواْ يَعۡمَلُونَ", "Hatta itha ma jaooha shahida AAalayhim samAAuhum waabsaruhum wajulooduhum bima kanoo yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 21, "And they will say to their skins, ""Why have you testified against us?"" They will say, ""We were made to speak by Allah, who has made everything speak; and He created you the first time, and to Him you are returned", "وَقَالُواْ لِجُلُودِهِمۡ لِمَ شَهِدتُّمۡ عَلَيۡنَاۖ قَالُوٓاْ أَنطَقَنَا ٱللَّهُ ٱلَّذِيٓ أَنطَقَ كُلَّ شَيۡءٖۚ وَهُوَ خَلَقَكُمۡ أَوَّلَ مَرَّةٖ وَإِلَيۡهِ تُرۡجَعُونَ", "Waqaloo lijuloodihim lima shahidtum AAalayna qaloo antaqana Allahu allathee antaqa kulla shayin wahuwa khalaqakum awwala marratin wailayhi turjaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 22, "And you were not covering yourselves, lest your hearing testify against you or your sight or your skins, but you assumed that Allah does not know much of what you do", "وَمَا كُنتُمۡ تَسۡتَتِرُونَ أَن يَشۡهَدَ عَلَيۡكُمۡ سَمۡعُكُمۡ وَلَآ أَبۡصَٰرُكُمۡ وَلَا جُلُودُكُمۡ وَلَٰكِن ظَنَنتُمۡ أَنَّ ٱللَّهَ لَا يَعۡلَمُ كَثِيرٗا مِّمَّا تَعۡمَلُونَ", "Wama kuntum tastatiroona an yashhada AAalaykum samAAukum wala absarukum wala juloodukum walakin thanantum anna Allaha la yaAAlamu katheeran mimma taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 23, "And that was your assumption which you assumed about your Lord. It has brought you to ruin, and you have become among the losers", "وَذَٰلِكُمۡ ظَنُّكُمُ ٱلَّذِي ظَنَنتُم بِرَبِّكُمۡ أَرۡدَىٰكُمۡ فَأَصۡبَحۡتُم مِّنَ ٱلۡخَٰسِرِينَ", "Wathalikum thannukumu allathee thanantum birabbikum ardakum faasbahtum mina alkhasireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 24, "So [even] if they are patient, the Fire is a residence for them; and if they ask to appease [Allah], they will not be of those who are allowed to appease", "فَإِن يَصۡبِرُواْ فَٱلنَّارُ مَثۡوٗى لَّهُمۡۖ وَإِن يَسۡتَعۡتِبُواْ فَمَا هُم مِّنَ ٱلۡمُعۡتَبِينَ", "Fain yasbiroo faalnnaru mathwan lahum wain yastaAAtiboo fama hum mina almuAAtabeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 25, "And We appointed for them companions who made attractive to them what was before them and what was behind them [of sin], and the word has come into effect upon them among nations which had passed on before them of jinn and men. Indeed, they [all] were losers", "۞وَقَيَّضۡنَا لَهُمۡ قُرَنَآءَ فَزَيَّنُواْ لَهُم مَّا بَيۡنَ أَيۡدِيهِمۡ وَمَا خَلۡفَهُمۡ وَحَقَّ عَلَيۡهِمُ ٱلۡقَوۡلُ فِيٓ أُمَمٖ قَدۡ خَلَتۡ مِن قَبۡلِهِم مِّنَ ٱلۡجِنِّ وَٱلۡإِنسِۖ إِنَّهُمۡ كَانُواْ خَٰسِرِينَ", "Waqayyadna lahum quranaa fazayyanoo lahum ma bayna aydeehim wama khalfahum wahaqqa AAalayhimu alqawlu fee omamin qad khalat min qablihim mina aljinni waalinsi innahum kanoo khasireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 26, "And those who disbelieve say, ""Do not listen to this Qur'an and speak noisily during [the recitation of] it that perhaps you will overcome", "وَقَالَ ٱلَّذِينَ كَفَرُواْ لَا تَسۡمَعُواْ لِهَٰذَا ٱلۡقُرۡءَانِ وَٱلۡغَوۡاْ فِيهِ لَعَلَّكُمۡ تَغۡلِبُونَ", "Waqala allatheena kafaroo la tasmaAAoo lihatha alqurani wailghaw feehi laAAallakum taghliboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 27, "But We will surely cause those who disbelieve to taste a severe punishment, and We will surely recompense them for the worst of what they had been doing", "فَلَنُذِيقَنَّ ٱلَّذِينَ كَفَرُواْ عَذَابٗا شَدِيدٗا وَلَنَجۡزِيَنَّهُمۡ أَسۡوَأَ ٱلَّذِي كَانُواْ يَعۡمَلُونَ", "Falanutheeqanna allatheena kafaroo AAathaban shadeedan walanajziyannahum aswaa allathee kanoo yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 28, "That is the recompense of the enemies of Allah - the Fire. For them therein is the home of eternity as recompense for what they, of Our verses, were rejecting", "ذَٰلِكَ جَزَآءُ أَعۡدَآءِ ٱللَّهِ ٱلنَّارُۖ لَهُمۡ فِيهَا دَارُ ٱلۡخُلۡدِ جَزَآءَۢ بِمَا كَانُواْ بِـَٔايَٰتِنَا يَجۡحَدُونَ", "Thalika jazao aAAdai Allahi alnnaru lahum feeha daru alkhuldi jazaan bima kanoo biayatina yajhadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 29, "And those who disbelieved will [then] say, ""Our Lord, show us those who misled us of the jinn and men [so] we may put them under our feet that they will be among the lowest", "وَقَالَ ٱلَّذِينَ كَفَرُواْ رَبَّنَآ أَرِنَا ٱلَّذَيۡنِ أَضَلَّانَا مِنَ ٱلۡجِنِّ وَٱلۡإِنسِ نَجۡعَلۡهُمَا تَحۡتَ أَقۡدَامِنَا لِيَكُونَا مِنَ ٱلۡأَسۡفَلِينَ", "Waqala allatheena kafaroo rabbana arina allathayni adallana mina aljinni waalinsi najAAalhuma tahta aqdamina liyakoona mina alasfaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 30, "Indeed, those who have said, ""Our Lord is Allah "" and then remained on a right course - the angels will descend upon them, [saying], ""Do not fear and do not grieve but receive good tidings of Paradise, which you were promised", "إِنَّ ٱلَّذِينَ قَالُواْ رَبُّنَا ٱللَّهُ ثُمَّ ٱسۡتَقَٰمُواْ تَتَنَزَّلُ عَلَيۡهِمُ ٱلۡمَلَـٰٓئِكَةُ أَلَّا تَخَافُواْ وَلَا تَحۡزَنُواْ وَأَبۡشِرُواْ بِٱلۡجَنَّةِ ٱلَّتِي كُنتُمۡ تُوعَدُونَ", "Inna allatheena qaloo rabbuna Allahu thumma istaqamoo tatanazzalu AAalayhimu almalaikatu alla takhafoo wala tahzanoo waabshiroo bialjannati allatee kuntum tooAAadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 31, "We [angels] were your allies in worldly life and [are so] in the Hereafter. And you will have therein whatever your souls desire, and you will have therein whatever you request [or wish]", "نَحۡنُ أَوۡلِيَآؤُكُمۡ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَفِي ٱلۡأٓخِرَةِۖ وَلَكُمۡ فِيهَا مَا تَشۡتَهِيٓ أَنفُسُكُمۡ وَلَكُمۡ فِيهَا مَا تَدَّعُونَ", "Nahnu awliyaokum fee alhayati alddunya wafee alakhirati walakum feeha ma tashtahee anfusukum walakum feeha ma taddaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 32, "As accommodation from a [Lord who is] Forgiving and Merciful", "نُزُلٗا مِّنۡ غَفُورٖ رَّحِيمٖ", "Nuzulan min ghafoorin raheemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 33, "And who is better in speech than one who invites to Allah and does righteousness and says, ""Indeed, I am of the Muslims", "وَمَنۡ أَحۡسَنُ قَوۡلٗا مِّمَّن دَعَآ إِلَى ٱللَّهِ وَعَمِلَ صَٰلِحٗا وَقَالَ إِنَّنِي مِنَ ٱلۡمُسۡلِمِينَ", "Waman ahsanu qawlan mimman daAAa ila Allahi waAAamila salihan waqala innanee mina almuslimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 34, "And not equal are the good deed and the bad. Repel [evil] by that [deed] which is better; and thereupon the one whom between you and him is enmity [will become] as though he was a devoted friend", "وَلَا تَسۡتَوِي ٱلۡحَسَنَةُ وَلَا ٱلسَّيِّئَةُۚ ٱدۡفَعۡ بِٱلَّتِي هِيَ أَحۡسَنُ فَإِذَا ٱلَّذِي بَيۡنَكَ وَبَيۡنَهُۥ عَدَٰوَةٞ كَأَنَّهُۥ وَلِيٌّ حَمِيمٞ", "Wala tastawee alhasanatu wala alssayyiatu idfaAA biallatee hiya ahsanu faitha allathee baynaka wabaynahu AAadawatun kaannahu waliyyun hameemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 35, "But none is granted it except those who are patient, and none is granted it except one having a great portion [of good]", "وَمَا يُلَقَّىٰهَآ إِلَّا ٱلَّذِينَ صَبَرُواْ وَمَا يُلَقَّىٰهَآ إِلَّا ذُو حَظٍّ عَظِيمٖ", "Wama yulaqqaha illa allatheena sabaroo wama yulaqqaha illa thoo haththin AAatheemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 36, "And if there comes to you from Satan an evil suggestion, then seek refuge in Allah. Indeed, He is the Hearing, the Knowing", "وَإِمَّا يَنزَغَنَّكَ مِنَ ٱلشَّيۡطَٰنِ نَزۡغٞ فَٱسۡتَعِذۡ بِٱللَّهِۖ إِنَّهُۥ هُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ", "Waimma yanzaghannaka mina alshshaytani nazghun faistaAAith biAllahi innahu huwa alssameeAAu alAAaleemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 37, "And of His signs are the night and day and the sun and moon. Do not prostrate to the sun or to the moon, but prostate to Allah, who created them, if it should be Him that you worship", "وَمِنۡ ءَايَٰتِهِ ٱلَّيۡلُ وَٱلنَّهَارُ وَٱلشَّمۡسُ وَٱلۡقَمَرُۚ لَا تَسۡجُدُواْ لِلشَّمۡسِ وَلَا لِلۡقَمَرِ وَٱسۡجُدُواْۤ لِلَّهِۤ ٱلَّذِي خَلَقَهُنَّ إِن كُنتُمۡ إِيَّاهُ تَعۡبُدُونَ", "Wamin ayatihi allaylu waalnnaharu waalshshamsu waalqamaru la tasjudoo lilshshamsi wala lilqamari waosjudoo lillahi alathee khalaqahunna in kuntum iyyahu taAAbudoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 38, "But if they are arrogant - then those who are near your Lord exalt Him by night and by day, and they do not become weary", "فَإِنِ ٱسۡتَكۡبَرُواْ فَٱلَّذِينَ عِندَ رَبِّكَ يُسَبِّحُونَ لَهُۥ بِٱلَّيۡلِ وَٱلنَّهَارِ وَهُمۡ لَا يَسۡـَٔمُونَ۩", "Faini istakbaroo faallatheena AAinda rabbika yusabbihoona lahu biallayli waalnnahari wahum la yasamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 39, "And of His signs is that you see the earth stilled, but when We send down upon it rain, it quivers and grows. Indeed, He who has given it life is the Giver of Life to the dead. Indeed, He is over all things competent", "وَمِنۡ ءَايَٰتِهِۦٓ أَنَّكَ تَرَى ٱلۡأَرۡضَ خَٰشِعَةٗ فَإِذَآ أَنزَلۡنَا عَلَيۡهَا ٱلۡمَآءَ ٱهۡتَزَّتۡ وَرَبَتۡۚ إِنَّ ٱلَّذِيٓ أَحۡيَاهَا لَمُحۡيِ ٱلۡمَوۡتَىٰٓۚ إِنَّهُۥ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ", "Wamin ayatihi annaka tara alarda khashiAAatan faitha anzalna AAalayha almaa ihtazzat warabat inna allathee ahyaha lamuhyee almawta innahu AAala kulli shayin qadeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 40, "Indeed, those who inject deviation into Our verses are not concealed from Us. So, is he who is cast into the Fire better or he who comes secure on the Day of Resurrection? Do whatever you will; indeed, He is Seeing of what you do", "إِنَّ ٱلَّذِينَ يُلۡحِدُونَ فِيٓ ءَايَٰتِنَا لَا يَخۡفَوۡنَ عَلَيۡنَآۗ أَفَمَن يُلۡقَىٰ فِي ٱلنَّارِ خَيۡرٌ أَم مَّن يَأۡتِيٓ ءَامِنٗا يَوۡمَ ٱلۡقِيَٰمَةِۚ ٱعۡمَلُواْ مَا شِئۡتُمۡ إِنَّهُۥ بِمَا تَعۡمَلُونَ بَصِيرٌ", "Inna allatheena yulhidoona fee ayatina la yakhfawna AAalayna afaman yulqa fee alnnari khayrun am man yatee aminan yawma alqiyamati iAAmaloo ma shitum innahu bima taAAmaloona baseerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 41, "Indeed, those who disbelieve in the message after it has come to them... And indeed, it is a mighty Book", "إِنَّ ٱلَّذِينَ كَفَرُواْ بِٱلذِّكۡرِ لَمَّا جَآءَهُمۡۖ وَإِنَّهُۥ لَكِتَٰبٌ عَزِيزٞ", "Inna allatheena kafaroo bialththikri lamma jaahum wainnahu lakitabun AAazeezun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 42, "Falsehood cannot approach it from before it or from behind it; [it is] a revelation from a [Lord who is] Wise and Praiseworthy", "لَّا يَأۡتِيهِ ٱلۡبَٰطِلُ مِنۢ بَيۡنِ يَدَيۡهِ وَلَا مِنۡ خَلۡفِهِۦۖ تَنزِيلٞ مِّنۡ حَكِيمٍ حَمِيدٖ", "La yateehi albatilu min bayni yadayhi wala min khalfihi tanzeelun min hakeemin hameedin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 43, "Nothing is said to you, [O Muhammad], except what was already said to the messengers before you. Indeed, your Lord is a possessor of forgiveness and a possessor of painful penalty", "مَّا يُقَالُ لَكَ إِلَّا مَا قَدۡ قِيلَ لِلرُّسُلِ مِن قَبۡلِكَۚ إِنَّ رَبَّكَ لَذُو مَغۡفِرَةٖ وَذُو عِقَابٍ أَلِيمٖ", "Ma yuqalu laka illa ma qad qeela lilrrusuli min qablika inna rabbaka lathoo maghfiratin wathoo AAiqabin aleemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 44, "And if We had made it a non-Arabic Qur'an, they would have said, ""Why are its verses not explained in detail [in our language]? Is it a foreign [recitation] and an Arab [messenger]?"" Say, ""It is, for those who believe, a guidance and cure."" And those who do not believe - in their ears is deafness, and it is upon them blindness. Those are being called from a distant place", "وَلَوۡ جَعَلۡنَٰهُ قُرۡءَانًا أَعۡجَمِيّٗا لَّقَالُواْ لَوۡلَا فُصِّلَتۡ ءَايَٰتُهُۥٓۖ ءَا۬عۡجَمِيّٞ وَعَرَبِيّٞۗ قُلۡ هُوَ لِلَّذِينَ ءَامَنُواْ هُدٗى وَشِفَآءٞۚ وَٱلَّذِينَ لَا يُؤۡمِنُونَ فِيٓ ءَاذَانِهِمۡ وَقۡرٞ وَهُوَ عَلَيۡهِمۡ عَمًىۚ أُوْلَـٰٓئِكَ يُنَادَوۡنَ مِن مَّكَانِۭ بَعِيدٖ", "Walaw jaAAalnahu quranan aAAjamiyyan laqaloo lawla fussilat ayatuhu aaAAjamiyyun waAAarabiyyun qul huwa lillatheena amanoo hudan washifaon waallatheena la yuminoona fee athanihim waqrun wahuwa AAalayhim AAaman olaika yunadawna min makanin baAAeedin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 45, "And We had already given Moses the Scripture, but it came under disagreement. And if not for a word that preceded from your Lord, it would have been concluded between them. And indeed they are, concerning the Qur'an, in disquieting doubt", "وَلَقَدۡ ءَاتَيۡنَا مُوسَى ٱلۡكِتَٰبَ فَٱخۡتُلِفَ فِيهِۚ وَلَوۡلَا كَلِمَةٞ سَبَقَتۡ مِن رَّبِّكَ لَقُضِيَ بَيۡنَهُمۡۚ وَإِنَّهُمۡ لَفِي شَكّٖ مِّنۡهُ مُرِيبٖ", "Walaqad atayna moosa alkitaba faikhtulifa feehi walawla kalimatun sabaqat min rabbika laqudiya baynahum wainnahum lafee shakkin minhu mureebin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 46, "Whoever does righteousness - it is for his [own] soul; and whoever does evil [does so] against it. And your Lord is not ever unjust to [His] servants", "مَّنۡ عَمِلَ صَٰلِحٗا فَلِنَفۡسِهِۦۖ وَمَنۡ أَسَآءَ فَعَلَيۡهَاۗ وَمَا رَبُّكَ بِظَلَّـٰمٖ لِّلۡعَبِيدِ", "Man AAamila salihan falinafsihi waman asaa faAAalayha wama rabbuka bithallamin lilAAabeedi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 47, "To him [alone] is attributed knowledge of the Hour. And fruits emerge not from their coverings nor does a female conceive or give birth except with His knowledge. And the Day He will call to them, ""Where are My 'partners'?"" they will say, ""We announce to You that there is [no longer] among us any witness [to that]", "۞إِلَيۡهِ يُرَدُّ عِلۡمُ ٱلسَّاعَةِۚ وَمَا تَخۡرُجُ مِن ثَمَرَٰتٖ مِّنۡ أَكۡمَامِهَا وَمَا تَحۡمِلُ مِنۡ أُنثَىٰ وَلَا تَضَعُ إِلَّا بِعِلۡمِهِۦۚ وَيَوۡمَ يُنَادِيهِمۡ أَيۡنَ شُرَكَآءِي قَالُوٓاْ ءَاذَنَّـٰكَ مَامِنَّا مِن شَهِيدٖ", "Ilayhi yuraddu AAilmu alssaAAati wama takhruju min thamaratin min akmamiha wama tahmilu min ontha wala tadaAAu illa biAAilmihi wayawma yunadeehim ayna shurakaee qaloo athannaka ma minna min shaheedin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 48, "And lost from them will be those they were invoking before, and they will be certain that they have no place of escape", "وَضَلَّ عَنۡهُم مَّا كَانُواْ يَدۡعُونَ مِن قَبۡلُۖ وَظَنُّواْ مَا لَهُم مِّن مَّحِيصٖ", "Wadalla AAanhum ma kanoo yadAAoona min qablu wathannoo ma lahum min maheesin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 49, "Man is not weary of supplication for good [things], but if evil touches him, he is hopeless and despairing", "لَّا يَسۡـَٔمُ ٱلۡإِنسَٰنُ مِن دُعَآءِ ٱلۡخَيۡرِ وَإِن مَّسَّهُ ٱلشَّرُّ فَيَـُٔوسٞ قَنُوطٞ", "La yasamu alinsanu min duAAai alkhayri wain massahu alshsharru fayaoosun qanootun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 50, "And if We let him taste mercy from Us after an adversity which has touched him, he will surely say, ""This is [due] to me, and I do not think the Hour will occur; and [even] if I should be returned to my Lord, indeed, for me there will be with Him the best."" But We will surely inform those who disbelieved about what they did, and We will surely make them taste a massive punishment", "وَلَئِنۡ أَذَقۡنَٰهُ رَحۡمَةٗ مِّنَّا مِنۢ بَعۡدِ ضَرَّآءَ مَسَّتۡهُ لَيَقُولَنَّ هَٰذَا لِي وَمَآ أَظُنُّ ٱلسَّاعَةَ قَآئِمَةٗ وَلَئِن رُّجِعۡتُ إِلَىٰ رَبِّيٓ إِنَّ لِي عِندَهُۥ لَلۡحُسۡنَىٰۚ فَلَنُنَبِّئَنَّ ٱلَّذِينَ كَفَرُواْ بِمَا عَمِلُواْ وَلَنُذِيقَنَّهُم مِّنۡ عَذَابٍ غَلِيظٖ", "Walain athaqnahu rahmatan minna min baAAdi darraa massathu layaqoolanna hatha lee wama athunnu alssaAAata qaimatan walain rujiAAtu ila rabbee inna lee AAindahu lalhusna falanunabbianna allatheena kafaroo bima AAamiloo walanutheeqannahum min AAathabin ghaleethin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 51, "And when We bestow favor upon man, he turns away and distances himself; but when evil touches him, then he is full of extensive supplication", "وَإِذَآ أَنۡعَمۡنَا عَلَى ٱلۡإِنسَٰنِ أَعۡرَضَ وَنَـَٔا بِجَانِبِهِۦ وَإِذَا مَسَّهُ ٱلشَّرُّ فَذُو دُعَآءٍ عَرِيضٖ", "Waitha anAAamna AAala alinsani aAArada wanaa bijanibihi waitha massahu alshsharru fathoo duAAain AAareedin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 52, "Say, ""Have you considered: if the Qur'an is from Allah and you disbelieved in it, who would be more astray than one who is in extreme dissension", "قُلۡ أَرَءَيۡتُمۡ إِن كَانَ مِنۡ عِندِ ٱللَّهِ ثُمَّ كَفَرۡتُم بِهِۦ مَنۡ أَضَلُّ مِمَّنۡ هُوَ فِي شِقَاقِۭ بَعِيدٖ", "Qul araaytum in kana min AAindi Allahi thumma kafartum bihi man adallu mimman huwa fee shiqaqin baAAeedin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 53, "We will show them Our signs in the horizons and within themselves until it becomes clear to them that it is the truth. But is it not sufficient concerning your Lord that He is, over all things, a Witness", "سَنُرِيهِمۡ ءَايَٰتِنَا فِي ٱلۡأٓفَاقِ وَفِيٓ أَنفُسِهِمۡ حَتَّىٰ يَتَبَيَّنَ لَهُمۡ أَنَّهُ ٱلۡحَقُّۗ أَوَلَمۡ يَكۡفِ بِرَبِّكَ أَنَّهُۥ عَلَىٰ كُلِّ شَيۡءٖ شَهِيدٌ", "Sanureehim ayatina fee alafaqi wafee anfusihim hatta yatabayyana lahum annahu alhaqqu awalam yakfi birabbika annahu AAala kulli shayin shaheedun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (41, 54, "Unquestionably, they are in doubt about the meeting with their Lord. Unquestionably He is, of all things, encompassing", "أَلَآ إِنَّهُمۡ فِي مِرۡيَةٖ مِّن لِّقَآءِ رَبِّهِمۡۗ أَلَآ إِنَّهُۥ بِكُلِّ شَيۡءٖ مُّحِيطُۢ", "Ala innahum fee miryatin min liqai rabbihim ala innahu bikulli shayin muheetun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 1, "Ha, Meem", "حمٓ", "Hameem");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 2, "Ayn, Seen, Qaf", "عٓسٓقٓ", "AAaynseenqaf");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 3, "Thus has He revealed to you, [O Muhammad], and to those before you - Allah, the Exalted in Might, the Wise", "كَذَٰلِكَ يُوحِيٓ إِلَيۡكَ وَإِلَى ٱلَّذِينَ مِن قَبۡلِكَ ٱللَّهُ ٱلۡعَزِيزُ ٱلۡحَكِيمُ", "Kathalika yoohee ilayka waila allatheena min qablika Allahu alAAazeezu alhakeemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 4, "To Him belongs whatever is in the heavens and whatever is in the earth, and He is the Most High, the Most Great", "لَهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۖ وَهُوَ ٱلۡعَلِيُّ ٱلۡعَظِيمُ", "Lahu ma fee alssamawati wama fee alardi wahuwa alAAaliyyu alAAatheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 5, "The heavens almost break from above them, and the angels exalt [Allah] with praise of their Lord and ask forgiveness for those on earth. Unquestionably, it is Allah who is the Forgiving, the Merciful", "تَكَادُ ٱلسَّمَٰوَٰتُ يَتَفَطَّرۡنَ مِن فَوۡقِهِنَّۚ وَٱلۡمَلَـٰٓئِكَةُ يُسَبِّحُونَ بِحَمۡدِ رَبِّهِمۡ وَيَسۡتَغۡفِرُونَ لِمَن فِي ٱلۡأَرۡضِۗ أَلَآ إِنَّ ٱللَّهَ هُوَ ٱلۡغَفُورُ ٱلرَّحِيمُ", "Takadu alssamawatu yatafattarna min fawqihinna waalmalaikatu yusabbihoona bihamdi rabbihim wayastaghfiroona liman fee alardi ala inna Allaha huwa alghafooru alrraheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 6, "And those who take as allies other than Him - Allah is [yet] Guardian over them; and you, [O Muhammad], are not over them a manager", "وَٱلَّذِينَ ٱتَّخَذُواْ مِن دُونِهِۦٓ أَوۡلِيَآءَ ٱللَّهُ حَفِيظٌ عَلَيۡهِمۡ وَمَآ أَنتَ عَلَيۡهِم بِوَكِيلٖ", "Waallatheena ittakhathoo min doonihi awliyaa Allahu hafeethun AAalayhim wama anta AAalayhim biwakeelin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 7, "And thus We have revealed to you an Arabic Qur'an that you may warn the Mother of Cities [Makkah] and those around it and warn of the Day of Assembly, about which there is no doubt. A party will be in Paradise and a party in the Blaze", "وَكَذَٰلِكَ أَوۡحَيۡنَآ إِلَيۡكَ قُرۡءَانًا عَرَبِيّٗا لِّتُنذِرَ أُمَّ ٱلۡقُرَىٰ وَمَنۡ حَوۡلَهَا وَتُنذِرَ يَوۡمَ ٱلۡجَمۡعِ لَا رَيۡبَ فِيهِۚ فَرِيقٞ فِي ٱلۡجَنَّةِ وَفَرِيقٞ فِي ٱلسَّعِيرِ", "Wakathalika awhayna ilayka quranan AAarabiyyan litunthira omma alqura waman hawlaha watunthira yawma aljamAAi la rayba feehi fareequn fee aljannati wafareequn fee alssaAAeeri");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 8, "And if Allah willed, He could have made them [of] one religion, but He admits whom He wills into His mercy. And the wrongdoers have not any protector or helper", "وَلَوۡ شَآءَ ٱللَّهُ لَجَعَلَهُمۡ أُمَّةٗ وَٰحِدَةٗ وَلَٰكِن يُدۡخِلُ مَن يَشَآءُ فِي رَحۡمَتِهِۦۚ وَٱلظَّـٰلِمُونَ مَا لَهُم مِّن وَلِيّٖ وَلَا نَصِيرٍ", "Walaw shaa Allahu lajaAAalahum ommatan wahidatan walakin yudkhilu man yashao fee rahmatihi waalththalimoona ma lahum min waliyyin wala naseerin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 9, "Or have they taken protectors [or allies] besides him? But Allah - He is the Protector, and He gives life to the dead, and He is over all things competent", "أَمِ ٱتَّخَذُواْ مِن دُونِهِۦٓ أَوۡلِيَآءَۖ فَٱللَّهُ هُوَ ٱلۡوَلِيُّ وَهُوَ يُحۡيِ ٱلۡمَوۡتَىٰ وَهُوَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ", "Ami ittakhathoo min doonihi awliyaa faAllahu huwa alwaliyyu wahuwa yuhyee almawta wahuwa AAala kulli shayin qadeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 10, "And in anything over which you disagree - its ruling is [to be referred] to Allah. [Say], ""That is Allah, my Lord; upon Him I have relied, and to Him I turn back", "وَمَا ٱخۡتَلَفۡتُمۡ فِيهِ مِن شَيۡءٖ فَحُكۡمُهُۥٓ إِلَى ٱللَّهِۚ ذَٰلِكُمُ ٱللَّهُ رَبِّي عَلَيۡهِ تَوَكَّلۡتُ وَإِلَيۡهِ أُنِيبُ", "Wama ikhtalaftum feehi min shayin fahukmuhu ila Allahi thalikumu Allahu rabbee AAalayhi tawakkaltu wailayhi oneebu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 11, "[He is] Creator of the heavens and the earth. He has made for you from yourselves, mates, and among the cattle, mates; He multiplies you thereby. There is nothing like unto Him, and He is the Hearing, the Seeing", "فَاطِرُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ جَعَلَ لَكُم مِّنۡ أَنفُسِكُمۡ أَزۡوَٰجٗا وَمِنَ ٱلۡأَنۡعَٰمِ أَزۡوَٰجٗا يَذۡرَؤُكُمۡ فِيهِۚ لَيۡسَ كَمِثۡلِهِۦ شَيۡءٞۖ وَهُوَ ٱلسَّمِيعُ ٱلۡبَصِيرُ", "Fatiru alssamawati waalardi jaAAala lakum min anfusikum azwajan wamina alanAAami azwajan yathraokum feehi laysa kamithlihi shayon wahuwa alssameeAAu albaseeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 12, "To Him belong the keys of the heavens and the earth. He extends provision for whom He wills and restricts [it]. Indeed He is, of all things, Knowing", "لَهُۥ مَقَالِيدُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ يَبۡسُطُ ٱلرِّزۡقَ لِمَن يَشَآءُ وَيَقۡدِرُۚ إِنَّهُۥ بِكُلِّ شَيۡءٍ عَلِيمٞ", "Lahu maqaleedu alssamawati waalardi yabsutu alrrizqa liman yashao wayaqdiru innahu bikulli shayin AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 13, "He has ordained for you of religion what He enjoined upon Noah and that which We have revealed to you, [O Muhammad], and what We enjoined upon Abraham and Moses and Jesus - to establish the religion and not be divided therein. Difficult for those who associate others with Allah is that to which you invite them. Allah chooses for Himself whom He wills and guides to Himself whoever turns back [to Him]", "۞شَرَعَ لَكُم مِّنَ ٱلدِّينِ مَا وَصَّىٰ بِهِۦ نُوحٗا وَٱلَّذِيٓ أَوۡحَيۡنَآ إِلَيۡكَ وَمَا وَصَّيۡنَا بِهِۦٓ إِبۡرَٰهِيمَ وَمُوسَىٰ وَعِيسَىٰٓۖ أَنۡ أَقِيمُواْ ٱلدِّينَ وَلَا تَتَفَرَّقُواْ فِيهِۚ كَبُرَ عَلَى ٱلۡمُشۡرِكِينَ مَا تَدۡعُوهُمۡ إِلَيۡهِۚ ٱللَّهُ يَجۡتَبِيٓ إِلَيۡهِ مَن يَشَآءُ وَيَهۡدِيٓ إِلَيۡهِ مَن يُنِيبُ", "SharaAAa lakum mina alddeeni ma wassa bihi noohan waallathee awhayna ilayka wama wassayna bihi ibraheema wamoosa waAAeesa an aqeemoo alddeena wala tatafarraqoo feehi kabura AAala almushrikeena ma tadAAoohum ilayhi Allahu yajtabee ilayhi man yashao wayahdee ilayhi man yuneebu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 14, "And they did not become divided until after knowledge had come to them - out of jealous animosity between themselves. And if not for a word that preceded from your Lord [postponing the penalty] until a specified time, it would have been concluded between them. And indeed, those who were granted inheritance of the Scripture after them are, concerning it, in disquieting doubt", "وَمَا تَفَرَّقُوٓاْ إِلَّا مِنۢ بَعۡدِ مَا جَآءَهُمُ ٱلۡعِلۡمُ بَغۡيَۢا بَيۡنَهُمۡۚ وَلَوۡلَا كَلِمَةٞ سَبَقَتۡ مِن رَّبِّكَ إِلَىٰٓ أَجَلٖ مُّسَمّٗى لَّقُضِيَ بَيۡنَهُمۡۚ وَإِنَّ ٱلَّذِينَ أُورِثُواْ ٱلۡكِتَٰبَ مِنۢ بَعۡدِهِمۡ لَفِي شَكّٖ مِّنۡهُ مُرِيبٖ", "Wama tafarraqoo illa min baAAdi ma jaahumu alAAilmu baghyan baynahum walawla kalimatun sabaqat min rabbika ila ajalin musamman laqudiya baynahum wainna allatheena oorithoo alkitaba min baAAdihim lafee shakkin minhu mureebin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 15, "So to that [religion of Allah] invite, [O Muhammad], and remain on a right course as you are commanded and do not follow their inclinations but say, ""I have believed in what Allah has revealed of the Qur'an, and I have been commanded to do justice among you. Allah is our Lord and your Lord. For us are our deeds, and for you your deeds. There is no [need for] argument between us and you. Allah will bring us together, and to Him is the [final] destination", "فَلِذَٰلِكَ فَٱدۡعُۖ وَٱسۡتَقِمۡ كَمَآ أُمِرۡتَۖ وَلَا تَتَّبِعۡ أَهۡوَآءَهُمۡۖ وَقُلۡ ءَامَنتُ بِمَآ أَنزَلَ ٱللَّهُ مِن كِتَٰبٖۖ وَأُمِرۡتُ لِأَعۡدِلَ بَيۡنَكُمُۖ ٱللَّهُ رَبُّنَا وَرَبُّكُمۡۖ لَنَآ أَعۡمَٰلُنَا وَلَكُمۡ أَعۡمَٰلُكُمۡۖ لَا حُجَّةَ بَيۡنَنَا وَبَيۡنَكُمُۖ ٱللَّهُ يَجۡمَعُ بَيۡنَنَاۖ وَإِلَيۡهِ ٱلۡمَصِيرُ", "Falithalika faodAAu waistaqim kama omirta wala tattabiAA ahwaahum waqul amantu bima anzala Allahu min kitabin waomirtu liaAAdila baynakum Allahu rabbuna warabbukum lana aAAmaluna walakum aAAmalukum la hujjata baynana wabaynakumu Allahu yajmaAAu baynana wailayhi almaseeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 16, "And those who argue concerning Allah after He has been responded to - their argument is invalid with their Lord, and upon them is [His] wrath, and for them is a severe punishment", "وَٱلَّذِينَ يُحَآجُّونَ فِي ٱللَّهِ مِنۢ بَعۡدِ مَا ٱسۡتُجِيبَ لَهُۥ حُجَّتُهُمۡ دَاحِضَةٌ عِندَ رَبِّهِمۡ وَعَلَيۡهِمۡ غَضَبٞ وَلَهُمۡ عَذَابٞ شَدِيدٌ", "Waallatheena yuhajjoona fee Allahi min baAAdi ma istujeeba lahu hujjatuhum dahidatun AAinda rabbihim waAAalayhim ghadabun walahum AAathabun shadeedun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 17, "It is Allah who has sent down the Book in truth and [also] the balance. And what will make you perceive? Perhaps the Hour is near", "ٱللَّهُ ٱلَّذِيٓ أَنزَلَ ٱلۡكِتَٰبَ بِٱلۡحَقِّ وَٱلۡمِيزَانَۗ وَمَا يُدۡرِيكَ لَعَلَّ ٱلسَّاعَةَ قَرِيبٞ", "Allahu allathee anzala alkitaba bialhaqqi waalmeezani wama yudreeka laAAalla alssaAAata qareebun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 18, "Those who do not believe in it are impatient for it, but those who believe are fearful of it and know that it is the truth. Unquestionably, those who dispute concerning the Hour are in extreme error", "يَسۡتَعۡجِلُ بِهَا ٱلَّذِينَ لَا يُؤۡمِنُونَ بِهَاۖ وَٱلَّذِينَ ءَامَنُواْ مُشۡفِقُونَ مِنۡهَا وَيَعۡلَمُونَ أَنَّهَا ٱلۡحَقُّۗ أَلَآ إِنَّ ٱلَّذِينَ يُمَارُونَ فِي ٱلسَّاعَةِ لَفِي ضَلَٰلِۭ بَعِيدٍ", "YastaAAjilu biha allatheena la yuminoona biha waallatheena amanoo mushfiqoona minha wayaAAlamoona annaha alhaqqu ala inna allatheena yumaroona fee alssaAAati lafee dalalin baAAeedin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 19, "Allah is Subtle with His servants; He gives provisions to whom He wills. And He is the Powerful, the Exalted in Might", "ٱللَّهُ لَطِيفُۢ بِعِبَادِهِۦ يَرۡزُقُ مَن يَشَآءُۖ وَهُوَ ٱلۡقَوِيُّ ٱلۡعَزِيزُ", "Allahu lateefun biAAibadihi yarzuqu man yashao wahuwa alqawiyyu alAAazeezu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 20, "Whoever desires the harvest of the Hereafter - We increase for him in his harvest. And whoever desires the harvest of this world - We give him thereof, but there is not for him in the Hereafter any share", "مَن كَانَ يُرِيدُ حَرۡثَ ٱلۡأٓخِرَةِ نَزِدۡ لَهُۥ فِي حَرۡثِهِۦۖ وَمَن كَانَ يُرِيدُ حَرۡثَ ٱلدُّنۡيَا نُؤۡتِهِۦ مِنۡهَا وَمَا لَهُۥ فِي ٱلۡأٓخِرَةِ مِن نَّصِيبٍ", "Man kana yureedu hartha alakhirati nazid lahu fee harthihi waman kana yureedu hartha alddunya nutihi minha wama lahu fee alakhirati min naseebin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 21, "Or have they other deities who have ordained for them a religion to which Allah has not consented? But if not for the decisive word, it would have been concluded between them. And indeed, the wrongdoers will have a painful punishment", "أَمۡ لَهُمۡ شُرَكَـٰٓؤُاْ شَرَعُواْ لَهُم مِّنَ ٱلدِّينِ مَا لَمۡ يَأۡذَنۢ بِهِ ٱللَّهُۚ وَلَوۡلَا كَلِمَةُ ٱلۡفَصۡلِ لَقُضِيَ بَيۡنَهُمۡۗ وَإِنَّ ٱلظَّـٰلِمِينَ لَهُمۡ عَذَابٌ أَلِيمٞ", "Am lahum shurakao sharaAAoo lahum mina alddeeni ma lam yathan bihi Allahu walawla kalimatu alfasli laqudiya baynahum wainna alththalimeena lahum AAathabun aleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 22, "You will see the wrongdoers fearful of what they have earned, and it will [certainly] befall them. And those who have believed and done righteous deeds will be in lush regions of the gardens [in Paradise] having whatever they will in the presence of their Lord. That is what is the great bounty", "تَرَى ٱلظَّـٰلِمِينَ مُشۡفِقِينَ مِمَّا كَسَبُواْ وَهُوَ وَاقِعُۢ بِهِمۡۗ وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ فِي رَوۡضَاتِ ٱلۡجَنَّاتِۖ لَهُم مَّا يَشَآءُونَ عِندَ رَبِّهِمۡۚ ذَٰلِكَ هُوَ ٱلۡفَضۡلُ ٱلۡكَبِيرُ", "Tara alththalimeena mushfiqeena mimma kasaboo wahuwa waqiAAun bihim waallatheena amanoo waAAamiloo alssalihati fee rawdati aljannati lahum ma yashaoona AAinda rabbihim thalika huwa alfadlu alkabeeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 23, "It is that of which Allah gives good tidings to His servants who believe and do righteous deeds. Say, [O Muhammad], ""I do not ask you for this message any payment [but] only good will through kinship."" And whoever commits a good deed - We will increase for him good therein. Indeed, Allah is Forgiving and Appreciative", "ذَٰلِكَ ٱلَّذِي يُبَشِّرُ ٱللَّهُ عِبَادَهُ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِۗ قُل لَّآ أَسۡـَٔلُكُمۡ عَلَيۡهِ أَجۡرًا إِلَّا ٱلۡمَوَدَّةَ فِي ٱلۡقُرۡبَىٰۗ وَمَن يَقۡتَرِفۡ حَسَنَةٗ نَّزِدۡ لَهُۥ فِيهَا حُسۡنًاۚ إِنَّ ٱللَّهَ غَفُورٞ شَكُورٌ", "Thalika allathee yubashshiru Allahu AAibadahu allatheena amanoo waAAamiloo alssalihati qul la asalukum AAalayhi ajran illa almawaddata fee alqurba waman yaqtarif hasanatan nazid lahu feeha husnan inna Allaha ghafoorun shakoorun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 24, "Or do they say, ""He has invented about Allah a lie""? But if Allah willed, He could seal over your heart. And Allah eliminates falsehood and establishes the truth by His words. Indeed, He is Knowing of that within the breasts", "أَمۡ يَقُولُونَ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبٗاۖ فَإِن يَشَإِ ٱللَّهُ يَخۡتِمۡ عَلَىٰ قَلۡبِكَۗ وَيَمۡحُ ٱللَّهُ ٱلۡبَٰطِلَ وَيُحِقُّ ٱلۡحَقَّ بِكَلِمَٰتِهِۦٓۚ إِنَّهُۥ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ", "Am yaqooloona iftara AAala Allahi kathiban fain yashai Allahu yakhtim AAala qalbika wayamhu Allahu albatila wayuhiqqu alhaqqa bikalimatihi innahu AAaleemun bithati alssudoori");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 25, "And it is He who accepts repentance from his servants and pardons misdeeds, and He knows what you do", "وَهُوَ ٱلَّذِي يَقۡبَلُ ٱلتَّوۡبَةَ عَنۡ عِبَادِهِۦ وَيَعۡفُواْ عَنِ ٱلسَّيِّـَٔاتِ وَيَعۡلَمُ مَا تَفۡعَلُونَ", "Wahuwa allathee yaqbalu alttawbata AAan AAibadihi wayaAAfoo AAani alssayyiati wayaAAlamu ma tafAAaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 26, "And He answers [the supplication of] those who have believed and done righteous deeds and increases [for] them from His bounty. But the disbelievers will have a severe punishment", "وَيَسۡتَجِيبُ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ وَيَزِيدُهُم مِّن فَضۡلِهِۦۚ وَٱلۡكَٰفِرُونَ لَهُمۡ عَذَابٞ شَدِيدٞ", "Wayastajeebu allatheena amanoo waAAamiloo alssalihati wayazeeduhum min fadlihi waalkafiroona lahum AAathabun shadeedun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 27, "And if Allah had extended [excessively] provision for His servants, they would have committed tyranny throughout the earth. But He sends [it] down in an amount which He wills. Indeed He is, of His servants, Acquainted and Seeing", "۞وَلَوۡ بَسَطَ ٱللَّهُ ٱلرِّزۡقَ لِعِبَادِهِۦ لَبَغَوۡاْ فِي ٱلۡأَرۡضِ وَلَٰكِن يُنَزِّلُ بِقَدَرٖ مَّا يَشَآءُۚ إِنَّهُۥ بِعِبَادِهِۦ خَبِيرُۢ بَصِيرٞ", "Walaw basata Allahu alrrizqa liAAibadihi labaghaw fee alardi walakin yunazzilu biqadarin ma yashao innahu biAAibadihi khabeerun baseerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 28, "And it is He who sends down the rain after they had despaired and spreads His mercy. And He is the Protector, the Praiseworthy", "وَهُوَ ٱلَّذِي يُنَزِّلُ ٱلۡغَيۡثَ مِنۢ بَعۡدِ مَا قَنَطُواْ وَيَنشُرُ رَحۡمَتَهُۥۚ وَهُوَ ٱلۡوَلِيُّ ٱلۡحَمِيدُ", "Wahuwa allathee yunazzilu alghaytha min baAAdi ma qanatoo wayanshuru rahmatahu wahuwa alwaliyyu alhameedu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 29, "And of his signs is the creation of the heavens and earth and what He has dispersed throughout them of creatures. And He, for gathering them when He wills, is competent", "وَمِنۡ ءَايَٰتِهِۦ خَلۡقُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا بَثَّ فِيهِمَا مِن دَآبَّةٖۚ وَهُوَ عَلَىٰ جَمۡعِهِمۡ إِذَا يَشَآءُ قَدِيرٞ", "Wamin ayatihi khalqu alssamawati waalardi wama baththa feehima min dabbatin wahuwa AAala jamAAihim itha yashao qadeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 30, "And whatever strikes you of disaster - it is for what your hands have earned; but He pardons much", "وَمَآ أَصَٰبَكُم مِّن مُّصِيبَةٖ فَبِمَا كَسَبَتۡ أَيۡدِيكُمۡ وَيَعۡفُواْ عَن كَثِيرٖ", "Wama asabakum min museebatin fabima kasabat aydeekum wayaAAfoo AAan katheerin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 31, "And you will not cause failure [to Allah] upon the earth. And you have not besides Allah any protector or helper", "وَمَآ أَنتُم بِمُعۡجِزِينَ فِي ٱلۡأَرۡضِۖ وَمَا لَكُم مِّن دُونِ ٱللَّهِ مِن وَلِيّٖ وَلَا نَصِيرٖ", "Wama antum bimuAAjizeena fee alardi wama lakum min dooni Allahi min waliyyin wala naseerin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 32, "And of His signs are the ships in the sea, like mountains", "وَمِنۡ ءَايَٰتِهِ ٱلۡجَوَارِ فِي ٱلۡبَحۡرِ كَٱلۡأَعۡلَٰمِ", "Wamin ayatihi aljawari fee albahri kaalaAAlami");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 33, "If He willed, He could still the wind, and they would remain motionless on its surface. Indeed in that are signs for everyone patient and grateful", "إِن يَشَأۡ يُسۡكِنِ ٱلرِّيحَ فَيَظۡلَلۡنَ رَوَاكِدَ عَلَىٰ ظَهۡرِهِۦٓۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّكُلِّ صَبَّارٖ شَكُورٍ", "In yasha yuskini alrreeha fayathlalna rawakida AAala thahrihi inna fee thalika laayatin likulli sabbarin shakoorin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 34, "Or He could destroy them for what they earned; but He pardons much", "أَوۡ يُوبِقۡهُنَّ بِمَا كَسَبُواْ وَيَعۡفُ عَن كَثِيرٖ", "Aw yoobiqhunna bima kasaboo wayaAAfu AAan katheerin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 35, "And [that is so] those who dispute concerning Our signs may know that for them there is no place of escape", "وَيَعۡلَمَ ٱلَّذِينَ يُجَٰدِلُونَ فِيٓ ءَايَٰتِنَا مَا لَهُم مِّن مَّحِيصٖ", "WayaAAlama allatheena yujadiloona fee ayatina ma lahum min maheesin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 36, "So whatever thing you have been given - it is but [for] enjoyment of the worldly life. But what is with Allah is better and more lasting for those who have believed and upon their Lord rely", "فَمَآ أُوتِيتُم مِّن شَيۡءٖ فَمَتَٰعُ ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۚ وَمَا عِندَ ٱللَّهِ خَيۡرٞ وَأَبۡقَىٰ لِلَّذِينَ ءَامَنُواْ وَعَلَىٰ رَبِّهِمۡ يَتَوَكَّلُونَ", "Fama ooteetum min shayin famataAAu alhayati alddunya wama AAinda Allahi khayrun waabqa lillatheena amanoo waAAala rabbihim yatawakkaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 37, "And those who avoid the major sins and immoralities, and when they are angry, they forgive", "وَٱلَّذِينَ يَجۡتَنِبُونَ كَبَـٰٓئِرَ ٱلۡإِثۡمِ وَٱلۡفَوَٰحِشَ وَإِذَا مَا غَضِبُواْ هُمۡ يَغۡفِرُونَ", "Waallatheena yajtaniboona kabaira alithmi waalfawahisha waitha ma ghadiboo hum yaghfiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 38, "And those who have responded to their lord and established prayer and whose affair is [determined by] consultation among themselves, and from what We have provided them, they spend", "وَٱلَّذِينَ ٱسۡتَجَابُواْ لِرَبِّهِمۡ وَأَقَامُواْ ٱلصَّلَوٰةَ وَأَمۡرُهُمۡ شُورَىٰ بَيۡنَهُمۡ وَمِمَّا رَزَقۡنَٰهُمۡ يُنفِقُونَ", "Waallatheena istajaboo lirabbihim waaqamoo alssalata waamruhum shoora baynahum wamimma razaqnahum yunfiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 39, "And those who, when tyranny strikes them, they defend themselves", "وَٱلَّذِينَ إِذَآ أَصَابَهُمُ ٱلۡبَغۡيُ هُمۡ يَنتَصِرُونَ", "Waallatheena itha asabahumu albaghyu hum yantasiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 40, "And the retribution for an evil act is an evil one like it, but whoever pardons and makes reconciliation - his reward is [due] from Allah. Indeed, He does not like wrongdoers", "وَجَزَـٰٓؤُاْ سَيِّئَةٖ سَيِّئَةٞ مِّثۡلُهَاۖ فَمَنۡ عَفَا وَأَصۡلَحَ فَأَجۡرُهُۥ عَلَى ٱللَّهِۚ إِنَّهُۥ لَا يُحِبُّ ٱلظَّـٰلِمِينَ", "Wajazao sayyiatin sayyiatun mithluha faman AAafa waaslaha faajruhu AAala Allahi innahu la yuhibbu alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 41, "And whoever avenges himself after having been wronged - those have not upon them any cause [for blame]", "وَلَمَنِ ٱنتَصَرَ بَعۡدَ ظُلۡمِهِۦ فَأُوْلَـٰٓئِكَ مَا عَلَيۡهِم مِّن سَبِيلٍ", "Walamani intasara baAAda thulmihi faolaika ma AAalayhim min sabeelin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 42, "The cause is only against the ones who wrong the people and tyrannize upon the earth without right. Those will have a painful punishment", "إِنَّمَا ٱلسَّبِيلُ عَلَى ٱلَّذِينَ يَظۡلِمُونَ ٱلنَّاسَ وَيَبۡغُونَ فِي ٱلۡأَرۡضِ بِغَيۡرِ ٱلۡحَقِّۚ أُوْلَـٰٓئِكَ لَهُمۡ عَذَابٌ أَلِيمٞ", "Innama alssabeelu AAala allatheena yathlimoona alnnasa wayabghoona fee alardi bighayri alhaqqi olaika lahum AAathabun aleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 43, "And whoever is patient and forgives - indeed, that is of the matters [requiring] determination", "وَلَمَن صَبَرَ وَغَفَرَ إِنَّ ذَٰلِكَ لَمِنۡ عَزۡمِ ٱلۡأُمُورِ", "Walaman sabara waghafara inna thalika lamin AAazmi alomoori");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 44, "And he whom Allah sends astray - for him there is no protector beyond Him. And you will see the wrongdoers, when they see the punishment, saying, ""Is there for return [to the former world] any way", "وَمَن يُضۡلِلِ ٱللَّهُ فَمَا لَهُۥ مِن وَلِيّٖ مِّنۢ بَعۡدِهِۦۗ وَتَرَى ٱلظَّـٰلِمِينَ لَمَّا رَأَوُاْ ٱلۡعَذَابَ يَقُولُونَ هَلۡ إِلَىٰ مَرَدّٖ مِّن سَبِيلٖ", "Waman yudlili Allahu fama lahu min waliyyin min baAAdihi watara alththalimeena lamma raawoo alAAathaba yaqooloona hal ila maraddin min sabeelin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 45, "And you will see them being exposed to the Fire, humbled from humiliation, looking from [behind] a covert glance. And those who had believed will say, ""Indeed, the [true] losers are the ones who lost themselves and their families on the Day of Resurrection. Unquestionably, the wrongdoers are in an enduring punishment", "وَتَرَىٰهُمۡ يُعۡرَضُونَ عَلَيۡهَا خَٰشِعِينَ مِنَ ٱلذُّلِّ يَنظُرُونَ مِن طَرۡفٍ خَفِيّٖۗ وَقَالَ ٱلَّذِينَ ءَامَنُوٓاْ إِنَّ ٱلۡخَٰسِرِينَ ٱلَّذِينَ خَسِرُوٓاْ أَنفُسَهُمۡ وَأَهۡلِيهِمۡ يَوۡمَ ٱلۡقِيَٰمَةِۗ أَلَآ إِنَّ ٱلظَّـٰلِمِينَ فِي عَذَابٖ مُّقِيمٖ", "Watarahum yuAAradoona AAalayha khashiAAeena mina alththulli yanthuroona min tarfin khafiyyin waqala allatheena amanoo inna alkhasireena allatheena khasiroo anfusahum waahleehim yawma alqiyamati ala inna alththalimeena fee AAathabin muqeemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 46, "And there will not be for them any allies to aid them other than Allah. And whoever Allah sends astray - for him there is no way", "وَمَا كَانَ لَهُم مِّنۡ أَوۡلِيَآءَ يَنصُرُونَهُم مِّن دُونِ ٱللَّهِۗ وَمَن يُضۡلِلِ ٱللَّهُ فَمَا لَهُۥ مِن سَبِيلٍ", "Wama kana lahum min awliyaa yansuroonahum min dooni Allahi waman yudlili Allahu fama lahu min sabeelin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 47, "Respond to your Lord before a Day comes from Allah of which there is no repelling. No refuge will you have that day, nor for you will there be any denial", "ٱسۡتَجِيبُواْ لِرَبِّكُم مِّن قَبۡلِ أَن يَأۡتِيَ يَوۡمٞ لَّا مَرَدَّ لَهُۥ مِنَ ٱللَّهِۚ مَا لَكُم مِّن مَّلۡجَإٖ يَوۡمَئِذٖ وَمَا لَكُم مِّن نَّكِيرٖ", "Istajeeboo lirabbikum min qabli an yatiya yawmun la maradda lahu mina Allahi ma lakum min maljain yawmaithin wama lakum min nakeerin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 48, "But if they turn away - then We have not sent you, [O Muhammad], over them as a guardian; upon you is only [the duty of] notification. And indeed, when We let man taste mercy from us, he rejoices in it; but if evil afflicts him for what his hands have put forth, then indeed, man is ungrateful", "فَإِنۡ أَعۡرَضُواْ فَمَآ أَرۡسَلۡنَٰكَ عَلَيۡهِمۡ حَفِيظًاۖ إِنۡ عَلَيۡكَ إِلَّا ٱلۡبَلَٰغُۗ وَإِنَّآ إِذَآ أَذَقۡنَا ٱلۡإِنسَٰنَ مِنَّا رَحۡمَةٗ فَرِحَ بِهَاۖ وَإِن تُصِبۡهُمۡ سَيِّئَةُۢ بِمَا قَدَّمَتۡ أَيۡدِيهِمۡ فَإِنَّ ٱلۡإِنسَٰنَ كَفُورٞ", "Fain aAAradoo fama arsalnaka AAalayhim hafeethan in AAalayka illa albalaghu wainna itha athaqna alinsana minna rahmatan fariha biha wain tusibhum sayyiatun bima qaddamat aydeehim fainna alinsana kafoorun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 49, "To Allah belongs the dominion of the heavens and the earth; He creates what he wills. He gives to whom He wills female [children], and He gives to whom He wills males", "لِّلَّهِ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ يَخۡلُقُ مَا يَشَآءُۚ يَهَبُ لِمَن يَشَآءُ إِنَٰثٗا وَيَهَبُ لِمَن يَشَآءُ ٱلذُّكُورَ", "Lillahi mulku alssamawati waalardi yakhluqu ma yashao yahabu liman yashao inathan wayahabu liman yashao alththukoora");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 50, "Or He makes them [both] males and females, and He renders whom He wills barren. Indeed, He is Knowing and Competent", "أَوۡ يُزَوِّجُهُمۡ ذُكۡرَانٗا وَإِنَٰثٗاۖ وَيَجۡعَلُ مَن يَشَآءُ عَقِيمًاۚ إِنَّهُۥ عَلِيمٞ قَدِيرٞ", "Aw yuzawwijuhum thukranan wainathan wayajAAalu man yashao AAaqeeman innahu AAaleemun qadeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 51, "And it is not for any human being that Allah should speak to him except by revelation or from behind a partition or that He sends a messenger to reveal, by His permission, what He wills. Indeed, He is Most High and Wise", "۞وَمَا كَانَ لِبَشَرٍ أَن يُكَلِّمَهُ ٱللَّهُ إِلَّا وَحۡيًا أَوۡ مِن وَرَآيِٕ حِجَابٍ أَوۡ يُرۡسِلَ رَسُولٗا فَيُوحِيَ بِإِذۡنِهِۦ مَا يَشَآءُۚ إِنَّهُۥ عَلِيٌّ حَكِيمٞ", "Wama kana libasharin an yukallimahu Allahu illa wahyan aw min warai hijabin aw yursila rasoolan fayoohiya biithnihi ma yashao innahu AAaliyyun hakeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 52, "And thus We have revealed to you an inspiration of Our command. You did not know what is the Book or [what is] faith, but We have made it a light by which We guide whom We will of Our servants. And indeed, [O Muhammad], you guide to a straight path", "وَكَذَٰلِكَ أَوۡحَيۡنَآ إِلَيۡكَ رُوحٗا مِّنۡ أَمۡرِنَاۚ مَا كُنتَ تَدۡرِي مَا ٱلۡكِتَٰبُ وَلَا ٱلۡإِيمَٰنُ وَلَٰكِن جَعَلۡنَٰهُ نُورٗا نَّهۡدِي بِهِۦ مَن نَّشَآءُ مِنۡ عِبَادِنَاۚ وَإِنَّكَ لَتَهۡدِيٓ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ", "Wakathalika awhayna ilayka roohan min amrina ma kunta tadree ma alkitabu wala aleemanu walakin jaAAalnahu nooran nahdee bihi man nashao min AAibadina wainnaka latahdee ila siratin mustaqeemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (42, 53, "The path of Allah, to whom belongs whatever is in the heavens and whatever is on the earth. Unquestionably, to Allah do [all] matters evolve", "صِرَٰطِ ٱللَّهِ ٱلَّذِي لَهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۗ أَلَآ إِلَى ٱللَّهِ تَصِيرُ ٱلۡأُمُورُ", "Sirati Allahi allathee lahu ma fee alssamawati wama fee alardi ala ila Allahi taseeru alomooru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 1, "Ha, Meem", "حمٓ", "Hameem");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 2, "By the clear Book", "وَٱلۡكِتَٰبِ ٱلۡمُبِينِ", "Waalkitabi almubeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 3, "Indeed, We have made it an Arabic Qur'an that you might understand", "إِنَّا جَعَلۡنَٰهُ قُرۡءَٰنًا عَرَبِيّٗا لَّعَلَّكُمۡ تَعۡقِلُونَ", "Inna jaAAalnahu quranan AAarabiyyan laAAallakum taAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 4, "And indeed it is, in the Mother of the Book with Us, exalted and full of wisdom", "وَإِنَّهُۥ فِيٓ أُمِّ ٱلۡكِتَٰبِ لَدَيۡنَا لَعَلِيٌّ حَكِيمٌ", "Wainnahu fee ommi alkitabi ladayna laAAaliyyun hakeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 5, "Then should We turn the message away, disregarding you, because you are a transgressing people", "أَفَنَضۡرِبُ عَنكُمُ ٱلذِّكۡرَ صَفۡحًا أَن كُنتُمۡ قَوۡمٗا مُّسۡرِفِينَ", "Afanadribu AAankumu alththikra safhan an kuntum qawman musrifeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 6, "And how many a prophet We sent among the former peoples", "وَكَمۡ أَرۡسَلۡنَا مِن نَّبِيّٖ فِي ٱلۡأَوَّلِينَ", "Wakam arsalna min nabiyyin fee alawwaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 7, "But there would not come to them a prophet except that they used to ridicule him", "وَمَا يَأۡتِيهِم مِّن نَّبِيٍّ إِلَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ", "Wama yateehim min nabiyyin illa kanoo bihi yastahzioona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 8, "And We destroyed greater than them in [striking] power, and the example of the former peoples has preceded", "فَأَهۡلَكۡنَآ أَشَدَّ مِنۡهُم بَطۡشٗا وَمَضَىٰ مَثَلُ ٱلۡأَوَّلِينَ", "Faahlakna ashadda minhum batshan wamada mathalu alawwaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 9, "And if you should ask them, ""Who has created the heavens and the earth?"" they would surely say, ""They were created by the Exalted in Might, the Knowing", "وَلَئِن سَأَلۡتَهُم مَّنۡ خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ لَيَقُولُنَّ خَلَقَهُنَّ ٱلۡعَزِيزُ ٱلۡعَلِيمُ", "Walain saaltahum man khalaqa alssamawati waalarda layaqoolunna khalaqahunna alAAazeezu alAAaleemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 10, "[The one] who has made for you the earth a bed and made for you upon it roads that you might be guided", "ٱلَّذِي جَعَلَ لَكُمُ ٱلۡأَرۡضَ مَهۡدٗا وَجَعَلَ لَكُمۡ فِيهَا سُبُلٗا لَّعَلَّكُمۡ تَهۡتَدُونَ", "Allathee jaAAala lakumu alarda mahdan wajaAAala lakum feeha subulan laAAallakum tahtadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 11, "And who sends down rain from the sky in measured amounts, and We revive thereby a dead land - thus will you be brought forth", "وَٱلَّذِي نَزَّلَ مِنَ ٱلسَّمَآءِ مَآءَۢ بِقَدَرٖ فَأَنشَرۡنَا بِهِۦ بَلۡدَةٗ مَّيۡتٗاۚ كَذَٰلِكَ تُخۡرَجُونَ", "Waallathee nazzala mina alssamai maan biqadarin faansharna bihi baldatan maytan kathalika tukhrajoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 12, "And who created the species, all of them, and has made for you of ships and animals those which you mount", "وَٱلَّذِي خَلَقَ ٱلۡأَزۡوَٰجَ كُلَّهَا وَجَعَلَ لَكُم مِّنَ ٱلۡفُلۡكِ وَٱلۡأَنۡعَٰمِ مَا تَرۡكَبُونَ", "Waallathee khalaqa alazwaja kullaha wajaAAala lakum mina alfulki waalanAAami ma tarkaboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 13, "That you may settle yourselves upon their backs and then remember the favor of your Lord when you have settled upon them and say. ""Exalted is He who has subjected this to us, and we could not have [otherwise] subdued it", "لِتَسۡتَوُۥاْ عَلَىٰ ظُهُورِهِۦ ثُمَّ تَذۡكُرُواْ نِعۡمَةَ رَبِّكُمۡ إِذَا ٱسۡتَوَيۡتُمۡ عَلَيۡهِ وَتَقُولُواْ سُبۡحَٰنَ ٱلَّذِي سَخَّرَ لَنَا هَٰذَا وَمَا كُنَّا لَهُۥ مُقۡرِنِينَ", "Litastawoo AAala thuhoorihi thumma tathkuroo niAAmata rabbikum itha istawaytum AAalayhi wataqooloo subhana allathee sakhkhara lana hatha wama kunna lahu muqrineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 14, "And indeed we, to our Lord, will [surely] return", "وَإِنَّآ إِلَىٰ رَبِّنَا لَمُنقَلِبُونَ", "Wainna ila rabbina lamunqaliboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 15, "But they have attributed to Him from His servants a portion. Indeed, man is clearly ungrateful", "وَجَعَلُواْ لَهُۥ مِنۡ عِبَادِهِۦ جُزۡءًاۚ إِنَّ ٱلۡإِنسَٰنَ لَكَفُورٞ مُّبِينٌ", "WajaAAaloo lahu min AAibadihi juzan inna alinsana lakafoorun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 16, "Or has He taken, out of what He has created, daughters and chosen you for [having] sons", "أَمِ ٱتَّخَذَ مِمَّا يَخۡلُقُ بَنَاتٖ وَأَصۡفَىٰكُم بِٱلۡبَنِينَ", "Ami ittakhatha mimma yakhluqu banatin waasfakum bialbaneena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 17, "And when one of them is given good tidings of that which he attributes to the Most Merciful in comparison, his face becomes dark, and he suppresses grief", "وَإِذَا بُشِّرَ أَحَدُهُم بِمَا ضَرَبَ لِلرَّحۡمَٰنِ مَثَلٗا ظَلَّ وَجۡهُهُۥ مُسۡوَدّٗا وَهُوَ كَظِيمٌ", "Waitha bushshira ahaduhum bima daraba lilrrahmani mathalan thalla wajhuhu muswaddan wahuwa katheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 18, "So is one brought up in ornaments while being during conflict unevident [attributed to Allah]", "أَوَمَن يُنَشَّؤُاْ فِي ٱلۡحِلۡيَةِ وَهُوَ فِي ٱلۡخِصَامِ غَيۡرُ مُبِينٖ", "Awaman yunashshao fee alhilyati wahuwa fee alkhisami ghayru mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 19, "And they have made the angels, who are servants of the Most Merciful, females. Did they witness their creation? Their testimony will be recorded, and they will be questioned", "وَجَعَلُواْ ٱلۡمَلَـٰٓئِكَةَ ٱلَّذِينَ هُمۡ عِبَٰدُ ٱلرَّحۡمَٰنِ إِنَٰثًاۚ أَشَهِدُواْ خَلۡقَهُمۡۚ سَتُكۡتَبُ شَهَٰدَتُهُمۡ وَيُسۡـَٔلُونَ", "WajaAAaloo almalaikata allatheena hum AAibadu alrrahmani inathan ashahidoo khalqahum satuktabu shahadatuhum wayusaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 20, "And they said, ""If the Most Merciful had willed, we would not have worshipped them."" They have of that no knowledge. They are not but falsifying", "وَقَالُواْ لَوۡ شَآءَ ٱلرَّحۡمَٰنُ مَا عَبَدۡنَٰهُمۗ مَّا لَهُم بِذَٰلِكَ مِنۡ عِلۡمٍۖ إِنۡ هُمۡ إِلَّا يَخۡرُصُونَ", "Waqaloo law shaa alrrahmanu ma AAabadnahum ma lahum bithalika min AAilmin in hum illa yakhrusoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 21, "Or have We given them a book before the Qur'an to which they are adhering", "أَمۡ ءَاتَيۡنَٰهُمۡ كِتَٰبٗا مِّن قَبۡلِهِۦ فَهُم بِهِۦ مُسۡتَمۡسِكُونَ", "Am ataynahum kitaban min qablihi fahum bihi mustamsikoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 22, "Rather, they say, ""Indeed, we found our fathers upon a religion, and we are in their footsteps [rightly] guided", "بَلۡ قَالُوٓاْ إِنَّا وَجَدۡنَآ ءَابَآءَنَا عَلَىٰٓ أُمَّةٖ وَإِنَّا عَلَىٰٓ ءَاثَٰرِهِم مُّهۡتَدُونَ", "Bal qaloo inna wajadna abaana AAala ommatin wainna AAala atharihim muhtadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 23, "And similarly, We did not send before you any warner into a city except that its affluent said, ""Indeed, we found our fathers upon a religion, and we are, in their footsteps, following", "وَكَذَٰلِكَ مَآ أَرۡسَلۡنَا مِن قَبۡلِكَ فِي قَرۡيَةٖ مِّن نَّذِيرٍ إِلَّا قَالَ مُتۡرَفُوهَآ إِنَّا وَجَدۡنَآ ءَابَآءَنَا عَلَىٰٓ أُمَّةٖ وَإِنَّا عَلَىٰٓ ءَاثَٰرِهِم مُّقۡتَدُونَ", "Wakathalika ma arsalna min qablika fee qaryatin min natheerin illa qala mutrafooha inna wajadna abaana AAala ommatin wainna AAala atharihim muqtadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 24, "[Each warner] said, ""Even if I brought you better guidance than that [religion] upon which you found your fathers?"" They said, ""Indeed we, in that with which you were sent, are disbelievers", "۞قَٰلَ أَوَلَوۡ جِئۡتُكُم بِأَهۡدَىٰ مِمَّا وَجَدتُّمۡ عَلَيۡهِ ءَابَآءَكُمۡۖ قَالُوٓاْ إِنَّا بِمَآ أُرۡسِلۡتُم بِهِۦ كَٰفِرُونَ", "Qala awalaw jitukum biahda mimma wajadtum AAalayhi abaakum qaloo inna bima orsiltum bihi kafiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 25, "So we took retribution from them; then see how was the end of the deniers", "فَٱنتَقَمۡنَا مِنۡهُمۡۖ فَٱنظُرۡ كَيۡفَ كَانَ عَٰقِبَةُ ٱلۡمُكَذِّبِينَ", "Faintaqamna minhum faonthur kayfa kana AAaqibatu almukaththibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 26, "And [mention, O Muhammad], when Abraham said to his father and his people, ""Indeed, I am disassociated from that which you worship", "وَإِذۡ قَالَ إِبۡرَٰهِيمُ لِأَبِيهِ وَقَوۡمِهِۦٓ إِنَّنِي بَرَآءٞ مِّمَّا تَعۡبُدُونَ", "Waith qala ibraheemu liabeehi waqawmihi innanee baraon mimma taAAbudoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 27, "Except for He who created me; and indeed, He will guide me", "إِلَّا ٱلَّذِي فَطَرَنِي فَإِنَّهُۥ سَيَهۡدِينِ", "Illa allathee fataranee fainnahu sayahdeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 28, "And he made it a word remaining among his descendants that they might return [to it]", "وَجَعَلَهَا كَلِمَةَۢ بَاقِيَةٗ فِي عَقِبِهِۦ لَعَلَّهُمۡ يَرۡجِعُونَ", "WajaAAalaha kalimatan baqiyatan fee AAaqibihi laAAallahum yarjiAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 29, "However, I gave enjoyment to these [people of Makkah] and their fathers until there came to them the truth and a clear Messenger", "بَلۡ مَتَّعۡتُ هَـٰٓؤُلَآءِ وَءَابَآءَهُمۡ حَتَّىٰ جَآءَهُمُ ٱلۡحَقُّ وَرَسُولٞ مُّبِينٞ", "Bal mattaAAtu haolai waabaahum hatta jaahumu alhaqqu warasoolun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 30, "But when the truth came to them, they said, ""This is magic, and indeed we are, concerning it, disbelievers", "وَلَمَّا جَآءَهُمُ ٱلۡحَقُّ قَالُواْ هَٰذَا سِحۡرٞ وَإِنَّا بِهِۦ كَٰفِرُونَ", "Walamma jaahumu alhaqqu qaloo hatha sihrun wainna bihi kafiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 31, "And they said, ""Why was this Qur'an not sent down upon a great man from [one of] the two cities", "وَقَالُواْ لَوۡلَا نُزِّلَ هَٰذَا ٱلۡقُرۡءَانُ عَلَىٰ رَجُلٖ مِّنَ ٱلۡقَرۡيَتَيۡنِ عَظِيمٍ", "Waqaloo lawla nuzzila hatha alquranu AAala rajulin mina alqaryatayni AAatheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 32, "Do they distribute the mercy of your Lord? It is We who have apportioned among them their livelihood in the life of this world and have raised some of them above others in degrees [of rank] that they may make use of one another for service. But the mercy of your Lord is better than whatever they accumulate", "أَهُمۡ يَقۡسِمُونَ رَحۡمَتَ رَبِّكَۚ نَحۡنُ قَسَمۡنَا بَيۡنَهُم مَّعِيشَتَهُمۡ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۚ وَرَفَعۡنَا بَعۡضَهُمۡ فَوۡقَ بَعۡضٖ دَرَجَٰتٖ لِّيَتَّخِذَ بَعۡضُهُم بَعۡضٗا سُخۡرِيّٗاۗ وَرَحۡمَتُ رَبِّكَ خَيۡرٞ مِّمَّا يَجۡمَعُونَ", "Ahum yaqsimoona rahmata rabbika nahnu qasamna baynahum maAAeeshatahum fee alhayati alddunya warafaAAna baAAdahum fawqa baAAdin darajatin liyattakhitha baAAduhum baAAdan sukhriyyan warahmatu rabbika khayrun mimma yajmaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 33, "And if it were not that the people would become one community [of disbelievers], We would have made for those who disbelieve in the Most Merciful - for their houses - ceilings and stairways of silver upon which to mount", "وَلَوۡلَآ أَن يَكُونَ ٱلنَّاسُ أُمَّةٗ وَٰحِدَةٗ لَّجَعَلۡنَا لِمَن يَكۡفُرُ بِٱلرَّحۡمَٰنِ لِبُيُوتِهِمۡ سُقُفٗا مِّن فِضَّةٖ وَمَعَارِجَ عَلَيۡهَا يَظۡهَرُونَ", "Walawla an yakoona alnnasu ommatan wahidatan lajaAAalna liman yakfuru bialrrahmani libuyootihim suqufan min fiddatin wamaAAarija AAalayha yathharoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 34, "And for their houses - doors and couches [of silver] upon which to recline", "وَلِبُيُوتِهِمۡ أَبۡوَٰبٗا وَسُرُرًا عَلَيۡهَا يَتَّكِـُٔونَ", "Walibuyootihim abwaban wasururan AAalayha yattakioona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 35, "And gold ornament. But all that is not but the enjoyment of worldly life. And the Hereafter with your Lord is for the righteous", "وَزُخۡرُفٗاۚ وَإِن كُلُّ ذَٰلِكَ لَمَّا مَتَٰعُ ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۚ وَٱلۡأٓخِرَةُ عِندَ رَبِّكَ لِلۡمُتَّقِينَ", "Wazukhrufan wain kullu thalika lamma mataAAu alhayati alddunya waalakhiratu AAinda rabbika lilmuttaqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 36, "And whoever is blinded from remembrance of the Most Merciful - We appoint for him a devil, and he is to him a companion", "وَمَن يَعۡشُ عَن ذِكۡرِ ٱلرَّحۡمَٰنِ نُقَيِّضۡ لَهُۥ شَيۡطَٰنٗا فَهُوَ لَهُۥ قَرِينٞ", "Waman yaAAshu AAan thikri alrrahmani nuqayyid lahu shaytanan fahuwa lahu qareenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 37, "And indeed, the devils avert them from the way [of guidance] while they think that they are [rightly] guided", "وَإِنَّهُمۡ لَيَصُدُّونَهُمۡ عَنِ ٱلسَّبِيلِ وَيَحۡسَبُونَ أَنَّهُم مُّهۡتَدُونَ", "Wainnahum layasuddoonahum AAani alssabeeli wayahsaboona annahum muhtadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 38, "Until, when he comes to Us [at Judgement], he says [to his companion], ""Oh, I wish there was between me and you the distance between the east and west - how wretched a companion", "حَتَّىٰٓ إِذَا جَآءَنَا قَالَ يَٰلَيۡتَ بَيۡنِي وَبَيۡنَكَ بُعۡدَ ٱلۡمَشۡرِقَيۡنِ فَبِئۡسَ ٱلۡقَرِينُ", "Hatta itha jaana qala ya layta baynee wabaynaka buAAda almashriqayni fabisa alqareenu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 39, "And never will it benefit you that Day, when you have wronged, that you are [all] sharing in the punishment", "وَلَن يَنفَعَكُمُ ٱلۡيَوۡمَ إِذ ظَّلَمۡتُمۡ أَنَّكُمۡ فِي ٱلۡعَذَابِ مُشۡتَرِكُونَ", "Walan yanfaAAakumu alyawma ith thalamtum annakum fee alAAathabi mushtarikoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 40, "Then will you make the deaf hear, [O Muhammad], or guide the blind or he who is in clear error", "أَفَأَنتَ تُسۡمِعُ ٱلصُّمَّ أَوۡ تَهۡدِي ٱلۡعُمۡيَ وَمَن كَانَ فِي ضَلَٰلٖ مُّبِينٖ", "Afaanta tusmiAAu alssumma aw tahdee alAAumya waman kana fee dalalin mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 41, "And whether [or not] We take you away [in death], indeed, We will take retribution upon them", "فَإِمَّا نَذۡهَبَنَّ بِكَ فَإِنَّا مِنۡهُم مُّنتَقِمُونَ", "Faimma nathhabanna bika fainna minhum muntaqimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 42, "Or whether [or not] We show you that which We have promised them, indeed, We are Perfect in Ability", "أَوۡ نُرِيَنَّكَ ٱلَّذِي وَعَدۡنَٰهُمۡ فَإِنَّا عَلَيۡهِم مُّقۡتَدِرُونَ", "Aw nuriyannaka allathee waAAadnahum fainna AAalayhim muqtadiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 43, "So adhere to that which is revealed to you. Indeed, you are on a straight path", "فَٱسۡتَمۡسِكۡ بِٱلَّذِيٓ أُوحِيَ إِلَيۡكَۖ إِنَّكَ عَلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ", "Faistamsik biallathee oohiya ilayka innaka AAala siratin mustaqeemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 44, "And indeed, it is a remembrance for you and your people, and you [all] are going to be questioned", "وَإِنَّهُۥ لَذِكۡرٞ لَّكَ وَلِقَوۡمِكَۖ وَسَوۡفَ تُسۡـَٔلُونَ", "Wainnahu lathikrun laka waliqawmika wasawfa tusaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 45, "And ask those We sent before you of Our messengers; have We made besides the Most Merciful deities to be worshipped", "وَسۡـَٔلۡ مَنۡ أَرۡسَلۡنَا مِن قَبۡلِكَ مِن رُّسُلِنَآ أَجَعَلۡنَا مِن دُونِ ٱلرَّحۡمَٰنِ ءَالِهَةٗ يُعۡبَدُونَ", "Waisal man arsalna min qablika min rusulina ajaAAalna min dooni alrrahmani alihatan yuAAbadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 46, "And certainly did We send Moses with Our signs to Pharaoh and his establishment, and he said, ""Indeed, I am the messenger of the Lord of the worlds", "وَلَقَدۡ أَرۡسَلۡنَا مُوسَىٰ بِـَٔايَٰتِنَآ إِلَىٰ فِرۡعَوۡنَ وَمَلَإِيْهِۦ فَقَالَ إِنِّي رَسُولُ رَبِّ ٱلۡعَٰلَمِينَ", "Walaqad arsalna moosa biayatina ila firAAawna wamalaihi faqala innee rasoolu rabbi alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 47, "But when he brought them Our signs, at once they laughed at them", "فَلَمَّا جَآءَهُم بِـَٔايَٰتِنَآ إِذَا هُم مِّنۡهَا يَضۡحَكُونَ", "Falamma jaahum biayatina itha hum minha yadhakoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 48, "And We showed them not a sign except that it was greater than its sister, and We seized them with affliction that perhaps they might return [to faith]", "وَمَا نُرِيهِم مِّنۡ ءَايَةٍ إِلَّا هِيَ أَكۡبَرُ مِنۡ أُخۡتِهَاۖ وَأَخَذۡنَٰهُم بِٱلۡعَذَابِ لَعَلَّهُمۡ يَرۡجِعُونَ", "Wama nureehim min ayatin illa hiya akbaru min okhtiha waakhathnahum bialAAathabi laAAallahum yarjiAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 49, "And they said [to Moses], ""O magician, invoke for us your Lord by what He has promised you. Indeed, we will be guided", "وَقَالُواْ يَـٰٓأَيُّهَ ٱلسَّاحِرُ ٱدۡعُ لَنَا رَبَّكَ بِمَا عَهِدَ عِندَكَ إِنَّنَا لَمُهۡتَدُونَ", "Waqaloo ya ayyuha alsahiru odAAu lana rabbaka bima AAahida AAindaka innana lamuhtadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 50, "But when We removed from them the affliction, at once they broke their word", "فَلَمَّا كَشَفۡنَا عَنۡهُمُ ٱلۡعَذَابَ إِذَا هُمۡ يَنكُثُونَ", "Falamma kashafna AAanhumu alAAathaba itha hum yankuthoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 51, "And Pharaoh called out among his people; he said, ""O my people, does not the kingdom of Egypt belong to me, and these rivers flowing beneath me; then do you not see", "وَنَادَىٰ فِرۡعَوۡنُ فِي قَوۡمِهِۦ قَالَ يَٰقَوۡمِ أَلَيۡسَ لِي مُلۡكُ مِصۡرَ وَهَٰذِهِ ٱلۡأَنۡهَٰرُ تَجۡرِي مِن تَحۡتِيٓۚ أَفَلَا تُبۡصِرُونَ", "Wanada firAAawnu fee qawmihi qala ya qawmi alaysa lee mulku misra wahathihi alanharu tajree min tahtee afala tubsiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 52, "Or am I [not] better than this one who is insignificant and hardly makes himself clear", "أَمۡ أَنَا۠ خَيۡرٞ مِّنۡ هَٰذَا ٱلَّذِي هُوَ مَهِينٞ وَلَا يَكَادُ يُبِينُ", "Am ana khayrun min hatha allathee huwa maheenun wala yakadu yubeenu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 53, "Then why have there not been placed upon him bracelets of gold or come with him the angels in conjunction", "فَلَوۡلَآ أُلۡقِيَ عَلَيۡهِ أَسۡوِرَةٞ مِّن ذَهَبٍ أَوۡ جَآءَ مَعَهُ ٱلۡمَلَـٰٓئِكَةُ مُقۡتَرِنِينَ", "Falawla olqiya AAalayhi aswiratun min thahabin aw jaa maAAahu almalaikatu muqtarineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 54, "So he bluffed his people, and they obeyed him. Indeed, they were [themselves] a people defiantly disobedient [of Allah]", "فَٱسۡتَخَفَّ قَوۡمَهُۥ فَأَطَاعُوهُۚ إِنَّهُمۡ كَانُواْ قَوۡمٗا فَٰسِقِينَ", "Faistakhaffa qawmahu faataAAoohu innahum kanoo qawman fasiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 55, "And when they angered Us, We took retribution from them and drowned them all", "فَلَمَّآ ءَاسَفُونَا ٱنتَقَمۡنَا مِنۡهُمۡ فَأَغۡرَقۡنَٰهُمۡ أَجۡمَعِينَ", "Falamma asafoona intaqamna minhum faaghraqnahum ajmaAAeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 56, "And We made them a precedent and an example for the later peoples", "فَجَعَلۡنَٰهُمۡ سَلَفٗا وَمَثَلٗا لِّلۡأٓخِرِينَ", "FajaAAalnahum salafan wamathalan lilakhireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 57, "And when the son of Mary was presented as an example, immediately your people laughed aloud", "۞وَلَمَّا ضُرِبَ ٱبۡنُ مَرۡيَمَ مَثَلًا إِذَا قَوۡمُكَ مِنۡهُ يَصِدُّونَ", "Walamma duriba ibnu maryama mathalan itha qawmuka minhu yasiddoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 58, "And they said, ""Are your gods better, or is he?"" They did not present the comparison except for [mere] argument. But, [in fact], they are a people prone to dispute", "وَقَالُوٓاْ ءَأَٰلِهَتُنَا خَيۡرٌ أَمۡ هُوَۚ مَا ضَرَبُوهُ لَكَ إِلَّا جَدَلَۢاۚ بَلۡ هُمۡ قَوۡمٌ خَصِمُونَ", "Waqaloo aalihatuna khayrun am huwa ma daraboohu laka illa jadalan bal hum qawmun khasimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 59, "Jesus was not but a servant upon whom We bestowed favor, and We made him an example for the Children of Israel", "إِنۡ هُوَ إِلَّا عَبۡدٌ أَنۡعَمۡنَا عَلَيۡهِ وَجَعَلۡنَٰهُ مَثَلٗا لِّبَنِيٓ إِسۡرَـٰٓءِيلَ", "In huwa illa AAabdun anAAamna AAalayhi wajaAAalnahu mathalan libanee israeela");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 60, "And if We willed, We could have made [instead] of you angels succeeding [one another] on the earth", "وَلَوۡ نَشَآءُ لَجَعَلۡنَا مِنكُم مَّلَـٰٓئِكَةٗ فِي ٱلۡأَرۡضِ يَخۡلُفُونَ", "Walaw nashao lajaAAalna minkum malaikatan fee alardi yakhlufoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 61, "And indeed, Jesus will be [a sign for] knowledge of the Hour, so be not in doubt of it, and follow Me. This is a straight path", "وَإِنَّهُۥ لَعِلۡمٞ لِّلسَّاعَةِ فَلَا تَمۡتَرُنَّ بِهَا وَٱتَّبِعُونِۚ هَٰذَا صِرَٰطٞ مُّسۡتَقِيمٞ", "Wainnahu laAAilmun lilssaAAati fala tamtarunna biha waittabiAAooni hatha siratun mustaqeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 62, "And never let Satan avert you. Indeed, he is to you a clear enemy", "وَلَا يَصُدَّنَّكُمُ ٱلشَّيۡطَٰنُۖ إِنَّهُۥ لَكُمۡ عَدُوّٞ مُّبِينٞ", "Wala yasuddannakumu alshshaytanu innahu lakum AAaduwwun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 63, "And when Jesus brought clear proofs, he said, ""I have come to you with wisdom and to make clear to you some of that over which you differ, so fear Allah and obey me", "وَلَمَّا جَآءَ عِيسَىٰ بِٱلۡبَيِّنَٰتِ قَالَ قَدۡ جِئۡتُكُم بِٱلۡحِكۡمَةِ وَلِأُبَيِّنَ لَكُم بَعۡضَ ٱلَّذِي تَخۡتَلِفُونَ فِيهِۖ فَٱتَّقُواْ ٱللَّهَ وَأَطِيعُونِ", "Walamma jaa AAeesa bialbayyinati qala qad jitukum bialhikmati waliobayyina lakum baAAda allathee takhtalifoona feehi faittaqoo Allaha waateeAAooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 64, "Indeed, Allah is my Lord and your Lord, so worship Him. This is a straight path", "إِنَّ ٱللَّهَ هُوَ رَبِّي وَرَبُّكُمۡ فَٱعۡبُدُوهُۚ هَٰذَا صِرَٰطٞ مُّسۡتَقِيمٞ", "Inna Allaha huwa rabbee warabbukum faoAAbudoohu hatha siratun mustaqeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 65, "But the denominations from among them differed [and separated], so woe to those who have wronged from the punishment of a painful Day", "فَٱخۡتَلَفَ ٱلۡأَحۡزَابُ مِنۢ بَيۡنِهِمۡۖ فَوَيۡلٞ لِّلَّذِينَ ظَلَمُواْ مِنۡ عَذَابِ يَوۡمٍ أَلِيمٍ", "Faikhtalafa alahzabu min baynihim fawaylun lillatheena thalamoo min AAathabi yawmin aleemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 66, "Are they waiting except for the Hour to come upon them suddenly while they perceive not", "هَلۡ يَنظُرُونَ إِلَّا ٱلسَّاعَةَ أَن تَأۡتِيَهُم بَغۡتَةٗ وَهُمۡ لَا يَشۡعُرُونَ", "Hal yanthuroona illa alssaAAata an tatiyahum baghtatan wahum la yashAAuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 67, "Close friends, that Day, will be enemies to each other, except for the righteous", "ٱلۡأَخِلَّآءُ يَوۡمَئِذِۭ بَعۡضُهُمۡ لِبَعۡضٍ عَدُوٌّ إِلَّا ٱلۡمُتَّقِينَ", "Alakhillao yawmaithin baAAduhum libaAAdin AAaduwwun illa almuttaqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 68, "[To whom Allah will say], ""O My servants, no fear will there be concerning you this Day, nor will you grieve", "يَٰعِبَادِ لَا خَوۡفٌ عَلَيۡكُمُ ٱلۡيَوۡمَ وَلَآ أَنتُمۡ تَحۡزَنُونَ", "Ya AAibadi la khawfun AAalaykumu alyawma wala antum tahzanoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 69, "[You] who believed in Our verses and were Muslims", "ٱلَّذِينَ ءَامَنُواْ بِـَٔايَٰتِنَا وَكَانُواْ مُسۡلِمِينَ", "Allatheena amanoo biayatina wakanoo muslimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 70, "Enter Paradise, you and your kinds, delighted", "ٱدۡخُلُواْ ٱلۡجَنَّةَ أَنتُمۡ وَأَزۡوَٰجُكُمۡ تُحۡبَرُونَ", "Odkhuloo aljannata antum waazwajukum tuhbaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 71, "Circulated among them will be plates and vessels of gold. And therein is whatever the souls desire and [what] delights the eyes, and you will abide therein eternally", "يُطَافُ عَلَيۡهِم بِصِحَافٖ مِّن ذَهَبٖ وَأَكۡوَابٖۖ وَفِيهَا مَا تَشۡتَهِيهِ ٱلۡأَنفُسُ وَتَلَذُّ ٱلۡأَعۡيُنُۖ وَأَنتُمۡ فِيهَا خَٰلِدُونَ", "Yutafu AAalayhim bisihafin min thahabin waakwabin wafeeha ma tashtaheehi alanfusu watalaththu alaAAyunu waantum feeha khalidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 72, "And that is Paradise which you are made to inherit for what you used to do", "وَتِلۡكَ ٱلۡجَنَّةُ ٱلَّتِيٓ أُورِثۡتُمُوهَا بِمَا كُنتُمۡ تَعۡمَلُونَ", "Watilka aljannatu allatee oorithtumooha bima kuntum taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 73, "For you therein is much fruit from which you will eat", "لَكُمۡ فِيهَا فَٰكِهَةٞ كَثِيرَةٞ مِّنۡهَا تَأۡكُلُونَ", "Lakum feeha fakihatun katheeratun minha takuloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 74, "Indeed, the criminals will be in the punishment of Hell, abiding eternally", "إِنَّ ٱلۡمُجۡرِمِينَ فِي عَذَابِ جَهَنَّمَ خَٰلِدُونَ", "Inna almujrimeena fee AAathabi jahannama khalidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 75, "It will not be allowed to subside for them, and they, therein, are in despair", "لَا يُفَتَّرُ عَنۡهُمۡ وَهُمۡ فِيهِ مُبۡلِسُونَ", "La yufattaru AAanhum wahum feehi mublisoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 76, "And We did not wrong them, but it was they who were the wrongdoers", "وَمَا ظَلَمۡنَٰهُمۡ وَلَٰكِن كَانُواْ هُمُ ٱلظَّـٰلِمِينَ", "Wama thalamnahum walakin kanoo humu alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 77, "And they will call, ""O Malik, let your Lord put an end to us!"" He will say, ""Indeed, you will remain", "وَنَادَوۡاْ يَٰمَٰلِكُ لِيَقۡضِ عَلَيۡنَا رَبُّكَۖ قَالَ إِنَّكُم مَّـٰكِثُونَ", "Wanadaw ya maliku liyaqdi AAalayna rabbuka qala innakum makithoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 78, "We had certainly brought you the truth, but most of you, to the truth, were averse", "لَقَدۡ جِئۡنَٰكُم بِٱلۡحَقِّ وَلَٰكِنَّ أَكۡثَرَكُمۡ لِلۡحَقِّ كَٰرِهُونَ", "Laqad jinakum bialhaqqi walakinna aktharakum lilhaqqi karihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 79, "Or have they devised [some] affair? But indeed, We are devising [a plan]", "أَمۡ أَبۡرَمُوٓاْ أَمۡرٗا فَإِنَّا مُبۡرِمُونَ", "Am abramoo amran fainna mubrimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 80, "Or do they think that We hear not their secrets and their private conversations? Yes, [We do], and Our messengers are with them recording", "أَمۡ يَحۡسَبُونَ أَنَّا لَا نَسۡمَعُ سِرَّهُمۡ وَنَجۡوَىٰهُمۚ بَلَىٰ وَرُسُلُنَا لَدَيۡهِمۡ يَكۡتُبُونَ", "Am yahsaboona anna la nasmaAAu sirrahum wanajwahum bala warusuluna ladayhim yaktuboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 81, "Say, [O Muhammad], ""If the Most Merciful had a son, then I would be the first of [his] worshippers", "قُلۡ إِن كَانَ لِلرَّحۡمَٰنِ وَلَدٞ فَأَنَا۠ أَوَّلُ ٱلۡعَٰبِدِينَ", "Qul in kana lilrrahmani waladun faana awwalu alAAabideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 82, "Exalted is the Lord of the heavens and the earth, Lord of the Throne, above what they describe", "سُبۡحَٰنَ رَبِّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ رَبِّ ٱلۡعَرۡشِ عَمَّا يَصِفُونَ", "Subhana rabbi alssamawati waalardi rabbi alAAarshi AAamma yasifoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 83, "So leave them to converse vainly and amuse themselves until they meet their Day which they are promised", "فَذَرۡهُمۡ يَخُوضُواْ وَيَلۡعَبُواْ حَتَّىٰ يُلَٰقُواْ يَوۡمَهُمُ ٱلَّذِي يُوعَدُونَ", "Fatharhum yakhoodoo wayalAAaboo hatta yulaqoo yawmahumu allathee yooAAadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 84, "And it is Allah who is [the only] deity in the heaven, and on the earth [the only] deity. And He is the Wise, the Knowing", "وَهُوَ ٱلَّذِي فِي ٱلسَّمَآءِ إِلَٰهٞ وَفِي ٱلۡأَرۡضِ إِلَٰهٞۚ وَهُوَ ٱلۡحَكِيمُ ٱلۡعَلِيمُ", "Wahuwa allathee fee alssamai ilahun wafee alardi ilahun wahuwa alhakeemu alAAaleemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 85, "And blessed is He to whom belongs the dominion of the heavens and the earth and whatever is between them and with whom is knowledge of the Hour and to whom you will be returned", "وَتَبَارَكَ ٱلَّذِي لَهُۥ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا بَيۡنَهُمَا وَعِندَهُۥ عِلۡمُ ٱلسَّاعَةِ وَإِلَيۡهِ تُرۡجَعُونَ", "Watabaraka allathee lahu mulku alssamawati waalardi wama baynahuma waAAindahu AAilmu alssaAAati wailayhi turjaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 86, "And those they invoke besides Him do not possess [power of] intercession; but only those who testify to the truth [can benefit], and they know", "وَلَا يَمۡلِكُ ٱلَّذِينَ يَدۡعُونَ مِن دُونِهِ ٱلشَّفَٰعَةَ إِلَّا مَن شَهِدَ بِٱلۡحَقِّ وَهُمۡ يَعۡلَمُونَ", "Wala yamliku allatheena yadAAoona min doonihi alshshafaAAata illa man shahida bialhaqqi wahum yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 87, "And if you asked them who created them, they would surely say, ""Allah."" So how are they deluded", "وَلَئِن سَأَلۡتَهُم مَّنۡ خَلَقَهُمۡ لَيَقُولُنَّ ٱللَّهُۖ فَأَنَّىٰ يُؤۡفَكُونَ", "Walain saaltahum man khalaqahum layaqoolunna Allahu faanna yufakoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 88, "And [Allah acknowledges] his saying, ""O my Lord, indeed these are a people who do not believe", "وَقِيلِهِۦ يَٰرَبِّ إِنَّ هَـٰٓؤُلَآءِ قَوۡمٞ لَّا يُؤۡمِنُونَ", "Waqeelihi yarabbi inna haolai qawmun la yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (43, 89, "So turn aside from them and say, ""Peace."" But they are going to know", "فَٱصۡفَحۡ عَنۡهُمۡ وَقُلۡ سَلَٰمٞۚ فَسَوۡفَ يَعۡلَمُونَ", "Faisfah AAanhum waqul salamun fasawfa yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 1, "Ha, Meem", "حمٓ", "Hameem");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 2, "By the clear Book", "وَٱلۡكِتَٰبِ ٱلۡمُبِينِ", "Waalkitabi almubeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 3, "Indeed, We sent it down during a blessed night. Indeed, We were to warn [mankind]", "إِنَّآ أَنزَلۡنَٰهُ فِي لَيۡلَةٖ مُّبَٰرَكَةٍۚ إِنَّا كُنَّا مُنذِرِينَ", "Inna anzalnahu fee laylatin mubarakatin inna kunna munthireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 4, "On that night is made distinct every precise matter", "فِيهَا يُفۡرَقُ كُلُّ أَمۡرٍ حَكِيمٍ", "Feeha yufraqu kullu amrin hakeemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 5, "[Every] matter [proceeding] from Us. Indeed, We were to send [a messenger]", "أَمۡرٗا مِّنۡ عِندِنَآۚ إِنَّا كُنَّا مُرۡسِلِينَ", "Amran min AAindina inna kunna mursileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 6, "As mercy from your Lord. Indeed, He is the Hearing, the Knowing", "رَحۡمَةٗ مِّن رَّبِّكَۚ إِنَّهُۥ هُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ", "Rahmatan min rabbika innahu huwa alssameeAAu alAAaleemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 7, "Lord of the heavens and the earth and that between them, if you would be certain", "رَبِّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا بَيۡنَهُمَآۖ إِن كُنتُم مُّوقِنِينَ", "Rabbi alssamawati waalardi wama baynahuma in kuntum mooqineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 8, "There is no deity except Him; He gives life and causes death. [He is] your Lord and the Lord of your first forefathers", "لَآ إِلَٰهَ إِلَّا هُوَ يُحۡيِۦ وَيُمِيتُۖ رَبُّكُمۡ وَرَبُّ ءَابَآئِكُمُ ٱلۡأَوَّلِينَ", "La ilaha illa huwa yuhyee wayumeetu rabbukum warabbu abaikumu alawwaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 9, "But they are in doubt, amusing themselves", "بَلۡ هُمۡ فِي شَكّٖ يَلۡعَبُونَ", "Bal hum fee shakkin yalAAaboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 10, "Then watch for the Day when the sky will bring a visible smoke", "فَٱرۡتَقِبۡ يَوۡمَ تَأۡتِي ٱلسَّمَآءُ بِدُخَانٖ مُّبِينٖ", "Fairtaqib yawma tatee alssamao bidukhanin mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 11, "Covering the people; this is a painful torment", "يَغۡشَى ٱلنَّاسَۖ هَٰذَا عَذَابٌ أَلِيمٞ", "Yaghsha alnnasa hatha AAathabun aleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 12, "[They will say], ""Our Lord, remove from us the torment; indeed, we are believers", "رَّبَّنَا ٱكۡشِفۡ عَنَّا ٱلۡعَذَابَ إِنَّا مُؤۡمِنُونَ", "Rabbana ikshif AAanna alAAathaba inna muminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 13, "How will there be for them a reminder [at that time]? And there had come to them a clear Messenger", "أَنَّىٰ لَهُمُ ٱلذِّكۡرَىٰ وَقَدۡ جَآءَهُمۡ رَسُولٞ مُّبِينٞ", "Anna lahumu alththikra waqad jaahum rasoolun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 14, "Then they turned away from him and said, ""[He was] taught [and is] a madman", "ثُمَّ تَوَلَّوۡاْ عَنۡهُ وَقَالُواْ مُعَلَّمٞ مَّجۡنُونٌ", "Thumma tawallaw AAanhu waqaloo muAAallamun majnoonun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 15, "Indeed, We will remove the torment for a little. Indeed, you [disbelievers] will return [to disbelief]", "إِنَّا كَاشِفُواْ ٱلۡعَذَابِ قَلِيلًاۚ إِنَّكُمۡ عَآئِدُونَ", "Inna kashifoo alAAathabi qaleelan innakum AAaidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 16, "The Day We will strike with the greatest assault, indeed, We will take retribution", "يَوۡمَ نَبۡطِشُ ٱلۡبَطۡشَةَ ٱلۡكُبۡرَىٰٓ إِنَّا مُنتَقِمُونَ", "Yawma nabtishu albatshata alkubra inna muntaqimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 17, "And We had already tried before them the people of Pharaoh, and there came to them a noble messenger", "۞وَلَقَدۡ فَتَنَّا قَبۡلَهُمۡ قَوۡمَ فِرۡعَوۡنَ وَجَآءَهُمۡ رَسُولٞ كَرِيمٌ", "Walaqad fatanna qablahum qawma firAAawna wajaahum rasoolun kareemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 18, "[Saying], ""Render to me the servants of Allah. Indeed, I am to you a trustworthy messenger", "أَنۡ أَدُّوٓاْ إِلَيَّ عِبَادَ ٱللَّهِۖ إِنِّي لَكُمۡ رَسُولٌ أَمِينٞ", "An addoo ilayya AAibada Allahi innee lakum rasoolun ameenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 19, "And [saying], ""Be not haughty with Allah. Indeed, I have come to you with clear authority", "وَأَن لَّا تَعۡلُواْ عَلَى ٱللَّهِۖ إِنِّيٓ ءَاتِيكُم بِسُلۡطَٰنٖ مُّبِينٖ", "Waan la taAAloo AAala Allahi innee ateekum bisultanin mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 20, "And indeed, I have sought refuge in my Lord and your Lord, lest you stone me", "وَإِنِّي عُذۡتُ بِرَبِّي وَرَبِّكُمۡ أَن تَرۡجُمُونِ", "Wainnee AAuthtu birabbee warabbikum an tarjumooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 21, "But if you do not believe me, then leave me alone", "وَإِن لَّمۡ تُؤۡمِنُواْ لِي فَٱعۡتَزِلُونِ", "Wain lam tuminoo lee faiAAtazilooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 22, "And [finally] he called to his Lord that these were a criminal people", "فَدَعَا رَبَّهُۥٓ أَنَّ هَـٰٓؤُلَآءِ قَوۡمٞ مُّجۡرِمُونَ", "FadaAAa rabbahu anna haolai qawmun mujrimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 23, "[Allah said], ""Then set out with My servants by night. Indeed, you are to be pursued", "فَأَسۡرِ بِعِبَادِي لَيۡلًا إِنَّكُم مُّتَّبَعُونَ", "Faasri biAAibadee laylan innakum muttabaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 24, "And leave the sea in stillness. Indeed, they are an army to be drowned", "وَٱتۡرُكِ ٱلۡبَحۡرَ رَهۡوًاۖ إِنَّهُمۡ جُندٞ مُّغۡرَقُونَ", "Waotruki albahra rahwan innahum jundun mughraqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 25, "How much they left behind of gardens and springs", "كَمۡ تَرَكُواْ مِن جَنَّـٰتٖ وَعُيُونٖ", "Kam tarakoo min jannatin waAAuyoonin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 26, "And crops and noble sites", "وَزُرُوعٖ وَمَقَامٖ كَرِيمٖ", "WazurooAAin wamaqamin kareemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 27, "And comfort wherein they were amused", "وَنَعۡمَةٖ كَانُواْ فِيهَا فَٰكِهِينَ", "WanaAAmatin kanoo feeha fakiheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 28, "Thus. And We caused to inherit it another people", "كَذَٰلِكَۖ وَأَوۡرَثۡنَٰهَا قَوۡمًا ءَاخَرِينَ", "Kathalika waawrathnaha qawman akhareena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 29, "And the heaven and earth wept not for them, nor were they reprieved", "فَمَا بَكَتۡ عَلَيۡهِمُ ٱلسَّمَآءُ وَٱلۡأَرۡضُ وَمَا كَانُواْ مُنظَرِينَ", "Fama bakat AAalayhimu alssamao waalardu wama kanoo munthareena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 30, "And We certainly saved the Children of Israel from the humiliating torment", "وَلَقَدۡ نَجَّيۡنَا بَنِيٓ إِسۡرَـٰٓءِيلَ مِنَ ٱلۡعَذَابِ ٱلۡمُهِينِ", "Walaqad najjayna banee israeela mina alAAathabi almuheeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 31, "From Pharaoh. Indeed, he was a haughty one among the transgressors", "مِن فِرۡعَوۡنَۚ إِنَّهُۥ كَانَ عَالِيٗا مِّنَ ٱلۡمُسۡرِفِينَ", "Min firAAawna innahu kana AAaliyan mina almusrifeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 32, "And We certainly chose them by knowledge over [all] the worlds", "وَلَقَدِ ٱخۡتَرۡنَٰهُمۡ عَلَىٰ عِلۡمٍ عَلَى ٱلۡعَٰلَمِينَ", "Walaqadi ikhtarnahum AAala AAilmin AAala alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 33, "And We gave them of signs that in which there was a clear trial", "وَءَاتَيۡنَٰهُم مِّنَ ٱلۡأٓيَٰتِ مَا فِيهِ بَلَـٰٓؤٞاْ مُّبِينٌ", "Waataynahum mina alayati ma feehi balaon mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 34, "Indeed, these [disbelievers] are saying", "إِنَّ هَـٰٓؤُلَآءِ لَيَقُولُونَ", "Inna haolai layaqooloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 35, "There is not but our first death, and we will not be resurrected", "إِنۡ هِيَ إِلَّا مَوۡتَتُنَا ٱلۡأُولَىٰ وَمَا نَحۡنُ بِمُنشَرِينَ", "In hiya illa mawtatuna aloola wama nahnu bimunshareena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 36, "Then bring [back] our forefathers, if you should be truthful", "فَأۡتُواْ بِـَٔابَآئِنَآ إِن كُنتُمۡ صَٰدِقِينَ", "Fatoo biabaina in kuntum sadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 37, "Are they better or the people of Tubba' and those before them? We destroyed them, [for] indeed, they were criminals", "أَهُمۡ خَيۡرٌ أَمۡ قَوۡمُ تُبَّعٖ وَٱلَّذِينَ مِن قَبۡلِهِمۡ أَهۡلَكۡنَٰهُمۡۚ إِنَّهُمۡ كَانُواْ مُجۡرِمِينَ", "Ahum khayrun am qawmu tubbaAAin waallatheena min qablihim ahlaknahum innahum kanoo mujrimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 38, "And We did not create the heavens and earth and that between them in play", "وَمَا خَلَقۡنَا ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَمَا بَيۡنَهُمَا لَٰعِبِينَ", "Wama khalaqna alssamawati waalarda wama baynahuma laAAibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 39, "We did not create them except in truth, but most of them do not know", "مَا خَلَقۡنَٰهُمَآ إِلَّا بِٱلۡحَقِّ وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَعۡلَمُونَ", "Ma khalaqnahuma illa bialhaqqi walakinna aktharahum la yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 40, "Indeed, the Day of Judgement is the appointed time for them all", "إِنَّ يَوۡمَ ٱلۡفَصۡلِ مِيقَٰتُهُمۡ أَجۡمَعِينَ", "Inna yawma alfasli meeqatuhum ajmaAAeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 41, "The Day when no relation will avail a relation at all, nor will they be helped", "يَوۡمَ لَا يُغۡنِي مَوۡلًى عَن مَّوۡلٗى شَيۡـٔٗا وَلَا هُمۡ يُنصَرُونَ", "Yawma la yughnee mawlan AAan mawlan shayan wala hum yunsaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 42, "Except those [believers] on whom Allah has mercy. Indeed, He is the Exalted in Might, the Merciful", "إِلَّا مَن رَّحِمَ ٱللَّهُۚ إِنَّهُۥ هُوَ ٱلۡعَزِيزُ ٱلرَّحِيمُ", "Illa man rahima Allahu innahu huwa alAAazeezu alrraheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 43, "Indeed, the tree of zaqqum", "إِنَّ شَجَرَتَ ٱلزَّقُّومِ", "Inna shajarata alzzaqqoomi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 44, "Is food for the sinful", "طَعَامُ ٱلۡأَثِيمِ", "TaAAamu alatheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 45, "Like murky oil, it boils within bellies", "كَٱلۡمُهۡلِ يَغۡلِي فِي ٱلۡبُطُونِ", "Kaalmuhli yaghlee fee albutooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 46, "Like the boiling of scalding water", "كَغَلۡيِ ٱلۡحَمِيمِ", "Kaghalyi alhameemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 47, "[It will be commanded], ""Seize him and drag him into the midst of the Hellfire", "خُذُوهُ فَٱعۡتِلُوهُ إِلَىٰ سَوَآءِ ٱلۡجَحِيمِ", "Khuthoohu faiAAtiloohu ila sawai aljaheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 48, "Then pour over his head from the torment of scalding water", "ثُمَّ صُبُّواْ فَوۡقَ رَأۡسِهِۦ مِنۡ عَذَابِ ٱلۡحَمِيمِ", "Thumma subboo fawqa rasihi min AAathabi alhameemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 49, "[It will be said], ""Taste! Indeed, you are the honored, the noble", "ذُقۡ إِنَّكَ أَنتَ ٱلۡعَزِيزُ ٱلۡكَرِيمُ", "Thuq innaka anta alAAazeezu alkareemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 50, "Indeed, this is what you used to dispute", "إِنَّ هَٰذَا مَا كُنتُم بِهِۦ تَمۡتَرُونَ", "Inna hatha ma kuntum bihi tamtaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 51, "Indeed, the righteous will be in a secure place", "إِنَّ ٱلۡمُتَّقِينَ فِي مَقَامٍ أَمِينٖ", "Inna almuttaqeena fee maqamin ameenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 52, "Within gardens and springs", "فِي جَنَّـٰتٖ وَعُيُونٖ", "Fee jannatin waAAuyoonin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 53, "Wearing [garments of] fine silk and brocade, facing each other", "يَلۡبَسُونَ مِن سُندُسٖ وَإِسۡتَبۡرَقٖ مُّتَقَٰبِلِينَ", "Yalbasoona min sundusin waistabraqin mutaqabileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 54, "Thus. And We will marry them to fair women with large, [beautiful] eyes", "كَذَٰلِكَ وَزَوَّجۡنَٰهُم بِحُورٍ عِينٖ", "Kathalika wazawwajnahum bihoorin AAeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 55, "They will call therein for every [kind of] fruit - safe and secure", "يَدۡعُونَ فِيهَا بِكُلِّ فَٰكِهَةٍ ءَامِنِينَ", "YadAAoona feeha bikulli fakihatin amineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 56, "They will not taste death therein except the first death, and He will have protected them from the punishment of Hellfire", "لَا يَذُوقُونَ فِيهَا ٱلۡمَوۡتَ إِلَّا ٱلۡمَوۡتَةَ ٱلۡأُولَىٰۖ وَوَقَىٰهُمۡ عَذَابَ ٱلۡجَحِيمِ", "La yathooqoona feeha almawta illa almawtata aloola wawaqahum AAathaba aljaheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 57, "As bounty from your Lord. That is what is the great attainment", "فَضۡلٗا مِّن رَّبِّكَۚ ذَٰلِكَ هُوَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ", "Fadlan min rabbika thalika huwa alfawzu alAAatheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 58, "And indeed, We have eased the Qur'an in your tongue that they might be reminded", "فَإِنَّمَا يَسَّرۡنَٰهُ بِلِسَانِكَ لَعَلَّهُمۡ يَتَذَكَّرُونَ", "Fainnama yassarnahu bilisanika laAAallahum yatathakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (44, 59, "So watch, [O Muhammad]; indeed, they are watching [for your end]", "فَٱرۡتَقِبۡ إِنَّهُم مُّرۡتَقِبُونَ", "Fairtaqib innahum murtaqiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (45, 1, "Ha, Meem", "حمٓ", "Hameem");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (45, 2, "The revelation of the Book is from Allah, the Exalted in Might, the Wise", "تَنزِيلُ ٱلۡكِتَٰبِ مِنَ ٱللَّهِ ٱلۡعَزِيزِ ٱلۡحَكِيمِ", "Tanzeelu alkitabi mina Allahi alAAazeezi alhakeemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (45, 3, "Indeed, within the heavens and earth are signs for the believers", "إِنَّ فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ لَأٓيَٰتٖ لِّلۡمُؤۡمِنِينَ", "Inna fee alssamawati waalardi laayatin lilmumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (45, 4, "And in the creation of yourselves and what He disperses of moving creatures are signs for people who are certain [in faith]", "وَفِي خَلۡقِكُمۡ وَمَا يَبُثُّ مِن دَآبَّةٍ ءَايَٰتٞ لِّقَوۡمٖ يُوقِنُونَ", "Wafee khalqikum wama yabuththu min dabbatin ayatun liqawmin yooqinoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (45, 5, "And [in] the alternation of night and day and [in] what Allah sends down from the sky of provision and gives life thereby to the earth after its lifelessness and [in His] directing of the winds are signs for a people who reason", "وَٱخۡتِلَٰفِ ٱلَّيۡلِ وَٱلنَّهَارِ وَمَآ أَنزَلَ ٱللَّهُ مِنَ ٱلسَّمَآءِ مِن رِّزۡقٖ فَأَحۡيَا بِهِ ٱلۡأَرۡضَ بَعۡدَ مَوۡتِهَا وَتَصۡرِيفِ ٱلرِّيَٰحِ ءَايَٰتٞ لِّقَوۡمٖ يَعۡقِلُونَ", "Waikhtilafi allayli waalnnahari wama anzala Allahu mina alssamai min rizqin faahya bihi alarda baAAda mawtiha watasreefi alrriyahi ayatun liqawmin yaAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (45, 6, "These are the verses of Allah which We recite to you in truth. Then in what statement after Allah and His verses will they believe", "تِلۡكَ ءَايَٰتُ ٱللَّهِ نَتۡلُوهَا عَلَيۡكَ بِٱلۡحَقِّۖ فَبِأَيِّ حَدِيثِۭ بَعۡدَ ٱللَّهِ وَءَايَٰتِهِۦ يُؤۡمِنُونَ", "Tilka ayatu Allahi natlooha AAalayka bialhaqqi fabiayyi hadeethin baAAda Allahi waayatihi yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (45, 7, "Woe to every sinful liar", "وَيۡلٞ لِّكُلِّ أَفَّاكٍ أَثِيمٖ", "Waylun likulli affakin atheemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (45, 8, "Who hears the verses of Allah recited to him, then persists arrogantly as if he had not heard them. So give him tidings of a painful punishment", "يَسۡمَعُ ءَايَٰتِ ٱللَّهِ تُتۡلَىٰ عَلَيۡهِ ثُمَّ يُصِرُّ مُسۡتَكۡبِرٗا كَأَن لَّمۡ يَسۡمَعۡهَاۖ فَبَشِّرۡهُ بِعَذَابٍ أَلِيمٖ", "YasmaAAu ayati Allahi tutla AAalayhi thumma yusirru mustakbiran kaan lam yasmaAAha fabashshirhu biAAathabin aleemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (45, 9, "And when he knows anything of Our verses, he takes them in ridicule. Those will have a humiliating punishment", "وَإِذَا عَلِمَ مِنۡ ءَايَٰتِنَا شَيۡـًٔا ٱتَّخَذَهَا هُزُوًاۚ أُوْلَـٰٓئِكَ لَهُمۡ عَذَابٞ مُّهِينٞ", "Waitha AAalima min ayatina shayan ittakhathaha huzuwan olaika lahum AAathabun muheenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (45, 10, "Before them is Hell, and what they had earned will not avail them at all nor what they had taken besides Allah as allies. And they will have a great punishment", "مِّن وَرَآئِهِمۡ جَهَنَّمُۖ وَلَا يُغۡنِي عَنۡهُم مَّا كَسَبُواْ شَيۡـٔٗا وَلَا مَا ٱتَّخَذُواْ مِن دُونِ ٱللَّهِ أَوۡلِيَآءَۖ وَلَهُمۡ عَذَابٌ عَظِيمٌ", "Min waraihim jahannamu wala yughnee AAanhum ma kasaboo shayan wala ma ittakhathoo min dooni Allahi awliyaa walahum AAathabun AAatheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (45, 11, "This [Qur'an] is guidance. And those who have disbelieved in the verses of their Lord will have a painful punishment of foul nature", "هَٰذَا هُدٗىۖ وَٱلَّذِينَ كَفَرُواْ بِـَٔايَٰتِ رَبِّهِمۡ لَهُمۡ عَذَابٞ مِّن رِّجۡزٍ أَلِيمٌ", "Hatha hudan waallatheena kafaroo biayati rabbihim lahum AAathabun min rijzin aleemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (45, 12, "It is Allah who subjected to you the sea so that ships may sail upon it by His command and that you may seek of His bounty; and perhaps you will be grateful", "۞ٱللَّهُ ٱلَّذِي سَخَّرَ لَكُمُ ٱلۡبَحۡرَ لِتَجۡرِيَ ٱلۡفُلۡكُ فِيهِ بِأَمۡرِهِۦ وَلِتَبۡتَغُواْ مِن فَضۡلِهِۦ وَلَعَلَّكُمۡ تَشۡكُرُونَ", "Allahu allathee sakhkhara lakumu albahra litajriya alfulku feehi biamrihi walitabtaghoo min fadlihi walaAAallakum tashkuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (45, 13, "And He has subjected to you whatever is in the heavens and whatever is on the earth - all from Him. Indeed in that are signs for a people who give thought", "وَسَخَّرَ لَكُم مَّا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِ جَمِيعٗا مِّنۡهُۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يَتَفَكَّرُونَ", "Wasakhkhara lakum ma fee alssamawati wama fee alardi jameeAAan minhu inna fee thalika laayatin liqawmin yatafakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (45, 14, "Say, [O Muhammad], to those who have believed that they [should] forgive those who expect not the days of Allah so that He may recompense a people for what they used to earn", "قُل لِّلَّذِينَ ءَامَنُواْ يَغۡفِرُواْ لِلَّذِينَ لَا يَرۡجُونَ أَيَّامَ ٱللَّهِ لِيَجۡزِيَ قَوۡمَۢا بِمَا كَانُواْ يَكۡسِبُونَ", "Qul lillatheena amanoo yaghfiroo lillatheena la yarjoona ayyama Allahi liyajziya qawman bima kanoo yaksiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (45, 15, "Whoever does a good deed - it is for himself; and whoever does evil - it is against the self. Then to your Lord you will be returned", "مَنۡ عَمِلَ صَٰلِحٗا فَلِنَفۡسِهِۦۖ وَمَنۡ أَسَآءَ فَعَلَيۡهَاۖ ثُمَّ إِلَىٰ رَبِّكُمۡ تُرۡجَعُونَ", "Man AAamila salihan falinafsihi waman asaa faAAalayha thumma ila rabbikum turjaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (45, 16, "And We did certainly give the Children of Israel the Scripture and judgement and prophethood, and We provided them with good things and preferred them over the worlds", "وَلَقَدۡ ءَاتَيۡنَا بَنِيٓ إِسۡرَـٰٓءِيلَ ٱلۡكِتَٰبَ وَٱلۡحُكۡمَ وَٱلنُّبُوَّةَ وَرَزَقۡنَٰهُم مِّنَ ٱلطَّيِّبَٰتِ وَفَضَّلۡنَٰهُمۡ عَلَى ٱلۡعَٰلَمِينَ", "Walaqad atayna banee israeela alkitaba waalhukma waalnnubuwwata warazaqnahum mina alttayyibati wafaddalnahum AAala alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (45, 17, "And We gave them clear proofs of the matter [of religion]. And they did not differ except after knowledge had come to them - out of jealous animosity between themselves. Indeed, your Lord will judge between them on the Day of Resurrection concerning that over which they used to differ", "وَءَاتَيۡنَٰهُم بَيِّنَٰتٖ مِّنَ ٱلۡأَمۡرِۖ فَمَا ٱخۡتَلَفُوٓاْ إِلَّا مِنۢ بَعۡدِ مَا جَآءَهُمُ ٱلۡعِلۡمُ بَغۡيَۢا بَيۡنَهُمۡۚ إِنَّ رَبَّكَ يَقۡضِي بَيۡنَهُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ فِيمَا كَانُواْ فِيهِ يَخۡتَلِفُونَ", "Waataynahum bayyinatin mina alamri fama ikhtalafoo illa min baAAdi ma jaahumu alAAilmu baghyan baynahum inna rabbaka yaqdee baynahum yawma alqiyamati feema kanoo feehi yakhtalifoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (45, 18, "Then We put you, [O Muhammad], on an ordained way concerning the matter [of religion]; so follow it and do not follow the inclinations of those who do not know", "ثُمَّ جَعَلۡنَٰكَ عَلَىٰ شَرِيعَةٖ مِّنَ ٱلۡأَمۡرِ فَٱتَّبِعۡهَا وَلَا تَتَّبِعۡ أَهۡوَآءَ ٱلَّذِينَ لَا يَعۡلَمُونَ", "Thumma jaAAalnaka AAala shareeAAatin mina alamri faittabiAAha wala tattabiAA ahwaa allatheena la yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (45, 19, "Indeed, they will never avail you against Allah at all. And indeed, the wrongdoers are allies of one another; but Allah is the protector of the righteous", "إِنَّهُمۡ لَن يُغۡنُواْ عَنكَ مِنَ ٱللَّهِ شَيۡـٔٗاۚ وَإِنَّ ٱلظَّـٰلِمِينَ بَعۡضُهُمۡ أَوۡلِيَآءُ بَعۡضٖۖ وَٱللَّهُ وَلِيُّ ٱلۡمُتَّقِينَ", "Innahum lan yughnoo AAanka mina Allahi shayan wainna alththalimeena baAAduhum awliyao baAAdin waAllahu waliyyu almuttaqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (45, 20, "This [Qur'an] is enlightenment for mankind and guidance and mercy for a people who are certain [in faith]", "هَٰذَا بَصَـٰٓئِرُ لِلنَّاسِ وَهُدٗى وَرَحۡمَةٞ لِّقَوۡمٖ يُوقِنُونَ", "Hatha basairu lilnnasi wahudan warahmatun liqawmin yooqinoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (45, 21, "Or do those who commit evils think We will make them like those who have believed and done righteous deeds - [make them] equal in their life and their death? Evil is that which they judge", "أَمۡ حَسِبَ ٱلَّذِينَ ٱجۡتَرَحُواْ ٱلسَّيِّـَٔاتِ أَن نَّجۡعَلَهُمۡ كَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ سَوَآءٗ مَّحۡيَاهُمۡ وَمَمَاتُهُمۡۚ سَآءَ مَا يَحۡكُمُونَ", "Am hasiba allatheena ijtarahoo alssayyiati an najAAalahum kaallatheena amanoo waAAamiloo alssalihati sawaan mahyahum wamamatuhum saa ma yahkumoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (45, 22, "And Allah created the heavens and earth in truth and so that every soul may be recompensed for what it has earned, and they will not be wronged", "وَخَلَقَ ٱللَّهُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ بِٱلۡحَقِّ وَلِتُجۡزَىٰ كُلُّ نَفۡسِۭ بِمَا كَسَبَتۡ وَهُمۡ لَا يُظۡلَمُونَ", "Wakhalaqa Allahu alssamawati waalarda bialhaqqi walitujza kullu nafsin bima kasabat wahum la yuthlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (45, 23, "Have you seen he who has taken as his god his [own] desire, and Allah has sent him astray due to knowledge and has set a seal upon his hearing and his heart and put over his vision a veil? So who will guide him after Allah? Then will you not be reminded", "أَفَرَءَيۡتَ مَنِ ٱتَّخَذَ إِلَٰهَهُۥ هَوَىٰهُ وَأَضَلَّهُ ٱللَّهُ عَلَىٰ عِلۡمٖ وَخَتَمَ عَلَىٰ سَمۡعِهِۦ وَقَلۡبِهِۦ وَجَعَلَ عَلَىٰ بَصَرِهِۦ غِشَٰوَةٗ فَمَن يَهۡدِيهِ مِنۢ بَعۡدِ ٱللَّهِۚ أَفَلَا تَذَكَّرُونَ", "Afaraayta mani ittakhatha ilahahu hawahu waadallahu Allahu AAala AAilmin wakhatama AAala samAAihi waqalbihi wajaAAala AAala basarihi ghishawatan faman yahdeehi min baAAdi Allahi afala tathakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (45, 24, "And they say, ""There is not but our worldly life; we die and live, and nothing destroys us except time."" And they have of that no knowledge; they are only assuming", "وَقَالُواْ مَا هِيَ إِلَّا حَيَاتُنَا ٱلدُّنۡيَا نَمُوتُ وَنَحۡيَا وَمَا يُهۡلِكُنَآ إِلَّا ٱلدَّهۡرُۚ وَمَا لَهُم بِذَٰلِكَ مِنۡ عِلۡمٍۖ إِنۡ هُمۡ إِلَّا يَظُنُّونَ", "Waqaloo ma hiya illa hayatuna alddunya namootu wanahya wama yuhlikuna illa alddahru wama lahum bithalika min AAilmin in hum illa yathunnoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (45, 25, "And when Our verses are recited to them as clear evidences, their argument is only that they say, ""Bring [back] our forefathers, if you should be truthful", "وَإِذَا تُتۡلَىٰ عَلَيۡهِمۡ ءَايَٰتُنَا بَيِّنَٰتٖ مَّا كَانَ حُجَّتَهُمۡ إِلَّآ أَن قَالُواْ ٱئۡتُواْ بِـَٔابَآئِنَآ إِن كُنتُمۡ صَٰدِقِينَ", "Waitha tutla AAalayhim ayatuna bayyinatin ma kana hujjatahum illa an qaloo itoo biabaina in kuntum sadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (45, 26, "Say, ""Allah causes you to live, then causes you to die; then He will assemble you for the Day of Resurrection, about which there is no doubt, but most of the people do not know", "قُلِ ٱللَّهُ يُحۡيِيكُمۡ ثُمَّ يُمِيتُكُمۡ ثُمَّ يَجۡمَعُكُمۡ إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِ لَا رَيۡبَ فِيهِ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَعۡلَمُونَ", "Quli Allahu yuhyeekum thumma yumeetukum thumma yajmaAAukum ila yawmi alqiyamati la rayba feehi walakinna akthara alnnasi la yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (45, 27, "And to Allah belongs the dominion of the heavens and the earth. And the Day the Hour appears - that Day the falsifiers will lose", "وَلِلَّهِ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَيَوۡمَ تَقُومُ ٱلسَّاعَةُ يَوۡمَئِذٖ يَخۡسَرُ ٱلۡمُبۡطِلُونَ", "Walillahi mulku alssamawati waalardi wayawma taqoomu alssaAAatu yawmaithin yakhsaru almubtiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (45, 28, "And you will see every nation kneeling [from fear]. Every nation will be called to its record [and told], ""Today you will be recompensed for what you used to do", "وَتَرَىٰ كُلَّ أُمَّةٖ جَاثِيَةٗۚ كُلُّ أُمَّةٖ تُدۡعَىٰٓ إِلَىٰ كِتَٰبِهَا ٱلۡيَوۡمَ تُجۡزَوۡنَ مَا كُنتُمۡ تَعۡمَلُونَ", "Watara kulla ommatin jathiyatan kullu ommatin tudAAa ila kitabiha alyawma tujzawna ma kuntum taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (45, 29, "This, Our record, speaks about you in truth. Indeed, We were having transcribed whatever you used to do", "هَٰذَا كِتَٰبُنَا يَنطِقُ عَلَيۡكُم بِٱلۡحَقِّۚ إِنَّا كُنَّا نَسۡتَنسِخُ مَا كُنتُمۡ تَعۡمَلُونَ", "Hatha kitabuna yantiqu AAalaykum bialhaqqi inna kunna nastansikhu ma kuntum taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (45, 30, "So as for those who believed and did righteous deeds, their Lord will admit them into His mercy. That is what is the clear attainment", "فَأَمَّا ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ فَيُدۡخِلُهُمۡ رَبُّهُمۡ فِي رَحۡمَتِهِۦۚ ذَٰلِكَ هُوَ ٱلۡفَوۡزُ ٱلۡمُبِينُ", "Faamma allatheena amanoo waAAamiloo alssalihati fayudkhiluhum rabbuhum fee rahmatihi thalika huwa alfawzu almubeenu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (45, 31, "But as for those who disbelieved, [it will be said], ""Were not Our verses recited to you, but you were arrogant and became a people of criminals", "وَأَمَّا ٱلَّذِينَ كَفَرُوٓاْ أَفَلَمۡ تَكُنۡ ءَايَٰتِي تُتۡلَىٰ عَلَيۡكُمۡ فَٱسۡتَكۡبَرۡتُمۡ وَكُنتُمۡ قَوۡمٗا مُّجۡرِمِينَ", "Waamma allatheena kafaroo afalam takun ayatee tutla AAalaykum faistakbartum wakuntum qawman mujrimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (45, 32, "And when it was said, 'Indeed, the promise of Allah is truth and the Hour [is coming] - no doubt about it,' you said, 'We know not what is the Hour. We assume only assumption, and we are not convinced", "وَإِذَا قِيلَ إِنَّ وَعۡدَ ٱللَّهِ حَقّٞ وَٱلسَّاعَةُ لَا رَيۡبَ فِيهَا قُلۡتُم مَّا نَدۡرِي مَا ٱلسَّاعَةُ إِن نَّظُنُّ إِلَّا ظَنّٗا وَمَا نَحۡنُ بِمُسۡتَيۡقِنِينَ", "Waitha qeela inna waAAda Allahi haqqun waalssaAAatu la rayba feeha qultum ma nadree ma alssaAAatu in nathunnu illa thannan wama nahnu bimustayqineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (45, 33, "And the evil consequences of what they did will appear to them, and they will be enveloped by what they used to ridicule", "وَبَدَا لَهُمۡ سَيِّـَٔاتُ مَا عَمِلُواْ وَحَاقَ بِهِم مَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ", "Wabada lahum sayyiatu ma AAamiloo wahaqa bihim ma kanoo bihi yastahzioona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (45, 34, "And it will be said, ""Today We will forget you as you forgot the meeting of this Day of yours, and your refuge is the Fire, and for you there are no helpers", "وَقِيلَ ٱلۡيَوۡمَ نَنسَىٰكُمۡ كَمَا نَسِيتُمۡ لِقَآءَ يَوۡمِكُمۡ هَٰذَا وَمَأۡوَىٰكُمُ ٱلنَّارُ وَمَا لَكُم مِّن نَّـٰصِرِينَ", "Waqeela alyawma nansakum kama naseetum liqaa yawmikum hatha wamawakumu alnnaru wama lakum min nasireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (45, 35, "That is because you took the verses of Allah in ridicule, and worldly life deluded you."" So that Day they will not be removed from it, nor will they be asked to appease [Allah]", "ذَٰلِكُم بِأَنَّكُمُ ٱتَّخَذۡتُمۡ ءَايَٰتِ ٱللَّهِ هُزُوٗا وَغَرَّتۡكُمُ ٱلۡحَيَوٰةُ ٱلدُّنۡيَاۚ فَٱلۡيَوۡمَ لَا يُخۡرَجُونَ مِنۡهَا وَلَا هُمۡ يُسۡتَعۡتَبُونَ", "Thalikum biannakumu ittakhathtum ayati Allahi huzuwan wagharratkumu alhayatu alddunya faalyawma la yukhrajoona minha wala hum yustaAAtaboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (45, 36, "Then, to Allah belongs [all] praise - Lord of the heavens and Lord of the earth, Lord of the worlds", "فَلِلَّهِ ٱلۡحَمۡدُ رَبِّ ٱلسَّمَٰوَٰتِ وَرَبِّ ٱلۡأَرۡضِ رَبِّ ٱلۡعَٰلَمِينَ", "Falillahi alhamdu rabbi alssamawati warabbi alardi rabbi alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (45, 37, "And to Him belongs [all] grandeur within the heavens and the earth, and He is the Exalted in Might, the Wise", "وَلَهُ ٱلۡكِبۡرِيَآءُ فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ", "Walahu alkibriyao fee alssamawati waalardi wahuwa alAAazeezu alhakeemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (46, 1, "Ha, Meem", "حمٓ", "Hameem");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (46, 2, "The revelation of the Book is from Allah, the Exalted in Might, the Wise", "تَنزِيلُ ٱلۡكِتَٰبِ مِنَ ٱللَّهِ ٱلۡعَزِيزِ ٱلۡحَكِيمِ", "Tanzeelu alkitabi mina Allahi alAAazeezi alhakeemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (46, 3, "We did not create the heavens and earth and what is between them except in truth and [for] a specified term. But those who disbelieve, from that of which they are warned, are turning away", "مَا خَلَقۡنَا ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَمَا بَيۡنَهُمَآ إِلَّا بِٱلۡحَقِّ وَأَجَلٖ مُّسَمّٗىۚ وَٱلَّذِينَ كَفَرُواْ عَمَّآ أُنذِرُواْ مُعۡرِضُونَ", "Ma khalaqna alssamawati waalarda wama baynahuma illa bialhaqqi waajalin musamman waallatheena kafaroo AAamma onthiroo muAAridoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (46, 4, "Say, [O Muhammad], ""Have you considered that which you invoke besides Allah? Show me what they have created of the earth; or did they have partnership in [creation of] the heavens? Bring me a scripture [revealed] before this or a [remaining] trace of knowledge, if you should be truthful", "قُلۡ أَرَءَيۡتُم مَّا تَدۡعُونَ مِن دُونِ ٱللَّهِ أَرُونِي مَاذَا خَلَقُواْ مِنَ ٱلۡأَرۡضِ أَمۡ لَهُمۡ شِرۡكٞ فِي ٱلسَّمَٰوَٰتِۖ ٱئۡتُونِي بِكِتَٰبٖ مِّن قَبۡلِ هَٰذَآ أَوۡ أَثَٰرَةٖ مِّنۡ عِلۡمٍ إِن كُنتُمۡ صَٰدِقِينَ", "Qul araaytum ma tadAAoona min dooni Allahi aroonee matha khalaqoo mina alardi am lahum shirkun fee alssamawati eetoonee bikitabin min qabli hatha aw atharatin min AAilmin in kuntum sadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (46, 5, "And who is more astray than he who invokes besides Allah those who will not respond to him until the Day of Resurrection, and they, of their invocation, are unaware", "وَمَنۡ أَضَلُّ مِمَّن يَدۡعُواْ مِن دُونِ ٱللَّهِ مَن لَّا يَسۡتَجِيبُ لَهُۥٓ إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِ وَهُمۡ عَن دُعَآئِهِمۡ غَٰفِلُونَ", "Waman adallu mimman yadAAoo min dooni Allahi man la yastajeebu lahu ila yawmi alqiyamati wahum AAan duAAaihim ghafiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (46, 6, "And when the people are gathered [that Day], they [who were invoked] will be enemies to them, and they will be deniers of their worship", "وَإِذَا حُشِرَ ٱلنَّاسُ كَانُواْ لَهُمۡ أَعۡدَآءٗ وَكَانُواْ بِعِبَادَتِهِمۡ كَٰفِرِينَ", "Waitha hushira alnnasu kanoo lahum aAAdaan wakanoo biAAibadatihim kafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (46, 7, "And when Our verses are recited to them as clear evidences, those who disbelieve say of the truth when it has come to them, ""This is obvious magic", "وَإِذَا تُتۡلَىٰ عَلَيۡهِمۡ ءَايَٰتُنَا بَيِّنَٰتٖ قَالَ ٱلَّذِينَ كَفَرُواْ لِلۡحَقِّ لَمَّا جَآءَهُمۡ هَٰذَا سِحۡرٞ مُّبِينٌ", "Waitha tutla AAalayhim ayatuna bayyinatin qala allatheena kafaroo lilhaqqi lamma jaahum hatha sihrun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (46, 8, "Or do they say, ""He has invented it?"" Say, ""If I have invented it, you will not possess for me [the power of protection] from Allah at all. He is most knowing of that in which you are involved. Sufficient is He as Witness between me and you, and He is the Forgiving the Merciful", "أَمۡ يَقُولُونَ ٱفۡتَرَىٰهُۖ قُلۡ إِنِ ٱفۡتَرَيۡتُهُۥ فَلَا تَمۡلِكُونَ لِي مِنَ ٱللَّهِ شَيۡـًٔاۖ هُوَ أَعۡلَمُ بِمَا تُفِيضُونَ فِيهِۚ كَفَىٰ بِهِۦ شَهِيدَۢا بَيۡنِي وَبَيۡنَكُمۡۖ وَهُوَ ٱلۡغَفُورُ ٱلرَّحِيمُ", "Am yaqooloona iftarahu qul ini iftaraytuhu fala tamlikoona lee mina Allahi shayan huwa aAAlamu bima tufeedoona feehi kafa bihi shaheedan baynee wabaynakum wahuwa alghafooru alrraheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (46, 9, "Say, ""I am not something original among the messengers, nor do I know what will be done with me or with you. I only follow that which is revealed to me, and I am not but a clear warner", "قُلۡ مَا كُنتُ بِدۡعٗا مِّنَ ٱلرُّسُلِ وَمَآ أَدۡرِي مَا يُفۡعَلُ بِي وَلَا بِكُمۡۖ إِنۡ أَتَّبِعُ إِلَّا مَا يُوحَىٰٓ إِلَيَّ وَمَآ أَنَا۠ إِلَّا نَذِيرٞ مُّبِينٞ", "Qul ma kuntu bidAAan mina alrrusuli wama adree ma yufAAalu bee wala bikum in attabiAAu illa ma yooha ilayya wama ana illa natheerun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (46, 10, "Say, ""Have you considered: if the Qur'an was from Allah, and you disbelieved in it while a witness from the Children of Israel has testified to something similar and believed while you were arrogant...?"" Indeed, Allah does not guide the wrongdoing people", "قُلۡ أَرَءَيۡتُمۡ إِن كَانَ مِنۡ عِندِ ٱللَّهِ وَكَفَرۡتُم بِهِۦ وَشَهِدَ شَاهِدٞ مِّنۢ بَنِيٓ إِسۡرَـٰٓءِيلَ عَلَىٰ مِثۡلِهِۦ فَـَٔامَنَ وَٱسۡتَكۡبَرۡتُمۡۚ إِنَّ ٱللَّهَ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلظَّـٰلِمِينَ", "Qul araaytum in kana min AAindi Allahi wakafartum bihi washahida shahidun min banee israeela AAala mithlihi faamana waistakbartum inna Allaha la yahdee alqawma alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (46, 11, "And those who disbelieve say of those who believe, ""If it had [truly] been good, they would not have preceded us to it."" And when they are not guided by it, they will say, ""This is an ancient falsehood", "وَقَالَ ٱلَّذِينَ كَفَرُواْ لِلَّذِينَ ءَامَنُواْ لَوۡ كَانَ خَيۡرٗا مَّا سَبَقُونَآ إِلَيۡهِۚ وَإِذۡ لَمۡ يَهۡتَدُواْ بِهِۦ فَسَيَقُولُونَ هَٰذَآ إِفۡكٞ قَدِيمٞ", "Waqala allatheena kafaroo lillatheena amanoo law kana khayran ma sabaqoona ilayhi waith lam yahtadoo bihi fasayaqooloona hatha ifkun qadeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (46, 12, "And before it was the scripture of Moses to lead and as a mercy. And this is a confirming Book in an Arabic tongue to warn those who have wronged and as good tidings to the doers of good", "وَمِن قَبۡلِهِۦ كِتَٰبُ مُوسَىٰٓ إِمَامٗا وَرَحۡمَةٗۚ وَهَٰذَا كِتَٰبٞ مُّصَدِّقٞ لِّسَانًا عَرَبِيّٗا لِّيُنذِرَ ٱلَّذِينَ ظَلَمُواْ وَبُشۡرَىٰ لِلۡمُحۡسِنِينَ", "Wamin qablihi kitabu moosa imaman warahmatan wahatha kitabun musaddiqun lisanan AAarabiyyan liyunthira allatheena thalamoo wabushra lilmuhsineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (46, 13, "Indeed, those who have said, ""Our Lord is Allah,"" and then remained on a right course - there will be no fear concerning them, nor will they grieve", "إِنَّ ٱلَّذِينَ قَالُواْ رَبُّنَا ٱللَّهُ ثُمَّ ٱسۡتَقَٰمُواْ فَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ", "Inna allatheena qaloo rabbuna Allahu thumma istaqamoo fala khawfun AAalayhim wala hum yahzanoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (46, 14, "Those are the companions of Paradise, abiding eternally therein as reward for what they used to do", "أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلۡجَنَّةِ خَٰلِدِينَ فِيهَا جَزَآءَۢ بِمَا كَانُواْ يَعۡمَلُونَ", "Olaika ashabu aljannati khalideena feeha jazaan bima kanoo yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (46, 15, "And We have enjoined upon man, to his parents, good treatment. His mother carried him with hardship and gave birth to him with hardship, and his gestation and weaning [period] is thirty months. [He grows] until, when he reaches maturity and reaches [the age of] forty years, he says, ""My Lord, enable me to be grateful for Your favor which You have bestowed upon me and upon my parents and to work righteousness of which You will approve and make righteous for me my offspring. Indeed, I have repented to You, and indeed, I am of the Muslims", "وَوَصَّيۡنَا ٱلۡإِنسَٰنَ بِوَٰلِدَيۡهِ إِحۡسَٰنًاۖ حَمَلَتۡهُ أُمُّهُۥ كُرۡهٗا وَوَضَعَتۡهُ كُرۡهٗاۖ وَحَمۡلُهُۥ وَفِصَٰلُهُۥ ثَلَٰثُونَ شَهۡرًاۚ حَتَّىٰٓ إِذَا بَلَغَ أَشُدَّهُۥ وَبَلَغَ أَرۡبَعِينَ سَنَةٗ قَالَ رَبِّ أَوۡزِعۡنِيٓ أَنۡ أَشۡكُرَ نِعۡمَتَكَ ٱلَّتِيٓ أَنۡعَمۡتَ عَلَيَّ وَعَلَىٰ وَٰلِدَيَّ وَأَنۡ أَعۡمَلَ صَٰلِحٗا تَرۡضَىٰهُ وَأَصۡلِحۡ لِي فِي ذُرِّيَّتِيٓۖ إِنِّي تُبۡتُ إِلَيۡكَ وَإِنِّي مِنَ ٱلۡمُسۡلِمِينَ", "Wawassayna alinsana biwalidayhi ihsanan hamalathu ommuhu kurhan wawadaAAathu kurhan wahamluhu wafisaluhu thalathoona shahran hatta itha balagha ashuddahu wabalagha arbaAAeena sanatan qala rabbi awziAAnee an ashkura niAAmataka allatee anAAamta AAalayya waAAala walidayya waan aAAmala salihan tardahu waaslih lee fee thurriyyatee innee tubtu ilayka wainnee mina almuslimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (46, 16, "Those are the ones from whom We will accept the best of what they did and overlook their misdeeds, [their being] among the companions of Paradise. [That is] the promise of truth which they had been promised", "أُوْلَـٰٓئِكَ ٱلَّذِينَ نَتَقَبَّلُ عَنۡهُمۡ أَحۡسَنَ مَا عَمِلُواْ وَنَتَجَاوَزُ عَن سَيِّـَٔاتِهِمۡ فِيٓ أَصۡحَٰبِ ٱلۡجَنَّةِۖ وَعۡدَ ٱلصِّدۡقِ ٱلَّذِي كَانُواْ يُوعَدُونَ", "Olaika allatheena nataqabbalu AAanhum ahsana ma AAamiloo wanatajawazu AAan sayyiatihim fee ashabi aljannati waAAda alssidqi allathee kanoo yooAAadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (46, 17, "But one who says to his parents, ""Uff to you; do you promise me that I will be brought forth [from the earth] when generations before me have already passed on [into oblivion]?"" while they call to Allah for help [and to their son], ""Woe to you! Believe! Indeed, the promise of Allah is truth."" But he says, ""This is not but legends of the former people", "وَٱلَّذِي قَالَ لِوَٰلِدَيۡهِ أُفّٖ لَّكُمَآ أَتَعِدَانِنِيٓ أَنۡ أُخۡرَجَ وَقَدۡ خَلَتِ ٱلۡقُرُونُ مِن قَبۡلِي وَهُمَا يَسۡتَغِيثَانِ ٱللَّهَ وَيۡلَكَ ءَامِنۡ إِنَّ وَعۡدَ ٱللَّهِ حَقّٞ فَيَقُولُ مَا هَٰذَآ إِلَّآ أَسَٰطِيرُ ٱلۡأَوَّلِينَ", "Waallathee qala liwalidayhi offin lakuma ataAAidaninee an okhraja waqad khalati alquroonu min qablee wahuma yastagheethani Allaha waylaka amin inna waAAda Allahi haqqun fayaqoolu ma hatha illa asateeru alawwaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (46, 18, "Those are the ones upon whom the word has come into effect, [who will be] among nations which had passed on before them of jinn and men. Indeed, they [all] were losers", "أُوْلَـٰٓئِكَ ٱلَّذِينَ حَقَّ عَلَيۡهِمُ ٱلۡقَوۡلُ فِيٓ أُمَمٖ قَدۡ خَلَتۡ مِن قَبۡلِهِم مِّنَ ٱلۡجِنِّ وَٱلۡإِنسِۖ إِنَّهُمۡ كَانُواْ خَٰسِرِينَ", "Olaika allatheena haqqa AAalayhimu alqawlu fee umamin qad khalat min qablihim mina aljinni waalinsi innahum kanoo khasireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (46, 19, "And for all there are degrees [of reward and punishment] for what they have done, and [it is] so that He may fully compensate them for their deeds, and they will not be wronged", "وَلِكُلّٖ دَرَجَٰتٞ مِّمَّا عَمِلُواْۖ وَلِيُوَفِّيَهُمۡ أَعۡمَٰلَهُمۡ وَهُمۡ لَا يُظۡلَمُونَ", "Walikullin darajatun mimma AAamiloo waliyuwaffiyahum aAAmalahum wahum la yuthlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (46, 20, "And the Day those who disbelieved are exposed to the Fire [it will be said], ""You exhausted your pleasures during your worldly life and enjoyed them, so this Day you will be awarded the punishment of [extreme] humiliation because you were arrogant upon the earth without right and because you were defiantly disobedient", "وَيَوۡمَ يُعۡرَضُ ٱلَّذِينَ كَفَرُواْ عَلَى ٱلنَّارِ أَذۡهَبۡتُمۡ طَيِّبَٰتِكُمۡ فِي حَيَاتِكُمُ ٱلدُّنۡيَا وَٱسۡتَمۡتَعۡتُم بِهَا فَٱلۡيَوۡمَ تُجۡزَوۡنَ عَذَابَ ٱلۡهُونِ بِمَا كُنتُمۡ تَسۡتَكۡبِرُونَ فِي ٱلۡأَرۡضِ بِغَيۡرِ ٱلۡحَقِّ وَبِمَا كُنتُمۡ تَفۡسُقُونَ", "Wayawma yuAAradu allatheena kafaroo AAala alnnari athhabtum tayyibatikum fee hayatikumu alddunya waistamtaAAtum biha faalyawma tujzawna AAathaba alhooni bima kuntum tastakbiroona fee alardi bighayri alhaqqi wabima kuntum tafsuqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (46, 21, "And mention, [O Muhammad], the brother of 'Aad, when he warned his people in the [region of] al-Ahqaf - and warners had already passed on before him and after him - [saying], ""Do not worship except Allah. Indeed, I fear for you the punishment of a terrible day", "۞وَٱذۡكُرۡ أَخَا عَادٍ إِذۡ أَنذَرَ قَوۡمَهُۥ بِٱلۡأَحۡقَافِ وَقَدۡ خَلَتِ ٱلنُّذُرُ مِنۢ بَيۡنِ يَدَيۡهِ وَمِنۡ خَلۡفِهِۦٓ أَلَّا تَعۡبُدُوٓاْ إِلَّا ٱللَّهَ إِنِّيٓ أَخَافُ عَلَيۡكُمۡ عَذَابَ يَوۡمٍ عَظِيمٖ", "Waothkur akha AAadin ith anthara qawmahu bialahqafi waqad khalati alnnuthuru min bayni yadayhi wamin khalfihi alla taAAbudoo illa Allaha innee akhafu AAalaykum AAathaba yawmin AAatheemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (46, 22, "They said, ""Have you come to delude us away from our gods? Then bring us what you promise us, if you should be of the truthful", "قَالُوٓاْ أَجِئۡتَنَا لِتَأۡفِكَنَا عَنۡ ءَالِهَتِنَا فَأۡتِنَا بِمَا تَعِدُنَآ إِن كُنتَ مِنَ ٱلصَّـٰدِقِينَ", "Qaloo ajitana litafikana AAan alihatina fatina bima taAAiduna in kunta mina alssadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (46, 23, "He said, ""Knowledge [of its time] is only with Allah, and I convey to you that with which I was sent; but I see you [to be] a people behaving ignorantly", "قَالَ إِنَّمَا ٱلۡعِلۡمُ عِندَ ٱللَّهِ وَأُبَلِّغُكُم مَّآ أُرۡسِلۡتُ بِهِۦ وَلَٰكِنِّيٓ أَرَىٰكُمۡ قَوۡمٗا تَجۡهَلُونَ", "Qala innama alAAilmu AAinda Allahi waoballighukum ma orsiltu bihi walakinnee arakum qawman tajhaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (46, 24, "And when they saw it as a cloud approaching their valleys, they said, ""This is a cloud bringing us rain!"" Rather, it is that for which you were impatient: a wind, within it a painful punishment", "فَلَمَّا رَأَوۡهُ عَارِضٗا مُّسۡتَقۡبِلَ أَوۡدِيَتِهِمۡ قَالُواْ هَٰذَا عَارِضٞ مُّمۡطِرُنَاۚ بَلۡ هُوَ مَا ٱسۡتَعۡجَلۡتُم بِهِۦۖ رِيحٞ فِيهَا عَذَابٌ أَلِيمٞ", "Falamma raawhu AAaridan mustaqbila awdiyatihim qaloo hatha AAaridun mumtiruna bal huwa ma istaAAjaltum bihi reehun feeha AAathabun aleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (46, 25, "Destroying everything by command of its Lord. And they became so that nothing was seen [of them] except their dwellings. Thus do We recompense the criminal people", "تُدَمِّرُ كُلَّ شَيۡءِۭ بِأَمۡرِ رَبِّهَا فَأَصۡبَحُواْ لَا يُرَىٰٓ إِلَّا مَسَٰكِنُهُمۡۚ كَذَٰلِكَ نَجۡزِي ٱلۡقَوۡمَ ٱلۡمُجۡرِمِينَ", "Tudammiru kulla shayin biamri rabbiha faasbahoo la yura illa masakinuhum kathalika najzee alqawma almujrimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (46, 26, "And We had certainly established them in such as We have not established you, and We made for them hearing and vision and hearts. But their hearing and vision and hearts availed them not from anything [of the punishment] when they were [continually] rejecting the signs of Allah; and they were enveloped by what they used to ridicule", "وَلَقَدۡ مَكَّنَّـٰهُمۡ فِيمَآ إِن مَّكَّنَّـٰكُمۡ فِيهِ وَجَعَلۡنَا لَهُمۡ سَمۡعٗا وَأَبۡصَٰرٗا وَأَفۡـِٔدَةٗ فَمَآ أَغۡنَىٰ عَنۡهُمۡ سَمۡعُهُمۡ وَلَآ أَبۡصَٰرُهُمۡ وَلَآ أَفۡـِٔدَتُهُم مِّن شَيۡءٍ إِذۡ كَانُواْ يَجۡحَدُونَ بِـَٔايَٰتِ ٱللَّهِ وَحَاقَ بِهِم مَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ", "Walaqad makkannahum feema in makkannakum feehi wajaAAalna lahum samAAan waabsaran waafidatan fama aghna AAanhum samAAuhum wala absaruhum wala afidatuhum min shayin ith kanoo yajhadoona biayati Allahi wahaqa bihim ma kanoo bihi yastahzioona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (46, 27, "And We have already destroyed what surrounds you of [those] cities, and We have diversified the signs [or verses] that perhaps they might return [from disbelief]", "وَلَقَدۡ أَهۡلَكۡنَا مَا حَوۡلَكُم مِّنَ ٱلۡقُرَىٰ وَصَرَّفۡنَا ٱلۡأٓيَٰتِ لَعَلَّهُمۡ يَرۡجِعُونَ", "Walaqad ahlakna ma hawlakum mina alqura wasarrafna alayati laAAallahum yarjiAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (46, 28, "Then why did those they took besides Allah as deities by which to approach [Him] not aid them? But they had strayed from them. And that was their falsehood and what they were inventing", "فَلَوۡلَا نَصَرَهُمُ ٱلَّذِينَ ٱتَّخَذُواْ مِن دُونِ ٱللَّهِ قُرۡبَانًا ءَالِهَةَۢۖ بَلۡ ضَلُّواْ عَنۡهُمۡۚ وَذَٰلِكَ إِفۡكُهُمۡ وَمَا كَانُواْ يَفۡتَرُونَ", "Falawla nasarahumu allatheena ittakhathoo min dooni Allahi qurbanan alihatan bal dalloo AAanhum wathalika ifkuhum wama kanoo yaftaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (46, 29, "And [mention, O Muhammad], when We directed to you a few of the jinn, listening to the Qur'an. And when they attended it, they said, ""Listen quietly."" And when it was concluded, they went back to their people as warners", "وَإِذۡ صَرَفۡنَآ إِلَيۡكَ نَفَرٗا مِّنَ ٱلۡجِنِّ يَسۡتَمِعُونَ ٱلۡقُرۡءَانَ فَلَمَّا حَضَرُوهُ قَالُوٓاْ أَنصِتُواْۖ فَلَمَّا قُضِيَ وَلَّوۡاْ إِلَىٰ قَوۡمِهِم مُّنذِرِينَ", "Waith sarafna ilayka nafaran mina aljinni yastamiAAoona alqurana falamma hadaroohu qaloo ansitoo falamma qudiya wallaw ila qawmihim munthireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (46, 30, "They said, ""O our people, indeed we have heard a [recited] Book revealed after Moses confirming what was before it which guides to the truth and to a straight path", "قَالُواْ يَٰقَوۡمَنَآ إِنَّا سَمِعۡنَا كِتَٰبًا أُنزِلَ مِنۢ بَعۡدِ مُوسَىٰ مُصَدِّقٗا لِّمَا بَيۡنَ يَدَيۡهِ يَهۡدِيٓ إِلَى ٱلۡحَقِّ وَإِلَىٰ طَرِيقٖ مُّسۡتَقِيمٖ", "Qaloo ya qawmana inna samiAAna kitaban onzila min baAAdi moosa musaddiqan lima bayna yadayhi yahdee ila alhaqqi waila tareeqin mustaqeemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (46, 31, "O our people, respond to the Messenger of Allah and believe in him; Allah will forgive for you your sins and protect you from a painful punishment", "يَٰقَوۡمَنَآ أَجِيبُواْ دَاعِيَ ٱللَّهِ وَءَامِنُواْ بِهِۦ يَغۡفِرۡ لَكُم مِّن ذُنُوبِكُمۡ وَيُجِرۡكُم مِّنۡ عَذَابٍ أَلِيمٖ", "Ya qawmana ajeeboo daAAiya Allahi waaminoo bihi yaghfir lakum min thunoobikum wayujirkum min AAathabin aleemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (46, 32, "But he who does not respond to the Caller of Allah will not cause failure [to Him] upon earth, and he will not have besides Him any protectors. Those are in manifest error", "وَمَن لَّا يُجِبۡ دَاعِيَ ٱللَّهِ فَلَيۡسَ بِمُعۡجِزٖ فِي ٱلۡأَرۡضِ وَلَيۡسَ لَهُۥ مِن دُونِهِۦٓ أَوۡلِيَآءُۚ أُوْلَـٰٓئِكَ فِي ضَلَٰلٖ مُّبِينٍ", "Waman la yujib daAAiya Allahi falaysa bimuAAjizin fee alardi walaysa lahu min doonihi awliyaa olaika fee dalalin mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (46, 33, "Do they not see that Allah, who created the heavens and earth and did not fail in their creation, is able to give life to the dead? Yes. Indeed, He is over all things competent", "أَوَلَمۡ يَرَوۡاْ أَنَّ ٱللَّهَ ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَلَمۡ يَعۡيَ بِخَلۡقِهِنَّ بِقَٰدِرٍ عَلَىٰٓ أَن يُحۡـِۧيَ ٱلۡمَوۡتَىٰۚ بَلَىٰٓۚ إِنَّهُۥ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ", "Awalam yaraw anna Allaha allathee khalaqa alssamawati waalarda walam yaAAya bikhalqihinna biqadirin AAala an yuhyiya almawta bala innahu AAala kulli shayin qadeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (46, 34, "And the Day those who disbelieved are exposed to the Fire [it will be said], ""Is this not the truth?"" They will say, ""Yes, by our Lord."" He will say, ""Then taste the punishment because you used to disbelieve", "وَيَوۡمَ يُعۡرَضُ ٱلَّذِينَ كَفَرُواْ عَلَى ٱلنَّارِ أَلَيۡسَ هَٰذَا بِٱلۡحَقِّۖ قَالُواْ بَلَىٰ وَرَبِّنَاۚ قَالَ فَذُوقُواْ ٱلۡعَذَابَ بِمَا كُنتُمۡ تَكۡفُرُونَ", "Wayawma yuAAradu allatheena kafaroo AAala alnnari alaysa hatha bialhaqqi qaloo bala warabbina qala fathooqoo alAAathaba bima kuntum takfuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (46, 35, "So be patient, [O Muhammad], as were those of determination among the messengers and do not be impatient for them. It will be - on the Day they see that which they are promised - as though they had not remained [in the world] except an hour of a day. [This is] notification. And will [any] be destroyed except the defiantly disobedient people", "فَٱصۡبِرۡ كَمَا صَبَرَ أُوْلُواْ ٱلۡعَزۡمِ مِنَ ٱلرُّسُلِ وَلَا تَسۡتَعۡجِل لَّهُمۡۚ كَأَنَّهُمۡ يَوۡمَ يَرَوۡنَ مَا يُوعَدُونَ لَمۡ يَلۡبَثُوٓاْ إِلَّا سَاعَةٗ مِّن نَّهَارِۭۚ بَلَٰغٞۚ فَهَلۡ يُهۡلَكُ إِلَّا ٱلۡقَوۡمُ ٱلۡفَٰسِقُونَ", "Faisbir kama sabara oloo alAAazmi mina alrrusuli wala tastaAAjil lahum kaannahum yawma yarawna ma yooAAadoona lam yalbathoo illa saAAatan min naharin balaghun fahal yuhlaku illa alqawmu alfasiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 1, "Those who disbelieve and avert [people] from the way of Allah - He will waste their deeds", "ٱلَّذِينَ كَفَرُواْ وَصَدُّواْ عَن سَبِيلِ ٱللَّهِ أَضَلَّ أَعۡمَٰلَهُمۡ", "Allatheena kafaroo wasaddoo AAan sabeeli Allahi adalla aAAmalahum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 2, "And those who believe and do righteous deeds and believe in what has been sent down upon Muhammad - and it is the truth from their Lord - He will remove from them their misdeeds and amend their condition", "وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ وَءَامَنُواْ بِمَا نُزِّلَ عَلَىٰ مُحَمَّدٖ وَهُوَ ٱلۡحَقُّ مِن رَّبِّهِمۡ كَفَّرَ عَنۡهُمۡ سَيِّـَٔاتِهِمۡ وَأَصۡلَحَ بَالَهُمۡ", "Waallatheena amanoo waAAamiloo alssalihati waamanoo bima nuzzila AAala muhammadin wahuwa alhaqqu min rabbihim kaffara AAanhum sayyiatihim waaslaha balahum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 3, "That is because those who disbelieve follow falsehood, and those who believe follow the truth from their Lord. Thus does Allah present to the people their comparisons", "ذَٰلِكَ بِأَنَّ ٱلَّذِينَ كَفَرُواْ ٱتَّبَعُواْ ٱلۡبَٰطِلَ وَأَنَّ ٱلَّذِينَ ءَامَنُواْ ٱتَّبَعُواْ ٱلۡحَقَّ مِن رَّبِّهِمۡۚ كَذَٰلِكَ يَضۡرِبُ ٱللَّهُ لِلنَّاسِ أَمۡثَٰلَهُمۡ", "Thalika bianna allatheena kafaroo ittabaAAoo albatila waanna allatheena amanoo ittabaAAoo alhaqqa min rabbihim kathalika yadribu Allahu lilnnasi amthalahum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 4, "So when you meet those who disbelieve [in battle], strike [their] necks until, when you have inflicted slaughter upon them, then secure their bonds, and either [confer] favor afterwards or ransom [them] until the war lays down its burdens. That [is the command]. And if Allah had willed, He could have taken vengeance upon them [Himself], but [He ordered armed struggle] to test some of you by means of others. And those who are killed in the cause of Allah - never will He waste their deeds", "فَإِذَا لَقِيتُمُ ٱلَّذِينَ كَفَرُواْ فَضَرۡبَ ٱلرِّقَابِ حَتَّىٰٓ إِذَآ أَثۡخَنتُمُوهُمۡ فَشُدُّواْ ٱلۡوَثَاقَ فَإِمَّا مَنَّۢا بَعۡدُ وَإِمَّا فِدَآءً حَتَّىٰ تَضَعَ ٱلۡحَرۡبُ أَوۡزَارَهَاۚ ذَٰلِكَۖ وَلَوۡ يَشَآءُ ٱللَّهُ لَٱنتَصَرَ مِنۡهُمۡ وَلَٰكِن لِّيَبۡلُوَاْ بَعۡضَكُم بِبَعۡضٖۗ وَٱلَّذِينَ قُتِلُواْ فِي سَبِيلِ ٱللَّهِ فَلَن يُضِلَّ أَعۡمَٰلَهُمۡ", "Faitha laqeetumu allatheena kafaroo fadarba alrriqabi hatta itha athkhantumoohum fashuddoo alwathaqa faimma mannan baAAdu waimma fidaan hatta tadaAAa alharbu awzaraha thalika walaw yashao Allahu laintasara minhum walakin liyabluwa baAAdakum bibaAAdin waallatheena qutiloo fee sabeeli Allahi falan yudilla aAAmalahum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 5, "He will guide them and amend their condition", "سَيَهۡدِيهِمۡ وَيُصۡلِحُ بَالَهُمۡ", "Sayahdeehim wayuslihu balahum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 6, "And admit them to Paradise, which He has made known to them", "وَيُدۡخِلُهُمُ ٱلۡجَنَّةَ عَرَّفَهَا لَهُمۡ", "Wayudkhiluhumu aljannata AAarrafaha lahum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 7, "O you who have believed, if you support Allah, He will support you and plant firmly your feet", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِن تَنصُرُواْ ٱللَّهَ يَنصُرۡكُمۡ وَيُثَبِّتۡ أَقۡدَامَكُمۡ", "Ya ayyuha allatheena amanoo in tansuroo Allaha yansurkum wayuthabbit aqdamakum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 8, "But those who disbelieve - for them is misery, and He will waste their deeds", "وَٱلَّذِينَ كَفَرُواْ فَتَعۡسٗا لَّهُمۡ وَأَضَلَّ أَعۡمَٰلَهُمۡ", "Waallatheena kafaroo fataAAsan lahum waadalla aAAmalahum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 9, "That is because they disliked what Allah revealed, so He rendered worthless their deeds", "ذَٰلِكَ بِأَنَّهُمۡ كَرِهُواْ مَآ أَنزَلَ ٱللَّهُ فَأَحۡبَطَ أَعۡمَٰلَهُمۡ", "Thalika biannahum karihoo ma anzala Allahu faahbata aAAmalahum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 10, "Have they not traveled through the land and seen how was the end of those before them? Allah destroyed [everything] over them, and for the disbelievers is something comparable", "۞أَفَلَمۡ يَسِيرُواْ فِي ٱلۡأَرۡضِ فَيَنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلَّذِينَ مِن قَبۡلِهِمۡۖ دَمَّرَ ٱللَّهُ عَلَيۡهِمۡۖ وَلِلۡكَٰفِرِينَ أَمۡثَٰلُهَا", "Afalam yaseeroo fee alardi fayanthuroo kayfa kana AAaqibatu allatheena min qablihim dammara Allahu AAalayhim walilkafireena amthaluha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 11, "That is because Allah is the protector of those who have believed and because the disbelievers have no protector", "ذَٰلِكَ بِأَنَّ ٱللَّهَ مَوۡلَى ٱلَّذِينَ ءَامَنُواْ وَأَنَّ ٱلۡكَٰفِرِينَ لَا مَوۡلَىٰ لَهُمۡ", "Thalika bianna Allaha mawla allatheena amanoo waanna alkafireena la mawla lahum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 12, "Indeed, Allah will admit those who have believed and done righteous deeds to gardens beneath which rivers flow, but those who disbelieve enjoy themselves and eat as grazing livestock eat, and the Fire will be a residence for them", "إِنَّ ٱللَّهَ يُدۡخِلُ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُۖ وَٱلَّذِينَ كَفَرُواْ يَتَمَتَّعُونَ وَيَأۡكُلُونَ كَمَا تَأۡكُلُ ٱلۡأَنۡعَٰمُ وَٱلنَّارُ مَثۡوٗى لَّهُمۡ", "Inna Allaha yudkhilu allatheena amanoo waAAamiloo alssalihati jannatin tajree min tahtiha alanharu waallatheena kafaroo yatamattaAAoona wayakuloona kama takulu alanAAamu waalnnaru mathwan lahum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 13, "And how many a city was stronger than your city [Makkah] which drove you out? We destroyed them; and there was no helper for them", "وَكَأَيِّن مِّن قَرۡيَةٍ هِيَ أَشَدُّ قُوَّةٗ مِّن قَرۡيَتِكَ ٱلَّتِيٓ أَخۡرَجَتۡكَ أَهۡلَكۡنَٰهُمۡ فَلَا نَاصِرَ لَهُمۡ", "Wakaayyin min qaryatin hiya ashaddu quwwatan min qaryatika allatee akhrajatka ahlaknahum fala nasira lahum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 14, "So is he who is on clear evidence from his Lord like him to whom the evil of his work has been made attractive and they follow their [own] desires", "أَفَمَن كَانَ عَلَىٰ بَيِّنَةٖ مِّن رَّبِّهِۦ كَمَن زُيِّنَ لَهُۥ سُوٓءُ عَمَلِهِۦ وَٱتَّبَعُوٓاْ أَهۡوَآءَهُم", "Afaman kana AAala bayyinatin min rabbihi kaman zuyyina lahu sooo AAamalihi waittabaAAoo ahwaahum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 15, "Is the description of Paradise, which the righteous are promised, wherein are rivers of water unaltered, rivers of milk the taste of which never changes, rivers of wine delicious to those who drink, and rivers of purified honey, in which they will have from all [kinds of] fruits and forgiveness from their Lord, like [that of] those who abide eternally in the Fire and are given to drink scalding water that will sever their intestines", "مَّثَلُ ٱلۡجَنَّةِ ٱلَّتِي وُعِدَ ٱلۡمُتَّقُونَۖ فِيهَآ أَنۡهَٰرٞ مِّن مَّآءٍ غَيۡرِ ءَاسِنٖ وَأَنۡهَٰرٞ مِّن لَّبَنٖ لَّمۡ يَتَغَيَّرۡ طَعۡمُهُۥ وَأَنۡهَٰرٞ مِّنۡ خَمۡرٖ لَّذَّةٖ لِّلشَّـٰرِبِينَ وَأَنۡهَٰرٞ مِّنۡ عَسَلٖ مُّصَفّٗىۖ وَلَهُمۡ فِيهَا مِن كُلِّ ٱلثَّمَرَٰتِ وَمَغۡفِرَةٞ مِّن رَّبِّهِمۡۖ كَمَنۡ هُوَ خَٰلِدٞ فِي ٱلنَّارِ وَسُقُواْ مَآءً حَمِيمٗا فَقَطَّعَ أَمۡعَآءَهُمۡ", "Mathalu aljannati allatee wuAAida almuttaqoona feeha anharun min main ghayri asinin waanharun min labanin lam yataghayyar taAAmuhu waanharun min khamrin laththatin lilshsharibeena waanharun min AAasalin musaffan walahum feeha min kulli alththamarati wamaghfiratun min rabbihim kaman huwa khalidun fee alnnari wasuqoo maan hameeman faqattaAAa amAAaahum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 16, "And among them, [O Muhammad], are those who listen to you, until when they depart from you, they say to those who were given knowledge, ""What has he said just now?"" Those are the ones of whom Allah has sealed over their hearts and who have followed their [own] desires", "وَمِنۡهُم مَّن يَسۡتَمِعُ إِلَيۡكَ حَتَّىٰٓ إِذَا خَرَجُواْ مِنۡ عِندِكَ قَالُواْ لِلَّذِينَ أُوتُواْ ٱلۡعِلۡمَ مَاذَا قَالَ ءَانِفًاۚ أُوْلَـٰٓئِكَ ٱلَّذِينَ طَبَعَ ٱللَّهُ عَلَىٰ قُلُوبِهِمۡ وَٱتَّبَعُوٓاْ أَهۡوَآءَهُمۡ", "Waminhum man yastamiAAu ilayka hatta itha kharajoo min AAindika qaloo lillatheena ootoo alAAilma matha qala anifan olaika allatheena tabaAAa Allahu AAala quloobihim waittabaAAoo ahwaahum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 17, "And those who are guided - He increases them in guidance and gives them their righteousness", "وَٱلَّذِينَ ٱهۡتَدَوۡاْ زَادَهُمۡ هُدٗى وَءَاتَىٰهُمۡ تَقۡوَىٰهُمۡ", "Waallatheena ihtadaw zadahum hudan waatahum taqwahum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 18, "Then do they await except that the Hour should come upon them unexpectedly? But already there have come [some of] its indications. Then what good to them, when it has come, will be their remembrance", "فَهَلۡ يَنظُرُونَ إِلَّا ٱلسَّاعَةَ أَن تَأۡتِيَهُم بَغۡتَةٗۖ فَقَدۡ جَآءَ أَشۡرَاطُهَاۚ فَأَنَّىٰ لَهُمۡ إِذَا جَآءَتۡهُمۡ ذِكۡرَىٰهُمۡ", "Fahal yanthuroona illa alssaAAata an tatiyahum baghtatan faqad jaa ashratuha faanna lahum itha jaathum thikrahum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 19, "So know, [O Muhammad], that there is no deity except Allah and ask forgiveness for your sin and for the believing men and believing women. And Allah knows of your movement and your resting place", "فَٱعۡلَمۡ أَنَّهُۥ لَآ إِلَٰهَ إِلَّا ٱللَّهُ وَٱسۡتَغۡفِرۡ لِذَنۢبِكَ وَلِلۡمُؤۡمِنِينَ وَٱلۡمُؤۡمِنَٰتِۗ وَٱللَّهُ يَعۡلَمُ مُتَقَلَّبَكُمۡ وَمَثۡوَىٰكُمۡ", "FaiAAlam annahu la ilaha illa Allahu waistaghfir lithanbika walilmumineena waalmuminati waAllahu yaAAlamu mutaqallabakum wamathwakum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 20, "Those who believe say, ""Why has a surah not been sent down? But when a precise surah is revealed and fighting is mentioned therein, you see those in whose hearts is hypocrisy looking at you with a look of one overcome by death. And more appropriate for them [would have been]", "وَيَقُولُ ٱلَّذِينَ ءَامَنُواْ لَوۡلَا نُزِّلَتۡ سُورَةٞۖ فَإِذَآ أُنزِلَتۡ سُورَةٞ مُّحۡكَمَةٞ وَذُكِرَ فِيهَا ٱلۡقِتَالُ رَأَيۡتَ ٱلَّذِينَ فِي قُلُوبِهِم مَّرَضٞ يَنظُرُونَ إِلَيۡكَ نَظَرَ ٱلۡمَغۡشِيِّ عَلَيۡهِ مِنَ ٱلۡمَوۡتِۖ فَأَوۡلَىٰ لَهُمۡ", "Wayaqoolu allatheena amanoo lawla nuzzilat sooratun faitha onzilat sooratun muhkamatun wathukira feeha alqitalu raayta allatheena fee quloobihim maradun yanthuroona ilayka nathara almaghshiyyi AAalayhi mina almawti faawla lahum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 21, "Obedience and good words. And when the matter [of fighting] was determined, if they had been true to Allah, it would have been better for them", "طَاعَةٞ وَقَوۡلٞ مَّعۡرُوفٞۚ فَإِذَا عَزَمَ ٱلۡأَمۡرُ فَلَوۡ صَدَقُواْ ٱللَّهَ لَكَانَ خَيۡرٗا لَّهُمۡ", "TaAAatun waqawlun maAAroofun faitha AAazama alamru falaw sadaqoo Allaha lakana khayran lahum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 22, "So would you perhaps, if you turned away, cause corruption on earth and sever your [ties of] relationship", "فَهَلۡ عَسَيۡتُمۡ إِن تَوَلَّيۡتُمۡ أَن تُفۡسِدُواْ فِي ٱلۡأَرۡضِ وَتُقَطِّعُوٓاْ أَرۡحَامَكُمۡ", "Fahal AAasaytum in tawallaytum an tufsidoo fee alardi watuqattiAAoo arhamakum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 23, "Those [who do so] are the ones that Allah has cursed, so He deafened them and blinded their vision", "أُوْلَـٰٓئِكَ ٱلَّذِينَ لَعَنَهُمُ ٱللَّهُ فَأَصَمَّهُمۡ وَأَعۡمَىٰٓ أَبۡصَٰرَهُمۡ", "Olaika allatheena laAAanahumu Allahu faasammahum waaAAma absarahum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 24, "Then do they not reflect upon the Qur'an, or are there locks upon [their] hearts", "أَفَلَا يَتَدَبَّرُونَ ٱلۡقُرۡءَانَ أَمۡ عَلَىٰ قُلُوبٍ أَقۡفَالُهَآ", "Afala yatadabbaroona alqurana am AAala quloobin aqfaluha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 25, "Indeed, those who reverted back [to disbelief] after guidance had become clear to them - Satan enticed them and prolonged hope for them", "إِنَّ ٱلَّذِينَ ٱرۡتَدُّواْ عَلَىٰٓ أَدۡبَٰرِهِم مِّنۢ بَعۡدِ مَا تَبَيَّنَ لَهُمُ ٱلۡهُدَى ٱلشَّيۡطَٰنُ سَوَّلَ لَهُمۡ وَأَمۡلَىٰ لَهُمۡ", "Inna allatheena irtaddoo AAala adbarihim min baAAdi ma tabayyana lahumu alhuda alshshaytanu sawwala lahum waamla lahum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 26, "That is because they said to those who disliked what Allah sent down, ""We will obey you in part of the matter."" And Allah knows what they conceal", "ذَٰلِكَ بِأَنَّهُمۡ قَالُواْ لِلَّذِينَ كَرِهُواْ مَا نَزَّلَ ٱللَّهُ سَنُطِيعُكُمۡ فِي بَعۡضِ ٱلۡأَمۡرِۖ وَٱللَّهُ يَعۡلَمُ إِسۡرَارَهُمۡ", "Thalika biannahum qaloo lillatheena karihoo ma nazzala Allahu sanuteeAAukum fee baAAdi alamri waAllahu yaAAlamu israrahum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 27, "Then how [will it be] when the angels take them in death, striking their faces and their backs", "فَكَيۡفَ إِذَا تَوَفَّتۡهُمُ ٱلۡمَلَـٰٓئِكَةُ يَضۡرِبُونَ وُجُوهَهُمۡ وَأَدۡبَٰرَهُمۡ", "Fakayfa itha tawaffathumu almalaikatu yadriboona wujoohahum waadbarahum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 28, "That is because they followed what angered Allah and disliked [what earns] His pleasure, so He rendered worthless their deeds", "ذَٰلِكَ بِأَنَّهُمُ ٱتَّبَعُواْ مَآ أَسۡخَطَ ٱللَّهَ وَكَرِهُواْ رِضۡوَٰنَهُۥ فَأَحۡبَطَ أَعۡمَٰلَهُمۡ", "Thalika biannahumu ittabaAAoo ma askhata Allaha wakarihoo ridwanahu faahbata aAAmalahum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 29, "Or do those in whose hearts is disease think that Allah would never expose their [feelings of] hatred", "أَمۡ حَسِبَ ٱلَّذِينَ فِي قُلُوبِهِم مَّرَضٌ أَن لَّن يُخۡرِجَ ٱللَّهُ أَضۡغَٰنَهُمۡ", "Am hasiba allatheena fee quloobihim maradun an lan yukhrija Allahu adghanahum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 30, "And if We willed, We could show them to you, and you would know them by their mark; but you will surely know them by the tone of [their] speech. And Allah knows your deeds", "وَلَوۡ نَشَآءُ لَأَرَيۡنَٰكَهُمۡ فَلَعَرَفۡتَهُم بِسِيمَٰهُمۡۚ وَلَتَعۡرِفَنَّهُمۡ فِي لَحۡنِ ٱلۡقَوۡلِۚ وَٱللَّهُ يَعۡلَمُ أَعۡمَٰلَكُمۡ", "Walaw nashao laaraynakahum falaAAaraftahum biseemahum walataAArifannahum fee lahni alqawli waAllahu yaAAlamu aAAmalakum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 31, "And We will surely test you until We make evident those who strive among you [for the cause of Allah] and the patient, and We will test your affairs", "وَلَنَبۡلُوَنَّكُمۡ حَتَّىٰ نَعۡلَمَ ٱلۡمُجَٰهِدِينَ مِنكُمۡ وَٱلصَّـٰبِرِينَ وَنَبۡلُوَاْ أَخۡبَارَكُمۡ", "Walanabluwannakum hatta naAAlama almujahideena minkum waalssabireena wanabluwa akhbarakum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 32, "Indeed, those who disbelieved and averted [people] from the path of Allah and opposed the Messenger after guidance had become clear to them - never will they harm Allah at all, and He will render worthless their deeds", "إِنَّ ٱلَّذِينَ كَفَرُواْ وَصَدُّواْ عَن سَبِيلِ ٱللَّهِ وَشَآقُّواْ ٱلرَّسُولَ مِنۢ بَعۡدِ مَا تَبَيَّنَ لَهُمُ ٱلۡهُدَىٰ لَن يَضُرُّواْ ٱللَّهَ شَيۡـٔٗا وَسَيُحۡبِطُ أَعۡمَٰلَهُمۡ", "Inna allatheena kafaroo wasaddoo AAan sabeeli Allahi washaqqoo alrrasoola min baAAdi ma tabayyana lahumu alhuda lan yaduroo Allaha shayan wasayuhbitu aAAmalahum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 33, "O you who have believed, obey Allah and obey the Messenger and do not invalidate your deeds", "۞يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ أَطِيعُواْ ٱللَّهَ وَأَطِيعُواْ ٱلرَّسُولَ وَلَا تُبۡطِلُوٓاْ أَعۡمَٰلَكُمۡ", "Ya ayyuha allatheena amanoo ateeAAoo Allaha waateeAAoo alrrasoola wala tubtiloo aAAmalakum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 34, "Indeed, those who disbelieved and averted [people] from the path of Allah and then died while they were disbelievers - never will Allah forgive them", "إِنَّ ٱلَّذِينَ كَفَرُواْ وَصَدُّواْ عَن سَبِيلِ ٱللَّهِ ثُمَّ مَاتُواْ وَهُمۡ كُفَّارٞ فَلَن يَغۡفِرَ ٱللَّهُ لَهُمۡ", "Inna allatheena kafaroo wasaddoo AAan sabeeli Allahi thumma matoo wahum kuffarun falan yaghfira Allahu lahum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 35, "So do not weaken and call for peace while you are superior; and Allah is with you and will never deprive you of [the reward of] your deeds", "فَلَا تَهِنُواْ وَتَدۡعُوٓاْ إِلَى ٱلسَّلۡمِ وَأَنتُمُ ٱلۡأَعۡلَوۡنَ وَٱللَّهُ مَعَكُمۡ وَلَن يَتِرَكُمۡ أَعۡمَٰلَكُمۡ", "Fala tahinoo watadAAoo ila alssalmi waantumu alaAAlawna waAllahu maAAakum walan yatirakum aAAmalakum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 36, "[This] worldly life is only amusement and diversion. And if you believe and fear Allah, He will give you your rewards and not ask you for your properties", "إِنَّمَا ٱلۡحَيَوٰةُ ٱلدُّنۡيَا لَعِبٞ وَلَهۡوٞۚ وَإِن تُؤۡمِنُواْ وَتَتَّقُواْ يُؤۡتِكُمۡ أُجُورَكُمۡ وَلَا يَسۡـَٔلۡكُمۡ أَمۡوَٰلَكُمۡ", "Innama alhayatu alddunya laAAibun walahwun wain tuminoo watattaqoo yutikum ojoorakum wala yasalkum amwalakum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 37, "If He should ask you for them and press you, you would withhold, and He would expose your unwillingness", "إِن يَسۡـَٔلۡكُمُوهَا فَيُحۡفِكُمۡ تَبۡخَلُواْ وَيُخۡرِجۡ أَضۡغَٰنَكُمۡ", "In yasalkumooha fayuhfikum tabkhaloo wayukhrij adghanakum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (47, 38, "Here you are - those invited to spend in the cause of Allah - but among you are those who withhold [out of greed]. And whoever withholds only withholds [benefit] from himself; and Allah is the Free of need, while you are the needy. And if you turn away, He will replace you with another people; then they will not be the likes of you", "هَـٰٓأَنتُمۡ هَـٰٓؤُلَآءِ تُدۡعَوۡنَ لِتُنفِقُواْ فِي سَبِيلِ ٱللَّهِ فَمِنكُم مَّن يَبۡخَلُۖ وَمَن يَبۡخَلۡ فَإِنَّمَا يَبۡخَلُ عَن نَّفۡسِهِۦۚ وَٱللَّهُ ٱلۡغَنِيُّ وَأَنتُمُ ٱلۡفُقَرَآءُۚ وَإِن تَتَوَلَّوۡاْ يَسۡتَبۡدِلۡ قَوۡمًا غَيۡرَكُمۡ ثُمَّ لَا يَكُونُوٓاْ أَمۡثَٰلَكُم", "Haantum haolai tudAAawna litunfiqoo fee sabeeli Allahi faminkum man yabkhalu waman yabkhal fainnama yabkhalu AAan nafsihi waAllahu alghaniyyu waantumu alfuqarao wain tatawallaw yastabdil qawman ghayrakum thumma la yakoonoo amthalakum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (48, 1, "Indeed, We have given you, [O Muhammad], a clear conquest", "إِنَّا فَتَحۡنَا لَكَ فَتۡحٗا مُّبِينٗا", "Inna fatahna laka fathan mubeenan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (48, 2, "That Allah may forgive for you what preceded of your sin and what will follow and complete His favor upon you and guide you to a straight path", "لِّيَغۡفِرَ لَكَ ٱللَّهُ مَا تَقَدَّمَ مِن ذَنۢبِكَ وَمَا تَأَخَّرَ وَيُتِمَّ نِعۡمَتَهُۥ عَلَيۡكَ وَيَهۡدِيَكَ صِرَٰطٗا مُّسۡتَقِيمٗا", "Liyaghfira laka Allahu ma taqaddama min thanbika wama taakhkhara wayutimma niAAmatahu AAalayka wayahdiyaka siratan mustaqeeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (48, 3, "And [that] Allah may aid you with a mighty victory", "وَيَنصُرَكَ ٱللَّهُ نَصۡرًا عَزِيزًا", "Wayansuraka Allahu nasran AAazeezan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (48, 4, "It is He who sent down tranquillity into the hearts of the believers that they would increase in faith along with their [present] faith. And to Allah belong the soldiers of the heavens and the earth, and ever is Allah Knowing and Wise", "هُوَ ٱلَّذِيٓ أَنزَلَ ٱلسَّكِينَةَ فِي قُلُوبِ ٱلۡمُؤۡمِنِينَ لِيَزۡدَادُوٓاْ إِيمَٰنٗا مَّعَ إِيمَٰنِهِمۡۗ وَلِلَّهِ جُنُودُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَكَانَ ٱللَّهُ عَلِيمًا حَكِيمٗا", "Huwa allathee anzala alssakeenata fee quloobi almumineena liyazdadoo eemanan maAAa eemanihim walillahi junoodu alssamawati waalardi wakana Allahu AAaleeman hakeeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (48, 5, "[And] that He may admit the believing men and the believing women to gardens beneath which rivers flow to abide therein eternally and remove from them their misdeeds - and ever is that, in the sight of Allah, a great attainment", "لِّيُدۡخِلَ ٱلۡمُؤۡمِنِينَ وَٱلۡمُؤۡمِنَٰتِ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَا وَيُكَفِّرَ عَنۡهُمۡ سَيِّـَٔاتِهِمۡۚ وَكَانَ ذَٰلِكَ عِندَ ٱللَّهِ فَوۡزًا عَظِيمٗا", "Liyudkhila almumineena waalmuminati jannatin tajree min tahtiha alanharu khalideena feeha wayukaffira AAanhum sayyiatihim wakana thalika AAinda Allahi fawzan AAatheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (48, 6, "And [that] He may punish the hypocrite men and hypocrite women, and the polytheist men and polytheist women - those who assume about Allah an assumption of evil nature. Upon them is a misfortune of evil nature; and Allah has become angry with them and has cursed them and prepared for them Hell, and evil it is as a destination", "وَيُعَذِّبَ ٱلۡمُنَٰفِقِينَ وَٱلۡمُنَٰفِقَٰتِ وَٱلۡمُشۡرِكِينَ وَٱلۡمُشۡرِكَٰتِ ٱلظَّآنِّينَ بِٱللَّهِ ظَنَّ ٱلسَّوۡءِۚ عَلَيۡهِمۡ دَآئِرَةُ ٱلسَّوۡءِۖ وَغَضِبَ ٱللَّهُ عَلَيۡهِمۡ وَلَعَنَهُمۡ وَأَعَدَّ لَهُمۡ جَهَنَّمَۖ وَسَآءَتۡ مَصِيرٗا", "WayuAAaththiba almunafiqeena waalmunafiqati waalmushrikeena waalmushrikati alththanneena biAllahi thanna alssawi AAalayhim dairatu alssawi waghadiba Allahu AAalayhim walaAAanahum waaAAadda lahum jahannama wasaat maseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (48, 7, "And to Allah belong the soldiers of the heavens and the earth. And ever is Allah Exalted in Might and Wise", "وَلِلَّهِ جُنُودُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَكَانَ ٱللَّهُ عَزِيزًا حَكِيمًا", "Walillahi junoodu alssamawati waalardi wakana Allahu AAazeezan hakeeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (48, 8, "Indeed, We have sent you as a witness and a bringer of good tidings and a warner", "إِنَّآ أَرۡسَلۡنَٰكَ شَٰهِدٗا وَمُبَشِّرٗا وَنَذِيرٗا", "Inna arsalnaka shahidan wamubashshiran wanatheeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (48, 9, "That you [people] may believe in Allah and His Messenger and honor him and respect the Prophet and exalt Allah morning and afternoon", "لِّتُؤۡمِنُواْ بِٱللَّهِ وَرَسُولِهِۦ وَتُعَزِّرُوهُ وَتُوَقِّرُوهُۚ وَتُسَبِّحُوهُ بُكۡرَةٗ وَأَصِيلًا", "Lituminoo biAllahi warasoolihi watuAAazziroohu watuwaqqiroohu watusabbihoohu bukratan waaseelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (48, 10, "Indeed, those who pledge allegiance to you, [O Muhammad] - they are actually pledging allegiance to Allah. The hand of Allah is over their hands. So he who breaks his word only breaks it to the detriment of himself. And he who fulfills that which he has promised Allah - He will give him a great reward", "إِنَّ ٱلَّذِينَ يُبَايِعُونَكَ إِنَّمَا يُبَايِعُونَ ٱللَّهَ يَدُ ٱللَّهِ فَوۡقَ أَيۡدِيهِمۡۚ فَمَن نَّكَثَ فَإِنَّمَا يَنكُثُ عَلَىٰ نَفۡسِهِۦۖ وَمَنۡ أَوۡفَىٰ بِمَا عَٰهَدَ عَلَيۡهُ ٱللَّهَ فَسَيُؤۡتِيهِ أَجۡرًا عَظِيمٗا", "Inna allatheena yubayiAAoonaka innama yubayiAAoona Allaha yadu Allahi fawqa aydeehim faman nakatha fainnama yankuthu AAala nafsihi waman awfa bima AAahada AAalayhu Allaha fasayuteehi ajran AAatheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (48, 11, "Those who remained behind of the bedouins will say to you, ""Our properties and our families occupied us, so ask forgiveness for us."" They say with their tongues what is not within their hearts. Say, ""Then who could prevent Allah at all if He intended for you harm or intended for you benefit? Rather, ever is Allah, with what you do, Acquainted", "سَيَقُولُ لَكَ ٱلۡمُخَلَّفُونَ مِنَ ٱلۡأَعۡرَابِ شَغَلَتۡنَآ أَمۡوَٰلُنَا وَأَهۡلُونَا فَٱسۡتَغۡفِرۡ لَنَاۚ يَقُولُونَ بِأَلۡسِنَتِهِم مَّا لَيۡسَ فِي قُلُوبِهِمۡۚ قُلۡ فَمَن يَمۡلِكُ لَكُم مِّنَ ٱللَّهِ شَيۡـًٔا إِنۡ أَرَادَ بِكُمۡ ضَرًّا أَوۡ أَرَادَ بِكُمۡ نَفۡعَۢاۚ بَلۡ كَانَ ٱللَّهُ بِمَا تَعۡمَلُونَ خَبِيرَۢا", "Sayaqoolu laka almukhallafoona mina alaAArabi shaghalatna amwaluna waahloona faistaghfir lana yaqooloona bialsinatihim ma laysa fee quloobihim qul faman yamliku lakum mina Allahi shayan in arada bikum darran aw arada bikum nafAAan bal kana Allahu bima taAAmaloona khabeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (48, 12, "But you thought that the Messenger and the believers would never return to their families, ever, and that was made pleasing in your hearts. And you assumed an assumption of evil and became a people ruined", "بَلۡ ظَنَنتُمۡ أَن لَّن يَنقَلِبَ ٱلرَّسُولُ وَٱلۡمُؤۡمِنُونَ إِلَىٰٓ أَهۡلِيهِمۡ أَبَدٗا وَزُيِّنَ ذَٰلِكَ فِي قُلُوبِكُمۡ وَظَنَنتُمۡ ظَنَّ ٱلسَّوۡءِ وَكُنتُمۡ قَوۡمَۢا بُورٗا", "Bal thanantum an lan yanqaliba alrrasoolu waalmuminoona ila ahleehim abadan wazuyyina thalika fee quloobikum wathanantum thanna alssawi wakuntum qawman booran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (48, 13, "And whoever has not believed in Allah and His Messenger - then indeed, We have prepared for the disbelievers a Blaze", "وَمَن لَّمۡ يُؤۡمِنۢ بِٱللَّهِ وَرَسُولِهِۦ فَإِنَّآ أَعۡتَدۡنَا لِلۡكَٰفِرِينَ سَعِيرٗا", "Waman lam yumin biAllahi warasoolihi fainna aAAtadna lilkafireena saAAeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (48, 14, "And to Allah belongs the dominion of the heavens and the earth. He forgives whom He wills and punishes whom He wills. And ever is Allah Forgiving and Merciful", "وَلِلَّهِ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ يَغۡفِرُ لِمَن يَشَآءُ وَيُعَذِّبُ مَن يَشَآءُۚ وَكَانَ ٱللَّهُ غَفُورٗا رَّحِيمٗا", "Walillahi mulku alssamawati waalardi yaghfiru liman yashao wayuAAaththibu man yashao wakana Allahu ghafooran raheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (48, 15, "Those who remained behind will say when you set out toward the war booty to take it, ""Let us follow you."" They wish to change the words of Allah. Say, ""Never will you follow us. Thus did Allah say before."" So they will say, ""Rather, you envy us."" But [in fact] they were not understanding except a little", "سَيَقُولُ ٱلۡمُخَلَّفُونَ إِذَا ٱنطَلَقۡتُمۡ إِلَىٰ مَغَانِمَ لِتَأۡخُذُوهَا ذَرُونَا نَتَّبِعۡكُمۡۖ يُرِيدُونَ أَن يُبَدِّلُواْ كَلَٰمَ ٱللَّهِۚ قُل لَّن تَتَّبِعُونَا كَذَٰلِكُمۡ قَالَ ٱللَّهُ مِن قَبۡلُۖ فَسَيَقُولُونَ بَلۡ تَحۡسُدُونَنَاۚ بَلۡ كَانُواْ لَا يَفۡقَهُونَ إِلَّا قَلِيلٗا", "Sayaqoolu almukhallafoona itha intalaqtum ila maghanima litakhuthooha tharoona nattabiAAkum yureedoona an yubaddiloo kalama Allahi qul lan tattabiAAoona kathalikum qala Allahu min qablu fasayaqooloona bal tahsudoonana bal kanoo la yafqahoona illa qaleelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (48, 16, "Say to those who remained behind of the bedouins, ""You will be called to [face] a people of great military might; you may fight them, or they will submit. So if you obey, Allah will give you a good reward; but if you turn away as you turned away before, He will punish you with a painful punishment", "قُل لِّلۡمُخَلَّفِينَ مِنَ ٱلۡأَعۡرَابِ سَتُدۡعَوۡنَ إِلَىٰ قَوۡمٍ أُوْلِي بَأۡسٖ شَدِيدٖ تُقَٰتِلُونَهُمۡ أَوۡ يُسۡلِمُونَۖ فَإِن تُطِيعُواْ يُؤۡتِكُمُ ٱللَّهُ أَجۡرًا حَسَنٗاۖ وَإِن تَتَوَلَّوۡاْ كَمَا تَوَلَّيۡتُم مِّن قَبۡلُ يُعَذِّبۡكُمۡ عَذَابًا أَلِيمٗا", "Qul lilmukhallafeena mina alaAArabi satudAAawna ila qawmin olee basin shadeedin tuqatiloonahum aw yuslimoona fain tuteeAAoo yutikumu Allahu ajran hasanan wain tatawallaw kama tawallaytum min qablu yuAAaththibkum AAathaban aleeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (48, 17, "There is not upon the blind any guilt or upon the lame any guilt or upon the ill any guilt [for remaining behind]. And whoever obeys Allah and His Messenger - He will admit him to gardens beneath which rivers flow; but whoever turns away - He will punish him with a painful punishment", "لَّيۡسَ عَلَى ٱلۡأَعۡمَىٰ حَرَجٞ وَلَا عَلَى ٱلۡأَعۡرَجِ حَرَجٞ وَلَا عَلَى ٱلۡمَرِيضِ حَرَجٞۗ وَمَن يُطِعِ ٱللَّهَ وَرَسُولَهُۥ يُدۡخِلۡهُ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُۖ وَمَن يَتَوَلَّ يُعَذِّبۡهُ عَذَابًا أَلِيمٗا", "Laysa AAala alaAAma harajun wala AAala alaAAraji harajun wala AAala almareedi harajun waman yutiAAi Allaha warasoolahu yudkhilhu jannatin tajree min tahtiha alanharu waman yatawalla yuAAaththibhu AAathaban aleeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (48, 18, "Certainly was Allah pleased with the believers when they pledged allegiance to you, [O Muhammad], under the tree, and He knew what was in their hearts, so He sent down tranquillity upon them and rewarded them with an imminent conquest", "۞لَّقَدۡ رَضِيَ ٱللَّهُ عَنِ ٱلۡمُؤۡمِنِينَ إِذۡ يُبَايِعُونَكَ تَحۡتَ ٱلشَّجَرَةِ فَعَلِمَ مَا فِي قُلُوبِهِمۡ فَأَنزَلَ ٱلسَّكِينَةَ عَلَيۡهِمۡ وَأَثَٰبَهُمۡ فَتۡحٗا قَرِيبٗا", "Laqad radiya Allahu AAani almumineena ith yubayiAAoonaka tahta alshshajarati faAAalima ma fee quloobihim faanzala alsakeenata AAalayhim waathabahum fathan qareeban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (48, 19, "And much war booty which they will take. And ever is Allah Exalted in Might and Wise", "وَمَغَانِمَ كَثِيرَةٗ يَأۡخُذُونَهَاۗ وَكَانَ ٱللَّهُ عَزِيزًا حَكِيمٗا", "Wamaghanima katheeratan yakhuthoonaha wakana Allahu AAazeezan hakeeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (48, 20, "Allah has promised you much booty that you will take [in the future] and has hastened for you this [victory] and withheld the hands of people from you - that it may be a sign for the believers and [that] He may guide you to a straight path", "وَعَدَكُمُ ٱللَّهُ مَغَانِمَ كَثِيرَةٗ تَأۡخُذُونَهَا فَعَجَّلَ لَكُمۡ هَٰذِهِۦ وَكَفَّ أَيۡدِيَ ٱلنَّاسِ عَنكُمۡ وَلِتَكُونَ ءَايَةٗ لِّلۡمُؤۡمِنِينَ وَيَهۡدِيَكُمۡ صِرَٰطٗا مُّسۡتَقِيمٗا", "WaAAadakumu Allahu maghanima katheeratan takhuthoonaha faAAajjala lakum hathihi wakaffa aydiya alnnasi AAankum walitakoona ayatan lilmumineena wayahdiyakum siratan mustaqeeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (48, 21, "And [He promises] other [victories] that you were [so far] unable to [realize] which Allah has already encompassed. And ever is Allah, over all things, competent", "وَأُخۡرَىٰ لَمۡ تَقۡدِرُواْ عَلَيۡهَا قَدۡ أَحَاطَ ٱللَّهُ بِهَاۚ وَكَانَ ٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٗا", "Waokhra lam taqdiroo AAalayha qad ahata Allahu biha wakana Allahu AAala kulli shayin qadeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (48, 22, "And if those [Makkans] who disbelieve had fought you, they would have turned their backs [in flight]. Then they would not find a protector or a helper", "وَلَوۡ قَٰتَلَكُمُ ٱلَّذِينَ كَفَرُواْ لَوَلَّوُاْ ٱلۡأَدۡبَٰرَ ثُمَّ لَا يَجِدُونَ وَلِيّٗا وَلَا نَصِيرٗا", "Walaw qatalakumu allatheena kafaroo lawallawoo aladbara thumma la yajidoona waliyyan wala naseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (48, 23, "[This is] the established way of Allah which has occurred before. And never will you find in the way of Allah any change", "سُنَّةَ ٱللَّهِ ٱلَّتِي قَدۡ خَلَتۡ مِن قَبۡلُۖ وَلَن تَجِدَ لِسُنَّةِ ٱللَّهِ تَبۡدِيلٗا", "Sunnata Allahi allatee qad khalat min qablu walan tajida lisunnati Allahi tabdeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (48, 24, "And it is He who withheld their hands from you and your hands from them within [the area of] Makkah after He caused you to overcome them. And ever is Allah of what you do, Seeing", "وَهُوَ ٱلَّذِي كَفَّ أَيۡدِيَهُمۡ عَنكُمۡ وَأَيۡدِيَكُمۡ عَنۡهُم بِبَطۡنِ مَكَّةَ مِنۢ بَعۡدِ أَنۡ أَظۡفَرَكُمۡ عَلَيۡهِمۡۚ وَكَانَ ٱللَّهُ بِمَا تَعۡمَلُونَ بَصِيرًا", "Wahuwa allathee kaffa aydiyahum AAankum waaydiyakum AAanhum bibatni makkata min baAAdi an athfarakum AAalayhim wakana Allahu bima taAAmaloona baseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (48, 25, "They are the ones who disbelieved and obstructed you from al-Masjid al-Haram while the offering was prevented from reaching its place of sacrifice. And if not for believing men and believing women whom you did not know - that you might trample them and there would befall you because of them dishonor without [your] knowledge - [you would have been permitted to enter Makkah]. [This was so] that Allah might admit to His mercy whom He willed. If they had been apart [from them], We would have punished those who disbelieved among them with painful punishment", "هُمُ ٱلَّذِينَ كَفَرُواْ وَصَدُّوكُمۡ عَنِ ٱلۡمَسۡجِدِ ٱلۡحَرَامِ وَٱلۡهَدۡيَ مَعۡكُوفًا أَن يَبۡلُغَ مَحِلَّهُۥۚ وَلَوۡلَا رِجَالٞ مُّؤۡمِنُونَ وَنِسَآءٞ مُّؤۡمِنَٰتٞ لَّمۡ تَعۡلَمُوهُمۡ أَن تَطَـُٔوهُمۡ فَتُصِيبَكُم مِّنۡهُم مَّعَرَّةُۢ بِغَيۡرِ عِلۡمٖۖ لِّيُدۡخِلَ ٱللَّهُ فِي رَحۡمَتِهِۦ مَن يَشَآءُۚ لَوۡ تَزَيَّلُواْ لَعَذَّبۡنَا ٱلَّذِينَ كَفَرُواْ مِنۡهُمۡ عَذَابًا أَلِيمًا", "Humu allatheena kafaroo wasaddookum AAani almasjidi alharami waalhadya maAAkoofan an yablugha mahillahu walawla rijalun muminoona wanisaon muminatun lam taAAlamoohum an tataoohum fatuseebakum minhum maAAarratun bighayri AAilmin liyudkhila Allahu fee rahmatihi man yashao law tazayyaloo laAAaththabna allatheena kafaroo minhum AAathaban aleeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (48, 26, "When those who disbelieved had put into their hearts chauvinism - the chauvinism of the time of ignorance. But Allah sent down His tranquillity upon His Messenger and upon the believers and imposed upon them the word of righteousness, and they were more deserving of it and worthy of it. And ever is Allah, of all things, Knowing", "إِذۡ جَعَلَ ٱلَّذِينَ كَفَرُواْ فِي قُلُوبِهِمُ ٱلۡحَمِيَّةَ حَمِيَّةَ ٱلۡجَٰهِلِيَّةِ فَأَنزَلَ ٱللَّهُ سَكِينَتَهُۥ عَلَىٰ رَسُولِهِۦ وَعَلَى ٱلۡمُؤۡمِنِينَ وَأَلۡزَمَهُمۡ كَلِمَةَ ٱلتَّقۡوَىٰ وَكَانُوٓاْ أَحَقَّ بِهَا وَأَهۡلَهَاۚ وَكَانَ ٱللَّهُ بِكُلِّ شَيۡءٍ عَلِيمٗا", "Ith jaAAala allatheena kafaroo fee quloobihimu alhamiyyata hamiyyata aljahiliyyati faanzala Allahu sakeenatahu AAala rasoolihi waAAala almumineena waalzamahum kalimata alttaqwa wakanoo ahaqqa biha waahlaha wakana Allahu bikulli shayin AAaleeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (48, 27, "Certainly has Allah showed to His Messenger the vision in truth. You will surely enter al-Masjid al-Haram, if Allah wills, in safety, with your heads shaved and [hair] shortened, not fearing [anyone]. He knew what you did not know and has arranged before that a conquest near [at hand]", "لَّقَدۡ صَدَقَ ٱللَّهُ رَسُولَهُ ٱلرُّءۡيَا بِٱلۡحَقِّۖ لَتَدۡخُلُنَّ ٱلۡمَسۡجِدَ ٱلۡحَرَامَ إِن شَآءَ ٱللَّهُ ءَامِنِينَ مُحَلِّقِينَ رُءُوسَكُمۡ وَمُقَصِّرِينَ لَا تَخَافُونَۖ فَعَلِمَ مَا لَمۡ تَعۡلَمُواْ فَجَعَلَ مِن دُونِ ذَٰلِكَ فَتۡحٗا قَرِيبًا", "Laqad sadaqa Allahu rasoolahu alrruya bialhaqqi latadkhulunna almasjida alharama in shaa Allahu amineena muhalliqeena ruoosakum wamuqassireena la takhafoona faAAalima ma lam taAAlamoo fajaAAala min dooni thalika fathan qareeban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (48, 28, "It is He who sent His Messenger with guidance and the religion of truth to manifest it over all religion. And sufficient is Allah as Witness", "هُوَ ٱلَّذِيٓ أَرۡسَلَ رَسُولَهُۥ بِٱلۡهُدَىٰ وَدِينِ ٱلۡحَقِّ لِيُظۡهِرَهُۥ عَلَى ٱلدِّينِ كُلِّهِۦۚ وَكَفَىٰ بِٱللَّهِ شَهِيدٗا", "Huwa allathee arsala rasoolahu bialhuda wadeeni alhaqqi liyuthhirahu AAala alddeeni kullihi wakafa biAllahi shaheedan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (48, 29, "Muhammad is the Messenger of Allah; and those with him are forceful against the disbelievers, merciful among themselves. You see them bowing and prostrating [in prayer], seeking bounty from Allah and [His] pleasure. Their mark is on their faces from the trace of prostration. That is their description in the Torah. And their description in the Gospel is as a plant which produces its offshoots and strengthens them so they grow firm and stand upon their stalks, delighting the sowers - so that Allah may enrage by them the disbelievers. Allah has promised those who believe and do righteous deeds among them forgiveness and a great reward", "مُّحَمَّدٞ رَّسُولُ ٱللَّهِۚ وَٱلَّذِينَ مَعَهُۥٓ أَشِدَّآءُ عَلَى ٱلۡكُفَّارِ رُحَمَآءُ بَيۡنَهُمۡۖ تَرَىٰهُمۡ رُكَّعٗا سُجَّدٗا يَبۡتَغُونَ فَضۡلٗا مِّنَ ٱللَّهِ وَرِضۡوَٰنٗاۖ سِيمَاهُمۡ فِي وُجُوهِهِم مِّنۡ أَثَرِ ٱلسُّجُودِۚ ذَٰلِكَ مَثَلُهُمۡ فِي ٱلتَّوۡرَىٰةِۚ وَمَثَلُهُمۡ فِي ٱلۡإِنجِيلِ كَزَرۡعٍ أَخۡرَجَ شَطۡـَٔهُۥ فَـَٔازَرَهُۥ فَٱسۡتَغۡلَظَ فَٱسۡتَوَىٰ عَلَىٰ سُوقِهِۦ يُعۡجِبُ ٱلزُّرَّاعَ لِيَغِيظَ بِهِمُ ٱلۡكُفَّارَۗ وَعَدَ ٱللَّهُ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ مِنۡهُم مَّغۡفِرَةٗ وَأَجۡرًا عَظِيمَۢا", "Muhammadun rasoolu Allahi waallatheena maAAahu ashiddao AAala alkuffari ruhamao baynahum tarahum rukkaAAan sujjadan yabtaghoona fadlan mina Allahi waridwanan seemahum fee wujoohihim min athari alssujoodi thalika mathaluhum fee alttawrati wamathaluhum fee alinjeeli kazarAAin akhraja shatahu faazarahu faistaghlatha faistawa AAala sooqihi yuAAjibu alzzurraAAa liyagheetha bihimu alkuffara waAAada Allahu allatheena amanoo waAAamiloo alssalihati minhum maghfiratan waajran AAatheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (49, 1, "O you who have believed, do not put [yourselves] before Allah and His Messenger but fear Allah. Indeed, Allah is Hearing and Knowing", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تُقَدِّمُواْ بَيۡنَ يَدَيِ ٱللَّهِ وَرَسُولِهِۦۖ وَٱتَّقُواْ ٱللَّهَۚ إِنَّ ٱللَّهَ سَمِيعٌ عَلِيمٞ", "Ya ayyuha allatheena amanoo la tuqaddimoo bayna yadayi Allahi warasoolihi waittaqoo Allaha inna Allaha sameeAAun AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (49, 2, "O you who have believed, do not raise your voices above the voice of the Prophet or be loud to him in speech like the loudness of some of you to others, lest your deeds become worthless while you perceive not", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَرۡفَعُوٓاْ أَصۡوَٰتَكُمۡ فَوۡقَ صَوۡتِ ٱلنَّبِيِّ وَلَا تَجۡهَرُواْ لَهُۥ بِٱلۡقَوۡلِ كَجَهۡرِ بَعۡضِكُمۡ لِبَعۡضٍ أَن تَحۡبَطَ أَعۡمَٰلُكُمۡ وَأَنتُمۡ لَا تَشۡعُرُونَ", "Ya ayyuha allatheena amanoo la tarfaAAoo aswatakum fawqa sawti alnnabiyyi wala tajharoo lahu bialqawli kajahri baAAdikum libaAAdin an tahbata aAAmalukum waantum la tashAAuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (49, 3, "Indeed, those who lower their voices before the Messenger of Allah - they are the ones whose hearts Allah has tested for righteousness. For them is forgiveness and great reward", "إِنَّ ٱلَّذِينَ يَغُضُّونَ أَصۡوَٰتَهُمۡ عِندَ رَسُولِ ٱللَّهِ أُوْلَـٰٓئِكَ ٱلَّذِينَ ٱمۡتَحَنَ ٱللَّهُ قُلُوبَهُمۡ لِلتَّقۡوَىٰۚ لَهُم مَّغۡفِرَةٞ وَأَجۡرٌ عَظِيمٌ", "Inna allatheena yaghuddoona aswatahum AAinda rasooli Allahi olaika allatheena imtahana Allahu quloobahum lilttaqwa lahum maghfiratun waajrun AAatheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (49, 4, "Indeed, those who call you, [O Muhammad], from behind the chambers - most of them do not use reason", "إِنَّ ٱلَّذِينَ يُنَادُونَكَ مِن وَرَآءِ ٱلۡحُجُرَٰتِ أَكۡثَرُهُمۡ لَا يَعۡقِلُونَ", "Inna allatheena yunadoonaka min warai alhujurati aktharuhum la yaAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (49, 5, "And if they had been patient until you [could] come out to them, it would have been better for them. But Allah is Forgiving and Merciful", "وَلَوۡ أَنَّهُمۡ صَبَرُواْ حَتَّىٰ تَخۡرُجَ إِلَيۡهِمۡ لَكَانَ خَيۡرٗا لَّهُمۡۚ وَٱللَّهُ غَفُورٞ رَّحِيمٞ", "Walaw annahum sabaroo hatta takhruja ilayhim lakana khayran lahum waAllahu ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (49, 6, "O you who have believed, if there comes to you a disobedient one with information, investigate, lest you harm a people out of ignorance and become, over what you have done, regretful", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِن جَآءَكُمۡ فَاسِقُۢ بِنَبَإٖ فَتَبَيَّنُوٓاْ أَن تُصِيبُواْ قَوۡمَۢا بِجَهَٰلَةٖ فَتُصۡبِحُواْ عَلَىٰ مَا فَعَلۡتُمۡ نَٰدِمِينَ", "Ya ayyuha allatheena amanoo in jaakum fasiqun binabain fatabayyanoo an tuseeboo qawman bijahalatin fatusbihoo AAala ma faAAaltum nadimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (49, 7, "And know that among you is the Messenger of Allah. If he were to obey you in much of the matter, you would be in difficulty, but Allah has endeared to you the faith and has made it pleasing in your hearts and has made hateful to you disbelief, defiance and disobedience. Those are the [rightly] guided", "وَٱعۡلَمُوٓاْ أَنَّ فِيكُمۡ رَسُولَ ٱللَّهِۚ لَوۡ يُطِيعُكُمۡ فِي كَثِيرٖ مِّنَ ٱلۡأَمۡرِ لَعَنِتُّمۡ وَلَٰكِنَّ ٱللَّهَ حَبَّبَ إِلَيۡكُمُ ٱلۡإِيمَٰنَ وَزَيَّنَهُۥ فِي قُلُوبِكُمۡ وَكَرَّهَ إِلَيۡكُمُ ٱلۡكُفۡرَ وَٱلۡفُسُوقَ وَٱلۡعِصۡيَانَۚ أُوْلَـٰٓئِكَ هُمُ ٱلرَّـٰشِدُونَ", "WaiAAlamoo anna feekum rasoola Allahi law yuteeAAukum fee katheerin mina alamri laAAanittum walakinna Allaha habbaba ilaykumu aleemana wazayyanahu fee quloobikum wakarraha ilaykumu alkufra waalfusooqa waalAAisyana olaika humu alrrashidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (49, 8, "[It is] as bounty from Allah and favor. And Allah is Knowing and Wise", "فَضۡلٗا مِّنَ ٱللَّهِ وَنِعۡمَةٗۚ وَٱللَّهُ عَلِيمٌ حَكِيمٞ", "Fadlan mina Allahi waniAAmatan waAllahu AAaleemun hakeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (49, 9, "And if two factions among the believers should fight, then make settlement between the two. But if one of them oppresses the other, then fight against the one that oppresses until it returns to the ordinance of Allah. And if it returns, then make settlement between them in justice and act justly. Indeed, Allah loves those who act justly", "وَإِن طَآئِفَتَانِ مِنَ ٱلۡمُؤۡمِنِينَ ٱقۡتَتَلُواْ فَأَصۡلِحُواْ بَيۡنَهُمَاۖ فَإِنۢ بَغَتۡ إِحۡدَىٰهُمَا عَلَى ٱلۡأُخۡرَىٰ فَقَٰتِلُواْ ٱلَّتِي تَبۡغِي حَتَّىٰ تَفِيٓءَ إِلَىٰٓ أَمۡرِ ٱللَّهِۚ فَإِن فَآءَتۡ فَأَصۡلِحُواْ بَيۡنَهُمَا بِٱلۡعَدۡلِ وَأَقۡسِطُوٓاْۖ إِنَّ ٱللَّهَ يُحِبُّ ٱلۡمُقۡسِطِينَ", "Wain taifatani mina almumineena iqtataloo faaslihoo baynahuma fain baghat ihdahuma AAala alokhra faqatiloo allatee tabghee hatta tafeea ila amri Allahi fain faat faaslihoo baynahuma bialAAadli waaqsitoo inna Allaha yuhibbu almuqsiteena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (49, 10, "The believers are but brothers, so make settlement between your brothers. And fear Allah that you may receive mercy", "إِنَّمَا ٱلۡمُؤۡمِنُونَ إِخۡوَةٞ فَأَصۡلِحُواْ بَيۡنَ أَخَوَيۡكُمۡۚ وَٱتَّقُواْ ٱللَّهَ لَعَلَّكُمۡ تُرۡحَمُونَ", "Innama almuminoona ikhwatun faaslihoo bayna akhawaykum waittaqoo Allaha laAAallakum turhamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (49, 11, "O you who have believed, let not a people ridicule [another] people; perhaps they may be better than them; nor let women ridicule [other] women; perhaps they may be better than them. And do not insult one another and do not call each other by [offensive] nicknames. Wretched is the name of disobedience after [one's] faith. And whoever does not repent - then it is those who are the wrongdoers", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا يَسۡخَرۡ قَوۡمٞ مِّن قَوۡمٍ عَسَىٰٓ أَن يَكُونُواْ خَيۡرٗا مِّنۡهُمۡ وَلَا نِسَآءٞ مِّن نِّسَآءٍ عَسَىٰٓ أَن يَكُنَّ خَيۡرٗا مِّنۡهُنَّۖ وَلَا تَلۡمِزُوٓاْ أَنفُسَكُمۡ وَلَا تَنَابَزُواْ بِٱلۡأَلۡقَٰبِۖ بِئۡسَ ٱلِٱسۡمُ ٱلۡفُسُوقُ بَعۡدَ ٱلۡإِيمَٰنِۚ وَمَن لَّمۡ يَتُبۡ فَأُوْلَـٰٓئِكَ هُمُ ٱلظَّـٰلِمُونَ", "Ya ayyuha allatheena amanoo la yaskhar qawmun min qawmin AAasa an yakoonoo khayran minhum wala nisaon min nisain AAasa an yakunna khayran minhunna wala talmizoo anfusakum wala tanabazoo bialalqabi bisa alismu alfusooqu baAAda aleemani waman lam yatub faolaika humu alththalimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (49, 12, "O you who have believed, avoid much [negative] assumption. Indeed, some assumption is sin. And do not spy or backbite each other. Would one of you like to eat the flesh of his brother when dead? You would detest it. And fear Allah; indeed, Allah is Accepting of repentance and Merciful", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱجۡتَنِبُواْ كَثِيرٗا مِّنَ ٱلظَّنِّ إِنَّ بَعۡضَ ٱلظَّنِّ إِثۡمٞۖ وَلَا تَجَسَّسُواْ وَلَا يَغۡتَب بَّعۡضُكُم بَعۡضًاۚ أَيُحِبُّ أَحَدُكُمۡ أَن يَأۡكُلَ لَحۡمَ أَخِيهِ مَيۡتٗا فَكَرِهۡتُمُوهُۚ وَٱتَّقُواْ ٱللَّهَۚ إِنَّ ٱللَّهَ تَوَّابٞ رَّحِيمٞ", "Ya ayyuha allatheena amanoo ijtaniboo katheeran mina alththanni inna baAAda alththanni ithmun wala tajassasoo wala yaghtab baAAdukum baAAdan ayuhibbu ahadukum an yakula lahma akheehi maytan fakarihtumoohu waittaqoo Allaha inna Allaha tawwabun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (49, 13, "O mankind, indeed We have created you from male and female and made you peoples and tribes that you may know one another. Indeed, the most noble of you in the sight of Allah is the most righteous of you. Indeed, Allah is Knowing and Acquainted", "يَـٰٓأَيُّهَا ٱلنَّاسُ إِنَّا خَلَقۡنَٰكُم مِّن ذَكَرٖ وَأُنثَىٰ وَجَعَلۡنَٰكُمۡ شُعُوبٗا وَقَبَآئِلَ لِتَعَارَفُوٓاْۚ إِنَّ أَكۡرَمَكُمۡ عِندَ ٱللَّهِ أَتۡقَىٰكُمۡۚ إِنَّ ٱللَّهَ عَلِيمٌ خَبِيرٞ", "Ya ayyuha alnnasu inna khalaqnakum min thakarin waontha wajaAAalnakum shuAAooban waqabaila litaAAarafoo inna akramakum AAinda Allahi atqakum inna Allaha AAaleemun khabeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (49, 14, "The bedouins say, ""We have believed."" Say, ""You have not [yet] believed; but say [instead], 'We have submitted,' for faith has not yet entered your hearts. And if you obey Allah and His Messenger, He will not deprive you from your deeds of anything. Indeed, Allah is Forgiving and Merciful", "۞قَالَتِ ٱلۡأَعۡرَابُ ءَامَنَّاۖ قُل لَّمۡ تُؤۡمِنُواْ وَلَٰكِن قُولُوٓاْ أَسۡلَمۡنَا وَلَمَّا يَدۡخُلِ ٱلۡإِيمَٰنُ فِي قُلُوبِكُمۡۖ وَإِن تُطِيعُواْ ٱللَّهَ وَرَسُولَهُۥ لَا يَلِتۡكُم مِّنۡ أَعۡمَٰلِكُمۡ شَيۡـًٔاۚ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٌ", "Qalati alaAArabu amanna qul lam tuminoo walakin qooloo aslamna walamma yadkhuli aleemanu fee quloobikum wain tuteeAAoo Allaha warasoolahu la yalitkum min aAAmalikum shayan inna Allaha ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (49, 15, "The believers are only the ones who have believed in Allah and His Messenger and then doubt not but strive with their properties and their lives in the cause of Allah. It is those who are the truthful", "إِنَّمَا ٱلۡمُؤۡمِنُونَ ٱلَّذِينَ ءَامَنُواْ بِٱللَّهِ وَرَسُولِهِۦ ثُمَّ لَمۡ يَرۡتَابُواْ وَجَٰهَدُواْ بِأَمۡوَٰلِهِمۡ وَأَنفُسِهِمۡ فِي سَبِيلِ ٱللَّهِۚ أُوْلَـٰٓئِكَ هُمُ ٱلصَّـٰدِقُونَ", "Innama almuminoona allatheena amanoo biAllahi warasoolihi thumma lam yartaboo wajahadoo biamwalihim waanfusihim fee sabeeli Allahi olaika humu alssadiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (49, 16, "Say, ""Would you acquaint Allah with your religion while Allah knows whatever is in the heavens and whatever is on the earth, and Allah is Knowing of all things", "قُلۡ أَتُعَلِّمُونَ ٱللَّهَ بِدِينِكُمۡ وَٱللَّهُ يَعۡلَمُ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۚ وَٱللَّهُ بِكُلِّ شَيۡءٍ عَلِيمٞ", "Qul atuAAallimoona Allaha bideenikum waAllahu yaAAlamu ma fee alssamawati wama fee alardi waAllahu bikulli shayin AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (49, 17, "They consider it a favor to you that they have accepted Islam. Say, ""Do not consider your Islam a favor to me. Rather, Allah has conferred favor upon you that He has guided you to the faith, if you should be truthful", "يَمُنُّونَ عَلَيۡكَ أَنۡ أَسۡلَمُواْۖ قُل لَّا تَمُنُّواْ عَلَيَّ إِسۡلَٰمَكُمۖ بَلِ ٱللَّهُ يَمُنُّ عَلَيۡكُمۡ أَنۡ هَدَىٰكُمۡ لِلۡإِيمَٰنِ إِن كُنتُمۡ صَٰدِقِينَ", "Yamunnoona AAalayka an aslamoo qul la tamunnoo AAalayya islamakum bali Allahu yamunnu AAalaykum an hadakum lileemani in kuntum sadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (49, 18, "Indeed, Allah knows the unseen [aspects] of the heavens and the earth. And Allah is Seeing of what you do", "إِنَّ ٱللَّهَ يَعۡلَمُ غَيۡبَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَٱللَّهُ بَصِيرُۢ بِمَا تَعۡمَلُونَ", "Inna Allaha yaAAlamu ghayba alssamawati waalardi waAllahu baseerun bima taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 1, "Qaf. By the honored Qur'an", "قٓۚ وَٱلۡقُرۡءَانِ ٱلۡمَجِيدِ", "Qaf waalqurani almajeedi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 2, "But they wonder that there has come to them a warner from among themselves, and the disbelievers say, ""This is an amazing thing", "بَلۡ عَجِبُوٓاْ أَن جَآءَهُم مُّنذِرٞ مِّنۡهُمۡ فَقَالَ ٱلۡكَٰفِرُونَ هَٰذَا شَيۡءٌ عَجِيبٌ", "Bal AAajiboo an jaahum munthirun minhum faqala alkafiroona hatha shayon AAajeebun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 3, "When we have died and have become dust, [we will return to life]? That is a distant return", "أَءِذَا مِتۡنَا وَكُنَّا تُرَابٗاۖ ذَٰلِكَ رَجۡعُۢ بَعِيدٞ", "Aitha mitna wakunna turaban thalika rajAAun baAAeedun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 4, "We know what the earth diminishes of them, and with Us is a retaining record", "قَدۡ عَلِمۡنَا مَا تَنقُصُ ٱلۡأَرۡضُ مِنۡهُمۡۖ وَعِندَنَا كِتَٰبٌ حَفِيظُۢ", "Qad AAalimna ma tanqusu alardu minhum waAAindana kitabun hafeethun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 5, "But they denied the truth when it came to them, so they are in a confused condition", "بَلۡ كَذَّبُواْ بِٱلۡحَقِّ لَمَّا جَآءَهُمۡ فَهُمۡ فِيٓ أَمۡرٖ مَّرِيجٍ", "Bal kaththaboo bialhaqqi lamma jaahum fahum fee amrin mareejin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 6, "Have they not looked at the heaven above them - how We structured it and adorned it and [how] it has no rifts", "أَفَلَمۡ يَنظُرُوٓاْ إِلَى ٱلسَّمَآءِ فَوۡقَهُمۡ كَيۡفَ بَنَيۡنَٰهَا وَزَيَّنَّـٰهَا وَمَا لَهَا مِن فُرُوجٖ", "Afalam yanthuroo ila alssamai fawqahum kayfa banaynaha wazayyannaha wama laha min furoojin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 7, "And the earth - We spread it out and cast therein firmly set mountains and made grow therein [something] of every beautiful kind", "وَٱلۡأَرۡضَ مَدَدۡنَٰهَا وَأَلۡقَيۡنَا فِيهَا رَوَٰسِيَ وَأَنۢبَتۡنَا فِيهَا مِن كُلِّ زَوۡجِۭ بَهِيجٖ", "Waalarda madadnaha waalqayna feeha rawasiya waanbatna feeha min kulli zawjin baheejin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 8, "Giving insight and a reminder for every servant who turns [to Allah]", "تَبۡصِرَةٗ وَذِكۡرَىٰ لِكُلِّ عَبۡدٖ مُّنِيبٖ", "Tabsiratan wathikra likulli AAabdin muneebin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 9, "And We have sent down blessed rain from the sky and made grow thereby gardens and grain from the harvest", "وَنَزَّلۡنَا مِنَ ٱلسَّمَآءِ مَآءٗ مُّبَٰرَكٗا فَأَنۢبَتۡنَا بِهِۦ جَنَّـٰتٖ وَحَبَّ ٱلۡحَصِيدِ", "Wanazzalna mina alssamai maan mubarakan faanbatna bihi jannatin wahabba alhaseedi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 10, "And lofty palm trees having fruit arranged in layers", "وَٱلنَّخۡلَ بَاسِقَٰتٖ لَّهَا طَلۡعٞ نَّضِيدٞ", "Waalnnakhla basiqatin laha talAAun nadeedun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 11, "As provision for the servants, and We have given life thereby to a dead land. Thus is the resurrection", "رِّزۡقٗا لِّلۡعِبَادِۖ وَأَحۡيَيۡنَا بِهِۦ بَلۡدَةٗ مَّيۡتٗاۚ كَذَٰلِكَ ٱلۡخُرُوجُ", "Rizqan lilAAibadi waahyayna bihi baldatan maytan kathalika alkhurooju");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 12, "The people of Noah denied before them, and the companions of the well and Thamud", "كَذَّبَتۡ قَبۡلَهُمۡ قَوۡمُ نُوحٖ وَأَصۡحَٰبُ ٱلرَّسِّ وَثَمُودُ", "Kaththabat qablahum qawmu noohin waashabu alrrassi wathamoodu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 13, "And 'Aad and Pharaoh and the brothers of Lot", "وَعَادٞ وَفِرۡعَوۡنُ وَإِخۡوَٰنُ لُوطٖ", "WaAAadun wafirAAawnu waikhwanu lootin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 14, "And the companions of the thicket and the people of Tubba'. All denied the messengers, so My threat was justly fulfilled", "وَأَصۡحَٰبُ ٱلۡأَيۡكَةِ وَقَوۡمُ تُبَّعٖۚ كُلّٞ كَذَّبَ ٱلرُّسُلَ فَحَقَّ وَعِيدِ", "Waashabu alaykati waqawmu tubbaAAin kullun kaththaba alrrusula fahaqqa waAAeedi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 15, "Did We fail in the first creation? But they are in confusion over a new creation", "أَفَعَيِينَا بِٱلۡخَلۡقِ ٱلۡأَوَّلِۚ بَلۡ هُمۡ فِي لَبۡسٖ مِّنۡ خَلۡقٖ جَدِيدٖ", "AfaAAayeena bialkhalqi alawwali bal hum fee labsin min khalqin jadeedin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 16, "And We have already created man and know what his soul whispers to him, and We are closer to him than [his] jugular vein", "وَلَقَدۡ خَلَقۡنَا ٱلۡإِنسَٰنَ وَنَعۡلَمُ مَا تُوَسۡوِسُ بِهِۦ نَفۡسُهُۥۖ وَنَحۡنُ أَقۡرَبُ إِلَيۡهِ مِنۡ حَبۡلِ ٱلۡوَرِيدِ", "Walaqad khalaqna alinsana wanaAAlamu ma tuwaswisu bihi nafsuhu wanahnu aqrabu ilayhi min habli alwareedi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 17, "When the two receivers receive, seated on the right and on the left", "إِذۡ يَتَلَقَّى ٱلۡمُتَلَقِّيَانِ عَنِ ٱلۡيَمِينِ وَعَنِ ٱلشِّمَالِ قَعِيدٞ", "Ith yatalaqqa almutalaqqiyani AAani alyameeni waAAani alshshimali qaAAeedun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 18, "Man does not utter any word except that with him is an observer prepared [to record]", "مَّا يَلۡفِظُ مِن قَوۡلٍ إِلَّا لَدَيۡهِ رَقِيبٌ عَتِيدٞ", "Ma yalfithu min qawlin illa ladayhi raqeebun AAateedun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 19, "And the intoxication of death will bring the truth; that is what you were trying to avoid", "وَجَآءَتۡ سَكۡرَةُ ٱلۡمَوۡتِ بِٱلۡحَقِّۖ ذَٰلِكَ مَا كُنتَ مِنۡهُ تَحِيدُ", "Wajaat sakratu almawti bialhaqqi thalika ma kunta minhu taheedu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 20, "And the Horn will be blown. That is the Day of [carrying out] the threat", "وَنُفِخَ فِي ٱلصُّورِۚ ذَٰلِكَ يَوۡمُ ٱلۡوَعِيدِ", "Wanufikha fee alssoori thalika yawmu alwaAAeedi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 21, "And every soul will come, with it a driver and a witness", "وَجَآءَتۡ كُلُّ نَفۡسٖ مَّعَهَا سَآئِقٞ وَشَهِيدٞ", "Wajaat kullu nafsin maAAaha saiqun washaheedun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 22, "[It will be said], ""You were certainly in unmindfulness of this, and We have removed from you your cover, so your sight, this Day, is sharp", "لَّقَدۡ كُنتَ فِي غَفۡلَةٖ مِّنۡ هَٰذَا فَكَشَفۡنَا عَنكَ غِطَآءَكَ فَبَصَرُكَ ٱلۡيَوۡمَ حَدِيدٞ", "Laqad kunta fee ghaflatin min hatha fakashafna AAanka ghitaaka fabasaruka alyawma hadeedun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 23, "And his companion, [the angel], will say, ""This [record] is what is with me, prepared", "وَقَالَ قَرِينُهُۥ هَٰذَا مَا لَدَيَّ عَتِيدٌ", "Waqala qareenuhu hatha ma ladayya AAateedun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 24, "[Allah will say], ""Throw into Hell every obstinate disbeliever", "أَلۡقِيَا فِي جَهَنَّمَ كُلَّ كَفَّارٍ عَنِيدٖ", "Alqiya fee jahannama kulla kaffarin AAaneedin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 25, "Preventer of good, aggressor, and doubter", "مَّنَّاعٖ لِّلۡخَيۡرِ مُعۡتَدٖ مُّرِيبٍ", "MannaAAin lilkhayri muAAtadin mureebin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 26, "Who made [as equal] with Allah another deity; then throw him into the severe punishment", "ٱلَّذِي جَعَلَ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَ فَأَلۡقِيَاهُ فِي ٱلۡعَذَابِ ٱلشَّدِيدِ", "Allathee jaAAala maAAa Allahi ilahan akhara faalqiyahu fee alAAathabi alshshadeedi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 27, "His [devil] companion will say, ""Our Lord, I did not make him transgress, but he [himself] was in extreme error", "۞قَالَ قَرِينُهُۥ رَبَّنَا مَآ أَطۡغَيۡتُهُۥ وَلَٰكِن كَانَ فِي ضَلَٰلِۭ بَعِيدٖ", "Qala qareenuhu rabbana ma atghaytuhu walakin kana fee dalalin baAAeedin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 28, "[Allah] will say, ""Do not dispute before Me, while I had already presented to you the warning", "قَالَ لَا تَخۡتَصِمُواْ لَدَيَّ وَقَدۡ قَدَّمۡتُ إِلَيۡكُم بِٱلۡوَعِيدِ", "Qala la takhtasimoo ladayya waqad qaddamtu ilaykum bialwaAAeedi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 29, "The word will not be changed with Me, and never will I be unjust to the servants", "مَا يُبَدَّلُ ٱلۡقَوۡلُ لَدَيَّ وَمَآ أَنَا۠ بِظَلَّـٰمٖ لِّلۡعَبِيدِ", "Ma yubaddalu alqawlu ladayya wama ana bithallamin lilAAabeedi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 30, "On the Day We will say to Hell, ""Have you been filled?"" and it will say, ""Are there some more", "يَوۡمَ نَقُولُ لِجَهَنَّمَ هَلِ ٱمۡتَلَأۡتِ وَتَقُولُ هَلۡ مِن مَّزِيدٖ", "Yawma naqoolu lijahannama hali imtalati wataqoolu hal min mazeedin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 31, "And Paradise will be brought near to the righteous, not far", "وَأُزۡلِفَتِ ٱلۡجَنَّةُ لِلۡمُتَّقِينَ غَيۡرَ بَعِيدٍ", "Waozlifati aljannatu lilmuttaqeena ghayra baAAeedin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 32, "[It will be said], ""This is what you were promised - for every returner [to Allah] and keeper [of His covenant]", "هَٰذَا مَا تُوعَدُونَ لِكُلِّ أَوَّابٍ حَفِيظٖ", "Hatha ma tooAAadoona likulli awwabin hafeethin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 33, "Who feared the Most Merciful unseen and came with a heart returning [in repentance]", "مَّنۡ خَشِيَ ٱلرَّحۡمَٰنَ بِٱلۡغَيۡبِ وَجَآءَ بِقَلۡبٖ مُّنِيبٍ", "Man khashiya alrrahmana bialghaybi wajaa biqalbin muneebin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 34, "Enter it in peace. This is the Day of Eternity", "ٱدۡخُلُوهَا بِسَلَٰمٖۖ ذَٰلِكَ يَوۡمُ ٱلۡخُلُودِ", "Odkhulooha bisalamin thalika yawmu alkhuloodi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 35, "They will have whatever they wish therein, and with Us is more", "لَهُم مَّا يَشَآءُونَ فِيهَا وَلَدَيۡنَا مَزِيدٞ", "Lahum ma yashaoona feeha waladayna mazeedun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 36, "And how many a generation before them did We destroy who were greater than them in [striking] power and had explored throughout the lands. Is there any place of escape", "وَكَمۡ أَهۡلَكۡنَا قَبۡلَهُم مِّن قَرۡنٍ هُمۡ أَشَدُّ مِنۡهُم بَطۡشٗا فَنَقَّبُواْ فِي ٱلۡبِلَٰدِ هَلۡ مِن مَّحِيصٍ", "Wakam ahlakna qablahum min qarnin hum ashaddu minhum batshan fanaqqaboo fee albiladi hal min maheesin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 37, "Indeed in that is a reminder for whoever has a heart or who listens while he is present [in mind]", "إِنَّ فِي ذَٰلِكَ لَذِكۡرَىٰ لِمَن كَانَ لَهُۥ قَلۡبٌ أَوۡ أَلۡقَى ٱلسَّمۡعَ وَهُوَ شَهِيدٞ", "Inna fee thalika lathikra liman kana lahu qalbun aw alqa alssamAAa wahuwa shaheedun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 38, "And We did certainly create the heavens and earth and what is between them in six days, and there touched Us no weariness", "وَلَقَدۡ خَلَقۡنَا ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَمَا بَيۡنَهُمَا فِي سِتَّةِ أَيَّامٖ وَمَا مَسَّنَا مِن لُّغُوبٖ", "Walaqad khalaqna alssamawati waalarda wama baynahuma fee sittati ayyamin wama massana min lughoobin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 39, "So be patient, [O Muhammad], over what they say and exalt [Allah] with praise of your Lord before the rising of the sun and before its setting", "فَٱصۡبِرۡ عَلَىٰ مَا يَقُولُونَ وَسَبِّحۡ بِحَمۡدِ رَبِّكَ قَبۡلَ طُلُوعِ ٱلشَّمۡسِ وَقَبۡلَ ٱلۡغُرُوبِ", "Faisbir AAala ma yaqooloona wasabbih bihamdi rabbika qabla tulooAAi alshshamsi waqabla alghuroobi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 40, "And [in part] of the night exalt Him and after prostration", "وَمِنَ ٱلَّيۡلِ فَسَبِّحۡهُ وَأَدۡبَٰرَ ٱلسُّجُودِ", "Wamina allayli fasabbihhu waadbara alssujoodi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 41, "And listen on the Day when the Caller will call out from a place that is near", "وَٱسۡتَمِعۡ يَوۡمَ يُنَادِ ٱلۡمُنَادِ مِن مَّكَانٖ قَرِيبٖ", "WaistamiAA yawma yunadi almunadi min makanin qareebin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 42, "The Day they will hear the blast [of the Horn] in truth. That is the Day of Emergence [from the graves]", "يَوۡمَ يَسۡمَعُونَ ٱلصَّيۡحَةَ بِٱلۡحَقِّۚ ذَٰلِكَ يَوۡمُ ٱلۡخُرُوجِ", "Yawma yasmaAAoona alssayhata bialhaqqi thalika yawmu alkhurooji");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 43, "Indeed, it is We who give life and cause death, and to Us is the destination", "إِنَّا نَحۡنُ نُحۡيِۦ وَنُمِيتُ وَإِلَيۡنَا ٱلۡمَصِيرُ", "Inna nahnu nuhyee wanumeetu wailayna almaseeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 44, "On the Day the earth breaks away from them [and they emerge] rapidly; that is a gathering easy for Us", "يَوۡمَ تَشَقَّقُ ٱلۡأَرۡضُ عَنۡهُمۡ سِرَاعٗاۚ ذَٰلِكَ حَشۡرٌ عَلَيۡنَا يَسِيرٞ", "Yawma tashaqqaqu alardu AAanhum siraAAan thalika hashrun AAalayna yaseerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (50, 45, "We are most knowing of what they say, and you are not over them a tyrant. But remind by the Qur'an whoever fears My threat", "نَّحۡنُ أَعۡلَمُ بِمَا يَقُولُونَۖ وَمَآ أَنتَ عَلَيۡهِم بِجَبَّارٖۖ فَذَكِّرۡ بِٱلۡقُرۡءَانِ مَن يَخَافُ وَعِيدِ", "Nahnu aAAlamu bima yaqooloona wama anta AAalayhim bijabbarin fathakkir bialqurani man yakhafu waAAeedi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 1, "By those [winds] scattering [dust] dispersing", "وَٱلذَّـٰرِيَٰتِ ذَرۡوٗا", "Waalththariyati tharwan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 2, "And those [clouds] carrying a load [of water]", "فَٱلۡحَٰمِلَٰتِ وِقۡرٗا", "Faalhamilati wiqran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 3, "And those [ships] sailing with ease", "فَٱلۡجَٰرِيَٰتِ يُسۡرٗا", "Faaljariyati yusran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 4, "And those [angels] apportioning [each] matter", "فَٱلۡمُقَسِّمَٰتِ أَمۡرًا", "Faalmuqassimati amran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 5, "Indeed, what you are promised is true", "إِنَّمَا تُوعَدُونَ لَصَادِقٞ", "Innama tooAAadoona lasadiqun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 6, "And indeed, the recompense is to occur", "وَإِنَّ ٱلدِّينَ لَوَٰقِعٞ", "Wainna alddeena lawaqiAAun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 7, "By the heaven containing pathways", "وَٱلسَّمَآءِ ذَاتِ ٱلۡحُبُكِ", "Waalssamai thati alhubuki");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 8, "Indeed, you are in differing speech", "إِنَّكُمۡ لَفِي قَوۡلٖ مُّخۡتَلِفٖ", "Innakum lafee qawlin mukhtalifin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 9, "Deluded away from the Qur'an is he who is deluded", "يُؤۡفَكُ عَنۡهُ مَنۡ أُفِكَ", "Yufaku AAanhu man ofika");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 10, "Destroyed are the falsifiers", "قُتِلَ ٱلۡخَرَّـٰصُونَ", "Qutila alkharrasoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 11, "Who are within a flood [of confusion] and heedless", "ٱلَّذِينَ هُمۡ فِي غَمۡرَةٖ سَاهُونَ", "Allatheena hum fee ghamratin sahoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 12, "They ask, ""When is the Day of Recompense", "يَسۡـَٔلُونَ أَيَّانَ يَوۡمُ ٱلدِّينِ", "Yasaloona ayyana yawmu alddeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 13, "[It is] the Day they will be tormented over the Fire", "يَوۡمَ هُمۡ عَلَى ٱلنَّارِ يُفۡتَنُونَ", "Yawma hum AAala alnnari yuftanoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 14, "[And will be told], ""Taste your torment. This is that for which you were impatient", "ذُوقُواْ فِتۡنَتَكُمۡ هَٰذَا ٱلَّذِي كُنتُم بِهِۦ تَسۡتَعۡجِلُونَ", "Thooqoo fitnatakum hatha allathee kuntum bihi tastaAAjiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 15, "Indeed, the righteous will be among gardens and springs", "إِنَّ ٱلۡمُتَّقِينَ فِي جَنَّـٰتٖ وَعُيُونٍ", "Inna almuttaqeena fee jannatin waAAuyoonin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 16, "Accepting what their Lord has given them. Indeed, they were before that doers of good", "ءَاخِذِينَ مَآ ءَاتَىٰهُمۡ رَبُّهُمۡۚ إِنَّهُمۡ كَانُواْ قَبۡلَ ذَٰلِكَ مُحۡسِنِينَ", "Akhitheena ma atahum rabbuhum innahum kanoo qabla thalika muhsineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 17, "They used to sleep but little of the night", "كَانُواْ قَلِيلٗا مِّنَ ٱلَّيۡلِ مَا يَهۡجَعُونَ", "Kanoo qaleelan mina allayli ma yahjaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 18, "And in the hours before dawn they would ask forgiveness", "وَبِٱلۡأَسۡحَارِ هُمۡ يَسۡتَغۡفِرُونَ", "Wabialashari hum yastaghfiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 19, "And from their properties was [given] the right of the [needy] petitioner and the deprived", "وَفِيٓ أَمۡوَٰلِهِمۡ حَقّٞ لِّلسَّآئِلِ وَٱلۡمَحۡرُومِ", "Wafee amwalihim haqqun lilssaili waalmahroomi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 20, "And on the earth are signs for the certain [in faith]", "وَفِي ٱلۡأَرۡضِ ءَايَٰتٞ لِّلۡمُوقِنِينَ", "Wafee alardi ayatun lilmooqineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 21, "And in yourselves. Then will you not see", "وَفِيٓ أَنفُسِكُمۡۚ أَفَلَا تُبۡصِرُونَ", "Wafee anfusikum afala tubsiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 22, "And in the heaven is your provision and whatever you are promised", "وَفِي ٱلسَّمَآءِ رِزۡقُكُمۡ وَمَا تُوعَدُونَ", "Wafee alssamai rizqukum wama tooAAadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 23, "Then by the Lord of the heaven and earth, indeed, it is truth - just as [sure as] it is that you are speaking", "فَوَرَبِّ ٱلسَّمَآءِ وَٱلۡأَرۡضِ إِنَّهُۥ لَحَقّٞ مِّثۡلَ مَآ أَنَّكُمۡ تَنطِقُونَ", "Fawarabbi alssamai waalardi innahu lahaqqun mithla ma annakum tantiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 24, "Has there reached you the story of the honored guests of Abraham", "هَلۡ أَتَىٰكَ حَدِيثُ ضَيۡفِ إِبۡرَٰهِيمَ ٱلۡمُكۡرَمِينَ", "Hal ataka hadeethu dayfi ibraheema almukrameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 25, "When they entered upon him and said, ""[We greet you with] peace."" He answered, ""[And upon you] peace, [you are] a people unknown", "إِذۡ دَخَلُواْ عَلَيۡهِ فَقَالُواْ سَلَٰمٗاۖ قَالَ سَلَٰمٞ قَوۡمٞ مُّنكَرُونَ", "Ith dakhaloo AAalayhi faqaloo salaman qala salamun qawmun munkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 26, "Then he went to his family and came with a fat [roasted] calf", "فَرَاغَ إِلَىٰٓ أَهۡلِهِۦ فَجَآءَ بِعِجۡلٖ سَمِينٖ", "Faragha ila ahlihi fajaa biAAijlin sameenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 27, "And placed it near them; he said, ""Will you not eat", "فَقَرَّبَهُۥٓ إِلَيۡهِمۡ قَالَ أَلَا تَأۡكُلُونَ", "Faqarrabahu ilayhim qala ala takuloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 28, "And he felt from them apprehension. They said, ""Fear not,"" and gave him good tidings of a learned boy", "فَأَوۡجَسَ مِنۡهُمۡ خِيفَةٗۖ قَالُواْ لَا تَخَفۡۖ وَبَشَّرُوهُ بِغُلَٰمٍ عَلِيمٖ", "Faawjasa minhum kheefatan qaloo la takhaf wabashsharoohu bighulamin AAaleemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 29, "And his wife approached with a cry [of alarm] and struck her face and said, ""[I am] a barren old woman", "فَأَقۡبَلَتِ ٱمۡرَأَتُهُۥ فِي صَرَّةٖ فَصَكَّتۡ وَجۡهَهَا وَقَالَتۡ عَجُوزٌ عَقِيمٞ", "Faaqbalati imraatuhu fee sarratin fasakkat wajhaha waqalat AAajoozun AAaqeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 30, "They said, ""Thus has said your Lord; indeed, He is the Wise, the Knowing", "قَالُواْ كَذَٰلِكِ قَالَ رَبُّكِۖ إِنَّهُۥ هُوَ ٱلۡحَكِيمُ ٱلۡعَلِيمُ", "Qaloo kathaliki qala rabbuki innahu huwa alhakeemu alAAaleemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 31, "[Abraham] said, ""Then what is your business [here], O messengers", "۞قَالَ فَمَا خَطۡبُكُمۡ أَيُّهَا ٱلۡمُرۡسَلُونَ", "Qala fama khatbukum ayyuha almursaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 32, "They said, ""Indeed, we have been sent to a people of criminals", "قَالُوٓاْ إِنَّآ أُرۡسِلۡنَآ إِلَىٰ قَوۡمٖ مُّجۡرِمِينَ", "Qaloo inna orsilna ila qawmin mujrimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 33, "To send down upon them stones of clay", "لِنُرۡسِلَ عَلَيۡهِمۡ حِجَارَةٗ مِّن طِينٖ", "Linursila AAalayhim hijaratan min teenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 34, "Marked in the presence of your Lord for the transgressors", "مُّسَوَّمَةً عِندَ رَبِّكَ لِلۡمُسۡرِفِينَ", "Musawwamatan AAinda rabbika lilmusrifeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 35, "So We brought out whoever was in the cities of the believers", "فَأَخۡرَجۡنَا مَن كَانَ فِيهَا مِنَ ٱلۡمُؤۡمِنِينَ", "Faakhrajna man kana feeha mina almumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 36, "And We found not within them other than a [single] house of Muslims", "فَمَا وَجَدۡنَا فِيهَا غَيۡرَ بَيۡتٖ مِّنَ ٱلۡمُسۡلِمِينَ", "Fama wajadna feeha ghayra baytin mina almuslimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 37, "And We left therein a sign for those who fear the painful punishment", "وَتَرَكۡنَا فِيهَآ ءَايَةٗ لِّلَّذِينَ يَخَافُونَ ٱلۡعَذَابَ ٱلۡأَلِيمَ", "Watarakna feeha ayatan lillatheena yakhafoona alAAathaba alaleema");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 38, "And in Moses [was a sign], when We sent him to Pharaoh with clear authority", "وَفِي مُوسَىٰٓ إِذۡ أَرۡسَلۡنَٰهُ إِلَىٰ فِرۡعَوۡنَ بِسُلۡطَٰنٖ مُّبِينٖ", "Wafee moosa ith arsalnahu ila firAAawna bisultanin mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 39, "But he turned away with his supporters and said,"" A magician or a madman", "فَتَوَلَّىٰ بِرُكۡنِهِۦ وَقَالَ سَٰحِرٌ أَوۡ مَجۡنُونٞ", "Fatawalla biruknihi waqala sahirun aw majnoonun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 40, "So We took him and his soldiers and cast them into the sea, and he was blameworthy", "فَأَخَذۡنَٰهُ وَجُنُودَهُۥ فَنَبَذۡنَٰهُمۡ فِي ٱلۡيَمِّ وَهُوَ مُلِيمٞ", "Faakhathnahu wajunoodahu fanabathnahum fee alyammi wahuwa muleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 41, "And in 'Aad [was a sign], when We sent against them the barren wind", "وَفِي عَادٍ إِذۡ أَرۡسَلۡنَا عَلَيۡهِمُ ٱلرِّيحَ ٱلۡعَقِيمَ", "Wafee AAadin ith arsalna AAalayhimu alrreeha alAAaqeema");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 42, "It left nothing of what it came upon but that it made it like disintegrated ruins", "مَا تَذَرُ مِن شَيۡءٍ أَتَتۡ عَلَيۡهِ إِلَّا جَعَلَتۡهُ كَٱلرَّمِيمِ", "Ma tatharu min shayin atat AAalayhi illa jaAAalathu kaalrrameemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 43, "And in Thamud, when it was said to them, ""Enjoy yourselves for a time", "وَفِي ثَمُودَ إِذۡ قِيلَ لَهُمۡ تَمَتَّعُواْ حَتَّىٰ حِينٖ", "Wafee thamooda ith qeela lahum tamattaAAoo hatta heenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 44, "But they were insolent toward the command of their Lord, so the thunderbolt seized them while they were looking on", "فَعَتَوۡاْ عَنۡ أَمۡرِ رَبِّهِمۡ فَأَخَذَتۡهُمُ ٱلصَّـٰعِقَةُ وَهُمۡ يَنظُرُونَ", "FaAAataw AAan amri rabbihim faakhathathumu alssaAAiqatu wahum yanthuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 45, "And they were unable to arise, nor could they defend themselves", "فَمَا ٱسۡتَطَٰعُواْ مِن قِيَامٖ وَمَا كَانُواْ مُنتَصِرِينَ", "Fama istataAAoo min qiyamin wama kanoo muntasireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 46, "And [We destroyed] the people of Noah before; indeed, they were a people defiantly disobedient", "وَقَوۡمَ نُوحٖ مِّن قَبۡلُۖ إِنَّهُمۡ كَانُواْ قَوۡمٗا فَٰسِقِينَ", "Waqawma noohin min qablu innahum kanoo qawman fasiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 47, "And the heaven We constructed with strength, and indeed, We are [its] expander", "وَٱلسَّمَآءَ بَنَيۡنَٰهَا بِأَيۡيْدٖ وَإِنَّا لَمُوسِعُونَ", "Waalssamaa banaynaha biaydin wainna lamoosiAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 48, "And the earth We have spread out, and excellent is the preparer", "وَٱلۡأَرۡضَ فَرَشۡنَٰهَا فَنِعۡمَ ٱلۡمَٰهِدُونَ", "Waalarda farashnaha faniAAma almahidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 49, "And of all things We created two mates; perhaps you will remember", "وَمِن كُلِّ شَيۡءٍ خَلَقۡنَا زَوۡجَيۡنِ لَعَلَّكُمۡ تَذَكَّرُونَ", "Wamin kulli shayin khalaqna zawjayni laAAallakum tathakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 50, "So flee to Allah. Indeed, I am to you from Him a clear warner", "فَفِرُّوٓاْ إِلَى ٱللَّهِۖ إِنِّي لَكُم مِّنۡهُ نَذِيرٞ مُّبِينٞ", "Fafirroo ila Allahi innee lakum minhu natheerun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 51, "And do not make [as equal] with Allah another deity. Indeed, I am to you from Him a clear warner", "وَلَا تَجۡعَلُواْ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَۖ إِنِّي لَكُم مِّنۡهُ نَذِيرٞ مُّبِينٞ", "Wala tajAAaloo maAAa Allahi ilahan akhara innee lakum minhu natheerun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 52, "Similarly, there came not to those before them any messenger except that they said, ""A magician or a madman", "كَذَٰلِكَ مَآ أَتَى ٱلَّذِينَ مِن قَبۡلِهِم مِّن رَّسُولٍ إِلَّا قَالُواْ سَاحِرٌ أَوۡ مَجۡنُونٌ", "Kathalika ma ata allatheena min qablihim min rasoolin illa qaloo sahirun aw majnoonun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 53, "Did they suggest it to them? Rather, they [themselves] are a transgressing people", "أَتَوَاصَوۡاْ بِهِۦۚ بَلۡ هُمۡ قَوۡمٞ طَاغُونَ", "Atawasaw bihi bal hum qawmun taghoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 54, "So leave them, [O Muhammad], for you are not to be blamed", "فَتَوَلَّ عَنۡهُمۡ فَمَآ أَنتَ بِمَلُومٖ", "Fatawalla AAanhum fama anta bimaloomin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 55, "And remind, for indeed, the reminder benefits the believers", "وَذَكِّرۡ فَإِنَّ ٱلذِّكۡرَىٰ تَنفَعُ ٱلۡمُؤۡمِنِينَ", "Wathakkir fainna alththikra tanfaAAu almumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 56, "And I did not create the jinn and mankind except to worship Me", "وَمَا خَلَقۡتُ ٱلۡجِنَّ وَٱلۡإِنسَ إِلَّا لِيَعۡبُدُونِ", "Wama khalaqtu aljinna waalinsa illa liyaAAbudooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 57, "I do not want from them any provision, nor do I want them to feed Me", "مَآ أُرِيدُ مِنۡهُم مِّن رِّزۡقٖ وَمَآ أُرِيدُ أَن يُطۡعِمُونِ", "Ma oreedu minhum min rizqin wama oreedu an yutAAimooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 58, "Indeed, it is Allah who is the [continual] Provider, the firm possessor of strength", "إِنَّ ٱللَّهَ هُوَ ٱلرَّزَّاقُ ذُو ٱلۡقُوَّةِ ٱلۡمَتِينُ", "Inna Allaha huwa alrrazzaqu thoo alquwwati almateenu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 59, "And indeed, for those who have wronged is a portion [of punishment] like the portion of their predecessors, so let them not impatiently urge Me", "فَإِنَّ لِلَّذِينَ ظَلَمُواْ ذَنُوبٗا مِّثۡلَ ذَنُوبِ أَصۡحَٰبِهِمۡ فَلَا يَسۡتَعۡجِلُونِ", "Fainna lillatheena thalamoo thanooban mithla thanoobi ashabihim fala yastaAAjiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (51, 60, "And woe to those who have disbelieved from their Day which they are promised", "فَوَيۡلٞ لِّلَّذِينَ كَفَرُواْ مِن يَوۡمِهِمُ ٱلَّذِي يُوعَدُونَ", "Fawaylun lillatheena kafaroo min yawmihimu allathee yooAAadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 1, "By the mount", "وَٱلطُّورِ", "Waalttoori");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 2, "And [by] a Book inscribed", "وَكِتَٰبٖ مَّسۡطُورٖ", "Wakitabin mastoorin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 3, "In parchment spread open", "فِي رَقّٖ مَّنشُورٖ", "Fee raqqin manshoorin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 4, "And [by] the frequented House", "وَٱلۡبَيۡتِ ٱلۡمَعۡمُورِ", "Waalbayti almaAAmoori");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 5, "And [by] the heaven raised high", "وَٱلسَّقۡفِ ٱلۡمَرۡفُوعِ", "Waalssaqfi almarfooAAi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 6, "And [by] the sea filled [with fire]", "وَٱلۡبَحۡرِ ٱلۡمَسۡجُورِ", "Waalbahri almasjoori");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 7, "Indeed, the punishment of your Lord will occur", "إِنَّ عَذَابَ رَبِّكَ لَوَٰقِعٞ", "Inna AAathaba rabbika lawaqiAAun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 8, "Of it there is no preventer", "مَّا لَهُۥ مِن دَافِعٖ", "Ma lahu min dafiAAin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 9, "On the Day the heaven will sway with circular motion", "يَوۡمَ تَمُورُ ٱلسَّمَآءُ مَوۡرٗا", "Yawma tamooru alssamao mawran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 10, "And the mountains will pass on, departing", "وَتَسِيرُ ٱلۡجِبَالُ سَيۡرٗا", "Wataseeru aljibalu sayran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 11, "Then woe, that Day, to the deniers", "فَوَيۡلٞ يَوۡمَئِذٖ لِّلۡمُكَذِّبِينَ", "Fawaylun yawmaithin lilmukaththibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 12, "Who are in [empty] discourse amusing themselves", "ٱلَّذِينَ هُمۡ فِي خَوۡضٖ يَلۡعَبُونَ", "Allatheena hum fee khawdin yalAAaboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 13, "The Day they are thrust toward the fire of Hell with a [violent] thrust, [its angels will say]", "يَوۡمَ يُدَعُّونَ إِلَىٰ نَارِ جَهَنَّمَ دَعًّا", "Yawma yudaAAAAoona ila nari jahannama daAAAAan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 14, "This is the Fire which you used to deny", "هَٰذِهِ ٱلنَّارُ ٱلَّتِي كُنتُم بِهَا تُكَذِّبُونَ", "Hathihi alnnaru allatee kuntum biha tukaththiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 15, "Then is this magic, or do you not see", "أَفَسِحۡرٌ هَٰذَآ أَمۡ أَنتُمۡ لَا تُبۡصِرُونَ", "Afasihrun hatha am antum la tubsiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 16, "[Enter to] burn therein; then be patient or impatient - it is all the same for you. You are only being recompensed [for] what you used to do", "ٱصۡلَوۡهَا فَٱصۡبِرُوٓاْ أَوۡ لَا تَصۡبِرُواْ سَوَآءٌ عَلَيۡكُمۡۖ إِنَّمَا تُجۡزَوۡنَ مَا كُنتُمۡ تَعۡمَلُونَ", "Islawha faisbiroo aw la tasbiroo sawaon AAalaykum innama tujzawna ma kuntum taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 17, "Indeed, the righteous will be in gardens and pleasure", "إِنَّ ٱلۡمُتَّقِينَ فِي جَنَّـٰتٖ وَنَعِيمٖ", "Inna almuttaqeena fee jannatin wanaAAeemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 18, "Enjoying what their Lord has given them, and their Lord protected them from the punishment of Hellfire", "فَٰكِهِينَ بِمَآ ءَاتَىٰهُمۡ رَبُّهُمۡ وَوَقَىٰهُمۡ رَبُّهُمۡ عَذَابَ ٱلۡجَحِيمِ", "Fakiheena bima atahum rabbuhum wawaqahum rabbuhum AAathaba aljaheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 19, "[They will be told], ""Eat and drink in satisfaction for what you used to do", "كُلُواْ وَٱشۡرَبُواْ هَنِيٓـَٔۢا بِمَا كُنتُمۡ تَعۡمَلُونَ", "Kuloo waishraboo haneean bima kuntum taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 20, "They will be reclining on thrones lined up, and We will marry them to fair women with large, [beautiful] eyes", "مُتَّكِـِٔينَ عَلَىٰ سُرُرٖ مَّصۡفُوفَةٖۖ وَزَوَّجۡنَٰهُم بِحُورٍ عِينٖ", "Muttakieena AAala sururin masfoofatin wazawwajnahum bihoorin AAeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 21, "And those who believed and whose descendants followed them in faith - We will join with them their descendants, and We will not deprive them of anything of their deeds. Every person, for what he earned, is retained", "وَٱلَّذِينَ ءَامَنُواْ وَٱتَّبَعَتۡهُمۡ ذُرِّيَّتُهُم بِإِيمَٰنٍ أَلۡحَقۡنَا بِهِمۡ ذُرِّيَّتَهُمۡ وَمَآ أَلَتۡنَٰهُم مِّنۡ عَمَلِهِم مِّن شَيۡءٖۚ كُلُّ ٱمۡرِيِٕۭ بِمَا كَسَبَ رَهِينٞ", "Waallatheena amanoo waittabaAAathum thurriyyatuhum bieemanin alhaqna bihim thurriyyatahum wama alatnahum min AAamalihim min shayin kullu imriin bima kasaba raheenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 22, "And We will provide them with fruit and meat from whatever they desire", "وَأَمۡدَدۡنَٰهُم بِفَٰكِهَةٖ وَلَحۡمٖ مِّمَّا يَشۡتَهُونَ", "Waamdadnahum bifakihatin walahmin mimma yashtahoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 23, "They will exchange with one another a cup [of wine] wherein [results] no ill speech or commission of sin", "يَتَنَٰزَعُونَ فِيهَا كَأۡسٗا لَّا لَغۡوٞ فِيهَا وَلَا تَأۡثِيمٞ", "YatanazaAAoona feeha kasan la laghwun feeha wala tatheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 24, "There will circulate among them [servant] boys [especially] for them, as if they were pearls well-protected", "۞وَيَطُوفُ عَلَيۡهِمۡ غِلۡمَانٞ لَّهُمۡ كَأَنَّهُمۡ لُؤۡلُؤٞ مَّكۡنُونٞ", "Wayatoofu AAalayhim ghilmanun lahum kaannahum luluon maknoonun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 25, "And they will approach one another, inquiring of each other", "وَأَقۡبَلَ بَعۡضُهُمۡ عَلَىٰ بَعۡضٖ يَتَسَآءَلُونَ", "Waaqbala baAAduhum AAala baAAdin yatasaaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 26, "They will say, ""Indeed, we were previously among our people fearful [of displeasing Allah]", "قَالُوٓاْ إِنَّا كُنَّا قَبۡلُ فِيٓ أَهۡلِنَا مُشۡفِقِينَ", "Qaloo inna kunna qablu fee ahlina mushfiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 27, "So Allah conferred favor upon us and protected us from the punishment of the Scorching Fire", "فَمَنَّ ٱللَّهُ عَلَيۡنَا وَوَقَىٰنَا عَذَابَ ٱلسَّمُومِ", "Famanna Allahu AAalayna wawaqana AAathaba alssamoomi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 28, "Indeed, we used to supplicate Him before. Indeed, it is He who is the Beneficent, the Merciful", "إِنَّا كُنَّا مِن قَبۡلُ نَدۡعُوهُۖ إِنَّهُۥ هُوَ ٱلۡبَرُّ ٱلرَّحِيمُ", "Inna kunna min qablu nadAAoohu innahu huwa albarru alrraheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 29, "So remind [O Muhammad], for you are not, by the favor of your Lord, a soothsayer or a madman", "فَذَكِّرۡ فَمَآ أَنتَ بِنِعۡمَتِ رَبِّكَ بِكَاهِنٖ وَلَا مَجۡنُونٍ", "Fathakkir fama anta biniAAmati rabbika bikahin wala majnoonin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 30, "Or do they say [of you], ""A poet for whom we await a misfortune of time", "أَمۡ يَقُولُونَ شَاعِرٞ نَّتَرَبَّصُ بِهِۦ رَيۡبَ ٱلۡمَنُونِ", "Am yaqooloona shaAAirun natarabbasu bihi rayba almanooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 31, "Say, ""Wait, for indeed I am, with you, among the waiters", "قُلۡ تَرَبَّصُواْ فَإِنِّي مَعَكُم مِّنَ ٱلۡمُتَرَبِّصِينَ", "Qul tarabbasoo fainnee maAAakum mina almutarabbiseena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 32, "Or do their minds command them to [say] this, or are they a transgressing people", "أَمۡ تَأۡمُرُهُمۡ أَحۡلَٰمُهُم بِهَٰذَآۚ أَمۡ هُمۡ قَوۡمٞ طَاغُونَ", "Am tamuruhum ahlamuhum bihatha am hum qawmun taghoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 33, "Or do they say, ""He has made it up""? Rather, they do not believe", "أَمۡ يَقُولُونَ تَقَوَّلَهُۥۚ بَل لَّا يُؤۡمِنُونَ", "Am yaqooloona taqawwalahu bal la yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 34, "Then let them produce a statement like it, if they should be truthful", "فَلۡيَأۡتُواْ بِحَدِيثٖ مِّثۡلِهِۦٓ إِن كَانُواْ صَٰدِقِينَ", "Falyatoo bihadeethin mithlihi in kanoo sadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 35, "Or were they created by nothing, or were they the creators [of themselves]", "أَمۡ خُلِقُواْ مِنۡ غَيۡرِ شَيۡءٍ أَمۡ هُمُ ٱلۡخَٰلِقُونَ", "Am khuliqoo min ghayri shayin am humu alkhaliqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 36, "Or did they create the heavens and the earth? Rather, they are not certain", "أَمۡ خَلَقُواْ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَۚ بَل لَّا يُوقِنُونَ", "Am khalaqoo alssamawati waalarda bal la yooqinoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 37, "Or have they the depositories [containing the provision] of your Lord? Or are they the controllers [of them]", "أَمۡ عِندَهُمۡ خَزَآئِنُ رَبِّكَ أَمۡ هُمُ ٱلۡمُصَۜيۡطِرُونَ", "Am AAindahum khazainu rabbika am humu almusaytiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 38, "Or have they a stairway [into the heaven] upon which they listen? Then let their listener produce a clear authority", "أَمۡ لَهُمۡ سُلَّمٞ يَسۡتَمِعُونَ فِيهِۖ فَلۡيَأۡتِ مُسۡتَمِعُهُم بِسُلۡطَٰنٖ مُّبِينٍ", "Am lahum sullamun yastamiAAoona feehi falyati mustamiAAuhum bisultanin mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 39, "Or has He daughters while you have sons", "أَمۡ لَهُ ٱلۡبَنَٰتُ وَلَكُمُ ٱلۡبَنُونَ", "Am lahu albanatu walakumu albanoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 40, "Or do you, [O Muhammad], ask of them a payment, so they are by debt burdened down", "أَمۡ تَسۡـَٔلُهُمۡ أَجۡرٗا فَهُم مِّن مَّغۡرَمٖ مُّثۡقَلُونَ", "Am tasaluhum ajran fahum min maghramin muthqaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 41, "Or have they [knowledge of] the unseen, so they write [it] down", "أَمۡ عِندَهُمُ ٱلۡغَيۡبُ فَهُمۡ يَكۡتُبُونَ", "Am AAindahumu alghaybu fahum yaktuboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 42, "Or do they intend a plan? But those who disbelieve - they are the object of a plan", "أَمۡ يُرِيدُونَ كَيۡدٗاۖ فَٱلَّذِينَ كَفَرُواْ هُمُ ٱلۡمَكِيدُونَ", "Am yureedoona kaydan faallatheena kafaroo humu almakeedoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 43, "Or have they a deity other than Allah? Exalted is Allah above whatever they associate with Him", "أَمۡ لَهُمۡ إِلَٰهٌ غَيۡرُ ٱللَّهِۚ سُبۡحَٰنَ ٱللَّهِ عَمَّا يُشۡرِكُونَ", "Am lahum ilahun ghayru Allahi subhana Allahi AAamma yushrikoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 44, "And if they were to see a fragment from the sky falling, they would say, ""[It is merely] clouds heaped up", "وَإِن يَرَوۡاْ كِسۡفٗا مِّنَ ٱلسَّمَآءِ سَاقِطٗا يَقُولُواْ سَحَابٞ مَّرۡكُومٞ", "Wain yaraw kisfan mina alssamai saqitan yaqooloo sahabun markoomun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 45, "So leave them until they meet their Day in which they will be struck insensible", "فَذَرۡهُمۡ حَتَّىٰ يُلَٰقُواْ يَوۡمَهُمُ ٱلَّذِي فِيهِ يُصۡعَقُونَ", "Fatharhum hatta yulaqoo yawmahumu allathee feehi yusAAaqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 46, "The Day their plan will not avail them at all, nor will they be helped", "يَوۡمَ لَا يُغۡنِي عَنۡهُمۡ كَيۡدُهُمۡ شَيۡـٔٗا وَلَا هُمۡ يُنصَرُونَ", "Yawma la yughnee AAanhum kayduhum shayan wala hum yunsaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 47, "And indeed, for those who have wronged is a punishment before that, but most of them do not know", "وَإِنَّ لِلَّذِينَ ظَلَمُواْ عَذَابٗا دُونَ ذَٰلِكَ وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَعۡلَمُونَ", "Wainna lillatheena thalamoo AAathaban doona thalika walakinna aktharahum la yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 48, "And be patient, [O Muhammad], for the decision of your Lord, for indeed, you are in Our eyes. And exalt [Allah] with praise of your Lord when you arise", "وَٱصۡبِرۡ لِحُكۡمِ رَبِّكَ فَإِنَّكَ بِأَعۡيُنِنَاۖ وَسَبِّحۡ بِحَمۡدِ رَبِّكَ حِينَ تَقُومُ", "Waisbir lihukmi rabbika fainnaka biaAAyunina wasabbih bihamdi rabbika heena taqoomu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (52, 49, "And in a part of the night exalt Him and after [the setting of] the stars", "وَمِنَ ٱلَّيۡلِ فَسَبِّحۡهُ وَإِدۡبَٰرَ ٱلنُّجُومِ", "Wamina allayli fasabbihhu waidbara alnnujoomi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 1, "By the star when it descends", "وَٱلنَّجۡمِ إِذَا هَوَىٰ", "Waalnnajmi itha hawa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 2, "Your companion [Muhammad] has not strayed, nor has he erred", "مَا ضَلَّ صَاحِبُكُمۡ وَمَا غَوَىٰ", "Ma dalla sahibukum wama ghawa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 3, "Nor does he speak from [his own] inclination", "وَمَا يَنطِقُ عَنِ ٱلۡهَوَىٰٓ", "Wama yantiqu AAani alhawa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 4, "It is not but a revelation revealed", "إِنۡ هُوَ إِلَّا وَحۡيٞ يُوحَىٰ", "In huwa illa wahyun yooha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 5, "Taught to him by one intense in strength", "عَلَّمَهُۥ شَدِيدُ ٱلۡقُوَىٰ", "AAallamahu shadeedu alquwa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 6, "One of soundness. And he rose to [his] true form", "ذُو مِرَّةٖ فَٱسۡتَوَىٰ", "Thoo mirratin faistawa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 7, "While he was in the higher [part of the] horizon", "وَهُوَ بِٱلۡأُفُقِ ٱلۡأَعۡلَىٰ", "Wahuwa bialofuqi alaAAla");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 8, "Then he approached and descended", "ثُمَّ دَنَا فَتَدَلَّىٰ", "Thumma dana fatadalla");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 9, "And was at a distance of two bow lengths or nearer", "فَكَانَ قَابَ قَوۡسَيۡنِ أَوۡ أَدۡنَىٰ", "Fakana qaba qawsayni aw adna");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 10, "And he revealed to His Servant what he revealed", "فَأَوۡحَىٰٓ إِلَىٰ عَبۡدِهِۦ مَآ أَوۡحَىٰ", "Faawha ila AAabdihi ma awha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 11, "The heart did not lie [about] what it saw", "مَا كَذَبَ ٱلۡفُؤَادُ مَا رَأَىٰٓ", "Ma kathaba alfuadu ma raa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 12, "So will you dispute with him over what he saw", "أَفَتُمَٰرُونَهُۥ عَلَىٰ مَا يَرَىٰ", "Afatumaroonahu AAala ma yara");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 13, "And he certainly saw him in another descent", "وَلَقَدۡ رَءَاهُ نَزۡلَةً أُخۡرَىٰ", "Walaqad raahu nazlatan okhra");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 14, "At the Lote Tree of the Utmost Boundary", "عِندَ سِدۡرَةِ ٱلۡمُنتَهَىٰ", "AAinda sidrati almuntaha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 15, "Near it is the Garden of Refuge", "عِندَهَا جَنَّةُ ٱلۡمَأۡوَىٰٓ", "AAindaha jannatu almawa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 16, "When there covered the Lote Tree that which covered [it]", "إِذۡ يَغۡشَى ٱلسِّدۡرَةَ مَا يَغۡشَىٰ", "Ith yaghsha alssidrata ma yaghsha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 17, "The sight [of the Prophet] did not swerve, nor did it transgress [its limit]", "مَا زَاغَ ٱلۡبَصَرُ وَمَا طَغَىٰ", "Ma zagha albasaru wama tagha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 18, "He certainly saw of the greatest signs of his Lord", "لَقَدۡ رَأَىٰ مِنۡ ءَايَٰتِ رَبِّهِ ٱلۡكُبۡرَىٰٓ", "Laqad raa min ayati rabbihi alkubra");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 19, "So have you considered al-Lat and al-'Uzza", "أَفَرَءَيۡتُمُ ٱللَّـٰتَ وَٱلۡعُزَّىٰ", "Afaraaytumu allata waalAAuzza");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 20, "And Manat, the third - the other one", "وَمَنَوٰةَ ٱلثَّالِثَةَ ٱلۡأُخۡرَىٰٓ", "Wamanata alththalithata alokhra");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 21, "Is the male for you and for Him the female", "أَلَكُمُ ٱلذَّكَرُ وَلَهُ ٱلۡأُنثَىٰ", "Alakumu alththakaru walahu alontha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 22, "That, then, is an unjust division", "تِلۡكَ إِذٗا قِسۡمَةٞ ضِيزَىٰٓ", "Tilka ithan qismatun deeza");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 23, "They are not but [mere] names you have named them - you and your forefathers - for which Allah has sent down no authority. They follow not except assumption and what [their] souls desire, and there has already come to them from their Lord guidance", "إِنۡ هِيَ إِلَّآ أَسۡمَآءٞ سَمَّيۡتُمُوهَآ أَنتُمۡ وَءَابَآؤُكُم مَّآ أَنزَلَ ٱللَّهُ بِهَا مِن سُلۡطَٰنٍۚ إِن يَتَّبِعُونَ إِلَّا ٱلظَّنَّ وَمَا تَهۡوَى ٱلۡأَنفُسُۖ وَلَقَدۡ جَآءَهُم مِّن رَّبِّهِمُ ٱلۡهُدَىٰٓ", "In hiya illa asmaon sammaytumooha antum waabaokum ma anzala Allahu biha min sultanin in yattabiAAoona illa alththanna wama tahwa alanfusu walaqad jaahum min rabbihimu alhuda");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 24, "Or is there for man whatever he wishes", "أَمۡ لِلۡإِنسَٰنِ مَا تَمَنَّىٰ", "Am lilinsani ma tamanna");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 25, "Rather, to Allah belongs the Hereafter and the first [life]", "فَلِلَّهِ ٱلۡأٓخِرَةُ وَٱلۡأُولَىٰ", "Falillahi alakhiratu waaloola");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 26, "And how many angels there are in the heavens whose intercession will not avail at all except [only] after Allah has permitted [it] to whom He wills and approves", "۞وَكَم مِّن مَّلَكٖ فِي ٱلسَّمَٰوَٰتِ لَا تُغۡنِي شَفَٰعَتُهُمۡ شَيۡـًٔا إِلَّا مِنۢ بَعۡدِ أَن يَأۡذَنَ ٱللَّهُ لِمَن يَشَآءُ وَيَرۡضَىٰٓ", "Wakam min malakin fee alssamawati la tughnee shafaAAatuhum shayan illa min baAAdi an yathana Allahu liman yashao wayarda");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 27, "Indeed, those who do not believe in the Hereafter name the angels female names", "إِنَّ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱلۡأٓخِرَةِ لَيُسَمُّونَ ٱلۡمَلَـٰٓئِكَةَ تَسۡمِيَةَ ٱلۡأُنثَىٰ", "Inna allatheena la yuminoona bialakhirati layusammoona almalaikata tasmiyata alontha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 28, "And they have thereof no knowledge. They follow not except assumption, and indeed, assumption avails not against the truth at all", "وَمَا لَهُم بِهِۦ مِنۡ عِلۡمٍۖ إِن يَتَّبِعُونَ إِلَّا ٱلظَّنَّۖ وَإِنَّ ٱلظَّنَّ لَا يُغۡنِي مِنَ ٱلۡحَقِّ شَيۡـٔٗا", "Wama lahum bihi min AAilmin in yattabiAAoona illa alththanna wainna alththanna la yughnee mina alhaqqi shayan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 29, "So turn away from whoever turns his back on Our message and desires not except the worldly life", "فَأَعۡرِضۡ عَن مَّن تَوَلَّىٰ عَن ذِكۡرِنَا وَلَمۡ يُرِدۡ إِلَّا ٱلۡحَيَوٰةَ ٱلدُّنۡيَا", "FaaAArid AAan man tawalla AAan thikrina walam yurid illa alhayata alddunya");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 30, "That is their sum of knowledge. Indeed, your Lord is most knowing of who strays from His way, and He is most knowing of who is guided", "ذَٰلِكَ مَبۡلَغُهُم مِّنَ ٱلۡعِلۡمِۚ إِنَّ رَبَّكَ هُوَ أَعۡلَمُ بِمَن ضَلَّ عَن سَبِيلِهِۦ وَهُوَ أَعۡلَمُ بِمَنِ ٱهۡتَدَىٰ", "Thalika mablaghuhum mina alAAilmi inna rabbaka huwa aAAlamu biman dalla AAan sabeelihi wahuwa aAAlamu bimani ihtada");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 31, "And to Allah belongs whatever is in the heavens and whatever is in the earth - that He may recompense those who do evil with [the penalty of] what they have done and recompense those who do good with the best [reward]", "وَلِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِ لِيَجۡزِيَ ٱلَّذِينَ أَسَـٰٓـُٔواْ بِمَا عَمِلُواْ وَيَجۡزِيَ ٱلَّذِينَ أَحۡسَنُواْ بِٱلۡحُسۡنَى", "Walillahi ma fee alssamawati wama fee alardi liyajziya allatheena asaoo bima AAamiloo wayajziya allatheena ahsanoo bialhusna");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 32, "Those who avoid the major sins and immoralities, only [committing] slight ones. Indeed, your Lord is vast in forgiveness. He was most knowing of you when He produced you from the earth and when you were fetuses in the wombs of your mothers. So do not claim yourselves to be pure; He is most knowing of who fears Him", "ٱلَّذِينَ يَجۡتَنِبُونَ كَبَـٰٓئِرَ ٱلۡإِثۡمِ وَٱلۡفَوَٰحِشَ إِلَّا ٱللَّمَمَۚ إِنَّ رَبَّكَ وَٰسِعُ ٱلۡمَغۡفِرَةِۚ هُوَ أَعۡلَمُ بِكُمۡ إِذۡ أَنشَأَكُم مِّنَ ٱلۡأَرۡضِ وَإِذۡ أَنتُمۡ أَجِنَّةٞ فِي بُطُونِ أُمَّهَٰتِكُمۡۖ فَلَا تُزَكُّوٓاْ أَنفُسَكُمۡۖ هُوَ أَعۡلَمُ بِمَنِ ٱتَّقَىٰٓ", "Allatheena yajtaniboona kabaira alithmi waalfawahisha illa allamama inna rabbaka wasiAAu almaghfirati huwa aAAlamu bikum ith anshaakum mina alardi waith antum ajinnatun fee butooni ommahatikum fala tuzakkoo anfusakum huwa aAAlamu bimani ittaqa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 33, "Have you seen the one who turned away", "أَفَرَءَيۡتَ ٱلَّذِي تَوَلَّىٰ", "Afaraayta allathee tawalla");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 34, "And gave a little and [then] refrained", "وَأَعۡطَىٰ قَلِيلٗا وَأَكۡدَىٰٓ", "WaaAAta qaleelan waakda");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 35, "Does he have knowledge of the unseen, so he sees", "أَعِندَهُۥ عِلۡمُ ٱلۡغَيۡبِ فَهُوَ يَرَىٰٓ", "aAAindahu AAilmu alghaybi fahuwa yara");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 36, "Or has he not been informed of what was in the scriptures of Moses", "أَمۡ لَمۡ يُنَبَّأۡ بِمَا فِي صُحُفِ مُوسَىٰ", "Am lam yunabba bima fee suhufi moosa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 37, "And [of] Abraham, who fulfilled [his obligations]", "وَإِبۡرَٰهِيمَ ٱلَّذِي وَفَّىٰٓ", "Waibraheema allathee waffa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 38, "That no bearer of burdens will bear the burden of another", "أَلَّا تَزِرُ وَازِرَةٞ وِزۡرَ أُخۡرَىٰ", "Alla taziru waziratun wizra okhra");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 39, "And that there is not for man except that [good] for which he strives", "وَأَن لَّيۡسَ لِلۡإِنسَٰنِ إِلَّا مَا سَعَىٰ", "Waan laysa lilinsani illa ma saAAa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 40, "And that his effort is going to be seen", "وَأَنَّ سَعۡيَهُۥ سَوۡفَ يُرَىٰ", "Waanna saAAyahu sawfa yura");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 41, "Then he will be recompensed for it with the fullest recompense", "ثُمَّ يُجۡزَىٰهُ ٱلۡجَزَآءَ ٱلۡأَوۡفَىٰ", "Thumma yujzahu aljazaa alawfa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 42, "And that to your Lord is the finality", "وَأَنَّ إِلَىٰ رَبِّكَ ٱلۡمُنتَهَىٰ", "Waanna ila rabbika almuntaha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 43, "And that it is He who makes [one] laugh and weep", "وَأَنَّهُۥ هُوَ أَضۡحَكَ وَأَبۡكَىٰ", "Waannahu huwa adhaka waabka");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 44, "And that it is He who causes death and gives life", "وَأَنَّهُۥ هُوَ أَمَاتَ وَأَحۡيَا", "Waannahu huwa amata waahya");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 45, "And that He creates the two mates - the male and female", "وَأَنَّهُۥ خَلَقَ ٱلزَّوۡجَيۡنِ ٱلذَّكَرَ وَٱلۡأُنثَىٰ", "Waannahu khalaqa alzzawjayni alththakara waalontha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 46, "From a sperm-drop when it is emitted", "مِن نُّطۡفَةٍ إِذَا تُمۡنَىٰ", "Min nutfatin itha tumna");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 47, "And that [incumbent] upon Him is the next creation", "وَأَنَّ عَلَيۡهِ ٱلنَّشۡأَةَ ٱلۡأُخۡرَىٰ", "Waanna AAalayhi alnnashata alokhra");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 48, "And that it is He who enriches and suffices", "وَأَنَّهُۥ هُوَ أَغۡنَىٰ وَأَقۡنَىٰ", "Waannahu huwa aghna waaqna");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 49, "And that it is He who is the Lord of Sirius", "وَأَنَّهُۥ هُوَ رَبُّ ٱلشِّعۡرَىٰ", "Waannahu huwa rabbu alshshiAAra");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 50, "And that He destroyed the first [people of] 'Aad", "وَأَنَّهُۥٓ أَهۡلَكَ عَادًا ٱلۡأُولَىٰ", "Waannahu ahlaka AAadan aloola");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 51, "And Thamud - and He did not spare [them]", "وَثَمُودَاْ فَمَآ أَبۡقَىٰ", "Wathamooda fama abqa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 52, "And the people of Noah before. Indeed, it was they who were [even] more unjust and oppressing", "وَقَوۡمَ نُوحٖ مِّن قَبۡلُۖ إِنَّهُمۡ كَانُواْ هُمۡ أَظۡلَمَ وَأَطۡغَىٰ", "Waqawma noohin min qablu innahum kanoo hum athlama waatgha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 53, "And the overturned towns He hurled down", "وَٱلۡمُؤۡتَفِكَةَ أَهۡوَىٰ", "Waalmutafikata ahwa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 54, "And covered them by that which He covered", "فَغَشَّىٰهَا مَا غَشَّىٰ", "Faghashshaha ma ghashsha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 55, "Then which of the favors of your Lord do you doubt", "فَبِأَيِّ ءَالَآءِ رَبِّكَ تَتَمَارَىٰ", "Fabiayyi alai rabbika tatamara");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 56, "This [Prophet] is a warner like the former warners", "هَٰذَا نَذِيرٞ مِّنَ ٱلنُّذُرِ ٱلۡأُولَىٰٓ", "Hatha natheerun mina alnnuthuri aloola");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 57, "The Approaching Day has approached", "أَزِفَتِ ٱلۡأٓزِفَةُ", "Azifati alazifatu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 58, "Of it, [from those] besides Allah, there is no remover", "لَيۡسَ لَهَا مِن دُونِ ٱللَّهِ كَاشِفَةٌ", "Laysa laha min dooni Allahi kashifatun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 59, "Then at this statement do you wonder", "أَفَمِنۡ هَٰذَا ٱلۡحَدِيثِ تَعۡجَبُونَ", "Afamin hatha alhadeethi taAAjaboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 60, "And you laugh and do not weep", "وَتَضۡحَكُونَ وَلَا تَبۡكُونَ", "Watadhakoona wala tabkoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 61, "While you are proudly sporting", "وَأَنتُمۡ سَٰمِدُونَ", "Waantum samidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (53, 62, "So prostrate to Allah and worship [Him]", "فَٱسۡجُدُواْۤ لِلَّهِۤ وَٱعۡبُدُواْ۩", "Faosjudoo lillahi waoAAbudoo");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 1, "The Hour has come near, and the moon has split [in two]", "ٱقۡتَرَبَتِ ٱلسَّاعَةُ وَٱنشَقَّ ٱلۡقَمَرُ", "Iqtarabati alssaAAatu wainshaqqa alqamaru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 2, "And if they see a miracle, they turn away and say, ""Passing magic", "وَإِن يَرَوۡاْ ءَايَةٗ يُعۡرِضُواْ وَيَقُولُواْ سِحۡرٞ مُّسۡتَمِرّٞ", "Wain yaraw ayatan yuAAridoo wayaqooloo sihrun mustamirrun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 3, "And they denied and followed their inclinations. But for every matter is a [time of] settlement", "وَكَذَّبُواْ وَٱتَّبَعُوٓاْ أَهۡوَآءَهُمۡۚ وَكُلُّ أَمۡرٖ مُّسۡتَقِرّٞ", "Wakaththaboo waittabaAAoo ahwaahum wakullu amrin mustaqirrun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 4, "And there has already come to them of information that in which there is deterrence", "وَلَقَدۡ جَآءَهُم مِّنَ ٱلۡأَنۢبَآءِ مَا فِيهِ مُزۡدَجَرٌ", "Walaqad jaahum mina alanbai ma feehi muzdajarun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 5, "Extensive wisdom - but warning does not avail [them]", "حِكۡمَةُۢ بَٰلِغَةٞۖ فَمَا تُغۡنِ ٱلنُّذُرُ", "Hikmatun balighatun fama tughnee alnnuthuru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 6, "So leave them, [O Muhammad]. The Day the Caller calls to something forbidding", "فَتَوَلَّ عَنۡهُمۡۘ يَوۡمَ يَدۡعُ ٱلدَّاعِ إِلَىٰ شَيۡءٖ نُّكُرٍ", "Fatawalla AAanhum yawma yadAAu alddaAAi ila shayin nukurin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 7, "Their eyes humbled, they will emerge from the graves as if they were locusts spreading", "خُشَّعًا أَبۡصَٰرُهُمۡ يَخۡرُجُونَ مِنَ ٱلۡأَجۡدَاثِ كَأَنَّهُمۡ جَرَادٞ مُّنتَشِرٞ", "KhushshaAAan absaruhum yakhrujoona mina alajdathi kaannahum jaradun muntashirun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 8, "Racing ahead toward the Caller. The disbelievers will say, ""This is a difficult Day", "مُّهۡطِعِينَ إِلَى ٱلدَّاعِۖ يَقُولُ ٱلۡكَٰفِرُونَ هَٰذَا يَوۡمٌ عَسِرٞ", "MuhtiAAeena ila alddaAAi yaqoolu alkafiroona hatha yawmun AAasirun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 9, "The people of Noah denied before them, and they denied Our servant and said, ""A madman,"" and he was repelled", "۞كَذَّبَتۡ قَبۡلَهُمۡ قَوۡمُ نُوحٖ فَكَذَّبُواْ عَبۡدَنَا وَقَالُواْ مَجۡنُونٞ وَٱزۡدُجِرَ", "Kaththabat qablahum qawmu noohin fakaththaboo AAabdana waqaloo majnoonun waizdujira");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 10, "So he invoked his Lord, ""Indeed, I am overpowered, so help", "فَدَعَا رَبَّهُۥٓ أَنِّي مَغۡلُوبٞ فَٱنتَصِرۡ", "FadaAAa rabbahu annee maghloobun faintasir");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 11, "Then We opened the gates of the heaven with rain pouring down", "فَفَتَحۡنَآ أَبۡوَٰبَ ٱلسَّمَآءِ بِمَآءٖ مُّنۡهَمِرٖ", "Fafatahna abwaba alssamai bimain munhamirin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 12, "And caused the earth to burst with springs, and the waters met for a matter already predestined", "وَفَجَّرۡنَا ٱلۡأَرۡضَ عُيُونٗا فَٱلۡتَقَى ٱلۡمَآءُ عَلَىٰٓ أَمۡرٖ قَدۡ قُدِرَ", "Wafajjarna alarda AAuyoonan failtaqa almao AAala amrin qad qudira");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 13, "And We carried him on a [construction of] planks and nails", "وَحَمَلۡنَٰهُ عَلَىٰ ذَاتِ أَلۡوَٰحٖ وَدُسُرٖ", "Wahamalnahu AAala thati alwahin wadusurin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 14, "Sailing under Our observation as reward for he who had been denied", "تَجۡرِي بِأَعۡيُنِنَا جَزَآءٗ لِّمَن كَانَ كُفِرَ", "Tajree biaAAyunina jazaan liman kana kufira");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 15, "And We left it as a sign, so is there any who will remember", "وَلَقَد تَّرَكۡنَٰهَآ ءَايَةٗ فَهَلۡ مِن مُّدَّكِرٖ", "Walaqad taraknaha ayatan fahal min muddakirin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 16, "And how [severe] were My punishment and warning", "فَكَيۡفَ كَانَ عَذَابِي وَنُذُرِ", "Fakayfa kana AAathabee wanuthuri");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 17, "And We have certainly made the Qur'an easy for remembrance, so is there any who will remember", "وَلَقَدۡ يَسَّرۡنَا ٱلۡقُرۡءَانَ لِلذِّكۡرِ فَهَلۡ مِن مُّدَّكِرٖ", "Walaqad yassarna alqurana lilththikri fahal min muddakirin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 18, "Aad denied; and how [severe] were My punishment and warning", "كَذَّبَتۡ عَادٞ فَكَيۡفَ كَانَ عَذَابِي وَنُذُرِ", "Kaththabat AAadun fakayfa kana AAathabee wanuthuri");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 19, "Indeed, We sent upon them a screaming wind on a day of continuous misfortune", "إِنَّآ أَرۡسَلۡنَا عَلَيۡهِمۡ رِيحٗا صَرۡصَرٗا فِي يَوۡمِ نَحۡسٖ مُّسۡتَمِرّٖ", "Inna arsalna AAalayhim reehan sarsaran fee yawmi nahsin mustamirrin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 20, "Extracting the people as if they were trunks of palm trees uprooted", "تَنزِعُ ٱلنَّاسَ كَأَنَّهُمۡ أَعۡجَازُ نَخۡلٖ مُّنقَعِرٖ", "TanziAAu alnnasa kaannahum aAAjazu nakhlin munqaAAirin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 21, "And how [severe] were My punishment and warning", "فَكَيۡفَ كَانَ عَذَابِي وَنُذُرِ", "Fakayfa kana AAathabee wanuthuri");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 22, "And We have certainly made the Qur'an easy for remembrance, so is there any who will remember", "وَلَقَدۡ يَسَّرۡنَا ٱلۡقُرۡءَانَ لِلذِّكۡرِ فَهَلۡ مِن مُّدَّكِرٖ", "Walaqad yassarna alqurana lilththikri fahal min muddakirin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 23, "Thamud denied the warning", "كَذَّبَتۡ ثَمُودُ بِٱلنُّذُرِ", "Kaththabat thamoodu bialnnuthuri");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 24, "And said, ""Is it one human being among us that we should follow? Indeed, we would then be in error and madness", "فَقَالُوٓاْ أَبَشَرٗا مِّنَّا وَٰحِدٗا نَّتَّبِعُهُۥٓ إِنَّآ إِذٗا لَّفِي ضَلَٰلٖ وَسُعُرٍ", "Faqaloo abasharan minna wahidan nattabiAAuhu inna ithan lafee dalalin wasuAAurin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 25, "Has the message been sent down upon him from among us? Rather, he is an insolent liar", "أَءُلۡقِيَ ٱلذِّكۡرُ عَلَيۡهِ مِنۢ بَيۡنِنَا بَلۡ هُوَ كَذَّابٌ أَشِرٞ", "Aolqiya alththikru AAalayhi min baynina bal huwa kaththabun ashirun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 26, "They will know tomorrow who is the insolent liar", "سَيَعۡلَمُونَ غَدٗا مَّنِ ٱلۡكَذَّابُ ٱلۡأَشِرُ", "SayaAAlamoona ghadan mani alkaththabu alashiru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 27, "Indeed, We are sending the she-camel as trial for them, so watch them and be patient", "إِنَّا مُرۡسِلُواْ ٱلنَّاقَةِ فِتۡنَةٗ لَّهُمۡ فَٱرۡتَقِبۡهُمۡ وَٱصۡطَبِرۡ", "Inna mursiloo alnnaqati fitnatan lahum fairtaqibhum waistabir");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 28, "And inform them that the water is shared between them, each [day of] drink attended [by turn]", "وَنَبِّئۡهُمۡ أَنَّ ٱلۡمَآءَ قِسۡمَةُۢ بَيۡنَهُمۡۖ كُلُّ شِرۡبٖ مُّحۡتَضَرٞ", "Wanabbihum anna almaa qismatun baynahum kullu shirbin muhtadarun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 29, "But they called their companion, and he dared and hamstrung [her]", "فَنَادَوۡاْ صَاحِبَهُمۡ فَتَعَاطَىٰ فَعَقَرَ", "Fanadaw sahibahum fataAAata faAAaqara");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 30, "And how [severe] were My punishment and warning", "فَكَيۡفَ كَانَ عَذَابِي وَنُذُرِ", "Fakayfa kana AAathabee wanuthuri");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 31, "Indeed, We sent upon them one blast from the sky, and they became like the dry twig fragments of an [animal] pen", "إِنَّآ أَرۡسَلۡنَا عَلَيۡهِمۡ صَيۡحَةٗ وَٰحِدَةٗ فَكَانُواْ كَهَشِيمِ ٱلۡمُحۡتَظِرِ", "Inna arsalna AAalayhim sayhatan wahidatan fakanoo kahasheemi almuhtathiri");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 32, "And We have certainly made the Qur'an easy for remembrance, so is there any who will remember", "وَلَقَدۡ يَسَّرۡنَا ٱلۡقُرۡءَانَ لِلذِّكۡرِ فَهَلۡ مِن مُّدَّكِرٖ", "Walaqad yassarna alqurana lilththikri fahal min muddakirin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 33, "The people of Lot denied the warning", "كَذَّبَتۡ قَوۡمُ لُوطِۭ بِٱلنُّذُرِ", "Kaththabat qawmu lootin bialnnuthuri");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 34, "Indeed, We sent upon them a storm of stones, except the family of Lot - We saved them before dawn", "إِنَّآ أَرۡسَلۡنَا عَلَيۡهِمۡ حَاصِبًا إِلَّآ ءَالَ لُوطٖۖ نَّجَّيۡنَٰهُم بِسَحَرٖ", "Inna arsalna AAalayhim hasiban illa ala lootin najjaynahum bisaharin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 35, "As favor from us. Thus do We reward he who is grateful", "نِّعۡمَةٗ مِّنۡ عِندِنَاۚ كَذَٰلِكَ نَجۡزِي مَن شَكَرَ", "NiAAmatan min AAindina kathalika najzee man shakara");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 36, "And he had already warned them of Our assault, but they disputed the warning", "وَلَقَدۡ أَنذَرَهُم بَطۡشَتَنَا فَتَمَارَوۡاْ بِٱلنُّذُرِ", "Walaqad antharahum batshatana fatamaraw bialnnuthuri");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 37, "And they had demanded from him his guests, but We obliterated their eyes, [saying], ""Taste My punishment and warning", "وَلَقَدۡ رَٰوَدُوهُ عَن ضَيۡفِهِۦ فَطَمَسۡنَآ أَعۡيُنَهُمۡ فَذُوقُواْ عَذَابِي وَنُذُرِ", "Walaqad rawadoohu AAan dayfihi fatamasna aAAyunahum fathooqoo AAathabee wanuthuri");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 38, "And there came upon them by morning an abiding punishment", "وَلَقَدۡ صَبَّحَهُم بُكۡرَةً عَذَابٞ مُّسۡتَقِرّٞ", "Walaqad sabbahahum bukratan AAathabun mustaqirrun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 39, "So taste My punishment and warning", "فَذُوقُواْ عَذَابِي وَنُذُرِ", "Fathooqoo AAathabee wanuthuri");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 40, "And We have certainly made the Qur'an easy for remembrance, so is there any who will remember", "وَلَقَدۡ يَسَّرۡنَا ٱلۡقُرۡءَانَ لِلذِّكۡرِ فَهَلۡ مِن مُّدَّكِرٖ", "Walaqad yassarna alqurana lilththikri fahal min muddakirin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 41, "And there certainly came to the people of Pharaoh warning", "وَلَقَدۡ جَآءَ ءَالَ فِرۡعَوۡنَ ٱلنُّذُرُ", "Walaqad jaa ala firAAawna alnnuthuru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 42, "They denied Our signs, all of them, so We seized them with a seizure of one Exalted in Might and Perfect in Ability", "كَذَّبُواْ بِـَٔايَٰتِنَا كُلِّهَا فَأَخَذۡنَٰهُمۡ أَخۡذَ عَزِيزٖ مُّقۡتَدِرٍ", "Kaththaboo biayatina kulliha faakhathnahum akhtha AAazeezin muqtadirin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 43, "Are your disbelievers better than those [former ones], or have you immunity in the scripture", "أَكُفَّارُكُمۡ خَيۡرٞ مِّنۡ أُوْلَـٰٓئِكُمۡ أَمۡ لَكُم بَرَآءَةٞ فِي ٱلزُّبُرِ", "Akuffarukum khayrun min olaikum am lakum baraatun fee alzzuburi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 44, "Or do they say, ""We are an assembly supporting [each other]", "أَمۡ يَقُولُونَ نَحۡنُ جَمِيعٞ مُّنتَصِرٞ", "Am yaqooloona nahnu jameeAAun muntasirun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 45, "[Their] assembly will be defeated, and they will turn their backs [in retreat]", "سَيُهۡزَمُ ٱلۡجَمۡعُ وَيُوَلُّونَ ٱلدُّبُرَ", "Sayuhzamu aljamAAu wayuwalloona alddubura");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 46, "But the Hour is their appointment [for due punishment], and the Hour is more disastrous and more bitter", "بَلِ ٱلسَّاعَةُ مَوۡعِدُهُمۡ وَٱلسَّاعَةُ أَدۡهَىٰ وَأَمَرُّ", "Bali alssaAAatu mawAAiduhum waalssaAAatu adha waamarru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 47, "Indeed, the criminals are in error and madness", "إِنَّ ٱلۡمُجۡرِمِينَ فِي ضَلَٰلٖ وَسُعُرٖ", "Inna almujrimeena fee dalalin wasuAAurin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 48, "The Day they are dragged into the Fire on their faces [it will be said], ""Taste the touch of Saqar", "يَوۡمَ يُسۡحَبُونَ فِي ٱلنَّارِ عَلَىٰ وُجُوهِهِمۡ ذُوقُواْ مَسَّ سَقَرَ", "Yawma yushaboona fee alnnari AAala wujoohihim thooqoo massa saqara");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 49, "Indeed, all things We created with predestination", "إِنَّا كُلَّ شَيۡءٍ خَلَقۡنَٰهُ بِقَدَرٖ", "Inna kulla shayin khalaqnahu biqadarin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 50, "And Our command is but one, like a glance of the eye", "وَمَآ أَمۡرُنَآ إِلَّا وَٰحِدَةٞ كَلَمۡحِۭ بِٱلۡبَصَرِ", "Wama amruna illa wahidatun kalamhin bialbasari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 51, "And We have already destroyed your kinds, so is there any who will remember", "وَلَقَدۡ أَهۡلَكۡنَآ أَشۡيَاعَكُمۡ فَهَلۡ مِن مُّدَّكِرٖ", "Walaqad ahlakna ashyaAAakum fahal min muddakirin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 52, "And everything they did is in written records", "وَكُلُّ شَيۡءٖ فَعَلُوهُ فِي ٱلزُّبُرِ", "Wakullu shayin faAAaloohu fee alzzuburi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 53, "And every small and great [thing] is inscribed", "وَكُلُّ صَغِيرٖ وَكَبِيرٖ مُّسۡتَطَرٌ", "Wakullu sagheerin wakabeerin mustatarun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 54, "Indeed, the righteous will be among gardens and rivers", "إِنَّ ٱلۡمُتَّقِينَ فِي جَنَّـٰتٖ وَنَهَرٖ", "Inna almuttaqeena fee jannatin wanaharin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (54, 55, "In a seat of honor near a Sovereign, Perfect in Ability", "فِي مَقۡعَدِ صِدۡقٍ عِندَ مَلِيكٖ مُّقۡتَدِرِۭ", "Fee maqAAadi sidqin AAinda maleekin muqtadirin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 1, "The Most Merciful", "ٱلرَّحۡمَٰنُ", "Alrrahmanu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 2, "Taught the Qur'an", "عَلَّمَ ٱلۡقُرۡءَانَ", "AAallama alqurana");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 3, "Created man", "خَلَقَ ٱلۡإِنسَٰنَ", "Khalaqa alinsana");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 4, "[And] taught him eloquence", "عَلَّمَهُ ٱلۡبَيَانَ", "AAallamahu albayana");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 5, "The sun and the moon [move] by precise calculation", "ٱلشَّمۡسُ وَٱلۡقَمَرُ بِحُسۡبَانٖ", "Alshshamsu waalqamaru bihusbanin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 6, "And the stars and trees prostrate", "وَٱلنَّجۡمُ وَٱلشَّجَرُ يَسۡجُدَانِ", "Waalnnajmu waalshshajaru yasjudani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 7, "And the heaven He raised and imposed the balance", "وَٱلسَّمَآءَ رَفَعَهَا وَوَضَعَ ٱلۡمِيزَانَ", "Waalssamaa rafaAAaha wawadaAAa almeezana");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 8, "That you not transgress within the balance", "أَلَّا تَطۡغَوۡاْ فِي ٱلۡمِيزَانِ", "Alla tatghaw fee almeezani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 9, "And establish weight in justice and do not make deficient the balance", "وَأَقِيمُواْ ٱلۡوَزۡنَ بِٱلۡقِسۡطِ وَلَا تُخۡسِرُواْ ٱلۡمِيزَانَ", "Waaqeemoo alwazna bialqisti wala tukhsiroo almeezana");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 10, "And the earth He laid [out] for the creatures", "وَٱلۡأَرۡضَ وَضَعَهَا لِلۡأَنَامِ", "Waalarda wadaAAaha lilanami");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 11, "Therein is fruit and palm trees having sheaths [of dates]", "فِيهَا فَٰكِهَةٞ وَٱلنَّخۡلُ ذَاتُ ٱلۡأَكۡمَامِ", "Feeha fakihatun waalnnakhlu thatu alakmami");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 12, "And grain having husks and scented plants", "وَٱلۡحَبُّ ذُو ٱلۡعَصۡفِ وَٱلرَّيۡحَانُ", "Waalhabbu thoo alAAasfi waalrrayhani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 13, "So which of the favors of your Lord would you deny", "فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ", "Fabiayyi alai rabbikuma tukaththibani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 14, "He created man from clay like [that of] pottery", "خَلَقَ ٱلۡإِنسَٰنَ مِن صَلۡصَٰلٖ كَٱلۡفَخَّارِ", "Khalaqa alinsana min salsalin kaalfakhkhari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 15, "And He created the jinn from a smokeless flame of fire", "وَخَلَقَ ٱلۡجَآنَّ مِن مَّارِجٖ مِّن نَّارٖ", "Wakhalaqa aljanna min marijin min narin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 16, "So which of the favors of your Lord would you deny", "فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ", "Fabiayyi alai rabbikuma tukaththibani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 17, "[He is] Lord of the two sunrises and Lord of the two sunsets", "رَبُّ ٱلۡمَشۡرِقَيۡنِ وَرَبُّ ٱلۡمَغۡرِبَيۡنِ", "Rabbu almashriqayni warabbu almaghribayni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 18, "So which of the favors of your Lord would you deny", "فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ", "Fabiayyi alai rabbikuma tukaththibani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 19, "He released the two seas, meeting [side by side]", "مَرَجَ ٱلۡبَحۡرَيۡنِ يَلۡتَقِيَانِ", "Maraja albahrayni yaltaqiyani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 20, "Between them is a barrier [so] neither of them transgresses", "بَيۡنَهُمَا بَرۡزَخٞ لَّا يَبۡغِيَانِ", "Baynahuma barzakhun la yabghiyani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 21, "So which of the favors of your Lord would you deny", "فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ", "Fabiayyi alai rabbikuma tukaththibani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 22, "From both of them emerge pearl and coral", "يَخۡرُجُ مِنۡهُمَا ٱللُّؤۡلُؤُ وَٱلۡمَرۡجَانُ", "Yakhruju minhuma alluluo waalmarjanu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 23, "So which of the favors of your Lord would you deny", "فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ", "Fabiayyi alai rabbikuma tukaththibani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 24, "And to Him belong the ships [with sails] elevated in the sea like mountains", "وَلَهُ ٱلۡجَوَارِ ٱلۡمُنشَـَٔاتُ فِي ٱلۡبَحۡرِ كَٱلۡأَعۡلَٰمِ", "Walahu aljawari almunshaatu fee albahri kaalaAAlami");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 25, "So which of the favors of your Lord would you deny", "فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ", "Fabiayyi alai rabbikuma tukaththibani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 26, "Everyone upon the earth will perish", "كُلُّ مَنۡ عَلَيۡهَا فَانٖ", "Kullu man AAalayha fanin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 27, "And there will remain the Face of your Lord, Owner of Majesty and Honor", "وَيَبۡقَىٰ وَجۡهُ رَبِّكَ ذُو ٱلۡجَلَٰلِ وَٱلۡإِكۡرَامِ", "Wayabqa wajhu rabbika thoo aljalali waalikrami");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 28, "So which of the favors of your Lord would you deny", "فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ", "Fabiayyi alai rabbikuma tukaththibani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 29, "Whoever is within the heavens and earth asks Him; every day He is bringing about a matter", "يَسۡـَٔلُهُۥ مَن فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ كُلَّ يَوۡمٍ هُوَ فِي شَأۡنٖ", "Yasaluhu man fee alssamawati waalardi kulla yawmin huwa fee shanin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 30, "So which of the favors of your Lord would you deny", "فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ", "Fabiayyi alai rabbikuma tukaththibani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 31, "We will attend to you, O prominent beings", "سَنَفۡرُغُ لَكُمۡ أَيُّهَ ٱلثَّقَلَانِ", "Sanafrughu lakum ayyuha alththaqalani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 32, "So which of the favors of your Lord would you deny", "فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ", "Fabiayyi alai rabbikuma tukaththibani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 33, "O company of jinn and mankind, if you are able to pass beyond the regions of the heavens and the earth, then pass. You will not pass except by authority [from Allah]", "يَٰمَعۡشَرَ ٱلۡجِنِّ وَٱلۡإِنسِ إِنِ ٱسۡتَطَعۡتُمۡ أَن تَنفُذُواْ مِنۡ أَقۡطَارِ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ فَٱنفُذُواْۚ لَا تَنفُذُونَ إِلَّا بِسُلۡطَٰنٖ", "Ya maAAshara aljinni waalinsi ini istataAAtum an tanfuthoo min aqtari alssamawati waalardi faonfuthoo la tanfuthoona illa bisultanin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 34, "So which of the favors of your Lord would you deny", "فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ", "Fabiayyi alai rabbikuma tukaththibani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 35, "There will be sent upon you a flame of fire and smoke, and you will not defend yourselves", "يُرۡسَلُ عَلَيۡكُمَا شُوَاظٞ مِّن نَّارٖ وَنُحَاسٞ فَلَا تَنتَصِرَانِ", "Yursalu AAalaykuma shuwathun min narin wanuhasun fala tantasirani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 36, "So which of the favors of your Lord would you deny", "فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ", "Fabiayyi alai rabbikuma tukaththibani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 37, "And when the heaven is split open and becomes rose-colored like oil", "فَإِذَا ٱنشَقَّتِ ٱلسَّمَآءُ فَكَانَتۡ وَرۡدَةٗ كَٱلدِّهَانِ", "Faitha inshaqqati alssamao fakanat wardatan kaalddihani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 38, "So which of the favors of your Lord would you deny", "فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ", "Fabiayyi alai rabbikuma tukaththibani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 39, "Then on that Day none will be asked about his sin among men or jinn", "فَيَوۡمَئِذٖ لَّا يُسۡـَٔلُ عَن ذَنۢبِهِۦٓ إِنسٞ وَلَا جَآنّٞ", "Fayawmaithin la yusalu AAan thanbihi insun wala jannun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 40, "So which of the favors of your Lord would you deny", "فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ", "Fabiayyi alai rabbikuma tukaththibani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 41, "The criminals will be known by their marks, and they will be seized by the forelocks and the feet", "يُعۡرَفُ ٱلۡمُجۡرِمُونَ بِسِيمَٰهُمۡ فَيُؤۡخَذُ بِٱلنَّوَٰصِي وَٱلۡأَقۡدَامِ", "YuAArafu almujrimoona biseemahum fayukhathu bialnnawasee waalaqdami");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 42, "So which of the favors of your Lord would you deny", "فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ", "Fabiayyi alai rabbikuma tukaththibani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 43, "This is Hell, which the criminals deny", "هَٰذِهِۦ جَهَنَّمُ ٱلَّتِي يُكَذِّبُ بِهَا ٱلۡمُجۡرِمُونَ", "Hathihi jahannamu allatee yukaththibu biha almujrimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 44, "They will go around between it and scalding water, heated [to the utmost degree]", "يَطُوفُونَ بَيۡنَهَا وَبَيۡنَ حَمِيمٍ ءَانٖ", "Yatoofoona baynaha wabayna hameemin anin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 45, "So which of the favors of your Lord would you deny", "فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ", "Fabiayyi alai rabbikuma tukaththibani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 46, "But for he who has feared the position of his Lord are two gardens", "وَلِمَنۡ خَافَ مَقَامَ رَبِّهِۦ جَنَّتَانِ", "Waliman khafa maqama rabbihi jannatani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 47, "So which of the favors of your Lord would you deny", "فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ", "Fabiayyi alai rabbikuma tukaththibani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 48, "Having [spreading] branches", "ذَوَاتَآ أَفۡنَانٖ", "Thawata afnanin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 49, "So which of the favors of your Lord would you deny", "فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ", "Fabiayyi alai rabbikuma tukaththibani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 50, "In both of them are two springs, flowing", "فِيهِمَا عَيۡنَانِ تَجۡرِيَانِ", "Feehima AAaynani tajriyani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 51, "So which of the favors of your Lord would you deny", "فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ", "Fabiayyi alai rabbikuma tukaththibani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 52, "In both of them are of every fruit, two kinds", "فِيهِمَا مِن كُلِّ فَٰكِهَةٖ زَوۡجَانِ", "Feehima min kulli fakihatin zawjani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 53, "So which of the favors of your Lord would you deny", "فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ", "Fabiayyi alai rabbikuma tukaththibani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 54, "[They are] reclining on beds whose linings are of silk brocade, and the fruit of the two gardens is hanging low", "مُتَّكِـِٔينَ عَلَىٰ فُرُشِۭ بَطَآئِنُهَا مِنۡ إِسۡتَبۡرَقٖۚ وَجَنَى ٱلۡجَنَّتَيۡنِ دَانٖ", "Muttakieena AAala furushin batainuha min istabraqin wajana aljannatayni danin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 55, "So which of the favors of your Lord would you deny", "فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ", "Fabiayyi alai rabbikuma tukaththibani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 56, "In them are women limiting [their] glances, untouched before them by man or jinni", "فِيهِنَّ قَٰصِرَٰتُ ٱلطَّرۡفِ لَمۡ يَطۡمِثۡهُنَّ إِنسٞ قَبۡلَهُمۡ وَلَا جَآنّٞ", "Feehinna qasiratu alttarfi lam yatmithhunna insun qablahum wala jannun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 57, "So which of the favors of your Lord would you deny", "فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ", "Fabiayyi alai rabbikuma tukaththibani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 58, "As if they were rubies and coral", "كَأَنَّهُنَّ ٱلۡيَاقُوتُ وَٱلۡمَرۡجَانُ", "Kaannahunna alyaqootu waalmarjanu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 59, "So which of the favors of your Lord would you deny", "فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ", "Fabiayyi alai rabbikuma tukaththibani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 60, "Is the reward for good [anything] but good", "هَلۡ جَزَآءُ ٱلۡإِحۡسَٰنِ إِلَّا ٱلۡإِحۡسَٰنُ", "Hal jazao alihsani illa alihsanu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 61, "So which of the favors of your Lord would you deny", "فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ", "Fabiayyi alai rabbikuma tukaththibani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 62, "And below them both [in excellence] are two [other] gardens", "وَمِن دُونِهِمَا جَنَّتَانِ", "Wamin doonihima jannatani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 63, "So which of the favors of your Lord would you deny", "فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ", "Fabiayyi alai rabbikuma tukaththibani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 64, "Dark green [in color]", "مُدۡهَآمَّتَانِ", "Mudhammatani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 65, "So which of the favors of your Lord would you deny", "فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ", "Fabiayyi alai rabbikuma tukaththibani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 66, "In both of them are two springs, spouting", "فِيهِمَا عَيۡنَانِ نَضَّاخَتَانِ", "Feehima AAaynani naddakhatani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 67, "So which of the favors of your Lord would you deny", "فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ", "Fabiayyi alai rabbikuma tukaththibani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 68, "In both of them are fruit and palm trees and pomegranates", "فِيهِمَا فَٰكِهَةٞ وَنَخۡلٞ وَرُمَّانٞ", "Feehima fakihatun wanakhlun warummanun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 69, "So which of the favors of your Lord would you deny", "فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ", "Fabiayyi alai rabbikuma tukaththibani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 70, "In them are good and beautiful women", "فِيهِنَّ خَيۡرَٰتٌ حِسَانٞ", "Feehinna khayratun hisanun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 71, "So which of the favors of your Lord would you deny", "فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ", "Fabiayyi alai rabbikuma tukaththibani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 72, "Fair ones reserved in pavilions", "حُورٞ مَّقۡصُورَٰتٞ فِي ٱلۡخِيَامِ", "Hoorun maqsooratun fee alkhiyami");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 73, "So which of the favors of your Lord would you deny", "فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ", "Fabiayyi alai rabbikuma tukaththibani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 74, "Untouched before them by man or jinni", "لَمۡ يَطۡمِثۡهُنَّ إِنسٞ قَبۡلَهُمۡ وَلَا جَآنّٞ", "Lam yatmithhunna insun qablahum wala jannun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 75, "So which of the favors of your Lord would you deny", "فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ", "Fabiayyi alai rabbikuma tukaththibani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 76, "Reclining on green cushions and beautiful fine carpets", "مُتَّكِـِٔينَ عَلَىٰ رَفۡرَفٍ خُضۡرٖ وَعَبۡقَرِيٍّ حِسَانٖ", "Muttakieena AAala rafrafin khudrin waAAabqariyyin hisanin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 77, "So which of the favors of your Lord would you deny", "فَبِأَيِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ", "Fabiayyi alai rabbikuma tukaththibani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (55, 78, "Blessed is the name of your Lord, Owner of Majesty and Honor", "تَبَٰرَكَ ٱسۡمُ رَبِّكَ ذِي ٱلۡجَلَٰلِ وَٱلۡإِكۡرَامِ", "Tabaraka ismu rabbika thee aljalali waalikrami");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 1, "When the Occurrence occurs", "إِذَا وَقَعَتِ ٱلۡوَاقِعَةُ", "Itha waqaAAati alwaqiAAatu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 2, "There is, at its occurrence, no denial", "لَيۡسَ لِوَقۡعَتِهَا كَاذِبَةٌ", "Laysa liwaqAAatiha kathibatun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 3, "It will bring down [some] and raise up [others]", "خَافِضَةٞ رَّافِعَةٌ", "Khafidatun rafiAAatun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 4, "When the earth is shaken with convulsion", "إِذَا رُجَّتِ ٱلۡأَرۡضُ رَجّٗا", "Itha rujjati alardu rajjan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 5, "And the mountains are broken down, crumbling", "وَبُسَّتِ ٱلۡجِبَالُ بَسّٗا", "Wabussati aljibalu bassan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 6, "And become dust dispersing", "فَكَانَتۡ هَبَآءٗ مُّنۢبَثّٗا", "Fakanat habaan munbaththan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 7, "And you become [of] three kinds", "وَكُنتُمۡ أَزۡوَٰجٗا ثَلَٰثَةٗ", "Wakuntum azwajan thalathatan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 8, "Then the companions of the right - what are the companions of the right", "فَأَصۡحَٰبُ ٱلۡمَيۡمَنَةِ مَآ أَصۡحَٰبُ ٱلۡمَيۡمَنَةِ", "Faashabu almaymanati ma ashabu almaymanati");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 9, "And the companions of the left - what are the companions of the left", "وَأَصۡحَٰبُ ٱلۡمَشۡـَٔمَةِ مَآ أَصۡحَٰبُ ٱلۡمَشۡـَٔمَةِ", "Waashabu almashamati ma ashabu almashamati");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 10, "And the forerunners, the forerunners", "وَٱلسَّـٰبِقُونَ ٱلسَّـٰبِقُونَ", "Waalssabiqoona alssabiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 11, "Those are the ones brought near [to Allah]", "أُوْلَـٰٓئِكَ ٱلۡمُقَرَّبُونَ", "Olaika almuqarraboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 12, "In the Gardens of Pleasure", "فِي جَنَّـٰتِ ٱلنَّعِيمِ", "Fee jannati alnnaAAeemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 13, "A [large] company of the former peoples", "ثُلَّةٞ مِّنَ ٱلۡأَوَّلِينَ", "Thullatun mina alawwaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 14, "And a few of the later peoples", "وَقَلِيلٞ مِّنَ ٱلۡأٓخِرِينَ", "Waqaleelun mina alakhireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 15, "On thrones woven [with ornament]", "عَلَىٰ سُرُرٖ مَّوۡضُونَةٖ", "AAala sururin mawdoonatin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 16, "Reclining on them, facing each other", "مُّتَّكِـِٔينَ عَلَيۡهَا مُتَقَٰبِلِينَ", "Muttakieena AAalayha mutaqabileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 17, "There will circulate among them young boys made eternal", "يَطُوفُ عَلَيۡهِمۡ وِلۡدَٰنٞ مُّخَلَّدُونَ", "Yatoofu AAalayhim wildanun mukhalladoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 18, "With vessels, pitchers and a cup [of wine] from a flowing spring", "بِأَكۡوَابٖ وَأَبَارِيقَ وَكَأۡسٖ مِّن مَّعِينٖ", "Biakwabin waabareeqa wakasin min maAAeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 19, "No headache will they have therefrom, nor will they be intoxicated", "لَّا يُصَدَّعُونَ عَنۡهَا وَلَا يُنزِفُونَ", "La yusaddaAAoona AAanha wala yunzifoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 20, "And fruit of what they select", "وَفَٰكِهَةٖ مِّمَّا يَتَخَيَّرُونَ", "Wafakihatin mimma yatakhayyaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 21, "And the meat of fowl, from whatever they desire", "وَلَحۡمِ طَيۡرٖ مِّمَّا يَشۡتَهُونَ", "Walahmi tayrin mimma yashtahoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 22, "And [for them are] fair women with large, [beautiful] eyes", "وَحُورٌ عِينٞ", "Wahoorun AAeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 23, "The likenesses of pearls well-protected", "كَأَمۡثَٰلِ ٱللُّؤۡلُوِٕ ٱلۡمَكۡنُونِ", "Kaamthali allului almaknooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 24, "As reward for what they used to do", "جَزَآءَۢ بِمَا كَانُواْ يَعۡمَلُونَ", "Jazaan bima kanoo yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 25, "They will not hear therein ill speech or commission of sin", "لَا يَسۡمَعُونَ فِيهَا لَغۡوٗا وَلَا تَأۡثِيمًا", "La yasmaAAoona feeha laghwan wala tatheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 26, "Only a saying: ""Peace, peace", "إِلَّا قِيلٗا سَلَٰمٗا سَلَٰمٗا", "Illa qeelan salaman salaman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 27, "The companions of the right - what are the companions of the right", "وَأَصۡحَٰبُ ٱلۡيَمِينِ مَآ أَصۡحَٰبُ ٱلۡيَمِينِ", "Waashabu alyameeni ma ashabu alyameeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 28, "[They will be] among lote trees with thorns removed", "فِي سِدۡرٖ مَّخۡضُودٖ", "Fee sidrin makhdoodin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 29, "And [banana] trees layered [with fruit]", "وَطَلۡحٖ مَّنضُودٖ", "Watalhin mandoodin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 30, "And shade extended", "وَظِلّٖ مَّمۡدُودٖ", "Wathillin mamdoodin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 31, "And water poured out", "وَمَآءٖ مَّسۡكُوبٖ", "Wamain maskoobin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 32, "And fruit, abundant [and varied]", "وَفَٰكِهَةٖ كَثِيرَةٖ", "Wafakihatin katheeratin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 33, "Neither limited [to season] nor forbidden", "لَّا مَقۡطُوعَةٖ وَلَا مَمۡنُوعَةٖ", "La maqtooAAatin wala mamnooAAatin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 34, "And [upon] beds raised high", "وَفُرُشٖ مَّرۡفُوعَةٍ", "Wafurushin marfooAAatin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 35, "Indeed, We have produced the women of Paradise in a [new] creation", "إِنَّآ أَنشَأۡنَٰهُنَّ إِنشَآءٗ", "Inna anshanahunna inshaan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 36, "And made them virgins", "فَجَعَلۡنَٰهُنَّ أَبۡكَارًا", "FajaAAalnahunna abkaran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 37, "Devoted [to their husbands] and of equal age", "عُرُبًا أَتۡرَابٗا", "AAuruban atraban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 38, "For the companions of the right [who are]", "لِّأَصۡحَٰبِ ٱلۡيَمِينِ", "Liashabi alyameeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 39, "A company of the former peoples", "ثُلَّةٞ مِّنَ ٱلۡأَوَّلِينَ", "Thullatun mina alawwaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 40, "And a company of the later peoples", "وَثُلَّةٞ مِّنَ ٱلۡأٓخِرِينَ", "Wathullatun mina alakhireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 41, "And the companions of the left - what are the companions of the left", "وَأَصۡحَٰبُ ٱلشِّمَالِ مَآ أَصۡحَٰبُ ٱلشِّمَالِ", "Waashabu alshshimali ma ashabu alshshimali");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 42, "[They will be] in scorching fire and scalding water", "فِي سَمُومٖ وَحَمِيمٖ", "Fee samoomin wahameemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 43, "And a shade of black smoke", "وَظِلّٖ مِّن يَحۡمُومٖ", "Wathillin min yahmoomin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 44, "Neither cool nor beneficial", "لَّا بَارِدٖ وَلَا كَرِيمٍ", "La baridin wala kareemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 45, "Indeed they were, before that, indulging in affluence", "إِنَّهُمۡ كَانُواْ قَبۡلَ ذَٰلِكَ مُتۡرَفِينَ", "Innahum kanoo qabla thalika mutrafeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 46, "And they used to persist in the great violation", "وَكَانُواْ يُصِرُّونَ عَلَى ٱلۡحِنثِ ٱلۡعَظِيمِ", "Wakanoo yusirroona AAala alhinthi alAAatheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 47, "And they used to say, ""When we die and become dust and bones, are we indeed to be resurrected", "وَكَانُواْ يَقُولُونَ أَئِذَا مِتۡنَا وَكُنَّا تُرَابٗا وَعِظَٰمًا أَءِنَّا لَمَبۡعُوثُونَ", "Wakanoo yaqooloona aitha mitna wakunna turaban waAAithaman ainna lamabAAoothoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 48, "And our forefathers [as well]", "أَوَءَابَآؤُنَا ٱلۡأَوَّلُونَ", "Awa abaona alawwaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 49, "Say, [O Muhammad], ""Indeed, the former and the later peoples", "قُلۡ إِنَّ ٱلۡأَوَّلِينَ وَٱلۡأٓخِرِينَ", "Qul inna alawwaleena waalakhireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 50, "Are to be gathered together for the appointment of a known Day", "لَمَجۡمُوعُونَ إِلَىٰ مِيقَٰتِ يَوۡمٖ مَّعۡلُومٖ", "LamajmooAAoona ila meeqati yawmin maAAloomin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 51, "Then indeed you, O those astray [who are] deniers", "ثُمَّ إِنَّكُمۡ أَيُّهَا ٱلضَّآلُّونَ ٱلۡمُكَذِّبُونَ", "Thumma innakum ayyuha alddalloona almukaththiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 52, "Will be eating from trees of zaqqum", "لَأٓكِلُونَ مِن شَجَرٖ مِّن زَقُّومٖ", "Laakiloona min shajarin min zaqqoomin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 53, "And filling with it your bellies", "فَمَالِـُٔونَ مِنۡهَا ٱلۡبُطُونَ", "Famalioona minha albutoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 54, "And drinking on top of it from scalding water", "فَشَٰرِبُونَ عَلَيۡهِ مِنَ ٱلۡحَمِيمِ", "Fashariboona AAalayhi mina alhameemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 55, "And will drink as the drinking of thirsty camels", "فَشَٰرِبُونَ شُرۡبَ ٱلۡهِيمِ", "Fashariboona shurba alheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 56, "That is their accommodation on the Day of Recompense", "هَٰذَا نُزُلُهُمۡ يَوۡمَ ٱلدِّينِ", "Hatha nuzuluhum yawma alddeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 57, "We have created you, so why do you not believe", "نَحۡنُ خَلَقۡنَٰكُمۡ فَلَوۡلَا تُصَدِّقُونَ", "Nahnu khalaqnakum falawla tusaddiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 58, "Have you seen that which you emit", "أَفَرَءَيۡتُم مَّا تُمۡنُونَ", "Afaraaytum ma tumnoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 59, "Is it you who creates it, or are We the Creator", "ءَأَنتُمۡ تَخۡلُقُونَهُۥٓ أَمۡ نَحۡنُ ٱلۡخَٰلِقُونَ", "Aantum takhluqoonahu am nahnu alkhaliqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 60, "We have decreed death among you, and We are not to be outdone", "نَحۡنُ قَدَّرۡنَا بَيۡنَكُمُ ٱلۡمَوۡتَ وَمَا نَحۡنُ بِمَسۡبُوقِينَ", "Nahnu qaddarna baynakumu almawta wama nahnu bimasbooqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 61, "In that We will change your likenesses and produce you in that [form] which you do not know", "عَلَىٰٓ أَن نُّبَدِّلَ أَمۡثَٰلَكُمۡ وَنُنشِئَكُمۡ فِي مَا لَا تَعۡلَمُونَ", "AAala an nubaddila amthalakum wanunshiakum fee ma la taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 62, "And you have already known the first creation, so will you not remember", "وَلَقَدۡ عَلِمۡتُمُ ٱلنَّشۡأَةَ ٱلۡأُولَىٰ فَلَوۡلَا تَذَكَّرُونَ", "Walaqad AAalimtumu alnnashata aloola falawla tathakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 63, "And have you seen that [seed] which you sow", "أَفَرَءَيۡتُم مَّا تَحۡرُثُونَ", "Afaraaytum ma tahruthoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 64, "Is it you who makes it grow, or are We the grower", "ءَأَنتُمۡ تَزۡرَعُونَهُۥٓ أَمۡ نَحۡنُ ٱلزَّـٰرِعُونَ", "Aantum tazraAAoonahu am nahnu alzzariAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 65, "If We willed, We could make it [dry] debris, and you would remain in wonder", "لَوۡ نَشَآءُ لَجَعَلۡنَٰهُ حُطَٰمٗا فَظَلۡتُمۡ تَفَكَّهُونَ", "Law nashao lajaAAalnahu hutaman fathaltum tafakkahoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 66, "[Saying], ""Indeed, we are [now] in debt", "إِنَّا لَمُغۡرَمُونَ", "Inna lamughramoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 67, "Rather, we have been deprived", "بَلۡ نَحۡنُ مَحۡرُومُونَ", "Bal nahnu mahroomoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 68, "And have you seen the water that you drink", "أَفَرَءَيۡتُمُ ٱلۡمَآءَ ٱلَّذِي تَشۡرَبُونَ", "Afaraaytumu almaa allathee tashraboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 69, "Is it you who brought it down from the clouds, or is it We who bring it down", "ءَأَنتُمۡ أَنزَلۡتُمُوهُ مِنَ ٱلۡمُزۡنِ أَمۡ نَحۡنُ ٱلۡمُنزِلُونَ", "Aantum anzaltumoohu mina almuzni am nahnu almunziloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 70, "If We willed, We could make it bitter, so why are you not grateful", "لَوۡ نَشَآءُ جَعَلۡنَٰهُ أُجَاجٗا فَلَوۡلَا تَشۡكُرُونَ", "Law nashao jaAAalnahu ojajan falawla tashkuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 71, "And have you seen the fire that you ignite", "أَفَرَءَيۡتُمُ ٱلنَّارَ ٱلَّتِي تُورُونَ", "Afaraaytumu alnnara allatee tooroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 72, "Is it you who produced its tree, or are We the producer", "ءَأَنتُمۡ أَنشَأۡتُمۡ شَجَرَتَهَآ أَمۡ نَحۡنُ ٱلۡمُنشِـُٔونَ", "Aantum anshatum shajarataha am nahnu almunshioona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 73, "We have made it a reminder and provision for the travelers", "نَحۡنُ جَعَلۡنَٰهَا تَذۡكِرَةٗ وَمَتَٰعٗا لِّلۡمُقۡوِينَ", "Nahnu jaAAalnaha tathkiratan wamataAAan lilmuqweena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 74, "So exalt the name of your Lord, the Most Great", "فَسَبِّحۡ بِٱسۡمِ رَبِّكَ ٱلۡعَظِيمِ", "Fasabbih biismi rabbika alAAatheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 75, "Then I swear by the setting of the stars", "۞فَلَآ أُقۡسِمُ بِمَوَٰقِعِ ٱلنُّجُومِ", "Fala oqsimu bimawaqiAAi alnnujoomi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 76, "And indeed, it is an oath - if you could know - [most] great", "وَإِنَّهُۥ لَقَسَمٞ لَّوۡ تَعۡلَمُونَ عَظِيمٌ", "Wainnahu laqasamun law taAAlamoona AAatheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 77, "Indeed, it is a noble Qur'an", "إِنَّهُۥ لَقُرۡءَانٞ كَرِيمٞ", "Innahu laquranun kareemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 78, "In a Register well-protected", "فِي كِتَٰبٖ مَّكۡنُونٖ", "Fee kitabin maknoonin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 79, "None touch it except the purified", "لَّا يَمَسُّهُۥٓ إِلَّا ٱلۡمُطَهَّرُونَ", "La yamassuhu illa almutahharoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 80, "[It is] a revelation from the Lord of the worlds", "تَنزِيلٞ مِّن رَّبِّ ٱلۡعَٰلَمِينَ", "Tanzeelun min rabbi alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 81, "Then is it to this statement that you are indifferent", "أَفَبِهَٰذَا ٱلۡحَدِيثِ أَنتُم مُّدۡهِنُونَ", "Afabihatha alhadeethi antum mudhinoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 82, "And make [the thanks for] your provision that you deny [the Provider]", "وَتَجۡعَلُونَ رِزۡقَكُمۡ أَنَّكُمۡ تُكَذِّبُونَ", "WatajAAaloona rizqakum annakum tukaththiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 83, "Then why, when the soul at death reaches the throat", "فَلَوۡلَآ إِذَا بَلَغَتِ ٱلۡحُلۡقُومَ", "Falawla itha balaghati alhulqooma");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 84, "And you are at that time looking on", "وَأَنتُمۡ حِينَئِذٖ تَنظُرُونَ", "Waantum heenaithin tanthuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 85, "And Our angels are nearer to him than you, but you do not see", "وَنَحۡنُ أَقۡرَبُ إِلَيۡهِ مِنكُمۡ وَلَٰكِن لَّا تُبۡصِرُونَ", "Wanahnu aqrabu ilayhi minkum walakin la tubsiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 86, "Then why do you not, if you are not to be recompensed", "فَلَوۡلَآ إِن كُنتُمۡ غَيۡرَ مَدِينِينَ", "Falawla in kuntum ghayra madeeneena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 87, "Bring it back, if you should be truthful", "تَرۡجِعُونَهَآ إِن كُنتُمۡ صَٰدِقِينَ", "TarjiAAoonaha in kuntum sadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 88, "And if the deceased was of those brought near to Allah", "فَأَمَّآ إِن كَانَ مِنَ ٱلۡمُقَرَّبِينَ", "Faamma in kana mina almuqarrabeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 89, "Then [for him is] rest and bounty and a garden of pleasure", "فَرَوۡحٞ وَرَيۡحَانٞ وَجَنَّتُ نَعِيمٖ", "Farawhun warayhanun wajannatu naAAeemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 90, "And if he was of the companions of the right", "وَأَمَّآ إِن كَانَ مِنۡ أَصۡحَٰبِ ٱلۡيَمِينِ", "Waamma in kana min ashabi alyameeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 91, "Then [the angels will say], ""Peace for you; [you are] from the companions of the right", "فَسَلَٰمٞ لَّكَ مِنۡ أَصۡحَٰبِ ٱلۡيَمِينِ", "Fasalamun laka min ashabi alyameeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 92, "But if he was of the deniers [who were] astray", "وَأَمَّآ إِن كَانَ مِنَ ٱلۡمُكَذِّبِينَ ٱلضَّآلِّينَ", "Waamma in kana mina almukaththibeena alddalleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 93, "Then [for him is] accommodation of scalding water", "فَنُزُلٞ مِّنۡ حَمِيمٖ", "Fanuzulun min hameemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 94, "And burning in Hellfire", "وَتَصۡلِيَةُ جَحِيمٍ", "Watasliyatu jaheemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 95, "Indeed, this is the true certainty", "إِنَّ هَٰذَا لَهُوَ حَقُّ ٱلۡيَقِينِ", "Inna hatha lahuwa haqqu alyaqeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (56, 96, "So exalt the name of your Lord, the Most Great", "فَسَبِّحۡ بِٱسۡمِ رَبِّكَ ٱلۡعَظِيمِ", "Fasabbih biismi rabbika alAAatheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (57, 1, "Whatever is in the heavens and earth exalts Allah, and He is the Exalted in Might, the Wise", "سَبَّحَ لِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ", "Sabbaha lillahi ma fee alssamawati waalardi wahuwa alAAazeezu alhakeemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (57, 2, "His is the dominion of the heavens and earth. He gives life and causes death, and He is over all things competent", "لَهُۥ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ يُحۡيِۦ وَيُمِيتُۖ وَهُوَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ", "Lahu mulku alssamawati waalardi yuhyee wayumeetu wahuwa AAala kulli shayin qadeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (57, 3, "He is the First and the Last, the Ascendant and the Intimate, and He is, of all things, Knowing", "هُوَ ٱلۡأَوَّلُ وَٱلۡأٓخِرُ وَٱلظَّـٰهِرُ وَٱلۡبَاطِنُۖ وَهُوَ بِكُلِّ شَيۡءٍ عَلِيمٌ", "Huwa alawwalu waalakhiru waalththahiru waalbatinu wahuwa bikulli shayin AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (57, 4, "It is He who created the heavens and earth in six days and then established Himself above the Throne. He knows what penetrates into the earth and what emerges from it and what descends from the heaven and what ascends therein; and He is with you wherever you are. And Allah, of what you do, is Seeing", "هُوَ ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ فِي سِتَّةِ أَيَّامٖ ثُمَّ ٱسۡتَوَىٰ عَلَى ٱلۡعَرۡشِۖ يَعۡلَمُ مَا يَلِجُ فِي ٱلۡأَرۡضِ وَمَا يَخۡرُجُ مِنۡهَا وَمَا يَنزِلُ مِنَ ٱلسَّمَآءِ وَمَا يَعۡرُجُ فِيهَاۖ وَهُوَ مَعَكُمۡ أَيۡنَ مَا كُنتُمۡۚ وَٱللَّهُ بِمَا تَعۡمَلُونَ بَصِيرٞ", "Huwa allathee khalaqa alssamawati waalarda fee sittati ayyamin thumma istawa AAala alAAarshi yaAAlamu ma yaliju fee alardi wama yakhruju minha wama yanzilu mina alssamai wama yaAAruju feeha wahuwa maAAakum ayna ma kuntum waAllahu bima taAAmaloona baseerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (57, 5, "His is the dominion of the heavens and earth. And to Allah are returned [all] matters", "لَّهُۥ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَإِلَى ٱللَّهِ تُرۡجَعُ ٱلۡأُمُورُ", "Lahu mulku alssamawati waalardi waila Allahi turjaAAu alomooru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (57, 6, "He causes the night to enter the day and causes the day to enter the night, and he is Knowing of that within the breasts", "يُولِجُ ٱلَّيۡلَ فِي ٱلنَّهَارِ وَيُولِجُ ٱلنَّهَارَ فِي ٱلَّيۡلِۚ وَهُوَ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ", "Yooliju allayla fee alnnahari wayooliju alnnahara fee allayi wahuwa AAaleemun bithati alssudoori");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (57, 7, "Believe in Allah and His Messenger and spend out of that in which He has made you successors. For those who have believed among you and spent, there will be a great reward", "ءَامِنُواْ بِٱللَّهِ وَرَسُولِهِۦ وَأَنفِقُواْ مِمَّا جَعَلَكُم مُّسۡتَخۡلَفِينَ فِيهِۖ فَٱلَّذِينَ ءَامَنُواْ مِنكُمۡ وَأَنفَقُواْ لَهُمۡ أَجۡرٞ كَبِيرٞ", "Aminoo biAllahi warasoolihi waanfiqoo mimma jaAAalakum mustakhlafeena feehi faallatheena amanoo minkum waanfaqoo lahum ajrun kabeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (57, 8, "And why do you not believe in Allah while the Messenger invites you to believe in your Lord and He has taken your covenant, if you should [truly] be believers", "وَمَا لَكُمۡ لَا تُؤۡمِنُونَ بِٱللَّهِ وَٱلرَّسُولُ يَدۡعُوكُمۡ لِتُؤۡمِنُواْ بِرَبِّكُمۡ وَقَدۡ أَخَذَ مِيثَٰقَكُمۡ إِن كُنتُم مُّؤۡمِنِينَ", "Wama lakum la tuminoona biAllahi waalrrasoolu yadAAookum lituminoo birabbikum waqad akhatha meethaqakum in kuntum mumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (57, 9, "It is He who sends down upon His Servant [Muhammad] verses of clear evidence that He may bring you out from darknesses into the light. And indeed, Allah is to you Kind and Merciful", "هُوَ ٱلَّذِي يُنَزِّلُ عَلَىٰ عَبۡدِهِۦٓ ءَايَٰتِۭ بَيِّنَٰتٖ لِّيُخۡرِجَكُم مِّنَ ٱلظُّلُمَٰتِ إِلَى ٱلنُّورِۚ وَإِنَّ ٱللَّهَ بِكُمۡ لَرَءُوفٞ رَّحِيمٞ", "Huwa allathee yunazzilu AAala AAabdihi ayatin bayyinatin liyukhrijakum mina alththulumati ila alnnoori wainna Allaha bikum laraoofun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (57, 10, "And why do you not spend in the cause of Allah while to Allah belongs the heritage of the heavens and the earth? Not equal among you are those who spent before the conquest [of Makkah] and fought [and those who did so after it]. Those are greater in degree than they who spent afterwards and fought. But to all Allah has promised the best [reward]. And Allah, with what you do, is Acquainted", "وَمَا لَكُمۡ أَلَّا تُنفِقُواْ فِي سَبِيلِ ٱللَّهِ وَلِلَّهِ مِيرَٰثُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ لَا يَسۡتَوِي مِنكُم مَّنۡ أَنفَقَ مِن قَبۡلِ ٱلۡفَتۡحِ وَقَٰتَلَۚ أُوْلَـٰٓئِكَ أَعۡظَمُ دَرَجَةٗ مِّنَ ٱلَّذِينَ أَنفَقُواْ مِنۢ بَعۡدُ وَقَٰتَلُواْۚ وَكُلّٗا وَعَدَ ٱللَّهُ ٱلۡحُسۡنَىٰۚ وَٱللَّهُ بِمَا تَعۡمَلُونَ خَبِيرٞ", "Wama lakum alla tunfiqoo fee sabeeli Allahi walillahi meerathu alssamawati waalardi la yastawee minkum man anfaqa min qabli alfathi waqatala olaika aAAthamu darajatan mina allatheena anfaqoo min baAAdu waqataloo wakullan waAAada Allahu alhusna waAllahu bima taAAmaloona khabeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (57, 11, "Who is it that would loan Allah a goodly loan so He will multiply it for him and he will have a noble reward", "مَّن ذَا ٱلَّذِي يُقۡرِضُ ٱللَّهَ قَرۡضًا حَسَنٗا فَيُضَٰعِفَهُۥ لَهُۥ وَلَهُۥٓ أَجۡرٞ كَرِيمٞ", "Man tha allathee yuqridu Allaha qardan hasanan fayudaAAifahu lahu walahu ajrun kareemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (57, 12, "On the Day you see the believing men and believing women, their light proceeding before them and on their right, [it will be said], ""Your good tidings today are [of] gardens beneath which rivers flow, wherein you will abide eternally."" That is what is the great attainment", "يَوۡمَ تَرَى ٱلۡمُؤۡمِنِينَ وَٱلۡمُؤۡمِنَٰتِ يَسۡعَىٰ نُورُهُم بَيۡنَ أَيۡدِيهِمۡ وَبِأَيۡمَٰنِهِمۖ بُشۡرَىٰكُمُ ٱلۡيَوۡمَ جَنَّـٰتٞ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَاۚ ذَٰلِكَ هُوَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ", "Yawma tara almumineena waalmuminati yasAAa nooruhum bayna aydeehim wabiaymanihim bushrakumu alyawma jannatun tajree min tahtiha alanharu khalideena feeha thalika huwa alfawzu alAAatheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (57, 13, "On the [same] Day the hypocrite men and hypocrite women will say to those who believed, ""Wait for us that we may acquire some of your light."" It will be said, ""Go back behind you and seek light."" And a wall will be placed between them with a door, its interior containing mercy, but on the outside of it is torment", "يَوۡمَ يَقُولُ ٱلۡمُنَٰفِقُونَ وَٱلۡمُنَٰفِقَٰتُ لِلَّذِينَ ءَامَنُواْ ٱنظُرُونَا نَقۡتَبِسۡ مِن نُّورِكُمۡ قِيلَ ٱرۡجِعُواْ وَرَآءَكُمۡ فَٱلۡتَمِسُواْ نُورٗاۖ فَضُرِبَ بَيۡنَهُم بِسُورٖ لَّهُۥ بَابُۢ بَاطِنُهُۥ فِيهِ ٱلرَّحۡمَةُ وَظَٰهِرُهُۥ مِن قِبَلِهِ ٱلۡعَذَابُ", "Yawma yaqoolu almunafiqoona waalmunafiqatu lillatheena amanoo onthuroona naqtabis min noorikum qeela irjiAAoo waraakum failtamisoo nooran faduriba baynahum bisoorin lahu babun batinuhu feehi alrrahmatu wathahiruhu min qibalihi alAAathabu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (57, 14, "The hypocrites will call to the believers, ""Were we not with you?"" They will say, ""Yes, but you afflicted yourselves and awaited [misfortune for us] and doubted, and wishful thinking deluded you until there came the command of Allah. And the Deceiver deceived you concerning Allah", "يُنَادُونَهُمۡ أَلَمۡ نَكُن مَّعَكُمۡۖ قَالُواْ بَلَىٰ وَلَٰكِنَّكُمۡ فَتَنتُمۡ أَنفُسَكُمۡ وَتَرَبَّصۡتُمۡ وَٱرۡتَبۡتُمۡ وَغَرَّتۡكُمُ ٱلۡأَمَانِيُّ حَتَّىٰ جَآءَ أَمۡرُ ٱللَّهِ وَغَرَّكُم بِٱللَّهِ ٱلۡغَرُورُ", "Yunadoonahum alam nakun maAAakum qaloo bala walakinnakum fatantum anfusakum watarabbastum wairtabtum wagharratkumu alamaniyyu hatta jaa amru Allahi wagharrakum biAllahi algharooru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (57, 15, "So today no ransom will be taken from you or from those who disbelieved. Your refuge is the Fire. It is most worthy of you, and wretched is the destination", "فَٱلۡيَوۡمَ لَا يُؤۡخَذُ مِنكُمۡ فِدۡيَةٞ وَلَا مِنَ ٱلَّذِينَ كَفَرُواْۚ مَأۡوَىٰكُمُ ٱلنَّارُۖ هِيَ مَوۡلَىٰكُمۡۖ وَبِئۡسَ ٱلۡمَصِيرُ", "Faalyawma la yukhathu minkum fidyatun wala mina allatheena kafaroo mawakumu alnnaru hiya mawlakum wabisa almaseeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (57, 16, "Has the time not come for those who have believed that their hearts should become humbly submissive at the remembrance of Allah and what has come down of the truth? And let them not be like those who were given the Scripture before, and a long period passed over them, so their hearts hardened; and many of them are defiantly disobedient", "۞أَلَمۡ يَأۡنِ لِلَّذِينَ ءَامَنُوٓاْ أَن تَخۡشَعَ قُلُوبُهُمۡ لِذِكۡرِ ٱللَّهِ وَمَا نَزَلَ مِنَ ٱلۡحَقِّ وَلَا يَكُونُواْ كَٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ مِن قَبۡلُ فَطَالَ عَلَيۡهِمُ ٱلۡأَمَدُ فَقَسَتۡ قُلُوبُهُمۡۖ وَكَثِيرٞ مِّنۡهُمۡ فَٰسِقُونَ", "Alam yani lillatheena amanoo an takhshaAAa quloobuhum lithikri Allahi wama nazala mina alhaqqi wala yakoonoo kaallatheena ootoo alkitaba min qablu fatala AAalayhimu alamadu faqasat quloobuhum wakatheerun minhum fasiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (57, 17, "Know that Allah gives life to the earth after its lifelessness. We have made clear to you the signs; perhaps you will understand", "ٱعۡلَمُوٓاْ أَنَّ ٱللَّهَ يُحۡيِ ٱلۡأَرۡضَ بَعۡدَ مَوۡتِهَاۚ قَدۡ بَيَّنَّا لَكُمُ ٱلۡأٓيَٰتِ لَعَلَّكُمۡ تَعۡقِلُونَ", "IAAlamoo anna Allaha yuhyee alarda baAAda mawtiha qad bayyanna lakumu alayati laAAallakum taAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (57, 18, "Indeed, the men who practice charity and the women who practice charity and [they who] have loaned Allah a goodly loan - it will be multiplied for them, and they will have a noble reward", "إِنَّ ٱلۡمُصَّدِّقِينَ وَٱلۡمُصَّدِّقَٰتِ وَأَقۡرَضُواْ ٱللَّهَ قَرۡضًا حَسَنٗا يُضَٰعَفُ لَهُمۡ وَلَهُمۡ أَجۡرٞ كَرِيمٞ", "Inna almussaddiqeena waalmussaddiqati waaqradoo Allaha qardan hasanan yudaAAafu lahum walahum ajrun kareemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (57, 19, "And those who have believed in Allah and His messengers - those are [in the ranks of] the supporters of truth and the martyrs, with their Lord. For them is their reward and their light. But those who have disbelieved and denied Our verses - those are the companions of Hellfire", "وَٱلَّذِينَ ءَامَنُواْ بِٱللَّهِ وَرُسُلِهِۦٓ أُوْلَـٰٓئِكَ هُمُ ٱلصِّدِّيقُونَۖ وَٱلشُّهَدَآءُ عِندَ رَبِّهِمۡ لَهُمۡ أَجۡرُهُمۡ وَنُورُهُمۡۖ وَٱلَّذِينَ كَفَرُواْ وَكَذَّبُواْ بِـَٔايَٰتِنَآ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلۡجَحِيمِ", "Waallatheena amanoo biAllahi warusulihi olaika humu alssiddeeqoona waalshshuhadao AAinda rabbihim lahum ajruhum wanooruhum waallatheena kafaroo wakaththaboo biayatina olaika ashabu aljaheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (57, 20, "Know that the life of this world is but amusement and diversion and adornment and boasting to one another and competition in increase of wealth and children - like the example of a rain whose [resulting] plant growth pleases the tillers; then it dries and you see it turned yellow; then it becomes [scattered] debris. And in the Hereafter is severe punishment and forgiveness from Allah and approval. And what is the worldly life except the enjoyment of delusion", "ٱعۡلَمُوٓاْ أَنَّمَا ٱلۡحَيَوٰةُ ٱلدُّنۡيَا لَعِبٞ وَلَهۡوٞ وَزِينَةٞ وَتَفَاخُرُۢ بَيۡنَكُمۡ وَتَكَاثُرٞ فِي ٱلۡأَمۡوَٰلِ وَٱلۡأَوۡلَٰدِۖ كَمَثَلِ غَيۡثٍ أَعۡجَبَ ٱلۡكُفَّارَ نَبَاتُهُۥ ثُمَّ يَهِيجُ فَتَرَىٰهُ مُصۡفَرّٗا ثُمَّ يَكُونُ حُطَٰمٗاۖ وَفِي ٱلۡأٓخِرَةِ عَذَابٞ شَدِيدٞ وَمَغۡفِرَةٞ مِّنَ ٱللَّهِ وَرِضۡوَٰنٞۚ وَمَا ٱلۡحَيَوٰةُ ٱلدُّنۡيَآ إِلَّا مَتَٰعُ ٱلۡغُرُورِ", "IAAlamoo annama alhayatu alddunya laAAibun walahwun wazeenatun watafakhurun baynakum watakathurun fee alamwali waalawladi kamathali ghaythin aAAjaba alkuffara nabatuhu thumma yaheeju fatarahu musfarran thumma yakoonu hutaman wafee alakhirati AAathabun shadeedun wamaghfiratun mina Allahi waridwanun wama alhayatu alddunya illa mataAAu alghuroori");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (57, 21, "Race toward forgiveness from your Lord and a Garden whose width is like the width of the heavens and earth, prepared for those who believed in Allah and His messengers. That is the bounty of Allah which He gives to whom He wills, and Allah is the possessor of great bounty", "سَابِقُوٓاْ إِلَىٰ مَغۡفِرَةٖ مِّن رَّبِّكُمۡ وَجَنَّةٍ عَرۡضُهَا كَعَرۡضِ ٱلسَّمَآءِ وَٱلۡأَرۡضِ أُعِدَّتۡ لِلَّذِينَ ءَامَنُواْ بِٱللَّهِ وَرُسُلِهِۦۚ ذَٰلِكَ فَضۡلُ ٱللَّهِ يُؤۡتِيهِ مَن يَشَآءُۚ وَٱللَّهُ ذُو ٱلۡفَضۡلِ ٱلۡعَظِيمِ", "Sabiqoo ila maghfiratin min rabbikum wajannatin AAarduha kaAAardi alssamai waalardi oAAiddat lillatheena amanoo biAllahi warusulihi thalika fadlu Allahi yuteehi man yashao waAllahu thoo alfadli alAAatheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (57, 22, "No disaster strikes upon the earth or among yourselves except that it is in a register before We bring it into being - indeed that, for Allah, is easy", "مَآ أَصَابَ مِن مُّصِيبَةٖ فِي ٱلۡأَرۡضِ وَلَا فِيٓ أَنفُسِكُمۡ إِلَّا فِي كِتَٰبٖ مِّن قَبۡلِ أَن نَّبۡرَأَهَآۚ إِنَّ ذَٰلِكَ عَلَى ٱللَّهِ يَسِيرٞ", "Ma asaba min museebatin fee alardi wala fee anfusikum illa fee kitabin min qabli an nabraaha inna thalika AAala Allahi yaseerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (57, 23, "In order that you not despair over what has eluded you and not exult [in pride] over what He has given you. And Allah does not like everyone self-deluded and boastful", "لِّكَيۡلَا تَأۡسَوۡاْ عَلَىٰ مَا فَاتَكُمۡ وَلَا تَفۡرَحُواْ بِمَآ ءَاتَىٰكُمۡۗ وَٱللَّهُ لَا يُحِبُّ كُلَّ مُخۡتَالٖ فَخُورٍ", "Likayla tasaw AAala ma fatakum wala tafrahoo bima atakum waAllahu la yuhibbu kulla mukhtalin fakhoorin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (57, 24, "[Those] who are stingy and enjoin upon people stinginess. And whoever turns away - then indeed, Allah is the Free of need, the Praiseworthy", "ٱلَّذِينَ يَبۡخَلُونَ وَيَأۡمُرُونَ ٱلنَّاسَ بِٱلۡبُخۡلِۗ وَمَن يَتَوَلَّ فَإِنَّ ٱللَّهَ هُوَ ٱلۡغَنِيُّ ٱلۡحَمِيدُ", "Allatheena yabkhaloona wayamuroona alnnasa bialbukhli waman yatawalla fainna Allaha huwa alghaniyyu alhameedu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (57, 25, "We have already sent Our messengers with clear evidences and sent down with them the Scripture and the balance that the people may maintain [their affairs] in justice. And We sent down iron, wherein is great military might and benefits for the people, and so that Allah may make evident those who support Him and His messengers unseen. Indeed, Allah is Powerful and Exalted in Might", "لَقَدۡ أَرۡسَلۡنَا رُسُلَنَا بِٱلۡبَيِّنَٰتِ وَأَنزَلۡنَا مَعَهُمُ ٱلۡكِتَٰبَ وَٱلۡمِيزَانَ لِيَقُومَ ٱلنَّاسُ بِٱلۡقِسۡطِۖ وَأَنزَلۡنَا ٱلۡحَدِيدَ فِيهِ بَأۡسٞ شَدِيدٞ وَمَنَٰفِعُ لِلنَّاسِ وَلِيَعۡلَمَ ٱللَّهُ مَن يَنصُرُهُۥ وَرُسُلَهُۥ بِٱلۡغَيۡبِۚ إِنَّ ٱللَّهَ قَوِيٌّ عَزِيزٞ", "Laqad arsalna rusulana bialbayyinati waanzalna maAAahumu alkitaba waalmeezana liyaqooma alnnasu bialqisti waanzalna alhadeeda feehi basun shadeedun wamanafiAAu lilnnasi waliyaAAlama Allahu man yansuruhu warusulahu bialghaybi inna Allaha qawiyyun AAazeezun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (57, 26, "And We have already sent Noah and Abraham and placed in their descendants prophethood and scripture; and among them is he who is guided, but many of them are defiantly disobedient", "وَلَقَدۡ أَرۡسَلۡنَا نُوحٗا وَإِبۡرَٰهِيمَ وَجَعَلۡنَا فِي ذُرِّيَّتِهِمَا ٱلنُّبُوَّةَ وَٱلۡكِتَٰبَۖ فَمِنۡهُم مُّهۡتَدٖۖ وَكَثِيرٞ مِّنۡهُمۡ فَٰسِقُونَ", "Walaqad arsalna noohan waibraheema wajaAAalna fee thurriyyatihima alnnubuwwata waalkitaba faminhum muhtadin wakatheerun minhum fasiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (57, 27, "Then We sent following their footsteps Our messengers and followed [them] with Jesus, the son of Mary, and gave him the Gospel. And We placed in the hearts of those who followed him compassion and mercy and monasticism, which they innovated; We did not prescribe it for them except [that they did so] seeking the approval of Allah. But they did not observe it with due observance. So We gave the ones who believed among them their reward, but many of them are defiantly disobedient", "ثُمَّ قَفَّيۡنَا عَلَىٰٓ ءَاثَٰرِهِم بِرُسُلِنَا وَقَفَّيۡنَا بِعِيسَى ٱبۡنِ مَرۡيَمَ وَءَاتَيۡنَٰهُ ٱلۡإِنجِيلَۖ وَجَعَلۡنَا فِي قُلُوبِ ٱلَّذِينَ ٱتَّبَعُوهُ رَأۡفَةٗ وَرَحۡمَةٗۚ وَرَهۡبَانِيَّةً ٱبۡتَدَعُوهَا مَا كَتَبۡنَٰهَا عَلَيۡهِمۡ إِلَّا ٱبۡتِغَآءَ رِضۡوَٰنِ ٱللَّهِ فَمَا رَعَوۡهَا حَقَّ رِعَايَتِهَاۖ فَـَٔاتَيۡنَا ٱلَّذِينَ ءَامَنُواْ مِنۡهُمۡ أَجۡرَهُمۡۖ وَكَثِيرٞ مِّنۡهُمۡ فَٰسِقُونَ", "Thumma qaffayna AAala atharihim birusulina waqaffayna biAAeesa ibni maryama waataynahu alinjeela wajaAAalna fee quloobi allatheena ittabaAAoohu rafatan warahmatan warahbaniyyatan ibtadaAAooha ma katabnaha AAalayhim illa ibtighaa ridwani Allahi fama raAAawha haqqa riAAayatiha faatayna allatheena amanoo minhum ajrahum wakatheerun minhum fasiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (57, 28, "O you who have believed, fear Allah and believe in His Messenger; He will [then] give you a double portion of His mercy and make for you a light by which you will walk and forgive you; and Allah is Forgiving and Merciful", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱتَّقُواْ ٱللَّهَ وَءَامِنُواْ بِرَسُولِهِۦ يُؤۡتِكُمۡ كِفۡلَيۡنِ مِن رَّحۡمَتِهِۦ وَيَجۡعَل لَّكُمۡ نُورٗا تَمۡشُونَ بِهِۦ وَيَغۡفِرۡ لَكُمۡۚ وَٱللَّهُ غَفُورٞ رَّحِيمٞ", "Ya ayyuha allatheena amanoo ittaqoo Allaha waaminoo birasoolihi yutikum kiflayni min rahmatihi wayajAAal lakum nooran tamshoona bihi wayaghfir lakum waAllahu ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (57, 29, "[This is] so that the People of the Scripture may know that they are not able [to obtain] anything from the bounty of Allah and that [all] bounty is in the hand of Allah; He gives it to whom He wills. And Allah is the possessor of great bounty", "لِّئَلَّا يَعۡلَمَ أَهۡلُ ٱلۡكِتَٰبِ أَلَّا يَقۡدِرُونَ عَلَىٰ شَيۡءٖ مِّن فَضۡلِ ٱللَّهِ وَأَنَّ ٱلۡفَضۡلَ بِيَدِ ٱللَّهِ يُؤۡتِيهِ مَن يَشَآءُۚ وَٱللَّهُ ذُو ٱلۡفَضۡلِ ٱلۡعَظِيمِ", "Lialla yaAAlama ahlu alkitabi alla yaqdiroona AAala shayin min fadli Allahi waanna alfadla biyadi Allahi yuteehi man yashao waAllahu thoo alfadli alAAatheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (58, 1, "Certainly has Allah heard the speech of the one who argues with you, [O Muhammad], concerning her husband and directs her complaint to Allah. And Allah hears your dialogue; indeed, Allah is Hearing and Seeing", "قَدۡ سَمِعَ ٱللَّهُ قَوۡلَ ٱلَّتِي تُجَٰدِلُكَ فِي زَوۡجِهَا وَتَشۡتَكِيٓ إِلَى ٱللَّهِ وَٱللَّهُ يَسۡمَعُ تَحَاوُرَكُمَآۚ إِنَّ ٱللَّهَ سَمِيعُۢ بَصِيرٌ", "Qad samiAAa Allahu qawla allatee tujadiluka fee zawjiha watashtakee ila Allahi waAllahu yasmaAAu tahawurakuma inna Allaha sameeAAun baseerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (58, 2, "Those who pronounce thihar among you [to separate] from their wives - they are not [consequently] their mothers. Their mothers are none but those who gave birth to them. And indeed, they are saying an objectionable statement and a falsehood. But indeed, Allah is Pardoning and Forgiving", "ٱلَّذِينَ يُظَٰهِرُونَ مِنكُم مِّن نِّسَآئِهِم مَّا هُنَّ أُمَّهَٰتِهِمۡۖ إِنۡ أُمَّهَٰتُهُمۡ إِلَّا ٱلَّـٰٓـِٔي وَلَدۡنَهُمۡۚ وَإِنَّهُمۡ لَيَقُولُونَ مُنكَرٗا مِّنَ ٱلۡقَوۡلِ وَزُورٗاۚ وَإِنَّ ٱللَّهَ لَعَفُوٌّ غَفُورٞ", "Allatheena yuthahiroona minkum min nisaihim ma hunna ommahatihim in ommahatuhum illa allaee waladnahum wainnahum layaqooloona munkaran mina alqawli wazooran wainna Allaha laAAafuwwun ghafoorun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (58, 3, "And those who pronounce thihar from their wives and then [wish to] go back on what they said - then [there must be] the freeing of a slave before they touch one another. That is what you are admonished thereby; and Allah is Acquainted with what you do", "وَٱلَّذِينَ يُظَٰهِرُونَ مِن نِّسَآئِهِمۡ ثُمَّ يَعُودُونَ لِمَا قَالُواْ فَتَحۡرِيرُ رَقَبَةٖ مِّن قَبۡلِ أَن يَتَمَآسَّاۚ ذَٰلِكُمۡ تُوعَظُونَ بِهِۦۚ وَٱللَّهُ بِمَا تَعۡمَلُونَ خَبِيرٞ", "Waallatheena yuthahiroona min nisaihim thumma yaAAoodoona lima qaloo fatahreeru raqabatin min qabli an yatamassa thalikum tooAAathoona bihi waAllahu bima taAAmaloona khabeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (58, 4, "And he who does not find [a slave] - then a fast for two months consecutively before they touch one another; and he who is unable - then the feeding of sixty poor persons. That is for you to believe [completely] in Allah and His Messenger; and those are the limits [set by] Allah. And for the disbelievers is a painful punishment", "فَمَن لَّمۡ يَجِدۡ فَصِيَامُ شَهۡرَيۡنِ مُتَتَابِعَيۡنِ مِن قَبۡلِ أَن يَتَمَآسَّاۖ فَمَن لَّمۡ يَسۡتَطِعۡ فَإِطۡعَامُ سِتِّينَ مِسۡكِينٗاۚ ذَٰلِكَ لِتُؤۡمِنُواْ بِٱللَّهِ وَرَسُولِهِۦۚ وَتِلۡكَ حُدُودُ ٱللَّهِۗ وَلِلۡكَٰفِرِينَ عَذَابٌ أَلِيمٌ", "Faman lam yajid fasiyamu shahrayni mutatabiAAayni min qabli an yatamassa faman lam yastatiAA faitAAamu sitteena miskeenan thalika lituminoo biAllahi warasoolihi watilka hudoodu Allahi walilkafireena AAathabun aleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (58, 5, "Indeed, those who oppose Allah and His Messenger are abased as those before them were abased. And We have certainly sent down verses of clear evidence. And for the disbelievers is a humiliating punishment", "إِنَّ ٱلَّذِينَ يُحَآدُّونَ ٱللَّهَ وَرَسُولَهُۥ كُبِتُواْ كَمَا كُبِتَ ٱلَّذِينَ مِن قَبۡلِهِمۡۚ وَقَدۡ أَنزَلۡنَآ ءَايَٰتِۭ بَيِّنَٰتٖۚ وَلِلۡكَٰفِرِينَ عَذَابٞ مُّهِينٞ", "Inna allatheena yuhaddoona Allaha warasoolahu kubitoo kama kubita allatheena min qablihim waqad anzalna ayatin bayyinatin walilkafireena AAathabun muheenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (58, 6, "On the Day when Allah will resurrect them all and inform them of what they did. Allah had enumerated it, while they forgot it; and Allah is, over all things, Witness", "يَوۡمَ يَبۡعَثُهُمُ ٱللَّهُ جَمِيعٗا فَيُنَبِّئُهُم بِمَا عَمِلُوٓاْۚ أَحۡصَىٰهُ ٱللَّهُ وَنَسُوهُۚ وَٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ شَهِيدٌ", "Yawma yabAAathuhumu Allahu jameeAAan fayunabbiohum bima AAamiloo ahsahu Allahu wanasoohu waAllahu AAala kulli shayin shaheedun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (58, 7, "Have you not considered that Allah knows what is in the heavens and what is on the earth? There is in no private conversation three but that He is the fourth of them, nor are there five but that He is the sixth of them - and no less than that and no more except that He is with them [in knowledge] wherever they are. Then He will inform them of what they did, on the Day of Resurrection. Indeed Allah is, of all things, Knowing", "أَلَمۡ تَرَ أَنَّ ٱللَّهَ يَعۡلَمُ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۖ مَا يَكُونُ مِن نَّجۡوَىٰ ثَلَٰثَةٍ إِلَّا هُوَ رَابِعُهُمۡ وَلَا خَمۡسَةٍ إِلَّا هُوَ سَادِسُهُمۡ وَلَآ أَدۡنَىٰ مِن ذَٰلِكَ وَلَآ أَكۡثَرَ إِلَّا هُوَ مَعَهُمۡ أَيۡنَ مَا كَانُواْۖ ثُمَّ يُنَبِّئُهُم بِمَا عَمِلُواْ يَوۡمَ ٱلۡقِيَٰمَةِۚ إِنَّ ٱللَّهَ بِكُلِّ شَيۡءٍ عَلِيمٌ", "Alam tara anna Allaha yaAAlamu ma fee alssamawati wama fee alardi ma yakoonu min najwa thalathatin illa huwa rabiAAuhum wala khamsatin illa huwa sadisuhum wala adna min thalika wala akthara illa huwa maAAahum ayna ma kanoo thumma yunabbiohum bima AAamiloo yawma alqiyamati inna Allaha bikulli shayin AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (58, 8, "Have you not considered those who were forbidden from private conversation, then they return to that which they were forbidden and converse among themselves about sin and aggression and disobedience to the Messenger? And when they come to you, they greet you with that [word] by which Allah does not greet you and say among themselves, ""Why does Allah not punish us for what we say?"" Sufficient for them is Hell, which they will [enter to] burn, and wretched is the destination", "أَلَمۡ تَرَ إِلَى ٱلَّذِينَ نُهُواْ عَنِ ٱلنَّجۡوَىٰ ثُمَّ يَعُودُونَ لِمَا نُهُواْ عَنۡهُ وَيَتَنَٰجَوۡنَ بِٱلۡإِثۡمِ وَٱلۡعُدۡوَٰنِ وَمَعۡصِيَتِ ٱلرَّسُولِۖ وَإِذَا جَآءُوكَ حَيَّوۡكَ بِمَا لَمۡ يُحَيِّكَ بِهِ ٱللَّهُ وَيَقُولُونَ فِيٓ أَنفُسِهِمۡ لَوۡلَا يُعَذِّبُنَا ٱللَّهُ بِمَا نَقُولُۚ حَسۡبُهُمۡ جَهَنَّمُ يَصۡلَوۡنَهَاۖ فَبِئۡسَ ٱلۡمَصِيرُ", "Alam tara ila allatheena nuhoo AAani alnnajwa thumma yaAAoodoona lima nuhoo AAanhu wayatanajawna bialithmi waalAAudwani wamaAAsiyati alrrasooli waitha jaooka hayyawka bima lam yuhayyika bihi Allahu wayaqooloona fee anfusihim lawla yuAAaththibuna Allahu bima naqoolu hasbuhum jahannamu yaslawnaha fabisa almaseeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (58, 9, "O you who have believed, when you converse privately, do not converse about sin and aggression and disobedience to the Messenger but converse about righteousness and piety. And fear Allah, to whom you will be gathered", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِذَا تَنَٰجَيۡتُمۡ فَلَا تَتَنَٰجَوۡاْ بِٱلۡإِثۡمِ وَٱلۡعُدۡوَٰنِ وَمَعۡصِيَتِ ٱلرَّسُولِ وَتَنَٰجَوۡاْ بِٱلۡبِرِّ وَٱلتَّقۡوَىٰۖ وَٱتَّقُواْ ٱللَّهَ ٱلَّذِيٓ إِلَيۡهِ تُحۡشَرُونَ", "Ya ayyuha allatheena amanoo itha tanajaytum fala tatanajaw bialithmi waalAAudwani wamaAAsiyati alrrasooli watanajaw bialbirri waalttaqwa waittaqoo Allaha allathee ilayhi tuhsharoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (58, 10, "Private conversation is only from Satan that he may grieve those who have believed, but he will not harm them at all except by permission of Allah. And upon Allah let the believers rely", "إِنَّمَا ٱلنَّجۡوَىٰ مِنَ ٱلشَّيۡطَٰنِ لِيَحۡزُنَ ٱلَّذِينَ ءَامَنُواْ وَلَيۡسَ بِضَآرِّهِمۡ شَيۡـًٔا إِلَّا بِإِذۡنِ ٱللَّهِۚ وَعَلَى ٱللَّهِ فَلۡيَتَوَكَّلِ ٱلۡمُؤۡمِنُونَ", "Innama alnnajwa mina alshshaytani liyahzuna allatheena amanoo walaysa bidarrihim shayan illa biithni Allahi waAAala Allahi falyatawakkali almuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (58, 11, "O you who have believed, when you are told, ""Space yourselves"" in assemblies, then make space; Allah will make space for you. And when you are told, ""Arise,"" then arise; Allah will raise those who have believed among you and those who were given knowledge, by degrees. And Allah is Acquainted with what you do", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِذَا قِيلَ لَكُمۡ تَفَسَّحُواْ فِي ٱلۡمَجَٰلِسِ فَٱفۡسَحُواْ يَفۡسَحِ ٱللَّهُ لَكُمۡۖ وَإِذَا قِيلَ ٱنشُزُواْ فَٱنشُزُواْ يَرۡفَعِ ٱللَّهُ ٱلَّذِينَ ءَامَنُواْ مِنكُمۡ وَٱلَّذِينَ أُوتُواْ ٱلۡعِلۡمَ دَرَجَٰتٖۚ وَٱللَّهُ بِمَا تَعۡمَلُونَ خَبِيرٞ", "Ya ayyuha allatheena amanoo itha qeela lakum tafassahoo fee almajalisi faifsahoo yafsahi Allahu lakum waitha qeela onshuzoo faonshuzoo yarfaAAi Allahu allatheena amanoo minkum waallatheena ootoo alAAilma darajatin waAllahu bima taAAmaloona khabeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (58, 12, "O you who have believed, when you [wish to] privately consult the Messenger, present before your consultation a charity. That is better for you and purer. But if you find not [the means] - then indeed, Allah is Forgiving and Merciful", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِذَا نَٰجَيۡتُمُ ٱلرَّسُولَ فَقَدِّمُواْ بَيۡنَ يَدَيۡ نَجۡوَىٰكُمۡ صَدَقَةٗۚ ذَٰلِكَ خَيۡرٞ لَّكُمۡ وَأَطۡهَرُۚ فَإِن لَّمۡ تَجِدُواْ فَإِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٌ", "Ya ayyuha allatheena amanoo itha najaytumu alrrasoola faqaddimoo bayna yaday najwakum sadaqatan thalika khayrun lakum waatharu fain lam tajidoo fainna Allaha ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (58, 13, "Have you feared to present before your consultation charities? Then when you do not and Allah has forgiven you, then [at least] establish prayer and give zakah and obey Allah and His Messenger. And Allah is Acquainted with what you do", "ءَأَشۡفَقۡتُمۡ أَن تُقَدِّمُواْ بَيۡنَ يَدَيۡ نَجۡوَىٰكُمۡ صَدَقَٰتٖۚ فَإِذۡ لَمۡ تَفۡعَلُواْ وَتَابَ ٱللَّهُ عَلَيۡكُمۡ فَأَقِيمُواْ ٱلصَّلَوٰةَ وَءَاتُواْ ٱلزَّكَوٰةَ وَأَطِيعُواْ ٱللَّهَ وَرَسُولَهُۥۚ وَٱللَّهُ خَبِيرُۢ بِمَا تَعۡمَلُونَ", "Aashfaqtum an tuqaddimoo bayna yaday najwakum sadaqatin faith lam tafAAaloo wataba Allahu AAalaykum faaqeemoo alssalata waatoo alzzakata waateeAAoo Allaha warasoolahu waAllahu khabeerun bima taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (58, 14, "Have you not considered those who make allies of a people with whom Allah has become angry? They are neither of you nor of them, and they swear to untruth while they know [they are lying]", "۞أَلَمۡ تَرَ إِلَى ٱلَّذِينَ تَوَلَّوۡاْ قَوۡمًا غَضِبَ ٱللَّهُ عَلَيۡهِم مَّا هُم مِّنكُمۡ وَلَا مِنۡهُمۡ وَيَحۡلِفُونَ عَلَى ٱلۡكَذِبِ وَهُمۡ يَعۡلَمُونَ", "Alam tara ila allatheena tawallaw qawman ghadiba Allahu AAalayhim ma hum minkum wala minhum wayahlifoona AAala alkathibi wahum yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (58, 15, "Allah has prepared for them a severe punishment. Indeed, it was evil that they were doing", "أَعَدَّ ٱللَّهُ لَهُمۡ عَذَابٗا شَدِيدًاۖ إِنَّهُمۡ سَآءَ مَا كَانُواْ يَعۡمَلُونَ", "aAAadda Allahu lahum AAathaban shadeedan innahum saa ma kanoo yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (58, 16, "They took their [false] oaths as a cover, so they averted [people] from the way of Allah, and for them is a humiliating punishment", "ٱتَّخَذُوٓاْ أَيۡمَٰنَهُمۡ جُنَّةٗ فَصَدُّواْ عَن سَبِيلِ ٱللَّهِ فَلَهُمۡ عَذَابٞ مُّهِينٞ", "Ittakhathoo aymanahum junnatan fasaddoo AAan sabeeli Allahi falahum AAathabun muheenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (58, 17, "Never will their wealth or their children avail them against Allah at all. Those are the companions of the Fire; they will abide therein eternally", "لَّن تُغۡنِيَ عَنۡهُمۡ أَمۡوَٰلُهُمۡ وَلَآ أَوۡلَٰدُهُم مِّنَ ٱللَّهِ شَيۡـًٔاۚ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلنَّارِۖ هُمۡ فِيهَا خَٰلِدُونَ", "Lan tughniya AAanhum amwaluhum wala awladuhum mina Allahi shayan olaika ashabu alnnari hum feeha khalidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (58, 18, "On the Day Allah will resurrect them all, and they will swear to Him as they swear to you and think that they are [standing] on something. Unquestionably, it is they who are the liars", "يَوۡمَ يَبۡعَثُهُمُ ٱللَّهُ جَمِيعٗا فَيَحۡلِفُونَ لَهُۥ كَمَا يَحۡلِفُونَ لَكُمۡ وَيَحۡسَبُونَ أَنَّهُمۡ عَلَىٰ شَيۡءٍۚ أَلَآ إِنَّهُمۡ هُمُ ٱلۡكَٰذِبُونَ", "Yawma yabAAathuhumu Allahu jameeAAan fayahlifoona lahu kama yahlifoona lakum wayahsaboona annahum AAala shayin ala innahum humu alkathiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (58, 19, "Satan has overcome them and made them forget the remembrance of Allah. Those are the party of Satan. Unquestionably, the party of Satan - they will be the losers", "ٱسۡتَحۡوَذَ عَلَيۡهِمُ ٱلشَّيۡطَٰنُ فَأَنسَىٰهُمۡ ذِكۡرَ ٱللَّهِۚ أُوْلَـٰٓئِكَ حِزۡبُ ٱلشَّيۡطَٰنِۚ أَلَآ إِنَّ حِزۡبَ ٱلشَّيۡطَٰنِ هُمُ ٱلۡخَٰسِرُونَ", "Istahwatha AAalayhimu alshshaytanu faansahum thikra Allahi olaika hizbu alshshaytani ala inna hizba alshshaytani humu alkhasiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (58, 20, "Indeed, the ones who oppose Allah and His Messenger - those will be among the most humbled", "إِنَّ ٱلَّذِينَ يُحَآدُّونَ ٱللَّهَ وَرَسُولَهُۥٓ أُوْلَـٰٓئِكَ فِي ٱلۡأَذَلِّينَ", "Inna allatheena yuhaddoona Allaha warasoolahu olaika fee alathalleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (58, 21, "Allah has written, ""I will surely overcome, I and My messengers."" Indeed, Allah is Powerful and Exalted in Might", "كَتَبَ ٱللَّهُ لَأَغۡلِبَنَّ أَنَا۠ وَرُسُلِيٓۚ إِنَّ ٱللَّهَ قَوِيٌّ عَزِيزٞ", "Kataba Allahu laaghlibanna ana warusulee inna Allaha qawiyyun AAazeezun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (58, 22, "You will not find a people who believe in Allah and the Last Day having affection for those who oppose Allah and His Messenger, even if they were their fathers or their sons or their brothers or their kindred. Those - He has decreed within their hearts faith and supported them with spirit from Him. And We will admit them to gardens beneath which rivers flow, wherein they abide eternally. Allah is pleased with them, and they are pleased with Him - those are the party of Allah. Unquestionably, the party of Allah - they are the successful", "لَّا تَجِدُ قَوۡمٗا يُؤۡمِنُونَ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِ يُوَآدُّونَ مَنۡ حَآدَّ ٱللَّهَ وَرَسُولَهُۥ وَلَوۡ كَانُوٓاْ ءَابَآءَهُمۡ أَوۡ أَبۡنَآءَهُمۡ أَوۡ إِخۡوَٰنَهُمۡ أَوۡ عَشِيرَتَهُمۡۚ أُوْلَـٰٓئِكَ كَتَبَ فِي قُلُوبِهِمُ ٱلۡإِيمَٰنَ وَأَيَّدَهُم بِرُوحٖ مِّنۡهُۖ وَيُدۡخِلُهُمۡ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَاۚ رَضِيَ ٱللَّهُ عَنۡهُمۡ وَرَضُواْ عَنۡهُۚ أُوْلَـٰٓئِكَ حِزۡبُ ٱللَّهِۚ أَلَآ إِنَّ حِزۡبَ ٱللَّهِ هُمُ ٱلۡمُفۡلِحُونَ", "La tajidu qawman yuminoona biAllahi waalyawmi alakhiri yuwaddoona man hadda Allaha warasoolahu walaw kanoo abaahum aw abnaahum aw ikhwanahum aw AAasheeratahum olaika kataba fee quloobihimu aleemana waayyadahum biroohin minhu wayudkhiluhum jannatin tajree min tahtiha alanharu khalideena feeha radiya Allahu AAanhum waradoo AAanhu olaika hizbu Allahi ala inna hizba Allahi humu almuflihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (59, 1, "Whatever is in the heavens and whatever is on the earth exalts Allah, and He is the Exalted in Might, the Wise", "سَبَّحَ لِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۖ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ", "Sabbaha lillahi ma fee alssamawati wama fee alardi wahuwa alAAazeezu alhakeemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (59, 2, "It is He who expelled the ones who disbelieved among the People of the Scripture from their homes at the first gathering. You did not think they would leave, and they thought that their fortresses would protect them from Allah; but [the decree of] Allah came upon them from where they had not expected, and He cast terror into their hearts [so] they destroyed their houses by their [own] hands and the hands of the believers. So take warning, O people of vision", "هُوَ ٱلَّذِيٓ أَخۡرَجَ ٱلَّذِينَ كَفَرُواْ مِنۡ أَهۡلِ ٱلۡكِتَٰبِ مِن دِيَٰرِهِمۡ لِأَوَّلِ ٱلۡحَشۡرِۚ مَا ظَنَنتُمۡ أَن يَخۡرُجُواْۖ وَظَنُّوٓاْ أَنَّهُم مَّانِعَتُهُمۡ حُصُونُهُم مِّنَ ٱللَّهِ فَأَتَىٰهُمُ ٱللَّهُ مِنۡ حَيۡثُ لَمۡ يَحۡتَسِبُواْۖ وَقَذَفَ فِي قُلُوبِهِمُ ٱلرُّعۡبَۚ يُخۡرِبُونَ بُيُوتَهُم بِأَيۡدِيهِمۡ وَأَيۡدِي ٱلۡمُؤۡمِنِينَ فَٱعۡتَبِرُواْ يَـٰٓأُوْلِي ٱلۡأَبۡصَٰرِ", "Huwa allathee akhraja allatheena kafaroo min ahli alkitabi min diyarihim liawwali alhashri ma thanantum an yakhrujoo wathannoo annahum maniAAatuhum husoonuhum mina Allahi faatahumu Allahu min haythu lam yahtasiboo waqathafa fee quloobihimu alrruAAba yukhriboona buyootahum biaydeehim waaydee almumineena faiAAtabiroo ya olee alabsari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (59, 3, "And if not that Allah had decreed for them evacuation, He would have punished them in [this] world, and for them in the Hereafter is the punishment of the Fire", "وَلَوۡلَآ أَن كَتَبَ ٱللَّهُ عَلَيۡهِمُ ٱلۡجَلَآءَ لَعَذَّبَهُمۡ فِي ٱلدُّنۡيَاۖ وَلَهُمۡ فِي ٱلۡأٓخِرَةِ عَذَابُ ٱلنَّارِ", "Walawla an kataba Allahu AAalayhimu aljalaa laAAaththabahum fee alddunya walahum fee alakhirati AAathabu alnnari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (59, 4, "That is because they opposed Allah and His Messenger. And whoever opposes Allah - then indeed, Allah is severe in penalty", "ذَٰلِكَ بِأَنَّهُمۡ شَآقُّواْ ٱللَّهَ وَرَسُولَهُۥۖ وَمَن يُشَآقِّ ٱللَّهَ فَإِنَّ ٱللَّهَ شَدِيدُ ٱلۡعِقَابِ", "Thalika biannahum shaqqoo Allaha warasoolahu waman yushaqqi Allaha fainna Allaha shadeedu alAAiqabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (59, 5, "Whatever you have cut down of [their] palm trees or left standing on their trunks - it was by permission of Allah and so He would disgrace the defiantly disobedient", "مَا قَطَعۡتُم مِّن لِّينَةٍ أَوۡ تَرَكۡتُمُوهَا قَآئِمَةً عَلَىٰٓ أُصُولِهَا فَبِإِذۡنِ ٱللَّهِ وَلِيُخۡزِيَ ٱلۡفَٰسِقِينَ", "Ma qataAAtum min leenatin aw taraktumooha qaimatan AAala osooliha fabiithni Allahi waliyukhziya alfasiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (59, 6, "And what Allah restored [of property] to His Messenger from them - you did not spur for it [in an expedition] any horses or camels, but Allah gives His messengers power over whom He wills, and Allah is over all things competent", "وَمَآ أَفَآءَ ٱللَّهُ عَلَىٰ رَسُولِهِۦ مِنۡهُمۡ فَمَآ أَوۡجَفۡتُمۡ عَلَيۡهِ مِنۡ خَيۡلٖ وَلَا رِكَابٖ وَلَٰكِنَّ ٱللَّهَ يُسَلِّطُ رُسُلَهُۥ عَلَىٰ مَن يَشَآءُۚ وَٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ", "Wama afaa Allahu AAala rasoolihi minhum fama awjaftum AAalayhi min khaylin wala rikabin walakinna Allaha yusallitu rusulahu AAala man yashao waAllahu AAala kulli shayin qadeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (59, 7, "And what Allah restored to His Messenger from the people of the towns - it is for Allah and for the Messenger and for [his] near relatives and orphans and the [stranded] traveler - so that it will not be a perpetual distribution among the rich from among you. And whatever the Messenger has given you - take; and what he has forbidden you - refrain from. And fear Allah; indeed, Allah is severe in penalty", "مَّآ أَفَآءَ ٱللَّهُ عَلَىٰ رَسُولِهِۦ مِنۡ أَهۡلِ ٱلۡقُرَىٰ فَلِلَّهِ وَلِلرَّسُولِ وَلِذِي ٱلۡقُرۡبَىٰ وَٱلۡيَتَٰمَىٰ وَٱلۡمَسَٰكِينِ وَٱبۡنِ ٱلسَّبِيلِ كَيۡ لَا يَكُونَ دُولَةَۢ بَيۡنَ ٱلۡأَغۡنِيَآءِ مِنكُمۡۚ وَمَآ ءَاتَىٰكُمُ ٱلرَّسُولُ فَخُذُوهُ وَمَا نَهَىٰكُمۡ عَنۡهُ فَٱنتَهُواْۚ وَٱتَّقُواْ ٱللَّهَۖ إِنَّ ٱللَّهَ شَدِيدُ ٱلۡعِقَابِ", "Ma afaa Allahu AAala rasoolihi min ahli alqura falillahi walilrrasooli walithee alqurba waalyatama waalmasakeeni waibni alssabeeli kay la yakoona doolatan bayna alaghniyai minkum wama atakumu alrrasoolu fakhuthoohu wama nahakum AAanhu faintahoo waittaqoo Allaha inna Allaha shadeedu alAAiqabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (59, 8, "For the poor emigrants who were expelled from their homes and their properties, seeking bounty from Allah and [His] approval and supporting Allah and His Messenger, [there is also a share]. Those are the truthful", "لِلۡفُقَرَآءِ ٱلۡمُهَٰجِرِينَ ٱلَّذِينَ أُخۡرِجُواْ مِن دِيَٰرِهِمۡ وَأَمۡوَٰلِهِمۡ يَبۡتَغُونَ فَضۡلٗا مِّنَ ٱللَّهِ وَرِضۡوَٰنٗا وَيَنصُرُونَ ٱللَّهَ وَرَسُولَهُۥٓۚ أُوْلَـٰٓئِكَ هُمُ ٱلصَّـٰدِقُونَ", "Lilfuqarai almuhajireena allatheena okhrijoo min diyarihim waamwalihim yabtaghoona fadlan mina Allahi waridwanan wayansuroona Allaha warasoolahu olaika humu alssadiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (59, 9, "And [also for] those who were settled in al-Madinah and [adopted] the faith before them. They love those who emigrated to them and find not any want in their breasts of what the emigrants were given but give [them] preference over themselves, even though they are in privation. And whoever is protected from the stinginess of his soul - it is those who will be the successful", "وَٱلَّذِينَ تَبَوَّءُو ٱلدَّارَ وَٱلۡإِيمَٰنَ مِن قَبۡلِهِمۡ يُحِبُّونَ مَنۡ هَاجَرَ إِلَيۡهِمۡ وَلَا يَجِدُونَ فِي صُدُورِهِمۡ حَاجَةٗ مِّمَّآ أُوتُواْ وَيُؤۡثِرُونَ عَلَىٰٓ أَنفُسِهِمۡ وَلَوۡ كَانَ بِهِمۡ خَصَاصَةٞۚ وَمَن يُوقَ شُحَّ نَفۡسِهِۦ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُفۡلِحُونَ", "Waallatheena tabawwaoo alddara waaleemana min qablihim yuhibboona man hajara ilayhim wala yajidoona fee sudoorihim hajatan mimma ootoo wayuthiroona AAala anfusihim walaw kana bihim khasasatun waman yooqa shuhha nafsihi faolaika humu almuflihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (59, 10, "And [there is a share for] those who came after them, saying, ""Our Lord, forgive us and our brothers who preceded us in faith and put not in our hearts [any] resentment toward those who have believed. Our Lord, indeed You are Kind and Merciful", "وَٱلَّذِينَ جَآءُو مِنۢ بَعۡدِهِمۡ يَقُولُونَ رَبَّنَا ٱغۡفِرۡ لَنَا وَلِإِخۡوَٰنِنَا ٱلَّذِينَ سَبَقُونَا بِٱلۡإِيمَٰنِ وَلَا تَجۡعَلۡ فِي قُلُوبِنَا غِلّٗا لِّلَّذِينَ ءَامَنُواْ رَبَّنَآ إِنَّكَ رَءُوفٞ رَّحِيمٌ", "Waallatheena jaoo min baAAdihim yaqooloona rabbana ighfir lana waliikhwanina allatheena sabaqoona bialeemani wala tajAAal fee quloobina ghillan lillatheena amanoo rabbana innaka raoofun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (59, 11, "Have you not considered those who practice hypocrisy, saying to their brothers who have disbelieved among the People of the Scripture, ""If you are expelled, we will surely leave with you, and we will not obey, in regard to you, anyone - ever; and if you are fought, we will surely aid you."" But Allah testifies that they are liars", "۞أَلَمۡ تَرَ إِلَى ٱلَّذِينَ نَافَقُواْ يَقُولُونَ لِإِخۡوَٰنِهِمُ ٱلَّذِينَ كَفَرُواْ مِنۡ أَهۡلِ ٱلۡكِتَٰبِ لَئِنۡ أُخۡرِجۡتُمۡ لَنَخۡرُجَنَّ مَعَكُمۡ وَلَا نُطِيعُ فِيكُمۡ أَحَدًا أَبَدٗا وَإِن قُوتِلۡتُمۡ لَنَنصُرَنَّكُمۡ وَٱللَّهُ يَشۡهَدُ إِنَّهُمۡ لَكَٰذِبُونَ", "Alam tara ila allatheena nafaqoo yaqooloona liikhwanihimu allatheena kafaroo min ahli alkitabi lain okhrijtum lanakhrujanna maAAakum wala nuteeAAu feekum ahadan abadan wain qootiltum lanansurannakum waAllahu yashhadu innahum lakathiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (59, 12, "If they are expelled, they will not leave with them, and if they are fought, they will not aid them. And [even] if they should aid them, they will surely turn their backs; then [thereafter] they will not be aided", "لَئِنۡ أُخۡرِجُواْ لَا يَخۡرُجُونَ مَعَهُمۡ وَلَئِن قُوتِلُواْ لَا يَنصُرُونَهُمۡ وَلَئِن نَّصَرُوهُمۡ لَيُوَلُّنَّ ٱلۡأَدۡبَٰرَ ثُمَّ لَا يُنصَرُونَ", "Lain okhrijoo la yakhrujoona maAAahum walain qootiloo la yansuroonahum walain nasaroohum layuwallunna aladbara thumma la yunsaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (59, 13, "You [believers] are more fearful within their breasts than Allah. That is because they are a people who do not understand", "لَأَنتُمۡ أَشَدُّ رَهۡبَةٗ فِي صُدُورِهِم مِّنَ ٱللَّهِۚ ذَٰلِكَ بِأَنَّهُمۡ قَوۡمٞ لَّا يَفۡقَهُونَ", "Laantum ashaddu rahbatan fee sudoorihim mina Allahi thalika biannahum qawmun la yafqahoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (59, 14, "They will not fight you all except within fortified cities or from behind walls. Their violence among themselves is severe. You think they are together, but their hearts are diverse. That is because they are a people who do not reason", "لَا يُقَٰتِلُونَكُمۡ جَمِيعًا إِلَّا فِي قُرٗى مُّحَصَّنَةٍ أَوۡ مِن وَرَآءِ جُدُرِۭۚ بَأۡسُهُم بَيۡنَهُمۡ شَدِيدٞۚ تَحۡسَبُهُمۡ جَمِيعٗا وَقُلُوبُهُمۡ شَتَّىٰۚ ذَٰلِكَ بِأَنَّهُمۡ قَوۡمٞ لَّا يَعۡقِلُونَ", "La yuqatiloonakum jameeAAan illa fee quran muhassanatin aw min warai judurin basuhum baynahum shadeedun tahsabuhum jameeAAan waquloobuhum shatta thalika biannahum qawmun la yaAAqiloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (59, 15, "[Theirs is] like the example of those shortly before them: they tasted the bad consequence of their affair, and they will have a painful punishment", "كَمَثَلِ ٱلَّذِينَ مِن قَبۡلِهِمۡ قَرِيبٗاۖ ذَاقُواْ وَبَالَ أَمۡرِهِمۡ وَلَهُمۡ عَذَابٌ أَلِيمٞ", "Kamathali allatheena min qablihim qareeban thaqoo wabala amrihim walahum AAathabun aleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (59, 16, "[The hypocrites are] like the example of Satan when he says to man, ""Disbelieve."" But when he disbelieves, he says, ""Indeed, I am disassociated from you. Indeed, I fear Allah, Lord of the worlds", "كَمَثَلِ ٱلشَّيۡطَٰنِ إِذۡ قَالَ لِلۡإِنسَٰنِ ٱكۡفُرۡ فَلَمَّا كَفَرَ قَالَ إِنِّي بَرِيٓءٞ مِّنكَ إِنِّيٓ أَخَافُ ٱللَّهَ رَبَّ ٱلۡعَٰلَمِينَ", "Kamathali alshshaytani ith qala lilinsani okfur falamma kafara qala innee bareeon minka innee akhafu Allaha rabba alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (59, 17, "So the outcome for both of them is that they will be in the Fire, abiding eternally therein. And that is the recompense of the wrong-doers", "فَكَانَ عَٰقِبَتَهُمَآ أَنَّهُمَا فِي ٱلنَّارِ خَٰلِدَيۡنِ فِيهَاۚ وَذَٰلِكَ جَزَـٰٓؤُاْ ٱلظَّـٰلِمِينَ", "Fakana AAaqibatahuma annahuma fee alnnari khalidayni feeha wathalika jazao alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (59, 18, "O you who have believed, fear Allah. And let every soul look to what it has put forth for tomorrow - and fear Allah. Indeed, Allah is Acquainted with what you do", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱتَّقُواْ ٱللَّهَ وَلۡتَنظُرۡ نَفۡسٞ مَّا قَدَّمَتۡ لِغَدٖۖ وَٱتَّقُواْ ٱللَّهَۚ إِنَّ ٱللَّهَ خَبِيرُۢ بِمَا تَعۡمَلُونَ", "Ya ayyuha allatheena amanoo ittaqoo Allaha waltanthur nafsun ma qaddamat lighadin waittaqoo Allaha inna Allaha khabeerun bima taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (59, 19, "And be not like those who forgot Allah, so He made them forget themselves. Those are the defiantly disobedient", "وَلَا تَكُونُواْ كَٱلَّذِينَ نَسُواْ ٱللَّهَ فَأَنسَىٰهُمۡ أَنفُسَهُمۡۚ أُوْلَـٰٓئِكَ هُمُ ٱلۡفَٰسِقُونَ", "Wala takoonoo kaallatheena nasoo Allaha faansahum anfusahum olaika humu alfasiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (59, 20, "Not equal are the companions of the Fire and the companions of Paradise. The companions of Paradise - they are the attainers [of success]", "لَا يَسۡتَوِيٓ أَصۡحَٰبُ ٱلنَّارِ وَأَصۡحَٰبُ ٱلۡجَنَّةِۚ أَصۡحَٰبُ ٱلۡجَنَّةِ هُمُ ٱلۡفَآئِزُونَ", "La yastawee ashabu alnnari waashabu aljannati ashabu aljannati humu alfaizoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (59, 21, "If We had sent down this Qur'an upon a mountain, you would have seen it humbled and coming apart from fear of Allah. And these examples We present to the people that perhaps they will give thought", "لَوۡ أَنزَلۡنَا هَٰذَا ٱلۡقُرۡءَانَ عَلَىٰ جَبَلٖ لَّرَأَيۡتَهُۥ خَٰشِعٗا مُّتَصَدِّعٗا مِّنۡ خَشۡيَةِ ٱللَّهِۚ وَتِلۡكَ ٱلۡأَمۡثَٰلُ نَضۡرِبُهَا لِلنَّاسِ لَعَلَّهُمۡ يَتَفَكَّرُونَ", "Law anzalna hatha alqurana AAala jabalin laraaytahu khashiAAan mutasaddiAAan min khashyati Allahi watilka alamthalu nadribuha lilnnasi laAAallahum yatafakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (59, 22, "He is Allah, other than whom there is no deity, Knower of the unseen and the witnessed. He is the Entirely Merciful, the Especially Merciful", "هُوَ ٱللَّهُ ٱلَّذِي لَآ إِلَٰهَ إِلَّا هُوَۖ عَٰلِمُ ٱلۡغَيۡبِ وَٱلشَّهَٰدَةِۖ هُوَ ٱلرَّحۡمَٰنُ ٱلرَّحِيمُ", "Huwa Allahu allathee la ilaha illa huwa AAalimu alghaybi waalshshahadati huwa alrrahmanu alrraheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (59, 23, "He is Allah, other than whom there is no deity, the Sovereign, the Pure, the Perfection, the Bestower of Faith, the Overseer, the Exalted in Might, the Compeller, the Superior. Exalted is Allah above whatever they associate with Him", "هُوَ ٱللَّهُ ٱلَّذِي لَآ إِلَٰهَ إِلَّا هُوَ ٱلۡمَلِكُ ٱلۡقُدُّوسُ ٱلسَّلَٰمُ ٱلۡمُؤۡمِنُ ٱلۡمُهَيۡمِنُ ٱلۡعَزِيزُ ٱلۡجَبَّارُ ٱلۡمُتَكَبِّرُۚ سُبۡحَٰنَ ٱللَّهِ عَمَّا يُشۡرِكُونَ", "Huwa Allahu allathee la ilaha illa huwa almaliku alquddoosu alssalamu almuminu almuhayminu alAAazeezu aljabbaru almutakabbiru subhana Allahi AAamma yushrikoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (59, 24, "He is Allah, the Creator, the Inventor, the Fashioner; to Him belong the best names. Whatever is in the heavens and earth is exalting Him. And He is the Exalted in Might, the Wise", "هُوَ ٱللَّهُ ٱلۡخَٰلِقُ ٱلۡبَارِئُ ٱلۡمُصَوِّرُۖ لَهُ ٱلۡأَسۡمَآءُ ٱلۡحُسۡنَىٰۚ يُسَبِّحُ لَهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ", "Huwa Allahu alkhaliqu albario almusawwiru lahu alasmao alhusna yusabbihu lahu ma fee alssamawati waalardi wahuwa alAAazeezu alhakeemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (60, 1, "O you who have believed, do not take My enemies and your enemies as allies, extending to them affection while they have disbelieved in what came to you of the truth, having driven out the Prophet and yourselves [only] because you believe in Allah, your Lord. If you have come out for jihad in My cause and seeking means to My approval, [take them not as friends]. You confide to them affection, but I am most knowing of what you have concealed and what you have declared. And whoever does it among you has certainly strayed from the soundness of the way", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَتَّخِذُواْ عَدُوِّي وَعَدُوَّكُمۡ أَوۡلِيَآءَ تُلۡقُونَ إِلَيۡهِم بِٱلۡمَوَدَّةِ وَقَدۡ كَفَرُواْ بِمَا جَآءَكُم مِّنَ ٱلۡحَقِّ يُخۡرِجُونَ ٱلرَّسُولَ وَإِيَّاكُمۡ أَن تُؤۡمِنُواْ بِٱللَّهِ رَبِّكُمۡ إِن كُنتُمۡ خَرَجۡتُمۡ جِهَٰدٗا فِي سَبِيلِي وَٱبۡتِغَآءَ مَرۡضَاتِيۚ تُسِرُّونَ إِلَيۡهِم بِٱلۡمَوَدَّةِ وَأَنَا۠ أَعۡلَمُ بِمَآ أَخۡفَيۡتُمۡ وَمَآ أَعۡلَنتُمۡۚ وَمَن يَفۡعَلۡهُ مِنكُمۡ فَقَدۡ ضَلَّ سَوَآءَ ٱلسَّبِيلِ", "Ya ayyuha allatheena amanoo la tattakhithoo AAaduwwee waAAaduwwakum awliyaa tulqoona ilayhim bialmawaddati waqad kafaroo bima jaakum mina alhaqqi yukhrijoona alrrasoola waiyyakum an tuminoo biAllahi rabbikum in kuntum kharajtum jihadan fee sabeelee waibtighaa mardatee tusirroona ilayhim bialmawaddati waana aAAlamu bima akhfaytum wama aAAlantum waman yafAAalhu minkum faqad dalla sawaa alssabeeli");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (60, 2, "If they gain dominance over you, they would be to you as enemies and extend against you their hands and their tongues with evil, and they wish you would disbelieve", "إِن يَثۡقَفُوكُمۡ يَكُونُواْ لَكُمۡ أَعۡدَآءٗ وَيَبۡسُطُوٓاْ إِلَيۡكُمۡ أَيۡدِيَهُمۡ وَأَلۡسِنَتَهُم بِٱلسُّوٓءِ وَوَدُّواْ لَوۡ تَكۡفُرُونَ", "In yathqafookum yakoonoo lakam aAAdaan wayabsutoo ilaykum aydiyahum waalsinatahum bialssooi wawaddoo law takfuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (60, 3, "Never will your relatives or your children benefit you; the Day of Resurrection He will judge between you. And Allah, of what you do, is Seeing", "لَن تَنفَعَكُمۡ أَرۡحَامُكُمۡ وَلَآ أَوۡلَٰدُكُمۡۚ يَوۡمَ ٱلۡقِيَٰمَةِ يَفۡصِلُ بَيۡنَكُمۡۚ وَٱللَّهُ بِمَا تَعۡمَلُونَ بَصِيرٞ", "Lan tanfaAAakum arhamukum wala awladukum yawma alqiyamati yafsilu baynakum waAllahu bima taAAmaloona baseerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (60, 4, "There has already been for you an excellent pattern in Abraham and those with him, when they said to their people, ""Indeed, we are disassociated from you and from whatever you worship other than Allah. We have denied you, and there has appeared between us and you animosity and hatred forever until you believe in Allah alone"" except for the saying of Abraham to his father, ""I will surely ask forgiveness for you, but I have not [power to do] for you anything against Allah. Our Lord, upon You we have relied, and to You we have returned, and to You is the destination", "قَدۡ كَانَتۡ لَكُمۡ أُسۡوَةٌ حَسَنَةٞ فِيٓ إِبۡرَٰهِيمَ وَٱلَّذِينَ مَعَهُۥٓ إِذۡ قَالُواْ لِقَوۡمِهِمۡ إِنَّا بُرَءَـٰٓؤُاْ مِنكُمۡ وَمِمَّا تَعۡبُدُونَ مِن دُونِ ٱللَّهِ كَفَرۡنَا بِكُمۡ وَبَدَا بَيۡنَنَا وَبَيۡنَكُمُ ٱلۡعَدَٰوَةُ وَٱلۡبَغۡضَآءُ أَبَدًا حَتَّىٰ تُؤۡمِنُواْ بِٱللَّهِ وَحۡدَهُۥٓ إِلَّا قَوۡلَ إِبۡرَٰهِيمَ لِأَبِيهِ لَأَسۡتَغۡفِرَنَّ لَكَ وَمَآ أَمۡلِكُ لَكَ مِنَ ٱللَّهِ مِن شَيۡءٖۖ رَّبَّنَا عَلَيۡكَ تَوَكَّلۡنَا وَإِلَيۡكَ أَنَبۡنَا وَإِلَيۡكَ ٱلۡمَصِيرُ", "Qad kanat lakum oswatun hasanatun fee ibraheema waallatheena maAAahu ith qaloo liqawmihim inna buraao minkum wamimma taAAbudoona min dooni Allahi kafarna bikum wabada baynana wabaynakumu alAAadawatu waalbaghdao abadan hatta tuminoo biAllahi wahdahu illa qawla ibraheema liabeehi laastaghfiranna laka wama amliku laka mina Allahi min shayin rabbana AAalayka tawakkalna wailayka anabna wailayka almaseeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (60, 5, "Our Lord, make us not [objects of] torment for the disbelievers and forgive us, our Lord. Indeed, it is You who is the Exalted in Might, the Wise", "رَبَّنَا لَا تَجۡعَلۡنَا فِتۡنَةٗ لِّلَّذِينَ كَفَرُواْ وَٱغۡفِرۡ لَنَا رَبَّنَآۖ إِنَّكَ أَنتَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ", "Rabbana la tajAAalna fitnatan lillatheena kafaroo waighfir lana rabbana innaka anta alAAazeezu alhakeemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (60, 6, "There has certainly been for you in them an excellent pattern for anyone whose hope is in Allah and the Last Day. And whoever turns away - then indeed, Allah is the Free of need, the Praiseworthy", "لَقَدۡ كَانَ لَكُمۡ فِيهِمۡ أُسۡوَةٌ حَسَنَةٞ لِّمَن كَانَ يَرۡجُواْ ٱللَّهَ وَٱلۡيَوۡمَ ٱلۡأٓخِرَۚ وَمَن يَتَوَلَّ فَإِنَّ ٱللَّهَ هُوَ ٱلۡغَنِيُّ ٱلۡحَمِيدُ", "Laqad kana lakum feehim oswatun hasanatun liman kana yarjoo Allaha waalyawma alakhira waman yatawalla fainna Allaha huwa alghanniyyu alhameedu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (60, 7, "Perhaps Allah will put, between you and those to whom you have been enemies among them, affection. And Allah is competent, and Allah is Forgiving and Merciful", "۞عَسَى ٱللَّهُ أَن يَجۡعَلَ بَيۡنَكُمۡ وَبَيۡنَ ٱلَّذِينَ عَادَيۡتُم مِّنۡهُم مَّوَدَّةٗۚ وَٱللَّهُ قَدِيرٞۚ وَٱللَّهُ غَفُورٞ رَّحِيمٞ", "AAasa Allahu an yajAAala baynakum wabayna allatheena AAadaytum minhum mawaddatan waAllahu qadeerun waAllahu ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (60, 8, "Allah does not forbid you from those who do not fight you because of religion and do not expel you from your homes - from being righteous toward them and acting justly toward them. Indeed, Allah loves those who act justly", "لَّا يَنۡهَىٰكُمُ ٱللَّهُ عَنِ ٱلَّذِينَ لَمۡ يُقَٰتِلُوكُمۡ فِي ٱلدِّينِ وَلَمۡ يُخۡرِجُوكُم مِّن دِيَٰرِكُمۡ أَن تَبَرُّوهُمۡ وَتُقۡسِطُوٓاْ إِلَيۡهِمۡۚ إِنَّ ٱللَّهَ يُحِبُّ ٱلۡمُقۡسِطِينَ", "La yanhakumu Allahu AAani allatheena lam yuqatilookum fee alddeeni walam yukhrijookum min diyarikum an tabarroohum watuqsitoo ilayhim inna Allaha yuhibbu almuqsiteena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (60, 9, "Allah only forbids you from those who fight you because of religion and expel you from your homes and aid in your expulsion - [forbids] that you make allies of them. And whoever makes allies of them, then it is those who are the wrongdoers", "إِنَّمَا يَنۡهَىٰكُمُ ٱللَّهُ عَنِ ٱلَّذِينَ قَٰتَلُوكُمۡ فِي ٱلدِّينِ وَأَخۡرَجُوكُم مِّن دِيَٰرِكُمۡ وَظَٰهَرُواْ عَلَىٰٓ إِخۡرَاجِكُمۡ أَن تَوَلَّوۡهُمۡۚ وَمَن يَتَوَلَّهُمۡ فَأُوْلَـٰٓئِكَ هُمُ ٱلظَّـٰلِمُونَ", "Innama yanhakumu Allahu AAani allatheena qatalookum fee alddeeni waakhrajookum min diyarikum wathaharoo AAala ikhrajikum an tawallawhum waman yatawallahum faolaika humu alththalimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (60, 10, "O you who have believed, when the believing women come to you as emigrants, examine them. Allah is most knowing as to their faith. And if you know them to be believers, then do not return them to the disbelievers; they are not lawful [wives] for them, nor are they lawful [husbands] for them. But give the disbelievers what they have spent. And there is no blame upon you if you marry them when you have given them their due compensation. And hold not to marriage bonds with disbelieving women, but ask for what you have spent and let them ask for what they have spent. That is the judgement of Allah; He judges between you. And Allah is Knowing and Wise", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِذَا جَآءَكُمُ ٱلۡمُؤۡمِنَٰتُ مُهَٰجِرَٰتٖ فَٱمۡتَحِنُوهُنَّۖ ٱللَّهُ أَعۡلَمُ بِإِيمَٰنِهِنَّۖ فَإِنۡ عَلِمۡتُمُوهُنَّ مُؤۡمِنَٰتٖ فَلَا تَرۡجِعُوهُنَّ إِلَى ٱلۡكُفَّارِۖ لَا هُنَّ حِلّٞ لَّهُمۡ وَلَا هُمۡ يَحِلُّونَ لَهُنَّۖ وَءَاتُوهُم مَّآ أَنفَقُواْۚ وَلَا جُنَاحَ عَلَيۡكُمۡ أَن تَنكِحُوهُنَّ إِذَآ ءَاتَيۡتُمُوهُنَّ أُجُورَهُنَّۚ وَلَا تُمۡسِكُواْ بِعِصَمِ ٱلۡكَوَافِرِ وَسۡـَٔلُواْ مَآ أَنفَقۡتُمۡ وَلۡيَسۡـَٔلُواْ مَآ أَنفَقُواْۚ ذَٰلِكُمۡ حُكۡمُ ٱللَّهِ يَحۡكُمُ بَيۡنَكُمۡۖ وَٱللَّهُ عَلِيمٌ حَكِيمٞ", "Ya ayyuha allatheena amanoo itha jaakumu almuminatu muhajiratin faimtahinoohunna Allahu aAAlamu bieemanihinna fain AAalimtumoohunna muminatin fala tarjiAAoohunna ila alkuffari la hunna hillun lahum wala hum yahilloona lahunna waatoohum ma anfaqoo wala junaha AAalaykum an tankihoohunna itha ataytumoohunna ojoorahunna wala tumsikoo biAAisami alkawafiri waisaloo ma anfaqtum walyasaloo ma anfaqoo thalikum hukmu Allahi yahkumu baynakum waAllahu AAaleemun hakeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (60, 11, "And if you have lost any of your wives to the disbelievers and you subsequently obtain [something], then give those whose wives have gone the equivalent of what they had spent. And fear Allah, in whom you are believers", "وَإِن فَاتَكُمۡ شَيۡءٞ مِّنۡ أَزۡوَٰجِكُمۡ إِلَى ٱلۡكُفَّارِ فَعَاقَبۡتُمۡ فَـَٔاتُواْ ٱلَّذِينَ ذَهَبَتۡ أَزۡوَٰجُهُم مِّثۡلَ مَآ أَنفَقُواْۚ وَٱتَّقُواْ ٱللَّهَ ٱلَّذِيٓ أَنتُم بِهِۦ مُؤۡمِنُونَ", "Wain fatakum shayon min azwajikum ila alkuffari faAAaqabtum faatoo allatheena thahabat azwajuhum mithla ma anfaqoo waittaqoo Allaha allathee antum bihi muminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (60, 12, "O Prophet, when the believing women come to you pledging to you that they will not associate anything with Allah, nor will they steal, nor will they commit unlawful sexual intercourse, nor will they kill their children, nor will they bring forth a slander they have invented between their arms and legs, nor will they disobey you in what is right - then accept their pledge and ask forgiveness for them of Allah. Indeed, Allah is Forgiving and Merciful", "يَـٰٓأَيُّهَا ٱلنَّبِيُّ إِذَا جَآءَكَ ٱلۡمُؤۡمِنَٰتُ يُبَايِعۡنَكَ عَلَىٰٓ أَن لَّا يُشۡرِكۡنَ بِٱللَّهِ شَيۡـٔٗا وَلَا يَسۡرِقۡنَ وَلَا يَزۡنِينَ وَلَا يَقۡتُلۡنَ أَوۡلَٰدَهُنَّ وَلَا يَأۡتِينَ بِبُهۡتَٰنٖ يَفۡتَرِينَهُۥ بَيۡنَ أَيۡدِيهِنَّ وَأَرۡجُلِهِنَّ وَلَا يَعۡصِينَكَ فِي مَعۡرُوفٖ فَبَايِعۡهُنَّ وَٱسۡتَغۡفِرۡ لَهُنَّ ٱللَّهَۚ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ", "Ya ayyuha alnnabiyyu itha jaaka almuminatu yubayiAAnaka AAala an la yushrikna biAllahi shayan wala yasriqna wala yazneena wala yaqtulna awladahunna wala yateena bibuhtanin yaftareenahu bayna aydeehinna waarjulihinna wala yaAAseenaka fee maAAroofin fabayiAAhunna waistaghfir lahunna Allaha inna Allaha ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (60, 13, "O you who have believed, do not make allies of a people with whom Allah has become angry. They have despaired of [reward in] the Hereafter just as the disbelievers have despaired of [meeting] the inhabitants of the graves", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَتَوَلَّوۡاْ قَوۡمًا غَضِبَ ٱللَّهُ عَلَيۡهِمۡ قَدۡ يَئِسُواْ مِنَ ٱلۡأٓخِرَةِ كَمَا يَئِسَ ٱلۡكُفَّارُ مِنۡ أَصۡحَٰبِ ٱلۡقُبُورِ", "Ya ayyuha allatheena amanoo la tatawallaw qawman ghadiba Allahu AAalayhim qad yaisoo mina alakhirati kama yaisa alkuffaru min ashabi alquboori");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (61, 1, "Whatever is in the heavens and whatever is on the earth exalts Allah, and He is the Exalted in Might, the Wise", "سَبَّحَ لِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۖ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ", "Sabbaha lillahi ma fee alssamawati wama fee alardi wahuwa alAAazeezu alhakeemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (61, 2, "O you who have believed, why do you say what you do not do", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لِمَ تَقُولُونَ مَا لَا تَفۡعَلُونَ", "Ya ayyuha allatheena amanoo lima taqooloona ma la tafAAaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (61, 3, "Great is hatred in the sight of Allah that you say what you do not do", "كَبُرَ مَقۡتًا عِندَ ٱللَّهِ أَن تَقُولُواْ مَا لَا تَفۡعَلُونَ", "Kabura maqtan AAinda Allahi an taqooloo ma la tafAAaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (61, 4, "Indeed, Allah loves those who fight in His cause in a row as though they are a [single] structure joined firmly", "إِنَّ ٱللَّهَ يُحِبُّ ٱلَّذِينَ يُقَٰتِلُونَ فِي سَبِيلِهِۦ صَفّٗا كَأَنَّهُم بُنۡيَٰنٞ مَّرۡصُوصٞ", "Inna Allaha yuhibbu allatheena yuqatiloona fee sabeelihi saffan kaannahum bunyanun marsoosun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (61, 5, "And [mention, O Muhammad], when Moses said to his people, ""O my people, why do you harm me while you certainly know that I am the messenger of Allah to you?"" And when they deviated, Allah caused their hearts to deviate. And Allah does not guide the defiantly disobedient people", "وَإِذۡ قَالَ مُوسَىٰ لِقَوۡمِهِۦ يَٰقَوۡمِ لِمَ تُؤۡذُونَنِي وَقَد تَّعۡلَمُونَ أَنِّي رَسُولُ ٱللَّهِ إِلَيۡكُمۡۖ فَلَمَّا زَاغُوٓاْ أَزَاغَ ٱللَّهُ قُلُوبَهُمۡۚ وَٱللَّهُ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلۡفَٰسِقِينَ", "Waith qala moosa liqawmihi ya qawmi lima tuthoonanee waqad taAAlamoona annee rasoolu Allahi ilaykum falamma zaghoo azagha Allahu quloobahum waAllahu la yahdee alqawma alfasiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (61, 6, "And [mention] when Jesus, the son of Mary, said, ""O children of Israel, indeed I am the messenger of Allah to you confirming what came before me of the Torah and bringing good tidings of a messenger to come after me, whose name is Ahmad."" But when he came to them with clear evidences, they said, ""This is obvious magic", "وَإِذۡ قَالَ عِيسَى ٱبۡنُ مَرۡيَمَ يَٰبَنِيٓ إِسۡرَـٰٓءِيلَ إِنِّي رَسُولُ ٱللَّهِ إِلَيۡكُم مُّصَدِّقٗا لِّمَا بَيۡنَ يَدَيَّ مِنَ ٱلتَّوۡرَىٰةِ وَمُبَشِّرَۢا بِرَسُولٖ يَأۡتِي مِنۢ بَعۡدِي ٱسۡمُهُۥٓ أَحۡمَدُۖ فَلَمَّا جَآءَهُم بِٱلۡبَيِّنَٰتِ قَالُواْ هَٰذَا سِحۡرٞ مُّبِينٞ", "Waith qala AAeesa ibnu maryama ya banee israeela innee rasoolu Allahi ilaykum musaddiqan lima bayna yadayya mina alttawrati wamubashshiran birasoolin yatee min baAAdee ismuhu ahmadu falamma jaahum bialbayyinati qaloo hatha sihrun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (61, 7, "And who is more unjust than one who invents about Allah untruth while he is being invited to Islam. And Allah does not guide the wrongdoing people", "وَمَنۡ أَظۡلَمُ مِمَّنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ ٱلۡكَذِبَ وَهُوَ يُدۡعَىٰٓ إِلَى ٱلۡإِسۡلَٰمِۚ وَٱللَّهُ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلظَّـٰلِمِينَ", "Waman athlamu mimmani iftara AAala Allahi alkathiba wahuwa yudAAa ila alislami waAllahu la yahdee alqawma alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (61, 8, "They want to extinguish the light of Allah with their mouths, but Allah will perfect His light, although the disbelievers dislike it", "يُرِيدُونَ لِيُطۡفِـُٔواْ نُورَ ٱللَّهِ بِأَفۡوَٰهِهِمۡ وَٱللَّهُ مُتِمُّ نُورِهِۦ وَلَوۡ كَرِهَ ٱلۡكَٰفِرُونَ", "Yureedoona liyutfioo noora Allahi biafwahihim waAllahu mutimmu noorihi walaw kariha alkafiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (61, 9, "It is He who sent His Messenger with guidance and the religion of truth to manifest it over all religion, although those who associate others with Allah dislike it", "هُوَ ٱلَّذِيٓ أَرۡسَلَ رَسُولَهُۥ بِٱلۡهُدَىٰ وَدِينِ ٱلۡحَقِّ لِيُظۡهِرَهُۥ عَلَى ٱلدِّينِ كُلِّهِۦ وَلَوۡ كَرِهَ ٱلۡمُشۡرِكُونَ", "Huwa allathee arsala rasoolahu bialhuda wadeeni alhaqqi liyuthhirahu AAala alddeeni kullihi walaw kariha almushrikoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (61, 10, "O you who have believed, shall I guide you to a transaction that will save you from a painful punishment", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ هَلۡ أَدُلُّكُمۡ عَلَىٰ تِجَٰرَةٖ تُنجِيكُم مِّنۡ عَذَابٍ أَلِيمٖ", "Ya ayyuha allatheena amanoo hal adullukum AAala tijaratin tunjeekum min AAathabin aleemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (61, 11, "[It is that] you believe in Allah and His Messenger and strive in the cause of Allah with your wealth and your lives. That is best for you, if you should know", "تُؤۡمِنُونَ بِٱللَّهِ وَرَسُولِهِۦ وَتُجَٰهِدُونَ فِي سَبِيلِ ٱللَّهِ بِأَمۡوَٰلِكُمۡ وَأَنفُسِكُمۡۚ ذَٰلِكُمۡ خَيۡرٞ لَّكُمۡ إِن كُنتُمۡ تَعۡلَمُونَ", "Tuminoona biAllahi warasoolihi watujahidoona fee sabeeli Allahi biamwalikum waanfusikum thalikum khayrun lakum in kuntum taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (61, 12, "He will forgive for you your sins and admit you to gardens beneath which rivers flow and pleasant dwellings in gardens of perpetual residence. That is the great attainment", "يَغۡفِرۡ لَكُمۡ ذُنُوبَكُمۡ وَيُدۡخِلۡكُمۡ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ وَمَسَٰكِنَ طَيِّبَةٗ فِي جَنَّـٰتِ عَدۡنٖۚ ذَٰلِكَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ", "Yaghfir lakum thunoobakum wayudkhilkum jannatin tajree min tahtiha alanharu wamasakina tayyibatan fee jannati AAadnin thalika alfawzu alAAatheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (61, 13, "And [you will obtain] another [favor] that you love - victory from Allah and an imminent conquest; and give good tidings to the believers", "وَأُخۡرَىٰ تُحِبُّونَهَاۖ نَصۡرٞ مِّنَ ٱللَّهِ وَفَتۡحٞ قَرِيبٞۗ وَبَشِّرِ ٱلۡمُؤۡمِنِينَ", "Waokhra tuhibboonaha nasrun mina Allahi wafathun qarreebun wabashshiri almumineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (61, 14, "O you who have believed, be supporters of Allah, as when Jesus, the son of Mary, said to the disciples, ""Who are my supporters for Allah?"" The disciples said, ""We are supporters of Allah."" And a faction of the Children of Israel believed and a faction disbelieved. So We supported those who believed against their enemy, and they became dominant", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ كُونُوٓاْ أَنصَارَ ٱللَّهِ كَمَا قَالَ عِيسَى ٱبۡنُ مَرۡيَمَ لِلۡحَوَارِيِّـۧنَ مَنۡ أَنصَارِيٓ إِلَى ٱللَّهِۖ قَالَ ٱلۡحَوَارِيُّونَ نَحۡنُ أَنصَارُ ٱللَّهِۖ فَـَٔامَنَت طَّآئِفَةٞ مِّنۢ بَنِيٓ إِسۡرَـٰٓءِيلَ وَكَفَرَت طَّآئِفَةٞۖ فَأَيَّدۡنَا ٱلَّذِينَ ءَامَنُواْ عَلَىٰ عَدُوِّهِمۡ فَأَصۡبَحُواْ ظَٰهِرِينَ", "Ya ayyuha allatheena amanoo koonoo ansara Allahi kama qala AAeesa ibnu maryama lilhawariyyeena man ansaree ila Allahi qala alhawariyyoona nahnu ansaru Allahi faamanat taifatun min banee israeela wakafarat taifatun faayyadna allatheena amanoo AAala AAaduwwihim faasbahoo thahireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (62, 1, "Whatever is in the heavens and whatever is on the earth is exalting Allah, the Sovereign, the Pure, the Exalted in Might, the Wise", "يُسَبِّحُ لِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِ ٱلۡمَلِكِ ٱلۡقُدُّوسِ ٱلۡعَزِيزِ ٱلۡحَكِيمِ", "Yusabbihu lillahi ma fee alssamawati wama fee alardi almaliki alquddoosi alAAazeezi alhakeemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (62, 2, "It is He who has sent among the unlettered a Messenger from themselves reciting to them His verses and purifying them and teaching them the Book and wisdom - although they were before in clear error", "هُوَ ٱلَّذِي بَعَثَ فِي ٱلۡأُمِّيِّـۧنَ رَسُولٗا مِّنۡهُمۡ يَتۡلُواْ عَلَيۡهِمۡ ءَايَٰتِهِۦ وَيُزَكِّيهِمۡ وَيُعَلِّمُهُمُ ٱلۡكِتَٰبَ وَٱلۡحِكۡمَةَ وَإِن كَانُواْ مِن قَبۡلُ لَفِي ضَلَٰلٖ مُّبِينٖ", "Huwa allathee baAAatha fee alommiyyeena rasoolan minhum yatloo AAalayhim ayatihi wayuzakkeehim wayuAAallimuhumu alkitaba waalhikmata wain kanoo min qablu lafee dalalin mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (62, 3, "And [to] others of them who have not yet joined them. And He is the Exalted in Might, the Wise", "وَءَاخَرِينَ مِنۡهُمۡ لَمَّا يَلۡحَقُواْ بِهِمۡۚ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ", "Waakhareena minhum lamma yalhaqoo bihim wahuwa alAAazeezu alhakeemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (62, 4, "That is the bounty of Allah, which He gives to whom He wills, and Allah is the possessor of great bounty", "ذَٰلِكَ فَضۡلُ ٱللَّهِ يُؤۡتِيهِ مَن يَشَآءُۚ وَٱللَّهُ ذُو ٱلۡفَضۡلِ ٱلۡعَظِيمِ", "Thalika fadlu Allahi yuteehi man yashao waAllahu thoo alfadli alAAatheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (62, 5, "The example of those who were entrusted with the Torah and then did not take it on is like that of a donkey who carries volumes [of books]. Wretched is the example of the people who deny the signs of Allah. And Allah does not guide the wrongdoing people", "مَثَلُ ٱلَّذِينَ حُمِّلُواْ ٱلتَّوۡرَىٰةَ ثُمَّ لَمۡ يَحۡمِلُوهَا كَمَثَلِ ٱلۡحِمَارِ يَحۡمِلُ أَسۡفَارَۢاۚ بِئۡسَ مَثَلُ ٱلۡقَوۡمِ ٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِ ٱللَّهِۚ وَٱللَّهُ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلظَّـٰلِمِينَ", "Mathalu allatheena hummiloo alttawrata thumma lam yahmilooha kamathali alhimari yahmilu asfaran bisa mathalu alqawmi allatheena kaththaboo biayati Allahi waAllahu la yahdee alqawma alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (62, 6, "Say, ""O you who are Jews, if you claim that you are allies of Allah, excluding the [other] people, then wish for death, if you should be truthful", "قُلۡ يَـٰٓأَيُّهَا ٱلَّذِينَ هَادُوٓاْ إِن زَعَمۡتُمۡ أَنَّكُمۡ أَوۡلِيَآءُ لِلَّهِ مِن دُونِ ٱلنَّاسِ فَتَمَنَّوُاْ ٱلۡمَوۡتَ إِن كُنتُمۡ صَٰدِقِينَ", "Qul ya ayyuha allatheena hadoo in zaAAamtum annakum awliyao lillahi min dooni alnnasi fatamannawoo almawta in kuntum sadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (62, 7, "But they will not wish for it, ever, because of what their hands have put forth. And Allah is Knowing of the wrongdoers", "وَلَا يَتَمَنَّوۡنَهُۥٓ أَبَدَۢا بِمَا قَدَّمَتۡ أَيۡدِيهِمۡۚ وَٱللَّهُ عَلِيمُۢ بِٱلظَّـٰلِمِينَ", "Wala yatamannawnahu abadan bima qaddamat aydeehim waAllahu AAaleemun bialththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (62, 8, "Say, ""Indeed, the death from which you flee - indeed, it will meet you. Then you will be returned to the Knower of the unseen and the witnessed, and He will inform you about what you used to do", "قُلۡ إِنَّ ٱلۡمَوۡتَ ٱلَّذِي تَفِرُّونَ مِنۡهُ فَإِنَّهُۥ مُلَٰقِيكُمۡۖ ثُمَّ تُرَدُّونَ إِلَىٰ عَٰلِمِ ٱلۡغَيۡبِ وَٱلشَّهَٰدَةِ فَيُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ", "Qul inna almawta allathee tafirroona minhu fainnahu mulaqeekum thumma turaddoona ila AAalimi alghaybi waalshshahadati fayunabbiokum bima kuntum taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (62, 9, "O you who have believed, when [the adhan] is called for the prayer on the day of Jumu'ah [Friday], then proceed to the remembrance of Allah and leave trade. That is better for you, if you only knew", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِذَا نُودِيَ لِلصَّلَوٰةِ مِن يَوۡمِ ٱلۡجُمُعَةِ فَٱسۡعَوۡاْ إِلَىٰ ذِكۡرِ ٱللَّهِ وَذَرُواْ ٱلۡبَيۡعَۚ ذَٰلِكُمۡ خَيۡرٞ لَّكُمۡ إِن كُنتُمۡ تَعۡلَمُونَ", "Ya ayyuha allatheena amanoo itha noodiya lilssalati min yawmi aljumuAAati faisAAaw ila thikri Allahi watharoo albayAAa thalikum khayrun lakum in kuntum taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (62, 10, "And when the prayer has been concluded, disperse within the land and seek from the bounty of Allah, and remember Allah often that you may succeed", "فَإِذَا قُضِيَتِ ٱلصَّلَوٰةُ فَٱنتَشِرُواْ فِي ٱلۡأَرۡضِ وَٱبۡتَغُواْ مِن فَضۡلِ ٱللَّهِ وَٱذۡكُرُواْ ٱللَّهَ كَثِيرٗا لَّعَلَّكُمۡ تُفۡلِحُونَ", "Faitha qudiyati alssalatu faintashiroo fee alardi waibtaghoo min fadli Allahi waothkuroo Allaha katheeran laAAallakum tuflihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (62, 11, "But when they saw a transaction or a diversion, [O Muhammad], they rushed to it and left you standing. Say, ""What is with Allah is better than diversion and than a transaction, and Allah is the best of providers", "وَإِذَا رَأَوۡاْ تِجَٰرَةً أَوۡ لَهۡوًا ٱنفَضُّوٓاْ إِلَيۡهَا وَتَرَكُوكَ قَآئِمٗاۚ قُلۡ مَا عِندَ ٱللَّهِ خَيۡرٞ مِّنَ ٱللَّهۡوِ وَمِنَ ٱلتِّجَٰرَةِۚ وَٱللَّهُ خَيۡرُ ٱلرَّـٰزِقِينَ", "Waitha raaw tijaratan aw lahwan infaddoo ilayha watarakooka qaiman qul ma AAinda Allahi khayrun mina allahwi wamina alttijarati waAllahu khayru alrraziqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (63, 1, "When the hypocrites come to you, [O Muhammad], they say, ""We testify that you are the Messenger of Allah."" And Allah knows that you are His Messenger, and Allah testifies that the hypocrites are liars", "إِذَا جَآءَكَ ٱلۡمُنَٰفِقُونَ قَالُواْ نَشۡهَدُ إِنَّكَ لَرَسُولُ ٱللَّهِۗ وَٱللَّهُ يَعۡلَمُ إِنَّكَ لَرَسُولُهُۥ وَٱللَّهُ يَشۡهَدُ إِنَّ ٱلۡمُنَٰفِقِينَ لَكَٰذِبُونَ", "Itha jaaka almunafiqoona qaloo nashhadu innaka larasoolu Allahi waAllahu yaAAlamu innaka larasooluhu waAllahu yashhadu inna almunafiqeena lakathiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (63, 2, "They have taken their oaths as a cover, so they averted [people] from the way of Allah. Indeed, it was evil that they were doing", "ٱتَّخَذُوٓاْ أَيۡمَٰنَهُمۡ جُنَّةٗ فَصَدُّواْ عَن سَبِيلِ ٱللَّهِۚ إِنَّهُمۡ سَآءَ مَا كَانُواْ يَعۡمَلُونَ", "Ittakhathoo aymanahum junnatan fasaddoo AAan sabeeli Allahi innahum saa ma kanoo yaAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (63, 3, "That is because they believed, and then they disbelieved; so their hearts were sealed over, and they do not understand", "ذَٰلِكَ بِأَنَّهُمۡ ءَامَنُواْ ثُمَّ كَفَرُواْ فَطُبِعَ عَلَىٰ قُلُوبِهِمۡ فَهُمۡ لَا يَفۡقَهُونَ", "Thalika biannahum amanoo thumma kafaroo fatubiAAa AAala quloobihim fahum la yafqahoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (63, 4, "And when you see them, their forms please you, and if they speak, you listen to their speech. [They are] as if they were pieces of wood propped up - they think that every shout is against them. They are the enemy, so beware of them. May Allah destroy them; how are they deluded", "۞وَإِذَا رَأَيۡتَهُمۡ تُعۡجِبُكَ أَجۡسَامُهُمۡۖ وَإِن يَقُولُواْ تَسۡمَعۡ لِقَوۡلِهِمۡۖ كَأَنَّهُمۡ خُشُبٞ مُّسَنَّدَةٞۖ يَحۡسَبُونَ كُلَّ صَيۡحَةٍ عَلَيۡهِمۡۚ هُمُ ٱلۡعَدُوُّ فَٱحۡذَرۡهُمۡۚ قَٰتَلَهُمُ ٱللَّهُۖ أَنَّىٰ يُؤۡفَكُونَ", "Waitha raaytahum tuAAjibuka ajsamuhum wain yaqooloo tasmaAA liqawlihim kaannahum khushubun musannadatun yahsaboona kulla sayhatin AAalayhim humu alAAaduwwu faihtharhum qatalahumu Allahu anna yufakoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (63, 5, "And when it is said to them, ""Come, the Messenger of Allah will ask forgiveness for you,"" they turn their heads aside and you see them evading while they are arrogant", "وَإِذَا قِيلَ لَهُمۡ تَعَالَوۡاْ يَسۡتَغۡفِرۡ لَكُمۡ رَسُولُ ٱللَّهِ لَوَّوۡاْ رُءُوسَهُمۡ وَرَأَيۡتَهُمۡ يَصُدُّونَ وَهُم مُّسۡتَكۡبِرُونَ", "Waitha qeela lahum taAAalaw yastaghfir lakum rasoolu Allahi lawwaw ruoosahum waraaytahum yasuddoona wahum mustakbiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (63, 6, "It is all the same for them whether you ask forgiveness for them or do not ask forgiveness for them; never will Allah forgive them. Indeed, Allah does not guide the defiantly disobedient people", "سَوَآءٌ عَلَيۡهِمۡ أَسۡتَغۡفَرۡتَ لَهُمۡ أَمۡ لَمۡ تَسۡتَغۡفِرۡ لَهُمۡ لَن يَغۡفِرَ ٱللَّهُ لَهُمۡۚ إِنَّ ٱللَّهَ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلۡفَٰسِقِينَ", "Sawaon AAalayhim astaghfarta lahum am lam tastaghfir lahum lan yaghfira Allahu lahum inna Allaha la yahdee alqawma alfasiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (63, 7, "They are the ones who say, ""Do not spend on those who are with the Messenger of Allah until they disband."" And to Allah belongs the depositories of the heavens and the earth, but the hypocrites do not understand", "هُمُ ٱلَّذِينَ يَقُولُونَ لَا تُنفِقُواْ عَلَىٰ مَنۡ عِندَ رَسُولِ ٱللَّهِ حَتَّىٰ يَنفَضُّواْۗ وَلِلَّهِ خَزَآئِنُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَلَٰكِنَّ ٱلۡمُنَٰفِقِينَ لَا يَفۡقَهُونَ", "Humu allatheena yaqooloona la tunfiqoo AAala man AAinda rasooli Allahi hatta yanfaddoo walillahi khazainu alssamawati waalardi walakinna almunafiqeena la yafqahoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (63, 8, "They say, ""If we return to al-Madinah, the more honored [for power] will surely expel therefrom the more humble."" And to Allah belongs [all] honor, and to His Messenger, and to the believers, but the hypocrites do not know", "يَقُولُونَ لَئِن رَّجَعۡنَآ إِلَى ٱلۡمَدِينَةِ لَيُخۡرِجَنَّ ٱلۡأَعَزُّ مِنۡهَا ٱلۡأَذَلَّۚ وَلِلَّهِ ٱلۡعِزَّةُ وَلِرَسُولِهِۦ وَلِلۡمُؤۡمِنِينَ وَلَٰكِنَّ ٱلۡمُنَٰفِقِينَ لَا يَعۡلَمُونَ", "Yaqooloona lain rajaAAna ila almadeenati layukhrijanna alaAAazzu minha alathalla walillahi alAAizzatu walirasoolihi walilmumineena walakinna almunafiqeena la yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (63, 9, "O you who have believed, let not your wealth and your children divert you from remembrance of Allah. And whoever does that - then those are the losers", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تُلۡهِكُمۡ أَمۡوَٰلُكُمۡ وَلَآ أَوۡلَٰدُكُمۡ عَن ذِكۡرِ ٱللَّهِۚ وَمَن يَفۡعَلۡ ذَٰلِكَ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡخَٰسِرُونَ", "Ya ayyuha allatheena amanoo la tulhikum amwalukum wala awladukum AAan thikri Allahi waman yafAAal thalika faolaika humu alkhasiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (63, 10, "And spend [in the way of Allah] from what We have provided you before death approaches one of you and he says, ""My Lord, if only You would delay me for a brief term so I would give charity and be among the righteous", "وَأَنفِقُواْ مِن مَّا رَزَقۡنَٰكُم مِّن قَبۡلِ أَن يَأۡتِيَ أَحَدَكُمُ ٱلۡمَوۡتُ فَيَقُولَ رَبِّ لَوۡلَآ أَخَّرۡتَنِيٓ إِلَىٰٓ أَجَلٖ قَرِيبٖ فَأَصَّدَّقَ وَأَكُن مِّنَ ٱلصَّـٰلِحِينَ", "Waanfiqoo min ma razaqnakum min qabli an yatiya ahadakumu almawtu fayaqoola rabbi lawla akhkhartanee ila ajalin qareebin faassaddaqa waakun mina alssaliheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (63, 11, "But never will Allah delay a soul when its time has come. And Allah is Acquainted with what you do", "وَلَن يُؤَخِّرَ ٱللَّهُ نَفۡسًا إِذَا جَآءَ أَجَلُهَاۚ وَٱللَّهُ خَبِيرُۢ بِمَا تَعۡمَلُونَ", "Walan yuakhkhira Allahu nafsan itha jaa ajaluha waAllahu khabeerun bima taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (64, 1, "Whatever is in the heavens and whatever is on the earth is exalting Allah. To Him belongs dominion, and to Him belongs [all] praise, and He is over all things competent", "يُسَبِّحُ لِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۖ لَهُ ٱلۡمُلۡكُ وَلَهُ ٱلۡحَمۡدُۖ وَهُوَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ", "Yusabbihu lillahi ma fee alssamawati wama fee alardi lahu almulku walahu alhamdu wahuwa AAala kulli shayin qadeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (64, 2, "It is He who created you, and among you is the disbeliever, and among you is the believer. And Allah, of what you do, is Seeing", "هُوَ ٱلَّذِي خَلَقَكُمۡ فَمِنكُمۡ كَافِرٞ وَمِنكُم مُّؤۡمِنٞۚ وَٱللَّهُ بِمَا تَعۡمَلُونَ بَصِيرٌ", "Huwa allathee khalaqakum faminkum kafirun waminkum muminun waAllahu bima taAAmaloona baseerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (64, 3, "He created the heavens and earth in truth and formed you and perfected your forms; and to Him is the [final] destination", "خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ بِٱلۡحَقِّ وَصَوَّرَكُمۡ فَأَحۡسَنَ صُوَرَكُمۡۖ وَإِلَيۡهِ ٱلۡمَصِيرُ", "Khalaqa alssamawati waalarda bialhaqqi wasawwarakum faahsana suwarakum wailayhi almaseeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (64, 4, "He knows what is within the heavens and earth and knows what you conceal and what you declare. And Allah is Knowing of that within the breasts", "يَعۡلَمُ مَا فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَيَعۡلَمُ مَا تُسِرُّونَ وَمَا تُعۡلِنُونَۚ وَٱللَّهُ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ", "YaAAlamu ma fee alssamawati waalardi wayaAAlamu ma tusirroona wama tuAAlinoona waAllahu AAaleemun bithati alssudoori");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (64, 5, "Has there not come to you the news of those who disbelieved before? So they tasted the bad consequence of their affair, and they will have a painful punishment", "أَلَمۡ يَأۡتِكُمۡ نَبَؤُاْ ٱلَّذِينَ كَفَرُواْ مِن قَبۡلُ فَذَاقُواْ وَبَالَ أَمۡرِهِمۡ وَلَهُمۡ عَذَابٌ أَلِيمٞ", "Alam yatikum nabao allatheena kafaroo min qablu fathaqoo wabala amrihim walahum AAathabun aleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (64, 6, "That is because their messengers used to come to them with clear evidences, but they said, ""Shall human beings guide us?"" and disbelieved and turned away. And Allah dispensed [with them]; and Allah is Free of need and Praiseworthy", "ذَٰلِكَ بِأَنَّهُۥ كَانَت تَّأۡتِيهِمۡ رُسُلُهُم بِٱلۡبَيِّنَٰتِ فَقَالُوٓاْ أَبَشَرٞ يَهۡدُونَنَا فَكَفَرُواْ وَتَوَلَّواْۖ وَّٱسۡتَغۡنَى ٱللَّهُۚ وَٱللَّهُ غَنِيٌّ حَمِيدٞ", "Thalika biannahu kanat tateehim rusuluhum bialbayyinati faqaloo abasharun yahdoonana fakafaroo watawallaw waistaghna Allahu waAllahu ghaniyyun hameedun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (64, 7, "Those who disbelieve have claimed that they will never be resurrected. Say, ""Yes, by my Lord, you will surely be resurrected; then you will surely be informed of what you did. And that, for Allah, is easy", "زَعَمَ ٱلَّذِينَ كَفَرُوٓاْ أَن لَّن يُبۡعَثُواْۚ قُلۡ بَلَىٰ وَرَبِّي لَتُبۡعَثُنَّ ثُمَّ لَتُنَبَّؤُنَّ بِمَا عَمِلۡتُمۡۚ وَذَٰلِكَ عَلَى ٱللَّهِ يَسِيرٞ", "ZaAAama allatheena kafaroo an lan yubAAathoo qul bala warabbee latubAAathunna thumma latunabbaonna bima AAamiltum wathalika AAala Allahi yaseerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (64, 8, "So believe in Allah and His Messenger and the Qur'an which We have sent down. And Allah is Acquainted with what you do", "فَـَٔامِنُواْ بِٱللَّهِ وَرَسُولِهِۦ وَٱلنُّورِ ٱلَّذِيٓ أَنزَلۡنَاۚ وَٱللَّهُ بِمَا تَعۡمَلُونَ خَبِيرٞ", "Faaminoo biAllahi warasoolihi waalnnoori allathee anzalna waAllahu bima taAAmaloona khabeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (64, 9, "The Day He will assemble you for the Day of Assembly - that is the Day of Deprivation. And whoever believes in Allah and does righteousness - He will remove from him his misdeeds and admit him to gardens beneath which rivers flow, wherein they will abide forever. That is the great attainment", "يَوۡمَ يَجۡمَعُكُمۡ لِيَوۡمِ ٱلۡجَمۡعِۖ ذَٰلِكَ يَوۡمُ ٱلتَّغَابُنِۗ وَمَن يُؤۡمِنۢ بِٱللَّهِ وَيَعۡمَلۡ صَٰلِحٗا يُكَفِّرۡ عَنۡهُ سَيِّـَٔاتِهِۦ وَيُدۡخِلۡهُ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَآ أَبَدٗاۚ ذَٰلِكَ ٱلۡفَوۡزُ ٱلۡعَظِيمُ", "Yawma yajmaAAukum liyawmi aljamAAi thalika yawmu alttaghabuni waman yumin biAllahi wayaAAmal salihan yukaffir AAanhu sayyiatihi wayudkhilhu jannatin tajree min tahtiha alanharu khalideena feeha abadan thalika alfawzu alAAatheemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (64, 10, "But the ones who disbelieved and denied Our verses - those are the companions of the Fire, abiding eternally therein; and wretched is the destination", "وَٱلَّذِينَ كَفَرُواْ وَكَذَّبُواْ بِـَٔايَٰتِنَآ أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلنَّارِ خَٰلِدِينَ فِيهَاۖ وَبِئۡسَ ٱلۡمَصِيرُ", "Waallatheena kafaroo wakaththaboo biayatina olaika ashabu alnnari khalideena feeha wabisa almaseeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (64, 11, "No disaster strikes except by permission of Allah. And whoever believes in Allah - He will guide his heart. And Allah is Knowing of all things", "مَآ أَصَابَ مِن مُّصِيبَةٍ إِلَّا بِإِذۡنِ ٱللَّهِۗ وَمَن يُؤۡمِنۢ بِٱللَّهِ يَهۡدِ قَلۡبَهُۥۚ وَٱللَّهُ بِكُلِّ شَيۡءٍ عَلِيمٞ", "Ma asaba min museebatin illa biithni Allahi waman yumin biAllahi yahdi qalbahu waAllahu bikulli shayin AAaleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (64, 12, "And obey Allah and obey the Messenger; but if you turn away - then upon Our Messenger is only [the duty of] clear notification", "وَأَطِيعُواْ ٱللَّهَ وَأَطِيعُواْ ٱلرَّسُولَۚ فَإِن تَوَلَّيۡتُمۡ فَإِنَّمَا عَلَىٰ رَسُولِنَا ٱلۡبَلَٰغُ ٱلۡمُبِينُ", "WaateeAAoo Allaha waateeAAoo alrrasoola fain tawallaytum fainnama AAala rasoolina albalaghu almubeenu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (64, 13, "Allah - there is no deity except Him. And upon Allah let the believers rely", "ٱللَّهُ لَآ إِلَٰهَ إِلَّا هُوَۚ وَعَلَى ٱللَّهِ فَلۡيَتَوَكَّلِ ٱلۡمُؤۡمِنُونَ", "Allahu la ilaha illa huwa waAAala Allahi falyatawakkali almuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (64, 14, "O you who have believed, indeed, among your wives and your children are enemies to you, so beware of them. But if you pardon and overlook and forgive - then indeed, Allah is Forgiving and Merciful", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِنَّ مِنۡ أَزۡوَٰجِكُمۡ وَأَوۡلَٰدِكُمۡ عَدُوّٗا لَّكُمۡ فَٱحۡذَرُوهُمۡۚ وَإِن تَعۡفُواْ وَتَصۡفَحُواْ وَتَغۡفِرُواْ فَإِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٌ", "Ya ayyuha allatheena amanoo inna min azwajikum waawladikum AAaduwwan lakum faihtharoohum wain taAAfoo watasfahoo wataghfiroo fainna Allaha ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (64, 15, "Your wealth and your children are but a trial, and Allah has with Him a great reward", "إِنَّمَآ أَمۡوَٰلُكُمۡ وَأَوۡلَٰدُكُمۡ فِتۡنَةٞۚ وَٱللَّهُ عِندَهُۥٓ أَجۡرٌ عَظِيمٞ", "Innama amwalukum waawladukum fitnatun waAllahu AAindahu ajrun AAatheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (64, 16, "So fear Allah as much as you are able and listen and obey and spend [in the way of Allah]; it is better for your selves. And whoever is protected from the stinginess of his soul - it is those who will be the successful", "فَٱتَّقُواْ ٱللَّهَ مَا ٱسۡتَطَعۡتُمۡ وَٱسۡمَعُواْ وَأَطِيعُواْ وَأَنفِقُواْ خَيۡرٗا لِّأَنفُسِكُمۡۗ وَمَن يُوقَ شُحَّ نَفۡسِهِۦ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡمُفۡلِحُونَ", "Faittaqoo Allaha ma istataAAtum waismaAAoo waateeAAoo waanfiqoo khayran lianfusikum waman yooqa shuhha nafsihi faolaika humu almuflihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (64, 17, "If you loan Allah a goodly loan, He will multiply it for you and forgive you. And Allah is Most Appreciative and Forbearing", "إِن تُقۡرِضُواْ ٱللَّهَ قَرۡضًا حَسَنٗا يُضَٰعِفۡهُ لَكُمۡ وَيَغۡفِرۡ لَكُمۡۚ وَٱللَّهُ شَكُورٌ حَلِيمٌ", "In tuqridoo Allaha qardan hasanan yudaAAifhu lakum wayaghfir lakum waAllahu shakoorun haleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (64, 18, "Knower of the unseen and the witnessed, the Exalted in Might, the Wise", "عَٰلِمُ ٱلۡغَيۡبِ وَٱلشَّهَٰدَةِ ٱلۡعَزِيزُ ٱلۡحَكِيمُ", "AAalimu alghaybi waalshshahadati alAAazeezu alhakeemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (65, 1, "O Prophet, when you [Muslims] divorce women, divorce them for [the commencement of] their waiting period and keep count of the waiting period, and fear Allah, your Lord. Do not turn them out of their [husbands'] houses, nor should they [themselves] leave [during that period] unless they are committing a clear immorality. And those are the limits [set by] Allah. And whoever transgresses the limits of Allah has certainly wronged himself. You know not; perhaps Allah will bring about after that a [different] matter", "يَـٰٓأَيُّهَا ٱلنَّبِيُّ إِذَا طَلَّقۡتُمُ ٱلنِّسَآءَ فَطَلِّقُوهُنَّ لِعِدَّتِهِنَّ وَأَحۡصُواْ ٱلۡعِدَّةَۖ وَٱتَّقُواْ ٱللَّهَ رَبَّكُمۡۖ لَا تُخۡرِجُوهُنَّ مِنۢ بُيُوتِهِنَّ وَلَا يَخۡرُجۡنَ إِلَّآ أَن يَأۡتِينَ بِفَٰحِشَةٖ مُّبَيِّنَةٖۚ وَتِلۡكَ حُدُودُ ٱللَّهِۚ وَمَن يَتَعَدَّ حُدُودَ ٱللَّهِ فَقَدۡ ظَلَمَ نَفۡسَهُۥۚ لَا تَدۡرِي لَعَلَّ ٱللَّهَ يُحۡدِثُ بَعۡدَ ذَٰلِكَ أَمۡرٗا", "Ya ayyuha alnnabiyyu itha tallaqtumu alnnisaa fatalliqoohunna liAAiddatihinna waahsoo alAAiddata waittaqoo Allaha rabbakum la tukhrijoohunna min buyootihinna wala yakhrujna illa an yateena bifahishatin mubayyinatin watilka hudoodu Allahi waman yataAAadda hudooda Allahi faqad thalama nafsahu la tadree laAAalla Allaha yuhdithu baAAda thalika amran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (65, 2, "And when they have [nearly] fulfilled their term, either retain them according to acceptable terms or part with them according to acceptable terms. And bring to witness two just men from among you and establish the testimony for [the acceptance of] Allah. That is instructed to whoever should believe in Allah and the Last day. And whoever fears Allah - He will make for him a way out", "فَإِذَا بَلَغۡنَ أَجَلَهُنَّ فَأَمۡسِكُوهُنَّ بِمَعۡرُوفٍ أَوۡ فَارِقُوهُنَّ بِمَعۡرُوفٖ وَأَشۡهِدُواْ ذَوَيۡ عَدۡلٖ مِّنكُمۡ وَأَقِيمُواْ ٱلشَّهَٰدَةَ لِلَّهِۚ ذَٰلِكُمۡ يُوعَظُ بِهِۦ مَن كَانَ يُؤۡمِنُ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِۚ وَمَن يَتَّقِ ٱللَّهَ يَجۡعَل لَّهُۥ مَخۡرَجٗا", "Faitha balaghna ajalahunna faamsikoohunna bimaAAroofin aw fariqoohunna bimaAAroofin waashhidoo thaway AAadlin minkum waaqeemoo alshshahadata lillahi thalikum yooAAathu bihi man kana yuminu biAllahi waalyawmi alakhiri waman yattaqi Allaha yajAAal lahu makhrajan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (65, 3, "And will provide for him from where he does not expect. And whoever relies upon Allah - then He is sufficient for him. Indeed, Allah will accomplish His purpose. Allah has already set for everything a [decreed] extent", "وَيَرۡزُقۡهُ مِنۡ حَيۡثُ لَا يَحۡتَسِبُۚ وَمَن يَتَوَكَّلۡ عَلَى ٱللَّهِ فَهُوَ حَسۡبُهُۥٓۚ إِنَّ ٱللَّهَ بَٰلِغُ أَمۡرِهِۦۚ قَدۡ جَعَلَ ٱللَّهُ لِكُلِّ شَيۡءٖ قَدۡرٗا", "Wayarzuqhu min haythu la yahtasibu waman yatawakkal AAala Allahi fahuwa hasbuhu inna Allaha balighu amrihi qad jaAAala Allahu likulli shayin qadran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (65, 4, "And those who no longer expect menstruation among your women - if you doubt, then their period is three months, and [also for] those who have not menstruated. And for those who are pregnant, their term is until they give birth. And whoever fears Allah - He will make for him of his matter ease", "وَٱلَّـٰٓـِٔي يَئِسۡنَ مِنَ ٱلۡمَحِيضِ مِن نِّسَآئِكُمۡ إِنِ ٱرۡتَبۡتُمۡ فَعِدَّتُهُنَّ ثَلَٰثَةُ أَشۡهُرٖ وَٱلَّـٰٓـِٔي لَمۡ يَحِضۡنَۚ وَأُوْلَٰتُ ٱلۡأَحۡمَالِ أَجَلُهُنَّ أَن يَضَعۡنَ حَمۡلَهُنَّۚ وَمَن يَتَّقِ ٱللَّهَ يَجۡعَل لَّهُۥ مِنۡ أَمۡرِهِۦ يُسۡرٗا", "Waallaee yaisna mina almaheedi min nisaikum ini irtabtum faAAiddatuhunna thalathatu ashhurin waallaee lam yahidna waolatu alahmali ajaluhunna an yadaAAna hamlahunna waman yattaqi Allaha yajAAal lahu min amrihi yusran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (65, 5, "That is the command of Allah, which He has sent down to you; and whoever fears Allah - He will remove for him his misdeeds and make great for him his reward", "ذَٰلِكَ أَمۡرُ ٱللَّهِ أَنزَلَهُۥٓ إِلَيۡكُمۡۚ وَمَن يَتَّقِ ٱللَّهَ يُكَفِّرۡ عَنۡهُ سَيِّـَٔاتِهِۦ وَيُعۡظِمۡ لَهُۥٓ أَجۡرًا", "Thalika amru Allahi anzalahu ilaykum waman yattaqi Allaha yukaffir AAanhu sayyiatihi wayuAAthim lahu ajran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (65, 6, "Lodge them [in a section] of where you dwell out of your means and do not harm them in order to oppress them. And if they should be pregnant, then spend on them until they give birth. And if they breastfeed for you, then give them their payment and confer among yourselves in the acceptable way; but if you are in discord, then there may breastfeed for the father another woman", "أَسۡكِنُوهُنَّ مِنۡ حَيۡثُ سَكَنتُم مِّن وُجۡدِكُمۡ وَلَا تُضَآرُّوهُنَّ لِتُضَيِّقُواْ عَلَيۡهِنَّۚ وَإِن كُنَّ أُوْلَٰتِ حَمۡلٖ فَأَنفِقُواْ عَلَيۡهِنَّ حَتَّىٰ يَضَعۡنَ حَمۡلَهُنَّۚ فَإِنۡ أَرۡضَعۡنَ لَكُمۡ فَـَٔاتُوهُنَّ أُجُورَهُنَّ وَأۡتَمِرُواْ بَيۡنَكُم بِمَعۡرُوفٖۖ وَإِن تَعَاسَرۡتُمۡ فَسَتُرۡضِعُ لَهُۥٓ أُخۡرَىٰ", "Askinoohunna min haythu sakantum min wujdikum wala tudarroohunna litudayyiqoo AAalayhinna wain kunna olati hamlin faanfiqoo AAalayhinna hatta yadaAAna hamlahunna fain ardaAAna lakum faatoohunna ojoorahunna watamiroo baynakum bimaAAroofin wain taAAasartum fasaturdiAAu lahu okhra");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (65, 7, "Let a man of wealth spend from his wealth, and he whose provision is restricted - let him spend from what Allah has given him. Allah does not charge a soul except [according to] what He has given it. Allah will bring about, after hardship, ease", "لِيُنفِقۡ ذُو سَعَةٖ مِّن سَعَتِهِۦۖ وَمَن قُدِرَ عَلَيۡهِ رِزۡقُهُۥ فَلۡيُنفِقۡ مِمَّآ ءَاتَىٰهُ ٱللَّهُۚ لَا يُكَلِّفُ ٱللَّهُ نَفۡسًا إِلَّا مَآ ءَاتَىٰهَاۚ سَيَجۡعَلُ ٱللَّهُ بَعۡدَ عُسۡرٖ يُسۡرٗا", "Liyunfiq thoo saAAatin min saAAatihi waman qudira AAalayhi rizquhu falyunfiq mimma atahu Allahu la yukallifu Allahu nafsan illa ma ataha sayajAAalu Allahu baAAda AAusrin yusran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (65, 8, "And how many a city was insolent toward the command of its Lord and His messengers, so We took it to severe account and punished it with a terrible punishment", "وَكَأَيِّن مِّن قَرۡيَةٍ عَتَتۡ عَنۡ أَمۡرِ رَبِّهَا وَرُسُلِهِۦ فَحَاسَبۡنَٰهَا حِسَابٗا شَدِيدٗا وَعَذَّبۡنَٰهَا عَذَابٗا نُّكۡرٗا", "Wakaayyin min qaryatin AAatat AAan amri rabbiha warusulihi fahasabnaha hisaban shadeedan waAAaththabnaha AAathaban nukran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (65, 9, "And it tasted the bad consequence of its affair, and the outcome of its affair was loss", "فَذَاقَتۡ وَبَالَ أَمۡرِهَا وَكَانَ عَٰقِبَةُ أَمۡرِهَا خُسۡرًا", "Fathaqat wabala amriha wakana AAaqibatu amriha khusran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (65, 10, "Allah has prepared for them a severe punishment; so fear Allah, O you of understanding who have believed. Allah has sent down to you the Qur'an", "أَعَدَّ ٱللَّهُ لَهُمۡ عَذَابٗا شَدِيدٗاۖ فَٱتَّقُواْ ٱللَّهَ يَـٰٓأُوْلِي ٱلۡأَلۡبَٰبِ ٱلَّذِينَ ءَامَنُواْۚ قَدۡ أَنزَلَ ٱللَّهُ إِلَيۡكُمۡ ذِكۡرٗا", "aAAadda Allahu lahum AAathaban shadeedan faittaqoo Allaha ya olee alalbabi allatheena amanoo qad anzala Allahu ilaykum thikran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (65, 11, "[He sent] a Messenger [Muhammad] reciting to you the distinct verses of Allah that He may bring out those who believe and do righteous deeds from darknesses into the light. And whoever believes in Allah and does righteousness - He will admit him into gardens beneath which rivers flow to abide therein forever. Allah will have perfected for him a provision", "رَّسُولٗا يَتۡلُواْ عَلَيۡكُمۡ ءَايَٰتِ ٱللَّهِ مُبَيِّنَٰتٖ لِّيُخۡرِجَ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ مِنَ ٱلظُّلُمَٰتِ إِلَى ٱلنُّورِۚ وَمَن يُؤۡمِنۢ بِٱللَّهِ وَيَعۡمَلۡ صَٰلِحٗا يُدۡخِلۡهُ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَآ أَبَدٗاۖ قَدۡ أَحۡسَنَ ٱللَّهُ لَهُۥ رِزۡقًا", "Rasoolan yatloo AAalaykum ayati Allahi mubayyinatin liyukhrija allatheena amanoo waAAamiloo alssalihati mina alththulumati ila alnnoori waman yumin biAllahi wayaAAmal salihan yudkhilhu jannatin tajree min tahtiha alanharu khalideena feeha abadan qad ahsana Allahu lahu rizqan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (65, 12, "It is Allah who has created seven heavens and of the earth, the like of them. [His] command descends among them so you may know that Allah is over all things competent and that Allah has encompassed all things in knowledge", "ٱللَّهُ ٱلَّذِي خَلَقَ سَبۡعَ سَمَٰوَٰتٖ وَمِنَ ٱلۡأَرۡضِ مِثۡلَهُنَّۖ يَتَنَزَّلُ ٱلۡأَمۡرُ بَيۡنَهُنَّ لِتَعۡلَمُوٓاْ أَنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ وَأَنَّ ٱللَّهَ قَدۡ أَحَاطَ بِكُلِّ شَيۡءٍ عِلۡمَۢا", "Allahu allathee khalaqa sabAAa samawatin wamina alardi mithlahunna yatanazzalu alamru baynahunna litaAAlamoo anna Allaha AAala kulli shayin qadeerun waanna Allaha qad ahata bikulli shayin AAilman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (66, 1, "O Prophet, why do you prohibit [yourself from] what Allah has made lawful for you, seeking the approval of your wives? And Allah is Forgiving and Merciful", "يَـٰٓأَيُّهَا ٱلنَّبِيُّ لِمَ تُحَرِّمُ مَآ أَحَلَّ ٱللَّهُ لَكَۖ تَبۡتَغِي مَرۡضَاتَ أَزۡوَٰجِكَۚ وَٱللَّهُ غَفُورٞ رَّحِيمٞ", "Ya ayyuha alnnabiyyu lima tuharrimu ma ahalla Allahu laka tabtaghee mardata azwajika waAllahu ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (66, 2, "Allah has already ordained for you [Muslims] the dissolution of your oaths. And Allah is your protector, and He is the Knowing, the Wise", "قَدۡ فَرَضَ ٱللَّهُ لَكُمۡ تَحِلَّةَ أَيۡمَٰنِكُمۡۚ وَٱللَّهُ مَوۡلَىٰكُمۡۖ وَهُوَ ٱلۡعَلِيمُ ٱلۡحَكِيمُ", "Qad farada Allahu lakum tahillata aymanikum waAllahu mawlakum wahuwa alAAaleemu alhakeemu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (66, 3, "And [remember] when the Prophet confided to one of his wives a statement; and when she informed [another] of it and Allah showed it to him, he made known part of it and ignored a part. And when he informed her about it, she said, ""Who told you this?"" He said, ""I was informed by the Knowing, the Acquainted", "وَإِذۡ أَسَرَّ ٱلنَّبِيُّ إِلَىٰ بَعۡضِ أَزۡوَٰجِهِۦ حَدِيثٗا فَلَمَّا نَبَّأَتۡ بِهِۦ وَأَظۡهَرَهُ ٱللَّهُ عَلَيۡهِ عَرَّفَ بَعۡضَهُۥ وَأَعۡرَضَ عَنۢ بَعۡضٖۖ فَلَمَّا نَبَّأَهَا بِهِۦ قَالَتۡ مَنۡ أَنۢبَأَكَ هَٰذَاۖ قَالَ نَبَّأَنِيَ ٱلۡعَلِيمُ ٱلۡخَبِيرُ", "Waith asarra alnnabiyyu ila baAAdi azwajihi hadeethan falamma nabbaat bihi waathharahu Allahu AAalayhi AAarrafa baAAdahu waaAArada AAan baAAdin falamma nabbaaha bihi qalat man anbaaka hatha qala nabbaaniya alAAaleemu alkhabeeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (66, 4, "If you two [wives] repent to Allah, [it is best], for your hearts have deviated. But if you cooperate against him - then indeed Allah is his protector, and Gabriel and the righteous of the believers and the angels, moreover, are [his] assistants", "إِن تَتُوبَآ إِلَى ٱللَّهِ فَقَدۡ صَغَتۡ قُلُوبُكُمَاۖ وَإِن تَظَٰهَرَا عَلَيۡهِ فَإِنَّ ٱللَّهَ هُوَ مَوۡلَىٰهُ وَجِبۡرِيلُ وَصَٰلِحُ ٱلۡمُؤۡمِنِينَۖ وَٱلۡمَلَـٰٓئِكَةُ بَعۡدَ ذَٰلِكَ ظَهِيرٌ", "In tatooba ila Allahi faqad saghat quloobukuma wain tathahara AAalayhi fainna Allaha huwa mawlahu wajibreelu wasalihu almumineena waalmalaikatu baAAda thalika thaheerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (66, 5, "Perhaps his Lord, if he divorced you [all], would substitute for him wives better than you - submitting [to Allah], believing, devoutly obedient, repentant, worshipping, and traveling - [ones] previously married and virgins", "عَسَىٰ رَبُّهُۥٓ إِن طَلَّقَكُنَّ أَن يُبۡدِلَهُۥٓ أَزۡوَٰجًا خَيۡرٗا مِّنكُنَّ مُسۡلِمَٰتٖ مُّؤۡمِنَٰتٖ قَٰنِتَٰتٖ تَـٰٓئِبَٰتٍ عَٰبِدَٰتٖ سَـٰٓئِحَٰتٖ ثَيِّبَٰتٖ وَأَبۡكَارٗا", "AAasa rabbuhu in tallaqakunna an yubdilahu azwajan khayran minkunna muslimatin muminatin qanitatin taibatin AAabidatin saihatin thayyibatin waabkaran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (66, 6, "O you who have believed, protect yourselves and your families from a Fire whose fuel is people and stones, over which are [appointed] angels, harsh and severe; they do not disobey Allah in what He commands them but do what they are commanded", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ قُوٓاْ أَنفُسَكُمۡ وَأَهۡلِيكُمۡ نَارٗا وَقُودُهَا ٱلنَّاسُ وَٱلۡحِجَارَةُ عَلَيۡهَا مَلَـٰٓئِكَةٌ غِلَاظٞ شِدَادٞ لَّا يَعۡصُونَ ٱللَّهَ مَآ أَمَرَهُمۡ وَيَفۡعَلُونَ مَا يُؤۡمَرُونَ", "Ya ayyuha allatheena amanoo qoo anfusakum waahleekum naran waqooduha alnnasu waalhijaratu AAalayha malaikatun ghilathun shidadun la yaAAsoona Allaha ma amarahum wayafAAaloona ma yumaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (66, 7, "O you who have disbelieved, make no excuses that Day. You will only be recompensed for what you used to do", "يَـٰٓأَيُّهَا ٱلَّذِينَ كَفَرُواْ لَا تَعۡتَذِرُواْ ٱلۡيَوۡمَۖ إِنَّمَا تُجۡزَوۡنَ مَا كُنتُمۡ تَعۡمَلُونَ", "Ya ayyuha allatheena kafaroo la taAAtathiroo alyawma innama tujzawna ma kuntum taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (66, 8, "O you who have believed, repent to Allah with sincere repentance. Perhaps your Lord will remove from you your misdeeds and admit you into gardens beneath which rivers flow [on] the Day when Allah will not disgrace the Prophet and those who believed with him. Their light will proceed before them and on their right; they will say, ""Our Lord, perfect for us our light and forgive us. Indeed, You are over all things competent", "يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ تُوبُوٓاْ إِلَى ٱللَّهِ تَوۡبَةٗ نَّصُوحًا عَسَىٰ رَبُّكُمۡ أَن يُكَفِّرَ عَنكُمۡ سَيِّـَٔاتِكُمۡ وَيُدۡخِلَكُمۡ جَنَّـٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ يَوۡمَ لَا يُخۡزِي ٱللَّهُ ٱلنَّبِيَّ وَٱلَّذِينَ ءَامَنُواْ مَعَهُۥۖ نُورُهُمۡ يَسۡعَىٰ بَيۡنَ أَيۡدِيهِمۡ وَبِأَيۡمَٰنِهِمۡ يَقُولُونَ رَبَّنَآ أَتۡمِمۡ لَنَا نُورَنَا وَٱغۡفِرۡ لَنَآۖ إِنَّكَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ", "Ya ayyuha allatheena amanoo tooboo ila Allahi tawbatan nasoohan AAasa rabbukum an yukaffira AAankum sayyiatikum wayudkhilakum jannatin tajree min tahtiha alanharu yawma la yukhzee Allahu alnnabiyya waallatheena amanoo maAAahu nooruhum yasAAa bayna aydeehim wabiaymanihim yaqooloona rabbana atmim lana noorana waighfir lana innaka AAala kulli shayin qadeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (66, 9, "O Prophet, strive against the disbelievers and the hypocrites and be harsh upon them. And their refuge is Hell, and wretched is the destination", "يَـٰٓأَيُّهَا ٱلنَّبِيُّ جَٰهِدِ ٱلۡكُفَّارَ وَٱلۡمُنَٰفِقِينَ وَٱغۡلُظۡ عَلَيۡهِمۡۚ وَمَأۡوَىٰهُمۡ جَهَنَّمُۖ وَبِئۡسَ ٱلۡمَصِيرُ", "Ya ayyuha alnnabiyyu jahidi alkuffara waalmunafiqeena waoghluth AAalayhim wamawahum jahannamu wabisa almaseeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (66, 10, "Allah presents an example of those who disbelieved: the wife of Noah and the wife of Lot. They were under two of Our righteous servants but betrayed them, so those prophets did not avail them from Allah at all, and it was said, ""Enter the Fire with those who enter", "ضَرَبَ ٱللَّهُ مَثَلٗا لِّلَّذِينَ كَفَرُواْ ٱمۡرَأَتَ نُوحٖ وَٱمۡرَأَتَ لُوطٖۖ كَانَتَا تَحۡتَ عَبۡدَيۡنِ مِنۡ عِبَادِنَا صَٰلِحَيۡنِ فَخَانَتَاهُمَا فَلَمۡ يُغۡنِيَا عَنۡهُمَا مِنَ ٱللَّهِ شَيۡـٔٗا وَقِيلَ ٱدۡخُلَا ٱلنَّارَ مَعَ ٱلدَّـٰخِلِينَ", "Daraba Allahu mathalan lillatheena kafaroo imraata noohin waimraata lootin kanata tahta AAabdayni min AAibadina salihayni fakhanatahuma falam yughniya AAanhuma mina Allahi shayan waqeela odkhula alnnara maAAa alddakhileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (66, 11, "And Allah presents an example of those who believed: the wife of Pharaoh, when she said, ""My Lord, build for me near You a house in Paradise and save me from Pharaoh and his deeds and save me from the wrongdoing people", "وَضَرَبَ ٱللَّهُ مَثَلٗا لِّلَّذِينَ ءَامَنُواْ ٱمۡرَأَتَ فِرۡعَوۡنَ إِذۡ قَالَتۡ رَبِّ ٱبۡنِ لِي عِندَكَ بَيۡتٗا فِي ٱلۡجَنَّةِ وَنَجِّنِي مِن فِرۡعَوۡنَ وَعَمَلِهِۦ وَنَجِّنِي مِنَ ٱلۡقَوۡمِ ٱلظَّـٰلِمِينَ", "Wadaraba Allahu mathalan lillatheena amanoo imraata firAAawna ith qalat rabbi ibni lee AAindaka baytan fee aljannati wanajjinee min firAAawna waAAamalihi wanajjinee mina alqawmi alththalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (66, 12, "And [the example of] Mary, the daughter of 'Imran, who guarded her chastity, so We blew into [her garment] through Our angel, and she believed in the words of her Lord and His scriptures and was of the devoutly obedient", "وَمَرۡيَمَ ٱبۡنَتَ عِمۡرَٰنَ ٱلَّتِيٓ أَحۡصَنَتۡ فَرۡجَهَا فَنَفَخۡنَا فِيهِ مِن رُّوحِنَا وَصَدَّقَتۡ بِكَلِمَٰتِ رَبِّهَا وَكُتُبِهِۦ وَكَانَتۡ مِنَ ٱلۡقَٰنِتِينَ", "Wamaryama ibnata AAimrana allatee ahsanat farjaha fanafakhna feehi min roohina wasaddaqat bikalimati rabbiha wakutubihi wakanat mina alqaniteena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (67, 1, "Blessed is He in whose hand is dominion, and He is over all things competent", "تَبَٰرَكَ ٱلَّذِي بِيَدِهِ ٱلۡمُلۡكُ وَهُوَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ", "Tabaraka allathee biyadihi almulku wahuwa AAala kulli shayin qadeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (67, 2, "[He] who created death and life to test you [as to] which of you is best in deed - and He is the Exalted in Might, the Forgiving", "ٱلَّذِي خَلَقَ ٱلۡمَوۡتَ وَٱلۡحَيَوٰةَ لِيَبۡلُوَكُمۡ أَيُّكُمۡ أَحۡسَنُ عَمَلٗاۚ وَهُوَ ٱلۡعَزِيزُ ٱلۡغَفُورُ", "Allathee khalaqa almawta waalhayata liyabluwakum ayyukum ahsanu AAamalan wahuwa alAAazeezu alghafooru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (67, 3, "[And] who created seven heavens in layers. You do not see in the creation of the Most Merciful any inconsistency. So return [your] vision [to the sky]; do you see any breaks", "ٱلَّذِي خَلَقَ سَبۡعَ سَمَٰوَٰتٖ طِبَاقٗاۖ مَّا تَرَىٰ فِي خَلۡقِ ٱلرَّحۡمَٰنِ مِن تَفَٰوُتٖۖ فَٱرۡجِعِ ٱلۡبَصَرَ هَلۡ تَرَىٰ مِن فُطُورٖ", "Allathee khalaqa sabAAa samawatin tibaqan ma tara fee khalqi alrrahmani min tafawutin fairjiAAi albasara hal tara min futoorin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (67, 4, "Then return [your] vision twice again. [Your] vision will return to you humbled while it is fatigued", "ثُمَّ ٱرۡجِعِ ٱلۡبَصَرَ كَرَّتَيۡنِ يَنقَلِبۡ إِلَيۡكَ ٱلۡبَصَرُ خَاسِئٗا وَهُوَ حَسِيرٞ", "Thumma irjiAAi albasara karratayni yanqalib ilayka albasaru khasian wahuwa haseerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (67, 5, "And We have certainly beautified the nearest heaven with stars and have made [from] them what is thrown at the devils and have prepared for them the punishment of the Blaze", "وَلَقَدۡ زَيَّنَّا ٱلسَّمَآءَ ٱلدُّنۡيَا بِمَصَٰبِيحَ وَجَعَلۡنَٰهَا رُجُومٗا لِّلشَّيَٰطِينِۖ وَأَعۡتَدۡنَا لَهُمۡ عَذَابَ ٱلسَّعِيرِ", "Walaqad zayyanna alssamaa alddunya bimasabeeha wajaAAalnaha rujooman lilshshayateeni waaAAtadna lahum AAathaba alssaAAeeri");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (67, 6, "And for those who disbelieved in their Lord is the punishment of Hell, and wretched is the destination", "وَلِلَّذِينَ كَفَرُواْ بِرَبِّهِمۡ عَذَابُ جَهَنَّمَۖ وَبِئۡسَ ٱلۡمَصِيرُ", "Walillatheena kafaroo birabbihim AAathabu jahannama wabisa almaseeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (67, 7, "When they are thrown into it, they hear from it a [dreadful] inhaling while it boils up", "إِذَآ أُلۡقُواْ فِيهَا سَمِعُواْ لَهَا شَهِيقٗا وَهِيَ تَفُورُ", "Itha olqoo feeha samiAAoo laha shaheeqan wahiya tafooru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (67, 8, "It almost bursts with rage. Every time a company is thrown into it, its keepers ask them, ""Did there not come to you a warner", "تَكَادُ تَمَيَّزُ مِنَ ٱلۡغَيۡظِۖ كُلَّمَآ أُلۡقِيَ فِيهَا فَوۡجٞ سَأَلَهُمۡ خَزَنَتُهَآ أَلَمۡ يَأۡتِكُمۡ نَذِيرٞ", "Takadu tamayyazu mina alghaythi kullama olqiya feeha fawjun saalahum khazanatuha alam yatikum natheerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (67, 9, "They will say,"" Yes, a warner had come to us, but we denied and said, 'Allah has not sent down anything. You are not but in great error", "قَالُواْ بَلَىٰ قَدۡ جَآءَنَا نَذِيرٞ فَكَذَّبۡنَا وَقُلۡنَا مَا نَزَّلَ ٱللَّهُ مِن شَيۡءٍ إِنۡ أَنتُمۡ إِلَّا فِي ضَلَٰلٖ كَبِيرٖ", "Qaloo bala qad jaana natheerun fakaththabna waqulna ma nazzala Allahu min shayin in antum illa fee dalalin kabeerin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (67, 10, "And they will say, ""If only we had been listening or reasoning, we would not be among the companions of the Blaze", "وَقَالُواْ لَوۡ كُنَّا نَسۡمَعُ أَوۡ نَعۡقِلُ مَا كُنَّا فِيٓ أَصۡحَٰبِ ٱلسَّعِيرِ", "Waqaloo law kunna nasmaAAu aw naAAqilu ma kunna fee ashabi alssaAAeeri");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (67, 11, "And they will admit their sin, so [it is] alienation for the companions of the Blaze", "فَٱعۡتَرَفُواْ بِذَنۢبِهِمۡ فَسُحۡقٗا لِّأَصۡحَٰبِ ٱلسَّعِيرِ", "FaiAAtarafoo bithanbihim fasuhqan liashabi alssaAAeeri");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (67, 12, "Indeed, those who fear their Lord unseen will have forgiveness and great reward", "إِنَّ ٱلَّذِينَ يَخۡشَوۡنَ رَبَّهُم بِٱلۡغَيۡبِ لَهُم مَّغۡفِرَةٞ وَأَجۡرٞ كَبِيرٞ", "Inna allatheena yakhshawna rabbahum bialghaybi lahum maghfiratun waajrun kabeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (67, 13, "And conceal your speech or publicize it; indeed, He is Knowing of that within the breasts", "وَأَسِرُّواْ قَوۡلَكُمۡ أَوِ ٱجۡهَرُواْ بِهِۦٓۖ إِنَّهُۥ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ", "Waasirroo qawlakum awi ijharoo bihi innahu AAaleemun bithati alssudoori");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (67, 14, "Does He who created not know, while He is the Subtle, the Acquainted", "أَلَا يَعۡلَمُ مَنۡ خَلَقَ وَهُوَ ٱللَّطِيفُ ٱلۡخَبِيرُ", "Ala yaAAlamu man khalaqa wahuwa allateefu alkhabeeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (67, 15, "It is He who made the earth tame for you - so walk among its slopes and eat of His provision - and to Him is the resurrection", "هُوَ ٱلَّذِي جَعَلَ لَكُمُ ٱلۡأَرۡضَ ذَلُولٗا فَٱمۡشُواْ فِي مَنَاكِبِهَا وَكُلُواْ مِن رِّزۡقِهِۦۖ وَإِلَيۡهِ ٱلنُّشُورُ", "Huwa allathee jaAAala lakumu alarda thaloolan faimshoo fee manakibiha wakuloo min rizqihi wailayhi alnnushooru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (67, 16, "Do you feel secure that He who [holds authority] in the heaven would not cause the earth to swallow you and suddenly it would sway", "ءَأَمِنتُم مَّن فِي ٱلسَّمَآءِ أَن يَخۡسِفَ بِكُمُ ٱلۡأَرۡضَ فَإِذَا هِيَ تَمُورُ", "Aamintum man fee alssamai an yakhsifa bikumu alarda faitha hiya tamooru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (67, 17, "Or do you feel secure that He who [holds authority] in the heaven would not send against you a storm of stones? Then you would know how [severe] was My warning", "أَمۡ أَمِنتُم مَّن فِي ٱلسَّمَآءِ أَن يُرۡسِلَ عَلَيۡكُمۡ حَاصِبٗاۖ فَسَتَعۡلَمُونَ كَيۡفَ نَذِيرِ", "Am amintum man fee alssamai an yursila AAalaykum hasiban fasataAAlamoona kayfa natheeri");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (67, 18, "And already had those before them denied, and how [terrible] was My reproach", "وَلَقَدۡ كَذَّبَ ٱلَّذِينَ مِن قَبۡلِهِمۡ فَكَيۡفَ كَانَ نَكِيرِ", "Walaqad kaththaba allatheena min qablihim fakayfa kana nakeeri");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (67, 19, "Do they not see the birds above them with wings outspread and [sometimes] folded in? None holds them [aloft] except the Most Merciful. Indeed He is, of all things, Seeing", "أَوَلَمۡ يَرَوۡاْ إِلَى ٱلطَّيۡرِ فَوۡقَهُمۡ صَـٰٓفَّـٰتٖ وَيَقۡبِضۡنَۚ مَا يُمۡسِكُهُنَّ إِلَّا ٱلرَّحۡمَٰنُۚ إِنَّهُۥ بِكُلِّ شَيۡءِۭ بَصِيرٌ", "Awalam yaraw ila alttayri fawqahum saffatin wayaqbidna ma yumsikuhunna illa alrrahmanu innahu bikulli shayin baseerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (67, 20, "Or who is it that could be an army for you to aid you other than the Most Merciful? The disbelievers are not but in delusion", "أَمَّنۡ هَٰذَا ٱلَّذِي هُوَ جُندٞ لَّكُمۡ يَنصُرُكُم مِّن دُونِ ٱلرَّحۡمَٰنِۚ إِنِ ٱلۡكَٰفِرُونَ إِلَّا فِي غُرُورٍ", "Amman hatha allathee huwa jundun lakum yansurukum min dooni alrrahmani ini alkafiroona illa fee ghuroorin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (67, 21, "Or who is it that could provide for you if He withheld His provision? But they have persisted in insolence and aversion", "أَمَّنۡ هَٰذَا ٱلَّذِي يَرۡزُقُكُمۡ إِنۡ أَمۡسَكَ رِزۡقَهُۥۚ بَل لَّجُّواْ فِي عُتُوّٖ وَنُفُورٍ", "Amman hatha allathee yarzuqukum in amsaka rizqahu bal lajjoo fee AAutuwwin wanufoorin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (67, 22, "Then is one who walks fallen on his face better guided or one who walks erect on a straight path", "أَفَمَن يَمۡشِي مُكِبًّا عَلَىٰ وَجۡهِهِۦٓ أَهۡدَىٰٓ أَمَّن يَمۡشِي سَوِيًّا عَلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ", "Afaman yamshee mukibban AAala wajhihi ahda amman yamshee sawiyyan AAala siratin mustaqeemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (67, 23, "Say, ""It is He who has produced you and made for you hearing and vision and hearts; little are you grateful", "قُلۡ هُوَ ٱلَّذِيٓ أَنشَأَكُمۡ وَجَعَلَ لَكُمُ ٱلسَّمۡعَ وَٱلۡأَبۡصَٰرَ وَٱلۡأَفۡـِٔدَةَۚ قَلِيلٗا مَّا تَشۡكُرُونَ", "Qul huwa allathee anshaakum wajaAAala lakumu alssamAAa waalabsara waalafidata qaleelan ma tashkuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (67, 24, "Say, ""It is He who has multiplied you throughout the earth, and to Him you will be gathered", "قُلۡ هُوَ ٱلَّذِي ذَرَأَكُمۡ فِي ٱلۡأَرۡضِ وَإِلَيۡهِ تُحۡشَرُونَ", "Qul huwa allathee tharaakum fee alardi wailayhi tuhsharoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (67, 25, "And they say, ""When is this promise, if you should be truthful", "وَيَقُولُونَ مَتَىٰ هَٰذَا ٱلۡوَعۡدُ إِن كُنتُمۡ صَٰدِقِينَ", "Wayaqooloona mata hatha alwaAAdu in kuntum sadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (67, 26, "Say, ""The knowledge is only with Allah, and I am only a clear warner", "قُلۡ إِنَّمَا ٱلۡعِلۡمُ عِندَ ٱللَّهِ وَإِنَّمَآ أَنَا۠ نَذِيرٞ مُّبِينٞ", "Qul innama alAAilmu AAinda Allahi wainnama ana natheerun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (67, 27, "But when they see it approaching, the faces of those who disbelieve will be distressed, and it will be said, ""This is that for which you used to call", "فَلَمَّا رَأَوۡهُ زُلۡفَةٗ سِيٓـَٔتۡ وُجُوهُ ٱلَّذِينَ كَفَرُواْ وَقِيلَ هَٰذَا ٱلَّذِي كُنتُم بِهِۦ تَدَّعُونَ", "Falamma raawhu zulfatan seeat wujoohu allatheena kafaroo waqeela hatha allathee kuntum bihi taddaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (67, 28, "Say, [O Muhammad], ""Have you considered: whether Allah should cause my death and those with me or have mercy upon us, who can protect the disbelievers from a painful punishment", "قُلۡ أَرَءَيۡتُمۡ إِنۡ أَهۡلَكَنِيَ ٱللَّهُ وَمَن مَّعِيَ أَوۡ رَحِمَنَا فَمَن يُجِيرُ ٱلۡكَٰفِرِينَ مِنۡ عَذَابٍ أَلِيمٖ", "Qul araaytum in ahlakaniya Allahu waman maAAiya aw rahimana faman yujeeru alkafireena min AAathabin aleemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (67, 29, "Say, ""He is the Most Merciful; we have believed in Him, and upon Him we have relied. And you will [come to] know who it is that is in clear error", "قُلۡ هُوَ ٱلرَّحۡمَٰنُ ءَامَنَّا بِهِۦ وَعَلَيۡهِ تَوَكَّلۡنَاۖ فَسَتَعۡلَمُونَ مَنۡ هُوَ فِي ضَلَٰلٖ مُّبِينٖ", "Qul huwa alrrahmanu amanna bihi waAAalayhi tawakkalna fasataAAlamoona man huwa fee dalalin mubeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (67, 30, "Say, ""Have you considered: if your water was to become sunken [into the earth], then who could bring you flowing water", "قُلۡ أَرَءَيۡتُمۡ إِنۡ أَصۡبَحَ مَآؤُكُمۡ غَوۡرٗا فَمَن يَأۡتِيكُم بِمَآءٖ مَّعِينِۭ", "Qul araaytum in asbaha maokum ghawran faman yateekum bimain maAAeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 1, "Nun. By the pen and what they inscribe", "نٓۚ وَٱلۡقَلَمِ وَمَا يَسۡطُرُونَ", "Noon waalqalami wama yasturoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 2, "You are not, [O Muhammad], by the favor of your Lord, a madman", "مَآ أَنتَ بِنِعۡمَةِ رَبِّكَ بِمَجۡنُونٖ", "Ma anta biniAAmati rabbika bimajnoonin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 3, "And indeed, for you is a reward uninterrupted", "وَإِنَّ لَكَ لَأَجۡرًا غَيۡرَ مَمۡنُونٖ", "Wainna laka laajran ghayra mamnoonin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 4, "And indeed, you are of a great moral character", "وَإِنَّكَ لَعَلَىٰ خُلُقٍ عَظِيمٖ", "Wainnaka laAAala khuluqin AAatheemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 5, "So you will see and they will see", "فَسَتُبۡصِرُ وَيُبۡصِرُونَ", "Fasatubsiru wayubsiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 6, "Which of you is the afflicted [by a devil]", "بِأَييِّكُمُ ٱلۡمَفۡتُونُ", "Biayyikumu almaftoonu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 7, "Indeed, your Lord is most knowing of who has gone astray from His way, and He is most knowing of the [rightly] guided", "إِنَّ رَبَّكَ هُوَ أَعۡلَمُ بِمَن ضَلَّ عَن سَبِيلِهِۦ وَهُوَ أَعۡلَمُ بِٱلۡمُهۡتَدِينَ", "Inna rabbaka huwa aAAlamu biman dalla AAan sabeelihi wahuwa aAAlamu bialmuhtadeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 8, "Then do not obey the deniers", "فَلَا تُطِعِ ٱلۡمُكَذِّبِينَ", "Fala tutiAAi almukaththibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 9, "They wish that you would soften [in your position], so they would soften [toward you]", "وَدُّواْ لَوۡ تُدۡهِنُ فَيُدۡهِنُونَ", "Waddoo law tudhinu fayudhinoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 10, "And do not obey every worthless habitual swearer", "وَلَا تُطِعۡ كُلَّ حَلَّافٖ مَّهِينٍ", "Wala tutiAA kulla hallafin maheenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 11, "[And] scorner, going about with malicious gossip", "هَمَّازٖ مَّشَّآءِۭ بِنَمِيمٖ", "Hammazin mashshain binameemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 12, "A preventer of good, transgressing and sinful", "مَّنَّاعٖ لِّلۡخَيۡرِ مُعۡتَدٍ أَثِيمٍ", "MannaAAin lilkhayri muAAtadin atheemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 13, "Cruel, moreover, and an illegitimate pretender", "عُتُلِّۭ بَعۡدَ ذَٰلِكَ زَنِيمٍ", "AAutullin baAAda thalika zaneemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 14, "Because he is a possessor of wealth and children", "أَن كَانَ ذَا مَالٖ وَبَنِينَ", "An kana tha malin wabaneena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 15, "When Our verses are recited to him, he says, ""Legends of the former peoples", "إِذَا تُتۡلَىٰ عَلَيۡهِ ءَايَٰتُنَا قَالَ أَسَٰطِيرُ ٱلۡأَوَّلِينَ", "Itha tutla AAalayhi ayatuna qala asateeru alawwaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 16, "We will brand him upon the snout", "سَنَسِمُهُۥ عَلَى ٱلۡخُرۡطُومِ", "Sanasimuhu AAala alkhurtoomi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 17, "Indeed, We have tried them as We tried the companions of the garden, when they swore to cut its fruit in the [early] morning", "إِنَّا بَلَوۡنَٰهُمۡ كَمَا بَلَوۡنَآ أَصۡحَٰبَ ٱلۡجَنَّةِ إِذۡ أَقۡسَمُواْ لَيَصۡرِمُنَّهَا مُصۡبِحِينَ", "Inna balawnahum kama balawna ashaba aljannati ith aqsamoo layasrimunnaha musbiheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 18, "Without making exception", "وَلَا يَسۡتَثۡنُونَ", "Wala yastathnoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 19, "So there came upon the garden an affliction from your Lord while they were asleep", "فَطَافَ عَلَيۡهَا طَآئِفٞ مِّن رَّبِّكَ وَهُمۡ نَآئِمُونَ", "Fatafa AAalayha taifun min rabbika wahum naimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 20, "And it became as though reaped", "فَأَصۡبَحَتۡ كَٱلصَّرِيمِ", "Faasbahat kaalssareemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 21, "And they called one another at morning", "فَتَنَادَوۡاْ مُصۡبِحِينَ", "Fatanadaw musbiheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 22, "[Saying], ""Go early to your crop if you would cut the fruit", "أَنِ ٱغۡدُواْ عَلَىٰ حَرۡثِكُمۡ إِن كُنتُمۡ صَٰرِمِينَ", "Ani ighdoo AAala harthikum in kuntum sarimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 23, "So they set out, while lowering their voices", "فَٱنطَلَقُواْ وَهُمۡ يَتَخَٰفَتُونَ", "Faintalaqoo wahum yatakhafatoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 24, "[Saying], ""There will surely not enter it today upon you [any] poor person", "أَن لَّا يَدۡخُلَنَّهَا ٱلۡيَوۡمَ عَلَيۡكُم مِّسۡكِينٞ", "An la yadkhulannaha alyawma AAalaykum miskeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 25, "And they went early in determination, [assuming themselves] able", "وَغَدَوۡاْ عَلَىٰ حَرۡدٖ قَٰدِرِينَ", "Waghadaw AAala hardin qadireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 26, "But when they saw it, they said, ""Indeed, we are lost", "فَلَمَّا رَأَوۡهَا قَالُوٓاْ إِنَّا لَضَآلُّونَ", "Falamma raawha qaloo inna ladalloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 27, "Rather, we have been deprived", "بَلۡ نَحۡنُ مَحۡرُومُونَ", "Bal nahnu mahroomoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 28, "The most moderate of them said, ""Did I not say to you, 'Why do you not exalt [Allah]", "قَالَ أَوۡسَطُهُمۡ أَلَمۡ أَقُل لَّكُمۡ لَوۡلَا تُسَبِّحُونَ", "Qala awsatuhum alam aqul lakum lawla tusabbihoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 29, "They said, ""Exalted is our Lord! Indeed, we were wrongdoers", "قَالُواْ سُبۡحَٰنَ رَبِّنَآ إِنَّا كُنَّا ظَٰلِمِينَ", "Qaloo subhana rabbina inna kunna thalimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 30, "Then they approached one another, blaming each other", "فَأَقۡبَلَ بَعۡضُهُمۡ عَلَىٰ بَعۡضٖ يَتَلَٰوَمُونَ", "Faaqbala baAAduhum AAala baAAdin yatalawamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 31, "They said, ""O woe to us; indeed we were transgressors", "قَالُواْ يَٰوَيۡلَنَآ إِنَّا كُنَّا طَٰغِينَ", "Qaloo ya waylana inna kunna tagheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 32, "Perhaps our Lord will substitute for us [one] better than it. Indeed, we are toward our Lord desirous", "عَسَىٰ رَبُّنَآ أَن يُبۡدِلَنَا خَيۡرٗا مِّنۡهَآ إِنَّآ إِلَىٰ رَبِّنَا رَٰغِبُونَ", "AAasa rabbuna an yubdilana khayran minha inna ila rabbina raghiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 33, "Such is the punishment [of this world]. And the punishment of the Hereafter is greater, if they only knew", "كَذَٰلِكَ ٱلۡعَذَابُۖ وَلَعَذَابُ ٱلۡأٓخِرَةِ أَكۡبَرُۚ لَوۡ كَانُواْ يَعۡلَمُونَ", "Kathalika alAAathabu walaAAathabu alakhirati akbaru law kanoo yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 34, "Indeed, for the righteous with their Lord are the Gardens of Pleasure", "إِنَّ لِلۡمُتَّقِينَ عِندَ رَبِّهِمۡ جَنَّـٰتِ ٱلنَّعِيمِ", "Inna lilmuttaqeena AAinda rabbihim jannati alnnaAAeemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 35, "Then will We treat the Muslims like the criminals", "أَفَنَجۡعَلُ ٱلۡمُسۡلِمِينَ كَٱلۡمُجۡرِمِينَ", "AfanajAAalu almuslimeena kaalmujrimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 36, "What is [the matter] with you? How do you judge", "مَا لَكُمۡ كَيۡفَ تَحۡكُمُونَ", "Ma lakum kayfa tahkumoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 37, "Or do you have a scripture in which you learn", "أَمۡ لَكُمۡ كِتَٰبٞ فِيهِ تَدۡرُسُونَ", "Am lakum kitabun feehi tadrusoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 38, "That indeed for you is whatever you choose", "إِنَّ لَكُمۡ فِيهِ لَمَا تَخَيَّرُونَ", "Inna lakum feehi lama takhayyaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 39, "Or do you have oaths [binding] upon Us, extending until the Day of Resurrection, that indeed for you is whatever you judge", "أَمۡ لَكُمۡ أَيۡمَٰنٌ عَلَيۡنَا بَٰلِغَةٌ إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِ إِنَّ لَكُمۡ لَمَا تَحۡكُمُونَ", "Am lakum aymanun AAalayna balighatun ila yawmi alqiyamati inna lakum lama tahkumoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 40, "Ask them which of them, for that [claim], is responsible", "سَلۡهُمۡ أَيُّهُم بِذَٰلِكَ زَعِيمٌ", "Salhum ayyuhum bithalika zaAAeemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 41, "Or do they have partners? Then let them bring their partners, if they should be truthful", "أَمۡ لَهُمۡ شُرَكَآءُ فَلۡيَأۡتُواْ بِشُرَكَآئِهِمۡ إِن كَانُواْ صَٰدِقِينَ", "Am lahum shurakao falyatoo bishurakaihim in kanoo sadiqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 42, "The Day the shin will be uncovered and they are invited to prostration but the disbelievers will not be able", "يَوۡمَ يُكۡشَفُ عَن سَاقٖ وَيُدۡعَوۡنَ إِلَى ٱلسُّجُودِ فَلَا يَسۡتَطِيعُونَ", "Yawma yukshafu AAan saqin wayudAAawna ila alssujoodi fala yastateeAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 43, "Their eyes humbled, humiliation will cover them. And they used to be invited to prostration while they were sound", "خَٰشِعَةً أَبۡصَٰرُهُمۡ تَرۡهَقُهُمۡ ذِلَّةٞۖ وَقَدۡ كَانُواْ يُدۡعَوۡنَ إِلَى ٱلسُّجُودِ وَهُمۡ سَٰلِمُونَ", "KhashiAAatan absaruhum tarhaquhum thillatun waqad kanoo yudAAawna ila alssujoodi wahum salimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 44, "So leave Me, [O Muhammad], with [the matter of] whoever denies the Qur'an. We will progressively lead them [to punishment] from where they do not know", "فَذَرۡنِي وَمَن يُكَذِّبُ بِهَٰذَا ٱلۡحَدِيثِۖ سَنَسۡتَدۡرِجُهُم مِّنۡ حَيۡثُ لَا يَعۡلَمُونَ", "Fatharnee waman yukaththibu bihatha alhadeethi sanastadrijuhum min haythu la yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 45, "And I will give them time. Indeed, My plan is firm", "وَأُمۡلِي لَهُمۡۚ إِنَّ كَيۡدِي مَتِينٌ", "Waomlee lahum inna kaydee mateenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 46, "Or do you ask of them a payment, so they are by debt burdened down", "أَمۡ تَسۡـَٔلُهُمۡ أَجۡرٗا فَهُم مِّن مَّغۡرَمٖ مُّثۡقَلُونَ", "Am tasaluhum ajran fahum min maghramin muthqaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 47, "Or have they [knowledge of] the unseen, so they write [it] down", "أَمۡ عِندَهُمُ ٱلۡغَيۡبُ فَهُمۡ يَكۡتُبُونَ", "Am AAindahumu alghaybu fahum yaktuboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 48, "Then be patient for the decision of your Lord, [O Muhammad], and be not like the companion of the fish when he called out while he was distressed", "فَٱصۡبِرۡ لِحُكۡمِ رَبِّكَ وَلَا تَكُن كَصَاحِبِ ٱلۡحُوتِ إِذۡ نَادَىٰ وَهُوَ مَكۡظُومٞ", "Faisbir lihukmi rabbika wala takun kasahibi alhooti ith nada wahuwa makthoomun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 49, "If not that a favor from his Lord overtook him, he would have been thrown onto the naked shore while he was censured", "لَّوۡلَآ أَن تَدَٰرَكَهُۥ نِعۡمَةٞ مِّن رَّبِّهِۦ لَنُبِذَ بِٱلۡعَرَآءِ وَهُوَ مَذۡمُومٞ", "Lawla an tadarakahu niAAmatun min rabbihi lanubitha bialAAarai wahuwa mathmoomun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 50, "And his Lord chose him and made him of the righteous", "فَٱجۡتَبَٰهُ رَبُّهُۥ فَجَعَلَهُۥ مِنَ ٱلصَّـٰلِحِينَ", "Faijtabahu rabbuhu fajaAAalahu mina alssaliheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 51, "And indeed, those who disbelieve would almost make you slip with their eyes when they hear the message, and they say, ""Indeed, he is mad", "وَإِن يَكَادُ ٱلَّذِينَ كَفَرُواْ لَيُزۡلِقُونَكَ بِأَبۡصَٰرِهِمۡ لَمَّا سَمِعُواْ ٱلذِّكۡرَ وَيَقُولُونَ إِنَّهُۥ لَمَجۡنُونٞ", "Wain yakadu allatheena kafaroo layuzliqoonaka biabsarihim lamma samiAAoo alththikra wayaqooloona innahu lamajnoonun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (68, 52, "But it is not except a reminder to the worlds", "وَمَا هُوَ إِلَّا ذِكۡرٞ لِّلۡعَٰلَمِينَ", "Wama huwa illa thikrun lilAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 1, "The Inevitable Reality", "ٱلۡحَآقَّةُ", "Alhaqqatu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 2, "What is the Inevitable Reality", "مَا ٱلۡحَآقَّةُ", "Ma alhaqqatu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 3, "And what can make you know what is the Inevitable Reality", "وَمَآ أَدۡرَىٰكَ مَا ٱلۡحَآقَّةُ", "Wama adraka ma alhaqqatu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 4, "Thamud and 'Aad denied the Striking Calamity", "كَذَّبَتۡ ثَمُودُ وَعَادُۢ بِٱلۡقَارِعَةِ", "Kaththabat thamoodu waAAadun bialqariAAati");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 5, "So as for Thamud, they were destroyed by the overpowering [blast]", "فَأَمَّا ثَمُودُ فَأُهۡلِكُواْ بِٱلطَّاغِيَةِ", "Faamma thamoodu faohlikoo bialttaghiyati");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 6, "And as for 'Aad, they were destroyed by a screaming, violent wind", "وَأَمَّا عَادٞ فَأُهۡلِكُواْ بِرِيحٖ صَرۡصَرٍ عَاتِيَةٖ", "Waamma AAadun faohlikoo bireehin sarsarin AAatiyatin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 7, "Which Allah imposed upon them for seven nights and eight days in succession, so you would see the people therein fallen as if they were hollow trunks of palm trees", "سَخَّرَهَا عَلَيۡهِمۡ سَبۡعَ لَيَالٖ وَثَمَٰنِيَةَ أَيَّامٍ حُسُومٗاۖ فَتَرَى ٱلۡقَوۡمَ فِيهَا صَرۡعَىٰ كَأَنَّهُمۡ أَعۡجَازُ نَخۡلٍ خَاوِيَةٖ", "Sakhkharaha AAalayhim sabAAa layalin wathamaniyata ayyamin husooman fatara alqawma feeha sarAAa kaannahum aAAjazu nakhlin khawiyatun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 8, "Then do you see of them any remains", "فَهَلۡ تَرَىٰ لَهُم مِّنۢ بَاقِيَةٖ", "Fahal tara lahum min baqiyatin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 9, "And there came Pharaoh and those before him and the overturned cities with sin", "وَجَآءَ فِرۡعَوۡنُ وَمَن قَبۡلَهُۥ وَٱلۡمُؤۡتَفِكَٰتُ بِٱلۡخَاطِئَةِ", "Wajaa firAAawnu waman qablahu waalmutafikatu bialkhatiati");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 10, "And they disobeyed the messenger of their Lord, so He seized them with a seizure exceeding [in severity]", "فَعَصَوۡاْ رَسُولَ رَبِّهِمۡ فَأَخَذَهُمۡ أَخۡذَةٗ رَّابِيَةً", "FaAAasaw rasoola rabbihim faakhathahum akhthatan rabiyatan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 11, "Indeed, when the water overflowed, We carried your ancestors in the sailing ship", "إِنَّا لَمَّا طَغَا ٱلۡمَآءُ حَمَلۡنَٰكُمۡ فِي ٱلۡجَارِيَةِ", "Inna lamma tagha almao hamalnakum fee aljariyati");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 12, "That We might make it for you a reminder and [that] a conscious ear would be conscious of it", "لِنَجۡعَلَهَا لَكُمۡ تَذۡكِرَةٗ وَتَعِيَهَآ أُذُنٞ وَٰعِيَةٞ", "LinajAAalaha lakum tathkiratan wataAAiyaha othunun waAAiyatun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 13, "Then when the Horn is blown with one blast", "فَإِذَا نُفِخَ فِي ٱلصُّورِ نَفۡخَةٞ وَٰحِدَةٞ", "Faitha nufikha fee alssoori nafkhatun wahidatun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 14, "And the earth and the mountains are lifted and leveled with one blow", "وَحُمِلَتِ ٱلۡأَرۡضُ وَٱلۡجِبَالُ فَدُكَّتَا دَكَّةٗ وَٰحِدَةٗ", "Wahumilati alardu waaljibalu fadukkata dakkatan wahidatan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 15, "Then on that Day, the Resurrection will occur", "فَيَوۡمَئِذٖ وَقَعَتِ ٱلۡوَاقِعَةُ", "Fayawmaithin waqaAAati alwaqiAAatu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 16, "And the heaven will split [open], for that Day it is infirm", "وَٱنشَقَّتِ ٱلسَّمَآءُ فَهِيَ يَوۡمَئِذٖ وَاهِيَةٞ", "Wainshaqqati alssamao fahiya yawmaithin wahiyatun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 17, "And the angels are at its edges. And there will bear the Throne of your Lord above them, that Day, eight [of them]", "وَٱلۡمَلَكُ عَلَىٰٓ أَرۡجَآئِهَاۚ وَيَحۡمِلُ عَرۡشَ رَبِّكَ فَوۡقَهُمۡ يَوۡمَئِذٖ ثَمَٰنِيَةٞ", "Waalmalaku AAala arjaiha wayahmilu AAarsha rabbika fawqahum yawmaithin thamaniyatun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 18, "That Day, you will be exhibited [for judgement]; not hidden among you is anything concealed", "يَوۡمَئِذٖ تُعۡرَضُونَ لَا تَخۡفَىٰ مِنكُمۡ خَافِيَةٞ", "Yawmaithin tuAAradoona la takhfa minkum khafiyatun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 19, "So as for he who is given his record in his right hand, he will say, ""Here, read my record", "فَأَمَّا مَنۡ أُوتِيَ كِتَٰبَهُۥ بِيَمِينِهِۦ فَيَقُولُ هَآؤُمُ ٱقۡرَءُواْ كِتَٰبِيَهۡ", "Faamma man ootiya kitabahu biyameenihi fayaqoolu haomu iqraoo kitabiyah");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 20, "Indeed, I was certain that I would be meeting my account", "إِنِّي ظَنَنتُ أَنِّي مُلَٰقٍ حِسَابِيَهۡ", "Innee thanantu annee mulaqin hisabiyah");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 21, "So he will be in a pleasant life", "فَهُوَ فِي عِيشَةٖ رَّاضِيَةٖ", "Fahuwa fee AAeeshatin radiyatin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 22, "In an elevated garden", "فِي جَنَّةٍ عَالِيَةٖ", "Fee jannatin AAaliyatin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 23, "Its [fruit] to be picked hanging near", "قُطُوفُهَا دَانِيَةٞ", "Qutoofuha daniyatun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 24, "[They will be told], ""Eat and drink in satisfaction for what you put forth in the days past", "كُلُواْ وَٱشۡرَبُواْ هَنِيٓـَٔۢا بِمَآ أَسۡلَفۡتُمۡ فِي ٱلۡأَيَّامِ ٱلۡخَالِيَةِ", "Kuloo waishraboo haneean bima aslaftum fee alayyami alkhaliyati");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 25, "But as for he who is given his record in his left hand, he will say, ""Oh, I wish I had not been given my record", "وَأَمَّا مَنۡ أُوتِيَ كِتَٰبَهُۥ بِشِمَالِهِۦ فَيَقُولُ يَٰلَيۡتَنِي لَمۡ أُوتَ كِتَٰبِيَهۡ", "Waamma man ootiya kitabahu bishimalihi fayaqoolu ya laytanee lam oota kitabiyah");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 26, "And had not known what is my account", "وَلَمۡ أَدۡرِ مَا حِسَابِيَهۡ", "Walam adri ma hisabiyah");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 27, "I wish my death had been the decisive one", "يَٰلَيۡتَهَا كَانَتِ ٱلۡقَاضِيَةَ", "Ya laytaha kanati alqadiyatu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 28, "My wealth has not availed me", "مَآ أَغۡنَىٰ عَنِّي مَالِيَهۡۜ", "Ma aghna AAannee maliyah");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 29, "Gone from me is my authority", "هَلَكَ عَنِّي سُلۡطَٰنِيَهۡ", "Halaka AAannee sultaniyah");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 30, "[Allah will say], ""Seize him and shackle him", "خُذُوهُ فَغُلُّوهُ", "Khuthoohu faghulloohu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 31, "Then into Hellfire drive him", "ثُمَّ ٱلۡجَحِيمَ صَلُّوهُ", "Thumma aljaheema salloohu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 32, "Then into a chain whose length is seventy cubits insert him", "ثُمَّ فِي سِلۡسِلَةٖ ذَرۡعُهَا سَبۡعُونَ ذِرَاعٗا فَٱسۡلُكُوهُ", "Thumma fee silsilatin tharAAuha sabAAoona thiraAAan faoslukoohu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 33, "Indeed, he did not used to believe in Allah, the Most Great", "إِنَّهُۥ كَانَ لَا يُؤۡمِنُ بِٱللَّهِ ٱلۡعَظِيمِ", "Innahu kana la yuminu biAllahi alAAatheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 34, "Nor did he encourage the feeding of the poor", "وَلَا يَحُضُّ عَلَىٰ طَعَامِ ٱلۡمِسۡكِينِ", "Wala yahuddu AAala taAAami almiskeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 35, "So there is not for him here this Day any devoted friend", "فَلَيۡسَ لَهُ ٱلۡيَوۡمَ هَٰهُنَا حَمِيمٞ", "Falaysa lahu alyawma hahuna hameemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 36, "Nor any food except from the discharge of wounds", "وَلَا طَعَامٌ إِلَّا مِنۡ غِسۡلِينٖ", "Wala taAAamun illa min ghisleenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 37, "None will eat it except the sinners", "لَّا يَأۡكُلُهُۥٓ إِلَّا ٱلۡخَٰطِـُٔونَ", "La yakuluhu illa alkhatioona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 38, "So I swear by what you see", "فَلَآ أُقۡسِمُ بِمَا تُبۡصِرُونَ", "Fala oqsimu bima tubsiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 39, "And what you do not see", "وَمَا لَا تُبۡصِرُونَ", "Wama la tubsiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 40, "[That] indeed, the Qur'an is the word of a noble Messenger", "إِنَّهُۥ لَقَوۡلُ رَسُولٖ كَرِيمٖ", "Innahu laqawlu rasoolin kareemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 41, "And it is not the word of a poet; little do you believe", "وَمَا هُوَ بِقَوۡلِ شَاعِرٖۚ قَلِيلٗا مَّا تُؤۡمِنُونَ", "Wama huwa biqawli shaAAirin qaleelan ma tuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 42, "Nor the word of a soothsayer; little do you remember", "وَلَا بِقَوۡلِ كَاهِنٖۚ قَلِيلٗا مَّا تَذَكَّرُونَ", "Wala biqawli kahinin qaleelan ma tathakkaroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 43, "[It is] a revelation from the Lord of the worlds", "تَنزِيلٞ مِّن رَّبِّ ٱلۡعَٰلَمِينَ", "Tanzeelun min rabbi alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 44, "And if Muhammad had made up about Us some [false] sayings", "وَلَوۡ تَقَوَّلَ عَلَيۡنَا بَعۡضَ ٱلۡأَقَاوِيلِ", "Walaw taqawwala AAalayna baAAda alaqaweeli");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 45, "We would have seized him by the right hand", "لَأَخَذۡنَا مِنۡهُ بِٱلۡيَمِينِ", "Laakhathna minhu bialyameeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 46, "Then We would have cut from him the aorta", "ثُمَّ لَقَطَعۡنَا مِنۡهُ ٱلۡوَتِينَ", "Thumma laqataAAna minhu alwateena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 47, "And there is no one of you who could prevent [Us] from him", "فَمَا مِنكُم مِّنۡ أَحَدٍ عَنۡهُ حَٰجِزِينَ", "Fama minkum min ahadin AAanhu hajizeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 48, "And indeed, the Qur'an is a reminder for the righteous", "وَإِنَّهُۥ لَتَذۡكِرَةٞ لِّلۡمُتَّقِينَ", "Wainnahu latathkiratun lilmuttaqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 49, "And indeed, We know that among you are deniers", "وَإِنَّا لَنَعۡلَمُ أَنَّ مِنكُم مُّكَذِّبِينَ", "Wainna lanaAAlamu anna minkum mukaththibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 50, "And indeed, it will be [a cause of] regret upon the disbelievers", "وَإِنَّهُۥ لَحَسۡرَةٌ عَلَى ٱلۡكَٰفِرِينَ", "Wainnahu lahasratun AAala alkafireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 51, "And indeed, it is the truth of certainty", "وَإِنَّهُۥ لَحَقُّ ٱلۡيَقِينِ", "Wainnahu lahaqqu alyaqeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (69, 52, "So exalt the name of your Lord, the Most Great", "فَسَبِّحۡ بِٱسۡمِ رَبِّكَ ٱلۡعَظِيمِ", "Fasabbih biismi rabbika alAAatheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 1, "A supplicant asked for a punishment bound to happen", "سَأَلَ سَآئِلُۢ بِعَذَابٖ وَاقِعٖ", "Saala sailun biAAathabin waqiAAin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 2, "To the disbelievers; of it there is no preventer", "لِّلۡكَٰفِرِينَ لَيۡسَ لَهُۥ دَافِعٞ", "Lilkafireena laysa lahu dafiAAun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 3, "[It is] from Allah, owner of the ways of ascent", "مِّنَ ٱللَّهِ ذِي ٱلۡمَعَارِجِ", "Mina Allahi thee almaAAariji");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 4, "The angels and the Spirit will ascend to Him during a Day the extent of which is fifty thousand years", "تَعۡرُجُ ٱلۡمَلَـٰٓئِكَةُ وَٱلرُّوحُ إِلَيۡهِ فِي يَوۡمٖ كَانَ مِقۡدَارُهُۥ خَمۡسِينَ أَلۡفَ سَنَةٖ", "TaAAruju almalaikatu waalrroohu ilayhi fee yawmin kana miqdaruhu khamseena alfa sanatin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 5, "So be patient with gracious patience", "فَٱصۡبِرۡ صَبۡرٗا جَمِيلًا", "Faisbir sabran jameelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 6, "Indeed, they see it [as] distant", "إِنَّهُمۡ يَرَوۡنَهُۥ بَعِيدٗا", "Innahum yarawnahu baAAeedan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 7, "But We see it [as] near", "وَنَرَىٰهُ قَرِيبٗا", "Wanarahu qareeban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 8, "On the Day the sky will be like murky oil", "يَوۡمَ تَكُونُ ٱلسَّمَآءُ كَٱلۡمُهۡلِ", "Yawma takoonu alssamao kaalmuhli");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 9, "And the mountains will be like wool", "وَتَكُونُ ٱلۡجِبَالُ كَٱلۡعِهۡنِ", "Watakoonu aljibalu kaalAAihni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 10, "And no friend will ask [anything of] a friend", "وَلَا يَسۡـَٔلُ حَمِيمٌ حَمِيمٗا", "Wala yasalu hameemun hameeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 11, "They will be shown each other. The criminal will wish that he could be ransomed from the punishment of that Day by his children", "يُبَصَّرُونَهُمۡۚ يَوَدُّ ٱلۡمُجۡرِمُ لَوۡ يَفۡتَدِي مِنۡ عَذَابِ يَوۡمِئِذِۭ بِبَنِيهِ", "Yubassaroonahum yawaddu almujrimu law yaftadee min AAathabi yawmiithin bibaneehi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 12, "And his wife and his brother", "وَصَٰحِبَتِهِۦ وَأَخِيهِ", "Wasahibatihi waakheehi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 13, "And his nearest kindred who shelter him", "وَفَصِيلَتِهِ ٱلَّتِي تُـٔۡوِيهِ", "Wafaseelatihi allatee tuweehi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 14, "And whoever is on earth entirely [so] then it could save him", "وَمَن فِي ٱلۡأَرۡضِ جَمِيعٗا ثُمَّ يُنجِيهِ", "Waman fee alardi jameeAAan thumma yunjeehi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 15, "No! Indeed, it is the Flame [of Hell]", "كَلَّآۖ إِنَّهَا لَظَىٰ", "Kalla innaha latha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 16, "A remover of exteriors", "نَزَّاعَةٗ لِّلشَّوَىٰ", "NazzaAAatan lilshshawa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 17, "It invites he who turned his back [on truth] and went away [from obedience]", "تَدۡعُواْ مَنۡ أَدۡبَرَ وَتَوَلَّىٰ", "TadAAoo man adbara watawalla");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 18, "And collected [wealth] and hoarded", "وَجَمَعَ فَأَوۡعَىٰٓ", "WajamaAAa faawAAa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 19, "Indeed, mankind was created anxious", "۞إِنَّ ٱلۡإِنسَٰنَ خُلِقَ هَلُوعًا", "Inna alinsana khuliqa halooAAan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 20, "When evil touches him, impatient", "إِذَا مَسَّهُ ٱلشَّرُّ جَزُوعٗا", "Itha massahu alshsharru jazooAAan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 21, "And when good touches him, withholding [of it]", "وَإِذَا مَسَّهُ ٱلۡخَيۡرُ مَنُوعًا", "Waitha massahu alkhayru manooAAan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 22, "Except the observers of prayer", "إِلَّا ٱلۡمُصَلِّينَ", "Illa almusalleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 23, "Those who are constant in their prayer", "ٱلَّذِينَ هُمۡ عَلَىٰ صَلَاتِهِمۡ دَآئِمُونَ", "Allatheena hum AAala salatihim daimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 24, "And those within whose wealth is a known right", "وَٱلَّذِينَ فِيٓ أَمۡوَٰلِهِمۡ حَقّٞ مَّعۡلُومٞ", "Waallatheena fee amwalihim haqqun maAAloomun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 25, "For the petitioner and the deprived", "لِّلسَّآئِلِ وَٱلۡمَحۡرُومِ", "Lilssaili waalmahroomi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 26, "And those who believe in the Day of Recompense", "وَٱلَّذِينَ يُصَدِّقُونَ بِيَوۡمِ ٱلدِّينِ", "Waallatheena yusaddiqoona biyawmi alddeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 27, "And those who are fearful of the punishment of their Lord", "وَٱلَّذِينَ هُم مِّنۡ عَذَابِ رَبِّهِم مُّشۡفِقُونَ", "Waallatheena hum min AAathabi rabbihim mushfiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 28, "Indeed, the punishment of their Lord is not that from which one is safe", "إِنَّ عَذَابَ رَبِّهِمۡ غَيۡرُ مَأۡمُونٖ", "Inna AAathaba rabbihim ghayru mamoonin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 29, "And those who guard their private parts", "وَٱلَّذِينَ هُمۡ لِفُرُوجِهِمۡ حَٰفِظُونَ", "Waallatheena hum lifuroojihim hafithoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 30, "Except from their wives or those their right hands possess, for indeed, they are not to be blamed", "إِلَّا عَلَىٰٓ أَزۡوَٰجِهِمۡ أَوۡ مَا مَلَكَتۡ أَيۡمَٰنُهُمۡ فَإِنَّهُمۡ غَيۡرُ مَلُومِينَ", "Illa AAala azwajihim aw ma malakat aymanuhum fainnahum ghayru maloomeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 31, "But whoever seeks beyond that, then they are the transgressors", "فَمَنِ ٱبۡتَغَىٰ وَرَآءَ ذَٰلِكَ فَأُوْلَـٰٓئِكَ هُمُ ٱلۡعَادُونَ", "Famani ibtagha waraa thalika faolaika humu alAAadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 32, "And those who are to their trusts and promises attentive", "وَٱلَّذِينَ هُمۡ لِأَمَٰنَٰتِهِمۡ وَعَهۡدِهِمۡ رَٰعُونَ", "Waallatheena hum liamanatihim waAAahdihim raAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 33, "And those who are in their testimonies upright", "وَٱلَّذِينَ هُم بِشَهَٰدَٰتِهِمۡ قَآئِمُونَ", "Waallatheena hum bishahadatihim qaimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 34, "And those who [carefully] maintain their prayer", "وَٱلَّذِينَ هُمۡ عَلَىٰ صَلَاتِهِمۡ يُحَافِظُونَ", "Waallatheena hum AAala salatihim yuhafithoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 35, "They will be in gardens, honored", "أُوْلَـٰٓئِكَ فِي جَنَّـٰتٖ مُّكۡرَمُونَ", "Olaika fee jannatin mukramoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 36, "So what is [the matter] with those who disbelieve, hastening [from] before you, [O Muhammad]", "فَمَالِ ٱلَّذِينَ كَفَرُواْ قِبَلَكَ مُهۡطِعِينَ", "Famali allatheena kafaroo qibalaka muhtiAAeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 37, "[To sit] on [your] right and [your] left in separate groups", "عَنِ ٱلۡيَمِينِ وَعَنِ ٱلشِّمَالِ عِزِينَ", "AAani alyameeni waAAani alshshimali AAizeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 38, "Does every person among them aspire to enter a garden of pleasure", "أَيَطۡمَعُ كُلُّ ٱمۡرِيٕٖ مِّنۡهُمۡ أَن يُدۡخَلَ جَنَّةَ نَعِيمٖ", "AyatmaAAu kullu imriin minhum an yudkhala jannata naAAeemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 39, "No! Indeed, We have created them from that which they know", "كَلَّآۖ إِنَّا خَلَقۡنَٰهُم مِّمَّا يَعۡلَمُونَ", "Kalla inna khalaqnahum mimma yaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 40, "So I swear by the Lord of [all] risings and settings that indeed We are able", "فَلَآ أُقۡسِمُ بِرَبِّ ٱلۡمَشَٰرِقِ وَٱلۡمَغَٰرِبِ إِنَّا لَقَٰدِرُونَ", "Fala oqsimu birabbi almashariqi waalmagharibi inna laqadiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 41, "To replace them with better than them; and We are not to be outdone", "عَلَىٰٓ أَن نُّبَدِّلَ خَيۡرٗا مِّنۡهُمۡ وَمَا نَحۡنُ بِمَسۡبُوقِينَ", "AAala an nubaddila khayran minhum wama nahnu bimasbooqeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 42, "So leave them to converse vainly and amuse themselves until they meet their Day which they are promised", "فَذَرۡهُمۡ يَخُوضُواْ وَيَلۡعَبُواْ حَتَّىٰ يُلَٰقُواْ يَوۡمَهُمُ ٱلَّذِي يُوعَدُونَ", "Fatharhum yakhoodoo wayalAAaboo hatta yulaqoo yawmahumu allathee yooAAadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 43, "The Day they will emerge from the graves rapidly as if they were, toward an erected idol, hastening", "يَوۡمَ يَخۡرُجُونَ مِنَ ٱلۡأَجۡدَاثِ سِرَاعٗا كَأَنَّهُمۡ إِلَىٰ نُصُبٖ يُوفِضُونَ", "Yawma yakhrujoona mina alajdathi siraAAan kaannahum ila nusubin yoofidoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (70, 44, "Their eyes humbled, humiliation will cover them. That is the Day which they had been promised", "خَٰشِعَةً أَبۡصَٰرُهُمۡ تَرۡهَقُهُمۡ ذِلَّةٞۚ ذَٰلِكَ ٱلۡيَوۡمُ ٱلَّذِي كَانُواْ يُوعَدُونَ", "KhashiAAatan absaruhum tarhaquhum thillatun thalika alyawmu allathee kanoo yooAAadoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (71, 1, "Indeed, We sent Noah to his people, [saying], ""Warn your people before there comes to them a painful punishment", "إِنَّآ أَرۡسَلۡنَا نُوحًا إِلَىٰ قَوۡمِهِۦٓ أَنۡ أَنذِرۡ قَوۡمَكَ مِن قَبۡلِ أَن يَأۡتِيَهُمۡ عَذَابٌ أَلِيمٞ", "Inna arsalna noohan ila qawmihi an anthir qawmaka min qabli an yatiyahum AAathabun aleemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (71, 2, "He said, ""O my people, indeed I am to you a clear warner", "قَالَ يَٰقَوۡمِ إِنِّي لَكُمۡ نَذِيرٞ مُّبِينٌ", "Qala ya qawmi innee lakum natheerun mubeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (71, 3, "[Saying], 'Worship Allah, fear Him and obey me", "أَنِ ٱعۡبُدُواْ ٱللَّهَ وَٱتَّقُوهُ وَأَطِيعُونِ", "Ani oAAbudoo Allaha waittaqoohu waateeAAooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (71, 4, "Allah will forgive you of your sins and delay you for a specified term. Indeed, the time [set by] Allah, when it comes, will not be delayed, if you only knew", "يَغۡفِرۡ لَكُم مِّن ذُنُوبِكُمۡ وَيُؤَخِّرۡكُمۡ إِلَىٰٓ أَجَلٖ مُّسَمًّىۚ إِنَّ أَجَلَ ٱللَّهِ إِذَا جَآءَ لَا يُؤَخَّرُۚ لَوۡ كُنتُمۡ تَعۡلَمُونَ", "Yaghfir lakum min thunoobikum wayuakhkhirkum ila ajalin musamman inna ajala Allahi itha jaa la yuakhkharu law kuntum taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (71, 5, "He said, ""My Lord, indeed I invited my people [to truth] night and day", "قَالَ رَبِّ إِنِّي دَعَوۡتُ قَوۡمِي لَيۡلٗا وَنَهَارٗا", "Qala rabbi innee daAAawtu qawmee laylan wanaharan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (71, 6, "But my invitation increased them not except in flight", "فَلَمۡ يَزِدۡهُمۡ دُعَآءِيٓ إِلَّا فِرَارٗا", "Falam yazidhum duAAaee illa firaran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (71, 7, "And indeed, every time I invited them that You may forgive them, they put their fingers in their ears, covered themselves with their garments, persisted, and were arrogant with [great] arrogance", "وَإِنِّي كُلَّمَا دَعَوۡتُهُمۡ لِتَغۡفِرَ لَهُمۡ جَعَلُوٓاْ أَصَٰبِعَهُمۡ فِيٓ ءَاذَانِهِمۡ وَٱسۡتَغۡشَوۡاْ ثِيَابَهُمۡ وَأَصَرُّواْ وَٱسۡتَكۡبَرُواْ ٱسۡتِكۡبَارٗا", "Wainnee kullama daAAawtuhum litaghfira lahum jaAAaloo asabiAAahum fee athanihim waistaghshaw thiyabahum waasarroo waistakbaroo istikbaran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (71, 8, "Then I invited them publicly", "ثُمَّ إِنِّي دَعَوۡتُهُمۡ جِهَارٗا", "Thumma innee daAAawtuhum jiharan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (71, 9, "Then I announced to them and [also] confided to them secretly", "ثُمَّ إِنِّيٓ أَعۡلَنتُ لَهُمۡ وَأَسۡرَرۡتُ لَهُمۡ إِسۡرَارٗا", "Thumma innee aAAlantu lahum waasrartu lahum israran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (71, 10, "And said, 'Ask forgiveness of your Lord. Indeed, He is ever a Perpetual Forgiver", "فَقُلۡتُ ٱسۡتَغۡفِرُواْ رَبَّكُمۡ إِنَّهُۥ كَانَ غَفَّارٗا", "Faqultu istaghfiroo rabbakum innahu kana ghaffaran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (71, 11, "He will send [rain from] the sky upon you in [continuing] showers", "يُرۡسِلِ ٱلسَّمَآءَ عَلَيۡكُم مِّدۡرَارٗا", "Yursili alssamaa AAalaykum midraran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (71, 12, "And give you increase in wealth and children and provide for you gardens and provide for you rivers", "وَيُمۡدِدۡكُم بِأَمۡوَٰلٖ وَبَنِينَ وَيَجۡعَل لَّكُمۡ جَنَّـٰتٖ وَيَجۡعَل لَّكُمۡ أَنۡهَٰرٗا", "Wayumdidkum biamwalin wabaneena wayajAAal lakum jannatin wayajAAal lakum anharan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (71, 13, "What is [the matter] with you that you do not attribute to Allah [due] grandeur", "مَّا لَكُمۡ لَا تَرۡجُونَ لِلَّهِ وَقَارٗا", "Ma lakum la tarjoona lillahi waqaran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (71, 14, "While He has created you in stages", "وَقَدۡ خَلَقَكُمۡ أَطۡوَارًا", "Waqad khalaqakum atwaran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (71, 15, "Do you not consider how Allah has created seven heavens in layers", "أَلَمۡ تَرَوۡاْ كَيۡفَ خَلَقَ ٱللَّهُ سَبۡعَ سَمَٰوَٰتٖ طِبَاقٗا", "Alam taraw kayfa khalaqa Allahu sabAAa samawatin tibaqan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (71, 16, "And made the moon therein a [reflected] light and made the sun a burning lamp", "وَجَعَلَ ٱلۡقَمَرَ فِيهِنَّ نُورٗا وَجَعَلَ ٱلشَّمۡسَ سِرَاجٗا", "WajaAAala alqamara feehinna nooran wajaAAala alshshamsa sirajan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (71, 17, "And Allah has caused you to grow from the earth a [progressive] growth", "وَٱللَّهُ أَنۢبَتَكُم مِّنَ ٱلۡأَرۡضِ نَبَاتٗا", "WaAllahu anbatakum mina alardi nabatan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (71, 18, "Then He will return you into it and extract you [another] extraction", "ثُمَّ يُعِيدُكُمۡ فِيهَا وَيُخۡرِجُكُمۡ إِخۡرَاجٗا", "Thumma yuAAeedukum feeha wayukhrijukum ikhrajan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (71, 19, "And Allah has made for you the earth an expanse", "وَٱللَّهُ جَعَلَ لَكُمُ ٱلۡأَرۡضَ بِسَاطٗا", "WaAllahu jaAAala lakumu alarda bisatan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (71, 20, "That you may follow therein roads of passage", "لِّتَسۡلُكُواْ مِنۡهَا سُبُلٗا فِجَاجٗا", "Litaslukoo minha subulan fijajan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (71, 21, "Noah said, ""My Lord, indeed they have disobeyed me and followed him whose wealth and children will not increase him except in loss", "قَالَ نُوحٞ رَّبِّ إِنَّهُمۡ عَصَوۡنِي وَٱتَّبَعُواْ مَن لَّمۡ يَزِدۡهُ مَالُهُۥ وَوَلَدُهُۥٓ إِلَّا خَسَارٗا", "Qala noohun rabbi innahum AAasawnee waittabaAAoo man lam yazidhu maluhu wawaladuhu illa khasaran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (71, 22, "And they conspired an immense conspiracy", "وَمَكَرُواْ مَكۡرٗا كُبَّارٗا", "Wamakaroo makran kubbaran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (71, 23, "And said, 'Never leave your gods and never leave Wadd or Suwa' or Yaghuth and Ya'uq and Nasr", "وَقَالُواْ لَا تَذَرُنَّ ءَالِهَتَكُمۡ وَلَا تَذَرُنَّ وَدّٗا وَلَا سُوَاعٗا وَلَا يَغُوثَ وَيَعُوقَ وَنَسۡرٗا", "Waqaloo la tatharunna alihatakum wala tatharunna waddan wala suwaAAan wala yaghootha wayaAAooqa wanasran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (71, 24, "And already they have misled many. And, [my Lord], do not increase the wrongdoers except in error", "وَقَدۡ أَضَلُّواْ كَثِيرٗاۖ وَلَا تَزِدِ ٱلظَّـٰلِمِينَ إِلَّا ضَلَٰلٗا", "Waqad adalloo katheeran wala tazidi alththalimeena illa dalalan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (71, 25, "Because of their sins they were drowned and put into the Fire, and they found not for themselves besides Allah [any] helpers", "مِّمَّا خَطِيٓـَٰٔتِهِمۡ أُغۡرِقُواْ فَأُدۡخِلُواْ نَارٗا فَلَمۡ يَجِدُواْ لَهُم مِّن دُونِ ٱللَّهِ أَنصَارٗا", "Mimma khateeatihim oghriqoo faodkhiloo naran falam yajidoo lahum min dooni Allahi ansaran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (71, 26, "And Noah said, ""My Lord, do not leave upon the earth from among the disbelievers an inhabitant", "وَقَالَ نُوحٞ رَّبِّ لَا تَذَرۡ عَلَى ٱلۡأَرۡضِ مِنَ ٱلۡكَٰفِرِينَ دَيَّارًا", "Waqala noohun rabbi la tathar AAala alardi mina alkafireena dayyaran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (71, 27, "Indeed, if You leave them, they will mislead Your servants and not beget except [every] wicked one and [confirmed] disbeliever", "إِنَّكَ إِن تَذَرۡهُمۡ يُضِلُّواْ عِبَادَكَ وَلَا يَلِدُوٓاْ إِلَّا فَاجِرٗا كَفَّارٗا", "Innaka in tatharhum yudilloo AAibadaka wala yalidoo illa fajiran kaffaran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (71, 28, "My Lord, forgive me and my parents and whoever enters my house a believer and the believing men and believing women. And do not increase the wrongdoers except in destruction", "رَّبِّ ٱغۡفِرۡ لِي وَلِوَٰلِدَيَّ وَلِمَن دَخَلَ بَيۡتِيَ مُؤۡمِنٗا وَلِلۡمُؤۡمِنِينَ وَٱلۡمُؤۡمِنَٰتِۖ وَلَا تَزِدِ ٱلظَّـٰلِمِينَ إِلَّا تَبَارَۢا", "Rabbi ighfir lee waliwalidayya waliman dakhala baytiya muminan walilmumineena waalmuminati wala tazidi alththalimeena illa tabaran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (72, 1, "Say, [O Muhammad], ""It has been revealed to me that a group of the jinn listened and said, 'Indeed, we have heard an amazing Qur'an", "قُلۡ أُوحِيَ إِلَيَّ أَنَّهُ ٱسۡتَمَعَ نَفَرٞ مِّنَ ٱلۡجِنِّ فَقَالُوٓاْ إِنَّا سَمِعۡنَا قُرۡءَانًا عَجَبٗا", "Qul oohiya ilayya annahu istamaAAa nafarun mina aljinni faqaloo inna samiAAna quranan AAajaban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (72, 2, "It guides to the right course, and we have believed in it. And we will never associate with our Lord anyone", "يَهۡدِيٓ إِلَى ٱلرُّشۡدِ فَـَٔامَنَّا بِهِۦۖ وَلَن نُّشۡرِكَ بِرَبِّنَآ أَحَدٗا", "Yahdee ila alrrushdi faamanna bihi walan nushrika birabbina ahadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (72, 3, "And [it teaches] that exalted is the nobleness of our Lord; He has not taken a wife or a son", "وَأَنَّهُۥ تَعَٰلَىٰ جَدُّ رَبِّنَا مَا ٱتَّخَذَ صَٰحِبَةٗ وَلَا وَلَدٗا", "Waannahu taAAala jaddu rabbina ma ittakhatha sahibatan wala waladan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (72, 4, "And that our foolish one has been saying about Allah an excessive transgression", "وَأَنَّهُۥ كَانَ يَقُولُ سَفِيهُنَا عَلَى ٱللَّهِ شَطَطٗا", "Waannahu kana yaqoolu safeehuna AAala Allahi shatatan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (72, 5, "And we had thought that mankind and the jinn would never speak about Allah a lie", "وَأَنَّا ظَنَنَّآ أَن لَّن تَقُولَ ٱلۡإِنسُ وَٱلۡجِنُّ عَلَى ٱللَّهِ كَذِبٗا", "Waanna thananna an lan taqoola alinsu waaljinnu AAala Allahi kathiban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (72, 6, "And there were men from mankind who sought refuge in men from the jinn, so they [only] increased them in burden", "وَأَنَّهُۥ كَانَ رِجَالٞ مِّنَ ٱلۡإِنسِ يَعُوذُونَ بِرِجَالٖ مِّنَ ٱلۡجِنِّ فَزَادُوهُمۡ رَهَقٗا", "Waannahu kana rijalun mina alinsi yaAAoothoona birijalin mina aljinni fazadoohum rahaqan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (72, 7, "And they had thought, as you thought, that Allah would never send anyone [as a messenger]", "وَأَنَّهُمۡ ظَنُّواْ كَمَا ظَنَنتُمۡ أَن لَّن يَبۡعَثَ ٱللَّهُ أَحَدٗا", "Waannahum thannoo kama thanantum an lan yabAAatha Allahu ahadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (72, 8, "And we have sought [to reach] the heaven but found it filled with powerful guards and burning flames", "وَأَنَّا لَمَسۡنَا ٱلسَّمَآءَ فَوَجَدۡنَٰهَا مُلِئَتۡ حَرَسٗا شَدِيدٗا وَشُهُبٗا", "Waanna lamasna alssamaa fawajadnaha muliat harasan shadeedan washuhuban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (72, 9, "And we used to sit therein in positions for hearing, but whoever listens now will find a burning flame lying in wait for him", "وَأَنَّا كُنَّا نَقۡعُدُ مِنۡهَا مَقَٰعِدَ لِلسَّمۡعِۖ فَمَن يَسۡتَمِعِ ٱلۡأٓنَ يَجِدۡ لَهُۥ شِهَابٗا رَّصَدٗا", "Waanna kunna naqAAudu minha maqaAAida lilssamAAi faman yastamiAAi alana yajid lahu shihaban rasadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (72, 10, "And we do not know [therefore] whether evil is intended for those on earth or whether their Lord intends for them a right course", "وَأَنَّا لَا نَدۡرِيٓ أَشَرٌّ أُرِيدَ بِمَن فِي ٱلۡأَرۡضِ أَمۡ أَرَادَ بِهِمۡ رَبُّهُمۡ رَشَدٗا", "Waanna la nadree asharrun oreeda biman fee alardi am arada bihim rabbuhum rashadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (72, 11, "And among us are the righteous, and among us are [others] not so; we were [of] divided ways", "وَأَنَّا مِنَّا ٱلصَّـٰلِحُونَ وَمِنَّا دُونَ ذَٰلِكَۖ كُنَّا طَرَآئِقَ قِدَدٗا", "Waanna minna alssalihoona waminna doona thalika kunna taraiqa qidadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (72, 12, "And we have become certain that we will never cause failure to Allah upon earth, nor can we escape Him by flight", "وَأَنَّا ظَنَنَّآ أَن لَّن نُّعۡجِزَ ٱللَّهَ فِي ٱلۡأَرۡضِ وَلَن نُّعۡجِزَهُۥ هَرَبٗا", "Wanna thananna an lan nuAAjiza Allaha fee alardi walan nuAAjizahu haraban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (72, 13, "And when we heard the guidance, we believed in it. And whoever believes in his Lord will not fear deprivation or burden", "وَأَنَّا لَمَّا سَمِعۡنَا ٱلۡهُدَىٰٓ ءَامَنَّا بِهِۦۖ فَمَن يُؤۡمِنۢ بِرَبِّهِۦ فَلَا يَخَافُ بَخۡسٗا وَلَا رَهَقٗا", "Waanna lamma samiAAna alhuda amanna bihi faman yumin birabbihi fala yakhafu bakhsan wala rahaqan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (72, 14, "And among us are Muslims [in submission to Allah], and among us are the unjust. And whoever has become Muslim - those have sought out the right course", "وَأَنَّا مِنَّا ٱلۡمُسۡلِمُونَ وَمِنَّا ٱلۡقَٰسِطُونَۖ فَمَنۡ أَسۡلَمَ فَأُوْلَـٰٓئِكَ تَحَرَّوۡاْ رَشَدٗا", "Waanna minna almuslimoona waminna alqasitoona faman aslama faolaika taharraw rashadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (72, 15, "But as for the unjust, they will be, for Hell, firewood", "وَأَمَّا ٱلۡقَٰسِطُونَ فَكَانُواْ لِجَهَنَّمَ حَطَبٗا", "Waama alqasitoona fakanoo lijahannama hataban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (72, 16, "And [Allah revealed] that if they had remained straight on the way, We would have given them abundant provision", "وَأَلَّوِ ٱسۡتَقَٰمُواْ عَلَى ٱلطَّرِيقَةِ لَأَسۡقَيۡنَٰهُم مَّآءً غَدَقٗا", "Waallawi istaqamoo AAala alttareeqati laasqaynahum maan ghadaqan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (72, 17, "So We might test them therein. And whoever turns away from the remembrance of his Lord He will put into arduous punishment", "لِّنَفۡتِنَهُمۡ فِيهِۚ وَمَن يُعۡرِضۡ عَن ذِكۡرِ رَبِّهِۦ يَسۡلُكۡهُ عَذَابٗا صَعَدٗا", "Linaftinahum feehi waman yuAArid AAan thikri rabbihi yaslukhu AAathaban saAAadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (72, 18, "And [He revealed] that the masjids are for Allah, so do not invoke with Allah anyone", "وَأَنَّ ٱلۡمَسَٰجِدَ لِلَّهِ فَلَا تَدۡعُواْ مَعَ ٱللَّهِ أَحَدٗا", "Waanna almasajida lillahi fala tadAAoo maAAa Allahi ahadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (72, 19, "And that when the Servant of Allah stood up supplicating Him, they almost became about him a compacted mass", "وَأَنَّهُۥ لَمَّا قَامَ عَبۡدُ ٱللَّهِ يَدۡعُوهُ كَادُواْ يَكُونُونَ عَلَيۡهِ لِبَدٗا", "Waannahu lamma qama AAabdu Allahi yadAAoohu kadoo yakoonoona AAalayhi libadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (72, 20, "Say, [O Muhammad], ""I only invoke my Lord and do not associate with Him anyone", "قُلۡ إِنَّمَآ أَدۡعُواْ رَبِّي وَلَآ أُشۡرِكُ بِهِۦٓ أَحَدٗا", "Qul innama adAAoo rabbee wala oshriku bihi ahadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (72, 21, "Say, ""Indeed, I do not possess for you [the power of] harm or right direction", "قُلۡ إِنِّي لَآ أَمۡلِكُ لَكُمۡ ضَرّٗا وَلَا رَشَدٗا", "Qul innee la amliku lakum darran wala rashadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (72, 22, "Say, ""Indeed, there will never protect me from Allah anyone [if I should disobey], nor will I find in other than Him a refuge", "قُلۡ إِنِّي لَن يُجِيرَنِي مِنَ ٱللَّهِ أَحَدٞ وَلَنۡ أَجِدَ مِن دُونِهِۦ مُلۡتَحَدًا", "Qul innee lan yujeeranee mina Allahi ahadun walan ajida min doonihi multahadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (72, 23, "But [I have for you] only notification from Allah, and His messages."" And whoever disobeys Allah and His Messenger - then indeed, for him is the fire of Hell; they will abide therein forever", "إِلَّا بَلَٰغٗا مِّنَ ٱللَّهِ وَرِسَٰلَٰتِهِۦۚ وَمَن يَعۡصِ ٱللَّهَ وَرَسُولَهُۥ فَإِنَّ لَهُۥ نَارَ جَهَنَّمَ خَٰلِدِينَ فِيهَآ أَبَدًا", "Illa balaghan mina Allahi warisalatihi waman yaAAsi Allaha warasoolahu fainna lahu nara jahannama khalideena feeha abadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (72, 24, "[The disbelievers continue] until, when they see that which they are promised, then they will know who is weaker in helpers and less in number", "حَتَّىٰٓ إِذَا رَأَوۡاْ مَا يُوعَدُونَ فَسَيَعۡلَمُونَ مَنۡ أَضۡعَفُ نَاصِرٗا وَأَقَلُّ عَدَدٗا", "Hatta itha raaw ma yooAAadoona fasayaAAlamoona man adAAafu nasiran waaqallu AAadadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (72, 25, "Say, ""I do not know if what you are promised is near or if my Lord will grant for it a [long] period", "قُلۡ إِنۡ أَدۡرِيٓ أَقَرِيبٞ مَّا تُوعَدُونَ أَمۡ يَجۡعَلُ لَهُۥ رَبِّيٓ أَمَدًا", "Qul in adree aqareebun ma tooAAadoona am yajAAalu lahu rabbee amadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (72, 26, "[He is] Knower of the unseen, and He does not disclose His [knowledge of the] unseen to anyone", "عَٰلِمُ ٱلۡغَيۡبِ فَلَا يُظۡهِرُ عَلَىٰ غَيۡبِهِۦٓ أَحَدًا", "AAalimu alghaybi fala yuthhiru AAala ghaybihi ahadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (72, 27, "Except whom He has approved of messengers, and indeed, He sends before each messenger and behind him observers", "إِلَّا مَنِ ٱرۡتَضَىٰ مِن رَّسُولٖ فَإِنَّهُۥ يَسۡلُكُ مِنۢ بَيۡنِ يَدَيۡهِ وَمِنۡ خَلۡفِهِۦ رَصَدٗا", "Illa mani irtada min rasoolin fainnahu yasluku min bayni yadayhi wamin khalfihi rasadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (72, 28, "That he may know that they have conveyed the messages of their Lord; and He has encompassed whatever is with them and has enumerated all things in number", "لِّيَعۡلَمَ أَن قَدۡ أَبۡلَغُواْ رِسَٰلَٰتِ رَبِّهِمۡ وَأَحَاطَ بِمَا لَدَيۡهِمۡ وَأَحۡصَىٰ كُلَّ شَيۡءٍ عَدَدَۢا", "LiyaAAlama an qad ablaghoo risalati rabbihim waahata bima ladayhim waahsa kulla shayin AAadadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (73, 1, "O you who wraps himself [in clothing]", "يَـٰٓأَيُّهَا ٱلۡمُزَّمِّلُ", "Ya ayyuha almuzzammilu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (73, 2, "Arise [to pray] the night, except for a little", "قُمِ ٱلَّيۡلَ إِلَّا قَلِيلٗا", "Qumi allayla illa qaleelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (73, 3, "Half of it - or subtract from it a little", "نِّصۡفَهُۥٓ أَوِ ٱنقُصۡ مِنۡهُ قَلِيلًا", "Nisfahu awi onqus minhu qaleelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (73, 4, "Or add to it, and recite the Qur'an with measured recitation", "أَوۡ زِدۡ عَلَيۡهِ وَرَتِّلِ ٱلۡقُرۡءَانَ تَرۡتِيلًا", "Aw zid AAalayhi warattili alqurana tarteelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (73, 5, "Indeed, We will cast upon you a heavy word", "إِنَّا سَنُلۡقِي عَلَيۡكَ قَوۡلٗا ثَقِيلًا", "Inna sanulqee AAalayka qawlan thaqeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (73, 6, "Indeed, the hours of the night are more effective for concurrence [of heart and tongue] and more suitable for words", "إِنَّ نَاشِئَةَ ٱلَّيۡلِ هِيَ أَشَدُّ وَطۡـٔٗا وَأَقۡوَمُ قِيلًا", "Inna nashiata allayli hiya ashaddu watan waaqwamu qeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (73, 7, "Indeed, for you by day is prolonged occupation", "إِنَّ لَكَ فِي ٱلنَّهَارِ سَبۡحٗا طَوِيلٗا", "Inna laka fee alnnahari sabhan taweelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (73, 8, "And remember the name of your Lord and devote yourself to Him with [complete] devotion", "وَٱذۡكُرِ ٱسۡمَ رَبِّكَ وَتَبَتَّلۡ إِلَيۡهِ تَبۡتِيلٗا", "Waothkuri isma rabbika watabattal ilayhi tabteelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (73, 9, "[He is] the Lord of the East and the West; there is no deity except Him, so take Him as Disposer of [your] affairs", "رَّبُّ ٱلۡمَشۡرِقِ وَٱلۡمَغۡرِبِ لَآ إِلَٰهَ إِلَّا هُوَ فَٱتَّخِذۡهُ وَكِيلٗا", "Rabbu almashriqi waalmaghribi la ilaha illa huwa faittakhithhu wakeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (73, 10, "And be patient over what they say and avoid them with gracious avoidance", "وَٱصۡبِرۡ عَلَىٰ مَا يَقُولُونَ وَٱهۡجُرۡهُمۡ هَجۡرٗا جَمِيلٗا", "Waisbir AAala ma yaqooloona waohjurhum hajran jameelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (73, 11, "And leave Me with [the matter of] the deniers, those of ease [in life], and allow them respite a little", "وَذَرۡنِي وَٱلۡمُكَذِّبِينَ أُوْلِي ٱلنَّعۡمَةِ وَمَهِّلۡهُمۡ قَلِيلًا", "Watharnee waalmukaththibeena olee alnnaAAmati wamahhilhum qaleelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (73, 12, "Indeed, with Us [for them] are shackles and burning fire", "إِنَّ لَدَيۡنَآ أَنكَالٗا وَجَحِيمٗا", "Inna ladayna ankalan wajaheeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (73, 13, "And food that chokes and a painful punishment", "وَطَعَامٗا ذَا غُصَّةٖ وَعَذَابًا أَلِيمٗا", "WataAAaman tha ghussatin waAAathaban aleeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (73, 14, "On the Day the earth and the mountains will convulse and the mountains will become a heap of sand pouring down", "يَوۡمَ تَرۡجُفُ ٱلۡأَرۡضُ وَٱلۡجِبَالُ وَكَانَتِ ٱلۡجِبَالُ كَثِيبٗا مَّهِيلًا", "Yawma tarjufu alardu waaljibalu wakanati aljibalu katheeban maheelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (73, 15, "Indeed, We have sent to you a Messenger as a witness upon you just as We sent to Pharaoh a messenger", "إِنَّآ أَرۡسَلۡنَآ إِلَيۡكُمۡ رَسُولٗا شَٰهِدًا عَلَيۡكُمۡ كَمَآ أَرۡسَلۡنَآ إِلَىٰ فِرۡعَوۡنَ رَسُولٗا", "Inna arsalna ilaykum rasoolan shahidan AAalaykum kama arsalna ila firAAawna rasoolan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (73, 16, "But Pharaoh disobeyed the messenger, so We seized him with a ruinous seizure", "فَعَصَىٰ فِرۡعَوۡنُ ٱلرَّسُولَ فَأَخَذۡنَٰهُ أَخۡذٗا وَبِيلٗا", "FaAAasa firAAawnu alrrasoola faakhathnahu akhthan wabeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (73, 17, "Then how can you fear, if you disbelieve, a Day that will make the children white- haired", "فَكَيۡفَ تَتَّقُونَ إِن كَفَرۡتُمۡ يَوۡمٗا يَجۡعَلُ ٱلۡوِلۡدَٰنَ شِيبًا", "Fakayfa tattaqoona in kafartum yawman yajAAalu alwildana sheeban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (73, 18, "The heaven will break apart therefrom; ever is His promise fulfilled", "ٱلسَّمَآءُ مُنفَطِرُۢ بِهِۦۚ كَانَ وَعۡدُهُۥ مَفۡعُولًا", "Alssamao munfatirun bihi kana waAAduhu mafAAoolan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (73, 19, "Indeed, this is a reminder, so whoever wills may take to his Lord a way", "إِنَّ هَٰذِهِۦ تَذۡكِرَةٞۖ فَمَن شَآءَ ٱتَّخَذَ إِلَىٰ رَبِّهِۦ سَبِيلًا", "Inna hathihi tathkiratun faman shaa ittakhatha ila rabbihi sabeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (73, 20, "Indeed, your Lord knows, [O Muhammad], that you stand [in prayer] almost two thirds of the night or half of it or a third of it, and [so do] a group of those with you. And Allah determines [the extent of] the night and the day. He has known that you [Muslims] will not be able to do it and has turned to you in forgiveness, so recite what is easy [for you] of the Qur'an. He has known that there will be among you those who are ill and others traveling throughout the land seeking [something] of the bounty of Allah and others fighting for the cause of Allah. So recite what is easy from it and establish prayer and give zakah and loan Allah a goodly loan. And whatever good you put forward for yourselves - you will find it with Allah. It is better and greater in reward. And seek forgiveness of Allah. Indeed, Allah is Forgiving and Merciful", "۞إِنَّ رَبَّكَ يَعۡلَمُ أَنَّكَ تَقُومُ أَدۡنَىٰ مِن ثُلُثَيِ ٱلَّيۡلِ وَنِصۡفَهُۥ وَثُلُثَهُۥ وَطَآئِفَةٞ مِّنَ ٱلَّذِينَ مَعَكَۚ وَٱللَّهُ يُقَدِّرُ ٱلَّيۡلَ وَٱلنَّهَارَۚ عَلِمَ أَن لَّن تُحۡصُوهُ فَتَابَ عَلَيۡكُمۡۖ فَٱقۡرَءُواْ مَا تَيَسَّرَ مِنَ ٱلۡقُرۡءَانِۚ عَلِمَ أَن سَيَكُونُ مِنكُم مَّرۡضَىٰ وَءَاخَرُونَ يَضۡرِبُونَ فِي ٱلۡأَرۡضِ يَبۡتَغُونَ مِن فَضۡلِ ٱللَّهِ وَءَاخَرُونَ يُقَٰتِلُونَ فِي سَبِيلِ ٱللَّهِۖ فَٱقۡرَءُواْ مَا تَيَسَّرَ مِنۡهُۚ وَأَقِيمُواْ ٱلصَّلَوٰةَ وَءَاتُواْ ٱلزَّكَوٰةَ وَأَقۡرِضُواْ ٱللَّهَ قَرۡضًا حَسَنٗاۚ وَمَا تُقَدِّمُواْ لِأَنفُسِكُم مِّنۡ خَيۡرٖ تَجِدُوهُ عِندَ ٱللَّهِ هُوَ خَيۡرٗا وَأَعۡظَمَ أَجۡرٗاۚ وَٱسۡتَغۡفِرُواْ ٱللَّهَۖ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمُۢ", "Inna rabbaka yaAAlamu annaka taqoomu adna min thuluthayi allayli wanisfahu wathuluthahu wataifatun mina allatheena maAAaka waAllahu yuqaddiru allayla waalnnahara AAalima an lan tuhsoohu fataba AAalaykum faiqraoo ma tayassara mina alqurani AAalima an sayakoonu minkum marda waakharoona yadriboona fee alardi yabtaghoona min fadli Allahi waakharoona yuqatiloona fee sabeeli Allahi faiqraoo ma tayassara minhu waaqeemoo alssalata waatoo alzzakata waaqridoo Allaha qardan hasanan wama tuqaddimoo lianfusikum min khayrin tajidoohu AAinda Allahi huwa khayran waaAAthama ajran waistaghfiroo Allaha inna Allaha ghafoorun raheemun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 1, "O you who covers himself [with a garment]", "يَـٰٓأَيُّهَا ٱلۡمُدَّثِّرُ", "Ya ayyuha almuddaththiru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 2, "Arise and warn", "قُمۡ فَأَنذِرۡ", "Qum faanthir");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 3, "And your Lord glorify", "وَرَبَّكَ فَكَبِّرۡ", "Warabbaka fakabbir");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 4, "And your clothing purify", "وَثِيَابَكَ فَطَهِّرۡ", "Wathiyabaka fatahhir");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 5, "And uncleanliness avoid", "وَٱلرُّجۡزَ فَٱهۡجُرۡ", "Waalrrujza faohjur");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 6, "And do not confer favor to acquire more", "وَلَا تَمۡنُن تَسۡتَكۡثِرُ", "Wala tamnun tastakthiru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 7, "But for your Lord be patient", "وَلِرَبِّكَ فَٱصۡبِرۡ", "Walirabbika faisbir");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 8, "And when the trumpet is blown", "فَإِذَا نُقِرَ فِي ٱلنَّاقُورِ", "Faitha nuqira fee alnnaqoori");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 9, "That Day will be a difficult day", "فَذَٰلِكَ يَوۡمَئِذٖ يَوۡمٌ عَسِيرٌ", "Fathalika yawmaithin yawmun AAaseerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 10, "For the disbelievers - not easy", "عَلَى ٱلۡكَٰفِرِينَ غَيۡرُ يَسِيرٖ", "AAala alkafireena ghayru yaseerin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 11, "Leave Me with the one I created alone", "ذَرۡنِي وَمَنۡ خَلَقۡتُ وَحِيدٗا", "Tharnee waman khalaqtu waheedan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 12, "And to whom I granted extensive wealth", "وَجَعَلۡتُ لَهُۥ مَالٗا مَّمۡدُودٗا", "WajaAAaltu lahu malan mamdoodan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 13, "And children present [with him]", "وَبَنِينَ شُهُودٗا", "Wabaneena shuhoodan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 14, "And spread [everything] before him, easing [his life]", "وَمَهَّدتُّ لَهُۥ تَمۡهِيدٗا", "Wamahhadtu lahu tamheedan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 15, "Then he desires that I should add more", "ثُمَّ يَطۡمَعُ أَنۡ أَزِيدَ", "Thumma yatmaAAu an azeeda");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 16, "No! Indeed, he has been toward Our verses obstinate", "كَلَّآۖ إِنَّهُۥ كَانَ لِأٓيَٰتِنَا عَنِيدٗا", "Kalla innahu kana liayatina AAaneedan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 17, "I will cover him with arduous torment", "سَأُرۡهِقُهُۥ صَعُودًا", "Saorhiquhu saAAoodan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 18, "Indeed, he thought and deliberated", "إِنَّهُۥ فَكَّرَ وَقَدَّرَ", "Innahu fakkara waqaddara");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 19, "So may he be destroyed [for] how he deliberated", "فَقُتِلَ كَيۡفَ قَدَّرَ", "Faqutila kayfa qaddara");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 20, "Then may he be destroyed [for] how he deliberated", "ثُمَّ قُتِلَ كَيۡفَ قَدَّرَ", "Thumma qutila kayfa qaddara");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 21, "Then he considered [again]", "ثُمَّ نَظَرَ", "Thumma nathara");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 22, "Then he frowned and scowled", "ثُمَّ عَبَسَ وَبَسَرَ", "Thumma AAabasa wabasara");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 23, "Then he turned back and was arrogant", "ثُمَّ أَدۡبَرَ وَٱسۡتَكۡبَرَ", "Thumma adbara waistakbara");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 24, "And said, ""This is not but magic imitated [from others]", "فَقَالَ إِنۡ هَٰذَآ إِلَّا سِحۡرٞ يُؤۡثَرُ", "Faqala in hatha illa sihrun yutharu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 25, "This is not but the word of a human being", "إِنۡ هَٰذَآ إِلَّا قَوۡلُ ٱلۡبَشَرِ", "In hatha illa qawlu albashari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 26, "I will drive him into Saqar", "سَأُصۡلِيهِ سَقَرَ", "Saosleehi saqara");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 27, "And what can make you know what is Saqar", "وَمَآ أَدۡرَىٰكَ مَا سَقَرُ", "Wama adraka ma saqaru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 28, "It lets nothing remain and leaves nothing [unburned]", "لَا تُبۡقِي وَلَا تَذَرُ", "La tubqee wala tatharu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 29, "Blackening the skins", "لَوَّاحَةٞ لِّلۡبَشَرِ", "Lawwahatun lilbashari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 30, "Over it are nineteen [angels]", "عَلَيۡهَا تِسۡعَةَ عَشَرَ", "AAalayha tisAAata AAashara");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 31, "And We have not made the keepers of the Fire except angels. And We have not made their number except as a trial for those who disbelieve - that those who were given the Scripture will be convinced and those who have believed will increase in faith and those who were given the Scripture and the believers will not doubt and that those in whose hearts is hypocrisy and the disbelievers will say, ""What does Allah intend by this as an example?"" Thus does Allah leave astray whom He wills and guides whom He wills. And none knows the soldiers of your Lord except Him. And mention of the Fire is not but a reminder to humanity", "وَمَا جَعَلۡنَآ أَصۡحَٰبَ ٱلنَّارِ إِلَّا مَلَـٰٓئِكَةٗۖ وَمَا جَعَلۡنَا عِدَّتَهُمۡ إِلَّا فِتۡنَةٗ لِّلَّذِينَ كَفَرُواْ لِيَسۡتَيۡقِنَ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ وَيَزۡدَادَ ٱلَّذِينَ ءَامَنُوٓاْ إِيمَٰنٗا وَلَا يَرۡتَابَ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ وَٱلۡمُؤۡمِنُونَ وَلِيَقُولَ ٱلَّذِينَ فِي قُلُوبِهِم مَّرَضٞ وَٱلۡكَٰفِرُونَ مَاذَآ أَرَادَ ٱللَّهُ بِهَٰذَا مَثَلٗاۚ كَذَٰلِكَ يُضِلُّ ٱللَّهُ مَن يَشَآءُ وَيَهۡدِي مَن يَشَآءُۚ وَمَا يَعۡلَمُ جُنُودَ رَبِّكَ إِلَّا هُوَۚ وَمَا هِيَ إِلَّا ذِكۡرَىٰ لِلۡبَشَرِ", "Wama jaAAalna ashaba alnnari illa malaikatan wama jaAAalna AAiddatahum illa fitnatan lillatheena kafaroo liyastayqina allatheena ootoo alkitaba wayazdada allatheena amanoo eemanan wala yartaba allatheena ootoo alkitaba waalmuminoona waliyaqoola allatheena fee quloobihim maradun waalkafiroona matha arada Allahu bihatha mathalan kathalika yudillu Allahu man yashao wayahdee man yashao wama yaAAlamu junooda rabbika illa huwa wama hiya illa thikra lilbashari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 32, "No! By the moon", "كَلَّا وَٱلۡقَمَرِ", "Kalla waalqamari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 33, "And [by] the night when it departs", "وَٱلَّيۡلِ إِذۡ أَدۡبَرَ", "Waallayli ith adbara");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 34, "And [by] the morning when it brightens", "وَٱلصُّبۡحِ إِذَآ أَسۡفَرَ", "Waalssubhi itha asfara");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 35, "Indeed, the Fire is of the greatest [afflictions]", "إِنَّهَا لَإِحۡدَى ٱلۡكُبَرِ", "Innaha laihda alkubari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 36, "As a warning to humanity", "نَذِيرٗا لِّلۡبَشَرِ", "Natheeran lilbashari");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 37, "To whoever wills among you to proceed or stay behind", "لِمَن شَآءَ مِنكُمۡ أَن يَتَقَدَّمَ أَوۡ يَتَأَخَّرَ", "Liman shaa minkum an yataqaddama aw yataakhkhara");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 38, "Every soul, for what it has earned, will be retained", "كُلُّ نَفۡسِۭ بِمَا كَسَبَتۡ رَهِينَةٌ", "Kullu nafsin bima kasabat raheenatun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 39, "Except the companions of the right", "إِلَّآ أَصۡحَٰبَ ٱلۡيَمِينِ", "Illa ashaba alyameeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 40, "[Who will be] in gardens, questioning each other", "فِي جَنَّـٰتٖ يَتَسَآءَلُونَ", "Fee jannatin yatasaaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 41, "About the criminals", "عَنِ ٱلۡمُجۡرِمِينَ", "AAani almujrimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 42, "[And asking them], ""What put you into Saqar", "مَا سَلَكَكُمۡ فِي سَقَرَ", "Ma salakakum fee saqara");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 43, "They will say, ""We were not of those who prayed", "قَالُواْ لَمۡ نَكُ مِنَ ٱلۡمُصَلِّينَ", "Qaloo lam naku mina almusalleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 44, "Nor did we used to feed the poor", "وَلَمۡ نَكُ نُطۡعِمُ ٱلۡمِسۡكِينَ", "Walam naku nutAAimu almiskeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 45, "And we used to enter into vain discourse with those who engaged [in it]", "وَكُنَّا نَخُوضُ مَعَ ٱلۡخَآئِضِينَ", "Wakunna nakhoodu maAAa alkhaideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 46, "And we used to deny the Day of Recompense", "وَكُنَّا نُكَذِّبُ بِيَوۡمِ ٱلدِّينِ", "Wakunna nukaththibu biyawmi alddeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 47, "Until there came to us the certainty", "حَتَّىٰٓ أَتَىٰنَا ٱلۡيَقِينُ", "Hatta atana alyaqeenu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 48, "So there will not benefit them the intercession of [any] intercessors", "فَمَا تَنفَعُهُمۡ شَفَٰعَةُ ٱلشَّـٰفِعِينَ", "Fama tanfaAAuhum shafaAAatu alshshafiAAeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 49, "Then what is [the matter] with them that they are, from the reminder, turning away", "فَمَا لَهُمۡ عَنِ ٱلتَّذۡكِرَةِ مُعۡرِضِينَ", "Fama lahum AAani alttathkirati muAArideena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 50, "As if they were alarmed donkeys", "كَأَنَّهُمۡ حُمُرٞ مُّسۡتَنفِرَةٞ", "Kaannahum humurun mustanfiratun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 51, "Fleeing from a lion", "فَرَّتۡ مِن قَسۡوَرَةِۭ", "Farrat min qaswaratin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 52, "Rather, every person among them desires that he would be given scriptures spread about", "بَلۡ يُرِيدُ كُلُّ ٱمۡرِيٕٖ مِّنۡهُمۡ أَن يُؤۡتَىٰ صُحُفٗا مُّنَشَّرَةٗ", "Bal yureedu kullu imriin minhum an yuta suhufan munashsharatan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 53, "No! But they do not fear the Hereafter", "كَلَّاۖ بَل لَّا يَخَافُونَ ٱلۡأٓخِرَةَ", "Kalla bal la yakhafoona alakhirata");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 54, "No! Indeed, the Qur'an is a reminder", "كَلَّآ إِنَّهُۥ تَذۡكِرَةٞ", "Kalla innahu tathkiratun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 55, "Then whoever wills will remember it", "فَمَن شَآءَ ذَكَرَهُۥ", "Faman shaa thakarahu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (74, 56, "And they will not remember except that Allah wills. He is worthy of fear and adequate for [granting] forgiveness", "وَمَا يَذۡكُرُونَ إِلَّآ أَن يَشَآءَ ٱللَّهُۚ هُوَ أَهۡلُ ٱلتَّقۡوَىٰ وَأَهۡلُ ٱلۡمَغۡفِرَةِ", "Wama yathkuroona illa an yashaa Allahu huwa ahlu alttaqwa waahlu almaghfirati");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 1, "I swear by the Day of Resurrection", "لَآ أُقۡسِمُ بِيَوۡمِ ٱلۡقِيَٰمَةِ", "La oqsimu biyawmi alqiyamati");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 2, "And I swear by the reproaching soul [to the certainty of resurrection]", "وَلَآ أُقۡسِمُ بِٱلنَّفۡسِ ٱللَّوَّامَةِ", "Wala oqsimu bialnnafsi allawwamati");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 3, "Does man think that We will not assemble his bones", "أَيَحۡسَبُ ٱلۡإِنسَٰنُ أَلَّن نَّجۡمَعَ عِظَامَهُۥ", "Ayahsabu alinsanu allan najmaAAa AAithamahu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 4, "Yes. [We are] Able [even] to proportion his fingertips", "بَلَىٰ قَٰدِرِينَ عَلَىٰٓ أَن نُّسَوِّيَ بَنَانَهُۥ", "Bala qadireena AAala an nusawwiya bananahu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 5, "But man desires to continue in sin", "بَلۡ يُرِيدُ ٱلۡإِنسَٰنُ لِيَفۡجُرَ أَمَامَهُۥ", "Bal yureedu alinsanu liyafjura amamahu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 6, "He asks, ""When is the Day of Resurrection", "يَسۡـَٔلُ أَيَّانَ يَوۡمُ ٱلۡقِيَٰمَةِ", "Yasalu ayyana yawmu alqiyamati");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 7, "So when vision is dazzled", "فَإِذَا بَرِقَ ٱلۡبَصَرُ", "Faitha bariqa albasaru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 8, "And the moon darkens", "وَخَسَفَ ٱلۡقَمَرُ", "Wakhasafa alqamaru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 9, "And the sun and the moon are joined", "وَجُمِعَ ٱلشَّمۡسُ وَٱلۡقَمَرُ", "WajumiAAa alshshamsu waalqamaru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 10, "Man will say on that Day, ""Where is the [place of] escape", "يَقُولُ ٱلۡإِنسَٰنُ يَوۡمَئِذٍ أَيۡنَ ٱلۡمَفَرُّ", "Yaqoolu alinsanu yawmaithin ayna almafarru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 11, "No! There is no refuge", "كَلَّا لَا وَزَرَ", "Kalla la wazara");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 12, "To your Lord, that Day, is the [place of] permanence", "إِلَىٰ رَبِّكَ يَوۡمَئِذٍ ٱلۡمُسۡتَقَرُّ", "Ila rabbika yawmaithin almustaqarru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 13, "Man will be informed that Day of what he sent ahead and kept back", "يُنَبَّؤُاْ ٱلۡإِنسَٰنُ يَوۡمَئِذِۭ بِمَا قَدَّمَ وَأَخَّرَ", "Yunabbao alinsanu yawmaithin bima qaddama waakhkhara");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 14, "Rather, man, against himself, will be a witness", "بَلِ ٱلۡإِنسَٰنُ عَلَىٰ نَفۡسِهِۦ بَصِيرَةٞ", "Bali alinsanu AAala nafsihi baseeratun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 15, "Even if he presents his excuses", "وَلَوۡ أَلۡقَىٰ مَعَاذِيرَهُۥ", "Walaw alqa maAAatheerahu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 16, "Move not your tongue with it, [O Muhammad], to hasten with recitation of the Qur'an", "لَا تُحَرِّكۡ بِهِۦ لِسَانَكَ لِتَعۡجَلَ بِهِۦٓ", "La tuharrik bihi lisanaka litaAAjala bihi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 17, "Indeed, upon Us is its collection [in your heart] and [to make possible] its recitation", "إِنَّ عَلَيۡنَا جَمۡعَهُۥ وَقُرۡءَانَهُۥ", "Inna AAalayna jamAAahu waquranahu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 18, "So when We have recited it [through Gabriel], then follow its recitation", "فَإِذَا قَرَأۡنَٰهُ فَٱتَّبِعۡ قُرۡءَانَهُۥ", "Faitha qaranahu faittabiAA quranahu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 19, "Then upon Us is its clarification [to you]", "ثُمَّ إِنَّ عَلَيۡنَا بَيَانَهُۥ", "Thumma inna AAalayna bayanahu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 20, "No! But you love the immediate", "كَلَّا بَلۡ تُحِبُّونَ ٱلۡعَاجِلَةَ", "Kalla bal tuhibboona alAAajilata");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 21, "And leave the Hereafter", "وَتَذَرُونَ ٱلۡأٓخِرَةَ", "Watatharoona alakhirata");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 22, "[Some] faces, that Day, will be radiant", "وُجُوهٞ يَوۡمَئِذٖ نَّاضِرَةٌ", "Wujoohun yawmaithin nadiratun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 23, "Looking at their Lord", "إِلَىٰ رَبِّهَا نَاظِرَةٞ", "Ila rabbiha nathiratun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 24, "And [some] faces, that Day, will be contorted", "وَوُجُوهٞ يَوۡمَئِذِۭ بَاسِرَةٞ", "Wawujoohun yawmaithin basiratun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 25, "Expecting that there will be done to them [something] backbreaking", "تَظُنُّ أَن يُفۡعَلَ بِهَا فَاقِرَةٞ", "Tathunnu an yufAAala biha faqiratun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 26, "No! When the soul has reached the collar bones", "كَلَّآ إِذَا بَلَغَتِ ٱلتَّرَاقِيَ", "Kalla itha balaghati alttaraqiya");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 27, "And it is said, ""Who will cure [him]", "وَقِيلَ مَنۡۜ رَاقٖ", "Waqeela man raqin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 28, "And the dying one is certain that it is the [time of] separation", "وَظَنَّ أَنَّهُ ٱلۡفِرَاقُ", "Wathanna annahu alfiraqu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 29, "And the leg is wound about the leg", "وَٱلۡتَفَّتِ ٱلسَّاقُ بِٱلسَّاقِ", "Wailtaffati alssaqu bialssaqi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 30, "To your Lord, that Day, will be the procession", "إِلَىٰ رَبِّكَ يَوۡمَئِذٍ ٱلۡمَسَاقُ", "Ila rabbika yawmaithin almasaqu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 31, "And the disbeliever had not believed, nor had he prayed", "فَلَا صَدَّقَ وَلَا صَلَّىٰ", "Fala saddaqa wala salla");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 32, "But [instead], he denied and turned away", "وَلَٰكِن كَذَّبَ وَتَوَلَّىٰ", "Walakin kaththaba watawalla");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 33, "And then he went to his people, swaggering [in pride]", "ثُمَّ ذَهَبَ إِلَىٰٓ أَهۡلِهِۦ يَتَمَطَّىٰٓ", "Thumma thahaba ila ahlihi yatamatta");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 34, "Woe to you, and woe", "أَوۡلَىٰ لَكَ فَأَوۡلَىٰ", "Awla laka faawla");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 35, "Then woe to you, and woe", "ثُمَّ أَوۡلَىٰ لَكَ فَأَوۡلَىٰٓ", "Thumma awla laka faawla");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 36, "Does man think that he will be left neglected", "أَيَحۡسَبُ ٱلۡإِنسَٰنُ أَن يُتۡرَكَ سُدًى", "Ayahsabu alinsanu an yutraka sudan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 37, "Had he not been a sperm from semen emitted", "أَلَمۡ يَكُ نُطۡفَةٗ مِّن مَّنِيّٖ يُمۡنَىٰ", "Alam yaku nutfatan min manayyin yumna");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 38, "Then he was a clinging clot, and [Allah] created [his form] and proportioned [him]", "ثُمَّ كَانَ عَلَقَةٗ فَخَلَقَ فَسَوَّىٰ", "Thumma kana AAalaqatan fakhalaqa fasawwa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 39, "And made of him two mates, the male and the female", "فَجَعَلَ مِنۡهُ ٱلزَّوۡجَيۡنِ ٱلذَّكَرَ وَٱلۡأُنثَىٰٓ", "FajaAAala minhu alzzawjayni alththakara waalontha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (75, 40, "Is not that [Creator] Able to give life to the dead", "أَلَيۡسَ ذَٰلِكَ بِقَٰدِرٍ عَلَىٰٓ أَن يُحۡـِۧيَ ٱلۡمَوۡتَىٰ", "Alaysa thalika biqadirin AAala an yuhyiya almawta");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (76, 1, "Has there [not] come upon man a period of time when he was not a thing [even] mentioned", "هَلۡ أَتَىٰ عَلَى ٱلۡإِنسَٰنِ حِينٞ مِّنَ ٱلدَّهۡرِ لَمۡ يَكُن شَيۡـٔٗا مَّذۡكُورًا", "Hal ata AAala alinsani heenun mina alddahri lam yakun shayan mathkooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (76, 2, "Indeed, We created man from a sperm-drop mixture that We may try him; and We made him hearing and seeing", "إِنَّا خَلَقۡنَا ٱلۡإِنسَٰنَ مِن نُّطۡفَةٍ أَمۡشَاجٖ نَّبۡتَلِيهِ فَجَعَلۡنَٰهُ سَمِيعَۢا بَصِيرًا", "Inna khalaqna alinsana min nutfatin amshajin nabtaleehi fajaAAalnahu sameeAAan baseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (76, 3, "Indeed, We guided him to the way, be he grateful or be he ungrateful", "إِنَّا هَدَيۡنَٰهُ ٱلسَّبِيلَ إِمَّا شَاكِرٗا وَإِمَّا كَفُورًا", "Inna hadaynahu alssabeela imma shakiran waimma kafooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (76, 4, "Indeed, We have prepared for the disbelievers chains and shackles and a blaze", "إِنَّآ أَعۡتَدۡنَا لِلۡكَٰفِرِينَ سَلَٰسِلَاْ وَأَغۡلَٰلٗا وَسَعِيرًا", "Inna aAAtadna lilkafireena salasila waaghlalan wasaAAeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (76, 5, "Indeed, the righteous will drink from a cup [of wine] whose mixture is of Kafur", "إِنَّ ٱلۡأَبۡرَارَ يَشۡرَبُونَ مِن كَأۡسٖ كَانَ مِزَاجُهَا كَافُورًا", "Inna alabrara yashraboona min kasin kana mizajuha kafooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (76, 6, "A spring of which the [righteous] servants of Allah will drink; they will make it gush forth in force [and abundance]", "عَيۡنٗا يَشۡرَبُ بِهَا عِبَادُ ٱللَّهِ يُفَجِّرُونَهَا تَفۡجِيرٗا", "AAaynan yashrabu biha AAibadu Allahi yufajjiroonaha tafjeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (76, 7, "They [are those who] fulfill [their] vows and fear a Day whose evil will be widespread", "يُوفُونَ بِٱلنَّذۡرِ وَيَخَافُونَ يَوۡمٗا كَانَ شَرُّهُۥ مُسۡتَطِيرٗا", "Yoofoona bialnnathri wayakhafoona yawman kana sharruhu mustateeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (76, 8, "And they give food in spite of love for it to the needy, the orphan, and the captive", "وَيُطۡعِمُونَ ٱلطَّعَامَ عَلَىٰ حُبِّهِۦ مِسۡكِينٗا وَيَتِيمٗا وَأَسِيرًا", "WayutAAimona alttaAAama AAala hubbihi miskeenan wayateeman waaseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (76, 9, "[Saying], ""We feed you only for the countenance of Allah. We wish not from you reward or gratitude", "إِنَّمَا نُطۡعِمُكُمۡ لِوَجۡهِ ٱللَّهِ لَا نُرِيدُ مِنكُمۡ جَزَآءٗ وَلَا شُكُورًا", "Innama nutAAimukum liwajhi Allahi la nureedu minkum jazaan wala shukooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (76, 10, "Indeed, We fear from our Lord a Day austere and distressful", "إِنَّا نَخَافُ مِن رَّبِّنَا يَوۡمًا عَبُوسٗا قَمۡطَرِيرٗا", "Inna nakhafu min rabbina yawman AAaboosan qamtareeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (76, 11, "So Allah will protect them from the evil of that Day and give them radiance and happiness", "فَوَقَىٰهُمُ ٱللَّهُ شَرَّ ذَٰلِكَ ٱلۡيَوۡمِ وَلَقَّىٰهُمۡ نَضۡرَةٗ وَسُرُورٗا", "Fawaqahumu Allahu sharra thalika alyawmi walaqqahum nadratan wasurooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (76, 12, "And will reward them for what they patiently endured [with] a garden [in Paradise] and silk [garments]", "وَجَزَىٰهُم بِمَا صَبَرُواْ جَنَّةٗ وَحَرِيرٗا", "Wajazahum bima sabaroo jannatan wahareeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (76, 13, "[They will be] reclining therein on adorned couches. They will not see therein any [burning] sun or [freezing] cold", "مُّتَّكِـِٔينَ فِيهَا عَلَى ٱلۡأَرَآئِكِۖ لَا يَرَوۡنَ فِيهَا شَمۡسٗا وَلَا زَمۡهَرِيرٗا", "Muttakieena feeha AAala alaraiki la yarawna feeha shamsan wala zamhareeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (76, 14, "And near above them are its shades, and its [fruit] to be picked will be lowered in compliance", "وَدَانِيَةً عَلَيۡهِمۡ ظِلَٰلُهَا وَذُلِّلَتۡ قُطُوفُهَا تَذۡلِيلٗا", "Wadaniyatan AAalayhim thilaluha wathullilat qutoofuha tathleelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (76, 15, "And there will be circulated among them vessels of silver and cups having been [created] clear [as glass]", "وَيُطَافُ عَلَيۡهِم بِـَٔانِيَةٖ مِّن فِضَّةٖ وَأَكۡوَابٖ كَانَتۡ قَوَارِيرَا۠", "Wayutafu AAalayhim bianiyatin min fiddatin waakwabin kanat qawareera");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (76, 16, "Clear glasses [made] from silver of which they have determined the measure", "قَوَارِيرَاْ مِن فِضَّةٖ قَدَّرُوهَا تَقۡدِيرٗا", "Qawareera min fiddatin qaddarooha taqdeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (76, 17, "And they will be given to drink a cup [of wine] whose mixture is of ginger", "وَيُسۡقَوۡنَ فِيهَا كَأۡسٗا كَانَ مِزَاجُهَا زَنجَبِيلًا", "Wayusqawna feeha kasan kana mizajuha zanjabeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (76, 18, "[From] a fountain within Paradise named Salsabeel", "عَيۡنٗا فِيهَا تُسَمَّىٰ سَلۡسَبِيلٗا", "AAaynan feeha tusamma salsabeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (76, 19, "There will circulate among them young boys made eternal. When you see them, you would think them [as beautiful as] scattered pearls", "۞وَيَطُوفُ عَلَيۡهِمۡ وِلۡدَٰنٞ مُّخَلَّدُونَ إِذَا رَأَيۡتَهُمۡ حَسِبۡتَهُمۡ لُؤۡلُؤٗا مَّنثُورٗا", "Wayatoofu AAalayhim wildanun mukhalladoona itha raaytahum hasibtahum luluan manthooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (76, 20, "And when you look there [in Paradise], you will see pleasure and great dominion", "وَإِذَا رَأَيۡتَ ثَمَّ رَأَيۡتَ نَعِيمٗا وَمُلۡكٗا كَبِيرًا", "Waitha raayta thamma raayta naAAeeman wamulkan kabeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (76, 21, "Upon the inhabitants will be green garments of fine silk and brocade. And they will be adorned with bracelets of silver, and their Lord will give them a purifying drink", "عَٰلِيَهُمۡ ثِيَابُ سُندُسٍ خُضۡرٞ وَإِسۡتَبۡرَقٞۖ وَحُلُّوٓاْ أَسَاوِرَ مِن فِضَّةٖ وَسَقَىٰهُمۡ رَبُّهُمۡ شَرَابٗا طَهُورًا", "AAaliyahum thiyabu sundusin khudrun waistabraqun wahulloo asawira min fiddatin wasaqahum rabbuhum sharaban tahooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (76, 22, "[And it will be said], ""Indeed, this is for you a reward, and your effort has been appreciated", "إِنَّ هَٰذَا كَانَ لَكُمۡ جَزَآءٗ وَكَانَ سَعۡيُكُم مَّشۡكُورًا", "Inna hatha kana lakum jazaan wakana saAAyukum mashkooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (76, 23, "Indeed, it is We who have sent down to you, [O Muhammad], the Qur'an progressively", "إِنَّا نَحۡنُ نَزَّلۡنَا عَلَيۡكَ ٱلۡقُرۡءَانَ تَنزِيلٗا", "Inna nahnu nazzalna AAalayka alqurana tanzeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (76, 24, "So be patient for the decision of your Lord and do not obey from among them a sinner or ungrateful [disbeliever]", "فَٱصۡبِرۡ لِحُكۡمِ رَبِّكَ وَلَا تُطِعۡ مِنۡهُمۡ ءَاثِمًا أَوۡ كَفُورٗا", "Faisbir lihukmi rabbika wala tutiAA minhum athiman aw kafooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (76, 25, "And mention the name of your Lord [in prayer] morning and evening", "وَٱذۡكُرِ ٱسۡمَ رَبِّكَ بُكۡرَةٗ وَأَصِيلٗا", "Waothkuri isma rabbika bukratan waaseelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (76, 26, "And during the night prostrate to Him and exalt Him a long [part of the] night", "وَمِنَ ٱلَّيۡلِ فَٱسۡجُدۡ لَهُۥ وَسَبِّحۡهُ لَيۡلٗا طَوِيلًا", "Wamina allayli faosjud lahu wasabbihhu laylan taweelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (76, 27, "Indeed, these [disbelievers] love the immediate and leave behind them a grave Day", "إِنَّ هَـٰٓؤُلَآءِ يُحِبُّونَ ٱلۡعَاجِلَةَ وَيَذَرُونَ وَرَآءَهُمۡ يَوۡمٗا ثَقِيلٗا", "Inna haolai yuhibboona alAAajilata wayatharoona waraahum yawman thaqeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (76, 28, "We have created them and strengthened their forms, and when We will, We can change their likenesses with [complete] alteration", "نَّحۡنُ خَلَقۡنَٰهُمۡ وَشَدَدۡنَآ أَسۡرَهُمۡۖ وَإِذَا شِئۡنَا بَدَّلۡنَآ أَمۡثَٰلَهُمۡ تَبۡدِيلًا", "Nahnu khalaqnahum washadadna asrahum waitha shina baddalna amthalahum tabdeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (76, 29, "Indeed, this is a reminder, so he who wills may take to his Lord a way", "إِنَّ هَٰذِهِۦ تَذۡكِرَةٞۖ فَمَن شَآءَ ٱتَّخَذَ إِلَىٰ رَبِّهِۦ سَبِيلٗا", "Inna hathihi tathkiratun faman shaa ittakhatha ila rabbihi sabeelan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (76, 30, "And you do not will except that Allah wills. Indeed, Allah is ever Knowing and Wise", "وَمَا تَشَآءُونَ إِلَّآ أَن يَشَآءَ ٱللَّهُۚ إِنَّ ٱللَّهَ كَانَ عَلِيمًا حَكِيمٗا", "Wama tashaoona illa an yashaa Allahu inna Allaha kana AAaleeman hakeeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (76, 31, "He admits whom He wills into His mercy; but the wrongdoers - He has prepared for them a painful punishment", "يُدۡخِلُ مَن يَشَآءُ فِي رَحۡمَتِهِۦۚ وَٱلظَّـٰلِمِينَ أَعَدَّ لَهُمۡ عَذَابًا أَلِيمَۢا", "Yudkhilu man yashao fee rahmatihi waalththalimeena aAAadda lahum AAathaban aleeman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 1, "By those [winds] sent forth in gusts", "وَٱلۡمُرۡسَلَٰتِ عُرۡفٗا", "Waalmursalati AAurfan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 2, "And the winds that blow violently", "فَٱلۡعَٰصِفَٰتِ عَصۡفٗا", "FaalAAasifati AAasfan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 3, "And [by] the winds that spread [clouds]", "وَٱلنَّـٰشِرَٰتِ نَشۡرٗا", "Waalnnashirati nashran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 4, "And those [angels] who bring criterion", "فَٱلۡفَٰرِقَٰتِ فَرۡقٗا", "Faalfariqati farqan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 5, "And those [angels] who deliver a message", "فَٱلۡمُلۡقِيَٰتِ ذِكۡرًا", "Faalmulqiyati thikran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 6, "As justification or warning", "عُذۡرًا أَوۡ نُذۡرًا", "AAuthran aw nuthran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 7, "Indeed, what you are promised is to occur", "إِنَّمَا تُوعَدُونَ لَوَٰقِعٞ", "Innama tooAAadoona lawaqiAAun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 8, "So when the stars are obliterated", "فَإِذَا ٱلنُّجُومُ طُمِسَتۡ", "Faitha alnnujoomu tumisat");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 9, "And when the heaven is opened", "وَإِذَا ٱلسَّمَآءُ فُرِجَتۡ", "Waitha alssamao furijat");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 10, "And when the mountains are blown away", "وَإِذَا ٱلۡجِبَالُ نُسِفَتۡ", "Waitha aljibalu nusifat");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 11, "And when the messengers' time has come", "وَإِذَا ٱلرُّسُلُ أُقِّتَتۡ", "Waitha alrrusulu oqqitat");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 12, "For what Day was it postponed", "لِأَيِّ يَوۡمٍ أُجِّلَتۡ", "Liayyi yawmin ojjilat");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 13, "For the Day of Judgement", "لِيَوۡمِ ٱلۡفَصۡلِ", "Liyawmi alfasli");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 14, "And what can make you know what is the Day of Judgement", "وَمَآ أَدۡرَىٰكَ مَا يَوۡمُ ٱلۡفَصۡلِ", "Wama adraka ma yawmu alfasli");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 15, "Woe, that Day, to the deniers", "وَيۡلٞ يَوۡمَئِذٖ لِّلۡمُكَذِّبِينَ", "Waylun yawmaithin lilmukaththibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 16, "Did We not destroy the former peoples", "أَلَمۡ نُهۡلِكِ ٱلۡأَوَّلِينَ", "Alam nuhliki alawwaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 17, "Then We will follow them with the later ones", "ثُمَّ نُتۡبِعُهُمُ ٱلۡأٓخِرِينَ", "Thumma nutbiAAuhumu alakhireena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 18, "Thus do We deal with the criminals", "كَذَٰلِكَ نَفۡعَلُ بِٱلۡمُجۡرِمِينَ", "Kathalika nafAAalu bialmujrimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 19, "Woe, that Day, to the deniers", "وَيۡلٞ يَوۡمَئِذٖ لِّلۡمُكَذِّبِينَ", "Waylun yawmaithin lilmukaththibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 20, "Did We not create you from a liquid disdained", "أَلَمۡ نَخۡلُقكُّم مِّن مَّآءٖ مَّهِينٖ", "Alam nakhluqkum min main maheenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 21, "And We placed it in a firm lodging", "فَجَعَلۡنَٰهُ فِي قَرَارٖ مَّكِينٍ", "FajaAAalnahu fee qararin makeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 22, "For a known extent", "إِلَىٰ قَدَرٖ مَّعۡلُومٖ", "Ila qadarin maAAloomin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 23, "And We determined [it], and excellent [are We] to determine", "فَقَدَرۡنَا فَنِعۡمَ ٱلۡقَٰدِرُونَ", "Faqadarna faniAAma alqadiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 24, "Woe, that Day, to the deniers", "وَيۡلٞ يَوۡمَئِذٖ لِّلۡمُكَذِّبِينَ", "Waylun yawmaithin lilmukaththibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 25, "Have We not made the earth a container", "أَلَمۡ نَجۡعَلِ ٱلۡأَرۡضَ كِفَاتًا", "Alam najAAali alarda kifatan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 26, "Of the living and the dead", "أَحۡيَآءٗ وَأَمۡوَٰتٗا", "Ahyaan waamwatan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 27, "And We placed therein lofty, firmly set mountains and have given you to drink sweet water", "وَجَعَلۡنَا فِيهَا رَوَٰسِيَ شَٰمِخَٰتٖ وَأَسۡقَيۡنَٰكُم مَّآءٗ فُرَاتٗا", "WajaAAalna feeha rawasiya shamikhatin waasqaynakum maan furatan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 28, "Woe, that Day, to the deniers", "وَيۡلٞ يَوۡمَئِذٖ لِّلۡمُكَذِّبِينَ", "Waylun yawmaithin lilmukaththibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 29, "[They will be told], ""Proceed to that which you used to deny", "ٱنطَلِقُوٓاْ إِلَىٰ مَا كُنتُم بِهِۦ تُكَذِّبُونَ", "Intaliqoo ila ma kuntum bihi tukaththiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 30, "Proceed to a shadow [of smoke] having three columns", "ٱنطَلِقُوٓاْ إِلَىٰ ظِلّٖ ذِي ثَلَٰثِ شُعَبٖ", "Intaliqoo ila thillin thee thalathi shuAAabin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 31, "[But having] no cool shade and availing not against the flame", "لَّا ظَلِيلٖ وَلَا يُغۡنِي مِنَ ٱللَّهَبِ", "La thaleelin wala yughnee mina allahabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 32, "Indeed, it throws sparks [as huge] as a fortress", "إِنَّهَا تَرۡمِي بِشَرَرٖ كَٱلۡقَصۡرِ", "Innaha tarmee bishararin kaalqasri");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 33, "As if they were yellowish [black] camels", "كَأَنَّهُۥ جِمَٰلَتٞ صُفۡرٞ", "Kaannahu jimalatun sufrun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 34, "Woe, that Day, to the deniers", "وَيۡلٞ يَوۡمَئِذٖ لِّلۡمُكَذِّبِينَ", "Waylun yawmaithin lilmukaththibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 35, "This is a Day they will not speak", "هَٰذَا يَوۡمُ لَا يَنطِقُونَ", "Hatha yawmu la yantiqoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 36, "Nor will it be permitted for them to make an excuse", "وَلَا يُؤۡذَنُ لَهُمۡ فَيَعۡتَذِرُونَ", "Wala yuthanu lahum fayaAAtathiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 37, "Woe, that Day, to the deniers", "وَيۡلٞ يَوۡمَئِذٖ لِّلۡمُكَذِّبِينَ", "Waylun yawmaithin lilmukaththibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 38, "This is the Day of Judgement; We will have assembled you and the former peoples", "هَٰذَا يَوۡمُ ٱلۡفَصۡلِۖ جَمَعۡنَٰكُمۡ وَٱلۡأَوَّلِينَ", "Hatha yawmu alfasli jamaAAnakum waalawwaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 39, "So if you have a plan, then plan against Me", "فَإِن كَانَ لَكُمۡ كَيۡدٞ فَكِيدُونِ", "Fain kana lakum kaydun fakeedooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 40, "Woe, that Day, to the deniers", "وَيۡلٞ يَوۡمَئِذٖ لِّلۡمُكَذِّبِينَ", "Waylun yawmaithin lilmukaththibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 41, "Indeed, the righteous will be among shades and springs", "إِنَّ ٱلۡمُتَّقِينَ فِي ظِلَٰلٖ وَعُيُونٖ", "Inna almuttaqeena fee thilalin waAAuyoonin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 42, "And fruits from whatever they desire", "وَفَوَٰكِهَ مِمَّا يَشۡتَهُونَ", "Wafawakiha mimma yashtahoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 43, "[Being told], ""Eat and drink in satisfaction for what you used to do", "كُلُواْ وَٱشۡرَبُواْ هَنِيٓـَٔۢا بِمَا كُنتُمۡ تَعۡمَلُونَ", "Kuloo waishraboo haneean bima kuntum taAAmaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 44, "Indeed, We thus reward the doers of good", "إِنَّا كَذَٰلِكَ نَجۡزِي ٱلۡمُحۡسِنِينَ", "Inna kathalika najzee almuhsineena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 45, "Woe, that Day, to the deniers", "وَيۡلٞ يَوۡمَئِذٖ لِّلۡمُكَذِّبِينَ", "Waylun yawmaithin lilmukaththibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 46, "[O disbelievers], eat and enjoy yourselves a little; indeed, you are criminals", "كُلُواْ وَتَمَتَّعُواْ قَلِيلًا إِنَّكُم مُّجۡرِمُونَ", "Kuloo watamattaAAoo qaleelan innakum mujrimoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 47, "Woe, that Day, to the deniers", "وَيۡلٞ يَوۡمَئِذٖ لِّلۡمُكَذِّبِينَ", "Waylun yawmaithin lilmukaththibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 48, "And when it is said to them, ""Bow [in prayer],"" they do not bow", "وَإِذَا قِيلَ لَهُمُ ٱرۡكَعُواْ لَا يَرۡكَعُونَ", "Waitha qeela lahumu irkaAAoo la yarkaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 49, "Woe, that Day, to the deniers", "وَيۡلٞ يَوۡمَئِذٖ لِّلۡمُكَذِّبِينَ", "Waylun yawmaithin lilmukaththibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (77, 50, "Then in what statement after the Qur'an will they believe", "فَبِأَيِّ حَدِيثِۭ بَعۡدَهُۥ يُؤۡمِنُونَ", "Fabiayyi hadeethin baAAdahu yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 1, "About what are they asking one another", "عَمَّ يَتَسَآءَلُونَ", "AAamma yatasaaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 2, "About the great news", "عَنِ ٱلنَّبَإِ ٱلۡعَظِيمِ", "AAani alnnabai alAAatheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 3, "That over which they are in disagreement", "ٱلَّذِي هُمۡ فِيهِ مُخۡتَلِفُونَ", "Allathee hum feehi mukhtalifoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 4, "No! They are going to know", "كَلَّا سَيَعۡلَمُونَ", "Kalla sayaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 5, "Then, no! They are going to know", "ثُمَّ كَلَّا سَيَعۡلَمُونَ", "Thumma kalla sayaAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 6, "Have We not made the earth a resting place", "أَلَمۡ نَجۡعَلِ ٱلۡأَرۡضَ مِهَٰدٗا", "Alam najAAali alarda mihadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 7, "And the mountains as stakes", "وَٱلۡجِبَالَ أَوۡتَادٗا", "Waaljibala awtadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 8, "And We created you in pairs", "وَخَلَقۡنَٰكُمۡ أَزۡوَٰجٗا", "Wakhalaqnakum azwajan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 9, "And made your sleep [a means for] rest", "وَجَعَلۡنَا نَوۡمَكُمۡ سُبَاتٗا", "WajaAAalna nawmakum subatan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 10, "And made the night as clothing", "وَجَعَلۡنَا ٱلَّيۡلَ لِبَاسٗا", "WajaAAalna allayla libasan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 11, "And made the day for livelihood", "وَجَعَلۡنَا ٱلنَّهَارَ مَعَاشٗا", "WajaAAalna alnnahara maAAashan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 12, "And constructed above you seven strong [heavens]", "وَبَنَيۡنَا فَوۡقَكُمۡ سَبۡعٗا شِدَادٗا", "Wabanayna fawqakum sabAAan shidadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 13, "And made [therein] a burning lamp", "وَجَعَلۡنَا سِرَاجٗا وَهَّاجٗا", "WajaAAalna sirajan wahhajan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 14, "And sent down, from the rain clouds, pouring water", "وَأَنزَلۡنَا مِنَ ٱلۡمُعۡصِرَٰتِ مَآءٗ ثَجَّاجٗا", "Waanzalna mina almuAAsirati maan thajjajan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 15, "That We may bring forth thereby grain and vegetation", "لِّنُخۡرِجَ بِهِۦ حَبّٗا وَنَبَاتٗا", "Linukhrija bihi habban wanabatan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 16, "And gardens of entwined growth", "وَجَنَّـٰتٍ أَلۡفَافًا", "Wajannatin alfafan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 17, "Indeed, the Day of Judgement is an appointed time", "إِنَّ يَوۡمَ ٱلۡفَصۡلِ كَانَ مِيقَٰتٗا", "Inna yawma alfasli kana meeqatan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 18, "The Day the Horn is blown and you will come forth in multitudes", "يَوۡمَ يُنفَخُ فِي ٱلصُّورِ فَتَأۡتُونَ أَفۡوَاجٗا", "Yawma yunfakhu fee alssoori fatatoona afwajan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 19, "And the heaven is opened and will become gateways", "وَفُتِحَتِ ٱلسَّمَآءُ فَكَانَتۡ أَبۡوَٰبٗا", "Wafutihati alssamao fakanat abwaban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 20, "And the mountains are removed and will be [but] a mirage", "وَسُيِّرَتِ ٱلۡجِبَالُ فَكَانَتۡ سَرَابًا", "Wasuyyirati aljibalu fakanat saraban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 21, "Indeed, Hell has been lying in wait", "إِنَّ جَهَنَّمَ كَانَتۡ مِرۡصَادٗا", "Inna jahannama kanat mirsadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 22, "For the transgressors, a place of return", "لِّلطَّـٰغِينَ مَـَٔابٗا", "Lilttagheena maaban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 23, "In which they will remain for ages [unending]", "لَّـٰبِثِينَ فِيهَآ أَحۡقَابٗا", "Labitheena feeha ahqaban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 24, "They will not taste therein [any] coolness or drink", "لَّا يَذُوقُونَ فِيهَا بَرۡدٗا وَلَا شَرَابًا", "La yathooqoona feeha bardan wala sharaban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 25, "Except scalding water and [foul] purulence", "إِلَّا حَمِيمٗا وَغَسَّاقٗا", "Illa hameeman waghassaqan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 26, "An appropriate recompense", "جَزَآءٗ وِفَاقًا", "Jazaan wifaqan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 27, "Indeed, they were not expecting an account", "إِنَّهُمۡ كَانُواْ لَا يَرۡجُونَ حِسَابٗا", "Innahum kanoo la yarjoona hisaban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 28, "And denied Our verses with [emphatic] denial", "وَكَذَّبُواْ بِـَٔايَٰتِنَا كِذَّابٗا", "Wakaththaboo biayatina kiththaban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 29, "But all things We have enumerated in writing", "وَكُلَّ شَيۡءٍ أَحۡصَيۡنَٰهُ كِتَٰبٗا", "Wakulla shayin ahsaynahu kitaban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 30, "So taste [the penalty], and never will We increase you except in torment", "فَذُوقُواْ فَلَن نَّزِيدَكُمۡ إِلَّا عَذَابًا", "Fathooqoo falan nazeedakum illa AAathaban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 31, "Indeed, for the righteous is attainment", "إِنَّ لِلۡمُتَّقِينَ مَفَازًا", "Inna lilmuttaqeena mafazan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 32, "Gardens and grapevines", "حَدَآئِقَ وَأَعۡنَٰبٗا", "Hadaiqa waaAAnaban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 33, "And full-breasted [companions] of equal age", "وَكَوَاعِبَ أَتۡرَابٗا", "WakawaAAiba atraban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 34, "And a full cup", "وَكَأۡسٗا دِهَاقٗا", "Wakasan dihaqan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 35, "No ill speech will they hear therein or any falsehood", "لَّا يَسۡمَعُونَ فِيهَا لَغۡوٗا وَلَا كِذَّـٰبٗا", "La yasmaAAoona feeha laghwan wala kiththaban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 36, "[As] reward from your Lord, [a generous] gift [made due by] account", "جَزَآءٗ مِّن رَّبِّكَ عَطَآءً حِسَابٗا", "Jazaan min rabbika AAataan hisaban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 37, "[From] the Lord of the heavens and the earth and whatever is between them, the Most Merciful. They possess not from Him [authority for] speech", "رَّبِّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا بَيۡنَهُمَا ٱلرَّحۡمَٰنِۖ لَا يَمۡلِكُونَ مِنۡهُ خِطَابٗا", "Rabbi alssamawati waalardi wama baynahuma alrrahmani la yamlikoona minhu khitaban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 38, "The Day that the Spirit and the angels will stand in rows, they will not speak except for one whom the Most Merciful permits, and he will say what is correct", "يَوۡمَ يَقُومُ ٱلرُّوحُ وَٱلۡمَلَـٰٓئِكَةُ صَفّٗاۖ لَّا يَتَكَلَّمُونَ إِلَّا مَنۡ أَذِنَ لَهُ ٱلرَّحۡمَٰنُ وَقَالَ صَوَابٗا", "Yawma yaqoomu alrroohu waalmalaikatu saffan la yatakallamoona illa man athina lahu alrrahmanu waqala sawaban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 39, "That is the True Day; so he who wills may take to his Lord a [way of] return", "ذَٰلِكَ ٱلۡيَوۡمُ ٱلۡحَقُّۖ فَمَن شَآءَ ٱتَّخَذَ إِلَىٰ رَبِّهِۦ مَـَٔابًا", "Thalika alyawmu alhaqqu faman shaa ittakhatha ila rabbihi maaban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (78, 40, "Indeed, We have warned you of a near punishment on the Day when a man will observe what his hands have put forth and the disbeliever will say, ""Oh, I wish that I were dust", "إِنَّآ أَنذَرۡنَٰكُمۡ عَذَابٗا قَرِيبٗا يَوۡمَ يَنظُرُ ٱلۡمَرۡءُ مَا قَدَّمَتۡ يَدَاهُ وَيَقُولُ ٱلۡكَافِرُ يَٰلَيۡتَنِي كُنتُ تُرَٰبَۢا", "Inna antharnakum AAathaban qareeban yawma yanthuru almaro ma qaddamat yadahu wayaqoolu alkafiru ya laytanee kuntu turaban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 1, "By those [angels] who extract with violence", "وَٱلنَّـٰزِعَٰتِ غَرۡقٗا", "WaalnnaziAAati gharqan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 2, "And [by] those who remove with ease", "وَٱلنَّـٰشِطَٰتِ نَشۡطٗا", "Waalnnashitati nashtan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 3, "And [by] those who glide [as if] swimming", "وَٱلسَّـٰبِحَٰتِ سَبۡحٗا", "Waalssabihati sabhan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 4, "And those who race each other in a race", "فَٱلسَّـٰبِقَٰتِ سَبۡقٗا", "Faalssabiqati sabqan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 5, "And those who arrange [each] matter", "فَٱلۡمُدَبِّرَٰتِ أَمۡرٗا", "Faalmudabbirati amran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 6, "On the Day the blast [of the Horn] will convulse [creation]", "يَوۡمَ تَرۡجُفُ ٱلرَّاجِفَةُ", "Yawma tarjufu alrrajifatu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 7, "There will follow it the subsequent [one]", "تَتۡبَعُهَا ٱلرَّادِفَةُ", "TatbaAAuha alrradifatu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 8, "Hearts, that Day, will tremble", "قُلُوبٞ يَوۡمَئِذٖ وَاجِفَةٌ", "Quloobun yawmaithin wajifatun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 9, "Their eyes humbled", "أَبۡصَٰرُهَا خَٰشِعَةٞ", "Absaruha khashiAAatun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 10, "They are [presently] saying, ""Will we indeed be returned to [our] former state [of life]", "يَقُولُونَ أَءِنَّا لَمَرۡدُودُونَ فِي ٱلۡحَافِرَةِ", "Yaqooloona ainna lamardoodoona fee alhafirati");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 11, "Even if we should be decayed bones", "أَءِذَا كُنَّا عِظَٰمٗا نَّخِرَةٗ", "Aitha kunna AAithaman nakhiratan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 12, "They say, ""That, then, would be a losing return", "قَالُواْ تِلۡكَ إِذٗا كَرَّةٌ خَاسِرَةٞ", "Qaloo tilka ithan karratun khasiratun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 13, "Indeed, it will be but one shout", "فَإِنَّمَا هِيَ زَجۡرَةٞ وَٰحِدَةٞ", "Fainnama hiya zajratun wahidatun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 14, "And suddenly they will be [alert] upon the earth's surface", "فَإِذَا هُم بِٱلسَّاهِرَةِ", "Faitha hum bialssahirati");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 15, "Has there reached you the story of Moses", "هَلۡ أَتَىٰكَ حَدِيثُ مُوسَىٰٓ", "Hal ataka hadeethu moosa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 16, "When his Lord called to him in the sacred valley of Tuwa", "إِذۡ نَادَىٰهُ رَبُّهُۥ بِٱلۡوَادِ ٱلۡمُقَدَّسِ طُوًى", "Ith nadahu rabbuhu bialwadi almuqaddasi tuwan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 17, "Go to Pharaoh. Indeed, he has transgressed", "ٱذۡهَبۡ إِلَىٰ فِرۡعَوۡنَ إِنَّهُۥ طَغَىٰ", "Ithhab ila firAAawna innahu tagha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 18, "And say to him, 'Would you [be willing to] purify yourself", "فَقُلۡ هَل لَّكَ إِلَىٰٓ أَن تَزَكَّىٰ", "Faqul hal laka ila an tazakka");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 19, "And let me guide you to your Lord so you would fear [Him]", "وَأَهۡدِيَكَ إِلَىٰ رَبِّكَ فَتَخۡشَىٰ", "Waahdiyaka ila rabbika fatakhsha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 20, "And he showed him the greatest sign", "فَأَرَىٰهُ ٱلۡأٓيَةَ ٱلۡكُبۡرَىٰ", "Faarahu alayata alkubra");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 21, "But Pharaoh denied and disobeyed", "فَكَذَّبَ وَعَصَىٰ", "Fakaththaba waAAasa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 22, "Then he turned his back, striving", "ثُمَّ أَدۡبَرَ يَسۡعَىٰ", "Thumma adbara yasAAa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 23, "And he gathered [his people] and called out", "فَحَشَرَ فَنَادَىٰ", "Fahashara fanada");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 24, "And said, ""I am your most exalted lord", "فَقَالَ أَنَا۠ رَبُّكُمُ ٱلۡأَعۡلَىٰ", "Faqala ana rabbukumu alaAAla");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 25, "So Allah seized him in exemplary punishment for the last and the first [transgression]", "فَأَخَذَهُ ٱللَّهُ نَكَالَ ٱلۡأٓخِرَةِ وَٱلۡأُولَىٰٓ", "Faakhathahu Allahu nakala alakhirati waaloola");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 26, "Indeed in that is a warning for whoever would fear [Allah]", "إِنَّ فِي ذَٰلِكَ لَعِبۡرَةٗ لِّمَن يَخۡشَىٰٓ", "Inna fee thalika laAAibratan liman yakhsha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 27, "Are you a more difficult creation or is the heaven? Allah constructed it", "ءَأَنتُمۡ أَشَدُّ خَلۡقًا أَمِ ٱلسَّمَآءُۚ بَنَىٰهَا", "Aantum ashaddu khalqan ami alssamao banaha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 28, "He raised its ceiling and proportioned it", "رَفَعَ سَمۡكَهَا فَسَوَّىٰهَا", "RafaAAa samkaha fasawwaha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 29, "And He darkened its night and extracted its brightness", "وَأَغۡطَشَ لَيۡلَهَا وَأَخۡرَجَ ضُحَىٰهَا", "Waaghtasha laylaha waakhraja duhaha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 30, "And after that He spread the earth", "وَٱلۡأَرۡضَ بَعۡدَ ذَٰلِكَ دَحَىٰهَآ", "Waalarda baAAda thalika dahaha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 31, "He extracted from it its water and its pasture", "أَخۡرَجَ مِنۡهَا مَآءَهَا وَمَرۡعَىٰهَا", "Akhraja minha maaha wamarAAaha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 32, "And the mountains He set firmly", "وَٱلۡجِبَالَ أَرۡسَىٰهَا", "Waaljibala arsaha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 33, "As provision for you and your grazing livestock", "مَتَٰعٗا لَّكُمۡ وَلِأَنۡعَٰمِكُمۡ", "MataAAan lakum walianAAamikum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 34, "But when there comes the greatest Overwhelming Calamity", "فَإِذَا جَآءَتِ ٱلطَّآمَّةُ ٱلۡكُبۡرَىٰ", "Faitha jaati alttammatu alkubra");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 35, "The Day when man will remember that for which he strove", "يَوۡمَ يَتَذَكَّرُ ٱلۡإِنسَٰنُ مَا سَعَىٰ", "Yawma yatathakkaru alinsanu ma saAAa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 36, "And Hellfire will be exposed for [all] those who see", "وَبُرِّزَتِ ٱلۡجَحِيمُ لِمَن يَرَىٰ", "Waburrizati aljaheemu liman yara");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 37, "So as for he who transgressed", "فَأَمَّا مَن طَغَىٰ", "Faamma man tagha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 38, "And preferred the life of the world", "وَءَاثَرَ ٱلۡحَيَوٰةَ ٱلدُّنۡيَا", "Waathara alhayata alddunya");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 39, "Then indeed, Hellfire will be [his] refuge", "فَإِنَّ ٱلۡجَحِيمَ هِيَ ٱلۡمَأۡوَىٰ", "Fainna aljaheema hiya almawa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 40, "But as for he who feared the position of his Lord and prevented the soul from [unlawful] inclination", "وَأَمَّا مَنۡ خَافَ مَقَامَ رَبِّهِۦ وَنَهَى ٱلنَّفۡسَ عَنِ ٱلۡهَوَىٰ", "Waama man khafa maqama rabbihi wanaha alnnafsa AAani alhawa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 41, "Then indeed, Paradise will be [his] refuge", "فَإِنَّ ٱلۡجَنَّةَ هِيَ ٱلۡمَأۡوَىٰ", "Fainna aljannata hiya almawa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 42, "They ask you, [O Muhammad], about the Hour: when is its arrival", "يَسۡـَٔلُونَكَ عَنِ ٱلسَّاعَةِ أَيَّانَ مُرۡسَىٰهَا", "Yasaloonaka AAani alssaAAati ayyana mursaha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 43, "In what [position] are you that you should mention it", "فِيمَ أَنتَ مِن ذِكۡرَىٰهَآ", "Feema anta min thikraha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 44, "To your Lord is its finality", "إِلَىٰ رَبِّكَ مُنتَهَىٰهَآ", "Ila rabbika muntahaha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 45, "You are only a warner for those who fear it", "إِنَّمَآ أَنتَ مُنذِرُ مَن يَخۡشَىٰهَا", "Innama anta munthiru man yakhshaha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (79, 46, "It will be, on the Day they see it, as though they had not remained [in the world] except for an afternoon or a morning thereof", "كَأَنَّهُمۡ يَوۡمَ يَرَوۡنَهَا لَمۡ يَلۡبَثُوٓاْ إِلَّا عَشِيَّةً أَوۡ ضُحَىٰهَا", "Kaannahum yawma yarawnaha lam yalbathoo illa AAashiyyatan aw duhaha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 1, "The Prophet frowned and turned away", "عَبَسَ وَتَوَلَّىٰٓ", "AAabasa watawalla");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 2, "Because there came to him the blind man, [interrupting]", "أَن جَآءَهُ ٱلۡأَعۡمَىٰ", "An jaahu alaAAma");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 3, "But what would make you perceive, [O Muhammad], that perhaps he might be purified", "وَمَا يُدۡرِيكَ لَعَلَّهُۥ يَزَّكَّىٰٓ", "Wama yudreeka laAAallahu yazzakka");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 4, "Or be reminded and the remembrance would benefit him", "أَوۡ يَذَّكَّرُ فَتَنفَعَهُ ٱلذِّكۡرَىٰٓ", "Aw yaththakkaru fatanfaAAahu alththikra");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 5, "As for he who thinks himself without need", "أَمَّا مَنِ ٱسۡتَغۡنَىٰ", "Amma mani istaghna");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 6, "To him you give attention", "فَأَنتَ لَهُۥ تَصَدَّىٰ", "Faanta lahu tasadda");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 7, "And not upon you [is any blame] if he will not be purified", "وَمَا عَلَيۡكَ أَلَّا يَزَّكَّىٰ", "Wama AAalayka alla yazzakka");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 8, "But as for he who came to you striving [for knowledge]", "وَأَمَّا مَن جَآءَكَ يَسۡعَىٰ", "Waamma man jaaka yasAAa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 9, "While he fears [Allah]", "وَهُوَ يَخۡشَىٰ", "Wahuwa yakhsha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 10, "From him you are distracted", "فَأَنتَ عَنۡهُ تَلَهَّىٰ", "Faanta AAanhu talahha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 11, "No! Indeed, these verses are a reminder", "كَلَّآ إِنَّهَا تَذۡكِرَةٞ", "Kalla innaha tathkiratun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 12, "So whoever wills may remember it", "فَمَن شَآءَ ذَكَرَهُۥ", "Faman shaa thakarahu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 13, "[It is recorded] in honored sheets", "فِي صُحُفٖ مُّكَرَّمَةٖ", "Fee suhufin mukarramatin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 14, "Exalted and purified", "مَّرۡفُوعَةٖ مُّطَهَّرَةِۭ", "MarfooAAatin mutahharatin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 15, "[Carried] by the hands of messenger-angels", "بِأَيۡدِي سَفَرَةٖ", "Biaydee safaratin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 16, "Noble and dutiful", "كِرَامِۭ بَرَرَةٖ", "Kiramin bararatin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 17, "Cursed is man; how disbelieving is he", "قُتِلَ ٱلۡإِنسَٰنُ مَآ أَكۡفَرَهُۥ", "Qutila alinsanu ma akfarahu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 18, "From what substance did He create him", "مِنۡ أَيِّ شَيۡءٍ خَلَقَهُۥ", "Min ayyi shayin khalaqahu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 19, "From a sperm-drop He created him and destined for him", "مِن نُّطۡفَةٍ خَلَقَهُۥ فَقَدَّرَهُۥ", "Min nutfatin khalaqahu faqaddarahu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 20, "Then He eased the way for him", "ثُمَّ ٱلسَّبِيلَ يَسَّرَهُۥ", "Thumma alssabeela yassarahu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 21, "Then He causes his death and provides a grave for him", "ثُمَّ أَمَاتَهُۥ فَأَقۡبَرَهُۥ", "Thumma amatahu faaqbarahu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 22, "Then when He wills, He will resurrect him", "ثُمَّ إِذَا شَآءَ أَنشَرَهُۥ", "Thumma itha shaa ansharahu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 23, "No! Man has not yet accomplished what He commanded him", "كَلَّا لَمَّا يَقۡضِ مَآ أَمَرَهُۥ", "Kalla lamma yaqdi ma amarahu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 24, "Then let mankind look at his food", "فَلۡيَنظُرِ ٱلۡإِنسَٰنُ إِلَىٰ طَعَامِهِۦٓ", "Falyanthuri alinsanu ila taAAamihi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 25, "How We poured down water in torrents", "أَنَّا صَبَبۡنَا ٱلۡمَآءَ صَبّٗا", "Anna sababna almaa sabban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 26, "Then We broke open the earth, splitting [it with sprouts]", "ثُمَّ شَقَقۡنَا ٱلۡأَرۡضَ شَقّٗا", "Thumma shaqaqna alarda shaqqan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 27, "And caused to grow within it grain", "فَأَنۢبَتۡنَا فِيهَا حَبّٗا", "Faanbatna feeha habban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 28, "And grapes and herbage", "وَعِنَبٗا وَقَضۡبٗا", "WaAAinaban waqadban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 29, "And olive and palm trees", "وَزَيۡتُونٗا وَنَخۡلٗا", "Wazaytoonan wanakhlan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 30, "And gardens of dense shrubbery", "وَحَدَآئِقَ غُلۡبٗا", "Wahadaiqa ghulban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 31, "And fruit and grass", "وَفَٰكِهَةٗ وَأَبّٗا", "Wafakihatan waabban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 32, "[As] enjoyment for you and your grazing livestock", "مَّتَٰعٗا لَّكُمۡ وَلِأَنۡعَٰمِكُمۡ", "MataAAan lakum walianAAamikum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 33, "But when there comes the Deafening Blast", "فَإِذَا جَآءَتِ ٱلصَّآخَّةُ", "Faitha jaati alssakhkhatu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 34, "On the Day a man will flee from his brother", "يَوۡمَ يَفِرُّ ٱلۡمَرۡءُ مِنۡ أَخِيهِ", "Yawma yafirru almaro min akheehi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 35, "And his mother and his father", "وَأُمِّهِۦ وَأَبِيهِ", "Waommihi waabeehi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 36, "And his wife and his children", "وَصَٰحِبَتِهِۦ وَبَنِيهِ", "Wasahibatihi wabaneehi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 37, "For every man, that Day, will be a matter adequate for him", "لِكُلِّ ٱمۡرِيٕٖ مِّنۡهُمۡ يَوۡمَئِذٖ شَأۡنٞ يُغۡنِيهِ", "Likulli imriin minhum yawmaithin shanun yughneehi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 38, "[Some] faces, that Day, will be bright", "وُجُوهٞ يَوۡمَئِذٖ مُّسۡفِرَةٞ", "Wujoohun yawmaithin musfiratun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 39, "Laughing, rejoicing at good news", "ضَاحِكَةٞ مُّسۡتَبۡشِرَةٞ", "Dahikatun mustabshiratun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 40, "And [other] faces, that Day, will have upon them dust", "وَوُجُوهٞ يَوۡمَئِذٍ عَلَيۡهَا غَبَرَةٞ", "Wawujoohun yawmaithin AAalayha ghabaratun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 41, "Blackness will cover them", "تَرۡهَقُهَا قَتَرَةٌ", "Tarhaquha qataratun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (80, 42, "Those are the disbelievers, the wicked ones", "أُوْلَـٰٓئِكَ هُمُ ٱلۡكَفَرَةُ ٱلۡفَجَرَةُ", "Olaika humu alkafaratu alfajaratu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (81, 1, "When the sun is wrapped up [in darkness]", "إِذَا ٱلشَّمۡسُ كُوِّرَتۡ", "Itha alshshamsu kuwwirat");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (81, 2, "And when the stars fall, dispersing", "وَإِذَا ٱلنُّجُومُ ٱنكَدَرَتۡ", "Waitha alnnujoomu inkadarat");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (81, 3, "And when the mountains are removed", "وَإِذَا ٱلۡجِبَالُ سُيِّرَتۡ", "Waitha aljibalu suyyirat");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (81, 4, "And when full-term she-camels are neglected", "وَإِذَا ٱلۡعِشَارُ عُطِّلَتۡ", "Waitha alAAisharu AAuttilat");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (81, 5, "And when the wild beasts are gathered", "وَإِذَا ٱلۡوُحُوشُ حُشِرَتۡ", "Waitha alwuhooshu hushirat");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (81, 6, "And when the seas are filled with flame", "وَإِذَا ٱلۡبِحَارُ سُجِّرَتۡ", "Waitha albiharu sujjirat");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (81, 7, "And when the souls are paired", "وَإِذَا ٱلنُّفُوسُ زُوِّجَتۡ", "Waitha alnnufoosu zuwwijat");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (81, 8, "And when the girl [who was] buried alive is asked", "وَإِذَا ٱلۡمَوۡءُۥدَةُ سُئِلَتۡ", "Waitha almawoodatu suilat");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (81, 9, "For what sin she was killed", "بِأَيِّ ذَنۢبٖ قُتِلَتۡ", "Biayyi thanbin qutilat");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (81, 10, "And when the pages are made public", "وَإِذَا ٱلصُّحُفُ نُشِرَتۡ", "Waitha alssuhufu nushirat");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (81, 11, "And when the sky is stripped away", "وَإِذَا ٱلسَّمَآءُ كُشِطَتۡ", "Waitha alssamao kushitat");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (81, 12, "And when Hellfire is set ablaze", "وَإِذَا ٱلۡجَحِيمُ سُعِّرَتۡ", "Waitha aljaheemu suAAAAirat");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (81, 13, "And when Paradise is brought near", "وَإِذَا ٱلۡجَنَّةُ أُزۡلِفَتۡ", "Waitha aljannatu ozlifat");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (81, 14, "A soul will [then] know what it has brought [with it]", "عَلِمَتۡ نَفۡسٞ مَّآ أَحۡضَرَتۡ", "AAalimat nafsun ma ahdarat");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (81, 15, "So I swear by the retreating stars", "فَلَآ أُقۡسِمُ بِٱلۡخُنَّسِ", "Fala oqsimu bialkhunnasi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (81, 16, "Those that run [their courses] and disappear", "ٱلۡجَوَارِ ٱلۡكُنَّسِ", "Aljawari alkunnasi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (81, 17, "And by the night as it closes in", "وَٱلَّيۡلِ إِذَا عَسۡعَسَ", "Waallayli itha AAasAAasa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (81, 18, "And by the dawn when it breathes", "وَٱلصُّبۡحِ إِذَا تَنَفَّسَ", "Waalssubhi itha tanaffasa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (81, 19, "[That] indeed, the Qur'an is a word [conveyed by] a noble messenger", "إِنَّهُۥ لَقَوۡلُ رَسُولٖ كَرِيمٖ", "Innahu laqawlu rasoolin kareemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (81, 20, "[Who is] possessed of power and with the Owner of the Throne, secure [in position]", "ذِي قُوَّةٍ عِندَ ذِي ٱلۡعَرۡشِ مَكِينٖ", "Thee quwwatin AAinda thee alAAarshi makeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (81, 21, "Obeyed there [in the heavens] and trustworthy", "مُّطَاعٖ ثَمَّ أَمِينٖ", "MutaAAin thamma ameenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (81, 22, "And your companion is not [at all] mad", "وَمَا صَاحِبُكُم بِمَجۡنُونٖ", "Wama sahibukum bimajnoonin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (81, 23, "And he has already seen Gabriel in the clear horizon", "وَلَقَدۡ رَءَاهُ بِٱلۡأُفُقِ ٱلۡمُبِينِ", "Walaqad raahu bialofuqi almubeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (81, 24, "And Muhammad is not a withholder of [knowledge of] the unseen", "وَمَا هُوَ عَلَى ٱلۡغَيۡبِ بِضَنِينٖ", "Wama huwa AAala alghaybi bidaneenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (81, 25, "And the Qur'an is not the word of a devil, expelled [from the heavens]", "وَمَا هُوَ بِقَوۡلِ شَيۡطَٰنٖ رَّجِيمٖ", "Wama huwa biqawli shaytanin rajeemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (81, 26, "So where are you going", "فَأَيۡنَ تَذۡهَبُونَ", "Faayna tathhaboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (81, 27, "It is not except a reminder to the worlds", "إِنۡ هُوَ إِلَّا ذِكۡرٞ لِّلۡعَٰلَمِينَ", "In huwa illa thikrun lilAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (81, 28, "For whoever wills among you to take a right course", "لِمَن شَآءَ مِنكُمۡ أَن يَسۡتَقِيمَ", "Liman shaa minkum an yastaqeema");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (81, 29, "And you do not will except that Allah wills - Lord of the worlds", "وَمَا تَشَآءُونَ إِلَّآ أَن يَشَآءَ ٱللَّهُ رَبُّ ٱلۡعَٰلَمِينَ", "Wama tashaoona illa an yashaa Allahu rabbu alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (82, 1, "When the sky breaks apart", "إِذَا ٱلسَّمَآءُ ٱنفَطَرَتۡ", "Itha alssamao infatarat");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (82, 2, "And when the stars fall, scattering", "وَإِذَا ٱلۡكَوَاكِبُ ٱنتَثَرَتۡ", "Waitha alkawakibu intatharat");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (82, 3, "And when the seas are erupted", "وَإِذَا ٱلۡبِحَارُ فُجِّرَتۡ", "Waitha albiharu fujjirat");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (82, 4, "And when the [contents of] graves are scattered", "وَإِذَا ٱلۡقُبُورُ بُعۡثِرَتۡ", "Waitha alqubooru buAAthirat");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (82, 5, "A soul will [then] know what it has put forth and kept back", "عَلِمَتۡ نَفۡسٞ مَّا قَدَّمَتۡ وَأَخَّرَتۡ", "AAalimat nafsun ma qaddamat waakhkharat");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (82, 6, "O mankind, what has deceived you concerning your Lord, the Generous", "يَـٰٓأَيُّهَا ٱلۡإِنسَٰنُ مَا غَرَّكَ بِرَبِّكَ ٱلۡكَرِيمِ", "Ya ayyuha alinsanu ma gharraka birabbika alkareemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (82, 7, "Who created you, proportioned you, and balanced you", "ٱلَّذِي خَلَقَكَ فَسَوَّىٰكَ فَعَدَلَكَ", "Allathee khalaqaka fasawwaka faAAadalaka");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (82, 8, "In whatever form He willed has He assembled you", "فِيٓ أَيِّ صُورَةٖ مَّا شَآءَ رَكَّبَكَ", "Fee ayyi sooratin ma shaa rakkabaka");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (82, 9, "No! But you deny the Recompense", "كَلَّا بَلۡ تُكَذِّبُونَ بِٱلدِّينِ", "Kalla bal tukaththiboona bialddeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (82, 10, "And indeed, [appointed] over you are keepers", "وَإِنَّ عَلَيۡكُمۡ لَحَٰفِظِينَ", "Wainna AAalaykum lahafitheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (82, 11, "Noble and recording", "كِرَامٗا كَٰتِبِينَ", "Kiraman katibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (82, 12, "They know whatever you do", "يَعۡلَمُونَ مَا تَفۡعَلُونَ", "YaAAlamoona ma tafAAaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (82, 13, "Indeed, the righteous will be in pleasure", "إِنَّ ٱلۡأَبۡرَارَ لَفِي نَعِيمٖ", "Inna alabrara lafee naAAeemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (82, 14, "And indeed, the wicked will be in Hellfire", "وَإِنَّ ٱلۡفُجَّارَ لَفِي جَحِيمٖ", "Wainna alfujjara lafee jaheemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (82, 15, "They will [enter to] burn therein on the Day of Recompense", "يَصۡلَوۡنَهَا يَوۡمَ ٱلدِّينِ", "Yaslawnaha yawma alddeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (82, 16, "And never therefrom will they be absent", "وَمَا هُمۡ عَنۡهَا بِغَآئِبِينَ", "Wama hum AAanha bighaibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (82, 17, "And what can make you know what is the Day of Recompense", "وَمَآ أَدۡرَىٰكَ مَا يَوۡمُ ٱلدِّينِ", "Wama adraka ma yawmu alddeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (82, 18, "Then, what can make you know what is the Day of Recompense", "ثُمَّ مَآ أَدۡرَىٰكَ مَا يَوۡمُ ٱلدِّينِ", "Thumma ma adraka ma yawmu alddeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (82, 19, "It is the Day when a soul will not possess for another soul [power to do] a thing; and the command, that Day, is [entirely] with Allah", "يَوۡمَ لَا تَمۡلِكُ نَفۡسٞ لِّنَفۡسٖ شَيۡـٔٗاۖ وَٱلۡأَمۡرُ يَوۡمَئِذٖ لِّلَّهِ", "Yawma la tamliku nafsun linafsin shayan waalamru yawmaithin lillahi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (83, 1, "Woe to those who give less [than due]", "وَيۡلٞ لِّلۡمُطَفِّفِينَ", "Waylun lilmutaffifeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (83, 2, "Who, when they take a measure from people, take in full", "ٱلَّذِينَ إِذَا ٱكۡتَالُواْ عَلَى ٱلنَّاسِ يَسۡتَوۡفُونَ", "Allatheena itha iktaloo AAala alnnasi yastawfoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (83, 3, "But if they give by measure or by weight to them, they cause loss", "وَإِذَا كَالُوهُمۡ أَو وَّزَنُوهُمۡ يُخۡسِرُونَ", "Waitha kaloohum aw wazanoohum yukhsiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (83, 4, "Do they not think that they will be resurrected", "أَلَا يَظُنُّ أُوْلَـٰٓئِكَ أَنَّهُم مَّبۡعُوثُونَ", "Ala yathunnu olaika annahum mabAAoothoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (83, 5, "For a tremendous Day", "لِيَوۡمٍ عَظِيمٖ", "Liyawmin AAatheemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (83, 6, "The Day when mankind will stand before the Lord of the worlds", "يَوۡمَ يَقُومُ ٱلنَّاسُ لِرَبِّ ٱلۡعَٰلَمِينَ", "Yawma yaqoomu alnnasu lirabbi alAAalameena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (83, 7, "No! Indeed, the record of the wicked is in sijjeen", "كَلَّآ إِنَّ كِتَٰبَ ٱلۡفُجَّارِ لَفِي سِجِّينٖ", "Kalla inna kitaba alfujjari lafee sijjeenin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (83, 8, "And what can make you know what is sijjeen", "وَمَآ أَدۡرَىٰكَ مَا سِجِّينٞ", "Wama adraka ma sijjeenun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (83, 9, "It is [their destination recorded in] a register inscribed", "كِتَٰبٞ مَّرۡقُومٞ", "Kitabun marqoomun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (83, 10, "Woe, that Day, to the deniers", "وَيۡلٞ يَوۡمَئِذٖ لِّلۡمُكَذِّبِينَ", "Waylun yawmaithin lilmukaththibeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (83, 11, "Who deny the Day of Recompense", "ٱلَّذِينَ يُكَذِّبُونَ بِيَوۡمِ ٱلدِّينِ", "Allatheena yukaththiboona biyawmi alddeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (83, 12, "And none deny it except every sinful transgressor", "وَمَا يُكَذِّبُ بِهِۦٓ إِلَّا كُلُّ مُعۡتَدٍ أَثِيمٍ", "Wama yukaththibu bihi illa kullu muAAtadin atheemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (83, 13, "When Our verses are recited to him, he says, ""Legends of the former peoples", "إِذَا تُتۡلَىٰ عَلَيۡهِ ءَايَٰتُنَا قَالَ أَسَٰطِيرُ ٱلۡأَوَّلِينَ", "Itha tutla AAalayhi ayatuna qala asateeru alawwaleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (83, 14, "No! Rather, the stain has covered their hearts of that which they were earning", "كَلَّاۖ بَلۡۜ رَانَ عَلَىٰ قُلُوبِهِم مَّا كَانُواْ يَكۡسِبُونَ", "Kalla bal rana AAala quloobihim ma kanoo yaksiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (83, 15, "No! Indeed, from their Lord, that Day, they will be partitioned", "كَلَّآ إِنَّهُمۡ عَن رَّبِّهِمۡ يَوۡمَئِذٖ لَّمَحۡجُوبُونَ", "Kalla innahum AAan rabbihim yawmaithin lamahjooboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (83, 16, "Then indeed, they will [enter and] burn in Hellfire", "ثُمَّ إِنَّهُمۡ لَصَالُواْ ٱلۡجَحِيمِ", "Thumma innahum lasaloo aljaheemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (83, 17, "Then it will be said [to them], ""This is what you used to deny", "ثُمَّ يُقَالُ هَٰذَا ٱلَّذِي كُنتُم بِهِۦ تُكَذِّبُونَ", "Thumma yuqalu hatha allathee kuntum bihi tukaththiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (83, 18, "No! Indeed, the record of the righteous is in 'illiyyun", "كَلَّآ إِنَّ كِتَٰبَ ٱلۡأَبۡرَارِ لَفِي عِلِّيِّينَ", "Kalla inna kitaba alabrari lafee AAilliyyeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (83, 19, "And what can make you know what is 'illiyyun", "وَمَآ أَدۡرَىٰكَ مَا عِلِّيُّونَ", "Wama adraka ma AAilliyyoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (83, 20, "It is [their destination recorded in] a register inscribed", "كِتَٰبٞ مَّرۡقُومٞ", "Kitabun marqoomun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (83, 21, "Which is witnessed by those brought near [to Allah]", "يَشۡهَدُهُ ٱلۡمُقَرَّبُونَ", "Yashhaduhu almuqarraboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (83, 22, "Indeed, the righteous will be in pleasure", "إِنَّ ٱلۡأَبۡرَارَ لَفِي نَعِيمٍ", "Inna alabrara lafee naAAeemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (83, 23, "On adorned couches, observing", "عَلَى ٱلۡأَرَآئِكِ يَنظُرُونَ", "AAala alaraiki yanthuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (83, 24, "You will recognize in their faces the radiance of pleasure", "تَعۡرِفُ فِي وُجُوهِهِمۡ نَضۡرَةَ ٱلنَّعِيمِ", "TaAArifu fee wujoohihim nadrata alnnaAAeemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (83, 25, "They will be given to drink [pure] wine [which was] sealed", "يُسۡقَوۡنَ مِن رَّحِيقٖ مَّخۡتُومٍ", "Yusqawna min raheeqin makhtoomin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (83, 26, "The last of it is musk. So for this let the competitors compete", "خِتَٰمُهُۥ مِسۡكٞۚ وَفِي ذَٰلِكَ فَلۡيَتَنَافَسِ ٱلۡمُتَنَٰفِسُونَ", "Khitamuhu miskun wafee thalika falyatanafasi almutanafisoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (83, 27, "And its mixture is of Tasneem", "وَمِزَاجُهُۥ مِن تَسۡنِيمٍ", "Wamizajuhu min tasneemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (83, 28, "A spring from which those near [to Allah] drink", "عَيۡنٗا يَشۡرَبُ بِهَا ٱلۡمُقَرَّبُونَ", "AAaynan yashrabu biha almuqarraboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (83, 29, "Indeed, those who committed crimes used to laugh at those who believed", "إِنَّ ٱلَّذِينَ أَجۡرَمُواْ كَانُواْ مِنَ ٱلَّذِينَ ءَامَنُواْ يَضۡحَكُونَ", "Inna allatheena ajramoo kanoo mina allatheena amanoo yadhakoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (83, 30, "And when they passed by them, they would exchange derisive glances", "وَإِذَا مَرُّواْ بِهِمۡ يَتَغَامَزُونَ", "Waitha marroo bihim yataghamazoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (83, 31, "And when they returned to their people, they would return jesting", "وَإِذَا ٱنقَلَبُوٓاْ إِلَىٰٓ أَهۡلِهِمُ ٱنقَلَبُواْ فَكِهِينَ", "Waitha inqalaboo ila ahlihimu inqalaboo fakiheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (83, 32, "And when they saw them, they would say, ""Indeed, those are truly lost", "وَإِذَا رَأَوۡهُمۡ قَالُوٓاْ إِنَّ هَـٰٓؤُلَآءِ لَضَآلُّونَ", "Waitha raawhum qaloo inna haolai ladalloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (83, 33, "But they had not been sent as guardians over them", "وَمَآ أُرۡسِلُواْ عَلَيۡهِمۡ حَٰفِظِينَ", "Wama orsiloo AAalayhim hafitheena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (83, 34, "So Today those who believed are laughing at the disbelievers", "فَٱلۡيَوۡمَ ٱلَّذِينَ ءَامَنُواْ مِنَ ٱلۡكُفَّارِ يَضۡحَكُونَ", "Faalyawma allatheena amanoo mina alkuffari yadhakoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (83, 35, "On adorned couches, observing", "عَلَى ٱلۡأَرَآئِكِ يَنظُرُونَ", "AAala alaraiki yanthuroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (83, 36, "Have the disbelievers [not] been rewarded [this Day] for what they used to do", "هَلۡ ثُوِّبَ ٱلۡكُفَّارُ مَا كَانُواْ يَفۡعَلُونَ", "Hal thuwwiba alkuffaru ma kanoo yafAAaloona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (84, 1, "When the sky has split [open]", "إِذَا ٱلسَّمَآءُ ٱنشَقَّتۡ", "Itha alssamao inshaqqat");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (84, 2, "And has responded to its Lord and was obligated [to do so]", "وَأَذِنَتۡ لِرَبِّهَا وَحُقَّتۡ", "Waathinat lirabbiha wahuqqat");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (84, 3, "And when the earth has been extended", "وَإِذَا ٱلۡأَرۡضُ مُدَّتۡ", "Waitha alardu muddat");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (84, 4, "And has cast out that within it and relinquished [it]", "وَأَلۡقَتۡ مَا فِيهَا وَتَخَلَّتۡ", "Waalqat ma feeha watakhallat");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (84, 5, "And has responded to its Lord and was obligated [to do so]", "وَأَذِنَتۡ لِرَبِّهَا وَحُقَّتۡ", "Waathinat lirabbiha wahuqqat");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (84, 6, "O mankind, indeed you are laboring toward your Lord with [great] exertion and will meet it", "يَـٰٓأَيُّهَا ٱلۡإِنسَٰنُ إِنَّكَ كَادِحٌ إِلَىٰ رَبِّكَ كَدۡحٗا فَمُلَٰقِيهِ", "Ya ayyuha alinsanu innaka kadihun ila rabbika kadhan famulaqeehi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (84, 7, "Then as for he who is given his record in his right hand", "فَأَمَّا مَنۡ أُوتِيَ كِتَٰبَهُۥ بِيَمِينِهِۦ", "Faama man ootiya kitabahu biyameenihi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (84, 8, "He will be judged with an easy account", "فَسَوۡفَ يُحَاسَبُ حِسَابٗا يَسِيرٗا", "Fasawfa yuhasabu hisaban yaseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (84, 9, "And return to his people in happiness", "وَيَنقَلِبُ إِلَىٰٓ أَهۡلِهِۦ مَسۡرُورٗا", "Wayanqalibu ila ahlihi masrooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (84, 10, "But as for he who is given his record behind his back", "وَأَمَّا مَنۡ أُوتِيَ كِتَٰبَهُۥ وَرَآءَ ظَهۡرِهِۦ", "Waamma man ootiya kitabahu waraa thahrihi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (84, 11, "He will cry out for destruction", "فَسَوۡفَ يَدۡعُواْ ثُبُورٗا", "Fasawfa yadAAoo thubooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (84, 12, "And [enter to] burn in a Blaze", "وَيَصۡلَىٰ سَعِيرًا", "Wayasla saAAeeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (84, 13, "Indeed, he had [once] been among his people in happiness", "إِنَّهُۥ كَانَ فِيٓ أَهۡلِهِۦ مَسۡرُورًا", "Innahu kana fee ahlihi masrooran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (84, 14, "Indeed, he had thought he would never return [to Allah]", "إِنَّهُۥ ظَنَّ أَن لَّن يَحُورَ", "Innahu thanna an lan yahoora");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (84, 15, "But yes! Indeed, his Lord was ever of him, Seeing", "بَلَىٰٓۚ إِنَّ رَبَّهُۥ كَانَ بِهِۦ بَصِيرٗا", "Bala inna rabbahu kana bihi baseeran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (84, 16, "So I swear by the twilight glow", "فَلَآ أُقۡسِمُ بِٱلشَّفَقِ", "Fala oqsimu bialshshafaqi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (84, 17, "And [by] the night and what it envelops", "وَٱلَّيۡلِ وَمَا وَسَقَ", "Waallayli wama wasaqa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (84, 18, "And [by] the moon when it becomes full", "وَٱلۡقَمَرِ إِذَا ٱتَّسَقَ", "Waalqamari itha ittasaqa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (84, 19, "[That] you will surely experience state after state", "لَتَرۡكَبُنَّ طَبَقًا عَن طَبَقٖ", "Latarkabunna tabaqan AAan tabaqin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (84, 20, "So what is [the matter] with them [that] they do not believe", "فَمَا لَهُمۡ لَا يُؤۡمِنُونَ", "Fama lahum la yuminoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (84, 21, "And when the Qur'an is recited to them, they do not prostrate [to Allah]", "وَإِذَا قُرِئَ عَلَيۡهِمُ ٱلۡقُرۡءَانُ لَا يَسۡجُدُونَۤ۩", "Waitha quria AAalayhimu alquranu la yasjudoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (84, 22, "But those who have disbelieved deny", "بَلِ ٱلَّذِينَ كَفَرُواْ يُكَذِّبُونَ", "Bali allatheena kafaroo yukaththiboona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (84, 23, "And Allah is most knowing of what they keep within themselves", "وَٱللَّهُ أَعۡلَمُ بِمَا يُوعُونَ", "WaAllahu aAAlamu bima yooAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (84, 24, "So give them tidings of a painful punishment", "فَبَشِّرۡهُم بِعَذَابٍ أَلِيمٍ", "Fabashshirhum biAAathabin aleemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (84, 25, "Except for those who believe and do righteous deeds. For them is a reward uninterrupted", "إِلَّا ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ لَهُمۡ أَجۡرٌ غَيۡرُ مَمۡنُونِۭ", "Illa allatheena amanoo waAAamiloo alssalihati lahum ajrun ghayru mamnoonin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (85, 1, "By the sky containing great stars", "وَٱلسَّمَآءِ ذَاتِ ٱلۡبُرُوجِ", "Waalssamai thati alburooji");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (85, 2, "And [by] the promised Day", "وَٱلۡيَوۡمِ ٱلۡمَوۡعُودِ", "Waalyawmi almawAAoodi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (85, 3, "And [by] the witness and what is witnessed", "وَشَاهِدٖ وَمَشۡهُودٖ", "Washahidin wamashhoodin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (85, 4, "Cursed were the companions of the trench", "قُتِلَ أَصۡحَٰبُ ٱلۡأُخۡدُودِ", "Qutila ashabu alukhdoodi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (85, 5, "[Containing] the fire full of fuel", "ٱلنَّارِ ذَاتِ ٱلۡوَقُودِ", "Alnnari thati alwaqoodi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (85, 6, "When they were sitting near it", "إِذۡ هُمۡ عَلَيۡهَا قُعُودٞ", "Ith hum AAalayha quAAoodun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (85, 7, "And they, to what they were doing against the believers, were witnesses", "وَهُمۡ عَلَىٰ مَا يَفۡعَلُونَ بِٱلۡمُؤۡمِنِينَ شُهُودٞ", "Wahum AAala ma yafAAaloona bialmumineena shuhoodun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (85, 8, "And they resented them not except because they believed in Allah, the Exalted in Might, the Praiseworthy", "وَمَا نَقَمُواْ مِنۡهُمۡ إِلَّآ أَن يُؤۡمِنُواْ بِٱللَّهِ ٱلۡعَزِيزِ ٱلۡحَمِيدِ", "Wama naqamoo minhum illa an yuminoo biAllahi alAAazeezi alhameedi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (85, 9, "To whom belongs the dominion of the heavens and the earth. And Allah, over all things, is Witness", "ٱلَّذِي لَهُۥ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ شَهِيدٌ", "Allathee lahu mulku alssamawati waalardi waAllahu AAala kulli shayin shaheedun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (85, 10, "Indeed, those who have tortured the believing men and believing women and then have not repented will have the punishment of Hell, and they will have the punishment of the Burning Fire", "إِنَّ ٱلَّذِينَ فَتَنُواْ ٱلۡمُؤۡمِنِينَ وَٱلۡمُؤۡمِنَٰتِ ثُمَّ لَمۡ يَتُوبُواْ فَلَهُمۡ عَذَابُ جَهَنَّمَ وَلَهُمۡ عَذَابُ ٱلۡحَرِيقِ", "Inna allatheena fatanoo almumineena waalmuminati thumma lam yatooboo falahum AAathabu jahannama walahum AAathabu alhareeqi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (85, 11, "Indeed, those who have believed and done righteous deeds will have gardens beneath which rivers flow. That is the great attainment", "إِنَّ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ لَهُمۡ جَنَّـٰتٞ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُۚ ذَٰلِكَ ٱلۡفَوۡزُ ٱلۡكَبِيرُ", "Inna allatheena amanoo waAAamiloo alssalihati lahum jannatun tajree min tahtiha alanharu thalika alfawzu alkabeeru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (85, 12, "Indeed, the vengeance of your Lord is severe", "إِنَّ بَطۡشَ رَبِّكَ لَشَدِيدٌ", "Inna batsha rabbika lashadeedun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (85, 13, "Indeed, it is He who originates [creation] and repeats", "إِنَّهُۥ هُوَ يُبۡدِئُ وَيُعِيدُ", "Innahu huwa yubdio wayuAAeedu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (85, 14, "And He is the Forgiving, the Affectionate", "وَهُوَ ٱلۡغَفُورُ ٱلۡوَدُودُ", "Wahuwa alghafooru alwadoodu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (85, 15, "Honorable Owner of the Throne", "ذُو ٱلۡعَرۡشِ ٱلۡمَجِيدُ", "Thoo alAAarshi almajeedi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (85, 16, "Effecter of what He intends", "فَعَّالٞ لِّمَا يُرِيدُ", "FaAAAAalun lima yureedu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (85, 17, "Has there reached you the story of the soldiers", "هَلۡ أَتَىٰكَ حَدِيثُ ٱلۡجُنُودِ", "Hal ataka hadeethu aljunoodi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (85, 18, "[Those of] Pharaoh and Thamud", "فِرۡعَوۡنَ وَثَمُودَ", "FirAAawna wathamooda");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (85, 19, "But they who disbelieve are in [persistent] denial", "بَلِ ٱلَّذِينَ كَفَرُواْ فِي تَكۡذِيبٖ", "Bali allatheena kafaroo fee taktheebin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (85, 20, "While Allah encompasses them from behind", "وَٱللَّهُ مِن وَرَآئِهِم مُّحِيطُۢ", "WaAllahu min waraihim muheetun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (85, 21, "But this is an honored Qur'an", "بَلۡ هُوَ قُرۡءَانٞ مَّجِيدٞ", "Bal huwa quranun majeedun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (85, 22, "[Inscribed] in a Preserved Slate", "فِي لَوۡحٖ مَّحۡفُوظِۭ", "Fee lawhin mahfoothin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (86, 1, "By the sky and the night comer", "وَٱلسَّمَآءِ وَٱلطَّارِقِ", "Waalssamai waalttariqi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (86, 2, "And what can make you know what is the night comer", "وَمَآ أَدۡرَىٰكَ مَا ٱلطَّارِقُ", "Wama adraka ma alttariqu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (86, 3, "It is the piercing star", "ٱلنَّجۡمُ ٱلثَّاقِبُ", "Alnnajmu alththaqibu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (86, 4, "There is no soul but that it has over it a protector", "إِن كُلُّ نَفۡسٖ لَّمَّا عَلَيۡهَا حَافِظٞ", "In kullu nafsin lamma AAalayha hafithun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (86, 5, "So let man observe from what he was created", "فَلۡيَنظُرِ ٱلۡإِنسَٰنُ مِمَّ خُلِقَ", "Falyanthuri alinsanu mimma khuliqa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (86, 6, "He was created from a fluid, ejected", "خُلِقَ مِن مَّآءٖ دَافِقٖ", "Khuliqa min main dafiqin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (86, 7, "Emerging from between the backbone and the ribs", "يَخۡرُجُ مِنۢ بَيۡنِ ٱلصُّلۡبِ وَٱلتَّرَآئِبِ", "Yakhruju min bayni alssulbi waalttaraibi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (86, 8, "Indeed, Allah, to return him [to life], is Able", "إِنَّهُۥ عَلَىٰ رَجۡعِهِۦ لَقَادِرٞ", "Innahu AAala rajAAihi laqadirun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (86, 9, "The Day when secrets will be put on trial", "يَوۡمَ تُبۡلَى ٱلسَّرَآئِرُ", "Yawma tubla alssarairu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (86, 10, "Then man will have no power or any helper", "فَمَا لَهُۥ مِن قُوَّةٖ وَلَا نَاصِرٖ", "Fama lahu min quwwatin wala nasirin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (86, 11, "By the sky which returns [rain]", "وَٱلسَّمَآءِ ذَاتِ ٱلرَّجۡعِ", "Waalssamai thati alrrajAAi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (86, 12, "And [by] the earth which cracks open", "وَٱلۡأَرۡضِ ذَاتِ ٱلصَّدۡعِ", "Waalardi thati alssadAAi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (86, 13, "Indeed, the Qur'an is a decisive statement", "إِنَّهُۥ لَقَوۡلٞ فَصۡلٞ", "Innahu laqawlun faslun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (86, 14, "And it is not amusement", "وَمَا هُوَ بِٱلۡهَزۡلِ", "Wama huwa bialhazli");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (86, 15, "Indeed, they are planning a plan", "إِنَّهُمۡ يَكِيدُونَ كَيۡدٗا", "Innahum yakeedoona kaydan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (86, 16, "But I am planning a plan", "وَأَكِيدُ كَيۡدٗا", "Waakeedu kaydan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (86, 17, "So allow time for the disbelievers. Leave them awhile", "فَمَهِّلِ ٱلۡكَٰفِرِينَ أَمۡهِلۡهُمۡ رُوَيۡدَۢا", "Famahhili alkafireena amhilhum ruwaydan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (87, 1, "Exalt the name of your Lord, the Most High", "سَبِّحِ ٱسۡمَ رَبِّكَ ٱلۡأَعۡلَى", "Sabbihi isma rabbika alaAAla");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (87, 2, "Who created and proportioned", "ٱلَّذِي خَلَقَ فَسَوَّىٰ", "Allathee khalaqa fasawwa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (87, 3, "And who destined and [then] guided", "وَٱلَّذِي قَدَّرَ فَهَدَىٰ", "Waallathee qaddara fahada");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (87, 4, "And who brings out the pasture", "وَٱلَّذِيٓ أَخۡرَجَ ٱلۡمَرۡعَىٰ", "Waallathee akhraja almarAAa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (87, 5, "And [then] makes it black stubble", "فَجَعَلَهُۥ غُثَآءً أَحۡوَىٰ", "FajaAAalahu ghuthaan ahwa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (87, 6, "We will make you recite, [O Muhammad], and you will not forget", "سَنُقۡرِئُكَ فَلَا تَنسَىٰٓ", "Sanuqrioka fala tansa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (87, 7, "Except what Allah should will. Indeed, He knows what is declared and what is hidden", "إِلَّا مَا شَآءَ ٱللَّهُۚ إِنَّهُۥ يَعۡلَمُ ٱلۡجَهۡرَ وَمَا يَخۡفَىٰ", "Illa ma shaa Allahu innahu yaAAlamu aljahra wama yakhfa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (87, 8, "And We will ease you toward ease", "وَنُيَسِّرُكَ لِلۡيُسۡرَىٰ", "Wanuyassiruka lilyusra");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (87, 9, "So remind, if the reminder should benefit", "فَذَكِّرۡ إِن نَّفَعَتِ ٱلذِّكۡرَىٰ", "Fathakkir in nafaAAati alththikra");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (87, 10, "He who fears [Allah] will be reminded", "سَيَذَّكَّرُ مَن يَخۡشَىٰ", "Sayaththakkaru man yakhsha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (87, 11, "But the wretched one will avoid it", "وَيَتَجَنَّبُهَا ٱلۡأَشۡقَى", "Wayatajannabuha alashqa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (87, 12, "[He] who will [enter and] burn in the greatest Fire", "ٱلَّذِي يَصۡلَى ٱلنَّارَ ٱلۡكُبۡرَىٰ", "Allathee yasla alnnara alkubra");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (87, 13, "Neither dying therein nor living", "ثُمَّ لَا يَمُوتُ فِيهَا وَلَا يَحۡيَىٰ", "Thumma la yamootu feeha wala yahya");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (87, 14, "He has certainly succeeded who purifies himself", "قَدۡ أَفۡلَحَ مَن تَزَكَّىٰ", "Qad aflaha man tazakka");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (87, 15, "And mentions the name of his Lord and prays", "وَذَكَرَ ٱسۡمَ رَبِّهِۦ فَصَلَّىٰ", "Wathakara isma rabbihi fasalla");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (87, 16, "But you prefer the worldly life", "بَلۡ تُؤۡثِرُونَ ٱلۡحَيَوٰةَ ٱلدُّنۡيَا", "Bal tuthiroona alhayata alddunya");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (87, 17, "While the Hereafter is better and more enduring", "وَٱلۡأٓخِرَةُ خَيۡرٞ وَأَبۡقَىٰٓ", "Waalakhiratu khayrun waabqa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (87, 18, "Indeed, this is in the former scriptures", "إِنَّ هَٰذَا لَفِي ٱلصُّحُفِ ٱلۡأُولَىٰ", "Inna hatha lafee alssuhufi aloola");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (87, 19, "The scriptures of Abraham and Moses", "صُحُفِ إِبۡرَٰهِيمَ وَمُوسَىٰ", "Suhufi ibraheema wamoosa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (88, 1, "Has there reached you the report of the Overwhelming [event]", "هَلۡ أَتَىٰكَ حَدِيثُ ٱلۡغَٰشِيَةِ", "Hal ataka hadeethu alghashiyati");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (88, 2, "[Some] faces, that Day, will be humbled", "وُجُوهٞ يَوۡمَئِذٍ خَٰشِعَةٌ", "Wujoohun yawmaithin khashiAAatun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (88, 3, "Working [hard] and exhausted", "عَامِلَةٞ نَّاصِبَةٞ", "AAamilatun nasibatun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (88, 4, "They will [enter to] burn in an intensely hot Fire", "تَصۡلَىٰ نَارًا حَامِيَةٗ", "Tasla naran hamiyatan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (88, 5, "They will be given drink from a boiling spring", "تُسۡقَىٰ مِنۡ عَيۡنٍ ءَانِيَةٖ", "Tusqa min AAaynin aniyatin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (88, 6, "For them there will be no food except from a poisonous, thorny plant", "لَّيۡسَ لَهُمۡ طَعَامٌ إِلَّا مِن ضَرِيعٖ", "Laysa lahum taAAamun illa min dareeAAin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (88, 7, "Which neither nourishes nor avails against hunger", "لَّا يُسۡمِنُ وَلَا يُغۡنِي مِن جُوعٖ", "La yusminu wala yughnee min jooAAin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (88, 8, "[Other] faces, that Day, will show pleasure", "وُجُوهٞ يَوۡمَئِذٖ نَّاعِمَةٞ", "Wujoohun yawmaithin naAAimatun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (88, 9, "With their effort [they are] satisfied", "لِّسَعۡيِهَا رَاضِيَةٞ", "LisaAAyiha radiyatun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (88, 10, "In an elevated garden", "فِي جَنَّةٍ عَالِيَةٖ", "Fee jannatin AAaliyatin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (88, 11, "Wherein they will hear no unsuitable speech", "لَّا تَسۡمَعُ فِيهَا لَٰغِيَةٗ", "La tasmaAAu feeha laghiyatan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (88, 12, "Within it is a flowing spring", "فِيهَا عَيۡنٞ جَارِيَةٞ", "Feeha AAaynun jariyatun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (88, 13, "Within it are couches raised high", "فِيهَا سُرُرٞ مَّرۡفُوعَةٞ", "Feeha sururun marfooAAatun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (88, 14, "And cups put in place", "وَأَكۡوَابٞ مَّوۡضُوعَةٞ", "Waakwabun mawdooAAatun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (88, 15, "And cushions lined up", "وَنَمَارِقُ مَصۡفُوفَةٞ", "Wanamariqu masfoofatun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (88, 16, "And carpets spread around", "وَزَرَابِيُّ مَبۡثُوثَةٌ", "Wazarabiyyu mabthoothatun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (88, 17, "Then do they not look at the camels - how they are created", "أَفَلَا يَنظُرُونَ إِلَى ٱلۡإِبِلِ كَيۡفَ خُلِقَتۡ", "Afala yanthuroona ila alibili kayfa khuliqat");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (88, 18, "And at the sky - how it is raised", "وَإِلَى ٱلسَّمَآءِ كَيۡفَ رُفِعَتۡ", "Waila alssamai kayfa rufiAAat");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (88, 19, "And at the mountains - how they are erected", "وَإِلَى ٱلۡجِبَالِ كَيۡفَ نُصِبَتۡ", "Waila aljibali kayfa nusibat");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (88, 20, "And at the earth - how it is spread out", "وَإِلَى ٱلۡأَرۡضِ كَيۡفَ سُطِحَتۡ", "Waila alardi kayfa sutihat");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (88, 21, "So remind, [O Muhammad]; you are only a reminder", "فَذَكِّرۡ إِنَّمَآ أَنتَ مُذَكِّرٞ", "Fathakkir innama anta muthakkirun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (88, 22, "You are not over them a controller", "لَّسۡتَ عَلَيۡهِم بِمُصَيۡطِرٍ", "Lasta AAalayhim bimusaytirin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (88, 23, "However, he who turns away and disbelieves", "إِلَّا مَن تَوَلَّىٰ وَكَفَرَ", "Illa man tawalla wakafara");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (88, 24, "Then Allah will punish him with the greatest punishment", "فَيُعَذِّبُهُ ٱللَّهُ ٱلۡعَذَابَ ٱلۡأَكۡبَرَ", "FayuAAaththibuhu Allahu alAAathaba alakbara");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (88, 25, "Indeed, to Us is their return", "إِنَّ إِلَيۡنَآ إِيَابَهُمۡ", "Inna ilayna iyabahum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (88, 26, "Then indeed, upon Us is their account", "ثُمَّ إِنَّ عَلَيۡنَا حِسَابَهُم", "Thumma inna AAalayna hisabahum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (89, 1, "By the dawn", "وَٱلۡفَجۡرِ", "Waalfajri");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (89, 2, "And [by] ten nights", "وَلَيَالٍ عَشۡرٖ", "Walayalin AAashrin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (89, 3, "And [by] the even [number] and the odd", "وَٱلشَّفۡعِ وَٱلۡوَتۡرِ", "WaalshshafAAi waalwatri");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (89, 4, "And [by] the night when it passes", "وَٱلَّيۡلِ إِذَا يَسۡرِ", "Waallayli itha yasri");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (89, 5, "Is there [not] in [all] that an oath [sufficient] for one of perception", "هَلۡ فِي ذَٰلِكَ قَسَمٞ لِّذِي حِجۡرٍ", "Hal fee thalika qasamun lithee hijrin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (89, 6, "Have you not considered how your Lord dealt with 'Aad", "أَلَمۡ تَرَ كَيۡفَ فَعَلَ رَبُّكَ بِعَادٍ", "Alam tara kayfa faAAala rabbuka biAAadin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (89, 7, "[With] Iram - who had lofty pillars", "إِرَمَ ذَاتِ ٱلۡعِمَادِ", "Irama thati alAAimadi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (89, 8, "The likes of whom had never been created in the land", "ٱلَّتِي لَمۡ يُخۡلَقۡ مِثۡلُهَا فِي ٱلۡبِلَٰدِ", "Allatee lam yukhlaq mithluha fee albiladi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (89, 9, "And [with] Thamud, who carved out the rocks in the valley", "وَثَمُودَ ٱلَّذِينَ جَابُواْ ٱلصَّخۡرَ بِٱلۡوَادِ", "Wathamooda allatheena jaboo alssakhra bialwadi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (89, 10, "And [with] Pharaoh, owner of the stakes", "وَفِرۡعَوۡنَ ذِي ٱلۡأَوۡتَادِ", "WafirAAawna thee alawtadi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (89, 11, "[All of] whom oppressed within the lands", "ٱلَّذِينَ طَغَوۡاْ فِي ٱلۡبِلَٰدِ", "Allatheena taghaw fee albiladi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (89, 12, "And increased therein the corruption", "فَأَكۡثَرُواْ فِيهَا ٱلۡفَسَادَ", "Faaktharoo feeha alfasada");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (89, 13, "So your Lord poured upon them a scourge of punishment", "فَصَبَّ عَلَيۡهِمۡ رَبُّكَ سَوۡطَ عَذَابٍ", "Fasabba AAalayhim rabbuka sawta AAathabin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (89, 14, "Indeed, your Lord is in observation", "إِنَّ رَبَّكَ لَبِٱلۡمِرۡصَادِ", "Inna rabbaka labialmirsadi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (89, 15, "And as for man, when his Lord tries him and [thus] is generous to him and favors him, he says, ""My Lord has honored me", "فَأَمَّا ٱلۡإِنسَٰنُ إِذَا مَا ٱبۡتَلَىٰهُ رَبُّهُۥ فَأَكۡرَمَهُۥ وَنَعَّمَهُۥ فَيَقُولُ رَبِّيٓ أَكۡرَمَنِ", "Faamma alinsanu itha ma ibtalahu rabbuhu faakramahu wanaAAAAamahu fayaqoolu rabbee akramani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (89, 16, "But when He tries him and restricts his provision, he says, ""My Lord has humiliated me", "وَأَمَّآ إِذَا مَا ٱبۡتَلَىٰهُ فَقَدَرَ عَلَيۡهِ رِزۡقَهُۥ فَيَقُولُ رَبِّيٓ أَهَٰنَنِ", "Waamma itha ma ibtalahu faqadara AAalayhi rizqahu fayaqoolu rabbee ahanani");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (89, 17, "No! But you do not honor the orphan", "كَلَّاۖ بَل لَّا تُكۡرِمُونَ ٱلۡيَتِيمَ", "Kalla bal la tukrimoona alyateema");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (89, 18, "And you do not encourage one another to feed the poor", "وَلَا تَحَـٰٓضُّونَ عَلَىٰ طَعَامِ ٱلۡمِسۡكِينِ", "Wala tahaddoona AAala taAAami almiskeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (89, 19, "And you consume inheritance, devouring [it] altogether", "وَتَأۡكُلُونَ ٱلتُّرَاثَ أَكۡلٗا لَّمّٗا", "Watakuloona altturatha aklan lamman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (89, 20, "And you love wealth with immense love", "وَتُحِبُّونَ ٱلۡمَالَ حُبّٗا جَمّٗا", "Watuhibboona almala hubban jamman");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (89, 21, "No! When the earth has been leveled - pounded and crushed", "كَلَّآۖ إِذَا دُكَّتِ ٱلۡأَرۡضُ دَكّٗا دَكّٗا", "Kalla itha dukkati alardu dakkan dakkan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (89, 22, "And your Lord has come and the angels, rank upon rank", "وَجَآءَ رَبُّكَ وَٱلۡمَلَكُ صَفّٗا صَفّٗا", "Wajaa rabbuka waalmalaku saffan saffan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (89, 23, "And brought [within view], that Day, is Hell - that Day, man will remember, but what good to him will be the remembrance", "وَجِاْيٓءَ يَوۡمَئِذِۭ بِجَهَنَّمَۚ يَوۡمَئِذٖ يَتَذَكَّرُ ٱلۡإِنسَٰنُ وَأَنَّىٰ لَهُ ٱلذِّكۡرَىٰ", "Wajeea yawmaithin bijahannama yawmaithin yatathakkaru alinsanu waanna lahu alththikra");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (89, 24, "He will say, ""Oh, I wish I had sent ahead [some good] for my life", "يَقُولُ يَٰلَيۡتَنِي قَدَّمۡتُ لِحَيَاتِي", "Yaqoolu ya laytanee qaddamtu lihayatee");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (89, 25, "So on that Day, none will punish [as severely] as His punishment", "فَيَوۡمَئِذٖ لَّا يُعَذِّبُ عَذَابَهُۥٓ أَحَدٞ", "Fayawmaithin la yuAAaththibu AAathabahu ahadun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (89, 26, "And none will bind [as severely] as His binding [of the evildoers]", "وَلَا يُوثِقُ وَثَاقَهُۥٓ أَحَدٞ", "Wala yoothiqu wathaqahu ahadun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (89, 27, "[To the righteous it will be said], ""O reassured soul", "يَـٰٓأَيَّتُهَا ٱلنَّفۡسُ ٱلۡمُطۡمَئِنَّةُ", "Ya ayyatuha alnnafsu almutmainnatu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (89, 28, "Return to your Lord, well-pleased and pleasing [to Him]", "ٱرۡجِعِيٓ إِلَىٰ رَبِّكِ رَاضِيَةٗ مَّرۡضِيَّةٗ", "IrjiAAee ila rabbiki radiyatan mardiyyatan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (89, 29, "And enter among My [righteous] servants", "فَٱدۡخُلِي فِي عِبَٰدِي", "Faodkhulee fee AAibadee");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (89, 30, "And enter My Paradise", "وَٱدۡخُلِي جَنَّتِي", "Waodkhulee jannatee");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (90, 1, "I swear by this city, Makkah", "لَآ أُقۡسِمُ بِهَٰذَا ٱلۡبَلَدِ", "La oqsimu bihatha albaladi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (90, 2, "And you, [O Muhammad], are free of restriction in this city", "وَأَنتَ حِلُّۢ بِهَٰذَا ٱلۡبَلَدِ", "Waanta hillun bihatha albaladi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (90, 3, "And [by] the father and that which was born [of him]", "وَوَالِدٖ وَمَا وَلَدَ", "Wawalidin wama walada");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (90, 4, "We have certainly created man into hardship", "لَقَدۡ خَلَقۡنَا ٱلۡإِنسَٰنَ فِي كَبَدٍ", "Laqad khalaqna alinsana fee kabadin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (90, 5, "Does he think that never will anyone overcome him", "أَيَحۡسَبُ أَن لَّن يَقۡدِرَ عَلَيۡهِ أَحَدٞ", "Ayahsabu an lan yaqdira AAalayhi ahadun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (90, 6, "He says, ""I have spent wealth in abundance", "يَقُولُ أَهۡلَكۡتُ مَالٗا لُّبَدًا", "Yaqoolu ahlaktu malan lubadan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (90, 7, "Does he think that no one has seen him", "أَيَحۡسَبُ أَن لَّمۡ يَرَهُۥٓ أَحَدٌ", "Ayahsabu an lam yarahu ahadun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (90, 8, "Have We not made for him two eyes", "أَلَمۡ نَجۡعَل لَّهُۥ عَيۡنَيۡنِ", "Alam najAAal lahu AAaynayni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (90, 9, "And a tongue and two lips", "وَلِسَانٗا وَشَفَتَيۡنِ", "Walisanan washafatayni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (90, 10, "And have shown him the two ways", "وَهَدَيۡنَٰهُ ٱلنَّجۡدَيۡنِ", "Wahadaynahu alnnajdayni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (90, 11, "But he has not broken through the difficult pass", "فَلَا ٱقۡتَحَمَ ٱلۡعَقَبَةَ", "Fala iqtahama alAAaqabata");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (90, 12, "And what can make you know what is [breaking through] the difficult pass", "وَمَآ أَدۡرَىٰكَ مَا ٱلۡعَقَبَةُ", "Wama adraka ma alAAaqabatu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (90, 13, "It is the freeing of a slave", "فَكُّ رَقَبَةٍ", "Fakku raqabatin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (90, 14, "Or feeding on a day of severe hunger", "أَوۡ إِطۡعَٰمٞ فِي يَوۡمٖ ذِي مَسۡغَبَةٖ", "Aw itAAamun fee yawmin thee masghabatin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (90, 15, "An orphan of near relationship", "يَتِيمٗا ذَا مَقۡرَبَةٍ", "Yateeman tha maqrabatin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (90, 16, "Or a needy person in misery", "أَوۡ مِسۡكِينٗا ذَا مَتۡرَبَةٖ", "Aw miskeenan tha matrabatin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (90, 17, "And then being among those who believed and advised one another to patience and advised one another to compassion", "ثُمَّ كَانَ مِنَ ٱلَّذِينَ ءَامَنُواْ وَتَوَاصَوۡاْ بِٱلصَّبۡرِ وَتَوَاصَوۡاْ بِٱلۡمَرۡحَمَةِ", "Thumma kana mina allatheena amanoo watawasaw bialssabri watawasaw bialmarhamati");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (90, 18, "Those are the companions of the right", "أُوْلَـٰٓئِكَ أَصۡحَٰبُ ٱلۡمَيۡمَنَةِ", "Olaika ashabu almaymanati");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (90, 19, "But they who disbelieved in Our signs - those are the companions of the left", "وَٱلَّذِينَ كَفَرُواْ بِـَٔايَٰتِنَا هُمۡ أَصۡحَٰبُ ٱلۡمَشۡـَٔمَةِ", "Waallatheena kafaroo biayatina hum ashabu almashamati");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (90, 20, "Over them will be fire closed in", "عَلَيۡهِمۡ نَارٞ مُّؤۡصَدَةُۢ", "AAalayhim narun musadatun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (91, 1, "By the sun and its brightness", "وَٱلشَّمۡسِ وَضُحَىٰهَا", "Waalshshamsi waduhaha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (91, 2, "And [by] the moon when it follows it", "وَٱلۡقَمَرِ إِذَا تَلَىٰهَا", "Waalqamari itha talaha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (91, 3, "And [by] the day when it displays it", "وَٱلنَّهَارِ إِذَا جَلَّىٰهَا", "Waalnnahari itha jallaha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (91, 4, "And [by] the night when it covers it", "وَٱلَّيۡلِ إِذَا يَغۡشَىٰهَا", "Waallayli itha yaghshaha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (91, 5, "And [by] the sky and He who constructed it", "وَٱلسَّمَآءِ وَمَا بَنَىٰهَا", "Waalssamai wama banaha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (91, 6, "And [by] the earth and He who spread it", "وَٱلۡأَرۡضِ وَمَا طَحَىٰهَا", "Waalardi wama tahaha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (91, 7, "And [by] the soul and He who proportioned it", "وَنَفۡسٖ وَمَا سَوَّىٰهَا", "Wanafsin wama sawwaha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (91, 8, "And inspired it [with discernment of] its wickedness and its righteousness", "فَأَلۡهَمَهَا فُجُورَهَا وَتَقۡوَىٰهَا", "Faalhamaha fujooraha wataqwaha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (91, 9, "He has succeeded who purifies it", "قَدۡ أَفۡلَحَ مَن زَكَّىٰهَا", "Qad aflaha man zakkaha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (91, 10, "And he has failed who instills it [with corruption]", "وَقَدۡ خَابَ مَن دَسَّىٰهَا", "Waqad khaba man dassaha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (91, 11, "Thamud denied [their prophet] by reason of their transgression", "كَذَّبَتۡ ثَمُودُ بِطَغۡوَىٰهَآ", "Kaththabat thamoodu bitaghwaha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (91, 12, "When the most wretched of them was sent forth", "إِذِ ٱنۢبَعَثَ أَشۡقَىٰهَا", "Ithi inbaAAatha ashqaha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (91, 13, "And the messenger of Allah [Salih] said to them, ""[Do not harm] the she-camel of Allah or [prevent her from] her drink", "فَقَالَ لَهُمۡ رَسُولُ ٱللَّهِ نَاقَةَ ٱللَّهِ وَسُقۡيَٰهَا", "Faqala lahum rasoolu Allahi naqata Allahi wasuqyaha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (91, 14, "But they denied him and hamstrung her. So their Lord brought down upon them destruction for their sin and made it equal [upon all of them]", "فَكَذَّبُوهُ فَعَقَرُوهَا فَدَمۡدَمَ عَلَيۡهِمۡ رَبُّهُم بِذَنۢبِهِمۡ فَسَوَّىٰهَا", "Fakaththaboohu faAAaqarooha fadamdama AAalayhim rabbuhum bithanbihim fasawwaha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (91, 15, "And He does not fear the consequence thereof", "وَلَا يَخَافُ عُقۡبَٰهَا", "Wala yakhafu AAuqbaha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (92, 1, "By the night when it covers", "وَٱلَّيۡلِ إِذَا يَغۡشَىٰ", "Waallayli itha yaghsha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (92, 2, "And [by] the day when it appears", "وَٱلنَّهَارِ إِذَا تَجَلَّىٰ", "Waalnnahari itha tajalla");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (92, 3, "And [by] He who created the male and female", "وَمَا خَلَقَ ٱلذَّكَرَ وَٱلۡأُنثَىٰٓ", "Wama khalaqa alththakara waalontha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (92, 4, "Indeed, your efforts are diverse", "إِنَّ سَعۡيَكُمۡ لَشَتَّىٰ", "Inna saAAyakum lashatta");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (92, 5, "As for he who gives and fears Allah", "فَأَمَّا مَنۡ أَعۡطَىٰ وَٱتَّقَىٰ", "Faamma man aAAta waittaqa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (92, 6, "And believes in the best [reward]", "وَصَدَّقَ بِٱلۡحُسۡنَىٰ", "Wasaddaqa bialhusna");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (92, 7, "We will ease him toward ease", "فَسَنُيَسِّرُهُۥ لِلۡيُسۡرَىٰ", "Fasanuyassiruhu lilyusra");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (92, 8, "But as for he who withholds and considers himself free of need", "وَأَمَّا مَنۢ بَخِلَ وَٱسۡتَغۡنَىٰ", "Waamma man bakhila waistaghna");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (92, 9, "And denies the best [reward]", "وَكَذَّبَ بِٱلۡحُسۡنَىٰ", "Wakaththaba bialhusna");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (92, 10, "We will ease him toward difficulty", "فَسَنُيَسِّرُهُۥ لِلۡعُسۡرَىٰ", "Fasanuyassiruhu lilAAusra");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (92, 11, "And what will his wealth avail him when he falls", "وَمَا يُغۡنِي عَنۡهُ مَالُهُۥٓ إِذَا تَرَدَّىٰٓ", "Wama yughnee AAanhu maluhu itha taradda");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (92, 12, "Indeed, [incumbent] upon Us is guidance", "إِنَّ عَلَيۡنَا لَلۡهُدَىٰ", "Inna AAalayna lalhuda");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (92, 13, "And indeed, to Us belongs the Hereafter and the first [life]", "وَإِنَّ لَنَا لَلۡأٓخِرَةَ وَٱلۡأُولَىٰ", "Wainna lana lalakhirata waaloola");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (92, 14, "So I have warned you of a Fire which is blazing", "فَأَنذَرۡتُكُمۡ نَارٗا تَلَظَّىٰ", "Faanthartukum naran talaththa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (92, 15, "None will [enter to] burn therein except the most wretched one", "لَا يَصۡلَىٰهَآ إِلَّا ٱلۡأَشۡقَى", "La yaslaha illa alashqa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (92, 16, "Who had denied and turned away", "ٱلَّذِي كَذَّبَ وَتَوَلَّىٰ", "Allathee kaththaba watawalla");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (92, 17, "But the righteous one will avoid it", "وَسَيُجَنَّبُهَا ٱلۡأَتۡقَى", "Wasayujannabuha alatqa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (92, 18, "[He] who gives [from] his wealth to purify himself", "ٱلَّذِي يُؤۡتِي مَالَهُۥ يَتَزَكَّىٰ", "Allathee yutee malahu yatazakka");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (92, 19, "And not [giving] for anyone who has [done him] a favor to be rewarded", "وَمَا لِأَحَدٍ عِندَهُۥ مِن نِّعۡمَةٖ تُجۡزَىٰٓ", "Wama liahadin AAindahu min niAAmatin tujza");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (92, 20, "But only seeking the countenance of his Lord, Most High", "إِلَّا ٱبۡتِغَآءَ وَجۡهِ رَبِّهِ ٱلۡأَعۡلَىٰ", "Illa ibtighaa wajhi rabbihi alaAAla");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (92, 21, "And he is going to be satisfied", "وَلَسَوۡفَ يَرۡضَىٰ", "Walasawfa yarda");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (93, 1, "By the morning brightness", "وَٱلضُّحَىٰ", "Waaldduha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (93, 2, "And [by] the night when it covers with darkness", "وَٱلَّيۡلِ إِذَا سَجَىٰ", "Waallayli itha saja");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (93, 3, "Your Lord has not taken leave of you, [O Muhammad], nor has He detested [you]", "مَا وَدَّعَكَ رَبُّكَ وَمَا قَلَىٰ", "Ma waddaAAaka rabbuka wama qala");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (93, 4, "And the Hereafter is better for you than the first [life]", "وَلَلۡأٓخِرَةُ خَيۡرٞ لَّكَ مِنَ ٱلۡأُولَىٰ", "Walalakhiratu khayrun laka mina aloola");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (93, 5, "And your Lord is going to give you, and you will be satisfied", "وَلَسَوۡفَ يُعۡطِيكَ رَبُّكَ فَتَرۡضَىٰٓ", "Walasawfa yuAAteeka rabbuka fatarda");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (93, 6, "Did He not find you an orphan and give [you] refuge", "أَلَمۡ يَجِدۡكَ يَتِيمٗا فَـَٔاوَىٰ", "Alam yajidka yateeman faawa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (93, 7, "And He found you lost and guided [you]", "وَوَجَدَكَ ضَآلّٗا فَهَدَىٰ", "Wawajadaka dallan fahada");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (93, 8, "And He found you poor and made [you] self-sufficient", "وَوَجَدَكَ عَآئِلٗا فَأَغۡنَىٰ", "Wawajadaka AAailan faaghna");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (93, 9, "So as for the orphan, do not oppress [him]", "فَأَمَّا ٱلۡيَتِيمَ فَلَا تَقۡهَرۡ", "Faamma alyateema fala taqhar");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (93, 10, "And as for the petitioner, do not repel [him]", "وَأَمَّا ٱلسَّآئِلَ فَلَا تَنۡهَرۡ", "Waamma alssaila fala tanhar");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (93, 11, "But as for the favor of your Lord, report [it]", "وَأَمَّا بِنِعۡمَةِ رَبِّكَ فَحَدِّثۡ", "Waamma biniAAmati rabbika fahaddith");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (94, 1, "Did We not expand for you, [O Muhammad], your breast", "أَلَمۡ نَشۡرَحۡ لَكَ صَدۡرَكَ", "Alam nashrah laka sadraka");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (94, 2, "And We removed from you your burden", "وَوَضَعۡنَا عَنكَ وِزۡرَكَ", "WawadaAAna AAanka wizraka");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (94, 3, "Which had weighed upon your back", "ٱلَّذِيٓ أَنقَضَ ظَهۡرَكَ", "Allathee anqada thahraka");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (94, 4, "And raised high for you your repute", "وَرَفَعۡنَا لَكَ ذِكۡرَكَ", "WarafaAAna laka thikraka");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (94, 5, "For indeed, with hardship [will be] ease", "فَإِنَّ مَعَ ٱلۡعُسۡرِ يُسۡرًا", "Fainna maAAa alAAusri yusran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (94, 6, "Indeed, with hardship [will be] ease", "إِنَّ مَعَ ٱلۡعُسۡرِ يُسۡرٗا", "Inna maAAa alAAusri yusran");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (94, 7, "So when you have finished [your duties], then stand up [for worship]", "فَإِذَا فَرَغۡتَ فَٱنصَبۡ", "Faitha faraghta fainsab");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (94, 8, "And to your Lord direct [your] longing", "وَإِلَىٰ رَبِّكَ فَٱرۡغَب", "Waila rabbika fairghab");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (95, 1, "By the fig and the olive", "وَٱلتِّينِ وَٱلزَّيۡتُونِ", "Waaltteeni waalzzaytooni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (95, 2, "And [by] Mount Sinai", "وَطُورِ سِينِينَ", "Watoori seeneena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (95, 3, "And [by] this secure city [Makkah]", "وَهَٰذَا ٱلۡبَلَدِ ٱلۡأَمِينِ", "Wahatha albaladi alameeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (95, 4, "We have certainly created man in the best of stature", "لَقَدۡ خَلَقۡنَا ٱلۡإِنسَٰنَ فِيٓ أَحۡسَنِ تَقۡوِيمٖ", "Laqad khalaqna alinsana fee ahsani taqweemin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (95, 5, "Then We return him to the lowest of the low", "ثُمَّ رَدَدۡنَٰهُ أَسۡفَلَ سَٰفِلِينَ", "Thumma radadnahu asfala safileena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (95, 6, "Except for those who believe and do righteous deeds, for they will have a reward uninterrupted", "إِلَّا ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ فَلَهُمۡ أَجۡرٌ غَيۡرُ مَمۡنُونٖ", "Illa allatheena amanoo waAAamiloo alssalihati falahum ajrun ghayru mamnoonin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (95, 7, "So what yet causes you to deny the Recompense", "فَمَا يُكَذِّبُكَ بَعۡدُ بِٱلدِّينِ", "Fama yukaththibuka baAAdu bialddeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (95, 8, "Is not Allah the most just of judges", "أَلَيۡسَ ٱللَّهُ بِأَحۡكَمِ ٱلۡحَٰكِمِينَ", "Alaysa Allahu biahkami alhakimeena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (96, 1, "Recite in the name of your Lord who created", "ٱقۡرَأۡ بِٱسۡمِ رَبِّكَ ٱلَّذِي خَلَقَ", "Iqra biismi rabbika allathee khalaqa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (96, 2, "Created man from a clinging substance", "خَلَقَ ٱلۡإِنسَٰنَ مِنۡ عَلَقٍ", "Khalaqa alinsana min AAalaqin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (96, 3, "Recite, and your Lord is the most Generous", "ٱقۡرَأۡ وَرَبُّكَ ٱلۡأَكۡرَمُ", "Iqra warabbuka alakramu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (96, 4, "Who taught by the pen", "ٱلَّذِي عَلَّمَ بِٱلۡقَلَمِ", "Allathee AAallama bialqalami");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (96, 5, "Taught man that which he knew not", "عَلَّمَ ٱلۡإِنسَٰنَ مَا لَمۡ يَعۡلَمۡ", "AAallama alinsana ma lam yaAAlam");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (96, 6, "No! [But] indeed, man transgresses", "كَلَّآ إِنَّ ٱلۡإِنسَٰنَ لَيَطۡغَىٰٓ", "Kalla inna alinsana layatgha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (96, 7, "Because he sees himself self-sufficient", "أَن رَّءَاهُ ٱسۡتَغۡنَىٰٓ", "An raahu istaghna");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (96, 8, "Indeed, to your Lord is the return", "إِنَّ إِلَىٰ رَبِّكَ ٱلرُّجۡعَىٰٓ", "Inna ila rabbika alrrujAAa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (96, 9, "Have you seen the one who forbids", "أَرَءَيۡتَ ٱلَّذِي يَنۡهَىٰ", "Araayta allathee yanha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (96, 10, "A servant when he prays", "عَبۡدًا إِذَا صَلَّىٰٓ", "AAabdan itha salla");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (96, 11, "Have you seen if he is upon guidance", "أَرَءَيۡتَ إِن كَانَ عَلَى ٱلۡهُدَىٰٓ", "Araayta in kana AAala alhuda");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (96, 12, "Or enjoins righteousness", "أَوۡ أَمَرَ بِٱلتَّقۡوَىٰٓ", "Aw amara bialttaqwa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (96, 13, "Have you seen if he denies and turns away", "أَرَءَيۡتَ إِن كَذَّبَ وَتَوَلَّىٰٓ", "Araayta in kaththaba watawalla");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (96, 14, "Does he not know that Allah sees", "أَلَمۡ يَعۡلَم بِأَنَّ ٱللَّهَ يَرَىٰ", "Alam yaAAlam bianna Allaha yara");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (96, 15, "No! If he does not desist, We will surely drag him by the forelock", "كَلَّا لَئِن لَّمۡ يَنتَهِ لَنَسۡفَعَۢا بِٱلنَّاصِيَةِ", "Kalla lain lam yantahi lanasfaAAan bialnnasiyati");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (96, 16, "A lying, sinning forelock", "نَاصِيَةٖ كَٰذِبَةٍ خَاطِئَةٖ", "Nasiyatin kathibatin khatiatin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (96, 17, "Then let him call his associates", "فَلۡيَدۡعُ نَادِيَهُۥ", "FalyadAAu nadiyahu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (96, 18, "We will call the angels of Hell", "سَنَدۡعُ ٱلزَّبَانِيَةَ", "SanadAAu alzzabaniyata");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (96, 19, "No! Do not obey him. But prostrate and draw near [to Allah]", "كَلَّا لَا تُطِعۡهُ وَٱسۡجُدۡۤ وَٱقۡتَرِب۩", "Kalla la tutiAAhu waosjud waiqtarib");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (97, 1, "Indeed, We sent the Qur'an down during the Night of Decree", "إِنَّآ أَنزَلۡنَٰهُ فِي لَيۡلَةِ ٱلۡقَدۡرِ", "Inna anzalnahu fee laylati alqadri");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (97, 2, "And what can make you know what is the Night of Decree", "وَمَآ أَدۡرَىٰكَ مَا لَيۡلَةُ ٱلۡقَدۡرِ", "Wama adraka ma laylatu alqadri");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (97, 3, "The Night of Decree is better than a thousand months", "لَيۡلَةُ ٱلۡقَدۡرِ خَيۡرٞ مِّنۡ أَلۡفِ شَهۡرٖ", "Laylatu alqadri khayrun min alfi shahrin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (97, 4, "The angels and the Spirit descend therein by permission of their Lord for every matter", "تَنَزَّلُ ٱلۡمَلَـٰٓئِكَةُ وَٱلرُّوحُ فِيهَا بِإِذۡنِ رَبِّهِم مِّن كُلِّ أَمۡرٖ", "Tanazzalu almalaikatu waalrroohu feeha biithni rabbihim min kulli amrin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (97, 5, "Peace it is until the emergence of dawn", "سَلَٰمٌ هِيَ حَتَّىٰ مَطۡلَعِ ٱلۡفَجۡرِ", "Salamun hiya hatta matlaAAi alfajri");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (98, 1, "Those who disbelieved among the People of the Scripture and the polytheists were not to be parted [from misbelief] until there came to them clear evidence", "لَمۡ يَكُنِ ٱلَّذِينَ كَفَرُواْ مِنۡ أَهۡلِ ٱلۡكِتَٰبِ وَٱلۡمُشۡرِكِينَ مُنفَكِّينَ حَتَّىٰ تَأۡتِيَهُمُ ٱلۡبَيِّنَةُ", "Lam yakuni allatheena kafaroo min ahli alkitabi waalmushrikeena munfakkeena hatta tatiyahumu albayyinatu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (98, 2, "A Messenger from Allah, reciting purified scriptures", "رَسُولٞ مِّنَ ٱللَّهِ يَتۡلُواْ صُحُفٗا مُّطَهَّرَةٗ", "Rasoolun mina Allahi yatloo suhufan mutahharatan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (98, 3, "Within which are correct writings", "فِيهَا كُتُبٞ قَيِّمَةٞ", "Feeha kutubun qayyimatun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (98, 4, "Nor did those who were given the Scripture become divided until after there had come to them clear evidence", "وَمَا تَفَرَّقَ ٱلَّذِينَ أُوتُواْ ٱلۡكِتَٰبَ إِلَّا مِنۢ بَعۡدِ مَا جَآءَتۡهُمُ ٱلۡبَيِّنَةُ", "Wama tafarraqa allatheena ootoo alkitaba illa min baAAdi ma jaathumu albayyinatu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (98, 5, "And they were not commanded except to worship Allah, [being] sincere to Him in religion, inclining to truth, and to establish prayer and to give zakah. And that is the correct religion", "وَمَآ أُمِرُوٓاْ إِلَّا لِيَعۡبُدُواْ ٱللَّهَ مُخۡلِصِينَ لَهُ ٱلدِّينَ حُنَفَآءَ وَيُقِيمُواْ ٱلصَّلَوٰةَ وَيُؤۡتُواْ ٱلزَّكَوٰةَۚ وَذَٰلِكَ دِينُ ٱلۡقَيِّمَةِ", "Wama omiroo illa liyaAAbudoo Allaha mukhliseena lahu alddeena hunafaa wayuqeemoo alssalata wayutoo alzzakata wathalika deenu alqayyimati");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (98, 6, "Indeed, they who disbelieved among the People of the Scripture and the polytheists will be in the fire of Hell, abiding eternally therein. Those are the worst of creatures", "إِنَّ ٱلَّذِينَ كَفَرُواْ مِنۡ أَهۡلِ ٱلۡكِتَٰبِ وَٱلۡمُشۡرِكِينَ فِي نَارِ جَهَنَّمَ خَٰلِدِينَ فِيهَآۚ أُوْلَـٰٓئِكَ هُمۡ شَرُّ ٱلۡبَرِيَّةِ", "Inna allatheena kafaroo min ahli alkitabi waalmushrikeena fee nari jahannama khalideena feeha olaika hum sharru albariyyati");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (98, 7, "Indeed, they who have believed and done righteous deeds - those are the best of creatures", "إِنَّ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ أُوْلَـٰٓئِكَ هُمۡ خَيۡرُ ٱلۡبَرِيَّةِ", "Inna allatheena amanoo waAAamiloo alssalihati olaika hum khayru albariyyati");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (98, 8, "Their reward with Allah will be gardens of perpetual residence beneath which rivers flow, wherein they will abide forever, Allah being pleased with them and they with Him. That is for whoever has feared his Lord", "جَزَآؤُهُمۡ عِندَ رَبِّهِمۡ جَنَّـٰتُ عَدۡنٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَآ أَبَدٗاۖ رَّضِيَ ٱللَّهُ عَنۡهُمۡ وَرَضُواْ عَنۡهُۚ ذَٰلِكَ لِمَنۡ خَشِيَ رَبَّهُۥ", "Jazaohum AAinda rabbihim jannatu AAadnin tajree min tahtiha alanharu khalideena feeha abadan radiya Allahu AAanhum waradoo AAanhu thalika liman khashiya rabbahu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (99, 1, "When the earth is shaken with its [final] earthquake", "إِذَا زُلۡزِلَتِ ٱلۡأَرۡضُ زِلۡزَالَهَا", "Itha zulzilati alardu zilzalaha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (99, 2, "And the earth discharges its burdens", "وَأَخۡرَجَتِ ٱلۡأَرۡضُ أَثۡقَالَهَا", "Waakhrajati alardu athqalaha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (99, 3, "And man says, ""What is [wrong] with it", "وَقَالَ ٱلۡإِنسَٰنُ مَا لَهَا", "Waqala alinsanu ma laha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (99, 4, "That Day, it will report its news", "يَوۡمَئِذٖ تُحَدِّثُ أَخۡبَارَهَا", "Yawmaithin tuhaddithu akhbaraha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (99, 5, "Because your Lord has commanded it", "بِأَنَّ رَبَّكَ أَوۡحَىٰ لَهَا", "Bianna rabbaka awha laha");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (99, 6, "That Day, the people will depart separated [into categories] to be shown [the result of] their deeds", "يَوۡمَئِذٖ يَصۡدُرُ ٱلنَّاسُ أَشۡتَاتٗا لِّيُرَوۡاْ أَعۡمَٰلَهُمۡ", "Yawmaithin yasduru alnnasu ashtatan liyuraw aAAmalahum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (99, 7, "So whoever does an atom's weight of good will see it", "فَمَن يَعۡمَلۡ مِثۡقَالَ ذَرَّةٍ خَيۡرٗا يَرَهُۥ", "Faman yaAAmal mithqala tharratin khayran yarahu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (99, 8, "And whoever does an atom's weight of evil will see it", "وَمَن يَعۡمَلۡ مِثۡقَالَ ذَرَّةٖ شَرّٗا يَرَهُۥ", "Waman yaAAmal mithqala tharratin sharran yarahu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (100, 1, "By the racers, panting", "وَٱلۡعَٰدِيَٰتِ ضَبۡحٗا", "WaalAAadiyati dabhan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (100, 2, "And the producers of sparks [when] striking", "فَٱلۡمُورِيَٰتِ قَدۡحٗا", "Faalmooriyati qadhan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (100, 3, "And the chargers at dawn", "فَٱلۡمُغِيرَٰتِ صُبۡحٗا", "Faalmugheerati subhan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (100, 4, "Stirring up thereby [clouds of] dust", "فَأَثَرۡنَ بِهِۦ نَقۡعٗا", "Faatharna bihi naqAAan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (100, 5, "Arriving thereby in the center collectively", "فَوَسَطۡنَ بِهِۦ جَمۡعًا", "Fawasatna bihi jamAAan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (100, 6, "Indeed mankind, to his Lord, is ungrateful", "إِنَّ ٱلۡإِنسَٰنَ لِرَبِّهِۦ لَكَنُودٞ", "Inna alinsana lirabbihi lakanoodun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (100, 7, "And indeed, he is to that a witness", "وَإِنَّهُۥ عَلَىٰ ذَٰلِكَ لَشَهِيدٞ", "Wainnahu AAala thalika lashaheedun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (100, 8, "And indeed he is, in love of wealth, intense", "وَإِنَّهُۥ لِحُبِّ ٱلۡخَيۡرِ لَشَدِيدٌ", "Wainnahu lihubbi alkhayri lashadeedun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (100, 9, "But does he not know that when the contents of the graves are scattered", "۞أَفَلَا يَعۡلَمُ إِذَا بُعۡثِرَ مَا فِي ٱلۡقُبُورِ", "Afala yaAAlamu itha buAAthira ma fee alquboori");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (100, 10, "And that within the breasts is obtained", "وَحُصِّلَ مَا فِي ٱلصُّدُورِ", "Wahussila ma fee alssudoori");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (100, 11, "Indeed, their Lord with them, that Day, is [fully] Acquainted", "إِنَّ رَبَّهُم بِهِمۡ يَوۡمَئِذٖ لَّخَبِيرُۢ", "Inna rabbahum bihim yawmaithin lakhabeerun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (101, 1, "The Striking Calamity", "ٱلۡقَارِعَةُ", "AlqariAAatu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (101, 2, "What is the Striking Calamity", "مَا ٱلۡقَارِعَةُ", "Ma alqariAAatu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (101, 3, "And what can make you know what is the Striking Calamity", "وَمَآ أَدۡرَىٰكَ مَا ٱلۡقَارِعَةُ", "Wama adraka ma alqariAAatu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (101, 4, "It is the Day when people will be like moths, dispersed", "يَوۡمَ يَكُونُ ٱلنَّاسُ كَٱلۡفَرَاشِ ٱلۡمَبۡثُوثِ", "Yawma yakoonu alnnasu kaalfarashi almabthoothi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (101, 5, "And the mountains will be like wool, fluffed up", "وَتَكُونُ ٱلۡجِبَالُ كَٱلۡعِهۡنِ ٱلۡمَنفُوشِ", "Watakoonu aljibalu kaalAAihni almanfooshi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (101, 6, "Then as for one whose scales are heavy [with good deeds]", "فَأَمَّا مَن ثَقُلَتۡ مَوَٰزِينُهُۥ", "Faamma man thaqulat mawazeenuhu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (101, 7, "He will be in a pleasant life", "فَهُوَ فِي عِيشَةٖ رَّاضِيَةٖ", "Fahuwa fee AAeeshatin radiyatin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (101, 8, "But as for one whose scales are light", "وَأَمَّا مَنۡ خَفَّتۡ مَوَٰزِينُهُۥ", "Waamma man khaffat mawazeenuhu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (101, 9, "His refuge will be an abyss", "فَأُمُّهُۥ هَاوِيَةٞ", "Faommuhu hawiyatun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (101, 10, "And what can make you know what that is", "وَمَآ أَدۡرَىٰكَ مَا هِيَهۡ", "Wama adraka ma hiyah");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (101, 11, "It is a Fire, intensely hot", "نَارٌ حَامِيَةُۢ", "Narun hamiyatun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (102, 1, "Competition in [worldly] increase diverts you", "أَلۡهَىٰكُمُ ٱلتَّكَاثُرُ", "Alhakumu alttakathuru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (102, 2, "Until you visit the graveyards", "حَتَّىٰ زُرۡتُمُ ٱلۡمَقَابِرَ", "Hatta zurtumu almaqabira");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (102, 3, "No! You are going to know", "كَلَّا سَوۡفَ تَعۡلَمُونَ", "Kalla sawfa taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (102, 4, "Then no! You are going to know", "ثُمَّ كَلَّا سَوۡفَ تَعۡلَمُونَ", "Thumma kalla sawfa taAAlamoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (102, 5, "No! If you only knew with knowledge of certainty", "كَلَّا لَوۡ تَعۡلَمُونَ عِلۡمَ ٱلۡيَقِينِ", "Kalla law taAAlamoona AAilma alyaqeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (102, 6, "You will surely see the Hellfire", "لَتَرَوُنَّ ٱلۡجَحِيمَ", "Latarawunna aljaheema");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (102, 7, "Then you will surely see it with the eye of certainty", "ثُمَّ لَتَرَوُنَّهَا عَيۡنَ ٱلۡيَقِينِ", "Thumma latarawunnaha AAayna alyaqeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (102, 8, "Then you will surely be asked that Day about pleasure", "ثُمَّ لَتُسۡـَٔلُنَّ يَوۡمَئِذٍ عَنِ ٱلنَّعِيمِ", "Thumma latusalunna yawmaithin AAani alnnaAAeemi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (103, 1, "By time", "وَٱلۡعَصۡرِ", "WaalAAasri");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (103, 2, "Indeed, mankind is in loss", "إِنَّ ٱلۡإِنسَٰنَ لَفِي خُسۡرٍ", "Inna alinsana lafee khusrin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (103, 3, "Except for those who have believed and done righteous deeds and advised each other to truth and advised each other to patience", "إِلَّا ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّـٰلِحَٰتِ وَتَوَاصَوۡاْ بِٱلۡحَقِّ وَتَوَاصَوۡاْ بِٱلصَّبۡرِ", "Illa allatheena amanoo waAAamiloo alssalihati watawasaw bialhaqqi watawasaw bialssabri");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (104, 1, "Woe to every scorner and mocker", "وَيۡلٞ لِّكُلِّ هُمَزَةٖ لُّمَزَةٍ", "Waylun likulli humazatin lumazatin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (104, 2, "Who collects wealth and [continuously] counts it", "ٱلَّذِي جَمَعَ مَالٗا وَعَدَّدَهُۥ", "Allathee jamaAAa malan waAAaddadahu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (104, 3, "He thinks that his wealth will make him immortal", "يَحۡسَبُ أَنَّ مَالَهُۥٓ أَخۡلَدَهُۥ", "Yahsabu anna malahu akhladahu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (104, 4, "No! He will surely be thrown into the Crusher", "كَلَّاۖ لَيُنۢبَذَنَّ فِي ٱلۡحُطَمَةِ", "Kalla layunbathanna fee alhutamati");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (104, 5, "And what can make you know what is the Crusher", "وَمَآ أَدۡرَىٰكَ مَا ٱلۡحُطَمَةُ", "Wama adraka ma alhutamatu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (104, 6, "It is the fire of Allah, [eternally] fueled", "نَارُ ٱللَّهِ ٱلۡمُوقَدَةُ", "Naru Allahi almooqadatu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (104, 7, "Which mounts directed at the hearts", "ٱلَّتِي تَطَّلِعُ عَلَى ٱلۡأَفۡـِٔدَةِ", "Allatee tattaliAAu AAala alafidati");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (104, 8, "Indeed, Hellfire will be closed down upon them", "إِنَّهَا عَلَيۡهِم مُّؤۡصَدَةٞ", "lnnaha AAalayhim musadatun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (104, 9, "In extended columns", "فِي عَمَدٖ مُّمَدَّدَةِۭ", "Fee AAamadin mumaddadatin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (105, 1, "Have you not considered, [O Muhammad], how your Lord dealt with the companions of the elephant", "أَلَمۡ تَرَ كَيۡفَ فَعَلَ رَبُّكَ بِأَصۡحَٰبِ ٱلۡفِيلِ", "Alam tara kayfa faAAala rabbuka biashabi alfeeli");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (105, 2, "Did He not make their plan into misguidance", "أَلَمۡ يَجۡعَلۡ كَيۡدَهُمۡ فِي تَضۡلِيلٖ", "Alam yajAAal kaydahum fee tadleelin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (105, 3, "And He sent against them birds in flocks", "وَأَرۡسَلَ عَلَيۡهِمۡ طَيۡرًا أَبَابِيلَ", "Waarsala AAalayhim tayran ababeela");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (105, 4, "Striking them with stones of hard clay", "تَرۡمِيهِم بِحِجَارَةٖ مِّن سِجِّيلٖ", "Tarmeehim bihijaratin min sijjeelin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (105, 5, "And He made them like eaten straw", "فَجَعَلَهُمۡ كَعَصۡفٖ مَّأۡكُولِۭ", "FajaAAalahum kaAAasfin makoolin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (106, 1, "For the accustomed security of the Quraysh", "لِإِيلَٰفِ قُرَيۡشٍ", "Lieelafi qurayshin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (106, 2, "Their accustomed security [in] the caravan of winter and summer", "إِۦلَٰفِهِمۡ رِحۡلَةَ ٱلشِّتَآءِ وَٱلصَّيۡفِ", "Eelafihim rihlata alshshitai waalssayfi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (106, 3, "Let them worship the Lord of this House", "فَلۡيَعۡبُدُواْ رَبَّ هَٰذَا ٱلۡبَيۡتِ", "FalyaAAbudoo rabba hatha albayti");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (106, 4, "Who has fed them, [saving them] from hunger and made them safe, [saving them] from fear", "ٱلَّذِيٓ أَطۡعَمَهُم مِّن جُوعٖ وَءَامَنَهُم مِّنۡ خَوۡفِۭ", "Allathee atAAamahum min jooAAin waamanahum min khawfin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (107, 1, "Have you seen the one who denies the Recompense", "أَرَءَيۡتَ ٱلَّذِي يُكَذِّبُ بِٱلدِّينِ", "Araayta allathee yukaththibu bialddeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (107, 2, "For that is the one who drives away the orphan", "فَذَٰلِكَ ٱلَّذِي يَدُعُّ ٱلۡيَتِيمَ", "Fathalika allathee yaduAAAAu alyateema");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (107, 3, "And does not encourage the feeding of the poor", "وَلَا يَحُضُّ عَلَىٰ طَعَامِ ٱلۡمِسۡكِينِ", "Wala yahuddu AAala taAAami almiskeeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (107, 4, "So woe to those who pray", "فَوَيۡلٞ لِّلۡمُصَلِّينَ", "Fawaylun lilmusalleena");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (107, 5, "[But] who are heedless of their prayer", "ٱلَّذِينَ هُمۡ عَن صَلَاتِهِمۡ سَاهُونَ", "Allatheena hum AAan salatihim sahoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (107, 6, "Those who make show [of their deeds]", "ٱلَّذِينَ هُمۡ يُرَآءُونَ", "Allatheena hum yuraoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (107, 7, "And withhold [simple] assistance", "وَيَمۡنَعُونَ ٱلۡمَاعُونَ", "WayamnaAAoona almaAAoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (108, 1, "Indeed, We have granted you, [O Muhammad], al-Kawthar", "إِنَّآ أَعۡطَيۡنَٰكَ ٱلۡكَوۡثَرَ", "Inna aAAtaynaka alkawthara");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (108, 2, "So pray to your Lord and sacrifice [to Him alone]", "فَصَلِّ لِرَبِّكَ وَٱنۡحَرۡ", "Fasalli lirabbika wainhar");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (108, 3, "Indeed, your enemy is the one cut off", "إِنَّ شَانِئَكَ هُوَ ٱلۡأَبۡتَرُ", "Inna shaniaka huwa alabtaru");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (109, 1, "Say, ""O disbelievers", "قُلۡ يَـٰٓأَيُّهَا ٱلۡكَٰفِرُونَ", "Qul ya ayyuha alkafiroona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (109, 2, "I do not worship what you worship", "لَآ أَعۡبُدُ مَا تَعۡبُدُونَ", "La aAAbudu ma taAAbudoona");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (109, 3, "Nor are you worshippers of what I worship", "وَلَآ أَنتُمۡ عَٰبِدُونَ مَآ أَعۡبُدُ", "Wala antum AAabidoona ma aAAbudu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (109, 4, "Nor will I be a worshipper of what you worship", "وَلَآ أَنَا۠ عَابِدٞ مَّا عَبَدتُّمۡ", "Wala ana AAabidun ma AAabadtum");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (109, 5, "Nor will you be worshippers of what I worship", "وَلَآ أَنتُمۡ عَٰبِدُونَ مَآ أَعۡبُدُ", "Wala antum AAabidoona ma aAAbudu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (109, 6, "For you is your religion, and for me is my religion", "لَكُمۡ دِينُكُمۡ وَلِيَ دِينِ", "Lakum deenukum waliya deeni");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (110, 1, "When the victory of Allah has come and the conquest", "إِذَا جَآءَ نَصۡرُ ٱللَّهِ وَٱلۡفَتۡحُ", "Itha jaa nasru Allahi waalfathu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (110, 2, "And you see the people entering into the religion of Allah in multitudes", "وَرَأَيۡتَ ٱلنَّاسَ يَدۡخُلُونَ فِي دِينِ ٱللَّهِ أَفۡوَاجٗا", "Waraayta alnnasa yadkhuloona fee deeni Allahi afwajan");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (110, 3, "Then exalt [Him] with praise of your Lord and ask forgiveness of Him. Indeed, He is ever Accepting of repentance", "فَسَبِّحۡ بِحَمۡدِ رَبِّكَ وَٱسۡتَغۡفِرۡهُۚ إِنَّهُۥ كَانَ تَوَّابَۢا", "Fasabbih bihamdi rabbika waistaghfirhu innahu kana tawwaban");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (111, 1, "May the hands of Abu Lahab be ruined, and ruined is he", "تَبَّتۡ يَدَآ أَبِي لَهَبٖ وَتَبَّ", "Tabbat yada abee lahabin watabba");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (111, 2, "His wealth will not avail him or that which he gained", "مَآ أَغۡنَىٰ عَنۡهُ مَالُهُۥ وَمَا كَسَبَ", "Ma aghna AAanhu maluhu wama kasaba");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (111, 3, "He will [enter to] burn in a Fire of [blazing] flame", "سَيَصۡلَىٰ نَارٗا ذَاتَ لَهَبٖ", "Sayasla naran thata lahabin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (111, 4, "And his wife [as well] - the carrier of firewood", "وَٱمۡرَأَتُهُۥ حَمَّالَةَ ٱلۡحَطَبِ", "Waimraatuhu hammalata alhatabi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (111, 5, "Around her neck is a rope of [twisted] fiber", "فِي جِيدِهَا حَبۡلٞ مِّن مَّسَدِۭ", "Fee jeediha hablun min masadin");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (112, 1, "Say, ""He is Allah, [who is] One", "قُلۡ هُوَ ٱللَّهُ أَحَدٌ", "Qul huwa Allahu ahadun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (112, 2, "Allah, the Eternal Refuge", "ٱللَّهُ ٱلصَّمَدُ", "Allahu alssamadu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (112, 3, "He neither begets nor is born", "لَمۡ يَلِدۡ وَلَمۡ يُولَدۡ", "Lam yalid walam yooladu");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (112, 4, "Nor is there to Him any equivalent", "وَلَمۡ يَكُن لَّهُۥ كُفُوًا أَحَدُۢ", "Walam yakun lahu kufuwan ahadun");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (113, 1, "Say, ""I seek refuge in the Lord of daybreak", "قُلۡ أَعُوذُ بِرَبِّ ٱلۡفَلَقِ", "Qul aAAoothu birabbi alfalaqi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (113, 2, "From the evil of that which He created", "مِن شَرِّ مَا خَلَقَ", "Min sharri ma khalaqa");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (113, 3, "And from the evil of darkness when it settles", "وَمِن شَرِّ غَاسِقٍ إِذَا وَقَبَ", "Wamin sharri ghasiqin itha waqaba");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (113, 4, "And from the evil of the blowers in knots", "وَمِن شَرِّ ٱلنَّفَّـٰثَٰتِ فِي ٱلۡعُقَدِ", "Wamin sharri alnnaffathati fee alAAuqadi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (113, 5, "And from the evil of an envier when he envies", "وَمِن شَرِّ حَاسِدٍ إِذَا حَسَدَ", "Wamin sharri hasidin itha hasada");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (114, 1, "Say, ""I seek refuge in the Lord of mankind", "قُلۡ أَعُوذُ بِرَبِّ ٱلنَّاسِ", "Qul aAAoothu birabbi alnnasi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (114, 2, "The Sovereign of mankind", "مَلِكِ ٱلنَّاسِ", "Maliki alnnasi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (114, 3, "The God of mankind", "إِلَٰهِ ٱلنَّاسِ", "Ilahi alnnasi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (114, 4, "From the evil of the retreating whisperer", "مِن شَرِّ ٱلۡوَسۡوَاسِ ٱلۡخَنَّاسِ", "Min sharri alwaswasi alkhannasi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (114, 5, "Who whispers [evil] into the breasts of mankind", "ٱلَّذِي يُوَسۡوِسُ فِي صُدُورِ ٱلنَّاسِ", "Allathee yuwaswisu fee sudoori alnnasi");
INSERT INTO quran (surah, verse, verseContentEnglish, verseContentArabic, verseContentTransliteration) VALUES (114, 6, "From among the jinn and mankind", "مِنَ ٱلۡجِنَّةِ وَٱلنَّاسِ", "Mina aljinnati wa alnnasm");